INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(149, 43, 'Tám trăm năm sau', '1', '<p>&quot;Tr&igrave; Dao, ta đối với ngươi như t&igrave;nh cảm ch&acirc;n th&agrave;nh, ngươi v&igrave; sao muốn giết ta?&quot;<br />\n<br />\nTrương Nhược Trần h&eacute;t lớn một tiếng, nghĩ trước bổ nh&agrave;o về ph&iacute;a trước, &eacute;p tới mạ v&agrave;ng chế tạo giường &quot;Kẽo kẹt&quot; một tiếng, đột nhi&ecirc;n ngồi dậy.<br />\n<br />\nPh&aacute;t hiện chỉ l&agrave; một giấc mộng, Trương Nhược Trần mới thở ra một hơi thật d&agrave;i, d&ugrave;ng ống tay &aacute;o đem mồ h&ocirc;i tr&ecirc;n tr&aacute;n lau kh&ocirc;.<br />\n<br />\nKh&ocirc;ng!<br />\n<br />\nĐ&acirc;y kh&ocirc;ng phải l&agrave; một giấc mộng!<br />\n<br />\nHắn c&ugrave;ng Tr&igrave; Dao c&ocirc;ng ch&uacute;a ph&aacute;t sinh hết thảy, lại thế n&agrave;o c&oacute; thể l&agrave; một giấc mộng?<br />\n<br />\nTrương Nhược Trần vốn l&agrave; C&ocirc;n L&ocirc;n Giới ch&iacute;n đại Đế Qu&acirc;n một trong &quot;Minh Đế&quot; con trai độc nhất, năm gần 16 tuổi, lợi dụng nghịch thi&ecirc;n thể chất, tu luyện tới Thi&ecirc;n Cực Cảnh đại vi&ecirc;n m&atilde;n.<br />\n<br />\nNhưng l&agrave;, ngay tại hắn trở th&agrave;nh C&ocirc;n L&ocirc;n Giới thế hệ tuổi trẻ đệ nhất nh&acirc;n thời điểm, lại chết tại m&igrave;nh thanh mai tr&uacute;c m&atilde; vị h&ocirc;n th&ecirc; Tr&igrave; Dao c&ocirc;ng ch&uacute;a trong tay.<br />\n<br />\nTr&igrave; Dao c&ocirc;ng ch&uacute;a, l&agrave; ch&iacute;n đại Đế Qu&acirc;n một trong &quot;Thanh Đế&quot; nữ nhi.<br />\n<br />\nMinh Đế c&ugrave;ng Thanh Đế l&agrave; tốt nhất bạn tri kỉ, Trương Nhược Trần c&ugrave;ng Tr&igrave; Dao c&ocirc;ng ch&uacute;a c&agrave;ng l&agrave; chỉ ph&uacute;c vi h&ocirc;n, từ nhỏ c&ugrave;ng nhau lớn l&ecirc;n, c&ugrave;ng một chỗ tu luyện. Một c&aacute;i tư th&ecirc;́ hi&ecirc;n ngang, một c&aacute;i mỹ mạo tuyệt lu&acirc;n, c&oacute; thể xưng Kim Đồng Ngọc Nữ, l&uacute;c đầu c&oacute; thể trở th&agrave;nh tu luyện giới một đoạn giai thoại.<br />\n<br />\nTrương Nhược Trần l&agrave;m sao cũng kh&ocirc;ng ngờ được, Tr&igrave; Dao c&ocirc;ng ch&uacute;a thế m&agrave; lại ra tay với hắn!<br />\n<br />\nChết tại Tr&igrave; Dao c&ocirc;ng ch&uacute;a trong tay về sau, khi Trương Nhược Trần lần nữa tỉnh lại, lại ph&aacute;t hiện m&igrave;nh đ&atilde; tại 800 năm về sau.<br />\n<br />\nĐ&atilde; từng Tr&igrave; Dao c&ocirc;ng ch&uacute;a, b&igrave;nh định Cửu Đế chi loạn, thống nhất cửu quốc, th&agrave;nh lập thứ nhất Trung Ương Đế Quốc, trở th&agrave;nh to&agrave;n bộ C&ocirc;n L&ocirc;n Giới Ch&uacute;a Tể &mdash;&mdash; Tr&igrave; Dao Nữ Ho&agrave;ng.<br />\n<br />\n800 năm trước, xưng h&ugrave;ng C&ocirc;n L&ocirc;n Giới Cửu Đế, triệt để trở th&agrave;nh qu&aacute; khứ, biến mất tại lịch sử trường h&agrave; b&ecirc;n trong.<br />\n<br />\nCửu Đế đ&atilde; chết, Nữ Ho&agrave;ng đương lập.<br />\n<br />\nThời đại n&agrave;y, chỉ c&oacute; một vị Ho&agrave;ng giả, c&aacute;i kia ch&iacute;nh l&agrave; Tr&igrave; Dao Nữ Ho&agrave;ng, thống ngự thi&ecirc;n hạ, uy l&acirc;m b&aacute;t phương.<br />\n<br />\n&quot;N&agrave;ng v&igrave; sao muốn giết ta? L&ograve;ng của n&agrave;ng tại sao c&oacute; thể &aacute;c như vậy, hay l&agrave; n&oacute;i l&ograve;ng của phụ nữ đều như thế hung &aacute;c?&quot;<br />\n<br />\nTrương Nhược Trần &aacute;nh mắt sắc b&eacute;n, t&acirc;m ch&igrave;m như sắt, đầy bụng nghi vấn. Nhưng l&agrave;, nhưng kh&ocirc;ng ai c&oacute; thể gi&uacute;p hắn giải đ&aacute;p.<br />\n<br />\n800 năm đi qua, sớm đ&atilde; thương hải tang điền, cảnh c&ograve;n người mất, ngoại trừ tu vi tuyệt thế Tr&igrave; Dao Nữ Ho&agrave;ng, thanh xu&acirc;n vẫn như cũ, bất l&atilde;o bất tử. Đ&atilde; từng những cố nh&acirc;n kia, to&agrave;n bộ đều đ&atilde; ho&aacute; th&agrave;nh c&aacute;t v&agrave;ng, biến th&agrave;nh bạch cốt.<br />\n<br />\nCho d&ugrave; l&agrave; năm đ&oacute; uy phong b&aacute;t diện Cửu Đế, cũng đều to&agrave;n bộ ở nh&acirc;n gian tuyệt t&iacute;ch, chỉ để lại từng đoạn để hậu nh&acirc;n k&eacute;o d&agrave;i truyền tụng huy ho&agrave;ng cố sự.<br />\n<br />\n&quot;Kẹt kẹt!&quot;<br />\n<br />\nMột c&aacute;i th&acirc;n thể nhu nhược cung trang mỹ phụ nh&acirc;n, từ b&ecirc;n ngo&agrave;i đẩy cửa đi tới, nh&igrave;n xem ngồi tại tr&ecirc;n giường Trương Nhược Trần, mang theo mắt &acirc;n cần thần, &quot;Trần Nhi, ngươi lại thấy &aacute;c mộng?&quot;</p>\n\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\n\n<p>&nbsp;</p>\n\n<p><br />\n<br />\nTrước mắt người mỹ phụ n&agrave;y, l&agrave; V&acirc;n V&otilde; Quận Vương Vương phi, cũng l&agrave; Trương Nhược Trần mẫu th&acirc;n, L&acirc;m Phi.<br />\n<br />\nC&aacute;i n&agrave;y một th&acirc;n thể nguy&ecirc;n chủ nh&acirc;n, bởi v&igrave; người yếu nhiều bệnh, ba ng&agrave;y trước liền bệnh chết tại tr&ecirc;n giường.<br />\n<br />\nTrương Nhược Trần bị Tr&igrave; Dao c&ocirc;ng ch&uacute;a giết chết về sau, lần nữa tỉnh lại, liền xuất hiện tại c&aacute;i n&agrave;y một th&acirc;n thể b&ecirc;n trong, để nguy&ecirc;n bản bệnh chết thiếu ni&ecirc;n khởi tử hồi sinh. C&agrave;ng th&ecirc;m tr&ugrave;ng hợp ch&iacute;nh l&agrave;, c&aacute;i n&agrave;y một th&acirc;n thể nguy&ecirc;n chủ nh&acirc;n, cũng gọi Trương Nhược Trần.<br />\n<br />\nTrương Nhược Trần vừa mới l&uacute;c tỉnh lại, c&ograve;n rất b&agrave;i x&iacute;ch L&acirc;m Phi. D&ugrave; sao ở trong mắt Trương Nhược Trần, L&acirc;m Phi, chỉ l&agrave; một người xa lạ.<br />\n<br />\nNhưng l&agrave;, đi qua ba ng&agrave;y tiếp x&uacute;c, Trương Nhược Trần dần dần ph&aacute;t hiện, L&acirc;m Phi thật hết sức quan t&acirc;m hắn, đơn giản cẩn thận, nh&igrave;n thấy Trương Nhược Trần l&agrave;m &aacute;c mộng bị l&agrave;m tỉnh lại, c&agrave;ng kh&ocirc;ng để &yacute; trời đ&ocirc;ng gi&aacute; r&eacute;t, lập tức chạy đến Trương Nhược Trần gian ph&ograve;ng.<br />\n<br />\nỞ kiếp trước, Trương Nhược Trần chưa bao giờ thấy qua m&igrave;nh mẹ đẻ. Nghe n&oacute;i, tại m&igrave;nh ra đời thời điểm, n&agrave;ng liền đi thế! Kh&ocirc;ng nghĩ tới, bị Tr&igrave; Dao c&ocirc;ng ch&uacute;a giết chết về sau, tr&ugrave;ng sinh tại c&aacute;i n&agrave;y một th&acirc;n thể b&ecirc;n trong, v&acirc;̣y mà để hắn nhiều một vị mẫu th&acirc;n, cảm nhận được t&igrave;nh thương của mẹ ấm &aacute;p.<br />\n<br />\n&quot;C&oacute; lẽ n&agrave;ng c&ograve;n kh&ocirc;ng biết, m&igrave;nh Trần Nhi, tại ba ng&agrave;y trước, liền bệnh chết!&quot;<br />\n<br />\nNếu l&agrave; n&oacute;i cho n&agrave;ng ch&acirc;n tướng, n&agrave;ng chưa hẳn chịu được tin dữ n&agrave;y đả k&iacute;ch.<br />\n<br />\nTrương Nhược Trần nh&igrave;n trước mắt người mỹ phụ n&agrave;y, nh&atilde;n thần trở n&ecirc;n nhu h&ograve;a, mỉm cười: &quot;Mẫu th&acirc;n, kh&ocirc;ng cần lo lắng cho ta, chỉ l&agrave; một giấc mộng m&agrave; th&ocirc;i.&quot;<br />\n<br />\nL&acirc;m Phi đơn bạc tr&ecirc;n th&acirc;n hất l&ecirc;n một kiện đỏ thẫm sắc ngay cả mũ l&ocirc;ng chồn, ngồi tại Trương Nhược Trần b&ecirc;n giường, vuốt ve Trương Nhược Trần c&aacute;i tr&aacute;n, lo lắng n&oacute;i: &quot;Đ&atilde; ba ng&agrave;y buổi tối, ngươi lu&ocirc;n lu&ocirc;n bị &aacute;c mộng l&agrave;m tỉnh lại, mỗi lần đều gọi &#39;Tr&igrave; Dao&#39; danh tự. N&agrave;ng đến c&ugrave;ng l&agrave; ai a?&quot;<br />\n<br />\nL&acirc;m Phi tự nhi&ecirc;n kh&ocirc;ng c&oacute; khả năng đem &quot;Tr&igrave; Dao&quot; c&aacute;i t&ecirc;n n&agrave;y, li&ecirc;n tưởng đến thứ nhất Trung Ương Đế Quốc Nữ Ho&agrave;ng.<br />\n<br />\nHuống hồ, Tr&igrave; Dao Nữ Ho&agrave;ng thống nhất C&ocirc;n L&ocirc;n Giới, th&agrave;nh lập thứ nhất Trung Ương Đế Quốc về sau, liền danh xưng &quot;Đại Uy Đại Đức Nữ Th&aacute;nh Ho&agrave;ng&quot;, b&igrave;nh thường căn bản kh&ocirc;ng c&oacute; người d&aacute;m nhắc tới &quot;Tr&igrave; Dao&quot; hai chữ. Sẽ phạm ki&ecirc;ng kị.<br />\n<br />\nTrương Nhược Trần n&oacute;i: &quot;Kh&ocirc;ng c&oacute; g&igrave;, mẫu th&acirc;n, ngươi nghe lầm!&quot;<br />\n<br />\nL&acirc;m Phi thở d&agrave;i một c&aacute;i, n&oacute;i: &quot;Sau n&agrave;y tuyệt đối kh&ocirc;ng n&ecirc;n lại gọi thẳng &#39;Tr&igrave; Dao&#39; hai chữ, cho d&ugrave; l&agrave; trong mộng cũng kh&ocirc;ng được, đ&acirc;y ch&iacute;nh l&agrave; Nữ Ho&agrave;ng tục danh. Gọi thẳng Nữ Ho&agrave;ng tục danh l&agrave; đại bất k&iacute;nh, một khi bị người hữu t&acirc;m nghe được, sẽ bị xử tử.&quot;<br />\n<br />\nTrương Nhược Trần nhẹ gật đầu, thật chặt nh&eacute;o nh&eacute;o ng&oacute;n tay, c&oacute; phần ngậm th&acirc;m &yacute; n&oacute;i: &quot;Tuyệt đối sẽ kh&ocirc;ng! Sau n&agrave;y...&quot;<br />\n<br />\nSau n&agrave;y, ta ch&iacute;nh l&agrave; n&agrave;ng &aacute;c mộng.<br />\n<br />\nL&acirc;m Phi nh&igrave;n xem d&aacute;ng người gầy yếu, sắc mặt t&aacute;i nhợt Trương Nhược Trần, nhẹ nh&agrave;ng thở d&agrave;i một hơi, trong l&ograve;ng v&ocirc; c&ugrave;ng chua x&oacute;t.<br />\n<br />\nMặc d&ugrave; sinh ở Quận Vương nh&agrave;, nhưng l&agrave;, hắn lại từ nhỏ người yếu nhiều bệnh, đ&atilde; 16 tuổi, vẫn như cũ chỉ c&oacute; thể l&acirc;u d&agrave;i nằm ở tr&ecirc;n giường, chỉ sợ đời n&agrave;y cũng chỉ c&oacute; thể bộ d&aacute;ng n&agrave;y!<br />\n<br />\nB&ecirc;n ngo&agrave;i, vang l&ecirc;n một trận xốc xếch tiếng bước ch&acirc;n.<br />\n<br />\n&quot;C&aacute;c ngươi chơi c&aacute;i g&igrave;? Nơi n&agrave;y ch&iacute;nh l&agrave; Ngọc Sấu Cung, ai cho c&aacute;c ngươi l&aacute; gan, d&aacute;m t&ugrave;y &yacute; x&ocirc;ng loạn tiến đến?&quot; Một c&aacute;i dung mạo xinh đẹp thị nữ, muốn ngăn lại x&ocirc;ng v&agrave;o B&aacute;t vương tử, lại bị B&aacute;t vương tử nhẹ nh&agrave;ng đẩy, n&eacute;m tới hơn mười m&eacute;t b&ecirc;n ngo&agrave;i.<br />\n<br />\nB&aacute;t vương tử thế nhưng l&agrave; một vị v&otilde; giả, tu vi đạt tới Ho&agrave;ng Cực Cảnh hậu kỳ, một chưởng đ&aacute;nh ra, đủ để đem nặng ba trăm c&acirc;n bàn đá đ&aacute;nh ra xa mười trượng, huống chi chỉ l&agrave; một c&aacute;i tr&ecirc;n dưới một trăm c&acirc;n nặng thị nữ?<br />\n<br />\nNg&oacute;n tay b&uacute;ng một c&aacute;i, liền c&oacute; thể đưa n&agrave;ng bắn bay ra ngo&agrave;i.<br />\n<br />\nMột c&aacute;i kia thị nữ k&ecirc;u thảm một tiếng, tr&ugrave;ng điệp ng&atilde; xuống đất, tay tr&aacute;i c&aacute;nh tay bị ng&atilde; đoạn.<br />\n<br />\nB&aacute;t vương tử mặc một th&acirc;n kim sợi &aacute;o, tr&ecirc;n lưng quấn lấy một c&acirc;y ngọc thạch mang, th&acirc;n thể tr&aacute;ng kiện, c&aacute;nh tay thon d&agrave;i, b&ocirc;̣ pháp trầm ổn, đi v&agrave;o Ngọc Sấu Cung, lặng lẽ nh&igrave;n chằm chằm người thị nữ kia một ch&uacute;t, &quot;Một c&aacute;i n&ocirc; tỳ cũng d&aacute;m cản bản vương tử con đường, thật sự l&agrave; muốn chết.&quot;<br />\n<br />\nB&aacute;t vương tử sau lưng, đi theo s&aacute;u vị người mặc l&acirc;n gi&aacute;p da gi&aacute;p thị vệ, th&acirc;n thể cao lớn, lưng h&ugrave;m vai gấu, hiển nhi&ecirc;n đều l&agrave; chiến lực cường đại tu sĩ V&otilde; Đạo, thuộc về ho&agrave;ng cung cấm vệ.<br />\n<br />\nL&acirc;m Phi nghe ph&iacute;a b&ecirc;n ngo&agrave;i động tĩnh, trấn an Trương Nhược Trần cảm x&uacute;c về sau, liền đ&oacute;ng cửa lại, đi ra ngo&agrave;i.<br />\n<br />\nN&agrave;ng nh&igrave;n chằm chằm đứng ở b&ecirc;n ngo&agrave;i B&aacute;t vương tử, c&oacute; ch&uacute;t cau lại l&ocirc;ng m&agrave;y, n&oacute;i: &quot;B&aacute;t vương tử điện hạ, nơi n&agrave;y ch&iacute;nh l&agrave; Ngọc Sấu Cung, coi như ngươi l&agrave; Vương tử, cũng kh&ocirc;ng thể x&ocirc;ng loạn đi!&quot;<br />\n<br />\nB&aacute;t vương tử Trương Tế ngẩng đầu nh&igrave;n chằm chằm L&acirc;m Phi, cất cao giọng n&oacute;i: &quot;Vương hậu c&oacute; lệnh, L&acirc;m Phi nương nương c&ugrave;ng Cửu đệ tẩm cung, đổi đến &#39;Tử Di Thi&ecirc;n Điện&#39; . Sau n&agrave;y Ngọc Sấu Cung chủ nh&acirc;n, ch&iacute;nh l&agrave; bản vương tử mẹ đẻ Ti&ecirc;u Phi nương nương.&quot;<br />\n<br />\nL&acirc;m Phi sắc mặt hơi đổi, n&agrave;ng đ&atilde; sớm ngờ tới một ng&agrave;y n&agrave;y sẽ đến, nhưng l&agrave;, nhưng kh&ocirc;ng c&oacute; nghĩ đến sẽ đến đ&ecirc;́n nhanh như vậy.<br />\n<br />\nL&acirc;m Phi đau thương cười một tiếng, n&oacute;i: &quot;Vương hậu nhanh như vậy liền muốn đuổi ch&uacute;ng ta mẹ con rời đi Ngọc Sấu Cung sao? Tốt a! Ng&agrave;y mai, ta liền c&ugrave;ng Trần Nhi dọn đi Thi&ecirc;n Điện.&quot;<br />\n<br />\nB&aacute;t vương tử n&oacute;i: &quot;Thật xin lỗi! Mẫu th&acirc;n n&oacute;i, n&agrave;ng đ&ecirc;m nay liền muốn v&agrave;o ở Ngọc Sấu Cung. Xin L&acirc;m Phi nương nương hiện tại liền dọn đi Thi&ecirc;n Điện!&quot;<br />\n<br />\nL&acirc;m Phi biết Trương Nhược Trần người yếu nhiều bệnh, chịu kh&ocirc;ng được giày vò, mang theo v&agrave;i phần cầu khẩn ngữ kh&iacute;, n&oacute;i: &quot;B&aacute;t vương tử điện hạ, ngươi cũng biết ngươi Cửu đệ người yếu nhiều bệnh, đ&ecirc;m đ&atilde; khuya, thời tiết r&eacute;t lạnh, vạn nhất...&quot;<br />\n<br />\nB&aacute;t vương tử cười lạnh, mảy may đều kh&ocirc;ng kh&aacute;ch kh&iacute; n&oacute;i: &quot;L&acirc;m Phi nương nương, tr&ecirc;n đời n&agrave;y người đ&aacute;ng thương nhiều đến đi, nhưng l&agrave;, kh&ocirc;ng phải mỗi người đều đ&aacute;ng gi&aacute; đ&aacute;ng thương. Tất nhi&ecirc;n Cửu đệ người yếu nhiều bệnh, c&aacute;i kia c&ograve;n sống tr&ecirc;n đời l&agrave;m g&igrave;?&quot;<br />\n<br />\n&quot;Hắn nhưng l&agrave; ngươi Cửu đệ!&quot;<br />\n<br />\nL&acirc;m Phi c&ograve;n muốn n&oacute;i tiếp c&aacute;i g&igrave;, đột nhi&ecirc;n, cửa ph&iacute;a sau bị đẩy ra.<br />\n<br />\nTrương Nhược Trần th&acirc;n thể suy yếu, lấy tay chống đỡ cột cửa mới c&oacute; thể miễn cưỡng đứng thẳng, nh&igrave;n chằm chằm c&aacute;ch đ&oacute; kh&ocirc;ng xa B&aacute;t vương tử. Hắn nh&igrave;n như yếu kh&ocirc;ng trải qua gi&oacute; th&acirc;n thể, giống như l&agrave; ẩn chứa &yacute; ch&iacute; bất khuất, n&oacute;i: &quot;Kh&ocirc;ng cần cầu bọn hắn, ch&uacute;ng ta b&acirc;y giờ liền dọn đi.&quot;<br />\n<br />\n&quot;Trần Nhi, ngươi l&agrave;m sao xuống giường? Kh&iacute; trời b&ecirc;n ngo&agrave;i r&eacute;t lạnh, c&ograve;n kh&ocirc;ng mau trở về.&quot; L&acirc;m Phi liền vội v&agrave;ng tiến l&ecirc;n đỡ Trương Nhược Trần, sợ hắn nhiễm l&ecirc;n phong h&agrave;n.<br />\n<br />\nTrương Nhược Trần cố chấp lắc đầu, n&oacute;i: &quot;Mẫu th&acirc;n, ch&uacute;ng ta kh&ocirc;ng cần cầu bất luận kẻ n&agrave;o, sớm muộn c&oacute; một ng&agrave;y... Ch&uacute;ng ta sẽ một lần nữa về tới đ&acirc;y!&quot;<br />\n<br />\nL&acirc;m Phi nh&igrave;n xem Trương Nhược Trần &aacute;nh mắt ki&ecirc;n định, tựa hồ cũng bị t&acirc;m t&igrave;nh của hắn cảm nhiễm, nước mắt lượn quanh nhẹ gật đầu.<br />\n<br />\nL&acirc;m Phi c&ugrave;ng tham gia vịn Trương Nhược Trần, từng bước một đi ra Ngọc Sấu Cung, ngoại trừ một c&aacute;i kia bị B&aacute;t vương tử một chưởng đẩy đi ra quẳng tay g&atilde;y c&aacute;nh tay thị nữ. Kh&aacute;c những người hầu kia, to&agrave;n bộ đều kh&ocirc;ng c&oacute; đi theo đ&aacute;m bọn hắn rời đi Ngọc Sấu Cung.<br />\n<br />\nTất cả mọi người nh&igrave;n ra được, L&acirc;m Phi c&ugrave;ng Cửu vương tử đ&atilde; triệt để thất thế, tại Quận Vương trong phủ, lại kh&oacute; c&oacute; bọn hắn nơi sống y&ecirc;n ổn.<br />\n<br />\nL&uacute;c đầu bọn hắn liền l&agrave; Ngọc Sấu Cung người hầu, hiện tại tự nhi&ecirc;n lựa chọn s&aacute;ng suốt lưu tại Ngọc Sấu Cung, to&agrave;n bộ đều đi nịnh nọt B&aacute;t vương tử vị n&agrave;y chủ nh&acirc;n mới.<br />\n<br />\nTử Di Thi&ecirc;n Điện, b&igrave;nh thường đều l&agrave; thất sủng Vương phi chỗ ở, mười phần vắng vẻ, đầy đất l&aacute; rụng, tựa hồ đ&atilde; thật l&acirc;u kh&ocirc;ng c&oacute; người ở.<br />\n<br />\nĐ&ecirc;m lấy s&acirc;u, h&agrave;n phong đ&igrave;u hiu.</p>\n\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\n\n<p>&nbsp;</p>\n\n<p><br />\n<br />\nNgồi tại băng l&atilde;nh tr&ecirc;n mặt ghế đ&aacute;, Trương Nhược Trần gầy yếu tr&ecirc;n th&acirc;n bọc lấy một kiện &aacute;o ngo&agrave;i, nhưng như cũ cảm gi&aacute;c được r&eacute;t lạnh.<br />\n<br />\n&quot;C&aacute;i n&agrave;y một bộ nhục th&acirc;n qu&aacute; yếu ớt, chỉ c&oacute; tu luyện V&otilde; Đạo, mới c&oacute; thể để cho th&acirc;n thể dần dần cường tr&aacute;ng. Nếu kh&ocirc;ng, coi như ta hiện tại l&agrave; Quận Vương chi tử, vẫn như cũ chỉ c&oacute; thể bị người b&agrave;i bố.&quot; Trương Nhược Trần trong l&ograve;ng thầm nghĩ.<br />\n<br />\n800 năm đi qua, Trương Nhược Trần cũng kh&ocirc;ng biết m&igrave;nh b&acirc;y giờ có th&ecirc;̉ đi nơi n&agrave;o? Tất nhi&ecirc;n thượng thi&ecirc;n an b&agrave;i hắn tr&ugrave;ng sinh tại c&aacute;i n&agrave;y một th&acirc;n thể b&ecirc;n trong, v&ocirc; luận l&agrave; v&igrave; tương lai hướng Tr&igrave; Dao Nữ Ho&agrave;ng b&aacute;o th&ugrave;, vẫn l&agrave; v&igrave; vị kia cẩn thận chiếu cố mẹ ruột của m&igrave;nh, hắn đều nhất định muốn cường đại l&ecirc;n.<br />\n<br />\nH&ocirc;m nay gặp khuất nhục c&ugrave;ng lạnh nhạt, ho&agrave;n to&agrave;n đều l&agrave; bởi v&igrave; ch&iacute;nh m&igrave;nh qu&aacute; yếu ớt, kh&ocirc;ng c&aacute;ch n&agrave;o phản kh&aacute;ng, kh&ocirc;ng c&aacute;ch n&agrave;o nắm giữ vận mệnh của m&igrave;nh, thậm ch&iacute; ngay cả m&igrave;nh chỗ ở đều bị người kh&aacute;c cưỡng chiếm.<br />\n<br />\nMuốn c&oacute; được người kh&aacute;c t&ocirc;n trọng, muốn thu hoạch được ấm &aacute;p thoải m&aacute;i dễ chịu ở lại ho&agrave;n cảnh, nhất định phải trở th&agrave;nh một t&ecirc;n v&otilde; giả, chứng minh năng lực của m&igrave;nh.<br />\n<br />\nTại C&ocirc;n L&ocirc;n Giới, muốn trở th&agrave;nh một t&ecirc;n v&otilde; giả, nhất định phải trước mở ra &quot;Thần V&otilde; Ấn K&yacute;&quot; .<br />\n<br />\nC&aacute;i gọi l&agrave; &quot;Thần V&otilde; Ấn K&yacute;&quot;, liền l&agrave; Thần Linh ban cho nh&acirc;n loại tu luyện V&otilde; Đạo tư c&aacute;ch. Kh&ocirc;ng c&oacute; mở ra &quot;Thần V&otilde; Ấn K&yacute;&quot; người, liền vĩnh viễn cũng tu luyện kh&ocirc;ng ra ch&acirc;n kh&iacute;, kh&ocirc;ng c&aacute;ch n&agrave;o trở th&agrave;nh giữa thi&ecirc;n địa cường giả.<br />\n<br />\nTrương Nhược Trần đ&atilde; 16 tuổi, vẫn kh&ocirc;ng c&oacute; mở ra &quot;Thần V&otilde; Ấn K&yacute;&quot; .<br />\n<br />\nQua 16 tuổi, liền bỏ lỡ tu v&otilde; tốt nhất tuổi t&aacute;c, coi như mở ra &quot;Thần V&otilde; Ấn K&yacute;&quot;, cũng kh&ocirc;ng c&oacute; khả năng lớn bao nhi&ecirc;u th&agrave;nh tựu.<br />\n<br />\nĐồng dạng đều l&agrave; V&acirc;n V&otilde; Quận Vương nhi tử, v&igrave; sao B&aacute;t vương tử liền c&oacute; thể t&agrave;i tr&iacute; hơn người? C&oacute; thể đem Trương Nhược Trần c&ugrave;ng L&acirc;m Phi đuổi ra Ngọc Sấu Cung?<br />\n<br />\nCũng l&agrave; bởi v&igrave;, B&aacute;t vương tử tại 10 tuổi thời điểm, liền mở ra &quot;Thần V&otilde; Ấn K&yacute;&quot;, hiện tại đ&atilde; l&agrave; Ho&agrave;ng Cực Cảnh hậu kỳ tuổi trẻ v&otilde; giả.<br />\n<br />\n&quot;Chỉ cần để cho ta mở ra &#39;Thần V&otilde; Ấn K&yacute;&#39;, ta liền c&oacute; thể tu luyện &laquo; Cửu Thi&ecirc;n Minh Đế Kinh &raquo;. Lấy &laquo; Cửu Thi&ecirc;n Minh Đế Kinh &raquo; huyền diệu, coi như ta đ&atilde; bỏ lỡ tốt nhất tu luyện ni&ecirc;n kỷ, vẫn như cũ có khả năng đuổi kịp kh&aacute;c thi&ecirc;n t&agrave;i, một lần nữa trở th&agrave;nh một t&ecirc;n V&otilde; Đạo cường giả.&quot;<br />\n<br />\n&laquo; Cửu Thi&ecirc;n Minh Đế Kinh &raquo; l&agrave; Minh Đế tu luyện ch&iacute; cao bảo điển, ngoại trừ Minh Đế b&ecirc;n ngo&agrave;i, liền chỉ c&oacute; Trương Nhược Trần biết &laquo; Cửu Thi&ecirc;n Minh Đế Kinh &raquo; ho&agrave;n chỉnh tu luyện ph&aacute;p quyết.<br />\n<br />\n&quot;Ng&agrave;y mai sẽ l&agrave; tế tự đại điển, hy vọng c&oacute; thể đạt được Thần Linh t&aacute;n th&agrave;nh, đem &#39;Thần V&otilde; Ấn K&yacute;&#39; mở ra.&quot; Trương Nhược Trần nắm thật chặt nắm đấm, đối mở ra &quot;Thần V&otilde; Ấn K&yacute;&quot; tr&agrave;n ngập kh&aacute;t vọng.<br />\n<br />\nL&acirc;m Phi đem gian ph&ograve;ng thu thập chỉnh l&yacute; tốt về sau, liền tới n&acirc;ng Trương Nhược Trần, &quot;Trần Nhi, ngươi nhanh sớm nghỉ ngơi một ch&uacute;t đi! Ng&agrave;y mai, c&ograve;n muốn đi tham gia tế tự đại điển.&quot;<br />\n<br />\n&quot;Mẫu th&acirc;n y&ecirc;n t&acirc;m, ta ng&agrave;y mai khẳng định c&oacute; thể mở ra &#39;Thần V&otilde; Ấn K&yacute;&#39; !&quot; Trương Nhược Trần nói.<br />\n<br />\n&quot;Ừm! Mẫu th&acirc;n tin tưởng ngươi!&quot;<br />\n<br />\nL&acirc;m Phi nh&igrave;n thật s&acirc;u Trương Nhược Trần một ch&uacute;t, trong l&ograve;ng nhẹ nh&agrave;ng thở d&agrave;i.<br />\n<br />\nKỳ thật, n&agrave;ng đối Trương Nhược Trần mở ra &quot;Thần V&otilde; Ấn K&yacute;&quot; căn bản kh&ocirc;ng b&aacute;o bất cứ hy vọng n&agrave;o, d&ugrave; sao Trương Nhược Trần đ&atilde; 16 tuổi, qua 16 tuổi, liền gần như kh&ocirc;ng c&oacute; khả năng c&ograve;n c&oacute; thể mở ra Thần V&otilde; Ấn K&yacute;.<br />\n<br />\nNhưng l&agrave;, l&agrave;m một vị mẫu th&acirc;n, n&agrave;ng lại nhất định phải cổ vũ con của m&igrave;nh, cho hắn l&ograve;ng tin.</p>', '2021-11-27 19:50:23', 132, 1),
(150, 43, 'Mở ra Thần Võ Ấn Ký', '2', '<p>Trương Nhược Trần hiện tại chỗ quốc gia, t&ecirc;n l&agrave; &quot;V&acirc;n V&otilde; Quận Quốc&quot;, chỉ l&agrave; C&ocirc;n L&ocirc;n Giới Đ&ocirc;ng Vực h&agrave;ng ng&agrave;n h&agrave;ng vạn c&aacute;i Quận Quốc b&ecirc;n trong một c&aacute;i.<br />\r\n<br />\r\nC&aacute;i gọi l&agrave; Quận Quốc, kỳ thật liền l&agrave; thứ nhất Trung Ương Đế Quốc c&aacute;i thứ nhất quận, h&agrave;ng năm nhất định phải hướng thứ nhất Trung Ương Đế Quốc cống l&ecirc;n c&ugrave;ng nộp thuế.<br />\r\n<br />\r\nQuận Quốc quốc qu&acirc;n, xưng l&agrave; &quot;Quận Vương&quot; .<br />\r\n<br />\r\nTrương Nhược Trần th&acirc;n phận b&acirc;y giờ, liền l&agrave; V&acirc;n V&otilde; Quận Vương con trai thứ ch&iacute;n.<br />\r\n<br />\r\nNằm thẳng tại băng l&atilde;nh, cứng rắn tr&ecirc;n giường gỗ, Trương Nhược Trần vẫn tại suy tư ng&agrave;y mai tế tự đại điển sự t&igrave;nh.<br />\r\n<br />\r\n&quot;Cỗ th&acirc;n thể n&agrave;y nguy&ecirc;n chủ nh&acirc;n, đến 16 tuổi thế m&agrave; đều kh&ocirc;ng c&oacute; mở ra &#39;Thần V&otilde; Ấn K&yacute;&#39;, khẳng định l&agrave; bị Chư Thần vứt bỏ người. Ta muốn l&agrave;m thế n&agrave;o, mới c&oacute; thể c&oacute; c&agrave;ng lớn cơ hội mở ra &#39;Thần V&otilde; Ấn K&yacute;&#39; ?&quot;<br />\r\n<br />\r\nTại C&ocirc;n L&ocirc;n Giới, muốn mở ra &quot;Thần V&otilde; Ấn K&yacute;&quot;, nhất định phải đạt được Thần Linh t&aacute;n đồng.<br />\r\n<br />\r\nĐược xưng l&agrave;, v&otilde; quyền thần thụ.<br />\r\n<br />\r\nTại tế tự đại điển thời điểm, Thần Giới c&ugrave;ng C&ocirc;n L&ocirc;n Giới ở giữa sẽ xuất hiện một đầu Thi&ecirc;n Địa Thần Kiều , li&ecirc;n tiếp lưỡng giới. Chư Thần tại hưởng dụng tế phẩm về sau, sẽ ban cho một ch&uacute;t người c&oacute; thi&ecirc;n ph&uacute; loại &quot;Thần V&otilde; Ấn K&yacute;&quot;, gi&uacute;p bọn hắn mở ra c&aacute;nh cửa tu luyện.<br />\r\n<br />\r\nThi&ecirc;n ph&uacute; c&agrave;ng cao người, liền c&oacute; thể c&agrave;ng sớm đạt được &quot;Thần V&otilde; Ấn K&yacute;&quot; .<br />\r\n<br />\r\nTrương Nhược Trần ở kiếp trước, c&ograve;n tại thai trong bụng thời điểm, liền mở ra &quot;Thần V&otilde; Ấn K&yacute;&quot;, c&oacute; thể n&oacute;i l&agrave; trời sinh kỳ t&agrave;i.<br />\r\n<br />\r\nMột thế n&agrave;y, đến 16 tuổi, thế m&agrave; đều kh&ocirc;ng c&oacute; mở ra &quot;Thần V&otilde; Ấn K&yacute;&quot;, c&aacute;i kia tr&ecirc;n cơ bản liền l&agrave; bị Chư Thần vứt bỏ người. Coi như ng&agrave;y mai lại tham gia tế tự đại điển, cơ hồ cũng kh&ocirc;ng c&oacute; khả năng thu hoạch được &quot;Thần V&otilde; Ấn K&yacute;&quot; .<br />\r\n<br />\r\nTrương Nhược Trần kh&ocirc;ng c&aacute;ch n&agrave;o ch&igrave;m v&agrave;o giấc ngủ, từ tr&ecirc;n giường ngồi dậy, cầm trong tay một vi&ecirc;n hạt t&aacute;o h&igrave;nh dạng m&agrave;u trắng Tinh Thạch, hai đầu b&eacute;n nhọn, ở giữa lập thể, &oacute;ng &aacute;nh s&aacute;ng long lanh, kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o tạp chất.<br />\r\n<br />\r\nHắn bắt đầu nghi&ecirc;n cứu c&aacute;i n&agrave;y một vi&ecirc;n m&agrave;u trắng Tinh Thạch, c&oacute; lẽ, n&oacute; c&oacute; thể gi&uacute;p m&igrave;nh, đạt được một vị n&agrave;o đ&oacute; Thần Linh t&aacute;n th&agrave;nh, mở ra &quot;Thần V&otilde; Ấn K&yacute;&quot; .<br />\r\n<br />\r\nC&aacute;i n&agrave;y một vi&ecirc;n m&agrave;u trắng Tinh Thạch, l&agrave; tại hắn 16 tuổi lễ th&agrave;nh nh&acirc;n thời điểm, Minh Đế đưa cho hắn lễ vật.<br />\r\n<br />\r\nTrương Nhược Trần cũng kh&ocirc;ng biết c&aacute;i n&agrave;y một vi&ecirc;n m&agrave;u trắng Tinh Thạch rốt cuộc l&agrave; thứ g&igrave;, chỉ l&agrave; đưa n&oacute; thiếp th&acirc;n đeo ở tr&ecirc;n người. Kh&ocirc;ng nghĩ tới, đi v&agrave;o 800 năm về sau, n&oacute; thế m&agrave; vẫn như cũ c&ograve;n tại tr&ecirc;n người m&igrave;nh.<br />\r\n<br />\r\n&quot;Ta sẽ từ 800 năm trước đi v&agrave;o 800 năm về sau, n&oacute;i kh&ocirc;ng chừng liền c&ugrave;ng n&oacute; c&oacute; quan hệ.&quot;<br />\r\n<br />\r\nTrương Nhược Trần thật chặt nắm vuốt m&agrave;u trắng Tinh Thạch, hai mắt nhắm lại, trong đầu hiện ra phụ th&acirc;n Minh Đế th&acirc;n ảnh, cũng kh&ocirc;ng biết phụ th&acirc;n c&ograve;n sống tr&ecirc;n đời kh&ocirc;ng c&oacute;?<br />\r\n<br />\r\nMột đ&ecirc;m n&agrave;y, Vương th&agrave;nh rơi ra tuyết lớn.<br />\r\n<br />\r\nS&aacute;ng sớm ng&agrave;y thứ hai, to&agrave;n bộ Vương th&agrave;nh đều bị thật d&agrave;y tuyết đọng bao tr&ugrave;m, từng t&ograve;a m&agrave;u đỏ thắm cung điện, lầu c&aacute;c, đ&igrave;nh đ&agrave;i, to&agrave;n bộ tr&ugrave;m l&ecirc;n một tầng băng đi&ecirc;u tuyết y.<br />\r\n<br />\r\nNg&agrave;y Đ&ocirc;ng ch&iacute;, cả năm r&eacute;t lạnh nhất một ng&agrave;y.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n<br />\r\nTo&agrave;n bộ Vương th&agrave;nh v&otilde; giả, tụ tập đến Chư Ho&agrave;ng Từ Đường b&ecirc;n ngo&agrave;i, tại Quận Vương dẫn đầu dưới, tế tự Chư Thần.<br />\r\n<br />\r\nChư Ho&agrave;ng Từ Đường b&ecirc;n ngo&agrave;i, d&ugrave;ng cự thạch đắp l&ecirc;n lấy một t&ograve;a cổ xưa tế đ&agrave;n. Tr&ecirc;n tế đ&agrave;i, kh&ocirc;ng chỉ c&oacute; tr&oacute;i chặt nước cờ lấy vạn nhớ s&uacute;c vật, tr&acirc;u, d&ecirc;, heo... , c&ograve;n c&oacute; rất đa dụng x&iacute;ch sắt kh&oacute;a lại cường đại Man th&uacute;.<br />\r\n<br />\r\nVăn v&otilde; b&aacute; quan, tu sĩ V&otilde; Đạo, Vương tử Tần phi, v&ocirc; số chờ đợi mở ra Thần V&otilde; Ấn K&yacute; thiếu ni&ecirc;n c&ugrave;ng thiếu nữ, thậm ch&iacute; l&agrave; một ch&uacute;t c&ograve;n tại trong t&atilde; l&oacute;t h&agrave;i nhi.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; một trận cả nước buổi lễ long trọng, kh&ocirc;ng chỉ c&oacute; chỉ l&agrave; Vương th&agrave;nh, tại V&acirc;n V&otilde; Quận Quốc mỗi một t&ograve;a th&agrave;nh tr&igrave;, mỗi một c&aacute;i tiểu trấn, mỗi một c&aacute;i th&ocirc;n x&oacute;m đều muốn cử h&agrave;nh tế tự.<br />\r\n<br />\r\n&quot;Ng&acirc;n ng&acirc;n! Cửu đệ, ngươi cũng 16 tuổi, coi như tham gia tế tự đại điển, cũng kh&ocirc;ng c&oacute; khả năng thu hoạch được &#39;Thần V&otilde; Ấn K&yacute;&#39;, l&agrave;m g&igrave; đến mất mặt xấu hổ?&quot; B&aacute;t vương tử Trương Tế chắp hai tay sau lưng, lạnh buốt cười một tiếng.<br />\r\n<br />\r\nLục vương tử liền đứng tại B&aacute;t vương tử b&ecirc;n người, gương mặt lạnh l&ugrave;ng, n&oacute;i: &quot;Đều n&oacute;i rồng sinh ch&iacute;n con, đều c&oacute; kh&aacute;c biệt. Phụ vương cỡ n&agrave;o anh h&ugrave;ng nh&acirc;n vật, lại sinh ra ngươi c&aacute;i phế vật n&agrave;y, 16 tuổi, ngay cả &#39;Thần V&otilde; Ấn K&yacute;&#39; đều kh&ocirc;ng c&oacute; mở ra, Vương tộc mặt đều bị ngươi mất hết. Ngươi sống ở tr&ecirc;n đời n&agrave;y l&agrave;m g&igrave;? V&igrave; c&aacute;i g&igrave; kh&ocirc;ng chết đi đ&acirc;u?&quot;<br />\r\n<br />\r\nC&acirc;u n&oacute;i n&agrave;y n&oacute;i đến c&oacute; ch&uacute;t qua, nhưng l&agrave; ở đ&acirc;y mấy vị Vương tử tiếng l&ograve;ng.<br />\r\n<br />\r\nĐế vương gia th&acirc;n t&igrave;nh, nhất l&agrave; đơn bạc, giờ khắc n&agrave;y thể hiện đ&ecirc;́n ph&aacute;t huy v&ocirc; c&ugrave;ng tinh tế.<br />\r\n<br />\r\nTại C&ocirc;n L&ocirc;n Giới, c&oacute; thể mở ra &quot;Thần V&otilde; Ấn K&yacute;&quot; người cũng kh&ocirc;ng nhiều, mười người b&ecirc;n trong cũng nhiều nhất chỉ c&oacute; một c&aacute;i. C&oacute; thể n&oacute;i, mỗi một vị địa vị của v&otilde; giả đều cực cao.<br />\r\n<br />\r\nĐương nhi&ecirc;n đối với V&otilde; Đạo cường giả tới n&oacute;i, lại l&agrave; một chuyện kh&aacute;c. V&otilde; Đạo cao thủ huyết mạch cường đại, hậu thế cũng sẽ đem cường đại huyết mạch truyền thừa xuống, mở ra &quot;Thần V&otilde; Ấn K&yacute;&quot; x&aacute;c suất cũng liền lớn.<br />\r\n<br />\r\nV&acirc;n V&otilde; Quận Vương hết thảy c&oacute; chín c&aacute;i nhi tử, trong đ&oacute; t&aacute;m c&aacute;i đều mở ra &quot;Thần V&otilde; Ấn K&yacute;&quot;, duy chỉ c&oacute; Trương Nhược Trần, đ&atilde; 16 tuổi, nhưng như cũ kh&ocirc;ng c&oacute; mở ra &quot;Thần V&otilde; Ấn K&yacute;&quot;, biến th&agrave;nh Vương tộc tr&ograve; cười.<br />\r\n<br />\r\nRất nhiều người đều cười xưng l&agrave; hắn &quot;Hổ phụ khuyển tử&quot; .<br />\r\n<br />\r\nThậm ch&iacute; trong triều c&ograve;n c&oacute; một số tin đồn, c&ocirc;ng bố Trương Nhược Trần cũng kh&ocirc;ng phải l&agrave; V&acirc;n V&otilde; Quận Vương con ruột. Mặc d&ugrave; chỉ l&agrave; lời đồn đại, nhưng cũng để trong vương tộc người tr&ecirc;n mặt kh&ocirc;ng &aacute;nh s&aacute;ng.<br />\r\n<br />\r\nCho n&ecirc;n, kh&aacute;c những c&aacute;i kia Vương tử, phần lớn đều cho rằng Trương Nhược Trần l&agrave; Vương tộc sỉ nhục, căn bản kh&ocirc;ng c&oacute; đem hắn xem như th&acirc;n huynh đệ, hận kh&ocirc;ng thể hắn sớm đi chết mất.<br />\r\n<br />\r\nMấy năm gần đ&acirc;y, liền ngay cả V&acirc;n V&otilde; Quận Vương đều c&oacute; ch&uacute;t xa l&aacute;nh Trương Nhược Trần c&ugrave;ng L&acirc;m Phi. Tại phi tần kh&aacute;c c&ugrave;ng Vương tử xa l&aacute;nh dưới, tại đ&ecirc;m qua, Trương Nhược Trần c&ugrave;ng L&acirc;m Phi rốt cục bị đuổi ra chủ điện, bị &eacute;p dọn đi Thi&ecirc;n Điện.<br />\r\n<br />\r\nTrương Nhược Trần chỉ l&agrave; y&ecirc;n lặng đứng ở nơi đ&oacute;, cũng kh&ocirc;ng để &yacute; tới Ngũ vương tử c&ugrave;ng B&aacute;t vương tử.<br />\r\n<br />\r\nTại kh&ocirc;ng c&oacute; đủ thực lực trước đ&oacute; , bất kỳ c&aacute;i g&igrave; miệng lưỡi chi tranh, sẽ chỉ l&agrave;m người kh&aacute;c c&agrave;ng th&ecirc;m xem nhẹ ngươi.<br />\r\n<br />\r\nL&acirc;m Phi c&ugrave;ng kh&aacute;c những c&aacute;i kia Vương phi đứng chung một chỗ, nh&igrave;n thấy c&oacute; thụ xa l&aacute;nh Trương Nhược Trần, đau l&ograve;ng kh&ocirc;ng th&ocirc;i, nhưng l&agrave;, lại kh&ocirc;ng c&oacute; biện ph&aacute;p n&agrave;o.<br />\r\n<br />\r\n&quot;Tế tự đại điển, bắt đầu!&quot;<br />\r\n<br />\r\nV&acirc;n V&otilde; Quận Quốc Quốc sư đứng tại ph&iacute;a tr&ecirc;n tế đ&agrave;n, bưng lấy một quyển Ch&uacute;c Văn, đọc diễn cảm.<br />\r\n<br />\r\nSau đ&oacute;, ph&iacute;a tr&ecirc;n tế đ&agrave;n, thổi l&ecirc;n đại ph&aacute;p loa, đại h&agrave;o gi&aacute;c, từng vị y phục rực rỡ thướt tha cung nữ g&otilde; vang kh&aacute;nh, chu&ocirc;ng nhạc, chi&ecirc;ng chu&ocirc;ng mười s&aacute;u loại nhạc kh&iacute;.<br />\r\n<br />\r\nTiếp theo, ch&eacute;m giết s&uacute;c vật, lấy huyết tế trời.<br />\r\n<br />\r\n&quot;Hoa &mdash;&mdash; &quot;<br />\r\n<br />\r\nNồng đậm huyết kh&iacute;, h&oacute;a th&agrave;nh một c&acirc;y tr&aacute;ng kiện cột s&aacute;ng, bay thẳng thi&ecirc;n khung, đem tầng m&acirc;y đ&aacute;nh n&aacute;t, x&ocirc;ng v&agrave;o m&ecirc;nh m&ocirc;ng thi&ecirc;n khung.<br />\r\n<br />\r\nĐột nhi&ecirc;n, một hạt tinh quang từ thi&ecirc;n ngoại bay thấp xuống tới, rơi xuống một c&aacute;i 6 tuổi tiểu nam h&agrave;i mi t&acirc;m, c&ugrave;ng tiểu nam h&agrave;i th&acirc;n thể h&ograve;a l&agrave;m một thể, h&oacute;a th&agrave;nh một c&aacute;i x&iacute;ch hồng sắc &quot;Thần V&otilde; Ấn K&yacute;&quot; .<br />\r\n<br />\r\nTrong đ&aacute;m người, ph&aacute;t ra một tiếng kinh h&ocirc;, &quot;Tiết đ&ocirc; thống ấu tử, mới 6 tuổi liền mở ra Thần V&otilde; Ấn K&yacute;!&quot;<br />\r\n<br />\r\n&quot;X&iacute;ch Diễm Thần V&otilde; Ấn K&yacute;, thuộc về Tứ phẩm Thần V&otilde; Ấn K&yacute;. Qu&aacute; lợi hại, tương lai tiền đồ v&ocirc; lượng!&quot;<br />\r\n<br />\r\nThần V&otilde; Ấn K&yacute; cũng chia phẩm cấp, từ Nhất phẩm đến Cửu phẩm.<br />\r\n<br />\r\nNhất phẩm Thần V&otilde; Ấn K&yacute; nhược tiểu nhất, Cửu phẩm Thần V&otilde; Ấn K&yacute; cường đại nhất.<br />\r\n<br />\r\nTất cả mọi người d&ugrave;ng &aacute;nh mắt h&acirc;m mộ, nh&igrave;n chằm chằm một c&aacute;i kia 6 tuổi tiểu nam h&agrave;i.<br />\r\n<br />\r\n6 tuổi liền mở ra Tứ phẩm Thần V&otilde; Ấn K&yacute;, c&oacute; thể xưng thi&ecirc;n chi ki&ecirc;u tử, th&agrave;nh tựu tương lai tuyệt đối kh&ocirc;ng thấp.<br />\r\n<br />\r\nV&acirc;n V&otilde; Quận Quốc đ&ocirc;ng đảo v&otilde; tướng b&ecirc;n trong, một người d&aacute;ng dấp kh&ocirc;i ng&ocirc; nam tử vỗ bộ ngực, cao giọng cười to, hưng phấn kh&ocirc;ng th&ocirc;i, &quot;Tốt! Kh&ocirc;ng hổ l&agrave; ta Tiết Lượng nhi tử, đ&ecirc;m nay đ&ocirc; thống phủ b&agrave;y yến, c&aacute;c vị nhưng nhất định phải tới đến dự. Ha ha!&quot;<br />\r\n<br />\r\n&quot;Xoạt!&quot;<br />\r\n<br />\r\nTr&ecirc;n bầu trời, lại bay xuống v&ocirc; số tinh quang, rơi v&agrave;o từng vị thiếu ni&ecirc;n, thiếu nữ mi t&acirc;m, h&oacute;a th&agrave;nh h&oacute;a th&agrave;nh từng c&aacute;i Thần V&otilde; Ấn K&yacute;.<br />\r\n<br />\r\nTrong đ&oacute;, lấy Nhất phẩm Thần V&otilde; Ấn K&yacute; nhiều nhất, c&oacute; thể mở ra Nhị phẩm Thần V&otilde; Ấn K&yacute; thiếu ni&ecirc;n đều &iacute;t c&agrave;ng th&ecirc;m &iacute;t, người lợi hại nhất, vẫn như cũ l&agrave; vị n&agrave;o Tiết đ&ocirc; thống nhi tử, Tứ phẩm Thần V&otilde; Ấn K&yacute;, l&agrave;m cho tất cả mọi người đều theo kh&ocirc;ng kịp.<br />\r\n<br />\r\nMở ra Thần V&otilde; Ấn K&yacute; người, d&ugrave; sao cũng l&agrave; số &iacute;t, đại kh&aacute;i chỉ c&oacute; tổng số người một phần mười. Mỗi người đều hưng phấn kh&ocirc;ng hiểu, rốt cục đạt được Thần Linh t&aacute;n th&agrave;nh, mở ra V&otilde; Đạo chi m&ocirc;n.<br />\r\n<br />\r\nNhững c&aacute;i kia kh&ocirc;ng c&oacute; mở ra Thần V&otilde; Ấn K&yacute; thiếu ni&ecirc;n c&ugrave;ng thiếu nữ, to&agrave;n bộ đều mười phần thất lạc, c&oacute; người c&agrave;ng l&agrave; kh&oacute;c rống nghẹn ng&agrave;o, c&oacute; thể n&oacute;i l&agrave; mấy nh&agrave; sung sướng mấy nh&agrave; sầu.<br />\r\n<br />\r\nMắt thấy tế tự liền muốn kết th&uacute;c, nhưng l&agrave;, Trương Nhược Trần nhưng như cũ kh&ocirc;ng c&oacute; mở ra Thần V&otilde; Ấn K&yacute;.<br />\r\n<br />\r\n16 tuổi đều kh&ocirc;ng c&oacute; mở ra Thần V&otilde; Ấn K&yacute;, cơ hồ liền l&agrave; kh&ocirc;ng thể n&agrave;o c&ograve;n c&oacute; thể mở ra Thần V&otilde; Ấn K&yacute;, sau n&agrave;y chỉ c&oacute; thể l&agrave;m một c&aacute;i b&igrave;nh thường người b&igrave;nh thường.<br />\r\n<br />\r\nTất cả mọi người đem hắn coi nhẹ, tựa như trong g&oacute;c một hạt bụi, căn bản kh&ocirc;ng nhận ch&uacute; &yacute;.<br />\r\n<br />\r\nL&acirc;m Phi ban đầu cũng &ocirc;m một tia huyễn tưởng, hi vọng m&igrave;nh h&agrave;i nhi c&oacute; thể s&aacute;ng tạo ra kỳ t&iacute;ch, mở ra Thần V&otilde; Ấn K&yacute;. Coi như kh&ocirc;ng thể trở th&agrave;nh V&otilde; Đạo cường giả, ch&iacute; &iacute;t c&oacute; thể cường th&acirc;n kiện thể, kh&ocirc;ng đến mức lại bị bệnh ma tra tấn.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n<br />\r\nTheo tế tự chuẩn bị kết th&uacute;c, L&acirc;m Phi hi vọng, lần nữa biến th&agrave;nh thất vọng, thậm ch&iacute; l&agrave; tuyệt vọng.<br />\r\n<br />\r\nNgay tại Trương Nhược Trần đều cho l&agrave; m&igrave;nh kh&ocirc;ng c&aacute;ch n&agrave;o mở ra Thần V&otilde; Ấn K&yacute; thời điểm, bị hắn nắm chặt trong tay m&agrave;u trắng Tinh Thạch, hơi s&aacute;ng một c&aacute;i.<br />\r\n<br />\r\nTại tế tự kết th&uacute;c trước tốt nhất một c&aacute;i s&aacute;t na, một hạt tinh quang, từ tr&ecirc;n trời gi&aacute;ng xuống, rơi xuống Trương Nhược Trần mi t&acirc;m, h&oacute;a th&agrave;nh một c&aacute;i m&agrave;u trắng h&igrave;nh tr&ograve;n Thần V&otilde; Ấn K&yacute;.<br />\r\n<br />\r\n&quot;Xoạt!&quot;<br />\r\n<br />\r\nMột cỗ n&oacute;ng rực cảm gi&aacute;c, từ mi t&acirc;m truyền đến, chợt truyền khắp to&agrave;n th&acirc;n.<br />\r\n<br />\r\nMở ra!<br />\r\n<br />\r\nTrương Nhược Trần hưng phấn v&ocirc; c&ugrave;ng, rốt cục mở ra Thần V&otilde; Ấn K&yacute;.<br />\r\n<br />\r\nChỉ cần mở ra Thần V&otilde; Ấn K&yacute; liền tốt, cho d&ugrave; l&agrave; Nhất phẩm Thần V&otilde; Ấn K&yacute;, hắn cũng kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o th&egrave;m quan t&acirc;m.<br />\r\n<br />\r\nL&uacute;c đầu kh&ocirc;ng c&oacute; người ch&uacute; &yacute; Trương Nhược Trần, nhưng l&agrave;, tại Trương Nhược Trần mở ra Thần V&otilde; Ấn K&yacute; một s&aacute;t na kia, lại hấp dẫn &aacute;nh mắt mọi người.<br />\r\n<br />\r\n&quot;Đ&acirc;y kh&ocirc;ng phải l&agrave; Cửu vương tử, hắn đều 16 tuổi, m&agrave; lại người yếu nhiều bệnh, thế m&agrave; c&ograve;n c&oacute; thể mở ra Thần V&otilde; Ấn K&yacute;!&quot; Rất nhiều người đều lấy một loại &aacute;nh mắt kh&ocirc;ng thể tin nh&igrave;n chằm chằm Trương Nhược Trần, cảm thấy rất kh&ocirc;ng thể tưởng tượng nổi.<br />\r\n<br />\r\nĐứng tại Trương Nhược Trần b&ecirc;n cạnh Lục vương tử c&ugrave;ng B&aacute;t vương tử cũng mở to hai mắt nh&igrave;n, lộ ra vẻ kinh dị.<br />\r\n<br />\r\nC&aacute;i n&agrave;y sao c&oacute; thể?<br />\r\n<br />\r\nL&acirc;m Phi hướng về Trương Nhược Trần nh&igrave;n sang, nh&igrave;n thấy Trương Nhược Trần mi t&acirc;m Thần V&otilde; Ấn K&yacute; về sau, mừng rỡ nước mắt chảy xuống, lập tức vọt tới Trương Nhược Trần trước mặt, đem Trương Nhược Trần nhục th&acirc;n th&acirc;n thể &ocirc;m chặt lấy, &quot;Trần Nhi, ngươi rốt cục l&agrave;m được! L&agrave;m được!&quot;<br />\r\n<br />\r\nV&acirc;n V&otilde; Quận Vương b&ecirc;n người một vị l&atilde;o th&aacute;i gi&aacute;m, đi đến Trương Nhược Trần trước mặt, cười nhẹ nh&agrave;ng m&agrave; n&oacute;i: &quot;Ch&uacute;c mừng L&acirc;m Phi nương nương, ch&uacute;c mừng Cửu vương tử điện hạ mở ra Thần V&otilde; Ấn K&yacute;! Vương hậu để l&atilde;o n&ocirc; đến xin Cửu vương tử điện hạ đi qua, n&agrave;ng muốn đ&iacute;ch th&acirc;n kiểm trắc Cửu vương tử điện hạ mở ra Thần V&otilde; Ấn K&yacute; phẩm cấp!&quot;<br />\r\n<br />\r\n&quot;Vương hậu!&quot;<br />\r\n<br />\r\nL&acirc;m Phi nụ cười tr&ecirc;n mặt lập tức cứng đờ, c&oacute; ch&uacute;t khẩn trương đem Trương Nhược Trần bảo hộ ở sau lưng.<br />\r\n<br />\r\n&quot;Mẫu th&acirc;n, ch&uacute;ng ta đi gặp Vương hậu đi!&quot;<br />\r\n<br />\r\nTrương Nhược Trần ph&aacute;t gi&aacute;c được L&acirc;m Phi biến h&oacute;a vi diệu, trong l&ograve;ng thầm nghĩ, xem ra c&aacute;i n&agrave;y Vương hậu nương nương kh&ocirc;ng phải thiện nh&acirc;n, được nhiều c&agrave;ng cẩn thận.</p>', '2021-11-27 19:50:50', 36, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(152, 43, 'Hoàng Cực Cảnh', '3', '<p>Vương hậu nương nương ngồi tại b&ecirc;n dưới tế đàn phương trong doanh trướng, đầu đội v&agrave;ng Kim Phượng quan, người kho&aacute;c Thải Phượng b&agrave;o, coi trọng l&ecirc;n 28, ch&iacute;n d&aacute;ng vẻ, t&uacute; lệ đoan trang, kh&ocirc;ng hề giống Trương Nhược Trần trong tưởng tượng như vậy gi&agrave; nua, ngược lại c&ograve;n cho người một loại kh&iacute; chất cao qu&yacute;.<br />\r\n<br />\r\nN&agrave;ng n&oacute;i: &quot;Đại vương tại Sinh Tử Điện bế quan tu luyện, lần n&agrave;y &#39;Tế tự đại điển&#39; l&agrave; từ bổn hậu chủ tr&igrave;. Quốc sư, kiểm trắc ra Cửu vương tử điện hạ mở ra Thần V&otilde; Ấn K&yacute; phẩm cấp sao?&quot;<br />\r\n<br />\r\nV&acirc;n V&otilde; Quận Quốc Quốc sư, trong tay bưng lấy một quyển thiết thư, lắc lắc, n&oacute;i: &quot;&laquo; Thần V&otilde; Ấn K&yacute; To&agrave;n S&aacute;ch &raquo; ph&iacute;a tr&ecirc;n ghi ch&eacute;p C&ocirc;n L&ocirc;n Giới từ trước tới nay tất cả xuất hiện qua v&otilde; đạo ấn k&yacute; đồ &aacute;n, từ Nhất phẩm v&otilde; đạo ấn k&yacute; đến Cửu phẩm v&otilde; đạo ấn k&yacute;, tất cả đều ghi lại ở. Tuy nhi&ecirc;n lại kh&ocirc;ng c&oacute; một c&aacute;i n&agrave;o ấn k&yacute; c&ugrave;ng Cửu vương tử điện hạ Thần V&otilde; Ấn K&yacute; tương xứng.&quot;<br />\r\n<br />\r\nVương hậu nương nương hướng về Trương Nhược Trần nh&igrave;n tho&aacute;ng qua, mang theo v&agrave;i phần đạm mạc, &quot;Tất nhi&ecirc;n kiểm trắc kh&ocirc;ng ra kết quả, đo&aacute;n chừng ch&iacute;nh l&agrave; kh&ocirc;ng c&oacute; phẩm cấp Thần V&otilde; Ấn K&yacute;. Tại kh&aacute;c Quận Quốc, cũng xuất hiện qua một ch&uacute;t mở ra kh&ocirc;ng biết Thần V&otilde; Ấn K&yacute; v&otilde; giả, th&agrave;nh tựu cuối c&ugrave;ng đều mười phần c&oacute; hạn.&quot;<br />\r\n<br />\r\nB&aacute;t vương tử cũng đứng tại trong doanh trướng, lập tức g&oacute;p lời, n&oacute;i: &quot;Vương hậu nương nương n&oacute;i kh&ocirc;ng sai, Cửu đệ d&ugrave; sao đ&atilde; 16 tuổi, bỏ qua tốt nhất tu luyện ni&ecirc;n kỷ, với hắn m&agrave; n&oacute;i, coi như mở ra Tứ phẩm Thần V&otilde; Ấn K&yacute;, Ngũ phẩm Thần V&otilde; Ấn K&yacute;, cũng sẽ kh&ocirc;ng c&oacute; c&aacute;i g&igrave; th&agrave;nh tựu.&quot;<br />\r\n<br />\r\nVương hậu nương nương nhẹ gật đầu, đồng &yacute; B&aacute;t vương tử, n&oacute;i: &quot;Tất nhi&ecirc;n Cửu vương tử mở ra chỉ l&agrave; kh&ocirc;ng c&oacute; phẩm cấp Thần V&otilde; Ấn K&yacute;, m&agrave; lại, cũng đ&atilde; 16 tuổi, v&igrave; tận lực tiết kiệm Vương tộc t&agrave;i nguy&ecirc;n tu luyện, cũng chỉ cho Cửu vương tử một phần Tẩy Tủy Dịch đi!&quot;<br />\r\n<br />\r\nL&acirc;m Phi sắc mặt biến đổi, n&oacute;i: &quot;Vương hậu nương nương, mở ra Thần V&otilde; Ấn K&yacute; năm thứ nhất, l&agrave; trọng yếu nhất một năm. Năm đ&oacute; Thất vương tử mở ra Thần V&otilde; Ấn K&yacute; về sau, hết thảy đạt được mười hai phần Tẩy Tủy Dịch, mỗi th&aacute;ng đều c&oacute; thể tẩy tủy một lần. Trần Nhi, v&igrave; sao chỉ c&oacute; thể đạt được một phần Tẩy Tủy Dịch?&quot;<br />\r\n<br />\r\nVương hậu nương nương lạnh giọng m&agrave; n&oacute;i: &quot;Thất vương tử tại l&uacute;c ba tuổi, liền mở ra Thất phẩm Thần V&otilde; Ấn K&yacute;. Cửu vương tử h&aacute; c&oacute; thể c&ugrave;ng Thất vương tử so s&aacute;nh?&quot;<br />\r\n<br />\r\nB&aacute;t vương tử nịnh nọt m&agrave; n&oacute;i: &quot;Thất ca, thế nhưng l&agrave; Vương hậu nương nương con trai trưởng, kế thừa Vương hậu nương nương cường đại huyết mạch, c&oacute; được Thất phẩm Thần V&otilde; Ấn K&yacute;, to&agrave;n bộ V&acirc;n V&otilde; Quận Quốc ai c&oacute; thể c&ugrave;ng Thất ca so s&aacute;nh? Tương lai V&acirc;n V&otilde; Quận Quốc c&oacute; thể hay kh&ocirc;ng c&agrave;ng th&ecirc;m hưng thịnh, c&agrave;ng th&ecirc;m cường đại, tất cả đều thắt ở Thất ca tr&ecirc;n người một người.&quot;<br />\r\n<br />\r\nHắn lại n&oacute;i: &quot;Bản vương tử n&oacute;i một c&acirc;u kh&ocirc;ng th&iacute;ch nghe, Thất ca một đầu ng&oacute;n tay, cũng so Cửu đệ t&iacute;nh mệnh trọng yếu gấp mười lần, gấp trăm lần, căn bản kh&ocirc;ng c&oacute; khả năng so s&aacute;nh.&quot;<br />\r\n<br />\r\nL&acirc;m Phi cắn chặt răng m&ocirc;i, tiếp tục v&igrave; Trương Nhược Trần tranh thủ, n&oacute;i: &quot;Thế nhưng l&agrave; B&aacute;t vương tử mở ra Thần V&otilde; Ấn K&yacute; thời điểm, cũng đ&atilde; nhận được bốn phần Tẩy Tủy Dịch.&quot;<br />\r\n<br />\r\nVương hậu nương nương mười phần kh&ocirc;ng vui n&oacute;i: &quot;Thi&ecirc;n ph&uacute; c&agrave;ng cao, lấy được t&agrave;i nguy&ecirc;n c&agrave;ng nhiều. Cửu vương tử thi&ecirc;n ph&uacute; thấp nhất, lấy được t&agrave;i nguy&ecirc;n tự nhi&ecirc;n c&agrave;ng &iacute;t.&quot;<br />\r\n<br />\r\n&quot;Thế nhưng l&agrave;...&quot; L&acirc;m Phi nói.<br />\r\n<br />\r\nVương hậu nương nương c&agrave;ng th&ecirc;m kh&ocirc;ng ki&ecirc;n nhẫn, trầm giọng n&oacute;i: &quot;Bổn hậu đ&atilde; l&agrave;m ra quyết định, L&acirc;m Phi, ngươi như c&ograve;n d&aacute;m n&oacute;i nhiều một c&acirc;u, tin hay kh&ocirc;ng bổn hậu để cho người ta đưa ngươi lần nữa k&eacute;o ra ngo&agrave;i trượng chứ?&quot;<br />\r\n<br />\r\n&quot;Lần nữa...&quot; Trương Nhược Trần nghe được Vương hậu nương nương, th&ocirc;ng suốt ngẩng đầu l&ecirc;n.<br />\r\n<br />\r\nChẳng lẽ Vương hậu nương nương đ&atilde; từng trượng tr&aacute;ch qua mẫu th&acirc;n?<br />\r\n<br />\r\nL&acirc;m Phi nghe được Vương hậu nương nương lời n&oacute;i về sau, to&agrave;n th&acirc;n khẽ run l&ecirc;n, giống như l&agrave; nhớ lại c&aacute;i g&igrave; đ&aacute;ng sợ sự t&igrave;nh, lập tức ngậm miệng lại.<br />\r\n<br />\r\nGiờ ph&uacute;t n&agrave;y, đ&atilde; c&oacute; người đem một phần Tẩy Tủy Dịch đưa tới, đưa tới Trương Nhược Trần trong tay.<br />\r\n<br />\r\nTrương Nhược Trần tiếp nhận Tẩy Tủy Dịch, liền đi tới L&acirc;m Phi b&ecirc;n người, xoay người thật s&acirc;u nh&igrave;n chằm chằm một ch&uacute;t ngồi ở ph&iacute;a tr&ecirc;n Vương hậu nương nương, n&oacute;i: &quot;Mẫu th&acirc;n, ch&uacute;ng ta trở về đi!&quot;<br />\r\n<br />\r\n&quot;Ừm!&quot; L&acirc;m Phi nhẹ nh&agrave;ng m&iacute;m m&ocirc;i, đắng chát nhẹ gật đầu.<br />\r\n<br />\r\nVương hậu nương nương nh&igrave;n chằm chằm Trương Nhược Trần c&ugrave;ng L&acirc;m Phi b&oacute;ng lưng rời đi, n&oacute;i: &quot;Ba th&aacute;ng về sau, liền l&agrave; Vương tộc cuối năm khảo hạch, Cửu vương tử nhưng nhất định phải cố gắng tu luyện, tranh thủ tại trong v&ograve;ng ba th&aacute;ng ho&agrave;n th&agrave;nh &#39;Tẩy tủy tr&ugrave;ng mạch&#39;, trở th&agrave;nh một t&ecirc;n ch&acirc;n ch&iacute;nh v&otilde; giả. Đến l&uacute;c đ&oacute;, Đại vương cũng sẽ xuất quan, hi vọng ngươi c&oacute; thể mang cho hắn kinh hỉ.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\nLập tức lại vang l&ecirc;n B&aacute;t vương tử khinh thường thanh &acirc;m, cười n&oacute;i: &quot;Cho d&ugrave; c&oacute; ba phần Tẩy Tủy Dịch, cũng kh&ocirc;ng c&oacute; khả năng tại trong v&ograve;ng ba th&aacute;ng ho&agrave;n th&agrave;nh tẩy tủy tr&ugrave;ng mạch. Ta đều hao tốn thời gian nửa năm mới l&agrave;m đến, lấy Cửu đệ tư chất, đo&aacute;n chừng phải hao ph&iacute; một năm trở l&ecirc;n, mới c&oacute; cơ hội. Ng&acirc;n ng&acirc;n!&quot;<br />\r\n<br />\r\nTrương Nhược Trần cũng kh&ocirc;ng quay đầu, nắm thật chặt nắm đấm, &yacute; niệm trong l&ograve;ng mười phần ki&ecirc;n định, &quot;C&aacute;c ngươi liền đợi đến nh&igrave;n đi, ta tuyệt đối sẽ kh&ocirc;ng để cho c&aacute;c ngươi thất vọng!&quot;<br />\r\n<br />\r\nTrở lại Tử Di Thi&ecirc;n Điện, Trương Nhược Trần liền kh&ocirc;ng kịp chờ đợi đ&oacute;ng cửa ph&ograve;ng, lập tức tu luyện.<br />\r\n<br />\r\nMặc d&ugrave; kh&ocirc;ng biết mở ra l&agrave; c&aacute;i g&igrave; Thần V&otilde; Ấn K&yacute;, nhưng l&agrave;, tất nhi&ecirc;n c&oacute; được Thần V&otilde; Ấn K&yacute;, liền khẳng định c&oacute; thể tu luyện ra ch&acirc;n kh&iacute;.<br />\r\n<br />\r\nĐầu ti&ecirc;n, bước đầu ti&ecirc;n, tại Thần V&otilde; Ấn K&yacute; ph&iacute;a dưới, mở ra &quot;Kh&iacute; Tr&igrave;&quot; .<br />\r\n<br />\r\nC&aacute;i gọi l&agrave; &quot;Kh&iacute; Tr&igrave;&quot;, liền l&agrave; dung nạp ch&acirc;n kh&iacute; ao.<br />\r\n<br />\r\nAo c&agrave;ng lớn, dung nạp ch&acirc;n kh&iacute; c&agrave;ng nhiều. Theo tu vi tăng l&ecirc;n, &quot;Kh&iacute; Tr&igrave;&quot; cũng sẽ dần dần mở rộng.<br />\r\n<br />\r\nN&oacute;i như vậy, muốn mở &quot;Kh&iacute; Tr&igrave;&quot;, nhất định phải tại trưởng bối chỉ điểm xuống tiến h&agrave;nh.<br />\r\n<br />\r\nĐối với c&oacute; được phong ph&uacute; kinh nghiệm tu luyện Trương Nhược Trần tới n&oacute;i, mở &quot;Kh&iacute; Tr&igrave;&quot; cũng kh&ocirc;ng phải l&agrave; việc kh&oacute;, vẻn vẹn chỉ l&agrave; tốn hao nửa canh giờ, hắn liền th&agrave;nh c&ocirc;ng mở ra &quot;Kh&iacute; Tr&igrave;&quot; .<br />\r\n<br />\r\nĐối với tu sĩ kh&aacute;c tới n&oacute;i, &iacute;t nhất cũng phải tốn hao mấy ng&agrave;y thời gian tiến h&agrave;nh nếm thử, mới c&oacute; thể đem &quot;Kh&iacute; Tr&igrave;&quot; mở ra tới.<br />\r\n<br />\r\nTrương Nhược Trần lại chỉ d&ugrave;ng nửa canh giờ.<br />\r\n<br />\r\n&quot;Cỗ th&acirc;n thể n&agrave;y quả nhi&ecirc;n rất nhỏ yếu, cũng chỉ l&agrave; mở ra lớn chừng c&aacute;i trứng g&agrave; một c&aacute;i Kh&iacute; Tr&igrave;, c&oacute; thể dung nạp ch&acirc;n kh&iacute; qu&aacute; &iacute;t!&quot;<br />\r\n<br />\r\nMở ra &quot;Kh&iacute; Tr&igrave;&quot;, c&ograve;n xa xa kh&ocirc;ng đủ.<br />\r\n<br />\r\nKh&iacute; Tr&igrave;, chỉ l&agrave; chứa đựng ch&acirc;n kh&iacute; địa phương.<br />\r\n<br />\r\nCh&acirc;n kh&iacute; muốn tại to&agrave;n th&acirc;n vận h&agrave;nh, nhất định phải mở ra thuộc về m&igrave;nh tu luyện kinh mạch.<br />\r\n<br />\r\nChỉ c&oacute; ho&agrave;n th&agrave;nh một bước n&agrave;y, mới c&oacute; thể c&oacute; được ch&acirc;n kh&iacute;, trở th&agrave;nh một t&ecirc;n ch&acirc;n ch&iacute;nh v&otilde; giả.<br />\r\n<br />\r\nSau đ&oacute; liền l&agrave; bước thứ hai, tẩy tủy tr&ugrave;ng mạch.<br />\r\n<br />\r\nTrương Nhược Trần lấy ra một con kia chứa Tẩy Tủy Dịch b&igrave;nh ngọc nhỏ, d&ugrave;ng c&aacute;i mũi nhẹ nh&agrave;ng h&iacute;t h&agrave;, x&aacute;c định l&agrave; thật Tẩy Tủy Dịch về sau, trực tiếp đem trọn b&igrave;nh Tẩy Tủy Dịch r&oacute;t v&agrave;o miệng b&ecirc;n trong.<br />\r\n<br />\r\nTẩy Tủy Dịch v&agrave;o cổ họng về sau, mười phần lạnh buốt, nhưng l&agrave;, vẻn vẹn chỉ l&agrave; đi qua một c&aacute;i s&aacute;t na, c&aacute;i kia một cỗ băng l&atilde;nh liền biến mất kh&ocirc;ng thấy, thay v&agrave;o đ&oacute; l&agrave; một cỗ n&oacute;ng hổi nhiệt độ cao.<br />\r\n<br />\r\nTựa như c&oacute; một đ&aacute;m lửa, tại thể nội thi&ecirc;u đốt!<br />\r\n<br />\r\nTừng tia hỏa diễm, tiến v&agrave;o Trương Nhược Trần to&agrave;n th&acirc;n mỗi một chỗ kinh mạch, sau đ&oacute;, dung nhập kinh mạch.<br />\r\n<br />\r\nCh&iacute;nh l&agrave; c&aacute;i n&agrave;y thời điểm!<br />\r\n<br />\r\n&quot;&laquo; Cửu Thi&ecirc;n Minh Đế Kinh &raquo;, tầng thứ nhất, Th&aacute;i Ho&agrave;ng Ho&agrave;ng Cảnh Thi&ecirc;n.&quot;<br />\r\n<br />\r\nTu luyện kh&aacute;c biệt c&ocirc;ng ph&aacute;p, mở ra đến kinh mạch lộ tuyến tự nhi&ecirc;n kh&ocirc;ng giống.<br />\r\n<br />\r\nC&agrave;ng l&agrave; cao th&acirc;m c&ocirc;ng ph&aacute;p, kinh mạch lộ tuyến th&igrave; c&agrave;ng nhiều, c&agrave;ng phức tạp, c&agrave;ng sợ kỳ.<br />\r\n<br />\r\n&laquo; Cửu Thi&ecirc;n Minh Đế Kinh &raquo; vận h&agrave;nh kinh mạch, hết thảy nhiều đến 36 đầu.<br />\r\n<br />\r\nĐương nhi&ecirc;n, Trương Nhược Trần hiện tại cũng kh&ocirc;ng cần đem tất cả kinh mạch to&agrave;n bộ mở ra đến, chỉ cần mở ra đầu thứ nhất kinh mạch, liền xem như ho&agrave;n th&agrave;nh tẩy tủy tr&ugrave;ng mạch.<br />\r\n<br />\r\n&quot;Bọn hắn n&oacute;i ta tốn hao ba th&aacute;ng thời điểm cũng kh&ocirc;ng thể ho&agrave;n th&agrave;nh tẩy tủy tr&ugrave;ng mạch, nhưng l&agrave;, ta sẽ d&ugrave;ng h&agrave;nh động thực tế chứng minh, đ&ecirc;m nay ta liền c&oacute; thể ho&agrave;n th&agrave;nh tẩy tủy tr&ugrave;ng mạch, trở th&agrave;nh một t&ecirc;n ch&acirc;n ch&iacute;nh v&otilde; giả!&quot;<br />\r\n<br />\r\nTrương Nhược Trần mượn nhờ ở kiếp trước kinh nghiệm tu luyện, lợi dụng chỉ c&oacute; một sợi ch&acirc;n kh&iacute;, to&agrave;n lực tr&ugrave;ng mạch, rất nhanh liền đem đầu thứ nhất kinh mạch đả th&ocirc;ng hơn ph&acirc;n nửa.<br />\r\n<br />\r\nMột cỗ đau đớn kịch liệt, từ thể nội truyền đến, tựa như kinh mạch muốn bị x&eacute; đứt, để Trương Nhược Trần to&agrave;n th&acirc;n run rẩy một c&aacute;i.<br />\r\n<br />\r\nNếu l&agrave; những người kh&aacute;c, gặp phải t&igrave;nh huống như vậy, khẳng định đ&atilde; tr&ugrave;ng mạch thất bại.<br />\r\n<br />\r\nNhưng l&agrave;, Trương Nhược Trần lại cắn chặt h&agrave;m răng, lấy &yacute; ch&iacute; cường đại lực ki&ecirc;n tr&igrave; nổi , mặc cho mồ h&ocirc;i kh&ocirc;ng ngừng rớt xuống đất, từ đầu đến cuối kh&ocirc;ng bu&ocirc;ng bỏ.<br />\r\n<br />\r\n&quot;Chỉ k&eacute;m... Cuối c&ugrave;ng một đoạn ngắn...&quot;<br />\r\n<br />\r\nHi vọng đang ở trước mắt, nhất định phải tr&ugrave;ng mạch th&agrave;nh c&ocirc;ng!<br />\r\n<br />\r\nNhất định phải th&agrave;nh c&ocirc;ng!<br />\r\n<br />\r\nTrương Nhược Trần nhất cổ t&aacute;c kh&iacute;, l&agrave; số kh&ocirc;ng nhiều ch&acirc;n kh&iacute; to&agrave;n bộ tr&ugrave;ng k&iacute;ch đi qua.<br />\r\n<br />\r\n&quot;Oanh!&quot;<br />\r\n<br />\r\nMột tiếng vang thật lớn từ thể nội truyền đến, ph&aacute;t ra thanh &acirc;m điếc tai nhức &oacute;c, k&eacute;m một ch&uacute;t đem Trương Nhược Trần chấn động đến ngất đi.<br />\r\n<br />\r\nC&aacute;i kia một cỗ cảm gi&aacute;c đau đớn biến mất về sau, chỉ cảm thấy một cỗ lạnh buốt kh&iacute; lưu ở trong kinh mạch lưu động, cảm gi&aacute;c đau đớn dần dần biến mất, một cỗ trước nay chưa c&oacute; sảng kho&aacute;i truyền khắp to&agrave;n th&acirc;n.<br />\r\n<br />\r\n&quot;Ha ha! Th&agrave;nh c&ocirc;ng! Th&agrave;nh c&ocirc;ng ho&agrave;n th&agrave;nh tẩy tủy tr&ugrave;ng mạch, trở th&agrave;nh một t&ecirc;n ch&acirc;n ch&iacute;nh v&otilde; giả!&quot;<br />\r\n<br />\r\nB&aacute;t vương tử ho&agrave;n th&agrave;nh một bước n&agrave;y, phục dụng bốn phần Tẩy Tủy Dịch, r&ograve;ng r&atilde; hao tốn thời gian nửa năm.<br />\r\n<br />\r\nTrương Nhược Trần vẻn vẹn chỉ l&agrave; d&ugrave;ng một đ&ecirc;m, cũng chỉ phục dụng một phần Tẩy Tủy Dịch.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\nHo&agrave;n th&agrave;nh tẩy tủy tr&ugrave;ng mạch, liền l&agrave; &#39;Ho&agrave;ng Cực Cảnh sơ kỳ&#39; tu vi.<br />\r\n<br />\r\nV&otilde; Đạo tu luyện, chia l&agrave;m &quot;V&agrave;ng, huyền,, trời&quot; tứ đại cảnh giới, tức: Ho&agrave;ng Cực Cảnh, Huyền Cực Cảnh, Địa Cực Cảnh, Thi&ecirc;n Cực Cảnh.<br />\r\n<br />\r\nMỗi c&aacute;i đại cảnh giới, chia l&agrave;m bảy c&aacute;i tiểu cảnh giới, chia l&agrave;m l&agrave;: Sơ kỳ, trung kỳ, hậu kỳ, tiểu cực vị, trung cực vị, đại cực vị, đại vi&ecirc;n m&atilde;n.<br />\r\n<br />\r\nTứ đại bảy nhỏ.<br />\r\n<br />\r\nTrương Nhược Trần hiện tại liền l&agrave; Ho&agrave;ng Cực Cảnh sơ kỳ tu vi.<br />\r\n<br />\r\nHo&agrave;ng Cực Cảnh &quot;Sơ kỳ&quot;, &quot;Trung kỳ&quot;, &quot;Hậu kỳ&quot;, mở kinh mạch, ch&acirc;n kh&iacute; b&igrave;nh thể, l&agrave;m lực lượng của th&acirc;n thể trở n&ecirc;n c&agrave;ng ng&agrave;y c&agrave;ng mạnh, mỗi tăng l&ecirc;n một c&aacute;i tiểu cảnh giới, lực lượng liền sẽ tăng cường một mảng lớn.<br />\r\n<br />\r\nB&aacute;t vương tử liền l&agrave; Ho&agrave;ng Cực Cảnh hậu kỳ tu vi, c&oacute; thể tay kh&ocirc;ng giơ l&ecirc;n nặng ba trăm c&acirc;n bàn đá, đem bàn đá n&eacute;m ra xa mười trượng. Hắn đ&aacute;nh ra một quyền lực lượng, c&oacute; thể so với một đầu Man Ngưu lực lượng.<br />\r\n<br />\r\nHo&agrave;ng Cực Cảnh hậu kỳ v&otilde; giả, phần lớn đều c&oacute; thể tu luyện ra &quot;Nhất ngưu chi lực&quot; .<br />\r\n<br />\r\nTại &quot;Hậu kỳ&quot; ph&iacute;a tr&ecirc;n, liền l&agrave; &quot;Tiểu cực vị&quot;, &quot;Trung cực vị&quot;, &quot;Đại cực vị&quot;, &quot;Đại vi&ecirc;n m&atilde;n&quot; bốn c&aacute;i cảnh giới.<br />\r\n<br />\r\nĐột ph&aacute; Ho&agrave;ng Cực Cảnh hậu kỳ, liền c&oacute; thể đạt tới Ho&agrave;ng Cực Cảnh tiểu cực vị, lập tức liền c&oacute; thể c&oacute; được &quot;Tứ ngưu chi lực&quot;, lực lượng lật ra bốn lần.<br />\r\n<br />\r\nCho n&ecirc;n, Ho&agrave;ng Cực Cảnh tiểu cực vị v&otilde; giả, đối đ&acirc;̀u Ho&agrave;ng Cực Cảnh hậu kỳ v&otilde; giả, đơn giản tựa như đại nh&acirc;n đối đ&acirc;̀u tiểu h&agrave;i, một người đ&aacute;nh mười người, cũng ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; vấn đề.<br />\r\n<br />\r\nLại hướng l&ecirc;n &quot;Trung cực vị&quot;, &quot;Đại cực vị&quot;, &quot;Đại vi&ecirc;n m&atilde;n&quot;, c&agrave;ng khủng bố hơn, c&oacute; được thường nh&acirc;n kh&oacute; c&oacute; thể tưởng tượng lực lượng.<br />\r\n<br />\r\nNhững cảnh giới kia c&aacute;ch Trương Nhược Trần c&ograve;n rất xa x&ocirc;i, b&acirc;y giờ căn bản kh&ocirc;ng cần suy nghĩ nhiều, ổn tr&aacute;t ổn đả tu luyện mới l&agrave; mấu chốt, một bước một c&aacute;i dấu ch&acirc;n, mới c&oacute; thể đi được c&agrave;ng xa.<br />\r\n<br />\r\nB&aacute;t vương tử tu luyện t&aacute;m năm, cũng mới đạt tới Ho&agrave;ng Cực Cảnh hậu kỳ, chỉ c&oacute; thể coi l&agrave; phổ Th&ocirc;ng Thi&ecirc;n ph&uacute;.<br />\r\n<br />\r\n&quot;B&aacute;t vương tử tốn hao thời gian nửa năm, mới ho&agrave;n th&agrave;nh tẩy tủy tr&ugrave;ng mạch. Ta chỉ d&ugrave;ng một đ&ecirc;m thời gian.&quot;<br />\r\n<br />\r\n&quot;B&aacute;t vương tử hao tốn thời gian t&aacute;m năm, mới tu luyện đến Ho&agrave;ng Cực Cảnh hậu kỳ. Ta nhất định phải tại trong v&ograve;ng ba th&aacute;ng, ho&agrave;n th&agrave;nh c&aacute;i mục ti&ecirc;u n&agrave;y. Đối với ta m&agrave; n&oacute;i, cũng kh&ocirc;ng phải l&agrave; kh&ocirc;ng c&oacute; cơ hội l&agrave;m đến.&quot;<br />\r\n<br />\r\nBa th&aacute;ng về sau, liền l&agrave; Vương tộc cuối năm khảo hạch, tất cả Vương tộc tuổi trẻ v&otilde; giả, bao qu&aacute;t c&aacute;c vị Vương tử, quận ch&uacute;a, ho&agrave;ng th&acirc;n quốc th&iacute;ch, to&agrave;n bộ đều sẽ tham gia khảo hạch, kiểm nghiệm một năm đ&atilde; qua th&agrave;nh quả tu luyện.<br />\r\n<br />\r\nTại trong vương tộc, muốn thu hoạch được địa vị, muốn được người t&ocirc;n trọng, muốn thu hoạch được c&agrave;ng nhiều t&agrave;i nguy&ecirc;n tu luyện, nhất định phải chứng minh thực lực của m&igrave;nh.<br />\r\n<br />\r\nBa th&aacute;ng!<br />\r\n<br />\r\nTrong v&ograve;ng ba th&aacute;ng, nhất định phải tu luyện tới Ho&agrave;ng Cực Cảnh hậu kỳ, coi như kh&ocirc;ng v&igrave; m&igrave;nh, cũng phải v&igrave; vị kia quan t&acirc;m c&ugrave;ng y&ecirc;u thương mẹ ruột của m&igrave;nh, v&igrave; n&agrave;ng tranh thủ đến thuộc về n&agrave;ng t&ocirc;n nghi&ecirc;m.</p>', '2021-11-27 19:59:36', 15, 1),
(153, 43, 'Thời Không Bí Điển', '4', '<p>Đạt tới Ho&agrave;ng Cực Cảnh sơ kỳ, &quot;Kh&iacute; Tr&igrave;&quot; biến lớn mấy chục lần, chừng một c&aacute;i b&oacute;ng rổ lớn như vậy.<br />\r\n<br />\r\nKh&iacute; Tr&igrave; b&ecirc;n trong ch&acirc;n kh&iacute; vẫn như cũ rất thưa thớt, chỉ c&oacute; trứng g&agrave; lớn như vậy một đo&agrave;n, kh&ocirc;ng đến Kh&iacute; Tr&igrave; dung lượng một phần mười.<br />\r\n<br />\r\nChỉ c&oacute; đem Kh&iacute; Tr&igrave; b&ecirc;n trong ch&acirc;n kh&iacute; tu đ&acirc;̀y, mới c&oacute; thể tr&ugrave;ng k&iacute;ch v&agrave;o một cảnh giới, Ho&agrave;ng Cực Cảnh trung kỳ.<br />\r\n<br />\r\nTrương Nhược Trần khoanh ch&acirc;n ngồi xuống, hai mắt nhắm lại, mi t&acirc;m Thần V&otilde; Ấn K&yacute; mở ra, bắt đầu tu luyện &laquo; Cửu Thi&ecirc;n Minh Đế Kinh &raquo; tầng thứ nhất &quot;Th&aacute;i Ho&agrave;ng Ho&agrave;ng Cảnh Thi&ecirc;n&quot; .<br />\r\n<br />\r\nCh&acirc;n kh&iacute;, từ Kh&iacute; Tr&igrave; b&ecirc;n trong chảy ra, dọc theo mở ra tới c&aacute;i kia một đầu kinh mạch, lưu chuyển to&agrave;n th&acirc;n.<br />\r\n<br />\r\nTheo ch&acirc;n kh&iacute; trong cơ thể vận h&agrave;nh, Trương Nhược Trần th&acirc;n thể tựa như l&agrave; h&oacute;a th&agrave;nh một c&aacute;i v&ograve;ng xo&aacute;y, bắt đầu chậm r&atilde;i hấp thu giữa thi&ecirc;n địa linh kh&iacute;.<br />\r\n<br />\r\nThi&ecirc;n Địa linh kh&iacute;, chảy đến mi t&acirc;m Thần V&otilde; Ấn K&yacute;.<br />\r\n<br />\r\nThần V&otilde; Ấn K&yacute; lại đem Thi&ecirc;n Địa linh kh&iacute; chuyển h&oacute;a l&agrave;m ch&acirc;n kh&iacute;, chứa đựng tại Kh&iacute; Tr&igrave;. Ch&acirc;n kh&iacute; từ Kh&iacute; Tr&igrave; b&ecirc;n trong chảy ra, dọc theo kinh mạch, vận chuyển to&agrave;n th&acirc;n.<br />\r\n<br />\r\nCh&acirc;n kh&iacute; tại thể nội vận h&agrave;nh một v&ograve;ng, ch&iacute;nh l&agrave; một c&aacute;i chu thi&ecirc;n.<br />\r\n<br />\r\nR&ograve;ng r&atilde; vận h&agrave;nh chín c&aacute;i chu thi&ecirc;n, khi Trương Nhược Trần lần nữa mở hai mắt ra thời điểm, ph&aacute;t hiện ch&acirc;n kh&iacute; trong cơ thể số lượng tăng l&ecirc;n gấp đ&ocirc;i, đạt đến Kh&iacute; Tr&igrave; dung lượng một phần mười.<br />\r\n<br />\r\nNếu l&agrave; kh&aacute;c v&otilde; giả biết, c&oacute; thể tại ngắn ngủi như vậy thời gian b&ecirc;n trong, để ch&acirc;n kh&iacute; trong cơ thể số lượng tăng l&ecirc;n gấp đ&ocirc;i, khẳng định sẽ mừng rỡ như đi&ecirc;n.<br />\r\n<br />\r\nNhưng l&agrave;, Trương Nhược Trần lại có chút kh&ocirc;ng vừa ý, &quot;Tu luyện chín c&aacute;i chu thi&ecirc;n, thế m&agrave; mới tăng l&ecirc;n ngần ấy ch&acirc;n kh&iacute;, qu&aacute; chậm! Nếu l&agrave; c&oacute; thể đạt được một vi&ecirc;n Linh Tinh liền tốt! Tốc độ tu luyện của ta, c&ograve;n c&oacute; thể tăng l&ecirc;n gấp đ&ocirc;i!&quot;<br />\r\n<br />\r\nPhải biết, Trương Nhược Trần tu luyện thế nhưng l&agrave; &laquo; Cửu Thi&ecirc;n Minh Đế Kinh &raquo;, hấp thu ch&acirc;n kh&iacute; tốc độ vốn l&agrave; so với c&aacute;i kia tu luyện cấp thấp c&ocirc;ng ph&aacute;p v&otilde; giả phải nhanh.<br />\r\n<br />\r\nHiện tại tốc độ tu luyện, tự nhi&ecirc;n để hắn rất kh&ocirc;ng h&agrave;i l&ograve;ng.<br />\r\n<br />\r\nNếu l&agrave; c&oacute; thể một vi&ecirc;n Linh Tinh, liền c&oacute; thể để tu vi tốc độ tăng l&ecirc;n một mảng lớn.<br />\r\n<br />\r\nLinh Tinh, từ linh kh&iacute; tụ tập th&agrave;nh Tinh Thạch.<br />\r\n<br />\r\nĐ&atilde; c&oacute; thể trong l&ograve;ng đất trong mỏ quặng đ&agrave;o m&oacute;c &quot;Tự nhi&ecirc;n Linh Tinh&quot;, cũng c&oacute; thể ch&eacute;m giết Man th&uacute;, đ&agrave;o lấy Man th&uacute; thể nội &quot;Hậu Thi&ecirc;n Linh Tinh&quot; .<br />\r\n<br />\r\nMột vi&ecirc;n Linh Tinh gi&aacute; trị, đồng dạng tương đương với 1000 mai ng&acirc;n tệ gi&aacute; trị, cũng chỉ c&oacute; vương t&ocirc;n qu&yacute; tộc, hoặc l&agrave; đại gia tộc bồi dưỡng thi&ecirc;n t&agrave;i, mới c&oacute; cơ hội đạt được Linh Tinh.<br />\r\n<br />\r\n1000 mai ng&acirc;n tệ, đối với hiện tại Trương Nhược Trần c&ugrave;ng L&acirc;m Phi tới n&oacute;i, tuyệt đối l&agrave; một c&aacute;i khổng lồ số lượng, căn bản kh&ocirc;ng c&oacute; khả năng mua được.<br />\r\n<br />\r\n&quot;Linh Tinh!&quot;<br />\r\n<br />\r\nTrương Nhược Trần trong l&ograve;ng khẽ động, lập tức đem c&aacute;i kia một vi&ecirc;n một mực đeo ở tr&ecirc;n người m&agrave;u trắng hạt t&aacute;o h&igrave;nh Tinh Thạch lấy ra, n&acirc;ng ở l&ograve;ng b&agrave;n tay.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\nN&agrave;y lại kh&ocirc;ng phải l&agrave; một khối Linh Tinh?<br />\r\n<br />\r\nCh&acirc;n kh&iacute; trong cơ thể vận chuyển, hắn mi t&acirc;m Thần V&otilde; Ấn K&yacute;, lập tức nổi l&ecirc;n, h&oacute;a th&agrave;nh một c&aacute;i tiền xu lớn nhỏ h&igrave;nh tr&ograve;n vầng s&aacute;ng.<br />\r\n<br />\r\nMột đạo ch&acirc;n kh&iacute; m&agrave;u trắng, từ mi t&acirc;m bắn ra, đ&aacute;nh v&agrave;o m&agrave;u trắng Tinh Thạch ph&iacute;a tr&ecirc;n.<br />\r\n<br />\r\n&quot;Xoạt!&quot;<br />\r\n<br />\r\nM&agrave;u trắng tinh thạch mặt ngo&agrave;i, hiện ra bốn c&aacute;i cổ l&atilde;o văn tự.<br />\r\n<br />\r\nRất cổ qu&aacute;i!<br />\r\n<br />\r\nTrương Nhược Trần từ trước tới nay chưa từng gặp qua dạng n&agrave;y văn tự, lại một ch&uacute;t đem bốn c&aacute;i văn tự nhận ra &mdash;&mdash; &quot;Thời Kh&ocirc;ng Tinh Thạch&quot; .<br />\r\n<br />\r\nPhải biết, ở tr&ecirc;n một thế thời điểm, Trương Nhược Trần đ&atilde; từng đem ch&acirc;n kh&iacute; r&oacute;t v&agrave;o m&agrave;u trắng Tinh Thạch, nhưng l&agrave;, nhưng lại chưa bao giờ đem Tinh Thạch mặt ngo&agrave;i văn tự k&iacute;ch ph&aacute;t ra tới.<br />\r\n<br />\r\n&quot;Ở kiếp trước, tu vi của ta so hiện tại cường đại gấp mấy trăm lần, cũng kh&ocirc;ng c&oacute; để m&agrave;u trắng tinh thạch mặt ngo&agrave;i xuất hiện văn tự. Một thế n&agrave;y, mới Ho&agrave;ng Cực Cảnh sơ kỳ tu vi, thế m&agrave; liền để m&agrave;u trắng Tinh Thạch xuất hiện biến h&oacute;a. N&oacute;i r&otilde; căn bản kh&ocirc;ng phải cường độ ch&acirc;n kh&iacute; nguy&ecirc;n nh&acirc;n, m&agrave; l&agrave; ch&acirc;n kh&iacute; thuộc t&iacute;nh nguy&ecirc;n nh&acirc;n.&quot;<br />\r\n<br />\r\nTỉ như, c&oacute; được Liệt Diễm Thần V&otilde; Ấn K&yacute; v&otilde; giả, sử dụng Hỏa thuộc t&iacute;nh Linh Tinh hiệu quả tốt nhất, c&oacute; thể đạt tới gấp ba tốc độ tu luyện . Sử dụng phổ th&ocirc;ng Linh Tinh, cũng chỉ c&oacute; thể đạt tới gấp hai tốc độ tu luyện.<br />\r\n<br />\r\nTrong giới tự nhi&ecirc;n, 90% Linh Tinh đều kh&ocirc;ng c&oacute; thuộc t&iacute;nh. Tựa như 90% v&otilde; giả, cũng chỉ c&oacute; thể k&iacute;ch ph&aacute;t ra kh&ocirc;ng c&oacute; thuộc t&iacute;nh v&otilde; đạo ấn k&yacute;.<br />\r\n<br />\r\n&quot;Chẳng lẽ ta mở ra Thần V&otilde; Ấn K&yacute; cũng c&oacute; được một loại n&agrave;o đ&oacute; thuộc t&iacute;nh, vừa vặn c&ugrave;ng c&aacute;i n&agrave;y một vi&ecirc;n Thời Kh&ocirc;ng Tinh Thạch ph&ugrave; hợp với nhau? Chờ một ch&uacute;t, Thời Kh&ocirc;ng Tinh Thạch l&agrave; c&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\nTrương Nhược Trần ở kiếp trước thế nhưng l&agrave; Minh Đế chi tử, kiến thức cực lớn, nghe n&oacute;i qua rất nhiều thuộc t&iacute;nh Linh Tinh, X&iacute;ch Diễm Linh Tinh, Huyền Băng Linh Tinh, l&ocirc;i điện Linh Tinh, T&agrave; Huyết Linh Tinh... , nhưng l&agrave;, nhưng lại chưa bao giờ nghe n&oacute;i qua thời kh&ocirc;ng thuộc t&iacute;nh Linh Tinh.<br />\r\n<br />\r\nBởi v&igrave;, thời gian c&ugrave;ng kh&ocirc;ng gian căn bản kh&ocirc;ng phải nh&acirc;n lực c&oacute; thể khống chế, liền xem như thần, cũng kh&ocirc;ng thể rung chuyển thời gian c&ugrave;ng kh&ocirc;ng gian, nhất định phải tu&acirc;n theo thời gian c&ugrave;ng kh&ocirc;ng gian quy tắc.<br />\r\n<br />\r\nNgay tại Trương Nhược Trần c&ograve;n mười phần kh&ocirc;ng hiểu thời điểm, Thời Kh&ocirc;ng Tinh Thạch mặt ngo&agrave;i hiện ra một vầng s&aacute;ng, h&oacute;a th&agrave;nh một c&aacute;i v&ograve;ng xo&aacute;y.<br />\r\n<br />\r\nV&ograve;ng xo&aacute;y kh&ocirc;ng ngừng biến lớn, bao tr&ugrave;m Trương Nhược Trần th&acirc;n thể.<br />\r\n<br />\r\nTrương Nhược Trần chỉ cảm thấy một trận trời đ&acirc;́t quay cu&ocirc;̀ng, sau một khắc, hắn liền tới đến một c&aacute;i kh&ocirc;ng gian bịt k&iacute;n, tr&ugrave;ng điệp ng&atilde; xuống đến cứng rắn tr&ecirc;n mặt đất.<br />\r\n<br />\r\nMay mắn hắn ho&agrave;n th&agrave;nh tẩy tủy tr&ugrave;ng mạch, đạt tới Ho&agrave;ng Cực Cảnh sơ kỳ, th&acirc;n thể tăng cường kh&ocirc;ng &iacute;t. Nếu l&agrave; lấy hắn trước kia yếu ớt th&acirc;n thể, quẳng lần n&agrave;y, đo&aacute;n chừng liền đ&atilde; ng&atilde; chết.<br />\r\n<br />\r\nTrương Nhược Trần từ dưới đất b&ograve; dậy, hoạt động một ch&uacute;t đau đớn g&acirc;n cốt, bắt đầu quan s&aacute;t kh&ocirc;ng gian bốn ph&iacute;a.<br />\r\n<br />\r\nC&aacute;i kh&ocirc;ng gian n&agrave;y ho&agrave;n to&agrave;n bịt k&iacute;n, kh&ocirc;ng nh&igrave;n thấy cửa sổ. Kh&ocirc;ng gian độ cao, đại kh&aacute;i l&agrave; mười mét, d&agrave;i c&ugrave;ng rộng kh&ocirc;ng sai biệt lắm cũng l&agrave; chừng mười thước.<br />\r\n<br />\r\n&quot;Tại sao c&oacute; thể như vậy? Ta từ chỗ n&agrave;o tiến đến? Nơi đ&acirc;y lại l&agrave; chỗ n&agrave;o? Ồ! Nơi đ&oacute; c&oacute; một t&ograve;a bệ đ&aacute;!&quot;<br />\r\n<br />\r\nTo&agrave;n bộ kh&ocirc;ng gian b&ecirc;n trong, chỉ c&oacute; một t&ograve;a bệ đ&aacute;.<br />\r\n<br />\r\nTr&ecirc;n bệ đ&aacute; để đ&oacute; một bức cuốn lại vẽ, một bản m&agrave;u bạc thiết thư. Trừ c&aacute;i đ&oacute; ra, liền kh&ocirc;ng c&oacute; thứ kh&aacute;c!<br />\r\n<br />\r\nTrương Nhược Trần dẫn đầu đi lấy c&aacute;i kia một bức họa, nhưng l&agrave;, c&aacute;i kia một bức họa lại nặng nề v&ocirc; c&ugrave;ng, giống như l&agrave; c&ugrave;ng bệ đ&aacute; hợp th&agrave;nh một thể, v&ocirc; luận Trương Nhược Trần sử dụng bao lớn lực lượng, bức tranh cũng kh&ocirc;ng nh&uacute;c nh&iacute;ch t&iacute; n&agrave;o.<br />\r\n<br />\r\nTất nhi&ecirc;n đem bức tranh kh&ocirc;ng cầm l&ecirc;n được, cũng đem bức tranh mở kh&ocirc;ng ra, Trương Nhược Trần chỉ c&oacute; thể tạm thời từ bỏ, sau đ&oacute; đem &aacute;nh mắt nh&igrave;n chăm ch&uacute; về ph&iacute;a c&aacute;i kia một bản thật mỏng m&agrave;u bạc thiết thư.<br />\r\n<br />\r\nM&agrave;u bạc thiết thư mặt ngo&agrave;i, viết lấy bốn chữ: &quot;Thời Kh&ocirc;ng B&iacute; Điển!&quot;<br />\r\n<br />\r\nLần n&agrave;y, Trương Nhược Trần c&oacute; chỗ chuẩn bị, đem vận chuyển ch&acirc;n kh&iacute; to&agrave;n th&acirc;n, đem lực lượng to&agrave;n th&acirc;n đều điều động, đem &laquo; Thời Kh&ocirc;ng B&iacute; Điển &raquo; tờ thứ nhất lật ra.<br />\r\n<br />\r\n&quot;Như thế... Dễ d&agrave;ng?&quot;<br />\r\n<br />\r\n&laquo; Thời Kh&ocirc;ng B&iacute; Điển &raquo; lật xem mười phần nhẹ nh&otilde;m, mảy may đều kh&ocirc;ng ph&iacute; sức.<br />\r\n<br />\r\nTrương Nhược Trần h&atilde;nh h&atilde;nh nhi&ecirc;n lắc đầu, ngừng vận chuyển ch&acirc;n kh&iacute;, đem &laquo; Thời Kh&ocirc;ng B&iacute; Điển &raquo; cầm l&ecirc;n, n&acirc;ng ở trong tay, cẩn thận đọc.<br />\r\n<br />\r\n&laquo; Thời Kh&ocirc;ng B&iacute; Điển &raquo; tờ thứ nhất ph&iacute;a tr&ecirc;n ghi lại cũng kh&ocirc;ng phải l&agrave; c&aacute;i g&igrave; tu luyện b&iacute; điển, m&agrave; l&agrave; Thời Kh&ocirc;ng Tinh Thạch đời trước chủ nh&acirc;n &quot;Tu Di Th&aacute;nh Tăng&quot; ghi ch&eacute;p.<br />\r\n<br />\r\nTrương Nhược Trần đem Tu Di Th&aacute;nh Tăng ghi ch&eacute;p sau khi xem xong, rốt cuộc hiểu r&otilde; tất cả sự t&igrave;nh.<br />\r\n<br />\r\n&quot;Nguy&ecirc;n lai ta mở ra Thần V&otilde; Ấn K&yacute;, lại l&agrave; Thời Kh&ocirc;ng Thần V&otilde; Ấn K&yacute;. Căn cứ Tu Di Th&aacute;nh Tăng n&oacute;i, ức vạn người b&ecirc;n trong cũng kh&ocirc;ng c&oacute; một c&aacute;i c&oacute; thể mở ra Thời Kh&ocirc;ng Thần V&otilde; Ấn K&yacute;. Từ xưa đến nay, vẻn vẹn chỉ c&oacute; hai người, t&iacute;nh cả ta ch&iacute;nh l&agrave; ba c&aacute;i.&quot;<br />\r\n<br />\r\n&quot;Tu Di Th&aacute;nh Tăng liền l&agrave; c&aacute;i thứ hai mở ra Thời Kh&ocirc;ng Thần V&otilde; Ấn K&yacute; người, nhưng l&agrave;, căn cứ hắn tại &laquo; Thời Kh&ocirc;ng B&iacute; Điển &raquo; ph&iacute;a tr&ecirc;n ghi ch&eacute;p thời gian, hắn tại hơn mười vạn năm trước liền đ&atilde; chết rồi. Hơn mười vạn năm trước, c&aacute;i kia đ&atilde; l&agrave; thời đại Trung Cổ, c&aacute;ch hiện tại qu&aacute; xa vời.&quot;<br />\r\n<br />\r\nTrương Nhược Trần hiện tại vị tr&iacute; kh&ocirc;ng gian, liền l&agrave; Thời Kh&ocirc;ng Tinh Thạch b&ecirc;n trong kh&ocirc;ng gian.<br />\r\n<br />\r\nB&ecirc;n trong kh&ocirc;ng gian b&ecirc;n trong thời gian lưu động tốc độ c&ugrave;ng ngoại giới ho&agrave;n to&agrave;n kh&ocirc;ng giống, ở b&ecirc;n trong kh&ocirc;ng gian b&ecirc;n trong tu luyện ba ng&agrave;y, ngoại giới mới đi qua một ng&agrave;y.<br />\r\n<br />\r\nTrương Nhược Trần đại hỉ, k&iacute;ch động n&oacute;i: &quot;Ở b&ecirc;n trong tu luyện ba ng&agrave;y, b&ecirc;n ngo&agrave;i mới đi qua một ng&agrave;y, chẳng phải l&agrave; so người kh&aacute;c trống rỗng th&ecirc;m ra gấp ba thời gian tu luyện? Thật sự l&agrave; qu&aacute; tốt.&quot;<br />\r\n<br />\r\nTrương Nhược Trần muốn đọc qua &laquo; Thời Kh&ocirc;ng B&iacute; Điển &raquo; trang thứ hai, nhưng l&agrave; v&ocirc; luận hắn sử dụng biện ph&aacute;p g&igrave;, lại đều kh&ocirc;ng c&aacute;ch n&agrave;o đem trang thứ hai lật ra.<br />\r\n<br />\r\n&quot;Lại kh&ocirc;ng c&aacute;ch n&agrave;o mở ra.&quot;<br />\r\n<br />\r\nTrương Nhược Trần c&oacute; một loại muốn đem &laquo; Thời Kh&ocirc;ng B&iacute; Điển &raquo; quẳng xuống đất x&uacute;c động, bỗng nhi&ecirc;n, hắn nh&igrave;n tờ thứ nhất h&agrave;ng cuối c&ugrave;ng c&ograve;n c&oacute; một h&agrave;ng chữ nhỏ.<br />\r\n<br />\r\n&quot;Tu luyện tới Ho&agrave;ng Cực Cảnh tiểu cực vị, đem ch&acirc;n kh&iacute; r&oacute;t v&agrave;o bức tranh, c&oacute; thể đem bức tranh mở ra.&quot;<br />\r\n<br />\r\nTrương Nhược Trần lần nữa đem &aacute;nh mắt nh&igrave;n chăm ch&uacute; về ph&iacute;a c&aacute;i kia m&ocirc;̣t bức tranh quyển, trong l&ograve;ng suy đo&aacute;n, trong bức họa khẳng định ghi ch&eacute;p một loại n&agrave;o đ&oacute; thần b&iacute; c&ocirc;ng ph&aacute;p, n&oacute;i kh&ocirc;ng chừng liền c&ugrave;ng thời gian tu luyện c&ugrave;ng kh&ocirc;ng gian c&oacute; quan hệ.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n&quot;Cố gắng tu luyện, tranh thủ sớm ng&agrave;y đột ph&aacute; đến tiểu cực vị, ngược lại muốn xem xem, trong bức tranh đến c&ugrave;ng ẩn giấu đi b&iacute; mật g&igrave;?&quot;<br />\r\n<br />\r\n&quot;Tiểu cực vị&quot; l&agrave; Ho&agrave;ng Cực Cảnh c&aacute;i thứ tư tiểu cảnh giới, lại hướng l&ecirc;n liền l&agrave; trung cực vị, đại cực vị, đại vi&ecirc;n m&atilde;n.<br />\r\n<br />\r\nTrương Nhược Trần trong bụng truyền đến một cỗ cảm gi&aacute;c đ&oacute;i bụng, liền lập tức từ Thời Kh&ocirc;ng Tinh Thạch b&ecirc;n trong kh&ocirc;ng gian tạm thời lui ra ngo&agrave;i, lần nữa trở lại gian ph&ograve;ng của hắn.<br />\r\n<br />\r\nHắn đem Thời Kh&ocirc;ng Tinh Thạch thật chặt nắm ở trong tay, c&oacute; c&aacute;i n&agrave;y một khối Tinh Thạch, liền c&oacute; c&agrave;ng lớn nắm chắc tại trong v&ograve;ng ba th&aacute;ng tu luyện tới Ho&agrave;ng Cực Cảnh hậu kỳ.<br />\r\n<br />\r\nTrương Nhược Trần, L&acirc;m Phi, thị nữ V&acirc;n Nhi, ngồi c&ugrave;ng một chỗ ăn điểm t&acirc;m.<br />\r\n<br />\r\nĂn đồ vật, vẻn vẹn chỉ l&agrave; gạo trắng c&ugrave;ng m&agrave;n thầu, căn bản kh&ocirc;ng nh&igrave;n thấy loại thịt.<br />\r\n<br />\r\nĐối với v&otilde; giả tới n&oacute;i, ti&ecirc;u hao thể lực so với người b&igrave;nh thường phải lớn hơn nhiều, ăn đồ vật liền lộ ra tương đối quan trọng.<br />\r\n<br />\r\nKh&aacute;c những c&aacute;i kia mở ra Thần V&otilde; Ấn K&yacute; Vương tử c&ugrave;ng quận ch&uacute;a, mỗi ng&agrave;y ăn đều l&agrave; d&ugrave;ng Man th&uacute; huyết dịch luyện chế th&agrave;nh &quot;Huyết Đan&quot;, đ&atilde; sớm kh&ocirc;ng c&ograve;n ăn thức ăn th&ocirc;ng thường.<br />\r\n<br />\r\nĂn v&agrave;o một hạt Huyết Đan, liền c&oacute; thể bổ sung cả ng&agrave;y thể lực ti&ecirc;u hao, coi như tu luyện một ng&agrave;y quyền ph&aacute;p, kiếm ph&aacute;p, cũng sẽ kh&ocirc;ng cảm thấy đ&oacute;i kh&aacute;t.<br />\r\n<br />\r\nM&agrave; lại, phục dụng Huyết Đan hay l&agrave; tăng l&ecirc;n huyết kh&iacute;, tăng cường v&otilde; thể, gia tăng nhục th&acirc;n lực lượng.<br />\r\n<br />\r\nNếu l&agrave; Trương Nhược Trần liền ăn gạo trắng c&ugrave;ng m&agrave;n thầu, coi như mỗi ng&agrave;y ăn t&aacute;m bữa cơm, cũng còn thi&ecirc;́u r&acirc;́t nhi&ecirc;̀u th&acirc;n thể ti&ecirc;u hao.<br />\r\n<br />\r\n&quot;Trần Nhi, ngươi b&acirc;y giờ đ&atilde; mở ra Thần V&otilde; Ấn K&yacute;, kh&ocirc;ng thể lại ăn thức ăn th&ocirc;ng thường. C&aacute;i n&agrave;y mười c&aacute;i Huyết Đan ngươi trước nhận lấy, nếu l&agrave; kh&ocirc;ng đủ, mẫu th&acirc;n đang suy nghĩ những biện ph&aacute;p kh&aacute;c.&quot; L&acirc;m Phi đem một chiếc b&igrave;nh ngọc lấy ra, đưa cho Trương Nhược Trần.<br />\r\n<br />\r\nTrương Nhược Trần ngay tại gặm m&agrave;n thầu, căn bản kh&ocirc;ng c&oacute; nghĩ đến L&acirc;m Phi lại c&oacute; thể xuất ra mười c&aacute;i Huyết Đan, hơi nghi hoặc một ch&uacute;t m&agrave; n&oacute;i: &quot;Mẫu th&acirc;n, năm mai ng&acirc;n tệ mới c&oacute; thể mua được một vi&ecirc;n Huyết Đan, mười c&aacute;i Huyết Đan liền l&agrave; 50 mai ng&acirc;n tệ, ngươi lấy đ&acirc;u ra nhiều tiền như vậy a?&quot;<br />\r\n<br />\r\nL&acirc;m Phi cười cười, n&oacute;i: &quot;Mẫu th&acirc;n lu&ocirc;n lu&ocirc;n c&oacute; biện ph&aacute;p.&quot;<br />\r\n<br />\r\nThị nữ V&acirc;n Nhi đứng sau lưng L&acirc;m Phi, n&oacute;i: &quot;Nương nương l&agrave; đem y&ecirc;u th&iacute;ch nhất tr&acirc;m c&agrave;i t&oacute;c tr&acirc;m c&agrave;i b&aacute;n, mới đi chợ đan dược đổi mười c&aacute;i Huyết Đan!&quot;<br />\r\n<br />\r\nL&acirc;m Phi nhẹ nh&agrave;ng trừng V&acirc;n Nhi một ch&uacute;t, giống như l&agrave; đang tr&aacute;ch n&agrave;ng lắm miệng, sau đ&oacute; lại n&oacute;i: &quot;Trần Nhi, ngươi đừng suy nghĩ nhiều, chỉ cần ngươi c&oacute; thể tu luyện th&agrave;nh một vị ch&acirc;n ch&iacute;nh v&otilde; giả, mẫu th&acirc;n coi như đem tất cả trang sức to&agrave;n bộ b&aacute;n đi, cũng sẽ ủng hộ ngươi tu luyện.&quot;<br />\r\n<br />\r\nTrương Nhược Trần trong l&ograve;ng v&ocirc; c&ugrave;ng cảm động, thật chặt nắm vuốt b&igrave;nh ngọc trong tay, cắn m&ocirc;i, rất muốn n&oacute;i cho L&acirc;m Phi, m&igrave;nh b&acirc;y giờ đ&atilde; tu luyện th&agrave;nh một vị ch&acirc;n ch&iacute;nh v&otilde; giả.<br />\r\n<br />\r\nKh&ocirc;ng được, kh&ocirc;ng thể l&agrave;m như thế.<br />\r\n<br />\r\nVẻn vẹn một đ&ecirc;m liền mở ra Kh&iacute; Tr&igrave;, ho&agrave;n th&agrave;nh tẩy tủy tr&ugrave;ng mạch, thực sự qu&aacute; nhanh, nếu l&agrave; tin tức kh&ocirc;ng cẩn thận r&ograve; rỉ ra ngo&agrave;i, khẳng định sẽ truyền đến kh&aacute;c Vương tử c&ugrave;ng Vương hậu trong tai. Đến l&uacute;c đ&oacute;, ngược lại sẽ hại m&igrave;nh, cũng hại L&acirc;m Phi.<br />\r\n<br />\r\nHắn hiện tại hay l&agrave; qu&aacute; yếu ớt, c&acirc;̀n lực lượng c&agrave;ng th&ecirc;m cường đại.</p>', '2021-11-27 20:00:14', 17, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(154, 43, 'Long Tượng Bàn Nhược', '5', '<p>Trương Nhược Trần trong &aacute;nh mắt mang theo một cỗ sắc b&eacute;n, &yacute; niệm ki&ecirc;n định n&oacute;i: &quot;Mẫu th&acirc;n, y&ecirc;n t&acirc;m đi! Ta nhất định sẽ mau ch&oacute;ng tu luyện th&agrave;nh l&agrave;m một t&ecirc;n cường giả, d&ugrave;ng ch&iacute;nh ta lực lượng đến bảo hộ ngươi.&quot;<br />\r\n<br />\r\nCầm Huyết Đan, Trương Nhược Trần trở về gian ph&ograve;ng của m&igrave;nh, tiếp tục ra sức tu luyện.<br />\r\n<br />\r\n&quot;Nương nương, nghe n&oacute;i v&otilde; giả kh&ocirc;ng chỉ c&oacute; chỉ l&agrave; muốn phục dụng Huyết Đan, hơn nữa c&ograve;n muốn tu luyện c&ocirc;ng ph&aacute;p, nhất định phải c&oacute; được tu luyện c&ocirc;ng ph&aacute;p, mới có th&ecirc;̉ mở trừ ra kinh mạch.&quot; Thị nữ V&acirc;n Nhi nói.<br />\r\n<br />\r\nL&acirc;m Phi nh&igrave;n qua Trương Nhược Trần rời đi b&oacute;ng lưng, ngậm miệng, đắng chát nhẹ gật đầu, n&oacute;i: &quot;Ta bi&ecirc;́t! Nhưng l&agrave;, cho d&ugrave; l&agrave; phẩm chất thấp nhất cấp c&ocirc;ng ph&aacute;p, cũng phải b&aacute;n 500 mai ng&acirc;n tệ trở l&ecirc;n, bằng v&agrave;o ta hiện tại t&agrave;i lực, căn bản mua kh&ocirc;ng nổi. M&agrave; lại, hiện tại l&agrave; Vương hậu c&ugrave;ng Quốc sư đang chủ tr&igrave; triều ch&iacute;nh, Vương hậu căn bản kh&ocirc;ng c&oacute; khả năng để Trần Nhi đi &#39;T&agrave;ng Thư Điện&#39; nhận lấy tu luyện c&ocirc;ng ph&aacute;p c&ugrave;ng v&otilde; kỹ. Xem ra chỉ c&oacute; một c&aacute;i biện ph&aacute;p!&quot;<br />\r\n<br />\r\nThị nữ V&acirc;n Nhi n&oacute;i: &quot;Nương nương muốn đi cầu người L&acirc;m gia? Thế nhưng l&agrave; ba năm trước đ&acirc;y, nương nương đ&atilde; c&ugrave;ng L&acirc;m gia triệt để trở mặt, bọn hắn sẽ cho Cửu vương tử điện hạ tu luyện c&ocirc;ng ph&aacute;p?&quot;<br />\r\n<br />\r\n&quot;Chỉ cần bọn hắn nguyện &yacute; cho Trần Nhi tu luyện c&ocirc;ng ph&aacute;p, coi như muốn ta quỳ tr&ecirc;n mặt đất nhận lầm, ta cũng giống vậy c&oacute; thể quỳ.&quot; L&acirc;m Phi giống như l&agrave; nghĩ tới chuyện cũ g&igrave;, trong mắt kh&ocirc;ng khỏi nước mắt chảy xuống.<br />\r\n<br />\r\n&quot;Thế nhưng l&agrave; l&uacute;c trước r&otilde; r&agrave;ng kh&ocirc;ng phải nương nương sai...&quot; Thị nữ V&acirc;n Nhi nhẹ nh&agrave;ng thở d&agrave;i.<br />\r\n<br />\r\n...<br />\r\n<br />\r\nThời Kh&ocirc;ng Tinh Thạch b&ecirc;n trong kh&ocirc;ng gian, linh kh&iacute; mười phần sung t&uacute;c, mức độ đậm đặc đại kh&aacute;i l&agrave; ngoại giới gấp hai.<br />\r\n<br />\r\nPhải biết, tại V&acirc;n V&otilde; Quận Quốc, những c&aacute;i kia danh sơn đại xuy&ecirc;n nồng độ linh kh&iacute; nếu l&agrave; đạt tới 1,5 lần, liền đ&atilde; xem như tu luyện bảo địa, sẽ gặp phải c&aacute;c đại gia tộc tranh đoạt.<br />\r\n<br />\r\nNgồi ở b&ecirc;n trong kh&ocirc;ng gian trung ương, Trương Nhược Trần đem một con kia chứa mười c&aacute;i Huyết Đan b&igrave;nh ngọc lấy ra, từ trong b&igrave;nh ngọc đổ ra một hạt Huyết Đan, c&acirc;̀m tới ch&oacute;p mũi nhẹ nh&agrave;ng h&iacute;t h&agrave;.<br />\r\n<br />\r\n&quot;Huyết Đan&quot; mặc d&ugrave; l&agrave; d&ugrave;ng Man th&uacute; huyết dịch luyện chế m&agrave; th&agrave;nh, nhưng lại cũng kh&ocirc;ng c&oacute; m&ugrave;i m&aacute;u tươi, ngược lại mang theo một cỗ nh&agrave;n nhạt m&ugrave;i thơm ng&aacute;t.<br />\r\n<br />\r\nLuyện Đan Sư tại luyện chế Huyết Đan thời điểm, trừ đi trong đ&oacute; m&ugrave;i m&aacute;u tanh, lại gia nhập hổ la cỏ c&ugrave;ng Mạn Đ&agrave; hoa.<br />\r\n<br />\r\nD&ugrave;ng l&acirc;u d&agrave;i Huyết Đan, kh&ocirc;ng chỉ c&oacute; thể v&igrave; v&otilde; giả cung cấp li&ecirc;n tục kh&ocirc;ng ngừng thể lực, hơn nữa c&ograve;n c&oacute; thể cải thiện v&otilde; giả kinh mạch, xương cốt, tạng phủ, l&agrave;m v&otilde; giả th&acirc;n thể trở n&ecirc;n c&agrave;ng th&ecirc;m cường đại.<br />\r\n<br />\r\n&quot;Chỉ l&agrave; Nhất phẩm Huyết Đan.&quot; Trương Nhược Trần khẽ gật đầu, tự lầm bầm n&oacute;i: &quot;Coi l&agrave; tu vi hiện tại, phục dụng Nhất phẩm Huyết Đan như vậy đủ rồi.&quot;<br />\r\n<br />\r\nTrương Nhược Trần đem một vi&ecirc;n Huyết Đan phục tiến miệng b&ecirc;n trong, liền lập tức đem b&igrave;nh ngọc một lần nữa đắp l&ecirc;n, ph&oacute;ng tới tr&ecirc;n bệ đ&aacute;.<br />\r\n<br />\r\nTại ch&acirc;n kh&iacute; th&ocirc;i động dưới, Huyết Đan huyết kh&iacute;, rất nhanh liền h&ograve;a tan mở, v&igrave; Trương Nhược Trần cung cấp cuồn cuộn kh&ocirc;ng dứt thể lực.<br />\r\n<br />\r\n&quot;Ta mặc d&ugrave; đạt tới Ho&agrave;ng Cực Cảnh sơ kỳ, trở th&agrave;nh một t&ecirc;n v&otilde; giả, nhưng l&agrave; c&aacute;i n&agrave;y một bộ nhục th&acirc;n thực sự qu&aacute; gầy yếu đi, căn bản kh&ocirc;ng thể c&ugrave;ng kh&aacute;c v&otilde; giả so s&aacute;nh. Nhất định phải đem th&acirc;n thể của m&igrave;nh luyện đến cường tr&aacute;ng, bằng kh&ocirc;ng tại c&ugrave;ng c&ugrave;ng cảnh giới v&otilde; giả giao thủ thời điểm, sẽ ăn rất lớn thua thiệt.&quot;<br />\r\n<br />\r\nĐối với v&otilde; giả tới n&oacute;i, kh&ocirc;ng chỉ c&oacute; chỉ l&agrave; muốn tu luyện ch&acirc;n kh&iacute;, hơn nữa c&ograve;n muốn tu luyện v&otilde; kỹ.<br />\r\n<br />\r\n&quot;Long Tượng B&agrave;n Nhược Chưởng!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n<br />\r\nTrương Nhược Trần trong đầu hiện ra một bộ n&agrave;y huyền diệu chưởng ph&aacute;p, tại hắn trong tr&iacute; nhớ v&otilde; kỹ b&iacute; điển b&ecirc;n trong đủ để xếp v&agrave;o ba vị tr&iacute; đầu, vừa vặn th&iacute;ch hợp hắn hiện tại tu luyện.<br />\r\n<br />\r\nHai ch&acirc;n của hắn t&aacute;ch ra, eo cuộn xuống ch&igrave;m, đem ch&acirc;n kh&iacute; trong cơ thể tr&agrave;n đầy đến hai ch&acirc;n, cố định trụ th&acirc;n thể, chậm r&atilde;i n&acirc;ng l&ecirc;n đ&ocirc;i c&aacute;nh tay, bắt đầu dựa theo một loại n&agrave;o đ&oacute; huyền diệu quy luật, đập động song chưởng.<br />\r\n<br />\r\nTrong đầu, tưởng tượng m&igrave;nh l&agrave; một đầu lực lượng v&ocirc; tận Th&aacute;i Cổ thần tượng, th&ocirc;n v&acirc;n thổ vụ vực s&acirc;u Ma Long, đ&aacute;nh ra mỗi một quyền đều d&ugrave;ng đem hết to&agrave;n lực, tựa như l&agrave; muốn đem thể nội mỗi một tia lực lượng đều đem ra ngo&agrave;i.<br />\r\n<br />\r\nMỗi một chưởng đ&aacute;nh đi ra, to&agrave;n th&acirc;n cơ bắp đều bị k&eacute;o động, ch&acirc;n kh&iacute; trong cơ thể cũng đi theo dung nhập cơ bắp c&ugrave;ng g&acirc;n cốt, l&agrave;m cơ bắp c&ugrave;ng g&acirc;n cốt ph&aacute;t sinh tho&aacute;t biến, trở n&ecirc;n c&agrave;ng th&ecirc;m c&oacute; lực, cứng c&aacute;p hơn, thậm ch&iacute; c&ugrave;ng ch&acirc;n kh&iacute; h&ograve;a l&agrave;m một thể.<br />\r\n<br />\r\nLong Tượng B&agrave;n Nhược Chưởng, một loại c&oacute; mười ba chi&ecirc;u chưởng ấn, thuộc về Vương cấp hạ phẩm v&otilde; kỹ.<br />\r\n<br />\r\nX&aacute;c thực n&oacute;i, nếu l&agrave; c&oacute; thể đem Long Tượng B&agrave;n Nhược Chưởng tu luyện tới thứ mười ba chưởng, coi như c&ugrave;ng Thần cấp v&otilde; kỹ so s&aacute;nh cũng kh&ocirc;ng sai biệt nhiều.<br />\r\n<br />\r\nC&ocirc;ng ph&aacute;p v&agrave; v&otilde; kỹ đều chia l&agrave;m năm c&aacute;i đẳng cấp: Người, linh, quỷ, vương, thần.<br />\r\n<br />\r\nLong Tượng B&agrave;n Nhược Chưởng thứ nhất chưởng &quot;Man Tượng Tr&igrave; Địa&quot;, tu luyện th&agrave;nh c&ocirc;ng, uy lực c&oacute; thể so với Nh&acirc;n cấp hạ phẩm v&otilde; kỹ.<br />\r\n<br />\r\nLong Tượng B&agrave;n Nhược Chưởng thứ hai chưởng &quot;Phi Long Tại Thi&ecirc;n&quot;, tu luyện th&agrave;nh c&ocirc;ng, uy lực c&oacute; thể so với Nh&acirc;n cấp trung phẩm v&otilde; kỹ.<br />\r\n<br />\r\nLong Tượng B&agrave;n Nhược Chưởng thứ ba chưởng &quot;Long Tượng Quy Điền&quot;, tu luyện th&agrave;nh c&ocirc;ng, uy lực c&oacute; thể so với Nh&acirc;n cấp thượng phẩm v&otilde; kỹ.<br />\r\n<br />\r\nLong Tượng B&agrave;n Nhược Chưởng thứ tư chưởng &quot;Long H&igrave;nh Tượng Ảnh&quot;, tu luyện th&agrave;nh c&ocirc;ng, uy lực c&oacute; thể so với Linh cấp hạ phẩm v&otilde; kỹ.<br />\r\n<br />\r\n...<br />\r\n<br />\r\nLong Tượng B&agrave;n Nhược Chưởng thứ mười ba chưởng, được xưng l&agrave; &quot;Long Tượng diệt thế&quot;, uy lực c&oacute; thể so với Thần cấp v&otilde; kỹ, ph&aacute;t huy ra lực lượng kh&ocirc;ng thể tưởng tượng.<br />\r\n<br />\r\nLong Tượng B&agrave;n Nhược Chưởng trước mấy chưởng, chỉ c&oacute; thể coi l&agrave; cấp thấp v&otilde; kỹ, uy lực cũng kh&ocirc;ng cường đại. M&agrave; lại, Long Tượng B&agrave;n Nhược Chưởng, ch&iacute; cương ch&iacute; dương, rất kh&oacute; tu luyện, c&oacute; thể tu luyện tới thứ 7 chưởng người đều &iacute;t c&agrave;ng th&ecirc;m &iacute;t.<br />\r\n<br />\r\nThứ 7 chưởng về sau, mỗi tu luyện một chưởng đều cần hao ph&iacute; đại lượng thời gian, đại lượng tinh lực, nếu l&agrave; ngăn cản kh&ocirc;ng nổi thể nội ch&iacute; cương ch&iacute; dương lực lượng, thậm ch&iacute; có khả năng đốt thể tự đốt.<br />\r\n<br />\r\nCh&iacute;nh l&agrave; bởi v&igrave; những nguy&ecirc;n nh&acirc;n n&agrave;y, dẫn đến Long Tượng B&agrave;n Nhược Chưởng chỉ c&oacute; thể coi l&agrave; Vương cấp hạ phẩm v&otilde; kỹ.<br />\r\n<br />\r\nMặc d&ugrave; Long Tượng B&agrave;n Nhược Chưởng mười phần kh&oacute; tu luyện, nhưng l&agrave;, lại vừa vặn th&iacute;ch hợp Trương Nhược Trần hiện tại tu luyện, c&oacute; thể trong thời gian ngắn, đem th&acirc;n thể luyện đến cường tr&aacute;ng.<br />\r\n<br />\r\n&quot;Long Tượng B&agrave;n Nhược Chưởng thứ nhất chưởng, Man Tượng Tr&igrave; Địa.&quot;<br />\r\n<br />\r\nTrương Nhược Trần đầu ti&ecirc;n l&agrave; đứng thẳng trung b&igrave;nh tấn, sau đ&oacute;, nhanh ch&oacute;ng khởi động b&ocirc;̣ pháp, vọt mạnh ra ngo&agrave;i, nhanh ch&oacute;ng đ&aacute;nh ra chưởng ấn.<br />\r\n<br />\r\nTĩnh như sơn nhạc, động như Man Tượng.<br />\r\n<br />\r\nMột lần lại một lần luyện tập, thẳng đến đem ch&acirc;n kh&iacute; trong cơ thể hao hết, mới lau kh&ocirc; mồ h&ocirc;i, xếp bằng ngồi dưới đất, lợi dụng mi t&acirc;m Thần V&otilde; Ấn K&yacute; hấp thu Thời Kh&ocirc;ng Tinh Thạch b&ecirc;n trong kh&ocirc;ng gian b&ecirc;n trong linh kh&iacute;, chuyển h&oacute;a l&agrave;m cuồn cuộn kh&ocirc;ng dứt ch&acirc;n kh&iacute;.<br />\r\n<br />\r\nTại Thời Kh&ocirc;ng Tinh Thạch b&ecirc;n trong kh&ocirc;ng gian b&ecirc;n trong, li&ecirc;n tiếp tu luyện ch&iacute;n ng&agrave;y, rốt cục đem Long Tượng B&agrave;n Nhược Chưởng thứ nhất chưởng &quot;Man Tượng Tr&igrave; Địa&quot; tu luyện th&agrave;nh c&ocirc;ng.<br />\r\n<br />\r\nB&ecirc;n trong kh&ocirc;ng gian b&ecirc;n trong ch&iacute;n ng&agrave;y, ngoại giới cũng liền chỉ l&agrave; đi qua ba ng&agrave;y m&agrave; th&ocirc;i.<br />\r\n<br />\r\n&quot;Cũng kh&ocirc;ng biết bằng v&agrave;o ta tu vi hiện tại, thi triển ra Long Tượng B&agrave;n Nhược Chưởng thứ nhất chưởng, lực lượng mạnh bao nhi&ecirc;u?&quot;<br />\r\n<br />\r\nTrương Nhược Trần từ Thời Kh&ocirc;ng Tinh Thạch b&ecirc;n trong kh&ocirc;ng gian đi tới, đi v&agrave;o hậu viện, đứng tại viện lạc trung ương, vận chuyển ch&acirc;n kh&iacute; trong cơ thể, tr&agrave;n ngập tại hai ch&acirc;n.<br />\r\n<br />\r\n&quot;Man Tượng Tr&igrave; Địa.&quot;<br />\r\n<br />\r\nHắn giẫm l&ecirc;n quy luật t&iacute;nh b&ocirc;̣ pháp, đột nhi&ecirc;n lao ra.<br />\r\n<br />\r\nTheo b&ocirc;̣ pháp rảo bước tiến l&ecirc;n, một cỗ lực lượng mạnh mẽ, từ hai ch&acirc;n vọt l&ecirc;n đến, đi qua eo, sống lưng, vọt tới phần lưng c&ugrave;ng hai vai, từ hai tay d&acirc;ng tr&agrave;o ra ngo&agrave;i.<br />\r\n<br />\r\nMặc d&ugrave; vẻn vẹn chỉ l&agrave; một chi&ecirc;u chưởng ph&aacute;p, nhưng lại điều động to&agrave;n th&acirc;n mỗi một chỗ bắp thịt lực lượng, lực bộc ph&aacute;t tự nhi&ecirc;n kh&aacute; kinh người.<br />\r\n<br />\r\n&quot;Bành!&quot;<br />\r\n<br />\r\nSong chưởng đ&aacute;nh v&agrave;o một khối cao cỡ nửa người ng&agrave;n c&acirc;n tr&ecirc;n tảng đ&aacute; lớn, sau đ&oacute;, lập tức thu chưởng, giẫm l&ecirc;n vừa rồi dấu ch&acirc;n, hối hả lui về nguy&ecirc;n địa.<br />\r\n<br />\r\nTrương Nhược Trần hướng v&ecirc;̀ kia một khối ng&agrave;n c&acirc;n tảng đ&aacute; lớn nh&igrave;n lại, chỉ gặp tảng đ&aacute; lớn mặt ngo&agrave;i th&ecirc;m ra hai c&aacute;i nh&agrave;n nhạt chưởng h&igrave;nh vết l&otilde;m. Tảng đ&aacute; lớn dưới đ&aacute;y, hướng về trong đất b&ugrave;n trầm xuống khoảng hai cen-ti-m&eacute;t.<br />\r\n<br />\r\nĐối với một chưởng n&agrave;y uy lực, Trương Nhược Trần vẫn c&oacute; ch&uacute;t h&agrave;i l&ograve;ng.<br />\r\n<br />\r\nMặc d&ugrave; Long Tượng B&agrave;n Nhược Chưởng hiện tại chỉ l&agrave; Nh&acirc;n cấp hạ phẩm v&otilde; kỹ, lại so những người kh&aacute;c cấp hạ phẩm v&otilde; kỹ c&agrave;ng cao minh hơn, sức mạnh b&ugrave;ng l&ecirc;n cũng c&agrave;ng th&ecirc;m cường đại.<br />\r\n<br />\r\n&quot;Phẩm cấp c&agrave;ng cao v&otilde; kỹ, tu luyện c&agrave;ng kh&oacute;. Nếu l&agrave; ta hiện tại liền trực tiếp tu luyện Linh cấp v&otilde; kỹ, căn bản kh&ocirc;ng c&oacute; khả năng tại cửu thi&ecirc;n b&ecirc;n trong tu luyện th&agrave;nh c&ocirc;ng, &iacute;t nhất cũng phải tốn hao nửa năm trở l&ecirc;n thời gian. M&agrave; lại, bằng v&agrave;o ta ch&acirc;n kh&iacute; trong cơ thể số lượng, cũng v&ocirc; ph&aacute;p thi triển ra Linh cấp v&otilde; kỹ.&quot;<br />\r\n<br />\r\nTu luyện v&otilde; kỹ c&ugrave;ng tu luyện c&ocirc;ng ph&aacute;p thời gian, nhất định phải hợp l&yacute; an b&agrave;i.<br />\r\n<br />\r\nNếu l&agrave; thi&ecirc;n về tại tu luyện v&otilde; kỹ, m&agrave; xem nhẹ c&ocirc;ng ph&aacute;p tu luyện, liền sẽ dẫn đến tu vi cảnh giới chậm chạp.<br />\r\n<br />\r\nNếu l&agrave; thi&ecirc;n về tại tu luyện c&ocirc;ng ph&aacute;p, m&agrave; xem nhẹ v&otilde; kỹ tu luyện, tại c&ugrave;ng người giao thủ thời điểm, liền sẽ thiệt th&ograve;i lớn.<br />\r\n<br />\r\nV&ocirc; luận n&oacute;i như thế n&agrave;o, tu luyện th&agrave;nh Long Tượng B&agrave;n Nhược Chưởng thứ nhất chưởng, Trương Nhược Trần tại 800 năm về sau, rốt cục sơ bộ c&oacute; được năng lực tự vệ.<br />\r\n<br />\r\nC&aacute;i n&agrave;y ch&iacute;n ng&agrave;y, Trương Nhược Trần tu vi cũng tiến bộ cực lớn, Kh&iacute; Tr&igrave; b&ecirc;n trong ch&acirc;n kh&iacute; đ&atilde; tu luyện đ&acirc;̀y, c&oacute; thể bắt đầu mở đầu thứ hai g&acirc;n mạch.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n<br />\r\nNhưng l&agrave; muốn mở g&acirc;n mạch, nhất định phải phục dụng Tẩy Tủy Dịch. Vương hậu vẻn vẹn chỉ cấp Trương Nhược Trần một phần Tẩy Tủy Dịch, tại mở đầu thứ nhất kinh mạch thời điểm liền đ&atilde; sử dụng.<br />\r\n<br />\r\nMuốn thế n&agrave;o mới c&oacute; thể đi l&agrave;m phần thứ hai Tẩy Tủy Dịch, thậm ch&iacute; nhiều hơn Tẩy Tủy Dịch?<br />\r\n<br />\r\n&quot;Cửu vương tử, nương nương ch&iacute;nh bốn ph&iacute;a t&igrave;m ngươi, ngươi ở chỗ n&agrave;y l&agrave;m g&igrave;?&quot; Thị nữ V&acirc;n Nhi nh&igrave;n thấy đứng tại trong s&acirc;n Trương Nhược Trần, thuận tiện kỳ đi tới.<br />\r\n<br />\r\nV&acirc;n Nhi l&agrave; L&acirc;m Phi c&ugrave;ng Trương Nhược Trần b&ecirc;n người duy nhất thị nữ, đại kh&aacute;i 17, 18 d&aacute;ng vẻ, d&aacute;ng dấp c&oacute; ch&uacute;t mỹ lệ, con mắt s&aacute;ng tỏ, c&aacute;i cằm nhọn vểnh l&ecirc;n.<br />\r\n<br />\r\nTrương Nhược Trần đi đến V&acirc;n Nhi đối diện, ngăn trở V&acirc;n Nhi &aacute;nh mắt, để tr&aacute;nh n&agrave;ng nh&igrave;n thấy xa xa c&aacute;i kia một khối ng&agrave;n c&acirc;n tr&ecirc;n tảng đ&aacute; lớn hai c&aacute;i thủ ấn, &acirc;n cần hỏi: &quot;V&acirc;n Nhi tỷ tỷ, tay ngươi tr&ecirc;n c&aacute;nh tay thương thế tốt l&ecirc;n ch&uacute;t &iacute;t sao?&quot;<br />\r\n<br />\r\nV&acirc;n Nhi nhẹ nh&agrave;ng lắc đầu, n&oacute;i: &quot;Thương c&acirc;n động cốt một trăm ng&agrave;y, chỉ sợ c&ograve;n muốn qua hai ba th&aacute;ng mới c&oacute; thể khỏi hẳn.&quot;<br />\r\n<br />\r\nC&aacute;nh tay n&agrave;ng b&ecirc;n tr&ecirc;n thương, liền l&agrave; trước mấy ng&agrave;y bị B&aacute;t vương tử một chưởng đẩy đi ra t&eacute; g&atilde;y xương cốt. Đối với c&aacute;c n&agrave;ng dạng n&agrave;y tỳ nữ, đừng n&oacute;i l&agrave; quẳng gãy xương đầu, liền xem như đưa c&aacute;c n&agrave;ng loạn c&ocirc;n đ&aacute;nh chết, B&aacute;t vương tử cũng kh&ocirc;ng cần phụ bất cứ tr&aacute;ch nhiệm n&agrave;o.<br />\r\n<br />\r\nTại cường giả vi t&ocirc;n thế giới, kẻ yếu căn bản kh&ocirc;ng c&oacute; bất luận c&aacute;i g&igrave; địa vị.<br />\r\n<br />\r\nTrương Nhược Trần n&oacute;i: &quot;V&igrave; c&aacute;i g&igrave; kh&ocirc;ng mua sắm một phần C&acirc;n Cốt Đoạn Tục Cao?&quot;<br />\r\n<br />\r\nV&acirc;n Nhi chịu đựng c&aacute;nh tay đau đớn, đắng chát cười một tiếng: &quot;Một phần k&eacute;m nhất C&acirc;n Cốt Đoạn Tục Cao cũng cần tốn hao 200 mai ng&acirc;n tệ mới c&oacute; thể mua được, giống ch&uacute;ng ta dạng n&agrave;y người hạ đẳng, căn bản sử dụng kh&ocirc;ng dậy nổi. Cửu vương tử, ngươi c&oacute; thể quan t&acirc;m ch&uacute;ng ta những n&agrave;y n&ocirc; tỳ liền đ&atilde; rất kh&aacute;. Nhanh đi với ta gặp nương nương, h&ocirc;m nay, ch&uacute;ng ta muốn xuất cung.&quot;<br />\r\n<br />\r\nTrương Nhược Trần c&ugrave;ng sau lưng V&acirc;n Nhi, hiếu kỳ hỏi: &quot;Xuất cung? Đi nơi n&agrave;o?&quot;<br />\r\n<br />\r\n&quot;Đi gặp N&iacute;nh San a! Thật l&acirc;u kh&ocirc;ng c&oacute; gặp n&agrave;ng, ngươi khẳng định thật cao hứng a?&quot; V&acirc;n Nhi tr&ecirc;n mặt lộ ra nụ cười x&aacute;n lạn, nh&igrave;n chằm chằm Trương Nhược Trần.<br />\r\n<br />\r\nMỗi lần n&oacute;i đến &quot;N&iacute;nh San&quot;, hắn liền khẳng định sẽ mặt đỏ tới mang tai, ngượng ng&ugrave;ng giống như một c&aacute;i đại c&ocirc; nương.<br />\r\n<br />\r\n&quot;N&iacute;nh San l&agrave; ai?&quot; C&acirc;u n&oacute;i n&agrave;y Trương Nhược Trần vừa định hỏi ra lời, liền lập tức nuốt trở v&agrave;o.<br />\r\n<br />\r\nRất hiển nhi&ecirc;n, bệnh chết trước Trương Nhược Trần, khẳng định l&agrave; nhận biết vị kia gọi l&agrave; N&iacute;nh San nữ tử, m&agrave; lại nghe V&acirc;n Nhi ngữ kh&iacute;, bọn hắn quan hệ n&oacute;i kh&ocirc;ng chừng c&ograve;n rất kh&ocirc;ng.<br />\r\n<br />\r\nNếu l&agrave; Trương Nhược Trần hỏi ra &quot;N&iacute;nh San l&agrave; ai&quot;, khẳng định sẽ lộ tẩy. Đ&atilde; như vậy, vậy liền giữ y&ecirc;n lặng, c&agrave;ng &iacute;t n&oacute;i hơn c&agrave;ng tốt.<br />\r\n<br />\r\nMay mắn những năm n&agrave;y, Trương Nhược Trần một mực người yếu nhiều bệnh, ngoại trừ L&acirc;m Phi một mực quan t&acirc;m hắn, liền rất &iacute;t c&ugrave;ng người tiếp x&uacute;c, nếu kh&ocirc;ng, đo&aacute;n chừng hắn cũng sớm đ&atilde; bị người ho&agrave;i nghi.<br />\r\n<br />\r\nV&acirc;n Nhi gặp Trương Nhược Trần v&acirc;̣y mà rất b&igrave;nh tĩnh d&aacute;ng vẻ, trong l&ograve;ng hơi c&oacute; ch&uacute;t kinh ngạc, nhưng cũng kh&ocirc;ng nghĩ ngợi th&ecirc;m, tiếp tục hướng về L&acirc;m Phi ở lại viện lạc đi đến.</p>', '2021-11-27 20:01:56', 16, 1),
(155, 44, 'Thông Thiên nghe được ta muốn chạy trốn', '1', '<p>Vu Y&ecirc;u nhất chiến, Hồng Hoang thế giới ph&aacute; n&aacute;t, h&oacute;a th&agrave;nh rất nhiều ch&acirc;u lục c&ugrave;ng th&aacute;i hư tinh kh&ocirc;ng.<br />\r\n<br />\r\nBởi v&igrave; địa vực nhỏ hẹp, chư thi&ecirc;n tu sĩ đều l&agrave; di chuyển Địa Ti&ecirc;n giới.<br />\r\n<br />\r\nHải ngoại c&oacute; một t&ograve;a ti&ecirc;n đảo!<br />\r\n<br />\r\nĐảo n&agrave;y li&ecirc;n mi&ecirc;n kh&ocirc;ng biết hơn ng&agrave;n tỉ dặm.<br />\r\n<br />\r\nĐảo ph&iacute;a tr&ecirc;n linh kh&iacute; bốc hơi, ti&ecirc;n vụ quanh quẩn.<br />\r\n<br />\r\nLinh Vi&ecirc;n tr&ecirc;u đ&ugrave;a, ti&ecirc;n hạc bay m&uacute;a.<br />\r\n<br />\r\nCh&iacute;nh l&agrave; Tiệt Gi&aacute;o Th&aacute;nh Nh&acirc;n, Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ đạo trường Kim Miết đảo.<br />\r\n<br />\r\n&quot;Đạo v&ocirc; danh, v&igrave; vạn vật bắt đầu; đạo kh&ocirc;ng thực, v&igrave; vạn vật chi h&igrave;nh.&quot;<br />\r\n<br />\r\n&quot;Đạo ph&aacute;p m&ecirc;nh m&ocirc;ng, v&igrave; niệm chỗ c&ugrave;ng, niệm đến hoa nở, niệm đến thủy kh&ocirc;.&quot;<br />\r\n<br />\r\n&quot;Ta chi đạo &middot; &middot; &middot; &quot;<br />\r\n<br />\r\n&middot; &middot; &middot;<br />\r\n<br />\r\nLượn lờ đạo &acirc;m đảo b&ecirc;n trong vờn quanh.<br />\r\n<br />\r\nTường v&acirc;n đỉnh, một bộ đạo b&agrave;o m&agrave;u xanh b&oacute;ng người ngồi cao.<br />\r\n<br />\r\nMiệng phun kim li&ecirc;n, quanh th&acirc;n đạo văn vờn quanh.<br />\r\n<br />\r\nTo&agrave;n bộ tr&ecirc;n h&ograve;n đảo, cũng l&agrave; địa dũng kim li&ecirc;n, thi&ecirc;n hoa loạn trụy.<br />\r\n<br />\r\nC&agrave;ng c&oacute; ng&agrave;n vạn đầu h&agrave; thụy kh&ocirc;ng trung cắt ngang, tốt một mảnh thần th&aacute;nh chi tướng.<br />\r\n<br />\r\nPh&iacute;a dưới sơn h&agrave; hồ nước ở giữa, đ&ocirc;ng đảo b&oacute;ng người ngồi ngay ngắn, nguy&ecirc;n một đ&aacute;m nghe được như si như say.<br />\r\n<br />\r\nXa hơn một ch&uacute;t chi địa, một số c&ograve;n chưa biến h&oacute;a tiểu y&ecirc;u, phủ phục tại đất, cũng l&agrave; say m&ecirc; b&ecirc;n trong.<br />\r\n<br />\r\nNgồi ngay ngắn bồ đo&agrave;n b&ecirc;n tr&ecirc;n Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ, nh&igrave;n lấy t&igrave;nh cảnh trước mắt, trong l&ograve;ng rất l&agrave; h&agrave;i l&ograve;ng.<br />\r\n<br />\r\nVạn ti&ecirc;n triều b&aacute;i!<br />\r\n<br />\r\nNgắn ngủi nhiều năm như vậy, Tiệt Gi&aacute;o th&igrave; c&oacute; thịnh huống như thế, Th&ocirc;ng Thi&ecirc;n rất l&agrave; tự h&agrave;o.<br />\r\n<br />\r\nTuy nhi&ecirc;n đ&ocirc;ng đảo m&ocirc;n nh&acirc;n linh căn cao thấp kh&ocirc;ng đều, nhưng thắng ở số lượng nhiều, hạt giống tốt vẫn l&agrave; c&oacute; kh&ocirc;ng &iacute;t.<br />\r\n<br />\r\nĐặc biệt l&agrave; ph&iacute;a trước nhất Đa Bảo, V&ocirc; Đương, Tam Ti&ecirc;u mấy vị đệ tử th&acirc;n truyền, đều l&agrave; thi&ecirc;n ph&uacute; bất ph&agrave;m, b&acirc;y giờ đạo hạnh cũng kh&ocirc;ng k&eacute;m.<br />\r\n<br />\r\nSuy nghĩ một ch&uacute;t l&uacute;c n&agrave;y Xiển Gi&aacute;o c&aacute;i kia m&egrave;o con hai ba con, lấy c&aacute;i g&igrave; c&ugrave;ng hắn so s&aacute;nh.<br />\r\n<br />\r\nNghĩ đến Nguy&ecirc;n Thủy Thi&ecirc;n T&ocirc;n gi&aacute;o nghĩa, Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ cũng l&agrave; lắc đầu li&ecirc;n tục.<br />\r\n<br />\r\nĐại Đạo 50, thi&ecirc;n diễn 49, vạn vật đều c&oacute; một đường sinh cơ, tự nhi&ecirc;n hữu gi&aacute;o v&ocirc; loại, b&igrave;nh thường c&oacute; hướng đạo chi t&acirc;m đều c&oacute; thể dạy, v&igrave; vạn vật lấy ra một đường sinh cơ.<br />\r\n<br />\r\nTo&agrave;n bộ Hồng Hoang đại địa đều l&agrave; B&agrave;n Cổ phụ thần biến th&agrave;nh, tại sao đủ loại kh&aacute;c biệt ph&acirc;n chia.<br />\r\n<br />\r\nVạn linh vạn vật c&oacute; cầu đạo chi t&acirc;m, tự nhi&ecirc;n c&ugrave;ng nhau điểm h&oacute;a, sao c&oacute; thể theo mới gi&aacute;o h&oacute;a.<br />\r\n<br />\r\n&quot;Vu Y&ecirc;u lượng kiếp đ&atilde; qua, l&uacute;c n&agrave;y coi ta Tiệt Gi&aacute;o đại hưng.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nNh&igrave;n ph&iacute;a dưới đoan tọa đ&ocirc;ng đảo b&oacute;ng người, Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ trong l&ograve;ng v&ocirc; c&ugrave;ng thoải m&aacute;i.<br />\r\n<br />\r\nTiệt Gi&aacute;o như mặt trời giữa trưa, kh&iacute; vận k&eacute;o d&agrave;i, hắn Th&ocirc;ng Thi&ecirc;n tự nhi&ecirc;n lần c&oacute; mặt mũi.<br />\r\n<br />\r\n【 chậc chậc chậc! Tiệt Gi&aacute;o danh xưng vạn ti&ecirc;n triều b&aacute;i quả nhi&ecirc;n kh&ocirc;ng phải kho&aacute;c l&aacute;c, chỉ l&agrave; đ&aacute;ng tiếc, đ&aacute;ng tiếc! 】<br />\r\n<br />\r\nĐang l&uacute;c Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ sắc mặt vui vẻ thời khắc, bỗng nhi&ecirc;n một thanh &acirc;m đ&aacute;nh g&atilde;y hắn trầm tư.<br />\r\n<br />\r\nNgồi ngay ngắn bồ đo&agrave;n b&ecirc;n tr&ecirc;n Th&ocirc;ng Thi&ecirc;n, nhướng m&agrave;y.<br />\r\n<br />\r\nHắn tuy nhi&ecirc;n hữu gi&aacute;o v&ocirc; loại, nhưng giảng đạo số lần cũng kh&ocirc;ng nhiều.<br />\r\n<br />\r\nHiện tại cơ hội tốt như vậy kh&ocirc;ng cố gắng tĩnh tu, cảm ngộ, lại c&ograve;n c&oacute; người ở thời đi&ecirc;̉m này n&oacute;i chuyện.<br />\r\n<br />\r\nĐối phương ngữ kh&iacute;, tựa hồ c&ograve;n mang theo một ch&uacute;t thương hại.<br />\r\n<br />\r\nD&ugrave; chưa trước ti&ecirc;n nh&igrave;n thấy người trong cuộc, nhưng Th&ocirc;ng Thi&ecirc;n đ&atilde; c&oacute; thể tưởng tượng đối phương thời khắc n&agrave;y thần th&aacute;i.<br />\r\n<br />\r\nCảm kh&aacute;i ở giữa, khẽ lắc đầu.<br />\r\n<br />\r\nC&ugrave;ng hắn suy nghĩ trong l&ograve;ng c&oacute; b&aacute;c, tựa hồ kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o nh&igrave;n kỹ Tiệt Gi&aacute;o tương lai.<br />\r\n<br />\r\nTại b&acirc;y giờ to&agrave;n bộ Hồng Hoang b&ecirc;n trong, hắn nhưng l&agrave; đường đường Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n, l&uacute;c n&agrave;y Tiệt Gi&aacute;o lại c&oacute; bực n&agrave;y rầm rộ.<br />\r\n<br />\r\nHuống chi c&ograve;n l&agrave; tại Kim Miết đảo, ch&iacute;nh l&agrave; đạo trường của hắn.<br />\r\n<br />\r\nNgười n&agrave;o gan lớn c&ugrave;ng cực, d&aacute;m ph&aacute;t lời ấy luận?<br />\r\n<br />\r\nL&ograve;ng c&oacute; giận t&aacute;i đi Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ, hai con mắt h&iacute;p lại hướng về ph&iacute;a dưới nh&igrave;n lại.<br />\r\n<br />\r\nNh&igrave;n chung quanh một v&ograve;ng, hắn liếc một ch&uacute;t th&igrave; nh&igrave;n thấy nơi xa một b&oacute;ng người.<br />\r\n<br />\r\nĐ&oacute; l&agrave; vị ngồi ngay ngắn c&aacute;ch đ&oacute; kh&ocirc;ng xa đỉnh n&uacute;i tảng đ&aacute; sau thanh ni&ecirc;n.<br />\r\n<br />\r\nR&otilde; r&agrave;ng ngồi tại đỉnh n&uacute;i, kh&ocirc;ng nh&igrave;n kỹ c&ograve;n nhẹ dễ d&agrave;ng kh&ocirc;ng ph&aacute;t hiện được.<br />\r\n<br />\r\nTh&acirc;n mang một bộ đạo b&agrave;o m&agrave;u t&iacute;m, d&agrave;i đến c&aacute;i kia l&agrave; mắt ngọc m&agrave;y ng&agrave;i, nhan như quan ngọc, kh&iacute; vũ hi&ecirc;n ngang, ngọc thụ l&acirc;m phong, nh&igrave;n qua rất l&agrave; cảnh đẹp &yacute; vui.<br />\r\n<br />\r\nCũng l&agrave; một hạng kh&ocirc;ng thế n&agrave;o xem mặt Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ, cũng l&agrave; &acirc;m thầm kinh t&aacute;n.<br />\r\n<br />\r\nChỉ l&agrave; so với bốn ph&iacute;a nghi&ecirc;m t&uacute;c nghe giảng m&ocirc;n nh&acirc;n, vị thanh ni&ecirc;n n&agrave;y th&ograve; đầu ra nh&igrave;n, hết nh&igrave;n đ&ocirc;ng tới nh&igrave;n t&acirc;y.<br />\r\n<br />\r\nXem x&eacute;t liền kh&ocirc;ng c&oacute; chuy&ecirc;n t&acirc;m tu luyện.<br />\r\n<br />\r\nĐ&acirc;y cũng l&agrave; Th&ocirc;ng Thi&ecirc;n, thứ nhất mắt ch&uacute; &yacute; tới đối phương nguy&ecirc;n nh&acirc;n.<br />\r\n<br />\r\nC&ugrave;ng bốn ph&iacute;a so s&aacute;nh, lộ ra v&ocirc; c&ugrave;ng kh&ocirc;ng hợp nhau.<br />\r\n<br />\r\nTuy nhi&ecirc;n ở tr&ecirc;n đảo m&ocirc;n nh&acirc;n đ&ocirc;ng đảo, nhưng cơ bản c&aacute;c đệ tử hắn đều nhận biết.<br />\r\n<br />\r\nNgười n&agrave;y k&ecirc;u l&agrave; Trường Phong, l&agrave; hắn đ&ocirc;ng đảo m&ocirc;n nh&acirc;n b&ecirc;n trong một vị.<br />\r\n<br />\r\nKim Miết đảo ph&iacute;a tr&ecirc;n sinh trưởng ở địa phương n&agrave;y tiểu ngưu y&ecirc;u, huyết mạch c&ograve;n c&oacute; ch&uacute;t đặc th&ugrave;, ch&iacute;nh l&agrave; trong truyền thuyết Hồng Hoang dị chủng Thử Thiết.<br />\r\n<br />\r\n【 oa! C&aacute;i kia ch&iacute;nh l&agrave; Tiệt Gi&aacute;o mỹ nữ nổi danh Tam Ti&ecirc;u a, quả nhi&ecirc;n nguy&ecirc;n một đ&aacute;m d&agrave;i đến khuynh quốc khuynh th&agrave;nh, ti&ecirc;n tư dật mạo, ho&agrave;n to&agrave;n kh&ocirc;ng phải hậu thế loại kia li&ecirc;n mi&ecirc;n bất tận khu&ocirc;n mặt c&oacute; thể so s&aacute;nh. 】<br />\r\n<br />\r\n【 nếu c&oacute; thể kết th&agrave;nh đạo lữ liền tốt, ba c&aacute;i c&ugrave;ng một chỗ tựu canh diệu liễu. 】<br />\r\n<br />\r\nMới vừa vặn ch&uacute; &yacute; tới đối phương, Th&ocirc;ng Thi&ecirc;n sắc mặt đột nhi&ecirc;n trầm xuống, lại c&oacute; hai &acirc;m thanh vang l&ecirc;n.<br />\r\n<br />\r\nDọc theo tầm mắt của đối phương, Th&ocirc;ng Thi&ecirc;n đ&atilde; chắc chắn thanh &acirc;m đến từ c&aacute;i n&agrave;y tiểu ngưu y&ecirc;u.<br />\r\n<br />\r\nC&aacute;i n&agrave;y tiểu ngưu y&ecirc;u thật sự l&agrave; cả gan l&agrave;m loạn, kh&ocirc;ng cố gắng tu luyện cũng được, c&ograve;n th&egrave;m nhỏ d&atilde;i hắn đệ tử th&acirc;n truyền.<br />\r\n<br />\r\nThần th&aacute;i kia, biểu t&igrave;nh kia, Th&ocirc;ng Thi&ecirc;n đ&atilde; biết được gia hỏa n&agrave;y trong đầu l&agrave; c&aacute;i g&igrave; h&igrave;nh ảnh.<br />\r\n<br />\r\nBất qu&aacute; thời khắc n&agrave;y Th&ocirc;ng Thi&ecirc;n, cũng ph&aacute;t hiện một tia dị dạng.<br />\r\n<br />\r\nHắn cũng kh&ocirc;ng nh&igrave;n thấy Trường Phong mở miệng.<br />\r\n<br />\r\nKh&ocirc;ng n&oacute;i g&igrave;?<br />\r\n<br />\r\nĐối phương hướng hắn truyền &acirc;m?<br />\r\n<br />\r\nỞ trước mặt tr&ecirc;u chọc hắn đệ tử th&acirc;n truyền?<br />\r\n<br />\r\nChỉ sợ c&aacute;i n&agrave;y tiểu ngưu y&ecirc;u cũng kh&ocirc;ng c&oacute; sao m&agrave; to gan như vậy.<br />\r\n<br />\r\nLấy tiểu ngưu y&ecirc;u c&aacute;i kia yếu ớt tu vi, cũng căn bản kh&ocirc;ng thể n&agrave;o l&agrave;m được.<br />\r\n<br />\r\nHắn nhưng l&agrave; Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n.<br />\r\n<br />\r\nHơn nữa nh&igrave;n Trường Phong thần t&igrave;nh kia, tựa hồ cũng kh&ocirc;ng hiểu biết hắn có th&ecirc;̉ nghe được thanh &acirc;m n&agrave;y.<br />\r\n<br />\r\nTiếng l&ograve;ng!<br />\r\n<br />\r\nL&agrave; tiếng l&ograve;ng!<br />\r\n<br />\r\nHắn nghe được đối phương tiếng l&ograve;ng!<br />\r\n<br />\r\n&quot;Cổ qu&aacute;i! Cổ qu&aacute;i! C&aacute;i n&agrave;y tiểu ngưu y&ecirc;u tựa hồ c&oacute; chút kỳ lạ.&quot;<br />\r\n<br />\r\nNh&igrave;n qua ph&iacute;a dưới đạo th&acirc;n ảnh kia, Th&ocirc;ng Thi&ecirc;n Nh&atilde;n b&ecirc;n trong kinh nghi.<br />\r\n<br />\r\nTh&agrave;nh v&igrave; Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n về sau, thế gian vạn vật đ&atilde; rất &iacute;t c&oacute; để hắn động dung, nhưng lần n&agrave;y c&oacute; thể bắt hắn cho kinh h&atilde;i đến.<br />\r\n<br />\r\nTh&aacute;nh Nh&acirc;n tuy nhi&ecirc;n thủ đoạn th&ocirc;ng thi&ecirc;n, ph&iacute;a tr&ecirc;n xem x&eacute;t cửu thi&ecirc;n, ph&iacute;a dưới hạ U Minh, nhưng cũng kh&ocirc;ng c&aacute;ch n&agrave;o hiểu r&otilde; sinh linh tiếng l&ograve;ng.<br />\r\n<br />\r\nBởi v&igrave; n&oacute;i v&igrave; vạn vật tự nhi&ecirc;n, mỗi một vị sinh linh đều l&agrave; đạo sản phẩm.<br />\r\n<br />\r\nBọn họ c&oacute; thể suy đo&aacute;n, phỏng đo&aacute;n đối phương tiếng l&ograve;ng, lại kh&ocirc;ng c&aacute;ch n&agrave;o trực tiếp biết được.<br />\r\n<br />\r\nL&uacute;c n&agrave;y tại hắn m&ocirc;n nh&acirc;n b&ecirc;n trong, v&acirc;̣y mà xuất hiện như thế một vị đệ tử.<br />\r\n<br />\r\nL&agrave;m sao kh&ocirc;ng để hắn chấn kinh!<br />\r\n<br />\r\nThời khắc n&agrave;y Trường Phong, cũng kh&ocirc;ng biết đ&atilde; khiến cho Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ ch&uacute; &yacute;.<br />\r\n<br />\r\nMột đ&ocirc;i tr&ograve;ng mắt li&ecirc;n tiếp tại Tam Ti&ecirc;u tr&ecirc;n th&acirc;n qu&eacute;t tới qu&eacute;t lui.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nChỉ l&agrave; &aacute;nh mắt lưu luyến thời khắc, thần sắc c&oacute; ch&uacute;t cảm kh&aacute;i.<br />\r\n<br />\r\n【 d&agrave;i đến thật l&agrave; dễ nh&igrave;n, chỉ l&agrave; đ&aacute;ng tiếc Phong Thần đại kiếp b&ecirc;n trong l&ecirc;n bảng l&ecirc;n bảng, bị trấn &aacute;p trấn &aacute;p. 】<br />\r\n<br />\r\n【 c&aacute;i n&agrave;y Kim Miết đảo chỉ sợ cũng đợi kh&ocirc;ng được bao l&acirc;u, l&agrave; thời điểm chuẩn bị chạy. 】<br />\r\n<br />\r\nTrường Phong vốn l&agrave; tr&ecirc;n Địa Cầu th&ecirc;́ kỷ 21 một t&ecirc;n thanh ni&ecirc;n, hơn một ng&agrave;n năm trước vượt qua đến Hồng Hoang, c&agrave;ng l&agrave; thu được một c&aacute;i phi thường tr&acirc;u b&ograve; &times; hệ thống.<br />\r\n<br />\r\nNằm ngửa hệ thống, cũng gọi l&agrave; c&aacute; ướp muối hệ thống.<br />\r\n<br />\r\nChuyện g&igrave; mặc kệ, cả ng&agrave;y nằm th&igrave; c&oacute; thể kh&ocirc;ng ngừng mạnh l&ecirc;n.<br />\r\n<br />\r\nHuyết mạch kh&ocirc;ng tệ, lại th&ecirc;m lại c&oacute; hệ thống k&ecirc;̀ b&ecirc;n người, kh&ocirc;ng kh&aacute;c n&agrave;o ho&agrave;n mỹ bắt đầu.<br />\r\n<br />\r\nNhưng c&oacute; biết tại Kim Miết đảo về sau, vẫn l&agrave; Vu Y&ecirc;u lượng kiếp về sau Kim Miết đảo, Trường Phong liền c&oacute; ch&uacute;t kh&ocirc;ng b&igrave;nh tĩnh.<br />\r\n<br />\r\nVu Y&ecirc;u lượng kiếp về sau c&aacute;i kia ch&iacute;nh l&agrave; Phong Thần đại kiếp, đừng nh&igrave;n Tiệt Gi&aacute;o l&uacute;c n&agrave;y vạn ti&ecirc;n triều b&aacute;i, c&oacute; thể sau c&ugrave;ng chỉ lưu lại một V&ocirc; Đương Th&aacute;nh Mẫu, c&ograve;n c&oacute; mấy c&aacute;i con t&ocirc;m nhỏ.<br />\r\n<br />\r\nC&oacute; thể n&oacute;i chỉ c&ograve;n tr&ecirc;n danh nghĩa, Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ cũng biến th&agrave;nh chỉ huy một m&igrave;nh.<br />\r\n<br />\r\nTrường Phong cũng kh&ocirc;ng biết Tiệt Gi&aacute;o c&oacute; hay kh&ocirc;ng hắn c&aacute;i n&agrave;y nh&acirc;n vật c&oacute; tiếng tăm, nhưng lưu tại Kim Miết đảo rất đại kh&aacute;i dẫn l&agrave; muốn ph&iacute;a tr&ecirc;n Phong Thần Bảng.<br />\r\n<br />\r\nLiền xem như may mắn kh&ocirc;ng c&oacute; l&ecirc;n, cũng phải bị phương t&acirc;y hai c&aacute;i con lừa trọc bắt đi.<br />\r\n<br />\r\nĐương nhi&ecirc;n c&ograve;n c&oacute; thảm nhất, th&acirc;n tử đạo ti&ecirc;u, d&ugrave; sao Phong Thần Bảng cũng kh&ocirc;ng phải ai cũng c&oacute; thể l&ecirc;n.<br />\r\n<br />\r\nThật vất vả vượt qua m&agrave; đến, c&agrave;ng l&agrave; thu hoạch được cường đại hệ thống, l&agrave;m sao cam t&acirc;m bực n&agrave;y kết cục.<br />\r\n<br />\r\nMuốn kh&ocirc;ng phải lo lắng Hồng Hoang qu&aacute; mức nguy hiểm, Trường Phong đ&atilde; sớm đường chạy.<br />\r\n<br />\r\nD&ugrave; sao hắn b&acirc;y giờ l&agrave; con ngưu y&ecirc;u, vẫn l&agrave; dị th&uacute; Thử Thiết.<br />\r\n<br />\r\nHồng Hoang b&ecirc;n trong m&acirc;́y cái này đại l&atilde;o đều ưa th&iacute;ch cưỡi tr&acirc;u, đừng đến l&uacute;c đ&oacute; c&ograve;n kh&ocirc;ng c&oacute; nằm mạnh l&ecirc;n, đ&atilde; bị người bắt đi l&agrave;m th&uacute; cưỡi.<br />\r\n<br />\r\nCho n&ecirc;n Trường Phong cũng liền lưu tại Kim Miết đảo, trước ti&ecirc;n ở dưới đại thụ ngồi h&oacute;ng m&aacute;t, chờ thực lực mạnh hơn một ch&uacute;t, lại đi vụng trộm chạy đi.<br />\r\n<br />\r\nTo&agrave;n bộ Kim Miết đảo ph&iacute;a tr&ecirc;n sinh linh đều xem như Tiệt Gi&aacute;o m&ocirc;n nh&acirc;n, thiếu hắn một c&aacute;i cũng sẽ kh&ocirc;ng c&oacute; người ch&uacute; &yacute;.<br />\r\n<br />\r\nM&agrave; lại n&agrave;y một ng&agrave;n năm qua, hắn th&acirc;m cư kh&ocirc;ng ra ngo&agrave;i, cơ bản đều tại động phủ nằm, căn bản th&igrave; kh&ocirc;ng c&oacute; mấy người biết hắn.<br />\r\n<br />\r\nD&ugrave; sao biết được Tiệt Gi&aacute;o kết cục hắn, cũng kh&ocirc;ng d&aacute;m qu&aacute; nhiều c&ugrave;ng bọn hắn sinh ra gặp nhau.<br />\r\n<br />\r\nR&agrave;ng buộc c&agrave;ng nhiều, lượng kiếp b&ecirc;n trong th&igrave; c&agrave;ng kh&oacute; chỉ lo th&acirc;n m&igrave;nh.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; tiếng tăm g&igrave;, kh&ocirc;ng ngừng nằm ngửa mạnh l&ecirc;n mới l&agrave; chủ sự.<br />\r\n<br />\r\nMuốn kh&ocirc;ng phải l&uacute;c n&agrave;y Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ giảng đạo, tất cả Tiệt Gi&aacute;o đệ tử đều muốn tham dự, hắn đều kh&ocirc;ng muốn tới.<br />\r\n<br />\r\nTrường Phong trong l&ograve;ng cảm kh&aacute;i, thượng thủ Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ lại l&agrave; mặt lộ vẻ kinh nghi.<br />\r\n<br />\r\nHắn lại nghe thấy Trường Phong tiếng l&ograve;ng, m&agrave; lại lần n&agrave;y lượng tin tức c&oacute; ch&uacute;t nặng.</p>', '2021-11-27 20:19:06', 34, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(156, 45, 'Lắc xúc xắc mười một năm, một sớm khí vận đỉnh cấp', '1', '<p>&laquo; t&iacute;nh danh: H&agrave;n Tuyệt &raquo;<br />\r\n<br />\r\n&laquo; tuổi thọ: 11/65 &raquo;<br />\r\n<br />\r\n&laquo; chủng tộc: Ph&agrave;m nh&acirc;n &raquo;<br />\r\n<br />\r\n&laquo; tu vi: Kh&ocirc;ng &raquo;<br />\r\n<br />\r\n&laquo; c&ocirc;ng ph&aacute;p: Kh&ocirc;ng &raquo;<br />\r\n<br />\r\n&laquo; ph&aacute;p thuật: Kh&ocirc;ng &raquo;<br />\r\n<br />\r\n&laquo; thần th&ocirc;ng: Kh&ocirc;ng &raquo;<br />\r\n<br />\r\n&laquo; ph&aacute;p kh&iacute;: Kh&ocirc;ng &raquo;<br />\r\n<br />\r\n&laquo; linh căn tư chất: Cực k&eacute;m ( click đổ x&uacute;c xắc ) &raquo;<br />\r\n<br />\r\n&laquo; Ti&ecirc;n Thi&ecirc;n kh&iacute; vận như sau ( click đổ x&uacute;c xắc ) &raquo;<br />\r\n<br />\r\n&laquo; Thổ Mộc song linh: Thổ, Mộc linh căn tư chất tăng cường &raquo;<br />\r\n<br />\r\n&laquo; Thương Đạo Linh Đồng: Thương Đạo tư chất tăng cường, thể ph&aacute;ch tăng cường &raquo;<br />\r\n<br />\r\n&laquo; click mở bắt đầu du h&iacute; cuộc đời &raquo;<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nNh&igrave;n qua trước mắt thuộc t&iacute;nh liệt biểu, 11 tuổi H&agrave;n Tuyệt nhanh tuyệt vọng.<br />\r\n<br />\r\nLinh căn tư chất, Ti&ecirc;n Thi&ecirc;n kh&iacute; vận mỗi ng&agrave;y đều c&oacute; thể đổ x&uacute;c xắc ngẫu nhi&ecirc;n cải biến, nhưng chỉ có th&ecirc;̉ lắc một lần, mỗi ng&agrave;y bảy giờ s&aacute;ng c&oacute; thể đổi mới.<br />\r\n<br />\r\nH&agrave;n Tuyệt từ xuất sinh l&ecirc;n liền bắt đầu lắc.<br />\r\n<br />\r\nMười một năm, đều kh&ocirc;ng c&oacute; lắc ra khỏi cực phẩm tư chất c&ugrave;ng tr&aacute;c tuyệt Ti&ecirc;n Thi&ecirc;n kh&iacute; vận.<br />\r\n<br />\r\n&quot;Nếu kh&ocirc;ng cứ như vậy?&quot;<br />\r\n<br />\r\nMột c&aacute;i &yacute; niệm trong đầu từ H&agrave;n Tuyệt trong l&ograve;ng to&aacute;t ra.<br />\r\n<br />\r\nKh&ocirc;ng được!<br />\r\n<br />\r\nThật vất vả đi v&agrave;o thần ti&ecirc;n quỷ qu&aacute;i thế giới, h&aacute; c&oacute; thể ph&agrave;m nh&acirc;n tu ti&ecirc;n?<br />\r\n<br />\r\nH&agrave;n Tuyệt muốn l&agrave;m sảng văn nam ch&iacute;nh!<br />\r\n<br />\r\nLại lắc!<br />\r\n<br />\r\nH&agrave;n Tuyệt đưa tay, hướng về ph&iacute;a trước mặt thuộc t&iacute;nh liệt biểu một ch&uacute;t.<br />\r\n<br />\r\nLinh căn tư chất cải biến!<br />\r\n<br />\r\n&laquo; linh căn tư chất: Kh&ocirc;ng &raquo;<br />\r\n<br />\r\nH&agrave;n Tuyệt tấm kia non nớt mặt thiếu ni&ecirc;n trong nh&aacute;y mắt đen lại.<br />\r\n<br />\r\nLại điểm!<br />\r\n<br />\r\n&laquo; Thi&ecirc;n Mệnh C&ocirc; Tinh: Khắc th&acirc;n khắc lữ khắc bạn, c&ocirc; độc cả đời, tuổi thọ gia tăng trăm năm &raquo;<br />\r\n<br />\r\nMẹ n&oacute;!<br />\r\n<br />\r\nThi&ecirc;n Mệnh C&ocirc; Tinh đều đi ra!</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nAi muốn c&ocirc; độc cả đời!<br />\r\n<br />\r\nH&agrave;n Tuyệt tức giận đến nằm xuống, ở tr&ecirc;n đồng cỏ lăn lộn chết thẳng cẳng.<br />\r\n<br />\r\nR&ograve;ng r&atilde; mười một năm đều lắc kh&ocirc;ng ra x&acirc;u tạc thi&ecirc;n linh căn tư chất, Ti&ecirc;n Thi&ecirc;n kh&iacute; vận!<br />\r\n<br />\r\nKh&ocirc;ng được!<br />\r\n<br />\r\nTiếp tục lắc!<br />\r\n<br />\r\nLão tử kh&ocirc;ng tin!<br />\r\n<br />\r\nH&agrave;n Tuyệt tức run người.<br />\r\n<br />\r\nGiày vò nửa giờ, hắn mới đứng dậy.<br />\r\n<br />\r\nH&agrave;n Tuyệt l&agrave; tr&ugrave;ng sinh nh&acirc;n sĩ, kiếp trước đến từ Địa Cầu thế kỷ 21, tuổi c&ograve;n trẻ liền bị tra ra ung thư thời kỳ cuối, hắn kh&ocirc;ng muốn thống khổ trị liệu, về nh&agrave; chờ chết, v&agrave;o l&uacute;c ban đ&ecirc;m v&igrave; t&ecirc; liệt ch&iacute;nh m&igrave;nh, hắn t&igrave;m một c&aacute;i ho&agrave;i cựu tu ti&ecirc;n tr&ograve; chơi chơi.<br />\r\n<br />\r\nChơi một c&aacute;i suốt đ&ecirc;m, rất n&agrave;y, hừng đ&ocirc;ng l&uacute;c buồn ngủ, sau đ&oacute; kh&ocirc;ng c&oacute; người.<br />\r\n<br />\r\nLần nữa mở mắt, hắn liền đầu thai đi v&agrave;o c&aacute;i n&agrave;y c&ugrave;ng loại cổ đại thế giới, hắn sinh ra ở một chi tu ti&ecirc;n m&ocirc;n ph&aacute;i b&ecirc;n trong.<br />\r\n<br />\r\nNgọc Thanh t&ocirc;ng, Đại Yến vương triều ch&iacute;nh đạo tu ti&ecirc;n m&ocirc;n ph&aacute;i.<br />\r\n<br />\r\nTại bị tra ra ung thư thời kỳ cuối ng&agrave;y đ&oacute;, H&agrave;n Tuyệt kh&ocirc;ng g&igrave; s&aacute;nh được sợ h&atilde;i, lần đầu biết sinh mệnh trọng yếu như vậy.<br />\r\n<br />\r\nMột thế n&agrave;y v&acirc;̣y mà c&oacute; thể tu ti&ecirc;n!<br />\r\n<br />\r\nHắn kinh hỉ qu&aacute; đỗi!<br />\r\n<br />\r\nHắn nhất định phải tu ti&ecirc;n!<br />\r\n<br />\r\nHắn muốn sống đ&ecirc;́n so với ai kh&aacute;c đều l&acirc;u!<br />\r\n<br />\r\nNhưng hắn kh&ocirc;ng c&oacute; khả năng ph&agrave;m nh&acirc;n tu ti&ecirc;n!<br />\r\n<br />\r\nTừ khi ra đời về sau, H&agrave;n Tuyệt liền sống được kh&ocirc;ng c&oacute; &aacute;p lực, cha mẹ của hắn l&agrave; Ngọc Thanh t&ocirc;ng ngoại m&ocirc;n Luyện Đan sư Thiết l&atilde;o n&ocirc; bộc, ng&agrave;y b&igrave;nh thường l&agrave; Thiết l&atilde;o trồng trọt dược thảo.<br />\r\n<br />\r\nLuyện Đan sư địa vị đặc th&ugrave;, ở ngoại m&ocirc;n, kh&ocirc;ng người n&agrave;o d&aacute;m đắc tội Thiết l&atilde;o, Thiết l&atilde;o thủ hạ c&oacute; hơn mười vị n&ocirc; bộc, tất cả đều l&agrave; ph&agrave;m nh&acirc;n.<br />\r\n<br />\r\nMặc d&ugrave; c&oacute; linh căn, Thiết l&atilde;o cũng kh&ocirc;ng cho ph&eacute;p n&ocirc; bộc tu luyện, đo&aacute;n chừng l&agrave; sợ đ&aacute;nh cắp hắn thảo dược.<br />\r\n<br />\r\nHắn thảo dược đối với tu ti&ecirc;n giả m&agrave; n&oacute;i tr&agrave;n ngập c&oacute; &iacute;ch, nhưng đối với ph&agrave;m nh&acirc;n m&agrave; n&oacute;i l&agrave; tuyệt đối độc dược.<br />\r\n<br />\r\nBất qu&aacute; 6 tuổi năm đ&oacute;, cha mẹ của hắn chạy tr&ocirc;́n, lưu lại tuổi nhỏ H&agrave;n Tuyệt tại Thiết l&atilde;o trong vườn dược thảo.<br />\r\n<br />\r\nH&agrave;n Tuyệt cũng c&oacute; thể l&yacute; giải, mang theo một t&ecirc;n h&agrave;i đồng chạy trốn, khẳng định c&oacute; nhiều bất tiện.<br />\r\n<br />\r\nThiết l&atilde;o cũng kh&ocirc;ng c&oacute; so đo, ngược lại để cho người ta dẫn đầu H&agrave;n Tuyệt bắt đầu hỗ trợ trồng trọt thảo dược.<br />\r\n<br />\r\nThời gian l&acirc;u d&agrave;i, H&agrave;n Tuyệt cũng đem trong vườn dược thảo tất cả hoa hoa thảo thảo biết r&otilde;.<br />\r\n<br />\r\nThiết l&atilde;o kh&ocirc;ng c&oacute; l&agrave;m kh&oacute; dễ H&agrave;n Tuyệt, H&agrave;n Tuyệt liền tiếp theo lắc Ti&ecirc;n Thi&ecirc;n kh&iacute; vận.<br />\r\n<br />\r\nD&ugrave; sao hắn hiện tại l&agrave; ph&agrave;m nh&acirc;n, kh&ocirc;ng bằng chờ một ch&uacute;t.<br />\r\n<br />\r\n&quot;Ai, tiếp tục lắc đi, nếu l&agrave; 30 tuổi trước lắc kh&ocirc;ng ra nghịch thi&ecirc;n kh&iacute; vận, qu&ecirc;n đi, ph&agrave;m nh&acirc;n tu ti&ecirc;n liền ph&agrave;m nh&acirc;n tu ti&ecirc;n.&quot;<br />\r\n<br />\r\nH&agrave;n Tuyệt y&ecirc;n lặng nghĩ đến.<br />\r\n<br />\r\nTrong vườn dược thảo nhất tuổi gi&agrave; n&ocirc; bộc đ&atilde; hơn 70 tuổi, t&ecirc;n l&agrave; Vương l&atilde;o đầu, hắn mười mấy tuổi l&uacute;c liền bị Thiết l&atilde;o chọn tr&uacute;ng, b&acirc;y giờ đ&atilde; l&agrave; n&ocirc; bộc l&atilde;nh tụ, bọn n&ocirc; bộc đều lấy hắn như thi&ecirc;n l&ocirc;i sai đ&acirc;u đ&aacute;nh đ&oacute;.<br />\r\n<br />\r\nH&agrave;n Tuyệt đứng dậy, trở lại vườn dược thảo, bắt đầu vẩy nước, thu thập l&aacute; kh&ocirc;.<br />\r\n<br />\r\nVườn dược thảo rất lớn, chừng một c&aacute;i s&acirc;n b&oacute;ng đ&aacute; lớn như vậy, mỗi một vị n&ocirc; bộc đang bận rộn l&uacute;c đều cẩn thận, nếu l&agrave; kh&ocirc;ng cẩn thận ph&aacute; hủy hoa cỏ, Thiết l&atilde;o nhất định tức giận, c&oacute; một &iacute;t dược thảo thậm ch&iacute; tr&agrave;n ngập kịch độc.<br />\r\n<br />\r\nThiết l&atilde;o th&aacute;ng trước mới ra ngo&agrave;i, đo&aacute;n chừng muốn hai ba năm mới c&oacute; thể trở về.<br />\r\n<br />\r\nĐối với tu ti&ecirc;n giả m&agrave; n&oacute;i, hai ba năm căn bản t&iacute;nh kh&ocirc;ng được c&aacute;i g&igrave;.<br />\r\n<br />\r\nTại vườn dược thảo b&ecirc;n trong, H&agrave;n Tuyệt trầm mặc &iacute;t n&oacute;i, cũng kh&ocirc;ng c&oacute; bằng hữu n&agrave;o, cũng liền c&ugrave;ng Vương l&atilde;o đầu giao lưu nhi&ecirc;̀u.<br />\r\n<br />\r\nL&agrave;m xong về sau, hắn liền trở về ph&ograve;ng, bắt đầu tập chống đẩy - h&iacute;t đất, r&egrave;n luyện thể ph&aacute;ch.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nH&ocirc;m sau trời vừa s&aacute;ng, H&agrave;n Tuyệt rửa mặt xong.<br />\r\n<br />\r\nHắn ngồi tại tr&ecirc;n giường c&acirc;y chờ đợi.<br />\r\n<br />\r\nMột mực đợi đến thuộc t&iacute;nh liệt biểu đổi mới điểm thời gian, H&agrave;n Tuyệt mới tinh thần phấn chấn.<br />\r\n<br />\r\nC&oacute; loại rút thưởng cảm gi&aacute;c.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; hắn mỗi ng&agrave;y mong đợi nhất sự t&igrave;nh.<br />\r\n<br />\r\nHắn xoa xoa đ&ocirc;i b&agrave;n tay.<br />\r\n<br />\r\nTrước lắc linh căn tư chất.<br />\r\n<br />\r\n&laquo; linh căn tư chất: Kh&ocirc;ng &raquo;<br />\r\n<br />\r\nThảo!<br />\r\n<br />\r\nĐ&acirc;y cũng qu&aacute; đen đi!<br />\r\n<br />\r\nH&agrave;n Tuyệt k&eacute;m ch&uacute;t tức chết.<br />\r\n<br />\r\nTay của hắn cũng bắt đầu run rẩy, tiếp tục r&uacute;t Ti&ecirc;n Thi&ecirc;n kh&iacute; vận!<br />\r\n<br />\r\nX&uacute;c xắc lay động!<br />\r\n<br />\r\n&laquo; Ti&ecirc;n Thi&ecirc;n kh&iacute; vận như sau &raquo;<br />\r\n<br />\r\n&laquo; tuyệt thế v&ocirc; song: Ti&ecirc;n tư, mị lực đỉnh cấp &raquo;<br />\r\n<br />\r\n&laquo; Thi&ecirc;n Mệnh Kiếm Si: Kiếm Đạo tư chất đỉnh cấp, Kiếm Đạo ngộ t&iacute;nh đỉnh cấp &raquo;<br />\r\n<br />\r\n&laquo; th&acirc;n ph&aacute;p tuyệt trần: Th&acirc;n ph&aacute;p tư chất đỉnh cấp &raquo;<br />\r\n<br />\r\n&laquo; Ti&ecirc;n Đế hậu duệ: Du h&iacute; cuộc đời bắt đầu về sau, thu hoạch được một bộ tuyệt thế c&ocirc;ng ph&aacute;p tu ti&ecirc;n, 1000 khối linh thạch thượng phẩm &raquo;<br />\r\n<br />\r\nH&agrave;n Tuyệt ngẩn người.<br />\r\n<br />\r\nHắn trừng to mắt, lập tức kinh hỉ.<br />\r\n<br />\r\nBốn c&aacute;i Ti&ecirc;n Thi&ecirc;n kh&iacute; vận!<br />\r\n<br />\r\nĐ&acirc;y l&agrave; lần đầu đổi mới ra bốn c&aacute;i Ti&ecirc;n Thi&ecirc;n kh&iacute; vận, hơn nữa thoạt nh&igrave;n đều rất ngưu ph&ecirc;.<br />\r\n<br />\r\nH&agrave;n Tuyệt c&agrave;ng xem c&agrave;ng hưng phấn.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nCh&iacute;nh l&agrave; n&oacute;!<br />\r\n<br />\r\nKh&ocirc;ng!<br />\r\n<br />\r\nBọn ch&uacute;ng!<br />\r\n<br />\r\nBốn c&aacute;i đỉnh cấp, một c&aacute;i tuyệt thế!<br />\r\n<br />\r\nXem x&eacute;t liền v&ocirc; c&ugrave;ng gh&ecirc; gớm.<br />\r\n<br />\r\nRung mười một năm, ng&agrave;y qua ng&agrave;y, Ho&agrave;ng Thi&ecirc;n rốt cục kh&ocirc;ng phụ người khổ t&acirc;m!<br />\r\n<br />\r\nH&agrave;n Tuyệt cố gắng b&igrave;nh phục t&acirc;m t&igrave;nh.<br />\r\n<br />\r\nLinh căn tư chất kh&ocirc;ng c&oacute;, hắn tạm thời c&ograve;n kh&ocirc;ng thể click mở bắt đầu du h&iacute; cuộc đời.<br />\r\n<br />\r\nC&ograve;n phải lại lung lay linh căn.<br />\r\n<br />\r\n&quot;Rốt cục khổ tận cam lai, c&oacute; c&aacute;i n&agrave;y bốn c&aacute;i cực phẩm Ti&ecirc;n Thi&ecirc;n kh&iacute; vận, ta cho d&ugrave; 40 tuổi tu luyện, cũng được, ta c&oacute; thể an t&acirc;m lắc linh căn tư chất.&quot;<br />\r\n<br />\r\nH&agrave;n Tuyệt nghĩ tới đ&acirc;y, t&acirc;m t&igrave;nh đắc &yacute;.<br />\r\n<br />\r\nĐều đ&atilde; lắc mười một năm, lại lắc mười một năm th&igrave; như thế n&agrave;o?<br />\r\n<br />\r\nH&agrave;n Tuyệt thở d&agrave;i ra một hơi, sau đ&oacute; đứng dậy ra khỏi ph&ograve;ng bắt đầu h&ocirc;m nay lao động.<br />\r\n<br />\r\nHắn chỗ ph&ograve;ng ốc c&oacute; s&aacute;u người ở, một người một c&aacute;i giường, những người kh&aacute;c s&aacute;ng sớm liền rời giường.<br />\r\n<br />\r\nMỗi người đều c&oacute; ch&iacute;nh m&igrave;nh phụ tr&aacute;ch một v&ugrave;ng khu vực, kh&ocirc;ng d&aacute;m c&oacute; sơ xuất.<br />\r\n<br />\r\nH&agrave;n Tuyệt c&ograve;n tuổi nhỏ, chỉ cần l&agrave;m đơn giản một ch&uacute;t c&ocirc;ng việc, Thiết l&atilde;o cũng kh&ocirc;ng d&aacute;m để hắn phụ tr&aacute;ch một v&ugrave;ng khu vực.<br />\r\n<br />\r\nH&ocirc;m nay &aacute;nh nắng đặc biệt tươi đẹp.<br />\r\n<br />\r\nC&oacute; lẽ c&ugrave;ng t&acirc;m t&igrave;nh c&oacute; quan hệ.<br />\r\n<br />\r\nMặt kh&aacute;c n&ocirc; bộc kh&ocirc;ng c&oacute; cảm nhận được H&agrave;n Tuyệt biến h&oacute;a, c&ograve;n chưa bắt đầu du h&iacute; cuộc đời, bốn c&aacute;i Ti&ecirc;n Thi&ecirc;n kh&iacute; vận tăng th&ecirc;m tự nhi&ecirc;n c&ograve;n chưa xuất hiện.<br />\r\n<br />\r\nGiữa trưa.<br />\r\n<br />\r\nC&oacute; hai t&ecirc;n tu sĩ đến.<br />\r\n<br />\r\nNgọc Thanh t&ocirc;ng rất lớn, vườn dược thảo chung quanh đều l&agrave; d&atilde;y n&uacute;i, c&aacute;c tu sĩ kh&ocirc;ng được ph&eacute;p tới nơi đ&acirc;y, đại đa số thời điểm đều l&agrave; ngoại m&ocirc;n chấp sự đến đ&acirc;y y&ecirc;u cầu đan dược, hai t&ecirc;n tu sĩ n&agrave;y kh&iacute; chất cực giai, một nam một nữ, tựa như thần ti&ecirc;n quyến lữ, hấp dẫn tất cả n&ocirc; bộc quay đầu nh&igrave;n lại.<br />\r\n<br />\r\nH&agrave;n Tuyệt cũng quay đầu nh&igrave;n về ph&iacute;a vườn dược thảo cửa ch&iacute;nh.<br />\r\n<br />\r\n&quot;Thật sự l&agrave; ngăn nắp xinh đẹp.&quot;<br />\r\n<br />\r\nH&agrave;n Tuyệt thở d&agrave;i.<br />\r\n<br />\r\nBọn hắn những n&ocirc; bộc n&agrave;y quần &aacute;o đều r&aacute;ch tung to&eacute;, m&agrave; hai vị kia tu sĩ &aacute;o b&agrave;o sạch sẽ hoa lệ, như l&agrave; tu ti&ecirc;n v&otilde;ng du b&ecirc;n trong đi ra tới NPC.<br />\r\n<br />\r\nH&agrave;n Tuyệt chỉ l&agrave; t&ugrave;y tiện cảm kh&aacute;i, hắn kh&ocirc;ng ch&uacute;t n&agrave;o h&acirc;m mộ.<br />\r\n<br />\r\nHắn đ&atilde; lắc ra khỏi bốn c&aacute;i Ti&ecirc;n Thi&ecirc;n kh&iacute; vận, sau n&agrave;y th&agrave;nh tựu tuyệt kh&ocirc;ng phải Ngọc Thanh t&ocirc;ng đệ tử ngoại m&ocirc;n c&oacute; thể so s&aacute;nh.<br />\r\n<br />\r\n&quot;Kể từ h&ocirc;m nay, hai người ch&uacute;ng ta phụ tr&aacute;ch thủ hộ Thiết l&atilde;o vườn dược thảo, c&aacute;c ngươi kh&ocirc;ng cần để &yacute; tới ch&uacute;ng ta, cũng kh&ocirc;ng thể đ&atilde; quấy rầy ch&uacute;ng ta tu luyện.&quot; Nam tu sĩ đối với Vương l&atilde;o đầu mặt kh&ocirc;ng thay đổi n&oacute;i ra.</p>', '2021-11-27 20:27:42', 41, 1),
(157, 45, 'Lục Đạo Linh Thể, đỉnh cấp linh căn', '2', '<p>&quot;Thiết l&atilde;o xảy ra chuyện rồi?&quot; Vương l&atilde;o đầu cẩn thận từng li từng t&iacute; hỏi.<br />\r\n<br />\r\nMặc d&ugrave; Thiết l&atilde;o hỉ nộ v&ocirc; thường, nhưng Vương l&atilde;o đầu l&agrave;m bạn hắn mấy chục năm, tự nhi&ecirc;n kh&ocirc;ng hy vọng Thiết l&atilde;o xảy ra chuyện.<br />\r\n<br />\r\nNữ tu sĩ lắc đầu n&oacute;i: &quot;Ngọc Thanh t&ocirc;ng gần nhất tiềm nhập ma tu, ngoại m&ocirc;n đặc biệt để cho ch&uacute;ng ta hai người đến bảo hộ, c&aacute;c ngươi kh&ocirc;ng cần lo lắng.&quot;<br />\r\n<br />\r\nVương l&atilde;o đầu nghe ch&uacute;t, kh&ocirc;ng khỏi thở d&agrave;i một hơi.<br />\r\n<br />\r\nHắn chắp tay h&agrave;nh lễ, sau đ&oacute; quay người rời đi.<br />\r\n<br />\r\nHai t&ecirc;n tu sĩ ri&ecirc;ng ph&acirc;̀n mình đi ra, tại vườn dược thảo đại m&ocirc;n hai b&ecirc;n dưới c&acirc;y ngồi xuống tu luyện.<br />\r\n<br />\r\nTheo bọn hắn bắt đầu thổ nạp, bọn hắn quanh th&acirc;n xuất hiện mắt trần c&oacute; thể thấy gi&oacute; lốc.<br />\r\n<br />\r\nĐ&oacute; ch&iacute;nh l&agrave; linh kh&iacute; đi.<br />\r\n<br />\r\nH&agrave;n Tuyệt y&ecirc;n lặng nghĩ đến, sau đ&oacute; tiếp tục vẩy nước.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nNg&agrave;y thứ hai.<br />\r\n<br />\r\nH&agrave;n Tuyệt tiếp tục lắc x&uacute;c xắc.<br />\r\n<br />\r\n&laquo; linh căn tư chất: Kh&ocirc;ng &raquo;<br />\r\n<br />\r\nH&agrave;n Tuyệt trợn trắng mắt.<br />\r\n<br />\r\nHắn v&acirc;̣y mà kh&ocirc;ng c&oacute; cảm thấy kỳ qu&aacute;i.<br />\r\n<br />\r\nHắn cũng kh&ocirc;ng hoảng hốt, d&ugrave; sao đều lắc ra khỏi bốn c&aacute;i đỉnh cấp Ti&ecirc;n Thi&ecirc;n kh&iacute; vận, linh căn tư chất c&oacute; thể từ từ lắc.<br />\r\n<br />\r\nHai vị tu sĩ gia nhập cũng kh&ocirc;ng c&oacute; cải biến vườn dược thảo bọn n&ocirc; bộc sinh hoạt.<br />\r\n<br />\r\nKh&ocirc; khan sinh hoạt c&ograve;n đang tiếp tục.<br />\r\n<br />\r\nH&agrave;n Tuyệt mỗi ng&agrave;y trừ lao động b&ecirc;n ngo&agrave;i, ch&iacute;nh l&agrave; r&egrave;n luyện th&acirc;n thể, đổ x&uacute;c xắc.<br />\r\n<br />\r\nNg&agrave;y thứ ba.<br />\r\n<br />\r\nX&uacute;c xắc lay động!<br />\r\n<br />\r\n&laquo; linh căn tư chất: Kh&ocirc;ng &raquo;<br />\r\n<br />\r\nC&oacute; thể!<br />\r\n<br />\r\nTr&ecirc;n c&aacute;n ta!<br />\r\n<br />\r\nH&agrave;n Tuyệt trong l&ograve;ng thầm mắng.<br />\r\n<br />\r\nNg&agrave;y thứ tư.<br />\r\n<br />\r\n&laquo; linh căn tư chất: Ngũ H&agrave;nh Tạp linh căn, th&agrave;nh tựu kh&oacute; si&ecirc;u Tr&uacute;c Cơ &raquo;<br />\r\n<br />\r\n&Acirc;n, ph&agrave;m nh&acirc;n tu ti&ecirc;n ph&ugrave; hợp.<br />\r\n<br />\r\nH&agrave;n Tuyệt lắc đầu.<br />\r\n<br />\r\nHắn c&ograve;n muốn tiếp tục lắc, đ&aacute;nh chết cũng kh&ocirc;ng thể ph&agrave;m nh&acirc;n tu ti&ecirc;n.<br />\r\n<br />\r\nCứ như vậy, H&agrave;n Tuyệt mỗi ng&agrave;y r&egrave;n luyện, mỗi ng&agrave;y lắc x&uacute;c xắc.<br />\r\n<br />\r\nĐại đa số thời điểm đều l&agrave; Tạp linh căn.<br />\r\n<br />\r\nHắn kh&ocirc;ng c&oacute; nhụt ch&iacute;, tin tưởng lu&ocirc;n c&oacute; thể lắc đến nghịch thi&ecirc;n linh căn tư chất.<br />\r\n<br />\r\nMuộn một ch&uacute;t tu ti&ecirc;n cũng kh&ocirc;ng c&oacute; việc g&igrave;.<br />\r\n<br />\r\nD&ugrave; sao hắn kh&ocirc;ng c&oacute; cừu gia.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nHai năm sau.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nThiết l&atilde;o trở về, hai vị kia tu sĩ h&agrave;nh lễ c&aacute;o từ.<br />\r\n<br />\r\nTất cả n&ocirc; bộc to&agrave;n bộ tụ tập tới, quỳ lạy Thiết l&atilde;o.<br />\r\n<br />\r\nThiết l&atilde;o người mặc &aacute;o b&agrave;o đen, b&ecirc;n h&ocirc;ng c&agrave;i lấy hai c&aacute;i hồ l&ocirc;, t&oacute;c trắng đen xen kẽ, khu&ocirc;n mặt &acirc;m lệ, cho người ta một loại t&iacute;nh t&igrave;nh thật kh&ocirc;ng tốt ấn tượng.<br />\r\n<br />\r\n&quot;Cũng kh&ocirc;ng tệ lắm, chưa từng xu&acirc;́t hi&ecirc;̣n sai lầm.&quot;<br />\r\n<br />\r\nThiết l&atilde;o nh&igrave;n chung quanh một v&ograve;ng, h&agrave;i l&ograve;ng cười n&oacute;i.<br />\r\n<br />\r\nBọn n&ocirc; bộc thở d&agrave;i một hơi, đều lộ ra d&aacute;ng tươi cười.<br />\r\n<br />\r\nThiết l&atilde;o nh&igrave;n về ph&iacute;a H&agrave;n Tuyệt c&ugrave;ng b&ecirc;n cạnh hắn thiếu ni&ecirc;n kh&aacute;c.<br />\r\n<br />\r\n&quot;C&aacute;c ngươi bốn người đi theo ta.&quot;<br />\r\n<br />\r\nThiết l&atilde;o chỉ một ch&uacute;t, liền đi hướng ch&iacute;nh m&igrave;nh lầu c&aacute;c.<br />\r\n<br />\r\nH&agrave;n Tuyệt ch&iacute;nh l&agrave; bốn người một trong.<br />\r\n<br />\r\nBọn hắn lập tức đuổi theo kịp đi.<br />\r\n<br />\r\n&quot;L&agrave; muốn an b&agrave;i ch&uacute;ng ta phụ tr&aacute;ch một bộ phận khu vườn?&quot; H&agrave;n Tuyệt y&ecirc;n lặng nghĩ đến.<br />\r\n<br />\r\nHắn bỗng nhi&ecirc;n ch&uacute; &yacute; tới Thiết l&atilde;o trong tay &aacute;o tay phải đang rỉ m&aacute;u.<br />\r\n<br />\r\nH&agrave;n Tuyệt lập tức kinh dị.<br />\r\n<br />\r\nHẳn l&agrave; Thiết l&atilde;o thụ thương, muốn bắt bọn hắn luyện chế kh&ocirc;i lỗi hoặc l&agrave; đan dược?<br />\r\n<br />\r\nH&agrave;n Tuyệt kiếp trước thế nhưng l&agrave; nh&igrave;n qua kh&ocirc;ng &iacute;t tu ti&ecirc;n tiểu thuyết, hắn ấn tượng s&acirc;u nhất một c&acirc;u ch&iacute;nh l&agrave; tử đạo hữu bất tử bần đạo.<br />\r\n<br />\r\nĐối với tu sĩ m&agrave; n&oacute;i, kh&ocirc;ng c&oacute; c&aacute;i g&igrave; so với ch&iacute;nh m&igrave;nh t&iacute;nh mệnh quan trọng hơn.<br />\r\n<br />\r\nH&agrave;n Tuyệt c&agrave;ng nghĩ c&agrave;ng hoảng, hắn lại kh&ocirc;ng d&aacute;m trốn, chỉ c&oacute; thể cố gắng khắc chế.<br />\r\n<br />\r\nNhập sau ph&ograve;ng, một t&ecirc;n thiếu ni&ecirc;n đ&oacute;ng cửa ph&ograve;ng lại.<br />\r\n<br />\r\nThiết l&atilde;o ngồi tại tr&ecirc;n thủ tọa, d&ograve; x&eacute;t H&agrave;n Tuyệt bốn người.<br />\r\n<br />\r\n&quot;C&aacute;c ngươi đưa tay qua đ&acirc;y.&quot; Thiết l&atilde;o mở miệng ph&acirc;n ph&oacute; n&oacute;i.<br />\r\n<br />\r\nH&agrave;n Tuyệt bốn người nhao nhao n&acirc;ng tay phải l&ecirc;n.<br />\r\n<br />\r\nThiết l&atilde;o từng c&aacute;i sờ soạng một ch&uacute;t.<br />\r\n<br />\r\nH&agrave;n Tuyệt nổi da g&agrave; đều nhanh đi l&ecirc;n.<br />\r\n<br />\r\n&quot;Liền ngươi c&oacute; linh căn, những người kh&aacute;c trở về đi.&quot;<br />\r\n<br />\r\nThiết l&atilde;o đối với một t&ecirc;n cao cao tr&aacute;ng tr&aacute;ng thiếu ni&ecirc;n n&oacute;i ra.<br />\r\n<br />\r\nH&agrave;n Tuyệt thở d&agrave;i một hơi.<br />\r\n<br />\r\nH&ocirc;m nay trước kia, hắn hơi lung lay một ch&uacute;t linh căn tư chất, h&ocirc;m qua hay l&agrave; tứ linh căn, h&ocirc;m nay trực tiếp lắc kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nKh&ocirc;ng nghĩ tới nh&acirc;n họa đắc ph&uacute;c.<br />\r\n<br />\r\nH&agrave;n Tuyệt đồng t&igrave;nh nh&igrave;n tho&aacute;ng qua cao tr&aacute;ng thiếu ni&ecirc;n.<br />\r\n<br />\r\nTrương C&aacute;p, một t&ecirc;n ưa th&iacute;ch bồ c&acirc;u nhiệt t&igrave;nh H&agrave;i Tử Vương.<br />\r\n<br />\r\nHai vị kh&aacute;c thiếu ni&ecirc;n th&igrave; h&acirc;m mộ nh&igrave;n về ph&iacute;a Trương C&aacute;p.<br />\r\n<br />\r\nTrương C&aacute;p đ&atilde; chờ mong, lại t&acirc;m th&acirc;̀n b&acirc;́t định.<br />\r\n<br />\r\nThiết l&atilde;o kh&ocirc;ng cho ph&eacute;p n&ocirc; bộc tu luyện, nhưng đ&acirc;y cũng l&agrave; lần thứ nhất l&agrave;m n&ocirc; t&agrave;i bọn họ kiểm tra linh căn.<br />\r\n<br />\r\nRời đi lầu c&aacute;c về sau, H&agrave;n Tuyệt kh&ocirc;ng để &yacute; tới hai vị kh&aacute;c thiếu ni&ecirc;n thảo luận, trở về tiếp tục l&agrave;m việc.<br />\r\n<br />\r\nTừ nay về sau, Trương C&aacute;p liền kh&ocirc;ng tham dự nữa lao động.<br />\r\n<br />\r\nNguy&ecirc;n lai hắn bị Thiết l&atilde;o thu l&agrave;m đồ đệ, c&aacute;i n&agrave;y khiến mặt kh&aacute;c n&ocirc; bộc c&agrave;ng th&ecirc;m ước ao ghen tị.<br />\r\n<br />\r\nH&agrave;n Tuyệt kh&ocirc;ng ch&uacute;t n&agrave;o h&acirc;m mộ.<br />\r\n<br />\r\nKhẳng định c&oacute; lừa dối!<br />\r\n<br />\r\nTrước dưỡng thục lại giết!<br />\r\n<br />\r\nH&agrave;n Tuyệt rất may mắn ch&iacute;nh m&igrave;nh kh&ocirc;ng c&oacute; bị chọn tr&uacute;ng.<br />\r\n<br />\r\nHắn c&oacute; hệ thống, kh&ocirc;ng cần b&aacute;i sư.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nTho&aacute;ng chớp mắt, lại l&agrave; thời gian hai năm.<br />\r\n<br />\r\nThiết l&atilde;o nửa năm trước lại rời đi vườn dược thảo, hắn sau khi đi, trước đ&oacute; đến đ&acirc;y thủ hộ vườn dược thảo hai vị tu sĩ lại tới.<br />\r\n<br />\r\nTrương C&aacute;p b&acirc;y giờ đ&atilde; l&agrave; Luyện Kh&iacute; tu sĩ, c&ograve;n c&ugrave;ng hai vị kia tu sĩ bắt chuyện.<br />\r\n<br />\r\nTất cả n&ocirc; bộc hiện tại cũng đ&ecirc;́n ngửa hắn hơi thở.<br />\r\n<br />\r\nCũng may Trương C&aacute;p ng&agrave;y b&igrave;nh thường vội v&agrave;ng tu luyện, cũng kh&ocirc;ng c&oacute; khi dễ mặt kh&aacute;c n&ocirc; bộc.<br />\r\n<br />\r\nBọn hắn những n&ocirc; bộc n&agrave;y cuối c&ugrave;ng cả đời đều l&agrave; n&ocirc; bộc, Thiết l&atilde;o thường xuy&ecirc;n ra ngo&agrave;i, bọn hắn lại kh&ocirc;ng được rời đi vườn dược thảo, kh&ocirc;ng c&oacute; lợi &iacute;ch tranh chấp, cho n&ecirc;n c&oacute; rất &iacute;t m&acirc;u thuẫn.<br />\r\n<br />\r\nH&agrave;n Tuyệt đ&atilde; 15 tuổi, vẫn như cũ điệu thấp.<br />\r\n<br />\r\nTrong bốn năm, hắn lắc qua tốt nhất tư chất l&agrave; tam linh căn, thể luyện đến Kim Đan cảnh.<br />\r\n<br />\r\nKim Đan cảnh t&iacute;nh l&agrave; c&aacute;i rắm g&igrave;!<br />\r\n<br />\r\nH&agrave;n Tuyệt mục ti&ecirc;u l&agrave; trường sinh bất tử, th&agrave;nh ti&ecirc;n th&agrave;nh thần.<br />\r\n<br />\r\nMột ng&agrave;y n&agrave;y s&aacute;ng sớm.<br />\r\n<br />\r\nH&agrave;n Tuyệt hững hờ lắc x&uacute;c xắc, kh&ocirc;ng c&oacute; &ocirc;m chờ mong, liền c&ugrave;ng mỗi ng&agrave;y đứng l&ecirc;n s&uacute;c miệng một dạng.<br />\r\n<br />\r\n&laquo; linh căn tư chất: Thủy, Mộc song linh căn, tư chất tr&aacute;c tuyệt, c&oacute; hi vọng tu luyện tới Nguy&ecirc;n Anh cảnh &raquo;<br />\r\n<br />\r\nSong linh căn?<br />\r\n<br />\r\nNguy&ecirc;n Anh cảnh?<br />\r\n<br />\r\nChỉ l&agrave; c&oacute; hi vọng?<br />\r\n<br />\r\nH&agrave;n Tuyệt cắt một tiếng, ho&agrave;n to&agrave;n kh&ocirc;ng động t&acirc;m.<br />\r\n<br />\r\nHắn mới 15 tuổi, c&ograve;n c&oacute; thể lại lắc mười lăm năm.<br />\r\n<br />\r\nTiếp tục lắc!<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nXu&acirc;n đi thu tới.<br />\r\n<br />\r\nLại l&agrave; một năm qua đi.<br />\r\n<br />\r\nH&agrave;n Tuyệt mỗi ng&agrave;y s&aacute;ng sớm ho&agrave;n to&agrave;n như trước đ&acirc;y đổ x&uacute;c xắc.<br />\r\n<br />\r\n&laquo; linh căn tư chất: Lục Đạo Linh Thể, ẩn chứa đỉnh cấp Phong, Hỏa, Thủy, Thổ, Mộc, L&ocirc;i linh căn, gia tăng nhất định kh&iacute; vận &raquo;<br />\r\n<br />\r\nLiền c&aacute;i n&agrave;y?<br />\r\n<br />\r\nH&agrave;n Tuyệt v&ocirc; &yacute; thức cắt một tiếng.<br />\r\n<br />\r\nChờ ch&uacute;t!<br />\r\n<br />\r\nLiền c&aacute;i n&agrave;y!</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nH&agrave;n Tuyệt trừng to mắt, h&ocirc; hấp đều dồn dập l&ecirc;n.<br />\r\n<br />\r\nĐến rồi!<br />\r\n<br />\r\nR&ograve;ng r&atilde; mười s&aacute;u năm!<br />\r\n<br />\r\nRốt cục lắc ra khỏi sảng văn nam ch&iacute;nh hẳn l&agrave; c&oacute; linh căn tư chất!<br />\r\n<br />\r\nH&agrave;n Tuyệt dụi dụi con mắt, cẩn thận x&aacute;c nhận.<br />\r\n<br />\r\nĐỉnh cấp linh căn!<br />\r\n<br />\r\nHay l&agrave; s&aacute;u loại thuộc t&iacute;nh đỉnh cấp linh căn!<br />\r\n<br />\r\n&quot;H&ocirc;. . .&quot;<br />\r\n<br />\r\nH&agrave;n Tuyệt cố gắng b&igrave;nh phục t&acirc;m t&igrave;nh.<br />\r\n<br />\r\nRốt cuộc đ&atilde; đến!<br />\r\n<br />\r\nCh&iacute;nh l&agrave; c&aacute;i n&agrave;y!<br />\r\n<br />\r\nĐ&acirc;y mới l&agrave; hắn hẳn l&agrave; c&oacute; linh căn tư chất!<br />\r\n<br />\r\nH&agrave;n Tuyệt kh&ocirc;ng c&oacute; lập tức click mở bắt đầu du h&iacute; cuộc đời, hắn sợ động tĩnh qu&aacute; lớn, d&ugrave; sao cửa ra v&agrave;o c&ograve;n c&oacute; hai vị tu sĩ tại trấn giữ.<br />\r\n<br />\r\n&quot;Vấn đề tới, ta phải chờ tới lúc nào?&quot;<br />\r\n<br />\r\nH&agrave;n Tuyệt xoắn xu&yacute;t nghĩ đến.<br />\r\n<br />\r\nTrước mắt hắn bỗng nhi&ecirc;n to&aacute;t ra một h&agrave;ng chữ:<br />\r\n<br />\r\n&laquo; c&oacute; thể lựa chọn một c&aacute;i địa phương nhỏ, hệ thống hỗ trợ s&aacute;ng tạo kết giới, b&ecirc;n ngo&agrave;i kết giới tu sĩ kh&ocirc;ng thể nhận ra đến trong kết giới biến h&oacute;a &raquo;<br />\r\n<br />\r\nH&agrave;n Tuyệt kinh hỉ, hắn lập tức đi tới cửa trước, x&aacute;c định tất cả n&ocirc; bộc đều đi vườn dược thảo về sau, hắn đ&oacute;ng cửa ph&ograve;ng.<br />\r\n<br />\r\nHắn đứng trong ph&ograve;ng, điều ra thuộc t&iacute;nh liệt biểu, ng&oacute;n tay run run rẩy rẩy điểm hướng ph&iacute;a dưới c&ugrave;ng nhất &laquo; click mở bắt đầu du h&iacute; cuộc đời &raquo;.<br />\r\n<br />\r\nClick th&agrave;nh c&ocirc;ng!<br />\r\n<br />\r\nThuộc t&iacute;nh liệt biểu biến đổi.<br />\r\n<br />\r\nTừng h&agrave;ng chữ xuất hiện tại H&agrave;n Tuyệt trước mắt:<br />\r\n<br />\r\n&laquo; bắt đầu du h&iacute; cuộc đời &raquo;<br />\r\n<br />\r\n&laquo; xem nh&acirc;n sinh kinh lịch &raquo;<br />\r\n<br />\r\n&laquo; H&agrave;n Uy&ecirc;n, ngươi sinh ra ở thế gian một tu ti&ecirc;n trong t&ocirc;ng m&ocirc;n, từ nhỏ đến lớn, dung nhan tuyệt thế, bị người y&ecirc;u th&iacute;ch, cha mẹ của ngươi tại ngươi tuổi nhỏ l&uacute;c vứt bỏ ngươi m&agrave; đi, từ nơi s&acirc;u xa, tựa hồ c&oacute; c&aacute;i g&igrave; vận mệnh c&acirc;̀n ngươi c&otilde;ng phụ, ngươi tr&ecirc;n Kiếm Đạo c&oacute; loại si&ecirc;u việt thế nh&acirc;n tuyệt đỉnh thi&ecirc;n ph&uacute;, ngươi thường xuy&ecirc;n c&oacute; thể cảm nhận được giữa thi&ecirc;n địa ẩn chứa s&aacute;u loại đo&aacute;n kh&ocirc;ng được, nh&igrave;n kh&ocirc;ng thấy lực lượng thần b&iacute;. . . &raquo;<br />\r\n<br />\r\n&laquo; cho đến h&ocirc;m nay, ngươi ngo&agrave;i &yacute; muốn thức tỉnh Ti&ecirc;n Đế truyền thừa, thu hoạch được tuyệt thế c&ocirc;ng ph&aacute;p &laquo; Lục Đạo Lu&acirc;n Hồi C&ocirc;ng &raquo;, ngươi bởi vậy bước l&ecirc;n con đường tu hành. &raquo;<br />\r\n<br />\r\n&laquo; xin mời lựa chọn ngươi chủ yếu tu h&agrave;nh lộ tuyến &raquo;<br />\r\n<br />\r\n&laquo; một, kiếm tu &raquo;<br />\r\n<br />\r\n&laquo; hai, l&ocirc;i tu &raquo;<br />\r\n<br />\r\n&laquo; ba, thổ tu &raquo;<br />\r\n<br />\r\n&laquo; bốn, hỏa tu &raquo;<br />\r\n<br />\r\n&laquo; năm, thủy tu &raquo;<br />\r\n<br />\r\n&laquo; s&aacute;u, mộc tu &raquo;<br />\r\n<br />\r\n&laquo; bảy, phong tu &raquo;</p>', '2021-11-27 21:01:25', 11, 1),
(158, 45, 'Luyện Khí cảnh tầng bảy, đáng chết mị lực', '3', '<p>Tu h&agrave;nh phương hướng?<br />\r\n<br />\r\nH&agrave;n Tuyệt kh&ocirc;ng c&oacute; qu&aacute; do dự.<br />\r\n<br />\r\nHắn ch&uacute; &yacute; tới m&igrave;nh thu được Lục Đạo Lu&acirc;n Hồi C&ocirc;ng, chắc hẳn c&ugrave;ng Lục Đạo Linh Thể tương quan, s&aacute;u loại tuyệt đỉnh linh căn tư chất, tất nhi&ecirc;n muốn c&ugrave;ng một chỗ ph&aacute;t triển, kh&ocirc;ng c&oacute; khả năng lệch khoa.<br />\r\n<br />\r\nĐồng thời, hắn c&oacute; được tuyệt đỉnh Kiếm Đạo tư chất, tự nhi&ecirc;n đ&ecirc;́n lựa chọn kiếm tu!<br />\r\n<br />\r\nH&agrave;n Tuyệt sau khi suy nghĩ cẩn thận, lập tức click kiếm tu.<br />\r\n<br />\r\nTrong chốc l&aacute;t, một cỗ kỳ dị nguồn nhiệt ở trong cơ thể hắn phun tr&agrave;o.<br />\r\n<br />\r\nCả người hắn hoảng hốt một ch&uacute;t.<br />\r\n<br />\r\nPhảng phất l&agrave;m một giấc mộng, rất d&agrave;i, nhưng hắn lại nhớ kh&ocirc;ng r&otilde; trong mộng xảy ra chuyện g&igrave;.<br />\r\n<br />\r\nLần nữa mở mắt, H&agrave;n Tuyệt to&agrave;n th&acirc;n l&agrave; mồ h&ocirc;i.<br />\r\n<br />\r\nTrước mắt hắn màn ánh sáng đ&atilde; biến th&agrave;nh thuộc t&iacute;nh liệt biểu:<br />\r\n<br />\r\n&laquo; t&iacute;nh danh: H&agrave;n Tuyệt &raquo;<br />\r\n<br />\r\n&laquo; tuổi thọ: 16/65 &raquo;<br />\r\n<br />\r\n&laquo; chủng tộc: Ph&agrave;m nh&acirc;n &raquo;<br />\r\n<br />\r\n&laquo; tu vi: Kh&ocirc;ng &raquo;<br />\r\n<br />\r\n&laquo; c&ocirc;ng ph&aacute;p: Lục Đạo Lu&acirc;n Hồi C&ocirc;ng ( có th&ecirc;̉ truyền thừa ) &raquo;<br />\r\n<br />\r\n&laquo; ph&aacute;p thuật: Kh&ocirc;ng &raquo;<br />\r\n<br />\r\n&laquo; thần th&ocirc;ng: Kh&ocirc;ng &raquo;<br />\r\n<br />\r\n&laquo; ph&aacute;p kh&iacute;: Kh&ocirc;ng &raquo;<br />\r\n<br />\r\n&laquo; linh căn tư chất: Lục Đạo Linh Thể, ẩn chứa đỉnh cấp Phong, Hỏa, Thủy, Thổ, Mộc, L&ocirc;i linh căn, gia tăng nhất định kh&iacute; vận &raquo;<br />\r\n<br />\r\n&laquo; Ti&ecirc;n Thi&ecirc;n kh&iacute; vận như sau &raquo;<br />\r\n<br />\r\n&laquo; tuyệt thế v&ocirc; song: Ti&ecirc;n tư, mị lực đỉnh cấp &raquo;<br />\r\n<br />\r\n&laquo; Thi&ecirc;n Mệnh Kiếm Si: Kiếm Đạo tư chất đỉnh cấp, Kiếm Đạo ngộ t&iacute;nh đỉnh cấp &raquo;<br />\r\n<br />\r\n&laquo; th&acirc;n ph&aacute;p tuyệt trần: Th&acirc;n ph&aacute;p tư chất đỉnh cấp &raquo;<br />\r\n<br />\r\n&laquo; Ti&ecirc;n Đế hậu duệ: Thu hoạch được một bộ tuyệt thế c&ocirc;ng ph&aacute;p tu ti&ecirc;n, 1000 khối linh thạch thượng phẩm &raquo;<br />\r\n<br />\r\n&laquo; xem x&eacute;t quan hệ nh&acirc;n mạch &raquo;<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nH&agrave;n Tuyệt kh&ocirc;ng c&oacute; vội v&atilde; truyền thừa Lục Đạo Lu&acirc;n Hồi C&ocirc;ng, m&agrave; l&agrave; click ph&iacute;a dưới c&ugrave;ng nhất quan hệ nh&acirc;n mạch.<br />\r\n<br />\r\nM&agrave;n s&aacute;ng biến đổi, xuất hiện một ảnh ch&acirc;n dung, l&agrave; ch&acirc;n nh&acirc;n ảnh chụp.<br />\r\n<br />\r\nThiết l&atilde;o ảnh chụp, c&ugrave;ng ch&acirc;n nh&acirc;n giống nhau như đ&uacute;c.<br />\r\n<br />\r\n&laquo; Thiết l&atilde;o: Luyện Kh&iacute; cảnh tầng bảy, đối với ngươi tr&agrave;n ngập ch&aacute;n gh&eacute;t, nếu l&agrave; ngươi c&oacute; tu ti&ecirc;n linh căn, nhất định t&iacute;nh to&aacute;n ngươi, cừu hận độ l&agrave; 1 tinh &raquo;<br />\r\n<br />\r\n&quot;Ta s&aacute;t, lão tử t&acirc;n t&acirc;n khổ khổ v&igrave; ngươi lao động, ngươi c&ograve;n cừu hận ta?&quot;<br />\r\n<br />\r\nH&agrave;n Tuyệt trong l&ograve;ng chửi ầm l&ecirc;n.<br />\r\n<br />\r\nChợt hắn lại b&igrave;nh tĩnh xuống tới.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; b&agrave;n tay v&agrave;ng a!<br />\r\n<br />\r\nC&oacute; thể xem x&eacute;t đến người kh&aacute;c đối với ngươi ấn tượng.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nBất qu&aacute; v&igrave; sao kh&ocirc;ng c&oacute; những người kh&aacute;c?<br />\r\n<br />\r\nPh&agrave;m nh&acirc;n kh&ocirc;ng đang suy nghĩ phạm vi?<br />\r\n<br />\r\nTrương C&aacute;p đối với hắn kh&ocirc;ng c&oacute; cảm gi&aacute;c?<br />\r\n<br />\r\nH&agrave;n Tuyệt kh&ocirc;ng nghĩ ra, cũng kh&ocirc;ng c&oacute; suy nghĩ nhiều, hắn trước đẩy cửa ph&ograve;ng ra, ph&aacute;t hiện thời gian đ&atilde; nhanh đến trưa rồi, kh&ocirc;ng c&oacute; khả năng lại lười biếng, sẽ bị ph&aacute;t gi&aacute;c được.<br />\r\n<br />\r\nHắn lập tức chạy hướng vườn dược thảo.<br />\r\n<br />\r\n&quot;H&agrave;n Tuyệt, tiểu tử ngươi c&oacute; phải hay kh&ocirc;ng ngủ nướng?&quot;<br />\r\n<br />\r\nMột người trung ni&ecirc;n n&ocirc; bộc cười mắng, H&agrave;n Tuyệt ngượng ng&ugrave;ng v&ograve; đầu, dẫn tới những người kh&aacute;c cười ha ha.<br />\r\n<br />\r\nBọn hắn kh&ocirc;ng c&oacute; suy nghĩ nhiều, cũng kh&ocirc;ng c&oacute; quở tr&aacute;ch, H&agrave;n Tuyệt rất &iacute;t thất tr&aacute;ch, ngẫu nhi&ecirc;n một lần cũng kh&ocirc;ng t&iacute;nh l&agrave; g&igrave;.<br />\r\n<br />\r\nVườn dược thảo cửa ra v&agrave;o t&ecirc;n nữ tu kia sĩ bỗng nhi&ecirc;n mở mắt nh&igrave;n về ph&iacute;a H&agrave;n Tuyệt.<br />\r\n<br />\r\nN&agrave;ng c&oacute; ch&uacute;t nh&iacute;u m&agrave;y.<br />\r\n<br />\r\n&quot;Thiếu ni&ecirc;n n&agrave;y. . . Tốt tuấn a!&quot;<br />\r\n<br />\r\nH&agrave;n Tuyệt trước mắt đi theo hiện ra một h&agrave;ng chữ:<br />\r\n<br />\r\n&laquo; H&igrave;nh Hồng Tuyền đối với ngươi c&oacute; ấn tượng tốt, trước mắt độ thiện cảm l&agrave; 1 tinh &raquo;<br />\r\n<br />\r\nC&aacute;i quỷ g&igrave;?<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền l&agrave; ai?<br />\r\n<br />\r\nH&agrave;n Tuyệt kh&ocirc;ng hiểu thấu, v&ocirc; &yacute; thức quay đầu nh&igrave;n lại.<br />\r\n<br />\r\nChẳng lẽ l&agrave; vị nữ tu kia?<br />\r\n<br />\r\nQuả nhi&ecirc;n, nữ tu kia v&acirc;̣y mà theo d&otilde;i hắn.<br />\r\n<br />\r\nBốn mắt nh&igrave;n nhau, H&igrave;nh Hồng Tuyền hướng hắn nh&agrave;n nhạt cười một tiếng.<br />\r\n<br />\r\nH&agrave;n Tuyệt vội v&agrave;ng nghi&ecirc;ng đầu đi.<br />\r\n<br />\r\nHỏng b&eacute;t.<br />\r\n<br />\r\nĐỉnh cấp mị lực bị để mắt tới.<br />\r\n<br />\r\nTa l&agrave; ph&agrave;m nh&acirc;n, n&agrave;ng l&agrave; tu sĩ, kh&ocirc;ng c&oacute; khả năng y&ecirc;u nhau.<br />\r\n<br />\r\nHẳn l&agrave; n&agrave;ng muốn lấy ta l&agrave;m l&ocirc; đỉnh?<br />\r\n<br />\r\nKh&ocirc;ng được!<br />\r\n<br />\r\nPhải nghĩ biện ph&aacute;p n&eacute; tr&aacute;nh!<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nV&agrave;o đ&ecirc;m.<br />\r\n<br />\r\nTrong ph&ograve;ng mặt kh&aacute;c năm vị n&ocirc; bộc biết r&otilde; hơn ngủ về sau, H&agrave;n Tuyệt nằm ở tr&ecirc;n giường, bắt đầu truyền thừa Lục Đạo Lu&acirc;n Hồi C&ocirc;ng.<br />\r\n<br />\r\nKhổng lồ k&yacute; ức tr&agrave;n v&agrave;o trong &oacute;c của hắn.<br />\r\n<br />\r\nThật l&acirc;u.<br />\r\n<br />\r\nHắn mở to mắt.<br />\r\n<br />\r\n&quot;Đ&acirc;y ch&iacute;nh l&agrave; c&ocirc;ng ph&aacute;p tu ti&ecirc;n sao, thật sự l&agrave; rườm r&agrave;. . .&quot;<br />\r\n<br />\r\nH&agrave;n Tuyệt y&ecirc;n lặng nghĩ đến, hắn chỉ truyền nhận đến tầng thứ nhất c&ocirc;ng ph&aacute;p, có th&ecirc;̉ tu luyện tới Luyện Kh&iacute; cảnh chín tầng.<br />\r\n<br />\r\nLục Đạo Lu&acirc;n Hồi C&ocirc;ng, ch&uacute; trọng s&aacute;u hệ linh căn c&acirc;n đối ph&aacute;t triển, thể nội đ&ecirc;́n bồi dưỡng được s&aacute;u loại thuộc t&iacute;nh linh lực, mặc d&ugrave; qu&aacute; tr&igrave;nh tu luyện so đơn thuộc t&iacute;nh c&ocirc;ng ph&aacute;p muốn rườm r&agrave;, nhưng H&agrave;n Tuyệt tư chất tuyệt đỉnh, c&oacute; thể đền b&ugrave; sự ch&ecirc;nh lệch n&agrave;y.<br />\r\n<br />\r\nS&aacute;u loại linh kh&iacute; đạt tới tr&igrave;nh độ nhất định, mới c&oacute; thể Tr&uacute;c Cơ!<br />\r\n<br />\r\nH&agrave;n Tuyệt tinh thần phấn chấn, lặng lẽ ngồi xuống, hắn để hệ thống lấy ch&iacute;nh m&igrave;nh c&aacute;i giường n&agrave;y l&agrave; phạm vi mở ra kết giới, tr&aacute;nh cho bị ph&iacute;a ngo&agrave;i ba vị tu sĩ ph&aacute;t gi&aacute;c được.<br />\r\n<br />\r\nMặt kh&aacute;c năm vị n&ocirc; bộc ngủ rất say, kh&ograve; kh&egrave; rung trời, d&ugrave; sao mệt mỏi một ng&agrave;y.<br />\r\n<br />\r\nH&agrave;n Tuyệt bắt đầu dựa theo tầng t&acirc;m ph&aacute;p thứ nhất thổ nạp.<br />\r\n<br />\r\nThổ nạp, ch&iacute;nh l&agrave; một loại phương ph&aacute;p h&ocirc; hấp.<br />\r\n<br />\r\nTại thổ nạp trong qu&aacute; tr&igrave;nh, ki&ecirc;n nhẫn cảm thụ thi&ecirc;n địa linh kh&iacute;.<br />\r\n<br />\r\nKh&ocirc;ng đến mười gi&acirc;y, H&agrave;n Tuyệt liền cảm nhận được trong kh&ocirc;ng kh&iacute; tr&agrave;n ngập Phong linh kh&iacute;, Thổ linh kh&iacute;, Mộc linh kh&iacute;, Thủy linh kh&iacute;.<br />\r\n<br />\r\nVề phần Hỏa linh kh&iacute;, L&ocirc;i linh kh&iacute;, tạm thời bắt kh&ocirc;ng đến.<br />\r\n<br />\r\nH&agrave;n Tuyệt bắt đầu hấp thu c&aacute;i n&agrave;y bốn cỗ linh kh&iacute;.<br />\r\n<br />\r\nTrước mắt hắn bỗng nhi&ecirc;n hiện ra từng h&agrave;ng chữ.<br />\r\n<br />\r\nHắn r&otilde; r&agrave;ng nhắm mắt lại, lại có th&ecirc;̉ nh&igrave;n thấy những chữ n&agrave;y, quả thực thần kỳ.<br />\r\n<br />\r\n&laquo; ngươi bắt đầu lần thứ nhất tu luyện, tu luyện của ngươi ph&aacute;t triển c&oacute; ph&iacute;a dưới lựa chọn &raquo;<br />\r\n<br />\r\n&laquo; một, cao điệu tuyệt thế thi&ecirc;n ki&ecirc;u, có th&ecirc;̉ đạt được một thanh Luyện Kh&iacute; cảnh ph&aacute;p kh&iacute; &raquo;<br />\r\n<br />\r\n&laquo; hai, điệu thấp tu luyện, có th&ecirc;̉ mở ra ẩn giấu tu vi, linh căn c&ocirc;ng năng &raquo;<br />\r\n<br />\r\nH&agrave;n Tuyệt kh&ocirc;ng ch&uacute;t do dự lựa chọn đầu thứ hai.<br />\r\n<br />\r\nĐầu thứ hai c&ocirc;ng năng xem x&eacute;t ch&iacute;nh l&agrave; thần kỹ.<br />\r\n<br />\r\nDạng n&agrave;y hắn về sau liền c&oacute; thể an t&acirc;m tu luyện, kh&ocirc;ng sợ bị ph&aacute;t gi&aacute;c!<br />\r\n<br />\r\n&laquo; ngươi c&oacute; thể t&ugrave;y thời mở ra ẩn t&agrave;ng c&ocirc;ng năng &raquo;<br />\r\n<br />\r\nH&agrave;n Tuyệt lộ ra d&aacute;ng tươi cười, ổn.<br />\r\n<br />\r\nThiết l&atilde;o c&ograve;n chưa trở về, hắn phải nắm chắc thời gian mạnh l&ecirc;n.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nTho&aacute;ng chớp mắt.<br />\r\n<br />\r\nThời gian hai năm cấp tốc đi qua.<br />\r\n<br />\r\n18 tuổi H&agrave;n Tuyệt đ&atilde; Luyện Kh&iacute; cảnh tầng bảy, Phong, Thổ, Mộc, Thủy tứ hệ linh căn tất cả đều tu luyện tới Luyện Kh&iacute; cảnh tầng bảy.<br />\r\n<br />\r\nTh&ocirc;ng qua quan hệ nh&acirc;n mạch liệt biểu, H&agrave;n Tuyệt biết H&igrave;nh Hồng Tuyền cũng mới Luyện Kh&iacute; cảnh tầng bảy tu vi.<br />\r\n<br />\r\nHai năm n&agrave;y, Thiết l&atilde;o một mực chưa c&oacute; trở về.<br />\r\n<br />\r\nĐ&aacute;ng nhắc tới ch&iacute;nh l&agrave;, H&igrave;nh Hồng Tuyền đối với hắn độ thiện cảm đ&atilde; d&acirc;ng l&ecirc;n đến 2 tinh.<br />\r\n<br />\r\nHai người đều kh&ocirc;ng c&oacute; giao lưu.<br />\r\n<br />\r\nBất qu&aacute; H&agrave;n Tuyệt có th&ecirc;̉ ph&aacute;t gi&aacute;c được H&igrave;nh Hồng Tuyền thường xuy&ecirc;n nh&igrave;n hắn chằm chằm, để hắn rất kh&ocirc;ng th&iacute;ch ứng.<br />\r\n<br />\r\nQu&aacute; đẹp trai cũng c&oacute; phiền phức.<br />\r\n<br />\r\nAi.<br />\r\n<br />\r\nTa chỉ muốn tu ti&ecirc;n.<br />\r\n<br />\r\nH&agrave;n Tuyệt trong l&ograve;ng thở d&agrave;i.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền d&aacute;ng dấp mỹ lệ, nhưng c&ograve;n chưa tới khuynh quốc khuynh th&agrave;nh d&aacute;ng vẻ, H&agrave;n Tuyệt kh&ocirc;ng muốn c&ugrave;ng n&agrave;ng sinh ra t&igrave;nh cảm.<br />\r\n<br />\r\nMột khi c&oacute; t&igrave;nh y&ecirc;u, hắn liền c&oacute; nhược điểm.<br />\r\n<br />\r\nHắn muốn trường sinh bất tử, cũng kh&ocirc;ng thể chết ở tr&ecirc;n nửa đường.<br />\r\n<br />\r\nChờ hắn vĩnh sinh bất tử, suy nghĩ th&ecirc;m t&igrave;nh y&ecirc;u.<br />\r\n<br />\r\nChỉ cần v&ocirc; địch, đến l&uacute;c đ&oacute; muốn bao nhi&ecirc;u nữ nh&acirc;n liền c&oacute; bấy nhi&ecirc;u nữ nh&acirc;n, cũng kh&ocirc;ng sợ cừu gia t&igrave;m phiền to&aacute;i!<br />\r\n<br />\r\nH&agrave;n Tuyệt một b&ecirc;n lao động, một b&ecirc;n ki&ecirc;n định nghĩ đến, xem H&igrave;nh Hồng Tuyền l&agrave; y&ecirc;u ma quỷ qu&aacute;i.<br />\r\n<br />\r\nĐ&uacute;ng l&uacute;c n&agrave;y.<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền bỗng nhi&ecirc;n đứng dậy.<br />\r\n<br />\r\nN&agrave;ng chậm r&atilde;i đi tới, dẫn tới mặt kh&aacute;c n&ocirc; bộc gh&eacute; mắt.<br />\r\n<br />\r\nN&agrave;ng trực tiếp đi v&agrave;o H&agrave;n Tuyệt trước mặt, cười n&oacute;i: &quot;Ngươi c&ugrave;ng ta tới một chuyến.&quot;<br />\r\n<br />\r\nH&agrave;n Tuyệt mộng.<br />\r\n<br />\r\nMặt kh&aacute;c n&ocirc; bộc đều h&acirc;m mộ ghen tỵ nh&igrave;n về ph&iacute;a hắn.<br />\r\n<br />\r\nCó th&ecirc;̉ bị như vậy ti&ecirc;n tử để mắt tới, hẳn l&agrave; c&oacute; chỗ tốt.<br />\r\n<br />\r\nĐổi lại l&agrave; bọn hắn, cho d&ugrave; chỉ l&agrave; đơn độc ở chung, đời n&agrave;y cũng đủ rồi.<br />\r\n<br />\r\nH&agrave;n Tuyệt kh&ocirc;ng d&aacute;m cự tuyệt, chỉ c&oacute; thể gật đầu.<br />\r\n<br />\r\nHai người đi ra vườn dược thảo.<br />\r\n<br />\r\nT&ecirc;n nam tu kia sĩ mở mắt hỏi: &quot;H&igrave;nh sư muội, ngươi đ&acirc;y l&agrave;?&quot;<br />\r\n<br />\r\nHắn d&ograve; x&eacute;t H&agrave;n Tuyệt, kh&ocirc;ng khỏi nh&iacute;u m&agrave;y.<br />\r\n<br />\r\nKẻ n&agrave;y cực kỳ tuấn mỹ!<br />\r\n<br />\r\nVườn dược thảo bọn n&ocirc; bộc cả một đời kh&ocirc;ng hề rời đi qua vườn dược thảo, kh&ocirc;ng c&oacute; thẩm mỹ, lại th&ecirc;m tư duy theo qu&aacute;n t&iacute;nh, kh&ocirc;ng c&oacute; l&agrave;m sao quan s&aacute;t H&agrave;n Tuyệt.<br />\r\n<br />\r\nKh&aacute;c ph&aacute;i h&uacute;t nhau, H&igrave;nh Hồng Tuyền dẫn đầu ph&aacute;t hiện H&agrave;n Tuyệt chỗ kh&aacute;c biệt.<br />\r\n<br />\r\nNam tu sĩ bởi v&igrave; H&igrave;nh Hồng Tuyền mới cẩn thận quan s&aacute;t H&agrave;n Tuyệt.<br />\r\n<br />\r\nH&agrave;n Tuyệt h&igrave;nh tượng để hắn bản năng kh&oacute; chịu.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; việc g&igrave;, sư ca tiếp tục tu luyện đi, ta muốn c&ugrave;ng vị tiểu ca n&agrave;y đơn độc t&acirc;m sự.&quot; H&igrave;nh Hồng Tuyền che miệng cười n&oacute;i.<br />\r\n<br />\r\nH&agrave;n Tuyệt hướng nam tu sĩ xấu hổ cười một tiếng, lộ ra rất khẩn trương.<br />\r\n<br />\r\nNam tu sĩ nh&iacute;u m&agrave;y, thật kh&ocirc;ng c&oacute; n&oacute;i c&aacute;i g&igrave;.<br />\r\n<br />\r\nH&agrave;n Tuyệt đi theo H&igrave;nh Hồng Tuyền đi hướng rừng c&acirc;y.<br />\r\n<br />\r\nTrong l&ograve;ng của hắn l&acirc;m v&agrave;o đấu tranh b&ecirc;n trong.<br />\r\n<br />\r\nTa c&aacute;i n&agrave;y đ&aacute;ng chết mị lực!<br />\r\n<br />\r\nSau đ&oacute; ta n&ecirc;n l&agrave;m c&aacute;i g&igrave;?<br />\r\n<br />\r\nPhản kh&aacute;ng?<br />\r\n<br />\r\nHay l&agrave;. . . Thuận theo?</p>', '2021-11-27 21:01:51', 7, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(159, 45, 'Ma tu bức hiếp, ban thưởng kiếm pháp', '4', '<p>H&agrave;n Tuyệt c&ugrave;ng H&igrave;nh Hồng Tuyền đi v&agrave;o một chỗ kh&ocirc;ng người rừng c&acirc;y b&ecirc;n trong, H&agrave;n Tuyệt t&acirc;m th&acirc;̀n b&acirc;́t định bất an, sợ H&igrave;nh Hồng Tuyền x&aacute;ch loại kia y&ecirc;u cầu.<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền quay người nh&igrave;n về ph&iacute;a hắn, &aacute;nh mắt s&aacute;ng rực.<br />\r\n<br />\r\nH&agrave;n Tuyệt kh&ocirc;ng d&aacute;m c&ugrave;ng chi đối mặt.<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền cười n&oacute;i: &quot;Tiểu ca, kh&ocirc;ng cần khẩn trương, ta cũng kh&ocirc;ng phải l&agrave; muốn l&agrave;m kh&oacute; ngươi, ta muốn hỏi hỏi ngươi, ngươi cam nguyện cả một đời l&agrave;m vườn dược thảo n&agrave;y n&ocirc; bộc sao?&quot;<br />\r\n<br />\r\nH&agrave;n Tuyệt trầm mặc.<br />\r\n<br />\r\nSau đ&oacute; ch&iacute;nh l&agrave; muốn ta c&uacute;i đầu, l&agrave;m trong l&ograve;ng b&agrave;n tay của ngươi đồ chơi?<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền tiến l&ecirc;n một bước, gh&eacute; v&agrave;o lỗ tai hắn n&oacute;i ra: &quot;Gi&uacute;p ta l&agrave;m một chuyện, ta mang ngươi tu ti&ecirc;n, tho&aacute;t khỏi ph&agrave;m nh&acirc;n th&acirc;n phận.&quot;<br />\r\n<br />\r\n&quot;Chuyện g&igrave;?&quot;<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền bờ m&ocirc;i khẽ trương khẽ hợp, cũng kh&ocirc;ng c&oacute; thanh &acirc;m truyền ra, nhưng H&agrave;n Tuyệt lại nghe được r&otilde; r&agrave;ng.<br />\r\n<br />\r\n&quot;Gi&uacute;p ta c&acirc;̀m tới Thiết l&atilde;o đan phổ, ta mang ngươi rời đi Ngọc Thanh t&ocirc;ng, tiến về ma gi&aacute;o, về sau ta sẽ thật tốt đợi ngươi, ở dưới tay ta, ngươi sẽ kh&ocirc;ng thụ nửa điểm tội nhi, nếu l&agrave; ngươi tư chất kh&ocirc;ng tệ, tu vi c&oacute; th&agrave;nh tựu, ch&uacute;ng ta thậm ch&iacute; c&oacute; thể kết th&agrave;nh đạo lữ.&quot;<br />\r\n<br />\r\nTruyền &Acirc;m Thuật!<br />\r\n<br />\r\nH&agrave;n Tuyệt trừng to mắt.<br />\r\n<br />\r\nB&agrave; nương n&agrave;y lại l&agrave; ma tu!<br />\r\n<br />\r\nH&agrave;n Tuyệt lộn xộn.<br />\r\n<br />\r\nViệc n&agrave;y cũng kh&ocirc;ng phải n&oacute;i đ&ugrave;a, hắn nếu l&agrave; đồng &yacute;, sẽ đắc tội to&agrave;n bộ Ngọc Thanh t&ocirc;ng.<br />\r\n<br />\r\nHắn nếu l&agrave; cự tuyệt, H&igrave;nh Hồng Tuyền tuyệt đối sẽ kh&ocirc;ng để hắn đi ra c&aacute;nh rừng c&acirc;y n&agrave;y.<br />\r\n<br />\r\nThiết l&atilde;o kh&ocirc;ng tại, nơi n&agrave;y chỉ l&agrave; Ngọc Thanh t&ocirc;ng ngoại m&ocirc;n, hắn h&ocirc; ra cuống họng đo&aacute;n chừng đều kh&ocirc;ng c&oacute; người ứng.<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền cũng bất th&ocirc;i gấp r&uacute;t, v&ograve;ng quanh H&agrave;n Tuyệt chuyển, ki&ecirc;n nhẫn chờ đợi.<br />\r\n<br />\r\nH&agrave;n Tuyệt tại ước lượng m&igrave;nh c&ugrave;ng H&igrave;nh Hồng Tuyền đối đ&acirc;̀u phần thắng.<br />\r\n<br />\r\nĐồng dạng l&agrave; Luyện Kh&iacute; cảnh tầng bảy, hắn kh&ocirc;ng c&oacute; ph&aacute;p thuật, ph&aacute;p kh&iacute;, l&agrave;m sao c&oacute; thể thắng được qua H&igrave;nh Hồng Tuyền?<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; khả năng liều mạng.<br />\r\n<br />\r\nH&agrave;n Tuyệt giả bộ sợ h&atilde;i, n&oacute;i: &quot;Ta kh&ocirc;ng biết vật kia vị tr&iacute;, huống hồ c&ograve;n c&oacute; Trương C&aacute;p tại. . .&quot;<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền mặt lộ vẻ h&agrave;i l&ograve;ng, H&agrave;n Tuyệt chưa hề n&oacute;i đan phổ hai chữ, n&oacute;i r&otilde; hay l&agrave; rất th&ocirc;ng minh.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng sao, c&ograve;n c&oacute; thời gian, ta c&oacute; thể đợi ngươi.&quot; H&igrave;nh Hồng Tuyền nhẹ giọng cười n&oacute;i.<br />\r\n<br />\r\nN&agrave;ng xuất ra một vi&ecirc;n đan dược, n&oacute;i: &quot;Đan n&agrave;y c&oacute; thể gi&uacute;p ngươi mở ra linh căn, ngươi trước ăn v&agrave;o, coi như lễ gặp mặt.&quot;<br />\r\n<br />\r\nH&agrave;n Tuyệt nh&igrave;n về ph&iacute;a đan n&agrave;y, tựa như chocolate.<br />\r\n<br />\r\nTuyệt b&iacute;ch l&agrave; độc dược!<br />\r\n<br />\r\nH&agrave;n Tuyệt kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o cự tuyệt, chỉ c&oacute; thể ăn v&agrave;o trong miệng.<br />\r\n<br />\r\nHắn d&ugrave;ng linh lực bao tr&ugrave;m đan dược, tr&aacute;nh cho đan dược h&ograve;a tan.<br />\r\n<br />\r\nNuốt v&agrave;o trong bụng về sau, H&agrave;n Tuyệt h&aacute; mồm, cố &yacute; để H&igrave;nh Hồng Tuyền nh&igrave;n thấy, hưng phấn n&oacute;i: &quot;Đa tạ ti&ecirc;n tử! Ta nhất định dốc hết to&agrave;n lực!&quot;<br />\r\n<br />\r\nMẹ n&oacute;!<br />\r\n<br />\r\nChờ ta lại tu luyện một năm, để cho ngươi biết lợi hại!<br />\r\n<br />\r\nH&agrave;n Tuyệt tuyệt kh&ocirc;ng c&oacute; khả năng cứ như vậy gia nhập ma gi&aacute;o.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nHắn muốn l&agrave; trường sinh bất tử, cũng kh&ocirc;ng phải mạo hiểm cả đời.<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền che miệng cười một tiếng, đưa thay sờ sờ H&agrave;n Tuyệt gương mặt, cười n&oacute;i: &quot;Tiểu ca thật sự l&agrave; hiểu chuyện, ngoan, về sau ta đem ngươi trở th&agrave;nh phu qu&acirc;n ta bồi dưỡng.&quot;<br />\r\n<br />\r\nH&agrave;n Tuyệt k&iacute;ch động.<br />\r\n<br />\r\nTrong l&ograve;ng th&igrave; buồn n&ocirc;n.<br />\r\n<br />\r\nX&uacute; b&agrave; nương, ngươi xứng với ta?<br />\r\n<br />\r\nBị H&igrave;nh Hồng Tuyền lau ch&ugrave;i một hồi l&acirc;u dầu, hai người mới trở lại vườn dược thảo.<br />\r\n<br />\r\nNam tu sĩ hồ nghi nh&igrave;n về ph&iacute;a H&agrave;n Tuyệt, chau m&agrave;y.<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền th&igrave; cười thần b&iacute;, tiếp tục ngồi xuống tu luyện.<br />\r\n<br />\r\nH&agrave;n Tuyệt đi v&agrave;o trong vườn dược thảo, trước mắt bỗng nhi&ecirc;n hiện ra từng h&agrave;ng chữ:<br />\r\n<br />\r\n&laquo; ngươi gặp phải ma tu H&igrave;nh Hồng Tuyền uy hiếp, ngươi c&oacute; ph&iacute;a dưới lựa chọn &raquo;<br />\r\n<br />\r\n&laquo; một, trộm lấy đan phổ, thu hoạch được H&igrave;nh Hồng Tuyền ưu &aacute;i, thu hoạch được Ngọc Thanh t&ocirc;ng 5 tinh cừu hận độ &raquo;<br />\r\n<br />\r\n&laquo; hai, tuyệt kh&ocirc;ng nghe theo, kh&ocirc;ng để cho H&igrave;nh Hồng Tuyền đạt được đan phổ, đợi H&igrave;nh Hồng Tuyền rời đi, có th&ecirc;̉ đạt được kiếm ph&aacute;p &mdash;&mdash; Tuyệt Chỉ Thần Kiếm &raquo;<br />\r\n<br />\r\nHả?<br />\r\n<br />\r\nC&ograve;n c&oacute; lựa chọn ban thưởng?<br />\r\n<br />\r\nC&oacute; thể!<br />\r\n<br />\r\nH&agrave;n Tuyệt nh&atilde;n t&igrave;nh s&aacute;ng l&ecirc;n.<br />\r\n<br />\r\nC&agrave;ng th&ecirc;m ki&ecirc;n định &yacute; nghĩ, tuyệt kh&ocirc;ng nghe theo.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nM&agrave;n đ&ecirc;m bu&ocirc;ng xuống.<br />\r\n<br />\r\nH&agrave;n Tuyệt trở lại trong ph&ograve;ng, mở ra kết giới c&ocirc;ng năng.<br />\r\n<br />\r\nHắn đem trong bụng đan dược phun ra, đừng ở trong d&acirc;y lưng.<br />\r\n<br />\r\nSau đ&oacute;, hắn đi đến giường gỗ trước tọa hạ, xuất ra m&agrave;n thầu bắt đầu ăn.<br />\r\n<br />\r\nHắn đ&atilde; t&iacute;ch cốc, nhưng v&igrave; diễn kịch, cũng phải ăn.<br />\r\n<br />\r\nĐ&ecirc;m đ&atilde; khuya.<br />\r\n<br />\r\nChờ năm vị n&ocirc; bộc ngủ về sau, H&agrave;n Tuyệt tiếp tục tu luyện.<br />\r\n<br />\r\nLấy tư chất của hắn, si&ecirc;u việt H&igrave;nh Hồng Tuyền tuyệt kh&ocirc;ng phải việc kh&oacute;.<br />\r\n<br />\r\nSau đ&oacute; mấy ng&agrave;y, H&agrave;n Tuyệt giống nhau thường ng&agrave;y lao động.<br />\r\n<br />\r\nTrương C&aacute;p một mực đợi tại Thiết l&atilde;o trong lầu c&aacute;c tu luyện, khiến cho H&agrave;n Tuyệt c&oacute; lấy cớ kh&ocirc;ng v&agrave;o l&acirc;u.<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền tựa hồ cũng kh&ocirc;ng n&oacute;ng nảy.<br />\r\n<br />\r\nTho&aacute;ng chớp mắt, thời gian nửa năm đi qua.<br />\r\n<br />\r\n&laquo; t&iacute;nh danh: H&agrave;n Tuyệt &raquo;<br />\r\n<br />\r\n&laquo; tuổi thọ: 18/85 &raquo;<br />\r\n<br />\r\n&laquo; chủng tộc: Ph&agrave;m nh&acirc;n &raquo;<br />\r\n<br />\r\n&laquo; tu vi: Luyện Kh&iacute; cảnh tám tầng &raquo;<br />\r\n<br />\r\n&laquo; c&ocirc;ng ph&aacute;p: Lục Đạo Lu&acirc;n Hồi C&ocirc;ng ( có th&ecirc;̉ truyền thừa ) &raquo;<br />\r\n<br />\r\n&laquo; ph&aacute;p thuật: Kh&ocirc;ng &raquo;<br />\r\n<br />\r\n&laquo; thần th&ocirc;ng: Kh&ocirc;ng &raquo;<br />\r\n<br />\r\n&laquo; ph&aacute;p kh&iacute;: Kh&ocirc;ng &raquo;<br />\r\n<br />\r\n&laquo; linh căn tư chất: Lục Đạo Linh Thể, ẩn chứa đỉnh cấp Phong, Hỏa, Thủy, Thổ, Mộc, L&ocirc;i linh căn, gia tăng nhất định kh&iacute; vận &raquo;<br />\r\n<br />\r\n&laquo; Ti&ecirc;n Thi&ecirc;n kh&iacute; vận như sau &raquo;<br />\r\n<br />\r\n&laquo; tuyệt thế v&ocirc; song: Ti&ecirc;n tư, mị lực đỉnh cấp &raquo;<br />\r\n<br />\r\n&laquo; Thi&ecirc;n Mệnh Kiếm Si: Kiếm Đạo tư chất đỉnh cấp, Kiếm Đạo ngộ t&iacute;nh đỉnh cấp &raquo;<br />\r\n<br />\r\n&laquo; th&acirc;n ph&aacute;p tuyệt trần: Th&acirc;n ph&aacute;p tư chất đỉnh cấp &raquo;<br />\r\n<br />\r\n&laquo; Ti&ecirc;n Đế hậu duệ: Thu hoạch được một bộ tuyệt thế c&ocirc;ng ph&aacute;p tu ti&ecirc;n, 1000 khối linh thạch thượng phẩm ( có th&ecirc;̉ r&uacute;t ra ) &raquo;<br />\r\n<br />\r\n&laquo; xem x&eacute;t quan hệ nh&acirc;n mạch &raquo;<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nH&agrave;n Tuyệt đột ph&aacute; tới Luyện Kh&iacute; cảnh tám tầng, tứ hệ linh căn tất cả đều đạt tới Luyện Kh&iacute; cảnh tám tầng.<br />\r\n<br />\r\nĐ&aacute;ng nhắc tới ch&iacute;nh l&agrave;, tuổi thọ của hắn cũng tăng trưởng 20 tuổi, để hắn mừng rỡ.<br />\r\n<br />\r\nLinh lực nhanh ch&oacute;ng tăng trưởng cảm gi&aacute;c thật l&agrave; mỹ diệu.<br />\r\n<br />\r\nTr&aacute;i lại H&igrave;nh Hồng Tuyền, Thiết l&atilde;o, tu vi một mực kh&ocirc;ng c&oacute; tiến bộ.<br />\r\n<br />\r\nĐ&acirc;y ch&iacute;nh l&agrave; thi&ecirc;n t&agrave;i c&ugrave;ng ph&agrave;m nh&acirc;n kh&aacute;c nhau.<br />\r\n<br />\r\nH&agrave;n Tuyệt &acirc;m thầm đắc &yacute;.<br />\r\n<br />\r\nQuả nhi&ecirc;n, muốn chịu được nh&agrave;m ch&aacute;n, mới c&oacute; thể c&agrave;ng mạnh.<br />\r\n<br />\r\nNếu như hắn ngay từ đầu liền tiến h&agrave;nh ph&agrave;m nh&acirc;n tu ti&ecirc;n, n&oacute;i kh&ocirc;ng chừng hiện tại ngay cả Luyện Kh&iacute; cảnh b&ocirc;́n tầng đều kh&ocirc;ng c&oacute; đạt tới.<br />\r\n<br />\r\nMột ng&agrave;y n&agrave;y ban đ&ecirc;m.<br />\r\n<br />\r\nH&agrave;n Tuyệt nghe được ngo&agrave;i cửa truyền đến tiếng bước ch&acirc;n, hắn lập tức nằm xuống, đ&oacute;ng lại kết giới.<br />\r\n<br />\r\n&quot;Ta chuẩn bị đem Trương C&aacute;p dẫn đi, đằng sau ngươi liền đi v&agrave;o t&igrave;m.&quot;<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền thanh &acirc;m truyền v&agrave;o H&agrave;n Tuyệt trong tai.<br />\r\n<br />\r\nH&agrave;n Tuyệt mở mắt, vội v&agrave;ng đứng l&ecirc;n.<br />\r\n<br />\r\nM&atilde; đức.<br />\r\n<br />\r\nHay l&agrave; kh&ocirc;ng tr&aacute;nh tho&aacute;t.<br />\r\n<br />\r\nH&agrave;n Tuyệt ki&ecirc;n nhẫn chờ đợi.<br />\r\n<br />\r\nLuyện Kh&iacute; cảnh tám tầng hắn đ&atilde; tu luyện ra thần thức, gi&aacute;c quan cũng si&ecirc;u việt thường nh&acirc;n.<br />\r\n<br />\r\nHắn mơ hồ nghe được xa xa lầu c&aacute;c truyền ra c&aacute;i b&agrave;n bị va chạm thanh &acirc;m.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Ngươi c&oacute; thể h&agrave;nh động.&quot;<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền thanh &acirc;m truyền đến, ngay sau đ&oacute;, H&agrave;n Tuyệt liền cảm gi&aacute;c được H&igrave;nh Hồng Tuyền c&ugrave;ng Trương C&aacute;p từ tr&ecirc;n m&aacute;i hi&ecirc;n bắt đi.<br />\r\n<br />\r\nH&agrave;n Tuyệt lặng lẽ đứng dậy, chuẩn bị đi l&agrave;m l&agrave;m bộ d&aacute;ng.<br />\r\n<br />\r\nCẩn thận từng li từng t&iacute; rời đi ph&ograve;ng ốc về sau, hắn hướng vườn dược thảo cửa ra v&agrave;o nh&igrave;n lại, ph&aacute;t hiện t&ecirc;n nam tu kia sĩ đổ v&agrave;o dưới c&acirc;y, kh&ocirc;ng biết sống chết.<br />\r\n<br />\r\n&quot;Rất yếu người đi đường.&quot;<br />\r\n<br />\r\nH&agrave;n Tuyệt &acirc;m thầm đậu đen rau muống.<br />\r\n<br />\r\nHắn hướng ph&iacute;a lầu c&aacute;c đi đến.<br />\r\n<br />\r\nThiết l&atilde;o lầu c&aacute;c c&oacute; hai tầng, c&ugrave;ng biệt thự một dạng, tr&aacute;ng lệ, b&agrave;y đầy c&aacute;c loại đồ sứ c&ugrave;ng bức tranh, H&agrave;n Tuyệt nhập sau ph&ograve;ng, bắt đầu m&ugrave; đi dạo.<br />\r\n<br />\r\nHo&agrave;n to&agrave;n kh&ocirc;ng cần t&acirc;m.<br />\r\n<br />\r\nCoi như thật c&oacute; bảo bối, hắn cũng kh&ocirc;ng c&oacute; địa phương thả.<br />\r\n<br />\r\nNgồi nửa giờ, H&agrave;n Tuyệt đứng dậy tiếp tục lục tung, &iacute;t nhất phải l&agrave;m bộ d&aacute;ng.<br />\r\n<br />\r\nLại qua nửa giờ.<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền trở về.<br />\r\n<br />\r\nN&agrave;ng vội v&atilde; nhập ph&ograve;ng, t&igrave;m tới H&agrave;n Tuyệt, hỏi: &quot;Đ&atilde; t&igrave;m được chưa?&quot;<br />\r\n<br />\r\nH&agrave;n Tuyệt đứng tại một c&aacute;i b&igrave;nh hoa b&ecirc;n cạnh, quay người n&oacute;i ra: &quot;T&igrave;m kh&ocirc;ng thấy a, lật ra một lần đều kh&ocirc;ng c&oacute; t&igrave;m tới.&quot;<br />\r\n<br />\r\nHắn bỗng nhi&ecirc;n nh&igrave;n thấy H&igrave;nh Hồng Tuyền tr&ecirc;n th&acirc;n tr&agrave;n đầy vết m&aacute;u.<br />\r\n<br />\r\nChẳng lẽ Trương C&aacute;p bị n&agrave;ng giết?<br />\r\n<br />\r\nH&agrave;n Tuyệt v&ocirc; &yacute; thức lui lại, kết quả v&ocirc; &yacute; đem tr&ecirc;n b&agrave;n nhỏ b&igrave;nh hoa đụng ng&atilde;.<br />\r\n<br />\r\nBinh &mdash;&mdash;<br />\r\n<br />\r\nB&igrave;nh hoa rơi tr&ecirc;n mặt đất, n&aacute;t.<br />\r\n<br />\r\nH&agrave;n Tuyệt nh&igrave;n thấy một bản b&iacute; tịch xuất hiện tại b&igrave;nh hoa trong mảnh vỡ, ph&iacute;a tr&ecirc;n khắc lấy hai c&aacute;i chữ to.<br />\r\n<br />\r\nĐan phổ.<br />\r\n<br />\r\nTĩnh!<br />\r\n<br />\r\nTrong ph&ograve;ng trong nh&aacute;y mắt y&ecirc;n tĩnh, c&acirc;y kim rơi cũng nghe tiếng.<br />\r\n<br />\r\nH&agrave;n Tuyệt sửng sốt, xấu hổ kh&ocirc;ng g&igrave; s&aacute;nh được.<br />\r\n<br />\r\nC&aacute;i n&agrave;y. . .<br />\r\n<br />\r\nMẹ n&oacute;!<br />\r\n<br />\r\nThiết l&atilde;o cứ như vậy giấu đồ vật?<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền đem &aacute;nh mắt từ đan phổ thượng chuyển dời, nh&igrave;n về ph&iacute;a H&agrave;n Tuyệt, &aacute;nh mắt thăm thẳm.<br />\r\n<br />\r\nH&agrave;n Tuyệt kinh hỉ n&oacute;i: &quot;V&acirc;̣y mà giấu ở trong b&igrave;nh hoa, l&atilde;o đầu n&agrave;y thực biết giấu đồ vật!&quot;</p>', '2021-11-27 21:02:16', 6, 1),
(160, 45, 'Tuyệt Chỉ Thần Kiếm, chuẩn bị Trúc Cơ', '5', '<p>H&agrave;n Tuyệt khẩn trương cực kỳ.<br />\r\n<br />\r\nHắn cố gắng biểu hiện được rất kinh hỉ, t&ugrave;y thời ph&ograve;ng bị H&igrave;nh Hồng Tuyền xuất thủ.<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền bỗng nhi&ecirc;n đi l&ecirc;n ph&iacute;a trước, nắm chắc tay của hắn, hưng phấn cười n&oacute;i: &quot;Kh&ocirc;ng hổ l&agrave; ngươi, thật sự l&agrave; ta vật biểu tượng!&quot;<br />\r\n<br />\r\n&quot;Chỉ cần ngươi cao hứng liền tốt.&quot;<br />\r\n<br />\r\n&laquo; H&igrave;nh Hồng Tuyền đối với ngươi hảo cảm tăng trưởng, có th&ecirc;̉ kết l&agrave;m đạo lữ, độ thiện cảm l&agrave; 3 tinh &raquo;<br />\r\n<br />\r\nH&agrave;n Tuyệt hiện ra h&agrave;ng chữ n&agrave;y, hắn lộ ra cười khổ.<br />\r\n<br />\r\nBất qu&aacute; H&igrave;nh Hồng Tuyền kh&ocirc;ng c&oacute; nh&igrave;n thấu, n&agrave;ng v&ograve;ng qua H&agrave;n Tuyệt, nhặt l&ecirc;n đan phổ, bắt đầu đọc qua.<br />\r\n<br />\r\nN&agrave;ng tựa hồ đang t&igrave;m c&aacute;i g&igrave;.<br />\r\n<br />\r\nH&agrave;n Tuyệt thấp thỏm chờ đợi.<br />\r\n<br />\r\nMuốn hay kh&ocirc;ng xuất thủ?<br />\r\n<br />\r\nNếu để cho H&igrave;nh Hồng Tuyền mang đi đan phổ, hắn liền kh&ocirc;ng chiếm được kiếm ph&aacute;p.<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền giống như bị thương, kh&iacute; tức rất kh&ocirc;ng ổn định.<br />\r\n<br />\r\n&Acirc;m n&agrave;ng một tay?<br />\r\n<br />\r\nĐể n&agrave;ng biết kh&oacute; m&agrave; lui.<br />\r\n<br />\r\nNgay tại H&agrave;n Tuyệt do dự l&uacute;c, một trận h&agrave;n phong từ đại m&ocirc;n cuốn tới.<br />\r\n<br />\r\nH&agrave;n Tuyệt vội v&agrave;ng kh&ocirc;ng kịp chuẩn bị, bị đụng bay ra ngo&agrave;i, nện ở tr&ecirc;n v&aacute;ch tường, bắn ngược rơi xuống đất.<br />\r\n<br />\r\nKh&ocirc;ng thế n&agrave;o đau.<br />\r\n<br />\r\nChỉ l&agrave; c&oacute; ch&uacute;t dọa người.<br />\r\n<br />\r\nH&agrave;n Tuyệt tập trung nh&igrave;n v&agrave;o, Thiết l&atilde;o!<br />\r\n<br />\r\nCổ của hắn nghi&ecirc;ng một c&aacute;i, ch&acirc;n phải khẽ run rẩy, l&agrave;m bộ h&ocirc;n m&ecirc; bất tỉnh.<br />\r\n<br />\r\n&quot;Bu&ocirc;ng ta xuống đan phổ!&quot;<br />\r\n<br />\r\nThiết l&atilde;o trầm giọng n&oacute;i, ngữ kh&iacute; băng l&atilde;nh.<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền &acirc;m thanh lạnh l&ugrave;ng n&oacute;i: &quot;Ngươi v&acirc;̣y mà trở về!&quot;<br />\r\n<br />\r\nThiết l&atilde;o mặt kh&ocirc;ng ch&uacute;t thay đổi n&oacute;i: &quot;Ta đ&atilde; sớm trở về, chỉ l&agrave; một mực đang chờ ngươi hiện th&acirc;n, trước đ&oacute; ta vừa về đến liền thấy hai người c&aacute;c ngươi, nhiều năm như vậy, đệ tử kh&aacute;c đều kh&ocirc;ng tiếp nhiệm vụ của ta, liền c&aacute;c ngươi tiếp, khẳng định c&oacute; chuy&ecirc;̣n &acirc;̉n ở b&ecirc;n trong.&quot;<br />\r\n<br />\r\nN&oacute;i đi, Thiết l&atilde;o bỗng nhi&ecirc;n xuất thủ, một chưởng đ&aacute;nh về ph&iacute;a H&igrave;nh Hồng Tuyền.<br />\r\n<br />\r\nTrong l&ograve;ng b&agrave;n tay của hắn d&aacute;n một tấm l&aacute; b&ugrave;a.<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền v&ocirc; &yacute; thức nhảy ra, Thiết l&atilde;o trong l&ograve;ng b&agrave;n tay bắn ra l&ocirc;i điện, h&oacute;a th&agrave;nh lưới điện cuốn lấy H&igrave;nh Hồng Tuyền.<br />\r\n<br />\r\nThiết l&atilde;o đi theo một cước đ&aacute; tới, đem H&igrave;nh Hồng Tuyền đạp bay ra ngo&agrave;i.<br />\r\n<br />\r\nCơ hồ l&agrave; trong nh&aacute;y mắt, Thiết l&atilde;o đem đan phổ cướp đi.<br />\r\n<br />\r\nH&igrave;nh Hồng Tuyền rơi xuống đất, n&agrave;ng to&agrave;n th&acirc;n run rẩy.<br />\r\n<br />\r\nThiết l&atilde;o thừa thắng x&ocirc;ng l&ecirc;n, H&igrave;nh Hồng Tuyền vội v&agrave;ng chạy trốn.<br />\r\n<br />\r\nHai người cấp tốc lao ra.<br />\r\n<br />\r\nNằm dưới đất H&agrave;n Tuyệt do dự muốn hay kh&ocirc;ng đứng dậy.<br />\r\n<br />\r\nKh&ocirc;ng được!</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nNếu l&agrave; đi l&ecirc;n, liền rửa sạch kh&ocirc;ng được chịu tội.<br />\r\n<br />\r\nH&agrave;n Tuyệt cũng nghĩ qua trốn.<br />\r\n<br />\r\nNhưng qu&aacute; nguy hiểm, hắn thậm ch&iacute; kh&ocirc;ng biết n&ecirc;n hướng chỗ n&agrave;o trốn.<br />\r\n<br />\r\nHắn ki&ecirc;n nhẫn chờ đợi.<br />\r\n<br />\r\nQua đi tới một giờ, H&agrave;n Tuyệt nhịn kh&ocirc;ng được g&atilde;i g&atilde;i c&aacute;i m&ocirc;ng.<br />\r\n<br />\r\nHắn bỗng nhi&ecirc;n cảm gi&aacute;c được một cỗ kh&iacute; tức tại ở gần lầu c&aacute;c.<br />\r\n<br />\r\nThiết l&atilde;o!<br />\r\n<br />\r\nH&agrave;n Tuyệt kh&ocirc;ng d&aacute;m động đậy.<br />\r\n<br />\r\nThiết l&atilde;o đi v&agrave;o trong lầu c&aacute;c.<br />\r\n<br />\r\nHắn nh&igrave;n thấy H&agrave;n Tuyệt, kh&ocirc;ng khỏi nh&iacute;u m&agrave;y.<br />\r\n<br />\r\nH&agrave;n Tuyệt đột nhi&ecirc;n ho khan.<br />\r\n<br />\r\nThiết l&atilde;o đi đến c&aacute;i ghế một b&ecirc;n trước tọa hạ, lẳng lặng nh&igrave;n chằm chằm H&agrave;n Tuyệt.<br />\r\n<br />\r\nH&agrave;n Tuyệt thi triển ra kiếp trước kiếp n&agrave;y nhất tr&aacute;c tuyệt nhất rất thật diễn kỹ.<br />\r\n<br />\r\nHắn bưng b&iacute;t lấy đầu đứng l&ecirc;n.<br />\r\n<br />\r\nM&ecirc; mang nh&igrave;n về ph&iacute;a chung quanh, hắn vừa nh&igrave;n thấy Thiết l&atilde;o, lập tức kinh hỉ.<br />\r\n<br />\r\n&quot;Thiết l&atilde;o! Ng&agrave;i rốt cục trở về!&quot;<br />\r\n<br />\r\nH&agrave;n Tuyệt cổ co rụt lại, khẩn trương nh&igrave;n về ph&iacute;a chung quanh.<br />\r\n<br />\r\nHắn cẩn thận từng li từng t&iacute; hỏi: &quot;Ma Nữ kia đi đ&acirc;u?&quot;<br />\r\n<br />\r\nThiết l&atilde;o mặt kh&ocirc;ng thay đổi nh&igrave;n về ph&iacute;a hắn.<br />\r\n<br />\r\nH&agrave;n Tuyệt liền vội v&agrave;ng đứng l&ecirc;n, c&uacute;i đầu đối mặt Thiết l&atilde;o, một bộ t&acirc;m th&acirc;̀n b&acirc;́t định bộ d&aacute;ng bất an.<br />\r\n<br />\r\nH&agrave;n Tuyệt đang đ&aacute;nh cược!<br />\r\n<br />\r\nCược Thiết l&atilde;o cũng kh&ocirc;ng c&oacute; ph&aacute;t hiện H&igrave;nh Hồng Tuyền c&ugrave;ng hắn giao lưu.<br />\r\n<br />\r\nNếu l&agrave; ph&aacute;t hiện, c&aacute;i kia H&agrave;n Tuyệt chỉ c&oacute; thể liều mạng.<br />\r\n<br />\r\n&quot;N&agrave;ng đ&atilde; n&oacute;i với ngươi như thế n&agrave;o?&quot; Thiết l&atilde;o d&ograve; hỏi.<br />\r\n<br />\r\nH&agrave;n Tuyệt kh&ocirc;ng d&aacute;m giấu diếm, th&agrave;nh thật trả lời.<br />\r\n<br />\r\n&quot;Hừ, n&agrave;ng muốn bồi dưỡng ngươi trở th&agrave;nh phu qu&acirc;n, thật sự l&agrave; tr&ograve; cười.&quot;<br />\r\n<br />\r\nThiết l&atilde;o bị chọc giận qu&aacute; m&agrave; cười l&ecirc;n, cảm thấy tiểu tử n&agrave;y đang nằm mơ.<br />\r\n<br />\r\nHắn quan s&aacute;t tỉ mỉ H&agrave;n Tuyệt.<br />\r\n<br />\r\nA.<br />\r\n<br />\r\nThật đ&uacute;ng l&agrave; đừng n&oacute;i.<br />\r\n<br />\r\nTiểu tử n&agrave;y thật biến tuấn, chỉ xem dung nhan c&ugrave;ng &aacute;nh mắt, kh&ocirc;ng ch&uacute;t n&agrave;o k&eacute;m cỏi hơn những đệ tử nội m&ocirc;n kia.<br />\r\n<br />\r\nBiến h&oacute;a qu&aacute; lớn!<br />\r\n<br />\r\nHẳn l&agrave;. . .<br />\r\n<br />\r\nThiết l&atilde;o đưa tay, bắt lấy H&agrave;n Tuyệt cổ tay phải, cẩn thận suy nghĩ.<br />\r\n<br />\r\nH&agrave;n Tuyệt rất khẩn trương, t&ugrave;y thời chuẩn bị xuất thủ.<br />\r\n<br />\r\n&quot;Tiểu tử n&agrave;y kh&ocirc;ng c&oacute; linh căn, cũng kh&ocirc;ng c&oacute; tu vi, chẳng lẽ l&agrave; ta qu&aacute; lo lắng?&quot;<br />\r\n<br />\r\nThiết l&atilde;o y&ecirc;n lặng nghĩ đến, nh&igrave;n thấy H&agrave;n Tuyệt kinh sợ bộ d&aacute;ng, hắn kh&ocirc;ng khỏi nhớ tới H&agrave;n Tuyệt phụ mẫu, t&acirc;m t&igrave;nh lập tức bực bội.<br />\r\n<br />\r\nHắn tức giận n&oacute;i: &quot;C&uacute;t về đi ngủ!&quot;<br />\r\n<br />\r\nH&agrave;n Tuyệt thở d&agrave;i một hơi, liền vội v&agrave;ng h&agrave;nh lễ.<br />\r\n<br />\r\nHắn cấp tốc rời đi lầu c&aacute;c.<br />\r\n<br />\r\nThiết l&atilde;o th&igrave; bắt đầu thu thập trong ph&ograve;ng.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nTrở lại trong ph&ograve;ng, H&agrave;n Tuyệt lặng lẽ l&ecirc;n giường.<br />\r\n<br />\r\n&laquo; bởi v&igrave; ngươi kh&ocirc;ng c&oacute; trợ gi&uacute;p H&igrave;nh Hồng Tuyền trộm lấy đan phổ, ngươi th&agrave;nh c&ocirc;ng thu hoạch được kiếm ph&aacute;p &mdash;&mdash; Tuyệt Chỉ Thần Kiếm &raquo;<br />\r\n<br />\r\nH&agrave;n Tuyệt t&acirc;m t&igrave;nh đắc &yacute;, rốt cục c&oacute; thủ đoạn bảo mệnh!<br />\r\n<br />\r\nHắn lập tức lựa chọn truyền thừa.<br />\r\n<br />\r\nMột cỗ k&yacute; ức tr&agrave;n v&agrave;o trong &oacute;c của hắn.<br />\r\n<br />\r\nTuyệt Chỉ Thần Kiếm kh&ocirc;ng tính là v&otilde; học, m&agrave; l&agrave; ph&aacute;p thuật, lấy chỉ l&agrave;m kiếm, linh lực chuyển h&oacute;a l&agrave;m kiếm kh&iacute;.<br />\r\n<br />\r\nH&agrave;n Tuyệt c&oacute; được tuyệt đỉnh Kiếm Đạo ngộ t&iacute;nh, đ&ecirc;m đ&oacute; liền học xong ph&aacute;p thuật n&agrave;y.<br />\r\n<br />\r\nBất qu&aacute; hắn kh&ocirc;ng c&oacute; bại lộ, vẫn như cũ bảo tr&igrave; điệu thấp.<br />\r\n<br />\r\nNg&agrave;y thứ hai.<br />\r\n<br />\r\nH&agrave;n Tuyệt sau khi rời giường, nghe mặt kh&aacute;c n&ocirc; bộc n&oacute;i Trương C&aacute;p chết rồi.<br />\r\n<br />\r\nNghe n&oacute;i đ&atilde; chết kh&ocirc;ng g&igrave; s&aacute;nh được th&ecirc; thảm, thất khiếu chảy m&aacute;u.<br />\r\n<br />\r\nCanh giữ ở vườn dược thảo cửa ra v&agrave;o nam tu sĩ cũng s&aacute;ng sớm rời đi, tối h&ocirc;m qua tựa hồ ph&aacute;t sinh đại sự.<br />\r\n<br />\r\nH&agrave;n Tuyệt kh&ocirc;ng khỏi v&igrave; Trương C&aacute;p mặc niệm.<br />\r\n<br />\r\n&laquo; H&igrave;nh Hồng Tuyền đối với ngươi hảo cảm tăng trưởng, có th&ecirc;̉ kết l&agrave;m đạo lữ, trước mắt độ thiện cảm l&agrave; 3.5 tinh &raquo;<br />\r\n<br />\r\nH&agrave;n Tuyệt sửng sốt.<br />\r\n<br />\r\nĐộ thiện cảm tại sao lại tăng trưởng?<br />\r\n<br />\r\nKh&ocirc;ng hiểu thấu!<br />\r\n<br />\r\nBất qu&aacute; trong thời gian ngắn, H&igrave;nh Hồng Tuyền cũng kh&ocirc;ng d&aacute;m xuất hiện nữa, H&agrave;n Tuyệt lại c&oacute; thể an t&acirc;m tu luyện.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nNửa th&aacute;ng sau.<br />\r\n<br />\r\nThiết l&atilde;o lại rời đi vườn dược thảo.<br />\r\n<br />\r\nGần nhất mười năm, hắn ra ngo&agrave;i tần suất c&agrave;ng ng&agrave;y c&agrave;ng cao.<br />\r\n<br />\r\nH&agrave;n Tuyệt nghe Vương l&atilde;o đầu n&oacute;i, Thiết l&atilde;o tựa hồ đại nạn sắp tới, ngay tại t&igrave;m tăng trưởng tuổi thọ dược liệu.<br />\r\n<br />\r\nKh&ocirc;ng phải ai đều giống như H&agrave;n Tuyệt, c&oacute; thể nhẹ nh&otilde;m kh&ocirc;ng ngừng đột ph&aacute;.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nThiết l&atilde;o tu vi kẹt tại Luyện Kh&iacute; cảnh tầng bảy, kh&ocirc;ng c&aacute;ch n&agrave;o lại tăng trưởng.<br />\r\n<br />\r\nĐ&acirc;y cũng l&agrave; tư chất kh&aacute;c biệt.<br />\r\n<br />\r\nThiết l&atilde;o l&agrave; điển h&igrave;nh ph&agrave;m nh&acirc;n tu ti&ecirc;n, muốn nghịch thi&ecirc;n cải mệnh.<br />\r\n<br />\r\nH&agrave;n Tuyệt ở trong l&ograve;ng cầu nguyện, hi vọng l&atilde;o đầu n&agrave;y chết ở tr&ecirc;n đường.<br />\r\n<br />\r\nHắn sở dĩ &aacute;c độc như vậy, l&agrave; bởi v&igrave; hắn ch&uacute; &yacute; tới Thiết l&atilde;o đối với m&igrave;nh vẫn như cũ l&agrave; 1 tinh cừu hận độ.<br />\r\n<br />\r\nTiềm ẩn địch nh&acirc;n a!<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nTho&aacute;ng chớp mắt.<br />\r\n<br />\r\nHai năm qua đi.<br />\r\n<br />\r\n20 tuổi H&agrave;n Tuyệt đem tứ hệ linh căn to&agrave;n bộ tu luyện tới Luyện Kh&iacute; cảnh chín tầng.<br />\r\n<br />\r\nChỉ cần đem l&ocirc;i, Hỏa linh căn cũng tu luyện tới Luyện Kh&iacute; cảnh chín tầng, hắn liền c&oacute; thể bắt đầu chuẩn bị đột ph&aacute; Tr&uacute;c Cơ cảnh.<br />\r\n<br />\r\nNhưng vườn dược thảo chung quanh bắt kh&ocirc;ng đến L&ocirc;i linh kh&iacute;, Hỏa linh kh&iacute;.<br />\r\n<br />\r\nH&agrave;n Tuyệt minh bạch ch&iacute;nh m&igrave;nh n&ecirc;n rời đi.<br />\r\n<br />\r\nĐ&ecirc;m đ&oacute;, hắn liền lặng lẽ rời đi.<br />\r\n<br />\r\nH&agrave;n Tuyệt c&ograve;n c&oacute; 1000 khối linh thạch thượng phẩm kh&ocirc;ng c&oacute; lấy ra, c&aacute;i n&agrave;y sẽ l&agrave; hắn h&agrave;nh tẩu tu ch&acirc;n giới lực lượng.<br />\r\n<br />\r\nTr&ecirc;n đường đi, H&agrave;n Tuyệt vừa đi, một b&ecirc;n d&ugrave;ng thần thức t&igrave;m kiếm chung quanh.<br />\r\n<br />\r\nHắn chuẩn bị trực tiếp đi b&aacute;i Ngọc Thanh t&ocirc;ng, trở th&agrave;nh Ngọc Thanh t&ocirc;ng đệ tử.<br />\r\n<br />\r\nNgọc Thanh t&ocirc;ng hẳn l&agrave; sẽ kh&ocirc;ng cự tuyệt một t&ecirc;n Luyện Kh&iacute; cảnh chín tầng tu sĩ.<br />\r\n<br />\r\nH&ocirc;m sau trời vừa s&aacute;ng.<br />\r\n<br />\r\nH&agrave;n Tuyệt rốt cuộc t&igrave;m được Ngọc Thanh t&ocirc;ng sơn m&ocirc;n.<br />\r\n<br />\r\nNgọc Thanh t&ocirc;ng, ở v&agrave;o giữa d&atilde;y n&uacute;i trong sơn cốc, ti&ecirc;n vụ tr&agrave;n ngập, sơn m&ocirc;n đứng thẳng hai t&ocirc;n tượng đ&aacute; khổng lồ, rất giống Kỳ L&acirc;n.<br />\r\n<br />\r\n&quot;Dừng lại, lộ ra cốc b&agrave;i.&quot;<br />\r\n<br />\r\nMột t&ecirc;n Ngọc Thanh t&ocirc;ng đệ tử n&acirc;ng l&ecirc;n trong tay kiếm n&oacute;i ra.<br />\r\n<br />\r\nHết thảy c&oacute; b&ocirc;́n t&ecirc;n đệ tử tr&ocirc;ng coi Ngọc Thanh t&ocirc;ng nhập miệng hang, tu vi đều l&agrave; Luyện Kh&iacute; cảnh năm tầng.<br />\r\n<br />\r\nH&agrave;n Tuyệt đ&oacute;ng lại ẩn t&agrave;ng c&ocirc;ng năng, chắp tay n&oacute;i: &quot;Tại hạ H&agrave;n Tuyệt, l&agrave; một kẻ t&aacute;n tu, Luyện Kh&iacute; cảnh chín tầng, muốn b&aacute;i nhập Ngọc Thanh t&ocirc;ng, kh&ocirc;ng biết c&oacute; thể c&oacute; phương ph&aacute;p?&quot;<br />\r\n<br />\r\nLuyện Kh&iacute; cảnh chín tầng!<br />\r\n<br />\r\nB&ocirc;́n t&ecirc;n đệ tử th&aacute;i độ trong nh&aacute;y mắt thay đổi.<br />\r\n<br />\r\nCầm kiếm đệ tử hướng b&ecirc;n cạnh đệ tử n&oacute;i một tiếng, đệ tử kia liền cấp tốc rời đi.<br />\r\n<br />\r\n&quot;Ngươi chờ ch&uacute;t, ta để cho người ta đi th&ocirc;ng tri ngoại m&ocirc;n chấp sự.&quot; Cầm kiếm đệ tử mở miệng n&oacute;i.<br />\r\n<br />\r\nH&agrave;n Tuyệt n&oacute;i một tiếng c&aacute;m ơn.<br />\r\n<br />\r\nNgọc Thanh t&ocirc;ng b&ecirc;n trong, trừ Thiết l&atilde;o, kh&ocirc;ng c&oacute; tu sĩ kh&aacute;c biết t&ecirc;n của hắn, Thiết l&atilde;o c&ograve;n chưa trở về, rất c&oacute; thể chết ở b&ecirc;n ngo&agrave;i, cho n&ecirc;n H&agrave;n Tuyệt cũng kh&ocirc;ng lo lắng bại lộ th&acirc;n phận.<br />\r\n<br />\r\nD&ugrave; sao hắn kh&ocirc;ng c&oacute; l&agrave;m qua nguy hại Ngọc Thanh t&ocirc;ng sự t&igrave;nh, kh&ocirc;ng thẹn với lương t&acirc;m.</p>', '2021-11-27 21:02:40', 7, 1),
(161, 46, 'Rốt cuộc có người đến đào ta!', '1', '<p>Đ&ocirc;ng đ&ocirc;ng đ&ocirc;ng!<br />\r\n<br />\r\nUy uy uy. . .<br />\r\n<br />\r\nC&oacute; hay kh&ocirc;ng người a!<br />\r\n<br />\r\nNhanh thả ta ra ngo&agrave;i nha!<br />\r\n<br />\r\nLão tử c&ograve;n sống, lão tử kh&ocirc;ng c&oacute; chết a!<br />\r\n<br />\r\nĐường X&aacute;n hung hăng ch&ugrave;y hai lần v&acirc;y khốn ch&iacute;nh m&igrave;nh quan t&agrave;i, th&ocirc;ng lệ gi&atilde;y dụa cầu cứu một phen về sau, liền tại nội t&acirc;m mặc niệm một tiếng &quot;KFC&quot;, sau đ&oacute; hai tay nhanh ch&oacute;ng l&agrave;m ra ☞☞☜☜ động t&aacute;c.<br />\r\n<br />\r\nGi&acirc;y l&aacute;t ở giữa, tại trước mắt của hắn, v&acirc;̣y mà trống rỗng xuất hiện một c&aacute;i n&oacute;ng hầm hập thơm ng&agrave;o ngạt Orleans Hambuger g&agrave; nướng tới.<br />\r\n<br />\r\n&quot;May mắn c&aacute;i n&agrave;y cấp thấp nhất m&agrave;u đỏ bug c&ograve;n c&oacute; thể d&ugrave;ng, kh&ocirc;ng đến mức thật chết đ&oacute;i tại c&aacute;i n&agrave;y quan t&agrave;i b&ecirc;n trong. . .&quot;<br />\r\n<br />\r\nTừng ngụm từng ngụm ăn Orleans Hambuger g&agrave; nướng, Đường X&aacute;n thực t&igrave;nh cảm thấy, những n&agrave;y ng&agrave;y ph&aacute;t sinh tr&ecirc;n người ch&iacute;nh m&igrave;nh sự t&igrave;nh, quả thực l&agrave; qu&aacute; mẹ n&oacute; hoang đường.<br />\r\n<br />\r\nNăm nay mới hai mươi hai tuổi, mới vừa tốt nghiệp Đường X&aacute;n, trừ n&atilde;o mạch k&iacute;n thường xuy&ecirc;n c&oacute; ch&uacute;t đậu bỉ b&ecirc;n ngo&agrave;i, thật l&agrave; một c&aacute;i lại phổ th&ocirc;ng bất qu&aacute; sinh vi&ecirc;n đại học.<br />\r\n<br />\r\nVốn nghĩ thật vất vả tốt nghiệp, tại gia nh&igrave;n xem tiểu thuyết xo&aacute;t xo&aacute;t động mạn, hảo hảo l&agrave;m mấy th&aacute;ng trạch nam.<br />\r\n<br />\r\nLại bị cha mẹ đuổi tới thi đậu thị thư viện bi&ecirc;n chế, th&agrave;nh v&igrave; một t&ecirc;n quang vinh m&agrave; vĩ đại s&aacute;ch b&aacute;o nh&acirc;n vi&ecirc;n quản l&yacute;.<br />\r\n<br />\r\nA? V&igrave; c&aacute;i g&igrave; s&aacute;ch b&aacute;o nh&acirc;n vi&ecirc;n quản l&yacute; c&oacute; thể d&ugrave;ng vĩ đại để h&igrave;nh dung đ&acirc;u?<br />\r\n<br />\r\nT&oacute;m lại, s&aacute;ch b&aacute;o nh&acirc;n vi&ecirc;n quản l&yacute; c&ocirc;ng t&aacute;c cũng c&ograve;n tạm được, trống kh&ocirc;ng thời gian c&agrave;ng nhiều, mấu chốt l&agrave; c&oacute; thể d&ugrave;ng quang minh ch&iacute;nh đại nh&igrave;n c&aacute;c chủng tiểu thuyết c&ugrave;ng Thư.<br />\r\n<br />\r\nThời gian b&igrave;nh thản như nước, thẳng đến c&oacute; một ng&agrave;y, Đường X&aacute;n chỉnh l&yacute; thư viện trong l&ograve;ng đất t&agrave;ng thư thất thời điểm, ph&aacute;t hiện một bản rất kỳ qu&aacute;i Thư, trang b&igrave;a đ&atilde; tổn hại, b&ecirc;n trong văn tự lại c&ograve;n l&agrave; thượng cổ thể triện.<br />\r\n<br />\r\nV&igrave; cho c&aacute;i n&agrave;y bản Thư đăng k&yacute; tạo s&aacute;ch c&ugrave;ng ph&acirc;n loại, Đường X&aacute;n ph&iacute; hết sức lực m&agrave; mới xem hiểu ph&iacute;a tr&ecirc;n viết l&agrave; c&aacute;i g&igrave; nội dung.<br />\r\n<br />\r\nC&aacute;i n&agrave;y bản &quot;Thi&ecirc;n Thư&quot; d&ugrave;ng vẻ nho nh&atilde; ng&ocirc;n ngữ mi&ecirc;u tả cả c&aacute;i thi&ecirc;n địa tại đản sinh thời điểm, l&agrave; phi thường kh&ocirc;ng ho&agrave;n mỹ , bất kỳ c&aacute;i g&igrave; đồ vật, đều tồn tại t&agrave;n khuyết c&ugrave;ng lỗ thủng, d&ugrave;ng hiện đại lời n&oacute;i đến n&oacute;i liền l&agrave; bug.<br />\r\n<br />\r\nbug ở khắp mọi nơi , bất kỳ c&aacute;i g&igrave; đồ vật đều tồn tại bug, nếu như c&oacute; thể nh&igrave;n đến đồng thời lợi dụng những n&agrave;y bug, liền c&oacute; thể thu hoạch đến năng lực kh&oacute; tin, đồng thời. . . Cũng c&oacute; thể trợ gi&uacute;p cả c&aacute;i Thi&ecirc;n Địa Biến đến c&agrave;ng th&ecirc;m ho&agrave;n mỹ.<br />\r\n<br />\r\nRất tốn sức đại kh&aacute;i nh&igrave;n xong c&aacute;i n&agrave;y bản &quot;Thi&ecirc;n Thư&quot; về sau, Đường X&aacute;n ch&iacute;nh ph&aacute;t sầu muốn thế n&agrave;o cho hắn ph&acirc;n loại l&uacute;c, đột nhi&ecirc;n c&aacute;i n&agrave;y bản &quot;Thi&ecirc;n Thư&quot; liền bạo ra một cổ ch&oacute;i mắt &aacute;nh s&aacute;ng, cơ hồ đem Đường X&aacute;n &aacute;nh mắt cho l&oacute;e m&ugrave;.<br />\r\n<br />\r\nNgắn ngủi m&ugrave; về sau, Đường X&aacute;n ph&aacute;t hiện tay b&ecirc;n trong &quot;Thi&ecirc;n Thư&quot; biến mất kh&ocirc;ng thấy g&igrave; nữa.<br />\r\n<br />\r\nC&aacute;i n&agrave;y thật sự l&agrave; qu&aacute; quỷ dị, Đường X&aacute;n nhanh ch&oacute;ng kiểm tra một ch&uacute;t tr&ecirc;n người m&igrave;nh, tốt tại cũng kh&ocirc;ng c&oacute; xuất hiện c&aacute;i g&igrave; dị trạng, trừ ch&iacute;nh m&igrave;nh. . . &Aacute;nh mắt.<br />\r\n<br />\r\nTừ ng&agrave;y đ&oacute; bắt đầu, Đường X&aacute;n liền ph&aacute;t hiện, chỉ cần m&igrave;nh đem &aacute;nh mắt tập trung nh&igrave;n chằm chằm nơi n&agrave;o đ&oacute; hoặc l&agrave; n&agrave;o đ&oacute; c&aacute;i đồ vật nh&igrave;n, liền hội hiện ra mang theo nhan sắc quang điểm tới.<br />\r\n<br />\r\nL&agrave;m hắn đem tầm mắt lại lần nữa tập trung đến quang điểm thời điểm, n&atilde;o hải b&ecirc;n trong liền sẽ xuất hiện kỳ qu&aacute;i tin tức tới.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nTỉ như n&oacute;i, l&agrave;m Đường X&aacute;n đem &aacute;nh mắt tập trung tại 【 T&acirc;n Hoa từ điển 】 l&uacute;c, ph&iacute;a tr&ecirc;n hiện ra một c&aacute;i m&agrave;u cam quang điểm, lại lần nữa tập trung về sau, đầu &oacute;c b&ecirc;n trong liền c&oacute; một thanh &acirc;m n&oacute;i cho Đường X&aacute;n, đối lấy 【 T&acirc;n Hoa từ điển 】 mười gi&acirc;y đồng hồ b&ecirc;n trong li&ecirc;n tục chớp mắt vượt qua một trăm lần.<br />\r\n<br />\r\nMười gi&acirc;y đồng hồ chớp mắt vượt qua một trăm lần?<br />\r\n<br />\r\nCũng ch&iacute;nh l&agrave; b&igrave;nh qu&acirc;n một gi&acirc;y đồng hồ ch&iacute; &iacute;t muốn mười lần.<br />\r\n<br />\r\nC&aacute;i n&agrave;y c&oacute; thể qu&aacute; kh&oacute;!<br />\r\n<br />\r\nNhưng l&agrave;, Đường X&aacute;n muốn biết c&aacute;i n&agrave;y l&agrave;m về sau, sẽ ph&aacute;t sinh c&aacute;i g&igrave;, liền khổ luyện v&agrave;i ng&agrave;y thời gian, rốt cuộc. . . L&agrave;m đến.<br />\r\n<br />\r\nTại ho&agrave;n th&agrave;nh kia một gi&acirc;y l&aacute;t ở giữa, Đường X&aacute;n cảm gi&aacute;c cặp mắt của m&igrave;nh c&oacute; một chủng n&oacute;i kh&ocirc;ng n&ecirc;n lời sảng kho&aacute;i, đại n&atilde;o cũng oanh minh một tiếng, sa v&agrave;o một chủng phi thường kh&ocirc;ng minh trạng th&aacute;i.<br />\r\n<br />\r\nSau đ&oacute;, n&atilde;o hải b&ecirc;n trong c&aacute;i thanh &acirc;m kia lại n&oacute;i cho hắn, giải kh&oacute;a &quot;Đ&atilde; gặp qua l&agrave; kh&ocirc;ng qu&ecirc;n được&quot; bug.<br />\r\n<br />\r\nLi&ecirc;n tưởng tới kia bản &quot;Thi&ecirc;n Thư&quot; kh&oacute; đọc nội dung, Đường X&aacute;n triệt để bị chấn kinh, nguy&ecirc;n lai kia bản &quot;Thi&ecirc;n Thư&quot; giao ph&oacute; ch&iacute;nh m&igrave;nh hai mắt c&oacute; thể d&ugrave;ng nh&igrave;n đến bug năng lực.<br />\r\n<br />\r\nC&aacute;i thứ nhất giải kh&oacute;a &quot;Đ&atilde; gặp qua l&agrave; kh&ocirc;ng qu&ecirc;n được&quot; bug, Đường X&aacute;n lợi dụng c&aacute;i n&agrave;y năng lực, tại l&uacute;c l&agrave;m việc, liền đi&ecirc;n cuồng tại lật xem thư viện b&ecirc;n trong c&aacute;c loại thư tịch, mỗi một tờ chỉ cần nhanh ch&oacute;ng qu&eacute;t mắt một v&ograve;ng, liền c&oacute; thể d&ugrave;ng vĩnh cửu ghi nhớ nội dung ph&iacute;a tr&ecirc;n.<br />\r\n<br />\r\nVề sau, Đường X&aacute;n rất nhanh nắm giữ ph&aacute;t hiện bug khiếu m&ocirc;n, li&ecirc;n tục ph&aacute;t hiện phi thường nhiều lại tốt chơi lại kỳ hoa bug.<br />\r\n<br />\r\nMột lần tại KFC chọn m&oacute;n ăn thời điểm, xếp h&agrave;ng qu&aacute; nhiều người, Đường X&aacute;n nh&igrave;n đến tr&ecirc;n quầy c&oacute; c&aacute;i m&agrave;u đỏ bug, liền tập trung &aacute;nh mắt đi qua, nội t&acirc;m mặc niệm KFC , dựa theo nhắc nhở thủ thế ☞☞☜☜ đ&aacute;ng l&agrave;m, v&acirc;̣y mà trống rỗng xuất hiện một c&aacute;i Orleans Hambuger g&agrave; nướng ở trước mặt m&igrave;nh.<br />\r\n<br />\r\nC&ograve;n c&oacute; một lần tại hỏa diễm b&ecirc;n trong ph&aacute;t hiện m&agrave;u v&agrave;ng bug, v&acirc;̣y mà l&agrave; tại l&ograve;ng b&agrave;n tay thi&ecirc;u đốt ra năm loại m&agrave;u sắc kh&aacute;c nhau hỏa diễm, duy tr&igrave; li&ecirc;n tục vượt qua ba gi&acirc;y đồng hồ về sau, liền c&oacute; thể vĩnh cửu miễn dịch hỏa hệ tổn thương bug.<br />\r\n<br />\r\nTại b&ecirc;n cạnh l&aacute;ng giềng thiếu phụ phơi nắng y phục b&ecirc;n tr&ecirc;n, Đường X&aacute;n ph&aacute;t hiện một c&aacute;i m&agrave;u đỏ bug, chỉ cần đem c&aacute;i n&agrave;y trọn bộ y phục trộm qua đến xuy&ecirc;n tại th&acirc;n b&ecirc;n tr&ecirc;n duy tr&igrave; li&ecirc;n tục ba giờ, đồng thời tại trong l&uacute;c n&agrave;y c&ugrave;ng n&ecirc;n thiếu phụ gọi điện thoại n&oacute;i một c&aacute;i &quot;Ngươi tốt tao a!&quot;, liền c&oacute; thể vĩnh cửu t&iacute;nh thu hoạch đến đối phương phương t&acirc;m.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nMu&ocirc;n h&igrave;nh mu&ocirc;n vẻ, c&aacute;c chủng cổ qu&aacute;i kỳ lạ bug, gi&acirc;y l&aacute;t ở giữa để Đường X&aacute;n cuộc đời b&igrave;nh thường, biến đến mu&ocirc;n m&agrave;u mu&ocirc;n vẻ l&ecirc;n đến.<br />\r\n<br />\r\nL&agrave;m đến một c&aacute;i kh&ocirc;ng c&oacute; bao nhi&ecirc;u l&yacute; tưởng c&ugrave;ng kh&aacute;t vọng trạch nam, Đường X&aacute;n một kh&ocirc;ng c&oacute; lợi dụng những năng lực n&agrave;y ph&aacute;t t&agrave;i, hai cũng kh&ocirc;ng c&oacute; t&ugrave;y &yacute; thi triển năng lực nhiễu loạn trị an x&atilde; hội c&ugrave;ng h&ograve;a b&igrave;nh thế giới, hắn chỉ l&agrave; lại tất cả c&oacute; thể điều khiển t&igrave;nh huống ph&iacute;a dưới, lần theo ch&iacute;nh m&igrave;nh đậu bỉ &yacute; nghĩ, l&agrave;m ra kh&ocirc;ng &iacute;t kh&ocirc;i h&agrave;i l&uacute;ng t&uacute;ng sự t&igrave;nh tới.<br />\r\n<br />\r\nNhưng m&agrave;. . .<br />\r\n<br />\r\nHết thảy đều tại ba ng&agrave;y trước, ph&aacute;t sinh kịch biến.<br />\r\n<br />\r\nĐương thời, Đường X&aacute;n ch&iacute;nh đi m&aacute;y bay muốn đến ở ngo&agrave;i ng&agrave;n dặm th&agrave;nh thị đi tương th&acirc;n, vừa l&uacute;c ở kh&ocirc;ng trung vạn m&eacute;t trở l&ecirc;n, hắn ph&aacute;t hiện một c&aacute;i m&agrave;u t&iacute;m bug.<br />\r\n<br />\r\nTh&ocirc;ng qua những ng&agrave;y n&agrave;y ph&aacute;t hiện c&ugrave;ng tổng kết, Đường X&aacute;n phỏng đo&aacute;n bug cấp bậc l&agrave; dựa theo hắn nhan sắc ph&acirc;n chia.<br />\r\n<br />\r\nTừ thấp đến cao hẳn l&agrave; hồng, chanh, ho&agrave;ng, lục, thanh, lam, tử bảy đẳng cấp, bất qu&aacute; đ&acirc;y cũng chỉ l&agrave; ch&iacute;nh Đường X&aacute;n suy đo&aacute;n, suy cho c&ugrave;ng trước mắt hắn tối cao chỉ gặp qua m&agrave;u v&agrave;ng bug, liền l&agrave; c&aacute;i kia ho&agrave;n to&agrave;n miễn dịch hỏa hệ tổn thương bug.<br />\r\n<br />\r\nM&agrave; ở tr&ecirc;n kh&ocirc;ng trung mười ng&agrave;n m&eacute;t tr&ecirc;n m&aacute;y bay, v&acirc;̣y mà c&oacute; c&aacute;i l&oacute;e tử sắc quang mang bug, đ&acirc;y tuyệt đối l&agrave; một c&aacute;i rất ngậm rất khốc huyễn cao đẳng cấp bug a!<br />\r\n<br />\r\nKh&ocirc;ng ch&uacute;t do dự, Đường X&aacute;n liền lập tức dựa theo m&agrave;u t&iacute;m bug n&oacute;i minh đi l&agrave;m, kh&ocirc;ng để &yacute; xinh đẹp tiếp vi&ecirc;n h&agrave;ng kh&ocirc;ng thuyết phục c&ugrave;ng ngăn cản, ở tr&ecirc;n m&aacute;y bay d&ugrave;ng g&agrave; đứng một ch&acirc;n tư thế duy tr&igrave; li&ecirc;n tục một ph&uacute;t, đồng thời h&ocirc; to một tiếng &quot;Ta tới, ta gặp, ta chinh phục&quot; .<br />\r\n<br />\r\nKết quả. . .<br />\r\n<br />\r\nVừa dứt lời!<br />\r\n<br />\r\nGi&acirc;y l&aacute;t ở giữa, Đường X&aacute;n liền tại chỗ liền hắc b&igrave;nh.<br />\r\n<br />\r\nĐ&uacute;ng! Đường X&aacute;n nh&acirc;n sinh liền hắc b&igrave;nh.<br />\r\n<br />\r\nMột trận m&ecirc; muội về sau, Đường X&aacute;n liền xuất hiện tại hiện tại chỗ c&aacute;i n&agrave;y quan t&agrave;i lớn b&ecirc;n trong.<br />\r\n<br />\r\nN&atilde;o hải b&ecirc;n trong thanh &acirc;m nhắc nhở hắn, giải kh&oacute;a &quot;Hồn xuy&ecirc;n dị giới&quot; bug, cũng liền ra n&oacute;i. . . Ch&iacute;nh hắn t&igrave;m đường chết k&iacute;ch ph&aacute;t c&aacute;i kia bug, sau đ&oacute; liền mẹ n&oacute; xuy&ecirc;n việt rồi, c&ograve;n l&agrave; hồn xuy&ecirc;n. . .<br />\r\n<br />\r\nM&agrave; lại, c&agrave;ng c&agrave;ng c&agrave;ng hố l&agrave;, Đường X&aacute;n hiện tại xuy&ecirc;n qua tới th&acirc;n thể n&agrave;y, mặc d&ugrave; cũng gọi l&agrave; Đường X&aacute;n, nhưng l&agrave; một c&aacute;i người chết, hơn nữa c&ograve;n l&agrave; một c&aacute;i đ&atilde; được hạ t&aacute;ng ch&ocirc;n đến dưới nền đất người chết a!<br />\r\n<br />\r\nD&ugrave;ng người chết th&acirc;n thể tỉnh lại, quan t&agrave;i b&ecirc;n trong rất đen, kh&ocirc;ng gian lớn nhỏ lại c&ograve;n được, d&ugrave; sao cũng l&agrave; nh&agrave; gi&agrave;u nh&acirc;n gia c&ocirc;ng tử, hạ t&aacute;ng quan t&agrave;i ch&iacute; &iacute;t c&oacute; một m&eacute;t năm cao, mặc d&ugrave; c&ograve;n chưa đủ d&ugrave;ng để Đường X&aacute;n đứng l&ecirc;n đến, nhưng &iacute;t ra ngồi dậy đến l&agrave; kh&ocirc;ng c&oacute; vấn đề.<br />\r\n<br />\r\nĐệ nhất thời gian, Đường X&aacute;n liền nhanh ch&oacute;ng kiểm tra một lần đầu &oacute;c b&ecirc;n trong k&yacute; ức, biết r&otilde; c&aacute;i n&agrave;y Đường X&aacute;n l&agrave; bản địa nổi danh thương nh&acirc;n chi gia Đường gia đại c&ocirc;ng tử.<br />\r\n<br />\r\nMặc d&ugrave; tướng mạo cực điểm xuất ch&uacute;ng, l&agrave; mười dặm t&aacute;m hương nổi danh mỹ nam tử. Nhưng m&agrave; từ nhỏ đầu &oacute;c liền c&oacute; ch&uacute;t vấn đề, ngốc ngốc sững sờ, cha mẹ kh&ocirc;ng c&oacute; &iacute;t bận t&acirc;m về hắn.<br />\r\n<br />\r\nC&aacute;i n&agrave;y dạng đầu &oacute;c hiển nhi&ecirc;n v&ocirc; ph&aacute;p th&agrave;nh v&igrave; gia nghiệp kế thừa người, th&ecirc;m l&ecirc;n l&uacute;c n&agrave;y Đường gia gặp nguy cơ, tộc b&ecirc;n trong trưởng l&atilde;o liền tập thể l&agrave;m chủ, cưỡng b&aacute;ch đường phụ đem Đường X&aacute;n ở rể cho Kim Lăng phủ Trần tri phủ thi&ecirc;n kim Trần Tư Mộc, d&ugrave;ng n&agrave;y kết tốt hai nh&agrave; quan hệ, mượn n&agrave;y vượt qua nguy cơ.<br />\r\n<br />\r\nC&oacute; thể l&agrave; ai biết, Đường X&aacute;n mặc d&ugrave; đầu &oacute;c kh&ocirc;ng được, cơ bản t&igrave;nh l&yacute; c&ograve;n l&agrave; c&oacute; kh&aacute;i niệm.<br />\r\n<br />\r\nNam tử ở rể, ở c&aacute;i thế giới n&agrave;y c&oacute; thể n&oacute;i l&agrave; v&ocirc; c&ugrave;ng nhục nh&atilde;, thậm ch&iacute; ở rể sau nam tử đều v&ocirc; ph&aacute;p tham gia khoa cử khảo th&iacute;, một đời đều hội bị người chỉ chỉ điểm điểm, kh&ocirc;ng ngẩng đầu được l&ecirc;n.<br />\r\n<br />\r\nCho n&ecirc;n, sỏa đầu sỏa n&atilde;o Đường X&aacute;n n&oacute;i c&aacute;i g&igrave; cũng kh&ocirc;ng đ&aacute;p ứng ở rể, sau c&ugrave;ng thậm ch&iacute; phục độc dược d&ugrave;ng chết minh ch&iacute;.<br />\r\n<br />\r\n&quot;&Aacute;ch! Sớm kh&ocirc;ng xuy&ecirc;n qua muộn kh&ocirc;ng xuy&ecirc;n qua, hết lần n&agrave;y tới lần kh&aacute;c. . . Đều mẹ n&oacute; nhập thổ vi an, ta c&aacute;i n&agrave;y t&iacute;nh kh&ocirc;ng t&iacute;nh x&aacute;c chết v&ugrave;ng dậy đ&acirc;u?&quot;<br />\r\n<br />\r\nVừa xuy&ecirc;n qua qua đến thời điểm, Đường X&aacute;n thật l&agrave; c&oacute; ch&uacute;t sụp đổ.<br />\r\n<br />\r\nQuan t&agrave;i rất lớn rất d&agrave;y, căn bản liền mở kh&ocirc;ng ra, bởi v&igrave; hắn th&acirc;n thể đ&atilde; đổi một c&aacute;i, cơ hồ tất cả tại tr&ecirc;n Địa Cầu giải kh&oacute;a bug năng lực, đều đ&atilde; mất đi hiệu lực.<br />\r\n<br />\r\nBao gồm đ&atilde; gặp qua l&agrave; kh&ocirc;ng qu&ecirc;n được, hỏa hệ tổn thương miễn dịch c&aacute;c loại một loạt bug, cũng kh&ocirc;ng c&oacute; đi theo xuất hiện tại c&aacute;i n&agrave;y th&acirc;n thể mới b&ecirc;n trong.<br />\r\n<br />\r\nChỉ c&oacute; Orleans Hambuger g&agrave; nướng c&aacute;i n&agrave;y dạng bug mới c&oacute; thể đủ sử dụng, t&aacute;c dụng kh&aacute;c tr&ecirc;n th&acirc;n thể bug, đo&aacute;n chừng phải một lần nữa tại th&acirc;n thể mới k&iacute;ch hoạt mới được.<br />\r\n<br />\r\nM&agrave; l&uacute;c n&agrave;y Đường X&aacute;n, bị kh&ocirc;́n tại đại đại quan t&agrave;i b&ecirc;n trong, liền sinh tồn cần thiết kh&ocirc;ng kh&iacute; c&ugrave;ng nước v&agrave; thức ăn cũng th&agrave;nh vấn đề.<br />\r\n<br />\r\nTheo lấy quan t&agrave;i b&ecirc;n trong kh&ocirc;ng kh&iacute; c&agrave;ng ng&agrave;y c&agrave;ng mỏng manh, Đường X&aacute;n tuyệt cảnh cầu sinh ph&iacute;a dưới, nh&igrave;n đến quan t&agrave;i n&agrave;o đ&oacute; vết nứt kh&iacute;ch b&ecirc;n trong, xuất hiện một c&aacute;i m&agrave;u cam bug quang điểm.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nM&agrave;u cam quang điểm bug, mặc d&ugrave; đẳng cấp kh&ocirc;ng cao, nhưng m&agrave;. . . N&oacute;i kh&ocirc;ng chừng liền c&oacute; thể thu hoạch đến gi&uacute;p m&igrave;nh đi ra năng lực a!<br />\r\n<br />\r\nĐường X&aacute;n lập tức tập trung &aacute;nh mắt, đọc đến bug nhắc nhở.<br />\r\n<br />\r\nCần thiết tại quan t&agrave;i b&ecirc;n trong li&ecirc;n tục l&agrave;m một trăm c&aacute;i ti&ecirc;u chuẩn chống đẩy về sau, h&ocirc; to một tiếng &quot;C&oacute; người chết rồi, nhưng l&agrave; hắn c&ograve;n sống&quot; .<br />\r\n<br />\r\nĐường X&aacute;n chật vật l&agrave;m theo về sau, một gi&acirc;y trước c&ograve;n đen chăm ch&uacute; quan t&agrave;i, gi&acirc;y l&aacute;t ở giữa ph&aacute;t sinh kịch biến.<br />\r\n<br />\r\nV&acirc;̣y mà biến th&agrave;nh một c&aacute;i cường đại tận thế sinh tồn thương, trừ c&ograve;n l&agrave; quan t&agrave;i tạo h&igrave;nh c&ugrave;ng chất liệu b&ecirc;n ngo&agrave;i, b&ecirc;n trong trang bị &aacute;nh đ&egrave;n c&ugrave;ng kh&ocirc;ng kh&iacute; hệ thống tuần ho&agrave;n, thậm ch&iacute; c&ograve;n c&oacute; một c&aacute;i x&iacute; bệt. . .<br />\r\n<br />\r\nNhưng l&agrave;, biến th&agrave;nh tận thế sinh tồn thương quan t&agrave;i, cũng biến đến c&agrave;ng th&ecirc;m ki&ecirc;n cố c&ugrave;ng ki&ecirc;n kh&ocirc;ng thể ph&aacute;, Đường X&aacute;n vẫn y như cũ ra kh&ocirc;ng được a!<br />\r\n<br />\r\nĐồng thời, c&aacute;i n&agrave;y tận thế sinh tồn thương b&ecirc;n trong, v&acirc;̣y mà kh&ocirc;ng c&oacute; bất kỳ thức ăn cung cấp, tồn trữ &aacute;p s&uacute;c kh&ocirc;ng kh&iacute; c&ugrave;ng nước cũng l&agrave; c&oacute; hạn.<br />\r\n<br />\r\nMột ng&agrave;y những n&agrave;y t&agrave;i nguy&ecirc;n hao hết về sau, Đường X&aacute;n chỉ sợ cũng thật muốn treo.<br />\r\n<br />\r\nĐ&oacute;i cả ng&agrave;y, bụng ục ục gọi Đường X&aacute;n, thử nghiệm lấy tại nội t&acirc;m mặc niệm một tiếng &quot;KFC&quot;, sau đ&oacute; l&agrave;m ra☞☞☜☜ thủ thế, quả nhi&ecirc;n. . . C&aacute;i n&agrave;y bug tại dị thế giới cũng l&agrave; th&iacute;ch hợp, Đường X&aacute;n lăng kh&ocirc;ng thu hoạch đến một c&aacute;i mỹ vị Orleans Hambuger g&agrave; nướng.<br />\r\n<br />\r\nKết quả l&agrave;. . .<br />\r\n<br />\r\nĐường X&aacute;n dựa v&agrave;o c&aacute;i n&agrave;y bug cung cấp thức ăn, c&ograve;n c&oacute; quan t&agrave;i biến th&agrave;nh tận thế sinh tồn thương, một ng&agrave;y bằng một năm qua ba ng&agrave;y.<br />\r\n<br />\r\nHắn thật l&agrave; muốn nổi đi&ecirc;n!<br />\r\n<br />\r\nC&aacute;i n&agrave;y ba ng&agrave;y, hắn cơ hồ đem quan t&agrave;i b&ecirc;n trong mỗi một chỗ đều nh&igrave;n hết, lại cũng kh&ocirc;ng c&oacute; ph&aacute;t hiện bất kỳ c&aacute;i g&igrave; t&acirc;n bug.<br />\r\n<br />\r\nKh&ocirc;ng lẽ, liền thật muốn m&agrave;i chết tại c&aacute;i n&agrave;y quan t&agrave;i b&ecirc;n trong r&ocirc;̀i sao?<br />\r\n<br />\r\nNắm giữ c&oacute; thể nh&igrave;n đến bug &aacute;nh mắt, ch&iacute;nh m&igrave;nh kh&ocirc;ng phải l&agrave; v&ocirc; địch sao?<br />\r\n<br />\r\nThế n&agrave;o c&oacute; thể d&ugrave;ng, c&ograve;n kh&ocirc;ng c&oacute; ra s&acirc;n c&ugrave;ng biểu diễn, liền c&aacute;i n&agrave;y biệt khuất bị v&acirc;y chết a?<br />\r\n<br />\r\nLiền tại Đường X&aacute;n kh&ocirc;ng cam lại l&uacute;c tuyệt vọng, đột nhi&ecirc;n đỉnh đầu b&ecirc;n tr&ecirc;n truyền đến một trận thanh &acirc;m kỳ qu&aacute;i.<br />\r\n<br />\r\nCộc cộc cộc. . .<br />\r\n<br />\r\n&quot;A? C&aacute;i n&agrave;y thanh &acirc;m. . . L&agrave; c&oacute; người tại đ&agrave;o đất! Thi&ecirc;n a! Th&acirc;n nh&acirc;n a! Rốt cuộc c&oacute; người đến đ&agrave;o ta r&ocirc;̀i sao? Nhanh nhanh nhanh. . . Nhanh. . . Lão tử nhất khắc cũng kh&ocirc;ng nghĩ tại c&aacute;i n&agrave;y trong quan t&agrave;i chờ lấy.&quot;<br />\r\n<br />\r\n. . .</p>', '2021-11-27 21:06:01', 17, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(162, 46, 'Còn cưới không cưới ta làm nương tử rồi?', '2', '<p>C&aacute;i n&agrave;y tiếng đ&agrave;o đất, tại Đường X&aacute;n nghe tới, tuyệt đối thắng được tiếng trời.<br />\r\n<br />\r\nChỉ l&agrave;, đến tột c&ugrave;ng l&agrave; ai hội chạy tới đ&agrave;o ch&iacute;nh m&igrave;nh mộ phần đ&acirc;u?<br />\r\n<br />\r\nTrộm mộ?<br />\r\n<br />\r\nĐệ nhất thời gian, Đường X&aacute;n liền nghĩ đến khả năng n&agrave;y.<br />\r\n<br />\r\nSuy cho c&ugrave;ng, Đường gia tại Kim Lăng phủ cũng coi l&agrave; tai to mặt lớn đại gia tộc, đại c&ocirc;ng tử qua đời vật bồi t&aacute;ng đ&aacute;ng tiền khẳng định kh&ocirc;ng &iacute;t.<br />\r\n<br />\r\nChỉ l&agrave;, những n&agrave;y trộm mộ kh&ocirc;ng lẽ liền thật d&aacute;m c&aacute;i n&agrave;y trắng trợn đến đ&agrave;o đất trộm mộ?<br />\r\n<br />\r\nKh&ocirc;ng sợ bị Đường gia ph&aacute;t hiện về sau, b&aacute;o quan đuổi bắt bọn hắn?<br />\r\n<br />\r\nCộc cộc cộc. . .<br />\r\n<br />\r\nTiếng đ&agrave;o đất c&agrave;ng ng&agrave;y c&agrave;ng gần, m&agrave; lại Đường X&aacute;n ngừng lại h&ocirc; hấp tỉ mỉ lắng nghe, tựa hồ chỉ c&oacute; một c&aacute;i xẻng tại đ&agrave;o đất, c&ograve;n đ&agrave;o đến c&oacute; ch&uacute;t ph&iacute; sức bộ dạng.<br />\r\n<br />\r\nNh&igrave;n đến, c&aacute;i n&agrave;y trộm mộ rất l&agrave; nghiệp dư a!<br />\r\n<br />\r\nChỉ c&oacute; một người tại đ&agrave;o, Đường X&aacute;n cũng kh&ocirc;ng biết, ch&iacute;nh m&igrave;nh quan t&agrave;i đến tột c&ugrave;ng l&agrave; ch&ocirc;n dưới đất bao nhi&ecirc;u m&eacute;t, c&oacute; thể l&agrave; dựa theo c&aacute;i n&agrave;y trộm mộ đ&agrave;o ph&aacute;p, muốn đ&agrave;o tới khi n&agrave;o a?<br />\r\n<br />\r\nKh&ocirc;ng thể kh&ocirc;ng n&oacute;i, c&aacute;i n&agrave;y trộm mộ thi c&ocirc;ng tiến độ x&aacute;c thực l&agrave; c&oacute; điểm chậm a!<br />\r\n<br />\r\nKh&ocirc;ng sai biệt lắm ba, bốn tiếng đi qua, Đường X&aacute;n nửa nằm tại quan t&agrave;i b&ecirc;n trong, d&ugrave;ng một c&aacute;i tay chống đỡ đầu, một b&ecirc;n nghe lấy tiếng đ&agrave;o đất, một b&ecirc;n lại d&ugrave;ng bug l&agrave;m c&aacute;i Orleans Hambuger g&agrave; nướng đến ăn.<br />\r\n<br />\r\nTiếng đ&agrave;o đất một mực kh&ocirc;ng c&oacute; ngừng qua, m&agrave; lại thanh &acirc;m c&agrave;ng ng&agrave;y c&agrave;ng gần.<br />\r\n<br />\r\nT&iacute;nh to&aacute;n đ&acirc;u ra đấy, Đường X&aacute;n tại c&acirc;n nhắc đến trộm mộ kh&ocirc;ng bỏ dở nửa chừng t&igrave;nh huống ph&iacute;a dưới, đại kh&aacute;i c&ograve;n c&oacute; năm, s&aacute;u tiếng, liền c&oacute; thể đ&agrave;o đến ch&iacute;nh m&igrave;nh quan t&agrave;i.<br />\r\n<br />\r\nM&agrave; b&acirc;y giờ Đường X&aacute;n cần thiết c&acirc;n nhắc ch&iacute;nh l&agrave;. . .<br />\r\n<br />\r\nMột hồi gặp đến trộm mộ, ch&iacute;nh m&igrave;nh l&agrave; x&aacute;c chết v&ugrave;ng dậy đ&acirc;u! C&ograve;n l&agrave; x&aacute;c chết v&ugrave;ng dậy đ&acirc;u! C&ograve;n l&agrave; x&aacute;c chết v&ugrave;ng dậy đ&acirc;u!<br />\r\n<br />\r\nC&aacute;i n&agrave;y l&agrave; một c&aacute;i rất lớn mật lại rất c&oacute; t&acirc;m trộm mộ, Đường X&aacute;n suy nghĩ, như thế n&agrave;o c&ugrave;ng hắn tiến h&agrave;nh một c&aacute;i hữu hảo lần thứ nhất hội gặp.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nHai giờ đi qua!<br />\r\n<br />\r\nLại ba giờ đi qua!</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nCộc cộc cộc!<br />\r\n<br />\r\nĐ&agrave;o đất thanh &acirc;m, rốt cuộc gần trong gang tấc.<br />\r\n<br />\r\nĐường X&aacute;n từ buồn ngủ trạng th&aacute;i b&ecirc;n trong, đột nhi&ecirc;n bừng tỉnh qua tới.<br />\r\n<br />\r\nBởi v&igrave; hắn nghe đến mặt đất nơi xa truyền đến một trận chấn động, thật giống l&agrave;. . . C&oacute; một đ&aacute;m người cưỡi ngựa qua tới.<br />\r\n<br />\r\n&quot;Ngọa t&agrave;o! Sẽ kh&ocirc;ng phải l&agrave;, c&oacute; người nh&igrave;n đến trộm mộ tại đ&agrave;o mộ, vụng trộm b&aacute;o quan đến bắt hắn đi?&quot;<br />\r\n<br />\r\nĐường X&aacute;n t&acirc;m liền l&agrave; ch&igrave;m xuống, &quot;Đừng nha! Vạn l&yacute; trường chinh chỉ c&ograve;n lại một bước cuối c&ugrave;ng. Muốn bắt, cũng phải đem ta trước đ&agrave;o ra ngo&agrave;i mới được a!&quot;<br />\r\n<br />\r\nV&oacute; ngựa tiếng c&agrave;ng ng&agrave;y c&agrave;ng gần, tiếng đ&agrave;o đất lại vẫn kh&ocirc;ng c&oacute; ngừng.<br />\r\n<br />\r\nC&aacute;i n&agrave;y để Đường X&aacute;n c&agrave;ng th&ecirc;m kỳ qu&aacute;i , ấn l&yacute; thuyết, như l&agrave; l&agrave; b&igrave;nh thường trộm mộ, nghe đến ph&iacute;a tr&ecirc;n động tĩnh lớn như vậy, nhất định sẽ đệ nhất thời gian nhanh ch&oacute;ng trước chạy là hơn.<br />\r\n<br />\r\nC&oacute; thể l&agrave;, c&aacute;i n&agrave;y trộm mộ nhưng thật giống như thiết t&acirc;m, từ bắt đầu đ&agrave;o liền một mực kh&ocirc;ng c&oacute; dừng lại qua, r&ograve;ng r&atilde; t&aacute;m, ch&iacute;n tiếng, ch&iacute;nh giữa kh&ocirc;ng ăn kh&ocirc;ng uống liền nh&agrave; vệ sinh đều kh&ocirc;ng l&ecirc;n sao?<br />\r\n<br />\r\nĐường X&aacute;n ch&iacute;nh nghi hoặc ở giữa, tiếng đ&agrave;o đất rốt cục cũng ngừng lại.<br />\r\n<br />\r\nM&agrave; lại, v&oacute; ngựa tiếng cũng dừng ở tr&ecirc;n đỉnh đầu của m&igrave;nh vị tr&iacute;, một đ&aacute;m đại kh&aacute;i bảy t&aacute;m người từ tr&ecirc;n ngựa nhảy xuống tới.<br />\r\n<br />\r\n&quot;&Aacute;ch! Nh&igrave;n tới c&aacute;i n&agrave;y c&aacute;i trộm mộ c&ugrave;ng ta vận kh&iacute;, kh&ocirc;ng ra sao. . .&quot;<br />\r\n<br />\r\nĐường X&aacute;n tại nội t&acirc;m lặng lẽ v&igrave; trộm mộ cầu nguyện, hắn c&ograve;n l&agrave; nhanh ch&oacute;ng chạy đi!<br />\r\n<br />\r\nTrộm mộ c&oacute; thể l&agrave; trọng tội, c&agrave;ng kh&ocirc;ng d&ugrave;ng n&oacute;i l&agrave; cướp Đường gia mộ, bị quan phủ bắt đến, nhẹ th&igrave; sung qu&acirc;n ba ng&agrave;n dặm, nặng th&igrave; Thu sau xử trảm.<br />\r\n<br />\r\nTốt xấu nh&acirc;n gia tại ch&iacute;nh m&igrave;nh mộ phần c&aacute;i n&agrave;y cố gắng t&aacute;m, ch&iacute;n tiếng, cũng t&iacute;nh l&agrave; đủ &yacute; tứ.<br />\r\n<br />\r\nM&agrave; ch&iacute;nh Đường X&aacute;n liền l&agrave; đ&aacute;nh gi&aacute; một ch&uacute;t c&ograve;n lại thổ lượng cao độ sẽ kh&ocirc;ng vượt qua nửa m&eacute;t, ch&iacute;nh m&igrave;nh chỉ cần khống chế quan t&agrave;i tận thế sinh tồn thương mở ra mui, hẳn l&agrave; kh&ocirc;ng kh&oacute; ra ngo&agrave;i.<br />\r\n<br />\r\nSợ nhất l&agrave; c&aacute;i g&igrave;. . .<br />\r\n<br />\r\nLiền l&agrave; đ&aacute;m kia ăn no kh&ocirc;ng c&oacute; việc g&igrave; l&agrave;m cưỡi ngựa gia hỏa, lại đem thổ cho điền trở về.<br />\r\n<br />\r\nCho n&ecirc;n, Đường X&aacute;n nhất định phải thừa dịp mau thừa dịp sớm từ quan t&agrave;i b&ecirc;n trong leo ra.<br />\r\n<br />\r\n&quot;Khởi động c&aacute;i chốt! Ta xem một ch&uacute;t, ở đ&acirc;u kia m&agrave;. . . Đ&uacute;ng rồi! C&aacute;i n&agrave;y m&agrave;u đỏ n&uacute;t bấm.&quot;<br />\r\n<br />\r\nCơ hội chớp mắt l&agrave; qua, Đường X&aacute;n ấn xuống một c&aacute;i m&agrave;u đỏ c&aacute;i chốt kh&oacute;a, v&egrave;o một lần, quan t&agrave;i liền hướng lấy hai b&ecirc;n thu v&agrave;o.<br />\r\n<br />\r\nQuả nhi&ecirc;n, ph&iacute;a tr&ecirc;n c&ograve;n bị &aacute;p lấy thật d&agrave;y tầng đất, kh&ocirc;ng sai biệt lắm một m&eacute;t d&agrave;y.<br />\r\n<br />\r\nPh&iacute;a trước Đường X&aacute;n cũng muốn, thực tại chịu đựng kh&ocirc;ng được thời điểm, liền muốn bắt đầu đ&agrave;o đất chạy trốn, giống ti&ecirc;u sinh g cứu rỗi b&ecirc;n trong kia dạng, từng ch&uacute;t từng ch&uacute;t đ&agrave;o ra ngo&agrave;i.<br />\r\n<br />\r\nHiện tại chỉ c&ograve;n sau c&ugrave;ng một m&eacute;t, kia c&ograve;n kh&ocirc;ng đơn giản?<br />\r\n<br />\r\nĐặc biệt l&agrave; Đường X&aacute;n l&agrave; từ ph&iacute;a dưới hướng l&ecirc;n đ&agrave;o, hắn bẻ g&atilde;y tận thế sinh tồn thương b&ecirc;n trong một c&aacute;i cờ-l&ecirc;, b&uacute; sữa mẹ lực kh&iacute; đều d&ugrave;ng đến, liều mạng hướng l&ecirc;n đ&agrave;o đất.<br />\r\n<br />\r\nCứng thực b&ugrave;n đất liền từng khối rơi xuống đến quan t&agrave;i b&ecirc;n trong, Đường X&aacute;n c&aacute;i n&agrave;y dạng đ&agrave;o tốc độ, hiển nhi&ecirc;n so c&aacute;i kia trộm mộ nhanh nhiều.<br />\r\n<br />\r\nM&agrave; l&uacute;c n&agrave;y. . .<br />\r\n<br />\r\nMặt đất b&ecirc;n tr&ecirc;n, Đường gia tam c&ocirc;ng tử, Đường Kim mang theo s&aacute;u c&aacute;i người hầu, đem Đường X&aacute;n mộ bia ph&iacute;a sau c&aacute;i hố to n&agrave;y cho bao bọc v&acirc;y quanh.<br />\r\n<br />\r\n&quot;Lớn mật ngốc c&ocirc;! Liền ta Đường gia đại thiếu mộ, ngươi cũng d&aacute;m cướp? Ngươi l&agrave; ăn h&ugrave;ng t&acirc;m b&aacute;o tử đảm?&quot;<br />\r\n<br />\r\nĐường Kim một mặt vui cười xem lấy b&ecirc;n trong hố to, c&oacute; ch&uacute;t đầy bụi đất một c&aacute;i d&aacute;ng dấp c&oacute; ch&uacute;t khỏe mạnh, ngũ quan so th&ocirc; kho&aacute;ng nữ h&agrave;i.<br />\r\n<br />\r\n&quot;Tam c&ocirc;ng tử! Ta cảm thấy c&aacute;i n&agrave;y ngốc c&ocirc; khẳng định l&agrave; nghĩ nam nh&acirc;n nghĩ đi&ecirc;n rồi sao? Liền đại c&ocirc;ng tử thi thể đều kh&ocirc;ng bu&ocirc;ng tha.&quot;<br />\r\n<br />\r\n&quot;C&ugrave;ng n&agrave;ng n&oacute;i nhảm c&aacute;i g&igrave;. Tam c&ocirc;ng tử, dứt kho&aacute;t đem c&aacute;i n&agrave;y ngốc c&ocirc; bắt đi gặp quan tốt.&quot;<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nC&aacute;i n&agrave;y nữ h&agrave;i t&ecirc;n thật l&agrave; g&igrave;, mọi người h&igrave;nh như đều kh&ocirc;ng biết, bốn dặm t&aacute;m hương người đều gọi n&agrave;ng ngốc c&ocirc;.<br />\r\n<br />\r\nBởi v&igrave; n&agrave;ng d&aacute;ng dấp rất khỏe mạnh, một m&eacute;t t&aacute;m v&oacute;c d&aacute;ng, sức lực to&agrave;n th&acirc;n lớn ra kỳ, d&aacute;ng dấp c&ograve;n xấu, người cũng l&agrave; ngốc b&ecirc;n trong ngu đần.<br />\r\n<br />\r\nAi cũng kh&ocirc;ng biết n&agrave;ng cha mẹ l&agrave; người n&agrave;o, từ c&aacute;i n&agrave;o th&ocirc;n đến, chỉ biết đột nhi&ecirc;n n&agrave;o đ&oacute; một ng&agrave;y, ngốc c&ocirc; liền xuất hiện tại Kim Lăng th&agrave;nh b&ecirc;n trong.<br />\r\n<br />\r\nMột c&aacute;i đại c&ocirc; nương gia, v&acirc;̣y mà giống những kia đầy tớ đồng dạng, khắp nơi dốc sức ăn tươi cơm.<br />\r\n<br />\r\nTại Kim Lăng th&agrave;nh b&ecirc;n trong khắp nơi đi dạo, thỉnh thoảng liền g&agrave;o to h&aacute;t một tiếng &quot;C&ocirc; nương gia kh&iacute; lực lớn như ngưu rồi! To&agrave;n th&agrave;nh Tuấn nhi lang c&uacute;t ph&acirc;n cầu lạc!&quot; .<br />\r\n<br />\r\nRất nhiều trong th&agrave;nh tiểu h&agrave;i đều hội truy lấy ngốc c&ocirc; nhổ nước miếng n&eacute;m đồ vật, h&ocirc; h&agrave;o c&aacute;c chủng vũ nhục chửi rủa, nhưng m&agrave; ngốc c&ocirc; lại ch&uacute;t n&agrave;o đều kh&ocirc;ng để &yacute; , mặc cho những n&agrave;y tiểu h&agrave;i v&acirc;y quanh ở ch&iacute;nh m&igrave;nh th&acirc;n một b&ecirc;n.<br />\r\n<br />\r\nNgược lại l&agrave; bởi v&igrave; ngốc c&ocirc; ph&aacute; hư th&agrave;nh b&ecirc;n trong rất nhiều lặn quy củ, dẫn đến rất nhiều đất bĩ lưu manh cũng muốn gi&aacute;o huấn ngốc c&ocirc;, lại bị ngốc c&ocirc; lực to như ngưu đ&aacute;nh đến nỗi ngay cả hắn mẹ đều kh&ocirc;ng nhận thức.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nNhất doạ người một lần, l&agrave; Kim Lăng th&agrave;nh b&ecirc;n trong T&agrave;o bang l&atilde;o đại tụ tập mười mấy t&ecirc;n v&otilde; c&ocirc;ng cao thủ, đem ngốc c&ocirc; v&acirc;y quanh ở trong một ng&otilde; hẻm.<br />\r\n<br />\r\nKết quả, mười mấy người đều bị ngốc c&ocirc; &ocirc;m lấy đại th&ocirc; c&acirc;y cột nện đến thất linh b&aacute;t lạc, lần lượt trọng thương chạy t&aacute;n loạn, ngốc c&ocirc; bản th&acirc;n nghe n&oacute;i bị ch&eacute;m hai đao, lưng b&ecirc;n tr&ecirc;n đều m&aacute;u thịt be b&eacute;t.<br />\r\n<br />\r\nTừ đ&oacute; về sau, một đoạn thời gian rất d&agrave;i, Kim Lăng th&agrave;nh b&ecirc;n trong người liền lại cũng chưa từng thấy qua ngốc c&ocirc;.<br />\r\n<br />\r\nC&oacute; người n&oacute;i, ngốc c&ocirc; mất m&aacute;u qu&aacute; nhiều, đ&atilde; kh&ocirc;ng biết r&otilde; chết tại chỗ n&agrave;o c&aacute;i rừng n&uacute;i hoang vắng.<br />\r\n<br />\r\nLại c&oacute; người n&oacute;i, ngốc c&ocirc; l&uacute;c h&ocirc;n m&ecirc;, bị n&agrave;o đ&oacute; c&aacute;i nh&agrave; gi&agrave;u nh&acirc;n gia hảo t&acirc;m mang về nh&agrave;, chữa khỏi vết thương về sau được thu l&agrave;m nghĩa nữ, th&agrave;nh v&igrave; c&aacute;i kia nh&agrave; gi&agrave;u nh&acirc;n gia thi&ecirc;n kim đại tiểu thư.<br />\r\n<br />\r\nHơn nữa c&oacute; người c&ograve;n n&oacute;i, ngốc c&ocirc; thụ thương h&ocirc;n m&ecirc; về sau, bị đi ngang qua thần ti&ecirc;n đạo trưởng cho cứu, thu l&agrave;m đồ đệ l&ecirc;n n&uacute;i tu luyện trường sinh bất l&atilde;o ti&ecirc;n thuật đi.<br />\r\n<br />\r\nMỗi người n&oacute;i một kiểu, c&aacute;c chủng c&aacute;ch n&oacute;i, liền liền tr&agrave; l&acirc;u tửu qu&aacute;n b&ecirc;n trong kể chuyện ti&ecirc;n sinh, đều chuy&ecirc;n m&ocirc;n v&igrave; ngốc c&ocirc; ra một tập 【 ngốc c&ocirc; tu ti&ecirc;n truyện 】 loại h&igrave;nh b&igrave;nh thư, cọ cọ điểm n&oacute;ng b&aacute;c b&aacute;c &aacute;nh mắt.<br />\r\n<br />\r\nNhưng m&agrave;. . .<br />\r\n<br />\r\nMấy th&aacute;ng về sau ng&agrave;y đ&oacute; s&aacute;ng sớm, đại bộ phận Kim Lăng th&agrave;nh b&ecirc;n trong người đều vẫn chưa rời giường, g&agrave; trống đều vừa mới vừa đ&aacute;nh qua h&oacute;t, th&agrave;nh b&ecirc;n trong liền lại vang l&ecirc;n một c&acirc;u kia quen thuộc m&agrave; xa lạ &quot;C&ocirc; nương gia kh&iacute; lực lớn như ngưu rồi! To&agrave;n th&agrave;nh Tuấn nhi lang c&uacute;t ph&acirc;n cầu lạc!&quot; .<br />\r\n<br />\r\nNgốc c&ocirc; trở về.<br />\r\n<br />\r\nThời gian mấy th&aacute;ng, đại gia ph&aacute;t hiện n&agrave;ng thật giống biến đến c&agrave;ng cao, cũng biến đến c&agrave;ng tr&aacute;ng, c&ugrave;ng với. . . C&agrave;ng xấu.<br />\r\n<br />\r\nTừ đ&oacute; về sau, th&agrave;nh b&ecirc;n trong mặc kệ c&aacute;i g&igrave; du c&ocirc;n lưu manh hoặc l&agrave; bang ph&aacute;i, đều kh&ocirc;ng d&aacute;m lại tr&ecirc;u chọc ngốc c&ocirc;. Ngốc c&ocirc; muốn l&agrave;m g&igrave; liền l&agrave;m g&igrave; đi! Kh&ocirc;ng c&oacute; người nguyện &yacute; c&ugrave;ng c&aacute;i n&agrave;y chủng kh&ocirc;ng muốn mệnh lại kh&iacute; lực lớn người đi&ecirc;n n&oacute;ng giận.<br />\r\n<br />\r\nH&ocirc;m nay ngốc c&ocirc; đến đ&agrave;o Đường X&aacute;n mộ phần sự t&igrave;nh, cũng rất nhanh truyền khắp cả c&aacute;i Kim Lăng th&agrave;nh.<br />\r\n<br />\r\nSuy cho c&ugrave;ng, ngốc c&ocirc; sớm thượng thi&ecirc;n kh&ocirc;ng s&aacute;ng liền chạy đến đ&agrave;o, phụ cận đi ngang qua một ch&uacute;t th&ocirc;n d&acirc;n c&ugrave;ng thương nh&acirc;n, kh&ocirc;ng đến hai giờ liền đem c&aacute;i n&agrave;y kỳ văn dị sự truyền khắp cả c&aacute;i Kim Lăng th&agrave;nh.<br />\r\n<br />\r\nĐương nhi&ecirc;n, Kim Lăng th&agrave;nh b&ecirc;n trong Đường gia cũng thu đến phong thanh, Đường Tam c&ocirc;ng tử Đường Kim liền dẫn lấy s&aacute;u c&aacute;i gia n&ocirc; hoả tốc cưỡi ngựa chạy tới.<br />\r\n<br />\r\nĐối mặt Đường Kim tr&aacute;ch cứ, ngốc c&ocirc; lại l&agrave; phanh một c&aacute;i, từ hố b&ecirc;n trong liền nhảy tới, phi thường lẽ thẳng kh&iacute; h&ugrave;ng địa đối đ&aacute;m người n&oacute;i ra:<br />\r\n<br />\r\n&quot;Đường X&aacute;n kh&ocirc;ng c&oacute; chết! Hắn c&ograve;n n&oacute;i, muốn cưới ta l&agrave;m vợ hắn đ&acirc;u! Hắn sao c&oacute; thể chết đ&acirc;u? Ta muốn đem hắn đ&agrave;o ra, hảo hảo hỏi hỏi hắn. . . C&ograve;n cưới kh&ocirc;ng cưới ta l&agrave;m nương tử.&quot;<br />\r\n<br />\r\n. . .</p>', '2021-11-27 21:06:24', 6, 1),
(163, 46, 'Uy uy uy! Có người hay không kéo ta một cái!', '3', '<p>Ngốc c&ocirc; uy danh tại b&ecirc;n ngo&agrave;i, Đường gia những n&agrave;y gia n&ocirc; nh&oacute;m cũng đều l&agrave; như sấm b&ecirc;n tai.<br />\r\n<br />\r\nVừa mới c&ograve;n d&aacute;m mặt mũi tr&agrave;n đầy ch&acirc;m chọc n&oacute;i những lời kia, c&oacute; thể hiện tại ngốc c&ocirc; nộ mắt nh&igrave;n hắn chằm chằm nh&oacute;m l&uacute;c, lại l&agrave; một c&aacute;i lời kh&ocirc;ng d&aacute;m n&oacute;i ra khỏi miệng đến.<br />\r\n<br />\r\nNgược lại l&agrave; Đường Tam c&ocirc;ng tử Đường Kim rất miệt thị nh&igrave;n tho&aacute;ng qua ngốc c&ocirc;, n&oacute;i ra: &quot;Hoang đường! Ta Đường gia đại c&ocirc;ng tử, thế n&agrave;o khả năng hội coi trọng ngươi c&aacute;i n&agrave;y người qu&aacute;i dị? C&ograve;n n&oacute;i muốn cưới ngươi? Cũng kh&ocirc;ng soi mặt v&agrave;o trong nước tiểu m&agrave; xem, nh&igrave;n ngươi d&aacute;ng dấp c&aacute;i g&igrave; bộ d&aacute;ng.&quot;<br />\r\n<br />\r\nC&oacute; thể ngốc c&ocirc; lại l&agrave; phi thường cố chấp lặp lại n&oacute;i: &quot;Đường X&aacute;n n&oacute;i qua, sẽ lấy ta. Hắn liền l&agrave; n&oacute;i như vậy, ngươi nh&oacute;m n&oacute;i to&agrave;n bộ kh&ocirc;ng t&iacute;nh. Ta chỉ tin tưởng hắn n&oacute;i, ta muốn đem hắn đ&agrave;o ra, trước mặt hỏi hỏi hắn.&quot;<br />\r\n<br />\r\n&quot;Ngậm miệng! Ngốc c&ocirc;, ngươi nếu như c&ograve;n d&aacute;m n&oacute;i lời như vậy, &ocirc; ta Đường gia thanh danh! Đừng tr&aacute;ch ta Đường Kim đối ngươi kh&ocirc;ng kh&aacute;ch kh&iacute;.&quot;<br />\r\n<br />\r\nĐường Kim nghe n&oacute;i liền nộ, lạnh l&ugrave;ng biểu t&igrave;nh, cầm lấy bội kiếm b&ecirc;n h&ocirc;ng liền chỉ lấy ngốc c&ocirc;.<br />\r\n<br />\r\nPhải biết, tại Kim Lăng th&agrave;nh Đường gia, Đường gia gia chủ đường Tu&acirc;n chỉ c&oacute; Đường X&aacute;n c&aacute;i n&agrave;y một c&aacute;i nhi tử ngốc, l&agrave; kh&ocirc;ng khả năng kế thừa Đường gia gia chủ chi vị.<br />\r\n<br />\r\nCho n&ecirc;n, đường Tu&acirc;n mấy c&aacute;i huynh đệ liền từ ch&iacute;nh m&igrave;nh sinh nhi tử ch&iacute;nh m&igrave;nh chọn lựa một c&aacute;i, nhận l&agrave;m con thừa tự cho đường Tu&acirc;n.<br />\r\n<br />\r\nNhị c&ocirc;ng tử đường ho&agrave;ng, tam c&ocirc;ng tử Đường Kim, tứ c&ocirc;ng tử đường tuấn, ngo&agrave;i s&aacute;ng l&agrave; gia chủ đường Tu&acirc;n nhi tử, tr&ecirc;n thực tế nhưng đều l&agrave; hắn chất nhi, cũng l&agrave; hắn c&aacute;i kh&aacute;c mấy c&aacute;i huynh đệ tranh đoạt Đường gia đời sau gia chủ thẻ đ&aacute;nh bạc.<br />\r\n<br />\r\nTại c&aacute;i n&agrave;y ba vị nhận l&agrave;m con thừa tự c&ocirc;ng tử b&ecirc;n trong, Đường Kim c&oacute; thể n&oacute;i l&agrave; b&ecirc;n trong người nổi bật.<br />\r\n<br />\r\nNăm nay vừa đầy hai mươi tuổi, lại l&agrave; đ&atilde; li&ecirc;n tiếp b&ecirc;n trong t&uacute; t&agrave;i c&ugrave;ng cử nh&acirc;n, sang năm kỳ thi m&ugrave;a xu&acirc;n cũng v&ocirc; c&ugrave;ng c&oacute; khả năng cập đệ b&ecirc;n trong c&aacute;i tiến sĩ.<br />\r\n<br />\r\nLại th&ecirc;m Đường Kim l&agrave; văn v&otilde; to&agrave;n t&agrave;i, từ nhỏ đ&atilde; b&aacute;i Kim Lăng th&agrave;nh phụ cận t&aacute;m đại v&otilde; l&acirc;m m&ocirc;n ph&aacute;i đứng đầu Thương V&acirc;n kiếm ph&aacute;i một vị trưởng l&atilde;o vi sư, kh&ocirc;ng đến hai mươi tuổi đ&atilde; l&agrave; v&otilde; giả trung kỳ tu vi, tại tr&ecirc;n giang hồ cũng coi l&agrave; tam lưu cao thủ.<br />\r\n<br />\r\nCưỡi ngựa bắn t&ecirc;n, dạng dạng tinh th&ocirc;ng, nếu như hắn c&ograve;n đi tham gia v&otilde; th&iacute;, thi c&aacute;i Cử nh&acirc;n v&otilde; trở về vấn đề một ch&uacute;t cũng kh&ocirc;ng lớn.<br />\r\n<br />\r\nM&agrave; lại, mấu chốt nhất l&agrave;, Đường Kim phụ th&acirc;n Đường Chu l&agrave; đời trước gia chủ cạnh tranh b&ecirc;n trong, bởi v&igrave; đặc th&ugrave; nguy&ecirc;n nh&acirc;n mới rơi bại v&agrave;o đường Tu&acirc;n.<br />\r\n<br />\r\nNhững gia tộc kia trưởng l&atilde;o thậm ch&iacute; một mực cũng đều c&agrave;ng th&ecirc;m tr&uacute;ng &yacute; Đường Chu l&agrave;m gia chủ, đ&uacute;ng l&uacute;c đời sau b&ecirc;n trong, đường Tu&acirc;n chỉ c&oacute; một t&ecirc;n nhi tử ngốc, căn bản v&ocirc; ph&aacute;p kế thừa vị tr&iacute; gia chủ, Đường Kim liền coi như được l&agrave; chiếm hết thi&ecirc;n thời địa lợi nh&acirc;n ho&agrave;, l&agrave; nội định Đường gia đời sau gia chủ.<br />\r\n<br />\r\nCũng ch&iacute;nh l&agrave; như đ&acirc;y, tại Đường gia b&ecirc;n trong, c&aacute;i n&agrave;y vị tam thiếu h&agrave;nh sự cũng phi thường ph&aacute;ch lối, nghiễm nhi&ecirc;n đem to&agrave;n bộ Đường gia coi như l&agrave; ch&iacute;nh m&igrave;nh t&uacute;i b&ecirc;n trong đồ vật.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nĐại c&ocirc;ng tử Đường X&aacute;n, mặc d&ugrave; l&agrave; hắn tr&ecirc;n miệng lu&ocirc;n mồm đại ca, c&oacute; thể lại một mực bị hắn coi l&agrave; cả c&aacute;i Đường gia sỉ nhục.<br />\r\n<br />\r\nTốt xấu Kim Lăng th&agrave;nh Đường gia cũng l&agrave; c&oacute; lai lịch lớn gia tộc, d&ugrave; l&agrave; hiện tại kh&ocirc;ng c&oacute; rơi đến dựa v&agrave;o kinh thương m&agrave; sống, gia tộc lại sinh ra c&aacute;i n&agrave;y một c&aacute;i si ngốc nhi tử ngốc, đối với Đường gia danh dự thanh danh đều l&agrave; cực lớn vũ nhục.<br />\r\n<br />\r\nCả c&aacute;i Kim Lăng th&agrave;nh c&aacute;i n&agrave;y hơn hai mươi năm qua, cũng kh&ocirc;ng biết nhiều &iacute;t người vụng trộm chế giễu Đường gia gia chủ đường Tu&acirc;n anh minh một thế, sau c&ugrave;ng sinh c&aacute;i xuẩn nhi tử, liền vị tr&iacute; gia chủ đều muốn truyền cho huynh đệ d&ograve;ng d&otilde;i.<br />\r\n<br />\r\nĐường Kim đ&atilde; sớm đem ch&iacute;nh m&igrave;nh xem l&agrave; Đường gia hạ một nhiệm kỳ gia chủ, tự nhi&ecirc;n l&agrave; đối &quot;L&agrave;m bẩn&quot; Đường gia thanh danh Đường X&aacute;n đủ kiểu ch&aacute;n gh&eacute;t c&ugrave;ng gh&eacute;t bỏ.<br />\r\n<br />\r\nCũng ch&iacute;nh bởi v&igrave; c&aacute;i n&agrave;y dạng, l&agrave;m nghe thấy c&oacute; người d&aacute;m can đảm đến đ&agrave;o đ&agrave;o Đường gia đại c&ocirc;ng tử phần mộ l&uacute;c, Đường Kim liền đệ nhất thời gian mang người trước tới.<br />\r\n<br />\r\nChỉ bất qu&aacute;, hắn kh&ocirc;ng nghĩ tới l&agrave;, đ&agrave;o mộ kh&ocirc;ng phải trộm mộ, m&agrave; l&agrave; ngốc c&ocirc; c&aacute;i n&agrave;y một c&aacute;i dị loại.<br />\r\n<br />\r\nM&agrave; lại, c&agrave;ng l&agrave;m cho Đường Kim nộ kh&ocirc;ng thể n&oacute;i l&agrave;, ngốc c&ocirc; lại vẫn d&aacute;m lu&ocirc;n miệng n&oacute;i Đường X&aacute;n đ&atilde; từng đ&atilde; đ&aacute;p ứng muốn cưới n&agrave;ng?<br />\r\n<br />\r\nHoang đường!<br />\r\n<br />\r\nNếu để cho lời đồn đ&atilde;i như vậy, truyền đến Kim Lăng th&agrave;nh b&ecirc;n trong, những kia kể chuyện ti&ecirc;n sinh n&oacute;i kh&ocirc;ng chắc muốn như thế n&agrave;o bố tr&iacute; cả c&aacute;i Đường gia.<br />\r\n<br />\r\nC&aacute;i g&igrave; &quot;Ngốc c&ocirc; Đường đại thiếu t&igrave;nh th&acirc;m s&acirc;u mưa mịt mờ&quot; &quot;Đ&agrave;o thi nghiệt duy&ecirc;n, nh&acirc;n quỷ t&igrave;nh chưa hết&quot; loại h&igrave;nh phao tin đồn, nhất định sẽ l&agrave;m cho Đường gia lại lần nữa th&agrave;nh v&igrave; cả c&aacute;i Kim Lăng th&agrave;nh chuyện cười.<br />\r\n<br />\r\n&quot;Đ&aacute;ng chết Đường X&aacute;n! Liền chết rồi, cũng c&ograve;n muốn bại hoại danh tiếng của gia tộc.&quot;<br />\r\n<br />\r\nSờ sờ c&aacute;i tr&aacute;n, Đường Kim trong l&ograve;ng liền c&agrave;ng l&agrave; tr&agrave;n đầy đối Đường X&aacute;n ch&aacute;n gh&eacute;t, bảo kiếm của hắn để ngang tại ngốc c&ocirc; trước mặt, sau c&ugrave;ng cảnh c&aacute;o n&oacute;i, &quot;Ngốc c&ocirc;! Ngươi b&acirc;y giờ lập tức rời mở c&aacute;i n&agrave;y b&ecirc;n trong, đồng thời bảo đảm kh&ocirc;ng lại n&oacute;i c&aacute;i n&agrave;y dạng ăn n&oacute;i kh&ugrave;ng đi&ecirc;n, bằng kh&ocirc;ng, ta hoặc l&agrave; đem đầu lưỡi của ngươi cắt, hoặc l&agrave; đem đầu của ngươi ch&eacute;m đứt.&quot;<br />\r\n<br />\r\nNgười kh&aacute;c sợ c&aacute;i n&agrave;y ngốc c&ocirc; sức lực to&agrave;n th&acirc;n, Đường Kim th&acirc;n v&igrave; đường đường v&otilde; giả, tự nhi&ecirc;n l&agrave; kh&ocirc;ng sợ.<br />\r\n<br />\r\nC&oacute; thể l&agrave;, ngốc c&ocirc; người cũng như t&ecirc;n, quyết định đồ vật liền kh&ocirc;ng cho ph&eacute;p người kh&aacute;c c&oacute; ch&uacute;t n&agrave;o can thiệp c&ugrave;ng cải biến, vẫn y như cũ kh&ocirc;ng kh&aacute;ch kh&iacute; lặp lại n&oacute;i: &quot;Ta sẽ kh&ocirc;ng đi. Ta đem Đường X&aacute;n cho đ&agrave;o ra, cũng nhanh. Ta biết r&otilde; hắn c&ograve;n sống, ta nhất định sẽ đem hắn đ&agrave;o ra. . .&quot;<br />\r\n<br />\r\n&quot;Tam c&ocirc;ng tử! Kh&ocirc;ng thể để tại đ&acirc;y c&aacute;i ngốc c&ocirc; rời đi, nếu kh&ocirc;ng n&agrave;ng đến th&agrave;nh b&ecirc;n trong n&oacute;i lung tung, ch&uacute;ng ta Đường gia liền lại muốn th&agrave;nh v&igrave; người kh&aacute;c tr&ograve; cười.&quot;<br />\r\n<br />\r\n&quot;Đ&uacute;ng vậy nha! Tam c&ocirc;ng tử. Đại c&ocirc;ng tử một chết, tri phủ đại nh&acirc;n kia một b&ecirc;n ban đầu liền kh&ocirc;ng tiện b&agrave;n giao. Trưởng l&atilde;o cố &yacute; đ&atilde; ph&acirc;n ph&oacute; ta nh&oacute;m những n&agrave;y hạ nh&acirc;n, Đường gia hiện tại l&agrave; đặc th&ugrave; thời k&igrave;, nguy cơ tr&ugrave;ng tr&ugrave;ng, để ch&uacute;ng ta quy củ một điểm l&agrave;m người, ng&agrave;n vạn kh&ocirc;ng được g&acirc;y chuyện. . .&quot;<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nMấy c&aacute;i n&ocirc; bộc cũng từ tr&ecirc;n ngựa cầm vũ kh&iacute;, c&oacute; quơ Lang Nha Bổng, c&oacute; cầm lấy đại khảm đao, c&ograve;n c&oacute; hai c&aacute;i cầm lấy cung tiễn.<br />\r\n<br />\r\nS&aacute;u c&aacute;i n&ocirc; bộc rất cẩn thận đem ngốc c&ocirc; tr&ugrave;ng điệp bao v&acirc;y lại, nhưng m&agrave; ngốc c&ocirc; lại căn bản kh&ocirc;ng đem hắn nh&oacute;m coi l&agrave; đại sự, n&agrave;ng quay người quay đầu, nghĩ muốn tiếp tục nhảy đến hố b&ecirc;n trong đi đ&agrave;o đất.<br />\r\n<br />\r\nC&aacute;i kia cầm lấy Lang Nha Bổng n&ocirc; bộc ngăn lại n&agrave;ng, ngốc c&ocirc; chỉ l&agrave; nhẹ nhẹ bắt lấy Lang Nha Bổng, sau đ&oacute; hướng lấy đằng sau một n&eacute;m, c&aacute;i n&agrave;y thể trọng hai trăm c&acirc;n đại h&aacute;n, liền trực tiếp bị ngốc c&ocirc; vung đến xa mười mấy m&eacute;t tiểu g&ograve; n&uacute;i kia một b&ecirc;n.<br />\r\n<br />\r\nPhịch một tiếng!<br />\r\n<br />\r\nĐem mặt kh&aacute;c năm c&aacute;i n&ocirc; bộc đều cho giật nảy m&igrave;nh!<br />\r\n<br />\r\nKh&ocirc;ng ai d&aacute;m lại ngăn ngốc c&ocirc;, m&agrave; mới vừa rồi c&ograve;n l&agrave; khinh thường ngốc c&ocirc; Đường Kim cũng l&agrave; sững sờ, nội t&acirc;m run l&ecirc;n n&oacute;i: &quot;C&aacute;i n&agrave;y ngốc c&ocirc;, đơn thuần nh&igrave;n kh&iacute; lực lời n&oacute;i, v&acirc;̣y mà kh&ocirc;ng k&eacute;m g&igrave; một dạng v&otilde; sư.&quot;<br />\r\n<br />\r\n&quot;Tam c&ocirc;ng tử! C&aacute;i n&agrave;y. . . C&aacute;i n&agrave;y l&agrave;m sao b&acirc;y giờ a?&quot;<br />\r\n<br />\r\nMắt nh&igrave;n lấy ngốc c&ocirc; lại muốn nhảy đi xuống tiếp tục đ&agrave;o đất, năm c&aacute;i n&ocirc; bộc đồng loạt cầu cứu một dạng nh&igrave;n về ph&iacute;a Đường Kim.<br />\r\n<br />\r\n&quot;Ngươi nh&oacute;m tản ra! Để ta tới thu thập n&agrave;ng.&quot;<br />\r\n<br />\r\nH&iacute;p mắt một lần &aacute;nh mắt, Đường Kim nắm chặt bảo kiếm trong tay, thu hồi l&ograve;ng khinh thường, d&ugrave;ng tốc độ nhanh nhất một kiếm nhanh ch&oacute;ng hướng lấy ngốc c&ocirc; đ&acirc;m tới.<br />\r\n<br />\r\nNhưng m&agrave;. . .<br />\r\n<br />\r\nLiền tại c&aacute;i n&agrave;y ngh&igrave;n c&acirc;n treo sợi t&oacute;c thời khắc, dưới nền đất t&acirc;n t&acirc;n khổ khổ tại đ&agrave;o đất Đường X&aacute;n, lại l&agrave; thật vất vả đem kia một m&eacute;t d&agrave;y tầng đất đều cho đ&acirc;m ph&aacute;, gi&atilde;y dụa lấy từ đống đất b&ecirc;n trong b&ograve; ra đến.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nPhi phi phi!<br />\r\n<br />\r\nNhổ ra miệng đầy b&ugrave;n cặn b&atilde;, Đường X&aacute;n mở to hai mắt nh&igrave;n, ngẩng đầu nh&igrave;n một ch&uacute;t c&aacute;i n&agrave;y kh&ocirc;ng sai biệt lắm cao bảy t&aacute;m m&eacute;t đại hố, c&aacute;i n&agrave;y. . . C&aacute;i n&agrave;y để ch&iacute;nh m&igrave;nh thế n&agrave;o đi l&ecirc;n a?<br />\r\n<br />\r\n&quot;Uy uy uy! C&oacute; người hay kh&ocirc;ng k&eacute;o ta một c&aacute;i!&quot;<br />\r\n<br />\r\n&quot;Lầu tr&ecirc;n bằng hữu! Ngươi nh&oacute;m nghe được ta n&oacute;i chuyện?&quot;<br />\r\n<br />\r\n&quot;Người tốt một đời b&igrave;nh an! Đến ta một c&aacute;i, ngươi kh&ocirc;ng k&eacute;o được ăn thiệt th&ograve;i, ngươi kh&ocirc;ng k&eacute;o được l&agrave;m a. . .&quot;<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nNghe đến ngoài h&ocirc;́ mặt thật giống c&oacute; động tĩnh, Đường X&aacute;n cũng mặc kệ kia nhiều, trực tiếp hướng lấy b&ecirc;n ngo&agrave;i lớn tiếng h&ocirc; l&ecirc;n.<br />\r\n<br />\r\nHắn c&aacute;i n&agrave;y một gọi kh&ocirc;ng sao, đem bờ hố b&ecirc;n tr&ecirc;n năm c&aacute;i Đường gia n&ocirc; bộc cho dọa đến bị thương.<br />\r\n<br />\r\n&quot;Thật giống l&agrave; đại c&ocirc;ng tử thanh &acirc;m!&quot;<br />\r\n<br />\r\n&quot;Từ hố b&ecirc;n trong truyền đến. . . Sẽ kh&ocirc;ng phải, đại c&ocirc;ng tử x&aacute;c chết v&ugrave;ng dậy đi?&quot;<br />\r\n<br />\r\n&quot;Hẳn l&agrave; ch&uacute;ng ta nghe sai đi? L&atilde;o tam, ngươi. . . Ngươi thăm d&ograve; đi xem một ch&uacute;t hố b&ecirc;n trong.&quot;<br />\r\n<br />\r\n&quot;A? Ta. . . Ta kh&ocirc;ng d&aacute;m a! Lại gọi! L&agrave; đại c&ocirc;ng tử thanh &acirc;m, thi&ecirc;n a! X&aacute;c chết v&ugrave;ng dậy! Đại c&ocirc;ng tử thi thể tại hố b&ecirc;n trong, c&ograve;n. . . C&ograve;n hướng ta vẫy gọi muốn lấy ta mệnh đ&acirc;u! Cứu mạng a. . .&quot;<br />\r\n<br />\r\n. . .</p>', '2021-11-27 21:06:54', 3, 1),
(164, 46, 'Thần kỳ màu xanh bug', '4', '<p>Đường Kim cầm trong tay bảo kiếm, ch&iacute;nh c&ugrave;ng ngốc c&ocirc; đối l&ecirc;n.<br />\r\n<br />\r\nNăm c&aacute;i n&ocirc; bộc lại l&agrave; bị hố b&ecirc;n trong đột nhi&ecirc;n xuất hiện Đường X&aacute;n cho dọa sợ, c&aacute;i n&agrave;y đại c&ocirc;ng tử c&oacute; thể l&agrave; đ&atilde; chết t&aacute;m ch&iacute;n ng&agrave;y a!<br />\r\n<br />\r\nC&aacute;i n&agrave;y kh&ocirc;ng phải x&aacute;c chết v&ugrave;ng dậy l&agrave; c&aacute;i g&igrave; a!<br />\r\n<br />\r\n&quot;Uy uy uy! C&aacute;i kia người. . . Ngươi thật giống như l&agrave; ta nh&oacute;m Đường gia gia đinh, ch&iacute;nh tốt! Nhanh nghĩ biện ph&aacute;p k&eacute;o ta đi l&ecirc;n.&quot;<br />\r\n<br />\r\nH&iacute;p mắt, Đường X&aacute;n nhận ra hố b&ecirc;n cạnh lộ mặt c&aacute;i kia n&ocirc; bộc, lập tức cao giọng gọi nói.<br />\r\n<br />\r\n&quot;Đừng a! Đại c&ocirc;ng tử, tha mạng a! Tha mạng a!&quot;<br />\r\n<br />\r\nT&ocirc;i tớ kia dọa đến m&ocirc;ng nước tiểu lưu, đối lấy đại hố liền l&agrave; kh&ocirc;ng ngừng dập đầu.<br />\r\n<br />\r\nC&aacute;i kh&aacute;c bốn c&aacute;i n&ocirc; bộc thấy thế, cũng đều lần lượt học lấy quỳ xuống đất dập đầu.<br />\r\n<br />\r\nSuy cho c&ugrave;ng, c&aacute;i n&agrave;y thế giới tương tự cổ đại Trung Quốc, tuyệt đại bộ phận người c&oacute; thể đều l&agrave; tin tưởng quỷ thần c&acirc;u chuyện, kh&ocirc;ng giống Đường X&aacute;n c&aacute;i n&agrave;y dạng sinh trưởng ở hồng kỳ hạ chủ nghĩa duy vật hảo thiếu ni&ecirc;n, từ trước đến nay cũng kh&ocirc;ng tin tr&ecirc;n thế giới n&agrave;y c&oacute; quỷ.<br />\r\n<br />\r\n&quot;Ta đi! Ta l&agrave; bảo ngươi nh&oacute;m k&eacute;o ta đi l&ecirc;n, kh&ocirc;ng phải để ngươi nh&oacute;m dập đầu.&quot;<br />\r\n<br />\r\nĐường X&aacute;n t&acirc;m l&yacute; mặt một trận phiền muộn, ch&iacute;nh r&otilde; r&agrave;ng tươi sống tại c&aacute;i n&agrave;y, n&oacute;i cũng l&agrave; người lời a!<br />\r\n<br />\r\nHết lần n&agrave;y tới lần kh&aacute;c bọn hắn đều cho l&agrave; m&igrave;nh x&aacute;c chết v&ugrave;ng dậy, kh&ocirc;ng được, đến trước hết nghĩ biện ph&aacute;p chứng minh ch&iacute;nh m&igrave;nh l&agrave; người sống mới l&agrave;.<br />\r\n<br />\r\nNhưng m&agrave;. . .<br />\r\n<br />\r\nTại Đường X&aacute;n nh&iacute;u lại &aacute;nh mắt thời điểm, hắn đột nhi&ecirc;n nh&igrave;n đến b&ecirc;n tr&aacute;i đằng trước b&ugrave;n b&ecirc;n trong, khảm nạm lấy một khỏa ng&oacute;n c&aacute;i kia lớn, c&ograve;n c&oacute; ch&uacute;t mang theo l&oacute;e lục quang h&ograve;n đ&aacute; nhỏ.<br />\r\n<br />\r\n&quot;Biết ph&aacute;t s&aacute;ng bảo thạch? Mẹ nha! Sẽ kh&ocirc;ng phải l&agrave; c&aacute;i g&igrave; t&iacute;nh ph&oacute;ng xạ vật chất a?&quot;<br />\r\n<br />\r\nĐường X&aacute;n c&oacute; thể l&agrave; nh&igrave;n qua qu&aacute; nhiều ti&ecirc;̉u thuy&ecirc;́t xuy&ecirc;n vi&ecirc;̣t, b&ecirc;n trong nh&acirc;n vật ch&iacute;nh liền thường xuy&ecirc;n lợi dụng người cổ đại th&iacute;ch g&igrave; &quot;Dạ Minh Ch&acirc;u&quot; c&ugrave;ng c&aacute;i kh&aacute;c &quot;Ph&aacute;t s&aacute;ng bảo thạch&quot; đặc chất, đem một ch&uacute;t m&atilde;nh liệt ph&aacute;t xạ t&iacute;nh vật chất, coi như bảo bối đưa cho cừu nh&acirc;n.<br />\r\n<br />\r\nSau đ&oacute;, kia cừu nh&acirc;n mỗi ng&agrave;y vuốt vuốt c&aacute;i n&agrave;y &quot;Hiếm thấy tr&acirc;n bảo&quot;, kh&ocirc;ng c&oacute; qua bao l&acirc;u liền l&ecirc;n t&acirc;y thi&ecirc;n đi.<br />\r\n<br />\r\nCho n&ecirc;n, Đường X&aacute;n nh&igrave;n đến c&aacute;i n&agrave;y một mai ph&aacute;t s&aacute;ng tảng đ&aacute; l&uacute;c, lập tức liền lui về sau mấy bước, sợ bị ph&oacute;ng xạ đến.<br />\r\n<br />\r\nBất qu&aacute;, tập trung nh&igrave;n v&agrave;o, Đường X&aacute;n mới nhẹ thở ra một hơi, bởi v&igrave; hắn ph&aacute;t hiện c&aacute;i n&agrave;y quang cũng kh&ocirc;ng phải tảng đ&aacute; bản th&acirc;n ph&aacute;t ra, m&agrave; l&agrave; ch&iacute;nh m&igrave;nh nh&igrave;n đến bug quang a!<br />\r\n<br />\r\nKh&ocirc;ng sai!<br />\r\n<br />\r\nM&agrave;u xanh bug quang mang.<br />\r\n<br />\r\nĐẳng cấp c&ograve;n kh&ocirc;ng thấp.<br />\r\n<br />\r\nN&oacute;i minh, c&aacute;i n&agrave;y tr&ecirc;n tảng đ&aacute;, tồn tại một c&aacute;i cao đẳng cấp bug a!</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nĐường X&aacute;n lập tức h&iacute;p mắt, tập trung tinh thần nh&igrave;n chằm chằm c&aacute;i n&agrave;y lục quang h&ograve;n đ&aacute; nhỏ nh&igrave;n lại, n&atilde;o hải b&ecirc;n trong liền hiện ra mở kh&oacute;a n&ecirc;n bug phương ph&aacute;p tới.<br />\r\n<br />\r\n【 ngậm lấy tảng đ&aacute; về sau, d&ugrave;ng đặc biệt tư thế dựng ngược ba ph&uacute;t! Sẽ giải kh&oacute;a bug. 】<br />\r\n<br />\r\n&quot;Hả? C&aacute;i n&agrave;y đơn giản? Tốt xấu l&agrave; c&aacute;i m&agrave;u xanh đẳng cấp bug, kh&ocirc;ng biết r&otilde; hội l&agrave; một c&aacute;i dạng g&igrave; năng lực đ&acirc;u? Tốt nhất l&agrave;. . . Kia chủng nghịch thi&ecirc;n cấp bậc, để ta c&oacute; thể d&ugrave;ng nhảy l&ecirc;n liền cao mười mấy m&eacute;t, gi&acirc;y l&aacute;t ở giữa biến th&agrave;nh v&otilde; l&acirc;m cao thủ cái chủng loại kia.&quot;<br />\r\n<br />\r\nPh&iacute;a trước trừ một c&aacute;i m&agrave;u v&agrave;ng bug c&ugrave;ng một c&aacute;i m&agrave;u t&iacute;m bug b&ecirc;n ngo&agrave;i, Đường X&aacute;n tại tr&ecirc;n Địa Cầu, cơ hồ kh&ocirc;ng c&oacute; giải kh&oacute;a c&aacute;i kh&aacute;c cao đẳng cấp bug.<br />\r\n<br />\r\nM&agrave;u xanh so m&agrave;u v&agrave;ng c&agrave;ng cao một bậc, cho n&ecirc;n Đường X&aacute;n cũng đối c&aacute;i n&agrave;y bug rất lớn hi vọng.<br />\r\n<br />\r\nĐ&atilde; hố ph&iacute;a tr&ecirc;n mấy c&aacute;i gia n&ocirc; tạm thời bị dọa đến kh&ocirc;ng d&aacute;m cứu m&igrave;nh đi l&ecirc;n, Đường X&aacute;n liền quyết định tự cứu.<br />\r\n<br />\r\nTr&igrave;nh tự so-easy!<br />\r\n<br />\r\nBước đầu ti&ecirc;n, đem h&ograve;n đ&aacute; nhỏ từ trong đất b&ugrave;n m&oacute;c ra đến.<br />\r\n<br />\r\nBước thứ hai, lau một ch&uacute;t ph&iacute;a tr&ecirc;n b&ugrave;n cặn b&atilde;, thổi thổi, tổng muốn l&agrave;m đến hơi hơi sạch sẽ một điểm đi!<br />\r\n<br />\r\nBước thứ ba, ngậm v&agrave;o trong miệng, a? C&ograve;n c&oacute; điểm ngọt, c&aacute;i n&agrave;y vị đạo cực giống &aacute;i t&igrave;nh a!<br />\r\n<br />\r\nBước thứ tư, vỗ vỗ hai tay, xoay người, dựng ngược l&ecirc;n đến!<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\n&quot;Đại c&ocirc;ng tử! Tha mạng a! Ch&uacute;ng tiểu nh&acirc;n mặc d&ugrave; ng&agrave;y thường b&ecirc;n trong cũng từng giễu cợt qua ngươi, nhưng l&agrave; cho tới nay kh&ocirc;ng c&oacute; l&agrave;m qua bất kỳ c&aacute;i g&igrave; h&atilde;m hại qua ng&agrave;i sự t&igrave;nh. . .&quot;<br />\r\n<br />\r\n&quot;Xin thương x&oacute;t a! Đại c&ocirc;ng tử! Nhỏ sau n&agrave;y trở về, nhất định mỗi ng&agrave;y đ&ecirc;m tối v&igrave; ng&agrave;i cầu ph&uacute;c, nguyện ng&agrave;i sớm ng&agrave;y c&oacute; thể d&ugrave;ng đầu thai chuyển thế, kiếp sau đầu c&aacute;i tốt thai.&quot;<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nNăm c&aacute;i n&ocirc; bộc một b&ecirc;n dập đầu một b&ecirc;n cầu xin tha thứ, sau đ&oacute; trong đ&oacute; c&oacute; một c&aacute;i lại len l&eacute;n hướng hố b&ecirc;n trong liếc một c&aacute;i.<br />\r\n<br />\r\nHắn bản &yacute; l&agrave; muốn nh&igrave;n một ch&uacute;t, hố b&ecirc;n trong kh&ocirc;ng c&oacute; động tĩnh, c&oacute; phải hay kh&ocirc;ng đại c&ocirc;ng tử lại về quan t&agrave;i b&ecirc;n trong đi, thật bỏ qua bọn hắn.<br />\r\n<br />\r\nC&oacute; thể l&agrave; kết quả. . .<br />\r\n<br />\r\nVội v&agrave;ng hướng xuống tho&aacute;ng nh&igrave;n, c&agrave;ng l&agrave; bị dọa sợ.<br />\r\n<br />\r\nVừa mới đại c&ocirc;ng tử, tốt xấu n&oacute;i l&agrave; người lời n&oacute;i, c&ograve;n tứ chi kiện to&agrave;n c&oacute; đầu c&oacute; ch&acirc;n giống người.<br />\r\n<br />\r\nThế n&agrave;o c&aacute;i n&agrave;y mới một l&aacute;t sau, liền biến th&agrave;nh một cỗ kh&ocirc;ng đầu nam thi a?<br />\r\n<br />\r\n&quot;A! Cứu mạng!&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng đầu &aacute;c quỷ! Đại c&ocirc;ng tử biến th&agrave;nh kh&ocirc;ng đầu &aacute;c quỷ. . .&quot;<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nNăm c&aacute;i n&ocirc; bộc c&aacute;i n&agrave;y một lần, dọa đến mau từ bờ hố lộn nh&agrave;o trốn đến Đường Kim c&ugrave;ng ngốc c&ocirc; b&ecirc;n n&agrave;y.<br />\r\n<br />\r\nThương thương thương. . .<br />\r\n<br />\r\nNgốc c&ocirc; cầm lấy đất b&ecirc;n tr&ecirc;n một c&aacute;i Lang Nha Bổng, ch&iacute;nh c&ugrave;ng Đường Kim đ&aacute;nh đến kh&oacute; bỏ kh&oacute; ph&acirc;n.<br />\r\n<br />\r\nĐường Kim kiếm chi&ecirc;u ra từ Thương V&acirc;n kiếm ph&aacute;i, liền bộ kiếm ph&aacute;p n&agrave;y khổ luyện năm năm chi l&acirc;u, cho n&ecirc;n mới c&oacute; thể đủ dựa v&agrave;o nhẹ nh&agrave;ng bộ ph&aacute;p c&ugrave;ng ngốc c&ocirc; cường đại man lực đối kh&aacute;ng.<br />\r\n<br />\r\nNhưng l&agrave;, mỗi một lần hắn nghĩ muốn ch&eacute;m về ph&iacute;a ngốc c&ocirc; yếu hại l&uacute;c, đều bị ngốc c&ocirc; Lang Nha Bổng ngăn cản trở về.<br />\r\n<br />\r\nKia cự lực chấn động, l&agrave;m cho Đường Kim thể nội kh&iacute; huyết cuồn cuộn kh&ocirc;ng ngừng, hắn t&acirc;m ch&igrave;m xuống, như l&agrave; lại ti&ecirc;́p tục như th&ecirc;́, kh&ocirc;ng ra mười chi&ecirc;u, hắn đều muốn bị ngốc c&ocirc; phản chấn trở về cự lực cho rung ra nội thương tới.<br />\r\n<br />\r\nĐường Kim ch&iacute;nh nhanh ch&oacute;ng tự hỏi, muốn thế n&agrave;o nhanh ch&oacute;ng t&igrave;m ra ngốc c&ocirc; sơ hở l&uacute;c, thủ hạ kia năm c&aacute;i phế vật gia ph&oacute; lại một bộ bị sợ mất mật bộ d&aacute;ng trốn qua tới.<br />\r\n<br />\r\n&quot;Tam c&ocirc;ng tử! Kh&ocirc;ng tốt! Đại c&ocirc;ng tử x&aacute;c chết v&ugrave;ng dậy. . .&quot;<br />\r\n<br />\r\n&quot;Đại c&ocirc;ng tử biến th&agrave;nh kh&ocirc;ng đầu &aacute;c quỷ, thật l&agrave; dọa người a! Ta nh&oacute;m nhanh ch&oacute;ng trốn đi!&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng đầu &aacute;c quỷ đến lấy mạng, nghe n&oacute;i l&agrave; nuốt chửng ch&iacute;n ng&agrave;n ch&iacute;n trăm ch&iacute;n mươi ch&iacute;n người đầu, mới bằng l&ograve;ng bỏ qua. Mau đ&agrave;o mạng nha!&quot;<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nNăm c&aacute;i n&ocirc; bộc một b&ecirc;n h&ocirc; h&agrave;o, một b&ecirc;n chạy l&ecirc;n m&atilde; đ&agrave;o mệnh đi.<br />\r\n<br />\r\nLiền mang theo vừa mới c&aacute;i kia bị ngốc c&ocirc; vứt qua một b&ecirc;n n&ocirc; bộc, cũng dọa đến trở mình l&ecirc;n ngựa nhanh ch&oacute;ng chạy trốn.<br />\r\n<br />\r\n&quot;S&aacute;u c&aacute;i phế vật!&quot;<br />\r\n<br />\r\nĐường Kim khịt mũi coi thường mắng, hắn c&oacute; thể kh&ocirc;ng tin tưởng c&aacute;i g&igrave; x&aacute;c chết v&ugrave;ng dậy c&ugrave;ng kh&ocirc;ng đầu &aacute;c quỷ c&acirc;u chuyện.<br />\r\n<br />\r\nBất qu&aacute;, hắn c&ograve;n l&agrave; nh&acirc;n cơ hội ngừng xuống c&ugrave;ng ngốc c&ocirc; tranh đấu, một c&aacute;i đi nhanh c&agrave;ng qua ngốc c&ocirc;, cầm trong tay bảo kiếm cẩn thận hướng lấy đại hố c&aacute;i kia qu&aacute; khứ, sau đ&oacute; cau m&agrave;y hướng xuống mặt thăm d&ograve; đầu.<br />\r\n<br />\r\nC&aacute;i n&agrave;y một nh&igrave;n. . .<br />\r\n<br />\r\nĐường Kim cũng lập tức to&agrave;n th&acirc;n sinh h&agrave;n, hố b&ecirc;n trong v&acirc;̣y mà thật l&agrave; ch&iacute;nh m&igrave;nh đại ca Đường X&aacute;n.<br />\r\n<br />\r\nKh&ocirc;ng đầu?</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nKh&ocirc;ng!<br />\r\n<br />\r\nHắn thật giống l&agrave;. . . Dựng ngược đ&acirc;m v&agrave;o trong đất.<br />\r\n<br />\r\nChẳng lẽ n&oacute;i, vừa mới ngốc c&ocirc; đ&atilde; đem Đường X&aacute;n từ quan t&agrave;i b&ecirc;n trong cho đ&agrave;o ra rồi?<br />\r\n<br />\r\nB&agrave;y th&agrave;nh c&aacute;i n&agrave;y một tư thế?<br />\r\n<br />\r\nKh&ocirc;ng kh&ocirc;ng kh&ocirc;ng!<br />\r\n<br />\r\nĐường Kim nội t&acirc;m run l&ecirc;n, mới vừa c&ugrave;ng ngốc c&ocirc; tranh đấu thời điểm, hắn r&otilde; r&agrave;ng cũng mơ hồ nghe đến từ hố b&ecirc;n trong truyền đến đại ca thanh &acirc;m.<br />\r\n<br />\r\nSẽ kh&ocirc;ng phải!<br />\r\n<br />\r\nĐường X&aacute;n thật x&aacute;c chết v&ugrave;ng dậy đi?<br />\r\n<br />\r\nC&oacute; l&uacute;c, người liền l&agrave; c&aacute;i n&agrave;y dạng!<br />\r\n<br />\r\nBan đầu kh&ocirc;ng thế n&agrave;o chuyện đ&aacute;ng sợ, c&oacute; thể d&ugrave;ng rất l&yacute; tr&iacute; đi suy nghĩ c&ugrave;ng ph&acirc;n t&iacute;ch hiện tượng, t&acirc;m l&yacute; mặt c&agrave;ng nghĩ th&igrave; c&agrave;ng sợ, c&agrave;ng sợ liền hội c&agrave;ng suy nghĩ mi&ecirc;n man.<br />\r\n<br />\r\nĐ&uacute;ng l&uacute;c l&uacute;c n&agrave;y, Đường X&aacute;n dựng ngược đầy ba ph&uacute;t, m&agrave;u xanh bug gi&acirc;y l&aacute;t ở giữa giải kh&oacute;a th&agrave;nh c&ocirc;ng.<br />\r\n<br />\r\nDựng ngược Đường X&aacute;n ngạc nhi&ecirc;n ph&aacute;t hiện, chỉ cần m&igrave;nh lực ch&uacute; &yacute; tập trung đến miệng, liền hội hiện ra từng đầu c&oacute; thể d&ugrave;ng hoạt động thanh tiến độ loại h&igrave;nh đồ vật tới.<br />\r\n<br />\r\n&quot;A? C&aacute;i n&agrave;y l&agrave; giải kh&oacute;a bug năng lực? Những n&agrave;y thanh tiến độ, d&ugrave;ng tới l&agrave;m g&igrave; a?&quot;<br />\r\n<br />\r\nLu&ocirc;n lu&ocirc;n ch&uacute; trọng động thủ năng lực Đường X&aacute;n, liền theo liền đem mấy c&aacute;i thanh tiến độ k&eacute;o đến lớn nhất, sau đ&oacute; ho khan một c&aacute;i, rất trịnh trọng hắn sự t&igrave;nh h&ocirc; to một tiếng: &quot;Cứu mạng a! C&oacute; hay kh&ocirc;ng người tới cứu ta đi l&ecirc;n a? Ta thật kh&ocirc;ng phải l&agrave; quỷ a! Ta c&ograve;n sống. . . Ta thật c&ograve;n c&oacute; thể d&ugrave;ng cấp cứu từng c&aacute;i. . .&quot;<br />\r\n<br />\r\nNhưng m&agrave;. . .<br />\r\n<br />\r\nĐường X&aacute;n gọi l&agrave; c&aacute;i n&agrave;y lời n&oacute;i, c&oacute; thể từ hắn miệng b&ecirc;n trong lại ph&aacute;t ra &acirc;m lượng to lớn, giọng điệu l&agrave; như quỷ k&ecirc;u một dạng tiếng rống tới.<br />\r\n<br />\r\nNgọa t&agrave;o!<br />\r\n<br />\r\nNguy&ecirc;n lai những n&agrave;y thanh tiến độ, c&oacute; ch&uacute;t l&agrave; khống chế Đường X&aacute;n tiếng &Acirc;m &Acirc;m lượng, c&oacute; ch&uacute;t l&agrave; khống chế hắn thanh &acirc;m giọng điệu a!<br />\r\n<br />\r\nĐổi một c&acirc;u n&oacute;i, Đường X&aacute;n giải kh&oacute;a ho&agrave;n to&agrave;n t&ugrave;y t&acirc;m sở dục khống chế c&ugrave;ng cường h&oacute;a ch&iacute;nh m&igrave;nh thanh &acirc;m &acirc;m thanh năng lực a!<br />\r\n<br />\r\n. . .</p>', '2021-11-27 21:07:46', 5, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(165, 46, 'Cái này thế giới thế nào khắp nơi đều là bug a?', '5', '<p>Giống như l&agrave; những kia điều &acirc;m sư sử dụng phần mềm đồng dạng, Đường X&aacute;n th&ocirc;ng qua k&eacute;o lấy những n&agrave;y thanh tiến độ, l&yacute; luận c&oacute; thể d&ugrave;ng để ch&iacute;nh m&igrave;nh m&ocirc; phỏng đảm nhiệm người n&agrave;o thanh &acirc;m tới.<br />\r\n<br />\r\nM&agrave; lại, &acirc;m lượng điều đến lớn nhất, cũng tương đương tại tiếp một c&aacute;i cự đại loa ph&oacute;ng thanh.<br />\r\n<br />\r\nChỉ bất qu&aacute;. . .<br />\r\n<br />\r\nVừa giải kh&oacute;a c&aacute;i n&agrave;y bug Đường X&aacute;n, căn bản c&ograve;n kh&ocirc;ng biết r&otilde; l&agrave; chuyện g&igrave; xảy ra, liền đem &acirc;m lượng khống chế c&ugrave;ng &acirc;m điệu trượt ch&acirc;n lớn nhất, kết quả ph&aacute;t ra c&aacute;i n&agrave;y khủng bố quỷ k&ecirc;u tiếng tới.<br />\r\n<br />\r\nBan đầu, rừng n&uacute;i hoang vắng c&aacute;i n&agrave;y hống v&agrave;i tiếng, cũng sẽ kh&ocirc;ng tạo th&agrave;nh nhiều đến ảnh hưởng.<br />\r\n<br />\r\nC&oacute; thể l&agrave; hết lần n&agrave;y tới lần kh&aacute;c. . .<br />\r\n<br />\r\nC&aacute;i n&agrave;y l&uacute;c Đường gia tam c&ocirc;ng tử Đường Kim, lại ch&iacute;nh vội v&atilde; cuống cuồng gh&eacute; v&agrave;o b&ecirc;n cạnh c&aacute;i hố lớn nghi thần nghi quỷ h&ugrave; dọa ch&iacute;nh m&igrave;nh, bị Đường X&aacute;n quỷ k&ecirc;u c&aacute;i n&agrave;y nhất hống. . .<br />\r\n<br />\r\nĐường Kim lập tức cũng bị dọa đến hoang mang lo sợ, th&acirc;n thể về sau một ng&atilde;, nhanh ch&oacute;ng xoay người chạy, một b&ecirc;n chạy c&ograve;n một b&ecirc;n k&ecirc;u l&ecirc;n: &quot;Đại ca! Oan c&oacute; đầu nợ c&oacute; chủ, kh&ocirc;ng phải ta hại ngươi, ngươi đừng tới t&igrave;m ta a. . .&quot;<br />\r\n<br />\r\nĐừng nh&igrave;n Đường Kim th&agrave;nh thi&ecirc;n một bộ thiếu ni&ecirc;n l&atilde;o th&agrave;nh rất trang bức bộ d&aacute;ng, c&aacute;i n&agrave;y nhất khắc lại l&agrave; thật bị Đường X&aacute;n một tiếng Quỷ Hống cho dọa cho bể mật gần chết, c&aacute;i g&igrave; h&igrave;nh tượng đều cố kh&ocirc;ng l&ecirc;n, cưỡi l&ecirc;n ngựa liền điều khiển bỏ trốn mất dạng.<br />\r\n<br />\r\n&quot;Ngọa t&agrave;o! Kh&ocirc;ng phải chứ? Tất cả. . . To&agrave;n bộ cho ta dọa chạy rồi?&quot;<br />\r\n<br />\r\nRất hiển nhi&ecirc;n, Đường X&aacute;n vừa mới cũng bị ch&iacute;nh m&igrave;nh c&aacute;i n&agrave;y một tiếng Quỷ Hống bị dọa cho ph&aacute;t sợ, hắn liền nhấn một c&aacute;i thanh &acirc;m điều chỉnh thanh tiến độ b&ecirc;n cạnh một c&aacute;i &quot;Trở lại vị tr&iacute; cũ&quot; kh&oacute;a, t&iacute;nh l&agrave; nhất kiện ho&agrave;n nguy&ecirc;n th&agrave;nh ch&iacute;nh m&igrave;nh thanh &acirc;m.<br />\r\n<br />\r\nC&aacute;i n&agrave;y một lần, Đường X&aacute;n l&agrave; thật t&acirc;m kh&oacute;c kh&ocirc;ng ra nước mắt, hắn vốn nghĩ giải kh&oacute;a m&agrave;u xanh bug, c&oacute; thể gi&uacute;p ch&iacute;nh m&igrave;nh rời đi c&aacute;i hố to n&agrave;y.<br />\r\n<br />\r\nLại kh&ocirc;ng nghĩ rằng, kh&ocirc;ng cẩn thận ngược lại đem ph&iacute;a tr&ecirc;n người to&agrave;n cho dọa chạy.<br />\r\n<br />\r\n&quot;A? C&aacute;c loại! Vừa mới v&acirc;y quanh đại hố, một ch&uacute;t l&agrave; Đường gia gia n&ocirc;, c&oacute; một c&aacute;i l&agrave;. . . Ta tam đệ Đường Kim. Kia. . . N&oacute;i minh cưỡi ngựa đến những kia người kh&ocirc;ng phải quan phủ, m&agrave; l&agrave; Đường Kim c&ugrave;ng gia n&ocirc;. Kia. . . C&aacute;i kia đ&agrave;o hố trộm mộ đ&acirc;u?&quot;<br />\r\n<br />\r\nĐường X&aacute;n trong l&ograve;ng d&acirc;ng l&ecirc;n một ch&uacute;t hi vọng sống, như l&agrave; c&aacute;i kia trộm mộ c&ograve;n tại bốn ph&iacute;a, ch&iacute;nh m&igrave;nh hẳn l&agrave; c&ograve;n c&oacute; cứu.<br />\r\n<br />\r\nChỉ bất qu&aacute;, c&aacute;i n&agrave;y trộm mộ khẳng định bị Đường Kim đ&aacute;m người cho đuổi đi hoặc l&agrave; dọa chạy, c&ograve;n hội trở lại?<br />\r\n<br />\r\nCh&iacute;nh l&agrave;m Đường X&aacute;n nghĩ, c&oacute; phải hay kh&ocirc;ng muốn d&ugrave;ng c&aacute;i n&agrave;y biến thanh năng lực, l&agrave;m c&aacute;i la lỵ &acirc;m đến c&acirc;u dẫn trộm mộ l&uacute;c, ph&iacute;a tr&ecirc;n đại hố xuất hiện một c&aacute;i tr&ograve;n trịa b&aacute;nh bao mặt.<br />\r\n<br />\r\n&quot;Ngọa t&agrave;o! Dọa ta một hồi!&quot;<br />\r\n<br />\r\nĐường X&aacute;n tập trung nh&igrave;n v&agrave;o, mới nhận ra được, &quot;Ngốc. . . Ngốc c&ocirc;? Ngươi l&agrave; ngốc c&ocirc;? Nguy&ecirc;n lai l&agrave; ngươi. . . Đến đ&agrave;o ta sao?&quot;<br />\r\n<br />\r\nLục so&aacute;t nguy&ecirc;n lai c&aacute;i n&agrave;y cỗ th&acirc;n thể k&yacute; ức, Đường X&aacute;n rất nhanh liền nhận ra ngốc c&ocirc;.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nPhanh một c&aacute;i!<br />\r\n<br />\r\nNgốc c&ocirc; c&oacute; thể một ch&uacute;t cũng kh&ocirc;ng sợ Đường X&aacute;n, n&agrave;ng từ ph&iacute;a tr&ecirc;n bỗng nhi&ecirc;n một lần nhảy xuống tới, th&acirc;n thể cường tr&aacute;ng, ph&aacute;t đạt cơ thịt, đều để Đường X&aacute;n tr&aacute;i tim nhỏ nhịn kh&ocirc;ng được kia khẽ run l&ecirc;n.<br />\r\n<br />\r\nKh&ocirc;ng n&oacute;i hai lời, ngốc c&ocirc; một c&aacute;i tay bắt lấy Đường X&aacute;n cổ &aacute;o, sau đ&oacute; lại đi nhảy một c&aacute;i.<br />\r\n<br />\r\nS&acirc;u bảy t&aacute;m thước đại hố a!<br />\r\n<br />\r\nNgốc c&ocirc; c&ograve;n mang theo Đường X&aacute;n, đều c&oacute; thể d&ugrave;ng bỗng nhi&ecirc;n một lần nhảy ra.<br />\r\n<br />\r\nC&aacute;i n&agrave;y ni m&atilde; thật l&agrave;. . . Thi&ecirc;n ph&uacute; dị bẩm.<br />\r\n<br />\r\nĐập đập bổ nh&agrave;o. . .<br />\r\n<br />\r\nĐường X&aacute;n vỗ vỗ to&agrave;n th&acirc;n b&ugrave;n đất, c&aacute;i n&agrave;y mới phản ứng được, nh&igrave;n trước mắt c&aacute;i n&agrave;y cường tr&aacute;ng đến c&oacute; ch&uacute;t qu&aacute; phận sửu nữ nh&acirc;n, nửa ng&agrave;y mới phun ra mấy chữ: &quot;Ngốc c&ocirc;! Tạ ơn ngươi. . . Cứu ta. . .&quot;<br />\r\n<br />\r\n&quot;Đại ngốc tử! Ngươi đ&atilde; cứu ta. M&agrave; lại, ngươi n&oacute;i qua muốn cưới ta l&agrave;m nương tử.&quot;<br />\r\n<br />\r\nNgốc c&ocirc; n&oacute;i đến rất lẽ thẳng kh&iacute; h&ugrave;ng, kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o c&ocirc; nương gia ngượng ng&ugrave;ng, c&oacute; lẽ n&oacute;i. . . Ngốc c&ocirc; căn bản cũng kh&ocirc;ng biết ngượng ng&ugrave;ng l&agrave; vật g&igrave;.<br />\r\n<br />\r\n&quot;A? Ta. . . Ta c&oacute; n&oacute;i qua?&quot;<br />\r\n<br />\r\nSửng sốt một ch&uacute;t, Đường X&aacute;n mới từ k&yacute; ức b&ecirc;n trong t&igrave;m thấy được từng m&agrave;n c&ugrave;ng ngốc c&ocirc; c&ugrave;ng một chỗ h&igrave;nh ảnh.<br />\r\n<br />\r\nNguy&ecirc;n lai. . .<br />\r\n<br />\r\nNgốc c&ocirc; l&uacute;c trước bị ch&eacute;m bị trọng thương về sau, liền dọc đường hướng ngo&agrave;i th&agrave;nh trốn đi.<br />\r\n<br />\r\nNhưng l&agrave; nửa đường c&ograve;n l&agrave; mất m&aacute;u qu&aacute; nhiều, t&eacute; xỉu tại v&ugrave;ng ngoại &ocirc; tr&ecirc;n hoang d&atilde;.<br />\r\n<br />\r\nCh&iacute;nh tốt l&uacute;c n&agrave;y, Đường gia đại c&ocirc;ng tử Đường X&aacute;n ở ngo&agrave;i th&agrave;nh T&acirc;y Giao gia tộc b&atilde;i săn kia chủng h&iacute; chơi, ch&oacute; săn nghe đến huyết tinh vị dọc đường mang theo Đường X&aacute;n t&igrave;m tới bị thương nặng h&ocirc;n m&ecirc; ngốc c&ocirc;.<br />\r\n<br />\r\nĐường X&aacute;n liền đem ngốc c&ocirc; cho mang về trang vi&ecirc;n dưỡng thương, ở giữa thời gian mấy th&aacute;ng, trừ một ch&uacute;t tất yếu trị liệu trọng thương thảo dược b&ecirc;n ngo&agrave;i, ngốc c&ocirc; cơ hồ ho&agrave;n to&agrave;n l&agrave; dựa v&agrave;o tự th&acirc;n cường đại tự l&agrave;nh năng lực, kh&ocirc;ng chỉ kh&ocirc;i phục như ban đầu, m&agrave; lại lực kh&iacute; c&ograve;n c&agrave;ng lớn.<br />\r\n<br />\r\nĐồng thời, tại dưỡng thương trong thời gian n&agrave;y, người xưng &quot;Đại ngốc tử&quot; Đường X&aacute;n, liền c&ugrave;ng ngốc c&ocirc; kết xuống th&acirc;m hậu t&igrave;nh nghĩa.<br />\r\n<br />\r\nThậm ch&iacute; Đường X&aacute;n c&ograve;n chủ động đề xuất muốn cưới ngốc c&ocirc; vi nương tử lời n&oacute;i đến, thật giống như. . . Nhỏ thời điểm c&ugrave;ng nhau chơi nh&agrave; ch&ograve;i rượu nam h&agrave;i nữ h&agrave;i kia dạng, t&igrave;nh đầu &yacute; hợp ph&iacute;a dưới liền rất tự nhi&ecirc;n n&oacute;i ra.<br />\r\n<br />\r\nBất qu&aacute;, Đường X&aacute;n lục so&aacute;t nguy&ecirc;n lai k&yacute; ức b&ecirc;n trong, lại c&oacute; một c&aacute;i ph&aacute;t hiện kinh người.<br />\r\n<br />\r\nKia ch&iacute;nh l&agrave;. . .<br />\r\n<br />\r\nTại Đường X&aacute;n nguy&ecirc;n lai k&yacute; ức b&ecirc;n trong, ngốc c&ocirc; một ch&uacute;t cũng kh&ocirc;ng xấu, thậm ch&iacute; c&ograve;n. . . Như thi&ecirc;n ti&ecirc;n.<br />\r\n<br />\r\nĐ&uacute;ng!<br />\r\n<br />\r\nLiền l&agrave; xinh đẹp đến kh&ocirc;ng g&igrave; s&aacute;nh được, tr&iacute; nhớ mơ hồ b&ecirc;n trong, tựa hồ c&aacute;i n&agrave;y ngốc c&ocirc; c&oacute; hai c&aacute;i gương mặt bộ dạng, một bộ liền l&agrave; hiện tại c&aacute;i n&agrave;y mặt đại đại, c&ograve;n c&oacute; một ch&uacute;t mặt rỗ, th&acirc;n thể phi thường tr&aacute;ng bộ dạng.<br />\r\n<br />\r\nM&agrave; kh&aacute;c một bộ như ẩn như hiện, l&agrave; như thi&ecirc;n ti&ecirc;n một dạng d&aacute;ng người c&ugrave;ng gương mặt, cũng l&agrave; thật s&acirc;u ấn khắc tại Đường X&aacute;n k&yacute; ức b&ecirc;n trong.<br />\r\n<br />\r\n&quot;Ta đi! Chẳng lẽ n&oacute;i, c&aacute;i n&agrave;y l&agrave; trong mắt người t&igrave;nh biến th&agrave;nh T&acirc;y Thi? Tại nguy&ecirc;n lai Đường X&aacute;n k&yacute; ức b&ecirc;n trong ngốc c&ocirc;, h&igrave;nh tượng bị n&atilde;o bổ th&agrave;nh đẹp như vậy?&quot;<br />\r\n<br />\r\nĐối mặt ngốc c&ocirc; &aacute;nh mắt chất vấn, Đường X&aacute;n hơi hơi ti&ecirc;u h&oacute;a một lần k&yacute; ức về sau, c&oacute; điểm chật vật gật đầu n&oacute;i: &quot;V&acirc;ng! Ta n&oacute;i qua c&aacute;i n&agrave;y lời n&oacute;i, ngốc c&ocirc;, ta sẽ lấy ngươi l&agrave;m nương tử.&quot;<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; biện ph&aacute;p!<br />\r\n<br />\r\nCh&iacute;nh m&igrave;nh đ&atilde; đ&aacute;p ứng sự t&igrave;nh, ngậm lấy lệ cũng phải thực hiện a!<br />\r\n<br />\r\nHuống chi, h&ocirc;m nay nếu kh&ocirc;ng phải l&agrave; người gia ngốc c&ocirc; đến đ&agrave;o ngươi mộ phần, c&ograve;n n&oacute;i kh&ocirc;ng chắc c&oacute; một ng&agrave;y mới c&oacute; thể lại thấy &aacute;nh mặt trời đ&acirc;u!<br />\r\n<br />\r\n&quot;Tốt! Ta sẽ chờ ngươi.&quot;<br />\r\n<br />\r\nTựa hồ liền v&igrave; c&aacute;c loại Đường X&aacute;n c&aacute;i n&agrave;y lời n&oacute;i, ngốc c&ocirc; nghe đến sau liền vừa l&ograve;ng thỏa &yacute;, quay người liền phi thường &quot;V&ocirc; t&igrave;nh&quot; t&iacute;nh to&aacute;n rời đi.<br />\r\n<br />\r\nC&oacute; thể hiện tại đ&atilde; l&agrave; ho&agrave;ng h&ocirc;n thời khắc. . .<br />\r\n<br />\r\nTrời lập tức liền đen a!<br />\r\n<br />\r\nC&aacute;i n&agrave;y rừng n&uacute;i hoang vắng, Đường X&aacute;n một c&aacute;i hiện đại trạch nam, c&oacute; thể kh&ocirc;ng c&oacute; Bear hoang d&atilde; cầu sinh bản sự.<br />\r\n<br />\r\nLại n&oacute;i, Đường X&aacute;n nghĩ muốn về Đường gia, c&oacute; thể l&agrave; liền Kim Lăng th&agrave;nh ở phương hướng n&agrave;o đều kh&ocirc;ng biết đến.<br />\r\n<br />\r\nCho n&ecirc;n, hắn nhanh ch&oacute;ng gọi lại nghĩ muốn &quot;R&uacute;t treo v&ocirc; t&igrave;nh&quot; ngốc c&ocirc;, n&oacute;i: &quot;Uy uy uy! Ngốc c&ocirc;, ngươi đừng đi một m&igrave;nh a! C&oacute; thể hay kh&ocirc;ng mang ta c&ugrave;ng nhau về Kim Lăng th&agrave;nh, ta t&igrave;m kh&ocirc;ng thấy đường về nh&agrave;. . .&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nNgốc c&ocirc; hồ nghi nh&igrave;n một lần Đường X&aacute;n, tựa như đang tự hỏi c&aacute;i g&igrave;, sau đ&oacute; mới gật đầu đ&aacute;p ứng : &quot;Tốt!&quot;<br />\r\n<br />\r\nN&oacute;i xong lời này về sau, Đường X&aacute;n liền thời gian phản ứng đều kh&ocirc;ng c&oacute;, liền bị ngốc c&ocirc; trực tiếp kẹp ở dưới c&aacute;nh tay, sau đ&oacute; nhanh ch&oacute;ng Kim Lăng th&agrave;nh phương hướng chạy như bay, đúng. . . Liền l&agrave; d&ugrave;ng mỗi giờ s&aacute;u mươi km cái chủng loại kia tốc độ. . .<br />\r\n<br />\r\n&quot;Ngừng ngừng ngừng! Ngốc c&ocirc;. . . Ngừng một ch&uacute;t, ta say xe. . . Ta muốn n&ocirc;n!&quot;<br />\r\n<br />\r\nChạy mới mười ph&uacute;t, Đường X&aacute;n lại kh&ocirc;ng được, bởi v&igrave; ngốc c&ocirc; kẹp chặt qu&aacute; gấp, tốc độ cũng qu&aacute; nhanh.<br />\r\n<br />\r\nL&agrave;m ngốc c&ocirc; tại một chỗ c&aacute;nh đồng b&ecirc;n cạnh dừng lại về sau, Đường X&aacute;n liền kh&ocirc;ng c&aacute;ch n&agrave;o khống chế tại ven đường bắt đầu &oacute;i ra.<br />\r\n<br />\r\nHắn ban đầu l&agrave; kh&ocirc;ng say xe, nhưng m&agrave; c&aacute;i n&agrave;y dạng bị đại c&aacute;nh tay kẹp lấy chạy như đi&ecirc;n, c&aacute;i n&agrave;y người n&agrave;o chịu nổi a!<br />\r\n<br />\r\n&quot;Ọe!&quot;<br />\r\n<br />\r\nĐường X&aacute;n đem bụng b&ecirc;n trong ăn Orleans Hambuger g&agrave; nướng đều cho phun ra, sau đ&oacute; vỗ vỗ ngực của m&igrave;nh, tr&igrave; ho&atilde;n thở ra một hơi vừa định c&ugrave;ng ngốc c&ocirc; đề một ch&uacute;t c&oacute; t&iacute;nh kiến thiết &yacute; kiến l&uacute;c, lại bị trước mặt nh&igrave;n đến mấy chỗ bug &aacute;nh s&aacute;ng cho kinh sợ.<br />\r\n<br />\r\nM&agrave;u đỏ &aacute;nh s&aacute;ng hai chỗ!<br />\r\n<br />\r\nM&agrave;u cam &aacute;nh s&aacute;ng một chỗ!<br />\r\n<br />\r\nMấu chốt l&agrave;. . . C&ograve;n c&oacute; một chỗ m&agrave;u v&agrave;ng đẳng cấp &aacute;nh s&aacute;ng!<br />\r\n<br />\r\nNgọa t&agrave;o!<br />\r\n<br />\r\nThế n&agrave;o khắp nơi đều l&agrave; bug a?<br />\r\n<br />\r\nĐường X&aacute;n tại tr&ecirc;n Địa Cầu, thời gian hai ba năm, tổng cộng ph&aacute;t hiện bug cộng lại cũng kh&ocirc;ng đến hai mươi c&aacute;i, m&agrave; lại tr&ecirc;n cơ bản đều l&agrave; đẳng cấp thấp nhất m&agrave;u đỏ &aacute;nh s&aacute;ng, đ&acirc;y l&agrave; Đường X&aacute;n khắp nơi c&oacute; mục đ&iacute;ch t&igrave;m kiếm đến.<br />\r\n<br />\r\nC&oacute; thể l&agrave; xuy&ecirc;n qua đến c&aacute;i n&agrave;y c&aacute;i thế giới về sau, c&aacute;i n&agrave;y mới mấy ng&agrave;y thời gian a!<br />\r\n<br />\r\nĐường X&aacute;n đ&atilde; ph&aacute;t hiện nhiều như vậy bug, cảm gi&aacute;c n&agrave;y thật giống như. . . C&aacute;i n&agrave;y thế giới khắp nơi đều l&agrave; bug bộ dạng a!<br />\r\n<br />\r\n. . .</p>', '2021-11-27 21:08:05', 5, 1),
(166, 44, 'Ta Tiệt Giáo sẽ đại bại', '2', '<p>Phong Thần đại kiếp?<br />\r\n<br />\r\nL&ecirc;n bảng l&ecirc;n bảng?<br />\r\n<br />\r\nTrấn &aacute;p trấn &aacute;p?<br />\r\n<br />\r\nChẳng lẽ tại c&aacute;i kia Phong Thần đại kiếp b&ecirc;n trong, Tiệt Gi&aacute;o sẽ thụ trọng thương?<br />\r\n<br />\r\nC&aacute;i n&agrave;y tiểu ngưu y&ecirc;u lại c&ograve;n muốn chạy trốn?<br />\r\n<br />\r\nMuốn tho&aacute;t ly hắn Tiệt Gi&aacute;o! ! !<br />\r\n<br />\r\nĐang nghe Phong Thần lượng kiếp thời điểm, Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ từ nơi s&acirc;u xa h&igrave;nh như c&oacute; cảm ứng.<br />\r\n<br />\r\nTh&acirc;n v&igrave; Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n hắn, tho&aacute;ng th&ocirc;i to&aacute;n một phen, nhất thời trong l&ograve;ng kinh h&atilde;i.<br />\r\n<br />\r\nVu Y&ecirc;u lượng kiếp về sau, coi l&agrave; thật v&igrave; Phong Thần đại kiếp!<br />\r\n<br />\r\nBất qu&aacute; lấy th&acirc;n phận của hắn, cũng vẻn vẹn biết được sẽ c&oacute; Phong Thần lượng kiếp, c&ograve;n lại đều kh&ocirc;ng biết.<br />\r\n<br />\r\nL&uacute;c n&agrave;y hắn thế m&agrave; theo một c&aacute;i tiểu ngưu y&ecirc;u tiếng l&ograve;ng b&ecirc;n trong, biết được tương lai đại kiếp.<br />\r\n<br />\r\nChuyện thế n&agrave;y, ngoại trừ Đạo Tổ Hồng Qu&acirc;n c&ugrave;ng bọn họ mấy vị Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n, người n&agrave;o c&oacute; thể biết được?<br />\r\n<br />\r\n&quot;Cổ qu&aacute;i! Cổ qu&aacute;i!&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y tiểu ngưu y&ecirc;u c&oacute; ch&uacute;t &yacute; tứ.&quot;<br />\r\n<br />\r\nThần niệm bao phủ Trường Phong Th&ocirc;ng Thi&ecirc;n, trong mắt mang theo ngạc nhi&ecirc;n.<br />\r\n<br />\r\nBọn họ mấy vị Th&aacute;nh Nh&acirc;n nguy&ecirc;n thần k&yacute; th&aacute;c tại Thi&ecirc;n Đạo, bất tử bất diệt, mới c&oacute; thể th&ocirc;i to&aacute;n ra.<br />\r\n<br />\r\nTiểu ngưu y&ecirc;u bất qu&aacute; Thi&ecirc;n Ti&ecirc;n cảnh, v&igrave; sao lại c&oacute; bực n&agrave;y bản sự?<br />\r\n<br />\r\nĐến mức đối phương n&oacute;i Tam Ti&ecirc;u bị trấn &aacute;p, Tiệt Gi&aacute;o trọng thương sự t&igrave;nh, Th&ocirc;ng Thi&ecirc;n lại kh&ocirc;ng để ở trong l&ograve;ng.<br />\r\n<br />\r\nL&uacute;c n&agrave;y Tiệt Gi&aacute;o kh&iacute; vận k&eacute;o d&agrave;i, m&ocirc;n nh&acirc;n, đệ tử tu luyện thần tốc, r&otilde; r&agrave;ng một ph&aacute;i vui vẻ phồn vinh chi thế.<br />\r\n<br />\r\nM&agrave; lại Huyền M&ocirc;n b&ecirc;n trong trừ hắn Tiệt Gi&aacute;o, c&ograve;n c&oacute; L&atilde;o Tử c&ugrave;ng Nguy&ecirc;n Thủy hai vị Th&aacute;nh Nh&acirc;n.<br />\r\n<br />\r\nBọn họ tuy nhi&ecirc;n gi&aacute;o nghĩa c&oacute; chỗ kh&aacute;c nhau, nhưng &#39;Hồng hoa bạch ngẫu thanh h&agrave; diệp, tam gi&aacute;o vốn l&agrave; một nh&agrave; &#39;, tr&aacute;i phải r&otilde; r&agrave;ng trước mặt, Tam Thanh khẳng định mặt trận thống nhất.<br />\r\n<br />\r\nNgười n&agrave;o d&aacute;m c&ugrave;ng bọn hắn đối địch!<br />\r\n<br />\r\nNữ Oa?<br />\r\n<br />\r\nĐối phương tĩnh lui Oa Ho&agrave;ng cung, liền Y&ecirc;u tộc, Nh&acirc;n tộc cũng kh&ocirc;ng để &yacute;, đương nhi&ecirc;n sẽ kh&ocirc;ng c&oacute; tranh đấu.<br />\r\n<br />\r\nĐến mức phương t&acirc;y Tiếp Dẫn, Chuẩn Đề hai người?<br />\r\n<br />\r\nTay cầm Tru Ti&ecirc;n Kiếm Trận hắn, kh&ocirc;ng phải tứ th&aacute;nh kh&ocirc;ng thể ph&aacute;.<br />\r\n<br />\r\nNếu l&agrave; d&aacute;m đến, c&ograve;n kh&ocirc;ng cố gắng dạy bọn họ l&agrave;m người.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nNghĩ như thế, tiểu ngưu y&ecirc;u suy nghĩ thật sự l&agrave; hoang đường.<br />\r\n<br />\r\nCăn bản kh&ocirc;ng đủ đ&ecirc;̉ tin!<br />\r\n<br />\r\n&quot;Đ&acirc;y bất qu&aacute; l&agrave; tiểu ngưu y&ecirc;u suy nghĩ trong l&ograve;ng, ta tại c&aacute;i n&agrave;y th&ocirc;i to&aacute;n c&aacute;i g&igrave;.&quot;<br />\r\n<br />\r\n&quot;Tu luyện nhiều như vậy năm th&aacute;ng, kh&ocirc;ng nghĩ tới bị một c&aacute;i tiểu ngưu y&ecirc;u cho ảnh hưởng tới.&quot;<br />\r\n<br />\r\nVứt bỏ trong đầu tạp nhạp &yacute; nghĩ, Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ trong l&ograve;ng c&oacute; ch&uacute;t kinh ngạc.<br />\r\n<br />\r\nHắn v&igrave; Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n, v&acirc;̣y mà v&igrave; một con tiểu ngưu y&ecirc;u lấy tướng.<br />\r\n<br />\r\nBất qu&aacute; bởi v&igrave; c&aacute;i n&agrave;y một tin tức, đối với c&aacute;i n&agrave;y tiểu ngưu y&ecirc;u cũng c&agrave;ng th&ecirc;m hiếu kỳ.<br />\r\n<br />\r\nTừ nơi s&acirc;u xa c&oacute; thể bắt được tương lai một g&oacute;c, coi l&agrave; đại kh&iacute; vận người.<br />\r\n<br />\r\nThời gian ng&agrave;n năm, tu luyện tới Thi&ecirc;n Ti&ecirc;n cảnh giới, tuy nhi&ecirc;n chưa n&oacute;i tới thi&ecirc;n ph&uacute; mạnh bao nhi&ecirc;u, nhưng c&oacute; năng lực n&agrave;y, cũng l&agrave; được trời ưu &aacute;i.<br />\r\n<br />\r\nKh&ocirc;ng nghĩ tới lần n&agrave;y giảng đạo, v&acirc;̣y mà ph&aacute;t hiện như thế một c&aacute;i c&oacute; tiềm lực m&ocirc;n nh&acirc;n.<br />\r\n<br />\r\n【 c&aacute;i n&agrave;y đầu tr&acirc;u mặt ngựa gia hỏa, hẳn l&agrave; Trường Nhĩ Định Quang Ti&ecirc;n, tướng do t&acirc;m sinh quả nhi&ecirc;n kh&ocirc;ng sai. 】<br />\r\n<br />\r\n【 muốn kh&ocirc;ng phải gia hỏa n&agrave;y tại Vạn Ti&ecirc;n đại trận b&ecirc;n trong phản bội sư t&ocirc;n, đem Lục Hồn Phi&ecirc;n trộm đi, hiến cho Nguy&ecirc;n Thủy Thi&ecirc;n T&ocirc;n, Tiệt Gi&aacute;o l&agrave;m sao lại bại. 】<br />\r\n<br />\r\nTh&acirc;n ở Hồng Hoang b&ecirc;n trong, Trường Phong tự nhi&ecirc;n r&otilde; r&agrave;ng Th&aacute;nh Nh&acirc;n m&ocirc;n đồ chỗ tốt.<br />\r\n<br />\r\nĐại thụ dưới đ&aacute;y tốt h&oacute;ng m&aacute;t!<br />\r\n<br />\r\nL&uacute;c n&agrave;y tuy nhi&ecirc;n Vu Y&ecirc;u lượng kiếp đ&atilde; qua, nhưng Hồng Hoang đồng dạng kh&ocirc;ng an to&agrave;n.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; trải qua Tam Ho&agrave;ng Ngũ Đế, Nh&acirc;n tộc c&ograve;n kh&ocirc;ng c&oacute; triệt để quật khởi.<br />\r\n<br />\r\nTo&agrave;n bộ Hồng Hoang đại địa, vẫn như cũ lưu lại đ&ocirc;ng đảo Y&ecirc;u tộc, Vu tộc, vẫn l&agrave; rất nguy hiểm.<br />\r\n<br />\r\nTr&ecirc;n th&acirc;n treo Th&aacute;nh Nh&acirc;n m&ocirc;n đồ bảng hiệu, tr&ecirc;n cơ bản vạn sự kh&ocirc;ng lo, đặc biệt l&agrave; giống Trường Phong dạng n&agrave;y Y&ecirc;u tộc.<br />\r\n<br />\r\nSẽ kh&ocirc;ng bị người mạo muội bắt đi l&agrave;m th&uacute; cưỡi, c&agrave;ng kh&ocirc;ng người d&aacute;m tuỳ tiện đ&aacute;nh giết.<br />\r\n<br />\r\nM&agrave; lại đối với Tiệt Gi&aacute;o, Trường Phong vẫn l&agrave; v&ocirc; c&ugrave;ng ưa th&iacute;ch.<br />\r\n<br />\r\nTiệt Gi&aacute;o m&ocirc;n nh&acirc;n ph&oacute;ng kho&aacute;ng, ngay thẳng, d&aacute;m nghĩ d&aacute;m l&agrave;m, kỳ thật thật hợp hắn khẩu vị.<br />\r\n<br />\r\nQuan trọng c&ograve;n c&oacute; Tam Ti&ecirc;u bực n&agrave;y Hồng Hoang c&oacute; t&ecirc;n ti&ecirc;n tử, chỉ l&agrave; kh&ocirc;ng biết sao kết cục c&oacute; chút khổ cực.<br />\r\n<br />\r\nNếu l&agrave; kh&ocirc;ng c&oacute; Phong Thần đại kiếp, nhất định &ocirc;m chặt Tiệt Gi&aacute;o c&acirc;y to n&agrave;y.<br />\r\n<br />\r\nMột luồng thần niệm đặt ở Trường Phong tr&ecirc;n người Th&ocirc;ng Thi&ecirc;n, trước ti&ecirc;n liền nghe đến.<br />\r\n<br />\r\nVốn l&agrave; ngồi ngay ngắn bồ đo&agrave;n b&ecirc;n tr&ecirc;n hắn, th&acirc;n h&igrave;nh hơi chấn động một ch&uacute;t.<br />\r\n<br />\r\nTrường Nhĩ Định Quang Ti&ecirc;n hắn tự nhi&ecirc;n sẽ hiểu l&agrave; ai.<br />\r\n<br />\r\nCh&iacute;nh l&agrave; hắn t&ugrave;y thị bảy ti&ecirc;n một trong, thuần hậu bản phận, đối với hắn cung cung k&iacute;nh k&iacute;nh, rất được hắn hậu &aacute;i.<br />\r\n<br />\r\nGia hỏa n&agrave;y sẽ ở Phong Thần lượng kiếp b&ecirc;n trong phản bội hắn?<br />\r\n<br />\r\nN&oacute;i thật, Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ c&oacute; chút kh&ocirc;ng tin.<br />\r\n<br />\r\nNhưng l&agrave; nội dung ph&iacute;a sau, lại l&agrave;m cho hắn ngồi kh&ocirc;ng y&ecirc;n.<br />\r\n<br />\r\nLục Hồn Phi&ecirc;n ch&iacute;nh l&agrave; Tử Ti&ecirc;u cung b&ecirc;n trong Đạo Tổ ban tặng, tiểu ngưu y&ecirc;u c&oacute; chỗ nghe n&oacute;i th&igrave; cũng th&ocirc;i đi.<br />\r\n<br />\r\nC&oacute; thể Vạn Ti&ecirc;n đại trận sự t&igrave;nh, hắn cũng chỉ l&agrave; vừa nh&igrave;n thấy Tiệt Gi&aacute;o thịnh huống như thế, trong đầu mới sinh ra luyện chế trận n&agrave;y &yacute; nghĩ.<br />\r\n<br />\r\nĐều c&ograve;n chưa kịp bắt đầu luyện chế, c&aacute;i n&agrave;y tiểu ngưu y&ecirc;u lại nhưng đ&atilde; sớm biết được.<br />\r\n<br />\r\nR&otilde; r&agrave;ng đến đ&oacute;n lấy lượng kiếp coi như xong, liền hắn c&aacute;i n&agrave;y Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n tr&ecirc;n người tin tức đều biết.<br />\r\n<br />\r\nChẳng lẽ c&aacute;i n&agrave;y tiểu ngưu y&ecirc;u thật c&oacute; thể hiểu r&otilde; tương lai?<br />\r\n<br />\r\nChủ yếu hơn ch&iacute;nh l&agrave;, đối phương tiếng l&ograve;ng b&ecirc;n trong để lộ ra một c&aacute;i tin tức trọng yếu.<br />\r\n<br />\r\n&#39;Trường Nhĩ Định Quang Ti&ecirc;n đem Lục Hồn Phi&ecirc;n hiến tặng cho Nguy&ecirc;n Thủy Thi&ecirc;n T&ocirc;n!&#39;<br />\r\n<br />\r\nHiến cho Nguy&ecirc;n Thủy Thi&ecirc;n T&ocirc;n! ! !<br />\r\n<br />\r\nTại sao muốn hiến cho Nguy&ecirc;n Thủy Thi&ecirc;n T&ocirc;n?<br />\r\n<br />\r\nChẳng lẽ Phong Thần lượng kiếp l&agrave; bọn họ Xiển Tiệt nhị gi&aacute;o chi tranh?<br />\r\n<br />\r\nNguy&ecirc;n Thủy sẽ c&ugrave;ng hắn đ&aacute;nh nhau?<br />\r\n<br />\r\nBởi v&igrave; gi&aacute;o nghĩa quan hệ, Tam Thanh t&aacute;ch ra, nhưng bọn hắn nguy&ecirc;n bản l&agrave; một thể, như thế n&agrave;o tương s&aacute;t?<br />\r\n<br />\r\nTuy nhi&ecirc;n cảm thấy việc n&agrave;y kh&ocirc;ng c&oacute; khả năng ph&aacute;t sinh, nhưng Th&ocirc;ng Thi&ecirc;n trong l&ograve;ng đ&atilde; ẩn ẩn c&oacute; cỗ lo lắng.<br />\r\n<br />\r\nD&ugrave; sao Vạn Ti&ecirc;n đại trận sự t&igrave;nh, l&uacute;c n&agrave;y giữa thi&ecirc;n địa trừ hắn kh&ocirc;ng một người biết được.<br />\r\n<br />\r\nC&aacute;i n&agrave;y tiểu ngưu y&ecirc;u lại sớm biết.<br />\r\n<br />\r\nM&agrave; lại hắn biết r&otilde; Lục Hồn Phi&ecirc;n m&oacute;n ph&aacute;p bảo n&agrave;y uy lực, nếu l&agrave; liền m&oacute;n ph&aacute;p bảo n&agrave;y đều tế ra.<br />\r\n<br />\r\nT&ecirc;!<br />\r\n<br />\r\nKhẳng định l&agrave; đến Tiệt Gi&aacute;o thời khắc sinh tử!<br />\r\n<br />\r\nC&oacute; thể coi l&agrave; l&agrave; c&ugrave;ng Nguy&ecirc;n Thủy đ&aacute;nh nhau, hắn cũng kh&ocirc;ng cần đến tế ra m&oacute;n ph&aacute;p bảo n&agrave;y.<br />\r\n<br />\r\nChỉ bằng v&agrave;o Tru Ti&ecirc;n Kiếm Trận, liền c&oacute; thể vững v&agrave;ng để l&ecirc;n gi&oacute;.<br />\r\n<br />\r\nTru Ti&ecirc;n Kiếm Trận kh&ocirc;ng phải tứ th&aacute;nh kh&ocirc;ng thể ph&aacute;!<br />\r\n<br />\r\nNghĩ tới chỗ n&agrave;y Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ, t&acirc;m thần hung hăng chấn động.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nChỉ c&oacute; Tru Ti&ecirc;n Kiếm Trận bị ph&aacute;, hắn mới c&oacute; thể thi triển sau c&ugrave;ng Lục Hồn Phi&ecirc;n!<br />\r\n<br />\r\nNguy&ecirc;n Thủy đều xuống tr&agrave;ng.<br />\r\n<br />\r\nVậy L&atilde;o Tử đ&acirc;u?<br />\r\n<br />\r\nNếu l&agrave; lại tăng th&ecirc;m t&acirc;y phương nhị th&aacute;nh, vừa tốt tứ th&aacute;nh.<br />\r\n<br />\r\nTề tụ bốn vị Th&aacute;nh Nh&acirc;n, c&oacute; thể ph&aacute; hắn Tru Ti&ecirc;n Kiếm Trận.<br />\r\n<br />\r\nTru Ti&ecirc;n Kiếm Trận bị ph&aacute;, l&agrave;m hắn muốn tế ra Lục Hồn Phi&ecirc;n thời điểm, lại bị Trường Nhĩ Định Quang Ti&ecirc;n đ&aacute;nh cắp hiến cho Nguy&ecirc;n Thủy, đến mức Tiệt Gi&aacute;o đại bại.<br />\r\n<br />\r\n&quot;H&ocirc;!&quot;<br />\r\n<br />\r\nNgồi ngay ngắn tr&ecirc;n bồ đo&agrave;n Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ hung hăng h&uacute;t miệng kh&iacute; lạnh.<br />\r\n<br />\r\nSắc mặt li&ecirc;n tục biến ảo!<br />\r\n<br />\r\nHắn giống như c&oacute; lẽ đ&atilde; theo Trường Phong chỗ lấy được tin tức.<br />\r\n<br />\r\nTuy nhi&ecirc;n c&oacute; chút kh&oacute; m&agrave; tin được Nguy&ecirc;n Thủy c&ugrave;ng L&atilde;o Tử sẽ li&ecirc;n hợp t&acirc;y phương nhị th&aacute;nh đối ph&oacute; hắn, nhưng b&acirc;y giờ đ&atilde; kh&ocirc;ng phải do hắn.<br />\r\n<br />\r\nMột ch&uacute;t để lộ ra tin tức, đ&atilde; để hắn đối tiểu ngưu y&ecirc;u ng&ocirc;n ngữ tin ba bốn ph&acirc;n.<br />\r\n<br />\r\nD&ugrave; sao hắn nghe được l&agrave; tiểu ngưu y&ecirc;u tiếng l&ograve;ng, đối phương kh&ocirc;ng c&oacute; l&yacute; do n&oacute;i l&aacute;o.<br />\r\n<br />\r\nHơn nữa nh&igrave;n tiểu ngưu y&ecirc;u thời khắc n&agrave;y thần sắc, căn bản th&igrave; kh&ocirc;ng biết m&igrave;nh tiếng l&ograve;ng bị hắn nghe được.<br />\r\n<br />\r\nCho n&ecirc;n đ&acirc;y đều l&agrave; tiểu ngưu y&ecirc;u trong l&ograve;ng &yacute; tưởng ch&acirc;n thật nhất.<br />\r\n<br />\r\nTh&acirc;n v&igrave; Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n, hắn biết r&otilde; Hồng Hoang ngọa hổ t&agrave;ng long, v&ocirc; c&ugrave;ng kỳ quặc sự t&igrave;nh thường c&oacute;.<br />\r\n<br />\r\nTrường Phong đầu n&agrave;y tiểu ngưu y&ecirc;u nắm giữ c&aacute;i n&agrave;y c&aacute;c loại năng lực, ngược lại cũng kh&ocirc;ng phải l&agrave; kh&ocirc;ng thể được.<br />\r\n<br />\r\nChỉ l&agrave; những tin tức n&agrave;y. . .<br />\r\n<br />\r\n&quot;Nguy&ecirc;n Thủy, L&atilde;o Tử! Ch&uacute;ng ta đều l&agrave; B&agrave;n Cổ đại thần nguy&ecirc;n thần biến th&agrave;nh, thật chẳng lẽ sẽ c&oacute; tự giết lẫn nhau một ng&agrave;y?&quot;<br />\r\n<br />\r\nNgồi ngay ngắn bồ đo&agrave;n b&ecirc;n tr&ecirc;n Th&ocirc;ng Thi&ecirc;n, khẽ ngẩng đầu nh&igrave;n ph&iacute;a thương khung.<br />\r\n<br />\r\nĐiều n&agrave;y hiển nhi&ecirc;n kh&ocirc;ng phải hắn hy vọng h&igrave;nh ảnh.<br />\r\n<br />\r\nĐột nhi&ecirc;n!<br />\r\n<br />\r\nLại l&agrave; hai &acirc;m thanh truyền đến.<br />\r\n<br />\r\n(cảnh giới: Dẫn kh&iacute; nhập thể - Luyện Kh&iacute; H&oacute;a Thần - Luyện Thần Phản Hư - Luyện Hư Hợp Đạo - Địa Ti&ecirc;n - Thi&ecirc;n Ti&ecirc;n - Ch&acirc;n Ti&ecirc;n - Huyền Ti&ecirc;n - Th&aacute;i Ất Huyền Ti&ecirc;n - Kim Ti&ecirc;n - Th&aacute;i Ất Kim Ti&ecirc;n - Đại La Kim Ti&ecirc;n - Chuẩn Th&aacute;nh (Hỗn Nguy&ecirc;n Kim Ti&ecirc;n)- Th&aacute;nh Nh&acirc;n (Hỗn Nguy&ecirc;n Đại La Kim Ti&ecirc;n)- Thi&ecirc;n Đạo - Đại Đạo))<br />\r\n<br />\r\nMỗi c&aacute;i cảnh giới ph&acirc;n tiền, trung, hậu, vi&ecirc;n m&atilde;n, bốn c&aacute;i cảnh giới nhỏ.</p>', '2021-11-27 21:09:25', 9, 1),
(167, 44, 'Thông Thiên chấn kinh', '3', '<p>【 sư t&ocirc;n kh&ocirc;ng hổ l&agrave; Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n, quanh th&acirc;n đạo văn vờn quanh, trong miệng kim li&ecirc;n, Mạn Ch&acirc;u phun ra, tr&agrave;n ngập v&ocirc; tận điềm l&agrave;nh chi tướng. 】<br />\r\n<br />\r\n【 ta nếu l&agrave; c&oacute; sư t&ocirc;n 10% thực lực, vậy liền sảng kho&aacute;i, to&agrave;n bộ Hồng Hoang nơi n&agrave;o đi kh&ocirc;ng được. 】<br />\r\n<br />\r\nThần sắc ngưng trọng Th&ocirc;ng Thi&ecirc;n, nao nao, vốn l&agrave; trang trọng biểu lộ c&oacute; chút thư thản.<br />\r\n<br />\r\nThi&ecirc;n xuy&ecirc;n vạn xuy&ecirc;n nịnh nọt kh&ocirc;ng xu&ocirc;i!<br />\r\n<br />\r\nCũng l&agrave; hắn c&aacute;i n&agrave;y Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n cũng kh&ocirc;ng ngoại lệ.<br />\r\n<br />\r\nTh&acirc;n v&igrave; Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n, nghe được lấy l&ograve;ng tự nhi&ecirc;n kh&ocirc;ng &iacute;t.<br />\r\n<br />\r\nBất qu&aacute; loại n&agrave;y ph&aacute;t đến s&acirc;u trong nội t&acirc;m ca ngợi, vẫn l&agrave; lệnh hắn v&ocirc; c&ugrave;ng vui vẻ.<br />\r\n<br />\r\nC&aacute;i n&agrave;y tiểu ngưu y&ecirc;u c&ograve;n t&iacute;nh l&agrave; c&oacute; ch&uacute;t kiến thức.<br />\r\n<br />\r\nBiết được bản tọa lợi hại!<br />\r\n<br />\r\n【 ai! Chỉ l&agrave; đ&aacute;ng tiếc, Phong Thần đại kiếp b&ecirc;n trong đần độn đi k&yacute; Phong Thần Bảng, đến mức bị &eacute;p ăn v&agrave;o Vẫn Th&aacute;nh Đan, sau c&ugrave;ng c&ograve;n bị cầm t&ugrave; tại Tử Ti&ecirc;u cung, thật đ&aacute;ng thương! 】<br />\r\n<br />\r\nTh&ocirc;ng Thi&ecirc;n: . . . .<br />\r\n<br />\r\nSắc mặt hoan hỉ Th&ocirc;ng Thi&ecirc;n, trong nh&aacute;y mắt biểu lộ cứng ở tr&ecirc;n mặt.<br />\r\n<br />\r\nVừa mới c&ograve;n n&oacute;i hắn đạo ph&aacute;p th&ocirc;ng huyền, thực lực đ&ecirc;́n, đảo mắt liền n&oacute;i hắn &#39;Ngốc &#39;, c&ograve;n đ&aacute;ng thương hắn.<br />\r\n<br />\r\nTh&aacute;nh Nh&acirc;n ph&iacute;a dưới đều l&agrave; con kiến h&ocirc;i, hắn nhưng l&agrave; vượt l&ecirc;n tr&ecirc;n ch&uacute;ng sinh Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n.<br />\r\n<br />\r\nTrải qua vạn kiếp m&agrave; bất diệt, nhiễm nh&acirc;n quả m&agrave; kh&ocirc;ng d&iacute;nh.<br />\r\n<br />\r\nC&ugrave;ng đại đạo c&ugrave;ng tồn tại, c&ugrave;ng Thi&ecirc;n Đạo c&ugrave;ng lưu giữ.<br />\r\n<br />\r\nNhất niệm c&oacute; biết qu&aacute; khứ, hiện tại, tương lai, kh&ocirc;ng cần tốn nhiều sức liền c&oacute; thể hủy diệt v&ocirc; số thế giới, c&ograve;n c&oacute; thể lại mở ra đất trời.<br />\r\n<br />\r\nNgười n&agrave;o d&aacute;m n&oacute;i bọn họ Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n ngốc?<br />\r\n<br />\r\nTiểu ngưu y&ecirc;u bất qu&aacute; chỉ l&agrave; một giới Thi&ecirc;n Ti&ecirc;n, lại c&ograve;n thương hại hắn?<br />\r\n<br />\r\nThật sự l&agrave; hoang đường c&ugrave;ng cực!<br />\r\n<br />\r\nChỉ l&agrave; thời khắc n&agrave;y Th&ocirc;ng Thi&ecirc;n, t&acirc;m tư cũng kh&ocirc;ng c&oacute; ở tr&ecirc;n đ&acirc;y.<br />\r\n<br />\r\nD&ugrave; sao tiểu ngưu y&ecirc;u mạo phạm, căn bản l&agrave; kh&ocirc;ng c&aacute;ch n&agrave;o c&ugrave;ng ph&iacute;a sau tin tức so s&aacute;nh.<br />\r\n<br />\r\nK&yacute; Phong Thần Bảng.<br />\r\n<br />\r\nBị &eacute;p ăn v&agrave;o Vẫn Th&aacute;nh Đan.<br />\r\n<br />\r\nSau c&ugrave;ng c&ograve;n bị cầm t&ugrave; tại Tử Ti&ecirc;u cung!<br />\r\n<br />\r\nC&aacute;i n&agrave;y li&ecirc;n tiếp tin tức, để hắn c&oacute; chút phản ứng kh&ocirc;ng kịp.<br />\r\n<br />\r\nPhong Thần đại kiếp b&ecirc;n trong, kh&ocirc;ng chỉ c&oacute; m&ocirc;n hạ mọi người kết cục bi thảm, tựa hồ liền hắn cũng kh&ocirc;ng ngoại lệ.<br />\r\n<br />\r\nBị cầm t&ugrave; tại Tử Ti&ecirc;u cung coi như xong, c&ograve;n ăn v&agrave;o Vẫn Th&aacute;nh Đan.<br />\r\n<br />\r\nVẫn Th&aacute;nh Đan hắn cũng kh&ocirc;ng biết c&aacute;i g&igrave; đồ chơi.<br />\r\n<br />\r\nBất qu&aacute; ri&ecirc;ng n&agrave;y mặt chữ &yacute; tứ nh&igrave;n, đ&oacute; l&agrave; ngay cả Th&aacute;nh Nh&acirc;n đều phải bỏ mạng đan dược.<br />\r\n<br />\r\nTuyệt đối kh&ocirc;ng phải vật g&igrave; tốt.<br />\r\n<br />\r\nChỉ l&agrave; bực n&agrave;y tin tức, kh&ocirc;ng khỏi qu&aacute; hoảng sợ.<br />\r\n<br />\r\nTử Ti&ecirc;u cung đ&acirc;y ch&iacute;nh l&agrave; Đạo Tổ đạo trường, chẳng lẽ liền Đạo Tổ đều xuống tr&agrave;ng?</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nĐạo Tổ Hợp Đạo về sau, hắn đ&atilde; l&acirc;u lắm chưa từng nh&igrave;n thấy.<br />\r\n<br />\r\nL&agrave;m sao c&oacute; thể tuỳ tiện hiện th&acirc;n!<br />\r\n<br />\r\nLấy hắn giải, trừ phi ph&aacute;t sinh ảnh hưởng to&agrave;n bộ thi&ecirc;n địa đại sự, kh&ocirc;ng phải vậy tuyệt đối kinh h&atilde;i bất động vị kia.<br />\r\n<br />\r\nBất qu&aacute; đang nghĩ đến Phong Thần Bảng thời điểm, Th&ocirc;ng Thi&ecirc;n l&ograve;ng c&oacute; cảm gi&aacute;c, nhất thời thần niệm khẽ nh&uacute;c nh&iacute;ch.<br />\r\n<br />\r\nC&oacute; thể một hồi thời gian về sau, hắn đột nhi&ecirc;n mở ra hai mắt, sắc mặt c&oacute; chút trắng bệch.<br />\r\n<br />\r\nTh&ocirc;i diễn Phong Thần Bảng, v&acirc;̣y mà bị phản phệ.<br />\r\n<br />\r\nC&ograve;n bị Thi&ecirc;n Đạo cảnh c&aacute;o! ! !<br />\r\n<br />\r\nPhong Thần Bảng, đ&acirc;y l&agrave; một c&aacute;i cấm kỵ tồn tại!<br />\r\n<br />\r\nD&agrave;i như vậy năm th&aacute;ng, hắn đều chưa bao giờ gặp loại chuyện n&agrave;y.<br />\r\n<br />\r\nTh&agrave;nh như tiểu ngưu y&ecirc;u n&oacute;i, c&aacute;i n&agrave;y Phong Thần Bảng liền hắn đều kh&ocirc;ng thể phỏng đo&aacute;n.<br />\r\n<br />\r\nC&aacute;i đồ chơi n&agrave;y khẳng định l&agrave; c&ugrave;ng Phong Thần đại kiếp c&oacute; li&ecirc;n quan thần vật.<br />\r\n<br />\r\n&Aacute;nh mắt nh&igrave;n ph&iacute;a nơi xa đạo th&acirc;n ảnh kia, Th&ocirc;ng Thi&ecirc;n giờ ph&uacute;t n&agrave;y đối tiểu ngưu y&ecirc;u n&oacute;i tới đ&atilde; tin bảy t&aacute;m phần.<br />\r\n<br />\r\nHắn bản v&igrave; Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n, Niệm Tức ng&agrave;n vạn.<br />\r\n<br />\r\nC&aacute;i kia đ&ocirc;ng đảo ph&acirc;n mảnh tin tức, ch&oacute;i mắt đ&atilde; bị hắn li&ecirc;n th&ocirc;ng.<br />\r\n<br />\r\nThậm ch&iacute; v&igrave; sao cuối c&ugrave;ng n&oacute;i tổ xuống tr&agrave;ng, Th&ocirc;ng Thi&ecirc;n cũng đ&atilde; c&oacute; suy đo&aacute;n.<br />\r\n<br />\r\nTất nhi&ecirc;n l&agrave; bởi v&igrave; tại Phong Thần đại kiếp nửa đường thống đại thương, l&ograve;ng hắn sinh kh&ocirc;ng cam l&ograve;ng chi niệm, muốn tr&ugrave;ng luyện Địa Thủy Hỏa Phong, lại mở thế giới.<br />\r\n<br />\r\nChỉ lại c&oacute; chuyện như vậy, mới c&oacute; thể dẫn được Đạo Tổ hiện th&acirc;n.<br />\r\n<br />\r\n&quot;Bản tọa Tiệt Gi&aacute;o thịnh huống như thế, chẳng lẽ liền muốn tại kế tiếp lượng kiếp b&ecirc;n trong biến th&agrave;nh tro bụi?&quot;<br />\r\n<br />\r\nNh&igrave;n qua dưới tay c&aacute;i kia một đ&aacute;m đoan tọa b&oacute;ng người, Th&ocirc;ng Thi&ecirc;n sắc mặt v&ocirc; c&ugrave;ng nghi&ecirc;m t&uacute;c.<br />\r\n<br />\r\nVạn ti&ecirc;n triều b&aacute;i, c&aacute;i n&agrave;y l&agrave; bực n&agrave;o đựng thế!<br />\r\n<br />\r\nC&oacute; thể nghĩ đến vừa mới lui ra Hồng Hoang s&acirc;n khấu Vu Y&ecirc;u nhị tộc, Th&ocirc;ng Thi&ecirc;n thật s&acirc;u thở ra một hơi.<br />\r\n<br />\r\nDứt bỏ hắn c&aacute;i n&agrave;y Th&aacute;nh Nh&acirc;n m&agrave; n&oacute;i, l&uacute;c n&agrave;y Tiệt Gi&aacute;o c&oacute; thể kh&ocirc;ng s&aacute;nh bằng c&aacute;i n&agrave;y hai tộc.<br />\r\n<br />\r\nL&atilde;o Tử, Nguy&ecirc;n Thủy, Chuẩn Đề, Tiếp Dẫn!<br />\r\n<br />\r\nNếu thật như tiểu ngưu y&ecirc;u n&oacute;i, tứ th&aacute;nh đều xuất hiện, quả nhi&ecirc;n l&agrave; tử cục.<br />\r\n<br />\r\nSắc mặt trang trọng Th&ocirc;ng Thi&ecirc;n, trong đầu một tia s&aacute;ng xẹt qua.<br />\r\n<br />\r\n&quot;Đ&uacute;ng a! Ta l&agrave;m sao qu&ecirc;n c&aacute;i n&agrave;y tiểu ngưu y&ecirc;u!&quot;<br />\r\n<br />\r\nC&aacute;i n&agrave;y hết thảy tất cả, đều l&agrave; từ nhỏ ngưu y&ecirc;u trong l&ograve;ng biết.<br />\r\n<br />\r\nĐại Đạo 50, thi&ecirc;n diễn 49, vạn sự đều l&agrave; c&oacute; biến số.<br />\r\n<br />\r\nTại Th&ocirc;ng Thi&ecirc;n xem ra, tiểu ngưu y&ecirc;u xuất hiện v&agrave;o l&uacute;c n&agrave;y, c&aacute;i kia ch&iacute;nh l&agrave; Tiệt Gi&aacute;o một đường sinh cơ.<br />\r\n<br />\r\nVạn sự lấy ra Nhất Tuyến Thi&ecirc;n Cơ, c&aacute;i n&agrave;y vốn l&agrave; Tiệt Gi&aacute;o gi&aacute;o nghĩa chỗ.<br />\r\n<br />\r\nM&agrave; lại c&oacute; tiểu ngưu y&ecirc;u tại, đến đ&oacute;n lấy nhất định c&oacute; thể biết được c&agrave;ng nhiều.<br />\r\n<br />\r\nNghĩ th&ocirc;ng suốt những thứ n&agrave;y Th&ocirc;ng Thi&ecirc;n, kh&ocirc;ng khỏi quan s&aacute;t tỉ mỉ lấy ph&iacute;a dưới đạo th&acirc;n ảnh kia.<br />\r\n<br />\r\n&quot;Bản tọa đ&atilde; biết ngươi suy nghĩ trong l&ograve;ng, sao c&oacute; thể để ngươi chạy.&quot;<br />\r\n<br />\r\nTiểu ngưu y&ecirc;u biết được nhiều như vậy liền hắn đều kh&ocirc;ng r&otilde; r&agrave;ng sự t&igrave;nh, tự nhi&ecirc;n muốn đem hắn chăm ch&uacute; cột v&agrave;o Tiệt Gi&aacute;o c&acirc;y to n&agrave;y ph&iacute;a tr&ecirc;n.<br />\r\n<br />\r\nNgồi ngay ngắn tảng đ&aacute; ph&iacute;a sau Trường Phong, nhưng kh&ocirc;ng biết Th&ocirc;ng Thi&ecirc;n đ&atilde; ch&uacute; &yacute; hắn thật l&acirc;u.<br />\r\n<br />\r\nThu hồi ngắm nh&igrave;n bốn ph&iacute;a &aacute;nh mắt, liếc nh&igrave;n m&agrave;n ảnh trước mắt.<br />\r\n<br />\r\nNằm ngửa hệ thống<br />\r\n<br />\r\nK&iacute; chủ: Trường Phong<br />\r\n<br />\r\nCảnh giới: Huyền Ti&ecirc;n trung kỳ (1 - 1500)+<br />\r\n<br />\r\nĐạo hạnh: 756<br />\r\n<br />\r\nHệ thống rất đơn giản, chỉ cần mỗi ng&agrave;y chuyện g&igrave; mặc kệ, liền c&oacute; thể kh&ocirc;ng ngừng thu hoạch được đạo hạnh.<br />\r\n<br />\r\nĐạo hạnh lấy năm l&agrave;m đơn vị, trước mắt cảnh giới đột ph&aacute; liền cần 1500 năm đạo hạnh,<br />\r\n<br />\r\nThu hoạch đạo hạnh, trực tiếp th&ecirc;m đến cảnh giới ph&iacute;a tr&ecirc;n, liền c&oacute; thể nhanh ch&oacute;ng đề cao thực lực.<br />\r\n<br />\r\nNgắn ngủi thời gian ng&agrave;n năm, hắn theo một c&aacute;i c&ograve;n chưa biến h&oacute;a tiểu y&ecirc;u, v&ocirc; c&ugrave;ng nỗ lực nằm, đ&atilde; đến cảnh giới cỡ n&agrave;y.<br />\r\n<br />\r\n&quot;Huyền Ti&ecirc;n trung kỳ, sư t&ocirc;n giảng đạo khẳng định phải c&aacute;i ngàn thanh năm thời gian, kh&ocirc;ng biết c&oacute; thể hay kh&ocirc;ng bước v&agrave;o Kim Ti&ecirc;n cảnh giới.&quot;<br />\r\n<br />\r\nTrong l&ograve;ng thầm nhủ một tiếng, Trường Phong tiện tay nhốt hệ thống m&agrave;n h&igrave;nh.<br />\r\n<br />\r\nLập tức kh&ocirc;ng để &yacute; bốn ph&iacute;a, nhắm mắt đi ngủ đ&acirc;y.<br />\r\n<br />\r\nL&uacute;c ngủ, hắn thu hoạch đạo hạnh tốc độ nhanh nhất, cho n&ecirc;n c&aacute;i n&agrave;y thời gian ng&agrave;n năm, hắn cơ bản đều đang ngủ.<br />\r\n<br />\r\n&quot;Thời gian ng&agrave;n năm liền muốn theo Huyền Ti&ecirc;n bước v&agrave;o Kim Ti&ecirc;n, c&aacute;i n&agrave;y tiểu ngưu y&ecirc;u t&acirc;m cũng thật l&agrave; qu&aacute; lớn.&quot;<br />\r\n<br />\r\nNgồi ngay ngắn thượng thủ Th&ocirc;ng Thi&ecirc;n, khẽ chau m&agrave;y.<br />\r\n<br />\r\nKhoảng một ngh&igrave;n năm thời gian, có th&ecirc;̉ c&oacute; một c&aacute;i cảnh giới nhỏ đột ph&aacute; thế l&agrave; tốt rồi.<br />\r\n<br />\r\nGia hỏa n&agrave;y c&ograve;n muốn trực tiếp bước v&agrave;o Kim Ti&ecirc;n?<br />\r\n<br />\r\nTrong l&uacute;c n&agrave;y c&ograve;n c&aacute;ch một c&aacute;i Th&aacute;i Ất Huyền Ti&ecirc;n đ&acirc;y.<br />\r\n<br />\r\nHai c&aacute;i đại cảnh giới vượt qua.<br />\r\n<br />\r\nD&atilde; t&acirc;m đến l&agrave; kh&ocirc;ng nhỏ.<br />\r\n<br />\r\nĐi qua Long H&aacute;n, Vu Y&ecirc;u lượng kiếp t&agrave;n ph&aacute;, l&uacute;c n&agrave;y thi&ecirc;n địa đ&atilde; kh&ocirc;ng so l&uacute;c trước.<br />\r\n<br />\r\nSinh linh tuy nhi&ecirc;n nắm giữ kh&ocirc;ng &iacute;t tu h&agrave;nh phương ph&aacute;p, nhưng tu luyện ngược lại kh&ocirc;ng c&oacute; trước kia dễ d&agrave;ng.<br />\r\n<br />\r\nỒ!<br />\r\n<br />\r\nKh&ocirc;ng đ&uacute;ng!<br />\r\n<br />\r\nC&aacute;i n&agrave;y tiểu ngưu y&ecirc;u kh&ocirc;ng phải chỉ c&oacute; Thi&ecirc;n Ti&ecirc;n cảnh giới &agrave;.<br />\r\n<br />\r\nTh&ocirc;ng Thi&ecirc;n sắc mặt khẽ giật m&igrave;nh, trong mắt mang theo kinh nghi.<br />\r\n<br />\r\nCẩn thận nh&igrave;n lại, tiểu ngưu y&ecirc;u x&aacute;c thực chỉ c&oacute; Thi&ecirc;n Ti&ecirc;n tiền kỳ cảnh giới.<br />\r\n<br />\r\nCổ qu&aacute;i! Cổ qu&aacute;i!</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nR&otilde; r&agrave;ng chỉ c&oacute; Thi&ecirc;n Ti&ecirc;n cảnh giới, v&acirc;̣y mà n&oacute;i m&igrave;nh c&oacute; Huyền Ti&ecirc;n tu vi.<br />\r\n<br />\r\nChẳng lẽ lấy thực lực của hắn c&ograve;n nh&igrave;n kh&ocirc;ng thấu c&aacute;i n&agrave;y tiểu ngưu y&ecirc;u?<br />\r\n<br />\r\nC&aacute;i n&agrave;y căn bản liền kh&ocirc;ng khả năng.<br />\r\n<br />\r\nC&oacute; thể c&aacute;i n&agrave;y tiểu ngưu y&ecirc;u cũng kh&ocirc;ng cần thiết lừa gạt hắn.<br />\r\n<br />\r\nD&ugrave; sao c&aacute;i n&agrave;y ch&iacute;nh l&agrave; l&agrave; đối phương tiếng l&ograve;ng, kh&ocirc;ng c&oacute; ai sẽ ở trong l&ograve;ng ch&iacute;nh m&igrave;nh lừa gạt m&igrave;nh.<br />\r\n<br />\r\nCẩn thận chu đ&aacute;o một hồi, Th&ocirc;ng Thi&ecirc;n ph&aacute;t hiện c&aacute;i n&agrave;y tiểu ngưu y&ecirc;u thể nội ph&aacute;p lực c&oacute; dị thường.<br />\r\n<br />\r\nC&aacute;i kia h&ugrave;ng hậu ph&aacute;p lực, quả quyết kh&ocirc;ng phải một c&aacute;i Thi&ecirc;n Ti&ecirc;n có th&ecirc;̉ nắm giữ.<br />\r\n<br />\r\nC&aacute;i n&agrave;y tiểu ngưu y&ecirc;u thật sự c&oacute; Huyền Ti&ecirc;n tu vi, c&ograve;n lừa gạt được hắn.<br />\r\n<br />\r\nHắn nhưng l&agrave; Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n a!<br />\r\n<br />\r\n&Aacute;p đảo chư thi&ecirc;n vạn giới ph&iacute;a tr&ecirc;n tồn tại.<br />\r\n<br />\r\nLại bị một c&aacute;i tiểu ngưu y&ecirc;u cho lừa gạt.<br />\r\n<br />\r\nDứt bỏ hắn thể nội ph&aacute;p lực m&agrave; n&oacute;i, tiểu ngưu y&ecirc;u nh&igrave;n qua thật chỉ c&oacute; Thi&ecirc;n Ti&ecirc;n cảnh giới.<br />\r\n<br />\r\nLiền hắn c&aacute;i n&agrave;y Th&aacute;nh Nh&acirc;n đều có th&ecirc;̉ che đậy! ! !<br />\r\n<br />\r\nTrong l&ograve;ng kinh nghi Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ, kh&ocirc;ng khỏi phỏng đo&aacute;n c&aacute;i n&agrave;y tiểu ngưu y&ecirc;u lai lịch.<br />\r\n<br />\r\nThi&ecirc;n cơ hỗn loạn, m&ecirc; vụ vờn quanh, lại kh&ocirc;ng nh&igrave;n thấy một ch&uacute;t tin tức.<br />\r\n<br />\r\nHắn, đường đường Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n, thế m&agrave; th&ocirc;i diễn kh&ocirc;ng ra một c&aacute;i tiểu ngưu y&ecirc;u căn nguy&ecirc;n.<br />\r\n<br />\r\nViệc n&agrave;y qu&aacute; quỷ dị!<br />\r\n<br />\r\nChấn kinh sau khi, Th&ocirc;ng Thi&ecirc;n đảo mắt một trận thoải m&aacute;i.<br />\r\n<br />\r\n&quot;Tốt tốt tốt! Vừa vặn thu l&agrave;m đệ tử th&acirc;n truyền.&quot;<br />\r\n<br />\r\nTiểu ngưu y&ecirc;u kh&ocirc;ng chỉ c&oacute; thể hiểu r&otilde; tương lai, m&agrave; lại liền hắn đều đẩy coi kh&ocirc;ng ra trải qua, định kh&ocirc;ng phải hạng người tầm thường.<br />\r\n<br />\r\nBất qu&aacute; một gi&acirc;y sau, Th&ocirc;ng Thi&ecirc;n th&igrave; ng&acirc;y ngẩn cả người.<br />\r\n<br />\r\nC&aacute;i n&agrave;y tiểu ngưu y&ecirc;u vậy m&agrave; tại ngủ! ! !<br />\r\n<br />\r\nVừa mới c&ograve;n lớn hơn thả h&agrave;o ng&ocirc;n, muốn thời gian ng&agrave;n năm theo Huyền Ti&ecirc;n đột ph&aacute; đến Kim Ti&ecirc;n.<br />\r\n<br />\r\nC&aacute;i n&agrave;y đảo mắt an vị c&aacute;i kia ngủ thiếp đi.<br />\r\n<br />\r\nTh&aacute;nh Nh&acirc;n giảng đạo, đ&acirc;y l&agrave; Hồng Hoang bao nhi&ecirc;u sinh linh tha thiết ước mơ sự t&igrave;nh.<br />\r\n<br />\r\nGia hỏa n&agrave;y vậy m&agrave; như thế kh&ocirc;ng ch&uacute;t n&agrave;o tr&acirc;n qu&yacute;.<br />\r\n<br />\r\nTại hắn giảng đạo thời điểm ngủ.<br />\r\n<br />\r\nThật sự l&agrave; đ&aacute;ng giận!<br />\r\n<br />\r\n&quot;Ta th&igrave; nh&igrave;n xem ngươi thời gian ng&agrave;n năm, như thế n&agrave;o bước v&agrave;o Kim Ti&ecirc;n!&quot;<br />\r\n<br />\r\nĐạo &acirc;m cuồn cuộn m&agrave; ra, bất qu&aacute; một luồng thần niệm lại gấp lượn quanh Trường Phong tr&ecirc;n th&acirc;n.</p>', '2021-11-27 21:09:50', 6, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(168, 44, 'Đại đạo chi tử', '4', '<p>Th&ocirc;ng Thi&ecirc;n thần sắc c&oacute; ch&uacute;t buồn bực.<br />\r\n<br />\r\nHắn cảm gi&aacute;c m&igrave;nh bị tiểu ngưu y&ecirc;u đ&ugrave;a bỡn.<br />\r\n<br />\r\nĐ&atilde; qua hai trăm năm thời gian, gia hỏa n&agrave;y y nguy&ecirc;n c&ograve;n tại ngủ.<br />\r\n<br />\r\nVừa mới bắt đầu hắn c&ograve;n nghĩ đến nh&igrave;n một c&aacute;i tiểu ngưu y&ecirc;u muốn thi triển thủ đoạn g&igrave; tu luyện.<br />\r\n<br />\r\nTh&igrave; c&aacute;i n&agrave;y?<br />\r\n<br />\r\nQuang ngủ th&igrave; c&oacute; thể đột ph&aacute;?<br />\r\n<br />\r\nHai trăm năm thời gian, dưới đ&aacute;y c&oacute; kh&ocirc;ng &iacute;t m&ocirc;n nh&acirc;n biến h&oacute;a, đột ph&aacute;, tiểu ngưu y&ecirc;u liền kh&ocirc;ng c&oacute; tỉnh qua.<br />\r\n<br />\r\nNhưng hắn nghe l&agrave; tiểu ngưu y&ecirc;u tiếng l&ograve;ng.<br />\r\n<br />\r\nTiểu ngưu y&ecirc;u lại kh&ocirc;ng biết hắn có th&ecirc;̉ nghe được thanh &acirc;m n&agrave;y.<br />\r\n<br />\r\nNếu l&agrave; tiếng l&ograve;ng, kh&ocirc;ng c&oacute; đạo l&yacute; l&agrave; giả.<br />\r\n<br />\r\nNh&igrave;n c&aacute;i kia một đạo nhắm mắt đoan tọa b&oacute;ng người, Th&ocirc;ng Thi&ecirc;n nghi ngờ trong l&ograve;ng c&agrave;ng tăng l&ecirc;n.<br />\r\n<br />\r\nTiểu ngưu y&ecirc;u rốt cuộc muốn l&agrave;m c&aacute;i g&igrave;?<br />\r\n<br />\r\nCh&oacute;i mắt lại l&agrave; 300 năm thời gian tr&ocirc;i qua, dưới đ&aacute;y mọi người nghe như si giống như say.<br />\r\n<br />\r\nC&oacute; thể tiểu ngưu y&ecirc;u, vẫn kh&ocirc;ng nh&uacute;c nh&iacute;ch.<br />\r\n<br />\r\nTh&ocirc;ng Thi&ecirc;n cảm gi&aacute;c m&igrave;nh thật bị chơi xỏ, nhưng c&aacute;i n&agrave;y lại giải th&iacute;ch kh&ocirc;ng th&ocirc;ng.<br />\r\n<br />\r\nC&aacute;i n&agrave;y khiến hắn rất kh&oacute; hiểu!<br />\r\n<br />\r\nThời gian năm trăm năm, hắn mặc d&ugrave; kh&ocirc;ng c&oacute; đ&igrave;nh chỉ giảng đạo, nhưng một luồng thần niệm thủy chung tại tiểu ngưu y&ecirc;u tr&ecirc;n th&acirc;n.<br />\r\n<br />\r\nGia hỏa n&agrave;y căn bản th&igrave; kh&ocirc;ng c&oacute; tu luyện qua!<br />\r\n<br />\r\nTh&ocirc;ng Thi&ecirc;n đều muốn trực tiếp tỉnh lại hắn.<br />\r\n<br />\r\nHỏi một ch&uacute;t Trường Phong, ngươi dạng n&agrave;y ngủ l&agrave;m sao thời gian ng&agrave;n năm bước v&agrave;o Kim Ti&ecirc;n.<br />\r\n<br />\r\nNhưng rất r&otilde; r&agrave;ng, hắn kh&ocirc;ng thể l&agrave;m như thế.<br />\r\n<br />\r\nD&ugrave; sao hắn nhưng l&agrave; nghe l&eacute;n đối phương tiếng l&ograve;ng.<br />\r\n<br />\r\nHỏi một ch&uacute;t vậy liền lộ tẩy.<br />\r\n<br />\r\nĐang l&uacute;c Th&ocirc;ng Thi&ecirc;n mặt c&oacute; tức giận thời khắc, c&aacute;i kia tập đoan tọa b&oacute;ng người, rốt cục chậm r&atilde;i mở ra hai mắt.<br />\r\n<br />\r\n【 thời gian tr&ocirc;i qua thật nhanh a, chớp mắt năm trăm năm liền đi qua. 】<br />\r\n<br />\r\nTrong đầu truyền đến thanh &acirc;m quen thuộc, để Th&ocirc;ng Thi&ecirc;n kh&oacute;e miệng giật một c&aacute;i.<br />\r\n<br />\r\nNgươi c&ograve;n biết 500 năm qua đi.<br />\r\n<br />\r\nTa nh&igrave;n ngươi đến đ&oacute;n lấy thời gian tu luyện thế n&agrave;o.<br />\r\n<br />\r\nNh&igrave;n c&aacute;i kia bẻ bẻ cổ, gi&atilde;n ra g&acirc;n cốt Trường Phong, trong l&ograve;ng c&aacute;i kia phiền muộn.<br />\r\n<br />\r\nThời gian năm trăm năm, đều ở c&aacute;i n&agrave;y nh&igrave;n hắn ngủ.<br />\r\n<br />\r\nVẫn l&agrave; tại hắn giảng đạo thời điểm ngủ.<br />\r\n<br />\r\nTuyệt đối l&agrave; ri&ecirc;ng một ngọn cờ!</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nTrường Phong nhưng kh&ocirc;ng biết Th&ocirc;ng Thi&ecirc;n suy nghĩ trong l&ograve;ng, nh&igrave;n chung quanh một v&ograve;ng, chậm r&atilde;i mở ra hệ thống m&agrave;n h&igrave;nh.<br />\r\n<br />\r\nNằm ngửa hệ thống<br />\r\n<br />\r\nK&iacute; chủ: Trường Phong<br />\r\n<br />\r\nCảnh giới: Huyền Ti&ecirc;n trung kỳ (1 - 1500)+<br />\r\n<br />\r\nĐạo hạnh: 9881<br />\r\n<br />\r\n&quot;Kh&ocirc;ng tệ! Kh&ocirc;ng tệ! T&iacute;ch lũy đạo hạnh miễn miễn cưỡng cưỡng, l&agrave; thời điểm đột ph&aacute;.&quot;<br />\r\n<br />\r\nThầm nghĩ lấy Trường Phong, trực tiếp đem lấy được đạo hạnh to&agrave;n th&ecirc;m tại cảnh giới ph&iacute;a tr&ecirc;n.<br />\r\n<br />\r\nHuyền Ti&ecirc;n hậu kỳ, Huyền Ti&ecirc;n vi&ecirc;n m&atilde;n, lập tức bước v&agrave;o Th&aacute;i Ất Huyền Ti&ecirc;n sơ kỳ.<br />\r\n<br />\r\nCảnh giới: Th&aacute;i Ất Huyền Ti&ecirc;n sơ kỳ (3382 - 5000)<br />\r\n<br />\r\nC&aacute;i n&agrave;y năm trăm năm ngủ lấy được đạo hạnh, tất cả đều th&ecirc;m xong.<br />\r\n<br />\r\nChỉ l&agrave; nh&igrave;n thấy trước mắt cảnh giới đột ph&aacute; cần đạo hạnh thời đi&ecirc;̉m, Trường Phong c&oacute; ch&uacute;t buồn bực.<br />\r\n<br />\r\nNg&agrave;n năm giảng đạo thời gian, đ&atilde; qua một nửa.<br />\r\n<br />\r\nHắn hiện tại mới Th&aacute;i Ất Huyền Ti&ecirc;n sơ kỳ.<br />\r\n<br />\r\nC&oacute; thể ph&aacute; nhập trung kỳ liền cần năm ng&agrave;n năm đạo hạnh.<br />\r\n<br />\r\n【 mới bước v&agrave;o Th&aacute;i Ất Huyền Ti&ecirc;n sơ kỳ, mặt sau n&agrave;y cần đạo hạnh c&agrave;ng ng&agrave;y c&agrave;ng nhiều, c&ograve;n lại năm trăm năm sợ l&agrave; kh&oacute; c&oacute; thể ph&aacute; vỡ m&agrave; v&agrave;o Kim Ti&ecirc;n. 】<br />\r\n<br />\r\nTh&ocirc;ng Thi&ecirc;n: ? ? ?<br />\r\n<br />\r\nNgồi ngay ngắn bồ đo&agrave;n b&ecirc;n tr&ecirc;n Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ ng&acirc;y ngẩn cả người.<br />\r\n<br />\r\nVừa mới nghe được tiểu ngưu y&ecirc;u n&oacute;i muốn đột ph&aacute;, c&ograve;n tưởng rằng gia hỏa n&agrave;y rốt cục muốn bắt đầu tu luyện, c&aacute;i kia biết r&otilde; đảo mắt liền n&oacute;i đột ph&aacute;.<br />\r\n<br />\r\nC&ograve;n tự xưng đ&atilde; bước v&agrave;o Th&aacute;i Ất Huyền Ti&ecirc;n? ? ?<br />\r\n<br />\r\nNgươi tại c&aacute;i n&agrave;y đ&ugrave;a ta đ&acirc;y.<br />\r\n<br />\r\nC&aacute;i n&agrave;y mới thời gian mấy hơi thở, liền vượt tốt mấy cảnh giới?<br />\r\n<br />\r\nNhưng cẩn thận liếc nh&igrave;n Trường Phong Th&ocirc;ng Thi&ecirc;n.<br />\r\n<br />\r\nTrong nh&aacute;y mắt t&acirc;m thần đại chấn!<br />\r\n<br />\r\nTiểu ngưu y&ecirc;u thể nội ph&aacute;p lực b&agrave;nh trướng.<br />\r\n<br />\r\nTuy nhi&ecirc;n nh&igrave;n qua cảnh giới vẫn l&agrave; Thi&ecirc;n Ti&ecirc;n tiền kỳ, nhưng quả thật c&oacute; Th&aacute;i Ất Huyền Ti&ecirc;n sơ kỳ ph&aacute;p lực.<br />\r\n<br />\r\nĐộng đều kh&ocirc;ng động tới, cứ như vậy đột ph&aacute;?<br />\r\n<br />\r\nM&agrave; lại đột ph&aacute; c&ograve;n kh&ocirc;ng c&oacute; một điểm ba động.<br />\r\n<br />\r\nThần sắc khiếp sợ hắn, vội v&agrave;ng li&ecirc;n tục d&ograve; x&eacute;t.<br />\r\n<br />\r\nKh&ocirc;ng sai!<br />\r\n<br />\r\nC&aacute;i kia m&ecirc;nh m&ocirc;ng ph&aacute;p lực, đ&uacute;ng l&agrave; Th&aacute;i Ất Huyền Ti&ecirc;n.<br />\r\n<br />\r\nM&agrave; lại ph&aacute;p lực h&ugrave;ng hậu ngưng thực, nền m&oacute;ng chắc cố v&ocirc; c&ugrave;ng.<br />\r\n<br />\r\nNh&igrave;n qua ph&iacute;a dưới mặt kia ph&iacute;a tr&ecirc;n c&ograve;n mang theo điểm phiền muộn b&oacute;ng người, Th&ocirc;ng Thi&ecirc;n cảm gi&aacute;c m&igrave;nh nhận biết muốn bị lật đổ.<br />\r\n<br />\r\nSinh linh tu luyện thần tốc, cố nhi&ecirc;n rung động t&acirc;m thần.<br />\r\n<br />\r\nThế nhưng cũng chỉ l&agrave; khiến người ta cảm th&aacute;n sinh linh ph&uacute;c phận th&acirc;m hậu, thi&ecirc;n ph&uacute; si&ecirc;u ph&agrave;m.<br />\r\n<br />\r\nNhưng trước mắt tiểu ngưu y&ecirc;u, ho&agrave;n to&agrave;n kh&ocirc;ng phải c&oacute; chuyện như vậy.<br />\r\n<br />\r\nC&aacute;i n&agrave;y năm trăm năm, hắn c&aacute;i kia sợi thần niệm th&igrave; kh&ocirc;ng hề rời đi qua tiểu ngưu y&ecirc;u.<br />\r\n<br />\r\nGia hỏa n&agrave;y ngủ năm trăm năm, cho tới b&acirc;y giờ liền kh&ocirc;ng c&oacute; tu luyện qua.<br />\r\n<br />\r\nNgủ một giấc, tỉnh lại trực tiếp bước v&agrave;o Th&aacute;i Ất Huyền Ti&ecirc;n.<br />\r\n<br />\r\nQuan trọng c&ograve;n kh&ocirc;ng c&oacute; g&acirc;y n&ecirc;n một điểm động tĩnh.<br />\r\n<br />\r\nLiền hắn c&aacute;i n&agrave;y chăm ch&uacute; ch&uacute; &yacute; đối phương Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n, đều kh&ocirc;ng c&oacute; ph&aacute;t gi&aacute;c được một ch&uacute;t dấu hiệu.<br />\r\n<br />\r\nHắn thậm ch&iacute; kh&ocirc;ng biết đối phương c&aacute;i g&igrave; thời điểm đột ph&aacute;.<br />\r\n<br />\r\nC&aacute;i n&agrave;y căn bản liền kh&ocirc;ng khả năng!<br />\r\n<br />\r\nSinh linh tăng cao tu vi, hoặc l&agrave; tiềm tu cảm ngộ tự nhi&ecirc;n, thu nạp thi&ecirc;n địa linh kh&iacute;, hoặc l&agrave; nuốt đan dược linh vật.<br />\r\n<br />\r\nKh&ocirc;ng phải vậy ở đ&acirc;u ra năng lượng chuyển h&oacute;a th&agrave;nh ph&aacute;p lực.<br />\r\n<br />\r\nC&oacute; thể gia hỏa n&agrave;y mạc danh kỳ diệu thể nội ph&aacute;p lực b&ugrave;ng l&ecirc;n.<br />\r\n<br />\r\nC&ograve;n lừa gạt được hắn c&aacute;i n&agrave;y Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n.<br />\r\n<br />\r\nChuyện thế n&agrave;y, đừng n&oacute;i gặp qua, cũng l&agrave; nghe đều chưa từng nghe qua.<br />\r\n<br />\r\nHắn sống nhiều năm như vậy, lần đầu nh&igrave;n thấy chuyện thế n&agrave;y.<br />\r\n<br />\r\nHơn nữa nh&igrave;n đối phương c&aacute;i kia phiền muộn biểu lộ, tựa hồ đối với ph&aacute; vỡ m&agrave; v&agrave;o Th&aacute;i Ất Huyền Ti&ecirc;n c&ograve;n bất m&atilde;n v&ocirc; c&ugrave;ng.<br />\r\n<br />\r\nThời gian năm trăm năm, ngủ c&aacute;i cảm gi&aacute;c, trực tiếp từ Huyền Ti&ecirc;n trung kỳ bước v&agrave;o Th&aacute;i Ất Huyền Ti&ecirc;n sơ kỳ, lại c&ograve;n kh&ocirc;ng cao hứng.<br />\r\n<br />\r\nC&aacute;i n&agrave;y c&ograve;n c&oacute; thi&ecirc;n l&yacute; hay kh&ocirc;ng!<br />\r\n<br />\r\n&quot;H&ocirc;!&quot;<br />\r\n<br />\r\n&quot;V&ocirc; Lượng Thi&ecirc;n T&ocirc;n! C&aacute;i n&agrave;y tiểu ngưu y&ecirc;u đến c&ugrave;ng l&agrave; thứ đồ g&igrave;.&quot;<br />\r\n<br />\r\nL&uacute;c trước th&ocirc;i to&aacute;n kh&ocirc;ng ra căn nguy&ecirc;n, l&uacute;c n&agrave;y đột ph&aacute; lại bực n&agrave;y quỷ dị.<br />\r\n<br />\r\nTh&ocirc;ng Thi&ecirc;n theo kh&ocirc;ng nghĩ tới, hắn m&ocirc;n nh&acirc;n đệ tử b&ecirc;n trong lại c&oacute; bực n&agrave;y kỳ lạ sinh linh.<br />\r\n<br />\r\nKh&ocirc;ng chỉ c&oacute; thể hiểu r&otilde; tương lai, tu luyện lại qu&aacute;i dị như vậy.<br />\r\n<br />\r\nGiống như b&iacute; ẩn đồng dạng.<br />\r\n<br />\r\n&quot;Như thế nghịch thi&ecirc;n ph&uacute;c phận, n&oacute;i c&acirc;u đại đạo chiếu cố cũng kh&ocirc;ng đủ.&quot;<br />\r\n<br />\r\n&quot;Đại đạo chiếu cố. . . .&quot;<br />\r\n<br />\r\n&quot;T&ecirc; ~~ &quot;<br />\r\n<br />\r\nĐột nhi&ecirc;n, Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ tựa hồ nghĩ tới điều g&igrave;, bản năng t&acirc;m thần h&iacute;t v&agrave;o một ngụm kh&iacute; lạnh.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nNgồi ngay ngắn bồ đo&agrave;n b&ecirc;n tr&ecirc;n hắn, cũng nhịn kh&ocirc;ng được khẽ run l&ecirc;n.<br />\r\n<br />\r\nTh&ocirc;ng Thi&ecirc;n t&acirc;m thần k&iacute;ch động, trong đ&ocirc;i mắt kim quang đại thịnh.<br />\r\n<br />\r\n&quot;C&oacute; thể hiểu r&otilde; thi&ecirc;n cơ, c&ograve;n tu luyện như thế thần tốc, c&aacute;i n&agrave;y tiểu ngưu y&ecirc;u rất c&oacute; thể cũng l&agrave; một vị kh&aacute;c đại đạo chi tử.&quot;<br />\r\n<br />\r\n&quot;C&ugrave;ng B&agrave;n Cổ phụ thần một dạng đại đạo chi tử, thụ đại đạo chiếu cố người.&quot;<br />\r\n<br />\r\n&quot;Thừa đạo người, tự người phi thường biết.&quot;<br />\r\n<br />\r\n&quot;C&oacute; c&aacute;i n&agrave;y tiểu ngưu y&ecirc;u tại, ta Tiệt Gi&aacute;o tất nhi&ecirc;n có th&ecirc;̉ gặp dữ h&oacute;a l&agrave;nh, c&ograve;n c&oacute; thể Trường Hưng kh&ocirc;ng suy.&quot;<br />\r\n<br />\r\n&quot;Nhất định kh&ocirc;ng thể để cho c&aacute;i n&agrave;y tiểu ngưu y&ecirc;u chạy.&quot;<br />\r\n<br />\r\nNh&igrave;n qua dưới tay đạo th&acirc;n ảnh kia, Th&ocirc;ng Thi&ecirc;n th&acirc;n h&igrave;nh k&iacute;ch động.<br />\r\n<br />\r\nCả người v&ocirc; c&ugrave;ng hưng phấn!<br />\r\n<br />\r\nGiờ khắc n&agrave;y, tất cả nghi hoặc đều giải khai.<br />\r\n<br />\r\nKh&oacute; tr&aacute;ch l&uacute;c trước th&ocirc;i diễn c&aacute;i n&agrave;y tiểu ngưu y&ecirc;u lai lịch, chỉ c&oacute; một mảnh m&ecirc; vụ.<br />\r\n<br />\r\nĐại đạo chi tử!<br />\r\n<br />\r\nHắn chưa từng c&oacute; nghĩ đến, hắn Tiệt Gi&aacute;o m&ocirc;n nh&acirc;n b&ecirc;n trong, lại c&oacute; một vị đại đạo chi tử.<br />\r\n<br />\r\nTrong l&ograve;ng chắc chắn Trường Phong th&acirc;n phận Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ, cũng bỏ đi trong đầu một c&aacute;i &yacute; niệm kh&aacute;c.<br />\r\n<br />\r\nTiểu ngưu y&ecirc;u c&aacute;i n&agrave;y li&ecirc;n tiếp chuyện qu&aacute;i dị, đ&atilde; sớm khiến Th&ocirc;ng Thi&ecirc;n l&ograve;ng sinh kinh nghi.<br />\r\n<br />\r\nNgoại trừ kh&ocirc;ng ngừng nghe l&eacute;n đối phương tiếng l&ograve;ng, hắn ho&agrave;n to&agrave;n c&oacute; thể trực tiếp sưu hồn.<br />\r\n<br />\r\nĐến mức v&igrave; sao kh&ocirc;ng c&oacute; trước ti&ecirc;n l&agrave;m như thế, tự nhi&ecirc;n l&agrave; th&acirc;n v&igrave; Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n cao ngạo, huống hồ tiểu ngưu y&ecirc;u c&ograve;n l&agrave; hắn m&ocirc;n nh&acirc;n.<br />\r\n<br />\r\nC&oacute; thể theo kh&ocirc;ng ngừng bạo chuyện xảy ra, Th&ocirc;ng Thi&ecirc;n đương nhi&ecirc;n cũng c&oacute; c&aacute;i n&agrave;y trực tiếp nhất phương ph&aacute;p.<br />\r\n<br />\r\nL&uacute;c n&agrave;y tin tưởng vững chắc tiểu ngưu y&ecirc;u đại đạo chi tử th&acirc;n phận, hắn đ&atilde; mất bực n&agrave;y &yacute; nghĩ.<br />\r\n<br />\r\nĐại đạo chi tử, đ&acirc;y ch&iacute;nh l&agrave; c&ugrave;ng B&agrave;n Cổ phụ thần một dạng tồn tại.<br />\r\n<br />\r\nMạo muội động đến hắn, tuyệt đối sẽ c&ugrave;ng đại đạo kết xuống lớn lao nh&acirc;n quả.<br />\r\n<br />\r\nTh&aacute;nh Nh&acirc;n n&oacute;i l&agrave; nh&acirc;n quả kh&ocirc;ng d&iacute;nh, c&aacute;i kia chỉ l&agrave; bởi v&igrave; ch&uacute;ng sinh đều l&agrave; con kiến h&ocirc;i.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; tư c&aacute;ch c&ugrave;ng bọn hắn kết nh&acirc;n quả.<br />\r\n<br />\r\nNhưng đại đạo vậy nhưng kh&ocirc;ng đồng dạng.<br />\r\n<br />\r\nC&ugrave;ng đại đạo kết xuống nh&acirc;n quả, cũng l&agrave; hắn c&aacute;i n&agrave;y Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n cũng kh&ocirc;ng d&aacute;m.<br />\r\n<br />\r\nM&agrave; lại đối mặt đại đạo chi tử, hắn liền xem như sưu hồn cũng kh&ocirc;ng nhất định sẽ c&oacute; kết quả.<br />\r\n<br />\r\nD&ugrave; sao đại đạo chi tử kỳ lạ, hiển nhi&ecirc;n kh&ocirc;ng ngừng trước mắt điểm n&agrave;y.<br />\r\n<br />\r\nPh&iacute;a dưới Trường Phong, nhưng kh&ocirc;ng biết Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ đ&atilde; đem hắn trở th&agrave;nh đại đạo chi tử.<br />\r\n<br />\r\nNgủ năm trăm năm hắn, lại bắt đầu vui vẻ thưởng thức xa xa Tam Ti&ecirc;u.</p>', '2021-11-27 21:10:20', 5, 1),
(169, 44, 'Ha ha! Chuồn đi! Chuồn đi!', '5', '<p>【 Tam Ti&ecirc;u l&agrave; thật mỹ a, nếu c&oacute; thể mang theo c&aacute;c n&agrave;ng c&ugrave;ng một chỗ chạy liền tốt. 】<br />\r\n<br />\r\nĐương nhi&ecirc;n bực n&agrave;y &yacute; nghĩ, chỉ c&oacute; thể Trường Phong m&igrave;nh tại thầm nghĩ trong l&ograve;ng.<br />\r\n<br />\r\nD&ugrave; sao đại thế kh&ocirc;ng thể tr&aacute;i.<br />\r\n<br />\r\nM&agrave; lại l&uacute;c n&agrave;y Tam Ti&ecirc;u, th&acirc;n l&agrave; Th&ocirc;ng Thi&ecirc;n đệ tử th&acirc;n truyền, thực lực cũng kh&ocirc;ng phải hắn hiện tại c&oacute; thể s&aacute;nh được.<br />\r\n<br />\r\n&quot;Ngươi c&aacute;i n&agrave;y tiểu ngưu y&ecirc;u, bản tọa đ&atilde; biết được ngươi suy nghĩ trong l&ograve;ng, ngươi c&ograve;n muốn chạy?&quot;<br />\r\n<br />\r\nNghe trong đầu truyền đến thanh &acirc;m, Th&ocirc;ng Thi&ecirc;n t&acirc;m t&igrave;nh v&ocirc; c&ugrave;ng vui vẻ.<br />\r\n<br />\r\nNghi ngờ trong l&ograve;ng c&ugrave;ng nhau giải khai, m&ocirc;n hạ c&ograve;n ra một c&aacute;i đại đạo chi tử.<br />\r\n<br />\r\nCh&iacute;nh m&igrave;nh có th&ecirc;̉ đ&aacute;nh cắp đối phương &yacute; nghĩ, quan trọng tiểu ngưu y&ecirc;u c&ograve;n một ch&uacute;t kh&ocirc;ng biết r&otilde; t&igrave;nh h&igrave;nh.<br />\r\n<br />\r\nLoại cảm gi&aacute;c n&agrave;y si&ecirc;u thoải m&aacute;i.<br />\r\n<br />\r\n&Aacute;nh mắt x&eacute;o qua qu&eacute;t mắt tuyến ngo&agrave;i c&ugrave;ng Tam Ti&ecirc;u, Th&ocirc;ng Thi&ecirc;n hai con mắt h&iacute;p lại, trong l&ograve;ng tựa hồ c&oacute; chỗ t&iacute;nh to&aacute;n.<br />\r\n<br />\r\nTiếp xuống t&igrave;nh cảnh, th&agrave;nh như Th&ocirc;ng Thi&ecirc;n sở liệu đồng dạng.<br />\r\n<br />\r\nTiểu ngưu y&ecirc;u vẫn kh&ocirc;ng c&oacute; tu luyện, thật tốt &#39;Thưởng thức&#39; Tam Ti&ecirc;u về sau, lại ngồi c&aacute;i kia đi ngủ đ&acirc;y.<br />\r\n<br />\r\nĐối mặt c&aacute;i n&agrave;y c&aacute;c loại t&igrave;nh huống, Th&ocirc;ng Thi&ecirc;n hiện tại đ&atilde; nhắm mắt l&agrave;m ngơ.<br />\r\n<br />\r\nNhận đại đạo chiếu cố, tu luyện xong to&agrave;n cũng l&agrave; ăn cơm uống nước đồng dạng đơn giản.<br />\r\n<br />\r\nTại đại đạo chi tử m&agrave; n&oacute;i, bọn họ cần chỉ l&agrave; thời gian.<br />\r\n<br />\r\nĐ&atilde; đến giờ, cảnh giới tự nhi&ecirc;n nước chảy th&agrave;nh s&ocirc;ng.<br />\r\n<br />\r\nLượn lờ đạo &acirc;m vờn quanh, nồng đậm kh&ocirc;ng rời đạo vận, bao phủ to&agrave;n bộ Kim Miết đảo.<br />\r\n<br />\r\nV&ocirc; tận kim li&ecirc;n chuỗi ngọc, đến sơn h&agrave; hồ nước ở giữa hiện l&ecirc;n.<br />\r\n<br />\r\nTử kh&iacute; d&acirc;ng tr&agrave;o ở giữa, c&agrave;ng c&oacute; đ&ocirc;ng đảo sinh linh khai linh tr&iacute;.<br />\r\n<br />\r\nS&aacute;ng ch&oacute;i tử h&agrave; b&ecirc;n trong, thường c&oacute; đột ph&aacute; ba động tạo n&ecirc;n.<br />\r\n<br />\r\nHồng Hoang kh&ocirc;ng c&aacute;i năm, ch&oacute;i mắt lại l&agrave; 500 năm.<br />\r\n<br />\r\n【 Ồ! Ng&agrave;n năm, sư t&ocirc;n l&agrave;m sao c&ograve;n đang giảng đạo? 】<br />\r\n<br />\r\nĐ&atilde; tỉnh lại Trường Phong, nghe kh&ocirc;ng trung quanh quẩn đạo &acirc;m, trong l&ograve;ng thầm nhủ.<br />\r\n<br />\r\nTuy nhi&ecirc;n c&ugrave;ng Kim Miết đảo ph&iacute;a tr&ecirc;n c&ograve;n lại m&ocirc;n nh&acirc;n tương giao kh&ocirc;ng nhiều, nhưng hắn cũng biết, trước mấy lần trước sư t&ocirc;n giảng đạo đều l&agrave; thời gian ng&agrave;n năm.<br />\r\n<br />\r\nB&acirc;y giờ thời gian ng&agrave;n năm đ&atilde; qua, sư t&ocirc;n thế m&agrave; kh&ocirc;ng c&oacute; dừng lại.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Bản tọa t&acirc;m t&igrave;nh tốt, n&oacute;i nhiều c&aacute;i một ng&agrave;n năm!&quot;<br />\r\n<br />\r\nTh&ocirc;ng Thi&ecirc;n tự nhi&ecirc;n nghe được Trường Phong tiếng l&ograve;ng, thần sắc lạnh nhạt.<br />\r\n<br />\r\nĐương nhi&ecirc;n s&acirc;u trong đ&aacute;y l&ograve;ng, cũng c&oacute; Trường Phong trong tin tức mang đến &aacute;p lực nguy&ecirc;n nh&acirc;n.<br />\r\n<br />\r\nLượng kiếp kh&ocirc;ng biết chừng n&agrave;o th&igrave; bắt đầu, hắn cũng muốn tận khả năng nhiều đề cao m&ocirc;n hạ một đ&aacute;m tu vi.<br />\r\n<br />\r\nỞ tr&ecirc;n đảo c&ograve;n lại m&ocirc;n nh&acirc;n cũng kh&ocirc;ng từng biết được, cả đ&aacute;m đều say m&ecirc; b&ecirc;n trong, c&aacute;i n&agrave;o c&oacute; kh&aacute;i niệm thời gian.<br />\r\n<br />\r\nNh&igrave;n coi Th&ocirc;ng Thi&ecirc;n, lại nh&igrave;n xung quanh, Trường Phong đ&agrave;nh phải bất đắc dĩ tiếp nhận.<br />\r\n<br />\r\nTiện tay mở ra hệ thống m&agrave;n h&igrave;nh.<br />\r\n<br />\r\nĐạo hạnh: 9125<br />\r\n<br />\r\nTăng th&ecirc;m hơn một ngh&igrave;n năm đạo hạnh, bước v&agrave;o Th&aacute;i Ất Huyền Ti&ecirc;n trung kỳ, lại ti&ecirc;u hao 6000 năm đạo hạnh, bước v&agrave;o Th&aacute;i Ất Huyền Ti&ecirc;n hậu kỳ.<br />\r\n<br />\r\nNằm ngửa hệ thống<br />\r\n<br />\r\nK&iacute; chủ: Trường Phong<br />\r\n<br />\r\nCảnh giới: Th&aacute;i Ất Huyền Ti&ecirc;n hậu kỳ (1507 - 7000)<br />\r\n<br />\r\nĐạo hạnh: 0<br />\r\n<br />\r\n【 thời gian ng&agrave;n năm bước v&agrave;o Kim Ti&ecirc;n quả nhi&ecirc;n c&oacute; chút độ kh&oacute; khăn, vừa vặn c&oacute; thể lại nằm nằm. 】<br />\r\n<br />\r\n【 nghe đạo một ng&agrave;n năm, chung quanh đồng m&ocirc;n hoặc nhiều hoặc &iacute;t đều c&oacute; chút đột ph&aacute;, ta cũng kh&ocirc;ng thể quá đặc th&ugrave;, đ&atilde; đột ph&aacute; đến Thi&ecirc;n Ti&ecirc;n trung kỳ đi. 】<br />\r\n<br />\r\nTrong đầu hai &acirc;m thanh vang l&ecirc;n, lập tức Th&ocirc;ng Thi&ecirc;n th&igrave; ph&aacute;t gi&aacute;c được Trường Phong vị tr&iacute;, một cỗ yếu ớt ba động truyền đến.<br />\r\n<br />\r\nĐưa mắt nh&igrave;n lại, tiểu ngưu y&ecirc;u cảnh giới nh&igrave;n qua biến th&agrave;nh Thi&ecirc;n Ti&ecirc;n trung kỳ.<br />\r\n<br />\r\nNhưng cẩn thận cảm gi&aacute;c trong cơ thể hắn ph&aacute;p lực, đ&atilde; đến Th&aacute;i Ất Huyền Ti&ecirc;n hậu kỳ.<br />\r\n<br />\r\nTiểu ngưu y&ecirc;u mặt ngo&agrave;i cảnh giới quả nhi&ecirc;n l&agrave; giả.<br />\r\n<br />\r\nT&igrave;nh cảnh trước mắt, cũng c&agrave;ng th&ecirc;m x&aacute;c nhận Th&ocirc;ng Thi&ecirc;n trong l&ograve;ng suy đo&aacute;n.<br />\r\n<br />\r\nNhư nếu kh&ocirc;ng phải đại đạo chi tử th&acirc;n phận, sao c&oacute; thể tuỳ tiện che đậy hắn vị n&agrave;y Th&aacute;nh Nh&acirc;n.<br />\r\n<br />\r\n&quot;Ngươi c&aacute;i n&agrave;y tiểu ngưu y&ecirc;u c&ograve;n trang, may mắn bản tọa đ&atilde; biết được ngươi căn nguy&ecirc;n.&quot;<br />\r\n<br />\r\nLoại n&agrave;y nghe l&eacute;n người kh&aacute;c tiếng l&ograve;ng cảm gi&aacute;c, khiến Th&ocirc;ng Thi&ecirc;n v&ocirc; c&ugrave;ng vui vẻ, quan trọng đối phương vẫn l&agrave; một c&aacute;i đại đạo chi tử.<br />\r\n<br />\r\n【 Ồ! L&agrave;m sao c&oacute; loại cảm gi&aacute;c bị người d&ograve;m ng&oacute;. 】<br />\r\n<br />\r\n【 chẳng lẽ đưa tới sư t&ocirc;n ch&uacute; &yacute;? Kh&ocirc;ng c&oacute; đạo l&yacute; a, ta ngoại trừ đẹp trai, đ&atilde; b&igrave;nh thường kh&ocirc;ng thể lại b&igrave;nh thường. 】<br />\r\n<br />\r\n【 muốn đến hẳn l&agrave; đột ph&aacute; ba động, đưa tới sư t&ocirc;n &aacute;nh mắt. 】<br />\r\n<br />\r\nC&aacute;i n&agrave;y li&ecirc;n tiếp thanh &acirc;m, kh&ocirc;ng hề nghi ngờ tiến nhập Th&ocirc;ng Thi&ecirc;n trong tai.<br />\r\n<br />\r\nC&aacute;i n&agrave;y khiến hắn tho&aacute;ng c&oacute; chút xấu hổ.<br />\r\n<br />\r\nC&oacute; loại nh&igrave;n trộm bị bắt được cảm gi&aacute;c.<br />\r\n<br />\r\nHắn nhưng l&agrave; đường đường Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n, loại chuyện n&agrave;y muốn l&agrave; truyền đi, mặt mo đều muốn n&eacute;m sạch.<br />\r\n<br />\r\n&quot;Khụ khụ. . .&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y tiểu ngưu y&ecirc;u cảnh giới đề cao, xem ra kh&ocirc;ng c&oacute; thể t&ugrave;y &yacute; d&ograve; x&eacute;t trong cơ thể hắn ph&aacute;p lực.&quot;<br />\r\n<br />\r\nN&oacute;i thầm v&agrave;i tiếng Th&ocirc;ng Thi&ecirc;n, sắc mặt c&oacute; từng điểm từng điểm phiền muộn.<br />\r\n<br />\r\nCứ như vậy, hắn th&igrave; kh&ocirc;ng c&aacute;ch n&agrave;o biết được đối phương thực lực cụ thể.<br />\r\n<br />\r\nBất qu&aacute; Th&ocirc;ng Thi&ecirc;n cũng kh&ocirc;ng th&egrave;m để &yacute;, chỉ muốn gắt gao đem tiểu ngưu y&ecirc;u cột v&agrave;o Tiệt Gi&aacute;o c&acirc;y to n&agrave;y ph&iacute;a tr&ecirc;n l&agrave; đủ rồi.<br />\r\n<br />\r\n&Aacute;nh mắt chuyển đi, c&aacute;i kia tiểu ngưu y&ecirc;u quả nhi&ecirc;n lại bắt đầu ngủ.<br />\r\n<br />\r\nNgủ một ch&uacute;t, cảnh giới liền t&ugrave;y t&ugrave;y tiện tiện đột ph&aacute;, liền hắn c&aacute;i n&agrave;y Th&aacute;nh Nh&acirc;n đều c&oacute; từng điểm từng điểm h&acirc;m mộ.<br />\r\n<br />\r\nThời gian chậm r&atilde;i tr&ocirc;i qua, Trường Phong thường c&oacute; tỉnh lại, nh&igrave;n thấy giảng đạo kh&ocirc;ng c&oacute; kết th&uacute;c, chỉ c&oacute; thể tiếp tục ngủ.<br />\r\n<br />\r\nĐi v&agrave;o phương thế giới n&agrave;y về sau, hắn l&agrave;m được nhiều nhất sự t&igrave;nh ngay cả khi ngủ, tự nhi&ecirc;n thuận buồm xu&ocirc;i gi&oacute;.<br />\r\n<br />\r\nỞ tr&ecirc;n đảo c&ograve;n lại m&ocirc;n nh&acirc;n, nhưng liền kh&ocirc;ng c&oacute; Trường Phong như thế lạnh nhạt.<br />\r\n<br />\r\nKh&ocirc;ng &iacute;t nửa đường tỉnh lại người, gặp Th&ocirc;ng Thi&ecirc;n c&ograve;n đang giảng đạo, đều l&agrave; sắc mặt đại hỉ.<br />\r\n<br />\r\nBọn họ tuy nhi&ecirc;n đắm ch&igrave;m trong giảng đạo b&ecirc;n trong, nhưng đồng dạng minh bạch sớm đ&atilde; đi qua thời gian ng&agrave;n năm.<br />\r\n<br />\r\nTh&ocirc;ng trời mặc d&ugrave; l&agrave; Tiệt Gi&aacute;o gi&aacute;o chủ, nhưng vẫn l&agrave; Thi&ecirc;n Đạo Th&aacute;nh Nh&acirc;n.<br />\r\n<br />\r\nMỗi một lần giảng đạo đều v&ocirc; c&ugrave;ng tr&acirc;n qu&yacute;.<br />\r\n<br />\r\nCứ như vậy, lại một ng&agrave;n năm tr&ocirc;i qua.<br />\r\n<br />\r\nR&ograve;ng r&atilde; thời gian hai ng&agrave;n năm, bỗng nhi&ecirc;n kh&ocirc;ng trung đạo &acirc;m im bặt m&agrave; dừng, lại l&agrave; Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ đ&igrave;nh chỉ giảng đạo.<br />\r\n<br />\r\nKim Miết đảo ph&iacute;a tr&ecirc;n đ&ocirc;ng đảo m&ocirc;n nh&acirc;n, đều l&agrave; theo như si như say b&ecirc;n trong tỉnh t&aacute;o lại.<br />\r\n<br />\r\nSau đ&oacute; nguy&ecirc;n một đ&aacute;m tr&ocirc;ng mong nh&igrave;n qua Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ, hi vọng hắn tiếp tục giảng đạo.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nD&ugrave; sao c&aacute;i n&agrave;y thời gian hai ng&agrave;n năm, cơ bản mỗi c&aacute;i hoặc nhiều hoặc &iacute;t đều c&oacute; thu hoạch.<br />\r\n<br />\r\nNgồi ngay ngắn kh&ocirc;ng trung bồ đo&agrave;n b&ecirc;n tr&ecirc;n Th&ocirc;ng Thi&ecirc;n gi&aacute;o chủ, mặt kh&ocirc;ng biểu t&igrave;nh, như c&oacute; như kh&ocirc;ng đạo &acirc;m truyền ra.<br />\r\n<br />\r\n&quot;Hai ng&agrave;n năm giảng đạo thời gian đ&atilde; qua, c&aacute;c ngươi đều mỗi người trở về động phủ, thật tốt thể ngộ bản tọa lần n&agrave;y giảng đại đạo, kh&ocirc;ng được lười biếng!&quot;<br />\r\n<br />\r\n&quot;Cẩn tu&acirc;n sư t&ocirc;n ph&aacute;p chỉ!&quot;<br />\r\n<br />\r\nRất nhiều Tiệt Gi&aacute;o m&ocirc;n nh&acirc;n tuy nhi&ecirc;n trong l&ograve;ng kh&ocirc;ng muốn, nhưng sư t&ocirc;n đ&atilde; mở miệng, tự nhi&ecirc;n kh&ocirc;ng d&aacute;m nhiều lời.<br />\r\n<br />\r\nNgủ b&ecirc;n trong Trường Phong, tự nhi&ecirc;n cũng đ&atilde; tỉnh lại.<br />\r\n<br />\r\nSo với chung quanh đ&ocirc;ng đảo lưu luyến kh&ocirc;ng rời đồng m&ocirc;n, hắn lại l&agrave; phi thường vui vẻ.<br />\r\n<br />\r\n【 cuối c&ugrave;ng kết th&uacute;c, chuồn đi, chuồn đi! Kim Miết đảo, b&aacute;i bai! 】<br />\r\n<br />\r\nĐợi l&acirc;u một ng&agrave;n năm, Trường Phong đ&atilde; sớm kh&ocirc;ng ki&ecirc;n nhẫn được nữa.<br />\r\n<br />\r\nC&aacute;i n&agrave;y thời gian ng&agrave;n năm t&iacute;ch lũy đạo hạnh, đ&atilde; đầy đủ hắn bước v&agrave;o Kim Ti&ecirc;n.<br />\r\n<br />\r\nKim Ti&ecirc;n tuy nhi&ecirc;n c&ograve;n nhỏ yếu, nhưng ở l&uacute;c n&agrave;y Hồng Hoang b&ecirc;n trong tự vệ vẫn l&agrave; kh&ocirc;ng nhiều lắm vấn đề.<br />\r\n<br />\r\n【 sư t&ocirc;n! Kh&ocirc;ng phải ta Trường Phong kh&ocirc;ng tử tế, đại thế kh&ocirc;ng thể tr&aacute;i, ta đ&acirc;y l&agrave; tại cho Tiệt Gi&aacute;o chừa ch&uacute;t đốm lửa nhỏ. 】<br />\r\n<br />\r\n【 rời đi Kim Miết đảo, ta th&igrave; t&igrave;m một chỗ thật tốt nằm, đi Nh&acirc;n tộc nằm lăn lộn đến hậu thế, l&agrave;m một người l&atilde;o thần ti&ecirc;n như thế n&agrave;o? 】<br />\r\n<br />\r\n【 kh&ocirc;ng được! Kh&ocirc;ng được! Tam Ho&agrave;ng Ngũ Đế c&ograve;n chưa bắt đầu, Nh&acirc;n tộc sắp l&agrave; mấy vị Th&aacute;nh Nh&acirc;n ch&uacute; &yacute; điểm, qu&aacute; l&agrave;m người kh&aacute;c ch&uacute; &yacute;. 】<br />\r\n<br />\r\n【 xoắn xu&yacute;t nhiều như vậy l&agrave;m g&igrave;, d&ugrave; sao ta nằm c&aacute;i n&agrave;o đều c&oacute; thể mạnh l&ecirc;n, t&ugrave;y tiện t&igrave;m c&aacute;i g&ograve; n&uacute;i nhỏ nằm l&agrave; được. 】<br />\r\n<br />\r\nVỗ vỗ c&aacute;i m&ocirc;ng Trường Phong, trượt b&ograve; l&ecirc;n.<br />\r\n<br />\r\nNh&igrave;n chằm chằm xa xa Tam Ti&ecirc;u, th&igrave; muốn ly khai.<br />\r\n<br />\r\n【 Tam Ti&ecirc;u ba vị sư tỷ, xem ra ch&uacute;ng ta nhất định l&agrave; hữu duy&ecirc;n v&ocirc; phận đi 】<br />\r\n<br />\r\nTrong l&ograve;ng thăm thẳm vứt xuống một c&acirc;u lời n&oacute;i, Trường Phong nh&uacute;n người nhảy l&ecirc;n, liền muốn hướng về bầu trời ph&iacute;a tr&ecirc;n bay đi.<br />\r\n<br />\r\n&quot;Tiểu ngưu y&ecirc;u, Trường Nhĩ Định Quang Ti&ecirc;n c&ugrave;ng Đa Bảo mấy vị đệ tử th&acirc;n truyền đến bản tọa B&iacute;ch Du cung một chuyến.&quot;<br />\r\n<br />\r\nN&agrave;o biết vừa mới bước ra một bước, một đạo như c&oacute; như kh&ocirc;ng đạo &acirc;m truyền đến.<br />\r\n<br />\r\nThanh &acirc;m kh&ocirc;ng lớn, lại bao phủ chung quanh mấy trăm vạn dặm khoảng c&aacute;ch.<br />\r\n<br />\r\nTrong lời n&oacute;i, mang theo kh&ocirc;ng thể nghi ngờ.</p>', '2021-11-27 21:10:50', 6, 1),
(170, 47, 'Trăm phần trăm chân thực trò chơi là có nhiều chân thực?', '1', '<p>&quot;. . . Trăm phần trăm ch&acirc;n thực ho&agrave;n to&agrave;n đắm ch&igrave;m thức tr&ograve; chơi l&agrave; c&oacute; nhiều ch&acirc;n thực?&quot;<br />\r\n<br />\r\nĐịa Cầu, đại học n&agrave;o đ&oacute; ph&ograve;ng m&aacute;y.<br />\r\n<br />\r\nNh&igrave;n xem group chat tin tức, Diệp Vĩ trong đầu trước ti&ecirc;n hiển hiện h&igrave;nh tượng kh&ocirc;ng phải tr&ograve; chơi, m&agrave; l&agrave; trắng b&oacute;ng đ&ugrave;i c&ugrave;ng ch&iacute;nh nghĩa.<br />\r\n<br />\r\nHướng nhỏ n&oacute;i, liền l&agrave; c&aacute;i kia đ&aacute;nh 200+ Mod thiếu nữ Scroll V, c&ugrave;ng 100+ c&aacute;i tự tay b&oacute;p l&atilde;o b&agrave; từ lưu trữ b&ecirc;n trong sống lại. (Skyrim ^,..,^)<br />\r\n<br />\r\n<br />\r\nLại nhỏ một ch&uacute;t m&agrave; liền kh&ocirc;ng thể n&oacute;i, phải kh&ocirc;ng hiệu đ&ecirc;́n kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nHắn ở trong bầy biệt danh gọi thiếu nữ c&ocirc;ng địch đ&ecirc;m mười lần, bởi v&igrave; qu&aacute; d&agrave;i lại kh&ocirc;ng văn nh&atilde;, bầy l&atilde;o ca nh&oacute;m đều t&ecirc;n gọi tắt hắn Dạ Thập.<br />\r\n<br />\r\n<br />\r\n&quot;Liền l&agrave; mặt chữ tr&ecirc;n &yacute; nghĩa ch&acirc;n thực.&quot;<br />\r\n<br />\r\n&quot;Thị gi&aacute;c, th&iacute;nh gi&aacute;c, khứu gi&aacute;c, x&uacute;c gi&aacute;c, vị gi&aacute;c. . . Tất cả đều c&ugrave;ng thế giới hiện thực đồng dạng, thế giới tr&ograve; chơi thời gian lưu động c&ugrave;ng thế giới hiện thực l&agrave; 1: 1, ch&ecirc;nh lệch đại kh&aacute;i 12 giờ, đồng thời bởi v&igrave; kỹ thuật nguy&ecirc;n l&yacute; l&agrave; đối mộng cảnh can thiệp, cho n&ecirc;n tr&ograve; chơi l&uacute;c tương đương với đi ngủ.&quot;<br />\r\n<br />\r\nG&otilde; ra c&aacute;i n&agrave;y hai h&agrave;ng chữ l&atilde;o ca, biệt danh gọi &quot;Quang&quot; .<br />\r\n<br />\r\nC&aacute;i n&agrave;y chững chạc đ&agrave;ng ho&agrave;ng một chữ độc nhất biệt danh, c&ugrave;ng &quot;Ngưu M&atilde; c&acirc;u lạc bộ&quot; c&aacute;i n&agrave;y bầy danh hiển đ&ecirc;́n kh&ocirc;ng hợp nhau, cho n&ecirc;n bầy b&ecirc;n trong sa đi&ecirc;u bầy bạn nh&oacute;m, ngẫu nhi&ecirc;n cũng sẽ xưng h&ocirc; vị n&agrave;y l&atilde;o ca &quot;Si&ecirc;u Nh&acirc;n Đi&ecirc;̣n Quang&quot;, &quot;Gaia&quot;, thậm ch&iacute; l&agrave; khai ca hoặc l&agrave; ngưu bức.<br />\r\n<br />\r\n<br />\r\nCấp t&aacute;m lớn cuồng phong (nh&acirc;n vi&ecirc;n quản l&yacute;): &quot;Đ&acirc;y l&agrave; c&aacute;i g&igrave; ảo tưởng hệ thiết lập sao? (cười) &quot;<br />\r\n<br />\r\nC&ograve;n nhiều thời gian (nh&acirc;n vi&ecirc;n quản l&yacute;): &quot;M&agrave; lại nghe nhiều năm rồi, cảm gi&aacute;c giống như l&agrave; l&uacute;c đầu văn học mạng b&ecirc;n trong li&ecirc;n quan tới giả lập hiện thực tr&ograve; chơi thiết lập.&quot;<br />\r\n<br />\r\nThời gian qua nhanh (chủ nhóm): &quot;Ha ha, ngươi n&oacute;i l&agrave; đầu đội n&oacute;n tr&ograve; chơi ảo sao? Ta khi c&ograve;n đi học m&agrave; c&ograve;n thật th&iacute;ch nh&igrave;n.&quot;<br />\r\n<br />\r\nThiếu nữ c&ocirc;ng địch đ&ecirc;m mười lần (nh&acirc;n vi&ecirc;n quản l&yacute;): &quot;Giảng đạo l&yacute; loại n&agrave;y thiết lập liền kh&ocirc;ng hợp l&yacute; đi, tổng đ&agrave;i kh&ocirc;ng v&igrave; kiếm tiền, liền v&igrave; để c&aacute;c người chơi thoải m&aacute;i? Nếu thật l&agrave; trăm phần trăm ch&acirc;n thực, l&agrave;m sao thể hiện nh&acirc;n d&acirc;n tệ chiến sĩ t&ocirc;n qu&yacute;? Tất cả mọi người bạch chơi!&quot;<br />\r\n<br />\r\nDiệp Vĩ l&agrave; kh&ocirc;ng nh&igrave;n những n&agrave;y tiểu thuyết, nhưng hắn cảm thấy m&igrave;nh nhận qua ch&iacute;n năm gi&aacute;o dục bắt buộc, c&ugrave;ng người kh&aacute;c kh&ocirc;ng giống, so với thoải m&aacute;i c&agrave;ng hẳn l&agrave; truy cầu hợp l&yacute; t&iacute;nh.<br />\r\n<br />\r\nCho n&ecirc;n hắn c&agrave;ng th&iacute;ch xem huyền huyễn c&ugrave;ng linh dị.<br />\r\n<br />\r\nC&ograve;n nhiều thời gian: &quot;Huynh đắc, ngươi qu&aacute; nghi&ecirc;m t&uacute;c, thật giảng hợp l&yacute; t&iacute;nh, so với để trong tiểu thuyết hư cấu tổng đ&agrave;i kiếm được tiền, để tiểu thuyết b&ecirc;n ngo&agrave;i độc giả thoải m&aacute;i đến mới thật sự l&agrave; hợp l&yacute; a?&quot;<br />\r\n<br />\r\nCấp t&aacute;m lớn cuồng phong: &quot;Cấm chỉ b&uacute;p b&ecirc;.&quot;<br />\r\n<br />\r\nChủ đề rất chạy mau lệch.<br />\r\n<br />\r\nMặc d&ugrave; Ngưu M&atilde; c&acirc;u lạc bộ l&agrave; tr&ograve; chơi bầy, nhưng rất &iacute;t như h&ocirc;m nay dạng n&agrave;y nghi&ecirc;m trang thảo luận tr&ograve; chơi.<br />\r\n<br />\r\nNhất l&agrave; một c&aacute;i căn bản kh&ocirc;ng tồn tại, ho&agrave;n to&agrave;n trống rỗng tưởng tượng ra tới &quot;Tr&ograve; chơi&quot; .<br />\r\n<br />\r\nNhưng m&agrave;, cho d&ugrave; chủ đề đ&atilde; triệt để đi xa, nhưng c&aacute;i đề t&agrave;i n&agrave;y người đề xuất -- cũng ch&iacute;nh l&agrave; vị kia gọi &quot;Quang&quot; l&atilde;o ca, lại vẫn cố chấp đem chủ đề k&eacute;o lại.<br />\r\n<br />\r\nQuang: &quot;Ta n&oacute;i l&agrave; nếu như.&quot;<br />\r\n<br />\r\nQuang: &quot;Nếu như thật c&oacute; dạng n&agrave;y tr&ograve; chơi, c&aacute;c ngươi nguyện &yacute; chơi sao?&quot;<br />\r\n<br />\r\nNh&igrave;n xem group chat, Diệp Vĩ cười lắc đầu.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nC&aacute;i n&agrave;y c&ograve;n cần hỏi sao?<br />\r\n<br />\r\nThiếu nữ c&ocirc;ng địch đ&ecirc;m mười lần: &quot;Nguyện &yacute; a! V&igrave; sao kh&ocirc;ng nguyện &yacute;? Trăm phần trăm ch&acirc;n thực tr&ograve; chơi, l&agrave;m g&igrave; kh&ocirc;ng thử một ch&uacute;t, ngươi hiểu. (buồn cười) &quot;<br />\r\n<br />\r\nCấp t&aacute;m lớn cuồng phong: &quot;+1, bất qu&aacute; li&ecirc;n quan ngươi n&oacute;i thiết lập, ta vẫn c&oacute; ch&uacute;t nghi vấn, nếu như tại tr&ograve; chơi thời gian chờ c&ugrave;ng với giấc ngủ thời gian. . . Loại kỹ thuật n&agrave;y ứng dụng đến trong c&ocirc;ng t&aacute;c kh&ocirc;ng phải c&agrave;ng hợp l&yacute; sao?&quot;<br />\r\n<br />\r\nThời gian qua nhanh: &quot;Ngọa t&agrave;o, ngươi l&agrave; ma quỷ sao? ? ?&quot;<br />\r\n<br />\r\nC&ograve;n nhiều thời gian: &quot;Vậy ta vẫn l&agrave; th&agrave;nh th&agrave;nh thật thật ngủ đi. (cười khổ) &quot;<br />\r\n<br />\r\nCai thuốc: &quot;C&aacute;c ngươi đ&ecirc;m h&ocirc;m khuya khoắt kh&ocirc;ng ngủ được, ở trong bầy nằm mơ đ&acirc;u.&quot;<br />\r\n<br />\r\n&quot;Chết cười ta.&quot;<br />\r\n<br />\r\nBầy b&ecirc;n trong nổi l&ecirc;n người c&agrave;ng ng&agrave;y c&agrave;ng nhiều.<br />\r\n<br />\r\nC&ograve;n c&oacute; ch&uacute;t người mặc d&ugrave; kh&ocirc;ng c&oacute; ph&aacute;t tin tức, nhưng cũng tại nhiều hứng th&uacute; d&ograve;m m&agrave;n h&igrave;nh.<br />\r\n<br />\r\nHai trăm người tr&ograve; chơi bầy, sinh động bầy vi&ecirc;n b&igrave;nh thường tại mười đến hai mươi c&aacute;i tả hữu, ngẫu nhi&ecirc;n cũng sẽ xuất hiện hai c&aacute;i &quot;Hắn nhận biết ngươi, nhưng ngươi kh&ocirc;ng biết hắn&quot; gương mặt lạ, hỏi một ch&uacute;t mới biết được d&ograve;m m&agrave;n h&igrave;nh hơn hai năm.<br />\r\n<br />\r\nC&aacute;i kia gọi Quang l&atilde;o ca lại l&agrave; kh&ocirc;ng để &yacute; tới, tiếp tục đắm ch&igrave;m trong thế giới của m&igrave;nh.<br />\r\n<br />\r\n&quot;Tr&ecirc;n thực tế, ta gần nhất nhập chức n&agrave;y nh&agrave; c&ocirc;ng ty, ngay tại nghi&ecirc;n cứu ph&aacute;t minh một c&aacute;i ho&agrave;n to&agrave;n đắm ch&igrave;m thức giả lập hiện thực v&otilde;ng du.&quot;<br />\r\n<br />\r\nN&aacute;o nhiệt group chat ngắn ngủi an tĩnh một hồi.<br />\r\n<br />\r\nNhưng rất nhanh, tựa như đ&atilde; hẹn, đụng tới li&ecirc;n tiếp tin tức.<br />\r\n<br />\r\n&quot;Ngọa t&agrave;o?&quot;<br />\r\n<br />\r\n&quot;Thật hay giả? !&quot;<br />\r\n<br />\r\n&quot;Tr&acirc;u a huynh đệ! Ta k&eacute;m ch&uacute;t đều tin. (buồn cười) &quot;<br />\r\n<br />\r\nDiệp Vĩ cảm thấy gia hỏa n&agrave;y kịch kh&ocirc;ng khỏi cũng qu&aacute; l&agrave; nhiều.<br />\r\n<br />\r\nHo&agrave;n to&agrave;n đắm ch&igrave;m thức giả lập hiện thực v&otilde;ng du?<br />\r\n<br />\r\nTrăm phần trăm ch&acirc;n thực?<br />\r\n<br />\r\nK&eacute;o con b&ecirc; đ&acirc;u.<br />\r\n<br />\r\nTựa như Cuồng Phong đại ca n&oacute;i, c&aacute;i đồ chơi n&agrave;y nếu thật l&agrave; tồn tại, sẽ lấy ra l&agrave;m tr&ograve; chơi? ? ?<br />\r\n<br />\r\n<br />\r\nĐương nhi&ecirc;n, trực tiếp d&ugrave;ng tại l&agrave;m việc lĩnh vực hắn kh&ocirc;ng qu&aacute; t&aacute;n đồng. Tại tưởng tượng của hắn b&ecirc;n trong, thật c&oacute; loại n&agrave;y ngưu bức kỹ thuật, khẳng định l&agrave; trước d&ugrave;ng tại lĩnh vực qu&acirc;n sự.<br />\r\n<br />\r\nLấy ra huấn luyện l&iacute;nh đặc chủng bao nhi&ecirc;u ngưu bức!<br />\r\n<br />\r\nVị kia gọi &quot;Quang&quot; l&atilde;o huynh kh&ocirc;ng lại n&oacute;i tiếp, chỉ l&agrave; n&eacute;m qua đến một c&aacute;i địa chỉ Internet.<br />\r\n<br />\r\nDiệp Vĩ tiện tay ấn mở, ph&aacute;t hiện lại l&agrave; một c&aacute;i tr&ograve; chơi Offical Website .<br />\r\n<br />\r\nOffical Website giao diện l&agrave;m kh&ocirc;ng ra thế n&agrave;o, nh&igrave;n xem giống như l&agrave; ngay cả mời trang tr&iacute; tiền đều ra kh&ocirc;ng dậy nổi, trống rỗng giao diện cũng kh&ocirc;ng c&oacute; bất kỳ c&aacute;i g&igrave; nội dung, chỉ c&oacute; trần truồng ba h&agrave;ng chữ.<br />\r\n<br />\r\n【 trăm phần trăm ch&acirc;n thực giả lập hiện thực tr&ograve; chơi -- &laquo; đ&acirc;́t ch&ecirc;́t OL &raquo; 】<br />\r\n<br />\r\n【 trước mắt hẹn trước nh&acirc;n số: 0 】<br />\r\n<br />\r\n【 hẹn trước: Yes/no 】<br />\r\n<br />\r\nDiệp Vĩ cười.<br />\r\n<br />\r\nKh&aacute; lắm, l&agrave;m nền như thế nửa ng&agrave;y, h&oacute;a ra l&agrave; quảng c&aacute;o a.<br />\r\n<br />\r\nCũng kh&ocirc;ng biết l&agrave; rau cải x&ocirc;i vẫn l&agrave; lưới chắn.<br />\r\n<br />\r\nBất qu&aacute; c&aacute;i n&agrave;y hẹn trước nh&acirc;n số ngược lại l&agrave; đủ ch&acirc;n thực, kh&ocirc;ng c&oacute; hư cấu c&aacute;i t&aacute;m chữ số online nh&acirc;n số, lại viết c&aacute;i ngay cả Tra Tra Huy đều đang chơi.<br />\r\n<br />\r\n&quot;Được, gia ngược lại muốn xem xem ngươi l&agrave; c&aacute;i g&igrave; tr&acirc;u ngựa.&quot;<br />\r\n<br />\r\nCầm con chuột điểm hạ yes.<br />\r\n<br />\r\nNhưng m&agrave; l&agrave;m hắn ngo&agrave;i &yacute; muốn ch&iacute;nh l&agrave;, trong dự đo&aacute;n đăng k&iacute; giao diện cũng kh&ocirc;ng c&oacute; bắn ra, ngoại trừ hẹn trước nh&acirc;n số từ 0 biến th&agrave;nh 1 b&ecirc;n ngo&agrave;i, liền kh&ocirc;ng c&oacute; bất kỳ biến h&oacute;a n&agrave;o.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y ph&aacute; tr&ograve; chơi ngay cả t&agrave;i khoản đều kh&ocirc;ng cần đăng k&iacute;? ? ?&quot;<br />\r\n<br />\r\nC&aacute;i n&agrave;y lẳng lơ thao t&aacute;c đem Diệp Vĩ cho chịu kh&ocirc;ng được, hắn trong chốc l&aacute;t cũng đắn đo kh&oacute; định, c&aacute;i đồ chơi n&agrave;y đến c&ugrave;ng c&oacute; t&iacute;nh kh&ocirc;ng quảng c&aacute;o.<br />\r\n<br />\r\nChẳng lẽ l&agrave; tr&uacute;ng độc?<br />\r\n<br />\r\nCũng rất kh&ocirc;ng c&oacute; khả năng.<br />\r\n<br />\r\nC&aacute;i n&agrave;y đều ni&ecirc;n đại g&igrave;, phần mềm diệt virus đều nhanh rời khỏi lịch sử v&otilde; đ&agrave;i, c&ograve;n c&oacute; loại kia điểm một ch&uacute;t trang web YES, NO liền c&oacute; thể l&acirc;y nhiễm virus?<br />\r\n<br />\r\nĐừng n&oacute;i l&agrave; ở trường học ph&ograve;ng m&aacute;y.<br />\r\n<br />\r\nLiền chỉ d&ugrave;ng của m&igrave;nh m&aacute;y t&iacute;nh, hắn cũng kh&ocirc;ng sợ c&aacute;i đồ chơi n&agrave;y.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\n【 hẹn trước nh&acirc;n số: 11 】<br />\r\n<br />\r\nĐ&acirc;́t ch&ecirc;́t, số 404 chỗ tr&aacute;nh nạn cư d&acirc;n đại sảnh.<br />\r\n<br />\r\nNh&igrave;n xem m&agrave;n ảnh m&aacute;y vi t&iacute;nh b&ecirc;n trong web page hậu trường số lượng, ngồi tại hẹp b&ecirc;n trong căn ph&ograve;ng nhỏ Sở Quang nhẹ nh&agrave;ng thở ra, bu&ocirc;ng lỏng ra nắm chắc con chuột.<br />\r\n<br />\r\n&quot;Dạng n&agrave;y l&agrave; được rồi sao?&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nTrả lời hắn, l&agrave; hai h&agrave;ng hiện l&ecirc;n ở trước mắt văn tự.<br />\r\n<br />\r\n【 nhiệm vụ ho&agrave;n th&agrave;nh. 】<br />\r\n<br />\r\n【 ban thưởng: Một bộ nano carbon &aacute;o l&oacute;t chống đạn, ban thưởng điểm số +5. 】<br />\r\n<br />\r\nGian ph&ograve;ng một b&ecirc;n v&aacute;ch tường ẩn ẩn chấn động, theo h&igrave;nh cung cửa hợp kim tấm mở ra, một kiện m&agrave;u đen sau lưng nằm tại cửa ph&iacute;a sau tr&ecirc;n b&igrave;nh đ&agrave;i.<br />\r\n<br />\r\nBăng chuyền đem sau lưng đưa ra.<br />\r\n<br />\r\nCửa hợp kim một lần nữa đ&oacute;ng lại, gian ph&ograve;ng cũng một lần nữa trở về b&igrave;nh tĩnh.<br />\r\n<br />\r\nSở Quang lập tức từ trước m&aacute;y vi t&iacute;nh đứng dậy, đi l&ecirc;n trước đem m&oacute;n kia sau lưng nhặt l&ecirc;n.<br />\r\n<br />\r\nTrọng lượng của n&oacute; rất nhẹ, cảm nhận cực kỳ giống băng tia &aacute;o ngủ, sờ l&ecirc;n tinh t&ecirc;́ tỉ mỉ mềm mại, rất kh&oacute; tưởng tượng c&aacute;i đồ chơi n&agrave;y c&oacute; thể chống cự đạn xạ k&iacute;ch.<br />\r\n<br />\r\n[<br />\r\n<br />\r\nT&ecirc;n: Nano carbon &aacute;o l&oacute;t chống đạn<br />\r\n<br />\r\nN&oacute;i r&otilde;: Nhưng c&oacute; hiệu hấp thu đạn động năng, tại bền bỉ trong phạm vi chịu đựng ph&acirc;n t&aacute;n lực tr&ugrave;ng k&iacute;ch, chống cự tr&igrave;nh độ nhất định đ&acirc;m xuy&ecirc;n, cắt ch&eacute;m tổn thương.<br />\r\n<br />\r\nBền bỉ: 100%<br />\r\n<br />\r\n】<br />\r\n<br />\r\nTừ chỗ tr&aacute;nh nạn hệ thống lấy được ban thưởng sẽ đăng k&yacute; tại thanh vật phẩm b&ecirc;n trong, bổ sung c&oacute; sản phẩm giới thiệu c&ugrave;ng sử dụng n&oacute;i r&otilde;.<br />\r\n<br />\r\nSở Quang lập tức cởi &aacute;o ra, đưa n&oacute; thiếp th&acirc;n mặc.<br />\r\n<br />\r\nNgay từ đầu c&oacute; ch&uacute;t lạnh sưu sưu, nhưng rất nhanh hắn liền th&iacute;ch ứng.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, đặt tại gian ph&ograve;ng nơi hẻo l&aacute;nh ống tr&ograve;n h&igrave;nh d&aacute;ng kim loại vật thể, ph&aacute;t ra kh&ocirc;ng c&oacute; cao thấp chập tr&ugrave;ng điện tử &acirc;m.<br />\r\n<br />\r\n&quot;Ch&uacute;c mừng ng&agrave;i, chủ nh&acirc;n, nh&igrave;n đến ng&agrave;i đ&atilde; ho&agrave;n th&agrave;nh nhiệm vụ thứ nhất.&quot;<br />\r\n<br />\r\n&quot;Ngươi c&oacute; thể đừng bất th&igrave;nh l&igrave;nh mở miệng sao?&quot;<br />\r\n<br />\r\n&quot;Được rồi chủ nh&acirc;n.&quot;<br />\r\n<br />\r\nC&aacute;i kia lớn l&ecirc;n giống giấy lộn c&aacute;i sọt người m&aacute;y, t&ecirc;n gọi Tiểu Thất.<br />\r\n<br />\r\nL&agrave;m số 404 chỗ tr&aacute;nh nạn người quản l&yacute; trợ l&yacute;, nghi&ecirc;m ngặt tr&ecirc;n &yacute; nghĩa tới n&oacute;i n&oacute; l&agrave; kh&ocirc;ng c&oacute; danh tự loại vật n&agrave;y, chỉ c&oacute; một c&aacute;i 777 số hiệu.<br />\r\n<br />\r\nNhưng Sở Quang ngại phiền phức, liền cho n&oacute; l&ecirc;n c&aacute;i t&ecirc;n.<br />\r\n<br />\r\nVề phần Sở Quang người thế n&agrave;o, v&igrave; sao lại tại c&aacute;i n&agrave;y số 404 chỗ tr&aacute;nh nạn b&ecirc;n trong, c&aacute;i kia c&ograve;n đ&ecirc;́n từ một c&aacute;i m&acirc;y đen gi&oacute; lớn ban đ&ecirc;m n&oacute;i l&ecirc;n. . .</p>', '2021-11-27 21:14:17', 23, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(171, 47, 'Số 404 chỗ tránh nạn', '2', '<p>Sở Quang, nam, tuổi mụ 26, tuổi nhỏ l&uacute;c phụ mẫu ly dị, từ nhỏ đi theo gia gia lớn l&ecirc;n.<br />\r\n<br />\r\nThi l&ecirc;n đại học về sau năm thứ hai, gia gia qua đời, hắn liền trở th&agrave;nh một người c&ocirc; đơn.<br />\r\n<br />\r\nMay m&agrave; sinh tại h&ograve;a b&igrave;nh ni&ecirc;n đại, vừa học vừa l&agrave;m cũng l&agrave; đem c&aacute;i n&agrave;y học tr&ecirc;n xong, sau khi tốt nghiệp t&igrave;m phần ti&ecirc;u thụ c&ocirc;ng việc. Bởi v&igrave; c&ocirc;ng việc biểu hiện xuất sắc, th&acirc;m thụ &ocirc;ng chủ thưởng thức, cũng kh&ocirc;ng l&acirc;u lắm liền lại lần nữa người n&acirc;ng l&ecirc;n chủ quản.<br />\r\n<br />\r\nKh&oacute; khăn nhất kia đoạn thời gian đều gắng gượng qua tới, ngay tại Sở Quang lạc quan cho rằng, cuối c&ugrave;ng c&oacute; thể qua mấy ng&agrave;y cuộc sống an dật thời điểm, hết lần n&agrave;y tới lần kh&aacute;c c&aacute;i n&agrave;y &ocirc;ng trời liền l&agrave; kh&ocirc;ng theo s&aacute;o lộ ra b&agrave;i.<br />\r\n<br />\r\nM&agrave; lại lần n&agrave;y, vận mệnh c&ugrave;ng hắn đ&ugrave;a giỡn c&oacute; ch&uacute;t lớn.<br />\r\n<br />\r\nĐại kh&aacute;i năm th&aacute;ng trước.<br />\r\n<br />\r\nSở Quang vẫn nhớ kỹ, khi đ&oacute; tại c&ugrave;ng hộ kh&aacute;ch đ&agrave;m một b&uacute;t hai ng&agrave;n vạn hợp đồng, đ&uacute;ng l&uacute;c gặp &ocirc;ng chủ tận hứng, tr&ecirc;n b&agrave;n rượu uống hơi nhiều, kh&ocirc;ng để &yacute; liền nằm.<br />\r\n<br />\r\nKhi hắn lại b&igrave;nh tĩnh lại đến, người đ&atilde; trải qua nằm tại một mảnh rừng n&uacute;i hoang vắng.<br />\r\n<br />\r\nM&agrave; lại, c&aacute;i n&agrave;y c&ograve;n kh&ocirc;ng phải phổ th&ocirc;ng rừng n&uacute;i hoang vắng.<br />\r\n<br />\r\nLi&ecirc;n tiếp đi hai dặm, hắn sửng sốt kh&ocirc;ng nh&igrave;n thấy một b&oacute;ng người kh&ocirc;ng n&oacute;i, ngược lại k&eacute;m ch&uacute;t bị một đầu mọc ra hai cái đ&acirc;̀u đại cẩu cho gặm.<br />\r\n<br />\r\nCũng may m&agrave; kia d&agrave;i hai cái đ&acirc;̀u s&uacute;c sinh.<br />\r\n<br />\r\nSở Quang l&uacute;c ấy bị h&ugrave; l&agrave; một th&acirc;n mồ h&ocirc;i lạnh, m&agrave; c&aacute;i n&agrave;y mồ h&ocirc;i vừa ra, rượu lập tức cũng tỉnh, quay người vắt ch&acirc;n l&ecirc;n cổ m&agrave; chạy.<br />\r\n<br />\r\nC&oacute; lẽ l&agrave; bởi v&igrave; hai cái đ&acirc;̀u qu&aacute; nặng, chạy l&ecirc;n đường tới kh&ocirc;ng c&acirc;n đối, con ch&oacute; kia đuổi hắn một hồi kh&ocirc;ng đuổi kịp, ngao ngao hai tiếng liền từ bỏ.<br />\r\n<br />\r\nNh&igrave;n kh&ocirc;ng thấy s&uacute;c sinh kia, Sở Quang cũng kh&ocirc;ng d&aacute;m đi trở về, c&agrave;ng kh&ocirc;ng biết n&ecirc;n đi đi đ&acirc;u.<br />\r\n<br />\r\nC&aacute;i n&agrave;y hắn mới ph&aacute;t hiện tr&ecirc;n người m&igrave;nh t&uacute;i tiền c&ugrave;ng điện thoại cũng bị mất, liền y phục đều bị đổi th&agrave;nh một th&acirc;n kh&ocirc;ng biết từ chỗ n&agrave;o nhặt được lam &aacute;o kho&aacute;c.<br />\r\n<br />\r\nT&igrave;m tới một chỗ vũng nước đọng, nhờ &aacute;nh trăng c&uacute;i đầu xem x&eacute;t, kh&aacute; lắm, m&igrave;nh c&aacute;i n&agrave;y kh&ocirc;ng chỉ đổi &aacute;o liền quần, ngay cả đầu đều đổi!<br />\r\n<br />\r\nCũng kh&ocirc;ng biết l&agrave; c&aacute;i n&agrave;o đầu &oacute;c c&oacute; bệnh gia hỏa đem m&igrave;nh m&ecirc; cho&aacute;ng, trả lại đổi khu&ocirc;n mặt, vứt xuống trước đ&acirc;y kh&ocirc;ng đến ph&iacute;a sau th&ocirc;n kh&ocirc;ng đến cửa h&agrave;ng địa phương quỷ qu&aacute;i.<br />\r\n<br />\r\nNgay tại Sở Quang ở tr&ecirc;n v&ugrave;ng hoang d&atilde; hoang mang lo sợ du đ&atilde;ng thời điểm, phương xa phi&ecirc;u khởi kh&oacute;i bếp hấp dẫn sự ch&uacute; &yacute; của hắn.<br />\r\n<br />\r\nHiện đang hồi tưởng lại đến, Sở Quang c&ograve;n c&oacute; ch&uacute;t nghĩ m&agrave; sợ.<br />\r\n<br />\r\nL&uacute;c ấy hắn tập trung tinh thần nghĩ đến b&aacute;o cảnh, liều lĩnh hướng ph&iacute;a kia một sợi người ở chạy tới, sau đ&oacute; ngẫm lại, may m&igrave;nh gặp ch&iacute;nh l&agrave; đường đường chính chính người sống s&oacute;t khu quần cư.<br />\r\n<br />\r\nĐổi lại l&agrave; những c&aacute;i kia giết người kh&ocirc;ng chớp mắt kẻ cướp đoạt, hay l&agrave; bộ tộc ăn thịt người, người biến dị bộ lạc c&aacute;i g&igrave;, chỉ sợ người hắn đ&atilde; trong nồi hầm lấy.<br />\r\n<br />\r\nL&agrave;m kh&ocirc;ng tốt ngay cả gi&agrave;y da biết r&otilde; hơn!<br />\r\n<br />\r\nC&ugrave;ng sinh hoạt ở nơi n&agrave;y người sống s&oacute;t tiếp x&uacute;c về sau, Sở Quang mới từ linh linh to&aacute;i to&aacute;i manh mối b&ecirc;n trong hiểu được đến, nơi n&agrave;y đ&atilde; kh&ocirc;ng phải l&agrave; Trung Quốc, thậm ch&iacute; đ&atilde; kh&ocirc;ng phải l&agrave; m&igrave;nh quen thuộc c&aacute;i kia Địa Cầu!<br />\r\n<br />\r\nTại c&aacute;i n&agrave;y thế giới song song, hai trăm năm trước chiến tranh ph&aacute; hủy văn minh hết thảy th&agrave;nh quả, chỉ để lại một mảnh cảnh ho&agrave;ng t&agrave;n khắp nơi đ&acirc;́t ch&ecirc;́t, c&ugrave;ng tại đ&acirc;́t ch&ecirc;́t tr&ecirc;n k&eacute;o d&agrave;i hơi t&agrave;n người sống s&oacute;t.<br />\r\n<br />\r\nM&agrave; b&acirc;y giờ, l&agrave; năm 2340.<br />\r\n<br />\r\nCũng l&agrave; từ năm 2129 sơ, nh&acirc;n loại văn minh tiến v&agrave;o đ&acirc;́t ch&ecirc;́t kỷ nguy&ecirc;n về sau thứ năm 211!<br />\r\n<br />\r\nMộng bức.<br />\r\n<br />\r\nChấn kinh.<br />\r\n<br />\r\nKh&ocirc;ng c&aacute;ch n&agrave;o h&igrave;nh dung sợ h&atilde;i.<br />\r\n<br />\r\nCũng may Sở Quang năng lực tiếp nhận coi như mạnh, chịu đựng qua ban sơ kia đoạn thời gian, cũng dần dần bắt đầu th&iacute;ch ứng b&ecirc;n n&agrave;y sinh hoạt.<br />\r\n<br />\r\nTại đ&acirc;́t ch&ecirc;́t tr&ecirc;n kh&oacute; khăn sinh sống năm th&aacute;ng, mỗi ng&agrave;y ăn bữa trước kh&ocirc;ng c&oacute; bữa sau, c&ograve;n phải c&ugrave;ng sinh vật biến dị c&ugrave;ng kh&ocirc;ng c&oacute; hảo &yacute; kẻ cướp đoạt nh&oacute;m đấu tr&iacute; đấu dũng.<br />\r\n<br />\r\nNgay tại Sở Quang bi quan cho rằng, m&igrave;nh đời n&agrave;y cứ như vậy thời điểm, hết thảy lại tại mấy giờ trước ph&aacute;t sinh chuyển cơ.<br />\r\n<br />\r\nTại một chỗ vứt bỏ trại an dưỡng h&agrave;i cốt b&ecirc;n trong, hắn ph&aacute;t hiện đ&aacute;nh số l&agrave; 404 chỗ tr&aacute;nh nạn lối v&agrave;o.<br />\r\n<br />\r\nCh&iacute;nh x&aacute;c tới n&oacute;i, kh&ocirc;ng phải Sở Quang t&igrave;m được n&oacute;, m&agrave; l&agrave; to&agrave; n&agrave;y t&ecirc;n l&agrave; &quot;Số 404 chỗ tr&aacute;nh nạn&quot; c&ocirc;ng tr&igrave;nh t&igrave;m được Sở Quang.<br />\r\n<br />\r\nTừ trong &oacute;c vang l&ecirc;n thanh &acirc;m, chỉ dẫn lấy hắn đến nơi n&agrave;y.<br />\r\n<br />\r\nM&agrave; khi Sở Quang tới gần trại an dưỡng phụ cận l&uacute;c mới đột nhi&ecirc;n ph&aacute;t hiện, nơi n&agrave;y ch&iacute;nh l&agrave; l&uacute;c trước hắn ở c&aacute;i thế giới n&agrave;y vừa tỉnh lại l&uacute;c c&aacute;i chỗ kia!<br />\r\n<br />\r\nCh&iacute;nh m&igrave;nh l&uacute;c trước nếu l&agrave; kh&ocirc;ng chạy loạn khắp nơi, hoặc l&agrave; sắc trời lại hơi s&aacute;ng một ch&uacute;t, kh&ocirc;ng chừng đều đ&atilde; t&igrave;m tới nơi n&agrave;y!<br />\r\n<br />\r\nV&ocirc; luận như thế n&agrave;o, đến chậm năm th&aacute;ng &quot;Hack&quot; rốt cục vẫn l&agrave; tới sổ.<br />\r\n<br />\r\nC&ugrave;ng l&uacute;c đ&oacute;, một phần mới tinh c&ocirc;ng việc cũng b&agrave;y tại trước mặt hắn.<br />\r\n<br />\r\nTo&agrave; n&agrave;y chỗ tr&aacute;nh nạn phảng phất ch&iacute;nh l&agrave; v&igrave; hắn m&agrave; chuẩn bị đồng dạng, đ&atilde; đợi chờ đ&atilde; l&acirc;u.<br />\r\n<br />\r\nTừ hắn bước v&agrave;o chỗ tr&aacute;nh nạn cửa lớn một khắc n&agrave;y bắt đầu, th&acirc;n phận của hắn liền từ một người nhặt r&aacute;c, biến th&agrave;nh to&agrave; n&agrave;y số 404 chỗ tr&aacute;nh nạn duy nhất cư d&acirc;n c&ugrave;ng người quản l&yacute;.<br />\r\n<br />\r\nM&agrave; c&ocirc;ng t&aacute;c của hắn chỉ c&oacute; một việc.<br />\r\n<br />\r\nĐ&oacute; ch&iacute;nh l&agrave; lợi dụng tr&ecirc;n Địa Cầu internet, dẫn đạo &mdash;&mdash; hoặc là nói lắc lư một đ&aacute;m t&ecirc;n l&agrave; &quot;Người chơi&quot; sinh vật, đến thế giới n&agrave;y cho hắn l&agrave;m c&ocirc;ng.<br />\r\n<br />\r\nĐ&uacute;ng vậy, to&agrave; n&agrave;y chỗ tr&aacute;nh nạn b&ecirc;n trong hắc khoa kỹ, chẳng những c&oacute; thể kết nối thế giới song song Địa Cầu internet, thậm ch&iacute; c&ograve;n c&oacute; được thuộc về m&igrave;nh &quot;Offical Website &quot; !<br />\r\n<br />\r\nHẹn trước tr&ograve; chơi người chơi sẽ tự động thu hoạch được một c&aacute;i số hiệu, đồng thời đăng k&yacute; tại quản l&yacute; người hệ thống 【 chỗ tr&aacute;nh nạn cư d&acirc;n mục lục 】 b&ecirc;n trong.<br />\r\n<br />\r\nL&agrave;m người quản l&yacute; Sở Quang, c&oacute; thể lựa chọn đặc biệt số hiệu cấp cho &quot;Tr&ograve; chơi tư c&aacute;ch&quot; .</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nHệ thống sẽ th&ocirc;ng qua một loại n&agrave;o đ&oacute; phương thức đặc th&ugrave;, đem mũ tr&ograve; chơi đưa đến người chơi trong tay. Người chơi chỉ cần đội n&oacute;n an to&agrave;n l&ecirc;n, liền c&oacute; thể c&ugrave;ng chỗ tr&aacute;nh nạn b&ecirc;n trong bồi dưỡng khoang thuyền kết nối, lấy nh&acirc;n bản thể th&acirc;n phận tỉnh lại.<br />\r\n<br />\r\nPhi thường c&oacute; t&iacute;nh người.<br />\r\n<br />\r\nCũng v&ocirc; c&ugrave;ng thuận tiện!<br />\r\n<br />\r\nMặc d&ugrave; b&atilde;i c&ocirc;ng cũng sẽ kh&ocirc;ng c&oacute; bất kỳ trừng phạt n&agrave;o, nhưng ho&agrave;n th&agrave;nh nhiệm vụ lấy được ban thưởng, lại l&agrave; Sở Quang v&ocirc; luận như thế n&agrave;o cũng v&ocirc; ph&aacute;p cự tuyệt.<br />\r\n<br />\r\nTỉ như m&oacute;n kia &aacute;o chống đạn.<br />\r\n<br />\r\nTại đ&acirc;́t ch&ecirc;́t tr&ecirc;n gian nan gi&atilde;y dụa cầu sinh năm th&aacute;ng, hắn qu&aacute; r&otilde; r&agrave;ng loại n&agrave;y c&oacute; thể bảo mệnh đồ chơi trọng yếu bao nhi&ecirc;u.<br />\r\n<br />\r\nTrừ c&aacute;i đ&oacute; ra , nhiệm vụ ban thưởng điểm số cũng l&agrave; đồ tốt, c&oacute; thể tại 【 người quản l&yacute; trợ cấp 】 giao diện hối đo&aacute;i t&ecirc;n l&agrave; &quot;M&ugrave; hộp&quot; ban thưởng.<br />\r\n<br />\r\nMua sắm sơ cấp m&ugrave; hộp c&acirc;̀n 1 điểm ban thưởng điểm, trung cấp m&ugrave; hộp 10 điểm, cao cấp m&ugrave; hộp 100 điểm.<br />\r\n<br />\r\nCăn cứ &quot;Số 404 chỗ tr&aacute;nh nạn người quản l&yacute; hệ thống&quot; đối 【 người quản l&yacute; trợ cấp 】 c&ocirc;ng năng giải th&iacute;ch, m&ugrave; hộp r&uacute;t đến đồ vật chủng loại, số lượng kh&ocirc;ng chừng, bất qu&aacute; &iacute;t nhiều đều sẽ c&oacute; ch&uacute;t, n&oacute;i c&aacute;ch kh&aacute;c kh&ocirc;ng c&oacute; &quot;Tạ ơn h&acirc;n hạnh chiếu cố&quot; loại t&igrave;nh huống n&agrave;y.<br />\r\n<br />\r\nĐẳng cấp kh&aacute;c nhau m&ugrave; hộp, quyết định giữ gốc ban thưởng.<br />\r\n<br />\r\nTỉ như sơ cấp m&ugrave; hộp k&eacute;m nhất cũng c&oacute; thể mở ra thực phẩm vật tư, trung cấp m&ugrave; hộp giữ gốc thu hoạch được dược phẩm, cao cấp m&ugrave; hộp c&oacute; thể mở ra vũ kh&iacute;.<br />\r\n<br />\r\n<br />\r\nVề phần sơ cấp m&ugrave; hộp c&oacute; thể hay kh&ocirc;ng mở ra trung cấp m&ugrave; hộp phần thưởng, hệ thống n&oacute;i r&otilde; b&ecirc;n trong ngược lại l&agrave; kh&ocirc;ng c&oacute; viết, nhưng nghĩ đến cho d&ugrave; c&oacute;, x&aacute;c suất đo&aacute;n chừng cũng thấp c&oacute; thể.<br />\r\n<br />\r\nNgay tại Sở Quang suy nghĩ, tr&ecirc;n tay c&aacute;i n&agrave;y 5 c&aacute;i điểm số l&agrave; to&agrave;n cầm đi mở sơ cấp m&ugrave; hộp, vẫn l&agrave; tích lũy đủ 100 điểm r&uacute;t m&oacute;n vũ kh&iacute; thời điểm, ngồi xổm ở g&oacute;c tường &quot;Giấy lộn c&aacute;i sọt&quot; lại mở miệng n&oacute;i chuyện.<br />\r\n<br />\r\n&quot;Chủ nh&acirc;n, nhiệm vụ mới đ&atilde; đổi mới tại ng&agrave;i người quản l&yacute; nhật k&yacute; l&ecirc;n ờ, xin ch&uacute; &yacute; kịp thời xem x&eacute;t.&quot;<br />\r\n<br />\r\n&quot;Biết.&quot; Sở Quang kh&ocirc;ng ki&ecirc;n nhẫn trả lời một c&acirc;u.<br />\r\n<br />\r\nMẹ n&oacute;.<br />\r\n<br />\r\nĐến c&ugrave;ng ai l&agrave; ai chủ nh&acirc;n!<br />\r\n<br />\r\nHướng ph&iacute;a g&oacute;c tường vung một c&aacute;i xem thường, Sở Quang đem r&uacute;t m&ugrave; hộp sự t&igrave;nh m&agrave; tạm thời đặt ở một b&ecirc;n, trong l&ograve;ng mặc niệm người quản l&yacute; hệ thống.<br />\r\n<br />\r\nRất nhanh, m&agrave;u lam nhạt to&agrave;n bộ tin tức m&agrave;n h&igrave;nh, bắn ra tại hắn v&otilde;ng mạc b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\n&mdash;&mdash; người quản l&yacute; nhật k&yacute; &mdash;&mdash;<br />\r\n<br />\r\n【 nhiệm vụ: số 404 chỗ tr&aacute;nh nạn k&iacute;ch hoạt điều kiện đ&atilde; thỏa m&atilde;n, nh&oacute;m đầu ti&ecirc;n &quot;Thu nhận vật&quot; x&aacute;c nhận n&eacute;m đưa, y&ecirc;u cầu bảo đảm ch&iacute; &iacute;t một người chơi đăng lục tr&ograve; chơi.<br />\r\n<br />\r\nLoại h&igrave;nh: Chủ tuyến.<br />\r\n<br />\r\nBan thưởng: Hoạt t&iacute;nh vật chất r&uacute;t ra dụng cụ.<br />\r\n<br />\r\n】<br />\r\n<br />\r\n【 nhiệm vụ: Chuẩn bị một đ&agrave;i c&ocirc;ng suất lớn hơn 10kw m&aacute;y ph&aacute;t điện.<br />\r\n<br />\r\nLoại h&igrave;nh: Chi nh&aacute;nh.<br />\r\n<br />\r\nBan thưởng: Ban thưởng điểm +100.<br />\r\n<br />\r\n】<br />\r\n<br />\r\nTa đi?<br />\r\n<br />\r\n100 ban thưởng điểm? !<br />\r\n<br />\r\nXem hết đổi mới tại quản l&yacute; người nhật k&yacute; tr&ecirc;n nhiệm vụ về sau, Sở Quang trợn cả mắt l&ecirc;n.<br />\r\n<br />\r\nBất qu&aacute; c&agrave;ng l&agrave;m cho hắn để &yacute;, l&agrave; c&aacute;i kia nhiệm vụ ch&iacute;nh tuyến ban thưởng.<br />\r\n<br />\r\n&quot;Hoạt t&iacute;nh vật chất r&uacute;t ra dụng cụ... C&aacute;i đồ chơi n&agrave;y đến c&ugrave;ng l&agrave; d&ugrave;ng để l&agrave;m g&igrave;?&quot;<br />\r\n<br />\r\nTiểu Thất hồi đ&aacute;p.<br />\r\n<br />\r\n&quot;Hoạt t&iacute;nh vật chất l&agrave; chế t&aacute;c nh&acirc;n bản thể nguy&ecirc;n liệu, về phần d&ugrave;ng như thế n&agrave;o, hẳn l&agrave; sẽ c&oacute; s&aacute;ch hướng dẫn loại h&igrave;nh đồ vật a?&quot;<br />\r\n<br />\r\nL&agrave;m AI trợ thủ, tr&ecirc;n thực tế Tiểu Thất biết đến t&igrave;nh b&aacute;o cũng kh&ocirc;ng nhiều.<br />\r\n<br />\r\nTrước mắt Sở Quang nắm giữ quyền hạn chỉ c&oacute; thể đến chỗ tr&aacute;nh nạn B1 tầng, những tầng lầu kh&aacute;c quyền hạn c&acirc;̀n th&ocirc;ng qua nhiệm vụ ch&iacute;nh tuyến tiến h&agrave;nh giải tỏa.<br />\r\n<br />\r\nThuận tiện nhấc l&ecirc;n, nếu như cưỡng &eacute;p ph&aacute; hư chỗ tr&aacute;nh nạn bức tường kết cấu, &yacute; đồ phi ph&aacute;p x&acirc;m lấn những tầng lầu kh&aacute;c, sẽ dẫn đến chỗ tr&aacute;nh nạn điện lực c&ocirc;ng tr&igrave;nh, kh&ocirc;ng kh&iacute; trao đổi c&ocirc;ng tr&igrave;nh gi&aacute;n đoạn.<br />\r\n<br />\r\nC&aacute;i n&agrave;y tại vừa tiến v&agrave;o chỗ tr&aacute;nh nạn thời điểm, ghi v&agrave;o hắn đại n&atilde;o &quot;Chỗ tr&aacute;nh nạn hệ thống&quot; liền đ&atilde; hướng hắn c&aacute;o tri.<br />\r\n<br />\r\nBất qu&aacute; coi như kh&ocirc;ng c&acirc;u n&agrave;y nhắc nhở, Sở Quang cảm thấy m&igrave;nh đại kh&aacute;i cũng l&agrave;m kh&ocirc;ng được ph&aacute; nh&agrave; chuyện ngu xuẩn.<br />\r\n<br />\r\nN&oacute;i đ&ugrave;a.<br />\r\n<br />\r\nPh&aacute; hủy nơi n&agrave;y, hắn về sau ở đ&acirc;u đ&acirc;y?<br />\r\n<br />\r\nM&agrave; lại loại n&agrave;y &quot;Tập trước khi chiến đấu khoa học kỹ thuật chi đại thừa, bị thiết kế th&agrave;nh c&oacute; thể chống cự cấp chiến lược nổ hạt nh&acirc;n&quot; chỗ tr&aacute;nh nạn, muốn ph&aacute; hủy sợ cũng kh&ocirc;ng phải dễ d&agrave;ng như vậy.<br />\r\n<br />\r\nĐừng n&oacute;i đạn hạt nh&acirc;n, hắn ngay cả khẩu s&uacute;ng đều kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\n&quot;Được th&ocirc;i, ta đại kh&aacute;i giải.&quot;<br />\r\n<br />\r\nN&oacute;i c&aacute;ch kh&aacute;c, hoạt t&iacute;nh vật chất c&ugrave;ng loại với &quot;Phục sinh tệ&quot;, l&agrave; c&aacute;c người chơi một lần nữa hợp th&agrave;nh nh&acirc;n bản thể c&acirc;̀n ti&ecirc;u hao t&agrave;i nguy&ecirc;n.<br />\r\n<br />\r\nTrước mắt B1 tầng tồn ph&oacute;ng 100 đ&agrave;i bồi dưỡng khoang thuyền, đồng đều ở v&agrave;o nhưng k&iacute;ch hoạt trạng th&aacute;i, nghĩ đến b&ecirc;n trong hẳn l&agrave; nội tr&iacute; 1 đơn vị hoạt t&iacute;nh vật chất, c&oacute; thể cung ứng lần đầu hợp th&agrave;nh nh&acirc;n bản thể ti&ecirc;u hao.<br />\r\n<br />\r\nCăn cứ hệ thống &quot;Một m&aacute;y m&oacute;c số một&quot; thiết lập, Sở Quang hiện tại nhiều nhất c&oacute; thể triệu ho&aacute;n 100 t&ecirc;n &quot;Người chơi&quot;, lấy nh&acirc;n bản thể h&igrave;nh thức gi&aacute;ng l&acirc;m thế giới n&agrave;y.<br />\r\n<br />\r\nĐương nhi&ecirc;n, Sở Quang trong l&ograve;ng cũng r&otilde; r&agrave;ng, sự t&igrave;nh cũng kh&ocirc;ng c&oacute; mặt ngo&agrave;i đơn giản như vậy.<br />\r\n<br />\r\nĐầu ti&ecirc;n trước mắt 《 Đất Chết OL》 Offical Website kh&ocirc;ng c&oacute; lưu lượng.<br />\r\n<br />\r\nTuyệt đại đa số người khi nghe đến &quot;Trăm phần trăm ch&acirc;n thực đắm ch&igrave;m thức giả lập hiện thực tr&ograve; chơi&quot; loại n&agrave;y tuy&ecirc;n truyền về sau, phản ứng đầu ti&ecirc;n hoặc l&agrave; gạt người, hoặc l&agrave; vẫn l&agrave; gạt người.<br />\r\n<br />\r\nRốt cuộc trong hiện thực căn bản kh&ocirc;ng tồn tại loại n&agrave;y kỹ thuật, cho d&ugrave; l&agrave; từ truyền th&ocirc;ng đều bi&ecirc;n kh&ocirc;ng ra như thế xả đạm chuyện ma quỷ.<br />\r\n<br />\r\nTiếp theo, cho d&ugrave; c&oacute; 11 t&ecirc;n người chơi bị hắn vừa dỗ vừa lừa địa điểm hẹn trước, hắn cũng v&ocirc; ph&aacute;p x&aacute;c định c&aacute;i n&agrave;y 1 1 người đều sẽ nghe m&igrave;nh.<br />\r\n<br />\r\nTiến v&agrave;o tr&ograve; chơi c&ugrave;ng đăng xuất tr&ograve; chơi quyền chủ động, đều ở c&aacute;c người chơi tr&ecirc;n tay m&igrave;nh.<br />\r\n<br />\r\nM&agrave; quyền lực của m&igrave;nh chỉ c&oacute; thể đem quấy rối người chơi &quot;Cưỡng chế đ&aacute; ra&quot;, &quot;Phong cấm đăng lục quyền hạn&quot;, c&ugrave;ng &quot;Tịch thu tr&ograve; chơi tư c&aacute;ch&quot;, cũng kh&ocirc;ng thể cưỡng chế bọn hắn đội n&oacute;n an to&agrave;n l&ecirc;n, thượng tuyến v&igrave; ch&iacute;nh m&igrave;nh l&agrave;m việc.<br />\r\n<br />\r\nBởi vậy, cấp cho tr&ograve; chơi tư c&aacute;ch nh&acirc;n tuyển &mdash;&mdash; nhất l&agrave; nh&oacute;m đầu ti&ecirc;n &quot;Closed Beta&quot; người chơi nh&acirc;n tuyển, nhất định phải thận trọng.<br />\r\n<br />\r\nTh&agrave; rằng &iacute;t một ch&uacute;t, cũng so lộn xộn mạnh.<br />\r\n<br />\r\nNếu kh&ocirc;ng ho&agrave;n to&agrave;n l&agrave; t&igrave;m phiền to&aacute;i cho m&igrave;nh!<br />\r\n<br />\r\nLại sau đ&oacute;, cũng l&agrave; mấu chốt nhất.<br />\r\n<br />\r\nĐ&acirc;́t ch&ecirc;́t b&ecirc;n n&agrave;y điều kiện cũng quyết định, l&uacute;c n&agrave;y kh&ocirc;ng phải c&agrave;ng nhiều người c&agrave;ng tốt.<br />\r\n<br />\r\nTh&ecirc;m một người liền mang &yacute; nghĩa th&ecirc;m một c&aacute;i miệng.<br />\r\n<br />\r\nHắn hiện tại ngh&egrave;o c&ugrave;ng quỷ đồng dạng, ch&iacute;nh m&igrave;nh cũng nhanh nu&ocirc;i kh&ocirc;ng sống, chớ n&oacute;i chi l&agrave; lại nu&ocirc;i một trăm tấm miệng.<br />\r\n<br />\r\nCoi như hiện tại hắn c&oacute; điều kiện kia, đem c&aacute;i n&agrave;y một trăm đ&agrave;i bồi dưỡng khoang thuyền cho hết mở, cũng l&agrave; cho dị chủng c&ugrave;ng kẻ cướp đoạt nh&oacute;m đưa khẩu phần lương thực.<br />\r\n<br />\r\nTrừ c&aacute;i đ&oacute; ra, kh&ocirc;ng c&oacute; bất kỳ c&aacute;i g&igrave; &yacute; nghĩa.<br />\r\n<br />\r\nSở Quang trở lại trước m&aacute;y vi t&iacute;nh lần nữa ngồi xuống.<br />\r\n<br />\r\nĐối m&agrave;n h&igrave;nh tỉnh t&aacute;o ngẫm nghĩ hồi l&acirc;u, &yacute; nghĩ của hắn dần dần r&otilde; r&agrave;ng.<br />\r\n<br />\r\nMột lần nữa leo l&ecirc;n m&igrave;nh Chim C&aacute;nh Cụt hiệu, Sở Quang đ&aacute;nh tiếp mở c&aacute;i kia gọi &quot;Ngưu M&atilde; c&acirc;u lạc bộ&quot; tr&ograve; chơi bầy, cho chủ nhóm c&ugrave;ng ba c&aacute;i quản l&yacute; k&eacute;o c&aacute;i thảo luận tổ.<br />\r\n<br />\r\nĐ&oacute;n lấy, ở trong bầy m&oacute;c chữ nói.<br />\r\n<br />\r\n【 c&aacute;c ngươi hẹn trước tr&ograve; chơi l&uacute;c, Offical Website giao diện tr&ecirc;n đổi mới số hiệu ph&aacute;t ta một ch&uacute;t. 】<br />\r\n<br />\r\n【 Closed Beta tư c&aacute;ch chỉ c&oacute; bốn c&aacute;i, ph&ugrave; sa kh&ocirc;ng lưu ruộng người ngo&agrave;i, ta hướng c&ocirc;ng ty xin, nh&igrave;n xem c&oacute; thể hay kh&ocirc;ng đem Closed Beta tư c&aacute;ch c&ugrave;ng mũ tr&ograve; chơi trực tiếp ph&aacute;t cho c&aacute;c ngươi. 】<br />\r\n<br />\r\nĐều l&agrave; trước kia thường xuy&ecirc;n mở hắc ăn g&agrave; đồng đội, chiếu cố một ch&uacute;t cũng l&agrave; kh&ocirc;ng lộ vẻ đột ngột.<br />\r\n<br />\r\nMấu chốt nhất l&agrave;, Sở Quang nghĩ kh&ocirc;ng ra trong hiện thực c&oacute; thể xin nhờ c&aacute;i n&agrave;o người quen, c&oacute; thể dựa v&agrave;o cũng chỉ c&oacute; những n&agrave;y tại trong hiện thực ho&agrave;n to&agrave;n kh&ocirc;ng gặp qua m&igrave;nh, nhưng lại c&oacute; chút giao t&igrave;nh d&acirc;n mạng.<br />\r\n<br />\r\nTh&acirc;n th&iacute;ch?<br />\r\n<br />\r\nGia gia đi về sau, rất nhiều năm đều kh&ocirc;ng đi động, nếu như cảnh s&aacute;t kh&ocirc;ng c&oacute; th&ocirc;ng tri đến, đều chưa hẳn biết m&igrave;nh người đ&atilde; trải qua kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nChủ thu&ecirc; nh&agrave;?<br />\r\n<br />\r\nL&uacute;c n&agrave;y chỉ sợ khắp thế giới t&igrave;m m&igrave;nh muốn tiền thu&ecirc; nh&agrave;.<br />\r\n<br />\r\nĐồng học?<br />\r\n<br />\r\nTốt nghiệp về sau liền kh&ocirc;ng li&ecirc;n lạc qua.<br />\r\n<br />\r\nĐồng sự?<br />\r\n<br />\r\nHa ha.<br />\r\n<br />\r\nĐo&aacute;n chừng l&agrave; cảm thấy x&uacute;i quẩy, Sở Quang ph&aacute;t hiện bọn hắn đ&atilde; đem m&igrave;nh x&oacute;a.<br />\r\n<br />\r\nHuống hồ coi như kh&ocirc;ng xóa, Sở Quang cũng kh&ocirc;ng c&oacute; khả năng cho bọn hắn ph&aacute;t tin tức, rốt cuộc m&igrave;nh l&uacute;c ấy thế nhưng l&agrave; tại tr&ecirc;n b&agrave;n rượu nghỉ cơm.<br />\r\n<br />\r\nNgười chết cho người sống ph&aacute;t tin tức, ngẫm lại đều c&oacute; đủ kinh dị.<br />\r\n<br />\r\nSo s&aacute;nh dưới, những n&agrave;y sa đi&ecirc;u đ&aacute;m d&acirc;n mạng kh&ocirc;ng thể nghi ngờ l&agrave; lựa chọn tốt nhất.<br />\r\n<br />\r\nBọn hắn c&ugrave;ng m&igrave;nh tại trong hiện thực cũng kh&ocirc;ng nhận ra, m&igrave;nh coi như năm th&aacute;ng kh&ocirc;ng c&oacute; thượng tuyến, bọn hắn cũng sẽ kh&ocirc;ng hướng phương diện kia nghĩ qu&aacute; nhiều.<br />\r\n<br />\r\nThời gian qua nhanh: 【 đại l&atilde;o tr&acirc;u ph&ecirc;! 】<br />\r\n<br />\r\nCấp t&aacute;m lớn cuồng phong: 【 tr&acirc;u a tr&acirc;u a! 】<br />\r\n<br />\r\nC&ograve;n nhiều thời gian: [666! 】<br />\r\n<br />\r\nThiếu nữ c&ocirc;ng địch đ&ecirc;m mười lần: 【 thỏa! L&atilde;o ca mạnh v&ocirc; địch! Chờ mũ tr&ograve; chơi! (buồn cười) 】<br />\r\n<br />\r\nSở Quang tiếp tục đ&aacute;nh chữ căn dặn.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n【 bất qu&aacute; c&oacute; chuyện ta phải tuy&ecirc;n bố một ch&uacute;t, thiết bị chỉ c&oacute; thể coi l&agrave; cho c&aacute;c ngươi mượn sử dụng, c&aacute;c ngươi đừng l&agrave;m mất đi, c&agrave;ng đừng ph&aacute; hủy, phải kh&ocirc;ng ta kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o c&ugrave;ng c&ocirc;ng ty b&agrave;n giao. 】<br />\r\n<br />\r\n【 mặt kh&aacute;c, thiết bị sẽ c&ugrave;ng người sử dụng th&ocirc;ng tin th&acirc;n phận kh&oacute;a lại, tr&ecirc;n nguy&ecirc;n tắc một đ&agrave;i thiết bị chỉ c&oacute; thể đăng k&iacute; một c&aacute;i t&agrave;i khoản, đồng thời chỉ c&oacute; thể từ một người sử dụng. 】<br />\r\n<br />\r\nKỳ thật n&oacute;i l&agrave; căn dặn, hắn cũng l&agrave; kh&ocirc;ng lo lắng c&aacute;c người chơi kh&ocirc;ng tu&acirc;n thủ.<br />\r\n<br />\r\nCăn cứ hệ thống thuyết ph&aacute;p, mũ gi&aacute;p sẽ đối với người chơi th&ocirc;ng tin th&acirc;n phận tiến h&agrave;nh ph&acirc;n biệt, kh&ocirc;ng phải người chơi bản nh&acirc;n căn bản kh&ocirc;ng d&ugrave;ng đến.<br />\r\n<br />\r\nVề phần thiết bị. . .<br />\r\n<br />\r\nHắn mới kh&ocirc;ng quan t&acirc;m c&aacute;i n&agrave;y.<br />\r\n<br />\r\nĐ&atilde; hệ thống c&oacute; thể đem mũ gi&aacute;p đưa đến người chơi tr&ecirc;n tay, kia tất nhi&ecirc;n c&oacute; biện ph&aacute;p giải quyết &quot;Phục vụ hậu m&atilde;i&quot; vấn đề, đ&acirc;y kh&ocirc;ng phải hắn c&acirc;̀n quan t&acirc;m.<br />\r\n<br />\r\nC&ograve;n nhiều thời gian: 【 thu ph&iacute; sao? 】<br />\r\n<br />\r\nSở Quang: 【 Closed Beta giai đoạn kh&ocirc;ng thu bất luận c&aacute;i g&igrave; ph&iacute; tổn, bất qu&aacute; bởi v&igrave; kinh ph&iacute; khẩn trương, cũng kh&ocirc;ng c&oacute; tiền lương ch&iacute;nh l&agrave;. 】<br />\r\n<br />\r\nC&ograve;n nhiều thời gian: [6666, ta số hiệu vụng trộm ph&aacute;t ngươi, cho ta gửi một phần. 】<br />\r\n<br />\r\nThời gian qua nhanh: 【 da tr&acirc;u da tr&acirc;u, ha ha ha, cho ta cũng cả một c&aacute;i, nếu thật l&agrave; ngươi n&oacute;i ho&agrave;n to&agrave;n đắm ch&igrave;m thức giả lập hiện thực, ta trực tiếp dựng ngược ti&ecirc;u chảy! 】<br />\r\n<br />\r\nCấp t&aacute;m lớn cuồng phong: 【 ha ha, ta cũng ủng hộ một đợt. 】<br />\r\n<br />\r\nThiếu nữ c&ocirc;ng địch đ&ecirc;m mười lần: [(buồn cười. jpg) 】<br />\r\n<br />\r\nMặc d&ugrave; bốn vị n&agrave;y l&atilde;o ca r&otilde; r&agrave;ng kh&ocirc;ng tin lời hắn n&oacute;i, c&agrave;ng kh&ocirc;ng tin tr&ecirc;n thế giới n&agrave;y thật tồn tại loại kia ho&agrave;n to&agrave;n đắm ch&igrave;m thức giả lập hiện thực tr&ograve; chơi, nhưng bọn hắn vẫn l&agrave; &ocirc;m &quot;Phối hợp hắn tr&ograve; đ&ugrave;a&quot; t&acirc;m th&aacute;i đem m&igrave;nh số hiệu ph&aacute;t tới.<br />\r\n<br />\r\nChỉ l&agrave; c&aacute;i Offical Website tạo ra số hiệu m&agrave; th&ocirc;i.<br />\r\n<br />\r\nCũng kh&ocirc;ng phải số thẻ ng&acirc;n h&agrave;ng c&ugrave;ng mật m&atilde;.<br />\r\n<br />\r\nHuống hồ bọn hắn cũng rất t&ograve; m&ograve;, gia hỏa n&agrave;y trong hồ l&ocirc; đến c&ugrave;ng muốn l&agrave;m c&aacute;i g&igrave;. . .<br />\r\n<br />\r\nThu được số hiệu về sau, Sở Quang kh&ocirc;ng do dự, lập tức gọi ra người quản l&yacute; hệ thống, t&igrave;m tới 【 chỗ tr&aacute;nh nạn cư d&acirc;n mục lục 】, tại bốn c&aacute;i số hiệu đằng sau c&acirc;u tuyển &quot;Cấp cho tr&ograve; chơi tư c&aacute;ch&quot; .<br />\r\n<br />\r\nBồi dưỡng khoang thuyền hợp th&agrave;nh nh&acirc;n bản thể c&acirc;̀n ba ng&agrave;y thời gian.<br />\r\n<br />\r\nTại ba ng&agrave;y nay b&ecirc;n trong, hệ thống sẽ th&ocirc;ng qua thủ đoạn đặc th&ugrave;, đem mũ tr&ograve; chơi đưa đến người chơi trong tay. . . Thậm ch&iacute; ngay cả thu h&agrave;ng địa chỉ đều kh&ocirc;ng cần điền.<br />\r\n<br />\r\nVề phần cụ thể l&agrave; d&ugrave;ng thủ đoạn g&igrave;, sẽ hay kh&ocirc;ng g&acirc;y n&ecirc;n x&atilde; hội hiện đại ch&uacute; &yacute;, hoặc l&agrave; kh&ocirc;ng d&iacute;nh l&iacute;u phi ph&aacute;p vận doanh những n&agrave;y vấn đề phiền to&aacute;i, Sở Quang l&agrave; c&agrave;ng lười đi quan t&acirc;m.<br />\r\n<br />\r\nHắn đến hi vọng ch&uacute; &yacute; tới nh&agrave; m&igrave;nh Offical Website nhiều người một ch&uacute;t, c&oacute; thể g&acirc;y n&ecirc;n oanh động c&agrave;ng l&agrave; tốt nhất.<br />\r\n<br />\r\nDạng n&agrave;y hắn lựa chọn n&agrave;o kh&aacute;c cũng nhiều một ch&uacute;t.<br />\r\n<br />\r\n&quot;Chủ nh&acirc;n, ch&uacute;ng ta Offical Website kh&ocirc;ng phải đ&atilde; c&oacute; 11 t&ecirc;n người chơi c&oacute; hẹn trước kh&ocirc;ng? V&igrave; c&aacute;i g&igrave; ng&agrave;i chỉ cấp cho 4 c&aacute;i danh ngạch nha.&quot;<br />\r\n<br />\r\nC&oacute; thể l&agrave; ch&uacute; &yacute; tới căn ph&ograve;ng c&aacute;ch v&aacute;ch bị k&iacute;ch hoạt bồi dưỡng khoang thuyền, ngồi xổm ở g&oacute;c tường Tiểu Thất biểu thị ra nghi hoặc.<br />\r\n<br />\r\n&quot;Bởi v&igrave; tạm thời kh&ocirc;ng cần đến nhiều như vậy.&quot;<br />\r\n<br />\r\nTắt đi 【 chỗ tr&aacute;nh nạn cư d&acirc;n mục lục 】.<br />\r\n<br />\r\nSở Quang sau đ&oacute; mở ra trong hệ thống 【 quản l&yacute; tử trợ cấp 】 giao diện, đem năm c&aacute;i ban thưởng điểm tất cả đều đập v&agrave;o &quot;Sơ cấp m&ugrave; hộp&quot; b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nTích lũy lấy mua cao cấp m&ugrave; hộp kh&ocirc;ng c&oacute; &yacute; nghĩa.<br />\r\n<br />\r\nNhiệm vụ 2 ban thưởng trực tiếp liền l&agrave; 100 điểm số, cũng kh&ocirc;ng k&eacute;m c&aacute;i n&agrave;y 5 c&aacute;i điểm.<br />\r\n<br />\r\nM&agrave;u lam nhạt to&agrave;n bộ tin tức trong m&agrave;n h&igrave;nh huỳnh quang lấp l&oacute;e, gian ph&ograve;ng một b&ecirc;n v&aacute;ch tường lần nữa nhẹ nh&agrave;ng chấn động, theo ca một tiếng vang nhỏ, &quot;Xuất h&agrave;ng miệng&quot; cửa hợp kim mở ra.<br />\r\n<br />\r\nNh&igrave;n xem nằm tại b&igrave;nh vật tr&ecirc;n đ&agrave;i, Sở Quang c&oacute; ch&uacute;t sửng sốt một ch&uacute;t.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y. . . Liền l&agrave; m&ugrave; hộp?&quot;<br />\r\n<br />\r\nTiểu Thất hồi đ&aacute;p.<br />\r\n<br />\r\n&quot;Hẳn l&agrave; đi.&quot;<br />\r\n<br />\r\nSở Quang lấy lại b&igrave;nh tĩnh, n&oacute;i.<br />\r\n<br />\r\n&quot;Ta c&ograve;n tưởng rằng phần thưởng ch&iacute; &iacute;t sẽ d&ugrave;ng hộp chứa.&quot;<br />\r\n<br />\r\nTiểu Thất thử cho ra giải th&iacute;ch.<br />\r\n<br />\r\n&quot;C&oacute; lẽ m&ugrave; hộp chỉ l&agrave; một loại thiết lập?&quot;<br />\r\n<br />\r\nThế nhưng l&agrave; kh&ocirc;ng cần hủy đi bao m&ugrave; hộp đến c&ugrave;ng c&oacute; &yacute; nghĩa g&igrave;?<br />\r\n<br />\r\nL&agrave;m g&igrave; kh&ocirc;ng trực tiếp gọi rút thưởng?<br />\r\n<br />\r\nNh&igrave;n chằm chằm băng chuyền trước ban thưởng, Sở Quang trầm mặc một hồi.<br />\r\n<br />\r\n&quot;Đạo l&yacute; ta đều hiểu.&quot;<br />\r\n<br />\r\n&quot;Nhưng v&igrave; c&aacute;i g&igrave; ta r&otilde; r&agrave;ng đổi năm c&aacute;i, đi l&ecirc;n chỉ c&oacute; bốn c&aacute;i? ? ?&quot;</p>', '2021-11-27 21:14:56', 12, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(172, 47, 'gia hỏa này là đến thật?', '3', '<p>Tốt a.<br />\r\n<br />\r\nSự thật chứng minh, l&agrave; m&igrave;nh hiểu lầm.<br />\r\n<br />\r\nM&ugrave; hộp mở ra ban thưởng, x&aacute;c thực c&oacute; &quot;Năm c&aacute;i&quot; .<br />\r\n<br />\r\nHệ thống cũng kh&ocirc;ng c&oacute; l&agrave;m &quot;Nuốt trang bị&quot; thứ chuyện thất đức n&agrave;y, cũng ho&agrave;n to&agrave;n ch&iacute;nh x&aacute;c kh&ocirc;ng tồn tại &quot;Tạ ơn h&acirc;n hạnh chiếu cố&quot; loại thuyết ph&aacute;p n&agrave;y.<br />\r\n<br />\r\nChỉ bất qu&aacute;, c&aacute;i thứ năm m&ugrave; hộp mở ra phần thưởng, bị mặt kh&aacute;c bốn c&aacute;i phần thưởng đ&egrave; ở...<br />\r\n<br />\r\nGiờ ph&uacute;t n&agrave;y b&agrave;y ở Sở Quang trước mặt, l&agrave; một khối ch&acirc;n kh&ocirc;ng đ&oacute;ng g&oacute;i, chỉ to&agrave;n h&agrave;m lượng 100g lương kh&ocirc;, c&ugrave;ng ba c&acirc;y chỉ to&agrave;n h&agrave;m lượng 25g kẹo que &mdash;&mdash; theo thứ tự l&agrave; quả t&aacute;o, chuối ti&ecirc;u, quả xo&agrave;i khẩu vị.<br />\r\n<br />\r\nC&ugrave;ng, một tờ giấy.<br />\r\n<br />\r\nLương kh&ocirc; đ&oacute;ng g&oacute;i ho&agrave;n hảo, kh&ocirc;ng c&oacute; bao, mặc d&ugrave; bảo đảm chất lượng kỳ c&ugrave;ng sản xuất ng&agrave;y l&agrave; c&aacute;i m&ecirc;, nhưng hẳn l&agrave; ăn kh&ocirc;ng chết người.<br />\r\n<br />\r\nC&aacute;i đồ chơi n&agrave;y l&agrave; c&aacute;i thứ tốt.<br />\r\n<br />\r\nPhối liệu bề ngo&agrave;i đ&aacute;nh dấu nhiệt lượng c&oacute; 500 kil&ocirc; calo, v&ocirc; luận l&agrave; l&agrave;m ăn vẫn l&agrave; n&eacute;m trong nồi luộc th&agrave;nh ch&aacute;o, đều c&oacute; thể nh&eacute;t đầy c&aacute;i bao tử.<br />\r\n<br />\r\nVề phần kẹo que, nhiệt lượng mặc d&ugrave; kh&ocirc;ng thấp, nhưng kh&ocirc;ng thể cung cấp chắc bụng cảm gi&aacute;c.<br />\r\n<br />\r\nBất qu&aacute;, đối với ngh&egrave;o đến sắp gặm vỏ c&acirc;y Sở Quang m&agrave; n&oacute;i, đ&atilde; rất tốt.<br />\r\n<br />\r\nLại nói hệ thống n&agrave;y cũng qu&aacute; mẹ n&oacute; keo kiệt, l&agrave; rất sợ hắn ăn qu&aacute; no bụng vẫn l&agrave; l&agrave;m sao tích, liền lấy những vật n&agrave;y đuổi hắn.<br />\r\n<br />\r\nĐứng tại xuất h&agrave;ng miệng b&ecirc;n cạnh Sở Quang, đem đồ ăn nh&eacute;t v&agrave;o mang theo người ba l&ocirc;, tiếp lấy nhặt l&ecirc;n bị băng chuyền đưa ra tới tờ giấy, triển khai nh&igrave;n tho&aacute;ng qua.<br />\r\n<br />\r\nTr&ecirc;n đ&oacute; viết một &iacute;t chữ, d&ugrave;ng ch&iacute;nh l&agrave; b&ecirc;n n&agrave;y thế giới ng&ocirc;n ngữ.<br />\r\n<br />\r\n[... Tr&ecirc;n thế giới n&agrave;y c&ograve;n c&oacute; c&aacute;i g&igrave; sinh vật so người chơi c&agrave;ng th&iacute;ch hợp l&agrave;m rau hẹ?<br />\r\n<br />\r\nBọn hắn chẳng những c&oacute; tr&agrave;n đầy l&ograve;ng hiếu kỳ, m&agrave; lại vĩnh viễn tr&agrave;n ngập nhiệt t&igrave;nh, đối mặt kh&oacute; khăn vượt kh&oacute; tiến l&ecirc;n, sẽ kh&ocirc;ng tuyệt vọng ở trước mắt quẫn cảnh. Đương nhi&ecirc;n, cực kỳ mấu chốt nhất l&agrave;, bọn hắn thậm ch&iacute; c&ograve;n c&oacute; thể đứng ở tổng đ&agrave;i g&oacute;c độ c&acirc;n nhắc vấn đề, gi&uacute;p đỡ tổng đ&agrave;i c&ugrave;ng một chỗ thu hoạch m&igrave;nh!<br />\r\n<br />\r\nĐem bộ n&agrave;y trang bị đ&oacute;ng g&oacute;i th&agrave;nh tr&ograve; chơi ta quả thực l&agrave; thi&ecirc;n t&agrave;i!<br />\r\n<br />\r\n&mdash;&mdash; số 404 chỗ tr&aacute;nh nạn đời thứ nhất người quản l&yacute;;(mời tờ giấy n&agrave;y đặt ở c&aacute;i thứ năm sơ cấp m&ugrave; hộp b&ecirc;n trong, đ&acirc;y l&agrave; ta lưu cho người kế nhiệm trứng m&agrave;u! ) 】<br />\r\n<br />\r\nSở Quang: &quot;...&quot;<br />\r\n<br />\r\nN&oacute;i thực ra, hắn chẳng những kh&ocirc;ng c&oacute; cảm thấy t&iacute; x&iacute;u thu hoạch trứng m&agrave;u kinh hỉ, thậm ch&iacute; c&oacute; ch&uacute;t im lặng.<br />\r\n<br />\r\nBất qu&aacute;...<br />\r\n<br />\r\nTrang bị l&agrave; c&oacute; &yacute; g&igrave;?<br />\r\n<br />\r\n&quot;Tiểu Thất.&quot;<br />\r\n<br />\r\n&quot;L&agrave;m sao vậy, chủ nh&acirc;n.&quot;<br />\r\n<br />\r\n&quot;Ngươi biết đời thứ nhất người quản l&yacute; sao?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng biết, l&agrave;m ng&agrave;i tiến v&agrave;o chỗ tr&aacute;nh nạn l&uacute;c, ta chương tr&igrave;nh xử l&yacute; ch&iacute;nh mới bị k&iacute;ch hoạt. Căn cứ chỗ tr&aacute;nh nạn kho số liệu b&ecirc;n trong tư liệu ghi ch&eacute;p, đời trước người quản l&yacute; đem thao t&aacute;c quyền hạn chuyển giao, đ&atilde; l&agrave; hơn một c&aacute;i thế kỷ trước sự t&igrave;nh.&quot;<br />\r\n<br />\r\nSở Quang tiếp tục hỏi.<br />\r\n<br />\r\n&quot;Cụ thể là đ&acirc;u một năm?&quot;<br />\r\n<br />\r\nTiểu Thất trả lời.<br />\r\n<br />\r\n&quot;Năm 2157 ngày mùng 1 tháng 1.&quot;<br />\r\n<br />\r\nNăm 2129 chiến tranh kết th&uacute;c, nh&acirc;n loại văn minh tiến v&agrave;o đ&acirc;́t ch&ecirc;́t kỷ nguy&ecirc;n, năm 2157 cũng ch&iacute;nh l&agrave; đ&acirc;́t ch&ecirc;́t kỷ nguy&ecirc;n thứ 28 năm.<br />\r\n<br />\r\nKhoảng c&aacute;ch hiện tại...<br />\r\n<br />\r\n183 năm !<br />\r\n<br />\r\nSở Quang từ nhỏ đối con số liền cực kỳ mẫn cảm, nhất l&agrave; loại n&agrave;y mấu chốt tin tức, hắn căn bản l&agrave; nhớ kỹ liền sẽ kh&ocirc;ng dễ d&agrave;ng qu&ecirc;n mất.<br />\r\n<br />\r\n&quot;N&oacute;i c&aacute;ch kh&aacute;c, 183 năm trước đời thứ nhất người quản l&yacute;, ph&aacute;t minh bộ n&agrave;y c&oacute; thể đem thế giới song song &yacute; thức thể, bắn ra đến người nh&acirc;n bản tr&ecirc;n người trang bị, cũng đem n&oacute; đ&oacute;ng g&oacute;i th&agrave;nh tr&ograve; chơi, nhưng ra nguy&ecirc;n nh&acirc;n n&agrave;o đ&oacute; một mực kh&ocirc;ng c&oacute; sử dụng đ&uacute;ng kh&ocirc;ng?&quot;<br />\r\n<br />\r\nCũng c&oacute; lẽ l&agrave; bởi v&igrave; hạch m&ugrave;a đ&ocirc;ng.<br />\r\n<br />\r\nCũng hay l&agrave; nguy&ecirc;n nh&acirc;n kh&aacute;c, dẫn đến chỗ tr&aacute;nh nạn chưa đầy đủ khởi động điều kiện.<br />\r\n<br />\r\nV&ocirc; luận như thế n&agrave;o, một trăm năm trước người, nghĩ như thế n&agrave;o đều cũng đ&atilde; qua đời.<br />\r\n<br />\r\nTiểu Thất n&oacute;i.<br />\r\n<br />\r\n&quot;Hẳn l&agrave; đi.&quot;<br />\r\n<br />\r\n&quot;Hắn t&ecirc;n gọi l&agrave; g&igrave;? C&aacute;i kia đời thứ nhất người quản l&yacute;.&quot;<br />\r\n<br />\r\nSở Quang đối th&acirc;n phận của hắn bỗng nhi&ecirc;n c&oacute; ch&uacute;t hiếu kỳ.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nNghĩ ra những c&aacute;i kia &aacute;c th&uacute; vị thiết định người kh&ocirc;ng nhất định l&agrave; thi&ecirc;n t&agrave;i, nhưng c&oacute; thể tại văn minh suy yếu về sau, bằng sức một m&igrave;nh ph&aacute;t minh ra như thế ngưu bức hắc khoa kỹ người, thi&ecirc;n t&agrave;i c&aacute;i từ n&agrave;y đều đ&atilde; kh&ocirc;ng đủ để d&ugrave;ng để h&igrave;nh dung hắn.<br />\r\n<br />\r\nĐem thế giới song song &yacute; thức thể bắn ra đến bản vị diện, đồng thời cất v&agrave;o l&agrave;m vật chứa nh&acirc;n bản thể.<br />\r\n<br />\r\nSở Quang kh&ocirc;ng tưởng tượng nổi, cuối c&ugrave;ng l&agrave; l&agrave;m được bằng c&aacute;ch n&agrave;o.<br />\r\n<br />\r\nVượt qua nhận biết kỹ thuật, đối với hắn m&agrave; n&oacute;i c&ugrave;ng ma ph&aacute;p kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c biệt.<br />\r\n<br />\r\n&quot;Ta cũng kh&ocirc;ng biết, đ&atilde; biết kho số liệu b&ecirc;n trong cũng kh&ocirc;ng c&oacute; li&ecirc;n quan tới hắn t&agrave;i liệu cặn kẽ, c&oacute; phải hay kh&ocirc;ng l&agrave; bị x&oacute;a bỏ rồi?&quot;<br />\r\n<br />\r\n&quot;Được rồi... Hỏi ngươi tương đương hỏi kh&ocirc;ng.&quot;<br />\r\n<br />\r\nSở Quang hơi thất vọng, cũng kh&ocirc;ng c&oacute; nhụt ch&iacute;.<br />\r\n<br />\r\nTrước mắt chỗ tr&aacute;nh nạn chỉ giải tỏa đếnB1 tầng.<br />\r\n<br />\r\nTheo nhiệm vụ kh&ocirc;ng ngừng th&uacute;c đẩy, ch&ocirc;n giấu tại to&agrave; n&agrave;y chỗ tr&aacute;nh nạn b&ecirc;n trong b&iacute; mật, một ng&agrave;y n&agrave;o đ&oacute; sẽ ở trước mặt hắn thể hiện ra to&agrave;n cảnh.<br />\r\n<br />\r\nĐem tờ giấy tiện tay nh&eacute;t v&agrave;o b&agrave;n m&aacute;y t&iacute;nh ngăn k&eacute;o, Sở Quang nắm thật chặt tr&ecirc;n người ba l&ocirc;.<br />\r\n<br />\r\nCh&uacute; &yacute; tới động t&aacute;c của hắn, ngồi xổm ở g&oacute;c tường Tiểu Thất hỏi.<br />\r\n<br />\r\n&quot;Chủ nh&acirc;n, ng&agrave;i muốn đi sao?&quot;<br />\r\n<br />\r\nVậy kh&ocirc;ng c&oacute; cao thấp chập tr&ugrave;ng thanh &acirc;m, nghe qu&aacute;i đ&aacute;ng thương.<br />\r\n<br />\r\n&quot;Ừm, người chơi ba ng&agrave;y sau mới c&oacute; thể đăng lục, ta trong n&agrave;y tiếp tục tr&ocirc;ng coi cũng kh&ocirc;ng c&oacute; bất kỳ c&aacute;i g&igrave; &yacute; nghĩa, kh&ocirc;ng bằng trở về chuẩn bị một ch&uacute;t.&quot;<br />\r\n<br />\r\nC&aacute;i chỗ chết tiệt n&agrave;y n&oacute;i l&agrave; chỗ tr&aacute;nh nạn, lại ngay cả ăn uống đều kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nSở Quang tr&ecirc;n th&acirc;n hiện tại liền thừa nửa khối b&agrave;n tay lớn b&aacute;nh c&ugrave;ng nửa b&igrave;nh nước, c&ugrave;ng vừa d&ugrave;ng ban thưởng điểm đổi lại lương kh&ocirc; c&ugrave;ng kẹo que.<br />\r\n<br />\r\nĐiểm ấy tiếp tế nhưng ki&ecirc;n tr&igrave; kh&ocirc;ng đến ba ng&agrave;y sau.<br />\r\n<br />\r\nHắn tại phụ cận người sống s&oacute;t khu quần cư, c&oacute; một c&aacute;i đơn sơ ổ nhỏ, b&ecirc;n trong đặt v&agrave;o một ch&uacute;t tiếp tế c&ugrave;ng cần d&ugrave;ng đến c&ocirc;ng cụ.<br />\r\n<br />\r\nSở Quang dự định trở về thu thập một ch&uacute;t, c&oacute; thể mang tới đều chậm r&atilde;i nhặt tới.<br />\r\n<br />\r\nVề sau nơi n&agrave;y ch&iacute;nh l&agrave; hắn mới căn cứ địa.<br />\r\n<br />\r\n&quot;Đ&uacute;ng rồi, Tiểu Thất.&quot;<br />\r\n<br />\r\n&quot;Thế n&agrave;o chủ nh&acirc;n?&quot;<br />\r\n<br />\r\n&quot;Ta c&acirc;̀n ngươi gi&uacute;p ta một chuyện.&quot;<br />\r\n<br />\r\nDừng một ch&uacute;t, Sở Quang tiếp tục n&oacute;i.<br />\r\n<br />\r\n&quot;Ch&uacute;ng ta Offical Website qu&aacute; đơn sơ, ta c&acirc;̀n ngươi cho n&oacute;, tăng th&ecirc;m một c&aacute;i diễn đ&agrave;n c&ocirc;ng năng c&ugrave;ng tư liệu, đồ gi&aacute;m c&ocirc;ng năng, c&oacute; thể l&agrave;m được sao?&quot;<br />\r\n<br />\r\nĐơn giản tới n&oacute;i, hắn muốn để người chơi hiểu r&otilde; hơn c&aacute;i n&agrave;y &quot;Tr&ograve; chơi&quot; .<br />\r\n<br />\r\nChỉ dựa v&agrave;o h&aacute; miệng liền muốn lắc lư người nhập hố, độ kh&oacute; c&oacute; ch&uacute;t lớn, ch&iacute; &iacute;t mặt ngo&agrave;i c&ocirc;ng phu cần phải l&agrave;m một lần.<br />\r\n<br />\r\n<br />\r\n&quot;Đương nhi&ecirc;n c&oacute; thể, ng&agrave;i c&oacute; Offical Website bi&ecirc;n tập quyền hạn, ngoại trừ tr&ograve; chơi hẹn trước giao diện b&ecirc;n ngo&agrave;i, c&oacute; thể ngạch tăng th&ecirc;m giao diện con hoặc l&agrave; đối đ&atilde; c&oacute; giao diện tiến h&agrave;nh sửa đổi.&quot;<br />\r\n<br />\r\n&quot;Cực kỳ tốt, vậy liền giao cho ngươi.&quot;<br />\r\n<br />\r\n&quot;Bao tr&ecirc;n người ta!&quot;<br />\r\n<br />\r\nĐối với chủ nh&acirc;n b&agrave;n giao cho nhiệm vụ của m&igrave;nh, Tiểu Thất tựa hồ thật cao hứng, ngay cả m&aacute;y biến điện năng th&agrave;nh &acirc;m thanh &acirc;m lượng đều đề cao nửa c&aacute;i &acirc;m lượng.<br />\r\n<br />\r\nĐương nhi&ecirc;n, c&aacute;i n&agrave;y cũng c&oacute; thể l&agrave; ch&iacute;nh Sở Quang hiểu lầm.<br />\r\n<br />\r\n&quot;Chủ nh&acirc;n, ng&agrave;i lúc nào trở về đ&acirc;u?&quot;<br />\r\n<br />\r\nSở Quang nghĩ nghĩ, n&oacute;i.<br />\r\n<br />\r\n&quot;Trễ nhất ba ng&agrave;y.&quot;<br />\r\n<br />\r\n&quot;Nếu như hết thảy thuận lợi, c&oacute; lẽ trời tối ng&agrave;y mai trước đ&oacute;.&quot;<br />\r\n<br />\r\n...<br />\r\n<br />\r\nHiện thế, Ngưu M&atilde; c&acirc;u lạc bộ bầy.<br />\r\n<br />\r\nC&aacute;i kia gọi &quot;Quang&quot; bầy l&atilde;o ca, vung xong địa chỉ Internet kết nối người liền kh&ocirc;ng c&ograve;n h&igrave;nh b&oacute;ng, kh&ocirc;ng biết l&agrave; hạ tuyến vẫn l&agrave; tr&ocirc;́n ở một b&ecirc;n d&ograve;m m&agrave;n h&igrave;nh.<br />\r\n<br />\r\nL&uacute;c đầu li&ecirc;n quan tới &quot;Ho&agrave;n to&agrave;n đắm ch&igrave;m thức giả lập hiện thực tr&ograve; chơi&quot; chủ đề đều sắp tới rồi, kết quả v&agrave;i ph&uacute;t trước vị n&agrave;y l&atilde;o ca k&eacute;o tiểu bầy tao thao t&aacute;c, lại đem nh&oacute;m lớn b&ecirc;n trong chủ đề cho k&eacute;o trở về.<br />\r\n<br />\r\nCai Thuốc: 【 đậu đen rau muống, gia hỏa n&agrave;y l&agrave; đến thật? 】<br />\r\n<br />\r\nC&aacute;i n&agrave;y Cai Thuốc cũng l&agrave; thường nổi l&ecirc;n sinh động bầy th&agrave;nh vi&ecirc;n, mặc d&ugrave; kh&ocirc;ng phải quản l&yacute;.<br />\r\n<br />\r\n<br />\r\nThời gian qua nhanh: 【 &acirc;n, c&aacute;c ngươi điểm xong hẹn trước về sau, hắn k&eacute;o c&aacute;i tiểu bầy, đem ch&uacute;ng ta mấy c&aacute;i bầy quản l&yacute; đều k&eacute;o tiến v&agrave;o. (v&ograve; đầu) 】<br />\r\n<br />\r\nThời gian qua nhanh l&agrave; chủ nhóm, tự nhi&ecirc;n cũng tại c&aacute;i kia tiểu bầy b&ecirc;n trong.<br />\r\n<br />\r\nNghe xong lời n&agrave;y, bầy vi&ecirc;n m&ocirc;n trong nh&aacute;y mắt s&ocirc;i tr&agrave;o.<br />\r\n<br />\r\n【 m&oacute;a! Tấm m&agrave;n đen a! 】<br />\r\n<br />\r\n【 Quang ca nhập chức nh&agrave; kia c&ocirc;ng ty gọi c&aacute;i g&igrave; c&aacute;c ngươi c&oacute; biết hay kh&ocirc;ng? Ta muốn b&aacute;o c&aacute;o hắn PY giao dịch! (buồn cười) 】<br />\r\n<br />\r\n【 k&eacute;o con b&ecirc; đi, c&ograve;n c&ocirc;ng ty! Năm ngo&aacute;i ung thư cỗ c&oacute; c&aacute;i l&agrave;m VR c&ocirc;ng ty, bồi mẹ n&oacute; &ocirc;ng chủ đều bị giam tiến v&agrave;o! Hiện tại đầu gi&oacute; đ&atilde; sớm kh&ocirc;ng phải VR! 】<br />\r\n<br />\r\n【 c&aacute;c ngươi n&oacute;i nhỏ ch&uacute;t thảo luận, người ta a Quang kh&ocirc;ng chừng tại d&ograve;m m&agrave;n h&igrave;nh đ&acirc;u. (buồn cười) 】<br />\r\n<br />\r\n【 ngươi n&oacute;i hắn đến c&ugrave;ng đồ c&aacute;i g&igrave;? Liền v&igrave; chỉ đ&ugrave;a một ch&uacute;t? 】<br />\r\n<br />\r\n【 quỷ hiểu được, kh&ocirc;ng chừng l&agrave; đem c&aacute;c ngươi những n&agrave;y th&acirc;n huynh đệ dẫn lưu đến rau cải x&ocirc;i trang web, bọn người nhiều cạc cạc cắt rau hẹ! 】<br />\r\n<br />\r\n【 ta nh&igrave;n gia hỏa n&agrave;y cũng kh&ocirc;ng phải l&agrave; vật g&igrave; tốt! Đ&aacute; được. 】<br />\r\n<br />\r\nChủ đề dần dần kh&oacute;.<br />\r\n<br />\r\nThậm ch&iacute; l&ecirc;n cao đến th&acirc;n người c&ocirc;ng k&iacute;ch.<br />\r\n<br />\r\nDiệp Vĩ nh&igrave;n xem thẳng nh&iacute;u m&agrave;y, vừa định n&oacute;i ch&uacute;t g&igrave;, liền tr&ocirc;ng thấy chủ nhóm ra phanh xe.<br />\r\n<br />\r\n(to&agrave;n bầy cấm ng&ocirc;n)<br />\r\n<br />\r\nThời gian qua nhanh: 【 mọi người đừng như vậy, mới tới bầy vi&ecirc;n khả năng kh&ocirc;ng biết a Quang, người kh&aacute;c cũng kh&ocirc;ng tệ lắm, liền l&agrave; thuật bắn s&uacute;ng ngựa một ch&uacute;t, con mắt m&ugrave; điểm, người thức ăn điểm, c&aacute;i kh&aacute;c cũng c&ograve;n tốt. 】<br />\r\n<br />\r\nC&ograve;n nhiều thời gian: 【 x&aacute;c thực, ta tin tưởng a Quang kh&ocirc;ng phải ngộ nhập lạc lối l&agrave;m c&aacute;i g&igrave; rau cải x&ocirc;i. Ch&uacute;ng ta b&acirc;y giờ liền l&agrave; kh&ocirc;ng x&aacute;c định, hắn c&oacute; phải hay kh&ocirc;ng bị trộm nick. Nếu như l&agrave;, c&aacute;i n&agrave;y ch&uacute;ng ta quản l&yacute; tổ sẽ xử l&yacute;. Một hồi tin tức ta đều r&uacute;t về, mọi người thay c&aacute;i chủ đề ha. (cười) 】<br />\r\n<br />\r\n(bầy cấm ng&ocirc;n giải trừ)<br />\r\n<br />\r\nChủ nhóm c&ugrave;ng quản l&yacute; đều đi ra bu&ocirc;ng lời, mọi người cũng cực kỳ cho mặt mũi kh&ocirc;ng c&ograve;n tr&ograve; chuyện c&aacute;i đề t&agrave;i n&agrave;y, rất chạy mau thi&ecirc;n đến C&uacute;p Ch&acirc;u &Acirc;u b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nDiệp Vĩ kh&ocirc;ng nh&igrave;n cầu, ch&iacute; &iacute;t kh&ocirc;ng nh&igrave;n b&oacute;ng đ&aacute;.<br />\r\n<br />\r\nMắt nh&igrave;n m&agrave;n h&igrave;nh dưới g&oacute;c phải thời gian, l&uacute;c n&agrave;y đ&atilde; 18: 00, vừa vặn cũng đến giờ cơm.<br />\r\n<br />\r\nĐem kh&ocirc;ng l&agrave;m xong đại t&aacute;c nghiệp thượng truyền đến v&acirc;n b&agrave;n, Diệp Vĩ đẩy ghế ra đứng dậy, đi xuống lầu nh&agrave; ăn điểm phần lạt k&ecirc; cơm chi&ecirc;n, mỹ tư tư ăn xong.<br />\r\n<br />\r\nKhi hắn thoải m&aacute;i nh&agrave;n nh&atilde; trở lại ph&ograve;ng ngủ thời điểm, nh&igrave;n thấy tr&ecirc;n b&agrave;n th&ugrave;ng giấy con, cả người bỗng nhi&ecirc;n sửng sốt một ch&uacute;t.<br />\r\n<br />\r\nChuyển ph&aacute;t nhanh?<br />\r\n<br />\r\n&quot;A vĩ, ngươi mua vật g&igrave;? Như thế to con đồ chơi?&quot;<br />\r\n<br />\r\nNgủ chung ph&ograve;ng anh em bu lại, mặt kh&aacute;c hai gia s&uacute;c cũng đem lực ch&uacute; &yacute; bỏ v&agrave;o b&ecirc;n n&agrave;y.<br />\r\n<br />\r\nTất cả mọi người l&agrave; người c&oacute; tư c&aacute;ch.<br />\r\n<br />\r\nTrước đ&oacute; Diệp Vĩ kh&ocirc;ng ở ph&ograve;ng ngủ, kh&ocirc;ng hiếu động hắn đồ vật. Hiện tại người trở về, cũng cũng kh&ocirc;ng sao.<br />\r\n<br />\r\nBọn hắn đều rất hiếu k&igrave;, gia hỏa n&agrave;y mua c&aacute;i g&igrave;.<br />\r\n<br />\r\n&quot;Ta kh&ocirc;ng c&oacute; mua đồ vật a... C&aacute;i đồ chơi n&agrave;y l&agrave; ai đưa tới?&quot;<br />\r\n<br />\r\nBa c&aacute;i bạn c&ugrave;ng ph&ograve;ng quen biết một ch&uacute;t.<br />\r\n<br />\r\n&quot;Ta kh&ocirc;ng ch&uacute; &yacute;...&quot;<br />\r\n<br />\r\n&quot;Ta cũng kh&ocirc;ng ch&uacute; &yacute;.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng biết, ta vừa đi lấy thức ăn ngo&agrave;i.&quot;<br />\r\n<br />\r\nHắc.<br />\r\n<br />\r\nBất thường!<br />\r\n<br />\r\nB&igrave;nh thường chuyển ph&aacute;t nhanh đều l&agrave; n&eacute;m t&uacute;c x&aacute; lầu dưới chuyển ph&aacute;t nhanh đứng, h&ocirc;m nay ngược lại l&agrave; đưa tới cửa.<br />\r\n<br />\r\nC&agrave;ng bất thường ch&iacute;nh l&agrave;, Diệp Vĩ suy nghĩ m&igrave;nh cũng kh&ocirc;ng c&oacute; ở mua qua Internet địa chỉ b&ecirc;n trong viết, m&igrave;nh l&agrave; ngồi c&aacute;i n&agrave;o chỗ ngồi.<br />\r\n<br />\r\nHiện tại chuyển ph&aacute;t nhanh tiểu ca đều ngưu bức như vậy sao?<br />\r\n<br />\r\nMở ra th&ugrave;ng giấy con, b&ecirc;n trong nằm một đầu mũ gi&aacute;p.<br />\r\n<br />\r\nC&aacute;i đồ chơi n&agrave;y tạo h&igrave;nh c&ugrave;ng mũ bảo hiểm xe m&aacute;y rất giống, trong ngo&agrave;i đều c&agrave; lấy nước sơn đen, nh&igrave;n xem bề ngo&agrave;i xấu x&iacute;.<br />\r\n<br />\r\nMuốn n&oacute;i chỗ n&agrave;o đặc biệt, chỉ sợ sẽ l&agrave; kh&ocirc;ng c&oacute; trước mặt, đeo l&ecirc;n đi to&agrave;n bộ đầu đều c&oacute; thể bao lại, căn bản c&aacute;i g&igrave; cũng nh&igrave;n kh&ocirc;ng thấy.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nChợt nh&igrave;n, Diệp Vĩ c&ograve;n tưởng rằng c&aacute;i đồ chơi n&agrave;y l&agrave; một c&aacute;i nồi.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y c&aacute;i g&igrave; đồ chơi.&quot;<br />\r\n<br />\r\nS&aacute;t v&aacute;ch b&agrave;n anh em cầm lấy mũ gi&aacute;p loay hoay xuống, đội ở tr&ecirc;n đầu, lại h&aacute;i xuống, kh&ocirc;ng hiểu ra sao c&ograve;n đưa Diệp Vĩ.<br />\r\n<br />\r\n&quot;Ngươi hỏi ta, ta hỏi ai.&quot;<br />\r\n<br />\r\nThật chẳng lẽ l&agrave; nồi?<br />\r\n<br />\r\nDiệp Vĩ cũng một mặt mộng bức thử đưa n&oacute; chụp tr&ecirc;n đầu.<br />\r\n<br />\r\nTầm mắt b&ecirc;n trong đen kịt một m&agrave;u.<br />\r\n<br />\r\nNgay tại l&uacute;c hắn đang muốn đưa n&oacute; lấy xuống thời điểm, một ch&ugrave;m nhạt &aacute;nh s&aacute;ng m&agrave;u lam s&aacute;ng bỗng nhi&ecirc;n xuy&ecirc;n thấu qua hắc &aacute;m, &aacute;nh v&agrave;o hắn v&otilde;ng mạc.<br />\r\n<br />\r\n【 chờ đợi tr&ograve; chơi k&iacute;ch hoạt: 7 1 giờ 19 ph&uacute;t 】<br />\r\n<br />\r\nC&aacute;i n&agrave;y. . .<br />\r\n<br />\r\nChẳng lẽ l&agrave; c&aacute;i kia mũ tr&ograve; chơi? !<br />\r\n<br />\r\nNgọa t&agrave;o.<br />\r\n<br />\r\nGiao h&agrave;ng tốc độ nhanh như vậy sao?<br />\r\n<br />\r\nDiệp Vĩ cả người đều cho&aacute;ng v&aacute;ng.<br />\r\n<br />\r\n&quot;C&aacute;c ngươi đều kh&ocirc;ng thấy sao?&quot;<br />\r\n<br />\r\nĐem đầu n&oacute;n trụ h&aacute;i xuống, Diệp Vĩ nh&igrave;n về ph&iacute;a vừa rồi mang theo mũ gi&aacute;p bạn c&ugrave;ng ph&ograve;ng.<br />\r\n<br />\r\nNhưng m&agrave; c&aacute;i kia bạn c&ugrave;ng ph&ograve;ng, đồng dạng một mặt mộng bức m&agrave; nh&igrave;n xem hắn.<br />\r\n<br />\r\n&quot;Tr&ocirc;ng thấy c&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\nDiệp Vĩ vội v&agrave;ng n&oacute;i: &quot;C&aacute;i kia đếm ngược a! Tr&ecirc;n đ&oacute; viết chờ đợi tr&ograve; chơi k&iacute;ch hoạt!&quot;<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave;? C&aacute;i đồ chơi n&agrave;y c&ograve;n c&oacute; thể l&agrave; m&aacute;y chơi game?&quot;<br />\r\n<br />\r\n&quot;Tr&aacute;nh ra tr&aacute;nh ra, Lưu ca con mắt kh&ocirc;ng tốt, ta tới nh&igrave;n một c&aacute;i.&quot;<br />\r\n<br />\r\nMột t&ecirc;n kh&aacute;c bạn c&ugrave;ng ph&ograve;ng tiếp nhận mũ gi&aacute;p đeo l&ecirc;n, nhưng m&agrave; một l&aacute;t sau, lại l&agrave; c&ugrave;ng trước đ&oacute; người kia đồng dạng, kh&ocirc;ng hiểu ra sao đem mũ gi&aacute;p h&aacute;i xuống.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; a.&quot;<br />\r\n<br />\r\n&quot;Ta tới.&quot;<br />\r\n<br />\r\nC&aacute;i n&agrave;y ba c&aacute;i bạn c&ugrave;ng ph&ograve;ng đều thử một lần, đều kh&ocirc;ng ngoại lệ c&aacute;i g&igrave; cũng kh&ocirc;ng thấy.<br />\r\n<br />\r\nNh&igrave;n nhau, bọn hắn kh&ocirc;ng hẹn m&agrave; c&ugrave;ng nh&igrave;n về ph&iacute;a Diệp Vĩ, biểu lộ cổ qu&aacute;i.<br />\r\n<br />\r\n&quot;Huynh đệ.&quot;<br />\r\n<br />\r\nDiệp Vĩ: &quot;... Thế n&agrave;o?&quot;<br />\r\n<br />\r\n&quot;Phải kh&ocirc;ng... Ngươi đi bệnh viện nh&igrave;n một c&aacute;i?&quot;<br />\r\n<br />\r\n&quot;C&uacute;t!&quot;<br />\r\n<br />\r\nDiệp Vĩ h&ugrave;ng h&ugrave;ng hổ hổ đoạt lấy mũ gi&aacute;p, kh&ocirc;ng tin t&agrave; lần nữa đeo l&ecirc;n.<br />\r\n<br />\r\nM&agrave;u lam nhạt chữ nhỏ đ&uacute;ng hạn m&agrave; tới.<br />\r\n<br />\r\n【 chờ đợi tr&ograve; chơi k&iacute;ch hoạt: 7 1 giờ 1 7 ph&uacute;t 】<br />\r\n<br />\r\n<br />\r\nĐếm ngược đi tới 2 ph&uacute;t đồng hồ.<br />\r\n<br />\r\nKh&ocirc;ng chỉ như đ&acirc;y...<br />\r\n<br />\r\nĐ&uacute;ng l&uacute;c n&agrave;y hắn chợt ph&aacute;t hiện, mặc kệ ch&iacute;nh m&igrave;nh l&agrave;m sao chuyển động mũ gi&aacute;p, vậy được đếm ngược vĩnh viễn xuất hiện tại m&igrave;nh tầm mắt ch&iacute;nh giữa.<br />\r\n<br />\r\nD&ugrave; l&agrave; hắn nhắm mắt lại.<br />\r\n<br />\r\nDiệp Vĩ lấy xuống mũ gi&aacute;p, một mặt gặp quỷ.<br />\r\n<br />\r\nNgọa t&agrave;o?<br />\r\n<br />\r\nThật đ&uacute;ng l&agrave; gặp quỷ!</p>', '2021-11-27 21:15:27', 7, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(173, 47, 'Sở Quang cùng kẹo que', '4', '<p>Hai trăm năm trước.<br />\r\n<br />\r\nChuẩn x&aacute;c m&agrave; n&oacute;i l&agrave; năm 2125, vi&ecirc;n n&agrave;y phồn vinh tinh cầu b&ecirc;n tr&ecirc;n bạo ph&aacute;t một trận chiến tranh.<br />\r\n<br />\r\nTrong chiến tranh song phương đều &ocirc;m triệt để hủy diệt đối phương quyết t&acirc;m, cơ hồ d&ugrave;ng tới tất cả mọi thứ c&oacute; thể d&ugrave;ng tới thủ đoạn.<br />\r\n<br />\r\nChiến tranh chỉ k&eacute;o d&agrave;i ba năm.<br />\r\n<br />\r\nNhưng cũng chỉ d&ugrave;ng ba năm, liền ph&aacute; hủy tr&ecirc;n mặt đất hết thảy.<br />\r\n<br />\r\nD&agrave;i dằng dặc hạch m&ugrave;a đ&ocirc;ng cơ hồ đem văn minh hỏa chủng dập tắt, song phương đều th&agrave;nh c&ocirc;ng đem đối phương đưa v&agrave;o Địa Ngục, cũng th&agrave;nh c&ocirc;ng mai t&aacute;ng ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\nTừ đ&oacute;, mở ra so lớn ti&ecirc;u điều c&agrave;ng th&ecirc;m ti&ecirc;u điều đ&acirc;́t ch&ecirc;́t kỷ nguy&ecirc;n.<br />\r\n<br />\r\nMặc d&ugrave; khoảng c&aacute;ch trận kia tận thế chi chiến đ&atilde; qua hai c&aacute;i thế kỷ, hạch m&ugrave;a đ&ocirc;ng cũng sớm tại hơn một c&aacute;i thế kỷ trước liền cơ bản đ&atilde; kết th&uacute;c, nhưng nh&acirc;n loại cũng kh&ocirc;ng c&oacute; một lần nữa trở lại đỉnh cao của chuỗi sinh vật sinh tồn trong tự nhi&ecirc;n.<br />\r\n<br />\r\nTr&agrave;n lan vũ kh&iacute; hạt nh&acirc;n, vũ kh&iacute; sinh vật, ch&iacute;nh l&agrave; đến gen vũ kh&iacute;, dẫn đến to&agrave;n bộ tinh cầu sinh th&aacute;i hệ thống đều hướng ph&iacute;a cực đoan phương hướng diễn biến.<br />\r\n<br />\r\nM&agrave; những c&aacute;i kia được xưng l&agrave; &quot;Dị chủng&quot; đột biến thể, cũng l&agrave; gi&atilde;y dụa tại phế t&iacute;ch b&ecirc;n trong những người sống s&oacute;t, đối mặt chủ yếu uy hiếp.<br />\r\n<br />\r\nTỉ như Sở Quang vừa tới đến thế giới n&agrave;y l&uacute;c, đụng phải chi kia mọc ra hai cái đ&acirc;̀u ch&oacute; săn, ch&iacute;nh l&agrave; một c&aacute;i trong số đ&oacute;.<br />\r\n<br />\r\nBất qu&aacute; liền xem như dị chủng, cũng l&agrave; c&oacute; ph&acirc;n chia mạnh yếu.<br />\r\n<br />\r\nGiống song đầu linh cẩu loại n&agrave;y từ Gamma xạ tuyến đưa đến &quot;Dị dạng &quot;, sức chiến đấu b&igrave;nh thường kh&ocirc;ng cao, trừ bỏ số &iacute;t may mắn ngoại lệ, sức chiến đấu thậm ch&iacute; so ra k&eacute;m đột biến trước đ&oacute;.<br />\r\n<br />\r\nSo s&aacute;nh dưới, gặm ăn người, [người b&ograve; s&aacute;t] c&aacute;c loại những n&agrave;y từ vũ kh&iacute; sinh vật tạo th&agrave;nh, lại đột biến đến đ&atilde; kh&ocirc;ng c&aacute;ch n&agrave;o tố nguy&ecirc;n ti&ecirc;n tổ dị chủng, mới thật sự l&agrave; l&agrave; giết ch&oacute;c m&agrave; th&agrave;nh qu&aacute;i vật.<br />\r\n<br />\r\nThần kinh của bọn n&oacute; hệ thống bị biến dị nấm ăn m&ograve;n, ban ng&agrave;y b&igrave;nh thường trốn ở phế t&iacute;ch, cống tho&aacute;t nước, hoặc l&agrave; t&agrave;u điện ngầm những n&agrave;y kh&ocirc;ng thấy &aacute;nh s&aacute;ng c&ocirc;ng tr&igrave;nh b&ecirc;n trong, chỉ cần l&agrave;m m&agrave;n đ&ecirc;m bu&ocirc;ng xuống mới c&oacute; thể ra đường tr&ecirc;n kiếm ăn.<br />\r\n<br />\r\nV&ugrave;ng ngoại th&agrave;nh t&igrave;nh huống lại so với trong th&agrave;nh phố tốt hơn nhiều.<br />\r\n<br />\r\nNhất l&agrave; ngoại th&agrave;nh.<br />\r\n<br />\r\nQu&aacute; khứ trong v&ograve;ng năm th&aacute;ng, Sở Quang đụng phải nguy hiểm nhất dị chủng, cũng bất qu&aacute; l&agrave; một đầu biến dị gấu ngựa. T&ecirc;n kia mặc d&ugrave; lực lượng cường h&atilde;n, nhưng tương đối phản ứng rất tr&igrave; độn.<br />\r\n<br />\r\nTại bị n&oacute; ph&aacute;t hiện trước đ&oacute;, Sở Quang rất cẩn thận tr&aacute;nh đi n&oacute;.<br />\r\n<br />\r\nTia nắng ban mai &aacute;nh s&aacute;ng nhạt xuy&ecirc;n qua tổn hại tường b&ecirc; t&ocirc;ng, c&ugrave;ng cốt th&eacute;p phế liệu c&aacute;i b&oacute;ng c&ugrave;ng một chỗ, rơi v&agrave;o tr&agrave;n đầy &ocirc; t&ocirc; h&agrave;i cốt c&ugrave;ng đ&aacute; vụn tr&ecirc;n đường.<br />\r\n<br />\r\nThời gian b&acirc;y giờ l&agrave; buổi s&aacute;ng t&aacute;m giờ.<br />\r\n<br />\r\nNơi n&agrave;y thời gian c&ugrave;ng hiện thế b&ecirc;n kia đ&ocirc;ng t&aacute;m m&uacute;i giờ, c&oacute; chừng mười hai giờ ch&ecirc;nh lệch.<br />\r\n<br />\r\nNh&igrave;n xem du đ&atilde;ng ở tr&ecirc;n đường hai con biến dị linh cẩu, Sở Quang nắm chặt trong tay c&acirc;y kia mang nhọn ống nước, cẩn thận v&acirc;y quanh phế t&iacute;ch ph&iacute;a sau.<br />\r\n<br />\r\nMặc d&ugrave; hắn c&oacute; tự tin xử l&yacute; n&oacute; hai, nhưng kh&ocirc;ng cần thiết phức tạp.<br />\r\n<br />\r\nHuống hồ những s&uacute;c sinh n&agrave;y cực kỳ th&ocirc;ng minh, thậm ch&iacute; biết cầm đồng loại l&agrave;m mồi nhử, ai cũng kh&ocirc;ng biết b&ecirc;n cạnh phế t&iacute;ch trong b&oacute;ng tối c&ograve;n cất giấu bao nhi&ecirc;u.<br />\r\n<br />\r\nDọc theo đường nhỏ xuy&ecirc;n qua r&aacute;ch n&aacute;t quảng trường.<br />\r\n<br />\r\nKhi thấy Bethe đường phố nhi đồng nhạc vi&ecirc;n bảng hiệu l&uacute;c, Sở Quang cuối c&ugrave;ng l&agrave; nhẹ nh&agrave;ng thở ra.<br />\r\n<br />\r\nPh&iacute;a trước liền l&agrave; &quot;Bethe đường phố&quot; &mdash;&mdash; phụ cận v&ugrave;ng n&agrave;y tương đối lớn người sống s&oacute;t khu quần cư, b&ecirc;n trong cư tr&uacute; hơn một trăm hộ người sống s&oacute;t.<br />\r\n<br />\r\nChiến tranh bộc ph&aacute;t trước đ&oacute;, nơi n&agrave;y đ&atilde; từng l&agrave; một chỗ đồng nhạc vi&ecirc;n, c&oacute; đại lượng chơi tr&ograve; chơi c&ocirc;ng tr&igrave;nh c&ugrave;ng một mảnh rộng lớn mặt cỏ.<br />\r\n<br />\r\nSau cuộc chiến nơi n&agrave;y bị qu&acirc;n đội chia l&agrave;m l&acirc;m thời tị nạn điểm, thu nhận đại lượng từ thanh tuyền thị nội th&agrave;nh b&ecirc;n trong trốn đi thị d&acirc;n.<br />\r\n<br />\r\nNhững c&aacute;i kia c&aacute;c nạn d&acirc;n về sau thế n&agrave;o kh&ocirc;ng thể n&agrave;o biết được, bất qu&aacute; hơn hai trăm năm sau h&ocirc;m nay, nơi n&agrave;y đ&atilde; ph&aacute;t triển th&agrave;nh một t&ograve;a &quot;Tiểu trấn&quot; .<br />\r\n<br />\r\nMọi người d&ugrave;ng vứt bỏ nhựa plastic tấm, lều tr&aacute;nh mưa c&ugrave;ng gỗ c&ugrave;ng kim loại gi&aacute; đỡ, tại trọc b&ugrave;n tr&ecirc;n mặt đất l&ocirc;̣n x&ocirc;̣n th&agrave;nh giản dị t&uacute;p lều.<br />\r\n<br />\r\nCực kỳ giống &quot;Frostpunk&quot; b&ecirc;n trong cái chủng loại kia.<br />\r\n<br />\r\nC&ocirc;ng vi&ecirc;n tr&ograve; chơi tường v&acirc;y l&agrave; tấm b&igrave;nh phong thi&ecirc;n nhi&ecirc;n, cũng tại trải qua th&ocirc; r&aacute;p tu sửa về sau, quấn l&ecirc;n d&acirc;y kẽm c&ugrave;ng mang c&aacute;i đinh tấm v&aacute;n gỗ.<br />\r\n<br />\r\nC&ocirc;ng vi&ecirc;n tr&ograve; chơi trung ương l&agrave; một t&ograve;a năm tầng cao cổ điển t&ograve;a th&agrave;nh, rất c&oacute; truyện cổ t&iacute;ch cảm gi&aacute;c. Chỉ l&agrave; kia mặt ngo&agrave;i sơn sớm đ&atilde; rơi sạch, mặt hướng trung t&acirc;m chợ kia một b&ecirc;n bức tường c&agrave;ng l&agrave; sụp đổ hơn ph&acirc;n nửa, chỉ c&ograve;n lại c&aacute;nh bắc nửa b&ecirc;n c&ugrave;ng một t&ograve;a lung lay sắp đổ c&ocirc; th&aacute;p.<br />\r\n<br />\r\nLiền xem như truyện cổ t&iacute;ch, cũng tuyệt đối hắc &aacute;m truyện cổ t&iacute;ch.<br />\r\n<br />\r\nBất qu&aacute; cho d&ugrave; t&ograve;a kiến tr&uacute;c n&agrave;y bị hư hao dạng n&agrave;y, n&oacute; vẫn l&agrave; Bethe tr&ecirc;n đường &quot;Xa hoa nhất&quot; kiến tr&uacute;c.<br />\r\n<br />\r\nĐồng thời, cũng l&agrave; nh&agrave; của trưởng trấn.<br />\r\n<br />\r\nMặc d&ugrave; trong n&agrave;y sinh sống năm th&aacute;ng, nhưng Sở Quang cũng chưa từng gặp qua trưởng trấn. Vị đại nh&acirc;n kia cực kỳ thần b&iacute;, rất &iacute;t xuất đầu lộ diện.<br />\r\n<br />\r\n&quot;Nha, sớm như vậy liền trở lại.&quot;<br />\r\n<br />\r\nNh&igrave;n xem từ đường đi g&oacute;c rẽ đi tới Sở Quang, treo thuốc l&aacute; rời l&atilde;o Walter h&iacute;p h&iacute;p mắt, từ trong lỗ mũi to&aacute;t ra một tia đục ngầu kh&oacute;i trắng.<br />\r\n<br />\r\nTrong tay của hắn cầm một c&acirc;y hai ống s&uacute;ng săn.<br />\r\n<br />\r\nMặc d&ugrave; nh&igrave;n xem nhiều năm rồi, nhưng kh&ocirc;ng ai sẽ ho&agrave;i nghi uy lực của n&oacute;.<br />\r\n<br />\r\nSở Quang đ&atilde; từng tận mắt nh&igrave;n thấy qua, l&atilde;o nh&acirc;n n&agrave;y chỉ nổ hai ph&aacute;t s&uacute;ng, liền đ&aacute;nh ng&atilde; một đầu ph&aacute;t cuồng ph&oacute;ng tới Bethe đường phố cửa lớn biến dị gấu ngựa.<br />\r\n<br />\r\nTừ đ&oacute; về sau, hắn liền một mực kh&aacute;t vọng cũng c&oacute; thể c&oacute; được một thanh.<br />\r\n<br />\r\n&quot;Ở b&ecirc;n ngo&agrave;i chậm trễ một đ&ecirc;m.&quot;<br />\r\n<br />\r\n&quot;B&ecirc;n ngo&agrave;i?&quot;<br />\r\n<br />\r\nL&atilde;o đầu xem x&eacute;t mắt Sở Quang c&otilde;ng ở sau lưng c&acirc;y kia mang nhọn ống nước, l&ocirc;ng m&agrave;y n&acirc;ng l&ecirc;n một tia kinh ngạc.<br />\r\n<br />\r\nBan đ&ecirc;m nguy hiểm cỡ n&agrave;o, kh&ocirc;ng ai so với hắn r&otilde; r&agrave;ng hơn.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nMỗi lần trực ca đ&ecirc;m, hắn ng&oacute;n trỏ cũng kh&ocirc;ng d&aacute;m từ tr&ecirc;n c&ograve; s&uacute;ng dịch chuyển khỏi , bất kỳ c&aacute;i g&igrave; t&iacute; x&iacute;u gi&oacute; thổi cỏ lay, đều sẽ để to&agrave;n th&acirc;n hắn thần kinh k&eacute;o căng.<br />\r\n<br />\r\nNgoại th&agrave;nh dị chủng mặc d&ugrave; kh&ocirc;ng th&agrave;nh khu b&ecirc;n trong nguy hiểm như vậy, nhưng c&oacute; qu&aacute; nhiều kẻ cướp đoạt đối với nơi n&agrave;y nh&igrave;n chằm chằm.<br />\r\n<br />\r\nRơi v&agrave;o tr&ecirc;n tay của bọn hắn, hạ tr&agrave;ng kh&ocirc;ng thể so với chết tại dị chủng tr&ecirc;n tay tốt qu&aacute; nhiều.<br />\r\n<br />\r\nL&atilde;o Walter kh&ocirc;ng qu&aacute; tin tưởng, gia hỏa n&agrave;y chỉ dựa v&agrave;o một c&acirc;y ống th&eacute;p, thế m&agrave; tại đ&acirc;́t ch&ecirc;́t tr&ecirc;n b&igrave;nh an vượt qua một đ&ecirc;m.<br />\r\n<br />\r\n&quot;Xảy ra ch&uacute;t ngo&agrave;i &yacute; muốn.&quot;<br />\r\n<br />\r\nSở Quang cũng kh&ocirc;ng giải th&iacute;ch, chỉ cấp l&atilde;o Walter một c&aacute;i mỏi mệt &aacute;nh mắt để ch&iacute;nh hắn đi thể hội, tiếp lấy trực tiếp xuy&ecirc;n thẳng qua khu quần cư cửa lớn.<br />\r\n<br />\r\nBethe đường phố chỉ c&oacute; một nh&agrave; phế phẩm vựa ve chai, vị tr&iacute; rất dễ t&igrave;m, s&aacute;t b&ecirc;n khu quần cư cửa ch&iacute;nh b&ecirc;n cạnh.<br />\r\n<br />\r\nCửa cuốn vạt &aacute;o lấy một đ&agrave;i vĩnh viễn đi kh&ocirc;ng cho ph&eacute;p kiểu cũ c&acirc;n điện tử, b&ecirc;n cạnh đứng thẳng &quot;Gi&aacute; cả vừa phải, gi&agrave; trẻ kh&ocirc;ng gạt&quot; đền thờ.<br />\r\n<br />\r\nTiệm n&agrave;y l&agrave; trấn trưởng đại nh&acirc;n sản nghiệp, cũng l&agrave; Bethe đường phố duy nhất thu mua vứt bỏ linh kiện c&ugrave;ng biến dị chu&ocirc;̣t đ&ocirc;̀ng da địa phương.<br />\r\n<br />\r\nV&igrave; lũng đoạn nhặt đồ bỏ đi sinh &yacute;, vị nh&agrave; độc t&agrave;i n&agrave;y thậm ch&iacute; ngang ngược ban bố một hạng ph&aacute;p luật.<br />\r\n<br />\r\nTức, tất cả mọi người kh&ocirc;ng được đem bắt được con mồi c&ugrave;ng nhặt được r&aacute;c rưởi, tự m&igrave;nh b&aacute;n cho dọc đường nơi đ&acirc;y thương đội.<br />\r\n<br />\r\nM&agrave; l&yacute; do th&igrave; l&agrave; v&igrave; cam đoan Bethe đường phố h&agrave;ng h&oacute;a c&oacute; thể b&aacute;n đi hợp l&yacute; gi&aacute; cả, m&agrave; kh&ocirc;ng phải bị &quot;Giảo hoạt c&aacute;c thương nh&acirc;n&quot; tại gi&aacute; cả tr&ecirc;n v&ocirc; sỉ nghiền &eacute;p.<br />\r\n<br />\r\nLoại n&agrave;y ngang ngược v&ocirc; l&yacute; quy củ sở dĩ c&oacute; thể th&ocirc;ng qua, tr&igrave;nh độ nhất định vậy c&ugrave;ng nơi n&agrave;y những người sống s&oacute;t đều cực kỳ ngu muội c&oacute; quan hệ.<br />\r\n<br />\r\nM&agrave; đồng dạng thương đội cũng sẽ kh&ocirc;ng bốc l&ecirc;n đắc tội vị nh&agrave; độc t&agrave;i n&agrave;y phong hiểm, đi những người nhặt r&aacute;c tr&ecirc;n tay thu mua một ch&uacute;t kia &iacute;t đến thương cảm vật tư.<br />\r\n<br />\r\nBọn hắn chỉ l&agrave;m mua b&aacute;n lớn.<br />\r\n<br />\r\nM&agrave; lại chỉ c&ugrave;ng người tin cẩn bu&ocirc;n b&aacute;n.<br />\r\n<br />\r\n&quot;B&aacute;n? Vẫn l&agrave; mua?&quot;<br />\r\n<br />\r\nChủ cửa h&agrave;ng l&agrave; một vị năm mươi tuổi l&atilde;o nh&acirc;n, t&ecirc;n gọi Charles, nghe n&oacute;i trước kia l&agrave; tỉnh ngo&agrave;i c&aacute;i n&agrave;o đ&oacute; chỗ tr&aacute;nh nạn cư d&acirc;n, về sau bị chộp tới l&agrave;m n&ocirc; lệ, thẳng đến trưởng trấn đem hắn từ c&aacute;i n&agrave;o đ&oacute; chủ n&ocirc; tr&ecirc;n tay mua lại, đem c&ugrave;ng những người nhặt r&aacute;c li&ecirc;n hệ c&ocirc;ng việc giao cho hắn.<br />\r\n<br />\r\nNơi n&agrave;y đại đa số cư d&acirc;n đều l&agrave; sinh trưởng ở địa phương &quot;Đ&acirc;́t ch&ecirc;́t người&quot;, kh&ocirc;ng c&oacute; nhận qua bất luận c&aacute;i g&igrave; gi&aacute;o dục, to&aacute;n học đều rất dở, m&agrave; lại l&agrave; n&aacute;t đến một trăm trong v&ograve;ng th&ecirc;m ph&eacute;p trừ đều c&oacute; thể t&iacute;nh sai tr&igrave;nh độ.<br />\r\n<br />\r\nNhưng Charles kh&aacute;c biệt.<br />\r\n<br />\r\nHắn l&agrave; từ chỗ tr&aacute;nh nạn b&ecirc;n trong ra.<br />\r\n<br />\r\nMặc d&ugrave; mảnh n&agrave;y đ&acirc;́t ch&ecirc;́t tr&ecirc;n chỗ tr&aacute;nh nạn kh&ocirc;ng c&oacute; một t&ograve;a l&agrave; ho&agrave;n to&agrave;n giống nhau, nhưng c&oacute; một ch&uacute;t lại l&agrave; cơ bản giống nhau.<br />\r\n<br />\r\nC&oacute; thể v&agrave;o ở người ở đ&oacute; kh&ocirc;ng c&oacute; chỗ n&agrave;o m&agrave; kh&ocirc;ng phải l&agrave; trước khi chiến đấu x&atilde; hội tinh anh, con của bọn hắn chẳng những theo cha bối nơi đ&oacute; kế thừa th&ocirc;ng minh đầu n&atilde;o, c&agrave;ng l&agrave; từ vỡ l&ograve;ng thời đại liền tiếp nhận tốt đẹp gi&aacute;o dục.<br />\r\n<br />\r\nNếu như kh&ocirc;ng phải thế giới n&agrave;y hỏng b&eacute;t, hắn đại kh&aacute;i sẽ giống hắn bậc cha ch&uacute; đồng dạng, trở th&agrave;nh một c&ocirc;ng tr&igrave;nh sư, b&aacute;c sĩ hoặc l&agrave; học giả.<br />\r\n<br />\r\nM&agrave; kh&ocirc;ng phải trong n&agrave;y k&yacute; sổ.<br />\r\n<br />\r\n&quot;B&aacute;n.&quot;<br />\r\n<br />\r\nKh&ocirc;ng n&oacute;i nhảm, Sở Quang từ hai vai trong bọc m&oacute;c ra 6 khối vứt bỏ pin, c&ugrave;ng 5 quản chất keo d&iacute;nh n&eacute;m v&agrave;o c&acirc;n điện tử tr&ecirc;n khay.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; hắn tại ph&aacute;t hiện số 404 chỗ tr&aacute;nh nạn trước đ&oacute;, tại phụ cận kiến tr&uacute;c h&agrave;i cốt b&ecirc;n trong vơ v&eacute;t đến.<br />\r\n<br />\r\nL&atilde;o Charles nhặt l&ecirc;n tr&ecirc;n b&agrave;n phế pin, t&ugrave;y &yacute; kiểm tra một ch&uacute;t loại h&igrave;nh c&ugrave;ng phải chăng trướng kh&iacute; tổn hại, liền đưa n&oacute; tiện tay n&eacute;m v&agrave;o một b&ecirc;n tr&ecirc;n c&aacute;i c&acirc;n.<br />\r\n<br />\r\nC&aacute;i đồ chơi n&agrave;y khẳng định l&agrave; b&aacute;o phế kh&ocirc;ng cần nghĩ, nhưng thu về một ch&uacute;t b&ecirc;n trong vật liệu vẫn l&agrave; c&oacute; thể.<br />\r\n<br />\r\n&quot;Phẩm chất cũng tạm được, v&ugrave;ng n&agrave;y r&aacute;c rưởi hẳn l&agrave; đều bị lục so&aacute;t sạch sẽ, ngươi từ chỗ n&agrave;o l&agrave;m cho đồ tốt?&quot;<br />\r\n<br />\r\nC&aacute;i n&agrave;y cũng c&oacute; thể để đồ tốt?<br />\r\n<br />\r\n&quot;Vận kh&iacute; tốt.&quot;<br />\r\n<br />\r\n&quot;Ha ha, ta chỉ l&agrave; thuận miệng hỏi một ch&uacute;t. &Acirc;n, pin kh&ocirc;ng sai, nhưng chất keo d&iacute;nh chất lượng liền b&igrave;nh thường, đ&oacute;ng k&iacute;n hủy đi qua, t&igrave;nh huống b&ecirc;n trong đo&aacute;n chừng rối loạn, ta chỉ c&oacute; thể cho ngươi theo 50% t&iacute;nh. . . Tất cả cộng lại hết thảy 3 điểm thẻ đ&aacute;nh bạc.&quot;<br />\r\n<br />\r\nSở Quang cũng kh&ocirc;ng c&oacute; c&ugrave;ng hắn trả gi&aacute;, từ trong tay hắn nhận lấy 3 vi&ecirc;n m&agrave;u trắng thẻ đ&aacute;nh bạc.<br />\r\n<br />\r\nLoại n&agrave;y c&oacute; kim loại cảm nhận nhựa plastic tiền xu, l&agrave; Thanh Tuyền th&agrave;nh phố lớn nhất người sống s&oacute;t khu quần cư Cự Thạch th&agrave;nh ph&aacute;t h&agrave;nh &quot;Tiền tệ&quot;, đồng thời tại Thanh Tuyền th&agrave;nh phố đại đa số người sống s&oacute;t khu quần cư đều c&oacute; thể đổi được đồ ăn c&ugrave;ng tiếp tế.<br />\r\n<br />\r\nThẻ đ&aacute;nh bạc ch&iacute;nh diện in mặt gi&aacute; trị, mặt sau c&oacute; đặc th&ugrave; ph&ograve;ng ngụy m&atilde; h&oacute;a c&ugrave;ng in hoa, tại &aacute;nh mặt trời chiếu xuống sẽ b&agrave;y biện ra đặc th&ugrave; quang trạch.<br />\r\n<br />\r\nLoại n&agrave;y thẻ đ&aacute;nh bạc c&oacute; chịu nhiệt chiếu cố, dễ d&agrave;ng bảo tồn, lại ph&acirc;n biệt độ cao rất nhiều ưu điểm. Nhất l&agrave; mấu chốt nhất m&ocirc;̣t đi&ecirc;̉m là, lấy sau chiến tranh kỹ thuật cơ bản kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o m&ocirc; phỏng.<br />\r\n<br />\r\nGiống Bethe đường phố loại n&agrave;y nh&acirc;n khẩu kh&ocirc;ng nhiều, kh&ocirc;ng c&oacute; c&ocirc;ng nghiệp năng lực cỡ trung tiểu người sống s&oacute;t l&agrave;ng x&oacute;m, chủ yếu cũng l&agrave; c&ugrave;ng Cự Thạch th&agrave;nh thương đội l&agrave;m ăn, d&ugrave;ng n&ocirc;ng sản phẩm, con mồi c&ugrave;ng nhặt được r&aacute;c rưởi trao đổi cần thiết sinh hoạt vật tư thậm ch&iacute; l&agrave; vũ kh&iacute;.<br />\r\n<br />\r\nTự nhi&ecirc;n m&agrave; vậy, loại tiền tệ n&agrave;y cũng liền tại Bethe đường phố lưu th&ocirc;ng.<br />\r\n<br />\r\nĐương nhi&ecirc;n, n&oacute; cũng kh&ocirc;ng phải bất cứ l&uacute;c n&agrave;o đều dễ d&ugrave;ng, nếu như c&aacute;i n&agrave;o nguyệt thương đội kh&ocirc;ng đến, to&agrave;n bộ khu quần cư gi&aacute; h&agrave;ng đều phải lộn xộn.<br />\r\n<br />\r\nTrưởng trấn cũng kh&ocirc;ng phải l&agrave; kh&ocirc;ng c&oacute; thử qua phổ biến Bethe đường phố m&igrave;nh tiền tệ &mdash;&mdash; một loại k&yacute; sổ thức phiếu quyển, nhưng l&agrave;m sao căn bản kh&ocirc;ng ai thừa nhận.<br />\r\n<br />\r\nLiền ngay cả Bethe đường phố người sống s&oacute;t đều biết, loại kia trang giấy liền ngay cả ch&ugrave;i đ&iacute;t đều vướng bận, c&ugrave;ng giấy lộn kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c biệt.<br />\r\n<br />\r\n&quot;Muốn mua ch&uacute;t g&igrave; sao? Trong tiệm vừa tới một nh&oacute;m từ Cự Thạch th&agrave;nh tới hàng mới.&quot;<br />\r\n<br />\r\nĐang định rời đi Sở Quang dừng bước, quay đầu lại hỏi nói.<br />\r\n<br />\r\n&quot;C&oacute; s&uacute;ng sao?&quot;<br />\r\n<br />\r\n&quot;Thế th&igrave; kh&ocirc;ng c&oacute;, c&oacute; ngươi cũng mua kh&ocirc;ng nổi.&quot;<br />\r\n<br />\r\nL&atilde;o Charles nhếch miệng cười cười, nh&igrave;n xem quay người muốn đi Sở Quang tiếp tục n&oacute;i.<br />\r\n<br />\r\n&quot;Bất qu&aacute; đồ ăn c&ugrave;ng nhi&ecirc;n liệu ngược lại l&agrave; c&oacute; một &iacute;t. Nếu như ta l&agrave; ngươi, nhất định sẽ thừa dịp tăng gi&aacute; trước nhiều mua một &iacute;t.&quot;<br />\r\n<br />\r\nS&uacute;ng loại vật n&agrave;y, cho d&ugrave; l&agrave; rẻ nhất cái chủng loại kia, đều rất kh&oacute; tại Bethe đường phố chỗ như vậy mua được.<br />\r\n<br />\r\nBởi v&igrave; coi như ngẫu nhi&ecirc;n c&oacute; bu&ocirc;n b&aacute;n s&uacute;ng ống đạn được thương đội đi ngang qua nơi n&agrave;y, những vũ kh&iacute; kia b&igrave;nh thường cũng đều l&agrave; tiến trưởng trấn m&igrave;nh nh&agrave; kho, căn bản sẽ kh&ocirc;ng b&agrave;y trong n&agrave;y kệ h&agrave;ng mặc người chọn lựa.<br />\r\n<br />\r\nHuống hồ tựa như l&atilde;o Charles n&oacute;i như vậy, coi như thật sự c&oacute; loại đồ vật n&agrave;y, bọn hắn những người nhặt r&aacute;c n&agrave;y cũng kh&ocirc;ng c&oacute; khả năng mua được.<br />\r\n<br />\r\nSở Quang rất r&otilde; r&agrave;ng hắn v&igrave; c&aacute;i g&igrave; n&oacute;i như vậy.<br />\r\n<br />\r\nL&agrave;m tiếp thụ qua gi&aacute;o dục tốt chỗ tr&aacute;nh nạn cư d&acirc;n, Charles nhất định sẽ kh&ocirc;ng nh&igrave;n kh&ocirc;ng ra, tất cả sinh hoạt tại Bethe đường phố người sống s&oacute;t, mặc kệ l&agrave; người nhặt r&aacute;c vẫn l&agrave; thợ săn, bọn hắn gi&aacute; trị thặng dư đều bị trưởng trấn người một nh&agrave; b&oacute;c lột kh&ocirc;ng c&ograve;n một mảnh.<br />\r\n<br />\r\nCho d&ugrave; trấn trưởng đại nh&acirc;n chưa hề tự m&igrave;nh đưa tay, từ bọn hắn trong t&uacute;i trực tiếp m&oacute;c đi đ&acirc;u sợ một vi&ecirc;n thẻ đ&aacute;nh bạc.<br />\r\n<br />\r\n&quot;Đồ ăn c&ugrave;ng nhi&ecirc;n liệu muốn l&ecirc;n gi&aacute;?&quot;<br />\r\n<br />\r\nNh&igrave;n xem tr&ecirc;n mặt viết ngo&agrave;i &yacute; muốn Sở Quang, Charles nh&agrave;n nhạt cười một c&aacute;i n&oacute;i.<br />\r\n<br />\r\n&quot;Ngươi kh&ocirc;ng ch&uacute; &yacute; tới sao, gần nhất tại hạ nhiệt độ, m&agrave; ph&iacute;a ngo&agrave;i dị chủng lại bắt đầu sinh động.&quot;<br />\r\n<br />\r\nSở Quang nh&iacute;u m&agrave;y suy tư một l&aacute;t, bỗng nhi&ecirc;n nghĩ tới điều g&igrave;, mở miệng n&oacute;i ra.<br />\r\n<br />\r\n&quot;M&ugrave;a đ&ocirc;ng muốn tới?&quot;<br />\r\n<br />\r\n&quot;Ta nhớ được ngươi l&agrave; năm th&aacute;ng trước mới tới chỗ n&agrave;y, khả năng kh&ocirc;ng c&oacute; trải qua, những năm qua l&uacute;c n&agrave;y kh&ocirc;ng sai biệt lắm liền muốn bắt đầu chậm r&atilde;i hạ nhiệt độ. Năm nay. . . M&ugrave;a đ&ocirc;ng đo&aacute;n chừng sẽ đến sớm một ch&uacute;t, l&agrave;m kh&ocirc;ng tốt l&uacute;c th&aacute;ng mười liền muốn tuyết rơi.&quot;<br />\r\n<br />\r\nN&oacute;i đến đ&acirc;y, Charles dừng lại một l&aacute;t, n&oacute;i một c&aacute;ch đầy &yacute; vị s&acirc;u xa nói.<br />\r\n<br />\r\n&quot;Muốn bắt đầu m&ugrave;a đ&ocirc;ng.&quot;<br />\r\n<br />\r\n&quot;Mặc kệ l&agrave; người vẫn l&agrave; dị chủng, đều phải sớm l&agrave;m chuẩn bị.&quot;<br />\r\n<br />\r\nVừa tới Bethe đường phố thời điểm, Sở Quang mặc tr&ecirc;n người kiện m&agrave;u lam &aacute;o kho&aacute;c, Charles v&ocirc; &yacute; thức đem hắn trở th&agrave;nh chỗ tr&aacute;nh nạn người, cho n&ecirc;n đối với hắn coi như chiếu cố.<br />\r\n<br />\r\nMặc d&ugrave; loại n&agrave;y chiếu cố chưa hề thể hiện tại giao dịch gi&aacute; cả b&ecirc;n tr&ecirc;n, nhưng ở phương diện kinh nghiệm l&atilde;o nh&acirc;n n&agrave;y x&aacute;c thực gi&uacute;p Sở Quang một ch&uacute;t.<br />\r\n<br />\r\nNếu kh&ocirc;ng, hắn cũng sẽ kh&ocirc;ng như thế nhanh liền th&iacute;ch ứng đ&acirc;́t ch&ecirc;́t tr&ecirc;n sinh hoạt.<br />\r\n<br />\r\nSở Quang nghi&ecirc;m t&uacute;c gật đầu.<br />\r\n<br />\r\n&quot;Ta đ&atilde; biết, tạ ơn.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng kh&aacute;ch kh&iacute;, &quot; Charles cười nhạt cười, &quot;Cũng đừng chết rồi.&quot;<br />\r\n<br />\r\nHiện tại đ&atilde; l&agrave; đầu th&aacute;ng ch&iacute;n, nếu quả như thật l&uacute;c th&aacute;ng mười liền muốn tuyết rơi, lưu chuẩn bị cho Sở Quang thời gian chỉ c&ograve;n hơn một th&aacute;ng.<br />\r\n<br />\r\nĐối với sinh hoạt tại v&ugrave;ng n&agrave;y những người sống s&oacute;t m&agrave; n&oacute;i, tuyết rơi tuyệt đối kh&ocirc;ng phải c&aacute;i g&igrave; đ&aacute;ng phải cao hứng sự t&igrave;nh. &Yacute; vị n&agrave;y ngoại trừ đồ ăn b&ecirc;n ngo&agrave;i, c&ograve;n phải tăng th&ecirc;m một b&uacute;t nhi&ecirc;n liệu chi ti&ecirc;u.<br />\r\n<br />\r\nNơi n&agrave;y tuyệt đại đa số người đều l&agrave; lấy nhặt đồ bỏ đi c&ugrave;ng đi săn m&agrave; sống, phụ cận n&ocirc;ng trường chỉ c&oacute; tại ng&agrave;y m&ugrave;a kỳ mới c&acirc;̀n người.<br />\r\n<br />\r\nĐến m&ugrave;a đ&ocirc;ng, nhặt đồ bỏ đi độ kh&oacute; sẽ gấp bội l&ecirc;n cao, d&ugrave; ai cũng kh&ocirc;ng c&aacute;ch n&agrave;o đo&aacute;n trước tuyết b&ecirc;n trong ch&ocirc;n ch&iacute;nh l&agrave; c&oacute; thể phế vật lợi dụng linh kiện, vẫn l&agrave; biến dị chuột răng nanh.<br />\r\n<br />\r\nM&agrave; giống như l&agrave; d&atilde; hươu, con thỏ những n&agrave;y c&oacute; thể cung cấp ăn thịt động vật, cũng sẽ giảm bớt thời gian hoạt động, thậm ch&iacute; dứt kho&aacute;t tr&ocirc;́n ở trong ổ qua m&ugrave;a đ&ocirc;ng.<br />\r\n<br />\r\nChỗ chết người nhất ch&iacute;nh l&agrave;, một khi bắt đầu m&ugrave;a đ&ocirc;ng, thương đội liền sẽ kh&ocirc;ng tới nơi n&agrave;y. Coi như nhặt được đồ tốt cũng chỉ c&oacute; thể chồng chất tại trong nh&agrave;, đợi đến năm sau đầu xu&acirc;n mới c&oacute; thể b&aacute;n rơi.<br />\r\n<br />\r\nHoặc l&agrave;, mạo hiểm tiến về mười c&acirc;y số b&ecirc;n ngo&agrave;i Cự Thạch th&agrave;nh.<br />\r\n<br />\r\nKia l&agrave; Thanh Tuyền th&agrave;nh phố v&ugrave;ng n&agrave;y lớn nhất người sống s&oacute;t khu quần cư, cho d&ugrave; l&agrave; m&ugrave;a đ&ocirc;ng nơi n&agrave;o thị trường cũng sẽ kh&ocirc;ng đ&oacute;ng cửa.<br />\r\n<br />\r\nNhưng m&agrave; vị tr&iacute; của n&oacute; tại Thanh Tuyền th&agrave;nh phố tam ho&agrave;n đường b&ecirc;n cạnh, li&ecirc;n tiếp Thanh Tuyền th&agrave;nh phố th&agrave;nh Bắc khu, tr&ecirc;n đường đi cất giấu đếm kh&ocirc;ng hết nguy hiểm.<br />\r\n<br />\r\nNgười b&igrave;nh thường muốn dựa v&agrave;o hai c&aacute;i đ&ugrave;i đi qua, hơn nữa c&ograve;n l&agrave; tại nhiệt độ thấp nhất c&oacute; thể đạt tới &acirc;m mười độ m&ugrave;a đ&ocirc;ng, kh&ocirc;ng kh&aacute;c tự s&aacute;t!<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nTừ vựa ve chai rời đi về sau, Sở Quang đầu ti&ecirc;n l&agrave; trở về một chuyến trụ sở của m&igrave;nh.<br />\r\n<br />\r\nKỳ thật c&ugrave;ng n&oacute; n&oacute;i l&agrave; trụ sở, chẳng bằng n&oacute;i l&agrave; một t&ograve;a miễn cưỡng c&oacute; thể che mưa t&uacute;p lều, thậm ch&iacute; đều kh&ocirc;ng c&oacute; c&aacute;i ra d&aacute;ng cửa sổ c&ugrave;ng cửa.<br />\r\n<br />\r\nThẳng đến h&ocirc;m qua mới th&ocirc;i, hắn c&ograve;n muốn lấy tích lũy lướt nước b&ugrave;n, PVC tấm loại h&igrave;nh vật liệu, đuổi tại m&ugrave;a đ&ocirc;ng tiến đến trước đ&oacute; đem c&aacute;i n&agrave;y hở tường cho che lại.<br />\r\n<br />\r\nNhưng hiện tại xem ra, đ&atilde; kh&ocirc;ng cần thiết n&agrave;y.<br />\r\n<br />\r\nTừ mốc meo t&uacute;i ngủ b&ecirc;n trong lật ra đến một khối rỉ s&eacute;t nh&ocirc;m hộp, Sở Quang ph&iacute; sức đẩy ra n&oacute;, đem n&uacute;p ở b&ecirc;n trong nhựa plastic tiền xu soạt ng&atilde; xuống qu&egrave; ch&acirc;n c&aacute;i b&agrave;n gỗ b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nHết thảy 47 vi&ecirc;n mặt gi&aacute; trị 1 điểm m&agrave;u trắng thẻ đ&aacute;nh bạc.<br />\r\n<br />\r\nT&iacute;nh đến tr&ecirc;n người m&igrave;nh 3 vi&ecirc;n, cộng lại vừa vặn 50 điểm!<br />\r\n<br />\r\nTiền n&agrave;y đều l&agrave; hắn bớt ăn bớt mặc, một ch&uacute;t x&iacute;u để d&agrave;nh được tới, v&igrave; ch&iacute;nh l&agrave; c&oacute; một ng&agrave;y c&oacute; thể rời đi c&aacute;i địa phương quỷ qu&aacute;i n&agrave;y, dọn đi điều kiện tốt hơn Cự Thạch th&agrave;nh.<br />\r\n<br />\r\nBất qu&aacute; b&acirc;y giờ nha. . .<br />\r\n<br />\r\nHắn đối khoản n&agrave;y &quot;T&agrave;i sản&quot; đ&atilde; c&oacute; mới quy hoạch.<br />\r\n<br />\r\nC&oacute; thể tự lập m&ocirc;n hộ, l&agrave;m g&igrave; ăn nhờ ở đậu?<br />\r\n<br />\r\n&quot;Chỗ tr&aacute;nh nạn ph&iacute;a tr&ecirc;n trại an dưỡng phế t&iacute;ch c&oacute; thể lợi dụng, c&ograve;n c&oacute; kiến tr&uacute;c chung quanh tường v&acirc;y, đều l&agrave; b&ecirc; t&ocirc;ng kết cấu. . . C&oacute; người chơi c&oacute; thể sai sử, tại phụ cận sưu tập một &iacute;t vật liệu, tu sửa độ kh&oacute; hẳn l&agrave; sẽ kh&ocirc;ng qu&aacute; lớn.&quot;<br />\r\n<br />\r\n&quot;Trại an dưỡng phụ cận l&agrave; Thấp Địa c&ocirc;ng vi&ecirc;n, sưu tập kim loại phế phẩm kh&ocirc;ng tiện lắm, nhưng chung quanh thảm thực vật coi như tươi tốt, sưởi ấm nhi&ecirc;n liệu hẳn l&agrave; sẽ kh&ocirc;ng qu&aacute; thiếu, trừ c&aacute;i đ&oacute; ra gỗ c&ograve;n c&oacute; thể l&agrave;m tu bổ kiến tr&uacute;c c&ugrave;ng chế t&aacute;c đồ d&ugrave;ng trong nh&agrave; vật liệu. . . R&igrave;u! Đúng, c&ograve;n phải mua bốn thanh b&uacute;a.&quot;<br />\r\n<br />\r\nĐ&aacute;nh qu&aacute;i luyện cấp?<br />\r\n<br />\r\nKh&ocirc;ng tồn tại!<br />\r\n<br />\r\nĐều n&oacute;i l&agrave; trăm phần trăm ch&acirc;n thực cứng rắn hạch tr&ograve; chơi, nếu l&agrave; cứng rắn hạch tr&ograve; chơi, vậy dĩ nhi&ecirc;n đ&ecirc;́n từ cơ sở nhất c&ocirc;ng việc bắt đầu l&agrave;m l&ecirc;n!</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Thuổng sắt c&ugrave;ng c&aacute;i cưa những c&ocirc;ng cụ n&agrave;y cũng kh&ocirc;ng c&oacute; thể thiếu!&quot; Người chơi c&ograve;n kh&ocirc;ng đăng lục, Sở Quang liền đ&atilde; nghĩ kỹ an b&agrave;i thế n&agrave;o bọn hắn.<br />\r\n<br />\r\nĐương nhi&ecirc;n, ngoại trừ l&agrave;m việc m&agrave; c&ocirc;ng cụ, c&ograve;n c&oacute; mấu chốt nhất khẩu phần lương thực.<br />\r\n<br />\r\nBồi dưỡng khoang thuyền một khi k&iacute;ch hoạt, liền sẽ lập tức ti&ecirc;u hao dự chứa đựng tại bồi dưỡng trong khoang thuyền hoạt t&iacute;nh vật chất, hợp th&agrave;nh cung cấp người chơi sử dụng nh&acirc;n bản thể.<br />\r\n<br />\r\nM&agrave; những n&agrave;y nh&acirc;n bản thể cũng phải cần ăn cơm!<br />\r\n<br />\r\nMặc d&ugrave; người chơi kh&ocirc;ng online thời điểm, nh&acirc;n bản thể c&oacute; thể nằm tại bồi dưỡng trong khoang thuyền, lấy hơi thấp thay thế tr&igrave;nh độ ngủ đ&ocirc;ng, nhưng lu&ocirc;n kh&ocirc;ng khả năng một mực nằm.<br />\r\n<br />\r\nNgười sống liền muốn ăn cơm, đ&acirc;y l&agrave; tuy&ecirc;n cổ bất biến ch&acirc;n l&yacute;!<br />\r\n<br />\r\n&quot;&Iacute;t nhất phải dự trữ đầy đủ năm t&ecirc;n người trưởng th&agrave;nh một tuần ti&ecirc;u hao lương thực. . . Nếu như theo một ng&agrave;y hai bữa t&iacute;nh, mỗi bữa một trương b&aacute;nh nếp, cũng phải 70 tấm.&quot;<br />\r\n<br />\r\nB&aacute;nh nếp l&agrave; Bethe đường phố thường thấy nhất đồ ăn, 1 điểm thẻ đ&aacute;nh bạc liền c&oacute; thể đ&ocirc;̉i hai tấm.<br />\r\n<br />\r\nC&aacute;i đồ chơi n&agrave;y đồng dạng c&oacute; to bằng b&agrave;n tay, cắn cảm gi&aacute;c rất cứng, cũng cực kỳ th&ocirc; r&aacute;p, giống trộn lẫn hạt c&aacute;t thổ, nhưng c&oacute; thể nh&eacute;t đầy c&aacute;i bao tử, c&ograve;n c&oacute; thể bổ sung một ch&uacute;t x&iacute;u muối điểm.<br />\r\n<br />\r\nNh&eacute;t v&agrave;o trong nồi nấu, c&ograve;n c&oacute; thể l&agrave;m ch&aacute;o.<br />\r\n<br />\r\n70 miếng b&aacute;nh, mua lời n&oacute;i đ&ecirc;́n 35 điểm thẻ đ&aacute;nh bạc, coi như trả gi&aacute;, 30 điểm thẻ đ&aacute;nh bạc cũng l&agrave; kh&ocirc;ng thiếu được. Cứ như vậy, dự t&iacute;nh của m&igrave;nh liền kh&ocirc;ng đủ,<br />\r\n<br />\r\nSở Quang ch&acirc;n m&agrave;y cau lại, bất qu&aacute; rất nhanh lại gi&atilde;n ra ra.<br />\r\n<br />\r\nMới vừa rồi l&agrave; hắn đem vấn đề cho nghĩ phức tạp.<br />\r\n<br />\r\nKỳ thật kh&ocirc;ng cần thiết để những c&aacute;i kia c&aacute;c người chơi ăn qu&aacute; tốt.<br />\r\n<br />\r\nNếu như đem b&aacute;nh nếp đổi th&agrave;nh chế t&aacute;c b&aacute;nh nếp nguy&ecirc;n vật liệu &mdash;&mdash; cũng ch&iacute;nh l&agrave; phụ cận n&ocirc;ng trường sản xuất thanh mạch, th&igrave; chỉ cần 3 điểm thẻ đ&aacute;nh bạc liền c&oacute; thể mua được trọn vẹn một kg!<br />\r\n<br />\r\nĐộn c&aacute;i 5 kg, đủ một m&igrave;nh hắn ăn hai tuần lễ!<br />\r\n<br />\r\nVề phần c&aacute;i kh&aacute;c, đến l&uacute;c đ&oacute; lại nghĩ biện ph&aacute;p.<br />\r\n<br />\r\n&quot;Tạm thời trước hết chuẩn bị những vật n&agrave;y tốt. . .&quot;<br />\r\n<br />\r\nĐem ph&acirc;n phối xong thẻ đ&aacute;nh bạc quét vào ba l&ocirc;, Sở Quang đem ba l&ocirc; một lần nữa v&aacute;c tại tr&ecirc;n th&acirc;n.<br />\r\n<br />\r\nMặc d&ugrave; một đ&ecirc;m kh&ocirc;ng ngủ, nhưng hắn hiện tại tinh thần phấn khởi kh&ocirc;ng được, phảng phất một lần nữa t&igrave;m được cuộc sống &yacute; nghĩa, căn bản một ch&uacute;t bối rối đều kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nKhi hắn đẩy cửa ra đi v&agrave;o b&ecirc;n ngo&agrave;i, gặp nh&agrave; h&agrave;ng x&oacute;m tiểu c&ocirc; nương ch&iacute;nh nấp tại s&aacute;t v&aacute;ch t&uacute;p lều cổng, t&ograve; m&ograve; nh&igrave;n ph&iacute;a b&ecirc;n m&igrave;nh.<br />\r\n<br />\r\nSở Quang nhận ra t&ecirc;n của n&agrave;ng, gọi Dư Tiểu Ngư, l&agrave; Dư nh&agrave; tiểu nữ nhi.<br />\r\n<br />\r\n<br />\r\nĐ&acirc;́t ch&ecirc;́t tr&ecirc;n người sống s&oacute;t phần lớn xanh xao v&agrave;ng vọt, c&oacute; hơn nh&agrave; tiểu nữ nhi tự nhi&ecirc;n cũng kh&ocirc;ng ngoại lệ, kia l&ocirc; củi tuyệt đồng dạng c&aacute;nh tay nhỏ bắp ch&acirc;n, để người kh&oacute; c&oacute; thể tưởng tượng n&agrave;ng đ&atilde; l&agrave; phải lập gia đ&igrave;nh tuổi t&aacute;c.<br />\r\n<br />\r\nGặp Sở Quang ch&uacute; &yacute; tới m&igrave;nh, n&agrave;ng cũng kh&ocirc;ng thẹn th&ugrave;ng, từ ph&ograve;ng b&ecirc;n trong đi ra.<br />\r\n<br />\r\n&quot;Ta nghe được trong nh&agrave; ngươi c&oacute; động tĩnh, liền đến nh&igrave;n xem.&quot;<br />\r\n<br />\r\nMỗi ng&agrave;y hừng đ&ocirc;ng, Bethe đường phố c&aacute;c nam nh&acirc;n đều sẽ ra ngo&agrave;i nhặt ve chai hoặc l&agrave; đi săn, người gi&agrave; trẻ em th&igrave; đồng dạng để ở nh&agrave; giữ nh&agrave;, hoặc l&agrave; l&agrave;m một &iacute;t c&oacute; thể kiếm tiền việc.<br />\r\n<br />\r\nMặc d&ugrave; mọi người đều l&agrave; một ngh&egrave;o hai trắng, ăn bữa trước kh&ocirc;ng c&oacute; bữa sau, tồn kh&ocirc;ng dưới tiền, trong nh&agrave; cũng kh&ocirc;ng c&oacute; g&igrave; có th&ecirc;̉ trộm, nhưng người n&agrave;o cũng kh&ocirc;ng muốn m&igrave;nh l&uacute;c ra cửa, c&oacute; người đến nh&agrave; m&igrave;nh b&ecirc;n trong chiếm món lời nhỏ.<br />\r\n<br />\r\nC&aacute;i n&agrave;y gọi Sở Quang nam nh&acirc;n, l&agrave; từ b&ecirc;n ngo&agrave;i tới, b&igrave;nh thường đi sớm về trễ, rất &iacute;t c&ugrave;ng khu quần cư h&agrave;ng x&oacute;m l&aacute;ng giềng giao lưu.<br />\r\n<br />\r\nN&agrave;ng đối với hắn hiểu r&otilde; cũng kh&ocirc;ng phải rất nhiều, chỉ cảm thấy hắn d&aacute;ng dấp kh&ocirc;ng qu&aacute; giống l&agrave; c&oacute; thể chịu được cực khổ d&aacute;ng vẻ.<br />\r\n<br />\r\nNgay từ đầu tất cả mọi người cực kỳ đề ph&ograve;ng hắn, mụ mụ cũng l&agrave;m cho n&agrave;ng nhiều nh&igrave;n chằm chằm điểm c&aacute;i n&agrave;y từ b&ecirc;n ngo&agrave;i tới tha hương người.<br />\r\n<br />\r\nBất qu&aacute;, c&aacute; con cảm thấy hắn kh&ocirc;ng giống người xấu, bởi v&igrave; c&oacute; lần hắn tại cửa ra v&agrave;o nấu canh, điểm n&agrave;ng một b&aacute;t.<br />\r\n<br />\r\n&quot;Tạ ơn.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng kh&aacute;ch kh&iacute;, ngươi ra ngo&agrave;i thời điểm, ta c&oacute; thể gi&uacute;p ngươi nh&igrave;n nh&agrave;.&quot; N&agrave;ng hơi chớp mắt, vui sướng bồi th&ecirc;m một c&acirc;u, &quot;D&ugrave; sao cũng l&agrave; nh&agrave;n rỗi.&quot;<br />\r\n<br />\r\nH&agrave;i tử đ&aacute;ng thương.<br />\r\n<br />\r\nNếu như l&agrave; tại thế giới kia, n&agrave;ng hiện tại tuổi t&aacute;c hẳn l&agrave; c&ograve;n ở đọc s&aacute;ch đi.<br />\r\n<br />\r\nKh&ocirc;ng muốn khiến người ta cảm thấy mạo phạm, Sở Quang giấu ở trong mắt một ch&uacute;t thương hại, mặt kh&ocirc;ng thay đổi từ trong t&uacute;i m&oacute;c ra một c&acirc;y kẹo que, nh&eacute;t v&agrave;o tiểu c&ocirc; nương tr&ecirc;n tay.<br />\r\n<br />\r\n&quot;Cầm ăn đi.&quot;<br />\r\n<br />\r\n&quot;Đừng n&oacute;i cho người kh&aacute;c biết ta cho.&quot;<br />\r\n<br />\r\n&quot;Nếu kh&ocirc;ng về sau liền kh&ocirc;ng c&oacute;.&quot;<br />\r\n<br />\r\nCho tới b&acirc;y giờ chưa thấy qua loại thức ăn n&agrave;y.<br />\r\n<br />\r\nDư Tiểu Ngư đầu ti&ecirc;n l&agrave; cắn một c&aacute;i giấy đ&oacute;ng g&oacute;i, ph&aacute;t hiện cắn kh&ocirc;ng ra, l&uacute;c n&agrave;y mới &yacute; thức được ph&iacute;a ngo&agrave;i đ&oacute;ng g&oacute;i l&agrave; muốn x&eacute; mở.<br />\r\n<br />\r\n&Ocirc; trượt mắt to nh&igrave;n chằm chằm c&acirc;y gậy nhựa tr&ecirc;n cắm quả hồng cầu, n&agrave;ng cẩn thận từng li từng t&iacute; l&egrave; lưỡi, nhẹ nh&agrave;ng liếm lấy một ngụm.<br />\r\n<br />\r\nĐ&acirc;y, đ&acirc;y l&agrave; m&ugrave;i vị g&igrave;? !<br />\r\n<br />\r\nRất ngọt!<br />\r\n<br />\r\nTrong mắt l&oacute;e ra ng&ocirc;i sao nhỏ, n&agrave;ng hạnh ph&uacute;c ngẩng đầu, muốn n&oacute;i tiếng c&aacute;m ơn.<br />\r\n<br />\r\nNhưng m&agrave; cho n&agrave;ng đường ăn người kia, cũng đ&atilde; đi xa.</p>', '2021-11-27 21:15:54', 8, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(174, 47, 'Đệ Tứ Thiên Tai', '5', '<p>Ngưu M&atilde; quản l&yacute; thảo luận tổ.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; một c&aacute;i chỉ c&oacute; bốn người nhỏ bầy.<br />\r\n<br />\r\n<br />\r\nChủ nhóm thời gian qua nhanh, bầy vi&ecirc;n l&agrave; s&aacute;t v&aacute;ch bầy ba c&aacute;i tr&acirc;u ngựa &mdash;&mdash; n&oacute;i c&aacute;ch kh&aacute;c liền l&agrave; ch&oacute; quản l&yacute;: Thiếu nữ c&ocirc;ng địch đ&ecirc;m mười lần, cấp t&aacute;m lớn cuồng phong, c&ograve;n nhiều thời gian.<br />\r\n<br />\r\nĐ&ecirc;m mười: 【 c&aacute;c ngươi đều thu được mũ gi&aacute;p rồi? ? ? 】<br />\r\n<br />\r\nL&atilde;o Bạch: 【 nhận được... Rất t&agrave; dị c&aacute;i đồ chơi n&agrave;y, trực tiếp n&eacute;m tới cửa nh&agrave; nha. 】<br />\r\n<br />\r\nC&ograve;n d&agrave;i: 【 ta l&agrave; tại cửa nh&agrave; để xe miệng ph&aacute;t hiện... 】<br />\r\n<br />\r\nCuồng phong: 【 ta l&agrave; ở tr&ecirc;n b&agrave;n l&agrave;m việc... Ta c&ograve;n tưởng rằng l&agrave; c&aacute;i n&agrave;o đồng sự. 】<br />\r\n<br />\r\nDạ thập: 【 c&aacute;c ngươi đều kh&ocirc;ng ta t&agrave; dị, c&aacute;i n&agrave;y bao khỏa trực tiếp đưa đến ta ph&ograve;ng ngủ! Ta ba bạn c&ugrave;ng ph&ograve;ng đều tại, kh&ocirc;ng một c&aacute;i tr&ocirc;ng thấy c&aacute;i đồ chơi n&agrave;y l&agrave; thế n&agrave;o đưa v&agrave;o! M&agrave; lại kỳ lạ nhất hồ kh&ocirc;ng biết c&aacute;c ngươi ph&aacute;t hiện kh&ocirc;ng, đội n&oacute;n an to&agrave;n l&ecirc;n c&oacute; thể nh&igrave;n thấy một c&aacute;i đếm ngược, nhưng những người kh&aacute;c c&aacute;i g&igrave; cũng kh&ocirc;ng nh&igrave;n thấy. 】<br />\r\n<br />\r\nBầy b&ecirc;n trong an tĩnh quỷ dị một hồi.<br />\r\n<br />\r\nTrước hết nhất đ&aacute;nh vỡ trầm mặc, l&agrave; chủ nhóm thời gian qua nhanh.<br />\r\n<br />\r\nL&atilde;o Bạch: [... Ngươi kiểu n&oacute;i n&agrave;y, quả thật c&oacute; ch&uacute;t m&agrave; t&agrave; dị. 】<br />\r\n<br />\r\nPhương Trường: 【 c&aacute;c ngươi n&oacute;i cho c&ugrave;ng l&agrave; c&aacute;i g&igrave; c&ocirc;ng ty game như thế thần th&ocirc;ng quảng đại, chẳng những biết ta địa chỉ, c&ograve;n c&oacute; thể thần kh&ocirc;ng biết quỷ kh&ocirc;ng hay đem bao khỏa đưa đến ta tr&ecirc;n tay? 】<br />\r\n<br />\r\nCuồng phong: 【 &acirc;n, đ&acirc;y cũng l&agrave; nhất l&agrave;m cho ta để &yacute;. Địa chỉ c&ograve;n dễ n&oacute;i, d&ugrave;ng đi&ecirc;̉m kích kết nối địa chỉ IP, ph&acirc;n t&iacute;ch ra ch&uacute;ng ta tại trong hiện thực vị tr&iacute;, tr&ecirc;n l&yacute; luận cũng kh&ocirc;ng phải kh&ocirc;ng được. Nhưng hậu cần... Ta nghĩ m&atilde;i m&agrave; kh&ocirc;ng r&otilde;. Ta tại Kim Lăng, Dạ Thập ta nhớ được l&agrave; tại Giang Th&agrave;nh đọc s&aacute;ch, trong l&uacute;c n&agrave;y k&eacute;m năm trăm c&acirc;y số, nhưng ch&uacute;ng ta cơ hồ l&agrave; đồng thời ph&aacute;t hiện nhận được mũ gi&aacute;p. 】<br />\r\n<br />\r\n<br />\r\nDạ Thập: 【 l&agrave;. . . M&agrave; lại ngay tại đi&ecirc;̉m kích kết nối kh&ocirc;ng l&acirc;u về sau. 】<br />\r\n<br />\r\nL&atilde;o Bạch: 【 thảo, muốn b&aacute;o cảnh sao? 】<br />\r\n<br />\r\nPhương Trường: 【 kh&ocirc;ng c&oacute; t&agrave;i sản tổn thất, cảnh s&aacute;t đo&aacute;n chừng cũng rất kh&oacute; xử l&yacute;, m&agrave; lại vấn đề l&agrave; ngươi c&ugrave;ng cảnh s&aacute;t n&oacute;i g&igrave; thế? Chuyển ph&aacute;t nhanh đưa sai rồi? Mũ gi&aacute;p c&oacute; vấn đề? Ngoại trừ bản nh&acirc;n sử dụng, những người kh&aacute;c đeo l&ecirc;n c&aacute;i g&igrave; cũng nh&igrave;n kh&ocirc;ng thấy. 】<br />\r\n<br />\r\nCuồng phong: 【 &acirc;n, đ&acirc;y l&agrave; vấn đề lớn nhất, rất kh&oacute; c&ugrave;ng những người kh&aacute;c n&oacute;i r&otilde; thứ n&agrave;y đến c&ugrave;ng l&agrave; c&aacute;i g&igrave;. Ta trước đ&oacute; c&ograve;n thử dưới, đội n&oacute;n an to&agrave;n l&ecirc;n đồng thời đưa điện thoại di động cũng nh&eacute;t đi v&agrave;o, nhưng ta r&otilde; r&agrave;ng c&oacute; thể nh&igrave;n thấy đồ vật, điện thoại lại c&aacute;i g&igrave; cũng đập kh&ocirc;ng đến. 】<br />\r\n<br />\r\nCuồng phong trong hiện thực l&agrave; l&atilde;o sư, dạy đại kh&aacute;i l&agrave; khoa học tự nhi&ecirc;n, n&oacute;i chuyện tương đối c&oacute; trật tự, lời n&agrave;y cũng đ&atilde; nhận được c&ograve;n d&agrave;i c&ugrave;ng l&atilde;o Bạch nhất tr&iacute; t&aacute;n đồng.<br />\r\n<br />\r\nDạ Thập: 【 phải kh&ocirc;ng. . . Ch&uacute;ng ta đem đầu n&oacute;n trụ ph&aacute; hủy nh&igrave;n xem? 】<br />\r\n<br />\r\nCuồng phong: 【 l&agrave;m như vậy kh&ocirc;ng phải kh&ocirc;ng được, nhưng c&oacute; phong hiểm. Ch&iacute; &iacute;t ta kh&ocirc;ng c&oacute; t&igrave;m được c&oacute; thể th&aacute;o dỡ địa phương, nếu như bạo lực th&aacute;o dỡ, rất kh&oacute; cam đoan c&ograve;n c&oacute; thể đưa mũ gi&aacute;p lắp trở lại. 】<br />\r\n<br />\r\nPhương Trường: 【 x&aacute;c thực, ta cũng kh&ocirc;ng thể hố a Quang. 】</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nC&oacute; Thể Thần Th&ocirc;ng Quảng Đại đem đầu n&agrave;y n&oacute;n trụ đặt ở nh&agrave; hắn nh&agrave; để xe cổng, hắn đ&atilde; c&oacute; ch&uacute;t tin tưởng c&aacute;i kia ID gọi Quang l&atilde;o ca n&oacute;i lời.<br />\r\n<br />\r\n<br />\r\nN&oacute;i kh&ocirc;ng ch&iacute;nh x&aacute;c n&agrave;y nh&agrave; c&ocirc;ng ty l&agrave; c&aacute;i n&agrave;o đ&oacute; nhận cơ quan nh&agrave; nước ủng hộ thần b&iacute; nghi&ecirc;n cứu cơ cấu?<br />\r\n<br />\r\nMặc kệ l&agrave; c&aacute;i g&igrave;, t&oacute;m lại xem ra đến b&acirc;y giờ, bọn hắn cũng kh&ocirc;ng c&oacute; bị bất luận c&aacute;i g&igrave; tổn thất. M&agrave; lại bản th&acirc;n hắn đối với ho&agrave;n to&agrave;n đắm ch&igrave;m thức VR tr&ograve; chơi, cũng thật cảm thấy hứng th&uacute;.<br />\r\n<br />\r\nC&aacute;i kia đếm ngược, th&agrave;nh c&ocirc;ng khơi gợi l&ecirc;n hứng th&uacute; của hắn.<br />\r\n<br />\r\nCuồng phong: 【 ta tại chim c&aacute;nh cụt tr&ecirc;n n&oacute;i chuyện ri&ecirc;ng Quang ca, nhưng người c&ograve;n kh&ocirc;ng về ta tin tức. C&aacute;i kia Offical Website địa chỉ Internet kh&ocirc;ng giống như l&agrave; cảnh nội trang web, thậm ch&iacute; c&oacute; ch&uacute;t kh&ocirc;ng qu&aacute; giống l&agrave; b&igrave;nh thường trang web vực t&ecirc;n, tra kh&ocirc;ng được n&oacute; đăng k&iacute; tin tức. 】<br />\r\n<br />\r\nDạ Thập: 【 &Aacute;m V&otilde;ng? ! 】<br />\r\n<br />\r\nCuồng phong: 【 kh&ocirc;ng r&otilde; r&agrave;ng. 】<br />\r\n<br />\r\nL&atilde;o Bạch: 【 chuyện n&agrave;y trước hết như vậy đi, thảo luận cũng kh&ocirc;ng c&oacute; g&igrave; &yacute; nghĩa. T&oacute;m lại đợi đến ba ng&agrave;y sau, ta liền c&oacute; thể x&aacute;c nhận đầu n&agrave;y n&oacute;n trụ đến c&ugrave;ng l&agrave; c&aacute;i c&aacute;i g&igrave; t&igrave;nh huống! 】<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nLiền ở c&aacute;c người chơi thảo luận thời điểm, th&acirc;n ở đ&acirc;́t ch&ecirc;́t Sở Quang vẫn c&ograve;n bận rộn l&agrave;m lấy chuẩn bị cuối c&ugrave;ng.<br />\r\n<br />\r\n5 k&iacute; l&ocirc; thanh mạch c&ugrave;ng một ch&uacute;t bằng sắt c&ocirc;ng cụ.<br />\r\n<br />\r\nSở Quang ph&iacute; hết một &iacute;t c&ocirc;ng phu mới đưa những vật n&agrave;y, tại kh&ocirc;ng để cho người ch&uacute; &yacute; t&igrave;nh huống dưới từ Bethe đường phố đem đến ngo&agrave;i ba c&acirc;y số Thấp Địa c&ocirc;ng vi&ecirc;n.<br />\r\n<br />\r\nSố 404 chỗ tr&aacute;nh nạn liền giấu ở to&agrave; n&agrave;y c&ocirc;ng vi&ecirc;n dưới mặt đất, cửa v&agrave;o tại một t&ograve;a bề ngo&agrave;i xấu x&iacute; trong viện dưỡng l&atilde;o.<br />\r\n<br />\r\nNơi n&agrave;y thảm thực vật phong ph&uacute;, hơn nữa c&ograve;n dựa v&agrave;o hồ, tr&ecirc;n l&yacute; luận hẳn l&agrave; c&oacute; kh&ocirc;ng &iacute;t động vật hoang d&atilde; tại phiến khu vực n&agrave;y hoạt động.<br />\r\n<br />\r\nTới gần nơi n&agrave;y về sau Sở Quang liền v&ocirc; c&ugrave;ng cẩn thận, đề ph&ograve;ng chung quanh khả năng tồn tại nguy hiểm.<br />\r\n<br />\r\nBất qu&aacute; may m&agrave; ch&iacute;nh l&agrave;, ngo&agrave;i &yacute; muốn cũng kh&ocirc;ng c&oacute; ph&aacute;t sinh.<br />\r\n<br />\r\nKiểm tra tại trại an dưỡng cổng l&agrave;m ti&ecirc;u k&yacute;, x&aacute;c nhận kh&ocirc;ng c&oacute; dị chủng hoặc l&agrave; kẻ cướp đoạt thừa dịp hắn kh&ocirc;ng c&oacute; ở đ&acirc;y thời điểm tiến v&agrave;o đi, Sở Quang vươn tay b&ecirc;n trong mang nhọn m&agrave; ống nước đẩy ra cửa, cẩn thận đi v&agrave;o.<br />\r\n<br />\r\nSố 404 chỗ tr&aacute;nh nạn lối v&agrave;o tại trại an dưỡng đại sảnh chỗ tốt nhất bộ kia thang m&aacute;y, cũng chỉ c&oacute; bộ kia thang m&aacute;y mặt đồng hồ tr&ecirc;n viết B1 tầng đ&aacute;nh dấu.<br />\r\n<br />\r\nSở Quang đầu ti&ecirc;n l&agrave; đi xuống một chuyến, đem lương thực n&eacute;m v&agrave;o thang m&aacute;y c&aacute;i bệ c&ugrave;ng giảm x&oacute;c b&ecirc;n ngo&agrave;i b&ecirc;n cạnh kh&iacute; miệng cống ở giữa h&agrave;nh lang.<br />\r\n<br />\r\nNơi n&agrave;y l&agrave; một mảnh đất trống, khoảng c&aacute;ch mặt đất đại kh&aacute;i bốn năm mươi m&eacute;t d&aacute;ng vẻ.<br />\r\n<br />\r\nN&oacute;i thực ra nơi n&agrave;y kh&ocirc;ng phải cực kỳ th&iacute;ch hợp cất giữ lương thực, nhưng để ở chỗ n&agrave;y d&ugrave; sao cũng tốt hơn đặt ở trại an dưỡng, bị đi ngang qua chuột hoặc l&agrave; biến dị con gi&aacute;n để mắt tới.<br />\r\n<br />\r\nXuy&ecirc;n qua giảm x&oacute;c ph&ograve;ng hai đạo kh&iacute; miệng cống.<br />\r\n<br />\r\nĐi v&agrave;o đại sảnh Sở Quang, nh&igrave;n xem ngồi xổm ở g&oacute;c tường Tiểu Thất n&oacute;i.<br />\r\n<br />\r\n&quot;Offical Website diễn đ&agrave;n c&ocirc;ng năng khai ph&aacute;t ra sao?&quot;<br />\r\n<br />\r\n&quot;Đ&atilde; l&agrave;m xong.&quot;<br />\r\n<br />\r\n&quot;C&aacute;c người chơi đ&acirc;u? Mũ gi&aacute;p của bọn họ nhận được sao?&quot;<br />\r\n<br />\r\n&quot;Đ&atilde; nhận được ờ.&quot;<br />\r\n<br />\r\nNhanh như vậy?<br />\r\n<br />\r\nSở Quang hơi kinh ngạc.<br />\r\n<br />\r\nHắn nhớ kỹ m&igrave;nh l&agrave; buổi s&aacute;ng s&aacute;u điểm rời đi chỗ tr&aacute;nh nạn, hiện tại cũng mới khoảng một giờ chiều, ở giữa chỉ c&aacute;ch xa bảy giờ.<br />\r\n<br />\r\n&quot;Đ&acirc;́t ch&ecirc;́t b&ecirc;n n&agrave;y thời gian c&ugrave;ng x&atilde; hội hiện đại thời gian thật l&agrave; 1: 1 lưu động sao?&quot;<br />\r\n<br />\r\n&quot;Đương nhi&ecirc;n l&agrave;, bất qu&aacute; b&ecirc;n n&agrave;y thời gian tương đương với tr&ecirc;n Địa Cầu t&acirc;y bốn m&ugrave;a khu.&quot;<br />\r\n<br />\r\nHiện thế b&ecirc;n kia Trung Quốc l&agrave; đ&ocirc;ng t&aacute;m m&uacute;i giờ, c&ugrave;ng t&acirc;y bốn m&ugrave;a khu ch&ecirc;nh lệch đại kh&aacute;i l&agrave; mười hai giờ, n&oacute;i c&aacute;ch kh&aacute;c Trung Quốc đ&ocirc;ng bộ địa khu hiện tại ch&iacute;nh l&agrave; trời vừa rạng s&aacute;ng.<br />\r\n<br />\r\nSở Quang ngồi ở trước m&aacute;y vi t&iacute;nh, đăng lục Offical Website hậu trường.<br />\r\n<br />\r\nTiểu Thất dựa theo ph&acirc;n ph&oacute; của hắn, l&agrave;m quan lưới tăng th&ecirc;m diễn đ&agrave;n c&ocirc;ng năng, chẳng qua trước mắt kh&ocirc;ng c&oacute; lưu lượng, tự nhi&ecirc;n l&agrave; một c&aacute;i thiệp đều kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nCơ sở dữ liệu c&ugrave;ng đồ gi&aacute;m cũng giống như vậy.<br />\r\n<br />\r\n&quot;Ta l&agrave;m như thế n&agrave;o đem b&ecirc;n n&agrave;y tr&ecirc;n tấm ảnh truyền đến nơi n&agrave;y?&quot; Sở Quang chỉ v&agrave;o trong m&agrave;n h&igrave;nh đồ kho n&oacute;i.<br />\r\n<br />\r\n&quot;Trực tiếp upload l&agrave; được rồi ờ.&quot;<br />\r\n<br />\r\n&quot;. . . Ta &yacute; tứ ch&iacute;nh l&agrave;, ta l&agrave;m như thế n&agrave;o chụp ảnh. Hoặc là nói ngươi lại m&aacute;y chụp ảnh loại h&igrave;nh đồ vật sao?&quot;<br />\r\n<br />\r\nNhư thế to con chỗ tr&aacute;nh nạn, to&agrave;n bộ B1 tầng l&ocirc;ng đều kh&ocirc;ng c&oacute; liền cực kỳ kh&ocirc;ng hợp th&oacute;i thường.<br />\r\n<br />\r\nKh&ocirc;ng n&oacute;i lưu một &iacute;t vũ kh&iacute; cho m&igrave;nh, cơ bản cầu sinh c&ocirc;ng cụ d&ugrave; sao cũng phải chuẩn bị một ch&uacute;t a?<br />\r\n<br />\r\n&quot;&Aacute;ch, đại kh&aacute;i kh&ocirc;ng c&oacute; m&aacute;y ảnh, bất qu&aacute; ta camera c&oacute; thể gi&uacute;p ng&agrave;i chụp ảnh. . . Ngươi nghĩ đập loại n&agrave;o? Ngay ở chỗ n&agrave;y sao?&quot;<br />\r\n<br />\r\nNh&igrave;n xem g&oacute;c tường giấy lộn c&aacute;i sọt đồng dạng người m&aacute;y, Sở Quang thở d&agrave;i n&oacute;i.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Ở chỗ n&agrave;y đập c&oacute; c&aacute;i quỷ d&ugrave;ng. . . Được rồi, ch&iacute;nh ta nghĩ một ch&uacute;t biện ph&aacute;p.&quot;<br />\r\n<br />\r\nCũng kh&ocirc;ng thể đem dị chủng bắt được nơi n&agrave;y.<br />\r\n<br />\r\nBất qu&aacute;, đem Tiểu Thất l&agrave;m đi ra b&ecirc;n ngo&agrave;i ngược lại l&agrave; c&oacute; thể c&acirc;n nhắc.<br />\r\n<br />\r\nĐại đa số dị chủng đ&uacute;ng kh&ocirc;ng c&oacute; thể ăn cục sắt đều kh&ocirc;ng ra thế n&agrave;o cảm thấy hứng th&uacute;, hơi ngụy trang một ch&uacute;t l&agrave;m mắt cắm ở m&aacute;i nh&agrave;, quả thực l&agrave; giữ nh&agrave; Thần kh&iacute; a!<br />\r\n<br />\r\nSở Quang c&agrave;ng nghĩ c&agrave;ng thấy phải dựa v&agrave;o phổ.<br />\r\n<br />\r\nTh&ocirc;ng qua cư d&acirc;n đại sảnh m&aacute;y t&iacute;nh leo l&ecirc;n Chim C&aacute;nh Cụt hiệu.<br />\r\n<br />\r\nSở Quang ph&aacute;t hiện bốn c&aacute;i người chơi đều cho m&igrave;nh ph&aacute;t n&oacute;i chuyện ri&ecirc;ng tin tức, hướng hắn nghe ng&oacute;ng hắn nhậm chức đến c&ugrave;ng l&agrave; nh&agrave; n&agrave;o c&ocirc;ng ty, ho&agrave;n to&agrave;n đắm ch&igrave;m thức tr&ograve; chơi rốt cuộc l&agrave; &yacute; g&igrave;, c&ugrave;ng mũ tr&ograve; chơi đến tột c&ugrave;ng l&agrave; chuyện g&igrave; xảy ra.<br />\r\n<br />\r\nN&oacute;i thực ra, Sở Quang cũng kh&ocirc;ng biết trả lời như thế n&agrave;o bọn hắn những vấn đề n&agrave;y, cũng kh&ocirc;ng c&oacute; khả năng đem thế giới n&agrave;y t&igrave;nh huống thật giảng cho bọn hắn.<br />\r\n<br />\r\nChỉ c&oacute; để bọn hắn tin tưởng nơi n&agrave;y l&agrave; thế giới tr&ograve; chơi.<br />\r\n<br />\r\nBọn hắn mới c&oacute; thể lấy &quot;Đệ Tứ Thi&ecirc;n Tai&quot; th&acirc;n phận gi&aacute;ng l&acirc;m, kh&ocirc;ng sợ h&atilde;i lại chịu mệt nhọc, trở th&agrave;nh hắn mạnh nhất c&ocirc;ng cụ.<br />\r\n<br />\r\n&quot;Chủ nh&acirc;n.&quot;<br />\r\n<br />\r\n&quot;Thế n&agrave;o.&quot;<br />\r\n<br />\r\nNh&igrave;n xem đ&oacute;ng lại m&aacute;y t&iacute;nh, từ trước ghế đứng người l&ecirc;n Sở Quang, ngồi xổm ở g&oacute;c tường Tiểu Thất hiếu k&igrave; hỏi.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng cần hồi phục tin tức của bọn hắn sao?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&acirc;̀n phải v&acirc;̣y.&quot;<br />\r\n<br />\r\nGiải th&iacute;ch c&agrave;ng nhiều sơ hở c&agrave;ng nhiều, kh&ocirc;ng bằng một c&acirc;u x&iacute; nghiệp cơ mật, quốc gia cơ mật hồ lộng qua.<br />\r\n<br />\r\nVề phần c&aacute;i kh&aacute;c?<br />\r\n<br />\r\nĐể ch&iacute;nh bọn hắn đo&aacute;n đi tốt.<br />\r\n<br />\r\nPhải kh&ocirc;ng đầu d&agrave;i đến l&agrave; d&ugrave;ng để l&agrave;m g&igrave;?<br />\r\n<br />\r\nKh&ocirc;ng phải liền l&agrave; d&ugrave;ng để n&atilde;o bổ sao.</p>', '2021-11-27 21:16:24', 8, 1),
(175, 48, 'Hấp Huyết Quỷ Chân Tổ!', '1', '<p>Hải Vi&ecirc;n lịch năm 641.<br />\r\n<br />\r\nGrand Line.<br />\r\n<br />\r\nLucifer đảo.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; một c&aacute;i l&acirc;u d&agrave;i kh&ocirc;ng th&acirc;́y ánh mặt trời h&ograve;n đảo.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, nơi n&agrave;y đang tại triển khai một hồi chưa từng c&oacute; chiến tranh.<br />\r\n<br />\r\nV&ocirc; số chiến hạm tại đảo b&ecirc;n ngo&agrave;i tập kết, hỏa lực oanh minh.<br />\r\n<br />\r\nMấy ng&agrave;n thước cao cự h&igrave;nh Hải Vương, phảng phất từng t&ograve;a giống như n&uacute;i cao kh&ocirc;ng ngừng đụng chạm lấy h&ograve;n đảo.<br />\r\n<br />\r\nM&agrave; tr&ecirc;n đảo, c&agrave;ng l&agrave; sớm đ&atilde; h&oacute;a th&agrave;nh một mảnh huyết sắc Tu La chiến trường.<br />\r\n<br />\r\nHai triệu t&ecirc;n đến từ vĩ đại vương triều c&ugrave;ng hai mươi c&aacute;i vương quốc li&ecirc;n hợp qu&acirc;n đội, trang bị tinh xảo binh sĩ bị tập kết tại đảo b&ecirc;n ngo&agrave;i, như l&agrave; kiến triều tr&agrave;n v&agrave;o tr&ecirc;n đảo.<br />\r\n<br />\r\nTr&ecirc;n đảo khắp nơi đều l&agrave; thi thể, những thi thể n&agrave;y đều đến từ nh&acirc;n loại, ngư nh&acirc;n thậm ch&iacute; cự nh&acirc;n từng c&aacute;i chủng tộc.<br />\r\n<br />\r\nM&aacute;u tươi chảy ngang, phủ k&iacute;n đại địa.<br />\r\n<br />\r\nQu&acirc;n Li&ecirc;n Hiệp kh&ocirc;ng ngừng x&acirc;m nhập ở v&agrave;o h&ograve;n đảo trung t&acirc;m to lớn t&ograve;a th&agrave;nh.<br />\r\n<br />\r\nT&ograve;a th&agrave;nh b&ecirc;n ngo&agrave;i tr&ecirc;n tường r&agrave;o, tr&ecirc;n v&aacute;ch tường từng c&aacute;i đi&ecirc;u khắc c&ugrave;ng tượng đ&aacute; kh&ocirc;ng ngừng h&oacute;a th&agrave;nh kh&ocirc;ng biết sinh chết tử vong binh sĩ.<br />\r\n<br />\r\nMọc ra răng nanh sắc mặt trắng bệch Hấp Huyết Quỷ nh&oacute;m theo s&aacute;t ph&iacute;a sau, đồng dạng như l&agrave; m&atilde;nh th&uacute; c&ugrave;ng những c&aacute;i kia qu&acirc;n Li&ecirc;n Hiệp đối kh&aacute;ng.<br />\r\n<br />\r\nMặc d&ugrave; t&ograve;a th&agrave;nh b&ecirc;n trong chiến sĩ đối mặt hơn mười lần tại phe m&igrave;nh số lượng địch nh&acirc;n, lại kh&ocirc;ng ch&uacute;t n&agrave;o l&ugrave;i bước, từng c&aacute;i mang theo chiến &yacute; đi&ecirc;n cuồng, phảng phất người b&ecirc;n trong th&agrave;nh đều đ&atilde; đem tử vong kh&ocirc;ng để &yacute;.<br />\r\n<br />\r\nBất qu&aacute; song phương số lượng ch&ecirc;nh lệch thật sự l&agrave; c&aacute;ch xa, nội th&agrave;nh chiến sĩ bị đ&aacute;nh li&ecirc;n tục bại lui.<br />\r\n<br />\r\nBỗng nhi&ecirc;n.<br />\r\n<br />\r\nMột cỗ huyết sắc từ tr&ecirc;n trời gi&aacute;ng xuống.<br />\r\n<br />\r\nTại to&agrave; n&agrave;y danh xưng Vĩnh Dạ Chi Th&agrave;nh t&ograve;a th&agrave;nh b&ecirc;n tr&ecirc;n Kong.<br />\r\n<br />\r\nTr&ecirc;n bầu trời nổi l&ecirc;n một v&ograve;ng huyết nguyệt, tại huyết nguyệt chiếu rọi xuống.<br />\r\n<br />\r\nNguy&ecirc;n bản li&ecirc;n tục bại lui Hấp Huyết Quỷ nh&oacute;m bỗng nhi&ecirc;n giống như l&agrave; đi&ecirc;n cuồng đồng dạng, nhất cử ngăn trở xu hướng suy t&agrave;n.<br />\r\n<br />\r\nC&agrave;ng quỷ dị ch&iacute;nh l&agrave;, huyết sắc ánh trăng những nơi đi qua.<br />\r\n<br />\r\nNhững c&aacute;i kia chết đi qu&acirc;n Li&ecirc;n Hiệp binh sĩ bỗng nhi&ecirc;n đều lung la lung lay đứng l&ecirc;n, sắc mặt t&aacute;i xanh v&ocirc; thần.<br />\r\n<br />\r\nMột gi&acirc;y sau, bị phục sinh qu&acirc;n Li&ecirc;n Hiệp c&aacute;c binh sĩ kh&ocirc;ng ch&uacute;t do dự đối nhau trước đồng bạn n&acirc;ng l&ecirc;n đồ đao.<br />\r\n<br />\r\nTại chiến trường hậu phương tr&ecirc;n biển, một chiếc thuyền t&agrave;u b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\n&quot;L&agrave; huyết chi n&ocirc; bộc, t&ecirc;n kia hiện th&acirc;n. . .&quot;<br />\r\n<br />\r\nBoong thuyền, một c&aacute;i mang theo Mũ Rơm to lớn b&oacute;ng người giảm thấp xuống v&agrave;nh n&oacute;n, thấp giọng n&oacute;i ra.<br />\r\n<br />\r\n&quot;Joy Boy, t&igrave;nh huống c&oacute; ch&uacute;t kh&ocirc;ng tốt lắm. .&quot;<br />\r\n<br />\r\nThuyền hạm b&ecirc;n cạnh, một c&aacute;i th&acirc;n h&igrave;nh gần trăm m&eacute;t cự h&igrave;nh Mỹ nh&acirc;n ngư c&oacute; ch&uacute;t lo lắng n&oacute;i ra.<br />\r\n<br />\r\nL&uacute;c đầu hai b&ecirc;n đều c&oacute; thương vong.<br />\r\n<br />\r\nNhưng l&agrave; hiện tại huyết nguyệt xuất hiện, những c&aacute;i kia tử vong binh sĩ tất cả đều một lần nữa sẽ một lần nữa đứng l&ecirc;n, qu&acirc;n Li&ecirc;n Hiệp số lượng ưu thế kh&ocirc;ng c&ograve;n tồn tại.<br />\r\n<br />\r\nN&agrave;ng l&agrave; Người C&aacute; c&ocirc;ng ch&uacute;a, cũng l&agrave; c&oacute; thể mệnh lệnh Hải Vương người.<br />\r\n<br />\r\nTại Joy Boy du thuyết dưới, quyết định phối hợp vĩ đại vương triều c&ugrave;ng hai mươi người loại quốc gia c&ugrave;ng một chỗ ti&ecirc;u diệt c&aacute;i kia c&oacute; thể uy hiếp được h&ograve;a b&igrave;nh thế giới tồn tại.<br />\r\n<br />\r\nNếu như c&oacute; thể lấy được thắng lợi, Người C&aacute; đảo b&ecirc;n tr&ecirc;n cư d&acirc;n đem thu hoạch được tại dưới th&aacute;i dương quang minh ch&iacute;nh đại h&agrave;nh tẩu quyền lực.<br />\r\n<br />\r\nBoong thuyền Joy Boy c&oacute; ch&uacute;t ngẩng đầu, lộ ra một v&ograve;ng nụ cười tự tin, đối Người C&aacute; c&ocirc;ng ch&uacute;a bảo đảm n&oacute;i: &quot;Y&ecirc;n t&acirc;m đi, vĩ đại vương triều đ&atilde; l&agrave;m tốt vạn to&agrave;n chuẩn bị.&quot;<br />\r\n<br />\r\n&quot;Xin y&ecirc;n t&acirc;m, chỉ cần c&oacute; thể ti&ecirc;u diệt c&aacute;i n&agrave;y đối thế giới c&oacute; cực lớn uy hiếp qu&aacute;i vật, c&aacute;i kh&aacute;c hai mươi vị Vương hướng ta cam đoan qua, nhất định sẽ to&agrave;n lực x&uacute;c tiến để ngư nh&acirc;n nh&oacute;m c&oacute; thể tr&ecirc;n đất bằng sinh hoạt.&quot;<br />\r\n<br />\r\nVĩ đại vương triều li&ecirc;n hợp mặt kh&aacute;c hai mươi c&aacute;i mạnh nhất tr&ecirc;n thế giới quốc gia, kết cục đ&atilde; l&agrave; tất nhi&ecirc;n.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nTheo Joy Boy vừa dứt lời.<br />\r\n<br />\r\nẦm ầm!<br />\r\n<br />\r\nBọt nước cuồn cuộn, tại một chiếc cự hạm từ dưới mặt biển nổi l&ecirc;n, đường k&iacute;nh hơn trăm m&eacute;t cự ph&aacute;o tại cự hạm ph&iacute;a trước.<br />\r\n<br />\r\n&quot;Minh Vương! Thế m&agrave; đem thứ n&agrave;y đều mang đến.&quot;<br />\r\n<br />\r\nNgười C&aacute; c&ocirc;ng ch&uacute;a nh&igrave;n thấy xa xa cự hạm, trong l&ograve;ng giật m&igrave;nh.<br />\r\n<br />\r\nMinh Vương Chiến Hạm b&ecirc;n tr&ecirc;n cự ph&aacute;o uy lực cực lớn, một ph&aacute;o thậm ch&iacute; c&oacute; thể hủy diệt một c&aacute;i h&ograve;n đảo.<br />\r\n<br />\r\nNhưng l&agrave; bởi v&igrave; h&igrave;nh thể to lớn, với lại c&acirc;̀n t&iacute;ch s&uacute;c năng lượng, đợi kh&ocirc;ng được ph&aacute;t xạ liền sẽ bị địch nh&acirc;n cưỡng &eacute;p hủy đi.<br />\r\n<br />\r\nBất qu&aacute; nghĩ lại, c&oacute; nhiều cường giả như vậy c&ugrave;ng binh sĩ kiềm chế, Minh Vương khuyết điểm vừa vặn được b&ugrave; đắp, với lại d&ugrave;ng để ph&aacute; giải c&aacute;i kia l&acirc;u d&agrave;i kh&ocirc;ng ti&ecirc;u tan m&acirc;y đen l&agrave; tốt nhất g&atilde;y tay.<br />\r\n<br />\r\nĐen nh&aacute;nh lỗ ph&aacute;o nhắm ngay h&ograve;n đảo tr&ecirc;n kh&ocirc;ng m&acirc;y đen.<br />\r\n<br />\r\nM&agrave;u v&agrave;ng &aacute;nh s&aacute;ng tại họng ph&aacute;o b&ecirc;n trong ngưng tụ.<br />\r\n<br />\r\nHọng ph&aacute;o kh&ocirc;ng gian chung quanh giống như đều tại vặn vẹo, phảng phất trong đ&oacute; đang tại ngưng tụ phi ph&agrave;m lực lượng.<br />\r\n<br />\r\nTiếp theo một c&aacute;i chớp mắt.<br />\r\n<br />\r\n&Ocirc;ng ~!<br />\r\n<br />\r\nCột s&aacute;ng từ họng ph&aacute;o b&ecirc;n trong bắn ra.<br />\r\n<br />\r\nMột đạo kim sắc cột s&aacute;ng ph&oacute;ng l&ecirc;n tận trời.<br />\r\n<br />\r\nTrong khoảnh khắc.<br />\r\n<br />\r\nBao phủ tại Vĩnh Dạ Chi Th&agrave;nh tr&ecirc;n kh&ocirc;ng m&acirc;y đen bỗng nhi&ecirc;n bị t&aacute;ch ra.<br />\r\n<br />\r\n&Aacute;nh mặt trời trong nh&aacute;y mắt từ bầu trời vẩy xuống, nguy&ecirc;n bản &acirc;m &aacute;m h&ograve;n đảo bỗng nhi&ecirc;n trở n&ecirc;n trong suốt.<br />\r\n<br />\r\nTại tiếp x&uacute;c đến &aacute;nh nắng một khắc n&agrave;y, huyết nguyệt lập tức tan r&atilde;.<br />\r\n<br />\r\n&Aacute;nh nắng bắn thẳng đến đ&ecirc;́n đại địa b&ecirc;n tr&ecirc;n, những c&aacute;i kia bị quang mang chiếu rọi đến người chết bỗng nhi&ecirc;n thống khổ k&ecirc;u r&ecirc;n.<br />\r\n<br />\r\nChỉ l&agrave; trong nh&aacute;y mắt, những c&aacute;i kia bị &aacute;nh mặt trời chiếu đến huyết chi n&ocirc; bộc liền tất cả đều h&oacute;a th&agrave;nh bụi bặm.<br />\r\n<br />\r\nHuyết nguyệt biến mất, tr&ecirc;n bầu trời, xuất hiện một c&aacute;i th&acirc;n mặc trường b&agrave;o m&agrave;u đỏ ng&ograve;m b&oacute;ng người.<br />\r\n<br />\r\nTất cả mọi người theo bản năng ngẩng đầu, nh&igrave;n xem c&aacute;i kia tuổi trẻ khu&ocirc;n mặt, n&atilde;o hải c&ugrave;ng hiện ra một c&aacute;i t&ecirc;n.<br />\r\n<br />\r\nAkatsuki Kojou.<br />\r\n<br />\r\nHấp Huyết Quỷ Ch&acirc;n Tổ.<br />\r\n<br />\r\nVĩnh Dạ Chi Th&agrave;nh chủ nh&acirc;n.<br />\r\n<br />\r\nTrấn &aacute;p một thời đại qu&aacute;i vật!<br />\r\n<br />\r\nĐồng thời, cũng l&agrave; lần n&agrave;y vĩ đại vương triều li&ecirc;n hợp hai mươi c&aacute;i vương quốc c&ugrave;ng đ&ocirc;ng đảo chủng tộc cộng đồng thảo phạt đối tượng.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, Akatsuki Kojou ch&iacute;nh lạnh l&ugrave;ng nh&igrave;n về ph&iacute;a trước.<br />\r\n<br />\r\nHắn cũng kh&ocirc;ng phải c&aacute;i thế giới n&agrave;y người.<br />\r\n<br />\r\nM&agrave; l&agrave; đến từ thế kỷ hai mươi mốt Địa Cầu.<br />\r\n<br />\r\nXuy&ecirc;n qua đến One Piece thế giới hắn, lại so hắn biết nội dung cốt truyện trước thời hạn hơn t&aacute;m trăm năm.<br />\r\n<br />\r\nTự th&acirc;n mang theo nửa thức tỉnh Hougyoku m&agrave; đến hắn, ngo&agrave;i &yacute; muốn ăn một vi&ecirc;n Tr&aacute;i &Aacute;c Quỷ, trở th&agrave;nh hệ Zoan Batto Batto no Mi hệ Mythical Model Vampire năng lực giả.<br />\r\n<br />\r\nNương tựa theo Hougyoku tăng th&ecirc;m, Tr&aacute;i &Aacute;c Quỷ năng lực bị hắn khai ph&aacute;t đến một c&aacute;i mức kh&ocirc;ng thể tưởng tượng nổi.<br />\r\n<br />\r\nHấp Huyết Quỷ Ch&acirc;n Tổ t&ecirc;n, trấn &aacute;p một thời đại.<br />\r\n<br />\r\nC&agrave;ng l&agrave; đ&atilde; s&aacute;ng tạo ra cả một c&aacute;i để to&agrave;n thế giới cũng v&igrave; đ&oacute; rung động đ&aacute;ng sợ chủng tộc, Huyết tộc!<br />\r\n<br />\r\nNhưng m&agrave;, qu&aacute; cường đại chắc chắn thu nhận diệt vong.<br />\r\n<br />\r\nL&agrave;m tr&ecirc;n đại dương bao la cường thịnh nhất hai mươi người loại quốc gia, li&ecirc;n hợp thần b&iacute; nhất vĩ đại vương triều, cự nh&acirc;n c&ugrave;ng ngư nh&acirc;n c&aacute;c loại đ&ocirc;ng đảo chủng tộc, đối Hấp Huyết Quỷ Ch&acirc;n Tổ triển khai cộng đồng thảo phạt.<br />\r\n<br />\r\nĐối mặt với c&aacute;i thế giới n&agrave;y đỉnh phong lực lượng tập kết.<br />\r\n<br />\r\nNương theo lấy Akatsuki Kojou c&ugrave;ng nhau xuất hiện.<br />\r\n<br />\r\nC&ograve;n c&oacute; một c&aacute;i b&oacute;ng đen to lớn.<br />\r\n<br />\r\nC&aacute;i kia liền như l&agrave; một c&aacute;i to lớn K&igrave;nh Ngư đồng dạng, mặc d&ugrave; kh&ocirc;ng c&aacute;nh, nhưng lại c&oacute; thể lơ lửng giữa kh&ocirc;ng trung.<br />\r\n<br />\r\nChung quanh c&oacute; v&ocirc; số đầu giống như sợi r&acirc;u đồng dạng x&uacute;c tu lung tung vũ động.<br />\r\n<br />\r\nĐ&oacute; l&agrave; Akatsuki Kojou l&agrave;m Hấp Huyết Quỷ Ch&acirc;n Tổ quyến th&uacute;.<br />\r\n<br />\r\nMột c&aacute;i, d&ugrave;ng v&ocirc; số năng lực giả m&aacute;u tươi s&aacute;ng tạo ra qu&aacute;i vật đ&aacute;ng sợ.<br />\r\n<br />\r\nKh&ocirc;ng chỉ c&oacute; h&igrave;nh thể to lớn, với lại c&oacute; thể sử dụng lấy gần như tất cả đ&atilde; biết Tr&aacute;i &Aacute;c Quỷ năng lực!<br />\r\n<br />\r\nAkatsuki Kojou cho n&oacute; đặt t&ecirc;n l&agrave;. . .<br />\r\n<br />\r\nThi&ecirc;n Kh&ocirc;ng chi thần, Uranus!<br />\r\n<br />\r\nĐ&acirc;y cũng l&agrave; Akatsuki Kojou cường đại nhất thủ đoạn.<br />\r\n<br />\r\n&quot;Ch&iacute;nh chủ rốt cục đi ra!&quot;<br />\r\n<br />\r\nNh&igrave;n xem cự th&uacute; xuất hiện, tại tr&ecirc;n chiến thuyền hai mươi vị Vương tất cả đều tinh thần chấn động.<br />\r\n<br />\r\n&quot;Dựa theo kế hoạch h&agrave;nh động.&quot;<br />\r\n<br />\r\nMột đạo nghe kh&ocirc;ng ra nam nữ thanh &acirc;m từ hai mươi vị Vương b&ecirc;n trong vang l&ecirc;n.<br />\r\n<br />\r\nĐ&oacute; l&agrave; một c&aacute;i bao phủ tại &aacute;o kho&aacute;c thon d&agrave;i th&acirc;n h&igrave;nh, thấy kh&ocirc;ng r&otilde; khu&ocirc;n mặt, duy nhất có th&ecirc;̉ thấy r&otilde;, ch&iacute;nh l&agrave; c&aacute;i kia phảng phất có th&ecirc;̉ thấy r&otilde; hết thảy v&ograve;ng trạng đ&ocirc;i mắt.<br />\r\n<br />\r\nMột gi&acirc;y sau.<br />\r\n<br />\r\nMột c&aacute;i to lớn bọt kh&iacute; từ dưới biển nổi l&ecirc;n. , chỉ thấy lại một chiếc Minh Vương cấp tốc xuất hiện ở tr&ecirc;n biển.<br />\r\n<br />\r\nThứ hai chiếc Minh Vương Chiến Hạm xuất hiện, tho&aacute;i vị tại một b&ecirc;n kh&aacute;c tr&ecirc;n chiến hạm, vĩ đại vương triều người giật m&igrave;nh.<br />\r\n<br />\r\n&quot;&Acirc;n? Lại một chiếc Minh Vương ?&quot;<br />\r\n<br />\r\nJoy Boy kinh ngạc l&acirc;̉m b&acirc;̉m n&oacute;i.<br />\r\n<br />\r\nBất qu&aacute;, tiếp theo một c&aacute;i chớp mắt, hắn liền biến sắc.<br />\r\n<br />\r\n&quot;Đ&aacute;ng chết, bọn gia hỏa n&agrave;y l&agrave; muốn. . .&quot;<br />\r\n<br />\r\nLần n&agrave;y Minh Vương họng ph&aacute;o nhắm ngay kh&ocirc;ng c&ograve;n l&agrave; bầu trời, m&agrave; l&agrave; to&agrave;n bộ h&ograve;n đảo.<br />\r\n<br />\r\n&quot;Bọn hắn muốn l&agrave;m c&aacute;i g&igrave;? !&quot;<br />\r\n<br />\r\nNgười C&aacute; c&ocirc;ng ch&uacute;a l&ecirc;n tiếng kinh h&ocirc;.<br />\r\n<br />\r\nN&agrave;ng đ&atilde; đo&aacute;n được thứ hai chiếc Minh Vương mục đ&iacute;ch.<br />\r\n<br />\r\nNhưng l&agrave;, tr&ecirc;n đảo kh&ocirc;ng chỉ c&oacute; lấy cự nh&acirc;n c&ugrave;ng người c&aacute; nhiều c&aacute;i chủng tộc, c&ograve;n c&oacute; rất nhiều nh&acirc;n loại binh sĩ a!<br />\r\n<br />\r\nJoy Boy sắc mặt cũng biến th&agrave;nh kh&oacute; coi, vội v&agrave;ng nh&igrave;n lại, ph&aacute;t hiện c&aacute;i kia chiếc Minh Vương ph&iacute;a tr&ecirc;n cũng kh&ocirc;ng phải l&agrave; vĩ đại vương triều cờ x&iacute;.<br />\r\n<br />\r\n&quot;Mau ngăn cản n&oacute;!&quot;<br />\r\n<br />\r\nNgười C&aacute; c&ocirc;ng ch&uacute;a lập tức hạ lệnh.<br />\r\n<br />\r\nTrong đ&oacute; hai đầu mấy ng&agrave;n thước lớn l&ecirc;n loại cực lớn Hải Vương lập tức từ dưới biển nổi l&ecirc;n, ngăn tại Minh Vương ph&iacute;a trước, muốn đem chi đ&aacute;nh ch&igrave;m.<br />\r\n<br />\r\nNhưng l&agrave;, l&uacute;c n&agrave;y c&aacute;i kia thứ hai chiếc Minh Vương năng lượng đ&atilde; ngưng tụ ho&agrave;n th&agrave;nh.<br />\r\n<br />\r\nOanh ~!<br />\r\n<br />\r\nM&agrave;u v&agrave;ng cột s&aacute;ng oanh ra.<br />\r\n<br />\r\nTrực tiếp xuy&ecirc;n thủng ph&iacute;a trước Hải Vương.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nKh&ocirc;ng chỉ c&oacute; như thế.<br />\r\n<br />\r\nLiền ngay cả qu&acirc;n Li&ecirc;n Hiệp chiến thuyền cũng bị cuốn v&agrave;o trong đ&oacute;, trong nh&aacute;y mắt h&oacute;a th&agrave;nh bột phấn.<br />\r\n<br />\r\n&quot;Minh Vương sao?&quot;<br />\r\n<br />\r\nNơi xa tr&ecirc;n bầu trời Akatsuki Kojou cũng ph&aacute;t hiện mục đ&iacute;ch của đối phương, nhướng m&agrave;y.<br />\r\n<br />\r\nAkatsuki Kojou quanh th&acirc;n tinh lực phun tr&agrave;o, trong nh&aacute;y mắt h&oacute;a th&agrave;nh một c&aacute;i to lớn huyết cầu liền x&ocirc;ng ra ngo&agrave;i.<br />\r\n<br />\r\nTiếp theo một c&aacute;i chớp mắt, liền ngăn tại cột s&aacute;ng ph&iacute;a trước.<br />\r\n<br />\r\n&quot;Dừng lại cho ta!&quot;<br />\r\n<br />\r\nAkatsuki Kojou hừ lạnh một tiếng.<br />\r\n<br />\r\nSau lưng của hắn l&agrave; Akatsuki Kojou một tay s&aacute;ng tạo Huyết tộc, cũng l&agrave; hắn vẫn lấy l&agrave;m ki&ecirc;u ngạo tối cao kiệt t&aacute;c, tuyệt đối kh&ocirc;ng thể lấy mắt nh&igrave;n địch nh&acirc;n đem bọn hắn tuỳ tiện ph&aacute; hủy.<br />\r\n<br />\r\nTrong nh&aacute;y mắt.<br />\r\n<br />\r\nTr&ecirc;n h&ograve;n đảo m&aacute;u tươi phảng phất nhận lấy điều động, cấp tốc hướng Akatsuki Kojou tr&ecirc;n th&acirc;n ngưng tụ.<br />\r\n<br />\r\nMột cỗ cực kỳ nồng đậm huyết dịch bị Akatsuki Kojou th&acirc;n thể chuyển h&oacute;a xuất hiện, giống như một c&aacute;i to lớn huyết sắc b&igrave;nh chướng đồng dạng trong nh&aacute;y mắt ngăn tại cọ rửa m&agrave; đến m&agrave;u v&agrave;ng cột s&aacute;ng trước.<br />\r\n<br />\r\nẦm ầm! !<br />\r\n<br />\r\nMinh Vương h&agrave;o xưng c&oacute; thể hủy diệt hết thảy si&ecirc;u cấp binh kh&iacute;, nhưng m&agrave; l&uacute;c n&agrave;y c&aacute;i n&agrave;y hủy thi&ecirc;n diệt địa một ph&aacute;o thế m&agrave; bị Akatsuki Kojou cản lại.<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave;? !&quot;<br />\r\n<br />\r\nMắt thấy Minh Vương c&ocirc;ng k&iacute;ch bị lập tức, để Joy Boy c&ugrave;ng Người C&aacute; c&ocirc;ng ch&uacute;a nhịn kh&ocirc;ng được h&eacute;t l&ecirc;n kinh ngạc.<br />\r\n<br />\r\n&quot;V&acirc;̣y mà đỡ được Minh Vương c&ocirc;ng k&iacute;ch, kh&ocirc;ng hổ l&agrave; được xưng l&agrave; Hấp Huyết Quỷ Ch&acirc;n Tổ qu&aacute;i vật.&quot;<br />\r\n<br />\r\nJoy Boy nhịn kh&ocirc;ng được sợ h&atilde;i than n&oacute;i.<br />\r\n<br />\r\nMặc d&ugrave; qu&acirc;n đội bạn may mắn tho&aacute;t khỏi tại kh&oacute;, nhưng l&agrave; mắt nh&igrave;n xem thủ đoạn của đối phương, lại l&agrave;m cho hắn cảm thấy da đầu run l&ecirc;n, thậm ch&iacute; đ&atilde; bắt đầu ho&agrave;i nghi lần n&agrave;y tiễu trừ x&aacute;c xuất th&agrave;nh c&ocirc;ng.<br />\r\n<br />\r\nĐương nhi&ecirc;n, Minh Vương lực c&ocirc;ng k&iacute;ch l&agrave; cực k&igrave; khủng bố.<br />\r\n<br />\r\nCho d&ugrave; l&agrave; Akatsuki Kojou, cũng điều động to&agrave;n bộ lực lượng, tr&ecirc;n người tinh lực đi&ecirc;n cuồng ti&ecirc;u hao, chỉ l&agrave; trong nh&aacute;y mắt liền ti&ecirc;u hao Akatsuki Kojou hơn ph&acirc;n nửa tinh lực.<br />\r\n<br />\r\nRất nhanh, cột s&aacute;ng hao hết năng lượng m&agrave; biến mất.<br />\r\n<br />\r\nAkatsuki Kojou chặn lại Minh Vương c&ocirc;ng k&iacute;ch, nhưng l&agrave; tr&ecirc;n người hắn cũng nứt to&aacute;c ra kh&ocirc;ng &iacute;t vết thương.<br />\r\n<br />\r\nBất qu&aacute; c&oacute; đầy đủ tinh lực bổ sung, vẻn vẹn thời gian một hơi thở, tr&ecirc;n người hắn tất cả thương thế liền kh&ocirc;i phục như ban đầu.<br />\r\n<br />\r\n&quot;Uranus.&quot;<br />\r\n<br />\r\nAkatsuki Kojou h&iacute;t s&acirc;u một hơi, lạnh l&ugrave;ng chằm chằm v&agrave;o ph&iacute;a dưới qu&acirc;n Li&ecirc;n Hiệp hạm đội, bờ m&ocirc;i khẽ nh&uacute;c nh&iacute;ch.<br />\r\n<br />\r\nSau một khắc, ph&iacute;a sau hắn to lớn b&oacute;ng ma liền theo &acirc;m thanh m&agrave; động, v&ocirc; số ở v&agrave;o đi&ecirc;n cuồng múa.<br />\r\n<br />\r\nẦm ầm! !<br />\r\n<br />\r\nC&aacute;i g&igrave; thời gian, bầu trời lại lần nữa bị trời u &aacute;m, v&ocirc; số điện x&agrave; tại tầng m&acirc;y nhảy l&ecirc;n, phảng phất liền muốn hướng về ph&iacute;a dưới mặt biển tr&uacute;t xuống.<br />\r\n<br />\r\nNgay tại l&uacute;c l&uacute;c n&agrave;y, Akatsuki Kojou b&ecirc;n người truyền đến một trận kh&ocirc;ng gian ba động.<br />\r\n<br />\r\nChỉ thấy b&ecirc;n cạnh kh&ocirc;ng gian giống như l&agrave; một c&aacute;nh cửa đồng dạng bị người mở ra, một b&agrave;n tay trắng n&otilde;n từ đ&oacute; nh&ocirc; ra, kho&aacute;c l&ecirc;n Akatsuki Kojou tr&ecirc;n c&aacute;nh tay.<br />\r\n<br />\r\n&quot;Rời đi thời đại n&agrave;y a.&quot;<br />\r\n<br />\r\nMột đạo mờ mịt vang l&ecirc;n, Akatsuki Kojou trong nh&aacute;y mắt biến mất ngay tại chỗ.<br />\r\n<br />\r\nĐồng thời, cự th&uacute; v&ocirc; số x&uacute;c tu cũng theo đ&oacute; dừng lại.<br />\r\n<br />\r\nBầu trời l&ocirc;i v&acirc;n c&ugrave;ng thiểm điện, tan th&agrave;nh m&acirc;y kh&oacute;i.<br />\r\n<br />\r\nCẦU HOA TƯƠI, CẦU BUFF KẸO. （づ￣3￣）づ╭❤～</p>', '2021-11-27 21:18:27', 20, 1),
(176, 48, 'Đến từ tám trăm năm trước quái vật!', '2', '<p>Hải Vi&ecirc;n lịch năm 1512.<br />\r\n<br />\r\nGrand Line nửa đoạn trước.<br />\r\n<br />\r\nJudicial Island, lại được xưng l&agrave; &#39;Bất Dạ Chi Đảo&#39; .<br />\r\n<br />\r\nNơi n&agrave;y, l&agrave; từ Ch&iacute;nh Phủ Thế Giới thiết lập Enies đảo, ph&agrave;m l&agrave; bị nhận định l&agrave; tội &aacute;c t&agrave;y trời người, đều sẽ bị mang đi kh&ocirc;ng c&oacute; một ai ph&aacute;p viện tiến h&agrave;nh tỉnh lại &#39;Thẩm ph&aacute;n&#39;, cuối c&ugrave;ng th&ocirc;ng qua to lớn băng l&atilde;nh Gates of Justice, bị mang đến giam giữ lấy v&ocirc; số tội nh&acirc;n Underwater Prison &mdash;&mdash; Great Prison.<br />\r\n<br />\r\nThời gian buổi chiều, ch&iacute;nh l&agrave; trong một ng&agrave;y thanh nh&agrave;n nhất thời điểm.<br />\r\n<br />\r\nNhưng m&agrave; ở v&agrave;o trước đảo cửa ch&iacute;nh, hai c&aacute;i thủ vệ cự nh&acirc;n lại ch&iacute;nh nghi hoặc nh&igrave;n tr&ecirc;n đảo bận rộn th&acirc;n ảnh.<br />\r\n<br />\r\n&quot;Garci, ngươi c&oacute; ph&aacute;t hiện hay kh&ocirc;ng, gần nhất tr&ecirc;n đảo tới thật nhiều Hải qu&acirc;n a?&quot;<br />\r\n<br />\r\nCự nh&acirc;n Oiimo c&oacute; ch&uacute;t kỳ qu&aacute;i hướng đồng bạn hỏi.<br />\r\n<br />\r\n&quot;Ta đương nhi&ecirc;n thấy được, đừng n&oacute;i phổ th&ocirc;ng hải qu&acirc;n, trước mấy ng&agrave;y liền ngay cả Hải qu&acirc;n tam đại chiến lực Đại tướng Aokiji đều tới đ&acirc;u, cũng kh&ocirc;ng biết l&agrave; muốn ph&aacute;t sinh đại sự g&igrave;.&quot;<br />\r\n<br />\r\nCự nh&acirc;n Garci hồi đ&aacute;p.<br />\r\n<br />\r\nĐang n&oacute;i, bỗng nhi&ecirc;n, hai c&aacute;i cự nh&acirc;n tựa hồ l&agrave; đ&atilde; nhận ra c&aacute;i g&igrave;, lập tức đứng người l&ecirc;n nh&igrave;n về ph&iacute;a phương xa.<br />\r\n<br />\r\nChỉ thấy hai chiếc to lớn Hải qu&acirc;n thuyền hạm đang tại đại lượng phổ th&ocirc;ng Hải qu&acirc;n qu&acirc;n hạm chen ch&uacute;c dưới, hướng ph&iacute;a Judicial Island b&ecirc;n n&agrave;y gần lại gần.<br />\r\n<br />\r\n&quot;Đ&oacute; l&agrave; Hải qu&acirc;n mặt kh&aacute;c hai c&aacute;i Đại tướng qu&acirc;n hạm! Th&aacute;ng trước kh&ocirc;ng phải mới tới qua rồi? Tại sao lại c&ugrave;ng đi?&quot;<br />\r\n<br />\r\nHai c&aacute;i cự nh&acirc;n đồng thời kinh h&ocirc; một tiếng, mở to hai mắt nh&igrave;n.<br />\r\n<br />\r\nBọn hắn ở chỗ n&agrave;y đ&atilde; đ&oacute;ng giữ bốn mươi năm, liền xem như l&uacute;c trước ph&aacute;n quyết Vua Hải Tặc Roger thời điểm, đều chưa từng thấy đến nhiều như vậy Hải qu&acirc;n.<br />\r\n<br />\r\nĐến tột c&ugrave;ng l&agrave; phải đề ph&ograve;ng dạng g&igrave; sự kiện lớn, v&acirc;̣y mà c&acirc;̀n ba Đại tướng đều tới?<br />\r\n<br />\r\nC&aacute;i n&agrave;y khiến để Judicial Island b&ecirc;n tr&ecirc;n tất cả qu&acirc;n coi giữ trong l&ograve;ng đều kinh nghi kh&ocirc;ng th&ocirc;i.<br />\r\n<br />\r\nSau đ&oacute; kh&ocirc;ng l&acirc;u, Akainu c&ugrave;ng Kizaru dẫn người leo l&ecirc;n Judicial Island.<br />\r\n<br />\r\nXuy&ecirc;n qua tầng tầng ph&ograve;ng tuyến, đi tới ở v&agrave;o h&ograve;n đảo ở giữa một c&aacute;i đặc th&ugrave; kiến tr&uacute;c trước.<br />\r\n<br />\r\nĐ&oacute; l&agrave; một c&aacute;i ho&agrave;n to&agrave;n do Seastone chế tạo cỡ nhỏ phương thể nh&agrave; giam, chỉnh thể đen kịt, kh&ocirc;ng c&oacute; cửa cửa sổ, ph&iacute;a tr&ecirc;n chỉ c&oacute; từng c&aacute;i đầu ng&oacute;n tay lớn lỗ nhỏ c&oacute; thể nh&igrave;n thấy t&igrave;nh huống b&ecirc;n trong.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nXuy&ecirc;n thấu qua ph&iacute;a tr&ecirc;n thật nhỏ trống rỗng, người chung quanh đều c&oacute; thể nh&igrave;n thấy, b&ecirc;n trong kh&ocirc;ng c&oacute; vật g&igrave;.<br />\r\n<br />\r\nAkainu nh&igrave;n trước mắt c&aacute;i n&agrave;y phảng phất v&igrave; kh&ocirc;i h&agrave;i m&agrave; tồn tại kiến tr&uacute;c, nhịn kh&ocirc;ng được cau m&agrave;y hỏi: &quot;Kh&ocirc;ng c&oacute; cửa v&agrave;o Seastone nh&agrave; giam, năm trăm chiếc qu&acirc;n hạm, mấy chục vạn Hải qu&acirc;n, c&ugrave;ng mỗi th&aacute;ng một lần đại triệu tập, Ch&iacute;nh Phủ Thế Giới đ&aacute;m kia ngu xuẩn đến tột c&ugrave;ng đang lo lắng c&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; bất kỳ c&aacute;i g&igrave; l&yacute; do, gần nhất trong một năm, Ch&iacute;nh Phủ Thế Giới kh&ocirc;ng ngừng điều động đại lượng Hải qu&acirc;n lại tới đ&acirc;y.<br />\r\n<br />\r\nB&acirc;y giờ ch&iacute;nh l&agrave; Đại hải tặc thời đại nhất l&agrave; rung chuyển bất an thời kỳ, tứ đại tr&ecirc;n biển Ho&agrave;ng Đế quần h&ugrave;ng c&aacute;t cứ, Shichibukai ch&iacute;nh s&aacute;ch chậm chạp c&ograve;n chưa ho&agrave;n thiện, Hải qu&acirc;n &aacute;p lực cực lớn.<br />\r\n<br />\r\nTại loại n&agrave;y thời kỳ, đột nhi&ecirc;n điều khiển đại lượng Hải qu&acirc;n tới một c&aacute;i kh&ocirc;ng cần đ&oacute;ng giữ Judicial Island vốn l&agrave; để Hải qu&acirc;n rất nhiều người đều c&oacute; ch&uacute;t bất m&atilde;n, hiện tại liền ngay cả ba Đại tướng đều muốn ở chỗ n&agrave;y thường tr&uacute;, đơn giản kh&ocirc;ng thể n&agrave;o hiểu được.<br />\r\n<br />\r\nM&agrave; tại Akainu b&ecirc;n cạnh, Kizaru đứng thẳng k&eacute;o lấy m&iacute; mắt, Aokiji c&agrave;ng l&agrave; một bộ buồn ngủ d&aacute;ng vẻ, đều kh&ocirc;ng c&oacute; mu&ocirc;́n nói chuy&ecirc;̣n &yacute; tứ.<br />\r\n<br />\r\nNgược lại l&agrave; Garp bu&ocirc;ng xuống trong tay nước tr&agrave;, tr&ecirc;n mặt &iacute;t c&oacute; lộ ra vẻ suy tư.<br />\r\n<br />\r\n&quot;Cụ thể, ta cũng kh&ocirc;ng r&otilde; r&agrave;ng lắm, bất qu&aacute; ta ngược lại l&agrave; biết một c&aacute;i c&oacute; quan hệ với Judicial Island truyền thuyết.&quot;<br />\r\n<br />\r\nTrong nh&aacute;y mắt, ba Đại tướng lập tức nh&igrave;n sang, liền ngay cả chung quanh những c&aacute;i kia đ&atilde; ở chỗ n&agrave;y đồn tr&uacute; thật l&acirc;u tướng lĩnh đều dựng l&ecirc;n lỗ tai.<br />\r\n<br />\r\n&quot;Truyền thuyết?&quot; Aokiji đứng thẳng k&eacute;o lấy m&iacute; mắt hỏi.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng sai, mặc d&ugrave; b&acirc;y giờ Judicial Island được xưng Bất Dạ Chi Đảo, tr&ecirc;n thực tế, tại trước đ&acirc;y thật l&acirc;u, nơi n&agrave;y đ&atilde; từng l&agrave; được xưng Vĩnh Dạ Chi Đảo.&quot;<br />\r\n<br />\r\n&quot;Vĩnh Dạ Chi Đảo?&quot;<br />\r\n<br />\r\nAkainu bọn người sững sờ, Judicial Island sở dĩ được xưng Bất Dạ Chi Đảo, cũng l&agrave; bởi v&igrave; tr&ecirc;n đảo l&acirc;u d&agrave;i ban ng&agrave;y, kh&ocirc;ng c&oacute; đ&ecirc;m tối.<br />\r\n<br />\r\nV&ocirc; luận như thế n&agrave;o, Akainu cũng kh&ocirc;ng nghĩ ra nơi n&agrave;y c&ugrave;ng vĩnh dạ c&oacute; quan hệ g&igrave;.<br />\r\n<br />\r\nBỗng nhi&ecirc;n, nhớ tới lần n&agrave;y Ch&iacute;nh Phủ Thế Giới như vậy gi&oacute;ng trống khua chi&ecirc;ng điều động Hải qu&acirc;n đại bộ phận binh lực c&ugrave;ng bộ đội tại Judicial Island thiết lập ph&ograve;ng tuyến, để Akainu &yacute; thức được c&aacute;i g&igrave;.<br />\r\n<br />\r\nGarp nh&igrave;n Akainu bọn người đo&aacute;n được một ch&uacute;t, liền tiếp tục n&oacute;i: &quot;Theo d&acirc;n gian truyền thuyết, l&agrave; Ch&iacute;nh Phủ Thế Giới d&ugrave;ng cải biến nơi n&agrave;y kh&iacute; hậu, đem vĩnh dạ đổi th&agrave;nh biến đổi th&agrave;nh bất dạ.&quot;<br />\r\n<br />\r\n&quot;Với lại, nghe n&oacute;i mục đ&iacute;ch l&agrave;m như vậy, l&agrave; v&igrave; đối ph&oacute; một c&aacute;i mưu toan ph&aacute; vỡ thế giới gia hỏa.&quot;<br />\r\n<br />\r\nGarp l&agrave;m Hải qu&acirc;n b&ecirc;n trong l&atilde;o nh&acirc;n, biết đến tin tức muốn xa so với hiện tại ba Đại tướng nhiều hơn nhiều.<br />\r\n<br />\r\nBất qu&aacute; mặc d&ugrave; như thế, c&aacute;i n&agrave;y cũng đ&atilde; l&agrave; hắn biết hết thảy c&ugrave;ng Judicial Island c&oacute; li&ecirc;n quan tin tức.<br />\r\n<br />\r\nC&ugrave;ng n&agrave;y đồng thời.<br />\r\n<br />\r\nRed Line, Pangaea Castle.<br />\r\n<br />\r\nRoom of Authority.<br />\r\n<br />\r\nGorosei cũng ch&iacute;nh ch&uacute; &yacute; Judicial Island động tĩnh.<br />\r\n<br />\r\n&quot;C&aacute;c vị đại nh&acirc;n, Hải qu&acirc;n ba Đại tướng đ&atilde; tại Judicial Island tập kết.&quot;<br />\r\n<br />\r\nCP0 tối cao trưởng quan, Spandine th&ocirc;ng qua Den Den Mushi đối Gorosei b&aacute;o c&aacute;o.<br />\r\n<br />\r\n&quot;Để bọn hắn nh&igrave;n chằm chằm Seastone nh&agrave; giam.&quot;<br />\r\n<br />\r\nRăng rắc.<br />\r\n<br />\r\nB&ecirc;n trong một c&aacute;i Gorosei vứt xuống một c&aacute;i mệnh lệnh, liền c&uacute;p Den Den Mushi.<br />\r\n<br />\r\nDen Den Mushi vừa mới c&uacute;p m&aacute;y, liền lập tức c&oacute; người n&oacute;i ra.<br />\r\n<br />\r\n&quot;Im đại nh&acirc;n c&oacute; phải hay kh&ocirc;ng qu&aacute; mức cẩn thận? Coi như qu&aacute;i vật kia lại thế n&agrave;o lợi hại, cũng chỉ l&agrave; t&aacute;m trăm năm trước nh&acirc;n vật, thời điểm đ&oacute; người n&oacute;i kh&ocirc;ng chừng ngay cả Haki đều kh&ocirc;ng c&oacute;, kh&ocirc;ng c&oacute; g&igrave; đ&aacute;ng lo lắng.&quot;<br />\r\n<br />\r\nTr&ecirc;n thực tế Gorosei cũng đều l&agrave; đối với c&aacute;i n&agrave;y truyền thừa t&aacute;m trăm năm mệnh lệnh cảm thấy nực cười.<br />\r\n<br />\r\nThời gian c&aacute;ch xa nhau t&aacute;m trăm năm, hai mươi vị Vương khai s&aacute;ng Ch&iacute;nh Phủ Thế Giới đ&atilde; l&agrave; c&aacute;i thế giới n&agrave;y cường đại nhất tổ chức.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; người sẽ cảm thấy một c&aacute;i đến từ t&aacute;m trăm năm trước người c&oacute; thể ph&aacute; vỡ Ch&iacute;nh Phủ Thế Giới thống trị.<br />\r\n<br />\r\n&quot;Hiện tại ch&acirc;n ch&iacute;nh uy hiếp được Ch&iacute;nh Phủ Thế Giới tồn tại l&agrave; thế giới mới Tứ Ho&agrave;ng, Shichibukai kế hoạch tiến h&agrave;nh thế n&agrave;o?&quot;<br />\r\n<br />\r\n&quot;Chuyện n&agrave;y l&agrave; Hải qu&acirc;n đang phụ tr&aacute;ch, trong danh s&aacute;ch, c&ograve;n k&eacute;m cái cu&ocirc;́i cùng Cửu X&agrave; đảo Nữ Đế.&quot;<br />\r\n<br />\r\n&quot;Nghi&ecirc;n cứu khoa học bộ đội Seastone thuyền hạm đ&atilde; ho&agrave;n th&agrave;nh, giải quyết hết c&aacute;i kia t&aacute;m trăm năm trước gia hỏa, liền c&oacute; thể ho&agrave;n th&agrave;nh sau c&ugrave;ng Shichibukai chi&ecirc;u mộ.&quot;<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nGorosei đang n&oacute;i, chợt thấy, Judicial Island h&igrave;nh ảnh Den Den Mushi truyền đến h&igrave;nh tượng một cơn chấn động.<br />\r\n<br />\r\nTại đen kịt Seastone trong nh&agrave; giam.<br />\r\n<br />\r\nBỗng nhi&ecirc;n s&aacute;ng l&ecirc;n một v&ograve;ng huyết sắc quang mang.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nMột cỗ nồng đậm tinh lực xuy&ecirc;n thấu qua Seastone nh&agrave; giam lỗ thủng truyền ra.<br />\r\n<br />\r\nT&ugrave;y theo m&agrave; đến, c&ograve;n c&oacute; một cỗ kh&oacute; n&oacute;i l&ecirc;n lời kinh khủng uy &aacute;p.<br />\r\n<br />\r\nJudicial Island b&ecirc;n tr&ecirc;n động vật chim bay lập tức bắt đầu trở n&ecirc;n xao động, phảng phất ng&agrave;y tận thế tới đồng dạng, đi&ecirc;n cuồng muốn rời khỏi h&ograve;n đảo n&agrave;y.<br />\r\n<br />\r\nTất cả Hải qu&acirc;n sắc mặt ửng hồng, trong nh&aacute;y mắt n&agrave;y, tất cả mọi người c&oacute; thể cảm nhận được m&igrave;nh to&agrave;n th&acirc;n m&aacute;u tươi đều tại s&ocirc;i tr&agrave;o.<br />\r\n<br />\r\nTrong cơ thể m&aacute;u tươi lưu động phảng phất tho&aacute;t ly khống chế của bọn hắn, l&agrave; tại nhảy cẫng hoan h&ocirc; ngh&ecirc;nh đ&oacute;n c&aacute;i n&agrave;o đ&oacute; vĩ đại tồn tại.<br />\r\n<br />\r\nBa Đại tướng cũng l&agrave; trước ti&ecirc;n hướng ph&iacute;a Seastone nh&agrave; giam nh&igrave;n sang.<br />\r\n<br />\r\n&quot;B&ecirc;n trong, xuất hiện thứ g&igrave;?&quot;<br />\r\n<br />\r\nAkainu l&uacute;c n&agrave;y to&agrave;n th&acirc;n l&ocirc;ng tơ nổ l&ecirc;n, &aacute;nh mắt nh&igrave;n ch&ograve;ng chọc v&agrave;o ph&iacute;a trước Seastone nh&agrave; giam, từ trong h&agrave;m răng gạt ra một c&acirc;u.<br />\r\n<br />\r\nThực lực c&agrave;ng mạnh, đối với c&aacute;i kia cỗ uy &aacute;p cảm x&uacute;c cũng c&agrave;ng s&acirc;u, ngoại trừ m&aacute;u trong cơ thể dị thường, ba Đại tướng tr&ecirc;n th&acirc;n thật giống như c&ograve;n ngo&agrave;i định mức lưng đeo một t&ograve;a đại sơn đồng dạng, nhục thể tại bản năng run rẩy.<br />\r\n<br />\r\nAokiji c&ugrave;ng Kizaru như l&agrave; bị kinh đến m&egrave;o đồng dạng, b&agrave;y ra c&ocirc;ng k&iacute;ch tư thế, cảnh giới chằm chằm v&agrave;o ph&iacute;a trước.<br />\r\n<br />\r\nBỗng nhi&ecirc;n, b&ecirc;n cạnh truyền đến một tiếng kinh ngạc thấp giọng h&ocirc; &quot;Cỗ kh&iacute; tức n&agrave;y! Chẳng lẽ l&agrave;, Rocks t&ecirc;n kia, sống lại? ! ! !&quot;<br />\r\n<br />\r\n&quot;Rocks?&quot;<br />\r\n<br />\r\nBa Đại tướng c&ugrave;ng nhau sững sờ, ph&aacute;t hiện l&agrave; Garp ph&aacute;t ra thấp giọng h&ocirc;, lập tức hướng n&oacute; n&eacute;m ra &aacute;nh mắt nghi hoặc.<br />\r\n<br />\r\n&quot;Rocks, phục sinh?&quot;<br />\r\n<br />\r\nKh&ocirc;ng đợi Garp giải th&iacute;ch, tại ph&iacute;a trước Seastone nh&agrave; giam b&ecirc;n trong, bỗng nhi&ecirc;n truyền ra một thanh &acirc;m.<br />\r\n<br />\r\n&quot;A? Rocks tiểu tử kia, đ&atilde; chết rồi sao?&quot;<br />\r\n<br />\r\nXuy&ecirc;n thấu qua Seastone nh&agrave; giam khe hở, người chung quanh đều rốt cục ph&aacute;t hiện, b&ecirc;n trong thế m&agrave; trống rỗng xuất hiện một c&aacute;i nh&acirc;n loại th&acirc;n ảnh.<br />\r\n<br />\r\nCẦU HOA TƯƠI, CẦU BUFF KẸO. （づ￣3￣）づ╭❤～</p>', '2021-11-27 21:18:55', 9, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(177, 48, 'Cero! Bị xỏ xuyên Judicial Island!', '3', '<p>&quot;B&ecirc;n trong c&oacute; người!&quot;<br />\r\n<br />\r\nNh&igrave;n xem chỉ c&oacute; ng&oacute;n tay nhỏ b&eacute; khe hở Seastone trong nh&agrave; giam mặt thế m&agrave; xuất hiện một người, để chung quanh Hải qu&acirc;n giật nảy m&igrave;nh.<br />\r\n<br />\r\n&quot;Cỗ kh&iacute; tức n&agrave;y, đơn giản c&ugrave;ng Rocks giống như đ&uacute;c! Kh&ocirc;ng, so Rocks c&ograve;n muốn cho người r&ugrave;ng m&igrave;nh, đến c&ugrave;ng l&agrave; chuyện g&igrave; xảy ra?&quot;<br />\r\n<br />\r\nGarp chằm chằm v&agrave;o ph&iacute;a trước Seastone nh&agrave; giam, mặt ngo&agrave;i kh&ocirc;ng n&oacute;i g&igrave;, trong l&ograve;ng cũng đ&atilde; bốc l&ecirc;n kh&ocirc;ng th&ocirc;i.<br />\r\n<br />\r\nNếu như c&oacute; thể m&agrave; n&oacute;i, hắn hiện tại liền muốn đem Sengoku cho k&eacute;o qua nh&igrave;n x&aacute;c nhận một ch&uacute;t, b&ecirc;n trong đến c&ugrave;ng c&oacute; phải hay kh&ocirc;ng đ&atilde; từng bị hắn c&ugrave;ng Roger li&ecirc;n thủ đ&aacute;nh bại Rocks.<br />\r\n<br />\r\nC&aacute;i kia cỗ phảng phất m&aacute;u tươi nhuộm dần kh&iacute; tức, để Garp đến b&acirc;y giờ đều kh&ocirc;ng thể qu&ecirc;n được.<br />\r\n<br />\r\nThế nhưng l&agrave; kh&ocirc;ng biết v&igrave; c&aacute;i g&igrave;, hắn lại c&oacute; loại cảm gi&aacute;c.<br />\r\n<br />\r\nC&aacute;i kia ch&iacute;nh l&agrave; b&ecirc;n trong gia hoả kia tinh lực giống như so Rocks tr&ecirc;n người tinh lực c&ograve;n muốn nồng đậm v&ocirc; số lần.<br />\r\n<br />\r\n&quot;Chẳng lẽ người b&ecirc;n trong lại so với Rocks c&ograve;n mạnh hơn?&quot;<br />\r\n<br />\r\nGarp trong l&ograve;ng hiện ra &yacute; nghĩ n&agrave;y, một gi&acirc;y sau liền bị hắn tan họp.<br />\r\n<br />\r\n&quot;Điều đ&oacute; kh&ocirc;ng c&oacute; khả năng!&quot;<br />\r\n<br />\r\nGarp khẳng định l&acirc;̉m b&acirc;̉m một tiếng.<br />\r\n<br />\r\nL&uacute;c n&agrave;y.<br />\r\n<br />\r\nRoom of Authority b&ecirc;n trong.<br />\r\n<br />\r\nGorosei cũng ngay đầu ti&ecirc;n thấy được Seastone nh&agrave; giam b&ecirc;n trong c&aacute;i th&acirc;n ảnh kia.<br />\r\n<br />\r\n&quot;Thật xuất hiện, nếm thử nh&igrave;n xem c&oacute; thể hay kh&ocirc;ng sống bắt t&ecirc;n kia.&quot; Gorosei nh&igrave;n xem h&igrave;nh chiếu, trong mắt đồng thời hiện l&ecirc;n một v&ograve;ng dị sắc.<br />\r\n<br />\r\nT&aacute;m trăm năm trước gia hỏa, tr&ecirc;n tay khẳng định nắm giữ lấy rất nhiều b&iacute; mật.<br />\r\n<br />\r\n&quot;Lập tức ph&oacute;ng th&iacute;ch trấn định kh&iacute; thể, bắt sống mục ti&ecirc;u!&quot;<br />\r\n<br />\r\nTại Judicial Island b&ecirc;n tr&ecirc;n, Ch&iacute;nh Phủ Thế Giới một phương nh&acirc;n vi&ecirc;n chiến đấu đ&atilde; chuẩn bị kỹ c&agrave;ng.<br />\r\n<br />\r\nTại Seastone nh&agrave; giam b&ecirc;n trong xuất hiện nh&acirc;n loại th&acirc;n ảnh trước ti&ecirc;n, liền đ&atilde; động thủ.<br />\r\n<br />\r\nM&agrave;u trắng sương m&ugrave; th&ocirc;ng qua nhiều c&aacute;i khe hở bị chuyển v&agrave;o Seastone nh&agrave; giam b&ecirc;n trong.<br />\r\n<br />\r\nTại c&aacute;i kia nhỏ hẹp Seastone trong nh&agrave; giam mặt.<br />\r\n<br />\r\nAkatsuki Kojou vừa xuất hiện liền rơi v&agrave;o nh&agrave; giam tr&agrave;n đầy gai nhọn Seastone tr&ecirc;n mặt đất.<br />\r\n<br />\r\nTr&aacute;i &Aacute;c Quỷ năng lực lập tức nhận lấy hạn chế.<br />\r\n<br />\r\nNhưng l&agrave; gai nhọn lại kh&ocirc;ng có th&ecirc;̉ đối Akatsuki Kojou tạo th&agrave;nh tổn thương, thậm ch&iacute; đều kh&ocirc;ng có th&ecirc;̉ đ&acirc;m r&aacute;ch Akatsuki Kojou l&agrave;n da.<br />\r\n<br />\r\nVới lại Akatsuki Kojou cũng kh&ocirc;ng c&oacute; giống đại đa số năng lực giả như thế triệt để mất đi lực lượng.<br />\r\n<br />\r\nVốn l&agrave; biết Tr&aacute;i &Aacute;c Quỷ năng lực giả tai hại Akatsuki Kojou, kh&ocirc;ng d&ugrave;ng một phần nhỏ Seastone ma luyện m&igrave;nh.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nCoi như kh&ocirc;ng c&aacute;ch n&agrave;o ph&aacute;t động năng lực, thể ph&aacute;ch của hắn cũng đ&atilde; vượt xa qua Cự Nh&acirc;n tộc.<br />\r\n<br />\r\nChỉ thấy Akatsuki Kojou mũi ch&acirc;n đạp mạnh.<br />\r\n<br />\r\nTh&acirc;n thể vọt l&ecirc;n, tho&aacute;t ly Seastone hạn chế.<br />\r\n<br />\r\nSau người huyết sắc &aacute;o cho&agrave;ng h&oacute;a th&agrave;nh một đ&ocirc;i m&agrave;u đỏ thắm c&aacute;nh thịt kh&ocirc;ng ngừng vỗ, để Akatsuki Kojou th&acirc;n thể bảo tr&igrave; tại giữa kh&ocirc;ng huyền lập.<br />\r\n<br />\r\nM&agrave; l&uacute;c n&agrave;y, Akatsuki Kojou đ&atilde; h&uacute;t v&agrave;o ch&uacute;t &iacute;t trấn định tề.<br />\r\n<br />\r\nMặc d&ugrave; b&acirc;y giờ đối với hắn ảnh hưởng kh&ocirc;ng lớn, nhưng l&agrave; hiện ở trong cơ thể hắn tinh lực đ&atilde; bởi v&igrave; l&uacute;c trước chống cự Minh Vương c&ocirc;ng k&iacute;ch, ti&ecirc;u hao hơn ph&acirc;n nửa.<br />\r\n<br />\r\nNếu như lại tiếp tục h&uacute;t v&agrave;o lời n&oacute;i, đối diện trước Seastone nh&agrave; giam liền kh&ocirc;ng c&oacute; biện ph&aacute;p.<br />\r\n<br />\r\nAkatsuki Kojou ph&iacute;a sau c&aacute;nh thịt vung l&ecirc;n, lập tức một cỗ nồng đậm tinh lực ph&oacute;ng th&iacute;ch.<br />\r\n<br />\r\nTinh lực trước ti&ecirc;n đem Akatsuki Kojou chung quanh th&acirc;n thể trấn định tề &eacute;p ra, v&igrave; Akatsuki Kojou tại bản n&agrave;y liền nhỏ hẹp Seastone nh&agrave; giam b&ecirc;n trong mở ra một mảnh khu vực an to&agrave;n.<br />\r\n<br />\r\nVừa ra tới liền Seastone nh&agrave; giam c&ugrave;ng kh&iacute; độc, lại th&ecirc;m vừa mới b&ecirc;n ngo&agrave;i truyền đến thanh &acirc;m, để Akatsuki Kojou trong mắt kh&ocirc;ng khỏi hiện l&ecirc;n một v&ograve;ng l&atilde;nh quang.<br />\r\n<br />\r\n&quot;Rocks a. . .&quot;<br />\r\n<br />\r\nAkatsuki Kojou l&acirc;̉m b&acirc;̉m một tiếng, nh&igrave;n trước mắt lồng giam nhỏ hẹp nh&agrave; giam.<br />\r\n<br />\r\nMột gi&acirc;y sau.<br />\r\n<br />\r\nRất nhiều tinh lực trong nh&aacute;y mắt từ Akatsuki Kojou tr&ecirc;n th&acirc;n bắn ra.<br />\r\n<br />\r\nChỉ thấy Akatsuki Kojou giơ tay l&ecirc;n, đưa ng&oacute;n trỏ ra.<br />\r\n<br />\r\nTinh lực lập tức ở Akatsuki Kojou đầu ng&oacute;n tay tụ tập.<br />\r\n<br />\r\n&Ocirc;ng ~!<br />\r\n<br />\r\nSeastone nh&agrave; giam chung quanh Hải qu&acirc;n lập tức nghe được một cỗ phảng phất l&agrave; kh&ocirc;ng kh&iacute; ma s&aacute;t sinh ra v&ugrave; v&ugrave; &acirc;m thanh.<br />\r\n<br />\r\nHuyết quang lập tức từ Seastone nh&agrave; giam b&ecirc;n trong xuy&ecirc;n thấu qua khe hở tỏa ra.<br />\r\n<br />\r\nNguy&ecirc;n bản đen kịt Seastone nh&agrave; giam, l&uacute;c n&agrave;y liền như l&agrave; một c&aacute;i to lớn m&agrave;u đỏ đ&egrave;n cầu đồng dạng ph&oacute;ng xuất ra từng đạo huyết hồng sắc cột s&aacute;ng.<br />\r\n<br />\r\nSeastone nh&agrave; giam b&ecirc;n ngo&agrave;i đám hải qu&acirc;n cảm thấy trong cơ thể m&aacute;u tươi s&ocirc;i tr&agrave;o c&agrave;ng th&ecirc;m m&atilde;nh liệt, phảng phất t&ugrave;y thời đều muốn đột ph&aacute; l&agrave;n da từ đ&oacute; x&ocirc;ng tới đồng dạng.<br />\r\n<br />\r\nKh&ocirc;ng &iacute;t người thậm ch&iacute; đ&atilde; ph&aacute;t động Busoshoku Haki &aacute;p chế, cưỡng &eacute;p trấn &aacute;p trong cơ thể bốc l&ecirc;n huyết dịch.<br />\r\n<br />\r\nM&agrave; một ch&uacute;t c&ograve;n kh&ocirc;ng c&oacute; nắm giữ Haki Hải qu&acirc;n th&igrave; liền kh&ocirc;ng c&oacute; vận tốt như vậy.<br />\r\n<br />\r\nPhốc ~!<br />\r\n<br />\r\nTừng c&aacute;i Hải qu&acirc;n trong miệng mũi bắt đầu c&oacute; m&aacute;u tươi tr&agrave;n ra, m&agrave; những c&aacute;i kia m&aacute;u tươi vừa xuất hiện, phảng phất như l&agrave; nhận lấy dẫn dắt đồng dạng, cấp tốc xuy&ecirc;n thấu qua khe hở hướng Seastone nh&agrave; giam b&ecirc;n trong hội tụ.<br />\r\n<br />\r\n&quot;Tản ra, tất cả mọi người lập tức tản ra!&quot;<br />\r\n<br />\r\nGarp mắt thấy một m&agrave;n n&agrave;y, lập tức cao giọng n&oacute;i,<br />\r\n<br />\r\nChung quanh Hải qu&acirc;n nghe được mệnh lệnh, như tr&uacute;t được g&aacute;nh nặng, cấp tốc hướng nơi xa t&aacute;n đi.<br />\r\n<br />\r\n&quot;T&ecirc;n kia, sẽ kh&ocirc;ng phải l&agrave; dự định ph&aacute; hư c&aacute;i n&agrave;y Seastone nh&agrave; giam a?&quot;<br />\r\n<br />\r\nKizaru h&iacute;t s&acirc;u một hơi, đ&egrave; xuống trong cơ thể bốc l&ecirc;n huyết dịch, chằm chằm v&agrave;o ph&iacute;a trước Seastone nh&agrave; giam, c&oacute; ch&uacute;t kh&ocirc;ng x&aacute;c định n&oacute;i ra.<br />\r\n<br />\r\n&quot;Ngu xuẩn, ho&agrave;n to&agrave;n do Seastone chế tạo nh&agrave; giam, liền xem như Tứ Ho&agrave;ng đều rất kh&oacute; ph&aacute; hỏng.&quot;<br />\r\n<br />\r\nAkainu nghe được cười lạnh một tiếng.<br />\r\n<br />\r\nAkainu vừa dứt lời.<br />\r\n<br />\r\nAkatsuki Kojou dứt kho&aacute;t đem tr&ecirc;n th&acirc;n c&ograve;n s&oacute;t lại bộ phận tinh lực thả ra.<br />\r\n<br />\r\nTrong l&uacute;c nhất thời, tinh lực c&agrave;ng th&ecirc;m nồng đậm, tại Seastone nh&agrave; giam b&ecirc;n trong cơ hồ ngưng tụ th&agrave;nh huyết tr&igrave;, bất qu&aacute; lại tại trong khoảnh khắc tất cả đều hội tụ đến Akatsuki Kojou đầu ng&oacute;n tay.<br />\r\n<br />\r\nCero!<br />\r\n<br />\r\nAkatsuki Kojou &aacute;nh mắt nhất động.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; hắn lấy Haki đem tinh lực quấn quanh &aacute;p s&uacute;c, cuối c&ugrave;ng th&ocirc;ng qua trong nh&aacute;y mắt ph&oacute;ng th&iacute;ch, mượn nhờ Hougyoku lực lượng, m&ocirc; phỏng ra Tử Thần b&ecirc;n trong Cero.<br />\r\n<br />\r\nTrong khoảnh khắc.<br />\r\n<br />\r\nTinh lực ngưng tụ th&agrave;nh Cero trong nh&aacute;y mắt ph&oacute;ng th&iacute;ch.<br />\r\n<br />\r\nGiờ khắc n&agrave;y, trong nh&agrave; giam triệt để biến th&agrave;nh một mảnh huyết sắc.<br />\r\n<br />\r\nKhe hở b&ecirc;n trong thả ra huyết sắc hồng mang thậm ch&iacute; đem trọn c&aacute;i Judicial Island đều nhuộm th&agrave;nh m&agrave;u đỏ.<br />\r\n<br />\r\nẦm ầm.<br />\r\n<br />\r\nTo&agrave;n bộ Judicial Island bắt đầu kịch liệt run rẩy l&ecirc;n.<br />\r\n<br />\r\nKh&iacute; tức kinh khủng lần nữa từ Seastone nh&agrave; giam b&ecirc;n trong di t&aacute;n đi ra.<br />\r\n<br />\r\nTại thời khắc n&agrave;y, tất cả mọi người trong đầu bỗng nhi&ecirc;n l&oacute;e l&ecirc;n một v&ograve;ng &yacute; tưởng bất khả tư nghị.<br />\r\n<br />\r\n&quot;Thật chẳng lẽ c&oacute; người c&oacute; thể đ&aacute;nh vỡ c&aacute;i n&agrave;y Seastone kiến tạo nh&agrave; giam?&quot;<br />\r\n<br />\r\n&Yacute; nghĩ n&agrave;y vừa mới d&acirc;ng l&ecirc;n.<br />\r\n<br />\r\nTiếp theo một c&aacute;i chớp mắt.<br />\r\n<br />\r\nHồng mang ph&oacute;ng đại.<br />\r\n<br />\r\nLần n&agrave;y, hồng mang trực tiếp che mất nửa c&aacute;i Judicial Island.<br />\r\n<br />\r\nMột mực x&ocirc;ng ra mấy chục km, t&iacute;nh cả Judicial Island b&ecirc;n ngo&agrave;i mấy chục t&agrave;u chiến hạm đều bị bao qu&aacute;t trong đ&oacute;.<br />\r\n<br />\r\nHồng mang c&ugrave;ng ch&oacute;i tai v&ugrave; v&ugrave; &acirc;m thanh trực tiếp tước đoạt Hải qu&acirc;n thị gi&aacute;c c&ugrave;ng th&iacute;nh gi&aacute;c.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nKh&ocirc;ng &iacute;t người người cảm thấy tinh lực d&acirc;ng l&ecirc;n, đầu v&aacute;ng mắt hoa.<br />\r\n<br />\r\nKh&ocirc;ng biết qua bao l&acirc;u.<br />\r\n<br />\r\nHồng mang c&ugrave;ng ch&oacute;i tai v&ugrave; v&ugrave; &acirc;m thanh đều biến mất.<br />\r\n<br />\r\nĐ&aacute;m người thị gi&aacute;c kh&ocirc;i phục sau liền trước ti&ecirc;n hướng ph&iacute;a nh&agrave; giam nh&igrave;n lại.<br />\r\n<br />\r\nT&ecirc; ~!<br />\r\n<br />\r\nTrong nh&aacute;y mắt.<br />\r\n<br />\r\nĐ&aacute;m người phảng phất nghe được v&ocirc; số h&iacute;t một hơi l&atilde;nh kh&iacute; thanh &acirc;m.<br />\r\n<br />\r\nChỉ thấy c&aacute;i kia danh xưng kh&ocirc;ng thể ph&aacute; vỡ Seastone nh&agrave; giam c&oacute; một nửa đ&atilde; biến mất kh&ocirc;ng thấy g&igrave; nữa.<br />\r\n<br />\r\nĐồng dạng biến mất.<br />\r\n<br />\r\nC&ograve;n c&oacute; c&aacute;i kia một mặt Seastone v&aacute;ch tường sau h&ograve;n đảo.<br />\r\n<br />\r\nSeastone nh&agrave; giam b&ecirc;n ngo&agrave;i thổ địa biến th&agrave;nh v&aacute;ch đ&aacute;.<br />\r\n<br />\r\nPh&iacute;a trước hơn mười km b&aacute;n k&iacute;nh h&igrave;nh quạt khu vực đ&atilde; ho&agrave;n to&agrave;n biến mất.<br />\r\n<br />\r\nLiền ngay cả đảo b&ecirc;n ngo&agrave;i đều xuất hiện một c&aacute;i to lớn h&igrave;nh quạt hồng c&acirc;u.<br />\r\n<br />\r\nM&agrave; nguy&ecirc;n bản đỗ tr&ecirc;n mặt biển qu&acirc;n hạm c&agrave;ng l&agrave; đ&atilde; theo h&ograve;n đảo c&ugrave;ng một chỗ biến mất kh&ocirc;ng thấy g&igrave; nữa.<br />\r\n<br />\r\nHoảng sợ!<br />\r\n<br />\r\nRun rẩy!<br />\r\n<br />\r\nĐ&acirc;y l&agrave; l&uacute;c n&agrave;y Judicial Island b&ecirc;n tr&ecirc;n đám hải qu&acirc;n trong l&ograve;ng chỉ c&oacute; hai loại biểu hiện.<br />\r\n<br />\r\n&Aacute;nh mắt mọi người đều tụ tập tại c&aacute;i kia Seastone nh&agrave; giam b&ecirc;n trong.<br />\r\n<br />\r\nMột c&aacute;i l&agrave;n da trắng t&iacute;ch, th&acirc;n mang trường b&agrave;o m&agrave;u đỏ ng&ograve;m nam nh&acirc;n từ đ&oacute; phe phẩy ph&iacute;a sau c&aacute;nh thịt từ đ&oacute; bay ra.<br />\r\n<br />\r\nĐối phương &aacute;nh mắt qu&eacute;t tới trong nh&aacute;y mắt.<br />\r\n<br />\r\nBa Đại tướng đồng thời tiến nhập nguy&ecirc;n tố h&oacute;a.<br />\r\n<br />\r\n&quot;Hải qu&acirc;n ba Đại tướng, c&ograve;n c&oacute; Garp.&quot;<br />\r\n<br />\r\nAkatsuki Kojou quan s&aacute;t ph&iacute;a dưới nguy&ecirc;n tố h&oacute;a ba người, c&ugrave;ng b&ecirc;n cạnh Garp, tr&ecirc;n mặt hiển hiện như đ&uacute;c thần sắc kh&aacute;c thường. .<br />\r\n<br />\r\n&quot;Hiện tại đ&acirc;y l&agrave;, t&aacute;m trăm năm sau?&quot;</p>', '2021-11-27 21:19:20', 5, 1),
(178, 48, 'Một quyền chi uy! Khiếp sợ Akainu!', '4', '<p>L&uacute;c n&agrave;y, Garp nh&igrave;n xem từ Seastone nh&agrave; giam b&ecirc;n trong lao ra Akatsuki Kojou, con ngươi bỗng nhi&ecirc;n co lại th&agrave;nh một điểm.<br />\r\n<br />\r\n&quot;Gia hỏa n&agrave;y rốt cuộc l&agrave; ai, kh&iacute; tức tr&ecirc;n th&acirc;n thế m&agrave; thật c&ugrave;ng Rocks giống như đ&uacute;c!&quot;<br />\r\n<br />\r\nGarp trong l&ograve;ng cuồng loạn, nh&igrave;n về ph&iacute;a trước người kia, kh&ocirc;ng khỏi nhớ tới hai mươi năm trước thời đại kia.<br />\r\n<br />\r\n&quot;Đ&uacute;ng, Rocks đ&atilde; từng rất nhiều lần tập k&iacute;ch Judicial Island, chẳng lẽ ch&iacute;nh l&agrave; v&igrave; gia hỏa n&agrave;y?&quot;<br />\r\n<br />\r\nBỗng nhi&ecirc;n, nghĩ tới đ&acirc;y, Garp trong l&ograve;ng nhảy c&agrave;ng th&ecirc;m lợi hại.<br />\r\n<br />\r\n&quot;C&ocirc;ng k&iacute;ch ~!&quot;<br />\r\n<br />\r\nL&uacute;c n&agrave;y, nơi xa truyền đến một tiếng to lớn chỉ huy &acirc;m thanh.<br />\r\n<br />\r\nChỉ thấy nơi xa Ch&iacute;nh Phủ Thế Giới nh&acirc;n vi&ecirc;n chiến đấu đ&atilde; h&agrave;nh động.<br />\r\n<br />\r\nTừng c&aacute;i cự ph&aacute;o nhắm ngay giữa kh&ocirc;ng trung Akatsuki Kojou.<br />\r\n<br />\r\nMột gi&acirc;y sau.<br />\r\n<br />\r\nLi&ecirc;n mi&ecirc;n bất tuyệt &aacute;nh lửa tại Judicial Island b&ecirc;n tr&ecirc;n s&aacute;ng l&ecirc;n.<br />\r\n<br />\r\nTừng khỏa đạn ph&aacute;o l&iacute;t nha l&iacute;t nh&iacute;t sắp xếp, c&ograve;n như mưa rơi một dạng bay vụt vẽ ph&aacute; kh&ocirc;ng kh&iacute;, hướng ph&iacute;a Akatsuki Kojou bay vụt tới.<br />\r\n<br />\r\nAkatsuki Kojou &aacute;nh mắt ngưng tụ, tr&ecirc;n người tinh lực lần nữa bộc ph&aacute;t, cũng lấy Haki b&aacute;m v&agrave;o.<br />\r\n<br />\r\nHuyết H&agrave; Chi Thuẫn!<br />\r\n<br />\r\nẦm ầm ~!<br />\r\n<br />\r\nĐạn ph&aacute;o tại Akatsuki Kojou quanh th&acirc;n bạo tạc.<br />\r\n<br />\r\nBạo tạc hỏa diễm trong khoảnh khắc liền tr&agrave;n ngập to&agrave;n bộ bầu trời.<br />\r\n<br />\r\nĐạn ph&aacute;o bạo tạc li&ecirc;n tục kh&ocirc;ng ngừng.<br />\r\n<br />\r\nTrọn vẹn kh&ocirc;ng ngừng oanh tạc hơn mười ph&uacute;t mới dừng lại.<br />\r\n<br />\r\nL&uacute;c n&agrave;y bầu trời kh&ocirc;ng kh&iacute; đ&atilde; bị bạo tạc hỏa diễm cho đốt vặn vẹo.<br />\r\n<br />\r\nNhiệt độ cao ph&oacute;ng xạ đến Judicial Island b&ecirc;n tr&ecirc;n, mỗi người đều c&oacute; thể cảm nhận được vừa mới c&aacute;i kia một v&ograve;ng đ&aacute;nh nổ uy lực.<br />\r\n<br />\r\nBất qu&aacute; một gi&acirc;y sau.<br />\r\n<br />\r\nTất cả mọi người lại đều chỉ cảm thấy m&igrave;nh to&agrave;n th&acirc;n ph&aacute;t lạnh.<br />\r\n<br />\r\nTheo bạo tạc hỏa diễm t&aacute;n đi.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nTất cả mọi người nh&igrave;n thấy, tr&ecirc;n bầu trời c&oacute; một c&aacute;i huyết sắc h&igrave;nh cầu.<br />\r\n<br />\r\nNh&igrave;n kỹ lại.<br />\r\n<br />\r\nĐ&oacute; l&agrave; một đo&agrave;n huyết vụ đang nhanh ch&oacute;ng xoay tr&ograve;n.<br />\r\n<br />\r\nĐợi đến huyết vụ tản ra.<br />\r\n<br />\r\nĐám hải qu&acirc;n thấy được kh&ocirc;ng muốn gặp nhất c&aacute;i th&acirc;n ảnh kia.<br />\r\n<br />\r\nC&aacute;i kia th&acirc;n mang trường b&agrave;o m&agrave;u đỏ ng&ograve;m nam nh&acirc;n c&ograve;n tại.<br />\r\n<br />\r\nVừa mới li&ecirc;n tục hơn mười ph&uacute;t oanh tạc phảng phất chỉ l&agrave; ảo gi&aacute;c đồng dạng.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y sao c&oacute; thể? !&quot;<br />\r\n<br />\r\nNh&igrave;n xem kh&ocirc;ng bị thương ch&uacute;t n&agrave;o Akatsuki Kojou, ph&iacute;a dưới Hải qu&acirc;n c&aacute;c binh sĩ đều muốn đi&ecirc;n rồi.<br />\r\n<br />\r\nNếu như đối phương l&agrave; Tứ Ho&agrave;ng b&ecirc;n trong một c&aacute;i, bọn hắn đều c&oacute; thể tiếp nhận, thế nhưng l&agrave; đối phương r&otilde; r&agrave;ng l&agrave; c&aacute;i cho tới b&acirc;y giờ chưa nghe n&oacute;i qua gia hỏa.<br />\r\n<br />\r\nKh&ocirc;ng chỉ c&oacute; ph&aacute; hủy Seastone chế tạo nh&agrave; giam, liền ngay cả hơn mười ph&uacute;t li&ecirc;n tục kh&ocirc;ng ngừng oanh tạc thế m&agrave; đều kh&ocirc;ng l&agrave;m g&igrave; được gia hỏa n&agrave;y.<br />\r\n<br />\r\nC&aacute;i n&agrave;y chỉ sợ đ&atilde; tho&aacute;t ly nh&acirc;n loại phạm vi.<br />\r\n<br />\r\n&quot;B&igrave;nh thường vũ kh&iacute; khẳng định kh&ocirc;ng tổn thương được t&ecirc;n kia, động thủ đi, loại qu&aacute;i vật n&agrave;y nếu l&agrave; chạy đi, mảnh n&agrave;y bi&ecirc;̉n cả liền thật xảy ra đại sự.&quot;<br />\r\n<br />\r\nGarp h&iacute;t s&acirc;u một hơi, nh&igrave;n ph&iacute;a xa tr&ecirc;n bầu trời đ&atilde; bị bạo tạc hỏa diễm bao phủ th&acirc;n ảnh, lập tức đối b&ecirc;n người ba c&aacute;i Hải qu&acirc;n Đại tướng n&oacute;i ra.<br />\r\n<br />\r\nRocks đ&atilde; chết, hơn nữa thoạt nh&igrave;n, đối phương c&ugrave;ng Rocks quan hệ kh&ocirc;ng ít, một khi l&agrave;m cho đối phương đ&agrave;o tẩu, đối với Hải qu&acirc;n tuyệt đối kh&ocirc;ng phải chuyện tốt.<br />\r\n<br />\r\nGarp n&oacute;i xong.<br />\r\n<br />\r\nChỉ thấy một đạo hỏa diễm ph&oacute;ng l&ecirc;n tận trời.<br />\r\n<br />\r\nAkainu tr&ecirc;n th&acirc;n dung nham lăn lộn, kh&ocirc;ng nh&igrave;n thẳng đạn ph&aacute;o bạo tạc hỏa diễm, dẫn đầu hướng ph&iacute;a Akatsuki Kojou x&ocirc;ng ra.<br />\r\n<br />\r\nAkainu h&oacute;a th&agrave;nh dung nham mang theo cuồn cuộn kh&oacute;i đặc cấp tốc tới gần Akatsuki Kojou.<br />\r\n<br />\r\n&quot;Chẳng cần biết ngươi l&agrave; ai, vi phạm ch&iacute;nh nghĩa liền đi chết đi!&quot;<br />\r\n<br />\r\nAkainu hừ lạnh một tiếng.<br />\r\n<br />\r\nĐối phương đ&atilde; kh&ocirc;ng phải ch&iacute;nh nghĩa đồng bạn, như vậy th&igrave; l&agrave; địch nh&acirc;n, địch nh&acirc;n liền kh&ocirc;ng c&oacute; tất yếu sống tr&ecirc;n c&otilde;i đời n&agrave;y.<br />\r\n<br />\r\nMinh Cẩu!<br />\r\n<br />\r\nAkainu dung nham h&oacute;a c&aacute;nh tay kh&ocirc;ng ch&uacute;t do dự hướng ph&iacute;a Akatsuki Kojou đ&aacute;nh ra.<br />\r\n<br />\r\nPhảng phất như l&agrave; ph&aacute;t xạ đạn ph&aacute;o đồng dạng, Akainu dung nham h&oacute;a c&aacute;nh tay trong nh&aacute;y mắt k&eacute;o d&agrave;i đem dung nham nắm đấm lấy tốc độ cực nhanh đẩy đi ra.<br />\r\n<br />\r\n&quot;Ti&ecirc;u hao c&oacute; chút lớn.&quot;<br />\r\n<br />\r\nAkatsuki Kojou trong l&ograve;ng n&oacute;i nhỏ.<br />\r\n<br />\r\nMắt thấy Akainu c&ocirc;ng k&iacute;ch, hắn cũng kh&ocirc;ng c&oacute; ngăn cản.<br />\r\n<br />\r\nL&uacute;c trước chống cự Minh Vương c&ocirc;ng k&iacute;ch, chỉ chớp mắt đi v&agrave;o t&aacute;m trăm năm về sau, vừa mới lại bộc ph&aacute;t ra si&ecirc;u cấp Cero ph&aacute; hủy Seastone nh&agrave; giam.<br />\r\n<br />\r\nHiện tại Akatsuki Kojou trong cơ thể tinh lực đ&atilde; thiếu nghi&ecirc;m trọng.<br />\r\n<br />\r\n&quot;Thời Kh&ocirc;ng Tr&aacute;i C&acirc;y năng lực giả sao?&quot;<br />\r\n<br />\r\nAkatsuki Kojou hơi suy tư, rất nhanh liền lấy lại tinh thần.<br />\r\n<br />\r\nChỉ l&agrave;, mặc d&ugrave; l&agrave; bị từ t&aacute;m trăm năm trước truyền tống tới.<br />\r\n<br />\r\nNhưng tr&ecirc;n thực tế, đối với Akatsuki Kojou tới n&oacute;i, hết thảy tựa như l&agrave; vừa vặn kinh lịch đồng dạng.<br />\r\n<br />\r\nR&otilde; rệt mới chống cự qua Minh Vương oanh k&iacute;ch, lại đem đại lượng tinh lực độ đưa cho Uranus, Akatsuki Kojou liền trực tiếp bị Thời Kh&ocirc;ng Tr&aacute;i C&acirc;y năng lực giả truyền tống đi.<br />\r\n<br />\r\nVừa mới Cero ph&oacute;ng th&iacute;ch, thậm ch&iacute; l&agrave; Akatsuki Kojou điều động trong cơ thể m&igrave;nh bản nguy&ecirc;n tinh huyết đến thả ra.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, Akatsuki Kojou kh&ocirc;ng chỉ c&oacute; tinh lực lỗ sạch, tinh thần c&ugrave;ng thể lực ti&ecirc;u hao cũng cực kỳ nghi&ecirc;m trọng.<br />\r\n<br />\r\nMặc d&ugrave; như thế, Akatsuki Kojou nhục thể lực ph&ograve;ng ngự c&ugrave;ng bản năng phản ứng y nguy&ecirc;n tồn tại.<br />\r\n<br />\r\nChỉ thấy Akatsuki Kojou thịt tr&ecirc;n người c&aacute;nh chấn động, th&acirc;n h&igrave;nh lập tức lướt ngang ra ngo&agrave;i.<br />\r\n<br />\r\nPhun ra dung nham c&aacute;nh tay cơ hồ l&agrave; d&aacute;n Akatsuki Kojou th&acirc;n thể đảo qua.<br />\r\n<br />\r\nBởi v&igrave; huyết kh&iacute; ti&ecirc;u hao, Akatsuki Kojou một mực kh&ocirc;ng c&oacute; tổn thương huyết sắc qu&yacute; tộc trường b&agrave;o cũng rốt cục xuất hiện tổn hại.<br />\r\n<br />\r\nTr&aacute;nh tho&aacute;t một k&iacute;ch Akatsuki Kojou th&acirc;n h&igrave;nh l&oacute;e l&ecirc;n, trong nh&aacute;y mắt đi tới Akainu trước mặt.<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave;? !&quot;<br />\r\n<br />\r\nAkainu l&uacute;c n&agrave;y một k&iacute;ch mới đ&aacute;nh ra, kh&ocirc;ng nghĩ tới tốc độ của đối phương thế m&agrave; nhanh như vậy, lập tức muốn d&ugrave;ng một c&aacute;i tay kh&aacute;c đ&aacute;nh trả.<br />\r\n<br />\r\nLại đ&atilde; muộn.<br />\r\n<br />\r\nL&uacute;c n&agrave;y Akatsuki Kojou nắm đấm đ&atilde; khắc ở Akainu ngực.<br />\r\n<br />\r\nAkatsuki Kojou lực lượng to&agrave;n th&acirc;n triệu tập, hội tụ đến Akatsuki Kojou tr&ecirc;n nắm tay, sau đ&oacute; th&ocirc;ng qua nắm đấm lại cấp tốc độ v&agrave;o đến Akainu trong ngực.<br />\r\n<br />\r\nHừ lạnh một tiếng</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;C&uacute;t ngay ~!&quot;<br />\r\n<br />\r\nOanh ~!<br />\r\n<br />\r\nPhảng phất đạn ph&aacute;o bạo tạc đồng dạng.<br />\r\n<br />\r\nMột tiếng bạo liệt tiếng vang vang l&ecirc;n.<br />\r\n<br />\r\nLực lượng kinh khủng trong nh&aacute;y mắt tại Akatsuki Kojou nắm đấm c&ugrave;ng Akainu ngực ở giữa bộc ph&aacute;t.<br />\r\n<br />\r\nPhốc ~!<br />\r\n<br />\r\nAkainu ngực lập tức bị c&aacute;i kia cỗ lực lượng kinh khủng đ&aacute;nh xuy&ecirc;n qua, xuất hiện một c&aacute;i động lớn.<br />\r\n<br />\r\nM&agrave; c&aacute;i kia cổ lực lượng cường đại ph&oacute;ng xuất ra như l&agrave; như v&ograve;i rồng quyền k&igrave;nh.<br />\r\n<br />\r\nTại xuy&ecirc;n thấu Akainu ngực về sau, vẫn hướng Akainu hậu phương x&ocirc;ng ra.<br />\r\n<br />\r\nKi&ecirc;n cố kiến tr&uacute;c tại cỗ n&agrave;y kinh khủng quyền k&igrave;nh dưới, trong nh&aacute;y mắt h&oacute;a th&agrave;nh bột mịn.<br />\r\n<br />\r\nLiền ngay cả c&oacute; v&agrave;i chục tầng lầu cao Enies Chi Th&aacute;p đều kh&ocirc;ng có th&ecirc;̉ ngăn cản một l&aacute;t, trực tiếp bị quyền k&igrave;nh lật tung.<br />\r\n<br />\r\nMột đường cọ rửa, m&atilde;i cho đến Judicial Island tối hậu phương c&aacute;i kia to lớn Gates of Justice b&ecirc;n tr&ecirc;n,<br />\r\n<br />\r\nOanh ~!<br />\r\n<br />\r\nPh&aacute;t ra một tiếng chấn động kịch liệt, Gates of Justice ngạnh sinh sinh bị đẩy ra v&agrave;i trăm m&eacute;t mới dừng lại.<br />\r\n<br />\r\nPh&iacute;a dưới đám hải qu&acirc;n nh&igrave;n xem trực tiếp qu&aacute;n xuy&ecirc;n to&agrave;n bộ Judicial Island ph&aacute; hư, chỉ cảm thấy một trận ngạt thở.<br />\r\n<br />\r\nNếu như một quyền n&agrave;y l&agrave; đ&aacute;nh v&agrave;o Hải Qu&acirc;n bản bộ, hoặc l&agrave; Marijoa, c&aacute;i kia tạo th&agrave;nh tổn thất tuyệt đối l&agrave; kh&ocirc;ng c&aacute;ch n&agrave;o lường được,<br />\r\n<br />\r\nAkatsuki Kojou ph&iacute;a trước, Akainu h&oacute;a th&agrave;nh dung nham một trận phun tr&agrave;o, Akainu dung nham ngưng tụ khắp khu&ocirc;n mặt l&agrave; kinh ngạc.<br />\r\n<br />\r\nNếu như vừa mới hắn kh&ocirc;ng c&oacute; đ&uacute;ng l&uacute;c d&ugrave;ng m&igrave;nh nguy&ecirc;n tố h&oacute;a th&acirc;n thể đem ngực bộ phận tr&aacute;nh ra, hiện tại tuyệt đối l&agrave; đ&atilde; chết.<br />\r\n<br />\r\n&quot;Gia hỏa n&agrave;y. . . L&agrave; qu&aacute;i vật sao?&quot;<br />\r\n<br />\r\nKh&ocirc;ng ch&uacute;t do dự.<br />\r\n<br />\r\nPhản ứng tới Akainu phi tốc lao ra ngo&agrave;i, một mực c&ugrave;ng c&ugrave;ng Akatsuki Kojou k&eacute;o ra hơn ng&agrave;n m&eacute;t khoảng c&aacute;ch sau mới dừng lại.</p>', '2021-11-27 21:19:41', 4, 1),
(179, 48, 'Rocks giống nhau năng lực?', '5', '<p>Akainu trong l&ograve;ng cuồng loạn kh&ocirc;ng th&ocirc;i.<br />\r\n<br />\r\nAkatsuki Kojou vừa mới một quyền kia, thậm ch&iacute; cho hắn một loại cả người đều sẽ bị đ&aacute;nh n&aacute;t &aacute;p lực khổng lồ.<br />\r\n<br />\r\n&quot;Muốn chạy trốn?&quot;<br />\r\n<br />\r\nAkatsuki Kojou nh&igrave;n xem Akainu cực tốc lui đến, lộ ra một v&ograve;ng dị dạng tiếu dung.<br />\r\n<br />\r\nL&agrave;m Hấp Huyết Quỷ tr&aacute;i c&acirc;y năng lực giả, hắn c&oacute; thể th&ocirc;ng qua hấp thụ huyết dịch của người kh&aacute;c, từ đ&oacute; thu hoạch được lực lượng của đối phương thậm ch&iacute; tuổi thọ.<br />\r\n<br />\r\nNhưng Đại Tướng cấp bậc cường giả, cho d&ugrave; l&agrave; tại t&aacute;m trăm năm trước cũng rất &iacute;t gặp.<br />\r\n<br />\r\nHiện tại Akatsuki Kojou trong cơ thể tinh lực đ&atilde; hao hết, nếu như c&oacute; thể thừa cơ xử l&yacute; một c&aacute;i Đại tướng, kh&ocirc;ng chỉ c&oacute; thể suy yếu địch nh&acirc;n một c&aacute;i cường đại chiến lực, hơn nữa c&ograve;n c&oacute; thể thừa cơ hấp thu Đại tướng tinh lực đến bổ sung l&agrave;m bản th&acirc;n lớn mạnh.<br />\r\n<br />\r\nNhưng m&agrave;, Akatsuki Kojou c&ograve;n chưa kịp truy k&iacute;ch.<br />\r\n<br />\r\nSưu sưu sưu! ! !<br />\r\n<br />\r\nD&agrave;y đặc tiếng x&eacute; gi&oacute; bỗng nhi&ecirc;n vang l&ecirc;n.<br />\r\n<br />\r\nChỉ thấy kh&iacute;a cạnh, từng đạo m&agrave;u v&agrave;ng quang đạn c&ograve;n như mưa rơi một dạng phi tốc ph&oacute;ng tới.<br />\r\n<br />\r\n&quot;Pika Pika no Mi sao?&quot;<br />\r\n<br />\r\nAkatsuki Kojou l&ocirc;ng m&agrave;y nh&iacute;u lại, chỉ c&oacute; thể vỗ c&aacute;nh đ&igrave;nh chỉ truy k&iacute;ch.<br />\r\n<br />\r\nM&agrave; ở ph&iacute;a xa, Garp c&ugrave;ng Aokiji cũng ngay đầu ti&ecirc;n lập tức vọt tới Akainu b&ecirc;n người.<br />\r\n<br />\r\n&quot;Đ&aacute;ng tiếc.&quot;<br />\r\n<br />\r\nAkatsuki Kojou l&acirc;̉m b&acirc;̉m một tiếng.<br />\r\n<br />\r\n&quot;Sakazuki, ngươi kh&ocirc;ng sao chứ?&quot;<br />\r\n<br />\r\nGarp k&ecirc;u một tiếng, &aacute;nh mắt mọi người tr&ocirc;ng lại, hiển nhi&ecirc;n kh&ocirc;ng chỉ l&agrave; lo lắng hắn đơn giản như vậy.<br />\r\n<br />\r\nAkainu cắn bập bẹ, mặc d&ugrave; kh&ocirc;ng muốn thừa nhận, nhưng l&agrave; m&igrave;nh ho&agrave;n to&agrave;n ch&iacute;nh x&aacute;c kh&ocirc;ng phải đối thủ của đối phương.<br />\r\n<br />\r\nNgột ngạt kh&ocirc;ng cam l&ograve;ng thanh &acirc;m từ Akainu trong kẽ răng gạt ra: &quot;T&ecirc;n kia rất mạnh, lực lượng của hắn đơn giản c&ugrave;ng R&acirc;u Trắng qu&aacute;i vật kia kh&ocirc;ng sai biệt lắm. . .&quot;<br />\r\n<br />\r\nL&agrave;m Hải qu&acirc;n Đại tướng, tự nhi&ecirc;n l&agrave; c&ugrave;ng l&agrave;m Tứ Ho&agrave;ng R&acirc;u Trắng từng c&oacute; giao thủ, hắn c&oacute; thể khẳng định, vừa mới một quyền kia uy lực, chỉ từ lực lượng tới n&oacute;i tuyệt đối kh&ocirc;ng so R&acirc;u Trắng phải k&eacute;m.<br />\r\n<br />\r\n&quot;C&oacute; thể so với R&acirc;u Trắng lực lượng? ! .&quot;<br />\r\n<br />\r\nNghe được Akainu lời n&oacute;i, người ở chỗ n&agrave;y vốn l&agrave; chấn động trong l&ograve;ng.<br />\r\n<br />\r\nR&acirc;u Trắng l&agrave;m thế giới mới bốn c&aacute;i tr&ecirc;n biển Ho&agrave;ng Đế thứ nhất, bao qu&aacute;t c&aacute;i kh&aacute;c ba vị, mỗi một c&aacute;i đều c&oacute; để Hải Qu&acirc;n bản bộ kh&ocirc;ng thể kh&ocirc;ng thận trọng đối đ&atilde;i lực lượng đ&aacute;ng sợ.<br />\r\n<br />\r\nM&agrave; b&acirc;y giờ bỗng nhi&ecirc;n xuất hiện gia hỏa, thế m&agrave; cũng c&oacute; được lực lượng như vậy?<br />\r\n<br />\r\nBất qu&aacute;, khi nh&igrave;n lướt qua chung quanh diện t&iacute;ch đ&atilde; thiếu một nửa Judicial Island, trong l&ograve;ng mọi người liền kh&ocirc;ng c&oacute; ho&agrave;i nghi.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, Akainu đ&atilde; từ trong thất thần kh&ocirc;i phục lại, lần nữa đứng thẳng tắp.<br />\r\n<br />\r\n&quot;C&ugrave;ng l&ecirc;n đi, ch&uacute;ng ta đồng loạt ra tay, d&ugrave; l&agrave; gia hỏa n&agrave;y thật l&agrave; R&acirc;u Trắng, cũng kh&ocirc;ng thể n&agrave;o l&agrave; đối thủ của ch&uacute;ng ta!&quot;<br />\r\n<br />\r\nAkainu nghi&ecirc;m vừa n&oacute;i n&oacute;i.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nN&oacute;i xong, liền xuất thủ lần nữa.<br />\r\n<br />\r\nChỉ thấy Akainu tr&ecirc;n tay lần nữa kh&oacute;i đặc lăn lộn, một c&aacute;i to lớn dung nham cự quyền cuốn l&ecirc;n lấy cuồn cuộn kh&oacute;i đặc cấp tốc bay ra.<br />\r\n<br />\r\nĐại Phun Lửa!<br />\r\n<br />\r\nTại to lớn dung nham nắm đấm dưới, hết thảy đều trở n&ecirc;n nhỏ b&eacute; như vậy.<br />\r\n<br />\r\nSo sánh c&ugrave;ng nhau, Akatsuki Kojou th&acirc;n ảnh c&agrave;ng l&agrave; như l&agrave; kiến h&ocirc;i.<br />\r\n<br />\r\nXa xa Kizaru nh&igrave;n thấy Akainu lần nữa tiến c&ocirc;ng, lập tức bắt đầu phối hợp Akainu, huyền lập trong hư kh&ocirc;ng hai tay vừa bấm, m&agrave;u v&agrave;ng &aacute;nh s&aacute;ng lấp l&oacute;e.<br />\r\n<br />\r\nYasakani no Magatama!<br />\r\n<br />\r\nTrong nh&aacute;y mắt, vừa mới chặn đường Akatsuki Kojou những c&aacute;i kia quang đạn lần nữa hiển hiện.<br />\r\n<br />\r\nSưu!<br />\r\n<br />\r\nSưu!<br />\r\n<br />\r\nD&agrave;y đặc quang đạn trong nh&aacute;y mắt muốn đem Akatsuki Kojou bao phủ.<br />\r\n<br />\r\n&quot;Đ&acirc;y ch&iacute;nh l&agrave; đối đ&atilde;i tiền bối nghi thức hoan ngh&ecirc;nh sao?&quot;<br />\r\n<br />\r\nAkatsuki Kojou khẽ cười một tiếng, đương nhi&ecirc;n sẽ kh&ocirc;ng ngạnh kh&aacute;ng, hai c&aacute;nh một trận, phi tốc tr&aacute;nh ra hai người c&ocirc;ng k&iacute;ch.<br />\r\n<br />\r\nBất qu&aacute; tiếp theo một c&aacute;i chớp mắt.<br />\r\n<br />\r\nAkatsuki Kojou lập tức đ&atilde; nhận ra ph&iacute;a dưới một v&ograve;ng h&agrave;n &yacute; đ&aacute;nh tới.<br />\r\n<br />\r\nẦm ầm!<br />\r\n<br />\r\nNhiệt độ chợt hạ.<br />\r\n<br />\r\nChỉ thấy một đạo Iceburg tr&ecirc;n mặt đất phi tốc d&acirc;ng l&ecirc;n, liền như l&agrave; một đầu m&agrave;u bạc trắng x&uacute;c tu đồng dạng phi tốc hướng ph&iacute;a Akatsuki Kojou &quot;Bắt&quot; đến.<br />\r\n<br />\r\nAkatsuki Kojou tr&ecirc;n lưng c&aacute;nh thịt chấn động, th&acirc;n thể bị đẩy giống như một đạo thiểm điện tr&aacute;nh tho&aacute;t Aokiji đ&aacute;nh l&eacute;n.<br />\r\n<br />\r\nKịch liệt trong chiến đấu.<br />\r\n<br />\r\nBa Đại tướng cũng kh&ocirc;ng biết Akatsuki Kojou tinh lực đ&atilde; hao hết, kh&ocirc;ng thể tại ph&aacute;t động vừa rồi những c&aacute;i kia kinh khủng c&ocirc;ng k&iacute;ch.<br />\r\n<br />\r\nBất qu&aacute; nghĩ đến Akainu cơ hồ k&eacute;m ch&uacute;t bị miểu s&aacute;t, ba người đều ăn &yacute; sử dụng nguy&ecirc;n tố c&ocirc;ng k&iacute;ch, ai cũng kh&ocirc;ng tiếp tục t&ugrave;y tiện x&ocirc;ng l&ecirc;n.<br />\r\n<br />\r\nAkatsuki Kojou li&ecirc;n tục tr&aacute;nh n&eacute; mấy lần c&ocirc;ng k&iacute;ch, nhướng m&agrave;y.<br />\r\n<br />\r\n&quot;Tiếp tục như vậy nữa, thể lực muốn ti&ecirc;u hao kh&ocirc;ng sai biệt lắm. . .&quot;<br />\r\n<br />\r\nAkatsuki Kojou thầm nghĩ trong l&ograve;ng một tiếng.<br />\r\n<br />\r\nHiện tại th&acirc;n thể của hắn đang đứng ở phụ tải trạng th&aacute;i, ba Đại tướng li&ecirc;n tục kh&ocirc;ng ngừng tiến c&ocirc;ng, liền l&agrave; tại l&agrave;m hao m&ograve;n hắn lực lượng cuối c&ugrave;ng.<br />\r\n<br />\r\nNếu như thể lực ti&ecirc;u hao qu&aacute; mức nghi&ecirc;m trọng, v&igrave; duy tr&igrave; cỗ n&agrave;y vĩnh sinh chi thể, kh&ocirc;ng chỉ c&oacute; cần đại lượng phẩm chất cao tinh lực kh&ocirc;i phục &agrave;, khả năng c&ograve;n muốn ngủ say kh&ocirc;ng &iacute;t thời gian.<br />\r\n<br />\r\nĐương nhi&ecirc;n, điều kiện ti&ecirc;n quyết l&agrave; kh&ocirc;ng sử dụng Hougyoku lực lượng.<br />\r\n<br />\r\nChỉ l&agrave;, kh&ocirc;ng phải vạn bất đắc dĩ, Akatsuki Kojou cũng kh&ocirc;ng t&iacute;nh tuỳ tiện vận dụng Hougyoku lực lượng.<br />\r\n<br />\r\nNửa thức tỉnh Hougyoku, vốn c&oacute; lực lượng qu&aacute; mức biến đổi thất thường, cho d&ugrave; l&agrave; Akatsuki Kojou, cũng kh&ocirc;ng c&oacute; nắm chắc có th&ecirc;̉ ho&agrave;n to&agrave;n chưởng khống Hougyoku lực lượng.<br />\r\n<br />\r\n&quot;Bất qu&aacute;. . .&quot;<br />\r\n<br />\r\nAkatsuki Kojou &aacute;nh mắt cấp tốc rơi xuống ph&iacute;a dưới những c&aacute;i kia Hải qu&acirc;n tr&ecirc;n th&acirc;n, m&agrave;u đỏ tươi trong đ&ocirc;i mắt hiện l&ecirc;n một v&ograve;ng t&agrave; &yacute; quang mang.<br />\r\n<br />\r\nSưu!<br />\r\n<br />\r\nThế l&agrave;, lần nữa tr&aacute;nh tho&aacute;t Akainu dung nham tr&ugrave;ng k&iacute;ch.<br />\r\n<br />\r\nAkatsuki Kojou th&acirc;n h&igrave;nh nhất chuyển, trực tiếp hướng ph&iacute;a dưới c&uacute;i vọt xuống dưới.<br />\r\n<br />\r\n&quot;Qu&aacute;i vật kia xuống?&quot;<br />\r\n<br />\r\n&quot;C&oacute; phải hay kh&ocirc;ng lực lượng hao hết?&quot;<br />\r\n<br />\r\nNh&igrave;n xem Akatsuki Kojou động t&aacute;c, đám hải qu&acirc;n đều l&agrave; sững sờ.<br />\r\n<br />\r\nĐối phương c&oacute; thể lưu lại l&acirc;u d&agrave;i tr&ecirc;n kh&ocirc;ng trung, đồng thời tự do hoạt động, r&otilde; r&agrave;ng ở tr&ecirc;n trời c&agrave;ng có hơn ưu thế, kh&ocirc;ng minh bạch tại sao phải rơi xuống.<br />\r\n<br />\r\nGarp ngược lại l&agrave; nghĩ đến c&aacute;i g&igrave;, lập tức biến sắc.<br />\r\n<br />\r\n&quot;Kuzan, mau ngăn cản hắn.&quot;<br />\r\n<br />\r\n&quot;Tất cả Hải qu&acirc;n, mau tr&aacute;nh ra! Lập tức rời đi nơi n&agrave;y!&quot;<br />\r\n<br />\r\nGarp lập tức k&ecirc;u lớn.<br />\r\n<br />\r\nAokiji lập tức ph&aacute;t động năng lực.<br />\r\n<br />\r\nBất qu&aacute; đ&atilde; muộn.<br />\r\n<br />\r\nAkatsuki Kojou nhẹ nh&otilde;m v&ograve;ng qua Aokiji một lần chặn đường về sau, liền đ&atilde; rơi v&agrave;o Hải qu&acirc;n trong đ&aacute;m người.<br />\r\n<br />\r\n&quot;T&ecirc;n kia tại ch&uacute;ng ta trong đ&aacute;m người, kh&ocirc;ng c&oacute; biện ph&aacute;p.&quot;<br />\r\n<br />\r\nAokiji nh&igrave;n xem đ&aacute;m người xa xa, nh&uacute;n vai, b&igrave;nh tĩnh n&oacute;i một c&acirc;u.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, những c&aacute;i kia Hải qu&acirc;n c&ograve;n kh&ocirc;ng biết m&igrave;nh gặp phải l&agrave; dạng g&igrave; kinh khủng tồn tại.<br />\r\n<br />\r\n&quot;Gia hỏa n&agrave;y rơi xuống, giết hắn!&quot;<br />\r\n<br />\r\nMột c&aacute;i Hải qu&acirc;n tướng lĩnh r&uacute;t ra bội kiếm, lập tức hạ lệnh n&oacute;i ra.<br />\r\n<br />\r\nChung quanh Hải qu&acirc;n lập tức giơ l&ecirc;n trong tay vũ kh&iacute;.<br />\r\n<br />\r\nAkatsuki Kojou nh&igrave;n lướt qua chung quanh, trong mắt nổi l&ecirc;n y&ecirc;u dị huyết sắc quang mang.<br />\r\n<br />\r\nTh&acirc;n h&igrave;nh l&oacute;e l&ecirc;n, trong nh&aacute;y mắt đi tới một c&aacute;i c&aacute;i kia Hải qu&acirc;n tướng lĩnh trước người.<br />\r\n<br />\r\nNh&igrave;n xem trước mặt bệnh trạng trắng n&otilde;n khu&ocirc;n mặt.<br />\r\n<br />\r\nC&aacute;i kia tướng lĩnh giật m&igrave;nh, đồng thời, cảm gi&aacute;c ngực đau x&oacute;t.<br />\r\n<br />\r\nC&uacute;i đầu nh&igrave;n lại, ph&aacute;t hiện m&igrave;nh đ&atilde; bị bị b&agrave;n tay của đối phương xuy&ecirc;n thủng ngực.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nC&aacute;i kia tướng lĩnh kh&ocirc;ng c&oacute; Akainu như thế nguy&ecirc;n tố h&oacute;a đến chủ động l&aacute;ch qua yếu hại năng lực, tại chỗ tử vong.<br />\r\n<br />\r\nM&agrave; t&ecirc;n kia Hải qu&acirc;n tướng lĩnh m&aacute;u tươi phảng phất nhận đến dẫn dắt, cấp tốc hội tụ đến Akatsuki Kojou l&ograve;ng b&agrave;n tay bị hấp thu.<br />\r\n<br />\r\nHấp thu một người huy&ecirc;́t dịch, Akatsuki Kojou thể lực c&ugrave;ng tinh lực lập tức kh&ocirc;i phục mấy phần.<br />\r\n<br />\r\nAkatsuki Kojou kh&oacute;e miệng khẽ nhếch.<br />\r\n<br />\r\nRoi m&aacute;u!<br />\r\n<br />\r\nNg&oacute;n tay kh&eacute;p lại, ng&oacute;n trỏ c&ugrave;ng ng&oacute;n giữa ở giữa tinh lực hội tụ, trong nh&aacute;y mắt ngưng tụ ra một c&acirc;y từ Haki quấn quanh huyết sắc trường ti&ecirc;n.<br />\r\n<br />\r\nTiện tay vung l&ecirc;n.<br />\r\n<br />\r\nHuyết sắc đường v&ograve;ng cung lập tức đảo qua chung quanh.<br />\r\n<br />\r\nPhốc phốc! !<br />\r\n<br />\r\nPhốc phốc! !<br />\r\n<br />\r\nPhốc phốc! !<br />\r\n<br />\r\nLiền như l&agrave; lưỡi dao cắt ch&eacute;m qua đồng dạng.<br />\r\n<br />\r\nTr&ecirc;n trăm t&ecirc;n Hải qu&acirc;n bị ch&eacute;m ngang lưng, bị mất mạng tại chỗ.<br />\r\n<br />\r\nNồng đậm tinh lực trong nh&aacute;y mắt tr&agrave;n v&agrave;o Akatsuki Kojou trong cơ thể.<br />\r\n<br />\r\nTrong nh&aacute;y mắt.<br />\r\n<br />\r\nAkatsuki Kojou th&acirc;n thể liền như l&agrave; bị chen l&agrave;m lượng nước bọt biển bỗng nhi&ecirc;n đạt được bổ sung đồng dạng.<br />\r\n<br />\r\nChỉ thấy Akatsuki Kojou bệnh trạng da thịt trắng n&otilde;n ph&iacute;a tr&ecirc;n nhiều hơn mấy phần hồng nhuận phơn phớt.<br />\r\n<br />\r\nGia hỏa n&agrave;y, đang hấp thu huy&ecirc;́t dịch kh&ocirc;i phục lực lượng! !<br />\r\n<br />\r\nNh&igrave;n xem một m&agrave;n n&agrave;y, tất cả mọi người nhịn kh&ocirc;ng được c&oacute; ch&uacute;t r&ugrave;ng m&igrave;nh.<br />\r\n<br />\r\n&quot;Đ&acirc;y cũng l&agrave; Tr&aacute;i &Aacute;c Quỷ năng lực sao? Thật đ&aacute;ng sợ đ&acirc;u!&quot; Kizaru hơi kinh ngạc l&ecirc;n tiếng.<br />\r\n<br />\r\nMột b&ecirc;n Garp, x&aacute;c thực sắc mặt trở n&ecirc;n khiếp sợ kh&ocirc;ng g&igrave; s&aacute;nh nổi.<br />\r\n<br />\r\nBởi v&igrave; trước mắt một m&agrave;n n&agrave;y, để hắn kh&ocirc;ng khỏi hồi tưởng lại mấy chục năm trước tại Thần Chi Cốc một m&agrave;n kia.<br />\r\n<br />\r\nHồi tưởng lại nam nh&acirc;n kia!<br />\r\n<br />\r\nC&aacute;i kia đồng dạng c&oacute; thể hấp thu người huy&ecirc;́t dịch!<br />\r\n<br />\r\nĐược xưng l&agrave; tr&ecirc;n biển b&aacute; chủ nam nh&acirc;n, Rocks!<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y sao c&oacute; thể?&quot;</p>', '2021-11-27 21:20:15', 8, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(180, 49, 'Thiếu chủ', '1', '<p>&#39;Ai, qu&aacute; kh&oacute; khăn.&#39;<br />\r\n<br />\r\nBờ s&ocirc;ng thiếu ni&ecirc;n nh&igrave;n xem trong khe nước đảo ảnh non nớt gương mặt, nh&igrave;n xem kia tạo n&ecirc;n nhỏ b&eacute; l&agrave;n s&oacute;ng xanh thẳm bầu trời.<br />\r\n<br />\r\nGiang hai tay ra, thiếu ni&ecirc;n c&oacute; ch&uacute;t th&acirc;n thể gầy yếu chậm r&atilde;i t&eacute; nằm cạn non b&atilde;i cỏ xanh biếc b&ecirc;n tr&ecirc;n, đưa tay muốn đi chạm đến bầu trời, lại v&ocirc; lực chỗ trượt xuống.<br />\r\n<br />\r\nĐi v&agrave;o c&aacute;i n&agrave;y Man Hoang thế giới đ&atilde; mười hai năm.<br />\r\n<br />\r\nMười hai năm!<br />\r\n<br />\r\nBiết hắn c&aacute;i n&agrave;y mười hai năm l&agrave; thế n&agrave;o qua sao!<br />\r\n<br />\r\nHắn!<br />\r\n<br />\r\n&quot;Thiếu chủ!&quot;<br />\r\n<br />\r\nB&ecirc;n đột nhi&ecirc;n truyền đến một tiếng th&ocirc; kệch g&agrave;o to.<br />\r\n<br />\r\nD&ugrave;ng thiếu ni&ecirc;n n&agrave;y nằm ngửa b&ecirc;n d&ograve;ng suối l&agrave; chấm tr&ograve;n phạm vi trăm trượng, rất nhiều ngồi cưỡi băng lam l&ocirc;ng d&agrave;i Cự Lang tr&aacute;ng h&aacute;n đang tới hồi trở lại tuần s&aacute;t.<br />\r\n<br />\r\nMột vị mặc gi&aacute;p da, kh&ocirc;i ng&ocirc; như g&acirc;́u đại th&uacute;c xem thiếu ni&ecirc;n đổ xuống, d&ugrave;ng ch&iacute;nh m&igrave;nh lớn giọng lo lắng chỗ hỏi thăm:<br />\r\n<br />\r\n&quot;Ng&agrave;i kh&ocirc;ng c&oacute; sao chứ Thiếu chủ! &quot;<br />\r\n<br />\r\nThiếu ni&ecirc;n v&ocirc; lực kho&aacute;t kho&aacute;t tay, hữu kh&iacute; v&ocirc; lực trở về c&acirc;u:<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; việc g&igrave;, để cho ta nghỉ ngơi một hồi.&quot;<br />\r\n<br />\r\nVừa định b&aacute;n c&aacute;i thảm, v&ocirc; t&igrave;nh bị vạch trần.<br />\r\n<br />\r\nCó th&ecirc;̉, hắn th&acirc;n l&agrave; Đan Hồ sơn chi bắc thập đại thị tộc một trong H&ugrave;ng B&atilde;o tộc c&oacute; được duy nhất tộc trưởng quyền kế thừa Thiếu chủ, tựu rất vui vẻ sao<br />\r\n<br />\r\nNơi n&agrave;y kh&ocirc;ng c&oacute; mạng, kh&ocirc;ng c&oacute; điện tử giải tr&iacute;, thậm ch&iacute; đều kh&ocirc;ng c&oacute; mấy quyển giấy chất thư tịch!<br />\r\n<br />\r\nHắn coi như th&acirc;n l&agrave; Thiếu chủ, c&ograve;n nhiều, rất nhiều chảy nước mũi, mặc cũ n&aacute;t gi&aacute;p da tiểu t&ugrave;y t&ugrave;ng, liền mấy c&aacute;i có th&ecirc;̉ ban đ&ecirc;m t&acirc;m sự bu&ocirc;ng lỏng xinh đẹp mềm mại tiểu thị nữ đều kh&ocirc;ng c&oacute;!<br />\r\n<br />\r\nNg&ocirc; Vọng ho&agrave;n to&agrave;n kh&ocirc;ng biết, l&atilde;o thi&ecirc;n gia để hắn mang theo đời trước kia thế gian phồn hoa k&yacute; ức sống th&ecirc;m một lần, đến c&ugrave;ng l&agrave; ban thưởng vẫn l&agrave; trừng phạt.<br />\r\n<br />\r\nKỳ thật người giải tr&iacute; phương diện trống rỗng c&ograve;n tốt, nhiều một ch&uacute;t thời gian th&iacute;ch ứng, ch&iacute;nh m&igrave;nh cũng kh&ocirc;ng phải kh&ocirc;ng chịu nổi tịch mịch.<br />\r\n<br />\r\nC&agrave;ng lớn tra tấn, c&ograve;n tại nam nữ điểm n&agrave;y sự t&igrave;nh b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nMặc d&ugrave; thị tộc b&ecirc;n trong theo Thủ Lĩnh đến Tiểu Binh đều l&agrave; một chồng một vợ, nhưng bởi v&igrave; l&agrave; Thượng Cổ thời đại, tất cả mọi người so s&aacute;nh mở ra, th&agrave;nh h&ocirc;n trước tuổi trẻ nam nữ bọn họ, đều c&oacute; thể nhiệt t&igrave;nh kh&ocirc;ng bị cản trở tự do y&ecirc;u đương.<br />\r\n<br />\r\nThật tự do y&ecirc;u đương.<br />\r\n<br />\r\nBờ s&ocirc;ng bụi cỏ lau, b&ecirc;n cạnh ngọn n&uacute;i rừng c&acirc;y nhỏ, du mục chỗ cung cấp d&acirc;n chăn nu&ocirc;i dừng ch&acirc;n ph&ograve;ng nhỏ, ph&iacute;a dưới n&uacute;i tuyết sưởi ấm băng ph&ograve;ng, đều c&oacute; thể đơn giản t&igrave;m tới chưa th&agrave;nh h&ocirc;n nam nữ rơi thanh xu&acirc;n hiện trường h&igrave;nh tượng.<br />\r\n<br />\r\nNgược lại l&agrave; th&agrave;nh h&ocirc;n về sau, mọi người sẽ muốn cầu lẫn nhau to&agrave;n t&acirc;m to&agrave;n &yacute;, d&ugrave; sao đều c&oacute; nặng hơn sinh tồn &aacute;p lực c&ugrave;ng t&igrave;nh cảm nhu cầu.<br />\r\n<br />\r\nSinh hoạt, v&ocirc; luận l&agrave; đời trước chỗ Lam Tinh, vẫn l&agrave; đời n&agrave;y chỗ Đại Hoang, đều kh&ocirc;ng phải l&agrave; một chuyện dễ d&agrave;ng sự t&igrave;nh.<br />\r\n<br />\r\nTrong tộc mỗi lần ba năm hội (sẽ) tổ chức một lần Đại H&ocirc;n hội, tuổi trẻ lại chưa th&agrave;nh gia c&aacute;c nữ nh&acirc;n, hội (sẽ) xuất ra tỉ mỉ chuẩn bị gậy gỗ, xương gậy, thạch c&ocirc;n, Lang Nha bổng, t&igrave;m tới ngưỡng mộ trong l&ograve;ng nam tử, lặng lẽ sờ l&ecirc;n<br />\r\n<br />\r\nCho bọn hắn c&aacute;i &oacute;t một gậy!<br />\r\n<br />\r\nĐ&aacute;nh ra đến thanh &acirc;m vượt gi&ograve;n, đại biểu c&aacute;i n&agrave;y nam tử xương đầu phẩm chất vượt ưu đẳng.<br />\r\n<br />\r\nĐương nhi&ecirc;n, nghe &acirc;m thanh kh&ocirc;ng phải trọng điểm.<br />\r\n<br />\r\nNhững nữ nh&acirc;n n&agrave;y sẽ đem nam nh&acirc;n g&aacute;nh tại tr&ecirc;n vai n&eacute;m hồi trở lại chỗ ở của m&igrave;nh, đi qua một đ&ecirc;m vất vả hợp t&aacute;c, s&aacute;ng sớm h&ocirc;m sau c&ugrave;ng nhau đi ra khỏi ph&ograve;ng, tựu tự h&agrave;nh kết th&agrave;nh vợ chồng.<br />\r\n<br />\r\nThuần t&uacute;y kết h&ocirc;n, theo một gậy bắt đầu.<br />\r\n<br />\r\nNhất định phải nhấn mạnh l&agrave;, bọn hắn H&ugrave;ng B&atilde;o tộc mặc d&ugrave; phổ biến khờ một ch&uacute;t, nhưng cũng sẽ kh&ocirc;ng cường g&otilde; cường khi&ecirc;ng.<br />\r\n<br />\r\nC&aacute;i n&agrave;y tập tục lưu truyền nhiều năm như vậy, hiện tại nam nữ song phương phần lớn l&agrave; sớm c&oacute; &#39;Hợp t&aacute;c đồng bạn&#39;, chờ đến Đại hội thời điểm tập thể đi c&aacute;i qu&aacute; tr&igrave;nh.<br />\r\n<br />\r\nLoại kia thuần t&uacute;y dựa theo Cổ lễ, tr&ecirc;n đại hội xem thuận mắt tựu g&otilde; bất tỉnh v&aacute;c đi t&igrave;nh h&igrave;nh mặc d&ugrave; thường c&oacute; ph&aacute;t sinh, nhưng nh&agrave; trai phần lớn hội (sẽ) nửa đ&ecirc;m liền chạy.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nNg&ocirc; Vọng nghi&ecirc;m trọng ho&agrave;i nghi, trong tộc cưới sau nam t&iacute;nh phần lớn l&agrave; ch&uacute;t &iacute;t tứ chi ph&aacute;t triển h&agrave;m h&agrave;m nguy&ecirc;n nh&acirc;n chủ yếu, liền l&agrave; th&agrave;nh h&ocirc;n thời điểm kh&ocirc;ng c&oacute; chịu lại ch&iacute;nh m&igrave;nh phu nh&acirc;n kia một gậy!<br />\r\n<br />\r\nĐương nhi&ecirc;n, hắn l&agrave; Thiếu chủ, mặc d&ugrave; kh&ocirc;ng c&oacute; Bạch M&atilde; chỉ c&oacute; Bạch Lang c&oacute; thể cưỡi, nhưng nghi&ecirc;m chỉnh m&agrave; n&oacute;i cũng l&agrave; Vương Tử.<br />\r\n<br />\r\nVề sau đại h&ocirc;n chỉ l&agrave; tượng trưng chỗ bị ch&iacute;nh m&igrave;nh người y&ecirc;u g&otilde; tho&aacute;ng c&aacute;i, c&oacute; thể trực tiếp giả h&ocirc;n m&ecirc;.<br />\r\n<br />\r\nMỹ lệ mẫu th&acirc;n l&agrave; như thế tự an ủi m&igrave;nh.<br />\r\n<br />\r\nCó th&ecirc;̉, có th&ecirc;̉<br />\r\n<br />\r\n&quot;Ai.&quot;<br />\r\n<br />\r\nThiếu ni&ecirc;n c&aacute;i tr&aacute;n treo đầy hắc tuyến, mu b&agrave;n tay che chắn tại tr&ecirc;n tr&aacute;n, c&aacute;c loại đề kh&ocirc;ng nổi tinh thần.<br />\r\n<br />\r\nTh&acirc;n thể của m&igrave;nh r&otilde; r&agrave;ng kh&ocirc;ng c&oacute; vấn đề, t&acirc;m l&yacute; cũng kh&ocirc;ng c&oacute; g&igrave; mao bệnh, lấy hướng cũng đ&uacute;ng quy đ&uacute;ng củ, thường xuy&ecirc;n c&ograve;n c&oacute; một số mi&ecirc;n man bất định tuổi dậy th&igrave; huyễn tưởng, l&agrave;m sao lại hội (sẽ) nhiễm l&ecirc;n thứ qu&aacute;i bệnh n&agrave;y<br />\r\n<br />\r\nL&agrave; bởi v&igrave; ba tuổi năm đ&oacute;, nghe được một đ&aacute;m a di đại mụ thảo luận giản dị Lang Nha bổng phương ph&aacute;p luyện chế đưa đến sao<br />\r\n<br />\r\nC&ograve;n l&agrave; bởi v&igrave; đời trước đ&ugrave;a qu&aacute; n&agrave;y, để cho m&igrave;nh sống lại một lần l&atilde;o thi&ecirc;n gia cố &yacute; l&agrave;m ch&iacute;nh m&igrave;nh t&acirc;m t&iacute;nh<br />\r\n<br />\r\nL&agrave;m sao lại sẽ như vậy<br />\r\n<br />\r\n&#39;Ng&ocirc; Vọng a Ng&ocirc; Vọng, ngươi đ&acirc;y rốt cuộc l&agrave; tạo c&aacute;i g&igrave; nghiệt!&#39;<br />\r\n<br />\r\nĐược th&ocirc;i, Đại Hoang thế giới kh&ocirc;ng thiếu c&aacute;i lạ, hắn vừa vặn liền l&agrave; một c&aacute;i tiểu &#39;Kỳ&#39; điểm.<br />\r\n<br />\r\nBảy t&aacute;m tuổi trước đ&oacute; Ng&ocirc; Vọng, chỉ muốn tự m&igrave;nh l&agrave;m c&aacute;i b&igrave;nh thường cường tr&aacute;ng H&ugrave;ng B&atilde;o tộc Thiếu chủ, kh&ocirc;ng cần c&aacute;i g&igrave; xuất ch&uacute;ng t&agrave;i hoa, cũng kh&ocirc;ng c&oacute; g&igrave; b&agrave;nh trướng d&atilde; t&acirc;m, hiểu được có th&ecirc;̉ thường thường vững v&agrave;ng sống qua mấy trăm năm buồn tẻ nh&acirc;n sinh, c&aacute;i n&agrave;y đủ.<br />\r\n<br />\r\nĐại Hoang Nh&acirc;n tộc tuổi thọ l&agrave; ba đến năm trăm tuổi.<br />\r\n<br />\r\nThời điểm đ&oacute; Ng&ocirc; Vọng, vẫn lu&ocirc;n rất vui vẻ, cũng rất ch&uacute; &yacute; bảo dưỡng sau g&aacute;y của m&igrave;nh x&aacute;c, để n&oacute; có th&ecirc;̉ tiếp nhận sinh hoạt c&agrave;ng nhiều đ&aacute;nh đập.<br />\r\n<br />\r\nĐợi đến bảy t&aacute;m tuổi l&uacute;c, hết thảy đều ph&aacute;t sinh biến h&oacute;a.<br />\r\n<br />\r\nC&aacute;i kia qu&aacute;i bệnh kh&ocirc;ng c&oacute; dấu hiệu n&agrave;o xuất hiện ở ch&iacute;nh m&igrave;nh th&acirc;n<br />\r\n<br />\r\n&Ocirc;<br />\r\n<br />\r\nNơi ch&acirc;n trời xa đột nhi&ecirc;n truyền đến trầm thấp tiếng k&egrave;n.<br />\r\n<br />\r\nNg&ocirc; Vọng đ&aacute;y l&ograve;ng dừng lại chửi bậy, một c&aacute;i giật m&igrave;nh trở m&igrave;nh nhảy dựng l&ecirc;n, hướng ph&iacute;a tiếng k&egrave;n truyền đến chỗ nh&igrave;n ra xa, xa xa gặp được một ch&ugrave;m thẳng tắp Lang Y&ecirc;n.<br />\r\n<br />\r\nC&aacute;i n&agrave;y Đại Hoang cũng kh&ocirc;ng th&aacute;i b&igrave;nh, Nh&acirc;n tộc cũng kh&ocirc;ng phải c&aacute;i g&igrave; thi&ecirc;n địa Ch&uacute;a tể.<br />\r\n<br />\r\nTương phản, bị Ti&ecirc;n Thi&ecirc;n Đại Thần Nữ Oa Tạo H&oacute;a ra Nh&acirc;n tộc, tại c&aacute;i n&agrave;y t&ecirc;n l&agrave; Đại Hoang Thi&ecirc;n Địa ở giữa chỗ dựa so s&aacute;nh V&atilde;n Thương sơn chi bắc mảnh n&agrave;y rộng lớn tr&ecirc;n thảo nguy&ecirc;n, thập đại thị tộc chỉ c&oacute; hai nh&agrave; l&agrave; d&ugrave;ng Nh&acirc;n tộc l&agrave;m chủ đạo.<br />\r\n<br />\r\nB&aacute;ch tộc hỗn tạp sinh tồn, khải linh tr&iacute; sau đa số h&igrave;nh người.<br />\r\n<br />\r\nH&ugrave;ng B&atilde;o tộc tổng thể thực lực tuy mạnh, nhưng tr&ecirc;n thảo nguy&ecirc;n hung hiểm kh&ocirc;ng thời kh&ocirc;ng khắc v&agrave;o uy hiếp tộc nh&acirc;n t&iacute;nh mệnh, bầy th&uacute; uy hiếp một mực tồn tại.<br />\r\n<br />\r\nH&agrave;o Gi&aacute;c, Lang Y&ecirc;n, đều l&agrave; tương đối thường gặp dự cảnh thủ đoạn.<br />\r\n<br />\r\nGiờ ph&uacute;t n&agrave;y tiếng k&egrave;n tương đối thư gi&atilde;n, Lang Y&ecirc;n cũng chỉ l&agrave; một tia, hẳn l&agrave; tuần tra tham tiếu ph&aacute;t hiện c&aacute;i g&igrave; kh&ocirc;ng t&iacute;nh nghi&ecirc;m trọng dị trạng.<br />\r\n<br />\r\n&quot;C&ograve;n tưởng rằng l&agrave; c&oacute; đại hung chi th&uacute;.&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng ng&aacute;p một c&aacute;i, c&oacute; ch&uacute;t kh&ocirc;ng th&uacute; vị chỗ ch&eacute;p miệng một c&aacute;i, liền muốn tiếp tục nằm lại b&atilde;i cỏ hưởng thụ thời khắc n&agrave;y an nh&agrave;n, tượng trưng địa đạo c&acirc;u:<br />\r\n<br />\r\n&quot;Tam Tướng qu&acirc;n, ph&aacute;i người đi d&ograve; th&aacute;m đ&atilde; xảy ra chuyện g&igrave;.&quot;<br />\r\n<br />\r\n&quot;Thiếu chủ! Ta đ&atilde; d&ugrave;ng diều h&acirc;u con mắt thấy được!&quot;<br />\r\n<br />\r\nVừa rồi gọi h&agrave;ng vị kia tr&aacute;ng h&aacute;n đại th&uacute;c quơ so Ng&ocirc; Vọng c&ograve;n th&ocirc; c&aacute;nh tay, tr&agrave;n đầy sốt ruột chỗ k&ecirc;u gọi:<br />\r\n<br />\r\n&quot;Ng&agrave;i muốn hay kh&ocirc;ng đi luyện một ch&uacute;t tay<br />\r\n<br />\r\nMột đ&aacute;m cũng liền mấy trăm năm ni&ecirc;n đại Chử Kiền th&uacute; kh&ocirc;ng biết bị ai kinh động đến, tại ngo&agrave;i trăm dặm đuổi theo c&aacute;i g&igrave;, người của ch&uacute;ng ta đ&atilde; theo hai b&ecirc;n đi theo.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng đi, &quot; Ng&ocirc; Vọng ng&aacute;p một c&aacute;i, &quot;Kh&ocirc;ng th&uacute; vị.&quot;<br />\r\n<br />\r\nH&ugrave;ng Tam tướng qu&acirc;n g&atilde;i g&atilde;i đầu, theo kia trượng cao Cự Lang tr&ecirc;n lưng nhảy xuống tới, mấy c&aacute;i dậm ch&acirc;n vọt tới Ng&ocirc; Vọng b&ecirc;n cạnh, xoa xoa đại thủ, d&ugrave;ng th&ocirc; cuồng tiếng n&oacute;i tận lực nhỏ giọng nhắc nhở lấy:<br />\r\n<br />\r\n&quot;Thiếu chủ, ng&agrave;i về sau l&agrave; đại tộc Thủ Lĩnh, c&aacute;i tuổi n&agrave;y liền n&ecirc;n tức thời khoe khoang xuống vũ lực, kh&ocirc;ng phải vậy về sau dễ d&agrave;ng sai lầm.<br />\r\n<br />\r\nTa biết Thiếu chủ ng&agrave;i lợi hại, có th&ecirc;̉ những c&aacute;i kia g&acirc;́u đ&aacute;m nh&oacute;c con kh&ocirc;ng biết a, ng&agrave;i cũng muốn th&ecirc;m ra tay, trấn trụ những c&aacute;i kia g&acirc;́u đứa con mới được!&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng:<br />\r\n<br />\r\nNếu như l&agrave; v&igrave; thị tộc ổn định vậy liền kh&ocirc;ng c&oacute; biện ph&aacute;p, hắn mặc d&ugrave; đối quản l&yacute; thị tộc đề kh&ocirc;ng nổi tinh thần, nhưng cũng kh&ocirc;ng muốn cho đời n&agrave;y cha mẹ dọa người.<br />\r\n<br />\r\n&quot;K&eacute;o ta khung xe tới.&quot;<br />\r\n<br />\r\n&quot;Ai, l&agrave;!&quot;<br />\r\n<br />\r\nH&ugrave;ng Tam tướng qu&acirc;n hưng phấn chỗ đ&aacute;p ứng một tiếng, nện một c&aacute;i ngực, quay đầu ph&aacute;t ra một trận gầm r&uacute;.<br />\r\n<br />\r\nLiền nghe sưu sưu tiếng x&eacute; gi&oacute; b&ecirc;n tai kh&ocirc;ng dứt, bờ s&ocirc;ng phụ cận Cự Lang cưỡi đồng thời bắt đ&acirc;̀u chuy&ecirc;̉n đ&ocirc;̣ng, nhanh như Huyễn Ảnh.<br />\r\n<br />\r\nThế l&agrave;, sau nửa canh giờ.<br />\r\n<br />\r\nMột c&aacute;i trải ba tầng thuần trắng tuyết h&ugrave;ng da khung xe, bị bốn c&aacute;i băng lam thuần l&ocirc;ng Cự Lang l&ocirc;i k&eacute;o, bốn c&aacute;i vết rỉ lốm đốm b&aacute;nh xe &eacute;p qua c&oacute; ch&uacute;t vũng b&ugrave;n b&atilde;i cỏ, &aacute;p hỏng hoa hoa thảo thảo, t&oacute;e l&ecirc;n một đường b&ugrave;n điểm.<br />\r\n<br />\r\nNg&ocirc; Vọng phủ th&ecirc;m tr&acirc;n qu&yacute; hung th&uacute; da may th&agrave;nh &aacute;o cho&agrave;ng, tr&ecirc;n đầu mang theo ấu th&uacute; h&agrave;m tr&ecirc;n l&agrave;m th&agrave;nh mũ, lệch qua mềm mại da gấu b&ecirc;n trong, th&acirc;n h&igrave;nh nương theo lấy khung xe tại c&oacute; ch&uacute;t lay động.<br />\r\n<br />\r\nHắn ch&iacute;nh n&acirc;ng một tấm cũ da d&ecirc;, suy nghĩ ph&iacute;a tr&ecirc;n cong cong xoay xoay k&yacute; hiệu, cũng l&agrave; c&oacute; ch&uacute;t chuy&ecirc;n ch&uacute;.<br />\r\n<br />\r\nTại khung xe chung quanh, từng người từng người người mặc tinh gi&aacute;p, v&otilde; trang đầy đủ Cự Lang thiết kỵ theo s&aacute;t, bọn hắn mục quang thậm ch&iacute; so dưới th&acirc;n Cự Lang c&ograve;n muốn hung &aacute;c, tr&aacute;ng kiện th&acirc;n thể ẩn chứa kinh người lực lượng.<br />\r\n<br />\r\nH&ugrave;ng Tam tướng qu&acirc;n l&aacute;i Cự Lang từ tiền phương chạy nhanh đến, quanh co nửa v&ograve;ng, thuần thục tiến tới khung xe b&ecirc;n cạnh, dắt cuống họng g&agrave;o to:<br />\r\n<br />\r\n&quot;Thiếu chủ! Những c&aacute;i kia Chử Kiền đang đuổi ba c&aacute;i Nh&acirc;n vực tới tu sĩ!<br />\r\n<br />\r\nKh&ocirc;ng sai được, bọn hắn mặc hoa hoa lục lục, nữ đ&aacute;nh nhau c&ograve;n mặc v&aacute;y, d&ugrave;ng vẫn l&agrave; loại kia tr&ograve; m&egrave;o ph&aacute;p thuật! Cầm mấy c&acirc;y c&acirc;y tăm n&eacute;m đến n&eacute;m đi.<br />\r\n<br />\r\nThực lực bọn hắn đều chẳng ra sao cả, phi kh&ocirc;ng Ph&aacute;p kh&iacute; giống như bị hủy, tr&ecirc;n mặt đất chạy so hao tổn rất lớn tử c&ograve;n nhanh hơn, bất qu&aacute; xem ra đ&atilde; nhanh kh&ocirc;ng ki&ecirc;n tr&igrave; nổi.<br />\r\n<br />\r\nTa muốn cứu bọn hắn sao &quot;<br />\r\n<br />\r\n&quot;Phụ th&acirc;n n&oacute;i qua, nh&igrave;n thấy gặp rủi ro Nh&acirc;n tộc, c&oacute; thể cứu tựu cứu.&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng cũng kh&ocirc;ng ngẩng đầu l&ecirc;n địa đạo c&acirc;u: &quot;Tam Tướng qu&acirc;n an b&agrave;i l&agrave; được.&quot;<br />\r\n<br />\r\n&quot;Ai! Nh&igrave;n tốt a ng&agrave;i!&quot;<br />\r\n<br />\r\nH&ugrave;ng Tam tướng qu&acirc;n nắm quyền m&atilde;nh lực nện một c&aacute;i ngực, ngửa đầu ph&aacute;t ra một tiếng g&acirc;́u chi gào thét:<br />\r\n<br />\r\n&quot;Thiếu chủ muốn xuất thủ! Đều trừng lớn mắt nh&igrave;n xem! Trở về đều cho l&atilde;o tử kỹ c&agrave;ng mi&ecirc;u tả, gặp ai cũng n&oacute;i một trăm lần!&quot;<br />\r\n<br />\r\nChung quanh Cự Lang cưỡi lập tức hưng phấn dị thường, ngao ngao r&eacute;o l&ecirc;n kh&ocirc;ng ngừng.<br />\r\n<br />\r\nSau đ&oacute;, đội xe đồng đều nhanh hướng về ph&iacute;a trước, ph&iacute;a trước tr&ecirc;n đại thảo nguy&ecirc;n đ&atilde; bạo ph&aacute;t một trận d&agrave;y đặc tiếng vang.<br />\r\n<br />\r\nTiếng s&oacute;i tru, g&agrave;o to &acirc;m thanh, tiếng th&uacute; g&agrave;o, d&acirc;y cung chấn động thanh &acirc;m, đầy trời đại hỏa bỗng dưng m&agrave; hiện.<br />\r\n<br />\r\nCổ ngữ có nói: Đạt th&igrave; hỏa lực bao tr&ugrave;m, c&ugrave;ng th&igrave; chiến thuật xen kẽ.<br />\r\n<br />\r\nL&agrave;m Ng&ocirc; Vọng khung xe đến, kia mấy chục con Chử Kiền đ&atilde; bị Cự Lang cưỡi bao bọc v&acirc;y quanh.<br />\r\n<br />\r\nLoại n&agrave;y th&uacute; chưa c&oacute; th&agrave;nh tựu l&uacute;c, tướng mạo tựu rất k&igrave; lạ, thể to như ngưu, d&aacute;ng như b&aacute;o đốm, nhưng đầu kiểu d&aacute;ng tiếp cận với Nh&acirc;n tộc, một mắt, người cầm đầu, c&aacute;i đu&ocirc;i chiếm một nửa chiều cao.<br />\r\n<br />\r\nBọn ch&uacute;ng trời sinh t&iacute;nh hung m&atilde;nh, phần lớn l&agrave; tốp năm tốp ba, trong miệng có th&ecirc;̉ ph&aacute;t ra s&oacute;ng &acirc;m, tại Bắc Vực xem như tương đối thường gặp đ&agrave;n th&uacute;.<br />\r\n<br />\r\nLiền l&agrave;<br />\r\n<br />\r\nThịt kh&ocirc;ng thế n&agrave;o ăn ngon.<br />\r\n<br />\r\nTh&uacute; thực lực xem x&eacute;t chủng tộc, hai xem thọ tuổi, dứt bỏ ni&ecirc;n đại thảo luận chiến lực kia đơn thuần n&oacute;i bậy nhạt.<br />\r\n<br />\r\nSống đến ng&agrave;n năm Chử Kiền c&oacute; thể đem b&aacute;o th&acirc;n tiến h&oacute;a l&agrave;m loại người tứ chi, ba ng&agrave;n năm ph&acirc;n Chử Kiền đ&atilde; xem như hung th&uacute; cấp bậc, một tiếng giận tr&aacute; có th&ecirc;̉ đ&aacute;nh chết ngo&agrave;i trăm dặm đ&agrave;n th&uacute;.<br />\r\n<br />\r\nBọn hắn H&ugrave;ng B&atilde;o tộc tựu từng săn bắn qua hung th&uacute; cấp Chử Kiền, tử thương rất nặng, nhưng thu hoạch cũng kh&ocirc;ng &iacute;t, một vi&ecirc;n Th&uacute; đan đổi rất nhiều lương thực, nu&ocirc;i sống c&agrave;ng nhiều người.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nLại n&oacute;i trước mắt.<br />\r\n<br />\r\nC&aacute;ch Cự Lang cưỡi v&ograve;ng v&acirc;y c&agrave;ng gần, những c&aacute;i kia Chử Kiền th&uacute; tiếng rống tựu vượt điếc tai.<br />\r\n<br />\r\nNg&ocirc; Vọng ngẩng đầu nh&igrave;n một ch&uacute;t, gặp được bị lang kỵ ngăn c&aacute;ch ba đạo th&acirc;n ảnh, kia l&agrave; hai nữ một nam, nữ đều l&agrave; tu th&acirc;n v&aacute;y d&agrave;i trang phục, nam cũng mặc xanh lam trường b&agrave;o, ri&ecirc;ng ph&acirc;̀n mình đều đ&atilde; bị thương.<br />\r\n<br />\r\nC&aacute;i n&agrave;y c&aacute;ch ăn mặc, th&acirc;́y nhi&ecirc;̀u tại Nh&acirc;n vực b&ecirc;n trong Nh&acirc;n tộc tu h&agrave;nh giả.<br />\r\n<br />\r\nBọn hắn ch&iacute;nh khẩn trương nh&igrave;n chằm chằm chung quanh kh&ocirc;ng ngừng t&uacute;i giới b&oacute;ng đen, nam nh&acirc;n kia cũng coi như c&oacute; ch&uacute;t đảm đương, nắm lấy một thanh trường kiếm đem hai t&ecirc;n nữ nh&acirc;n trẻ tuổi bảo hộ ở sau lưng.<br />\r\n<br />\r\nA, hẳn l&agrave; xưng l&agrave; nữ tử.<br />\r\n<br />\r\nTheo Nh&acirc;n vực quy củ, đều l&agrave; xưng nam tử c&ugrave;ng nữ tử.<br />\r\n<br />\r\nĐại Hoang ph&acirc;n ch&iacute;n d&atilde;, Nh&acirc;n vực cư tại nam, l&agrave; Nh&acirc;n tộc tụ tập chi địa, cũng l&agrave; Nh&acirc;n tộc tại Thi&ecirc;n Địa ở giữa lớn nhất căn cứ.<br />\r\n<br />\r\nBọn hắn H&ugrave;ng B&atilde;o tộc tại Đại Hoang đ&ocirc;ng bắc phương hướng, c&aacute;ch Nh&acirc;n vực tương đối xa, có th&ecirc;̉ ở chỗ n&agrave;y nh&igrave;n thấy Nh&acirc;n vực tu h&agrave;nh giả, cũng coi l&agrave; chuyện hiếm lạ.<br />\r\n<br />\r\nNg&ocirc; Vọng nhịn kh&ocirc;ng được nh&igrave;n nhiều kia hai c&aacute;i nữ tu v&agrave;i lần.<br />\r\n<br />\r\n&Acirc;n<br />\r\n<br />\r\nQuả nhi&ecirc;n, tu h&agrave;nh đối l&agrave;n da trắng đẹp, mềm nhẵn c&ugrave;ng quang trạch rất c&oacute; trợ gi&uacute;p, bọn hắn H&ugrave;ng B&atilde;o tộc thiếu nữ phần lớn l&agrave; khỏe mạnh m&agrave;u n&acirc;u sẫm, c&aacute;i n&agrave;y hai nữ tu tựu đặc biệt bạch.<br />\r\n<br />\r\nHai c&aacute;i n&agrave;y Nh&acirc;n vực tới nữ tu thắng ở th&acirc;n thể mảnh mai, c&oacute; liễu nhược đỡ gi&oacute; cảm nhận, nhưng đều thiếu điểm c&acirc;n lượng, bọn hắn H&ugrave;ng B&atilde;o tộc Muội Tử tựu kh&ocirc;ng giống với l&uacute;c trước, phần lớn<br />\r\n<br />\r\n&quot;Thiếu chủ! Mấy chục con c&aacute;i đồ chơi n&agrave;y, một m&igrave;nh ng&agrave;i được hay kh&ocirc;ng &quot;<br />\r\n<br />\r\nH&ugrave;ng Tam tướng qu&acirc;n ở b&ecirc;n cạnh lại bu lại, nhỏ giọng thầm th&igrave; một c&acirc;u: &quot;Nếu kh&ocirc;ng ta an b&agrave;i mấy c&aacute;i Tế Tự b&ecirc;n cạnh niệm ch&uacute;, c&ugrave;ng ng&agrave;i đồng loạt ra tay &quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng cần.&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng từ khung xe đứng dậy, vịn h&agrave;ng ph&iacute;a trước lan can.<br />\r\n<br />\r\nBốn đầu Sương bạch Cự Lang ngửa đầu th&eacute;t d&agrave;i, tiền phương từng tầng từng tầng giao thoa tiến l&ecirc;n Cự Lang cưỡi giống như thủy triều thối lui, đem mấy chục con khốn th&uacute; hiện ra ở Ng&ocirc; Vọng &aacute;nh mắt.<br />\r\n<br />\r\nKhung xe vọt tới trước, Ng&ocirc; Vọng sắc mặt đạm mạc, xương th&uacute; dưới mũ gi&aacute;p t&oacute;c d&agrave;i đ&oacute;n gi&oacute; phất phới.<br />\r\n<br />\r\nHắn n&acirc;ng l&ecirc;n tay tr&aacute;i, l&ograve;ng b&agrave;n tay nhắm ngay đ&aacute;m kia Chử Kiền.<br />\r\n<br />\r\nChử Kiền th&uacute; nh&oacute;m b&eacute;n nhạy đ&atilde; nhận ra nguy hiểm, đối Ng&ocirc; Vọng h&aacute; mồm gầm th&eacute;t, từng tầng từng tầng tiếng gầm giống như trong suốt v&aacute;ch tường đối Ng&ocirc; Vọng đ&egrave; xuống.<br />\r\n<br />\r\nNg&ocirc; Vọng biểu lộ kh&ocirc;ng thay đổi ch&uacute;t n&agrave;o, l&ograve;ng b&agrave;n tay hiện ra thập nhị khỏa tinh thần, tr&ecirc;n đ&oacute; quang mang lẫn nhau cấu kết.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; Bắc D&atilde; lưu truyền Kỳ Tinh thuật, Bắc D&atilde; c&aacute;c tộc c&aacute;c tế tự đời đời truy&ecirc;̀n lại kỹ ph&aacute;p, cũng l&agrave; bọn hắn H&ugrave;ng B&atilde;o tộc có th&ecirc;̉ tại Bắc D&atilde; chỗ dựa một trụ cột lớn.<br />\r\n<br />\r\nTr&ecirc;n bầu trời, thập nhị khỏa tinh thần lấp l&aacute;nh tại phương vi&ecirc;n trăm dặm bầu trời.<br />\r\n<br />\r\nChử Kiền bọn họ tiếng k&ecirc;u yếu đi xuống dưới, bọn ch&uacute;ng một mắt b&ecirc;n trong mang theo nghi hoặc, mang theo sợ h&atilde;i, lại tại trong triều ở giữa hội tụ.<br />\r\n<br />\r\nCũng hiểu e ngại sao<br />\r\n<br />\r\nL&agrave;, c&aacute;i n&agrave;y Đại Hoang b&ecirc;n trong, th&uacute; đều l&agrave; linh tr&iacute;, Đại Hoang đại bộ phận khu vực b&ecirc;n trong, nh&acirc;n tộc địa vị cũng kh&ocirc;ng như th&uacute;.<br />\r\n<br />\r\nNgười lý chi quang c&ograve;n yếu ớt<br />\r\n<br />\r\nNh&acirc;n tộc dấu ch&acirc;n chưa đến đỉnh cao nhất của thế giới n&agrave;y<br />\r\n<br />\r\nTụ tập tại Nh&acirc;n vực Nh&acirc;n tộc, c&ugrave;ng tản m&aacute;t tại Đại Hoang ch&iacute;n d&atilde; Nh&acirc;n tộc, đ&atilde; đi l&ecirc;n ho&agrave;n to&agrave;n kh&aacute;c biệt ph&aacute;t triển chi lộ.<br />\r\n<br />\r\nPh&aacute;p tắc, đạo tắc<br />\r\n<br />\r\nKỳ tinh, thuật ph&aacute;p.<br />\r\n<br />\r\nNg&ocirc; Vọng &aacute;nh mắt liếc nh&igrave;n bị Cự Lang cưỡi v&acirc;y quanh ba đạo th&acirc;n ảnh, n&acirc;ng l&ecirc;n b&agrave;n tay tr&aacute;i d&ugrave;ng sức nắm chặt, đ&agrave;n th&uacute; bốn ph&iacute;a đột nhi&ecirc;n xuất hiện một sợi Băng Lăng, đ&oacute;a đ&oacute;a Băng Li&ecirc;n chớp mắt tỏa ra, li&ecirc;n tiếp li&ecirc;n mi&ecirc;n.<br />\r\n<br />\r\nKỳ Tinh thuật, Đại Băng Trạch!</p>', '2021-11-27 21:23:34', 16, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(181, 49, 'Quái bệnh!', '2', '<p>Trời chiều dư huy b&ecirc;n trong, một c&aacute;i phần lưng mọc đầy gai đ&acirc;m Cự Th&uacute; k&eacute;o lấy th&ocirc; mộc l&agrave;m tựu xe t&ugrave;, hướng Tuyết Sơn phương hướng chậm r&atilde;i đi v&agrave;o.<br />\r\n<br />\r\nN&oacute; tiến l&ecirc;n tốc độ cũng kh&ocirc;ng chậm, nhưng so với những c&aacute;i kia tới lui một trận gi&oacute; Cự Lang, x&aacute;c thực kh&ocirc;ng t&iacute;nh cấp tốc.<br />\r\n<br />\r\nXe t&ugrave; b&ecirc;n trong, kia hai nữ một nam khu&ocirc;n mặt u &aacute;m, ri&ecirc;ng ph&acirc;̀n mình ngồi xếp bằng.<br />\r\n<br />\r\nC&aacute;ch hơi gần kia đối nam nữ đang kh&ocirc;ng ngừng truyền thanh n&oacute;i g&igrave; đ&oacute;, ngồi trong g&oacute;c tuổi trẻ nữ tử chỉ l&agrave; nhắm mắt ngưng thần, l&ocirc;ng mi thỉnh thoảng nh&aacute;y tho&aacute;ng c&aacute;i.<br />\r\n<br />\r\nX&oacute; xỉnh b&ecirc;n trong nữ tử t&ecirc;n l&agrave; L&acirc;m Tố Khinh, đạo hiệu cũng l&agrave; Tố Khinh, b&aacute;i sư l&uacute;c sư phụ hiểu được danh tự n&agrave;y cũng kh&ocirc;ng tệ lắm, liền trực tiếp tiếp tục sử dụng.<br />\r\n<br />\r\nĐột xuất một c&aacute;i tuỳ &yacute;.<br />\r\n<br />\r\nChốc l&aacute;t trước, bọn hắn c&ograve;n tưởng rằng ch&iacute;nh m&igrave;nh được cứu<br />\r\n<br />\r\nL&acirc;m Tố Khinh c&acirc;u kia &quot;Đa tạ c&aacute;c vị trượng nghĩa xuất thủ&quot; chỉ n&oacute;i c&aacute;i &quot;Đa&quot; chữ, từng thanh từng thanh lưỡi dao đ&atilde; g&aacute;c ở bọn hắn tr&ecirc;n cổ.<br />\r\n<br />\r\nBắc D&atilde; đ&aacute;m nam nhi tốc độ xuất thủ, so với bọn hắn ba c&aacute;i th&ocirc;i động ph&aacute;p thuật tốc độ nhanh kh&ocirc;ng biết bao nhi&ecirc;u.<br />\r\n<br />\r\nBọn hắn bị chế trụ sau liền bị n&eacute;m tới bộ n&agrave;y tr&ecirc;n xe t&ugrave;, c&aacute;i kia vừa rung động bọn họ n&oacute;i t&acirc;m một c&aacute;i &#39;Thiếu chủ&#39; đ&atilde; trước một bước rời đi.<br />\r\n<br />\r\nThiếu chủ kia r&otilde; r&agrave;ng chỉ l&agrave; người thiếu ni&ecirc;n, xem tuổi t&aacute;c tuyệt đối sẽ kh&ocirc;ng vượt qua mười lăm tuổi m&agrave; nh&acirc;n tộc b&igrave;nh qu&acirc;n thọ tuổi hơn ba trăm, người n&agrave;y t&iacute;nh thế n&agrave;o đều chỉ l&agrave; oa oa.<br />\r\n<br />\r\nCh&iacute;nh l&agrave; tại Nh&acirc;n vực tu h&agrave;nh t&ocirc;ng m&ocirc;n, lớn nhỏ như vậy thiếu ni&ecirc;n cũng bất qu&aacute; l&agrave; vừa mới bắt đầu tu h&agrave;nh.<br />\r\n<br />\r\nL&agrave;m sao lại<br />\r\n<br />\r\nThật mạnh Kỳ Tinh thuật.<br />\r\n<br />\r\nL&acirc;m Tố Khinh từng nghe tới, lưu lạc Nh&acirc;n vực b&ecirc;n ngo&agrave;i Nh&acirc;n tộc, c&ugrave;ng b&aacute;ch tộc c&ugrave;ng nhau tại Thi&ecirc;n Địa ở giữa cạnh sinh tồn, cũng tại b&aacute;ch tộc tr&ecirc;n th&acirc;n học được rất nhiều huyền diệu ph&aacute;p m&ocirc;n.<br />\r\n<br />\r\nKỳ Tinh thuật liền l&agrave; học được từ Bắc D&atilde; Nh&acirc;n tộc, c&oacute; ch&uacute;t c&ugrave;ng loại với Nh&acirc;n vực Ngũ H&agrave;nh thuật ph&aacute;p, c&oacute; thể coi l&agrave; uy lực cường đại, cũng kh&ocirc;ng n&ecirc;n mạnh mẽ như thế, kh&ocirc;ng phải vậy c&aacute;c vị tiền bối cao nh&acirc;n tất nhi&ecirc;n sớm đ&atilde; tại Nh&acirc;n vực mở rộng Bắc D&atilde; Kỳ Tinh thuật.<br />\r\n<br />\r\nN&ecirc;n n&oacute;i, l&agrave; thật mạnh thiếu ni&ecirc;n.<br />\r\n<br />\r\nL&acirc;m Tố Khinh c&oacute; ch&uacute;t h&eacute; miệng, n&agrave;ng đối thiếu ni&ecirc;n n&agrave;y ấn tượng kh&aacute; l&agrave; s&acirc;u sắc.<br />\r\n<br />\r\nBọn hắn vừa mới bị bắt giữ lấy to&agrave; kia khung xe b&ecirc;n cạnh l&uacute;c, n&agrave;y c&aacute;i thiếu chủ ngồi nghi&ecirc;ng ở bảo tọa b&ecirc;n trong, trong tay bưng một tấm quyển da cừu, kia xem thường mục quang, c&ugrave;ng mang theo mệt mỏi lười &aacute;nh mắt, xem bọn hắn liền phảng phất xem ba c&aacute;i<br />\r\n<br />\r\nVật kiện.<br />\r\n<br />\r\nM&agrave; c&aacute;i n&agrave;y &#39;Thiếu chủ&#39; đối bọn hắn n&oacute;i duy nhất một c&acirc;u, liền l&agrave; ở b&ecirc;n cạnh một đầu g&acirc;́u hỏi xử tr&iacute; như thế n&agrave;o bọn hắn l&uacute;c, &#39;Thiếu chủ&#39; n&oacute;i c&acirc;u:<br />\r\n<br />\r\n&#39;Trước mang về, ta đến an b&agrave;i.&#39;<br />\r\n<br />\r\nSinh tử bị người nắm tư vị quả thực kh&ocirc;ng dễ chịu.<br />\r\n<br />\r\nL&acirc;m Tố Khinh đ&aacute;y l&ograve;ng tinh tường, cho d&ugrave; l&agrave; bọn họ chết tại Bắc D&atilde;, cũng sẽ kh&ocirc;ng c&oacute; người mạo hiểm đến cứu.<br />\r\n<br />\r\nHuống chi Bắc D&atilde; rộng lớn như vậy, một trận mưa lớn liền c&oacute; thể hủy đi hơn ph&acirc;n nửa vết t&iacute;ch ch&iacute;nh l&agrave; sư m&ocirc;n trưởng bối đến t&igrave;m, sợ cũng kh&oacute; t&igrave;m đến bọn hắn nửa điểm tung t&iacute;ch.<br />\r\n<br />\r\nMột tia linh thức truyền thanh chui v&agrave;o trong tai:<br />\r\n<br />\r\n&quot;Sư tỷ, ch&uacute;ng ta kh&ocirc;ng thể thật bị bắt trở về, kh&ocirc;ng phải vậy sinh tử cũng liền thiếu ni&ecirc;n kia một c&acirc;u định.<br />\r\n<br />\r\nHắn đ&atilde; đi, ch&uacute;ng ta kh&ocirc;ng bằng như vậy &quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh lập tức mở hai mắt ra, mắt h&agrave;m thu thuỷ, nh&igrave;n về ph&iacute;a b&ecirc;n nam tu.<br />\r\n<br />\r\nL&agrave;m n&agrave;ng nh&igrave;n thấy gần như c&aacute;nh tay s&aacute;t b&ecirc;n c&aacute;nh tay sư đệ sư muội, kh&oacute;e miệng nhẹ nh&agrave;ng bĩu một c&aacute;i, mục quang cấp tốc dịch chuyển khỏi.<br />\r\n<br />\r\nR&otilde; r&agrave;ng, l&agrave; n&agrave;ng tới trước<br />\r\n<br />\r\nC&aacute;ch nơi đ&acirc;y kh&ocirc;ng biết bao xa Sương Lang xa tr&ecirc;n kệ, Ng&ocirc; Vọng nh&igrave;n xem trước mặt thủy tinh cầu.<br />\r\n<br />\r\nThủy tinh cầu tỏa ra &aacute;nh s&aacute;ng lung linh, trong đ&oacute; hiển lộ lấy xe t&ugrave; b&ecirc;n trong t&igrave;nh h&igrave;nh, Ng&ocirc; Vọng b&eacute;n nhạy bắt được L&acirc;m Tố Khinh biểu t&igrave;nh biến h&oacute;a, c&ugrave;ng mặt kh&aacute;c một đ&ocirc;i nam nữ bờ m&ocirc;i kh&eacute;p mở.<br />\r\n<br />\r\nC&oacute; ch&uacute;t &yacute; tứ.<br />\r\n<br />\r\nNg&ocirc; Vọng đương nhi&ecirc;n biết tu sĩ phần lớn c&oacute; linh thức truyền thanh thủ đoạn, đ&acirc;y l&agrave; sơ giai tu sĩ liền c&oacute; thể nắm giữ một điểm nhỏ kỹ xảo, khuyết điểm l&agrave; dễ d&agrave;ng bị linh thức cường đại, hoặc l&agrave; tu ra thần thức người b&ecirc;n ngo&agrave;i nghe qua.<br />\r\n<br />\r\nNg&ocirc; Vọng nhấc nhấc tay, b&ecirc;n lập tức c&oacute; Cự Lang cưỡi thấu hướng về ph&iacute;a trước, cấp tốc lĩnh mệnh m&agrave; đi.<br />\r\n<br />\r\nMột l&aacute;t sau<br />\r\n<br />\r\nXe t&ugrave; b&ecirc;n trong, ba c&aacute;i ngồi xếp bằng th&acirc;n ảnh ri&ecirc;ng ph&acirc;̀n mình &aacute;nh mắt giao lưu một hai, theo bọn hắn vẻ mặt nghi&ecirc;m t&uacute;c, nhạy b&eacute;n mục quang, đ&atilde; có th&ecirc;̉ nh&igrave;n ra gi&acirc;y l&aacute;t về sau sẽ c&oacute; một trận &#39;Xe n&aacute;t&#39; đại chiến.<br />\r\n<br />\r\nNam tu tiếng n&oacute;i chui v&agrave;o sư tỷ c&ugrave;ng sư muội trong tai:<br />\r\n<br />\r\n&quot;Chuẩn bị, đợi ta đếm tới mười, c&ugrave;ng nhau xuất thủ oanh c&acirc;y thứ s&aacute;u c&acirc;y cột.<br />\r\n<br />\r\nMột, hai năm &quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nNgao &ocirc;!<br />\r\n<br />\r\nMột b&ecirc;n đột nhi&ecirc;n truyền đến Cự Lang tiếng g&agrave;o th&eacute;t, ba người v&ocirc; &yacute; thức nh&igrave;n lại, đ&atilde; thấy mấy t&ecirc;n h&ugrave;ng tr&aacute;ng h&aacute;n tử tại Cự Lang tr&ecirc;n lưng n&acirc;ng l&ecirc;n hiện ra Thanh Đồng kim loại quang mang cường nỏ.<br />\r\n<br />\r\nLại c&oacute; một t&ecirc;n tr&aacute;ng h&aacute;n tại b&ecirc;n ngo&agrave;i hơn mười trượng n&eacute;m ra một khối đầu lớn tiểu Hoang Thạch.<br />\r\n<br />\r\nKen k&eacute;t!<br />\r\n<br />\r\nMấy t&ecirc;n Cự Lang cưỡi đồng thời k&eacute;o động cơ khấu trừ, kịch liệt tiếng x&eacute; gi&oacute; giống như hung th&uacute; th&ecirc; lương th&eacute;t l&ecirc;n, khối kia Hoang Thạch tr&ecirc;n kh&ocirc;ng trung trực tiếp nổ n&aacute;t vụn, chung quanh Cự Lang cưỡi vung vẩy trong tay binh kh&iacute; một trận reo h&ograve;.<br />\r\n<br />\r\n&quot;Cục cục.&quot;<br />\r\n<br />\r\nNam tu tiếng n&oacute;i nhọn run rẩy, truyền thanh v&ocirc; &yacute; thức tựu biến th&agrave;nh:<br />\r\n<br />\r\n&quot;S&aacute;u, năm, bốn &quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh y&ecirc;n lặng nghi&ecirc;ng đầu đi, kh&ocirc;ng chịu được đưa tay gi&uacute;p đỡ cằm dưới đầu.<br />\r\n<br />\r\n&Acirc;n, bản th&acirc;n sư đệ đ&acirc;y kh&ocirc;ng phải tham sống sợ chết, chỉ l&agrave; đơn thuần c&oacute; chút tiếc mệnh, như thế thức thời quả nhi&ecirc;n kh&ocirc;ng hổ l&agrave; tuấn kiệt.<br />\r\n<br />\r\nHo&agrave;ng h&ocirc;n l&uacute;c, trời chiều t&agrave;n huyết.<br />\r\n<br />\r\nBắc D&atilde; đại thảo nguy&ecirc;n bầu trời cao xa lại th&acirc;m th&uacute;y, rực rỡ tinh kh&ocirc;ng đ&atilde; kh&ocirc;ng kịp chờ đợi lấp l&aacute;nh tại c&ograve;n chưa ho&agrave;n to&agrave;n nhuộm đen m&agrave;n trời, hai đầu giao thoa Ng&acirc;n H&agrave; chọc tới kh&ocirc;ng biết nhiều &iacute;t Đại Hoang sinh linh mơ m&agrave;ng c&ugrave;ng t&agrave;i s&aacute;ng tạo.<br />\r\n<br />\r\nL&acirc;m Tố Khinh hơi c&oacute; ch&uacute;t hoảng hốt, lấy lại tinh thần l&uacute;c, xe t&ugrave; đ&atilde; chạy tại bằng phẳng tr&ecirc;n đường lớn, đại lộ hai b&ecirc;n bắt đầu đứt qu&atilde;ng xuất hiện liền khối lều vải c&ugrave;ng thạch ốc.<br />\r\n<br />\r\nXe t&ugrave; điểm cuối c&ugrave;ng, l&agrave; một chỗ nhẹ nh&agrave;ng dốc n&uacute;i.<br />\r\n<br />\r\nTr&ecirc;n sườn n&uacute;i x&acirc;y đầy thạch ốc, lều nỉ, dốc n&uacute;i tr&ecirc;n cùng th&igrave; l&agrave; v&agrave;i to&agrave; cự đại lều trướng, như Cung Điện, tr&ecirc;n đ&oacute; treo một c&aacute;i H&ocirc;i H&ugrave;ng ngửa đầu gào thét cờ x&iacute;.<br />\r\n<br />\r\nCự Lang cưỡi c&oacute; thứ tự r&uacute;t lui, một nh&oacute;m mặc đen nh&aacute;nh &aacute;o gi&aacute;p, th&acirc;n h&igrave;nh c&agrave;ng cường tr&aacute;ng hơn, tự th&acirc;n tr&agrave;n đầy s&aacute;t kh&iacute; nam nữ tiếp thủ xe t&ugrave;, &aacute;p lấy ba người bọn họ l&ecirc;n n&uacute;i sườn n&uacute;i ph&iacute;a tr&ecirc;n tiến l&ecirc;n.<br />\r\n<br />\r\n&quot;Đ&acirc;y ch&iacute;nh l&agrave; Thiếu chủ bắt tới Nh&acirc;n vực tu h&agrave;nh giả &quot;<br />\r\n<br />\r\nC&oacute; người tại ven đường ch&agrave;o hỏi &acirc;m thanh, L&acirc;m Tố Khinh ba người lập tức hấp dẫn đại lượng mục quang.<br />\r\n<br />\r\nMột ch&uacute;t kh&oacute; nghe b&igrave;nh luận cũng theo đ&oacute; m&agrave; đến, phần lớn đều l&agrave; ch&uacute;t &iacute;t th&acirc;n thể khoẻ mạnh, mặc &aacute;o da th&uacute; phụ nh&acirc;n tại đối bọn hắn chỉ trỏ, b&igrave;nh phẩm từ đầu đến ch&acirc;n:<br />\r\n<br />\r\n&quot;Nh&acirc;n vực Nh&acirc;n tộc c&ugrave;ng ch&uacute;ng ta loại liền l&agrave; kh&ocirc;ng giống, c&aacute;i n&agrave;y tiểu c&ocirc; nương gầy nha, kh&ocirc;ng c&oacute; sức lực.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y tiểu ca thật tuấn! C&ugrave;ng ch&uacute;ng ta trong tộc những n&agrave;y đại l&atilde;o th&ocirc; cũng kh&ocirc;ng thể so.&quot;<br />\r\n<br />\r\n&quot;Tuấn c&oacute; c&aacute;i g&igrave; d&ugrave;ng, nh&igrave;n xem th&acirc;n thể, c&ograve;n kh&ocirc;ng phải tịch đầu thương.&quot;<br />\r\n<br />\r\nC&aacute;i kia nam tu trừng mắt nh&igrave;n n&oacute;i lời n&agrave;y phụ nh&acirc;n, hắn mặc d&ugrave; trong n&uacute;i tu h&agrave;nh nhiều năm, nhưng cũng biết đ&acirc;y kh&ocirc;ng phải c&aacute;i g&igrave; tốt n&oacute;i.<br />\r\n<br />\r\n&Aacute;nh mắt bi&ecirc;n giới, một t&ecirc;n tr&aacute;ng h&atilde;n phụ nh&acirc;n giơ một c&aacute;i đồng ch&ugrave;y, đem đ&ocirc;ng lạnh th&agrave;nh đại h&agrave;o băng u cục Chử Kiền th&uacute; cậy mạnh g&otilde; th&agrave;nh khối vụn.<br />\r\n<br />\r\nHừ!<br />\r\n<br />\r\nĐại tỷ xem người thật chuẩn!<br />\r\n<br />\r\nHắn năm ngo&aacute;i nhưng vẫn l&agrave; Thuần Dương chi th&acirc;n!<br />\r\n<br />\r\nC&oacute; c&aacute;i phụ nh&acirc;n buồn bực hỏi một c&acirc;u: &quot;Thiếu chủ bắt bọn hắn trở về l&agrave;m c&aacute;i g&igrave; nu&ocirc;i lang tể sao &quot;<br />\r\n<br />\r\nCũng ch&iacute;nh l&agrave; c&aacute;i n&agrave;y &acirc;m thanh nghi hoặc, th&agrave;nh c&ocirc;ng khơi gợi l&ecirc;n L&acirc;m Tố Khinh ba người bất an.<br />\r\n<br />\r\nL&acirc;m Tố Khinh lần nữa nh&igrave;n thấy vị n&agrave;y &#39;Thiếu chủ&#39; l&uacute;c, l&agrave; tại một chỗ đ&egrave;n đuốc s&aacute;ng rực lều lớn.<br />\r\n<br />\r\nTrong trướng kia mấy ngọn cung cấp dồi d&agrave;o nguồn s&aacute;ng tinh xảo Ph&aacute;p kh&iacute; đ&egrave;n, để L&acirc;m Tố Khinh hơi c&oacute; ch&uacute;t ngo&agrave;i &yacute; muốn một ch&uacute;t liền c&oacute; thể nh&igrave;n ra, đ&acirc;y l&agrave; Nh&acirc;n vực tu sĩ luyện chế, gi&aacute; trị c&oacute; ch&uacute;t kh&ocirc;ng &iacute;t.<br />\r\n<br />\r\nLại nh&igrave;n người thiếu chủ kia, đối phương ngồi tại một tấm da th&uacute; trong ghế.<br />\r\n<br />\r\nL&acirc;m Tố Khinh kh&ocirc;ng khỏi nhiều đ&aacute;nh gi&aacute; v&agrave;i lần thực lực n&agrave;y cường ho&agrave;nh thiếu ni&ecirc;n.<br />\r\n<br />\r\nHắn đổi lại một th&acirc;n phổ th&ocirc;ng trang phục, thoải m&aacute;i dễ chịu vải bố &aacute;o ngắn, quần d&agrave;i, t&oacute;c d&agrave;i đơn giản buộc ở sau &oacute;t, kia Trương Đoan vu&ocirc;ng ch&iacute;nh khu&ocirc;n mặt mặc d&ugrave; thanh t&uacute;, nhưng kh&ocirc;ng c&oacute; nửa phần &acirc;m nhu cảm gi&aacute;c.<br />\r\n<br />\r\nNgược lại l&agrave; cho người ta một loại kh&ocirc;ng hiểu ấm &aacute;p.<br />\r\n<br />\r\nNg&ocirc; Vọng hơi đưa tay, một b&ecirc;n c&oacute; thị vệ nhấc đến ba thanh da th&uacute; c&aacute;i ghế, b&agrave;y tại thảm bi&ecirc;n giới, c&aacute;ch hắn xa ba trượng.<br />\r\n<br />\r\n&quot;Ngồi đi, &quot; Ng&ocirc; Vọng lạnh nhạt mở miệng, &quot;Ta đ&atilde; tốn kh&iacute; lực cứu c&aacute;c ngươi, cũng sẽ kh&ocirc;ng l&uacute;c n&agrave;y giết c&aacute;c ngươi.&quot;<br />\r\n<br />\r\nBa người r&otilde; r&agrave;ng nhẹ nh&agrave;ng thở ra, ri&ecirc;ng ph&acirc;̀n mình đối mặt v&agrave;i lần.<br />\r\n<br />\r\nL&acirc;m Tố Khinh trước hướng về ph&iacute;a trước nửa bước, ngồi ph&iacute;a b&ecirc;n tr&aacute;i da th&uacute; tr&ecirc;n ghế, t&iacute;nh cho sư đệ sư muội l&agrave;m l&agrave;m gương mẫu.<br />\r\n<br />\r\nN&agrave;ng thấp giọng n&oacute;i: &quot;Vị n&agrave;y Thiếu chủ, đa tạ ngươi &acirc;n cứu mạng.&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng cũng kh&ocirc;ng phản ứng n&agrave;ng, chỉ l&agrave; n&oacute;i:<br />\r\n<br />\r\n&quot;Nh&acirc;n vực chủ lưu tu h&agrave;nh con đường l&agrave; Linh Tu c&ugrave;ng Thể Tu, c&aacute;c ngươi thể nội huyết kh&iacute; kh&ocirc;ng vượng, n&ecirc;n l&agrave; tu linh.<br />\r\n<br />\r\nNạp Linh, Tụ Kh&iacute;, Quy Nguy&ecirc;n, Ngưng Đan, đ&acirc;y l&agrave; Nh&acirc;n vực Linh Tu phổ cập nhất Kim Đan đạo trước bốn cảnh, c&aacute;c ngươi chưa có th&ecirc;̉ ngự kh&ocirc;ng m&agrave; đi, hiển nhi&ecirc;n thực lực l&agrave; tại dưới kim đan.<br />\r\n<br />\r\nNgưng Đan cảnh &quot;<br />\r\n<br />\r\nBa c&aacute;i tu sĩ ri&ecirc;ng ph&acirc;̀n mình mặt lộ vẻ n&eacute;t hổ thẹn.<br />\r\n<br />\r\nL&acirc;m Tố Khinh trả lời: &quot;Ba người ch&uacute;ng ta b&ecirc;n trong, ta l&agrave; sư tỷ, tu vi bất qu&aacute; Quy Nguy&ecirc;n cảnh trung kỳ, để ng&agrave;i ch&ecirc; cười.&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng kh&oacute;e miệng cong l&ecirc;n: &quot;Rất yếu.&quot;<br />\r\n<br />\r\nTốt!<br />\r\n<br />\r\nL&acirc;m Tố Khinh ngậm miệng, lại nghĩ tới to&agrave; kia bỗng dưng ngưng tụ th&agrave;nh băng sơn, y&ecirc;n lặng c&uacute;i đầu than nhẹ.<br />\r\n<br />\r\nNg&ocirc; Vọng hỏi: &quot;Bằng ch&iacute;nh c&aacute;c ngươi thực lực, chỉ sợ kh&ocirc;ng ra được Nh&acirc;n vực, v&igrave; c&aacute;i g&igrave; đến đ&acirc;y Bắc D&atilde;, lại như thế n&agrave;o tới Bắc D&atilde;.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh đ&aacute;p:<br />\r\n<br />\r\n&quot;Ch&uacute;ng ta l&agrave; tại Đ&ocirc;ng Hải chi t&acirc;n l&ecirc;n t&agrave;u l&ecirc;n ph&iacute;a bắc đổi h&agrave;ng thuyền lớn, phi&ecirc;u lưu ba năm rưỡi lại, cuối c&ugrave;ng đ&atilde; tới Bắc D&atilde;.<br />\r\n<br />\r\nCh&uacute;ng ta đến đ&acirc;y nơi đ&acirc;y, l&agrave; v&igrave; t&igrave;m trong truyền thuyết linh điểu Bạch Hổ Đ&ocirc;ng, lấy hắn Th&uacute; đan luyện chế linh đan, d&ugrave;ng chữa sư m&ocirc;n trưởng bối chi chứng bệnh.&quot;<br />\r\n<br />\r\n&quot;Sư tỷ!&quot;<br />\r\n<br />\r\nC&aacute;i kia nam tu cau m&agrave;y n&oacute;i: &quot;Chớ c&oacute; n&oacute;i nhiều như vậy &quot;<br />\r\n<br />\r\n&quot;Sư tỷ của ngươi rất th&ocirc;ng minh, &quot; Ng&ocirc; Vọng nh&agrave;n nhạt cắt ngang nam tu lời n&oacute;i, &quot;Tối thiểu so ngươi th&ocirc;ng minh.&quot;<br />\r\n<br />\r\nNam tu mặt trướng đến chuyển hồng, lại chỉ l&agrave; hừ lạnh một tiếng, ưỡn ngực ngẩng đầu ngồi ở kia.<br />\r\n<br />\r\nNg&ocirc; Vọng ng&oacute;n tay g&otilde; lấy da th&uacute; chỗ ngồi lan can, mục quang tại ba người th&acirc;n thượng lưu chuyển, qua một trận mới chậm r&atilde;i n&oacute;i:<br />\r\n<br />\r\n&quot;V&agrave;o tay Bạch Hổ Đ&ocirc;ng Th&uacute; đan &quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; &quot;<br />\r\n<br />\r\n&quot;Ta chỗ n&agrave;y c&oacute; rất nhiều, c&oacute; thể cho c&aacute;c ngươi cầm một vi&ecirc;n.&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng tiếp tục n&oacute;i: &quot;Nhưng ta cứu được c&aacute;c ngươi, lại cho c&aacute;c ngươi cần thiết chi vật, cũng kh&ocirc;ng phải l&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; sở cầu.<br />\r\n<br />\r\nTa c&acirc;̀n ba người c&aacute;c ngươi b&ecirc;n trong lưu lại một người phục thị ta, chỉ cần ở ta nơi n&agrave;y chờ đủ s&aacute;u năm.<br />\r\n<br />\r\nC&oacute; ngo&agrave;i hai người cầm linh đan rời đi, ta sẽ c&ograve;n cho rời đi chi nh&acirc;n đầy đủ lộ ph&iacute;, cũng ph&aacute;i người đưa c&aacute;c ngươi đi c&oacute; thể l&ecirc;n thuyền xu&ocirc;i nam chi địa.&quot;<br />\r\n<br />\r\nBa t&ecirc;n tu sĩ kh&ocirc;ng khỏi sửng sốt.<br />\r\n<br />\r\nNg&ocirc; Vọng kh&ocirc;ng n&oacute;i th&ecirc;m lời, ngồi ở kia lật tới lật lui trước mặt s&aacute;ch da d&ecirc; cuốn.<br />\r\n<br />\r\nL&acirc;m Tố Khinh quay đầu nh&igrave;n về ph&iacute;a c&aacute;i kia nam tu, lại ph&aacute;t hiện ch&iacute;nh m&igrave;nh ngưỡng mộ trong l&ograve;ng sư đệ giờ ph&uacute;t n&agrave;y bờ m&ocirc;i hơi trắng bệch, nắm đấm nắm lại lại bu&ocirc;ng ra, hiển nhi&ecirc;n l&agrave; đang xoắn xu&yacute;t.<br />\r\n<br />\r\nN&agrave;ng đ&aacute;y l&ograve;ng than nhỏ, ch&iacute;nh m&igrave;nh d&ugrave; sao cũng l&agrave; sư tỷ<br />\r\n<br />\r\n&quot;Ta lưu lại!&quot;<br />\r\n<br />\r\nNam tu định &acirc;m thanh h&ocirc; h&agrave;o, tiếng n&oacute;i hơi c&oacute; ch&uacute;t chột dạ, &quot;Ta lưu lại, L&acirc;m sư tỷ, Tr&agrave; sư muội c&aacute;c ngươi mang theo linh đan hồi trở lại!&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng, ta lưu lại, &quot; L&acirc;m Tố Khinh trong mắt tr&agrave;n đầy ki&ecirc;n quyết, hướng về ph&iacute;a trước bước ra hai bước, nh&igrave;n thẳng Ng&ocirc; Vọng, &quot;Ta lưu ở nơi đ&acirc;y phục thị ngươi s&aacute;u năm, xem như b&aacute;o đ&aacute;p &acirc;n t&igrave;nh của ngươi.<br />\r\n<br />\r\nXin cho sư đệ ta sư muội b&igrave;nh an rời đi.&quot;<br />\r\n<br />\r\n&quot;Thương lượng định &quot;<br />\r\n<br />\r\nNg&ocirc; Vọng đối c&aacute;i kia nam tu giơ l&ecirc;n c&aacute;i cằm, &quot;L&agrave;m nam nh&acirc;n, ngươi kh&ocirc;ng ki&ecirc;n tr&igrave; ki&ecirc;n tr&igrave; &quot;<br />\r\n<br />\r\n&quot;Sư tỷ, ta cảm thấy ngươi &quot;<br />\r\n<br />\r\n&quot;Sư huynh!&quot;<br />\r\n<br />\r\nMột mực kh&ocirc;ng l&ecirc;n tiếng nữ tu tiếng gọi khẽ, c&ugrave;ng c&aacute;i kia nam tu liếc nhau.<br />\r\n<br />\r\nL&acirc;m Tố Khinh quay đầu nh&igrave;n lại, vừa vặn thấy được cả hai k&eacute;o c&ugrave;ng một chỗ tay<br />\r\n<br />\r\nNam tu kh&ocirc;ng d&aacute;m nh&igrave;n thẳng L&acirc;m Tố Khinh, c&uacute;i đầu n&oacute;i: &quot;Sư tỷ, nhi&ecirc;̀u, đa tạ.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh th&acirc;n thể rung động xuống, chỉ l&agrave; khẽ gật đầu, liền đứng tại kia c&uacute;i đầu kh&ocirc;ng n&oacute;i.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nNguy&ecirc;n, xấu, m&igrave;nh.<br />\r\n<br />\r\nPhục thị hai chữ h&agrave;m nghĩa, L&acirc;m Tố Khinh lo&aacute;ng tho&aacute;ng l&agrave; minh bạch.<br />\r\n<br />\r\nChỉ l&agrave; n&agrave;ng kh&ocirc;ng nghĩ tới, vị n&agrave;y nh&igrave;n mười phần t&uacute; kh&iacute; thiếu ni&ecirc;n nh&acirc;n, lại hội, sẽ như thế sốt ruột.<br />\r\n<br />\r\nGần như bọn hắn ba c&aacute;i vừa định ra bởi n&agrave;ng lưu lại, c&aacute;i n&agrave;y &#39;Thiếu chủ&#39; tựu ph&aacute;i người đưa tiễn n&agrave;ng sư đệ sư muội, sau đ&oacute; sai người tại lều lớn tả hữu trấn giữ.<br />\r\n<br />\r\nC&aacute;i n&agrave;y<br />\r\n<br />\r\nThiếu ni&ecirc;n n&agrave;y mi thanh mục t&uacute;, ch&iacute;nh m&igrave;nh giống như cũng kh&ocirc;ng phải qu&aacute; ăn thiệt th&ograve;i.<br />\r\n<br />\r\nL&acirc;m Tố Khinh d&ugrave;ng sức cắn m&ocirc;i dưới, khu&ocirc;n mặt n&oacute;ng hổi dường như muốn nhỏ m&aacute;u.<br />\r\n<br />\r\nTh&ocirc;i, v&igrave; sư đệ c&ugrave;ng sư muội bọn hắn có th&ecirc;̉ b&igrave;nh an rời đi, m&agrave; lại m&igrave;nh quả thật thiếu thiếu ni&ecirc;n n&agrave;y thi&ecirc;n đại &acirc;n t&igrave;nh.<br />\r\n<br />\r\nCoi như, đ&acirc;y l&agrave; b&aacute;o &acirc;n.<br />\r\n<br />\r\nL&acirc;m Tố Khinh khẽ thở d&agrave;i &acirc;m thanh, tố thủ mở ra đ&atilde; tổn hại đai lưng, đưa tay gỡ xuống Ph&aacute;p kh&iacute; ngọc tr&acirc;m, Tam Thi&ecirc;n Thanh Ti như th&aacute;c nước trượt xuống.<br />\r\n<br />\r\nMặc d&ugrave; n&agrave;ng cũng kh&ocirc;ng biết đằng sau cụ thể tr&igrave;nh tự, tại Nh&acirc;n vực nh&igrave;n qua tạp thư trong chuyện xưa, đều l&agrave; một c&acirc;u cởi &aacute;o nới d&acirc;y lưng liền trực tiếp đổi tr&agrave;ng cảnh.<br />\r\n<br />\r\nNhưng n&agrave;ng đ&atilde; lựa chọn lưu lại, liền đ&atilde; l&agrave;m xong hi sinh chuẩn bị!<br />\r\n<br />\r\nNg&ocirc; Vọng:<br />\r\n<br />\r\nĐ&aacute;ng tiếc, d&aacute;ng người rất kh&ocirc;ng tệ, c&aacute;i n&agrave;y nếu l&agrave; ch&iacute;nh m&igrave;nh b&igrave;nh thường điểm kh&ocirc;ng c&oacute; kia qu&aacute;i bệnh tốt biết bao nhi&ecirc;u.<br />\r\n<br />\r\n&quot;Ngươi kh&ocirc;ng phải l&agrave; nghĩ chiếm Bổn thiếu chủ tiện nghi &quot;<br />\r\n<br />\r\nNg&ocirc; Vọng mỉm cười lắc đầu: &quot;Đem y phục mặc tốt, liền xem như muốn ngươi phục thị phương diện n&agrave;y, tối thiểu nhất cũng muốn bồi dưỡng một ch&uacute;t t&igrave;nh cảm.&quot;<br />\r\n<br />\r\n&quot;Ừ&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh tr&agrave;n đầy nghi hoặc kh&ocirc;ng hiểu, c&oacute; ch&uacute;t luống cuống tay ch&acirc;n đem đai lưng cột l&ecirc;n.<br />\r\n<br />\r\nGi&acirc;y l&aacute;t, mấy t&ecirc;n cầm đao tr&aacute;ng h&aacute;n đi đến, đứng tại Ng&ocirc; Vọng trước người sau người, mục quang gấp chằm chằm L&acirc;m Tố Khinh.<br />\r\n<br />\r\n&quot;Hướng về ph&iacute;a trước tới.&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng đem tay tr&aacute;i tại hai vị thị vệ đại th&uacute;c liều ra trong khe hẹp, cố gắng tiếp cận ra ngo&agrave;i, &quot;Ng&oacute;n tay đụng v&agrave;o ta đầu ng&oacute;n tay.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh lập tức đầu đầy dấu chấm hỏi, c&ograve;n tưởng rằng đ&acirc;y l&agrave; c&aacute;i n&agrave;y bộ lạc một loại n&agrave;o đ&oacute; thần b&iacute; nghi thức, nửa tin nửa ngờ n&acirc;ng l&ecirc;n ng&oacute;n tay, điểm hướng Ng&ocirc; Vọng ng&oacute;n tay.<br />\r\n<br />\r\nKia mấy t&ecirc;n thị vệ đại th&uacute;c ri&ecirc;ng ph&acirc;̀n mình trừng lớn mắt, quanh người huyết kh&iacute; vỡ bờ, cơ hồ khiến L&acirc;m Tố Khinh bị thương.<br />\r\n<br />\r\nNg&ocirc; Vọng cũng kh&ocirc;ng khỏi ngừng thở.<br />\r\n<br />\r\nC&oacute; thể đ&aacute;nh ph&aacute; sao<br />\r\n<br />\r\nNh&acirc;n vực nữ tử c&oacute; lẽ c&ugrave;ng hắn qu&ecirc; qu&aacute;n những n&agrave;y nữ tử kh&ocirc;ng giống, d&ugrave; sao nh&igrave;n tựu mềm mại rất nhiều.<br />\r\n<br />\r\nCh&iacute;nh m&igrave;nh cơ hội, chẳng lẽ lại thật nếu ứng nghiệm tại Nh&acirc;n vực b&ecirc;n tr&ecirc;n<br />\r\n<br />\r\nTrong đại trướng tiếng kim rơi cũng c&oacute; thể nghe được.<br />\r\n<br />\r\nĐ&aacute;t.<br />\r\n<br />\r\nHai c&aacute;i đầu ng&oacute;n tay nhẹ nh&agrave;ng đụng v&agrave;o, L&acirc;m Tố Khinh kh&ocirc;ng ph&aacute;t gi&aacute;c g&igrave;, lại nghe r&ecirc;n l&ecirc;n một tiếng, trước mặt thiếu ni&ecirc;n lại mắt trợn trắng l&ecirc;n, th&acirc;n h&igrave;nh t&eacute; ngửa về ph&iacute;a sau, to&agrave;n th&acirc;n c&ograve;n v&ocirc; &yacute; thức co quắp mấy lần.<br />\r\n<br />\r\n&quot;Thiếu chủ! Thiếu chủ!&quot;<br />\r\n<br />\r\n&quot;Ngươi kh&ocirc;ng được qua đ&acirc;y a!&quot;<br />\r\n<br />\r\nMấy c&aacute;i h&ugrave;ng tr&aacute;ng uy vũ đại th&uacute;c khẩn trương đại hống, L&acirc;m Tố Khinh lại l&agrave; triệt để sững sờ ngay tại chỗ.<br />\r\n<br />\r\nC&aacute;i n&agrave;y, chuyện n&agrave;y l&agrave; sao<br />\r\n<br />\r\n(PS: Nam ch&iacute;nh qu&aacute;i bệnh l&agrave; trọng yếu phục b&uacute;t, sau văn c&oacute; ho&agrave;n chỉnh giải th&iacute;ch, lấy hướng b&igrave;nh thường, kh&ocirc;ng c&oacute; bất luận c&aacute;i g&igrave; độc điểm, mọi người y&ecirc;n t&acirc;m liền tốt. Cầu cất giữ, cầu phiếu đề cử! S&aacute;ch mới ng&agrave;y c&agrave;ng 7 ng&agrave;n +! Ng&agrave;y mai bắt đầu cố định thời gian đổi mới! )<br />\r\n<br />\r\nCVT: Bạch Hổ Đ&ocirc;ng = 鵺 (Tiếng Nhật dịch ra l&agrave; Nue) -- Mọi người chỉ cần search &quot;Nue&quot; l&agrave; ra h&igrave;nh ảnh, v&igrave; Tiếng Nhật kh&ocirc;ng dịch được n&ecirc;n m&igrave;nh để tạm l&agrave; Hổ Đ&ocirc;ng, v&igrave; b&ecirc;n Nhật gọi như vậy<br />\r\n<br />\r\n&quot;Con qu&aacute;i vật n&agrave;y h&igrave;nh th&aacute;i c&oacute; Ngũ H&agrave;nh thần dị h&igrave;nh tượng, tr&ecirc;n người của n&oacute; ki&ecirc;m hữu b&aacute;t phương biểu tượng động vật b&ecirc;n trong ph&iacute;a đ&ocirc;ng bắc hổ (địa chi b&ecirc;n trong dần), ph&iacute;a đ&ocirc;ng nam x&agrave; (tị), ph&iacute;a t&acirc;y nam khỉ (th&acirc;n), hướng t&acirc;y bắc cẩu c&ugrave;ng trư (tuất, hợi) đặc th&ugrave;, bởi vậy c&oacute; người cho rằng đ&acirc;y l&agrave; một đầu dung hợp can chi tư tưởng chỗ hợp th&agrave;nh hư cấu qu&aacute;i vật.&quot;</p>', '2021-11-27 21:24:02', 7, 1),
(182, 49, 'Gia giáo', '3', '<p>Thảo, một loại thực vật.<br />\r\n<br />\r\nNh&acirc;n vực nữ tử cũng kh&ocirc;ng được.<br />\r\n<br />\r\nNg&agrave;y thứ hai, trước lều, Ng&ocirc; Vọng ngồi tại dốc n&uacute;i hơi cao chỗ b&agrave;n nhỏ b&ecirc;n tr&ecirc;n, hai tay cắm ở trong đầu t&oacute;c, biểu lộ kh&ocirc;ng n&oacute;i ra được sa s&uacute;t tinh thần.<br />\r\n<br />\r\nLu&ocirc;n cảm gi&aacute;c ch&iacute;nh m&igrave;nh l&agrave; bị nhằm v&agrave;o!<br />\r\n<br />\r\nC&aacute;i n&agrave;y tặc đ&aacute;ng y&ecirc;u l&atilde;o thi&ecirc;n gia, cho hắn coi như kh&ocirc;i ng&ocirc; tướng mạo, cho hắn xuất ch&uacute;ng tu h&agrave;nh thi&ecirc;n ph&uacute;, cho hắn đại thị tộc Thiếu chủ th&acirc;n phận, lại cầm đi c&ugrave;ng kh&aacute;c ph&aacute;i tiếp x&uacute;c tư c&aacute;ch!<br />\r\n<br />\r\nC&ugrave;ng nữ tử tứ chi tiếp x&uacute;c, d&ugrave; l&agrave; c&aacute;ch quần &aacute;o, chỉ cần c&oacute; x&uacute;c cảm, liền hội trực tiếp mắt tối sầm lại, lục thần người mất.<br />\r\n<br />\r\nMang t&ecirc;n khoa học: X&uacute;c nữ h&ocirc;n thụy chứng .<br />\r\n<br />\r\nNg&ocirc; Vọng đột nhi&ecirc;n bắt đầu h&acirc;m mộ l&ecirc;n c&aacute;i kia bị hắn đưa tiễn nam tu.<br />\r\n<br />\r\nĐối phương mặc d&ugrave; tư chất k&eacute;m một ch&uacute;t, nghị lực k&eacute;m một ch&uacute;t, kh&ocirc;ng c&oacute; g&igrave; đảm đương, mục quang c&oacute; ch&uacute;t thiển cận, tu h&agrave;nh rất kh&oacute; c&oacute; c&aacute;i g&igrave; th&agrave;nh tựu<br />\r\n<br />\r\nNhưng tối thiểu nh&acirc;n gia b&igrave;nh thường a.<br />\r\n<br />\r\nMột ch&uacute;t dương quang vẩy xuống, Ng&ocirc; Vọng ngẩng đầu nh&igrave;n về ph&iacute;a kia lam nhạt sắc thi&ecirc;n kh&ocirc;ng, kh&oacute;e miệng c&oacute; ch&uacute;t co quắp mấy lần.<br />\r\n<br />\r\nHắn đại kh&aacute;i, c&oacute; thể, c&oacute; lẽ, ẩn ẩn biết vấn đề xuất hiện ở c&aacute;i g&igrave; địa phương.<br />\r\n<br />\r\nBảy t&aacute;m tuổi năm đ&oacute; c&ograve;n xảy ra chuyện g&igrave;<br />\r\n<br />\r\nCh&iacute;nh m&igrave;nh bởi v&igrave; t&ograve; m&ograve; bắt đầu nghi&ecirc;n cứu Kỳ Tinh thuật, xuất hiện thứ qu&aacute;i bệnh n&agrave;y triệu chứng l&uacute;c, ch&iacute;nh m&igrave;nh đối Kỳ Tinh thuật l&yacute; giải vừa vặn c&oacute; kh&aacute; lớn đột ph&aacute;, cảm ứng được tinh lực bản nguy&ecirc;n.<br />\r\n<br />\r\nCó th&ecirc;̉ c&aacute;i n&agrave;y lại v&igrave; c&aacute;i g&igrave;<br />\r\n<br />\r\nCụ thể nguy&ecirc;n l&yacute; g&igrave;<br />\r\n<br />\r\nNg&ocirc; Vọng truy tra hồi l&acirc;u đều kh&ocirc;ng c&oacute; nửa điểm kết quả.<br />\r\n<br />\r\nLại n&oacute;i, c&aacute;i n&agrave;y thị tộc hơn ng&agrave;n h&agrave;o c&oacute; thể l&ecirc;n chiến trường Tế Tự, cũng kh&ocirc;ng gặp bọn hắn c&oacute; c&aacute;i g&igrave; mao bệnh, c&oacute; vị tinh tế h&agrave;i tử đều sinh ch&iacute;n c&aacute;i, c&ograve;n thu được bộ lạc &#39;Chấn năng thịnh&#39; danh hiệu vinh dự.<br />\r\n<br />\r\nHắn bắt đầu ho&agrave;i nghi l&agrave; Kỳ Tinh thuật đưa đến qu&aacute;i bệnh, tựu lập tức đi t&igrave;m Kỳ Tinh thuật thầy gi&aacute;o vỡ l&ograve;ng, thị tộc b&ecirc;n trong Kỳ Tinh thuật đại l&atilde;o đời n&agrave;y th&acirc;n b&agrave; b&agrave;.<br />\r\n<br />\r\nĐương nhi&ecirc;n, c&oacute; th&acirc;n hay kh&ocirc;ng kh&ocirc;ng trọng yếu, trọng yếu l&agrave; đủ chuy&ecirc;n nghiệp.<br />\r\n<br />\r\nM&agrave; lại Ng&ocirc; Vọng kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o c&ugrave;ng kh&aacute;c ph&aacute;i tiếp x&uacute;c, hắn tổ mẫu mới l&agrave; nhất n&oacute;ng nảy c&aacute;i kia kh&ocirc;ng cẩn thận, bọn hắn H&ugrave;ng B&atilde;o bộ tộc tộc trưởng nhất mạch, coi như thật tuyệt hậu!<br />\r\n<br />\r\nNhưng phương ph&aacute;p g&igrave; đều thử một lần, mệt mỏi ngốc trong tộc thực lực cao nhất hơn mười vị Tế Tự, cũng kh&ocirc;ng thể t&igrave;m tới c&aacute;i n&agrave;y qu&aacute;i bệnh bệnh căn nguy&ecirc;n nh&acirc;n, tổ mẫu đại nh&acirc;n chỉ c&oacute; thể đem c&aacute;i n&agrave;y qu&aacute;i bệnh quy kết l&agrave;m &#39;Thần Linh ch&uacute;c ph&uacute;c&#39; .<br />\r\n<br />\r\nNg&ocirc; Vọng được một loại n&agrave;o đ&oacute; qu&aacute;i bệnh tin tức, b&acirc;y giờ bị khống chế tại rất nhỏ phạm vi b&ecirc;n trong.<br />\r\n<br />\r\nV&igrave; bộ tộc ổn định, tộc trưởng người thừa kế c&oacute; thể b&igrave;nh thường, nhưng tuyệt kh&ocirc;ng thể kỳ lạ như vậy.<br />\r\n<br />\r\nNhưng theo Ng&ocirc; Vọng ni&ecirc;n kỷ tăng trưởng, trong tộc tr&ecirc;n dưới chưa lập gia đ&igrave;nh mỹ thiếu nữ đều đ&atilde; chuẩn bị xong ch&iacute;nh m&igrave;nh &#39;Bất tỉnh c&ocirc;n&#39;, chuẩn bị l&agrave; Thiếu chủ đại nh&acirc;n trước h&ocirc;n nh&acirc;n sinh hoạt d&acirc;ng l&ecirc;n hời hợt vận g&oacute;c.<br />\r\n<br />\r\nCó th&ecirc;̉ trốn đến lúc nào, Ng&ocirc; Vọng cũng kh&ocirc;ng qu&aacute; s&aacute;ng tỏ.<br />\r\n<br />\r\nM&agrave; lại hắn l&agrave; c&aacute;i rất truyền thống nam nh&acirc;n, mười phần t&ocirc;n trọng trong tộc truyền thống, giống như kh&ocirc;ng phải qu&aacute;i bệnh, cũng kh&ocirc;ng muốn tr&aacute;nh loại n&agrave;y truyền thống chuyện tốt.<br />\r\n<br />\r\n&quot;Ai ~ Thiếu chủ đại nh&acirc;n ~ &quot;<br />\r\n<br />\r\nNơi xa truyền đến thiếu nữ ngọt ng&agrave;o ng&aacute;n k&ecirc;u gọi, Ng&ocirc; Vọng theo thanh &acirc;m nh&igrave;n lại, t&acirc;m t&igrave;nh lập tức thay đổi tốt hơn ch&uacute;t &iacute;t.<br />\r\n<br />\r\nMấy t&ecirc;n mặc b&aacute;o vằn v&aacute;y ngắn, thuộc da Tiểu Sam thiếu nữ, bưng chậu nước tại dốc thoải ph&iacute;a dưới d&ugrave;ng sức phất tay.<br />\r\n<br />\r\nDương quang vừa vặn, c&aacute;c n&agrave;ng khỏe mạnh da thịt l&oacute;e ra đại biểu trơn mềm quang trạch, bởi v&igrave; linh kh&iacute; thoải m&aacute;i m&agrave; phổ biến &#39;Mỹ lệ&#39; khu&ocirc;n mặt tr&agrave;n đầy thanh xu&acirc;n kh&iacute; tức, nhất l&agrave; y&ecirc;u th&iacute;ch cưỡi Lang lao vụt tại tr&ecirc;n thảo nguy&ecirc;n c&aacute;c n&agrave;ng, phần lớn c&oacute; thon d&agrave;i ch&acirc;n nhỏ, c&ugrave;ng kia mang theo nh&acirc;n ngư tuyến phong y&ecirc;u<br />\r\n<br />\r\nNg&ocirc; Vọng đ&aacute;y l&ograve;ng đang kh&oacute;c a, hắn đang kh&oacute;c a!<br />\r\n<br />\r\nKh&ocirc;ng bằng hắn tr&ecirc;n mặt vẫn l&agrave; duy tr&igrave; nụ cười ấm &aacute;p, đối kia mấy t&ecirc;n thiếu nữ khẽ gật đầu, dẫn xuất một trận vui vẻ tiếng hoan h&ocirc;.<br />\r\n<br />\r\nĐợi c&aacute;c c&ocirc; nương sau khi đi.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Ai.&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng ch&aacute;n nản thở d&agrave;i, bắt đầu điều chỉnh t&acirc;m t&iacute;nh.<br />\r\n<br />\r\nT&oacute;m lại lại c&oacute; biện ph&aacute;p.<br />\r\n<br />\r\nTrời kh&ocirc;ng tuyệt đường người, hắn thật vất vả sống một thế n&agrave;y, nếu như ngay cả vấn đề n&agrave;y đều vượt qua kh&ocirc;ng được, vậy sau n&agrave;y c&ograve;n n&oacute;i g&igrave; mỉm cười cửu tuyền.<br />\r\n<br />\r\nĐ&atilde; trải qua rồi nghi&ecirc;n cứu ph&aacute;t minh, thời khắc thanh tỉnh cơ!<br />\r\n<br />\r\nNg&ocirc; Vọng sau đầu xuất hiện một ch&uacute;t bọt kh&iacute;, trong đ&oacute; nổi l&ecirc;n một c&aacute;i kh&ocirc;ng t&iacute;nh phức tạp máy móc kết cấu, c&ugrave;ng loại với đời trước gội đầu giường, hắn c&oacute; thể nằm ở ph&iacute;a tr&ecirc;n, ngay ph&iacute;a tr&ecirc;n lơ lửng th&ugrave;ng gỗ tiếp tục c&oacute; nước đ&aacute; chảy ra, tư mặt của hắn.<br />\r\n<br />\r\nLiền l&agrave;<br />\r\n<br />\r\nDưới loại trạng th&aacute;i n&agrave;y c&ograve;n muốn bảo tr&igrave; phấn khởi trạng th&aacute;i, đ&uacute;ng l&agrave; phải cần một khoảng thời gian gian khổ th&iacute;ch ứng t&iacute;nh tu h&agrave;nh.<br />\r\n<br />\r\nTa kh&ocirc;ng c&oacute; thời gian đợi.<br />\r\n<br />\r\nMặc d&ugrave; Đại Hoang thế giới Nh&acirc;n tộc thọ tuổi d&agrave;i, nhưng th&agrave;nh ni&ecirc;n cũng liền hai mươi tuổi.<br />\r\n<br />\r\nNối d&otilde;i t&ocirc;ng đường vấn đề n&agrave;y nhất định phải giải quyết, kh&ocirc;ng chỉ l&agrave; bởi v&igrave; ch&iacute;nh m&igrave;nh ham muốn hưởng lạc, cũng l&agrave; v&igrave; c&aacute;i n&agrave;y thị tộc bộ lạc!<br />\r\n<br />\r\n&Acirc;n, v&igrave; bộ lạc!<br />\r\n<br />\r\n&quot;Thiếu Thiếu chủ &quot;<br />\r\n<br />\r\nPh&iacute;a sau truyền đến một tiếng rất nhỏ la l&ecirc;n, Ng&ocirc; Vọng quay đầu mắt nh&igrave;n, ngược lại l&agrave; hai mắt tỏa s&aacute;ng.<br />\r\n<br />\r\nH&ocirc;m qua cứu trở về l&uacute;c, c&aacute;i n&agrave;y Nh&acirc;n vực nữ tu phong trần mệt mỏi, mệt mỏi đầy mặt, cũng kh&ocirc;ng c&oacute; qu&aacute; mức ch&uacute; &yacute; l&uacute;c n&agrave;y n&agrave;ng nghỉ ngơi một đ&ecirc;m, đơn giản sơ trang, ho&agrave;n to&agrave;n giống như l&agrave; biến th&agrave;nh người kh&aacute;c.<br />\r\n<br />\r\nChu thoa ho&agrave;nh ph&oacute;ng thanh ti oản, liễu diệp loan mi m&acirc;u h&agrave;m thu.<br />\r\n<br />\r\nTr&ecirc;n mặt mang mấy phần trời sinh &ocirc;n nhuận, ngọc ngưng da thịt lộ ra một ch&uacute;t đỏ ửng, ngũ quan tinh xảo, c&aacute;i cổ thon d&agrave;i, từ c&aacute;i tr&aacute;n đến xương quai xanh kh&ocirc;ng c&oacute; nửa điểm t&igrave; vết, kia bởi v&igrave; mảnh mai m&agrave; hơi c&oacute; vẻ cao gầy tư th&aacute;i, cũng dễ d&agrave;ng để cho người ta mục quang lưu luyến qu&ecirc;n về.<br />\r\n<br />\r\nN&agrave;ng c&ograve;n nhớ r&otilde; ng&agrave;y h&ocirc;m qua t&igrave;nh h&igrave;nh, gặp Ng&ocirc; Vọng sau khi tỉnh lại sa s&uacute;t tinh thần hồi l&acirc;u, t&oacute;m lại nhịn kh&ocirc;ng được hướng về ph&iacute;a trước nh&igrave;n xem.<br />\r\n<br />\r\nNgười thiếu chủ n&agrave;y cũng tr&aacute;ch đ&aacute;ng thương, tuổi c&ograve;n trẻ tựu<br />\r\n<br />\r\n&quot;Ừm, &quot; Ng&ocirc; Vọng ứng tiếng, thu hồi d&ograve; x&eacute;t n&agrave;ng mục quang, tại b&ecirc;n h&ocirc;ng lớp da b&ecirc;n trong lấy ra một c&aacute;i b&agrave;n nhỏ, n&eacute;m tới xa nửa trượng an to&agrave;n vị tr&iacute;.<br />\r\n<br />\r\n&quot;Tọa hạ t&acirc;m sự đi.&quot;<br />\r\n<br />\r\n&quot;Tốt, &quot; L&acirc;m Tố Khinh đ&aacute;p ứng một tiếng, thu nạp v&aacute;y, kh&eacute;p lại hai ch&acirc;n, c&oacute; ch&uacute;t kh&ocirc;ng quen ngồi tại c&aacute;i n&agrave;y b&agrave;n nhỏ b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nNg&ocirc; Vọng n&oacute;i: &quot;Ngươi cũng c&oacute; thể ngồi xuống.&quot;<br />\r\n<br />\r\n&quot;Dạng n&agrave;y liền tốt, &quot; L&acirc;m Tố Khinh đưa tay sửa lại xuống b&ecirc;n tai m&aacute;i t&oacute;c, &quot;Thiếu chủ, ng&agrave;i kh&ocirc;ng c&oacute; chuyện g&igrave; sao &quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng cho ph&eacute;p n&acirc;ng.&quot;<br />\r\n<br />\r\n&quot;Ừm, kh&ocirc;ng đề cập tới, &quot; L&acirc;m Tố Khinh nh&aacute;y mắt mấy c&aacute;i, nhỏ giọng hỏi, &quot;L&agrave; tu h&agrave;nh xảy ra sai s&oacute;t sao &quot;<br />\r\n<br />\r\nNg&ocirc; Vọng kh&ocirc;ng c&oacute; trả lời, chỉ l&agrave; vỗ tay ph&aacute;t ra tiếng, một b&ecirc;n c&oacute; hai t&ecirc;n thị vệ giơ l&ecirc;n c&aacute;i phủ k&iacute;n tr&acirc;n qu&yacute; da l&ocirc;ng ghế nằm, chuyển đến c&aacute;i da l&ocirc;ng may cực đại che nắng d&ugrave;.<br />\r\n<br />\r\nLại c&oacute; thị vệ chuyển đến một c&aacute;i trang nửa vời th&ugrave;ng gỗ, b&ecirc;n trong ng&acirc;m bảy, t&aacute;m c&aacute;i v&ograve; rượu.<br />\r\n<br />\r\nNg&ocirc; Vọng ng&oacute;n giữa tay phải tại trong th&ugrave;ng gỗ lung lay, trong đ&oacute; lập tức tr&ocirc;i nổi l&ecirc;n một tầng khối băng.<br />\r\n<br />\r\nA, sinh hoạt.<br />\r\n<br />\r\nCũng ch&iacute;nh l&agrave; phổ th&ocirc;ng chỗ nằm, phổ th&ocirc;ng chỗ lăn lộn, phổ th&ocirc;ng dương quang, phổ th&ocirc;ng say.<br />\r\n<br />\r\n&quot;Hướng gi&oacute; kh&ocirc;ng đ&uacute;ng đ&acirc;y &quot;<br />\r\n<br />\r\nNg&ocirc; Vọng l&acirc;̉m b&acirc;̉m một tiếng, b&ecirc;n lập tức c&oacute; thị vệ c&uacute;i đầu chạy xa, nơi xa bụi cỏ nhảy ra hai vị mặc &aacute;o cho&agrave;ng, &ocirc;m thủy tinh cầu Tế Tự, niệm l&ecirc;n li&ecirc;n tiếp &acirc;m điệu cổ qu&aacute;i.<br />\r\n<br />\r\nRất nhanh, chầm chậm gi&oacute; nhẹ từ Tuyết Sơn phương hướng thổi tới, kh&ocirc;ng c&oacute; cho tr&ecirc;n thảo nguy&ecirc;n liệt nhật ph&aacute;t uy cơ hội, l&agrave; n&agrave;y n&uacute;i sườn n&uacute;i đưa tới một tia m&aacute;t mẻ.<br />\r\n<br />\r\nL&acirc;m Tố Khinh:<br />\r\n<br />\r\n&quot;Ngươi t&ecirc;n l&agrave; g&igrave;&quot; Ng&ocirc; Vọng đột nhi&ecirc;n hỏi.<br />\r\n<br />\r\nL&acirc;m Tố Khinh c&ocirc;ng việc đ&aacute;p: &quot;Tố Khinh.&quot;<br />\r\n<br />\r\n&quot;Tốt phổ th&ocirc;ng.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y &quot;<br />\r\n<br />\r\n&quot;Sư m&ocirc;n, tu h&agrave;nh tuổi t&aacute;c, tự th&acirc;n tuổi t&aacute;c, l&agrave;m tự giới thiệu đi.&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng lạnh nhạt n&oacute;i: &quot;Ngươi sau n&agrave;y s&aacute;u năm muốn đi theo b&ecirc;n cạnh ta, ta sẽ cho ngươi th&ugrave; lao tương ứng, việc ngươi cần cũng kh&ocirc;ng phải cởi quần &aacute;o chuyện sau đ&oacute;, m&agrave; l&agrave; gi&uacute;p ta nghiệm chứng một ch&uacute;t tr&ecirc;n tu h&agrave;nh l&yacute; luận, thuận tiện l&agrave; ta kỹ c&agrave;ng giới thiệu Nh&acirc;n vực t&igrave;nh h&igrave;nh.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh hơi suy tư, nhỏ giọng n&oacute;i:<br />\r\n<br />\r\n&quot;Ta thuở nhỏ b&aacute;i sư Thanh Phong Vọng Nguyệt m&ocirc;n, đi theo sư phụ Tả Động ch&acirc;n nh&acirc;n tu h&agrave;nh đ&atilde; c&oacute; hơn ba mươi năm, trong m&ocirc;n tu sĩ mặc d&ugrave; kh&ocirc;ng nhiều, nhưng mọi người lễ k&iacute;nh hỗ trợ, tổng đi Ti&ecirc;n Lộ.&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng gật gật đầu, tổng kết n&oacute;i: &quot;Phổ th&ocirc;ng tiểu m&ocirc;n ph&aacute;i xuất th&acirc;n, tư chất thường thường, năm nay hơn bốn mươi tuổi.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh c&aacute;i tr&aacute;n trong nh&aacute;y mắt treo đầy hắc tuyến, vội n&oacute;i: &quot;Chỉ cần ta bước v&agrave;o Ngưng Đan cảnh, liền c&oacute; thể c&oacute; ng&agrave;n năm thọ tuổi, hơn bốn mươi tuổi cũng bất qu&aacute; l&agrave;, bất qu&aacute; l&agrave; &quot;<br />\r\n<br />\r\nNg&ocirc; Vọng lộ ra mấy phần &ocirc;n nhu mỉm cười, mỉm cười gật đầu, dựa v&agrave;o &quot;Ừ&quot;, &quot;Ừ&quot; .<br />\r\n<br />\r\nL&acirc;m Tố Khinh kh&oacute;e miệng cong l&ecirc;n, y&ecirc;n lặng nghi&ecirc;ng đầu đi, cố gắng b&igrave;nh phục c&aacute;i tr&aacute;n thập tự (十) g&acirc;n.<br />\r\n<br />\r\nGh&ecirc; tởm, l&agrave;m sao lại như thế l&agrave;m giận!<br />\r\n<br />\r\n&quot;Đ&uacute;ng rồi, &quot; Ng&ocirc; Vọng b&igrave;nh tĩnh chỗ cắm một đao, &quot;Ngươi sư đệ c&ugrave;ng sư muội trong đ&ecirc;m đi, vận kh&iacute; kh&ocirc;ng tệ vừa vặn c&oacute; một chiếc Nh&acirc;n vực thuyền lớn đi ngang qua.<br />\r\n<br />\r\nXem ngươi sư đệ d&aacute;ng vẻ, giống như l&aacute;i thuyền tối nay, hắn c&oacute; thể đem thuyền nhấc l&ecirc;n đi.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh th&acirc;n h&igrave;nh l&acirc;m v&agrave;o &acirc;m ảnh, tay tr&aacute;i c&oacute; ch&uacute;t v&ocirc; lực n&acirc;ng l&ecirc;n, lại kh&ocirc;ng chỗ sắp đặt, cuối c&ugrave;ng chỉ c&oacute; thể đỡ lấy c&aacute;nh tay phải.<br />\r\n<br />\r\n&quot;Thật buồn cười đ&uacute;ng kh&ocirc;ng &quot;<br />\r\n<br />\r\nN&agrave;ng tự lẩm bẩm.<br />\r\n<br />\r\nNg&ocirc; Vọng vừa định n&oacute;i v&agrave;i lời lời an ủi, d&ugrave; sao đ&acirc;y l&agrave; về sau ch&iacute;nh m&igrave;nh muốn ỷ lại &#39;Gia sư&#39;, nhưng c&ograve;n chưa kịp mở miệng, liền nghe đến L&acirc;m Tố Khinh cười lạnh một tiếng.<br />\r\n<br />\r\n&quot;Tu h&agrave;nh mấy chục năm chẳng l&agrave;m n&ecirc;n tr&ograve; trống g&igrave;, hơn mười năm t&acirc;m huyết dạy bảo sư đệ c&aacute;ch ta m&agrave; đi, sư phụ bị thương nặng tại trong m&ocirc;n cũng kh&ocirc;ng c&oacute; người hỏi thăm.&quot;<br />\r\n<br />\r\nN&agrave;ng ngẩng đầu l&ecirc;n, trong mắt ngậm lấy nước mắt, b&ecirc;n miệng mang theo tiếng kh&oacute;c nức nở:<br />\r\n<br />\r\n&quot;Ta thật rất k&eacute;m cỏi đ&uacute;ng kh&ocirc;ng &quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y &quot;<br />\r\n<br />\r\n&quot;Hơn bốn mươi tuổi vẫn chỉ l&agrave; Quy Nguy&ecirc;n trung kỳ rất k&eacute;m cỏi c&oacute; đ&uacute;ng kh&ocirc;ng &quot;<br />\r\n<br />\r\n&quot;Ừ&quot;<br />\r\n<br />\r\n&quot;Luyện đan học được hơn mười năm vẫn l&agrave; nổ l&ocirc; rất k&eacute;m cỏi c&oacute; đ&uacute;ng kh&ocirc;ng &quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; c&aacute;i mới nhập m&ocirc;n sư muội c&oacute; linh kh&iacute;, tựu rất k&eacute;m cỏi c&oacute; đ&uacute;ng kh&ocirc;ng &quot;<br />\r\n<br />\r\n&quot;Mọi thứ đều ch&iacute;nh m&igrave;nh khi&ecirc;ng, sẽ kh&ocirc;ng theo sư đệ nũng nịu, để sư đệ cảm gi&aacute;c c&oacute; ch&uacute;t tr&oacute;i buộc, cũng rất k&eacute;m cỏi c&oacute; đ&uacute;ng kh&ocirc;ng &quot;<br />\r\n<br />\r\n&quot;Oa ta ch&iacute;nh l&agrave; c&aacute;i phổ th&ocirc;ng nữ tu sĩ rất k&eacute;m cỏi c&oacute; đ&uacute;ng kh&ocirc;ng!&quot;<br />\r\n<br />\r\n&quot;Nương ta c&oacute; lỗi với ngươi, ta liền n&ecirc;n nghe ngươi tại ph&agrave;m tục t&igrave;m người gả, tại sao phải đi sửa ti&ecirc;n th&agrave;nh đạo!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nNh&igrave;n trước mắt c&aacute;i n&agrave;y che mặt kh&oacute;c lớn nữ tu, Ng&ocirc; Vọng cũng c&oacute; ch&uacute;t ch&acirc;n tay luống cuống.<br />\r\n<br />\r\nLại nói, Nh&acirc;n vực b&ecirc;n kia kh&ocirc;ng phải c&oacute; hệ thống c&ocirc;ng ph&aacute;p tu h&agrave;nh, tu ti&ecirc;n giảng cứu đạo t&acirc;m b&igrave;nh ổn, tu ma giảng cứu truy t&igrave;m nguồn gốc, c&aacute;i n&agrave;y đại tỷ, a kh&ocirc;ng, a di n&agrave;y đạo t&acirc;m l&agrave;m sao yếu ớt như vậy.<br />\r\n<br />\r\nNg&ocirc; Vọng quay đầu nh&igrave;n một ch&uacute;t, t&igrave;m c&aacute;i nh&igrave;n coi như mi thanh mục t&uacute;, hơi tuổi trẻ điểm thị vệ, đạo nhất c&acirc;u:<br />\r\n<br />\r\n&quot;Đến b&ecirc;n n&agrave;y điểm, để n&agrave;ng cảm thụ cảm gi&aacute;c nam tử h&aacute;n kh&iacute; ph&aacute;ch, cho n&agrave;ng điểm an ủi.&quot;<br />\r\n<br />\r\nThị vệ kia lập tức h&eacute; miệng nh&iacute;u m&agrave;y, sắc mặt trắng bệch, nhỏ giọng n&oacute;i:<br />\r\n<br />\r\n&quot;Thiếu chủ, n&agrave;ng kh&ocirc;ng ai muốn, ta kết h&ocirc;n.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh tiếng kh&oacute;c im bặt m&agrave; dừng, ngẹo đầu, kh&oacute;e miệng bay ra một tia kh&oacute;i trắng, ngưng tụ th&agrave;nh nho nhỏ hồn ph&aacute;ch, trong miệng lẩm bẩm &#39;Ta c&ograve;n l&agrave; tự đoạn t&acirc;m mạch t&iacute;nh to&aacute;n&#39; loại h&igrave;nh.<br />\r\n<br />\r\nNg&ocirc; Vọng k&eacute;m ch&uacute;t tựu cười ra tiếng.<br />\r\n<br />\r\n&quot;Ngươi kỳ thật thật kh&ocirc;ng tệ, &quot; Ng&ocirc; Vọng chậm r&atilde;i n&oacute;i, &quot;Tr&ecirc;n đời kh&ocirc;ng c&oacute; nhiều như vậy thi&ecirc;n t&agrave;i, c&oacute; lẽ ngươi b&acirc;y giờ cũng chỉ l&agrave; tại hậu t&iacute;ch bạc ph&aacute;t t&iacute;ch lũy giai đoạn.<br />\r\n<br />\r\nCh&uacute;ng ta đều l&agrave; người b&igrave;nh thường, chỉ bất qu&aacute; am hiểu lĩnh vực c&oacute; chỗ kh&aacute;c biệt th&ocirc;i.<br />\r\n<br />\r\nCũng tỷ như vừa rồi, ngươi theo lều vải đi tới l&uacute;c, cũng cho ta nh&igrave;n kh&ocirc;ng chuyển mắt, hiểu được ngươi sinh c&oacute; phần đẹp.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh bờ m&ocirc;i run rẩy: &quot;Thiếu chủ &quot;<br />\r\n<br />\r\n&quot;Bảo tr&igrave; cự ly.&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng đưa tay l&agrave;m c&aacute;i cự tuyệt thủ thế, &quot;Buổi chiều dẫn ngươi đi tr&ecirc;n thảo nguy&ecirc;n dạo chơi, bu&ocirc;ng lỏng một ch&uacute;t t&acirc;m t&igrave;nh, đ&ecirc;m nay ta sẽ gi&uacute;p ngươi chế định dạy ta chương tr&igrave;nh học.&quot;<br />\r\n<br />\r\n&quot;Chương tr&igrave;nh học &quot;<br />\r\n<br />\r\n&quot;Ừm, &quot; Ng&ocirc; Vọng nghi&ecirc;m t&uacute;c gật gật đầu, &quot;C&oacute; thể l&agrave; phương ph&aacute;p tu h&agrave;nh, giống như ngươi m&ocirc;n quy kh&ocirc;ng hạn chế n&oacute;i.<br />\r\n<br />\r\nCũng c&oacute; thể l&agrave; &acirc;m luật, phục sức, lễ nghi, thậm ch&iacute; l&agrave; ngươi thấy biết phong thổ, Nh&acirc;n vực c&aacute;c gia tu h&agrave;nh t&ocirc;ng m&ocirc;n một ch&uacute;t B&aacute;t Qu&aacute;i tin tức.<br />\r\n<br />\r\nTa c&acirc;̀n kiến thức của ngươi c&ugrave;ng kiến thức.&quot;<br />\r\n<br />\r\n&quot;Thiếu chủ ng&agrave;i muốn đi Nh&acirc;n vực&quot; L&acirc;m Tố Khinh nhỏ giọng hỏi.<br />\r\n<br />\r\n&quot;C&oacute; lẽ vậy.&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng nh&igrave;n về ph&iacute;a thảo nguy&ecirc;n ch&acirc;n trời, tiếng n&oacute;i cũng c&agrave;ng ph&aacute;t ra mộng ảo.<br />\r\n<br />\r\n&quot;Tạm thời tới n&oacute;i, ta c&ograve;n l&agrave; nơi n&agrave;y duy nhất Thiếu chủ, c&acirc;̀n đối t&iacute;n nhiệm của ta tộc nh&acirc;n phụ tr&aacute;ch.<br />\r\n<br />\r\nNhưng giống như thời cơ ch&iacute;n muồi, ta nhất định phải đi Nh&acirc;n vực nh&igrave;n xem.&quot;<br />\r\n<br />\r\nHắn trong mắt l&oacute;ng l&aacute;nh điểm điểm tinh quang, ở một b&ecirc;n L&acirc;m Tố Khinh trong mắt, đ&uacute;ng l&agrave; như vậy sặc sỡ lo&aacute; mắt.<br />\r\n<br />\r\nNg&ocirc; Vọng định tiếng n&oacute;i:<br />\r\n<br />\r\n&quot;Ta nhất định muốn t&igrave;m tới giải khai tr&ecirc;n người của ta thứ qu&aacute;i bệnh n&agrave;y biện ph&aacute;p!&quot;<br />\r\n<br />\r\n&quot;Thiếu chủ &quot;<br />\r\n<br />\r\n&quot;Kết h&ocirc;n trước đ&oacute; hảo hảo s&oacute;ng hắn mấy năm!&quot;<br />\r\n<br />\r\n&quot;Khục! Khụ khụ!&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh che ngực một trận ho khan, b&ecirc;n cạnh bọn thị vệ đối Ng&ocirc; Vọng quăng tới t&aacute;n thưởng &aacute;nh mắt.<br />\r\n<br />\r\nC&aacute;i n&agrave;y, mới l&agrave; nh&agrave; bọn hắn tương lai Thủ Lĩnh phong phạm.</p>', '2021-11-27 21:24:27', 4, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(183, 49, 'Tương xích', '4', '<p>Trời tối người y&ecirc;n, bận rộn cả ng&agrave;y H&ugrave;ng B&atilde;o tộc mọi người, ngh&ecirc;nh đ&oacute;n Thi&ecirc;n Địa ở giữa y&ecirc;n ả nhất thời khắc.<br />\r\n<br />\r\nL&acirc;m Tố Khinh rũ cụp lấy đầu, k&eacute;o lấy mệt mỏi th&acirc;n thể trở về trướng bồng của m&igrave;nh, tự &iacute;ch cốc về sau rất &iacute;t ngủ n&agrave;ng, đột nhi&ecirc;n nghĩ ng&atilde; đầu tựu ngủ.<br />\r\n<br />\r\nN&agrave;y c&aacute;i thiếu chủ thật sự l&agrave;!<br />\r\n<br />\r\nQu&aacute; mệt nhọc.<br />\r\n<br />\r\nMột đ&ecirc;m, l&ocirc;i k&eacute;o n&agrave;ng hỏi lung tung n&agrave;y kia, c&ograve;n để n&agrave;ng cầm một c&aacute;i gậy gỗ nhỏ, đứng tại phiến đ&aacute; trước, mạnh mẽ vẽ tay Nh&acirc;n vực Cương Vực đồ!<br />\r\n<br />\r\nM&agrave; t&ecirc;n kia ngưỡng tại b&agrave;y khắp tr&acirc;n qu&yacute; hung th&uacute; da tr&ecirc;n giường &ecirc;m, ăn xong hoa quả ăn thịt nướng, ăn xong thịt nướng ăn canh canh!<br />\r\n<br />\r\nĐ&aacute;ng giận nhất l&agrave; l&agrave;, kia thiếu đ&aacute;nh Thiếu chủ hỏi n&agrave;ng c&oacute; ăn hay kh&ocirc;ng, n&agrave;ng kh&aacute;ch kh&iacute; n&oacute;i c&acirc;u đ&atilde; t&iacute;ch cốc, t&ecirc;n kia v&acirc;̣y mà ph&aacute;i người đi cho n&agrave;ng g&oacute;p nhặt một b&aacute;t hạt sương!<br />\r\n<br />\r\n&quot;Ai &quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh c&uacute;i đầu thở d&agrave;i, hữu kh&iacute; v&ocirc; lực ng&atilde; sấp tại giường ở giữa, trong l&uacute;c nhất thời trở m&igrave;nh đều kh&ocirc;ng muốn trở m&igrave;nh.<br />\r\n<br />\r\nSau n&agrave;y m&igrave;nh, liền muốn tại Bắc D&atilde; tu h&agrave;nh sao<br />\r\n<br />\r\nS&aacute;u năm cũng kh&ocirc;ng t&iacute;nh l&agrave; qu&aacute; l&acirc;u, đối với ch&acirc;n ch&iacute;nh lợi hại tu sĩ m&agrave; n&oacute;i, cũng bất qu&aacute; l&agrave; lần một lần hai bế quan thời gian.<br />\r\n<br />\r\nNơi cổ tay v&ograve;ng ngọc lấy ra một mặt ngọc b&agrave;i, L&acirc;m Tố Khinh ng&oacute;n tay nhẹ nh&agrave;ng hoạt động l&ecirc;n, đem từng c&aacute;i chữ viết để v&agrave;o ngọc b&agrave;i b&ecirc;n trong.<br />\r\n<br />\r\nNgọc b&agrave;i &oacute;ng &aacute;nh &aacute;nh s&aacute;ng nhạt chiếu đến n&agrave;ng thổi qua liền ph&aacute; khu&ocirc;n mặt, kia trương tr&ecirc;n khu&ocirc;n mặt nhỏ nhắn thỉnh thoảng lộ ra mấy phần suy tư, viết một trận, thật mỏng ống tay &aacute;o từ thủ oản c&oacute; ch&uacute;t trượt xuống, c&aacute;nh tay ngọc chống đỡ gương mặt hơi xuất thần.<br />\r\n<br />\r\nTu h&agrave;nh ng&agrave;y d&agrave;i, đột ph&aacute; gian nan, n&agrave;ng t&oacute;m lại kh&oacute; tr&aacute;nh khỏi lại c&oacute; ch&uacute;t &iacute;t buồn khổ, dần dần dưỡng th&agrave;nh đem ch&iacute;nh m&igrave;nh t&acirc;m sự ghi v&agrave;o k&iacute; sự ngọc ph&ugrave; thói quen.<br />\r\n<br />\r\nViết viết, L&acirc;m Tố Khinh trước mắt kh&ocirc;ng khỏi hiện ra một ch&uacute;t h&igrave;nh tượng.<br />\r\n<br />\r\nXanh thẳm bầu trời, m&acirc;y trắng phấp phới ph&iacute;a dưới, n&agrave;ng ngồi tại Cự Lang tr&ecirc;n lưng theo gi&oacute; lao vụt<br />\r\n<br />\r\nHo&agrave;ng h&ocirc;n tiệc tối l&uacute;c, từng vị khu&ocirc;n mặt b&ecirc;n tr&ecirc;n tr&agrave;n đầy nhiệt t&igrave;nh H&ugrave;ng B&atilde;o tộc thiếu nữ l&ocirc;i k&eacute;o n&agrave;ng khi&ecirc;u vũ đo&agrave;n tụ<br />\r\n<br />\r\nS&aacute;ch, Thiếu chủ b&ecirc;n người c&ograve;n c&oacute; mấy c&aacute;i kh&ocirc;ng c&oacute; bị đ&aacute;nh bất tỉnh qua kh&ocirc;i ng&ocirc; thị vệ<br />\r\n<br />\r\nH&igrave;nh tượng nhất chuyển, lại l&agrave; quen thuộc sơn m&ocirc;n trong rừng tr&uacute;c, gắn b&oacute; thắm thiết sư đệ c&ugrave;ng sư muội<br />\r\n<br />\r\n&#39;Tại c&aacute;i n&agrave;y Bắc D&atilde; Man Hoang chi địa, kỳ thật cũng kh&ocirc;ng tệ nha.&#39;<br />\r\n<br />\r\nTr&ecirc;n thảo nguy&ecirc;n hạt sương cũng rất ngọt.<br />\r\n<br />\r\nL&acirc;m Tố Khinh kh&oacute;e miệng lộ ra một ch&uacute;t mỉm cười, xem ngọc ph&ugrave; b&ecirc;n trong mới viết nhập mấy trăm chữ, thỏa m&atilde;n đưa n&oacute; thu hồi v&ograve;ng ngọc, m&ecirc; m&ecirc; mang mang liền muốn như thế ngủ đi qua.<br />\r\n<br />\r\nD&ugrave;ng ngồi xuống thay thế giấc ngủ, xem như tu sĩ cơ bản chức nghiệp tố dưỡng.<br />\r\n<br />\r\nNhưng h&ocirc;m nay, L&acirc;m Tố Khinh đột nhi&ecirc;n nghĩ tho&aacute;ng c&aacute;i tiểu soa.<br />\r\n<br />\r\nPhi&ecirc;u phi&ecirc;u miểu mịt mù giống như huyền diệu mộng cảnh, tự tại Ti&ecirc;u Dao kh&oacute; được v&ocirc; c&acirc;u m&ocirc;ng lung.<br />\r\n<br />\r\nĐột nhi&ecirc;n<br />\r\n<br />\r\n&quot;L&acirc;m ti&ecirc;n tử, L&acirc;m ti&ecirc;n tử &quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh bị người tỉnh lại, mở mắt một c&aacute;i chớp mắt, ba tấm mang theo mũ gi&aacute;p tr&aacute;ng h&aacute;n khu&ocirc;n mặt tr&agrave;n ngập &aacute;nh mắt, quả thực đem n&agrave;ng dọa đến giật m&igrave;nh.<br />\r\n<br />\r\nChe cổ &aacute;o, trở m&igrave;nh, cầm kiếm, một bộ động t&aacute;c h&agrave;nh v&acirc;n lưu thủy, liền l&agrave; t&oacute;c d&agrave;i hơi c&oacute; ch&uacute;t lộn xộn.<br />\r\n<br />\r\n&quot;C&aacute;c ngươi muốn l&agrave;m c&aacute;i g&igrave;!&quot;<br />\r\n<br />\r\nC&aacute;i n&agrave;y ba t&ecirc;n thị vệ vội v&agrave;ng lui lại nửa bước, cho thấy ch&iacute;nh m&igrave;nh kh&ocirc;ng c&oacute; địch &yacute;.<br />\r\n<br />\r\n&quot;L&acirc;m ti&ecirc;n tử, Thiếu chủ xin ng&agrave;i lập tức đi qua một chuyến, c&oacute; việc gấp thương lượng.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh lập tức tỉnh t&aacute;o lại, thu hồi trường kiếm lập tức đ&aacute;p ứng &acirc;m thanh, đi theo ba người đi s&aacute;t v&aacute;ch lều vải, thầm mắng m&igrave;nh v&igrave; c&aacute;i g&igrave; như thế đại &yacute;, như vậy t&igrave;nh cảnh lại vẫn c&oacute; thể ngủ đ&ecirc;́n.<br />\r\n<br />\r\nĐợi n&agrave;ng đến Ng&ocirc; Vọng lều lớn, bị dẫn đi nơi hẻo l&aacute;nh, mới ngạc nhi&ecirc;n ph&aacute;t hiện nơi n&agrave;y cất giấu một c&aacute;i &Aacute;m M&ocirc;n, li&ecirc;n th&ocirc;ng một chỗ kh&aacute;c hơi nhỏ lều vải.<br />\r\n<br />\r\nNơi n&agrave;y dĩ nhi&ecirc;n ch&iacute;nh l&agrave; Ng&ocirc; Vọng ph&ograve;ng ngủ.<br />\r\n<br />\r\nNơi đ&acirc;y bố tr&iacute; c&oacute; ch&uacute;t hoa lệ, L&acirc;m Tố Khinh lại kh&ocirc;ng t&acirc;m t&igrave;nh d&ograve; x&eacute;t, nh&igrave;n xem h&igrave;nh như c&oacute; ch&uacute;t &iacute;t suy yếu, nằm tại tr&ecirc;n giường lớn Ng&ocirc; Vọng, vội hỏi:<br />\r\n<br />\r\n&quot;Thiếu chủ, thế n&agrave;o &quot;<br />\r\n<br />\r\n&Aacute;ch, ch&iacute;nh m&igrave;nh l&agrave;m sao đ&atilde; c&oacute; chút quan t&acirc;m gia hỏa n&agrave;y.<br />\r\n<br />\r\n&quot;Mau tới.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nNg&ocirc; Vọng n&acirc;ng tay phải l&ecirc;n, dường như muốn n&agrave;ng n&acirc;ng.<br />\r\n<br />\r\nL&acirc;m Tố Khinh bước nhanh ph&oacute;ng tới b&ecirc;n giường, vừa muốn đỡ lấy Ng&ocirc; Vọng b&agrave;n tay l&uacute;c, lại kh&ocirc;ng khỏi dừng lại động t&aacute;c.<br />\r\n<br />\r\nThiếu chủ bệnh.<br />\r\n<br />\r\nXem Thiếu chủ mặt lộ vẻ quyện sắc, kh&ocirc;ng phải l&agrave; bởi v&igrave; đang nghi&ecirc;n cứu như thế n&agrave;o vượt qua như vậy chứng bệnh<br />\r\n<br />\r\nAi, tuổi c&ograve;n trẻ liền phải bệnh n&agrave;y, cả đời n&agrave;y gần như c&ugrave;ng nữ sắc ngăn c&aacute;ch, mặc d&ugrave; n&agrave;ng L&acirc;m Tố Khinh l&agrave; người trong tu h&agrave;nh, vốn n&ecirc;n &iacute;t ham muốn, nhưng vẫn như cũ hiểu được, người thiếu ni&ecirc;n trước mắt n&agrave;y người c&oacute; ch&uacute;t đ&aacute;ng thương.<br />\r\n<br />\r\nC&oacute; được quyền thế lại như thế n&agrave;o<br />\r\n<br />\r\nC&oacute; được thực lực mạnh mẽ lại c&oacute; thể thế n&agrave;o<br />\r\n<br />\r\nHắn thủy chung l&agrave; c&oacute; tiếc nuối, nh&acirc;n sinh kh&ocirc;ng c&oacute; khả năng vi&ecirc;n m&atilde;n.<br />\r\n<br />\r\n&quot;Thiếu chủ, ta n&ecirc;n l&agrave;m như thế n&agrave;o &quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh &ocirc;n nhu hỏi, một tia nghịch ngợm ánh trăng chiếu v&agrave;o n&agrave;ng mở đầu, lại để cho n&agrave;ng nh&igrave;n c&oacute; một loại kh&oacute; tả thần vận.<br />\r\n<br />\r\n&quot;Đụng ta tho&aacute;ng c&aacute;i a, c&aacute;i n&agrave;y c&ograve;n phải hỏi &quot;<br />\r\n<br />\r\nNg&ocirc; Vọng quay đầu mắt nh&igrave;n L&acirc;m Tố Khinh, mang theo buồn bực lẩm bẩm: &quot;C&oacute; chút mất ngủ, vừa vặn cho ngươi mượn d&ugrave;ng tho&aacute;ng c&aacute;i, nhớ r&otilde; trưa mai lại đ&aacute;nh thức ta, ta ngay tại lớn th&acirc;n thể c&acirc;̀n sung t&uacute;c giấc ngủ.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh:<br />\r\n<br />\r\nPhi!<br />\r\n<br />\r\nB&oacute;p chết ngươi c&oacute; được hay kh&ocirc;ng a!<br />\r\n<br />\r\n&quot;Thất thần l&agrave;m g&igrave; &quot;<br />\r\n<br />\r\n&quot;Ai, tới, Thiếu chủ ng&agrave;i nghỉ ngơi thật tốt, ng&agrave;y mai gặp nha.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh mang theo ngọt ng&agrave;o giả cười, ng&oacute;n tay d&ugrave;ng sức chọc lấy xuống Ng&ocirc; Vọng c&aacute;nh tay, c&aacute;i sau trong nh&aacute;y mắt nghi&ecirc;ng đầu ngủ m&ecirc; đi qua.<br />\r\n<br />\r\nMấy c&aacute;i thị vệ theo b&ecirc;n cạnh thiểm đến, đem L&acirc;m Tố Khinh ngăn c&aacute;ch b&ecirc;n ngo&agrave;i.<br />\r\n<br />\r\nBọn hắn ngược lại l&agrave; kh&ocirc;ng c&oacute; nửa phần thư gi&atilde;n.<br />\r\n<br />\r\nLại qua một ng&agrave;y.<br />\r\n<br />\r\nSau giờ ngọ trong đại trướng, chỉ c&ograve;n lại c&oacute; c&ocirc; nam quả nữ hai th&acirc;n ảnh.<br />\r\n<br />\r\n&quot;Tố Khinh, c&oacute; thể chứ &quot;<br />\r\n<br />\r\n&quot;Thiếu chủ nếu c&oacute; c&acirc;̀n, ta b&ecirc;n n&agrave;y l&agrave; kh&ocirc;ng c&oacute; bất cứ vấn đề g&igrave;.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y thật c&oacute; thể chứ &quot;<br />\r\n<br />\r\n&quot;Ng&agrave;i sao so với ta một c&aacute;i nữ nhi gia c&ograve;n b&agrave; mẹ ta đều kh&ocirc;ng th&egrave;m để &yacute; những thứ n&agrave;y.&quot;<br />\r\n<br />\r\n&quot;Kia, tới đi.&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng mục quang s&aacute;ng rực chỗ nh&igrave;n chăm ch&uacute; l&ecirc;n L&acirc;m Tố Khinh: &quot;Kh&ocirc;ng n&ecirc;n l&agrave;m kh&oacute;, ta chỉ cần cơ sở c&ocirc;ng ph&aacute;p tu h&agrave;nh, thử một ch&uacute;t m&igrave;nh liệu c&oacute; thể tu h&agrave;nh.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh mang theo bất đắc dĩ ứng tiếng, giải th&iacute;ch n&oacute;i:<br />\r\n<br />\r\n&quot;Ch&uacute;ng ta Thanh Phong Vọng Nguyệt m&ocirc;n cho đệ tử d&ugrave;ng Tr&uacute;c Cơ c&ocirc;ng ph&aacute;p, kỳ thật cũng kh&ocirc;ng phải l&agrave; ch&uacute;ng ta sơn m&ocirc;n độc hữu, m&agrave; l&agrave; Nh&acirc;n vực lưu truyền mấy quyển c&ocirc;ng ph&aacute;p cơ bản một trong.<br />\r\n<br />\r\nChỉ c&oacute; những c&aacute;i kia đại t&ocirc;ng m&ocirc;n lại c&oacute; ch&iacute;nh m&igrave;nh Tr&uacute;c Cơ c&ocirc;ng ph&aacute;p, để bọn hắn đệ tử tu vi cao th&acirc;m sau c&oacute; thể trực tiếp chuyển tu cao si&ecirc;u hơn c&ocirc;ng ph&aacute;p.<br />\r\n<br />\r\nSở dĩ Thiếu chủ kh&ocirc;ng cần lo lắng, ta chỉ cần kh&ocirc;ng thổ lộ Vọng Nguyệt Thanh T&acirc;m quyết, liền sẽ kh&ocirc;ng vi phạm m&ocirc;n quy.&quot;<br />\r\n<br />\r\n&quot;Nguy&ecirc;n lai l&agrave; như vậy, &quot; Ng&ocirc; Vọng sờ l&ecirc;n cằm trầm ng&acirc;m v&agrave;i tiếng, &quot;Vậy đến đ&acirc;y đi, ta chuẩn bị xong.&quot;<br />\r\n<br />\r\n&quot;Có th&ecirc;̉.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh trịnh trọng gật gật đầu, tại thủ trạc b&ecirc;n trong lấy ra một c&aacute;i bồ đo&agrave;n b&agrave;y ra dưới th&acirc;n thể, v&aacute;y tung bay ở giữa, đ&atilde; l&agrave; đ&uacute;ng quy đ&uacute;ng củ địa b&agrave;n ch&acirc;n nhập tọa.<br />\r\n<br />\r\nC&oacute; sao n&oacute;i vậy, Nh&acirc;n vực nữ tu đả tọa tư thế x&aacute;c thực rất ưu mỹ.<br />\r\n<br />\r\nNg&ocirc; Vọng học theo, để cho người ta chuyển đến c&aacute;i phổ th&ocirc;ng ng&agrave;n năm no&atilde;n ngọc phối hợp Vạn Ni&ecirc;n Huyền Băng t&acirc;m gh&eacute;p lại m&agrave; th&agrave;nh ngọc thạch bồ đo&agrave;n, ngồi tại L&acirc;m Tố Khinh ch&iacute;nh đối diện.<br />\r\n<br />\r\nL&acirc;m Tố Khinh mắt đều xem sững sờ, ngẩn người, nhỏ giọng hỏi:<br />\r\n<br />\r\n&quot;Thiếu chủ, ch&uacute;ng ta thị tộc thịnh sản bảo ngọc sao &quot;<br />\r\n<br />\r\n&quot;A, &quot; Ng&ocirc; Vọng thuận miệng ứng với, &quot;To&agrave; kia Đại Tuyết Sơn trong phạm vi ba ng&agrave;n dặm c&aacute;c loại kho&aacute;ng sản đều thuộc về ch&uacute;ng ta thị tộc, c&aacute;i phạm vi n&agrave;y đ&acirc;y cũng l&agrave; ch&uacute;ng ta thị tộc cương vực.<br />\r\n<br />\r\nCủa ta l&atilde;o mẫu th&acirc;n liền ở tại to&agrave; kia Đại Tuyết Sơn b&ecirc;n tr&ecirc;n, c&oacute; rảnh dẫn ngươi đi nh&igrave;n một ch&uacute;t, n&oacute;i kh&ocirc;ng chừng c&ograve;n c&oacute; thể cho ngươi một điểm chỗ tốt.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh kh&ocirc;ng chịu được sai lệch ph&iacute;a dưới: &quot;H&ugrave;ng B&atilde;o tộc địa vực như thế đại &quot;<br />\r\n<br />\r\n&quot;Ngươi cho rằng tựu nơi n&agrave;y như thế chọn người, c&oacute; thể trở th&agrave;nh Bắc D&atilde; đệ nhị đại thị tộc sao<br />\r\n<br />\r\nNơi đ&acirc;y l&agrave; vương đ&igrave;nh, cũng ch&iacute;nh l&agrave; cha ta xử l&yacute; trong tộc sự vụ, tổ chức c&aacute;c loại Tế Tự điển lễ địa.<br />\r\n<br />\r\nCha ta l&agrave; tộc trưởng, cũng l&agrave; thị tộc Thủ Lĩnh, hắn muốn dẫn lấy ch&iacute;nh m&igrave;nh Th&acirc;n Vệ tại c&aacute;c nơi tuần s&aacute;t, hai ba năm mới c&oacute; thể tới một lần vương đ&igrave;nh.&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng c&oacute; chút phiền muộn chỗ vuốt vuốt mi t&acirc;m, thở d&agrave;i:<br />\r\n<br />\r\n&quot;Hiện tại ngươi hẳn l&agrave; biết, ta c&aacute;i n&agrave;y qu&aacute;i bệnh g&acirc;y &aacute;p lực lớn bao nhi&ecirc;u.<br />\r\n<br />\r\nKh&ocirc;ng cẩn thận liền hội tạo th&agrave;nh thị tộc rung chuyển, nếu như ta c&aacute;i n&agrave;y qu&aacute;i bệnh trị kh&ocirc;ng hết, chỉ c&oacute; thể nghĩ biện ph&aacute;p để cho ta cha mẹ t&aacute;i sinh một c&aacute;i đệ đệ hoặc l&agrave; muội muội.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh nhỏ giọng hỏi: &quot;Muội muội cũng c&oacute; thể kế thừa tộc trưởng chi vị sao &quot;<br />\r\n<br />\r\n&quot;Một dạng, &quot; Ng&ocirc; Vọng cười n&oacute;i, &quot;Nhiều khi, nơi n&agrave;y nữ tử địa vị so nam tử cao hơn n&eacute;m một c&aacute;i n&eacute;m.<br />\r\n<br />\r\nB&ecirc;n ngo&agrave;i trong tộc l&agrave; tộc trưởng định đoạt<br />\r\n<br />\r\nTr&ecirc;n thực tế, tộc trưởng nhất định phải đầy đủ c&acirc;n nhắc c&aacute;c vị Tế Tự &yacute; kiến, m&agrave; ch&acirc;n ch&iacute;nh có th&ecirc;̉ ảnh hưởng đến tộc trưởng l&agrave;m ra quyết đo&aacute;n Tế Tự, phần lớn đều l&agrave; nữ t&iacute;nh.<br />\r\n<br />\r\nTa tổ mẫu ch&iacute;nh l&agrave; trong tộc địa vị cao nhất Tế Tự, mẫu th&acirc;n l&agrave; trong tộc thực lực tối cường Tế Tự, c&aacute;c n&agrave;ng tại to&agrave;n bộ Bắc D&atilde; đều nổi danh.<br />\r\n<br />\r\nM&agrave; mới chọn tộc trưởng th&ecirc; tử, phần lớn đều l&agrave; từ nhỏ bồi dưỡng nữ tế, th&ocirc;ng hiểu Kỳ Tinh thuật, dung mạo ưu tr&uacute;ng tuyển ưu, c&oacute; được phong ph&uacute; học thức &quot;<br />\r\n<br />\r\nNg&ocirc; Vọng lời n&oacute;i một trận, c&aacute;ch lều vải bi&ecirc;n giới mắt nh&igrave;n Tuyết Sơn phương hướng, kh&oacute;e miệng c&oacute; ch&uacute;t phủi xuống.<br />\r\n<br />\r\nRất l&acirc;u kh&ocirc;ng gặp ch&iacute;nh m&igrave;nh l&atilde;o mẫu th&acirc;n.<br />\r\n<br />\r\nL&acirc;m Tố Khinh cũng kh&ocirc;ng c&oacute; ch&uacute; &yacute; tới Ng&ocirc; Vọng nhỏ b&eacute; biểu lộ, giờ ph&uacute;t n&agrave;y hai mắt s&aacute;ng l&ecirc;n hỏi:<br />\r\n<br />\r\n&quot;Thiếu chủ cũng c&oacute; dạng n&agrave;y tương lai đạo lữ sao đ&aacute;ng y&ecirc;u sao hiện tại đ&atilde; gặp được sao &quot;<br />\r\n<br />\r\nC&aacute;i n&agrave;y l&atilde;o a di l&agrave;m sao như thế B&aacute;t Qu&aacute;i<br />\r\n<br />\r\nNg&ocirc; Vọng lạnh nhạt trả lời: &quot;Hẳn l&agrave; c&oacute;, bất qu&aacute; bởi v&igrave; ta c&aacute;i n&agrave;y qu&aacute;i bệnh, mẫu th&acirc;n c&ugrave;ng tổ mẫu đều kh&ocirc;ng đối ta đề cập qua. Ngươi c&ograve;n dạy kh&ocirc;ng dạy &quot;<br />\r\n<br />\r\n&quot;Ai, l&agrave;, ng&agrave;i nghe kỹ, ta trước l&agrave; ng&agrave;i n&oacute;i c&aacute;i g&igrave; l&agrave; tu h&agrave;nh, đoạn văn n&agrave;y l&agrave; nhập m&ocirc;n l&uacute;c chưởng m&ocirc;n từng v&igrave; ch&uacute;ng ta giảng.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh hắng giọng, để cho m&igrave;nh ngồi sửa chữa ch&uacute;t &iacute;t, nh&igrave;n chăm ch&uacute; l&ecirc;n Ng&ocirc; Vọng, nghi&ecirc;m trang chậm r&atilde;i n&oacute;i:<br />\r\n<br />\r\n&quot;Tu h&agrave;nh trước đ&oacute;, biết được Nh&acirc;n tộc qu&aacute; khứ.<br />\r\n<br />\r\nĐại Hoang từ xưa, cường giả nhiều kh&ocirc;ng kể xiết, cường tộc trải rộng c&aacute;c nơi, vậy m&agrave; Nh&acirc;n tộc ta tự th&acirc;n suy nhược, như thế n&agrave;o lập tồn<br />\r\n<br />\r\nTi&ecirc;n Thi&ecirc;n Thần chỉ sinh m&agrave; chấp chưởng thi&ecirc;n địa đạo tắc, c&aacute;c phương hung th&uacute; sinh m&agrave; nhục th&acirc;n cường ho&agrave;nh, ta Nh&acirc;n tộc nếu muốn tồn tại thế gian, l&agrave;m sao c&oacute; thể phụ thuộc cường Thần<br />\r\n<br />\r\nThần chỉ hung th&uacute; ninh hữu kh&iacute; ph&aacute;ch hồ!<br />\r\n<br />\r\nHỏa Ho&agrave;ng Toại Nh&acirc;n diễn Hỏa Chi Đại Đạo, ch&eacute;m giết hung th&uacute;, mở Nh&acirc;n vực!<br />\r\n<br />\r\nThi&ecirc;n Ho&agrave;ng Phục Hi B&aacute;t Qu&aacute;i diễn to&agrave;n bộ vạn tu h&agrave;nh ph&aacute;p, hộ ta Nh&acirc;n vực mười vạn năm an b&igrave;nh!<br />\r\n<br />\r\nH&ocirc;m nay sở tu chi ph&aacute;p, vốn l&agrave; tiền nh&acirc;n t&acirc;m huyết, l&agrave; Nh&acirc;n tộc ta cường thịnh, Nh&acirc;n vực sinh s&ocirc;i cường thịnh!<br />\r\n<br />\r\nSinh m&agrave; l&agrave;m người, cần biết tự cường.<br />\r\n<br />\r\nPhương ph&aacute;p tu h&agrave;nh, tự m&igrave;nh Nh&acirc;n Đạo kh&ocirc;ng ngừng!&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng sắc mặt ngưng trọng gật gật đầu, đ&aacute;y mắt cũng dấy l&ecirc;n một ch&uacute;t hỏa quang.<br />\r\n<br />\r\nL&acirc;m Tố Khinh cố gắng nhớ lại lấy ch&iacute;nh m&igrave;nh nhập m&ocirc;n l&uacute;c t&igrave;nh h&igrave;nh, trầm ng&acirc;m hồi l&acirc;u mới n&oacute;i:<br />\r\n<br />\r\n&quot;Ch&iacute;nh thức tu h&agrave;nh trước, ch&uacute;ng ta trước giải như thế n&agrave;o kh&iacute;, như thế n&agrave;o tinh, như thế n&agrave;o Thần &quot;<br />\r\n<br />\r\nC&ugrave;ng với n&agrave;ng coi như thanh nhuận tiếng n&oacute;i, Ng&ocirc; Vọng rất nhanh liền tiến v&agrave;o trạng th&aacute;i.<br />\r\n<br />\r\nNg&ocirc; Vọng trước đ&acirc;y nghi&ecirc;n cứu qua Nh&acirc;n vực phương ph&aacute;p tu h&agrave;nh, mặc d&ugrave; đọc lướt qua kh&ocirc;ng s&acirc;u, nhưng cũng coi như c&oacute; ch&uacute;t cơ sở.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nKh&ocirc;ng bao l&acirc;u , theo L&acirc;m Tố Khinh chỉ điểm, hắn đ&atilde; nhắm mắt Ngưng Thần, bắt đầu h&ocirc; hấp thổ nạp, tụ tập trong kh&ocirc;ng kh&iacute; phi&ecirc;u đ&atilde;ng linh kh&iacute;, ngưng tụ tự th&acirc;n luồng thứ nhất &#39;Kh&iacute; tức&#39; .<br />\r\n<br />\r\n&#39;Tuổi c&ograve;n trẻ, nghi&ecirc;m t&uacute;c bộ d&aacute;ng cũng l&agrave; ủng hộ hấp dẫn người nha.&#39;<br />\r\n<br />\r\nL&acirc;m Tố Khinh n&acirc;ng cằm l&ecirc;n d&ograve; x&eacute;t thiếu ni&ecirc;n ở trước mắt người, nh&igrave;n hắn gương mặt c&ograve;n c&oacute; ch&uacute;t &iacute;t non nớt từ đ&oacute; qu&aacute; thanh t&uacute;, có th&ecirc;̉ hai đầu l&ocirc;ng m&agrave;y lại c&oacute; một loại kh&oacute; tả kh&iacute; độ.<br />\r\n<br />\r\nĐ&aacute;ng tiếc, lớn h&aacute; mồm.<br />\r\n<br />\r\nM&agrave; lại nhất định l&agrave; c&aacute;i n&agrave;y tr&ecirc;n đại thảo nguy&ecirc;n cao cao tại thượng Thủ Lĩnh, c&ugrave;ng với n&agrave;ng loại n&agrave;y Tiểu Tu sĩ d&atilde; bất qu&aacute; chỉ c&oacute; s&aacute;u năm gặp nhau.<br />\r\n<br />\r\nCh&iacute;nh m&igrave;nh l&agrave;m sao nhanh như vậy tựu đối với hắn đổi c&aacute;i nh&igrave;n<br />\r\n<br />\r\nL&acirc;m Tố Khinh hơi c&oacute; ch&uacute;t kinh ngạc, sau đ&oacute; vừa cẩn thận nghĩ nghĩ.<br />\r\n<br />\r\nHắn qu&aacute;i bệnh hẳn l&agrave; chiếm một phần, để cho m&igrave;nh ở chỗ n&agrave;y cảm thấy an t&acirc;m.<br />\r\n<br />\r\n&Acirc;n<br />\r\n<br />\r\nL&acirc;m Tố Khinh hơi kinh h&atilde;i, trừng mắt trước thiếu ni&ecirc;n.<br />\r\n<br />\r\nHắn quanh người xuất hiện từng sợi gi&oacute; nhẹ, ch&uacute;t &iacute;t n&agrave;y gi&oacute; trộn lẫn lấy nh&agrave;n nhạt xanh, lam lưu quang, hướng Ng&ocirc; Vọng miệng mũi, l&ograve;ng b&agrave;n tay, l&ograve;ng b&agrave;n ch&acirc;n hội tụ m&agrave; đi.<br />\r\n<br />\r\nNạp, Nạp Linh<br />\r\n<br />\r\nLần thứ nhất ngồi xuống liền bắt đầu Nạp Linh<br />\r\n<br />\r\nC&aacute;i n&agrave;y!<br />\r\n<br />\r\nSau đ&oacute;, để L&acirc;m Tố Khinh chấn kinh sự t&igrave;nh li&ecirc;n tiếp, n&agrave;ng kia đ&agrave;n m&ocirc;i khẽ nhếch, trong thời gian ngắn lại kh&ocirc;ng kh&eacute;p được.<br />\r\n<br />\r\nNg&ocirc; Vọng quanh người xuất hiện yếu ớt tinh quang, những n&agrave;y tinh quang hội tụ th&agrave;nh d&ograve;ng suối, từng v&ograve;ng từng v&ograve;ng quấn quanh ở Ng&ocirc; Vọng quanh người c&aacute;c nơi, đem hắn khu&ocirc;n mặt nổi bật l&ecirc;n giống như Thần Minh.<br />\r\n<br />\r\nL&acirc;m Tố Khinh cẩn thận từng li từng t&iacute; thả ra ch&iacute;nh m&igrave;nh linh thức, lập tức ph&aacute;t gi&aacute;c được, Ng&ocirc; Vọng thể nội đang c&oacute; kh&iacute; tức lưu động.<br />\r\n<br />\r\nM&agrave; kh&iacute; tức lưu động con đường ch&iacute;nh l&agrave; n&agrave;ng vừa rồi truyền thụ Ng&ocirc; Vọng Nh&acirc;n vực đệ tứ bộ cơ sở tu h&agrave;nh ph&aacute;p &laquo; Thi&ecirc;n Nạp Quyết &raquo; tiểu chu thi&ecirc;n đường đi!<br />\r\n<br />\r\nL&acirc;m Tố Khinh kh&ocirc;ng chịu được c&uacute;i đầu thở d&agrave;i.<br />\r\n<br />\r\nĐ&acirc;y thật l&agrave; Ng&ocirc; Vọng lần thứ nhất ngồi xuống lần thứ nhất tiếp x&uacute;c Thi&ecirc;n Nạp Quyết<br />\r\n<br />\r\nA, ha ha.<br />\r\n<br />\r\nTr&ecirc;n đời n&agrave;y quả nhi&ecirc;n l&agrave; tồn tại thi&ecirc;n ph&uacute; dị bẩm, giống như n&agrave;ng loại n&agrave;y ngồi xuống nửa năm mới ho&agrave;n th&agrave;nh lần thứ nhất tiểu chu thi&ecirc;n phổ th&ocirc;ng tu sĩ, quả thực l&agrave; k&eacute;m cỏi bản sức lực.<br />\r\n<br />\r\nTr&aacute;ch kh&ocirc;ng được sư đệ chọn sư muội, c&ograve;n nhấc l&ecirc;n thuyền chạy<br />\r\n<br />\r\n&quot;Ừ&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng đột nhi&ecirc;n chau m&agrave;y, quanh người tinh quang đột nhi&ecirc;n l&oacute;ng l&aacute;nh dưới, thể nội đoàn kia đ&atilde; tiếp cận th&agrave;nh h&igrave;nh kh&iacute; tức đột nhi&ecirc;n nổ tan.<br />\r\n<br />\r\nHắn k&ecirc;u l&ecirc;n một tiếng đau đớn, sắc mặt đột nhi&ecirc;n trở n&ecirc;n yếu ớt v&ocirc; c&ugrave;ng, kh&oacute;e miệng thấm ra một tia ti&ecirc;n huyết.<br />\r\n<br />\r\nL&acirc;m Tố Khinh cũng l&agrave; cả kinh, th&acirc;n h&igrave;nh vọt l&ecirc;n liền muốn đi gi&uacute;p Ng&ocirc; Vọng b&igrave;nh phục kh&iacute; tức, nhưng nh&ocirc; ra b&agrave;n tay kh&oacute; khăn lắm lơ lửng tại Ng&ocirc; Vọng ph&iacute;a sau, lại nghĩ tới Ng&ocirc; Vọng qu&aacute;i bệnh.<br />\r\n<br />\r\n&quot;Người tới!&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh quay đầu la l&ecirc;n: &quot;Mau tới người!&quot;<br />\r\n<br />\r\n&quot;Ta kh&ocirc;ng sao, khụ, khụ khục, &quot; Ng&ocirc; Vọng lau đi kh&oacute;e miệng huyết, &quot;Liền l&agrave; kh&iacute; tức x&oacute;a bổ ngươi c&ocirc;ng ph&aacute;p n&agrave;y x&aacute;c định l&agrave; ch&iacute;nh bản &quot;<br />\r\n<br />\r\n&quot;Ừ&quot; L&acirc;m Tố Khinh kh&ocirc;ng chịu được sai lệch ph&iacute;a dưới.<br />\r\n<br />\r\n&quot;Ngươi theo vừa rồi n&oacute;i vận chuyển xuống kh&iacute; tức, &quot; Ng&ocirc; Vọng đứng dậy, &quot;Ngồi b&ecirc;n n&agrave;y, linh kh&iacute; hội (sẽ) c&agrave;ng dồi d&agrave;o ch&uacute;t &iacute;t.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh đ&aacute;p ứng một tiếng, mang theo chần chờ theo lời l&agrave;m theo.<br />\r\n<br />\r\nNg&ocirc; Vọng lại l&agrave; nh&iacute;u m&agrave;y nhăn tr&aacute;n, một b&ecirc;n nh&igrave;n chăm ch&uacute; l&ecirc;n L&acirc;m Tố Khinh kia miễn cưỡng cũng coi như uyển chuyển tư th&aacute;i, một b&ecirc;n dưới đ&aacute;y l&ograve;ng kh&ocirc;ng ngừng ph&acirc;n t&iacute;ch vấn đề ở chỗ n&agrave;o.<br />\r\n<br />\r\nTựa hồ l&agrave; Kỳ Tinh thuật c&ugrave;ng Nh&acirc;n vực tu h&agrave;nh ph&aacute;p<br />\r\n<br />\r\nTương x&iacute;ch.</p>', '2021-11-27 21:24:58', 4, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(184, 49, 'Lão mẫu thân', '5', '<p>&quot;Oa &quot;<br />\r\n<br />\r\n&quot;Phốc!&quot;<br />\r\n<br />\r\n&quot;Khụ khụ!&quot;<br />\r\n<br />\r\nTrong đại trướng, hai người thỉnh thoảng c&uacute;i đầu thổ huyết, tr&agrave;ng diện c&oacute; ch&uacute;t dọa người.<br />\r\n<br />\r\nL&acirc;m Tố Khinh lau đi kh&oacute;e miệng, quanh người kia nhạt nhẽo tinh thần chi lực ho&agrave;n to&agrave;n t&aacute;n đi, đ&aacute;ng thương nh&igrave;n xem Ng&ocirc; Vọng.<br />\r\n<br />\r\n&quot;Thiếu chủ, ta ti&ecirc;́p tục như th&ecirc;́ cũng kh&ocirc;ng phải biện ph&aacute;p.<br />\r\n<br />\r\nTa nhất tu Kỳ Tinh thuật liền hội kh&iacute; huyết s&ocirc;i tr&agrave;o, tu ti&ecirc;n kh&ocirc;ng phục t&ugrave;ng huyết thực, cũng kh&ocirc;ng c&oacute; mấy c&acirc;n huyết có th&ecirc;̉ n&ocirc;n, thực sự kh&ocirc;ng chịu nổi.<br />\r\n<br />\r\nPhốc!&quot;<br />\r\n<br />\r\n&quot;Trước ngừng đi.&quot;<br />\r\n<br />\r\nNg&ocirc; Vọng chậm r&atilde;i thở ra một hơi, ra hiệu L&acirc;m Tố Khinh tạm thời nghỉ ngơi dưới, gọi tới thị vệ qu&eacute;t dọn ph&ograve;ng, thuận tiện lấy ch&uacute;t bổ huyết đồ ăn.<br />\r\n<br />\r\nTại sao c&oacute; thể như vậy<br />\r\n<br />\r\nHắn tại Kỳ Tinh thuật tr&ecirc;n cơ sở tu Nh&acirc;n vực tu h&agrave;nh ph&aacute;p, hoặc l&agrave; L&acirc;m Tố Khinh tại Nh&acirc;n vực tu h&agrave;nh ph&aacute;p tr&ecirc;n cơ sở tu Kỳ Tinh thuật, đều l&agrave; giống nhau kết quả.<br />\r\n<br />\r\nĐường n&agrave;y kh&ocirc;ng th&ocirc;ng, phun m&aacute;u ba lần.<br />\r\n<br />\r\nCả hai v&igrave; c&aacute;i g&igrave; kh&ocirc;ng thể c&ugrave;ng tồn tại tương dung<br />\r\n<br />\r\nNg&ocirc; Vọng cố gắng nhớ lại lấy trước đ&acirc;y rất nhiều chi tiết, n&acirc;ng cằm l&ecirc;n ngồi ở kia, dần dần nhập thần.<br />\r\n<br />\r\nL&agrave; hắn trước đ&acirc;y l&yacute; giải sai lầm sao<br />\r\n<br />\r\nKỳ Tinh thuật, Tế Tự th&ocirc;ng qua đối Quần Tinh chi thần tiến h&agrave;nh cầu nguyện, từ đ&oacute; thu hoạch được Quần Tinh chi thần ban thưởng tinh thần chi lực, chưởng khống phong sương vũ tuyết c&ugrave;ng l&ocirc;i đ&igrave;nh.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; Kỳ Tinh thuật ch&iacute;nh thức giải th&iacute;ch.<br />\r\n<br />\r\nNg&ocirc; Vọng đối Kỳ Tinh thuật l&yacute; giải liền trực tiếp nhiều.<br />\r\n<br />\r\nThi&ecirc;n Địa ở giữa tồn tại một loại n&agrave;o đ&oacute; đạo tắc, cho ph&eacute;p sinh linh nắm giữ tinh thần chi lực.<br />\r\n<br />\r\nSinh linh th&ocirc;ng qua cầu nguyện, cầu nguyện c&aacute;i n&agrave;y bản th&acirc;n &aacute;m chỉ phương ph&aacute;p, kh&ocirc;ng ngừng cường h&oacute;a ch&iacute;nh m&igrave;nh tinh thần, c&ugrave;ng tinh thần đạo tắc sinh ra cộng minh.<br />\r\n<br />\r\nCuối c&ugrave;ng mượn từ cố định phương thức, ti&ecirc;u hao tự th&acirc;n tinh thần, dẫn động tinh thần chi lực, ph&oacute;ng xuất ra kh&aacute;c biệt Kỳ Tinh thuật.<br />\r\n<br />\r\nC&aacute;i n&agrave;y l&yacute; giải, trước đ&oacute; đều l&agrave; ch&iacute;nh x&aacute;c.<br />\r\n<br />\r\nNg&ocirc; Vọng th&ocirc;ng qua c&aacute;c loại phương thức cường h&oacute;a bản th&acirc;n &aacute;m chỉ, kh&ocirc;ng ngừng ma luyện ch&iacute;nh m&igrave;nh &#39;Thần&#39;, thời gian rất ngắn b&ecirc;n trong tựu nắm giữ rất nhiều Kỳ Tinh thuật.<br />\r\n<br />\r\nLiền l&agrave; cường h&oacute;a bản th&acirc;n &aacute;m chỉ qu&aacute; tr&igrave;nh c&oacute; một ch&uacute;t điểm xấu hổ.<br />\r\n<br />\r\nN&oacute;i chung, trời tối người y&ecirc;n kho&aacute;c tr&ecirc;n vai c&aacute;i &aacute;o cho&agrave;ng, mang mặt nạ đứng tại Lưu Ly trước gương, b&agrave;y một ch&uacute;t xấu hổ thủ thế, đối với m&igrave;nh la l&ecirc;n ch&iacute;nh m&igrave;nh l&agrave; &#39;Tinh thần Si&ecirc;u Nh&acirc;n Đi&ecirc;̣n Quang&#39; .<br />\r\n<br />\r\nL&agrave;m ch&iacute;nh m&igrave;nh t&ugrave;y t&acirc;m đ&iacute;ch x&aacute;c tin m&igrave;nh c&oacute; thể cường đại, liền hội trở n&ecirc;n cường đại.<br />\r\n<br />\r\nTrừ c&aacute;i đ&oacute; ra, c&ograve;n muốn dựa v&agrave;o c&aacute;c loại dược liệu qu&yacute; gi&aacute; c&ugrave;ng huấn luyện.<br />\r\n<br />\r\nTheo Ng&ocirc; Vọng l&yacute; giải, tu ti&ecirc;n ph&aacute;p thuật cũng tốt, Kỳ Tinh thuật cũng được, cuối c&ugrave;ng đều l&agrave; d&ugrave;ng tự th&acirc;n tinh, kh&iacute;, thần, c&ugrave;ng ph&aacute;p tắc trong thi&ecirc;n địa cộng minh.<br />\r\n<br />\r\nKỳ Tinh thuật trọng điểm ch&iacute;nh l&agrave; &#39;Thần &#39;<br />\r\n<br />\r\nNh&acirc;n vực tu h&agrave;nh ph&aacute;p l&agrave; &#39;Tinh&#39;, &#39;Kh&iacute;&#39;, &#39;Thần&#39; tổng trọng, luyện tinh dưỡng kh&iacute;, lấy kh&iacute; H&oacute;a Thần, d&ugrave;ng Thần Phản Hư, th&agrave;nh bản th&acirc;n chi Thần Minh, được hưởng d&agrave;i l&acirc;u thọ nguy&ecirc;n.<br />\r\n<br />\r\nCho n&ecirc;n, Kỳ Tinh thuật kh&ocirc;ng tăng thọ nguy&ecirc;n, đ&acirc;y cũng l&agrave; Ng&ocirc; Vọng v&igrave; c&aacute;i g&igrave; lưu lại L&acirc;m Tố Khinh, lại kh&ocirc;ng đoạn thổ huyết cũng muốn đi nếm thử Nh&acirc;n vực tu ti&ecirc;n ph&aacute;p chủ yếu động lực.<br />\r\n<br />\r\nKia v&igrave; c&aacute;i g&igrave;, Nh&acirc;n vực Tr&uacute;c Cơ c&ocirc;ng ph&aacute;p c&ugrave;ng Kỳ Tinh thuật b&agrave;i x&iacute;ch lẫn nhau<br />\r\n<br />\r\nKh&ocirc;ng, kh&ocirc;ng đ&uacute;ng.<br />\r\n<br />\r\nTựa hồ l&agrave; Kỳ Tinh thuật đơn phương b&agrave;i x&iacute;ch Nh&acirc;n vực tu h&agrave;nh ph&aacute;p.<br />\r\n<br />\r\nNg&ocirc; Vọng ngẩng đầu hỏi: &quot;Tố Khinh, trước hết nhất dị động ch&iacute;nh l&agrave; kh&ocirc;ng phải tinh thần chi lực &quot;<br />\r\n<br />\r\n&quot;Ừ&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh cẩn thận nhớ một ch&uacute;t, gật đầu n&oacute;i: &quot;Kh&iacute; tức của ta cũng kh&ocirc;ng xuất hiện hỗn loạn, ngược lại l&agrave; hấp dẫn tới tinh thần chi lực quấy nhiễu ta kh&iacute; tức vận chuyển.<br />\r\n<br />\r\nThiếu chủ, ta trước đ&acirc;y nghe người ta n&oacute;i đến qua Bắc D&atilde; Kỳ Tinh thuật, đều n&oacute;i đ&acirc;y l&agrave; một loại uy lực v&ocirc; c&ugrave;ng lớn thuật ph&aacute;p, cũng c&oacute; ch&uacute;t thần b&iacute;, l&agrave; Bắc D&atilde; đại thị tộc Tế Tự độc hữu.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nC&oacute; thể hay kh&ocirc;ng, l&agrave; Kỳ Tinh thuật bản th&acirc;n tồn tại một loại n&agrave;o đ&oacute; hạn chế &quot;<br />\r\n<br />\r\nNg&ocirc; Vọng đơn giản ứng tiếng, n&oacute;i: &quot;Ngươi c&aacute;ch ta hơi xa một ch&uacute;t, ta d&ograve; x&eacute;t xuống nguy&ecirc;n nh&acirc;n.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh theo lời thối lui lều lớn nơi hẻo l&aacute;nh, trong mắt mang theo v&agrave;i phần lo lắng m&agrave; nh&igrave;n xem nhắm mắt Ngưng Thần, tựa hồ l&agrave; muốn tiến h&agrave;nh một loại n&agrave;o đ&oacute; thần b&iacute; nghi thức Ng&ocirc; Vọng.<br />\r\n<br />\r\nNg&ocirc; Vọng đột nhi&ecirc;n trợn khai nh&atilde;n, quay đầu nh&igrave;n xem L&acirc;m Tố Khinh, cười n&oacute;i:<br />\r\n<br />\r\n&quot;Ch&uacute;ng ta l&agrave; kh&ocirc;ng phải, mới nhận biết hai ba ng&agrave;y.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh c&aacute;i tr&aacute;n lập tức treo hai c&aacute;i dấu hỏi, c&ograve;n kh&ocirc;ng c&oacute; minh bạch Ng&ocirc; Vọng l&agrave; c&oacute; &yacute; g&igrave;, chỉ thấy Ng&ocirc; Vọng l&agrave;m thủ thế, một đ&aacute;m tản ra thảo nguy&ecirc;n nam tử m&ugrave;i mồ h&ocirc;i đạo tr&aacute;ng h&aacute;n vọt v&agrave;o, đưa n&agrave;ng ngăn c&aacute;ch tại nơi hẻo l&aacute;nh.<br />\r\n<br />\r\nNg&ocirc; Vọng tiếng n&oacute;i mang theo một ch&uacute;t &aacute;y n&aacute;y:<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; th&acirc;n l&agrave; thị tộc người thừa kế gi&aacute;c ngộ.&quot;<br />\r\n<br />\r\nL&acirc;m Tố Khinh kh&oacute;e miệng hếch l&ecirc;n, th&agrave;nh th&agrave;nh thật thật đứng tại kia, biểu lộ một lần mười phần kh&ocirc;n kh&eacute;o.<br />\r\n<br />\r\nN&agrave;ng chỉ gặp<br />\r\n<br />\r\nNg&ocirc; Vọng bảo tr&igrave; ngồi xếp bằng tư thế, hai tay li&ecirc;n tục b&oacute;p ra một ch&uacute;t phức tạp lại phức tạp thủ ấn.<br />\r\n<br />\r\nBất qu&aacute; gi&acirc;y l&aacute;t, Ng&ocirc; Vọng l&ograve;ng b&agrave;n tay, khuỷu tay ở giữa, đầu vai, lưng, hai ch&acirc;n, chậm r&atilde;i hiện ra từng khỏa &oacute;ng &aacute;nh quang ban.<br />\r\n<br />\r\nGiống nhau ban đ&ecirc;m tinh kh&ocirc;ng b&ecirc;n trong điểm điểm tinh thần.<br />\r\n<br />\r\nNg&ocirc; Vọng bờ m&ocirc;i nhẹ nh&agrave;ng kh&eacute;p mở, một ch&uacute;t kỳ dị &acirc;m tiết tự đại trong trướng quanh quẩn, m&agrave; nương theo lấy những n&agrave;y l&acirc;̉m b&acirc;̉m &acirc;m thanh, Ng&ocirc; Vọng quanh người tinh quang m&atilde;nh liệt, th&acirc;n h&igrave;nh hắn c&agrave;ng trở n&ecirc;n c&oacute; ch&uacute;t trong suốt, thể nội xuất hiện một đầu rực rỡ Tinh H&agrave;, li&ecirc;n tiếp đến kh&ocirc;ng biết nơi n&agrave;o hư kh&ocirc;ng.<br />\r\n<br />\r\nL&acirc;m Tố Khinh ngừng thở, mặc d&ugrave; xem kh&ocirc;ng hiểu l&uacute;c n&agrave;y xảy ra chuyện g&igrave;, nhưng n&agrave;ng c&oacute; thể cảm nhận được cỗ n&agrave;y tinh thần chi lực cường ho&agrave;nh.<br />\r\n<br />\r\nC&oacute; thể so với bản th&acirc;n Kim Đan hậu kỳ chưởng m&ocirc;n, kh&ocirc;ng!<br />\r\n<br />\r\nLoại n&agrave;y th&acirc;m th&uacute;y s&acirc;u xa chi cảm gi&aacute;c, n&agrave;ng chỉ ở s&aacute;t v&aacute;ch đại t&ocirc;ng m&ocirc;n mấy vị kia t&oacute;c hoa r&acirc;m trưởng l&atilde;o tr&ecirc;n người cảm thụ qua!<br />\r\n<br />\r\nTrước người mấy t&ecirc;n thị vệ tựa hồ c&agrave;ng th&ecirc;m khẩn trương, thời khắc đề ph&ograve;ng n&agrave;ng c&oacute; c&aacute;i g&igrave; dị động, c&aacute;i n&agrave;y khiến L&acirc;m Tố Khinh cảm thấy kh&ocirc;ng tự tại.<br />\r\n<br />\r\nTh&ocirc;i, ch&iacute;nh m&igrave;nh chỉ l&agrave; đi ngang qua nơi đ&acirc;y, đợi s&aacute;u năm m&agrave; th&ocirc;i.<br />\r\n<br />\r\nBất qu&aacute; b&igrave;nh tĩnh m&agrave; xem x&eacute;t, l&uacute;c n&agrave;y kh&ocirc;ng biết mở ra a thuật ph&aacute;p Thiếu chủ, x&aacute;c thực c&oacute; ch&uacute;t suất kh&iacute;.<br />\r\n<br />\r\nSau nửa canh giờ.<br />\r\n<br />\r\nNg&ocirc; Vọng quanh người tinh quang khi th&igrave; lấp l&aacute;nh khi th&igrave; ảm đạm, m&agrave; hắn nhắm lại hai mắt một mực chưa mở ra.<br />\r\n<br />\r\nL&acirc;m Tố Khinh kh&ocirc;ng chịu được dụi dụi mắt da, chung quanh bọn thị vệ như th&aacute;p sắt lẳng lặng đứng thẳng.<br />\r\n<br />\r\n&#39;Ngồi xuống đi, d&ugrave; sao nh&agrave;n rỗi cũng l&agrave; nh&agrave;n rỗi.&#39;<br />\r\n<br />\r\nSau ba canh giờ.<br />\r\n<br />\r\n&quot;Ha ha, ng&aacute;p.&quot;<br />\r\n<br />\r\nMột c&aacute;i che miệng ưu nh&atilde; ng&aacute;p, kh&ocirc;ng khỏi l&agrave;m L&acirc;m Tố Khinh hai mắt đẫm lệ, vừa định đưa tay lau lau kh&oacute;e mắt, tiền phương mấy thị vệ quay đầu trừng n&agrave;ng một ch&uacute;t.<br />\r\n<br />\r\nSau đ&oacute; trong đại trướng b&oacute;ng người theo thứ tự đ&aacute;nh l&ecirc;n ng&aacute;p.<br />\r\n<br />\r\nBa ng&agrave;y sau.<br />\r\n<br />\r\nChỉnh tề trong tiếng bước ch&acirc;n, hai đội thị vệ nối đu&ocirc;i nhau m&agrave; v&agrave;o, thay thế ở chỗ n&agrave;y ph&ograve;ng thủ nửa ng&agrave;y đồng bạn.<br />\r\n<br />\r\nL&acirc;m Tố Khinh liếc nh&igrave;n vẫn c&ograve;n đang đả tọa Ng&ocirc; Vọng, mục quang tại những thị vệ n&agrave;y tr&ecirc;n th&acirc;n l&uacute;c ẩn l&uacute;c hiện.<br />\r\n<br />\r\nChậc chậc, c&agrave;ng xem c&agrave;ng hiểu được, những thị vệ n&agrave;y cũng đều rất kh&ocirc;ng tệ đ&acirc;u.<br />\r\n<br />\r\nBất qu&aacute; lời n&oacute;i đi cũng phải n&oacute;i lại<br />\r\n<br />\r\nL&acirc;m Tố Khinh mang theo lo lắng nh&igrave;n về ph&iacute;a Ng&ocirc; Vọng, l&uacute;c n&agrave;y Ng&ocirc; Vọng quanh người tinh quang đ&atilde; hơi c&oacute; vẻ ảm đạm, nhưng ở những n&agrave;y ảm đạm tinh quang b&ecirc;n trong, khi th&igrave; hội (sẽ) hiển lộ một ch&uacute;t dị tượng.<br />\r\n<br />\r\nTừ trong hư kh&ocirc;ng tr&ocirc;i nổi bằng đ&aacute; cổ điện, tại tinh thần ở giữa vẫy v&ugrave;ng dị th&uacute;, lẫn nhau ch&eacute;m giết b&oacute;ng người<br />\r\n<br />\r\nMọi việc như thế, kh&ocirc;ng đồng nhất m&agrave; n&oacute;i.<br />\r\n<br />\r\nĐ&acirc;y ch&iacute;nh l&agrave; tu h&agrave;nh Kỳ Tinh thuật phương thức sao<br />\r\n<br />\r\nCó th&ecirc;̉ L&acirc;m Tố Khinh ẩn ẩn hiểu được, Ng&ocirc; Vọng l&uacute;c n&agrave;y trạng th&aacute;i, tho&aacute;ng c&oacute; ch&uacute;t kh&ocirc;ng th&iacute;ch hợp.<br />\r\n<br />\r\n&#39;Đến c&ugrave;ng l&agrave; nơi n&agrave;o xảy ra vấn đề v&igrave; sao lại b&agrave;i x&iacute;ch c&aacute;i kh&aacute;c tu h&agrave;nh ph&aacute;p &#39;<br />\r\n<br />\r\nTinh kh&ocirc;ng b&ecirc;n trong, Ng&ocirc; Vọng hư ảnh lẳng lặng xếp bằng ở kia, trước mặt h&igrave;nh như c&oacute; một mảnh tinh quang xuy&ecirc;n th&agrave;nh r&egrave;m ch&acirc;u, mỗi một c&aacute;i tinh quang hội tụ th&agrave;nh sợi tơ, đại biểu cho Ng&ocirc; Vọng nắm giữ một loại Kỳ Tinh thuật.<br />\r\n<br />\r\nTổ mẫu l&agrave; trong tộc đại chủ tế, hắn tự nhi&ecirc;n thiếu kh&ocirc;ng được học tập Kỳ Tinh thuật con đường.<br />\r\n<br />\r\nGiờ ph&uacute;t n&agrave;y, th&acirc;n h&igrave;nh hắn phai mờ, đầu ng&oacute;n tay v&acirc;n v&ecirc; một sợi sợi tơ, giống như đang bện m&agrave;n trời viễn cổ Thần Linh.<br />\r\n<br />\r\nNg&ocirc; Vọng tới n&agrave;y c&aacute;i t&ecirc;n l&agrave; Đại Hoang thế giới đ&atilde; c&oacute; mười hai năm, tốn hao thời gian nhiều nhất sự t&igrave;nh, liền l&agrave; sưu tập tri thức c&ugrave;ng suy nghĩ Kỳ Tinh thuật.<br />\r\n<br />\r\nHắn có th&ecirc;̉ nhanh ch&oacute;ng nắm giữ Kỳ Tinh thuật, cũng cấp tốc tăng l&ecirc;n ch&iacute;nh m&igrave;nh &#39;Thần&#39;, đời trước k&yacute; ức cho hắn lợi &iacute;ch to lớn.<br />\r\n<br />\r\nKh&ocirc;ng ra đ&ugrave;a giỡn giảng, Ng&ocirc; Vọng có th&ecirc;̉ tại mấy ng&agrave;n người t&igrave;nh nguyện b&ecirc;n trong trổ hết t&agrave;i năng, trở th&agrave;nh duy nhất chỉ định lỗ s&acirc;u thăm d&ograve; chịu chết đội th&agrave;nh vi&ecirc;n, t&acirc;m l&yacute; tố chất tuyệt đối qu&aacute; cứng, c&agrave;ng l&agrave; kinh lịch mấy năm v&ocirc; c&ugrave;ng t&agrave;n khốc huấn luyện.<br />\r\n<br />\r\nTinh kh&iacute; thần b&ecirc;n trong &#39;Thần&#39;, ở mức độ rất lớn quyết định bởi tại tự th&acirc;n &yacute; ch&iacute; lực.<br />\r\n<br />\r\nLiền l&agrave; phần n&agrave;y ma luyện ra &yacute; ch&iacute; lực, lại th&ecirc;m tổ mẫu c&ugrave;ng mẫu th&acirc;n ức điểm điểm tương trợ, mới c&oacute; hắn mười hai tuổi trở th&agrave;nh Đại Tinh Tế điểm ấy tiểu th&agrave;nh tựu.<br />\r\n<br />\r\nRất hổ thẹn, cho Lam Tinh qu&ecirc; qu&aacute;n mất mặt.<br />\r\n<br />\r\nKỳ Tinh thuật cấp bậc ph&acirc;n chia b&ecirc;n trong, tinh tế vị Vu Ch&uacute;c, Đại Ch&uacute;c, Đảo, Đại Đảo c&aacute;c loại (chờ) cấp độ ph&iacute;a tr&ecirc;n, Đại Tinh Tế gần với Nguyệt Tế, c&ugrave;ng c&oacute; được tự th&acirc;n phong h&agrave;o Nhật Tế.<br />\r\n<br />\r\nTo&agrave;n bộ Bắc D&atilde; Nhật Tế kh&ocirc;ng cao hơn bảy người, Nguyệt Tế đ&atilde; có th&ecirc;̉ đảm nhiệm một phương đại thị tộc chủ tế.<br />\r\n<br />\r\nTại Kỳ Tinh thuật tr&ecirc;n tu h&agrave;nh, Ng&ocirc; Vọng một mực xu&ocirc;i gi&oacute; xu&ocirc;i nước, d&ugrave; l&agrave; c&oacute; c&aacute;i g&igrave; kh&oacute; d&ugrave;ng lĩnh ngộ đạo l&yacute;, hắn loại suy, suy một ra ba, lớn mật tưởng tượng, cẩn thận thực tiễn, cuối c&ugrave;ng cũng đều thuận lợi vượt qua.<br />\r\n<br />\r\nDuy chỉ c&oacute; h&ocirc;m nay, khi hắn nghĩ bước ra Kỳ Tinh thuật phạm tr&ugrave;, nếm thử đối với hắn m&agrave; n&oacute;i cực kỳ trọng yếu Nh&acirc;n vực tu h&agrave;nh ph&aacute;p, Kỳ Tinh thuật tr&aacute;i lại tr&oacute;i buộc lại cước bộ của hắn.<br />\r\n<br />\r\nM&agrave; đ&aacute;p &aacute;n của vấn đề n&agrave;y, hẳn l&agrave; tựu giấu ở Kỳ Tinh thuật b&ecirc;n trong.<br />\r\n<br />\r\nTại Ng&ocirc; Vọng l&yacute; giải b&ecirc;n trong, mỗi c&aacute;i phương ph&aacute;p tu h&agrave;nh bản chất, đều c&oacute; thể đơn giản ho&aacute; l&agrave; nắm giữ c&ugrave;ng đạo tắc cộng minh phương ph&aacute;p, c&ugrave;ng đạo tắc lấy được cộng minh, mượn d&ugrave;ng đạo tắc th&uacute;c đẩy đối ứng linh lực, từ đ&oacute; ngưng tụ th&agrave;nh ph&aacute;p thuật.<br />\r\n<br />\r\nTinh kh&ocirc;ng chi lực cũng tốt, Ngũ H&agrave;nh chi lực cũng được, chung quy chỉ l&agrave; tr&agrave;n ngập tại Đại Hoang Thi&ecirc;n Địa ở giữa &#39;Linh lực&#39; .<br />\r\n<br />\r\nGiống như c&agrave;ng th&acirc;m nhập đi suy nghĩ, ngăn cản ch&iacute;nh m&igrave;nh tu h&agrave;nh Nh&acirc;n vực tu sĩ phổ th&ocirc;ng Tr&uacute;c Cơ ph&aacute;p m&ocirc;n, n&ecirc;n l&agrave; Kỳ Tinh thuật ph&iacute;a sau đầu kia đạo tắc.<br />\r\n<br />\r\nTinh thần l&agrave; đạo.<br />\r\n<br />\r\nL&agrave; từng c&oacute; Kỳ Tinh thuật một đạo cường giả, l&agrave; đầu n&agrave;y đạo tắc thiết hạ g&ocirc;ng xiềng<br />\r\n<br />\r\nVẫn l&agrave; c&aacute;i n&agrave;y đạo tắc bản th&acirc;n tựu c&ugrave;ng Nh&acirc;n vực tu h&agrave;nh ph&aacute;p cơ sở đạo tắc c&ugrave;ng nhau vi phạm<br />\r\n<br />\r\nCh&iacute;nh m&igrave;nh c&aacute;i n&agrave;y qu&aacute;i bệnh, đến c&ugrave;ng phải hay kh&ocirc;ng c&ugrave;ng Kỳ Tinh thuật c&oacute; quan hệ<br />\r\n<br />\r\nC&aacute;i n&agrave;y qu&aacute;i bệnh xuất hiện c&ugrave;ng m&igrave;nh tiếp x&uacute;c Kỳ Tinh thuật thời gian gần như tr&ugrave;ng hợp, mặc d&ugrave; tổ mẫu c&ugrave;ng mẫu th&acirc;n đều n&oacute;i Kỳ Tinh thuật l&agrave; Tinh Kh&ocirc;ng Thần ban &acirc;n, nhưng Ng&ocirc; Vọng đối với c&aacute;i n&agrave;y một mực c&oacute; ho&agrave;i nghi.<br />\r\n<br />\r\nĐi t&igrave;m đ&aacute;p &aacute;n.<br />\r\n<br />\r\nĐi t&igrave;m hiểu đầu n&agrave;y đạo tắc.<br />\r\n<br />\r\nNếu như thật sự c&oacute; Ch&uacute;a tể đầu n&agrave;y đạo tắc &yacute; ch&iacute;, ch&iacute;nh m&igrave;nh phải chăng có th&ecirc;̉ th&ocirc;ng qua đạo tắc tiếp x&uacute;c đến như vậy tồn tại<br />\r\n<br />\r\nTừng c&aacute;i vấn đề h&ograve;a tan dưới đ&aacute;y l&ograve;ng, Ng&ocirc; Vọng kh&ocirc;ng ngừng nếm thử tiến v&agrave;o vật ng&atilde; lưỡng vong trạng th&aacute;i.<br />\r\n<br />\r\nC&ugrave;ng tinh kh&ocirc;ng tương dung, t&igrave;m kiếm Kỳ Tinh thuật vận h&agrave;nh tầng dưới ch&oacute;t Logic, t&igrave;m đầu kia Ch&uacute;a tể tinh kh&ocirc;ng hết thảy đạo, đem n&oacute; ngưng tụ th&agrave;nh tự th&acirc;n ph&aacute;p.<br />\r\n<br />\r\nKh&ocirc;ng ph&aacute;t hiện được tuế nguyệt tr&ocirc;i qua, kh&ocirc;ng cảm gi&aacute;c được C&agrave;n Kh&ocirc;n biến ảo.<br />\r\n<br />\r\nTinh kh&ocirc;ng b&ecirc;n trong c&oacute; yếu ớt tinh quang tụ hợp v&agrave;o tự th&acirc;n, thần niệm đang thong thả lại tiếp tục tăng cường, to&agrave;n th&acirc;n t&ecirc; dại, c&oacute; ch&uacute;t thoải m&aacute;i dễ chịu.<br />\r\n<br />\r\nĐ&acirc;y thật ra l&agrave; Ng&ocirc; Vọng ng&agrave;y b&igrave;nh thường tu h&agrave;nh Kỳ Tinh thuật trạng th&aacute;i b&igrave;nh thường.<br />\r\n<br />\r\n&Acirc;n<br />\r\n<br />\r\nNg&ocirc; Vọng đ&aacute;y l&ograve;ng đột nhi&ecirc;n hiện l&ecirc;n một ch&uacute;t linh quang.<br />\r\n<br />\r\nNếu l&agrave; m&igrave;nh theo Nh&acirc;n vực tu h&agrave;nh ph&aacute;p c&ocirc;ng ph&aacute;p cơ bản, đem tinh thần chi lực xem như thu nạp linh kh&iacute;, để tinh thần chi lực tại thể nội chu thi&ecirc;n vận chuyển, đ&acirc;y chẳng phải l&agrave; Tinh Thần Thối Thể<br />\r\n<br />\r\nCh&iacute;nh m&igrave;nh giống như trong l&uacute;c v&ocirc; t&igrave;nh mở ra c&aacute;i g&igrave; đại m&ocirc;n.<br />\r\n<br />\r\nNg&ocirc; Vọng nh&iacute;u m&agrave;y, lập tức liền muốn như thế nếm thử, nhưng hắn vừa mới chuẩn bị động thủ, trong c&otilde;i u minh phảng phất nghe thấy được một tiếng v&ocirc; c&ugrave;ng xa x&ocirc;i k&ecirc;u gọi:<br />\r\n<br />\r\n&#39;Ổn một tay ~ thiếu ni&ecirc;n ~ ổn một tay ~ &#39;<br />\r\n<br />\r\nNg&ocirc; Vọng kh&oacute;e miệng co giật xuống, b&igrave;nh tĩnh chỗ nhắm hai mắt, hai tay b&atilde;o nguy&ecirc;n quy nhất.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nC&aacute;i n&agrave;y lại kh&ocirc;ng phải l&agrave;m c&aacute;i g&igrave; nguy hiểm th&iacute; nghiệm, hắn sẽ chỉ d&ugrave;ng yếu ớt một tia tinh thần chi lực, tối đa cũng tựu kh&iacute; tức tương xung phun ngụm m&aacute;u, c&ograve;n c&oacute; thể chết người kh&ocirc;ng th&agrave;nh<br />\r\n<br />\r\nN&oacute;i l&agrave;m liền l&agrave;m!<br />\r\n<br />\r\nNg&ocirc; Vọng nhắm mắt Ngưng Thần, nhẹ nh&agrave;ng h&iacute;t v&agrave;o một hơi, một tia tinh thần chi lực từ hắn trong miệng tụ hợp v&agrave;o thể nội, hội tụ đan điền, du tẩu kinh mạch, &yacute; đồ đi qua một c&aacute;i tiểu chu thi&ecirc;n.<br />\r\n<br />\r\nTa c&oacute; một đạo tan vạn ph&aacute;p, d&aacute;m gọi thập nhật chuyển hồ thi&ecirc;n.<br />\r\n<br />\r\nTinh thần chi lực giống như d&ograve;ng suối, tại Ng&ocirc; Vọng lồng ngực xoay chầm chậm, thổi qua rất nhiều cửa ải, hướng đan điền hội tụ m&agrave; đi.<br />\r\n<br />\r\nPhương ph&aacute;p n&agrave;y c&oacute; thể th&agrave;nh<br />\r\n<br />\r\nChẳng lẽ l&agrave; bởi v&igrave; tinh thần ph&aacute;p tắc c&ugrave;ng c&aacute;i kh&aacute;c ph&aacute;p tắc kh&ocirc;ng thể tương dung, biểu hiện l&agrave; kh&aacute;c biệt tu h&agrave;nh hệ thống kh&ocirc;ng thể c&ugrave;ng tu<br />\r\n<br />\r\nNg&ocirc; Vọng gắt gao nh&igrave;n chằm chằm kia một tia tinh thần chi lực, c&agrave;ng l&agrave; đến l&uacute;c n&agrave;y, vượt dễ d&agrave;ng thất bại trong gang tấc.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; dấu hiệu n&agrave;o, c&aacute;i n&agrave;y một tia tinh thần chi lực quỷ dị nổ tan.<br />\r\n<br />\r\nNg&ocirc; Vọng mở bừng mắt ra, lọt v&agrave;o trong tầm mắt l&agrave; rực rỡ tinh kh&ocirc;ng, trước mặt l&agrave; tinh thần xuy&ecirc;n th&agrave;nh r&egrave;m ch&acirc;u, nhưng ở r&egrave;m ch&acirc;u b&ecirc;n trong, một tia m&agrave;u x&aacute;m tro nhạt ảm đạm sợi tơ đang muốn ti&ecirc;u t&aacute;n!<br />\r\n<br />\r\nLiền l&agrave; n&oacute;!<br />\r\n<br />\r\nKh&ocirc;ng kịp suy nghĩ nhi&ecirc;̀u kiểm tra, cũng kh&ocirc;ng đo&aacute;i ho&agrave;i tới suy nghĩ nhiều c&aacute;i g&igrave;, Ng&ocirc; Vọng &yacute; niệm b&ecirc;n trong &#39;Đưa tay&#39; nắm c&aacute;i n&agrave;y sợi tơ, &#39;D&ugrave;ng sức&#39; k&eacute;o một c&aacute;i.<br />\r\n<br />\r\nCảnh tượng trước mắt bỗng nhi&ecirc;n đại biến, nguy&ecirc;n bản b&igrave;nh tĩnh tường h&ograve;a tinh kh&ocirc;ng h&oacute;a th&agrave;nh v&ocirc; bi&ecirc;n lưu quang, tiền phương đen kịt một m&agrave;u, lại c&oacute; một hạt ch&ugrave;m s&aacute;ng tại đen nh&aacute;nh b&ecirc;n trong bỗng nhi&ecirc;n phồng lớn, h&oacute;a th&agrave;nh một t&ograve;a rộng lớn đại điện, đứng vững tại vạn tinh ph&iacute;a tr&ecirc;n!<br />\r\n<br />\r\nĐại điện n&agrave;y chỉnh thể bởi hơi mờ tinh thần ngọc đắp l&ecirc;n m&agrave; th&agrave;nh, chỉ c&oacute; đơn giản khung đỉnh, nền tảng c&ugrave;ng mười hai c&acirc;y trụ c&ocirc;̣t.<br />\r\n<br />\r\nNg&ocirc; Vọng đầu ng&oacute;n tay v&acirc;n v&ecirc; ảm đạm sợi tơ đ&atilde; chẳng biết l&uacute;c n&agrave;o ti&ecirc;u t&aacute;n kh&ocirc;ng thấy, m&agrave; th&acirc;n h&igrave;nh hắn lơ lửng tại đại điện b&ecirc;n ngo&agrave;i, lăng lăng nh&igrave;n xem trong đ&oacute; t&igrave;nh h&igrave;nh.<br />\r\n<br />\r\nNơi đ&acirc;y, ẩn chứa Kỳ Tinh thuật chung cực &aacute;o nghĩa<br />\r\n<br />\r\nNơi đ&acirc;y, cất giữ lấy tinh kh&ocirc;ng đạo tắc to&agrave;n bộ giải th&iacute;ch<br />\r\n<br />\r\nChỉ một c&aacute;i liếc mắt, v&ocirc; bi&ecirc;n tin tức tại Ng&ocirc; Vọng đ&aacute;y l&ograve;ng đột nhi&ecirc;n hiện ra, m&agrave; hắn &aacute;nh mắt đột nhi&ecirc;n bắt được một th&acirc;n ảnh, một đạo đứng tại to&agrave; n&agrave;y vạn tinh chi điện ch&iacute;nh giữa, như l&agrave; thạch tố th&acirc;n ảnh.<br />\r\n<br />\r\nN&agrave;ng th&acirc;n mang rực rỡ tinh b&agrave;o, cầm trong tay một c&acirc;y m&agrave;u đen nh&aacute;nh trường trượng, giờ ph&uacute;t n&agrave;y lại chậm r&atilde;i quay người, một đ&ocirc;i kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o thần sắc, bị thần quang lấp đầy con ngươi đ&atilde; để mắt tới Ng&ocirc; Vọng.<br />\r\n<br />\r\nNg&ocirc; Vọng sai lệch ph&iacute;a dưới, con ngươi c&oacute; chút rung động.<br />\r\n<br />\r\nC&aacute;i n&agrave;y, c&aacute;i n&agrave;y, c&aacute;i n&agrave;y!<br />\r\n<br />\r\nKia nữ thần mở miệng n&oacute;i ra một c&acirc;u tối nghĩa kh&oacute; hiểu ngữ điệu, Ng&ocirc; Vọng đ&aacute;y l&ograve;ng nhưng trong nh&aacute;y mắt minh bạch lời n&oacute;i n&agrave;y h&agrave;m nghĩa:<br />\r\n<br />\r\n&quot;Tự tiện x&ocirc;ng v&agrave;o Th&aacute;nh Điện người, chết.&quot;<br />\r\n<br />\r\nChữ chết chưa rơi, th&acirc;n ảnh kia trong tay trường trượng bay ra một đạo ph&iacute;ch lịch, đối Ng&ocirc; Vọng thần niệm v&agrave;o đầu đập tới!<br />\r\n<br />\r\nC&ugrave;ng n&agrave;y đồng thời, Ng&ocirc; Vọng chỗ lều lớn tr&ecirc;n kh&ocirc;ng!<br />\r\n<br />\r\nS&acirc;u trong tinh kh&ocirc;ng quỷ dị xuất hiện một đạo m&agrave;u xanh biếc l&ocirc;i trụ, hướng Ng&ocirc; Vọng lều lớn ngang nhi&ecirc;n đ&aacute;nh rớt, lều lớn b&ecirc;n trong mọi người, thậm ch&iacute; kh&ocirc;ng kịp ph&aacute;t hiện c&aacute;i n&agrave;y l&ocirc;i trụ x&acirc;m nhập, mắt thấy l&agrave; phải h&ocirc;i phi y&ecirc;n diệt!<br />\r\n<br />\r\nC&aacute;i n&agrave;y một c&aacute;i chớp mắt!<br />\r\n<br />\r\nTựu c&aacute;i n&agrave;y một c&aacute;i chớp mắt!<br />\r\n<br />\r\nNg&ocirc; Vọng thần niệm tại cung điện kia trước, Ng&ocirc; Vọng th&acirc;n thể tại trong lều lớn, động t&aacute;c tr&igrave; trệ, tr&agrave;n đầy nghi hoặc, h&aacute; mồm h&ocirc; l&ecirc;n một tiếng v&ocirc; c&ugrave;ng kinh ngạc &acirc;m tiết.<br />\r\n<br />\r\nLiền l&agrave; tiếng h&ocirc; ho&aacute;n n&agrave;y!<br />\r\n<br />\r\nTrong thần điện th&acirc;n ảnh cực nhanh chỗ nhắm mắt, mở mắt, trong mắt thần quang trong nh&aacute;y mắt lui bước, tr&agrave;n đầy sốt ruột chỗ đối Ng&ocirc; Vọng mở ra tay tr&aacute;i.<br />\r\n<br />\r\nNg&ocirc; Vọng chỗ lều lớn, d&aacute;n chặt lấy lều lớn trướng đỉnh, một đạo m&agrave;u xanh biếc b&igrave;nh chướng đột nhi&ecirc;n hiện ra, Ng&ocirc; Vọng trước người chẳng biết l&uacute;c n&agrave;o nhiều đạo th&acirc;n ảnh, t&oacute;c d&agrave;i c&ugrave;ng khoan b&agrave;o c&ograve;n tại nhẹ nh&agrave;ng phất phới.<br />\r\n<br />\r\nL&acirc;m Tố Khinh ở b&ecirc;n thấy ngơ ng&aacute;c sững sờ<br />\r\n<br />\r\nBọn thị vệ cũng đ&atilde; một gối chạm đất, một tay che ở ngực, c&uacute;i đầu h&agrave;nh lễ.<br />\r\n<br />\r\nNg&ocirc; Vọng h&ocirc; l&ecirc;n c&aacute;i chữ kia mắt phảng phất c&ograve;n tại trong trướng bồng v&ograve;ng chuyển, lại l&agrave; một tiếng<br />\r\n<br />\r\n&quot;Nương &quot;</p>', '2021-11-27 21:25:25', 4, 1),
(185, 50, 'Tuần Kiểm ti', '1', '<p>Ng&acirc;n Th&agrave;nh.<br />\r\n<br />\r\nTuần Kiểm ti.<br />\r\n<br />\r\nTuổi trẻ L&yacute; Hạo, d&aacute;ng người hơi c&oacute; vẻ thon gầy, v&agrave;nh mắt c&oacute; ch&uacute;t biến th&agrave;nh m&agrave;u đen, giống như tối h&ocirc;m qua thức đ&ecirc;m chưa ngủ.<br />\r\n<br />\r\nMặc Tuần Kiểm ti tuần kiểm cấp ba chế ngự, L&yacute; Hạo cất bước bước v&agrave;o Tuần Kiểm ti khu l&agrave;m việc.<br />\r\n<br />\r\nL&agrave;m một t&ecirc;n gia nhập Tuần Kiểm ti mới một năm nửa người mới, L&yacute; Hạo tại Tuần Kiểm ti tư lịch kh&ocirc;ng s&acirc;u, b&igrave;nh thường đều sẽ hơi đến sớm một ch&uacute;t, đơn giản qu&eacute;t dọn một ch&uacute;t khu l&agrave;m việc vệ sinh, lại đốt ấm nước , chờ đợi những đồng nghiệp kh&aacute;c đến.<br />\r\n<br />\r\nBất qu&aacute; h&ocirc;m nay L&yacute; Hạo, tới so b&igrave;nh thường hơi trễ một điểm, giờ ph&uacute;t n&agrave;y khu l&agrave;m việc đ&atilde; c&oacute; kh&ocirc;ng &iacute;t người đ&atilde; đến.<br />\r\n<br />\r\nNh&igrave;n thấy L&yacute; Hạo v&agrave;o cửa, cửa ra v&agrave;o b&agrave;n c&ocirc;ng t&aacute;c, một vị đồng dạng mặc đồng phục b&aacute;c g&aacute;i trung ni&ecirc;n, một mặt nhiệt t&igrave;nh, mang theo một ch&uacute;t tr&ecirc;u chọc &yacute; vị, tr&ecirc;u ghẹo n&oacute;i: &quot;Tiểu Hạo, h&ocirc;m nay tới chậm, mắt quầng th&acirc;m đều đi ra, tối h&ocirc;m qua l&agrave; kh&ocirc;ng phải đi ti&ecirc;u s&aacute;i?&quot;<br />\r\n<br />\r\nL&yacute; Hạo lộ ra tinh khiết d&aacute;ng tươi cười, giống như rất đơn thuần chất ph&aacute;c, vội v&agrave;ng kho&aacute;t tay: &quot;Du tỷ, cũng chớ n&oacute;i lung tung, ta c&ograve;n kh&ocirc;ng c&oacute; t&igrave;m bạn g&aacute;i đ&acirc;u, truyền ra ngo&agrave;i, về sau kh&ocirc;ng ai gả ta!&quot;<br />\r\n<br />\r\n&quot;Ha ha ha, ngươi đứa nhỏ n&agrave;y, đều tại Tuần Kiểm ti chờ đợi một năm, điểm ấy n&oacute;i đều kh&ocirc;ng tiếp nổi.&quot;<br />\r\n<br />\r\nTrung ni&ecirc;n phụ nh&acirc;n giống như rất ưa th&iacute;ch tr&ecirc;u ghẹo L&yacute; Hạo.<br />\r\n<br />\r\nNh&igrave;n L&yacute; Hạo &aacute;nh mắt, cũng mang theo một ch&uacute;t kh&ocirc;ng giống b&igrave;nh thường, cởi mở cười một trận, bỗng nhi&ecirc;n lời cũ nhắc lại n&oacute;i: &quot;Tiểu Hạo, b&igrave;nh thường một người kh&ocirc;ng c&oacute; thời gian nấu cơm, b&ecirc;n ngo&agrave;i ăn kh&ocirc;ng sạch sẽ, quay đầu đi nh&agrave; ta ăn.&quot;<br />\r\n<br />\r\nL&yacute; Hạo lần nữa lộ ra d&aacute;ng tươi cười, bất qu&aacute; vẫn l&agrave; cự tuyệt, &quot;Du tỷ, liền kh&ocirc;ng cho ngươi th&ecirc;m phiền to&aacute;i.&quot;<br />\r\n<br />\r\nDu tỷ c&ograve;n chưa mở miệng, c&aacute;ch đ&oacute; kh&ocirc;ng xa, một vị đại h&aacute;n trung ni&ecirc;n cười nhạo một tiếng, xen v&agrave;o n&oacute;i: &quot;Tiểu Hạo, ngươi Du tỷ l&agrave; gọi ngươi đi ăn cơm sao? L&agrave; gọi ngươi tới cửa l&agrave;m con rể đ&acirc;u, ngươi tiểu tử n&agrave;y, l&agrave;m sao một ch&uacute;t kh&ocirc;ng thượng đạo đ&acirc;u!&quot;<br />\r\n<br />\r\n&quot;Ha ha ha!&quot;<br />\r\n<br />\r\nKhu l&agrave;m việc trong nh&aacute;y mắt vang l&ecirc;n một trận cười vang.<br />\r\n<br />\r\nDu tỷ bị vạch trần mục đ&iacute;ch, cũng kh&ocirc;ng xấu hổ, mở to hai mắt nh&igrave;n, mạnh mẽ mắng: &quot;L&atilde;o nương vui l&ograve;ng! Tiểu Hạo tốt bao nhi&ecirc;u, nh&acirc;n phẩm tốt, đầu &oacute;c tốt, tướng mạo cũng tốt, thật cho ta l&agrave;m con rể, ta nằm mơ đều c&oacute; thể cười tỉnh!&quot;<br />\r\n<br />\r\nLời n&agrave;y vừa ra, đ&aacute;m người cười về cười, hay l&agrave; kh&ocirc;ng &iacute;t người gật gật đầu biểu thị đồng &yacute;.<br />\r\n<br />\r\nL&yacute; Hạo tiểu tử n&agrave;y, kh&ocirc;ng &iacute;t người hay l&agrave; rất xem trọng.<br />\r\n<br />\r\nTrước đ&oacute; n&oacute;i tiếp đại h&aacute;n trung ni&ecirc;n, bị mắng cũng kh&ocirc;ng th&egrave;m để &yacute;, ngược lại c&oacute; ch&uacute;t tiếc nuối n&oacute;i: &quot;Tiểu Hạo l&agrave; kh&ocirc;ng tệ, ch&iacute;nh l&agrave; c&oacute; ch&uacute;t đ&aacute;ng tiếc!&quot;<br />\r\n<br />\r\nĐ&aacute;ng tiếc c&aacute;i g&igrave;?<br />\r\n<br />\r\nLời n&agrave;y vừa ra, những người kh&aacute;c lộ ra một ch&uacute;t tiếc nuối biểu lộ.<br />\r\n<br />\r\nNgược lại l&agrave; L&yacute; Hạo bản nh&acirc;n kh&ocirc;ng qu&aacute; để &yacute;, d&aacute;ng tươi cười x&aacute;n lạn, &quot;Chu ca, ch&iacute;nh ta chọn, c&oacute; g&igrave; c&oacute; thể tiếc nuối?&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nHọ Chu đại h&aacute;n vẫn c&ograve;n c&oacute; ch&uacute;t đ&aacute;ng tiếc, thở d&agrave;i: &quot;Kh&ocirc;ng c&oacute; khả năng n&oacute;i như vậy, Tiểu Hạo, Tuần Kiểm ti l&agrave; kh&ocirc;ng tệ, ngươi nửa năm chuyển ch&iacute;nh thức, hiện tại l&agrave; tuần kiểm cấp ba, đối với người kh&aacute;c tới n&oacute;i cũng kh&ocirc;ng tệ. Có th&ecirc;̉ ngươi nếu l&agrave; kh&ocirc;ng từ Ng&acirc;n Th&agrave;nh cổ viện nghỉ học , đợi đến từ Ng&acirc;n Th&agrave;nh cổ viện tốt nghiệp, ngươi nếu l&agrave; c&ograve;n lựa chọn tiến Tuần Kiểm ti, vậy tiến v&agrave;o Tuần Kiểm ti ch&iacute;nh l&agrave; tuần kiểm cấp một, đ&acirc;y l&agrave; thấp nhất!&quot;<br />\r\n<br />\r\nLời n&agrave;y vừa n&oacute;i ra, đ&aacute;m người cũng bị đưa tới ph&aacute;t biểu dục vọng.<br />\r\n<br />\r\nL&yacute; Hạo sau lưng, mới vừa v&agrave;o cửa tuổi trẻ lạt muội Trần Na cấp tốc n&oacute;i tiếp: &quot;Đúng đ&acirc;́y, L&yacute; Hạo, ngươi đang y&ecirc;n đang l&agrave;nh sớm nghỉ học l&agrave;m c&aacute;i g&igrave;? Ngươi xem ch&uacute;ng ta, muốn thi Ng&acirc;n Th&agrave;nh cổ viện đều kh&ocirc;ng c&oacute; cơ hội, c&ograve;n k&eacute;m hai năm liền c&oacute; thể tốt nghiệp, ch&uacute;ng ta những người n&agrave;y từ tuần kiểm cấp ba nhịn đến cấp một, d&ugrave; l&agrave; thuận lợi, kh&ocirc;ng c&oacute; năm năm trở l&ecirc;n đều kh&ocirc;ng c&oacute; cơ hội, kh&ocirc;ng thuận lợi, cả một đời đến c&ugrave;ng, có th&ecirc;̉ ngao c&aacute;i tuần kiểm cấp một cũng kh&ocirc;ng tệ rồi!&quot;<br />\r\n<br />\r\nTrong lời n&oacute;i mang theo một ch&uacute;t h&acirc;m mộ, h&acirc;m mộ l&agrave; Ng&acirc;n Th&agrave;nh cổ viện sinh vi&ecirc;n tốt nghiệp.<br />\r\n<br />\r\nCũng c&oacute; ch&uacute;t tiếc nuối, tiếc nuối l&agrave; L&yacute; Hạo c&ograve;n k&eacute;m hai năm tốt nghiệp, bỗng nhi&ecirc;n sớm th&ocirc;i học, lựa chọn gia nhập Tuần Kiểm ti.<br />\r\n<br />\r\nL&yacute; Hạo gia nhập Tuần Kiểm ti, ngay cả Ng&acirc;n Th&agrave;nh Tuần Kiểm ti ti trưởng đều tới hỏi qua, thậm ch&iacute; c&ograve;n khuy&ecirc;n L&yacute; Hạo trở về tiếp tục học tập, thật muốn đến, chờ tốt nghiệp lại ghi danh Tuần Kiểm ti.<br />\r\n<br />\r\nĐ&aacute;ng tiếc, L&yacute; Hạo mặc d&ugrave; tốt n&oacute;i chuyện, tuổi trẻ, hiểu lễ ph&eacute;p, nhưng lại tại tr&ecirc;n việc n&agrave;y trục cực k&igrave;.<br />\r\n<br />\r\nTừ trước đến nay nghe lời hắn, lại l&agrave; kh&ocirc;ng c&oacute; đ&aacute;p ứng trở về tiếp tục học tập.<br />\r\n<br />\r\nTrần Na thậm ch&iacute; biết, L&yacute; Hạo tại Ng&acirc;n Th&agrave;nh cổ viện đạo sư, đều từng khuy&ecirc;n qua L&yacute; Hạo kh&ocirc;ng cần nghỉ học, hắn vẫn rất c&oacute; tiền đồ.<br />\r\n<br />\r\nNghe đ&aacute;m người thảo luận sự t&igrave;nh của ri&ecirc;ng m&igrave;nh, L&yacute; Hạo vẫn như cũ vẻ mặt tươi cười.<br />\r\n<br />\r\nĐi đến nơi hẻo l&aacute;nh, bắt đầu nấu nước, một b&ecirc;n bận rộn, vừa cười đ&aacute;p lại n&oacute;i: &quot;Từ tuần kiểm cấp ba bắt đầu l&agrave;m l&ecirc;n, kh&ocirc;ng phải cũng rất tốt? Lại n&oacute;i, thật muốn từ Ng&acirc;n Th&agrave;nh cổ viện tốt nghiệp lại đi v&agrave;o, đ&acirc;y chẳng phải l&agrave; thiếu thời gian hai năm nhận biết c&aacute;c vị đại ca đại tỷ? Vậy cũng rất tiếc nuối.&quot;<br />\r\n<br />\r\n&quot;Ha ha ha, n&oacute;i rất đ&uacute;ng!&quot;<br />\r\n<br />\r\nMọi người nhất thời cười to, lời n&agrave;y nghe dễ chịu.<br />\r\n<br />\r\nL&yacute; Hạo miệng rất ngọt, ni&ecirc;n kỷ lại nhỏ, năm nay vừa đ&acirc;̀y hai mươi, tại Ng&acirc;n Th&agrave;nh Tuần Kiểm ti xem như nhỏ nhất một vị, lại l&agrave; cao t&agrave;i sinh, nghe hắn ton h&oacute;t lời n&oacute;i, mọi người nghe đều rất dễ chịu.<br />\r\n<br />\r\nS&aacute;ng sớm, Tuần Kiểm ti khu l&agrave;m việc tr&agrave;n đầy hoan thanh tiếu ngữ.<br />\r\n<br />\r\nĐối với L&yacute; Hạo từ Ng&acirc;n Th&agrave;nh cổ viện nghỉ học, mọi người cũng kh&ocirc;ng c&oacute; tiếp tục nhiều lời, cũng liền L&yacute; Hạo kh&ocirc;ng qu&aacute; để &yacute;, nếu kh&ocirc;ng tất cả mọi người sẽ kh&ocirc;ng đề cập, d&ugrave; sao từ cổ viện nghỉ học, đ&iacute;ch thật l&agrave; trong nh&acirc;n sinh một nỗi tiếc nuối kh&ocirc;n ngu&ocirc;i.<br />\r\n<br />\r\nĐối với l&uacute;c ấy L&yacute; Hạo như thế n&agrave;o c&acirc;n nhắc, v&igrave; sao như thế lựa chọn, mọi người cũng kh&ocirc;ng tốt hỏi nhiều.<br />\r\n<br />\r\nDựa theo L&yacute; Hạo lời n&oacute;i của ch&iacute;nh m&igrave;nh, l&agrave; v&igrave; sớm cầm tiền lương, cổ viện học ph&iacute; qu&aacute; cao, tốn hao qu&aacute; lớn, hắn kh&ocirc;ng c&oacute; tiền.<br />\r\n<br />\r\nThế nhưng l&agrave;, l&agrave;m cổ viện học vi&ecirc;n, học ph&iacute; kh&ocirc;ng đủ, thật l&agrave; nguy&ecirc;n nh&acirc;n sao?<br />\r\n<br />\r\nTiếng cười vui, theo mấy vị Tuần Kiểm ti l&atilde;nh đạo đến, dần dần ngừng.<br />\r\n<br />\r\nNg&acirc;n Th&agrave;nh Tuần Kiểm ti, l&agrave; Ng&acirc;n Th&agrave;nh cơ cấu chấp ph&aacute;p tổng bộ.<br />\r\n<br />\r\nTrừ tổng bộ, Tuần Kiểm ti tại Ng&acirc;n Th&agrave;nh c&ograve;n c&oacute; bốn chỗ ph&acirc;n bộ.<br />\r\n<br />\r\nM&agrave; tổng bộ cũng c&oacute; bao nhi&ecirc;u c&aacute;i bộ m&ocirc;n, L&yacute; Hạo chỗ ph&ograve;ng cơ yếu, chủ yếu phụ tr&aacute;ch vụ &aacute;n hồ sơ đệ đơn, bản &aacute;n cũ khởi động lại, &aacute;n chưa giải quyết ph&uacute;c thẩm, hội nghị kỷ yếu. . .<br />\r\n<br />\r\nPh&ograve;ng cơ yếu cũng kh&ocirc;ng phải l&agrave; một đường cơ cấu, rất &iacute;t tham dự đối ngoại chấp ph&aacute;p.<br />\r\n<br />\r\nĐương nhi&ecirc;n, nếu l&agrave; những bộ m&ocirc;n kh&aacute;c nh&acirc;n thủ kh&ocirc;ng đủ d&ugrave;ng, cũng sẽ l&acirc;m thời điều tạm L&yacute; Hạo bọn hắn đi qua hỗ trợ, tổng thể tới n&oacute;i hay l&agrave; văn chức l&agrave;m việc.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nTheo mấy vị l&atilde;nh đạo đến, đ&aacute;m người lại bắt đầu một ng&agrave;y bận rộn l&agrave;m việc.<br />\r\n<br />\r\nL&yacute; Hạo kh&ocirc;ng hề đơn độc ph&ograve;ng l&agrave;m việc, một vị nhập chức một năm tuần kiểm cấp ba, cũng kh&ocirc;ng c&oacute; tư c&aacute;ch c&oacute; được, trước đ&oacute; Du tỷ, Chu ca mấy vị đều l&agrave; tuần kiểm cấp hai, bọn hắn cũng kh&ocirc;ng c&oacute; tư c&aacute;ch.<br />\r\n<br />\r\nL&yacute; Hạo b&agrave;n c&ocirc;ng t&aacute;c đang l&agrave;m việc khu tới gần ph&ograve;ng vệ sinh phương vị, cũng kh&ocirc;ng c&oacute; qu&aacute; nhiều m&ugrave;i vị kh&aacute;c thường, chỉ l&agrave; người đến người đi, c&oacute; vẻ hơi ồn &agrave;o, khu l&agrave;m việc c&aacute;c l&atilde;o nh&acirc;n đều sẽ kh&ocirc;ng th&iacute;ch b&ecirc;n n&agrave;y.<br />\r\n<br />\r\nL&yacute; Hạo b&agrave;n c&ocirc;ng t&aacute;c c&ugrave;ng đối diện Trần Na dựa chung một chỗ, hai người mặt đối mặt l&agrave;m việc.<br />\r\n<br />\r\nTrần Na cũng chỉ so L&yacute; Hạo sớm đến nửa năm, cũng coi l&agrave; người mới, hai người tư lịch đều kh&ocirc;ng s&acirc;u.<br />\r\n<br />\r\nBất qu&aacute; rất nhanh Tuần Kiểm ti liền sẽ chi&ecirc;u t&acirc;n, tiếp xuống hai người liền c&oacute; thể tho&aacute;t khỏi người mới danh h&agrave;o.<br />\r\n<br />\r\nL&yacute; Hạo đang c&uacute;i đầu t&igrave;m đọc một ch&uacute;t văn bản t&agrave;i liệu hồ sơ, đối diện truyền đến một trận c&aacute;i b&agrave;n nhẹ vang l&ecirc;n &acirc;m thanh, L&yacute; Hạo ngẩng đầu nh&igrave;n lại, chỉ thấy Trần Na nằm nho&agrave;i tr&ecirc;n mặt b&agrave;n, nhẹ nh&agrave;ng g&otilde; c&aacute;i b&agrave;n, gặp L&yacute; Hạo xem ra, d&aacute;ng tươi cười x&aacute;n lạn, thấp giọng n&oacute;i: &quot;L&yacute; Hạo, tiếp xuống Tuần Kiểm ti c&oacute; c&aacute;i ngoại xuất nhiệm vụ, c&oacute; thể ra ngo&agrave;i một th&aacute;ng, muốn hay kh&ocirc;ng xin mời? Ch&uacute;ng ta c&ugrave;ng một chỗ, đi ra ngo&agrave;i chơi một th&aacute;ng , nhiệm vụ rất nhẹ nh&agrave;ng.&quot;<br />\r\n<br />\r\nL&yacute; Hạo nao nao, hồi tưởng một ch&uacute;t, rất nhanh lắc đầu n&oacute;i: &quot;Kh&ocirc;ng đi, ra ngo&agrave;i cũng kh&ocirc;ng c&oacute; g&igrave; tốt, m&agrave; lại. . . Cũng kh&ocirc;ng nhất định an to&agrave;n.&quot;<br />\r\n<br />\r\n&quot;Rất an to&agrave;n!&quot;<br />\r\n<br />\r\nTrần Na c&oacute; ch&uacute;t buồn bực, &quot;Ch&iacute;nh l&agrave; bồi Ng&acirc;n Th&agrave;nh cổ viện. . .&quot;<br />\r\n<br />\r\nN&oacute;i đến đ&acirc;y, n&agrave;ng nao nao, nghĩ tới điều g&igrave;, lại nh&igrave;n L&yacute; Hạo, c&oacute; ch&uacute;t minh ngộ, c&oacute; ch&uacute;t &aacute;y n&aacute;y n&oacute;i: &quot;A, su&yacute;t nữa qu&ecirc;n mất chuyện n&agrave;y! Ngươi l&agrave; kh&ocirc;ng muốn gặp bạn học ng&agrave;y xưa c&ugrave;ng đạo sư thật sao? Lần n&agrave;y dẫn đội đi khảo s&aacute;t giống như ch&iacute;nh l&agrave; Vi&ecirc;n Thạc gi&aacute;o sư. . .&quot;<br />\r\n<br />\r\nN&agrave;ng thầm mắng một tiếng, ch&iacute;nh m&igrave;nh thế m&agrave; qu&ecirc;n việc n&agrave;y.<br />\r\n<br />\r\nVi&ecirc;n Thạc, kh&ocirc;ng phải liền l&agrave; L&yacute; Hạo trước đ&oacute; tại cổ viện đạo sư sao?<br />\r\n<br />\r\nNghe n&oacute;i vị đạo sư n&agrave;y rất coi trọng L&yacute; Hạo, bởi v&igrave; L&yacute; Hạo kh&ocirc;ng nghe khuy&ecirc;n bảo, nhất định phải nghỉ học, nghe n&oacute;i c&ograve;n một lần muốn đ&aacute;nh L&yacute; Hạo, tại Ng&acirc;n Th&agrave;nh cổ viện n&aacute;o động l&ecirc;n phong ba kh&ocirc;ng nhỏ, cũng l&agrave;m cho kh&ocirc;ng &iacute;t cổ viện học vi&ecirc;n mắng L&yacute; Hạo kh&ocirc;ng biết điều.<br />\r\n<br />\r\nVi&ecirc;n Thạc l&agrave; ai?<br />\r\n<br />\r\nĐ&acirc;y ch&iacute;nh l&agrave; cổ viện mấy vị đỉnh cấp đại l&atilde;o một trong, bao nhi&ecirc;u học vi&ecirc;n kh&aacute;t vọng b&aacute;i sư Vi&ecirc;n Thạc, kết quả người ta căn bản chướng mắt.<br />\r\n<br />\r\nL&yacute; Hạo cười cười, cũng kh&ocirc;ng c&oacute; phản b&aacute;c.<br />\r\n<br />\r\nKh&ocirc;ng muốn gặp sao?</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nThế th&igrave; kh&ocirc;ng đến mức.<br />\r\n<br />\r\nNgoại giới truyền lợi hại, tr&ecirc;n thực tế kh&ocirc;ng c&oacute; khoa trương như vậy, Vi&ecirc;n l&atilde;o sư chỉ l&agrave; đối với m&igrave;nh lựa chọn cảm thấy tiếc nuối th&ocirc;i, v&agrave;i ng&agrave;y trước L&yacute; Hạo c&ograve;n đi nh&agrave; l&atilde;o sư b&aacute;i phỏng qua, c&ugrave;ng một chỗ ăn cơm tối, n&agrave;o c&oacute; ngoại nh&acirc;n n&oacute;i gặp mặt liền đ&aacute;nh ch&iacute;nh m&igrave;nh khoa trương như vậy.<br />\r\n<br />\r\nĐương nhi&ecirc;n, c&oacute; một số việc cũng kh&ocirc;ng cần thiết đi giải th&iacute;ch, ngoại giới l&agrave;m sao truyền, vậy t&ugrave;y bọn hắn t&acirc;m &yacute; liền tốt, c&agrave;ng giải th&iacute;ch c&agrave;ng phiền phức.<br />\r\n<br />\r\n&quot;Vi&ecirc;n l&atilde;o sư dẫn đội. . .&quot;<br />\r\n<br />\r\nTrong l&ograve;ng mặc niệm một tiếng, Vi&ecirc;n l&atilde;o sư l&agrave; cổ viện mấy vị đỉnh cấp gi&aacute;o sư một trong, dẫn đội đi ra ngo&agrave;i, bảo an hẳn l&agrave; sẽ kh&ocirc;ng qu&aacute; k&eacute;m, Tuần Kiểm ti cũng tốt, hay l&agrave; những bộ m&ocirc;n kh&aacute;c, đều sẽ l&agrave;m tốt đề ph&ograve;ng.<br />\r\n<br />\r\nCh&iacute;nh m&igrave;nh cũng l&agrave; kh&ocirc;ng cần lo lắng c&aacute;i g&igrave;, huống chi, Tuần Kiểm ti tham dự trong đ&oacute;, x&aacute;c suất lớn cũng ch&iacute;nh l&agrave; đ&aacute;nh c&aacute;i x&igrave; dầu, ch&acirc;n ch&iacute;nh phụ tr&aacute;ch Vi&ecirc;n l&atilde;o sư bảo an, chỉ sợ kh&ocirc;ng phải Tuần Kiểm ti những t&ocirc;m t&eacute;p n&agrave;y, m&agrave; l&agrave; ch&iacute;nh m&igrave;nh đ&atilde; qua một năm mơ hồ c&oacute; nghe thấy Tuần Dạ Nh&acirc;n.<br />\r\n<br />\r\nTuần Dạ Nh&acirc;n, c&ugrave;ng loại Tuần Kiểm ti dạng n&agrave;y cơ cấu chấp ph&aacute;p.<br />\r\n<br />\r\nBất qu&aacute;. . . C&ugrave;ng Tuần Kiểm ti phụ tr&aacute;ch tr&ecirc;n mặt nổi vụ &aacute;n kh&aacute;c biệt, cơ cấu n&agrave;y giống như chỉ phụ tr&aacute;ch một ch&uacute;t nghi &aacute;n &aacute;n chưa giải quyết, Tuần Kiểm ti b&ecirc;n n&agrave;y một ch&uacute;t kh&ocirc;ng c&aacute;ch n&agrave;o xử l&yacute;, hoặc l&agrave; n&oacute;i dứt kho&aacute;t kh&ocirc;ng c&oacute; bất kỳ đầu mối g&igrave; vụ &aacute;n, đều sẽ giao ra.<br />\r\n<br />\r\nNgoại nh&acirc;n kh&ocirc;ng hiểu r&otilde;, thậm ch&iacute; Tuần Kiểm ti cơ cấu, phần lớn người cũng kh&ocirc;ng r&otilde; r&agrave;ng.<br />\r\n<br />\r\nBất qu&aacute; L&yacute; Hạo th&acirc;n ở ph&ograve;ng cơ yếu, chủ yếu ch&iacute;nh l&agrave; phụ tr&aacute;ch vụ &aacute;n đệ đơn, nghi &aacute;n &aacute;n chưa giải quyết truy tung, ngược lại l&agrave; mơ hồ biết được một hai.<br />\r\n<br />\r\n&quot;Tuần Dạ Nh&acirc;n!&quot;<br />\r\n<br />\r\nL&yacute; Hạo trong l&ograve;ng mặc niệm một c&acirc;u, hắn lựa chọn nghỉ học, đi v&agrave;o Tuần Kiểm ti khi một c&aacute;i tuần kiểm cấp ba, c&ugrave;ng Tuần Dạ Nh&acirc;n quan hệ rất lớn, hoặc l&agrave; n&oacute;i, hơn ph&acirc;n nửa mục đ&iacute;ch đ&uacute;ng l&agrave; hướng về ph&iacute;a b&ecirc;n kia đi.<br />\r\n<br />\r\nĐương nhi&ecirc;n, việc n&agrave;y chưa bao giờ đ&atilde; n&oacute;i với bất luận kẻ n&agrave;o.<br />\r\n<br />\r\nCơ cấu n&agrave;y qu&aacute; thần b&iacute;, L&yacute; Hạo khi tiến v&agrave;o Tuần Kiểm ti trước đ&oacute; kỳ thật chưa từng nghe n&oacute;i qua, nhưng l&agrave; hắn từ Vi&ecirc;n Thạc đạo sư b&ecirc;n kia biết được một hai, biết c&oacute; dạng n&agrave;y một c&aacute;i cơ cấu chấp ph&aacute;p tồn tại, người b&igrave;nh thường căn bản sẽ kh&ocirc;ng biết được.<br />\r\n<br />\r\nBất qu&aacute; nghỉ học trước đ&oacute;, L&yacute; Hạo biết một ch&uacute;t, Tuần Kiểm ti nhưng thật ra l&agrave; Tuần Dạ Nh&acirc;n thẳng đứng cấp dưới cơ cấu, ngoại nh&acirc;n kh&ocirc;ng biết, L&yacute; Hạo ngược lại l&agrave; nghe Vi&ecirc;n l&atilde;o đề cập qua một c&acirc;u, Tuần Kiểm ti b&ecirc;n trong một ch&uacute;t hạt giống tốt, c&oacute; thể sẽ bị chuyển vận đi qua.<br />\r\n<br />\r\n&quot;Một năm, ta hiện tại cũng coi l&agrave; căn ch&iacute;nh mi&ecirc;u hồng, Tuần Kiểm ti xuất th&acirc;n, kh&ocirc;ng biết c&oacute; cơ hội hay kh&ocirc;ng tiếp x&uacute;c đến Tuần Dạ Nh&acirc;n.&quot;<br />\r\n<br />\r\nL&yacute; Hạo thầm nghĩ lấy, mang theo một ch&uacute;t bức thiết, lại l&agrave; cưỡng chế đi, sắc mặt như thường, kh&ocirc;ng ch&uacute;t n&agrave;o lộ ra.<br />\r\n<br />\r\nCũng nhanh!<br />\r\n<br />\r\nHắn cảm gi&aacute;c gần nhất Ng&acirc;n Th&agrave;nh c&oacute; thể muốn xảy ra vấn đề, Tuần Kiểm ti lại l&agrave; kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o ph&aacute;t gi&aacute;c, chỉ sợ chỉ c&oacute; thể để Tuần Dạ Nh&acirc;n đến xử l&yacute;.<br />\r\n<br />\r\nGiờ ph&uacute;t n&agrave;y, c&ograve;n k&eacute;m một cơ hội!<br />\r\n<br />\r\nPS: S&aacute;ch mới tuy&ecirc;n bố cầu duy tr&igrave;, đổi mở đầu sửa lại thật nhiều ng&agrave;y, c&agrave;ng đổi c&agrave;ng đau đầu, cứ như vậy đi, ta cũng kh&ocirc;ng lấy mở đầu thủ thắng, từ từ sẽ đến, hậu t&iacute;ch bạc ph&aacute;t mới l&agrave; diều h&acirc;u phong c&aacute;ch, s&aacute;ch mới kỳ một ng&agrave;y hai canh, rất l&acirc;u kh&ocirc;ng c&oacute; g&otilde; chữ, trước th&iacute;ch ứng một ch&uacute;t, xế chiều mỗi ng&agrave;y c&ugrave;ng ban đ&ecirc;m tất cả canh một.</p>', '2021-11-27 21:27:27', 21, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(186, 50, 'Hồng ảnh', '2', '<p>Cơ hội, l&agrave; c&acirc;̀n ch&iacute;nh m&igrave;nh đi s&aacute;ng tạo.<br />\r\n<br />\r\nL&yacute; Hạo cần thời cơ, cũng cần ch&iacute;nh m&igrave;nh đi s&aacute;ng tạo.<br />\r\n<br />\r\nRất nhanh, L&yacute; Hạo trước mặt chỉnh l&yacute; tốt một phần hồ sơ, đứng dậy, L&yacute; Hạo hướng khu l&agrave;m việc ph&ograve;ng l&agrave;m việc nhỏ đi đến.<br />\r\n<br />\r\n&quot;Đ&ocirc;ng đ&ocirc;ng đ&ocirc;ng!&quot;<br />\r\n<br />\r\nL&yacute; Hạo g&otilde; ph&ograve;ng l&agrave;m việc cửa lớn.<br />\r\n<br />\r\nTrong m&ocirc;n truyền đến ph&ograve;ng cơ yếu trưởng ph&ograve;ng thanh &acirc;m: &quot;Tiến đến!&quot;<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nL&yacute; Hạo đẩy ra ph&ograve;ng l&agrave;m việc cửa lớn.<br />\r\n<br />\r\n&quot;Trưởng ph&ograve;ng!&quot;<br />\r\n<br />\r\n&quot;L&yacute; Hạo a!&quot;<br />\r\n<br />\r\nNgười đ&atilde; trung ni&ecirc;n Vương Kiệt, nh&igrave;n thấy L&yacute; Hạo, kh&ocirc;ng khỏi lộ ra d&aacute;ng tươi cười.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; một c&aacute;i hiểu lễ ph&eacute;p, chịu l&agrave;m việc hảo h&agrave;i tử, d&ugrave; sao nhận qua Ng&acirc;n Th&agrave;nh cổ viện gi&aacute;o dục cao đẳng, mặc d&ugrave; sớm th&ocirc;i học, thế nhưng kh&ocirc;ng phải l&agrave; bị khai trừ, m&agrave; l&agrave; ch&iacute;nh m&igrave;nh nghỉ học, hắn hay l&agrave; rất ch&uacute; &yacute;.<br />\r\n<br />\r\nL&yacute; Hạo cũng lộ ra d&aacute;ng tươi cười, rất nhanh mở miệng n&oacute;i: &quot;Trưởng ph&ograve;ng, ta c&oacute; việc b&aacute;o c&aacute;o.&quot;<br />\r\n<br />\r\n&quot;Ngồi đi!&quot;<br />\r\n<br />\r\nVương Kiệt ch&agrave;o hỏi một tiếng, ph&ograve;ng cơ yếu nhiệm vụ kh&ocirc;ng t&iacute;nh qu&aacute; nặng nề, về phần sự t&igrave;nh, cũng kh&ocirc;ng c&oacute; việc lớn g&igrave;, đại sự đồng dạng cũng l&agrave; trước mặt đội chấp ph&aacute;p đi chấp h&agrave;nh, tổng thể tới n&oacute;i, ph&ograve;ng cơ yếu hay l&agrave; rất an ổn.<br />\r\n<br />\r\nĐối với L&yacute; Hạo đến b&aacute;o c&aacute;o l&agrave;m việc, Vương Kiệt phỏng đo&aacute;n, c&oacute; phải hay kh&ocirc;ng tiểu tử n&agrave;y muốn lăn lộn c&aacute;i quen mặt, để cho m&igrave;nh đề bạt đề bạt hắn?<br />\r\n<br />\r\nC&oacute; thể thời gian qu&aacute; ngắn, gia hỏa n&agrave;y lại th&ocirc;i học, mới chuyển ch&iacute;nh thức nửa năm, thời gian ngắn muốn đề bạt cũng kh&ocirc;ng thể n&agrave;o.<br />\r\n<br />\r\nĐang nghĩ ngợi, trước mặt L&yacute; Hạo sắc mặt bắt đầu nghi&ecirc;m t&uacute;c l&ecirc;n, thanh &acirc;m c&oacute; vẻ hơi ngột ngạt: &quot;Trưởng ph&ograve;ng, ta tại ph&ograve;ng cơ yếu, chủ yếu phụ tr&aacute;ch một ch&uacute;t nghi &aacute;n, &aacute;n chưa giải quyết hồ sơ chỉnh l&yacute;. Tiến v&agrave;o ph&ograve;ng cơ yếu một năm, ta đem gần nhất mười năm một ch&uacute;t hồ sơ đều tiến h&agrave;nh một lần nữa chỉnh l&yacute;, một lần nữa ph&acirc;n loại. . .&quot;<br />\r\n<br />\r\nVương Kiệt trong nh&aacute;y mắt lộ ra d&aacute;ng tươi cười, gật đầu n&oacute;i: &quot;Ta biết, cao t&agrave;i sinh ch&iacute;nh l&agrave; cao t&agrave;i sinh! Trước kia hồ sơ c&ograve;n c&oacute; ch&uacute;t loạn, một ch&uacute;t hồ sơ cũ đều sắp bị tr&ugrave;ng đục, ta cũng l&agrave;m cho người một lần nữa chỉnh l&yacute; qua, bất qu&aacute; vẫn l&agrave; thường xuy&ecirc;n cần thời điểm t&igrave;m kh&ocirc;ng thấy, ngươi vừa đến, gần nhất muốn t&igrave;m vụ &aacute;n g&igrave;, lập tức liền c&oacute; thể t&igrave;m tới, điểm ấy ngươi c&oacute; c&ocirc;ng. . .&quot;<br />\r\n<br />\r\nL&yacute; Hạo liền vội v&agrave;ng lắc đầu: &quot;Trưởng ph&ograve;ng, ta cũng kh&ocirc;ng phải đến khoe khoang khen c&ocirc;ng.&quot;<br />\r\n<br />\r\nKh&ocirc;ng phải sao?<br />\r\n<br />\r\nVương Kiệt bật cười, vậy chẳng lẽ lại thật l&agrave; c&oacute; sự t&igrave;nh b&aacute;o c&aacute;o?<br />\r\n<br />\r\nHắn cũng kh&ocirc;ng n&oacute;i c&aacute;i g&igrave;, gật gật đầu, ra hiệu L&yacute; Hạo n&oacute;i tiếp.<br />\r\n<br />\r\nL&yacute; Hạo lấy ra trước mặt văn bản t&agrave;i liệu, đưa cho Vương Kiệt một phần, ch&iacute;nh m&igrave;nh lật ra một phần, tuy c&ograve;n trẻ tuổi, thanh &acirc;m lại l&agrave; trầm ổn: &quot;Năm 1720, ngày 16 tháng 9, Ng&acirc;n Th&agrave;nh cư x&aacute; An Cư ph&aacute;t sinh một lần &aacute;n tự thi&ecirc;u, c&oacute; người mắt thấy người bị hại trước mặt mọi người tự thi&ecirc;u, trong cơ thể bỗng nhi&ecirc;n to&aacute;t ra hỏa hoa, sống sờ sờ đem người trong cuộc thi&ecirc;u chết.&quot;<br />\r\n<br />\r\nVương Kiệt giờ ph&uacute;t n&agrave;y cũng nh&igrave;n l&ecirc;n trong tay m&igrave;nh hồ sơ, tiếp lấy nh&igrave;n về ph&iacute;a L&yacute; Hạo.<br />\r\n<br />\r\n10 năm trước vụ &aacute;n, m&agrave; lại đ&atilde; kết &aacute;n, kh&ocirc;ng phải l&agrave; bị hại, m&agrave; l&agrave; ngo&agrave;i &yacute; muốn, tiểu tử n&agrave;y nhắc lại 10 năm trước đ&atilde; kết &aacute;n bản &aacute;n l&agrave;m c&aacute;i g&igrave;?<br />\r\n<br />\r\nL&yacute; Hạo tiếp tục: &quot;Năm 1723, ngày 22 tháng 9, Ng&acirc;n Th&agrave;nh trung t&acirc;m thương mậu, một vị người b&aacute;n h&agrave;ng bỗng nhi&ecirc;n t&oacute;c lửa cháy, rất nhanh thi&ecirc;u đốt to&agrave;n th&acirc;n, trước mặt mọi người bị đốt ch&aacute;y m&agrave; chết.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nVương Kiệt gật gật đầu, &quot;C&aacute;i n&agrave;y ta biết, l&uacute;c trước ch&iacute;nh l&agrave; ta đệ đơn, ta c&ograve;n tự th&acirc;n đi hiện trường, l&agrave; ngo&agrave;i &yacute; muốn, l&uacute;c ấy trung t&acirc;m thương mậu tĩnh điện qu&aacute; lớn đưa đến.&quot;<br />\r\n<br />\r\nL&yacute; Hạo cũng kh&ocirc;ng c&oacute; c&atilde;i lại, tiếp tục đọc: &quot;Năm 1725, ngày 18 tháng 8, Ng&acirc;n Th&agrave;nh kh&aacute;ch sạn d&acirc;n sinh, một vị kh&aacute;ch ở tại trong kh&aacute;ch sạn v&agrave;o ở, sau một ng&agrave;y bị người ph&aacute;t hiện chết bởi trong kh&aacute;ch sạn, cũng l&agrave; tự thi&ecirc;u m&agrave; chết.&quot;<br />\r\n<br />\r\nKhoảng c&aacute;ch thứ một lần &aacute;n tự thi&ecirc;u khoảng c&aacute;ch năm năm, khoảng c&aacute;ch lần thứ hai khoảng c&aacute;ch hai năm.<br />\r\n<br />\r\nNăm năm ở giữa, ph&aacute;t sinh ba lần &aacute;n tự thi&ecirc;u, bất qu&aacute; đều l&agrave; ngo&agrave;i &yacute; muốn, Ng&acirc;n Th&agrave;nh mỗi ng&agrave;y đều phải xử l&yacute; rất nhiều bản &aacute;n, năm năm ba lần ngo&agrave;i &yacute; muốn đưa đến &aacute;n tự thi&ecirc;u, thật đ&uacute;ng l&agrave; kh&ocirc;ng phải việc đại sự g&igrave;, cũng kh&ocirc;ng c&oacute; mấy người để &yacute;.<br />\r\n<br />\r\nTrừ phi tr&ugrave;ng hợp, ba lần bản &aacute;n đều l&agrave; một người qua tay, c&oacute; lẽ c&ograve;n c&oacute; thể g&acirc;y n&ecirc;n một ch&uacute;t ch&uacute; &yacute;.<br />\r\n<br />\r\nPh&ograve;ng cơ yếu hồ sơ v&ocirc; số, Vương Kiệt b&igrave;nh thường cũng kh&ocirc;ng để &yacute;, giờ ph&uacute;t n&agrave;y nghe v&agrave;i c&acirc;u, mơ hồ c&oacute; ch&uacute;t cảm gi&aacute;c, nh&iacute;u m&agrave;y nh&igrave;n về ph&iacute;a L&yacute; Hạo.<br />\r\n<br />\r\nL&yacute; Hạo tiếp tục n&oacute;i: &quot;Năm 1727, ngày 16 tháng 2, Ng&acirc;n Th&agrave;nh đường c&aacute;i Đ&ocirc;ng Phương, lại c&oacute; một người tự thi&ecirc;u m&agrave; chết, nguy&ecirc;n nh&acirc;n l&agrave; phụ cận c&oacute; tia chớp h&igrave;nh cầu xuất hiện, hư hư thực thực tia chớp h&igrave;nh cầu dẫn đến, dụ ph&aacute;t nh&acirc;n thể tự thi&ecirc;u, thời gian qua đi một lần &aacute;n tự thi&ecirc;u, một năm rưỡi!&quot;<br />\r\n<br />\r\nVương Kiệt hơi biến sắc mặt, từ ba năm đến hai năm, hai năm đến một năm rưỡi, Ng&acirc;n Th&agrave;nh lại ph&aacute;t sinh một lần &aacute;n tự thi&ecirc;u!<br />\r\n<br />\r\n&quot;Năm 1728, ngày 12 tháng 8, Ng&acirc;n Th&agrave;nh v&ugrave;ng ngoại th&agrave;nh Tuần Kiểm ti b&aacute;o c&aacute;o, cảnh nội một người mất t&iacute;ch, sau đ&oacute; tại bờ s&ocirc;ng ph&aacute;t hiện một bộ bị đốt ch&aacute;y thi cốt, hư hư thực thực ngày mưa d&ocirc;ng l&ocirc;i điện đ&aacute;nh tr&uacute;ng dẫn đến, lấy &yacute; b&ecirc;n ngo&agrave;i kết &aacute;n.&quot;<br />\r\n<br />\r\nLại l&agrave; một năm rưỡi!<br />\r\n<br />\r\nĐ&acirc;y l&agrave; cấp dưới cơ cấu b&aacute;o c&aacute;o đi l&ecirc;n, ph&ograve;ng cơ yếu chỉ l&agrave; l&agrave;m hồ sơ chỉnh l&yacute;, cũng kh&ocirc;ng tham dự vụ &aacute;n xử l&yacute;, huống chi loại bản &aacute;n ngo&agrave;i &yacute; muốn đưa tới n&agrave;y, ph&iacute;a dưới đ&atilde; kết &aacute;n, ph&iacute;a tr&ecirc;n cũng sẽ kh&ocirc;ng t&ugrave;y tiện đẩy ra lật.<br />\r\n<br />\r\nVương Kiệt sắc mặt c&agrave;ng ng&agrave;y c&agrave;ng ngưng trọng, hắn thật đ&uacute;ng l&agrave; kh&ocirc;ng để &yacute;, thật sự l&agrave; ph&ograve;ng cơ yếu văn bản t&agrave;i liệu hồ sơ nhiều lắm, thậm ch&iacute; h&agrave;ng năm đều sẽ ti&ecirc;u hủy rất nhiều năm xưa hồ sơ.<br />\r\n<br />\r\n&quot;Lại l&agrave; một năm rưỡi. . .&quot;<br />\r\n<br />\r\nVương Kiệt kh&ocirc;ng c&oacute; lật xem hồ sơ trong tay, m&agrave; l&agrave; nh&igrave;n về ph&iacute;a L&yacute; Hạo, trầm giọng n&oacute;i: &quot;Một lần ba năm, một lần hai năm, hai lần khoảng c&aacute;ch một năm rưỡi, năm 1729 th&aacute;ng 8, 9, c&oacute; phải hay kh&ocirc;ng lại ph&aacute;t sinh &aacute;n tự thi&ecirc;u?&quot;<br />\r\n<br />\r\nNăm 1729, cũng ch&iacute;nh l&agrave; năm ngo&aacute;i.<br />\r\n<br />\r\nVương Kiệt hồi tưởng một ch&uacute;t, năm ngo&aacute;i ph&aacute;t sinh qua sao?<br />\r\n<br />\r\nThời gian l&agrave; khoảng c&aacute;ch một năm sao?<br />\r\n<br />\r\nNếu l&agrave; thật sự c&oacute;, vậy đại biểu trong v&ograve;ng mười năm, Ng&acirc;n Th&agrave;nh cảnh nội thế m&agrave; ph&aacute;t sinh s&aacute;u lần &aacute;n tự thi&ecirc;u!<br />\r\n<br />\r\nTrải phẳng xuống tới kỳ thật thật kh&ocirc;ng nhiều, hai năm một lần, Ng&acirc;n Th&agrave;nh rất lớn, c&ograve;n kh&ocirc;ng phải một c&aacute;i ph&acirc;n bộ xử l&yacute;, m&agrave; l&agrave; ph&acirc;n t&aacute;n ra, một c&aacute;i ph&acirc;n bộ khả năng mười năm qua cũng liền nhận được một lần, ai sẽ để &yacute;?<br />\r\n<br />\r\nL&yacute; Hạo gật đầu: &quot;Năm 1729 lại ph&aacute;t sinh một lần, m&agrave; lại ngay tại Ng&acirc;n Th&agrave;nh cổ viện, m&agrave; lại. . . Người trong cuộc ch&iacute;nh l&agrave; ta đồng học, s&aacute;t v&aacute;ch ph&ograve;ng ngủ! Ta rời đi Ng&acirc;n Th&agrave;nh cổ viện, cũng c&oacute; một bộ phận nguy&ecirc;n nh&acirc;n c&ugrave;ng việc n&agrave;y c&oacute; quan hệ, l&uacute;c ấy ta mắt thấy hết thảy, đối với cổ viện sinh ra hoảng hốt sợ h&atilde;i t&acirc;m l&yacute;, tăng th&ecirc;m ta bạn học kia tự thi&ecirc;u quỷ dị, cho n&ecirc;n ta muốn biết, đến c&ugrave;ng tại sao phải tự thi&ecirc;u!&quot;<br />\r\n<br />\r\nL&yacute; Hạo sắc mặt ngưng trọng: &quot;Cho n&ecirc;n ta đi v&agrave;o Tuần Kiểm ti, c&oacute; một bộ phận nguy&ecirc;n nh&acirc;n đ&atilde; l&agrave; như thế, ngoại nh&acirc;n kh&ocirc;ng c&aacute;ch n&agrave;o t&igrave;m đọc những hồ sơ n&agrave;y, ta c&oacute; thể! Ta đọc qua sửa sang lại gần nhất mười năm qua &aacute;n tự thi&ecirc;u, ta ph&aacute;t hiện. . . C&aacute;i n&agrave;y cũng c&oacute; thể cũng kh&ocirc;ng phải l&agrave; ngo&agrave;i &yacute; muốn g&igrave;!&quot;<br />\r\n<br />\r\nVương Kiệt cấp tốc lật xem văn kiện trong tay, nh&igrave;n một hồi, nh&igrave;n về ph&iacute;a L&yacute; Hạo, trầm giọng n&oacute;i: &quot;N&oacute;i một ch&uacute;t &yacute; nghĩ của ngươi.&quot;<br />\r\n<br />\r\n&quot;Mười năm, tổng cộng ph&aacute;t sinh s&aacute;u lần, kh&ocirc;ng, nghi&ecirc;m chỉnh m&agrave; n&oacute;i l&agrave; ch&iacute;n năm, năm nay đến b&acirc;y giờ c&ograve;n kh&ocirc;ng c&oacute; nhận đến dạng n&agrave;y bản &aacute;n.&quot;<br />\r\n<br />\r\nL&yacute; Hạo nghi&ecirc;m mặt n&oacute;i: &quot;Trưởng ph&ograve;ng, bởi v&igrave; ta đồng học sự t&igrave;nh, cho n&ecirc;n ta rất xem trọng, mắt thấy đ&atilde; th&aacute;ng bảy, nếu l&agrave; n&oacute;i, &aacute;n tự thi&ecirc;u cũng kh&ocirc;ng phải l&agrave; tr&ugrave;ng hợp, vậy b&acirc;y giờ khoảng c&aacute;ch lần trước &aacute;n tự thi&ecirc;u, đ&atilde; nhanh một năm! Thời gian tự thi&ecirc;u n&agrave;y, l&agrave; lần lượt tại r&uacute;t ngắn, nếu như kh&ocirc;ng phải ngo&agrave;i &yacute; muốn, vậy rất c&oacute; thể gần nhất sẽ xuất hiện c&ugrave;ng loại vụ &aacute;n.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải ngo&agrave;i &yacute; muốn sao?&quot;<br />\r\n<br />\r\nVương Kiệt từng lần một đọc qua văn bản t&agrave;i liệu hồ sơ, hắn cũng l&agrave; l&atilde;o tuần kiểm, về sau ni&ecirc;n kỷ hơi lớn một ch&uacute;t, l&uacute;c n&agrave;y mới đổi đi nơi kh&aacute;c đến ph&ograve;ng cơ yếu.<br />\r\n<br />\r\nL&yacute; Hạo nếu l&agrave; kh&ocirc;ng chỉnh l&yacute;, kh&ocirc;ng đem mấy c&aacute;i bản &aacute;n đơn độc chỉnh hợp đến một lần, chỉ sợ thật kh&ocirc;ng ai sẽ để &yacute;.<br />\r\n<br />\r\nNhưng m&agrave;, khi s&aacute;u lần &aacute;n tự thi&ecirc;u chỉnh hợp đến một lần , bất kỳ người n&agrave;o đều c&oacute; thể mơ hồ ph&aacute;t gi&aacute;c được một ch&uacute;t kh&ocirc;ng ổn.<br />\r\n<br />\r\nVương Kiệt nhẹ nh&agrave;ng thở hắt ra: &quot;Chỉ từ hiện hữu t&igrave;nh huống đến xem, cũng kh&ocirc;ng c&oacute; biện ph&aacute;p x&aacute;c định đ&acirc;y l&agrave; c&oacute; dự mưu, nhưng l&agrave;. . . Qu&aacute; nhiều tr&ugrave;ng hợp, chưa hẳn ch&iacute;nh l&agrave; tr&ugrave;ng hợp!&quot;<br />\r\n<br />\r\nVương Kiệt hay l&agrave; rất b&eacute;n nhạy, giờ ph&uacute;t n&agrave;y cấp tốc n&oacute;i: &quot;Việc n&agrave;y ta sẽ hướng những bộ m&ocirc;n kh&aacute;c, nhất l&agrave; đội chấp ph&aacute;p b&ecirc;n kia c&acirc;u th&ocirc;ng, ph&ograve;ng cơ yếu cũng kh&ocirc;ng trực tiếp tham dự ph&aacute; &aacute;n, nhưng l&agrave; li&ecirc;n tiếp ph&aacute;t sinh tự thi&ecirc;u, c&aacute;i n&agrave;y chỉ sợ c&acirc;̀n coi trọng một ch&uacute;t, nếu như gần nhất lại ph&aacute;t sinh. . .&quot;<br />\r\n<br />\r\nN&oacute;i đến đ&acirc;y, Vương Kiệt hơi biến sắc mặt: &quot;Vậy liền đại biểu, ngươi ph&aacute;n đo&aacute;n ch&iacute;nh l&agrave; ch&iacute;nh x&aacute;c, khả năng thật kh&ocirc;ng phải l&agrave; ngo&agrave;i &yacute; muốn đơn giản như vậy!&quot;<br />\r\n<br />\r\nHắn nh&igrave;n về ph&iacute;a L&yacute; Hạo, sắc mặt nghi&ecirc;m t&uacute;c bỗng nhi&ecirc;n trầm tĩnh lại, lộ ra d&aacute;ng tươi cười: &quot;Đương nhi&ecirc;n, cũng kh&ocirc;ng cần thiết khẩn trương th&aacute;i qu&aacute;! Liền xem như thật hung s&aacute;t &aacute;n, so đ&acirc;y c&agrave;ng &aacute;c liệt đều c&oacute;, ngược lại l&agrave; L&yacute; Hạo ngươi. . . L&agrave;m kh&ocirc;ng tệ!&quot;<br />\r\n<br />\r\nVương Kiệt nghi&ecirc;m t&uacute;c, cũng chỉ duy tr&igrave; kh&ocirc;ng l&acirc;u.<br />\r\n<br />\r\nChưa n&oacute;i xong kh&ocirc;ng x&aacute;c định, coi như thật x&aacute;c định l&agrave; hung s&aacute;t, kỳ thật cũng kh&ocirc;ng c&oacute; g&igrave; lớn, hắn gặp nhiều lắm, nghe nhiều lắm, mười năm chết s&aacute;u người, kỳ thật thật kh&ocirc;ng t&iacute;nh l&agrave; g&igrave;.<br />\r\n<br />\r\nHắn c&agrave;ng vui mừng hơn, L&yacute; Hạo đối với vụ &aacute;n chấp nhất c&ugrave;ng phụ tr&aacute;ch, đương nhi&ecirc;n, cũng tr&ecirc;n đại thể minh bạch m&ocirc;̣t chút L&yacute; Hạo v&igrave; sao muốn nghỉ học nguy&ecirc;n nh&acirc;n.<br />\r\n<br />\r\nĐồng học?<br />\r\n<br />\r\nHảo bằng hữu đi!<br />\r\n<br />\r\nNếu kh&ocirc;ng, L&yacute; Hạo cũng kh&ocirc;ng cần thiết th&ocirc;i học.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nĐối diện, L&yacute; Hạo cũng kh&ocirc;ng nhiều lời.<br />\r\n<br />\r\nHung s&aacute;t &aacute;n?<br />\r\n<br />\r\nNếu l&agrave; thật sự hung s&aacute;t, hắn l&agrave;m g&igrave; nghỉ học, l&agrave;m g&igrave; tho&aacute;t đi Ng&acirc;n Th&agrave;nh cổ viện.<br />\r\n<br />\r\nTận mắt nh&igrave;n thấy hết thảy kia, đ&oacute; l&agrave; đơn giản hung s&aacute;t ngược lại tốt.<br />\r\n<br />\r\nHuyết ảnh m&agrave;u đỏ kia đến c&ugrave;ng l&agrave; c&aacute;i g&igrave;?<br />\r\n<br />\r\nTự thi&ecirc;u?<br />\r\n<br />\r\nKh&ocirc;ng, đ&oacute; l&agrave; bị huyết ảnh đốt ch&aacute;y m&agrave; chết, thế nhưng l&agrave; l&uacute;c ấy cũng giống như m&igrave;nh tận mắt thấy đồng học tự thi&ecirc;u mấy vị kh&aacute;c đồng học, thế m&agrave; kh&ocirc;ng một người nh&igrave;n thấy huyết ảnh kia!<br />\r\n<br />\r\nL&agrave; bọn hắn n&oacute;i l&aacute;o sao?<br />\r\n<br />\r\nKh&ocirc;ng, kh&ocirc;ng cần thiết.<br />\r\n<br />\r\nL&yacute; Hạo biết, ho&agrave;n to&agrave;n ch&iacute;nh x&aacute;c chỉ c&oacute; ch&iacute;nh m&igrave;nh thấy được, những người kh&aacute;c căn bản l&agrave; kh&ocirc;ng c&oacute; ph&aacute;t hiện huyết ảnh, hoặc l&agrave; n&oacute;i, bọn hắn kh&ocirc;ng nh&igrave;n thấy.<br />\r\n<br />\r\nM&agrave; lại, gần nhất hắn lại cảm nhận được, thậm ch&iacute; tối h&ocirc;m qua mơ hồ lại thấy được đạo huyết ảnh kia.<br />\r\n<br />\r\nC&oacute; lẽ. . . Kh&ocirc;ng phải một đạo!<br />\r\n<br />\r\n&quot;Kh&ocirc;ng ai ch&uacute; &yacute; coi như xong, một khi Tuần Kiểm ti ch&uacute; &yacute; tới, x&aacute;c định kh&ocirc;ng phải ngo&agrave;i &yacute; muốn, Tuần Kiểm ti nếu như kh&ocirc;ng c&aacute;ch n&agrave;o xử l&yacute;, vậy nhất định sẽ l&ecirc;n b&aacute;o đến Tuần Dạ Nh&acirc;n b&ecirc;n kia.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nNếu như kh&ocirc;ng ai để &yacute;, vậy những bản &aacute;n n&agrave;y, chỉ biết coi th&agrave;nh ngo&agrave;i &yacute; muốn kết &aacute;n, hồ sơ sẽ chỉ nh&eacute;t v&agrave;o ph&ograve;ng cơ yếu h&iacute;t bụi.<br />\r\n<br />\r\nNhưng nếu l&agrave; bị lưu t&acirc;m đến, Tuần Kiểm ti vẫn l&agrave; tương đối phụ tr&aacute;ch.<br />\r\n<br />\r\n&quot;Tuần Dạ Nh&acirc;n!&quot;<br />\r\n<br />\r\nL&yacute; Hạo trong l&ograve;ng lần nữa mặc niệm, đối diện, Vương Kiệt đứng l&ecirc;n n&oacute;i: &quot;Vậy ta đi t&igrave;m đội chấp ph&aacute;p thương lượng một ch&uacute;t, đến tiếp sau khả năng c&acirc;̀n L&yacute; Hạo ngươi kết nối một ch&uacute;t, cung cấp một ch&uacute;t trợ gi&uacute;p, d&ugrave; sao vụ &aacute;n n&agrave;y l&agrave; ngươi tập hợp. . .&quot;<br />\r\n<br />\r\nL&yacute; Hạo vội v&agrave;ng gật đầu, trong l&ograve;ng c&oacute; ch&uacute;t nhẹ nh&agrave;ng thở ra.<br />\r\n<br />\r\nRất tốt, nghe được m&igrave;nh muốn nghe.<br />\r\n<br />\r\nHắn l&agrave;m đ&acirc;y hết thảy, ch&iacute;nh l&agrave; v&igrave; tham dự trong đ&oacute;, cũng kh&ocirc;ng phải l&agrave; trực tiếp tham dự, m&agrave; l&agrave; l&agrave;m ph&aacute;t hiện ngo&agrave;i &yacute; muốn người đi tham dự, kể từ đ&oacute;, nếu l&agrave; Tuần Dạ Nh&acirc;n nh&uacute;ng tay, c&aacute;i kia L&yacute; Hạo rất c&oacute; thể sẽ ở sau đ&oacute; tiếp x&uacute;c đến Tuần Dạ Nh&acirc;n.<br />\r\n<br />\r\nĐương nhi&ecirc;n, tiếp x&uacute;c kh&ocirc;ng c&oacute; nghĩa l&agrave; c&oacute; thể gia nhập, coi như thật c&oacute; thể gia nhập. . . L&yacute; Hạo cũng chưa chắc d&aacute;m gia nhập!<br />\r\n<br />\r\nMột năm qua n&agrave;y, hắn nh&igrave;n qua hồ sơ nhiều lắm.<br />\r\n<br />\r\nC&oacute; một số việc, trong c&acirc;u chữ đều sẽ thể hiện đi ra, tỉ như nh&igrave;n thấy hồng ảnh sự t&igrave;nh, giống như cũng kh&ocirc;ng phải l&agrave; ch&iacute;nh m&igrave;nh một người c&oacute; thể nh&igrave;n thấy, L&yacute; Hạo t&igrave;m đọc qua một ch&uacute;t tư liệu hồ sơ, Ng&acirc;n Th&agrave;nh từng c&oacute; dạng n&agrave;y ghi ch&eacute;p.<br />\r\n<br />\r\nNhưng m&agrave;, những người kia, kh&ocirc;ng phải chết ch&iacute;nh l&agrave; mất t&iacute;ch, kh&ocirc;ng một may mắn tho&aacute;t khỏi!<br />\r\n<br />\r\nL&yacute; Hạo cũng kh&ocirc;ng n&oacute;i đến việc n&agrave;y, ở trong đ&oacute; c&oacute; nguy hiểm cực lớn, hắn thậm ch&iacute; kh&ocirc;ng d&aacute;m n&oacute;i cho bất luận kẻ n&agrave;o, ch&iacute;nh m&igrave;nh có th&ecirc;̉ nh&igrave;n thấy hồng ảnh, d&ugrave; l&agrave; Tuần Dạ Nh&acirc;n, cũng chưa chắc liền đ&aacute;ng tin cậy.<br />\r\n<br />\r\n&quot;Bất kể như thế n&agrave;o. . . Nhất định phải th&ocirc;ng qua một ch&uacute;t b&igrave;nh thường đường tắt, đi tiếp x&uacute;c nguồn lực lượng thần b&iacute; n&agrave;y!&quot;<br />\r\n<br />\r\nL&yacute; Hạo h&iacute;t s&acirc;u một hơi, thối lui ra khỏi ph&ograve;ng l&agrave;m việc.<br />\r\n<br />\r\nKh&ocirc;ng đơn thuần l&agrave; v&igrave; d&ograve; x&eacute;t ch&acirc;n tướng, mấu chốt l&agrave;, v&igrave; m&igrave;nh mạng nhỏ phụ tr&aacute;ch.<br />\r\n<br />\r\nCó th&ecirc;̉ nh&igrave;n thấy hồng ảnh đều đ&atilde; chết, về phần phải chăng c&ograve;n c&oacute; những người kh&aacute;c có th&ecirc;̉ nh&igrave;n thấy, L&yacute; Hạo kh&ocirc;ng biết, nhưng hắn biết, ph&agrave;m l&agrave; bại lộ đều đ&atilde; chết, c&aacute;i n&agrave;y rất kh&ocirc;ng tầm thường.<br />\r\n<br />\r\n&quot;Ta c&aacute;i mạng nhỏ n&agrave;y cũng kh&ocirc;ng thể tuỳ tiện mất đi, rời đi Ng&acirc;n Th&agrave;nh cổ viện đi v&agrave;o Tuần Kiểm ti, cũng kh&ocirc;ng phải v&igrave; trốn tr&aacute;nh.&quot;<br />\r\n<br />\r\nĐi ra ph&ograve;ng l&agrave;m việc cửa lớn, L&yacute; Hạo nhẹ nh&agrave;ng thở hắt ra, lộ ra nụ cười x&aacute;n lạn.<br />\r\n<br />\r\nKết quả tốt nhất, ch&iacute;nh l&agrave; Tuần Dạ Nh&acirc;n giải quyết c&aacute;i phiền to&aacute;i n&agrave;y, ch&iacute;nh m&igrave;nh nếu c&oacute; thể c&oacute; ch&uacute;t thu hoạch ngo&agrave;i &yacute; muốn vậy tốt nhất rồi, l&agrave;m nh&acirc;n vi&ecirc;n văn ph&ograve;ng, nếu như c&oacute; thể gia nhập Tuần Dạ Nh&acirc;n m&agrave; n&oacute;i, vậy L&yacute; Hạo cũng hy vọng c&oacute; thể gia nhập trong Tuần Dạ Nh&acirc;n văn chức cơ cấu.<br />\r\n<br />\r\nVề phần cơ cấu chấp ph&aacute;p, L&yacute; Hạo kh&ocirc;ng cảm thấy ch&iacute;nh m&igrave;nh có th&ecirc;̉ đảm nhiệm, m&agrave; lại cũng rất nguy hiểm, v&agrave;i ng&agrave;y trước đi l&atilde;o sư b&ecirc;n kia ăn cơm, L&yacute; Hạo liền n&oacute;i b&oacute;ng n&oacute;i gi&oacute; một phen, Tuần Dạ Nh&acirc;n. . . Thương vong cũng kh&ocirc;ng nhỏ!<br />\r\n<br />\r\n&quot;Đi một bước nh&igrave;n một bước đi!&quot;<br />\r\n<br />\r\nL&yacute; Hạo nh&igrave;n xem Vương Kiệt vội v&agrave;ng rời đi, lộ ra d&aacute;ng tươi cười, bước đầu ti&ecirc;n cuối c&ugrave;ng l&agrave; bước ra.<br />\r\n<br />\r\n&quot;Hồng ảnh. . .&quot;<br />\r\n<br />\r\nTrong l&ograve;ng mặc niệm, đồ chơi kia rốt cuộc l&agrave; thứ g&igrave;?<br />\r\n<br />\r\nM&agrave; ta có th&ecirc;̉ nh&igrave;n thấy, lại l&agrave; bởi v&igrave; c&aacute;i g&igrave;?</p>', '2021-11-27 21:27:55', 8, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(187, 50, 'Kiếm của Lý gia', '3', '<p>Từ ph&ograve;ng l&agrave;m việc của trưởng ph&ograve;ng trở lại chỗ ngồi của m&igrave;nh.<br />\r\n<br />\r\nL&yacute; Hạo nh&igrave;n tho&aacute;ng qua tr&ecirc;n b&agrave;n lịch ng&agrave;y, năm 1730 ngày 12 tháng 7.<br />\r\n<br />\r\n&quot;Gần một năm!&quot;<br />\r\n<br />\r\nThấp giọng nỉ non một c&acirc;u.<br />\r\n<br />\r\nĐối diện, Trần Na hiếu kỳ n&oacute;i: &quot;C&aacute;i g&igrave; gần một năm?&quot;<br />\r\n<br />\r\nL&yacute; Hạo cười cười, giải th&iacute;ch n&oacute;i: &quot;Ta n&oacute;i ta gia nhập Tuần Kiểm ti gần một năm.&quot;<br />\r\n<br />\r\n&quot;A, ngươi tr&iacute; nhớ thật tốt.&quot;<br />\r\n<br />\r\nTrần Na kh&ocirc;ng c&oacute; lại ch&uacute; &yacute;, ai nhớ kỹ c&aacute;i n&agrave;y, lại kh&ocirc;ng &yacute; nghĩa g&igrave;.<br />\r\n<br />\r\nL&yacute; Hạo cũng kh&ocirc;ng nhiều lời.<br />\r\n<br />\r\nHắn nhớ kỹ!<br />\r\n<br />\r\nNhớ kỹ rất r&otilde; r&agrave;ng!<br />\r\n<br />\r\nNăm 1729 ngày mùng 1 tháng 8, hắn gia nhập Tuần Kiểm ti.<br />\r\n<br />\r\nM&agrave; liền tại v&agrave;i ng&agrave;y trước, ngày 23 tháng 7, chuyện xảy ra sau ng&agrave;y thứ hai, L&yacute; Hạo lựa chọn nghỉ học, rất nhanh, L&yacute; Hạo liền gia nhập Tuần Kiểm ti.<br />\r\n<br />\r\nNăm 1729 ngày 22 tháng 7, Ng&acirc;n Th&agrave;nh cổ viện ph&aacute;t sinh một lần &aacute;n tự thi&ecirc;u.<br />\r\n<br />\r\nCổ viện cấp hai học vi&ecirc;n Trương Viễn, tại ngo&agrave;i ph&ograve;ng ngủ nh&acirc;n thể tự thi&ecirc;u, tươi sống bị thi&ecirc;u chết.<br />\r\n<br />\r\nV&igrave; kh&ocirc;ng ảnh hưởng Ng&acirc;n Th&agrave;nh cổ viện danh dự, c&ugrave;ng Tuần Kiểm ti kiểm tra thực hư đằng sau, đ&iacute;ch thật l&agrave; ngo&agrave;i &yacute; muốn dẫn đến, việc n&agrave;y tin tức bị phong tỏa, cực &iacute;t c&oacute; người biết Ng&acirc;n Th&agrave;nh cổ viện chết một người học vi&ecirc;n.<br />\r\n<br />\r\nĐương nhi&ecirc;n, kh&ocirc;ng c&oacute; l&agrave;m lớn chuyện c&ograve;n c&oacute; một ch&uacute;t, Trương Viễn phụ mẫu đều mất, trong nh&agrave; cũng kh&ocirc;ng c&oacute; g&igrave; th&acirc;n th&iacute;ch, cũng kh&ocirc;ng c&oacute; g&igrave; khổ chủ, cho n&ecirc;n sự t&igrave;nh rất nhanh liền bị &aacute;p xuống tới.<br />\r\n<br />\r\nSau đ&oacute;, L&yacute; Hạo nghỉ học, đạo sư của hắn cũng suy đo&aacute;n c&ugrave;ng Trương Viễn c&oacute; quan hệ, nghe n&oacute;i L&yacute; Hạo c&ugrave;ng Trương Viễn quan hệ kh&ocirc;ng tệ.<br />\r\n<br />\r\n&quot;Trương Viễn!&quot;<br />\r\n<br />\r\nTrong l&ograve;ng mặc niệm một tiếng, khoảng c&aacute;ch Tiểu Viễn tử vong gần một năm.<br />\r\n<br />\r\nTrong đầu lần nữa hiện ra ng&agrave;y đ&oacute; một m&agrave;n kia.<br />\r\n<br />\r\nHuyết ảnh m&agrave;u đỏ, giống như nắm lấy Tiểu Viễn linh hồn, huyết hồng hỏa diễm thi&ecirc;u đốt Tiểu Viễn linh hồn, thống khổ, gi&atilde;y dụa, sụp đổ, đủ loại cảm x&uacute;c, những người kh&aacute;c kh&ocirc;ng thấy được, L&yacute; Hạo lại l&agrave; phảng phất nh&igrave;n nhất thanh nhị sở.<br />\r\n<br />\r\nHắn muốn l&ecirc;n trước ngăn cản, thế nhưng l&agrave;, một khắc n&agrave;y, Tiểu Viễn miệng kh&ocirc;ng ngừng đ&oacute;ng mở, tại như thế cực đoan thống khổ dưới, dưới t&igrave;nh huống kh&ocirc;ng c&aacute;ch n&agrave;o ph&aacute;t ra bất kỳ thanh &acirc;m, miệng hắn một mực tại động.<br />\r\n<br />\r\nNhững người kh&aacute;c c&oacute; lẽ cảm thấy l&agrave; k&ecirc;u r&ecirc;n, duy chỉ c&oacute; L&yacute; Hạo biết, kh&ocirc;ng phải.<br />\r\n<br />\r\n&quot;Trốn!&quot;<br />\r\n<br />\r\nĐ&uacute;ng vậy, đ&oacute; l&agrave; để cho m&igrave;nh trốn.<br />\r\n<br />\r\nL&yacute; Hạo qu&aacute; quen thuộc Trương Viễn!<br />\r\n<br />\r\nĐể cho m&igrave;nh kh&ocirc;ng n&ecirc;n tới gần, để cho m&igrave;nh đ&agrave;o tẩu.<br />\r\n<br />\r\nTrương Viễn kh&ocirc;ng c&oacute; chết tại ph&ograve;ng ngủ, thời điểm hắn chết, thậm ch&iacute; chỉ mặc một kiện đồ l&oacute;t, nh&igrave;n buồn cười, có th&ecirc;̉ L&yacute; Hạo từ trước tới giờ kh&ocirc;ng cảm thấy buồn cười.<br />\r\n<br />\r\nTrương Viễn thời điểm chết, cũng đ&atilde; chuẩn bị đi ngủ, nhưng hắn lại l&agrave; ở dưới t&igrave;nh huống v&ocirc; c&ugrave;ng thống khổ, trốn ra ph&ograve;ng ngủ, hắn cất bước muốn đi phương hướng, l&agrave; L&yacute; Hạo ph&ograve;ng ngủ!<br />\r\n<br />\r\nHắn kh&ocirc;ng c&aacute;ch n&agrave;o ph&aacute;t ra &acirc;m thanh, cơ hồ l&agrave; lấy lớn nhất nghị lực, đập n&aacute;t một khối gạch men sứ, l&uacute;c n&agrave;y mới tại thời điểm trời tối người y&ecirc;n kia, đưa tới động tĩnh kh&ocirc;ng nhỏ, kh&ocirc;ng &iacute;t người đi ra ngo&agrave;i xem x&eacute;t, bao qu&aacute;t L&yacute; Hạo.<br />\r\n<br />\r\nTrương Viễn l&agrave; đang cầu cứu sao?<br />\r\n<br />\r\nNgoại nh&acirc;n cảm thấy l&agrave; như vậy, L&yacute; Hạo cảm thấy. . . Kh&ocirc;ng phải.<br />\r\n<br />\r\nHắn l&agrave; đang c&ugrave;ng ch&iacute;nh m&igrave;nh cảnh b&aacute;o, Trương Viễn từ ph&ograve;ng ngủ tho&aacute;t đi đi ra, n&aacute;o ra động tĩnh, dẫn xuất L&yacute; Hạo, cuối c&ugrave;ng im lặng n&oacute;i một c&aacute;i &quot;Trốn&quot;, đ&acirc;y kh&ocirc;ng phải hướng L&yacute; Hạo cầu cứu, m&agrave; l&agrave; để hắn đ&agrave;o tẩu.<br />\r\n<br />\r\n&quot;14 năm.&quot;<br />\r\n<br />\r\nL&yacute; Hạo trong l&ograve;ng lại th&igrave; th&agrave;o một tiếng.<br />\r\n<br />\r\nHắn c&ugrave;ng Trương Viễn quen biết 14 năm, cũng kh&ocirc;ng phải l&agrave; những người kh&aacute;c trong tưởng tượng chỉ l&agrave; hai năm đồng m&ocirc;n, bọn hắn nhận biết 14 năm, từ nhỏ đến lớn đồng học, hoặc l&agrave; n&oacute;i đồng đảng?<br />\r\n<br />\r\nChỉ l&agrave; hai người đều l&agrave; người trầm mặc &iacute;t n&oacute;i, nam nh&acirc;n ở giữa hữu nghị, cũng kh&ocirc;ng cần lu&ocirc;n lu&ocirc;n treo ở b&ecirc;n miệng.<br />\r\n<br />\r\nGiống nhau Trương Viễn, tử vong trước một khắc, lấy cực kỳ cường h&atilde;n &yacute; ch&iacute; lực, gi&atilde;y dụa trốn tới, chỉ v&igrave; hướng L&yacute; Hạo truyền lại một c&aacute;i tin tức, đ&agrave;o tẩu!<br />\r\n<br />\r\nTrương Viễn &aacute;n tự thi&ecirc;u, cuối c&ugrave;ng bị đ&egrave; xuống, v&ocirc; thanh v&ocirc; tức.<br />\r\n<br />\r\nL&yacute; Hạo vị n&agrave;y tri giao hảo hữu, tự nhi&ecirc;n cũng kh&ocirc;ng c&oacute; mấy người biết đi tra s&acirc;u c&aacute;i g&igrave;.<br />\r\n<br />\r\n&quot;Tiểu Viễn để cho ta trốn, đến c&ugrave;ng l&agrave; bởi v&igrave; hắn nh&igrave;n thấy c&aacute;i g&igrave;, nghe được c&aacute;i g&igrave;, hoặc l&agrave; đơn thuần hoảng hốt sợ h&atilde;i? Lại hoặc l&agrave;. . . Tiểu Viễn biết, huyết ảnh mục ti&ecirc;u kế tiếp l&agrave; ta?&quot;<br />\r\n<br />\r\nĐ&acirc;y cũng l&agrave; đ&atilde; qua một năm, L&yacute; Hạo một mực tại suy nghĩ vấn đề.<br />\r\n<br />\r\nHắn kh&ocirc;ng c&aacute;ch n&agrave;o tưởng tượng Trương Viễn tiếp nhận bao lớn thống khổ, một khắc n&agrave;y, nhưng như cũ hướng m&igrave;nh cảnh b&aacute;o, L&yacute; Hạo cảm thấy cũng kh&ocirc;ng đơn giản, c&oacute; lẽ. . . Huyết ảnh mục ti&ecirc;u kế tiếp ch&iacute;nh l&agrave; m&igrave;nh!<br />\r\n<br />\r\n&quot;S&aacute;u người, tăng th&ecirc;m ta, c&oacute; lẽ l&agrave; bảy người, ch&uacute;ng ta những người n&agrave;y, đến c&ugrave;ng c&oacute; c&aacute;i g&igrave; điểm giống nhau?&quot;<br />\r\n<br />\r\n&quot;Khoảng c&aacute;ch mười năm, thời gian c&agrave;ng l&uacute;c c&agrave;ng nhanh, nếu l&agrave; t&iacute;nh cả ta, bảy người, trừ ta c&ugrave;ng Trương Viễn, đều kh&ocirc;ng c&oacute; c&aacute;i g&igrave; tương quan địa phương, l&agrave; ngẫu nhi&ecirc;n mục ti&ecirc;u, hay l&agrave; c&oacute; t&iacute;nh mục ti&ecirc;u r&otilde; rệt?&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nL&yacute; Hạo nhẹ nh&agrave;ng lau tr&aacute;n, lật nh&igrave;n trước mặt văn bản t&agrave;i liệu, đ&acirc;y l&agrave; s&aacute;u người tất cả hồ sơ, cũng l&agrave; hắn đ&atilde; qua một năm th&ocirc;ng qua đủ loại con đường đi thu tập được một ch&uacute;t manh mối.<br />\r\n<br />\r\nKhoảng c&aacute;ch người thứ nhất tử vong đ&atilde; mười năm, trước l&uacute;c n&agrave;y c&oacute; lẽ c&ograve;n c&oacute;, c&oacute; lẽ kh&ocirc;ng c&oacute;, L&yacute; Hạo kh&ocirc;ng thể n&agrave;o biết được.<br />\r\n<br />\r\nThời gian khoảng c&aacute;ch mười năm, rất nhiều thứ kỳ thật rất kh&oacute; đi thăm d&ograve; hỏi &yacute; kiến.<br />\r\n<br />\r\n&quot;Giới t&iacute;nh? Tuổi t&aacute;c? Nghề nghiệp? Th&acirc;n phận? Cộng đồng người li&ecirc;n lạc?&quot;<br />\r\n<br />\r\nTrước mắt văn bản t&agrave;i liệu, L&yacute; Hạo đ&atilde; lật nh&igrave;n rất nhiều lần, th&ocirc;ng thường tr&ecirc;n &yacute; nghĩa điểm giống nhau, L&yacute; Hạo một mực kh&ocirc;ng c&oacute; ph&aacute;t hiện qua, đ&acirc;y ch&iacute;nh l&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng li&ecirc;n quan g&igrave; mấy người.<br />\r\n<br />\r\n&quot;Huyết ảnh tại sao muốn giết bọn hắn? L&agrave; bọn hắn uy hiếp đến huyết ảnh, hay l&agrave; c&oacute; mục đ&iacute;ch kh&aacute;c?&quot;<br />\r\n<br />\r\nV&ocirc; số nghi hoặc, tr&agrave;n ngập tại L&yacute; Hạo n&atilde;o hải.<br />\r\n<br />\r\nĐương nhi&ecirc;n, một mực truy tra xuống dưới, c&ograve;n c&oacute; một ch&uacute;t. . . B&aacute;o th&ugrave;!<br />\r\n<br />\r\nTrương Viễn chết rồi, kh&ocirc;ng ai sẽ để &yacute;, có th&ecirc;̉ L&yacute; Hạo để &yacute;.<br />\r\n<br />\r\nTuần Dạ Nh&acirc;n b&ecirc;n kia, tr&agrave;n đầy kh&ocirc;ng x&aacute;c định nh&acirc;n tố, nếu kh&ocirc;ng phải m&igrave;nh kh&ocirc;ng c&aacute;ch n&agrave;o đạt được hữu hiệu manh mối, hoặc l&agrave; n&oacute;i kh&ocirc;ng c&oacute; c&aacute;i g&igrave; tốt biện ph&aacute;p, đi đối ph&oacute; một c&aacute;i mọi người kh&ocirc;ng thấy được huyết ảnh, L&yacute; Hạo căn bản sẽ kh&ocirc;ng gửi hi vọng Tuần Dạ Nh&acirc;n giết huyết ảnh, hắn c&agrave;ng hy vọng ch&iacute;nh m&igrave;nh đi l&agrave;m thịt t&ecirc;n kia!<br />\r\n<br />\r\n&quot;L&yacute; Hạo, lại nh&igrave;n mấy người kia tư liệu?&quot;<br />\r\n<br />\r\nĐối diện, Trần Na gặp L&yacute; Hạo lại mở ra phần kia quen thuộc văn bản t&agrave;i liệu, kh&ocirc;ng khỏi hỏi một c&acirc;u.<br />\r\n<br />\r\nMột năm qua n&agrave;y, n&agrave;ng nh&igrave;n thấy qua rất nhiều lần, L&yacute; Hạo một mực tại nh&igrave;n phần văn kiện n&agrave;y, m&agrave; lại văn bản t&agrave;i liệu c&agrave;ng ng&agrave;y c&agrave;ng d&agrave;y, đều sắp bị L&yacute; Hạo nh&igrave;n ra l&ocirc;ng tới.<br />\r\n<br />\r\nBất qu&aacute; n&agrave;ng mỗi lần muốn nh&igrave;n một ch&uacute;t, L&yacute; Hạo đều cấp tốc kh&eacute;p lại.<br />\r\n<br />\r\nChỉ l&agrave; mơ hồ thấy qua, l&agrave; mấy người một ch&uacute;t t&agrave;i liệu c&aacute; nh&acirc;n.<br />\r\n<br />\r\nL&yacute; Hạo ngẩng đầu, lộ ra tinh khiết d&aacute;ng tươi cười, &quot;Na tỷ, t&ugrave;y tiện nh&igrave;n xem.&quot;<br />\r\n<br />\r\n&quot;Dừng a!&quot;<br />\r\n<br />\r\nTrần Na chẳng th&egrave;m ng&oacute; tới, t&ugrave;y tiện nh&igrave;n xem, ngươi có th&ecirc;̉ nh&igrave;n gần một năm?<br />\r\n<br />\r\nTiểu tử n&agrave;y, c&oacute; đ&ocirc;i khi cũng kh&ocirc;ng th&agrave;nh thật.<br />\r\n<br />\r\n&quot;Đều nh&igrave;n đ&atilde; l&acirc;u như vậy, L&yacute; Hạo, ngươi muốn tra c&aacute;i g&igrave;? Nơi n&agrave;y nhiều như vậy l&atilde;o tuần kiểm ngươi kh&ocirc;ng hỏi, liền ch&iacute;nh m&igrave;nh cắm đầu tra, ngươi một t&acirc;n thủ, có th&ecirc;̉ tra được c&aacute;i g&igrave;? Nếu kh&ocirc;ng cho ta xem một ch&uacute;t, c&oacute; lẽ ta c&oacute; thể cho ngươi một ch&uacute;t đề nghị, y&ecirc;n t&acirc;m, ta kh&ocirc;ng tiết ra ngo&agrave;i.&quot;<br />\r\n<br />\r\nTrần Na cười h&igrave; h&igrave; n&oacute;i: &quot;Thế n&agrave;o, cho ta xem một ch&uacute;t?&quot;<br />\r\n<br />\r\nThật thật t&ograve; m&ograve;!<br />\r\n<br />\r\nL&yacute; Hạo trước kia kh&ocirc;ng nguyện &yacute; để người ta biết m&igrave;nh tại tra c&aacute;i n&agrave;y, cũng l&agrave; lo lắng g&acirc;y n&ecirc;n hồng ảnh ch&uacute; &yacute;, nhưng h&ocirc;m nay, hắn một mực tra kh&ocirc;ng được c&aacute;i g&igrave;, bản &aacute;n cũng hướng Vương Kiệt b&aacute;o c&aacute;o, giờ ph&uacute;t n&agrave;y cho Trần Na nh&igrave;n xem cũng kh&ocirc;ng phải kh&ocirc;ng thể.<br />\r\n<br />\r\nBất qu&aacute; suy t&iacute;nh một hồi, L&yacute; Hạo hay l&agrave; mở miệng n&oacute;i: &quot;Vậy ngươi xem nh&igrave;n, bất qu&aacute; cũng đừng c&oacute; l&agrave;m phiền những người kh&aacute;c.&quot;<br />\r\n<br />\r\nHắn kh&ocirc;ng hy vọng qu&aacute; nhiều người biết ch&iacute;nh m&igrave;nh ch&uacute; &yacute; c&aacute;i n&agrave;y.<br />\r\n<br />\r\nTrần Na cũng coi như người mới, kh&ocirc;ng c&oacute; như vậy mẫn cảm c&ugrave;ng nhạy cảm, một khi những người kh&aacute;c thấy được mấy người kia vật liệu, c&oacute; lẽ rất nhanh sẽ li&ecirc;n tưởng đến trước kia những vụ &aacute;n kia, biết L&yacute; Hạo đang tra &aacute;n tự thi&ecirc;u.<br />\r\n<br />\r\nCho Trần Na nh&igrave;n, cũng l&agrave; L&yacute; Hạo nh&igrave;n qu&aacute; l&acirc;u, lại l&agrave; kh&ocirc;ng thu hoạch được g&igrave;, kh&ocirc;ng biết c&oacute; phải hay kh&ocirc;ng l&agrave; bởi v&igrave; hắn li&ecirc;n lụy qu&aacute; s&acirc;u, trong nh&agrave; chưa tỏ, ngo&agrave;i ng&otilde; đ&atilde; tường nguy&ecirc;n nh&acirc;n, hay l&agrave; bởi v&igrave; thật kh&ocirc;ng c&oacute; g&igrave; li&ecirc;n quan chỗ.<br />\r\n<br />\r\nTrần Na người ngo&agrave;i cuộc n&agrave;y, cũng c&oacute; thể nh&igrave;n ra ch&uacute;t g&igrave;?<br />\r\n<br />\r\nTốt a, L&yacute; Hạo kỳ thật kh&ocirc;ng &ocirc;m bất cứ hy vọng n&agrave;o, chỉ l&agrave; trải qua thời gian d&agrave;i kh&ocirc;ng thu hoạch, để hắn c&oacute; ch&uacute;t kh&ocirc;ng cam t&acirc;m.<br />\r\n<br />\r\n&quot;Y&ecirc;n t&acirc;m đi!&quot;<br />\r\n<br />\r\nTrần Na lập tức vui vẻ ra mặt, cuối c&ugrave;ng l&agrave; c&oacute; cơ hội nh&igrave;n L&yacute; Hạo b&iacute; mật nhỏ.<br />\r\n<br />\r\nSợ L&yacute; Hạo đổi &yacute;, n&agrave;ng vội v&agrave;ng nằm sấp tới, một thanh cướp đi L&yacute; Hạo văn kiện trong tay, cấp tốc lật xem.<br />\r\n<br />\r\nMột l&aacute;t sau, Trần Na đầu to n&oacute;i: &quot;L&yacute; Hạo, ngươi đến c&ugrave;ng muốn từ trong những văn kiện n&agrave;y t&igrave;m tới c&aacute;i g&igrave;? Ta xem một ch&uacute;t, cũng qu&aacute; l&agrave; nhiều, c&aacute;i g&igrave; việc nhỏ ngươi cũng cho ghi ch&eacute;p đi v&agrave;o, đều c&oacute; thể l&agrave;m hồi ức lục đến xem, ngươi nghĩ ra được kết quả g&igrave;?&quot;<br />\r\n<br />\r\nL&yacute; Hạo c&acirc;n nhắc một hồi mở miệng n&oacute;i: &quot;Điểm giống nhau! Bọn hắn s&aacute;u người điểm giống nhau!&quot;<br />\r\n<br />\r\nTrần Na lập tức im lặng.<br />\r\n<br />\r\nN&agrave;ng cẩn thận lật xem một lượt, s&aacute;u người, ni&ecirc;n kỷ kh&ocirc;ng giống với, nghề nghiệp, th&acirc;n phận cũng kh&ocirc;ng giống với, nam nữ đều c&oacute;, v&ograve;ng tr&ograve;n cũng kh&ocirc;ng giống với.<br />\r\n<br />\r\nM&agrave; lại chết sớm, đều đ&atilde; chết năm 10!<br />\r\n<br />\r\nTrễ nhất c&aacute;i kia, chết một năm.<br />\r\n<br />\r\nS&aacute;u người n&agrave;y, ở đ&acirc;u ra điểm giống nhau?<br />\r\n<br />\r\nTh&igrave; ra một năm qua n&agrave;y, L&yacute; Hạo đều đang nh&igrave;n c&aacute;i n&agrave;y, thật đ&uacute;ng l&agrave;. . . Đủ nh&agrave;n.<br />\r\n<br />\r\nBất qu&aacute; nh&igrave;n tho&aacute;ng qua người cuối c&ugrave;ng tư liệu, Trương Viễn, Ng&acirc;n Th&agrave;nh cổ viện học vi&ecirc;n, Trần Na mơ hồ biết một ch&uacute;t c&aacute;i g&igrave;, L&yacute; Hạo mục đ&iacute;ch chủ yếu hay l&agrave; bởi v&igrave; vị n&agrave;y a?<br />\r\n<br />\r\n&quot;Trương Viễn, năm 1729 ngày 22 tháng 7 tử vong. . . Giống như khoảng c&aacute;ch L&yacute; Hạo nghỉ học kh&ocirc;ng bao l&acirc;u, hay l&agrave; n&oacute;i kh&ocirc;ng sai biệt lắm ch&iacute;nh l&agrave; khi đ&oacute; nghỉ học?&quot;<br />\r\n<br />\r\nTrần Na nh&igrave;n kỹ một trận, li&ecirc;n tưởng đến L&yacute; Hạo nghỉ học, giống như biết một ch&uacute;t c&aacute;i g&igrave;.<br />\r\n<br />\r\nMột vị cổ viện học vi&ecirc;n, để đ&oacute; tốt đẹp tiền đồ kh&ocirc;ng cần nghỉ học, cho tới nay đều c&oacute; người kh&ocirc;ng hiểu, h&ocirc;m nay nh&igrave;n thấy phần n&agrave;y hồ sơ, Trần Na cảm thấy c&oacute; lẽ c&oacute; một ch&uacute;t đầu mối.<br />\r\n<br />\r\nNghĩ đến c&aacute;i n&agrave;y, trước đ&oacute; &ocirc;m chơi đ&ugrave;a chi t&acirc;m nh&igrave;n hồ sơ Trần Na, cũng nhiều mấy phần chăm ch&uacute;.<br />\r\n<br />\r\nL&yacute; Hạo l&agrave; ho&agrave;i nghi Trương Viễn bị hắn giết sao?<br />\r\n<br />\r\nS&aacute;u người, chẳng lẽ đều l&agrave; hắn giết?<br />\r\n<br />\r\nHệ liệt vụ &aacute;n?<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nTrần Na tại chăm ch&uacute; nh&igrave;n hồ sơ, L&yacute; Hạo đợi một trận, kh&ocirc;ng thể đạt được phản hồi, cũng kh&ocirc;ng phải qu&aacute; để &yacute;, kh&ocirc;ng c&oacute; lại nhiều quản, m&agrave; l&agrave; suy nghĩ từ bản th&acirc;n bước kế tiếp l&agrave;m như thế n&agrave;o đi.<br />\r\n<br />\r\nHiện tại, bản &aacute;n giao cho Tuần Kiểm ti, Tuần Kiểm ti hẳn l&agrave; sẽ đi thăm d&ograve;, nhưng l&agrave; L&yacute; Hạo cũng kh&ocirc;ng c&oacute; &ocirc;m lấy hy vọng qu&aacute; lớn.<br />\r\n<br />\r\nTrừ phi rất nhanh Tuần Dạ Nh&acirc;n tham gia trong đ&oacute;!<br />\r\n<br />\r\n&quot;Mấu chốt l&agrave;, mục ti&ecirc;u kế tiếp l&agrave; của ta n&oacute;i, c&oacute; lẽ gần đ&acirc;y ta liền sẽ gặp phải huyết ảnh, mấy ng&agrave;y trước đ&acirc;y ta mơ hồ nh&igrave;n thấy c&aacute;i b&oacute;ng m&agrave;u đỏ, l&agrave; huyết ảnh sao? N&oacute; đang t&igrave;m ta? Hay l&agrave; n&oacute;i, đ&atilde; t&igrave;m được ta, chỉ l&agrave; bởi v&igrave; th&acirc;n phận của ta, tạm thời kh&ocirc;ng d&aacute;m động thủ?&quot;<br />\r\n<br />\r\nTuần Kiểm ti, d&ugrave; sao cũng l&agrave; cơ cấu chấp ph&aacute;p, học vi&ecirc;n tự thi&ecirc;u chết rồi, chưa chắc sẽ g&acirc;y n&ecirc;n động tĩnh qu&aacute; lớn, d&ugrave; l&agrave; cổ viện học vi&ecirc;n cũng giống vậy.<br />\r\n<br />\r\nCó th&ecirc;̉ một vị tuần kiểm cấp ba chết rồi, l&agrave;m người một nh&agrave;, Tuần Kiểm ti nhất định sẽ cẩn thận kiểm tra truy tra, c&aacute;i n&agrave;y so chết học vi&ecirc;n nghi&ecirc;m trọng hơn.<br />\r\n<br />\r\n&quot;Dựa theo ta hiểu r&otilde; tư liệu, huyết ảnh n&agrave;y mỗi một lần xuất hiện, hẳn l&agrave; c&oacute; thời gian hạn chế, thời gian sẽ kh&ocirc;ng qu&aacute; l&acirc;u, thậm ch&iacute; n&oacute;i, tại điểm đặc biệt n&agrave;o đ&oacute; mới c&oacute; thể xuất hiện.&quot;<br />\r\n<br />\r\n&quot;Mấy vị người chết, kỳ thật cũng c&oacute; một ch&uacute;t điểm giống nhau, chỉ l&agrave;. . . C&oacute; ch&uacute;t kh&ocirc;ng r&otilde;!&quot;<br />\r\n<br />\r\nĐ&atilde; qua một năm, L&yacute; Hạo cũng kh&ocirc;ng phải kh&ocirc;ng thu hoạch được g&igrave;.<br />\r\n<br />\r\nTối thiểu hắn tra được một ch&uacute;t, bao qu&aacute;t Trương Viễn tử vong, s&aacute;u người tử vong ng&agrave;y đ&oacute;, thời tiết giống như đều kh&ocirc;ng phải l&agrave; qu&aacute; tốt.<br />\r\n<br />\r\nC&aacute;i g&igrave; ngày mưa d&ocirc;ng, tia chớp h&igrave;nh cầu, ngày mưa d&acirc;̀m. . .<br />\r\n<br />\r\nLoại kh&iacute; trời n&agrave;y t&igrave;nh huống, để &yacute; người kh&ocirc;ng nhiều, L&yacute; Hạo cũng l&agrave; thật sự l&agrave; kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o t&igrave;m kiếm được c&agrave;ng nhiều manh mối, mới c&oacute; thể đi ch&uacute; &yacute;.<br />\r\n<br />\r\nTrương Viễn chết ng&agrave;y ấy, L&yacute; Hạo r&otilde; r&agrave;ng nhớ kỹ, trong đ&ecirc;m hạ mưa nhỏ, kh&ocirc;ng t&iacute;nh qu&aacute; lớn.<br />\r\n<br />\r\n&quot;Ng&agrave;y mưa mới xuất hiện, hoặc l&agrave; n&oacute;i thời tiết kh&ocirc;ng tốt thời điểm mới ra đến g&acirc;y &aacute;n.&quot;<br />\r\n<br />\r\nL&yacute; Hạo kh&ocirc;ng ngừng ở tr&ecirc;n giấy viết c&aacute;i g&igrave;, rất nhanh lại đem trang giấy x&eacute; n&aacute;t, triệt để vỡ n&aacute;t đằng sau, l&uacute;c n&agrave;y mới để b&uacute;t xuống rơi v&agrave;o trầm tư b&ecirc;n trong.<br />\r\n<br />\r\nHắn đang nghĩ ngợi, đối diện, Trần Na bỗng nhi&ecirc;n thấp giọng hoảng sợ n&oacute;i: &quot;Ta ph&aacute;t hiện!&quot;<br />\r\n<br />\r\nL&yacute; Hạo khẽ giật m&igrave;nh, vội v&agrave;ng ngẩng đầu, ph&aacute;t hiện?<br />\r\n<br />\r\nPh&aacute;t hiện c&aacute;i g&igrave;?<br />\r\n<br />\r\nL&agrave;m sao c&oacute; thể!<br />\r\n<br />\r\nHắn kh&ocirc;ng &ocirc;m hi vọng, Trần Na mới nh&igrave;n một hồi liền ph&aacute;t hiện c&aacute;i g&igrave;, L&yacute; Hạo cảm thấy gia hỏa n&agrave;y l&agrave; đang lừa dối ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\nL&yacute; Hạo nh&iacute;u m&agrave;y, rất nhanh t&ocirc; triển mi đầu, kh&ocirc;i phục d&aacute;ng tươi cười: &quot;Na tỷ, ph&aacute;t hiện c&aacute;i g&igrave; rồi?&quot;<br />\r\n<br />\r\nTrần Na mơ hồ thấy được L&yacute; Hạo trước đ&oacute; cau m&agrave;y bộ d&aacute;ng, c&oacute; ch&uacute;t xấu hổ, l&uacute;c n&agrave;y mới nhớ lại L&yacute; Hạo rất quan t&acirc;m việc n&agrave;y, kh&ocirc;ng phải đ&ugrave;a giỡn thời điểm, hơi c&oacute; vẻ l&uacute;ng t&uacute;ng n&oacute;i: &quot;C&aacute;i kia. . . Kỳ thật ta chưa xem xong, tư liệu nhiều lắm.&quot;<br />\r\n<br />\r\nL&yacute; Hạo trong l&ograve;ng kh&ocirc;ng gợn s&oacute;ng, đo&aacute;n được.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; c&aacute;i g&igrave; thất vọng, bởi v&igrave; vốn l&agrave; kh&ocirc;ng mang theo hi vọng.<br />\r\n<br />\r\nBất qu&aacute; sau một khắc, Trần Na hay l&agrave; mở miệng n&oacute;i: &quot;Ta chưa xem xong, nhưng l&agrave;. . . Thật giống như c&oacute; chút li&ecirc;n quan, chỉ l&agrave; ta kh&ocirc;ng d&aacute;m x&aacute;c định.&quot;<br />\r\n<br />\r\nN&oacute;i, n&agrave;ng giơ l&ecirc;n b&ecirc;n người trang giấy, &quot;Ta c&oacute; ghi viết vẽ tranh thói quen, vừa mới nh&igrave;n một ch&uacute;t văn bản t&agrave;i liệu, ta đem mấy c&aacute;i người trong cuộc danh tự đều viết xuống dưới.&quot;<br />\r\n<br />\r\nL&yacute; Hạo nh&igrave;n tho&aacute;ng qua, khẽ gật đầu.<br />\r\n<br />\r\nS&aacute;u vị người trong cuộc!<br />\r\n<br />\r\nMấy vị n&agrave;y danh tự, L&yacute; Hạo đều c&oacute; thể đọc ngược như chảy, c&aacute;i n&agrave;y lại có th&ecirc;̉ ph&aacute;t hiện c&aacute;i g&igrave;?<br />\r\n<br />\r\nKh&ocirc;ng phải c&ugrave;ng t&ecirc;n, kh&ocirc;ng phải c&ugrave;ng họ, c&oacute; hai chữ, c&oacute; ba chữ, đơn giản n&oacute;i kh&ocirc;ng ch&uacute;t n&agrave;o tương quan.<br />\r\n<br />\r\nM&agrave; Trần Na gặp L&yacute; Hạo ch&uacute; &yacute;, cấp tốc n&oacute;i: &quot;Chu Kh&aacute;nh, Hồng Kiều, Vương Hạo Minh, Lưu V&acirc;n Sinh, Triệu Thế H&agrave;o, Trương Viễn, l&agrave; s&aacute;u người n&agrave;y t&iacute;nh danh a?&quot;<br />\r\n<br />\r\nL&yacute; Hạo khẽ gật đầu.<br />\r\n<br />\r\nTrần Na lại nh&igrave;n một ch&uacute;t tr&ecirc;n giấy danh tự, bỗng nhi&ecirc;n lại c&oacute; ch&uacute;t kh&ocirc;ng x&aacute;c định, n&agrave;ng sợ m&igrave;nh n&oacute;i, L&yacute; Hạo cảm thấy m&igrave;nh lừa gạt hắn, ngược lại kh&ocirc;ng tốt.<br />\r\n<br />\r\nC&oacute; ch&uacute;t xấu hổ, Trần Na ho nhẹ một tiếng n&oacute;i: &quot;Được rồi, coi ta kh&ocirc;ng n&oacute;i, ta lại nh&igrave;n kỹ một ch&uacute;t, giống như kh&ocirc;ng th&iacute;ch hợp, c&ograve;n k&eacute;m hai c&aacute;i.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\nL&yacute; Hạo sửng sốt một ch&uacute;t: &quot;C&aacute;i g&igrave; c&ograve;n k&eacute;m hai c&aacute;i?&quot;<br />\r\n<br />\r\nTrần Na c&agrave;ng th&ecirc;m xấu hổ: &quot;Nếu l&agrave; lại nhiều một c&aacute;i họ Trịnh, một c&aacute;i họ L&yacute;, vậy liền đ&uacute;ng, hiện tại kh&ocirc;ng c&oacute;. . . Khụ khụ, ta cứ như vậy n&oacute;i chuyện, ngươi đừng coi l&agrave; thật.&quot;<br />\r\n<br />\r\nN&agrave;ng kh&ocirc;ng muốn n&oacute;i th&ecirc;m, qu&aacute; l&uacute;ng t&uacute;ng!<br />\r\n<br />\r\nL&yacute; Hạo lại l&agrave; chấn động trong l&ograve;ng.<br />\r\n<br />\r\nTại sao muốn nhiều hai c&aacute;i mới đ&uacute;ng?<br />\r\n<br />\r\nHọ L&yacute;. . . Hắn liền họ L&yacute;!<br />\r\n<br />\r\nC&ograve;n k&eacute;m một c&aacute;i họ Trịnh, k&eacute;m một c&aacute;i. . . L&yacute; Hạo chỉ c&oacute; thể tra được 10 năm trước một ch&uacute;t bản &aacute;n, bởi v&igrave; vượt qua mười năm, một ch&uacute;t kh&ocirc;ng c&oacute; nghi nghị bản &aacute;n, đều sẽ ti&ecirc;u hủy hồ sơ, bởi v&igrave; c&oacute; ch&uacute;t ngo&agrave;i &yacute; muốn vụ &aacute;n qu&aacute; nhiều, bản &aacute;n qu&aacute; nhiều, ph&ograve;ng cơ yếu sẽ kh&ocirc;ng đều bảo tồn.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nGiờ ph&uacute;t n&agrave;y, L&yacute; Hạo chấn động trong l&ograve;ng, lại l&agrave; mặt kh&ocirc;ng đổi sắc, cười cười n&oacute;i: &quot;Na tỷ, c&oacute; c&aacute;i g&igrave; th&igrave; n&oacute;i c&aacute;i đ&oacute;, c&aacute;i n&agrave;y c&oacute; c&aacute;i g&igrave;, n&oacute;i một ch&uacute;t, coi như t&aacute;n gẫu, v&igrave; c&aacute;i g&igrave; n&oacute;i muốn bao nhi&ecirc;u hai c&aacute;i họ mới đ&uacute;ng?&quot;<br />\r\n<br />\r\nTrần Na nh&igrave;n một ch&uacute;t L&yacute; Hạo, hiếu kỳ n&oacute;i: &quot;Ngươi kh&ocirc;ng phải người Ng&acirc;n Th&agrave;nh?&quot;<br />\r\n<br />\r\n&quot;Đúng v&acirc;̣y a.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i kia. . .&quot;<br />\r\n<br />\r\nTrần Na n&oacute;i, nghĩ tới điều g&igrave;, gật đầu n&oacute;i: &quot;Minh bạch, nh&agrave; ngươi c&oacute; phải hay kh&ocirc;ng. . . Kh&ocirc;ng c&oacute; g&igrave; lớn tuổi l&atilde;o nh&acirc;n?&quot;<br />\r\n<br />\r\n&quot;&Ocirc;ng b&agrave; nội của ta qua đời sớm.&quot;<br />\r\n<br />\r\nL&yacute; Hạo c&agrave;ng th&ecirc;m hiếu kỳ, v&igrave; sao lại c&ugrave;ng l&atilde;o nh&acirc;n c&oacute; li&ecirc;n lạc?<br />\r\n<br />\r\nTrần Na gặp hắn c&oacute; hứng th&uacute;, lần n&agrave;y cũng kh&ocirc;ng xấu hổ, mở miệng n&oacute;i: &quot;Nếu l&agrave; trong nh&agrave; c&oacute; l&atilde;o nh&acirc;n m&agrave; n&oacute;i, c&oacute; ch&uacute;t cũ ni&ecirc;n nh&acirc;n ưa th&iacute;ch kể một &iacute;t nơi đ&oacute; lưu truyền thổ ngữ, n&atilde;i n&atilde;i ta trước kia tại ta khi c&ograve;n b&eacute; liền đặc biệt ưa th&iacute;ch n&oacute;i những thứ n&agrave;y.&quot;<br />\r\n<br />\r\nTrần Na nghĩ nghĩ, nhớ lại một ch&uacute;t mới n&oacute;i: &quot;Trước kia ta c&ugrave;ng n&atilde;i n&atilde;i ta c&ugrave;ng một chỗ sinh hoạt, n&atilde;i n&atilde;i ta ưa th&iacute;ch h&aacute;t một ch&uacute;t Ng&acirc;n Th&agrave;nh nơi đ&oacute; lưu truyền thổ kh&uacute;c. Ta nhớ được, c&oacute; một b&agrave;i thổ kh&uacute;c l&agrave; như thế h&aacute;t. . .&quot;<br />\r\n<br />\r\nTrần Na học trước kia n&atilde;i n&atilde;i d&aacute;ng vẻ, hắng giọng một c&aacute;i, nhỏ giọng h&aacute;t n&oacute;i: &quot;L&yacute; gia kiếm, Trương gia đao, Triệu gia quyền, Lưu gia ch&acirc;n. . . Trịnh gia thiếu gia cản trở!&quot;<br />\r\n<br />\r\nH&aacute;t một đoạn ngắn, Trần Na ch&iacute;nh m&igrave;nh cũng kh&ocirc;ng c&oacute; &yacute; tứ, xấu hổ cười n&oacute;i: &quot;D&ugrave;ng tiếng phổ th&ocirc;ng h&aacute;t c&oacute; ch&uacute;t kh&ocirc;ng dễ nghe, kỳ thật n&atilde;i n&atilde;i ta trước kia d&ugrave;ng Ng&acirc;n Th&agrave;nh thổ ngữ h&aacute;t hay l&agrave; rất &ecirc;m tai, đương nhi&ecirc;n, ch&iacute;nh l&agrave; một ch&uacute;t nơi đ&oacute; đản sinh h&iacute; kh&uacute;c, chưa từng nghe qua lời n&oacute;i đều cảm thấy c&oacute; ch&uacute;t thổ.&quot;<br />\r\n<br />\r\nM&agrave; giờ khắc n&agrave;y, L&yacute; Hạo lại l&agrave; &aacute;nh mắt đi&ecirc;n cuồng lấp l&oacute;e.<br />\r\n<br />\r\nThổ kh&uacute;c!<br />\r\n<br />\r\nL&yacute; gia kiếm, Trương gia đao. . .<br />\r\n<br />\r\nTrong thổ kh&uacute;c n&agrave;y, c&oacute; t&aacute;m c&aacute;i họ!<br />\r\n<br />\r\nL&yacute;, Trương, Triệu, Lưu, Vương, Hồng, Chu, Trịnh!<br />\r\n<br />\r\nHắn đoạt lấy Trần Na trang giấy trong tay, một ch&uacute;t kh&ocirc;ng c&oacute; kh&aacute;ch kh&iacute;, trong nh&aacute;y mắt rơi xuống t&ecirc;n thứ nhất ph&iacute;a tr&ecirc;n, Chu Kh&aacute;nh.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; 10 năm trước tử vong vị kia!<br />\r\n<br />\r\nCũng l&agrave; L&yacute; Hạo tra được vị thứ nhất &aacute;n tự thi&ecirc;u người trong cuộc.<br />\r\n<br />\r\nTiếp theo l&agrave; c&aacute;i kia người b&aacute;n h&agrave;ng Hồng Kiều, sau đ&oacute; l&agrave; Vương Hạo Minh. . .<br />\r\n<br />\r\nNếu như, nếu như c&aacute;i thứ nhất chết kh&ocirc;ng phải Chu Kh&aacute;nh, m&agrave; l&agrave; một vị họ Trịnh, vậy Chu Kh&aacute;nh ch&iacute;nh l&agrave; c&aacute;i thứ hai, Hồng Kiều c&aacute;i thứ ba, Vương Hồng Minh c&aacute;i thứ tư. . .<br />\r\n<br />\r\nTrong Ng&acirc;n Th&agrave;nh thổ kh&uacute;c, t&aacute;m đại họ đảo lại, tr&igrave;nh tự thế m&agrave; ho&agrave;n to&agrave;n ph&ugrave; hợp tử vong thời gian.<br />\r\n<br />\r\nCái cu&ocirc;́i cùng chết Trương Viễn, đối ứng Trương gia đao.<br />\r\n<br />\r\nĐ&oacute; ch&iacute;nh l&agrave; n&oacute;i. . . C&ograve;n c&oacute; một c&aacute;i!<br />\r\n<br />\r\nC&ograve;n c&oacute; c&aacute; nh&acirc;n sẽ chết, đối phương họ L&yacute;, trong h&iacute; kh&uacute;c c&aacute;i thứ nhất, L&yacute; gia kiếm!<br />\r\n<br />\r\nL&yacute; Hạo sắc mặt thay đổi.<br />\r\n<br />\r\nHắn vội v&agrave;ng nh&igrave;n về ph&iacute;a Trần Na, kh&ocirc;ng đ&egrave; n&eacute;n được chấn động c&ugrave;ng k&iacute;ch động, thanh &acirc;m đều c&oacute; ch&uacute;t kh&agrave;n kh&agrave;n: &quot;Từ kh&uacute;c n&agrave;y. . . Chừng n&agrave;o th&igrave; bắt đầu lưu truyền, ai truyền tới. . .&quot;<br />\r\n<br />\r\nTrần Na bị &aacute;nh mắt của hắn giật nảy m&igrave;nh, rất nhanh trấn định lại, &quot;Đừng n&oacute;ng vội, L&yacute; Hạo, từ kh&uacute;c n&agrave;y kh&ocirc;ng &iacute;t l&atilde;o nh&acirc;n hẳn l&agrave; nghe qua, người trẻ tuổi nghe qua &iacute;t, cho n&ecirc;n ngươi kh&ocirc;ng biết, ngươi nếu l&agrave; cảm thấy hứng th&uacute;, ch&uacute;ng ta c&oacute; thể từ từ tra, ngươi đừng vội!&quot;<br />\r\n<br />\r\nL&yacute; Hạo h&iacute;t s&acirc;u một hơi, đ&egrave; xuống xung động trong l&ograve;ng.<br />\r\n<br />\r\nKh&ocirc;ng thể gấp!<br />\r\n<br />\r\nHắn kh&ocirc;ng nghĩ tới, chỉ l&agrave; cho Trần Na nh&igrave;n tho&aacute;ng qua, đối phương thế m&agrave; cấp ra trọng yếu như vậy manh mối, từ kh&uacute;c n&agrave;y L&yacute; Hạo chưa từng nghe n&oacute;i qua, cũng kh&ocirc;ng ai h&aacute;t qua, hẳn l&agrave; rất nhiều năm trước liền kh&ocirc;ng lại lưu h&agrave;nh.<br />\r\n<br />\r\nBởi v&igrave; theo tiếng phổ th&ocirc;ng phổ cập, những n&agrave;y nơi đ&oacute; n&ocirc;ng th&ocirc;n thổ kh&uacute;c, sớm sẽ theo người một đời trước xuống mồ, dần dần thất truyền.<br />\r\n<br />\r\nL&yacute; Hạo gia gia bối phận kia chết sớm, L&yacute; Hạo đều kh&ocirc;ng c&oacute; gặp qua, tự nhi&ecirc;n cũng sẽ kh&ocirc;ng c&oacute; người h&aacute;t cho hắn nghe.<br />\r\n<br />\r\n&quot;Trấn định!&quot;<br />\r\n<br />\r\nL&yacute; Hạo trong l&ograve;ng h&ocirc; một tiếng, gấp c&aacute;i g&igrave;, c&oacute; manh mối, đ&acirc;y mới l&agrave; mấu chốt, kh&ocirc;ng vội.<br />\r\n<br />\r\nHiện tại mấu chốt l&agrave;, ta muốn x&aacute;c định c&aacute;i thứ nhất chết, c&oacute; phải hay kh&ocirc;ng họ Trịnh.<br />\r\n<br />\r\n10 năm trước hồ sơ vụ &aacute;n, ph&ograve;ng cơ yếu l&agrave; ti&ecirc;u hủy một ch&uacute;t, có th&ecirc;̉ cũng kh&ocirc;ng phải l&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; chỗ có th&ecirc;̉ tra, m&agrave; lại ch&iacute;nh m&igrave;nh kh&ocirc;ng cần xem kỹ, hắn chỉ cần tra ra mười lăm năm trước đến 10 năm trước tả hữu, c&oacute; hay kh&ocirc;ng bởi v&igrave; tự thi&ecirc;u m&agrave; chết họ Trịnh người chết l&agrave; được.<br />\r\n<br />\r\nGiờ khắc n&agrave;y L&yacute; Hạo, lại l&agrave; k&iacute;ch động, lại l&agrave; hoảng hốt sợ h&atilde;i.<br />\r\n<br />\r\nNếu l&agrave; thật sự đối mặt, vậy kế tiếp. . . Ch&iacute;nh l&agrave; m&igrave;nh!<br />\r\n<br />\r\nTiểu Viễn nhất định nh&igrave;n thấy c&aacute;i g&igrave;, nghe được c&aacute;i g&igrave;, cho n&ecirc;n mới để cho m&igrave;nh trốn.<br />\r\n<br />\r\n&quot;L&yacute; gia kiếm. . .&quot;<br />\r\n<br />\r\n&quot;L&yacute; gia kiếm!&quot;<br />\r\n<br />\r\nL&yacute; Hạo thầm nghĩ lấy, đột nhi&ecirc;n con mắt trợn to, L&yacute; gia. . . C&oacute; kiếm!<br />\r\n<br />\r\nPS: Thời gian đổi mới, khoảng bốn giờ chiều, khoảng mười giờ đ&ecirc;m, s&aacute;ch mới kỳ mỗi ng&agrave;y tạm định hai canh, kh&ocirc;ng c&oacute; c&aacute;ch, s&aacute;ch mới kỳ ta b&ecirc;n n&agrave;y đại kh&aacute;i l&agrave; 40 ng&agrave;y, nhất định phải đi đến, l&ecirc;n giá sau ch&uacute;ng ta nh&igrave;n t&igrave;nh huống bạo chương đi, s&aacute;ch mới kỳ diều h&acirc;u m&ocirc;̣t chương cũng sẽ viết nhiều điểm, chương n&agrave;y liền c&oacute; 5000.</p>', '2021-11-27 21:29:01', 9, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(188, 50, 'Mê vụ dần dần tán ( cầu cất giữ đề cử )', '4', '<p>L&yacute; Hạo nghĩ tới!<br />\r\n<br />\r\nMuốn n&oacute;i kiếm, L&yacute; gia thật l&agrave; c&oacute; một thanh. . . Hoặc l&agrave; một vi&ecirc;n?<br />\r\n<br />\r\nSở dĩ c&oacute; ch&uacute;t kh&ocirc;ng x&aacute;c định, đ&oacute; l&agrave; bởi v&igrave; L&yacute; Hạo nhớ tới kiếm. . . N&oacute; c&oacute; chút kh&ocirc;ng giống kiếm.<br />\r\n<br />\r\nCó th&ecirc;̉ hết lần n&agrave;y tới lần kh&aacute;c L&yacute; Hạo lại biết, đ&acirc;y ch&iacute;nh l&agrave; kiếm.<br />\r\n<br />\r\nRất m&acirc;u thuẫn.<br />\r\n<br />\r\nNhưng m&agrave;, đ&acirc;y l&agrave; sự thật.<br />\r\n<br />\r\nKhi c&ograve;n b&eacute;, phụ th&acirc;n của L&yacute; Hạo đem một vi&ecirc;n ngọc bội h&igrave;nh kiếm đeo đến L&yacute; Hạo tr&ecirc;n cổ, rất nghi&ecirc;m t&uacute;c n&oacute;i cho hắn biết, &quot;C&aacute;i n&agrave;y gọi Tinh Kh&ocirc;ng Kiếm, l&agrave; ta L&yacute; gia duy nhất truyền xuống đồ vật cũ, về sau ngươi giao cho ngươi h&agrave;i tử, đừng n&oacute;i l&agrave; ngọc, đ&acirc;y ch&iacute;nh l&agrave; kiếm.&quot;<br />\r\n<br />\r\nL&uacute;c n&oacute;i chuyện, phụ th&acirc;n rất nghi&ecirc;m t&uacute;c.<br />\r\n<br />\r\nĐương nhi&ecirc;n, nghi&ecirc;m t&uacute;c một l&aacute;t, gặp L&yacute; Hạo m&ecirc; mang, phụ th&acirc;n cũng rất bất đắc dĩ, c&ograve;n n&oacute;i: &quot;Đ&acirc;y l&agrave; gia gia ngươi n&oacute;i với ta, l&atilde;o tổ t&ocirc;ng truyền xuống lời n&oacute;i, đều l&agrave; như thế lời nhắn nhủ, nhất định phải n&oacute;i l&agrave; kiếm, d&ugrave; sao ngươi cứ như vậy gọi n&oacute; l&agrave; được rồi.&quot;<br />\r\n<br />\r\nCho n&ecirc;n L&yacute; Hạo mới c&oacute; thể trước ti&ecirc;n nghĩ đến, L&yacute; gia thật l&agrave; c&oacute; kiếm, t&ecirc;n l&agrave; Tinh Kh&ocirc;ng Kiếm.<br />\r\n<br />\r\nTr&ecirc;n thực tế, giờ ph&uacute;t n&agrave;y liền đeo tại L&yacute; Hạo tr&ecirc;n cổ, một vi&ecirc;n nho nhỏ ngọc bội h&igrave;nh kiếm.<br />\r\n<br />\r\nThời khắc n&agrave;y L&yacute; Hạo, hơi c&oacute; ch&uacute;t hỗn loạn.<br />\r\n<br />\r\nNếu như, trong b&agrave;i d&acirc;n ca n&oacute;i L&yacute; gia kiếm, n&oacute;i ch&iacute;nh l&agrave; ngọc bội kia, vậy L&yacute; gia thật c&oacute; kiếm.<br />\r\n<br />\r\nTrương gia c&oacute; đao sao?<br />\r\n<br />\r\nNgười kh&aacute;c L&yacute; Hạo kh&ocirc;ng qu&aacute; r&otilde; r&agrave;ng, Trương Viễn phụ mẫu sớm qua đời, L&yacute; Hạo quen biết hắn nhiều năm, hắn đối với Trương Viễn rất quen thuộc.<br />\r\n<br />\r\n&quot;Trương gia đao. . .&quot;<br />\r\n<br />\r\nL&yacute; Hạo cẩn thận hồi tưởng một trận, Trương gia c&oacute; hay kh&ocirc;ng đao?<br />\r\n<br />\r\nHồi ức qua lại, rất nhanh, L&yacute; Hạo &aacute;nh mắt khu&ocirc;n mặt c&oacute; ch&uacute;t động một ch&uacute;t, Trương gia, khả năng thật c&oacute; đao.<br />\r\n<br />\r\nC&ugrave;ng m&igrave;nh ngọc bội kh&ocirc;ng giống với, ngọc tốt xấu l&agrave; c&aacute;i đ&aacute;ng tiền đồ chơi, hay l&agrave; khối ngọc cũ, bao nhi&ecirc;u gi&aacute; trị &iacute;t tiền, L&yacute; gia vẫn c&ograve;n c&oacute; ch&uacute;t coi trọng.<br />\r\n<br />\r\nM&agrave; Trương gia đao, L&yacute; Hạo chợt nhớ tới khi c&ograve;n b&eacute;, ch&iacute;nh m&igrave;nh đi Trương Viễn nh&agrave; chơi, Trương Viễn đ&atilde; từng trộm xuất ra một vật c&ugrave;ng L&yacute; Hạo c&ugrave;ng nhau chơi đ&ugrave;a, rất nhanh bị Trương Viễn phụ th&acirc;n ph&aacute;t hiện, đ&aacute;nh tơi bời Trương Viễn một trận.<br />\r\n<br />\r\nVật kia. . . Tựa như l&agrave; một khối đ&aacute; h&igrave;nh đao.<br />\r\n<br />\r\nThời khắc n&agrave;y L&yacute; Hạo, k&yacute; ức c&oacute; ch&uacute;t xa x&ocirc;i mơ hồ, có th&ecirc;̉ mơ hồ c&ograve;n nhớ r&otilde;, ng&agrave;y đ&oacute; Trương Viễn phụ th&acirc;n mắng một trận, n&oacute;i đồ chơi kia l&agrave; l&atilde;o tổ t&ocirc;ng lưu lại, mặc d&ugrave; kh&ocirc;ng đ&aacute;ng tiền, liền truyền khối tảng đ&aacute; vụn, có th&ecirc;̉ đ&oacute; cũng l&agrave; l&atilde;o tổ t&ocirc;ng lưu lại, kh&ocirc;ng cho ph&eacute;p n&eacute;m loạn.<br />\r\n<br />\r\nĐương nhi&ecirc;n, tr&ecirc;n thực tế L&yacute; Hạo r&otilde; r&agrave;ng tr&ocirc;ng thấy, Trương Viễn phụ th&acirc;n t&ugrave;y &yacute; đem h&ograve;n đ&aacute; kia vứt xuống một b&ecirc;n, L&yacute; Hạo ho&agrave;i nghi, ng&agrave;y đ&oacute; Trương phụ ch&iacute;nh l&agrave; muốn đ&aacute;nh nhi tử m&agrave; th&ocirc;i, t&ugrave;y tiện t&igrave;m c&aacute;i cớ.<br />\r\n<br />\r\n&quot;Chẳng lẽ n&oacute;i, đ&oacute; ch&iacute;nh l&agrave; trong b&agrave;i d&acirc;n ca n&oacute;i Trương gia đao?&quot;<br />\r\n<br />\r\nNếu thật l&agrave; dạng n&agrave;y, vậy liền có th&ecirc;̉ đối mặt.<br />\r\n<br />\r\nVề sau, L&yacute; Hạo rốt cuộc chưa thấy qua tảng đ&aacute; kia, tr&ecirc;n thực tế lớn tuổi, L&yacute; Hạo c&ugrave;ng Trương Viễn cũng sẽ kh&ocirc;ng nh&agrave;n rỗi kh&ocirc;ng chuyện g&igrave;, chạy tới chơi một khối đ&aacute;, ven đường c&ograve;n nhiều.<br />\r\n<br />\r\nGiờ khắc n&agrave;y, L&yacute; Hạo tr&ecirc;n đại thể có th&ecirc;̉ x&aacute;c định, khả năng trong b&agrave;i d&acirc;n ca n&agrave;y n&oacute;i đều l&agrave; thật.<br />\r\n<br />\r\nV&ocirc; &yacute; thức sờ l&ecirc;n ngực, ngọc bội thanh lương, cũng kh&ocirc;ng cái gì biến h&oacute;a.<br />\r\n<br />\r\nTia n&agrave;y thanh lương, cũng l&agrave;m cho L&yacute; Hạo trong nh&aacute;y mắt thức tỉnh, rất nhanh, hắn nh&igrave;n về ph&iacute;a Trần Na, c&oacute; ch&uacute;t kh&aacute;t vọng c&ugrave;ng x&uacute;c động, cấp tốc n&oacute;i: &quot;Na tỷ, ta có th&ecirc;̉ gặp ngươi một ch&uacute;t n&atilde;i n&atilde;i sao?&quot;<br />\r\n<br />\r\nHắn muốn biết, từ kh&uacute;c n&agrave;y từ chỗ n&agrave;o truyền tới, ai truyền tới, truyền b&aacute; đ&atilde; bao nhi&ecirc;u năm, từ kh&uacute;c n&agrave;y ho&agrave;n chỉnh kh&ocirc;ng ho&agrave;n chỉnh?<br />\r\n<br />\r\nV&igrave; sao &aacute;n tự thi&ecirc;u n&agrave;y, sẽ c&ugrave;ng một b&agrave;i n&ocirc;ng th&ocirc;n b&agrave;i d&acirc;n ca d&iacute;nh l&iacute;u quan hệ?<br />\r\n<br />\r\nC&aacute;i b&oacute;ng m&agrave;u đỏ kia lại l&agrave; c&aacute;i g&igrave;, v&igrave; sao muốn giết trong b&agrave;i d&acirc;n ca n&agrave;y t&aacute;m nh&agrave; người?<br />\r\n<br />\r\nM&agrave; lại, giết, l&agrave; giết một c&aacute;i, hay l&agrave; n&oacute;i chỉ cần người c&ugrave;ng t&aacute;m nh&agrave; c&oacute; quan hệ đều giết, nếu l&agrave; như vậy. . .<br />\r\n<br />\r\n&quot;Ừm?&quot;<br />\r\n<br />\r\nKh&ocirc;ng đợi Trần Na n&oacute;i chuyện, L&yacute; Hạo đột nhi&ecirc;n khẽ giật m&igrave;nh.<br />\r\n<br />\r\nHắn cấp tốc lật ra văn bản t&agrave;i liệu hồ sơ, mặc d&ugrave; những văn kiện n&agrave;y, hắn sớm đ&atilde; nh&igrave;n v&ocirc; số lần, giờ ph&uacute;t n&agrave;y hay l&agrave; cấp tốc tra x&eacute;t đứng l&ecirc;n, lẩm bẩm n&oacute;i: &quot;Hồng Kiều phụ mẫu đ&atilde; qua đời, l&uacute;c n&agrave;ng chết tuổi kh&ocirc;ng lớn lắm, cho n&ecirc;n Hồng Kiều sau khi chết, Hồng gia b&ecirc;n n&agrave;y, cũng kh&ocirc;ng c&oacute; những người kh&aacute;c.&quot;<br />\r\n<br />\r\n&quot;Chu Kh&aacute;nh b&ecirc;n n&agrave;y, hắn người y&ecirc;u ngược lại l&agrave; c&ograve;n sống, bất qu&aacute; Chu Kh&aacute;nh cũng kh&ocirc;ng con c&aacute;i, vợ chồng nhiều năm cũng kh&ocirc;ng thai ngh&eacute;n hậu đại.&quot;<br />\r\n<br />\r\n&quot;Trương Viễn kh&ocirc;ng cần phải n&oacute;i. . .&quot;<br />\r\n<br />\r\nTrương gia cũng liền Trương Viễn một c&acirc;y d&ograve;ng độc đinh, Trương Viễn phụ mẫu trước kia qua đời, cho n&ecirc;n Trương Viễn cũng l&agrave; Trương gia một vị duy nhất hậu nh&acirc;n.<br />\r\n<br />\r\n&quot;Vương Hạo Minh thời điểm chết c&ograve;n chưa kết h&ocirc;n, bất qu&aacute; Vương Hạo Minh cũng kh&ocirc;ng phải l&agrave; con trai độc nhất, hắn c&ograve;n c&oacute; c&aacute;i đệ đệ. . .&quot;<br />\r\n<br />\r\n&quot;Lưu V&acirc;n Sinh l&agrave; c&aacute;i l&atilde;o nh&acirc;n, độc th&acirc;n cả một đời.&quot;<br />\r\n<br />\r\n&quot;Triệu Thế H&agrave;o c&oacute; c&aacute;i nữ nhi, Triệu Thế H&agrave;o sau khi chết, đi theo hắn th&ecirc; tử c&ugrave;ng rời đi Ng&acirc;n Th&agrave;nh, h&agrave;nh tung kh&ocirc;ng r&otilde;.&quot;<br />\r\n<br />\r\nL&yacute; Hạo cấp tốc tra x&eacute;t một phen, những người n&agrave;y c&oacute; kết h&ocirc;n, c&oacute; c&oacute; h&agrave;i tử, cũng kh&ocirc;ng phải l&agrave; đều l&agrave; người c&ocirc; đơn, cho n&ecirc;n trước đ&oacute; L&yacute; Hạo cũng kh&ocirc;ng ở tr&ecirc;n đ&acirc;y ph&aacute;t hiện vấn đề g&igrave;.<br />\r\n<br />\r\nCó th&ecirc;̉ giờ ph&uacute;t n&agrave;y, L&yacute; Hạo bỗng nhi&ecirc;n nghĩ đến ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\n&quot;Cha mẹ ta ba năm trước đ&acirc;y ngo&agrave;i &yacute; muốn qua đời. . . Tuyệt đối ngo&agrave;i &yacute; muốn, xe cộ mất khống chế đưa đến lật xe, thế nhưng l&agrave;. . . Hiện tại hay l&agrave; ngo&agrave;i &yacute; muốn sao?&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nL&yacute; Hạo phụ mẫu, ba năm trước đ&acirc;y qua đời.<br />\r\n<br />\r\nMột trận ngo&agrave;i &yacute; muốn đưa đến!<br />\r\n<br />\r\nC&ugrave;ng &aacute;n tự thi&ecirc;u kh&ocirc;ng li&ecirc;n hệ ch&uacute;t n&agrave;o, cho n&ecirc;n L&yacute; Hạo trước đ&oacute; cũng chưa từng đem cha mẹ của m&igrave;nh thay v&agrave;o trong đ&oacute;, thay v&agrave;o vụ &aacute;n n&agrave;y, bởi v&igrave; đ&acirc;y l&agrave; &aacute;n tự thi&ecirc;u.<br />\r\n<br />\r\nGiờ khắc n&agrave;y, L&yacute; Hạo trong l&ograve;ng thi&ecirc;n đầu vạn tự.<br />\r\n<br />\r\nTrương, Hồng, Chu, Lưu bốn nh&agrave; xem như triệt để đoạn tuyệt, kh&ocirc;ng c&oacute; trực hệ tồn tại.<br />\r\n<br />\r\nVương Hạo Minh c&oacute; c&aacute;i đệ đệ, Triệu Thế H&agrave;o c&oacute; c&aacute;i nữ nhi, m&agrave; m&igrave;nh nếu l&agrave; L&yacute; gia kiếm n&oacute;i tới L&yacute;, vậy L&yacute; gia. . . Giống như cũng liền ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\n&quot;Đ&acirc;y kh&ocirc;ng phải giết một người, m&agrave; l&agrave;. . . Diệt tộc a!&quot;<br />\r\n<br />\r\nTrong l&ograve;ng c&agrave;ng th&ecirc;m hoảng sợ, hoảng sợ đồng thời, cũng c&agrave;ng th&ecirc;m phẫn nộ!<br />\r\n<br />\r\nCha mẹ của ta, kh&ocirc;ng phải ngo&agrave;i &yacute; muốn tử vong sao?<br />\r\n<br />\r\nĐương nhi&ecirc;n, đ&acirc;y hết thảy c&ograve;n kh&ocirc;ng c&aacute;ch n&agrave;o ho&agrave;n to&agrave;n x&aacute;c định, d&ugrave; sao hiện tại Vương, Triệu hai nh&agrave; c&ograve;n c&oacute; người.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\n&quot;L&yacute; Hạo!&quot;<br />\r\n<br />\r\nTrần Na tiếng la, để L&yacute; Hạo thanh tỉnh lại.<br />\r\n<br />\r\n&quot;Ngươi kh&ocirc;ng sao chứ?&quot;<br />\r\n<br />\r\nTrần Na gặp L&yacute; Hạo hỏi xong n&oacute;i, ch&iacute;nh m&igrave;nh trả lời, hắn cũng kh&ocirc;ng để &yacute; tới, giống như thất thần.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; việc g&igrave;!&quot;<br />\r\n<br />\r\nL&yacute; Hạo vội v&agrave;ng lắc đầu, Trần Na nh&igrave;n hắn một c&aacute;i, bỗng nhi&ecirc;n n&oacute;i: &quot;Ngươi. . . Ngươi cảm thấy mấy người n&agrave;y, c&ugrave;ng trong từ kh&uacute;c mấy nh&agrave; c&oacute; quan hệ? Đều l&agrave; n&ocirc;ng th&ocirc;n b&agrave;i d&acirc;n ca, c&oacute; ch&uacute;t ch&iacute;nh l&agrave; căn cứ một ch&uacute;t thần thoại cải bi&ecirc;n, hơi ch&uacute;t dứt kho&aacute;t ch&iacute;nh l&agrave; ng&agrave;y b&igrave;nh thường t&ugrave;y tiện h&aacute;t một ch&uacute;t, ta cảm thấy khả năng chỉ l&agrave; ngo&agrave;i &yacute; muốn tr&ugrave;ng hợp, đ&acirc;y kh&ocirc;ng phải c&ograve;n thiếu hai c&aacute;i sao?&quot;<br />\r\n<br />\r\nN&oacute;i, n&agrave;ng muốn cười, c&ograve;n kh&ocirc;ng c&oacute; bật cười, bỗng nhi&ecirc;n nao nao thần, nh&igrave;n tho&aacute;ng qua L&yacute; Hạo.<br />\r\n<br />\r\nThiếu đi hai nh&agrave;, một c&aacute;i họ Trịnh, một c&aacute;i họ L&yacute;!<br />\r\n<br />\r\nL&yacute; Hạo?<br />\r\n<br />\r\nViệc n&agrave;y c&ugrave;ng L&yacute; Hạo c&oacute; quan hệ?<br />\r\n<br />\r\nN&agrave;ng d&ugrave; sao cũng l&agrave; tuần kiểm, cơ bản logic c&ugrave;ng suy nghĩ vẫn phải c&oacute;, trong nh&aacute;y mắt liền li&ecirc;n tưởng đến L&yacute; Hạo tr&ecirc;n th&acirc;n, L&yacute; Hạo một mực truy tra những người n&agrave;y, kh&ocirc;ng sẽ c&ugrave;ng hắn c&oacute; quan hệ a?<br />\r\n<br />\r\n&quot;L&yacute; Hạo, ngươi họ L&yacute; đ&uacute;ng kh&ocirc;ng?&quot;<br />\r\n<br />\r\n&quot;. . .&quot;<br />\r\n<br />\r\nL&yacute; Hạo cười khổ một tiếng, ta c&oacute; thể n&oacute;i kh&ocirc;ng phải sao?<br />\r\n<br />\r\nTrần Na lập tức nh&iacute;u m&agrave;y: &quot;Ngươi lời n&oacute;i thật n&oacute;i với ta, s&aacute;u người n&agrave;y chết đi, c&oacute; phải hay kh&ocirc;ng c&oacute; vấn đề?&quot;<br />\r\n<br />\r\nN&agrave;ng nh&igrave;n văn bản t&agrave;i liệu, kh&ocirc;ng c&oacute; nh&igrave;n kỹ, có th&ecirc;̉ giống như đều l&agrave; ngo&agrave;i &yacute; muốn tử vong, m&agrave; lại kh&ocirc;ng phải tập trung ở một năm, c&aacute;i n&agrave;y cũng có th&ecirc;̉ d&iacute;nh l&iacute;u quan hệ?<br />\r\n<br />\r\n&quot;Na tỷ, kh&ocirc;ng n&oacute;i trước c&aacute;i n&agrave;y, ta c&oacute; thể gặp gặp ngươi n&atilde;i n&atilde;i sao?&quot;<br />\r\n<br />\r\nL&yacute; Hạo kh&ocirc;ng muốn ở tr&ecirc;n đ&acirc;y nhiều lời.<br />\r\n<br />\r\nTrần Na thấy thế, cũng kh&ocirc;ng hỏi nữa, đem sự t&igrave;nh trước đ&egrave; &eacute;p xuống, mở miệng n&oacute;i: &quot;Vừa mới ta n&oacute;i, ngươi kh&ocirc;ng nghe thấy, n&atilde;i n&atilde;i ta mấy năm trước liền đ&atilde; qua đời. . .&quot;<br />\r\n<br />\r\n&quot;Thật c&oacute; lỗi!&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; việc g&igrave;, n&atilde;i n&atilde;i ta thời điểm ra đi đều hơn 90, xem như hỉ tang.&quot;<br />\r\n<br />\r\nTrần Na ngược lại l&agrave; kh&ocirc;ng c&oacute; g&igrave;, rất nhanh n&oacute;i: &quot;Bất qu&aacute; từ kh&uacute;c n&agrave;y, n&atilde;i n&atilde;i ta b&ecirc;n kia rất nhiều người biết h&aacute;t. . . Khi c&ograve;n b&eacute; l&agrave; như thế n&agrave;y, hiện tại kh&ocirc;ng biết, qu&ecirc; qu&aacute;n b&ecirc;n kia, c&ograve;n kh&ocirc;ng biết c&oacute; mấy l&atilde;o nh&acirc;n c&ograve;n tại thế, ta rất nhiều năm kh&ocirc;ng c&oacute; trở về.&quot;<br />\r\n<br />\r\nTrần Na gặp L&yacute; Hạo để bụng, lại n&oacute;i: &quot;Nếu kh&ocirc;ng dạng n&agrave;y, ngươi nếu l&agrave; thật cảm thấy hứng th&uacute;, ch&uacute;ng ta r&uacute;t c&aacute;i thời gian, đi ta qu&ecirc; qu&aacute;n một chuyến, thực địa xem x&eacute;t một ch&uacute;t, nếu như gặp phải biết h&aacute;t l&atilde;o nh&acirc;n gia, cũng c&oacute; thể hỏi ch&uacute;t g&igrave;.&quot;<br />\r\n<br />\r\nL&yacute; Hạo nghĩ nghĩ, nhẹ gật đầu.<br />\r\n<br />\r\n&quot;Ng&agrave;y kia ch&uacute;ng ta nghỉ ngơi, Na tỷ ngươi thuận tiện sao?&quot;<br />\r\n<br />\r\n&quot;C&oacute; thể!&quot;<br />\r\n<br />\r\nHai người n&oacute;i xong, L&yacute; Hạo cũng kh&ocirc;ng c&oacute; lại xoắn xu&yacute;t c&aacute;i n&agrave;y, cấp tốc n&oacute;i: &quot;Na tỷ, ngươi tại Tuần Kiểm ti quan hệ so ta rộng, c&oacute; thể gi&uacute;p ta chuyện sao?&quot;<br />\r\n<br />\r\n&quot;N&oacute;i một ch&uacute;t.&quot;<br />\r\n<br />\r\nThời khắc n&agrave;y L&yacute; Hạo, bởi v&igrave; cho Trần Na nh&igrave;n hồ sơ, cũng kh&ocirc;ng c&ograve;n cất giấu che, thấp giọng n&oacute;i: &quot;Ngươi c&oacute; thể gi&uacute;p ta điều tra th&ecirc;m mấy người h&agrave;nh tung sao? C&aacute;i thứ nhất, Vương Hạo Minh đệ đệ, hắn kh&ocirc;ng tại Ng&acirc;n Th&agrave;nh, Ng&acirc;n Th&agrave;nh b&ecirc;n n&agrave;y, hắn liền Vương Hạo Minh một người th&acirc;n, Vương Hạo Minh sau khi chết, hắn thật l&acirc;u chưa từng trở về.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i thứ hai, Triệu Thế H&agrave;o nữ nhi! Triệu Thế H&agrave;o sau khi chết, nữ nhi của hắn c&ugrave;ng vợ hắn c&ugrave;ng một chỗ dọn nh&agrave; rời đi Ng&acirc;n Th&agrave;nh, cũng mất tin tức.&quot;<br />\r\n<br />\r\nNg&acirc;n Th&agrave;nh Tuần Kiểm ti có th&ecirc;̉ quản địa phương, cũng liền Ng&acirc;n Th&agrave;nh khối n&agrave;y, đối với những kh&aacute;c khu vực kh&ocirc;ng c&oacute; quyền quản hạt, c&agrave;ng kh&ocirc;ng c&oacute; hồ sơ tuần tra tư c&aacute;ch.<br />\r\n<br />\r\nT&igrave;m Trần Na, l&agrave; bởi v&igrave; Trần Na tại Tuần Kiểm ti người quen biết c&agrave;ng nhiều.<br />\r\n<br />\r\nM&agrave; Tuần Kiểm ti b&ecirc;n n&agrave;y, L&yacute; Hạo c&ugrave;ng ngoại th&agrave;nh Tuần Kiểm ti kh&ocirc;ng quen, có th&ecirc;̉ lu&ocirc;n c&oacute; những người kh&aacute;c đồng sự, đồng học, bằng hữu tại địa phương kh&aacute;c Tuần Kiểm ti nhậm chức, thật muốn đi t&igrave;m hiểu một ch&uacute;t, cũng c&oacute; thể thăm d&ograve; được tin tức.<br />\r\n<br />\r\nTrần Na &aacute;nh mắt c&oacute; ch&uacute;t l&oacute;e l&ecirc;n một c&aacute;i.<br />\r\n<br />\r\nTra mấy c&aacute;i người trong cuộc gia thuộc?<br />\r\n<br />\r\nL&yacute; Hạo l&agrave; c&oacute; &yacute; g&igrave;?<br />\r\n<br />\r\n&quot;Na tỷ, ngươi nh&igrave;n có th&ecirc;̉ tra được sao?&quot;<br />\r\n<br />\r\nTrần Na nghĩ nghĩ, gật gật đầu: &quot;Tr&ecirc;n đại thể c&oacute; c&aacute;i khu vực m&agrave; n&oacute;i, vậy l&agrave; tốt rồi tra, sợ l&agrave; sợ một ch&uacute;t manh mối kh&ocirc;ng c&oacute;, ngươi phải biết, ra Ng&acirc;n Th&agrave;nh, trời đất bao la, ch&uacute;ng ta kh&ocirc;ng c&oacute; vượt qua khu vực quyền chấp ph&aacute;p hạn, cũng kh&ocirc;ng c&oacute; tư liệu thẩm tra quyền hạn, nhờ người, cũng phải c&oacute; c&aacute;i cố định mục ti&ecirc;u mới tốt nhờ người t&igrave;m hiểu.&quot;<br />\r\n<br />\r\n&quot;Minh bạch, tư liệu của bọn hắn, ta c&oacute; một &iacute;t, quay đầu ta viết cho ngươi, mặc d&ugrave; kh&ocirc;ng biết vị tr&iacute; cụ thể, nhưng tại th&agrave;nh thị n&agrave;o, ta vẫn l&agrave; biết đến.&quot;<br />\r\n<br />\r\nL&yacute; Hạo tra x&eacute;t thật l&acirc;u, đối với mấy vị th&acirc;n nh&acirc;n của người chết t&igrave;nh huống cũng c&oacute; một ch&uacute;t hi&ecirc;̉u rõ, chỉ l&agrave; bởi v&igrave; đối phương ở nơi kh&aacute;c, L&yacute; Hạo kh&ocirc;ng c&aacute;ch n&agrave;o li&ecirc;n hệ với th&ocirc;i.<br />\r\n<br />\r\n&quot;Vậy liền dễ l&agrave;m, việc n&agrave;y giao cho ta tốt!&quot;<br />\r\n<br />\r\nTrần Na đ&aacute;p ứng thống kho&aacute;i.<br />\r\n<br />\r\nL&yacute; Hạo vội v&agrave;ng n&oacute;i tạ ơn.<br />\r\n<br />\r\nHai người n&oacute;i một trận, Trần Na bỗng nhi&ecirc;n giảm thấp thanh &acirc;m n&oacute;i: &quot;L&yacute; Hạo, c&oacute; một số việc. . . D&ugrave; sao ch&iacute;nh ngươi ch&uacute; &yacute; một ch&uacute;t đi! Ta cảm thấy. . . Cảm thấy việc n&agrave;y c&oacute; lẽ kh&ocirc;ng qu&aacute; đơn giản.&quot;<br />\r\n<br />\r\nN&agrave;ng cũng kh&ocirc;ng ngốc, tr&ecirc;n đại thể cũng đo&aacute;n được một ch&uacute;t.<br />\r\n<br />\r\nGiờ ph&uacute;t n&agrave;y, Trần Na đang suy nghĩ, L&yacute; Hạo nếu l&agrave; thật l&agrave; trong b&agrave;i d&acirc;n ca L&yacute; trong &quot;L&yacute; gia kiếm&quot;, vậy liệu rằng cũng gặp phải nguy hiểm?<br />\r\n<br />\r\nTuần Kiểm ti kh&ocirc;ng sợ nguy hiểm, sợ ch&iacute;nh l&agrave; kh&ocirc;ng biết đến từ nơi n&agrave;o nguy hiểm.<br />\r\n<br />\r\nMấy người kia bản &aacute;n, đều l&agrave; ngo&agrave;i &yacute; muốn đưa đến.<br />\r\n<br />\r\nL&yacute; Hạo khẽ gật đầu, kh&ocirc;ng nhiều lời c&aacute;i g&igrave;.<br />\r\n<br />\r\nM&agrave; L&yacute; Hạo ch&iacute;nh m&igrave;nh giờ ph&uacute;t n&agrave;y cũng c&oacute; rất nhiều chuyện c&acirc;̀n bận bịu, cần phải đi nghiệm chứng.<br />\r\n<br />\r\nHắn muốn tra năm 10 trước bản &aacute;n, người Trịnh gia phải chăng chết rồi?<br />\r\n<br />\r\nNếu l&agrave; t&igrave;nh huống cho ph&eacute;p, hắn c&ograve;n muốn đi một chuyến nh&agrave; Trương Viễn, Trương Viễn sau khi chết, nh&agrave; hắn liền kh&ocirc;ng xuống, L&yacute; Hạo muốn đi xem, Trương gia đao, c&ograve;n ở đ&oacute; hay kh&ocirc;ng?<br />\r\n<br />\r\nKh&ocirc;ng chỉ Trương gia đao, c&ograve;n c&oacute; Hồng gia ch&ugrave;y, Chu gia thương, những binh kh&iacute; trong b&agrave;i d&acirc;n ca n&agrave;y, đến c&ugrave;ng tồn tại kh&ocirc;ng tồn tại, c&ograve;n c&oacute; hay kh&ocirc;ng?<br />\r\n<br />\r\nHồng ảnh để mắt tới đến c&ugrave;ng l&agrave; người t&aacute;m nh&agrave;, hay l&agrave; t&aacute;m nh&agrave; tại trong b&agrave;i d&acirc;n ca binh kh&iacute;?<br />\r\n<br />\r\nQuyền cước kh&ocirc;ng cần nhiều lời, người đ&atilde; chết, vậy th&igrave; c&aacute;i g&igrave; cũng bị mất.<br />\r\n<br />\r\nCó th&ecirc;̉ binh kh&iacute;, nếu l&agrave; c&aacute;c nh&agrave; thật c&oacute; truyền thừa, c&ugrave;ng L&yacute; gia một dạng, l&agrave;m bảo vật gia truyền truyền thừa xuống, c&oacute; lẽ c&ograve;n c&oacute; thể t&igrave;m tới một ch&uacute;t manh mối.<br />\r\n<br />\r\nPhải x&aacute;c định hồng ảnh mục đ&iacute;ch, hiểu r&otilde; hơn đối phương, mới c&oacute; thể tốt hơn đi ứng đối nguy cơ.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nL&yacute; Hạo cũng kh&ocirc;ng trực tiếp đi Trương gia, cũng kh&ocirc;ng c&oacute; về nh&agrave;, hắn c&ograve;n c&oacute; việc phải bận rộn.<br />\r\n<br />\r\nTuần Kiểm ti.<br />\r\n<br />\r\nKho hồ sơ.<br />\r\n<br />\r\nQuản l&yacute; hồ sơ Triệu đại mụ, từ trong khố ph&ograve;ng tr&agrave;n đầy tro bụi, chuyển ra một chồng tư liệu, vỗ vỗ tro bụi, giơ l&ecirc;n một mảnh sương m&ugrave; x&aacute;m.<br />\r\n<br />\r\nĐem ph&aacute;t v&agrave;ng tư liệu đưa cho L&yacute; Hạo, c&oacute; ch&uacute;t kỳ qu&aacute;i n&oacute;i: &quot;Tiểu Hạo, ngươi muốn những thứ n&agrave;y để l&agrave;m g&igrave;? Năm 1715 đến 1719 năm, Ng&acirc;n Th&agrave;nh tử vong ti&ecirc;u hộ họ Trịnh danh s&aacute;ch đều ở đ&acirc;y, tổng cộng v&agrave;i trăm người đi, bất qu&aacute; người đều chết rất nhiều năm. . .&quot;<br />\r\n<br />\r\nL&yacute; Hạo khắp khu&ocirc;n mặt l&agrave; ng&acirc;y thơ d&aacute;ng tươi cười, &quot;Triệu tỷ, ph&ograve;ng cơ yếu b&ecirc;n kia c&oacute; ch&uacute;t nghi &aacute;n d&iacute;nh đến những n&agrave;y, cụ thể kh&ocirc;ng thật nhiều n&oacute;i, ng&agrave;i bị li&ecirc;n lụy, quay đầu t&igrave;m được manh mối, ta mời ng&agrave;i ăn cơm.&quot;<br />\r\n<br />\r\n&quot;Ăn cơm coi như xong, Tiểu Hạo a, ngươi c&aacute;i n&agrave;y đều 20 tuổi, n&oacute;i nhỏ cũng kh&ocirc;ng nhỏ, nữ nhi của ta năm nay 22, so ngươi l&agrave; lớn hai tuổi. . .&quot;<br />\r\n<br />\r\n&quot;. . .&quot;<br />\r\n<br />\r\nL&yacute; Hạo c&oacute; ch&uacute;t bất đắc dĩ, vẫn như cũ bảo tr&igrave; d&aacute;ng tươi cười: &quot;Triệu tỷ, ta quay đầu tr&ograve; chuyện, ta c&ograve;n nhỏ.&quot;<br />\r\n<br />\r\nN&oacute;i, hơi c&oacute; vẻ chật vật cầm hồ sơ cấp tốc rời đi.<br />\r\n<br />\r\nTuần Kiểm ti c&aacute;c b&aacute;c g&aacute;i rất nhiệt t&igrave;nh, nhưng l&agrave; liền điểm ấy kh&ocirc;ng tốt lắm, ưa th&iacute;ch giới thiệu với hắn nữ nhi.<br />\r\n<br />\r\nKh&ocirc;ng phải nữ nhi, ch&iacute;nh l&agrave; chất nữ, nếu kh&ocirc;ng phải l&agrave; nh&agrave; ai ai ai c&ocirc; nương.<br />\r\n<br />\r\nAi bảo hắn L&yacute; Hạo thi được qua Ng&acirc;n Th&agrave;nh cổ viện, mặc d&ugrave; th&ocirc;i học, vẫn như cũ kh&ocirc;ng trở ngại hắn được hoan ngh&ecirc;nh tr&igrave;nh độ.<br />\r\n<br />\r\nLấy được danh s&aacute;ch L&yacute; Hạo, cấp tốc lật xem.<br />\r\n<br />\r\nV&agrave;i trăm người danh s&aacute;ch kh&ocirc;ng nhiều, tr&ecirc;n đại thể đều giới thiệu nguy&ecirc;n nh&acirc;n tử vong, đ&acirc;y l&agrave; ti&ecirc;u hộ danh s&aacute;ch, tiếp xuống L&yacute; Hạo c&ograve;n muốn b&agrave;i trừ một bộ phận, kết hợp với một ch&uacute;t tư liệu kh&aacute;c, mới c&oacute; thể kh&oacute;a chặt phải chăng c&oacute; hắn muốn t&igrave;m kiếm người Trịnh gia.<br />\r\n<br />\r\nKết quả tốt nhất, tự nhi&ecirc;n l&agrave; trực tiếp c&oacute; giới thiệu, chết bởi tự thi&ecirc;u, đ&oacute; l&agrave; tốt nhất thuận tiện nhất.<br />\r\n<br />\r\nL&yacute; Hạo xem như ph&aacute;t hiện, hồng ảnh giết người, hoặc l&agrave; n&oacute;i nếu như hồng ảnh ph&iacute;a sau c&oacute; c&aacute;i g&igrave; thế lực n&oacute;i, bọn hắn giết người, giết t&aacute;m nh&agrave; d&ograve;ng ch&iacute;nh hoặc l&agrave; d&ograve;ng độc đinh, đều l&agrave; d&ugrave;ng tự thi&ecirc;u thủ đoạn đi giết người.<br />\r\n<br />\r\nNếu như kh&ocirc;ng phải như vậy, L&yacute; Hạo cũng kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o kh&oacute;a chặt ph&iacute;a trước mấy người.<br />\r\n<br />\r\nỞ trong đ&oacute;, c&oacute; lẽ ẩn giấu đi một số b&iacute; mật, L&yacute; Hạo tạm thời c&ograve;n kh&ocirc;ng c&aacute;ch n&agrave;o thăm d&ograve;.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Năm 1720 ph&aacute;t sinh qua &aacute;n tự thi&ecirc;u, tiếp theo l&agrave; năm 1723 ph&aacute;t sinh, cho n&ecirc;n trước mặt thời gian hẳn l&agrave; ngang nhau hoặc l&agrave; c&agrave;ng ng&agrave;y c&agrave;ng d&agrave;i, lớn nhất khả năng, l&agrave; năm 1717 trước đ&oacute; tử vong.&quot;<br />\r\n<br />\r\nL&yacute; Hạo vừa đi vừa nh&igrave;n, hắn nhất định phải x&aacute;c định, c&aacute;i n&agrave;y người Trịnh gia, phải chăng chết rồi.<br />\r\n<br />\r\nNếu như chết rồi, vậy đại biểu trước đ&oacute; suy đo&aacute;n đều l&agrave; đ&uacute;ng.<br />\r\n<br />\r\nNếu như kh&ocirc;ng chết. . . Vậy người Trịnh gia nếu l&agrave; c&ograve;n sống, cũng l&agrave; kh&ocirc;ng phải kh&ocirc;ng thu hoạch, đối phương c&ograve;n sống, c&oacute; thể sẽ cho L&yacute; Hạo cung cấp c&agrave;ng nhiều manh mối, thậm ch&iacute; hắn cảm thấy người Trịnh gia c&ograve;n sống c&agrave;ng tốt hơn , vậy rất c&oacute; thể c&ugrave;ng hồng ảnh c&oacute; trực tiếp li&ecirc;n quan.<br />\r\n<br />\r\n&quot;Trong b&agrave;i d&acirc;n ca, h&aacute;t l&agrave; Trịnh gia thiếu gia cản trở, c&ugrave;ng những nh&agrave; kh&aacute;c kh&ocirc;ng giống nhau lắm. . . C&oacute; lẽ Trịnh gia cũng c&oacute; ch&uacute;t chỗ kh&aacute;c biệt.&quot;<br />\r\n<br />\r\nĐang nghĩ ngợi, L&yacute; Hạo lật đến một tờ.<br />\r\n<br />\r\n&quot;Trịnh V&acirc;n Kỳ, năm 1715 th&aacute;ng 9 ng&agrave;y 12 tử vong, nguy&ecirc;n nh&acirc;n c&aacute;i chết: Kh&iacute; ga bạo tạc. . .&quot;<br />\r\n<br />\r\nL&yacute; Hạo &aacute;nh mắt trong nh&aacute;y mắt sắc b&eacute;n đứng l&ecirc;n, kh&iacute; ga bạo tạc?<br />\r\n<br />\r\nLời như vậy, tử vong thời điểm, c&oacute; thể sẽ chết kh&ocirc;ng to&agrave;n th&acirc;y, c&ugrave;ng tự thi&ecirc;u tử vong phương thức rất giống.<br />\r\n<br />\r\nM&agrave; lại phương thức tử vong như vậy, cũng sẽ kh&ocirc;ng qu&aacute; để người ch&uacute; &yacute;.<br />\r\n<br />\r\n&quot;L&agrave; hắn sao?&quot;<br />\r\n<br />\r\nL&yacute; Hạo th&igrave; th&agrave;o một tiếng, kh&iacute; ga bạo tạc, khả năng chết kh&ocirc;ng chỉ một, lời như vậy, Trịnh gia thậm ch&iacute; c&oacute; thể tận diệt!<br />\r\n<br />\r\nLật xem mấy trăm danh s&aacute;ch n&agrave;y, vị n&agrave;y l&agrave; trong b&agrave;i d&acirc;n ca người Trịnh gia khả năng lớn nhất.<br />\r\n<br />\r\n&quot;C&oacute; lẽ. . . C&oacute; thể đi nh&igrave;n xem.&quot;<br />\r\n<br />\r\nL&yacute; Hạo nghĩ đến, ph&iacute;a tr&ecirc;n c&oacute; đối phương địa chỉ, đương nhi&ecirc;n, 15 năm về sau, chỗ kia c&ograve;n ở đ&oacute; hay kh&ocirc;ng, cũng kh&ocirc;ng tốt n&oacute;i.<br />\r\n<br />\r\nH&ocirc;m nay Trần Na một b&agrave;i b&agrave;i d&acirc;n ca, để L&yacute; Hạo trong nh&aacute;y mắt nắm giữ rất nhiều manh mối.<br />\r\n<br />\r\nTrong đầu, cũng dần dần l&agrave;m r&otilde; &aacute;n tự thi&ecirc;u n&agrave;y nơi mấu chốt.<br />\r\n<br />\r\nNếu như c&aacute;i n&agrave;y Trịnh V&acirc;n Kỳ ch&iacute;nh l&agrave; L&yacute; Hạo muốn t&igrave;m người Trịnh gia, n&oacute;i như vậy, trong người t&aacute;m họ, khả năng liền m&igrave;nh c&ograve;n sống.<br />\r\n<br />\r\n&quot;B&aacute;o th&ugrave;? Hay l&agrave; kh&aacute;c?&quot;<br />\r\n<br />\r\n&quot;Hồng ảnh l&agrave; người v&igrave; nu&ocirc;i, hay l&agrave; một loại năng lực đặc th&ugrave;, lại hoặc l&agrave; chỉ l&agrave; một loại chướng nh&atilde;n ph&aacute;p?&quot;<br />\r\n<br />\r\nL&yacute; Hạo lần nữa sờ l&ecirc;n ngực ngọc kiếm, tổ truyền ngọc kiếm n&agrave;y, chỉ l&agrave; đơn thuần gia truyền bảo, vẫn c&ograve;n c&oacute; ch&uacute;t t&aacute;c dụng kh&aacute;c?<br />\r\n<br />\r\n&quot;Người bảy nh&agrave; đều đ&atilde; chết, như vậy kế tiếp tất nhi&ecirc;n l&agrave; ta, đối phương lại l&agrave; như thế n&agrave;o t&igrave;m đến ta, t&igrave;m tới những người kh&aacute;c, ch&uacute;ng ta cũng kh&ocirc;ng cái gì chỗ đặc th&ugrave;, hồng ảnh v&igrave; c&aacute;i g&igrave; c&oacute; thể kh&oacute;a chặt ch&uacute;ng ta?&quot;<br />\r\n<br />\r\nNguy cơ tử vong, giống như dần dần tiến đến.<br />\r\n<br />\r\nGiờ khắc n&agrave;y, L&yacute; Hạo đ&atilde; cảm nhận được mưa gi&oacute; nổi l&ecirc;n, hắn cấp tốc kiểm tra một hồi m&aacute;y truyền tin trong tay, hắn muốn điều tra th&ecirc;m, gần nhất ng&agrave;y n&agrave;o l&agrave; ngày mưa d&acirc;̀m , dựa theo thời gian quy củ, lần tiếp theo ngày mưa d&acirc;̀m, khả năng ch&iacute;nh l&agrave; hồng ảnh thời điểm xuất hiện.<br />\r\n<br />\r\n&quot;Ngày 18 tháng 7, &acirc;m!&quot;<br />\r\n<br />\r\nL&yacute; Hạo sắc mặt biến h&oacute;a, h&ocirc;m nay l&agrave; số 12, mấy ng&agrave;y kế tiếp đều l&agrave; thời tiết tốt, số 18 sẽ chuyển &acirc;m, đằng sau liền c&oacute; một đoạn thời gian ngày mưa d&acirc;̀m.<br />\r\n<br />\r\n&quot;N&oacute;i c&aacute;ch kh&aacute;c, nhanh nhất n&oacute;i, khả năng sau s&aacute;u ng&agrave;y, hồng ảnh liền sẽ xuất hiện lần nữa? Coi như kh&ocirc;ng phải s&aacute;u ng&agrave;y, tiếp xuống ngày mưa d&acirc;̀m, đối phương đều c&oacute; thể sẽ xuất hiện.&quot;<br />\r\n<br />\r\nGiờ khắc n&agrave;y, L&yacute; Hạo tr&aacute;i tim giống như bị siết chặt!<br />\r\n<br />\r\nTrước đ&oacute; hắn c&ograve;n kh&ocirc;ng x&aacute;c định, đối phương c&oacute; phải hay kh&ocirc;ng muốn t&igrave;m ch&iacute;nh m&igrave;nh, mục ti&ecirc;u kế tiếp c&oacute; phải hay kh&ocirc;ng ch&iacute;nh m&igrave;nh, có th&ecirc;̉ giờ ph&uacute;t n&agrave;y, hắn rất chắc chắn, hồng ảnh mục ti&ecirc;u kế tiếp ch&iacute;nh l&agrave; m&igrave;nh.<br />\r\n<br />\r\n&quot;Tiểu Viễn cuối c&ugrave;ng để cho ta trốn, c&oacute; phải hay kh&ocirc;ng n&oacute;i, hắn cũng đo&aacute;n được, hoặc l&agrave; từ hồng ảnh nơi đ&oacute; biết được, kế tiếp giết ch&iacute;nh l&agrave; ta?&quot;<br />\r\n<br />\r\n&quot;Vậy v&igrave; sao lần trước hồng ảnh kh&ocirc;ng trực tiếp giết ta, l&agrave;m g&igrave; c&aacute;ch một năm mới đến giết ta?&quot;<br />\r\n<br />\r\n&quot;Hồng ảnh giết người, c&oacute; lẽ l&agrave; c&oacute; hạn chế, hoặc l&agrave; n&oacute;i, tự thi&ecirc;u thủ đoạn, c&oacute; lẽ chỉ c&oacute; thể d&ugrave;ng ra một lần, m&agrave; giết ta, phải d&ugrave;ng tự thi&ecirc;u thủ đoạn mới được?&quot;<br />\r\n<br />\r\nVuốt vuốt huyệt th&aacute;i dương, L&yacute; Hạo bỗng nhi&ecirc;n cười.<br />\r\n<br />\r\n&quot;An t&acirc;m!&quot;<br />\r\n<br />\r\nĐối với kh&ocirc;ng biết sợ h&atilde;i, kỳ thật thắng qua sắp đến nguy cơ.<br />\r\n<br />\r\nTrước đ&oacute; hắn đối với hồng ảnh hiểu qu&aacute; &iacute;t, mục đ&iacute;ch, lai lịch, mục ti&ecirc;u. . . Những n&agrave;y L&yacute; Hạo đều ho&agrave;n to&agrave;n kh&ocirc;ng biết g&igrave; cả, cho n&ecirc;n hắn một năm qua n&agrave;y, kỳ thật rất sợ h&atilde;i.<br />\r\n<br />\r\nNhưng m&agrave;, khi hắn thăm d&ograve; m&ocirc;̣t chút manh mối, minh x&aacute;c biết mục ti&ecirc;u của đối phương ch&iacute;nh l&agrave; m&igrave;nh, L&yacute; Hạo ngược lại kh&ocirc;ng c&oacute; sợ h&atilde;i như vậy.<br />\r\n<br />\r\nC&oacute; chỉ l&agrave; phẫn nộ, phẫn hận!<br />\r\n<br />\r\nNợ m&aacute;u trả bằng m&aacute;u!<br />\r\n<br />\r\nLần n&agrave;y, đối phương nếu quả như thật sẽ xuất hiện, d&ugrave; l&agrave; bốc l&ecirc;n bại lộ phong hiểm, hắn cũng muốn đi t&igrave;m Tuần Dạ Nh&acirc;n, đi t&igrave;m bọn họ, n&oacute;i cho bọn hắn, m&igrave;nh c&oacute; thể nh&igrave;n thấy hồng ảnh!<br />\r\n<br />\r\nNếu l&agrave; c&oacute; thể đ&aacute;nh giết hồng ảnh, d&ugrave; l&agrave; ch&iacute;nh m&igrave;nh cũng rơi v&agrave;o c&aacute;i bỏ m&igrave;nh hạ tr&agrave;ng, vậy cũng đ&aacute;ng gi&aacute;!<br />\r\n<br />\r\n&quot;Tiểu Viễn. . . C&ograve;n c&oacute; cha mẹ, nếu như c&aacute;c ngươi cũng l&agrave; chết tại hồng ảnh trong tay, lần n&agrave;y, ta nhất định sẽ b&aacute;o th&ugrave; cho c&aacute;c ngươi!&quot;<br />\r\n<br />\r\nL&yacute; Hạo nắm chặt nắm đấm, tr&ecirc;n khu&ocirc;n mặt &ocirc;n h&ograve;a, lộ ra một v&ograve;ng kh&ocirc;ng tương xứng vẻ dữ tợn!</p>', '2021-11-27 21:29:29', 10, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(189, 50, 'Một cái tác động đến nhiều cái', '5', '<p>S&aacute;u giờ chiều.<br />\r\n<br />\r\nTuần Kiểm ti giờ tan sở đến, L&yacute; Hạo cũng kh&ocirc;ng phải l&agrave; Team 1 nh&acirc;n vi&ecirc;n chấp ph&aacute;p, tự nhi&ecirc;n l&agrave; theo giờ tan tầm.<br />\r\n<br />\r\nNg&agrave;y b&igrave;nh thường, L&yacute; Hạo đ&atilde; sớm rời đi.<br />\r\n<br />\r\nCó th&ecirc;̉ h&ocirc;m nay, L&yacute; Hạo kh&ocirc;ng c&oacute; đi vội v&atilde;.<br />\r\n<br />\r\nPh&ograve;ng l&agrave;m việc của trưởng ph&ograve;ng.<br />\r\n<br />\r\nVương Kiệt nh&igrave;n tho&aacute;ng qua L&yacute; Hạo, trầm giọng n&oacute;i: &quot;Ngươi n&oacute;i ngươi muốn đi nh&agrave; Trương Viễn nh&igrave;n xem?&quot;<br />\r\n<br />\r\n&quot;V&acirc;ng.&quot;<br />\r\n<br />\r\n&quot;Ngươi c&ugrave;ng hắn l&agrave; đồng học, hiện tại bản &aacute;n c&oacute; thể l&agrave; hung s&aacute;t &aacute;n, ngươi đi d&ograve; tra cũng kh&ocirc;ng phải kh&ocirc;ng thể, bất qu&aacute;. . . Ph&ograve;ng cơ yếu nh&acirc;n vi&ecirc;n, xin mời s&uacute;ng ống mang về nh&agrave;, đ&acirc;y cũng l&agrave; c&oacute; ch&uacute;t phiền phức.&quot;<br />\r\n<br />\r\nĐ&uacute;ng vậy, L&yacute; Hạo l&agrave; đến xin mời s&uacute;ng ống.<br />\r\n<br />\r\nNhất định phải Vương Kiệt trả lời, hắn c&oacute; thể mang theo s&uacute;ng về nh&agrave;.<br />\r\n<br />\r\nCứ việc c&aacute;i b&oacute;ng m&agrave;u đỏ kia rất cổ qu&aacute;i, s&uacute;ng ống chưa hẳn có th&ecirc;̉ hữu dụng, có th&ecirc;̉ hồng ảnh hiện tại chưa hẳn có th&ecirc;̉ đi ra, đ&atilde; như vậy, L&yacute; Hạo c&agrave;ng hẳn l&agrave; cẩn thận ngược lại l&agrave; người.<br />\r\n<br />\r\nNếu như l&agrave; người, vậy s&uacute;ng ống liền c&oacute; lực s&aacute;t thương rất lớn.<br />\r\n<br />\r\n&quot;Trưởng ph&ograve;ng, kh&ocirc;ng phải mang về nh&agrave;, l&agrave; ph&aacute; &aacute;n d&ugrave;ng!&quot;<br />\r\n<br />\r\nL&yacute; Hạo uốn nắn một ch&uacute;t.<br />\r\n<br />\r\nVương Kiệt cười, mang về nh&agrave; c&ugrave;ng ph&aacute; &aacute;n d&ugrave;ng, đ&oacute; l&agrave; hai kh&aacute;i niệm, tiểu tử n&agrave;y kh&ocirc;ng hổ l&agrave; Ng&acirc;n Th&agrave;nh cổ viện học vi&ecirc;n, một c&acirc;u liền đổi t&iacute;nh chất.<br />\r\n<br />\r\n&quot;Ch&uacute;ng ta ph&ograve;ng cơ yếu, cũng kh&ocirc;ng phải đội chấp ph&aacute;p.&quot;<br />\r\n<br />\r\nVương Kiệt lắc đầu, L&yacute; Hạo c&oacute; ch&uacute;t thất vọng.<br />\r\n<br />\r\nĐối ph&oacute; hồng ảnh, hắn tạm thời c&ograve;n kh&ocirc;ng c&oacute; nghĩ đến biện ph&aacute;p tốt, bởi v&igrave; kh&ocirc;ng hiểu nhiều, nhưng nếu l&agrave; trong tay cầm một khẩu s&uacute;ng, bao nhi&ecirc;u sẽ gia tăng một ch&uacute;t cảm gi&aacute;c an to&agrave;n.<br />\r\n<br />\r\n&quot;Trương Viễn bản &aacute;n, hiện tại chỉ l&agrave; c&ugrave;ng đội chấp ph&aacute;p trao đổi một ch&uacute;t, c&ograve;n kh&ocirc;ng c&oacute; ch&iacute;nh thức lập &aacute;n, ngươi l&agrave; kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o dựa theo c&aacute;i n&agrave;y đến xin mời s&uacute;ng lục.&quot;<br />\r\n<br />\r\nN&oacute;i, Vương Kiệt suy nghĩ một ch&uacute;t, rất nhanh n&oacute;i: &quot;Dạng n&agrave;y, gần nhất Ng&acirc;n Th&agrave;nh cổ viện b&ecirc;n kia, rất nhanh sẽ c&oacute; một lần đối ngoại khảo s&aacute;t, Tuần Kiểm ti b&ecirc;n n&agrave;y lại ph&aacute;i một bộ phận người tiến h&agrave;nh bảo hộ l&agrave;m việc. Ngươi điền giấy tờ một ch&uacute;t, xin mời tham gia, dạng n&agrave;y tr&ecirc;n người ngươi liền c&oacute; nhiệm vụ, l&agrave;m nh&acirc;n vi&ecirc;n bảo hộ, l&agrave; c&oacute; tư c&aacute;ch mang theo s&uacute;ng.&quot;<br />\r\n<br />\r\nL&yacute; Hạo ngẩn người, rất nhanh c&oacute; ch&uacute;t bất đắc dĩ.<br />\r\n<br />\r\nQuanh đi quẩn lại, th&igrave; ra nhiệm vụ n&agrave;y hay l&agrave; treo ở tr&ecirc;n đầu?<br />\r\n<br />\r\nBuổi s&aacute;ng thời điểm, Trần Na liền đề cập qua việc n&agrave;y, L&yacute; Hạo cự tuyệt.<br />\r\n<br />\r\nNg&acirc;n Th&agrave;nh cổ viện b&ecirc;n kia, lần n&agrave;y nghe n&oacute;i hay l&agrave; đạo sư của m&igrave;nh dẫn đội, n&oacute;i như vậy, nếu l&agrave; đ&oacute;n lấy nhiệm vụ n&agrave;y, rất nhanh lại được gặp được sư phụ rồi?<br />\r\n<br />\r\nVương Kiệt gặp hắn trầm mặc, thở d&agrave;i một tiếng n&oacute;i: &quot;Thế n&agrave;o? Sợ bị Vi&ecirc;n l&atilde;o đ&aacute;nh? Nghe n&oacute;i ngươi nghỉ học thời điểm, c&ugrave;ng Vi&ecirc;n l&atilde;o g&acirc;y kh&ocirc;ng qu&aacute; vui sướng, Tiểu Hạo a, ngươi nếu tiến nhập Tuần Kiểm ti, ta nh&igrave;n ngươi hay l&agrave; t&igrave;m cơ hội cải thiện một ch&uacute;t quan hệ, d&ugrave; sao cũng l&agrave; l&atilde;o sư của ngươi.&quot;<br />\r\n<br />\r\n&quot;Vi&ecirc;n l&atilde;o nếu l&agrave; k&eacute;o ngươi một c&aacute;i, ngươi tại Tuần Kiểm ti tiền đồ c&agrave;ng quang minh, nhưng nếu l&agrave; mọi người đều biết, Vi&ecirc;n l&atilde;o kh&ocirc;ng ch&agrave;o đ&oacute;n ngươi, tuy n&oacute;i Vi&ecirc;n l&atilde;o kh&ocirc;ng quản được Tuần Kiểm ti, có th&ecirc;̉ những người kh&aacute;c cũng phải c&acirc;n nhắc, c&oacute; cần thiết hay kh&ocirc;ng v&igrave; ngươi một c&aacute;i tuần kiểm cấp ba, c&ugrave;ng Vi&ecirc;n l&atilde;o l&agrave;m kh&oacute; dễ.&quot;<br />\r\n<br />\r\nVương Kiệt cũng coi l&agrave; nhọc l&ograve;ng, hắn kh&ocirc;ng biết cụ thể, lại l&agrave; nghe n&oacute;i L&yacute; Hạo nghỉ học, c&ugrave;ng đạo sư của hắn g&acirc;y kh&ocirc;ng thoải m&aacute;i.<br />\r\n<br />\r\nĐối phương l&agrave; kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o tham dự Tuần Kiểm ti l&ecirc;n xuống, có th&ecirc;̉ Ng&acirc;n Th&agrave;nh Tuần Kiểm ti, bao nhi&ecirc;u muốn b&aacute;n mấy phần mặt mũi.<br />\r\n<br />\r\n&quot;Ngươi muốn s&uacute;ng lục, đ&acirc;y l&agrave; biện ph&aacute;p tốt nhất, c&ograve;n c&oacute; thể h&ograve;a ho&atilde;n một ch&uacute;t c&ugrave;ng ngươi l&atilde;o sư quan hệ, nhất cử lưỡng tiện. . .&quot;<br />\r\n<br />\r\nHắn đang muốn khuy&ecirc;n nhiều v&agrave;i c&acirc;u, bởi v&igrave; hắn rất xem trọng L&yacute; Hạo.<br />\r\n<br />\r\nC&ograve;n kh&ocirc;ng c&oacute; khuy&ecirc;n v&agrave;i c&acirc;u, liền nghe L&yacute; Hạo gật đầu n&oacute;i: &quot;Được rồi!&quot;<br />\r\n<br />\r\nLời vừa tới miệng, bỗng nhi&ecirc;n kẹt tại trong cổ họng!<br />\r\n<br />\r\nVương Kiệt c&oacute; ch&uacute;t cổ qu&aacute;i nh&igrave;n tho&aacute;ng qua L&yacute; Hạo, kh&ocirc;ng phải n&oacute;i tiểu tử n&agrave;y mặc d&ugrave; lương thiện, có th&ecirc;̉ t&iacute;nh c&aacute;ch c&oacute; chút trục sao?<br />\r\n<br />\r\nH&ocirc;m nay l&agrave;m sao đ&aacute;p ứng thống kho&aacute;i như vậy?<br />\r\n<br />\r\nL&yacute; Hạo tr&ecirc;n mặt lộ ra trung thực m&agrave; bất đắc dĩ d&aacute;ng tươi cười: &quot;Trưởng ph&ograve;ng, b&ecirc;n ngo&agrave;i n&oacute;i chưa hẳn ch&iacute;nh l&agrave; thật. Ta c&ugrave;ng l&atilde;o sư quan hệ, kh&ocirc;ng c&oacute; khoa trương như vậy. Nghỉ học thời điểm, l&atilde;o sư l&agrave; kh&ocirc;ng h&agrave;i l&ograve;ng lắm, bất qu&aacute; đ&atilde; qua một năm, đ&atilde; sớm ti&ecirc;u tan, v&agrave;i ng&agrave;y trước c&ograve;n đốc x&uacute;c ta, để cho ta tại Tuần Kiểm ti l&agrave;m rất tốt đ&acirc;u.&quot;<br />\r\n<br />\r\n&quot;Vậy l&agrave; tốt rồi, vậy l&agrave; tốt rồi!&quot;<br />\r\n<br />\r\nVương Kiệt gật gật đầu, biểu thị rất h&agrave;i l&ograve;ng, mặc d&ugrave; kh&ocirc;ng biết thật giả, bất qu&aacute; L&yacute; Hạo người coi như trung thực, hẳn l&agrave; cũng kh&ocirc;ng đến mức qu&aacute; giả.<br />\r\n<br />\r\n&quot;Vậy ngươi điền phiếu một ch&uacute;t, qua v&agrave;i ng&agrave;y ngươi c&ograve;n phải ra chuyến l&agrave;m việc b&ecirc;n ngo&agrave;i, c&ugrave;ng đi đội chấp ph&aacute;p c&ugrave;ng một chỗ, bảo hộ Ng&acirc;n Th&agrave;nh cổ viện đội khảo s&aacute;t.&quot;<br />\r\n<br />\r\nL&yacute; Hạo tiếp nhận bảng biểu, nh&igrave;n tho&aacute;ng qua, cổ viện đội khảo s&aacute;t xuất h&agrave;nh ng&agrave;y ổn định ở cuối th&aacute;ng.<br />\r\n<br />\r\nBất qu&aacute; mục đ&iacute;ch g&igrave; lại l&agrave; kh&ocirc;ng c&oacute; viết, chỉ l&agrave; để t&ugrave;y h&agrave;nh nh&acirc;n vi&ecirc;n chờ lệnh.<br />\r\n<br />\r\nĐ&acirc;y cũng l&agrave; để ph&ograve;ng tin tức tiết ra ngo&agrave;i, cổ viện c&oacute; cao thượng địa vị, tự nhi&ecirc;n cũng c&oacute; n&oacute; gi&aacute; trị chỗ, cổ viện đội khảo s&aacute;t, hay l&agrave; l&atilde;o sư của m&igrave;nh dẫn đội khảo s&aacute;t, nhiệm vụ lần n&agrave;y chỉ sợ sẽ kh&ocirc;ng qu&aacute; đơn giản.<br />\r\n<br />\r\nCũng kh&ocirc;ng biết c&oacute; thể hay kh&ocirc;ng c&ugrave;ng hồng ảnh sự t&igrave;nh c&oacute; xung đột.<br />\r\n<br />\r\nL&atilde;o sư xuất h&agrave;nh, x&aacute;c suất lớn sẽ c&oacute; Tuần Dạ Nh&acirc;n đi theo, nếu l&agrave; hồng ảnh khi đ&oacute; ph&aacute;t động, c&oacute; lẽ vẫn l&agrave; chuyện tốt.<br />\r\n<br />\r\nSuy nghĩ lấp l&oacute;e, rất nhanh, L&yacute; Hạo đem bảng biểu điền ho&agrave;n tất.<br />\r\n<br />\r\n&quot;Đi kho trang bị nhận lấy trang bị đi!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nVương Kiệt thu hồi bảng biểu.<br />\r\n<br />\r\nL&yacute; Hạo kh&ocirc;ng c&oacute; ra ngo&agrave;i, chần chờ một ch&uacute;t, hay l&agrave; cẩn thận thử d&ograve; x&eacute;t n&oacute;i: &quot;Trưởng ph&ograve;ng, Trương Viễn bản &aacute;n, c&oacute; ch&uacute;t. . . C&oacute; ch&uacute;t quỷ dị, ng&agrave;i n&oacute;i, c&oacute; thể hay kh&ocirc;ng d&iacute;nh đến một ch&uacute;t kh&ocirc;ng tầm thường lực lượng? Ch&uacute;ng ta Tuần Kiểm ti thật c&oacute; thể giải quyết sao?&quot;<br />\r\n<br />\r\n&quot;Ừm?&quot;<br />\r\n<br />\r\nVương Kiệt cấp tốc nh&igrave;n về ph&iacute;a L&yacute; Hạo, &aacute;nh mắt hơi động một ch&uacute;t, cau m&agrave;y n&oacute;i: &quot;Ngươi nghe n&oacute;i c&aacute;i g&igrave; rồi?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng, kh&ocirc;ng c&oacute;.&quot;<br />\r\n<br />\r\nL&yacute; Hạo c&oacute; ch&uacute;t xấu hổ, giống như k&igrave;m n&eacute;n kh&ocirc;ng được đồng dạng, hay l&agrave; kh&ocirc; khốc n&oacute;i: &quot;Ta l&agrave; lo lắng đội chấp ph&aacute;p b&ecirc;n kia. . . Đ&aacute;nh cỏ động rắn, hoặc l&agrave; ra ch&uacute;t ngo&agrave;i &yacute; muốn, ta nghe n&oacute;i. . . Ta Tuần Kiểm ti. . . C&ograve;n giống như c&oacute; một cơ cấu kh&aacute;c?&quot;<br />\r\n<br />\r\n&quot;Tốt!&quot;<br />\r\n<br />\r\nVương Kiệt cau m&agrave;y n&oacute;i: &quot;N&oacute;i cẩn thận! Ta bi&ecirc;́t c&aacute;c ngươi c&oacute; lẽ nghe n&oacute;i qua một ch&uacute;t tiếng gi&oacute;, bất qu&aacute; đ&acirc;y kh&ocirc;ng phải ch&uacute;ng ta n&ecirc;n thảo luận sự t&igrave;nh! Hơn nữa c&ograve;n c&oacute; một ch&uacute;t, kh&ocirc;ng n&ecirc;n nghĩ những n&agrave;y, n&oacute;i thật, thật muốn đến t&igrave;nh trạng kia, phiền phức c&agrave;ng lớn! B&ecirc;n kia người tới, lần n&agrave;o kh&ocirc;ng phải phiền phức một đống!&quot;<br />\r\n<br />\r\nHắn kh&ocirc;ng qu&aacute; suy nghĩ nhiều n&oacute;i c&aacute;i g&igrave;, cũng kh&ocirc;ng muốn đi n&oacute;i Tuần Dạ Nh&acirc;n sự t&igrave;nh.<br />\r\n<br />\r\nNhững người n&agrave;y đến, kh&ocirc;ng phải chuyện tốt.<br />\r\n<br />\r\nMột khi li&ecirc;n lụy đến Tuần Dạ Nh&acirc;n, vậy sẽ rất phiền phức, m&agrave; lại đối phương đến, thường thường sẽ mang đến một ch&uacute;t đại phiền to&aacute;i.<br />\r\n<br />\r\nCho n&ecirc;n kh&ocirc;ng phải vạn bất đắc dĩ, hắn l&agrave; kh&ocirc;ng muốn c&ugrave;ng Tuần Dạ Nh&acirc;n b&ecirc;n kia c&oacute; bất kỳ li&ecirc;n lụy.<br />\r\n<br />\r\nĐội chấp ph&aacute;p b&ecirc;n kia, chỉ sợ cũng kh&ocirc;ng qu&aacute; nguyện &yacute; đối phương d&iacute;nh v&agrave;o.<br />\r\n<br />\r\nĐương nhi&ecirc;n, thật đến chưa biện ph&aacute;p thời điểm, Tuần Dạ Nh&acirc;n tự nhi&ecirc;n sẽ xuất hiện, khi đ&oacute; cũng kh&ocirc;ng phải l&agrave; Tuần Kiểm ti muốn hay kh&ocirc;ng đối phương xuất hiện.<br />\r\n<br />\r\nL&yacute; Hạo gật gật đầu, cuối c&ugrave;ng vẫn l&agrave; nhắc nhở một c&acirc;u: &quot;Trưởng ph&ograve;ng, ta quan s&aacute;t qua &aacute;n tự thi&ecirc;u ph&aacute;t sinh thời gian, mỗi lần đều l&agrave; ngày mưa d&acirc;̀m, Ng&acirc;n Th&agrave;nh rất nhanh lại phải tiến v&agrave;o m&ugrave;a mưa dầm, về khoảng c&aacute;ch lần vụ án phát sinh, cũng gần một năm , dựa theo dĩ v&atilde;ng điểm thời gian, ta ho&agrave;i nghi nếu như l&agrave; cố &yacute; hung s&aacute;t &aacute;n, khả năng n&agrave;y ngay tại gần nhất ph&aacute;t sinh!&quot;<br />\r\n<br />\r\nViệc n&agrave;y Vương Kiệt ngược lại l&agrave; chăm ch&uacute; ghi xuống.<br />\r\n<br />\r\nHắn vừa tiếp nhận vụ &aacute;n n&agrave;y, c&ograve;n kh&ocirc;ng c&oacute; kỹ c&agrave;ng hi&ecirc;̉u rõ, tự nhi&ecirc;n kh&ocirc;ng c&oacute; L&yacute; Hạo hiểu r&otilde; nhi&ecirc;̀u.<br />\r\n<br />\r\nNghe n&oacute;i lời ấy, Vương Kiệt trầm mặc một hồi, gật gật đầu: &quot;Ta sẽ c&ugrave;ng đội chấp ph&aacute;p b&ecirc;n kia n&oacute;i, ch&iacute;nh ngươi cũng cẩn thận một ch&uacute;t, đi thăm d&ograve; nh&igrave;n Trương Viễn ph&ograve;ng ốc thời điểm, cẩn thận một ch&uacute;t, c&acirc;̀n điều phối nh&acirc;n thủ hỗ trợ sao?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng cần!&quot;<br />\r\n<br />\r\nL&yacute; Hạo lắc đầu, kh&ocirc;ng phải m&igrave;nh muốn t&igrave;m chết, gặp hồng ảnh , người b&igrave;nh thường kh&ocirc;ng nh&igrave;n thấy, kh&ocirc;ng c&oacute; g&igrave; t&aacute;c dụng.<br />\r\n<br />\r\nKh&ocirc;ng gặp được hồng ảnh m&agrave; n&oacute;i, ch&iacute;nh m&igrave;nh mang theo thương, c&ograve;n c&oacute; một số những an b&agrave;i kh&aacute;c, vậy vấn đề liền sẽ kh&ocirc;ng qu&aacute; lớn.<br />\r\n<br />\r\nHuống chi, ph&ograve;ng cơ yếu b&ecirc;n n&agrave;y, đại bộ phận đều l&agrave; văn chức, thật gặp phải phiền to&aacute;i, ai bảo vệ ai hay l&agrave; c&aacute;i vấn đề.<br />\r\n<br />\r\n&quot;Vậy m&igrave;nh cẩn thận một ch&uacute;t, mặc d&ugrave; người đ&atilde; chết một năm, ta cảm thấy cũng kh&ocirc;ng c&oacute; g&igrave; nguy hiểm.&quot;<br />\r\n<br />\r\nVương Kiệt kho&aacute;t kho&aacute;t tay, đuổi L&yacute; Hạo rời đi.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nL&yacute; Hạo rất đi mau ra ph&ograve;ng l&agrave;m việc, c&oacute; ch&uacute;t nhẹ nh&agrave;ng thở ra.<br />\r\n<br />\r\nVương Kiệt hay l&agrave; rất chiếu cố.<br />\r\n<br />\r\nCất bước hướng kho trang bị đi đến, cầm Vương Kiệt ph&ecirc; văn, rất nhanh, L&yacute; Hạo nhận lấy đến một chi m&agrave;u đen đặc s&uacute;ng ngắn, tr&agrave;n đầy kim loại cảm nhận.<br />\r\n<br />\r\nV&ograve;ng Xo&aacute;y đời thứ ba, tầm s&aacute;t thương 50 m, vượt qua 50 m ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; độ ch&iacute;nh x&aacute;c c&oacute; thể n&oacute;i.<br />\r\n<br />\r\nNghe n&oacute;i c&oacute; ch&uacute;t th&agrave;nh lớn, đ&atilde; bắt đầu mở rộng V&ograve;ng Xo&aacute;y đời bốn, tầm s&aacute;t thương có th&ecirc;̉ đạt tới 100 m&eacute;t, đương nhi&ecirc;n, Ng&acirc;n Th&agrave;nh kh&ocirc;ng t&iacute;nh lớn, c&ograve;n kh&ocirc;ng c&oacute; phổ cập tới, hiện tại bao qu&aacute;t đội chấp ph&aacute;p tuyến 1, cũng l&agrave; sử dụng V&ograve;ng Xo&aacute;y đời thứ ba.<br />\r\n<br />\r\nPhối ph&aacute;t đạn 18 ph&aacute;t, mỗi lần nh&eacute;t v&agrave;o 6 ph&aacute;t, c&oacute; thể li&ecirc;n ph&aacute;t, xem như tương đương c&oacute; lực uy hiếp vũ kh&iacute;.<br />\r\n<br />\r\nL&yacute; Hạo tiến v&agrave;o Tuần Kiểm ti trước đ&oacute;, mặc d&ugrave; tiến v&agrave;o l&agrave; ph&ograve;ng cơ yếu, bất qu&aacute; cũng l&acirc;m thời học bổ t&uacute;c qua một th&aacute;ng, xạ k&iacute;ch cũng l&agrave; trong huấn luyện cho một trong, c&ograve;n kh&ocirc;ng đến mức lấy được kh&ocirc;ng biết d&ugrave;ng.<br />\r\n<br />\r\nChỉ l&agrave; b&igrave;nh thường một mực đợi tại ph&ograve;ng cơ yếu, thật l&acirc;u kh&ocirc;ng d&ugrave;ng qua.<br />\r\n<br />\r\nTr&ecirc;n th&acirc;n đeo vũ kh&iacute;, L&yacute; Hạo lực lượng lại đủ một ch&uacute;t.<br />\r\n<br />\r\nCũng kh&ocirc;ng biết c&oacute; thể hay kh&ocirc;ng tổn thương đến hồng ảnh, đồ chơi kia người kh&aacute;c kh&ocirc;ng nh&igrave;n thấy, m&igrave;nh c&oacute; thể nh&igrave;n thấy, nếu l&agrave; hướng đối phương xạ k&iacute;ch, sẽ hữu hiệu quả sao?<br />\r\n<br />\r\n&quot;Nh&igrave;n Vương trưởng ph&ograve;ng &yacute; tứ, hiện tại đội chấp ph&aacute;p &yacute; nghĩ, chỉ sợ kh&ocirc;ng qu&aacute; nguyện &yacute; th&ocirc;ng tri Tuần Dạ Nh&acirc;n nh&uacute;ng tay. C&oacute; thể thời gian đ&atilde; rất khẩn cấp, Tuần Dạ Nh&acirc;n kh&ocirc;ng nh&uacute;ng tay v&agrave;o m&agrave; n&oacute;i, bọn hắn c&oacute; thể đối ph&oacute; hồng ảnh sao?&quot;<br />\r\n<br />\r\n&quot;Nếu như ta b&acirc;y giờ n&oacute;i ta c&oacute; thể nh&igrave;n thấy hồng ảnh. . .&quot;<br />\r\n<br />\r\n&Yacute; nghĩ n&agrave;y, tại L&yacute; Hạo trong đầu lấp l&oacute;e.<br />\r\n<br />\r\nNhưng hắn lại kh&ocirc;ng khỏi nhớ tới trước đ&oacute; hồ sơ vụ &aacute;n, một bộ phận c&oacute; thể nh&igrave;n thấy hồng ảnh gia hỏa, cuối c&ugrave;ng cũng bị mất &acirc;m thanh.<br />\r\n<br />\r\nChết rồi, hay l&agrave; như thế n&agrave;o?<br />\r\n<br />\r\nNh&igrave;n thấy hồng ảnh, dạng n&agrave;y vụ &aacute;n, Tuần Dạ Nh&acirc;n khả năng nh&uacute;ng tay, kết quả những người kia lại l&agrave; đều mất t&iacute;ch hoặc l&agrave; tử vong.<br />\r\n<br />\r\nL&yacute; Hạo kh&ocirc;ng biết, những người n&agrave;y l&agrave; chết, vẫn l&agrave; bị hấp thu v&agrave;o Tuần Dạ Nh&acirc;n.<br />\r\n<br />\r\nDạng n&agrave;y sự kh&ocirc;ng chắc chắn, để L&yacute; Hạo c&oacute; ch&uacute;t gi&atilde;y dụa.<br />\r\n<br />\r\nNếu như Tuần Dạ Nh&acirc;n c&oacute; thể giải quyết hồng ảnh, nguyện &yacute; giải quyết, vậy m&igrave;nh bại lộ c&ograve;n c&oacute; gi&aacute; trị, sợ l&agrave; sợ. . . Tuần Dạ Nh&acirc;n c&ugrave;ng hồng ảnh ở giữa, đến c&ugrave;ng c&oacute; c&aacute;i g&igrave; cấu kết?<br />\r\n<br />\r\nNếu l&agrave; m&igrave;nh sớm bại lộ, Tuần Dạ Nh&acirc;n đến c&ugrave;ng l&agrave; sẽ đi giải quyết hồng ảnh, hay l&agrave; giải quyết ch&iacute;nh m&igrave;nh?<br />\r\n<br />\r\n&quot;Lực lượng thần b&iacute;. . . Tuần Dạ Nh&acirc;n c&oacute; lẽ l&agrave; như vậy, hồng ảnh cũng hẳn l&agrave;, song phương l&agrave; c&ugrave;ng một bọn, hay l&agrave; đối địch?&quot;<br />\r\n<br />\r\nL&yacute; Hạo kh&ocirc;ng c&aacute;ch n&agrave;o ph&aacute;n đo&aacute;n, hắn thiếu khuyết hi&ecirc;̉u rõ những tin tức n&agrave;y con đường.<br />\r\n<br />\r\nNếu l&agrave; cuối c&ugrave;ng hai b&ecirc;n l&agrave; c&ugrave;ng một bọn, những người ph&aacute;t hiện hồng ảnh kia đều bị giết người diệt khẩu, đ&acirc;y mới l&agrave; L&yacute; Hạo lo lắng nhất kết quả.<br />\r\n<br />\r\nĐau đầu!<br />\r\n<br />\r\nGiờ khắc n&agrave;y, L&yacute; Hạo rất xoắn xu&yacute;t.<br />\r\n<br />\r\nHắn kh&aacute;t vọng Tuần Dạ Nh&acirc;n xuất thủ giải quyết hồng ảnh, v&igrave; Trương Viễn b&aacute;o th&ugrave;, b&aacute;o th&ugrave; cho cha mẹ, lại lo lắng cuối c&ugrave;ng đi ngược lại, ngược lại đem ch&iacute;nh m&igrave;nh ph&aacute; tan lộ, bị Tuần Dạ Nh&acirc;n cho diệt khẩu.<br />\r\n<br />\r\n&quot;Đợi th&ecirc;m mấy ng&agrave;y đi. . .&quot;<br />\r\n<br />\r\nL&yacute; Hạo biết m&igrave;nh rất nguy hiểm, có th&ecirc;̉ d&ugrave; l&agrave; như vậy, hắn cũng t&igrave;nh nguyện đợi th&ecirc;m mấy ng&agrave;y , chờ đội chấp ph&aacute;p b&ecirc;n kia kết quả c&ugrave;ng th&aacute;i độ.<br />\r\n<br />\r\nM&igrave;nh b&acirc;y giờ, chỉ l&agrave; trong l&uacute;c v&ocirc; t&igrave;nh ph&aacute;t hiện &aacute;n tự thi&ecirc;u dị thường, tin tưởng người đội chấp ph&aacute;p chỉ cần chăm ch&uacute;, rất nhanh cũng sẽ ph&aacute;t hiện.<br />\r\n<br />\r\nKhi đ&oacute;, nếu l&agrave; đội chấp ph&aacute;p c&ograve;n c&oacute; người kh&ocirc;n kh&eacute;o, L&yacute; Hạo cảm thấy, đội chấp ph&aacute;p rất c&oacute; thể sẽ sớm li&ecirc;n hệ Tuần Dạ Nh&acirc;n.<br />\r\n<br />\r\nNgay tại vừa mới, L&yacute; Hạo lại để lộ ra, &aacute;n tự thi&ecirc;u rất c&oacute; thể sẽ tại mấy ng&agrave;y gần đ&acirc;y nhất ph&aacute;t sinh, ch&iacute;nh l&agrave; v&igrave; để đội chấp ph&aacute;p cảm nhận được bức thiết, nếu l&agrave; th&ocirc;ng qua đội chấp ph&aacute;p đi dẫn xuất Tuần Dạ Nh&acirc;n, vậy L&yacute; Hạo liền kh&ocirc;ng cần bại lộ c&oacute; thể nh&igrave;n thấy hồng ảnh chuyện.<br />\r\n<br />\r\n&quot;M&agrave; lại, l&atilde;o sư rất nhanh sẽ dẫn đội ra ngo&agrave;i khảo s&aacute;t, kể từ đ&oacute;, Tuần Dạ Nh&acirc;n vốn l&agrave; muốn xuất hiện, mặc kệ Ng&acirc;n Th&agrave;nh c&oacute; hay kh&ocirc;ng Tuần Dạ Nh&acirc;n tồn tại, mấy ng&agrave;y gần đ&acirc;y nhất, hẳn l&agrave; đều sẽ c&oacute; Tuần Dạ Nh&acirc;n chạy tới.&quot;<br />\r\n<br />\r\nĐem sự t&igrave;nh đều cho gỡ một lần, vững tin Tuần Dạ Nh&acirc;n x&aacute;c suất lớn sẽ xuất hiện, L&yacute; Hạo tạm thời bu&ocirc;ng xuống chủ động bại lộ &yacute; nghĩ.<br />\r\n<br />\r\nĐối phương c&oacute; phải hay kh&ocirc;ng c&ugrave;ng một bọn, c&oacute; lẽ rất nhanh liền c&oacute; thể biết được.<br />\r\n<br />\r\nNgày mưa d&acirc;̀m đến trước đ&oacute;, nếu như Tuần Dạ Nh&acirc;n c&ograve;n kh&ocirc;ng c&oacute; xuất hiện, kh&ocirc;ng c&oacute; chủ động t&igrave;m ch&iacute;nh m&igrave;nh n&oacute;i chuyện t&igrave;m hiểu t&igrave;nh h&igrave;nh. . . Vậy m&igrave;nh liền phải nghĩ biện ph&aacute;p chủ động bại lộ.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nĐi ra Tuần Kiểm ti.<br />\r\n<br />\r\nLiệt nhật đ&atilde; xuống n&uacute;i, bầu trời c&oacute; ch&uacute;t phiếm hồng, r&aacute;ng chiều hiển hiện.<br />\r\n<br />\r\nB&ecirc;n ngo&agrave;i người qua đường kh&ocirc;ng nhiều, thời tiết oi bức, gần nhất thậm ch&iacute; đến tối t&aacute;m ch&iacute;n điểm c&ograve;n cực kỳ oi bức, đi ra đ&iacute;ch x&aacute;c rất &iacute;t người.<br />\r\n<br />\r\nL&yacute; Hạo nh&agrave;, khoảng c&aacute;ch Tuần Kiểm ti kh&ocirc;ng t&iacute;nh qu&aacute; xa, bảy, t&aacute;m c&acirc;y số.<br />\r\n<br />\r\nLấy ra xe đạp của m&igrave;nh, L&yacute; Hạo nhảy l&ecirc;n xe đạp, một năm n&agrave;y hắn đều l&agrave; cưỡi xe đi l&agrave;m.<br />\r\n<br />\r\nMột mặt l&agrave; v&igrave; r&egrave;n luyện th&acirc;n thể, một phương diện cũng l&agrave; v&igrave; tr&aacute;nh đi một ch&uacute;t nguy hiểm.<br />\r\n<br />\r\nĐịa phương nhiều người, ngược lại kh&ocirc;ng dễ d&agrave;ng ph&aacute;t gi&aacute;c được nguy hiểm.<br />\r\n<br />\r\nTrương Viễn sau khi chết, L&yacute; Hạo vẫn lu&ocirc;n rất cẩn thận.<br />\r\n<br />\r\nCưỡi l&ecirc;n xe, L&yacute; Hạo giống như v&ocirc; &yacute;, nh&igrave;n chung quanh một lần, giống như đang nh&igrave;n đường x&aacute;, đang ngắm phong cảnh, tr&ecirc;n thực tế l&agrave; muốn nh&igrave;n một ch&uacute;t, c&oacute; người theo d&otilde;i m&igrave;nh hay kh&ocirc;ng.<br />\r\n<br />\r\nNhất l&agrave; h&ocirc;m nay, x&aacute;c định hồng ảnh mục ti&ecirc;u kế tiếp ch&iacute;nh l&agrave; m&igrave;nh.<br />\r\n<br />\r\nL&yacute; Hạo ngay tại suy nghĩ, hồng ảnh l&agrave; như thế n&agrave;o định vị bọn hắn những người n&agrave;y?<br />\r\n<br />\r\nDựa theo L&yacute; Hạo ph&aacute;n đo&aacute;n, hồng ảnh xuất hiện thời gian sẽ kh&ocirc;ng qu&aacute; d&agrave;i, mỗi lần xuất hiện đều sẽ rất nhanh biến mất, ở b&ecirc;n ngo&agrave;i dừng lại thời gian rất ngắn, đ&atilde; như vậy, l&agrave;m sao c&oacute; thể tinh chuẩn định vị đến bọn hắn?<br />\r\n<br />\r\nVậy c&oacute; phải đại biểu cho, kỳ thật c&ograve;n c&oacute; người trong b&oacute;ng tối sớm quan s&aacute;t, chỉ l&agrave; chờ đợi hồng ảnh đến thu hoạch giết người?<br />\r\n<br />\r\nLoại khả năng n&agrave;y, kh&ocirc;ng phải l&agrave; kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nCứ việc kh&ocirc;ng phải chuy&ecirc;n nghiệp tuần kiểm xuất th&acirc;n, L&yacute; Hạo hay l&agrave; c&oacute; một ch&uacute;t cơ bản phản trinh s&aacute;t năng lực.<br />\r\n<br />\r\n&quot;Tạm thời kh&ocirc;ng c&oacute; ph&aacute;t hiện c&aacute;i g&igrave;, chẳng lẽ hồng ảnh c&oacute; thể trực tiếp định vị ch&uacute;ng ta? C&ugrave;ng tr&ecirc;n người ta Tinh Kh&ocirc;ng Kiếm c&oacute; quan hệ sao? Th&ocirc;ng qua những n&agrave;y truyền thừa xuống bảo vật, có th&ecirc;̉ truy tung đến ch&uacute;ng ta?&quot;<br />\r\n<br />\r\nXe đạp chậm r&atilde;i di động tới, V&ograve;ng Xo&aacute;y đời thứ ba bị L&yacute; Hạo đặt ở trong &aacute;o tr&ecirc;n t&uacute;i, n&uacute;t thắt giải khai, nh&igrave;n như h&oacute;ng gi&oacute;, tr&ecirc;n thực tế l&agrave; v&igrave; bằng tốc độ nhanh nhất lấy s&uacute;ng xạ k&iacute;ch.<br />\r\n<br />\r\nHo&agrave;n to&agrave;n như trước đ&acirc;y, L&yacute; Hạo cũng kh&ocirc;ng ph&aacute;t hiện vấn đề g&igrave;.<br />\r\n<br />\r\nNhưng khi L&yacute; Hạo cưỡi l&ecirc;n một đoạn đường xuống dốc, xe đạp nhanh ch&oacute;ng trượt trong nh&aacute;y mắt, L&yacute; Hạo trong l&ograve;ng c&oacute; ch&uacute;t lộp bộp nhảy một c&aacute;i.<br />\r\n<br />\r\nTr&ecirc;n xe đạp nhanh ch&oacute;ng trượt, L&yacute; Hạo dư quang tho&aacute;ng nh&igrave;n, đối diện tr&ecirc;n làn xe, đối diện chạy tới một cỗ xe nhỏ m&agrave;u đen, rất phổ th&ocirc;ng, rất b&igrave;nh thường, c&ugrave;ng lui tới xe cộ giống như kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c biệt.<br />\r\n<br />\r\n&quot;Ng&acirc;n 7219!&quot;<br />\r\n<br />\r\nL&yacute; Hạo chấn động trong l&ograve;ng, kh&ocirc;ng đ&uacute;ng.<br />\r\n<br />\r\nXe n&agrave;y hắn vừa mới thấy qua, mặc d&ugrave; đầy đường loại xe n&agrave;y, chỉ l&agrave; cơ sở nhất xe thay đi bộ, mười chiếc c&oacute; chín chiếc đều l&agrave; loại n&agrave;y, thế nhưng l&agrave;. . . L&yacute; Hạo nhớ kỹ biển số xe n&agrave;y!<br />\r\n<br />\r\nCó th&ecirc;̉ thi v&agrave;o Ng&acirc;n Th&agrave;nh cổ viện L&yacute; Hạo, tự nhi&ecirc;n cũng c&oacute; n&oacute; chỗ hơn người.<br />\r\n<br />\r\nK&yacute; ức tốt, đ&acirc;y cơ hồ l&agrave; thiết yếu.<br />\r\n<br />\r\nNếu như kh&ocirc;ng c&oacute; một ch&uacute;t ưu điểm, hắn cũng v&ocirc; ph&aacute;p bị Vi&ecirc;n Thạc vị gi&aacute;o sư gi&agrave; kia nh&igrave;n tr&uacute;ng, rất mau tiến v&agrave;o Vi&ecirc;n Thạc nh&atilde;n tuyến, trở th&agrave;nh học vi&ecirc;n của hắn.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Kh&ocirc;ng th&iacute;ch hợp, xe n&agrave;y vừa mới l&agrave; c&ugrave;ng ta c&ugrave;ng phương hướng, ta cưỡi xe chậm, chiếc xe n&agrave;y đ&atilde; vượt qua ta l&aacute;i đi, đại kh&aacute;i rời đi 7 ph&uacute;t đồng hồ!&quot;<br />\r\n<br />\r\n7 ph&uacute;t đồng hồ , dựa theo xe n&agrave;y tốc độ, một mực hướng ph&iacute;a trước mở, ph&iacute;a trước đều l&agrave; một đầu trực đạo, kh&ocirc;ng c&oacute; c&aacute;i g&igrave; th&iacute;ch hợp điểm dừng xe.<br />\r\n<br />\r\nNếu v&igrave; l&agrave;m việc, đưa người, đều kh&ocirc;ng c&oacute; nhanh như vậy l&aacute;i về.<br />\r\n<br />\r\nB&acirc;y giờ đối phương l&aacute;i về, c&ugrave;ng L&yacute; Hạo đối diện, n&oacute;i r&otilde; xe n&agrave;y trước đ&oacute; rời đi L&yacute; Hạo kh&ocirc;ng bao l&acirc;u, liền quay đầu chạy nhanh trở về.<br />\r\n<br />\r\nL&yacute; Hạo vẫn như cũ mặt kh&ocirc;ng đổi sắc, nhưng trong l&ograve;ng th&igrave; chấn động.<br />\r\n<br />\r\n&quot;Trước đ&oacute; chưa từng c&oacute;, h&ocirc;m nay l&agrave; lần thứ nhất. . . Thật sự c&oacute; người đang theo d&otilde;i ta?&quot;<br />\r\n<br />\r\n&quot;V&igrave; c&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\nRất nhanh, L&yacute; Hạo nghĩ đến một c&aacute;i khả năng, hắn c&ugrave;ng Vương Kiệt hồi b&aacute;o bản &aacute;n. . . Khả năng. . . Tiết lộ!<br />\r\n<br />\r\n&quot;Tuần Kiểm ti c&oacute; nội ứng!&quot;<br />\r\n<br />\r\n&quot;Rất c&oacute; thể ngay tại đội chấp ph&aacute;p!&quot;<br />\r\n<br />\r\nĐ&acirc;y l&agrave; L&yacute; Hạo &yacute; nghĩ đầu ti&ecirc;n, trong l&ograve;ng kh&ocirc;ng khỏi trở n&ecirc;n nặng nề, cứ việc đối mới c&oacute; thể c&oacute; thể l&agrave; tr&ugrave;ng hợp, thật chỉ l&agrave; quay đầu chạy, nhưng m&agrave; L&yacute; Hạo kh&ocirc;ng d&aacute;m nhận th&agrave;nh tr&ugrave;ng hợp đến xem.<br />\r\n<br />\r\nBất luận c&aacute;i g&igrave; tr&ugrave;ng hợp, đều muốn xem như c&oacute; chuẩn bị m&agrave; đến.<br />\r\n<br />\r\n&quot;Vương trưởng ph&ograve;ng đi đội chấp ph&aacute;p, n&oacute;i cho bọn hắn &aacute;n tự thi&ecirc;u, nếu như đội chấp ph&aacute;p b&ecirc;n kia c&oacute; người biết l&agrave; ta đưa ra lật lại bản &aacute;n, vậy liền khả năng để mắt tới ta! Đội chấp ph&aacute;p nếu l&agrave; c&oacute; vấn đề, c&oacute; thể trực tiếp hỏi ta, kh&ocirc;ng cần thiết theo d&otilde;i ta, bởi v&igrave; ta tr&ecirc;n mặt nổi chỉ l&agrave; bởi v&igrave; đồng học bản &aacute;n cho n&ecirc;n đặc biệt ch&uacute; &yacute; một ch&uacute;t. . .&quot;<br />\r\n<br />\r\n&quot;C&ograve;n c&oacute; trước đ&oacute;, mỗi người b&aacute;o &aacute;n n&oacute;i m&igrave;nh nh&igrave;n thấy hồng ảnh, chẳng mấy chốc sẽ bị ph&aacute;t hiện. . . Kỳ thật ta đ&atilde; sớm cảm thấy Tuần Kiểm ti c&oacute; hồng ảnh hoặc l&agrave; Tuần Dạ Nh&acirc;n &aacute;m tuyến!&quot;<br />\r\n<br />\r\nL&yacute; Hạo nh&igrave;n qua những hồ sơ vụ &aacute;n kia, người b&aacute;o &aacute;n nh&igrave;n thấy hồng ảnh, rất nhanh liền chết rồi, hoặc l&agrave; biến mất.<br />\r\n<br />\r\nĐương nhi&ecirc;n, kh&ocirc;ng phải cố &yacute; ch&uacute; &yacute;, kỳ thật rất kh&oacute; ph&aacute;t hiện.<br />\r\n<br />\r\nRất nhiều tuần kiểm nhận được loại &aacute;n n&agrave;y, cũng chỉ l&agrave; qua loa cho xong, t&ugrave;y tiện ghi ch&eacute;p một ch&uacute;t, ngay cả thăm đ&aacute;p lễ cũng sẽ kh&ocirc;ng c&oacute;, bởi v&igrave; c&oacute; ch&uacute;t lời n&oacute;i v&ocirc; căn cứ.<br />\r\n<br />\r\nCó th&ecirc;̉ hết lần n&agrave;y tới lần kh&aacute;c, c&oacute; người ch&iacute;nh l&agrave; ch&uacute; &yacute; đến.<br />\r\n<br />\r\nĐiều n&agrave;y n&oacute;i r&otilde; c&aacute;i g&igrave;?<br />\r\n<br />\r\nN&oacute;i r&otilde; trong Tuần Kiểm ti, nhất định c&oacute; người đặc biệt lưu &yacute; điểm n&agrave;y, ph&agrave;m l&agrave; c&oacute; người b&aacute;o &aacute;n, rất nhanh sẽ truyền đến mặt kh&aacute;c một nh&oacute;m người trong tai.<br />\r\n<br />\r\n&quot;Hiện tại ta bị để mắt tới sao?&quot;<br />\r\n<br />\r\n&quot;L&agrave; bởi v&igrave; ta đưa ra &aacute;n tự thi&ecirc;u, những người n&agrave;y muốn x&aacute;c định, ta l&agrave; thấy được hồng ảnh hay l&agrave; trong v&ocirc; &yacute; ph&aacute;t hiện sao?&quot;<br />\r\n<br />\r\n&quot;Năm ngo&aacute;i Tiểu Viễn thời điểm chết, ta kh&ocirc;ng d&aacute;m n&oacute;i thấy được hồng ảnh, những người n&agrave;y nếu như giải m&agrave; n&oacute;i, x&aacute;c suất lớn sẽ cảm thấy ta l&agrave; ph&aacute;t hiện ngo&agrave;i &yacute; muốn mới đ&uacute;ng, m&agrave; kh&ocirc;ng phải bởi v&igrave; ta có th&ecirc;̉ nh&igrave;n thấy hồng ảnh, nếu kh&ocirc;ng năm ngo&aacute;i ta liền n&ecirc;n b&aacute;o &aacute;n!&quot;<br />\r\n<br />\r\nTrong t&iacute;ch tắc, L&yacute; Hạo nghĩ đến rất nhiều, ph&acirc;n t&iacute;ch rất nhiều.<br />\r\n<br />\r\nHắn hiện tại tốt xấu l&agrave; một vị tuần kiểm cấp ba, hay l&agrave; vừa mới ph&aacute;t hiện &aacute;n tự thi&ecirc;u c&oacute; vấn đề tuần kiểm, L&yacute; Hạo suy đo&aacute;n, cho d&ugrave; c&oacute; người c&ugrave;ng hồng ảnh cấu kết, cũng sẽ kh&ocirc;ng l&uacute;c n&agrave;y đối ph&oacute; ch&iacute;nh m&igrave;nh, giết ch&iacute;nh m&igrave;nh, vậy ngược lại ngồi vững &aacute;n tự thi&ecirc;u c&oacute; vấn đề, thậm ch&iacute; sẽ khiến phiền to&aacute;i c&agrave;ng lớn.<br />\r\n<br />\r\n&quot;C&agrave;ng ng&agrave;y c&agrave;ng c&oacute; &yacute; tứ!&quot;<br />\r\n<br />\r\nL&yacute; Hạo trong l&ograve;ng hừ lạnh một tiếng, tr&ecirc;n mặt vẫn như cũ kh&ocirc;ng hiển lộ mảy may, giống như kh&ocirc;ng c&oacute; ph&aacute;t hiện bất cứ dị thường n&agrave;o, tiếp tục kỵ h&agrave;nh.<br />\r\n<br />\r\nM&agrave; chiếc kia bị hắn để mắt tới xe nhỏ, cũng rất nhanh biến mất.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nNg&acirc;n 7219.<br />\r\n<br />\r\nVừa mới đối diện hướng L&yacute; Hạo chạy tr&ecirc;n xe nhỏ, giờ ph&uacute;t n&agrave;y, trừ một vị trung ni&ecirc;n nam t&iacute;nh l&aacute;i xe, ngồi kế b&ecirc;n t&agrave;i xế c&ograve;n c&oacute; một vị nh&igrave;n phổ th&ocirc;ng trung ni&ecirc;n phụ nh&acirc;n.<br />\r\n<br />\r\nNhư l&agrave; b&igrave;nh thường vợ chồng trung ni&ecirc;n, kh&ocirc;ng c&oacute; bất kỳ c&aacute;i g&igrave; chỗ đặc th&ugrave;.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; ph&aacute;t hiện c&aacute;i g&igrave;.&quot;<br />\r\n<br />\r\nNhanh ch&oacute;ng c&aacute;ch rời L&yacute; Hạo, trong xe trầm mặc một hồi, phụ nh&acirc;n thấp giọng n&oacute;i một c&acirc;u.<br />\r\n<br />\r\nT&agrave;i xế l&aacute;i xe, giống như kh&ocirc;ng nghe thấy.<br />\r\n<br />\r\nMột l&aacute;t sau, l&aacute;i xe mới ngữ kh&iacute; b&igrave;nh thản n&oacute;i: &quot;Hẳn l&agrave; chỉ l&agrave; ngo&agrave;i &yacute; muốn, hắn hẳn l&agrave; bởi v&igrave; c&aacute;i kia Trương Viễn chết, cho n&ecirc;n một mực ch&uacute; &yacute;, Trương Viễn c&ugrave;ng hắn l&agrave; bạn tốt.&quot;<br />\r\n<br />\r\nPhụ nh&acirc;n khẽ gật đầu.<br />\r\n<br />\r\nTrong xe lần nữa kh&ocirc;i phục trầm mặc.<br />\r\n<br />\r\nLại qua một trận, l&aacute;i xe bỗng nhi&ecirc;n n&oacute;i: &quot;Kh&ocirc;ng b&agrave;i trừ c&oacute; những khả năng kh&aacute;c, trước tiếp tục nh&igrave;n chằm chằm, xa xa nh&igrave;n chằm chằm l&agrave; được, gần nhất cẩn thận một ch&uacute;t, những gia hỏa đ&aacute;ng gh&eacute;t kia c&oacute; thể muốn đến Ng&acirc;n Th&agrave;nh.&quot;<br />\r\n<br />\r\n&quot;Minh bạch!&quot;<br />\r\n<br />\r\nPhụ nh&acirc;n lần nữa gật đầu, n&agrave;ng biết l&aacute;i xe n&oacute;i tới ai, những t&ecirc;n kia ho&agrave;n to&agrave;n ch&iacute;nh x&aacute;c rất đ&aacute;ng gh&eacute;t.<br />\r\n<br />\r\nTrong xe triệt để y&ecirc;n tĩnh lại.</p>', '2021-11-27 21:30:04', 12, 1),
(190, 51, 'Vương Trường Sinh', '1', '<p>Đại Tống, Ninh ch&acirc;u.<br />\r\n<br />\r\nB&igrave;nh An huyện l&agrave; Ninh ch&acirc;u bảy mươi hai huyện một trong, cảnh nội phần lớn l&agrave; s&ocirc;ng n&uacute;i đồi n&uacute;i, nh&acirc;n khẩu thưa thớt, Li&ecirc;n Hoa đảo l&agrave; B&igrave;nh An huyện nội duy nhất nhất t&ograve;a hồ đảo.<br />\r\n<br />\r\nLi&ecirc;n Hoa đảo bởi v&igrave; ngoại h&igrave;nh cực gi&ocirc;́ng một đ&oacute;a nở rộ hoa sen m&agrave; gọi t&ecirc;n, ở tr&ecirc;n đảo trồng kh&ocirc;ng &iacute;t hoa sen.<br />\r\n<br />\r\nS&aacute;ng sớm, một v&ograve;ng ki&ecirc;u dương từ Đ&ocirc;ng Phương d&acirc;ng l&ecirc;n, chiếu rọi ph&iacute;a tr&ecirc;n Li&ecirc;n Hoa đảo, cấp Li&ecirc;n Hoa đảo mang đến một tia ấm &aacute;p.<br />\r\n<br />\r\nTại h&ograve;n đảo trung t&acirc;m, c&oacute; nhất t&ograve;a chiếm diện t&iacute;ch cực lớn viện tử, đ&igrave;nh đ&agrave;i lầu c&aacute;c, vườn hoa h&agrave;nh lang chỗ n&agrave;o cũng c&oacute;.<br />\r\n<br />\r\nTrong s&acirc;n, c&oacute; một c&aacute;i nh&acirc;n c&ocirc;ng đ&agrave;o đục hồ nhỏ, một mảng lớn m&agrave;u trắng nồng vụ bao lại to&agrave;n bộ hồ nước, thấy kh&ocirc;ng r&otilde; lắm t&igrave;nh h&igrave;nh b&ecirc;n trong.<br />\r\n<br />\r\nTheo thời gian tr&ocirc;i qua, bạch vụ dần dần t&aacute;n đi.<br />\r\n<br />\r\nTrong hồ mọc ra một mảng lớn thanh sắc hoa sen, trung ương đứng vững nhất t&ograve;a h&igrave;nh hoa sen trạng bệ đ&aacute;, tr&ecirc;n bệ đ&aacute; c&oacute; nhất t&ograve;a hai tầng cao thanh sắc lầu c&aacute;c, tại lầu c&aacute;c lối v&agrave;o ph&iacute;a tr&ecirc;n, treo nhất khối d&agrave;i hơn một trượng thanh sắc bảng hiệu, rồng bay phượng m&uacute;a viết &quot;Thanh Li&ecirc;n c&aacute;c&quot; tam c&aacute;i chữ to m&agrave;u v&agrave;ng.<br />\r\n<br />\r\nThanh Li&ecirc;n c&aacute;c lầu hai gian n&agrave;o đ&oacute; gian ph&ograve;ng b&ecirc;n trong, một t&ecirc;n mười t&aacute;m mười ch&iacute;n tuổi thanh ni&ecirc;n &aacute;o lam xếp bằng ở nhất khối thanh sắc bồ đo&agrave;n b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nThanh ni&ecirc;n &aacute;o lam song mi như kiếm, mi thanh mục t&uacute;, hắc ph&aacute;t &aacute;o cho&agrave;ng, cho người ta một loại cảm gi&aacute;c rất thoải m&aacute;i.<br />\r\n<br />\r\nThanh ni&ecirc;n &aacute;o lam kh&eacute;p hờ lấy đ&ocirc;i mắt, trong mũi h&ocirc; hấp b&agrave;y biện ra một loại n&agrave;o đ&oacute; cảm gi&aacute;c tiết tấu, theo th&ocirc;n hấp thổ nạp, chung quanh c&oacute; điểm điểm lam quang hiển hiện, Nhất tầng nh&agrave;n nhạt lam quang bao phủ th&acirc;n thể của hắn.<br />\r\n<br />\r\nThời gian từng giờ tr&ocirc;i qua, lam quang điểm s&aacute;ng nhao nhao tr&agrave;n v&agrave;o trong cơ thể của hắn.<br />\r\n<br />\r\nThanh ni&ecirc;n &aacute;o lam mở hai mắt ra, hai mắt bắn ra một v&ograve;ng tinh quang.<br />\r\n<br />\r\n&quot;Rốt cục tiến v&agrave;o Luyện kh&iacute; Ngũ tầng.&quot;<br />\r\n<br />\r\nVương Trường Sinh nhẹ thở ra một hơi, gầy g&ograve; gương mặt lộ ra một v&ograve;ng &yacute; cười.<br />\r\n<br />\r\n&quot;Cửu th&uacute;c c&ocirc;ng, điểm t&acirc;m l&agrave;m xong.&quot;<br />\r\n<br />\r\nMột đạo cung k&iacute;nh thanh &acirc;m nam tử từ b&ecirc;n ngo&agrave;i truyền đến.<br />\r\n<br />\r\nVương Trường Sinh cười nhẹ, n&oacute;i: &quot;Thu Sinh, đem điểm t&acirc;m bắt đầu v&agrave;o tới đi!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nVương Trường Sinh đẩy cửa đi ra ngo&agrave;i, đi xuống lầu dưới.<br />\r\n<br />\r\nMột t&ecirc;n hơn hai mươi tuổi thanh ni&ecirc;n &aacute;o lam đẩy cửa đi đến, trong tay cầm nhất c&aacute;i thanh sắc khay.<br />\r\n<br />\r\nKhay b&agrave;y biện một b&aacute;t cơm trắng, một b&agrave;n thanh đồ ăn c&ugrave;ng một đĩa đậu hũ, m&ugrave;i thơm nức mũi, nhường nh&acirc;n nghe chi muốn ăn mở rộng.<br />\r\n<br />\r\n&quot;Cửu th&uacute;c c&ocirc;ng, đ&acirc;y l&agrave; Thu Nguyệt v&igrave; ng&agrave;i l&agrave;m điểm t&acirc;m, ng&agrave;i nh&igrave;n xem c&oacute; hợp hay kh&ocirc;ng khẩu vị.&quot;<br />\r\n<br />\r\nVương Thu Sinh đem thức ăn dọn xong, đứng ở một b&ecirc;n, thần sắc cung k&iacute;nh dị thường.<br />\r\n<br />\r\nVương Trường Sinh nh&iacute;u m&agrave;y, n&oacute;i: &quot;Ta kh&ocirc;ng phải n&oacute;i a? Ch&uacute;ng ta c&ugrave;ng một chỗ d&ugrave;ng điểm t&acirc;m, ngươi như thế n&agrave;o chỉ đưa tới ta kia một phần?&quot;<br />\r\n<br />\r\n&quot;Phụ th&acirc;n li&ecirc;n tục dặn d&ograve; qua, Cửu th&uacute;c c&ocirc;ng l&agrave; trưởng bối, t&ocirc;n nhi nhất định phải cẩn thận phụng dưỡng, kh&ocirc;ng thể chậm trễ Cửu th&uacute;c c&ocirc;ng, t&ocirc;n nhi vạn vạn kh&ocirc;ng d&aacute;m c&ugrave;ng Cửu th&uacute;c c&ocirc;ng ngồi c&ugrave;ng b&agrave;n d&ugrave;ng bữa, c&ograve;n Cửu th&uacute;c c&ocirc;ng th&ocirc;ng cảm.&quot;<br />\r\n<br />\r\nVương Thu Sinh sắc mặt xiết chặt, thận trọng giải th&iacute;ch n&oacute;i.<br />\r\n<br />\r\nVương Trường Sinh khẽ thở d&agrave;i một hơi, lắc đầu, n&oacute;i ra: &quot;Được rồi, ngươi xuống dưới d&ugrave;ng điểm t&acirc;m đi! Sử dụng hết điểm t&acirc;m lại tới thu thập b&aacute;t đũa.&quot;<br />\r\n<br />\r\n&quot;T&ocirc;n nhi tu&acirc;n mệnh.&quot;<br />\r\n<br />\r\nVương Thu Sinh lập tức đ&aacute;p ứng một tiếng, khom người thi lễ về sau, lui về ra Thanh Li&ecirc;n c&aacute;c.<br />\r\n<br />\r\nNăm trăm năm trước, Vương gia ti&ecirc;n tổ Vương Nguy&ecirc;n Cương mang nh&agrave; mang người đi v&agrave;o Ninh ch&acirc;u Trường B&igrave;nh quận định cư.<br />\r\n<br />\r\nĐương thời Vương gia bất qu&aacute; l&agrave; thất khẩu gia đ&igrave;nh, bất qu&aacute; tại hai vị Tr&uacute;c Cơ tu sĩ che chở cho, kh&ocirc;ng đến trăm năm, Vương gia nh&acirc;n khẩu lật mười mấy lần kh&ocirc;ng ngừng, tuy nhi&ecirc;n chỉ c&oacute; số &iacute;t may mắn c&oacute; được Linh căn, bất qu&aacute; cũng v&igrave; về sau Vương gia đặt cơ sở vững chắc.<br />\r\n<br />\r\nĐi qua mấy trăm năm ph&aacute;t triển, Vương gia nh&acirc;n khẩu kh&ocirc;ng ngừng gấp bội, tu ti&ecirc;n giả số lượng kh&ocirc;ng ngừng gia tăng, đoạn cho đến trước mắt, Vương gia c&oacute; một trăm bốn mươi năm danh tu ti&ecirc;n giả, c&ograve;n c&oacute; mười mấy vạn kh&ocirc;ng c&oacute; Linh căn tộc nh&acirc;n.<br />\r\n<br />\r\nVương gia gia tộc trụ sở tại Trường B&igrave;nh quận Thanh Li&ecirc;n sơn, c&oacute; được Linh căn Vương gia tộc nh&acirc;n phần lớn ở tại Thanh Li&ecirc;n sơn.<br />\r\n<br />\r\nThanh Li&ecirc;n sơn nguy&ecirc;n bản c&oacute; một đầu Nhất giai Thượng phẩm Linh mạch, bất qu&aacute; tại lịch đại Vương gia ti&ecirc;n tổ cố gắng dưới, Nhất giai Linh mạch đ&atilde; bồi dưỡng th&agrave;nh Nhị giai Hạ phẩm Linh mạch, c&oacute; thể v&igrave; Tr&uacute;c Cơ tu sĩ cung cấp đầy đủ Linh kh&iacute;.<br />\r\n<br />\r\nVề phần mười mấy vạn kh&ocirc;ng c&oacute; Linh căn Vương gia tộc nh&acirc;n, th&igrave; ph&acirc;n bố tại Trường B&igrave;nh quận nội tam c&aacute;i Huyện th&agrave;nh, theo thứ tự l&agrave; B&igrave;nh An huyện, Thanh V&acirc;n huyện, Thanh Tr&uacute;c huyện.<br />\r\n<br />\r\nVương gia gia chủ đương thời l&agrave; Vương Minh Viễn, cũng l&agrave; Vương Trường Sinh phụ th&acirc;n, Vương Minh Viễn l&agrave; Tứ Linh căn, tư chất cũng kh&ocirc;ng tốt, bất qu&aacute; hắn xử sự c&ocirc;ng bằng, đối xử mọi người khi&ecirc;m tốn, bị đại đa số tộc nh&acirc;n đề cử l&agrave; Vương gia Gia chủ, phụ tr&aacute;ch quản l&yacute; gia tộc sự vụ lớn nhỏ.<br />\r\n<br />\r\nVương gia Tr&uacute;c Cơ tu sĩ Vương Diệu T&ocirc;ng ng&agrave;y thường rất &iacute;t lộ diện, l&acirc;u d&agrave;i bế quan tu luyện.<br />\r\n<br />\r\nVương Trường Sinh mẫu th&acirc;n Liễu Thanh Nhi l&agrave; một t&ecirc;n t&aacute;n tu, Tam Linh căn, tư chất so Vương Minh Viễn tốt một ch&uacute;t.<br />\r\n<br />\r\nNăm đ&oacute; Vương Trường Sinh gia gia Vương Diệu Tổ kh&ocirc;ng đồng &yacute; Liễu Thanh Nhi v&agrave;o cửa, bởi v&igrave; Vương Diệu Tổ dự định nhường Vương Minh Viễn cưới Quảng Dương quận Triệu gia đ&iacute;ch nữ, tăng cường hai nh&agrave; quan hệ trong đ&oacute;, Vương gia c&ugrave;ng Triệu gia thực lực ch&ecirc;nh lệch kh&ocirc;ng nhiều, hai nh&agrave; Tr&uacute;c Cơ L&atilde;o tổ lại l&agrave; hảo hữu, cửa h&ocirc;n sự n&agrave;y đối với song phương đều c&oacute; chỗ tốt.<br />\r\n<br />\r\nVương Minh Viễn ch&iacute;nh l&agrave; kh&ocirc;ng đ&aacute;p ứng, một bộ kh&ocirc;ng phải Liễu Thanh Nhi kh&ocirc;ng cưới tư thế , tức giận đến Vương Diệu Tổ đem Vương Minh Viễn nhốt ba năm đ&oacute;ng chặt.<br />\r\n<br />\r\nLiễu Thanh Nhi tại Thanh Li&ecirc;n sơn phụ cận khổ đợi Vương Minh Viễn ba năm, đả động Vương Diệu Tổ, n&agrave;y mới khiến Liễu Thanh Nhi tới cửa gặp nhau.<br />\r\n<br />\r\nLiễu Thanh Nhi tại một nh&agrave; Luyện kh&iacute; cửa h&agrave;ng nhậm chức mười năm, l&agrave; một t&ecirc;n Luyện kh&iacute; học đồ, Vương Diệu Tổ biết được n&agrave;y sự, l&uacute;c n&agrave;y mới thống kho&aacute;i đ&aacute;p ứng cửa h&ocirc;n sự n&agrave;y.<br />\r\n<br />\r\nVương Trường Sinh l&agrave; con trai độc nhất, tại c&ugrave;ng bối phận b&ecirc;n trong x&ecirc;́p hạng đệ Cửu, hắn l&agrave; Kim Thủy Thổ Tam Linh căn, chủ Linh căn l&agrave; thủy, Thủy Linh căn cảm ứng độ đạt tới s&aacute;u mươi, tư chất tại c&ugrave;ng bối phận b&ecirc;n trong, chỉ c&oacute; thể coi l&agrave; b&igrave;nh thường, hắn tu luyện ch&iacute;nh l&agrave; Vương gia tổ truyền &laquo; V&acirc;n Vũ quyết &raquo;.<br />\r\n<br />\r\nC&ocirc;ng ph&aacute;p n&agrave;y l&agrave; nhất bộ Nh&acirc;n phẩm Nhất giai C&ocirc;ng ph&aacute;p, c&oacute; thể từ Luyện kh&iacute; Nhất tầng tu luyện tới Luyện kh&iacute; Cửu tầng, c&ocirc;ng ph&aacute;p n&agrave;y thắng ở b&igrave;nh ổn, b&igrave;nh cảnh &iacute;t, lại rất dễ đột ph&aacute;.<br />\r\n<br />\r\nVương Trường Sinh năm tuổi tiến nhập giảng đạo đường, học chữ, từ tộc l&atilde;o truyền thụ tu ti&ecirc;n tri thức.<br />\r\n<br />\r\nT&aacute;m tuổi bắt đầu tu luyện, mỗi th&aacute;ng nhận lấy thập c&acirc;n Nhất giai Hạ phẩm Linh mễ.<br />\r\n<br />\r\nMười ba tuổi năm đ&oacute;, Vương Trường Sinh tiến v&agrave;o Luyện kh&iacute; Tầng bốn, mỗi th&aacute;ng c&oacute; thể nhận lấy tam c&acirc;n Nhất giai Trung phẩm Linh mễ.<br />\r\n<br />\r\nVương Trường Sinh khi 16 tuổi, bất qu&aacute; l&agrave; Luyện kh&iacute; Tầng bốn , dựa theo tộc quy, mười t&aacute;m tuổi ph&iacute;a trước kh&ocirc;ng cần l&agrave;m sự, gia tộc cung cấp t&agrave;i nguy&ecirc;n tu luyện, nếu l&agrave; chưa thể tại mười t&aacute;m tuổi tu luyện tới Luyện kh&iacute; Ngũ tầng, sẽ v&igrave; gia tộc xuất lực, đổi lấy t&agrave;i nguy&ecirc;n tu luyện.<br />\r\n<br />\r\nVương gia hiện tại nhập kh&ocirc;ng đủ xuất, Vương Minh Viễn chỉ c&oacute; thể nhường thế hệ trẻ tuổi sớm hai năm ra l&agrave;m việc, tăng thu giảm chi.<br />\r\n<br />\r\nQuyết định n&agrave;y, lọt v&agrave;o bộ phận tộc l&atilde;o phản đối, tộc l&atilde;o m&ocirc;n cho l&agrave; thế hệ trẻ tuổi l&agrave; gia tộc tương lai, cần phải để bọn hắn tu luyện tới mười t&aacute;m tuổi, tộc l&atilde;o nguyện &yacute; đem m&igrave;nh mỗi th&aacute;ng bổng lộc lấy ra, phụ cấp cấp thế hệ trẻ tuổi.<br />\r\n<br />\r\nVương Minh Viễn cho l&agrave; đ&acirc;y kh&ocirc;ng phải giải quyết gia tộc khốn cảnh biện ph&aacute;p, v&igrave; thuyết phục phản đối tộc l&atilde;o, hắn đem Vương Diệu T&ocirc;ng mời ra, tổ chức tộc hội.<br />\r\n<br />\r\nVương Minh Viễn th&acirc;n l&agrave; Gia chủ, l&agrave;m gương tốt, đem con của m&igrave;nh ph&aacute;i ra ngo&agrave;i, đến B&igrave;nh An huyện đảm nhiệm Thi&ecirc;n sư, trong v&ograve;ng bốn năm.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nB&igrave;nh An huyện l&agrave; Vương gia khống chế tam c&aacute;i trong huyện th&agrave;nh điều kiện k&eacute;m cỏi nhất nhất c&aacute;i Huyện th&agrave;nh, Vương Minh Viễn l&agrave;m gương tốt, lại th&ecirc;m Vương Diệu T&ocirc;ng đồng &yacute;, thanh &acirc;m phản đối cũng liền biến mất.<br />\r\n<br />\r\nBởi v&igrave; chuyện n&agrave;y, Liễu Thanh Nhi trong &acirc;m thầm kh&ocirc;ng &iacute;t o&aacute;n tr&aacute;ch Vương Minh Viễn, n&oacute;i Vương Minh Viễn trong l&ograve;ng chỉ c&oacute; Vương gia c&aacute;i n&agrave;y mọi người, kh&ocirc;ng c&oacute; m&igrave;nh tiểu gia.<br />\r\n<br />\r\nMười s&aacute;u tuổi năm đ&oacute;, Vương Trường Sinh tại mẫu th&acirc;n lưu luyến kh&ocirc;ng rời trong &aacute;nh mắt, mang theo mẫu th&acirc;n đi suốt đ&ecirc;m chế năm c&acirc;n linh b&aacute;nh ngọt, ly khai Thanh Li&ecirc;n sơn, đi v&agrave;o B&igrave;nh An huyện.<br />\r\n<br />\r\nB&igrave;nh An huyện tương đối cằn cỗi, nh&acirc;n khẩu thưa thớt, cư tr&uacute; mấy vạn danh Vương gia tộc nh&acirc;n, ph&acirc;n t&aacute;n tại Huyện th&agrave;nh c&ugrave;ng Huyện th&agrave;nh phụ cận th&ocirc;n trấn.<br />\r\n<br />\r\nVương Trường Sinh nhiệm vụ, ch&iacute;nh l&agrave; bảo hộ B&igrave;nh An huyện ph&agrave;m nh&acirc;n b&aacute;ch t&iacute;nh kh&ocirc;ng nhận t&agrave; ma quỷ qu&aacute;i x&acirc;m hại.<br />\r\n<br />\r\nTuy n&oacute;i ở tại thế tục Vương gia tộc nh&acirc;n kh&ocirc;ng c&oacute; Linh căn, bất qu&aacute; bọn hắn tổ ti&ecirc;n c&oacute; được Linh căn, bởi vậy, những người n&agrave;y hậu duệ c&oacute; rất lớn tỉ lệ người mang Linh căn.<br />\r\n<br />\r\nV&igrave; thế, Vương gia tại khống chế tam c&aacute;i Huyện th&agrave;nh đều ph&aacute;i tu ti&ecirc;n giả đ&oacute;ng giữ, định kỳ kiểm Trắc Linh căn.<br />\r\n<br />\r\nMấy trăm năm qua, ở tại thế tục Vương gia tộc nh&acirc;n xuất hiện mười mấy danh người mang Linh căn giả, Vương Diệu T&ocirc;ng phụ mẫu ch&iacute;nh l&agrave; ph&agrave;m nh&acirc;n, bởi v&igrave; kiểm trắc xuất Linh căn, l&uacute;c n&agrave;y mới bị đưa đến Thanh Li&ecirc;n sơn tu luyện, ở gia tộc bồi dưỡng dưới, th&agrave;nh c&ocirc;ng Tr&uacute;c Cơ.<br />\r\n<br />\r\nBởi v&igrave; c&oacute; Vương Diệu T&ocirc;ng c&aacute;i n&agrave;y v&iacute; dụ sống sờ sờ, Vương gia rất xem trọng ở tại thế tục tộc nh&acirc;n, đương nhi&ecirc;n, Vương gia cũng coi trọng c&aacute;i kh&aacute;c b&aacute;ch t&iacute;nh, ph&agrave;m nh&acirc;n đản sinh hậu duệ xuất hiện tu ti&ecirc;n giả tỉ lệ tuy nhi&ecirc;n rất nhỏ, bất qu&aacute; Vương gia đối xử như nhau, Vương gia c&aacute;ch đoạn thời gian ph&aacute;i người đi thế tục kiểm trắc con mới sinh phải chăng c&oacute; được Linh căn, tốt bổ sung m&aacute;u mới.<br />\r\n<br />\r\nB&igrave;nh An huyện Huyện lệnh từ triều đ&igrave;nh cắt cử, Vương gia sẽ kh&ocirc;ng can thiệp Huyện lệnh xử l&yacute; ch&iacute;nh vụ, nếu l&agrave; c&oacute; t&agrave; ma y&ecirc;u qu&aacute;i l&agrave;m loạn, Vương gia lại ph&aacute;i tu ti&ecirc;n giả xử l&yacute;.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; Linh căn Vương gia tộc nh&acirc;n, hoặc kinh thương, hoặc tham ch&iacute;nh, Vương gia sẽ kh&ocirc;ng can thiệp, bất qu&aacute; bọn hắn hậu nh&acirc;n nếu l&agrave; người mang Linh căn, nhất định phải đưa đi Thanh Li&ecirc;n sơn.<br />\r\n<br />\r\nVương Trường Sinh đến B&igrave;nh An huyện ba năm, ng&agrave;y thường tựu ở tr&ecirc;n Li&ecirc;n Hoa đảo, rất &iacute;t ly khai Li&ecirc;n Hoa đảo.<br />\r\n<br />\r\nLi&ecirc;n Hoa đảo thượng cũng kh&ocirc;ng c&oacute; Linh mạch, Linh kh&iacute; mờ nhạt, ở chỗ n&agrave;y tu luyện, tốc độ rất chậm, hắn tới B&igrave;nh An huyện ba năm, chỉ l&agrave; tấn thăng nhất c&aacute;i tiểu cảnh giới, đ&acirc;y l&agrave; hắn phục dụng kh&ocirc;ng &iacute;t Linh cốc t&igrave;nh huống dưới, nếu l&agrave; kh&ocirc;ng c&oacute; Linh cốc, Vương Trường Sinh năm năm đều chưa hẳn có th&ecirc;̉ tiến v&agrave;o Luyện kh&iacute; Ngũ tầng.<br />\r\n<br />\r\nLinh đan gi&aacute; cả hơi đắt, Vương Trường Sinh xấu hổ v&igrave; trong v&iacute; tiền rỗng tuếch, lựa chọn d&ugrave;ng ăn Linh cốc.<br />\r\n<br />\r\nHắn hiện tại l&agrave; Luyện kh&iacute; trung kỳ, th&iacute;ch hợp d&ugrave;ng ăn Nhất giai Trung phẩm Linh cốc, Nhất giai Trung phẩm Linh cốc hai ba năm mới ch&iacute;n, gi&aacute; cả tại một hai khối Linh thạch tả hữu.<br />\r\n<br />\r\nTại B&igrave;nh An huyện đảm nhiệm Thi&ecirc;n sư cơ hồ kh&ocirc;ng c&oacute; cơ hội lập c&ocirc;ng, mỗi th&aacute;ng nhất khối Linh thạch bổng lộc, mua được nhất c&acirc;n Linh cốc rất nhanh liền đ&atilde; ăn xong, kh&ocirc;ng c&oacute; c&aacute;ch, hắn chỉ c&oacute; thể ăn kh&ocirc;ng c&oacute; linh kh&iacute; đồ ăn.</p>', '2021-11-27 21:42:45', 17, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(191, 51, 'Vương Gia trấn', '2', '<p>Ngoại trừ Vương Trường Sinh, ở tr&ecirc;n đảo c&ograve;n c&oacute; mười t&ecirc;n ph&agrave;m nh&acirc;n, c&aacute;i n&agrave;y mười t&ecirc;n ph&agrave;m nh&acirc;n l&agrave; B&igrave;nh An huyện bản gia ph&aacute;i tới chăm s&oacute;c Vương Trường Sinh ẩm thực sinh hoạt thường ng&agrave;y.<br />\r\n<br />\r\nVương Thu Sinh phụ tr&aacute;ch đầu đưa thức ăn c&ugrave;ng thu thập ch&eacute;n dĩa , dựa theo bối phận, hắn n&ecirc;n gọi Vương Trường Sinh một tiếng Cửu th&uacute;c c&ocirc;ng.<br />\r\n<br />\r\nVương Trường Sinh ngồi xuống, cầm lấy đũa lột một ngụm cơm trắng, m&agrave;u sắc &oacute;ng &aacute;nh, nhuyễn nhu ngon miệng, đ&aacute;ng tiếc ch&iacute;nh l&agrave; kh&ocirc;ng c&oacute; mảy may Linh kh&iacute;.<br />\r\n<br />\r\nSử dụng hết điểm t&acirc;m, Vương Trường Sinh đi ra Thanh Li&ecirc;n c&aacute;c, bờ m&ocirc;i khẽ nh&uacute;c nh&iacute;ch mấy lần, t&uacute;c hạ to&aacute;t ra một mảng lớn sương m&ugrave; m&agrave;u trắng.<br />\r\n<br />\r\nSương m&ugrave; m&agrave;u trắng cuồn cuộn ngưng tụ về sau, h&oacute;a th&agrave;nh một đo&agrave;n gần trượng lớn nhỏ m&agrave;u trắng đ&aacute;m m&acirc;y, nhẹ nh&agrave;ng tr&ocirc;i nổi ở nơi đ&oacute;, giống như đất bằng.<br />\r\n<br />\r\nĐằng V&acirc;n Gi&aacute; Vụ thuật, Sơ cấp hạ giai phi h&agrave;nh Ph&aacute;p thuật, tốc độ phi h&agrave;nh kh&ocirc;ng nhanh, bất qu&aacute; tại kh&ocirc;ng c&oacute; phi h&agrave;nh Linh kh&iacute; t&igrave;nh huống dưới, Luyện Kh&iacute; kỳ tu sĩ phần lớn đều tu luyện n&agrave;y thuật.<br />\r\n<br />\r\nVương Trường Sinh đi đến m&agrave;u trắng đ&aacute;m m&acirc;y ph&iacute;a tr&ecirc;n, m&agrave;u trắng đ&aacute;m m&acirc;y chậm r&atilde;i l&ecirc;n kh&ocirc;ng, hướng ph&iacute;a nơi xa bay đi.<br />\r\n<br />\r\nCũng kh&ocirc;ng l&acirc;u lắm, Vương Trường Sinh đ&aacute;p xuống một c&aacute;i sơn cốc b&ecirc;n trong, sơn cốc hai b&ecirc;n v&aacute;ch đ&aacute; mấp m&ocirc;, tr&ecirc;n mặt đất t&aacute;n lạc đại lượng to&aacute;i thạch, hắn thường xuy&ecirc;n đến nơi n&agrave;y luyện tập Ph&aacute;p thuật.<br />\r\n<br />\r\nBởi v&igrave; Li&ecirc;n Hoa đảo Linh kh&iacute; cằn cỗi, tu luyện l&agrave;m nhiều c&ocirc;ng &iacute;t, hắn đại đa số thời gian, đều d&ugrave;ng để tu luyện Ph&aacute;p thuật.<br />\r\n<br />\r\nVương Trường Sinh nhẹ thở ra một hơi, trong miệng n&oacute;i lẩm bẩm, hai tay bấm niệm ph&aacute;p quyết kh&ocirc;ng th&ocirc;i, kết xuất từng c&aacute;i kỳ qu&aacute;i ph&aacute;p ấn.<br />\r\n<br />\r\nMột l&aacute;t sau, đại lượng lam quang tại Vương Trường Sinh quanh th&acirc;n nổi l&ecirc;n, nhanh ch&oacute;ng ngưng tụ th&agrave;nh một vi&ecirc;n d&agrave;i hơn thước &oacute;ng &aacute;nh băng tr&ugrave;y, tản m&aacute;t ra trận trận H&agrave;n kh&iacute;.<br />\r\n<br />\r\n&quot;Đi.&quot;<br />\r\n<br />\r\nVương Trường Sinh đưa tay x&ocirc;ng v&aacute;ch đ&aacute; nhẹ nh&agrave;ng điểm một c&aacute;i, nhẹ giọng qu&aacute;t.<br />\r\n<br />\r\nVừa dứt lời, &oacute;ng &aacute;nh băng tr&ugrave;y thẳng đến v&aacute;ch đ&aacute; m&agrave; đi.<br />\r\n<br />\r\nMột tiếng tiếng bạo liệt vang l&ecirc;n về sau, tr&ecirc;n v&aacute;ch đ&aacute; nhiều nhất c&aacute;i hơn một x&iacute;ch thấm nhần c&aacute;i hố nhỏ, c&aacute;i hố nhỏ phụ cận bị một tầng sương lạnh bao tr&ugrave;m.<br />\r\n<br />\r\n&quot;Th&agrave;nh c&ocirc;ng!&quot;<br />\r\n<br />\r\nVương Trường Sinh mắt thấy cảnh n&agrave;y, tr&ecirc;n mặt lộ ra vẻ mừng rỡ.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nBăng Tr&ugrave;y thuật l&agrave; Sơ cấp Trung giai Ph&aacute;p thuật, uy lực kh&ocirc;ng phải đặc biệt lớn, Vương Trường Sinh hoa hơn nửa năm, rốt cục đem Băng Tr&ugrave;y thuật tu luyện tới Đại vi&ecirc;n m&atilde;n.<br />\r\n<br />\r\nPh&aacute;p thuật căn cứ uy lực lớn nhỏ c&ugrave;ng thi ph&aacute;p thời gian, chia l&agrave;m Nhập m&ocirc;n, Tiểu th&agrave;nh, Đại vi&ecirc;n m&atilde;n ba c&aacute;i giai đoạn , dưới t&igrave;nh huống b&igrave;nh thường, mười ng&agrave;y nửa th&aacute;ng liền c&oacute; thể đem một m&ocirc;n Sơ cấp Ph&aacute;p thuật tu luyện Nhập m&ocirc;n, muốn đem Sơ cấp Ph&aacute;p thuật tu luyện tới Tiểu th&agrave;nh, như thế n&agrave;o cũng muốn hai ba th&aacute;ng, nếu l&agrave; Sơ cấp Ph&aacute;p thuật muốn tu luyện tới Đại vi&ecirc;n m&atilde;n, n&oacute;i &iacute;t muốn nửa năm trở l&ecirc;n.<br />\r\n<br />\r\nN&oacute;i thật, Vương Trường Sinh ngay từ đầu đi v&agrave;o Li&ecirc;n Hoa đảo c&aacute;i n&agrave;y địa phương cứt chim cũng kh&ocirc;ng c&oacute;, trong l&ograve;ng đối phụ th&acirc;n vẫn l&agrave; rất c&oacute; ph&ecirc; b&igrave;nh k&iacute;n đ&aacute;o, bất qu&aacute; ng&acirc;y người ba năm sau, t&acirc;m cảnh của hắn c&oacute; chỗ biến h&oacute;a, chậm r&atilde;i hiểu được phụ th&acirc;n nỗi khổ t&acirc;m trong l&ograve;ng.<br />\r\n<br />\r\nTại trong ba năm n&agrave;y, tốc độ tu luyện của hắn giống như r&ugrave;a b&ograve;, bất qu&aacute; t&acirc;m cảnh ki&ecirc;n định kh&ocirc;ng &iacute;t, trừ c&aacute;i đ&oacute; ra, hắn c&ograve;n đem năm m&ocirc;n Sơ cấp Ph&aacute;p thuật tu luyện tới Đại vi&ecirc;n m&atilde;n, c&oacute; thể thuấn ph&aacute;t Ph&aacute;p thuật, nếu l&agrave; trả ở tại gia tộc tu luyện, hắn chưa chắc sẽ r&uacute;t xuất nhiều thời gian như vậy tu luyện Ph&aacute;p thuật.<br />\r\n<br />\r\nNhất c&aacute;i buổi s&aacute;ng thời gian, rất nhanh liền đi qua, Vương Trường Sinh luyện nhất c&aacute;i buổi s&aacute;ng Ph&aacute;p thuật.<br />\r\n<br />\r\nBuổi trưa, hắn trở lại Thanh Li&ecirc;n c&aacute;c thời điểm, Vương Thu Sinh vừa vặn đem ăn trưa bưng tới.<br />\r\n<br />\r\nVương Thu Sinh bu&ocirc;ng xuống đồ ăn, liền rời đi.<br />\r\n<br />\r\nHai m&oacute;n m&ocirc;̣t chén canh, hấp thanh tước trứng, vi&ecirc;n thịt đậu hũ canh, thịt kho t&agrave;u lộc thịt.<br />\r\n<br />\r\nNguy&ecirc;n liệu nấu ăn đều l&agrave; Vương gia bản tộc đưa tới, đều l&agrave; tốt nhất nguy&ecirc;n liệu nấu ăn, đầu bếp cũng l&agrave; Nhất lưu đầu bếp, duy nhất khuyết điểm ch&iacute;nh l&agrave; kh&ocirc;ng c&oacute; Linh kh&iacute;.<br />\r\n<br />\r\nVương Trường Sinh uống một ch&eacute;n canh, vừa muốn động đũa, Vương Thu Sinh bước nhanh đến, tr&ecirc;n tay bưng lấy một trương thư, khắp khu&ocirc;n mặt l&agrave; vẻ khẩn trương.<br />\r\n<br />\r\n&quot;Cửu th&uacute;c c&ocirc;ng, Vương Gia trấn li&ecirc;n tiếp c&oacute; b&aacute;ch t&iacute;nh chết đi, c&oacute; ch&uacute;t cổ qu&aacute;i, ng&agrave;i nh&igrave;n?&quot;<br />\r\n<br />\r\nVương Trường Sinh nh&iacute;u m&agrave;y lại, hỏi: &quot;Lấy ra ta xem một ch&uacute;t.&quot;<br />\r\n<br />\r\nVương Thu Sinh kh&ocirc;ng d&aacute;m thất lễ, hai tay đem thư đưa tới.<br />\r\n<br />\r\nVương Trường Sinh &aacute;nh mắt qu&eacute;t qua, suy nghĩ một ch&uacute;t, ph&acirc;n ph&oacute; n&oacute;i: &quot;Ngươi biết Vương Gia trấn trưởng trấn a?&quot;<br />\r\n<br />\r\n&quot;Nhận biết , dựa theo bối phận, Vương Gia trấn trưởng trấn Vương Thanh V&acirc;n l&agrave; t&ocirc;n nhi Tam th&uacute;c.&quot;<br />\r\n<br />\r\n&quot;Trở về thu thập một ch&uacute;t, mang l&ecirc;n một ch&uacute;t lương kh&ocirc;, đi với ta một chuyến Vương Gia trấn.&quot;<br />\r\n<br />\r\nVương Gia trấn hơn ch&iacute;n th&agrave;nh b&aacute;ch t&iacute;nh đều l&agrave; Vương gia tộc nh&acirc;n, c&aacute;i trấn n&agrave;y b&aacute;ch t&iacute;nh l&agrave; Vương Trường Sinh trọng điểm bảo hộ đối tượng.<br />\r\n<br />\r\nVương Gia trấn xảy ra chuyện, Vương Trường Sinh tự nhi&ecirc;n kh&ocirc;ng d&aacute;m thất lễ.<br />\r\n<br />\r\nMột khắc đồng hồ về sau, Vương Thu Sinh lưng c&otilde;ng một bao quần &aacute;o, đi v&agrave;o Thanh Li&ecirc;n c&aacute;c, sau lưng hắn, c&ograve;n c&oacute; một t&ecirc;n khu&ocirc;n mặt thanh t&uacute; lam v&aacute;y thiếu nữ.<br />\r\n<br />\r\nVương Trường Sinh nh&igrave;n về ph&iacute;a lam v&aacute;y thiếu nữ, ph&acirc;n ph&oacute; n&oacute;i: &quot;Thu Nguyệt, l&uacute;c ta kh&ocirc;ng c&oacute; ở đ&acirc;y, Li&ecirc;n Hoa đảo tựu giao cho ngươi, nếu như thu được c&aacute;i g&igrave; thư t&iacute;n, lập tức ph&aacute;i người đưa đến Vương Gia trấn.&quot;<br />\r\n<br />\r\n&quot;L&agrave;, Cửu th&uacute;c c&ocirc;ng.&quot;<br />\r\n<br />\r\nĐi ra Thanh Li&ecirc;n c&aacute;c, Vương Trường Sinh bờ m&ocirc;i khẽ nh&uacute;c nh&iacute;ch mấy lần, t&uacute;c hạ trống rỗng d&acirc;ng l&ecirc;n một đo&agrave;n m&agrave;u trắng đ&aacute;m m&acirc;y.<br />\r\n<br />\r\nMặc d&ugrave; kh&ocirc;ng phải lần đầu ti&ecirc;n nh&igrave;n thấy Vương Trường Sinh thi ph&aacute;p, Vương Thu Sinh c&ugrave;ng Vương Thu Nguyệt tr&ecirc;n mặt vẫn l&agrave; lộ ra thần sắc h&acirc;m mộ.<br />\r\n<br />\r\nM&agrave;u trắng đ&aacute;m m&acirc;y chở Vương Trường Sinh c&ugrave;ng Vương Thu Sinh chậm r&atilde;i d&acirc;ng l&ecirc;n, hướng nơi xa bay đi, cũng kh&ocirc;ng l&acirc;u lắm, hai người tựu ti&ecirc;u thất ở ch&acirc;n trời.<br />\r\n<br />\r\n&middot;&middot;&middot;&middot;&middot;&middot;<br />\r\n<br />\r\nVương Gia trấn l&agrave; nhất c&aacute;i c&oacute; hơn năm ng&agrave;n người quy m&ocirc; tiểu trấn, thị trấn Bắc bộ c&oacute; một đầu ba trượng đến rộng tiểu H&agrave;, dọc theo s&ocirc;ng l&agrave; một mảng lớn đồng ruộng.<br />\r\n<br />\r\nVương Gia trấn b&aacute;ch t&iacute;nh lấy l&agrave;m ruộng c&ugrave;ng đi săn m&agrave; sống, trải qua tự cấp tự t&uacute;c sinh hoạt.<br />\r\n<br />\r\nVương Gia trấn chỉ c&oacute; một lối đi, đường đi hiển nam bắc phương hướng, tại đầu nam có x&acirc;y to to nhỏ nhỏ mấy trăm t&ograve;a nh&agrave; l&agrave;m bằng gỗ hoặc gạch chế kiến tr&uacute;c, trong đ&oacute; nhất t&ograve;a viện tối v&igrave; kh&iacute; ph&aacute;i, cao hai trượng m&agrave;u đỏ thắm đại m&ocirc;n nh&igrave;n kh&iacute; thế b&agrave;ng bạc, bề ngo&agrave;i thượng hiện đầy l&oacute;e s&aacute;ng đồng đinh, dưới ánh mặt trời th&ocirc;i th&ocirc;i tỏa s&aacute;ng.<br />\r\n<br />\r\nTại cửa viện hai b&ecirc;n tr&aacute;i phải, c&aacute;c thả c&oacute; một con dữ tợn sư tử đ&aacute;.<br />\r\n<br />\r\nCổng treo nhất khối d&agrave;i hơn một trượng hắc sắc bảng hiệu, ph&iacute;a tr&ecirc;n khắc lấy &quot;Vương gia Từ đường&quot; tứ c&aacute;i chữ to m&agrave;u v&agrave;ng, v&ocirc; c&ugrave;ng dễ thấy.<br />\r\n<br />\r\nTrong ch&iacute;nh sảnh, hơn hai mươi danh tuổi tr&ecirc;n năm mươi l&atilde;o giả tụ tập c&ugrave;ng một chỗ, một t&ecirc;n năm s&aacute;u mươi tuổi ph&uacute;c hậu l&atilde;o giả ngồi ở chủ vị thượng, ph&uacute;c hậu l&atilde;o giả ch&iacute;nh l&agrave; Vương Gia trấn trưởng trấn Vương Thanh V&acirc;n.<br />\r\n<br />\r\nVương Thanh V&acirc;n h&ocirc;m nay s&aacute;u mươi bảy tuổi, l&agrave; Vương Gia trấn bối phận cao nhất nh&acirc;n, đức cao vọng trọng, được mọi người đề cử v&igrave; trưởng trấn.<br />\r\n<br />\r\nTại dưới người hắn hai b&ecirc;n l&atilde;o giả, đều l&agrave; Vương Gia trấn từng c&aacute;i chi nh&aacute;nh đại biểu.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nĐ&aacute;ng lưu &yacute; ch&iacute;nh l&agrave;, tại Vương Thanh V&acirc;n b&ecirc;n cạnh, đứng đấy một cao một thấp hai t&ecirc;n bộ kho&aacute;i, thần sắc ngưng trọng.<br />\r\n<br />\r\nHai t&ecirc;n bộ kho&aacute;i cũng l&agrave; Vương gia tộc nh&acirc;n, l&uacute;c đầu bọn hắn kh&ocirc;ng tư c&aacute;ch tham dự loại n&agrave;y quy m&ocirc; tộc hội, bất qu&aacute; b&acirc;y giờ xuất hiện &aacute;n mạng, Vương Thanh V&acirc;n liền để bọn hắn tham gia h&ocirc;m nay trận n&agrave;y tộc hội.<br />\r\n<br />\r\nMột t&ecirc;n cao cao gầy teo l&atilde;o giả đứng dậy, x&ocirc;ng Vương Thanh V&acirc;n vừa chắp tay, hỏi: &quot;Tam th&uacute;c, đến tột c&ugrave;ng chuyện g&igrave; xảy ra? Hung thủ c&ograve;n kh&ocirc;ng c&oacute; bắt được a? Thi&ecirc;n Hổ c&ugrave;ng Thi&ecirc;n Long c&aacute;c ngươi th&acirc;n l&agrave; bộ kho&aacute;i, nhiều ng&agrave;y như vậy, như thế n&agrave;o trả bắt kh&ocirc;ng được hung thủ?&quot;<br />\r\n<br />\r\n&quot;Lục b&aacute;, kh&ocirc;ng phải ch&uacute;ng ta hai anh em v&ocirc; năng, mà là chúng ta bất lực, người bị hại kh&ocirc;ng c&oacute; vết thương n&agrave;o, trong ph&ograve;ng cũng kh&ocirc;ng c&oacute; đ&aacute;nh nhau vết t&iacute;ch, tr&ecirc;n trấn cũng kh&ocirc;ng c&oacute; ngoại lai nh&acirc;n vi&ecirc;n, c&aacute;i n&agrave;y r&otilde; r&agrave;ng l&agrave; quỷ vật l&agrave;m.&quot; Mập l&ugrave;n bộ kho&aacute;i cười khổ giải th&iacute;ch n&oacute;i.<br />\r\n<br />\r\nVương Gia trấn b&aacute;ch t&iacute;nh tuy nhi&ecirc;n chưa thể tu ti&ecirc;n, bất qu&aacute; bọn hắn đều biết tr&ecirc;n đời c&oacute; tu ti&ecirc;n giả, cũng tin tưởng tr&ecirc;n đời tồn tại quỷ vật, mập l&ugrave;n bộ kho&aacute;i thuyết ph&aacute;p n&agrave;y, đạt được rất nhiều người t&aacute;n đồng.<br />\r\n<br />\r\n&quot;Ta đ&atilde; n&oacute;i rồi! Việc n&agrave;y khẳng định kh&ocirc;ng phải cố &yacute;, quả nhi&ecirc;n kh&ocirc;ng ngo&agrave;i sở liệu của ta.&quot;<br />\r\n<br />\r\n&quot;Ch&iacute;nh l&agrave; ch&iacute;nh l&agrave;, ta cũng đo&aacute;n được, nguy&ecirc;n lai thật sự l&agrave; quỷ vật l&agrave;m.&quot;<br />\r\n<br />\r\n&quot;Nếu biết l&agrave; quỷ vật l&agrave;m? Vậy c&ograve;n chờ g&igrave;, ta nhớ được B&igrave;nh An huyện c&oacute; Thi&ecirc;n sư trấn thủ, ph&ugrave; hộ ch&uacute;ng ta một phương b&igrave;nh an, Ngũ b&aacute;, mau ph&aacute;i người đi mời bản gia Thi&ecirc;n sư, diệt trừ quỷ vật, nếu kh&ocirc;ng ch&uacute;ng ta trong đ&ecirc;m cũng kh&ocirc;ng d&aacute;m đi ngủ, liền sợ mơ hồ chết mất, chỉ d&aacute;m tại ban ng&agrave;y h&iacute;p mắt một hồi.&quot;<br />\r\n<br />\r\n&middot;&middot;&middot;&middot;&middot;&middot;<br />\r\n<br />\r\nTừng c&aacute;i chi nh&aacute;nh đại biểu ngươi một lời, ta nhất c&uacute;, nhao nhao ph&aacute;t biểu c&aacute;i nh&igrave;n của m&igrave;nh.<br />\r\n<br />\r\nVương Thanh V&acirc;n kho&aacute;t tay &aacute;o, ph&acirc;n ph&oacute; n&oacute;i: &quot;Tốt, y&ecirc;n lặng, ta đ&atilde; nhường nh&acirc;n d&ugrave;ng bồ c&acirc;u đưa tin th&ocirc;ng tri bản gia Thi&ecirc;n sư, tại bản gia Thi&ecirc;n sư chạy tới ph&iacute;a trước, c&aacute;c ngươi ước th&uacute;c tốt tộc nh&acirc;n, ban đ&ecirc;m kh&ocirc;ng muốn một m&igrave;nh ra ngo&agrave;i, để tr&aacute;nh lại c&oacute; tộc nh&acirc;n ngộ hại.&quot;<br />\r\n<br />\r\nĐ&uacute;ng l&uacute;c n&agrave;y, một t&ecirc;n d&aacute;ng người cao gầy nam tử trung ni&ecirc;n bước nhanh đến, mặt mũi tr&agrave;n đầy mừng rỡ.<br />\r\n<br />\r\n&quot;Phụ th&acirc;n, Thu Sinh đường huynh mang theo bản gia Thi&ecirc;n sư đến đ&acirc;y.&quot;<br />\r\n<br />\r\nVương Thanh V&acirc;n nghe lời n&agrave;y, lập tức đứng l&ecirc;n, truy vấn: &quot;Thi&ecirc;n sư ở đ&acirc;u? Mau dẫn đường, cũng đừng chậm trễ bản gia Thi&ecirc;n sư.&quot;<br />\r\n<br />\r\nN&oacute;i xong lời n&agrave;y, Vương Thanh V&acirc;n bước nhanh đi ra ngo&agrave;i, c&aacute;i kh&aacute;c Vương gia tộc nh&acirc;n nhao nhao c&ugrave;ng b&ecirc;n tr&ecirc;n.</p>', '2021-11-27 21:43:10', 10, 1),
(192, 51, 'Cẩn thận thăm dò', '3', '<p>&quot;Kh&ocirc;ng cần, ta đ&atilde; đến.&quot; Một đạo nam tử thanh &acirc;m bỗng nhi&ecirc;n vang l&ecirc;n.<br />\r\n<br />\r\nVương Trường Sinh đi đến, Vương Thu Sinh đi theo ph&iacute;a sau của hắn.<br />\r\n<br />\r\n&quot;L&atilde;o hủ l&agrave; Vương Gia trấn trưởng trấn Vương Thanh V&acirc;n, b&aacute;i kiến Thi&ecirc;n sư.&quot; Vương Thanh V&acirc;n x&ocirc;ng Vương Trường Sinh c&uacute;i người h&agrave;nh lễ, cung k&iacute;nh thanh &acirc;m.<br />\r\n<br />\r\n&quot;B&aacute;i kiến Thi&ecirc;n sư.&quot;<br />\r\n<br />\r\nC&aacute;i kh&aacute;c Vương gia tộc nh&acirc;n gặp t&igrave;nh h&igrave;nh n&agrave;y, nhao nhao h&agrave;nh lễ.<br />\r\n<br />\r\n&quot;Một c&acirc;y b&uacute;t kh&ocirc;ng viết ra được lưỡng c&aacute;i chữ Vương, kh&ocirc;ng cần đến kh&aacute;ch kh&iacute; như vậy, cho ta n&oacute;i một ch&uacute;t chuyện đ&atilde; xảy ra đi!&quot; Vương Trường Sinh kho&aacute;t tay &aacute;o, vẻ mặt &ocirc;n h&ograve;a n&oacute;i.<br />\r\n<br />\r\nVương Thanh V&acirc;n gật đầu, nh&igrave;n về ph&iacute;a Vương Thu Sinh.<br />\r\n<br />\r\nVương Thu Sinh minh bạch Vương Thanh V&acirc;n &yacute; tứ, giải th&iacute;ch n&oacute;i: &quot;Tam th&uacute;c, Cửu th&uacute;c c&ocirc;ng biết được Vương Gia trấn nh&aacute;o quỷ, cơm đều kh&ocirc;ng để &yacute; tới ăn, lập tức liền chạy đến.&quot;<br />\r\n<br />\r\nVương Thanh V&acirc;n ngầm hiểu, cung k&iacute;nh n&oacute;i ra: &quot;Cửu th&uacute;c, l&agrave; chuyện như thế, nửa th&aacute;ng trước, c&oacute; một t&ecirc;n tộc nh&acirc;n buổi s&aacute;ng bị ph&aacute;t hiện chết ở trong nh&agrave;, tr&ecirc;n th&acirc;n kh&ocirc;ng c&oacute; vết thương n&agrave;o, vốn cho l&agrave; l&agrave; hung s&aacute;t &aacute;n, chất nhi liền nhường Thi&ecirc;n Long Thi&ecirc;n Hổ bọn hắn truy n&atilde; hung phạm, bất qu&aacute; bọn hắn nhất trực kh&ocirc;ng c&oacute; đầu mối, bản &aacute;n kh&ocirc;ng c&oacute; tiến triển, tiếp xuống nửa th&aacute;ng, lần lượt c&oacute; tộc nh&acirc;n ngộ hại, chất nhi l&uacute;c n&agrave;y mới &yacute; thức được kh&ocirc;ng ổn, vội v&agrave;ng ph&aacute;i người d&ugrave;ng bồ c&acirc;u đưa tin th&ocirc;ng tri Cửu th&uacute;c, kh&ocirc;ng nghĩ tới Cửu th&uacute;c nhanh như vậy tựu chạy tới.&quot;<br />\r\n<br />\r\nNghe xong Vương Thanh V&acirc;n trần thuật, Vương Trường Sinh nh&iacute;u m&agrave;y, thần sắc ngưng trọng hỏi: &quot;Ngươi mới vừa n&oacute;i, lần lượt c&oacute; tộc nh&acirc;n ngộ hại? Ngộ hại đều l&agrave; Vương gia tộc nh&acirc;n?&quot;<br />\r\n<br />\r\nChuyện n&agrave;y so Vương Trường Sinh tưởng tượng c&ograve;n nghi&ecirc;m trọng hơn, Vương Gia trấn xuất hiện quỷ vật kh&ocirc;ng t&iacute;nh l&agrave; g&igrave;, nếu l&agrave; c&oacute; nh&acirc;n tại nhằm v&agrave;o Vương gia, t&iacute;nh chất tựu kh&ocirc;ng đồng dạng.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng sai, cho tới b&acirc;y giờ, hết thảy c&oacute; ch&iacute;n t&ecirc;n tộc nh&acirc;n ngộ hại, đều kh&ocirc;ng ngoại lệ, người chết đều l&agrave; ban đ&ecirc;m ngộ hại, đều l&agrave; chết ở trong nh&agrave;, thi thể kh&ocirc;ng c&oacute; vết thương n&agrave;o, kh&ocirc;ng c&oacute; đ&aacute;nh nhau vết t&iacute;ch, chất nhi l&uacute;c n&agrave;y mới &yacute; thức được, hung thủ rất c&oacute; thể l&agrave; quỷ vật.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nVương Trường Sinh cau m&agrave;y , dựa theo Vương Thanh V&acirc;n thuyết ph&aacute;p, hung thủ khẳng định l&agrave; quỷ vật kh&ocirc;ng sai.<br />\r\n<br />\r\nCư hắn biết, quỷ vật trời sinh ưa th&iacute;ch h&uacute;t người sống tinh kh&iacute; trả lại tự th&acirc;n, người sống tinh kh&iacute; nếu l&agrave; bị h&uacute;t qu&aacute; nhiều, t&iacute;nh mệnh c&oacute; nguy.<br />\r\n<br />\r\nC&aacute;i n&agrave;y quỷ vật c&oacute; thể l&agrave; bản địa, cũng c&oacute; thể l&agrave; l&agrave; ngoại lai, c&aacute;i trước c&ograve;n tốt, c&aacute;i sau liền phiền to&aacute;i.<br />\r\n<br />\r\nVương Trường Sinh nghĩ nghĩ, hỏi: &quot;Vương Gia trấn c&oacute; b&atilde;i tha ma a? Hoặc l&agrave; c&oacute; hay kh&ocirc;ng đặc biệt chuyện kỳ qu&aacute;i ph&aacute;t sinh?&quot;<br />\r\n<br />\r\n&quot;Bổn trấn kh&ocirc;ng c&oacute; b&atilde;i tha ma, ch&uacute;ng ta tổ ti&ecirc;n đi ra tu ti&ecirc;n giả, b&atilde;i tha ma hội d&agrave;nh dụm &Acirc;m kh&iacute;, rất dễ d&agrave;ng xuất hiện quỷ vật, nếu l&agrave; c&oacute; nh&acirc;n sau khi chết kh&ocirc;ng người mai t&aacute;ng, chất nhi sẽ ph&aacute;i người đem thi thể thi&ecirc;u hủy, tuyệt kh&ocirc;ng cho ph&eacute;p loạn mai t&aacute;ng loạn t&aacute;ng, gần nhất cũng kh&ocirc;ng c&oacute; c&aacute;i g&igrave; chuyện kỳ qu&aacute;i ph&aacute;t sinh!&quot; Vương Thanh V&acirc;n một năm một mười trả lời.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng đ&uacute;ng, Tam b&aacute; c&ocirc;ng, hơn một th&aacute;ng trước, Đậu Hũ T&acirc;y Thi tiểu Phượng mất t&iacute;ch.&quot; Mập l&ugrave;n bộ kho&aacute;i một ch&uacute;t do dự, nhắc nhở.<br />\r\n<br />\r\nVương Trường Sinh nh&iacute;u m&agrave;y, trầm giọng hỏi: &quot;Mất t&iacute;ch? B&acirc;y giờ c&ograve;n chưa t&igrave;m tới nh&acirc;n a? C&aacute;i n&agrave;y Đậu Hũ T&acirc;y Thi l&agrave; lai lịch g&igrave;? Cũng l&agrave; Vương gia tộc nh&acirc;n a?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải, Cửu th&uacute;c, tiểu Phượng kh&ocirc;ng phải Vương gia tộc nh&acirc;n, n&agrave;ng tuổi phương thập b&aacute;t, lớn l&ecirc;n tương đối xinh đẹp, trong nh&agrave; c&ograve;n c&oacute; nhất c&aacute;i mắt m&ugrave; l&atilde;o nương, tiểu Phượng bu&ocirc;n b&aacute;n đậu hũ m&agrave; sống, bị nh&acirc;n l&ecirc;n nhất c&aacute;i Đậu Hũ T&acirc;y Thi ngoại hiệu, hơn một th&aacute;ng trước, tiểu Phượng đột nhi&ecirc;n kh&ocirc;ng ra b&agrave;y, bởi v&igrave; mẹ n&agrave;ng người yếu nhiều bệnh, tiểu Phượng muốn chiếu cố mẹ n&agrave;ng, c&oacute; l&uacute;c kh&ocirc;ng ra b&agrave;y, mọi người cũng kh&ocirc;ng cảm thấy kỳ qu&aacute;i, thẳng đến mấy ng&agrave;y về sau, tiểu Phượng gia trong truyền đến h&ocirc;i thối, Thi&ecirc;n Hổ tới cửa, l&uacute;c n&agrave;y mới ph&aacute;t hiện tiểu Phượng mẹ n&agrave;ng chết mất, bất qu&aacute; tiểu Phượng lại kh&ocirc;ng ở nh&agrave;, chất nhi ph&aacute;i người t&igrave;m một đoạn thời gian, cũng kh&ocirc;ng c&oacute; t&igrave;m được tiểu Phượng, liền đem thi thể đốt rụi.&quot;<br />\r\n<br />\r\n&quot;Tiểu Phượng mẹ n&agrave;ng l&agrave; thế n&agrave;o chết?&quot;<br />\r\n<br />\r\n&quot;Kinh Ngỗ t&aacute;c kiểm nghiệm, mẹ n&agrave;ng l&agrave; bị chết đ&oacute;i, tiểu Phượng từ tiểu c&ugrave;ng với n&agrave;ng nương sống nương tựa lẫn nhau, n&agrave;ng kh&ocirc;ng c&oacute; khả năng vứt xuống mẹ n&agrave;ng, vụ &aacute;n n&agrave;y tra x&eacute;t hơn một th&aacute;ng đều kh&ocirc;ng c&oacute; đầu mối, cũng liền kh&ocirc;ng giải quyết được g&igrave;, tiểu Phượng mẹ n&agrave;ng sau khi chết kh&ocirc;ng bao l&acirc;u, tựu c&oacute; tộc nh&acirc;n ngộ hại.&quot;<br />\r\n<br />\r\n&quot;Tam b&aacute; c&ocirc;ng, kh&ocirc;ng phải l&agrave; tiểu Phượng mẹ n&agrave;ng trở về t&igrave;m ch&uacute;ng ta b&aacute;o th&ugrave; đi! Ch&uacute;ng ta khi c&ograve;n sống kh&ocirc;ng c&oacute; hại qua n&agrave;ng a! Trước tuổi tiểu Phượng gia trong đ&oacute;i, ta trả lại cho n&agrave;ng đưa đi thập c&acirc;n gạo trắng ni! N&agrave;ng chết tựu đến hại ch&uacute;ng ta, lấy o&aacute;n trả ơn.&quot; Vương Thi&ecirc;n Hổ c&oacute; ch&uacute;t bất m&atilde;n n&oacute;i.<br />\r\n<br />\r\nVương Thanh V&acirc;n trừng Vương Thi&ecirc;n Hổ một ch&uacute;t, kh&ocirc;ng ch&uacute;t kh&aacute;ch kh&iacute; khiển tr&aacute;ch: &quot;Ngươi c&ograve;n c&oacute; mặt mũi n&oacute;i, ngươi &aacute;p chế tiểu Phượng, muốn cho n&agrave;ng gả cho ngươi, tiểu Phượng kh&ocirc;ng đ&aacute;p ứng, kia thập c&acirc;n gạo trắng kh&ocirc;ng phải l&agrave; bị ngươi cầm trở lại?&quot;<br />\r\n<br />\r\n&quot;C&oacute; việc n&agrave;y?&quot; Vương Trường Sinh nh&igrave;n về ph&iacute;a Vương Thi&ecirc;n Hổ, mặt mũi tr&agrave;n đầy nghi hoặc.<br />\r\n<br />\r\nVương Thi&ecirc;n Hổ nh&igrave;n thấy Vương Trường Sinh tr&ocirc;ng lại, sắc mặt xiết chặt, trung thực n&oacute;i ra: &quot;L&agrave; c&oacute; việc n&agrave;y, bất qu&aacute; ta về sau kh&ocirc;ng tiếp tục d&acirc;y dưa tiểu Phượng, cũng kh&ocirc;ng thể bởi v&igrave; chuyện n&agrave;y liền trở lại t&igrave;m ch&uacute;ng ta b&aacute;o th&ugrave; đi! L&yacute; Nhị Ma Tử trả nh&igrave;n l&eacute;n qua tiểu Phượng rửa r&aacute;y ni! Nếu như mẹ n&agrave;ng biến th&agrave;nh quỷ vật trở về b&aacute;o th&ugrave;, như thế n&agrave;o kh&ocirc;ng t&igrave;m L&yacute; Nhị Ma Tử? L&yacute; Nhị Ma Tử hiện tại trả sống thật tốt đ&acirc;y n&agrave;y!&quot;<br />\r\n<br />\r\n&quot;L&yacute; Nhị Ma Tử? Ph&aacute;i người đem hắn t&igrave;m đến, ta c&oacute; lời muốn hỏi hắn.&quot;<br />\r\n<br />\r\nVương Thanh V&acirc;n gật đầu, x&ocirc;ng b&ecirc;n người nam tử trung ni&ecirc;n ph&acirc;n ph&oacute; n&oacute;i: &quot;Thu Hiền, ngươi đi đem L&yacute; Nhị Ma Tử mời đến nơi n&agrave;y tới.&quot;<br />\r\n<br />\r\nNam tử trung ni&ecirc;n l&ecirc;n tiếng, quay người ly khai.<br />\r\n<br />\r\nVương Trường Sinh trầm ng&acirc;m một l&aacute;t, hỏi tiếp:<br />\r\n<br />\r\n&quot;Tiểu Phượng trước khi mất t&iacute;ch, ph&aacute;t sinh qua chuyện kỳ qu&aacute;i g&igrave; a? Hoặc l&agrave; n&oacute;i c&oacute; c&aacute;i g&igrave; dấu hiệu a?&quot;<br />\r\n<br />\r\nVương Thanh V&acirc;n lắc đầu, nh&igrave;n về ph&iacute;a c&aacute;i kh&aacute;c nh&acirc;n, hỏi: &quot;C&aacute;c ngươi c&oacute; biết kh&ocirc;ng? Biết tựu n&oacute;i cho Cửu th&uacute;c, d&aacute;m giấu diếm sự thật, nghi&ecirc;m trị kh&ocirc;ng tha.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng biết.&quot;<br />\r\n<br />\r\nỞ đ&acirc;y Vương gia tộc nh&acirc;n thẳng lắc đầu, trăm miệng một lời n&oacute;i.<br />\r\n<br />\r\nBọn hắn c&oacute; cuộc sống của m&igrave;nh, kh&ocirc;ng c&oacute; khả năng mỗi ng&agrave;y nh&igrave;n chằm chằm tiểu Phượng.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nVương Trường Sinh nh&igrave;n về ph&iacute;a Vương Thi&ecirc;n Hổ, n&oacute;i: &quot;Ngươi đ&acirc;y! Ngươi th&iacute;ch tiểu Phượng, c&oacute; biết hay kh&ocirc;ng?&quot;<br />\r\n<br />\r\nVương Thi&ecirc;n Hổ thẳng lắc đầu, nghi&ecirc;m t&uacute;c n&oacute;i ra: &quot;T&ocirc;n nhi ng&agrave;y thường tại nha m&ocirc;n l&agrave;m việc, kh&ocirc;ng c&oacute; khả năng mỗi ng&agrave;y nh&igrave;n chằm chằm tiểu Phượng, ph&iacute;a trước cầu th&acirc;n thất bại, trong nh&agrave; đem n&oacute;i ra một mối h&ocirc;n sự, ta vừa th&agrave;nh th&acirc;n, nơi n&agrave;o c&ograve;n d&aacute;m đi tr&ecirc;u chọc tiểu Phượng.&quot;<br />\r\n<br />\r\nL&uacute;c n&agrave;y, vương Thu Hiền mang theo một t&ecirc;n tr&ecirc;n mặt mọc đầy ma tử nam tử trung ni&ecirc;n đi đến.<br />\r\n<br />\r\nNam tử trung ni&ecirc;n mặt d&agrave;i như mặt ngựa, hai mắt kỳ tiểu, lại th&ecirc;m tr&ecirc;n mặt mọc đầy ma tử, cho người ta một loại kh&ocirc;ng tốt cảm gi&aacute;c.<br />\r\n<br />\r\nVương Trường Sinh nh&igrave;n về ph&iacute;a nam tử trung ni&ecirc;n, nh&iacute;u m&agrave;y hỏi: &quot;Ngươi ch&iacute;nh l&agrave; L&yacute; Nhị Ma Tử? Tiểu Phượng mất t&iacute;ch c&ugrave;ng ngươi c&oacute; quan hệ hay kh&ocirc;ng?&quot;<br />\r\n<br />\r\n&quot;T&ecirc;n b&eacute;o da đen đều hỏi qua nhiều lần, như thế n&agrave;o c&ograve;n hỏi? Đều n&oacute;i kh&ocirc;ng quan hệ với ta, lão tử l&agrave; nh&igrave;n l&eacute;n qua n&agrave;ng rửa r&aacute;y, khả n&agrave;ng mất t&iacute;ch c&ugrave;ng lão tử cái rắm quan hệ đều kh&ocirc;ng c&oacute;.&quot; L&yacute; Nhị Ma Tử nhếch nhếch miệng, lộ ra đầy miệng răng v&agrave;ng, l&agrave;m cho l&ograve;ng người sinh ch&aacute;n gh&eacute;t &aacute;c.<br />\r\n<br />\r\n&quot;L&yacute; Nhị Ma Tử, đem miệng đặt sạch sẽ điểm, ở trước mặt ngươi, l&agrave; ta Cửu th&uacute;c c&ocirc;ng.&quot; Vương Thu Hiền nhướng m&agrave;y, kh&ocirc;ng ch&uacute;t kh&aacute;ch kh&iacute; khiển tr&aacute;ch.<br />\r\n<br />\r\n&quot;Ngươi Cửu th&uacute;c c&ocirc;ng cũng kh&ocirc;ng phải lão tử Cửu th&uacute;c c&ocirc;ng, c&aacute;n lão tử th&iacute; sự!&quot; L&yacute; Nhị Ma Tử xem thường.<br />\r\n<br />\r\nVương Thi&ecirc;n Hổ sắc mặt lạnh xuống, mặt &acirc;m trầm n&oacute;i ra: &quot;L&yacute; Nhị Ma Tử, ta khuy&ecirc;n ngươi đem miệng đặt sạch sẽ điểm, Cửu tằng tổ hỏi ngươi c&aacute;i g&igrave;, ngươi tựu th&agrave;nh thật trả lời, muốn ăn cơm t&ugrave; cứ việc n&oacute;i thẳng.&quot;<br />\r\n<br />\r\nNghe lời n&agrave;y, L&yacute; Nhị Ma Tử c&oacute; phần kh&ocirc;ng t&igrave;nh nguyện n&oacute;i ra: &quot;C&oacute; lời g&igrave; cứ hỏi đi! Ta cam đoan th&agrave;nh thật trả lời.&quot;</p>', '2021-11-27 21:43:38', 7, 1),
(193, 51, 'Xác nhận hung phạm', '4', '<p>L&yacute; Nhị Ma Tử năm nay bốn mươi lẻ ba, ở trong nh&agrave; x&ecirc;́p hạng thứ hai, bởi v&igrave; tr&ecirc;n mặt mọc đầy mụn, được người xưng l&agrave; L&yacute; Nhị Ma Tử, t&ecirc;n thật đ&atilde; được mọi người qu&ecirc;n l&atilde;ng, bởi v&igrave; tướng mạo xấu x&iacute;, lại th&ecirc;m chơi bời l&ecirc;u lổng, trong nh&agrave; n&oacute;i cho hắn nhiều m&ocirc;n việc h&ocirc;n nh&acirc;n, kh&ocirc;ng c&oacute; c&aacute;i n&agrave;o gia đ&igrave;nh nguyện &yacute; đem khu&ecirc; nữ gả cấp L&yacute; Nhị Ma Tử.<br />\r\n<br />\r\nL&yacute; Nhị Ma Tử cha mẹ sau khi qua đời, liền kh&ocirc;ng c&oacute; nh&acirc;n quản hắn, L&yacute; Nhị Ma Tử l&agrave;m việc c&agrave;ng th&ecirc;m kh&ocirc;ng ki&ecirc;ng nể g&igrave; cả.<br />\r\n<br />\r\nD&ugrave; sao hắn một người ăn no, cả nh&agrave; kh&ocirc;ng đ&oacute;i bụng, l&agrave;m việc cũng kh&ocirc;ng c&oacute; nhiều cố kỵ như vậy, l&agrave; Vương Gia trấn thượng nổi danh lưu manh v&ocirc; lại.<br />\r\n<br />\r\nVương Gia trấn hơn ch&iacute;n th&agrave;nh b&aacute;ch t&iacute;nh đều họ Vương, L&yacute; Nhị Ma Tử kh&ocirc;ng d&aacute;m tr&ecirc;u chọc Vương gia tộc nh&acirc;n nữ quyến, liền đem ma trảo đưa về ph&iacute;a kh&aacute;c họ c&ocirc; nhi quả mẫu, tuổi trẻ mỹ mạo tiểu Phượng kh&ocirc;ng &iacute;t bị L&yacute; Nhị Ma Tử khi dễ.<br />\r\n<br />\r\nL&yacute; Nhị Ma Tử c&oacute; tặc t&acirc;m kh&ocirc;ng c&oacute; tặc đảm, lại th&ecirc;m Vương gia tộc nh&acirc;n ước th&uacute;c, L&yacute; Nhị Ma Tử cũng kh&ocirc;ng d&aacute;m l&agrave;m ẩu.<br />\r\n<br />\r\nTiểu Phượng sau khi mất t&iacute;ch, bộ kho&aacute;i t&igrave;m L&yacute; Nhị Ma Tử hỏi qua mấy c&acirc;u, Vương Trường Sinh mới mở miệng hỏi thăm tiểu Phượng sự t&igrave;nh, l&uacute;c n&agrave;y mới đưa tới L&yacute; Nhị Ma Tử kh&oacute; chịu.<br />\r\n<br />\r\n&quot;Ngươi một lần cuối c&ugrave;ng nh&igrave;n thấy tiểu Phượng l&agrave; l&uacute;c n&agrave;o? Gần nhất trong nh&agrave; người c&oacute; hay kh&ocirc;ng chuyện kỳ qu&aacute;i ph&aacute;t sinh? Ngoại trừ nh&igrave;n l&eacute;n tiểu Phượng rửa r&aacute;y, ngươi c&ograve;n c&oacute; hay kh&ocirc;ng đối tiểu Phượng l&agrave;m qua chuyện qu&aacute; đ&aacute;ng?&quot;<br />\r\n<br />\r\nVương Trường Sinh li&ecirc;n tiếp n&eacute;m ra ngo&agrave;i tam c&aacute;i vấn đề, &aacute;nh mắt nh&igrave;n chằm chằm L&yacute; Nhị Ma Tử.<br />\r\n<br />\r\nNh&acirc;n c&oacute; tam hồn thất ph&aacute;ch , dưới t&igrave;nh huống b&igrave;nh thường, nh&acirc;n sau khi chết, tam hồn thất ph&aacute;ch sẽ từ từ ti&ecirc;u t&aacute;n, tiểu Phượng mẹ n&agrave;ng l&agrave; bị chết đ&oacute;i, thi thể cũng đốt rụi, biến th&agrave;nh quỷ tỉ lệ rất thấp, so s&aacute;nh dưới, mất t&iacute;ch tiểu Phượng biến th&agrave;nh quỷ vật tỉ lệ lớn hơn.<br />\r\n<br />\r\nNh&acirc;n trước khi chết nếu l&agrave; c&oacute; o&aacute;n kh&iacute;, thi thể lại nh&eacute;t v&agrave;o &Acirc;m kh&iacute; dư thừa địa phương, sau khi chết c&oacute; rất lớn tỉ lệ biến th&agrave;nh quỷ.<br />\r\n<br />\r\nTiểu Phượng tuổi trẻ mỹ mạo, nếu như bị nam nh&acirc;n lăng nhục ch&iacute; tử, khẳng định c&ograve;n c&oacute; o&aacute;n kh&iacute;, biến th&agrave;nh quỷ vật tỉ lệ rất lớn.<br />\r\n<br />\r\nVương Trường Sinh kh&ocirc;ng c&oacute; đo&aacute;n sai, tiểu Phượng khẳng định đ&atilde; ngộ hại, l&uacute;c n&agrave;y mới kh&ocirc;ng c&oacute; trở về cho n&agrave;ng nương nấu cơm ăn, dẫn đến mẹ n&agrave;ng chết đ&oacute;i.<br />\r\n<br />\r\n&quot;Hơn một th&aacute;ng trước đi! Buổi trưa đi ngang qua n&agrave;ng tiệm đậu hũ, mua một điểm đậu hoa ăn, về phần trong nh&agrave; của ta, kh&ocirc;ng c&oacute; g&igrave; chuyện kỳ qu&aacute;i ph&aacute;t sinh, ta tựu nh&igrave;n l&eacute;n qua n&agrave;ng rửa r&aacute;y, chưa l&agrave;m qua sự t&igrave;nh kh&aacute;c, n&agrave;ng mất t&iacute;ch kh&ocirc;ng quan hệ với ta.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;C&oacute; phải hay kh&ocirc;ng l&agrave; ngươi giết tiểu Phượng, đem thi thể giấu ở nơi n&agrave;o rồi?&quot; Vương Trường Sinh sắc mặt lạnh lẽo, qu&aacute;t lớn.<br />\r\n<br />\r\nL&yacute; Nhị Ma Tử nghe lời n&agrave;y, như l&agrave; kiến b&ograve; tr&ecirc;n chảo n&oacute;ng đồng dạng vội v&agrave;ng xao động, giải th&iacute;ch: &quot;Thi&ecirc;n địa lương t&acirc;m a! Ta L&yacute; Nhị Ma Tử ngay cả g&agrave; cũng kh&ocirc;ng d&aacute;m s&aacute;t, ta n&agrave;o d&aacute;m s&aacute;t nh&acirc;n, c&aacute;c ngươi cũng kh&ocirc;ng thể đem c&aacute;i n&agrave;y nước bẩn hướng tr&ecirc;n người của ta giội.&quot;<br />\r\n<br />\r\n&quot;Hừ, ngươi c&ograve;n d&aacute;m n&oacute;i l&aacute;o, lần trước ngươi nửa đ&ecirc;m chạy đến tiểu Phượng gia trong, nếu kh&ocirc;ng phải tiểu Phượng h&eacute;t to, chỉ sợ cũng bị ngươi ch&agrave; đạp, khẳng định l&agrave; ngươi s&aacute;t hại tiểu Phượng, mau n&oacute;i, miễn cho gặp da thịt nỗi khổ.&quot; Vương Thi&ecirc;n Hổ hung thần &aacute;c s&aacute;t n&oacute;i.<br />\r\n<br />\r\nL&yacute; Nhị Ma Tử nghe vậy, sắc mặt trắng nhợt, phản b&aacute;c: &quot;T&ecirc;n b&eacute;o da đen, ngươi cũng kh&ocirc;ng thể l&agrave;m ẩu, ta L&yacute; Nhị Ma Tử li&ecirc;n s&aacute;t g&agrave; cũng kh&ocirc;ng d&aacute;m, ta n&agrave;o d&aacute;m s&aacute;t nh&acirc;n, ta lần kia l&agrave; uống say, tiểu Phượng nha đầu n&agrave;y mắt cao hơn đỉnh, c&oacute; thể n&agrave;ng c&ugrave;ng c&aacute;i n&agrave;o tiểu bạch kiểm chạy cũng kh&oacute; n&oacute;i, dựa v&agrave;o c&aacute;i g&igrave; đem b&ocirc; ỉa chụp tại tr&ecirc;n đầu ta.&quot;<br />\r\n<br />\r\nGiống L&yacute; Nhị Ma Tử loại n&agrave;y lấn yếu sợ mạnh lưu manh v&ocirc; lại, sợ nhất người trong quan phủ.<br />\r\n<br />\r\nNếu l&agrave; hắn thực bị bắt v&agrave;o trong đại lao, khẳng định sẽ bị vu oan gi&aacute; hoạ.<br />\r\n<br />\r\n&quot;Tiểu bạch kiểm? Người n&agrave;y l&agrave; ai? N&oacute;i r&otilde; r&agrave;ng.&quot;<br />\r\n<br />\r\nL&yacute; Nhị Ma Tử nhếch miệng, n&oacute;i: &quot;Một th&aacute;ng trước, c&oacute; một vị họ Triệu c&ocirc;ng tử ca đi ngang qua nơi đ&acirc;y, tiểu tử kia d&aacute;ng dấp bạch bạch tịnh tịnh, như c&aacute;i nương m&ocirc;n, kh&ocirc;ng biết tiểu tử kia cấp tiểu Phượng ăn c&aacute;i g&igrave; mứt t&aacute;o, tiểu Phượng khuya khoắt c&ugrave;ng hắn tại trong hẻm nhỏ anh anh em em, bất qu&aacute; bị ta bắt gặp, hừ, kh&ocirc;ng muốn mặt nữ nh&acirc;n, tại lão tử trước mặt giả thanh cao, t&agrave;i c&ugrave;ng đối phương nhận biết mấy ng&agrave;y, ngay tại trong hẻm nhỏ ấp ấp &ocirc;m một c&aacute;i, c&ograve;n kh&ocirc;ng phải gh&eacute;t bỏ lão tử kh&ocirc;ng c&oacute; tiền a?&quot;<br />\r\n<br />\r\n&quot;C&aacute;c ngươi ai biết c&aacute;i n&agrave;y Triệu c&ocirc;ng tử lai lịch? Hoặc l&agrave; gặp qua người n&agrave;y?&quot; Vương Trường Sinh nh&igrave;n về ph&iacute;a Vương Thanh V&acirc;n, trầm giọng hỏi.<br />\r\n<br />\r\nVương Thi&ecirc;n Hổ nghĩ nghĩ, c&oacute; phần kh&ocirc;ng x&aacute;c định n&oacute;i ra: &quot;Triệu c&ocirc;ng tử? Hắn c&oacute; phải hay kh&ocirc;ng mang theo thư đồng?&quot;<br />\r\n<br />\r\n&quot;Vậy ta kh&ocirc;ng r&otilde; r&agrave;ng, bất qu&aacute; c&aacute;i n&agrave;o tiểu bạch kiểm x&aacute;c thực c&oacute; c&aacute;i thư đồng, tuổi nhỏ tiền nhiều, c&ograve;n c&oacute; người hầu thiếp th&acirc;n hầu hạ, kh&oacute; tr&aacute;ch tiểu Phượng nha đầu kia hội coi trọng c&aacute;i n&agrave;o tiểu bạch kiểm.&quot;<br />\r\n<br />\r\n&quot;Một th&aacute;ng trước, c&oacute; mấy t&ecirc;n đi thi thư sinh đi qua ch&uacute;ng ta Vương Gia trấn, trong đ&oacute; c&oacute; một vị họ Triệu thư sinh, bởi v&igrave; c&aacute;i n&agrave;o Triệu c&ocirc;ng tử xuất thủ xa xỉ, ta đối với hắn ấn tượng tương đối s&acirc;u khắc, bất qu&aacute; ta nhớ kỹ bọn hắn rời đi thời điểm, kh&ocirc;ng c&oacute; mang theo nữ quyến a!&quot; Vương Thi&ecirc;n Hổ nghi ngờ n&oacute;i.<br />\r\n<br />\r\n&quot;Ngươi x&aacute;c định bọn hắn rời đi thời điểm kh&ocirc;ng mang lấy nữ quyến? Nh&acirc;n số c&oacute; sai ch&ecirc;nh lệch a?&quot;<br />\r\n<br />\r\nVương Thi&ecirc;n Hổ nghĩ nghĩ, gật đầu n&oacute;i ra: &quot;Ta x&aacute;c định, bọn hắn ở kh&aacute;ch sạn l&agrave; t&ocirc;n nhi trong nh&agrave; mở, ngoại trừ Triệu c&ocirc;ng tử mang theo thư đồng, c&aacute;i kh&aacute;c thư sinh đều l&agrave; lẻ loi một m&igrave;nh, hết thảy s&aacute;u người, bọn hắn hết thảy ở ba ng&agrave;y, bọn hắn rời đi thời điểm l&agrave; buổi s&aacute;ng, vừa l&uacute;c bị t&ocirc;n nhi thấy được, kh&ocirc;ng nhiều kh&ocirc;ng &iacute;t, ch&iacute;nh l&agrave; s&aacute;u người, về phần bọn hắn tr&ecirc;n đường c&oacute; hay kh&ocirc;ng mang l&ecirc;n tiểu Phượng, t&ocirc;n nhi cũng kh&ocirc;ng r&otilde; r&agrave;ng.&quot;<br />\r\n<br />\r\n&quot;Bọn hắn v&agrave;o kinh th&agrave;nh đi thi, tại sao lại tại tr&ecirc;n trấn tr&igrave; ho&atilde;n ba ng&agrave;y?&quot; Vương Trường Sinh đưa ra nghi vấn trong l&ograve;ng.<br />\r\n<br />\r\n&quot;Tựa như l&agrave; vị kia Triệu c&ocirc;ng tử ngẫu cảm phong h&agrave;n, vẫn l&agrave; ta gi&uacute;p hắn mời đến đại phu, vị kia Triệu c&ocirc;ng tử ốm yếu, tay tr&oacute;i g&agrave; kh&ocirc;ng chặt, nếu như tiểu Phượng bị người giết hại, hắn hẳn kh&ocirc;ng phải l&agrave; hung thủ, hắn kh&ocirc;ng c&oacute; động cơ g&acirc;y &aacute;n.&quot; Vương Thi&ecirc;n Hổ kh&aacute;ch quan ph&acirc;n t&iacute;ch n&oacute;i.<br />\r\n<br />\r\n&quot;Đ&uacute;ng rồi, ta tưởng khởi một sự kiện, nh&agrave; ta Vượng T&agrave;i bị người d&ugrave;ng Thạch Đầu đập chết, đ&uacute;ng l&agrave; mẹ n&oacute; x&uacute;i quẩy.&quot; L&yacute; Nhị Ma Tử n&oacute;i bổ sung.<br />\r\n<br />\r\n&quot;Nh&agrave; ngươi Vượng T&agrave;i c&oacute; phải hay kh&ocirc;ng ch&oacute; đen? Mấy ng&agrave;y nay c&oacute; c&aacute;i g&igrave; kh&aacute;c thường a?&quot; Vương Trường Sinh đ&ocirc;i mắt s&aacute;ng l&ecirc;n, truy vấn.<br />\r\n<br />\r\n&quot;L&agrave;m sao ngươi biết Vượng T&agrave;i l&agrave; đầu ch&oacute; đen?&quot; L&yacute; Nhị Ma Tử hơi kinh ngạc, nghĩ nghĩ, n&oacute;i ra: &quot;Vượng T&agrave;i l&agrave; cha ta dưỡng, dưỡng hơn mười năm, răng đều nhanh rơi sạch, cắn kh&ocirc;ng được nh&acirc;n, ta cũng liền kh&ocirc;ng buộc lại n&oacute;, bất qu&aacute; gần nhất kh&ocirc;ng biết chuyện g&igrave; xảy ra, vừa đến nửa đ&ecirc;m n&oacute; tựu r&eacute;o l&ecirc;n kh&ocirc;ng ngừng, ta trong cơn tức giận, liền đem n&oacute; bu&ocirc;̣c tại kho củi, buổi s&aacute;ng h&ocirc;m nay ph&aacute;t hiện, n&oacute; bị Thạch Đầu đập chết, kh&ocirc;ng biết c&aacute;i n&agrave;o thất đức gia hỏa nửa đ&ecirc;m chạy đến nh&agrave; ta trong, đem Vượng T&agrave;i đập chết.&quot;<br />\r\n<br />\r\nNghe lời n&agrave;y, Vương Trường Sinh c&oacute; thể x&aacute;c định, tiểu Phượng đ&atilde; chết, đồng thời biến th&agrave;nh quỷ vật hại người,<br />\r\n<br />\r\nCẩu l&agrave; ch&iacute; dương chi s&uacute;c, đối ứng Thập nhị địa chi Ngũ H&agrave;nh l&agrave; tuất thổ, th&uacute; thổ l&agrave; dương thổ, quỷ vật thuần &acirc;m, cẩu trời sinh khắc chế quỷ vật, đạo sĩ thường xuy&ecirc;n sử dụng m&aacute;u ch&oacute; đen trừ t&agrave; trị ma, tiểu Phượng biến th&agrave;nh quỷ vật, muốn t&igrave;m L&yacute; Nhị Ma Tử b&aacute;o th&ugrave;, bất qu&aacute; n&agrave;ng khẽ dựa gần L&yacute; Nhị Ma Tử trong nh&agrave;, tựu bị ch&oacute; đen ph&aacute;t hiện, ch&oacute; đen t&agrave;i sủa loạn kh&ocirc;ng th&ocirc;i.<br />\r\n<br />\r\nVương Trường Sinh kh&ocirc;ng đo&aacute;n sai, tiểu Phượng nay tối khẳng định sẽ ra ngo&agrave;i gia hại L&yacute; Nhị Ma Tử.<br />\r\n<br />\r\nL&yacute; Nhị Ma Tử cũng kh&ocirc;ng phải đồ đần, nh&igrave;n thấy nhiều như vậy Vương gia tộc l&atilde;o tụ tập c&ugrave;ng một chỗ, lại li&ecirc;n tưởng tới gần nhất &aacute;n mạng, c&oacute; ch&uacute;t khẩn trương mà hỏi: &quot;Kh&ocirc;ng phải l&agrave; c&aacute;i n&agrave;o s&aacute;t nh&acirc;n ma giết chết Vượng T&agrave;i a! Hắn chẳng lẽ để mắt tới ta rồi?&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nVương Trường Sinh lắc đầu, n&oacute;i ra: &quot;Kh&ocirc;ng phải, Huyện nha ph&aacute;t hiện một bộ nữ thi, sơ bộ ho&agrave;i nghi l&agrave; tiểu Phượng, t&igrave;m ngươi đến, l&agrave; tưởng x&aacute;c nhận ngươi c&oacute; phải hay kh&ocirc;ng hung thủ m&agrave; th&ocirc;i, hiện tại hỏi r&otilde; r&agrave;ng, vị kia Triệu c&ocirc;ng tử c&oacute; rất lớn động cơ g&acirc;y &aacute;n, ngươi c&oacute; thể đi về, đ&uacute;ng, chuyện n&agrave;y kh&ocirc;ng muốn đối ngoại tuy&ecirc;n dương.&quot;<br />\r\n<br />\r\nL&yacute; Nhị Ma Tử lu&ocirc;n cảm thấy c&oacute; ch&uacute;t kh&ocirc;ng đ&uacute;ng, thế nhưng l&agrave; hắn lại t&igrave;m kh&ocirc;ng ra Vương Trường Sinh trong lời n&oacute;i sơ hở, cũng kh&ocirc;ng c&oacute; suy nghĩ nhiều, quay người ly khai.<br />\r\n<br />\r\nĐ&aacute;nh chết L&yacute; Nhị Ma Tử cũng kh&ocirc;ng nghĩ ra, s&aacute;t nh&acirc;n ma kh&ocirc;ng phải nh&acirc;n, m&agrave; l&agrave; quỷ vật.<br />\r\n<br />\r\n&quot;Thu Hiền, đi b&ecirc;n ngo&agrave;i tr&ocirc;ng coi, kh&ocirc;ng cho ph&eacute;p người kh&ocirc;ng c&oacute; phận sự tới gần Từ đường, ta c&oacute; lời muốn c&ugrave;ng mọi người b&agrave;n giao.&quot; Vương Trường Sinh ph&acirc;n ph&oacute; n&oacute;i.<br />\r\n<br />\r\n&quot;L&agrave;, Cửu th&uacute;c c&ocirc;ng.&quot; Vương Thu Hiền l&ecirc;n tiếng, đi theo L&yacute; Nhị Ma Tử đi ra ngo&agrave;i.<br />\r\n<br />\r\n&quot;Cửu th&uacute;c, con kia nữ quỷ thật l&agrave; tiểu Phượng a?&quot; Vương Thanh V&acirc;n thấp giọng hỏi, trong mắt tr&agrave;n đầy vẻ lo lắng.<br />\r\n<br />\r\nVương Trường Sinh gật đầu, n&oacute;i ra: &quot;Ừm, ta kh&ocirc;ng đo&aacute;n sai, n&agrave;ng nay tối khẳng định sẽ ra ngo&agrave;i hại người, th&ocirc;ng tri một ch&uacute;t đi, sau khi trời tối, hết thảy tộc nh&acirc;n kh&ocirc;ng được một m&igrave;nh ra ngo&agrave;i, trong nh&agrave; kh&ocirc;ng c&oacute; thanh ni&ecirc;n trai tr&aacute;ng, nay tối đem đến thanh ni&ecirc;n trai tr&aacute;ng nhiều bản gia ở một đ&ecirc;m thượng, một gia đ&igrave;nh tối thiểu muốn ở t&aacute;m người, ăn uống ngủ nghỉ đều muốn c&ugrave;ng một chỗ, mặt kh&aacute;c, nghĩ biện ph&aacute;p nhường c&aacute;i n&agrave;y L&yacute; Nhị Ma Tử nay tối ngoan ngo&atilde;n ngủ ở nh&agrave; đại cảm gi&aacute;c, ngủ được c&agrave;ng chết c&agrave;ng tốt.&quot;<br />\r\n<br />\r\n&quot;Nhường L&yacute; Nhị Ma Tử nay ngủ trễ đ&ecirc;́n sít sao, t&ocirc;n nhi c&oacute; thể l&agrave;m được, đảm bảo hắn ngủ đến ng&agrave;y mai hừng đ&ocirc;ng đều vẫn chưa tỉnh lại.&quot; Vương Thi&ecirc;n Hổ vỗ ngực bảo đảm n&oacute;i, l&ograve;ng tin tr&agrave;n đầy.<br />\r\n<br />\r\n&quot;Cũng nghe được Cửu th&uacute;c lời n&oacute;i a? Đều trở về th&ocirc;ng tri tộc nh&acirc;n, nghi&ecirc;m ngặt dựa theo Cửu th&uacute;c ph&acirc;n ph&oacute; đi l&agrave;m, đối ngoại tuy&ecirc;n bố l&agrave; nghi&ecirc;m ph&ograve;ng s&aacute;t nh&acirc;n ma, vừa rồi Cửu th&uacute;c n&oacute;i lời, nhất c&uacute; cũng kh&ocirc;ng cho ph&eacute;p tiết lộ ra ngo&agrave;i, nếu như người n&agrave;o tiết lộ ra ngo&agrave;i, l&agrave;m trễ nải Cửu th&uacute;c bắt quỷ, nghi&ecirc;m trị kh&ocirc;ng tha.&quot; Vương Thanh V&acirc;n nh&igrave;n về ph&iacute;a c&aacute;i kh&aacute;c Vương gia tộc l&atilde;o, nghi&ecirc;m nghị n&oacute;i.<br />\r\n<br />\r\n&#39;V&acirc;ng, Tam b&aacute;.&#39; Vương gia tộc l&atilde;o nhao nhao miệng đầy đ&aacute;p ứng, lần lượt ly khai.</p>', '2021-11-27 21:44:08', 8, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(194, 51, 'Tiểu Phượng kinh lịch', '5', '<p>Vương Trường Sinh nh&igrave;n về ph&iacute;a Vương Thu Sinh, ph&acirc;n ph&oacute; n&oacute;i: &quot;Thu Sinh, đi chuẩn bị một chậu m&aacute;u ch&oacute; đen, ta có tác dụng lớn.&quot;<br />\r\n<br />\r\n&quot;L&agrave;, Cửu th&uacute;c c&ocirc;ng.&quot; Vương Thu Sinh đ&aacute;p ứng, quay người ly khai.<br />\r\n<br />\r\n&quot;Cửu th&uacute;c, b&acirc;y giờ sắc trời c&ograve;n sớm, Cửu th&uacute;c kh&ocirc;ng ch&ecirc;, đến chất nhi trong nh&agrave; nghỉ ngơi một l&aacute;t.&quot; Vương Thanh V&acirc;n thận trọng hỏi.<br />\r\n<br />\r\n&quot;Ừm, dẫn đường đi!&quot;<br />\r\n<br />\r\nVương Thanh V&acirc;n đại hỉ, vội v&agrave;ng dẫn Vương Trường Sinh hướng tự gia chỗ ở đi đến.<br />\r\n<br />\r\n&middot;&middot;&middot;&middot;&middot;&middot;<br />\r\n<br />\r\nT&ograve;a n&agrave;o đ&oacute; cỏ dại rậm rạp Tứ Hợp Viện, viện tử b&ecirc;n tr&aacute;i c&oacute; một c&aacute;i giếng nước, giếng nước thượng đ&oacute;ng nhất khối đại Thạch Đầu, khoảng c&aacute;ch giếng nước c&aacute;ch đ&oacute; kh&ocirc;ng xa, c&oacute; một gốc cao bốn, năm trượng c&acirc;y h&ograve;e.<br />\r\n<br />\r\nC&aacute;i n&agrave;y khỏa c&acirc;y h&ograve;e c&agrave;nh l&aacute; rậm rạp, dưới c&acirc;y mười phần m&aacute;t mẻ.<br />\r\n<br />\r\nHai t&ecirc;n nam đồng đang ở trong s&acirc;n chơi đ&ugrave;a, ngươi truy ta đuổi.<br />\r\n<br />\r\nMột l&aacute;t sau, bọn hắn c&oacute; thể l&agrave; chơi mệt rồi, đi đến dưới t&agrave;ng c&acirc;y ho&egrave; h&oacute;ng m&aacute;t.<br />\r\n<br />\r\n&quot;M&ugrave;i vị g&igrave;, thối qu&aacute; a!&quot; Một t&ecirc;n năm s&aacute;u tuổi nam đồng khinh ngửi mấy lần, tr&ecirc;n mặt lộ ra thần sắc ch&aacute;n gh&eacute;t.<br />\r\n<br />\r\n&quot;Đại Hổ ca, Nhị Ngưu gia ngay tại s&aacute;t v&aacute;ch, nh&agrave; hắn dưỡng mười mấy con đại heo m&acirc;̣p, hẳn l&agrave; m&ugrave;i cứt heo đạo.&quot; Một t&ecirc;n kh&aacute;c nam đồng xem thường.<br />\r\n<br />\r\n&quot;Cứt heo kh&ocirc;ng phải loại n&agrave;y vị, m&ugrave;i thối tựa như l&agrave; từ giếng nước trong truyền tới.&quot; Nam đồng cất bước hướng ph&iacute;a giếng nước đi đến.<br />\r\n<br />\r\nĐ&uacute;ng l&uacute;c n&agrave;y, một đạo phụ nh&acirc;n thanh &acirc;m bỗng nhi&ecirc;n vang l&ecirc;n: &quot;Đại hổ, đại hổ, ngươi ở đ&acirc;u ni!&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng tốt, mẹ ta t&igrave;m ta, Nhị Lăng tử, ng&agrave;y kh&aacute;c lại t&igrave;m ngươi chơi, ta đi trước.&quot; Nam đồng vội v&agrave;ng ly khai.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nMột t&ecirc;n kh&aacute;c nam đồng cũng kh&ocirc;ng c&oacute; lưu th&ecirc;m, đi theo ly khai.<br />\r\n<br />\r\nXuy&ecirc;n thấu qua một ch&uacute;t khe hở, c&oacute; thể nh&igrave;n thấy, một bộ hư thối nữ thi ng&acirc;m m&igrave;nh ở nước giếng b&ecirc;n trong, tr&ecirc;n thi thể đều sinh gi&ograve;i, mười phần buồn n&ocirc;n.<br />\r\n<br />\r\nCh&iacute;nh đối c&acirc;y h&ograve;e một gian ph&ograve;ng ốc, cửa ph&ograve;ng đ&oacute;ng chặt, chỉ c&oacute; một ch&uacute;t dương quang có th&ecirc;̉ xuy&ecirc;n thấu qua cũ n&aacute;t cửa sổ bay v&agrave;o tới.<br />\r\n<br />\r\nTrong ph&ograve;ng bố tr&iacute; đơn giản, một trương hồng sắc giường gỗ, giường gỗ b&ecirc;n cạnh c&oacute; một trương tủ gỗ, cửa tủ nửa đậy, mơ hồ có th&ecirc;̉ nh&igrave;n thấy một đạo b&oacute;ng xanh n&uacute;p ở trong tủ ch&eacute;n.<br />\r\n<br />\r\nB&oacute;ng xanh ch&iacute;nh l&agrave; biến th&agrave;nh quỷ vật tiểu Phượng.<br />\r\n<br />\r\nTiểu Phượng từ k&iacute; sự l&ecirc;n, liền kh&ocirc;ng c&oacute; gặp qua cha n&agrave;ng, nghe n&agrave;ng nương n&oacute;i, cha n&agrave;ng bỏ xuống mẹ con c&aacute;c n&agrave;ng hai, c&ugrave;ng người kh&aacute;c nữ nh&acirc;n ti&ecirc;u dao kho&aacute;i hoạt đi.<br />\r\n<br />\r\nMẫu th&acirc;n vất vả th&agrave;nh tật, người yếu nhiều bệnh, b&agrave;y biện nhất c&aacute;i tiệm đậu hũ, miễn cưỡng duy tr&igrave; hai mẹ con sinh hoạt.<br />\r\n<br />\r\nTrong nh&agrave; kh&ocirc;ng nam đinh, mẹ con c&aacute;c n&agrave;ng hai người trở th&agrave;nh những c&aacute;i kia lưu manh v&ocirc; lại khi dễ đối tượng.<br />\r\n<br />\r\nMười tuổi năm đ&oacute;, nhất c&aacute;i uống say l&atilde;o quang c&ocirc;n nửa đ&ecirc;m chạy đến tiểu Phượng gia trong, muốn đối n&agrave;ng mẫu th&acirc;n B&aacute; Vương ngạnh thượng cung, tiểu Phượng mẫu th&acirc;n thề sống chết kh&ocirc;ng theo, tiểu Phượng lớn tiếng gọi tới h&agrave;ng x&oacute;m, h&ugrave; chạy l&atilde;o quang c&ocirc;n, bất qu&aacute; n&agrave;ng mẫu th&acirc;n đang c&ugrave;ng l&atilde;o quang c&ocirc;n xoay đ&aacute;nh tr&uacute;ng, con mắt bị l&agrave;m m&ugrave;.<br />\r\n<br />\r\nSau đ&oacute;, l&atilde;o quang c&ocirc;n bồi cho tiểu Phượng gia ba trăm văn tiền.<br />\r\n<br />\r\nTừ đ&acirc;y, mười tuổi tiểu Phượng gánh vác l&ecirc;n gia đ&igrave;nh tr&aacute;ch nhiệm, tại mắt m&ugrave; mẫu th&acirc;n chỉ đạo dưới, học l&agrave;m đậu hũ, dựa v&agrave;o b&aacute;n đậu hũ m&agrave; sống.<br />\r\n<br />\r\nMười lăm mười s&aacute;u tuổi về sau, đến th&agrave;nh th&acirc;n ni&ecirc;n kỷ, l&agrave;m mai b&agrave; mối cơ hồ đạp ph&aacute; tiểu Phượng gia trong c&aacute;nh cửa.<br />\r\n<br />\r\nTiểu Phượng ki&ecirc;n tr&igrave; muốn dẫn mẫu xuất gi&aacute;, m&agrave; lại muốn cho mười lượng bạc l&agrave;m s&iacute;nh lễ, nh&agrave; trai đều kh&ocirc;ng thể tiếp nhận.<br />\r\n<br />\r\nMười t&aacute;m tuổi năm đ&oacute;, tiểu Phượng l&agrave;m quen một vị kinh th&agrave;nh đi thi họ Triệu c&ocirc;ng tử.<br />\r\n<br />\r\nTriệu c&ocirc;ng tử đối tiểu Phượng vừa thấy đ&atilde; y&ecirc;u, vỗ bộ ngực đ&aacute;p ứng tiểu Phượng n&oacute;i l&ecirc;n lưỡng điều kiện, thậm ch&iacute; nguyện &yacute; v&igrave; tiểu Phượng, kh&ocirc;ng v&agrave;o kinh th&agrave;nh đi thi, trước ti&ecirc;n đem việc h&ocirc;n nh&acirc;n l&agrave;m lại n&oacute;i.<br />\r\n<br />\r\nTriệu c&ocirc;ng tử tại tr&ecirc;n trấn ở ba ng&agrave;y, mỗi lần gặp mặt đều mang cho tiểu Phượng một ch&uacute;t tiểu lễ vật, đối tiểu Phượng quan t&acirc;m đầy đủ, t&ugrave; binh tiểu Phượng phương t&acirc;m.<br />\r\n<br />\r\nTại nhất c&aacute;i đ&ecirc;m tối, Triệu c&ocirc;ng tử cầm giữ kh&ocirc;ng được, đối tiểu Phượng giở tr&ograve;, bất qu&aacute; bị tiểu Phượng h&agrave;ng x&oacute;m L&yacute; Nhị Ma Tử bắt gặp.<br />\r\n<br />\r\nHai người chạy đến vứt bỏ dương trạch hẹn h&ograve;, tại Triệu c&ocirc;ng tử dỗ ngon dỗ ngọt dưới, hai người lấy trời l&agrave;m chăn, lấy đất l&agrave;m giường, sinh gạo nấu th&agrave;nh cơm.<br />\r\n<br />\r\nTriệu c&ocirc;ng tử vừa chiếm tiểu Phượng trong sạch, đảo mắt liền n&oacute;i muốn l&ecirc;n kinh đi thi, thu hoạch được c&ocirc;ng danh trở lại t&igrave;m tiểu Phượng, tiểu Phượng tự nhi&ecirc;n kh&ocirc;ng đ&aacute;p ứng, hai người c&atilde;i v&atilde;, trong cơn tức giận, tiểu Phượng muốn đi b&aacute;o quan, c&aacute;o trạng Triệu c&ocirc;ng tử x&acirc;m chiếm trong sạch của n&agrave;ng.<br />\r\n<br />\r\nTriệu c&ocirc;ng tử li&ecirc;n tục thuyết phục, tiểu Phượng ch&iacute;nh l&agrave; kh&ocirc;ng đ&aacute;p ứng, muốn Triệu c&ocirc;ng tử lập tức cưới n&agrave;ng về nh&agrave;.<br />\r\n<br />\r\nN&agrave;ng lại kh&ocirc;ng ngốc, Triệu c&ocirc;ng tử nếu l&agrave; thật thi đậu c&ocirc;ng danh, ai c&oacute; thể đảm bảo Triệu c&ocirc;ng tử nhất định trở về t&igrave;m n&agrave;ng.<br />\r\n<br />\r\nTriệu c&ocirc;ng tử bị tiểu Phượng &eacute;p, lộ ra khu&ocirc;n mặt dữ tợn, hắn d&ugrave;ng đọc s&aacute;ch viết chữ hai tay, b&oacute;p chết tiểu Phượng, cũng đem tiểu Phượng thi thể n&eacute;m đến giếng nước trong, d&ugrave;ng nhất khối đại Thạch Đầu che lại miệng giếng.<br />\r\n<br />\r\nBởi v&igrave; s&aacute;t v&aacute;ch c&oacute; nh&acirc;n dưỡng mười mấy con đại heo m&acirc;̣p, thi thể tản ra h&ocirc;i thối cũng kh&ocirc;ng c&oacute; g&acirc;y n&ecirc;n ch&uacute; &yacute; của những người kh&aacute;c.<br />\r\n<br />\r\nTiểu Phượng chết rất oan, n&agrave;ng vạn vạn nghĩ kh&ocirc;ng ra, mới vừa rồi c&ograve;n đối n&agrave;ng dỗ ngon dỗ ngọt t&igrave;nh lang, trong nh&aacute;y mắt tựu b&oacute;p chết n&agrave;ng.<br />\r\n<br />\r\nO&aacute;n kh&iacute; của n&agrave;ng rất lớn, dương trạch hoang phế rất l&acirc;u, nhất trực kh&ocirc;ng c&oacute; người ở, bởi v&igrave; trong viện c&oacute; một gốc c&acirc;y h&ograve;e, ch&iacute;nh đối c&acirc;y h&ograve;e ph&ograve;ng, đồ d&ugrave;ng b&ecirc;n trong đều l&agrave; d&ugrave;ng h&ograve;e l&agrave;m bằng gỗ l&agrave;m, &Acirc;m kh&iacute; rất nặng, tiểu Phượng tam hồn thất ph&aacute;ch thụ &Acirc;m kh&iacute; tẩm bổ, chậm r&atilde;i biến th&agrave;nh quỷ vật.<br />\r\n<br />\r\nL&agrave;m tiểu Phượng ph&aacute;t hiện m&igrave;nh biến th&agrave;nh quỷ vật về sau, v&agrave;o l&uacute;c ban đ&ecirc;m, n&agrave;ng lập tức liền chạy về gia thăm hỏi mắt m&ugrave; l&atilde;o nương, l&agrave;m l&ograve;ng người đau l&agrave;, mẹ n&agrave;ng người yếu nhiều bệnh, kh&ocirc;ng c&oacute; chiếu cố cho n&agrave;ng, tươi sống chết đ&oacute;i.<br />\r\n<br />\r\nSự t&igrave;nh ph&aacute;t triển đến l&uacute;c n&agrave;y, tiểu Phượng cũng kh&ocirc;ng c&oacute; hại người t&acirc;m tư, n&agrave;ng chẳng c&oacute; mục đ&iacute;ch tr&ecirc;n đường du đ&atilde;ng.<br />\r\n<br />\r\nKh&ocirc;ng kh&eacute;o ch&iacute;nh l&agrave;, tiểu Phượng gặp mấy t&ecirc;n khi dễ qua mẹ con c&aacute;c n&agrave;ng lưu manh v&ocirc; lại, những n&agrave;y lưu manh v&ocirc; lại ăn nhiều rượu, n&oacute;i chuyện kh&ocirc;ng kinh đại n&atilde;o, cầm tiểu Phượng mẹ n&agrave;ng cầm tạm l&agrave;m đề t&agrave;i nói chuyện, th&ecirc;m mắm th&ecirc;m muối , tức giận đến tiểu Phượng trong l&ograve;ng kia cỗ o&aacute;n kh&iacute; c&agrave;ng l&uacute;c c&agrave;ng lớn.<br />\r\n<br />\r\nTiểu Phượng trở lại h&ograve;e l&agrave;m bằng gỗ l&agrave;m ph&ograve;ng về sau, c&agrave;ng nghĩ c&agrave;ng giận, cha n&agrave;ng bỏ xuống mẹ con c&aacute;c n&agrave;ng, l&atilde;o quang c&ocirc;n lộng m&ugrave; n&agrave;ng nương con mắt, Triệu c&ocirc;ng tử lừa th&acirc;n thể của n&agrave;ng trả giết chết n&agrave;ng, mẹ n&agrave;ng bởi vậy chết đ&oacute;i, sau khi chết c&ograve;n muốn bị nam nh&acirc;n chế giễu.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nĐều l&agrave; những n&agrave;y x&uacute; nam nh&acirc;n, n&agrave;ng mới c&oacute; thể bị nh&acirc;n b&oacute;p chết vứt x&aacute;c giếng nước, mẹ n&agrave;ng mới c&oacute; thể chết đ&oacute;i. N&agrave;ng hận chết những nam nh&acirc;n n&agrave;y.<br />\r\n<br />\r\nTrong cơn tức giận, tiểu Phượng l&agrave;m tối liền chạy đi một t&ecirc;n lưu manh v&ocirc; lại trong nh&agrave;, h&uacute;t kh&ocirc; hắn tinh kh&iacute;.<br />\r\n<br />\r\nNgay từ đầu, tiểu Phượng chỉ l&agrave; nghĩ ra khẩu &aacute;c kh&iacute;, bất qu&aacute; khi n&agrave;ng h&uacute;t kh&ocirc; t&ecirc;n kia lưu manh v&ocirc; lại tinh kh&iacute; về sau, n&agrave;ng ph&aacute;t hiện m&igrave;nh giống như mạnh mẽ hơn kh&ocirc;ng &iacute;t.<br />\r\n<br />\r\nNếm đến ngon ngọt về sau, tiểu Phong hoặc l&agrave; kh&ocirc;ng l&agrave;m, đ&atilde; l&agrave;m th&igrave; cho xong, bắt đầu tấp nập h&uacute;t thanh ni&ecirc;n tr&aacute;ng đinh tinh kh&iacute;, lớn mạnh ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\nN&agrave;ng khi c&ograve;n sống bị người khi dễ, sau khi chết muốn đem sổ s&aacute;ch đ&ograve;i lại.<br />\r\n<br />\r\nHết thảy khi dễ qua mẹ con c&aacute;c n&agrave;ng hai người, đều đ&aacute;ng chết.<br />\r\n<br />\r\nL&yacute; Nhị Ma Tử trong nh&agrave; dưỡng một đầu ch&oacute; đen, tiểu Phong khẽ dựa gần L&yacute; Nhị Ma Tử trụ sở, ch&oacute; đen tựu cuồng khiếu kh&ocirc;ng ngừng, đ&aacute;nh thức L&yacute; Nhị Ma Tử.<br />\r\n<br />\r\nTiểu Phong ngay từ đầu cầm ch&oacute; đen kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o, bởi v&igrave; n&agrave;ng qu&aacute; yếu.<br />\r\n<br />\r\nBất qu&aacute; khi n&agrave;ng h&uacute;t kh&ocirc; mấy tr&aacute;ng ni&ecirc;n nam tử tinh kh&iacute; về sau, n&agrave;ng ph&aacute;t hiện m&igrave;nh c&oacute; thể khống chế vật thể, tỉ như Thạch Đầu gỗ.<br />\r\n<br />\r\nL&agrave;m n&agrave;ng lần nữa đi v&agrave;o L&yacute; Nhị Ma Tử trong nh&agrave;, đầu kia ch&oacute; đen đối n&agrave;ng sủa loạn kh&ocirc;ng ngừng, c&ograve;n muốn nh&agrave;o l&ecirc;n cắn n&agrave;ng.<br />\r\n<br />\r\nCh&oacute; đen tiếng k&ecirc;u đ&aacute;nh thức L&yacute; Nhị Ma Tử, hắn đem ch&oacute; đen bu&ocirc;̣c tại kho củi, thuận tiện tiểu Phượng l&agrave;m việc.<br />\r\n<br />\r\nChờ L&yacute; Nhị Ma Tử lần nữa nằm ngủ về sau, tiểu Phượng khống chế nhất khối Thạch Đầu, đập chết ch&oacute; đen, bất qu&aacute; l&uacute;c n&agrave;y, sắc trời cũng sắp s&aacute;ng, tiểu Phượng chỉ c&oacute; thể trở về dương trạch , chờ đợi đ&ecirc;m tối gi&aacute;ng l&acirc;m.<br />\r\n<br />\r\nThời gian từng giờ tr&ocirc;i qua, sắc trời chậm r&atilde;i tối xuống.</p>', '2021-11-27 21:44:34', 9, 1),
(195, 52, 'Thần thông', '1', '<p>Đại Qu&yacute; vương triều Dật Quốc C&ocirc;ng phủ<br />\r\n<br />\r\nSở Ly k&eacute;o thuyền tr&ocirc;i tại tr&ecirc;n hồ, gi&oacute; hồ phả v&agrave;o mặt, nhẹ nh&agrave;ng khoan kho&aacute;i thấm người.<br />\r\n<br />\r\nDật Quốc C&ocirc;ng phủ l&agrave; một mảnh vạn khoảnh Cự Hồ, ch&iacute;n mươi ch&iacute;n t&ograve;a đảo, hắn phải đi Diễn Vũ Điện c&ugrave;ng Đ&ocirc;ng Hoa Vi&ecirc;n khoảng c&aacute;ch mười t&aacute;m dặm.<br />\r\n<br />\r\nThuyền nhỏ ph&iacute;a tr&ecirc;n, hắn v&oacute;c người trung đẳng, một bộ thanh sam phi&ecirc;u phi&ecirc;u, tướng mạo b&igrave;nh thường lại kh&aacute; c&oacute; anh tuấn uy vũ kh&iacute;, nh&igrave;n xem m&ecirc;nh m&ocirc;ng hồ nước, suy nghĩ bay xa.<br />\r\n<br />\r\nMười ch&iacute;n năm hai th&aacute;ng lẻ ba ng&agrave;y, đ&atilde; mười ch&iacute;n năm hai th&aacute;ng lẻ ba ng&agrave;y!<br />\r\n<br />\r\nĐường đường cao năng vật l&yacute; nghi&ecirc;n cứu vi&ecirc;n, tương lai tươi s&aacute;ng học b&aacute;, tương lai đại khoa học gia, th&iacute; nghiệm b&ecirc;n trong một c&aacute;i nho nhỏ sai lầm, bạo tạc sau n&agrave;y đến c&aacute;i n&agrave;y thế giới, chuyển thế đầu thai một lần nữa l&agrave;m người!<br />\r\n<br />\r\nMột t&ograve;a lầu cao thu v&agrave;o hắn tầm mắt.<br />\r\n<br />\r\nTrăm m&eacute;t cao lầu, thiểm thước thanh đồng quang mang, nặng nề s&acirc;m nghi&ecirc;m, hiển thị r&otilde; Quốc C&ocirc;ng Phủ uy nghi.<br />\r\n<br />\r\nTo&agrave; n&agrave;y thanh đồng cao lầu liền l&agrave; Diễn Vũ Điện!<br />\r\n<br />\r\nDiễn Vũ Điện ph&iacute;a trước l&agrave; một mảnh kho&aacute;ng đạt luyện v&otilde; trường, b&ugrave;n đỏ trải đất, mấy trăm người tại luyện c&ocirc;ng, hoặc luyện đao luyện kiếm luyện quyền luyện chưởng, hoặc tỷ th&iacute; với nhau, phi thường n&aacute;o nhiệt.<br />\r\n<br />\r\nSở Ly d&ugrave;ng thuyền l&ecirc;n bờ, đạp v&agrave;o luyện v&otilde; trường, đi chưa được mấy bước, bỗng nhi&ecirc;n l&oacute;e ra một c&aacute;i thanh ni&ecirc;n anh tuấn, mặt như ngọc, l&ocirc;ng m&agrave;y như kiếm, thần th&aacute;i phi dương.<br />\r\n<br />\r\nTh&acirc;n h&igrave;nh hắn cao, cao Sở Ly một c&aacute;i đầu, &ocirc;m vai nh&igrave;n xuống hắn: &quot;Họ Sở, ngươi tới đ&acirc;y l&agrave;m g&igrave; đ&oacute;? !&quot;<br />\r\n<br />\r\nSở Ly nh&iacute;u m&agrave;y: &quot;Tr&aacute;nh ra, ch&oacute; ngoan kh&ocirc;ng cản đường!&quot;<br />\r\n<br />\r\nThanh ni&ecirc;n anh tuấn ha ha cười n&oacute;i: &quot;Chẳng lẽ muốn luyện v&otilde;?&quot;<br />\r\n<br />\r\nSở Ly lạnh l&ugrave;ng nh&igrave;n hắn, tiếp tục đi l&ecirc;n ph&iacute;a trước: &quot;Tr&aacute;c Phi Dương, ta ng&atilde; xuống đất k&ecirc;u thảm, tin hay kh&ocirc;ng ngươi muốn ăn kh&ocirc;ng được lượn tới đi?&quot;<br />\r\n<br />\r\nPhủ vệ cấm chỉ tư đấu, kh&ocirc;ng chuẩn tự giết lẫn nhau, phủ vệ ph&acirc;n hộ vệ c&ugrave;ng thị vệ, một v&otilde; một văn, cả hai đặc biệt l&agrave; nghi&ecirc;m cấm tư đấu, nhẹ th&igrave; h&agrave;ng đẳng cấp, nặng th&igrave; trục xuất phủ.<br />\r\n<br />\r\nSở Ly l&agrave; chưa nhập phẩm thị vệ, Tr&aacute;c Phi Dương nhưng l&agrave; B&aacute;t phẩm hộ vệ, hộ vệ đối thị vệ động thủ l&agrave; tối kỵ, hắn ng&atilde; xuống đất k&ecirc;u thảm, Tr&aacute;c Phi Dương thi&ecirc;n t&agrave;i đi nữa cũng phải bị phạt nặng.<br />\r\n<br />\r\nTr&aacute;c Phi Dương m&agrave;y kiếm nh&iacute;u một c&aacute;i: &quot;Họ Sở , được, đủ v&ocirc; sỉ!&quot;<br />\r\n<br />\r\nSở Ly tiếp tục đi l&ecirc;n ph&iacute;a trước.<br />\r\n<br />\r\nTr&aacute;c Phi Dương nghi&ecirc;ng người tr&aacute;nh ra một bước, b&igrave;nh tĩnh khu&ocirc;n mặt tuấn t&uacute;: &quot;Họ Sở, ngươi chỉ c&oacute; thể d&ugrave;ng những n&agrave;y tiểu thủ đoạn!&quot;<br />\r\n<br />\r\nSở Ly khẽ n&oacute;i: &quot;Đối ph&oacute; ngươi kiểu n&agrave;y ngu xuẩn, tiểu thủ đoạn liền đủ!&quot;<br />\r\n<br />\r\nTr&aacute;c Phi Dương gắt gao nh&igrave;n hắn chằm chằm, nắm đấm nắm được c&agrave;ng ng&agrave;y c&agrave;ng gấp, tựa như sau một khắc liền vung ra, Sở Ly lại uể oải nh&igrave;n hắn, khi&ecirc;u kh&iacute;ch vị đạo thuần chất.<br />\r\n<br />\r\nTr&aacute;c Phi Dương bỗng nhi&ecirc;n lộ ra rực rỡ nụ cười, d&ugrave;ng sức giương tay: &quot;Triệu sư muội!&quot;<br />\r\n<br />\r\nNơi xa một c&aacute;i d&aacute;ng vẻ thướt tha mềm mại mỹ mạo thiếu nữ tha thướt đi tới, n&agrave;ng da thịt hơn tuyết, mắt hạnh liễu mi, kh&iacute; chất sở sở, &ocirc;n nhu như nước.<br />\r\n<br />\r\nSở Ly sắc mặt biến h&oacute;a, Triệu Dĩnh, c&ugrave;ng bọn hắn đồng thời v&agrave;o phủ, mỹ mạo, thiện lương, &ocirc;n nhu, hắn rất ưa th&iacute;ch, rất muốn đạt được n&agrave;ng!<br />\r\n<br />\r\n&quot;Tr&aacute;c sư huynh?&quot; Triệu Dĩnh nhẹ nh&agrave;ng đi tới, kinh ngạc n&oacute;i: &quot;Sở sư huynh?&quot;<br />\r\n<br />\r\nSở Ly ngửi ngửi n&agrave;ng mang đến nh&agrave;n nhạt m&ugrave;i thơm, mỉm cười: &quot;Triệu sư muội, đ&atilde; l&acirc;u kh&ocirc;ng gặp.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nHắn kể từ ph&acirc;n c&ocirc;ng tiến Đ&ocirc;ng Hoa Vi&ecirc;n, đa số thời gian đều đi T&agrave;ng Thư L&acirc;u, muốn d&ugrave;ng tri thức cải biến vận mệnh, hi hữu tới Diễn Vũ Điện.<br />\r\n<br />\r\nLần n&agrave;y tới Diễn Vũ Điện, l&agrave; tu luyện Tiểu Tẩy Mạch Quyết gặp được vấn đề, muốn nh&igrave;n một lần Tiểu Tẩy Mạch Quyết c&oacute; li&ecirc;n quan b&iacute; k&iacute;p c&ugrave;ng tu luyện b&uacute;t k&yacute;.<br />\r\n<br />\r\nTriệu Dĩnh cười n&oacute;i: &quot;Sở sư huynh, cung hỉ, Đ&ocirc;ng Hoa Vi&ecirc;n l&agrave; c&aacute;i mỹ soa đ&acirc;u.&quot;<br />\r\n<br />\r\nSở Ly cười gật đầu: &quot;Coi như kh&ocirc;ng tệ.&quot;<br />\r\n<br />\r\nĐ&ocirc;ng Hoa Vi&ecirc;n trực thuộc ở tam tiểu thư Ti&ecirc;u Kỳ Ngọc Kỳ đảo, vẻn vẹn hắn c&ugrave;ng L&yacute; Việt hai thị vệ, kh&ocirc;ng những thong dong tự tại, c&ograve;n c&oacute; cơ hội gặp mặt tam tiểu thư, một khi lấy được tam tiểu thư coi trọng, l&ecirc;n như diều gặp gi&oacute; ở trong tầm tay.<br />\r\n<br />\r\nTr&aacute;c Phi Dương h&iacute;t s&acirc;u một hơi, n&agrave;y gia hỏa vận kh&iacute; qu&aacute; tốt, hết lần n&agrave;y tới lần kh&aacute;c lại v&agrave;o Đ&ocirc;ng Hoa Vi&ecirc;n, l&atilde;o thi&ecirc;n qu&aacute; kh&ocirc;ng ra mắt, ch&iacute;nh m&igrave;nh kh&ocirc;ng phải hảo hảo thu thập hắn!<br />\r\n<br />\r\n&quot;Triệu sư muội, khỏi phải lý n&agrave;y phế phẩm, ta h&ocirc;m qua vừa mới luyện th&agrave;nh Ph&aacute; Vọng kiếm ph&aacute;p, luận b&agrave;n một lần th&ocirc;i?&quot;<br />\r\n<br />\r\n&quot;Tr&aacute;c sư huynh &mdash;&mdash;!&quot; Triệu Dĩnh nh&iacute;u m&agrave;y, cảm thấy Tr&aacute;c Phi Dương n&oacute;i chuyện kh&oacute; nghe.<br />\r\n<br />\r\n&quot;Hảo hảo, kh&ocirc;ng n&oacute;i hắn phế phẩm l&agrave; được!&quot; Tr&aacute;c Phi Dương bận bịu cười l&agrave;m l&agrave;nh, qu&eacute;t mắt một v&ograve;ng Sở Ly: &quot;Mặc d&ugrave; hắn liền l&agrave; c&aacute;i phế phẩm!&quot;<br />\r\n<br />\r\n&quot;C&ograve;n n&oacute;i!&quot; Triệu Dĩnh bất m&atilde;n trừng lớn mắt.<br />\r\n<br />\r\n&quot;Triệu sư muội, kh&ocirc;ng quan hệ, &quot; Sở Ly mỉm cười n&oacute;i: &quot;Đối loại n&agrave;y cuồng vọng tự đại ngu xuẩn, kh&ocirc;ng cần thiết t&iacute;nh to&aacute;n.&quot;<br />\r\n<br />\r\n&quot;Sở sư huynh!&quot; Triệu Dĩnh sẵng giọng.<br />\r\n<br />\r\nSở Ly đưa tay l&agrave;m đầu h&agrave;ng h&igrave;nh d&aacute;ng: &quot;Triệu sư muội, tới Đ&ocirc;ng Hoa Vi&ecirc;n chơi a, phong cảnh rất đẹp, L&yacute; huynh t&agrave;i nấu nướng cũng v&ocirc; c&ugrave;ng tốt.&quot;<br />\r\n<br />\r\n&quot;Tốt.&quot; Triệu Dĩnh cười h&iacute;p mắt n&oacute;i: &quot;Một mực muốn đi xem đ&acirc;u, Đ&ocirc;ng Hoa Vi&ecirc;n a. . .&quot;<br />\r\n<br />\r\n&quot;Hừ, c&oacute; g&igrave; c&oacute; thể nh&igrave;n!&quot; Tr&aacute;c Phi Dương bĩu m&ocirc;i: &quot;Bất qu&aacute; hoa hoa cỏ cỏ m&agrave; th&ocirc;i, có chút thời gian c&ograve;n kh&ocirc;ng bằng luyện v&otilde;, m&ecirc; muội mất cả &yacute; ch&iacute;!&quot;<br />\r\n<br />\r\n&quot;Chiếu ngươi n&oacute;i như vậy, tam tiểu thư c&ugrave;ng đại c&ocirc;ng tử đều l&agrave; m&ecirc; muội mất cả &yacute; ch&iacute;, kh&ocirc;ng bằng ngươi đi?&quot; Sở Ly giống như cười m&agrave; kh&ocirc;ng phải cười, t&acirc;y hoa vi&ecirc;n thuộc về đại c&ocirc;ng tử Ti&ecirc;u Thiết Ưng.<br />\r\n<br />\r\nTr&aacute;c Phi Dương khu&ocirc;n mặt tuấn t&uacute; đỏ l&ecirc;n, cười lạnh li&ecirc;n tục, quyết định kh&ocirc;ng c&ugrave;ng hắn đấu v&otilde; mồm.<br />\r\n<br />\r\n&quot;Tr&aacute;c sư huynh! Sở sư huynh!&quot; Triệu Dĩnh bất m&atilde;n sẵng giọng: &quot;C&aacute;c ngươi y&ecirc;n tĩnh một hồi c&oacute; được hay kh&ocirc;ng! . . . Sở sư huynh, ta sẽ đi qua chơi, ngươi c&oacute; việc trước hết mau l&ecirc;n.&quot;<br />\r\n<br />\r\nSở Ly thật s&acirc;u liếc nh&igrave;n n&agrave;ng một c&aacute;i: &quot;Triệu sư muội, c&oacute; v&oacute;c người một bộ tốt t&uacute;i da, lại v&igrave; tư lợi, l&acirc;u ng&agrave;y mới c&oacute; thể mới biết được nh&acirc;n t&acirc;m, sư muội kh&ocirc;ng n&ecirc;n t&ugrave;y tiện tin tưởng người kh&aacute;c!&quot;<br />\r\n<br />\r\n&quot;Họ Sở, ngươi c&oacute; &yacute; tứ g&igrave;! ?&quot; Tr&aacute;c Phi Dương khẽ n&oacute;i.<br />\r\n<br />\r\nSở Ly kh&ocirc;ng để &yacute; tới hắn, x&ocirc;ng l&ecirc;n Triệu Dĩnh cười &ocirc;m một c&aacute;i quyền.<br />\r\n<br />\r\nTriệu Dĩnh bận bịu k&eacute;o một lần Tr&aacute;c Phi Dương, Tr&aacute;c Phi Dương kh&ocirc;ng t&igrave;nh nguyện xoay người, trước khi đi thời khắc, thừa dịp Triệu Dĩnh kh&ocirc;ng ch&uacute; &yacute;, bỗng nhi&ecirc;n khẽ vươn tay.<br />\r\n<br />\r\n&quot;Ba!&quot; Hắn b&agrave;n tay phiến tại Sở Ly tr&ecirc;n mặt.<br />\r\n<br />\r\nTriệu Dĩnh bận bịu quay đầu nh&igrave;n.<br />\r\n<br />\r\nSở Ly thần sắc bất biến, giống như một chưởng n&agrave;y kh&ocirc;ng phải đ&aacute;nh v&agrave;o tr&ecirc;n mặt m&igrave;nh.<br />\r\n<br />\r\nHắn sớm nh&igrave;n thấy Tr&aacute;c Phi Dương t&acirc;m tư, lại cố t&igrave;nh kh&ocirc;ng c&oacute; trốn, cứ thế m&agrave; chống cự một bạt tai n&agrave;y.<br />\r\n<br />\r\nTr&aacute;c Phi Dương giống như cười m&agrave; kh&ocirc;ng phải cười, &ocirc;m một c&aacute;i quyền: &quot;Nha, thực thật xin lỗi, ta qu&aacute; kh&ocirc;ng cẩn thận, Sở sư đệ, kh&ocirc;ng sao a?&quot;<br />\r\n<br />\r\n&quot;Hảo thủ đoạn!&quot; Sở Ly nh&igrave;n chằm chằm Tr&aacute;c Phi Dương, khinh thường cười cười.<br />\r\n<br />\r\nPhủ quy kh&ocirc;ng chuẩn tự giết lẫn nhau, hắn như vậy giả bộ như sai lầm, lập tức xin lỗi, hơn nữa chỉ l&agrave; một b&agrave;n tay, rất kh&oacute; chịu đến trừng phạt.<br />\r\n<br />\r\nMột chưởng n&agrave;y ra sức cực kh&eacute;o l&eacute;o, thanh &acirc;m vang dội lại kh&ocirc;ng lưu chưởng ấn, thuần t&uacute;y v&igrave; vũ nhục hắn, ch&agrave; đạp tự t&ocirc;n của hắn c&ugrave;ng mặt mũi, để hắn tại Triệu Dĩnh b&ecirc;n cạnh mất mặt, ở trước mặt mọi người kh&ocirc;ng ng&oacute;c đầu l&ecirc;n được.<br />\r\n<br />\r\n&quot;Tr&aacute;c sư huynh &mdash;&mdash;!&quot; Triệu Dĩnh trầm xuống mặt ngọc.<br />\r\n<br />\r\n&quot;C&oacute; lỗi với c&oacute; lỗi với, ta thực kh&ocirc;ng phải cố &yacute;.&quot; Tr&aacute;c Phi Dương vội &ocirc;m quyền cười n&oacute;i: &quot;Chắc hẳn Sở sư đệ sẽ kh&ocirc;ng để &yacute;, đ&uacute;ng kh&ocirc;ng, Sở sư đệ?&quot;<br />\r\n<br />\r\nSở Ly quay đầu thản nhi&ecirc;n n&oacute;i: &quot;Triệu sư muội, được rồi.&quot;<br />\r\n<br />\r\nTriệu Dĩnh ngượng ng&ugrave;ng xem hắn, bận bịu k&eacute;o lấy Tr&aacute;c Phi Dương ly khai, lại n&aacute;n lại xuống dưới, hai người kh&ocirc;ng phải đ&aacute;nh l&ecirc;n tới kh&ocirc;ng thể!<br />\r\n<br />\r\nSở Ly nh&igrave;n chằm chằm Triệu Dĩnh thướt tha b&oacute;ng lưng kh&ocirc;ng nh&uacute;c nh&iacute;ch, sờ l&ecirc;n n&oacute;ng bỏng m&aacute; tr&aacute;i, khởi động Đại Vi&ecirc;n K&iacute;nh Tr&iacute; ngăn chặn s&ocirc;i tr&agrave;o lửa giận.<br />\r\n<br />\r\nHắn ưa th&iacute;ch Triệu Dĩnh, nghĩ ra được Triệu Dĩnh, nhưng Triệu Dĩnh thực chất b&ecirc;n trong vẫn l&agrave; khinh thị ch&iacute;nh m&igrave;nh, ch&iacute;nh thương hại!<br />\r\n<br />\r\nƯa th&iacute;ch nữ nh&acirc;n xem thường ch&iacute;nh m&igrave;nh, th&acirc;n v&igrave; nam nh&acirc;n, thực tế kh&ocirc;ng thể nhịn!<br />\r\n<br />\r\nTr&aacute;c Phi Dương, Ph&aacute; Vọng kiếm ph&aacute;p. . .<br />\r\n<br />\r\nHắn cười lạnh một tiếng, xuy&ecirc;n qua trong luyện v&otilde; trường đ&aacute;m người, tiến v&agrave;o Diễn Vũ Điện.<br />\r\n<br />\r\nCởi xuống thắt lưng mộc b&agrave;i, đưa cấp điện ph&iacute;a trước thanh ni&ecirc;n, thanh ni&ecirc;n &aacute;nh mắt như điện, qu&eacute;t mắt một v&ograve;ng, ra hiệu hắn c&oacute; thể đi v&agrave;o.<br />\r\n<br />\r\nĐập v&agrave;o mắt l&agrave; từng h&agrave;ng gi&aacute; s&aacute;ch, hắn dạo chơi m&agrave; đi, ngẫu nhi&ecirc;n dừng lại, đưa tay r&uacute;t ra một quyển s&aacute;ch, nhanh ch&oacute;ng lật một lượt, trả về tiếp tục đi, lại dừng lại, lại r&uacute;t một quyển s&aacute;ch lật một lượt.<br />\r\n<br />\r\nMột canh giờ, hắn lật ra hai mươi hai quyển s&aacute;ch.<br />\r\n<br />\r\nGiống như hắn đ&atilde; sớm biết những s&aacute;ch n&agrave;y vị tr&iacute;, trực tiếp đi qua r&uacute;t ra liền tốt, kỳ thật hắn l&agrave; lần đầu ti&ecirc;n tới, Tiểu Tẩy Mạch Quyết l&agrave; tại T&agrave;ng Thư L&acirc;u nh&igrave;n.<br />\r\n<br />\r\nTo&agrave;n bộ Diễn Vũ Điện đều hiện ra tại hắn n&atilde;o hải, mỗi một h&agrave;ng gi&aacute; s&aacute;ch, mỗi một quyển s&aacute;ch danh tự, r&otilde; r&agrave;ng, r&otilde; mồn một trước mắt.<br />\r\n<br />\r\nHắn v&ocirc; t&igrave;nh đi đến lầu hai, đi thẳng tới h&agrave;ng thứ ba gi&aacute; s&aacute;ch, tại tầng thứ hai r&uacute;t ra Ph&aacute; Vọng Kiếm Phổ, lật qua một lượt, sau đ&oacute; lại r&uacute;t ra bảy bản b&iacute; k&iacute;p, l&agrave; Ph&aacute; Vọng Kiếm Phổ tu luyện t&acirc;m đắc.<br />\r\n<br />\r\nHắn ly khai Diễn Vũ Điện l&uacute;c, n&atilde;o hải đ&atilde; lạc ấn ba mươi quyển b&iacute; k&iacute;p, hai mươi hai bản Tiểu Tẩy Mạch Quyết tu luyện b&uacute;t k&yacute;, một bản Ph&aacute; Vọng Kiếm Phổ, bảy bản Ph&aacute; Vọng Kiếm Tu luyện t&acirc;m đắc.<br />\r\n<br />\r\nHắn điều khiển thuyền nhỏ l&agrave;m tại tr&ecirc;n hồ, gi&oacute; m&aacute;t chầm chậm đến, nguy&ecirc;n bản hừng hực lửa giận đ&atilde; ti&ecirc;u t&aacute;n.<br />\r\n<br />\r\nTrong đầu ba mươi quyển b&iacute; k&iacute;p bắt đầu th&ocirc;ng hiểu đạo l&iacute;.<br />\r\n<br />\r\n&mdash;&mdash;<br />\r\n<br />\r\nGiữa trưa dương quang s&aacute;ng rực, hắn mặt &acirc;m trầm l&aacute;i thuyền trở về Đ&ocirc;ng Hoa Vi&ecirc;n.<br />\r\n<br />\r\nĐ&ocirc;ng Hoa Vi&ecirc;n l&agrave; một t&ograve;a phương vi&ecirc;n hai dặm tiểu đảo, chia l&agrave;m từng c&aacute;i một vườn hoa, mỗi vườn một chủng qu&yacute; hiếm hoa, cả t&ograve;a tiểu đảo nổi tr&ocirc;i m&ugrave;i hoa.<br />\r\n<br />\r\nĐảo b&ecirc;n tr&ecirc;n chỉ c&oacute; một t&ograve;a tiểu viện, Sở Ly đẩy cửa tiến viện, &acirc;m lương kh&iacute; tức phả v&agrave;o mặt m&agrave; đến, một c&acirc;y đại thụ che khuất nửa b&ecirc;n viện tử, khu đi nhiệt kh&iacute;.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nHắn tiến t&acirc;y ph&ograve;ng nhỏ, đề một bả Thanh Phong Kiếm ra đ&acirc;y, ở trong viện hết vung vẩy, chậm chạp như Th&aacute;i Cực Kiếm, một l&aacute;t liền ra một tầng mồ h&ocirc;i, c&aacute;i tr&aacute;n lan tr&agrave;n hiện ra.<br />\r\n<br />\r\nHắn đ&atilde; gặp qua l&agrave; kh&ocirc;ng qu&ecirc;n được, nh&igrave;n qua b&iacute; k&iacute;p một lượt tức lạc ấn n&atilde;o hải, mới từ Diễn Vũ Điện học được Ph&aacute; Vọng kiếm ph&aacute;p quả nhi&ecirc;n uy lực kinh người, nhưng cũng cực kỳ hao tổn kh&iacute; lực.<br />\r\n<br />\r\n&quot;Kẹt kẹt. . .&quot; Đại sảnh đi ra một cường tr&aacute;ng thanh ni&ecirc;n, đĩa tr&ograve;n mặt to, mắt nhỏ.<br />\r\n<br />\r\nHắn d&aacute;ng hắc h&ugrave;ng lung la lung lay, d&agrave;i duỗi người, đ&aacute;nh li&ecirc;n tục hai ng&aacute;p, mới uể oải n&oacute;i chuyện: &quot;Huynh đệ, sớm như vậy liền trở lại rồi?&quot;<br />\r\n<br />\r\nSở Ly chuy&ecirc;n ch&uacute; luyện kiếm, chỉ &quot;Ừ&quot; một tiếng.<br />\r\n<br />\r\nL&yacute; Việt, Cửu phẩm thị vệ, hai người c&ugrave;ng một chỗ chịu tr&aacute;ch nhiệm Đ&ocirc;ng Hoa Vi&ecirc;n, tốt ăn uống ham muốn, t&agrave;i nấu nướng v&ocirc; c&ugrave;ng tốt, v&otilde; c&ocirc;ng giỏi, lại sợ chết kh&ocirc;ng x&ocirc;ng Cửu phẩm lầu, cam t&acirc;m thị vệ.<br />\r\n<br />\r\nQuốc C&ocirc;ng Phủ thị vệ c&ugrave;ng hộ vệ đều ph&acirc;n Cửu phẩm, tiền c&ocirc;ng k&eacute;m mười lần.<br />\r\n<br />\r\nThị vệ tiền c&ocirc;ng &iacute;t, phẩm cấp muốn chịu tư lịch, ba năm Cửu phẩm, mười năm B&aacute;t phẩm, hai mươi năm Thất phẩm, bốn mươi năm Lục phẩm, bảy mươi năm Ngũ phẩm.<br />\r\n<br />\r\nHộ vệ l&ecirc;n đẳng cấp bất luận c&aacute;i kh&aacute;c chỉ nh&igrave;n v&otilde; c&ocirc;ng, x&ocirc;ng Cửu phẩm lầu, x&ocirc;ng qua một tầng l&ecirc;n Nhất phẩm.<br />\r\n<br />\r\nTiền c&ocirc;ng nhi&ecirc;̀u, c&ograve;n có th&ecirc;̉ thường xuy&ecirc;n xuất phủ l&agrave;m nhiệm vụ, sống được đa thải đa tư, kh&ocirc;ng giống thị vệ chỉ c&oacute; thể n&aacute;n lại phủ b&ecirc;n trong hầu hạ người, ph&agrave;m l&agrave; c&oacute; ch&uacute;t năng lực, đều l&agrave;m hộ vệ.<br />\r\n<br />\r\nL&yacute; Việt vẫy vẫy cổ, duỗi duỗi ch&acirc;n, khớp xương ph&aacute;t ra từng chuỗi đ&ugrave;ng đ&ugrave;ng vang dội, sau đ&oacute; huy quyền bắt đầu luyện, quyền như lưu tinh, v&ugrave; v&ugrave; x&eacute; gi&oacute;.<br />\r\n<br />\r\n&quot;Huynh đệ, chớ luyện kiếm, theo ta luyện quyền đi!&quot; L&yacute; Việt miệng kh&ocirc;ng chịu ngồi y&ecirc;n.<br />\r\n<br />\r\nSở Ly kh&ocirc;ng c&oacute; l&ecirc;n tiếng.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng thể luyện nội c&ocirc;ng, luyện bộ quyền ph&aacute;p cường th&acirc;n kiện thể liền phải a, luyện g&igrave; đ&oacute; kiếm!&quot;<br />\r\n<br />\r\n&quot;Ngậm miệng!&quot; Sở Ly phun ra hai chữ, c&aacute;i tr&aacute;n một tầng đậu n&agrave;nh mồ h&ocirc;i.<br />\r\n<br />\r\nC&aacute;i n&agrave;y thế giới v&otilde; học hưng thịnh, luyện v&otilde; theo con n&iacute;t nắm l&ecirc;n.<br />\r\n<br />\r\nT&aacute;m tuổi đến mười t&aacute;m tuổi, thừa dịp kỳ dậy th&igrave;, luyện Tr&uacute;c Cơ T&acirc;m Ph&aacute;p, đem kinh mạch luyện được cường tr&aacute;ng cứng cỏi, nếu kh&ocirc;ng chịu kh&ocirc;ng được nội lực tr&ugrave;ng k&iacute;ch, c&oacute; nguy hiểm đến t&iacute;nh mạng.<br />\r\n<br />\r\nQua t&aacute;m tuổi đến mười t&aacute;m tuổi, một khi kinh mạch định h&igrave;nh, lại thần kỳ t&acirc;m ph&aacute;p cũng l&agrave;m nhiều c&ocirc;ng &iacute;t, phải bỏ ra gấp mấy lần thời gian Tr&uacute;c Cơ.<br />\r\n<br />\r\nMười t&aacute;m tuổi ph&iacute;a trước, hắn tại từ nhỏ đến lớn Thu Diệp Tự khổ tham Đại Tr&iacute; Độ Bản Nguy&ecirc;n Kinh, bỏ lỡ Tr&uacute;c Cơ.<br />\r\n<br />\r\nTinh Ph&aacute;ch l&agrave; thường nh&acirc;n gấp đ&ocirc;i, tinh thần mạnh mẽ, cho n&ecirc;n hắn cuối c&ugrave;ng luyện th&agrave;nh Đại Tr&iacute; Độ Bản Nguy&ecirc;n Kinh, th&agrave;nh tựu Đại Vi&ecirc;n K&iacute;nh Tr&iacute; thần th&ocirc;ng.<br />\r\n<br />\r\nTinh thần mạnh mẽ, hắn tu luyện duy tr&igrave; li&ecirc;n tục thời gian hơn nhiều thường nh&acirc;n, thần th&ocirc;ng tại th&acirc;n, quan chiếu nhập vi, nguy&ecirc;n nh&acirc;n hắn Tiểu Tẩy Mạch Quyết tiến độ thật nhanh năm năm liền c&oacute; thể Tr&uacute;c Cơ.<br />\r\n<br />\r\n&quot;Ha ha. . . , liền ngươi kia kiếm, g&agrave; cũng kh&ocirc;ng giết chết, ngươi c&ograve;n luyện c&aacute;i g&igrave; đ&oacute; sức lực?&quot;<br />\r\n<br />\r\nSở Ly bỗng nhi&ecirc;n thu v&agrave;o kiếm, khẽ n&oacute;i: &quot;So t&agrave;i một ch&uacute;t?&quot;<br />\r\n<br />\r\nHắn thở hồng hộc, to&agrave;n th&acirc;n mềm k&eacute;o d&agrave;i, thanh kiếm hướng tr&ecirc;n mặt đất một trụ, mới chống đỡ th&acirc;n thể, Ph&aacute; Vọng kiếm ph&aacute;p qu&aacute; hao tổn thể lực.</p>', '2021-11-27 21:46:26', 26, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(196, 52, 'Khô Vinh', '2', '<p>&quot;Ha ha!&quot; L&yacute; Việt cười to, quyền phong càng mạnh.<br />\r\n<br />\r\n&quot;Ngươi kh&ocirc;ng đ&aacute;nh lại được ta!&quot;<br />\r\n<br />\r\n&quot;Ha ha! Ha ha!&quot; L&yacute; Việt cười đến c&agrave;ng lớn tiếng.<br />\r\n<br />\r\n&quot;Cười c&aacute;i rắm!&quot; Sở Ly tức giận: &quot;Kh&ocirc;ng d&aacute;m so với?&quot;<br />\r\n<br />\r\nL&yacute; Việt thu thế, mặt kh&ocirc;ng đỏ kh&iacute; kh&ocirc;ng th&ocirc;, &yacute; cười kh&ocirc;ng giảm: &quot;Thật muốn so với?&quot;<br />\r\n<br />\r\n&quot;Ph&iacute; lời!&quot;<br />\r\n<br />\r\n&quot;Kh&agrave; kh&agrave;, đừng tổn thương ngươi, ta c&oacute; thể đ&aacute;nh thắng được cửu phẩm hộ vệ!&quot;<br />\r\n<br />\r\n&quot;Bớt d&agrave;i d&ograve;ng, so với kh&ocirc;ng thể so?&quot;<br />\r\n<br />\r\n&quot;Được, vậy th&igrave; nhiều lần.&quot;<br />\r\n<br />\r\nSở Ly đem gi&oacute; m&aacute;t kiếm ph&oacute;ng tới tr&ecirc;n b&agrave;n đ&aacute;, cầm lấy vỏ kiếm: &quot;Đừng tổn thương ngươi!&quot;<br />\r\n<br />\r\n&quot;Được... Được!&quot; L&yacute; Việt khẽ cắn răng.<br />\r\n<br />\r\nSở Ly vỏ ki&ecirc;́m vẩy một c&aacute;i: &quot;Đến đ&acirc;y đi!&quot;<br />\r\n<br />\r\n&quot;Xem quyền!&quot; L&yacute; Việt sợ lại kh&ocirc;ng động thủ, Sở Ly n&oacute;i ra c&agrave;ng lời kh&oacute; nghe, tới ch&iacute;nh l&agrave; một Hắc Hổ thật l&ograve;ng.<br />\r\n<br />\r\nHắn ho&agrave;n to&agrave;n tự tin, c&oacute; nội c&ocirc;ng c&ugrave;ng người b&igrave;nh thường lại như đại nh&acirc;n c&ugrave;ng đứa nhỏ.<br />\r\n<br />\r\nSở Ly khởi động Đại Vi&ecirc;n K&iacute;nh Tr&iacute;, trước mắt đột nhi&ecirc;n m&agrave; biến đổi.<br />\r\n<br />\r\nTrong mắt của hắn, L&yacute; Việt trong nh&aacute;y mắt đ&atilde; biến th&agrave;nh một bộ xương kh&ocirc;, theo hắn hơi suy nghĩ, bộ xương lập tức lại phụ tr&ecirc;n cơ nhục, m&agrave;u trắng kinh mạch c&ugrave;ng m&agrave;u xanh lục nội lực, L&yacute; Việt đầu &oacute;c xuất hiện từng c&aacute;i từng c&aacute;i cảnh tượng, Sở Ly trong l&ograve;ng ai quyền, Sở Ly ng&atilde; xuống đất, sắc mặt kh&oacute; coi chịu thua.<br />\r\n<br />\r\nL&yacute; Việt tất cả, cơ nhục nhỏ bé vận động, nội lực lưu chuyển, &yacute; nghĩ biến h&oacute;a, kh&ocirc;ng kh&ocirc;ng r&otilde; r&agrave;ng hiện ra với Sở Ly trước mắt, r&otilde; r&agrave;ng như quan k&iacute;nh.<br />\r\n<br />\r\nHắn vỏ kiếm vẩy một c&aacute;i rung một c&aacute;i, vừa lúc ở L&yacute; Việt lực mới lực cũ giao tiếp th&igrave;, cơ nhục vận động chậm nhất một khắc, v&igrave; lẽ đ&oacute; nh&igrave;n qua L&yacute; Việt b&eacute; ngoan chờ, kh&ocirc;ng hề phản kh&aacute;ng.<br />\r\n<br />\r\n&quot;A...&quot; L&yacute; Việt k&ecirc;u thảm thiết, d&ugrave;ng sức v&ograve; c&aacute;nh tay.<br />\r\n<br />\r\nSở Ly cười cợt: &quot;Trở lại?&quot;<br />\r\n<br />\r\n&quot;Ăn ta một quyền!&quot; L&yacute; Việt k&ecirc;u to.<br />\r\n<br />\r\nSở Ly khinh chuyển oản, đ&acirc;m một c&aacute;i, L&yacute; Việt dường như b&eacute; ngoan đem nắm đấm tập hợp đi tới, lại h&eacute;t thảm một tiếng, nửa người t&ecirc; dại, kh&ocirc;ng nghe sai khiến.<br />\r\n<br />\r\nSở Ly vỏ kiếm đứng ở hắn cảnh trước: &quot;L&yacute; huynh, ngươi chết rồi!&quot;<br />\r\n<br />\r\nL&yacute; Việt trợn mắt l&ecirc;n: &quot;N&agrave;y c&aacute;i g&igrave; kiếm ph&aacute;p?&quot;<br />\r\n<br />\r\n&quot;Ph&aacute; Vọng kiếm, trả lại?&quot;<br />\r\n<br />\r\n&quot;Trở lại! ... Ta c&ograve;n kh&ocirc;ng sử dụng tuyệt chi&ecirc;u đ&acirc;y!&quot; L&yacute; Việt hồng n&eacute;t mặt gi&agrave; nua hừ n&oacute;i.<br />\r\n<br />\r\nHắn cảm thấy vừa n&atilde;y thật giống ch&iacute;nh m&igrave;nh đưa tới cửa, Sở Ly biết được chi&ecirc;u số của ch&iacute;nh m&igrave;nh, sớm ph&aacute; giải, lần n&agrave;y đổi bộ chưa từng khiến qu&aacute;!<br />\r\n<br />\r\n&quot;Bao nhi&ecirc;u lần đều giống nhau, đến đ&acirc;y đi!&quot;<br />\r\n<br />\r\n&quot;Uống!&quot; L&yacute; Việt rống to, như một đạo ph&iacute;ch lịch nổ vang, thuận thế tr&ugrave;ng quyền, triển khai cả người thế v&otilde;.<br />\r\n<br />\r\nSở Ly lười biếng đ&acirc;m ra một chi&ecirc;u kiếm, L&yacute; Việt lần thứ hai &quot;A&quot; một tiếng h&eacute;t thảm, lảo đảo l&ugrave;i về sau.<br />\r\n<br />\r\nSở Ly đuổi tới một bước, vỏ kiếm lại li&ecirc;n lụy hắn cổ họng: &quot;Ngươi lại chết rồi!&quot;<br />\r\n<br />\r\n&quot;T&agrave; m&ocirc;n, thật mẹ kiếp bất thường!&quot; L&yacute; Việt v&ograve; cổ tay, kh&ocirc;ng r&otilde; trừng mắt Sở Ly: &quot;Ngươi n&ecirc;n chưa từng thấy ta bộ quyền ph&aacute;p n&agrave;y a.&quot;<br />\r\n<br />\r\nSở Ly thu hồi vỏ kiếm: &quot;Quyền ph&aacute;p g&igrave; đều giống nhau.&quot;<br />\r\n<br />\r\n&quot;Ph&aacute; Vọng kiếm ph&aacute;p thật như vậy thần?&quot; L&yacute; Việt kh&ocirc;ng tin tưởng n&oacute;i: &quot;Kh&ocirc;ng nội lực đều c&oacute; lớn như vậy uy lực?&quot;<br />\r\n<br />\r\nSở Ly gật đầu: &quot;Ngươi c&oacute; thể luyện một ch&uacute;t xem.&quot;<br />\r\n<br />\r\nHắn trước đ&acirc;y cũng luyện qua kiếm ph&aacute;p, c&ugrave;ng kiếm ph&aacute;p n&agrave;y kh&aacute;c biệt một trời một vực, tuyệt đ&aacute;nh kh&ocirc;ng lại Tr&aacute;c Phi Dương.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nPh&aacute; Vọng kiếm ph&aacute;p c&oacute; Độc C&ocirc; cửu kiếm m&ugrave;i vị, phối hợp hắn Đại Vi&ecirc;n K&iacute;nh Tr&iacute;, triển l&ecirc;n uy lực cực ở ngo&agrave;i mạnh, xem ra, ch&iacute;nh m&igrave;nh b&acirc;y giờ c&oacute; thể c&ugrave;ng Tr&aacute;c Phi Dương một trận chiến!<br />\r\n<br />\r\n&quot;Vẫn l&agrave; qu&ecirc;n đi, ta kh&ocirc;ng luyện kiếm thi&ecirc;n ph&uacute;.&quot; L&yacute; Việt bận bịu xua tay: &quot;Ngươi kiếm ph&aacute;p n&agrave;y nhất định c&oacute; thể x&ocirc;ng qua Cửu Phẩm l&acirc;u.&quot;<br />\r\n<br />\r\nSở Ly tức giận hừ n&oacute;i: &quot;&Aacute;m kh&iacute; đ&acirc;y!&quot;<br />\r\n<br />\r\n&quot;Ha ha...&quot; L&yacute; Việt bừng tỉnh, ha ha cười l&ecirc;n, t&acirc;m l&yacute; một hồi thoải m&aacute;i : &quot;Đ&aacute;ng tiếc ngươi kh&ocirc;ng tr&uacute;c cơ, bằng kh&ocirc;ng lại l&agrave; một Tr&aacute;c Phi Dương!&quot;<br />\r\n<br />\r\nV&agrave;o phủ một năm x&ocirc;ng qua hai l&acirc;u, Tr&aacute;c Phi Dương b&acirc;y giờ l&agrave; b&aacute;t phẩm hộ vệ, l&agrave; tiền đồ v&ocirc; lượng thi&ecirc;n t&agrave;i.<br />\r\n<br />\r\n&quot;Tr&aacute;c Phi Dương...&quot; Sở Ly kh&oacute;e miệng ngậm lấy một nụ cười lạnh l&ugrave;ng.<br />\r\n<br />\r\nL&yacute; Việt tập hợp lại đ&acirc;y, thấp giọng n&oacute;i: &quot;Huynh đệ, ngươi c&ugrave;ng Tr&aacute;c Phi Dương đến c&ugrave;ng c&oacute; c&aacute;i g&igrave; cừu?&quot;<br />\r\n<br />\r\nSở Ly r&ecirc;n một tiếng.<br />\r\n<br />\r\nL&yacute; Việt hiếu kỳ hỏi: &quot;C&aacute;c ngươi kh&ocirc;ng phải c&ugrave;ng trường sao? Quan hệ phải rất kh&aacute; a.&quot;<br />\r\n<br />\r\n&quot;Khỏi n&oacute;i hắn, mất hứng!&quot; Sở Ly xua tay, đem tr&ecirc;n b&agrave;n gi&oacute; m&aacute;t kiếm trở v&agrave;o bao.<br />\r\n<br />\r\nB&acirc;y giờ Tr&aacute;c Phi Dương thanh danh tước l&ecirc;n, ch&iacute;nh m&igrave;nh vẫn l&agrave; một kh&ocirc;ng c&oacute; tiếng tăm g&igrave; tạp dịch, địa vị kh&aacute;c nhau một trời một vực, Triệu Dĩnh c&oacute; thể nhận được trụ Tr&aacute;c Phi Dương truy đuổi gắt gao sao? Hắn kh&ocirc;ng khỏi bay l&ecirc;n cảm gi&aacute;c gấp g&aacute;p!<br />\r\n<br />\r\nSở Ly phun ra một c&aacute;i &uacute;c kh&iacute;, trở về t&acirc;y ph&ograve;ng nhỏ nằm tr&ecirc;n giường, nh&igrave;n chằm chằm khung trang tr&iacute; suy nghĩ xuất thần.<br />\r\n<br />\r\nNh&igrave;n khắp chư giới, thanh tịnh kh&ocirc;ng nhiễm, gọi l&agrave; Đại Vi&ecirc;n K&iacute;nh Tr&iacute;.<br />\r\n<br />\r\nĐại Vi&ecirc;n K&iacute;nh Tr&iacute; l&agrave; Th&ocirc;ng Thi&ecirc;n Nh&atilde;n c&ugrave;ng Tha T&acirc;m Th&ocirc;ng kết hợp.<br />\r\n<br />\r\nĐại Vi&ecirc;n K&iacute;nh Tr&iacute; phạm vi bao phủ b&ecirc;n trong, thi&ecirc;n địa vạn vật ho&agrave;n to&agrave;n thấm nhuần, bao qu&aacute;t th&acirc;n thể trong ngo&agrave;i, xương cốt, cơ nhục, huyết dịch, kinh mạch, tinh kh&iacute; c&ugrave;ng nội lực, thậm ch&iacute; đầu &oacute;c cảnh tượng.<br />\r\n<br />\r\nTr&iacute; Tuệ Độ Bản Nguy&ecirc;n kinh luyện th&agrave;nh sau, đ&atilde; gặp qua l&agrave; kh&ocirc;ng qu&ecirc;n được, t&acirc;m tr&iacute; vượt xa thế nh&acirc;n.<br />\r\n<br />\r\nLiền hắn thoả thu&ecirc; m&atilde;n nguyện, muốn thoả th&iacute;ch hưởng thụ thế giới n&agrave;y vinh hoa ph&uacute; qu&yacute;, chưởng v&ocirc; thượng quyền lợi, đ&ecirc;́n như m&acirc;y mỹ nữ.<br />\r\n<br />\r\nNghĩ tới đ&acirc;y hắn lắc đầu một c&aacute;i.<br />\r\n<br />\r\nSư phụ dựa v&agrave;o quan hệ đưa hắn tiến v&agrave;o Quốc C&ocirc;ng phủ, hắn vừa tiến đến mới biết, ch&iacute;nh m&igrave;nh kh&ocirc;ng tr&uacute;c cơ, lạc hậu với người, cần mấy lần khổ c&ocirc;ng mới c&oacute; thể truy được với.<br />\r\n<br />\r\nVừa v&agrave;o hầu m&ocirc;n s&acirc;u như biển, Dật Quốc C&ocirc;ng b&ecirc;n trong phủ kh&ocirc;ng phải ho&agrave; hợp &ecirc;m thấm.<br />\r\n<br />\r\nV&agrave;o phủ ban đầu, c&ugrave;ng đồng thời tiến v&agrave;o người mới tiếp thu phủ quy gi&aacute;o vi&ecirc;n, hắn kh&ocirc;ng thể luyện vũ, bị xem thường bị cười nhạo bị nhục nh&atilde;, c&oacute; một đối thủ một mất một c&ograve;n Tr&aacute;c Phi Dương &mdash; -- -- c&aacute;i tập v&otilde; thi&ecirc;n t&agrave;i, Tr&aacute;c Phi Dương đ&atilde; x&ocirc;ng qua Cửu Phẩm l&acirc;u, trở th&agrave;nh b&aacute;t phẩm thị vệ, m&agrave; hắn vẫn l&agrave; một kh&ocirc;ng ra g&igrave; tạp dịch.<br />\r\n<br />\r\nTr&aacute;c Phi Dương khắp nơi đối ph&oacute; với hắn, nếu kh&ocirc;ng c&oacute; hắn l&agrave;m người hai đời m&agrave; c&oacute; Đại Vi&ecirc;n K&iacute;nh Tr&iacute;, đ&atilde; sớm tan vỡ, b&acirc;y giờ luyện th&agrave;nh Ph&aacute; Vọng kiếm ph&aacute;p, cơn gi&acirc;̣n này kh&ocirc;ng cần nhịn nữa !<br />\r\n<br />\r\n&mdash;&mdash;<br />\r\n<br />\r\nNg&agrave;y thứ hai vừa rạng s&aacute;ng, hắn luyện hai lần Ph&aacute; Vọng kiếm ph&aacute;p, gi&aacute; chu vượt hồ đến T&agrave;ng thư l&acirc;u vị tr&iacute; tiểu đảo, tr&ecirc;n đảo chỉ c&oacute; một to&agrave; đồng thau cao lầu sừng sững đứng sừng sững.<br />\r\n<br />\r\nT&agrave;ng thư l&acirc;u l&ograve;e l&ograve;e tỏa &aacute;nh s&aacute;ng, người đứng ở dưới lầu như giun dế gi&ocirc;́ng như nhỏ b&eacute;.<br />\r\n<br />\r\nCửa đứng thẳng hai to&agrave; đồng thau cự đỉnh, ba tầng l&acirc;u cao.<br />\r\n<br />\r\nBậc thang bằng đ&aacute; xanh xếp đặt một tấm ghế Th&aacute;i sư, ghế tựa b&ecirc;n trong ngồi một t&oacute;c bạc b&agrave; l&atilde;o, gầy g&ograve; thấp b&eacute;, ch&iacute;nh vui vẻ nh&igrave;n một quyển s&aacute;ch, t&oacute;c bạc v&atilde;n đ&ecirc;́n một tia kh&ocirc;ng loạn, gọn g&agrave;ng nhanh ch&oacute;ng.<br />\r\n<br />\r\n&quot;T&ocirc;n b&agrave; b&agrave;!&quot; Sở Ly tiến l&ecirc;n h&agrave;nh lễ.<br />\r\n<br />\r\nT&ocirc;n b&agrave; b&agrave; con mắt nh&igrave;n chằm chằm thư, vung vung tay.<br />\r\n<br />\r\nSở Ly lại thi lễ, r&oacute;n r&eacute;n ti&ecirc;́n v&agrave;o l&acirc;u.<br />\r\n<br />\r\nHắn vừa bước v&agrave;o, đốn c&oacute; thời kh&ocirc;ng th&aacute;c loạn cảm gi&aacute;c, thật giống trở lại nguy&ecirc;n bản thế giới thư viện, một loạt b&agrave;i gi&aacute; s&aacute;ch toả khắp mi&ecirc;u tả hương.<br />\r\n<br />\r\nD&ugrave;ng xe ủi đất thức đọc s&aacute;ch ph&aacute;p, một gi&aacute; s&aacute;ch một gi&aacute; s&aacute;ch đọc, đ&atilde; đọc xong lầu một, lầu hai một nửa đ&atilde; đọc xong.<br />\r\n<br />\r\nTrong lầu chỉ c&oacute; hắn một người, phần lớn người đều đi Diễn V&otilde; điện, nơi đ&oacute; l&agrave; chuy&ecirc;n m&ocirc;n v&otilde; học b&iacute; k&iacute;p, đến T&agrave;ng thư l&acirc;u nh&acirc;n cực nhỏ.<br />\r\n<br />\r\nSở Ly từ khi đến T&agrave;ng thư l&acirc;u đọc s&aacute;ch, chỉ thấy được T&ocirc;n b&agrave; b&agrave; một người, lại kh&ocirc;ng ngộ qu&aacute; người b&ecirc;n ngo&agrave;i.<br />\r\n<br />\r\nHắn đ&aacute;ng tiếc lại thiết hỉ, hiển nhi&ecirc;n bọn họ kh&ocirc;ng trọng thị T&agrave;ng thư l&acirc;u những n&agrave;y tạp thư, kh&ocirc;ng r&otilde; r&agrave;ng tri thức tức sức mạnh, chuyển h&oacute;a th&agrave;nh tr&iacute; tuệ, l&agrave; kh&ocirc;ng thua với v&otilde; c&ocirc;ng sức mạnh to lớn.<br />\r\n<br />\r\n&quot;Ồ?&quot; Hắn bỗng nhi&ecirc;n dừng lại, h&iacute;t s&acirc;u một hơi đ&egrave; xuống vui sướng, lần thứ hai x&aacute;c nhận, tờ n&agrave;y x&aacute;c thực vẽ ra Kh&ocirc; Vinh thụ!<br />\r\n<br />\r\nLẽ n&agrave;o vận may của ch&iacute;nh m&igrave;nh tới rồi? Từ kh&ocirc;ng &ocirc;m hi vọng Kh&ocirc; Vinh thụ đồ thật xuất hiện , n&agrave;y kh&ocirc;ng kh&aacute;c n&agrave;o b&ecirc;n trong v&eacute; xổ số x&aacute;c suất!<br />\r\n<br />\r\nThu Diệp tự c&oacute; hai đại trấn tự bảo điển, một quyển Tr&iacute; Tuệ Độ Bản Nguy&ecirc;n kinh, một quyển Kh&ocirc; Vinh kinh, Tr&iacute; Tuệ Độ Bản Nguy&ecirc;n kinh hắn luyện th&agrave;nh, Kh&ocirc; Vinh kinh thiếu mất Kh&ocirc; Vinh thụ đồ kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o luyện.<br />\r\n<br />\r\nKh&ocirc; Vinh thụ, truyền thuyết đồ vật, t&iacute;ch trữ ở Tam Thập Tam Thi&ecirc;n b&ecirc;n tr&ecirc;n, kh&ocirc;ng cấu kh&ocirc;ng xấu, thường tr&uacute; thế gian, độ V&ocirc; Lượng Kiếp, tuổi thọ lượng.<br />\r\n<br />\r\nTam Thập Tam Thi&ecirc;n đồ vật l&agrave;m sao c&oacute; khả năng xuất hiện ở Nh&acirc;n Thế Gian, v&igrave; lẽ đ&oacute; Sở Ly kh&ocirc;ng l&agrave;m sao lưu &yacute;, một đời trước c&ograve;n s&oacute;t lại tập t&iacute;nh &mdash;&mdash; l&ograve;ng hiếu kỳ tr&ugrave;ng, để hắn thuận tiện hiểu thấu đ&aacute;o Kh&ocirc; Vinh kinh, d&ugrave; sao truyền thuyết luyện th&agrave;nh c&aacute;i n&agrave;y c&oacute; thể trường sinh bất tử.<br />\r\n<br />\r\nC&agrave;ng nh&igrave;n thấy Kh&ocirc; Vinh thụ đồ, ch&iacute;nh m&igrave;nh muốn thời cơ đến vận chuyển?<br />\r\n<br />\r\nHắn nhắm mắt, đầu &oacute;c trống rỗng, c&oacute; một luồng v&ocirc; lượng sức mạnh trở ngại ch&iacute;nh m&igrave;nh k&yacute; ức Kh&ocirc; Vinh thụ, th&uacute; vị, xem ra l&agrave; thật đồ!<br />\r\n<br />\r\nHắn xuất hiện ở cửa tr&ecirc;n bậc thang: &quot;T&ocirc;n b&agrave; b&agrave;, n&agrave;y tấm đồ rất c&oacute; th&uacute; , ta nghĩ vẽ.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc; Vinh thụ đồ?&quot; T&ocirc;n b&agrave; b&agrave; qu&eacute;t một ch&uacute;t, đứng dậy ti&ecirc;́n v&agrave;o l&acirc;u: &quot;Ngươi chờ một ch&uacute;t.&quot;<br />\r\n<br />\r\nNàng ti&ecirc;́n v&agrave;o l&acirc;u, rất mau ra đến, đưa cho hắn một cuộn tranh: &quot;C&aacute;i n&agrave;y l&agrave; L&yacute; Ngọc Minh b&uacute;t t&iacute;ch thực, đưa ngươi r&ocirc;̀i.&quot;<br />\r\n<br />\r\nSở Ly mở ra nh&igrave;n l&ecirc;n, nhất thời l&ugrave;i một bước.<br />\r\n<br />\r\nC&aacute;i n&agrave;y cũng l&agrave; một bức Kh&ocirc; Vinh thụ đồ, che trời đại thụ thật giống từ vẽ l&ecirc;n nhảy ra, khổng lồ m&ecirc;nh m&ocirc;ng kh&iacute; thế giống như thật, hắn lại c&oacute; nằm rạp quỳ xuống k&iacute;ch động.<br />\r\n<br />\r\nT&ocirc;n b&agrave; b&agrave; cười h&iacute;p mắt đ&aacute;nh gi&aacute; hắn: &quot;C&oacute; ch&uacute;t &yacute; nghĩa!&quot;<br />\r\n<br />\r\nMở ra n&agrave;y đồ kh&ocirc;ng quỳ xuống, Sở Ly l&agrave; đầu một, nàng cười thầm, c&aacute;i n&agrave;y tiểu Sở quả nhi&ecirc;n kh&ocirc;ng phải l&agrave; vật ph&agrave;m a.<br />\r\n<br />\r\n&quot;Đa tạ T&ocirc;n b&agrave; b&agrave;!&quot; Sở Ly kh&ocirc;ng kh&aacute;ch kh&iacute; quyển thật cuộn tranh, &ocirc;m quyền h&agrave;nh lễ.<br />\r\n<br />\r\n&quot;Ai...&quot; T&ocirc;n b&agrave; b&agrave; lắc đầu một c&aacute;i: &quot;Đọc s&aacute;ch người trẻ tuổi l&agrave; c&agrave;ng ng&agrave;y c&agrave;ng &iacute;t r&ocirc;̀i, mỗi người t&aacute;o bạo, tiền đồ c&oacute; hạn, hiếm thấy ngươi y&ecirc;u th&iacute;ch thư!&quot;<br />\r\n<br />\r\nSở Ly c&aacute;o từ rời đi, gi&aacute; thuyền về Đ&ocirc;ng Hoa vi&ecirc;n.<br />\r\n<br />\r\nThuyền nhỏ h&agrave;nh với hồ tr&ecirc;n, đ&aacute;y hồ l&agrave; lam sa, &aacute;nh đ&ecirc;́n hồ nước đặc biệt trong suốt, con c&aacute; thản nhi&ecirc;n tự đắc, từng mảng từng mảng hoa sen theo gi&oacute; đong đưa.<br />\r\n<br />\r\nSở Ly đón gi&oacute; m&aacute;t cười, c&oacute; t&acirc;n hi vọng, hết thảy đều trở n&ecirc;n mỹ hảo.<br />\r\n<br />\r\nChỉ mong Kh&ocirc; Vinh kinh c&oacute; thể b&ugrave; đắp tr&uacute;c cơ, c&oacute; thể l&agrave;m cho m&igrave;nh ch&iacute;nh thức trong tu luyện lực!<br />\r\n<br />\r\n&mdash;&mdash;<br />\r\n<br />\r\nCuộn tranh treo ở đầu giường, hắn ngồi xếp bằng m&agrave; ngồi, xem v&agrave;i lần, nhắm mắt lại, lại mở mắt một l&uacute;c, lại nhắm mắt, nhiều lần kh&ocirc;ng ngớt, thử cấu tr&uacute;c quan tưởng.<br />\r\n<br />\r\nBận việc một l&aacute;t nhưng kh&ocirc;ng thu hoạch được g&igrave;, hắn cắn răng một c&aacute;i, khởi động Đại Vi&ecirc;n K&iacute;nh Tr&iacute;, quan chiếu Kh&ocirc; Vinh thụ.<br />\r\n<br />\r\n&quot;Ầm ầm!&quot; Một tiếng vang trầm thấp, bầu trời thật giống nổ tung một đạo l&ocirc;i, hắn th&acirc;n thể chấn động, như bị s&eacute;t đ&aacute;nh b&ecirc;n trong, cả người kh&ocirc;ng thể động đậy một ch&uacute;t n&agrave;o.<br />\r\n<br />\r\nB&ecirc;n tai ầm ầm ầm vang l&ecirc;n kh&ocirc;ng ngừng, trước mắt hết thảy đều đang lay động, một nguồn sức mạnh hung m&atilde;nh tiến v&agrave;o th&acirc;n thể hắn, muốn đem hắn x&eacute; r&aacute;ch.<br />\r\n<br />\r\nTất cả bỗng nhi&ecirc;n bất động, dường như nh&aacute;y mắt vừa giống như rất l&acirc;u, ngũ quan kh&ocirc;i phục vận chuyển, đau đớn biến mất, chỉ c&oacute; vui sướng c&ugrave;ng thoải m&aacute;i.<br />\r\n<br />\r\nHắn đứng dậy đi tới ở giữa khu nh&agrave; nhỏ, ngửa đầu nh&igrave;n bầu trời, lộ ra say sưa mỉm cười.<br />\r\n<br />\r\nTh&acirc;n thể ở ngo&agrave;i v&ocirc; h&igrave;nh x&aacute;c bị đ&aacute;nh n&aacute;t, bị r&agrave;ng buộc ngũ quan một hồi mở tr&oacute;i, tự do tự tại, v&ocirc; hạn lan tr&agrave;n ra đi, c&ugrave;ng chu vi hồn nhi&ecirc;n h&ograve;a l&agrave;m một thể, tuy hai m&agrave; một.<br />\r\n<br />\r\nHắn ch&iacute;nh l&agrave; một th&acirc;n c&acirc;y, một cơn gi&oacute;, một tảng đ&aacute;, một mảnh đại địa...<br />\r\n<br />\r\nM&ograve; tr&ecirc;n một gốc c&acirc;y mai thụ, nhất thời nổi l&ecirc;n huyết nhục li&ecirc;n kết cảm gi&aacute;c, cảm quan tương th&ocirc;ng, hắn phảng phất h&oacute;a th&acirc;n mai thụ, đại địa ấm &aacute;p, &aacute;nh mặt trời long lanh, kh&ocirc;ng kh&iacute; thanh t&acirc;n, từng c&aacute;i bị mai thụ chuyển h&oacute;a th&agrave;nh sức mạnh, thoải m&aacute;i ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\nMai thụ kh&iacute; tức vọt tới th&acirc;n thể hắn, theo bản năng vận chuyển Tiểu tẩy mạch quyết, thuấn mắt một chu thi&ecirc;n, hiệu suất hơn xa từ trước.<br />\r\n<br />\r\nD&ugrave;ng mai thụ kh&iacute; tức luyện, chỉ cần b&ocirc;́n năm!<br />\r\n<br />\r\nHắn bu&ocirc;ng ra mai thụ, th&iacute; c&aacute;i kh&aacute;c thụ, c&acirc;y liễu, c&acirc;y th&ocirc;ng, hoa thụ... , sau khi l&agrave; c&aacute;c loại hoa cỏ, cuối c&ugrave;ng lựa chọn Thanh Tang hoa.<br />\r\n<br />\r\nThanh Tang hoa tương tự hoa c&uacute;c, mảnh mai tinh tế, kh&iacute; tức nhưng tinh khiết m&aacute;t lạnh hơn xa c&aacute;c loại thụ, ba năm c&oacute; thể luyện th&agrave;nh Tiểu tẩy mạch quyết!<br />\r\n<br />\r\nHắn hận kh&ocirc;ng thể ngửa mặt l&ecirc;n trời cười to, kh&aacute; lắm Kh&ocirc; Vinh kinh, quả nhi&ecirc;n lợi hại!</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nThanh Tang hoa vốn l&agrave; cửu phẩm linh dược, bởi v&igrave; đẹp đẽ, tam tiểu thư Ti&ecirc;u Kỳ y&ecirc;u th&iacute;ch, v&igrave; lẽ đ&oacute; Đ&ocirc;ng Hoa vi&ecirc;n loại m&ocirc;̣t chút.<br />\r\n<br />\r\nDược vi&ecirc;n ph&acirc;n cửu phẩm, dược vi&ecirc;n thị vệ so với hoa vi&ecirc;n thị vệ cấp bậc cao hơn nhiều, &iacute;t nhất b&aacute;t phẩm, m&agrave; tinh th&ocirc;ng hoa cỏ, mới c&oacute; tư c&aacute;ch tiến v&agrave;o cửu phẩm dược vi&ecirc;n.<br />\r\n<br />\r\nViệc cấp b&aacute;ch l&agrave; tấn phẩm!<br />\r\n<br />\r\nTấn phẩm, tiếp x&uacute;c được c&agrave;ng thượng phẩm linh thảo, Tiểu tẩy mạch quyết luyện được c&agrave;ng nhanh hơn, rất sớm tr&uacute;c cơ, nhất định đem Triệu Dĩnh đoạt tới!<br />\r\n<br />\r\n&quot;Huynh đệ, kh&ocirc;ng tốt rồi!&quot; L&yacute; Việt vội v&atilde; chạy tới, đ&aacute;nh g&atilde;y hắn ước mơ.<br />\r\n<br />\r\n&quot;Chuyện g&igrave;?&quot; Sở Ly hững hờ hỏi, nhưng ch&igrave;m đắm ở tươi đẹp cảm thụ b&ecirc;n trong, tương lai ước mơ b&ecirc;n trong.<br />\r\n<br />\r\n&quot;Tuyết Lan sinh bệnh , mau mau xem một ch&uacute;t đi!&quot; L&yacute; Việt bứt l&ecirc;n hắn liền chạy.<br />\r\n<br />\r\nSở Ly vừa nghe l&agrave; Tuyết Lan sinh bệnh, bận bịu sải bước đi tới Tuyết Lan phố.<br />\r\n<br />\r\nTuyết Lan trạng th&aacute;i như tầm thường hoa lan, l&aacute; c&acirc;y &oacute;ng &aacute;nh long lanh như băng tuyết, ch&iacute;nh l&agrave; thi&ecirc;n địa tạo h&oacute;a thần kỳ.<br />\r\n<br />\r\nTuyết Lan phố b&ecirc;n trong cộng 120 c&acirc;y Tuyết Lan, đi vào phảng phất bước v&agrave;o kh&ocirc;ng ch&uacute;t t&igrave; vết thế giới, t&acirc;m linh trở n&ecirc;n trong suốt, thực l&agrave; lớn lao hưởng thụ.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, năm mươi c&acirc;y Tuyết Lan l&aacute; c&acirc;y c&oacute; một hai hắc ban, đặc biệt ch&oacute;i mắt.<br />\r\n<br />\r\nHắn đến gần ngửi một c&aacute;i, tanh h&ocirc;i gay mũi, cau m&agrave;y n&oacute;i: &quot;Kh&ocirc;ng phải gỉ ban bệnh!&quot;<br />\r\n<br />\r\n&quot;C&aacute;i kia bệnh g&igrave;?&quot; L&yacute; Việt lo lắng n&oacute;i: &quot;Lần n&agrave;y c&oacute; thể xong đời !&quot;<br />\r\n<br />\r\nTuyết Lan ở b&ecirc;n ngo&agrave;i b&aacute;n mười lạng một c&acirc;y, nếu như dưỡng chết rồi, trong phủ phạt mười lăm hai, hơn năm mươi c&acirc;y đều chết rồi, vậy th&igrave; kh&ocirc;ng chỉ l&agrave; phạt tiền, khả năng h&agrave;ng phẩm, dời Đ&ocirc;ng Hoa vi&ecirc;n.<br />\r\n<br />\r\nỞ Đ&ocirc;ng Hoa vi&ecirc;n Ti&ecirc;u Dao tự tại, hơn nữa l&agrave; tam tiểu thư Ti&ecirc;u Kỳ vườn, gần thủy l&acirc;u đ&agrave;i, bị tam tiểu thư thưởng thức, chỗ tốt v&ocirc; c&ugrave;ng!<br />\r\n<br />\r\nSở Ly đưa tay, c&ugrave;ng Tuyết Lan cảm quan li&ecirc;n kết, tất cả kh&ocirc;ng chỗ che th&acirc;n.<br />\r\n<br />\r\nHắn cau m&agrave;y suy tư, ở k&yacute; ức trong cung điện t&igrave;m t&ograve;i.<br />\r\n<br />\r\nMột l&aacute;t sau, c&oacute; đoạt được: &quot;L&agrave; Thực Lộ tr&ugrave;ng!&quot;<br />\r\n<br />\r\n&quot;Thực Lộ tr&ugrave;ng?&quot; L&yacute; Việt m&ecirc; man trợn mắt l&ecirc;n.<br />\r\n<br />\r\nSở Ly n&oacute;i: &quot;L&yacute; huynh, ngươi c&oacute; thể lấy Đoạn Trường thảo c&ugrave;ng T&aacute;c Hồn thảo chứ?&quot;<br />\r\n<br />\r\n&quot;Đều l&agrave; kịch độc a, ... Ta l&agrave; cửu phẩm, chỉ c&oacute; thể lấy mười khỏa.&quot;<br />\r\n<br />\r\n&quot;Vậy th&igrave; c&aacute;c lấy mười khỏa, đem ch&uacute;ng n&oacute; trấp hỗn hợp.&quot;<br />\r\n<br />\r\n&quot;Được rồi!&quot; L&yacute; Việt vội v&atilde; đi ra ngo&agrave;i.<br />\r\n<br />\r\nSở Ly ngồi ở Tuyết Lan phố, hai tay c&aacute;c x&uacute;c một Tuyết Lan, đem khỏe mạnh Tuyết Lan kh&iacute; tức vận chuyển đ&atilde; c&oacute; hắc ban Tuyết Lan b&ecirc;n trong.<br />\r\n<br />\r\nThực Lộ tr&ugrave;ng trạng th&aacute;i như sợi t&oacute;c, m&oacute;ng tay trường, hỉ thực nước sương, nướt bọt kịch độc, nuốt chửng sinh cơ.<br />\r\n<br />\r\nC&oacute; hắc ban Tuyết Lan nguy&ecirc;n kh&iacute; đại thương, y&ecirc;n đầu đạp n&atilde;o, thậm ch&iacute; c&oacute; hấp hối, lập tức sẽ xong, nếu kh&ocirc;ng c&oacute; c&oacute; Kh&ocirc; Vinh kinh, chẳng mấy chốc sẽ chết đến v&agrave;i c&acirc;y.<br />\r\n<br />\r\nChờ L&yacute; Việt nh&acirc;́c theo hai c&aacute;i hộp chạy về đến, bệnh nặng nhất : coi trọng nhất năm c&acirc;y Tuyết Lan kh&ocirc;i phục tinh thần, Sở Ly cũng thở một hơi, trở về từ c&otilde;i chết!<br />\r\n<br />\r\nSở Ly cầm một c&aacute;i tế thảo c&ocirc;n, tiếu tr&ecirc;n thảo trấp cẩn thận từng li từng t&iacute; một b&ocirc;i l&ecirc;n, lấy độc c&ocirc;ng độc, c&oacute; thể l&agrave;m cho hắc ban kh&ocirc;i phục trắng như tuyết, cũng sẽ để kh&ocirc;ng phải hắc ban nơi biến th&agrave;nh đen.<br />\r\n<br />\r\nL&yacute; Việt kh&ocirc;ng d&aacute;m sờ chạm, hắn tay ch&acirc;n vụng về, kh&ocirc;ng l&agrave;m được Sở Ly như vậy ch&iacute;nh x&aacute;c tỉ mỉ.<br />\r\n<br />\r\n&quot;Huynh đệ, ta xin mời Mạnh l&atilde;o tới xem một ch&uacute;t đi.&quot; L&yacute; Việt nhỏ giọng hỏi.<br />\r\n<br />\r\nSở Ly l&uacute;c lắc tay tr&aacute;i.<br />\r\n<br />\r\nL&yacute; Việt thật kh&ocirc;ng tiện g&atilde;i đầu một c&aacute;i, động t&aacute;c n&agrave;y l&agrave; kh&ocirc;ng tin được Sở Ly, nhưng can hệ trọng đại, ổn thỏa là hơn.<br />\r\n<br />\r\nSở Ly kh&ocirc;ng để &yacute;, ch&iacute;nh m&igrave;nh thần th&ocirc;ng L&yacute; Việt kh&ocirc;ng biết, L&yacute; Việt lại nh&aacute;t gan, mọi việc cầu ổn.</p>', '2021-11-27 21:46:53', 12, 1),
(197, 52, 'Tấn đẳng cấp', '3', '<p>Một l&aacute;t, tiếng bước ch&acirc;n vang l&ecirc;n, L&yacute; Việt c&ugrave;ng ở một c&aacute;i b&eacute;o l&ugrave;n chắc nịch, tr&ograve;n vo &ocirc;ng l&atilde;o mặt sau, đầy mặt cười bồi, ch&acirc;n ch&oacute; dạng mười phần.<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m, viện B&aacute;ch Thảo cung phụng, lục phẩm thị vệ.<br />\r\n<br />\r\nHắn mặt b&eacute;o vi&ecirc;n v&ocirc; c&ugrave;ng, mắt nhỏ, nh&igrave;n thấy hắc ban, mắt nhỏ một hồi trợn tr&ograve;n, lớn tiếng n&oacute;i: &quot;C&ograve;n kh&ocirc;ng ngừng tay!&quot;<br />\r\n<br />\r\nSở Ly thả xuống tế thảo c&ocirc;n, &ocirc;m quyền h&agrave;nh lễ: &quot;Mạnh l&atilde;o.&quot;<br />\r\n<br />\r\n&quot;Tiểu Sở, ngươi l&agrave;m g&igrave; vậy!&quot; Mạnh Kh&aacute;nh L&acirc;m x&ocirc;ng tới, mạnh mẽ trừng hắn.<br />\r\n<br />\r\nHắn tiến đến một c&acirc;y Tuyết Lan hắc ban trước, nh&igrave;n một ch&uacute;t, lại ngửi một c&aacute;i, b&aacute;t tr&ecirc;n đất ngửi một c&aacute;i, lại phủng một c&aacute;i thổ thả trong miệng nếm trải thường.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải gỉ ban bệnh!&quot; Mạnh Kh&aacute;nh L&acirc;m cau m&agrave;y, phi phi thổ b&ugrave;n.<br />\r\n<br />\r\nL&yacute; Việt biết điều d&acirc;ng ch&eacute;n tr&agrave;, bồi cười.<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m mạnh mẽ thấu miệng, cau m&agrave;y, gỉ ban bệnh l&agrave; Tuyết Lan th&ocirc;ng thường bệnh, c&oacute; rất nhiều biến h&igrave;nh, m&agrave; những n&agrave;y hắc ban sạ xem như gỉ ban bệnh, rất c&oacute; lừa dối t&iacute;nh.<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m mắt nhỏ trừng mắt Sở Ly: &quot;Tiểu Sở, ngươi sẽ kh&ocirc;ng nắm gỉ ban bệnh tr&igrave; chứ?&quot;<br />\r\n<br />\r\nSở Ly n&oacute;i: &quot;L&agrave; Thực Lộ tr&ugrave;ng.&quot;<br />\r\n<br />\r\n&quot;Thực Lộ tr&ugrave;ng?&quot; Mạnh Kh&aacute;nh L&acirc;m chớp chớp mắt nhỏ, c&oacute; ch&uacute;t ấn tượng, hừ n&oacute;i: &quot;Ngươi chắc chắn?&quot;<br />\r\n<br />\r\n&quot;S&aacute;u phần mười đi.&quot;<br />\r\n<br />\r\n&quot;Tiểu tử ngươi, thật l&agrave; to gan!&quot; Mạnh Kh&aacute;nh L&acirc;m đem ch&eacute;n tr&agrave; một đệ, hừ n&oacute;i: &quot;Thật muốn tr&igrave; chết, ngươi đời n&agrave;y to&aacute;n xong!&quot;<br />\r\n<br />\r\nSở Ly cười cợt, tiến l&ecirc;n tiếp nhận ch&eacute;n tr&agrave;.<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m xem hắn ho&agrave;n to&agrave;n tự tin, hừ n&oacute;i: &quot;Muốn mấy ng&agrave;y thấy hiệu quả?&quot;<br />\r\n<br />\r\n&quot;B&ocirc;́n ng&agrave;y đầy đủ.&quot;<br />\r\n<br />\r\n&quot;Tốt lắm, liền chờ đợi xem, thật muốn tr&igrave; chết rồi, ngươi khỏi muốn lại ở lại nơi n&agrave;y!&quot;<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m vẩy tay &aacute;o xoay người liền đi, L&yacute; Việt cười theo c&ugrave;ng ở mặt sau, Sở Ly &ocirc;m một c&aacute;i quyền, thả xuống ch&eacute;n tr&agrave; tiếp tục b&ocirc;i l&ecirc;n thảo trấp.<br />\r\n<br />\r\n&mdash;&mdash;&mdash;&mdash;<br />\r\n<br />\r\nSở Ly ng&agrave;y đ&ecirc;m ở tại Tuyết Lan phố, c&oacute; bệnh tr&ugrave;ng, liền na di kh&iacute; tức tr&ocirc;i qua, L&yacute; Việt ở một b&ecirc;n đưa ăn đưa uống, ph&aacute;t hiện dị dạng.<br />\r\n<br />\r\nY&ecirc;n đầu đạp n&atilde;o, trạng th&aacute;i kh&ocirc;ng ổn Tuyết Lan, Sở Ly chạm lập tức sinh cơ bừng bừng, thật giống ăn bồi nguy&ecirc;n đan tự.<br />\r\n<br />\r\nHắn hiếu kỳ hỏi Sở Ly, Sở Ly n&oacute;i đ&acirc;y l&agrave; trời sinh bản lĩnh, để hắn ngo&agrave;i miệng đem trụ m&ocirc;n nhi, đừng đi ra ngo&agrave;i n&oacute;i lung tung, L&yacute; Việt hưng phấn vỗ ngực bảo đảm.<br />\r\n<br />\r\nĐ&ocirc;ng Hoa vi&ecirc;n tuy Ti&ecirc;u Dao, nhưng cũng tr&aacute;ch nhiệm trọng đại, gặp sự cố muốn bị phạt nặng, v&igrave; lẽ đ&oacute; hắn vừa nh&igrave;n thấy đậu phộng bệnh, liền lo sợ bất an, lo lắng lo lắng, ăn kh&ocirc;ng thơm ngủ kh&ocirc;ng được.<br />\r\n<br />\r\nHiện tại được rồi, Sở Ly c&oacute; bản l&atilde;nh n&agrave;y, ch&iacute;nh m&igrave;nh rốt cục c&oacute; thể ngủ tr&ecirc;n an giấc r&ocirc;̀i!<br />\r\n<br />\r\nBa ng&agrave;y qua đi, hắc ban biến mất, Tuyết Lan kh&ocirc;i phục &oacute;ng &aacute;nh long lanh, kh&ocirc;ng nh&igrave;n ra một điểm bệnh qu&aacute; dấu vết.<br />\r\n<br />\r\nL&yacute; Việt xin mời Mạnh Kh&aacute;nh L&acirc;m lại đ&acirc;y, Mạnh Kh&aacute;nh L&acirc;m điều tra Thực Lộ tr&ugrave;ng, lại thấy Sở Ly th&agrave;nh c&ocirc;ng, t&agrave;n nhẫn khoa một trận, cũng n&oacute;i sẽ tranh thủ hắn si&ecirc;u việt tăng phẩm.<br />\r\n<br />\r\nThị vệ thăng phẩm ngoại trừ ngao tư lịch, c&ograve;n c&oacute; một loại con đường &mdash; lập c&ocirc;ng, lập c&ocirc;ng lớn, c&oacute; thể si&ecirc;u việt tăng phẩm.<br />\r\n<br />\r\nLần n&agrave;y, Quốc C&ocirc;ng phủ bị gieo vạ kh&ocirc;ng &iacute;t Tuyết Lan, nếu như chọn d&ugrave;ng Sở Ly biện ph&aacute;p chữa khỏi, vậy th&igrave; l&agrave; một c&aacute;i c&ocirc;ng lớn, miễn cưỡng đủ tấn phẩm.<br />\r\n<br />\r\n&mdash;&mdash;&mdash;&mdash;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nBuổi trưa ăn cơm xong, &aacute;nh mặt trời vừa vặn, người lười biếng, Sở Ly đang muốn chợp mắt, ngọc kh&aacute;nh tiếng h&oacute;t vang l&ecirc;n, c&oacute; người ti&ecirc;́n v&agrave;o đảo, Sở Ly c&ugrave;ng L&yacute; Việt bận bịu chạy tới b&ecirc;n bờ xem.<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m cười h&iacute;p mắt rời thuyền, mặt đỏ l&ecirc;n.<br />\r\n<br />\r\nSở Ly c&ugrave;ng L&yacute; Việt đ&oacute;n nhận h&agrave;nh lễ.<br />\r\n<br />\r\n&quot;Hay, hay!&quot; Mạnh Kh&aacute;nh L&acirc;m vỗ vỗ Sở Ly vai, th&acirc;n thiết n&oacute;i: &quot;Tiểu Sở, lần n&agrave;y th&aacute;c ngươi ph&uacute;c, ch&uacute;ng ta Đ&ocirc;ng Hoa vi&ecirc;n xem như l&agrave; tăng mặt.&quot;<br />\r\n<br />\r\nViện B&aacute;ch Thảo chưởng quản b&ecirc;n trong phủ hết thảy hoa cỏ dược liệu, Đ&ocirc;ng Hoa vi&ecirc;n l&agrave; tam tiểu thư Ti&ecirc;u Kỳ tương ứng, T&acirc;y Hoa vi&ecirc;n ch&uacute;c đại c&ocirc;ng tử Ti&ecirc;u Thiết Ưng, hai mạch ở viện B&aacute;ch Thảo đ&aacute;nh đến rất kịch liệt, Mạnh Kh&aacute;nh L&acirc;m l&agrave; Đ&ocirc;ng Hoa vi&ecirc;n một mạch.<br />\r\n<br />\r\n&quot;Đều chữa khỏi ?&quot; Sở Ly cười n&oacute;i.<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m cười ha ha: &quot;Tổng cộng ba trăm c&acirc;y Tuyết Lan!&quot;<br />\r\n<br />\r\nL&yacute; Việt vội hỏi: &quot;Mạnh l&atilde;o, c&aacute;i kia Sở huynh đệ c&oacute; thể tấn phẩm sao?&quot;<br />\r\n<br />\r\n&quot;Bắt đầu từ h&ocirc;m nay, tiểu Sở ngươi l&agrave; cửu phẩm thị vệ, tiểu L&yacute;, ngươi cũng thơm l&acirc;y, b&aacute;t phẩm!&quot; Mạnh Kh&aacute;nh L&acirc;m cười h&igrave; h&igrave; lấy ra hai tấm ng&acirc;n phiếu: &quot;10 ng&agrave;n hai l&agrave; tiểu Sở, một ng&agrave;n lạng l&agrave; tiểu L&yacute;, tiền n&agrave;y l&agrave; ch&uacute;ng ta viện B&aacute;ch Thảo ban thưởng.&quot;<br />\r\n<br />\r\nSở Ly tiếp nhận bạc, &ocirc;m quyền cười n&oacute;i: &quot;Đa tạ Mạnh l&atilde;o!&quot;<br />\r\n<br />\r\nC&oacute; thể đ&ecirc;́n nặng như vậy ban thưởng, Mạnh Kh&aacute;nh L&acirc;m khẳng định bỏ ra nhiều c&ocirc;ng sức.<br />\r\n<br />\r\n&quot;Được đ&oacute; tiểu Sở, c&oacute; thể tạo t&agrave;i năng!&quot; Mạnh Kh&aacute;nh L&acirc;m nhạc kh&ocirc;ng thể chi: &quot;Cuối c&ugrave;ng cũng coi như đ&egrave; &eacute;p Cố Lập Đồng nhất đầu!&quot;<br />\r\n<br />\r\nL&yacute; Việt kinh ngạc hỏi: &quot;Cố Lập Đồng lần n&agrave;y kh&ocirc;ng t&igrave;m được c&aacute;ch chữa? Hắn nhưng là thi&ecirc;n t&agrave;i!&quot;<br />\r\n<br />\r\nCố Lập Đồng v&agrave;o phủ ba năm, b&aacute;t phẩm thị vệ, lập được hai lần đại c&ocirc;ng, tiến cử hai c&aacute;i giống kỳ hoa, tiền đồ v&ocirc; lượng.<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m l&uacute;c lắc mập tay: &quot;Hắn kh&ocirc;ng t&acirc;m tư l&agrave;m c&aacute;i n&agrave;y.&quot;<br />\r\n<br />\r\nSở Ly n&oacute;i: &quot;Mạnh l&atilde;o, ta c&oacute; c&aacute;i nho nhỏ thỉnh cầu.&quot;<br />\r\n<br />\r\n&quot;N&oacute;i!&quot;<br />\r\n<br />\r\n&quot;Ta nghĩ thăm một ch&uacute;t dược vi&ecirc;n.&quot;<br />\r\n<br />\r\n&quot;Y&ecirc;u, d&atilde; t&acirc;m kh&ocirc;ng nhỏ oa, muốn v&agrave;o dược vi&ecirc;n?&quot;<br />\r\n<br />\r\n&quot;Nghe t&ecirc;n đ&atilde; l&acirc;u, rất t&ograve; m&ograve;.&quot;<br />\r\n<br />\r\n&quot;Ừm...&quot; Mạnh Kh&aacute;nh L&acirc;m nghĩ một hồi, cười n&oacute;i: &quot;Được rồi, chỉ c&oacute; thể v&agrave;o cửu phẩm dược vi&ecirc;n!&quot;<br />\r\n<br />\r\n&quot;Đa tạ Mạnh l&atilde;o!&quot;<br />\r\n<br />\r\n&quot;Dược vi&ecirc;n kh&ocirc;ng tưởng tượng đẹp như vậy, đi th&ocirc;i!&quot;<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m mang theo hai người l&ecirc;n thuyền, hắn ở đầu thuyền chỉ huy, L&yacute; Việt ch&egrave;o thuyền, qu&aacute; nửa giờ leo l&ecirc;n một h&ograve;n đảo nhỏ.<br />\r\n<br />\r\nBốn c&aacute;i trung ni&ecirc;n hộ vệ tiến l&ecirc;n, &aacute;nh mắt sắc b&eacute;n như mũi t&ecirc;n, Mạnh Kh&aacute;nh L&acirc;m đưa ra y&ecirc;u, bọn hộ vệ gật đầu, lui về ph&iacute;a sau cho đi.<br />\r\n<br />\r\nL&yacute; Việt bưng mũi vẻ mặt đau khổ, bất cứ l&uacute;c n&agrave;o muốn n&ocirc;n mửa chi&ecirc;u thức.<br />\r\n<br />\r\nSở Ly lắc đầu bật cười, tr&ecirc;n đảo n&agrave;y &yacute; vị đủ n&ugrave;ng, nhiều loại m&ugrave;i vị hỗn c&ugrave;ng nhau, hương kh&ocirc;ng thơm x&uacute; kh&ocirc;ng th&uacute;i, rất dằn vặt khứu gi&aacute;c.<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m tr&ugrave;ng L&yacute; Việt cười n&oacute;i: &quot;Tiểu L&yacute;, dược vi&ecirc;n ng&ocirc;́c l&acirc;u, tr&ecirc;n người cũng sẽ c&oacute; m&ugrave;i n&agrave;y nhi, l&agrave;m sao tẩy cũng rửa kh&ocirc;ng sạch, như thế n&agrave;o, dễ ngửi đi, ha ha!&quot;<br />\r\n<br />\r\nL&yacute; Việt vẻ mặt đau khổ n&oacute;i: &quot;Vậy l&agrave;m sao đi Y&ecirc;u Nguyệt l&acirc;u a, c&ocirc; nương n&agrave;o nhận được c&aacute;i n&agrave;y!&quot;<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m cười n&oacute;i: &quot;Ngươi cho rằng hậu hĩnh tiền thưởng l&agrave; cho kh&ocirc;ng ?&quot;<br />\r\n<br />\r\nBa người đi tới một t&ograve;a tiểu viện.<br />\r\n<br />\r\nSở Ly Đại Vi&ecirc;n K&iacute;nh Tr&iacute; năng chiếu khắp chu vi một dặm, nh&igrave;n thấy từng to&agrave; từng to&agrave; tiểu viện li&ecirc;n kết, mỗi t&ograve;a tiểu viện đều l&agrave; một khối vườn thuốc, một loại linh dược.<br />\r\n<br />\r\nKhu nh&agrave; nhỏ n&agrave;y vườn thuốc gieo một mảnh cẩu đu&ocirc;i thảo, Sở Ly nhận ra đ&acirc;y l&agrave; lơ lửng giữa trời thảo, ở cửu phẩm trong linh dược xem như l&agrave; h&agrave;ng đầu.<br />\r\n<br />\r\nMột gầy g&ograve; người đ&agrave;n &ocirc;ng trung ni&ecirc;n ch&iacute;nh khom lưng giẫy cỏ, cẩn thận từng li từng t&iacute; một, e sợ cho đụng tới lơ lửng giữa trời thảo, cuốc ch&iacute;nh l&agrave; ngọc b&iacute;ch l&agrave;m ra, kh&eacute;o l&eacute;o m&agrave; tinh xảo.<br />\r\n<br />\r\n&quot;Mạnh l&atilde;o!&quot; Hắn nghe được tiếng bước ch&acirc;n quay đầu, &ocirc;m quyền h&agrave;nh lễ.<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m xua tay: &quot;Tiểu Kinh, bận bịu ngươi, ta dẫn bọn họ thăm một ch&uacute;t.&quot;<br />\r\n<br />\r\n&quot;Mới tới ?&quot; Kinh Tr&igrave; Hải cười hỏi.<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m n&oacute;i: &quot;Lập c&ocirc;ng, si&ecirc;u việt tăng phẩm, l&agrave;m như khen thưởng, dẫn bọn họ mở mang kiến thức một ch&uacute;t dược vi&ecirc;n.&quot;<br />\r\n<br />\r\nKinh Tr&igrave; Hải biến sắc mặt, trịnh trọng &ocirc;m một c&aacute;i quyền: &quot;Thất lễ thất lễ, hai vị tiểu huynh đệ mời theo &yacute;.&quot;<br />\r\n<br />\r\nSi&ecirc;u việt tăng phẩm cũng kh&ocirc;ng dễ d&agrave;ng, nh&acirc;n vật như thế tiền đồ quang minh, c&oacute; c&aacute;i g&igrave; tốt ph&aacute;i đi, chức vị tốt, đệ nhất lựa chọn đều l&agrave; si&ecirc;u việt tăng phẩm thị vệ.<br />\r\n<br />\r\n&quot;Kinh tiền bối, ta c&oacute; thể sờ một ch&uacute;t sao?&quot;<br />\r\n<br />\r\n&quot;... Đương nhi&ecirc;n, chỉ cần cẩn thận một điểm, ch&uacute;ng n&oacute; rất mảnh mai.&quot;<br />\r\n<br />\r\nSở Ly gật đầu cảm ơn, cẩn thận từng li từng t&iacute; một đụng chạm một gốc c&acirc;y lơ lửng giữa trời thảo, tinh khiết kh&iacute; tức tr&agrave;n v&agrave;o đến, duy&ecirc;n Tiểu tẩy mạch quyết một chu thi&ecirc;n.<br />\r\n<br />\r\nD&ugrave;ng n&oacute; luyện nho nhỏ tẩy mạch quyết, cần hai năm!<br />\r\n<br />\r\nĐ&acirc;y l&agrave; cửu phẩm linh dược, nếu như l&agrave; nhất phẩm linh dược, c&aacute;i kia đem cỡ n&agrave;o cấp tốc!<br />\r\n<br />\r\nLinh Dược tinh kh&iacute; c&ugrave;ng Tiểu tẩy mạch quyết kết hợp, c&oacute; thể so với Thuế ph&agrave;m quả, Thuế ph&agrave;m quả c&oacute; thể trực tiếp phạt mao tẩy tủy, ho&agrave;n th&agrave;nh tr&uacute;c cơ, thậm ch&iacute; c&ograve;n c&oacute; lợi &iacute;ch to lớn, đ&aacute;ng tiếc l&agrave; truyền thuyết đồ vật.<br />\r\n<br />\r\nLinh Dược tinh kh&iacute; như c&ugrave;ng người ngũ cốc tinh kh&iacute;, một khi chết đi, tinh kh&iacute; đốn t&aacute;n, linh dược dược lực c&ugrave;ng tinh kh&iacute; l&agrave; hai chuyện kh&aacute;c nhau.<br />\r\n<br />\r\nNội c&ocirc;ng l&agrave; đem ngũ cốc tinh kh&iacute; chuyển h&oacute;a th&agrave;nh nội lực, ngũ cốc tinh kh&iacute; c&oacute; hạn, nhưng thảo mộc tinh kh&iacute; v&ocirc; hạn, d&ugrave;ng n&oacute; thay th&ecirc;́ tinh kh&iacute;, m&igrave;nh luyện c&ocirc;ng chắc chắn tiến triển cực nhanh!<br />\r\n<br />\r\nNghĩ tới đ&acirc;y hắn đ&ocirc;̣t ng&ocirc;̣t sinh ra v&ocirc; c&ugrave;ng hi vọng!<br />\r\n<br />\r\n&quot;Tiểu tử, nghĩ đến cái gì mỹ chuyện?&quot; Mạnh Kh&aacute;nh L&acirc;m đ&aacute;nh g&atilde;y hắn sướng nghĩ.<br />\r\n<br />\r\nSở Ly bu&ocirc;ng tay ra cười cười.<br />\r\n<br />\r\n&quot;Lại đi nơi kh&aacute;c đi dạo, tiểu Kinh, bận bịu ngươi đi.&quot; Mạnh Kh&aacute;nh L&acirc;m vung vung tay.<br />\r\n<br />\r\nKinh Tr&igrave; Hải &acirc;n cần đưa bọn họ đến ngo&agrave;i cửa.<br />\r\n<br />\r\nSở Ly ba người tham quan ngũ t&ograve;a tiểu viện, Sở Ly từng c&aacute;i đụng v&agrave;o, lơ lửng giữa trời thảo tinh khiết nhất, ch&iacute;nh m&igrave;nh đ&ecirc;́n d&agrave;nh thời gian tấn phẩm, sớm một ch&uacute;t tới nơi n&agrave;y tu luyện.<br />\r\n<br />\r\nBa người đường về, L&yacute; Việt ch&egrave;o thuyền, Mạnh Kh&aacute;nh L&acirc;m tr&ugrave;ng Sở Ly cười n&oacute;i: &quot;Tiểu Sở, muốn v&agrave;o đến?&quot;<br />\r\n<br />\r\nSở Ly gật g&ugrave;.<br />\r\n<br />\r\nL&yacute; Việt vội hỏi: &quot;Huynh đệ, ngươi kh&ocirc;ng muốn ở Đ&ocirc;ng Hoa vi&ecirc;n r&ocirc;̀i?&quot;<br />\r\n<br />\r\n&quot;Hai b&ecirc;n chạy chứ.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Tiểu Sở, ngươi tư lịch qu&aacute; n&ocirc;ng, phải đợi mấy năm.&quot;<br />\r\n<br />\r\n&quot;Mạnh l&atilde;o, tổng c&oacute; biện ph&aacute;p đi.&quot;<br />\r\n<br />\r\n&quot;Biện ph&aacute;p đ&uacute;ng l&agrave; c&oacute;, lập c&ocirc;ng!&quot;<br />\r\n<br />\r\n&quot;Mong rằng Mạnh l&atilde;o chỉ điểm!&quot;<br />\r\n<br />\r\n&quot;Ngươi biết Cố Lập Đồng đang nghi&ecirc;n cứu Nguyệt Quang lan chứ?&quot; Mạnh Kh&aacute;nh L&acirc;m cười h&iacute;p mắt n&oacute;i: &quot;Ngươi cướp ở hắn đằng trước, đem Nguyệt Quang lan nu&ocirc;i sống, ta đảm bảo ngươi tiến v&agrave;o cửu phẩm dược vi&ecirc;n!&quot;<br />\r\n<br />\r\n&quot;Nguyệt Quang lan...&quot; Sở Ly cau m&agrave;y.<br />\r\n<br />\r\nDiệp h&igrave;nh loan loan, buổi tối thả bạch quang, như một v&ograve;ng Huyền Nguyệt, cố c&oacute; Nguyệt Quang lan danh xưng.<br />\r\n<br />\r\nN&oacute; sinh ở hải ngoại Nguyệt Quang đảo, một khi rời đảo cũng chỉ c&oacute; một nguyệt tuổi thọ, đến nay kh&ocirc;ng c&oacute; ngoại lệ, nhưng nh&acirc;n sự mỹ lệ kh&ocirc;ng g&igrave; tả nổi, rất được v&acirc;y đỡ.<br />\r\n<br />\r\nMột c&acirc;y Nguyệt Quang lan &iacute;t nhất hai trăm lạng bạc r&ograve;ng.<br />\r\n<br />\r\nHai trăm lạng bạc r&ograve;ng c&oacute; thể ở S&ugrave;ng Minh th&agrave;nh mua một to&agrave; nh&agrave; nhỏ tử.<br />\r\n<br />\r\nSở Ly cửu phẩm thị vệ, một th&aacute;ng mười lượng bạc, đ&atilde; l&agrave; lương cao, l&agrave;m một năm cũng mua kh&ocirc;ng nổi một c&acirc;y Nguyệt Quang lan, c&oacute; thể thấy được đắt giá.<br />\r\n<br />\r\nĐắt như thế Nguyệt Quang lan, kh&ocirc;ng biết c&oacute; bao nhi&ecirc;u cao thủ đang nghi&ecirc;n cứu, muốn duy&ecirc;n tuổi thọ, đ&aacute;ng tiếc kh&ocirc;ng ai th&agrave;nh c&ocirc;ng, Cố Lập Đồng ki&ecirc;u căng tự m&atilde;n, mới d&aacute;m khi&ecirc;u chiến.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng d&aacute;m?&quot; Mạnh Kh&aacute;nh L&acirc;m mắt nhỏ cười h&iacute;p mắt theo d&otilde;i hắn xem.<br />\r\n<br />\r\nL&yacute; Việt li&ecirc;n tục cho Sở Ly nh&aacute;y mắt ra dấu.<br />\r\n<br />\r\nSở Ly gật đầu: &quot;Được, ta thử một ch&uacute;t xem.&quot;<br />\r\n<br />\r\nChờ đem Mạnh Kh&aacute;nh L&acirc;m đưa trở về, hai người trở về Đ&ocirc;ng Hoa vi&ecirc;n, L&yacute; Việt hoa thuyền o&aacute;n giận Sở Ly: &quot;Huynh đệ, ngươi thật sự d&aacute;m n&oacute;i, Nguyệt Quang lan a!&quot;<br />\r\n<br />\r\nSở Ly đ&oacute;n gi&oacute; cười n&oacute;i: &quot;Thử xem ngại g&igrave;.&quot;<br />\r\n<br />\r\n&quot;Nếu như Cố Lập Đồng biết rồi, nhất định sẽ c&oacute; c&aacute;i nh&igrave;n!&quot;<br />\r\n<br />\r\nSở Ly cười cợt.<br />\r\n<br />\r\n&quot;Ai... , ngươi l&aacute; gan n&agrave;y...&quot; L&yacute; Việt bất đắc dĩ lắc đầu.<br />\r\n<br />\r\nHắn rất kh&acirc;m phục Sở Ly h&agrave;o hiệp, thật giống kh&ocirc;ng c&aacute;i g&igrave; quan t&acirc;m, c&aacute;i g&igrave; đều c&oacute; thể thả xuống, nhưng t&iacute;nh c&aacute;ch n&agrave;y qu&aacute; dễ d&agrave;ng đắc tội người, dễ d&agrave;ng gặp rắc rối.<br />\r\n<br />\r\nSở Ly nh&igrave;n hắn lo lắng, cười n&oacute;i: &quot;Hắn tổng kh&ocirc;ng d&aacute;m x&ocirc;ng v&agrave;o Đ&ocirc;ng Hoa vi&ecirc;n chứ?&quot;<br />\r\n<br />\r\n&quot;C&aacute;i kia ngược lại kh&ocirc;ng d&aacute;m, nhưng hắn kh&iacute; lượng c&oacute; thể kh&ocirc;ng rộng, l&agrave;m việc kh&ocirc;ng chừa thủ đoạn n&agrave;o, nhất định sẽ t&igrave;m ngươi phiền phức!&quot;<br />\r\n<br />\r\n&quot;C&aacute;i kia bảo vệ miệng, lặng lẽ được!&quot;<br />\r\n<br />\r\n&quot;Ta đương nhi&ecirc;n kh&ocirc;ng th&agrave;nh vấn đề!&quot; L&yacute; Việt bận bịu lớn tiếng n&oacute;i.<br />\r\n<br />\r\nSở Ly kh&ocirc;ng phản đ&ocirc;́i lắc đầu một c&aacute;i, L&yacute; Việt người n&agrave;y kh&ocirc;ng hề l&ograve;ng dạ, căn bản tồn kh&ocirc;ng được thoại.<br />\r\n<br />\r\nL&yacute; Việt lớn tiếng n&oacute;i: &quot;Ta chắc chắn sẽ kh&ocirc;ng n&oacute;i!&quot;</p>', '2021-11-27 21:47:18', 10, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(198, 52, 'Khiêu chiến', '4', '<p>Sở Ly c&ugrave;ng L&yacute; Việt nhảy v&agrave;o Kỳ thảo hi&ecirc;n.<br />\r\n<br />\r\nKỳ thảo hi&ecirc;n c&aacute;ch một con đường ch&iacute;nh l&agrave; S&ugrave;ng Minh trung t&acirc;m th&agrave;nh phố lớn, n&aacute;o nhiệt huy&ecirc;n hoa, ngựa xe như nước, một nhai chi c&aacute;ch nơi n&agrave;y nhưng y&ecirc;n tĩnh l&agrave;nh lạnh, kh&aacute;c n&agrave;o hai c&aacute;i thế giới.<br />\r\n<br />\r\nKỳ thảo hi&ecirc;n m&ocirc;n đầu cổ xưa r&aacute;ch n&aacute;t, chu vi một &iacute;t đồ cổ cửa h&agrave;ng, đồ trang sức cửa h&agrave;ng, đều kh&ocirc;ng kh&aacute;c mấy như vậy phong c&aacute;ch, thật giống muốn đ&oacute;ng cửa đ&oacute;ng cửa.<br />\r\n<br />\r\nSở Ly nhưng sẽ kh&ocirc;ng coi khinh nơi n&agrave;y, đều l&agrave; ba năm kh&ocirc;ng khai trương, khai trương ăn ba năm.<br />\r\n<br />\r\nHất r&egrave;m cửa đi v&agrave;o, trước mắt rộng mở một rộng, rộng tho&aacute;ng ph&ograve;ng kh&aacute;ch m&ugrave;i thơm thấm người, một chậu bồn kỳ hoa dị thảo đặt tại b&aacute;c cổ gi&aacute; tr&ecirc;n.<br />\r\n<br />\r\nTrung ương một c&aacute;i b&agrave;n b&aacute;t ti&ecirc;n, một vị tu mi bạc trắng &ocirc;ng l&atilde;o ở thản nhi&ecirc;n uống tr&agrave;.<br />\r\n<br />\r\nNh&igrave;n thấy hai người đi vào, hắn cười h&iacute;p mắt đứng dậy, &ocirc;m một c&aacute;i quyền: &quot;Hai vị tiểu ca, t&ugrave;y tiện nh&igrave;n.&quot;<br />\r\n<br />\r\nSở Ly c&ugrave;ng L&yacute; Việt &aacute;nh mắt một hồi bị Nguyệt Quang lan hấp dẫn, b&iacute;ch lục l&aacute; c&acirc;y loan loan, hiện ra &ocirc;n h&ograve;a &aacute;nh s&aacute;ng lộng lẫy, như l&agrave; ngọc b&iacute;ch đi&ecirc;u th&agrave;nh, mỹ đ&ecirc;́n kh&ocirc;ng giống vật c&ograve;n sống.<br />\r\n<br />\r\n&Ocirc;ng l&atilde;o tập hợp lại đ&acirc;y, ha ha cười n&oacute;i: &quot;Hai vị tiểu ca y&ecirc;u th&iacute;ch Nguyệt Quang lan?&quot;<br />\r\n<br />\r\nL&yacute; Việt hừ n&oacute;i: &quot;Ph&iacute; lời, ai kh&ocirc;ng th&iacute;ch!&quot;<br />\r\n<br />\r\n&quot;Chưởng quỹ, n&agrave;y khỏa Nguyệt Quang lan c&oacute; thể sống bao l&acirc;u?&quot;<br />\r\n<br />\r\n&quot;Mới vừa chở tới đ&acirc;y kh&ocirc;ng l&acirc;u, &quot; &ocirc;ng l&atilde;o dựng thẳng l&ecirc;n hai ng&oacute;n tay: &quot;&Iacute;t nhất hai th&aacute;ng!&quot;<br />\r\n<br />\r\n&quot;Mười ng&agrave;y!&quot; Sở Ly cười cợt.<br />\r\n<br />\r\nL&yacute; Việt vỗ vỗ eo, hừ n&oacute;i: &quot;Chưởng quỹ, con mắt trừng lớn điểm nhi!&quot;<br />\r\n<br />\r\n&quot;Ha ha, l&agrave; Quốc C&ocirc;ng phủ đàn &ocirc;ng?&quot; &Ocirc;ng l&atilde;o biến sắc mặt, &ocirc;m quyền cười n&oacute;i: &quot;Thất k&iacute;nh thất k&iacute;nh, ... C&aacute;i kia tiểu l&atilde;o nhi cũng kh&ocirc;ng d&aacute;m lừa gạt, đ&uacute;ng l&agrave; mười ng&agrave;y, c&oacute; điều năm nay chỉ c&oacute; n&agrave;y một c&acirc;y ra đảo, dưới một c&acirc;y muốn qua sang năm r&ocirc;̀i, tiểu l&atilde;o nhi tr&ecirc;n c&oacute; gi&agrave; dưới c&oacute; trẻ, phải nu&ocirc;i gia sống tạm, đ&aacute;nh chết cũng kh&ocirc;ng thể b&aacute;n th&aacute;o!&quot;<br />\r\n<br />\r\nL&yacute; Việt phất tay một c&aacute;i: &quot;Thiếu kh&oacute;c than trang đ&aacute;ng thương, ch&uacute;ng ta kh&ocirc;ng mắc bẫy n&agrave;y!&quot;<br />\r\n<br />\r\nKh&ocirc;ng gian kh&ocirc;ng thương, c&oacute; thể ở đ&acirc;y mở cửa h&agrave;ng kh&ocirc;ng một người ngh&egrave;o, d&ograve;ng d&otilde;i hậu hĩnh.<br />\r\n<br />\r\nSở Ly hững hờ chạm thử Nguyệt Quang lan: &quot;Ra gi&aacute; đi!&quot;<br />\r\n<br />\r\nNguyệt Quang lan tinh kh&iacute; trong nh&aacute;y mắt tr&agrave;n v&agrave;o, vận chuyển Tiểu tẩy mạch quyết m&ocirc;̣t tu&acirc;̀n.<br />\r\n<br />\r\nHắn phun ra một hơi, ngăn chặn mừng như đi&ecirc;n, tinh khiết cực điểm linh kh&iacute;! Một năm, chỉ cần một năm liền c&oacute; thể tr&uacute;c cơ!<br />\r\n<br />\r\n&Ocirc;ng l&atilde;o mới vừa h&aacute; mồm mu&ocirc;́n nói chuy&ecirc;̣n, tiếng bước ch&acirc;n vang l&ecirc;n, một người thanh ni&ecirc;n v&agrave;o nh&agrave;, hai tay nhấc l&ecirc;n r&egrave;m cửa, cung k&iacute;nh khom lưng, ngo&agrave;i cửa truyền đến &acirc;m thanh.<br />\r\n<br />\r\n&quot;Triệu sư muội, xin mời &mdash;&mdash;!&quot;<br />\r\n<br />\r\n&quot;Vẫn l&agrave; Tr&aacute;c sư huynh trước hết mời!&quot;<br />\r\n<br />\r\nMột thanh ni&ecirc;n anh tuấn nhanh ch&acirc;n nhảy v&agrave;o ốc, mặt như ngọc, m&agrave;y kiếm l&atilde;ng mục, b&ecirc;n h&ocirc;ng quải một thanh trường kiếm, tinh thần phấn chấn, ngạo nghễ nh&igrave;n quanh bốn ph&iacute;a, ch&iacute;nh l&agrave; Tr&aacute;c Phi Dương.<br />\r\n<br />\r\nPh&iacute;a sau theo một thiếu nữ xinh đẹp, da thịt t&aacute;i tuyết, quyến rũ m&ecirc; người, l&agrave; Triệu Dĩnh.<br />\r\n<br />\r\nSở Ly c&ugrave;ng Tr&aacute;c Phi Dương hai mắt nh&igrave;n nhau, hai người đều nh&iacute;u nh&iacute;u m&agrave;y.<br />\r\n<br />\r\nSở Ly t&acirc;m trạng kh&ocirc;ng thoải m&aacute;i, Triệu Dĩnh tại sao lại c&ugrave;ng Tr&aacute;c Phi Dương tập hợp c&ugrave;ng nơi!<br />\r\n<br />\r\nXem ra Tr&aacute;c Phi Dương thế tiến c&ocirc;ng rất mạnh, Đại Vi&ecirc;n K&iacute;nh Tr&iacute; khởi động, &aacute;m thở ra m&ocirc;̣t hơi, cũng c&ograve;n tốt Triệu Dĩnh kh&ocirc;ng động t&acirc;m, nhưng do dự b&ecirc;n trong.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nTr&aacute;c Phi Dương lạnh r&ecirc;n một tiếng: &quot;Ngươi l&agrave;m sao ở chỗ n&agrave;y?&quot;<br />\r\n<br />\r\nSở Ly cười gằn: &quot;Lời n&agrave;y th&uacute; vị, ta l&agrave;m sao kh&ocirc;ng thể ở chỗ n&agrave;y?&quot;<br />\r\n<br />\r\nTr&aacute;c Phi Dương hừ n&oacute;i: &quot;Kh&ocirc;ng c&oacute; phẩm trật tạp dịch, một cọng cỏ cũng mua kh&ocirc;ng nổi, chạy nơi n&agrave;y xem qua ẩn đến rồi, đủ đáng thương!&quot;<br />\r\n<br />\r\n&quot;Tr&aacute;c đại c&ocirc;ng tử quản được cũng rộng!&quot;<br />\r\n<br />\r\nL&yacute; Việt &aacute;m đạo kh&ocirc;ng ổn, thi&ecirc;n t&agrave;i Tr&aacute;c Phi Dương a, hai người thật xung đột l&ecirc;n c&oacute; thể kh&ocirc;ng chiếm được lợi &iacute;ch.<br />\r\n<br />\r\nHắn bận bịu &ocirc;m quyền, giương giọng cười n&oacute;i: &quot;Ha ha, Tr&aacute;c huynh, Triệu c&ocirc; nương, ta l&agrave; Đ&ocirc;ng Hoa vi&ecirc;n L&yacute; Việt!&quot;<br />\r\n<br />\r\nTr&aacute;c Phi Dương lạnh l&ugrave;ng ho&agrave;nh hắn một ch&uacute;t, kh&ocirc;ng l&ecirc;n tiếng.<br />\r\n<br />\r\nMột bộ xanh nhạt la sam Triệu Dĩnh mỉm cười đ&aacute;p lễ: &quot;L&yacute; sư huynh.&quot;<br />\r\n<br />\r\nTr&aacute;c Phi Dương ở tr&ecirc;n cao nh&igrave;n xuống nh&igrave;n xuống: &quot;Họ Sở, c&ograve;n chưa c&uacute;t mở, thật cẩu kh&ocirc;ng cản đường!&quot;<br />\r\n<br />\r\n&quot;Tr&aacute;c Phi Dương, người kh&aacute;c xưng ngươi thi&ecirc;n t&agrave;i, ngươi coi như thật?&quot;<br />\r\n<br />\r\n&quot;Ta đương nhi&ecirc;n l&agrave; thi&ecirc;n t&agrave;i!&quot; Tr&aacute;c Phi Dương ngang đầu ưỡn ngực: &quot;Ngươi lại th&ocirc;ng minh c&oacute; &iacute;ch lợi g&igrave;, c&ograve;n kh&ocirc;ng phải c&aacute;i kh&ocirc;ng tr&uacute;c cơ ?&quot;<br />\r\n<br />\r\n&quot;Ai... , v&otilde; c&ocirc;ng mạnh hơn, kh&ocirc;ng đầu &oacute;c, như thế l&agrave; ngu xuẩn!&quot; Sở Ly lắc đầu một c&aacute;i.<br />\r\n<br />\r\nTr&aacute;c Phi Dương khẽ cắn răng, xem thường liếc ch&eacute;o hắn: &quot;Thế giới n&agrave;y, v&otilde; c&ocirc;ng ch&iacute;nh l&agrave; tất cả! ... Họ Sở, ta hiện tại l&agrave; b&aacute;t phẩm, năm nay liền c&oacute; thể đạt đến thất phẩm, ngươi đ&acirc;y, kh&ocirc;ng c&oacute; phẩm trật tạp dịch một, nghĩ đến b&aacute;t phẩm, đ&ecirc;́n ngao tr&ecirc;n hai mươi năm, vẫn chưa thể phạm sai lầm, liền ngươi n&agrave;y x&uacute; đạo đức c&oacute; thể kh&ocirc;ng gặp rắc rối?&quot;<br />\r\n<br />\r\n&quot;Liền ngươi n&agrave;y đầu &oacute;c, v&otilde; c&ocirc;ng cho d&ugrave; tốt cũng chỉ c&oacute; thể nh&igrave;n gia hộ viện, kh&ocirc;ng c&oacute; t&aacute;c dụng lớn!&quot;<br />\r\n<br />\r\nTr&aacute;c Phi Dương hừ n&oacute;i: &quot;Kh&ocirc;ng c&oacute; t&aacute;c dụng lớn cũng so với ngươi hữu dụng!&quot;<br />\r\n<br />\r\n&quot;Tr&aacute;c sư huynh, Sở sư huynh &mdash;&mdash;!&quot; Triệu Dĩnh giậm ch&acirc;n hờn dỗi.<br />\r\n<br />\r\nTr&aacute;c Phi Dương lập tức thay đổi một bức sắc mặt, &ocirc;n nhu cười n&oacute;i: &quot;Triệu sư muội, n&agrave;y c&acirc;y Nguyệt Quang lan thế n&agrave;o?&quot;<br />\r\n<br />\r\n&quot;Rất đẹp, nhưng qu&aacute; đắt .&quot; Triệu Dĩnh nhẹ lay động vuốt tay, đưa &aacute;nh mắt từ Nguyệt Quang lan tr&ecirc;n y lưu luyến kh&ocirc;ng rời thu hồi.<br />\r\n<br />\r\nTr&aacute;c Phi Dương liếc mắt nh&igrave;n Nguyệt Quang lan, mỉm cười n&oacute;i: &quot;Triệu sư muội, ch&uacute;ng ta đi nơi kh&aacute;c đi dạo đi, với hắn c&ugrave;ng nơi qu&aacute; x&uacute;i quẩy, đi!&quot;<br />\r\n<br />\r\n&quot;Sở sư huynh...&quot; Triệu Dĩnh &aacute;y n&aacute;y cười cười, Sở Ly vung vung tay ra hiệu kh&ocirc;ng sao, &acirc;m trầm nh&igrave;n bọn họ cũng mệ rời đi.<br />\r\n<br />\r\n&quot;H&ocirc;...&quot; L&yacute; Việt thở d&agrave;i một hơi, bất đắc dĩ xem Sở Ly: &quot;Huynh đệ, ngươi...&quot;<br />\r\n<br />\r\nTr&aacute;c Phi Dương thi&ecirc;n t&agrave;i ngang dọc, tương lai tất l&agrave; đại nh&acirc;n vật, c&oacute; thể kh&ocirc;ng nhạ tốt nhất chớ chọc, hậu hoạn v&ocirc; c&ugrave;ng.<br />\r\n<br />\r\nSở Ly vung vung tay: &quot;Chưởng quỹ, ra gi&aacute; đi.&quot;<br />\r\n<br />\r\n&Ocirc;ng l&atilde;o ha ha cười n&oacute;i: &quot;Thừa huệ, hai trăm lạng!&quot;<br />\r\n<br />\r\n&quot;Thực sự l&agrave; gian thương!&quot; L&yacute; Việt lắc đầu, từ trong lòng m&oacute;c ra ng&acirc;n phiếu.<br />\r\n<br />\r\n&quot;Chậm đ&atilde;!&quot; R&egrave;m cửa vẩy một c&aacute;i, đi vào một người thanh ni&ecirc;n, vừa n&atilde;y đứng Tr&aacute;c Phi Dương ph&iacute;a sau thị vệ, gầy g&ograve; thon d&agrave;i, tướng mạo anh tuấn.<br />\r\n<br />\r\nBạch Tri Tiết, cửu phẩm thị vệ, gặp người liền cười.<br />\r\n<br />\r\nHắn v&agrave;o l&uacute;c n&agrave;y nhưng mặt lạnh đứng ở &ocirc;ng l&atilde;o trước mặt: &quot;Ta ra ba trăm hai!&quot;<br />\r\n<br />\r\nL&yacute; Việt trợn mắt l&ecirc;n, k&ecirc;u l&ecirc;n: &quot;Tiểu Bạch? !&quot;<br />\r\n<br />\r\nC&aacute;i t&ecirc;n n&agrave;y b&igrave;nh thường đối với m&igrave;nh cười h&iacute;p mắt, một c&aacute;i một L&yacute; ca k&ecirc;u, n&agrave;y sẽ c&agrave;ng bỗng nhi&ecirc;n trở mặt, thay đổi một người kh&aacute;c!<br />\r\n<br />\r\n&quot;Chưởng quỹ, l&agrave;m sao?&quot; Bạch Tri Tiết cũng kh&ocirc;ng th&egrave;m nh&igrave;n tới L&yacute; Việt, lạnh l&ugrave;ng nh&igrave;n chằm chằm &ocirc;ng l&atilde;o.<br />\r\n<br />\r\n&Ocirc;ng l&atilde;o chần chờ: &quot;C&aacute;i n&agrave;y...&quot;<br />\r\n<br />\r\nHắn nh&igrave;n Sở Ly, lại nh&igrave;n Bạch Tri Tiết, Quốc C&ocirc;ng phủ đ&agrave;n &ocirc;ng c&oacute; thể kh&ocirc;ng dễ tr&ecirc;u, nhưng l&atilde;i kếch s&ugrave; ở trước mắt, kh&ocirc;ng thể k&igrave;m được kh&ocirc;ng động l&ograve;ng.<br />\r\n<br />\r\nHắn quay đầu tr&ugrave;ng Sở Ly &aacute;y n&aacute;y cười bồi.<br />\r\n<br />\r\nSở Ly n&oacute;i: &quot;Năm trăm lạng!&quot;<br />\r\n<br />\r\n&quot;Sáu trăm hai!&quot;<br />\r\n<br />\r\n&quot;Bảy trăm!&quot;<br />\r\n<br />\r\n&quot;Tám trăm!&quot;<br />\r\n<br />\r\n&quot;M&ocirc;̣t ngàn!&quot;<br />\r\n<br />\r\n&quot;M&ocirc;̣t ngàn một!&quot; Bạch Tri Tiết lạnh l&ugrave;ng nh&igrave;n gần Sở Ly: &quot;Sở huynh đệ, ta khuy&ecirc;n ngươi suy nghĩ kỹ c&agrave;ng, rốt cuộc muốn kh&ocirc;ng muốn c&ugrave;ng Tr&aacute;c c&ocirc;ng tử đối ph&oacute;!&quot;<br />\r\n<br />\r\nSở Ly nh&igrave;n chằm chằm Bạch Tri Tiết bỗng nhi&ecirc;n nở nụ cười: &quot;10 ng&agrave;n hai!&quot;<br />\r\n<br />\r\nBạch Tri Tiết chết nh&igrave;n ch&ograve;ng chọc Sở Ly: &quot;Sở huynh đệ ngươi muốn một con đường đi tới đen?&quot;<br />\r\n<br />\r\nSở Ly cười cợt.<br />\r\n<br />\r\nBạch Tri Tiết do dự một l&aacute;t, cuối c&ugrave;ng giậm ch&acirc;n một c&aacute;i: &quot;10 ng&agrave;n một!&quot;<br />\r\n<br />\r\nSở Ly &ocirc;m quyền nở nụ cười: &quot;Kh&acirc;m phục, ngươi thắng!&quot;<br />\r\n<br />\r\nHắn xoay người rời đi, L&yacute; Việt mạnh mẽ trừng một ch&uacute;t Bạch Tri Tiết, bận bịu đuổi tới.<br />\r\n<br />\r\nTrung t&acirc;m phố lớn, đưa th&acirc;n v&agrave;o n&aacute;o nhiệt trong đ&aacute;m người, Sở Ly trong lồng ngực tức giận m&atilde;nh liệt, kh&ocirc;ng ch&uacute;t n&agrave;o khanh một c&aacute;i Tr&aacute;c Phi Dương vui vẻ.<br />\r\n<br />\r\nMột năm sau mới sẽ c&oacute; đệ nhị c&acirc;y Nguyệt Quang lan, một năm, Cố Lập Đồng rất c&oacute; thể ph&aacute;t triển th&agrave;nh c&ocirc;ng, Tr&aacute;c Phi Dương sẽ tấn thất phẩm hộ vệ, ch&iacute;nh m&igrave;nh tấn phẩm v&ocirc; vọng, tr&uacute;c cơ cũng v&ocirc; vọng, đ&aacute;ng sợ nhất ch&iacute;nh l&agrave;, Triệu Dĩnh phương t&acirc;m cũng khả năng thuộc về Tr&aacute;c Phi Dương!<br />\r\n<br />\r\nL&yacute; Việt nh&igrave;n hắn mặt &acirc;m trầm, bất đắc dĩ khuy&ecirc;n nhủ: &quot;Huynh đệ, nghĩ th&ocirc;ng điểm nhi, Tr&aacute;c Phi Dương giàu nứt đ&ocirc;́ đ&ocirc;̉ vách, x&aacute;c thực kh&ocirc;ng đấu lại.&quot;<br />\r\n<br />\r\nSở Ly cau m&agrave;y kh&ocirc;ng n&oacute;i, lại kh&ocirc;ng t&acirc;m tư du ngoạn, trực tiếp hồi phủ.<br />\r\n<br />\r\nS&aacute;ng ng&agrave;y thứ hai, Sở Ly ch&iacute;nh đang Thanh Tang trong vườn hoa luyện c&ocirc;ng, L&yacute; Việt chạy tới: &quot;Huynh đệ, đại sự kh&ocirc;ng ổn, đại sự kh&ocirc;ng ổn!&quot;<br />\r\n<br />\r\nSở Ly cau m&agrave;y nh&igrave;n hắn.<br />\r\n<br />\r\nL&yacute; Việt chạy tới k&ecirc;u l&ecirc;n: &quot;Tr&aacute;c Phi Dương qu&aacute; kh&ocirc;ng ch&acirc;n ch&iacute;nh!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;N&oacute;i mau!&quot; Sở Ly tức giận.<br />\r\n<br />\r\nL&yacute; Việt o&aacute;n hận n&oacute;i: &quot;Tr&aacute;c Phi Dương phải thay đổi thị vệ!&quot;<br />\r\n<br />\r\nSở Ly sầm mặt lại: &quot;Muốn ta?&quot;<br />\r\n<br />\r\nL&yacute; Việt gật đầu, một quyền đấm đất: &quot;Khinh người qu&aacute; đ&aacute;ng!&quot;<br />\r\n<br />\r\nSở Ly sắc mặt &acirc;m trầm, tức giận cuồn cuộn.<br />\r\n<br />\r\nPhẩm đại cấp một đ&egrave; chết người, thị vệ c&ugrave;ng hộ vệ tiền c&ocirc;ng c&oacute; kh&aacute;c biệt, địa vị nhưng kh&ocirc;ng k&eacute;m l&agrave; mấy, b&aacute;t phẩm hộ vệ c&oacute; thể phối một cửu phẩm thị vệ , tương tự, b&aacute;t phẩm thị vệ cũng c&oacute; thể phối một cửu phẩm hộ vệ.<br />\r\n<br />\r\nTr&aacute;c Phi Dương c&ugrave;ng viện B&aacute;ch Thảo yếu nh&acirc;n, viện B&aacute;ch Thảo sẽ kh&ocirc;ng nh&acirc;n v&igrave; ch&iacute;nh m&igrave;nh đắc tội t&ecirc;n thi&ecirc;n t&agrave;i n&agrave;y.<br />\r\n<br />\r\nHuống hồ, hộ vệ thị vệ cũng l&agrave; mỹ k&eacute;m, hộ vệ một năm c&oacute; mười th&aacute;ng ở b&ecirc;n ngo&agrave;i l&agrave;m nhiệm vụ, thị vệ ở trong phủ kh&ocirc;ng những kh&aacute;c ph&aacute;i đi, nh&agrave;n nh&atilde; tự tại, hộ vệ ra nhiệm vụ c&oacute; hậu hĩnh tiền thưởng, c&ograve;n c&oacute; bổng lộc, tay khe trong lậu một ch&uacute;t liền đủ thị vệ ăn ngon mặc đẹp.<br />\r\n<br />\r\nCh&iacute;nh m&igrave;nh từ chối, người b&ecirc;n ngo&agrave;i xem ra ch&iacute;nh l&agrave; kh&ocirc;ng biết c&acirc;n nhắc.<br />\r\n<br />\r\nKh&aacute; lắm Tr&aacute;c Phi Dương, tiến rất xa, n&agrave;y một chi&ecirc;u đủ t&agrave;n nhẫn đủ tiện!<br />\r\n<br />\r\n&quot;L&agrave;m sao b&acirc;y giờ?&quot; L&yacute; Việt lo lắng n&oacute;i.<br />\r\n<br />\r\nNếu như kh&ocirc;ng c&ograve;n Sở Ly, ch&iacute;nh m&igrave;nh lại kh&ocirc;ng ngủ ngon được , Đ&ocirc;ng Hoa vi&ecirc;n Hoa Hoa thảo Sota y&ecirc;u kiều, động một ch&uacute;t l&agrave; gặp sự cố.<br />\r\n<br />\r\nSở Ly hừ n&oacute;i: &quot;Chỉ c&oacute; thể d&ugrave;ng một chi&ecirc;u cuối c&ugrave;ng !&quot;<br />\r\n<br />\r\n&quot;N&oacute;i mau!&quot; L&yacute; Việt vội hỏi.<br />\r\n<br />\r\nSở Ly n&oacute;i: &quot;Ta viết phong khi&ecirc;u chiến thiếp, L&yacute; huynh ngươi gi&uacute;p ta đưa tới.&quot;<br />\r\n<br />\r\n&quot;Ngươi muốn khi&ecirc;u chiến Tr&aacute;c Phi Dương?&quot; L&yacute; Việt chần chờ n&oacute;i: &quot;Huynh đệ, qu&aacute; mạo hiểm chứ?&quot;<br />\r\n<br />\r\nSở Ly kiếm ph&aacute;p l&agrave; mạnh, nhưng Tr&aacute;c Phi Dương c&oacute; thể kh&ocirc;ng phải l&agrave; m&igrave;nh, kh&ocirc;ng hẳn c&oacute; thể thắng.<br />\r\n<br />\r\nSở Ly cười cợt: &quot;Thử xem chứ.&quot;<br />\r\n<br />\r\n&quot;Ai... , ngươi gan n&agrave;y nha...&quot; L&yacute; Việt bất đắc dĩ lắc đầu, Sở Ly c&aacute;i g&igrave; đều kh&ocirc;ng để &yacute;, c&aacute;i g&igrave; cũng d&aacute;m l&agrave;m.<br />\r\n<br />\r\nSở Ly về tiểu viện, viết hai c&aacute;i thiếp mời, cho Tr&aacute;c Phi Dương khi&ecirc;u chiến thiếp, cho Triệu Dĩnh mời thiếp.<br />\r\n<br />\r\nL&yacute; Việt kh&ocirc;ng r&otilde; hỏi: &quot;L&agrave;m sao k&iacute;nh xin Triệu c&ocirc; nương đến?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng mời Triệu sư muội, Tr&aacute;c Phi Dương sẽ chơi xấu!&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng đến nỗi chứ?&quot;<br />\r\n<br />\r\n&quot;C&aacute;i t&ecirc;n n&agrave;y c&oacute; thể kh&ocirc;ng ngươi nghĩ tới như vậy ch&iacute;nh trực, tiện đ&ecirc;́n mức r&acirc;́t!&quot;<br />\r\n<br />\r\n&quot;... Được rồi.&quot; L&yacute; Việt b&aacute;n t&iacute;n b&aacute;n nghi đ&aacute;p ứng rồi.</p>', '2021-11-27 21:47:41', 8, 1),
(199, 52, 'Kiếm pháp', '5', '<p>Kh&oacute;i s&oacute;ng m&ecirc;nh m&ocirc;ng hồ tr&ecirc;n, một c&aacute;i thuyền nhỏ ch&iacute;nh đi tới Đ&ocirc;ng Hoa vi&ecirc;n.<br />\r\n<br />\r\nThuyền nhỏ c&oacute; ba người, Bạch Tri Tiết gi&aacute; thuyền, Tr&aacute;c Phi Dương c&ugrave;ng Triệu Dĩnh đứng ở đầu thuyền, gi&oacute; nhẹ từ từ, hai người tay &aacute;o lướt nhẹ.<br />\r\n<br />\r\nTriệu Dĩnh một bộ xanh nhạt la sam, mặt ngọc trắng lo&aacute;ng, nhẹ giọng hỏi: &quot;Tr&aacute;c sư huynh, ngươi c&ugrave;ng Sở sư huynh đến c&ugrave;ng tại sao kết th&ugrave;?&quot;<br />\r\n<br />\r\nTr&aacute;c Phi Dương cau m&agrave;y ngẫm lại: &quot;Lần thứ nhất thấy hắn liền kh&ocirc;ng hợp mắt, ... Kh&ocirc;ng c&aacute;i g&igrave; c&oacute; thể nại, c&ograve;n ngưu h&ograve; h&eacute;t, vừa nh&igrave;n thấy liền cảm thấy ch&aacute;n gh&eacute;t, hận kh&ocirc;ng thể một chưởng vỗ chết hắn!&quot;<br />\r\n<br />\r\n&quot;C&aacute;c ngươi lại kh&ocirc;ng huyết hải th&acirc;m cừu, tại sao muốn như vậy a!&quot;<br />\r\n<br />\r\nTriệu Dĩnh lắc đầu cười cười, hai người đều rất ngạo, Tr&aacute;c sư huynh đ&uacute;ng l&agrave; c&oacute; ngạo kh&iacute; tư bản, Sở sư huynh kh&ocirc;ng thể luyện vũ nhưng thật giống như c&oacute; thị, kh&ocirc;ng đem Tr&aacute;c sư huynh để ở trong mắt, đ&acirc;y l&agrave; để Tr&aacute;c sư huynh tối kh&ocirc;ng c&aacute;ch n&agrave;o khoan dung.<br />\r\n<br />\r\nPh&aacute;t khi&ecirc;u chiến thiếp, n&agrave;y kh&ocirc;ng phải l&agrave; đ&ugrave;a giỡn.<br />\r\n<br />\r\nQuốc C&ocirc;ng phủ cấm chỉ tư đấu, một khi xuất hiện thương vong, nhẹ th&igrave; huỷ bỏ v&otilde; c&ocirc;ng trục xuất phủ, nặng th&igrave; xử tử, khi&ecirc;u chiến l&agrave; cho ph&eacute;p, đối phương c&oacute; quyền từ chối, nếu như đ&aacute;p ứng, chỉ cần bất tử kh&ocirc;ng phế liền kh&ocirc;ng truy cứu.<br />\r\n<br />\r\nSở sư huynh chủ động khi&ecirc;u chiến, bị Tr&aacute;c sư huynh đả thương, Quốc C&ocirc;ng phủ cũng sẽ kh&ocirc;ng quản.<br />\r\n<br />\r\nTr&aacute;c Phi Dương hừ n&oacute;i: &quot;Ta liền kh&ocirc;ng nh&igrave;n nổi hắn ngạo mạn d&aacute;ng vẻ!&quot;<br />\r\n<br />\r\nTriệu Dĩnh lắc đầu một c&aacute;i bật cười, ki&ecirc;u ngạo nh&acirc;n tối kh&ocirc;ng nh&igrave;n nổi người kh&aacute;c ki&ecirc;u ngạo.<br />\r\n<br />\r\n&quot;Tr&aacute;c sư huynh, ngươi thật muốn xuống tay &aacute;c độc?&quot;<br />\r\n<br />\r\n&quot;Đ&oacute; l&agrave; đương nhi&ecirc;n!&quot; Tr&aacute;c Phi Dương hừ n&oacute;i: &quot;Hắn muốn t&igrave;m c&aacute;i chết, ta người n&agrave;y th&iacute;ch nhất t&aacute;c th&agrave;nh người kh&aacute;c, để hắn triệt để th&agrave;nh r&aacute;c rưởi!&quot;<br />\r\n<br />\r\nTriệu Dĩnh nh&iacute;u m&agrave;y, t&iacute;nh kh&iacute; kh&ocirc;ng đầu liền xuống tay &aacute;c độc, qu&aacute; mức : &quot;Sở sư huynh tại sao d&aacute;m khi&ecirc;u chiến đ&acirc;y?&quot;<br />\r\n<br />\r\n&quot;Bị tức đ&ecirc;́n bị v&aacute;ng đầu th&ocirc;i!&quot; Tr&aacute;c Phi Dương đắc &yacute; hừ n&oacute;i: &quot;N&agrave;y ngạo mạn gia hỏa tuyệt kh&ocirc;ng chịu được khi ta thị vệ!&quot;<br />\r\n<br />\r\n&quot;Ai...&quot; Triệu Dĩnh lắc đầu một c&aacute;i: &quot;Tr&aacute;c sư huynh, kỳ thực c&aacute;c ngươi kh&ocirc;ng đại th&ugrave;, cần g&igrave; chứ!&quot;<br />\r\n<br />\r\n&quot;Triệu sư muội, ngươi kh&ocirc;ng hiểu.&quot; Tr&aacute;c Phi Dương kho&aacute;t tay chặn lại đ&aacute;nh g&atilde;y nàng: &quot;N&ecirc;n t&agrave;n nhẫn phải t&agrave;n nhẫn, c&aacute;i t&ecirc;n n&agrave;y... , bắt lấy cơ hội phải đ&aacute;nh chết!&quot;<br />\r\n<br />\r\n&quot;Vốn l&agrave; c&ugrave;ng trường...&quot; Triệu Dĩnh thở d&agrave;i.<br />\r\n<br />\r\nTr&aacute;c Phi Dương hừ n&oacute;i: &quot;Triệu sư muội, l&ograve;ng dạ đ&agrave;n b&agrave; kh&ocirc;ng được!&quot;<br />\r\n<br />\r\n&quot;Ai...&quot; Triệu Dĩnh bất đắc dĩ thở d&agrave;i, quyết t&acirc;m l&uacute;c mấu chốt ra tay ngăn cản hắn.<br />\r\n<br />\r\nNàng &acirc;m thầm o&aacute;n giận Sở Ly, địa thế c&ograve;n mạnh hơn người, n&ecirc;n c&uacute;i đầu phải c&uacute;i đầu, phục chịu thua liền tr&ocirc;i qua , cần g&igrave; phải c&ugrave;ng Tr&aacute;c sư huynh đấu đ&acirc;y!<br />\r\n<br />\r\nBạch Tri Tiết n&oacute;i: &quot;C&ocirc;ng tử, Triệu c&ocirc; nương, đến !&quot;<br />\r\n<br />\r\nBa người nh&igrave;n thấy một tiểu đảo nổi hồ tr&ecirc;n, thật giống ở tung bay theo gi&oacute;, tr&ecirc;n đảo c&acirc;y liễu vờn quanh, c&agrave;nh rủ xuống đến mặt hồ, b&agrave; sa y&ecirc;u kiều, như sương như kh&oacute;i.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nTr&aacute;c Phi Dương n&oacute;i: &quot;Tri Tiết, ngươi ở tr&ecirc;n thuyền chờ.&quot;<br />\r\n<br />\r\n&quot;C&ocirc;ng tử, cẩn thận!&quot; Bạch Tri Tiết n&oacute;i: &quot;D&ugrave; sao cũng l&agrave; địa b&agrave;n của bọn họ!&quot;<br />\r\n<br />\r\n&quot;Ta ch&iacute;nh l&agrave; muốn xem hắn s&aacute;i tr&ograve; gian g&igrave;!&quot; Tr&aacute;c Phi Dương vung vung tay: &quot;Tất cả hoa chi&ecirc;u ở mạnh mẽ v&otilde; c&ocirc;ng trước mặt đều kh&ocirc;ng đỡ nổi một đ&ograve;n!&quot;<br />\r\n<br />\r\n&quot;V&acirc;ng.&quot; Bạch Tri Tiết cung k&iacute;nh gật đầu.<br />\r\n<br />\r\nThuyền nhỏ chậm r&atilde;i cặp bờ, dưới c&acirc;y liễu huyền một ngọc kh&aacute;nh, quải một cổ chuy, Triệu Dĩnh vang l&ecirc;n ngọc kh&aacute;nh, thanh tiếng h&oacute;t vang vọng Đ&ocirc;ng Hoa vi&ecirc;n.<br />\r\n<br />\r\nSở Ly c&ugrave;ng L&yacute; Việt xuất hiện, Sở Ly b&ecirc;n h&ocirc;ng quải một thanh kiếm, oai h&ugrave;ng bất ph&agrave;m.<br />\r\n<br />\r\nSở Ly đứng chỗ cao, ở tr&ecirc;n cao nh&igrave;n xuống gật g&ugrave;: &quot;Tr&aacute;c Phi Dương, vẫn t&iacute;nh c&oacute; ch&uacute;t can đảm! ... Triệu sư muội, hoan ngh&ecirc;nh!&quot;<br />\r\n<br />\r\nTr&aacute;c Phi Dương gương mặt tuấn t&uacute; &acirc;m trầm lại, bị hắn như thế ở tr&ecirc;n cao nh&igrave;n xuống nh&igrave;n rất kh&oacute; chịu, cười lạnh n&oacute;i: &quot;Một m&igrave;nh ngươi r&aacute;c rưởi c&oacute; c&aacute;i g&igrave; đ&aacute;ng sợ!&quot;<br />\r\n<br />\r\nSở Ly quay đầu n&oacute;i: &quot;Triệu sư muội, đi vào uống ch&eacute;n tr&agrave; đi.&quot;<br />\r\n<br />\r\nTr&aacute;c Phi Dương gi&agrave;nh trước hừ n&oacute;i: &quot;Uống g&igrave; tr&agrave;, ai biết ngươi c&oacute; hay kh&ocirc;ng hạ độc, kh&ocirc;ng phải muốn khi&ecirc;u chiến ta sao? Đừng n&oacute;i nhảm, mau mau động thủ!&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng vội.&quot;<br />\r\n<br />\r\n&quot;H&atilde;y chấm dứt việc đ&oacute;!&quot; Tr&aacute;c Phi Dương g&agrave;o to n&oacute;i: &quot;Họ Sở, muốn động thủ mau mau động thủ, kh&ocirc;ng động thủ liền chịu thua, b&eacute; ngoan l&agrave;m thị vệ của ta!&quot;<br />\r\n<br />\r\n&quot;Ngươi nha, lỗ m&atilde;ng k&iacute;ch động!&quot; Sở Ly lắc đầu một c&aacute;i: &quot;Kh&oacute; th&agrave;nh đại kh&iacute;!&quot;<br />\r\n<br />\r\n&quot;Ngươi coi ch&iacute;nh m&igrave;nh l&agrave; ai, lời n&agrave;y c&oacute; tư c&aacute;ch n&oacute;i? !&quot; Tr&aacute;c Phi Dương cười gằn bĩu m&ocirc;i.<br />\r\n<br />\r\nSở Ly n&oacute;i: &quot;Muốn động thủ cũng được, thế n&agrave;o cũng phải c&oacute; ch&uacute;t đi&ecirc;̀m t&ocirc;́t, Tr&aacute;c Phi Dương, nếu như ta thắng rồi, ngươi l&agrave;m hộ vệ của ta!&quot;<br />\r\n<br />\r\n&quot;Nằm mơ!&quot; Tr&aacute;c Phi Dương nhất thời nở nụ cười: &quot;Ta n&oacute;i họ Sở, ngươi kh&ocirc;ng tỉnh đi, để ta l&agrave;m hộ vệ của ngươi?&quot;<br />\r\n<br />\r\n&quot;L&agrave;m sao, ngươi cảm gi&aacute;c m&igrave;nh sẽ bại?&quot;<br />\r\n<br />\r\n&quot;Đương nhi&ecirc;n sẽ kh&ocirc;ng!&quot;<br />\r\n<br />\r\n&quot;C&aacute;i kia v&igrave; sao kh&ocirc;ng d&aacute;m đ&aacute;p ứng? Tự tin kh&ocirc;ng đủ chứ?&quot;<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave; gọi l&agrave; kh&ocirc;ng d&aacute;m!&quot;<br />\r\n<br />\r\n&quot;C&ograve;n c&oacute; Nguyệt Quang lan, thua liền trả lại ta!&quot;<br />\r\n<br />\r\n&quot;Ngươi thật lớn khẩu vị!&quot; Tr&aacute;c Phi Dương một mặt tr&agrave;o ph&uacute;ng cười gằn, cắn răng: &quot;Vậy cũng l&agrave; 11,000 hai!&quot;<br />\r\n<br />\r\nVừa nghĩ tới c&aacute;i n&agrave;y hắn lửa giận đằng x&ocirc;ng l&ecirc;n tr&aacute;n nhi, 11,000 hai liền như thế bị h&atilde;m hại!<br />\r\n<br />\r\nTriệu Dĩnh cười n&oacute;i: &quot;Sở sư huynh, chỉ ngươi muốn thắng rồi, Nguyệt Quang lan cho ngươi!&quot;<br />\r\n<br />\r\nSở Ly &ocirc;m quyền cười n&oacute;i: &quot;Vậy th&igrave; đa tạ Triệu sư muội !&quot;<br />\r\n<br />\r\nN&agrave;y kh&ocirc;ng phải l&uacute;c kh&aacute;ch kh&iacute;, Nguyệt Quang lan quan hệ qu&aacute; trọng đại, thay đổi vận mệnh của m&igrave;nh!<br />\r\n<br />\r\nTriệu Dĩnh n&oacute;i: &quot;Sở sư huynh, ngươi kh&ocirc;ng phải l&agrave; kh&ocirc;ng thể luyện v&otilde; sao?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng thể luyện nội c&ocirc;ng t&acirc;m ph&aacute;p.&quot; Sở Ly lắc đầu: &quot;Nhưng kiếm ph&aacute;p vẫn l&agrave; c&oacute; thể luyện.&quot;<br />\r\n<br />\r\nTriệu Dĩnh tr&aacute;ch nhiệm nh&igrave;n hắn: &quot;C&aacute;i kia s&iacute;nh c&aacute;i g&igrave; cường!&quot;<br />\r\n<br />\r\nSở Ly liếc ch&eacute;o một ch&uacute;t Tr&aacute;c Phi Dương: &quot;Đối ph&oacute; hắn loại n&agrave;y ngu xuẩn, kh&ocirc;ng cần nội c&ocirc;ng!&quot;<br />\r\n<br />\r\n&quot;Họ Sở, khẩu kh&iacute; thật l&agrave; lớn!&quot; Tr&aacute;c Phi Dương li&ecirc;n tục cười lạnh: &quot;Kh&ocirc;ng nội c&ocirc;ng, ngươi lấy c&aacute;i g&igrave; đ&aacute;nh?&quot;<br />\r\n<br />\r\nSở Ly n&oacute;i: &quot;Ta luyện Ph&aacute; Vọng kiếm ph&aacute;p, trừng trị ngươi đầy đủ !&quot;<br />\r\n<br />\r\n&quot;Ph&aacute; Vọng kiếm ph&aacute;p? Ha ha!&quot; Tr&aacute;c Phi Dương cười ha ha: &quot;Liền ngươi phế vật n&agrave;y, ch&iacute;nh l&agrave; cầm bảo kiếm ch&eacute;m sắt như ch&eacute;m b&ugrave;n th&igrave; c&oacute; &iacute;ch lợi g&igrave;?&quot;<br />\r\n<br />\r\nTriệu Dĩnh cảm thấy Tr&aacute;c Phi Dương tuy n&oacute;i kh&oacute; nghe, nhưng thoại kh&ocirc;ng sai, kh&ocirc;ng nội c&ocirc;ng, kh&ocirc;ng ph&aacute;t huy ra kiếm ph&aacute;p uy lực.<br />\r\n<br />\r\nSở Ly hừ n&oacute;i: &quot;Tr&aacute;c Phi Dương, trước ti&ecirc;n n&oacute;i ngươi c&oacute; đ&aacute;p ứng hay kh&ocirc;ng chứ?&quot;<br />\r\n<br />\r\n&quot;Nếu như ngươi thua th&igrave; sao?&quot; Tr&aacute;c Phi Dương nụ cười hơi thu lại: &quot;Tiện nghi kh&ocirc;ng thể một m&igrave;nh ngươi chiếm chứ?&quot;<br />\r\n<br />\r\nSở Ly vỗ ngực một c&aacute;i: &quot;10 ng&agrave;n hai ng&acirc;n phiếu l&agrave; ngươi, ta rời đi Quốc C&ocirc;ng phủ!&quot;<br />\r\n<br />\r\n&quot;Ng&acirc;n phiếu ta c&oacute;, kh&ocirc;ng k&eacute;m 10 ng&agrave;n hai!&quot; Tr&aacute;c Phi Dương hừ n&oacute;i: &quot;Ngươi chỉ cần cút khỏi Quốc C&ocirc;ng phủ, kh&ocirc;ng &yacute; kiến ta mắt l&agrave; được!&quot;<br />\r\n<br />\r\n&quot;Liền như thế định , Triệu sư muội l&agrave;m chứng!&quot;<br />\r\n<br />\r\nTriệu Dĩnh nh&iacute;u m&agrave;y n&oacute;i: &quot;Sở sư huynh, c&aacute;c ngươi h&agrave; tất nh&aacute;o đến một bước n&agrave;y đ&acirc;y?&quot;<br />\r\n<br />\r\n&quot;Hết c&aacute;ch rồi, Tr&aacute;c Phi Dương bụng dạ hẹp h&ograve;i, khắp nơi t&igrave;m ta phiền phức, chỉ c&oacute; thể cho hắn điểm nhi m&agrave;u sắc nh&igrave;n một c&aacute;i, để hắn y&ecirc;n tĩnh y&ecirc;n tĩnh!&quot; Sở Ly lắc lắc đầu n&oacute;i.<br />\r\n<br />\r\n&quot;L&agrave;m Tr&aacute;c sư huynh thị vệ kỳ thực kh&ocirc;ng mất mặt.&quot; Triệu Dĩnh nỗ lực khuy&ecirc;n nhủ.<br />\r\n<br />\r\nSở Ly nh&igrave;n chằm chằm nàng nh&igrave;n mấy lần, bất đắc dĩ n&oacute;i: &quot;Ngươi l&agrave; c&aacute;i thi&ecirc;n thật thiện lương c&ocirc; nương tốt!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nTriệu Dĩnh thật kh&ocirc;ng tiện cười cười, hắn ki&ecirc;u căng tự m&atilde;n, l&agrave;m Tr&aacute;c sư huynh thị vệ x&aacute;c thực qu&aacute; l&agrave;m kh&oacute; hắn, Tr&aacute;c sư huynh l&agrave;m được qu&aacute; mức .<br />\r\n<br />\r\nSở Ly r&uacute;t kiếm ra khỏi vỏ: &quot;Tr&aacute;c Phi Dương, xuất kiếm đi!&quot;<br />\r\n<br />\r\nTr&aacute;c Phi Dương cười ngạo nghễ, cất bước đến hắn trước mặt, b&agrave;y ra chưởng ph&aacute;p thức mở đầu: &quot;Đối ph&oacute; ngươi c&ograve;n kh&ocirc;ng cần kiếm!&quot;<br />\r\n<br />\r\n&quot;Xem kiếm!&quot; Sở Ly một chi&ecirc;u kiếm đ&acirc;m ra.<br />\r\n<br />\r\nTr&aacute;c Phi Dương tay tr&aacute;i d&aacute;n v&agrave;o kiếm thiết đi vào, x&ocirc;ng thẳng b&ecirc;n trong cung, khinh bỉ cực điểm.<br />\r\n<br />\r\nSở Ly lui bước t&aacute;ch ra, một chi&ecirc;u kiếm đ&acirc;m ra.<br />\r\n<br />\r\n&quot;Ồ?&quot; Tr&aacute;c Phi Dương bất đắc dĩ biến chi&ecirc;u, m&agrave;y kiếm nhăn lại.<br />\r\n<br />\r\nSở Ly lại một chi&ecirc;u kiếm.<br />\r\n<br />\r\nTr&aacute;c Phi Dương lại biến chi&ecirc;u, quả đo&aacute;n thay đổi một bộ chưởng ph&aacute;p, kiếm thế mềm mại v&ocirc; lực, một mực đ&acirc;m hướng về chỗ yếu, họ Sở định l&agrave; biết r&otilde; bộ chưởng ph&aacute;p n&agrave;y.<br />\r\n<br />\r\nSở Ly lại một chi&ecirc;u kiếm đ&acirc;m ra.<br />\r\n<br />\r\nTr&aacute;c Phi Dương lực cũ đ&atilde; hết lực mới chưa sinh, trơ mắt nh&igrave;n kiếm đ&acirc;m tr&uacute;ng ch&iacute;nh m&igrave;nh cổ tay phải.<br />\r\n<br />\r\nSở Ly thu kiếm, cười h&iacute;p mắt : &quot;C&ograve;n muốn lại đ&aacute;nh?&quot;<br />\r\n<br />\r\n&quot;Trở lại!&quot; Tr&aacute;c Phi Dương khẽ cắn răng hừ n&oacute;i.<br />\r\n<br />\r\nSở Ly mũi kiếm vẩy một c&aacute;i.<br />\r\n<br />\r\nTr&aacute;c Phi Dương biến chưởng th&agrave;nh quyền, đột nhi&ecirc;n một quyền đảo ra, Sở Ly rất kiếm đ&acirc;m một c&aacute;i.<br />\r\n<br />\r\n&quot;A!&quot; Tr&aacute;c Phi Dương g&agrave;o l&ecirc;n đau đớn, b&agrave;n tay phải tr&uacute;ng kiếm.<br />\r\n<br />\r\nSở Ly sắc mặt t&aacute;i nhợt, lấy kiếm chống đỡ địa kh&ocirc;ng để cho m&igrave;nh ng&atilde; xuống, chi&ecirc;u kiếm n&agrave;y tụ tập sức lực to&agrave;n th&acirc;n, trong cơ thể tặc đi lầu trống, trống rỗng rất kh&oacute; chịu.<br />\r\n<br />\r\nMũi kiếm đ&acirc;m thủng Tr&aacute;c Phi Dương b&agrave;n tay.<br />\r\n<br />\r\nTriệu Dĩnh bận bịu lấy ra b&igrave;nh sứ, tung b&ocirc;i thuốc phấn cầm m&aacute;u, lo lắng nh&igrave;n hắn.<br />\r\n<br />\r\nTr&aacute;c Phi Dương trợn tr&ograve;n đ&ocirc;i mắt, gắt gao trừng mắt Sở Ly.</p>', '2021-11-27 21:48:07', 7, 1),
(200, 52, 'Tô Như', '6', '<p>Sở Ly n&oacute;i: &quot;Tr&aacute;c Phi Dương, c&ograve;n kh&ocirc;ng chịu thua?&quot;<br />\r\n<br />\r\nTriệu Dĩnh vội hỏi: &quot;Tr&aacute;c sư huynh!&quot;<br />\r\n<br />\r\nTr&aacute;c Phi Dương gắt gao trừng mắt Sở Ly, h&ocirc; hấp c&agrave;ng ng&agrave;y c&agrave;ng ồ ồ, kh&oacute; c&oacute; thể tin, kh&ocirc;ng cam l&ograve;ng, phẫn nộ, xoắn xu&yacute;t c&ugrave;ng nhau, ở trong l&ograve;ng lăn lộn .<br />\r\n<br />\r\nSở Ly lắc đầu cười cợt.<br />\r\n<br />\r\nTriệu Dĩnh nhẹ giọng n&oacute;i: &quot;Tr&aacute;c sư huynh!&quot;<br />\r\n<br />\r\nTr&aacute;c Phi Dương quay đầu nh&igrave;n n&agrave;ng, cuối c&ugrave;ng o&aacute;n hận phun ra ba chữ: &quot;Ta chịu thua!&quot;<br />\r\n<br />\r\n&quot;Đ&ugrave;ng đ&ugrave;ng đ&ugrave;ng đ&ugrave;ng đ&ugrave;ng đ&ugrave;ng!&quot; Hắn một hơi phiến ch&iacute;nh m&igrave;nh s&aacute;u b&agrave;n tay, gương mặt tuấn t&uacute; hiện l&ecirc;n d&agrave;y đặc hồng ấn.<br />\r\n<br />\r\nSở Ly lắc đầu bật cười: &quot;Thắng bại binh gia chuyện thường, đ&acirc;y l&agrave; cần g&igrave; chứ?&quot;<br />\r\n<br />\r\n&quot;Ngươi...&quot; Tr&aacute;c Phi Dương hai mắt tự phun lửa.<br />\r\n<br />\r\n&quot;Sở sư huynh, ngươi liền bớt tranh c&atilde;i một t&iacute; đi!&quot; Triệu Dĩnh sẵng giọng.<br />\r\n<br />\r\n&quot;Qu&ecirc;n đi, n&oacute;i rồi cũng kh&ocirc;ng d&ugrave;ng!&quot; Sở Ly vung vung tay: &quot;Trở về luyện thật giỏi c&ocirc;ng, chớ đem thổi phồng coi l&agrave; thật, đần độn coi ch&iacute;nh m&igrave;nh thực sự l&agrave; thi&ecirc;n t&agrave;i!&quot;<br />\r\n<br />\r\n&quot;H&ocirc;m nay ban tặng, tất c&oacute; b&aacute;o đ&aacute;p lớn!&quot; Tr&aacute;c Phi Dương cắn răng n&oacute;i ra c&acirc;u n&agrave;y, xoay người rời đi.<br />\r\n<br />\r\nTriệu Dĩnh hờn dỗi: &quot;Sở sư huynh, ngươi thật qu&aacute; mức rồi!&quot;<br />\r\n<br />\r\nSở Ly lắc đầu cười n&oacute;i: &quot;Triệu sư muội, hắn da mặt d&agrave;y cực k&igrave;, kh&ocirc;ng yếu ớt như vậy!&quot;<br />\r\n<br />\r\n&quot;Ta lập tức đem Nguyệt Quang lan đưa tới!&quot; Triệu Dĩnh kiều r&ecirc;n một tiếng, dậm ch&acirc;n một c&aacute;i quay đầu bước đi.<br />\r\n<br />\r\nNh&igrave;n ba người gi&aacute; thuyền rời đi, L&yacute; Việt cười ha ha hai tiếng, Sở Ly nhưng quơ quơ, bận bịu sử dụng kiếm chống đỡ ch&iacute;nh m&igrave;nh, sắc mặt c&agrave;ng ng&agrave;y c&agrave;ng trắng x&aacute;m.<br />\r\n<br />\r\n&quot;Huynh đệ?&quot; L&yacute; Việt muốn đi ph&ugrave;, bị Sở Ly giơ tay.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng nghi&ecirc;m trọng như vậy, d&ugrave;ng sức qu&aacute; độ.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i kia mau mau ngồi xuống.&quot; L&yacute; Việt vội hỏi.<br />\r\n<br />\r\nSở Ly chậm r&atilde;i trở lại Thanh Tang vườn hoa, đưa tay x&uacute;c một c&acirc;y Thanh Tang hoa, m&atilde;nh liệt linh kh&iacute; nhất thời tr&agrave;n v&agrave;o, hắn bỗng cảm thấy phấn chấn, vận chuyển hai lần Trường Xu&acirc;n C&ocirc;ng, tinh thần c&agrave;ng đủ, hết thảy uể oải ti&ecirc;u t&acirc;̣n.<br />\r\n<br />\r\nTrường Xu&acirc;n C&ocirc;ng l&agrave; hắn t&acirc;n luyện một m&ocirc;n t&acirc;m ph&aacute;p, Đạo gia chu thi&ecirc;n nội đan thuật, luyện t&iacute;nh k&eacute;o d&agrave;i mạng sống c&aacute;ch thức, ngũ cốc tinh kh&iacute; th&ocirc;i th&uacute;c, khử bệnh duy&ecirc;n ni&ecirc;n.<br />\r\n<br />\r\n&quot;Keng...&quot; Ngọc kh&aacute;nh vang l&ecirc;n, L&yacute; Việt đi ra ngo&agrave;i, rất nhanh chuyển Nguyệt Quang lan đi vào: &quot;Triệu c&ocirc; nương cũng thật l&agrave; người đ&aacute;ng tin, nhanh như vậy sẽ đưa đến rồi!&quot;<br />\r\n<br />\r\nSở Ly mừng rỡ nh&igrave;n Nguyệt Quang lan, ngoắc ngoắc tay.<br />\r\n<br />\r\nL&yacute; Việt đưa đến hắn phụ cận: &quot;Muốn di đi ra kh&ocirc;ng?&quot;<br />\r\n<br />\r\nSở Ly lắc đầu.<br />\r\n<br />\r\nTay phải hắn chạm v&agrave;o Nguyệt Quang lan, m&aacute;t mẻ linh kh&iacute; tr&agrave;n v&agrave;o, duy&ecirc;n Tiểu tẩy mạch quyết vận chuyển.<br />\r\n<br />\r\nSau đ&oacute; mấy ng&agrave;y, hắn hầu như vẫn ngồi ở Nguyệt Quang lan b&ecirc;n, cảm thụ Nguyệt Quang lan, điều tra Nguyệt Quang lan tập t&iacute;nh c&ugrave;ng quy luật.<br />\r\n<br />\r\nN&oacute; yếm thủy, nước sương liền đủ, kh&ocirc;ng thể lại tưới nước.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nN&oacute; cực kỳ hao tổn ph&igrave;, đặc biệt l&agrave; buổi tối ph&aacute;t s&aacute;ng, bồn b&ecirc;n trong b&ugrave;n đất cấp tốc cằn cỗi.<br />\r\n<br />\r\nSở Ly cuối c&ugrave;ng đem đ&aacute;y hồ nước b&ugrave;n hong kh&ocirc;, hỗn hợp hủ thổ thay bồn b&ecirc;n trong b&ugrave;n đất.<br />\r\n<br />\r\nNguyệt Quang lan đối với nước b&ugrave;n c&ugrave;ng hủ thổ rất kh&ocirc;ng th&iacute;ch ứng, lại như một t&ugrave;y hứng đứa nhỏ, th&agrave; rằng chết đ&oacute;i cũng kh&ocirc;ng ăn ch&aacute;n gh&eacute;t m&oacute;n ăn.<br />\r\n<br />\r\nSở Ly d&ugrave;ng Thanh Tang hoa linh kh&iacute; treo n&oacute; mệnh, một tuần hạ xuống, n&oacute; chậm r&atilde;i th&iacute;ch ứng, bắt đầu thu nạp độ ph&igrave;.<br />\r\n<br />\r\nSở Ly lần thứ hai cảm kh&aacute;i, kh&ocirc;ng Kh&ocirc; Vinh kinh, Nguyệt Quang lan thật kh&ocirc;ng sống nổi!<br />\r\n<br />\r\nMột th&aacute;ng qua, Sở Ly vẫn ở tại Nguyệt Quang lan b&ecirc;n người, kh&ocirc;ng để &yacute; đến chuyện b&ecirc;n ngo&agrave;i, từ nguy&ecirc;n bản Nguyệt Quang lan căn dưới ph&acirc;n ra một nh&aacute;nh mầm non, d&agrave;i đến một nửa cao.<br />\r\n<br />\r\nLinh kh&iacute; r&oacute;t v&agrave;o dưới, t&acirc;n Nguyệt Quang lan d&agrave;i đến cực nhanh, c&aacute;i n&agrave;y cũng l&agrave; hắn ph&aacute;t hiện mới diệu dụng &mdash;&mdash; gia tốc sinh trưởng.<br />\r\n<br />\r\nKh&ocirc; Vinh thụ thật giống l&agrave; hoa cỏ vương giả, c&oacute; thể khiến kh&ocirc; vinh, quyền sinh quyền s&aacute;t trong tay.<br />\r\n<br />\r\nHắn trong nh&aacute;y mắt liền d&acirc;ng l&ecirc;n v&ocirc; số &yacute; nghĩ, Nguyệt Quang lan như vậy, nếu như đổi th&agrave;nh những linh thảo kh&aacute;c đ&acirc;y, những thứ ở trong truyền thuyết thi&ecirc;n t&agrave;i địa bảo đ&acirc;y?<br />\r\n<br />\r\nNếu như c&oacute; thể t&igrave;m tới ch&uacute;ng n&oacute; hạt giống, Kh&ocirc; Vinh kinh liền c&oacute; thể khiến cho sinh trưởng, r&uacute;t ngắn sinh trưởng chu kỳ, chỗ tốt kh&oacute; c&oacute; thể tưởng tượng.<br />\r\n<br />\r\nNếu như mở một gian Kỳ thảo hi&ecirc;n, kiếm tiền như nước chảy.<br />\r\n<br />\r\nL&yacute; Việt vẫn th&uacute;c hắn đăng b&aacute;o, miễn cho bị Cố Lập Đồng gi&agrave;nh trước, Sở Ly vẫn đ&egrave; l&ecirc;n, kh&ocirc;ng bồi dưỡng được t&acirc;n Nguyệt Quang lan, dịch sinh chi tiết, kh&oacute; ứng ph&oacute; nghi vấn giả.<br />\r\n<br />\r\nHuống hồ, Kh&ocirc; Vinh kinh để hắn sức lực mười phần.<br />\r\n<br />\r\n&mdash;&mdash;<br />\r\n<br />\r\nChạng vạng, mặt trời chiều ng&atilde; về t&acirc;y, Sở Ly ngồi ở hai c&acirc;y Nguyệt Quang lan b&ecirc;n luyện c&ocirc;ng, L&yacute; Việt từ b&ecirc;n ngo&agrave;i trở về, đặt m&ocirc;ng ngồi dưới đất.<br />\r\n<br />\r\n&quot;Huynh đệ, c&oacute; c&aacute;i tin tức...&quot;<br />\r\n<br />\r\n&quot;Cố Lập Đồng th&agrave;nh c&ocirc;ng ?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải Cố Lập Đồng, l&agrave; Tr&aacute;c Phi Dương!&quot;<br />\r\n<br />\r\n&quot;Hắn &mdash;&mdash;?&quot;<br />\r\n<br />\r\n&quot;Tr&aacute;c Phi Dương ng&agrave;y h&ocirc;m qua xuất quan, x&ocirc;ng Cửu Phẩm l&acirc;u... , hắn hiện tại l&agrave; thất phẩm hộ vệ!&quot;<br />\r\n<br />\r\n&quot;Biết sỉ sau đ&oacute; dũng, tức giận phấn đấu, &quot; Sở Ly hững hờ gật g&ugrave;: &quot;Cũng kh&ocirc;ng t&iacute;nh lạ kỳ, hắn thi&ecirc;n ph&uacute; quả thật kh&ocirc;ng tệ.&quot;<br />\r\n<br />\r\nL&yacute; Việt kinh ngạc đ&ecirc;́n nh&igrave;n hắn: &quot;Ngươi kh&ocirc;ng lo lắng?&quot;<br />\r\n<br />\r\n&quot;Lo lắng trả th&ugrave; ta?&quot;<br />\r\n<br />\r\n&quot;Hắn vạn nhất lại t&igrave;m lại đ&acirc;y, kh&ocirc;ng hẳn l&agrave; đối thủ a!&quot;<br />\r\n<br />\r\n&quot;Vậy th&igrave; từ chối chứ.&quot; Sở Ly cười cợt.<br />\r\n<br />\r\nQuốc C&ocirc;ng phủ phủ quy l&agrave; bảo vệ người yếu, kh&ocirc;ng giống phủ ở ngo&agrave;i như vậy nhược nhục cường thực, đương nhi&ecirc;n, thật muốn trải qua thư thư phục phục, hay l&agrave; muốn nỗ lực.<br />\r\n<br />\r\nL&yacute; Việt trợn mắt l&ecirc;n: &quot;Từ chối? Chuyện n&agrave;y...&quot;<br />\r\n<br />\r\nSở Ly than bu&ocirc;ng tay: &quot;Ch&uacute;ng ta d&ugrave; sao cũng l&agrave; thị vệ, sao c&oacute; thể c&oacute; thể c&ugrave;ng hộ vệ luận v&otilde; c&ocirc;ng, bọn họ c&oacute; loại c&ugrave;ng ch&uacute;ng ta so với trồng hoa!&quot;<br />\r\n<br />\r\nL&yacute; Việt lo lắng than thở: &quot;Huynh đệ, lu&ocirc;n cảm thấy c&oacute; ch&uacute;t... Kh&ocirc;ng th&iacute;ch hợp.&quot;<br />\r\n<br />\r\nSở Ly cười n&oacute;i: &quot;Được rồi, đem tr&aacute;i tim thả trong bụng l&agrave; được, ng&agrave;y mai đăng b&aacute;o viện B&aacute;ch Thảo đi.&quot;<br />\r\n<br />\r\nHắn t&acirc;m trạng cảnh gi&aacute;c, Tr&aacute;c Phi Dương kh&ocirc;ng phải loại kia rộng lượng, c&oacute; cừu o&aacute;n tất b&aacute;o.<br />\r\n<br />\r\n&quot;Ch&iacute;nh l&agrave; m&agrave;!&quot; L&yacute; Việt nhất thời trở n&ecirc;n hưng phấn: &quot;Đ&atilde; sớm n&ecirc;n b&aacute;o l&ecirc;n, miễn cho đ&ecirc;m d&agrave;i lắm mộng, ng&agrave;y mai ăn cơm liền đi... , vẫn l&agrave; hiện tại liền đi th&ocirc;i!&quot;<br />\r\n<br />\r\nHắn đứng dậy liền đi.<br />\r\n<br />\r\nSở Ly trầm mặt xuống đến, đăm chi&ecirc;u, Tr&aacute;c Phi Dương đủ cứng cỏi, lần trước nh&igrave;n như thắng phải cho dịch, nhưng l&agrave; đem hết to&agrave;n lực.<br />\r\n<br />\r\nTr&uacute;c cơ c&ograve;n c&oacute; năm th&aacute;ng, c&oacute; thể năm th&aacute;ng sau Tr&aacute;c Phi Dương sẽ c&agrave;ng mạnh hơn!<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m c&ugrave;ng L&yacute; Việt vội v&atilde; lại đ&acirc;y, xem qua t&acirc;n Nguyệt Quang lan, vui mừng kh&ocirc;n xiết, vỗ Sở Ly vai cười ha ha.<br />\r\n<br />\r\n&quot;Kh&aacute; lắm, thật sự c&oacute; ngươi!&quot; Mạnh Kh&aacute;nh L&acirc;m cười to: &quot;Thật bị ngươi biến th&agrave;nh r&ocirc;̀i!&quot;<br />\r\n<br />\r\nSở Ly cười cợt: &quot;May mắn th&agrave;nh c&ocirc;ng.&quot;<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m vung vung tay đặt m&ocirc;ng cố định tr&ecirc;n: &quot;Tiểu Sở, kh&ocirc;ng l&agrave;m giả chứ? Chớ v&igrave; thắng Cố Lập Đồng liền s&aacute;i hoa hoạt!&quot;<br />\r\n<br />\r\nL&yacute; Việt lớn tiếng n&oacute;i: &quot;Mạnh l&atilde;o &mdash;&mdash;!&quot;<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m mắt nhỏ lườm hắn một c&aacute;i: &quot;K&iacute;ch động c&aacute;i c&aacute;i g&igrave; k&iacute;nh, ch&iacute;nh l&agrave; tr&ocirc;i chảy hỏi một ch&uacute;t, n&agrave;y c&oacute; thể quan hệ đến tiểu thư mặt mũi, kh&ocirc;ng th&agrave;nh c&ocirc;ng kh&ocirc;ng quan trọng lắm, giở tr&ograve; bịp bợm c&agrave;ng mất mặt!&quot;<br />\r\n<br />\r\nSở Ly ngồi xuống, tay x&uacute;c Nguyệt Quang lan, linh kh&iacute; vận chuyển kh&ocirc;ng ngớt: &quot;Mạnh l&atilde;o, n&agrave;y một c&acirc;y l&agrave; mầm non bồi dưỡng, c&aacute;i kia c&acirc;y l&agrave; hơn một th&aacute;ng trước ở Kỳ thảo hi&ecirc;n mua.&quot;<br />\r\n<br />\r\n&quot;Mua một c&acirc;y liền biến th&agrave;nh ?&quot; Mạnh Kh&aacute;nh L&acirc;m cười hỏi.<br />\r\n<br />\r\nSở Ly cười gật g&ugrave;.<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m nở nụ cười, Cố Lập Đồng mua kh&ocirc;ng xuống mười mấy c&acirc;y Nguyệt Quang lan, bỏ ra gi&aacute; cao, vẫn l&agrave; kh&ocirc;ng nu&ocirc;i sống, Sở Ly một c&acirc;y liền hoạt, vận may n&agrave;y!<br />\r\n<br />\r\nL&yacute; Việt n&oacute;i: &quot;Mạnh l&atilde;o, c&aacute;i kia Kỳ thảo hi&ecirc;n n&oacute;i năm nay chỉ c&oacute; n&agrave;y một c&acirc;y, Nguyệt Quang đảo b&ecirc;n kia xảy ra ch&uacute;t vấn đề, ch&uacute;ng ta muốn mua cũng kh&ocirc;ng địa phương lại mua a!&quot;<br />\r\n<br />\r\n&quot;N&agrave;y, người l&agrave;m ăn sao c&oacute; thể coi l&agrave; thật, đầu cơ kiếm lợi, buộc c&aacute;c ngươi mua chứ.&quot; Mạnh Kh&aacute;nh L&acirc;m cười n&oacute;i: &quot;C&aacute;c ngươi vẫn đ&uacute;ng l&agrave; tin r&ocirc;̀i?&quot;<br />\r\n<br />\r\n&quot;Mạnh l&atilde;o, &quot; Sở Ly cười h&iacute;p mắt n&oacute;i: &quot;Kim h&ocirc;m sau, Nguyệt Quang lan kh&ocirc;ng c&ograve;n l&agrave; Kỳ thảo hi&ecirc;n một nh&agrave; độc nhất nắm giữ r&ocirc;̀i.&quot;<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m đ&aacute;nh gi&aacute; b&aacute;n cao Nguyệt Quang lan, xoa xoa tay: &quot;Nhanh đến tối , ta muốn thưởng thức một hồi Nguyệt Quang lan phong th&aacute;i!&quot;<br />\r\n<br />\r\nHắn ngược lại kh&ocirc;ng l&agrave; ho&agrave;i nghi Sở Ly, Sở Ly l&agrave; t&ecirc;n th&ocirc;ng minh, sẽ kh&ocirc;ng đồ trước mắt lợi &iacute;ch đem m&igrave;nh tiền đồ li&ecirc;n lụy.<br />\r\n<br />\r\nChuyện n&agrave;y căn bản kh&ocirc;ng cho ph&eacute;p giở tr&ograve; bịp bợm, Nguyệt Quang lan ph&aacute;t triển th&agrave;nh c&ocirc;ng, nhất định phải trải rộng b&ecirc;n trong phủ hết thảy hoa vi&ecirc;n, c&oacute; thể mua một c&acirc;y hai c&acirc;y, mười c&acirc;y tám c&acirc;y, c&oacute; thể mua kh&ocirc;ng được m&acirc;́y trăm c&acirc;y, Kỳ thảo hi&ecirc;n kh&ocirc;ng nhiều như vậy Nguyệt Quang lan!<br />\r\n<br />\r\nHắn hỏi những n&agrave;y l&agrave; cho bọn họ nhắc nhở một ch&uacute;t, T&acirc;y Hoa vi&ecirc;n một mạch tuyệt sẽ kh&ocirc;ng cam l&ograve;ng, nghĩ trăm phương ng&agrave;n kế phủ định, kh&ocirc;ng cho Sở Ly l&ocirc;̣ đ&acirc;̀u.<br />\r\n<br />\r\nL&yacute; Việt T&uacute; một c&aacute;i tr&ugrave; nghệ, l&agrave;m t&aacute;m đạo m&oacute;n ăn hai c&aacute;i thang, ba người ăn được vị no đỗ vi&ecirc;n.<br />\r\n<br />\r\nM&agrave;n đ&ecirc;m bu&ocirc;ng xuống, Nguyệt Quang bay l&ecirc;n, Sở Ly bọn họ trước mắt cũng c&oacute; hai vòng Huyền Nguyệt, hai c&acirc;y Nguyệt Quang lan tỏa s&aacute;ng, mỹ đ&ecirc;́n kh&oacute; m&agrave; tin nổi.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Ai...&quot; Mạnh Kh&aacute;nh L&acirc;m một mặt m&ecirc; say, thở d&agrave;i n&oacute;i: &quot;Qu&aacute; đẹp r&ocirc;̀i!&quot;<br />\r\n<br />\r\nSở Ly cười n&oacute;i: &quot;Thấy nhiều cũng l&agrave; như vậy.&quot;<br />\r\n<br />\r\n&quot;S&aacute;t phong cảnh!&quot; Mạnh Kh&aacute;nh L&acirc;m ngu&yacute;t hắn một c&aacute;i hừ n&oacute;i: &quot;Ch&iacute;nh l&agrave; coi trọng một trăm năm cũng sẽ kh&ocirc;ng ch&aacute;n!&quot;<br />\r\n<br />\r\nSở Ly lắc đầu cười cười, mặt trăng l&agrave; đẹp, c&oacute; thể c&oacute; mấy người mỗi ng&agrave;y buổi tối ngắm trăng.<br />\r\n<br />\r\n&quot;Tiểu Sở, chuẩn bị xong chưa?&quot; Mạnh Kh&aacute;nh L&acirc;m nh&igrave;n Nguyệt Quang lan n&oacute;i rằng.<br />\r\n<br />\r\nSở Ly cười n&oacute;i: &quot;Chuẩn bị c&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\n&quot;Ngươi muốn nổi danh .&quot; Mạnh Kh&aacute;nh L&acirc;m vỗ vỗ bả vai hắn: &quot;So với Cố Lập Đồng t&ecirc;n kh&iacute; c&agrave;ng to lớn hơn!&quot;<br />\r\n<br />\r\nSở Ly cười cợt.<br />\r\n<br />\r\n&quot;Li&ecirc;n quan với tiến v&agrave;o dược vi&ecirc;n, ngươi đ&ecirc;́n ki&ecirc;n nhẫn ch&uacute;t nhi, T&acirc;y Hoa vi&ecirc;n sẽ liều mạng ngăn, nhưng ngươi c&ocirc;ng lao ở chỗ n&agrave;y, bọn họ kh&ocirc;ng ngăn được!&quot;<br />\r\n<br />\r\n&quot;V&acirc;ng, xin nhờ mạnh gi&agrave; rồi.&quot;<br />\r\n<br />\r\n&quot;Ai... , th&aacute;c ngươi ph&uacute;c, ch&uacute;ng ta Đ&ocirc;ng Hoa vi&ecirc;n một mạch cuối c&ugrave;ng cũng coi như c&oacute; thể ngẩng đầu l&ecirc;n!&quot;<br />\r\n<br />\r\nMột Cố Lập Đồng &eacute;p tới Đ&ocirc;ng Hoa vi&ecirc;n một mạch thở kh&ocirc;ng l&ecirc;n kh&iacute;, Cố Lập Đồng loại thi&ecirc;n t&agrave;i n&agrave;y l&agrave; rất hiếm thấy.<br />\r\n<br />\r\nNgười l&agrave;m vườn lại như b&aacute;c sĩ, học đồng dạng s&aacute;ch thuốc, y thuật nhưng có cao thấp, then chốt vẫn l&agrave; thi&ecirc;n ph&uacute;.<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m k&ecirc;u l&ecirc;n: &quot;Mang rượu tới!&quot;<br />\r\n<br />\r\nL&yacute; Việt chạy vui vẻ đem ra hai v&ograve; rượu, Mạnh Kh&aacute;nh L&acirc;m thoải m&aacute;i ch&egrave; ch&eacute;n nửa v&ograve;, rất nhanh hu&acirc;n hu&acirc;n nhi&ecirc;n, nằm tr&ecirc;n đất ngủ say như chết, m&atilde;i đến tận s&aacute;ng sớm ng&agrave;y thứ hai mới rời khỏi.<br />\r\n<br />\r\nMạnh Kh&aacute;nh L&acirc;m rời đi kh&ocirc;ng bao l&acirc;u, một c&aacute;i thuyền nhỏ phi&ecirc;u phi&ecirc;u m&agrave; đến, tr&ecirc;n thuyền đứng một hạnh ho&agrave;ng la sam thiếu nữ, khu&ocirc;n mặt đẹp dị thường, mắt hạnh dịu d&agrave;ng, mũi duy&ecirc;n d&aacute;ng, miệng anh đ&agrave;o nhỏ, đ&uacute;ng như người trong bức họa vật.<br />\r\n<br />\r\nNàng nhẹ nh&agrave;ng rơi xuống tr&ecirc;n bờ, vang l&ecirc;n ngọc kh&aacute;nh.<br />\r\n<br />\r\nSở Ly c&ugrave;ng L&yacute; Việt xuất hiện, L&yacute; Việt lớn tiếng k&ecirc;u l&ecirc;n: &quot;T&ocirc; tổng quản, kh&ocirc;ng c&oacute; từ xa tiếp đ&oacute;n, kh&ocirc;ng c&oacute; từ xa tiếp đ&oacute;n!&quot;<br />\r\n<br />\r\nSở Ly &ocirc;m quyền thi lễ, nghe L&yacute; Việt gọi đ&ecirc;́n nhiệt t&igrave;nh như vậy, lập tức biết rồi vị n&agrave;y th&acirc;n phận, tam tiểu thư Ti&ecirc;u Kỳ thiếp th&acirc;n hầu g&aacute;i T&ocirc; Như.<br />\r\n<br />\r\nTừ nhỏ ở tại tam tiểu thư b&ecirc;n người, danh nghĩa l&agrave; chủ tớ, t&igrave;nh c&ugrave;ng tỷ muội, ở trong phủ địa vị cực cao, l&agrave; Ngọc Kỳ đảo tổng quản, đối với thị vệ m&agrave; n&oacute;i, một lời c&oacute; thể quyết tiền đồ vận mệnh.<br />\r\n<br />\r\nT&ocirc; Như h&eacute; miệng cười khẽ, l&uacute;c lắc tay ngọc: &quot;Được rồi, đừng nghịch những n&agrave;y hư, ta nghe Mạnh l&atilde;o n&oacute;i c&aacute;c ngươi l&agrave;m ra Nguyệt Quang lan?&quot;<br />\r\n<br />\r\n&quot;Ch&iacute;nh l&agrave;!&quot; L&yacute; Việt tự h&agrave;o gật g&ugrave;: &quot;Sở huynh đệ l&agrave;m ra đến!&quot;<br />\r\n<br />\r\nT&ocirc; Như dịu d&agrave;ng như nước s&oacute;ng mắt rơi xuống Sở Ly tr&ecirc;n mặt.<br />\r\n<br />\r\nSở Ly mỉm cười &ocirc;m quyền: &quot;Sở Ly gặp T&ocirc; tổng quản.&quot;<br />\r\n<br />\r\nT&ocirc; Như cười n&oacute;i: &quot;C&ugrave;ng Tr&aacute;c Phi Dương kh&ocirc;ng hợp nhau c&aacute;i kia?&quot;<br />\r\n<br />\r\nSở Ly thật kh&ocirc;ng tiện cười cười.</p>', '2021-11-27 21:48:34', 8, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(201, 43, 'Lâm Nính San', '6', '<p>Trương Nhược Trần, L&acirc;m Phi, thị nữ V&acirc;n Nhi ngồi một cỗ Linh M&atilde; cổ xa, chầm chậm đi ra V&acirc;n V&otilde; ho&agrave;ng cung.<br />\r\n<br />\r\nLinh M&atilde;, cũng kh&ocirc;ng phải thật sự l&agrave; ngựa, m&agrave; l&agrave; một loại tr&ecirc;n đầu mọc ra độc gi&aacute;c Man th&uacute;, th&acirc;n thể cao tới hơn ba mét, tựa như l&agrave; một đầu Tiểu Tượng, chạy tốc độ l&agrave; phổ th&ocirc;ng chiến m&atilde; gấp năm lần trở l&ecirc;n. Trong đ&oacute; một ch&uacute;t cường tr&aacute;ng Linh M&atilde;, thậm ch&iacute; c&oacute; thể l&agrave;m đến ng&agrave;y đi ba ng&agrave;n dặm.<br />\r\n<br />\r\nLinh M&atilde; cổ xa cũng kh&ocirc;ng biết đi bao xa, dần dần ngừng lại.<br />\r\n<br />\r\nTrương Nhược Trần từ trong xe đi xuống, nh&igrave;n c&aacute;ch đ&oacute; kh&ocirc;ng xa v&agrave;ng son lộng lẫy hai phiến đại m&ocirc;n, lại nh&igrave;n một ch&uacute;t treo ở đại m&ocirc;n đỉnh ch&oacute;p viền v&agrave;ng tấm biển, ph&iacute;a tr&ecirc;n viết lấy hai c&aacute;i cứng c&aacute;p hữu lực chữ lớn: &quot;L&acirc;m phủ!&quot;<br />\r\n<br />\r\nTrong l&ograve;ng của hắn khẽ động, mẫu th&acirc;n chẳng phải họ &quot;L&acirc;m&quot; ?<br />\r\n<br />\r\nNh&igrave;n t&ograve;a phủ đệ n&agrave;y kh&iacute; ph&aacute;i bộ d&aacute;ng, L&acirc;m gia cũng kh&ocirc;ng phải l&agrave; c&aacute;i g&igrave; tiểu gia nh&agrave; ngh&egrave;o, m&agrave; l&agrave; một ph&aacute;i đại gia tộc phong phạm.<br />\r\n<br />\r\nNếu l&agrave; mẫu th&acirc;n nh&agrave; mẹ đẻ thật l&agrave; đại gia tộc, lại v&igrave; sao kh&ocirc;ng chiếm được nh&agrave; mẹ đẻ duy tr&igrave;, ngược lại tại Quận Vương trong phủ khắp nơi nhận kh&aacute;c Vương phi ức hiếp?<br />\r\n<br />\r\nKhẳng định c&oacute; ẩn t&igrave;nh.<br />\r\n<br />\r\nL&acirc;m Phi cũng từ trong xe ngựa đi xuống, ngẩng đầu nh&igrave;n một ch&uacute;t c&aacute;i kia một c&aacute;i tức l&agrave; quen thuộc m&agrave; xa lạ đại m&ocirc;n, n&oacute;i: &quot;Trần Nhi, ngươi khẳng định đ&atilde; sớm muốn gặp N&iacute;nh San đi! Hiện tại ngươi cũng mở ra Thần V&otilde; Ấn K&yacute;, tin tưởng ngươi c&ugrave;ng N&iacute;nh San c&oacute; rất nhiều lời đề c&oacute; thể đ&agrave;m, ngươi nhưng nhất định phải ủng hộ a!&quot;<br />\r\n<br />\r\nCho đến b&acirc;y giờ, Trương Nhược Trần cũng kh&ocirc;ng biết N&iacute;nh San rốt cuộc l&agrave; ai, nghe được V&acirc;n Nhi c&ugrave;ng L&acirc;m Phi, lu&ocirc;n c&oacute; một loại cảm gi&aacute;c l&agrave; lạ.<br />\r\n<br />\r\nTại L&acirc;m Phi dẫn đầu dưới, Trương Nhược Trần c&ugrave;ng V&acirc;n Nhi c&ugrave;ng đi tiến L&acirc;m phủ.<br />\r\n<br />\r\nVương phi về đến gia tộc, tr&ecirc;n l&yacute; luận l&agrave; hẳn l&agrave; nhận long trọng ngh&ecirc;nh đ&oacute;n, nhưng l&agrave; đem L&acirc;m Phi c&ugrave;ng Trương Nhược Trần ngh&ecirc;nh đ&oacute;n tiến đại m&ocirc;n vẻn vẹn chỉ l&agrave; một c&aacute;i lớn tuổi quản gia.<br />\r\n<br />\r\nL&acirc;m Phi bị vị kia l&atilde;o quản gia mang đến nội phủ, Trương Nhược Trần c&ugrave;ng V&acirc;n Nhi th&igrave; lưu tại b&ecirc;n ngo&agrave;i phủ, chỉ c&oacute; hai vị thị nữ lưu lại tiếp đ&atilde;i bọn hắn.<br />\r\n<br />\r\nTrương Nhược Trần lu&ocirc;n cảm gi&aacute;c bầu kh&ocirc;ng kh&iacute; rất kh&ocirc;ng th&iacute;ch hợp, nhưng lại kh&ocirc;ng biết n&ecirc;n như thế n&agrave;o hỏi thăm, chỉ c&oacute; thể tiếp tục giữ y&ecirc;n lặng.<br />\r\n<br />\r\n&quot;Cửu vương tử, ngươi kh&ocirc;ng đi gặp N&iacute;nh San sao? N&agrave;ng hiện tại hẳn l&agrave; Diễn V&otilde; Trường, L&acirc;m gia những kia tuổi trẻ tử đệ, hẳn l&agrave; đều ở nơi đ&oacute; tu luyện.&quot; V&acirc;n Nhi hỏi.<br />\r\n<br />\r\nBị V&acirc;n Nhi c&ugrave;ng L&acirc;m Phi đề mấy lần, Trương Nhược Trần trong l&ograve;ng liền c&agrave;ng th&ecirc;m hiếu kỳ, một c&aacute;i kia gọi l&agrave; &quot;N&iacute;nh San&quot; nữ tử đến c&ugrave;ng l&agrave; ai? Đi gặp một lần, hẳn l&agrave; cũng kh&ocirc;ng sao.<br />\r\n<br />\r\n&quot;Ừm! Đi th&ocirc;i, đi L&acirc;m gia Diễn V&otilde; Trường.&quot; Trương Nhược Trần gật đầu n&oacute;i.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n<br />\r\n...<br />\r\n<br />\r\nL&acirc;m gia nội phủ.<br />\r\n<br />\r\nMột t&ograve;a cổ k&iacute;nh m&ugrave;i hương cổ xưa trong th&iacute;nh đường, L&acirc;m gia gia chủ L&acirc;m Phụng Ti&ecirc;n tứ b&igrave;nh b&aacute;t ổn ngồi tại tr&ecirc;n ghế b&agrave;nh.<br />\r\n<br />\r\nHắn nh&igrave;n qua chừng 30 tuổi, tr&ecirc;n m&ocirc;i phương giữ lại hai phiết chỉnh tề sợi r&acirc;u, hướng về ngồi tại đối diện L&acirc;m Phi nh&igrave;n tho&aacute;ng qua, n&oacute;i: &quot;Cửu vương tử ch&iacute;nh l&agrave; Quận Vương phủ con ch&aacute;u đời sau, coi như mở ra Thần V&otilde; Ấn K&yacute;, cũng n&ecirc;n đi Quận Vương phủ T&agrave;ng Thư C&aacute;c chọn lựa tu luyện c&ocirc;ng ph&aacute;p, L&acirc;m Phi nương nương v&igrave; sao muốn đến L&acirc;m gia y&ecirc;u cầu tu luyện c&ocirc;ng ph&aacute;p?&quot;<br />\r\n<br />\r\nL&acirc;m Phi nhẹ nh&agrave;ng cắn m&ocirc;i một c&aacute;i, n&oacute;i: &quot;Kh&ocirc;ng phải y&ecirc;u cầu, l&agrave; ta thỉnh cầu Đại huynh xem ở Trần Nhi l&agrave; ngươi th&acirc;n ngoại sinh về mặt t&igrave;nh cảm, c&oacute; thể cho hắn một quyển tu luyện c&ocirc;ng ph&aacute;p.&quot;<br />\r\n<br />\r\n&quot;Bành!&quot;<br />\r\n<br />\r\nL&acirc;m Phụng Ti&ecirc;n hừ lạnh một tiếng, một chưởng vỗ ở tr&ecirc;n b&agrave;n, n&oacute;i: &quot;Hiện tại biết đi cầu ta người huynh trưởng n&agrave;y rồi? Hiện tại đến c&ugrave;ng ta van xin hộ ph&acirc;n? Ba năm trước đ&acirc;y, ta đi Quận Vương phủ cầu ngươi thời điểm, ngươi tại sao kh&ocirc;ng c&oacute; xem ở thần dụ l&agrave; ngươi th&acirc;n ngoại sinh về mặt t&igrave;nh cảm cứu hắn? Ngươi biết r&otilde;, thần dụ l&agrave; ta con trai trưởng, c&agrave;ng l&agrave; L&acirc;m gia trăm năm kh&oacute; ra một vị tuyệt thế thi&ecirc;n t&agrave;i, chỉ cần ngươi đi cầu Quận Vương, lu&ocirc;n c&oacute; thể đem hắn bảo trụ, nhưng l&agrave;, ngươi nhưng kh&ocirc;ng c&oacute;...&quot;<br />\r\n<br />\r\n&quot;Ba năm trước đ&acirc;y...&quot; L&acirc;m Phi mười phần ủy khuất, nhịn kh&ocirc;ng được rơi lệ, muốn n&oacute;i ra ba năm trước đ&acirc;y ch&acirc;n tướng.<br />\r\n<br />\r\nNhưng l&agrave;, L&acirc;m Phụng Ti&ecirc;n lại đ&aacute;nh g&atilde;y n&agrave;ng, n&oacute;i: &quot;Ngươi đi đi! L&acirc;m gia c&ugrave;ng ngươi cũng sớm đ&atilde; &acirc;n đoạn nghĩa tuyệt, sau n&agrave;y cũng đừng trở lại nữa. L&acirc;m Phi nương nương.&quot;<br />\r\n<br />\r\n&quot;Đ&ocirc;ng!&quot;<br />\r\n<br />\r\nL&acirc;m Phi trực tiếp quỳ tr&ecirc;n mặt đất, kh&ocirc;ng ngừng rơi lệ, thanh &acirc;m nghẹn ng&agrave;o m&agrave; n&oacute;i: &quot;Đại ca, ngươi giống như n&agrave;y tuyệt t&igrave;nh sao? Ta muốn gặp cha.&quot;<br />\r\n<br />\r\n&quot;Cha đi Thi&ecirc;n Ma Lĩnh, ba th&aacute;ng về sau mới c&oacute; thể trở về, ngươi b&acirc;y giờ kh&ocirc;ng gặp được hắn.&quot; L&acirc;m Phụng Ti&ecirc;n đạm mạc m&agrave; n&oacute;i: &quot;C&ograve;n c&oacute; một việc, N&iacute;nh San c&ugrave;ng Thất vương tử điện hạ liền muốn đ&iacute;nh h&ocirc;n, gọi Cửu vương tử điện hạ sau n&agrave;y c&aacute;ch N&iacute;nh San xa một ch&uacute;t.&quot;<br />\r\n<br />\r\nL&acirc;m Phi trong l&ograve;ng c&agrave;ng th&ecirc;m tuyệt vọng, n&oacute;i: &quot;Ngươi biết r&otilde; Trần Nhi một mực ưa th&iacute;ch N&iacute;nh San, hắn nếu l&agrave; biết N&iacute;nh San c&ugrave;ng Thất vương tử đ&iacute;nh h&ocirc;n, sẽ cỡ n&agrave;o thương t&acirc;m? Lại n&oacute;i, thế n&agrave;o lại l&agrave; Thất vương tử?&quot;<br />\r\n<br />\r\nL&acirc;m Phụng Ti&ecirc;n n&oacute;i: &quot;Thất vương tử điện hạ tại l&uacute;c ba tuổi, liền mở ra Thất phẩm Thần V&otilde; Ấn K&yacute;, thi&ecirc;n tư cỡ n&agrave;o kinh diễm? Lấy hắn b&acirc;y giờ tu vi V&otilde; Đạo, to&agrave;n bộ V&acirc;n V&otilde; Quận Quốc thế hệ tuổi trẻ, căn bản kh&ocirc;ng c&oacute; người c&oacute; thể nh&igrave;n theo b&oacute;ng lưng. N&iacute;nh San c&oacute; thể c&ugrave;ng Thất vương tử điện hạ kết th&agrave;nh vợ chồng, đối với ch&uacute;ng ta L&acirc;m gia tương lai c&oacute; lợi &iacute;ch cực kỳ lớn.&quot;<br />\r\n<br />\r\n&quot;Cửu vương tử mặc d&ugrave; c&ugrave;ng N&iacute;nh San l&agrave; biểu huynh muội, khi c&ograve;n b&eacute; cũng l&agrave; rất tốt bạn chơi, được cho thanh Mai Tr&uacute;c ngựa. Nhưng l&agrave;, Cửu vương tử d&ugrave; sao tư chất b&igrave;nh thường, 16 tuổi mới mở ra Thần V&otilde; Ấn K&yacute;, kiếp n&agrave;y cũng sẽ kh&ocirc;ng c&oacute; bao lớn th&agrave;nh tựu, c&oacute; thể tu luyện tới Ho&agrave;ng Cực Cảnh hậu kỳ hẳn l&agrave; cực hạn, c&ugrave;ng Thất vương tử căn bản kh&ocirc;ng c&aacute;ch n&agrave;o so s&aacute;nh được.&quot;<br />\r\n<br />\r\nL&acirc;m Phi n&oacute;i: &quot;N&iacute;nh San cũng nguyện &yacute; c&ugrave;ng Thất vương tử đ&iacute;nh h&ocirc;n? V&igrave; gia tộc lợi &iacute;ch kết hợp, thật sẽ hạnh ph&uacute;c sao?&quot;<br />\r\n<br />\r\nL&acirc;m Phụng Ti&ecirc;n nh&igrave;n chằm chằm L&acirc;m Phi, đạm mạc m&agrave; n&oacute;i: &quot;Ngươi sai, bản n&agrave;y liền l&agrave; N&iacute;nh San quyết định của m&igrave;nh!&quot;<br />\r\n<br />\r\n...<br />\r\n<br />\r\nL&acirc;m gia Diễn V&otilde; Trường, mười phần kho&aacute;ng đạt, chừng nửa c&aacute;i s&acirc;n b&oacute;ng lớn như vậy.<br />\r\n<br />\r\nTừng c&aacute;i mặc m&agrave;u xanh v&otilde; &aacute;o L&acirc;m gia con em trẻ tuổi, ngay tại tr&ecirc;n diễn v&otilde; trường tu luyện v&otilde; kỹ, c&oacute; tại tu luyện quyền ph&aacute;p, c&oacute; tại tu luyện kiếm ph&aacute;p, c&oacute; tại tu luyện đao ph&aacute;p.<br />\r\n<br />\r\nBọn hắn thuộc về L&acirc;m gia tinh anh, mỗi một c&aacute;i đều mở ra Thần V&otilde; Ấn K&yacute;, hết sức chuy&ecirc;n ch&uacute; tu luyện. Đồng thời cũng c&oacute; L&acirc;m gia trưởng bối tại diễn v&otilde; trường b&ecirc;n trong đối bọn hắn tiến h&agrave;nh chỉ điểm, một ph&aacute;i vui vẻ phồn vinh kh&iacute; tượng.<br />\r\n<br />\r\nTrương Nhược Trần nhẹ gật đầu, trong l&ograve;ng thầm nghĩ, &quot;L&acirc;m gia tại V&acirc;n V&otilde; Quận Quốc cũng coi như được l&agrave; đại tộc.&quot;<br />\r\n<br />\r\nBỗng nhi&ecirc;n, Trương Nhược Trần &aacute;nh mắt chăm ch&uacute; v&agrave;o m&ocirc;̣t cái vóc người mảnh khảnh thiếu nữ tr&ecirc;n th&acirc;n, kh&ocirc;ng khỏi c&oacute; ch&uacute;t kinh diễm.<br />\r\n<br />\r\nChỉ gặp thiếu nữ kia nh&igrave;n qua cũng chỉ c&oacute; 14~15 tuổi, d&aacute;ng người nhỏ nhắn xinh xắn linh lung, đại mi như l&aacute; liễu, đ&ocirc;i mắt s&aacute;ng tỏ giống như sao trời, da thịt tuyết trắng giống như Linh Ngọc, tốt một c&aacute;i mỹ nh&acirc;n bại hoại.<br />\r\n<br />\r\nTay n&agrave;ng cầm một thanh tản ra nh&agrave;n nhạt tinh quang bảo kiếm, tản m&aacute;t ra m&agrave;u xanh nhạt kiếm mang, v&ocirc; số kiếm kh&iacute; vờn quanh tại th&acirc;n thể của n&agrave;ng chung quanh, đi theo bước tiến của n&agrave;ng du tẩu, nhanh như cầu vồng, uyển như Du Long, kiếm ph&aacute;p đơn giản tinh diệu tới cực điểm.<br />\r\n<br />\r\n&quot;Ch&acirc;n kh&iacute; ngoại ph&oacute;ng, Kiếm T&ugrave;y T&acirc;m Tẩu. V&otilde; đạo tu vi của n&agrave;ng ch&iacute; &iacute;t cũng đạt tới Ho&agrave;ng Cực Cảnh trung cực vị, mạnh mẽ hơn B&aacute;t vương tử nhiều lắm.&quot; Trương Nhược Trần trong l&ograve;ng &aacute;m đạo.<br />\r\n<br />\r\n&quot;Ồ! Đ&acirc;y kh&ocirc;ng phải l&agrave; Cửu vương tử sao? Hắn thế m&agrave; c&ograve;n tới L&acirc;m phủ?&quot; Một c&aacute;i L&acirc;m gia con em trẻ tuổi nh&igrave;n thấy đứng tại diễn v&otilde; trường b&ecirc;n ngo&agrave;i Trương Nhược Trần, lộ ra mấy phần lạnh lẽo &yacute; cười.<br />\r\n<br />\r\n&quot;Khẳng định lại l&agrave; t&igrave;m đến N&iacute;nh San muội muội, đ&aacute;ng tiếc a, N&iacute;nh San muội muội b&acirc;y giờ căn bản đều chẳng muốn gặp hắn.&quot;<br />\r\n<br />\r\n&quot;Nghe n&oacute;i hắn cũng mở ra Thần V&otilde; Ấn K&yacute;.&quot;<br />\r\n<br />\r\n&quot;Ha ha! 16 tuổi mới mở ra Thần V&otilde; Ấn K&yacute;, có th&ecirc;̉ c&oacute; c&aacute;i g&igrave; l&agrave;m? Như hắn kh&ocirc;ng phải N&iacute;nh San muội muội biểu ca, n&oacute;i kh&ocirc;ng chừng ngay cả L&acirc;m gia đại m&ocirc;n đều v&agrave;o kh&ocirc;ng được.&quot;<br />\r\n<br />\r\n&quot;Nghe n&oacute;i N&iacute;nh San muội muội liền muốn c&ugrave;ng Thất vương tử đ&iacute;nh h&ocirc;n, thật sự l&agrave; trai t&agrave;i g&aacute;i sắc a!&quot;<br />\r\n<br />\r\n&quot;Hắc hắc! Nghe n&oacute;i vị kia Cửu vương tử vẫn lu&ocirc;n thầm mến N&iacute;nh San muội muội, c&aacute;c ngươi đo&aacute;n, nếu l&agrave; hắn nghe n&oacute;i N&iacute;nh San c&ugrave;ng Thất vương tử điện hạ liền muốn đ&iacute;nh h&ocirc;n, sẽ l&agrave; biểu t&igrave;nh g&igrave;?&quot;<br />\r\n<br />\r\nL&acirc;m gia những kia tuổi trẻ v&otilde; giả to&agrave;n bộ đều dừng lại tu luyện, nh&igrave;n chằm chằm đứng tại diễn v&otilde; trường b&ecirc;n ngo&agrave;i Trương Nhược Trần, chỉ trỏ, nghị luận ầm ĩ, khi th&igrave; ph&aacute;t ra tr&ecirc;u tức tiếng cười.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n<br />\r\nL&acirc;m N&iacute;nh San cũng đ&igrave;nh chỉ luyện kiếm, hướng về đứng tại diễn v&otilde; trường b&ecirc;n ngo&agrave;i Trương Nhược Trần nh&igrave;n tho&aacute;ng qua, mảnh khảnh c&aacute;nh tay ngọc nhẹ nh&agrave;ng vung l&ecirc;n, trong tay tinh huy bảo kiếm liền tinh chuẩn cắm v&agrave;o năm m&eacute;t b&ecirc;n ngo&agrave;i vỏ kiếm.<br />\r\n<br />\r\nL&acirc;m N&iacute;nh San đi đến Trương Nhược Trần trước mặt, nh&igrave;n một ch&uacute;t Trương Nhược Trần đơn bạc th&acirc;n thể, n&oacute;i: &quot;Biểu ca, đ&atilde; l&acirc;u kh&ocirc;ng gặp, nghe n&oacute;i ngươi cũng mở ra Thần V&otilde; Ấn K&yacute;?&quot;<br />\r\n<br />\r\nTại l&uacute;c c&ograve;n rất nhỏ, L&acirc;m N&iacute;nh San c&ugrave;ng Trương Nhược Trần vẫn rất tốt bạn chơi, coi l&agrave; thanh Mai Tr&uacute;c ngựa, nhưng l&agrave; về sau L&acirc;m N&iacute;nh San mở ra Thần V&otilde; Ấn K&yacute;, phần lớn thời gian liền tốn hao về mặt tu luyện mặt, c&ugrave;ng Trương Nhược Trần c&agrave;ng ng&agrave;y c&agrave;ng xa l&aacute;nh.<br />\r\n<br />\r\nBa năm trước đ&acirc;y sự t&igrave;nh sau khi ph&aacute;t sinh, n&agrave;ng liền kh&ocirc;ng c&ograve;n c&oacute; đi qua V&acirc;n V&otilde; ho&agrave;ng cung. Trương Nhược Trần mặc d&ugrave; l&acirc;u d&agrave;i nhiều bệnh, nhưng như cũ thường xuy&ecirc;n đi L&acirc;m gia t&igrave;m n&agrave;ng, cho d&ugrave; l&agrave; c&oacute; thể gặp n&agrave;ng một mặt, cũng sẽ cảm thấy tương đương vui vẻ.<br />\r\n<br />\r\nNhưng l&agrave;, c&oacute; thể nh&igrave;n thấy n&agrave;ng số lần lại c&agrave;ng ng&agrave;y c&agrave;ng &iacute;t, gần nhất nửa năm c&agrave;ng l&agrave; chưa bao giờ từng thấy n&agrave;ng. N&agrave;ng đều l&agrave; điều động một vị thị nữ đi ra, đem Trương Nhược Trần đuổi rời đi.<br />\r\n<br />\r\n&quot;Nguy&ecirc;n lai n&agrave;ng l&agrave; biểu muội của ta.&quot;<br />\r\n<br />\r\nHiện tại Trương Nhược Trần c&oacute; thể n&oacute;i l&agrave; lần thứ nhất nh&igrave;n thấy L&acirc;m N&iacute;nh San, đối n&agrave;ng cũng kh&ocirc;ng c&oacute; cảm gi&aacute;c g&igrave;, cho n&ecirc;n lộ ra rất b&igrave;nh tĩnh, khi&ecirc;m tốn một c&acirc;u: &quot;Đ&iacute;ch thật l&agrave; mở ra Thần V&otilde; Ấn K&yacute;, bất qu&aacute; Vương hậu nương nương n&oacute;i chỉ l&agrave; kh&ocirc;ng c&oacute; phẩm cấp Thần V&otilde; Ấn K&yacute;, tự nhi&ecirc;n l&agrave; kh&ocirc;ng thể c&ugrave;ng biểu muội Thần V&otilde; Ấn K&yacute; đ&aacute;nh đồng.&quot;<br />\r\n<br />\r\nL&acirc;m N&iacute;nh San nhẹ gật đầu, ngẩng l&ecirc;n tuyết trắng c&aacute;i cằm, cao ngạo đ&ecirc;́n tựa như một c&aacute;i trắng Thi&ecirc;n Nga, n&oacute;i: &quot;Ngươi d&ugrave; sao đ&atilde; 16 tuổi, c&oacute; thể mở ra Thần V&otilde; Ấn K&yacute;, cũng coi l&agrave; thượng thi&ecirc;n đối ngươi ban &acirc;n. Sau n&agrave;y nhất định phải cố gắng tu luyện, mặc d&ugrave; kh&ocirc;ng thể trở th&agrave;nh V&otilde; Đạo cường giả, nhưng l&agrave; ch&iacute; &iacute;t có th&ecirc;̉ lấy cường th&acirc;n kiện thể, kh&ocirc;ng cần l&acirc;u d&agrave;i nằm tại tr&ecirc;n giường bệnh, đối với ngươi m&agrave; n&oacute;i... Ch&iacute; &iacute;t có th&ecirc;̉ lấy l&agrave;m một người b&igrave;nh thường.&quot;<br />\r\n<br />\r\nTrương Nhược Trần c&oacute; ch&uacute;t cau lại l&ocirc;ng m&agrave;y, nhẹ gật đầu, n&oacute;i: &quot;Ta sau n&agrave;y nhất định sẽ cố gắng tu luyện, tranh thủ đuổi kịp biểu muội tu vi của ngươi.&quot;<br />\r\n<br />\r\nL&acirc;m N&iacute;nh San tự nhi&ecirc;n biết Trương Nhược Trần th&iacute;ch n&agrave;ng, nghe được Trương Nhược Trần, liền cho rằng Trương Nhược Trần vẫn như cũ chưa từ bỏ &yacute; định, c&ograve;n muốn tiếp tục truy cầu n&agrave;ng.<br />\r\n<br />\r\n&quot;Ấy! Biểu ca, tu vi của ta đ&atilde; đạt tới Ho&agrave;ng Cực Cảnh b&ecirc;n trong cực cảnh, c&aacute;ch lớn cực cảnh cũng chỉ c&oacute; c&aacute;ch xa một bước. Lấy tư chất của ngươi, đo&aacute;n chừng cả đời cũng tu luyện kh&ocirc;ng đến b&ecirc;n trong cực cảnh, đối ngươi b&acirc;y giờ tới n&oacute;i phải l&agrave;m nhất liền l&agrave; cước đạp thực địa tu luyện, đừng m&ugrave; qu&aacute;ng truy cầu một ch&uacute;t kh&ocirc;ng n&ecirc;n theo đuổi đồ vật, l&agrave;m người kh&ocirc;ng thể mơ tưởng xa vời, kh&ocirc;ng phải sẽ phản thụ n&oacute; hại.&quot; L&acirc;m N&iacute;nh San n&oacute;i một c&acirc;u hai nghĩa.<br />\r\n<br />\r\nTrương Nhược Trần m&agrave;y nh&iacute;u lại đ&ecirc;́n c&agrave;ng s&acirc;u.<br />\r\n<br />\r\nL&acirc;m N&iacute;nh San c&oacute; ch&uacute;t đồng t&igrave;nh nh&igrave;n Trương Nhược Trần một ch&uacute;t, n&oacute;i: &quot;Biểu ca, c&ograve;n c&oacute; một chuyện ta phải n&oacute;i cho ngươi, hi vọng ngươi kh&ocirc;ng n&ecirc;n qu&aacute; thương t&acirc;m. Ba th&aacute;ng về sau , chờ đến Quận Vương xuất quan, ta c&ugrave;ng Thất vương tử điện hạ hẳn l&agrave; liền muốn đ&iacute;nh h&ocirc;n.&quot;<br />\r\n<br />\r\n&quot;C&oacute; tr&ograve; hay để nh&igrave;n! Hắc hắc!&quot;<br />\r\n<br />\r\nL&acirc;m gia những kia tuổi trẻ v&otilde; giả, to&agrave;n bộ đều trong l&ograve;ng mừng rỡ, nhao nhao đem &aacute;nh mắt nh&igrave;n chăm ch&uacute; về ph&iacute;a Trương Nhược Trần, muốn xem Trương Nhược Trần sẽ l&agrave; dạng g&igrave; phản ứng?</p>', '2021-11-27 21:56:46', 34, 1),
(202, 53, 'Xuất ra đánh được chứ?', '1', '<p>Ch&iacute;nh l&agrave; th&aacute;ng ch&iacute;n thời tiết, mưa thu li&ecirc;n mi&ecirc;n, rửa sạch giữa h&egrave; n&oacute;ng rực.<br />\r\n<br />\r\n&quot;Ai, gió th&ocirc;̉i giảm ph&acirc;n nửa, trời mưa to&agrave;n bộ xong &quot;<br />\r\n<br />\r\nPh&iacute;a Nam th&agrave;nh Kim Lăng, T&uacute;y Ti&ecirc;n L&acirc;u trước cửa, Vương D&atilde; dựa khung cửa, nh&igrave;n v&agrave;o c&ocirc; li&ecirc;u đầu đường, bất đắc dĩ n&oacute;i: &quot;H&ocirc;m nay mua b&aacute;n lại ế ẩm rồi!&quot;<br />\r\n<br />\r\nHắn bộ d&aacute;ng thanh t&uacute;, lấy 1 bộ thanh sam, thẳng tắp tr&ecirc;n th&acirc;n thể cơ bắp sung m&atilde;n, ki&ecirc;n cố hữu lực.<br />\r\n<br />\r\nNh&igrave;n một c&aacute;i, giống như l&agrave; c&aacute;i kh&iacute; vũ bất ph&agrave;m văn nh&acirc;n thư sinh.<br />\r\n<br />\r\nQuay đầu nh&igrave;n một ch&uacute;t gh&eacute; v&agrave;o 1 b&ecirc;n ngủ g&agrave; ngủ gật tiểu nhị, hắn nhếch m&eacute;p một c&aacute;i, lộ ra 1 tia bất đắc dĩ: &quot;A C&aacute;t c&aacute;i n&agrave;y loại ngốc, ngoại trừ ngủ ch&iacute;nh l&agrave; ăn, nếu kh&ocirc;ng phải c&aacute;i n&agrave;y t&ocirc;n tử tay ch&acirc;n lưu lo&aacute;t, lão tử kh&ocirc;ng phải x&agrave;o hắn bất khả!&quot;<br />\r\n<br />\r\n&quot;Ai, l&agrave;m ăn kh&ocirc;ng kh&aacute; l&agrave;m a!&quot;<br />\r\n<br />\r\n&quot;Bất qu&aacute; sinh &yacute; lại kh&oacute;, c&oacute; thể tốt hơn đao quang kiếm ảnh, lục đục với nhau . . .&quot;<br />\r\n<br />\r\nN&oacute;i ra, Vương D&atilde; nh&igrave;n một ch&uacute;t gan b&agrave;n tay m&igrave;nh chỗ thật dầy vết chai, nhẹ nh&agrave;ng lắc đầu.<br />\r\n<br />\r\nVương D&atilde; cũng kh&ocirc;ng phải người của thế giới n&agrave;y.<br />\r\n<br />\r\nSớm tại 10 năm trước, Vương D&atilde; thuận dịp xuy&ecirc;n vi&ecirc;̣t đến nơi n&agrave;y c&aacute;i v&otilde; hiệp thế giới.<br />\r\n<br />\r\nC&ugrave;ng trong tiểu thuyết kiều đoạn đồng dạng, Vương D&atilde; đ&ecirc;́n kỳ ngộ, luyện thần c&ocirc;ng, chỉ d&ugrave;ng 10 năm t&igrave;nh cảnh thuận dịp v&ocirc; địch thi&ecirc;n hạ, trở th&agrave;nh hung danh hiển h&aacute;ch, uy chấn thi&ecirc;n hạ Ma Gi&aacute;o gi&aacute;o chủ.<br />\r\n<br />\r\nĐại kh&aacute;i l&agrave; bởi v&igrave; v&ocirc; địch m&agrave; qu&aacute; mức tịch mịch, cũng c&oacute; lẽ l&agrave; bởi v&igrave; ch&aacute;n gh&eacute;t hục hặc với nhau giang hồ ph&acirc;n tranh.<br />\r\n<br />\r\nVương D&atilde; tại 1 lần ch&iacute;nh đạo v&acirc;y qu&eacute;t b&ecirc;n trong giả &yacute; chết đi, từ nay về sau v&otilde; l&acirc;m 1 mảnh h&agrave;i h&ograve;a.<br />\r\n<br />\r\nGiang hồ kh&ocirc;ng c&oacute; hung danh hiển h&aacute;ch Ma Gi&aacute;o gi&aacute;o chủ, Kim Lăng th&agrave;nh nhưng nhiều hơn một người tham tiền h&aacute;o sắc tửu l&acirc;u l&atilde;o bản.<br />\r\n<br />\r\n&quot;Ai, lại mở 2 canh giờ, nếu như c&ograve;n kh&ocirc;ng c&oacute; kh&aacute;ch nh&acirc;n, liền trực tiếp đ&oacute;ng cửa!&quot;<br />\r\n<br />\r\nHồi tưởng lại tất cả những thứ n&agrave;y, Vương D&atilde; tự nhủ: &quot;Di Hồng viện gần nhất vừa l&ecirc;n 2 c&aacute;i Mao muội, t&oacute;c v&agrave;ng mắt xanh rất l&agrave; khả quan, vừa vặn thừa dịp kh&ocirc;ng c&oacute; người đi kho&aacute;i hoạt một phen, h&aacute; kh&ocirc;ng phải tốt thay . . .&quot;<br />\r\n<br />\r\nTrong ng&ocirc;n ngữ, Vương D&atilde; tr&ecirc;n mặt lộ ra 1 tia h&egrave;n mọn, phảng phất 2 c&aacute;i kia vừa l&ecirc;n Mao muội đang ở trước mắt.<br />\r\n<br />\r\n&quot;Chưởng quỹ, xin hỏi cũng c&oacute; thể c&oacute; ph&ograve;ng trống? !&quot;<br />\r\n<br />\r\nNgay tại Vương D&atilde; t&acirc;m tr&iacute; hướng về, &yacute; nghĩ kỳ qu&aacute;i thời điểm, 1 c&aacute;i thanh &acirc;m xa lạ truyền đến.<br />\r\n<br />\r\nT&igrave;m theo tiếng nh&igrave;n lại, lại ph&aacute;t hiện mấy c&aacute;i th&acirc;n ảnh đang đứng tại kh&aacute;ch sạn trước cửa.<br />\r\n<br />\r\nMấy c&aacute;i n&agrave;y th&acirc;n ảnh đầu đội mũ rộng v&agrave;nh thấy kh&ocirc;ng r&otilde; dung mạo, &aacute;o tơi ph&iacute;a dưới trang phục kết th&uacute;c, trong tay c&ograve;n nắm lấy trường thương đao kiếm, xem x&eacute;t ch&iacute;nh l&agrave; h&agrave;nh tẩu giang hồ hiệp kh&aacute;ch.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nKim Lăng Th&agrave;nh Lục Th&ocirc;ng Ngũ Lĩnh, thế &aacute;ch Tam Giang, l&agrave; trong v&ograve;ng ng&agrave;n dặm b&ecirc;n trong th&agrave;nh phố lớn nhất, trong đ&oacute; du thương kh&aacute;ch lữ v&ocirc; số kể, t&oacute;c v&agrave;ng mắt xanh phi&ecirc;n bang Dị Nh&acirc;n cũng đi lại trong l&uacute;c đ&oacute;.<br />\r\n<br />\r\nĐối với c&aacute;i n&agrave;y gi&uacute;p giang hồ du hiệp, Vương D&atilde; c&aacute;i n&agrave;y mở kh&aacute;ch sạn sớm đ&atilde; kh&ocirc;ng thấy kinh ngạc.<br />\r\n<br />\r\n&quot;C&oacute; c&oacute; c&oacute;!&quot;<br />\r\n<br />\r\nNghe vậy, Vương D&atilde; tr&ecirc;n mặt nụ cười bỉ ổi trong nh&aacute;y mắt biến th&agrave;nh nịnh nọt, hắn hướng về ph&iacute;a ngo&agrave;i cửa mấy người mở miệng n&oacute;i: &quot;Đương nhi&ecirc;n c&oacute; rảnh gian ph&ograve;ng, mấy vị, b&ecirc;n ngo&agrave;i mưa lớn, ch&uacute;ng ta v&agrave;o n&oacute;i chuyện!&quot;<br />\r\n<br />\r\nN&oacute;i ra, Vương D&atilde; khom người đem mấy người kia ngh&ecirc;nh v&agrave;o kh&aacute;ch sạn.<br />\r\n<br />\r\nNếu như đặt ở trước đ&oacute;, Vương D&atilde; c&ograve;n l&agrave; Ma Gi&aacute;o gi&aacute;o chủ thời đi&ecirc;̉m, đương nhi&ecirc;n kh&ocirc;ng cần như thế.<br />\r\n<br />\r\nVậy b&acirc;y giờ nha, v&igrave; sinh &yacute;, kh&ocirc;ng kh&oacute; coi.<br />\r\n<br />\r\n&quot;Cho ba người ch&uacute;ng ta ba gian ph&ograve;ng tr&ecirc;n &quot;<br />\r\n<br />\r\nTiến v&agrave;o b&ecirc;n trong kh&aacute;ch sạn, một c&aacute;i nam nh&acirc;n đem một n&eacute;n bạc nhỏ đưa cho Vương D&atilde;.<br />\r\n<br />\r\n&quot;Được rồi!&quot;<br />\r\n<br />\r\nGặp được bạc, Vương D&atilde; trong l&ograve;ng vui vẻ, mở miệng n&oacute;i: &quot;Mấy vị kh&aacute;ch quan, ch&uacute;ng ta nơi n&agrave;y c&ograve;n c&oacute; thượng hạng tửu thủy, c&aacute;i n&agrave;y mưa thu li&ecirc;n mi&ecirc;n, sao kh&ocirc;ng đi l&ecirc;n một b&igrave;nh khu khu c&aacute;i n&agrave;y ẩm ướt H&agrave;n chi kh&iacute;?&quot;<br />\r\n<br />\r\n&quot;Cũng tốt, ngươi cho ch&uacute;ng ta đến một v&ograve; rượu hoa đi&ecirc;u, lại x&agrave;o 2 c&aacute;i thức nhắm, b&ecirc;n tr&ecirc;n hai c&acirc;n thịt d&ecirc;!&quot;<br />\r\n<br />\r\nNghe được Vương D&atilde; ng&ocirc;n ngữ, trong đ&oacute; một t&ecirc;n nam tử mở miệng n&oacute;i ra.<br />\r\n<br />\r\n&quot;Được rồi, ta đ&acirc;y đi chuẩn bị ngay &quot;<br />\r\n<br />\r\n~~~ l&uacute;c n&agrave;y Vương D&atilde; l&ecirc;n tiếng, đồng thời một cước đ&aacute; tỉnh c&ograve;n ở b&ecirc;n cạnh ngủ gật A C&aacute;t: &quot;Ngủ ngủ ngủ, liền biết ngủ, mang theo c&aacute;i n&agrave;y mấy t&ecirc;n kh&aacute;ch quan l&ecirc;n tr&ecirc;n lầu ph&ograve;ng tr&ecirc;n nghỉ ngơi!&quot;<br />\r\n<br />\r\n&quot;Tay ngươi ch&acirc;n lưu lo&aacute;t ch&uacute;t, nếu như chậm trễ mấy vị kh&aacute;ch quan, ta chụp tiểu tử ngươi nửa th&aacute;ng tiền bạc!&quot;<br />\r\n<br />\r\n&quot;A!&quot;<br />\r\n<br />\r\nC&aacute;i n&agrave;y A C&aacute;t bị Vương D&atilde; một cước đ&aacute; tỉnh, đầu &oacute;c cũng thanh tỉnh lại, ngốc đầu ngốc n&atilde;o l&ecirc;n tiếng về sau,<br />\r\n<br />\r\nHướng về ph&iacute;a mấy nam nh&acirc;n mở miệng n&oacute;i: &quot;Mấy vị kh&aacute;ch quan xin mời đi theo ta!&quot;<br />\r\n<br />\r\nN&oacute;i ra, liền mang theo mấy nam nh&acirc;n hướng về tr&ecirc;n lầu ph&ograve;ng trọ đi đến.<br />\r\n<br />\r\n&quot;Tiểu tử th&uacute;i!&quot;<br />\r\n<br />\r\nNh&igrave;n v&agrave;o A C&aacute;t bộ d&aacute;ng, Vương D&atilde; thấp giọng n&oacute;i ra.<br />\r\n<br />\r\nĐồng thời hắn lấy ra mới vừa rồi nam nh&acirc;n đưa tới bạc, lấy tay một ước lượng, mừng thầm n&oacute;i: &quot;Chừng hai mươi lượng tiền bạc, 1 đợt n&agrave;y mua b&aacute;n kiếm bộn rồi!&quot;<br />\r\n<br />\r\nN&oacute;i ra, Vương D&atilde; liền đối với ph&ograve;ng bếp h&ocirc;: &quot;3 m&oacute;n x&agrave;o, hai c&acirc;n thịt d&ecirc;!&quot;<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nSau một hồi l&acirc;u, mới vừa rồi c&aacute;i kia mấy nam nh&acirc;n từ tr&ecirc;n lầu đi xuống.<br />\r\n<br />\r\nBọn họ nguy&ecirc;n một đ&aacute;m tr&uacute;t bỏ tr&ecirc;n người mũ rộng v&agrave;nh &aacute;o tơi, lộ ra ch&acirc;n dung.<br />\r\n<br />\r\nNhững người n&agrave;y nguy&ecirc;n một đ&aacute;m khu&ocirc;n mặt ki&ecirc;n nghị, đao kiếm kh&ocirc;ng rời tay, trong mắt tr&agrave;n đầy đề ph&ograve;ng, t&igrave;m một chỗ sau khi ngồi xuống liền bắt đầu miệng lớn ăn uống, trong l&uacute;c đ&oacute; kh&ocirc;ng n&oacute;i một lời.<br />\r\n<br />\r\nC&aacute;i n&agrave;y khiến nguy&ecirc;n bản l&agrave; an tĩnh kh&aacute;ch sạn, c&agrave;ng ng&agrave;y c&agrave;ng quỷ dị.<br />\r\n<br />\r\nHiển nhi&ecirc;n, mấy người n&agrave;y nam nh&acirc;n c&oacute; việc trong người.<br />\r\n<br />\r\nThấy một m&agrave;n như vậy, Vương D&atilde; lắc đầu.<br />\r\n<br />\r\nMấy c&aacute;i n&agrave;y Sồ, liền cơ bản nhất ngụy trang cũng kh&ocirc;ng biết . . .<br />\r\n<br />\r\n&quot;Chưởng quỹ . . .&quot;<br />\r\n<br />\r\nNgay tại Vương D&atilde; &acirc;m thầm cảm th&aacute;n thế hệ n&agrave;y v&otilde; l&acirc;m nh&acirc;n sĩ &yacute; thức n&aacute;t nhừ thời điểm, A C&aacute;t đi tới b&ecirc;n cạnh hắn, lặng lẽ n&oacute;i ra: &quot;Mấy người n&agrave;y kh&ocirc;ng b&igrave;nh thường a!&quot;<br />\r\n<br />\r\n&quot;Thế n&agrave;o?&quot;<br />\r\n<br />\r\nNghe vậy, Vương D&atilde; m&iacute; mắt vẩy một c&aacute;i, uể oải hỏi.<br />\r\n<br />\r\n&quot;Mới vừa rồi ta dẫn bọn hắn đến ph&ograve;ng kh&aacute;ch thời điểm, ph&aacute;t hiện bọn họ l&ograve;ng ph&ograve;ng bị mạnh cực kỳ, m&agrave; c&ograve;n trong ngực cổ cổ nang nang cất kh&ocirc;ng biết thứ g&igrave;, m&agrave; c&ograve;n tr&ecirc;n người c&ograve;n c&oacute; m&ugrave;i m&aacute;u tươi, những người n&agrave;y khẳng định . . .&quot;<br />\r\n<br />\r\n~~~ l&uacute;c n&agrave;y A C&aacute;t thần b&iacute; hề hề n&oacute;i ra.<br />\r\n<br />\r\n&quot;Dừng lại!&quot;<br />\r\n<br />\r\nKh&ocirc;ng đợi A C&aacute;t n&oacute;i hết lời, Vương D&atilde; thuận dịp lối ra cắt ngang: &quot;Ta dạy ngươi quy củ ngươi đều qu&ecirc;n đ&uacute;ng kh&ocirc;ng? Th&agrave;nh th&agrave;nh thật thật l&agrave;m việc, kh&aacute;ch nh&acirc;n sự t&igrave;nh ta kh&ocirc;ng quan t&acirc;m, cũng đừng đến hỏi, đắc tội kh&aacute;ch quan, nh&igrave;n ta kh&ocirc;ng đập chết t&ecirc;n tiểu tử thối nh&agrave; ngươi!&quot;<br />\r\n<br />\r\n&quot;Thế nhưng l&agrave; . . .&quot;<br />\r\n<br />\r\nNghe được Vương D&atilde; ng&ocirc;n ngữ, A C&aacute;t c&ograve;n muốn n&oacute;i g&igrave;.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nNhưng m&agrave;, Vương D&atilde; nhưng lại kh&ocirc;ng cho hắn cơ hội.<br />\r\n<br />\r\n&quot;Thế nhưng l&agrave; c&aacute;i rắm!&quot;<br />\r\n<br />\r\n~~~ l&uacute;c n&agrave;y Vương D&atilde; hướng về ph&iacute;a A C&aacute;t đầu ch&iacute;nh l&agrave; 1 c&aacute;i bạo lật: &quot;Tiểu tử ngươi thế n&agrave;o rảnh rỗi như vậy đ&acirc;y? Cửa ra v&agrave;o qua chiếc xe chở ph&acirc;n ngươi c&oacute; phải hay kh&ocirc;ng đều muốn cầm mu&ocirc;i nếm thử mặn nhạt a?&quot;<br />\r\n<br />\r\n&quot;Ta cho ngươi biết a . . .&quot;<br />\r\n<br />\r\n~~~ l&uacute;c n&agrave;y, Vương D&atilde; liền chuẩn bị bắt đầu thao thao bất tuyệt gi&aacute;o dục A C&aacute;t.<br />\r\n<br />\r\n&quot;Lạc Trưởng Thi&ecirc;n, ngươi thực nghĩ đến đ&aacute;m c&aacute;c ngươi an to&agrave;n? !&quot;<br />\r\n<br />\r\nNhưng v&agrave;o l&uacute;c n&agrave;y, hừ lạnh một tiếng truyền đến.<br />\r\n<br />\r\nT&igrave;m theo tiếng nh&igrave;n lại, đ&atilde; thấy một c&aacute;i đầu đội n&oacute;n l&aacute; nam nh&acirc;n đi v&agrave;o trong tiệm, tr&ocirc;ng chừng tiệm b&ecirc;n trong ăn uống mấy nam nh&acirc;n cười gằn n&oacute;i: &quot;Thức thời giao ra đồ vật, nếu kh&ocirc;ng m&agrave; n&oacute;i, đừng tr&aacute;ch hạ thủ v&ocirc; t&igrave;nh!&quot;<br />\r\n<br />\r\nNam nh&acirc;n n&agrave;y mũ rộng v&agrave;nh ph&iacute;a dưới &aacute;o đen che mặt, tr&ecirc;n người &aacute;o tơi thủy kh&iacute; ngưng kết, cũng kh&ocirc;ng che giấu được tản ra s&aacute;t kh&iacute;!<br />\r\n<br />\r\n&quot;Hừ, thực sự l&agrave; hu&ecirc;nh hoang kho&aacute;c l&aacute;c, kh&ocirc;ng dứt!&quot;<br />\r\n<br />\r\nNghe vậy, m&ocirc;̣t cái vóc người h&aacute;n tử cường tr&aacute;ng trường kiếm ra khỏi vỏ, cười lạnh n&oacute;i: &quot;Trừ phi ta chết, nếu kh&ocirc;ng nhất định kh&ocirc;ng khả năng!&quot;<br />\r\n<br />\r\n&quot;Tốt!&quot;<br />\r\n<br />\r\nNghe vậy, nam tử mặc &aacute;o đen n&agrave;y lạnh r&ecirc;n một tiếng, mở miệng n&oacute;i: &quot;Đ&atilde; như vậy, vậy ta liền th&agrave;nh to&agrave;n ngươi!&quot;<br />\r\n<br />\r\n&quot;Đều cũng ra đi!&quot;<br />\r\n<br />\r\nSoạt!<br />\r\n<br />\r\nTheo nam nh&acirc;n ra lệnh một tiếng, 8 c&aacute;i nam nh&acirc;n &aacute;o đen trong nh&aacute;y mắt ph&aacute; cửa sổ m&agrave; v&agrave;o, trong khoảnh khắc liền đem Lạc Trưởng Thi&ecirc;n một nh&oacute;m người v&acirc;y v&agrave;o giữa.<br />\r\n<br />\r\nLớn như vậy b&ecirc;n trong kh&aacute;ch sạn, trong nh&aacute;y mắt giương cung bạt kiếm!<br />\r\n<br />\r\n&quot;Chư vị kh&aacute;ch quan!&quot;<br />\r\n<br />\r\nỞ nơi n&agrave;y giương cung bạt kiếm thời khắc mấu chốt, 1 c&aacute;i thanh &acirc;m kh&ocirc;ng h&agrave;i h&ograve;a từ một b&ecirc;n truyền đến: &quot;Tiểu điếm vốn lợi nhỏ hơi, chư vị muốn đ&aacute;nh m&agrave; n&oacute;i, phiền phức xuất ra đ&aacute;nh được chứ?&quot;</p>', '2021-11-27 22:01:19', 25, 1),
(203, 53, 'Thâm tàng bất lậu?', '2', '<p>Lời vừa n&oacute;i ra, hai phe nh&acirc;n m&atilde; &aacute;nh mắt trong nh&aacute;y mắt rơi v&agrave;o Vương D&atilde; tr&ecirc;n người.<br />\r\n<br />\r\nHắn &aacute;nh mắt s&aacute;ng quắc, tr&agrave;n đầy &yacute; s&aacute;t phạt.<br />\r\n<br />\r\nCảm thụ được t&uacute;i n&agrave;y chứa s&aacute;t &yacute; &aacute;nh mắt, Vương D&atilde; cười mỉa hai tiếng, mở miệng n&oacute;i: &quot;Đương nhi&ecirc;n, ta đ&acirc;y ch&iacute;nh l&agrave; một kh&ocirc;ng th&agrave;nh thục tiểu kiến nghị, c&aacute;c ngươi tiếp tục, tiếp tục . . .&quot;<br />\r\n<br />\r\n&quot;Cẩu vật, lại d&aacute;m ti&ecirc;u khiển nh&agrave; ngươi gia gia, nạp mạng đi!&quot;<br />\r\n<br />\r\nNam nh&acirc;n &aacute;o đen nhướng m&agrave;y, mở miệng mắng.<br />\r\n<br />\r\nĐồng thời đ&atilde; thấy trường kiếm trong tay của hắn một dẫn, mang theo 1 cỗ t&uacute;c s&aacute;t chi &yacute;, bay thẳng Vương D&atilde; cổ họng m&agrave; đến.<br />\r\n<br />\r\n1 kiếm n&agrave;y vừa nhanh vừa vội, bao h&agrave;m s&aacute;t &yacute;, kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o lưu thủ!<br />\r\n<br />\r\nNh&igrave;n thấy 1 kiếm n&agrave;y, Vương D&atilde; khẽ chau m&agrave;y.<br />\r\n<br />\r\nC&aacute;i n&agrave;y ở ngoại nh&acirc;n xem ra nhanh như thiểm điện 1 kiếm, hắn thấy lại l&agrave; v&ocirc; c&ugrave;ng chậm r&atilde;i.<br />\r\n<br />\r\nTrong l&uacute;c nhất thời trong đầu của hắn hiện l&ecirc;n mấy c&aacute;i suy nghĩ.<br />\r\n<br />\r\nĐộng thủ?<br />\r\n<br />\r\nTh&acirc;n phận của m&igrave;nh x&aacute;c định vững chắc bại lộ!<br />\r\n<br />\r\nNếu kh&ocirc;ng, giết hết?<br />\r\n<br />\r\nLấy th&acirc;n thủ của m&igrave;nh, ti&ecirc;u diệt những người n&agrave;y so nghiền chết con kiến c&ograve;n muốn đơn giản.<br />\r\n<br />\r\nThế nhưng l&agrave;, kể từ đ&oacute; A C&aacute;t l&agrave;m sao b&acirc;y giờ?<br />\r\n<br />\r\nChẳng lẽ liền hắn cũng c&ugrave;ng l&uacute;c l&agrave;m sạch?<br />\r\n<br />\r\nTrong l&uacute;c nhất thời, li&ecirc;n tiếp vấn đề x&ocirc;ng l&ecirc;n Vương D&atilde; trong đầu.<br />\r\n<br />\r\n&quot;Đừng tổn thương người kh&aacute;c t&iacute;nh mệnh!&quot;<br />\r\n<br />\r\nNgay tại Vương D&atilde; xoắn xu&yacute;t c&oacute; muốn hay kh&ocirc;ng ti&ecirc;u diệt những người n&agrave;y thời điểm, gầm l&ecirc;n giận dữ truyền đến.<br />\r\n<br />\r\n~~~ l&uacute;c n&agrave;y, đ&atilde; thấy Lạc Trưởng Thi&ecirc;n gầm th&eacute;t 1 tiếng, trường kiếm trong tay đột nhi&ecirc;n vẩy một c&aacute;i!<br />\r\n<br />\r\nBang!<br />\r\n<br />\r\nLại nghe một tiếng vang gi&ograve;n, Lạc Trưởng Thi&ecirc;n trực tiếp đẩy ra rồi nam tử n&agrave;y đ&acirc;m về ph&iacute;a Vương D&atilde; 1 kiếm.<br />\r\n<br />\r\n&quot;Thực sự l&agrave; nghĩa bạc v&acirc;n thi&ecirc;n a!&quot;<br />\r\n<br />\r\nBị ngăn 1 kiếm n&agrave;y về sau, lại nghe hắc y nam tử cười lạnh n&oacute;i: &quot;Bản th&acirc;n đều kh&oacute; bảo to&agrave;n, c&ograve;n băn khoăn cứu một c&aacute;i kh&ocirc;ng li&ecirc;n hệ nhau chưởng quỹ!&quot;<br />\r\n<br />\r\n&quot;Khoan thai thi&ecirc;n địa, ch&iacute;nh kh&iacute; trường tồn!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n~~~ l&uacute;c n&agrave;y Lạc Trưởng Thi&ecirc;n đem trường kiếm ở trước ngực qu&eacute;t ngang, nghi&ecirc;m mặt n&oacute;i: &quot;Đ&acirc;y l&agrave; Thi&ecirc;n Hạ Hội đặt ch&acirc;n căn bản, c&aacute;c ngươi so&aacute;n quyền tiểu nh&acirc;n h&aacute; c&oacute; thể minh bạch! ?&quot;<br />\r\n<br />\r\nThi&ecirc;n Hạ Hội?<br />\r\n<br />\r\nNghe được Lạc Trưởng Thi&ecirc;n ng&ocirc;n ngữ, Vương D&atilde; trong đầu hơi động một ch&uacute;t.<br />\r\n<br />\r\nHắn mặc d&ugrave; sớm đ&atilde; ẩn cư, kh&ocirc;ng c&ograve;n qua hỏi tới chuyện của giang hồ, nhưng l&agrave; thi&ecirc;n hạ n&agrave;y biết danh h&agrave;o hắn vẫn l&agrave; nghe qua.<br />\r\n<br />\r\nThi&ecirc;n hạ n&agrave;y sẽ l&agrave; mấy năm gần đ&acirc;y quật khởi thế lực, m&ocirc;n hạ cao thủ nhiều như m&acirc;y, đệ tử đ&ocirc;ng đảo, danh xưng giang hồ đệ nhất đại ph&aacute;i.<br />\r\n<br />\r\n&quot;Cẩu th&iacute; ch&iacute;nh kh&iacute;, kh&ocirc;ng c&oacute; quy tắc!&quot;<br />\r\n<br />\r\nNh&igrave;n v&agrave;o Lạc Trưởng Thi&ecirc;n, nam tử mặc &aacute;o đen n&agrave;y cười lạnh một tiếng, trầm giọng n&oacute;i: &quot;Miễn l&agrave; ngươi giao ra Qu&acirc;n Thi&ecirc;n Lệnh, h&ocirc;m nay ch&uacute;ng ta liền bỏ qua cho bọn ngươi, l&agrave;m sao?&quot;<br />\r\n<br />\r\n&quot;&Uacute;i ch&agrave;!&quot;<br />\r\n<br />\r\nNghe được hắc y nam tử ng&ocirc;n ngữ, Lạc Trưởng Thi&ecirc;n gắt một c&aacute;i, lạnh l&ugrave;ng n&oacute;i ra: &quot;Lục K&igrave;nh Xuy&ecirc;n giết Hữu Th&iacute; Sư, cướp Thi&ecirc;n Hạ Hội Bang Chủ chi vị, như thế tiểu nh&acirc;n cũng muốn dựa dẫm v&agrave;o ta lấy được Qu&acirc;n Thi&ecirc;n Lệnh, quả thực si t&acirc;m vọng tưởng!&quot;<br />\r\n<br />\r\n&quot;Nếu kh&ocirc;ng phải Lục bang chủ c&ocirc;ng năng, Thi&ecirc;n Hạ Hội bất qu&aacute; l&agrave; một bừa b&atilde;i v&ocirc; danh tiểu bang ph&aacute;i m&agrave; th&ocirc;i, tức cho d&ugrave; Bang Chủ chi vị bất qu&aacute; l&agrave; người c&oacute; t&agrave;i mới chiếm được, nếu ngươi kh&ocirc;ng muốn giao ra Qu&acirc;n Thi&ecirc;n Lệnh, như vậy ta liền tự m&igrave;nh tới lấy!&quot;<br />\r\n<br />\r\nN&oacute;i ra, nam tử mặc &aacute;o đen n&agrave;y biến sắc, mở miệng n&oacute;i: &quot;Ch&eacute;m tận giết tuyệt, một t&ecirc;n cũng kh&ocirc;ng để lại!&quot;<br />\r\n<br />\r\nLời vừa n&oacute;i ra, c&ograve;n lại mấy người quần &aacute;o đen trường kiếm trong tay chấn động, nhất thời trong đ&oacute; hướng về đ&aacute;m người đ&aacute;nh tới.<br />\r\n<br />\r\nTrong l&uacute;c nhất thời, hai phe nh&acirc;n m&atilde; đ&aacute;nh nhau chết sống c&ugrave;ng một chỗ.<br />\r\n<br />\r\nĐ&aacute;nh nhau chết sống trong đ&oacute;, trong kh&aacute;ch sạn tiếng sắt th&eacute;p va chạm b&ecirc;n tai kh&ocirc;ng dứt, binh kh&iacute; va chạm trong đ&oacute; phun đi ra kh&ocirc;ng chỗ hỏa hoa, nh&igrave;n một c&aacute;i kh&aacute; l&agrave; bắt mắt.<br />\r\n<br />\r\nM&agrave; Lạc Trưởng Thi&ecirc;n một phương đều l&agrave; ương ngạnh, mặc d&ugrave; địch nhiều ta &iacute;t, cũng kh&ocirc;ng sợ h&atilde;i ch&uacute;t n&agrave;o, ra sức ch&eacute;m giết.<br />\r\n<br />\r\nVậy d&ugrave; l&agrave; như thế, 3 người n&agrave;y như cũ quả bất địch ch&uacute;ng, thua trận.<br />\r\n<br />\r\nTrong ba người 2 người dĩ nhi&ecirc;n bỏ m&igrave;nh tại chỗ, Lạc Trưởng Thi&ecirc;n mặc d&ugrave; c&ograve;n tại độc lập ch&egrave;o chống, nhưng hắn tr&ecirc;n người dĩ nhi&ecirc;n thụ thương rất nặng, kh&ocirc;ng ngừng chảy m&aacute;u.<br />\r\n<br />\r\nM&agrave; mọi người ở đ&acirc;y ch&eacute;m giết thời điểm, Vương D&atilde; nhưng đứng ở một b&ecirc;n, vẻ mặt lo lắng.<br />\r\n<br />\r\nHắn đ&acirc;y cũng kh&ocirc;ng phải l&agrave; lo lắng Lạc Trưởng Thi&ecirc;n sinh tử.<br />\r\n<br />\r\nTrong chốn giang hồ &acirc;n o&aacute;n ph&acirc;n tranh hắn sớm đ&atilde; kh&ocirc;ng thấy kinh ngạc, cảnh tượng trước mắt căn bản l&agrave; kh&ocirc;ng c&oacute; c&aacute;ch ở hắn trong l&ograve;ng nhấc l&ecirc;n 1 tia gợn s&oacute;ng.<br />\r\n<br />\r\nHắn lo lắng, l&agrave; kh&aacute;ch sạn n&agrave;y b&ecirc;n trong b&agrave;y biện.<br />\r\n<br />\r\nV&otilde; l&acirc;m nh&acirc;n sĩ đ&aacute;nh nhau có th&ecirc;̉ kh&ocirc;ng chọn Tửu Qu&aacute;n Kh&aacute;ch Sạn sao?<br />\r\n<br />\r\nL&agrave;m hỏng c&aacute;i b&agrave;n b&agrave;y biện cũng l&agrave; trắng b&oacute;ng bạc a!<br />\r\n<br />\r\nHưu!<br />\r\n<br />\r\nNgay tại Vương D&atilde; suy nghĩ lung tung thời khắc, 1 tiếng b&eacute;n nhọn &acirc;m thanh x&eacute; gi&oacute; l&ecirc;n.<br />\r\n<br />\r\nT&igrave;m theo tiếng xem x&eacute;t, đ&atilde; thấy 1 đạo lạnh lẽo phảng phất điện quang đồng dạng, bay thẳng tự m&igrave;nh cổ họng m&agrave; đến.<br />\r\n<br />\r\n1 kiếm n&agrave;y tr&agrave;n đầy s&aacute;t cơ, hiển nhi&ecirc;n muốn đem ch&iacute;nh m&igrave;nh trảm dưới kiếm.<br />\r\n<br />\r\nM&agrave; th&ocirc;i!<br />\r\n<br />\r\nNh&igrave;n thấy c&aacute;i n&agrave;y chạm mặt tới kiếm quang, Vương D&atilde; lắc đầu.<br />\r\n<br />\r\nNếu đ&aacute;m người n&agrave;y khăng khăng như thế, c&aacute;i kia dứt kho&aacute;t liền giết hết a!<br />\r\n<br />\r\nNghĩ tới đ&acirc;y, Vương D&atilde; th&acirc;n thể hơi động một ch&uacute;t, chuẩn bị động thủ.<br />\r\n<br />\r\n&quot;Chưởng quỹ cẩn thận!&quot;<br />\r\n<br />\r\nNgay tại Vương D&atilde; chuẩn bị động thủ thời khắc, lại nghe 1 tiếng quen thuộc thanh &acirc;m truyền đến.<br />\r\n<br />\r\nT&igrave;m theo tiếng nh&igrave;n lại, đ&atilde; thấy 1 b&oacute;ng người đi tới trước mặt m&igrave;nh.<br />\r\n<br />\r\nTh&acirc;n ảnh n&agrave;y tốc độ cực nhanh, trong chớp mắt liền đến trước người m&igrave;nh. Hắn ra tay như điện, hai ng&oacute;n tay c&ugrave;ng nhau kẹp lấy đ&acirc;m về ph&iacute;a Vương D&atilde; trường kiếm, cổ tay chuyển một c&aacute;i.<br />\r\n<br />\r\nBinh!<br />\r\n<br />\r\nMột tiếng vang gi&ograve;n, c&aacute;i n&agrave;y một thanh trường kiếm vỡ vụn th&agrave;nh từng mảnh.<br />\r\n<br />\r\nTrường kiếm tan vỡ trong nh&aacute;y mắt, th&acirc;n ảnh n&agrave;y kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o do dự, đột nhi&ecirc;n 1 chưởng hướng về người &aacute;o đen kia ngay ngực ấn đi.<br />\r\n<br />\r\nẦm!<br />\r\n<br />\r\nLại nghe một tiếng vang trầm, người &aacute;o đen kia th&acirc;n thể phảng phất vải rách một dạng bay rớt ra ngo&agrave;i, đập xuống đất đầu n&atilde;o nghi&ecirc;ng một c&aacute;i, kh&ocirc;ng c&ograve;n kh&iacute; tức.<br />\r\n<br />\r\n! ! !<br />\r\n<br />\r\nThấy một m&agrave;n như vậy, Vương D&atilde; trong l&ograve;ng giật m&igrave;nh.<br />\r\n<br />\r\nHắn mục quang lại nhất chuyển, lại ph&aacute;t hiện c&aacute;i th&acirc;n ảnh n&agrave;y, lại l&agrave; ng&agrave;y b&igrave;nh thường kh&ocirc;ng c&oacute; tiếng tăm g&igrave; A C&aacute;t.<br />\r\n<br />\r\nA C&aacute;t thế m&agrave; lại v&otilde; c&ocirc;ng?<br />\r\n<br />\r\nKh&ocirc;ng chỉ biết, c&ograve;n 1 chưởng ấn ra sinh sinh đem người đ&aacute;nh chết, chỉ ri&ecirc;ng 1 chưởng n&agrave;y, dĩ nhi&ecirc;n nh&igrave;n ra c&aacute;i n&agrave;y A C&aacute;t kh&ocirc;ng phải yếu ớt!<br />\r\n<br />\r\nTrong l&ograve;ng kinh ngạc thời khắc, Vương D&atilde; &aacute;nh mắt rơi v&agrave;o A C&aacute;t tr&ecirc;n người.<br />\r\n<br />\r\nĐ&atilde; thấy l&uacute;c n&agrave;y A C&aacute;t thay đổi trong ng&agrave;y thường trộm gian d&ugrave;ng m&aacute;nh lới lười nh&aacute;c t&iacute;nh c&aacute;ch, thay v&agrave;o đ&oacute; x&aacute;c thực vẻ mặt kh&iacute; kh&aacute;i h&agrave;o h&ugrave;ng.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n~~~ nguy&ecirc;n bản c&ograve;ng xuống th&acirc;n thể l&uacute;c n&agrave;y cũng đứng thẳng l&ecirc;n, một cổ kh&iacute; tức v&ocirc; h&igrave;nh từ trong đến ngo&agrave;i tản m&aacute;t ra m&agrave;, mang theo 1 cỗ khiếp người &aacute;p b&aacute;ch cảm gi&aacute;c.<br />\r\n<br />\r\n&quot;Hảo tiểu tử, lại l&agrave; một T&ocirc;ng Sư cảnh giới cao thủ!&quot;<br />\r\n<br />\r\nChỉ l&agrave; nh&agrave;n nhạt nh&igrave;n lướt qua, Vương D&atilde; thuận dịp nh&igrave;n ra A C&aacute;t cảnh giới.<br />\r\n<br />\r\nV&otilde; đạo một đường, đi qua c&aacute;c đời cao nh&acirc;n tổng kết quy nạp, chia l&agrave;m 9 c&aacute;i cảnh giới: V&otilde; Sư, tam lưu V&otilde; Sư, nhị lưu V&otilde; Sư, nhất lưu V&otilde; Sư, T&ocirc;ng sư, Đại T&ocirc;ng Sư, V&otilde; Th&aacute;nh, Nh&acirc;n Ti&ecirc;n, Ti&ecirc;n Thi&ecirc;n.<br />\r\n<br />\r\nTrong đ&oacute;, đến T&ocirc;ng Sư cảnh giới, tr&ecirc;n giang hồ cũng đ&atilde; l&agrave; cao thủ tồn tại.<br />\r\n<br />\r\nNgay tại Vương D&atilde; &acirc;m thầm suy nghĩ về sau, cầm đầu nam nh&acirc;n &aacute;o đen cũng phản ứng lại.<br />\r\n<br />\r\nHắn đầu ti&ecirc;n l&agrave; đ&aacute;nh gi&aacute; A C&aacute;t hai mắt, &ocirc;m quyền chắp tay, mở miệng n&oacute;i ra: &quot;Ch&uacute;ng ta kh&ocirc;ng chỉ c&oacute; cao thủ ở đ&acirc;y, c&oacute; nhiều đắc tội, nếu như c&aacute;c hạ c&ugrave;ng việc n&agrave;y kh&ocirc;ng quan hệ đều c&oacute; thể rời đi, ch&uacute;ng ta tuyệt kh&ocirc;ng ngăn trở!&quot;<br />\r\n<br />\r\nN&oacute;i ra, nam nh&acirc;n n&agrave;y n&eacute; người sang một b&ecirc;n, cho A C&aacute;t tr&aacute;nh ra một con đường.<br />\r\n<br />\r\nThấy một m&agrave;n như vậy, Vương D&atilde; trong l&ograve;ng cười thầm 1 tiếng.<br />\r\n<br />\r\nGiang hồ ch&iacute;nh l&agrave; như thế, thực lực vi t&ocirc;n.<br />\r\n<br />\r\nNgươi thực lực mạnh mẽ, đương nhi&ecirc;n khắp nơi được người t&ocirc;n k&iacute;nh, nếu như thực lực thấp chỉ l&agrave; người kh&aacute;c dưới kiếm d&ecirc; đợi l&agrave;m thịt!<br />\r\n<br />\r\nNghe được nam nh&acirc;n n&agrave;y ng&ocirc;n ngữ, A C&aacute;t nh&igrave;n một ch&uacute;t Lạc Trưởng Thi&ecirc;n, lại nh&igrave;n một ch&uacute;t Vương D&atilde;, lắc đầu.<br />\r\n<br />\r\nTiểu tử n&agrave;y coi như c&oacute; ch&uacute;t lương t&acirc;m!<br />\r\n<br />\r\nNh&igrave;n v&agrave;o A C&aacute;t lắc đầu, Vương D&atilde; trong l&ograve;ng &acirc;m thầm suy nghĩ.<br />\r\n<br />\r\nĐồng thời hắn cũng y&ecirc;n l&ograve;ng.<br />\r\n<br />\r\nNếu A C&aacute;t th&acirc;n mang v&otilde; nghệ, m&igrave;nh cũng tránh khỏi xuất thủ!<br />\r\n<br />\r\n&quot;Nếu c&aacute;c hạ khăng khăng như thế, vậy liền đừng tr&aacute;ch ch&uacute;ng ta v&ocirc; t&igrave;nh!&quot;<br />\r\n<br />\r\n~~~ l&uacute;c n&agrave;y, cầm đầu hắc y nam tử lạnh r&ecirc;n một tiếng, đ&atilde; thấy trường kiếm trong tay của hắn khẽ động: &quot;Giết!&quot;<br />\r\n<br />\r\nBang!<br />\r\n<br />\r\nLời vừa n&oacute;i ra, mấy người c&ograve;n lại trường kiếm trong tay, c&ugrave;ng nhau hướng về A C&aacute;t c&ocirc;ng tới.<br />\r\n<br />\r\nNh&igrave;n thấy một m&agrave;n n&agrave;y, A C&aacute;t cũng vẫn kh&ocirc;ng hoảng hốt.<br />\r\n<br />\r\nĐ&atilde; thấy quanh người hắn 1 cỗ kim sắc nội kh&iacute; d&acirc;ng l&ecirc;n, hướng về đ&aacute;m người trước mặt ph&oacute;ng đi.</p>', '2021-11-27 22:01:41', 12, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(204, 53, 'Thanh niên nhiệt huyết tiểu thuyết: Giáo chủ về hưu thường ngày tác giả: Vân Sơn Thanh', '3', '<p>X&ocirc;ng v&agrave;o đ&aacute;m người b&ecirc;n trong, A C&aacute;t trong nh&aacute;y mắt liền c&ugrave;ng mấy t&ecirc;n triền đấu c&ugrave;ng một chỗ.<br />\r\n<br />\r\nTrong l&uacute;c đ&oacute; đ&atilde; thấy A C&aacute;t quanh th&acirc;n kim quang phun tr&agrave;o, chưởng ảnh bay t&aacute;n loạn.<br />\r\n<br />\r\nHắn mỗi một chưởng oanh ra, đều cũng mang theo 1 cỗ hồn trầm cự lực, giống như cự phủ khai sơn một dạng cương m&atilde;nh kh&ocirc;ng đ&uacute;c.<br />\r\n<br />\r\nNh&igrave;n một c&aacute;i, giống như La H&aacute;n h&agrave;ng thế, Phục Hổ cầm long, đ&aacute;nh mấy t&ecirc;n li&ecirc;n tục bại lui.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; Đại Thiện tự B&agrave;n Nhược thần chưởng!&quot;<br />\r\n<br />\r\nNh&igrave;n v&agrave;o A C&aacute;t thi triển chưởng ph&aacute;p, Vương D&atilde; kh&ocirc;ng khỏi trong l&ograve;ng thầm n&oacute;i: &quot;Kh&ocirc;ng nghĩ tới tuyệt t&iacute;ch nhiều năm B&agrave;n Nhược thần chưởng, lại c&ograve;n c&oacute; truyền thừa, hơn nữa c&ograve;n l&agrave; A C&aacute;t tiểu tử n&agrave;y!&quot;<br />\r\n<br />\r\n&quot;Phật quang chiếu thế! Vạn ph&aacute;p giai kh&ocirc;ng!&quot;<br />\r\n<br />\r\nNgay tại Vương D&atilde; suy tư thời khắc, đ&atilde; thấy A C&aacute;t đột nhi&ecirc;n h&eacute;t lớn một tiếng.<br />\r\n<br />\r\nĐồng thời to&agrave;n th&acirc;n hắn kim sắc nội tức ngưng tụ, bỗng nhi&ecirc;n 1 chưởng hướng về ph&iacute;a trước hung hăng ấn ra.<br />\r\n<br />\r\n&Ocirc;ng!<br />\r\n<br />\r\n1 chưởng ấn n&agrave;y đi ra, ph&aacute;t ra 1 tiếng to lớn vang tr&acirc;̀m, tho&aacute;ng như hồng chung đại lữ đinh tai nhức &oacute;c.<br />\r\n<br />\r\nNh&igrave;n thật kỹ,, đ&atilde; thấy 1 c&aacute;i kim sắc chữ &quot;Vạn&quot; (卍) lăng kh&ocirc;ng oanh ra, trực tiếp đ&aacute;nh v&agrave;o mấy t&ecirc;n tr&ecirc;n người.<br />\r\n<br />\r\nNhất thời ở giữa 3 c&aacute;i n&agrave;y t&ecirc;n nguy&ecirc;n một đ&aacute;m bay rớt ra ngo&agrave;i, ng&atilde; tr&ecirc;n mặt đất, l&uacute;c ấy kh&ocirc;ng c&ograve;n kh&iacute; tức.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng sai, A C&aacute;t tiểu tử n&agrave;y tuổi c&ograve;n trẻ liền c&oacute; như c&ocirc;ng lực n&agrave;y, l&agrave; c&aacute;i tập v&otilde; hạt giống tốt &quot;<br />\r\n<br />\r\nNh&igrave;n trước mắt một m&agrave;n, Vương D&atilde; gật đầu một c&aacute;i, trong l&ograve;ng &acirc;m thầm t&aacute;n dương: &quot;Nếu như hắn cần c&agrave;y kh&ocirc;ng ngừng, luyện th&ecirc;m trước hơn hai mươi năm, cũng miễn cưỡng c&oacute; thể đủ b&ugrave; đắp được ta một th&agrave;nh c&ocirc;ng lực!&quot;<br />\r\n<br />\r\n&quot;Đại ca, tiểu tử n&agrave;y l&agrave; c&aacute;i cứng tay!&quot;<br />\r\n<br />\r\nĐ&aacute;nh chết 3 t&ecirc;n về sau, trong đ&oacute; một người &aacute;o đen mở miệng n&oacute;i ra.<br />\r\n<br />\r\n&quot;Người n&agrave;y v&otilde; c&ocirc;ng cao cường, ch&uacute;ng ta kh&ocirc;ng phải l&agrave; đối thủ, đi đầu r&uacute;t lui lại b&agrave;n bạc kỹ hơn!&quot;<br />\r\n<br />\r\nNghe tiếng, c&aacute;i kia người &aacute;o đen cầm đầu gật đầu một c&aacute;i.<br />\r\n<br />\r\nĐồng thời th&acirc;n h&igrave;nh hắn nhảy l&ecirc;n hướng về b&ecirc;n ngo&agrave;i nhanh ch&oacute;ng lao đi.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;A C&aacute;t, ngươi mau đuổi theo a!&quot;<br />\r\n<br />\r\nNh&igrave;n thấy trốn chạy, l&uacute;c n&agrave;y Vương D&atilde; vỗ mặt b&agrave;n vội v&agrave;ng n&oacute;i: &quot;Nếu l&agrave; thả chạy bọn họ, v&ocirc; c&ugrave;ng hậu hoạn a!&quot;<br />\r\n<br />\r\n~~~ l&uacute;c n&agrave;y Vương D&atilde; mặt đầy lo lắng, phảng phất thực sợ h&atilde;i đ&aacute;m người &aacute;o đen kia ng&agrave;y sau trả th&ugrave;.<br />\r\n<br />\r\nNhưng m&agrave;, l&uacute;c n&agrave;y A C&aacute;t nhưng cũng kh&ocirc;ng để &yacute; tới Vương D&atilde;, đ&atilde; thấy th&acirc;n h&igrave;nh hắn khẽ động đi tới Lạc Trưởng Thi&ecirc;n 1 b&ecirc;n, một đ&aacute;m đem hắn đỡ lấy, mở miệng n&oacute;i: &quot;Vị kh&aacute;ch quan kia, ngươi kh&ocirc;ng sao chứ?&quot;<br />\r\n<br />\r\n&quot;Đa, đa tạ thiếu hiệp . . . Xuất thủ cứu gi&uacute;p &quot;<br />\r\n<br />\r\n~~~ l&uacute;c n&agrave;y Lạc Trưởng Thi&ecirc;n bị A C&aacute;t đỡ lấy, hơi thở mong manh n&oacute;i: &quot;Đều tại ta chuyến n&agrave;y chủ quan, li&ecirc;n lụy thiếu hiệp . . .&quot;<br />\r\n<br />\r\n&quot;Giang hồ hỗn loạn, kh&oacute; tr&aacute;nh khỏi sự t&igrave;nh, kh&aacute;ch quan kh&ocirc;ng cần tự tr&aacute;ch &quot;<br />\r\n<br />\r\nA C&aacute;t an ủi Lạc Trưởng Thi&ecirc;n, đồng thời &aacute;nh mắt qu&eacute;t một v&ograve;ng vết thương của hắn.<br />\r\n<br />\r\nĐ&atilde; thấy Lạc Trưởng Thi&ecirc;n tr&ecirc;n người đ&atilde; c&oacute; nhiều chỗ kiếm thương, m&aacute;u tươi kh&ocirc;ng ngừng tu&ocirc;n ra, c&oacute; v&agrave;i chỗ dĩ nhi&ecirc;n thương tới chỗ yếu, mắt thấy đ&atilde; l&agrave; hết c&aacute;ch xoay chuyển.<br />\r\n<br />\r\n&quot;Mắt của ta nh&igrave;n l&agrave; kh&ocirc;ng được . . . Trước khi chết, vẫn c&ograve;n c&oacute; c&aacute;i y&ecirc;u cầu qu&aacute; đ&aacute;ng!&quot;<br />\r\n<br />\r\nN&oacute;i ra Lạc Trưởng Thi&ecirc;n từ trong ngực lấy ra một khối Thanh Ngọc lệnh b&agrave;i, đem hắn giao cho A C&aacute;t, mở miệng n&oacute;i: &quot;Đ&acirc;y l&agrave; Thi&ecirc;n Hạ Hội bang chủ t&iacute;n vật . . . Qu&acirc;n Thi&ecirc;n Lệnh, c&oacute; c&aacute;i n&agrave;y l&agrave;m cho người, c&oacute; thể khống chế Thi&ecirc;n Hạ Hội sự vụ lớn nhỏ, thiếu hiệp vả lại muốn đem hắn bảo quản thỏa đ&aacute;ng, kh&ocirc;ng cần thiết để cho rơi v&agrave;o tặc nh&acirc;n tay!&quot;<br />\r\n<br />\r\n&quot;Kh&aacute;ch quan, ch&uacute;ng ta đều l&agrave; bu&ocirc;n b&aacute;n, chỉ cầu b&igrave;nh an sống tạm, v&ocirc; &yacute; tr&ecirc;u chọc sự cố, chuyện n&agrave;y ng&agrave;i vẫn l&agrave; đi t&igrave;m những người kh&aacute;c a!&quot;<br />\r\n<br />\r\n~~~ l&uacute;c n&agrave;y, kh&ocirc;ng đợi A C&aacute;t đ&aacute;p ứng, 1 b&ecirc;n Vương D&atilde; vội v&agrave;ng n&oacute;i.<br />\r\n<br />\r\nTheo qua sự t&igrave;nh vừa rồi Vương D&atilde; liền c&oacute; thể th&acirc;́y v&acirc;̣y m&agrave; ra, c&aacute;i n&agrave;y Qu&acirc;n Thi&ecirc;n Lệnh căn bản ch&iacute;nh l&agrave; một tấm b&ugrave;a đ&ograve;i mạng, đến trong tay ai cũng l&agrave; v&ocirc; tận phiền phức.<br />\r\n<br />\r\nNếu như đặt ở 10 năm trước, Vương D&atilde; sơ nhập giang hồ một bầu nhiệt huyết, nhất định sẽ đ&oacute;n lấy khối n&agrave;y Qu&acirc;n Thi&ecirc;n Lệnh.<br />\r\n<br />\r\nNhưng l&agrave; hắn 10 năm trong đ&oacute; thường thấy giang hồ ngươi lừa ta gạt, đối với nh&acirc;n t&igrave;nh t&igrave;nh thế sớm đ&atilde; lương bạc.<br />\r\n<br />\r\nCăn cứ nhi&ecirc;̀u m&ocirc;̣t chuyện kh&ocirc;ng bằng &iacute;t một chuyện th&aacute;i độ, Vương D&atilde; tuyệt đối sẽ kh&ocirc;ng tu&acirc;n theo tấm lệnh b&agrave;i n&agrave;y.<br />\r\n<br />\r\nNhưng l&agrave;, c&aacute;i n&agrave;y vẻn vẹn Vương D&atilde; &yacute; nghĩ.<br />\r\n<br />\r\nĐ&atilde; thấy A C&aacute;t kh&ocirc;ng để &yacute; đến Vương D&atilde; ng&ocirc;n ngữ, hắn chậm r&atilde;i tiếp nhận Qu&acirc;n Thi&ecirc;n Lệnh, mở miệng n&oacute;i: &quot;Kh&aacute;ch quan y&ecirc;n t&acirc;m, n&agrave;y lệnh b&agrave;i ta tuyệt sẽ kh&ocirc;ng để cho rơi v&agrave;o tặc nh&acirc;n tay!&quot;<br />\r\n<br />\r\nAi,<br />\r\n<br />\r\nThanh ni&ecirc;n nhiệt huyết a!<br />\r\n<br />\r\nThấy một m&agrave;n như vậy, Vương D&atilde; khe khẽ lắc đầu.<br />\r\n<br />\r\nC&aacute;i n&agrave;y trẻ tuổi hiệp kh&aacute;ch ch&iacute;nh l&agrave; như thế, 1 cỗ nhiệt huyết x&ocirc;ng l&ecirc;n đầu, c&aacute;i g&igrave; củ khoai n&oacute;ng bỏng tay cũng d&aacute;m hướng tr&ecirc;n người &ocirc;m . . .<br />\r\n<br />\r\n&quot;Tạ . . . Thiếu hiệp . . .&quot;<br />\r\n<br />\r\nNghe được A C&aacute;t ng&ocirc;n ngữ, Lạc Trưởng Thi&ecirc;n nhẹ nh&agrave;ng phun ra một c&acirc;u, chợt đầu n&atilde;o nghi&ecirc;ng một c&aacute;i, bỏ m&igrave;nh tại chỗ.<br />\r\n<br />\r\n&quot;Ấy u ta th&acirc;n gia gia ấy!&quot;<br />\r\n<br />\r\nNh&igrave;n thấy Lạc Trưởng Thi&ecirc;n sau khi chết, Vương D&atilde; lập tức đi tới A C&aacute;t 1 b&ecirc;n, mở miệng n&oacute;i ra: &quot;Sự t&igrave;nh vừa rồi ngươi l&agrave; kh&ocirc;ng c&oacute; nghe sao? Đ&aacute;m người kia cũng l&agrave; Thi&ecirc;n Hạ Hội người, m&agrave; c&ograve;n cũng l&agrave; hướng về ph&iacute;a tấm lệnh b&agrave;i n&agrave;y đến!&quot;<br />\r\n<br />\r\n&quot;~~~ l&uacute;c n&agrave;y ngươi đem lệnh b&agrave;i nhận lấy, đ&acirc;y kh&ocirc;ng phải đem tai họa hướng tr&ecirc;n người m&igrave;nh dẫn sao?&quot;<br />\r\n<br />\r\n~~~ l&uacute;c n&agrave;y Vương D&atilde; tr&agrave;n đầy o&aacute;n tr&aacute;ch, trong lời n&oacute;i như l&agrave; 1 c&aacute;i o&aacute;n phụ một dạng.<br />\r\n<br />\r\n&quot;Chưởng quỹ, trong số mệnh n&ecirc;n c&oacute; t&aacute;m đấu gạo, đi khắp thi&ecirc;n hạ bất m&atilde;n thăng &quot;<br />\r\n<br />\r\n~~~ l&uacute;c n&agrave;y, A C&aacute;t lắc đầu, mở miệng n&oacute;i ra: &quot;Những ng&agrave;y n&agrave;y ph&iacute;a dưới người biết chết ở ch&uacute;ng ta kh&aacute;ch sạn, v&ocirc; luận ta c&oacute; tiếp hay kh&ocirc;ng tấm lệnh b&agrave;i n&agrave;y, bọn họ đều sẽ tới!&quot;<br />\r\n<br />\r\n&quot;M&agrave; c&ograve;n, người n&agrave;y cứu ngươi một mạng, l&uacute;c sắp chết, ta đ&aacute;p ứng đều l&agrave; để cho hắn an t&acirc;m . . .&quot;<br />\r\n<br />\r\nTrong ng&ocirc;n ngữ, A C&aacute;t mang theo từng tia từng tia th&acirc;m trầm.<br />\r\n<br />\r\n&quot;An t&acirc;m?&quot;<br />\r\n<br />\r\nNghe vậy, Vương D&atilde; hai mắt trợn tr&ograve;n, hắn nh&igrave;n trước mắt A C&aacute;t, mở miệng n&oacute;i: &quot;Hắn hiện tại hai ch&acirc;n đạp một c&aacute;i ngược lại l&agrave; an t&acirc;m, ch&uacute;ng ta đ&acirc;y! ?&quot;<br />\r\n<br />\r\n&quot;Đ&aacute;ng thương ta vất vả kinh doanh kh&aacute;ch sạn, l&atilde;o b&agrave; chết đều kh&ocirc;ng tục huyền, ng&agrave;y b&igrave;nh thường khổ t&acirc;m tiến h&agrave;nh nu&ocirc;i ngươi v&agrave; hậu tr&ugrave; 2 c&aacute;i n&agrave;y ăn c&aacute;i g&igrave; c&aacute;i g&igrave; kh&ocirc;ng đủ, l&agrave;m g&igrave; c&aacute;i g&igrave; kh&ocirc;ng được loại ngốc!&quot;<br />\r\n<br />\r\n&quot;Nhưng đến đầu đến, c&aacute;c ngươi c&ograve;n tận cho ta g&acirc;y chuyện thị phi!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Mệnh của ta l&agrave;m sao khổ như vậy a!&quot;<br />\r\n<br />\r\n~~~ l&uacute;c n&agrave;y Vương D&atilde; k&eacute;o d&agrave;i cuống họng phảng phất lừa h&iacute; một dạng g&agrave;o lấy tang, thỉnh thoảng c&ograve;n l&agrave;m bộ lau lau nước mắt.<br />\r\n<br />\r\nNếu l&agrave; ng&agrave;y trước, Vương D&atilde; th&acirc;n l&agrave;m Ma Gi&aacute;o gi&aacute;o chủ l&atilde;nh khốc băng l&atilde;nh s&aacute;t phạt quả đo&aacute;n, tựa như loại n&agrave;y g&acirc;n giọng kh&oacute;c tang đ&agrave;n b&agrave; đanh đ&aacute; h&agrave;nh vi hắn vốn l&agrave; khinh thường đi l&agrave;m.<br />\r\n<br />\r\nNhưng l&agrave; qua thời gian t&acirc;̉y l&ecirc;̃ về sau, Vương D&atilde; cũng liền coi nhẹ.<br />\r\n<br />\r\nNh&acirc;n sinh nha, dễ chịu trọng yếu nhất.<br />\r\n<br />\r\nVề phần trang bức đ&ugrave;a nghịch nha, giao cho người trẻ tuổi đi l&agrave;m liền tốt.<br />\r\n<br />\r\n&quot;Chưởng quỹ, ngươi chừng n&agrave;o th&igrave; khổ t&acirc;m kinh doanh?&quot;<br />\r\n<br />\r\nNghe được Vương D&atilde; lời n&oacute;i, A C&aacute;t mở miệng hỏi ngược lại: &quot;Ngươi ng&agrave;y b&igrave;nh thường ngoại trừ đứng quầy b&ecirc;n trong lấy tiền mắng chửi người, ch&iacute;nh l&agrave; đi dạo Di Hồng viện c&ugrave;ng nh&igrave;n l&eacute;n đại c&ocirc; nương tắm rửa, kh&aacute;ch sạn n&agrave;y thế nhưng l&agrave; ch&uacute;ng ta mệt nhất a!&quot;<br />\r\n<br />\r\nLời vừa n&oacute;i ra, Vương D&atilde; đ&atilde; l&agrave; nghẹn lời.<br />\r\n<br />\r\nLấy tiền mắng chửi người, đi dạo kỹ viện nh&igrave;n l&eacute;n đại c&ocirc; nương tắm rửa.<br />\r\n<br />\r\nNhững việc n&agrave;y, tự m&igrave;nh giống như tốt x&aacute;c thực l&agrave;m qua . . .<br />\r\n<br />\r\n&quot;Ngươi lấy ở đ&acirc;u nhiều như vậy n&oacute;i nhảm? !&quot;<br />\r\n<br />\r\nNghe được A C&aacute;t m&agrave; n&oacute;i, Vương D&atilde; n&acirc;ng nhấc tay, muốn cho A C&aacute;t 1 c&aacute;i bạo lật, nhưng l&agrave; nhớ tới A C&aacute;t người c&oacute; v&otilde; c&ocirc;ng về sau một chỉ thi thể tr&ecirc;n đất: &quot;Ngươi c&ograve;n đứng ng&acirc;y đ&oacute; l&agrave;m g&igrave;? Ngươi c&ograve;n kh&ocirc;ng nhanh đi b&aacute;o quan? !&quot;<br />\r\n<br />\r\n&quot;Như vậy mấy đều thi thể nằm tr&ecirc;n mặt đất, lui về ph&iacute;a sau c&ograve;n c&oacute; mở cửa kh&ocirc;ng?&quot;<br />\r\n<br />\r\nNghe được Vương D&atilde; ng&ocirc;n ngữ, A C&aacute;t trợn trắng mắt.<br />\r\n<br />\r\nĐồng thời hắn lấy đem &ocirc; giấy dầu đi ra ngo&agrave;i, trực tiếp hướng về Kim Lăng Th&agrave;nh phủ nha đi đến.<br />\r\n<br />\r\nQuả nhi&ecirc;n, c&ograve;n l&agrave; tự m&igrave;nh chống đỡ tất cả, tr&ocirc;ng cậy v&agrave;o c&aacute;i phế vật n&agrave;y điểm t&acirc;m chưởng quỹ căn bản kh&ocirc;ng đ&aacute;ng tin cậy . . .</p>', '2021-11-27 22:02:07', 16, 1),
(209, 58, 'Thế giới này có chút không đúng!', '1', '<p>Tiến v&agrave;o th&aacute;ng s&aacute;u, Đ&ocirc;ng Hải đ&atilde; n&oacute;ng như c&aacute;i l&ograve; lửa lớn.<br />\r\n<br />\r\nBầu trời xanh thẳm, kh&ocirc;ng nh&igrave;n thấy một đ&aacute;m m&acirc;y, ch&oacute;i chang liệt nhật phảng phất một vi&ecirc;n hỏa cầu khổng lồ, treo cao tr&ecirc;n bầu trời.<br />\r\n<br />\r\nĐ&ocirc;ng Hải nhất trung.<br />\r\n<br />\r\nNăm giờ rưỡi chiều.<br />\r\n<br />\r\n&quot;Reng reng reng...&quot;<br />\r\n<br />\r\nTiếng chu&ocirc;ng tan học vừa vang, to&agrave;n bộ s&acirc;n trường phảng phất từ trong ngủ m&ecirc; thức tỉnh, lập tức truyền ra một mảnh tiếng ồn &agrave;o.<br />\r\n<br />\r\nSau một l&aacute;t, lượng lớn học sinh từ c&aacute;c t&ograve;a nh&agrave; lầu dạy học b&ecirc;n trong tu&ocirc;n ra, tốp năm tốp ba cười n&oacute;i đi về hướng cửa trường học.<br />\r\n<br />\r\n&quot;Ng&agrave;y n&agrave;y, qu&aacute; n&oacute;ng!&quot; C&oacute; người chửi bới n&oacute;i.<br />\r\n<br />\r\n&quot;Vương Đằng ca, ban đ&ecirc;m Tường Vi qu&aacute;n bar đi uống rượu.&quot;<br />\r\n<br />\r\nMột thanh &acirc;m đem Vương Đằng từ trong suy nghĩ k&eacute;o về hiện thực.<br />\r\n<br />\r\n&quot;Hứa Kiệt!&quot;<br />\r\n<br />\r\nHắn nh&igrave;n xem người tới, gương mặt quen thuộc, xa xưa k&yacute; ức từ chỗ s&acirc;u trong &oacute;c tu&ocirc;n ra, đ&acirc;y l&agrave; hắn cao trung thời k&igrave; v&ograve;ng tr&ograve;n b&ecirc;n trong một c&aacute;i bạn chơi.<br />\r\n<br />\r\n&quot;C&aacute;c ngươi chơi đi, ban đ&ecirc;m ta liền kh&ocirc;ng đi, c&oacute; chút đau đầu.&quot; Vương Đằng n&oacute;i.<br />\r\n<br />\r\nHứa Kiệt vội la l&ecirc;n: &quot;Đừng a, Vương Đằng ca, ch&uacute;ng ta c&aacute;i n&agrave;y v&ograve;ng quan hệ vốn l&agrave; kh&ocirc;ng c&oacute; mấy người, ngươi kh&ocirc;ng đến liền kh&ocirc;ng c&oacute; &yacute; nghĩa, m&agrave; lại ch&uacute;ng ta cũng c&oacute; đoạn thời gian kh&ocirc;ng c&oacute; tụ, dạng n&agrave;y, thay c&aacute;i thời gian, ng&agrave;y mai? Ng&agrave;y kia?&quot;<br />\r\n<br />\r\nVương Đằng lắc đầu, bất đắc dĩ n&oacute;i: &quot;Vậy liền trời tối ng&agrave;y mai đi.&quot;<br />\r\n<br />\r\n&quot;Được rồi, ta th&ocirc;ng tri tiểu Vi bọn hắn một tiếng, trời tối ng&agrave;y mai lại tụ họp.&quot; Hứa Kiệt cao hứng cười n&oacute;i.<br />\r\n<br />\r\nHai người đi đến cửa trường học, ph&iacute;a trước đột nhi&ecirc;n rối loạn tưng bừng.<br />\r\n<br />\r\n&quot;A, ph&iacute;a trước chuyện g&igrave; xảy ra? Nhiều người như vậy v&acirc;y quanh.&quot; Hứa Kiệt nghi ngờ n&oacute;i.<br />\r\n<br />\r\n&quot;Ta đi xem một ch&uacute;t.&quot;<br />\r\n<br />\r\nN&oacute;i hắn đ&atilde; chạy đi l&ecirc;n, gạt ra đ&aacute;m người chui v&agrave;o trước đ&aacute;m người mặt.<br />\r\n<br />\r\nVương Đằng hai tay đ&uacute;t t&uacute;i, chậm ung dung đi qua, mấy c&aacute;i học sinh vừa nh&igrave;n thấy hắn, nhao nhao tr&aacute;nh ra n&oacute;i, sợ chọc tới hắn.<br />\r\n<br />\r\nM&igrave;nh Vương đại thiếu t&ecirc;n tuổi thật đ&uacute;ng l&agrave; đủ lớn!<br />\r\n<br />\r\nVương Đằng kh&ocirc;ng khỏi tự giễu.<br />\r\n<br />\r\nĐ&aacute;ng tiếc cũng kh&ocirc;ng phải l&agrave; c&aacute;i g&igrave; tốt thanh danh.<br />\r\n<br />\r\nHắn đi đến trước đ&aacute;m người một b&ecirc;n, nh&igrave;n thấy tr&ecirc;n đất trống, một nữ sinh đem mấy c&aacute;i t&oacute;c nhuộm v&agrave;ng v&agrave;ng lục lục thanh ni&ecirc;n l&ecirc;u lổng đ&aacute;nh ng&atilde; tr&ecirc;n mặt đất, từng c&aacute;i k&ecirc;u cha gọi mẹ, nh&igrave;n xem nữ sinh kia &aacute;nh mắt tr&agrave;n ngập sợ h&atilde;i.<br />\r\n<br />\r\nKết hợp với bốn ph&iacute;a nghị luận, hắn đại kh&aacute;i hi&ecirc;̉u rõ đến chuyện đ&atilde; xảy ra.<br />\r\n<br />\r\nMột &iacute;t thanh ni&ecirc;n l&ecirc;u lổng th&iacute;ch tụ tập ở cấp ba sơ trung nằm v&ugrave;ng, chuy&ecirc;n chọn thoạt nh&igrave;n dễ khi dễ học sinh hạ thủ, doạ dẫm bắt chẹt, đ&ugrave;a giỡn nữ học sinh v&acirc;n v&acirc;n.<br />\r\n<br />\r\nThụ hại học sinh sợ g&acirc;y phiền to&aacute;i, phần lớn n&eacute;n giận, dần d&agrave; c&agrave;ng cổ vũ bọn c&ocirc;n đồ kh&iacute; diễm.<br />\r\n<br />\r\nVừa mới tan học, mấy c&aacute;i ho&agrave;ng mao l&ocirc;ng xanh đụng phải H&agrave; Man Dung, gặp n&agrave;ng dung mạo xinh đẹp, liền kh&ocirc;ng nhịn được tiến l&ecirc;n đ&ugrave;a giỡn, n&oacute;i v&agrave;i c&acirc;u &ocirc; ng&ocirc;n uế ngữ.<br />\r\n<br />\r\nBọn hắn kh&ocirc;ng nghĩ tới, H&agrave; Man Dung kh&ocirc;ng những kh&ocirc;ng phải c&aacute;i g&igrave; mặc người nh&agrave;o nặn quả hồng mềm, ngược lại l&agrave; đ&oacute;a mọc gai hoa hồng.<br />\r\n<br />\r\nN&agrave;ng n&eacute;n giận xuất thủ, hai ba lần liền đem người đ&aacute;nh kh&ocirc;ng đứng dậy được.<br />\r\n<br />\r\n&quot;H&agrave; nữ thần kh&ocirc;ng hổ l&agrave; cao cấp V&otilde; Đồ, mấy c&aacute;i kia thanh ni&ecirc;n l&ecirc;u lổng l&uacute;c n&agrave;y xem như đ&aacute; tr&uacute;ng thiết bản!&quot;<br />\r\n<br />\r\nB&ecirc;n cạnh một c&aacute;i nam học sinh quơ nắm đấm, tr&ecirc;n mặt thanh xu&acirc;n đậu bởi v&igrave; qu&aacute; k&iacute;ch động m&agrave; c&oacute; ch&uacute;t phiếm hồng.<br />\r\n<br />\r\nNghe hắn lời n&oacute;i, nghiễm nhi&ecirc;n đem H&agrave; Man Dung xem như nữ thần.<br />\r\n<br />\r\n&quot;Trước kia liền nghe n&oacute;i c&aacute;i n&agrave;y H&agrave; Man Dung l&agrave; c&aacute;i học b&aacute;, kh&ocirc;ng nghĩ tới, th&acirc;n thủ của n&agrave;ng cũng lợi hại như vậy, chẳng lẽ c&oacute; luyện qua Taekwondo? Trong truyền thuyết đai đen cửu đoạn?&quot;<br />\r\n<br />\r\n&quot;Bất qu&aacute; lời n&oacute;i đi cũng phải n&oacute;i lại, c&aacute;i n&agrave;y đ&ocirc;i ch&acirc;n d&agrave;i thật đ&uacute;ng l&agrave; hiếm thấy, t&agrave;i cao b&ecirc;n trong d&aacute;ng người cứ như vậy tốt, về sau c&ograve;n phải!&quot;<br />\r\n<br />\r\nVương Đằng đang cảm kh&aacute;i H&agrave; Man Dung cường h&atilde;n, thuận tiện thưởng thức một ch&uacute;t thế gian mỹ hảo, đột nhi&ecirc;n nghe được 【 cao cấp V&otilde; Đồ 】 bốn chữ, biểu lộ trong nh&aacute;y mắt ngưng kết tr&ecirc;n mặt.<br />\r\n<br />\r\nHắn c&oacute; ch&uacute;t cho&aacute;ng v&aacute;ng!<br />\r\n<br />\r\nTa c&oacute; phải hay kh&ocirc;ng nghe lầm c&aacute;i g&igrave;?<br />\r\n<br />\r\nCũng đ&uacute;ng, m&igrave;nh vừa tr&ugrave;ng sinh, tinh thần c&oacute; chút hoảng hốt, nghe lầm cũng l&agrave; t&igrave;nh c&oacute; thể hiểu.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, Hứa Kiệt bu lại, ao ước n&oacute;i ra: &quot;Trường học của ch&uacute;ng ta tổng cộng năm c&aacute;i cao cấp V&otilde; Đồ, H&agrave; Man Dung l&agrave; trong đ&oacute; duy nhất nữ sinh, vẫn l&agrave; gia đ&igrave;nh b&igrave;nh thường xuất th&acirc;n, thật l&agrave; một c&aacute;i thi&ecirc;n t&agrave;i!&quot;<br />\r\n<br />\r\n&quot;...&quot; Vương Đằng im lặng.<br />\r\n<br />\r\nThần mẹ n&oacute; cao cấp V&otilde; Đồ, xem ra vừa rồi x&aacute;c thực kh&ocirc;ng nghe lầm.<br />\r\n<br />\r\nNhưng l&agrave;, hảo hảo đ&ocirc; thị trọng sinh, đột nhi&ecirc;n to&aacute;t ra c&aacute;i cao năng nghề nghiệp t&iacute;nh chuyện g&igrave; xảy ra a?<br />\r\n<br />\r\nĐ&acirc;y rốt cuộc c&oacute; phải l&agrave; tr&ugrave;ng sinh a?<br />\r\n<br />\r\nVương Đằng cảm thụ được đến từ to&agrave;n thế giới &aacute;c &yacute;, nội t&acirc;m tr&agrave;n ngập nghi vấn.<br />\r\n<br />\r\nH&agrave; Man Dung thấy dẫn tới nhiều người như vậy, nh&iacute;u m&agrave;y, kh&ocirc;ng tiếp tục để &yacute; tr&ecirc;n đất lưu manh, trực tiếp liền rời đi.<br />\r\n<br />\r\nN&agrave;ng vừa đi, đ&aacute;m người v&acirc;y xem tự nhi&ecirc;n cũng liền t&aacute;n.<br />\r\n<br />\r\nVương Đằng c&ugrave;ng Hứa Kiệt ở cửa trường học mỗi người đi một ngả.<br />\r\n<br />\r\nVương Đằng t&igrave;m tới đến đ&oacute;n m&igrave;nh xe, tại một đ&aacute;m học sinh ao ước b&ecirc;n trong, ngồi l&ecirc;n dừng ở ven đường BMW, nhanh ch&oacute;ng đi.<br />\r\n<br />\r\nTr&ecirc;n xe, hắn nh&igrave;n qua ngo&agrave;i cửa sổ kh&ocirc;ng ngừng r&uacute;t lui cảnh đường phố, c&oacute; ch&uacute;t xuất thần.<br />\r\n<br />\r\nL&uacute;c n&agrave;y hắn mới ch&uacute; &yacute; tới, hai b&ecirc;n đường phố c&acirc;y đều d&aacute;ng dấp cao lớn lạ thường, m&agrave; lại thảm thực vật phi thường tươi tốt.<br />\r\n<br />\r\nNhững thực vật n&agrave;y dinh dưỡng qu&aacute; thịnh đi, kh&oacute; tr&aacute;ch trước đ&oacute; ở trường học lu&ocirc;n lu&ocirc;n cảm thấy c&oacute; chút kh&ocirc;ng h&agrave;i h&ograve;a cảm gi&aacute;c.<br />\r\n<br />\r\nTrường học thảm thực vật cũng l&agrave; dị thường tươi tốt, chỉ bất qu&aacute; hắn một mực đắm ch&igrave;m trong tr&ugrave;ng sinh chuyện n&agrave;y b&ecirc;n trong, v&acirc;̣y mà kh&ocirc;ng c&oacute; ph&aacute;t gi&aacute;c.<br />\r\n<br />\r\nTr&ecirc;n đường đi, chứng kiến hết thảy để Vương Đằng phi thường giật m&igrave;nh.<br />\r\n<br />\r\nSạch sẽ gọn g&agrave;ng đường đi, người qua đường tiền vệ mặc phong c&aacute;ch, thương nghiệp phồn hoa tr&igrave;nh độ c&aacute;c loại.<br />\r\n<br />\r\nMột điểm kh&ocirc;ng giống như l&agrave; năm 2009, thậm ch&iacute; ngay cả hắn tr&ugrave;ng sinh l&uacute;c năm 2019 đều chưa hẳn có th&ecirc;̉ đạt tới tr&igrave;nh độ n&agrave;y.<br />\r\n<br />\r\nThế giới n&agrave;y quả nhi&ecirc;n c&oacute; vấn đề!<br />\r\n<br />\r\nVương Đằng đầu hỗn loạn tưng bừng, đột nhi&ecirc;n tr&ocirc;ng thấy một hai mươi tuổi đ&ocirc; thị mỹ nh&acirc;n, nắm một đầu l&ocirc;ng t&oacute;c xo&atilde; tung đại cẩu từ b&ecirc;n đường đi qua.<br />\r\n<br />\r\nC&aacute;i n&agrave;y vốn l&agrave; kh&ocirc;ng c&oacute; vấn đề g&igrave;, nhưng l&agrave; c&aacute;i n&agrave;y ch&oacute; cũng qu&aacute; lớn, chừng con ngh&eacute; con lớn như vậy.<br />\r\n<br />\r\nHắn nhịn kh&ocirc;ng được xổ một c&acirc;u n&oacute;i tục.<br />\r\n<br />\r\n&quot;Ta mẹ n&oacute;, c&aacute;i n&agrave;y ch&oacute; c&aacute;i g&igrave; chủng loại? Như thế lớn!&quot;<br />\r\n<br />\r\nHắn h&iacute;t một hơi thật s&acirc;u, trấn định lại, sờ l&ecirc;n cằm kinh nghi bất định.<br />\r\n<br />\r\n&quot;Xem ra ch&iacute;nh l&agrave; c&aacute;i l&ocirc;ng v&agrave;ng, thế nhưng l&agrave; đ&acirc;y cũng qu&aacute; lớn đi?&quot;<br />\r\n<br />\r\nTiếp xuống, hắn lại nh&igrave;n thấy mấy c&aacute;i nắm sủng vật người.<br />\r\n<br />\r\nNhững c&aacute;i kia sủng vật ch&oacute; đều lớn l&ecirc;n rất lớn, h&igrave;nh thể ho&agrave;n to&agrave;n vượt chỉ ti&ecirc;u, c&oacute; gia trưởng trực tiếp để đứa trẻ cưỡi tại đại cẩu tr&ecirc;n th&acirc;n dạo phố.<br />\r\n<br />\r\nSủng vật ch&oacute; đều th&agrave;nh tọa kỵ!<br />\r\n<br />\r\nHắn c&ograve;n chứng kiến c&oacute; sủng vật m&egrave;o v&acirc;̣y mà mọc ra hai c&aacute;i đu&ocirc;i, c&ograve;n c&oacute; h&igrave;nh thể đồng dạng to lớn h&oacute;a, tựa như b&aacute;o săn giống như.<br />\r\n<br />\r\nBất qu&aacute; bọn ch&uacute;ng đều lộ ra rất ngoan ngo&atilde;n, chung quanh người qua đường tựa hồ kh&ocirc;ng cảm thấy kinh ngạc, ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; coi ch&uacute;ng l&agrave; m&atilde;nh th&uacute;.<br />\r\n<br />\r\nVương Đằng đều c&oacute; chút chết lặng.<br />\r\n<br />\r\nĐ&acirc;y tuyệt đối kh&ocirc;ng phải hắn thế giới cũ, d&ugrave; l&agrave; đồng học vẫn l&agrave; những bạn học kia, trường học cũng l&agrave; c&aacute;i kia trường học, thế nhưng l&agrave; c&oacute; nhiều thứ r&otilde; r&agrave;ng đi chệch a.<br />\r\n<br />\r\nMở hơn hai mươi ph&uacute;t, đến gi&agrave;u hoa khu biệt thự, một trận bảy lần quặt t&aacute;m lần rẽ về sau, xe rốt cục dừng ở một t&ograve;a trước biệt thự.<br />\r\n<br />\r\nC&ograve;n tốt, nh&agrave; của m&igrave;nh c&ograve;n ở nơi n&agrave;y.<br />\r\n<br />\r\nN&oacute;i đến, nh&agrave; bọn hắn cũng coi như c&aacute;i kh&ocirc;ng lớn kh&ocirc;ng nhỏ ph&uacute; h&agrave;o.<br />\r\n<br />\r\nVương Đằng gia gia c&oacute; bốn c&aacute;i tử nữ, m&agrave; phụ th&acirc;n của hắn Vương Thịnh Quốc trong nh&agrave; sắp xếp l&atilde;o nhị, rất c&oacute; thương nghiệp t&agrave;i năng, tự m&igrave;nh lập bếp nấu kh&aacute;c, th&agrave;nh lập Tinh Đằng tập đo&agrave;n, gần với Vương Đằng gia gia s&aacute;ng lập Vinh Thịnh tập đo&agrave;n.<br />\r\n<br />\r\nTinh Đằng tập đo&agrave;n c&ugrave;ng Vinh Thịnh tập đo&agrave;n đều l&agrave; t&agrave;i sản qu&aacute; trăm triệu c&ocirc;ng ty lớn.<br />\r\n<br />\r\nBất qu&aacute;, Vương Đằng biết, to&agrave;n cầu c&ocirc;ng ty như vậy c&oacute; rất nhiều, Vương gia tại c&aacute;i n&agrave;y một mẫu ba phần đất c&oacute; lẽ kh&ocirc;ng tệ, nhưng l&agrave; tại những c&aacute;i kia ch&acirc;n ch&iacute;nh đại tập đo&agrave;n trước mặt, cũng bất qu&aacute; l&agrave; lớn một ch&uacute;t con kiến.<br />\r\n<br />\r\nỞ kiếp trước, Vương gia đắc tội người, bị một lột đến c&ugrave;ng, vẻn vẹn ki&ecirc;n tr&igrave; mấy th&aacute;ng, c&ocirc;ng ty tất cả đều ph&aacute; sản đ&oacute;ng cửa.<br />\r\n<br />\r\nVương gia triệt để hủy diệt, trở th&agrave;nh qu&aacute; khứ.<br />\r\n<br />\r\nHai nh&agrave; qu&aacute; trăm triệu c&ocirc;ng ty nghe v&agrave;o kh&ocirc;ng tệ, kết quả c&ograve;n chưa đủ người ta nh&eacute;t kẽ răng.<br />\r\n<br />\r\nKh&ocirc;ng thể kh&ocirc;ng n&oacute;i đ&acirc;y l&agrave; một loại ch&acirc;m chọc!<br />\r\n<br />\r\nVương Đằng từ tr&ecirc;n trời rơi xuống ph&agrave;m trần, về sau mấy năm, trải qua kh&ocirc;ng bằng heo ch&oacute; sinh hoạt, nhận hết ch&egrave;n &eacute;p nhục nh&atilde;, cho đến tr&ugrave;ng sinh... Kh&ocirc;ng đ&uacute;ng, c&oacute; lẽ kh&ocirc;ng phải tr&ugrave;ng sinh!<br />\r\n<br />\r\nThế giới n&agrave;y thay đổi qu&aacute; nhiều, kh&ocirc;ng biết Vương gia lại biến th&agrave;nh c&aacute;i dạng g&igrave;?<br />\r\n<br />\r\nThậm ch&iacute;, cha mẹ của m&igrave;nh vẫn l&agrave; ban đầu phụ mẫu sao?<br />\r\n<br />\r\nĐừng vừa v&agrave;o cửa, ngay cả phụ mẫu đều cho đ&ocirc;̉i.<br />\r\n<br />\r\n&quot;Sẽ kh&ocirc;ng! Sẽ kh&ocirc;ng!&quot; Vương Đằng vội v&agrave;ng an ủi m&igrave;nh một c&acirc;u, nội t&acirc;m của hắn cũng kh&ocirc;ng b&igrave;nh tĩnh, h&iacute;t s&acirc;u v&agrave;i khẩu kh&iacute;, mới bước v&agrave;o biệt thự đại m&ocirc;n.</p>', '2021-11-28 13:27:40', 15, 1),
(210, 58, 'Dị giới chung sống hoà bình nguyên tắc', '2', '<p>Năm 09 l&uacute;c n&agrave;y, Ph&uacute; Hoa cư x&aacute; biệt thự đ&atilde; đạt tới hơn 2 vạn một m2.<br />\r\n<br />\r\nVương Đằng nh&agrave; biệt thự chừng 800 m2, l&uacute;c trước v&agrave;o tay liền hoa hơn một ngh&igrave;n vạn, tăng th&ecirc;m trang tr&iacute;, vượt qua hai ng&agrave;n vạn.<br />\r\n<br />\r\nĐi v&agrave;o biệt thự, vừa mắt ch&iacute;nh l&agrave; rộng r&atilde;i đại sảnh.<br />\r\n<br />\r\nHai c&aacute;i người hầu đang đ&aacute;nh qu&eacute;t vệ sinh.<br />\r\n<br />\r\nVương Mẫu L&yacute; T&uacute; Mai th&igrave; l&agrave; tại ph&ograve;ng bếp bận rộn.<br />\r\n<br />\r\nN&agrave;ng xuất th&acirc;n thư hương m&ocirc;n đệ, l&uacute;c tuổi c&ograve;n trẻ c&agrave;ng l&agrave; trong đại học nổi danh t&agrave;i nữ, nhưng cũng kh&ocirc;ng phải l&agrave; mười ng&oacute;n kh&ocirc;ng d&iacute;nh nước m&ugrave;a xu&acirc;n.<br />\r\n<br />\r\nTương phản t&agrave;i nấu nướng của n&agrave;ng phi thường tinh xảo, l&agrave;m đồ ăn lu&ocirc;n lu&ocirc;n để Vương Đằng hai cha con t&aacute;n miệng kh&ocirc;ng dứt.<br />\r\n<br />\r\nCho n&ecirc;n L&yacute; T&uacute; Mai đều l&agrave; m&igrave;nh xuống bếp, mỗi ng&agrave;y l&agrave;m tốt đồ ăn, chờ trượng phu c&ugrave;ng nhi tử trở về ăn.<br />\r\n<br />\r\nNh&igrave;n xem trong ph&ograve;ng bếp bận rộn th&acirc;n ảnh, nghe quen thuộc đồ ăn m&ugrave;i thơm.<br />\r\n<br />\r\nVương Đằng đứng tại cửa ph&ograve;ng bếp, mi&ecirc;̣ng ng&acirc;̣p ngừng, thật l&acirc;u mới h&ocirc; một tiếng.<br />\r\n<br />\r\n&quot;Mẹ, ta trở về!&quot;<br />\r\n<br />\r\n&quot;Trở về &aacute;!&quot;<br />\r\n<br />\r\nL&yacute; T&uacute; Mai quay đầu, cười n&oacute;i: &quot;Đợi một l&aacute;t, cha ngươi hẳn l&agrave; cũng sắp đến nh&agrave;, chờ hắn trở về c&ugrave;ng nhau ăn cơm.&quot;<br />\r\n<br />\r\n&quot;Tốt! Ta đi trước tẩy c&aacute;i mặt.&quot; Vương Đằng cũng kh&ocirc;ng quay đầu lại x&ocirc;ng l&ecirc;n lầu, chạy v&agrave;o ph&ograve;ng vệ sinh.<br />\r\n<br />\r\n&quot;Đứa nhỏ n&agrave;y! H&ocirc;m nay l&agrave;m sao rồi?&quot; L&yacute; T&uacute; Mai lắc đầu.<br />\r\n<br />\r\nTrong ph&ograve;ng vệ sinh, Vương Đằng mở v&ograve;i b&ocirc;ng sen, mặc cho nước lạnh cọ rửa khu&ocirc;n mặt.<br />\r\n<br />\r\nTrở về!<br />\r\n<br />\r\nKh&ocirc;ng nghĩ tới, c&aacute;ch xa nhau mười năm, c&ograve;n c&oacute; cơ hội lại trở lại c&aacute;i nh&agrave; n&agrave;y.<br />\r\n<br />\r\nL&uacute;c trước Vương gia r&aacute;ch n&aacute;t về sau, Vương Thịnh Quốc bị người &aacute;m to&aacute;n qua đời, cũng kh&ocirc;ng l&acirc;u lắm, L&yacute; T&uacute; Mai cũng buồn bực sầu n&atilde;o m&agrave; chết.<br />\r\n<br />\r\nVương Đằng ngẩng đầu, lau mặt một c&aacute;i, nh&igrave;n xem trong gương m&igrave;nh, con mắt c&oacute; ch&uacute;t phiếm hồng.<br />\r\n<br />\r\nMười năm cải biến qu&aacute; nhiều.<br />\r\n<br />\r\nVương Đằng thay đổi, hắn kh&ocirc;ng c&ograve;n l&agrave; l&uacute;c trước hắn, kiếp trước, hắn t&acirc;m sớm đ&atilde; trở n&ecirc;n lạnh lẽo cứng rắn như sắt.<br />\r\n<br />\r\nNhưng l&agrave; kỳ thật ở đ&aacute;y l&ograve;ng hắn một g&oacute;c n&agrave;o đ&oacute;, vẫn cất giấu mấy chỗ mềm mại, chỉ l&agrave; trước kia kh&ocirc;ng muốn đi đụng v&agrave;o th&ocirc;i.<br />\r\n<br />\r\nVương Đằng đem mặt lau kh&ocirc;, đi ra ph&ograve;ng vệ sinh, theo k&yacute; ức mở ra ph&ograve;ng ngủ của m&igrave;nh, đi v&agrave;o.<br />\r\n<br />\r\nQuen thuộc b&agrave;i tr&iacute;, quen thuộc hết thảy.<br />\r\n<br />\r\nTr&ecirc;n tường d&aacute;n NBA ng&ocirc;i sao cầu thủ &aacute;p ph&iacute;ch, tr&ecirc;n gi&aacute; s&aacute;ch chất đống &acirc;m nhạc CD c&ugrave;ng tiểu thuyết, b&agrave;n đọc s&aacute;ch dựa v&agrave;o cửa sổ, rời giường cũng kh&ocirc;ng xa, tr&ecirc;n b&agrave;n b&agrave;y biện m&aacute;y t&iacute;nh.<br />\r\n<br />\r\nM&aacute;y t&iacute;nh m&ocirc; h&igrave;nh rất mới, hơn nữa c&ograve;n l&agrave; quả t&aacute;o b&agrave;i, s&aacute;ng ng&acirc;n sắc th&acirc;n m&aacute;y bay nhẹ nh&agrave;ng m&agrave; thời thượng.<br />\r\n<br />\r\nLại l&agrave; một c&aacute;i kh&ocirc;ng ph&ugrave; hợp thời gian tuyến vật phẩm.<br />\r\n<br />\r\nNh&igrave;n thấy m&aacute;y t&iacute;nh, hắn chợt nhớ tới c&aacute;i g&igrave;, từ trong t&uacute;i x&aacute;ch t&igrave;m ra một khung điện thoại, quả nhi&ecirc;n cũng kh&ocirc;ng phải năm 09 lỗi thời, m&agrave; l&agrave; kiểu mới nhất quả t&aacute;o 8.<br />\r\n<br />\r\nVương Đằng sớm c&oacute; chuẩn bị t&acirc;m l&yacute;, rất b&igrave;nh tĩnh điền mật m&atilde; v&agrave;o, mở ra điện thoại di động.<br />\r\n<br />\r\nHắn đột nhi&ecirc;n rất may mắn m&igrave;nh l&agrave; c&aacute;i người kh&ocirc;ng nhớ được mật m&atilde;, nhiều năm như vậy một mực d&ugrave;ng c&ugrave;ng một c&aacute;i mật m&atilde;, liền kh&ocirc;ng đổi qua.<br />\r\n<br />\r\nMeituan, đ&oacute;i sao, Wechat, TikTok... B&ecirc;n trong rất nhiều phần mềm APP đều l&agrave; về sau mới xuất hiện, năm 09 căn bản liền kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nVừa tr&ugrave;ng sinh l&uacute;c đ&oacute;, hắn c&ograve;n muốn dựa v&agrave;o tr&iacute; nhớ kiếp trước, một đường nghịch tập thượng vị, để Jack M&atilde;, Tiểu M&atilde; Ca c&aacute;c loại đại l&atilde;o tập thể h&aacute;t chinh phục!<br />\r\n<br />\r\nPhải, hiện tại kh&ocirc;ng đ&ugrave;a!<br />\r\n<br />\r\nThở hắt ra, hắn để điện thoại di động xuống, bật m&aacute;y t&iacute;nh l&ecirc;n.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; biện ph&aacute;p g&igrave;, so s&aacute;nh với lưới thẩm tra c&agrave;ng nhanh gọn<br />\r\n<br />\r\nM&aacute;y t&iacute;nh điện thoại đ&atilde; ph&aacute;t triển đến loại tr&igrave;nh độ n&agrave;y, kh&ocirc;ng c&oacute; đạo l&yacute; mạng lưới c&ograve;n dừng lại tại năm 09 bộ d&aacute;ng.<br />\r\n<br />\r\nKhoa học kỹ thuật c&ugrave;ng mạng lưới xưa nay kh&ocirc;ng nhưng chia cắt.<br />\r\n<br />\r\nBật m&aacute;y t&iacute;nh l&ecirc;n về sau, Vương Đằng hơi kinh ngạc.<br />\r\n<br />\r\nM&aacute;y vi t&iacute;nh hệ điều h&agrave;nh v&acirc;̣y mà kh&ocirc;ng phải WIN10, m&agrave; l&agrave; đ&atilde; đời 11.<br />\r\n<br />\r\nXem ra cũng kh&ocirc;ng phải l&agrave; ho&agrave;n to&agrave;n c&ugrave;ng năm 2019 ph&aacute;t triển tr&igrave;nh độ giống nhau, vẫn l&agrave; tồn tại kh&aacute;c biệt t&iacute;nh.<br />\r\n<br />\r\nHắn quen thuộc một hồi l&acirc;u, mới th&iacute;ch ứng tới.<br />\r\n<br />\r\nMở ra Baidu tr&igrave;nh duyệt, t&igrave;m t&ograve;i &quot;V&otilde; Giả&quot; hai chữ, lượng lớn tin tức xo&aacute;t ra.<br />\r\n<br />\r\nHơn một giờ về sau, Vương Đằng đ&oacute;ng lại tr&igrave;nh duyệt, dựa v&agrave;o ghế, mặt mũi tr&agrave;n đầy kh&ocirc;ng thể tưởng tượng nổi.<br />\r\n<br />\r\n&quot;30 năm trước, to&agrave;n cầu c&aacute;c nơi xuất hiện vết nứt kh&ocirc;ng gian, khe hở b&ecirc;n kia nối liền dị thế giới &mdash;&mdash; Tinh V&otilde; đại lục!&quot;<br />\r\n<br />\r\n&quot;Tinh V&otilde; đại lục, tồn tại Nguy&ecirc;n Lực, người người tu luyện Nguy&ecirc;n Lực trở th&agrave;nh V&otilde; Giả!&quot;<br />\r\n<br />\r\n&quot;V&otilde; Giả v&ocirc; c&ugrave;ng cường đại!&quot;<br />\r\n<br />\r\n&quot;Cấp thấp V&otilde; Giả vỡ bia nứt đ&aacute;, r&uacute;t đao đoạn thủy, cao giai V&otilde; Giả ph&aacute; vỡ núi đoạn nhạc, dời s&ocirc;ng lấp biển...&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y cũng qu&aacute; huyền ảo đi!<br />\r\n<br />\r\nVương Đằng đều c&oacute; ch&uacute;t kh&ocirc;ng biết n&oacute;i c&aacute;i g&igrave;.<br />\r\n<br />\r\nĐịa Cầu nh&agrave; khoa học đối với Nguy&ecirc;n Lực tiến h&agrave;nh nghi&ecirc;n cứu, ph&aacute;t hiện n&oacute; rất c&oacute; thể ch&iacute;nh l&agrave; 【 vật chất tối 】, nhưng l&agrave; v&igrave; c&aacute;i g&igrave; Tinh V&otilde; đại lục người c&ugrave;ng động thực vật c&oacute; thể hấp thu vật chất tối đến cải biến tự th&acirc;n, m&agrave; Địa Cầu sinh vật lại kh&ocirc;ng c&aacute;ch n&agrave;o l&agrave;m được, bọn hắn cũng kh&ocirc;ng thể n&agrave;o biết được.<br />\r\n<br />\r\nKh&ocirc;ng bao l&acirc;u, theo vết nứt kh&ocirc;ng gian xuất hiện, Địa Cầu tựa hồ sinh ra một loại n&agrave;o đ&oacute; biến h&oacute;a, Địa Cầu sinh vật vậy m&agrave; bắt đầu nhận vật chất tối ảnh hưởng.<br />\r\n<br />\r\nThực vật trở n&ecirc;n c&agrave;ng cao hơn lớn tươi tốt, một &iacute;t sớm đ&atilde; tuyệt t&iacute;ch giống lo&agrave;i t&aacute;i hiện thế gian.<br />\r\n<br />\r\nĐộng vật cũng biến th&agrave;nh c&agrave;ng th&ecirc;m khổng lồ, th&acirc;n thể của bọn ch&uacute;ng đặc th&ugrave;, như l&agrave; l&acirc;n gi&aacute;p, m&oacute;ng nhọn, răng c&aacute;c loại cũng nhận được cường h&oacute;a.<br />\r\n<br />\r\nThậm ch&iacute; c&ograve;n c&oacute; một &iacute;t thi&ecirc;n ph&uacute; dị bẩm động vật sinh ra biến dị, c&oacute; được Kim Mộc Thủy Hỏa Thổ c&aacute;c loại thuộc t&iacute;nh năng lực đặc th&ugrave;.<br />\r\n<br />\r\nNh&acirc;n loại l&agrave;m vạn vật linh trưởng, tự nhi&ecirc;n cũng tại tiến h&oacute;a phạm vi.<br />\r\n<br />\r\nTh&acirc;n thể của nh&acirc;n loại cũng biến mạnh, chạy c&agrave;ng nhanh, nhảy cao hơn, lực lượng c&agrave;ng lớn, tuổi thọ... Cũng c&agrave;ng d&agrave;i!<br />\r\n<br />\r\n30 năm trước vốn l&agrave; hơn bảy mươi t&aacute;m mươi những l&atilde;o nh&acirc;n kia phần lớn cũng c&ograve;n c&ograve;n sống, bọn hắn đ&atilde; hơn một trăm tuổi, trạng th&aacute;i tinh thần c&ugrave;ng s&aacute;u bảy mươi tuổi kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c biệt.<br />\r\n<br />\r\nNgười Địa Cầu đi&ecirc;n cuồng!<br />\r\n<br />\r\nThế giới c&aacute;c quốc gia v&igrave; thế vứt bỏ hiềm kh&iacute;ch l&uacute;c trước, tổ chức to&agrave;n cầu li&ecirc;n hiệp hội nghị.<br />\r\n<br />\r\nCuối c&ugrave;ng quyết định tổ chức nh&acirc;n thủ tiến về trong vết nứt kh&ocirc;ng gian thăm d&ograve;, x&aacute;c định an to&agrave;n về sau, lần lượt điều động qu&acirc;n nh&acirc;n c&ugrave;ng c&aacute;c phương diện chuy&ecirc;n gia học giả tiến v&agrave;o chiếm giữ Tinh V&otilde; đại lục.<br />\r\n<br />\r\nNhững người n&agrave;y, được xưng l&agrave; Người Mở Đường!<br />\r\n<br />\r\nTinh V&otilde; đại lục rộng lớn v&ocirc; ngần, những Người Mở Đường tự xưng đến từ hải ngoại, lấy được d&acirc;n bản xứ t&iacute;n nhiệm, c&ugrave;ng bọn hắn tiến h&agrave;nh giao lưu c&ugrave;ng mậu dịch.<br />\r\n<br />\r\nNgười Địa Cầu cũng kh&ocirc;ng phải l&agrave; d&atilde; man x&acirc;m lấn, m&agrave; l&agrave; tiếp thu Hoa Hạ người l&atilde;nh đạo đưa ra chung sống ho&agrave; b&igrave;nh nguy&ecirc;n tắc, lấy &ocirc;n h&ograve;a phương thức c&ugrave;ng Tinh V&otilde; đại lục d&acirc;n bản địa ở chung.<br />\r\n<br />\r\nKh&ocirc;ng bao l&acirc;u, Người Mở Đường mang về Tinh V&otilde; đại lục phương ph&aacute;p tu luyện.<br />\r\n<br />\r\nTrải qua nghi&ecirc;n cứu nếm thử, địa cầu nh&acirc;n loại c&oacute; thể tu luyện, cuối c&ugrave;ng c&aacute;c quốc gia nhất tr&iacute; quyết định, c&ocirc;ng khai phương ph&aacute;p tu luyện, mở ra to&agrave;n d&acirc;n v&otilde; đạo thời đại.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; một lần trọng đại biến đổi!<br />\r\n<br />\r\nLịch sử từ đ&acirc;y ngoặt một c&aacute;i ngoặt lớn, dọc theo kh&ocirc;ng thể dự b&aacute;o phương hướng ph&aacute;t triển.<br />\r\n<br />\r\nNơi n&agrave;y g&otilde; bảng đen vạch trọng điểm, thi đại học muốn thi.<br />\r\n<br />\r\nNh&acirc;n loại cường đại nhất địa phương ch&iacute;nh l&agrave; tr&iacute; tuệ, th&ocirc;ng qua tu luyện, nh&acirc;n loại nhanh ch&oacute;ng l&agrave;m bản th&acirc;n lớn mạnh, ngắn ngủi thời gian mấy năm, c&aacute;c nơi tr&ecirc;n thế giới liền hiện ra lượng lớn cường đại V&otilde; Giả.<br />\r\n<br />\r\nNhững V&otilde; Giả n&agrave;y c&oacute; tiến v&agrave;o qu&acirc;n đội, c&oacute; tiến v&agrave;o ch&iacute;nh phủ, c&ograve;n c&oacute; th&igrave; thuộc về từng c&aacute;i đại tập đo&agrave;n đại gia tộc, h&igrave;nh th&agrave;nh li&ecirc;n minh thể, cũng c&oacute; một ch&uacute;t d&acirc;n gian tự do V&otilde; Giả kh&ocirc;ng muốn nhận tr&oacute;i buộc, thế l&agrave; th&agrave;nh lập từng c&aacute;i v&otilde; đạo qu&aacute;n, v&igrave; b&igrave;nh d&acirc;n cung cấp tu luyện đường tắt.<br />\r\n<br />\r\nCh&iacute;nh l&agrave; c&oacute; những V&otilde; Giả n&agrave;y tồn tại, mới c&oacute; thể đem một &iacute;t biến dị cường đại d&atilde; th&uacute; ngăn tại th&agrave;nh thị b&ecirc;n ngo&agrave;i, kh&ocirc;ng đến mức đối với x&atilde; hội văn minh tạo th&agrave;nh đại quy m&ocirc; xung k&iacute;ch c&ugrave;ng ph&aacute; hư.<br />\r\n<br />\r\nNhưng m&agrave;, V&otilde; Giả chung quy l&agrave; một c&aacute;i nghề nghiệp c&acirc;̀n thi&ecirc;n ph&uacute;, cũng kh&ocirc;ng phải l&agrave; người người đều c&oacute; thể trở th&agrave;nh V&otilde; Giả.<br />\r\n<br />\r\nMuốn trở th&agrave;nh ch&iacute;nh thức V&otilde; Giả, nhất định phải r&egrave;n luyện nhục th&acirc;n, an t&acirc;m căn cơ.<br />\r\n<br />\r\nGiai đoạn n&agrave;y người tu luyện ch&iacute;nh l&agrave; c&aacute;i gọi l&agrave; V&otilde; Đồ.<br />\r\n<br />\r\nCăn cơ đầy đủ ki&ecirc;n cố, mới c&oacute; thể hấp thu Nguy&ecirc;n Lực, chứa đựng trong th&acirc;n thể, cũng ch&iacute;nh l&agrave; ch&iacute;nh thức V&otilde; Giả.<br />\r\n<br />\r\nNếu như đem Nguy&ecirc;n Lực so s&aacute;nh nước, th&acirc;n thể ch&iacute;nh l&agrave; một c&aacute;i chứa nước vật chứa, hấp thu Nguy&ecirc;n Lực qu&aacute; tr&igrave;nh tựa như l&agrave; hướng trong th&ugrave;ng đựng nước, chỉ c&oacute; đem vật chứa th&ecirc;m rộng th&ecirc;m lớn th&ecirc;m bền, mới c&oacute; thể chứa đựng c&agrave;ng nhiều nước.<br />\r\n<br />\r\nV&otilde; Giả tu luyện nguy&ecirc;n l&yacute;, đơn giản đến n&oacute;i ch&iacute;nh l&agrave; như vậy,<br />\r\n<br />\r\nV&otilde; Đồ dựa theo cao thấp của lực lượng, tốc độ c&aacute;c th&acirc;n thể tổng hợp tố chất, có th&ecirc;̉ chia l&agrave;m sơ, trung, cao ba cấp bậc.<br />\r\n<br />\r\nTrước đ&oacute; ở cửa trường học đụng phải H&agrave; Man Dung ch&iacute;nh l&agrave; cao cấp V&otilde; Đồ, khoảng c&aacute;ch ch&iacute;nh thức V&otilde; Giả c&aacute;ch chỉ một bước.<br />\r\n<br />\r\nChỉ bất qu&aacute;, một bước n&agrave;y l&agrave;m kh&oacute; rất nhiều người.<br />\r\n<br />\r\nC&oacute; hay kh&ocirc;ng hấp thu Nguy&ecirc;n Lực nhập thể, tồn tại chất kh&aacute;c biệt.<br />\r\n<br />\r\nMột ch&iacute;nh thức V&otilde; Giả đ&atilde; tu luyện Nguy&ecirc;n Lực, c&oacute; thể đ&aacute;nh mười c&aacute;i cao cấp V&otilde; Đồ kh&ocirc;ng c&oacute; Nguy&ecirc;n Lực, đồng thời kh&ocirc;ng c&oacute; &aacute;p lực ch&uacute;t n&agrave;o.<br />\r\n<br />\r\nCao cấp V&otilde; Đồ c&ugrave;ng ch&iacute;nh thức V&otilde; Giả ở giữa, nh&igrave;n như chỉ k&eacute;m một ch&uacute;t, k&igrave; thực giống như hồng c&acirc;u.<br />\r\n<br />\r\nM&agrave; lại, ngh&egrave;o văn gi&agrave;u v&otilde;, V&otilde; Giả tu luyện cần đại lượng t&agrave;i nguy&ecirc;n, t&agrave;i nguy&ecirc;n đắt đỏ, người b&igrave;nh thường d&ugrave; sao ở v&agrave;o thế yếu.<br />\r\n<br />\r\nN&oacute;i t&oacute;m lại, tại lớn cơ số trước mặt, V&otilde; Giả số lượng vẫn l&agrave; số &iacute;t, đại đa số người bất qu&aacute; l&agrave; người b&igrave;nh thường hơi cường đại một điểm m&agrave; th&ocirc;i.<br />\r\n<br />\r\nNhưng c&aacute;i n&agrave;y cũng kh&ocirc;ng hề có th&ecirc;̉ ngăn cản V&otilde; Giả trở th&agrave;nh chủ lưu, V&otilde; Giả thưa thớt c&ugrave;ng tầm quan trọng ngược lại tạo n&ecirc;n n&oacute; địa vị si&ecirc;u nhi&ecirc;n.</p>', '2021-11-28 13:30:54', 4, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(211, 58, 'Từ hôm nay trở đi bật hack!', '3', '<p>Hơn bảy giờ tối.<br />\r\n<br />\r\nVương Đằng người một nh&agrave; mới cuối c&ugrave;ng ngồi c&ugrave;ng một chỗ ăn cơm chiều.<br />\r\n<br />\r\n&quot;Cha ngươi xe đều đến nửa đường, c&ocirc;ng ty lại c&oacute; ch&uacute;t sự t&igrave;nh, vừa đến một lần chậm trễ hơn một giờ, liền kh&ocirc;ng thể cơm nước xong xu&ocirc;i lại trở về.&quot; L&yacute; T&uacute; Mai cho Vương Đằng gắp thức ăn, ph&agrave;n n&agrave;n n&oacute;i.<br />\r\n<br />\r\nVương Đằng trong ph&ograve;ng tra hơn một giờ tư liệu, ngược lại l&agrave; đem ăn cơm cấp qu&ecirc;n, may m&agrave; cha hắn vừa vặn c&oacute; việc trở về trễ.<br />\r\n<br />\r\n&quot;Sự t&igrave;nh khẩn cấp, chỉ c&oacute; thể về trước đi xử l&yacute; một ch&uacute;t.&quot; Vương Thịnh Quốc bất đắc dĩ n&oacute;i.<br />\r\n<br />\r\n&quot;Ch&uacute;ng ta đừng để &yacute; tới cha ngươi, hắn liền tập trung tinh thần đặt ở hắn c&aacute;i kia ph&aacute; c&ocirc;ng ty ph&iacute;a tr&ecirc;n.&quot; L&yacute; T&uacute; Mai hướng ph&iacute;a Vương Thịnh Quốc trợn mắt.<br />\r\n<br />\r\nVương Đằng trong l&ograve;ng cười cười.<br />\r\n<br />\r\nKỳ thật, ở cấp ba thời k&igrave;, Vương Đằng c&ugrave;ng cha hắn quan hệ c&oacute; chút cứng đờ, Vương Thịnh Quốc mong con hơn người, đối với hắn tự nhi&ecirc;n nghi&ecirc;m ngặt một ch&uacute;t, nhưng l&agrave; Vương Đằng ở cấp ba thời k&igrave; t&oacute;m lại l&agrave; c&oacute; chút phản nghịch, cũng rất bất học v&ocirc; thuật, c&ugrave;ng hắn cha xung đột liền c&agrave;ng ng&agrave;y c&agrave;ng s&acirc;u.<br />\r\n<br />\r\nBất qu&aacute; b&acirc;y giờ ngẫm lại, Vương Đằng cảm thấy qu&aacute; khứ m&igrave;nh, một &iacute;t h&agrave;nh vi thực ng&acirc;y thơ buồn cười.<br />\r\n<br />\r\nTr&ecirc;n b&agrave;n cơm, Vương Đằng c&ugrave;ng Vương Thịnh Quốc đều lộ ra tương đối trầm mặc, L&yacute; T&uacute; Mai vội v&agrave;ng cho Vương Đằng gắp thức ăn, c&ugrave;ng hai cha con l&ocirc;i k&eacute;o việc nh&agrave;.<br />\r\n<br />\r\nVương Đằng rất hưởng thụ loại n&agrave;y kh&ocirc;ng kh&iacute;.<br />\r\n<br />\r\nVương Thịnh Quốc c&ugrave;ng Vương Đằng tr&ograve; chuyện v&agrave;i c&acirc;u, chủ yếu l&agrave; hỏi thăm học tập b&ecirc;n tr&ecirc;n t&igrave;nh huống.<br />\r\n<br />\r\nVương Đằng trung quy trung củ trả lời, kh&ocirc;ng c&oacute; giống trước kia tr&agrave;n đầy trung nhị kh&iacute; tức, động một ch&uacute;t lại quẳng đũa c&aacute;u kỉnh.<br />\r\n<br />\r\nVương Thịnh Quốc c&oacute; ch&uacute;t kinh ngạc, cảm thấy hắn h&ocirc;m nay c&oacute; ch&uacute;t kh&ocirc;ng giống, nhưng l&agrave; cũng kh&ocirc;ng nghĩ nhiều.<br />\r\n<br />\r\nHắn ăn cơm xong, vừa vội vội v&agrave;ng đi ra cửa.<br />\r\n<br />\r\nL&agrave;m một nh&agrave; c&ocirc;ng ty lớn người cầm l&aacute;i, hắn một ng&agrave;y sự t&igrave;nh nhưng thật ra l&agrave; rất nhiều, hắn mỗi ng&agrave;y cơ hồ đều chỉ ngủ bốn, năm tiếng.<br />\r\n<br />\r\nVương Đằng c&ugrave;ng L&yacute; T&uacute; Mai l&ecirc;n tiếng ch&agrave;o hỏi, cũng ra cửa, sau đ&oacute; thẳng đến 【 Cực Tinh v&otilde; qu&aacute;n 】.<br />\r\n<br />\r\nMột cỗ m&agrave;u v&agrave;ng xe thể thao tại ban đ&ecirc;m tr&ecirc;n đường phố đi nhanh, tiếng nổ l&agrave;m người kh&aacute;c ch&uacute; &yacute;, hiện tại xe đều &aacute;p dụng Nguy&ecirc;n Lực l&agrave;m nguồn năng lượng, đ&atilde; sớm đ&agrave;o thải xăng, c&agrave;ng th&ecirc;m ho&agrave;n bảo c&ugrave;ng tiện nghi.<br />\r\n<br />\r\nNhững Người Mở Đường chẳng những từ Tinh V&otilde; đại lục mang về phương ph&aacute;p tu luyện, c&agrave;ng mang về đủ loại Nguy&ecirc;n Lực văn minh sản phẩm.<br />\r\n<br />\r\nNguy&ecirc;n Lực ph&ugrave; văn ch&iacute;nh l&agrave; trong đ&oacute; một loại.<br />\r\n<br />\r\nĐịa Cầu khoa học kỹ thuật kết hợp Nguy&ecirc;n Lực văn minh, sinh ra Nguy&ecirc;n Lực khoa học kỹ thuật, &ocirc; t&ocirc; sử dụng Nguy&ecirc;n Lực, m&aacute;y t&iacute;nh c&ugrave;ng điện thoại cũng sử dụng mới nhất nghi&ecirc;n cứu Nguy&ecirc;n Lực ph&ugrave; văn Chip c&ugrave;ng pin, vận h&agrave;nh c&agrave;ng nhanh, thời gian sử dụng c&agrave;ng l&acirc;u...<br />\r\n<br />\r\nNửa giờ sau, Vương Đằng l&aacute;i xe dừng ở v&otilde; qu&aacute;n trước cửa.<br />\r\n<br />\r\nDựa theo hắn t&igrave;m tới tư liệu biểu hiện, Cực Tinh v&otilde; qu&aacute;n l&agrave; Hoa Hạ khu vực v&otilde; qu&aacute;n b&ecirc;n trong một trong tam cự đầu, v&otilde; qu&aacute;n qu&aacute;n chủ l&agrave; 12 tinh Chiến Tướng cấp V&otilde; Giả, chiến lực c&oacute; thể xếp v&agrave;o Hoa Hạ trước năm.<br />\r\n<br />\r\nCho d&ugrave; tại to&agrave;n cầu phạm vi, cũng l&agrave; trước hai mươi cường giả.<br />\r\n<br />\r\nCh&iacute;nh thức V&otilde; Giả, 1 đến 9 tinh l&agrave; Chiến Binh cấp, 10 tinh l&agrave;m chuẩn cấp Chiến Tướng, 11 tinh hạ vị cấp Chiến Tướng, 12 trung vị cấp Chiến Tướng, 13 tinh thượng vị cấp Chiến Tướng...<br />\r\n<br />\r\nCực Tinh v&otilde; qu&aacute;n qu&aacute;n chủ thực lực c&oacute; thể thấy được ch&uacute;t &iacute;t!<br />\r\n<br />\r\nCực Tinh v&otilde; qu&aacute;n tổng bộ tại Hạ đ&ocirc;, bất qu&aacute; tại Hoa Hạ c&aacute;c th&agrave;nh phố lớn đều c&oacute; ph&acirc;n qu&aacute;n, V&otilde; Giả th&agrave;nh vi&ecirc;n đ&ocirc;ng đảo, cường giả như m&acirc;y.<br />\r\n<br />\r\nCực Tinh v&otilde; qu&aacute;n Đ&ocirc;ng Hải ph&acirc;n qu&aacute;n ở v&agrave;o bờ biển, trong đ&ecirc;m tối tựa như một đầu cự th&uacute; phủ phục ở nơi đ&oacute;, chiếm diện t&iacute;ch c&oacute; thể so với một t&ograve;a trường trung học.<br />\r\n<br />\r\nV&otilde; qu&aacute;n cửa ch&iacute;nh đủ để cho năm sáu chiếc xe song h&agrave;nh th&ocirc;ng qua, rất l&agrave; đại kh&iacute;.<br />\r\n<br />\r\nCửa ch&iacute;nh ở giữa đứng thẳng một tảng đ&aacute; lớn, ph&iacute;a tr&ecirc;n tảng đ&aacute; Cực Tinh v&otilde; qu&aacute;n Đ&ocirc;ng Hải ph&acirc;n qu&aacute;n mấy c&aacute;i rồng bay phượng m&uacute;a thiếp v&agrave;ng chữ lớn cực k&igrave; bắt mắt.<br />\r\n<br />\r\nTrạm g&aacute;c chỗ, mấy c&aacute;i cầm s&uacute;ng nh&acirc;n vi&ecirc;n bảo vệ đi đến b&ecirc;n cạnh xe.<br />\r\n<br />\r\n&quot;Th&ocirc;ng lệ kiểm tra, xin ngươi phối hợp một ch&uacute;t!&quot;<br />\r\n<br />\r\nVương Đằng hạ xuống cửa sổ xe, nhẹ gật đầu, sau đ&oacute; đi xuống xe.<br />\r\n<br />\r\nNh&acirc;n vi&ecirc;n bảo vệ kiểm tra xe thể thao b&ecirc;n trong, d&ugrave;ng m&aacute;y qu&eacute;t qu&eacute;t h&igrave;nh Vương Đằng to&agrave;n th&acirc;n, kiểm tra phi thường cẩn thận.<br />\r\n<br />\r\nC&ugrave;ng l&uacute;c đ&oacute;, Vương Đằng cũng đang đ&aacute;nh gi&aacute; những n&agrave;y nh&acirc;n vi&ecirc;n bảo vệ.<br />\r\n<br />\r\nBọn hắn mặc m&agrave;u đen ph&ograve;ng hộ sau lưng, s&uacute;ng trong tay r&otilde; r&agrave;ng l&agrave; đại đường k&iacute;nh s&uacute;ng m&aacute;y, lộ ra rất dữ tợn.<br />\r\n<br />\r\nTh&acirc;n s&uacute;ng b&ecirc;n tr&ecirc;n khắc r&otilde; Nguy&ecirc;n Lực ph&ugrave; văn, n&oacute;i r&otilde; đ&acirc;y l&agrave; s&uacute;ng Nguy&ecirc;n Lực giới, uy lực so với phổ th&ocirc;ng s&uacute;ng ống mạnh hơn gấp mấy lần, d&ugrave; cho ch&iacute;nh thức V&otilde; Giả ch&iacute;nh diện tr&uacute;ng v&agrave;o một s&uacute;ng, cũng phải t&agrave;n phế.<br />\r\n<br />\r\nKiểm tra xong, bảo vệ cho qua.<br />\r\n<br />\r\nVương Đằng đem xe ngừng tốt, đi tại tr&ecirc;n đường trong nội viện v&otilde; qu&aacute;n.<br />\r\n<br />\r\nD&ugrave; l&agrave; đ&atilde; hơn t&aacute;m giờ, b&ecirc;n trong v&otilde; qu&aacute;n vẫn l&agrave; s&aacute;ng như ban ng&agrave;y, tr&ecirc;n đường, tr&ecirc;n b&atilde;i cỏ, c&oacute; lượng lớn v&otilde; qu&aacute;n học vi&ecirc;n.<br />\r\n<br />\r\nBan đ&ecirc;m huấn luyện người cũng rất nhiều.<br />\r\n<br />\r\nV&otilde; Giả huấn luyện tranh thủ thời gian, c&oacute; thi&ecirc;n ph&uacute; chỉ l&agrave; điều kiện cơ bản nhất, c&ograve;n cần kh&ocirc;ng ngừng cố gắng, huy s&aacute;i mồ h&ocirc;i, mới c&oacute; thể c&oacute; tạo th&agrave;nh.<br />\r\n<br />\r\nB&ecirc;n trong v&otilde; qu&aacute;n, c&oacute; ba t&ograve;a nh&agrave; kiến tr&uacute;c cao lớn.<br />\r\n<br />\r\nBọn ch&uacute;ng chỉnh thể hiện l&ecirc;n m&agrave;u trắng bạc, đặc sắc tươi s&aacute;ng, rất c&oacute; nhận ra t&iacute;nh.<br />\r\n<br />\r\nỞ giữa c&aacute;i kia t&ograve;a nh&agrave; l&agrave; h&agrave;nh ch&iacute;nh cao ốc, hiện l&ecirc;n h&igrave;nh chữ nhật, chừng trăm m&eacute;t cao, xuy&ecirc;n thẳng v&acirc;n ti&ecirc;u, nhưng l&agrave; đỉnh cao nhất lại l&agrave; c&aacute;i quỷ dị nửa v&ograve;ng tr&ograve;n.<br />\r\n<br />\r\nB&ecirc;n tr&aacute;i l&agrave; V&otilde; Đồ ph&ograve;ng học lớn, b&ecirc;n phải th&igrave; l&agrave; V&otilde; Giả huấn luyện cao ốc.<br />\r\n<br />\r\nC&aacute;i n&agrave;y hai t&ograve;a nh&agrave; cao ốc phảng phất hai vi&ecirc;n to lớn vi&ecirc;n cầu, kh&ocirc;ng cao, chỉ c&oacute; ba tầng, nhưng l&agrave; b&ecirc;n trong rất rộng r&atilde;i, nh&igrave;n ra tối thiểu l&agrave; Đ&ocirc;ng Hải nhất trung thao trường hai lần.<br />\r\n<br />\r\nBa t&ograve;a nh&agrave; kiến tr&uacute;c đặt chung một chỗ, Vương Đằng c&oacute; ch&uacute;t ho&agrave;i nghi đ&acirc;y c&oacute; phải hay kh&ocirc;ng l&agrave; v&igrave; biểu hiện qu&aacute;n chủ &aacute;c th&uacute; vị.<br />\r\n<br />\r\nVương Đằng trước khi đến đ&atilde; l&agrave;m qua hi&ecirc;̉u rõ, cho n&ecirc;n thẳng đến h&agrave;nh ch&iacute;nh cao ốc.<br />\r\n<br />\r\nTại nh&acirc;n vi&ecirc;n c&ocirc;ng t&aacute;c chỉ dẫn dưới, b&aacute;o danh trở th&agrave;nh v&otilde; qu&aacute;n học vi&ecirc;n, sau đ&oacute; tiến về b&ecirc;n tr&aacute;i V&otilde; Đồ lầu dạy học.<br />\r\n<br />\r\nV&otilde; qu&aacute;n sẽ chỉ tuyển nhận học vi&ecirc;n ở giữa mười s&aacute;u tuổi tr&ograve;n đến ba mươi tuổi tr&ograve;n, để tr&aacute;nh l&atilde;ng ph&iacute; t&agrave;i nguy&ecirc;n.<br />\r\n<br />\r\nVương Đằng mới mười bảy tuổi tr&ograve;n, kh&ocirc;ng c&oacute; bất cứ vấn đề g&igrave;.<br />\r\n<br />\r\nĐi v&agrave;o V&otilde; Đồ lầu dạy học, trong đại sảnh tiếng ồn &agrave;o đập v&agrave;o mặt.<br />\r\n<br />\r\nLầu một l&agrave; sơ cấp học vi&ecirc;n huấn luyện địa phương, sơ cấp học vi&ecirc;n đối ứng ch&iacute;nh l&agrave; sơ cấp V&otilde; Đồ, Cực Tinh v&otilde; qu&aacute;n c&oacute; hơn ba ng&agrave;n t&ecirc;n sơ cấp V&otilde; Đồ.<br />\r\n<br />\r\nLầu một huấn luyện trong sảnh, c&aacute;c học vi&ecirc;n ri&ecirc;ng ph&acirc;̀n mình tu luyện, lộ ra phi thường n&aacute;o nhiệt.<br />\r\n<br />\r\nVương Đằng vừa mới đi v&agrave;o đại sảnh, lập tức sững sờ tại nguy&ecirc;n chỗ.<br />\r\n<br />\r\nHắn nh&igrave;n thấy c&aacute;i g&igrave;?<br />\r\n<br />\r\nMột chỗ trong suốt bọt kh&iacute;, phi&ecirc;u ph&ugrave; ở giữa kh&ocirc;ng trung, chừng bốn năm mươi c&aacute;i nhiều.<br />\r\n<br />\r\n【 lực lượng *1 】<br />\r\n<br />\r\n【 tốc độ *3 】<br />\r\n<br />\r\n【 thể chất *1 】<br />\r\n<br />\r\n【 ngộ t&iacute;nh * 0.5 】<br />\r\n<br />\r\n【 lực lượng *4 】<br />\r\n<br />\r\n...<br />\r\n<br />\r\nVương Đằng d&ugrave;ng sức nh&aacute;y nh&aacute;y mắt, những c&aacute;i kia bọt kh&iacute; y nguy&ecirc;n tồn tại, huấn luyện trong sảnh học vi&ecirc;n giống như đều nh&igrave;n kh&ocirc;ng thấy đồng dạng.<br />\r\n<br />\r\nB&agrave;n tay v&agrave;ng?!<br />\r\n<br />\r\nHắn c&oacute; ch&uacute;t kh&ocirc;ng x&aacute;c định, giữ chặt một c&aacute;i đi ngang qua học vi&ecirc;n.<br />\r\n<br />\r\n&quot;L&agrave;m g&igrave;!&quot;<br />\r\n<br />\r\nĐối phương cũng mới mười s&aacute;u mười bảy tuổi, kh&ocirc;ng ki&ecirc;n nhẫn nh&igrave;n xem Vương Đằng, một mặt &quot;Ngươi thế m&agrave; so với ta so&aacute;i&quot; kh&oacute; chịu biểu lộ.<br />\r\n<br />\r\n&quot;Huynh đệ, c&aacute;c ngươi đất n&agrave;y tấm... Trải kh&ocirc;ng tệ a!&quot; Vương Đằng chỉ v&agrave;o mặt đất nói.<br />\r\n<br />\r\nNhững c&aacute;i kia trong suốt bong b&oacute;ng phi&ecirc;u ph&ugrave; ở người bắp ch&acirc;n độ cao, c&uacute;i đầu xuống liền c&oacute; thể nh&igrave;n thấy.<br />\r\n<br />\r\nThế nhưng l&agrave; t&ecirc;n học vi&ecirc;n n&agrave;y ho&agrave;n to&agrave;n coi nhẹ, gh&eacute;t bỏ n&oacute;i.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; tốt nhất nhựa plastic s&agrave;n nh&agrave;, khắc họa Nguy&ecirc;n Lực ph&ugrave; văn, c&oacute; giảm x&oacute;c, bền bỉ, kh&aacute;ng đả k&iacute;ch c&aacute;c loại c&ocirc;ng năng.&quot;<br />\r\n<br />\r\n&quot;Nơi n&agrave;o đến, một điểm kiến thức đều kh&ocirc;ng c&oacute;!&quot;<br />\r\n<br />\r\nN&oacute;i xong, hơi vung tay, cũng kh&ocirc;ng quay đầu lại đi.<br />\r\n<br />\r\nVương Đằng sờ l&ecirc;n cằm, nh&igrave;n đối phương phản ứng, hiển nhi&ecirc;n l&agrave; kh&ocirc;ng nh&igrave;n thấy, cho n&ecirc;n thật chỉ c&oacute; ch&iacute;nh m&igrave;nh có th&ecirc;̉ nh&igrave;n thấy.<br />\r\n<br />\r\nNhững học vi&ecirc;n kia đang huấn luyện trong sảnh đi tới đi lui, c&oacute; trực tiếp từ bọt kh&iacute; b&ecirc;n trong xuy&ecirc;n qua, đối bọn hắn đến n&oacute;i những c&aacute;i kia bọt kh&iacute; tựa như hư ảo.<br />\r\n<br />\r\nVương Đằng đi l&ecirc;n trước, x&iacute;ch lại gần c&aacute;ch m&igrave;nh gần nhất một c&aacute;i bọt kh&iacute;, thử một ch&uacute;t m&igrave;nh c&oacute; thể hay kh&ocirc;ng xuy&ecirc;n qua.<br />\r\n<br />\r\nKết quả vừa mới đụng v&agrave;o, c&aacute;i n&agrave;y bọt kh&iacute; trực tiếp biến mất.<br />\r\n<br />\r\nTrước mắt của hắn đột nhi&ecirc;n một hoa, một c&aacute;i trong suốt bảng nhảy ra ngo&agrave;i<br />\r\n<br />\r\n【 ngộ t&iacute;nh 】: 18<br />\r\n<br />\r\n【 thể chất 】: 5<br />\r\n<br />\r\n【 lực lượng 】: 5(+1)<br />\r\n<br />\r\n【 tốc độ 】: 7<br />\r\n<br />\r\n...<br />\r\n<br />\r\nNh&igrave;n chằm chằm trong suốt bảng, Vương Đằng tr&aacute;i tim nhảy c&agrave;ng l&uacute;c c&agrave;ng nhanh, giống như l&agrave; sắp từ trong cổ họng đụng tới.<br />\r\n<br />\r\nQuá kích thích!<br />\r\n<br />\r\nVương Đằng cảm thấy m&igrave;nh coi như b&ecirc;n trong một trăm triệu xổ số, khả năng cũng sẽ kh&ocirc;ng k&iacute;ch động như vậy.<br />\r\n<br />\r\nNhưng l&agrave; hiện tại hắn nhịn kh&ocirc;ng được!<br />\r\n<br />\r\nC&aacute;i n&agrave;y đặc meo tuyệt đối l&agrave; b&agrave;n tay v&agrave;ng a!<br />\r\n<br />\r\nTại b&ecirc;n trong c&aacute;i thời đại V&otilde; Giả n&agrave;y l&agrave; chủ lưu, mạnh l&ecirc;n mới l&agrave; đường ra duy nhất.<br />\r\n<br />\r\nC&aacute;i n&agrave;y b&agrave;n tay v&agrave;ng c&oacute; thể nhặt thuộc t&iacute;nh, sau đ&oacute; biến th&agrave;nh thuộc t&iacute;nh của m&igrave;nh, c&aacute;i n&agrave;y t&aacute;c dụng qu&aacute; nghịch thi&ecirc;n!<br />\r\n<br />\r\nNgười kh&aacute;c muốn vất vả tu luyện, m&agrave; Vương Đằng chỉ cần nhặt thuộc t&iacute;nh, hắn tựa hồ nh&igrave;n thấy một đầu kh&ocirc;ng trở ngại thi&ecirc;n đồ tại trước mặt rộng mở.<br />\r\n<br />\r\nCuối c&ugrave;ng sẽ c&oacute; một ng&agrave;y, hắn Vương Đằng sẽ l&ecirc;n như diều gặp gi&oacute; ch&iacute;n vạn dặm!</p>', '2021-11-28 13:31:14', 3, 1),
(212, 58, 'Đánh chết không luyện 【 Toàn Quốc Thứ Chín Bộ Phát Thanh Luyện Thể Thao 】', '4', '<p>Vương Đằng đến Cực Tinh v&otilde; qu&aacute;n, vốn l&agrave; v&igrave; tu luyện.<br />\r\n<br />\r\nB&acirc;y giờ lại vội v&agrave;ng nhặt thuộc t&iacute;nh.<br />\r\n<br />\r\nHắn cảm thấy m&igrave;nh tựa như một c&aacute;i người nhặt ve chai, vất vả cần c&ugrave; nhặt người kh&aacute;c rơi xuống đồ vật.<br />\r\n<br />\r\nTrước đ&oacute; hắn quan s&aacute;t một ch&uacute;t, tr&ecirc;n đất bọt kh&iacute; trong suốt đều l&agrave; v&otilde; qu&aacute;n học vi&ecirc;n tại huấn luyện qu&aacute; tr&igrave;nh b&ecirc;n trong đến rơi xuống.<br />\r\n<br />\r\nB&ecirc;n trong v&otilde; qu&aacute;n huấn luyện học vi&ecirc;n c&oacute; rất nhiều, cho n&ecirc;n rơi xuống thuộc t&iacute;nh bọt kh&iacute; cũng rất nhiều.<br />\r\n<br />\r\nBất qu&aacute; c&oacute; một ch&uacute;t để Vương Đằng rất đau l&ograve;ng.<br />\r\n<br />\r\nBọt kh&iacute; tồn tại c&oacute; thời gian hạn chế, qua mấy ph&uacute;t liền sẽ biến mất.<br />\r\n<br />\r\nVừa mới hắn liền sững sờ trong chốc l&aacute;t, mấy c&aacute;i bọt kh&iacute; biến mất kh&ocirc;ng thấy g&igrave; nữa, nhưng l&agrave;m t&acirc;m hắn đau, cảm gi&aacute;c tổn thất mấy ức.<br />\r\n<br />\r\nC&aacute;c học vi&ecirc;n kh&ocirc;ng ngừng huấn luyện, khi th&igrave; rơi xuống mấy c&aacute;i thuộc t&iacute;nh bọt kh&iacute;.<br />\r\n<br />\r\nVương Đằng nhặt qu&ecirc;n cả trời đất, hắn thuộc t&iacute;nh &quot;Soạt soạt soạt&quot; kh&ocirc;ng cần tiền giống như d&acirc;ng đi l&ecirc;n.<br />\r\n<br />\r\nMột V&otilde; Đồ đạo sư gặp hắn tại huấn luyện trong qu&aacute;n như c&aacute;i con ruồi kh&ocirc;ng đầu chạy tới chạy lui, thực nh&igrave;n kh&ocirc;ng được, đem hắn gọi lại.<br />\r\n<br />\r\n&quot;&Agrave;i &agrave;i, đồng học kia, ngươi đến v&otilde; qu&aacute;n kh&ocirc;ng hảo hảo huấn luyện, chạy lung tung c&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\n&quot;L&atilde;o sư, ta h&ocirc;m nay vừa tới, nh&igrave;n xem tất cả mọi người huấn luyện như thế n&agrave;o, cổ nh&acirc;n n&oacute;i ba người đi tất c&oacute; thầy ta, ta trước từ mọi người tr&ecirc;n th&acirc;n học tập cho giỏi.&quot;<br />\r\n<br />\r\nVương Đằng nh&igrave;n xem t&ecirc;n kia V&otilde; Đồ đạo sư, cười h&igrave; h&igrave; t&ugrave;y tiện n&oacute;i m&ograve;.<br />\r\n<br />\r\n&quot;Vừa tới? Vậy ngươi tới, ta trước ti&ecirc;n đem 【 To&agrave;n Quốc Thứ Ch&iacute;n Bộ Ph&aacute;t Thanh Luyện Thể Thao 】 dạy cho ngươi, hiện tại ngươi c&aacute;i g&igrave; cũng kh&ocirc;ng biết, nh&igrave;n cũng kh&ocirc;ng thấy g&igrave;, đem cơ sở đồ vật học tốt lại n&oacute;i.&quot; T&ecirc;n đạo sư kia n&oacute;i.<br />\r\n<br />\r\nVương Đằng ngẫm lại, cảm thấy hắn n&oacute;i c&oacute; đạo l&yacute;, người đạo sư n&agrave;y vẫn l&agrave; rất c&oacute; tr&aacute;ch nhiệm.<br />\r\n<br />\r\nKh&ocirc;ng th&ocirc;i nh&igrave;n tho&aacute;ng qua huấn luyện trong sảnh vừa rơi xuống mấy c&aacute;i thuộc t&iacute;nh bọt kh&iacute;, phảng phất c&oacute; bao nhi&ecirc;u ức c&aacute;ch m&igrave;nh m&agrave; đi.<br />\r\n<br />\r\n&quot;L&atilde;o sư, 【 To&agrave;n Quốc Thứ Ch&iacute;n Bộ Ph&aacute;t Thanh Luyện Thể Thao 】 l&agrave; c&aacute;i g&igrave; thao?&quot; Vương Đằng đi qua, t&ograve; m&ograve; hỏi.<br />\r\n<br />\r\n&quot;【 To&agrave;n Quốc Thứ Ch&iacute;n Bộ Ph&aacute;t Thanh Luyện Thể Thao 】 l&agrave; quốc gia kết hợp cơ sở r&egrave;n thể ph&aacute;p của Tinh V&otilde; đại lục khai ph&aacute;t ra khoa học luyện thể chi ph&aacute;p.&quot;<br />\r\n<br />\r\nT&ecirc;n đạo sư kia n&oacute;i, mở ra &acirc;m hưởng.<br />\r\n<br />\r\n&quot;To&agrave;n Quốc Thứ Ch&iacute;n Bộ Ph&aacute;t Thanh Luyện Thể Thao &mdash;&mdash; V&otilde; động thanh xu&acirc;n, hiện tại bắt đầu!&quot;<br />\r\n<br />\r\nVương Đằng to&agrave;n th&acirc;n dừng lại!<br />\r\n<br />\r\n&Acirc;m nhạc vang l&ecirc;n, l&agrave; c&aacute;i kia m&ugrave;i vị quen thuộc, c&ugrave;ng c&aacute;i kia quen thuộc chuẩn bị động t&aacute;c.<br />\r\n<br />\r\n&quot;Nh&igrave;n cho thật kỹ!&quot;<br />\r\n<br />\r\nCao lớn to con đạo sư tối thiểu một m&eacute;t t&aacute;m trở l&ecirc;n, bắp thịt to&agrave;n th&acirc;n v&ocirc; c&ugrave;ng kinh khủng, l&uacute;c n&agrave;y thần sắc nghi&ecirc;m t&uacute;c, ch&iacute;nh l&agrave;m lấy chuẩn bị động t&aacute;c.<br />\r\n<br />\r\nVương Đằng mộng!<br />\r\n<br />\r\nĐạo sư th&igrave; l&agrave; đ&atilde; theo &acirc;m nhạc, l&agrave;m l&ecirc;n ph&aacute;t thanh luyện thể thao.<br />\r\n<br />\r\n&quot;Thức thứ nhất, mở rộng vận động, một hai ba bốn, hai hai ba bốn...&quot;<br />\r\n<br />\r\n&quot;Thức thứ hai, khuếch trương ngực vận động, một hai ba bốn, hai hai ba bốn...&quot;<br />\r\n<br />\r\n&quot;Thức thứ ba, đ&aacute; ch&acirc;n vận động, một hai ba bốn, hai hai ba bốn...&quot;<br />\r\n<br />\r\nVương Đằng ngơ ng&aacute;c nh&igrave;n đối phương, trong đầu v&ocirc; số con mẹ n&oacute; đang đi&ecirc;n cuồng lao nhanh, cản đều ngăn kh&ocirc;ng được.<br />\r\n<br />\r\nThần đặc meo ph&aacute;t thanh luyện thể đệt!<br />\r\n<br />\r\nMặc d&ugrave; hắn cũng ph&aacute;t hiện, cứ việc t&ecirc;n vẫn l&agrave; những c&aacute;i kia t&ecirc;n, nhưng l&agrave; động t&aacute;c muốn nhiều phức tạp, một &iacute;t độ kh&oacute; cao động t&aacute;c đối với th&acirc;n thể y&ecirc;u cầu phi thường cao.<br />\r\n<br />\r\nGiống như... Quả thật c&oacute; thể đưa đến luyện thể t&aacute;c dụng?<br />\r\n<br />\r\nThế nhưng l&agrave;, c&aacute;i n&agrave;y &acirc;m nhạc, c&aacute;i n&agrave;y tiết tấu, cảm gi&aacute;c quen thuộc n&agrave;y, thật thật xấu hổ a!<br />\r\n<br />\r\n&quot;Thế n&agrave;o, đều thấy r&otilde; r&agrave;ng chưa? Kh&ocirc;ng thấy r&otilde; r&agrave;ng, ta cho ngươi th&ecirc;m diễn luyện một lần!&quot;<br />\r\n<br />\r\n&Acirc;m nhạc ngừng lại, t&ecirc;n đạo sư kia cũng chậm r&atilde;i dừng động t&aacute;c, thở d&agrave;i ra một hơi, tr&ecirc;n người hắn tr&ecirc;n tr&aacute;n to&aacute;t ra rất nhiều mồ h&ocirc;i n&oacute;ng, 【 To&agrave;n Quốc Thứ Ch&iacute;n Bộ Ph&aacute;t Thanh Luyện Thể Thao 】 hiệu quả kinh người.<br />\r\n<br />\r\n&quot;L&atilde;o sư, ta thấy r&otilde; r&agrave;ng!&quot; Vương Đằng liền vội v&agrave;ng gật đầu, sợ hắn thật lại đến một lần.<br />\r\n<br />\r\nNh&igrave;n hắn vẫn chưa thỏa m&atilde;n d&aacute;ng vẻ, rất c&oacute; thể n&oacute;i được th&igrave; l&agrave;m được, kh&ocirc;ng ra tr&ograve; đ&ugrave;a.<br />\r\n<br />\r\n&quot;A, xem ra tr&iacute; nhớ của ngươi kh&ocirc;ng tệ, l&uacute;c trước ta nh&igrave;n ba lần mới nhớ kỹ.&quot; Đạo sư c&oacute; ch&uacute;t thất vọng n&oacute;i.<br />\r\n<br />\r\nMồ h&ocirc;i, ngươi c&aacute;i n&agrave;y một bộ thất vọng bộ d&aacute;ng l&agrave; chuyện g&igrave; xảy ra a!<br />\r\n<br />\r\nL&agrave;m luyện thể thao c&ograve;n nghiện ngươi!<br />\r\n<br />\r\nVương Đằng giống đưa &ocirc;n thần đồng dạng đem đạo sư khuy&ecirc;n đi, để hắn đi tai họa học vi&ecirc;n kh&aacute;c, bọn hắn đều rất t&igrave;nh nguyện.<br />\r\n<br />\r\nC&aacute;i g&igrave; 【 To&agrave;n Quốc Thứ Ch&iacute;n Bộ Ph&aacute;t Thanh Luyện Thể Thao 】, Vương Đằng ta l&agrave; tuyệt đối sẽ kh&ocirc;ng luyện, đ&aacute;nh chết cũng sẽ kh&ocirc;ng đi luyện!<br />\r\n<br />\r\nĐời n&agrave;y chỉ c&oacute; thể dựa v&agrave;o nhặt thuộc t&iacute;nh mạnh l&ecirc;n!<br />\r\n<br />\r\nThừa dịp vị đạo sư kia kh&ocirc;ng ch&uacute; &yacute;, Vương Đằng tiếp tục hắn nhặt thuộc t&iacute;nh đại nghiệp.<br />\r\n<br />\r\n【 lực lượng *2 】<br />\r\n<br />\r\n【 tốc độ *1 】<br />\r\n<br />\r\n【 ngộ t&iacute;nh *0. 2 】<br />\r\n<br />\r\n...<br />\r\n<br />\r\n【 cơ sở quyền ph&aacute;p *5 】<br />\r\n<br />\r\n...<br />\r\n<br />\r\nVương Đằng rất vui vẻ, nhặt rất nhiều lực lượng, tốc độ thuộc t&iacute;nh, chưa từng thử qua, cũng kh&ocirc;ng biết lực lượng đạt tới tr&igrave;nh độ g&igrave;, nhưng l&agrave; hắn tại trong sảnh huấn luyện tản bộ l&uacute;c, r&otilde; r&agrave;ng cảm gi&aacute;c tốc độ nhanh hơn rất nhiều, kh&ocirc;ng cẩn thận liền nhảy l&ecirc;n ra ngo&agrave;i xa mấy m&eacute;t.<br />\r\n<br />\r\n【 ngộ t&iacute;nh 】 thuộc t&iacute;nh hơi &iacute;t, m&agrave; lại đều l&agrave; kh&ocirc;ng phẩy mấy, mấy c&aacute;i cộng lại mới c&oacute; thể g&oacute;p đủ một điểm.<br />\r\n<br />\r\n&quot;【 ngộ t&iacute;nh 】 thuộc t&iacute;nh gia tăng, c&oacute; phải l&agrave; liền biến th&ocirc;ng minh, cũng kh&ocirc;ng c&oacute; cảm gi&aacute;c g&igrave; a?&quot; Vương Đằng nghĩ như vậy.<br />\r\n<br />\r\nChờ ch&uacute;t!<br />\r\n<br />\r\n【 cơ sở quyền ph&aacute;p *5 】?!<br />\r\n<br />\r\nMột c&aacute;i c&ugrave;ng l&uacute;c trước đều kh&ocirc;ng giống nhau bọt kh&iacute; bị Vương Đằng hấp thu, trong đầu của hắn đột nhi&ecirc;n xuất hiện rất nhiều luyện quyền k&yacute; ức.<br />\r\n<br />\r\n&quot;Cơ sở quyền ph&aacute;p, ta c&aacute;i n&agrave;y biết rồi?&quot; Vương Đằng c&oacute; ch&uacute;t kh&oacute; tin.<br />\r\n<br />\r\nV&otilde; Đồ giai đoạn, kh&ocirc;ng c&oacute; Nguy&ecirc;n Lực, kh&ocirc;ng c&aacute;ch n&agrave;o tu luyện Nguy&ecirc;n Lực chiến kỹ.<br />\r\n<br />\r\nV&igrave; vậy quốc gia quy nạp tổng kết rất nhiều cơ sở chiến kỹ, trong đ&oacute; bao h&agrave;m th&acirc;n ph&aacute;p, quyền ph&aacute;p, kiếm ph&aacute;p, đao ph&aacute;p v&acirc;n v&acirc;n.<br />\r\n<br />\r\nSau đ&oacute; ph&aacute;t triển ra đến, để to&agrave;n d&acirc;n đều c&oacute; thể tu luyện.<br />\r\n<br />\r\nVương Đằng nh&igrave;n xem trước mặt c&aacute;ch đ&oacute; kh&ocirc;ng xa đang tu luyện cơ sở quyền ph&aacute;p hai mươi tuổi thanh ni&ecirc;n, hai mắt tỏa &aacute;nh s&aacute;ng.<br />\r\n<br />\r\nHắn mặc d&ugrave; học xong cơ sở quyền ph&aacute;p, nhưng l&agrave; r&otilde; r&agrave;ng c&ograve;n l&agrave;m kh&ocirc;ng được tinh th&ocirc;ng, chỉ l&agrave; biết m&agrave; th&ocirc;i, phi thường th&ocirc; r&aacute;p, tương đương với người b&igrave;nh thường luyện bốn năm ng&agrave;y tr&igrave;nh độ.<br />\r\n<br />\r\nBất qu&aacute;, nhiều nhặt một &iacute;t 【 cơ sở quyền ph&aacute;p 】 thuộc t&iacute;nh, hẳn l&agrave; c&oacute; thể tăng l&ecirc;n độ thuần thục a?<br />\r\n<br />\r\nB&agrave;nh b&agrave;nh b&agrave;nh!<br />\r\n<br />\r\nT&ecirc;n thanh ni&ecirc;n kia đại lực đ&aacute;nh tại tr&ecirc;n mặt bao c&aacute;t, bao c&aacute;t t&ugrave;y theo tr&aacute;i phải lay động, mỗi một quyền đều để bao c&aacute;t xuất hiện c&aacute;i hố nhỏ.<br />\r\n<br />\r\n&quot;Mạnh!&quot;<br />\r\n<br />\r\n&quot;Quả nhi&ecirc;n V&otilde; Giả mới l&agrave; đường ra.&quot;<br />\r\n<br />\r\n&quot;L&agrave;m sao c&ograve;n kh&ocirc;ng xong... A, rơi rơi, lại rơi!&quot;<br />\r\n<br />\r\nVương Đằng c&oacute; ch&uacute;t hưng phấn, vụng trộm từ ph&iacute;a sau lưng sờ l&ecirc;n, lơ đ&atilde;ng đem thuộc t&iacute;nh nhặt, sau đ&oacute; chạy đi.<br />\r\n<br />\r\n【 cơ sở quyền ph&aacute;p *7 】<br />\r\n<br />\r\n【 lực lượng *5 】<br />\r\n<br />\r\n&quot;Thanh ni&ecirc;n n&agrave;y, c&oacute; liệu a! Rất c&oacute; liệu!&quot; Vương Đằng cảm th&aacute;n n&oacute;i.<br />\r\n<br />\r\nTrụ cột quyền ph&aacute;p của hắn quả nhi&ecirc;n thuần thục hơn!<br />\r\n<br />\r\nLại chờ trong chốc l&aacute;t, thấy đối phương kh&ocirc;ng tiếp tục rơi xuống thuộc t&iacute;nh, Vương Đằng kh&ocirc;ng thể kh&ocirc;ng chuyển hướng học vi&ecirc;n kh&aacute;c, d&ugrave; sao cũng kh&ocirc;ng thể được c&aacute;i n&agrave;y mất c&aacute;i kh&aacute;c.<br />\r\n<br />\r\nChỉ c&oacute; đứa trẻ, mới l&agrave;m lựa chọn.<br />\r\n<br />\r\nĐại nh&acirc;n, tất cả đều muốn!<br />\r\n<br />\r\nVương Đằng ta l&agrave; c&aacute;i đại nh&acirc;n!<br />\r\n<br />\r\n...<br />\r\n<br />\r\n【 cơ sở th&acirc;n ph&aacute;p *2 】<br />\r\n<br />\r\nB&ecirc;n ngo&agrave;i ph&ograve;ng huấn luyện v&acirc;y l&agrave; một v&ograve;ng đường chạy, như l&agrave; thao trường.<br />\r\n<br />\r\nMột mập mạp từ tr&ecirc;n đường chạy chạy m&agrave; qua, giống một trận gi&oacute;, thổi l&ecirc;n Vương Đằng t&oacute;c.<br />\r\n<br />\r\nPh&iacute;a sau c&aacute;i m&ocirc;ng rơi xuống một c&aacute;i bọt kh&iacute;.<br />\r\n<br />\r\nX&aacute;c nhận qua h&igrave;nh thể, l&agrave; c&aacute;i linh hoạt mập mạp!<br />\r\n<br />\r\n&quot;Ừm? Cơ sở th&acirc;n ph&aacute;p!!&quot;<br />\r\n<br />\r\nVương Đằng nhặt l&ecirc;n thuộc t&iacute;nh, bỗng nhi&ecirc;n l&agrave; giật m&igrave;nh, lại l&agrave; một c&aacute;i cơ sở chiến kỹ, vẫn l&agrave; th&acirc;n ph&aacute;p, vận kh&iacute; n&agrave;y c&oacute; chút &Acirc;u a.<br />\r\n<br />\r\nC&aacute;i t&ecirc;n mập mạp kia chăm chỉ kh&ocirc;ng ngừng chạy nhanh, ph&iacute;a sau c&aacute;i m&ocirc;ng phảng phất g&agrave; m&aacute;i đẻ trứng, từng c&aacute;i bọt kh&iacute; rớt xuống.<br />\r\n<br />\r\n&quot;Nhiều như vậy!&quot;<br />\r\n<br />\r\nVương Đằng lại kinh, căn cứ nguy&ecirc;n tắc tuyệt kh&ocirc;ng l&atilde;ng ph&iacute;, đi theo ph&iacute;a sau hắn vừa chạy vừa nhặt.<br />\r\n<br />\r\nVừa gia tăng tốc độ thuộc t&iacute;nh ph&aacute;t huy t&aacute;c dụng, chống đỡ lấy Vương Đằng d&aacute;n tại mập mạp sau lưng, chạy c&oacute; ch&uacute;t sung sướng.<br />\r\n<br />\r\n【 tốc độ *1 】<br />\r\n<br />\r\n【 tốc độ *1 】<br />\r\n<br />\r\n【 tốc độ *1 】<br />\r\n<br />\r\n【 cơ sở th&acirc;n ph&aacute;p *3 】<br />\r\n<br />\r\n【 tốc độ *1 】<br />\r\n<br />\r\n...<br />\r\n<br />\r\n&quot;A ~ a ~ ha... Ngươi đi theo ta đằng sau l&agrave;m g&igrave;?&quot; Mập mạp ngừng lại, l&egrave; lưỡi, nh&igrave;n chằm chằm Vương Đằng hỏi.<br />\r\n<br />\r\n&quot;Ai đi theo ngươi, ta đang luyện th&acirc;n ph&aacute;p, chớ tự m&igrave;nh đa t&igrave;nh.&quot; Vương Đằng cũng trợn mắt n&oacute;i.<br />\r\n<br />\r\n&quot;Ngươi...&quot;<br />\r\n<br />\r\n&quot;Ngươi c&aacute;i g&igrave; ngươi?&quot;<br />\r\n<br />\r\n&quot;M&oacute;a, ta kh&ocirc;ng chạy, ch&iacute;nh ngươi chạy tới đi.&quot; Mập mạp kh&oacute; thở, lại đối với Vương Đằng kh&ocirc;ng thể l&agrave;m g&igrave;.<br />\r\n<br />\r\n&quot;Đừng a, lại chạy hai lần, ta cảm thấy ngươi rất c&oacute; tiền đồ, kh&ocirc;ng n&ecirc;n t&ugrave;y tiện từ bỏ a!&quot; Vương Đằng giữ lại nói.<br />\r\n<br />\r\n&quot;...&quot; Mập mạp.<br />\r\n&nbsp;</p>', '2021-11-28 13:32:18', 3, 1),
(213, 58, 'Vương Đằng ta, rất chăm chỉ!', '5', '<p>Mười một giờ, đ&ecirc;m đ&atilde; thật khuya.<br />\r\n<br />\r\nVương Đằng từ Cực Tinh v&otilde; qu&aacute;n rời đi, một mặt vẫn chưa thỏa m&atilde;n.<br />\r\n<br />\r\nNgười kh&aacute;c huấn luyện một đ&ecirc;m, hắn th&igrave; l&agrave; pha trộn một đ&ecirc;m.<br />\r\n<br />\r\nPha trộn?<br />\r\n<br />\r\nPhi phi... V&otilde; Giả sự t&igrave;nh sao c&oacute; thể n&oacute;i l&agrave; pha trộn!<br />\r\n<br />\r\nC&aacute;i kh&aacute;c V&otilde; Đồ học vi&ecirc;n cũng lần lượt rời đi, Vương Đằng l&agrave; cuối c&ugrave;ng một nh&oacute;m.<br />\r\n<br />\r\nKh&ocirc;ng phải hắn c&oacute; bao nhi&ecirc;u cố gắng, m&agrave; l&agrave; v&igrave; kh&ocirc;ng lọt mất bất kỳ một c&aacute;i n&agrave;o thuộc t&iacute;nh bọt kh&iacute;, &acirc;n... Đ&acirc;y cũng l&agrave; một loại cố gắng.<br />\r\n<br />\r\nVương Đằng l&aacute;i xe tại tr&ecirc;n đường phố kh&ocirc;ng người.<br />\r\n<br />\r\n【 ngộ t&iacute;nh 】: 19. 3<br />\r\n<br />\r\n【 thể chất 】: 13<br />\r\n<br />\r\n【 lực lượng 】: 135<br />\r\n<br />\r\n【 tốc độ 】: 76<br />\r\n<br />\r\n【 chiến kỹ 】: Cơ sở quyền ph&aacute;p (hơi biết), cơ sở th&acirc;n ph&aacute;p (hơi biết)<br />\r\n<br />\r\nLiếc một c&aacute;i giao diện thuộc t&iacute;nh, vừa l&ograve;ng thỏa &yacute;.<br />\r\n<br />\r\nTừ ban đầu một chữ số, đến b&acirc;y giờ hai chữ số, ba chữ số thuộc t&iacute;nh, biến h&oacute;a kh&ocirc;ng thể nghi ngờ l&agrave; to lớn.<br />\r\n<br />\r\nLực lượng c&ugrave;ng tốc độ tăng l&ecirc;n nhiều nhất, m&agrave; ngộ t&iacute;nh c&ugrave;ng thể chất tương đối hơi &iacute;t.<br />\r\n<br />\r\nKh&ocirc;ng kh&oacute; nh&igrave;n ra, c&aacute;c loại thuộc t&iacute;nh rơi xuống x&aacute;c suất tồn tại kh&aacute;c biệt t&iacute;nh nhất định.<br />\r\n<br />\r\nL&agrave;m hắn ngo&agrave;i &yacute; muốn ch&iacute;nh l&agrave;, đ&ecirc;m nay c&ograve;n được đến cơ sở quyền ph&aacute;p c&ugrave;ng cơ sở th&acirc;n ph&aacute;p, một V&otilde; Giả, chỉ c&oacute; th&acirc;n thể cường h&atilde;n l&agrave; kh&ocirc;ng được, nhất định phải học được chiến kỹ, mới c&oacute; thể ph&aacute;t huy ra vốn c&oacute; thực lực.<br />\r\n<br />\r\n&quot;Ng&agrave;y mai lại đến v&otilde; qu&aacute;n, đi kiểm tra một ch&uacute;t, nh&igrave;n xem ta c&oacute; thể tới tr&igrave;nh độ g&igrave;?&quot; Vương Đằng thầm nghĩ.<br />\r\n<br />\r\nVề đến nh&agrave;, L&yacute; T&uacute; Mai chuẩn bị ăn khuya.<br />\r\n<br />\r\nVương Thịnh Quốc đang ăn, nh&igrave;n thấy Vương Đằng trở về, nh&iacute;u m&agrave;y hỏi: &quot;Muộn như vậy, đi đ&acirc;u rồi?&quot;<br />\r\n<br />\r\n&quot;Đi một chuyến Cực Tinh v&otilde; qu&aacute;n.&quot; Vương Đằng thuận miệng đ&aacute;p.<br />\r\n<br />\r\nSau đ&oacute; lại x&ocirc;ng L&yacute; T&uacute; Mai n&oacute;i: &quot;Mẹ, ngươi nấu m&igrave; trứng g&agrave; a, c&oacute; hay kh&ocirc;ng chừa ch&uacute;t cho ta đ&acirc;y? Ta đ&oacute;i.&quot;<br />\r\n<br />\r\n&quot;C&oacute;! Giữ lại cho ngươi đ&acirc;y.&quot;<br />\r\n<br />\r\nNhi tử th&iacute;ch m&igrave;nh nấu đồ vật, L&yacute; T&uacute; Mai thật cao hứng.<br />\r\n<br />\r\nH&uacute;t trượt h&uacute;t trượt ~<br />\r\n<br />\r\nVương Đằng tiếp nhận b&aacute;t, ngồi tại Vương Thịnh Quốc b&ecirc;n cạnh, miệng lớn bắt đầu ăn, ăn ph&aacute; lệ hương.<br />\r\n<br />\r\nVương Thịnh Quốc kh&ocirc;ng c&oacute; tại Vương Đằng tr&ecirc;n th&acirc;n ngửi được m&ugrave;i rượu, l&ocirc;ng m&agrave;y c&oacute; ch&uacute;t bu&ocirc;ng ra, kinh ngạc hỏi: &quot;Ngươi đi v&otilde; qu&aacute;n l&agrave;m c&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\n&quot;Ta đi luyện v&otilde;!&quot; Vương Đằng cũng kh&ocirc;ng ngẩng đầu l&ecirc;n n&oacute;i.<br />\r\n<br />\r\n&quot;Luyện v&otilde;!&quot;<br />\r\n<br />\r\n&quot;Luyện v&otilde;!&quot;<br />\r\n<br />\r\nVương Thịnh Quốc c&ugrave;ng L&yacute; T&uacute; Mai đều lấy l&agrave;m kinh h&atilde;i, hai người kh&ocirc;ng khỏi liếc nhau, sau đ&oacute; c&ugrave;ng nhau nh&igrave;n về ph&iacute;a Vương Đằng.<br />\r\n<br />\r\nBọn hắn trước kia khuy&ecirc;n qua Vương Đằng, nhưng l&agrave; Vương Đằng từ nhỏ nu&ocirc;ng chiều từ b&eacute;, lại bất học v&ocirc; thuật, căn bản kh&ocirc;ng c&oacute; đem bọn hắn để trong l&ograve;ng.<br />\r\n<br />\r\nHiện tại hắn thế m&agrave; chủ động muốn học v&otilde;?<br />\r\n<br />\r\n&quot;Nhi tử, ngươi muốn luyện v&otilde;?&quot; L&yacute; T&uacute; Mai c&oacute; ch&uacute;t kh&ocirc;ng tin hỏi.<br />\r\n<br />\r\n&quot;Đ&uacute;ng a!&quot; Vương Đằng nhẹ gật đầu.<br />\r\n<br />\r\n&quot;Nhi tử, ngươi y&ecirc;u đương rồi?&quot; L&yacute; T&uacute; Mai hồ nghi n&oacute;i.<br />\r\n<br />\r\n&quot;A?&quot; Vương Đằng một mộng.<br />\r\n<br />\r\nTa lúc nào y&ecirc;u đương? Ta l&agrave;m sao kh&ocirc;ng biết?<br />\r\n<br />\r\n&quot;Ngươi trước kia kh&ocirc;ng phải kh&ocirc;ng y&ecirc;u luyện v&otilde; sao?&quot; Vương Thịnh Quốc hỏi.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y c&oacute; c&aacute;i g&igrave; đ&aacute;ng ngại, b&acirc;y giờ muốn th&ocirc;ng, tự nhi&ecirc;n l&agrave; đi luyện.&quot; Vương Đằng l&agrave;m ra một bộ t&ugrave;y &yacute; bộ d&aacute;ng n&oacute;i.<br />\r\n<br />\r\nChỉ đơn giản như vậy?<br />\r\n<br />\r\nVương Thịnh Quốc c&ugrave;ng L&yacute; T&uacute; Mai lại một lần nữa hai mặt nh&igrave;n nhau.<br />\r\n<br />\r\n&quot;Tốt, đ&atilde; ngươi muốn luyện v&otilde;, vậy ta liền cho ngươi mời một c&aacute;i tốt l&atilde;o sư đến dạy ngươi!&quot; Vương Thịnh Quốc thấy Vương Đằng kh&ocirc;ng giống n&oacute;i đ&ugrave;a, cao hứng vỗ tay một c&aacute;i, n&oacute;i.<br />\r\n<br />\r\n&quot;Cha, l&atilde;o sư trước hết đừng mời, luyện v&otilde; c&acirc;̀n thi&ecirc;n ph&uacute;, ta nghĩ tự m&igrave;nh luyện một ch&uacute;t nh&igrave;n.&quot;<br />\r\n<br />\r\nVương Đằng lập tức cự tuyệt, c&aacute;i g&igrave; l&atilde;o sư so ra m&agrave; vượt m&igrave;nh b&agrave;n tay v&agrave;ng?<br />\r\n<br />\r\nCha hắn tối đa mời một c&aacute;i cao cấp V&otilde; Đồ tới l&agrave;m l&atilde;o sư của hắn, lấy hắn th&acirc;n gia, V&otilde; Giả khẳng định mời kh&ocirc;ng nổi.<br />\r\n<br />\r\nC&ugrave;ng một c&aacute;i cao cấp V&otilde; Đồ học tập, c&ograve;n kh&ocirc;ng bằng d&ugrave;ng nhiều ch&uacute;t thời gian nhặt thuộc t&iacute;nh.<br />\r\n<br />\r\nMời l&atilde;o sư c&ograve;n l&atilde;ng ph&iacute; tiền đ&acirc;y.<br />\r\n<br />\r\nB&acirc;y giờ V&otilde; Giả thời đại, ngay cả mời một c&aacute;i cao cấp V&otilde; Đồ, ph&iacute; tổn đều kh&ocirc;ng thấp.<br />\r\n<br />\r\nVương Thịnh Quốc kh&ocirc;ng c&oacute; cưỡng cầu, gật đầu n&oacute;i: &quot;Cũng tốt, v&otilde; qu&aacute;n b&ecirc;n trong cũng c&oacute; đạo sư, Cực Tinh v&otilde; qu&aacute;n l&agrave;m một trong tam cự đầu b&ecirc;n trong Hoa Hạ v&otilde; qu&aacute;n, đạo sư của bọn hắn sẽ kh&ocirc;ng k&eacute;m.&quot;<br />\r\n<br />\r\n&quot;Ngươi hảo hảo học, ta ngược lại kh&ocirc;ng y&ecirc;u cầu xa vời ngươi c&oacute; thể trở th&agrave;nh V&otilde; Giả, chỉ cần c&oacute; thể tu luyện tới cao cấp V&otilde; Đồ, tố chất th&acirc;n thể sẽ bi&ecirc;n độ lớn tăng l&ecirc;n, tuổi thọ cũng sẽ tăng cường kh&ocirc;ng &iacute;t.&quot;<br />\r\n<br />\r\n&quot;Th&acirc;n thể l&agrave; tiền vốn, c&oacute; một bộ th&acirc;n thể tốt, l&agrave;m c&aacute;i g&igrave; đều l&agrave;m &iacute;t m&agrave; hiệu quả nhiều.&quot;<br />\r\n<br />\r\nKỳ thật hắn cũng sợ Vương Đằng ba ph&uacute;t nhiệt độ, kh&ocirc;ng c&oacute; hai ng&agrave;y kh&ocirc;ng chịu khổ nổi, lại kh&ocirc;ng luyện.<br />\r\n<br />\r\nKh&ocirc;ng bằng trước hết để cho Vương Đằng tự m&igrave;nh tu luyện một đoạn thời gian, thật c&oacute; thể ki&ecirc;n tr&igrave; nổi, mới quyết định cũng kh&ocirc;ng muộn.<br />\r\n<br />\r\n&quot;Biết, cha.&quot; Vương Đằng trong l&ograve;ng cười thầm, cha hắn ch&iacute;nh l&agrave; th&iacute;ch thuyết gi&aacute;o, đổi th&agrave;nh trước kia, hắn căn bản nghe kh&ocirc;ng v&agrave;o.<br />\r\n<br />\r\nBất qu&aacute;.<br />\r\n<br />\r\nCao cấp V&otilde; Đồ?<br />\r\n<br />\r\nM&igrave;nh c&oacute; b&agrave;n tay v&agrave;ng, đừng n&oacute;i ch&iacute;nh thức V&otilde; Giả, ch&iacute;nh l&agrave; cấp Chiến Tướng V&otilde; Giả đều kh&ocirc;ng phải kh&ocirc;ng thể n&agrave;o.<br />\r\n<br />\r\nCh&iacute;nh l&agrave; sợ n&oacute;i ra h&ugrave; đến hắn m&agrave; th&ocirc;i.<br />\r\n<br />\r\nVương Thịnh Quốc l&ograve;ng mang vui mừng, cảm thấy Vương Đằng th&agrave;nh thục kh&ocirc;ng &iacute;t... Thật chẳng lẽ y&ecirc;u đương rồi?<br />\r\n<br />\r\n&quot;Nhi tử, ngươi cũng mười bảy tuổi, nếu quả thật y&ecirc;u đương, ch&uacute;ng ta kh&ocirc;ng phản đối, tr&ecirc;n người tiền ti&ecirc;u vặt c&ograve;n c&oacute; đủ hay kh&ocirc;ng?&quot;<br />\r\n<br />\r\nVương Đằng c&oacute; chút m&ecirc;.<br />\r\n<br />\r\nCh&uacute;ng ta l&agrave; quấn kh&ocirc;ng ra c&aacute;i n&agrave;y khảm đ&uacute;ng kh&ocirc;ng?<br />\r\n<br />\r\n&quot;Cha mẹ, ta thật kh&ocirc;ng c&oacute; y&ecirc;u đương, nếu như y&ecirc;u đương, khẳng định ngay lập tức mang về cho c&aacute;c ngươi nh&igrave;n.&quot; Hắn bất đắc dĩ n&oacute;i.<br />\r\n<br />\r\n&quot;Đ&aacute;ng tiếc!&quot; L&yacute; T&uacute; Mai c&oacute; hơi thất vọng.<br />\r\n<br />\r\nVương Thịnh Quốc t&aacute;n đồng gật gật đầu.<br />\r\n<br />\r\n&quot;...&quot; Vương Đằng im lặng n&oacute;i: &quot;Mẹ, ta vừa mười bảy tuổi, l&uacute;c n&agrave;y mới chỗ n&agrave;o c&ugrave;ng chỗ n&agrave;o a!&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&ograve;n sớm, hiện nay mười t&aacute;m tuổi liền c&oacute; thể lĩnh chứng kết h&ocirc;n, b&acirc;y giờ y&ecirc;u đương, bồi dưỡng một ch&uacute;t t&igrave;nh cảm, một năm sau kết h&ocirc;n vừa vặn.&quot; L&yacute; T&uacute; Mai nói.<br />\r\n<br />\r\n&quot;Mười t&aacute;m tuổi liền c&oacute; thể kết h&ocirc;n!&quot; Vương Đằng trong l&ograve;ng giật m&igrave;nh.<br />\r\n<br />\r\nV&otilde; đạo thời đại, như thế hạnh ph&uacute;c sao?<br />\r\n<br />\r\n&quot;Mẹ, ta ăn xong, l&ecirc;n trước l&acirc;u tắm rửa đi ngủ.&quot;<br />\r\n<br />\r\nHắn cũng như chạy trốn chạy l&ecirc;n l&acirc;u, lại c&ugrave;ng hắn mẹ n&oacute;i một hồi, chỉ sợ ngay cả nhi tử đều muốn ra.<br />\r\n<br />\r\nMẹ hắn ch&iacute;nh l&agrave; người như vậy.<br />\r\n<br />\r\n&quot;Đứa nhỏ n&agrave;y! L&atilde;o c&ocirc;ng, ta cảm thấy Đường gia c&ocirc; nương kia kh&ocirc;ng tệ, h&ocirc;m n&agrave;o muốn hay kh&ocirc;ng đi nh&agrave; n&agrave;ng hỏi một ch&uacute;t nh&igrave;n...&quot;<br />\r\n<br />\r\nDưới lầu, L&yacute; T&uacute; Mai c&ograve;n đang c&ugrave;ng Vương Thịnh Quốc lải nhải.<br />\r\n<br />\r\nMay mắn Vương Đằng kh&ocirc;ng nghe thấy, kh&ocirc;ng phải sợ l&agrave; muốn dọa nước tiểu, Đường gia c&ocirc; nương tối thiểu hai trăm c&acirc;n a mẹ của ta!<br />\r\n<br />\r\n...<br />\r\n<br />\r\nNg&agrave;y thứ hai.<br />\r\n<br />\r\nS&aacute;ng sớm, Vương Thịnh Quốc đang ăn bữa s&aacute;ng, b&aacute;nh bao, b&aacute;nh quẩy, sữa đậu n&agrave;nh... Mặc d&ugrave; đơn giản, lại l&agrave; người Hoa y&ecirc;u nhất.<br />\r\n<br />\r\nVương Đằng từ tr&ecirc;n lầu đi xuống.<br />\r\n<br />\r\nBởi v&igrave; l&agrave; thứ bảy, kh&ocirc;ng cần l&ecirc;n kh&oacute;a, thời gian c&oacute; thể tự do an b&agrave;i.<br />\r\n<br />\r\nĂn xong điểm t&acirc;m, Vương Đằng c&ugrave;ng phụ mẫu ch&agrave;o hỏi, vội v&agrave;ng ra cửa.<br />\r\n<br />\r\nVương Thịnh Quốc lại l&agrave; một trận vui mừng, x&ocirc;ng L&yacute; T&uacute; Mai n&oacute;i: &quot;H&ocirc;m nay để người hầu mua ch&uacute;t tinh th&uacute; thịt, tiểu Đằng luyện v&otilde;, ăn tinh th&uacute; thịt c&oacute; trợ gi&uacute;p tăng l&ecirc;n tố chất th&acirc;n thể.&quot;<br />\r\n<br />\r\nCực Tinh v&otilde; qu&aacute;n.<br />\r\n<br />\r\nVương Đằng đến rất sớm, huấn luyện trong sảnh chỉ c&oacute; hai ba c&aacute;i học vi&ecirc;n, những người n&agrave;y phi thường chăm chỉ.<br />\r\n<br />\r\nBọn hắn nh&igrave;n thấy Vương Đằng l&agrave; gương mặt lạ, hơi kinh ngạc.<br />\r\n<br />\r\nBất qu&aacute; Vương Đằng &quot;Chăm chỉ&quot;, đạt được bọn hắn t&aacute;n đồng, mấy c&aacute;i học vi&ecirc;n tương đối hiền l&agrave;nh nhẹ gật đầu, cảm thấy Vương Đằng c&ugrave;ng bọn hắn hẳn l&agrave; c&ugrave;ng một loại người.<br />\r\n<br />\r\nVương Đằng cũng cảm thấy m&igrave;nh rất chăm chỉ, thế nhưng l&agrave; người đến &iacute;t, bọt kh&iacute; cũng rất &iacute;t a.<br />\r\n<br />\r\nHắn tiến l&ecirc;n từng c&aacute;i c&ugrave;ng mấy c&aacute;i học vi&ecirc;n ch&agrave;o hỏi, thuận tiện đem thuộc t&iacute;nh nhặt đi.<br />\r\n<br />\r\nĐúng, chỉ l&agrave; thuận tiện!<br />\r\n<br />\r\nVương Đằng ta, l&agrave; c&aacute;i người c&oacute; lễ ph&eacute;p, ch&agrave;o hỏi mới thật sự l&agrave; mục đ&iacute;ch.<br />\r\n<br />\r\n【 cơ sở quyền ph&aacute;p *6 】<br />\r\n<br />\r\n【 lực lượng *8 】<br />\r\n<br />\r\n【 tốc độ *6 】<br />\r\n<br />\r\n【 cơ sở kiếm ph&aacute;p *4 】<br />\r\n<br />\r\n...<br />\r\n<br />\r\nOa, thuộc t&iacute;nh tốt phong ph&uacute;, c&ograve;n c&oacute; cơ sở quyền ph&aacute;p, cơ sở kiếm ph&aacute;p, lại học được một loại cơ sở chiến kỹ!<br />\r\n<br />\r\nQuả nhi&ecirc;n dậy sớm chim ch&oacute;c c&oacute; s&acirc;u ăn.<br />\r\n<br />\r\nV&otilde; qu&aacute;n học vi&ecirc;n mỗi c&aacute;i đều l&agrave; nh&acirc;n t&agrave;i, n&oacute;i chuyện lại &ecirc;m tai, c&ograve;n rớt rất nhiều thuộc t&iacute;nh, thật sự l&agrave; si&ecirc;u th&iacute;ch bọn hắn.</p>', '2021-11-28 13:32:44', 3, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(214, 58, 'Ta thật không phải chiến năm cặn bã', '6', '<p>Vương Đằng nhặt xong mấy c&aacute;i thuộc t&iacute;nh bọt kh&iacute;, thấy mấy t&ecirc;n học vi&ecirc;n kia trong thời gian ngắn sẽ kh&ocirc;ng lại rơi, liền chạy tới nơi hẻo l&aacute;nh chỗ.<br />\r\n<br />\r\nMấy đ&agrave;i m&aacute;y khảo nghiệm b&agrave;y ra ở nơi đ&oacute;.<br />\r\n<br />\r\nM&aacute;y đo lực, m&aacute;y đo tốc độ, thể chất dụng cụ đo lường các loại.<br />\r\n<br />\r\nĐ&acirc;y đều l&agrave; m&aacute;y m&oacute;c khảo s&aacute;t V&otilde; Đồ tố chất th&acirc;n thể, tương đối ti&ecirc;u chuẩn, sai s&oacute;t cực nhỏ.<br />\r\n<br />\r\nVương Đằng tới trước đến m&aacute;y đo lực trước mặt.<br />\r\n<br />\r\nHắn đ&atilde; học xong cơ sở quyền ph&aacute;p, tương đương với nắm giữ cơ bản nhất kỹ xảo ph&aacute;t lực.<br />\r\n<br />\r\nNgười kh&aacute;c ch&iacute; &iacute;t c&acirc;̀n một th&aacute;ng thời gian, coi như thi&ecirc;n t&agrave;i, cũng phải chừng mười ng&agrave;y, Vương Đằng lại chỉ d&ugrave;ng một đ&ecirc;m.<br />\r\n<br />\r\nKhoảng c&aacute;ch bất qu&aacute; l&agrave; mấy c&aacute;i thuộc t&iacute;nh bọt kh&iacute;!<br />\r\n<br />\r\nTr&ecirc;n thế giới gần nhất khoảng c&aacute;ch, c&ugrave;ng lắm cũng chỉ như thế n&agrave;y th&ocirc;i, người kh&aacute;c sợ l&agrave; cũng kh&ocirc;ng d&aacute;m nghĩ.<br />\r\n<br />\r\nVương Đằng chậm r&atilde;i đứng vững, h&iacute;t v&agrave;o một hơi thật s&acirc;u, nhắm mắt lại, cả người trầm tĩnh lại.<br />\r\n<br />\r\nHắn bỗng nhi&ecirc;n mở to mắt, &aacute;nh mắt ngưng lại, phảng phất tại đi săn d&atilde; th&uacute;, bỗng nhi&ecirc;n bạo khởi, hai ch&acirc;n t&oacute;m chặt lấy mặt đất, h&ocirc;ng eo khẽ nh&uacute;c nh&iacute;ch, ph&iacute;a sau lưng xương sống k&eacute;o căng như d&acirc;y cung, một quyền đ&aacute;nh ra.<br />\r\n<br />\r\n&quot;Bành!&quot;<br />\r\n<br />\r\nVương Đằng nắm đấm trực tiếp nện ở m&aacute;y đo lực ph&iacute;a tr&ecirc;n.<br />\r\n<br />\r\nM&aacute;y đo lực tr&ecirc;n m&agrave;n h&igrave;nh số lượng đi&ecirc;n cuồng loạn động, cuối c&ugrave;ng dừng lại tại một v&agrave;i gi&aacute; trị ph&iacute;a tr&ecirc;n &mdash;&mdash;143kg!<br />\r\n<br />\r\nTối h&ocirc;m qua khi về nh&agrave;, hắn lực lượng thuộc t&iacute;nh l&agrave; 135, vừa mới lại lấy được 8 điểm, tổng cộng ch&iacute;nh l&agrave; 143, vừa vặn đối ứng 143 kg.<br />\r\n<br />\r\nC&aacute;i n&agrave;y lực lượng đ&atilde; tiếp cận người b&igrave;nh thường cực hạn!<br />\r\n<br />\r\nCh&uacute; &yacute;, l&agrave; cực hạn lực lượng!<br />\r\n<br />\r\nMột c&aacute;i bị buộc đến tuyệt cảnh người, cũng c&oacute; thể ph&aacute;t huy ra vượt qua th&acirc;n thể cực hạn lực lượng, nhưng l&agrave; người b&igrave;nh thường, rất kh&oacute;! Phi thường kh&oacute;!<br />\r\n<br />\r\nB&acirc;y giờ suy nghĩ một ch&uacute;t, ban đầu hắn lực lượng thuộc t&iacute;nh chỉ c&oacute; 50, đ&oacute; kh&ocirc;ng phải l&agrave; chiến năm cặn b&atilde;??<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; khả năng, ta tuyệt đối kh&ocirc;ng phải chiến năm cặn b&atilde;!<br />\r\n<br />\r\nVương Đằng bản th&acirc;n an ủi lắc đầu, sau đ&oacute; mở ra m&aacute;y đo tốc độ, đứng l&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nM&aacute;y đo tốc độ tựa như một đ&agrave;i m&aacute;y chạy bộ, theo người kiểm tra chạy, ph&iacute;a dưới săm xe t&ugrave;y theo chuyển động, đo ra tốc độ.<br />\r\n<br />\r\nVương Đằng bắt đầu chậm chạp chạy, tiếp theo chậm r&atilde;i gia tốc, cho đến đạt tới max trị số.<br />\r\n<br />\r\nM&aacute;y đo tốc độ sẽ đem người kiểm tra tốc độ nhanh nhất hiện ra.<br />\r\n<br />\r\nVương Đằng cảm gi&aacute;c m&igrave;nh đ&atilde; chạy đến nhất nhanh về sau, chậm r&atilde;i ngừng lại, đi xuống m&aacute;y đo tốc độ.<br />\r\n<br />\r\nHắn nh&igrave;n một ch&uacute;t m&aacute;y đo tốc độ b&ecirc;n tr&ecirc;n biểu hiện trị số: &quot;12.7m/s!&quot;<br />\r\n<br />\r\nRất hiển nhi&ecirc;n, tốc độ c&ugrave;ng thuộc t&iacute;nh cũng kh&ocirc;ng th&agrave;nh ngang nhau, về phần cả hai đến c&ugrave;ng l&agrave;m sao chuyển đổi, Vương Đằng tựa hồ kh&ocirc;ng quan t&acirc;m.<br />\r\n<br />\r\nĐơn giản ch&iacute;nh l&agrave; trị số kh&aacute;c biệt m&agrave; th&ocirc;i.<br />\r\n<br />\r\nVương Đằng đối vừa mới khảo th&iacute; ra tốc độ c&oacute; ch&uacute;t giật m&igrave;nh.<br />\r\n<br />\r\nC&aacute;i n&agrave;y đ&atilde; gặp phải kiếp trước thế giới trăm m&eacute;t chạy qu&aacute;n qu&acirc;n đi? V&otilde; Đồ th&acirc;n thể đều khủng bố như vậy, huống chi l&agrave; V&otilde; Giả.<br />\r\n<br />\r\n&quot;Nh&igrave;n nh&igrave;n lại thể chất.&quot;<br />\r\n<br />\r\nThể chất dụng cụ đo lường tựa như khoang ngủ đ&ocirc;ng b&ecirc;n trong phim khoa học viễn tưởng, đứng thẳng trong g&oacute;c.<br />\r\n<br />\r\nVương Đằng đi v&agrave;o, ấn xuống mở ra khảo th&iacute; n&uacute;t bấm, c&oacute; một ch&ugrave;m s&aacute;ng từ đầu đến ch&acirc;n đem hắn th&acirc;n thể qu&eacute;t h&igrave;nh mấy lần!<br />\r\n<br />\r\nThể chất khảo th&iacute; bao h&agrave;m huy&ecirc;́t dịch, cơ bắp, xương cốt, kinh lạc c&aacute;c loại tổng hợp yếu tố, phi thường phức tạp, cũng chỉ c&oacute; thể dựa v&agrave;o c&ocirc;ng nghệ cao mới c&oacute; thể l&agrave;m đến.<br />\r\n<br />\r\n&quot;Đinh, qu&eacute;t h&igrave;nh kết th&uacute;c, học vi&ecirc;n Vương Đằng, thể chất 13!&quot;<br />\r\n<br />\r\nMột c&aacute;i thanh &acirc;m nữ từ duy&ecirc;n d&aacute;ng từ b&ecirc;n trong thể chất dụng cụ đo lường truyền ra.<br />\r\n<br />\r\n&quot;Chậc chậc, c&ograve;n mang giọng n&oacute;i th&ocirc;ng b&aacute;o c&ocirc;ng năng.&quot;<br />\r\n<br />\r\nVương Đằng cảm kh&aacute;i thể chất dụng cụ đo lường muốn so m&aacute;y đo lực c&ugrave;ng m&aacute;y đo tốc độ cấp cao hơn đồng thời, kh&ocirc;ng khỏi trầm ng&acirc;m n&oacute;i:<br />\r\n<br />\r\n&quot;Thể chất 13, ngược lại l&agrave; c&ugrave;ng thể chất thuộc t&iacute;nh đối ứng, xem ra chỉ c&oacute; tốc độ kh&aacute;c biệt.&quot;<br />\r\n<br />\r\nNh&igrave;n xuống d&aacute;n tại b&ecirc;n cạnh tr&ecirc;n tường 【 V&otilde; Đồ đẳng cấp ph&acirc;n chia danh s&aacute;ch 】.<br />\r\n<br />\r\nC&acirc;́p th&acirc;́p V&otilde; Đồ ti&ecirc;u chuẩn l&agrave; lực lượng 100 kg đến 300kg, tốc độ trăm m&eacute;t ch&iacute;n gi&acirc;y đến bảy gi&acirc;y, cũng ch&iacute;nh l&agrave; 11.11 m/s đến 14. 28m/s dạng n&agrave;y, thể chất 10 đến 30.<br />\r\n<br />\r\nR&otilde; r&agrave;ng, Vương Đằng đ&atilde; coi như l&agrave; một c&acirc;́p th&acirc;́p V&otilde; Đồ!<br />\r\n<br />\r\nHắn đối với kết quả n&agrave;y, vẫn l&agrave; thật h&agrave;i l&ograve;ng.<br />\r\n<br />\r\nKh&ocirc;ng h&agrave;i l&ograve;ng c&ograve;n muốn thượng thi&ecirc;n sao?<br />\r\n<br />\r\nD&ugrave; sao từ một c&aacute;i chiến năm cặn b&atilde; biến th&agrave;nh một c&acirc;́p th&acirc;́p V&otilde; Đồ, hắn so với người kh&aacute;c kh&ocirc;ng biết bớt bao nhi&ecirc;u thời gian.<br />\r\n<br />\r\nL&agrave;m người n&ecirc;n biết đủ...<br />\r\n<br />\r\n&quot;H&ocirc;m nay tranh thủ vọt tới trung cấp V&otilde; Đồ!&quot; Vương Đằng h&agrave;o hứng tr&agrave;n đầy, quyết định cũng l&agrave; kh&ocirc;ng đi, một ng&agrave;y đều ng&acirc;m m&igrave;nh ở huấn luyện sảnh.<br />\r\n<br />\r\nMấy c&aacute;i kia học vi&ecirc;n đều tại ri&ecirc;ng ph&acirc;̀n mình huấn luyện, kh&ocirc;ng c&oacute; ch&uacute; &yacute; Vương Đằng kết quả khảo nghiệm.<br />\r\n<br />\r\nD&ugrave; sao bọn hắn h&ocirc;m nay chỉ l&agrave; lần thứ nhất gặp, kh&ocirc;ng c&oacute; g&igrave; giao t&igrave;nh, mạo muội nh&igrave;n trộm thực lực của người kh&aacute;c, đ&uacute;ng l&agrave; kh&ocirc;ng lễ ph&eacute;p.<br />\r\n<br />\r\nNgay tại Vương Đằng vừa mới khảo nghiệm thời điểm, mấy t&ecirc;n học vi&ecirc;n dưới ch&acirc;n lại rơi xuống mấy c&aacute;i thuộc t&iacute;nh bọt kh&iacute;.<br />\r\n<br />\r\nVương Đằng nh&atilde;n t&igrave;nh s&aacute;ng l&ecirc;n, lơ đ&atilde;ng từ b&ecirc;n cạnh bọn họ đi qua, lặng y&ecirc;n nhặt.<br />\r\n<br />\r\n【 cơ sở kiếm ph&aacute;p *3 】<br />\r\n<br />\r\n【 lực lượng *5 】<br />\r\n<br />\r\n【 ngộ t&iacute;nh * 0.6 】<br />\r\n<br />\r\n【 tốc độ *2 】<br />\r\n<br />\r\n...<br />\r\n<br />\r\n&quot;Kh&ocirc;ng sai kh&ocirc;ng sai, lại c&ograve;n c&oacute; ngộ t&iacute;nh, cơ sở kiếm ph&aacute;p cũng thuần thục kh&ocirc;ng &iacute;t!&quot; Vương Đằng mừng thầm.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, người c&ograve;n qu&aacute; &iacute;t, Vương Đằng cũng kh&ocirc;ng nghĩ qu&aacute; mức h&agrave;nh xử kh&aacute;c người, để người ch&uacute; &yacute;, hắn đi v&agrave;o binh kh&iacute; thất, &aacute;nh mắt khẽ qu&eacute;t m&agrave; qua.<br />\r\n<br />\r\nBinh kh&iacute; cửa h&agrave;ng trưng b&agrave;y rất nhiều binh kh&iacute;, kiếm, đao, thương, k&iacute;ch, c&ocirc;n, ch&ugrave;y c&aacute;c loại.<br />\r\n<br />\r\nC&ugrave;ng một loại lớn binh kh&iacute;, c&ograve;n c&oacute; rất nhiều nhỏ ph&acirc;n loại, tỉ như kiếm, c&oacute; trường kiếm, đoản kiếm, nhuyễn kiếm, hai tay kiếm c&aacute;c loại.<br />\r\n<br />\r\nC&oacute; thể n&oacute;i nơi n&agrave;y binh kh&iacute; chủng loại phong ph&uacute;, c&aacute;i g&igrave; cần c&oacute; đều c&oacute;.<br />\r\n<br />\r\nD&ugrave; cho học vi&ecirc;n học binh kh&iacute; lại thi&ecirc;n m&ocirc;n, đều c&oacute; thể tại trong ph&ograve;ng binh kh&iacute; n&agrave;y t&igrave;m tới bọn hắn cần binh kh&iacute;.<br />\r\n<br />\r\nVương Đằng đứng tại b&agrave;y ra kiếm kh&iacute; trước ngăn tủ, gỡ xuống một thanh &quot;&Aacute;m Ảnh hệ liệt&quot; Hắc Thiết Kiếm.<br />\r\n<br />\r\nV&otilde; đạo thịnh h&agrave;nh, binh kh&iacute; ng&agrave;nh nghề tự nhi&ecirc;n cũng t&ugrave;y theo ph&aacute;t triển, d&ugrave; sao binh kh&iacute; đối với V&otilde; Giả m&agrave; n&oacute;i, qu&aacute; l&agrave; quan trọng.<br />\r\n<br />\r\nMột tay cầm vũ kh&iacute; sắc b&eacute;n V&otilde; Giả, c&ugrave;ng kh&ocirc;ng c&oacute; binh kh&iacute; V&otilde; Giả giao đấu l&uacute;c, chiếm hết ưu thế.<br />\r\n<br />\r\nBất qu&aacute; cung cấp V&otilde; Đồ binh kh&iacute; đều l&agrave; m&ocirc; phỏng ch&acirc;n thật binh kh&iacute;, chất liệu b&igrave;nh thường, m&agrave; lại kh&ocirc;ng c&oacute; b&aacute;m v&agrave;o ph&ugrave; văn.<br />\r\n<br />\r\nChu&ocirc;i n&agrave;y &quot;&Aacute;m Ảnh hệ liệt&quot; Hắc Thiết Kiếm to&agrave;n th&acirc;n đen nh&aacute;nh, chế tạo c&ugrave;ng trọng lượng đều c&ugrave;ng ch&iacute;nh phẩm kh&ocirc;ng kh&aacute;c, nhưng l&agrave; tr&ecirc;n th&acirc;n kiếm của n&oacute; kh&ocirc;ng c&oacute; huyền ảo ph&ugrave; văn.<br />\r\n<br />\r\nThật muốn l&agrave;m so s&aacute;nh.<br />\r\n<br />\r\nThanh kiếm n&agrave;y ph&iacute; tổn chỉ cần mấy trăm khối tiền, m&agrave; ch&iacute;nh phẩm, th&igrave; c&acirc;̀n mấy chục vạn, quả thực k&eacute;m c&aacute;ch xa vạn dặm.<br />\r\n<br />\r\nVương Đằng đi&ecirc;n đi&ecirc;n trong tay Hắc Thiết Kiếm, trọng lượng vừa vặn.<br />\r\n<br />\r\nNam sinh đối với c&aacute;c loại binh kh&iacute;, đều sẽ c&oacute; một loại thi&ecirc;n vị c&ugrave;ng hướng tới.<br />\r\n<br />\r\nRất nhiều người, thậm ch&iacute; l&agrave;m qua mộng giang hồ cầm kiếm đi thi&ecirc;n nhai!<br />\r\n<br />\r\nKiếp trước l&agrave; khoa học kỹ thuật x&atilde; hội, kh&ocirc;ng tồn tại v&otilde; đạo, Vương Đằng kh&ocirc;ng c&oacute; cơ hội tiếp x&uacute;c những thứ n&agrave;y.<br />\r\n<br />\r\nHiện tại hắn nh&igrave;n xem trường kiếm trong tay, c&oacute; ch&uacute;t n&oacute;ng l&ograve;ng kh&ocirc;ng đợi được.<br />\r\n<br />\r\nTrước đ&oacute; hắn thu hoạch được 【 cơ sở kiếm ph&aacute;p 】 thuộc t&iacute;nh, đối với cơ sở kiếm ph&aacute;p c&oacute; ch&uacute;t hi&ecirc;̉u rõ, thừa dịp người kh&aacute;c cũng c&ograve;n kh&ocirc;ng đến huấn luyện, cũng kh&ocirc;ng c&oacute; g&igrave; bọt kh&iacute; tốt nhặt, vừa vặn ra ngo&agrave;i luyện một ch&uacute;t, nh&igrave;n xem c&aacute;i n&agrave;y cơ sở uy lực kiếm ph&aacute;p.<br />\r\n<br />\r\nHắn đi ra binh kh&iacute; thất, t&igrave;m một c&aacute;i kh&ocirc;ng ai kh&ocirc;ng trung, dựa theo trong đầu th&ecirc;m ra kiếm ph&aacute;p k&yacute; ức, luyện l&ecirc;n kiếm ph&aacute;p.<br />\r\n<br />\r\n&quot;B&aacute; b&aacute; b&aacute;!&quot;<br />\r\n<br />\r\nTrường kiếm huy động, ph&aacute;t ra nhỏ x&iacute;u tiếng x&eacute; gi&oacute;.<br />\r\n<br />\r\nC&aacute;i n&agrave;y nếu l&agrave; ch&eacute;m v&agrave;o người tr&ecirc;n th&acirc;n, kh&ocirc;ng chết cũng bị thương.<br />\r\n<br />\r\nMấy c&aacute;i học vi&ecirc;n b&ecirc;n trong, t&ecirc;n kia cho Vương Đằng cung cấp 【 cơ sở kiếm ph&aacute;p 】 thuộc t&iacute;nh thanh ni&ecirc;n cũng đang luyện kiếm, nh&igrave;n thấy Vương Đằng cầm một thanh trường kiếm ra, lập tức hơi kinh ngạc.<br />\r\n<br />\r\nThế l&agrave; hắn chậm r&atilde;i thu thế, ở ph&iacute;a xa quan s&aacute;t Vương Đằng diễn luyện.<br />\r\n<br />\r\n&quot;Nguy&ecirc;n lai chỉ l&agrave; c&aacute;i người mới học!&quot;<br />\r\n<br />\r\nNhưng m&agrave; nh&igrave;n qua, hắn liền lắc đầu, kh&ocirc;ng c&ograve;n quan t&acirc;m.<br />\r\n<br />\r\n&quot;L&agrave;m sao cảm gi&aacute;c cũng kh&ocirc;ng phải l&agrave; qu&aacute; kh&oacute;? Luyện hai lần liền thuần thục rất nhiều.&quot;<br />\r\n<br />\r\n&quot;Ngộ t&iacute;nh của ta mới 19. 3, cũng kh&ocirc;ng phải rất cao a!&quot;<br />\r\n<br />\r\nVương Đằng dừng lại, kinh ngạc nghĩ đến.<br />\r\n<br />\r\nHắn cũng kh&ocirc;ng biết m&igrave;nh 19. 3 ngộ t&iacute;nh, đ&atilde; vượt qua thường nh&acirc;n rất nhiều, tu luyện cơ sở đồ vật đương nhi&ecirc;n sẽ kh&ocirc;ng cảm thấy qu&aacute; kh&oacute;.<br />\r\n<br />\r\nNếu như l&agrave; học tập Nguy&ecirc;n Lực chiến kỹ, liền kh&ocirc;ng khả năng nhẹ nh&agrave;ng như vậy!<br />\r\n<br />\r\nNguy&ecirc;n Lực chiến kỹ d&iacute;nh đến Nguy&ecirc;n Lực vận dụng, bản th&acirc;n liền tương đối th&acirc;m &aacute;o phức tạp, ngộ t&iacute;nh kh&ocirc;ng đủ, tu luyện sẽ phi thường kh&oacute; khăn.<br />\r\n<br />\r\nBởi vậy c&oacute; thể thấy được, tu luyện v&otilde; đạo x&aacute;c thực rất coi trọng thi&ecirc;n ph&uacute;, c&oacute; thể trở th&agrave;nh V&otilde; Giả, tất nhi&ecirc;n đều l&agrave; thi&ecirc;n t&agrave;i!</p>', '2021-11-28 13:33:07', 4, 1),
(215, 58, 'Nhổ nha nhổ lông dê...', '7', '<p>Luyện trong chốc l&aacute;t kiếm, Vương Đằng đem n&oacute; thả lại binh kh&iacute; thất.<br />\r\n<br />\r\nSau khi ra ngo&agrave;i đổi th&agrave;nh luyện quyền.<br />\r\n<br />\r\nT&ecirc;n kia luyện kiếm học vi&ecirc;n thấy, lại l&agrave; một trận lắc đầu, v&otilde; đạo tu luyện ở tinh m&agrave; kh&ocirc;ng ở nhiều.<br />\r\n<br />\r\nLiền Vương Đằng dạng n&agrave;y luyện hai lần kiếm ph&aacute;p, lại đi luyện quyền ph&aacute;p, c&ograve;n kh&ocirc;ng bằng chỉ luyện một loại, nếu kh&ocirc;ng đến cuối c&ugrave;ng, khẳng định l&agrave; hai loại đều luyện kh&ocirc;ng tốt.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y mới tới, chăm chỉ l&agrave; chăm chỉ, ch&iacute;nh l&agrave; ngốc một ch&uacute;t.&quot;<br />\r\n<br />\r\nVương Đằng c&ograve;n kh&ocirc;ng biết m&igrave;nh lại bị người d&aacute;n l&ecirc;n &quot;Ngốc&quot; nh&atilde;n hi&ecirc;̣u.<br />\r\n<br />\r\nHắn luyện hai lần quyền ph&aacute;p, cảm thấy đối với cơ sở quyền ph&aacute;p nắm giữ cũng l&agrave; lại thuần thục mấy phần, &acirc;n, hiệu quả kh&ocirc;ng tệ.<br />\r\n<br />\r\nVừa quay đầu, nh&igrave;n thấy một th&acirc;n ảnh cao to hai tay khoanh đứng ở b&ecirc;n cạnh.<br />\r\n<br />\r\nXem ra, đ&atilde; nh&igrave;n c&oacute; một hồi.<br />\r\n<br />\r\n&quot;L&atilde;o sư, ngươi chừng n&agrave;o th&igrave; đến, l&agrave;m sao cũng kh&ocirc;ng n&oacute;i chuyện, dọa ta một hồi.&quot;<br />\r\n<br />\r\nB&oacute;ng người n&agrave;y ch&iacute;nh l&agrave; tối h&ocirc;m qua dạy Vương Đằng 【 To&agrave;n Quốc Thứ Ch&iacute;n Bộ Ph&aacute;t Thanh Luyện Thể Thao 】 t&ecirc;n kia V&otilde; Đồ đạo sư, b&acirc;y giờ thấy hắn Vương Đằng vẫn c&oacute; ch&uacute;t t&ecirc; cả da đầu.<br />\r\n<br />\r\n&quot;Thiếu ni&ecirc;n, ta nh&igrave;n ngươi luyện c&oacute; một hồi, ngươi quyền, kh&ocirc;ng sai!&quot; Đạo sư hướng về ph&iacute;a Vương Đằng giơ ng&oacute;n tay c&aacute;i l&ecirc;n.<br />\r\n<br />\r\n&quot;Tạ ơn kh&iacute;ch lệ!&quot;<br />\r\n<br />\r\nVương Đằng xấu hổ, n&oacute;i ra: &quot;L&atilde;o sư, ta gọi Vương Đằng, c&ograve;n kh&ocirc;ng biết ngươi t&ecirc;n g&igrave; đ&acirc;u?&quot;<br />\r\n<br />\r\n&quot;Ta gọi B&agrave;nh Hải, ngươi cũng kh&ocirc;ng cần gọi ta l&atilde;o sư, k&ecirc;u một tiếng B&agrave;nh sư huynh liền tốt.&quot; B&agrave;nh Hải n&oacute;i.<br />\r\n<br />\r\n&quot;B&agrave;nh sư huynh.&quot; Vương Đằng biết nghe lời phải, sau đ&oacute; hỏi: &quot;Ngươi cũng sớm như vậy đến sao?&quot;<br />\r\n<br />\r\n&quot;Ta thi&ecirc;n ph&uacute; kh&ocirc;ng đủ, hơn hai mươi tuổi, vẫn l&agrave; cao cấp V&otilde; Đồ, chỉ c&oacute; thể dựa v&agrave;o cố gắng a.&quot; B&agrave;nh Hải buồn b&atilde; n&oacute;i.<br />\r\n<br />\r\nVương Đằng lập tức kh&ocirc;ng biết n&oacute;i c&aacute;i g&igrave; cho phải.<br />\r\n<br />\r\n&quot;Ha ha ha, kh&ocirc;ng cần để &yacute;, ta chỉ l&agrave; t&ugrave;y tiện ph&aacute;t c&agrave;u nh&agrave;u, Vương Đằng, ngươi thi&ecirc;n ph&uacute; kh&ocirc;ng tồi, lại chịu cố gắng, ta tin tưởng d&ugrave;ng kh&ocirc;ng đến ba năm, ngươi nhất định sẽ trở th&agrave;nh một V&otilde; Giả, hảo hảo cố l&ecirc;n nha.&quot; B&agrave;nh Hải kh&iacute;ch lệ n&oacute;i.<br />\r\n<br />\r\nBa năm?<br />\r\n<br />\r\nKh&ocirc;ng, đại kh&aacute;i ba ng&agrave;y liền đủ!<br />\r\n<br />\r\nVương Đằng nghĩ như vậy, bất qu&aacute; hắn chỉ c&oacute; thể gật đầu n&oacute;i: &quot;Ta sẽ cố gắng, B&agrave;nh sư huynh!&quot;<br />\r\n<br />\r\n&quot;Tốt, ta đi lầu ba huấn luyện, h&ocirc;m nay sẽ c&oacute; c&aacute;i kh&aacute;c cao cấp V&otilde; Đồ phụ tr&aacute;ch chỉ đạo c&aacute;c ngươi, đương nhi&ecirc;n, ngươi c&oacute; vấn đề g&igrave; cũng c&oacute; thể t&ugrave;y thời t&igrave;m ta.&quot; B&agrave;nh Hải n&oacute;i.<br />\r\n<br />\r\n&quot;Thật sự l&agrave; một c&aacute;i nhiệt t&igrave;nh sư huynh!&quot;<br />\r\n<br />\r\nVương Đằng nh&igrave;n qua hắn đi đến lầu ba, kh&ocirc;ng khỏi cảm kh&aacute;i n&oacute;i.<br />\r\n<br />\r\nCũng kh&ocirc;ng l&acirc;u lắm, lục tục ngo ngoe c&oacute; học vi&ecirc;n đến, trong huấn luyện sảnh dần dần n&aacute;o nhiệt l&ecirc;n.<br />\r\n<br />\r\n&quot;Sư huynh.&quot;<br />\r\n<br />\r\n&quot;Sư huynh sớm a!&quot;<br />\r\n<br />\r\n&quot;Sư tỷ, h&ocirc;m nay vẫn l&agrave; xinh đẹp như vậy!&quot;<br />\r\n<br />\r\n...<br />\r\n<br />\r\nQuen biết người lẫn nhau ch&agrave;o hỏi, sau đ&oacute; ri&ecirc;ng ph&acirc;̀n mình bắt đầu tu luyện, mọi người thời gian đều rất qu&yacute; gi&aacute;, gi&agrave;nh giật từng gi&acirc;y.<br />\r\n<br />\r\nVương Đằng nh&igrave;n xem bọn hắn, đột nhi&ecirc;n cảm thấy những học vi&ecirc;n n&agrave;y rất giống từng con d&ecirc; hai c&aacute;i đ&ugrave;i đứng thẳng cất bước, tr&ecirc;n người bọn họ c&oacute; rất nhiều l&ocirc;ng, nhổ đều nhổ kh&ocirc;ng hết l&ocirc;ng.<br />\r\n<br />\r\n&quot;Thật tốt!&quot; Kh&oacute;e miệng của hắn kh&ocirc;ng khỏi lộ ra một ch&uacute;t l&atilde;o phụ th&acirc;n d&aacute;ng tươi cười.<br />\r\n<br />\r\nChờ mập, liền c&oacute; thể l&agrave;m thịt... Phi phi, d&ecirc; tốt như vậy cũng kh&ocirc;ng phải d&ugrave;ng để l&agrave;m thịt, nhất định phải một mực nhổ xuống dưới.<br />\r\n<br />\r\nTheo mọi người bắt đầu kh&iacute; thế ngất trời huấn luyện, c&aacute;c loại thuộc t&iacute;nh bọt kh&iacute; nhao nhao rớt xuống.<br />\r\n<br />\r\nRất nhanh huấn luyện trong sảnh liền biến th&agrave;nh một mảnh bọt kh&iacute; hải dương.<br />\r\n<br />\r\nVương Đằng hạnh ph&uacute;c rong chơi ở trong đ&oacute;.<br />\r\n<br />\r\n&quot;Ta l&agrave; một c&aacute;i đảng l&ocirc;ng d&ecirc;, nhổ nha nhổ l&ocirc;ng d&ecirc;...&quot;<br />\r\n<br />\r\nHắn kh&ocirc;ng d&aacute;m đem b&agrave;i h&aacute;t n&agrave;y thật cho h&aacute;t ra, sợ bị đ&aacute;nh chết, chỉ l&agrave; một b&ecirc;n tiếng trầm ph&aacute;t đại t&agrave;i, một b&ecirc;n trong l&ograve;ng y&ecirc;n lặng h&aacute;t vang.<br />\r\n<br />\r\nL&uacute;c n&agrave;y nếu c&oacute; thanh &acirc;m nhắc nhở, đ&oacute; nhất định l&agrave; dạng n&agrave;y:<br />\r\n<br />\r\nPh&iacute;a trước rơi xuống 【 lực lượng *2 】, 【 tốc độ *6 】, ngươi nhặt l&ecirc;n, lực lượng của ngươi c&ugrave;ng tốc độ c&oacute; tăng l&ecirc;n!<br />\r\n<br />\r\nPh&iacute;a trước rơi xuống 【 cơ sở quyền ph&aacute;p *2 】, ngươi nhặt l&ecirc;n, ngươi cơ sở quyền ph&aacute;p c&agrave;ng th&ecirc;m thuần thục!<br />\r\n<br />\r\nPh&iacute;a trước rơi xuống 【 ngộ t&iacute;nh *1 】, 【 thể chất *3 】, ngươi nhặt l&ecirc;n, ngộ t&iacute;nh của ngươi c&ugrave;ng thể chất c&oacute; tăng l&ecirc;n!<br />\r\n<br />\r\n...<br />\r\n<br />\r\nThứ bảy người huấn luyện so với ban đ&ecirc;m h&ocirc;m qua c&ograve;n nhiều hơn.<br />\r\n<br />\r\nVương Đằng ho&agrave;n to&agrave;n đ&aacute;nh gi&aacute; thấp số lượng thuộc t&iacute;nh mọi người rơi xuống, hoặc l&agrave; n&oacute;i, hắn l&agrave; đ&aacute;nh gi&aacute; thấp tr&igrave;nh độ mọi người ra sức.<br />\r\n<br />\r\nC&aacute;c học vi&ecirc;n c&agrave;ng l&agrave; ra sức huấn luyện, rơi xuống thuộc t&iacute;nh x&aacute;c suất lại c&agrave;ng lớn.<br />\r\n<br />\r\nC&aacute;i n&agrave;y giống vắt sữa, ngươi c&agrave;ng d&ugrave;ng sức, dồn ra c&agrave;ng nhiều.<br />\r\n<br />\r\nCả ng&agrave;y qua đi, hắn thế m&agrave; ho&agrave;n th&agrave;nh chuyển biến từ c&acirc;́p th&acirc;́p V&otilde; Đồ đến trung cấp V&otilde; Đồ.<br />\r\n<br />\r\nV&ocirc; luận l&agrave; lực lượng, tốc độ, vẫn l&agrave; thể chất, đều l&agrave; đột ph&aacute; trung cấp V&otilde; Đồ c&aacute;nh cửa ti&ecirc;u chuẩn, kh&ocirc;ng c&oacute; nửa điểm tr&igrave;nh độ.<br />\r\n<br />\r\nKh&ocirc;ng chỉ c&oacute; như thế, trụ cột quyền ph&aacute;p, cơ sở th&acirc;n ph&aacute;p, cơ sở kiếm ph&aacute;p của hắn, đều đạt tới tiểu th&agrave;nh!<br />\r\n<br />\r\nChiến kỹ dựa theo độ thuần thục c&oacute; thể chia l&agrave;m hơi biết, thuần thục, tiểu th&agrave;nh, đại th&agrave;nh, nhập vi...<br />\r\n<br />\r\nĐạt tới thuần thục, đ&atilde; c&oacute; thể ph&aacute;t huy ra chiến kỹ vốn c&oacute; uy lực, tiểu th&agrave;nh th&igrave; c&agrave;ng c&oacute; ưu thế, uy lực tối thiểu mạnh một lần.<br />\r\n<br />\r\nM&agrave; nếu như đạt đến đại th&agrave;nh, thậm ch&iacute; nhập vi, c&aacute;i kia uy lực sẽ cường đại mấy lần kh&ocirc;ng th&ocirc;i.<br />\r\n<br />\r\nTrừ c&aacute;i đ&oacute; ra, Vương Đằng c&ograve;n được đến 【 cơ sở đao ph&aacute;p 】, đồng thời đem n&oacute; đẩy l&ecirc;n thuần thục tr&igrave;nh độ.<br />\r\n<br />\r\nThu hoạch kh&ocirc;ng thể bảo l&agrave; kh&ocirc;ng lớn.<br />\r\n<br />\r\nChạng vạng tối, đến giờ cơm.<br />\r\n<br />\r\nC&aacute;c học vi&ecirc;n huấn luyện một ng&agrave;y, sớm đ&atilde; bụng đ&oacute;i k&ecirc;u vang, nhao nhao thu thập một ch&uacute;t, lần lượt rời đi huấn luyện đại sảnh.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, Vương Đằng ngay tại nơi hẻo l&aacute;nh b&ecirc;n trong nghiệm thu đoạt được.<br />\r\n<br />\r\nTay hắn cầm Hắc Thiết Kiếm, phối hợp với th&acirc;n ph&aacute;p, đằng chuyển na di, huy động trường kiếm, ph&aacute;t ra kh&ocirc;ng kh&iacute; x&eacute; r&aacute;ch thanh &acirc;m.<br />\r\n<br />\r\nBuổi s&aacute;ng luyện kiếm t&ecirc;n thanh ni&ecirc;n kia đem thanh kiếm trả lại, từ binh kh&iacute; thất đi tới.<br />\r\n<br />\r\nTr&ugrave;ng hợp đi qua Vương Đằng b&ecirc;n cạnh, lơ đ&atilde;ng nh&igrave;n l&ecirc;n.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y, c&aacute;i n&agrave;y...&quot;<br />\r\n<br />\r\nCả người hắn đều chấn kinh, mi&ecirc;̣ng há l&atilde;o đại, d&ugrave;ng sức dụi dụi con mắt, phảng phất ho&agrave;i nghi m&igrave;nh sinh ra ảo gi&aacute;c.<br />\r\n<br />\r\n&quot;Đ&acirc;y tuyệt đối kh&ocirc;ng phải thuần thục tr&igrave;nh độ cơ sở kiếm ph&aacute;p, tiểu th&agrave;nh, nhất định l&agrave; tiểu th&agrave;nh!&quot;<br />\r\n<br />\r\nCh&iacute;nh hắn ch&iacute;nh l&agrave; thuần thục tr&igrave;nh độ, mặc d&ugrave; kh&ocirc;ng c&oacute; đạt tới tiểu th&agrave;nh, nhưng l&agrave; &aacute;nh mắt vẫn phải c&oacute;.<br />\r\n<br />\r\nNhưng l&agrave; c&aacute;i n&agrave;y sao c&oacute; thể, buổi s&aacute;ng, hắn nh&igrave;n Vương Đằng huấn luyện, vẫn chỉ l&agrave; một c&aacute;i người mới học, kiếm ph&aacute;p rất l&agrave; th&ocirc; r&aacute;p.<br />\r\n<br />\r\nMột ng&agrave;y tr&ocirc;i qua, lại nh&igrave;n thế m&agrave; liền đạt tới tiểu th&agrave;nh!<br />\r\n<br />\r\nC&aacute;i n&agrave;y nhất định l&agrave; n&oacute;i đ&ugrave;a a?<br />\r\n<br />\r\nChẳng lẽ tr&ecirc;n thế giới thật sự c&oacute; loại thi&ecirc;n t&agrave;i n&agrave;y sao?<br />\r\n<br />\r\nHắn cảm thấy m&igrave;nh muốn ho&agrave;i nghi nh&acirc;n sinh, buổi s&aacute;ng c&ograve;n chế giễu người ta ngốc, l&uacute;c n&agrave;y nghĩ lại, tựa hồ c&oacute; ch&uacute;t buồn cười.<br />\r\n<br />\r\n&quot;Đi&ecirc;n! Đi&ecirc;n!&quot;<br />\r\n<br />\r\nThanh ni&ecirc;n một mặt ch&aacute;n nản, cười khổ lắc đầu, đi ra huấn luyện sảnh.<br />\r\n<br />\r\nVương Đằng căn bản kh&ocirc;ng c&oacute; ph&aacute;t hiện c&oacute; người như vậy tồn tại, hắn đem đao, kiếm, quyền, th&acirc;n ph&aacute;p đều luyện một lần, to&agrave;n th&acirc;n c&oacute; một loại th&ocirc;ng suốt cảm gi&aacute;c.<br />\r\n<br />\r\n&quot;H&ocirc;!&quot;<br />\r\n<br />\r\nHắn chậm r&atilde;i phun ra một ngụm trọc kh&iacute;, đem binh kh&iacute; thả lại binh kh&iacute; thất.<br />\r\n<br />\r\nMột b&ecirc;n hướng v&otilde; qu&aacute;n b&atilde;i đậu xe dưới đất đi đến, một b&ecirc;n gọi ra giao diện thuộc t&iacute;nh.<br />\r\n<br />\r\n【 ngộ t&iacute;nh 】: 28<br />\r\n<br />\r\n【 thể chất 】: 42<br />\r\n<br />\r\n【 lực lượng 】: 455<br />\r\n<br />\r\n【 tốc độ 】: 185<br />\r\n<br />\r\n【 chiến kỹ 】: Cơ sở quyền ph&aacute;p (tiểu th&agrave;nh), cơ sở th&acirc;n ph&aacute;p (tiểu th&agrave;nh), cơ sở kiếm ph&aacute;p (tiểu th&agrave;nh), cơ sở đao ph&aacute;p (thuần thục)<br />\r\n<br />\r\n&quot;H&ocirc;m nay thu hoạch tr&agrave;n đầy a!&quot; Vương Đằng &yacute; cười đầy mặt l&aacute;i xe về nh&agrave;.<br />\r\n<br />\r\nTrong nh&agrave;, L&yacute; T&uacute; Mai đang nấu cơm, nồng đậm m&ugrave;i thịt từ ph&ograve;ng bếp bay ra, Vương Đằng bụng lập tức lẩm bẩm k&ecirc;u l&ecirc;n.<br />\r\n<br />\r\n&quot;Mẹ, h&ocirc;m nay nấu c&aacute;i g&igrave; tốt ăn? Thơm như vậy!&quot; Hắn đi v&agrave;o ph&ograve;ng bếp hỏi.<br />\r\n<br />\r\n&quot;Cha ngươi chuy&ecirc;n m&ocirc;n căn dặn ta mua cho ngươi tinh th&uacute; thịt.&quot; L&yacute; T&uacute; Mai cười n&oacute;i.<br />\r\n<br />\r\n&quot;Thật! Cha ta như thế bỏ tiền vốn a.&quot; Vương Đằng kinh ngạc n&oacute;i.<br />\r\n<br />\r\nTinh th&uacute; ch&iacute;nh l&agrave; nhận Nguy&ecirc;n Lực ảnh hưởng, sinh ra biến dị d&atilde; th&uacute;, bởi v&igrave; Nguy&ecirc;n Lực đản sinh tại vũ trụ tinh kh&ocirc;ng, v&igrave; thế m&agrave; gọi t&ecirc;n.<br />\r\n<br />\r\nThịt của bọn n&oacute; phi thường bổ dưỡng, kh&ocirc;ng phải loại th&uacute; b&igrave;nh thường c&oacute; thể so s&aacute;nh, gi&aacute; cả mười phần đắt đỏ.<br />\r\n<br />\r\n&quot;Thơm qu&aacute;, ta nếm thử xem.&quot;<br />\r\n<br />\r\nHắn nh&igrave;n thấy bếp l&ograve; b&ecirc;n tr&ecirc;n một b&agrave;n thịt kho t&agrave;u, nước bọt bài ti&ecirc;́t, nhịn kh&ocirc;ng được cầm bốc l&ecirc;n một khối n&eacute;m v&agrave;o miệng b&ecirc;n trong.<br />\r\n<br />\r\n&quot;Ng&ocirc;, ăn qu&aacute; ngon, mẹ, ngươi tr&ugrave; nghệ thật tốt.&quot;<br />\r\n<br />\r\n&quot;Ai nha, ngươi thật sự l&agrave; quỷ chết đ&oacute;i đầu thai, c&ograve;n c&oacute; ngươi c&aacute;i n&agrave;y một th&acirc;n mồ h&ocirc;i bẩn, c&ograve;n kh&ocirc;ng mau đi tắm.&quot; L&yacute; T&uacute; Mai đem hắn đuổi ra ph&ograve;ng bếp.<br />\r\n&nbsp;</p>', '2021-11-28 13:33:25', 4, 1),
(216, 58, 'Tường Vi quán bar', '8', '<p>Hơn s&aacute;u giờ, Vương Thịnh Quốc về đến nh&agrave;.<br />\r\n<br />\r\nL&yacute; T&uacute; Mai để người hầu Trần di gọi Vương Đằng xuống lầu ăn cơm.<br />\r\n<br />\r\nVương Đằng vừa tắm rửa xong, thay quần &aacute;o kh&aacute;c, đang d&ugrave;ng m&aacute;y sấy h&ocirc; h&ocirc; thổi t&oacute;c.<br />\r\n<br />\r\n&quot;Biết, Trần di, ta lập tức liền xuống đi.&quot;<br />\r\n<br />\r\nHắn thuận miệng đ&aacute;p.<br />\r\n<br />\r\nTrở th&agrave;nh V&otilde; Đồ về sau, ngay cả th&iacute;nh lực đều tăng cường kh&ocirc;ng &iacute;t, m&aacute;y sấy tạp &acirc;m kh&ocirc;ng c&oacute; ảnh hưởng ch&uacute;t n&agrave;o đến hắn.<br />\r\n<br />\r\nKh&ocirc;ng đến một ph&uacute;t, hắn thổi tốt t&oacute;c, nhổ đầu cắm, xuống lầu ăn cơm.<br />\r\n<br />\r\nVương Thịnh Quốc c&ugrave;ng L&yacute; T&uacute; Mai đều đang đợi hắn.<br />\r\n<br />\r\nTr&ecirc;n b&agrave;n b&agrave;y biện mấy b&aacute;t đồ ăn, n&oacute;ng h&ocirc;i hổi, m&ugrave;i thơm x&ocirc;ng v&agrave;o mũi.<br />\r\n<br />\r\nNgười một nh&agrave; ngồi v&acirc;y chung một chỗ ăn cơm.<br />\r\n<br />\r\n&quot;Nhi tử, h&ocirc;m nay luyện thế n&agrave;o?&quot;<br />\r\n<br />\r\nL&yacute; T&uacute; Mai hỏi ra Vương Thịnh Quốc muốn hỏi nhất m&agrave; n&oacute;i, hắn trơ mắt nh&igrave;n Vương Đằng.<br />\r\n<br />\r\nN&oacute;i trở lại, Vương Thịnh Quốc c&aacute;i n&agrave;y l&agrave;m cha cũng l&agrave; kh&ocirc;ng dễ d&agrave;ng, b&igrave;nh thường bằng hữu của hắn đều c&ugrave;ng hắn khoe khoang nhi tử con g&aacute;i l&agrave;m sao l&agrave;m sao ưu t&uacute;, ai ai con c&aacute;i nh&agrave; ai lại trở th&agrave;nh trung cấp V&otilde; Đồ, cao cấp V&otilde; Đồ c&aacute;c loại.<br />\r\n<br />\r\nDuy chỉ c&oacute; Vương Thịnh Quốc kh&oacute; m&agrave; mở miệng, nghĩ hắn đường đường một c&aacute;i tổng gi&aacute;m đốc c&ocirc;ng ty t&agrave;i sản lớn hơn trăm triệu, nhi tử lại bất học v&ocirc; thuật.<br />\r\n<br />\r\nN&oacute;i ra khẳng định sẽ bị người ch&ecirc; cười.<br />\r\n<br />\r\nHiện tại nhi tử rốt cục ho&agrave;n to&agrave;n tỉnh ngộ, chịu luyện v&otilde;, chờ thật th&agrave;nh cao cấp V&otilde; Đồ, hắn cũng có th&ecirc;̉ ra ngo&agrave;i n&oacute;i một ch&uacute;t.<br />\r\n<br />\r\n&quot;Tạm được, v&otilde; qu&aacute;n sư huynh n&oacute;i ta vẫn l&agrave; c&oacute; thi&ecirc;n ph&uacute;.&quot; Vương Đằng n&oacute;i.<br />\r\n<br />\r\nHắn kh&ocirc;ng c&oacute; c&ugrave;ng phụ mẫu n&oacute;i m&igrave;nh đ&atilde; l&agrave; trung cấp V&otilde; Đồ, c&aacute;c loại hạnh ph&uacute;c tới qu&aacute; đột nhi&ecirc;n, sợ bọn họ cao hứng t&eacute; xỉu.<br />\r\n<br />\r\nVề sau lại từ từ n&oacute;i cho bọn hắn tốt.<br />\r\n<br />\r\n&quot;C&oacute; thi&ecirc;n ph&uacute; liền tốt, c&oacute; thi&ecirc;n ph&uacute; liền tốt.&quot;<br />\r\n<br />\r\nVương Thịnh Quốc cao hứng con mắt đều h&iacute;p lại, vung tay l&ecirc;n n&oacute;i ra: &quot;Ngươi hảo hảo luyện, Vương gia ch&uacute;ng ta mặc d&ugrave; kh&ocirc;ng t&iacute;nh l&agrave; h&agrave;o m&ocirc;n cự ph&uacute;, nhưng tối thiểu so với người b&igrave;nh thường tốt hơn nhiều, luyện v&otilde; t&agrave;i nguy&ecirc;n phương diện ngươi kh&ocirc;ng cần lo lắng.&quot;<br />\r\n<br />\r\nVương Đằng nhẹ gật đầu, kh&ocirc;ng n&oacute;i th&ecirc;m g&igrave;.<br />\r\n<br />\r\nHắn cũng kh&ocirc;ng phải l&agrave; rất lo lắng t&agrave;i nguy&ecirc;n vấn đề, người kh&aacute;c c&oacute; lẽ rất c&acirc;̀n c&aacute;c loại luyện v&otilde; t&agrave;i nguy&ecirc;n, nhưng l&agrave; hắn cũng kh&ocirc;ng phải l&agrave; rất ỷ lại.<br />\r\n<br />\r\nB&agrave;n tay v&agrave;ng mang theo, tư nguy&ecirc;n của người kh&aacute;c ch&iacute;nh l&agrave; hắn t&agrave;i nguy&ecirc;n!<br />\r\n<br />\r\n&quot;Đến, ăn nhiều một ch&uacute;t, luyện một ng&agrave;y, khẳng định lại đ&oacute;i vừa mệt.&quot;<br />\r\n<br />\r\nSo s&aacute;nh Vương Đằng c&oacute; thể hay kh&ocirc;ng trở th&agrave;nh V&otilde; Giả, L&yacute; T&uacute; Mai quan t&acirc;m hơn hắn phải chăng chịu khổ bị li&ecirc;n lụy, nh&igrave;n xem Vương Đằng ăn như hổ đ&oacute;i, c&oacute; ch&uacute;t đau l&ograve;ng.<br />\r\n<br />\r\nVương Đằng đều kh&ocirc;ng để &yacute; tới n&oacute;i chuyện, kh&ocirc;ng thể kh&ocirc;ng n&oacute;i, tinh th&uacute; thịt x&aacute;c thực ăn qu&aacute; ngon, b&eacute;o m&agrave; kh&ocirc;ng ng&aacute;n, v&agrave;o miệng tan đi.<br />\r\n<br />\r\nĂn v&agrave;o trong bụng, phảng phất c&oacute; một d&ograve;ng nước ấm tại b&ecirc;n trong to&agrave;n th&acirc;n chảy xu&ocirc;i, thư gi&atilde;n mỏi mệt.<br />\r\n<br />\r\nL&yacute; T&uacute; Mai c&ugrave;ng Vương Thịnh Quốc cũng ăn một điểm, nhưng l&agrave; kh&ocirc;ng d&aacute;m ăn nhiều.<br />\r\n<br />\r\nBọn hắn d&ugrave; sao kh&ocirc;ng c&oacute; luyện v&otilde;, ăn nhiều, tốt qu&aacute; ho&aacute; dở.<br />\r\n<br />\r\nV&otilde; đạo thời đại người b&igrave;nh thường, mặc d&ugrave; nhận Nguy&ecirc;n Lực ảnh hưởng, tố chất th&acirc;n thể cao rất nhiều, bất qu&aacute; đ&oacute; l&agrave; thời gian d&agrave;i thay đổi một c&aacute;ch v&ocirc; tri v&ocirc; gi&aacute;c ảnh hưởng.<br />\r\n<br />\r\nCơm nước xong xu&ocirc;i, Vương Đằng đang chuẩn bị tiến về Cực Tinh v&otilde; qu&aacute;n, chu&ocirc;ng điện thoại di động vang l&ecirc;n.<br />\r\n<br />\r\nNhận nghe xong, Hứa Kiệt thanh &acirc;m truyền ra.<br />\r\n<br />\r\n&quot;Vương Đằng ca, ch&uacute;ng ta đ&atilde; tại Tường Vi qu&aacute;n bar, ngươi nhanh l&ecirc;n tới a.&quot;<br />\r\n<br />\r\nVương Đằng rốt cục nhớ tới c&aacute;i n&agrave;y sự t&igrave;nh sớm bị hắn qu&ecirc;n đến l&ecirc;n ch&iacute;n tầng m&acirc;y, kh&ocirc;ng khỏi cười khổ một tiếng, n&oacute;i: &quot;Được, chờ lấy, ta sau đ&oacute; liền đến.&quot;<br />\r\n<br />\r\n&quot;Xem ra tối nay l&agrave; đi kh&ocirc;ng được Cực Tinh v&otilde; qu&aacute;n.&quot;<br />\r\n<br />\r\nVương Đằng c&oacute; ch&uacute;t tiếc nuối, bất qu&aacute; hắn cũng biết cuối c&ugrave;ng kh&ocirc;ng c&oacute; khả năng mỗi ng&agrave;y ng&acirc;m m&igrave;nh ở v&otilde; qu&aacute;n.<br />\r\n<br />\r\n&quot;Coi như bu&ocirc;ng lỏng tốt.&quot;<br />\r\n<br />\r\nVương Đằng c&ugrave;ng phụ mẫu l&ecirc;n tiếng ch&agrave;o hỏi, liền đi ra cửa.<br />\r\n<br />\r\nRầm rầm rầm...<br />\r\n<br />\r\nXe thể thao tr&ecirc;n đường phố oanh minh, mặc kệ l&agrave; c&aacute;i kia th&acirc;n xe h&igrave;nh giọt nước, vẫn l&agrave; c&aacute;i n&agrave;y nổ thật to &acirc;m thanh, đều l&agrave; điểm &aacute;nh mắt người qua đường tập trung.<br />\r\n<br />\r\nNam Th&agrave;nh qu&aacute;n bar một con đường!<br />\r\n<br />\r\nĐ&ocirc;ng Hải c&oacute; thật nhiều đầu đường phố qu&aacute;n bar, Nam Th&agrave;nh c&aacute;i n&agrave;y một khối xem như phồn hoa nhất.<br />\r\n<br />\r\nLượng lớn qu&aacute;n bar tụ tập ở đ&acirc;y, loại h&igrave;nh kh&aacute;c nhau, kh&aacute;c biệt phong c&aacute;ch, thậm ch&iacute; kh&aacute;c biệt gi&aacute; vị.<br />\r\n<br />\r\nĐại ch&uacute;ng qu&aacute;n bar ph&ugrave; hợp cấp thấp người ti&ecirc;u d&ugrave;ng, cấp cao qu&aacute;n bar th&igrave; th&iacute;ch hợp x&atilde; hội tinh anh giai tầng, hoặc l&agrave; con em nh&agrave; gi&agrave;u.<br />\r\n<br />\r\nMặc d&ugrave; kh&ocirc;ng muốn n&oacute;i như vậy, nhưng người đ&uacute;ng l&agrave; trong l&uacute;c v&ocirc; h&igrave;nh bị chia l&agrave;m kh&aacute;c biệt giai tầng.<br />\r\n<br />\r\nMỗi đến ban đ&ecirc;m, một con đường qu&aacute;n bar xa hoa truỵ lạc, &oacute;ng &aacute;nh đ&egrave;n n&ecirc; &ocirc;ng, hoa mỹ quang ảnh đan xen, hội tụ th&agrave;nh kỳ cảnh tựa như ảo mộng.<br />\r\n<br />\r\nTrẻ tuổi đ&ocirc; thị nam nữ ban ng&agrave;y phụ trọng c&ocirc;ng việc, &aacute;p lực quấn th&acirc;n, ban đ&ecirc;m chỉ hi vọng t&igrave;m một c&aacute;i chỗ bu&ocirc;ng lỏng bản th&acirc;n.<br />\r\n<br />\r\nX&atilde; hội &aacute;p lực chưa từng c&oacute; biến mất, v&otilde; đạo thời đại, &aacute;p lực càng nặng.<br />\r\n<br />\r\nBọn hắn c&acirc;̀n ph&aacute;t tiết!<br />\r\n<br />\r\nĐương nhi&ecirc;n, đối với Vương Đằng những người n&agrave;y đến n&oacute;i, đơn giản ch&iacute;nh l&agrave; c&aacute;i địa phương t&igrave;m th&uacute; vui.<br />\r\n<br />\r\nTường Vi qu&aacute;n bar b&ecirc;n ngo&agrave;i, ngừng lại một dải xe thể thao xe sang, Vương Đằng t&igrave;m nửa ng&agrave;y, rốt cuộc t&igrave;m được một c&aacute;i chỗ trống đem xe ngừng tốt, sau đ&oacute; đi v&agrave;o ở trong qu&aacute;n bar.<br />\r\n<br />\r\nOanh!<br />\r\n<br />\r\nMột c&aacute;nh cửa, đem trong ngo&agrave;i qu&aacute;n rượu ngăn c&aacute;ch th&agrave;nh hai thế giới.<br />\r\n<br />\r\nMở ra đại m&ocirc;n trong nh&aacute;y mắt, k&igrave;nh bạo &acirc;m nhạc, tuấn nam tịnh nữ n&oacute;i đ&ugrave;a đ&ugrave;a giỡn thanh &acirc;m, tia s&aacute;ng s&aacute;ng tối lấp l&oacute;e, tụ tập c&ugrave;ng một chỗ, đ&aacute;nh thẳng v&agrave;o m&agrave;ng nhĩ c&ugrave;ng con mắt Vương Đằng.<br />\r\n<br />\r\n&quot;Thật sự l&agrave; cảm gi&aacute;c quen thuộc!&quot;<br />\r\n<br />\r\nKiếp trước, Vương gia lạc bại biến mất về sau, Vương Đằng kh&ocirc;ng c&ograve;n c&oacute; tới qua qu&aacute;n bar.<br />\r\n<br />\r\nHắn giống như l&agrave; bị từ b&ecirc;n trong c&aacute;i n&agrave;y huy&ecirc;n n&aacute;o thế giới đ&aacute; ra ngo&agrave;i.<br />\r\n<br />\r\nNhưng l&agrave; hiện tại, hắn lại trở về!<br />\r\n<br />\r\nVương Đằng sải bước đi tiến trong Tường Vi qu&aacute;n rượu, so&aacute;i kh&iacute; khu&ocirc;n mặt, một th&acirc;n h&agrave;ng hiệu trang phục, th&ecirc;m nữa hai ng&agrave;y n&agrave;y trở th&agrave;nh V&otilde; Đồ về sau th&acirc;n thể trở n&ecirc;n cường tr&aacute;ng thẳng tắp, tinh thần diện mạo rực rỡ hẳn l&ecirc;n, cả người tựa như kim cương ở trong đất c&aacute;t, lộ ra ph&aacute; lệ s&aacute;ng lo&aacute; mắt.<br />\r\n<br />\r\nRất nhiều nữ nh&acirc;n &aacute;nh mắt bị hắn hấp dẫn, tựa hồ c&oacute; chút cảm thấy hứng th&uacute;.<br />\r\n<br />\r\nVương Đằng đ&atilde; từng l&agrave; một c&aacute;i l&atilde;o thịt kh&ocirc;, nhưng b&acirc;y giờ lại l&agrave; thực sự tieẻu thịt tươi, dung mạo mang theo v&agrave;i tia non nớt, nhưng m&agrave; kh&iacute; chất kh&aacute;c biệt qu&aacute; nhiều.<br />\r\n<br />\r\nKiếp trước hắn kinh lịch rất nhiều sự t&igrave;nh, nh&acirc;n sinh chập tr&ugrave;ng.<br />\r\n<br />\r\nHưởng thụ qua ph&uacute; qu&yacute;, cũng cảm thụ qua ngh&egrave;o kh&oacute;.<br />\r\n<br />\r\nĐ&atilde; từng hăng h&aacute;i, thiếu ni&ecirc;n đắc ch&iacute;, đ&atilde; từng trầm lu&acirc;n sa đọa, &acirc;u sầu thất bại...<br />\r\n<br />\r\nM&agrave; phong ph&uacute; kinh lịch tạo n&ecirc;n hắn trầm ổn t&iacute;nh t&igrave;nh, để người gặp một lần liền dễ d&agrave;ng sinh ra hảo cảm.<br />\r\n<br />\r\nVương Đằng &aacute;nh mắt tại trong qu&aacute;n rượu liếc nh&igrave;n, t&igrave;m kiếm Hứa Kiệt bọn người.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, một thanh &acirc;m từ b&ecirc;n tr&aacute;i truyền tới.<br />\r\n<br />\r\n&quot;Vương Đằng ca, nơi n&agrave;y, nơi n&agrave;y!&quot;<br />\r\n<br />\r\nVương Đằng nh&igrave;n thấy nữ sinh đang hướng ph&iacute;a m&igrave;nh vẫy gọi, Bạch Vi, một c&aacute;i tiểu muội muội b&ecirc;n trong v&ograve;ng tr&ograve;n bọn hắn.<br />\r\n<br />\r\nBuộc t&oacute;c vi&ecirc;n thuốc, d&aacute;ng dấp hoạt b&aacute;t, lại mang theo một ch&uacute;t vũ mị.<br />\r\n<br />\r\nVương Đằng c&ograve;n nhớ r&otilde;, sau trưởng th&agrave;nh, tiểu nha đầu n&agrave;y d&aacute;ng dấp gọi l&agrave; một c&aacute;i hại nước hại d&acirc;n, kh&ocirc;ng biết bao nhi&ecirc;u nam nh&acirc;n bị n&agrave;ng m&ecirc; đảo.<br />\r\n<br />\r\n&quot;C&aacute;c ngươi đến đủ sớm a.&quot;<br />\r\n<br />\r\nBạch Vi cho Vương Đằng nhường ra vị tr&iacute;, Vương Đằng ngay tại b&ecirc;n người n&agrave;ng ngồi xuống.<br />\r\n<br />\r\n&quot;Vương Đằng ca, l&agrave; ngươi tới chậm.&quot; Hứa Kiệt ph&agrave;n n&agrave;n n&oacute;i.<br />\r\n<br />\r\n&quot;Đ&ecirc;m d&agrave;i đằng đẵng, l&uacute;c n&agrave;y mới vừa mới bắt đầu, đến sớm như vậy l&agrave;m g&igrave;.&quot; Vương Đằng cười n&oacute;i.<br />\r\n<br />\r\n&quot;Vương Đằng ca!&quot;<br />\r\n<br />\r\nMột nam sinh kh&aacute;c cũng c&ugrave;ng Vương Đằng l&ecirc;n tiếng ch&agrave;o hỏi.<br />\r\n<br />\r\n&quot;Dư Hạo, rất l&acirc;u kh&ocirc;ng gặp.&quot; Vương Đằng nh&igrave;n về ph&iacute;a đối phương.<br />\r\n<br />\r\n&quot;Gần nhất bị cha ta bắt cực kỳ, h&ocirc;m nay vẫn l&agrave; vụng trộm chạy ra ngo&agrave;i.&quot; Dư Hạo bất đắc dĩ n&oacute;i.<br />\r\n<br />\r\n&quot;Tiểu tử n&agrave;y bị cha hắn nắm lấy luyện v&otilde;.&quot; Hứa Kiệt cười tr&ecirc;n nỗi đau của người kh&aacute;c cười n&oacute;i.<br />\r\n<br />\r\n&quot;Ồ? Luyện thế n&agrave;o rồi?&quot; Vương Đằng kinh ngạc n&oacute;i.<br />\r\n<br />\r\n&quot;Ta đ&atilde; l&agrave; c&acirc;́p th&acirc;́p V&otilde; Đồ.&quot; Dư Hạo sờ sờ đầu, ngượng ng&ugrave;ng n&oacute;i.<br />\r\n<br />\r\n&quot;Ta mịa n&oacute;, ngươi tiểu tử n&agrave;y, kh&ocirc;ng n&oacute;i một tiếng liền th&agrave;nh c&acirc;́p th&acirc;́p V&otilde; Đồ!&quot; Hứa Kiệt cả kinh k&ecirc;u l&ecirc;n, hiển nhi&ecirc;n hắn c&ograve;n kh&ocirc;ng biết tin tức n&agrave;y.<br />\r\n<br />\r\n&quot;Ch&uacute;ng ta mấy c&aacute;i b&ecirc;n trong liền ngươi th&agrave;nh V&otilde; Đồ, về sau bị người khi dễ, cũng đều phải t&igrave;m ngươi.&quot;<br />\r\n<br />\r\nLập tức hắn liền b&aacute; bả vai lấy Dư Hạo, cười hắc hắc n&oacute;i.<br />\r\n<br />\r\n&quot;Tiểu h&aacute;o tử (con chuột con), về sau liền dựa v&agrave;o ngươi!&quot; Bạch Vi cũng cười phụ họa n&oacute;i<br />\r\n<br />\r\nDư Hạo bị bọn hắn n&oacute;i đến c&oacute; ch&uacute;t đắc &yacute;, nhất l&agrave; Bạch Vi khẳng định c&agrave;ng l&agrave;m cho hắn mười phần hưởng thụ.<br />\r\n<br />\r\nMấy người đi gần nhất, giữa nam nữ kh&oacute; tr&aacute;nh khỏi c&oacute; ch&uacute;t m&ocirc;ng lung hảo cảm.<br />\r\n<br />\r\nDư Hạo đối với Bạch Vi ch&iacute;nh l&agrave; như thế.<br />\r\n<br />\r\nVương Đằng cười cười, hỏi: &quot;L&agrave;m sao kh&ocirc;ng thấy được Vi&ecirc;n Ch&iacute;nh Hoa?&quot;<br />\r\n<br />\r\nMấy người lập tức một trận trầm mặc.<br />\r\n<br />\r\n&quot;Hắn sẽ kh&ocirc;ng đến!&quot; Hứa Kiệt cười lạnh n&oacute;i.<br />\r\n<br />\r\n&quot;Ừm? Chuyện g&igrave; xảy ra?&quot; Vương Đằng nh&iacute;u m&agrave;y hỏi.<br />\r\n<br />\r\n&quot;T&ecirc;n kia tr&egrave;o l&ecirc;n L&yacute; gia L&yacute; Vinh Th&agrave;nh, th&agrave;nh người ta ch&oacute; săn.&quot; Hứa Kiệt bĩu m&ocirc;i n&oacute;i.<br />\r\n<br />\r\n&quot;L&yacute; Vinh Th&agrave;nh!&quot;<br />\r\n<br />\r\nVương Đằng nghĩ nghĩ, cuối c&ugrave;ng từ k&yacute; ức suy nghĩ s&acirc;u xa l&ecirc;n người như vậy tới.<br />\r\n<br />\r\nVương Đằng mấy người gia cảnh kh&ocirc;ng k&eacute;m bao nhi&ecirc;u, bậc cha ch&uacute; cũng đều nhận biết, thương nghiệp b&ecirc;n tr&ecirc;n lẫn nhau c&oacute; lui tới, cho n&ecirc;n tự nhi&ecirc;n th&agrave;nh một c&aacute;i v&ograve;ng quan hệ.<br />\r\n<br />\r\nM&agrave; L&yacute; Vinh Th&agrave;nh chỗ L&yacute; gia, tại Đ&ocirc;ng Hải c&oacute; thể đứng v&agrave;o trước năm, so với bọn hắn mấy nh&agrave; đều mạnh hơn rất nhiều.<br />\r\n<br />\r\nVi&ecirc;n Ch&iacute;nh Hoa c&ugrave;ng L&yacute; Vinh Th&agrave;nh đi được gần, theo Hứa Kiệt, ch&iacute;nh l&agrave; tr&egrave;o l&ecirc;n đối phương đ&ugrave;i.<br />\r\n<br />\r\nBọn hắn những n&agrave;y ph&uacute; nhị đại v&ograve;ng tr&ograve;n ch&iacute;nh l&agrave; dạng n&agrave;y, thực lực gần, mới c&oacute; thể xưng l&agrave; bằng hữu.<br />\r\n<br />\r\nCh&ecirc;nh lệch qu&aacute; nhiều, ngược lại th&agrave;nh leo l&ecirc;n.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng n&oacute;i hắn, người c&oacute; ch&iacute; ri&ecirc;ng, kh&ocirc;ng c&oacute; g&igrave; để n&oacute;i nhiều.&quot; Vương Đằng cũng kh&ocirc;ng th&egrave;m để &yacute; n&oacute;i.<br />\r\n<br />\r\n&quot;Đúng, kh&ocirc;ng n&oacute;i tiểu tử kia, nhấc l&ecirc;n hắn ta liền tức giận.&quot; Hứa Kiệt nói.<br />\r\n<br />\r\nMấy người dời đi chỗ kh&aacute;c chủ đề, điểm rượu c&ugrave;ng m&acirc;m đựng tr&aacute;i c&acirc;y, cười cười n&oacute;i n&oacute;i, Vương Đằng dần dần t&igrave;m về cảm gi&aacute;c tuổi trẻ đ&atilde; từng.<br />\r\n&nbsp;</p>', '2021-11-28 13:33:46', 4, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(217, 58, 'Bằng hữu của ta, đến phiên ngươi giáo huấn?', '9', '<p>Cười cười n&oacute;i n&oacute;i, thời gian tr&ocirc;i qua rất nhanh.<br />\r\n<br />\r\nTrong qu&aacute;n rượu &acirc;m nhạc khuấy động, bầu kh&ocirc;ng kh&iacute; lửa n&oacute;ng, mọi người dần dần chơi mở.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, Hứa Kiệt c&ugrave;ng Bạch Vi ba người đang chơi tr&ograve; chơi lấy lời thật l&ograve;ng đại mạo hiểm.<br />\r\n<br />\r\nBa người, lấy l&ograve;ng b&agrave;n tay mu b&agrave;n tay đến quyết định!<br />\r\n<br />\r\nTr&ograve; chơi đ&atilde; tiến h&agrave;nh mấy v&ograve;ng, lẫn nhau c&oacute; thắng thua, n&oacute;i ch&uacute;t th&uacute; vị nhưng kh&ocirc;ng ảnh hưởng to&agrave;n cục lời thật l&ograve;ng.<br />\r\n<br />\r\nKh&ocirc;ng muốn n&oacute;i, liền lựa chọn đại mạo hiểm.<br />\r\n<br />\r\nMột lần th&uacute; vị nhất, Dư Hạo để Bạch Vi đi ghẹo muội tử.<br />\r\n<br />\r\nKết quả, chẳng ai ngờ rằng, Bạch Vi v&acirc;̣y mà th&agrave;nh c&ocirc;ng!<br />\r\n<br />\r\nC&aacute;i kia mỹ nữ bất hạnh được tuyển chọn bị n&agrave;ng ghẹo mặt đỏ tới mang tai, dẫn tới mọi người một trận &ocirc;m bụng cười to.<br />\r\n<br />\r\nM&agrave; lần n&agrave;y, đến phi&ecirc;n Dư Hạo.<br />\r\n<br />\r\nHứa Kiệt c&ugrave;ng Bạch Vi cao hứng cười ha ha.<br />\r\n<br />\r\nSau đ&oacute; Hứa Kiệt ra c&aacute;i chủ &yacute; ngu ngốc, để Dư Hạo n&oacute;i ra đối tượng xu&acirc;n mộng lần thứ nhất.<br />\r\n<br />\r\nVấn đề n&agrave;y để Dư Hạo mặt đỏ giống gan heo.<br />\r\n<br />\r\nVương Đằng cười lắc đầu, hắn cũng kh&ocirc;ng c&oacute; tham dự, d&ugrave; sao b&iacute; mật của hắn qu&aacute; nhiều, chơi loại tr&ograve; chơi n&agrave;y rất dễ d&agrave;ng chơi tho&aacute;t.<br />\r\n<br />\r\nHứa Kiệt Bạch Vi hai người ở một b&ecirc;n d&ugrave;ng sức ồn &agrave;o th&uacute;c giục, để Dư Hạo mau n&oacute;i.<br />\r\n<br />\r\nVương Đằng đứng người l&ecirc;n, đi m&ocirc;̣t chuy&ecirc;́n nh&agrave; vệ sinh.<br />\r\n<br />\r\nBan đ&ecirc;m ăn tinh th&uacute; thịt, c&oacute; trợ gi&uacute;p trao đổi chất, đem thể nội tạp chất c&ugrave;ng nhau b&agrave;i xuất.<br />\r\n<br />\r\nM&agrave; lại trong qu&aacute;n bar m&ugrave;i kh&oacute;i, cũng l&agrave;m cho hắn mắc nghiện thuốc.<br />\r\n<br />\r\nKiếp trước hắn kh&ocirc;ng biết từ l&uacute;c n&agrave;o bắt đầu, một ng&agrave;y kh&ocirc;ng h&uacute;t thuốc l&aacute; liền to&agrave;n th&acirc;n kh&oacute; chịu, muốn cai lại cai kh&ocirc;ng xong.<br />\r\n<br />\r\nKh&ocirc;ng đầy một l&aacute;t, hắn từ nh&agrave; vệ sinh ra, lại đi b&ecirc;n ngo&agrave;i mua bao thuốc thấp k&eacute;m.<br />\r\n<br />\r\nTốt c&ograve;n h&uacute;t kh&ocirc;ng quen!<br />\r\n<br />\r\nVương Đằng miệng b&ecirc;n trong ngậm lấy điếu thuốc, hướng Hứa Kiệt mấy người đi đến.<br />\r\n<br />\r\nMới vừa đi tới chỗ gần, lại ph&aacute;t hiện bọn hắn bị mấy người v&acirc;y quanh, Hứa Kiệt, Dư Hạo tựa hồ đang c&ugrave;ng đ&aacute;m người kia c&atilde;i lộn.<br />\r\n<br />\r\nĐ&aacute;m người kia d&aacute;ng vẻ lưu manh, mang theo v&ograve;ng tai, tr&ecirc;n th&acirc;n c&ograve;n hoa văn h&igrave;nh xăm, một bộ &quot;Lão tử rất kh&oacute; d&acirc;y v&agrave;o&quot; d&aacute;ng vẻ.<br />\r\n<br />\r\nVương Đằng kh&ocirc;ng khỏi nh&iacute;u m&agrave;y.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, trong đ&oacute; một người x&ocirc; đẩy Hứa Kiệt một ch&uacute;t, h&ugrave;ng h&ugrave;ng hổ hổ n&oacute;i ra: &quot;M&atilde;, tiểu nha đầu n&agrave;y đem y phục của ta l&agrave;m bẩn, ngươi một c&acirc;u thật xin lỗi, liền nghĩ xong việc?&quot;<br />\r\n<br />\r\n&quot;Ngươi biết y phục n&agrave;y bao nhi&ecirc;u tiền kh&ocirc;ng?&quot;<br />\r\n<br />\r\n&quot;Bồi thường nổi sao?&quot;<br />\r\n<br />\r\n&quot;Mấy c&aacute;i tiểu th&iacute; h&agrave;i, cũng học đại nh&acirc;n đến qu&aacute;n bar chơi.&quot;<br />\r\n<br />\r\nN&oacute;i b&ecirc;n cạnh mấy người cười l&ecirc;n ha hả, tr&ecirc;n mặt lộ ra đ&ugrave;a cợt biểu lộ.<br />\r\n<br />\r\nDư Hạo mấy người đ&acirc;u chịu nổi loại n&agrave;y kh&iacute;.<br />\r\n<br />\r\nBọn hắn những n&agrave;y ph&uacute; nhị đại, vốn l&agrave; nu&ocirc;ng chiều từ b&eacute;, đi tới chỗ n&agrave;o đều l&agrave; đối tượng người kh&aacute;c nịnh nọt lấy l&ograve;ng.<br />\r\n<br />\r\nNếu l&agrave; người th&acirc;n phận địa vị cao hơn bọn họ, chịu một ch&uacute;t ủy khuất cũng liền th&ocirc;i, thế nhưng l&agrave; mấy c&aacute;i n&agrave;y r&otilde; r&agrave;ng cũng kh&ocirc;ng biết tiểu l&acirc;u la từ b&ecirc;n trong c&aacute;i n&agrave;o sừng th&uacute; u cục xuất hiện.<br />\r\n<br />\r\nNếu l&agrave; quả thật bị dạng người n&agrave;y khi dễ, truyền đi sợ l&agrave; đều sẽ trở th&agrave;nh tr&ograve; cười của ph&uacute; nhị đại kh&aacute;c b&ecirc;n trong v&ograve;ng tr&ograve;n.<br />\r\n<br />\r\nHứa Kiệt bị người đẩy một ch&uacute;t, sắc mặt c&agrave;ng l&agrave; trầm xuống.<br />\r\n<br />\r\nBất qu&aacute; chớ nh&igrave;n hắn b&igrave;nh thường một bộ t&ugrave;y tiện, l&atilde;o thi&ecirc;n thứ nhất ta thứ hai d&aacute;ng vẻ, tr&ecirc;n thực tế cũng kh&ocirc;ng phải người kh&ocirc;ng n&atilde;o g&igrave; đ&oacute;.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, đối phương r&otilde; r&agrave;ng nhiều người, m&agrave; trong bọn họ c&ograve;n c&oacute; nữ sinh tại.<br />\r\n<br />\r\nL&ecirc;n xung đột, thua thiệt khẳng định l&agrave; bọn hắn.<br />\r\n<br />\r\nLại n&oacute;i b&acirc;y giờ thế nhưng l&agrave; v&otilde; đạo thời đại, c&oacute; &iacute;t người thoạt nh&igrave;n bề ngo&agrave;i kh&ocirc;ng đẹp, vạn nhất l&agrave; cao thủ l&agrave;m sao b&acirc;y giờ?<br />\r\n<br />\r\nVậy kh&ocirc;ng phải ngỏm củ tỏi!<br />\r\n<br />\r\nCho n&ecirc;n hắn &acirc;m thầm h&iacute;t một hơi thật s&acirc;u, cố gắng b&igrave;nh tĩnh n&oacute;i ra: &quot;Mấy vị, coi như l&agrave;m bẩn ngươi quần &aacute;o l&agrave; ch&uacute;ng ta kh&ocirc;ng đ&uacute;ng trước, c&ugrave;ng lắm ch&uacute;ng ta bồi thường tiền l&agrave; được.&quot;<br />\r\n<br />\r\n&quot;C&aacute;c ngươi như thế h&ugrave;ng hổ dọa người, thật chẳng lẽ muốn ồn &agrave;o đ&ecirc;́n kh&ocirc;ng cách nào thu thập kh&ocirc;ng được?&quot;<br />\r\n<br />\r\n&quot;Người đang ngồi đều nh&igrave;n r&otilde; r&otilde; r&agrave;ng r&agrave;ng, coi như t&igrave;m cảnh s&aacute;t đến điều giải, ch&uacute;ng ta cũng l&agrave; chiếm lý một phương.&quot;<br />\r\n<br />\r\n&quot;Mẹ, vẫn c&ograve;n muốn t&igrave;m cảnh s&aacute;t, uy hiếp ta đ&uacute;ng kh&ocirc;ng?&quot;<br />\r\n<br />\r\nC&aacute;i kia bị l&agrave;m quần &aacute;o bẩn ch&iacute;nh l&agrave; c&aacute;i hai mươi tuổi thanh ni&ecirc;n, mũi ưng, dung mạo hung &aacute;c nham hiểm, ho&agrave;n to&agrave;n ch&iacute;nh l&agrave; c&aacute;i lưu manh, cảm thấy Hứa Kiệt l&agrave; đang uy hiếp hắn, lập tức mắng to l&ecirc;n.<br />\r\n<br />\r\n&quot;Hứa Kiệt, c&ugrave;ng hắn n&oacute;i nhảm c&aacute;i g&igrave;, c&aacute;i n&agrave;y hỗn đản r&otilde; r&agrave;ng ch&iacute;nh l&agrave; ở kh&ocirc;ng đi g&acirc;y sự, căn bản kh&ocirc;ng phải Bạch Vi đụng phải hắn, m&agrave; l&agrave; hắn cố &yacute; tại Bạch Vi đứng dậy thời điểm đi tới, để Bạch Vi đụng v&agrave;o.&quot; Dư ch&iacute;nh kh&iacute; phẫn n&oacute;i.<br />\r\n<br />\r\n&quot;Tiểu tử, chuyện kh&ocirc;ng c&oacute; chứng cớ, cũng kh&ocirc;ng n&ecirc;n n&oacute;i lung tung.&quot; Hung &aacute;c nham hiểm thanh ni&ecirc;n cười lạnh n&oacute;i.<br />\r\n<br />\r\n&quot;C&oacute; phải l&agrave; chuyện như vậy, ch&iacute;nh ngươi trong l&ograve;ng r&otilde; r&agrave;ng&quot; Dư Hạo đứng ra chỉ v&agrave;o hắn nói.<br />\r\n<br />\r\nHung &aacute;c nham hiểm thanh ni&ecirc;n một mặt kh&ocirc;ng ki&ecirc;n nhẫn, hừ lạnh n&oacute;i: &quot;&Iacute;t mẹ n&oacute; n&oacute;i nhảm, để c&ocirc; n&agrave;ng n&agrave;y c&ugrave;ng ch&uacute;ng ta uống v&agrave;i ch&eacute;n rượu, chuyện n&agrave;y th&igrave; th&ocirc;i, bằng kh&ocirc;ng th&igrave;... Hắc hắc.&quot;<br />\r\n<br />\r\n&quot;Ngươi n&oacute;i c&aacute;i g&igrave;, lặp lại lần nữa, c&oacute; tin ta hay kh&ocirc;ng chơi chết ngươi?&quot; Dư Hạo lập tức liền nổ.<br />\r\n<br />\r\nNhững người n&agrave;y v&acirc;̣y mà muốn để Bạch Vi bồi rượu, rắp t&acirc;m l&agrave;m g&igrave; nghĩ kh&ocirc;ng cần nghĩ cũng biết.<br />\r\n<br />\r\nLần n&agrave;y l&agrave; thật đem hắn l&agrave;m ph&aacute;t bực.<br />\r\n<br />\r\nHứa Kiệt lập tức n&oacute;i thầm một tiếng &quot;Hỏng b&eacute;t &quot;!<br />\r\n<br />\r\nĐối phương r&otilde; r&agrave;ng l&agrave; cho bọn hắn gài b&acirc;̃y, Dư Hạo c&aacute;i n&agrave;y một nổ, sự t&igrave;nh cũng liền trở n&ecirc;n kh&ocirc;ng thể điều h&ograve;a.<br />\r\n<br />\r\n&quot;Dư Hạo, đừng n&oacute;i!&quot; Bạch Vi vừa mới một mực l&ocirc;i k&eacute;o Dư Hạo, vừa kh&ocirc;ng c&oacute; ch&uacute; &yacute; liền bị hắn tr&aacute;nh ra khỏi.<br />\r\n<br />\r\nGiờ ph&uacute;t n&agrave;y n&agrave;ng r&otilde; r&agrave;ng cảm gi&aacute;c được bầu kh&ocirc;ng kh&iacute; một trận căng cứng.<br />\r\n<br />\r\n&quot;Tiểu s&uacute;c sinh, c&ograve;n muốn chơi chết ta, l&atilde;o tử h&ocirc;m nay trước chơi chết ngươi.&quot;<br />\r\n<br />\r\nHung &aacute;c nham hiểm thanh ni&ecirc;n một trận nhe răng cười, bước ch&acirc;n hướng ph&iacute;a trước đạp mạnh, hai tay như ưng trảo, chụp v&agrave;o cổ Dư Hạo.<br />\r\n<br />\r\nQuả thực t&agrave;n nhẫn, vừa ra tay ch&iacute;nh l&agrave; ngoan chi&ecirc;u.<br />\r\n<br />\r\n&quot;V&otilde; Đồ!&quot;<br />\r\n<br />\r\nC&oacute; người hoảng sợ n&oacute;i.<br />\r\n<br />\r\nDư Hạo sắc mặt cũng l&agrave; đột nhi&ecirc;n biến đổi, một c&aacute;i l&agrave; kh&ocirc;ng nghĩ tới đối phương n&oacute;i động thủ liền động thủ, thứ hai c&agrave;ng l&agrave; kh&ocirc;ng nghĩ tới đối phương đ&uacute;ng l&agrave; c&aacute;i V&otilde; Đồ.<br />\r\n<br />\r\nVội v&agrave;ng ph&iacute;a dưới, hắn căn bản kh&ocirc;ng c&oacute; dũng kh&iacute; ch&iacute;nh diện ngạnh kh&aacute;ng, chỉ c&oacute; thể hướng b&ecirc;n cạnh n&eacute; tr&aacute;nh.<br />\r\n<br />\r\nMay mắn Dư Hạo gần nhất bị cha hắn nắm lấy luyện v&otilde;, b&acirc;y giờ đ&atilde; l&agrave; c&acirc;́p th&acirc;́p V&otilde; Đồ, kh&ocirc;ng phải nếu l&agrave; đổi th&agrave;nh người b&igrave;nh thường.<br />\r\n<br />\r\nLần n&agrave;y đừng n&oacute;i l&agrave; tr&aacute;nh, chỉ sợ tại chỗ liền sẽ bị đối phương hung &aacute;c kh&iacute; thế dọa ngơ tại chỗ.<br />\r\n<br />\r\nTrong qu&aacute;n rượu lập tức loạn cả một đo&agrave;n.<br />\r\n<br />\r\nHứa Kiệt vội v&agrave;ng l&ocirc;i k&eacute;o Bạch Vi n&eacute; tr&aacute;nh, bọn hắn kh&ocirc;ng phải người luyện v&otilde;, l&uacute;c n&agrave;y ho&agrave;n to&agrave;n gi&uacute;p kh&ocirc;ng được g&igrave;.<br />\r\n<br />\r\nHung &aacute;c nham hiểm thanh ni&ecirc;n thấy Dư Hạo c&oacute; thể n&eacute; tr&aacute;nh, hơi kinh ngạc một ch&uacute;t, lại lơ đễnh, th&acirc;n thể uốn &eacute;o, một c&aacute;i qu&eacute;t đường ch&acirc;n hướng Dư Hạo qu&eacute;t ngang m&agrave; tới.<br />\r\n<br />\r\nDư Hạo bận bịu nắm l&ecirc;n c&aacute;i ghế b&ecirc;n cạnh, ngăn trở c&ocirc;ng k&iacute;ch của đối phương.<br />\r\n<br />\r\nBành!<br />\r\n<br />\r\nC&aacute;i ghế bằng sắt trực tiếp bị hung &aacute;c nham hiểm thanh ni&ecirc;n một ch&acirc;n đ&aacute; cong.<br />\r\n<br />\r\nDư Hạo cũng tại dưới cường đại k&igrave;nh lực t&aacute;c dụng, bay ngang ra ngo&agrave;i, tr&ugrave;ng điệp ng&atilde; xuống đất.<br />\r\n<br />\r\nC&aacute;i b&agrave;n, c&aacute;i ghế bị nện ng&atilde; tr&aacute;i ng&atilde; phải.<br />\r\n<br />\r\nB&igrave;nh rượu loảng xoảng coong coong n&aacute;t một chỗ, to&agrave;n bộ qu&aacute;n bar trong nh&aacute;y mắt g&agrave; bay ch&oacute; chạy.<br />\r\n<br />\r\nC&aacute;c nam nh&acirc;n nữ nh&acirc;n sợ h&atilde;i k&ecirc;u lấy trốn đến nơi xa.<br />\r\n<br />\r\nNhưng l&agrave; rất nhiều người kh&ocirc;ng hề rời đi, ngược lại ở ph&iacute;a xa nh&igrave;n l&ecirc;n n&aacute;o nhiệt.<br />\r\n<br />\r\n&quot;Khụ khụ!&quot; Dư Hạo che ngực kh&ocirc; khốc một hồi khục, cảm gi&aacute;c xương sườn giống như l&agrave; g&atilde;y mất đồng dạng.<br />\r\n<br />\r\nHắn biết lần n&agrave;y l&agrave; đụng phải cọng rơm cứng!<br />\r\n<br />\r\nHắn mới v&agrave;o V&otilde; Đồ, m&agrave; từ đối phương lực đạo đến xem, mặc d&ugrave; cũng tại c&acirc;́p th&acirc;́p V&otilde; Đồ phạm tr&ugrave; b&ecirc;n trong, nhưng kh&ocirc;ng sai biệt lắm tiếp cận trung cấp.<br />\r\n<br />\r\nHung &aacute;c nham hiểm thanh ni&ecirc;n thu hồi ch&acirc;n, chậm r&atilde;i đi hướng Dư Hạo.<br />\r\n<br />\r\n&quot;Tiểu tử, vừa mới kh&ocirc;ng phải rất ph&aacute;ch lối sao? Muốn l&agrave;m chết ta, thực lực của ngươi kh&ocirc;ng đủ a.&quot;<br />\r\n<br />\r\nHung &aacute;c nham hiểm thanh ni&ecirc;n từng bước một đem Dư Hạo bức đến g&oacute;c tường, tr&ecirc;n cao nh&igrave;n xuống quan s&aacute;t hắn, đ&ugrave;a cợt n&oacute;i.<br />\r\n<br />\r\n&quot;Ngươi d&aacute;m đụng đến ta, cha ta sẽ kh&ocirc;ng bỏ qua cho ngươi.&quot; Dư Hạo cắn răng, sắc mặt t&aacute;i nhợt n&oacute;i.<br />\r\n<br />\r\n&quot;Tiểu bằng hữu, đ&aacute;nh kh&ocirc;ng thắng t&igrave;m ba ba, c&ograve;n kh&ocirc;ng c&oacute; dứt sữa đ&acirc;u.&quot; Hung &aacute;c nham hiểm thanh ni&ecirc;n cười khẩy n&oacute;i.<br />\r\n<br />\r\n&quot;Ngươi!&quot;<br />\r\n<br />\r\nDư Hạo cả khu&ocirc;n mặt thẹn đến đỏ bừng.<br />\r\n<br />\r\nHứa Kiệt tự nhi&ecirc;n kh&ocirc;ng thể nh&igrave;n bằng hữu chịu nhục, đứng ra n&oacute;i: &quot;H&ocirc;m nay coi như ch&uacute;ng ta t&eacute; ng&atilde;, ngươi n&oacute;i t&iacute;nh ra đến, ch&uacute;ng ta bồi thường.&quot;<br />\r\n<br />\r\nHung &aacute;c nham hiểm thanh ni&ecirc;n lắc lắc ng&oacute;n trỏ, miệng b&ecirc;n trong ph&aacute;t ra chậc chậc chậc thanh &acirc;m.<br />\r\n<br />\r\nKhắp khu&ocirc;n mặt l&agrave; khinh thường!<br />\r\n<br />\r\n&quot;Tiểu th&iacute; h&agrave;i, h&ocirc;m nay ta dạy cho ngươi c&aacute;i ngoan, kh&ocirc;ng c&oacute; bản sự cũng đừng loạn ra mặt.&quot; Hung &aacute;c nham hiểm thanh ni&ecirc;n n&acirc;ng l&ecirc;n ch&acirc;n phải, hướng về Dư Hạo đầu hung hăng đ&aacute; tới.<br />\r\n<br />\r\nMột số người kh&ocirc;ng đ&agrave;nh l&ograve;ng nhắm mắt lại.<br />\r\n<br />\r\nHứa Kiệt c&ugrave;ng Bạch Vi sắc mặt bỗng nhi&ecirc;n đại biến.<br />\r\n<br />\r\nMột cước n&agrave;y xuống dưới, Dư Hạo khẳng định phải trọng thương.<br />\r\n<br />\r\nHơn nữa c&ograve;n l&agrave; đ&aacute; v&agrave;o tr&ecirc;n đầu, vạn nhất c&oacute; nguy hiểm, đời n&agrave;y coi như xong.<br />\r\n<br />\r\nBọn hắn thực tế kh&ocirc;ng nghĩ tới, c&aacute;i n&agrave;y hung &aacute;c nham hiểm thanh ni&ecirc;n vậy m&agrave; lại như thế hung &aacute;c.<br />\r\n<br />\r\nBành!<br />\r\n<br />\r\nNặng nề tiếng va chạm vang l&ecirc;n l&ecirc;n.<br />\r\n<br />\r\nBất qu&aacute; Dư Hạo lại c&ograve;n tại tại chỗ, l&ocirc;ng t&oacute;c kh&ocirc;ng thương, m&agrave; hung &aacute;c nham hiểm thanh ni&ecirc;n lại lập tức bay ra ngo&agrave;i, tr&ugrave;ng điệp đ&acirc;m v&agrave;o tr&ecirc;n tường.<br />\r\n<br />\r\n&quot;Phốc!&quot; Hắn ng&atilde; xuống đất, một ngụm m&aacute;u tươi phun tới.<br />\r\n<br />\r\nMặc kệ l&agrave; Hứa Kiệt c&ugrave;ng Bạch Vi, vẫn l&agrave; trong qu&aacute;n bar những người kh&aacute;c, đều bị một m&agrave;n bất th&igrave;nh l&igrave;nh kinh ngạc đến ng&acirc;y người.<br />\r\n<br />\r\nTất cả mọi người sững sờ nh&igrave;n xem đứng ở Vương Đằng trước mặt Dư Hạo.<br />\r\n<br />\r\nHắn một tay đ&uacute;t ở trong t&uacute;i, miệng b&ecirc;n trong ngậm điếu thuốc, thản nhi&ecirc;n n&oacute;i: &quot;Bằng hữu của ta, lúc nào đến phi&ecirc;n ngươi gi&aacute;o huấn?&quot;<br />\r\n<br />\r\nVừa mới dứt lời, trong l&ograve;ng của hắn bỗng nhi&ecirc;n nhảy một c&aacute;i!<br />\r\n<br />\r\nT&ecirc;n kia hung &aacute;c nham hiểm thanh ni&ecirc;n rơi tr&ecirc;n mặt đất đồng thời, mấy c&aacute;i thuộc t&iacute;nh bọt kh&iacute; cũng theo đ&oacute; rơi xuống xuống dưới.<br />\r\n<br />\r\n&quot;Dạng n&agrave;y cũng có th&ecirc;̉ bạo thuộc t&iacute;nh?&quot;<br />\r\n<br />\r\nVương Đằng kiềm chế lại trong l&ograve;ng nghi hoặc, bởi v&igrave; qu&aacute; nhiều người, hắn cũng kh&ocirc;ng tiến l&ecirc;n.<br />\r\n<br />\r\n&quot;Vương, Vương Đằng ca!&quot;<br />\r\n<br />\r\nDư Hạo l&uacute;c n&agrave;y phản ứng lại, lắp bắp k&ecirc;u l&ecirc;n.<br />\r\n<br />\r\nHứa Kiệt c&ugrave;ng Bạch Vi cũng ngơ ng&aacute;c nh&igrave;n Vương Đằng, giống như lần thứ nhất biết hắn đồng dạng.<br />\r\n<br />\r\n&quot;Vẫn chưa chịu dậy, tr&ecirc;n mặt đất m&aacute;t mẻ a?&quot; Vương Đằng cười mắng.<br />\r\n<br />\r\n&quot;Vương Đằng ca, kh&ocirc;ng nghĩ tới ngươi lợi hại như vậy!&quot; Bạch Vi chạy chậm tới, trong mắt l&oacute;e l&ecirc;n một ch&uacute;t h&agrave;o quang kh&ocirc;ng dễ d&agrave;ng ph&aacute;t gi&aacute;c.<br />\r\n<br />\r\n&quot;Đại ca, ngươi sau n&agrave;y sẽ l&agrave; ta đại ca ruột, ngươi nhưng giấu cho ch&uacute;ng ta thật khổ a.&quot; Hứa Kiệt k&iacute;ch động h&eacute;t lớn.<br />\r\n<br />\r\n&quot;Được r&ocirc;̀i, quỷ g&agrave;o g&igrave; đ&acirc;u, ta lúc nào giấu diếm c&aacute;c ngươi, l&agrave; c&aacute;c ngươi kh&ocirc;ng c&oacute; hỏi ta m&agrave; th&ocirc;i.&quot; Vương Đằng bất đắc dĩ n&oacute;i.<br />\r\n<br />\r\n&quot;Hỗn trướng, ngươi d&aacute;m đ&aacute;nh ta, l&ecirc;n cho ta, chơi chết tiểu s&uacute;c sinh n&agrave;y!&quot;<br />\r\n<br />\r\nHung &aacute;c nham hiểm thanh ni&ecirc;n từ dưới đất b&ograve; dậy, lau đi m&aacute;u tr&ecirc;n kh&oacute;e miệng, trong mắt ngậm lấy o&aacute;n độc, hướng hắn c&ugrave;ng nhau mấy người kia h&eacute;t lớn.<br />\r\n<br />\r\n&quot;Đại, đại ca, tiểu tử n&agrave;y qu&aacute; lợi hại, ch&uacute;ng ta kh&ocirc;ng phải l&agrave; đối thủ a!&quot; Một người e ngại nh&igrave;n xem Vương Đằng nói.<br />\r\n<br />\r\nBọn hắn c&ograve;n kh&ocirc;ng c&oacute; nh&igrave;n thấy Vương Đằng l&agrave;m sao ra tay, hung &aacute;c nham hiểm thanh ni&ecirc;n thực lực tiếp cận trung cấp V&otilde; Đồ liền ng&atilde; bay ra ngo&agrave;i, bọn hắn những n&agrave;y kh&ocirc;ng phải V&otilde; Đồ người có th&ecirc;̉ đỉnh c&aacute;i g&igrave; d&ugrave;ng?<br />\r\n<br />\r\n&quot;Phế vật!&quot; Hung &aacute;c nham hiểm thanh ni&ecirc;n hận hận đ&aacute; đối phương một cước, x&ocirc;ng Vương Đằng n&oacute;i: &quot;Tiểu tử, c&oacute; gan b&aacute;o danh ra, chờ ta gọi người tới.&quot;<br />\r\n<br />\r\nL&uacute;c n&agrave;y Vương Đằng đột nhi&ecirc;n đi l&ecirc;n trước, dọa đến hung &aacute;c nham hiểm thanh ni&ecirc;n kh&ocirc;ng khỏi r&uacute;t lui mấy bước.<br />\r\n<br />\r\nVương Đằng lơ đ&atilde;ng đụng v&agrave;o mấy c&aacute;i kia bọt kh&iacute;, đem n&oacute; nhặt.<br />\r\n<br />\r\n【 lực lượng *15 】<br />\r\n<br />\r\n【 tốc độ *12 】<br />\r\n<br />\r\n&quot;Nhiều như vậy!&quot;<br />\r\n<br />\r\nVương Đằng hơi kinh ngạc, đ&acirc;y coi như l&agrave; c&ugrave;ng l&agrave; tr&ecirc;n th&acirc;n một người, l&agrave; một lần rơi xuống nhiều nhất.<br />\r\n<br />\r\n&quot;Đ&aacute;nh qu&aacute;i bạo thuộc t&iacute;nh!&quot;<br />\r\n<br />\r\n&quot;Chẳng lẽ đ&acirc;y mới l&agrave; phương thức ch&iacute;nh x&aacute;c mở ra b&agrave;n tay v&agrave;ng?&quot;<br />\r\n<br />\r\nHắn chỉ l&agrave; suy nghĩ một ch&uacute;t liền đem chuyện n&agrave;y bu&ocirc;ng xuống, thời cơ r&otilde; r&agrave;ng kh&ocirc;ng đ&uacute;ng, hiện tại cũng kh&ocirc;ng phải thời điểm suy nghĩ c&aacute;i n&agrave;y.<br />\r\n<br />\r\nVương Đằng kh&oacute;e miệng khẽ nhếch, n&oacute;i.<br />\r\n<br />\r\n&quot;Ngươi sợ c&aacute;i g&igrave;, ta gọi L&yacute; Vinh Th&agrave;nh, cho ngươi nửa giờ gọi người, qu&aacute; hạn kh&ocirc;ng đợi!&quot;<br />\r\n<br />\r\n&quot;Tốt, ngươi chờ đ&oacute; cho ta.&quot; Hung &aacute;c nham hiểm thanh ni&ecirc;n mang theo người x&aacute;m xịt rời đi Tường Vi qu&aacute;n bar.<br />\r\n<br />\r\nBọn người vừa đi, Bạch Vi nhịn kh&ocirc;ng được cười l&ecirc;n.<br />\r\n<br />\r\n&quot;Vương Đằng ca, ngươi qu&aacute; xấu đi, thế m&agrave; để L&yacute; Vinh Th&agrave;nh chịu oan ức, nếu như hắn biết, khẳng định hận chết ngươi!&quot;<br />\r\n<br />\r\nVương Đằng nh&uacute;n vai.<br />\r\n<br />\r\n&quot;Đi rồi, về nh&agrave;!&quot;<br />\r\n<br />\r\n&quot;Ch&uacute;ng ta kh&ocirc;ng chờ bọn họ trở về sao?&quot; Dư Hạo kh&ocirc;ng cam t&acirc;m m&agrave; hỏi.<br />\r\n<br />\r\nVương Đằng một b&agrave;n tay vỗ tại tr&ecirc;n đầu của hắn: &quot;Luyện v&otilde; luyện ngốc hả ngươi, ai biết bọn hắn sẽ t&igrave;m bao nhi&ecirc;u người đến, vạn nhất c&oacute; cao cấp V&otilde; Đồ l&agrave;m sao b&acirc;y giờ? Thậm ch&iacute; V&otilde; Giả, ch&uacute;ng ta c&ograve;n ngốc ngốc lưu tại nơi n&agrave;y, chẳng phải l&agrave; chờ chết?&quot;<br />\r\n<br />\r\n&quot;Đúng đ&acirc;́y, ngươi tiểu tử n&agrave;y, qu&aacute; x&uacute;c động, lần n&agrave;y cần kh&ocirc;ng phải Vương Đằng ca, ngươi cần phải thiệt th&ograve;i lớn.&quot; Hứa Kiệt cũng kh&ocirc;ng nhịn được dạy dỗ.<br />\r\n<br />\r\n&quot;Đi th&ocirc;i đi th&ocirc;i, bọn hắn muốn t&igrave;m t&igrave;m L&yacute; Vinh Th&agrave;nh đi, ta lại kh&ocirc;ng phải L&yacute; Vinh Th&agrave;nh.&quot;<br />\r\n<br />\r\nVương Đằng mấy người đi ra qu&aacute;n bar, mở xe, chỉ chớp mắt liền tiến v&agrave;o trong b&oacute;ng đ&ecirc;m, đ&aacute;m người kia muốn t&igrave;m được hắn, đo&aacute;n chừng phải xem vận kh&iacute;.<br />\r\n<br />\r\nVề phần t&igrave;m tới hắn về sau, đến c&ugrave;ng l&agrave; vận may vẫn l&agrave; điều xấu? Vậy liền kh&oacute; m&agrave; n&oacute;i...</p>', '2021-11-28 13:34:05', 7, 1),
(218, 58, 'Yêu nhìn chăm chú đến từ lão phụ thân', '10', '<p>Vương Đằng mấy người vừa đi, nh&acirc;n vi&ecirc;n c&ocirc;ng t&aacute;c nh&igrave;n xem một mảnh hỗn độn qu&aacute;n bar, hỏi một c&aacute;i nam tử ngo&agrave;i ba mươi, mặc t&acirc;y trang n&oacute;i.<br />\r\n<br />\r\n&quot;Quản l&yacute;, cứ như vậy thả bọn họ đi rồi?&quot;<br />\r\n<br />\r\n&Acirc;u phục nam tử trừng nh&acirc;n vi&ecirc;n n&agrave;y một ch&uacute;t, tức giận n&oacute;i: &quot;Kh&ocirc;ng phải l&agrave;m sao b&acirc;y giờ? Ngươi đi giữ bọn họ lại?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; ch&uacute;t nh&atilde;n lực nh&igrave;n!<br />\r\n<br />\r\n&quot;C&aacute;i kia Triệu Cương B&aacute;o l&agrave; c&aacute;i cấp thấp V&otilde; Đồ, m&agrave; hắn ca c&agrave;ng l&agrave; cao cấp V&otilde; Đồ!&quot;<br />\r\n<br />\r\n&quot;Về phần mấy c&aacute;i kia ph&uacute; nhị đại, trong nh&agrave; c&aacute;i n&agrave;o kh&ocirc;ng phải t&agrave;i sản hơn trăm triệu, chủ yếu nhất ch&iacute;nh l&agrave; bọn hắn b&ecirc;n trong cũng c&oacute; hai c&aacute;i V&otilde; Đồ, bị đ&aacute;nh ng&atilde; c&aacute;i kia liền kh&ocirc;ng n&oacute;i, vừa mới xuất thủ c&aacute;i kia tối thiểu nhất cũng l&agrave; trung cấp V&otilde; Đồ.&quot;<br />\r\n<br />\r\n&quot;C&ograve;n trẻ như vậy trung cấp V&otilde; Đồ, ngươi biết &yacute; vị như thế n&agrave;o sao?&quot;<br />\r\n<br />\r\n&quot;Mang &yacute; nghĩa hắn về sau thấp nhất cũng l&agrave; cao cấp V&otilde; Đồ a, ngươi c&aacute;i tẩy nh&agrave;o n&ecirc;n! Muốn chết cũng đừng l&ocirc;i k&eacute;o ta rồi!&quot;<br />\r\n<br />\r\nT&ecirc;n kia nh&acirc;n vi&ecirc;n c&ocirc;ng t&aacute;c bị n&oacute;i &aacute; khẩu kh&ocirc;ng trả lời được, trong nội t&acirc;m lập tức phục s&aacute;t đất... Nguy&ecirc;n lai quản l&yacute; đ&atilde; sớm đem lai lịch của đối phương tra r&otilde; r&agrave;ng.<br />\r\n<br />\r\nKh&oacute; tr&aacute;ch người ta c&oacute; thể l&agrave;m quản l&yacute;, m&agrave; m&igrave;nh chỉ l&agrave; c&aacute;i trong suốt nhỏ l&agrave;m việc vặt!<br />\r\n<br />\r\nĐụng một c&aacute;i đến chuyện, ch&ecirc;nh lệch n&agrave;y cũng kh&ocirc;ng l&ograve;i ra.<br />\r\n<br />\r\n&quot;Vậy b&acirc;y giờ ch&uacute;ng ta l&agrave;m sao b&acirc;y giờ?&quot; Nh&acirc;n vi&ecirc;n c&ocirc;ng t&aacute;c lập tức khi&ecirc;m tốn thỉnh gi&aacute;o.<br />\r\n<br />\r\n&quot;Chờ xem, c&aacute;i kia Triệu Cương B&aacute;o kh&ocirc;ng phải đi gọi người sao? Lấy hắn c&oacute; th&ugrave; tất b&aacute;o t&iacute;nh c&aacute;ch, một hồi khẳng định sẽ trở về, kh&ocirc;ng chừng lại ph&aacute; hư ch&uacute;t g&igrave;, chờ bọn hắn đi lại c&ugrave;ng nhau kiểm k&ecirc; tổn thất.&quot;<br />\r\n<br />\r\n&quot;T&ecirc;n vương b&aacute;t đản n&agrave;y, g&acirc;y ai kh&ocirc;ng tốt hết lần n&agrave;y tới lần kh&aacute;c đi g&acirc;y mấy c&aacute;i kia ph&uacute; nhị đại, ngươi tin hay kh&ocirc;ng hắn lần n&agrave;y khẳng định kh&ocirc;ng chiếm được lợi &iacute;ch?&quot;<br />\r\n<br />\r\n&quot;Coi như ca hắn tự m&igrave;nh đến, đều chưa hẳn hữu dụng.&quot; &Acirc;u phục nam tử mắng to l&ecirc;n.<br />\r\n<br />\r\nNghĩ nghĩ, lại n&oacute;i.<br />\r\n<br />\r\n&quot;Tất cả tổn thất đều cho ta ghi tạc tr&ecirc;n đầu của hắn, mặt kh&aacute;c, gi&aacute;m s&aacute;t trong qu&aacute;n rượu nhất định phải bảo tồn tốt, c&aacute;i n&agrave;y đều l&agrave; chứng cứ, ng&agrave;n vạn kh&ocirc;ng thể mất, chờ ch&uacute;t ta một phần, ph&aacute;t Wechat cho l&atilde;o bản nh&igrave;n xem.&quot;<br />\r\n<br />\r\n&quot;Chuyện n&agrave;y li&ecirc;n lụy đến mấy c&aacute;i V&otilde; Đồ, ch&uacute;ng ta người b&igrave;nh thường kh&ocirc;ng xen tay v&agrave;o được, chỉ c&oacute; thể cho l&atilde;o bản định đoạt.&quot;<br />\r\n<br />\r\n...<br />\r\n<br />\r\nVương Đằng mấy người rời đi về sau, cũng kh&ocirc;ng c&oacute; h&agrave;o hứng lại đi địa phương kh&aacute;c chơi, liền ri&ecirc;ng ph&acirc;̀n mình về nh&agrave;.<br />\r\n<br />\r\nVề đến nh&agrave;, mười giờ hơn.<br />\r\n<br />\r\nVương Đằng kh&ocirc;ng tiếp tục đi Cực Tinh v&otilde; qu&aacute;n, trong nh&agrave; l&ecirc;n mạng, lật qua tư liệu, l&agrave;m s&acirc;u sắc đối với v&otilde; đạo thời đại hiểu r&otilde;.<br />\r\n<br />\r\nĐ&ecirc;m nay chuyện n&agrave;y, cũng coi l&agrave; cho hắn g&otilde; vang một c&aacute;i cảnh b&aacute;o.<br />\r\n<br />\r\nT&ugrave;y tiện đụng phải một c&aacute;i người tiểu lưu manh như thế, thực lực đều cơ hồ tiếp cận trung cấp V&otilde; Đồ.<br />\r\n<br />\r\nNếu kh&ocirc;ng phải hắn hai ng&agrave;y n&agrave;y liều mạng nhặt thuộc t&iacute;nh, xo&aacute;t đẳng cấp, đ&ecirc;m nay khẳng định thu kh&ocirc;ng được trận, muốn ủ th&agrave;nh đại họa.<br />\r\n<br />\r\nMặc d&ugrave; người luyện v&otilde;, tại b&ecirc;n trong mấy tỉ nh&acirc;n khẩu x&aacute;c thực chiếm so kh&ocirc;ng cao.<br />\r\n<br />\r\nNhưng vấn đề l&agrave;, đem bọn hắn nh&eacute;t v&agrave;o trong bể người, ngươi ho&agrave;n to&agrave;n kh&ocirc;ng biết l&uacute;c n&agrave;o sẽ đen đủi đụng tới.<br />\r\n<br />\r\nVề sau vạn nhất chọc tới c&aacute;i ch&iacute;nh thức V&otilde; Giả đ&acirc;y?<br />\r\n<br />\r\nNgười ta chẳng phải l&agrave; động động ng&oacute;n tay, liền c&oacute; thể để cho m&igrave;nh c&ugrave;ng Vương gia thương c&acirc;n động cốt.<br />\r\n<br />\r\nĐời trước vết xe đổ, hắn kh&ocirc;ng muốn lại trải qua một lần.<br />\r\n<br />\r\nVương Đằng vừa tr&ugrave;ng sinh trở về, trong l&ograve;ng vốn l&agrave; c&oacute; một loại cảm gi&aacute;c nguy cơ, b&acirc;y giờ cổ nguy cơ n&agrave;y cảm gi&aacute;c c&agrave;ng cường liệt.<br />\r\n<br />\r\nNhất định phải nhanh trở th&agrave;nh ch&iacute;nh thức V&otilde; Giả!<br />\r\n<br />\r\nV&otilde; Đồ, cho d&ugrave; l&agrave; cao cấp V&otilde; Đồ, cũng l&agrave; kh&ocirc;ng đủ.<br />\r\n<br />\r\nChỉ c&oacute; trở th&agrave;nh V&otilde; Giả, mới t&iacute;nh c&oacute; một ch&uacute;t sức tự vệ.<br />\r\n<br />\r\nHơn mười một giờ, Vương Đằng tắm rửa xong, chuẩn bị đi ngủ, cầm điện thoại di động l&ecirc;n chuẩn bị định vị đồng hồ b&aacute;o thức, ng&agrave;y mai phải dậy sớm.<br />\r\n<br />\r\nMở ra điện thoại, biểu hiện Wechat b&ecirc;n tr&ecirc;n thu được hai đầu tin tức.<br />\r\n<br />\r\nThế giới n&agrave;y mặc d&ugrave; mới năm 2009, nhưng đ&atilde; c&oacute; Wechat, m&agrave; lại đ&atilde; sớm phổ cập ra.<br />\r\n<br />\r\nMọi người cũng kh&ocirc;ng c&oacute; việc g&igrave; ph&aacute;t v&ograve;ng người bằng hữu.<br />\r\n<br />\r\nNhất l&agrave; v&ograve;ng bằng hữu V&otilde; Giả, được truy phủng c&ugrave;ng hoan ngh&ecirc;nh nhất, bởi v&igrave; bọn hắn c&oacute; thể th&ocirc;ng qua thời kh&ocirc;ng khe hở tiến v&agrave;o dị giới.<br />\r\n<br />\r\nCho n&ecirc;n chụp kh&ocirc;ng &iacute;t dị thế giới kỳ quan dị cảnh, thậm ch&iacute; dị giới mỹ nữ tr&agrave;n ngập dị vực phong t&igrave;nh...<br />\r\n<br />\r\nHiện nay, phơi quần &aacute;o phơi t&uacute;i x&aacute;ch phơi xe sang c&aacute;i g&igrave;, đều low bạo!<br />\r\n<br />\r\nPhơi một c&aacute;i thế giới mới l&agrave; cấp cao đại kh&iacute; cao cấp!<br />\r\n<br />\r\nVương Đằng nh&igrave;n xuống, hai đầu Wechat, một c&aacute;i l&agrave; hơn một giờ trước ph&aacute;t.<br />\r\n<br />\r\n&quot;Vương Đằng ca, ta tốt.&quot; &mdash;&mdash; đến từ Bạch Vi.<br />\r\n<br />\r\nĐằng sau c&ograve;n đi theo c&aacute;i biểu tượng mặt đ&aacute;ng y&ecirc;u.<br />\r\n<br />\r\nMột c&aacute;i c&aacute;i kh&aacute;c th&igrave; l&agrave; ba ph&uacute;t trước.<br />\r\n<br />\r\n&quot;Vương Đằng ca, ta phải ngủ, ngủ ngon.&quot; &mdash;&mdash; đến từ Bạch Vi.<br />\r\n<br />\r\nĐi theo một c&aacute;i biểu tượng ngủ.<br />\r\n<br />\r\n&quot;Nha đầu n&agrave;y lúc nào như thế c&oacute; lễ ph&eacute;p, n&oacute;i một tiếng về đến nh&agrave; cũng được rồi, ngay cả đi ngủ đều học n&oacute;i với ta ngủ ngon rồi?&quot; Vương Đằng sắc mặt cổ qu&aacute;i.<br />\r\n<br />\r\nHắn nghĩ nghĩ, ba ph&uacute;t trước ph&aacute;t, hiện tại hẳn l&agrave; ngủ đi?<br />\r\n<br />\r\n&Acirc;n, bốn ph&uacute;t!<br />\r\n<br />\r\nVẫn l&agrave; kh&ocirc;ng muốn trả lời, miễn cho lại đem n&agrave;ng đ&aacute;nh thức.<br />\r\n<br />\r\nVương Đằng xo&aacute;t mấy đầu v&ograve;ng bằng hữu, thấy kh&ocirc;ng c&oacute; g&igrave; chuyện th&uacute; vị, liền đưa di động bu&ocirc;ng xuống, nhắm mắt lại đi ngủ.<br />\r\n<br />\r\nMột ph&uacute;t kh&ocirc;ng đến, hắn h&ocirc; hấp dần dần b&igrave;nh ổn, ch&igrave;m v&agrave;o mộng đẹp.<br />\r\n<br />\r\nĐiện thoại b&ecirc;n kia, trong ph&ograve;ng c&ocirc;ng ch&uacute;a m&agrave;u hồng, Bạch Vi chờ nửa ng&agrave;y, kh&ocirc;ng gặp đ&aacute;p lại, gương mặt xinh đẹp b&ecirc;n tr&ecirc;n kh&ocirc;ng khỏi lộ ra vẻ thất vọng.<br />\r\n<br />\r\n&quot;Ba!&quot; N&agrave;ng đem di động quăng ra, ph&aacute;t đi&ecirc;n tr&ecirc;n giường lăn lộn v&agrave;i v&ograve;ng, rất l&agrave; phiền muộn.<br />\r\n<br />\r\n...<br />\r\n<br />\r\nNg&agrave;y thứ hai, chủ nhật.<br />\r\n<br />\r\nVương Đằng lại l&agrave; dậy thật sớm, ăn s&aacute;ng xong, tại l&atilde;o phụ th&acirc;n Vương Thịnh Quốc một mặt vui mừng vẻ mặt rời khỏi nh&agrave;.<br />\r\n<br />\r\n&quot;Rốt cục kh&ocirc;ng cảm gi&aacute;c được c&aacute;i loại &aacute;nh mắt n&agrave;y.&quot;<br />\r\n<br />\r\nXe thể thao mở ra rất xa về sau, Vương Đằng r&ugrave;ng m&igrave;nh một c&aacute;i.<br />\r\n<br />\r\n&quot;Chẳng lẽ đ&acirc;y ch&iacute;nh l&agrave; y&ecirc;u nh&igrave;n chăm ch&uacute; trong truyền thuyết!&quot;<br />\r\n<br />\r\n&quot;T&ecirc;... Khủng bố như vậy!&quot;<br />\r\n<br />\r\nKh&ocirc;ng bao l&acirc;u, Vương Đằng đến Cực Tinh v&otilde; qu&aacute;n.<br />\r\n<br />\r\nB&ecirc;n trong v&otilde; qu&aacute;n tr&ecirc;n mặt cỏ c&ugrave;ng đường vụn vặt lẻ tẻ rơi xuống một &iacute;t thuộc t&iacute;nh bọt kh&iacute;, hắn thuận đường nhặt, sau đ&oacute; đi v&agrave;o V&otilde; Đồ huấn luyện cao ốc.<br />\r\n<br />\r\nLầu một trong đại sảnh c&oacute; mấy t&ecirc;n học vi&ecirc;n đang huấn luyện.<br />\r\n<br />\r\nVương Đằng đại kh&aacute;i nh&igrave;n xuống, cơ bản đều l&agrave; đ&aacute;m người h&ocirc;m qua tới sớm nhất.<br />\r\n<br />\r\nVương Đằng đối bọn hắn ấn tượng vẫn l&agrave; rất s&acirc;u.<br />\r\n<br />\r\nHắn cho l&agrave; m&igrave;nh đ&atilde; đủ sớm, nguy&ecirc;n lai người ta so với hắn sớm hơn, quả nhi&ecirc;n rất chăm chỉ a.<br />\r\n<br />\r\nBất qu&aacute; tại b&ecirc;n tr&ecirc;n đường chăm chỉ n&agrave;y, Vương Đằng ta, l&agrave; sẽ kh&ocirc;ng nhận thua!<br />\r\n<br />\r\nVương Đằng đi tới, lễ ph&eacute;p l&ecirc;n tiếng ch&agrave;o hỏi, đem mấy người rơi xuống thuộc t&iacute;nh một c&aacute;i kh&ocirc;ng lọt nhặt đi.<br />\r\n<br />\r\n【 cơ sở kiếm ph&aacute;p *1 】<br />\r\n<br />\r\n【 cơ sở th&acirc;n ph&aacute;p *1 】<br />\r\n<br />\r\n【 lực lượng *3 】<br />\r\n<br />\r\n【 cơ sở quyền ph&aacute;p *2 】<br />\r\n<br />\r\n【 tốc độ *5 】<br />\r\n<br />\r\n...<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; khởi đầu tốt đẹp a! Sảng kho&aacute;i!!&quot;<br />\r\n<br />\r\nNhặt xong thuộc t&iacute;nh, Vương Đằng kh&ocirc;ng c&oacute; dừng lại, trực tiếp hướng lầu hai đi đến.<br />\r\n<br />\r\nCử động của hắn g&acirc;y n&ecirc;n mấy t&ecirc;n học vi&ecirc;n ch&uacute; &yacute;.<br />\r\n<br />\r\nT&ecirc;n kia luyện kiếm thanh ni&ecirc;n kh&ocirc;ng khỏi l&ecirc;n tiếng nhắc nhở một c&acirc;u: &quot;V&otilde; qu&aacute;n quy định, c&acirc;́p th&acirc;́p V&otilde; Đồ kh&ocirc;ng cho ph&eacute;p l&ecirc;n lầu hai.&quot;<br />\r\n<br />\r\n&quot;Ta biết, tạ ơn nhắc nhở.&quot; Vương Đằng quay đầu lại, cười cười: &quot;Bất qu&aacute;, đ&ecirc;m qua, ta kh&ocirc;ng cẩn thận đột ph&aacute; trung cấp V&otilde; Đồ!&quot;<br />\r\n<br />\r\nN&oacute;i xong đ&atilde; l&ecirc;n thang lầu cấp thứ hai, kh&ocirc;ng nh&igrave;n thấy người.<br />\r\n<br />\r\nB&igrave;nh tĩnh trang xong bức, vung vung l&ecirc;n ống tay &aacute;o, kh&ocirc;ng mang đi một &aacute;ng m&acirc;y ~<br />\r\n<br />\r\nLuyện kiếm thanh ni&ecirc;n ngốc tại chỗ.<br />\r\n<br />\r\n&quot;Hắn, hắn n&oacute;i c&aacute;i g&igrave;?&quot; Hắn c&oacute; ch&uacute;t kh&oacute; c&oacute; thể tin hỏi thăm c&aacute;i kh&aacute;c mấy t&ecirc;n học vi&ecirc;n.<br />\r\n<br />\r\n&quot;Tiểu tử n&agrave;y, l&agrave; h&ocirc;m qua vừa tới a?&quot;<br />\r\n<br />\r\nNhững người kia kh&ocirc;ng c&oacute; trực tiếp trả lời hắn, m&agrave; l&agrave; lại hỏi một vấn đề.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng đ&uacute;ng, hắn l&agrave; buổi tối thứ s&aacute;u tới, ta gặp qua hắn.&quot; Một t&ecirc;n kh&aacute;c học vi&ecirc;n bỗng nhi&ecirc;n n&oacute;i.<br />\r\n<br />\r\n&quot;Luyện hai ng&agrave;y, liền th&agrave;nh trung cấp V&otilde; Đồ? N&oacute;i đ&ugrave;a a!&quot;<br />\r\n<br />\r\n&quot;Tuyệt đối kh&ocirc;ng c&oacute; khả năng, hắn kh&ocirc;ng đến v&otilde; qu&aacute;n trước đ&oacute; khẳng định liền c&oacute; luyện v&otilde;, nh&igrave;n d&aacute;ng vẻ của hắn, r&otilde; r&agrave;ng l&agrave; c&aacute;i ph&uacute; nhị đại, ngh&egrave;o văn gi&agrave;u v&otilde;, trong nh&agrave; c&oacute; tiền, luyện v&otilde; lu&ocirc;n lu&ocirc;n so với ch&uacute;ng ta muốn sớm hơn nhiều, cũng c&agrave;ng dễ d&agrave;ng.&quot;<br />\r\n<br />\r\nThuyết ph&aacute;p n&agrave;y đạt được mọi người nhất tr&iacute; t&aacute;n đồng.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; người tin tưởng, tr&ecirc;n thế giới n&agrave;y sẽ c&oacute; người chỉ d&ugrave;ng thời gian kh&ocirc;ng đến hai ng&agrave;y, từ một c&aacute;i tay tr&oacute;i g&agrave; kh&ocirc;ng chặt chiến năm cặn b&atilde; biến th&agrave;nh một c&acirc;́p th&acirc;́p V&otilde; Đồ, sau đ&oacute; lại tăng l&ecirc;n đến trung cấp V&otilde; Đồ.<br />\r\n<br />\r\nQuả thực ch&iacute;nh l&agrave; thi&ecirc;n phương dạ đ&agrave;m.<br />\r\n<br />\r\nVậy căn bản kh&ocirc;ng phải hai chữ thi&ecirc;n t&agrave;i có th&ecirc;̉ h&igrave;nh dung, chỉ c&oacute; thể xưng l&agrave; y&ecirc;u nghiệt!<br />\r\n<br />\r\n&mdash;&mdash; Đương nhi&ecirc;n bật hack gian lận ngoại trừ!</p>', '2021-11-28 13:34:27', 14, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(219, 59, 'Giáng lâm dị giới', '1', '<p>Hắc &aacute;m, d&agrave;y đặc đến cơ hồ v&ocirc; ph&aacute;p tan ra đen nh&aacute;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;i n&agrave;y nồng đậm đen nh&aacute;nh ở giữa l&agrave; một mảnh y&ecirc;n tĩnh như chết.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thanh ni&ecirc;n kh&ocirc;ng biết c&aacute;i n&agrave;y kh&ocirc;ng tiếng động hắc &aacute;m tồn tại bao l&acirc;u, hắn kh&ocirc;ng c&oacute; tồn tại cảm thụ đến một loại kh&oacute; m&agrave; chịu được bực bội.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn c&acirc;̀n quang minh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Triệu C&agrave;n Vũ cố gắng gi&atilde;y dụa lấy, muốn x&eacute; n&aacute;t c&aacute;i n&agrave;y hắc &aacute;m.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Gi&atilde;y dụa ở giữa, tr&ecirc;n bầu trời giống như bởi v&igrave; hắn &yacute; nguyện m&agrave; dần dần vỡ ra một đường vết r&aacute;ch, từ n&agrave;y khe hở ở giữa quang mang rơi v&agrave;o, hắc &aacute;m tại quang minh b&ecirc;n dưới như băng tuyết tan r&atilde;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Y&ecirc;n tĩnh kh&ocirc;ng kh&iacute; vậy sụp đổ, ồn &agrave;o tiếng vang lọt v&agrave;o tai.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&ecirc;n tai l&agrave; l&aacute; rụng bị từng t&acirc;́c từng t&acirc;́c nghiền n&aacute;t m&agrave; ph&aacute;t ra k&ecirc;u r&ecirc;n, c&ugrave;ng một ch&uacute;t tối nghĩa ng&ocirc;n ngữ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Những n&agrave;y ng&ocirc;n ngữ từ một ch&uacute;t giống như đ&atilde; từng quen biết &acirc;m tiết tạo th&agrave;nh, r&otilde; r&agrave;ng trong đầu kh&ocirc;ng c&oacute; x&aacute;c thực k&yacute; ức, nhưng kỳ qu&aacute;i l&agrave; Triệu C&agrave;n Vũ v&acirc;̣y mà c&oacute; thể hiểu được &yacute; tứ trong đ&oacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;H&aacute;, Hook. C&aacute;i n&agrave;y kh&ocirc;ng c&ocirc;ng bằng, dựa v&agrave;o c&aacute;i g&igrave; để cho ta k&eacute;o lấy nặng nề như vậy gia hỏa.&quot; C&aacute;i n&agrave;y sắc nhọn thanh &acirc;m tr&agrave;n ngập bực tức c&ugrave;ng bất m&atilde;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngậm miệng! Ngươi thằng ngu n&agrave;y, dạng n&agrave;y heo m&acirc;̣p kh&ocirc;ng nhường ngươi k&eacute;o, chẳng lẽ c&ograve;n muốn để th&ocirc;ng minh Hook tới sao?&quot; Đồng dạng rất c&oacute; đặc sắc, giống như l&agrave; một con bị bóp chặt yết hầu g&agrave; ph&aacute;t ra thanh &acirc;m tại Triệu C&agrave;n Vũ đỉnh đầu vang l&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Triệu C&agrave;n Vũ trước mắt hắc &aacute;m giờ ph&uacute;t n&agrave;y vậy triệt để ti&ecirc;u t&aacute;n, hắn ngơ ng&aacute;c nh&igrave;n l&ecirc;n bầu trời.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thanh ni&ecirc;n c&oacute; thể cảm gi&aacute;c được bản th&acirc;n đang bị thứ g&igrave; dắt lấy, tr&ecirc;n mặt đất k&eacute;o đi lấy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&acirc;y l&agrave; đ&acirc;u?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Triệu C&agrave;n Vũ đầu &oacute;c c&oacute; ch&uacute;t hỗn loạn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn nhớ mang m&aacute;ng trước một gi&acirc;y hắn c&ograve;n đứng ở Thần Th&aacute;nh sơn đỉnh, lẳng lặng nh&igrave;n xem đại lục Metz tại gi&oacute; nổi m&acirc;y phun ở giữa bị m&agrave;u x&aacute;m tro sương m&ugrave; từng t&acirc;́c từng t&acirc;́c nuốt hết hầu như kh&ocirc;ng c&ograve;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;i n&agrave;y bồi bạn hắn t&aacute;m năm thế giới giả tưởng sinh mệnh ch&iacute;nh l&agrave; bởi v&igrave; kia sương m&ugrave; tr&agrave;n ngập m&agrave; tới điểm kết th&uacute;c, những c&aacute;i kia đ&atilde; từng trải qua đặc sắc qu&aacute; khứ cũng theo đ&oacute; hủy diệt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&agrave;m trong thế giới giả lập mỗi nơi đều bị sương m&ugrave; m&agrave;u x&aacute;m nuốt hết l&uacute;c, ch&iacute;nh l&agrave; &laquo; Thần Vực &raquo; triệt để đ&oacute;ng Server một khắc n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&laquo; Thần Vực &raquo; l&agrave; một c&aacute;i ph&aacute;t h&agrave;nh Vu B&aacute;t năm trước 3D thể cảm tr&ograve; chơi, căn cứ v&agrave;o đầy đủ khoa học kỹ thuật, hắn c&oacute; kỹ cao cực cao chất lượng. Cũng đang bởi v&igrave; hắn cực độ gần s&aacute;t hiện thực chất lượng ảnh c&ugrave;ng ch&acirc;n thật gi&aacute;c quan thể nghiệm, th&ecirc;m nữa cao độ tự do đặc t&iacute;nh, &laquo; Thần Vực &raquo; đ&atilde; tuy&ecirc;n bố liền lập tức hấp dẫn đ&ocirc;ng đảo player, ở trong đ&oacute; liền bao gồm Triệu C&agrave;n Vũ c&aacute;i n&agrave;y th&acirc;m ni&ecirc;n tr&ograve; chơi kẻ y&ecirc;u th&iacute;ch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Triệu C&agrave;n Vũ trơ mắt nh&igrave;n xem sương m&ugrave; x&aacute;m đem ch&iacute;nh m&igrave;nh v&ugrave;i lấp, liền sau đ&oacute; một khắc mất đi &yacute; thức.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đột nhi&ecirc;n, hắn cảm gi&aacute;c được trong đầu đau x&oacute;t, li&ecirc;n tục kh&ocirc;ng ngừng k&yacute; ức hiện ra tới.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ta đ&acirc;y l&agrave;... Xuy&ecirc;n qua đến &laquo; Thần Vực &raquo; b&ecirc;n trong?&quot; Triệu C&agrave;n Vũ chấn động trong l&ograve;ng, c&oacute; ch&uacute;t kh&oacute; c&oacute; thể tin.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave; đột nhi&ecirc;n hiện l&ecirc;n k&yacute; ức n&oacute;i cho hắn biết đ&acirc;y cũng kh&ocirc;ng phải l&agrave; hắn ph&aacute;n đo&aacute;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&ecirc;n tai kia hai c&aacute;i lanh lảnh thanh &acirc;m như cũ tại đối thoại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;H&ocirc; h&ocirc; h&ocirc;, gia hỏa n&agrave;y cũng thật l&agrave; ch&igrave;m a... Hook, trưởng l&atilde;o kh&ocirc;ng phải n&oacute;i để hai ch&uacute;ng ta nhấc gia hỏa n&agrave;y trở về sao, ngươi thế m&agrave; để cho ta một người l&agrave;m, ta trở về nhất định phải n&oacute;i cho trưởng l&atilde;o.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngậm miệng, ngươi vĩnh viễn kh&ocirc;ng biết th&ocirc;ng minh Hook đang suy nghĩ g&igrave;, trở về đừng hướng người kh&aacute;c nhấc l&ecirc;n việc n&agrave;y, ta kia phần cơm tối về ngươi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;C&oacute; thật kh&ocirc;ng? Qu&aacute; tốt rồi!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;...&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Triệu C&agrave;n Vũ nhớ tới thanh &acirc;m n&agrave;y v&igrave; sao quen thuộc như thế.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn cố gắng nh&igrave;n l&ecirc;n tr&ecirc;n nh&igrave;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Quả nhi&ecirc;n, c&aacute;i n&agrave;y kh&ocirc; cằn da dẻ, th&acirc;n thể gầy nhỏ, một c&aacute;i đầu to lớn b&ecirc;n tr&ecirc;n chỉ c&oacute; thể nh&igrave;n thấy mấy c&acirc;y kh&ocirc; cạn t&oacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mặc d&ugrave; chỉ c&oacute; thể nh&igrave;n thấy b&oacute;ng lưng, nhưng hắn vẫn l&agrave; x&aacute;c định k&eacute;o lấy bản th&acirc;n sinh vật ch&iacute;nh l&agrave; &laquo; Thần Vực &raquo; b&ecirc;n trong tầng dưới ch&oacute;t nhất ma vật Goblin.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>D&ugrave; sao vừa tiến v&agrave;o &laquo; Thần Vực &raquo; hắn v&igrave; luyện cấp thế nhưng l&agrave; tại Goblin l&agrave;ng x&oacute;m điểm nảy sinh mới ngồi chờ nửa th&aacute;ng, thẳng đến xo&aacute;t đến vừa nh&igrave;n thấy Goblin liền nghĩ n&ocirc;n tr&igrave;nh độ.</p>\r\n\r\n<p>N&oacute;i ra đều c&oacute; thể coi l&agrave; một đoạn huyết lệ sử.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Goblin k&eacute;o lấy nh&acirc;n loại di chuyển, hiển nhi&ecirc;n l&agrave; tại dự trữ lương thực.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;i chủng tộc n&agrave;y thực đơn b&ecirc;n trong mặn chay kh&ocirc;ng kị, đồng thời l&acirc;u d&agrave;i quấy rối một ch&uacute;t l&agrave;ng, giết chết th&ocirc;n d&acirc;n sau sẽ thi thể k&eacute;o về tộc đ&agrave;n trụ sở xem như lương thực chứa đựng l&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ th&acirc;n thể nguy&ecirc;n chủ trong tr&iacute; nhớ vậy chứng thực Triệu C&agrave;n Vũ suy đo&aacute;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cảm thụ được th&acirc;n thể yếu đuối, hắn biết r&otilde; l&uacute;c n&agrave;y m&igrave;nh đ&atilde; kh&ocirc;ng c&ograve;n l&agrave; trước đ&oacute; trong tr&ograve; chơi c&aacute;i kia LV. 200 player, m&agrave; l&agrave; trở th&agrave;nh dị thế giới một việc sinh sinh sinh mệnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&agrave;m sao b&acirc;y giờ?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Triệu C&agrave;n Vũ suy nghĩ xoay nhanh, b&acirc;y giờ bản th&acirc;n so với Goblin c&oacute; thể n&oacute;i l&agrave; tay tr&oacute;i g&agrave; kh&ocirc;ng chặt, t&ugrave;y tiện h&agrave;nh động sợ rằng muốn gọi đến tr&iacute; mạng tai hoạ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chẳng lẽ cứ như vậy t&ugrave;y &yacute; bọn ch&uacute;ng k&eacute;o về tộc đ&agrave;n đi? Khi đ&oacute; sợ rằng c&agrave;ng th&ecirc;m kh&oacute; m&agrave; tho&aacute;t th&acirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Liều chết chống cự? Vẫn l&agrave;?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngay tại Triệu C&agrave;n Vũ l&acirc;m v&agrave;o xoắn xu&yacute;t l&uacute;c, một c&aacute;i rộng lớn thanh &acirc;m đinh tai nhức &oacute;c, ở b&ecirc;n tai bỗng nhi&ecirc;n vang l&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Đinh! Th&agrave;nh tựu hệ thống mở ra!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ho&agrave;n th&agrave;nh th&agrave;nh tựu [ mới quen dị giới ] : Người lần đầu ti&ecirc;n l&agrave; qu&yacute; b&aacute;u, ta thật hi vọng ngươi lần thứ nhất gặp phải ma vật sẽ kh&ocirc;ng l&agrave; Goblin xấu như vậy lậu gia hỏa. Ch&uacute;c mừng ng&agrave;i thu hoạch được trở xuống ban thưởng: Thi thuật giả găng tay (bởi v&igrave; chưa kiểm tra đo lường đến t&uacute;c thể tr&ecirc;n người c&oacute; kh&ocirc;ng gian trữ vật liền tự h&agrave;nh trang bị), điểm kinh nghiệm 200 điểm!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ch&uacute;c mừng ng&agrave;i thăng cấp đến LV. 2! Thu hoạch được một điểm tự do đi&ecirc;̉m thu&ocirc;̣c tính!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Đạt th&agrave;nh điều kiện, thuộc t&iacute;nh bảng mở ra!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Li&ecirc;n tiếp nhắc nhở để Triệu C&agrave;n Vũ cảm gi&aacute;c được quen thuộc vừa xa lạ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&agrave;ng nhiều hơn ch&iacute;nh l&agrave; một loại tha hương ngộ cố tri kinh hỉ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&acirc;y kh&ocirc;ng phải &laquo; Thần Vực &raquo; b&ecirc;n trong hệ thống thanh &acirc;m nhắc nhở sao? L&agrave;m sao cũng c&ugrave;ng hắn một đợt đi tới nơi n&agrave;y c&aacute;i thế giới rồi?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave; c&ugrave;ng dĩ v&atilde;ng th&agrave;nh tựu hệ thống so s&aacute;nh, c&aacute;i hệ thống n&agrave;y c&agrave;ng giống l&agrave; trải qua biến chủng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&uacute;c đ&oacute; tại ho&agrave;n th&agrave;nh th&agrave;nh tựu hệ thống khi&ecirc;u chiến hạng mục về sau, player c&oacute; thể thu hoạch được khi&ecirc;u chiến mang tới cảm gi&aacute;c th&agrave;nh tựu, hướng bằng hữu hoặc l&agrave; x&atilde; khu khoe khoang, gia tăng ngo&agrave;i định mức niềm vui th&uacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; b&acirc;y giờ ho&agrave;n th&agrave;nh khi&ecirc;u chiến hạng mục sau lại c&ograve;n c&oacute; ban thưởng, t&iacute;nh thực dụng gia tăng thật lớn, loại cảm gi&aacute;c n&agrave;y kh&ocirc;ng thể bảo l&agrave; kh&ocirc;ng kỳ diệu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&ecirc;n h&ocirc;ng bị một sợi d&acirc;y thừng một mực thắt chặt, biết rồi d&acirc;y thừng b&ecirc;n kia bị Goblin nắm ở trong tay, Triệu C&agrave;n Vũ kh&ocirc;ng d&aacute;m h&agrave;nh động thiếu suy nghĩ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>D&ugrave; sao Goblin ri&ecirc;ng c&oacute; &quot;Người mới s&aacute;t thủ &quot; danh hiệu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vừa mở server trận kia, rất nhiều luyện cấp cuồng nh&acirc;n vừa tiến v&agrave;o tr&ograve; chơi liền vội v&atilde; kh&ocirc;ng nhịn nổi x&ocirc;ng ra điểm tr&ugrave;ng sinh muốn t&igrave;m ch&uacute;t &iacute;t qu&aacute;i khai đao, tự nhi&ecirc;n cũng liền theo d&otilde;i T&acirc;n Thủ th&ocirc;n b&ecirc;n ngo&agrave;i những n&agrave;y da xanh vật nhỏ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Dựa theo b&igrave;nh thường tr&ograve; chơi s&aacute;o lộ, xấu như vậy lậu sinh vật nhất định l&agrave; c&aacute;c người chơi đ&aacute; m&agrave;i đao, người người c&oacute; thể lấn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Luyện cấp cuồng nh&acirc;n nh&oacute;m cũng nghĩ như vậy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>T&acirc;n thủ c&aacute;c người chơi phần lớn th&acirc;n v&ocirc; trường vật chỉ c&oacute; một khối tấm m&agrave;n che, tay kh&ocirc;ng tấc sắt. So s&aacute;nh với nhau, người mặc thấp k&eacute;m gi&aacute;p da, cầm g&acirc;̣y g&ocirc;̃ lớn Goblin thậm ch&iacute; c&oacute; thể được xưng l&agrave; &#39;Trang bị tinh lương&#39; .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kết quả l&agrave; r&otilde; r&agrave;ng, nhờ v&agrave;o si&ecirc;u cao cảm gi&aacute;c đau cảm gi&aacute;c, c&aacute;c người chơi bị những n&agrave;y xấu x&iacute; vật nhỏ đ&aacute;nh k&ecirc;u cha gọi mẹ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Triệu C&agrave;n Vũ trước mắt liền gặp phải c&aacute;i n&agrave;y dạng l&uacute;ng t&uacute;ng t&igrave;nh trạng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>T&igrave;nh thế c&ograve;n mạnh hơn người, Triệu C&agrave;n Vũ chỉ c&oacute; thể ở trong l&ograve;ng y&ecirc;n lặng gọi ra thuộc t&iacute;nh bảng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trước mắt lập tức xuất hiện một tấm m&agrave;u xanh nhạt hơi mờ m&agrave;n s&aacute;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;T&iacute;nh danh: Triệu C&agrave;n Vũ</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Xưng h&agrave;o: Kh&ocirc;ng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chủng tộc: Nh&acirc;n tộc</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chủng tộc đẳng cấp: 2(100 ∕ 500)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chiến đấu nghề nghiệp: Kh&ocirc;ng (0 ∕ 0)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sinh hoạt nghề nghiệp: Kh&ocirc;ng (0 ∕ 0)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thuộc t&iacute;nh: Lực lượng 5, tốc độ 3, nhanh nhẹn 4, sức chịu đựng 3, tr&iacute; lực 4, may mắn 2(có th&ecirc;̉ ph&acirc;n phối tự do đi&ecirc;̉m thu&ocirc;̣c tính 1)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ma lực: 40 ∕ 40 [ 1 điểm tr&iacute; lực mang đến 10 điểm ma lực hạn mức cao nhất, tự nhi&ecirc;n trạng th&aacute;i dưới 10 ph&uacute;t kh&ocirc;i phục 1 điểm MP ]</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kỹ năng: Kh&ocirc;ng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trang bị: Thi thuật giả găng tay (kh&ocirc;ng thể sử dụng)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Danh vọng: Kh&ocirc;ng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>...</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&aacute; lắm, từ đầu đến đu&ocirc;i một c&aacute;i bạch bản, so ban đầu Triệu C&agrave;n Vũ khai s&aacute;ng vai diễn c&ograve;n muốn yếu đuối.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tốt xấu l&uacute;c trước hắn Roll ra thuộc t&iacute;nh tổng cộng cộng lại cũng c&oacute; 25 điểm chi cao.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&ugrave;ng dĩ v&atilde;ng game online kh&aacute;c biệt, tại chủ đ&aacute;nh ch&acirc;n thật &laquo; Thần Vực &raquo;, player c&ugrave;ng ma vật cũng kh&ocirc;ng c&oacute; thanh m&aacute;u, ph&aacute;n đo&aacute;n điểm sinh mệnh phương ph&aacute;p chỉ c&oacute; quan s&aacute;t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Triệu C&agrave;n Vũ t&acirc;m niệm vừa động, thuộc t&iacute;nh bảng tiếp lấy hướng ph&iacute;a dưới nhấp nh&ocirc;, lộ ra ngay từ đầu kh&ocirc;ng c&oacute; hiển lộ bộ phận.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Cổ Long chi huyết, nh&acirc;n quả ph&aacute;p trượng, g&atilde;y vọt chi m&ocirc;n, diệt thế tay, c&agrave;nh Thế Giới thụ... T&ecirc;, những vật n&agrave;y l&agrave;m sao đều chạy đến hệ thống n&agrave;y b&ecirc;n trong...&quot; Triệu C&agrave;n Vũ lật xem th&agrave;nh tựu hệ thống điều mục, kh&ocirc;ng khỏi ở trong l&ograve;ng quất thẳng tới l&atilde;nh kh&iacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Những vật n&agrave;y Triệu C&agrave;n Vũ tự nhi&ecirc;n kh&ocirc;ng xa lạ g&igrave;, mỗi một kiện vật phẩm đều đủ để tr&ecirc;n đại lục Metz khuấy động Phong V&acirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong tr&ograve; chơi muốn thu hoạch bọn ch&uacute;ng đều kh&ocirc;ng ngoại lệ đều cần th&ocirc;ng qua kh&oacute; khăn nặng nề nhiệm vụ, về sau lại ph&ugrave; hợp hắn h&agrave; khắc sử dụng điều kiện sau mới c&oacute; thể thu được.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng nghĩ tới c&aacute;i n&agrave;y th&agrave;nh tựu hệ thống thế m&agrave; trực tiếp xa hoa đem những vật phẩm n&agrave;y l&agrave;m đạt th&agrave;nh th&agrave;nh tựu ban thưởng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn th&ocirc; sơ giản lược qu&eacute;t qua, mặc d&ugrave; đạt th&agrave;nh điều kiện cũng kh&ocirc;ng thấp, nhưng so với trong tr&ograve; chơi thu hoạch độ kh&oacute; đ&uacute;ng l&agrave; thấp kh&ocirc;ng &iacute;t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&acirc;y ch&iacute;nh l&agrave; ta ng&oacute;n tay v&agrave;ng &agrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Triệu C&agrave;n Vũ kh&ocirc;ng nghĩ tới bản th&acirc;n v&acirc;̣y mà thật sự trang bị người xuy&ecirc;n việt chuy&ecirc;n m&ocirc;n phần mềm hack.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn c&oacute; ch&uacute;t vặn vẹo uốn &eacute;o đầu, n&acirc;ng l&ecirc;n tr&ecirc;n tay phải ch&iacute;nh mang theo một con to&agrave;n th&acirc;n m&agrave;u trắng, tr&ecirc;n đ&oacute; c&oacute; mấy c&acirc;y kim sắc sợi tơ b&agrave;y ra bao tay.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Thi thuật giả găng tay</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Loại h&igrave;nh: Trang bị</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đẳng cấp: Kh&ocirc;ng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sử dụng y&ecirc;u cầu: Tr&iacute; lực 5(&times;)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kỹ năng: [ Hỏa Cầu thuật ] (một cấp): Độc th&acirc;n mười t&aacute;m năm đại ma ph&aacute;p sư c&oacute; thể xoa ra cường lực (? ) hỏa cầu, thi thuật phạm vi 20 m, thời gian cooldown hai gi&acirc;y, ti&ecirc;u hao 20 điểm MP.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Giới thiệu: Một vị n&agrave;o đ&oacute; ma ph&aacute;p học đồ luyện tập t&aacute;c phẩm, trừ bề ngo&agrave;i nhìn được, đi c&ocirc;ng trường dời gạch thậm ch&iacute; kh&ocirc;ng s&aacute;nh bằng b&igrave;nh thường găng tay d&ugrave;ng tốt.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;i n&agrave;y giản lược bảng, ho&agrave;n to&agrave;n ở Triệu C&agrave;n Vũ trong dự liệu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn kh&ocirc;ng c&oacute; tr&ocirc;ng cậy v&agrave;o th&agrave;nh tựu hệ thống c&aacute;i thứ nhất th&agrave;nh tựu đạt th&agrave;nh liền c&oacute; thể cho hắn c&aacute;i g&igrave; lập tức c&oacute; thể để cho hắn đại s&aacute;t tứ phương Thần kh&iacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bất qu&aacute;, c&aacute;i n&agrave;y trang bị vừa l&uacute;c thuận theo hắn t&acirc;m &yacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đến tận đ&acirc;y, Triệu C&agrave;n Vũ đ&atilde; triệt để tiếp nhận rồi xuy&ecirc;n qua đến thế giới tr&ograve; chơi thiết lập.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn kh&ocirc;ng ch&uacute;t n&agrave;o h&agrave;m hồ đem thăng cấp ban thưởng một điểm tự do đi&ecirc;̉m thu&ocirc;̣c tính th&ecirc;m ở tr&iacute; lực b&ecirc;n tr&ecirc;n, đạt tới c&oacute; thể sử dụng thi thuật giả găng tay ti&ecirc;u chuẩn đồng thời, ma lực cũng tới đến 50 điểm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trang bị mặc c&ugrave;ng sử dụng l&agrave; hai m&atilde; sự t&igrave;nh, cũng tỷ như c&aacute;i bao tay n&agrave;y. Bất kể c&oacute; hay kh&ocirc;ng ph&ugrave; hợp sử dụng ti&ecirc;u chuẩn cũng c&oacute; thể mặc mang, nhưng l&agrave; nếu như tr&iacute; lực kh&ocirc;ng đủ 5 điểm, liền v&ocirc; ph&aacute;p sử dụng găng tay bổ sung Hỏa Cầu thuật.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&oacute; thi thuật giả găng tay, Triệu C&agrave;n Vũ trong l&ograve;ng đ&atilde; c&oacute; lực lượng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hỏa Cầu thuật l&agrave;m ma ph&aacute;p sư nghề nghiệp thể hệ cơ sở kỹ năng mặc d&ugrave; đang ở player quần thể b&ecirc;n trong gần gũi trong tay mỗi người c&oacute; một c&aacute;i, nhưng l&agrave; uy lực đối ph&oacute; c&acirc;́p th&acirc;́p Goblin c&ograve;n chưa phải th&agrave;nh vấn đề.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bất kể như thế n&agrave;o, trước được giải quyết hết c&aacute;i n&agrave;y hai con đ&aacute;ng gh&eacute;t Goblin.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Triệu C&agrave;n Vũ trở m&igrave;nh, tựa như c&aacute; ướp muối trở mặt.</p>', '2021-11-28 13:47:28', 103, 1),
(220, 59, 'Thi thuật giả găng tay', '2', '<p>B&igrave;nh thường Goblin lực lượng muốn so player trị số b&igrave;nh qu&acirc;n thấp một ch&uacute;t, gần gũi c&ugrave;ng b&acirc;y giờ Triệu C&agrave;n Vũ ngang h&agrave;ng. Nhưng ở kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o ph&ograve;ng bị t&igrave;nh huống dưới, Hook c&ograve;n l&agrave; bị bỗng nhi&ecirc;n xuất hiện lực cản vấp c&aacute;i cẩu gặm b&ugrave;n.<br />\r\n<br />\r\n&quot;Ồ trời ạ! Hắn thế m&agrave; tại động!&quot; V&ocirc; c&ugrave;ng hoảng sợ thanh &acirc;m vang l&ecirc;n.<br />\r\n<br />\r\nTừ dưới đất chật vật b&ograve; dậy Hook giống như l&agrave; gặp quỷ, tr&ecirc;n mặt loại người ngũ quan vặn vẹo giống một đ&oacute;a hoa c&uacute;c, cuống qu&iacute;t trốn đến một c&aacute;i kh&aacute;c Goblin sau lưng.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ từ dưới đất đứng l&ecirc;n, thuận tay giải khai thắt ở b&ecirc;n h&ocirc;ng d&acirc;y gai.<br />\r\n<br />\r\nMượn &aacute;nh trăng trong s&aacute;ng, Triệu C&agrave;n Vũ thấy r&otilde; từ xuy&ecirc;n việt đến &laquo; Thần Vực &raquo; sau đệ nhất v&agrave; c&aacute;i thứ hai ma vật.<br />\r\n<br />\r\nMột con th&acirc;n cao đến b&ecirc;n h&ocirc;ng hắn Goblin đang n&uacute;p ở một c&aacute;i kh&aacute;c xem ra hơi cao một ch&uacute;t đồng tộc sau lưng, một đ&ocirc;i mắt to tr&agrave;n ngập hoảng sợ nh&igrave;n xem Triệu C&agrave;n Vũ.<br />\r\n<br />\r\nHai con Goblin tr&ecirc;n th&acirc;n đều mặc thấp k&eacute;m gi&aacute;p da, cầm căn g&acirc;̣y g&ocirc;̃ lớn.<br />\r\n<br />\r\nH&igrave;nh tượng n&agrave;y c&ugrave;ng Triệu C&agrave;n Vũ trong tr&iacute; nhớ giống nhau như đ&uacute;c.<br />\r\n<br />\r\n&quot;Những người n&agrave;y kh&ocirc;ng phải l&agrave; bị Quinn trưởng l&atilde;o giết chết sao, l&agrave;m sao sống lại. . .&quot; Cường tr&aacute;ng ch&uacute;t Goblin ch&iacute;nh ng&acirc;y người tại nguy&ecirc;n chỗ, nh&igrave;n xem Triệu C&agrave;n Vũ động t&aacute;c.<br />\r\n<br />\r\nNh&agrave;n rỗi một c&aacute;i tay nghi hoặc m&agrave; g&atilde;i lấy gần gũi h&oacute;i đầu đỉnh đầu, gia hỏa n&agrave;y lộ ra mười phần nghi hoặc.<br />\r\n<br />\r\n&quot;Ngươi ngươi ngươi ngươi. . . Ngươi rốt cuộc l&agrave; người l&agrave; quỷ. . .&quot; Nhỏ gầy Goblin lắp ba lắp bắp ngữ kh&iacute; lộ ra mười phần buồn cười.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ qu&eacute;t mắt bị cường tr&aacute;ng Goblin k&eacute;o lấy, mặc b&igrave;nh d&acirc;n phục sức, l&uacute;c n&agrave;y hai mắt nhắm nghiền kh&iacute; tức ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; nam nh&acirc;n.<br />\r\n<br />\r\nTăng th&ecirc;m Quinn c&aacute;i n&agrave;y c&oacute; ch&uacute;t quen tai danh tự, hắn lập tức kịp phản ứng.<br />\r\n<br />\r\nBản th&acirc;n quả nhi&ecirc;n l&agrave; bị coi như qua m&ugrave;a đ&ocirc;ng đồ ăn.<br />\r\n<br />\r\n&laquo; Thần Vực &raquo; b&ecirc;n trong Goblin c&oacute; thể n&oacute;i trời sinh t&iacute;nh t&agrave;n bạo, ăn người đối bọn ch&uacute;ng tới n&oacute;i bất qu&aacute; l&agrave; một bữa ăn s&aacute;ng.<br />\r\n<br />\r\n&quot;Ngươi c&aacute;i n&agrave;y ngớ ngẩn! Kh&ocirc; nhanh hơn một ch&uacute;t rơi hắn!&quot; Nh&igrave;n xem vẫn như cũ v&ograve; đầu đồng bạn, Hook kh&iacute; cấp bại ph&ocirc;i chỉ huy đến.<br />\r\n<br />\r\nC&aacute;i n&agrave;y Goblin rất c&oacute; &yacute; tứ a. . . Triệu C&agrave;n Vũ nh&igrave;n lướt qua lộ ra sợ h&atilde;i rụt r&egrave; Goblin.<br />\r\n<br />\r\nTrừ một ch&uacute;t cao đẳng cấp Goblin, b&igrave;nh thường Goblin đều rất đần độn c&aacute;i n&agrave;y Goblin thế m&agrave; lại chỉ huy đồng bạn, Triệu C&agrave;n Vũ c&oacute; ch&uacute;t ngo&agrave;i &yacute; muốn.<br />\r\n<br />\r\n&quot;Đồ ăn! Đồ ăn! Chết đi cho ta!&quot;<br />\r\n<br />\r\nBị t&ecirc;n gọi Hook Goblin một nhắc nhở, cường tr&aacute;ng Goblin giống như đại mộng mới tỉnh.<br />\r\n<br />\r\nBỏ qua sợi d&acirc;y tr&ecirc;n tay, chừng Triệu C&agrave;n Vũ ngực cao &#39;Cự h&igrave;nh&#39; Goblin cuồng hống, hai tay giơ tr&aacute;ng kiện c&acirc;y gỗ lao đến.<br />\r\n<br />\r\nTiếng gi&oacute; v&ugrave; v&ugrave; chứng minh căn n&agrave;y nh&igrave;n như tầm thường c&acirc;y gỗ l&agrave; đủ để đoạt t&iacute;nh mạng người khủng bố hung kh&iacute;.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ chỉ cảm thấy &aacute;c phong đập v&agrave;o mặt, da xanh qu&aacute;i vật thẳng tắp đ&aacute;nh tới.<br />\r\n<br />\r\nNếu như kh&ocirc;ng khai th&aacute;c biện ph&aacute;p, sợ rằng một gi&acirc;y sau liền muốn &oacute;c bắn tung to&eacute;.<br />\r\n<br />\r\nĐ&acirc;y cũng l&agrave; l&uacute;c trước tuyệt đại đa số t&acirc;n thủ player đối mặt t&igrave;nh huống &aacute;c liệt, g&acirc;̣y g&ocirc;̃ lớn nện đầu tư vị, cho d&ugrave; l&agrave; nhiều năm về sau cũng l&agrave; rất nhiều người kh&oacute; m&agrave; ma diệt khắc s&acirc;u ấn tượng.<br />\r\n<br />\r\nCũng may Triệu C&agrave;n Vũ l&agrave; một người xuy&ecirc;n việt, một b&ecirc;n th&acirc;n lại tr&aacute;nh được c&aacute;i n&agrave;y kh&iacute; thế hung hung phủ đầu một gậy.<br />\r\n<br />\r\n&quot;Ngu xuẩn! Tiếp tục, đem hắn chơi chết!&quot;<br />\r\n<br />\r\nNh&igrave;n thấy một gậy n&agrave;y tử kh&ocirc;ng c&oacute; nện v&agrave;o Triệu C&agrave;n Vũ, Hook li&ecirc;n tục kh&ocirc;ng ngừng tiếp tục ra lệnh.<br />\r\n<br />\r\nC&ograve;n tốt đ&acirc;y chỉ l&agrave; c&aacute;i b&igrave;nh thường b&igrave;nh d&acirc;n, Botu lẽ ra c&oacute; thể đủ giải quyết.<br />\r\n<br />\r\nMặc kệ ngươi l&agrave; người l&agrave; quỷ, ngươi đều chết chắc rồi!<br />\r\n<br />\r\nHook đứng ở b&ecirc;n cạnh, khẩn trương nắm chặt gậy gỗ trong tay, trong l&ograve;ng hận hận nghĩ đến.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ hiểm hiểm tr&aacute;nh đi c&aacute;i n&agrave;y đoạt mệnh một c&ocirc;n, kh&ocirc;ng nhịn được thở d&agrave;i.<br />\r\n<br />\r\nSo với trước đ&oacute;, b&acirc;y giờ bản th&acirc;n bảng như thế bần c&ugrave;ng, quả thực l&agrave; một đ&ecirc;m trở lại trước giải ph&oacute;ng.<br />\r\n<br />\r\nĐặt ở xuy&ecirc;n qua trước, thấp như vậy cấp ma vật bản th&acirc;n một &aacute;nh mắt liền c&oacute; thể diệt s&aacute;t, c&aacute;i n&agrave;o d&ugrave;ng giống như b&acirc;y giờ n&eacute; tr&aacute;nh.<br />\r\n<br />\r\nBotu tr&ecirc;n mặt m&ecirc; hoặc chi t&igrave;nh c&agrave;ng tăng l&ecirc;n, lấy đầu của hắn tự nhi&ecirc;n nghĩ m&atilde;i m&agrave; kh&ocirc;ng r&otilde; v&igrave; c&aacute;i g&igrave; một gậy n&agrave;y Tử Cư nhưng kh&ocirc;ng thể giải quyết hết Triệu C&agrave;n Vũ.<br />\r\n<br />\r\nNghe tới Hook chỉ lệnh, to con gia hỏa lần nữa giơ c&acirc;y gỗ lao đến.<br />\r\n<br />\r\nBất qu&aacute; lần n&agrave;y, Triệu C&agrave;n Vũ kh&ocirc;ng định n&eacute;.<br />\r\n<br />\r\nHook nh&igrave;n xem kh&ocirc;ng tr&aacute;nh kh&ocirc;ng n&eacute; Triệu C&agrave;n Vũ, cuối c&ugrave;ng l&agrave; y&ecirc;n t&acirc;m.<br />\r\n<br />\r\nXem ra tiểu tử n&agrave;y mới vừa trốn tr&aacute;nh bất qu&aacute; l&agrave; gặp vận may.<br />\r\n<br />\r\nHắn chết chắc rồi!<br />\r\n<br />\r\nNhưng m&agrave; tiếp xuống ph&aacute;t sinh một m&agrave;n lại l&agrave;m cho c&aacute;i n&agrave;y Goblin h&aacute; to miệng.<br />\r\n<br />\r\nMột c&aacute;i to bằng chậu rửa mặt tiểu nh&acirc;n hỏa cầu đột nhi&ecirc;n xuất hiện ở bị n&oacute; coi l&agrave; thức ăn Triệu C&agrave;n Vũ tr&ecirc;n tay, cho d&ugrave; c&aacute;ch nhau kh&ocirc;ng xa khoảng c&aacute;ch, Hook y nguy&ecirc;n c&oacute; thể cảm gi&aacute;c được s&oacute;ng nhiệt cuồn cuộn.<br />\r\n<br />\r\nC&aacute;i n&agrave;y nh&acirc;n loại tiểu tử l&agrave;m sao lại ma ph&aacute;p! ?<br />\r\n<br />\r\nKh&ocirc;ng lớn kh&ocirc;ng nhỏ hỏa cầu ph&uacute;t chốc một tiếng bay ra ngo&agrave;i, ch&iacute;nh ch&iacute;nh đập v&agrave;o x&ocirc;ng tới Botu tấm kia mặt xấu x&iacute; b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nPhanh!<br />\r\n<br />\r\nTia lửa tung t&oacute;e!<br />\r\n<br />\r\nC&aacute;i n&agrave;y dạng một c&aacute;i hỏa cầu trực tiếp tại Goblin tr&ecirc;n mặt nổ tung, uy lực c&oacute; thể nghĩ.<br />\r\n<br />\r\nBotu gậy gỗ trong tay tr&ugrave;ng điệp quẳng xuống đất, m&agrave; n&oacute; tự m&igrave;nh th&igrave; l&agrave; bị hỏa cầu mang lực tr&ugrave;ng k&iacute;ch đ&aacute;nh ng&atilde; xuống đất.<br />\r\n<br />\r\n&quot;A! ! ! Mặt của ta!&quot; Ng&atilde; tr&ecirc;n mặt đất Botu hai tay vuốt ch&aacute;y hừng hực phần đầu, th&acirc;n thể lăn lộn ở giữa ph&aacute;t ra cực kỳ bi thảm k&ecirc;u th&ecirc; lương thảm thiết, tiếng tốt người trong l&ograve;ng đều sinh ra băng h&agrave;n chi &yacute;.<br />\r\n<br />\r\nC&aacute;i n&agrave;y đập đối thi&ecirc;u đốt hỏa diễm kh&ocirc;ng ch&uacute;t n&agrave;o l&ecirc;n ức chế t&aacute;c dụng, thế lửa ngược lại bởi v&igrave; vụng về động t&aacute;c m&agrave; c&agrave;ng ng&agrave;y c&agrave;ng nghi&ecirc;m trọng.<br />\r\n<br />\r\nTiếng k&ecirc;u thảm thiết một mực tiếp tục đến hỏa diễm dập tắt.<br />\r\n<br />\r\nTheo hỏa diễm dập tắt, c&aacute;i n&agrave;y Goblin gi&atilde;y dụa bi&ecirc;n độ vậy xuống tới thấp nhất, n&oacute; nằm tr&ecirc;n mặt đất kh&ocirc;ng nh&uacute;c nh&iacute;ch.<br />\r\n<br />\r\nL&uacute;c n&agrave;y c&aacute;i n&agrave;y Goblin phần đầu đ&atilde; triệt để h&oacute;a th&agrave;nh một khối mất đi h&igrave;nh thể h&igrave;nh dạng than cốc, vẻ bề ngo&agrave;i sự khốc liệt nhường cho người kh&ocirc;ng đ&agrave;nh l&ograve;ng xem lần thứ hai.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ chậm r&atilde;i thu hồi lại con kia mang theo thi thuật giả găng tay tay, trong l&ograve;ng hiện ra v&ocirc; cùng suy y&ecirc;́u cảm gi&aacute;c.<br />\r\n<br />\r\n20 điểm ma lực ti&ecirc;u hao gần gũi trước mắt hắn ma lực tổng gi&aacute; trị một nửa, một lần ti&ecirc;u hao nhiều như vậy ma lực rất kh&oacute; kh&ocirc;ng sinh ra cảm gi&aacute;c như vậy.<br />\r\n<br />\r\n&quot;Ho&agrave;n th&agrave;nh th&agrave;nh tựu [ tiểu th&iacute; ngưu đao ] : Tại gi&aacute;ng l&acirc;m dị giới về sau, ngươi kh&oacute; khăn đ&aacute;nh chết c&aacute;i thứ nhất ma vật, tại dũng giả con đường b&ecirc;n tr&ecirc;n tiến th&ecirc;m một bước. Ch&uacute;c mừng ng&agrave;i thu hoạch được trở xuống ban thưởng: Kim Lanck 1 mai, điểm kinh nghiệm 500 điểm.&quot;<br />\r\n<br />\r\n&quot;Ch&uacute;c mừng ng&agrave;i thăng cấp đến LV. 3! Thu hoạch được một điểm tự do đi&ecirc;̉m thu&ocirc;̣c tính!&quot;<br />\r\n<br />\r\nB&ecirc;n tai thanh &acirc;m nhắc nhở vang l&ecirc;n lần nữa.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ thỏa m&atilde;n nh&igrave;n xuống đất b&ecirc;n tr&ecirc;n kiệt t&aacute;c của m&igrave;nh, hắn h&ocirc;m nay cũng coi l&agrave; c&oacute; nhất định thực lực, ở nơi n&agrave;y nguy hiểm thế giới cũng coi như c&oacute; lập th&acirc;n tư bản.<br />\r\n<br />\r\n&laquo; Thần Vực &raquo; đại bối cảnh ch&iacute;nh l&agrave; đại lục Metz b&ecirc;n tr&ecirc;n nh&acirc;n loại c&ugrave;ng c&aacute;c loại dị tộc c&ugrave;ng ma th&uacute; đấu tranh sinh tồn, theo thời gian chuyển dời loại n&agrave;y đấu tranh c&agrave;ng ng&agrave;y c&agrave;ng nghi&ecirc;m trọng, đến tr&ograve; chơi hậu kỳ đại lục ở b&ecirc;n tr&ecirc;n ho&agrave;n to&agrave;n một mảnh chiến hỏa bay t&aacute;n loạn cảnh tượng.<br />\r\n<br />\r\nTrước kia l&agrave;m player Triệu C&agrave;n Vũ đối đại lục ở b&ecirc;n tr&ecirc;n thảm thiết sinh sinh tử tử kh&ocirc;ng c&oacute; cảm gi&aacute;c ch&uacute;t n&agrave;o, nhưng b&acirc;y giờ đ&atilde; l&agrave;m thế giới n&agrave;y một phần tử, Triệu C&agrave;n Vũ cũng kh&ocirc;ng khỏi được cảm gi&aacute;c được t&acirc;m tư c&agrave;ng th&ecirc;m trĩu nặng.<br />\r\n<br />\r\nĐ&aacute;nh giết c&aacute;i thứ nhất Goblin, xem như v&igrave; hắn dị giới h&agrave;nh tr&igrave;nh mở đầu xong.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ xoay người, đang chuẩn bị cho một c&aacute;i kh&aacute;c Goblin cũng tới b&ecirc;n tr&ecirc;n một ph&aacute;t độc th&acirc;n nhiều năm t&agrave;i năng xoa ra tới hỏa cầu l&uacute;c.<br />\r\n<br />\r\nMột c&aacute;i m&agrave;u lục c&aacute;i b&oacute;ng phi tốc đ&aacute;nh tới.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ cũng kh&ocirc;ng nhịn được qu&aacute; sợ h&atilde;i, tốc độ n&agrave;y quả thực c&oacute; một kh&ocirc;ng hai hắn đ&atilde; gặp qua c&acirc;́p th&acirc;́p Goblin.<br />\r\n<br />\r\nT&ecirc;n n&agrave;y gọi Hook Goblin mặc d&ugrave; th&acirc;n thể gầy yếu, nhưng l&agrave; tốc độ n&agrave;y thật kh&ocirc;ng cho khinh thường.<br />\r\n<br />\r\nCh&iacute;nh đ&aacute;ng Triệu C&agrave;n Vũ chuẩn bị k&iacute;ch ph&aacute;t găng tay b&ecirc;n tr&ecirc;n ma văn mạch k&iacute;n l&uacute;c, trước mắt c&aacute;i n&agrave;y Goblin l&agrave;m ra một c&aacute;i l&agrave;m hắn kh&ocirc;ng tưởng được cử động.</p>', '2021-11-28 13:48:25', 31, 1),
(221, 59, '\'Hắc thủ\' Hook', '3', '<p>Gầy c&ograve;m Goblin phi tốc nh&agrave;o tới, nhưng l&agrave; tr&ecirc;n tay nhưng kh&ocirc;ng c&oacute; cầm c&acirc;y kia mang t&iacute;nh ti&ecirc;u ch&iacute; g&acirc;̣y g&ocirc;̃ lớn.<br />\r\n<br />\r\nHook quỳ s&aacute;t tại Triệu C&agrave;n Vũ dưới ch&acirc;n, kh&ocirc;ng ngừng m&agrave; đập lấy đầu, kh&ocirc; quắt thanh &acirc;m tr&agrave;n ngập sợ h&atilde;i.<br />\r\n<br />\r\n&quot;Thật xin lỗi, t&ocirc;n qu&yacute; ma ph&aacute;p sư đại nh&acirc;n, tiểu nh&acirc;n Hook c&oacute; mắt kh&ocirc;ng tr&ograve;ng đụng phải c&aacute;c hạ, thực tế tội đ&aacute;ng chết vạn lần. Nhưng ng&agrave;i đại nh&acirc;n c&oacute; đại lượng, khẳng định khinh thường tại c&ugrave;ng tiểu nh&acirc;n loại n&agrave;y l&acirc;u la so đo, ng&agrave;i liền đem tiểu nh&acirc;n xem như c&aacute;i rắm thả đi. Tiểu nh&acirc;n tr&ecirc;n c&oacute; l&atilde;o, dưới c&oacute; nhỏ...&quot;<br />\r\n<br />\r\nTi tiện bộ d&aacute;ng c&ugrave;ng mới vừa ngang ngược c&agrave;n rỡ h&igrave;nh th&agrave;nh c&aacute;ch biệt một trời.<br />\r\n<br />\r\nNghe Hook bắn li&ecirc;n thanh thức cầu xin tha thứ, Triệu C&agrave;n Vũ cũng bị c&aacute;i n&agrave;y trở mặt so lật s&aacute;ch c&ograve;n nhanh Goblin chấn kinh rồi một thanh.<br />\r\n<br />\r\nĐẳng cấp thấp Goblin thường thường đều rất ngu dại, chỉ biết nghe theo người kh&aacute;c chỉ huy.<br />\r\n<br />\r\nNhưng l&agrave; c&aacute;i n&agrave;y Goblin l&agrave; hắn thấy qua duy nhất ngoại lệ.<br />\r\n<br />\r\nXem ra c&aacute;i n&agrave;y Goblin đem m&igrave;nh ngộ nhận th&agrave;nh ma ph&aacute;p sư, cho n&ecirc;n mới mất đi dũng kh&iacute;, chỉ c&oacute; thể b&aacute;i phục khi hắn dưới ch&acirc;n cầu xin tha thứ.<br />\r\n<br />\r\nKh&ocirc;ng đ&uacute;ng.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ bỗng nhi&ecirc;n nhớ tới một &iacute;t chuyện.<br />\r\n<br />\r\n&quot;Ngẩng đầu l&ecirc;n n&oacute;i chuyện.&quot; Triệu C&agrave;n Vũ thanh &acirc;m tr&agrave;n ngập lạnh lẽo cứng rắn c&ugrave;ng kh&ocirc;ng thể nghi ngờ.<br />\r\n<br />\r\nThanh &acirc;m n&agrave;y tại l&uacute;c n&agrave;y Hook trong lỗ tai lộ ra v&ocirc; c&ugrave;ng uy nghi&ecirc;m, Hook to&agrave;n th&acirc;n run rẩy n&acirc;ng l&ecirc;n tấm kia bị dọa đến tr&agrave;n đầy nước mắt c&ugrave;ng nước mũi mặt.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ quan s&aacute;t tỉ mỉ lấy c&aacute;i n&agrave;y chật vật kh&ocirc;ng chịu nổi gia hỏa, tấm kia mặt xanh cũng đang c&ugrave;ng trong tr&iacute; nhớ tấm kia dần dần tr&ugrave;ng hợp.<br />\r\n<br />\r\nTrong l&ograve;ng của hắn kh&ocirc;ng khỏi sinh ra một tia hoang đường cảm gi&aacute;c, tr&ograve; chơi hậu kỳ rất c&oacute; danh kh&iacute; gia hỏa hiện tại l&agrave;m sao kh&ocirc;ng chịu được như thế.<br />\r\n<br />\r\n&quot;Ngươi vừa n&oacute;i ngươi t&ecirc;n gọi l&agrave; g&igrave;.&quot;<br />\r\n<br />\r\n&quot;Hook, hồi bẩm đại nh&acirc;n, tiểu nh&acirc;n gọi Hook.&quot; Nghe thấy Triệu C&agrave;n Vũ tra hỏi, Hook cố gắng nghĩ gạt ra một c&aacute;i khu&ocirc;n mặt tươi cười.<br />\r\n<br />\r\nNhưng đ&acirc;y kh&ocirc;ng thể nghi ngờ l&agrave; thất bại, loại n&agrave;y độ kh&oacute; cao biểu lộ để Hook tấm kia m&agrave;u lục gương mặt trở n&ecirc;n c&agrave;ng th&ecirc;m xấu x&iacute;.<br />\r\n<br />\r\nSẽ kh&ocirc;ng sai, ch&iacute;nh l&agrave; c&aacute;i n&agrave;y danh tự.<br />\r\n<br />\r\nKết hợp Hook tr&ecirc;n người dị thường hiện tượng c&ugrave;ng t&ecirc;n của n&oacute;, Triệu C&agrave;n Vũ c&agrave;ng ph&aacute;t ra khẳng định đ&acirc;y ch&iacute;nh l&agrave; hắn nghĩ c&aacute;i kia Goblin.<br />\r\n<br />\r\n&quot;Hắc thủ&quot; Hook.<br />\r\n<br />\r\nTrong tr&ograve; chơi kỳ đạo tặc chức hệ cao giai nghề nghiệp người nắm giữ, bởi v&igrave; nhiều lần trộm cắp ma vật c&ugrave;ng nh&acirc;n loại vật phẩm tư nh&acirc;n m&agrave; x&uacute; danh chi&ecirc;u, gần gũi đến người người k&ecirc;u đ&aacute;nh t&igrave;nh trạng, nhưng ỷ v&agrave;o cao minh thủ đoạn lu&ocirc;n c&oacute; thể tại v&acirc;y giết b&ecirc;n trong trốn xa ng&agrave;n dặm.<br />\r\n<br />\r\nHắc thủ Hook trước mặt xem như trong tr&ograve; chơi kỳ ma vật b&ecirc;n trong nh&acirc;n vật truyền kỳ, từng t&ecirc;n nổi như cồn, nhưng sau n&agrave;y kh&ocirc;ng biết bởi v&igrave; nguy&ecirc;n nh&acirc;n g&igrave; m&agrave; mai danh ẩn t&iacute;ch, kh&ocirc;ng ai biết r&otilde; n&oacute; đến tột c&ugrave;ng l&agrave; chết rồi vẫn l&agrave; rửa tay g&aacute;c kiếm.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ b&ecirc;n người c&oacute; kh&ocirc;ng &iacute;t c&ugrave;ng nhau chơi đ&ugrave;a hảo hữu đều bị hắc thủ Hook trộm qua đồ vật, nhưng l&agrave; bản th&acirc;n hắn lại kh&ocirc;ng lấy lối đi nhỏ, thế l&agrave; đối hắc thủ Hook ấn tượng cũng kh&ocirc;ng s&acirc;u.<br />\r\n<br />\r\nHiện tại mới d&aacute;m x&aacute;c định vậy đ&uacute;ng l&agrave; b&igrave;nh thường.<br />\r\n<br />\r\nGia hỏa n&agrave;y vẫn l&agrave; c&acirc;́p th&acirc;́p Goblin liền c&oacute; thể c&oacute; được như thế tr&iacute; tuệ, sau n&agrave;y trở th&agrave;nh hắc thủ cũng kh&ocirc;ng phải kh&ocirc;ng c&oacute; đạo l&yacute;.<br />\r\n<br />\r\nTrong l&uacute;c nhất thời Triệu C&agrave;n Vũ t&acirc;m niệm thay đổi thật nhanh, hắn đột nhi&ecirc;n c&oacute; chủ &yacute;.<br />\r\n<br />\r\n&quot;Hook đ&uacute;ng kh&ocirc;ng, ngươi l&agrave; muốn sống , vẫn l&agrave; muốn chết đ&acirc;u?&quot; N&oacute;i, Triệu C&agrave;n Vũ tr&ecirc;n tay phải c&oacute; ngọn lửa ti&ecirc;u t&aacute;n ra tới, tựa như một gi&acirc;y sau liền sẽ c&oacute; hỏa cầu từ trong tay ph&oacute;ng xu&acirc;́t ra.<br />\r\n<br />\r\nNh&igrave;n thấy kia một tia mang theo kh&iacute; tức nguy hiểm kh&ocirc;ng ổn &aacute;nh lửa, Hook trong mắt sợ h&atilde;i c&agrave;ng tăng l&ecirc;n, th&acirc;n thể run như run rẩy.<br />\r\n<br />\r\nTại Triệu C&agrave;n Vũ trước mặt n&oacute; kh&ocirc;ng d&aacute;m c&oacute; bất kỳ động t&aacute;c kh&aacute;c.<br />\r\n<br />\r\n&quot;Muốn sống... Muốn sống... Đại nh&acirc;n, tiểu nh&acirc;n muốn sống!&quot;<br />\r\n<br />\r\nTriệu C&agrave;n Vũ thỏa m&atilde;n nhẹ gật đầu, lộ ra một c&aacute;i mỉm cười th&acirc;n thiện.<br />\r\n<br />\r\nNhưng ở trong mắt Hook, c&aacute;i n&agrave;y kh&ocirc;ng thể nghi ngờ c&ugrave;ng &Aacute;c ma tiếu dung kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c nhau.<br />\r\n<br />\r\nSau đ&oacute; lời n&oacute;i c&agrave;ng l&agrave;m cho n&oacute; huyết dịch khắp người đều phảng phất đọng lại b&igrave;nh thường.<br />\r\n<br />\r\n&quot;Như vậy, nhận ta l&agrave;m chủ đi.&quot;<br />\r\n<br />\r\nTriệu C&agrave;n Vũ trong miệng nhận chủ, cũng kh&ocirc;ng phải thật đơn giản miệng thừa nhận đơn giản như vậy.<br />\r\n<br />\r\nHook ngay tại run run th&acirc;n thể bởi v&igrave; n&agrave;y c&acirc;u n&oacute;i đột nhi&ecirc;n dừng lại, mặt của n&oacute; vậy nh&iacute;u giống như l&agrave; một c&aacute;i h&eacute;o rũ mướp đắng.<br />\r\n<br />\r\nHook nh&igrave;n xem Triệu C&agrave;n Vũ tấm kia như cười như kh&ocirc;ng mặt, c&oacute; ch&uacute;t kh&ocirc;ng nắm chắc được v&igrave; c&aacute;i g&igrave; c&aacute;i n&agrave;y dạng một c&aacute;i ma ph&aacute;p sư đại nh&acirc;n thế m&agrave; lại đưa ra y&ecirc;u cầu như vậy.<br />\r\n<br />\r\n&quot;Thế n&agrave;o, kh&ocirc;ng nguyện &yacute; sao?&quot;<br />\r\n<br />\r\n&quot;Nguyện &yacute;, nguyện &yacute;.&quot; Hook th&acirc;n thể đột nhi&ecirc;n run run một lần, n&oacute; c&uacute;i đầu xuống, một c&aacute;i từ m&agrave;u lam tia s&aacute;ng đủ th&agrave;nh phức tạp ph&aacute;p trận từ đỉnh đầu nổi l&ecirc;n.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ th&ocirc;i động trong cơ thể ph&aacute;p lực, tại tr&ecirc;n trận ph&aacute;p lưu lại bản th&acirc;n k&yacute; hiệu.<br />\r\n<br />\r\nTừ n&agrave;y k&yacute; hiệu in v&agrave;o Goblin sinh mệnh văn in l&ecirc;n, Hook đ&atilde; triệt để biến th&agrave;nh của hắn t&ocirc;i tớ.<br />\r\n<br />\r\nC&aacute;i n&agrave;y hậu thế đại danh đỉnh đỉnh Goblin b&acirc;y giờ sinh tử bất qu&aacute; tại ch&iacute;nh m&igrave;nh một &yacute; niệm.<br />\r\n<br />\r\n&quot;Ho&agrave;n th&agrave;nh th&agrave;nh tựu [ c&aacute;i thứ nhất n&ocirc; bộc ] : Đại trượng phu lập thế, sao c&oacute; thể kh&ocirc;ng c&oacute; gi&uacute;p đỡ? Nhớ được muốn đối xử tử tế nh&acirc;n vi&ecirc;n, kh&ocirc;ng cần l&agrave;m l&atilde;o bản l&ograve;ng dạ đen tối. Ch&uacute;c mừng ng&agrave;i thu hoạch được trở xuống ban thưởng: Tập được kỹ năng [ &acirc;m b&iacute;ch ] , điểm kinh nghiệm 500 điểm.&quot;<br />\r\n<br />\r\n&quot;Ch&uacute;c mừng ng&agrave;i thăng cấp đến LV. 4! Thu hoạch được một điểm tự do đi&ecirc;̉m thu&ocirc;̣c tính!&quot;<br />\r\n<br />\r\nTại đ&aacute;nh giết Botu sau Triệu C&agrave;n Vũ kh&ocirc;ng c&oacute; thu được điểm kinh nghiệm nhắc nhở l&uacute;c, hắn liền hiểu giết qu&aacute;i thu hoạch được điểm kinh nghiệm con đường n&agrave;y đ&atilde; ho&agrave;n to&agrave;n bị chặt đứt, hắn hiện tại chỉ c&oacute; thể dựa v&agrave;o th&agrave;nh tựu hệ thống đến thu hoạch kinh nghiệm.<br />\r\n<br />\r\nMặc d&ugrave; con đường trở n&ecirc;n hẹp h&ograve;i, nhưng l&agrave; th&agrave;nh tựu đạt th&agrave;nh th&igrave; cho kinh nghiệm thật kh&ocirc;ng &iacute;t.<br />\r\n<br />\r\nNgắn ngủi mười ph&uacute;t b&ecirc;n trong hắn liền dựa v&agrave;o th&agrave;nh tựu hệ thống đạt tới LV. 4, tr&ograve; chơi mở server trận kia v&igrave; đạt tới LV. 4 hắn nhưng l&agrave; bận rộn cả ng&agrave;y.<br />\r\n<br />\r\nĐ&acirc;y cũng l&agrave; một chuyện tốt, d&ugrave; sao nhậm chức c&ugrave;ng rất nhiều phương diện đều cần nhất định chủng tộc đẳng cấp ủng hộ.<br />\r\n<br />\r\n&Acirc;m b&iacute;ch kỹ năng n&agrave;y cũng l&agrave; bạn cũ, ở kiếp trước Triệu C&agrave;n Vũ cũng bởi v&igrave; T&acirc;n Thủ th&ocirc;n c&aacute;i n&agrave;o đ&oacute; nhiệm vụ ẩn học được c&aacute;i n&agrave;y to&agrave;n server chỉ c&oacute; năm trăm c&aacute;i danh ngạch hi hữu kỹ năng.<br />\r\n<br />\r\nTh&agrave;nh tựu hệ thống mới l&agrave; vĩnh viễn thần, đ&aacute;nh qu&aacute;i luyện cấp l&agrave;m rơi đồ c&aacute;i g&igrave; đều yếu bạo.<br />\r\n<br />\r\nHook vẫn như cũ c&uacute;i thấp đầu l&acirc;u, l&uacute;c n&agrave;y th&acirc;n phận của n&oacute; đ&atilde; triệt để chuyển biến th&agrave;nh Triệu C&agrave;n Vũ n&ocirc; bộc, đương nhi&ecirc;n phải c&agrave;ng th&ecirc;m cung k&iacute;nh.<br />\r\n<br />\r\nTrong l&uacute;c nhất thời Goblin t&acirc;m l&yacute; ngũ vị tạp trần, cao hứng l&agrave; của m&igrave;nh mạng nhỏ bảo vệ, bi thương l&agrave; từ đ&oacute; n&oacute; kh&ocirc;ng c&ograve;n l&agrave; một con miễn ph&iacute; (free) Goblin.<br />\r\n<br />\r\n&quot;Đứng l&ecirc;n đi.&quot; Nửa ng&agrave;y, chủ nh&acirc;n thanh &acirc;m vang l&ecirc;n.<br />\r\n<br />\r\nHook lau mặt, bằng nhanh nhất tốc độ từ dưới đất b&ograve; dậy.<br />\r\n<br />\r\nN&oacute; đ&atilde; nghĩ th&ocirc;ng suốt, mặc d&ugrave; n&oacute; đ&atilde; biến th&agrave;nh nh&acirc;n loại n&ocirc; bộc, nhưng tốt xấu l&agrave; một ma ph&aacute;p sư n&ocirc; bộc, kh&ocirc;ng xấu hổ!<br />\r\n<br />\r\nNguy cơ sinh tử lập tức giải trừ, t&acirc;m tư linh hoạt Hook kh&ocirc;ng nhịn được bắt đầu tưởng tượng lấy bị Triệu C&agrave;n Vũ mang về x&atilde; hội lo&agrave;i người vẻ đẹp sinh sống.<br />\r\n<br />\r\nNhưng m&agrave; Triệu C&agrave;n Vũ c&acirc;u n&oacute;i tiếp theo lại l&agrave;m cho đ&aacute;ng thương Goblin to&agrave;n th&acirc;n cứng đờ.<br />\r\n<br />\r\n&quot;Mặc d&ugrave; ch&uacute;ng ta k&yacute; kết chủ ph&oacute; khế ước, nhưng l&agrave; ngươi y&ecirc;n t&acirc;m.&quot; Triệu C&agrave;n Vũ lại lộ ra chi&ecirc;u b&agrave;i kia hiền l&agrave;nh tiếu dung, tối thiểu trong mắt hắn ch&iacute;nh hắn một tiếu dung tr&agrave;n ngập hiền l&agrave;nh, &quot;Ta sẽ kh&ocirc;ng can thiệp ngươi sinh hoạt.&quot;<br />\r\n<br />\r\nGầy yếu Goblin như bị s&eacute;t đ&aacute;nh, ngơ ng&aacute;c đứng tại chỗ.<br />\r\n<br />\r\nC&aacute;i n&agrave;y ma ph&aacute;p sư t&aacute;m th&agrave;nh l&agrave; ở lấy n&oacute; l&agrave;m tr&ograve; cười.<br />\r\n<br />\r\nN&oacute; nguy&ecirc;n lai tưởng rằng c&aacute;i n&agrave;y giả heo ăn thịt hổ chủ l&agrave; coi trọng n&oacute; hơn người th&ocirc;ng minh t&agrave;i tr&iacute; mới phải thu n&oacute; l&agrave; bộc từ, hiện tại xem ra, sự t&igrave;nh kh&ocirc;ng hề giống n&oacute; nghĩ tốt đẹp như vậy.<br />\r\n<br />\r\nNh&igrave;n xem ng&acirc;y người như phỗng Goblin, Triệu C&agrave;n Vũ vẫn kh&ocirc;ng qu&ecirc;n bổ th&ecirc;m một đao: &quot;L&agrave;m sao? Gi&agrave;nh lấy tự do c&ograve;n kh&ocirc;ng vui vẻ sao?&quot;<br />\r\n<br />\r\nHook tr&ecirc;n mặt miễn cưỡng gạt ra một c&aacute;i so với kh&oacute;c c&ograve;n kh&oacute; nh&igrave;n hơn khu&ocirc;n mặt tươi cười: &quot;Đ&uacute;ng vậy, chủ nh&acirc;n, ta rất vui vẻ.&quot;<br />\r\n<br />\r\nVừa nghĩ tới lại phải về về đến ngơ ngơ ng&aacute;c ng&aacute;c tộc đ&agrave;n sinh hoạt, Hook đ&atilde; tại trong l&ograve;ng kh&ocirc;ng ngừng k&ecirc;u khổ.<br />\r\n<br />\r\nTa khả năng kh&ocirc;ng phải l&agrave; người, nhưng ngươi l&agrave; thật cẩu.<br />\r\n<br />\r\nGoblin trong l&ograve;ng tr&agrave;n ngập hận đời cảm x&uacute;c.<br />\r\n<br />\r\nChẳng lẽ ma ph&aacute;p sư liền c&oacute; thể t&ugrave;y &yacute; tr&ecirc;u đ&ugrave;a n&oacute; sao?<br />\r\n<br />\r\nTỉ mỉ nghĩ nghĩ, đ&aacute;p &aacute;n thế m&agrave; l&agrave; khẳng định.<br />\r\n<br />\r\nHiện thực t&agrave;n khốc để Hook trong l&ograve;ng c&agrave;ng l&agrave; h&ocirc;i bại kh&ocirc;ng chịu nổi.</p>', '2021-11-28 13:48:47', 20, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(222, 59, 'Ẩn tàng chức nghiệp', '4', '<p>Trong đ&ecirc;m b&ocirc;n ba, để Triệu C&agrave;n Vũ cuối c&ugrave;ng đuổi tại nắng sớm mờ mờ l&uacute;c chạy tới đ&iacute;ch đến của chuyến n&agrave;y &mdash;&mdash; trấn Sikh.<br />\r\n<br />\r\nTại lật xem th&acirc;n thể nguy&ecirc;n chủ &iacute;t đến thương cảm k&yacute; ức về sau, hắn rốt cuộc biết bản th&acirc;n th&acirc;n ở phương n&agrave;o.<br />\r\n<br />\r\nT&igrave;nh huống cũng kh&ocirc;ng như Triệu C&agrave;n Vũ trong tưởng tượng như thế ho&agrave;n mỹ.<br />\r\n<br />\r\n&Yacute; thức của hắn gi&aacute;ng sinh ở một c&aacute;i đại lục Metz khuynh hướng phương bắc, cực kỳ tới gần nơi cực h&agrave;n địa phương.<br />\r\n<br />\r\nNơi cực h&agrave;n hắn ngược lại l&agrave; tới qua rất nhiều lần, nhưng l&agrave; tại phụ cận th&agrave;nh trấn tr&uacute; lưu số lần c&oacute; thể đếm được tr&ecirc;n đầu ng&oacute;n tay. Trừ hắn ra, những thứ kh&aacute;c đại bộ phận player cũng rất &iacute;t chuy&ecirc;n thăm viếng phụ cận th&agrave;nh trấn.<br />\r\n<br />\r\nChỉ v&igrave; tr&ograve; chơi ph&iacute;a ch&iacute;nh thức tại thiết kế &laquo; Thần Vực &raquo; tr&ograve; chơi n&agrave;y th&igrave; giống như đem khu vực n&agrave;y l&atilde;ng qu&ecirc;n.<br />\r\n<br />\r\nBỏ qua một b&ecirc;n nơi cực h&agrave;n b&ecirc;n trong thừa th&atilde;i H&agrave;n Tủy c&ugrave;ng với kh&aacute;c một ch&uacute;t tr&acirc;n qu&yacute; kho&aacute;ng thạch b&ecirc;n ngo&agrave;i, phụ cận linh linh tinh tinh ph&acirc;n bố trong th&agrave;nh trấn cơ hồ kh&ocirc;ng c&oacute; c&aacute;i g&igrave; đối player c&oacute; chỗ trợ gi&uacute;p nhiệm vụ hoặc l&agrave; đạo cụ, liền ngay cả nội dung ch&iacute;nh tuyến cũng rất &iacute;t d&iacute;nh đến b&ecirc;n n&agrave;y th&ugrave;y chi địa.<br />\r\n<br />\r\nHắn c&agrave;ng hi vọng m&igrave;nh c&oacute; thể gi&aacute;ng sinh tại đại lục trung bộ, so với nơi n&agrave;y, hắn quen thuộc hơn đại lục trung bộ c&agrave;ng có th&ecirc;̉ dễ d&agrave;ng cho hắn thi triển quyền cước.<br />\r\n<br />\r\nKh&ocirc;ng hơn vạn sự t&igrave;nh vạn vật cũng kh&ocirc;ng thể qu&aacute; qu&aacute; nghi&ecirc;m khắc ho&agrave;n mỹ, c&oacute; được th&agrave;nh tựu sưu tập hệ thống hắn c&oacute; thể n&oacute;i l&agrave; c&oacute; được Thi&ecirc;n Hồ bắt đầu.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ ch&iacute;nh ch&iacute;nh từ hai c&aacute;i c&ograve;n buồn ngủ lộ ra phi thường mơ hồ vệ binh ở giữa đi v&agrave;o trấn nhỏ.<br />\r\n<br />\r\nC&aacute;i n&agrave;y dạng lười biếng NPC, từ khi thế giới kịch bản theo player số lượng đầy đủ mở ra thả sau cũng rất &iacute;t gặp được. . .<br />\r\n<br />\r\nChờ ch&uacute;t?<br />\r\n<br />\r\nTriệu C&agrave;n Vũ đột nhi&ecirc;n &yacute; thức được một vấn đề.<br />\r\n<br />\r\nTh&acirc;n thể nguy&ecirc;n chủ nh&acirc;n k&yacute; ức c&ugrave;ng c&aacute;c loại dấu hiệu mặt ngo&agrave;i, bản th&acirc;n gi&aacute;ng l&acirc;m ở c&aacute;i thế giới n&agrave;y thời gian tiết điểm, tựa hồ đang tr&ograve; chơi mở server trước đ&oacute;?<br />\r\n<br />\r\nTriệu C&agrave;n Vũ cố gắng nhớ lại một lần ni&ecirc;n đại, kinh ngạc ph&aacute;t hiện b&acirc;y giờ lại đ&uacute;ng như hắn sở liệu, thế giới n&agrave;y ở v&agrave;o tr&ograve; chơi mở server trước một năm.<br />\r\n<br />\r\nN&oacute;i như vậy. . . Tr&ecirc;n đường đi kh&ocirc;ng thấy player ch&acirc;n thực nguy&ecirc;n nh&acirc;n cũng kh&ocirc;ng phải l&agrave; hắn tưởng tượng player đ&atilde; triệt để từ nơi n&agrave;y tr&ecirc;n thế giới biến mất, m&agrave; l&agrave; player c&ograve;n kh&ocirc;ng c&oacute; gi&aacute;ng l&acirc;m thế giới n&agrave;y?<br />\r\n<br />\r\nTriệu C&agrave;n Vũ tr&aacute;i tim nhảy l&ecirc;n kịch liệt l&ecirc;n.<br />\r\n<br />\r\nHưng phấn, k&iacute;ch động. . .<br />\r\n<br />\r\nC&aacute;c loại phức tạp cảm x&uacute;c ngũ vị tạp trần, Triệu C&agrave;n Vũ trong đầu l&oacute;e qua rất nhiều &yacute; nghĩ.<br />\r\n<br />\r\nHắn biết r&otilde; loại n&agrave;y ti&ecirc;n cơ &yacute; vị như thế n&agrave;o.<br />\r\n<br />\r\nL&agrave;m t&aacute;m năm cấp l&atilde;o l&agrave;ng player, mặc d&ugrave; kh&ocirc;ng thể n&oacute;i Triệu C&agrave;n Vũ đối &laquo; Thần Vực &raquo; b&ecirc;n trong tất cả mọi chuyện cũng như l&ograve;ng b&agrave;n tay, nhưng l&agrave; trong tr&ograve; chơi ph&aacute;t sinh tất cả mọi chuyện lớn nhỏ kiện hắn tr&ecirc;n cơ bản đều c&oacute; nhất định ấn tượng.<br />\r\n<br />\r\nVượt mức quy định tin tức số lượng dự trữ phối hợp b&ecirc;n tr&ecirc;n biến dị th&agrave;nh tựu sưu tập hệ thống, Triệu C&agrave;n Vũ ở nơi n&agrave;y sắp trở n&ecirc;n nguy hiểm thế giới b&ecirc;n trong kh&ocirc;ng thể nghi ngờ c&oacute; được l&yacute; tưởng nhất bắt đầu.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ cưỡng &eacute;p đ&egrave; xuống t&acirc;m t&igrave;nh trong l&ograve;ng, nghĩ lại nghĩ th&ocirc;ng suốt một &iacute;t chuyện.<br />\r\n<br />\r\nBiết được player c&ugrave;ng NPC th&acirc;n phận kh&aacute;c biệt, Triệu C&agrave;n Vũ rất dễ d&agrave;ng liền phỏng đo&aacute;n xuất hiện ở l&agrave;m thế giới d&acirc;n bản địa hắn, đối với những c&aacute;i kia c&ograve;n chưa gi&aacute;ng sinh player tới n&oacute;i kh&ocirc;ng thể nghi ngờ l&agrave; c&aacute;i NPC.<br />\r\n<br />\r\nĐiều n&agrave;y cũng rất dễ giải th&iacute;ch v&igrave; c&aacute;i g&igrave; l&uacute;c trước hắn đ&aacute;nh giết Goblin, lại kh&ocirc;ng thu hoạch được bất luận c&aacute;i g&igrave; điểm kinh nghiệm vấn đề.<br />\r\n<br />\r\nMọi người đều biết, NPC kh&ocirc;ng c&oacute; c&aacute;c người chơi phổ biến c&oacute; thuộc t&iacute;nh bảng, bởi vậy bọn họ thăng cấp muốn tu&acirc;n theo thế giới tr&ograve; chơi kh&aacute;ch quan quy luật, dựa v&agrave;o player kia một bộ đ&aacute;nh qu&aacute;i thu hoạch kinh nghiệm đến thăng cấp l&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng thể thực hiện được.<br />\r\n<br />\r\nMặc d&ugrave; b&acirc;y giờ biến th&agrave;nh thế giới d&acirc;n bản địa, nhưng l&agrave; c&oacute; được th&agrave;nh tựu hệ thống c&ugrave;ng thuộc t&iacute;nh bảng Triệu C&agrave;n Vũ nghi&ecirc;m ngặt tr&ecirc;n &yacute; nghĩa kh&ocirc;ng thể ho&agrave;n to&agrave;n xem như một c&aacute;i NPC.<br />\r\n<br />\r\nNếu như nhất định phải kế tiếp định nghĩa, Triệu C&agrave;n Vũ l&agrave; NPC c&ugrave;ng player kết hợp thể.<br />\r\n<br />\r\nMột nh&aacute;y mắt, Triệu C&agrave;n Vũ suy tư rất nhiều.<br />\r\n<br />\r\nTrong l&uacute;c suy tư, hắn đ&atilde; đi tới một t&ograve;a nh&agrave; cao tầng trước mặt.<br />\r\n<br />\r\nNơi đ&acirc;y, ch&iacute;nh l&agrave; trấn b&ecirc;n tr&ecirc;n nhiều nh&agrave; trong lữ điếm một nh&agrave; trong đ&oacute; &mdash;&mdash; Thomas l&atilde;o cha lữ qu&aacute;n.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ tiến v&agrave;o trấn nhỏ sau liền xe nhẹ đường quen thẳng đến nh&agrave; n&agrave;y lữ qu&aacute;n, tự nhi&ecirc;n c&oacute; hắn t&iacute;nh to&aacute;n.<br />\r\n<br />\r\nChu&ocirc;ng cửa thanh minh ở giữa, Triệu C&agrave;n Vũ đẩy cửa v&agrave;o.<br />\r\n<br />\r\nC&ugrave;ng đa số lữ qu&aacute;n một dạng, đại sảnh cấu tạo cực kỳ đơn giản, mấy trương rộng lớn b&agrave;n rượu chiếm cứ tuyệt đại bộ phận kh&ocirc;ng gian, ở giữa quầy ba gồm cả pha rượu s&acirc;n b&atilde;i c&ugrave;ng lữ qu&aacute;n tiếp t&acirc;n c&ocirc;ng dụng.<br />\r\n<br />\r\nTrong kh&ocirc;ng kh&iacute; tr&agrave;n ngập rượu kém ch&acirc;́t lượng tinh hương vị, vừa nghe liền biết r&otilde; tối h&ocirc;m qua nơi n&agrave;y nhất định c&oacute; một trận để đ&aacute;m người say m&egrave;m rượu cục.<br />\r\n<br />\r\n&quot;L&atilde;o bản, gian ph&ograve;ng.&quot; Triệu C&agrave;n Vũ đi đến trước quầy bar, lấy tay g&otilde; g&otilde; mặt b&agrave;n, thức tỉnh gh&eacute; v&agrave;o quầy bar sau c&ograve;n trong giấc mộng nam nh&acirc;n.<br />\r\n<br />\r\n&quot;Ai vậy! Như thế kh&ocirc;ng c&oacute; mắt, quấy rầy bản đại gia. . . A, nguy&ecirc;n lai l&agrave; kh&aacute;ch nh&acirc;n t&ocirc;n qu&yacute; a. Thật c&oacute; lỗi, ta vừa rồi kh&ocirc;ng nghe r&otilde; ph&acirc;n ph&oacute; của ng&agrave;i. Phiền phức ng&agrave;i lập lại một lần nữa!&quot;<br />\r\n<br />\r\nBị đ&aacute;nh thức trung ni&ecirc;n nam nh&acirc;n g&atilde;i lấy rối bời t&oacute;c, vốn muốn thốt ra bực tức lại bởi v&igrave; Triệu C&agrave;n Vũ tr&ecirc;n tay vi&ecirc;n kia chuyển động, thả ra h&agrave;o quang v&agrave;ng &oacute;ng Lanck m&agrave; sinh sinh bị nam nh&acirc;n nuốt xuống.<br />\r\n<br />\r\nXem ra vị n&agrave;y ch&iacute;nh l&agrave; lữ qu&aacute;n chủ cửa h&agrave;ng Thomas l&atilde;o cha.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ hững hờ tr&ecirc;n tay xoay tr&ograve;n lấy tự th&agrave;nh liền hệ thống nơi đ&oacute; c&oacute; được một vi&ecirc;n kim Lanck, trong l&ograve;ng cũng kh&ocirc;ng khỏi cảm kh&aacute;i tiền t&agrave;i thế c&ocirc;ng uy lực.<br />\r\n<br />\r\nNh&igrave;n xem h&egrave;m rượu trong mũi năm nam nh&acirc;n t&aacute;o bạo h&igrave;nh tượng, nếu l&agrave; đổi lại những người kh&aacute;c sợ rằng đ&atilde; bị hắn chửi mắng một trận.<br />\r\n<br />\r\nCần biết, tại trấn Sikh dạng n&agrave;y thị trấn nhỏ nơi bi&ecirc;n giới, một vi&ecirc;n kim Lanck đầy đủ th&ocirc;ng thường nh&agrave; ba người thư thư phục phục sống tr&ecirc;n bốn th&aacute;ng.<br />\r\n<br />\r\nKhoản n&agrave;y &#39;Khoản tiền lớn&#39; đặt ở thương nh&acirc;n trước mặt, tự nhi&ecirc;n l&agrave;m cho nam nh&acirc;n tức giận qu&eacute;t sạch s&agrave;nh sanh.<br />\r\n<br />\r\nThomas con mắt c&oacute; ch&uacute;t chuyển động, kh&ocirc;ng lưu dấu vết phi tốc quan s&aacute;t một ch&uacute;t vị n&agrave;y c&oacute; thể m&oacute;c ra một vi&ecirc;n kim Lanck h&agrave;o kh&aacute;ch.<br />\r\n<br />\r\nL&uacute;c n&agrave;y Triệu C&agrave;n Vũ tr&ecirc;n th&acirc;n c&ograve;n mặc th&acirc;n thể nguy&ecirc;n chủ kia th&acirc;n vải th&ocirc; &aacute;o gai, quần &aacute;o b&ecirc;n tr&ecirc;n cũng bởi v&igrave; Goblin l&ocirc;i k&eacute;o m&agrave; d&iacute;nh v&agrave;o một ch&uacute;t b&ugrave;n đất.<br />\r\n<br />\r\nNếu kh&ocirc;ng phải tận mắt nh&igrave;n thấy, Thomas l&agrave;m sao cũng v&ocirc; ph&aacute;p tưởng tượng c&oacute; được một bộ nạn d&acirc;n h&igrave;nh tượng người thanh ni&ecirc;n thế m&agrave; có th&ecirc;̉ một lần m&oacute;c ra một vi&ecirc;n kim Lanck.<br />\r\n<br />\r\nNhưng m&agrave; kh&aacute;ch h&agrave;ng ch&iacute;nh l&agrave; Thượng Đế, trở ngại kim Lanck phương diện t&igrave;nh cảm, vị n&agrave;y tinh minh thương nh&acirc;n nhưng kh&ocirc;ng c&oacute; quản nhiều như vậy.<br />\r\n<br />\r\nThomas xoa xoa tay mắt lom lom nh&igrave;n Triệu C&agrave;n Vũ , chờ đợi lấy người tuổi trẻ ph&acirc;n ph&oacute;.<br />\r\n<br />\r\n&quot;Cho ta mở một gian ph&ograve;ng kh&aacute;ch, ta muốn ở đ&acirc;y ở một thời gian ngắn.&quot;<br />\r\n<br />\r\n&quot;Được rồi, ti&ecirc;n sinh, xin chờ một ch&uacute;t.&quot; Trong l&ograve;ng c&oacute; để Thomas kh&ocirc;ng ch&uacute;t n&agrave;o mập mờ.<br />\r\n<br />\r\nTrung ni&ecirc;n nam nh&acirc;n lập tức từ một chuỗi ch&igrave;a kho&aacute; b&ecirc;n trong t&igrave;m ra một thanh đưa cho Triệu C&agrave;n Vũ.<br />\r\n<br />\r\n&quot;Xin cầm lấy ch&igrave;a kho&aacute;, ti&ecirc;n sinh, ng&agrave;i số ph&ograve;ng l&agrave; 215.&quot; Nh&igrave;n thấy vi&ecirc;n kia kim Lanck, Thomas kh&ocirc;ng lo lắng Triệu C&agrave;n Vũ sẽ ở B&aacute; Vương cửa h&agrave;ng.<br />\r\n<br />\r\n&quot;R&acirc;́t t&ocirc;́t.&quot; Quay người rời đi thời khắc, Triệu C&agrave;n Vũ đột nhi&ecirc;n quay đầu, &quot;Vừa rồi qu&ecirc;n n&oacute;i, chuẩn bị cho ta hai bộ ra d&aacute;ng quần &aacute;o, một hồi đưa ra.&quot;<br />\r\n<br />\r\n&quot;Được rồi, ti&ecirc;n sinh.&quot; Thomas thật l&ograve;ng tư th&aacute;i, thật giống như nhận được đại nh&acirc;n vật g&igrave; trọng yếu chỉ thị.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; c&aacute;ch, người c&oacute; tiền tại lợi lớn thương nh&acirc;n trong mắt ch&iacute;nh l&agrave; đại gia.<br />\r\n<br />\r\nNh&igrave;n xem Triệu C&agrave;n Vũ b&oacute;ng lưng, trung ni&ecirc;n nam nh&acirc;n ở trong l&ograve;ng lẩm bẩm một c&acirc;u.<br />\r\n<br />\r\nC&aacute;i n&agrave;y sợ rằng lại l&agrave; nh&agrave; n&agrave;o thiếu gia chơi t&acirc;m nổi l&ecirc;n, thế m&agrave; mặc y phục r&aacute;ch rưới đến ti&ecirc;u khiển m&igrave;nh. C&ograve;n tốt vừa rồi bản th&acirc;n đ&egrave; lại nộ kh&iacute;, bằng kh&ocirc;ng hậu quả thiết tưởng kh&ocirc;ng chịu nổi.<br />\r\n<br />\r\nHậu tri hậu gi&aacute;c Thomas cũng kh&ocirc;ng khỏi được cảm gi&aacute;c được một trận hoảng sợ, trong l&ograve;ng đối vị gia này th&acirc;n phận cũng c&oacute; một ch&uacute;t suy đo&aacute;n.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nSau khi rửa mặt, nằm ở mềm mại tr&ecirc;n giường lớn Triệu C&agrave;n Vũ cuối c&ugrave;ng c&oacute; cơ hội thở dốc.<br />\r\n<br />\r\nMặc d&ugrave; b&acirc;y giờ ma vật c&ograve;n kh&ocirc;ng c&oacute; đạt tới tr&agrave;n lan t&igrave;nh trạng, tối h&ocirc;m qua h&agrave;nh tr&igrave;nh cũng kh&ocirc;ng qu&aacute; mức nguy hiểm, nhưng l&agrave; đơn thuần đi rồi một đ&ecirc;m đường ban đ&ecirc;m vậy ti&ecirc;u hao hắn đại lượng tinh lực c&ugrave;ng thể lực.<br />\r\n<br />\r\nHiện tại hắn vậy cuối c&ugrave;ng c&oacute; thời gian suy nghĩ bước kế tiếp kế hoạch.<br />\r\n<br />\r\nĐang suy đo&aacute;n player tỉ lệ lớn sẽ ở một năm sau gi&aacute;ng l&acirc;m thế giới n&agrave;y về sau, Triệu C&agrave;n Vũ trong l&ograve;ng cũng c&oacute; kh&ocirc;ng nhỏ cảm gi&aacute;c cấp b&aacute;ch.<br />\r\n<br />\r\nPlayer gi&aacute;ng l&acirc;m mang &yacute; nghĩa ma vật mang tới tai nạn lớn diện t&iacute;ch bộc ph&aacute;t, vậy mang &yacute; nghĩa thế giới n&agrave;y trở n&ecirc;n c&agrave;ng th&ecirc;m nguy hiểm.<br />\r\n<br />\r\nHắn nhất định phải nắm chặt qu&yacute; gi&aacute; n&agrave;y kỳ an to&agrave;n, tận khả năng tăng cường thực lực bản th&acirc;n.<br />\r\n<br />\r\nViệc cấp b&aacute;ch l&agrave; tranh thủ thời gian nhậm chức một c&aacute;i chiến đấu nghề nghiệp đến đề thăng lực chiến đấu của m&igrave;nh, chỉ c&oacute; c&oacute; đủ thực lực hắn c&oacute; thể thực hiện của m&igrave;nh một &iacute;t kế hoạch.<br />\r\n<br />\r\nChiến đấu nghề nghiệp, l&agrave; nh&acirc;n loại lực lượng căn bản nơi ph&aacute;t ra, cũng l&agrave; NPC c&ugrave;ng player ở giữa &iacute;t c&oacute; ho&agrave;n to&agrave;n đồng bộ đồ vật.<br />\r\n<br />\r\n&laquo; Thần Vực &raquo; b&ecirc;n trong cơ sở chiến đấu nghề nghiệp hệ thống tổng cộng chia l&agrave;m chiến sĩ, ma ph&aacute;p sư, cung tiễn thủ, mục sư, kỵ sĩ, đạo tặc, Launcher bảy đại nghề nghiệp hệ, mỗi c&aacute;i cơ sở chiến đấu nghề nghiệp thể hệ nhậm chức đ&aacute;m người đang chiến đấu nghề nghiệp đẳng cấp đạt tới nhất định hạn mức cao nhất sau liền c&oacute; thể chuyển chức th&agrave;nh hệ thống b&ecirc;n trong tiếp theo giai đoạn một loại n&agrave;o đ&oacute; nghề nghiệp.<br />\r\n<br />\r\nTheo ho&agrave;n to&agrave;n thống k&ecirc;, chỉ l&agrave; bảy đại nghề nghiệp hệ thống liền bao gồm tổng cộng gần hai trăm loại nghề nghiệp, đ&acirc;y cũng l&agrave; &laquo; Thần Vực &raquo; cao độ tự do một loại thể hiện.<br />\r\n<br />\r\nTrừ bảy đại cơ sở chiến đấu nghề nghiệp hệ thống b&ecirc;n ngo&agrave;i, c&ograve;n c&oacute; c&aacute;i kh&aacute;c một số nhỏ c&acirc;̀n đặc th&ugrave; điều kiện t&agrave;i năng nhậm chức chiến đấu nghề nghiệp hệ thống được xưng l&agrave; &mdash;&mdash; ẩn t&agrave;ng chức nghiệp hệ thống.<br />\r\n<br />\r\nL&uacute;c đ&oacute; Triệu C&agrave;n Vũ đến tr&ograve; chơi đ&oacute;ng Server trước mới đạt tới cửu chuyển đấu thần cao độ, nhưng cũng sức chiến đấu thường thường.<br />\r\n<br />\r\nB&acirc;y giờ, sớm gi&aacute;ng sinh tr&ecirc;n đại lục Metz hắn, t&acirc;m tư tự nhi&ecirc;n linh hoạt.<br />\r\n<br />\r\nNhậm chức một c&aacute;i b&igrave;nh thường nghề nghiệp, ho&agrave;n to&agrave;n kh&ocirc;ng ph&ugrave; hợp t&acirc;m l&yacute; của hắn mong chờ.<br />\r\n<br />\r\nM&agrave;n s&aacute;ng b&ecirc;n tr&ecirc;n th&agrave;nh tựu điều mục nhấp nh&ocirc;, Triệu C&agrave;n Vũ từng c&aacute;i xem lấy.<br />\r\n<br />\r\nĐột nhi&ecirc;n, hắn hai mắt ngưng lại, kh&oacute;a được một đầu th&agrave;nh tựu nhiệm vụ.<br />\r\n<br />\r\n&quot;[ ph&aacute; huỷ S&agrave;i Lang nh&acirc;n s&agrave;o huyệt ] : S&agrave;i Lang nh&acirc;n tại x&atilde; hội lo&agrave;i người dư luận b&ecirc;n trong kh&ocirc;ng thể nghi ngờ l&agrave; người người k&ecirc;u đ&aacute;nh chuột chạy qua đường, th&acirc;n l&agrave; dũng giả ngươi nhất định kh&aacute;t vọng th&agrave;nh lập một phen c&ocirc;ng lao sự nghiệp, như vậy th&igrave; trước từ ph&aacute; huỷ một to&agrave; S&agrave;i Lang nh&acirc;n s&agrave;o huyệt cũng giết chết trong s&agrave;o huyệt sở hữu S&agrave;i Lang nh&acirc;n bắt đầu đi!<br />\r\n<br />\r\nBan thưởng: Ch&uacute; Ấn sư nghề nghiệp hệ thống nhậm chức quyển trục một cuốn, kim Lanck 5 mai, điểm kinh nghiệm 2000 điểm.&quot;<br />\r\n<br />\r\nKim Lanck c&ugrave;ng điểm kinh nghiệm đều l&agrave; vật k&egrave;m theo, Triệu C&agrave;n Vũ ch&uacute; &yacute; c&aacute;i n&agrave;y th&agrave;nh tựu nhiệm vụ trọng điểm ở chỗ Ch&uacute; Ấn sư nghề nghiệp thể hệ nhậm chức tr&ecirc;n quyển trục.</p>', '2021-11-28 13:49:02', 19, 1),
(223, 59, 'Quán rượu kiến thức', '5', '<p>Nếu như Triệu C&agrave;n Vũ nhớ kh&ocirc;ng lầm, Ch&uacute; Ấn sư nghề nghiệp hệ thống tại to&agrave;n bộ Thần Vực ở trong l&agrave; cấp thế giới duy nhất t&iacute;nh nghề nghiệp.<br />\r\n<br />\r\nBao qu&aacute;t thế giới d&acirc;n bản địa c&ugrave;ng ngoại lai player ở b&ecirc;n trong sở hữu trong nh&acirc;n loại, chỉ c&oacute; một người c&oacute; thể ở nơi n&agrave;y chiến đấu nghề nghiệp hệ thống b&ecirc;n trong nhậm chức.<br />\r\n<br />\r\nỞ kiếp trước Triệu C&agrave;n Vũ vẫn l&agrave; player l&uacute;c, liền từng nghe n&oacute;i li&ecirc;n quan tới Ch&uacute; Ấn sư c&aacute;c loại nghe đồn, đ&atilde; từng c&oacute; may mắn được gặp c&aacute;i kia nhậm chức Ch&uacute; Ấn sư player tại một trận chiến đấu b&ecirc;n trong xuất thủ.<br />\r\n<br />\r\nMặc d&ugrave; khi đ&oacute; thế giới kịch bản chỉ tiến h&agrave;nh đến trung kỳ, nhưng l&agrave; c&aacute;i kia Ch&uacute; Ấn sư liền đ&atilde; c&oacute; kh&ocirc;ng thể khinh thường, lại để c&aacute;i kh&aacute;c player gh&eacute; mắt lực lượng cường đại.<br />\r\n<br />\r\nẨn t&agrave;ng chức nghiệp tất nhi&ecirc;n so rộng khắp phổ cập đại ch&uacute;ng nghề nghiệp c&oacute; nhiều hơn đặc điểm c&ugrave;ng chỗ độc đ&aacute;o, đ&acirc;y cơ hồ l&agrave; player b&ecirc;n trong thừa h&agrave;nh c&ocirc;ng l&yacute;.<br />\r\n<br />\r\nLật nh&igrave;n l&acirc;u như vậy, giống như chỉ c&oacute; c&aacute;i n&agrave;y th&agrave;nh tựu nhiệm vụ tương đối ph&ugrave; hợp nhu cầu của hắn.<br />\r\n<br />\r\nC&aacute;i kh&aacute;c c&oacute; thể thu hoạch được chiến đấu nghề nghiệp nhậm chức quyển trục th&agrave;nh tựu điều mục đối với hắn hiện tại tới n&oacute;i qu&aacute; mức kh&oacute; khăn, Triệu C&agrave;n Vũ cũng chỉ c&oacute; thể lực bất t&ograve;ng t&acirc;m.<br />\r\n<br />\r\n&quot;Chỉ l&agrave;. . . Việc n&agrave;y sợ rằng c&ograve;n cần b&agrave;n bạc kỹ hơn. . .&quot; Triệu C&agrave;n Vũ kh&ocirc;ng c&oacute; bị nhường cho người tim đập th&igrave;nh thịch ban thưởng l&agrave;m cho&aacute;ng v&aacute;ng đầu &oacute;c.<br />\r\n<br />\r\nC&oacute; năm n&agrave;y th&aacute;ng nọ tr&ograve; chơi kinh nghiệm, Triệu C&agrave;n Vũ rất r&otilde; r&agrave;ng b&acirc;y giờ m&igrave;nh v&agrave; hung h&atilde;n S&agrave;i Lang nh&acirc;n so ra quả thực qu&aacute; yếu g&agrave;.<br />\r\n<br />\r\nMột đối một giết chết một con S&agrave;i Lang nh&acirc;n đều rất kh&oacute; khăn, c&agrave;ng n&oacute;i thế n&agrave;o ti&ecirc;u diệt cả một c&aacute;i s&agrave;o huyệt S&agrave;i Lang nh&acirc;n.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ suy nghĩ tung bay ở giữa, tại tinh thần c&ugrave;ng nhục thể song trọng &aacute;p lực dưới, hắn kh&ocirc;ng khỏi ngủ say sưa b&ecirc;n dưới.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nChờ đến Triệu C&agrave;n Vũ lần nữa mở mắt ra, ch&acirc;n trời lưu lại một vệt hồng h&agrave; để hắn hiểu được m&igrave;nh đ&atilde; ngủ thẳng tới chạng vạng tối.<br />\r\n<br />\r\nC&aacute;i n&agrave;y một giấc k&eacute;o d&agrave;i thời gian kh&ocirc;ng thể bảo l&agrave; kh&ocirc;ng d&agrave;i.<br />\r\n<br />\r\nTrong bụng cảm gi&aacute;c trống rỗng c&agrave;ng ph&aacute;t ra m&atilde;nh liệt, thay xong người phục vụ đưa l&ecirc;n quần &aacute;o, đeo l&ecirc;n thi thuật giả găng tay Triệu C&agrave;n Vũ lần theo dưới lầu ẩn ẩn truyền tới tiếng ồn &agrave;o c&ugrave;ng kh&oacute;i lửa đi tới lầu một đại sảnh.<br />\r\n<br />\r\nV&agrave;i chiếc đ&egrave;n đuốc mờ tối l&agrave;m th&ocirc; dầu hoả đ&egrave;n đem kh&ocirc;ng lớn kh&ocirc;ng gian b&ecirc;n trong ăn uống linh đ&igrave;nh, n&acirc;ng ly cạn ch&eacute;n, oẳn t&ugrave; t&igrave; vui cười giận mắng chiếu rọi được r&otilde; r&agrave;ng r&agrave;nh mạch.<br />\r\n<br />\r\nThấp k&eacute;m cồn tr&agrave;n ngập ở đại sảnh mỗi một nơi hẻo l&aacute;nh, thức ăn hương kh&iacute; để Triệu C&agrave;n Vũ cổ họng bỗng nh&uacute;c nh&iacute;ch qua một c&aacute;i.<br />\r\n<br />\r\nĐ&atilde; từng khi n&agrave;o, l&agrave;m player hắn liền từng cảm th&aacute;n qua &laquo; Thần Vực &raquo; phi thường ch&acirc;n thật, kh&ocirc;ng nghĩ tới b&acirc;y giờ v&acirc;̣y mà trực tiếp để hắn cảm nhận được thuần t&uacute;y nhất ch&acirc;n thật.<br />\r\n<br />\r\nHắn hiện tại cũng kh&ocirc;ng khỏi được cảm th&aacute;n Thomas l&atilde;o cha đầu &oacute;c bu&ocirc;n b&aacute;n, thế m&agrave; có th&ecirc;̉ nghĩ đến đem lữ qu&aacute;n lầu một đại sảnh cải biến th&agrave;nh một c&aacute;i cỡ nhỏ qu&aacute;n rượu.<br />\r\n<br />\r\nMột d&atilde;y nh&agrave; kiếm được hai lần tiền, thật sự l&agrave; hảo thủ đoạn.<br />\r\n<br />\r\nT&ugrave;y &yacute; chọn Trương Gi&aacute;c kh&ocirc;ng minh kh&ocirc;ng người c&aacute;i b&agrave;n điểm ch&uacute;t đồ ăn, Triệu C&agrave;n Vũ đột nhi&ecirc;n tại ồn &agrave;o trong tiếng tr&ograve; chuyện bắt được bản th&acirc;n cảm gi&aacute;c hứng th&uacute; từ ngữ.<br />\r\n<br />\r\nQu&aacute;n rượu từ trước đến nay đều l&agrave; nghe ng&oacute;ng tin tức nơi đến tốt đẹp, lời n&agrave;y một ch&uacute;t cũng kh&ocirc;ng giả.<br />\r\n<br />\r\nCh&iacute;nh l&agrave; bởi v&igrave; kh&aacute;ch uống rượu nh&oacute;m th&acirc;n phận ngư long hỗn tạp, tam lục cửu dạy người đều sẽ xuất hiện ở qu&aacute;n rượu. Nếu c&oacute; t&acirc;m, liền c&oacute; thể t&igrave;m hiểu đến cần tin tức.<br />\r\n<br />\r\n&quot;Barry, nghe n&oacute;i ngươi dong binh đo&agrave;n gần nhất l&agrave;m phiếu lớn a, tiểu tử ngươi khẳng định m&ograve; được kh&ocirc;ng &iacute;t chất b&eacute;o đi.&quot;<br />\r\n<br />\r\n&quot;Ha ha, tin tức của ngươi l&aacute;u lỉnh th&ocirc;ng sao? L&atilde;o đại vừa mới mang bọn ta nộp nhiệm vụ, ngươi sẽ biết.&quot;<br />\r\n<br />\r\n&quot;Vậy khẳng định a. . . Trấn Sikh liền kh&ocirc;ng c&oacute; ta &#39;Vạn Sự Th&ocirc;ng&#39; Fitz kh&ocirc;ng biết sự t&igrave;nh, c&aacute;c ngươi đương thời k&eacute;o lấy mấy c&aacute;i kia rướm m&aacute;u bao tải. Chậc chậc chậc, nghĩ kh&ocirc;ng l&agrave;m cho sự ch&uacute; &yacute; của người kh&aacute;c cũng kh&oacute; khăn a. . .&quot;<br />\r\n<br />\r\n&quot;Th&iacute;ch, thiếu tự bi&ecirc;n tự diễn. Kh&ocirc;ng sai, ch&uacute;ng ta lần n&agrave;y ch&iacute;nh l&agrave; tiếp săn giết S&agrave;i Lang nh&acirc;n nhiệm vụ. Những s&uacute;c sinh n&agrave;y cũng thật l&agrave; hung h&atilde;n, chỉ l&agrave; săn giết mười đầu th&ocirc;ng thường S&agrave;i Lang nh&acirc;n liền qu&aacute; sức, kh&ocirc;ng nghĩ tới cuối c&ugrave;ng. . .&quot; Giảng đến nơi đ&acirc;y, c&aacute;i thanh &acirc;m kia tựa hồ c&oacute; ch&uacute;t do dự &quot;Ch&uacute;ng ta cuối c&ugrave;ng rời đi thời điểm kh&ocirc;ng cẩn thận tr&ecirc;u chọc đến con kia sắp tấn thăng Tinh Thiết giai S&agrave;i Lang Nh&acirc;n chi vương, tổn thất mấy c&aacute;i huynh đệ.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave;?&quot; Một c&aacute;i thanh &acirc;m kh&aacute;c kinh h&ocirc; một tiếng &quot;C&aacute;i n&agrave;y sao c&oacute; thể, nếu l&agrave; thật gặp gỡ S&agrave;i Lang Nh&acirc;n chi vương, tiểu tử ngươi có th&ecirc;̉ ho&agrave;n chỉnh trở về?&quot;<br />\r\n<br />\r\nN&oacute;i tới nhạy cảm bộ phận, hai người thanh tuyến đều tận lực đ&egrave; thấp, chung quanh ồn &agrave;o v&ocirc; c&ugrave;ng ho&agrave;n cảnh để Triệu C&agrave;n Vũ kh&oacute; m&agrave; nghe r&otilde; ph&iacute;a sau n&oacute;i chuyện.<br />\r\n<br />\r\n&quot;S&agrave;i Lang Nh&acirc;n chi vương &agrave;. . . Tinh Thiết cấp, c&oacute; ch&uacute;t kh&oacute; đối ph&oacute; a. . .&quot;<br />\r\n<br />\r\nThanh ni&ecirc;n hai tay chống lấy c&aacute;i cằm, tr&ecirc;n mặt lộ ra vẻ suy tư.<br />\r\n<br />\r\nTrong thế giới n&agrave;y, ma vật giống như nh&acirc;n loại c&oacute; được cấp 200 đẳng cấp hạn mức cao nhất, căn cứ đẳng cấp c&ugrave;ng ph&acirc;n chia thực lực mười c&aacute;i giai vị, từ yếu đến mạnh theo thứ tự chia l&agrave;m X&iacute;ch Đồng giai, Tinh Thiết giai, Thước Kim giai, Huy Tinh giai, Phỉ Th&uacute;y giai, X&aacute;n Thần giai, Diệu Nguyệt giai, Diệu Nhật giai, Bất Hủ giai, Ma Thần giai, mỗi hai mươi cấp v&igrave; nhất giai.<br />\r\n<br />\r\nGiống trước đ&oacute; bị Triệu C&agrave;n Vũ đ&aacute;nh chết Botu c&ugrave;ng bị thu phục Hook, đều l&agrave; X&iacute;ch Đồng giai cấp ba tả hữu ma vật.<br />\r\n<br />\r\nB&igrave;nh thường S&agrave;i Lang nh&acirc;n cơ bản đều l&agrave; ở v&agrave;o X&iacute;ch Đồng giai khoảng cấp mười ma vật, S&agrave;i Lang Nh&acirc;n chi vương b&igrave;nh thường l&agrave; Tinh Thiết giai hơn hai mươi cấp.<br />\r\n<br />\r\nS&agrave;i Lang Nh&acirc;n chi vương tồn tại, để Triệu C&agrave;n Vũ biết được hai c&aacute;i tin tức.<br />\r\n<br />\r\nTrấn Sikh phụ cận nhất định c&oacute; một S&agrave;i Lang nh&acirc;n s&agrave;o huyệt.<br />\r\n<br />\r\nC&aacute;i n&agrave;y S&agrave;i Lang nh&acirc;n s&agrave;o huyệt nhất định mới h&igrave;nh th&agrave;nh kh&ocirc;ng l&acirc;u.<br />\r\n<br />\r\nMọi người đều biết, tiểu tộc quần b&ecirc;n trong chỉ c&oacute; xuất hiện đủ để thống lĩnh tộc quần vương giả, mới c&oacute; thể x&acirc;y th&agrave;nh ma vật s&agrave;o huyệt. Vừa rồi nghe hai người kia đối thoại, c&aacute;i n&agrave;y S&agrave;i Lang Nh&acirc;n chi vương hiển nhi&ecirc;n c&ograve;n kh&ocirc;ng c&oacute; đạt tới b&igrave;nh thường S&agrave;i Lang Nh&acirc;n chi vương giai vị, chỉ c&oacute; thể n&oacute;i r&otilde; n&oacute; vừa trở th&agrave;nh S&agrave;i Lang Nh&acirc;n chi vương kh&ocirc;ng l&acirc;u.<br />\r\n<br />\r\n&quot;Ho&agrave;n th&agrave;nh th&agrave;nh tựu [ qu&aacute;n rượu kiến thức ] : Hợp c&aacute;ch dũng giả sẽ lợi dụng hết thảy sự vật v&igrave; ch&iacute;nh m&igrave;nh trải đường. Ngươi th&agrave;nh c&ocirc;ng lần thứ nhất tại dị giới trong tửu qu&aacute;n thăm d&ograve; được tin tức m&igrave;nh muốn, nhưng l&agrave; nhất thiết phải ch&uacute; &yacute; ph&acirc;n biệt những tin tức n&agrave;y l&agrave; thật hay giả. Ch&uacute;c mừng ng&agrave;i thu hoạch được trở xuống ban thưởng: Một cấp kh&ocirc;ng gian trữ vật, điểm kinh nghiệm 200 điểm!&quot;<br />\r\n<br />\r\nTriệu Thi&ecirc;n vũ ngạc nhi&ecirc;n, ở kiếp trước ho&agrave;n th&agrave;nh th&agrave;nh tựu kh&ocirc;ng c&oacute; bất kỳ c&aacute;i g&igrave; t&iacute;nh thực chất ban thưởng, dẫn đến hắn đối kia phức tạp th&agrave;nh tựu điều mục ấn tượng cũng kh&ocirc;ng sao m&agrave; khắc s&acirc;u.<br />\r\n<br />\r\nLại c&ograve;n c&oacute; loại n&agrave;y th&agrave;nh tựu? Hắn cũng kh&ocirc;ng khỏi được cảm gi&aacute;c được một tia kinh ngạc.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng nghĩ tới th&agrave;nh tựu điều mục bao tr&ugrave;m phạm vi rộng như vậy &agrave;. . .&quot; Đ&acirc;y đối với hắn tới n&oacute;i kh&ocirc;ng thể nghi ngờ l&agrave; một chuyện tốt, th&agrave;nh tựu điều mục c&agrave;ng nhiều đại biểu cho hắn c&oacute; thể thu hoạch ban thưởng th&igrave; c&agrave;ng nhiều.<br />\r\n<br />\r\nKh&ocirc;ng gian trữ vật l&agrave; c&aacute;c người chơi ti&ecirc;u chuẩn thấp nhất một c&aacute;i d&ugrave;ng để chứa đựng vật phẩm tư nh&acirc;n dị độ kh&ocirc;ng gian.<br />\r\n<br />\r\nCấp một kh&ocirc;ng gian trữ vật quy c&aacute;ch nhỏ b&eacute;, chỉ c&oacute; một m&eacute;t khối dung t&iacute;ch, tại chủng tộc đẳng cấp đạt tới nhất định gi&aacute; trị sau player liền c&oacute; thể lựa chọn d&ugrave;ng Lanck đến thăng cấp kh&ocirc;ng gian trữ vật lấy thu hoạch được c&agrave;ng lớn dung t&iacute;ch.<br />\r\n<br />\r\n&quot;Chỉ tiếc ta hiện tại đ&atilde; kh&ocirc;ng phải l&agrave; player, muốn thăng cấp kh&ocirc;ng gian trữ vật sợ rằng kh&ocirc;ng đơn giản như thế. .. Bất qu&aacute;, c&oacute; t&oacute;m lại so kh&ocirc;ng c&oacute; tốt.&quot; C&oacute; t&ugrave;y th&acirc;n mang theo vật phẩm năng lực, Triệu C&agrave;n Vũ cũng nhiều kh&ocirc;ng &iacute;t l&agrave;m việc tiện lợi.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ rất mau đem suy nghĩ từ kh&ocirc;ng gian trữ vật b&ecirc;n tr&ecirc;n chuyển dời đến ch&iacute;nh sự b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nBiết được S&agrave;i Lang nh&acirc;n tin tức hắn ngồi tại chỗ, so như nhai s&aacute;p nến giống như nhai nuốt lấy trong miệng đồ ăn.<br />\r\n<br />\r\nVận kh&iacute; của m&igrave;nh tựa hồ đang sau khi xuy&ecirc;n việt lấy được cải thiện cực lớn.<br />\r\n<br />\r\nMột ngủ gật đ&atilde; c&oacute; người đưa gối đầu.<br />\r\n<br />\r\nXem ra ph&aacute; huỷ S&agrave;i Lang nh&acirc;n s&agrave;o huyệt chuyện n&agrave;y c&oacute; thể nhanh ch&oacute;ng n&acirc;ng l&ecirc;n thường ng&agrave;y.<br />\r\n<br />\r\nHiện tại chỉ c&oacute; thể đơn giản kế hoạch một lần, d&ugrave; sao hắn vẫn chỉ l&agrave; c&aacute;i LV. 4 yếu g&agrave;.<br />\r\n<br />\r\n&quot;Nhất định phải tranh thủ thời gian mạnh l&ecirc;n a. . .&quot; Triệu C&agrave;n Vũ cảm gi&aacute;c s&acirc;u sắc đau đầu, mặc kệ c&oacute; kế hoạch g&igrave;, đều cần thực lực ủng hộ.<br />\r\n<br />\r\nNghĩ lại, hắn lại kh&ocirc;ng nhịn được v&igrave; một chuyện kh&aacute;c bắt đầu ph&aacute;t sầu.<br />\r\n<br />\r\nChuyện n&agrave;y, cũng ch&iacute;nh l&agrave; hắn lựa chọn nh&agrave; n&agrave;y lữ qu&aacute;n ở lại nguy&ecirc;n nh&acirc;n căn bản.<br />\r\n<br />\r\nHắn c&acirc;̀n gặp m&ocirc;̣t người.<br />\r\n<br />\r\nC&aacute;i n&agrave;y người tại Triệu C&agrave;n Vũ vẫn l&agrave; player th&igrave; liền tiếp x&uacute;c qua, cũng từ hắn tr&ecirc;n th&acirc;n lấy được kh&ocirc;ng nhỏ trợ lực. Chỉ l&agrave; l&uacute;c n&agrave;y thời gian đ&atilde; quay lại đến mở server trước một năm, lại kh&ocirc;ng luận người kia phải chăng đ&atilde; tại c&aacute;i n&agrave;y, coi như ở nơi n&agrave;y, chỉ sợ cũng liền tiếp x&uacute;c hắn đều th&agrave;nh kh&oacute; khăn.<br />\r\n<br />\r\nNgười hắn muốn t&igrave;m, ch&iacute;nh l&agrave; ở kiếp trước trợ hắn nhậm chức thợ r&egrave;n vị kia nhỏ ẩn ẩn tại thế r&egrave;n đ&uacute;c t&ocirc;ng sư.<br />\r\n<br />\r\nC&ugrave;ng chiến đấu nghề nghiệp kh&aacute;c biệt, player nhiều nhất c&oacute; thể đồng thời nhậm chức hai c&aacute;i sinh hoạt nghề nghiệp.<br />\r\n<br />\r\nSinh hoạt nghề nghiệp, cũng l&agrave; tr&ograve; chơi thể nghiệm cần thiết một ho&agrave;n, d&ugrave; sao &laquo; Thần Vực &raquo; l&agrave; độ m&ocirc; phỏng thực tế cực cao tr&ograve; chơi, đ&aacute;m NPC kh&ocirc;ng c&oacute; khả năng l&agrave;m tr&aacute;i lẽ thường cung cấp li&ecirc;n tục kh&ocirc;ng ngừng đạo cụ c&ugrave;ng trang bị.<br />\r\n<br />\r\nPlayer trong chợ nhu cầu độ c&ugrave;ng lượng cung ứng ở giữa sai gi&aacute; trị, liền cần dựa v&agrave;o c&aacute;c người chơi bản th&acirc;n bổ t&uacute;c.<br />\r\n<br />\r\nỞ kiếp trước l&agrave;m phổ th&ocirc;ng player, Triệu C&agrave;n Vũ kh&ocirc;ng c&oacute; qu&aacute; mức ch&uacute; trọng chiến đấu nghề nghiệp tăng l&ecirc;n, c&aacute;i n&agrave;y dẫn đến cho đến đ&oacute;ng Server thực lực của hắn tại một đ&aacute;m player b&ecirc;n trong xem như bình thường kh&ocirc;ng c&oacute; g&igrave; lạ.<br />\r\n<br />\r\nNhưng l&agrave; tại sinh hoạt nghề nghiệp trong lĩnh vực, Triệu C&agrave;n Vũ c&oacute; thể n&oacute;i b&ecirc;n tr&ecirc;n l&agrave; c&oacute; ch&uacute;t danh tiếng.<br />\r\n<br />\r\nDanh kh&iacute; nơi ph&aacute;t ra đ&uacute;ng l&agrave; hắn cao đến Truyền Kỳ cấp thợ r&egrave;n đẳng cấp.<br />\r\n<br />\r\nTrong tr&ograve; chơi sinh hoạt nghề nghiệp đẳng cấp từ thấp đến cao tổng cộng chia l&agrave;m học đồ, thực tập, sư, đại sư, t&ocirc;ng sư, sử thi, truyền kỳ, B&aacute;n Thần, thần chín c&aacute;i đẳng cấp.<br />\r\n<br />\r\nCần biết, sinh hoạt nghề nghiệp tăng l&ecirc;n muốn so chiến đấu nghề nghiệp tăng l&ecirc;n kh&oacute; khăn rất nhiều. Ở kiếp trước &laquo; Thần Vực &raquo; b&ecirc;n trong , li&ecirc;n đới Triệu C&agrave;n Vũ, thợ r&egrave;n một đạo b&ecirc;n tr&ecirc;n vượt qua Truyền Kỳ cấp bậc player tổng cộng kh&ocirc;ng cao hơn hai tay số lượng.<br />\r\n<br />\r\nMột c&aacute;i cao đẳng cấp thợ r&egrave;n chế tạo trang bị, tại player quần thể b&ecirc;n trong thậm ch&iacute; c&oacute; thể b&aacute;n ra hơn 10 triệu gi&aacute; cao, dạng n&agrave;y giả lập gi&aacute; trị, thậm ch&iacute; c&oacute; thể ở Triệu C&agrave;n Vũ sinh hoạt th&agrave;nh phố khu trung t&acirc;m mua lấy một bộ nh&agrave; vệ sinh, ở trong đ&oacute; h&agrave;m kim lượng c&oacute; thể nghĩ.<br />\r\n<br />\r\nCũng đang bởi v&igrave; đ&acirc;y, lấy truyền kỳ thợ r&egrave;n th&acirc;n phận kiếm được tiền, Triệu C&agrave;n Vũ chất lượng sinh hoạt rất kh&ocirc;ng tệ.<br />\r\n<br />\r\nTất cả mọi người biết r&otilde; truyền kỳ thợ r&egrave;n danh kh&iacute;, lại chưa c&oacute; người biết Triệu C&agrave;n Vũ thợ r&egrave;n nghề nghiệp truyền thừa l&agrave; ở nơi n&agrave;o thu hoạch đến, c&oacute; thể để hắn xu&ocirc;i gi&oacute; xu&ocirc;i nước đạt tới kia kinh người cao độ.<br />\r\n<br />\r\nChuyện n&agrave;y c&ograve;n phải từ tr&ograve; chơi bắt đầu Triệu C&agrave;n Vũ nhận được một c&aacute;i chạy v&ograve;ng nhiệm vụ n&oacute;i l&ecirc;n.<br />\r\n<br />\r\nKh&ocirc;ng tr&ugrave;ng hợp kh&ocirc;ng th&agrave;nh s&aacute;ch, c&aacute;i kia chạy v&ograve;ng nhiệm vụ c&oacute; một v&ograve;ng d&iacute;nh đến trấn Sikh c&aacute;i n&agrave;y thị trấn nhỏ nơi bi&ecirc;n giới.<br />\r\n<br />\r\nCh&iacute;nh l&agrave; ở đ&acirc;y, Triệu C&agrave;n Vũ tại dưới cơ duy&ecirc;n xảo hợp bền chắc một vị sắp chết r&egrave;n đ&uacute;c t&ocirc;ng sư. Tại chung sống một tuần l&acirc;u, Triệu C&agrave;n Vũ bầu bạn l&atilde;o nh&acirc;n đi qua sau c&ugrave;ng thời gian về sau, l&uacute;c n&agrave;y mới thuận thế thừa kế r&egrave;n đ&uacute;c t&ocirc;ng sư y b&aacute;t.<br />\r\n<br />\r\nTinh tường biết r&otilde; l&atilde;o nh&acirc;n qu&aacute; khứ trải qua Triệu C&agrave;n Vũ l&uacute;c n&agrave;y mới quyết định tại Thomas l&atilde;o cha kh&aacute;ch sạn ở lại.<br />\r\n<br />\r\nMột l&agrave; v&igrave; thu hoạch được thợ r&egrave;n nghề nghiệp truyền thừa, hai l&agrave; hi vọng c&oacute; thể sớm cải biến vị n&agrave;y bảy ng&agrave;y chi sư kết cục bi thảm.<br />\r\n<br />\r\nNhưng b&acirc;y giờ ngồi ở trong đ&aacute;m người Triệu C&agrave;n Vũ cũng kh&ocirc;ng khỏi được l&agrave;m cho n&agrave;y sự kiện phạm v&agrave;o kh&oacute;.</p>', '2021-11-28 13:49:54', 16, 1),
(224, 59, 'Khách không mời mà đến', '6', '<p>Triệu C&agrave;n Vũ kh&oacute; khăn nguy&ecirc;n nh&acirc;n rất đơn giản, hắn chuẩn bị tiếp x&uacute;c vị n&agrave;y r&egrave;n đ&uacute;c t&ocirc;ng sư t&iacute;nh t&igrave;nh cũng kh&ocirc;ng l&agrave; b&igrave;nh thường qu&aacute;i.<br />\r\n<br />\r\nNếu như kh&ocirc;ng phải t&iacute;nh t&igrave;nh cổ qu&aacute;i chỉ sợ cũng sẽ kh&ocirc;ng ở tại nơi n&agrave;y dạng vắng vẻ chi địa.<br />\r\n<br />\r\nTrừ những c&aacute;i kia ẩn cư sơn l&acirc;m hoặc l&agrave; được cung phụng tại đại gia tộc b&ecirc;n trong phụng l&agrave;m thượng kh&aacute;ch đỉnh cấp thợ r&egrave;n b&ecirc;n ngo&agrave;i, một c&aacute;i t&ocirc;ng sư cấp thợ r&egrave;n tại to&agrave;n bộ đại lục Metz b&ecirc;n tr&ecirc;n cũng coi l&agrave; số một.<br />\r\n<br />\r\nNh&acirc;n vật như vậy sở dĩ chọn đại ẩn ẩn tại th&agrave;nh thị, ho&agrave;n to&agrave;n l&agrave; bị &eacute;p cử chỉ.<br />\r\n<br />\r\nL&uacute;c đ&oacute; tuổi trẻ r&egrave;n đ&uacute;c t&ocirc;ng sư, bởi v&igrave; tuổi c&ograve;n trẻ liền tại r&egrave;n đ&uacute;c một đường b&ecirc;n tr&ecirc;n m&agrave; rất c&oacute; th&agrave;nh t&iacute;ch m&agrave; kh&iacute; thịnh nhất thời tự cho m&igrave;nh thanh cao, kh&ocirc;ng ngờ hắn ngạo kh&iacute; chọc giận tới địa phương quyền qu&yacute; m&agrave; bị khắp nơi ch&egrave;n &eacute;p, r&ograve;ng r&atilde; ba mươi năm tại r&egrave;n đ&uacute;c thuật b&ecirc;n tr&ecirc;n kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o tiến triển.<br />\r\n<br />\r\nNản l&ograve;ng tho&aacute;i ch&iacute; r&egrave;n đ&uacute;c t&ocirc;ng sư tại trong l&uacute;c n&agrave;y muốn đi địa phương kh&aacute;c thay đường ra, nhưng l&agrave; hắn đắc tội qu&yacute; tộc thế lực cực lớn, kh&ocirc;ng người n&agrave;o d&aacute;m đắc tội cừu gia tiếp đ&atilde;i hắn, c&aacute;i n&agrave;y khiến hắn cảm nhận được khắp nơi vấp phải trắc trở tư vị.<br />\r\n<br />\r\n&Acirc;u sầu thất bại hắn chỉ c&oacute; thể t&igrave;m c&aacute;i cừu gia thế lực ph&oacute;ng xạ kh&ocirc;ng tới địa phương ẩn cư lại, c&ocirc; độc sống qu&atilde;ng đời c&ograve;n lại.<br />\r\n<br />\r\nNhiều thăng trầm mệnh đồ để tuổi gi&agrave; r&egrave;n đ&uacute;c t&ocirc;ng sư t&iacute;nh c&aacute;ch cổ qu&aacute;i, thay đổi thất thường lại t&iacute;nh t&igrave;nh n&oacute;ng nảy, kh&ocirc;ng th&iacute;ch c&ugrave;ng người giao tiếp.<br />\r\n<br />\r\nỞ kiếp trước nếu kh&ocirc;ng phải duy&ecirc;n phận tế hội, Triệu C&agrave;n Vũ cũng rất kh&oacute; c&ugrave;ng dạng n&agrave;y người đ&aacute;p l&ecirc;n quan hệ cũng từ chỗ của hắn đạt được thợ r&egrave;n c&aacute;i n&agrave;y sinh hoạt nghề nghiệp.<br />\r\n<br />\r\nB&acirc;y giờ l&agrave; mở server trước một năm , dựa theo d&ograve;ng thời gian đến phỏng đo&aacute;n, l&uacute;c n&agrave;y vị n&agrave;y r&egrave;n đ&uacute;c t&ocirc;ng sư liền ở nhờ tại Thomas l&atilde;o cha trong lữ điếm.<br />\r\n<br />\r\nChỉ l&agrave;, muốn thế n&agrave;o mới có th&ecirc;̉ tiếp x&uacute;c đến hắn lại kh&ocirc;ng l&agrave;m cho phản cảm đ&acirc;u. . .<br />\r\n<br />\r\nTriệu C&agrave;n Vũ khớp xương r&otilde; r&agrave;ng ng&oacute;n tay ở tr&ecirc;n b&agrave;n c&oacute; tiết tấu đập, kh&ocirc;ng nhịn được lộ ra vẻ suy tư.<br />\r\n<br />\r\nỞ nơi n&agrave;y thời gian tiết điểm muốn b&aacute;i tại vị n&agrave;y r&egrave;n đ&uacute;c t&ocirc;ng sư m&ocirc;n hạ, độ kh&oacute; c&oacute; thể n&oacute;i l&agrave; cao v&ocirc; c&ugrave;ng. Nhưng l&agrave; Triệu C&agrave;n Vũ quyết t&acirc;m muốn l&agrave;m như thế, một l&agrave; v&igrave; thu hoạch sinh hoạt nghề nghiệp, hai l&agrave; hắn hi vọng c&oacute; thể mượn xuy&ecirc;n qua ti&ecirc;n cơ cải biến vị l&atilde;o nh&acirc;n n&agrave;y vận mệnh.<br />\r\n<br />\r\nỞ kiếp trước bảy ng&agrave;y chi sư &acirc;n t&igrave;nh tại l&atilde;o nh&acirc;n sau khi chết Triệu C&agrave;n Vũ liền kh&ocirc;ng c&oacute; b&aacute;o đ&aacute;p cơ hội, hiện tại xuy&ecirc;n qua đến đại lục Metz, hắn quyết t&acirc;m phải v&igrave; l&atilde;o sư l&agrave;m ch&uacute;t g&igrave;.<br />\r\n<br />\r\nL&acirc;m v&agrave;o trầm tư Triệu C&agrave;n Vũ đột nhi&ecirc;n bị một trận k&ecirc;u la &acirc;m thanh cắt đứt suy nghĩ.<br />\r\n<br />\r\n&quot;Huyết Đảng l&agrave;m việc! Nh&acirc;n vi&ecirc;n kh&ocirc;ng quan hệ lập tức rời s&acirc;n, miễn cho bị li&ecirc;n lụy!&quot; Ngo&agrave;i cửa tiệm xa xa truyền đến một tiếng ngang ngược tiếng h&ocirc; ho&aacute;n.<br />\r\n<br />\r\nNghe thế dạng gọi, trong đại sảnh l&uacute;c đầu th&acirc;n thiện bầu kh&ocirc;ng kh&iacute; tr&igrave; trệ.<br />\r\n<br />\r\n&quot;M&aacute;u. . . Huyết Đảng? Đ&aacute;m kia t&ecirc;n đi&ecirc;n? Thật x&uacute;i quẩy, đi nhanh l&ecirc;n đi!&quot;<br />\r\n<br />\r\n&quot;Đ&aacute;ng chết! H&ocirc;m nay l&agrave;m sao xui xẻo như vậy, ban đ&ecirc;m ra tới thư gi&atilde;n một t&iacute; cũng c&oacute; thể gặp gỡ bọn n&agrave;y cẩu &middot; mẹ dưỡng.&quot;<br />\r\n<br />\r\n&quot;Xuỵt, nhỏ giọng một ch&uacute;t, ngươi kh&ocirc;ng muốn sống nữa! Đi mau đi mau!&quot;<br />\r\n<br />\r\n&quot;. . .&quot;<br />\r\n<br />\r\nKh&aacute;ch uống rượu nh&oacute;m bởi v&igrave; &#39;Huyết Đảng &#39; danh hiệu tao động, rất nhiều người thậm ch&iacute; ngay cả rượu trong ly đều kh&ocirc;ng uống xong, giống như l&agrave; bị đạp c&aacute;i đu&ocirc;i con m&egrave;o một dạng từ tr&ecirc;n chỗ ngồi nhảy dựng l&ecirc;n, thần th&aacute;i vội v&atilde; rời đi.<br />\r\n<br />\r\nMặc d&ugrave; đại đa số người như l&agrave; tr&aacute;nh n&eacute; &ocirc;n thần b&igrave;nh thường cực nhanh chuồn mất, nhưng l&agrave; kh&ocirc;ng thiếu như cũ trấn định ngồi tại tại chỗ b&ecirc;n tr&ecirc;n tự r&oacute;t tự uống kh&aacute;ch uống rượu.<br />\r\n<br />\r\n&quot;Barry, ch&uacute;ng ta chẳng lẽ kh&ocirc;ng đi sao? Huyết Đảng đến rồi?&quot; Lực lượng chưa đủ thanh &acirc;m yếu ớt m&agrave; hỏi thăm.<br />\r\n<br />\r\n&quot;X&ugrave;y, uổng cho ngươi c&ograve;n tự xưng trấn Sikh b&aacute;ch sự th&ocirc;ng, thế m&agrave; l&aacute; gan nhỏ như vậy? Y&ecirc;n t&acirc;m, mặc d&ugrave; Huyết Đảng tại trong trấn thế lực rất lớn, nhưng ch&uacute;ng ta &#39;H&ugrave;ng Sư dong binh đo&agrave;n&#39; cũng kh&ocirc;ng phải ăn chay, liền xem như l&atilde;o đại bọn họ đến rồi cũng kh&ocirc;ng d&aacute;m tuỳ tiện đụng đến ta!&quot; Nam nh&acirc;n thanh &acirc;m r&otilde; r&agrave;ng để lộ ra hắn uống say sự thật.<br />\r\n<br />\r\n&quot;Ngươi đi&ecirc;n ư! Ta xem ngươi ch&iacute;nh l&agrave; uống nhiều rồi, lại d&aacute;m n&oacute;i lời như vậy, đầu c&oacute; phải l&agrave; kh&ocirc;ng muốn? C&aacute;i trước d&aacute;m n&oacute;i như vậy n&oacute;i người thi thể đều bị đ&uacute;t cho ch&oacute; hoang, đi, đi nhanh l&ecirc;n!&quot;<br />\r\n<br />\r\nTriệu C&agrave;n Vũ nghi&ecirc;ng nghi&ecirc;ng đầu, &aacute;nh mắt na di.<br />\r\n<br />\r\nHạ giọng tr&ograve; chuyện với nhau ch&iacute;nh l&agrave; vừa rồi đ&agrave;m luận S&agrave;i Lang Nh&acirc;n chi vương hai người.<br />\r\n<br />\r\nL&uacute;c n&agrave;y c&aacute;i t&ecirc;n mập mạp kia hống li&ecirc;n tục t&uacute;m lưng quần đem sắc mặt hơi say rượu kh&ocirc;i ng&ocirc; h&aacute;n tử k&eacute;o đi rồi, xem ra &#39;Huyết Đảng &#39; ở đ&acirc;y t&iacute;ch uy đ&atilde; l&acirc;u.<br />\r\n<br />\r\nC&oacute; thể săn giết S&agrave;i Lang nh&acirc;n dong binh đo&agrave;n b&ecirc;n trong một vị th&agrave;nh vi&ecirc;n, tự nhi&ecirc;n kh&ocirc;ng c&oacute; khả năng thấp hơn LV. 10, nhưng cũng muốn bởi v&igrave; &#39;Huyết Đảng &#39; đến m&agrave; đường v&ograve;ng.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y &#39;Huyết Đảng&#39; thể diện thật lớn. . .&quot; Triệu C&agrave;n Vũ nh&igrave;n thấy n&agrave;y tấm cảnh tượng cũng kh&ocirc;ng khỏi tự lẩm bẩm.<br />\r\n<br />\r\nNgắn ngủi một ph&uacute;t b&ecirc;n trong, bởi v&igrave; kh&aacute;ch uống rượu nh&oacute;m cuống qu&iacute;t tho&aacute;t đi, trước kia n&aacute;o nhiệt đại sảnh liền trở n&ecirc;n lạnh nhạt.<br />\r\n<br />\r\nNhưng m&agrave;, Triệu C&agrave;n Vũ ngồi ở chỗ cũ kh&ocirc;ng hề động.<br />\r\n<br />\r\nChỉ v&igrave; trong b&agrave;n ăn c&ograve;n c&oacute; một khối b&aacute;nh m&igrave; kh&ocirc;ng ăn xong.<br />\r\n<br />\r\nTiết kiệm lương thực l&agrave; thời đại mới năm tốt thanh ni&ecirc;n thiết yếu tố dưỡng, huống chi tại dạng n&agrave;y sơn d&atilde; trong tiểu trấn, dựa v&agrave;o thi thuật giả găng tay h&ograve;a &acirc;m v&aacute;ch tường đủ để tự vệ.<br />\r\n<br />\r\nĐương nhi&ecirc;n, để cho an to&agrave;n, hắn &acirc;m thầm đem thăng cấp lấy được hai điểm tự do đi&ecirc;̉m thu&ocirc;̣c tính th&ecirc;m ở tr&iacute; lực b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nMặc d&ugrave; giai đoạn trước th&ecirc;m điểm đối lại sau ph&aacute;t triển con đường c&oacute; ảnh hưởng kh&ocirc;ng nhỏ, nhưng l&agrave; theo Triệu C&agrave;n Vũ biết Ch&uacute; Ấn sư l&agrave; khuynh hướng ma ph&aacute;p hệ m&agrave; kh&ocirc;ng phải hệ vật l&yacute; nghề nghiệp, nhiều một ch&uacute;t ch&uacute;t tr&iacute; lực t&oacute;m lại kh&ocirc;ng c&oacute; sai.<br />\r\n<br />\r\nHuống chi trước mắt hắn có th&ecirc;̉ b&agrave;y ra tr&ecirc;n mặt b&agrave;n thủ đoạn đều cần ma lực l&agrave;m ch&egrave;o chống.<br />\r\n<br />\r\nY&ecirc;n lặng tra x&eacute;t liếc mắt thuộc t&iacute;nh bảng, Triệu C&agrave;n Vũ lực lượng c&agrave;ng tăng l&ecirc;n:<br />\r\n<br />\r\n&quot;T&iacute;nh danh: Triệu C&agrave;n Vũ<br />\r\n<br />\r\nXưng h&agrave;o: Kh&ocirc;ng<br />\r\n<br />\r\nChủng tộc: Nh&acirc;n tộc<br />\r\n<br />\r\nChủng tộc đẳng cấp: 4(300 ∕ 800)<br />\r\n<br />\r\nChiến đấu nghề nghiệp: Kh&ocirc;ng (0 ∕ 0)<br />\r\n<br />\r\nSinh hoạt nghề nghiệp: Kh&ocirc;ng (0 ∕ 0)<br />\r\n<br />\r\nThuộc t&iacute;nh: Lực lượng 5, tốc độ 3, nhanh nhẹn 4, sức chịu đựng 3, tr&iacute; lực 7, may mắn 2(có th&ecirc;̉ ph&acirc;n phối tự do đi&ecirc;̉m thu&ocirc;̣c tính 1)<br />\r\n<br />\r\nMa lực: 70 ∕ 70<br />\r\n<br />\r\nKỹ năng: [ &acirc;m b&iacute;ch ] (một cấp): Ti&ecirc;u hao ma lực tại mục ti&ecirc;u vị tr&iacute; chế tạo ra một tầng s&oacute;ng &acirc;m cấu tr&uacute;c b&igrave;nh chướng, b&igrave;nh chướng c&oacute; kh&ocirc;ng tầm thường lực ph&ograve;ng ngự lại đối c&oacute; thực thể vật phẩm c&oacute; trở ngại ngại t&aacute;c dụng, thời gian cooldown kh&ocirc;ng, mỗi gi&acirc;y ti&ecirc;u hao hai điểm ma lực.<br />\r\n<br />\r\nTrang bị: Thi thuật giả găng tay<br />\r\n<br />\r\nDanh vọng: Kh&ocirc;ng<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\n&quot;<br />\r\n<br />\r\nVẻn vẹn xuy&ecirc;n qua đến thế giới n&agrave;y kh&ocirc;ng đến một ng&agrave;y liền đ&atilde; tập được hai c&aacute;i nhất cấp ma ph&aacute;p, c&aacute;i tốc độ n&agrave;y đặt ở tr&ograve; chơi mở server th&igrave; thuộc về nhất lưu.<br />\r\n<br />\r\nĐ&acirc;y hết thảy tiến triển, đều muốn quy c&ocirc;ng cho th&agrave;nh tựu hệ thống.<br />\r\n<br />\r\nTriệu C&agrave;n Vũ chậm r&atilde;i ăn b&aacute;nh m&igrave;, y&ecirc;n lặng nh&igrave;n xem t&igrave;nh thế ph&aacute;t triển.<br />\r\n<br />\r\nBởi v&igrave; những kh&aacute;ch nh&acirc;n rời đi m&agrave; vắng vẻ bầu kh&ocirc;ng kh&iacute; rất nhanh bị đ&aacute;nh ph&aacute;.<br />\r\n<br />\r\nChu&ocirc;ng cửa rung động, một h&agrave;ng bảy t&aacute;m người kh&iacute; thế h&ugrave;ng hổ hung thần &aacute;c s&aacute;t x&ocirc;ng v&agrave;o.<br />\r\n<br />\r\nCầm đầu l&agrave; một thon gầy thấp b&eacute; mặc &aacute;o kho&aacute;c m&agrave;u đen, nghi&ecirc;m t&uacute;c thận trọng nam nh&acirc;n, hẹp d&agrave;i con mắt giống như l&agrave; một con ẩn giấu ở trong b&oacute;ng tối thừa cơ h&agrave;nh động độc x&agrave;.<br />\r\n<br />\r\nM&agrave; thấp b&eacute; nam nh&acirc;n sau lưng th&igrave; l&agrave; thuần một sắc mặc &aacute;o đu&ocirc;i ngắn, trần trụi tr&aacute;ng kiện t&ugrave; kiện c&aacute;nh tay kh&ocirc;i ng&ocirc; đại h&aacute;n, hung thần &aacute;c s&aacute;t khu&ocirc;n mặt sợ rằng có th&ecirc;̉ dừng tiểu nhi kh&oacute;c đ&ecirc;m.<br />\r\n<br />\r\nNhững người n&agrave;y c&oacute; một c&aacute;i cộng đồng đặc điểm, mỗi người m&aacute; tr&aacute;i b&ecirc;n tr&ecirc;n đều c&oacute; một c&aacute;i huyết sắc xi&ecirc;n, tựa hồ l&agrave; c&aacute;i g&igrave; thống nhất k&yacute; hiệu.<br />\r\n<br />\r\n&quot;Đ&acirc;y ch&iacute;nh l&agrave; &#39;Huyết Đảng&#39; a. . .&quot; Triệu C&agrave;n Vũ vuốt cằm, dạng n&agrave;y ho&aacute; trang liếc mắt nh&igrave;n qua quả thật c&oacute; ch&uacute;t dọa người.<br />\r\n<br />\r\nĐứng tại quầy bar sau Thomas l&atilde;o cha sắc mặt c&oacute; ch&uacute;t &acirc;m trầm.<br />\r\n<br />\r\nHắn biết r&otilde;, n&ecirc;n tới t&oacute;m lại vẫn phải tới.<br />\r\n<br />\r\nThấp b&eacute; nam nh&acirc;n dẫn đ&aacute;m tay ch&acirc;n thẳng tắp đi về ph&iacute;a Thomas, v&agrave;i đ&ocirc;i ủng da tr&ecirc;n s&agrave;n nh&agrave; đập được lạch cạch rung động.<br />\r\n<br />\r\nPh&ograve;ng lớn như thế b&ecirc;n trong, chỉ c&oacute; c&aacute;i n&agrave;y v&agrave;i đ&ocirc;i ủng da đạp ở tr&ecirc;n đất tiếng vang.<br />\r\n<br />\r\nSố lượng kh&ocirc;ng nhiều mặc nhi&ecirc;n ngồi tại chỗ những kh&aacute;ch nh&acirc;n bởi v&igrave; n&agrave;y bầu kh&ocirc;ng kh&iacute; ngột ngạt m&agrave; ngừng thở.<br />\r\n<br />\r\n&#39;Huyết Đảng &#39; danh hiệu tại trấn Sikh t&iacute;ch uy đ&atilde; l&acirc;u, bọn hắn đ&atilde; từng l&agrave;m ra cử động đi&ecirc;n cuồng cho d&ugrave; ai nghe xong cũng muốn theo t&acirc;m ngọn nguồn cảm thấy sợ h&atilde;i.<br />\r\n<br />\r\nTất cả mọi người từ bọn họ trong trầm mặc cảm nhận được một cỗ gi&oacute; thổi b&aacute;o gi&ocirc;ng b&atilde;o sắp đến cảm gi&aacute;c.<br />\r\n<br />\r\nĐ&aacute;m người n&agrave;y tại sao lại muốn tới Thomas l&atilde;o cha lữ qu&aacute;n?<br />\r\n<br />\r\nDạng n&agrave;y nghi hoặc xoay quanh tại chỗ c&oacute; người trong đầu.</p>', '2021-11-28 13:50:10', 21, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(225, 60, 'Hôm nay ta ấn đường biến đen, là một phổ cập khoa học linh dị ngày tốt lành!', '1', '<p>&quot;@ L&acirc;m Mục C&aacute;p, ra một kỳ đi th&agrave;nh Nam vứt bỏ bệnh viện t&acirc;m thần video ta trực tiếp đem ngươi trước kia sở hữu video đều một kiện tam li&ecirc;n!&quot;<br />\r\n<br />\r\n&quot;Đừng để up đi đưa, b&ecirc;n trong thật c&oacute; linh dị.&quot;<br />\r\n<br />\r\n&quot;Sở dĩ th&agrave;nh Nam linh dị c&aacute;i kia truyền thuyết l&agrave; thật giả?&quot;<br />\r\n<br />\r\n&quot;Thật sự, bằng kh&ocirc;ng c&aacute;i video n&agrave;y giải th&iacute;ch thế n&agrave;o?&quot;<br />\r\n<br />\r\n&quot;@ khủng bố đại sư quay xong c&aacute;i video n&agrave;y về sau bị dọa đến b&acirc;y giờ c&ograve;n tại nằm bệnh viện đ&acirc;u, mấy ng&agrave;y nay mới miễn cưỡng c&oacute; thể n&oacute;i chuyện.&quot;<br />\r\n<br />\r\n&quot;Cũng l&agrave; bởi v&igrave; linh dị sở dĩ bệnh viện t&acirc;m thần mới bị bỏ hoang.&quot;<br />\r\n<br />\r\n&quot;Ta khi c&ograve;n b&eacute; bởi v&igrave; th&agrave;nh Nam linh dị cố sự ban đ&ecirc;m căn bản kh&ocirc;ng d&aacute;m tắt đ&egrave;n đi ngủ.&quot;<br />\r\n<br />\r\nTh&agrave;nh Nam, hơn t&aacute;m giờ tối.<br />\r\n<br />\r\nNh&igrave;n xem đại gia ở một c&aacute;i video b&igrave;nh luận khu @ bản th&acirc;n đầu kia b&igrave;nh luận ph&iacute;a dưới thảo luận đến mức dị thường kịch liệt.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p c&oacute; ch&uacute;t nhếch l&ecirc;n miệng.<br />\r\n<br />\r\nHắn hơn hai mươi năm trước từ một c&aacute;i linh dị hệ thống phi thường ho&agrave;n thiện thế giới xuy&ecirc;n qua đến c&aacute;i n&agrave;y linh dị vừa mới kh&ocirc;i phục thế giới.<br />\r\n<br />\r\nXuy&ecirc;n qua trước đ&oacute;, hắn tốt nghiệp ở linh dị t&agrave;i nguy&ecirc;n quản l&yacute; chuy&ecirc;n nghiệp.<br />\r\n<br />\r\nChuyện ma quỷ cấp s&aacute;u, xử l&iacute; linh dị nh&acirc;n t&agrave;i th&ocirc;ng b&aacute;o tuyển dụng c&ugrave;ng tuyển chọn c&ugrave;ng huấn luyện c&ugrave;ng khai ph&aacute;t, một đầu &oacute;c li&ecirc;n quan tới c&aacute;c loại linh dị c&aacute;c loại tri thức.<br />\r\n<br />\r\nNhưng thế giới n&agrave;y linh dị mới vừa vặn kh&ocirc;i phục mấy năm, cơ hồ tất cả mọi người kh&ocirc;ng tin linh dị tồn tại.<br />\r\n<br />\r\nĐối với một ch&uacute;t video c&ugrave;ng h&igrave;nh ảnh tư liệu, tuyệt đại đa số người cũng đều xem như thần b&iacute; đ&ocirc; thị chuyện lạ.<br />\r\n<br />\r\nC&aacute;i n&agrave;y đưa đến đại đa số người l&acirc;m v&agrave;o đối linh dị lại sợ lại kh&ocirc;ng qu&aacute; tin m&acirc;u thuẫn t&acirc;m l&yacute;.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p vậy &yacute; thức được phổ cập khoa học quỷ dị sinh vật c&ocirc;ng t&aacute;c g&aacute;nh nặng đường xa.<br />\r\n<br />\r\nSở dĩ hắn lựa chọn trở th&agrave;nh một t&ecirc;n tiểu ph&aacute; trạm (bilibili) uploader, nỗ lực hơn nửa năm đ&atilde; c&oacute; mấy vạn fan h&acirc;m mộ.<br />\r\n<br />\r\nĐ&aacute;ng tiếc l&agrave; c&aacute;i n&agrave;y chỉ c&oacute; mấy vạn fan h&acirc;m mộ nh&igrave;n hắn video cũng chỉ l&agrave; xem như đặc hiệu c&ugrave;ng kh&ocirc;i h&agrave;i giải tr&iacute;.<br />\r\n<br />\r\nB&igrave;nh thường tại đứng l&ecirc;n nh&igrave;n thấy một ch&uacute;t trong video xuất hiện linh dị, tất cả mọi người sẽ @ hắn coi như tr&ecirc;u chọc.<br />\r\n<br />\r\nTỉ như hiện tại c&aacute;i n&agrave;y trực tiếp ghi ch&eacute;p b&igrave;nh phong video.<br />\r\n<br />\r\n&quot;Th&agrave;nh Nam vứt bỏ bệnh viện t&acirc;m thần. . .&quot;<br />\r\n<br />\r\nXem hết b&igrave;nh luận sau.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p mới y&ecirc;n lặng vạch đến ph&iacute;a tr&ecirc;n ấn mở c&aacute;i video n&agrave;y.<br />\r\n<br />\r\nC&aacute;i video n&agrave;y quay chụp người l&agrave; @ khủng bố đại sư, một c&aacute;i hơn 50 vạn fan h&acirc;m mộ uploader.<br />\r\n<br />\r\nNgắn ngủi hai ph&uacute;t video đ&atilde; ph&aacute; 6 triệu ph&aacute;t ra, tại tiểu ph&aacute; trạm nhiệt bảng b&ecirc;n tr&ecirc;n treo ba ng&agrave;y.<br />\r\n<br />\r\nLi&ecirc;n quan tới video c&oacute; phải l&agrave; đặc hiệu, th&agrave;nh Nam linh dị truyền thuyết l&agrave; thật l&agrave; giả thảo luận tại c&aacute;c đại b&igrave;nh đ&agrave;i b&ecirc;n tr&ecirc;n cũng kh&ocirc;ng ngừng l&ecirc;n men.<br />\r\n<br />\r\n&quot;! Ph&iacute;a trước cao năng ph&iacute;a trước cao năng ph&iacute;a trước cao năng!&quot;<br />\r\n<br />\r\n&quot;! Nh&aacute;t gan cẩn thận khi đi v&agrave;o nh&aacute;t gan cẩn thận khi đi v&agrave;o nh&aacute;t gan cẩn thận khi đi v&agrave;o!&quot;<br />\r\n<br />\r\nVừa mới ấn mở video.<br />\r\n<br />\r\nL&iacute;t nha l&iacute;t nh&iacute;t đủ mọi m&agrave;u sắc ph&iacute;a trước cao năng nh&aacute;y mắt liền đem to&agrave;n bộ m&agrave;n h&igrave;nh cho chiếm hết.<br />\r\n<br />\r\n&quot;Như thế cao năng &agrave;. . .&quot;<br />\r\n<br />\r\nĐ&oacute;ng lại mưa đạn về sau, L&acirc;m Mục C&aacute;p hơi nh&iacute;u nổi l&ecirc;n l&ocirc;ng m&agrave;y.<br />\r\n<br />\r\n&quot;Mọi người tốt, b&acirc;y giờ l&agrave; mười giờ rưỡi tối, ta ở v&agrave;o. . .&quot;<br />\r\n<br />\r\n&quot;Ba!&quot;<br />\r\n<br />\r\nKhủng bố đại sư c&ograve;n kh&ocirc;ng c&oacute; giới thiệu xong.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p trực tiếp ba một c&aacute;i đ&egrave; xuống ph&iacute;m c&aacute;ch tạm dừng sau đ&oacute; l&ugrave;i lại mấy tấm.<br />\r\n<br />\r\nỞ nơi n&agrave;y c&aacute;i khủng bố đại sư sau lưng, v&agrave;i gi&acirc;y đồng hồ ở giữa một c&aacute;i m&agrave;u tuyết trắng khủng bố th&acirc;n h&igrave;nh trong b&oacute;ng đ&ecirc;m cực tốc lướt qua.<br />\r\n<br />\r\n&quot;R&acirc;́t t&ocirc;́t.&quot;<br />\r\n<br />\r\nHắn cắt c&aacute;i đồ, nhiều hứng th&uacute; tiếp tục xem.<br />\r\n<br />\r\n&quot;Mọi người đều biết th&agrave;nh Nam linh dị truyền thuyết, n&oacute;i l&agrave; c&oacute; lệ quỷ c&ugrave;ng cương thi, truyền nhiều năm như vậy ta h&ocirc;m nay liền muốn v&agrave;o xem đ&acirc;y rốt cuộc l&agrave; kh&ocirc;ng phải thật sự.&quot;<br />\r\n<br />\r\nGiơ camera, sắc mặt đ&atilde; t&aacute;i nhợt khủng bố đại sư k&eacute;o ra trước người hắn vứt bỏ bệnh viện t&acirc;m thần đại m&ocirc;n.<br />\r\n<br />\r\nTo&agrave;n bộ đại m&ocirc;n đ&atilde; l&agrave; rỉ sắt loang lổ.<br />\r\n<br />\r\nMờ tối tia s&aacute;ng ph&iacute;a dưới, nương theo lấy cửa bị k&eacute;o ra cái chủng loại kia nhỏ b&eacute; ti&ecirc;́ng vang ch&oacute;i tai.<br />\r\n<br />\r\nThấy thế n&agrave;o thế n&agrave;o cảm gi&aacute;c quỷ dị khủng bố.<br />\r\n<br />\r\n&quot;C&aacute;c huynh đệ ta hiện tại đ&atilde; v&agrave;o. . .&quot;<br />\r\n<br />\r\n&quot;Oanh!&quot;<br />\r\n<br />\r\nHắn mới vừa v&agrave;o cửa kh&ocirc;ng đợi n&oacute;i dứt lời, cửa ph&iacute;a sau liền bị ầm vang đ&oacute;ng lại.<br />\r\n<br />\r\n&quot;Thứ g&igrave;? !&quot;<br />\r\n<br />\r\nĐ&egrave;n pin thật nhỏ tia s&aacute;ng ph&iacute;a dưới một c&aacute;i b&oacute;ng đen chợt l&oacute;e l&ecirc;n.<br />\r\n<br />\r\nTrong kh&ocirc;ng kh&iacute; tro bụi k&iacute;ch động.<br />\r\n<br />\r\nKhủng bố đại sư tr&ecirc;n tr&aacute;n nh&aacute;y mắt hiện đầy mồ h&ocirc;i lạnh, h&ocirc; hấp cũng biến th&agrave;nh dồn dập.<br />\r\n<br />\r\n&quot;Ba.&quot;<br />\r\n<br />\r\nNgay tại l&uacute;c hắn run rẩy giơ đ&egrave;n pin c&ugrave;ng camera kh&ocirc;ng biết l&agrave;m sao thời điểm.<br />\r\n<br />\r\nTr&ecirc;n bờ vai, một con trắng bệch ph&aacute;t xanh tay đ&atilde; v&ocirc; thanh v&ocirc; tức đ&aacute;p đi l&ecirc;n.<br />\r\n<br />\r\n&quot;Đ&ocirc;ng đ&ocirc;ng đ&ocirc;ng!&quot;<br />\r\n<br />\r\nKhủng bố đại sư nh&aacute;y mắt liền đứng im ngay tại chỗ.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; tăng th&ecirc;m bất luận c&aacute;i g&igrave; c&aacute;i kh&aacute;c &acirc;m thanh trong video c&oacute; thể r&otilde; r&agrave;ng nghe tới hắn kia c&agrave;ng l&uacute;c c&agrave;ng nhanh tiếng tim đập.<br />\r\n<br />\r\nDừng lại hai gi&acirc;y sau.<br />\r\n<br />\r\nHắn rung động rung động c&oacute; ch&uacute;t quay đầu lại.<br />\r\n<br />\r\nLiền sau lưng hắn trong b&oacute;ng tối, đ&egrave;n pin kia yếu ớt mơ hồ dưới &aacute;nh s&aacute;ng.<br />\r\n<br />\r\nMột c&aacute;i to&agrave;n th&acirc;n hư thối răng trần trụi giống như l&agrave; ở trong nước bị ng&acirc;m tẩm mấy th&aacute;ng thi thể đang đứng ở nơi đ&oacute;.<br />\r\n<br />\r\nSở dĩ l&agrave; thi thể, l&agrave; bởi v&igrave; n&oacute; lồng ngực bị đuổi một c&aacute;i động lớn, đ&egrave;n pin quang mang ho&agrave;n to&agrave;n c&oacute; thể xuy&ecirc;n thấu qua.<br />\r\n<br />\r\nNh&igrave;n thấy khủng bố đại sư quay đầu lại về sau, thi thể vốn định c&oacute; ch&uacute;t nghi&ecirc;ng đi đầu.<br />\r\n<br />\r\nNhưng một lần ph&aacute;t lực cổ của hắn v&acirc;̣y mà trực tiếp kh&ocirc;ng chịu nổi g&aacute;nh nặng bẻ g&atilde;y.<br />\r\n<br />\r\nTo&agrave;n bộ đầu &ugrave;ng ục ục lăn đến tr&ecirc;n mặt đất dừng ở khủng bố đại sư b&ecirc;n ch&acirc;n.<br />\r\n<br />\r\nNgay sau đ&oacute;, c&aacute;i n&agrave;y thi thể tr&ecirc;n cổ vậy m&agrave; liền giống như l&agrave; nấu canh nấu đến bong b&oacute;ng đồng dạng.<br />\r\n<br />\r\nMột c&aacute;i mới tinh hư thối đầu ngay tại một ch&uacute;t x&iacute;u trống ra.<br />\r\n<br />\r\n&quot;A mả mẹ n&oacute;!&quot;<br />\r\n<br />\r\nCực hạn trong sự sợ h&atilde;i mới hồi phục tinh thần lại khủng bố đại sư ngao h&eacute;t to một tiếng t&ugrave;y tiện t&igrave;m c&aacute;i phương hướng đi&ecirc;n cuồng chạy.<br />\r\n<br />\r\n&quot;Ba!&quot;<br />\r\n<br />\r\nNhưng vừa ph&oacute;ng ra hai bước khủng bố đại sư liền trực tiếp t&eacute; ng&atilde; tr&ecirc;n đất.<br />\r\n<br />\r\nRung động camera b&ecirc;n trong mơ hồ bắt được nhiều đ&aacute;m màu ngà sữa c&aacute;nh tay từ tr&ecirc;n s&agrave;n nh&agrave; duỗi ra.<br />\r\n<br />\r\nGiống như l&agrave; ở trong nước biển du đ&atilde;ng tảo biển b&igrave;nh thường, bọn ch&uacute;ng tr&ecirc;n kh&ocirc;ng trung nhộn nhạo từng c&aacute;i kh&ocirc;ng biết lúc nào đ&atilde; b&ograve; đến khủng bố đại sư tr&ecirc;n th&acirc;n.<br />\r\n<br />\r\nU &aacute;m mơ hồ h&igrave;nh tượng, tiếng thở dốc dồn dập c&ugrave;ng lắc lư camera l&agrave;m cho tất cả mọi người adrenalin đều tăng vọt l&ecirc;n.<br />\r\n<br />\r\n&quot;H&ocirc;! H&ocirc;! H&ocirc;!&quot;<br />\r\n<br />\r\nKh&ocirc;ng biết video l&agrave; bị cắt vẫn l&agrave; như thế n&agrave;o.<br />\r\n<br />\r\nLần nữa xuất hiện h&igrave;nh tượng, khủng bố đại sư đ&atilde; t&igrave;m một c&aacute;i ph&ograve;ng tử trốn đi.<br />\r\n<br />\r\n&quot;Qu&aacute;. . . Kinh khủng. . . Th&agrave;nh Nam linh dị truyền thuyết, l&agrave; thật. . .&quot;<br />\r\n<br />\r\nHắn tựa ở tr&ecirc;n tường lắc đầu s&aacute;t mồ h&ocirc;i lạnh &acirc;m thanh run rẩy n&oacute;i đến.<br />\r\n<br />\r\n&quot;C&aacute;c huynh đệ, ta vừa mới. . . Ta. . .&quot;<br />\r\n<br />\r\nMột hơi nhi c&ograve;n chưa l&ecirc;n đến, khủng bố đại sư đột nhi&ecirc;n ch&uacute; &yacute; tới trước mắt một mặt ngang k&iacute;nh.<br />\r\n<br />\r\nTrong k&iacute;nh hắn ch&iacute;nh tựa ở tr&ecirc;n tường.<br />\r\n<br />\r\nNhưng nh&igrave;n kỹ.<br />\r\n<br />\r\nSau lưng c&ograve;n c&oacute; một c&aacute;i mặc &aacute;o trắng t&oacute;c d&agrave;i tản ra c&uacute;i đầu nh&igrave;n kh&ocirc;ng thấy mặt tiểu nữ h&agrave;i.<br />\r\n<br />\r\n&quot;. . .&quot;<br />\r\n<br />\r\nKhủng bố đại sư h&ocirc; hấp cứng lại, cứng đờ quay đầu lại.<br />\r\n<br />\r\n&quot;H&ocirc;. . .&quot;<br />\r\n<br />\r\nD&aacute;n chặt lấy lấy sau lưng cứng rắn lạnh như băng v&aacute;ch tường, hắn mới cuồng nhẹ nh&agrave;ng thở ra.<br />\r\n<br />\r\nNhưng khi hắn lần nữa quay đầu lại nh&igrave;n về ph&iacute;a tấm gương thời điểm.<br />\r\n<br />\r\nTrong k&iacute;nh tiểu nữ h&agrave;i chẳng biết l&uacute;c n&agrave;o đ&atilde; c&aacute;ch ph&iacute;a sau hắn chỉ c&oacute; một bước xa.<br />\r\n<br />\r\n&quot;Đ&ocirc;ng đ&ocirc;ng đ&ocirc;ng!&quot;<br />\r\n<br />\r\nDựa v&agrave;o tường vừa x&ecirc; dịch một lần bước ch&acirc;n.<br />\r\n<br />\r\nTiểu nữ h&agrave;i giống như l&agrave; căn bản kh&ocirc;ng tồn tại một dạng biến mất ở trong k&iacute;nh.<br />\r\n<br />\r\n&quot;Ai u ta. . .&quot;<br />\r\n<br />\r\nDụi dụi con mắt hết lần n&agrave;y đến lần kh&aacute;c x&aacute;c nhận tiểu nữ h&agrave;i đ&atilde; biến mất rồi về sau, khủng bố đại sư thở d&agrave;i nhẹ nh&otilde;m.<br />\r\n<br />\r\nNhưng một gi&acirc;y sau, n&eacute;t mặt của hắn cứng đờ.<br />\r\n<br />\r\nTrong k&iacute;nh hắn K&iacute;nh Tượng ch&iacute;nh hoảng sợ nh&igrave;n chằm chằm hắn, giống như l&agrave; đang cầu cứu đồng dạng.<br />\r\n<br />\r\nM&agrave; K&iacute;nh Tượng sau lưng.<br />\r\n<br />\r\nC&aacute;i kia thấy kh&ocirc;ng r&otilde; mặt tiểu nữ h&agrave;i ch&iacute;nh mở ra miệng to như chậu m&aacute;u trong l&uacute;c đ&oacute; từ sau lưng của hắn tho&aacute;t ra.<br />\r\n<br />\r\nRậm rạp chằng chịt răng nanh hiện ra l&agrave;m người run rẩy h&agrave;n quang hung hăng hướng ph&iacute;a cổ của hắn t&aacute;p tới.<br />\r\n<br />\r\nM&aacute;u tươi từ trong k&iacute;nh &agrave;o ạt chảy ra.<br />\r\n<br />\r\nKhủng bố đại sư chỉ cảm thấy cổ m&aacute;t lạnh, bản năng k&ecirc;u l&ecirc;n một tiếng sợ h&atilde;i sau đi&ecirc;n cuồng một lần nữa mở cửa kh&oacute;a k&eacute;o cửa ra.<br />\r\n<br />\r\nMở cửa một nh&aacute;y mắt, to&agrave;n bộ camera đều chấn động một c&aacute;i.<br />\r\n<br />\r\nNgo&agrave;i cửa, cỗ thi thể kia ch&iacute;nh cơ hồ d&aacute;n tại tr&ecirc;n cửa kh&eacute;o l&eacute;o chờ lấy hắn.<br />\r\n<br />\r\nMới mọc ra tr&ecirc;n đầu c&ograve;n kh&ocirc;ng c&oacute; con mắt c&ugrave;ng c&aacute;i mũi.<br />\r\n<br />\r\nNhưng lại c&oacute; miệng.<br />\r\n<br />\r\nC&oacute; một trương có th&ecirc;̉ liệt đến hai c&aacute;i tai căn đem to&agrave;n bộ đầu đều mở ra miệng.<br />\r\n<br />\r\nSau đ&oacute; to&agrave;n bộ h&igrave;nh tượng một trận lắc lư.<br />\r\n<br />\r\nVideo đến đ&acirc;y im bặt m&agrave; dừng.<br />\r\n<br />\r\n&quot;C&oacute; ch&uacute;t &yacute; tứ. . .&quot;<br />\r\n<br />\r\nHủy bỏ tự động ph&aacute;t s&oacute;ng li&ecirc;n tục lại mở ra mưa đạn nh&igrave;n một lần.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p trực tiếp nhặt l&ecirc;n một b&ecirc;n t&uacute;i s&aacute;ch.<br />\r\n<br />\r\n&quot;H&ocirc;m nay trăng mờ gi&oacute; lớn, ta lại ấn đường biến đen, l&agrave; một phổ cập khoa học linh dị ng&agrave;y tốt l&agrave;nh!&quot;<br />\r\n<br />\r\nHắn từ trong tủ lạnh lấy ra một b&igrave;nh đặc chế m&aacute;u.<br />\r\n<br />\r\nTh&ocirc;ng qua chuy&ecirc;n nghiệp thị gi&aacute;c, L&acirc;m Mục C&aacute;p vậy x&aacute;c định c&aacute;i n&agrave;y bỏ hoang bệnh viện t&acirc;m thần b&ecirc;n trong thật c&oacute; linh dị.<br />\r\n<br />\r\nVừa vặn mang một &iacute;t nhi đồ tốt đi b&aacute;i phỏng một lần, lảm nhảm lảm nhảm việc nh&agrave;.</p>', '2021-11-28 16:03:30', 13, 1),
(226, 60, '« phong thủy bảo địa »', '2', '<p><br />\r\n&quot;Sư phụ, ra ngo&agrave;i v&ograve;ng b&ecirc;n kia.&quot;<br />\r\n<br />\r\nSau khi ra cửa L&acirc;m Mục C&aacute;p trực tiếp đ&aacute;nh chiếc xe.<br />\r\n<br />\r\n&quot;C&aacute;c vị, ta đ&ecirc;m nay mười điểm trực tiếp thăm viếng th&agrave;nh Nam vứt bỏ bệnh viện t&acirc;m thần linh dị.&quot;<br />\r\n<br />\r\nNgồi ở chỗ ngồi ph&iacute;a sau, hắn trực tiếp tại tiểu ph&aacute; trạm phát ra một c&aacute;i trực tiếp b&aacute;o trước.<br />\r\n<br />\r\n&quot;Tiểu hỏa tử, ngươi đ&ecirc;m h&ocirc;m khuya khoắt đến b&ecirc;n n&agrave;y l&agrave;m g&igrave;?&quot;<br />\r\n<br />\r\n&quot;&Agrave;i sư phụ, ng&agrave;i biết r&otilde; th&agrave;nh Nam linh dị truyền thuyết sao?&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p thu hồi điện thoại hỏi ngược lại.<br />\r\n<br />\r\n&quot;Đương nhi&ecirc;n biết rồi! L&atilde;o th&agrave;nh Nam người đều tự m&igrave;nh trải qua, b&acirc;y giờ suy nghĩ một ch&uacute;t c&ograve;n h&atilde;i được hoảng, đoạn thời gian kia ta căn bản kh&ocirc;ng d&aacute;m mở ca đ&ecirc;m xe.&quot;<br />\r\n<br />\r\nT&agrave;i xế sư phụ xuy&ecirc;n thấu qua k&iacute;nh b&ecirc;n nh&igrave;n L&acirc;m Mục C&aacute;p.<br />\r\n<br />\r\n&quot;Ng&agrave;i c&ugrave;ng ta n&oacute;i một ch&uacute;t th&ocirc;i, ta đ&acirc;y một mực tin đồn, chưa từng nghe qua một c&aacute;i thống nhất phi&ecirc;n bản.&quot;<br />\r\n<br />\r\n&quot;Linh dị truyền thuyết x&aacute;c thực kh&ocirc;ng c&oacute; thống nhất phi&ecirc;n bản, bởi v&igrave; trong truyền thuyết linh dị cũng kh&ocirc;ng dừng một c&aacute;i, to&agrave;n bộ c&ugrave;ng b&aacute;ch quỷ dạ h&agrave;nh đồng dạng.&quot;<br />\r\n<br />\r\n&quot;Vậy ng&agrave;i trước c&ugrave;ng ta n&oacute;i n&oacute;i cương thi sự t&igrave;nh th&ocirc;i, gần nhất tr&ecirc;n mạng lại lửa cháy đến rồi.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p để s&aacute;ch xuống bao đem đầu tựa ở ngồi trước b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nHắn muốn nghe xem linh dị tại người b&igrave;nh thường trong l&ograve;ng rốt cuộc l&agrave; một loại như thế n&agrave;o tồn tại.<br />\r\n<br />\r\n&quot;Đ&uacute;ng vậy a, gần nhất th&agrave;nh Nam sự kiện linh dị lại ph&aacute;t hỏa, ta tr&ecirc;n xe mỗi ng&agrave;y buổi tối ph&aacute;t thanh đều l&agrave; để cho ta cẩn thận.&quot;<br />\r\n<br />\r\nT&agrave;i xế sư phụ t&ugrave;y tiện điều mấy c&aacute;i đ&agrave;i ph&aacute;t thanh, đều ở đ&acirc;y giảng th&agrave;nh Nam sự kiện linh dị.<br />\r\n<br />\r\nN&oacute;i như vậy lấy kể, liền xem như kh&ocirc;ng tin linh dị trong l&ograve;ng người cũng được sợ h&atilde;i.<br />\r\n<br />\r\n&quot;Cương thi chuyện kia c&ograve;n phải l&agrave; v&agrave;i thập ni&ecirc;n trước.&quot;<br />\r\n<br />\r\nĐ&oacute;ng lại ph&aacute;t thanh về sau, t&agrave;i xế sư phụ hắng giọng một c&aacute;i bắt đầu n&oacute;i.<br />\r\n<br />\r\n&quot;Liền c&aacute;i kia hiện tại bỏ hoang bệnh viện t&acirc;m thần l&agrave; tư nh&acirc;n, ch&agrave;o gi&aacute; đặc biệt qu&yacute;.&quot;<br />\r\n<br />\r\n&quot;Nhưng trị liệu hiệu quả đặc biệt tốt, những c&aacute;i kia đặc biệt y&ecirc;u động đặc biệt phấn khởi bệnh t&acirc;m thần chỉ cần đi mấy ng&agrave;y, trở về liền đặc biệt trung thực.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng lộn xộn cũng kh&ocirc;ng n&oacute;i chuyện, to&agrave;n bộ giống như l&agrave; biến th&agrave;nh người kh&aacute;c đồng dạng.&quot;<br />\r\n<br />\r\n&quot;Ch&iacute;nh l&agrave; l&uacute;c buổi tối lu&ocirc;n lu&ocirc;n sẽ v&ocirc; h&igrave;nh m&agrave;i răng, c&oacute; đ&ocirc;i khi nửa đ&ecirc;m sẽ nh&igrave;n trừng trừng lấy ngươi chảy nước miếng, m&agrave; lại kh&ocirc;ng thể gặp &aacute;nh nắng, vừa thấy được &aacute;nh nắng rồi c&ugrave;ng như bị đi&ecirc;n.&quot;<br />\r\n<br />\r\n&quot;Thẳng đến c&oacute; lần một c&aacute;i bệnh t&acirc;m thần trực tiếp chạy đến tr&ecirc;n đường c&aacute;i đi&ecirc;n cuồng h&ocirc; c&oacute; cương thi.&quot;<br />\r\n<br />\r\n&quot;Cả người đều t&oacute;c tai b&ugrave; x&ugrave;, trong mắt tất cả đều l&agrave; tơ m&aacute;u, ch&acirc;n trần tại tr&ecirc;n đường c&aacute;i chạy, nh&igrave;n thấy một người liền &ocirc;m lấy kh&ocirc;ng bu&ocirc;ng tay nhường cho người mau cứu hắn.&quot;<br />\r\n<br />\r\nGiảng đến nơi đ&acirc;y, t&agrave;i xế sư phụ thở d&agrave;i.<br />\r\n<br />\r\n&quot;N&agrave;o c&oacute; người cứu hắn sao?&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p hỏi.<br />\r\n<br />\r\n&quot;N&agrave;o c&oacute; a? Coi như một c&aacute;i bệnh t&acirc;m thần m&agrave; th&ocirc;i.&quot;<br />\r\n<br />\r\n&quot;Sau n&agrave;y hắn liền hoả tốc bị bệnh viện t&acirc;m thần bắt đi.&quot;<br />\r\n<br />\r\n&quot;Lại sau n&agrave;y ch&iacute;nh l&agrave; th&agrave;nh Nam giết người sự kiện, c&oacute; c&aacute;i trị tốt bệnh t&acirc;m thần ban đ&ecirc;m nửa đ&ecirc;m đem trong nh&agrave; l&atilde;o nh&acirc;n h&agrave;i tử tất cả đều tươi sống cắn chết.&quot;<br />\r\n<br />\r\n&quot;M&agrave; lại những thi thể n&agrave;y ph&aacute;t hiện thời điểm đều l&agrave; cứng đờ ph&aacute;t xanh, r&otilde; r&agrave;ng mới chết mấy giờ liền lạnh buốt lạnh buốt.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng bao l&acirc;u, từng c&aacute;i từ nơi đ&oacute; ra tới bệnh t&acirc;m thần cũng bắt đầu ph&aacute;t cuồng, nửa đ&ecirc;m cắn người, to bằng c&aacute;nh tay d&acirc;y x&iacute;ch sắt n&oacute;i tr&aacute;nh tho&aacute;t liền tr&aacute;nh tho&aacute;t.&quot;<br />\r\n<br />\r\n&quot;Li&ecirc;n ph&aacute;t sinh ba đợt diệt m&ocirc;n &aacute;n mạng về sau mới rốt cục tra được c&aacute;i kia bệnh viện t&acirc;m thần tại tầng hầm ngầm nu&ocirc;i cương thi, để cương thi đi h&uacute;t những c&aacute;i kia bệnh t&acirc;m thần m&aacute;u.&quot;<br />\r\n<br />\r\n&quot;Đ&aacute;ng sợ nhất ch&iacute;nh l&agrave; cuối c&ugrave;ng chuyện n&agrave;y cũng kh&ocirc;ng s&aacute;ng tỏ, ai cũng kh&ocirc;ng biết cương thi đều l&agrave;m kh&ocirc;ng c&oacute; l&agrave;m sạch sẽ.&quot;<br />\r\n<br />\r\nT&agrave;i xế sư phụ trực tiếp r&ugrave;ng m&igrave;nh một c&aacute;i.<br />\r\n<br />\r\nTầng hầm ngầm nu&ocirc;i cương thi, d&ugrave;ng bệnh t&acirc;m thần m&aacute;u đi đ&uacute;t, cuối c&ugrave;ng c&ograve;n để bệnh t&acirc;m thần trở về. . .<br />\r\n<br />\r\nLiền xem như dưới ban ng&agrave;y ban mặt, ngẫm lại loại chuyện n&agrave;y đều sợ h&atilde;i trong l&ograve;ng.<br />\r\n<br />\r\n&quot;Vậy ng&agrave;i l&agrave; thế n&agrave;o nh&igrave;n cương thi đ&acirc;y n&agrave;y?&quot;<br />\r\n<br />\r\n&quot;Vậy c&ograve;n có th&ecirc;̉ thế n&agrave;o nh&igrave;n? Nửa tin nửa ngờ th&ocirc;i, hiện tại truyền ra ai cũng kh&ocirc;ng biết l&agrave; thật l&agrave; giả, m&agrave; lại kh&ocirc;ng ngừng ch&uacute;ng ta th&agrave;nh Nam, thật nhiều địa phương đều c&oacute; c&aacute;c loại linh dị truyền thuyết, thời gian như thường qua chứ sao.&quot;<br />\r\n<br />\r\n&quot;Ng&agrave;i sợ sao?&quot;<br />\r\n<br />\r\n&quot;Ngươi nh&igrave;n một c&aacute;i ngươi đứa nhỏ n&agrave;y hỏi, ta hơn năm mươi tuổi đại l&atilde;o gia. . . Kỳ thật cũng sợ.&quot;<br />\r\n<br />\r\nT&agrave;i xế sư phụ chiến thuật ngửa ra sau một đợt cuối c&ugrave;ng &aacute;nh mắt n&eacute; tr&aacute;nh n&oacute;i đến.<br />\r\n<br />\r\n&quot;X&aacute;c thực. . .&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p nhẹ gật đầu.<br />\r\n<br />\r\nHiện tại đại gia đối linh dị phần lớn đều c&oacute; một loại e ngại t&acirc;m l&yacute;.<br />\r\n<br />\r\nN&agrave;y chủ yếu l&agrave; căn cứ v&agrave;o linh dị tại đại gia trong l&ograve;ng l&agrave; thần b&iacute; kh&ocirc;ng biết, đối với kh&ocirc;ng biết sinh vật ai c&oacute; thể kh&ocirc;ng sợ?<br />\r\n<br />\r\nSở dĩ hắn phổ cập khoa học linh dị con đường thật sự g&aacute;nh nặng đường xa a. . .<br />\r\n<br />\r\n&quot;Tiểu hỏa tử, ngươi hơn nửa đ&ecirc;m tới chỗ n&agrave;y l&agrave;m g&igrave;?&quot;<br />\r\n<br />\r\nXuất ra th&agrave;nh khu về sau, t&agrave;i xế sư phụ mới rốt cục &yacute; thức được m&igrave;nh ở hướng c&aacute;i n&agrave;o mở.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; việc g&igrave;, ta ch&iacute;nh l&agrave; đi c&aacute;i kia vứt bỏ bệnh viện t&acirc;m thần đi bộ một ch&uacute;t.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave;! ?&quot;<br />\r\n<br />\r\nNghe xong L&acirc;m Mục C&aacute;p n&oacute;i như vậy.<br />\r\n<br />\r\nT&agrave;i xế sư phụ trước điểm một cước phanh lại.<br />\r\n<br />\r\n&quot;Tiểu hỏa tử, ngươi kh&ocirc;ng muốn sống nữa? Ta vừa mới n&oacute;i ngươi l&agrave;m chuyện kể trước khi ngủ nghe đ&acirc;u? Đ&oacute; cũng đều l&agrave; thật sự! Kia đẫm m&aacute;u &aacute;n mạng b&acirc;y giờ c&ograve;n tại tr&ecirc;n mạng b&agrave;y biện đ&acirc;u!&quot;<br />\r\n<br />\r\n&quot;Cũng bởi v&igrave; l&agrave; thật ta mới phải đi.&quot;<br />\r\n<br />\r\n&quot;Ngươi, ngươi ngươi ngươi cũng kh&ocirc;ng n&ecirc;n kh&ocirc;ng biết trời cao đất rộng a ta cho ngươi biết, đ&acirc;y ch&iacute;nh l&agrave; cương thi a, ăn thịt người uống máu người.&quot;<br />\r\n<br />\r\n&quot;Thực sẽ chết người, ngươi nghe th&uacute;c một lời khuy&ecirc;n, b&acirc;y giờ đi về, th&uacute;c cũng kh&ocirc;ng thu ngươi tiền xe.&quot;<br />\r\n<br />\r\nT&agrave;i xế sư phụ xuy&ecirc;n thấu qua k&iacute;nh b&ecirc;n nh&igrave;n xem kh&ocirc;ng qu&aacute; để &yacute; L&acirc;m Mục C&aacute;p trừng tr&ograve;ng mắt n&oacute;i đến.<br />\r\n<br />\r\nTựa hồ chỉ cần L&acirc;m Mục C&aacute;p một c&acirc;u, hắn lập tức giẫm l&ecirc;n ch&acirc;n ga quay đầu liền đi.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; chuyện sư phụ, nếu như l&agrave; thật sự, ta trẻ tuổi khỏe mạnh cường tráng c&oacute; thể chạy, nếu như l&agrave; giả, ta đ&acirc;y kh&ocirc;ng coi như b&aacute;c bỏ tin đồn sao.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p vỗ bộ ngực n&oacute;i đến.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải tiểu hỏa tử, một m&igrave;nh ngươi ngươi. . .&quot;<br />\r\n<br />\r\n&quot;Y&ecirc;n t&acirc;m đi sư phụ! Cũng l&agrave; bởi v&igrave; c&aacute;i n&agrave;y linh dị truyền thuyết, l&agrave;m cho ch&uacute;ng ta th&agrave;nh Nam l&ograve;ng người b&agrave;ng ho&agrave;ng, tr&ecirc;n mạng cũng l&agrave; lời đồn nhảm bay đầy trời, ta coi như v&igrave; ch&uacute;ng ta th&agrave;nh Nam l&agrave;m cống hiến.&quot;<br />\r\n<br />\r\n&quot;Ngươi. . . Được th&ocirc;i, n&oacute;i kh&ocirc;ng lại ngươi.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; chuyện sư phụ, y&ecirc;n t&acirc;m đi, &quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p đến b&ecirc;n ngo&agrave;i đường v&agrave;nh đai b&ecirc;n cạnh một mảnh b&atilde;i cỏ trước, qu&eacute;t xong m&atilde; giao xong khoản về sau, cầm cẩn thận quay chụp thiết bị n&oacute;i đến.<br />\r\n<br />\r\n&quot;. . . Người trẻ tuổi kia. . .&quot;<br />\r\n<br />\r\nT&agrave;i xế sư phụ c&oacute; ch&uacute;t muốn n&oacute;i lại th&ocirc;i lắc đầu, quay đầu trực tiếp th&ecirc;m đến 80 bước phi tốc rời đi.<br />\r\n<br />\r\nC&aacute;i n&agrave;y ng&agrave;y m&ugrave;a hạ, hắn chỉ l&agrave; đứng ở nơi n&agrave;y c&aacute;i địa phương hắn liền to&agrave;n th&acirc;n r&eacute;t run.<br />\r\n<br />\r\n&quot;R&acirc;́t t&ocirc;́t, &acirc;m kh&iacute; rất nặng, thần thanh kh&iacute; sảng.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p h&iacute;t s&acirc;u một hơi, tinh lực tr&agrave;n đầy từ trong bọc m&oacute;c ra camera chờ thiết bị, sau đ&oacute; mở ra tiểu ph&aacute; trạm.<br />\r\n<br />\r\n&quot;Hoắc, nhiều như vậy người!&quot;<br />\r\n<br />\r\nHắn một c&aacute;i kh&ocirc;ng đến mười vạn phấn nhỏ up, mười giờ tối trực tiếp b&aacute;o trước lại c&oacute; hơn một vạn người hẹn trước.<br />\r\n<br />\r\nM&agrave; lại ngắn ngủi nửa giờ thời gian lại c&oacute; hơn bốn trăm đầu b&igrave;nh luận.<br />\r\n<br />\r\n&quot;Bồ c&acirc;u bồ c&acirc;u kh&ocirc;ng n&ecirc;n v&igrave; nhiệt độ đi a!&quot;<br />\r\n<br />\r\n&quot;666 nh&igrave;n khủng bố đại sư video vẫn c&ograve;n c&oacute; up d&aacute;m đi.&quot;<br />\r\n<br />\r\n&quot;Bỏ lỡ khủng bố đại sư trực tiếp, lần n&agrave;y ta cũng kh&ocirc;ng n&ecirc;n bỏ lỡ.&quot;<br />\r\n<br />\r\n&quot;Đều mười giờ, up người đ&acirc;u?&quot;<br />\r\n<br />\r\n&quot;Ta đ&atilde; bắt đầu khẩn trương.&quot;<br />\r\n<br />\r\n&quot;Chớ khẩn trương, linh dị đều l&agrave; kh&ocirc;ng tồn tại, những n&agrave;y up ch&iacute;nh l&agrave; kết phường gạt người.&quot;<br />\r\n<br />\r\n&quot;Trước dự định một đợt bệnh viện giường ngủ.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p qu&eacute;t một đợt b&igrave;nh luận khu sau c&agrave;ng ph&aacute;t gi&aacute;c phổ cập khoa học linh dị sự t&igrave;nh lửa s&eacute;m l&ocirc;ng m&agrave;y.<br />\r\n<br />\r\nHiện tại đại gia hoặc l&agrave; kh&ocirc;ng tin, hoặc l&agrave; liền sợ, về sau chờ linh dị ho&agrave;n to&agrave;n kh&ocirc;i phục c&ograve;n thế n&agrave;o c&ugrave;ng thi&ecirc;n h&igrave;nh vạn trạng linh dị nh&oacute;m chung sống ho&agrave; b&igrave;nh kề vai s&aacute;t c&aacute;nh?<br />\r\n<br />\r\n&quot;Mọi người tốt, b&acirc;y giờ l&agrave; ban đ&ecirc;m. . . Mười giờ hơn một ch&uacute;t x&iacute;u.&quot;<br />\r\n<br />\r\n&quot;Ta nh&igrave;n thấy đại gia @ ta c&aacute;i kia video, bởi v&igrave; ta bản th&acirc;n li&ecirc;̀n là th&agrave;nh Nam người, hiện tại th&agrave;nh Nam linh dị nhiệt độ lại cao như vậy, ta lần n&agrave;y liền cho đại gia đến t&igrave;m kiếm.&quot;<br />\r\n<br />\r\n&quot;Như đại gia nh&igrave;n thấy, nơi n&agrave;y c&oacute; thể được xưng l&agrave; phong thuỷ bảo địa, v&ocirc; c&ugrave;ng mỹ lệ.&quot;<br />\r\n<br />\r\nHắn nghi&ecirc;ng người sang, xuy&ecirc;n thấu qua m&acirc;y đen yếu ớt nguyệt quang chiếu xạ đến ph&iacute;a sau hắn cao cỡ nửa người loạn thảo tr&ecirc;n mặt đất.<br />\r\n<br />\r\nGi&oacute; đ&ecirc;m thổi qua thanh &acirc;m nhường cho người nhịn kh&ocirc;ng được đem ch&acirc;n r&uacute;t v&agrave;o ổ chăn.<br />\r\n<br />\r\nĐằng sau lo&aacute;ng tho&aacute;ng c&oacute; thể nh&igrave;n thấy từng c&aacute;i mộ bia như ẩn như hiện, cỏ xanh hương vị xen lẫn một cỗ v&ocirc; h&igrave;nh nh&agrave;n nhạt huyết tinh để L&acirc;m Mục C&aacute;p tr&ecirc;n mặt l&oacute;e l&ecirc;n vẻ hưng phấn.<br />\r\n<br />\r\nM&aacute;y thu h&igrave;nh quang mang b&ecirc;n dưới thỉnh thoảng mắt hiện lục quang răng gặm động vật phi tốc vọt qua.<br />\r\n<br />\r\nPh&iacute;a sau trong b&oacute;ng tối c&ograve;n c&oacute; thể nh&igrave;n trộm một c&aacute;i &acirc;m trầm kiến tr&uacute;c mơ hồ h&igrave;nh d&aacute;ng.<br />\r\n<br />\r\n&quot;Thật l&agrave; th&agrave;nh Nam c&aacute;i kia vứt bỏ bệnh viện t&acirc;m thần.&quot;<br />\r\n<br />\r\n&quot;up lại c&ograve;n thật sự d&aacute;m đến.&quot;<br />\r\n<br />\r\n&quot;&laquo; phong thuỷ bảo địa &raquo; &quot;<br />\r\n<br />\r\n&quot;&laquo; mỹ lệ phi thường &raquo; &quot;<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; ta gặp qua b&igrave;nh tĩnh nhất uploader.&quot;<br />\r\n<br />\r\n&quot;Tiến nhanh đến up một hồi bị dọa đến tè ra qu&acirc;̀n c&ugrave;ng khủng bố đại sư một c&aacute;i ph&ograve;ng bệnh.&quot;<br />\r\n<br />\r\n&quot;Uploader tựa hồ c&ograve;n kh&ocirc;ng c&oacute; &yacute; thức được đối mặt m&igrave;nh l&agrave; địa phương n&agrave;o.&quot;<br />\r\n<br />\r\n&quot;up đi nhanh đi, nơi n&agrave;y thật sự nguy hiểm, đừng lấy t&iacute;nh mạng m&igrave;nh n&oacute;i đ&ugrave;a a!&quot;</p>', '2021-11-28 16:03:48', 6, 1),
(227, 60, 'Nhiều hiếu khách a!', '3', '<p>Cứ việc L&acirc;m Mục C&aacute;p chỉ l&agrave; một fan h&acirc;m mộ lượng kh&ocirc;ng đến mười vạn nhỏ up.<br />\r\n<br />\r\nNhưng bởi v&igrave; th&agrave;nh Nam vứt bỏ bệnh viện t&acirc;m thần b&acirc;y giờ nhiệt độ cực cao, sở dĩ hiện tại k&ecirc;nh trực tiếp b&ecirc;n trong th&igrave; c&oacute; hơn hai vạn người.<br />\r\n<br />\r\n&quot;Lần n&agrave;y d&atilde; ngoại trực tiếp, ta vẫn như cũ mang s&oacute;ng b&iacute; chế m&aacute;u.&quot;<br />\r\n<br />\r\n&quot;Lần n&agrave;y m&aacute;u chất lượng phi thường cao, lẽ ra c&oacute; thể c&acirc;u được rất nhiều linh dị.&quot;<br />\r\n<br />\r\n&quot;Được rồi, kh&ocirc;ng n&oacute;i nhiều n&oacute;i! Ch&uacute;ng ta đi!&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p điều chỉnh tốt m&aacute;y thu h&igrave;nh vị tr&iacute;, mặc quần đ&ugrave;i sải bước hướng ph&iacute;a trước mặt trong b&oacute;ng tối đi đến.<br />\r\n<br />\r\n&quot;Đại gia b&igrave;nh thường đến loại n&agrave;y kh&ocirc;ng biết địa phương, đều cần mang một &iacute;t nhi thứ g&igrave; hộ th&acirc;n, ta h&ocirc;m nay hay d&ugrave;ng c&aacute;i xẻng.&quot;<br />\r\n<br />\r\nHắn lung lay trong tay c&aacute;i xẻng nhỏ n&oacute;i đến.<br />\r\n<br />\r\n&quot;up xem ra l&agrave;m sao kh&iacute; định thần nh&agrave;n d&aacute;ng vẻ.&quot;<br />\r\n<br />\r\n&quot;Sở dĩ th&agrave;nh Nam linh dị truyền thuyết rốt cuộc l&agrave; thật hay giả?&quot;<br />\r\n<br />\r\n&quot;Vật l&yacute; khu ma, đại sư ta hiểu!&quot;<br />\r\n<br />\r\n&quot;Ngươi cầm c&aacute;i c&aacute;i xẻng nhỏ đặt kia đ&ugrave;a quỷ chơi đ&acirc;u?&quot;<br />\r\n<br />\r\n&quot;Nh&agrave; ta x&uacute;c ph&acirc;n c&aacute;i xẻng đều so c&aacute;i n&agrave;y lớn.&quot;<br />\r\n<br />\r\nK&ecirc;nh trực tiếp đại đa số người đều đ&atilde; nh&igrave;n khủng bố đại sư video.<br />\r\n<br />\r\nNhưng đối với trong video chỗ xuất hiện linh dị c&ugrave;ng th&agrave;nh Nam linh dị truyền thuyết, đại gia vẫn l&agrave; xuất ph&aacute;t từ một loại nửa tin nửa ngờ trạng th&aacute;i.<br />\r\n<br />\r\nMặc d&ugrave; L&acirc;m Mục C&aacute;p k&ecirc;nh trực tiếp ti&ecirc;u đề l&agrave; &quot;Phổ cập khoa học th&agrave;nh Nam linh dị trong truyền thuyết linh dị&quot;, nh&atilde;n hi&ecirc;̣u b&ecirc;n trong vậy mang theo phổ cập khoa học chữ.<br />\r\n<br />\r\nNhưng đo&aacute;n chừng tuyệt đại đa số người cũng l&agrave;m th&agrave;nh truyền thuyết đ&ocirc; thị th&aacute;m hiểm loại trực tiếp.<br />\r\n<br />\r\n&quot;D&aacute;t. . . K&iacute;t ~ &quot;<br />\r\n<br />\r\nTại L&acirc;m Mục C&aacute;p nỗ lực.<br />\r\n<br />\r\nBệnh viện t&acirc;m thần ph&iacute;a ngo&agrave;i đại m&ocirc;n ph&aacute;t ra một trận l&agrave;m l&ograve;ng người ngứa một ch&uacute;t nhẹ vang l&ecirc;n.<br />\r\n<br />\r\nTro bụi tại mờ tối dưới &aacute;nh trăng h&ocirc; một lần tr&ocirc;i dạt đến kh&ocirc;ng trung, sau đ&oacute; chậm r&atilde;i rơi xuống.<br />\r\n<br />\r\nC&aacute;ch m&agrave;n h&igrave;nh tựa hồ cũng có th&ecirc;̉ nghe được loại kia mục n&aacute;t r&aacute;ch n&aacute;t hương vị, cảm nhận được loại kia l&agrave;m người run rẩy cảm gi&aacute;c.<br />\r\n<br />\r\n&quot;Tin tưởng mọi người đều nghe qua th&agrave;nh Nam linh dị truyền thuyết, n&oacute;i l&agrave; ở nơi n&agrave;y c&aacute;i bệnh viện t&acirc;m thần tầng hầm ngầm, nu&ocirc;i cương thi, sau đ&oacute; d&ugrave;ng bệnh t&acirc;m thần người bệnh m&aacute;u đi đ&uacute;t.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p quan s&aacute;t một lần ho&agrave;n cảnh chung quanh sau thu hồi c&aacute;i xẻng nh&igrave;n xem camera n&oacute;i đến.<br />\r\n<br />\r\n&quot;Ta sẽ đi ngay b&acirc;y giờ. . .&quot;<br />\r\n<br />\r\n&quot;Ba!&quot;<br />\r\n<br />\r\nHắn lời c&ograve;n chưa n&oacute;i hết, ph&iacute;a trước chỗ hắc &aacute;m trong một c&aacute;i ph&ograve;ng liền truyền đến một trận nhỏ b&eacute; lại tiếng vang quỷ dị.<br />\r\n<br />\r\n&quot;Xuỵt!&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p vội v&agrave;ng hưng phấn hướng về ph&iacute;a camera so c&aacute;i im lặng thủ thế.<br />\r\n<br />\r\nSau đ&oacute; cau m&agrave;y thận trọng đi đến ph&iacute;a trước đẩy cửa ph&ograve;ng ra.<br />\r\n<br />\r\nLinh dị kh&ocirc;i phục sơ kỳ linh dị kh&ocirc;ng biết v&igrave; c&aacute;i g&igrave; đều so s&aacute;nh xấu hổ, c&ograve;n c&oacute; một ch&uacute;t sợ người.<br />\r\n<br />\r\nMặc d&ugrave; hắn tự nhận l&agrave; bản th&acirc;n mặt mũi hiền l&agrave;nh hơi bị đẹp trai, nhưng chỉ cần động tĩnh qu&aacute; lớn liền sẽ chạy đi.<br />\r\n<br />\r\nĐi v&agrave;o gian ph&ograve;ng sau.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p m&iacute;m m&ocirc;i qu&eacute;t mắt một lần.<br />\r\n<br />\r\nCả ph&ograve;ng đều l&agrave; kh&ocirc;ng, thoạt nh&igrave;n kh&ocirc;ng c&oacute; linh dị tồn tại qua vết t&iacute;ch.<br />\r\n<br />\r\nNhưng l&agrave; nhiều năm như vậy học tập c&ugrave;ng kinh nghiệm n&oacute;i cho hắn biết trong n&agrave;y, nhất định c&oacute; quỷ.<br />\r\n<br />\r\n&quot;Nếu như ta l&agrave; linh dị, nh&igrave;n thấy ta, sẽ n&uacute;p ở chỗ n&agrave;o đ&acirc;u. . .&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p dừng bước lại.<br />\r\n<br />\r\nTrầm tư v&agrave;i gi&acirc;y đồng hồ sau cổ của hắn kết c&oacute; ch&uacute;t nhấp nh&ocirc;, sau đ&oacute; chậm r&atilde;i quay đầu lại.<br />\r\n<br />\r\nTại vừa mới hắn đẩy ra ph&iacute;a sau cửa, c&aacute;ch hắn ph&iacute;a sau lưng kh&ocirc;ng đến nửa m&eacute;t vị tr&iacute;.<br />\r\n<br />\r\nĐứng cỗ thi thể kia.<br />\r\n<br />\r\nCỗ kia to&agrave;n th&acirc;n trắng x&aacute;m hư thối, ngực bị đuổi c&aacute;i lỗ lớn thi thể.<br />\r\n<br />\r\nN&oacute; t&acirc;n sinh tr&ecirc;n đầu con mắt c&ugrave;ng c&aacute;i mũi đều rất t&ugrave;y &yacute;, d&agrave;i đến một ch&uacute;t đều kh&ocirc;ng đối xứng.<br />\r\n<br />\r\nChỉ c&oacute; một c&aacute;i miệng, rất đỏ, rất ti&ecirc;u ch&iacute;.<br />\r\n<br />\r\nMỉm cười rất đ&uacute;ng chỗ, trực tiếp liệt đến b&ecirc;n tai.<br />\r\n<br />\r\nM&agrave; vừa mới n&oacute; vẫn c&aacute;i n&agrave;y dạng kh&ocirc;ng tiếng động đứng tại ph&iacute;a sau cửa, lẳng lặng nh&igrave;n L&acirc;m Mục C&aacute;p.<br />\r\n<br />\r\nLẳng lặng mỉm cười. . .<br />\r\n<br />\r\n&quot;Mưa đạn hộ thể mưa đạn hộ thể mưa đạn hộ thể mưa đạn hộ thể!&quot;<br />\r\n<br />\r\n&quot;Cao năng dự cảnh cao năng dự cảnh cao năng dự cảnh cao năng dự cảnh!&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y kh&ocirc;ng phải liền l&agrave; trực tiếp đem khủng bố đại sư h&ugrave; đến ph&aacute; ph&ograve;ng c&aacute;i kia linh dị sao!&quot;<br />\r\n<br />\r\n&quot;Ngọa t&agrave;o thật sự c&oacute;!&quot;<br />\r\n<br />\r\n&quot;up chạy mau a! !&quot;<br />\r\n<br />\r\nNgay tại to&agrave;n bộ m&agrave;n h&igrave;nh đều bị mưa đạn hộ thể che đậy k&iacute;n l&uacute;c.<br />\r\n<br />\r\nCỗ thi thể kia to&agrave;n bộ &aacute;nh mắt, ngay tại L&acirc;m Mục C&aacute;p nh&igrave;n chăm ch&uacute; ph&iacute;a dưới từ trong hốc mắt mang theo thịt vụn c&ugrave;ng m&aacute;u chảy sướng trượt xuống.<br />\r\n<br />\r\nLạch cạch một lần quẳng xuống đất, ki&ecirc;n tr&igrave; một gi&acirc;y sau trực tiếp giống như l&agrave; thạch quả một dạng vỡ vụn.<br />\r\n<br />\r\nNgay sau đ&oacute;, n&oacute; kia trống rỗng trong hốc mắt lại lấy tốc độ m&agrave; mắt thường cũng c&oacute; thể thấy được sinh ra một c&aacute;i &aacute;nh mắt.<br />\r\n<br />\r\nSau đ&oacute; giống như l&agrave; v&ocirc; ph&aacute;p cố định trụ b&igrave;nh thường lần nữa trượt xuống quẳng xuống đất.<br />\r\n<br />\r\nMột vi&ecirc;n tiếp lấy một vi&ecirc;n.<br />\r\n<br />\r\n&quot;L&agrave;m g&igrave; chứ chạy mau a!&quot;<br />\r\n<br />\r\n&quot;up sẽ kh&ocirc;ng bị sợ cho&aacute;ng v&aacute;ng đi!&quot;<br />\r\n<br />\r\n&quot;Mau b&aacute;o cảnh s&aacute;t! Một hồi up bị ăn rồi!&quot;<br />\r\n<br />\r\n&quot;Ai xo&aacute;t c&aacute;i chiến hạm nhắc nhở một ch&uacute;t up a!&quot;<br />\r\n<br />\r\n&quot;Qu&aacute; dọa người rồi!&quot;<br />\r\n<br />\r\nCamera tại L&acirc;m Mục C&aacute;p sự khống chế vững v&agrave;ng nhắm ngay trước người hắn linh dị.<br />\r\n<br />\r\nTo&agrave;n bộ k&ecirc;nh trực tiếp phản hồi b&igrave;nh phong nh&aacute;y mắt liền bị c&aacute;c loại mưa đạn bao phủ.<br />\r\n<br />\r\nNh&acirc;n số vậy hết lần n&agrave;y đến lần kh&aacute;c hạ xuống lại tăng th&ecirc;m.<br />\r\n<br />\r\nKhoảng c&aacute;ch gần như vậy nh&igrave;n linh dị, mặc d&ugrave; l&agrave; trực tiếp nhưng l&agrave; đem đại đa số người dọa đến ngao một tiếng, huống chi L&acirc;m Mục C&aacute;p camera cầm được vững v&ocirc; c&ugrave;ng.<br />\r\n<br />\r\n&quot;Huynh đệ, xin hỏi. . . Ngươi chết đ&atilde; bao nhi&ecirc;u năm?&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p nh&igrave;n xuống đất b&ecirc;n tr&ecirc;n &aacute;nh mắt rơi xuống đất sinh ra thịt vụn trầm mặc v&agrave;i gi&acirc;y đồng hồ đằng sau mang mỉm cười th&acirc;n mật hỏi.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; chuyện, c&ugrave;ng ta n&oacute;i một ch&uacute;t, ta l&agrave; người tốt a!&quot;<br />\r\n<br />\r\nHắn mở ra tay một mặt v&ocirc; tội n&oacute;i đến.<br />\r\n<br />\r\n&quot; &quot;<br />\r\n<br />\r\n&quot;Uploader đang hỏi linh dị chết đ&atilde; bao nhi&ecirc;u năm? ?&quot;<br />\r\n<br />\r\n&quot;up sẽ kh&ocirc;ng cũng l&agrave; linh dị a?&quot;<br />\r\n<br />\r\n&quot;Chạy mau a c&ograve;n đứng ng&acirc;y đ&oacute; l&agrave;m g&igrave;? ?&quot;<br />\r\n<br />\r\n&quot;up sẽ kh&ocirc;ng đ&atilde; bị sợ cho&aacute;ng v&aacute;ng a?&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p kh&ocirc;ng phải đến kh&ocirc;ng c&oacute; chạy thậm ch&iacute; c&ograve;n chủ động c&ugrave;ng linh dị đ&aacute;p lời phản ứng để k&ecirc;nh trực tiếp b&ecirc;n trong v&ocirc; số dấu chấm hỏi xo&aacute;t tới.<br />\r\n<br />\r\nTo&agrave;n bộ trực tiếp nhiệt độ nh&aacute;y mắt bạo tăng.<br />\r\n<br />\r\nKh&ocirc;ng chỉ l&agrave; k&ecirc;nh trực tiếp, đối diện linh dị vậy mộng bức g&atilde;i g&atilde;i đầu.<br />\r\n<br />\r\n&quot;Tới tới tới! Ngồi tr&ograve; chuyện một hồi.&quot;<br />\r\n<br />\r\nVừa vặn trong ph&ograve;ng c&oacute; hai c&aacute;i ghế.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p thổi thổi ph&iacute;a tr&ecirc;n phủ bụi, sau đ&oacute; lấy một c&aacute;i một đối một thăm hỏi tiết mục d&aacute;ng vẻ cố định lại camera n&oacute;i đến.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; chuyện kh&ocirc;ng c&oacute; chuyện, ta kh&ocirc;ng ăn, ngươi ăn đi!&quot;<br />\r\n<br />\r\nHắn nh&igrave;n thấy cỗ n&agrave;y hiếu kh&aacute;ch thi thể c&ograve;n ngồi xổm người xuống n&acirc;ng l&ecirc;n n&oacute; &aacute;nh mắt rơi quẳng th&agrave;nh n&aacute;t mạt đưa cho bản th&acirc;n, vội v&agrave;ng kho&aacute;t tay &aacute;o.<br />\r\n<br />\r\n&quot;Nguy&ecirc;n lai ngươi vừa mới l&agrave; ở nấu cơm a, ta c&ograve;n tưởng rằng ngươi ng&atilde; bệnh.&quot;<br />\r\n<br />\r\nThi thể &aacute;nh mắt kh&ocirc;ng c&ograve;n rơi địa.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p vậy bừng tỉnh đại ngộ.<br />\r\n<br />\r\n&quot;Nh&igrave;n xem, nh&acirc;n gia c&ograve;n khoản đ&atilde;i ta mời ta ăn bữa ăn khuya đ&acirc;u!&quot;<br />\r\n<br />\r\nHắn hướng về ph&iacute;a camera n&oacute;i đến.<br />\r\n<br />\r\n&quot;Đến cấp ngươi một ch&uacute;t m&aacute;u.&quot;<br />\r\n<br />\r\nTrong l&ograve;ng rất cảm thấy ấm &aacute;p L&acirc;m Mục C&aacute;p lại từ trong t&uacute;i x&aacute;ch lấy ra một c&aacute;i ch&eacute;n giấy, sau đ&oacute; tiếp nửa ch&eacute;n nước đưa tới.<br />\r\n<br />\r\n&quot;Ngọa t&agrave;o ta đến c&ugrave;ng đang nh&igrave;n c&aacute;i g&igrave; trực tiếp?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải, linh dị đến c&ugrave;ng c&oacute; tồn tại hay kh&ocirc;ng a?&quot;<br />\r\n<br />\r\n&quot;Đương nhi&ecirc;n tồn tại a! Bằng kh&ocirc;ng dẫn chương tr&igrave;nh ngồi đối diện ch&iacute;nh l&agrave; c&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\n&quot;X&aacute;c định đ&acirc;y kh&ocirc;ng phải l&agrave; người diễn sao?&quot;<br />\r\n<br />\r\n&quot;&laquo; bữa ăn khuya &raquo; &quot;<br />\r\n<br />\r\n&quot;V&igrave; c&aacute;i g&igrave; up c&oacute; thể l&agrave;m đến b&igrave;nh tĩnh như vậy a? ?&quot;<br />\r\n<br />\r\n&quot;Ta vừa mới bị dọa đến trực tiếp rời khỏi k&ecirc;nh trực tiếp. &quot;<br />\r\n<br />\r\nL&uacute;c n&agrave;y to&agrave;n bộ trực tiếp h&igrave;nh tượng đều b&agrave;y biện ra một loại quỷ dị kết cấu, cho người ta t&acirc;m hồn tạo th&agrave;nh một loại cực lớn xung k&iacute;ch.<br />\r\n<br />\r\nCứ việc linh dị liền b&agrave;y ở trước mắt.<br />\r\n<br />\r\nNhưng tuyệt đại đa số người vẫn c&ograve;n c&oacute; ch&uacute;t kh&oacute; c&oacute; thể tin kh&oacute; m&agrave; tiếp nhận.<br />\r\n<br />\r\n&quot;Ngươi chết đ&atilde; bao nhi&ecirc;u năm?&quot;<br />\r\n<br />\r\n&quot;Ngao ngao.&quot;<br />\r\n<br />\r\n&quot;Ngươi t&ecirc;n l&agrave; g&igrave;?&quot;<br />\r\n<br />\r\n&quot;Ngao ngao a!&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p nh&igrave;n thấy trước mặt linh dị ăn đến kh&ocirc;ng sai biệt lắm, rồi c&ugrave;ng hắn rất tự nhi&ecirc;n trao đổi.<br />\r\n<br />\r\nCứ việc đối phương ph&aacute;t &acirc;m kh&ocirc;ng phải rất ti&ecirc;u chuẩn, nhưng L&acirc;m Mục C&aacute;p đời trước d&ugrave; sao cũng l&agrave; chuyện ma quỷ cấp s&aacute;u tr&igrave;nh độ, phi&ecirc;n dịch căn bản kh&ocirc;ng c&oacute; c&aacute;i g&igrave; chướng ngại.<br />\r\n<br />\r\n&quot;C&aacute;c vị, hắn n&oacute;i hắn gọi Tiểu Bạch, đ&atilde; chết bốn năm mươi năm, cụ thể hắn vậy kh&ocirc;ng nhớ r&otilde;.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p nh&igrave;n xem camera rất nghi&ecirc;m t&uacute;c n&oacute;i đến.<br />\r\n<br />\r\n&quot;M&agrave; lại Tiểu Bạch vốn l&agrave; rất hướng ngoại, khủng bố đại sư lần trước tới thời điểm hắn l&agrave;m người mời kh&aacute;ch, c&ograve;n chủ động kh&oacute;a cửa lại, cũng đem tay kho&aacute;c l&ecirc;n khủng bố đại sư tr&ecirc;n th&acirc;n ch&agrave;o hỏi hắn.&quot;<br />\r\n<br />\r\n&quot;Thậm ch&iacute; c&ograve;n chủ động nhường cho m&igrave;nh đầu rớt xuống, d&ugrave;ng đầu của m&igrave;nh đi khoản đ&atilde;i khủng bố đại sư.&quot;<br />\r\n<br />\r\n&quot;Nhưng cũng tiếc, khủng bố đại sư trực tiếp liền bị h&ugrave; chạy, thương thế kia Tiểu Bạch t&acirc;m, sở dĩ lần n&agrave;y hắn nh&igrave;n thấy ta đến mới trốn đi.&quot;<br />\r\n<br />\r\nHắn s&acirc;u k&iacute;n thở d&agrave;i.<br />\r\n<br />\r\nLinh dị một ch&uacute;t cử động, kỳ thật đều l&agrave; rất đơn thuần.<br />\r\n<br />\r\nKhủng bố đại sư tới đ&acirc;y Tiểu Bạch một ch&uacute;t đều kh&ocirc;ng nghĩ dọa hắn, ch&iacute;nh l&agrave; đơn thuần nghĩ khoản đ&atilde;i hắn m&agrave; th&ocirc;i.<br />\r\n<br />\r\nNhiều hiếu kh&aacute;ch a!</p>', '2021-11-28 16:04:12', 6, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(228, 60, 'Hắn làm sao dám a?', '4', '<p>&quot;? ? ? up c&oacute; thể c&ugrave;ng linh dị giao lưu?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải, đ&acirc;y rốt cuộc l&agrave; c&aacute;i g&igrave; trực tiếp a?&quot;<br />\r\n<br />\r\n&quot;C&aacute;i kia Tiểu Bạch. . . Thật l&agrave; linh dị sao?&quot;<br />\r\n<br />\r\n&quot;up l&agrave;m sao cảm gi&aacute;c sẽ được nhiều như vậy a, hơn nữa nh&igrave;n đến linh dị v&igrave; c&aacute;i g&igrave; một ch&uacute;t c&ograve;n kh&ocirc;ng sợ a?&quot;<br />\r\n<br />\r\n&quot;Sở dĩ linh dị nhưng thật ra l&agrave; th&acirc;n mật?&quot;<br />\r\n<br />\r\n&quot;B&igrave;nh thường dẫn chương tr&igrave;nh hiện tại đ&atilde; bị dọa đến tại nằm bệnh viện đi?&quot;<br />\r\n<br />\r\nL&uacute;c n&agrave;y th&agrave;nh Nam trung t&acirc;m bệnh viện 404 trong ph&ograve;ng bệnh.<br />\r\n<br />\r\nCh&iacute;nh nh&igrave;n trực tiếp khủng bố đại sư gương mặt kh&oacute; c&oacute; thể tin c&ugrave;ng bản th&acirc;n ho&agrave;i nghi.<br />\r\n<br />\r\nNgồi ở L&acirc;m Mục C&aacute;p đối diện linh dị hắn có th&ecirc;̉ qu&aacute; r&otilde;.<br />\r\n<br />\r\nTrực tiếp ng&agrave;y đ&oacute; hắn mới vừa v&agrave;o đến liền nắm tay kho&aacute;c l&ecirc;n tr&ecirc;n vai của hắn, m&agrave; lại đầu c&ograve;n rớt.<br />\r\n<br />\r\nKinh khủng nhất l&agrave; kia rơi mất đầu sẽ c&ograve;n lấy tốc độ m&agrave; mắt thường cũng c&oacute; thể thấy được mọc ra.<br />\r\n<br />\r\nChỉ như vậy một c&aacute;i l&agrave; đủ đem sở hữu người b&igrave;nh thường dọa đến hồn phi ph&aacute;ch t&aacute;n linh dị, L&acirc;m Mục C&aacute;p v&acirc;̣y mà có th&ecirc;̉ b&igrave;nh tĩnh như vậy thậm ch&iacute;. . . Th&acirc;n mật đối mặt?<br />\r\n<br />\r\nC&ograve;n giải đọc ra c&aacute;i n&agrave;y linh dị nhưng thật ra l&agrave; nghĩ khoản đ&atilde;i hắn?<br />\r\n<br />\r\nA?<br />\r\n<br />\r\nV&igrave; sao a?<br />\r\n<br />\r\nChạy kh&ocirc;ng phải người b&igrave;nh thường cử động sao?<br />\r\n<br />\r\nVừa nghĩ tới m&igrave;nh l&agrave;m th&igrave; bị dọa đến trực tiếp từ lầu hai ph&aacute; cửa sổ nhảy chịu đựng g&atilde;y xương đau đớn chạy như đi&ecirc;n hai c&acirc;y số mới chậm tới.<br />\r\n<br />\r\nKhủng bố đại sư tr&ecirc;n mặt kh&oacute; hiểu th&igrave; c&agrave;ng nhiều hơn mấy phần.<br />\r\n<br />\r\nC&aacute;i n&agrave;y L&acirc;m Mục C&aacute;p. . .<br />\r\n<br />\r\nL&agrave;m sao l&agrave;m được một ch&uacute;t c&ograve;n kh&ocirc;ng sợ c&ograve;n c&oacute; thể b&igrave;nh thường c&ugrave;ng linh dị đ&aacute;p lời a?<br />\r\n<br />\r\nCho d&ugrave; l&agrave; b&acirc;y giờ nh&igrave;n lấy trực tiếp, khủng bố đại sư trong l&ograve;ng đều duang duang sợ h&atilde;i.<br />\r\n<br />\r\nKh&ocirc;ng chỉ l&agrave; hắn.<br />\r\n<br />\r\nK&ecirc;nh trực tiếp b&ecirc;n trong tất cả mọi người thậm ch&iacute; ngay cả Tiểu Bạch trong l&ograve;ng đều c&oacute; đồng dạng nghi vấn.<br />\r\n<br />\r\n&quot;Minh bạch.&quot;<br />\r\n<br />\r\nL&uacute;c n&agrave;y L&acirc;m Mục C&aacute;p c&ograve;n kh&ocirc;ng biết cử động của hắn đ&atilde; nhấc l&ecirc;n s&oacute;ng to gi&oacute; lớn.<br />\r\n<br />\r\nC&ugrave;ng Tiểu Bạch lảm nhảm năm ph&uacute;t sau, hắn cau m&agrave;y nhẹ gật đầu, một lần nữa nh&igrave;n về ph&iacute;a camera.<br />\r\n<br />\r\n&quot;C&aacute;c vị, Tiểu Bạch ch&iacute;nh l&agrave; trong truyền thuyết bệnh viện t&acirc;m thần tầng hầm ngầm nu&ocirc;i cương thi, nhưng sự thật cũng kh&ocirc;ng phải l&agrave; giống như l&agrave; trong truyền thuyết như thế.&quot;<br />\r\n<br />\r\n&quot;Đại gia cũng c&oacute; thể nh&igrave;n thấy, Tiểu Bạch d&aacute;ng vẻ c&ugrave;ng phim truyền h&igrave;nh b&ecirc;n trong th&acirc;n thể cứng đờ cương thi cũng kh&ocirc;ng giống nhau.&quot;<br />\r\n<br />\r\n&quot;Bởi v&igrave; ta l&agrave; một t&ecirc;n phổ cập khoa học khu uploader, chủ yếu phổ cập khoa học linh dị sinh vật, sở dĩ ở đ&acirc;y trước n&oacute;i cho đại gia một c&aacute;i nhỏ tri thức.&quot;<br />\r\n<br />\r\n&quot;Cương thi thi độc, theo một &yacute; nghĩa n&agrave;o đ&oacute; c&ugrave;ng ong mật độc ch&acirc;m đồng dạng.&quot;<br />\r\n<br />\r\n&quot;Ong mật mất đi độc ch&acirc;m sẽ chết đi, cương thi mất đi to&agrave;n bộ thi độc về sau, liền sẽ biến th&agrave;nh Tiểu Bạch c&aacute;i n&agrave;y dạng, th&acirc;n thể cơ hồ kh&ocirc;ng c&oacute; một ch&uacute;t x&iacute;u năng lực ph&ograve;ng ngự chỉ c&ograve;n lại năng lực t&aacute;i sinh, v&ocirc; c&ugrave;ng yếu ớt.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y bệnh viện t&acirc;m thần ch&iacute;nh l&agrave; lợi dụng Tiểu Bạch thi độc đi &#39;Trị liệu&#39; người bị bệnh t&acirc;m thần.&quot;<br />\r\n<br />\r\n&quot;Sở dĩ Tiểu Bạch c&ugrave;ng những c&aacute;i kia bệnh t&acirc;m thần người bệnh, c&ugrave;ng người bị bệnh t&acirc;m thần người bệnh người nh&agrave; một dạng đều l&agrave; người bị hại, ch&acirc;n ch&iacute;nh kẻ thu lợi chỉ c&oacute; nh&agrave; tư bản.&quot;<br />\r\n<br />\r\n&quot;Tr&ecirc;n thế giới n&agrave;y, nh&agrave; tư bản xa xa muốn so linh dị c&agrave;ng đ&aacute;ng sợ.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p h&iacute;t thật s&acirc;u một hơi mang theo huyết tinh c&ugrave;ng thịt thối m&ugrave;i vị kh&ocirc;ng kh&iacute;.<br />\r\n<br />\r\nTiểu Bạch mặc d&ugrave; coi như rất khủng bố, nhưng vẫn l&agrave; rất đ&aacute;ng y&ecirc;u.<br />\r\n<br />\r\n&quot;Nh&igrave;n xem Tiểu Bạch ăn được nhiều hương a, c&oacute; l&agrave;m blogger ẩm thực tiềm chất a, nếu l&agrave; linh dị đều hướng hắn c&aacute;i n&agrave;y dạng c&oacute; tốt khẩu vị tốt bao nhi&ecirc;u.&quot;<br />\r\n<br />\r\nHắn cảm th&aacute;n đến.<br />\r\n<br />\r\nM&agrave; Tiểu Bạch trắng bệch hư thối đến kh&ocirc;ng c&oacute; t&iacute; x&iacute;u huyết sắc tr&ecirc;n mặt vậy lộ ra một tia nụ cười dữ tợn.<br />\r\n<br />\r\nTại camera b&ecirc;n dưới.<br />\r\n<br />\r\nHắn nhấp miệng m&aacute;u, ăn miệng thịt vụn.<br />\r\n<br />\r\nKh&ocirc;ng để &yacute; ch&uacute;t n&agrave;o c&ugrave;ng h&igrave;nh tượng của m&igrave;nh.<br />\r\n<br />\r\nThậm ch&iacute; lại giơ tay l&ecirc;n trực tiếp cắm v&agrave;o đầu của m&igrave;nh b&ecirc;n trong cầm ra một đo&agrave;n n&atilde;o nh&acirc;n nhi miệng lớn nhai nuốt lấy.<br />\r\n<br />\r\nĂn đến phi thường hương.<br />\r\n<br />\r\n&quot;&laquo; tốt khẩu vị &raquo; &quot;<br />\r\n<br />\r\n&quot;&laquo; c&oacute; l&agrave;m blogger ẩm thực tiềm chất &raquo; &quot;<br />\r\n<br />\r\n&quot;Ta kh&ocirc;ng thể n&agrave;o tiếp thu được.&quot;<br />\r\n<br />\r\n&quot;V&ocirc; dụng tri thức c&oacute; gia tăng rồi đ&acirc;u.&quot;<br />\r\n<br />\r\n&quot;Sở dĩ đ&acirc;y con mẹ n&oacute; cũng thật l&agrave; c&aacute;i phổ cập khoa học k&ecirc;nh trực tiếp?&quot;<br />\r\n<br />\r\n&quot;Ta từ trang đầu đến liền cho ta xem c&aacute;i n&agrave;y? Y&ecirc;u y&ecirc;u.&quot;<br />\r\n<br />\r\n&quot;C&aacute;c huynh đệ đ&acirc;y đều l&agrave; khai vị dưa cải,<br />\r\n<br />\r\nMột hồi dẫn chương tr&igrave;nh tự m&igrave;nh mới l&agrave; m&oacute;n ngon.&quot;<br />\r\n<br />\r\nTrong bất tri bất gi&aacute;c, k&ecirc;nh trực tiếp nh&acirc;n số đ&atilde; đột ph&aacute; bốn mươi vạn hướng ph&iacute;a năm mươi vạn đại quan rảo bước tiến l&ecirc;n.<br />\r\n<br />\r\nCứ việc mưa đạn b&ecirc;n tr&ecirc;n c&aacute;c vị vẫn như cũ c&oacute; nghi vấn đầy bụng nhưng kh&ocirc;ng kh&iacute; vậy so ban đầu bu&ocirc;ng lỏng rất nhiều.<br />\r\n<br />\r\nĐ&acirc;y hết thảy tựa hồ giống như lại c&oacute; ch&uacute;t nhi hợp l&yacute;. . .<br />\r\n<br />\r\nTiểu ph&aacute; trạm b&ecirc;n trong c&oacute; phổ cập khoa học nh&acirc;n vật lịch sử, c&oacute; phổ cập khoa học v&otilde;ng hồng vật dụng, c&ograve;n c&oacute; gi&aacute;m định internet hấp dẫn sinh vật.<br />\r\n<br />\r\nGiống như l&agrave; L&acirc;m Mục C&aacute;p c&aacute;i n&agrave;y dạng phổ cập khoa học linh dị , vẫn l&agrave; đầu một c&aacute;i.<br />\r\n<br />\r\nM&agrave; lại đối mặt linh dị, b&igrave;nh thường up c&aacute;i n&agrave;o kh&ocirc;ng phải quay đầu liền chạy?<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p c&aacute;i n&agrave;y dạng xem ra đặc biệt chuy&ecirc;n nghiệp bộ d&aacute;ng c&ograve;n gi&uacute;p hắn h&uacute;t một đợt phấn.<br />\r\n<br />\r\n&quot;Đến, chụp tấm h&igrave;nh đi.&quot;<br />\r\n<br />\r\nChờ Tiểu Bạch ăn uống no đủ.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p trực tiếp nhiệt t&igrave;nh &ocirc;m chầm ngượng ng&ugrave;ng Tiểu Bạch.<br />\r\n<br />\r\n&quot;Ư!&quot;<br />\r\n<br />\r\nTại hơn bốn trăm ng&agrave;n người nh&igrave;n chăm ch&uacute;.<br />\r\n<br />\r\nHắn so c&aacute;i c&aacute;i k&eacute;o tay, c&ugrave;ng Tiểu Bạch một đợt lộ ra một c&aacute;i mỉm cười rực rỡ, lưu lại thế giới n&agrave;y tờ thứ nhất nh&acirc;n loại c&ugrave;ng linh dị kề vai s&aacute;t c&aacute;nh tr&acirc;n qu&yacute; chụp ảnh chung.<br />\r\n<br />\r\n&quot;Căn cứ khủng bố đại sư video c&ugrave;ng th&agrave;nh Nam linh dị truyền thuyết, c&aacute;i n&agrave;y bệnh viện t&acirc;m thần b&ecirc;n trong ch&iacute; &iacute;t c&ograve;n c&oacute; ba loại linh dị.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y bệnh viện t&acirc;m thần chợt nh&igrave;n thật đ&uacute;ng l&agrave; rất giống kia chuyện.&quot;<br />\r\n<br />\r\nHắn c&aacute;o biệt lưu luyến kh&ocirc;ng rời Tiểu Bạch sau giơ camera leo l&ecirc;n l&acirc;u, sải bước đi ở trong h&agrave;nh lang.<br />\r\n<br />\r\nThỉnh thoảng trả cho mọi người xem một lần h&agrave;nh lang hai b&ecirc;n gian ph&ograve;ng kết cấu.<br />\r\n<br />\r\nMỗi c&aacute;i gian ph&ograve;ng trang tr&iacute; sửa chữa lại c&ograve;n thật thật kh&ocirc;ng tệ, thật c&oacute; thể h&ugrave; dọa người.<br />\r\n<br />\r\n&quot;Ừm?&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p đột nhi&ecirc;n dừng bước.<br />\r\n<br />\r\nHắn tiến về ph&iacute;a trước một bước, lại lui về sau một bước.<br />\r\n<br />\r\nSau đ&oacute; đem trong tay t&uacute;i m&aacute;u miệng t&uacute;i mở ra.<br />\r\n<br />\r\n&quot;Mọi người xem hiện tại ta đ&acirc;y c&aacute;i t&iacute;n hiệu.&quot;<br />\r\n<br />\r\nHắn lấy điện thoại di động ra đặt ở camera trước.<br />\r\n<br />\r\nTiến về ph&iacute;a trước một bước, đợi v&agrave;i gi&acirc;y đồng hồ vốn l&agrave; 6G liền biến th&agrave;nh 5G.<br />\r\n<br />\r\n&quot;Th&ocirc;ng qua nh&igrave;n t&iacute;n hiệu của điện thoại di động, cũng l&agrave; ph&aacute;n đo&aacute;n ph&iacute;a trước c&oacute; kh&ocirc;ng c&oacute; linh dị phương ph&aacute;p tốt.&quot;<br />\r\n<br />\r\n&quot;Nếu như x&aacute;c định ph&iacute;a trước c&oacute; linh dị, đại gia phải tất yếu chuẩn bị kỹ c&agrave;ng ph&ograve;ng th&acirc;n vật phẩm.&quot;<br />\r\n<br />\r\n&quot;Đừng nh&igrave;n ta tay kh&ocirc;ng, nhưng ta trong đầu c&oacute; tri thức, kh&ocirc;ng sợ.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p cầm điện thoại thận trọng đi thẳng về ph&iacute;a trước.<br />\r\n<br />\r\n&quot;&laquo; tay kh&ocirc;ng tấc sắt &raquo;(chỉ cầm trong tay điện thoại c&ugrave;ng camera, trong bọc c&ograve;n cắm c&aacute;i c&aacute;i xẻng) &quot;<br />\r\n<br />\r\n&quot;Ta nhớ được khủng bố đại sư video đằng sau c&oacute; c&aacute;i kia tấm gương, up sẽ kh&ocirc;ng cần đi t&igrave;m tấm gương a? ?&quot;<br />\r\n<br />\r\n&quot;C&aacute;i kia tấm gương l&agrave; thật đ&aacute;ng sợ, ta kh&ocirc;ng ra mưa đạn căn bản kh&ocirc;ng d&aacute;m nh&igrave;n.&quot;<br />\r\n<br />\r\n&quot;up thật kh&ocirc;ng phải l&agrave; c&aacute;i g&igrave;. . . H&igrave;nh người linh dị sao?&quot;<br />\r\n<br />\r\n&quot;Ta vẫn l&agrave; kh&ocirc;ng hiểu r&otilde; v&igrave; c&aacute;i g&igrave; up một ch&uacute;t c&ograve;n kh&ocirc;ng sợ.&quot;<br />\r\n<br />\r\n&quot;Ta hiện tại thật l&agrave; sợ vừa mới c&aacute;i kia linh dị đuổi theo a.&quot;<br />\r\n<br />\r\nTrong bất tri bất gi&aacute;c, L&acirc;m Mục C&aacute;p k&ecirc;nh trực tiếp nh&acirc;n số đ&atilde; c&oacute; bảy mươi vạn.<br />\r\n<br />\r\nVượt qua xa hắn fan h&acirc;m mộ đếm.<br />\r\n<br />\r\nMặc d&ugrave; từ mưa đạn đến xem, mọi người đối với linh dị tồn tại c&ugrave;ng linh dị t&iacute;nh bản thiện vẫn c&ograve;n c&oacute; ch&uacute;t kh&ocirc;ng thể n&agrave;o tiếp thu được.<br />\r\n<br />\r\nNhưng lần thứ nhất bị nhiều như vậy người ch&uacute; &yacute;, L&acirc;m Mục C&aacute;p trong l&ograve;ng vẫn l&agrave; rất vui mừng.<br />\r\n<br />\r\n&quot;Ở đ&acirc;y.&quot;<br />\r\n<br />\r\nHắn dừng ở cuối h&agrave;nh lang một c&aacute;i ph&ograve;ng trước cửa.<br />\r\n<br />\r\nĐầu ti&ecirc;n l&agrave; g&otilde; cửa một c&aacute;i, đợi hai gi&acirc;y kh&ocirc;ng c&oacute; quỷ đ&aacute;p lại sau hắn mới rất lễ ph&eacute;p nhẹ nh&agrave;ng đẩy cửa ra.<br />\r\n<br />\r\n&quot;Ngươi tốt? Moshi moshi? Hello ?&quot;<br />\r\n<br />\r\nĐẩy cửa ra một đường nhỏ thăm d&ograve; qua đầu. Nh&igrave;n xem trống kh&ocirc;ng một quỷ gian ph&ograve;ng, L&acirc;m Mục C&aacute;p nheo lại mắt.<br />\r\n<br />\r\n&quot;T&igrave;m được.&quot;<br />\r\n<br />\r\nHắn đi v&agrave;o gian ph&ograve;ng cầm camera đứng ở một c&aacute;i gương to&agrave;n th&acirc;n ph&iacute;a trước.<br />\r\n<br />\r\nMặc d&ugrave; ánh trăng tại m&acirc;y đen b&ecirc;n dưới như ẩn như hiện.<br />\r\n<br />\r\nNhưng l&agrave; l&agrave; đủ đem điều n&agrave;y ph&ograve;ng nhỏ chiếu s&aacute;ng.<br />\r\n<br />\r\nMột c&aacute;i trống rỗng ph&ograve;ng nhỏ.<br />\r\n<br />\r\nMột c&aacute;i đột ngột gương to&agrave;n th&acirc;n.<br />\r\n<br />\r\nNgay tại mặt k&iacute;nh sở đối tr&ecirc;n tường, thậm ch&iacute; c&ograve;n lưu lại khủng bố đại sư gi&atilde;y dụa qua mồ h&ocirc;i lạnh vết t&iacute;ch.<br />\r\n<br />\r\n&quot;Ngọa t&agrave;o up đến thật sự.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y coi như xong đi, vừa mới c&aacute;i kia linh dị ch&iacute; &iacute;t c&oacute; thực thể, c&aacute;i n&agrave;y tấm gương căn bản kh&ocirc;ng c&oacute; thực thể a!&quot;<br />\r\n<br />\r\n&quot;Ta thật l&agrave; sợ K&iacute;nh Tượng đột nhi&ecirc;n liền biến th&agrave;nh quỷ.&quot;<br />\r\n<br />\r\n&quot;Mưa đạn hộ thể mưa đạn hộ thể mưa đạn hộ thể.&quot;<br />\r\n<br />\r\n&quot;What&#39; s up!&quot;<br />\r\n<br />\r\nTr&ecirc;n giường bệnh khủng bố đại sư nh&igrave;n thấy L&acirc;m Mục C&aacute;p chủ động đi v&agrave;o gian ph&ograve;ng n&agrave;y đối mặt tấm gương.<br />\r\n<br />\r\nTrực tiếp mở ra mưa đạn.<br />\r\n<br />\r\nNh&igrave;n xem rậm rạp chằng chịt mưa đạn hắn mới cảm thấy trận trận trận an t&acirc;m.<br />\r\n<br />\r\nD&ugrave; l&agrave; đ&atilde; qua nhiều ng&agrave;y như vậy.<br />\r\n<br />\r\nHắn ban đ&ecirc;m đi ngủ vẫn như cũ sẽ nghĩ tới trong k&iacute;nh bản th&acirc;n chết thảm bộ d&aacute;ng, sau đ&oacute; người đổ mồ h&ocirc;i lạnh.<br />\r\n<br />\r\n&quot;Hắn l&agrave;m sao d&aacute;m a. . .&quot;<br />\r\n<br />\r\nNh&igrave;n xem L&acirc;m Mục C&aacute;p, khủng bố đại sư d&ugrave;ng băng b&oacute; thạch cao tay kh&oacute; hiểu g&atilde;i g&atilde;i đầu.</p>', '2021-11-28 16:04:35', 5, 1),
(229, 60, 'Linh dị: Ngươi lễ phép sao?', '5', '<p>&quot;Trong k&iacute;nh quỷ phổ biến so s&aacute;nh xấu hổ, nhưng l&agrave; c&oacute; c&aacute;i nhược điểm, ch&iacute;nh l&agrave; th&iacute;ch bắt chước người kh&aacute;c, sau đ&oacute; c&ograve;n sợ xấu hổ.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p đem camera bu&ocirc;ng xuống, sau đ&oacute; nh&igrave;n m&igrave;nh trong k&iacute;nh n&oacute;i đến.<br />\r\n<br />\r\n&quot;B&igrave;nh thường muốn thấy một lần trong k&iacute;nh quỷ ch&acirc;n dung, ch&uacute;ng ta chỉ cần lợi dụng hai điểm n&agrave;y.&quot;<br />\r\n<br />\r\n&quot;L&agrave;m ra một c&aacute;i trong k&iacute;nh quỷ bắt chước kh&ocirc;ng ra được động t&aacute;c, sau đ&oacute; n&oacute; liền xấu hổ, một xấu hổ liền ra tới đem người dọa chạy.&quot;<br />\r\n<br />\r\n&quot;Đặc biệt đ&aacute;ng y&ecirc;u tiểu gia hỏa.&quot;<br />\r\n<br />\r\nHắn g&otilde; g&otilde; tấm gương cười n&oacute;i đến.<br />\r\n<br />\r\n&quot;&laquo; đặc biệt đ&aacute;ng y&ecirc;u tiểu gia hỏa &raquo; &quot;<br />\r\n<br />\r\n&quot;Ta kh&ocirc;ng muốn thấy trong k&iacute;nh quỷ ch&acirc;n dung a!&quot;<br />\r\n<br />\r\n&quot;Cứu mạng, v&igrave; c&aacute;i g&igrave; ta lại sợ lại muốn nh&igrave;n.&quot;<br />\r\n<br />\r\n&quot;up thật kh&ocirc;ng muốn mệnh sao?&quot;<br />\r\n<br />\r\n&quot;up biết m&igrave;nh đang l&agrave;m g&igrave; sao?&quot;<br />\r\n<br />\r\n&quot;L&agrave;m sao cảm gi&aacute;c up n&oacute;i bậy giống như rất c&oacute; đạo l&yacute; một dạng?&quot;<br />\r\n<br />\r\n&quot;Chẳng lẽ up giống như l&agrave; đi&ecirc;n chưa ph&aacute;t gi&aacute;c một dạng kh&ocirc;ng cảm gi&aacute;c được sợ h&atilde;i?&quot;<br />\r\n<br />\r\nNgay tại mưa đạn c&agrave;ng th&ecirc;m lửa n&oacute;ng thời điểm.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p đầu ti&ecirc;n l&agrave; l&agrave;m mấy c&aacute;i khuếch trương ngực lại đơn giản k&eacute;o duỗi một lần, sau đ&oacute; bẻ bẻ cổ.<br />\r\n<br />\r\nTại k&ecirc;nh trực tiếp hơn s&aacute;u trăm ng&agrave;n người quan s&aacute;t b&ecirc;n dưới.<br />\r\n<br />\r\nHắn v&acirc;̣y mà biểu lộ nghi&ecirc;m t&uacute;c lại thật l&ograve;ng đối tấm gương l&agrave;m cả nước học sinh trung tiểu học tập thể dục theo đ&agrave;i &mdash;&mdash; mới l&ecirc;n Th&aacute;i Dương.<br />\r\n<br />\r\nV&igrave; để cho mọi người xem xem x&eacute;t trong k&iacute;nh quỷ.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p cũng l&agrave; kh&ocirc;ng để &yacute; ch&uacute;t n&agrave;o c&ugrave;ng h&igrave;nh tượng của m&igrave;nh.<br />\r\n<br />\r\nTam quốc thời k&igrave; Lưu Bị v&igrave; thấy Gia C&aacute;t Lượng đều ba lần đến mời.<br />\r\n<br />\r\nHắn v&igrave; gặp một ch&uacute;t linh dị, đơn giản nhảy mấy lần lại coi l&agrave; c&aacute;i g&igrave;?<br />\r\n<br />\r\n&quot;? ? ? ?&quot;<br />\r\n<br />\r\n&quot;Trong k&iacute;nh quỷ l&uacute;ng t&uacute;ng kh&oacute; xử kh&ocirc;ng xấu hổ ta kh&ocirc;ng biết, nhưng l&agrave; ta cảm gi&aacute;c dẫn chương tr&igrave;nh hẳn l&agrave; rất l&uacute;ng t&uacute;ng.&quot;<br />\r\n<br />\r\n&quot;Xin hỏi up ng&agrave;i tr&ecirc;n c&aacute;i tinh cầu n&agrave;y thật kh&ocirc;ng c&oacute; bất luận c&aacute;i g&igrave; để &yacute; người sao?&quot;<br />\r\n<br />\r\n&quot;Ta tiến v&agrave;o r&otilde; r&agrave;ng l&agrave; khủng bố k&ecirc;nh trực tiếp a!&quot;<br />\r\n<br />\r\n&quot;Ai c&oacute; thể nh&igrave;n ra đ&acirc;y thật ra l&agrave; nghi&ecirc;m chỉnh phổ cập khoa học k&ecirc;nh trực tiếp [ đầu ch&oacute; bảo mệnh ] &quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải, chưa đi đến k&ecirc;nh trực tiếp trước đ&oacute;, ta cho tới b&acirc;y giờ kh&ocirc;ng nghĩ tới sẽ l&agrave; loại n&agrave;y họa phong.&quot;<br />\r\n<br />\r\n&quot;Nhưng l&agrave; ta vẫn l&agrave; rất sợ h&atilde;i.&quot;<br />\r\n<br />\r\n&quot;Nếu như linh dị thật sự kh&ocirc;ng tồn tại dẫn chương tr&igrave;nh chẳng phải l&agrave; sẽ trong đ&ecirc;m rời đi cái tinh cầu n&agrave;y?&quot;<br />\r\n<br />\r\n&quot;Dẫn chương tr&igrave;nh sẽ trong đ&ecirc;m c&otilde;ng Địa cầu tho&aacute;t đi hệ ng&acirc;n h&agrave;, Lưu lạc địa cầu căn cứ dẫn chương tr&igrave;nh ch&acirc;n thật trải nghiệm cải bi&ecirc;n [ đầu ch&oacute; bảo mệnh ] &quot;<br />\r\n<br />\r\nHo&agrave;n cảnh chung quanh &acirc;m trầm khủng bố, bối cảnh cố sự cũng l&agrave; kinh dị dọa người, thậm ch&iacute; c&ograve;n c&oacute; c&aacute;i mới tới qua up đang ở bệnh viện nằm.<br />\r\n<br />\r\nNhưng ở L&acirc;m Mục C&aacute;p trực tiếp b&ecirc;n dưới, l&agrave;m sao. . .<br />\r\n<br />\r\nQuỷ dị như vậy đ&acirc;u?<br />\r\n<br />\r\n&quot;Mọi người xem!&quot;<br />\r\n<br />\r\nĐơn giản nhảy nửa ph&uacute;t sau.<br />\r\n<br />\r\nẢnh trong gương b&ecirc;n trong L&acirc;m Mục C&aacute;p th&acirc;n h&igrave;nh liền đ&atilde; hết sức r&otilde; r&agrave;ng theo kh&ocirc;ng kịp động t&aacute;c của hắn.<br />\r\n<br />\r\nC&oacute; động t&aacute;c tỉ như nhảy vọt vận động thậm ch&iacute; trực tiếp qua loa bỏ bớt đi.<br />\r\n<br />\r\nKh&ocirc;ng đợi L&acirc;m Mục C&aacute;p lại th&ecirc;m đem lực, mặt k&iacute;nh đột nhi&ecirc;n chấn động một c&aacute;i.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p ảnh trong gương đột nhi&ecirc;n biến mất kh&ocirc;ng thấy g&igrave; nữa.<br />\r\n<br />\r\nTrong mặt gương xuất hiện một người mặc bạch y c&uacute;i đầu nh&igrave;n kh&ocirc;ng thấy bất kỳ biểu lộ g&igrave; tiểu nữ h&agrave;i.<br />\r\n<br />\r\n&quot;Ha ha ha ~ &quot;<br />\r\n<br />\r\nMột trận khiếp người tiếng cười truyền đến.<br />\r\n<br />\r\nÁnh trăng phản xạ b&ecirc;n dưới.<br />\r\n<br />\r\nTrong k&iacute;nh lần nữa nổi l&ecirc;n L&acirc;m Mục C&aacute;p ảnh trong gương.<br />\r\n<br />\r\nChỉ bất qu&aacute; trong k&iacute;nh hắn hai mắt l&agrave; đen nh&aacute;nh.<br />\r\n<br />\r\nCả người cũng kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o sinh kh&iacute;.<br />\r\n<br />\r\nV&ocirc; luận L&acirc;m Mục C&aacute;p l&agrave;m c&aacute;i g&igrave; động t&aacute;c.<br />\r\n<br />\r\nẢnh trong gương cũng kh&ocirc;ng c&oacute; t&iacute; x&iacute;u muốn động &yacute; tứ.<br />\r\n<br />\r\nM&agrave; c&aacute;i kia trong k&iacute;nh tiểu nữ h&agrave;i, th&igrave; c&uacute;i đầu từng bước một hướng ph&iacute;a L&acirc;m Mục C&aacute;p ảnh trong gương đến gần.<br />\r\n<br />\r\n&quot;up đừng l&agrave;m rộn chạy mau đi!&quot;<br />\r\n<br />\r\n&quot;Thật c&oacute; linh dị!&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y kh&ocirc;ng phải liền l&agrave; cắn đứt khủng bố đại sư cổ c&aacute;i kia linh dị sao?&quot;<br />\r\n<br />\r\n&quot;up chạy mau!&quot;<br />\r\n<br />\r\n&quot;Ta sợ up vừa mở cửa ra b&ecirc;n ngo&agrave;i c&aacute;i kia thi thể đang đứng loại kia lấy!&quot;<br />\r\n<br />\r\nMặt k&iacute;nh một trận lắc lư.<br />\r\n<br />\r\nTrong nh&aacute;y mắt, tiểu nữ h&agrave;i liền đ&atilde; nh&agrave;o tới ảnh trong gương b&ecirc;n trong L&acirc;m Mục C&aacute;p tr&ecirc;n th&acirc;n, sau đ&oacute; mở ra miệng to như chậu m&aacute;u hướng ph&iacute;a cổ của hắn t&aacute;p tới.<br />\r\n<br />\r\n&quot;Ư!&quot;<br />\r\n<br />\r\nThấy cảnh n&agrave;y, L&acirc;m Mục C&aacute;p vội v&agrave;ng cầm điện thoại di động l&ecirc;n.<br />\r\n<br />\r\nCạch!<br />\r\n<br />\r\nHắn lần nữa so với c&aacute;i k&eacute;o tay, mặt lộ vẻ nụ cười x&aacute;n lạn tại 60 vạn dọa đến ngao ngao người gọi trước mặt tự chụp một tấm.<br />\r\n<br />\r\n&quot;Chờ trong k&iacute;nh quỷ c&ugrave;ng ngươi quen về sau liền sẽ kh&ocirc;ng dọa ngươi, ngược lại sẽ giống m&egrave;o con một dạng kề cận ngươi, đặc biệt đ&aacute;ng y&ecirc;u.&quot;<br />\r\n<br />\r\n&quot;Sở dĩ dạng n&agrave;y ảnh chụp v&ocirc; c&ugrave;ng kh&oacute; được, chỉ c&oacute; đang c&ugrave;ng trong k&iacute;nh quỷ lần đầu l&uacute;c gặp mặt mới c&oacute; thể xuất hiện.&quot;<br />\r\n<br />\r\n&quot;Mọi người xem, trong mặt gương ta đ&atilde; chết rồi.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p thu hồi điện thoại cầm lấy camera đối mặt k&iacute;nh quay chụp đến.<br />\r\n<br />\r\n&quot;Chết được phi thường th&ecirc; thảm, đ&acirc;y l&agrave; trong k&iacute;nh quỷ dọa người cơ bản kỹ xảo một trong.&quot;<br />\r\n<br />\r\n&quot;B&igrave;nh thường người nh&igrave;n đến đ&acirc;y đ&atilde; bị h&ugrave; chạy.&quot;<br />\r\n<br />\r\nHắn x&ocirc;ng trong k&iacute;nh tiểu nữ h&agrave;i lộ ra một c&aacute;i nụ cười hiền l&agrave;nh.<br />\r\n<br />\r\n&quot;Chờ một l&aacute;t a, ta trước cảm tạ một đợt lễ vật.&quot;<br />\r\n<br />\r\n&quot;Cảm tạ @ bốn kh&ocirc;ng b&ocirc;́n mét bốn đạt năm c&aacute;i Tử Kim hộp b&aacute;u!&quot;<br />\r\n<br />\r\n&quot;Cảm tạ @ x&atilde; giao sợ bức tự m&igrave;nh n&oacute;i r&otilde; b&oacute; hoa, cảm ơn cảm ơn.&quot;<br />\r\n<br />\r\n&quot;Cảm tạ @ dưa kh&oacute; giữ được quen cạn ly, cạn ly cạn ly!&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p đắm ch&igrave;m trong trắng bệch dưới &aacute;nh trăng, tại trong k&iacute;nh quỷ hận kh&ocirc;ng thể lao ra ăn hắn dưới con mắt b&igrave;nh tĩnh cảm tạ lấy vừa mới tặng qu&agrave; c&aacute;c vị.<br />\r\n<br />\r\n&quot;Cảm ơn c&aacute;c vị a, tiếp xuống ch&uacute;ng ta phỏng vấn. . .&quot;<br />\r\n<br />\r\nKh&ocirc;ng đợi hắn n&oacute;i dứt lời, trong k&iacute;nh tiểu nữ h&agrave;i trực tiếp liền biến mất.<br />\r\n<br />\r\n&quot;? ? ?&quot;<br />\r\n<br />\r\n&quot;Sở dĩ up vừa mới thậm ch&iacute; c&ograve;n b&igrave;nh tĩnh cảm tạ một đợt lễ vật? ?&quot;<br />\r\n<br />\r\n&quot;. . . up đem linh dị h&ugrave; chạy? ?&quot;<br />\r\n<br />\r\n&quot;Ta. . . Ta thật hỗn loạn a, linh dị đến c&ugrave;ng c&oacute; tồn tại hay kh&ocirc;ng a? Đ&acirc;y l&agrave; đặc hiệu sao?&quot;<br />\r\n<br />\r\n&quot;Ta cũng kh&ocirc;ng hiểu a, v&igrave; c&aacute;i g&igrave; linh dị c&oacute; thể như vậy a?&quot;<br />\r\n<br />\r\n&quot;Linh dị kh&ocirc;ng n&ecirc;n trực tiếp nh&agrave;o l&ecirc;n đem up chia rồi sao?&quot;<br />\r\n<br />\r\nVừa trong k&iacute;nh quỷ ra tới một nh&aacute;y mắt.<br />\r\n<br />\r\nMưa đạn số lượng lần nữa tăng vọt một đợt đồng thời, k&ecirc;nh trực tiếp nh&acirc;n số ngược lại một nh&aacute;y mắt &iacute;t đi mấy vạn.<br />\r\n<br />\r\nMặc d&ugrave; L&acirc;m Mục C&aacute;p đ&atilde; biểu hiện được c&aacute;i n&agrave;y dạng, nhưng nh&igrave;n thấy linh dị c&oacute; ch&uacute;t người xem vẫn l&agrave; sẽ hạ &yacute; thức trực tiếp lui ra ngo&agrave;i.<br />\r\n<br />\r\nTrong l&uacute;c nhất thời vẫn c&ograve;n c&oacute; ch&uacute;t kh&ocirc;ng thể n&agrave;o tiếp thu được.<br />\r\n<br />\r\n&quot;Ha ha, xem ra đứa nhỏ n&agrave;y so s&aacute;nh xấu hổ.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p hắng giọng một c&aacute;i h&oacute;a giải một lần xấu hổ.<br />\r\n<br />\r\n&quot;Tấm gương c&aacute;i n&agrave;y vật dẫn đối với trong k&iacute;nh quỷ tới n&oacute;i rất trọng yếu.&quot;<br />\r\n<br />\r\n&quot;Một khi tấm gương bị đ&aacute;nh ph&aacute;, bọn ch&uacute;ng sẽ kh&ocirc;ng giống l&agrave; ốc mượn hồn một dạng một lần nữa t&igrave;m ph&ograve;ng ở, m&agrave; sẽ trực tiếp tử vong.&quot;<br />\r\n<br />\r\nHắn nh&igrave;n xem trước mặt tấm gương trầm mặc một ch&uacute;t.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y trong k&iacute;nh quỷ đi, l&agrave; ph&ugrave; hợp c&oacute; thể l&agrave;m sủng vật linh dị ti&ecirc;u chuẩn.&quot;<br />\r\n<br />\r\n&quot;D&atilde; ngoại trong k&iacute;nh quỷ rất dễ d&agrave;ng kh&ocirc;ng cẩn thận đ&atilde; bị đ&aacute;nh ph&aacute; mất mạng.&quot;<br />\r\n<br />\r\n&quot;Sở dĩ. . . Ta xuất ph&aacute;t từ bảo vệ mục đ&iacute;ch, ta quyết định mang n&agrave;ng về nh&agrave;.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p xoa xoa khung k&iacute;nh tro bụi mặt kh&ocirc;ng đổi sắc n&oacute;i đến.<br />\r\n<br />\r\n&quot;Loại n&agrave;y quỷ nu&ocirc;i dưỡng ở trong nh&agrave; đặc biệt m&aacute;t mẻ, so điều ho&agrave; kh&ocirc;ng kh&iacute; c&ograve;n c&oacute; t&aacute;c dụng.&quot;<br />\r\n<br />\r\n&quot;Ta đ&ecirc;m nay liền đem n&agrave;ng đặt ở b&ecirc;n giường, ng&agrave;y m&ugrave;a hạ ban đ&ecirc;m đi ngủ thậm ch&iacute; được đ&oacute;ng chăn b&ocirc;ng.&quot;<br />\r\n<br />\r\n&quot;Đặc biệt ti&ecirc;́t ki&ecirc;̣m đi&ecirc;̣n, c&ograve;n bảo vệ m&ocirc;i trường.&quot;<br />\r\n<br />\r\n&quot;C&aacute; nh&acirc;n ta l&agrave; m&atilde;nh liệt đề cử, chờ c&oacute; cơ hội cho đại gia phổ cập khoa học một đợt c&oacute; thể l&agrave;m sủng vật linh dị.&quot;<br />\r\n<br />\r\nHắn đem c&aacute;i n&agrave;y gương to&agrave;n th&acirc;n đ&oacute;ng g&oacute;i tốt, một b&ecirc;n n&acirc;ng xuống lầu một b&ecirc;n giơ camera n&oacute;i đến.<br />\r\n<br />\r\n&quot;? ? ?&quot;<br />\r\n<br />\r\n&quot;Điều n&agrave;y cũng muốn lấy ra chủ nghĩa?&quot;<br />\r\n<br />\r\n&quot;Ta kh&ocirc;ng nh&igrave;n lầm đem? up muốn đem linh dị mang về nh&agrave; c&ograve;n đặt ở ph&ograve;ng ngủ đầu giường? ? ?&quot;<br />\r\n<br />\r\n&quot;Linh dị: Ngươi lễ ph&eacute;p sao?&quot;<br />\r\n<br />\r\n&quot;Lễ ph&eacute;p: Ngươi linh dị sao?&quot;<br />\r\n<br />\r\n&quot;Lễ ph&eacute;p linh dị: Ngươi sao?&quot;<br />\r\n<br />\r\nMặc d&ugrave; mọi người ngo&agrave;i miệng vẫn l&agrave; c&aacute;c loại kh&ocirc;ng tin, cảm thấy đ&acirc;y l&agrave; đặc hiệu c&aacute;i g&igrave;.<br />\r\n<br />\r\nNhưng k&ecirc;nh trực tiếp b&ecirc;n trong mưa đạn bầu kh&ocirc;ng kh&iacute; so với vừa ph&aacute;t s&oacute;ng l&uacute;c ấy đ&atilde; nhẹ nh&otilde;m nhiều lắm.<br />\r\n<br />\r\n&quot;Hoắc! Đang n&oacute;i sủng vật đ&acirc;u, c&aacute;i n&agrave;y kh&ocirc;ng lại nh&igrave;n thấy một c&aacute;i!&quot;<br />\r\n<br />\r\nVừa xuống lầu c&ograve;n chưa đi mấy bước.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p cũng cảm gi&aacute;c mắt c&aacute; ch&acirc;n ch&iacute;nh m&igrave;nh m&aacute;t lạnh.<br />\r\n<br />\r\nHắn c&uacute;i đầu xuống.<br />\r\n<br />\r\nTừng c&aacute;i màu ngà sữa tay kh&ocirc;ng biết lúc nào từ l&ograve;ng đất x&ocirc;ng tới ch&iacute;nh nắm lấy ch&acirc;n của hắn.<br />\r\n<br />\r\n&quot;Những tiểu gia hỏa n&agrave;y t&ecirc;n khoa học gọi ng&agrave;n tay, nếu như n&oacute;i c&aacute;i n&agrave;y trong k&iacute;nh quỷ c&oacute; thể l&agrave;m th&agrave;nh c&aacute;i tiểu sủng vật lời n&oacute;i, như vậy ng&agrave;n tay th&igrave; c&oacute; thể l&agrave;m th&agrave;nh bồn hoa.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p đem camera nhắm ngay tr&ecirc;n mặt đất nhiều đ&aacute;m hướng về th&acirc;n thể hắn leo l&ecirc;n tay cất kỹ.<br />\r\n<br />\r\nSau đ&oacute; từng c&aacute;i c&ugrave;ng bọn ch&uacute;ng nắm tay, vỗ tay.<br />\r\n<br />\r\nTr&ecirc;n mặt tr&agrave;n đầy nụ cười vui mừng.<br />\r\n<br />\r\n&quot;Đại gia ngẫm lại, đem những n&agrave;y tay trồng ở cửa nh&agrave;, kh&aacute;ch nh&acirc;n vừa đến đ&atilde; lắc lư k&ecirc;u gọi, kh&aacute;ch nh&acirc;n đi rồi đ&atilde; bắt kh&aacute;ch nh&acirc;n ch&acirc;n lưu kh&aacute;ch, nhường ngươi lộ ra nhiều nhiệt t&igrave;nh a!&quot;<br />\r\n<br />\r\nHắn nắm l&ecirc;n một c&aacute;i tay, sau đ&oacute; d&ugrave;ng qu&aacute; sức nhi trực tiếp nhổ tận gốc.<br />\r\n<br />\r\nTr&ecirc;n mặt đất từng c&acirc;y m&agrave;u t&iacute;m đồ vật mang theo m&aacute;u b&agrave;nh bạch bị mang ra ngo&agrave;i.<br />\r\n<br />\r\n&quot;Đ&acirc;y ch&iacute;nh l&agrave; mạch m&aacute;u, c&oacute; thể hiểu th&agrave;nh rễ thực vật, trồng ở tr&ecirc;n s&agrave;n nh&agrave;, tr&ecirc;n tường đều được, hai ba con liền c&oacute; thể sinh s&ocirc;i th&agrave;nh một đ&aacute;m hơn mười c&aacute;i.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p vừa n&oacute;i một b&ecirc;n lại r&uacute;t hai c&acirc;y.<br />\r\n<br />\r\nSau đ&oacute; c&ugrave;ng c&aacute;i kh&aacute;c tr&ecirc;n đất từng c&aacute;i vẫy tay từ biệt.<br />\r\n<br />\r\n&quot;. . . A?&quot;<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; đang đ&oacute;ng phim sao?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải. . . Ta. . . Đầu c&oacute; ch&uacute;t chuyển kh&ocirc;ng đến.&quot;<br />\r\n<br />\r\n&quot;up nh&igrave;n thấy linh dị chẳng những kh&ocirc;ng chạy c&ograve;n. . . Mang về nh&agrave;?&quot;<br />\r\n<br />\r\n&quot;N&oacute;i cho ta biết đ&acirc;y kh&ocirc;ng phải l&agrave; thật.&quot;<br />\r\n<br />\r\n&quot;Ta kh&ocirc;ng thể n&agrave;o tiếp thu được.&quot;<br />\r\n<br />\r\nC&ugrave;ng mưa đạn b&ecirc;n tr&ecirc;n phản ứng đồng dạng.<br />\r\n<br />\r\nTh&agrave;nh Nam trung t&acirc;m bệnh viện tr&ecirc;n giường bệnh khủng bố đại sư y&ecirc;n lặng lột mở bản th&acirc;n ống quần.<br />\r\n<br />\r\nTr&ecirc;n mắt c&aacute; ch&acirc;n c&oacute; một rất r&otilde; r&agrave;ng t&iacute;m thủ ấn.<br />\r\n<br />\r\nĐ&oacute; ch&iacute;nh l&agrave; L&acirc;m Mục C&aacute;p trong miệng ng&agrave;n tay cầm ra tới.<br />\r\n<br />\r\n&quot;Ta kh&ocirc;ng tiếp thụ nổi. . .&quot;<br />\r\n<br />\r\nNh&igrave;n xem trực tiếp L&acirc;m Mục C&aacute;p đ&atilde; h&agrave;o hứng kẹp lấy ba c&aacute;i tay l&ocirc;i k&eacute;o gương to&agrave;n th&acirc;n tại Tiểu Bạch lưu luyến kh&ocirc;ng rời dưới &aacute;nh mắt rời đi vứt bỏ bệnh viện t&acirc;m thần, sau đ&oacute; rất b&igrave;nh thường tắt đi trực tiếp.<br />\r\n<br />\r\nKhủng bố đại sư trong mắt dần dần mất đi cao quang.<br />\r\n<br />\r\nC&aacute;i n&agrave;y. . . Đem ta dọa đến kh&ocirc;ng muốn kh&ocirc;ng muốn linh dị tại nh&acirc;n gia trong mắt lại l&agrave;. . .<br />\r\n<br />\r\nSủng vật?<br />\r\n<br />\r\nBồn hoa? ?<br />\r\n<br />\r\nC&ograve;n muốn đặt ở đầu giường nu&ocirc;i dưỡng ở trong nh&agrave;? ? ?<br />\r\n<br />\r\n&quot;Thế giới n&agrave;y qu&aacute; đi&ecirc;n cuồng.&quot;</p>', '2021-11-28 16:04:52', 6, 1),
(230, 60, 'Ngươi phải chống chân chống đứng dậy a!', '6', '<p>&quot;H&ocirc;! Thật sợ kh&ocirc;ng cẩn thận n&eacute;m tới ngươi.&quot;<br />\r\n<br />\r\nTrời vừa rạng s&aacute;ng nhi&ecirc;̀u.<br />\r\n<br />\r\nTrải qua thi&ecirc;n t&acirc;n vạn khổ cuối c&ugrave;ng k&eacute;o lấy tấm gương &ocirc;m mấy c&aacute;i tay đến cửa tiểu khu về sau, L&acirc;m Mục C&aacute;p cuồng thở d&agrave;i một hơi.<br />\r\n<br />\r\nVẫn được, mặt k&iacute;nh ho&agrave;n hảo kh&ocirc;ng ch&uacute;t tổn hại.<br />\r\n<br />\r\nB&igrave;nh thường trong k&iacute;nh quỷ mặt k&iacute;nh đều chẳng phải dễ d&agrave;ng đ&aacute;nh vỡ, nhưng vị n&agrave;y qu&aacute; xấu hổ, mặt k&iacute;nh vậy tương đối yếu ớt một ch&uacute;t.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p một đường n&agrave;y một mực tại chiếu cố n&oacute;, ngay cả vừa mới trực tiếp hiệu quả cũng kh&ocirc;ng kịp nh&igrave;n.<br />\r\n<br />\r\n&quot;Đại gia c&ograve;n chưa ngủ đ&acirc;u?&quot;<br />\r\n<br />\r\nHắn giơ l&ecirc;n trong ngực tay g&atilde;y x&ocirc;ng tr&ocirc;ng cửa đại gia l&ecirc;n tiếng ch&agrave;o, sau đ&oacute; khẽ h&aacute;t ng&acirc;n nga đi v&agrave;o cư x&aacute;.<br />\r\n<br />\r\n&quot;A?&quot;<br />\r\n<br />\r\nNh&igrave;n xem L&acirc;m Mục C&aacute;p trong ngực ba con tựa hồ c&ograve;n tại đung đưa hướng hắn ch&agrave;o hỏi ba con tay g&atilde;y, tr&ocirc;ng cửa đại gia sửng sốt hai gi&acirc;y từ g&aacute;c cổng sảnh th&ograve; đầu ra.<br />\r\n<br />\r\n&quot;. . . A?&quot;<br />\r\n<br />\r\nC&aacute;i n&agrave;y thăm d&ograve; đầu, vừa hay nh&igrave;n thấy L&acirc;m Mục C&aacute;p sau lưng k&eacute;o lấy cực lớn gương to&agrave;n th&acirc;n.<br />\r\n<br />\r\nNhưng gương to&agrave;n th&acirc;n b&ecirc;n trong kh&ocirc;ng c&oacute; xuất hiện hắn ảnh trong gương, trống rỗng trong mặt gương tựa hồ chỉ c&oacute; một &ocirc;m ch&acirc;n n&uacute;p ở g&oacute;c khuất, một mặt sinh kh&ocirc;ng thể luyến xem ra đặc biệt ủy khuất tiểu c&ocirc; nương.<br />\r\n<br />\r\n&quot;Chuyện g&igrave; xảy ra?&quot;<br />\r\n<br />\r\nTr&ocirc;ng cửa đại gia trong l&ograve;ng giật m&igrave;nh, vội v&agrave;ng dụi dụi mắt.<br />\r\n<br />\r\nLần nữa nh&igrave;n về ph&iacute;a tấm gương thời điểm, tiểu nữ h&agrave;i đ&atilde; biến mất kh&ocirc;ng thấy g&igrave; nữa.<br />\r\n<br />\r\n&quot;A. . . Nguy&ecirc;n lai ch&iacute;nh l&agrave; một mặt kh&ocirc;ng thể hiện ảnh phổ th&ocirc;ng tấm gương a. . .&quot;<br />\r\n<br />\r\nHắn ngồi ở tr&ecirc;n ghế xoa xoa mồ h&ocirc;i lạnh tr&ecirc;n tr&aacute;n.<br />\r\n<br />\r\nNg&agrave;y n&oacute;ng nực tại sao lạnh như vậy chứ. . .<br />\r\n<br />\r\nHắn r&ugrave;ng m&igrave;nh một c&aacute;i.<br />\r\n<br />\r\n&quot;Cuối c&ugrave;ng đến nh&agrave;.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p nh&agrave; th&igrave; ở lầu một.<br />\r\n<br />\r\nL&uacute;c đ&oacute; thu&ecirc; thời điểm kh&ocirc;ng nghĩ nhiều như vậy, b&acirc;y giờ suy nghĩ một ch&uacute;t coi như kh&ocirc;ng tệ, về sau nhặt được c&aacute;i g&igrave; đ&aacute;ng y&ecirc;u tiểu linh dị chuyển tới đặc biệt thuận tiện.<br />\r\n<br />\r\n&quot;Ta cắm.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng sai, c&oacute; nh&agrave; cảm gi&aacute;c.&quot;<br />\r\n<br />\r\nNh&igrave;n thấy ba c&aacute;i tay cứ như vậy tới lui, hắn vỗ trương chiếu rất h&agrave;i l&ograve;ng nhẹ gật đầu.<br />\r\n<br />\r\n&quot;Đến, giới thiệu cho c&aacute;c ngươi một ch&uacute;t.&quot;<br />\r\n<br />\r\nHắn một tay dắt lấy tấm gương, một tay k&eacute;o một con ng&agrave;n tay.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; ph&ograve;ng vệ sinh, chờ c&oacute; thời gian lại dạy ngươi l&agrave;m sao rửa tay.&quot;<br />\r\n<br />\r\nBị hắn l&ocirc;i k&eacute;o ng&agrave;n tay ngay tại tr&ecirc;n mặt đất hoạt động l&ecirc;n, cơ hồ kh&ocirc;ng c&oacute; c&aacute;i g&igrave; lực cản.<br />\r\n<br />\r\nLinh dị theo L&acirc;m Mục C&aacute;p giống như l&agrave; động vật đồng dạng.<br />\r\n<br />\r\nNhất l&agrave; ng&agrave;n tay, mặc d&ugrave; kh&ocirc;ng c&oacute; g&igrave; tr&iacute; lực, nhưng đơn giản một ch&uacute;t động t&aacute;c cũng vẫn l&agrave; c&oacute; thể học được.<br />\r\n<br />\r\nTỉ như ban đ&ecirc;m k&ecirc;u gọi ng&agrave;n tay tới tắt một lần đ&egrave;n, l&uacute;c xem truyền h&igrave;nh k&ecirc;u gọi n&oacute; nắm đậu phộng hạt dưa nhi đưa qua loại h&igrave;nh.<br />\r\n<br />\r\nTắm thời điểm cũng c&oacute; thể gi&uacute;p ngươi kỳ lưng, đặc biệt thuận tiện.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; ph&ograve;ng kh&aacute;ch, ngươi về sau t&ugrave;y tiện ở đ&acirc;y du đ&atilde;ng l&agrave; được, trừ ph&ograve;ng bếp, c&aacute;i nh&agrave; n&agrave;y ch&iacute;nh l&agrave; của ngươi phạm vi hoạt động, tr&ecirc;n mặt đất tr&ecirc;n tường lều đỉnh t&ugrave;y tiện, đừng đem đ&egrave;n đụng hỏng rồi l&agrave; được.&quot;<br />\r\n<br />\r\n&quot;Nơi n&agrave;y ch&iacute;nh l&agrave; ph&ograve;ng bếp, v&ocirc; c&ugrave;ng nguy hiểm, ngươi cấm địa.&quot;<br />\r\n<br />\r\n&quot;Nơi n&agrave;y l&agrave; ta chỗ ăn cơm, c&aacute;c ngươi vậy ăn kh&ocirc;ng được.&quot;<br />\r\n<br />\r\n&quot;Nơi n&agrave;y nguy&ecirc;n lai chuẩn bị trang m&aacute;y điều h&ograve;a kh&ocirc;ng kh&iacute;, nhưng l&agrave; c&oacute; c&aacute;c ngươi cũng kh&ocirc;ng cần.&quot;<br />\r\n<br />\r\n&quot;Cuối c&ugrave;ng đ&acirc;y l&agrave; ta ph&ograve;ng ngủ, cũng l&agrave; k&iacute;nh k&iacute;nh nh&agrave; mới, về sau ngươi nghĩ tiến đến g&otilde; g&otilde; cửa.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p x&ocirc;ng ng&agrave;n tay n&oacute;i đến.<br />\r\n<br />\r\nNh&agrave; hắn cũng liền hơn 80m2, kh&ocirc;ng c&oacute; bao nhi&ecirc;u.<br />\r\n<br />\r\nĐến rồi hai c&aacute;i tiểu sủng vật về sau mặc d&ugrave; đột nhi&ecirc;n liền lộ ra chật chội l&ecirc;n, nhưng l&agrave; ấm &aacute;p rất nhiều.<br />\r\n<br />\r\n&quot;Tới đi, về sau ngươi ở chỗ n&agrave;y, hai ta bồi dưỡng một ch&uacute;t t&igrave;nh cảm.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p đem tấm gương đặt ở hắn giường ch&iacute;nh đối diện.<br />\r\n<br />\r\nHiện tại tr&ecirc;n mặt k&iacute;nh đ&atilde; kh&ocirc;ng hiện ảnh, điều n&agrave;y n&oacute;i r&otilde; trong k&iacute;nh quỷ hoặc l&agrave; cảm x&uacute;c sa s&uacute;t hoặc l&agrave; phi thường phấn khởi.<br />\r\n<br />\r\nBằng v&agrave;o kinh nghiệm nhiều năm, L&acirc;m Mục C&aacute;p cảm thấy hẳn l&agrave; bởi v&igrave; qu&aacute; mức hưng phấn m&agrave; đưa đến qu&aacute; mức ngượng ng&ugrave;ng.<br />\r\n<br />\r\nHợp l&yacute;!<br />\r\n<br />\r\n&quot;Ngủ ngon.&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p đơn giản x&ocirc;ng c&aacute;i tắm, thay đổi &aacute;o ngủ t&igrave;m ra m&ugrave;a thu đ&oacute;ng nhỏ chăn b&ocirc;ng r&uacute;c về ph&iacute;a sau ti&ecirc;́n v&agrave;o ổ chăn.<br />\r\n<br />\r\n&quot;Dễ chịu a. . .&quot;<br />\r\n<br />\r\nNg&agrave;y m&ugrave;a hạ, c&oacute; hai vị n&agrave;y, căn bản kh&ocirc;ng cần mở điều h&ograve;a.<br />\r\n<br />\r\nCả ph&ograve;ng chẳng những &acirc;m kh&iacute; vờn quanh c&ograve;n an tĩnh đến đ&aacute;ng sợ.<br />\r\n<br />\r\nC&oacute; một loại nh&agrave; x&aacute;c h&agrave;i h&ograve;a kh&ocirc;ng kh&iacute;, phi thường th&iacute;ch hợp an nghỉ.<br />\r\n<br />\r\nNgắn ngủi kh&ocirc;ng đến hai ph&uacute;t, L&acirc;m Mục C&aacute;p liền tiến v&agrave;o ngọt ng&agrave;o mộng đẹp.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; người n&agrave;o?&quot;<br />\r\n<br />\r\n&quot;Nếu như l&agrave; đặc hiệu lời n&oacute;i, kh&ocirc;ng khỏi cũng qu&aacute; giống như thật a?&quot;<br />\r\n<br />\r\n&quot;Mấu chốt hắn thấy những c&aacute;i kia linh dị đều l&agrave; @ khủng bố đại sư trải qua.&quot;<br />\r\n<br />\r\n&quot;C&oacute; phải hay kh&ocirc;ng l&agrave; hai người kia tại kết phường gạt người?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng cần thiết a. . .&quot;<br />\r\n<br />\r\nTrong l&uacute;c ngủ mơ L&acirc;m Mục C&aacute;p cũng kh&ocirc;ng biết hắn trực tiếp đ&atilde; tại tr&ecirc;n internet nhấc l&ecirc;n s&oacute;ng to gi&oacute; lớn.<br />\r\n<br />\r\n&quot;Ta xem cả tr&agrave;ng trực tiếp, hắn nh&igrave;n thấy linh dị biểu hiện được giống như l&agrave;. . . Nh&igrave;n thấy động vật.&quot;<br />\r\n<br />\r\n&quot;Hơn nửa đ&ecirc;m liền xem như nh&igrave;n thấy con ch&oacute; cũng kh&ocirc;ng khả năng như vậy b&igrave;nh tĩnh a?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải, linh dị đến c&ugrave;ng c&oacute; tồn tại hay kh&ocirc;ng a? Ta hiện tại đ&atilde; kh&ocirc;ng ngủ y&ecirc;n giấc.&quot;<br />\r\n<br />\r\nLi&ecirc;n quan tới linh dị l&agrave; c&oacute; tồn tại hay kh&ocirc;ng, linh dị bản chất l&agrave; c&aacute;i g&igrave; thảo luận trải qua mấy ng&agrave;y nay l&ecirc;n men, tại L&acirc;m Mục C&aacute;p tự m&igrave;nh thực tiễn phổ cập khoa học b&ecirc;n dưới, ngược lại c&agrave;ng ng&agrave;y c&agrave;ng nghi&ecirc;m trọng l&ecirc;n.<br />\r\n<br />\r\n&quot;Ta kh&ocirc;ng tiếp thụ nổi. . .&quot;<br />\r\n<br />\r\nĐ&ecirc;m khuya th&agrave;nh Nam trung t&acirc;m bệnh viện 404 trong ph&ograve;ng bệnh, khủng bố đại sư lật qua lật lại trằn trọc v&ocirc; ph&aacute;p ngủ.<br />\r\n<br />\r\nĐầy trong đầu đều l&agrave; L&acirc;m Mục C&aacute;p tại vứt bỏ bệnh viện t&acirc;m thần b&ecirc;n trong giống như l&agrave; chơi Pokemon tr&ograve; chơi một dạng, nh&igrave;n thấy linh dị sau ngạc nhi&ecirc;n bộ d&aacute;ng.<br />\r\n<br />\r\n&quot;Ng&agrave;i tốt, nh&igrave;n ng&agrave;i trực tiếp về sau, ta đối của ch&iacute;nh ta thế giới quan sinh ra s&acirc;u đậm ho&agrave;i nghi.&quot;<br />\r\n<br />\r\n&quot;Đoạn thời gian trước ta g&oacute;p nhặt thật nhiều truyền thuyết đ&ocirc; thị, l&uacute;c đầu nghĩ một m&igrave;nh đi th&aacute;m hiểm.&quot;<br />\r\n<br />\r\n&quot;Nhưng v&igrave; m&igrave;nh nghi ngờ trong l&ograve;ng, cũng v&igrave; cho người xem một c&aacute;i c&ocirc;ng đạo, ta nghĩ c&ugrave;ng ng&agrave;i c&ugrave;ng nhau đi tới từng c&aacute;i truyền thuyết đ&ocirc; thị.&quot;<br />\r\n<br />\r\n&quot;Ta cho ng&agrave;i l&agrave;m thợ quay phim cũng được!&quot;<br />\r\n<br />\r\nThực tế ngủ kh&ocirc;ng được khủng bố đại sư mở ra điện thoại.<br />\r\n<br />\r\nĐang đối thoại khung b&ecirc;n trong đ&aacute;nh lại xóa sau cho L&acirc;m Mục C&aacute;p pm ph&aacute;t đến.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nS&aacute;ng ng&agrave;y thứ hai hơn ch&iacute;n điểm.<br />\r\n<br />\r\n&Aacute;nh nắng xuy&ecirc;n thấu qua m&agrave;n cửa khe hở chiếu xạ đến L&acirc;m Mục C&aacute;p tr&ecirc;n giường.<br />\r\n<br />\r\nNgay tại hắn giường đối diện trong gương, một c&aacute;i t&oacute;c tai b&ugrave; x&ugrave; tiểu nữ h&agrave;i đang lườm mắt nh&igrave;n ch&ograve;ng chọc v&agrave;o hắn.<br />\r\n<br />\r\nC&aacute;ch tấm gương đều c&oacute; thể cảm nhận được n&agrave;ng khuất nhục.<br />\r\n<br />\r\nL&agrave;m một b&igrave;nh qu&acirc;n mỗi th&aacute;ng đều c&oacute; thể h&ugrave; chết mấy người quỷ, n&agrave;ng gặp phải trước đ&oacute; chưa từng c&oacute; vũ nhục.<br />\r\n<br />\r\nC&aacute;i n&agrave;y nam nh&acirc;n chẳng những kh&ocirc;ng sợ n&agrave;ng, thậm ch&iacute; c&ograve;n b&igrave;nh tĩnh nhường n&agrave;ng cảm gi&aacute;c đ&aacute;ng sợ. . .<br />\r\n<br />\r\nC&aacute;i n&agrave;y đ&uacute;ng sao?<br />\r\n<br />\r\nMột hồi chờ hắn l&ecirc;n, ta h&ugrave; chết hắn!<br />\r\n<br />\r\nTrong k&iacute;nh quỷ cầm quyền ở trong l&ograve;ng nghĩ đến.<br />\r\n<br />\r\n&quot;Ch&agrave;o buổi s&aacute;ng. . .&quot;<br />\r\n<br />\r\nMấy ph&uacute;t sau.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p phạm v&agrave;o th&acirc;n chậm r&atilde;i ngồi dậy.<br />\r\n<br />\r\n&quot;Nha, đi ra? Tiểu c&ocirc; nương khỏe mạnh l&agrave;m sao kh&ocirc;ng ngay ngắn lý một lần t&oacute;c đ&acirc;u?&quot;<br />\r\n<br />\r\n&quot;Ngươi lấy trước kia kiểu t&oacute;c đ&acirc;u? Ngươi phải ngẩn ngơ đứng dậy a!&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p c&ugrave;ng trong k&iacute;nh quỷ th&ecirc; lương tinh hồng hiện đầy tơ m&aacute;u con mắt nh&igrave;n nhau v&agrave;i gi&acirc;y đồng hồ sau ng&aacute;p một c&aacute;i n&oacute;i đến.<br />\r\n<br />\r\nSau đ&oacute; hắn như kh&ocirc;ng c&oacute; chuyện g&igrave; xảy ra đi xuống giường.<br />\r\n<br />\r\nMở cửa.<br />\r\n<br />\r\nBa đ&ocirc;i trắng bệch tay ch&iacute;nh treo ngược lấy rũ xuống tới trước mắt của hắn.<br />\r\n<br />\r\n&quot;Hi.&quot;<br />\r\n<br />\r\nC&ugrave;ng bọn ch&uacute;ng từng c&aacute;i vỗ tay sau khi bắt tay, tại trong k&iacute;nh quỷ l&ograve;ng tự trọng lần nữa gặp kh&oacute; ủy khuất dưới &aacute;nh mắt.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p giống như l&agrave; b&igrave;nh thường một lần đ&aacute;nh răng rửa mặt, to&agrave;n th&acirc;n tr&ecirc;n dưới kh&ocirc;ng nh&igrave;n thấy ch&uacute;t n&agrave;o sợ h&atilde;i.<br />\r\n<br />\r\n&quot;Hoắc!&quot;<br />\r\n<br />\r\nĐ&aacute;nh răng xong rửa mặt xong, mở ra điện thoại tiểu ph&aacute; trạm, L&acirc;m Mục C&aacute;p dụi dụi mắt.</p>', '2021-11-28 16:05:12', 8, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(231, 60, 'Chủ yếu là có thể thu được trên tinh thần an ủi', '7', '<p>H&ocirc;m qua vẫn l&agrave; chỉ c&oacute; hơn bảy vạn fan h&acirc;m mộ hắn, hiện tại fan h&acirc;m mộ lượng trực tiếp đột ph&aacute; mười vạn, chuẩn x&aacute;c một điểm n&oacute;i l&agrave; 1 0.1 vạn.<br />\r\n<br />\r\n&quot;Ta đi. . .&quot;<br />\r\n<br />\r\nVội v&agrave;ng leo l&ecirc;n m&aacute;y t&iacute;nh mở ra sau khi đ&agrave;i s&aacute;ng t&aacute;c trung t&acirc;m, L&acirc;m Mục C&aacute;p k&iacute;ch động đến ba một c&aacute;i cầm một b&ecirc;n kh&ocirc;ng cẩn thận đi ngang qua ng&agrave;n tay.<br />\r\n<br />\r\nĐem mặt kh&aacute;c hai con vậy giật nảy m&igrave;nh.<br />\r\n<br />\r\nTrực tiếp quan s&aacute;t nh&acirc;n số max trị số c&oacute; 86. 4 vạn người, mưa đạn số lượng 97. 9 vạn, tối cao xếp tại trực tiếp hấp dẫn thứ 8 vị!<br />\r\n<br />\r\nLike b&igrave;nh luận c&aacute;i g&igrave; cũng l&agrave; chợt tăng mấy lần.<br />\r\n<br />\r\nChủ yếu nhất l&agrave; fan h&acirc;m mộ lượng tăng trưởng đường cong, trực tiếp từ một đường thẳng biến th&agrave;nh một đầu đường dọc.<br />\r\n<br />\r\nC&agrave;ng l&agrave;m cho L&acirc;m Mục C&aacute;p vui mừng ch&iacute;nh l&agrave;, hắn ng&agrave;y h&ocirc;m qua trực tiếp &iacute;ch lợi v&acirc;̣y mà vượt qua bốn ng&agrave;n khối.<br />\r\n<br />\r\nBắt mắt nhất đ&uacute;ng l&agrave; trực tiếp nhiều mười hai c&aacute;i hạm trưởng.<br />\r\n<br />\r\n&quot;Đại gia đối linh dị kiến thức học tập nhiệt t&igrave;nh vẫn l&agrave; rất tăng cao a!&quot;<br />\r\n<br />\r\nCho m&igrave;nh lần thứ nhất bay vọt hậu đ&agrave;i số liệu cắt c&aacute;i đồ về sau, L&acirc;m Mục C&aacute;p rất cảm thấy vui mừng.<br />\r\n<br />\r\nCũng kh&ocirc;ng phải l&agrave; bởi v&igrave; tiền c&aacute;i g&igrave;.<br />\r\n<br />\r\nChủ yếu l&agrave; hiện tại xuất ph&aacute;t từ linh dị kh&ocirc;i phục sơ kỳ, tại đời trước tử trải qua bởi v&igrave; mọi người chưa quen thuộc, sợ h&atilde;i linh dị m&agrave; tạo th&agrave;nh đau ngắn về sau, hắn ph&aacute; lệ hi vọng c&oacute; thể th&ocirc;ng qua bản th&acirc;n trong đầu tri thức khiến mọi người hi&ecirc;̉u rõ linh dị.<br />\r\n<br />\r\nX&uacute;c tiến linh dị c&ugrave;ng nh&acirc;n loại h&agrave;i h&ograve;a chung sống.<br />\r\n<br />\r\nCho tới h&ocirc;m nay, hắn mới ph&aacute;t gi&aacute;c được m&igrave;nh đ&atilde; bước ra một bước nhỏ, cũng l&agrave; trọng yếu nhất một bước.<br />\r\n<br />\r\n&quot;Ng&agrave;i tốt nơi n&agrave;y l&agrave; tiểu ph&aacute; trạm ph&iacute;a ch&iacute;nh thức, ch&uacute;c mừng ng&agrave;i đạt tới mười vạn fan h&acirc;m mộ! Xin trả lời gia đ&igrave;nh địa chỉ, v&igrave; ng&agrave;i chuy&ecirc;n m&ocirc;n đặt l&agrave;m tiểu ng&acirc;n b&agrave;i sẽ tại trong một th&aacute;ng giao h&agrave;ng!&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p theo th&oacute;i quen ấn mở pm.<br />\r\n<br />\r\nĐầu thứ nhất ch&iacute;nh l&agrave; tiểu ph&aacute; trạm quan phương.<br />\r\n<br />\r\n&quot;Tiểu ng&acirc;n b&agrave;i a. . .&quot;<br />\r\n<br />\r\nHắn m&iacute;m m&ocirc;i c&oacute; ch&uacute;t k&iacute;ch động lấp l&ecirc;n bản th&acirc;n địa chỉ.<br />\r\n<br />\r\nĐ&acirc;y cũng l&agrave; hắn trở th&agrave;nh uploader sau ph&oacute;ng ra bước thứ nhất!<br />\r\n<br />\r\n&quot;Bồ c&acirc;u ngươi tốt, ta hiện tại ch&uacute; &yacute; c&oacute; phải l&agrave; ch&iacute;nh l&agrave; fan kỳ cựu rồi?&quot;<br />\r\n<br />\r\n&quot;up, ta khi c&ograve;n b&eacute; đặc biệt sợ quỷ, nhưng nh&igrave;n ngươi trực tiếp, đột nhi&ecirc;n cảm gi&aacute;c quỷ giống như cũng kh&ocirc;ng phải đ&aacute;ng sợ như vậy, cảm ơn!&quot;<br />\r\n<br />\r\n&quot;Xin hỏi uploader tr&ecirc;n thế giới n&agrave;y thật tồn tại linh dị sao?&quot;<br />\r\n<br />\r\n&quot;up, ta l&aacute; gan đặc biệt nhỏ, xin hỏi c&oacute; c&aacute;i g&igrave; phương ph&aacute;p c&oacute; thể trở n&ecirc;n giống như ngươi gan to bằng trời muốn l&agrave;m g&igrave; th&igrave; l&agrave;m sao?&quot;<br />\r\n<br />\r\n&quot;Xin hỏi lần sau trực tiếp c&acirc;n nhắc ban đ&ecirc;m đi một lần th&agrave;nh Nam cao ốc sao? Nghe n&oacute;i th&agrave;nh Nam cao ốc buổi tối thang m&aacute;y c&oacute; linh dị.&quot;<br />\r\n<br />\r\nĐi&ecirc;̀n xong địa chỉ sau L&acirc;m Mục C&aacute;p rất chuy&ecirc;n nghiệp từng c&aacute;i hồi phục pm b&ecirc;n trong vấn đề kh&aacute;c.<br />\r\n<br />\r\n&quot;Khủng bố đại sư. . .&quot;<br />\r\n<br />\r\nĐ&ocirc;ng đảo pm b&ecirc;n trong, hắn thấy được khủng bố đại sư h&ocirc;m qua ba giờ s&aacute;ng cho th&ecirc;m hắn ph&aacute;t.<br />\r\n<br />\r\n&quot;Tốt, c&oacute; thể, linh dị loại vật n&agrave;y ngươi liền coi n&oacute; l&agrave; th&agrave;nh động vật l&agrave; tốt rồi.&quot;<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; nhiều do dự, L&acirc;m Mục C&aacute;p trực tiếp đ&aacute;p ứng rồi khủng bố đại sư thỉnh cầu.<br />\r\n<br />\r\nTiểu ph&aacute; trạm uploader ở giữa kh&ocirc;ng hề giống l&agrave; c&aacute;i kh&aacute;c trực tiếp b&igrave;nh đ&agrave;i như thế cuồn cuộn s&oacute;ng ngầm lục đục với nhau.<br />\r\n<br />\r\nĐại gia ban sơ cũng đều l&agrave; bởi v&igrave; y&ecirc;u qu&yacute; m&agrave; tụ tập ở chung với nhau, loại n&agrave;y có th&ecirc;̉ x&uacute;c tiến tuy&ecirc;n truyền hợp l&yacute; y&ecirc;u cầu L&acirc;m Mục C&aacute;p thậm ch&iacute; cầu c&ograve;n kh&ocirc;ng được đ&acirc;u.<br />\r\n<br />\r\n&quot;Cảm ơn cảm ơn!&quot;<br />\r\n<br />\r\nKh&ocirc;ng đợi hắn về xong c&aacute;i kh&aacute;c pm, khủng bố đại sư liền lập tức trở về đến.<br />\r\n<br />\r\n&quot;Ta h&ocirc;m nay xuất viện, ch&uacute;ng ta trời tối ng&agrave;y mai đi một lần th&agrave;nh Nam cao ốc c&oacute; thể chứ? C&aacute;i kia thang m&aacute;y nghe n&oacute;i c&oacute; quỷ.&quot;<br />\r\n<br />\r\n&quot;Th&agrave;nh Nam cao ốc. . .&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p hơi nh&iacute;u nổi l&ecirc;n l&ocirc;ng m&agrave;y.<br />\r\n<br />\r\nHắn vừa mới hồi phục pm b&ecirc;n trong c&oacute; thật nhiều đầu đều l&agrave; để hắn đi một chuyến th&agrave;nh Nam cao ốc.<br />\r\n<br />\r\nNhưng l&agrave; linh dị kh&ocirc;i phục sơ kỳ, loại n&agrave;y hiện đại ho&aacute; kiến tr&uacute;c b&ecirc;n trong b&igrave;nh thường vẫn l&agrave; rất an to&agrave;n. . .<br />\r\n<br />\r\n&quot;C&oacute; thể.&quot;<br />\r\n<br />\r\n&quot;Tốt, ta th&ecirc;m một lần ng&agrave;i.&quot;<br />\r\n<br />\r\n&quot;Ừm.&quot;<br />\r\n<br />\r\nC&ugrave;ng khủng bố đại sư lẫn nhau bỏ th&ecirc;m một lần, đem c&aacute;i kh&aacute;c pm đều hồi phục xong, L&acirc;m Mục C&aacute;p duỗi lưng một c&aacute;i tra được li&ecirc;n quan tới th&agrave;nh Nam cao ốc tin tức tương quan.<br />\r\n<br />\r\n&quot;Th&agrave;nh Nam cao ốc trong thang m&aacute;y c&oacute; nh&acirc;n vi&ecirc;n thắt cổ tự s&aacute;t, kh&oacute;e miệng lộ ra quỷ dị mỉm cười.&quot;<br />\r\n<br />\r\n&quot;Kinh hồn một khắc! Th&agrave;nh Nam cao ốc trời vừa rạng s&aacute;ng thang m&aacute;y đột nhi&ecirc;n mất linh!&quot;<br />\r\n<br />\r\n&quot;Chấn kinh! Th&agrave;nh Nam cao ốc trong thang m&aacute;y ph&aacute;t hiện quỷ dị t&oacute;c d&agrave;i!&quot;<br />\r\n<br />\r\n&quot;Th&agrave;nh Nam cao ốc thang m&aacute;y kh&ocirc;ng hiểu rướm m&aacute;u,<br />\r\n<br />\r\nCảnh s&aacute;t điều tra vẫn chưa ph&aacute;t hiện dị thường.&quot;<br />\r\n<br />\r\nĐưa v&agrave;o th&agrave;nh Nam cao ốc thang m&aacute;y từ mấu chốt, từng c&aacute;i khoa trương tin tức ti&ecirc;u đề trực tiếp bật đi ra.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p thấy thế n&agrave;o l&agrave;m sao cảm gi&aacute;c giống như l&agrave; l&agrave;m c&ocirc;ng người kh&ocirc;ng muốn tăng ca bi&ecirc;n ra tới. . .<br />\r\n<br />\r\nCũng kh&ocirc;ng c&oacute; video loại h&igrave;nh cho hắn tham khảo một ch&uacute;t. . .<br />\r\n<br />\r\n&quot;Được rồi, đ&ecirc;m mai đến liền biết rồi.&quot;<br />\r\n<br />\r\nHắn kh&ocirc;ng c&oacute; lại xoắn xu&yacute;t những n&agrave;y truyền thuyết đ&ocirc; thị thật giả.<br />\r\n<br />\r\nThật sự liền phổ cập khoa học, giả liền b&aacute;c bỏ tin đồn.<br />\r\n<br />\r\nĐơn giản ăn điểm t&acirc;m thu thập một ch&uacute;t về sau, L&acirc;m Mục C&aacute;p k&eacute;o l&ecirc;n trong nh&agrave; m&agrave;n cửa.<br />\r\n<br />\r\nLinh dị mỗi ng&agrave;y phơi c&aacute;i hai mươi ph&uacute;t Th&aacute;i Dương ngược lại có th&ecirc;̉ x&uacute;c tiến bọn ch&uacute;ng khỏe mạnh trưởng th&agrave;nh.<br />\r\n<br />\r\nNhưng phơi nhiều kh&ocirc;ng được, nhất l&agrave; trong k&iacute;nh quỷ.<br />\r\n<br />\r\n&quot;Ta đi mua một ch&uacute;t đồ vật.&quot;<br />\r\n<br />\r\nHắn vỗ vỗ giường đối diện tấm gương, nhưng trong k&iacute;nh vẫn kh&ocirc;ng c&oacute; h&igrave;nh ảnh.<br />\r\n<br />\r\nC&ograve;n chưa phải qu&aacute; quen, qu&aacute; xấu hổ.<br />\r\n<br />\r\n&quot;Ai, d&ugrave; sao cũng l&agrave; tiểu c&ocirc; nương.&quot;<br />\r\n<br />\r\nTrước khi ra cửa L&acirc;m Mục C&aacute;p đặc biệt quay đầu nh&igrave;n tho&aacute;ng qua, hắn ch&acirc;n trước vừa đi trong k&iacute;nh tiểu c&ocirc; nương tựu ra đến rồi.<br />\r\n<br />\r\nĐời trước thời điểm đại học bọn họ t&uacute;c x&aacute; trong gương l&agrave; một đại gia , vẫn l&agrave; trước một giới học trưởng lưu lại.<br />\r\n<br />\r\nC&ugrave;ng bọn hắn gặp mặt ng&agrave;y đầu ti&ecirc;n liền c&oacute; thể quen, một lảm nhảm liền c&oacute; thể lảm nhảm đến sau nửa đ&ecirc;m.<br />\r\n<br />\r\nBuổi s&aacute;ng qu&ecirc;n định đồng hồ b&aacute;o thức c&ograve;n c&oacute; thể g&agrave;o to một cuống họng nhắc nhở ngươi, tốt nghiệp về sau tất cả mọi người c&ograve;n c&oacute; ch&uacute;t kh&ocirc;ng bỏ.<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p c&ograve;n nghe n&oacute;i chợ đen b&ecirc;n trong c&oacute; b&aacute;n loại kia t&igrave;nh th&uacute; tấm gương.<br />\r\n<br />\r\nB&ecirc;n trong ở c&aacute;i g&igrave; JK thiếu nữ thanh thuần Loli c&aacute;c loại, v&igrave; thỏa m&atilde;n một ch&uacute;t đặc th&ugrave; XP c&ograve;n c&oacute; c&aacute;i g&igrave; bạch tuộc nương Cthulhu phong c&aacute;ch.<br />\r\n<br />\r\nD&ugrave; sao ban đ&ecirc;m đặt ở giường đối diện, trong gương b&agrave;y biện ra tới ch&iacute;nh l&agrave; độc th&acirc;n b&ecirc;n cạnh ngươi nằm một &iacute;t mềm mại tiểu khả &aacute;i.<br />\r\n<br />\r\nChủ yếu l&agrave; c&oacute; thể thu được tr&ecirc;n tinh thần an ủi.<br />\r\n<br />\r\n&quot;Tiểu L&acirc;m, bồi th&uacute;c tiếp theo bàn kh&ocirc;ng?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng được kh&ocirc;ng được, b&ecirc;n dưới bất qu&aacute; ng&agrave;i a.&quot;<br />\r\n<br />\r\n&quot;Buổi tối tới thẩm nhi nh&agrave; ăn cơm kh&ocirc;ng?&quot;<br />\r\n<br />\r\n&quot;H&ocirc;m n&agrave;o đi, h&ocirc;m n&agrave;o cho ng&agrave;i ch&aacute;u trai cả mua c&aacute;i Ultraman tấm thẻ.&quot;<br />\r\n<br />\r\n&quot;Đến Tiểu L&acirc;m, vừa tẩy lớn quả mận!&quot;<br />\r\n<br />\r\n&quot;Cảm ơn Trương th&uacute;c!&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p tiếp nhận ăn một miếng, rất ngọt.<br />\r\n<br />\r\nHắn tới đ&acirc;y c&aacute;i cư x&aacute; bất tri bất gi&aacute;c vậy năm s&aacute;u năm, c&ugrave;ng trong khu cư x&aacute; c&aacute;c vị c&aacute;c &ocirc;ng c&aacute;c b&agrave; ở giữa vậy th&acirc;n quen.<br />\r\n<br />\r\nThứ bảy chủ nhật kh&ocirc;ng c&oacute; chuyện tản bộ thời điểm cũng c&oacute; thể gi&uacute;p nh&igrave;n xem h&agrave;i tử.<br />\r\n<br />\r\nSinh hoạt mặc d&ugrave; kh&ocirc;ng gi&agrave;u c&oacute;, nhưng L&acirc;m Mục C&aacute;p lại cảm thấy rất ấm &aacute;p h&agrave;i l&ograve;ng.<br />\r\n<br />\r\n&quot;Những n&agrave;y thịt tươi thả tầm v&agrave;i ng&agrave;y liền c&oacute; thể ăn.&quot;<br />\r\n<br />\r\nCửa tiểu khu si&ecirc;u thị nhi b&ecirc;n trong, hắn chọn hai c&acirc;n tươi mới thịt heo.<br />\r\n<br />\r\nChờ c&oacute; ch&uacute;t ph&aacute;t mục n&aacute;t liền c&oacute; thể đ&uacute;t cho ng&agrave;n tay.<br />\r\n<br />\r\n&quot;Tiểu c&ocirc; nương lời n&oacute;i, mua c&aacute;i lớn l&ocirc;ng nhung đồ chơi đi.&quot;<br />\r\n<br />\r\nTrước khi đi hắn lại mua c&aacute;i gấu tr&uacute;c lớn gối &ocirc;m, xem ra cũng rất đ&aacute;ng y&ecirc;u.<br />\r\n<br />\r\nCả ng&agrave;y h&ocirc;m qua trực tiếp tiền, c&ograve;n k&eacute;m kh&ocirc;ng nhiều có th&ecirc;̉ tr&ecirc;n đỉnh trước kia gần nửa th&aacute;ng.<br />\r\n<br />\r\n&quot;Đến! Nh&igrave;n ta một ch&uacute;t mua cho ngươi c&aacute;i g&igrave;!&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p sau khi về nh&agrave; c&ugrave;ng ng&agrave;n tay đ&aacute;nh c&aacute;i chưởng, sau đ&oacute; bưng lấy gấu tr&uacute;c gối &ocirc;m đặt ở trước gương.<br />\r\n<br />\r\nHắn vừa mới bu&ocirc;ng xuống, trong k&iacute;nh mặc cả người trắng v&aacute;y tiểu c&ocirc; nương lại đột nhi&ecirc;n xuất hiện &ocirc;m đi gấu tr&uacute;c gối &ocirc;m ảnh trong gương.<br />\r\n<br />\r\nNh&igrave;n L&acirc;m Mục C&aacute;p liếc mắt sau lập tức lại biến mất kh&ocirc;ng gặp.<br />\r\n<br />\r\n&quot;Đừng a, tr&ograve; chuyện hai c&acirc;u chứ sao.&quot;<br />\r\n<br />\r\n&quot;Ta c&ograve;n kh&ocirc;ng biết ngươi gọi c&aacute;i g&igrave; đ&acirc;u.&quot;<br />\r\n<br />\r\n&quot;Ngươi l&uacute;c n&agrave;o chết?&quot;<br />\r\n<br />\r\nL&acirc;m Mục C&aacute;p cười khổ một c&aacute;i.<br />\r\n<br />\r\nHắn đem trong k&iacute;nh quỷ mang về ch&iacute;nh l&agrave; kh&ocirc;ng c&oacute; chuyện t&acirc;m sự d&ugrave;ng.<br />\r\n<br />\r\nC&aacute;i n&agrave;y lại la &oacute;, c&ugrave;ng c&aacute;i c&acirc;y xấu hổ đồng dạng.<br />\r\n<br />\r\n&quot;Ừm?&quot;<br />\r\n<br />\r\nGiống như l&agrave; cảm nhận được L&acirc;m Mục C&aacute;p trong giọng n&oacute;i bất đắc dĩ, tr&ecirc;n mặt k&iacute;nh ban ng&agrave;y chậm r&atilde;i nổi l&ecirc;n một tầng sương m&ugrave;.<br />\r\n<br />\r\nTrong sương m&ugrave; tiểu nữ h&agrave;i &ocirc;m gấu tr&uacute;c nhỏ th&acirc;n ảnh mơ hồ dần dần xuất hiện.<br />\r\n<br />\r\nN&agrave;ng quỳ tr&ecirc;n mặt đất duỗi ra một ng&oacute;n tay, tại mặt k&iacute;nh sương m&ugrave; b&ecirc;n tr&ecirc;n viết xuống t&ecirc;n của m&igrave;nh.<br />\r\n<br />\r\nBởi v&igrave; n&agrave;ng l&agrave; ở trong k&iacute;nh nguy&ecirc;n nh&acirc;n, sở dĩ viết chữ l&agrave; ảnh trong gương.<br />\r\n<br />\r\n&quot;T&ocirc; Tiểu Nhữ, vậy sau n&agrave;y gọi ngươi Tiểu Nhữ đi.&quot;<br />\r\n<br />\r\nT&ocirc; Tiểu Nhữ kh&ocirc;ng c&oacute; trả lời.<br />\r\n<br />\r\nTheo n&agrave;ng ảnh trong gương biến mất, mặt k&iacute;nh sương m&ugrave; vậy chậm r&atilde;i ti&ecirc;u t&aacute;n.</p>', '2021-11-28 16:05:30', 7, 1),
(232, 61, '300 năm tân thủ bảo hộ kỳ', '1', '<p>Đại thụ che trời, che đậy &aacute;nh nắng.<br />\r\n<br />\r\nTh&ocirc;i Hằng nh&igrave;n xem chung quanh cao cỡ một người &quot;Cỏ dại&quot;, chỉ cảm thấy trong l&ograve;ng c&oacute; một vạn đầu Tiểu Lộc tại đi loạn.<br />\r\n<br />\r\nM&igrave;nh mười ph&uacute;t trước đang ở nh&agrave; b&ecirc;n trong trải qua đ&aacute;nh tr&ograve; chơi ăn thức ăn ngo&agrave;i thần ti&ecirc;n thời gian.<br />\r\n<br />\r\nL&agrave;m sao bỗng nhi&ecirc;n liền xuy&ecirc;n qua đến như thế một c&aacute;i rừng rậm nguy&ecirc;n thủy b&ecirc;n trong? !<br />\r\n<br />\r\nMặc d&ugrave; m&igrave;nh l&agrave; c&ocirc; nhi, kh&ocirc;ng c&oacute; g&igrave; lo lắng, thế nhưng l&agrave;. . .<br />\r\n<br />\r\n&laquo; Quỷ Cốc B&aacute;t Hoang &raquo; nhất t&ocirc;ng m&ocirc;n mới phi&ecirc;n bản c&ograve;n kh&ocirc;ng c&oacute; chơi đến đ&acirc;y!<br />\r\n<br />\r\n&laquo; Nguy&ecirc;n thần &raquo; cũng c&ograve;n kh&ocirc;ng c&oacute; r&uacute;t đến l&atilde;o b&agrave; đ&acirc;y!<br />\r\n<br />\r\nNgay tại c&aacute;i n&agrave;y thời điểm, hắn chợt nghe &mdash;&mdash;<br />\r\n<br />\r\n【 đinh! 】<br />\r\n<br />\r\n【 ch&uacute;c mừng ng&agrave;i! Đ&atilde; thức tỉnh to&agrave;n năng tu ti&ecirc;n hệ thống, tận tuỵ v&igrave; ng&agrave;i phục vụ, v&igrave; ng&agrave;i con đường trường sinh hộ gi&aacute; hộ tống! 】<br />\r\n<br />\r\nHệ thống!<br />\r\n<br />\r\nTh&ocirc;i Hằng nh&atilde;n t&igrave;nh s&aacute;ng l&ecirc;n, vui vẻ ra mặt.<br />\r\n<br />\r\nXuy&ecirc;n qua quả nhi&ecirc;n tự mang kim thủ chỉ!<br />\r\n<br />\r\nĐiểm xuất ph&aacute;t thật kh&ocirc;ng lừa ta!<br />\r\n<br />\r\nKh&ocirc;ng uổng c&ocirc;ng ta l&agrave; ủng hộ ch&iacute;nh bản đặt mua cao cấp VIP.<br />\r\n<br />\r\n&quot;Hệ thống, ngươi c&oacute; c&aacute;i g&igrave; c&ocirc;ng. . .&quot;<br />\r\n<br />\r\nTh&ocirc;i Hằng đang định hỏi thăm hệ thống c&ocirc;ng năng, trước mắt chợt lo&eacute; l&ecirc;n hồng quang.<br />\r\n<br />\r\n【 cảnh b&aacute;o! Cảnh b&aacute;o!<br />\r\n<br />\r\n【 kiểm trắc đến đ&acirc;y phương thế giới l&agrave; cao cấp ti&ecirc;n hiệp thời kh&ocirc;ng, Ti&ecirc;n Vương khắp nơi tr&ecirc;n đất, hung th&uacute; v&ocirc; số, ng&agrave;i ho&agrave;n cảnh lớn l&ecirc;n cực kỳ &aacute;c liệt!<br />\r\n<br />\r\n【 v&igrave; cam đoan ng&agrave;i khỏe mạnh trưởng th&agrave;nh, hiện hữu một phần t&acirc;n thủ bảo hộ ph&uacute;c lợi đợi nhận lấy!<br />\r\n<br />\r\n【 xin hỏi ng&agrave;i c&oacute; phải kh&ocirc;ng nhận lấy? 】<br />\r\n<br />\r\nB&eacute;n nhọn c&ograve;i b&aacute;o động ch&oacute;i tai vang l&ecirc;n.<br />\r\n<br />\r\nTh&ocirc;i Hằng lập tức một mặt mộng bức, lập tức liền vội v&agrave;ng gật đầu, &quot;Nhận lấy, nhất định phải nhận lấy!&quot;<br />\r\n<br />\r\nN&oacute;i đ&ugrave;a!<br />\r\n<br />\r\nCao cấp ti&ecirc;n hiệp thời kh&ocirc;ng a!</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nTi&ecirc;n Vương khắp nơi tr&ecirc;n đất a!<br />\r\n<br />\r\nKia thế nhưng l&agrave; Ti&ecirc;n Vương a!<br />\r\n<br />\r\nL&agrave; g&aacute;nh v&aacute;c Thi&ecirc;n Uy&ecirc;n, cần một tay n&acirc;ng Nguy&ecirc;n Thủy đế th&agrave;nh, đều c&oacute; thể v&ocirc; địch tại thế gian tồn tại!<br />\r\n<br />\r\nM&igrave;nh loại thức ăn n&agrave;y g&agrave; ph&agrave;m nh&acirc;n, tại loại nguy hiểm n&agrave;y thế giới chỉ sợ liền l&agrave;m bia đỡ đạn tư c&aacute;ch đều kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nNếu như kh&ocirc;ng c&oacute; t&acirc;n thủ ph&uacute;c lợi, tuyệt đối l&agrave; sẽ l&agrave;m chết người!<br />\r\n<br />\r\n【 ch&uacute;c mừng ng&agrave;i! Thu hoạch được t&acirc;n thủ bảo hộ ph&uacute;c lợi *8!<br />\r\n<br />\r\n【 t&acirc;n thủ kh&ocirc;ng gian: C&oacute; được ph&ograve;ng ngự tuyệt đối độc lập tiểu thế giới, sẽ tự động du tẩu tại chư thi&ecirc;n vạn giới kẽ hở b&ecirc;n trong, tr&aacute;nh cho bị đại năng giả ph&aacute;t hiện, ở chỗ n&agrave;y ng&agrave;i sẽ kh&ocirc;ng nhận bất cứ thương tổn g&igrave;.<br />\r\n<br />\r\n【 t&acirc;n thủ ph&ograve;ng nhỏ: Một t&ograve;a ba tầng biệt thự, mang một c&aacute;i lớn vườn hoa, tổng diện t&iacute;ch m&ocirc;̣t ngàn b&igrave;nh, cũng tặng k&egrave;m tr&ugrave;ng tu sạch sẽ c&ugrave;ng nguy&ecirc;n bộ đồ d&ugrave;ng trong nh&agrave; c&ugrave;ng đồ điện gia dụng, tự động cung cấp điện, sẽ kh&ocirc;ng cắt điện.<br />\r\n<br />\r\n【 t&acirc;n thủ n&ocirc;ng trường: Tự động sản xuất c&aacute;c thức c&acirc;y n&ocirc;ng nghiệp, mỗi ng&agrave;y c&oacute; thể căn cứ ng&agrave;i y&ecirc;u th&iacute;ch sản xuất kh&aacute;c biệt lương thực, rau quả, loại thịt c&aacute;c loại .<br />\r\n<br />\r\n【 t&acirc;n thủ lực sĩ: C&oacute; yếu ớt ph&aacute;p lực sơ cấp Ho&agrave;ng C&acirc;n lực sĩ, am hiểu di chuyển vật nặng, đ&aacute;nh x&aacute;m ph&aacute; loại sơn l&oacute;t, cọ nồi rửa ch&eacute;n l&agrave;m đồ ăn c&aacute;c loại cũng c&oacute; thể đảm nhiệm, đối với ng&agrave;i n&oacute;i g&igrave; nghe nấy.<br />\r\n<br />\r\n【 mới thủ ph&aacute;p b&agrave;o: C&oacute; yếu ớt ph&aacute;p lực đạo b&agrave;o, đ&ocirc;ng ấm h&egrave; m&aacute;t, khu muỗi tr&aacute;nh bụi, c&oacute; thể t&ugrave;y &yacute; biến h&oacute;a quần &aacute;o kiểu d&aacute;ng.<br />\r\n<br />\r\n【 t&acirc;n thủ phi kiếm: C&oacute; yếu ớt ph&aacute;p lực phi kiếm, c&acirc;̀n Luyện Kh&iacute; ba tầng mới c&oacute; thể th&ocirc;i động, c&oacute; thể d&ugrave;ng đến chẻ củi đốn c&acirc;y, th&aacute;i thịt gọt hoa quả.<br />\r\n<br />\r\n【 t&acirc;n thủ tĩnh thất: C&oacute; yếu ớt ph&aacute;p lực tĩnh thất, ở b&ecirc;n trong tu luyện c&oacute; thể tăng l&ecirc;n 10% tốc độ tu luyện.<br />\r\n<br />\r\n【 T&acirc;n Thủ Ti&ecirc;n Ph&aacute;p: C&oacute; yếu ớt thần dị ti&ecirc;n ph&aacute;p, c&oacute; thể ho&agrave;n th&agrave;nh tương đối cơ sở tu luyện. 】<br />\r\n<br />\r\nLi&ecirc;n tiếp tiếng nhắc nhở vang l&ecirc;n.<br />\r\n<br />\r\nTh&ocirc;i Hằng chỉ cảm thấy trước mắt m&igrave;nh c&oacute; từng đạo kim quang hiện l&ecirc;n, chung quanh đại địa bắt đầu lay động.<br />\r\n<br />\r\nC&ugrave;ng l&uacute;c đ&oacute;, một tầng m&agrave;n &aacute;nh s&aacute;ng m&agrave;u v&agrave;ng &oacute;ng m&oacute;c ngược xuống tới, đem mảnh n&agrave;y tr&agrave;n đầy rừng rậm nguy&ecirc;n thủy khu vực ho&agrave;n to&agrave;n bao phủ, cũng ngưng tụ th&agrave;nh một vi&ecirc;n mặt trời nhỏ c&ugrave;ng một vi&ecirc;n mặt trăng nhỏ treo ở tr&ecirc;n trời, tạo th&agrave;nh một phương độc lập tiểu thế giới.<br />\r\n<br />\r\nNgay sau đ&oacute;, hắn ph&aacute;t hiện y phục của m&igrave;nh cũng thay đổi, từ nguy&ecirc;n bản lớn quần cộc ngắn tay, biến th&agrave;nh hoa lệ ph&aacute;p b&agrave;o, ph&iacute;a tr&ecirc;n c&ograve;n mang theo đủ mọi m&agrave;u sắc Quang hiệu, mười phần khốc huyễn.<br />\r\n<br />\r\nRộng lượng trong tay &aacute;o c&ograve;n đặt v&agrave;o một thanh d&agrave;i 10 cm đao nhỏ c&ugrave;ng một bản thư tịch, hẳn l&agrave; t&acirc;n thủ phi kiếm c&ugrave;ng T&acirc;n Thủ Ti&ecirc;n Ph&aacute;p.<br />\r\n<br />\r\nẦm ầm! !<br />\r\n<br />\r\nNương theo lấy từng tiếng tiếng vang, ph&iacute;a trước m&ocirc;̣t ngàn b&igrave;nh rừng rậm nguy&ecirc;n thủy bị san th&agrave;nh b&igrave;nh địa,<br />\r\n<br />\r\nMột t&ograve;a ba tầng vườn hoa biệt thự trong khoảnh khắc đột ngột từ mặt đất mọc l&ecirc;n, c&aacute;ch đ&oacute; kh&ocirc;ng xa n&ocirc;ng trường cũng kiến tạo xong xu&ocirc;i.<br />\r\n<br />\r\nẦm!<br />\r\n<br />\r\nCao chừng ba m&eacute;t cự h&igrave;nh tr&aacute;ng h&aacute;n từ tr&ecirc;n trời gi&aacute;ng xuống.<br />\r\n<br />\r\nHắn một th&acirc;n m&agrave;u v&agrave;ng trang phục, một mực cung k&iacute;nh quỳ một gối xuống tại Th&ocirc;i Hằng trước mặt, &ocirc;m quyền h&agrave;nh lễ, &quot;B&aacute;i kiến Ti&ecirc;n T&ocirc;n!&quot;<br />\r\n<br />\r\nNgắn ngủi kh&ocirc;ng đến mười gi&acirc;y đồng hồ thời gian, Th&ocirc;i Hằng trước mắt cũng đ&atilde; l&agrave; long trời lở đất.<br />\r\n<br />\r\nThấy hắn trợn mắt hốc mồm, trong l&ograve;ng gọi thẳng ngưu oa!<br />\r\n<br />\r\nBất qu&aacute; hắn rất nhanh lại ph&aacute;t hiện những n&agrave;y t&acirc;n thủ ph&uacute;c lợi ch&uacute; th&iacute;ch giới thiệu.<br />\r\n<br />\r\n【 ch&uacute; th&iacute;ch: Trở l&ecirc;n ph&uacute;c lợi đều l&agrave; t&acirc;n thủ hạn định, thời hạn c&oacute; hiệu lực ba trăm năm.<br />\r\n<br />\r\n【 như vượt qua t&acirc;n thủ kỳ sau c&ograve;n muốn tiếp tục c&oacute; được, cần tiến h&agrave;nh tương ứng nạp ti&ecirc;̀n tục ph&iacute;.<br />\r\n<br />\r\n【 nạp ti&ecirc;̀n con đường sẽ tại ng&agrave;i vượt qua t&acirc;n thủ kỳ sau khai th&ocirc;ng ~? ~ 】<br />\r\n<br />\r\nKh&ocirc;ng hợp th&oacute;i thường!<br />\r\n<br />\r\nTh&ocirc;i Hằng trước mắt kh&ocirc;ng tự chủ được nổi l&ecirc;n c&aacute;i n&agrave;o đ&oacute; đeo k&iacute;nh trung ni&ecirc;n nam nh&acirc;n c&ugrave;ng một c&aacute;i cười tủm tỉm chim c&aacute;nh cụt.<br />\r\n<br />\r\nQua tốt một một l&aacute;t, nội t&acirc;m của hắn mới rốt cục b&igrave;nh tĩnh lại.<br />\r\n<br />\r\nBắt đầu suy nghĩ cuộc sống sau n&agrave;y.<br />\r\n<br />\r\n&quot;Đ&atilde; đều xuy&ecirc;n qua đến nguy hiểm như vậy thế giới, tu ti&ecirc;n l&agrave; nhất định phải tu ti&ecirc;n, kh&ocirc;ng phải ba trăm năm tho&aacute;ng qua một c&aacute;i chỉ sợ cũng muốn l&agrave;m trận chết bất đắc kỳ tử. H&agrave;ng đầu mục ti&ecirc;u vẫn l&agrave; phải mau ch&oacute;ng tăng l&ecirc;n m&igrave;nh tu ti&ecirc;n cảnh giới.<br />\r\n<br />\r\n&quot;Ba trăm năm thời gian kh&ocirc;ng n&oacute;i th&agrave;nh ti&ecirc;n th&agrave;nh Phật, tối thiểu cũng hẳn l&agrave; tu luyện tới Phản Hư, Hợp Đạo c&aacute;i g&igrave; đi, thực sự kh&ocirc;ng được, H&oacute;a Thần cũng được, nếu như l&agrave; tại thế gian vị diện, hẳn l&agrave; cũng c&oacute; nhất định năng lực tự vệ.&quot;<br />\r\n<br />\r\nThế l&agrave;, &ocirc;m vươn l&ecirc;n h&ugrave;ng mạnh t&acirc;m th&aacute;i, Th&ocirc;i Hằng đi v&agrave;o biệt thự.<br />\r\n<br />\r\nHắn kh&ocirc;ng nh&igrave;n tr&agrave;n đầy Cocacola c&ugrave;ng m&oacute;n điểm t&acirc;m ngọt tủ lạnh, c&ugrave;ng đỉnh phối m&aacute;y t&iacute;nh, trực tiếp đi trước tầng cao nhất gian kia tu luyện d&ugrave;ng tĩnh thất.<br />\r\n<br />\r\nTừ trong tay &aacute;o lấy ra quyển kia &laquo; T&acirc;n Thủ Ti&ecirc;n Ph&aacute;p &raquo;, cẩn thận đọc.<br />\r\n<br />\r\nT&ecirc;n đầy đủ &laquo; T&acirc;n Thủ Ti&ecirc;n Ph&aacute;p &middot; Từ Nhập M&ocirc;n Đến Thăng Thi&ecirc;n &raquo;.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; một bộ chỉ c&oacute; phương ph&aacute;p tu luyện, nhưng kh&ocirc;ng c&oacute; một ch&uacute;t x&iacute;u ph&aacute;p thuật thần th&ocirc;ng tu ti&ecirc;n c&ocirc;ng ph&aacute;p.<br />\r\n<br />\r\nDanh ph&ugrave; kỳ thực t&acirc;n thủ &quot;Gi&aacute;o tr&igrave;nh&quot; .<br />\r\n<br />\r\nBất qu&aacute;, mặc d&ugrave; chỉ l&agrave; t&acirc;n thủ gi&aacute;o tr&igrave;nh, nhưng cũng bao gồm Luyện Kh&iacute;, Tr&uacute;c Cơ, Kim Đan, Nguy&ecirc;n Anh, H&oacute;a Thần, Phản Hư, Hợp Đạo, Độ Kiếp, Đại Thừa tất cả tu luyện tr&igrave;nh tự!<br />\r\n<br />\r\n&quot;Kh&ocirc;ng hổ l&agrave; cao cấp ti&ecirc;n hiệp thời kh&ocirc;ng, nh&igrave;n bộ dạng n&agrave;y l&agrave; muốn tu luyện th&agrave;nh ti&ecirc;n mới t&iacute;nh ho&agrave;n th&agrave;nh t&acirc;n thủ kỳ a!&quot; Th&ocirc;i Hằng trong l&ograve;ng mười phần rung động, với b&ecirc;n ngo&agrave;i thế giới cũng c&agrave;ng ph&aacute;t ra e ngại.<br />\r\n<br />\r\nC&ograve;n tốt c&oacute; t&acirc;n thủ kh&ocirc;ng gian, nếu kh&ocirc;ng m&igrave;nh nếu l&agrave; b&ecirc;n ngo&agrave;i lắc lư, người ta thổi khẩu kh&iacute; liền c&oacute; thể để cho m&igrave;nh h&ocirc;i phi y&ecirc;n diệt.<br />\r\n<br />\r\nC&aacute;i n&agrave;y khiến hắn v&ocirc; c&ugrave;ng kh&aacute;t vọng mạnh l&ecirc;n.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nThế l&agrave;, tại khắc khổ tu luyện mười ph&uacute;t về sau, hắn duỗi ra lưng mỏi, ng&aacute;p một c&aacute;i, &quot;Ai nha, ng&agrave;y đầu ti&ecirc;n tựa hồ cũng kh&ocirc;ng cần thiết mệt mỏi như vậy, c&ograve;n c&oacute; ba trăm năm đ&acirc;y. Đi trước nh&igrave;n xem bộ kia đỉnh phối m&aacute;y t&iacute;nh.&quot;<br />\r\n<br />\r\nSau đ&oacute; liền để xuống quyển kia &laquo; T&acirc;n Thủ Ti&ecirc;n Ph&aacute;p &middot; Từ Nhập M&ocirc;n Đến Thăng Thi&ecirc;n &raquo;, cũng nhẹ nh&agrave;ng vỗ vỗ trang b&igrave;a, cười n&oacute;i: &quot;Hắc hắc, lần sau nhất định.&quot;<br />\r\n<br />\r\nPhốc phốc!<br />\r\n<br />\r\nKh&iacute; thể x&ocirc;ng ra b&igrave;nh thanh &acirc;m tại trong biệt thự vang l&ecirc;n.<br />\r\n<br />\r\nTh&ocirc;i Hằng mở ra một b&igrave;nh ướp lạnh Fat Nerd Merry Drink, nằm ở nh&acirc;n thể c&ocirc;ng học tr&ecirc;n ghế, thuần thục d&ugrave;ng ch&acirc;n theo mở m&aacute;y t&iacute;nh chốt mở.<br />\r\n<br />\r\nĐầy c&otilde;i l&ograve;ng chờ mong!<br />\r\n<br />\r\n&quot;WIN 10 hệ thống ngay tại đổi mới. . . 0.11%. . . 0. 12%. . . 0. 121%. . .&quot;<br />\r\n<br />\r\nCỏ!<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nThời gian nho&aacute;ng l&ecirc;n liền đ&atilde; qua mười năm.<br />\r\n<br />\r\nTh&ocirc;i Hằng tại qu&aacute; tr&igrave;nh tu luyện b&ecirc;n trong, rốt cục minh bạch c&aacute;i g&igrave; gọi l&agrave; người kh&ocirc;ng thể qu&aacute; để &yacute; m&igrave;nh.<br />\r\n<br />\r\nMười năm!<br />\r\n<br />\r\nHắn mới tu luyện tới Luyện Kh&iacute; s&aacute;u tầng!<br />\r\n<br />\r\nDựa theo tiến độ n&agrave;y, ba trăm năm thời gian, đừng n&oacute;i th&agrave;nh ti&ecirc;n th&agrave;nh Phật, Phản Hư Hợp Đạo, có th&ecirc;̉ kết th&agrave;nh Kim Đan liền kh&ocirc;ng tệ.<br />\r\n<br />\r\n&quot;Ba trăm năm về sau, ta một c&aacute;i Kim Đan kỳ s&acirc;u kiến, liền muốn bước v&agrave;o c&aacute;i n&agrave;y Ti&ecirc;n Vương khắp nơi tr&ecirc;n đất thế giới sao?&quot; Th&ocirc;i Hằng trong l&ograve;ng buồn b&atilde; v&ocirc; c&ugrave;ng tận, buồn từ đ&oacute; tới.<br />\r\n<br />\r\nHắn cũng chỉ c&oacute; thể nhận r&otilde; hiện thực, m&igrave;nh cũng kh&ocirc;ng phải l&agrave; c&aacute;i g&igrave; Hoang Cổ Th&aacute;nh Thể, Ti&ecirc;n Thi&ecirc;n Đạo Thai, v&ocirc; địch Thần thể loại h&igrave;nh thi&ecirc;n ki&ecirc;u.<br />\r\n<br />\r\nChỉ l&agrave; một c&aacute;i tư chất người b&igrave;nh thường.<br />\r\n<br />\r\nThế l&agrave;, hắn tiếp tục duy tr&igrave; mỗi ng&agrave;y s&aacute;ng sớm, tu luyện, thu đồ ăn, để Ho&agrave;ng C&acirc;n lực sĩ nấu cơm, ăn cơm, đ&aacute;nh tr&ograve; chơi uống Cocacola, tu luyện sinh hoạt.<br />\r\n<br />\r\nNhưng lại tại n&agrave;o đ&oacute; một ng&agrave;y, Th&ocirc;i Hằng tại n&ocirc;ng trường thu m&oacute;n ăn thời điểm, chợt thấy tr&ecirc;n trời rơi xuống tới một c&aacute;i bẩn thỉu th&acirc;n ảnh.<br />\r\n<br />\r\nĐ&uacute;ng l&uacute;c nện tr&uacute;ng ở h&ocirc;m nay vừa sản xuất ra tr&acirc;u khối cơ thịt b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nHắn tại chỗ liền mộng.<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave; t&igrave;nh huống, tại sao c&oacute; thể c&oacute; người tiến đến? ! ! ! !&quot;</p>', '2021-11-28 16:42:18', 17, 1),
(233, 61, 'Ngộ nhập đào nguyên, nhìn thấy Tiên Tôn', '2', '<p>&quot;Hệ thống! Đ&acirc;y l&agrave; c&oacute; chuyện g&igrave;, v&igrave; sao lại c&oacute; người tiến đến? !&quot;<br />\r\n<br />\r\nTh&ocirc;i Hằng ở trong l&ograve;ng k&ecirc;u gọi hệ thống.<br />\r\n<br />\r\nHắn hoảng đ&ecirc;́n một nh&oacute;m.<br />\r\n<br />\r\nD&ugrave; sao, căn cứ hệ thống l&uacute;c trước kiểm trắc, thế giới n&agrave;y thế nhưng l&agrave; cao cấp ti&ecirc;n hiệp thời kh&ocirc;ng.<br />\r\n<br />\r\nTi&ecirc;n Vương khắp nơi tr&ecirc;n đất, hung th&uacute; v&ocirc; số!<br />\r\n<br />\r\nLiền xem như chỉ biết r&otilde; uống th&uacute; sữa tiểu h&agrave;i tử tiện tay đều c&oacute; mấy trăm vạn c&acirc;n lực lượng.<br />\r\n<br />\r\nMặc d&ugrave; vừa rồi c&aacute;i n&agrave;y từ tr&ecirc;n trời gi&aacute;ng xuống th&acirc;n ảnh nh&igrave;n chỉ l&agrave; c&aacute;i mười hai mười ba tuổi tiểu nam h&agrave;i, nhưng người n&agrave;o c&oacute; thể bảo chứng hắn kh&ocirc;ng c&oacute; lực lượng kinh thi&ecirc;n động địa?<br />\r\n<br />\r\nVạn nhất l&agrave; c&aacute;i g&igrave; Thi&ecirc;n Đế chi tử hạ giới trang bức. . . Kh&ocirc;ng, lịch luyện đ&acirc;y?<br />\r\n<br />\r\nM&igrave;nh l&uacute;c n&agrave;y mới Luyện Kh&iacute; s&aacute;u tầng tiểu th&acirc;n thể, tuyệt đối g&aacute;nh kh&ocirc;ng được a!<br />\r\n<br />\r\n【 ng&agrave;i tốt! To&agrave;n năng tu ti&ecirc;n hệ thống tận tuỵ v&igrave; ng&agrave;i phục vụ, v&igrave; ng&agrave;i con đường trường sinh hộ gi&aacute; hộ tống! 】<br />\r\n<br />\r\n【 bởi v&igrave; t&acirc;n thủ kh&ocirc;ng gian du tẩu c&ugrave;ng chư thi&ecirc;n vạn giới kẽ hở, thường xuy&ecirc;n sẽ c&ugrave;ng kh&aacute;c biệt thi&ecirc;n địa vị diện tiếp x&uacute;c, bộ phận vị diện kh&ocirc;ng gian quy tắc kh&ocirc;ng ổn định, liền sẽ ph&aacute;t sinh c&oacute; sinh linh xuy&ecirc;n qua kẽ hở rơi xuống tiến t&acirc;n thủ kh&ocirc;ng gian hiện tượng.<br />\r\n<br />\r\n【 loại n&agrave;y ngẫu nhi&ecirc;n ly khai nguy&ecirc;n bản thi&ecirc;n địa vị diện sinh linh lại nhận nguy&ecirc;n bản thi&ecirc;n địa trở về lực hấp dẫn, nhiều nhất mười hai ng&agrave;y sau liền sẽ ly khai, trở về bọn hắn thế giới cũ. 】<br />\r\n<br />\r\n&quot;Nguy&ecirc;n lai l&agrave; dạng n&agrave;y.&quot;<br />\r\n<br />\r\nTh&ocirc;i Hằng nghe được hệ thống giải th&iacute;ch về sau, thở ph&agrave;o một hơi, y&ecirc;n l&ograve;ng.<br />\r\n<br />\r\nLiền xem như cao cấp ti&ecirc;n hiệp thời kh&ocirc;ng, khẳng định cũng sẽ tồn tại c&acirc;́p th&acirc;́p vị diện.<br />\r\n<br />\r\nTỉ như c&aacute;i g&igrave; thế gian, nh&acirc;n gian loại h&igrave;nh.<br />\r\n<br />\r\nN&oacute;i như vậy, loại n&agrave;y c&acirc;́p th&acirc;́p vị diện kh&ocirc;ng gian hơn ph&acirc;n nửa l&agrave; chưa vững chắc.<br />\r\n<br />\r\nĐọc đủ thứ tiểu thuyết hắn đối với mấy c&aacute;i n&agrave;y m&ocirc;n thanh.<br />\r\n<br />\r\nM&agrave; lại, t&acirc;m t&igrave;nh bu&ocirc;ng lỏng về sau, hắn lại &yacute; thức được, nơi n&agrave;y thế nhưng l&agrave; t&acirc;n thủ kh&ocirc;ng gian, m&igrave;nh ở chỗ n&agrave;y sẽ kh&ocirc;ng nhận bất cứ thương tổn g&igrave;.<br />\r\n<br />\r\nCoi như rơi v&agrave;o đến thật đại l&atilde;o , c&oacute; vẻ như cũng kh&ocirc;ng c&oacute; vấn đề g&igrave;.<br />\r\n<br />\r\nNghĩ minh bạch về sau, Th&ocirc;i Hằng t&acirc;m t&igrave;nh liền triệt để bu&ocirc;ng lỏng.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nHắn đi v&agrave;o n&ocirc;ng trường cẩn thận quan s&aacute;t c&aacute;i n&agrave;y từ tr&ecirc;n trời gi&aacute;ng xuống th&acirc;n ảnh.<br />\r\n<br />\r\nĐ&oacute; l&agrave; c&aacute;i nh&igrave;n chỉ c&oacute; mười hai mười ba tuổi tiểu nam h&agrave;i, tướng mạo phổ th&ocirc;ng, quần &aacute;o tả tơi, l&agrave;n da ngăm đen, gầy khọm, đều c&oacute; ch&uacute;t da bọc xương.<br />\r\n<br />\r\nTại tr&ecirc;n đầu của hắn b&ecirc;n cạnh c&ograve;n điểm xi&ecirc;u xi&ecirc;u vẹo vẹo giới ba, thoạt nh&igrave;n l&agrave; c&aacute;i tiểu sa di.<br />\r\n<br />\r\nNhưng cũng đ&atilde; lớn kh&ocirc;ng &iacute;t t&oacute;c.<br />\r\n<br />\r\n&quot;Đ&oacute;i, thật đ&oacute;i, thật đ&oacute;i a. . .&quot; Tiểu nam h&agrave;i b&ecirc;n trong miệng ph&aacute;t ra v&ocirc; &yacute; thức nỉ non &acirc;m thanh, th&acirc;n thể cuộn m&igrave;nh bắt đầu, run nh&egrave; nhẹ.<br />\r\n<br />\r\n&quot;. . .&quot; Th&ocirc;i Hằng hơi trầm mặc, thở d&agrave;i n&oacute;i: &quot;Ai k&ecirc;u ta thiện t&acirc;m đ&acirc;y, liền cứu ngươi một mạng đi.&quot;<br />\r\n<br />\r\nTự m&igrave;nh một người ở lại đ&acirc;y mười năm, qu&aacute;i c&ocirc; độc.<br />\r\n<br />\r\nKh&oacute; được có th&ecirc;̉ c&oacute; c&aacute;i người n&oacute;i chuyện.<br />\r\n<br />\r\nCũng kh&ocirc;ng d&ecirc;̃ dàng.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nHồng Ph&uacute; Qu&yacute; kh&oacute; khăn mở mắt, sau đ&oacute; lại lập tức nhắm lại.<br />\r\n<br />\r\nBởi v&igrave;, hắn vừa &yacute; mới c&oacute; một đo&agrave;n ngay tại ph&oacute;ng th&iacute;ch ra s&aacute;ng tỏ bạch quang quang cầu, cảm gi&aacute;c c&oacute; ch&uacute;t ch&oacute;i mắt.<br />\r\n<br />\r\nQua tốt một một l&aacute;t, hắn mới lại từ từ mở mắt, miễn cưỡng th&iacute;ch ứng quang mang về sau, mới nh&igrave;n r&otilde; r&agrave;ng chung quanh t&igrave;nh huống.<br />\r\n<br />\r\nTrắng tinh như tuyết v&aacute;ch tường, xảo đoạt thi&ecirc;n c&ocirc;ng trang trí, chỉnh tề sạch sẽ c&aacute;i b&agrave;n, c&ograve;n c&oacute; m&igrave;nh nằm giường, thế m&agrave; phủ l&ecirc;n trắng như tuyết tơ lụa!<br />\r\n<br />\r\n&quot;Nơi n&agrave;y l&agrave; chỗ n&agrave;o?&quot; Hồng Ph&uacute; Qu&yacute; nội t&acirc;m mười phần m&ecirc; mang, nh&igrave;n qua ph&iacute;a tr&ecirc;n lớn quang cầu, &quot;Chẳng lẽ nơi n&agrave;y ch&iacute;nh l&agrave; thế giới sau khi chết sao? Nhưng v&igrave; c&aacute;i g&igrave; chết sẽ c&ograve;n cảm gi&aacute;c đ&oacute;i đ&acirc;y?&quot;<br />\r\n<br />\r\nKẹt kẹt!<br />\r\n<br />\r\nCửa ph&ograve;ng bị đẩy ra, một người mặc trắng như tuyết trường b&agrave;o thiếu ni&ecirc;n đi v&agrave;o gian ph&ograve;ng, hắn dung mạo tuấn l&atilde;ng, kh&iacute; chất phi&ecirc;u dật, phảng phất l&agrave; tr&ecirc;n trời xuống ph&agrave;m Tr&iacute;ch Ti&ecirc;n Nh&acirc;n.<br />\r\n<br />\r\nHồng Ph&uacute; Qu&yacute; nh&igrave;n thấy hắn đi tới, sửng sốt một c&aacute;i, nghi ngờ n&oacute;i: &quot;Ng&agrave;i l&agrave; tr&ecirc;n trời ti&ecirc;n nh&acirc;n sao?&quot;<br />\r\n<br />\r\n&quot;Ta chỉ l&agrave; một c&aacute;i đi tại th&agrave;nh Ti&ecirc;n Đạo tr&ecirc;n đường ph&agrave;m nh&acirc;n m&agrave; th&ocirc;i.&quot; Th&ocirc;i Hằng cười cười, đi v&agrave;o Hồng Ph&uacute; Qu&yacute; b&ecirc;n giường, &quot;Thế n&agrave;o, nhưng c&oacute; c&aacute;i g&igrave; kh&oacute; chịu chỗ.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute;, ch&iacute;nh l&agrave;. . .&quot; Hồng Ph&uacute; Qu&yacute; liền vội v&agrave;ng lắc đầu, nhưng lại tại đ&acirc;y l&agrave; bụng của hắn bỗng nhi&ecirc;n vang l&ecirc;n &quot;&Ugrave;ng ục ục&quot; thanh &acirc;m.<br />\r\n<br />\r\n&quot;Ha ha ha, l&agrave; đ&oacute;i bụng kh&ocirc;ng.&quot; Th&ocirc;i Hằng nở nụ cười, sau đ&oacute; nh&igrave;n về ph&iacute;a b&ecirc;n cạnh hư kh&ocirc;ng, thấp giọng qu&aacute;t n&oacute;i, &quot; Ho&agrave;ng C&acirc;n lực sĩ ở đ&acirc;u?&quot;<br />\r\n<br />\r\n&quot;Ho&agrave;ng C&acirc;n lực sĩ?&quot; Hồng Ph&uacute; Qu&yacute; c&oacute; ch&uacute;t m&ecirc; mang, kh&ocirc;ng biết r&otilde; Th&ocirc;i Hằng tại đối hư kh&ocirc;ng n&oacute;i c&aacute;i g&igrave;, nhưng tiếp theo một c&aacute;i chớp mắt trước mắt xuất hiện cảnh tượng lại l&agrave;m cho hắn trợn mắt hốc mồm.<br />\r\n<br />\r\nChỉ gặp mảnh n&agrave;y hư kh&ocirc;ng thế m&agrave; giống như l&agrave; bị h&ograve;n đ&aacute; đ&aacute;nh vỡ b&igrave;nh tĩnh nước hồ, nổi l&ecirc;n từng v&ograve;ng từng v&ograve;ng gợn s&oacute;ng.<br />\r\n<br />\r\nSau đ&oacute; liền c&oacute; cả người cao ba thuớc, bắp thịt cuồn cuộn, h&igrave;nh thể cường tr&aacute;ng đến cực điểm &aacute;o v&agrave;ng đại h&aacute;n trống rỗng xuất hiện tại trong ph&ograve;ng, một mực cung k&iacute;nh quỳ một gối xuống tại Th&ocirc;i Hằng trước mặt, &quot;B&aacute;i kiến Ti&ecirc;n T&ocirc;n!&quot;<br />\r\n<br />\r\nTi&ecirc;n thuật!<br />\r\n<br />\r\nThần ti&ecirc;n a!<br />\r\n<br />\r\nHồng Ph&uacute; Qu&yacute; kh&ocirc;ng thể tin nh&igrave;n về ph&iacute;a Th&ocirc;i Hằng, hai mắt trợn l&ecirc;n, mi&ecirc;̣ng há lớn, mặt mũi tr&agrave;n đầy chấn kinh.<br />\r\n<br />\r\nNgươi c&ograve;n n&oacute;i m&igrave;nh kh&ocirc;ng phải Ti&ecirc;n nh&acirc;n? !<br />\r\n<br />\r\nGiờ khắc n&agrave;y, hắn ph&uacute;c ch&iacute; t&acirc;m linh, ph&ugrave; ph&ugrave; một tiếng quỳ xuống xuống tới, đầu rạp xuống đất, tất cung tất k&iacute;nh n&oacute;i: &quot;Cầu Ti&ecirc;n T&ocirc;n ban thưởng ta cứu thế chi ph&aacute;p!&quot;<br />\r\n<br />\r\n&quot;A c&aacute;i n&agrave;y. . .&quot; Th&ocirc;i Hằng lập tức cảm gi&aacute;c người đều t&ecirc;.<br />\r\n<br />\r\nM&igrave;nh chỉ l&agrave; muốn gọi Ho&agrave;ng C&acirc;n lực sĩ lấy ch&uacute;t ăn tới m&agrave; th&ocirc;i, l&agrave;m sao c&aacute;i n&agrave;y tiểu tử bỗng nhi&ecirc;n liền quỳ xuống tới?<br />\r\n<br />\r\nQuả nhi&ecirc;n l&agrave; đến từ ti&ecirc;n hiệp thế giới thế gian vị diện đi.<br />\r\n<br />\r\nNhư thế kh&ocirc;ng kiến thức.<br />\r\n<br />\r\n&quot;Bắt đầu bắt đầu, ăn trước điểm đồ vật.&quot; Th&ocirc;i Hằng nhẹ nh&agrave;ng kho&aacute;t tay, v&ocirc; h&igrave;nh ph&aacute;p lực trực tiếp k&eacute;o lấy Hồng Ph&uacute; Qu&yacute; đứng l&ecirc;n.<br />\r\n<br />\r\nHắn mặc d&ugrave; kh&ocirc;ng biết c&aacute;i g&igrave; ph&aacute;p thuật, nhưng ph&aacute;p lực bản th&acirc;n tựu c&oacute; nhất định thần dị đặc t&iacute;nh, l&agrave;m được loại tr&igrave;nh độ n&agrave;y sự t&igrave;nh vẫn l&agrave; rất nhẹ nh&agrave;ng.<br />\r\n<br />\r\nNhưng theo Hồng Ph&uacute; Qu&yacute;, đ&acirc;y cũng l&agrave; h&agrave;ng thật gi&aacute; thật thần ti&ecirc;n thủ đoạn!<br />\r\n<br />\r\nC&aacute;ch kh&ocirc;ng ngự kh&iacute; để cho người ta đứng thẳng l&ecirc;n!<br />\r\n<br />\r\nĐ&acirc;y l&agrave; giang hồ trong truyền thuyết V&otilde; L&acirc;m Thần Thoại, thậm ch&iacute; Lục Địa Thần Ti&ecirc;n mới c&oacute; thể l&agrave;m đến sự t&igrave;nh a!<br />\r\n<br />\r\nHắn c&agrave;ng ph&aacute;t ra chắc chắn m&igrave;nh vị n&agrave;y &acirc;n nh&acirc;n, khẳng định l&agrave; một vị ẩn thế Ti&ecirc;n nh&acirc;n!<br />\r\n<br />\r\nBất qu&aacute;, Hồng Ph&uacute; Qu&yacute; cũng kh&ocirc;ng tiếp tục quỳ thỉnh cầu Th&ocirc;i Hằng.<br />\r\n<br />\r\nM&agrave; l&agrave; nghe theo Th&ocirc;i Hằng, chờ đợi ăn cơm.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nHắn thuở nhỏ kh&ocirc;ng c&oacute; phụ mẫu, từng bị người lừa gạt b&aacute;n đi trong miếu, l&agrave;m qua h&ograve;a thượng, trốn tới về sau lại lang thang hồi l&acirc;u, l&agrave;m qua t&ecirc;n ăn m&agrave;y, gặp qu&aacute; nhiều th&oacute;i đời n&oacute;ng lạnh.<br />\r\n<br />\r\nBởi vậy, hắn rất r&otilde; r&agrave;ng, l&agrave;m m&igrave;nh muốn từ người kh&aacute;c nơi đ&oacute; thu hoạch được c&aacute;i g&igrave; thời điểm, tuyệt kh&ocirc;ng thể mong muốn đơn phương đi bức b&aacute;ch.<br />\r\n<br />\r\nNhất l&agrave; kh&ocirc;ng thể lợi dụng đối phương l&ograve;ng từ bi đi bức b&aacute;ch.<br />\r\n<br />\r\nM&agrave; l&agrave; hẳn l&agrave; trước thể hiện ra gi&aacute; trị của m&igrave;nh.<br />\r\n<br />\r\nB&agrave;n lại c&aacute;i kh&aacute;c.<br />\r\n<br />\r\nTại thể hiện ra gi&aacute; trị của m&igrave;nh trước đ&oacute;, tốt nhất biểu hiện thuận theo.<br />\r\n<br />\r\n&quot;Ta tuyệt kh&ocirc;ng thể tại Ti&ecirc;n T&ocirc;n trong l&ograve;ng lưu lại ấn tượng xấu.&quot; Hồng Ph&uacute; Qu&yacute; trong l&ograve;ng thầm nghĩ.<br />\r\n<br />\r\nThế nhưng l&agrave;, l&agrave;m Ho&agrave;ng C&acirc;n lực sĩ trống rỗng biến ra một lớn c&aacute;i b&agrave;n mỹ vị m&oacute;n ngon về sau.<br />\r\n<br />\r\nHắn lại kh&ocirc;ng c&aacute;ch n&agrave;o b&igrave;nh tĩnh.<br />\r\n<br />\r\nCon mắt nh&igrave;n chằm chằm tr&ecirc;n b&agrave;n g&agrave; quay, thịt vịt nướng, thịt b&ograve; kho tương c&aacute;c loại, kh&ocirc;ng nh&uacute;c nh&iacute;ch.<br />\r\n<br />\r\nĐ&aacute;ng thương Hồng Ph&uacute; Qu&yacute; đ&atilde; lớn như vậy cũng chưa từng ăn mấy ngụm thịt.<br />\r\n<br />\r\n&quot;Ngươi cầm nhầm, đ&acirc;y l&agrave; ta b&igrave;nh thường ăn.&quot; Th&ocirc;i Hằng liếc mắt, đối Ho&agrave;ng C&acirc;n lực sĩ n&oacute;i, &quot; bưng ch&eacute;n kia dưỡng sinh ch&aacute;o hoa tới.&quot;<br />\r\n<br />\r\nCho nhanh chết đ&oacute;i người ăn như thế một b&agrave;n thịt c&aacute;, kh&ocirc;ng phải mở vui đ&ugrave;a m&agrave;!<br />\r\n<br />\r\n&quot;L&agrave; Ti&ecirc;n T&ocirc;n!&quot; Ho&agrave;ng C&acirc;n lực sĩ gật đầu.<br />\r\n<br />\r\nLập tức hắn vung tay l&ecirc;n, liền đem c&aacute;i n&agrave;y một c&aacute;i b&agrave;n đồ ăn thu v&agrave;o, đổi th&agrave;nh một c&aacute;i bồn lớn thơm ng&agrave;o ngạt ch&aacute;o hoa, vẫn xứng hai ch&eacute;n nhỏ cải bẹ.<br />\r\n<br />\r\n&quot;A, ăn đi.&quot; Th&ocirc;i Hằng chỉ chỉ ch&aacute;o hoa c&ugrave;ng cải bẹ, đối Hồng Ph&uacute; Qu&yacute; n&oacute;i, &quot; ngươi b&acirc;y giờ kh&ocirc;ng qu&aacute; th&iacute;ch hợp ăn qu&aacute; nhiều loại thịt, uống trước điểm ch&aacute;o nu&ocirc;i một nu&ocirc;i.&quot;<br />\r\n<br />\r\n&quot;. . .&quot; Hồng Ph&uacute; Qu&yacute; l&uacute;c n&agrave;y lại trầm mặc, nh&igrave;n qua tr&ecirc;n b&agrave;n ch&aacute;o hoa đ&atilde; xuất thần, &aacute;nh mắt đau thương, phản ứng so vừa mới nh&igrave;n đến những c&aacute;i kia thịt c&aacute; c&ograve;n lớn hơn, tựa hồ l&agrave; l&acirc;m v&agrave;o một loại n&agrave;o đ&oacute; hồi ức ở trong.<br />\r\n<br />\r\nPh&ugrave; ph&ugrave;!<br />\r\n<br />\r\nHắn bỗng nhi&ecirc;n quỳ tr&ecirc;n mặt đất, hướng v&ecirc;̀ kia b&aacute;t ch&aacute;o hoa thật s&acirc;u dập đầu, thanh &acirc;m c&oacute; ch&uacute;t kh&agrave;n kh&agrave;n, &quot;Cha, mẹ, h&agrave;i nhi bất hiếu, để c&aacute;c ngươi trước khi chết đều kh&ocirc;ng uống tr&ecirc;n một b&aacute;t n&oacute;ng hổi ch&aacute;o hoa a!&quot;</p>', '2021-11-28 16:42:44', 6, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(234, 61, 'Đây là một vị vô thượng tồn tại', '3', '<p>Hồng Ph&uacute; Qu&yacute; phản ứng ngo&agrave;i Th&ocirc;i Hằng đo&aacute;n trước.<br />\r\n<br />\r\nTrải qua một phen hỏi thăm, hắn rốt cục minh bạch đ&acirc;y l&agrave; c&oacute; chuyện g&igrave;.<br />\r\n<br />\r\nNguy&ecirc;n lai, Hồng Ph&uacute; Qu&yacute; sinh ra ở một c&aacute;i xa x&ocirc;i sơn th&ocirc;n nhỏ b&ecirc;n trong, phụ mẫu đều l&agrave; địa chủ t&aacute; điền, địa chủ cắt x&eacute;n nghi&ecirc;m trọng, bọn hắn một nh&agrave; chưa từng nếm qua một bữa cơm no.<br />\r\n<br />\r\nHồng Ph&uacute; Qu&yacute; t&aacute;m tuổi năm đ&oacute;, ph&aacute;t sinh nạn hạn h&aacute;n, chín tuổi năm đ&oacute; lại ph&aacute;t sinh nạn ch&acirc;u chấu c&ugrave;ng &ocirc;n dịch, nghi&ecirc;m trọng nạn đ&oacute;i lan tr&agrave;n, c&oacute; ch&uacute;t địa phương đ&atilde; đến người tướng ăn t&igrave;nh trạng.<br />\r\n<br />\r\nLoại n&agrave;y t&igrave;nh huống dưới, địa chủ vẫn như cũ kh&ocirc;ng chịu giảm thu&ecirc;, ngược lại l&agrave; l&agrave;m tầm trọng th&ecirc;m nghiền &eacute;p, dẫn đến Hồng Ph&uacute; Qu&yacute; phụ mẫu, hai người ca ca, một c&aacute;i muội muội đều bị tươi sống chết đ&oacute;i.<br />\r\n<br />\r\nHồng Ph&uacute; Qu&yacute; phụ mẫu trước khi chết thời điểm, đ&atilde; sinh ra ảo gi&aacute;c, b&ecirc;n trong miệng th&igrave; th&agrave;o n&oacute;i nhỏ đều l&agrave; &quot;C&oacute; ch&aacute;o uống, l&agrave; uống kh&ocirc;ng hết ch&aacute;o hoa, ph&uacute; qu&yacute; con a, mau tới uống a. . .&quot;<br />\r\n<br />\r\nNhưng bọn hắn đến chết đều kh&ocirc;ng thể thật uống d&ugrave; l&agrave; một ngụm ch&aacute;o.<br />\r\n<br />\r\nThậm ch&iacute;, sau khi chết c&ograve;n bị địa chủ phơi th&acirc;y, k&eacute;m ch&uacute;t bị d&acirc;n đ&oacute;i cướp đi nấu thịt ăn.<br />\r\n<br />\r\nL&agrave; Hồng Ph&uacute; Qu&yacute; dẫn theo li&ecirc;m đao, ch&eacute;m chết ba người về sau, mới miễn cưỡng bảo vệ phụ mẫu huynh muội thi thể, đem bọn hắn hoả t&aacute;ng an t&aacute;ng.<br />\r\n<br />\r\nBởi vậy, tại nh&igrave;n thấy ch&eacute;n n&agrave;y thơm ng&agrave;o ngạt ch&aacute;o hoa l&uacute;c, hắn c&oacute; ch&uacute;t x&uacute;c cảnh sinh t&igrave;nh, nghĩ đến qua đời phụ mẫu huynh muội.<br />\r\n<br />\r\nTh&ocirc;i Hằng nghe vậy trầm mặc hồi l&acirc;u , c&aacute;c loại Hồng Ph&uacute; Qu&yacute; cảm x&uacute;c b&igrave;nh tĩnh trở lại về sau, nhẹ nh&agrave;ng vỗ vỗ bờ vai của hắn, n&oacute;i: &quot;Tới đi, uống trước điểm ch&aacute;o.&quot;<br />\r\n<br />\r\nHiện tại Hồng Ph&uacute; Qu&yacute; t&igrave;nh huống rất nguy hiểm, cực độ đ&oacute;i kh&aacute;t trạng th&aacute;i lại th&ecirc;m kịch liệt t&acirc;m t&igrave;nh chập chờn, rất c&oacute; thể trực tiếp ngất đi.<br />\r\n<br />\r\n&quot;Đa tạ ti&ecirc;n, Ti&ecirc;n T&ocirc;n.&quot; Hồng Ph&uacute; Qu&yacute; tự m&igrave;nh n&oacute;i lời cảm tạ, hắn kh&ocirc;ng biết m&igrave;nh l&agrave;m như thế n&agrave;o xưng h&ocirc; Th&ocirc;i Hằng, liền dứt kho&aacute;t đi theo Ho&agrave;ng C&acirc;n lực sĩ c&ugrave;ng một chỗ gọi Ti&ecirc;n T&ocirc;n.<br />\r\n<br />\r\n&quot;Ta họ Th&ocirc;i, ngươi gọi ta Th&ocirc;i ti&ecirc;n sinh đi.&quot; Th&ocirc;i Hằng mỉm cười n&oacute;i.<br />\r\n<br />\r\n&quot;V&acirc;ng, Th&ocirc;i ti&ecirc;n sinh.&quot; Hồng Ph&uacute; Qu&yacute; vội v&agrave;ng x&aacute;c nhận, đồng thời ngồi ở trước b&agrave;n, nh&igrave;n một ch&uacute;t ch&eacute;n kia ch&aacute;o, lại nh&igrave;n một ch&uacute;t Th&ocirc;i Hằng, cẩn thận nghi&ecirc;m t&uacute;c hỏi thăm, &quot;Th&ocirc;i ti&ecirc;n sinh, ta, ta thật c&oacute; thể uống sao?&quot;<br />\r\n<br />\r\n&quot;Uống đi, c&oacute; thể chậm một ch&uacute;t, đừng n&oacute;ng.&quot; Th&ocirc;i Hằng nhẹ nh&agrave;ng gật đầu.<br />\r\n<br />\r\n&quot;&Ocirc;! Thơm qu&aacute;! Thật n&oacute;ng!&quot; Hồng Ph&uacute; Qu&yacute; một b&ecirc;n h&uacute;p ch&aacute;o một b&ecirc;n kinh h&ocirc;, coi như rất bỏng hắn cũng kh&ocirc;ng muốn dừng lại, d&ugrave; sao, sống v&agrave;i chục năm, chưa từng như h&ocirc;m nay dạng n&agrave;y thống kho&aacute;i m&agrave; ăn cơm xong.<br />\r\n<br />\r\nTh&ocirc;i Hằng kh&ocirc;ng c&oacute; ngăn cản hắn, ngay tại một b&ecirc;n nh&igrave;n xem.<br />\r\n<br />\r\nC&aacute;i n&agrave;y ch&aacute;o hoa d&ugrave;ng gạo cũng kh&ocirc;ng phổ th&ocirc;ng, l&agrave; t&acirc;n thủ trong n&ocirc;ng trại sản xuất ra Linh mễ.<br />\r\n<br />\r\nKh&ocirc;ng chỉ c&oacute; sắc hương vị đều đủ, c&ograve;n ẩn chứa nhỏ x&iacute;u linh kh&iacute;, dinh dưỡng phong ph&uacute;, nhưng lại nhuận vật mảnh im ắng, sẽ kh&ocirc;ng lập tức đại bổ qu&aacute; thừa.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nTh&ocirc;i Hằng đối Hồng Ph&uacute; Qu&yacute; ấn tượng kh&ocirc;ng tệ, đ&acirc;y l&agrave; một c&aacute;i nhận hết cực khổ người đ&aacute;ng thương.<br />\r\n<br />\r\nChỉ l&agrave; một b&aacute;t ch&aacute;o hoa, với hắn m&agrave; n&oacute;i bất qu&aacute; l&agrave; tiện tay m&agrave; th&ocirc;i, cũng đ&atilde; c&oacute; thể cho người đ&aacute;ng thương n&agrave;y trước nay chưa từng c&oacute; hạnh ph&uacute;c.<br />\r\n<br />\r\nHuống hồ, một người tại c&aacute;i n&agrave;y t&acirc;n thủ kh&ocirc;ng gian b&ecirc;n trong ng&acirc;y người mười năm, nếu kh&ocirc;ng c&oacute; Ho&agrave;ng C&acirc;n lực sĩ tại, hắn đều nhanh qu&ecirc;n n&ecirc;n n&oacute;i như thế n&agrave;o.<br />\r\n<br />\r\nNhưng Ho&agrave;ng C&acirc;n lực sĩ chỉ l&agrave; c&aacute;i n&oacute;i g&igrave; nghe nấy người m&aacute;y, cũng kh&ocirc;ng c&oacute; đ&uacute;ng nghĩa linh tr&iacute;.<br />\r\n<br />\r\nHiện tại kh&oacute; khăn tiến đến một c&aacute;i c&oacute; thể b&igrave;nh thường giao lưu nh&acirc;n loại, hắn đương nhi&ecirc;n muốn để Hồng Ph&uacute; Qu&yacute; trở n&ecirc;n khỏe mạnh bắt đầu, d&ugrave; l&agrave; chỉ n&oacute;i l&agrave; n&oacute;i chuyện cũng l&agrave; tốt.<br />\r\n<br />\r\nC&ograve;n c&oacute; thể tiện thể hi&ecirc;̉u rõ một c&aacute;i thế giới b&ecirc;n ngo&agrave;i t&igrave;nh huống.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nHồng Ph&uacute; Qu&yacute; l&agrave; ăn như hổ đ&oacute;i lấy ăn xong.<br />\r\n<br />\r\nR&ograve;ng r&atilde; một c&aacute;i bồn lớn ch&aacute;o hoa kh&ocirc;ng đến một ph&uacute;t thời gian liền bị hắn ti&ecirc;u diệt sạch sẽ, hai ch&eacute;n nhỏ cải bẹ tự nhi&ecirc;n cũng đều bỏ m&igrave;nh.<br />\r\n<br />\r\nTrong bụng ấm &aacute;p, ph&igrave;nh l&ecirc;n, đ&acirc;y l&agrave; hắn chưa hề cảm nhận được qua hạnh ph&uacute;c.<br />\r\n<br />\r\nBất qu&aacute;, Hồng Ph&uacute; Qu&yacute; kh&ocirc;ng c&oacute; chỉ muốn dư vị, hắn vội v&agrave;ng đứng l&ecirc;n, muốn c&ugrave;ng Th&ocirc;i Hằng n&oacute;i lời cảm tạ, chợt ph&aacute;t hiện m&igrave;nh tựa hồ cao lớn.<br />\r\n<br />\r\n&quot;Chuyện g&igrave; xảy ra?&quot; Hắn kinh ngạc c&uacute;i đầu nh&igrave;n một ch&uacute;t m&igrave;nh tay, lại nh&igrave;n một ch&uacute;t ch&acirc;n của m&igrave;nh, ph&aacute;t hiện thủ chưởng lớn rất nhiều, tay &aacute;o ngắn, quần cũng ngắn.<br />\r\n<br />\r\nM&igrave;nh chỉ l&agrave; uống một b&aacute;t ch&aacute;o, thế m&agrave; liền d&agrave;i cao, trưởng th&agrave;nh?<br />\r\n<br />\r\nM&agrave; lại th&acirc;n thể tựa hồ cũng nhẹ nh&agrave;ng rất nhiều, đầy người tổn thương khỏi bệnh nghĩ cũng đều khỏi hẳn.<br />\r\n<br />\r\n&quot;Nguy&ecirc;n lai đ&acirc;y kh&ocirc;ng phải ch&aacute;o, đ&acirc;y l&agrave; ti&ecirc;n dược a!&quot; Hồng Ph&uacute; Qu&yacute; trong l&ograve;ng khiếp sợ kh&ocirc;ng g&igrave; s&aacute;nh nổi, nh&igrave;n về ph&iacute;a Th&ocirc;i Hằng &aacute;nh mắt tr&agrave;n ngập cảm k&iacute;ch c&ugrave;ng kh&oacute; m&agrave; n&oacute;i n&ecirc;n lời s&ugrave;ng k&iacute;nh.<br />\r\n<br />\r\nNhư thế ti&ecirc;n dược thế m&agrave; dạng n&agrave;y khẳng kh&aacute;i cho m&igrave;nh dạng n&agrave;y một c&aacute;i lớp người qu&ecirc; m&ugrave;a ăn.<br />\r\n<br />\r\nĐ&acirc;y tuyệt đối l&agrave; một vị cứu khổ cứu nạn Đại Ti&ecirc;n T&ocirc;n a!<br />\r\n<br />\r\n&quot;Đa tạ Ti&ecirc;n T&ocirc;n &acirc;n cứu mạng, đa tạ Ti&ecirc;n T&ocirc;n ban thưởng ta ti&ecirc;n dược!&quot; Hồng Ph&uacute; Qu&yacute; dưới sự k&iacute;ch động kh&ocirc;ng biết r&otilde; l&agrave;m như thế n&agrave;o biểu đạt, dứt kho&aacute;t lại quỳ xuống đến dập đầu, lấy cảm tạ thần phật phương thức hướng Th&ocirc;i Hằng lễ b&aacute;i.<br />\r\n<br />\r\n&quot;Chỉ l&agrave; dừng lại phổ th&ocirc;ng đồ ăn th&ocirc;i.&quot; Th&ocirc;i Hằng lắc đầu mỉm cười, đưa tay c&aacute;ch kh&ocirc;ng để Hồng Ph&uacute; Qu&yacute; đứng l&ecirc;n, n&oacute;i: &quot;Về sau đừng lại quỳ, c&ograve;n c&oacute; gọi ti&ecirc;n sinh liền tốt, chớ c&oacute; lại xưng Ti&ecirc;n T&ocirc;n.&quot;<br />\r\n<br />\r\nGặp qua Luyện Kh&iacute; s&aacute;u tầng Ti&ecirc;n T&ocirc;n sao?<br />\r\n<br />\r\nHo&agrave;ng C&acirc;n lực sĩ kia thuộc về &quot;Hệ thống thiết lập&quot;, kh&ocirc;ng đổi được coi như xong, bị người kh&aacute;c gọi như vậy hắn cảm gi&aacute;c chột dạ.<br />\r\n<br />\r\n&quot;L&agrave; ti&ecirc;n, ti&ecirc;n sinh.&quot; Hồng Ph&uacute; Qu&yacute; liền vội v&agrave;ng gật đầu, lập tức lại cung k&iacute;nh hỏi, &quot;Ti&ecirc;n sinh đ&atilde; cứu ta t&iacute;nh mệnh, ban thưởng ta ti&ecirc;n dược, ta c&aacute;i mạng n&agrave;y ch&iacute;nh l&agrave; ti&ecirc;n sinh ng&agrave;i, c&oacute; c&aacute;i g&igrave; ph&acirc;n ph&oacute;, ti&ecirc;n sinh cứ việc n&oacute;i ch&iacute;nh l&agrave;.&quot;<br />\r\n<br />\r\nHắn kh&ocirc;ng tiếp tục cầu lấy cứu thế chi ph&aacute;p, m&agrave; l&agrave; trước ti&ecirc;n đem t&acirc;m tư đặt ở b&aacute;o đ&aacute;p Th&ocirc;i Hằng &acirc;n huệ b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải l&agrave; muốn cứu thế chi ph&aacute;p sao?&quot; Th&ocirc;i Hằng khẽ cười n&oacute;i.<br />\r\n<br />\r\n&quot;Ti&ecirc;n sinh đại &acirc;n ta cũng c&ograve;n kh&ocirc;ng c&oacute; b&aacute;o, nơi n&agrave;o c&ograve;n d&aacute;m cầu lấy th&aacute;nh ph&aacute;p.&quot; Hồng Ph&uacute; Qu&yacute; lắc đầu n&oacute;i.<br />\r\n<br />\r\n&quot;Vậy trước ti&ecirc;n theo gi&uacute;p ta tr&ograve; chuyện, đ&aacute;nh một ch&uacute;t tr&ograve; chơi đi.&quot; Th&ocirc;i Hằng kh&oacute;e miệng c&oacute; ch&uacute;t giương l&ecirc;n, cười n&oacute;i, &quot;Đi theo ta, dạy ngươi quen biết một ch&uacute;t c&aacute;i g&igrave; gọi l&agrave; PS5.&quot;<br />\r\n<br />\r\n&quot;Cái rắm Ace m&uacute;a?&quot; Hồng Ph&uacute; Qu&yacute; một mặt m&ecirc; mang, chỉ cảm thấy ti&ecirc;n nh&acirc;n n&oacute;i thật sự l&agrave; huyền ảo kh&oacute; lường.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nSau đ&oacute; ba ng&agrave;y, Th&ocirc;i Hằng kh&ocirc;ng c&oacute; gấp hướng Hồng Ph&uacute; Qu&yacute; hỏi thăm ph&iacute;a ngo&agrave;i t&igrave;nh huống.<br />\r\n<br />\r\nHắn mang theo Hồng Ph&uacute; Qu&yacute; đ&aacute;nh tr&ograve; chơi, ăn cơm, trồng trọt, tr&ograve; chuyện một ch&uacute;t c&oacute; kh&ocirc;ng c&oacute;, t&acirc;m t&iacute;nh thả rất nhẹ nh&agrave;ng.<br />\r\n<br />\r\nSo với một m&igrave;nh sinh hoạt kia mười năm, Th&ocirc;i Hằng cảm gi&aacute;c đ&acirc;y l&agrave; m&igrave;nh t&acirc;m t&igrave;nh tốt nhất ba ng&agrave;y.<br />\r\n<br />\r\nỞ v&agrave;o loại t&acirc;m t&iacute;nh n&agrave;y ph&iacute;a dưới, hắn cảm gi&aacute;c được m&igrave;nh dừng lại hơn một năm cảnh giới b&igrave;nh cảnh thế m&agrave; bu&ocirc;ng lỏng!<br />\r\n<br />\r\nĐ&uacute;ng vậy, chỉ l&agrave; Luyện Kh&iacute; s&aacute;u tầng liền để hắn dừng lại hơn một năm!<br />\r\n<br />\r\nĐ&acirc;y cũng l&agrave; hắn nhận r&otilde; r&agrave;ng hiện thực nguy&ecirc;n nh&acirc;n một trong.<br />\r\n<br />\r\n&quot;Quả nhi&ecirc;n phải g&igrave;n giữ vui vẻ mới c&oacute; thể tu ti&ecirc;n a!&quot; Th&ocirc;i Hằng trong l&ograve;ng nghĩ như thế nói.<br />\r\n<br />\r\nM&agrave; đối với Hồng Ph&uacute; Qu&yacute; tới n&oacute;i.<br />\r\n<br />\r\nTại ti&ecirc;n cảnh c&aacute;i n&agrave;y ba ng&agrave;y trải qua, c&oacute; thể n&oacute;i huyền b&iacute;.<br />\r\n<br />\r\nHắn thấy được qu&aacute; nhiều kỳ qu&aacute;i, kh&ocirc;ng thể tưởng tượng nổi Ti&ecirc;n gia bảo vật.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nTỉ như:<br />\r\n<br />\r\nKh&ocirc;ng cần ch&igrave;a kho&aacute;, chỉ cần nhẹ nh&agrave;ng theo một c&aacute;i liền c&oacute; thể mở ra Ti&ecirc;n nh&acirc;n kh&oacute;a cửa.<br />\r\n<br />\r\nKh&ocirc;ng cần s&aacute;p dầu, chỉ cần n&oacute;i c&acirc;u n&agrave;o liền c&oacute; thể tự do chốt mở Ti&ecirc;n nh&acirc;n &aacute;nh đ&egrave;n.<br />\r\n<br />\r\nKh&ocirc;ng cần ch&acirc;m lửa, chỉ cần đem nước bỏ v&agrave;o liền c&oacute; thể tự động đốt l&ecirc;n Ti&ecirc;n nh&acirc;n ấm nước.<br />\r\n<br />\r\nC&ograve;n c&oacute; kia thần kỳ nhất cái rắm Ace m&uacute;a!<br />\r\n<br />\r\nKh&ocirc;ng phải một loại vũ đạo, m&agrave; l&agrave; một loại Ti&ecirc;n gia ph&aacute;p bảo.<br />\r\n<br />\r\nC&aacute;i n&agrave;y ph&aacute;p bảo c&oacute; thể tại một c&aacute;i m&agrave;n s&aacute;ng tr&ecirc;n cho thấy đủ loại cảnh tượng, c&oacute; kinh khủng &acirc;m trầm, c&oacute; kỳ diệu mỹ lệ, b&ecirc;n trong phảng phất chứa v&ocirc; số c&aacute;i thế giới.<br />\r\n<br />\r\nC&agrave;ng th&ecirc;m ly kỳ ch&iacute;nh l&agrave;, m&igrave;nh dạng n&agrave;y một c&aacute;i nho nhỏ ph&agrave;m nh&acirc;n, thế m&agrave; đều c&oacute; thể th&ocirc;ng qua c&aacute;i n&agrave;y ph&aacute;p Bảo Lai khống chế những thế giới n&agrave;y sinh linh!<br />\r\n<br />\r\nQu&aacute; l&agrave;m cho người ta kh&oacute; c&oacute; thể tin!<br />\r\n<br />\r\nChỉ sợ chỉ c&oacute; ho&agrave;n to&agrave;n chưởng khống những thế giới n&agrave;y tồn tại, mới c&oacute; thể đem dạng n&agrave;y vĩ đại quyền hạn trao tặng m&igrave;nh dạng n&agrave;y một c&aacute;i nhỏ b&eacute; ph&agrave;m nh&acirc;n.<br />\r\n<br />\r\nNgắn ngủi ba ng&agrave;y thời gian, tại Hồng Ph&uacute; Qu&yacute; trong l&ograve;ng, Th&ocirc;i Hằng đ&atilde; từ một c&aacute;i ẩn thế ti&ecirc;n nh&acirc;n h&igrave;nh tượng, thăng cấp th&agrave;nh một vị chưởng khống kh&ocirc;ng biết bao nhi&ecirc;u thế giới v&ocirc; thượng tồn tại.<br />\r\n<br />\r\nC&aacute;i n&agrave;y một ng&agrave;y s&aacute;ng sớm, Hồng Ph&uacute; Qu&yacute; ra khỏi ph&ograve;ng, đang định đi t&igrave;m Th&ocirc;i Hằng vấn an.<br />\r\n<br />\r\nNhưng vừa ra cửa, hắn liền thấy tr&ecirc;n trời nhật nguyệt tề huy, đại ph&oacute;ng quang minh.<br />\r\n<br />\r\nChỉ gặp kia kh&ocirc;ng trung c&oacute; từng đạo m&agrave;u v&agrave;ng kim thải h&agrave; chảy xu&ocirc;i.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, đang c&oacute; một th&acirc;n ảnh tắm rửa lấy ng&agrave;y &aacute;nh trăng huy c&ugrave;ng đầy trời h&agrave;o quang, chậm r&atilde;i hướng trời cao phi thăng.<br />\r\n<br />\r\nTh&ocirc;i Hằng đột ph&aacute;.<br />\r\n<br />\r\nLuyện Kh&iacute; bảy tầng!</p>', '2021-11-28 16:43:07', 4, 1),
(235, 61, 'Tiểu nhân vật chí hướng lớn', '4', '<p>Luyện Kh&iacute; bảy tầng l&agrave; một loại g&igrave; cảm gi&aacute;c?<br />\r\n<br />\r\nTh&ocirc;i Hằng c&oacute; thể r&otilde; r&agrave;ng cảm gi&aacute;c được, th&acirc;n thể của m&igrave;nh so trước đ&oacute; cường đại hơn gấp mười lần, thể nội huyết dịch như l&agrave; s&ocirc;ng lớn chảy xiết, giơ tay nhấc ch&acirc;n đều c&oacute; kh&ocirc;ng g&igrave; s&aacute;nh nổi lực lượng cường đại.<br />\r\n<br />\r\nMột quyền đ&aacute;nh nổ một t&ograve;a s&aacute;u tầng cư d&acirc;n l&acirc;u hẳn l&agrave; kh&ocirc;ng vấn đề g&igrave;.<br />\r\n<br />\r\nĐ&aacute;ng tiếc kh&ocirc;ng biết ph&aacute;p thuật.<br />\r\n<br />\r\nCũng chưa từng xu&acirc;́t hi&ecirc;̣n c&aacute;i g&igrave; trời sinh thần th&ocirc;ng, đo&aacute;n chừng gặp được ch&iacute;nh thống tu ti&ecirc;n giả, tại chỗ liền bị diệt đi.<br />\r\n<br />\r\nNghĩ tới đ&acirc;y, Th&ocirc;i Hằng vừa mới bởi v&igrave; đột ph&aacute; cảnh giới m&agrave; cảm thấy t&acirc;m t&igrave;nh hưng phấn lập tức liền lạnh xuống.<br />\r\n<br />\r\n&quot;Ta thật sự l&agrave; nhẹ nh&agrave;ng, vừa rồi thế m&agrave; bởi v&igrave; đột ph&aacute; đến Luyện Kh&iacute; bảy tầng đ&atilde; cảm thấy m&igrave;nh rất lợi hại.&quot;<br />\r\n<br />\r\nTh&ocirc;i Hằng lắc đầu, ở trong l&ograve;ng cảnh c&aacute;o m&igrave;nh, &quot;C&aacute;i n&agrave;y thế nhưng l&agrave; c&aacute;i Ti&ecirc;n Vương khắp nơi tr&ecirc;n đất cao cấp ti&ecirc;n hiệp thế giới, đừng n&oacute;i Luyện Kh&iacute; bảy tầng, liền xem như Phản Hư, Hợp Đạo cũng đều l&agrave; th&aacute;i k&ecirc;.&quot;<br />\r\n<br />\r\nBất qu&aacute;, tu vi cảnh giới đột ph&aacute; t&oacute;m lại l&agrave; một chuyện tốt, để t&acirc;m t&igrave;nh của hắn lại đ&atilde; thả lỏng một ch&uacute;t, rốt cục cũng c&oacute; t&acirc;m tư đi hỏi thăm Hồng Ph&uacute; Qu&yacute; li&ecirc;n quan tới thế giới b&ecirc;n ngo&agrave;i t&igrave;nh huống nh&igrave;n.<br />\r\n<br />\r\nMặc d&ugrave; , dựa theo hệ thống thuyết ph&aacute;p, hiện tại t&acirc;n thủ kh&ocirc;ng gian tiếp x&uacute;c chỉ l&agrave; c&aacute;i n&agrave;y cao cấp ti&ecirc;n hiệp thời kh&ocirc;ng bao h&agrave;m chư thi&ecirc;n vạn giới một trong, c&ograve;n c&oacute; thể chỉ l&agrave; c&aacute;i cấp thấp ph&agrave;m nh&acirc;n vị diện, nhưng cũng l&agrave; c&oacute; rất cao hiểu gi&aacute; trị.<br />\r\n<br />\r\nD&ugrave; sao, mọi người đều biết, ti&ecirc;n hiệp thế giới ph&agrave;m nh&acirc;n kh&oacute; tr&aacute;nh khỏi cũng l&agrave; c&oacute; v&ocirc; số tu ti&ecirc;n giả, tối thiểu cũng sẽ c&oacute; Nguy&ecirc;n Anh cấp bậc l&atilde;o qu&aacute;i.<br />\r\n<br />\r\nM&igrave;nh như thế c&aacute;i nho nhỏ Luyện Kh&iacute; bảy tầng n&agrave;o c&oacute; tư c&aacute;ch xem thường thế gian.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave;, ngươi n&oacute;i ngươi chỗ địa phương tồn tại Lục Địa Thần Ti&ecirc;n? !&quot; Th&ocirc;i Hằng một mặt mộng bức nh&igrave;n xem Hồng Ph&uacute; Qu&yacute;, cơ hồ cho l&agrave; m&igrave;nh nghe lầm.<br />\r\n<br />\r\nĐ&atilde; n&oacute;i xong thế gian vị diện đ&acirc;y?<br />\r\n<br />\r\nL&agrave;m sao liền thần ti&ecirc;n đều đi ra rồi?<br />\r\n<br />\r\nA kh&ocirc;ng đ&uacute;ng, Lục Địa Thần Ti&ecirc;n, cũng ch&iacute;nh l&agrave; c&ograve;n kh&ocirc;ng c&oacute; ch&acirc;n ch&iacute;nh th&agrave;nh ti&ecirc;n?<br />\r\n<br />\r\nL&uacute;c n&agrave;y, Th&ocirc;i Hằng ngay tại hỏi thăm Hồng Ph&uacute; Qu&yacute; li&ecirc;n quan tới ngoại giới t&igrave;nh huống.<br />\r\n<br />\r\nMới đầu c&ograve;n tốt, tại Hồng Ph&uacute; Qu&yacute; trong mi&ecirc;u tả, hắn sinh hoạt địa phương l&agrave; một c&aacute;i hưng thịnh hơn hai trăm năm ho&agrave;ng triều, l&agrave; d&ugrave;ng qu&acirc;n đội để duy tr&igrave; ch&iacute;nh quyền bạo lực cơ quan quốc gia.<br />\r\n<br />\r\nC&oacute; thể giảng lấy kể, Th&ocirc;i Hằng cũng cảm gi&aacute;c kh&ocirc;ng được b&igrave;nh thường.<br />\r\n<br />\r\nNgoại trừ ho&agrave;ng triều b&ecirc;n ngo&agrave;i, c&ograve;n c&oacute; Phật m&ocirc;n, Đạo M&ocirc;n, c&ugrave;ng c&aacute;c loại m&ocirc;n ph&aacute;i.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nTại Hồng Ph&uacute; Qu&yacute; trong mi&ecirc;u tả, m&ocirc;n ph&aacute;i cao cao tại thượng, nhất l&agrave; Phật m&ocirc;n c&ugrave;ng Đạo M&ocirc;n, bọn hắn truyền thừa l&acirc;u đời, nắm giữ lấy Si&ecirc;u Ph&agrave;m Nhập Th&aacute;nh lực lượng, mảy may đều kh&ocirc;ng th&egrave;m để &yacute; ho&agrave;ng triều cường quyền.<br />\r\n<br />\r\nM&agrave; lại, nghe n&oacute;i một ch&uacute;t cổ l&atilde;o m&ocirc;n ph&aacute;i b&ecirc;n trong, c&ograve;n c&oacute; Lục Địa Thần Ti&ecirc;n ẩn cư, c&oacute; được kh&ocirc;ng thể tưởng tượng nổi vĩ lực, bọn hắn trường sinh cửu thị, ngồi xem v&ocirc; số ho&agrave;ng triều hưng suy l&ecirc;n xuống.<br />\r\n<br />\r\nC&aacute;i n&agrave;y liền rất kh&ocirc;ng hợp th&oacute;i thường.<br />\r\n<br />\r\nTh&ocirc;i Hằng th&ocirc;ng qua m&igrave;nh một ch&uacute;t kinh nghiệm, rất nhanh liền &quot;L&agrave;m r&otilde; r&agrave;ng&quot; ngoại giới thực tế t&igrave;nh huống.<br />\r\n<br />\r\nHồng Ph&uacute; Qu&yacute; sinh hoạt địa phương, đ&uacute;ng l&agrave; một ph&agrave;m nh&acirc;n thế giới, m&agrave; lại hẳn l&agrave; một loại tương đối thường gặp tu ti&ecirc;n thế giới Nh&acirc;n Gian giới.<br />\r\n<br />\r\nTại thế giới như thế n&agrave;y, tu ti&ecirc;n m&ocirc;n ph&aacute;i Si&ecirc;u Tho&aacute;t tại ph&agrave;m nh&acirc;n ph&iacute;a tr&ecirc;n, nắm giữ lấy ph&agrave;m Nh&acirc;n Ho&agrave;ng hướng sinh tử tồn vong.<br />\r\n<br />\r\nN&oacute;i theo một &yacute; nghĩa n&agrave;o đ&oacute;, c&aacute;i n&agrave;y c&aacute;i gọi l&agrave; ho&agrave;ng triều đều chỉ l&agrave; m&ocirc;n ph&aacute;i kho l&uacute;a, ng&acirc;n h&agrave;ng, chi&ecirc;u sinh xử l&yacute; m&agrave; th&ocirc;i.<br />\r\n<br />\r\nNhững c&aacute;i kia &quot;Lục Địa Thần Ti&ecirc;n&quot;, đo&aacute;n chừng ch&iacute;nh l&agrave; tu vi cao s&acirc;u tu ti&ecirc;n giả, c&oacute; lẽ l&agrave; Tr&uacute;c Cơ, cũng c&oacute; thể l&agrave; l&agrave; Kim Đan.<br />\r\n<br />\r\nMặc d&ugrave; c&aacute;i n&agrave;y so thập Y&ecirc;u ti&ecirc;n vương tung ho&agrave;nh cao cấp thế giới an to&agrave;n kh&ocirc;ng &iacute;t, nhưng hệ số an to&agrave;n cũng mười phần c&oacute; hạn, m&igrave;nh như thế c&aacute;i Luyện Kh&iacute; bảy tầng tiểu th&aacute;i k&ecirc;, gặp được c&aacute;i Tr&uacute;c Cơ đều sẽ chết kh&ocirc;ng c&ograve;n s&oacute;t lại một ch&uacute;t cặn.<br />\r\n<br />\r\nD&ugrave; sao, thường gặp tu ti&ecirc;n thế giới c&ograve;n c&oacute; một loại kh&aacute;c c&aacute;ch gọi, đ&oacute; ch&iacute;nh l&agrave; &quot;X&atilde; hội đen Tu Ti&ecirc;n giới&quot; ! !<br />\r\n<br />\r\nHồng Ph&uacute; Qu&yacute; th&igrave; l&agrave; cảm thấy hơi nghi hoặc một ch&uacute;t.<br />\r\n<br />\r\nV&igrave; c&aacute;i g&igrave; Th&ocirc;i ti&ecirc;n sinh vừa nghe đến Lục Địa Thần Ti&ecirc;n cứ như vậy k&iacute;ch động?<br />\r\n<br />\r\nĐối với dạng n&agrave;y một vị nắm trong tay kh&ocirc;ng biết bao nhi&ecirc;u c&aacute;i thế giới v&ocirc; thượng tồn tại tới n&oacute;i, cho d&ugrave; l&agrave; Lục Địa Thần Ti&ecirc;n cũng c&ugrave;ng con kiến kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c biệt đi.<br />\r\n<br />\r\n&quot;Ti&ecirc;n sinh, Lục Địa Thần Ti&ecirc;n c&oacute; vấn đề g&igrave; kh&ocirc;ng?&quot; Hồng Ph&uacute; Qu&yacute; m&ecirc; mang n&oacute;i.<br />\r\n<br />\r\n&quot;Khụ khụ!&quot; Th&ocirc;i Hằng nhẹ nh&agrave;ng ho khan hai tiếng, tu&acirc;n hỏi, &quot;Ngươi biết r&otilde; những c&aacute;i kia Lục Địa Thần Ti&ecirc;n đều c&oacute; c&aacute;i g&igrave; bản lĩnh sao?&quot;<br />\r\n<br />\r\n&quot;Bản lĩnh?&quot; Hồng Ph&uacute; Qu&yacute; nghĩ nghĩ, lắc đầu n&oacute;i, &quot;Lục Địa Thần Ti&ecirc;n l&agrave; si&ecirc;u việt ph&agrave;m tục nh&acirc;n vật, ta chỉ l&agrave; t&ecirc;n ăn m&agrave;y nhỏ, tiếp x&uacute;c kh&ocirc;ng đến c&aacute;i kia phương diện.<br />\r\n<br />\r\n&quot;Bất qu&aacute;, ta lang thang c&aacute;c nơi, cũng nghe qua một ch&uacute;t nghe đồn, n&oacute;i những n&agrave;y Lục Địa Thần Ti&ecirc;n từng c&aacute;i đều c&oacute; thể bắt trăng hái sao, v&aacute;c n&uacute;i khi&ecirc;ng biển, l&agrave; kh&ocirc;ng tầm thường đại thần ti&ecirc;n.&quot;<br />\r\n<br />\r\nĐ&acirc;y l&agrave; hắn từ qu&aacute;n tr&agrave; b&igrave;nh thư ti&ecirc;n sinh nơi đ&oacute; nghe được.<br />\r\n<br />\r\n&quot;Th&igrave; ra l&agrave; thế.&quot; Th&ocirc;i Hằng nghe vậy nhẹ gật đầu, dường như rơi v&agrave;o trầm tư.<br />\r\n<br />\r\nTr&ecirc;n thực tế hắn hiện tại cả người đều kh&ocirc;ng tốt.<br />\r\n<br />\r\nHợp lấy đ&acirc;y l&agrave; tương đối cao cấp Ph&agrave;m Nh&acirc;n giới, thế m&agrave; liền bắt trăng hái sao, v&aacute;c n&uacute;i khi&ecirc;ng biển đại năng đều c&oacute;.<br />\r\n<br />\r\nNguy!<br />\r\n<br />\r\nHồng Ph&uacute; Qu&yacute; nh&igrave;n xem Th&ocirc;i Hằng d&aacute;ng vẻ trầm tư, trong l&ograve;ng bỗng nhi&ecirc;n c&oacute; chỗ minh ngộ, &quot;Ta biết r&otilde;, như Th&ocirc;i ti&ecirc;n sinh dạng n&agrave;y v&ocirc; thượng tồn tại, khẳng định l&agrave; si&ecirc;u việt ph&agrave;m tục qu&aacute; l&acirc;u, kh&ocirc;ng hiểu r&otilde; lắm nh&acirc;n gian sự t&igrave;nh.<br />\r\n<br />\r\n&quot;Sở dĩ sẽ hỏi ta li&ecirc;n quan tới Lục Địa Thần Ti&ecirc;n sự t&igrave;nh, đo&aacute;n chừng liền chỉ l&agrave; nghĩ muốn hiểu r&otilde; một c&aacute;i thế gian t&igrave;nh huống, từ đ&oacute; th&ocirc;ng qua thế gian những n&agrave;y t&igrave;nh huống, suy tư th&acirc;m ảo Ti&ecirc;n gia đạo l&yacute;.&quot;<br />\r\n<br />\r\nThật giống như m&igrave;nh c&oacute; thời điểm cũng sẽ ch&uacute; &yacute; con kiến dọn nh&agrave;, suy nghĩ trong đ&oacute; đạo l&yacute; đồng dạng.<br />\r\n<br />\r\nC&aacute;i n&agrave;y khiến Hồng Ph&uacute; Qu&yacute; c&oacute; ch&uacute;t hưng phấn.<br />\r\n<br />\r\nHắn cảm gi&aacute;c m&igrave;nh rốt cuộc t&igrave;m được gi&aacute; trị của m&igrave;nh chỗ.<br />\r\n<br />\r\nChỉ c&oacute; hiện ra gi&aacute; trị của m&igrave;nh, mới c&oacute; hi vọng từ Th&ocirc;i ti&ecirc;n sinh nơi đ&oacute; cầu tới cứu thế chi ph&aacute;p!<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nHồng Ph&uacute; Qu&yacute; cuối c&ugrave;ng chỉ l&agrave; một c&aacute;i mười hai tuổi h&agrave;i tử.<br />\r\n<br />\r\nLại một l&ograve;ng nghĩ hiện ra gi&aacute; trị của m&igrave;nh.<br />\r\n<br />\r\nThế l&agrave;, ở sau đ&oacute; trong ba ng&agrave;y, hắn cơ hồ l&agrave; biết g&igrave; n&oacute;i nấy, đem hắn đi qua lang thang c&aacute;c nơi kiến thức đều c&ugrave;ng Th&ocirc;i Hằng n&oacute;i một lần.<br />\r\n<br />\r\nChỉ bất qu&aacute;, bởi v&igrave; hắn tự th&acirc;n th&acirc;n phận thị gi&aacute;c c&ugrave;ng nguồn tin tức vấn đề, để Th&ocirc;i Hằng sinh ra ức điểm điểm l&yacute; giải sai lầm.<br />\r\n<br />\r\nTại Hồng Ph&uacute; Qu&yacute; kh&ocirc;ng giữ lại ch&uacute;t n&agrave;o mi&ecirc;u tả xuống.<br />\r\n<br />\r\nMột c&aacute;i x&atilde; hội đen tu ti&ecirc;n thế giới tại Th&ocirc;i Hằng trong l&ograve;ng dần dần th&agrave;nh h&igrave;nh, đồng thời hiểu được c&aacute;i kia ho&agrave;ng triều kỹ c&agrave;ng t&igrave;nh huống.<br />\r\n<br />\r\nMặc d&ugrave; c&aacute;i n&agrave;y ho&agrave;ng triều l&agrave; tu ti&ecirc;n m&ocirc;n ph&aacute;i thuộc hạ thế lực, nhưng tựa hồ vẫn như cũ trốn kh&ocirc;ng tho&aacute;t chu kỳ quy luật, quốc ph&uacute;c vừa qua khỏi ba trăm năm, liền đ&atilde; thổ địa s&aacute;t nhập, th&ocirc;n t&iacute;nh cực kỳ nghi&ecirc;m trọng, nạn d&acirc;n khắp nơi tr&ecirc;n đất, một bộ tận thế cảnh tượng.<br />\r\n<br />\r\nM&agrave; tu ti&ecirc;n m&ocirc;n ph&aacute;i đối với c&aacute;i n&agrave;y ngồi nh&igrave;n kh&ocirc;ng để &yacute; tới , mặc cho ho&agrave;ng triều đại loạn c&aacute;ch l&agrave;m, cũng ph&ugrave; hợp Th&ocirc;i Hằng đối x&atilde; hội đen Tu Ti&ecirc;n giới nhận biết.<br />\r\n<br />\r\n&quot;Sinh hoạt tại dạng n&agrave;y thế giới, thật đ&uacute;ng l&agrave; qu&aacute; thảm r&ocirc;̀i.&quot; Th&ocirc;i Hằng c&oacute; ch&uacute;t thương hại vỗ vỗ Hồng Ph&uacute; Qu&yacute; bả vai, &quot;Qu&aacute; cực khổ.&quot;<br />\r\n<br />\r\nHo&agrave;ng triều chỉ l&agrave; m&ocirc;n ph&aacute;i thuộc hạ, b&aacute;ch t&iacute;nh tự nhi&ecirc;n l&agrave; chỉ l&agrave; m&ocirc;n ph&aacute;i chăn thả cừu non.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; ai sẽ để &yacute; s&uacute;c vật chết sống.<br />\r\n<br />\r\nCoi như đằng sau gia nhập m&ocirc;n ph&aacute;i, th&agrave;nh c&ocirc;ng tu ti&ecirc;n, tho&aacute;t ly cừu non h&agrave;ng ngũ, cũng muốn đối mặt c&agrave;ng t&agrave;n khốc hơn x&atilde; hội đen Tu Ti&ecirc;n giới.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nMột chữ.<br />\r\n<br />\r\nThảm!<br />\r\n<br />\r\n&quot;Ti&ecirc;n sinh! .&quot; Hồng Ph&uacute; Qu&yacute; bỗng nhi&ecirc;n mở miệng, một mực cung k&iacute;nh hướng Th&ocirc;i Hằng h&agrave;nh lễ, &quot;Ti&ecirc;n sinh, những năm n&agrave;y ta lưu l&atilde;ng tứ xứ, đi qua rất nhiều địa phương, thấy qua qu&aacute; nhiều th&ecirc; thảm cảnh tượng.<br />\r\n<br />\r\n&quot;V&ocirc; số c&ugrave;ng ta đồng dạng d&ograve;ng người c&aacute;ch kh&ocirc;ng nơi y&ecirc;n sống, v&ocirc; số c&ugrave;ng cha mẹ của ta huynh muội đồng dạng bị tươi sống chết đ&oacute;i, thậm ch&iacute; bị người ăn hết! Triều đ&igrave;nh mặc kệ nh&oacute;m ch&uacute;ng ta, thần ti&ecirc;n cũng mặc kệ nh&oacute;m ch&uacute;ng ta, kh&ocirc;ng c&oacute; người n&agrave;o cứu nh&oacute;m ch&uacute;ng ta. . .<br />\r\n<br />\r\n&quot;Ta muốn cải biến đ&acirc;y hết thảy , ta muốn khiến c&aacute;i n&agrave;y c&ugrave;ng ta đồng dạng người vượt qua tốt thời gian, nhưng ta lại kh&ocirc;ng biết r&otilde; phải n&ecirc;n l&agrave;m như thế n&agrave;o. . . Hồng Ph&uacute; Qu&yacute; cả gan, cầu ti&ecirc;n sinh ban thưởng cứu thế chi ph&aacute;p!&quot;<br />\r\n<br />\r\n&quot;. . .&quot; Th&ocirc;i Hằng hơi trầm mặc, sau đ&oacute; n&oacute;i, &quot;Ngươi l&agrave; muốn thay đổi triều đại?&quot;<br />\r\n<br />\r\n&quot;Đổi, thay đổi triều đại?&quot; Hồng Ph&uacute; Qu&yacute; nghe vậy đầu ti&ecirc;n l&agrave; sững sờ, lại c&oacute; ch&uacute;t m&ecirc; mang, cuối c&ugrave;ng trịnh trọng kỳ sự gật đầu, &quot;V&acirc;ng, kh&ocirc;ng sai! Ta muốn thay đổi triều đại, cải thi&ecirc;n ho&aacute;n địa, th&agrave;nh lập một c&aacute;i l&agrave;m cho tất cả mọi người đều c&oacute; thể ăn cơm no thời đại mới!&quot;<br />\r\n<br />\r\n&Aacute;nh mắt của hắn giống như l&agrave; tại tỏa &aacute;nh s&aacute;ng, sau c&ugrave;ng một đoạn văn, cơ hồ l&agrave; k&ecirc;u đi ra.<br />\r\n<br />\r\n&quot;Ngươi nếu thực như thế?&quot; Th&ocirc;i Hằng ch&acirc;n m&agrave;y cau lại, trầm giọng n&oacute;i, &quot;Con đường n&agrave;y ch&uacute; định long đong, thậm ch&iacute; khả năng chết kh&ocirc;ng c&oacute; chỗ ch&ocirc;n.&quot;<br />\r\n<br />\r\nTại một c&aacute;i x&atilde; hội đen tu ti&ecirc;n thế giới tạo phản, th&agrave;nh lập mới ho&agrave;ng triều.<br />\r\n<br />\r\nTr&igrave;nh độ kh&oacute; khăn c&oacute; thể nghĩ.<br />\r\n<br />\r\nTrải qua c&aacute;i n&agrave;y mấy ng&agrave;y ở chung, Hồng Ph&uacute; Qu&yacute; cho hắn ấn tượng rất kh&ocirc;ng tệ, m&agrave; lại cũng coi l&agrave; gi&uacute;p hắn đột ph&aacute; cảnh giới.<br />\r\n<br />\r\nHắn kh&ocirc;ng qu&aacute; muốn cho đứa nhỏ n&agrave;y rơi v&agrave;o c&aacute;i kết cục bi thảm.<br />\r\n<br />\r\n&quot;Cầu ti&ecirc;n sinh cho ph&eacute;p.&quot; Hồng Ph&uacute; Qu&yacute; &aacute;nh mắt vẫn như cũ ki&ecirc;n định.<br />\r\n<br />\r\n&quot;Nếu như ngươi chịu an ổn sinh hoạt, ta có th&ecirc;̉ bảo vệ ngươi một thế ph&uacute; qu&yacute;.&quot; Th&ocirc;i Hằng đứng chắp tay, thản nhi&ecirc;n n&oacute;i, &quot;Nhưng nếu đi con đường kia, cũng chỉ c&oacute; thể tự cầu Tạo H&oacute;a. Ngươi sẽ kh&ocirc;ng hối hận sao?&quot;<br />\r\n<br />\r\n&quot;Bất Hối!&quot; Hồng Ph&uacute; Qu&yacute; &aacute;nh mắt đ&atilde; vững như b&agrave;n thạch.<br />\r\n<br />\r\n&quot;Ai. . .&quot; Th&ocirc;i Hằng nhẹ nh&agrave;ng lắc đầu thở d&agrave;i, tựa hồ phi thường thất vọng, trực tiếp đi qua đ&aacute;nh Hồng Ph&uacute; Qu&yacute; đầu ba lần, sau đ&oacute; liền chắp tay sau lưng đi v&agrave;o trong ph&ograve;ng, trực tiếp đem cửa ch&iacute;nh đ&oacute;ng lại.<br />\r\n<br />\r\nHồng Ph&uacute; Qu&yacute; ngẩn người, đứng đầy một một l&aacute;t mới ho&agrave;n hồn, một mực cung k&iacute;nh dập đầu h&agrave;nh lễ, &quot;B&aacute;i tạ ti&ecirc;n sinh đại &acirc;n!&quot;<br />\r\n<br />\r\nHắn đ&atilde; đo&aacute;n được Th&ocirc;i Hằng &yacute; tứ.</p>', '2021-11-28 16:43:32', 3, 1),
(236, 61, 'Thời gian trôi mau một trăm năm', '5', '<p>Khuya khoắt thời gian.<br />\r\n<br />\r\nHồng Ph&uacute; Qu&yacute; từ cửa sau tiến v&agrave;o biệt thự, vừa vặn nh&igrave;n thấy Th&ocirc;i Hằng ngồi ở ph&iacute;a trước tr&ecirc;n một c&aacute;i ghế, đưa lưng về ph&iacute;a hắn.<br />\r\n<br />\r\nHắn kh&ocirc;ng d&aacute;m trực tiếp l&ecirc;n tiếng, chỉ quỳ ở nơi đ&oacute;, lẳng lặng chờ đợi.<br />\r\n<br />\r\nQua tốt một một l&aacute;t.<br />\r\n<br />\r\nTh&ocirc;i Hằng mới nhẹ nh&agrave;ng lắc đầu, thở d&agrave;i n&oacute;i: &quot;Đ&atilde; ngươi ngộ ra tới, ta liền truyền cho ngươi một ch&uacute;t ph&aacute;p m&ocirc;n đi.&quot;<br />\r\n<br />\r\nHồng Ph&uacute; Qu&yacute; nghe vậy mừng rỡ, vội v&agrave;ng n&oacute;i: &quot;Khấu tạ ti&ecirc;n sinh!&quot;<br />\r\n<br />\r\nTh&ocirc;i Hằng đứng dậy đứng l&ecirc;n, chắp tay tiến l&ecirc;n, n&oacute;i: &quot;Đi theo ta thư ph&ograve;ng đi.&quot;<br />\r\n<br />\r\nHắn l&agrave; kh&ocirc;ng qu&aacute; muốn cho Hồng Ph&uacute; Qu&yacute; đi con đường kia.<br />\r\n<br />\r\nNhưng Hồng Ph&uacute; Qu&yacute; lại quyết t&acirc;m ki&ecirc;n tr&igrave;.<br />\r\n<br />\r\nThế l&agrave; hắn liền d&ugrave;ng một lần T&acirc;y Du K&yacute; b&ecirc;n trong ngạnh.<br />\r\n<br />\r\nNếu l&agrave; Hồng Ph&uacute; Qu&yacute; ngộ kh&ocirc;ng đến trong đ&oacute; &yacute; tứ, kia về sau liền kh&ocirc;ng cần nhắc lại cứu thế chi ph&aacute;p sự t&igrave;nh.<br />\r\n<br />\r\nNếu như Hồng Ph&uacute; Qu&yacute; có th&ecirc;̉ ngộ đến, đ&oacute; ch&iacute;nh l&agrave; thi&ecirc;n &yacute; như thế.<br />\r\n<br />\r\nVề phần muốn truyền thụ c&aacute;i g&igrave;, hắn cũng đ&atilde; nghĩ kỹ.<br />\r\n<br />\r\nLuận tạo phản, luận cải thi&ecirc;n ho&aacute;n địa, luận để người b&igrave;nh thường ăn cơm no, ai hơn được vị kia Th&aacute;i Tổ?<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nTrong thư ph&ograve;ng.<br />\r\n<br />\r\n&quot;Ai l&agrave; địch nh&acirc;n, ai l&agrave; bằng hữu, đ&acirc;y l&agrave; ngươi đầu ti&ecirc;n muốn l&agrave;m r&otilde; r&agrave;ng vấn đề.&quot;<br />\r\n<br />\r\nTh&ocirc;i Hằng ở lưng đối Hồng Ph&uacute; Qu&yacute;, thản nhi&ecirc;n n&oacute;i, &quot;M&agrave; muốn l&agrave;m cho tất cả mọi người đều c&oacute; thể ăn cơm no, đầu ti&ecirc;n liền muốn cam đoan tất cả mọi người đều c&oacute; ruộng đồng, như vậy ruộng từ đ&acirc;u đến? Đ&acirc;y cũng l&agrave; ngươi muốn suy nghĩ vấn đề. . .&quot;<br />\r\n<br />\r\nHắn hiện tại l&agrave; đem một ch&uacute;t cơ sở l&yacute; niệm khẩu thuật cho Hồng Ph&uacute; Qu&yacute; nghe, trước ti&ecirc;n đem tư tưởng nền tảng tạo dựng l&ecirc;n.<br />\r\n<br />\r\nĐột ph&aacute; đến Luyện Kh&iacute; bảy tầng về sau, hắn kh&ocirc;ng chỉ lực lượng mạnh l&ecirc;n, tinh thần cũng thay đổi mạnh.<br />\r\n<br />\r\nTư duy trở n&ecirc;n v&ocirc; c&ugrave;ng r&otilde; r&agrave;ng, tr&iacute; nhớ c&agrave;ng l&agrave; cường đại, c&oacute; thể trở về nhớ tới m&igrave;nh tại Địa Cầu l&uacute;c nh&igrave;n qua tất cả thư tịch nội dung.<br />\r\n<br />\r\nĐương nhi&ecirc;n, truyền thụ cho Hồng Ph&uacute; Qu&yacute; l&yacute; luận khẳng định kh&ocirc;ng thể ho&agrave;n to&agrave;n rập khu&ocirc;n.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nD&ugrave; sao, thế giới bối cảnh l&agrave; ho&agrave;n to&agrave;n kh&aacute;c biệt.<br />\r\n<br />\r\nC&acirc;̀n thực sự cầu thị tiến h&agrave;nh nhất định sửa chữa.<br />\r\n<br />\r\nTh&ocirc;i Hằng dự định đem những n&agrave;y sửa chữa sau l&yacute; luận bi&ecirc;n soạn th&agrave;nh một bộ s&aacute;ch nhỏ đưa cho Hồng Ph&uacute; Qu&yacute;, cung cấp hắn rời đi nơi n&agrave;y về sau chậm r&atilde;i nghi&ecirc;n cứu.<br />\r\n<br />\r\nDanh tự liền gọi &laquo; Đại Đồng Tập &raquo;.<br />\r\n<br />\r\nThi&ecirc;n hạ đại đồng!<br />\r\n<br />\r\nHồng Ph&uacute; Qu&yacute; đứng ở một b&ecirc;n, nghe Th&ocirc;i Hằng khẩu thuật l&yacute; luận, trong l&ograve;ng đ&atilde; nhấc l&ecirc;n kinh đ&agrave;o hải l&atilde;ng.<br />\r\n<br />\r\nNhững l&yacute; luận n&agrave;y bao h&agrave;m tư tưởng c&ugrave;ng kiến giải, l&agrave; hắn chưa hề nghĩ tới, c&agrave;ng l&agrave; chưa từng nghe qua, để hắn c&oacute; một loại b&aacute;t v&acirc;n kiến nhật cảm gi&aacute;c, phảng phất như l&agrave; vạch ph&aacute; v&ocirc; tận hắc &aacute;m một đạo &aacute;nh s&aacute;ng!<br />\r\n<br />\r\nQu&aacute; lợi hại!<br />\r\n<br />\r\nTh&ocirc;i ti&ecirc;n sinh thật l&agrave; qu&aacute; lợi hại!<br />\r\n<br />\r\nHiện tại hắn trong l&ograve;ng nhận thấy đến rung động, thậm ch&iacute; c&ograve;n tại &quot;Biết được&quot; Th&ocirc;i Hằng l&agrave; chưởng khống rất nhiều thế giới vĩ đại tồn tại ph&iacute;a tr&ecirc;n!<br />\r\n<br />\r\nBất qu&aacute;, Hồng Ph&uacute; Qu&yacute; cuối c&ugrave;ng tuổi kh&ocirc;ng lớn lắm, kiến thức c&oacute; hạn, kh&ocirc;ng đọc s&aacute;ch nhiều, đối với hắn m&agrave; n&oacute;i, Th&ocirc;i Hằng giảng những cơ sở n&agrave;y l&yacute; luận, vẫn l&agrave; c&oacute; rất nhiều kh&oacute; hiểu địa phương.<br />\r\n<br />\r\nBởi vậy, hắn thường xuy&ecirc;n gấp đến độ v&ograve; đầu bứt tai, muốn hỏi thăm.<br />\r\n<br />\r\nNghe tới Th&ocirc;i Hằng n&oacute;i sẽ đem những l&yacute; luận n&agrave;y chỉnh bi&ecirc;n th&agrave;nh sổ về sau, Hồng Ph&uacute; Qu&yacute; cơ hồ vui vẻ muốn nhảy dựng l&ecirc;n.<br />\r\n<br />\r\nSau đ&oacute; năm ng&agrave;y thời gian, Th&ocirc;i Hằng đều đang giảng giải m&ocirc;n n&agrave;y đồ long chi thuật.<br />\r\n<br />\r\nĐồng thời cũng trong bi&ecirc;n chế toản &laquo; Đại Đồng Tập &raquo;.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nĐ&acirc;y l&agrave; &aacute;nh nắng tươi s&aacute;ng một ng&agrave;y.<br />\r\n<br />\r\nTh&ocirc;i Hằng cho sắp rời đi Hồng Ph&uacute; Qu&yacute; chuẩn bị ba kiện lễ vật tiễn đưa.<br />\r\n<br />\r\nTh&ocirc;ng qua kh&ocirc;ng gian kẽ hở ngo&agrave;i &yacute; muốn rơi xuống tiến t&acirc;n thủ kh&ocirc;ng gian người, đều sẽ nhận đến từ nguy&ecirc;n bản thế giới lực hấp dẫn, nhiều nhất mười hai ng&agrave;y liền sẽ trở về.<br />\r\n<br />\r\nBởi vậy, h&ocirc;m nay l&agrave; Hồng Ph&uacute; Qu&yacute; lưu tại nơi n&agrave;y cuối c&ugrave;ng một ng&agrave;y.<br />\r\n<br />\r\nNh&igrave;n xem Th&ocirc;i Hằng ph&oacute;ng tới trước mặt m&igrave;nh c&aacute;i n&agrave;y ba kiện lễ vật, Hồng Ph&uacute; Qu&yacute; kh&ocirc;ng khỏi c&oacute; ch&uacute;t hoảng hốt.<br />\r\n<br />\r\nThời gian tr&ocirc;i qua thật nhanh.<br />\r\n<br />\r\nC&aacute;i n&agrave;y mười hai ng&agrave;y b&ecirc;n trong, m&igrave;nh ngộ nhập c&aacute;i n&agrave;y đ&agrave;o nguy&ecirc;n, nh&igrave;n thấy Ti&ecirc;n T&ocirc;n, thể nghiệm trước kia nghĩ cũng kh&ocirc;ng d&aacute;m nghĩ sinh hoạt, c&agrave;ng l&agrave; đạt được tha thiết ước mơ cứu thế chi ph&aacute;p!<br />\r\n<br />\r\nGiống như mộng ảo.<br />\r\n<br />\r\nM&agrave; b&acirc;y giờ, c&aacute;i n&agrave;y mộng liền muốn tỉnh lại.<br />\r\n<br />\r\nM&igrave;nh muốn một lần nữa đi đối mặt b&ecirc;n ngo&agrave;i kia th&ecirc; thảm tận thế cảnh tượng, cũng nhất định phải đi đối mặt!<br />\r\n<br />\r\nThế giới b&ecirc;n ngo&agrave;i, đang chờ m&igrave;nh đi cải biến!<br />\r\n<br />\r\n&quot;Vạn Tạ ti&ecirc;n sinh!&quot; Hồng Ph&uacute; Qu&yacute; cung k&iacute;nh quỳ gối.<br />\r\n<br />\r\n&quot;Ngươi có th&ecirc;̉ hảo hảo c&ograve;n sống, thọ hết chết gi&agrave;, coi như c&aacute;m ơn ta.&quot; Th&ocirc;i Hằng kho&aacute;t tay &aacute;o, chỉ chỉ b&ecirc;n cạnh tr&ecirc;n b&agrave;n ba kiện đồ vật, &quot;Những n&agrave;y đồ vật đều cất kỹ, sẽ c&oacute; trợ gi&uacute;p.&quot;<br />\r\n<br />\r\nC&aacute;i n&agrave;y ba kiện đồ vật theo thứ tự l&agrave; &laquo; Đại Đồng Tập &raquo;, một nhỏ tr&oacute;i th&acirc;n mầm, c&ugrave;ng một thanh tinh thiết trường kiếm.<br />\r\n<br />\r\n&quot;V&acirc;ng, ti&ecirc;n sinh.&quot; Hồng Ph&uacute; Qu&yacute; cẩn thận nghi&ecirc;m t&uacute;c đem &laquo; Đại Đồng Tập &raquo; cất kỹ, nh&igrave;n một ch&uacute;t khoai lang th&acirc;n mầm, nghi ngờ n&oacute;i: &quot;Ti&ecirc;n sinh, đ&acirc;y l&agrave; c&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\n&quot;N&oacute; l&agrave; khoai lang th&acirc;n mầm, trồng xuống về sau, nhưng mẫu sinh vạn c&acirc;n lương.&quot; Th&ocirc;i Hằng mỉm cười n&oacute;i, đ&acirc;y l&agrave; t&acirc;n thủ n&ocirc;ng trường cải tiến bản khoai lang mầm, đang gieo trồng trước sẽ kh&ocirc;ng hư thối.<br />\r\n<br />\r\n&quot;Mẫu sinh vạn, vạn c&acirc;n lương? !&quot; Hồng Ph&uacute; Qu&yacute; nghe vậy lập tức mở to hai mắt nh&igrave;n, mặt mũi tr&agrave;n đầy kh&ocirc;ng thể tin biểu lộ, lời n&agrave;y nếu như kh&ocirc;ng phải từ Th&ocirc;i Hằng b&ecirc;n trong miệng n&oacute;i ra, hắn liền tin cũng kh&ocirc;ng d&aacute;m tin.<br />\r\n<br />\r\nTr&ecirc;n đời l&agrave;m sao lại c&oacute; loại n&agrave;y sản lượng hoa m&agrave;u!<br />\r\n<br />\r\nĐ&acirc;y tuyệt đối l&agrave; ti&ecirc;n lương a!<br />\r\n<br />\r\n&quot;Nếu ngươi thật c&oacute; thể đắc thế, c&oacute; thể đem những n&agrave;y th&acirc;n mầm gieo xuống, đến l&uacute;c đ&oacute; liền biết r&otilde;.&quot; Th&ocirc;i Hằng lại cầm lấy thanh trường kiếm kia, n&oacute;i: &quot;Đ&acirc;y l&agrave; ta đưa cho ngươi ph&ograve;ng th&acirc;n chi vật, b&ecirc;n trong c&oacute; ta một ch&uacute;t ph&aacute;p lực, c&oacute; thể vận dụng ch&iacute;n lần.&quot;<br />\r\n<br />\r\nMặc d&ugrave; trong kiếm ẩn chứa ph&aacute;p lực ti&ecirc;u chuẩn chỉ c&oacute; Luyện Kh&iacute; bảy tầng, đối cao cấp tu ti&ecirc;n giả kh&ocirc;ng c&oacute; t&aacute;c dụng g&igrave;, nhưng Hồng Ph&uacute; Qu&yacute; muốn thay đổi triều đại, chỉ c&oacute; thể đem mục ti&ecirc;u đặt ở thế gian ho&agrave;ng triều đấu tranh b&ecirc;n tr&ecirc;n, kh&ocirc;ng c&oacute; khả năng c&ugrave;ng tu ti&ecirc;n giả ch&iacute;nh diện chiến đấu.<br />\r\n<br />\r\nBởi vậy, thanh kiếm n&agrave;y hẳn l&agrave; cũng đủ.<br />\r\n<br />\r\n&quot;Vạn tạ trước. . .&quot; Hồng Ph&uacute; Qu&yacute; đối Th&ocirc;i Hằng cảm k&iacute;ch tới cực điểm, lại muốn h&agrave;nh lễ cảm tạ, nhưng lại bị Th&ocirc;i Hằng d&ugrave;ng ph&aacute;p lực ngăn cản.<br />\r\n<br />\r\n&quot;Hảo hảo sống đến thọ hết chết gi&agrave; ch&iacute;nh l&agrave; tốt nhất cảm tạ.&quot; Th&ocirc;i Hằng mỉm cười n&oacute;i.<br />\r\n<br />\r\n&quot;V&acirc;ng! Ti&ecirc;n sinh!&quot; Hồng Ph&uacute; Qu&yacute; nặng nề m&agrave; gật đầu.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nMười hai ng&agrave;y thời gian vừa tới.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nTh&ocirc;i Hằng liền thấy Hồng Ph&uacute; Qu&yacute; tr&ecirc;n th&acirc;n s&aacute;ng l&ecirc;n một đạo v&ocirc; c&ugrave;ng m&atilde;nh liệt kim quang, mang theo hắn hư kh&ocirc;ng ti&ecirc;u thất tại c&aacute;i n&agrave;y t&acirc;n thủ kh&ocirc;ng gian b&ecirc;n trong.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; thế giới ph&aacute;p tắc lực lượng.<br />\r\n<br />\r\nLấy Th&ocirc;i Hằng cảnh giới b&acirc;y giờ tu vi căn bản l&agrave; kh&ocirc;ng thể n&agrave;o hiểu được.<br />\r\n<br />\r\nHắn tại nguy&ecirc;n chỗ đứng một một l&aacute;t, ngẩng đầu nh&igrave;n bầu trời, lại nh&igrave;n một ch&uacute;t c&aacute;ch đ&oacute; kh&ocirc;ng xa t&acirc;n thủ ph&ograve;ng nhỏ, cuối c&ugrave;ng lắc đầu cười khẽ bắt đầu.<br />\r\n<br />\r\n&quot;Lại l&agrave; một người a.&quot;<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nLại qua hai mươi năm.<br />\r\n<br />\r\nTh&ocirc;i Hằng tu vi cảnh giới đột ph&aacute; đến Luyện Kh&iacute; ch&iacute;n tầng.<br />\r\n<br />\r\nC&aacute;i n&agrave;y tốc độ tu luyện c&ugrave;ng những c&aacute;i kia thi&ecirc;n t&agrave;i so lời n&oacute;i, đơn giản chậm l&agrave;m cho người giận s&ocirc;i.<br />\r\n<br />\r\nPh&oacute;ng tới rất nhiều tu ti&ecirc;n trong tiểu thuyết liền l&agrave;m bị nh&acirc;n vật ch&iacute;nh đ&aacute;nh mặt ph&aacute;o h&ocirc;i cũng kh&ocirc;ng c&oacute; tư c&aacute;ch.<br />\r\n<br />\r\nBất qu&aacute;, hiện tại Th&ocirc;i Hằng cũng sớm đ&atilde; quen thuộc loại n&agrave;y trạng th&aacute;i tu luyện, đ&atilde; kh&ocirc;ng giống vừa mới bắt đầu thời điểm vội v&atilde; như vậy n&oacute;ng nảy.<br />\r\n<br />\r\nMột bước một c&aacute;i dấu ch&acirc;n, l&agrave;m g&igrave; chắc đ&oacute; tu luyện cũng kh&ocirc;ng tệ.<br />\r\n<br />\r\nBa mươi năm sau, hắn th&agrave;nh c&ocirc;ng dựng th&agrave;nh Đạo Cơ, l&agrave;m từng bước bước v&agrave;o Tr&uacute;c Cơ cảnh giới.<br />\r\n<br />\r\nLại bốn mươi năm về sau, hắn mới ho&agrave;n th&agrave;nh Tr&uacute;c Cơ cảnh giới sơ kỳ tu luyện, v&igrave; đột ph&aacute; Tr&uacute;c Cơ trung kỳ l&agrave;m chuẩn bị.<br />\r\n<br />\r\nThời gian tr&ocirc;i mau, Th&ocirc;i Hằng xuy&ecirc;n qua đến nay, đ&atilde; c&oacute; trăm năm.<br />\r\n<br />\r\nCự ly Hồng Ph&uacute; Qu&yacute; ngộ nhập nơi đ&acirc;y cũng c&oacute; ch&iacute;n mươi năm.<br />\r\n<br />\r\nMột ng&agrave;y n&agrave;y, Th&ocirc;i Hằng lại đi tới t&acirc;n thủ n&ocirc;ng trường thu đồ ăn.<br />\r\n<br />\r\nL&agrave;m c&acirc;̀m tới một khối tr&acirc;u khối cơ thịt l&uacute;c, hắn mở ra đ&atilde; ba mươi năm chưa n&oacute;i qua một c&acirc;u miệng, khẽ nở nụ cười.<br />\r\n<br />\r\n&quot;Rất nhiều năm qua đi, cũng kh&ocirc;ng biết r&otilde; kia tiểu tử c&oacute; hay kh&ocirc;ng thực hiện ch&iacute; hướng của m&igrave;nh, lại c&oacute; hay kh&ocirc;ng đ&atilde; thọ hết chết gi&agrave;?&quot;<br />\r\n<br />\r\nĐ&uacute;ng l&uacute;c n&agrave;y, đ&atilde; ch&iacute;n mươi năm kh&ocirc;ng c&oacute; thay đổi g&igrave; bầu trời, bỗng nhi&ecirc;n lại nổi l&ecirc;n gợn s&oacute;ng.</p>', '2021-11-28 16:43:54', 4, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(237, 61, 'Trên trời rơi xuống tiểu cô nương', '6', '<p>&quot;A a a! !&quot;<br />\r\n<br />\r\nGi&agrave;u c&oacute; nguy&ecirc;n kh&iacute; tiếng k&ecirc;u thảm thiết từ tr&ecirc;n trời truyền đến, một c&aacute;i th&acirc;n ảnh kiều tiểu xuy&ecirc;n qua bầu trời gợn s&oacute;ng, cấp tốc rơi xuống ph&iacute;a dưới.<br />\r\n<br />\r\n&quot;Lần n&agrave;y rơi v&agrave;o người tới ngược lại l&agrave; rất tinh thần.&quot; Th&ocirc;i Hằng nghe được thanh &acirc;m n&agrave;y lập tức nở nụ cười.<br />\r\n<br />\r\nLập tức, hắn nhẹ nh&agrave;ng kho&aacute;t tay, liền d&ugrave;ng ph&aacute;p lực th&ocirc;i động kh&iacute; lưu, đem c&aacute;i kia từ tr&ecirc;n trời gi&aacute;ng xuống th&acirc;n ảnh n&acirc;ng ở kh&ocirc;ng trung, ph&ograve;ng ngừa n&agrave;ng trực tiếp ng&atilde; xuống khỏi tới.<br />\r\n<br />\r\nĐ&acirc;y kh&ocirc;ng phải ph&aacute;p thuật, l&agrave; c&aacute;i n&agrave;y trăm năm qua, ch&iacute;nh hắn suy nghĩ ra được một điểm ph&aacute;p lực vận dụng tiểu kỹ xảo.<br />\r\n<br />\r\nĐối với Tr&uacute;c Cơ kỳ tu ti&ecirc;n giả tới n&oacute;i, ph&aacute;p lực đ&atilde; kh&ocirc;ng c&ograve;n chỉ l&agrave; chứa đựng tại thể nội &quot;Năng lượng&quot; .<br />\r\n<br />\r\nM&agrave; l&agrave; triệt để trở th&agrave;nh th&acirc;n thể của m&igrave;nh một bộ phận.<br />\r\n<br />\r\nBởi vậy, hắn mặc d&ugrave; kh&ocirc;ng biết r&otilde; muốn l&agrave;m sao mới c&oacute; thể đem c&aacute;i n&agrave;y bộ phận biến th&agrave;nh thủy hỏa phong l&ocirc;i loại h&igrave;nh ph&aacute;p thuật, nhưng khai ph&aacute;t ra một ch&uacute;t đặc th&ugrave; c&aacute;ch d&ugrave;ng đến cũng kh&ocirc;ng phải l&agrave; vấn đề g&igrave;.<br />\r\n<br />\r\nTại Th&ocirc;i Hằng ph&aacute;p lực n&acirc;ng đỡ ph&iacute;a dưới, c&aacute;i n&agrave;y mới &quot;Kh&aacute;ch đến từ thi&ecirc;n ngoại&quot; chậm r&atilde;i hạ xuống tới.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; một người d&aacute;ng dấp hết sức xinh đẹp tiểu c&ocirc; nương, ước chừng chỉ c&oacute; mười ba mười bốn tuổi bộ d&aacute;ng, l&agrave;n da trắng n&otilde;n thủy nộn, khu&ocirc;n mặt đường cong nhu h&ograve;a, ngũ quan c&agrave;ng l&agrave; cực k&igrave; tinh xảo, giống như l&agrave; c&aacute;i phấn đi&ecirc;u ngọc tr&aacute;c oa oa.<br />\r\n<br />\r\nTr&ecirc;n người n&agrave;ng quần &aacute;o cũng kh&ocirc;ng tầm thường, mặc ch&iacute;nh l&agrave; m&agrave;u xanh nhạt tơ lụa may v&aacute;y &aacute;o, ph&iacute;a tr&ecirc;n th&ecirc;u l&ecirc;n m&agrave;u v&agrave;ng kim hoa văn, ch&ecirc;́ tác mười phần tinh mỹ, tr&ecirc;n đầu mang theo chất ngọc nạm v&agrave;ng vật trang sức, eo thon ở giữa c&ograve;n mang theo một khối &ocirc;n nhuận ngọc bội.<br />\r\n<br />\r\nC&oacute; thể n&oacute;i l&agrave; tơ lụa, v&agrave;ng bạc ngọc thạch đều tại hắn th&acirc;n.<br />\r\n<br />\r\nRi&ecirc;ng n&agrave;y người mặc mang, liền đủ đ&atilde; từng Hồng Ph&uacute; Qu&yacute; tích lũy cả đời, c&ograve;n tích lũy kh&ocirc;ng đến.<br />\r\n<br />\r\nĐ&acirc;y cũng l&agrave; một c&aacute;i nh&agrave; gi&agrave;u đại tiểu thư.<br />\r\n<br />\r\nA kh&ocirc;ng đ&uacute;ng, ni&ecirc;n kỷ qu&aacute; nhỏ, c&ograve;n kh&ocirc;ng t&iacute;nh lớn, Th&ocirc;i Hằng lại đ&aacute;nh gi&aacute; tiểu c&ocirc; nương n&agrave;y hai mắt.<br />\r\n<br />\r\nTạm thời c&ograve;n chỉ c&oacute; thể gọi l&agrave; tiểu tiểu thư.<br />\r\n<br />\r\nTh&ocirc;i Hằng dự định đi qua ch&agrave;o hỏi, nhưng h&aacute; miệng nhưng lại kh&ocirc;ng biết r&otilde; n&ecirc;n n&oacute;i c&aacute;i g&igrave;, trong l&ograve;ng thầm nghĩ: &quot;Dạng n&agrave;y một c&aacute;i tiểu c&ocirc; nương, bỗng nhi&ecirc;n đi v&agrave;o một c&aacute;i xa lạ địa phương, gặp ta người xa lạ n&agrave;y, c&oacute; thể hay kh&ocirc;ng rất sợ h&atilde;i?&quot;<br />\r\n<br />\r\nNhưng lại tại c&aacute;i n&agrave;y thời điểm, tiểu c&ocirc; nương kia đ&atilde; nhảy nhảy Khi&ecirc;u Khi&ecirc;u chạy chậm đến đ&acirc;y, mắt to vụt s&aacute;ng vụt s&aacute;ng, v&ocirc; c&ugrave;ng t&ograve; m&ograve; nh&igrave;n xem Th&ocirc;i Hằng, vui vẻ n&oacute;i: &quot;Ca ca, ngươi thật lợi hại, ngươi l&agrave; thần ti&ecirc;n sao?&quot;<br />\r\n<br />\r\nTựa hồ tuyệt kh&ocirc;ng sợ người lạ.<br />\r\n<br />\r\n&quot;Ta chỉ l&agrave;. . .&quot; Th&ocirc;i Hằng vừa muốn giống trước đ&oacute; gặp được Hồng Ph&uacute; Qu&yacute; l&uacute;c đồng dạng giải th&iacute;ch.<br />\r\n<br />\r\n&quot;Ng&agrave;i nhất định l&agrave; thần ti&ecirc;n đ&uacute;ng kh&ocirc;ng! Ca ca!&quot; Tiểu c&ocirc; nương nhưng thật giống như l&agrave; đ&atilde; nhận định điểm ấy, k&iacute;ch động đến gương mặt phiếm hồng, trong mắt tr&agrave;n đầy s&ugrave;ng b&aacute;i &aacute;nh mắt, kh&ocirc;ng đợi Th&ocirc;i Hằng giải th&iacute;ch xong liền tu&acirc;n hỏi, &quot;Ta c&oacute; thể gọi ng&agrave;i thần ti&ecirc;n ca ca sao?&quot;<br />\r\n<br />\r\n&quot;Ai, gọi ta Th&ocirc;i ti&ecirc;n sinh đi.&quot; Th&ocirc;i Hằng &yacute; đồ uốn nắn.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Được rồi đ&acirc;y, thần ti&ecirc;n ca ca!&quot; Tiểu c&ocirc; nương cười h&igrave; h&igrave; m&agrave; n&oacute;i, &quot;Gọi ti&ecirc;n sinh nhiều tr&ocirc;ng c&oacute; vẻ gi&agrave; a, ng&agrave;i nh&igrave;n c&ograve;n trẻ như vậy, lớn hơn ta kh&ocirc;ng được mấy tuổi đ&acirc;y, vẫn l&agrave; gọi thần ti&ecirc;n ca ca c&agrave;ng tốt hơn một ch&uacute;t đ&uacute;ng kh&ocirc;ng!&quot;<br />\r\n<br />\r\nHiện tại Th&ocirc;i Hằng thoạt nh&igrave;n cũng chỉ mười t&aacute;m mười ch&iacute;n tuổi d&aacute;ng vẻ.<br />\r\n<br />\r\n&quot;Cũng l&agrave; kh&ocirc;ng phải kh&ocirc;ng được.&quot; Th&ocirc;i Hằng bất đắc dĩ n&oacute;i.<br />\r\n<br />\r\nBởi v&igrave; gần nhất ch&iacute;n mươi năm đều kh&ocirc;ng c&oacute; c&ugrave;ng người trao đổi qua, gần ba mươi năm ngay cả lời đều kh&ocirc;ng n&oacute;i một c&acirc;u, hắn x&atilde; giao năng lực kịch liệt tho&aacute;i h&oacute;a.<br />\r\n<br />\r\nTiểu c&ocirc; nương n&agrave;y như quen thuộc d&aacute;ng vẻ c&agrave;ng l&agrave; trực tiếp bắt hắn cho cả sẽ kh&ocirc;ng.<br />\r\n<br />\r\nTinh khiết x&atilde; tr&acirc;u chứng thuộc về l&agrave;.<br />\r\n<br />\r\n&quot;Tốt &agrave;i! Thần ti&ecirc;n ca ca, nơi n&agrave;y l&agrave; ngươi Ti&ecirc;n phủ sao?&quot; Tiểu c&ocirc; nương hoan h&ocirc; l&ecirc;n, đồng thời nh&igrave;n chung quanh.<br />\r\n<br />\r\nN&agrave;ng tr&aacute;i nh&igrave;n một c&aacute;i nh&igrave;n b&ecirc;n phải một ch&uacute;t, một bộ hiếu k&igrave; bảo bảo d&aacute;ng vẻ, sau đ&oacute; một mặt thuần ch&acirc;n nh&igrave;n về ph&iacute;a Th&ocirc;i Hằng, &quot;L&agrave; ng&agrave;i đem ta đưa đến nơi n&agrave;y sao?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải.&quot; Th&ocirc;i Hằng nhẹ nh&agrave;ng lắc đầu, con mắt nhắm lại, cười n&oacute;i, &quot;Cơ duy&ecirc;n bố tr&iacute;, ngươi mới c&oacute; thể lại tới đ&acirc;y, ngươi cũng kh&ocirc;ng cần sợ h&atilde;i, nhiều nhất mười hai ng&agrave;y, ngươi liền sẽ trở lại thế giới cũ.&quot;<br />\r\n<br />\r\nTiểu c&ocirc; nương n&agrave;y kh&ocirc;ng hề giống l&agrave; nh&igrave;n bề ngo&agrave;i như thế thi&ecirc;n ch&acirc;n v&ocirc; t&agrave;.<br />\r\n<br />\r\n&quot;Mười, mười hai ng&agrave;y?&quot; Tiểu c&ocirc; nương trong mắt l&oacute;e l&ecirc;n một tia kh&ocirc;ng tự nhi&ecirc;n, nụ cười tr&ecirc;n mặt c&oacute; ch&uacute;t cứng đờ, nhưng rất nhanh liền kh&ocirc;i phục tự nhi&ecirc;n, dường như c&oacute; ch&uacute;t uể oải m&agrave; n&oacute;i, &quot;L&agrave;m sao mới mười hai ng&agrave;y đ&acirc;y, thần ti&ecirc;n ca ca ng&agrave;i c&oacute; thể để cho ta tại to&agrave; n&agrave;y Ti&ecirc;n phủ b&ecirc;n trong chờ l&acirc;u một l&aacute;t a?&quot;<br />\r\n<br />\r\n&quot;Chờ mười hai ng&agrave;y sau đ&oacute;, ngươi tự nhi&ecirc;n l&agrave; biết r&otilde;.&quot; Th&ocirc;i Hằng mỉm cười n&oacute;i, hắn d&ugrave; sao cũng l&agrave; Tr&uacute;c Cơ kỳ tu sĩ, tư duy nhanh nhẹn, rất nhanh liền quen thuộc b&igrave;nh thường x&atilde; giao đối thoại.<br />\r\n<br />\r\n&quot;Dạng n&agrave;y a.&quot; Tiểu c&ocirc; nương một bộ bừng tỉnh đại ngộ d&aacute;ng vẻ, c&oacute; ch&uacute;t m&eacute;o một ch&uacute;t c&aacute;i đầu nhỏ, bộ d&aacute;ng mười phần đ&aacute;ng y&ecirc;u.<br />\r\n<br />\r\nBất qu&aacute;, thanh &acirc;m của n&agrave;ng c&ugrave;ng trước đ&oacute; so đ&atilde; thấp một ch&uacute;t, dường như kh&ocirc;ng biết r&otilde; tiếp xuống n&ecirc;n n&oacute;i c&aacute;i g&igrave;, nhưng n&agrave;ng rất nhanh lại giật ra m&aacute;y h&aacute;t, &quot;Ta gọi Khương Thất Thất, thần ti&ecirc;n ca ca, ng&agrave;i k&ecirc;u c&aacute;i g&igrave; nha?&quot;<br />\r\n<br />\r\n&quot;Th&ocirc;i Hằng, hằng trong vĩnh hằng.&quot; Th&ocirc;i Hằng đối với m&igrave;nh danh tự cũng kh&ocirc;ng tị hu&yacute;, liền trực tiếp n&oacute;i, cũng tốt để tiểu c&ocirc; nương n&agrave;y an t&acirc;m một ch&uacute;t.<br />\r\n<br />\r\n&quot;Thần ti&ecirc;n ca ca danh tự đều như thế c&oacute; thần ti&ecirc;n hương vị đ&acirc;y.&quot; Khương Thất Thất vẻ mặt tươi cười m&agrave; n&oacute;i, &quot;C&aacute;c thần ti&ecirc;n c&oacute; phải hay kh&ocirc;ng đều l&agrave; Vĩnh Hằng tồn tại a?&quot;<br />\r\n<br />\r\n&quot;Tiểu nha đầu n&agrave;y l&agrave; từ khoa khoa bầy ra a.&quot; Th&ocirc;i Hằng trong l&ograve;ng nhịn kh&ocirc;ng được nhả r&atilde;nh, nhưng mặt ngo&agrave;i vẫn như cũ l&agrave; mặt mỉm cười, &quot;Ta chỉ l&agrave; người b&igrave;nh thường m&agrave; th&ocirc;i.&quot;<br />\r\n<br />\r\n&quot;Thần ti&ecirc;n ca ca, giống ng&agrave;i dạng n&agrave;y. . . Ng&ocirc;. . .&quot; Khương Thất Thất c&ograve;n muốn tiếp tục n&agrave;ng khoa khoa đại nghiệp, trong bụng đột nhi&ecirc;n xuất hiện tiếng vang lại đ&aacute;nh g&atilde;y n&agrave;ng.<br />\r\n<br />\r\n&quot;&Ugrave;ng ục ục!&quot;<br />\r\n<br />\r\nN&agrave;ng đ&oacute;i bụng.<br />\r\n<br />\r\nBởi v&igrave; trầm m&ecirc; luyện v&otilde;, n&agrave;ng đều chưa kịp ăn cơm trưa, liền tiến v&agrave;o to&agrave; n&agrave;y t&acirc;n thủ kh&ocirc;ng gian b&ecirc;n trong.<br />\r\n<br />\r\n&quot;Ha ha, đi th&ocirc;i, đi với ta ăn ch&uacute;t đồ vật.&quot; Th&ocirc;i Hằng nở nụ cười.<br />\r\n<br />\r\n&quot;. . .&quot; Khương Thất Thất trong mắt l&oacute;e l&ecirc;n một ch&uacute;t do dự, nhưng rất nhanh liền hạ quyết định, nhẹ gật đầu, Điềm Điềm m&agrave; n&oacute;i, &quot;Ừm, tạ ơn thần ti&ecirc;n ca ca!&quot;<br />\r\n<br />\r\nTh&ocirc;i Hằng mang theo tiểu c&ocirc; nương đi v&agrave;o t&acirc;n thủ ph&ograve;ng nhỏ biệt thự trước cửa, đem ng&oacute;n tay nhẹ nh&agrave;ng tại v&acirc;n tay cầm tr&ecirc;n tay nhấn một c&aacute;i.<br />\r\n<br />\r\nChỉ nghe một trận tr&ocirc;i chảy thuận hoạt giải tỏa tiếng vang l&ecirc;n, cửa liền m&igrave;nh mở ra.<br />\r\n<br />\r\n&quot;Oa! Thật l&agrave; lợi hại!&quot; Khương Thất Thất trừng lớn l&oacute;e s&aacute;ng con mắt, v&ocirc; c&ugrave;ng t&aacute;n thưởng m&agrave; n&oacute;i, &quot;Đ&acirc;y ch&iacute;nh l&agrave; thần ti&ecirc;n ca ca ở ph&ograve;ng ở sao? Thật thật thần kỳ!&quot;<br />\r\n<br />\r\n&quot;. . .&quot; Th&ocirc;i Hằng nghe được c&oacute; chút giới ở, kh&ocirc;ng n&oacute;i g&igrave;, đi thẳng v&agrave;o.<br />\r\n<br />\r\nNhưng Khương Thất Thất nhưng kh&ocirc;ng c&oacute; c&ugrave;ng l&ecirc;n đến, n&agrave;ng đứng tại cửa ra v&agrave;o, dường như c&oacute; ch&uacute;t khẩn trương, nh&igrave;n về ph&iacute;a Th&ocirc;i Hằng, nhỏ giọng tu&acirc;n hỏi: &quot;Ta, ta thật c&oacute; thể trực tiếp đi v&agrave;o a? Ta chỉ l&agrave; c&aacute;i ph&agrave;m nh&acirc;n.&quot;<br />\r\n<br />\r\n&quot;Tiến đến ăn ch&uacute;t đồ vật đi.&quot; Th&ocirc;i Hằng c&oacute; ch&uacute;t dở kh&oacute;c dở cười.<br />\r\n<br />\r\n&quot;Ừm &acirc;n, tốt.&quot; Khương Thất Thất nắm chặt lại nắm tay nhỏ, c&oacute; ch&uacute;t hếch bộ ngực nhỏ, bước v&agrave;o.<br />\r\n<br />\r\nNhưng lại tại c&aacute;i n&agrave;y thời điểm, tr&iacute; năng Tinh Linh kiểm trắc đến ngoại trừ Th&ocirc;i Hằng b&ecirc;n ngo&agrave;i người đi v&agrave;o gian ph&ograve;ng, trực tiếp liền mở ra giọng n&oacute;i vấn an: &quot;Ng&agrave;i tốt! Ch&agrave;o mừng ng&agrave;i! T&ocirc;n qu&yacute; kh&aacute;ch nh&acirc;n!&quot;<br />\r\n<br />\r\n&quot;A!&quot; Khương Thất Thất kinh h&ocirc; l&ecirc;n, r&otilde; r&agrave;ng bị c&aacute;i n&agrave;y đột hiển xuất hiện thanh &acirc;m hạ nhảy một c&aacute;i.<br />\r\n<br />\r\nL&agrave; thật nhảy dựng l&ecirc;n.<br />\r\n<br />\r\nĐầu k&eacute;m ch&uacute;t đ&acirc;m v&agrave;o tr&ecirc;n mặt điếu đỉnh.<br />\r\n<br />\r\n&quot;Đ&acirc;y, đ&acirc;y l&agrave;, l&agrave; c&aacute;i g&igrave;?&quot; Khương Thất Thất kinh nghi bất định khoảng chừng quan s&aacute;t, lại ph&aacute;t hiện cũng kh&ocirc;ng c&oacute; người thứ ba tại.<br />\r\n<br />\r\n&quot;L&agrave; ph&ograve;ng n&agrave;y đang n&oacute;i chuyện, kh&ocirc;ng cần để &yacute;.&quot; Th&ocirc;i Hằng mỉm cười n&oacute;i, hắn tổng kh&ocirc;ng tốt lại c&ugrave;ng tiểu c&ocirc; nương n&agrave;y giảng giải c&aacute;i g&igrave; gọi l&agrave; tr&iacute; tuệ nh&acirc;n tạo, c&aacute;i g&igrave; gọi l&agrave; tr&iacute; năng đồ điện gia dụng.<br />\r\n<br />\r\nMấy chục năm kh&ocirc;ng c&oacute; c&ugrave;ng người giao lưu, kh&ocirc;ng chỉ thấp xuống hắn x&atilde; giao năng lực, c&agrave;ng thấp xuống hắn n&oacute;i chuyện dục vọng.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y, dạng n&agrave;y a, thần ti&ecirc;n, ti&ecirc;n ca ca ph&ograve;ng ở thật thật thần kỳ a!&quot; Khương Thất Thất trong l&ograve;ng kỳ thật c&agrave;ng th&ecirc;m chấn kinh.<br />\r\n<br />\r\nPh&ograve;ng Tử Cư nhưng biết n&oacute;i chuyện!<br />\r\n<br />\r\nChẳng lẽ nơi n&agrave;y thật l&agrave; thần ti&ecirc;n ở địa phương, trước mắt c&aacute;i n&agrave;y lớn hơn m&igrave;nh kh&ocirc;ng được mấy tuổi thiếu ni&ecirc;n thật sự l&agrave; trường sinh cửu thị thần ti&ecirc;n?<br />\r\n<br />\r\n&quot;Ngươi qua b&ecirc;n kia cạnh b&agrave;n ăn ngồi.&quot; Th&ocirc;i Hằng chỉ chỉ ph&ograve;ng ăn phương hướng n&oacute;i, &quot; ta nấu hai b&aacute;t m&igrave;.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nBởi v&igrave; c&oacute; Hồng Ph&uacute; Qu&yacute; vết xe đổ, cho n&ecirc;n lần n&agrave;y Th&ocirc;i Hằng kh&ocirc;ng c&oacute; trực tiếp gọi Ho&agrave;ng C&acirc;n lực sĩ tới.<br />\r\n<br />\r\nM&agrave; tại Th&ocirc;i Hằng nấu b&aacute;t m&igrave; qu&aacute; tr&igrave;nh b&ecirc;n trong, tiểu c&ocirc; nương n&agrave;y lại l&acirc;m v&agrave;o mộng bức trạng th&aacute;i.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; lửa l&agrave;m sao nấu nước, l&agrave;m sao nấu b&aacute;t m&igrave;?<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; lửa v&igrave; c&aacute;i g&igrave; c&oacute; thể đem nước đốt l&ecirc;n?<br />\r\n<br />\r\nV&igrave; c&aacute;i g&igrave; kh&oacute;i ch&iacute;nh sẽ hướng l&ecirc;n dũng m&atilde;nh lao tới, thế m&agrave; kh&ocirc;ng c&oacute; bốn ph&iacute;a phi&ecirc;u t&aacute;n?<br />\r\n<br />\r\nRất nhiều t&igrave;nh huống đều để n&agrave;ng cảm thấy kh&oacute; c&oacute; thể l&yacute; giải được.<br />\r\n<br />\r\nChẳng lẽ người n&agrave;y thật l&agrave; thần ti&ecirc;n sao?<br />\r\n<br />\r\nNhưng n&agrave;ng kh&ocirc;ng d&aacute;m hỏi qu&aacute; nhiều.<br />\r\n<br />\r\nNếm qua mặt về sau, Th&ocirc;i Hằng cũng kh&ocirc;ng c&oacute; lại tiếp tục n&oacute;i chuyện với Khương Thất Thất, cũng kh&ocirc;ng c&oacute; gấp hỏi thăm.<br />\r\n<br />\r\nM&agrave; l&agrave; mang n&agrave;ng tới lầu hai, chỉ một gian ph&ograve;ng ngủ cung cấp n&agrave;ng nghỉ ngơi.<br />\r\n<br />\r\nSau đ&oacute; liền m&igrave;nh trở về lầu ba ph&ograve;ng tu luyện.<br />\r\n<br />\r\nMuốn cho c&aacute;i tuổi n&agrave;y tiểu c&ocirc; nương an t&acirc;m, tốt nhất biện ph&aacute;p ch&iacute;nh l&agrave; trước kh&ocirc;ng hề l&agrave;m g&igrave;.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nT&acirc;n thủ ph&ograve;ng nhỏ biệt thự tầng hai nằm trong ph&ograve;ng.<br />\r\n<br />\r\nCửa ph&ograve;ng đ&atilde; bị c&aacute;i b&agrave;n gắt gao đứng vững, cửa sổ cũng bị kh&oacute;a k&iacute;n, m&agrave;n cửa cũng k&eacute;o l&ecirc;n, trong ph&ograve;ng một mảnh lờ mờ.<br />\r\n<br />\r\nL&uacute;c n&agrave;y Khương Thất Thất co ro ngồi tại ch&acirc;n giường, &ocirc;m m&igrave;nh hai ch&acirc;n, t&oacute;c d&agrave;i đen nh&aacute;nh x&otilde;a, đem mặt giấu đi, nhỏ nhắn xinh xắn th&acirc;n thể nhịn kh&ocirc;ng được run, thỉnh thoảng ph&aacute;t ra nghẹn ng&agrave;o thanh &acirc;m.<br />\r\n<br />\r\nNửa điểm cũng kh&ocirc;ng c&oacute; l&uacute;c trước c&ugrave;ng Th&ocirc;i Hằng ở chung l&uacute;c hoạt b&aacute;t s&aacute;ng sủa d&aacute;ng vẻ.<br />\r\n<br />\r\n&quot;Ba ba mụ mụ, c&aacute;c ngươi ở nơi n&agrave;o a?<br />\r\n<br />\r\n&quot;Ta, ta rất sợ h&atilde;i. . .&quot;</p>', '2021-11-28 16:44:16', 6, 1),
(238, 65, 'Từ Hôm Nay Trở đi Làm Tộc Trưởng', '1', '<p>Đại C&agrave;n Long Xương 3,145 năm &mdash;&mdash; m&ugrave;ng ch&iacute;n th&aacute;ng tư.<br />\r\n<br />\r\nB&igrave;nh An Vương thị, trước gia chủ Vương Định Nhạc tr&aacute;ng ni&ecirc;n thương tiếc.<br />\r\n<br />\r\nBảy ng&agrave;y tang kỳ về sau, th&acirc;n l&agrave; đ&iacute;ch hệ huyết mạch con trai trưởng vương thủ triết theo nước chế tộc quy, tại đại nương Vương thị C&ocirc;ng T&ocirc;n Huệ c&ugrave;ng đ&ocirc;ng đảo tộc l&atilde;o chủ tr&igrave; dưới, b&aacute;o c&aacute;o Lung Y&ecirc;n l&atilde;o tổ c&ugrave;ng liệt tổ liệt t&ocirc;ng về sau, kế nhiệm l&agrave; một đời mới tộc trưởng.<br />\r\n<br />\r\nVương thị l&agrave; Đại C&agrave;n Quốc tạo s&aacute;ch đ&aacute;nh gi&aacute; cửu phẩm Huyền Vũ thế gia, lực ảnh hưởng vẻn vẹn cực hạn tại Trường Ninh vệ B&igrave;nh An trấn một v&ugrave;ng, thuộc về mạt lưu thế gia.<br />\r\n<br />\r\nNhưng tổ ti&ecirc;n đ&atilde; từng kho&aacute;t qua, t&igrave;m căn nguy&ecirc;n tố nguy&ecirc;n nhưng đến Đại C&agrave;n Vương thị một mạch.<br />\r\n<br />\r\nNg&agrave;n năm trước ti&ecirc;n tổ chẳng biết tại sao từ Đại C&agrave;n Vương thị ph&acirc;n gia, kh&ocirc;ng xa vạn dặm đi v&agrave;o Lũng Tả quận, khai sơn t&iacute;ch thổ th&agrave;nh lập Lũng Tả Vương gia, đặt vững gia tộc nền tảng.<br />\r\n<br />\r\nNgay l&uacute;c đ&oacute; Lũng Tả Vương gia cũng coi l&agrave; danh m&ocirc;n đại tộc, danh tiếng kh&ocirc;ng hai.<br />\r\n<br />\r\nChỉ tiếc thời trẻ qua mau, người kh&ocirc;ng ngàn ng&agrave;y tốt.<br />\r\n<br />\r\nPhong quang về sau, gia thế ng&agrave;y c&agrave;ng đi&ecirc;u t&agrave;n.<br />\r\n<br />\r\nVề sau.<br />\r\n<br />\r\nLong Xương ba ng&agrave;n năm, Lũng Tả Vương thị t&agrave;i tuấn Vương Trụ Hi&ecirc;n dắt người nh&agrave; c&ugrave;ng khai th&aacute;c lệnh xu&ocirc;i nam đến Trường Ninh vệ, tại Lục B&igrave;nh sơn mạch dư mạch c&ugrave;ng An Giang chỗ giao hội khai hoang x&acirc;y trại lập trấn, gian khổ khi lập nghiệp khai th&aacute;c nh&acirc;n loại nơi ở lập xuống c&ocirc;ng hu&acirc;n, x&acirc;y B&igrave;nh An Vương thị cắm rễ phồn diễn sinh sống.<br />\r\n<br />\r\nTrụ Hi&ecirc;n l&atilde;o tổ đỉnh phong chiến lực l&uacute;c từng đạt tới Linh Đ&agrave;i cảnh hậu kỳ, tại B&igrave;nh An trấn một tay Gi&agrave; Thi&ecirc;n.<br />\r\n<br />\r\nC&agrave;ng l&agrave;m Trụ Hi&ecirc;n l&atilde;o tổ ki&ecirc;u ngạo ch&iacute;nh l&agrave;, hắn đ&iacute;ch t&ocirc;n nữ Vương Lung Y&ecirc;n dung nhan tung ho&agrave;nh, mười t&aacute;m tuổi liền Luyện Kh&iacute; cảnh bảy tầng, bị đại danh đỉnh đỉnh Tử Phủ Học Cung tr&uacute;ng tuyển sau tại ba mươi tuổi đột ph&aacute; tới Linh Đ&agrave;i cảnh, kh&ocirc;ng đến s&aacute;u mươi tuổi liền đột ph&aacute; đến Linh Đ&agrave;i cảnh trung giai, th&acirc;m thụ Tử Phủ Học Cung coi trọng t&agrave;i bồi.<br />\r\n<br />\r\nĐồng thời Vương Lung Y&ecirc;n c&ugrave;ng Sơn &Acirc;m Liễu thị Liễu Huy&ecirc;n Ph&ugrave; tịnh xưng Trường Ninh song kiều, thanh danh truyền xa.<br />\r\n<br />\r\nNgo&agrave;i ra l&atilde;o tổ d&ograve;ng ch&iacute;nh chắt trai Vương Khung Nguy&ecirc;n, đồng dạng thi&ecirc;n ph&uacute; phi ph&agrave;m, tại l&atilde;o tổ t&agrave;i bồi n&acirc;ng đỡ hạ bốn mươi tuổi liền đạt tới Linh Đ&agrave;i cảnh, cũng chấp chưởng gia tộc tộc trưởng chi vị.<br />\r\n<br />\r\nĐến l&uacute;c đ&oacute; Vương thị một m&ocirc;n ba linh đ&agrave;i phong quang v&ocirc; hạn, gia tộc thế lực thậm ch&iacute; ảnh hưởng đến to&agrave;n bộ Trường Ninh vệ địa khu.<br />\r\n<br />\r\nCho đến Long Xương 3,095 năm, l&uacute;c năm Trụ Hi&ecirc;n l&atilde;o tổ một trăm bốn mươi năm tuổi, lắng lại đ&atilde; l&acirc;u Lục B&igrave;nh sơn chỗ s&acirc;u hung th&uacute; đột nhi&ecirc;n bạo động, B&igrave;nh An trấn nằm ở trong trở th&agrave;nh tai khu.<br />\r\n<br />\r\nL&agrave; thủ hộ gia tộc trăm năm cơ nghiệp, Trụ Hi&ecirc;n l&atilde;o tổ kh&ocirc;ng tiếc tự m&igrave;nh ngăn chặn ngũ giai hung th&uacute; Kim S&iacute; Th&ocirc;n Nhật Hổ , chờ đợi viện qu&acirc;n m&agrave; tới.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nChiến dịch n&agrave;y t&igrave;nh h&igrave;nh chiến đấu cực kỳ thảm liệt, mặc d&ugrave; Kim S&iacute; Th&ocirc;n Nhật Hổ c&ugrave;ng th&uacute; tai, đều bị sau đ&oacute; chạy đến Trường Ninh vệ viện qu&acirc;n v&acirc;y c&ocirc;ng ch&eacute;m giết, nhưng m&agrave; B&igrave;nh An Vương thị cũng đ&atilde; bị cực lớn thương t&iacute;ch.<br />\r\n<br />\r\nChiến dịch n&agrave;y kh&ocirc;ng những Trụ Hi&ecirc;n l&atilde;o tổ vẫn lạc, m&agrave; lại bị l&atilde;o tổ k&yacute; th&aacute;c kỳ vọng đời thứ tư Linh Đ&agrave;i cảnh sơ giai tộc trưởng Vương Khung Nguy&ecirc;n, c&ugrave;ng đời thứ năm người nối nghiệp Vương Ti&ecirc;u Khải đồng dạng chiến tử th&uacute; tai.<br />\r\n<br />\r\nB&igrave;nh An Vương thị hai đời Linh Đ&agrave;i cảnh vẫn lạc, thực lực uy vọng đại giảm.<br />\r\n<br />\r\nM&agrave; Lưu thị c&ugrave;ng Triệu thị hai nh&agrave; n&agrave;y nhu thuận nghe lời gia tộc phụ thuộc ngo ngoe muốn động, hợp tung li&ecirc;n ho&agrave;nh &yacute; đồ chiếm đoạt Vương thị cơ nghiệp.<br />\r\n<br />\r\nThời khắc mấu chốt Vương thị đời thứ ba tộc nh&acirc;n Vương Lung Y&ecirc;n, dứt kho&aacute;t quyết nhi&ecirc;n từ bỏ Tử Phủ Học Cung tốt đẹp tiền đồ trở về gia tộc, cũng chủ động xuất k&iacute;ch ngh&ecirc;nh chiến địch phương tổng cộng ba vị Linh Đ&agrave;i cảnh cường giả.<br />\r\n<br />\r\nLấy một địch ba c&aacute;i, Vương Lung Y&ecirc;n ngang nhi&ecirc;n ch&eacute;m giết địch qu&acirc;n Linh Đ&agrave;i cảnh minh hữu, chấn nhiếp Lưu thị c&ugrave;ng Triệu thị linh đ&agrave;i l&atilde;o tổ.<br />\r\n<br />\r\nChỉ tiếc Vương Lung Y&ecirc;n trận chiến n&agrave;y thương thế qu&aacute; nặng, thực lực rơi xuống Linh Đ&agrave;i cảnh sơ giai lại thọ nguy&ecirc;n đại giảm.<br />\r\n<br />\r\nNhưng d&ugrave; vậy, Vương Lung Y&ecirc;n vẫn như cũ lấy sức một m&igrave;nh n&acirc;ng l&ecirc;n g&aacute;nh nặng, miễn cưỡng bảo vệ B&igrave;nh An Vương thị. Lưu thị c&ugrave;ng Triệu thị kh&ocirc;ng d&aacute;m qu&aacute; mức chọc giận Vương Lung Y&ecirc;n, liền định ra từng bước x&acirc;m chiếm chi thuật.<br />\r\n<br />\r\nThời gian tho&aacute;ng một c&aacute;i đ&atilde; qua, lại l&agrave; bốn năm mươi năm qua đi, nhiều đời tộc nh&acirc;n từ đầu đến cuối chưa từng xuất hiện vị kế tiếp Linh Đ&agrave;i cảnh, lần n&agrave;y c&agrave;ng l&agrave; ngay cả trước tộc trưởng Vương Định Nhạc đều đ&atilde; chết.<br />\r\n<br />\r\nTất cả g&aacute;nh nặng, đều tựa hồ rơi xuống một đời mới tộc trưởng Vương Thủ Triết tr&ecirc;n th&acirc;n!<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nVương thị chủ trạch, một t&ograve;a cổ k&iacute;nh s&acirc;n nhỏ trong sương ph&ograve;ng.<br />\r\n<br />\r\n&quot;Thiếu, kh&ocirc;ng, gia chủ ~ chuyện cũ đ&atilde; qua, ng&agrave;i trước ăn một ch&uacute;t g&igrave; đi. Lại cứ tiếp như thế, th&acirc;n thể chỉ sợ ăn kh&ocirc;ng ti&ecirc;u. Những thức ăn n&agrave;y, l&agrave; C&ocirc;ng T&ocirc;n đại nương tử tự th&acirc;n v&igrave; ng&agrave;i chuẩn bị.&quot; Thiếp th&acirc;n g&atilde; sai vặt Vương Qu&yacute; cẩn thận từng li từng t&iacute; bưng gỗ tử đ&agrave;n hộp cơm, hai đầu l&ocirc;ng m&agrave;y tr&agrave;n đầy lo lắng.<br />\r\n<br />\r\n&quot;H&ocirc; ~&quot; Vương Thủ Triết nhổ ngụm trọc kh&iacute;, bu&ocirc;ng xuống &laquo; Vương thị truyền thừa kỷ yếu &raquo;, &aacute;nh mắt kh&ocirc;i phục một &iacute;t thanh minh, &yacute; niệm trong l&ograve;ng ph&aacute;t l&ecirc;n, &quot;Nghĩ kh&ocirc;ng ra một trận tai nạn xe cộ, v&acirc;̣y mà xuy&ecirc;n qua đến một c&aacute;i huyền huyễn thế giới, c&ograve;n kh&ocirc;ng giải th&iacute;ch được th&agrave;nh một c&aacute;i xuống dốc Huyền Vũ thế gia tộc trưởng. Chỉ tiếc, xem ra g&aacute;nh rất nặng a.&quot;<br />\r\n<br />\r\nNguy&ecirc;n lai sớm tại ba ng&agrave;y trước, nguy&ecirc;n th&acirc;n Vương Thủ Triết bởi v&igrave; thủ linh kh&oacute;c tang qu&aacute; mức thương t&acirc;m. Một đầu t&eacute; xỉu trước đ&acirc;y toto quan t&agrave;i trước, chờ hắn yếu ớt tỉnh lại, lại trở th&agrave;nh người xuy&ecirc;n việt.<br />\r\n<br />\r\nC&aacute;i n&agrave;y ba ng&agrave;y đến, k&yacute; ức dung hợp qu&aacute; tr&igrave;nh để Vương Thủ Triết nh&igrave;n c&oacute; ch&uacute;t ngơ ngơ ng&aacute;c ng&aacute;c, đ&aacute;m người chỉ n&oacute;i hắn qu&aacute; độ thương t&acirc;m c&oacute; ch&uacute;t mất hồn, lại kh&ocirc;ng biết b&ecirc;n trong c&agrave;n kh&ocirc;n t&igrave;nh h&igrave;nh thực tế, chỉ l&agrave; y theo tộc quy c&ugrave;ng truyền thừa, đem hắn đẩy l&ecirc;n tộc trưởng chi vị.<br />\r\n<br />\r\nĐọc xong &laquo; Vương thị truyền thừa kỷ yếu &raquo;, Vương Thủ Triết n&oacute;i chung đối gia tộc n&agrave;y c&ugrave;ng thế giới c&oacute; ch&uacute;t &iacute;t giải. Hắn chỗ phương thế giới n&agrave;y gọi 【 Đại C&agrave;n Quốc 】, Đại C&agrave;n lấy Huyền Vũ thế gia lập quốc, ho&agrave;ng thất ch&iacute;nh l&agrave; lớn nhất Huyền Vũ gia tộc, c&aacute;i kh&aacute;c to to nhỏ nhỏ Huyền Vũ thế gia c&agrave;ng l&agrave; như tinh la mật bố.<br />\r\n<br />\r\nLẫn nhau ở giữa rắc rối kh&oacute; gỡ, hoặc trực tiếp, hoặc gi&aacute;n tiếp lấy ảnh hưởng to&agrave;n bộ Đại C&agrave;n ch&iacute;nh trị, kinh tế, thậm ch&iacute; l&agrave; quốc vận mệnh mạch.<br />\r\n<br />\r\nĐừng nh&igrave;n Vương thị gia tộc vẻn vẹn mạt lưu cửu phẩm thế gia, m&agrave; ở Trường Ninh B&igrave;nh An một v&ugrave;ng đ&atilde; thuộc về cường thế địa đầu x&agrave; một trong, quan phủ cơ cấu trấn thủ trong phủ cũng chiếm cứ kh&ocirc;ng nhỏ quyền l&ecirc;n tiếng.<br />\r\n<br />\r\nThậm ch&iacute; hồ, sớm tại Vương Thủ Triết tằng tổ phụ kia một đời, Vương thị gia tộc c&agrave;ng l&agrave; B&igrave;nh An trấn n&oacute;i một kh&ocirc;ng hai b&aacute; chủ cấp thế gia, quyền thế x&uacute;c tu c&oacute; thể ảnh hưởng đến Trường Ninh vệ xung quanh.<br />\r\n<br />\r\nChỉ l&agrave; theo &laquo; Vương thị truyền thừa kỷ yếu &raquo; b&ecirc;n trong, kia hai trận kinh t&acirc;m động ph&aacute;ch chuyển hướng đại chiến, tạo th&agrave;nh gia tộc cao thủ vẫn lạc về sau, ch&iacute;nh l&agrave; c&agrave;ng ng&agrave;y c&agrave;ng tệ. D&ugrave; l&agrave; c&oacute; Lung Y&ecirc;n l&atilde;o tổ nỗ lực ch&egrave;o chống, cũng kh&oacute; c&oacute; thể ngăn cản Lưu thị c&ugrave;ng Triệu thị li&ecirc;n thủ từng bước x&acirc;m chiếm.<br />\r\n<br />\r\nTrước gia chủ Vương Định Nhạc v&igrave; c&oacute; thể tu luyện th&agrave;nh Linh Đ&agrave;i cảnh ngăn cơn s&oacute;ng dữ, kh&ocirc;ng tiếc x&acirc;m nhập ngoại vực hiểm cảnh t&igrave;m kiếm đột ph&aacute; cơ duy&ecirc;n.<br />\r\n<br />\r\nNhưng m&agrave; Vương Định Nhạc sắp th&agrave;nh lại bại, bất hạnh tr&aacute;ng ni&ecirc;n sớm tang, để suy bại kh&ocirc;ng chỉ B&igrave;nh An Vương thị đ&atilde; r&eacute;t v&igrave; tuyết lại lạnh v&igrave; sương.<br />\r\n<br />\r\nNếu l&agrave; kh&ocirc;ng c&oacute; chuyện ngo&agrave;i &yacute; muốn xảy ra, dựa theo n&agrave;y ph&aacute;t triển tiếp tương lai trong v&ograve;ng mấy chục năm Vương thị gia tộc địa b&agrave;n sản nghiệp, sẽ bị B&igrave;nh An trấn c&ograve;n lại hai nh&agrave; dần dần từng bước x&acirc;m chiếm thay thế, đ&atilde; từng to như vậy m&agrave; huy ho&agrave;ng gia tộc cũng sẽ suy bại sụp đổ, gia tộc huyết mạch đem đọa th&agrave;nh b&igrave;nh d&acirc;n.<br />\r\n<br />\r\n&quot;Bất qu&aacute; ngược lại l&agrave; c&aacute;i th&uacute; vị thế giới.&quot; Vương Thủ Triết hơi nheo mắt lại, dần dần dung hợp k&yacute; ức hắn kh&ocirc;ng khỏi thầm nghĩ, &quot;Giống như tiền th&acirc;n Vương Thủ Triết mặc d&ugrave; tuổi trẻ, nhưng cũng kh&ocirc;ng phải một nh&acirc;n vật đơn giản.&quot;<br />\r\n<br />\r\nK&yacute; ức n&oacute;i cho hắn biết, Vương Thủ Triết từ nhỏ được vinh dự thiếu ni&ecirc;n thi&ecirc;n t&agrave;i, thi&ecirc;n ph&uacute; tư chất tại Trụ Hi&ecirc;n l&atilde;o tổ mấy c&aacute;i đến bảy đời d&ograve;ng d&otilde;i trong huyết mạch, cũng liền vẻn vẹn so Lung Y&ecirc;n l&atilde;o tổ hơi k&eacute;m nửa bậc.<br />\r\n<br />\r\nỞ gia tộc tập trung t&agrave;i nguy&ecirc;n bồi dưỡng dưới, Vương Thủ Triết mười t&aacute;m tuổi liền đạt đến Luyện Kh&iacute; cảnh s&aacute;u tầng cao đoạn, khoảng c&aacute;ch tầng thứ bảy cũng kh&ocirc;ng xa.<br />\r\n<br />\r\nNhư thế một tin tức tốt, ch&iacute; &iacute;t kh&ocirc;ng phải xuy&ecirc;n qua đến phế vật tr&ecirc;n th&acirc;n.<br />\r\n<br />\r\nMột b&ecirc;n g&atilde; sai vặt Vương Qu&yacute;, nh&igrave;n xem Vương Thủ Triết sắc mặt &acirc;m t&igrave;nh biến h&oacute;a kh&ocirc;ng chừng, c&agrave;ng th&ecirc;m lo lắng: &quot;Gia chủ ng&agrave;i nghỉ ngơi trước, ta c&aacute;i n&agrave;y đi mời y sư tới.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng sao, ta đ&atilde; kh&ocirc;ng c&ograve;n đ&aacute;ng ngại.&quot; Vương Thủ Triết ngăn trở cử động của hắn, &quot;Vương Qu&yacute;, ngươi đi ra ngo&agrave;i trước, ta c&oacute; một số việc c&acirc;̀n suy nghĩ một ch&uacute;t.&quot;<br />\r\n<br />\r\n&quot;&Acirc;y!&quot;<br />\r\n<br />\r\nVương Qu&yacute; bu&ocirc;ng xuống hộp cơm lĩnh mệnh thối lui, cẩn thận đ&oacute;ng cửa lại.<br />\r\n<br />\r\nChờ Vương Qu&yacute; sau khi đi, chỉ c&ograve;n lại một người l&uacute;c, Vương Thủ Triết mới ch&iacute;nh thức thở d&agrave;i một hơi, tinh thần thư gi&atilde;n xuống. Những ng&agrave;y n&agrave;y thủ linh đặc biệt ch&uacute; &yacute; biết dung hợp, kh&ocirc;ng ăn cơm thật ngon, bụng đ&atilde; sớm đ&oacute;i đến k&ecirc;u l&ecirc;n &ugrave;ng ục, chấn hưng gia tộc c&aacute;i g&igrave; thi&ecirc;n đầu vạn tự, trước nh&eacute;t đầy c&aacute;i bao tử quay đầu lại từ từ suy nghĩ.<br />\r\n<br />\r\nMở ra tinh xảo đ&agrave;n mộc hộp cơm c&aacute;i nắp, một trận kỳ diệu đồ ăn m&ugrave;i thơm x&ocirc;ng v&agrave;o mũi m&agrave; tới. Hộp cơm nội bộ chia mấy c&aacute;i ngăn c&aacute;ch, c&oacute; cơm c&oacute; đồ ăn c&oacute; thịt, c&ograve;n c&oacute; một khối c&aacute; hấp thịt.<br />\r\n<br />\r\nMuốn ăn k&iacute;ch th&iacute;ch dưới, hắn nhanh ch&oacute;ng cầm lấy đũa kẹp khối thịt nh&eacute;t v&agrave;o miệng b&ecirc;n trong, gia vị đồng dạng, cảm gi&aacute;c lại dị thường phong ph&uacute;, tốt đẹp nhai k&igrave;nh hạ thơm nức vị thịt che k&iacute;n khoang miệng, nuốt xuống bụng về sau, ẩn ẩn c&ograve;n c&oacute; thể cảm gi&aacute;c được một cỗ nhỏ x&iacute;u nhiệt lưu đang cuộn tr&agrave;o.<br />\r\n<br />\r\nNg&ocirc;, ăn ngon như vậy?</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nĐ&acirc;y l&agrave; c&aacute;i g&igrave; thịt? Thịt heo? Kh&ocirc;ng đ&uacute;ng, thịt heo cảm gi&aacute;c th&ocirc; r&aacute;p kh&ocirc;ng c&oacute; như thế tinh t&ecirc;́ tỉ mỉ. Thịt d&ecirc;? Cũng kh&ocirc;ng đ&uacute;ng, thịt n&agrave;y kh&ocirc;ng c&oacute; nửa điểm m&ugrave;i vị, m&ugrave;i thịt thuần hậu chất lỏng đầy đặn, cảm gi&aacute;c giống như l&agrave; xuy&ecirc;n qua trước ngẫu nhi&ecirc;n thưởng thức qua một lần đắt đỏ nhập khẩu thịt b&ograve;.<br />\r\n<br />\r\nNhưng m&agrave; rất nhanh, hắn dung hợp &quot;K&yacute; ức&quot; b&aacute;o cho hắn, đ&acirc;y l&agrave; Linh Gi&aacute;c Ly Ngưu thịt, l&agrave; một loại tr&acirc;n qu&yacute; ăn thịt, trường kỳ d&ugrave;ng ăn c&oacute; thể cường tr&aacute;ng thể ph&aacute;ch x&uacute;c tiến tu luyện t&aacute;c dụng.<br />\r\n<br />\r\nHắn tiền th&acirc;n l&agrave; thiếu tộc trưởng, lại l&agrave; thi&ecirc;n tư bất ph&agrave;m, l&agrave; gia tộc t&agrave;i nguy&ecirc;n cung cấp nu&ocirc;i dưỡng thứ nhất danh s&aacute;ch, tự nhi&ecirc;n c&oacute; tư c&aacute;ch hưởng dụng như thế t&agrave;i nguy&ecirc;n.<br />\r\n<br />\r\nVương Thủ Triết cảm thụ được thể nội một ch&uacute;t nhiệt lưu đang cuộn tr&agrave;o, tinh lực cấp tốc kh&ocirc;i phục kỳ diệu cảm gi&aacute;c. Kh&ocirc;ng khỏi &acirc;m thầm cảm kh&aacute;i một tiếng, quả nhi&ecirc;n l&agrave; thịt ngon, thức ăn ngon dụ hoặc dưới, ba lượng đũa liền đem l&agrave; số kh&ocirc;ng nhiều Ly Ngưu thịt ăn đến kh&ocirc;ng c&ograve;n một mảnh.<br />\r\n<br />\r\nVẫn chưa thỏa m&atilde;n dưới, hắn lại để mắt tới kia một đoạn c&aacute; hấp thịt, dung hợp k&yacute; ức n&oacute;i cho hắn biết c&aacute;i n&agrave;y gọi Hồng Vĩ Linh Cảm. Đ&oacute; l&agrave; một loại sản xuất từ l&acirc;n cận An Giang b&ecirc;n trong hiếm thấy Linh Ngư, thuộc về nhị giai hung th&uacute; cấp độ, h&igrave;nh thể động một t&iacute; c&oacute; thể d&agrave;i đến mấy trăm c&acirc;n, lực nhưng lật thuyền cực k&igrave; hung m&atilde;nh.<br />\r\n<br />\r\nPhổ th&ocirc;ng ngư d&acirc;n đụng phải loại hung th&uacute; n&agrave;y chỉ sợ tr&aacute;nh kh&ocirc;ng kịp, chạy trốn hơi chậm hẳn l&agrave; thuyền lật người vong cục diện.<br />\r\n<br />\r\nĐầu n&agrave;y Hồng Vĩ Linh Cảm vẫn l&agrave; trước tộc trưởng Vương Định Nhạc tại năm ngo&aacute;i thời đi&ecirc;̉m, c&ugrave;ng mấy vị trong tộc cao thủ thi triển thủ đoạn cũng may mắn bắt được. Ph&acirc;n giải ng&acirc;m dưa muối sau tr&acirc;n t&agrave;ng tại gia tộc trong hầm băng, l&agrave;m gia tộc tuổi trẻ huyết mạch linh thực bổ sung.<br />\r\n<br />\r\nĐầu bếp nữ sớm đem xương c&aacute; chọn tận, thịt c&aacute; cửa v&agrave;o ướp tươi thoải m&aacute;i trơn mềm, cũng nương theo lấy một cỗ thanh lương thoải m&aacute;i dễ chịu cảm gi&aacute;c tại thể nội du tẩu, to&agrave;n th&acirc;n thư sướng mỹ vị đến cực điểm.<br />\r\n<br />\r\nCơm cũng kh&ocirc;ng phải l&agrave; ph&agrave;m vật, hạt cơm sung m&atilde;n &oacute;ng &aacute;nh, bắt đ&acirc;̀u ăn căng đầy đạn răng cũng c&oacute; nồng đậm chi lan hương vị, đ&acirc;y ch&iacute;nh l&agrave; đại danh đỉnh đỉnh linh thực Bạch Ngọc Chi Mễ.<br />\r\n<br />\r\nBữa cơm n&agrave;y ăn đến Vương Thủ Triết tư vị v&ocirc; tận, th&acirc;n thể cảm gi&aacute;c suy yếu c&ugrave;ng uể oải tinh thần lập tức ti&ecirc;u t&aacute;n kh&ocirc;ng c&ograve;n, thay v&agrave;o đ&oacute; l&agrave; tinh lực tr&agrave;n đầy c&ugrave;ng thần th&aacute;i s&aacute;ng l&aacute;ng.<br />\r\n<br />\r\nNếu như ngừng lại đều như thế phương ph&aacute;p ăn, tu luyện chắc chắn dị thường thần tốc.<br />\r\n<br />\r\nNhưng c&aacute;i n&agrave;y hiển nhi&ecirc;n l&agrave; chuyện kh&ocirc;ng thể n&agrave;o, b&acirc;y giờ gia tộc kh&ocirc;ng c&ograve;n năm đ&oacute; rầm rộ. Cho d&ugrave; l&agrave; địa vị của hắn danh s&aacute;ch cũng c&ograve;n l&acirc;u mới c&oacute; thể như thế xa hoa l&atilde;ng ph&iacute; phương ph&aacute;p ăn, lại c&agrave;ng kh&ocirc;ng cần phải n&oacute;i gia tộc kh&aacute;c th&agrave;nh vi&ecirc;n.<br />\r\n<br />\r\nHắn b&acirc;y giờ tấn thăng lập gia đ&igrave;nh chủ, tất nhi&ecirc;n l&agrave; kh&ocirc;ng thể tổn hại gia tộc kh&aacute;c th&agrave;nh vi&ecirc;n, kh&ocirc;ng ngừng nghỉ x&acirc;m chiếm bọn hắn t&agrave;i nguy&ecirc;n đến cung cấp ch&iacute;nh m&igrave;nh. Như đ&uacute;ng như đ&acirc;y, kh&ocirc;ng được bao l&acirc;u gia tộc n&agrave;y liền sẽ l&ograve;ng người ly t&aacute;n, gia tốc diệt vong.<br />\r\n<br />\r\nCho d&ugrave; l&agrave; v&igrave; ngừng lại ăn được c&aacute;c m&oacute;n ăn ngon m&oacute;n ngon, cũng phải gi&uacute;p Vương thị gia tộc trưởng th&agrave;nh cường đại a.<br />\r\n<br />\r\n&quot;Hệ thống!&quot; Vương Thủ Triết nằm ở tr&ecirc;n giường ti&ecirc;u thực, nhắm mắt d&ugrave;ng &yacute; niệm b&igrave;nh tĩnh k&ecirc;u gọi.<br />\r\n<br />\r\n. . .</p>', '2021-11-30 20:38:47', 5, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(239, 65, 'Hệ Thống đi đâu?', '2', '<p>. . .<br />\r\n<br />\r\nNhưng qua nửa ng&agrave;y, kh&ocirc;ng c&oacute; nửa điểm đ&aacute;p lại.<br />\r\n<br />\r\nKh&ocirc;ng thể n&agrave;o? Vương Thủ Triết nội t&acirc;m lộp bộp một ch&uacute;t, xuy&ecirc;n qua kh&ocirc;ng mang theo kim thủ chỉ, c&aacute;i n&agrave;y tiết tấu c&oacute; chút hố a? Sau đ&oacute;, Vương Thủ Triết lại l&agrave; phản phản phục phục nhiều lần nếm thử, lại từ đầu đến cuối kh&ocirc;ng thấy kim thủ chỉ b&oacute;ng d&aacute;ng.<br />\r\n<br />\r\nHắn t&acirc;m c&agrave;ng ng&agrave;y c&agrave;ng oa lạnh oa lạnh, c&aacute;i n&agrave;y huyền huyễn thế giới cao thủ nhiều như m&acirc;y, hung th&uacute; b&aacute; đạo ho&agrave;nh h&agrave;nh, kh&ocirc;ng c&oacute; kim thủ chỉ chơi như thế n&agrave;o?<br />\r\n<br />\r\n&quot;Xem ra, trước chỉ c&oacute; thể đi một bước nh&igrave;n một bước.&quot; Vương Thủ Triết biểu lộ hơi c&oacute; vẻ ngưng trọng, bất qu&aacute; hắn rất nhanh liền b&igrave;nh thường trở lại, cho d&ugrave; kh&ocirc;ng c&oacute; kim thủ chỉ hắn cũng &iacute;t nhiều c&oacute; ch&uacute;t ưu thế.<br />\r\n<br />\r\nCh&iacute; &iacute;t tại dung hợp k&yacute; ức về sau, hắn ph&aacute;t hiện thế giới n&agrave;y mặc d&ugrave; cao thủ rất nhiều rất mạnh, nhưng l&agrave; tại d&acirc;n sinh ph&aacute;t triển c&ugrave;ng tư duy ti&ecirc;n tiến tr&igrave;nh độ b&ecirc;n tr&ecirc;n, đều l&agrave; kh&ocirc;ng bằng người Địa Cầu, d&ugrave; l&agrave; hắn chỉ l&agrave; một c&aacute;i b&igrave;nh thường đại học tốt nghiệp d&acirc;n đi l&agrave;m. . .<br />\r\n<br />\r\nNgay tại Vương Thủ Triết chuẩn bị tổng kết một ch&uacute;t ưu thế l&uacute;c.<br />\r\n<br />\r\nBỗng dưng, g&atilde; sai vặt Vương Qu&yacute; ở b&ecirc;n ngo&agrave;i hoang mang rối loạn trương Trương Tr&ugrave;ng tiến đến: &quot;Gia chủ, gia chủ, việc lớn kh&ocirc;ng tốt, Lục l&atilde;o gia bị đ&aacute;nh, hắn ph&aacute;i người trở về cầu viện.&quot;<br />\r\n<br />\r\nLục l&atilde;o gia?<br />\r\n<br />\r\nVương Thủ Triết nh&iacute;u m&agrave;y, k&yacute; ức vọt tới: &quot;L&agrave; Lục th&uacute;c Vương Định Hải.&quot;<br />\r\n<br />\r\nVương Định Hải l&agrave; Vương Thủ Triết tộc th&uacute;c, tại nam đinh định chữ l&oacute;t b&ecirc;n trong x&ecirc;́p hạng thứ s&aacute;u. Hắn tư chất phổ th&ocirc;ng tu vi b&igrave;nh thường, lại tương đối am hiểu thuỷ t&iacute;nh, bởi vậy phụ tr&aacute;ch gia tộc sản nghiệp một trong thuyền đ&aacute;nh c&aacute; đội.<br />\r\n<br />\r\nB&acirc;y giờ Vương Định Hải bị đ&aacute;nh, hẳn l&agrave; gia sản d&ograve;ng họ thuyền đ&aacute;nh c&aacute; đội b&ecirc;n kia xuất hiện biến số, vừa vặn c&oacute; thể mượn cơ hội tận mắt quan s&aacute;t một ch&uacute;t thế giới n&agrave;y.<br />\r\n<br />\r\n&quot;Trấn tĩnh, vội v&agrave;ng hấp tấp c&ograve;n thể thống g&igrave;?&quot; Vương Thủ Triết &yacute; niệm tới đ&acirc;y, liền b&igrave;nh tĩnh lại. Tr&aacute;ch cứ một tiếng về sau, b&igrave;nh tĩnh n&oacute;i, &quot;Chuẩn bị xe ngựa, ch&uacute;ng ta đi xem một ch&uacute;t.&quot;<br />\r\n<br />\r\n&quot;V&acirc;ng, gia chủ.&quot; Vương Qu&yacute; vội v&agrave;ng theo tiếng, nhanh như chớp chạy tới chuẩn bị xuất h&agrave;nh.<br />\r\n<br />\r\nVương Thủ Triết căn cứ k&yacute; ức ra ngo&agrave;i ph&ograve;ng, chậm r&atilde;i hướng chủ trạch cửa lớn đi đến, một đường quan s&aacute;t đến chủ trạch c&aacute;c kiến tr&uacute;c c&ocirc;ng tr&igrave;nh c&ugrave;ng kết cấu, c&ugrave;ng trong đầu k&yacute; ức từng c&aacute;i x&aacute;c minh.<br />\r\n<br />\r\nTr&ecirc;n đường, vừa l&uacute;c nh&igrave;n thấy mẹ cả C&ocirc;ng T&ocirc;n Huệ sắc mặt nghi&ecirc;m t&uacute;c suất lĩnh mấy t&ecirc;n gia tướng, vội v&agrave;ng hướng cửa ch&iacute;nh đi đến.<br />\r\n<br />\r\nVương Thủ Triết đối C&ocirc;ng T&ocirc;n Huệ h&agrave;nh lễ: &quot;Gặp qua đại nương.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nK&yacute; ức dung hợp Vương Thủ Triết đ&atilde; biết, hắn thuở nhỏ mất mẹ, C&ocirc;ng T&ocirc;n Huệ l&agrave; ti&ecirc;n phụ Vương Định Nhạc sau cưới ch&iacute;nh th&ecirc;, dục c&oacute; một đ&iacute;ch nữ t&ecirc;n gọi Vương Lạc Miểu, năm nay đ&atilde; c&oacute; t&aacute;m tuổi.<br />\r\n<br />\r\nBởi vậy C&ocirc;ng T&ocirc;n Huệ tuy l&agrave; Vương Thủ Triết mẹ cả, lại kh&ocirc;ng phải mẹ đẻ. Nhưng l&agrave; n&agrave;ng bản t&iacute;nh thuần hậu, từ trước đến nay xem Vương Thủ Triết như m&igrave;nh ra, c&aacute;c mặt đều chiếu cố cực kỳ tốt.<br />\r\n<br />\r\nNhư thế c&ugrave;ng đồng dạng huyền huyễn tiểu thuyết s&aacute;o lộ kh&ocirc;ng giống nhau lắm.<br />\r\n<br />\r\nC&ocirc;ng T&ocirc;n Huệ vừa thấy được Vương Thủ Triết, liền l&ecirc;n trước giữ chặt tay của hắn, khắp khu&ocirc;n mặt l&agrave; vẻ &acirc;n cần: &quot;Triết Nhi ngươi sao lại ra l&agrave;m g&igrave;? Ngươi d&ugrave;ng cơm xong sao? Th&acirc;n thể nhưng từng rất nhiều.&quot;<br />\r\n<br />\r\nC&ograve;n lại gia tướng cũng vội v&agrave;ng đối Vương Thủ Triết chắp tay h&agrave;nh lễ.<br />\r\n<br />\r\n&quot;Đại nương, ta đ&atilde; kh&ocirc;ng ngại, ng&agrave;i mệnh người chuẩn bị linh thiện cũng đều đ&atilde; ăn xong, quay đầu sẽ c&ugrave;ng ng&agrave;i tinh tế n&oacute;i.&quot; Vương Thủ Triết bất động thanh sắc tr&aacute;nh tho&aacute;t tay của n&agrave;ng, sắc mặt trịnh trọng n&oacute;i, &quot;Đại nương dẫn gia tướng, kh&ocirc;ng phải l&agrave; muốn đi chi viện Lục th&uacute;c? Ta bồi đại nương c&ugrave;ng nhau đi.&quot;<br />\r\n<br />\r\nC&ocirc;ng T&ocirc;n Huệ sầm mặt lại, hơi ch&uacute;t do dự sau n&oacute;i: &quot;Triết Nhi, đ&atilde; ngươi đ&atilde; kế thừa tộc trưởng chi vị, sớm muộn gặp phải những n&agrave;y, đ&atilde; th&acirc;n thể đ&atilde; kh&ocirc;ng việc g&igrave;, vậy liền c&ugrave;ng đi xem xem đi.&quot;<br />\r\n<br />\r\n&quot;V&acirc;ng, đại nương.&quot;<br />\r\n<br />\r\nHai người đồng loạt ra chủ trạch cửa ch&iacute;nh, cửa ch&iacute;nh bảng hiệu b&ecirc;n tr&ecirc;n rồng bay phượng m&uacute;a viết B&igrave;nh An Vương thị v&agrave;i c&aacute;i chữ to, kh&iacute; thế c&oacute; thể n&oacute;i bất ph&agrave;m.<br />\r\n<br />\r\nC&oacute; thể phối hợp lấy sơn sắc pha tạp cột trụ h&agrave;nh lang, c&ugrave;ng viết &quot;Điện&quot; chữ m&agrave;u trắng đ&egrave;n lồng trong gi&oacute; phi&ecirc;u đ&atilde;ng, lại tại hiện lộ r&otilde; r&agrave;ng Vương gia nước s&ocirc;ng ng&agrave;y một r&uacute;t xuống xuống dốc đ&igrave;u hiu cảm gi&aacute;c.<br />\r\n<br />\r\n&quot;Gia chủ, C&ocirc;ng T&ocirc;n đại nương tử, xe ngựa đ&atilde; chuẩn bị tốt.&quot; Thiếp th&acirc;n g&atilde; sai vặt Vương Qu&yacute; một đường chạy chậm tới, &quot;Kiếm c&ugrave;ng cung tiễn đ&atilde; thả ở tr&ecirc;n xe ngựa.&quot;<br />\r\n<br />\r\nHơi ch&uacute;t thương lượng, Vương Thủ Triết c&ugrave;ng C&ocirc;ng T&ocirc;n Huệ c&ugrave;ng cưỡi một chiếc xe ngựa, c&ograve;n lại bốn vị gia tướng c&aacute;c cưỡi ngựa l&ocirc;ng v&agrave;ng đốm trắng hộ vệ tả hữu, một đường hướng An Giang Định Bồ bến đ&ograve; m&agrave; đi.<br />\r\n<br />\r\nỞ tr&ecirc;n đường, C&ocirc;ng T&ocirc;n Huệ vừa tiền căn hậu quả từng c&aacute;i tự thuật.<br />\r\n<br />\r\nC&oacute; được kiếp trước kiếp n&agrave;y tư duy Vương Thủ Triết, rất nhanh liền vuốt r&otilde; r&agrave;ng t&igrave;nh huống thực tế, An Giang l&agrave; một đầu thư th&aacute;i mấy trăm trượng đại giang, trong nước thuỷ sản phong ph&uacute; c&ograve;n c&oacute; đặc sắc, th&ocirc;ng qua đ&aacute;nh bắt đội t&agrave;u nhưng sinh ra l&acirc;u d&agrave;i kinh tế lợi &iacute;ch.<br />\r\n<br />\r\nNhưng c&oacute; lợi &iacute;ch địa phương, thường thường liền sẽ c&oacute; m&acirc;u thuẫn xung đột, B&igrave;nh An trấn tam đại thế gia tự nhi&ecirc;n nh&agrave; ai cũng kh&ocirc;ng nguyện &yacute; từ bỏ cục thịt b&eacute;o n&agrave;y, trải qua đủ loại tranh đấu c&ugrave;ng thỏa hiệp, b&acirc;y giờ B&igrave;nh An tam đại thế gia ri&ecirc;ng ph&acirc;̀n mình x&aacute;c định địa b&agrave;n lẫn nhau kh&ocirc;ng vi phạm.<br />\r\n<br />\r\nC&aacute;i n&agrave;y An Giang từ Định Bồ bến đ&ograve; phải l&ecirc;n ba mươi dặm đến loạn thạch b&atilde;i, liền l&agrave; B&igrave;nh An Vương thị thuỷ vực địa b&agrave;n.<br />\r\n<br />\r\nChỉ l&agrave; những năm gần đ&acirc;y, mặt kh&aacute;c hai c&aacute;i thế gia nh&acirc;n t&agrave;i xuất hiện lớp lớp kh&ocirc;ng ngừng quật khởi, khẩu vị lớn dần, m&agrave; tại tổng thể địa b&agrave;n c&oacute; hạn t&igrave;nh huống dưới, tự nhi&ecirc;n dễ d&agrave;ng đem đũa duỗi tới nh&agrave; người kh&aacute;c trong nồi.<br />\r\n<br />\r\nTừng bước x&acirc;m chiếm kế hoạch đ&atilde; sớm kh&ocirc;ng ngừng tr&igrave;nh diễn, gia tộc địa b&agrave;n c&ugrave;ng sản nghiệp tại năm qua năm kh&ocirc;ng ngừng thu nhỏ. Địa b&agrave;n c&agrave;ng nhỏ, t&agrave;i nguy&ecirc;n liền sẽ c&agrave;ng chặt thiếu, tộc nh&acirc;n khuyết thiếu t&agrave;i nguy&ecirc;n cũng c&agrave;ng kh&oacute; th&agrave;nh d&agrave;i, từ đ&acirc;y l&acirc;m v&agrave;o tuần ho&agrave;n &aacute;c t&iacute;nh b&ecirc;n trong.<br />\r\n<br />\r\nT&igrave;nh huống c&agrave;ng th&ecirc;m &aacute;c liệt ch&iacute;nh l&agrave;, Vương thị gia tộc Định Hải Thần Ch&acirc;m Lung Y&ecirc;n l&atilde;o tổ, th&acirc;n thể tựa hồ c&agrave;ng ng&agrave;y c&agrave;ng tệ, gần nhất mấy năm đều đang bế quan, ch&iacute;nh l&agrave; ngay cả trong nh&agrave; xảy ra chuyện lớn như vậy, cũng kh&ocirc;ng c&oacute; lộ một mặt, ngoại giới tất nhi&ecirc;n sẽ đối với n&agrave;ng c&oacute; hay kh&ocirc;ng c&ograve;n c&oacute; thể một trận chiến m&agrave; suy đo&aacute;n kh&ocirc;ng th&ocirc;i.<br />\r\n<br />\r\nT&igrave;nh huống kh&ocirc;ng thể lạc quan a, Vương Thủ Triết m&agrave;y nhăn lại.<br />\r\n<br />\r\nLại nói Vương thị chủ trạch dựa v&agrave;o n&uacute;i, ở cạnh s&ocirc;ng x&acirc;y l&ecirc;n, khoảng c&aacute;ch Định Bồ bến đ&ograve; bất qu&aacute; hơn mười dặm, nền đường cứng rắn lại l&acirc;u d&agrave;i c&oacute; giữ g&igrave;n. Lại c&oacute; tiền ti&ecirc;u gia tướng mở đường, một đường th&ocirc;ng suốt dưới, xe ngựa kh&ocirc;ng ra nửa canh giờ liền đ&atilde; đến.<br />\r\n<br />\r\nN&agrave;y bến đ&ograve; tại B&igrave;nh An trấn mới lập l&uacute;c, cũng đ&atilde; l&agrave; Trường Ninh vệ tiền ti&ecirc;u một trong. Lại trải qua B&igrave;nh An trấn hơn trăm năm ph&aacute;t triển, Định Bồ bến đ&ograve; đ&atilde; th&agrave;nh li&ecirc;n th&ocirc;ng nam bắc giao th&ocirc;ng yếu đạo.<br />\r\n<br />\r\nB&igrave;nh An tam thế gia ở đ&acirc;y đều c&oacute; bến t&agrave;u, đ&ograve; ngang, cửa h&agrave;ng chờ cơ sở c&ocirc;ng tr&igrave;nh.<br />\r\n<br />\r\nBến đ&ograve; c&oacute; đơn giản một ch&uacute;t thiết kế ph&ograve;ng ngự, tường th&agrave;nh c&oacute; ch&uacute;t đơn sơ bất qu&aacute; cao năm s&aacute;u m&eacute;t, chỉ c&oacute; thể ngăn cản tiểu quy m&ocirc; hung th&uacute; xung k&iacute;ch.<br />\r\n<br />\r\nVương Thủ Triết một đo&agrave;n người tiến v&agrave;o bến đ&ograve; về sau, thẳng đến nước s&acirc;u bến t&agrave;u m&agrave; đi. Thật d&agrave;i mộc kết cấu cầu t&agrave;u dưới, đứng sừng sững lấy từng c&acirc;y hai tay v&acirc;y k&iacute;n cự h&igrave;nh cột gỗ, một đường đem cầu t&agrave;u chọn đến ba bốn trượng nước s&acirc;u chỗ, khu nước s&acirc;u lại c&oacute; hai đường ngang cầu t&agrave;u, hợp th&agrave;nh từng c&aacute;i nơi cập bến.<br />\r\n<br />\r\nNơi cập bến tr&ecirc;n đỗ lấy từng chiếc từng chiếc lớn nhỏ kh&ocirc;ng đều thuyền, c&oacute; đ&ograve; ngang, thương thuyền, cũng c&oacute; thuyền đ&aacute;nh c&aacute;.<br />\r\n<br />\r\nXa xa nh&igrave;n lại, chỉ thấy cầu t&agrave;u cuối c&ugrave;ng c&oacute; hai nh&oacute;m nh&acirc;n m&atilde; tương hỗ l&agrave; giằng co. Song phương ri&ecirc;ng ph&acirc;̀n mình cầm trong tay c&ocirc;n bổng dao nĩa, cảm x&uacute;c đều mười phần kịch liệt.<br />\r\n<br />\r\n&quot;Vương l&atilde;o lục, ngươi l&aacute; gan nhưng đủ mập. C&ograve;n d&aacute;m dẫn người đến chắn nh&agrave; ngươi Vĩnh Ch&acirc;u tiểu gia, ngươi l&agrave; ch&ecirc; tiểu gia đ&aacute;nh ngươi kh&ocirc;ng đủ nặng sao?&quot; Một thanh ni&ecirc;n tr&ecirc;u tức đ&ugrave;a cợt thanh &acirc;m vang l&ecirc;n.<br />\r\n<br />\r\n&quot;Ngươi c&aacute;i n&agrave;y Lưu gia ch&oacute; con.&quot; Một t&ecirc;n h&aacute;n tử kh&aacute;c thanh &acirc;m k&iacute;ch động tức giận g&agrave;o th&eacute;t, &quot;Ngươi kh&ocirc;ng phải liền l&agrave; ngầm hạ trong nước đ&aacute;nh l&eacute;n sao? L&agrave; gia m&ocirc;n ch&uacute;ng ta chỉ bằng bản l&atilde;nh của m&igrave;nh, lại xuống nước đao thật thương thật l&agrave;m một cuộc, Vương gia ngươi Lục gia dạy dỗ ngươi ch&oacute; chết bầm n&agrave;y l&agrave;m người như thế n&agrave;o.&quot;<br />\r\n<br />\r\n&quot;Vương l&atilde;o lục, ngươi đừng tr&aacute;ch ta xem thường ngươi, ngươi c&aacute;i n&agrave;y tuổi đ&atilde; cao to&agrave;n sống tr&ecirc;n th&acirc;n ch&oacute;, nửa điểm đầu &oacute;c đều kh&ocirc;ng c&oacute;.&quot; Thanh ni&ecirc;n thanh &acirc;m khịt mũi coi thường n&oacute;i, &quot;Dưới nước phục k&iacute;ch vốn l&agrave; đ&aacute;nh trận chiến thuật một trong, chẳng lẽ lại đ&aacute;nh nhau trước đ&oacute; c&ograve;n phải trước đ&oacute; tuy&ecirc;n bố, kh&ocirc;ng cho ph&eacute;p sử dụng chiến thuật sao? Ha ha, thật sự l&agrave; c&agrave;ng sống c&agrave;ng trở về. Kh&ocirc;ng bằng sớm một ch&uacute;t lăn ra B&igrave;nh An trấn, miễn cho ch&agrave; đạp lương thực.&quot;<br />\r\n<br />\r\nThanh ni&ecirc;n một nh&oacute;m người ồn &agrave;o cười ha hả, đi theo thanh ni&ecirc;n ồn &agrave;o, lăn ra B&igrave;nh An trấn, lăn ra B&igrave;nh An trấn.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nĐịnh Bồ bến đ&ograve; nh&acirc;n khẩu lộn xộn, tụ tập kh&ocirc;ng &iacute;t qu&aacute; khứ lữ kh&aacute;ch h&agrave;nh thương, l&uacute;c n&agrave;y đều đ&atilde; x&uacute;m lại sang đ&acirc;y xem l&ecirc;n n&aacute;o nhiệt, c&aacute;c loại tiếng nghị luận kh&ocirc;ng ngừng.<br />\r\n<br />\r\nH&ocirc;m nay trận n&agrave;y xung đột, tất nhi&ecirc;n sẽ đi theo cước lực của bọn hắn c&ugrave;ng miệng lưỡi, truyền miệng đến địa phương kh&aacute;c. L&agrave;m lần n&agrave;y xung đột ăn thiệt th&ograve;i một phương B&igrave;nh An Vương gia, cũng sẽ trở th&agrave;nh nhất thời tr&ograve; cười, trở th&agrave;nh tr&agrave; dư tửu hậu đề t&agrave;i nói chuyện, c&oacute; hại Vương thị uy vũ.<br />\r\n<br />\r\n&quot;Lưu gia ch&oacute; con.&quot; H&aacute;n tử kia đ&atilde; mặt đỏ l&ecirc;n, nổi giận đến cực hạn, &quot;Ngươi khinh người qu&aacute; đ&aacute;ng, ngươi Lục gia liều mạng với ngươi!&quot;<br />\r\n<br />\r\n&quot;Vương gia l&atilde;o cẩu, c&oacute; gan liền đến!&quot;<br />\r\n<br />\r\nSong phương một trận giới đấu, ch&iacute;nh như d&acirc;y cung băng ti li&ecirc;n hết sức căng thẳng.<br />\r\n<br />\r\n&quot;Dừng tay!&quot; C&ocirc;ng T&ocirc;n Huệ gặp chuyện qu&aacute; khẩn cấp, vội v&agrave;ng bước nhanh đi đến trong l&uacute;c giằng co c&aacute;ch dừng, &quot;Vương Định Hải, Lưu Vĩnh Ch&acirc;u, c&aacute;c ngươi trước tỉnh t&aacute;o một ch&uacute;t, c&oacute; chuyện thật tốt n&oacute;i.&quot;<br />\r\n<br />\r\nVương Thủ Triết sợ đại nương ăn thiệt th&ograve;i, vung tay l&ecirc;n, bốn vị Vương gia gia tướng liền c&ugrave;ng nhau tiến l&ecirc;n, đem C&ocirc;ng T&ocirc;n Huệ bảo hộ ở trong đ&oacute;. C&ograve;n hắn th&igrave; theo s&aacute;t ph&iacute;a sau, tiếp tục lặng lẽ quan s&aacute;t đến hết thảy.<br />\r\n<br />\r\nHiện trường quan s&aacute;t c&ugrave;ng k&yacute; ức n&oacute;i cho hắn biết, c&aacute;i kia th&acirc;n mang thiếp th&acirc;n đồ lặn, b&ecirc;n h&ocirc;ng treo một thanh dao găm, nh&igrave;n qua c&oacute; ch&uacute;t c&agrave; lơ phất phơ thanh ni&ecirc;n gọi Lưu Vĩnh Ch&acirc;u, Lưu thị chữ l&oacute;t vĩnh th&agrave;nh vi&ecirc;n gia tộc.<br />\r\n<br />\r\nTrong truyền thuyết kẻ n&agrave;y am hiểu thuỷ chiến, lại l&agrave; thủy độn chi thuật, từng cầm trong tay dao găm lặn xuống nước bắt giết qua một đầu nặng hơn hai trăm c&acirc;n nhất giai hung th&uacute; X&iacute;ch L&acirc;n thiện, bất qu&aacute; hai mươi ba hai mươi bốn tuổi liền thanh danh vang dội, l&agrave; B&igrave;nh An Lưu thị ng&agrave;n dặm c&acirc;u.<br />\r\n<br />\r\nMột c&aacute;i kh&aacute;c nam tử d&aacute;ng người gầy g&ograve;, l&agrave;n da ngăm đen, xem x&eacute;t liền l&agrave; vị trường kỳ tr&ecirc;n nước kiếm ăn người. Chỉ l&agrave; đầu hắn c&ugrave;ng c&aacute;nh tay đều bị thương, quấn lấy một &iacute;t vải bố c&oacute; vẻ hơi chật vật.<br />\r\n<br />\r\nĐ&oacute; ch&iacute;nh l&agrave; Vương Thủ Triết tộc th&uacute;c &mdash;&mdash; Vương Định Hải, chuy&ecirc;n m&ocirc;n phụ tr&aacute;ch gia tộc tại thế lực trong thủy vực ngư nghiệp đ&aacute;nh bắt b&agrave;i tập, đồng dạng mười phần am hiểu thuỷ chiến đ&aacute;nh c&aacute; v&agrave; săn bắt, cũng l&agrave; Vương Thủ Triết l&uacute;c trước ăn đầu kia Hồng Vĩ Linh Cảm chủ lực người bắt t&oacute;m.<br />\r\n<br />\r\n&quot;Nha, nguy&ecirc;n lai l&agrave; Vương gia C&ocirc;ng T&ocirc;n đại nương tử.&quot; C&aacute;i kia gọi Lưu Vĩnh Ch&acirc;u thanh ni&ecirc;n, cười h&iacute;p mắt t&ugrave;y tiện chắp tay. Sau đ&oacute; lại hướng Vương Định Hải khinh thường tr&agrave;o ph&uacute;ng, &quot;Vương l&atilde;o lục, ngươi c&ugrave;ng ta tiểu bối n&agrave;y c&oacute; ch&uacute;t ma s&aacute;t xung đột, lại vẫn chuyển ra tộc tẩu đến chỗ dựa, Vĩnh Ch&acirc;u k&iacute;nh nể k&iacute;nh nể, mặc cảm a.&quot;<br />\r\n<br />\r\n&quot;Ngươi. . .&quot; Vương Định Hải đen nh&aacute;nh gương mặt trướng đến đỏ thẫm.<br />\r\n<br />\r\n. . .</p>', '2021-11-30 20:39:33', 6, 1),
(240, 65, 'Sáo Lộ Phản Chế', '3', '<p>...<br />\r\n<br />\r\n&quot;Đủ rồi.&quot; C&ocirc;ng T&ocirc;n Huệ giọng dịu d&agrave;ng tr&aacute;ch cứ ngăn cản, sau đ&oacute; lại n&oacute;i, &quot;Lục th&uacute;c, ngươi trước đừng x&uacute;c động, mọi thứ ch&uacute;ng ta tỉnh t&aacute;o giải quyết.&quot;<br />\r\n<br />\r\nC&ocirc;ng T&ocirc;n Huệ bản th&acirc;n l&agrave; Sơn Dương C&ocirc;ng T&ocirc;n thị đ&iacute;ch nữ, lại l&agrave; Vương thị trước tộc trưởng ch&iacute;nh th&ecirc;, c&agrave;ng l&agrave; Vương thị một đời mới tộc trưởng mẹ cả, d&ugrave;ng c&aacute;i n&agrave;y th&acirc;n phận đến xử tr&iacute; điều đ&igrave;nh việc n&agrave;y đã đ&acirc;̀y đủ.<br />\r\n<br />\r\nVương Định Hải giọng k&iacute;ch động n&oacute;i: &quot;Ngũ tẩu tẩu, kh&ocirc;ng phải ta l&atilde;o Lục x&uacute;c động, m&agrave; l&agrave; Lưu gia tiểu tử qu&aacute; mức. C&aacute;i n&agrave;y B&igrave;nh An trấn ai cũng biết, c&aacute;i n&agrave;y An Giang từ Định Bồ bến đ&ograve; phải l&ecirc;n ba mươi dặm đến loạn thạch b&atilde;i, l&agrave; ta B&igrave;nh An Vương gia thuỷ vực địa b&agrave;n. Nhưng Lưu Vĩnh Ch&acirc;u tiểu tử kia ba phen m&acirc;́y b&acirc;̣n vượt tuyến đ&aacute;nh bắt, lần n&agrave;y v&acirc;̣y mà đường ho&agrave;ng vượt tuyến mười dặm, ta lại muốn kh&ocirc;ng động thủ khu trục, c&aacute;i n&agrave;y An Giang c&ograve;n c&oacute; ta Vương gia đặt ch&acirc;n chỗ trống sao?&quot;<br />\r\n<br />\r\n&quot;&Ocirc;i &ocirc;i &ocirc;i, Vương l&atilde;o lục ngươi cũng đừng &aacute;c nh&acirc;n c&aacute;o trạng trước.&quot; Lưu Vĩnh Ch&acirc;u một bộ nhận lấy lớn lao oan uổng bộ d&aacute;ng, &quot;C&aacute;i n&agrave;y An Giang gi&oacute; lớn nước gấp, ta Lưu gia thuỷ vực địa b&agrave;n lại tại An Giang thượng du, kh&oacute; tr&aacute;nh khỏi sẽ c&oacute; định kh&ocirc;ng ở thuyền thời điểm. Ngược lại l&agrave; ngươi vừa l&ecirc;n đến, liền lấy thuyền đ&aacute;nh c&aacute; đến đụng ch&uacute;ng ta, xung đột ph&iacute;a dưới bị thất thế lại c&ograve;n gọi gia trưởng. Thật sự l&agrave; lẽ n&agrave;o lại như vậy, l&agrave; bắt nạt ta Lưu gia kh&ocirc;ng c&oacute; tộc trưởng trưởng bối sao?&quot;<br />\r\n<br />\r\nVương Thủ Triết một mực kh&ocirc;ng n&oacute;i chuyện đang &acirc;m thầm quan s&aacute;t c&ugrave;ng phỏng đo&aacute;n, hắn đ&atilde; x&aacute;c định lần n&agrave;y l&agrave; Lưu thị chủ động bốc l&ecirc;n tranh chấp, mục đ&iacute;ch hơn ph&acirc;n nửa liền l&agrave; thừa dịp hắn tộc trưởng n&agrave;y mới thượng vị l&uacute;c thăm d&ograve; một hai, cũng c&oacute; thể l&agrave; t&ocirc;̀n l&acirc;́y một ch&uacute;t ước lượng hắn vị n&agrave;y mới tộc trưởng năng lực &yacute; nghĩ.<br />\r\n<br />\r\nNếu l&agrave; hắn tộc trưởng n&agrave;y bản sự kh&ocirc;ng đủ lại nh&aacute;t gan sợ phiền phức. Tiếp xuống hơn ph&acirc;n nửa lại l&agrave; c&ugrave;ng Triệu thị li&ecirc;n thủ, một loạt &acirc;m mưu minh mưu liền sẽ như thủy triều vọt tới.<br />\r\n<br />\r\n&quot;Khụ khụ!&quot;<br />\r\n<br />\r\nVương Thủ Triết ho khan hai tiếng, b&igrave;nh tĩnh đi ra ph&iacute;a trước.<br />\r\n<br />\r\n&quot;Thủ triết chất nhi, ngươi cũng tới?&quot; Vương Định Hải sắc mặt vui mừng, lập tức lại tức giận n&oacute;i, &quot;Nhanh cho ngươi Lục th&uacute;c ph&acirc;n xử thử, tiểu tử n&agrave;y &acirc;m thầm chơi lừa gạt đ&aacute;nh l&eacute;n. Ngươi Lục th&uacute;c kh&ocirc;ng phục, muốn c&ugrave;ng hắn một lần nữa c&ocirc;ng bằng đọ sức.&quot;<br />\r\n<br />\r\n&quot;Nguy&ecirc;n lai l&agrave; Vương thị thiếu tộc, kh&ocirc;ng, hẳn l&agrave; tộc trưởng gi&aacute; l&acirc;m.&quot; Lưu Vĩnh Ch&acirc;u con mắt khẽ h&iacute;p một c&aacute;i, đối Vương Thủ Triết lộ ra một ch&uacute;t ki&ecirc;ng kị c&ugrave;ng vẻ ghen gh&eacute;t.<br />\r\n<br />\r\nKh&ocirc;ng hề nghi ngờ, Vương Thủ Triết ở mọi phương diện thực lực tổng hợp tr&ecirc;n đều l&agrave; vượt xa qu&aacute; hắn Lưu Vĩnh Ch&acirc;u. M&agrave; lại Vương Thủ Triết mới mười t&aacute;m tuổi, liền tu luyện đến Luyện Kh&iacute; cảnh tầng thứ s&aacute;u cao đoạn, tương lai tiềm lực trưởng th&agrave;nh rất lớn.<br />\r\n<br />\r\nB&acirc;y giờ Lưu thị nội bộ cũng phi thường ki&ecirc;ng kị Vương Thủ Triết, nếu như cho hắn cơ hội x&ocirc;ng l&ecirc;n Linh Đ&agrave;i cảnh, kia B&igrave;nh An trấn c&aacute;ch cục liền sẽ ph&aacute;t sinh to lớn biến h&oacute;a.<br />\r\n<br />\r\nChung quanh một ch&uacute;t quần ch&uacute;ng v&acirc;y xem nh&oacute;m, cũng bắt đầu thấp giọng nghị luận ầm ĩ, tựa hồ đối với B&igrave;nh An Vương thị vừa kế nhiệm tuổi trẻ tộc trưởng rất c&oacute; hứng th&uacute;, ng&ocirc;n từ ở giữa đều mơ hồ vạch, Vương thị trước tộc trưởng Vương Định Nhạc chết qu&aacute; sớm, nếu như c&oacute; thể lại chống đỡ c&aacute;i mười năm, chờ Vương Thủ Triết người thiếu tộc trưởng n&agrave;y triệt để trưởng th&agrave;nh về sau, B&igrave;nh An Vương thị cục diện liền kh&ocirc;ng đồng dạng.<br />\r\n<br />\r\n&quot;Lục th&uacute;c, thắng bại l&agrave; chuyện thường binh gia.&quot; Vương Thủ Triết c&oacute; ch&uacute;t nheo mắt lại, đ&egrave; thấp lấy thanh &acirc;m thuận miệng thử d&ograve; x&eacute;t n&oacute;i, &quot;Bất qu&aacute;, Lục th&uacute;c ngươi thật c&oacute; nắm chắc chắc thắng hắn? Nếu l&agrave; vạn ch&uacute;ng nh&igrave;n trừng trừng hạ lại thua một trận, c&oacute; hại ta Vương thị uy vọng a.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;C&aacute;i n&agrave;y. . .&quot; Vương Định Hải hơi ch&uacute;t do dự, thấp giọng n&oacute;i, &quot;Ta c&ugrave;ng kia họ Lưu tiểu tử từng c&oacute; mấy lần xung đột, b&igrave;nh thường đều l&agrave; lực lượng ngang nhau, lẫn nhau c&oacute; ưu thế. Chẳng biết tại sao h&ocirc;m nay t&agrave; m&ocirc;n, tiểu tử kia thủy độn chi thuật tựa như tiến bộ một mảng lớn, trong nước trơn trượt giống con c&aacute; chạch đồng dạng, vội v&agrave;ng kh&ocirc;ng kịp chuẩn bị hạ Lục th&uacute;c ăn phải c&aacute;i lỗ vốn. Bất qu&aacute; chất nhi ngươi y&ecirc;n t&acirc;m, ngươi Lục th&uacute;c liều mạng gi&agrave; cũng muốn thắng được trận n&agrave;y.&quot;<br />\r\n<br />\r\nThủy độn chi thuật tiến bộ một mảng lớn?<br />\r\n<br />\r\nHa ha, c&aacute;i n&agrave;y c&oacute; &yacute; tứ, Vương Thủ Triết sờ l&ecirc;n c&aacute;i mũi, n&oacute;i chung tr&ecirc;n đ&atilde; minh bạch đối phương chiến thuật. G&acirc;y m&acirc;u thuẫn xung đột, k&iacute;ch th&iacute;ch Vương Định Hải kh&ocirc;ng phục t&acirc;m t&iacute;nh, sau đ&oacute; tại trước mắt bao người để Lưu Vĩnh Ch&acirc;u c&aacute;i n&agrave;y nh&acirc;n t&agrave;i mới nổi đ&aacute;nh bại Vương Định Hải, hao tổn Vương thị uy vọng.<br />\r\n<br />\r\nNếu l&agrave; c&oacute; thể lại k&iacute;ch th&iacute;ch một ch&uacute;t cảm x&uacute;c, song phương k&yacute; c&aacute;i đổ ước c&aacute;i g&igrave;, vậy liền lại ho&agrave;n mỹ cực kỳ. Đến l&uacute;c đ&oacute;, tất nhi&ecirc;n l&agrave; lại c&oacute; thể từ tr&ecirc;n th&acirc;n Vương thị cắn xuống một khối thịt lớn tới.<br />\r\n<br />\r\nVương Thủ Triết đối hiện trường c&oacute; minh x&aacute;c ph&aacute;n đo&aacute;n, l&uacute;c n&agrave;y n&oacute;i: &quot;Lục th&uacute;c, việc n&agrave;y giao cho ta đến xử tr&iacute;.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y. . .&quot; Vương Định Hải một ch&uacute;t do dự, bất qu&aacute;, khi nh&igrave;n đến Vương Thủ Triết một bộ kh&iacute; định thần nh&agrave;n bộ d&aacute;ng về sau, liền b&igrave;nh tĩnh lại lui ra ph&iacute;a sau hai bước, &quot;Thủ Triết, ngươi phải cẩn thận nhiều hơn một &iacute;t, Lưu thị am hiểu nhất đ&ugrave;a bỡn quỷ kế.&quot;<br />\r\n<br />\r\n&quot;Lục th&uacute;c cứ y&ecirc;n t&acirc;m, kh&ocirc;ng ăn thi&ecirc;̣t thòi.&quot; Vương Thủ Triết trấn an một c&acirc;u về sau, nh&igrave;n chung quanh hiện trường một tuần, tại vạn ch&uacute;ng nh&igrave;n trừng trừng hạ cao giọng n&oacute;i: &quot;Huyền Vũ thế gia, từ trước đến nay l&agrave; d&ugrave;ng v&otilde; lập nh&agrave;. C&aacute;i gọi l&agrave; tổ truyền địa b&agrave;n, nếu l&agrave; hậu bối v&ocirc; năng thủ hộ, lui khỏi vị tr&iacute; một g&oacute;c cũng l&agrave; chuyện đương nhi&ecirc;n. Lưu Vĩnh Ch&acirc;u, c&aacute;c ngươi Lưu thị đ&atilde; nghĩ từng bước x&acirc;m chiếm chiếm đoạt ta Vương thị đ&aacute;nh c&aacute; v&agrave; săn bắt khu vực, g&acirc;y m&acirc;u thuẫn xung đột bất qu&aacute; l&agrave; tiểu đạo m&agrave; th&ocirc;i. Kh&ocirc;ng bằng sảng kho&aacute;i một ch&uacute;t, hai người ch&uacute;ng ta giữa đồng bối, kh&ocirc;ng bằng bắt chước tổ t&ocirc;ng đến một trận bất luận sinh tử l&ocirc;i đ&agrave;i chiến, người n&agrave;o thắng đối phương ngư trường liền về ai như thế n&agrave;o?&quot;<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\nChung quanh tiếng k&ecirc;u kinh ngạc khắp nơi, sinh tử l&ocirc;i đ&agrave;i! ? Vương thị thiếu ni&ecirc;n kia tộc trưởng muốn hay kh&ocirc;ng như thế cấp tiến a?<br />\r\n<br />\r\nVương Thủ Triết so Lưu Vĩnh Ch&acirc;u nhỏ trọn vẹn năm tuổi, nhưng như thế ở trước mặt khởi xướng sinh tử l&ocirc;i đ&agrave;i chiến, lại l&agrave;m cho Lưu Vĩnh Ch&acirc;u sắc mặt l&uacute;c trắng l&uacute;c xanh, c&aacute;i n&agrave;y gốc rạ hắn kh&ocirc;ng d&aacute;m nhận, cũng kh&ocirc;ng tiếp nổi, chỉ c&oacute; thể ngượng ng&ugrave;ng n&oacute;i: &quot;Vương gia tộc trưởng ng&agrave;i n&oacute;i đ&ugrave;a, ngươi thế nhưng l&agrave; tộc trưởng, c&oacute; thể n&agrave;o xuống l&ocirc;i đ&agrave;i sinh tử chiến đ&acirc;u?&quot;<br />\r\n<br />\r\nVẻn vẹn từ một điểm n&agrave;y nh&igrave;n lại, Lưu Vĩnh Ch&acirc;u mặc d&ugrave; trong người đồng lứa cũng kh&ocirc;ng tầm thường, nhưng l&agrave; so với một mực coi như thiếu tộc trưởng bồi dưỡng Vương Thủ Triết tới n&oacute;i k&eacute;m kh&ocirc;ng chỉ một bậc.<br />\r\n<br />\r\n&quot;Ha ha ~&quot; Vương Thủ Triết cười lạnh tr&agrave;o ph&uacute;ng, &quot;Nh&aacute;t gan ch&iacute;nh l&agrave; nh&aacute;t gan, sao phải n&oacute;i đ&ecirc;́n như thế đường ho&agrave;ng. Ta Vương Thủ Triết h&ocirc;m nay liền đứng ở chỗ n&agrave;y, chỉ cần l&agrave; ngươi Lưu thị chữ l&oacute;t vĩnh, đến nhiều &iacute;t ta Vương Thủ Triết tiếp bao nhi&ecirc;u.&quot;<br />\r\n<br />\r\n&quot;Tốt! N&oacute;i hay lắm, Vương thị mới tộc trưởng đủ h&agrave;o kh&iacute; a.&quot;<br />\r\n<br />\r\n&quot;Huyền Vũ gia tộc, d&ugrave;ng v&otilde; lập nh&agrave;, c&oacute; ch&uacute;t &yacute; tứ, lần n&agrave;y nh&igrave;n Lưu thị l&agrave;m sao tiếp. Theo ta được biết, Lưu thị chữ l&oacute;t vĩnh lớn nhất ba mươi s&aacute;u ba mươi bảy tuổi nhi&ecirc;̀u, nhỏ nhất c&ograve;n tại bi b&ocirc; tập n&oacute;i, c&agrave;ng nghĩ, thật đ&uacute;ng l&agrave; l&agrave; nghĩ kh&ocirc;ng ra ai c&oacute; thể c&ugrave;ng Vương thị mới tộc trưởng một trận chiến.&quot;<br />\r\n<br />\r\n&quot;Huynh đ&agrave;i, người trẻ tuổi kia thực lực thế n&agrave;o? Thật đ&uacute;ng l&agrave; c&oacute; thể đ&aacute;nh bại Lưu thị tất cả chữ l&oacute;t vĩnh?&quot;<br />\r\n<br />\r\n&quot;Ngươi l&agrave; người b&ecirc;n ngo&agrave;i a? Đ&oacute; l&agrave; ch&uacute;ng ta B&igrave;nh An Vương thị mới tộc trưởng Vương Thủ Triết, năm gần mười t&aacute;m liền sắp tu luyện đ&ecirc;́n Luyện Kh&iacute; cảnh cao giai, m&agrave; lại truyền thuyết hắn tại kiếm đạo thi&ecirc;n ph&uacute; tr&ecirc;n cực mạnh, kiếm thuật đ&atilde; sớm đến tr&igrave;nh độ l&ocirc; hỏa thuần thanh.&quot; Người địa phương mang theo tự h&agrave;o giới thiệu n&oacute;i, &quot;M&agrave; lại kia Vương Thủ Triết đ&atilde; th&ocirc;ng qua Tử Phủ Học Cung chi&ecirc;u sinh khảo hạch, &iacute;t ng&agrave;y nữa sắp nhập học, chỉ tiếc Vương thị ra như vậy một cọc đại sự, đ&agrave;nh phải từ bỏ việc học.&quot;<br />\r\n<br />\r\n&quot;Tử Phủ Học Cung, liền l&agrave; c&aacute;i kia danh xưng vừa v&agrave;o Tử Phủ tất th&agrave;nh linh đ&agrave;i Tử Phủ Học Cung? Lợi hại lợi hại, đ&aacute;ng tiếc đ&aacute;ng tiếc!&quot;<br />\r\n<br />\r\nChung quanh nghị luận ầm ĩ truyền đến Vương Thủ Triết trong tai, ngược lại để hắn nhớ lại trước đ&oacute; kh&ocirc;ng l&acirc;u thật đ&uacute;ng l&agrave; th&ocirc;ng qua được Tử Phủ Học Cung chi&ecirc;u sinh sơ tuyển.<br />\r\n<br />\r\nLũng Tả quận Tử Phủ Học Cung mỗi ba năm đều sẽ tuyển nhận một giới t&acirc;n sinh, bất qu&aacute; y&ecirc;u cầu của bọn hắn cực k&igrave; nghi&ecirc;m ngặt, Trường Ninh phạm vi b&ecirc;n trong mỗi lần đều sẽ c&oacute; một đến ba c&aacute;i th&ocirc;ng qua sơ tuyển, cũng c&oacute; tư c&aacute;ch tham dự phục thi danh ngạch, m&agrave; một năm n&agrave;y Vương Thủ Triết danh liệt trong đ&oacute;.<br />\r\n<br />\r\nNhưng l&agrave; n&oacute;i c&aacute;i g&igrave; sắp nhập học liền khoa trương, to&agrave;n bộ Trường Ninh vệ phạm vi quản hạt, mười năm t&aacute;m năm cũng chưa chắc c&oacute; thể ra một c&aacute;i Tử Phủ học sinh. Vương Thủ Triết tự nhận bất ph&agrave;m, nhưng cũng biết c&oacute; thể th&ocirc;ng qua thi v&ograve;ng hai khảo hạch x&aacute;c suất kh&ocirc;ng đủ ba th&agrave;nh.<br />\r\n<br />\r\nChung quanh hướng gi&oacute; v&agrave; dư luận, bắt đầu hướng Vương thị nghi&ecirc;ng.<br />\r\n<br />\r\nTrước đ&oacute; kia một trận xung đột m&acirc;u thuẫn, nguy&ecirc;n bản xử l&yacute; bất đương sẽ tổn thương Vương thị thanh danh. Nhưng b&acirc;y giờ, lại ngược lại cổ vũ Vương thị uy vọng.<br />\r\n<br />\r\n&quot;Đại điệt m&agrave; l&agrave;m tốt lắm.&quot; Vương Định Hải mặt mũi tr&agrave;n đầy vui vẻ n&oacute;i, &quot;Nh&igrave;n kia Lưu gia oắt con c&ograve;n d&aacute;m ph&aacute;ch lối kh&ocirc;ng?&quot;<br />\r\n<br />\r\nC&ocirc;ng T&ocirc;n Huệ kia đối trong đ&ocirc;i mắt đẹp, c&agrave;ng l&agrave; đối với Vương Thủ Triết dị sắc li&ecirc;n tục, kh&ocirc;ng nghĩ tới hắn dăm ba c&acirc;u vừa ra, v&acirc;̣y mà sẽ c&oacute; tổn hại gia tộc uy vọng chuyện xấu, thay đổi th&agrave;nh đối Lưu thị uy vọng bất lợi cục diện.<br />\r\n<br />\r\nTrước đ&oacute;, n&agrave;ng kia con trai trưởng từ trước đến nay chỉ chuy&ecirc;n ch&uacute; tại tự th&acirc;n tu luyện, đối c&ograve;n lại sự t&igrave;nh quan t&acirc;m cực &iacute;t, kh&ocirc;ng nghĩ tới sơ l&agrave;m tộc trưởng, liền hiện ra năng lực.<br />\r\n<br />\r\n&quot;Tốt!&quot;<br />\r\n<br />\r\nBỗng dưng, một c&aacute;i &acirc;m thanh ủng hộ vang l&ecirc;n, &quot;Vương thị gia chủ lần n&agrave;y ng&ocirc;n luận, thật sự l&agrave; l&agrave;m cho Lưu mỗ đinh tai nhức &oacute;c, bội phục bội phục, Huyền Vũ gia tộc, d&ugrave;ng v&otilde; lập nh&agrave;, như thủ kh&ocirc;ng được tổ nghiệp, sao kh&ocirc;ng lui khỏi vị tr&iacute; một g&oacute;c, tốt, n&oacute;i hay lắm!&quot;<br />\r\n<br />\r\nĐang khi n&oacute;i chuyện một vị người mặc cẩm b&agrave;o, kh&iacute; độ bất ph&agrave;m nam tử trung ni&ecirc;n, tại mấy t&ecirc;n dũng m&atilde;nh gia tướng chen ch&uacute;c hạ long h&agrave;nh hổ bộ m&agrave; ra, biểu lộ cười như kh&ocirc;ng cười nh&igrave;n chằm chằm Vương Thủ Triết.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nNgười n&agrave;y vừa ra, C&ocirc;ng T&ocirc;n Huệ c&ugrave;ng Vương Định Hải c&ugrave;ng nhau biến sắc: &quot;Lại l&agrave; Lưu Thắng Nghiệp!&quot;<br />\r\n<br />\r\nNhất l&agrave; C&ocirc;ng T&ocirc;n Huệ, trực tiếp sắc mặt kh&oacute; chịu đối Vương Thủ Triết nhẹ giọng nhắc nhở: &quot;Triết Nhi, Lưu thị gia chủ l&uacute;c n&agrave;y xuất hiện tất c&oacute; kỳ quặc, ngươi chỉ cần cẩn thận ứng đối.&quot;<br />\r\n<br />\r\nLưu Thắng Nghiệp.<br />\r\n<br />\r\nVương Thủ Triết con mắt c&oacute; ch&uacute;t nheo lại, B&igrave;nh An Lưu thị đương nhiệm tộc trưởng, nghe n&oacute;i hắn c&aacute; nh&acirc;n v&otilde; lực cũng kh&ocirc;ng phải l&agrave; Lưu thị mạnh nhất, bốn mươi tuổi c&ograve;n kh&ocirc;ng đột ph&aacute; đến Luyện Kh&iacute; cảnh tám tầng.<br />\r\n<br />\r\nNhưng l&agrave; tại Vương Thủ Triết trong tr&iacute; nhớ, người n&agrave;y từ trước đến nay lấy t&iacute;nh to&aacute;n xảo tr&aacute; nổi danh, l&agrave; nổi danh khẩu Phật t&acirc;m x&agrave;. Hắn c&ugrave;ng Vương thị trước tộc trưởng Vương Định Nhạc giao phong b&ecirc;n trong nhiều lần chiếm tiện nghi, lại phi thường giỏi về kinh doanh gia sản d&ograve;ng họ, để Lưu thị thực lực ph&aacute;t triển kh&ocirc;ng ngừng.<br />\r\n<br />\r\n&quot;Nguy&ecirc;n lai l&agrave; Lưu gia chủ gi&aacute; l&acirc;m.&quot; Vương Thủ Triết b&igrave;nh tĩnh tự nhi&ecirc;n địa đ&aacute;p lại, &quot;Đ&atilde; Lưu gia chủ đều t&aacute;n đồng ta, vậy c&oacute; phải đồng &yacute; ta c&ugrave;ng Lưu Vĩnh Ch&acirc;u sinh tử l&ocirc;i đ&agrave;i rồi?&quot; Hắn kh&ocirc;ng n&oacute;i hai lời, trước cho Lưu Thắng Nghiệp hạ c&aacute;i bộ lại n&oacute;i.<br />\r\n<br />\r\n&quot;N&agrave;o c&oacute; việc n&agrave;y, vương gia chủ mặc d&ugrave; c&oacute; đạo l&yacute;, nhưng l&agrave; ngươi th&acirc;n l&agrave; tộc trưởng, h&aacute; c&oacute; thể lấy lớn hiếp nhỏ?&quot; Lưu Thắng Nghiệp đả x&agrave; t&ugrave;y c&ocirc;n tr&ecirc;n n&oacute;i, &quot;Đ&atilde; l&agrave; sinh tử l&ocirc;i đ&agrave;i, tự nhi&ecirc;n đ&ecirc;́n th&acirc;n phận kh&ocirc;ng sai biệt lắm mới c&oacute; đ&aacute;ng xem.&quot;<br />\r\n<br />\r\nHắn &yacute; tứ, tự nhi&ecirc;n l&agrave; muốn đem trận chiến n&agrave;y l&ocirc;i k&eacute;o về Lưu Vĩnh Ch&acirc;u c&ugrave;ng Vương Định Hải tr&ecirc;n th&acirc;n.<br />\r\n<br />\r\n&quot;Lưu gia chủ lời ấy thật l&agrave; hữu l&yacute;.&quot; Vương Thủ Triết biểu lộ nghi&ecirc;m t&uacute;c gật đầu đồng &yacute;, &quot;Ngươi ta Vương thị Lưu thị, ng&agrave;y b&igrave;nh thường ma s&aacute;t rất nhiều, o&aacute;n hận chất chứa kh&ocirc;ng &iacute;t. Đ&atilde; muốn sinh tử l&ocirc;i đ&agrave;i, kh&ocirc;ng bằng hai ch&uacute;ng ta vị tộc trưởng tự th&acirc;n l&ecirc;n trận, cũng coi l&agrave; th&acirc;n phận tương đương.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\nLưu Thắng Nghiệp khu&ocirc;n mặt co lại, tộc trưởng tự th&acirc;n l&ecirc;n sinh tử l&ocirc;i đ&agrave;i? Vương Thủ Triết tiểu tử n&agrave;y đi&ecirc;n rồi sao? Đầu ti&ecirc;n l&agrave; cắn l&ecirc;n Lưu Vĩnh Ch&acirc;u, hiện tại trực tiếp cắn một c&aacute;i l&ecirc;n hắn Lưu Thắng Nghiệp! Đ&acirc;y l&agrave; bắt ai cắn ai vậy, ta n&oacute;i đ&uacute;ng &yacute; kia sao?<br />\r\n<br />\r\nChung quanh những c&aacute;i kia v&acirc;y xem h&agrave;nh thương lữ kh&aacute;ch, c&ugrave;ng bến đ&ograve; những người kh&aacute;c cũng l&acirc;m v&agrave;o lặng ngắt như tờ trong l&uacute;c khiếp sợ, c&aacute;i n&agrave;y vừa l&ecirc;n đến liền l&agrave; hai c&aacute;i thế gia tộc trưởng tự m&igrave;nh sinh tử chiến, muốn hay kh&ocirc;ng như thế x&uacute;c động a?<br />\r\n<br />\r\n. . .</p>', '2021-11-30 20:39:58', 6, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(241, 65, 'Tộc Trưởng Có Chút Hung', '4', '<p>...<br />\r\n<br />\r\n&quot;Đại điệt, tuyệt đối kh&ocirc;ng thể.&quot; Vương Định Hải t&igrave;nh thế cấp b&aacute;ch khuy&ecirc;n can n&oacute;i, &quot;Lưu thị gia chủ đ&atilde; bốn mươi tuổi, kinh nghiệm gi&agrave; dặn.&quot;<br />\r\n<br />\r\n&quot;Triết Nhi, ngươi qu&aacute; vọng động rồi.&quot; C&ocirc;ng T&ocirc;n Huệ cũng c&oacute; ch&uacute;t bối rối, &quot;Tộc trưởng th&acirc;n phận t&ocirc;n qu&yacute;, l&agrave; gia tộc Định Hải Thần Ch&acirc;m...&quot;<br />\r\n<br />\r\nNếu l&agrave; c&ugrave;ng Lưu Vĩnh Ch&acirc;u đ&aacute;nh, bọn hắn ngược lại l&agrave; mừng rỡ hắn gặp, rốt cuộc song phương thực lực sai biệt kh&aacute; lớn. C&oacute; thể trực tiếp khi&ecirc;u chiến Lưu Thắng Nghiệp, vậy liền t&igrave;nh huống kh&ocirc;ng r&otilde; qu&aacute; mức mạo hiểm.<br />\r\n<br />\r\nMột b&ecirc;n Lưu Thắng Nghiệp ngược lại l&agrave; ngầm hạ vuốt một c&aacute;i mồ h&ocirc;i lạnh, c&ograve;n tốt c&ograve;n tốt, c&aacute;i n&agrave;y Vương thị vẫn l&agrave; c&oacute; r&otilde; l&iacute; lẽ hạng người, l&agrave; hẳn l&agrave; thật tốt khuy&ecirc;n nhủ Vương thị c&aacute;i n&agrave;y tuổi trẻ tộc trưởng, chuyện n&agrave;y l&agrave; sao sao?<br />\r\n<br />\r\nN&agrave;o c&oacute; vừa l&ecirc;n đến, hai qu&acirc;n chủ so&aacute;i tới trước sinh tử quyết chiến, c&aacute;i n&agrave;y kh&ocirc;ng lộn xộn sao.<br />\r\n<br />\r\nHuống chi hắn Lưu Thắng Nghiệp nhiều năm như vậy đến một mực sống an nh&agrave;n sung sướng, l&acirc;u kh&ocirc;ng c&ugrave;ng người động thủ, sinh tử quyết chiến cũng kh&ocirc;ng phải n&oacute;i đ&ugrave;a.<br />\r\n<br />\r\n&quot;Đại nương, Lục th&uacute;c.&quot; Vương Thủ Triết lại l&agrave; trực tiếp đ&aacute;nh g&atilde;y bọn hắn, &quot;Tộc trưởng bất qu&aacute; cũng l&agrave; gia tộc một vi&ecirc;n, ph&agrave;m l&agrave; gia tộc một vi&ecirc;n, ai cũng c&oacute; quyền lợi c&ugrave;ng nghĩa vụ, v&igrave; gia tộc n&eacute;m đầu l&acirc;u vẩy nhiệt huyết, v&igrave; gia tộc sinh tồn m&agrave; hi sinh. Huống chi Lung Y&ecirc;n l&atilde;o...&quot;<br />\r\n<br />\r\n<br />\r\nn&oacute;i đến phần sau, Vương Thủ Triết cảm x&uacute;c c&oacute; ch&uacute;t k&iacute;ch động c&ugrave;ng bi phẫn, phảng phất l&agrave; muốn tại trong sự ngột ngạt bộc ph&aacute;t đồng dạng, cuối c&ugrave;ng ba chữ c&agrave;ng l&agrave; muốn n&oacute;i lại th&ocirc;i, bỗng nhi&ecirc;n nuốt trở v&agrave;o.<br />\r\n<br />\r\n&quot;Thủ Triết!&quot; Vương Định Hải lập tức luống cuống, vội v&agrave;ng thấp giọng hỏi, &quot;Lung Y&ecirc;n l&atilde;o tổ n&agrave;ng sẽ kh&ocirc;ng...&quot;<br />\r\n<br />\r\n&quot;Y&ecirc;n t&acirc;m, n&agrave;ng kh&ocirc;ng c&oacute; việc g&igrave;.&quot; Vương Thủ Triết sắc mặt c&oacute; ch&uacute;t t&aacute;i nhợt, &quot;Liền l&agrave; đau l&ograve;ng l&atilde;o tổ cả một đời v&igrave; gia tộc nỗ lực rất rất nhiều, ch&uacute;ng ta những n&agrave;y l&agrave;m hậu bối l&agrave; thời điểm đứng ra lưu điểm m&aacute;u.&quot;<br />\r\n<br />\r\nNhưng m&agrave; lời hắn n&oacute;i, thấy thế n&agrave;o đều giống như tại che giấu.<br />\r\n<br />\r\nDứt lời, Vương Thủ Triết quay người để mắt tới Lưu Thắng Nghiệp: &quot;Lưu gia chủ, đ&atilde; ch&uacute;ng ta hai nh&agrave; m&acirc;u thuẫn tr&ugrave;ng điệp, kh&ocirc;ng bằng thừa n&agrave;y cơ hội tốt tới một lần kết th&uacute;c. Ngươi ta k&yacute; kết sinh tử quyết chiến khế ước, người thua r&uacute;t khỏi An Giang ngư trường, đồng thời gia tộc kh&ocirc;ng được truy cứu.&quot; Ng&ocirc;n từ b&ecirc;n trong, rất c&oacute; một cỗ quyết tuyệt chi sắc.<br />\r\n<br />\r\n&quot;Tốt, Vương tộc trưởng thiếu ni&ecirc;n anh h&ugrave;ng quả nhi&ecirc;n c&oacute; huyết t&iacute;nh.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Đ&atilde; hai nh&agrave; c&oacute; m&acirc;u thuẫn, kh&ocirc;ng bằng vương đối vương, đến c&aacute;i kết th&uacute;c.&quot;<br />\r\n<br />\r\nQuần ch&uacute;ng v&acirc;y xem nh&oacute;m, tự nhi&ecirc;n l&agrave; xem n&aacute;o nhiệt kh&ocirc;ng ch&ecirc; chuyện lớn, c&oacute; cơ hội nh&igrave;n thấy hai đại tộc trưởng tự th&acirc;n l&ecirc;n trận sinh tử chiến, l&agrave; bực n&agrave;o hiếm thấy chuyện l&yacute; th&uacute;, l&uacute;c n&agrave;y, đều nhao nhao ồn &agrave;o, đem việc n&agrave;y dựng l&ecirc;n đến lại n&oacute;i.<br />\r\n<br />\r\n&quot;Nương! Tiểu tử n&agrave;y l&agrave;m sao như đầu s&oacute;i đ&oacute;i giống như? Nh&igrave;n hắn ng&ocirc;n từ lấp l&oacute;e, chẳng lẽ... Truyền ng&ocirc;n l&agrave; thật... Kể từ đ&oacute;, t&igrave;nh thế liền thay đổi a.&quot; Lưu Thắng Nghiệp giật m&igrave;nh, nh&iacute;u chặt m&agrave;y, hơi suy nghĩ một ch&uacute;t sau liền c&oacute; so đo, n&oacute;i sang chuyện kh&aacute;c lại lộ ra một bộ h&ograve;a kh&iacute; sinh t&agrave;i biểu lộ, &quot;Hiền chất a, n&oacute;i đến ta c&ugrave;ng phụ th&acirc;n ngươi, cũng coi l&agrave; nhiều năm bạn cũ, gọi ngươi một tiếng hiền chất kh&ocirc;ng qu&aacute; phận a? Ch&uacute;ng ta B&igrave;nh An tam thế gia đặt ch&acirc;n B&igrave;nh An trấn nhiều năm, đều l&agrave; hương th&acirc;n hương l&yacute;, lẽ ra lẫn nhau n&acirc;ng đỡ c&ugrave;ng chiếu cố. Như thế nho nhỏ m&acirc;u thuẫn xung đột, l&agrave;m sao đến mức đ&acirc;y, l&agrave;m sao đến mức n&agrave;y đ&acirc;y n&agrave;y.&quot;<br />\r\n<br />\r\n&quot;Vậy theo Lưu gia chủ c&oacute; &yacute; tứ l&agrave;, chuẩn bị bồi thường ta Lục th&uacute;c tiền thuốc?&quot; Vương Thủ Triết h&iacute;p mắt lại, kh&oacute;e m&ocirc;i nhếch l&ecirc;n cười lạnh.<br />\r\n<br />\r\nBồi thường tiền thuốc men?<br />\r\n<br />\r\nLưu Thắng Nghiệp mặt tối sầm, bất qu&aacute; hắn từ trước đến nay am hiểu ẩn nhẫn ẩn n&uacute;p tiếu l&yacute; t&agrave;ng đao, l&uacute;c n&agrave;y cười đến c&agrave;ng th&ecirc;m h&ograve;a &aacute;i: &quot;Hương th&acirc;n hương l&yacute; m&acirc;u thuẫn xung đột kh&ocirc;ng thể tr&aacute;nh được, bất qu&aacute; lần n&agrave;y nếu l&agrave; Vĩnh Ch&acirc;u sai, ch&uacute;ng ta tự nhi&ecirc;n l&agrave; đ&ecirc;́n thừa nhận. Đ&acirc;y l&agrave; ba mươi c&agrave;n kim, xem như đứng y&ecirc;n Hải huynh đệ bồi tội.&quot;<br />\r\n<br />\r\nDứt lời, Lưu Thắng Nghiệp m&oacute;c ra ba tấm mười c&aacute;i c&agrave;n kim kim phiếu, cười h&iacute;p mắt nh&eacute;t v&agrave;o Vương Thủ Triết trong tay: &quot;Đều n&oacute;i hiền chất l&agrave; nh&acirc;n trung long phượng, tiềm lực kh&ocirc;ng thua năm đ&oacute; Lung Y&ecirc;n l&atilde;o tổ, tương lai hẳn l&agrave; ch&uacute;ng ta B&igrave;nh An trấn đỉnh lương chi trụ.&quot;<br />\r\n<br />\r\nBa mươi c&agrave;n kim?<br />\r\n<br />\r\nDung hợp k&yacute; ức Vương Thủ Triết biết, c&aacute;i n&agrave;y đ&atilde; l&agrave; một khoản tiền lớn, l&uacute;c n&agrave;y hắn thấy tốt th&igrave; lấy, đồng dạng cười n&oacute;i: &quot;Lưu gia chủ mới l&agrave; ta B&igrave;nh An Định Hải Thần Ch&acirc;m, Thủ Triết vừa mới đảm nhiệm tộc trưởng chi vị, sau n&agrave;y c&ograve;n phải dựa v&agrave;o Lưu gia chủ nhiều hơn tr&ocirc;ng nom.&quot;<br />\r\n<br />\r\nNhất định nhất định!<br />\r\n<br />\r\nLưu Thắng Nghiệp cười ha ha hai tiếng về sau, mang theo gia tướng c&ugrave;ng Lưu Vĩnh Ch&acirc;u rời đi nước s&acirc;u bến t&agrave;u khu.<br />\r\n<br />\r\nQuần ch&uacute;ng v&acirc;y xem nh&oacute;m gặp kh&ocirc;ng thật đ&aacute;nh nhau, ngược lại l&agrave; một mảnh tiếc hận c&ugrave;ng tiếng thở d&agrave;i, phảng phất giống như l&agrave; bỏ qua c&aacute;i g&igrave; vở kịch, hơi c&oacute; vẻ thất vọng tốp năm tốp ba tản ra.<br />\r\n<br />\r\n&quot;Vẫn l&agrave; chất nhi c&oacute; thủ đoạn.&quot; Vương Định Hải phảng phất xả được cơn giận đồng dạng, vui mừng nhướng m&agrave;y n&oacute;i, &quot;Dăm ba c&acirc;u liền lật về t&igrave;nh thế, c&ograve;n để Lưu Thắng Nghiệp đầu kia l&atilde;o hồ ly cam t&acirc;m t&igrave;nh nguyện chịu nhận lỗi.&quot;<br />\r\n<br />\r\nM&agrave; C&ocirc;ng T&ocirc;n Huệ lại l&agrave; kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o vui mừng, hai đầu l&ocirc;ng m&agrave;y ngược lại lộ ra lo lắng.<br />\r\n<br />\r\n&quot;Lục th&uacute;c, đại nương, ch&uacute;ng ta tới đều tới, kh&ocirc;ng bằng tr&ecirc;n Lục th&uacute;c tr&ecirc;n thuyền nh&igrave;n xem.&quot; Vương Thủ Triết cười cười, &quot;Ta c&aacute;i n&agrave;y từ nhỏ đến lớn, c&ograve;n kh&ocirc;ng theo Lục th&uacute;c đi ra thuyền.&quot;<br />\r\n<br />\r\n&quot;Tốt tốt tốt, kia Lục th&uacute;c liền mang ngươi thật tốt chơi đ&ugrave;a.&quot; Vương Định Hải cao hứng n&oacute;i, &quot;Chờ một l&uacute;c cho ngươi biểu diễn một ch&uacute;t bắt c&aacute; tuyệt kỹ.&quot;<br />\r\n<br />\r\nL&acirc;m thượng thuyền thời khắc, Vương Thủ Triết đem mấy c&aacute;i gia tướng chi&ecirc;u đến b&ecirc;n người, c&ugrave;ng bọn hắn n&oacute;i nhỏ b&agrave;n giao một phen. Gia tướng hiểu &yacute;, sau khi h&agrave;nh lễ bốn ph&iacute;a tản ra chấp h&agrave;nh nhiệm vụ đi.<br />\r\n<br />\r\nVương Thủ Triết l&uacute;c n&agrave;y mới c&ugrave;ng C&ocirc;ng T&ocirc;n Huệ c&ugrave;ng một chỗ, theo Vương Định Hải hướng nước s&acirc;u nơi cập bến đi đến.<br />\r\n<br />\r\nC&ugrave;ng l&uacute;c đ&oacute;.<br />\r\n<br />\r\nĐịnh Bồ bến đ&ograve; b&ecirc;n ngo&agrave;i, trẻ tuổi n&oacute;ng t&iacute;nh Lưu Vĩnh Ch&acirc;u sắc mặt rất kh&oacute; chịu n&oacute;i: &quot;Gia chủ, ch&uacute;ng ta đ&atilde; n&oacute;i xong muốn tr&ecirc;n người Vương thị cắn khối thịt xuống tới, l&agrave;m sao l&agrave;m đến cuối c&ugrave;ng, ch&uacute;ng ta chịu nhận lỗi rồi?&quot;<br />\r\n<br />\r\n&quot;Vĩnh Ch&acirc;u a, ngươi c&ograve;n qu&aacute; trẻ.&quot; Lưu Thắng Nghiệp gi&oacute; nhạt m&acirc;y nhẹ, chắp hai tay sau lưng cười tủm tỉm n&oacute;i, &quot;Bởi v&igrave; c&aacute;i gọi l&agrave; trước kh&aacute;c nay kh&aacute;c, hiện tại loại n&agrave;y thế cục dưới, ch&uacute;ng ta kh&ocirc;ng những kh&ocirc;ng thể chủ động đi tr&ecirc;u chọc Vương thị, ngược lại hẳn l&agrave; khắp nơi cẩn thận ph&ograve;ng ngừa c&ugrave;ng Vương thị xung đột.&quot;<br />\r\n<br />\r\n&quot;Gia chủ...&quot; Lưu Vĩnh Ch&acirc;u c&oacute; ch&uacute;t tức giận c&ugrave;ng kh&ocirc;ng hiểu.<br />\r\n<br />\r\n&quot;Bằng v&agrave;o ta suy đo&aacute;n, Vương Lung Y&ecirc;n kia l&atilde;o bất tử đ&atilde; kh&ocirc;ng c&ograve;n s&ocirc;́ng l&acirc;u nữa.&quot; Lưu Thắng Nghiệp h&iacute;p mắt, tr&iacute; tuệ vững v&agrave;ng n&oacute;i, &quot;Vương thị hiện tại tựa như l&agrave; một con sắp chết đi bệnh hổ, ch&iacute;nh l&agrave; tối thời điểm nguy hiểm. Nếu l&agrave; ch&uacute;ng ta bức b&aacute;ch thật chặt, n&oacute;i kh&ocirc;ng chừng liền sẽ g&acirc;y n&ecirc;n đối phương đi&ecirc;n cuồng phản phệ. Ta Lưu thị mặc d&ugrave; kh&ocirc;ng sợ, nhưng cũng kh&ocirc;ng muốn tiện nghi Triệu thị ngư &ocirc;ng đắc lợi.&quot;<br />\r\n<br />\r\nVương Lung Y&ecirc;n!<br />\r\n<br />\r\nC&aacute;i t&ecirc;n n&agrave;y tựa như l&agrave; ngạnh tại Lưu thị yết hầu một c&acirc;y gai độc, n&agrave;ng một ng&agrave;y bất tử, Lưu thị liền một ng&agrave;y kh&ocirc;ng d&aacute;m c&ugrave;ng Vương thị triệt để vạch mặt.<br />\r\n<br />\r\n&quot;Gia chủ đ&acirc;y l&agrave; sự thực?&quot; Lưu Vĩnh Ch&acirc;u hưng phấn kh&ocirc;ng th&ocirc;i.<br />\r\n<br />\r\n&quot;Trước kia Vương Lung Y&ecirc;n bế quan kh&ocirc;ng ra, ngay cả Vương thị gia chủ thay đổi đại sự như thế đều kh&ocirc;ng xuất hiện, ta liền đ&atilde; c&oacute; ba bốn điểm suy đo&aacute;n.&quot; Lưu Thắng Nghiệp hắc hắc nở nụ cười lạnh, &quot;Ta xem Vương Thủ Triết c&aacute;i kia mới tộc trưởng như thế kh&ocirc;ng giữ được b&igrave;nh tĩnh, c&ograve;n muốn được ăn cả ng&atilde; về kh&ocirc;ng c&ugrave;ng ta liều chết một trận chiến, liền ấn chứng bảy t&aacute;m phần suy đo&aacute;n. Hắc hắc, ta h&aacute; c&oacute; thể như hắn &yacute;. Nhanh th&igrave; hai ba năm, chậm th&igrave; bốn, năm năm, chờ Vương Lung Y&ecirc;n vừa chết...&quot;<br />\r\n<br />\r\n&quot;Gia chủ? C&ograve;n muốn l&acirc;u như vậy?&quot; Lưu Vĩnh Ch&acirc;u c&oacute; ch&uacute;t cho&aacute;ng v&aacute;ng.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Ngươi biết c&aacute;i g&igrave;? Gia tộc hưng suy giao thế h&aacute; lại một sớm một chiều c&oacute; thể đạt th&agrave;nh? Ta Lưu thị nhiều năm như vậy cũng chờ đến đ&acirc;y, chỉ l&agrave; mấy năm c&ograve;n chịu kh&ocirc;ng dậy nổi sao?&quot; Lưu Thắng Nghiệp vung l&ecirc;n ống tay &aacute;o, ở nh&agrave; đem n&acirc;ng đỡ đi đến xe ngựa, &quot;Huống chi tại Vương Lung Y&ecirc;n chết trước đ&oacute;, ch&uacute;ng ta Lưu thị c&ograve;n c&oacute; rất nhiều c&ocirc;ng t&aacute;c chuẩn bị muốn l&agrave;m. Vĩnh Ch&acirc;u, ngươi nhớ lấy, kh&ocirc;ng c&oacute; ta mệnh lệnh trước kh&ocirc;ng được c&ugrave;ng Vương thị xung đột, nếu kh&ocirc;ng gia ph&aacute;p xử tr&iacute;!&quot;<br />\r\n<br />\r\n&quot;V&acirc;ng, gia chủ.&quot; Lưu Vĩnh Ch&acirc;u k&igrave;m n&eacute;n một cỗ kh&iacute;, lại lại kh&ocirc;ng thể l&agrave;m g&igrave;, đ&agrave;nh phải cung tiễn tộc trưởng Lưu Thắng Nghiệp xe ngựa rời đi.<br />\r\n<br />\r\n...<br />\r\n<br />\r\nLại nói Vương Thủ Triết để mấy vị gia tướng lưu thủ bến t&agrave;u chấp h&agrave;nh nhiệm vụ, c&ugrave;ng đại nương C&ocirc;ng T&ocirc;n Huệ c&ugrave;ng nhau leo l&ecirc;n Vương Định Hải cỡ trung thuyền đ&aacute;nh c&aacute; &quot;Định hải&quot; hiệu, kia chiếc cỡ trung thuyền đ&aacute;nh c&aacute; điều ước d&agrave;i hạn t&aacute;m ch&iacute;n trượng, thư th&aacute;i hai trượng, to&agrave;n bộ từ chất gỗ kết cấu chế tạo.<br />\r\n<br />\r\nTừ cao cao cột buồm c&ugrave;ng rơi xuống c&aacute;nh buồm nh&igrave;n, l&agrave; một chiếc c&aacute;nh buồm động lực thuyền.<br />\r\n<br />\r\nVương Định Hải ph&acirc;n ph&oacute; dưới trướng người ch&egrave;o thuyền giương buồm xuất cảng, cũng tr&agrave;n đầy phấn khởi địa muốn c&ugrave;ng Vương Thủ Triết giới thiệu một ch&uacute;t tr&ecirc;n thuyền c&aacute;c hạng c&ocirc;ng tr&igrave;nh l&uacute;c, C&ocirc;ng T&ocirc;n Huệ sắc mặt nghi&ecirc;m t&uacute;c n&oacute;i: &quot;Triết Nhi, Lục th&uacute;c, ch&uacute;ng ta đến đu&ocirc;i thuyền n&oacute;i chuyện.&quot;<br />\r\n<br />\r\nHai người theo lời m&agrave; theo đến, thuyền một trận lắc lư dưới, dần dần l&aacute;i ra cảng nước s&acirc;u.<br />\r\n<br />\r\n&quot;Ai ~ Triết Nhi, ngươi h&ocirc;m nay c&oacute; ch&uacute;t qu&aacute; mức.&quot; C&ocirc;ng T&ocirc;n Huệ thở d&agrave;i n&oacute;i, &quot;Lung Y&ecirc;n l&atilde;o tổ v&igrave; gia tộc hi sinh cả một đời, trong đ&oacute; chua x&oacute;t c&ugrave;ng khổ cực nhiều v&ocirc; số kể. Ngươi h&aacute; c&oacute; thể n&oacute;i năng bậy bạ &aacute;m chỉ n&agrave;ng l&atilde;o nh&acirc;n gia, c&aacute;i kia... , hơn nữa c&ograve;n l&agrave; tại trước mặt mọi người? C&ograve;n c&oacute;, ngươi v&acirc;̣y mà mới vừa rồi c&ograve;n điều động gia tướng khuếch t&aacute;n lời đồn! Ngươi biểu hiện h&ocirc;m nay tất nhi&ecirc;n sẽ truyền đến tộc l&atilde;o trong lỗ tai, mấy c&aacute;i kia trưởng bối kh&ocirc;ng thiếu được sẽ răn dạy ngươi.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y...&quot; Một b&ecirc;n Vương Định Hải cũng c&oacute; ch&uacute;t hoảng hốt, &quot;Ngũ tẩu, vậy l&agrave;m sao b&acirc;y giờ? C&aacute;c trưởng l&atilde;o sẽ kh&ocirc;ng vạch tội Thủ Triết a?&quot; Lung Y&ecirc;n l&atilde;o tổ chẳng những l&agrave; gia tộc Định Hải Thần Ch&acirc;m, vẫn l&agrave; như l&agrave; cao như n&uacute;i tồn tại, Vương Định Hải trong l&ograve;ng tự nhi&ecirc;n l&agrave; k&iacute;nh sợ đến cực điểm.<br />\r\n<br />\r\n&quot;Lục th&uacute;c, b&igrave;nh tĩnh, b&igrave;nh tĩnh.&quot; Vương Thủ Triết cười nhạt một tiếng, &quot;Tộc l&atilde;o đều l&agrave; trưởng bối, ta tin tưởng hiểu chi lấy lý sau bọn hắn sẽ l&yacute; giải. Đại nương ta hỏi ngươi, Lung Y&ecirc;n l&atilde;o tổ c&ograve;n c&oacute; thể ki&ecirc;n tr&igrave; bao l&acirc;u?&quot;<br />\r\n<br />\r\n&quot;Triết Nhi, ngươi h&aacute; c&oacute; thể đối Lung Y&ecirc;n l&atilde;o tổ như thế bất k&iacute;nh, ch&uacute;ng ta kh&ocirc;ng thể vọng nghị...&quot;<br />\r\n<br />\r\n&quot;Đại nương, ta th&acirc;n l&agrave; tộc trưởng, tự nhi&ecirc;n nhất định phải lấy to&agrave;n tộc lợi &iacute;ch ph&aacute;t triển to&agrave;n diện c&acirc;n nhắc.&quot; Vương Thủ Triết trực tiếp đ&aacute;nh g&atilde;y C&ocirc;ng T&ocirc;n Huệ, &quot;Nếu l&agrave; ta ngay cả l&atilde;o tổ c&ograve;n c&oacute; thể ki&ecirc;n tr&igrave; bao l&acirc;u cũng kh&ocirc;ng thể hỏi kh&ocirc;ng sao biết được, l&agrave;m sao c&oacute; thể t&iacute;nh to&aacute;n gia tộc tương lai ph&aacute;t triển? Nếu như thế, ta Vương Thủ Triết chỉ sợ đảm đương kh&ocirc;ng nổi tộc trưởng n&agrave;y chi vị, vẫn l&agrave; sớm l&agrave;m thay người đi.&quot;<br />\r\n<br />\r\n...</p>', '2021-11-30 20:40:20', 7, 1),
(242, 65, 'Lung Yên Lão Tổ! Bên Ta Vương Nổ', '5', '<p>. . .<br />\r\n<br />\r\n&quot;Triết Nhi, ngươi. . . Ngươi sao c&oacute; thể n&oacute;i lời như vậy?&quot; C&ocirc;ng T&ocirc;n Huệ kh&ocirc;ng ngờ tới vẫn lu&ocirc;n nhu thuận Vương Thủ Triết, n&oacute;i chuyện vậy m&agrave; như thế kinh người.<br />\r\n<br />\r\n&quot;Ngũ tẩu tẩu, ta cảm thấy Thủ Triết c&oacute; đạo l&yacute;.&quot; Vương Định Hải h&ocirc;m nay c&oacute; ch&uacute;t chịu phục Vương Thủ Triết, l&uacute;c n&agrave;y h&aacute;t đệm n&oacute;i, &quot;Lung Y&ecirc;n l&atilde;o tổ c&oacute; thể chống l&ecirc;n gia tộc bao l&acirc;u, mọi người cũng tốt c&oacute; chuẩn bị t&acirc;m l&yacute;.&quot;<br />\r\n<br />\r\nC&ocirc;ng T&ocirc;n Huệ biến đổi sắc mặt mấy lần, rốt cục vẫn l&agrave; n&oacute;i: &quot;Đ&acirc;y l&agrave; gia tộc cơ mật, v&agrave;i ng&agrave;y trước ta đi bẩm b&aacute;o Lung Y&ecirc;n l&atilde;o tổ l&uacute;c, n&agrave;ng ch&iacute;nh miệng n&oacute;i với ta nếu l&agrave; kh&ocirc;ng động thủ n&agrave;ng c&ograve;n c&oacute; thể trấn được hai mươi năm, nếu l&agrave; động thủ. . . Chỉ sợ liền kh&ocirc;ng n&oacute;i được rồi.&quot;<br />\r\n<br />\r\nHai mươi năm? Vương Thủ Triết thầm nghĩ kh&ocirc;ng th&ocirc;i, ngược lại l&agrave; so ch&iacute;nh m&igrave;nh tưởng tượng b&ecirc;n trong l&acirc;u d&agrave;i nhiều lắm, như thế ngược lại l&agrave; chưa hẳn kh&ocirc;ng c&oacute; lật b&agrave;n thời cơ.<br />\r\n<br />\r\n&quot;Triết Nhi, ngươi l&agrave; gia tộc hi vọng duy nhất.&quot; C&ocirc;ng T&ocirc;n Huệ sắc mặt nghi&ecirc;m t&uacute;c kh&ocirc;ng th&ocirc;i, &quot;Ch&uacute;ng ta Vương thị tiếp xuống hẳn l&agrave; tiếp tục ẩn nhẫn điệu thấp, tập to&agrave;n tộc chi lực gi&uacute;p ngươi tại trong v&ograve;ng hai mươi năm đột ph&aacute; Linh Đ&agrave;i cảnh, tuyệt đối đừng mọc lan tr&agrave;n sự cố!&quot;<br />\r\n<br />\r\n&quot;Đúng v&acirc;̣y a đ&uacute;ng vậy a, Lục th&uacute;c sẽ nghĩ biện ph&aacute;p nhiều bắt một &iacute;t Linh Ngư b&aacute;n lấy tiền cho ngươi kiếm t&agrave;i nguy&ecirc;n.&quot; Vương Định Hải cũng l&agrave; c&oacute; ch&uacute;t t&aacute;n đồng n&oacute;i, &quot;Chỉ cần ngươi c&oacute; thể x&ocirc;ng l&ecirc;n Linh Đ&agrave;i cảnh, ch&uacute;ng ta Vương thị mới t&iacute;nh thật sự c&oacute; cứu.&quot;<br />\r\n<br />\r\n&quot;Ta x&ocirc;ng l&ecirc;n Linh Đ&agrave;i cảnh? Sau đ&oacute; th&igrave; sao?&quot; Vương Thủ Triết cười lạnh khịt mũi coi thường, &quot;Kh&ocirc;ng n&oacute;i trước ta c&oacute; thể hay kh&ocirc;ng x&ocirc;ng đến b&ecirc;n tr&ecirc;n, cho d&ugrave; c&oacute; thời cơ x&ocirc;ng, Lưu thị c&ugrave;ng Triệu thị sẽ trơ mắt nh&igrave;n ta x&ocirc;ng? Sẽ cho ta thời gian hai mươi năm? Huống chi, cho d&ugrave; ta may mắn x&ocirc;ng l&ecirc;n, cũng bất qu&aacute; l&agrave; một c&aacute;i kh&aacute;c Lung Y&ecirc;n l&atilde;o tổ m&agrave; th&ocirc;i!&quot;<br />\r\n<br />\r\nVương Thủ Triết l&agrave; người xuy&ecirc;n việt, tự nhi&ecirc;n c&oacute; thể nhảy ra Vương thị tộc nh&acirc;n tư duy theo qu&aacute;n t&iacute;nh.<br />\r\n<br />\r\nHắn, để C&ocirc;ng T&ocirc;n Huệ c&ugrave;ng Vương Định Hải trong l&ograve;ng ph&aacute;t lạnh, trước đ&oacute; bọn hắn một mực sống ở Lung Y&ecirc;n l&atilde;o tổ che chở cho, coi l&agrave; Lưu thị Triệu thị sẽ kh&ocirc;ng thật vạch mặt.<br />\r\n<br />\r\nNhưng l&agrave;, nếu như bị bọn hắn k&yacute; th&aacute;c to&agrave;n bộ hi vọng Vương Thủ Triết thật sự c&oacute; vọng xung k&iacute;ch linh đ&agrave;i, Lưu thị c&ugrave;ng Triệu thị sao lại ngồi y&ecirc;n kh&ocirc;ng l&yacute; đến?<br />\r\n<br />\r\n&quot;Được r&ocirc;̀i, ta vừa rồi cố &yacute; biểu hiện ra l&atilde;o tổ kh&ocirc;ng c&ograve;n s&ocirc;́ng l&acirc;u nữa &aacute;m chỉ. Ta tin tưởng lấy Lưu Thắng Nghiệp l&atilde;o hồ ly kia mưu t&iacute;nh, tuyệt đối sẽ kh&ocirc;ng lựa chọn l&uacute;c n&agrave;y đ&aacute;nh ti&ecirc;n phong c&ugrave;ng ta Vương thị cắn x&eacute;. Bởi vậy, ta để gia tướng tiếp tục &acirc;m thầm canh chừng, cho Lưu thị c&ugrave;ng Triệu thị một c&aacute;i năm năm kỳ tả hữu đếm ngược.&quot; Vương Thủ Triết hừ n&oacute;i, &quot;Ai d&aacute;m tại trong v&ograve;ng năm năm tr&ecirc;u chọc ch&uacute;ng ta, ch&uacute;ng ta liền liều mạng với người đ&oacute;, bắt lấy một nh&agrave; hướng chết đ&aacute;nh, thời khắc mấu chốt ch&uacute;ng ta c&oacute; thể thả ra. . . &Aacute;ch ~ mời ra Lung Y&ecirc;n l&atilde;o tổ trấn b&atilde;i.&quot;<br />\r\n<br />\r\nTheo Vương Thủ Triết, Lung Y&ecirc;n l&atilde;o tổ liền l&agrave; một tay vương nổ, vương nổ t&aacute;c dụng lớn nhất t&iacute;nh kh&ocirc;ng phải nổ ra đi trong nh&aacute;y mắt đ&oacute;, m&agrave; l&agrave; tại nổ ra trước khi đi lực uy hiếp.<br />\r\n<br />\r\n&quot;Diệu a.&quot; Vương Định Hải vỗ đ&ugrave;i, &quot;Gia chủ giỏi t&iacute;nh to&aacute;n, ch&uacute;ng ta b&acirc;y giờ liền l&agrave; một đầu trước khi chết m&atilde;nh th&uacute;, bọn hắn liền sợ ch&uacute;ng ta trước khi chết liều mạng phản phệ. Kể từ đ&oacute;, bọn hắn chẳng những kh&ocirc;ng d&aacute;m khi dễ ch&uacute;ng ta, c&ograve;n phải nghĩ biện ph&aacute;p giao hảo dỗ d&agrave;nh. Kh&oacute; tr&aacute;ch Lưu Thắng Nghiệp l&atilde;o hồ ly kia, ngoan ngo&atilde;n cấp ra ba mươi c&agrave;n kim nhận lỗi.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Kh&ocirc;ng những như thế, bởi v&igrave; hiện tại c&oacute; ch&uacute;ng ta Vương thị minh x&aacute;c &#39;Tử kỳ&#39;, như thế một khối lớn m&ecirc; người thịt mỡ đặt ở trước mặt, l&uacute;c trước bởi v&igrave; c&ugrave;ng chung mối th&ugrave; m&agrave; kết minh Lưu thị c&ugrave;ng Triệu thị tại ẩn nhẫn chờ đợi đồng thời, sao lại kh&ocirc;ng c&oacute; &yacute; kh&aacute;c?&quot; Vương Thủ Triết cười tủm tỉm n&oacute;i, &quot;Phải biết, tham lam c&ugrave;ng kh&ocirc;ng vừa l&ograve;ng thế nhưng l&agrave; cơ bản nhất nh&acirc;n t&iacute;nh. Tiếp xuống cục diện, tất nhi&ecirc;n sẽ trở n&ecirc;n rất c&oacute; &yacute; tứ. Nếu như lại nghĩ biện ph&aacute;p &acirc;m thầm trợ gi&uacute;p một đợt ~ ha ha &quot;<br />\r\n<br />\r\nNghe xong Vương Thủ Triết ph&acirc;n t&iacute;ch, C&ocirc;ng T&ocirc;n Huệ c&ugrave;ng Vương Định Hải đều l&agrave; h&iacute;t v&agrave;o một ngụm kh&iacute; lạnh, kh&ocirc;ng khỏi hai mặt nh&igrave;n nhau, đều cảm gi&aacute;c trong l&ograve;ng c&oacute; ch&uacute;t sợ sệt . Bất qu&aacute;, bọn hắn đồng thời đều sinh ra một tia hi vọng, một tia gia tộc c&oacute; cơ hội lật b&agrave;n hi vọng!<br />\r\n<br />\r\n&quot;Đại nương, Lục th&uacute;c, việc n&agrave;y l&agrave; Vương thị gia tộc sinh tử tồn vong lớn cơ mật.&quot; Vương Thủ Triết trịnh trọng kỳ sự chắp tay n&oacute;i, &quot;Thứ nhất l&agrave; hi vọng c&aacute;c ngươi c&oacute; thể giữ vững cơ mật, thứ hai l&agrave;, hi vọng c&aacute;c ngươi c&oacute; thể t&iacute;n nhiệm v&ocirc; điều kiện ta ủng hộ ta.&quot;<br />\r\n<br />\r\nĐ&acirc;y mới l&agrave; Vương Thủ Triết mục đ&iacute;ch, hắn th&acirc;n thể n&agrave;y qu&aacute; trẻ tuổi, cho d&ugrave; l&agrave; c&oacute; tộc trưởng c&aacute;i th&acirc;n phận n&agrave;y cũng kh&oacute; tr&aacute;nh khỏi sẽ gặp người xen v&agrave;o, chỉ c&oacute; thể trước n&eacute;m ra ngo&agrave;i một ch&uacute;t kinh người &yacute; nghĩ đến tin phục một &iacute;t nh&acirc;n vật mấu chốt, thắng được minh hữu.<br />\r\n<br />\r\nC&ocirc;ng T&ocirc;n Huệ l&agrave; hắn mẹ cả, hắn cũng l&agrave; C&ocirc;ng T&ocirc;n Huệ tương lai dựa v&agrave;o, m&agrave; Lục th&uacute;c Vương Định Hải t&iacute;nh c&aacute;ch cũng l&agrave; trực tiếp h&agrave;o sảng dễ d&agrave;ng l&ocirc;i k&eacute;o.<br />\r\n<br />\r\nC&ocirc;ng T&ocirc;n Huệ c&ugrave;ng Vương Định Hải đều l&agrave; h&iacute;t s&acirc;u một hơi, trịnh trọng kỳ sự ph&aacute;t thề.<br />\r\n<br />\r\nBa người đang khi n&oacute;i chuyện, cỡ trung thuyền đ&aacute;nh c&aacute; đ&atilde; ở người ch&egrave;o thuyền điều khiển dưới, nhanh ch&oacute;ng c&aacute;ch rời nước s&acirc;u bến t&agrave;u, tiến v&agrave;o d&ograve;ng nước chảy xiết An Giang trung t&acirc;m.<br />\r\n<br />\r\nAn Giang chi kho&aacute;t, viễn si&ecirc;u Vương Thủ Triết tưởng tượng, liếc nh&igrave;n lại kh&oacute;i tr&ecirc;n s&ocirc;ng m&ecirc;nh m&ocirc;ng v&ocirc; ngần v&ocirc; tận, Định Bồ bến đ&ograve; c&agrave;ng ng&agrave;y c&agrave;ng nhỏ dần dần nh&igrave;n kh&ocirc;ng r&otilde;.<br />\r\n<br />\r\nTheo c&agrave;ng th&ecirc;m tiến v&agrave;o An Giang chỗ s&acirc;u, s&oacute;ng gi&oacute; lớn dần, thuyền lay động c&agrave;ng th&ecirc;m kịch liệt, ch&iacute;nh l&agrave; ngay cả Vương Thủ Triết Luyện Kh&iacute; cảnh s&aacute;u tầng đỉnh phong tố chất th&acirc;n thể, cũng cảm thấy c&oacute; ch&uacute;t buồn n&ocirc;n.<br />\r\n<br />\r\nĐồng dạng sống an nh&agrave;n sung sướng C&ocirc;ng T&ocirc;n Huệ, cũng c&oacute; ch&uacute;t sắc mặt trắng bệch, kh&ocirc;ng thoải m&aacute;i bắt đầu.<br />\r\n<br />\r\n&quot;Ha ha, Ngũ tẩu, Thủ Triết, thuyền đ&aacute;nh c&aacute; kh&ocirc;ng thể so với t&agrave;u thuỷ vững v&agrave;ng. Tại thuyền lay động thời điểm, c&aacute;c ngươi đừng d&ugrave;ng th&acirc;n thể đi đối kh&aacute;ng nghi&ecirc;ng, m&agrave; ứng như c&aacute;i con lật đật đồng dạng thuận thế m&agrave; l&agrave;m.&quot; Vương Định Hải ki&ecirc;n nhẫn truyền thụ hai người ứng đối ra sao s&oacute;ng gi&oacute; x&oacute;c nảy.<br />\r\n<br />\r\nVương Thủ Triết c&ugrave;ng C&ocirc;ng T&ocirc;n Huệ đều l&agrave; Huyền Vũ người, một th&acirc;n Huyền kh&iacute; c&ugrave;ng tố chất th&acirc;n thể c&ograve;n tại đ&oacute;, kh&ocirc;ng ra một l&aacute;t đ&atilde; th&iacute;ch ứng x&oacute;c nảy. Nhất l&agrave; Vương Thủ Triết, học Vương Định Hải hai ch&acirc;n như c&aacute;i cọc đinh tr&ecirc;n boong thuyền, th&acirc;n thể theo gi&oacute; s&oacute;ng chập tr&ugrave;ng c&oacute; vận luật đong đưa, tư th&aacute;i dần dần thư gi&atilde;n th&iacute;ch &yacute; bắt đầu.<br />\r\n<br />\r\n&quot;Thủ Triết quả nhi&ecirc;n tốt thi&ecirc;n ph&uacute;, Lục th&uacute;c cho ngươi biểu diễn một ch&uacute;t b&igrave;nh thường đều l&agrave; như thế n&agrave;o bắt c&aacute;.&quot; Vương Định Hải tr&agrave;n đầy phấn khởi m&agrave; chuẩn bị hiện ra một ch&uacute;t m&igrave;nh, trong gia tộc c&oacute; rất &iacute;t người sẽ th&iacute;ch đến thuyền đ&aacute;nh c&aacute; nh&igrave;n hắn bắt c&aacute;.<br />\r\n<br />\r\n&quot;Ta tin tưởng Lục th&uacute;c ngươi kỹ thuật khẳng định bất ph&agrave;m, bất qu&aacute; vẫn l&agrave; trước hết để cho phổ th&ocirc;ng ngư c&ocirc;ng thử một ch&uacute;t.&quot; Vương Thủ Triết đề nghị.<br />\r\n<br />\r\nVương Định Hải sững sờ, lại chợt cười n&oacute;i: &quot;Thủ Triết ngươi đ&acirc;y l&agrave; tới khảo hạch thuyền đ&aacute;nh c&aacute; ti&ecirc;u chuẩn. Thật tốt, ngươi Lục th&uacute;c c&aacute;i n&agrave;y kỳ hạm thuyền đ&aacute;nh c&aacute; tr&ecirc;n ngư c&ocirc;ng, từng c&aacute;i đều l&agrave; hảo thủ. Vương Cẩu Đản, ngươi đến cho tộc trưởng biểu thị một thanh.&quot;<br />\r\n<br />\r\n&quot;V&acirc;ng, Hải gia.&quot; Một c&aacute;i hai mươi d&acirc;y x&iacute;ch tuổi trẻ tiểu hỏa tử từ cột buồm tr&ecirc;n thoan xuống tới, động t&aacute;c linh hoạt giống con con khỉ, cầm l&ecirc;n một trương lưới đ&aacute;nh c&aacute; sửa sang lại một phen về sau, phần eo vặn một c&aacute;i hất l&ecirc;n, lưới đ&aacute;nh c&aacute; từ mạn thuyền tr&ecirc;n bay ra ngo&agrave;i, tr&ecirc;n kh&ocirc;ng trung mở ra một c&aacute;i xinh đẹp v&ograve;ng tr&ograve;n lớn t&uacute;i,<br />\r\n<br />\r\nSoạt!<br />\r\n<br />\r\nChụp lấy nặng rơi lưới đ&aacute;nh c&aacute;, tựa như l&agrave; một c&aacute;i m&oacute;c ngược b&aacute;t nước lớn nhập v&agrave;o trong nước, mấy hơi thở liền ch&igrave;m xuống đ&aacute;y nước.<br />\r\n<br />\r\n&quot;Tốt!&quot;<br />\r\n<br />\r\nChung quanh ngư c&ocirc;ng đều khen một tiếng.<br />\r\n<br />\r\nĐ&acirc;y l&agrave;. . . Tung lưới, m&agrave; lại kỹ thuật cực kỳ mạnh.<br />\r\n<br />\r\nVương Thủ Triết cũng l&agrave; trước ti&ecirc;n ph&aacute;n đo&aacute;n ra, xuy&ecirc;n qua trước hắn liền l&agrave; một c&aacute;i b&igrave;nh thường d&acirc;n đi l&agrave;m, b&igrave;nh thường ngẫu nhi&ecirc;n c&ugrave;ng bằng hữu c&acirc;u c&acirc;u c&aacute;. Bởi vậy đang c&agrave;y n&agrave;o đ&oacute; &acirc;m ngọn n&uacute;i n&agrave;o đ&oacute; chờ video APP l&uacute;c, ph&eacute;p t&iacute;nh sẽ đề cử một ch&uacute;t bắt c&aacute; video, trong đ&oacute; kh&ocirc;ng thiếu c&oacute; một &iacute;t tung lưới cao thủ video c&ugrave;ng trực tiếp.<br />\r\n<br />\r\nMặc d&ugrave; ch&iacute;nh Vương Thủ Triết sẽ kh&ocirc;ng chơi, nhưng cũng c&oacute; thể đ&aacute;nh gi&aacute; ra c&aacute;i n&agrave;y một lưới l&agrave; tốt l&agrave; xấu. Kh&ocirc;ng thể kh&ocirc;ng thừa nhận, c&aacute;i n&agrave;y gọi Cẩu Đản người trẻ tuổi ti&ecirc;u chuẩn kh&ocirc;ng sai.<br />\r\n<br />\r\nVương Cẩu Đản chờ lưới ch&igrave;m tới đ&aacute;y về sau, bắt đầu cẩn thận từng li từng t&iacute; thu d&acirc;y thừng, đem lưới thiếp đ&aacute;y s&ocirc;ng thu nạp về sau, lại một ch&uacute;t x&iacute;u nhổ l&ecirc;n, cuối c&ugrave;ng, hắn bỗng nhi&ecirc;n co lại, cả thanh lưới đ&aacute;nh c&aacute; đều bị hắn một nh&aacute;y mắt k&eacute;o đến tr&ecirc;n thuyền.<br />\r\n<br />\r\nTr&uacute;ng rồi!<br />\r\n<br />\r\nLưới đ&aacute;nh c&aacute; giữ được một đầu v&acirc;y lưng hoa r&acirc;m c&aacute; lớn, vương Cẩu Đản đem c&aacute; từ t&uacute;i lưới b&ecirc;n trong giải ra, bắt sau hoan thi&ecirc;n hỉ địa tới tranh c&ocirc;ng: &quot;Hải ca, l&agrave; đầu Cửu Tinh Lư, đ&ecirc;́n c&oacute; nặng năm c&acirc;n.&quot;<br />\r\n<br />\r\n&quot;Tiểu tử ngươi kh&ocirc;ng tệ a, n&eacute;m nước chảy trong rương.&quot; Vương Định Hải vỗ vỗ bả vai hắn khen một tiếng, quay đầu hướng Vương Thủ Triết giải th&iacute;ch n&oacute;i, &quot;Cửu Tinh Lư mặc d&ugrave; kh&ocirc;ng thuộc về Linh Ngư, lại cảm gi&aacute;c trơn mềm sướng miệng thuộc về thượng đẳng lo&agrave;i c&aacute;, đầu n&agrave;y cho d&ugrave; b&aacute;n cho h&agrave;ng c&aacute; tử đều phải c&aacute;i trước đại đồng. Bất qu&aacute; gia tộc ch&uacute;ng ta tại Trường Ninh trong th&agrave;nh c&oacute; c&aacute; trải, đều l&agrave; tự sản từ ti&ecirc;u, dạng n&agrave;y c&oacute; thể nhiều kiếm mấy c&aacute;i tiền xu.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nVương Thủ Triết gật đầu t&aacute;n thưởng, Đại C&agrave;n Quốc tiền tệ lấy tiền xu, đại đồng, c&agrave;n kim đến t&iacute;nh to&aacute;n. B&igrave;nh thường m&agrave; n&oacute;i một c&acirc;n c&acirc;y l&uacute;a mới năm đến mười c&aacute;i sừng tử, một trăm tiền xu mới c&oacute; thể hối đo&aacute;i một c&aacute;i đại đồng, bởi vậy con c&aacute; n&agrave;y gi&aacute; trị một c&aacute;i đại đồng c&oacute; gi&aacute; trị kh&ocirc;ng nhỏ.<br />\r\n<br />\r\nVương Cẩu Đản đem c&aacute; lấy được ph&oacute;ng tới thuyền đ&aacute;nh c&aacute; trung ương khoang th&ocirc;ng nước b&ecirc;n trong, c&aacute;i n&agrave;y khoang thuyền c&ugrave;ng ngoại giới d&ograve;ng nước th&ocirc;ng suốt, c&oacute; thể mức độ lớn nhất bảo tr&igrave; c&aacute; lấy được hoạt t&iacute;nh.<br />\r\n<br />\r\nBất qu&aacute; theo Vương Định Hải n&oacute;i, chỉ cần một ch&uacute;t gi&aacute; trị tương đối cao c&aacute; lấy được mới c&oacute; đ&atilde;i ngộ n&agrave;y. Khoang th&ocirc;ng nước cũng bị chia cắt thành m&acirc;́y mảnh khu vực, chuy&ecirc;n m&ocirc;n cất giữ chủng loại kh&aacute;c biệt c&aacute; lấy được.<br />\r\n<br />\r\nC&oacute; vương Cẩu Đản tr&igrave;nh diễn t&agrave;i nghệ, c&ograve;n lại ngư c&ocirc;ng cũng nhao nhao biểu diễn tuyệt chi&ecirc;u, từng c&aacute;i thủ ph&aacute;p đều mười phần gi&agrave; dặn, rải ra lưới vừa lớn vừa tr&ograve;n, bất qu&aacute; c&aacute;i n&agrave;y c&aacute; lấy được lại l&agrave; kh&ocirc;ng nhất định.<br />\r\n<br />\r\nĐa số k&eacute;o l&ecirc;n lưới đều l&agrave; kh&ocirc;ng lưới, ngẫu nhi&ecirc;n mới c&oacute; một &iacute;t kinh hỉ.<br />\r\n<br />\r\nHơn một canh giờ về sau, c&aacute;c loại c&aacute; lấy được dần dần nhiều hơn, Cửu Tinh Lư đều c&oacute; ba đầu, c&ograve;n c&oacute; một đầu c&agrave;ng th&ecirc;m đắt đỏ s&oacute;ng dữ b&aacute;o vằn quyết, mặc d&ugrave; bất qu&aacute; hai c&acirc;n nhi&ecirc;̀u, nhưng l&agrave; Vương Định Hải lại biểu thị con c&aacute; n&agrave;y &iacute;t nhất phải 1 đại đồng 60 tiền xu, tốt c&aacute; lấy được đều giữ lại cho tộc trưởng hưởng dụng.<br />\r\n<br />\r\nVương Thủ Triết mặt ngo&agrave;i cười nhẹ nh&agrave;ng, vừa &yacute; tiếp theo thẳng đang t&iacute;nh to&aacute;n kh&ocirc;ng th&ocirc;i. C&aacute;i n&agrave;y ba mươi dặm đ&aacute;nh c&aacute; v&agrave; săn bắt trận l&agrave; B&igrave;nh An Vương thị đ&aacute;nh c&aacute; v&agrave; săn bắt phạm vi, gia tộc đối với c&aacute;i n&agrave;y gia sản d&ograve;ng họ cũng mười phần coi trọng, n&ecirc;n l&agrave; gia tộc trọng yếu t&agrave;i ch&iacute;nh thu nhập một trong.<br />\r\n<br />\r\nTheo thời gian tr&ocirc;i qua, Vương Thủ Triết dần dần hơi kh&ocirc;ng ki&ecirc;n nhẫn, n&oacute;i thẳng: &quot;Lục th&uacute;c, ch&uacute;ng ta biểu diễn cũng biểu diễn đ&ecirc;́n kh&ocirc;ng sai biệt lắm, c&aacute;c vị ngư c&ocirc;ng tung lưới kỹ thuật đều cực kỳ mạnh. Ch&uacute;ng ta c&oacute; thể hay kh&ocirc;ng cấp tốc tiến v&agrave;o ch&iacute;nh đề, biểu diễn một lượt ch&acirc;n ch&iacute;nh bắt c&aacute; b&agrave;i tập.&quot;<br />\r\n<br />\r\nVương Định Hải l&uacute;c đầu hồng quang toả s&aacute;ng biểu lộ trong nh&aacute;y mắt dừng lại, c&oacute; ch&uacute;t kh&ocirc;ng nghĩ ra n&oacute;i: Thủ Triết, ch&uacute;ng ta kh&ocirc;ng phải đ&atilde; đang diễn b&agrave;y ra ch&acirc;n ch&iacute;nh bắt c&aacute; sao? Hẳn l&agrave;, ng&agrave;i l&agrave; muốn nh&igrave;n bắt giữ Linh Ngư? Một ch&uacute;t h&igrave;nh thể nhỏ b&eacute; Linh Ngư, đều l&agrave; th&ocirc;ng qua vận kh&iacute; đ&aacute;nh bắt đi l&ecirc;n, một ch&uacute;t cỡ lớn Linh Ngư mười phần hung m&atilde;nh, cần d&ugrave;ng đ&aacute;nh c&aacute; v&agrave; săn bắt tiễn mới c&oacute; thể đối ph&oacute;.&quot;<br />\r\n<br />\r\nĐ&acirc;y ch&iacute;nh l&agrave; nghề nghiệp bắt c&aacute; rồi?<br />\r\n<br />\r\nVương Thủ Triết mặt đều c&oacute; ch&uacute;t đen, đời trước bởi v&igrave; t&ograve; m&ograve;, cọ lấy đồng sự gia th&acirc;n th&iacute;ch nghề nghiệp thuyền đ&aacute;nh c&aacute; đi thể nghiệm qua nghề nghiệp đ&aacute;nh bắt, cuối c&ugrave;ng kia một lưới thu đi l&ecirc;n trọn vẹn hơn bốn vạn c&acirc;n, c&ograve;n bị cho rằng l&agrave; mất m&ugrave;a. . .<br />\r\n<br />\r\nVề phần tung lưới, tr&ecirc;n địa cầu kia l&agrave; thuần t&uacute;y c&aacute; vui t&iacute;nh c&ocirc;ng cụ, liền so c&acirc;u c&aacute; c&aacute;i g&igrave; mạnh hơn một ch&uacute;t, người chơi đều l&agrave; đồ c&aacute;i hưu nh&agrave;n giải tr&iacute;. Ai muốn thật cầm tung lưới l&agrave;m nghề nghiệp bắt c&aacute; đi nu&ocirc;i s&ocirc;́ng gia đình, cả nh&agrave; đều phải chết đ&oacute;i.</p>', '2021-11-30 20:40:44', 9, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(243, 66, 'Bắt đầu Chính Là Thần', '1', '<p>&quot;Ta l&agrave; Quang Minh Thần!&quot;<br />\r\n<br />\r\nNg&ocirc; Huy khoanh ch&acirc;n ngồi tại kh&ocirc;ng trung, quan s&aacute;t dưới ch&acirc;n thần quốc, t&acirc;m t&igrave;nh hơi c&oacute; vẻ phức tạp.<br />\r\n<br />\r\nTh&acirc;n v&igrave; một c&aacute;i Địa Cầu phổ th&ocirc;ng người trẻ tuổi, Ng&ocirc; Huy th&iacute;ch xem tiểu thuyết, chơi game, &quot;Chơi&quot; Anime, tự nhi&ecirc;n đ&atilde; từng từng c&oacute; xuy&ecirc;n qua dị thế giới ảo tưởng.<br />\r\n<br />\r\nCó th&ecirc;̉ Ng&ocirc; Huy vạn vạn kh&ocirc;ng nghĩ tới, hắn vậy m&agrave; lại đạt được một c&aacute;i thần quốc, trở th&agrave;nh trong truyền thuyết &quot;Quang Minh Thần&quot; .<br />\r\n<br />\r\nTại vừa xuy&ecirc;n qua l&uacute;c, Ng&ocirc; Huy đương nhi&ecirc;n l&agrave; cực kỳ hưng phấn, bởi v&igrave; th&acirc;n l&agrave; rất nhiều huyền huyễn tiểu thuyết b&ecirc;n trong tr&ugrave;m phản diện &quot;Quang Minh Thần&quot;, thực lực phi thường cường đại, thần lực kh&ocirc;ng g&igrave; l&agrave;m kh&ocirc;ng được.<br />\r\n<br />\r\nDưới trướng thống trị nhiều c&aacute;i vị diện, c&oacute; được chục triệu thi&ecirc;n sứ đại qu&acirc;n, nh&igrave;n ai kh&ocirc;ng vừa mắt, liền hướng chết đ&aacute;nh.<br />\r\n<br />\r\nM&agrave; hắn Ng&ocirc; Huy, vĩ đại Quang Minh Thần hiện đang ở thần quốc, cũng nhất định phải l&agrave; l&agrave; một t&ograve;a kh&iacute; thế rộng r&atilde;i, v&agrave;ng son lộng lẫy thần điện, cao qu&yacute; xinh đẹp thi&ecirc;n sứ bay lượn ở giữa, trong kh&ocirc;ng kh&iacute; c&oacute; phi&ecirc;u miểu th&aacute;nh ca quanh quẩn.<br />\r\n<br />\r\nC&ograve;n c&oacute; ở đằng kia thần quốc trong hậu cung, c&oacute; thể thu thập ng&agrave;n vạn c&aacute;c tộc mỹ nữ, c&aacute;i g&igrave; xinh đẹp, th&aacute;nh khiết, cao qu&yacute;, l&atilde;nh diễm, t&agrave;i tr&iacute;, thuần manh loại h&igrave;nh hết thảy kh&ocirc;ng thể thiếu.<br />\r\n<br />\r\nT&igrave;nh cảnh n&agrave;y, Ng&ocirc; Huy chỉ l&agrave; suy nghĩ một ch&uacute;t, liền thoải m&aacute;i kh&ocirc;ng muốn kh&ocirc;ng muốn.<br />\r\n<br />\r\nĐ&aacute;ng tiếc!<br />\r\n<br />\r\nTưởng tượng l&agrave; đầy đặn, hiện thực lại l&agrave; xương cảm gi&aacute;c.<br />\r\n<br />\r\nNg&ocirc; Huy kế thừa tới Quang Minh Thần quốc, kh&ocirc;ng chỉ c&oacute; kh&ocirc;ng c&oacute; hắn trong tưởng tượng kh&iacute; thế rộng r&atilde;i, ngược lại l&agrave; một mảnh đầy rẫy thương di r&aacute;ch n&aacute;t.<br />\r\n<br />\r\nThần quốc quảng trường trung ương to&agrave; kia vốn n&ecirc;n thần th&aacute;nh uy nghi&ecirc;m, chừng mấy chục tầng lầu cao tượng th&acirc;̀n Quang Minh đ&atilde; g&atilde;y th&agrave;nh hai đoạn, vi&ecirc;n kia t&ocirc;n qu&yacute; đầu l&acirc;u, cũng bị ch&ocirc;n ở một đống gạch ng&oacute;i vụn ở giữa, rơi đầy bụi trần.<br />\r\n<br />\r\nQuảng trường hậu phương, vốn n&ecirc;n nguy nga tr&aacute;ng lệ Quang Minh Thần điện cũng đ&atilde; biến th&agrave;nh một chỗ cặn b&atilde;, duy c&oacute; linh tinh một hai c&acirc;y cột trụ h&agrave;nh lang, ki&ecirc;n cường đứng ngạo nghễ tại phế t&iacute;ch b&ecirc;n trong, lờ mờ khả biện đ&atilde; từng kh&iacute; ph&aacute;ch c&ugrave;ng trang nghi&ecirc;m.<br />\r\n<br />\r\nC&aacute;i n&agrave;y lụi bại thần quốc, tựa như l&agrave; đ&atilde; từng huy ho&agrave;ng Vi&ecirc;n Minh Vi&ecirc;n bị li&ecirc;n qu&acirc;n t&aacute;m nước đ&aacute;nh cướp về sau, một mồi lửa thi&ecirc;u hủy sau th&ecirc; lương thảm liệt.<br />\r\n<br />\r\nC&agrave;ng l&agrave;m Ng&ocirc; Huy nội t&acirc;m run rẩy ch&iacute;nh l&agrave;.<br />\r\n<br />\r\nỞ v&agrave;o thần quốc ph&iacute;a t&acirc;y bắc, c&oacute; ba đạo cự đại x&eacute; r&aacute;ch khe ngang qua tại kh&ocirc;ng gian tinh b&iacute;ch b&ecirc;n tr&ecirc;n, tựa như l&agrave; bị một loại n&agrave;o đ&oacute; cường đại cự th&uacute; d&ugrave;ng lợi trảo vạch ph&aacute;, vết c&agrave;o trong c&aacute;i khe hiện ra quỷ quyệt hắc quang, đạo đạo vết rạn đang thần quốc kh&ocirc;ng gian tinh b&iacute;ch b&ecirc;n tr&ecirc;n chậm r&atilde;i lan tr&agrave;n.<br />\r\n<br />\r\nTheo thời gian tr&ocirc;i qua, vết r&aacute;ch phạm vi c&agrave;ng ng&agrave;y c&agrave;ng rộng, cũng kh&ocirc;ng biết lúc nào, liền sẽ che k&iacute;n to&agrave;n bộ bầu trời. Một khi thần quốc kh&ocirc;ng gian tinh b&iacute;ch băng liệt, v&ocirc; tận hư kh&ocirc;ng b&ecirc;n trong đen năng lượng tối liền sẽ giống như hồng thủy khuynh tiết m&agrave; xuống.<br />\r\n<br />\r\nĐến l&uacute;c đ&oacute; Ng&ocirc; Huy c&aacute;i n&agrave;y &quot;Vĩ đại&quot; Quang Minh Thần kết cục, c&oacute; thể nghĩ.<br />\r\n<br />\r\n&quot;Ai ~&quot; Ng&ocirc; Huy lạnh run, nội t&acirc;m thật s&acirc;u thở d&agrave;i một tiếng ấm ức.<br />\r\n<br />\r\nHắn cảm gi&aacute;c tr&ecirc;n thế giới n&agrave;y, đại kh&aacute;i kh&ocirc;ng c&oacute; c&aacute;i n&agrave;o thần linh sẽ so với hắn c&agrave;ng bi thảm hơn.<br />\r\n<br />\r\nThần quốc, thần quốc kia l&agrave; ph&aacute;.<br />\r\n<br />\r\nThi&ecirc;n sứ, mỹ nh&acirc;n, l&agrave; hết thảy kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nThậm ch&iacute;, hắn liền cỗ ra d&aacute;ng nhục th&acirc;n đều kh&ocirc;ng c&oacute;, chỉ c&oacute; một sợi t&agrave;n tạ kh&ocirc;ng chịu nổi linh hồn thể, giống như l&agrave; gi&oacute; thổi qua liền sẽ bị thổi tan.<br />\r\n<br />\r\nM&agrave; lại bởi v&igrave; l&agrave; hắn sinh tồn h&igrave;nh th&aacute;i, vẻn vẹn l&agrave; linh hồn thể quan hệ. Hắn hiện tại cũng kh&ocirc;ng cần đi bộ, đi chỗ n&agrave;o đều l&agrave; d&ugrave;ng phi&ecirc;u.<br />\r\n<br />\r\nPhi&ecirc;u! ?<br />\r\n<br />\r\nNgươi có th&ecirc;̉ tưởng tượng sao? Hắn một c&aacute;i đường đường Quang Minh Thần, thế m&agrave; lẫn v&agrave;o c&ugrave;ng c&aacute;i quỷ, cả ng&agrave;y bay tới bay lui, liền người đều kh&ocirc;ng bằng.<br />\r\n<br />\r\nĐược rồi, kh&ocirc;ng n&oacute;i. N&oacute;i nhiều đều l&agrave; nước mắt.<br />\r\n<br />\r\nNg&ocirc; Huy nhịn kh&ocirc;ng được lại thở d&agrave;i.<br />\r\n<br />\r\nBỏ mặc linh hồn thể tiếp tục v&ocirc; &yacute; thức tại to&agrave;n bộ thần quốc b&ecirc;n trong chậm r&atilde;i phi&ecirc;u đ&atilde;ng, hắn t&acirc;m niệm vừa động, khởi động thần c&aacute;ch.<br />\r\n<br />\r\n&quot;Xem x&eacute;t thần quốc tin tức.&quot;<br />\r\n<br />\r\nTrong nh&aacute;y mắt, li&ecirc;n tiếp tin tức liền xuất hiện ở &yacute; thức của hắn b&ecirc;n trong.<br />\r\n<br />\r\nThần danh: Quang Minh Thần (cảnh c&aacute;o: Cực độ suy sụp, t&ugrave;y thời chết)<br />\r\n<br />\r\nThần lực: 0(cảnh c&aacute;o: Thần lực cực độ thiếu thốn, xin mau sớm bổ sung)<br />\r\n<br />\r\nThi&ecirc;n sứ: 0(cảnh c&aacute;o: Thi&ecirc;n sứ cực độ thiếu thốn, xin mau sớm bổ sung)<br />\r\n<br />\r\nThi&ecirc;n Đường Chi M&ocirc;n: 1(cảnh c&aacute;o: Bản Thi&ecirc;n Đường Chi M&ocirc;n đang gia tốc sụp đổ, xin mau sớm giữ g&igrave;n)<br />\r\n<br />\r\nT&iacute;n ngưỡng chi lực: 999(cảnh c&aacute;o: T&iacute;n ngưỡng chi lực cực độ thiếu thốn, xin mau sớm bổ sung)<br />\r\n<br />\r\nGiáo hoàng: 1(cảnh c&aacute;o: Trạng th&aacute;i dị thường, xin mau sớm điều động thi&ecirc;n sứ điều tra)<br />\r\n<br />\r\nT&iacute;n đồ: 316(cảnh c&aacute;o: T&iacute;n đồ cực độ thiếu thốn, xin mau sớm bổ sung)<br />\r\n<br />\r\nVị diện. . .<br />\r\n<br />\r\nNhững tin tức n&agrave;y c&oacute; kh&ocirc;ng &iacute;t, nhưng mỗi một c&aacute;i tin tức đều ở màu đỏ chót cảnh c&aacute;o b&ecirc;n trong, d&ugrave; chỉ l&agrave; nh&igrave;n liếc mắt, đều c&oacute; loại nh&igrave;n thấy m&agrave; giật m&igrave;nh cảm gi&aacute;c.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nBất qu&aacute;, xuy&ecirc;n qua th&agrave;nh Quang Minh Thần gần một năm Ng&ocirc; Huy, đối với c&aacute;i n&agrave;y đ&atilde; tập m&atilde;i th&agrave;nh th&oacute;i quen, căn bản kh&ocirc;ng nhiều lắm phản ứng.<br />\r\n<br />\r\n&Yacute; thức của hắn từ c&aacute;i kia một chuyến lại một chuyến tin tức b&ecirc;n tr&ecirc;n xẹt qua, khi ch&uacute; &yacute; tới trong đ&oacute; t&iacute;n đồ số lượng l&uacute;c, t&acirc;m bỗng dưng trầm xuống.<br />\r\n<br />\r\n&quot;Xem x&eacute;t t&iacute;n đồ tường t&igrave;nh.&quot;<br />\r\n<br />\r\nHắn bỗng nhi&ecirc;n mở miệng, trong nh&aacute;y mắt, thuộc về t&iacute;n đồ c&aacute;i kia một chuyến tin tức đơn độc đột hiển ra, sau đ&oacute; trong chớp mắt biến h&oacute;a th&agrave;nh một đoạn mới nội dung tin tức.<br />\r\n<br />\r\nTh&aacute;nh linh: 0<br />\r\n<br />\r\nTh&aacute;nh đồ: 0<br />\r\n<br />\r\nCuồng t&iacute;n đồ: 0<br />\r\n<br />\r\nKiền t&iacute;n đồ: 0<br />\r\n<br />\r\nThật t&iacute;n đồ: 0<br />\r\n<br />\r\nCạn t&iacute;n đồ: 316<br />\r\n<br />\r\nCạn t&iacute;n đồ thế m&agrave; đ&atilde; chỉ c&ograve;n lại 316 c&aacute;i!<br />\r\n<br />\r\nNg&ocirc; Huy nhớ kỹ hắn h&ocirc;m qua xem x&eacute;t t&iacute;n đồ l&uacute;c, c&ograve;n c&oacute; 323 c&aacute;i cạn t&iacute;n đồ. Có th&ecirc;̉ l&uacute;c n&agrave;y mới qua một ng&agrave;y, thế m&agrave; liền sụt giảm 7 c&aacute;i!<br />\r\n<br />\r\nHắn nh&iacute;u nh&iacute;u m&agrave;y kh&ocirc;ng tồn tại l&ocirc;ng m&agrave;y, t&acirc;m niệm vừa động, to&agrave;n bộ tinh thần linh hồn thể nh&aacute;y mắt tung bay m&agrave; l&ecirc;n, phi&ecirc;u lạc đến trong phế t&iacute;ch một c&acirc;y trắng tinh cột đ&aacute; b&ecirc;n cạnh.<br />\r\n<br />\r\nC&aacute;i n&agrave;y c&acirc;y cột đ&aacute; ở v&agrave;o thần điện ngay ph&iacute;a trước, ch&iacute;nh l&agrave; c&ograve;n s&oacute;t lại cột trụ h&agrave;nh lang b&ecirc;n trong cao nhất một c&acirc;y, cũng l&agrave; duy nhất bảo tồn tương đối ho&agrave;n chỉnh một c&acirc;y.<br />\r\n<br />\r\nTừ tr&ecirc;n hướng xuống, c&aacute;i n&agrave;y c&acirc;y cột đ&aacute; b&ecirc;n tr&ecirc;n l&iacute;t nha l&iacute;t nh&iacute;t ghi ch&eacute;p một nh&oacute;m lớn số lượng, ch&iacute;nh l&agrave; từ hắn tiếp nhận Quang Minh Thần đến nay, mỗi ng&agrave;y t&iacute;n đồ số lượng biến h&oacute;a.<br />\r\n<br />\r\nSớm nhất một bộ số liệu, l&agrave; gần một năm trước hắn vừa kế thừa thần quốc ng&agrave;y ấy.<br />\r\n<br />\r\nKhi đ&oacute;, hắn c&ograve;n c&oacute; 1 c&aacute;i cuồng t&iacute;n đồ, 708 c&aacute;i cạn t&iacute;n đồ. Nhưng l&agrave; b&acirc;y giờ, hắn chỉ c&ograve;n lại c&oacute; 316 c&aacute;i cạn t&iacute;n đồ.<br />\r\n<br />\r\nNg&ocirc; Huy d&ugrave;ng linh hồn chi lực cuốn l&ecirc;n một khối b&eacute;n nhọn tảng đ&aacute;, đem 316 số liệu n&agrave;y điền tại dòng cu&ocirc;́i cùng, sau đ&oacute; từ tr&ecirc;n xuống dưới, đem tất cả số lượng tế nh&igrave;n kỹ một lần.<br />\r\n<br />\r\n&quot;Ai, c&ograve;n lại thời gian kh&ocirc;ng nhiều lắm.&quot;<br />\r\n<br />\r\nTiện tay đem tảng đ&aacute; n&eacute;m đi, Ng&ocirc; Huy l&acirc;̉m b&acirc;̉m một tiếng, t&acirc;m t&igrave;nh c&agrave;ng th&ecirc;m hỏng b&eacute;t.<br />\r\n<br />\r\nT&iacute;n đồ của hắn số lượng kỳ thật một mực tại biến, nhưng ngay từ đầu, loại n&agrave;y giảm bớt tương đối chậm chạp, thậm ch&iacute; ngẫu nhi&ecirc;n sẽ c&ograve;n gia tăng mấy c&aacute;i.<br />\r\n<br />\r\nCó th&ecirc;̉ nửa năm trước, hắn duy nhất c&aacute;i kia cuồng t&iacute;n đồ t&iacute;n ngưỡng th&ocirc;ng đạo bỗng nhi&ecirc;n sụp đổ, từ đ&oacute; về sau, cạn t&iacute;n đồ số lượng liền bắt đầu nhanh ch&oacute;ng giảm bớt, đến gần nhất, c&agrave;ng l&agrave; cơ hồ mỗi ng&agrave;y đều muốn thiếu đi mấy c&aacute;i.<br />\r\n<br />\r\nChiếu c&aacute;i n&agrave;y xu thế xuống dưới, kh&ocirc;ng bao l&acirc;u, t&iacute;n đồ của hắn số lượng liền lại biến th&agrave;nh 0.<br />\r\n<br />\r\nNghĩ đến đ&acirc;y kết quả, Ng&ocirc; Huy t&acirc;m liền thẳng ch&igrave;m xuống dưới.<br />\r\n<br />\r\nHắn tại kế thừa Quang Minh Thần vị thời điểm, đồng thời cũng kế thừa đến m&ocirc;̣t chút vụn vặt tri thức. Hắn biết, thần linh lực lượng bắt nguồn từ thần lực.<br />\r\n<br />\r\nThần lực l&agrave; một loại cường đại m&agrave; vĩ ngạn lực lượng, c&oacute; thể d&ugrave;ng đến tạo n&ecirc;n nhục th&acirc;n, c&oacute; thể d&ugrave;ng tới sửa bổ thần quốc, c&oacute; thể d&ugrave;ng đến s&aacute;ng tạo thi&ecirc;n sứ, cũng c&oacute; thể d&ugrave;ng đi mở mang Thi&ecirc;n Đường Chi M&ocirc;n, c&oacute; thể d&ugrave;ng đến ch&uacute;c ph&uacute;c t&iacute;n đồ. . .<br />\r\n<br />\r\nN&oacute; kh&ocirc;ng g&igrave; l&agrave;m kh&ocirc;ng được, thậm ch&iacute; có th&ecirc;̉ s&aacute;ng tạo một c&aacute;i thế giới.<br />\r\n<br />\r\nLấy thần quốc trước mắt loại n&agrave;y l&agrave;m kh&ocirc;ng tốt lúc nào liền sẽ triệt để vỡ vụn t&igrave;nh huống, muốn cải biến cục diện, ngăn chặn thần quốc dần dần vỡ vụn, chỉ c&oacute; dựa v&agrave;o thần lực.<br />\r\n<br />\r\nNgưng tụ thần lực, c&acirc;̀n ti&ecirc;u hao t&iacute;n ngưỡng chi lực. M&agrave; t&iacute;n đồ, ch&iacute;nh l&agrave; t&iacute;n ngưỡng chi lực đầu nguồn.<br />\r\n<br />\r\nMột khi t&iacute;n đồ số lượng biến th&agrave;nh 0, hắn liền kh&ocirc;ng c&ograve;n c&oacute; t&iacute;n ngưỡng chi lực nơi ph&aacute;t ra, cũng lại kh&ocirc;ng c&ograve;n ngưng tụ thần lực khả năng.<br />\r\n<br />\r\nNếu quả như thật xuất hiện loại t&igrave;nh huống n&agrave;y, vậy hắn liền thật rốt cuộc l&agrave;m kh&ocirc;ng l&agrave; c&aacute;i g&igrave;. Chỉ c&oacute; thể trơ mắt nh&igrave;n tr&ecirc;n đỉnh đầu c&aacute;i kia đạo vết c&agrave;o c&agrave;ng l&uacute;c c&agrave;ng lớn, trơ mắt nh&igrave;n thần quốc tường ngo&agrave;i b&ecirc;n tr&ecirc;n vết nứt c&agrave;ng ng&agrave;y c&agrave;ng lớn, cuối c&ugrave;ng, trong tương lai một ng&agrave;y n&agrave;o đ&oacute;, hắn liền sẽ c&ugrave;ng to&agrave; n&agrave;y thần quốc c&ugrave;ng một chỗ t&aacute;ng th&acirc;n ở trong hư kh&ocirc;ng.<br />\r\n<br />\r\nTử vong c&oacute; lẽ đ&aacute;ng sợ, nhưng trơ mắt nh&igrave;n tử vong một ch&uacute;t x&iacute;u tới gần, lại c&aacute;i g&igrave; đều kh&ocirc;ng l&agrave;m được, so tử vong c&agrave;ng đ&aacute;ng sợ.<br />\r\n<br />\r\nChỉ l&agrave; loại kia từng bước tử vong từng bước tới gần &aacute;p lực l&agrave; c&oacute; thể đem người bức đi&ecirc;n.<br />\r\n<br />\r\nNg&ocirc; Huy chỉ l&agrave; suy nghĩ một ch&uacute;t loại t&igrave;nh huống n&agrave;y đều cảm gi&aacute;c sợ nổi da g&agrave;, tuyệt đối kh&ocirc;ng hi vọng n&oacute; biến th&agrave;nh sự thật.<br />\r\n<br />\r\nThần lực!<br />\r\n<br />\r\nNhất định muốn ngưng tụ thần lực!<br />\r\n<br />\r\nCăn cứ hắn kế thừa Thần vị đồng thời kế thừa tới tin tức, chỉ cần tích lũy đủ 1000 điểm t&iacute;n ngưỡng chi lực, liền c&oacute; thể ngưng tụ thần lực.<br />\r\n<br />\r\nHắn hiện tại c&oacute; 9 99 điểm t&iacute;n ngưỡng chi lực, chỉ k&eacute;m 1 điểm liền c&oacute; thể ngưng tụ thần lực.<br />\r\n<br />\r\nChỉ k&eacute;m cuối c&ugrave;ng 1 điểm!<br />\r\n<br />\r\nNg&ocirc; Huy quyết định, từ giờ trở đi, hắn liền thời thời khắc khắc nh&igrave;n chằm chằm t&iacute;n ngưỡng chi lực c&ugrave;ng t&iacute;n đồ số liệu, d&ugrave; sao hắn hiện tại l&agrave; linh hồn thể, kh&ocirc;ng cần ăn uống ngủ nghỉ, coi như một ng&agrave;y hai mươi bốn giờ nh&igrave;n chằm chằm số liệu cũng kh&ocirc;ng c&oacute; vấn đề g&igrave;.<br />\r\n<br />\r\nT&ugrave;y &yacute; bay tới một nơi dừng lại, hắn nh&igrave;n chằm chằm trong đầu số liệu, bắt đầu d&agrave;i dằng dặc m&agrave; d&agrave;y v&ograve; chờ đợi.<br />\r\n<br />\r\nChẳng biết chẳng hay, một c&aacute;i giờ tr&ocirc;i qua, hai giờ tr&ocirc;i qua. . .<br />\r\n<br />\r\nNửa ng&agrave;y về sau, cạn t&iacute;n đồ số lượng từ 316 hạ xuống 313, t&iacute;n ngưỡng chi lực nhưng kh&ocirc;ng c&oacute; trướng.<br />\r\n<br />\r\nNg&agrave;y thứ hai, cạn t&iacute;n đồ số lượng từ 313 hạ xuống 309, t&iacute;n ngưỡng chi lực vẫn như cũ kh&ocirc;ng c&oacute; trướng.<br />\r\n<br />\r\nNg&agrave;y thứ ba, cạn t&iacute;n đồ số lượng từ 309 hạ xuống 299, t&iacute;n ngưỡng chi lực vẫn như cũ kh&ocirc;ng c&oacute; trướng.<br />\r\n<br />\r\nNg&agrave;y thứ tư, cạn t&iacute;n đồ số lượng từ 299 bạo hạ xuống 286, trọn vẹn thiếu đi 13 c&aacute;i, t&iacute;n ngưỡng chi lực vẫn như cũ kh&ocirc;ng c&oacute; trướng.<br />\r\n<br />\r\nTheo thời gian tr&ocirc;i qua, Ng&ocirc; Huy t&acirc;m t&igrave;nh c&agrave;ng th&ecirc;m vội v&agrave;ng, cũng c&agrave;ng th&ecirc;m n&ocirc;n n&oacute;ng. Mỗi một lần, khi t&iacute;n đồ số lượng rơi xuống một c&aacute;i thời điểm, hắn t&acirc;m đều giống như bị người kho&eacute;t một miếng thịt đồng dạng, đau nhỏ m&aacute;u.<br />\r\n<br />\r\nTrong l&uacute;c bất tri bất gi&aacute;c, cả người hắn. . . A kh&ocirc;ng, to&agrave;n bộ thần đều l&acirc;m v&agrave;o thấp thỏm b&ecirc;n trong.<br />\r\n<br />\r\nNgay tại loại n&agrave;y d&agrave;y v&ograve; thời gian b&ecirc;n trong, hi vọng &aacute;nh rạng đ&ocirc;ng gi&aacute;ng l&acirc;m.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; dấu hiệu n&agrave;o, t&iacute;n ngưỡng chi lực ph&iacute;a sau số lượng bỗng nhi&ecirc;n từ 9 99 điểm nhảy tới 1000 điểm!<br />\r\n<br />\r\nỞ v&agrave;o chết lặng b&ecirc;n trong Ng&ocirc; Huy, cũng đi theo bỗng nhi&ecirc;n &quot;H&ocirc; hấp&quot; tr&igrave; trệ.<br />\r\n<br />\r\n1000 điểm.<br />\r\n<br />\r\nHắn t&iacute;n ngưỡng chi lực cuối c&ugrave;ng đ&atilde; tăng tới 1000 điểm!<br />\r\n<br />\r\nTrong l&ograve;ng hắn một trận mừng rỡ, cơ hồ kh&ocirc;ng d&aacute;m tin tưởng &yacute; thức của m&igrave;nh, nhịn kh&ocirc;ng được lại một lần nữa x&aacute;c nhận nhiều lần mới rốt cục x&aacute;c thực tin, hắn t&iacute;n ngưỡng chi lực, thật đ&atilde; tăng tới 1000 điểm.<br />\r\n<br />\r\nHắn cuối c&ugrave;ng c&oacute; thể ngưng tụ thần lực!<br />\r\n<br />\r\nLấy lại b&igrave;nh tĩnh, Ng&ocirc; Huy thứ nhất thời gian đối với thần c&aacute;ch hạ lệnh, ngữ kh&iacute; v&ocirc; c&ugrave;ng trịnh trọng, cũng v&ocirc; c&ugrave;ng phấn chấn: &quot;Thần c&aacute;ch, ngưng tụ thần lực!&quot;<br />\r\n<br />\r\nThoại &acirc;m rơi xuống, chuyện kỳ diệu ph&aacute;t sinh.<br />\r\n<br />\r\nMột đạo bạch quang tự hắn s&acirc;u trong linh hồn bắn ra.<br />\r\n<br />\r\nMột gi&acirc;y sau, hắn liền cảm gi&aacute;c được một cỗ uy nghi&ecirc;m m&ecirc;nh m&ocirc;ng lực lượng dung nhập trong linh hồn hắn, để hắn to&agrave;n th&acirc;n chấn động, cảm gi&aacute;c ch&iacute;nh m&igrave;nh phảng phất một nh&aacute;y mắt cường đại v&ocirc; số lần!<br />\r\n<br />\r\nLoại cảm gi&aacute;c n&agrave;y, thật giống như hắn kh&ocirc;ng g&igrave; l&agrave;m kh&ocirc;ng được, một c&aacute;i &yacute; niệm trong đầu liền c&oacute; thể di sơn đảo hải, hủy thi&ecirc;n diệt địa.<br />\r\n<br />\r\nNg&ocirc; Huy nhịn kh&ocirc;ng được một tiếng reo h&ograve;, cả người nh&aacute;y mắt đằng kh&ocirc;ng m&agrave; l&ecirc;n, hưng phấn v&ograve;ng quanh thần quốc chuyển tầm v&agrave;i v&ograve;ng.<br />\r\n<br />\r\nThật l&acirc;u c&ocirc;ng phu, hắn mới từ trong hưng phấn thong thả lại sức. T&acirc;m niệm vừa động, lần nữa điều ra m&igrave;nh tin tức: &quot;Biểu hiện thần lực số liệu.&quot;<br />\r\n<br />\r\nTrong chốc l&aacute;t, thần lực số liệu liền xuất hiện ở &yacute; thức của hắn b&ecirc;n trong.<br />\r\n<br />\r\nThần lực: 1 điểm (cảnh c&aacute;o: Thần lực cực độ thiếu thốn, xin mau sớm bổ sung).<br />\r\n<br />\r\nNg&ocirc; Huy sững sờ, cả người giống như l&agrave; bị người r&oacute;t chậu nước lạnh đồng dạng, r&oacute;t lạnh thấu tim.<br />\r\n<br />\r\nTrọn vẹn 1000 điểm t&iacute;n ngưỡng chi lực, thế m&agrave; vẻn vẹn tăng l&ecirc;n 1 điểm thần lực? Hắn hiện tại nếu c&oacute; th&acirc;n thể, kh&ocirc;ng chừng sẽ một ngụm m&aacute;u phun ra.<br />\r\n<br />\r\n1 điểm thần lực c&oacute; thể l&agrave;m g&igrave;?<br />\r\n<br />\r\nTrị liệu thần hồn? Thần hồn đ&atilde; suy sụp đến loại tr&igrave;nh độ n&agrave;y, chỉ l&agrave; 1 điểm thần lực căn bản ch&iacute;nh l&agrave; c&oacute; cũng được m&agrave; kh&ocirc;ng c&oacute; cũng kh&ocirc;ng sao.<br />\r\n<br />\r\nChế tạo thi&ecirc;n sứ? Chế tạo một c&aacute;i cấp thấp nhất thi&ecirc;n sứ, c&acirc;̀n trọn vẹn 100 điểm thần lực, c&ograve;n ngo&agrave;i định mức c&acirc;̀n một c&aacute;i th&aacute;nh linh. Hắn đ&atilde; kh&ocirc;ng c&oacute; th&aacute;nh linh cũng kh&ocirc;ng c&oacute; c&oacute; thần lực, vẫn l&agrave; th&ocirc;i đi.<br />\r\n<br />\r\nChế tạo một th&acirc;n thể cho m&igrave;nh sử dụng? 1 điểm thần lực ngược lại l&agrave; miễn cưỡng đủ rồi, có th&ecirc;̉ chế tạo ra th&acirc;n thể, cũng đo&aacute;n chừng chỉ l&agrave; tư chất b&igrave;nh thường người b&igrave;nh thường th&acirc;n thể, căn bản kh&ocirc;ng nhiều lắm d&ugrave;ng.<br />\r\n<br />\r\nChữa trị thần quốc kh&ocirc;ng gian tinh b&iacute;ch b&ecirc;n tr&ecirc;n vết c&agrave;o khe?<br />\r\n<br />\r\nĐừng l&agrave;m rộn, 1 điểm thần lực có th&ecirc;̉ chậm lại một ch&uacute;t khe sụp đổ tốc độ cũng kh&ocirc;ng tệ rồi.<br />\r\n<br />\r\nSuy bại thần linh, lụi bại thần quốc, c&oacute; v&ocirc; số cần d&ugrave;ng đến thần lực địa phương, 1 điểm thần lực liền hạt c&aacute;t trong sa mạc cũng kh&ocirc;ng bằng.<br />\r\n<br />\r\nNg&ocirc; Huy trong l&ograve;ng c&oacute; ch&uacute;t sụp đổ, nhưng lại kh&ocirc;ng có th&ecirc;̉ l&agrave;m sao.<br />\r\n<br />\r\nDuy nhất may mắn ch&iacute;nh l&agrave;, coi như chỉ c&oacute; 1 điểm thần lực, đ&oacute; cũng l&agrave; đường đường chính chính thần lực, c&oacute; d&ugrave; sao cũng so kh&ocirc;ng c&oacute; tốt.<br />\r\n<br />\r\nTrong l&ograve;ng của hắn r&otilde; r&agrave;ng , dựa theo cạn t&iacute;n đồ số lượng kh&ocirc;ng ngừng hạ xuống tốc độ, muốn lần nữa tích lũy đ&acirc;̀y 1000 điểm t&iacute;n ngưỡng chi lực kh&ocirc;ng kh&aacute;c người si n&oacute;i mộng. C&aacute;i n&agrave;y 1 điểm thần lực, chẳng những l&agrave; hắn hiện tại chỉ c&oacute; thần lực, cũng l&agrave; hắn cơ hội cuối c&ugrave;ng.<br />\r\n<br />\r\nL&agrave; theo thần quốc vỡ vụn m&agrave; triệt để ti&ecirc;u vong, vẫn l&agrave; như vậy ph&aacute; cục, đạp l&ecirc;n cẩm t&uacute; tiền đồ, bưng nh&igrave;n hắn d&ugrave;ng như thế n&agrave;o c&aacute;i n&agrave;y 1 điểm thần lực.<br />\r\n<br />\r\nNg&ocirc; Huy ngồi xổm ở c&acirc;y kia cao nhất tr&ecirc;n trụ đ&aacute; nghĩ đi nghĩ lại, cuối c&ugrave;ng vẫn l&agrave; cảm thấy, c&aacute;i n&agrave;y 1 điểm thần lực cần phải d&ugrave;ng tr&ecirc;n người t&iacute;n đồ.<br />\r\n<br />\r\nT&iacute;n đồ l&agrave; thần căn cơ. Chỉ c&oacute; để t&iacute;n đồ số lượng đ&igrave;nh chỉ giảm bớt, thậm ch&iacute; tr&aacute;i lại tăng l&ecirc;n, hắn mới c&oacute; cơ hội tiếp tục g&oacute;p nhặt thần lực.<br />\r\n<br />\r\nCăn cứ hắn kế thừa vụn vặt tri thức, c&ugrave;ng trước đ&oacute; suy đo&aacute;n diễn to&aacute;n, mỗi một c&aacute;i cạn t&iacute;n đồ ước chừng b&igrave;nh qu&acirc;n mỗi ng&agrave;y có th&ecirc;̉ cung cấp 0. 001 t&iacute;n ngưỡng chi lực. Kh&ocirc;ng sai, ch&iacute;nh l&agrave; 0. 001 như thế yếu ớt. Trải qua t&iacute;nh to&aacute;n, một c&aacute;i cạn t&iacute;n đồ c&acirc;̀n trọn vẹn ba năm khoảng chừng, mới c&oacute; thể cung cấp 1 điểm t&iacute;n ngưỡng chi lực.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nNếu như có th&ecirc;̉ bảo tr&igrave; lại hiện tại 286 c&aacute;i cạn t&iacute;n đồ lại kh&ocirc;ng giảm dần, ước chừng thời gian mười năm liền c&oacute; thể lần nữa tích lũy đ&acirc;̀y 1000 điểm t&iacute;n ngưỡng chi lực, một lần nữa ngưng tụ ra 1 điểm thần lực.<br />\r\n<br />\r\nMười năm rất d&agrave;i, có th&ecirc;̉ lại d&agrave;i, cũng chỉ c&oacute; hi vọng kh&ocirc;ng phải?<br />\r\n<br />\r\n&quot;Nhưng l&agrave; muốn l&agrave;m sao duy tr&igrave; t&iacute;n đồ, thậm ch&iacute; khuếch trương tăng t&iacute;n đồ đ&acirc;u?&quot;<br />\r\n<br />\r\nNg&ocirc; Huy c&oacute; ch&uacute;t đau đầu.<br />\r\n<br />\r\nKỳ thật truyền gi&aacute;o c&ugrave;ng mở rộng t&iacute;n đồ số lượng b&igrave;nh thường l&agrave; Giáo hoàng sự t&igrave;nh. Giáo hoàng l&agrave; thần linh &yacute; ch&iacute; ở nh&acirc;n gian đại biểu, thống so&aacute;i c&aacute;c Phương gi&aacute;o chủ mục sư, l&agrave; chuy&ecirc;n m&ocirc;n thay thần linh khuếch t&aacute;n t&iacute;n ngưỡng, duy tr&igrave; t&iacute;n ngưỡng người hầu.<br />\r\n<br />\r\nTh&acirc;n l&agrave; thần linh, chỉ cần thư thư phục phục hưởng thụ t&iacute;n ngưỡng chi lực cung phụng, ngẫu nhi&ecirc;n hiển lộ hiển lộ thần t&iacute;ch liền đi.<br />\r\n<br />\r\nCó th&ecirc;̉ b&acirc;y giờ, Ng&ocirc; Huy c&aacute;i kia Giáo hoàng kh&ocirc;ng biết xảy ra điều g&igrave; trạng huống dị thường, trong thời gian ngắn l&agrave; kh&ocirc;ng tr&ocirc;ng cậy được v&agrave;o c&aacute;i n&agrave;y một khối.<br />\r\n<br />\r\nHắn nhịn kh&ocirc;ng được thở d&agrave;i: &quot;Được rồi, vẫn l&agrave; dựa v&agrave;o ch&iacute;nh m&igrave;nh đi.&quot;<br />\r\n<br />\r\nKỳ thật muốn duy tr&igrave; t&iacute;n đồ số lượng cũng rất đơn giản, chỉ cần c&oacute; thể tại t&iacute;n đồ trước mặt triển lộ một ch&uacute;t thần t&iacute;ch, trợ gi&uacute;p bọn hắn vượt qua cửa ải kh&oacute;, liền đầy đủ duy tr&igrave; được t&iacute;n ngưỡng của bọn họ, thậm ch&iacute; c&oacute; khả năng đem bọn hắn ph&aacute;t triển th&agrave;nh thật t&iacute;n đồ, thậm ch&iacute; l&agrave; kiền t&iacute;n đồ.<br />\r\n<br />\r\nThật t&iacute;n đồ c&ugrave;ng cạn t&iacute;n đồ kh&ocirc;ng tầm thường, bọn hắn sẽ xuất ph&aacute;t từ nội t&acirc;m phế phủ t&iacute;n ngưỡng Quang Minh Thần, nội t&acirc;m thuần t&uacute;y, t&iacute;n ngưỡng cường đại. Một c&aacute;i thật t&iacute;n đồ, tối thiểu b&ugrave; đắp được mười c&aacute;i cạn t&iacute;n đồ, m&agrave; lại t&iacute;n ngưỡng th&ocirc;ng đạo tương đối vững chắc, sẽ kh&ocirc;ng động một ch&uacute;t lại sụp đổ.<br />\r\n<br />\r\nThật t&iacute;n đồ l&agrave; một vị thần linh tồn tại nền tảng, thật t&iacute;n đồ số lượng c&agrave;ng nhiều, thần linh liền c&agrave;ng cường đại.<br />\r\n<br />\r\nKiền t&iacute;n đồ liền c&agrave;ng th&ecirc;m tr&acirc;n qu&yacute;, bọn hắn đối với thần linh tin chắc kh&ocirc;ng thể nghi ngờ, th&agrave;nh k&iacute;nh trung th&agrave;nh, thời thời khắc khắc đem thần linh ghi nhớ trong l&ograve;ng, lấy thần linh chuẩn tắc nghi&ecirc;m ngặt ước th&uacute;c tự th&acirc;n h&agrave;nh vi. Đồng thời bọn hắn cũng l&agrave; t&iacute;n ngưỡng truyền b&aacute; người, th&ocirc;ng qua tự th&acirc;n dạy dỗ ảnh hưởng những người kh&aacute;c t&iacute;n ngưỡng cung phụng thần linh.<br />\r\n<br />\r\nMột c&aacute;i kiền t&iacute;n đồ c&oacute; khả năng sinh ra t&iacute;n ngưỡng chi lực, b&ugrave; đắp được một trăm c&aacute;i cạn t&iacute;n đồ. Mặc d&ugrave; kiền t&iacute;n đồ kh&ocirc;ng nhất định l&agrave; chuy&ecirc;n nghiệp giảng đạo mục sư, nhưng bọn hắn thường xuy&ecirc;n sẽ ph&aacute;t triển ra một ch&uacute;t t&iacute;n đồ tới.<br />\r\n<br />\r\nD&ugrave;ng người Địa Cầu đến n&oacute;i, kiền t&iacute;n đồ đều l&agrave; thuần thi&ecirc;n nhi&ecirc;n nước m&aacute;y.<br />\r\n<br />\r\nVề phần cuồng t&iacute;n đồ, tr&acirc;n qu&yacute; tr&igrave;nh độ c&agrave;ng tại kiền t&iacute;n đồ ph&iacute;a tr&ecirc;n, bọn hắn đối với thần t&iacute;n ngưỡng cuồng nhiệt m&agrave; hừng hực, ho&agrave;n to&agrave;n đem thần đặt ở ch&iacute; cao v&ocirc; thượng địa vị. Một c&aacute;i cuồng t&iacute;n đồ, mỗi ng&agrave;y có th&ecirc;̉ sinh ra t&iacute;n ngưỡng chi lực b&ugrave; đắp được m&ocirc;̣t ngàn c&aacute;i cạn t&iacute;n đồ.<br />\r\n<br />\r\nNghĩ tới cuồng t&iacute;n đồ, Ng&ocirc; Huy kh&ocirc;ng khỏi lại c&oacute; ch&uacute;t thổn thức.<br />\r\n<br />\r\nNhớ ng&agrave;y đ&oacute;, hắn cũng l&agrave; từng c&oacute; cuồng t&iacute;n đồ. Gần một năm trước, hắn vừa kế thừa thần quốc thời điểm, liền nắm giữ một c&aacute;i cuồng t&iacute;n đồ. Quang c&aacute;i kia cuồng t&iacute;n đồ ch&iacute;nh m&igrave;nh, mỗi ng&agrave;y liền cho Ng&ocirc; Huy cung cấp 1 điểm t&iacute;n ngưỡng chi lực.<br />\r\n<br />\r\nĐ&aacute;ng tiếc, từ khi nửa năm trước t&ecirc;n kia cuồng t&iacute;n đồ t&iacute;n ngưỡng th&ocirc;ng đạo kh&ocirc;ng hiểu sụp đổ về sau, hắn liền rốt cuộc kh&ocirc;ng c&oacute; hưởng thụ qua mỗi ng&agrave;y đều c&oacute; t&iacute;n ngưỡng chi lực nhập trướng kho&aacute;i cảm, dẫn đến về sau mỗi một điểm t&iacute;n ngưỡng chi lực t&iacute;ch lũy đều trở n&ecirc;n ph&aacute; lệ gian nan.<br />\r\n<br />\r\n&quot;Đ&aacute;ng tiếc a đ&aacute;ng tiếc, l&uacute;c ấy kh&ocirc;ng c&oacute; c&oacute; thần lực, v&ocirc; ph&aacute;p xử l&yacute; c&aacute;i kia cuồng t&iacute;n đồ c&acirc;̀u nguyện.&quot; Ng&ocirc; Huy tiếc hận kh&ocirc;ng th&ocirc;i, &quot;Nếu như ta l&uacute;c ấy c&oacute; thần lực, c&oacute; lẽ t&igrave;nh huống liền sẽ kh&ocirc;ng giống như b&acirc;y giờ.&quot;<br />\r\n<br />\r\nC&acirc;̀u nguyện, ch&iacute;nh l&agrave; t&iacute;n đồ hướng thần linh cầu xin gi&uacute;p đỡ. Thần linh c&oacute; thể lựa chọn gi&uacute;p hắn, hoặc l&agrave; kh&ocirc;ng để &yacute;. B&igrave;nh thường m&agrave; n&oacute;i, một c&aacute;i cao cao tại thượng cường đại thần linh, trừ phi t&acirc;m huyết d&acirc;ng tr&agrave;o, bằng kh&ocirc;ng b&igrave;nh thường sẽ rất &iacute;t đ&aacute;p ứng một c&aacute;i t&iacute;n đồ c&acirc;̀u nguyện.<br />\r\n<br />\r\nThần linh b&igrave;nh thường sẽ chỉ đ&aacute;p lại Giáo hoàng c&acirc;̀u nguyện, hoặc l&agrave; lớn quy m&ocirc; lớn diện t&iacute;ch đẳng cấp cao t&iacute;n đồ tập thể c&acirc;̀u nguyện.<br />\r\n<br />\r\nCó th&ecirc;̉ b&acirc;y giờ Ng&ocirc; Huy c&aacute;i n&agrave;y Quang Minh Thần đ&atilde; c&ugrave;ng đồ mạt lộ, căn bản kh&ocirc;ng c&oacute; chọn lựa chỗ trống.<br />\r\n<br />\r\n&quot;Cho d&ugrave; l&agrave; một c&aacute;i t&iacute;n đồ c&acirc;̀u nguyện cũng tốt, tranh thủ thời gian đến một c&aacute;i đi. . .&quot;<br />\r\n<br />\r\nNg&ocirc; Huy nh&igrave;n chằm chằm chỉ c&ograve;n lại 286 c&aacute;i cạn t&iacute;n đồ, trong l&ograve;ng ch&iacute;nh l&agrave; chờ mong, lại l&agrave; c&oacute; ch&uacute;t khẩn trương.<br />\r\n<br />\r\nThời gian một giờ, một giờ đi qua, cạn t&iacute;n đồ t&iacute;n ngưỡng th&ocirc;ng đạo như cũ tại kh&ocirc;ng ngừng sụp đổ, chẳng biết chẳng hay, cạn t&iacute;n đồ số lượng liền từ 286 ng&atilde; xuống280.<br />\r\n<br />\r\nNhưng m&agrave;, lại từ đầu đến cuối kh&ocirc;ng c&oacute; bất luận c&aacute;i g&igrave; cạn t&iacute;n đồ hướng Quang Minh Thần c&acirc;̀u nguyện.<br />\r\n<br />\r\nNếu như Ng&ocirc; Huy hiện tại nắm giữ th&acirc;n thể, hắn sợ l&agrave; đ&atilde; gấp đến độ sắc mặt nhợt nhạt, mồ h&ocirc;i lạnh chảy r&ograve;ng. Coi như kh&ocirc;ng c&oacute; c&oacute; th&acirc;n thể, hắn hiện tại cũng gấp được linh hồn bất ổn, nh&igrave;n c&agrave;ng th&ecirc;m suy nhược.<br />\r\n<br />\r\nTheo cạn t&iacute;n đồ số lượng giảm bớt, hi vọng, ch&iacute;nh đang nhanh ch&oacute;ng ma diệt.<br />\r\n<br />\r\nNgay tại l&uacute;c n&agrave;y, liền liền chờ đợi, đều l&agrave; một loại d&agrave;y v&ograve;. Ở đ&acirc;y loại d&agrave;y v&ograve; b&ecirc;n trong, thời gian phảng phất biến được v&ocirc; c&ugrave;ng d&agrave;i.<br />\r\n<br />\r\nThời gian cứ như vậy tr&ocirc;i qua v&agrave;i ng&agrave;y, Ng&ocirc; Huy trơ mắt nh&igrave;n cạn t&iacute;n đồ số lượng rơi ph&aacute; 100, biến th&agrave;nh 99, 89, hắn t&acirc;m cũng một ch&uacute;t x&iacute;u rơi v&agrave;o đ&aacute;y cốc.<br />\r\n<br />\r\nChẳng lẽ lại, hắn Ng&ocirc; Huy c&ograve;n kh&ocirc;ng c&oacute; hưởng thụ được khi một c&aacute;i thần linh niềm vui th&uacute;, liền phải bỏ mạng sao? Kh&ocirc;ng, đo&aacute;n chừng trong thời gian ngắn liền vẫn lạc đều l&agrave;m kh&ocirc;ng được, hắn sẽ chỉ bị l&acirc;u d&agrave;i v&acirc;y ở to&agrave; n&agrave;y c&ocirc; độc r&aacute;ch n&aacute;t thần quốc b&ecirc;n trong, một ch&uacute;t x&iacute;u nh&igrave;n xem ch&iacute;nh m&igrave;nh đi v&agrave;o tử vong.<br />\r\n<br />\r\nC&aacute;i n&agrave;y qu&aacute; kinh khủng ~<br />\r\n<br />\r\nNgay tại Ng&ocirc; Huy đ&atilde; l&acirc;m v&agrave;o trong tuyệt vọng thời điểm.<br />\r\n<br />\r\nBỗng dưng!<br />\r\n<br />\r\nTrong đ&oacute; một đầu c&ugrave;ng thần quốc kết nối yếu ớt t&iacute;n ngưỡng th&ocirc;ng đạo, c&oacute; ch&uacute;t rung động bắt đ&acirc;̀u chuy&ecirc;̉n đ&ocirc;̣ng.<br />\r\n<br />\r\nĐ&acirc;y, đ&acirc;y l&agrave; c&oacute; t&iacute;n đồ tại hướng hắn c&acirc;̀u nguyện dấu hiệu!<br />\r\n<br />\r\n&quot;Nhanh! Thần c&aacute;ch, lắng nghe t&iacute;n đồ c&acirc;̀u nguyện.&quot;<br />\r\n<br />\r\nNg&ocirc; Huy chỉ một tho&aacute;ng mừng rỡ, &quot;Cạn t&iacute;n đồ bảo bối, nhanh, n&oacute;i nhanh một ch&uacute;t ra nguyện vọng của ngươi. Bản Thần nhất định sẽ thỏa m&atilde;n ngươi ~ &quot;<br />\r\n<br />\r\n. . .</p>', '2021-11-30 20:53:40', 7, 1),
(244, 66, 'Ta Bảo Bối Tu Nữ', '2', '<p>Trong rừng rậm cổ thụ từng cục, vụn vặt khắp nơi tr&ecirc;n đất, chỉ c&oacute; một đầu vũng b&ugrave;n ruột d&ecirc; tiểu đạo uốn lượn lấy th&ocirc;ng hướng rừng rậm chỗ s&acirc;u. Lẻ tẻ &aacute;nh nắng xuy&ecirc;n thấu qua d&agrave;y đặc t&aacute;n c&acirc;y tầng vẩy xuống, xua t&aacute;n đi trong rừng c&acirc;y u &aacute;m, v&igrave; c&aacute;i n&agrave;y duy nhất tiểu đạo th&ecirc;m v&agrave;o một vệt s&aacute;ng sắc.<br />\r\n<br />\r\nGiờ ph&uacute;t n&agrave;y, tr&ecirc;n đường nhỏ, c&oacute; một c&aacute;i thiếu nữ &aacute;o trắng đang chật vật chạy nhanh.<br />\r\n<br />\r\nH&ocirc;m qua vừa hạ một trận mưa, vốn l&agrave; chật hẹp tiểu đạo biến đến mức dị thường vũng b&ugrave;n, n&agrave;ng chạy lảo đảo, một th&acirc;n đại biểu th&aacute;nh khiết m&agrave;u trắng tu nữ nuốt v&agrave;o d&iacute;nh đầy c&aacute;u bẩn, liền liền dưới ch&acirc;n gi&agrave;y cũng đ&atilde; chạy mất một con, nhưng như cũ kh&oacute; n&eacute;n c&aacute;i kia mỹ lệ tư th&aacute;i c&ugrave;ng một th&acirc;n tinh khiết kh&ocirc;ng một hạt bụi kh&iacute; tức.<br />\r\n<br />\r\nBởi v&igrave; tho&aacute;t lực, sắc mặt n&agrave;ng một mảnh nhợt nhạt, nhưng như cũ nắm thật chặt d&ugrave;ng trường li&ecirc;n đeo tr&ecirc;n cổ ng&acirc;n sắc gi&aacute; chữ thập, thanh &acirc;m ph&aacute;t run cầu nguyện: &quot;Nh&acirc;n từ m&agrave; vĩ đại Quang Minh Thần a, xin ng&agrave;i hạ xuống thần &acirc;n, cứu lấy ch&uacute;ng ta đi ~ &quot;<br />\r\n<br />\r\n&quot;Catherina, nhanh! Chạy mau!&quot; Một c&aacute;i chiến sĩ trẻ tuổi bỗng nhi&ecirc;n từ sau chui l&ecirc;n đến, l&ocirc;i k&eacute;o n&agrave;ng liền bay về ph&iacute;a trước chạy, nhợt nhạt thần sắc tr&ecirc;n mặt bi phẫn, &quot;C&aacute;i kia c&aacute;i rắm ch&oacute; Quang Minh Thần sớm liền treo, ngươi lại cầu nguyện c&oacute; &iacute;ch lợi g&igrave;?&quot;<br />\r\n<br />\r\nHắn nh&igrave;n qua bất qu&aacute; mười t&aacute;m mười ch&iacute;n tuổi, trong tay x&aacute;ch ngược lấy một thanh tinh cương trường kiếm, mặc tr&ecirc;n người chế t&aacute;c tinh lương gi&aacute;p da, lộ ra c&aacute;i kia kh&ocirc;i ng&ocirc; th&acirc;n h&igrave;nh, nh&igrave;n chiến lực kh&ocirc;ng tầm thường.<br />\r\n<br />\r\nĐ&aacute;ng tiếc, hắn sau lưng đ&atilde; tr&uacute;ng một mũi t&ecirc;n, mũi t&ecirc;n theo chạy run rẩy. M&agrave; bụng của hắn c&agrave;ng l&agrave; th&ocirc;ng suốt mở đầu lỗ hổng, vẻn vẹn tới kịp d&ugrave;ng bố quấn trở xuống, vết thương kh&ocirc;ng ngừng c&oacute; m&aacute;u tươi chảy ra, nh&igrave;n thấy m&agrave; giật m&igrave;nh.<br />\r\n<br />\r\nPh&iacute;a sau hai người, mấy c&aacute;i cầm trong tay trường thương hoặc cung t&ecirc;n binh sĩ ch&iacute;nh bước nhanh đuổi theo.<br />\r\n<br />\r\nNhững binh l&iacute;nh n&agrave;y mặc rắn chắc gi&aacute;p da, vũ kh&iacute; trong tay hiện ra h&agrave;n quang, nh&igrave;n về ph&iacute;a &aacute;nh mắt hai người tựa như l&agrave; đang nh&igrave;n d&ecirc; đợi l&agrave;m thịt, hiện ra dữ tợn hung quang.<br />\r\n<br />\r\nBọn hắn tại vũng b&ugrave;n tr&ecirc;n đường nhỏ bước đi như bay, kh&ocirc;ng l&acirc;u lắm liền c&agrave;ng đuổi c&agrave;ng gần.<br />\r\n<br />\r\n&quot;Vĩ đại Quang Minh Thần a, xin ng&agrave;i hạ xuống thần &acirc;n, cứu lấy ch&uacute;ng ta đi ~&quot; thiếu nữ &aacute;o trắng mắt trong l&ograve;ng tuyệt vọng, nhưng như cũ kh&ocirc;ng chịu hết hi vọng, thở h&agrave;o hển thấp giọng cầu nguyện.<br />\r\n<br />\r\nCho đến ng&agrave;y nay, n&agrave;ng kỳ thật đối với Quang Minh Thần phải chăng c&ograve;n sống s&oacute;t đ&atilde; kh&ocirc;ng &ocirc;m c&aacute;i g&igrave; hi vọng, chỉ l&agrave; b&acirc;y giờ c&ugrave;ng đồ mạt lộ, đ&acirc;y l&agrave; n&agrave;ng duy nhất c&oacute; thể bắt lấy một cọng cỏ cứu mạng.<br />\r\n<br />\r\nĐ&aacute;ng tiếc, c&ugrave;ng dĩ v&atilde;ng mỗi một lần đồng dạng, cầu nguyện của n&agrave;ng kh&ocirc;ng c&oacute; đạt được bất kỳ đ&aacute;p lại n&agrave;o.<br />\r\n<br />\r\nN&agrave;ng xanh thẳm đ&ocirc;i mắt b&ecirc;n trong xẹt qua một vệt tuyệt vọng.<br />\r\n<br />\r\nChẳng lẽ, Quang Minh Thần thật đ&atilde; vẫn lạc sao? Chẳng lẽ, bọn hắn h&ocirc;m nay thật ch&uacute; định phải chết ở chỗ n&agrave;y sao?<br />\r\n<br />\r\nBỗng dưng.<br />\r\n<br />\r\nCatherina l&ograve;ng b&agrave;n ch&acirc;n trượt đi, bỗng nhi&ecirc;n ng&atilde; c&aacute;i t&eacute; ng&atilde;.<br />\r\n<br />\r\nLần n&agrave;y bị ng&atilde; rất nặng, n&agrave;ng cả người đều nặng nề m&agrave; nện xuống đất, li&ecirc;n tiếp lăn hai v&ograve;ng mới đụng v&agrave;o b&ecirc;n cạnh r&ecirc;̃ c&acirc;y ngừng lại, đau đến n&agrave;ng to&agrave;n th&acirc;n run l&ecirc;n, liền đứng l&ecirc;n cũng kh&ocirc;ng nổi.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Nguy rồi!&quot;<br />\r\n<br />\r\nTuổi trẻ chiến sĩ nhất thời sắc mặt đại biến.<br />\r\n<br />\r\nHắn liền vội vươn tay giữ chặt Catherina, ph&iacute; sức m&agrave; đem n&agrave;ng dựng l&ecirc;n đến, muốn mang theo n&agrave;ng tiếp tục chạy.<br />\r\n<br />\r\nNhưng m&agrave;, cho d&ugrave; hắn phản ứng đ&atilde; rất nhanh, nhưng như cũ chậm một bước. Cứ như vậy thời gian một c&aacute;i chớp mắt, mấy người l&iacute;nh kia đ&atilde; nhanh ch&acirc;n đuổi theo, như lang như hổ m&agrave; đem bọn hắn v&acirc;y v&agrave;o giữa.<br />\r\n<br />\r\nBọn hắn đều l&agrave; lệ thuộc v&agrave;o nam tước vừa Dea ngươi lệ thuộc trực tiếp tư binh.<br />\r\n<br />\r\n&quot;Ha ha ha ~ c&ocirc; n&agrave;ng, ngươi kh&ocirc;ng phải rất c&oacute; thể chạy sao? Tiếp tục chạy a ~ &quot;<br />\r\n<br />\r\nCầm đầu r&acirc;u quai n&oacute;n ph&aacute;t ra một tiếng ** qu&aacute;i điệu cười to, dẫn theo c&aacute;n h&agrave;n quang b&oacute;ng lưỡng trường thương từng bước một hướng hai người tới gần. Thanh trường thương kia b&ecirc;n tr&ecirc;n, c&ograve;n d&iacute;nh lấy tinh hồng huyết thủy, đ&acirc;m trong l&ograve;ng người ph&aacute;t lạnh.<br />\r\n<br />\r\n&quot;C&uacute;t ngay!&quot;<br />\r\n<br />\r\nTuổi trẻ trong l&ograve;ng chiến sĩ xiết chặt, l&uacute;c n&agrave;y k&eacute;o lại Catherina đem n&agrave;ng hộ sau lưng , giơ kiếm trợn mắt, hướng c&aacute;i kia r&acirc;u quai n&oacute;n gầm nhẹ: &quot;Catherina l&agrave; tu nữ, l&agrave; Quang Minh Thần người hầu, c&aacute;c ngươi d&aacute;m can đảm Độc Thần!&quot;<br />\r\n<br />\r\nMặc d&ugrave; hắn đ&atilde; lại kh&ocirc;ng t&iacute;n ngưỡng Quang Minh Thần, có th&ecirc;̉ loại thời điểm n&agrave;y, hắn cũng kh&ocirc;ng lo được nhiều như vậy, quyết định trước k&eacute;o c&aacute;i da hổ lại n&oacute;i.<br />\r\n<br />\r\n&quot;Độc Thần?&quot; R&acirc;u quai n&oacute;n sửng sốt một ch&uacute;t, lập tức giống như l&agrave; nghe được c&aacute;i g&igrave; cười nhạo, ph&aacute;ch lối cuồng tiếu, &quot;Ngu xuẩn! Bệ hạ đ&atilde; c&ocirc;ng khai tuy&ecirc;n bố Quang Minh Thần vẫn lạc, thủ ti&ecirc;u ti&ecirc;u diệt Quang Minh gi&aacute;o đ&igrave;nh c&ocirc;ng h&agrave;m đ&atilde; sớm tuy&ecirc;n bố cả nước. Ngươi b&acirc;y giờ c&ograve;n d&aacute;m thờ phụng cứt ch&oacute; Quang Minh Thần, thật sự l&agrave; buồn cười.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; khả năng, Luke cha cố hơn một năm trước c&ograve;n lắng nghe qua Quang Minh Thần &yacute; chỉ!&quot; Catherina sắc mặt nhợt nhạt, dường như bị buộc đến tuyệt cảnh mẫu sư giống như ra sức gi&atilde;y dụa, thanh &acirc;m bi phẫn, &quot;Vĩ đại quang minh chủ ta, l&agrave;m sao c&oacute; thể vẫn lạc? !&quot;<br />\r\n<br />\r\nHơn một năm trước, ch&iacute;nh l&agrave; Ng&ocirc; Huy c&ograve;n kh&ocirc;ng c&oacute; kế thừa Quang Minh Thần Thần vị thời đi&ecirc;̉m.<br />\r\n<br />\r\n&quot;Luke cha cố?&quot; R&acirc;u quai n&oacute;n một tiếng cười qu&aacute;i dị, &quot;Ngươi n&oacute;i l&agrave; c&aacute;i kia d&aacute;m can đảm đi Th&aacute;nh th&agrave;nh tuy&ecirc;n dương dị đoan t&agrave; thuyết người đi&ecirc;n? Ha ha ha, nếu như Quang Minh Thần thật c&ograve;n sống s&oacute;t, nửa năm trước c&aacute;i kia người đi&ecirc;n tại Th&aacute;nh th&agrave;nh quảng trường bị ph&aacute;n quyết thời điểm, Thần l&agrave;m sao kh&ocirc;ng c&oacute; hiện ra thần t&iacute;ch?&quot;<br />\r\n<br />\r\nCatherina nghe vậy sắc mặt c&agrave;ng trắng, nước mắt khống chế kh&ocirc;ng nổi từ kh&oacute;e mắt lăn xuống.<br />\r\n<br />\r\nMặc d&ugrave; đ&atilde; sớm nghe được kh&ocirc;ng tốt nghe đồn, có th&ecirc;̉ cuối c&ugrave;ng kh&ocirc;ng d&aacute;m tin tưởng. Nghĩ kh&ocirc;ng ra, c&aacute;i kia hết thảy dĩ nhi&ecirc;n l&agrave; thật. Luke cha cố hắn, hắn thật. . . Tuẫn, tuẫn đạo. . .<br />\r\n<br />\r\nCatherina nắm thật chặt Luke cha cố trước khi đi giao cho n&agrave;ng ng&acirc;n sắc gi&aacute; chữ thập, nước mắt nh&agrave;o tốc nh&agrave;o tốc rơi xuống, t&iacute;ch t&iacute;ch xẹt qua n&agrave;ng non mềm trắng n&otilde;n gương mặt.<br />\r\n<br />\r\n&quot;Ha ha ha ~ nhỏ tu nữ, ngươi vẫn l&agrave; hết hi vọng đi! Nếu như c&aacute;i kia đồ bỏ Quang Minh Thần c&ograve;n sống s&oacute;t, l&agrave;m sao sẽ t&ugrave;y &yacute; ch&uacute;ng ta trắng trợn diệt trừ Quang Minh Thần điện c&ugrave;ng tu đạo viện? Đ&atilde; sớm hạ xuống thần t&iacute;ch trừng phạt ch&uacute;ng ta.&quot; R&acirc;u quai n&oacute;n cười khằng khặc qu&aacute;i dị, &quot;Ta khuy&ecirc;n ngươi vẫn l&agrave; từ bỏ chống lại, bồi huynh đệ ch&uacute;ng ta mấy c&aacute;i hảo hảo chơi đ&ugrave;a ~ chờ ch&uacute;ng ta chơi cao hứng, n&oacute;i kh&ocirc;ng chừng c&oacute; thể tha ngươi một c&aacute;i mạng.&quot;<br />\r\n<br />\r\n&quot;Ngậm miệng! !&quot;<br />\r\n<br />\r\nTuổi trẻ chiến sĩ nhịn lại nhẫn, cuối c&ugrave;ng kh&ocirc;ng thể k&igrave;m được, rống giận x&ocirc;ng tới, bỗng nhi&ecirc;n một kiếm hướng r&acirc;u quai n&oacute;n đ&acirc;m tới: &quot;Catherina, đừng c&ugrave;ng bọn hắn lời thừa, đi mau! Ta thay ngươi đoạn hậu!&quot;<br />\r\n<br />\r\nHắn kiếm thuật kh&ocirc;ng tầm thường, r&otilde; r&agrave;ng l&agrave; tiếp thụ qua hệ thống dạy bảo c&ugrave;ng trường kỳ huấn luyện, c&aacute;i n&agrave;y một c&aacute;i xuất thủ như điện, kiếm thế hung m&atilde;nh, lại sửng sốt đem r&acirc;u quai n&oacute;n bức lui một bước.<br />\r\n<br />\r\nĐ&aacute;ng tiếc, thương thế tr&ecirc;n người hắn thực sự qu&aacute; nặng, lại l&agrave; d&ugrave;ng &iacute;t địch nhiều, coi như tạm thời bức lui r&acirc;u quai n&oacute;n, cũng căn bản bất lực thừa thắng x&ocirc;ng l&ecirc;n, bất qu&aacute; trong chớp mắt liền lại một lần nữa l&acirc;m v&agrave;o trong v&ograve;ng v&acirc;y.<br />\r\n<br />\r\nTại mấy người l&iacute;nh hợp lực v&acirc;y c&ocirc;ng ph&iacute;a dưới, kh&ocirc;ng l&acirc;u lắm, tr&ecirc;n người hắn liền lại th&ecirc;m hai đạo mới tổn thương, l&acirc;m ly m&aacute;u tươi cơ hồ đem to&agrave;n th&acirc;n hắn quần &aacute;o đều nhuộm th&agrave;nh tinh hồng sắc.<br />\r\n<br />\r\n&quot;Nh&acirc;n từ m&agrave; vĩ đại thần a ~ ng&agrave;i thật muốn từ bỏ ng&agrave;i con d&acirc;n sao?&quot;<br />\r\n<br />\r\nNh&igrave;n xem tuổi trẻ chiến sĩ đẫm m&aacute;u ch&eacute;m giết b&oacute;ng lưng, Catherine hai mắt đẫm lệ m&ocirc;ng lung, trạm tr&ograve;ng mắt m&agrave;u xanh lam b&ecirc;n trong nổi l&ecirc;n thật s&acirc;u tuyệt vọng.<br />\r\n<br />\r\nCho đến ng&agrave;y nay, đừng n&oacute;i n&agrave;ng kh&ocirc;ng trốn tho&aacute;t được, coi như chạy đi th&igrave; thế n&agrave;o đ&acirc;u?<br />\r\n<br />\r\nB&ecirc;n ngo&agrave;i khắp nơi l&agrave; l&ugrave;ng bắt bọn hắn những n&agrave;y c&aacute;i gọi l&agrave; &quot;Quang minh dư nghiệt&quot; c&aacute;c lộ binh sĩ, n&agrave;ng bất qu&aacute; l&agrave; c&aacute;i tay tr&oacute;i g&agrave; kh&ocirc;ng chặt nhỏ tu nữ, kh&ocirc;ng c&oacute; người bảo hộ, đừng n&oacute;i tại binh sĩ l&ugrave;ng bắt hạ sống s&oacute;t, liền liền c&aacute;i n&agrave;y d&atilde; th&uacute; ẩn hiện rừng rậm đều chưa hẳn đi được ra ngo&agrave;i.<br />\r\n<br />\r\nThật liền kh&ocirc;ng c&oacute; nửa điểm hi vọng sao?<br />\r\n<br />\r\nCatherina cắn chặt miệng m&ocirc;i dưới, kiều nộn c&aacute;nh m&ocirc;i đều bị cắn ra m&aacute;u, n&agrave;ng lại phảng phất chưa tỉnh, như cũ nắm thật chặt trong tay ng&acirc;n sắc gi&aacute; chữ thập, d&ugrave;ng sức đến đốt ng&oacute;n tay hiện trắng cũng kh&ocirc;ng c&oacute; bu&ocirc;ng tay.<br />\r\n<br />\r\nMột lần cuối c&ugrave;ng.<br />\r\n<br />\r\nLiền để n&agrave;ng cuối c&ugrave;ng thử một lần nữa.<br />\r\n<br />\r\nNếu như kh&ocirc;ng th&agrave;nh c&ocirc;ng, c&ugrave;ng lắm th&igrave;, n&agrave;ng h&ocirc;m nay liền c&ugrave;ng Luke cha cố một dạng lấy th&acirc;n tuẫn đạo!<br />\r\n<br />\r\nVừa nghĩ đến đ&acirc;y, trong l&ograve;ng n&agrave;ng phảng phất một nh&aacute;y mắt sinh ra lớn lao dũng kh&iacute;, &aacute;nh mắt cũng một nh&aacute;y mắt trở n&ecirc;n v&ocirc; c&ugrave;ng ki&ecirc;n định. Bỗng nhi&ecirc;n nhắm mắt lại, hai tay nắm ở ng&acirc;n sắc gi&aacute; chữ thập, ngậm lấy nước mắt h&aacute;t l&ecirc;n hi vọng th&aacute;nh ca.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Vĩ đại quang minh chủ ta ~ ng&agrave;i l&agrave; đ&ecirc;m tối vĩnh hằng b&ecirc;n trong duy nhất tinh thần, chỉ dẫn m&ecirc; muội mang con d&acirc;n đi hướng quang minh. Ta ca ngợi ng&agrave;i, vĩ đại quang minh chi chủ. . .&quot;<br />\r\n<br />\r\nPhi&ecirc;u miểu tiếng ca tại u &aacute;m rừng c&acirc;y b&ecirc;n trong tung bay ra.<br />\r\n<br />\r\nTừng tia từng sợi &aacute;nh nắng xuy&ecirc;n thấu qua t&aacute;n c&acirc;y, rơi v&agrave;o Catherina tr&agrave;n đầy b&ugrave;n &ocirc; tr&ecirc;n th&acirc;n, tr&ecirc;n mặt. R&otilde; r&agrave;ng n&agrave;ng giờ ph&uacute;t n&agrave;y một th&acirc;n bừa bộn, nhưng m&agrave;, trong nh&aacute;y mắt n&agrave;y, tr&ecirc;n người n&agrave;ng lại kh&ocirc;ng hiểu lộ ra mấy phần kh&iacute; tức th&aacute;nh khiết.<br />\r\n<br />\r\nChẳng biết chẳng hay, n&agrave;ng c&uacute;i xuống eo chậm r&atilde;i rất đứng thẳng l&ecirc;n, ngay cả &acirc;m thanh cũng lại kh&ocirc;ng run rẩy, trở n&ecirc;n ki&ecirc;n định m&agrave; chấp nhất.<br />\r\n<br />\r\n&quot;Catherina!&quot;<br />\r\n<br />\r\nTuổi trẻ chiến sĩ Landen mở to song vằn vện tia m&aacute;u con mắt một m&agrave;n n&agrave;y, trong l&ograve;ng bi thương m&agrave; tuyệt vọng.<br />\r\n<br />\r\nHắn l&agrave; hi vọng dường n&agrave;o Catherina l&uacute;c n&agrave;y có th&ecirc;̉ vứt bỏ hắn m&agrave; đi, một m&igrave;nh chạy trốn, có th&ecirc;̉ hắn đồng thời cũng biết, Catherina n&agrave;ng kh&ocirc;ng phải l&agrave; người như thế.<br />\r\n<br />\r\nCho d&ugrave; l&agrave; tại tu nữ đ&ocirc;ng đảo trong tu đạo viện, Catherina cũng l&agrave; thuần t&uacute;y nhất một c&aacute;i kia. N&agrave;ng cho tới b&acirc;y giờ &ocirc;n nhu m&agrave; ki&ecirc;n định, liền liền Luke cha cố t&aacute;n thưởng n&agrave;ng l&agrave; mầm mống tốt, tương lai c&oacute; hi vọng th&agrave;nh v&igrave; một c&aacute;i cao qu&yacute; đại tu nữ.<br />\r\n<br />\r\nDạng n&agrave;y n&agrave;ng, lại thế n&agrave;o sẽ tại trong tuyệt cảnh vứt bỏ ch&iacute;nh m&igrave;nh m&agrave; đi?<br />\r\n<br />\r\n&quot;Ai ~&quot; Landen thở d&agrave;i một hơi, lần n&agrave;y mọi người đều phải chết ở chỗ n&agrave;y, &aacute;nh mắt b&ecirc;n trong lộ ra liều mạng giống như quyết tuyệt.<br />\r\n<br />\r\n&quot;Ha ha ha ~ thật l&agrave; một c&aacute;i ngu xuẩn tu nữ, sắp chết đến nơi, lại c&ograve;n h&aacute;t th&aacute;nh ca.&quot;<br />\r\n<br />\r\nR&acirc;u quai n&oacute;n một thương đem Landen đ&aacute;nh văng ra, d&ugrave;ng nh&igrave;n &aacute;nh mắt của con mồi nh&igrave;n xem hai người giễu giễu n&oacute;i: &quot;Đ&atilde; c&aacute;c ngươi Quang Minh Thần vĩ đại như vậy, tới tới tới, hiển lộ một c&aacute;i thần t&iacute;ch cho ch&uacute;ng ta nh&igrave;n xem. Kia cẩu th&iacute; quang minh. . .&quot;<br />\r\n<br />\r\nNhưng m&agrave;, hắn mới vừa vặn n&oacute;i ph&acirc;n nửa, cả người liền bỗng dưng cứng đờ.<br />\r\n<br />\r\nBởi v&igrave; ngay tại vừa rồi trong nh&aacute;y mắt đ&oacute;, Catherina trong tay c&acirc;y kia bạch ng&acirc;n gi&aacute; chữ thập trụy sức, bỗng dưng bạo ph&aacute;t ra rực rỡ bạch quang.<br />\r\n<br />\r\nCh&oacute;i m&ugrave; người con mắt.</p>', '2021-11-30 20:56:20', 5, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(245, 66, 'Thần ân Chúc Phúc', '3', '<p>Quang mang tinh khiết kh&ocirc;ng t&igrave; vết, thần th&aacute;nh cao qu&yacute;.<br />\r\n<br />\r\nTại quang mang n&agrave;y bao phủ xuống, c&aacute;i kia h&igrave;nh ảnh th&ocirc; r&aacute;p Quang Minh Thần pho tượng, phảng phất đ&atilde; c&oacute; được sinh mạng giống như trở n&ecirc;n sinh động như thật, tr&agrave;n đầy thần th&aacute;nh m&agrave; uy nghi&ecirc;m kh&iacute; tức, để người k&igrave;m l&ograve;ng kh&ocirc;ng đặng sinh l&ograve;ng k&iacute;nh sợ.<br />\r\n<br />\r\nLiền phảng phất, thật sự c&oacute; cao cao tại thượng thần chi gi&aacute;ng l&acirc;m nh&acirc;n gian.<br />\r\n<br />\r\nChiến đấu, trong nh&aacute;y mắt n&agrave;y im bặt m&agrave; dừng.<br />\r\n<br />\r\nTất cả mọi người, bao qu&aacute;t Landen đều h&aacute; hốc miệng, nh&igrave;n xem Catherina trong tay ng&acirc;n sắc gi&aacute; chữ thập. Liền liền nhắm mắt lại ng&acirc;m xướng th&aacute;nh ca Catherina đều giống như c&oacute; cảm gi&aacute;c, bỗng nhi&ecirc;n mở mắt.<br />\r\n<br />\r\nChẳng, chẳng lẽ thật l&agrave; Quang Minh Thần gi&aacute;ng l&acirc;m rồi?<br />\r\n<br />\r\n&quot;Ta liền biết! Ta liền biết, vĩ đại quang minh chủ ta, sẽ kh&ocirc;ng quẳng đi Thần con d&acirc;n!&quot;<br />\r\n<br />\r\nCatherina vui đến ph&aacute;t kh&oacute;c, k&iacute;ch động đến to&agrave;n th&acirc;n run rẩy, liền liền hai g&ograve; m&aacute; cũng nhiễm l&ecirc;n một mạt đ&agrave; hồng.<br />\r\n<br />\r\nN&agrave;ng cao cao n&acirc;ng l&ecirc;n trong tay gi&aacute; chữ thập, k&iacute;ch động chuẩn bị quỳ xuống ngh&ecirc;nh đ&oacute;n thật thần h&agrave;ng l&acirc;m.<br />\r\n<br />\r\nĐ&uacute;ng l&uacute;c n&agrave;y, trong đ&acirc;̀u của n&agrave;ng đột nhi&ecirc;n vang l&ecirc;n một thanh &acirc;m: &quot;Cạn t&iacute;n đồ bảo bối, nhanh, n&oacute;i nhanh một ch&uacute;t ra nguyện vọng của ngươi. Bản Thần nhất định sẽ thỏa m&atilde;n ngươi ~ &quot;<br />\r\n<br />\r\n&quot;A?&quot;<br />\r\n<br />\r\nCatherina to&agrave;n th&acirc;n chấn động, đang muốn quỳ xuống hai ch&acirc;n một ch&uacute;t cương ngay tại chỗ.<br />\r\n<br />\r\nBảo, bảo bối? Vĩ đại Quang Minh Thần dĩ nhi&ecirc;n gọi n&agrave;ng bảo bối, m&agrave; lại ngữ kh&iacute; như vậy ngả ngớn. Chẳng lẽ n&oacute;i, Thần, Thần muốn để n&agrave;ng phụng dưỡng sao?<br />\r\n<br />\r\nMặt của n&agrave;ng đằng một ch&uacute;t đốt l&ecirc;n, sắc mặt đỏ đến nhỏ m&aacute;u.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, Ng&ocirc; Huy &yacute; thức vừa vặn &quot;Gi&aacute;ng l&acirc;m&quot; tại gi&aacute; chữ thập b&ecirc;n trong, ch&uacute; &yacute; tới trước mặt c&aacute;i kia nữ cạn t&iacute;n đồ sắc mặt đỏ bừng, một bộ bị dọa ngốc d&aacute;ng vẻ, nh&aacute;y mắt cũng &yacute; thức được kh&ocirc;ng đ&uacute;ng.<br />\r\n<br />\r\nHắn c&aacute;i n&agrave;y cũng l&agrave; lần đầu ti&ecirc;n lắng nghe t&iacute;n đồ c&acirc;̀u nguyện, kh&ocirc;ng biết l&agrave; c&aacute;i t&igrave;nh huống như thế n&agrave;o. Nhưng nh&igrave;n tiểu c&ocirc; nương n&agrave;y c&aacute;i bộ d&aacute;ng n&agrave;y, l&agrave; hắn biết n&agrave;ng khẳng định nghe được ch&iacute;nh m&igrave;nh vừa rồi c&acirc;u kia kh&ocirc;ng đứng đắn.<br />\r\n<br />\r\nL&uacute;c trước trong một năm, hắn cẩn thận nghi&ecirc;n cứu qua thần c&aacute;ch, tăng th&ecirc;m kế thừa vụn vặt tri thức. Biết thần linh một th&acirc;n bản lĩnh, hơn ph&acirc;n nửa l&agrave; th&ocirc;ng qua thần c&aacute;ch đến thi triển. Thần linh c&oacute; gi&aacute;ng l&acirc;m vị diện kh&aacute;c bản lĩnh, hơn nữa c&ograve;n c&oacute; nhiều loại gi&aacute;ng l&acirc;m phương thức.<br />\r\n<br />\r\nLoại thứ nhất, liền l&agrave; ch&acirc;n th&acirc;n gi&aacute;ng l&acirc;m, chỉ l&agrave; thần linh lực lượng qu&aacute; mức khổng lồ, ch&acirc;n th&acirc;n gi&aacute;ng l&acirc;m rất nhiều bậc thấp vị diện chưa hẳn có th&ecirc;̉ tiếp nhận.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nLoại thứ hai, ch&iacute;nh l&agrave; ph&acirc;n th&acirc;n gi&aacute;ng l&acirc;m, thần linh c&oacute; thể thực hiện bồi dưỡng mấy cỗ ph&acirc;n th&acirc;n, tại thời điểm cần thiết đầu nhập bậc thấp vị diện. Đ&aacute;ng tiếc, Ng&ocirc; Huy hiện tại ngay cả m&igrave;nh đều nu&ocirc;i kh&ocirc;ng nổi, đừng n&oacute;i bồi dưỡng ph&acirc;n th&acirc;n.<br />\r\n<br />\r\nLoại thứ ba, ch&iacute;nh l&agrave; h&igrave;nh chiếu gi&aacute;ng l&acirc;m, lấy thần lực trực tiếp tại bậc thấp vị diện ngưng tụ ra một th&acirc;n thể h&igrave;nh chiếu, h&igrave;nh chiếu thực lực điểm số th&acirc;n thấp hơn, nhưng thắng ở thuận tiện mau lẹ. Đ&aacute;ng tiếc, Ng&ocirc; Huy đồng dạng kh&ocirc;ng c&oacute; c&oacute; thần lực ngưng tụ h&igrave;nh chiếu.<br />\r\n<br />\r\nLoại thứ tư, l&agrave; chuyển thế gi&aacute;ng l&acirc;m, thần linh ph&acirc;n ra một sợi &yacute; thức, tại bản vị diện đầu thai chuyển thế.<br />\r\n<br />\r\nLoại thứ năm, ch&iacute;nh l&agrave; &yacute; thức gi&aacute;ng l&acirc;m, thần linh ph&acirc;n ra một sợi &yacute; thức, gi&aacute;ng l&acirc;m tại Th&aacute;nh đồ, Th&aacute;nh kh&iacute;, hoặc l&agrave; ch&iacute;nh m&igrave;nh tượng th&acirc;̀n b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nHắn hiện tại d&ugrave;ng ch&iacute;nh l&agrave; loại thứ năm phương thức. &Yacute; thức gi&aacute;ng l&acirc;m.<br />\r\n<br />\r\nChỉ bất qu&aacute;, &yacute; thức gi&aacute;ng l&acirc;m b&igrave;nh thường c&acirc;̀n vững chắc t&iacute;n ngưỡng th&ocirc;ng đạo. Catherina chỉ l&agrave; c&aacute;i cạn t&iacute;n đồ, t&iacute;n ngưỡng th&ocirc;ng đạo mười phần yếu ớt kh&ocirc;ng ổn định, dưới t&igrave;nh huống b&igrave;nh thường, cầu nguyện của n&agrave;ng cần phải căn bản kh&ocirc;ng đủ để dẫn động thần c&aacute;ch cảm ứng.<br />\r\n<br />\r\nCatherina sở dĩ có th&ecirc;̉ dẫn động thần c&aacute;ch, một l&agrave; bởi v&igrave; n&agrave;ng tại tuyệt vọng l&uacute;c &ocirc;m được ăn cả ng&atilde; về kh&ocirc;ng tử ch&iacute;, t&iacute;n niệm so b&igrave;nh thường bất cứ l&uacute;c n&agrave;o đều c&agrave;ng th&ecirc;m ki&ecirc;n định, bộc ph&aacute;t ra tinh thần lực mạnh mẽ hơn b&igrave;nh thường rất nhiều, hai cũng l&agrave; bởi v&igrave; n&agrave;ng d&ugrave;ng gi&aacute; chữ thập kh&ocirc;ng phải l&agrave; ph&agrave;m vật.<br />\r\n<br />\r\nC&aacute;i n&agrave;y mai ng&acirc;n sắc gi&aacute; chữ thập l&agrave; cuồng t&iacute;n đồ Luke cha cố trước khi đi giao cho n&agrave;ng, bởi v&igrave; Luke quanh năm suốt th&aacute;ng đeo c&ugrave;ng cầu nguyện m&agrave; l&acirc;y d&iacute;nh mấy phần thần th&aacute;nh kh&iacute; tức, c&oacute; tấn thăng th&aacute;nh vật tiềm lực. Đủ loại nguy&ecirc;n do hạ, mới dẫn động thần c&aacute;ch cảm ứng.<br />\r\n<br />\r\nTh&ocirc;ng qua như thế chưa vững chắc t&iacute;n ngưỡng th&ocirc;ng đạo gi&aacute;ng l&acirc;m, đối với trước mắt linh hồn &yacute; thức đều rất suy nhược Ng&ocirc; Huy m&agrave; n&oacute;i, kỳ thật v&ocirc; c&ugrave;ng nguy hiểm, nhưng hắn trước mắt cũng kh&ocirc;ng c&oacute; lựa chọn kh&aacute;c, chỉ c&oacute; thể mạo hiểm thử một lần.<br />\r\n<br />\r\n&Yacute; thức được tiểu c&ocirc; nương khả năng nghe được ch&iacute;nh m&igrave;nh vừa rồi c&aacute;i kia gần như đ&ugrave;a giỡn, hắn l&uacute;c n&agrave;y c&oacute; ch&uacute;t xấu hổ.<br />\r\n<br />\r\nThần linh nha, phải c&oacute; ch&uacute;t thần linh d&aacute;ng vẻ, ch&iacute;nh m&igrave;nh vừa rồi thực sự l&agrave; qu&aacute; kh&ocirc;ng trang trọng~<br />\r\n<br />\r\nHắn vội v&agrave;ng trong b&oacute;ng tối thu liễm một v&ocirc; &yacute; thức, l&agrave;m bộ vừa rồi c&aacute;i g&igrave; cũng kh&ocirc;ng c&oacute; ph&aacute;t sinh, tận lực thấp giọng l&agrave;m bộ n&oacute;i ra: &quot;H&egrave;n mọn cạn t&iacute;n đồ, n&oacute;i ra nguyện vọng của ngươi.&quot;<br />\r\n<br />\r\nĐ&acirc;y mới l&agrave; một c&aacute;i thần linh n&ecirc;n c&oacute; th&aacute;i độ.<br />\r\n<br />\r\nThanh &acirc;m uy nghi&ecirc;m trải qua thần lực truyền lại, giống như hồng chung tại Catherine trong đầu quanh quẩn.<br />\r\n<br />\r\nCatherina t&acirc;m t&igrave;nh chấn động, kh&ocirc;ng để &yacute; vũng b&ugrave;n bưng lấy gi&aacute; chữ thập quỳ xuống lạy: &quot;Vĩ đại quang minh chủ ta, xin ng&agrave;i mau cứu ng&agrave;i t&iacute;n đồ đi ~ &quot;<br />\r\n<br />\r\nBởi v&igrave; lấy Quang Minh Thần ngo&agrave;i &yacute; muốn đ&aacute;p lại,<br />\r\n<br />\r\nTrong chớp nho&aacute;ng n&agrave;y, trong l&ograve;ng n&agrave;ng đối với Quang Minh Thần k&iacute;nh sợ c&ugrave;ng t&iacute;n ngưỡng nh&aacute;y mắt đạt đến một c&aacute;i cao độ trước đ&oacute; chưa từng c&oacute;.<br />\r\n<br />\r\nTheo n&agrave;ng c&acirc;u n&oacute;i kia n&oacute;i ra, Ng&ocirc; Huy r&otilde; r&agrave;ng cảm gi&aacute;c được, n&agrave;ng v&agrave; m&igrave;nh thần c&aacute;ch ở giữa t&iacute;n ngưỡng th&ocirc;ng đạo bắt đầu cấp tốc cường h&oacute;a, ngưng kết, cơ hồ trong chớp mắt liền mạnh hơn trước kia gấp mười.<br />\r\n<br />\r\n&quot;Ừm? Biến th&agrave;nh thật t&iacute;n đồ rồi?&quot;<br />\r\n<br />\r\nNg&ocirc; Huy &acirc;m thầm nhất hỉ, &yacute; thức ch&igrave;m v&agrave;o thần c&aacute;ch xem x&eacute;t, quả nhi&ecirc;n thấy được cải biến.<br />\r\n<br />\r\nThật t&iacute;n đồ ph&iacute;a sau số lượng, th&igrave;nh l&igrave;nh từ 0 biến th&agrave;nh 1!<br />\r\n<br />\r\nTốt! Tốt! Tốt! &Yacute; thức gi&aacute;ng l&acirc;m một ch&uacute;t, liền c&oacute; th&ecirc;m c&aacute;i thật t&iacute;n đồ.<br />\r\n<br />\r\nĐ&acirc;y ch&iacute;nh l&agrave; Ng&ocirc; Huy c&aacute;i thứ nhất thật t&iacute;n đồ, mỗi một c&aacute;i thật t&iacute;n đồ gi&aacute; trị, muốn vượt qua mười c&aacute;i cạn t&iacute;n đồ, l&agrave; rộng r&atilde;i t&iacute;n đồ nền tảng.<br />\r\n<br />\r\nBất qu&aacute;, cơ hồ l&agrave; tại đồng thời, hắn cũng ch&uacute; &yacute; tới thần lực con số biến h&oacute;a.<br />\r\n<br />\r\nThần lực: 0.9 điểm (cảnh c&aacute;o: Thần lực cực độ thiếu thốn, xin mau sớm bổ sung).<br />\r\n<br />\r\nNg&ocirc; Huy kh&ocirc;ng nghĩ tới loại n&agrave;y đơn giản nhất &yacute; thức gi&aacute;ng l&acirc;m, dĩ nhi&ecirc;n hao ph&iacute; 0.1 điểm thần lực, lập tức t&acirc;m đau kh&ocirc;ng ngớt.<br />\r\n<br />\r\nĐ&acirc;y ch&iacute;nh l&agrave; tương đương với 100 điểm t&iacute;n ngưỡng chi lực thần lực a ~ một c&aacute;i t&iacute;n ngưỡng vững chắc thật t&iacute;n đồ tương đương với mười c&aacute;i cạn t&iacute;n đồ, ước chừng mỗi ng&agrave;y có th&ecirc;̉ sinh ra 0. 01 t&iacute;n ngưỡng chi lực, nhỏ ba mươi năm mới c&oacute; thể trở về bản.<br />\r\n<br />\r\nHắn nh&aacute;y mắt minh bạch tới, kh&oacute; tr&aacute;ch thần linh cũng kh&ocirc;ng qu&aacute; phản ứng một c&aacute;i cạn t&iacute;n đồ, thật t&iacute;n đồ, đầu nhập thu hoạch tỉ lệ qu&aacute; thấp!<br />\r\n<br />\r\nBất qu&aacute;, Ng&ocirc; Huy hiện tại chỉ c&ograve;n lại mười mấy c&aacute;i cạn t&iacute;n đồ. Đối với hắn hiện tại đến n&oacute;i, mỗi một c&aacute;i cạn t&iacute;n đồ đều l&agrave; bảo bối, so tiền nhiệm Quang Minh Thần cường đại nhất thời Th&aacute;nh đồ c&ograve;n trọng yếu hơn.<br />\r\n<br />\r\nThật t&iacute;n đồ, c&aacute;i kia c&agrave;ng l&agrave; bảo bối b&ecirc;n trong bảo bối, được n&acirc;ng trong l&ograve;ng b&agrave;n tay che chở.<br />\r\n<br />\r\nHắn trong l&ograve;ng biết ch&iacute;nh m&igrave;nh gi&aacute;ng l&acirc;m mỗi thời mỗi khắc đều đang ti&ecirc;u hao thần lực, mặc d&ugrave; ti&ecirc;u hao yếu ớt, lại kh&ocirc;ng thể l&atilde;ng ph&iacute;. Nghe được Catherina, l&uacute;c n&agrave;y kh&ocirc;ng ch&uacute;t do dự đ&aacute;p ứng xuống.<br />\r\n<br />\r\n&quot;Như ngươi mong muốn.&quot;<br />\r\n<br />\r\nHạo đ&atilde;ng uy nghi&ecirc;m thần &acirc;m tại Catherina trong đầu vang l&ecirc;n.<br />\r\n<br />\r\nSau một khắc, bị Catherina cao cao n&acirc;ng l&ecirc;n đỉnh đầu ng&acirc;n sắc gi&aacute; chữ thập b&ecirc;n trong liền c&oacute; một đạo bạch quang nở rộ ra, nh&aacute;y mắt đưa n&agrave;ng cả người bao phủ tại trong đ&oacute;.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nC&ugrave;ng l&uacute;c đ&oacute;, Catherina tất cả tin tức cũng xuất hiện ở Ng&ocirc; Huy trong &yacute; thức.<br />\r\n<br />\r\nT&iacute;nh danh: Catherina<br />\r\n<br />\r\nGiới t&iacute;nh: Nữ<br />\r\n<br />\r\nTuổi t&aacute;c: 16 tuổi (nụ hoa chớm nở)<br />\r\n<br />\r\nTiềm chất: Thượng phẩm (ng&agrave;n dặm mới t&igrave;m được một, c&oacute; cơ hội tấn thăng đại tu nữ)<br />\r\n<br />\r\nT&iacute;n ngưỡng: Quang Minh Thần (thật t&iacute;n đồ)<br />\r\n<br />\r\nNghề nghiệp: Thực tập tu nữ (Quang Minh Thần)<br />\r\n<br />\r\nĐẳng cấp: Cấp 0 (nhỏ yếu, nh&acirc;n loại b&igrave;nh thường ti&ecirc;u chuẩn)<br />\r\n<br />\r\nThần thuật nắm giữ: 1, quang minh Tr&igrave; Dũ Thuật cấp 0 (trị được liệu cực độ rất nhỏ vết thương)2, quang minh ch&uacute;c ph&uacute;c thuật cấp 0 (có th&ecirc;̉ cực độ rất nhỏ đề thăng qu&acirc;n đội bạn sức chiến đấu)<br />\r\n<br />\r\nNh&igrave;n thấy tiềm chất ph&iacute;a sau &quot;Thượng phẩm&quot; hai chữ, Ng&ocirc; Huy trong l&ograve;ng nhất hỉ.<br />\r\n<br />\r\nPhải biết, tối thiểu cũng phải l&agrave; ng&agrave;n dặm mới t&igrave;m được một tư chất mới c&oacute; thể được xưng l&agrave; thượng phẩm. Tiểu c&ocirc; nương n&agrave;y tiềm chất so ch&iacute;nh m&igrave;nh tưởng tượng b&ecirc;n trong tốt hơn qu&aacute; nhiều.<br />\r\n<br />\r\nHắn l&uacute;c n&agrave;y kh&ocirc;ng do dự nữa, t&acirc;m niệm vừa động, liền đối với thần c&aacute;ch hạ đạt chỉ lệnh: &quot;Thần c&aacute;ch, đề thăng thật t&iacute;n đồ Catherina tu vi đẳng cấp.&quot;<br />\r\n<br />\r\nThoại &acirc;m rơi xuống, một cỗ uy nghi&ecirc;m m&ecirc;nh m&ocirc;ng lực lượng liền tự trong cơ thể hắn r&uacute;t c&aacute;ch m&agrave; ra, th&ocirc;ng qua ng&acirc;n sắc gi&aacute; chữ thập tr&agrave;n v&agrave;o Catherina trong cơ thể, bắt đầu đề thăng thực lực của n&agrave;ng.<br />\r\n<br />\r\nC&ugrave;ng l&uacute;c đ&oacute;, Catherina cũng cảm nhận được một cỗ b&agrave;ng bạc v&ocirc; c&ugrave;ng lực lượng tiến v&agrave;o trong cơ thể của m&igrave;nh, n&agrave;ng nh&aacute;y mắt cảm thấy thần thanh kh&iacute; sảng, tai th&iacute;nh mắt tinh, liền ngay cả th&acirc;n thể đều phảng phất trong nh&aacute;y mắt trở n&ecirc;n nhẹ nh&agrave;ng.<br />\r\n<br />\r\nC&aacute;i n&agrave;y, c&aacute;i n&agrave;y chẳng lẽ ch&iacute;nh l&agrave; Luke cha cố n&oacute;i thần &acirc;n ch&uacute;c ph&uacute;c? Trời ạ, kia l&agrave; chỉ c&oacute; th&agrave;nh t&iacute;n nhất t&iacute;n đồ, mới c&oacute; thể đạt được như thế thần quyến.<br />\r\n<br />\r\nN&agrave;ng xanh thẳm hai con ngươi nh&aacute;y mắt s&aacute;ng l&ecirc;n, k&iacute;ch động đến to&agrave;n th&acirc;n đều run rẩy l&ecirc;n.</p>', '2021-11-30 20:56:40', 5, 1),
(246, 66, 'Thánh Nữ Dưỡng Thành', '4', '<p>Ng&ocirc; Huy một vệt &yacute; thức b&aacute;m v&agrave;o ng&acirc;n sắc tr&ecirc;n thập tự gi&aacute;, lập tức nhạy cảm cảm gi&aacute;c được Catherina &yacute; nghĩ, trong l&ograve;ng kh&ocirc;ng khỏi h&agrave;i l&ograve;ng. Quả nhi&ecirc;n l&agrave; c&aacute;i th&ocirc;ng minh muội tử, dạng n&agrave;y, hắn ch&uacute;c ph&uacute;c đứng l&ecirc;n cũng y&ecirc;n t&acirc;m một ch&uacute;t, tối thiểu kh&ocirc;ng cần lo lắng ch&iacute;nh m&igrave;nh Thần lực sẽ đổ xuống s&ocirc;ng xuống biển.<br />\r\n<br />\r\nKỳ thật, hắn cũng kh&ocirc;ng phải l&agrave; kh&ocirc;ng thể trực tiếp d&ugrave;ng Thần lực đem mấy c&aacute;i kia binh l&iacute;nh đế quốc xử l&yacute;.<br />\r\n<br />\r\nĐừng nh&igrave;n hắn hiện tại chỉ c&ograve;n lại 0.9 điểm Thần lực, nhưng ch&acirc;n muỗi lại nhỏ, đ&oacute; cũng l&agrave; Thần lực. Chỉ cần Ng&ocirc; Huy nguyện &yacute; vận dụng trong đ&oacute; một phần nhỏ, mấy c&aacute;i kia nhỏ yếu binh l&iacute;nh đế quốc lập tức liền sẽ một mệnh &ocirc; h&ocirc;.<br />\r\n<br />\r\nNhưng Ng&ocirc; Huy cũng kh&ocirc;ng định l&agrave;m như vậy.<br />\r\n<br />\r\nL&agrave;m như vậy mặc d&ugrave; đơn giản th&ocirc; bạo, có th&ecirc;̉ tỉ suất chi ph&iacute; - hiệu quả quả thực qu&aacute; thấp.<br />\r\n<br />\r\nHắn hiện tại cần thiết l&agrave; một vị c&oacute; thể thay hắn ổn định, cũng truyền b&aacute; t&iacute;n ngưỡng người ph&aacute;t ng&ocirc;n. M&agrave; kh&ocirc;ng phải đơn thuần nhiều một hai c&aacute;i t&iacute;n đồ. Thật vất vả gi&aacute;ng l&acirc;m một lần, lại đ&atilde; c&oacute; sẵn người th&iacute;ch hợp tuyển, hắn đương nhi&ecirc;n muốn ưu ti&ecirc;n đem Thần lực ti&ecirc;u v&agrave;o ch&iacute;nh m&igrave;nh chuẩn bị bồi dưỡng người ph&aacute;t ng&ocirc;n tr&ecirc;n th&acirc;n.<br />\r\n<br />\r\nTuy n&oacute;i Catherina tiềm chất c&ograve;n chưa đủ lấy g&aacute;nh v&aacute;c l&agrave;m Th&aacute;nh nữ, thực lực cũng còn thi&ecirc;́u r&acirc;́t nhi&ecirc;̀u, nhưng c&aacute;i n&agrave;y kh&ocirc;ng quan hệ, chỉ cần c&oacute; Thần lực tại, liền c&oacute; thể đề thăng thực lực của n&agrave;ng đẳng cấp.<br />\r\n<br />\r\nHuống chi, Ng&ocirc; Huy hiện tại cũng kh&ocirc;ng c&oacute; lựa chọn kh&aacute;c. Nếu như kh&ocirc;ng thể n&acirc;ng đỡ ra một vị người ph&aacute;t ng&ocirc;n, ngh&ecirc;nh đ&oacute;n hắn vị n&agrave;y Quang Minh Thần, ch&iacute;nh l&agrave; triệt để hủy diệt.<br />\r\n<br />\r\nThần lực kh&ocirc;ng g&igrave; l&agrave;m kh&ocirc;ng được, cũng tương tự có th&ecirc;̉ đề thăng một người tiềm chất, chỉ bất qu&aacute; sinh linh tiềm chất c&ugrave;ng linh hồn cường độ c&oacute; quan hệ. M&agrave; linh hồn cường độ, lại c&ugrave;ng sinh mệnh bản nguy&ecirc;n ưu tư tương quan, tăng l&ecirc;n cực k&igrave; hao ph&iacute; Thần lực, cũng kh&ocirc;ng phải trước mắt có th&ecirc;̉ chơi đến chuyển.<br />\r\n<br />\r\nĐương nhi&ecirc;n, hắn hiện tại Thần lực thiếu thốn, cẩn thận l&yacute; do, vẫn l&agrave; chỉ c&oacute; thể một ch&uacute;t x&iacute;u nếm thử đề thăng thực lực của n&agrave;ng.<br />\r\n<br />\r\nHắn đem trong cơ thể c&ograve;n lại 0.9 điểm Thần lực chia l&agrave;m chín cỗ, mỗi lần chỉ chuyển vận một cỗ, một ch&uacute;t x&iacute;u đưa v&agrave;o Catherina trong cơ thể, thử nghiệm đề thăng thực lực của n&agrave;ng.<br />\r\n<br />\r\nMột cỗ. . .<br />\r\n<br />\r\nKhi cỗ thứ nhất Thần lực tr&agrave;n v&agrave;o Catherina trong cơ thể trong nh&aacute;y mắt đ&oacute;, Catherina to&agrave;n th&acirc;n chấn động, phảng phất c&oacute; một đạo v&ocirc; h&igrave;nh r&agrave;ng buộc bị nh&aacute;y mắt đ&aacute;nh vỡ.<br />\r\n<br />\r\nTrong chốc l&aacute;t.<br />\r\n<br />\r\nMột đạo thuần tịnh v&ocirc; hạ bạch quang bỗng nhi&ecirc;n từ tr&ecirc;n trời gi&aacute;ng xuống, quay đầu đem Catherina bao phủ tại trong đ&oacute;.<br />\r\n<br />\r\nCường đại lực lượng gột rửa lấy th&acirc;n thể của n&agrave;ng, xương cốt, thậm ch&iacute; l&agrave; linh hồn, n&agrave;ng vốn l&agrave; mỹ lệ mặt trong nh&aacute;y mắt bịt k&iacute;n tầng một th&aacute;nh quang, &aacute;nh mắt dần dần trở n&ecirc;n th&acirc;m th&uacute;y, liền li&ecirc;n th&ocirc;ng thể kh&iacute; chất cũng trong nh&aacute;y mắt n&agrave;y biến đến v&ocirc; c&ugrave;ng th&aacute;nh khiết.<br />\r\n<br />\r\nN&oacute;i th&igrave; chậm m&agrave; xảy ra th&igrave; nhanh.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nTất cả biến h&oacute;a đều phảng phất l&agrave; trong nh&aacute;y mắt ph&aacute;t sinh, ngay trong nh&aacute;y mắt n&agrave;y, thần c&aacute;ch tin tức biểu hiện b&ecirc;n trong, Catherina đẳng cấp từ &quot;Cấp 0&quot; một ch&uacute;t nhảy tới &quot;Cấp 1&quot; .<br />\r\n<br />\r\nNg&ocirc; Huy trong l&ograve;ng nhất hỉ, chỉ l&agrave; 0.1 Thần lực, liền để Catherina thực lực tu vi tăng l&ecirc;n rất nhiều. C&oacute; thể thấy được Thần lực thật sự l&agrave; cường đại, cho d&ugrave; l&agrave; chỉ c&oacute; 1 điểm Thần lực, cũng l&agrave; một m&oacute;n t&agrave;i sản khổng lồ.<br />\r\n<br />\r\nHắn kh&ocirc;ng do dự nữa l&uacute;c n&agrave;y hạ đạt đạo thứ hai chỉ lệnh: &quot;Thần c&aacute;ch, tiếp tục đề cao Catherina thực lực đẳng cấp.&quot;<br />\r\n<br />\r\nVừa dứt lời, lấy 0.1 điểm l&agrave;m đơn vị Thần lực, một ch&uacute;t x&iacute;u tr&agrave;n v&agrave;o Catherina trong cơ thể.<br />\r\n<br />\r\nNhưng l&agrave; lần n&agrave;y, tổng cộng hao ph&iacute; 0. 2 điểm Thần lực, mới l&agrave;m ra hiệu quả.<br />\r\n<br />\r\nCatherina tr&ecirc;n người bạch sắc quang mang bỗng nhi&ecirc;n đại thịnh, uy nghi&ecirc;m m&ecirc;nh m&ocirc;ng Thần lực h&oacute;a th&agrave;nh c&oacute; như thực chất lực lượng cường đại, để trong cơ thể n&agrave;ng mỗi một tế b&agrave;o đều tại trong khoảnh khắc ho&agrave;n th&agrave;nh thuế biến.<br />\r\n<br />\r\nTrong chốc l&aacute;t, một cỗ c&agrave;ng cường đại hơn kh&iacute; tức từ tr&ecirc;n người n&agrave;ng bay l&ecirc;n.<br />\r\n<br />\r\nThần c&aacute;ch tin tức biểu hiện b&ecirc;n trong, Catherina nghề nghiệp đẳng cấp cũng trong c&ugrave;ng một l&uacute;c biến th&agrave;nh cấp 2.<br />\r\n<br />\r\nCấp 2!<br />\r\n<br />\r\nNgắn ngủi một nh&aacute;y mắt, Catherina liền đạt đến cấp 2.<br />\r\n<br />\r\nBất qu&aacute;, cấp 2 thực lực, cũng liền có th&ecirc;̉ miễn cưỡng ứng ph&oacute; một ch&uacute;t trước mắt tr&agrave;ng diện.<br />\r\n<br />\r\nNhưng l&agrave; c&ograve;n chưa đủ.<br />\r\n<br />\r\nLại đến!<br />\r\n<br />\r\nNg&ocirc; Huy vừa ngoan t&acirc;m, lần nữa thua ra Thần lực, lần n&agrave;y hao ph&iacute; đến 0. 4 điểm Thần lực.<br />\r\n<br />\r\nTrong chốc l&aacute;t, Catherina kh&iacute; tức lại lần nữa bạt thăng, thực lực cũng tại trong chớp mắt biến th&agrave;nh cấp 3.<br />\r\n<br />\r\nMỗi một cấp đề thăng, đều cần c&agrave;ng nhiều Thần lực, cấp 2 thăng cấp 3, dĩ nhi&ecirc;n hao ph&iacute; 0. 4 điểm Thần lực, để Ng&ocirc; Huy t&acirc;m đau kh&ocirc;ng ngớt.<br />\r\n<br />\r\nC&ograve;n lại Thần lực, đ&atilde; xa kh&ocirc;ng đủ để đem Catherina từ cấp 3 thăng đến cấp 4 . Bất qu&aacute;, cấp 3 tu nữ, đ&atilde; l&agrave; phi thường cường đại tồn tại, đầy đủ thay m&igrave;nh l&agrave;m việc.<br />\r\n<br />\r\nC&ograve;n lại 0. 2 Thần lực trước giữ lại, về sau c&ograve;n c&oacute; thể d&ugrave;ng để gi&aacute;ng l&acirc;m, ứng đối c&oacute; khả năng tồn tại đột ph&aacute;t t&igrave;nh trạng.<br />\r\n<br />\r\nNg&ocirc; Huy nhẹ nh&agrave;ng thở ra.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, hắn trong &yacute; thức, Catherina tin tức đ&atilde; ph&aacute;t sinh biến h&oacute;a cực lớn.<br />\r\n<br />\r\nT&iacute;nh danh: Catherina<br />\r\n<br />\r\nGiới t&iacute;nh: Nữ<br />\r\n<br />\r\nTuổi t&aacute;c: 16 tuổi (nụ hoa chớm nở)<br />\r\n<br />\r\nTiềm chất: Thượng phẩm (ng&agrave;n dặm mới t&igrave;m được một, c&oacute; cơ hội tấn thăng đại tu nữ)<br />\r\n<br />\r\nT&iacute;n ngưỡng: Quang Minh Thần (cuồng t&iacute;n đồ)<br />\r\n<br />\r\nNghề nghiệp: Tu nữ (Quang Minh Thần)<br />\r\n<br />\r\nĐẳng cấp: Cấp 3 (thực lực kh&ocirc;ng yếu, Gi&aacute;o Đ&igrave;nh cốt c&aacute;n)<br />\r\n<br />\r\nThần thuật nắm giữ: 1, quang minh Tr&igrave; Dũ Thuật cấp 3 (trị được liệu trọng thương, tật bệnh, khử độc)2, quang minh ch&uacute;c ph&uacute;c thuật cấp 3 (có th&ecirc;̉ tr&ecirc;n diện rộng đề thăng qu&acirc;n đội bạn sức chiến đấu)<br />\r\n<br />\r\n&quot;Ừm?&quot;<br />\r\n<br />\r\nNg&ocirc; Huy vừa rồi nh&igrave;n chằm chằm v&agrave;o Catherina thực lực đẳng cấp, cho đến l&uacute;c n&agrave;y, mới ph&aacute;t hiện t&iacute;n ngưỡng của n&agrave;ng lại trong l&uacute;c v&ocirc; t&igrave;nh ph&aacute;t sinh biến h&oacute;a, đ&uacute;ng l&agrave; trực tiếp nhảy qua kiền t&iacute;n đồ, trở th&agrave;nh cuồng t&iacute;n đồ.<br />\r\n<br />\r\nMột c&aacute;i cuồng t&iacute;n đồ, một ng&agrave;y liền c&oacute; thể sinh ra 1 điểm t&iacute;n ngưỡng chi lực, m&agrave; lại t&iacute;n ngưỡng th&ocirc;ng đạo mười phần ki&ecirc;n cố. Địch nh&acirc;n c&oacute; thể ph&aacute; hủy một c&aacute;i cuồng t&iacute;n đồ th&acirc;n thể thậm ch&iacute; linh hồn, lại mấy kh&ocirc;ng khả năng ph&aacute; hủy t&iacute;n ngưỡng của hắn.<br />\r\n<br />\r\nVẻn vẹn một c&aacute;i cuồng t&iacute;n đồ, gi&aacute; trị liền vượt xa m&ocirc;̣t ngàn c&aacute;i cạn t&iacute;n đồ!<br />\r\n<br />\r\nNg&ocirc; Huy kh&ocirc;ng khỏi mừng rỡ.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nĐ&aacute;ng gi&aacute;! Những n&agrave;y Thần lực đầu nhập quả thực l&agrave; vật si&ecirc;u chỗ đ&aacute;ng gi&aacute; ~<br />\r\n<br />\r\nD&ugrave; cho Ng&ocirc; Huy kh&ocirc;ng cần đầu nghĩ, đều biết dưới t&igrave;nh huống b&igrave;nh thường, c&aacute;i n&agrave;y khu khu 0.7 điểm Thần lực, l&agrave; kh&ocirc;ng thể n&agrave;o bồi dưỡng được một vị cuồng t&iacute;n đồ. Như loại n&agrave;y đặc th&ugrave; cơ duy&ecirc;n, thuần t&uacute;y l&agrave; c&oacute; thể ngộ nhưng kh&ocirc;ng thể cầu.<br />\r\n<br />\r\nN&oacute;i th&igrave; chậm, khi đ&oacute; th&igrave; nhanh.<br />\r\n<br />\r\nNg&ocirc; Huy qu&aacute;n ch&uacute; Thần lực qu&aacute; tr&igrave;nh nh&igrave;n như d&agrave;i dằng dặc, k&igrave; thực bất qu&aacute; l&agrave; qua thời gian mấy hơi thở m&agrave; th&ocirc;i.<br />\r\n<br />\r\nTại th&aacute;nh khiết bạch quang bao phủ xuống, Catherina h&igrave;nh tượng kh&iacute; chất tại ngắn ph&uacute;t chốc ở giữa liền ph&aacute;t sinh biến h&oacute;a long trời lở đất. Tr&ecirc;n người n&agrave;ng tất cả c&aacute;u bẩn đều biến mất, to&agrave;n th&acirc;n m&ocirc;ng lấy tầng một m&agrave;u trắng th&aacute;nh quang, cường đại kh&iacute; tức ở tr&ecirc;n người n&agrave;ng kh&ocirc;ng ki&ecirc;ng nể g&igrave; cả triển lộ.<br />\r\n<br />\r\nTrắng tinh th&aacute;nh quang từ tr&ecirc;n người n&agrave;ng khuếch t&aacute;n ra đến, xua t&aacute;n đi trong rừng u &aacute;m, cũng đưa n&agrave;ng nổi bật l&ecirc;n quang minh m&agrave; th&aacute;nh khiết, dường như gi&aacute;ng l&acirc;m nh&acirc;n gian thi&ecirc;n sứ.<br />\r\n<br />\r\nN&agrave;ng c&aacute;i kia xanh thẳm &aacute;nh mắt b&ecirc;n trong, c&agrave;ng l&agrave; tr&agrave;n ngập một loại kh&ocirc;ng c&oacute; g&igrave; s&aacute;nh kịp cuồng nhiệt, tựa như l&agrave; b&ecirc;n trong cất giấu một đ&aacute;m lửa, lại kh&ocirc;ng c&oacute; bất cứ chuyện g&igrave;, cũng kh&ocirc;ng c&oacute; bất kỳ người n&agrave;o c&oacute; thể dao động t&iacute;n ngưỡng của n&agrave;ng.<br />\r\n<br />\r\nNg&ocirc; Huy kh&ocirc;ng ch&uacute;t nghi ngờ, chỉ cần hắn một c&aacute;i mệnh lệnh, cho d&ugrave; l&agrave; để n&agrave;ng tự s&aacute;t n&agrave;ng cũng sẽ kh&ocirc;ng c&oacute; bất cứ ch&uacute;t do dự n&agrave;o.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y c&aacute;i n&agrave;y c&aacute;i n&agrave;y. . . Sao, l&agrave;m sao c&oacute; thể? !&quot;<br />\r\n<br />\r\nR&acirc;u quai n&oacute;n c&ugrave;ng mấy người l&iacute;nh, bị trước mắt một m&agrave;n n&agrave;y sợ ng&acirc;y người. Chỉ l&agrave; mấy hơi thở c&ocirc;ng phu, c&aacute;i kia yếu đuối Catherina, kh&iacute; tức dĩ nhi&ecirc;n lặp đi lặp lại nhiều lần đề thăng.<br />\r\n<br />\r\nB&acirc;y giờ n&agrave;ng c&ograve;n quỳ gối tr&ecirc;n mặt đất b&ecirc;n trong, lại l&agrave; c&oacute; một cỗ khiến người ngưỡng mộ n&uacute;i cao, t&acirc;m tức giận e ngại hơi thở.<br />\r\n<br />\r\nChẳng lẽ, chẳng lẽ đoàn kia quang mang b&ecirc;n trong, thật l&agrave; Quang Minh Thần gi&aacute;ng l&acirc;m rồi? Kh&ocirc;ng sai, chỉ sợ chỉ c&oacute; ch&acirc;n ch&iacute;nh thần linh, mới c&oacute; loại n&agrave;y lật tay th&agrave;nh m&acirc;y trở tay th&agrave;nh mưa bản lĩnh.<br />\r\n<br />\r\nV&ocirc; c&ugrave;ng cảm gi&aacute;c sợ h&atilde;i bao phủ lại bọn hắn, thần linh a, đ&acirc;y ch&iacute;nh l&agrave; một c&aacute;i thần linh a!<br />\r\n<br />\r\nSống đời n&agrave;y, c&ograve;n l&agrave; lần đầu ti&ecirc;n nh&igrave;n thấy thần linh gi&aacute;ng l&acirc;m. Để bọn hắn tuyệt vọng l&agrave;, trước đ&oacute;, bọn hắn vừa mới ng&ocirc;n ngữ tiết độc t&ocirc;n thần n&agrave;y linh. Hắn, bọn hắn sẽ kh&ocirc;ng bị ph&aacute;n quyết a?</p>', '2021-11-30 20:57:03', 10, 1),
(247, 67, 'Thế Giới Mới', '1', '<p>Tại một v&ugrave;ng trời đen trong kh&ocirc;ng gian, thời gian tr&ocirc;i qua Lục Thi&ecirc;n dần kh&ocirc;i phục &yacute; thức, vừa mới bắt đầu chỉ c&oacute; thể tỉnh lại trong gi&acirc;y l&aacute;t về sau c&agrave;ng l&uacute;c c&agrave;ng thanh tỉnh.<br />\r\n<br />\r\nĐến khi m&agrave; hắn c&oacute; thể suy nghĩ được, mới ph&aacute;t hiện kh&ocirc;ng biết bản th&acirc;n đang ở đ&acirc;u, hiện tại hắn cảm gi&aacute;c dường như cơ thể hắn kh&ocirc;ng c&ograve;n tồn tại, giống như những điểm s&aacute;ng tr&ocirc;i lơ lửng ở giữa kh&ocirc;ng gian đen tối.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; c&oacute; chuyện g&igrave;? Hắn đ&atilde; chết? Linh Hồn xuất thể?<br />\r\n<br />\r\nLục Thi&ecirc;n sợ h&atilde;i k&ecirc;u l&ecirc;n một tiếng, cố gắng hồi tưởng lại, khi đ&oacute; hắn đang tr&ecirc;n đường học về, chợt thấy c&oacute; một b&eacute; g&aacute;i đang đuổi theo quả b&oacute;ng bay m&agrave; ph&iacute;a trước ch&iacute;nh l&agrave; c&ocirc;ng trường đang thi c&ocirc;ng. Bỗng nhi&ecirc;n gi&agrave;n sắt b&ecirc; t&ocirc;ng từ tr&ecirc;n tầng cao đổ sập xuống b&ecirc;n dưới, hắn kh&ocirc;ng kịp nghĩ nhiều, ch&iacute;nh m&igrave;nh chạy về ph&iacute;a trước đẩy c&ocirc; b&eacute; tr&aacute;nh tho&aacute;t, bản th&acirc;n lại tr&aacute;nh kh&ocirc;ng kịp, giữa những tiếng la h&eacute;t ồn &agrave;o xung quanh, &yacute; thức của hắn dần dần trở l&ecirc;n mơ hồ rồi ch&igrave;m v&agrave;o b&oacute;ng tối v&ocirc; tận.<br />\r\n<br />\r\nĐiều cuối c&ugrave;ng trong &yacute; thức của hắn c&ograve;n s&oacute;t lại, giống như tr&ocirc;ng thấy thi&ecirc;n địa biến sắc, m&acirc;y đen k&iacute;n cả bầu trời tạo th&agrave;nh một v&ograve;ng xo&aacute;y, đợi hắn tỉnh lại th&igrave; ph&aacute;t hiện m&igrave;nh đang trong trạng th&aacute;i n&agrave;y rồi . . .<br />\r\n<br />\r\n&quot;A, m&igrave;nh đ&atilde; chết rồi!&quot; Lục Thi&ecirc;n thở d&agrave;i một hơi, gia đ&igrave;nh hắn mặc d&ugrave; kh&ocirc;ng gi&agrave;u c&oacute; g&igrave; nhưng mọi người lại rất y&ecirc;u thương nhau. Cũng may còn có anh trai, chỉ là lúc này đã không còn có hắn, mong rằng anh trai ở thế giới đó có thể thay hắn chăm sóc bố mẹ.<br />\r\n<br />\r\nTừ nhỏ đến lớn, th&agrave;nh t&iacute;ch học tập của hắn vẫn rất tốt. Đặc biệt l&agrave; khi l&ecirc;n cấp 3, kỳ thi đại học gia đ&igrave;nh muốn hắn thi v&agrave;o trường cảnh s&aacute;t nhưng hắn lại th&iacute;ch học về kinh tế t&agrave;i ch&iacute;nh. Nhưng cuối c&ugrave;ng lại thi v&agrave;o trường kỹ thuật, đại học B&aacute;ch Khoa.<br />\r\n<br />\r\nBản th&acirc;n hắn đang l&agrave; sinh vi&ecirc;n đại học năm 3 rồi, sắp tới cũng sẽ ra trường kiếm việc l&agrave;m .M&agrave; với t&iacute;nh c&aacute;ch &iacute;t n&oacute;i c&ugrave;ng chuy&ecirc;n ng&agrave;nh kỹ thuật sau n&agrave;y cũng chỉ l&agrave;m việc với m&aacute;y m&oacute;c m&agrave; th&ocirc;i, sau đ&oacute; liền cưới một c&ocirc; vợ hiền v&agrave; b&aacute;o hiếu với bố mẹ như vậy coi như đ&atilde; vi&ecirc;n m&atilde;n rồi.<br />\r\n<br />\r\nCuộc sống mỗi ng&agrave;y chỉ cần điềm đạm, ấm &aacute;p như vậy l&agrave; tốt rồi, mặc d&ugrave; tr&ocirc;ng c&oacute; vẻ đơn điệu nhưng b&igrave;nh y&ecirc;n l&agrave; tốt rồi. Nhưng giờ thì xong rồi, tất cả những điều hắn mong đợi đã không còn cách nào có thể thực hiện được nữa, Lục Thiên chỉ có thể tiếc nuối mà cầu chúc mọi người sẽ sống thật tốt khi hắn không còn bên cạnh.<br />\r\n<br />\r\nSau khi suy nghĩ hết mọi chuyện, hắn mới để &yacute; tới hiện trạng của bản th&acirc;n &quot;Ta đ&acirc;y l&agrave; linh hồn xuất khiếu?&quot;. Lục Thi&ecirc;n kh&ocirc;ng ngừng chạy khắp nơi trong kh&ocirc;ng gian, mỗi chỗ đều bị hắn hỏi thăm qua, cuối c&ugrave;ng cho ra một c&aacute;i kết luận, hắn kh&ocirc;ng c&aacute;ch n&agrave;o tho&aacute;t khỏi nơi đ&acirc;y.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nTheo thời gian tr&ocirc;i qua, Lục Thi&ecirc;n c&agrave;ng ng&agrave;y c&agrave;ng bực bội, nếu hắn đ&atilde; chết theo l&yacute; thuyết phải đi đầu thai rồi chứ, tại sao lại bị nhốt ở một nơi tối tăm như n&agrave;y? Chẳng lẽ do nh&acirc;n phẩm của hắn c&oacute; vấn đề?<br />\r\n<br />\r\nHắn cũng kh&ocirc;ng c&oacute; l&agrave;m chuyện g&igrave; thương thi&ecirc;n hại l&yacute;, trời phật ph&ugrave; hộ đem hắn thả ra. L&uacute;c n&agrave;y hắn cũng chỉ biết cầu nguyện, cả ng&agrave;y buồn bực ở trong một kh&ocirc;ng gian đen tối tĩnh lặng, hắn nghi ngờ sớm hay muộn m&igrave;nh sẽ đi&ecirc;n mất.<br />\r\n<br />\r\nNhưng cũng may, thời gian tr&ocirc;i qua kh&ocirc;ng l&acirc;u liền xuất hiện một ch&uacute;t điểm &aacute;nh s&aacute;ng từ từ tiến v&agrave;o. Lục Thi&ecirc;n t&acirc;m trạng cũng bu&ocirc;ng lỏng một ch&uacute;t, hắn c&oacute; ch&uacute;t chịu kh&ocirc;ng được ho&agrave;n cảnh tịch mịch như vậy. B&acirc;y giờ th&igrave; tốt rồi c&oacute; &aacute;nh s&aacute;ng xuất hiện, hắn liền c&oacute; cơ hội tho&aacute;t khỏi nơi đ&acirc;y.<br />\r\n<br />\r\nThời gian tiếp tục qua đi, hắn ph&aacute;t hiện bản th&acirc;n sau khi hấp thu những ch&ugrave;m tia s&aacute;ng kia, cảm gi&aacute;c linh hồn c&agrave;ng ng&agrave;y c&agrave;ng ngưng thực. Hắn ph&aacute;t hiện m&igrave;nh đang chuyển biến tốt, t&iacute;ch cực tiến lại gần những nơi c&oacute; ch&ugrave;m tia s&aacute;ng. Mỗi khi tỉnh lại hắn đều sẽ tiến gần th&ecirc;m về nơi đ&oacute; , cảm gi&aacute;c mỗi l&uacute;c c&agrave;ng dễ chịu thoải m&aacute;i.<br />\r\n<br />\r\nTheo hắn hấp thu c&agrave;ng nhiều những tia s&aacute;ng, linh hồn lại c&agrave;ng hiện ra ngưng thực, thời gian tr&ocirc;i qua bắt đầu đ&atilde; c&oacute; được sơ bộ h&igrave;nh th&aacute;i. Lục Thi&ecirc;n cảm thấy c&agrave;ng ng&agrave;y c&agrave;ng thoải m&aacute;i, hấp thu tia s&aacute;ng c&agrave;ng th&ecirc;m hăng say.<br />\r\n<br />\r\nThời gian tr&ocirc;i qua cứ như vậy lặp đi lặp lại, hắn cảm thấy tinh lực của m&igrave;nh c&agrave;ng ng&agrave;y c&agrave;ng tốt rồi, tỉnh dậy thời gian cũng c&agrave;ng ng&agrave;y c&agrave;ng d&agrave;i, cũng sẽ kh&ocirc;ng xuất hiện t&igrave;nh trạng mệt mỏi nữa, cũng bắt đầu đ&atilde; nắm bắt được một ch&uacute;t sức lực.<br />\r\n<br />\r\nThời gian cứ thế qua đi, hắn thỉnh thoảng bỗng c&oacute; thể nghe được một &iacute;t &acirc;m thanh, tuy rằng nghe kh&ocirc;ng r&otilde;, nhưng l&agrave; l&agrave;m cho hắn cực kỳ hưng phấn, c&oacute; &acirc;m thanh, như vậy gần đ&acirc;y nhất định c&oacute; người, đ&acirc;y chẳng phải l&agrave; hắn sắp c&oacute; thể rời khỏi v&ugrave;ng kh&ocirc;ng gian n&agrave;y hay sao.<br />\r\n<br />\r\nĐ&atilde; nhận thức được việc n&agrave;y, Lục Thi&ecirc;n c&agrave;ng quyết t&acirc;m gi&agrave;nh giật lấy từng tia s&aacute;ng, cho rằng chỉ cần h&iacute;t v&agrave;o đủ nhiều năng lượng, hắn c&oacute; thể đi ra ngo&agrave;i.<br />\r\n<br />\r\nQua một đoạn thời gian, Lục Thi&ecirc;n cảm nhận được linh hồn dần dần ngưng tụ th&agrave;nh h&igrave;nh th&aacute;i một đứa con n&iacute;t, cả v&ugrave;ng kh&ocirc;ng gian cũng dần dần trở n&ecirc;n chật chội, hắn nh&igrave;n linh hồn trạng th&aacute;i của m&igrave;nh l&uacute;c n&agrave;y. Lục Thi&ecirc;n cảm thấy rất nghi hoặc, hắn rốt cuộc l&agrave; bị đ&agrave;y đến c&aacute;i địa phương n&agrave;o đ&acirc;y?.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nChợt hắn nghe thấy &acirc;m thanh:<br />\r\n<br />\r\n&quot;Bảo bối, h&ocirc;m nay như thế n&agrave;o như vậy nghe lời a?&quot; Đ&acirc;y l&agrave; c&acirc;u đầu ti&ecirc;n Lục Thi&ecirc;n nghe r&otilde;.<br />\r\n<br />\r\n&quot;Ta l&agrave; một lần nữa đầu thai sao? B&acirc;y giờ đang ở trong bụng mẹ?&quot; Lục Thi&ecirc;n thầm n&oacute;i.<br />\r\n<br />\r\nM&atilde;i qua một thời gian d&agrave;i, hắn mới nghe r&otilde; hết từng lời thoại.<br />\r\n<br />\r\n&quot;C&ograve;n c&oacute; hai th&aacute;ng tiểu chủ tử liền đi ra.&quot; Đ&acirc;y l&agrave; một thanh &acirc;m nghe &ocirc;n nhụ nhẹ nh&agrave;ng.<br />\r\n<br />\r\n&quot;Đ&uacute;ng vậy a, bảo bối rất nhanh sẽ sinh ra rồi.&quot; Một c&aacute;i &ocirc;n nhu &acirc;m thanh kh&aacute;c l&ecirc;n tiếng.<br />\r\n<br />\r\n&quot; Tiểu gia hỏa muốn d&ugrave;ng đến đồ vật đều chuẩn bị đầy đủ sao?&quot;<br />\r\n<br />\r\n&quot;Phu nh&acirc;n, người y&ecirc;n t&acirc;m, tiểu chủ tử đồ vật đ&atilde; sớm chuẩn bị ,l&uacute;c trước người kh&ocirc;ng phải đ&atilde; kiểm tra qua rồi a&quot;<br />\r\n<br />\r\n&quot;Nh&igrave;n ta, lu&ocirc;n lo lắng, sợ kh&ocirc;ng c&oacute; chuẩn bị cho tốt.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Phu nh&acirc;n quan t&acirc;m sẽ bị loạn!&quot;<br />\r\n<br />\r\nLục Thi&ecirc;n suy đo&aacute;n, hắn hiện tại đại kh&aacute;i đang ho&agrave;i thai trong bụng mẹ, điều n&agrave;y l&agrave;m cho hắn cảm thấy thở ph&agrave;o nhẹ nh&otilde;m, nghe qua c&aacute;ch xưng h&ocirc; c&oacute; lẽ hắn đ&atilde; xuy&ecirc;n qua thế giới cổ đại.<br />\r\n<br />\r\nKh&ocirc;ng nghĩ tới c&oacute; ng&agrave;y hắn lại trải qua điều n&agrave;y, đ&atilde; kh&ocirc;ng c&oacute; thể quay lại kiếp trước kiếp n&agrave;y hắn phải tr&acirc;n trọng gia đ&igrave;nh của m&igrave;nh. Phu nh&acirc;n? Tiểu chủ tử? Phải cổ đại xưng h&ocirc; a! Gia cảnh c&oacute; lẽ cũng kh&ocirc;ng tệ lắm, điều n&agrave;y l&agrave;m cho hắn c&agrave;ng th&ecirc;m an t&acirc;m.<br />\r\n<br />\r\nVề sau mỗi ng&agrave;y Lục Thi&ecirc;n tỉnh dậy liền hấp thu tia s&aacute;ng, ngẫu nhi&ecirc;n hắn cũng sẽ c&ugrave;ng nh&agrave; m&igrave;nh mẫu th&acirc;n t&aacute;c động qua lại lẫn nhau, đ&aacute; n&agrave;ng một cước, n&agrave;y sẽ lại để cho mẫu th&acirc;n của hắn rất k&iacute;ch động, Lục Thi&ecirc;n nghịch c&agrave;ng l&uacute;c c&agrave;ng hăng say, đương nhi&ecirc;n mỗi lần hắn đều ch&uacute; &yacute; d&ugrave;ng lực nhẹ nh&agrave;ng. Thời gian chậm r&atilde;i tr&ocirc;i qua, trong nh&aacute;y mắt đ&atilde; đến ng&agrave;y hắn được sinh ra .<br />\r\n<br />\r\nKh&ocirc;ng biết đ&atilde; trả qua bao nhi&ecirc;u thời gian, trong khi Lục Thi&ecirc;n đang hấp thu lực lượng th&igrave; hắn cảm thấy một trận chấn động kịch liệt. Tiếp theo đ&oacute; liền ph&aacute;t hiện rằng thứ nước sền sệt từ trước tới nay vẫn bao quanh th&acirc;n thể m&igrave;nh đang dần dần tr&ocirc;i đi. Đồng thời hắn cũng tho&aacute;ng tho&aacute;ng nghe được những tiếng k&ecirc;u gọi vội v&atilde;, những &acirc;m thanh luống cuống tay ch&acirc;n từ b&ecirc;n ngo&agrave;i truyền đến. Lục Thi&ecirc;n liền biết được ng&agrave;y đầu ti&ecirc;n m&igrave;nh xuất hiện tr&ecirc;n thế giới n&agrave;y đ&atilde; đến.<br />\r\n<br />\r\nNghĩ đến đ&acirc;y Lục Thi&ecirc;n liền căn cứ theo những tri thức m&igrave;nh học trước kia đem th&acirc;n thể m&igrave;nh đảo ngược trở lại, biến th&agrave;nh tư thế đầu hạ xuống dưới cho mẫu th&acirc;n dễ d&agrave;ng sinh sản.<br />\r\n<br />\r\nLục Thi&ecirc;n cảm gi&aacute;c bản th&acirc;n hắn đ&atilde; sử dụng hết kh&iacute; lực, cả người nhức mỏi hư tho&aacute;t. Mẫu th&acirc;n c&agrave;ng lại kh&ocirc;ng ngừng k&ecirc;u r&ecirc;n thảm thiết, mẫu tử hai người đồng t&acirc;m hiệp lực, sau nhiều lần khổ cực cố gắng, đến khi Lục Thi&ecirc;n cảm gi&aacute;c ch&iacute;nh m&igrave;nh đ&atilde; d&ugrave;ng tới phần kh&iacute; lực cuối c&ugrave;ng, th&igrave; rốt cuộc cũng nghe được một tiếng h&ocirc; vui mừng lẫn sợ h&atilde;i vang l&ecirc;n: &quot;Ra rồi, h&agrave;i tử ra rồi... một vị tiểu thiếu gia...&quot;Ngay sau đó, Lục Thiên cảm thấy toàn thân duỗi đến thoải mái, kế tiếp liền mất đi ý thức, giống như là mệt mỏi mà ngủ mê đi</p>', '2021-11-30 21:01:32', 11, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(248, 67, 'Thiên Kiếp Vạn Tượng - Tử Long Lôi Kiếp', '2', '<p>Oanh.<br />\r\n<br />\r\nOanh.<br />\r\n<br />\r\nTr&ecirc;n trời cao, từng tiếng vang thanh &acirc;m truyền ra.<br />\r\n<br />\r\nTại v&ugrave;ng trời ẩn tộc chi địa - Bắc tộc, một trong tam đại ẩn tộc chỉ thấy tr&ecirc; hư kh&ocirc;ng xuất hiện v&ocirc; số đạo Hắc L&ocirc;i Tử Long đang đi&ecirc;n cuồng x&eacute; r&aacute;ch hư kh&ocirc;ng, phảng phất như c&oacute; tận thế L&ocirc;i thần bu&ocirc;ng xuống, c&oacute; v&ocirc; tận Tử L&ocirc;i muốn x&ocirc;ng ra tho&aacute;t khỏi tr&oacute;i buộc.<br />\r\n<br />\r\nB&ecirc;n ngo&agrave;i Tử V&acirc;n l&acirc;u,<br />\r\n<br />\r\nTộc trưởng Bắc Thần mang theo mười t&ecirc;n tộc l&atilde;o ngẩng đầu nh&igrave;n l&ecirc;n trời, tr&ecirc;n mặt nổi l&ecirc;n vẻ mờ mịt, một t&ecirc;n tộc l&atilde;o trầm giọng n&oacute;i: &quot;Tộc trưởng, thi&ecirc;n sinh dị tượng, tộc ta lại th&ecirc;m một vị thi&ecirc;n ki&ecirc;u bất ph&agrave;m&quot;<br />\r\n<br />\r\nBắc Thần chau m&agrave;y, tr&ecirc;n mặt hiện l&ecirc;n vẻ lo lắng, đ&acirc;y ch&iacute;nh l&agrave; thi&ecirc;n tượng thi&ecirc;n đạo ban ph&uacute;c?<br />\r\n<br />\r\nĐ&uacute;ng l&uacute;c n&agrave;y, từ b&ecirc;n trong Tử V&acirc;n L&acirc;u một nữ tử chạy ra ngo&agrave;i, k&iacute;ch động n&oacute;i: &quot;Tộc trưởng, sinh... l&agrave; c&ocirc;ng tử.&quot;<br />\r\n<br />\r\nBắc Thần hiện l&ecirc;n vẻ vui mừng gật gật đầu, n&oacute;i: &quot;Mau đưa h&agrave;i tử &ocirc;m tới ta nh&igrave;n một ch&uacute;t.&quot;<br />\r\n<br />\r\nNữ tử nghe thấy vậy vội v&agrave;ng quay người đi v&agrave;o b&ecirc;n trong lầu c&aacute;c, sau một v&agrave;i gi&acirc;y Lục Thi&ecirc;n được nữ tử &ocirc;m ra.<br />\r\n<br />\r\nBắc Thần đưa hai tay ra tiếp nhận c&ograve;n chưa kịp d&ograve; x&eacute;t, tr&ecirc;n hư kh&ocirc;ng liền truyền tới một tiếng nổ rung rời động đất, một v&agrave;i đạo Tử L&ocirc;i đ&aacute;nh rơi v&agrave;o bầu trời Bắc tộc chi địa, giống như muốn hủy diệt thi&ecirc;n địa.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nBắc Thần theo &acirc;m thanh nh&igrave;n qua, chỉ thấy b&ecirc;n tr&ecirc;n hư kh&ocirc;ng l&ocirc;i đ&igrave;nh đang tụ lại th&agrave;nh v&ograve;ng xo&aacute;y dần dần c&oacute; xu thế h&igrave;nh th&aacute;i Cự long.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng tốt, kh&ocirc;ng phải l&agrave; dị tượng ch&uacute;c ph&uacute;c, đ&acirc;y l&agrave; thi&ecirc;n kiếp!&quot;<br />\r\n<br />\r\nN&oacute;i đến đ&acirc;y, hắn ngẩng đầu chăm ch&uacute; nh&igrave;n ph&iacute;a tr&ecirc;n hư kh&ocirc;ng sắc mặt hơi đổi một ch&uacute;t, trong l&ograve;ng c&oacute; lo lắng. &quot;Tiểu tử n&agrave;y thi&ecirc;n tư qu&aacute; mạnh bị Thi&ecirc;n Đạo ki&ecirc;ng kị, thi&ecirc;n kiếp bu&ocirc;ng xuống muốn x&oacute;a bỏ sự tồn tại của đứa nhỏ n&agrave;y sao?&quot;.<br />\r\n<br />\r\nTại Tu ch&acirc;n giới, chỉ c&oacute; tu sĩ l&uacute;c đột ph&aacute; cảnh giới hoặc những người c&oacute; thi&ecirc;n tư y&ecirc;u nghiệt l&agrave;m ảnh hưởng tới thi&ecirc;n đạo ph&aacute;p tắc. L&uacute;c đ&oacute; mới bị thi&ecirc;n đạo để mắt tới, kh&ocirc;ng thể chứa chấp được, cho n&ecirc;n thi&ecirc;n kiếp bu&ocirc;ng xuống muốn huy diệt đi sự tồn tại của những người n&agrave;y. V&igrave; thế Tu ch&acirc;n giới gọi đ&acirc;y l&agrave; kiếp nạn, vượt qua được vậy tất cả đều nắm giữ thi&ecirc;n ph&uacute; kinh khủng. Nhưng c&oacute; bao nhi&ecirc;u kẻ c&oacute; thể mỉm cười vượt qua được chứ, qu&aacute; &iacute;t, chỉ c&oacute; những thế lực lớn mới đủ khả năng trợ gi&uacute;p họ vượt qua được.<br />\r\n<br />\r\nTiểu tử n&agrave;y mới ra đời đ&atilde; c&oacute; thể l&agrave;m thi&ecirc;n kiếp bu&ocirc;ng xuống, chứng tỏ nắm giữ thi&ecirc;n tư y&ecirc;u nghiệt. Đứa nhỏ n&agrave;y, c&oacute; lẽ rất may mắn khi được sinh ra ở Bắc tộc. Nếu như thế lực n&agrave;o d&aacute;m cam đoan ch&iacute;n phần mười x&aacute;c xuất th&agrave;nh c&ocirc;ng, vậy trong đ&oacute; chắc chắn c&oacute; Bắc tộc. Ngay cả khi Bắc tộc kh&ocirc;ng ngăn cản được, vậy đại lục n&agrave;y chắc gần như kh&ocirc;ng c&oacute; một thế lực n&agrave;o c&oacute; thể l&agrave;m được.<br />\r\n<br />\r\nGần nhất thế hệ n&agrave;y của Bắc tộc cũng chỉ xuất hiện duy nhất một người nắm giữ thi&ecirc;n tư y&ecirc;u nghiệt, đ&atilde; từng l&agrave;m thi&ecirc;n kiếp bu&ocirc;ng xuống. Người đ&oacute; ch&iacute;nh l&agrave; tiểu c&ocirc;ng ch&uacute;a Bắc Tử Y thức tỉnh ẩn linh thể, Băng linh thể.<br />\r\n<br />\r\nChuyện n&agrave;y cũng đ&atilde; l&agrave;m c&aacute;c vị Th&aacute;i thượng trưởng l&atilde;o vui mừng hết sức, khi đ&oacute; c&ograve;n kinh động tới một vị l&atilde;o tổ xuất quan trực tiếp phong Bắc Tử Y l&agrave;m Thần Nữ, c&ograve;n tự m&igrave;nh chỉ dạy tu luyện.<br />\r\n<br />\r\nNơi đ&acirc;y trong nh&aacute;y mắt xuất hiện mười hai vị Th&aacute;i thượng trưởng l&atilde;o, tất cả tỏa ra kh&iacute; tức tu vi H&oacute;a Ch&acirc;n tầng ch&iacute;n. Bởi vậy c&oacute; thể thấy được tầm quan trọng của việc n&agrave;y, lần n&agrave;y chắc chắn sẽ kh&ocirc;ng ngoại lệ m&agrave; c&ograve;n trọng đại hơn.<br />\r\n<br />\r\nMột người trong số đ&oacute; l&ecirc;n tiếng, hắn ch&iacute;nh l&agrave; người c&oacute; uy t&iacute;n cao nhất ở đ&acirc;y Bắc Thi&ecirc;n Huy.<br />\r\n<br />\r\n&ldquo; Dưới H&oacute;a Ch&acirc;n l&ugrave;i lại, mở đại hộ trận ph&aacute;p, chư vị trưởng l&atilde;o ch&uacute;ng ta mau kết trận để tr&aacute;nh xuất hiện biến cố &rdquo;. Đ&atilde; c&oacute; kinh nghiệm lần trước n&ecirc;n rất nhanh trận ph&aacute;p đồ &aacute;n đ&atilde; xuất hiện, đ&acirc;y ch&iacute;nh l&agrave; &Acirc;m Dương V&ocirc; Tịnh trận một trong những trận ph&aacute;p mạnh nhất của tộc địa.<br />\r\n<br />\r\nBởi v&igrave; c&oacute; biến cố cho n&ecirc;n Lục Thi&ecirc;n tỉnh lại so với dự kiến nhanh hơn.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nThi&ecirc;n kiếp?<br />\r\n<br />\r\nKh&ocirc;ng đ&ugrave;a chứ, hắn chỉ l&agrave; một đứa b&eacute; mới xuất sinh kh&ocirc;ng l&acirc;u sao m&agrave; lại c&oacute; l&ocirc;i kiếp bu&ocirc;ng xuống. Lục Thi&ecirc;n giật m&igrave;nh, l&ocirc;i kiếp, thi&ecirc;n kiếp kh&ocirc;ng lẽ hắn đang ở thế giới tu ti&ecirc;n.<br />\r\n<br />\r\nĐiều n&agrave;y qu&aacute; bất ngờ, kh&ocirc;ng nghĩ tới, kh&ocirc;ng nghĩ tới. L&uacute;c đầu, hắn chỉ nghĩ rằng đ&acirc;y l&agrave; thế giới cổ đại, bất ngờ lại l&agrave; một thế giới cao cấp.<br />\r\n<br />\r\nChuyện n&agrave;y c&oacute; n&ecirc;n coi l&agrave; niềm vui bất ngờ kh&ocirc;ng!<br />\r\n<br />\r\nM&agrave; khoan, trời sao lại c&oacute; l&ocirc;i kiếp kh&ocirc;ng lẽ do hắn l&agrave; người xuy&ecirc;n việt. Kh&ocirc;ng...kh&ocirc;ng, kh&ocirc;ng đ&uacute;ng việc n&agrave;y kh&ocirc;ng thể n&agrave;o nếu kh&ocirc;ng phải như vậy, theo như bảy năm duyệt qua v&ocirc; số quyển tiểu thuyết c&oacute; thể hắn đang sở hữu thi&ecirc;n tư qu&aacute; y&ecirc;u nghiệt, chắc chắn bị thi&ecirc;n đạo ki&ecirc;ng kị. Th&ocirc;i kệ, trước mắt n&ecirc;n nghĩ l&agrave;m sao để vượt qua được đ&acirc;y?<br />\r\n<br />\r\nNếu nhỡ hắn sinh ra trong một thế lực g&acirc;n g&agrave;, vậy liệu c&oacute; vượt qua được kh&ocirc;ng? Vừa mới xuất sinh đ&atilde; nghẻo th&igrave; chắc sẽ l&agrave; chuyện cười cho tất cả c&aacute;c vị tiền bối xuy&ecirc;n việt mất.<br />\r\n<br />\r\nĐ&uacute;ng rồi l&agrave; kịch bản, hắn đang nắm giữ thi&ecirc;n tư khủng bố bị đồng tộc ghen gh&eacute;t, sau đ&oacute; bị h&atilde;m hại trở th&agrave;nh phế vật bị lưu đ&agrave;y ra khỏi gia tộc, sau đ&oacute; thức tỉnh hệ thống nhanh ch&oacute;ng quật khởi trở th&agrave;nh truyền thuyết đứng tr&ecirc;n đỉnh cao.<br />\r\n<br />\r\nNghĩ tới đ&acirc;y, hắn bắt đầu b&igrave;nh thản hơn rất nhiều, mặc d&ugrave; đ&acirc;y l&agrave; motip cũ r&iacute;ch nhưng m&agrave; chịu th&ocirc;i đ&acirc;u c&oacute; lựa chọn n&agrave;o kh&aacute;c nữa, d&ugrave; sao cũng c&oacute; ch&uacute;t mong chờ.<br />\r\n<br />\r\nB&ecirc;n ngo&agrave;i, Tử Long L&ocirc;i Kiếp h&oacute;a th&agrave;nh tia chớp đi&ecirc;n cuồng x&eacute; r&aacute;ch hư kh&ocirc;ng, một đạo hướng về ph&iacute;a Tử V&acirc;n L&acirc;u nơi Lục Thi&ecirc;n đang ở.<br />\r\n<br />\r\nTiếng sấm nổ l&ecirc;n, khoảng c&aacute;ch Tử V&acirc;n l&acirc;u c&agrave;ng ng&agrave;y c&agrave;ng gần.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nC&ugrave;ng l&uacute;c đ&oacute; dẫn đầu l&agrave; c&aacute;c vị Th&aacute;i thượng trưởng l&atilde;o nhanh tay đ&aacute;nh ra từng đạo ph&aacute;p quyết, theo sau l&agrave; những vị Trưởng l&atilde;o cũng đi&ecirc;n cuồng kết ấn, chống đỡ thi&ecirc;n kiếp.<br />\r\n<br />\r\nTử Long L&ocirc;i Kiếp đi&ecirc;n cuồng cắn x&eacute; trận ph&aacute;p, lực lượng thi&ecirc;n kiếp rất mạnh, mắt thấy c&oacute; xu hướng sắp ph&aacute; vỡ trận địa ph&aacute; kh&ocirc;ng đi v&agrave;o. Mọi người đều qu&aacute; bất ngờ, kh&ocirc;ng nghĩ tới thi&ecirc;n kiếp lần n&agrave;y, sẽ c&oacute; tử l&ocirc;i mạnh như vậy. &ldquo; kh&ocirc;ng thể chống đỡ được&rdquo; ở đ&acirc;y tất cả mọi người đều hiện l&ecirc;n suy nghĩ vậy.<br />\r\n<br />\r\nThấy sự việc kh&ocirc;ng ổn, Tộc trưởng Bắc Thần biến sắc c&uacute;i người về ph&iacute;a cấm địa truyền &acirc;m : &ldquo; Mong c&aacute;c vị l&atilde;o tổ xuất quan &hellip;&rdquo; . C&ograve;n chưa đợi hắn n&oacute;i xong, cấm địa đ&atilde; xuất hiện ba luồng kh&iacute; tức nhanh ch&oacute;ng xuất hiện tại l&ocirc;i kiếp. Mỗi người một g&oacute;c mở ra &ldquo; Vực &ldquo; tạo th&agrave;nh h&igrave;nh tam gi&aacute;c kh&iacute; thế v&ocirc; c&ugrave;ng hủy diệt, từng người xuất ra ph&aacute;p bảo l&agrave; một thanh kim đao, một chiếc đỉnh v&agrave; một cự b&uacute;a m&ugrave;a xanh, đặc biệt tất cả đều xuất hiện những hoa văn kh&iacute; tức cổ xưa, c&oacute; thể thấy rất bất ph&agrave;m.<br />\r\n<br />\r\n&ldquo; Diệt Thế L&ocirc;i Đao&rdquo;<br />\r\n<br />\r\n&ldquo; Vạn Tượng Thi&ecirc;n Đỉnh &rdquo;<br />\r\n<br />\r\n&ldquo; Tam Thi&ecirc;n Băng Ch&ugrave;y &ldquo;<br />\r\n<br />\r\nMột tiếng nổ vang rung trời, sau đ&oacute; thi&ecirc;n kiếp cũng bị đ&aacute;nh tan, từng đạo L&ocirc;i Linh V&acirc;n ẩn chứa thi&ecirc;n t&agrave;i địa bảo tinh hoa xuất hiện tr&agrave;n ngập xung quanh , từng đợt luồng bay v&agrave;o trong cơ thể của Lục Thi&ecirc;n, c&ograve;n lại ph&acirc;n t&aacute;n v&agrave;o người đệ tử tộc địa.<br />\r\n<br />\r\nC&oacute; đệ tử kinh h&ocirc; l&ecirc;n, những người mắc kẹt tại cảnh giới đang dần được bu&ocirc;ng lỏng, c&oacute; người c&ograve;n đột ph&aacute; cảnh giới ngay tại chỗ. Tại tộc địa s&aacute;u phần l&agrave; c&oacute; l&ocirc;i linh căn, đơn linh căn l&ocirc;i thuộc t&iacute;nh chiếm hai th&agrave;nh, đơn linh căn băng thuộc t&iacute;nh chiếm hơn một th&agrave;nh, c&ograve;n lại l&agrave; thuộc t&iacute;nh ngũ h&agrave;nh đơn linh lăn cũng c&oacute; gần một th&agrave;nh. C&ograve;n lại đều l&agrave; song linh căn c&ugrave;ng tam linh căn v&ocirc; số.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, Lục Thi&ecirc;n cảm thấy cơ thể m&igrave;nh dường như c&oacute; từng đợt luồng kh&iacute; tr&agrave;n v&agrave;o cơ thể, kinh mạch như được mở rộng ra, thoải m&aacute;i, từng chất dơ bẩn từ người tr&agrave;n ra, c&oacute; lẽ đ&acirc;y l&agrave; tẩy kinh phạt tủy. Nhưng phải biết Lục Thi&ecirc;n chưa từng tu luyện, sinh ra cũng chỉ l&agrave; ph&agrave;m nh&acirc;n, nhưng l&uacute;c n&agrave;y vừa c&oacute; thi&ecirc;n ph&uacute; y&ecirc;u nghiệt, lại c&ograve;n c&oacute; ti&ecirc;n thi&ecirc;n cơ sở vững chắc hơn lứa tuổi. C&oacute; thể khẳng định sau n&agrave;y tr&ecirc;n con đường trở th&agrave;nh Vương giả, hắn sẽ l&agrave; một trong những người đứng ở điểm cuối.</p>', '2021-11-30 21:01:54', 9, 1),
(249, 67, 'Bắc Tiểu Lục', '3', '<p>Mọi người ở đ&acirc;y đều l&agrave; tu vi Ho&aacute; Ch&acirc;n, l&agrave;m sao c&oacute; thể kh&ocirc;ng nh&igrave;n thấy sự biến h&oacute;a của Lục Thi&ecirc;n, trong l&ograve;ng đều cảm th&aacute;n kh&ocirc;ng th&ocirc;i, y&ecirc;u nghiệt kh&ocirc;ng thể h&igrave;nh dung theo lẽ thường được.<br />\r\n<br />\r\nMột vị trung ni&ecirc;n xuất hiện trước mặt Lục Thi&ecirc;n, nh&igrave;n qua rất hiền ho&agrave; nhưng kh&iacute; thế tự nhi&ecirc;n, lại l&agrave;m mọi người xung quanh đổ mồ h&ocirc;i.<br />\r\n<br />\r\n&ldquo;Gặp qua Bắc Kiểu l&atilde;o tổ&rdquo;. Mọi người cung k&iacute;nh h&ocirc;.<br />\r\n<br />\r\nĐ&acirc;y ch&iacute;nh l&agrave; một trong ba vị l&atilde;o tổ vừa xuất thủ, d&ugrave;ng ch&iacute;nh l&agrave; Kim đao Bắc Ki&ecirc;u l&atilde;o tổ, c&ograve;n hai vị đ&atilde; trở về cấm địa. &#39;Vị n&agrave;y chẳng lẽ l&agrave; &hellip; &quot; Lục Thi&ecirc;n nghĩ.<br />\r\n<br />\r\n&ldquo; Phụ th&acirc;n &ldquo;. Tộc trưởng Bắc Thần h&agrave;nh lễ. Sau đ&oacute; cung k&iacute;nh n&oacute;i :&quot;Ta c&ograve;n tưởng ng&agrave;i đang bế quan, đột ph&aacute; H&oacute;a Ch&acirc;n vi&ecirc;n m&atilde;n, kh&ocirc;ng d&aacute;m l&agrave;m phiền người&quot;.<br />\r\n<br />\r\n&ldquo;Phụ th&acirc;n, vừa rồi kh&iacute; thế kh&ocirc;ng lẽ người đ&atilde; bước v&agrave;o cảnh giới kia&rdquo;.<br />\r\n<br />\r\nBắc Ki&ecirc;u kho&aacute;c tay, b&igrave;nh thản n&oacute;i :&ldquo;Ha ha, chuyện trong tầm tay, trong tầm tay&rdquo;.<br />\r\n<br />\r\nBắc Ki&ecirc;u nh&igrave;n xung quanh tộc nh&acirc;n, gật đầu n&oacute;i chuyện :&ldquo;H&ocirc;m nay quả nhi&ecirc;n l&agrave; ng&agrave;y tốt l&agrave;nh của Bắc tộc ta, tộc ta lại xuất hiện th&ecirc;m một vị y&ecirc;u nghiệt thi&ecirc;n tư, quả nhi&ecirc;n thời đại n&agrave;y đại thế thuộc về ch&uacute;ng ta&rdquo; .<br />\r\n<br />\r\nHắn rất vui mừng khi thấy tộc địa ph&aacute;t triển mạnh mẽ.<br />\r\n<br />\r\nBắc Thần chờ phụ th&acirc;n n&oacute;i xong, liền bảo : &ldquo; Phụ th&acirc;n, nếu kh&ocirc;ng ng&agrave;i đặt t&ecirc;n lu&ocirc;n cho tiểu tử n&agrave;y đi &ldquo;.<br />\r\n<br />\r\nMọi người nghe vậy, đều nh&igrave;n sang Bắc Ki&ecirc;u l&atilde;o tổ, hắn cũng nh&igrave;n sang vị gia gia tiện nghi n&agrave;y của m&igrave;nh. Mong chờ một c&aacute;i t&ecirc;n ki&ecirc;u ngạo tuyệt lu&acirc;n, ta đ&atilde; nghĩ sẵn rồi Bắc V&ocirc; Song , Bắc Mặc Thần , Bắc Tử Qu&acirc;n ,...., nghĩ th&ocirc;i đ&atilde; rất l&atilde;nh khốc rồi.<br />\r\n<br />\r\nBế tr&ecirc;n tay đứa ch&aacute;u trai của m&igrave;nh , Bắc Ki&ecirc;u trầm ng&acirc;m một l&uacute;c rồi n&oacute;i :<br />\r\n<br />\r\n&rdquo; Sinh ra bất ph&agrave;m mang theo Tử L&ocirc;i, tướng mạo suất kh&iacute; , kh&iacute; chất đặc biệt&rdquo;, bỗng th&igrave; thầm một c&acirc;u :&ldquo; Bắc Huyền Phong&rdquo;.<br />\r\n<br />\r\nMọi người nghe thấy t&ecirc;n n&agrave;y đều gật g&ugrave;, rất ph&ugrave; hợp với kh&iacute; chất của đứa trẻ n&agrave;y. Lục Thi&ecirc;n nghe thấy cũng s&aacute;ng mắt l&ecirc;n , nhưng hắn chưa kịp cười, th&igrave; đ&atilde; nghe thấy c&acirc;u n&oacute;i tiếp theo.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&ldquo; Được rồi, tiểu tử n&agrave;y sau n&agrave;y sẽ l&agrave; Tiểu Lục, Bắc Tiểu Lục &rdquo;.<br />\r\n<br />\r\nT&ecirc;n cũng như người, qu&aacute; ki&ecirc;u ngạo sẽ chỉ l&agrave;m bản t&acirc;m lung lay, tu sĩ ch&uacute;ng ta tu ti&ecirc;n ch&iacute;nh l&agrave; nghịch thi&ecirc;n m&agrave; đi, ta kh&ocirc;ng mong hắn c&oacute; thể lưu danh thi&ecirc;n cổ, chỉ cần hắn s&aacute;nh vai c&ugrave;ng Sở Cửu Vũ l&agrave; tốt rồi.<br />\r\n<br />\r\nMọi người h&oacute;a đ&aacute; trong gi&acirc;y l&aacute;t, c&aacute;i t&ecirc;n n&agrave;y với mấy c&aacute;i tr&ecirc;n c&oacute; li&ecirc;n quan tới nhau &agrave;, tất cả mọi người đều nh&igrave;n nhau, chỉ ri&ecirc;ng c&oacute; tộc trưởng l&agrave; phấn khởi :<br />\r\n<br />\r\n&ldquo; V&acirc;ng , thưa phụ th&acirc;n&rdquo; .<br />\r\n<br />\r\nNh&igrave;n nhi tử của m&igrave;nh, Bắc Thần n&oacute;i :&ldquo; Từ nay con ch&iacute;nh l&agrave; Bắc Tiểu Lục, ch&iacute;nh l&agrave; con trai ki&ecirc;u ngạo của ta&rdquo;.<br />\r\n<br />\r\nHa ha ha!<br />\r\n<br />\r\nL&uacute;c n&agrave;y đ&acirc;y Lục Thi&ecirc;n, &agrave; kh&ocirc;ng b&acirc;y giờ phải gọi l&agrave; Bắc Tiểu Lục, trợn mắt l&ecirc;n nh&igrave;n Phụ th&acirc;n v&agrave; gia gia tiện nghi, c&aacute;i g&igrave; m&agrave; suất kh&iacute; chứ, c&aacute;i t&ecirc;n kia rất hợp với ta, ta sẽ kh&ocirc;ng ki&ecirc;u ngạo m&agrave;.<br />\r\n<br />\r\nNhắc tới c&aacute;i t&ecirc;n Sở Cửu Vũ ai cũng bất đắc dĩ, đ&acirc;y l&agrave; c&aacute;i t&ecirc;n cấm kị nhất Tu ch&acirc;n giới, l&uacute;c trước Bắc tộc cũng ăn thiệt th&ograve;i trước hắn ta. Thi&ecirc;n tư của hắn được lưu truyền thi&ecirc;n cổ, đ&atilde; gần một vạn năm đến b&acirc;y giờ, cũng chưa ai vượt qua được.<br />\r\n<br />\r\nNhưng l&uacute;c đ&oacute; trong tộc c&oacute; một vị, gần như s&aacute;nh bước được c&ugrave;ng với Sở Cửu Vũ, vị đ&oacute; l&agrave; Bắc Mộc Y&ecirc;n l&agrave; người rất c&oacute; c&aacute; t&iacute;nh, chủ động theo đuổi Sở Cửu Vũ. Trải qua rất nhiều kiếp nạn với nhau , l&uacute;c hai người chuẩn bị đến được với nhau, th&igrave; Sở Cửu Vũ đắc tội c&aacute;c thế lực, bị v&acirc;y c&ocirc;ng.<br />\r\n<br />\r\nHơn trăm vị H&oacute;a Ch&acirc;n, đặc biệt l&agrave; c&oacute; v&agrave;i vị H&oacute;a ch&acirc;n vi&ecirc;n m&atilde;n, m&agrave; l&uacute;c đ&oacute; Sở Cửu Vũ cũng chỉ mới bước v&agrave;o cấp độ Kiếp Biến vi&ecirc;n m&atilde;n. Bắc Mộc Y&ecirc;n tự m&igrave;nh tiến về nơi đ&oacute;, m&agrave; kh&ocirc;ng kịp th&ocirc;ng b&aacute;o cho tộc địa cầu cứu, một m&igrave;nh tiến đến.<br />\r\n<br />\r\nTrận chiến kịch liệt diễn ra nhiều ng&agrave;y, khi tộc địa biết tin tiến về, th&igrave; nơi đ&oacute; đ&atilde; trở th&agrave;nh một v&ugrave;ng trời m&aacute;u, chỉ thấy người kia đang &ocirc;m một vị nữ tử, quỳ đ&oacute; suốt bảy ng&agrave;y bảy đ&ecirc;m.<br />\r\n<br />\r\nMọi người sau khi t&igrave;m hiểu mới biết. Bắc Mộc Y&ecirc;n từ cấm địa ra đ&atilde; bị thương, l&uacute;c đ&oacute; nghe được tin Sở Cửu Vũ gặp nạn, đ&atilde; kh&ocirc;ng kịp nghĩ nhiều ăn v&agrave;o Tử H&oacute;a Đan liền chạy tới. Bắc Mộc Y&ecirc;n mặc d&ugrave; y&ecirc;u nghiệt nhưng do bị thương, dẫn đến dưới sự v&acirc;y c&ocirc;ng của đ&ocirc;ng đảo H&oacute;a Ch&acirc;n, vết thương c&agrave;ng nghi&ecirc;m trọng, đến khi Sở Cửu Vũ biết điều đ&oacute; th&igrave; đ&atilde; kh&ocirc;ng kịp nữa rồi.<br />\r\n<br />\r\nNh&igrave;n trong tay l&agrave; Cửu Thi&ecirc;n H&oacute;a Thảo, do Bắc Mộc Y&ecirc;n cửu tử nhất sinh, lấy được từ cấm địa ra, trước khi tử vong đ&atilde; đưa cho hắn. Sở Cửu Vũ g&agrave;o kh&oacute;c, thi&ecirc;n địa biến sắc, hắn cắn răng nh&eacute;t dược thảo v&agrave;o miệng nhai hấp thu, đ&acirc;y l&agrave; thảo dược một vạn năm chỉ xuất hiện trong cấm địa Thượng Cổ cấm khu.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nLoại linh thảo thể gi&uacute;p Kiếp Biến vi&ecirc;n m&atilde;n tấn cấp H&oacute;a Ch&acirc;n, một trăm phần trăm trong mọi t&igrave;nh huống, m&agrave; kh&ocirc;ng để lại di chứng g&igrave;. Sau khi Sở Cửu Vũ th&agrave;nh c&ocirc;ng bước v&agrave;o H&oacute;a Ch&acirc;n, đ&atilde; đuổi giết ngược lại một đ&aacute;m H&oacute;a Ch&acirc;n. Khi đ&oacute; mưa m&aacute;u phủ k&iacute;n nơi n&agrave;y, cuộc chiến qu&aacute; &aacute;c liệt, chỉ tiếc l&agrave; c&oacute; những thứ đ&atilde; mất, th&igrave; kh&oacute; c&oacute; thể gặp lại được .<br />\r\n<br />\r\nBắc tộc chi địa nổi giận, từ khi n&agrave;o m&agrave; bọn hắn bị coi nhẹ như vậy, d&aacute;m động v&agrave;o tộc nh&acirc;n của Bắc tộc. Tiếp sau đ&oacute; tộc địa đ&atilde; qu&eacute;t sạch c&aacute;c thế lực li&ecirc;n quan, sự kiện đẫm m&aacute;u, chấn động Tu ch&acirc;n giới. Tổng c&aacute;c thế lực bị Bắc tộc trả th&ugrave; gồm c&oacute; năm thế lực ch&iacute;n sao: Ti&ecirc;n Bảo L&acirc;u, V&ocirc; Song C&aacute;c, Tử Ti&ecirc;u M&ocirc;n, l&agrave; do tộc địa diệt, c&ograve;n lại l&agrave; Lưu gia , Hạ gia do người kia diệt.<br />\r\n<br />\r\nC&aacute;c vị trưởng l&atilde;o nghĩ tới chuyện n&agrave;y rất tiếc nuối.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, Bắc Ki&ecirc;u l&atilde;o tổ thấy ta trợn mắt cong m&ocirc;i th&igrave; lại cười :<br />\r\n<br />\r\n&ldquo; Ha ha, nh&igrave;n xem n&agrave;y Tiểu Lục cười với ta, xem ra rất th&iacute;ch c&aacute;i t&ecirc;n n&agrave;y&rdquo;.<br />\r\n<br />\r\nMọi người b&ecirc;n ngo&agrave;i cười, b&ecirc;n trong th&igrave; lại n&oacute;i đ&oacute; l&agrave; n&oacute; bỉu m&ocirc;i, kh&aacute;ng nghị &yacute; kiến của người đ&oacute; th&ocirc;i.<br />\r\n<br />\r\nL&uacute;c n&agrave;y từ trong ph&ograve;ng đi ra, một vị nữ tử cung trang, rất đẹp trẻ tuổi, mang theo n&eacute;t mệt mỏi, nhưng lại kh&ocirc;ng giấu được sự vui mừng. Bắc Thần h&ocirc; l&ecirc;n một tiếng :<br />\r\n<br />\r\n&ldquo;Nương tử, sao n&agrave;ng lại ra đ&acirc;y, sao kh&ocirc;ng chịu kh&oacute; nằm trong ph&ograve;ng &ldquo;. Đ&acirc;y l&agrave; An Nguyệt mẫu th&acirc;n của hắn.<br />\r\n<br />\r\nAn Nguyệt lắc đầu mỉm cười với Bắc Thần, dịu d&agrave;ng n&oacute;i:<br />\r\n<br />\r\n&ldquo;Muội kh&ocirc;ng sao, muội lo lắng cho Lục Lục&rdquo;.<br />\r\n<br />\r\nBắc Thần cười một tiếng :&ldquo; T&ecirc;n tiểu tử n&agrave;y c&oacute; thể bị sao chứ rất an to&agrave;n, con ch&uacute;ng ta ch&iacute;nh l&agrave; thi&ecirc;n ph&uacute; cao, bị thi&ecirc;n đạo đố kị m&agrave; th&ocirc;i, giờ mọi chuyện y&ecirc;n ổn cả rồi&rdquo;.<br />\r\n<br />\r\nAn Nguyệt mặc d&ugrave; l&agrave; th&ecirc; tử của tộc trưởng Bắc Thần, nhưng l&agrave;m người lu&ocirc;n được tr&ecirc;n dưới Bắc tộc, k&iacute;nh trọng v&agrave; y&ecirc;u mến. N&agrave;ng nh&igrave;n về c&aacute;c vị trưởng l&atilde;o ở đ&acirc;y, h&agrave;nh lễ thỉnh an, rất quy củ. Mọi người gật gật đầu mỉm cười.<br />\r\n<br />\r\nSau đ&oacute; n&agrave;ng quay sang phụ th&acirc;n, mỉm cười n&oacute;i :</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&ldquo; An Nguyệt ch&uacute;c mừng phụ th&acirc;n, đột ph&aacute; thuận lợi.&rdquo;<br />\r\n<br />\r\nBắc Ki&ecirc;u nghe được con d&acirc;u ch&uacute;c mừng, th&igrave; đắc &yacute; gật đầu. So với đứa con trai Bắc Thần, th&igrave; hắn c&agrave;ng ưu &aacute;i đứa nhỏ An Nguyệt n&agrave;y. Nhiều l&uacute;c nghĩ nhi tử đ&uacute;ng may mắn, được An Nguyệt đứa nhỏ n&agrave;y nh&igrave;n tr&uacute;ng.<br />\r\n<br />\r\nHắn h&ograve;a &aacute;i, cười một tiếng :<br />\r\n<br />\r\n&ldquo; Ha ha , chuyện nhỏ , chuyện nhỏ, vẫn l&agrave; t&ecirc;n tiểu tử n&agrave;y quan trọng hơn&rdquo;.<br />\r\n<br />\r\nAn Nguyệt tiến tới, &ocirc;m Bắc Tiểu Lục v&agrave;o l&ograve;ng, nở nụ cười vui vẻ. :<br />\r\n<br />\r\n&ldquo; Lục Lục đ&aacute;ng y&ecirc;u qu&aacute;, sau n&agrave;y phải thương mẫu th&acirc;n nhiều nha, đừng như phụ th&acirc;n con, chỉ biết tu luyện với ch&iacute;nh sự&rdquo;. Vừa n&oacute;i vừa lườm Bắc Thần.<br />\r\n<br />\r\n&quot;khụ khụ!&quot;<br />\r\n<br />\r\nBắc Thần ho khan v&agrave;i tiếng.<br />\r\n<br />\r\nAn nguyệt xoay người thỉnh lễ chư vị trưởng l&atilde;o :&rdquo; An nguyệt thay mặt Lục Lục, cảm ơn c&aacute;c vị trưởng l&atilde;o ưu &aacute;i&rdquo;.<br />\r\n<br />\r\nC&aacute;c vị trưởng l&atilde;o vui vẻ ,n&oacute;i :<br />\r\n<br />\r\n&ldquo; Kh&ocirc;ng c&oacute; g&igrave;, đ&acirc;y l&agrave; tr&aacute;ch nhiệm của trưởng bối ch&uacute;ng ta. M&agrave; lại c&oacute; thể chứng kiến sự bất ph&agrave;m của t&ecirc;n tiểu tử n&agrave;y, l&agrave; may mắn của ch&uacute;ng ta.&rdquo;<br />\r\n<br />\r\nAn nguyệt mỉm cười gật đầu, xin ph&eacute;p mọi người &ocirc;m Bắc Tiểu Lục về Tử V&acirc;n l&acirc;u. Mọi người đều hiểu n&ecirc;n cũng gật đầu, Bắc Tiểu Lục d&ugrave; bất ph&agrave;m nhưng cũng mới ra đời, cần tĩnh dưỡng hấp thu hết tinh hoa của L&ocirc;i Linh V&acirc;n .</p>', '2021-11-30 21:02:17', 8, 1),
(250, 67, 'Tam Đại Ẩn Tộc', '4', '<p>C&ugrave;ng l&uacute;c đ&oacute;,<br />\r\n<br />\r\nTại một t&ograve;a cung điện kh&iacute; tức cổ xưa, b&ecirc;n trong đang ngồi rất nhiều người, đặc biệt c&oacute; mười hai luồng kh&iacute; tức thuộc về H&oacute;a Ch&acirc;n trung kỳ .Đ&acirc;y ch&iacute;nh l&agrave; một trong ba ẩn tộc Nam Cung nhất tộc, l&uacute;c n&agrave;y tất cả trưởng l&atilde;o đều c&oacute; mặt, họ đều được tộc trưởng triệu tập.<br />\r\n<br />\r\nMột người trong số đ&oacute; l&ecirc;n tiếng:<br />\r\n<br />\r\n&ldquo; Tộc trưởng b&ecirc;n Bắc tộc c&oacute; dị tượng sinh ra, kh&ocirc;ng biết đ&acirc;y l&agrave; dị bảo xuất thế, hay l&agrave; lại xuất hiện th&ecirc;m một cường giả&rdquo;.<br />\r\n<br />\r\nNam Cung V&ocirc; Phong, tộc trưởng của Nam Cung nhất tộc, nổi tiếng l&agrave; cường giả H&oacute;a Ch&acirc;n tầng 5 hậu kỳ , c&ugrave;ng thời đại với Bắc Thần tộc trưởng Bắc tộc chi địa.<br />\r\n<br />\r\nHắn kẽ trầm ng&acirc;m rồi n&oacute;i :&rdquo; Nếu đ&atilde; kh&ocirc;ng biết chuyện g&igrave;, vậy xin nhờ hai vị lục thất trưởng l&atilde;o sang thăm d&ograve;. D&ugrave; sao hai tộc ch&uacute;ng ta vẫn l&agrave; giao t&igrave;nh kh&ocirc;ng cạn.&rdquo;. Bắc tộc đ&atilde; c&oacute; mấy ng&agrave;n năm kh&ocirc;ng ra khỏi giới vực của m&igrave;nh rồi, kh&ocirc;ng biết t&ecirc;n Bắc Thần chết tiệt kia, đ&atilde; đột ph&aacute; H&oacute;a Ch&acirc;n tầng s&aacute;u hay chưa.<br />\r\n<br />\r\n&ldquo; Tu&acirc;n lệnh tộc trưởng&rdquo;. Lục trưởng l&atilde;o Nam Cung Vực l&ecirc;n tiếng, hắn cũng l&agrave; người vừa n&oacute;i chuyện khi n&atilde;y. B&ecirc;n cạnh l&agrave; thất trưởng l&atilde;o Nam Cung Bạch cũng gật đầu.<br />\r\n<br />\r\n&ldquo; Được rồi chuyện đ&oacute; t&iacute;nh sau đi&rdquo;. Nam Cung V&ocirc; Phong l&ecirc;n tiếng, dừng lại ch&uacute;t hắn khẽ n&oacute;i :&ldquo; Tứ trưởng l&atilde;o, chuyện b&ecirc;n Nam Thi&ecirc;n C&aacute;c thế n&agrave;o rồi&rdquo;.<br />\r\n<br />\r\nTứ trưởng l&atilde;o Nam Cung Định l&agrave; một vị l&atilde;o giả, l&uacute;c n&agrave;y hắn đang vuốt r&acirc;u nghe thấy tộc trưởng hỏi, khẽ cười n&oacute;i :</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&rdquo; Tộc trưởng cứ y&ecirc;n t&acirc;m, Nam Thi&ecirc;n C&aacute;c đang ph&aacute;t triển rất tốt, đ&atilde; chiếm được hai th&agrave;nh thị trường của đại lục. Chỉ l&agrave; b&ecirc;n Vạn Ph&uacute;c Bảo L&acirc;u b&aacute; chủ từ xa xưa, nội t&igrave;nh rất th&acirc;m hậu, bởi vậy rất kh&oacute; để ch&uacute;ng ta tiến th&ecirc;m một bước.<br />\r\n<br />\r\nVạn Ph&uacute;c Bảo L&acirc;u l&agrave; cửa h&agrave;ng thương nghiệp, b&aacute; chủ của đại lục chiếm giữ năm th&agrave;nh lưu lượng của đại lục, b&ecirc;n cạnh đ&oacute; th&igrave; c&oacute; rất nhiều thương nghiệp ph&iacute;a dưới : Tứ Tụ L&acirc;u, V&ocirc; Th&agrave;nh C&aacute;c , Thương Ngọc L&acirc;u nắm giữ 1 th&agrave;nh đang cạnh tranh nhau.<br />\r\n<br />\r\nC&oacute; thể n&oacute;i rằng Vạn Ph&uacute;c Bảo L&acirc;u phủ k&iacute;n cả đại lục Thất Nguyệt. Mặc d&ugrave; chủ yếu l&agrave; thương nghiệp, nhưng nếu kh&ocirc;ng c&oacute; thực lực l&agrave;m sao c&oacute; thể đứng vực được chứ. Sớm đ&atilde; bị c&aacute;c thế lực mạnh chia cắt cả rồi. Vạn Ph&uacute;c Bảo L&acirc;u được đ&aacute;nh gi&aacute; l&agrave; v&ocirc; hạn tiếp cận thế lực ch&iacute;n sao, nhưng nếu c&oacute; va chạm với một trong c&aacute;c thế lực ch&iacute;n sao. Vạn Ph&uacute;c Bảo L&acirc;u c&oacute; thể nắm giữ s&aacute;u phần chiến thắng, bởi bọn họ giao thiệp rất rộng, nh&acirc;n mạch trải khắp đại lục. V&igrave; thế rất &iacute;t thế lực d&aacute;m qu&aacute; đ&aacute;ng, trước mặt bọn họ khoa ch&acirc;n m&uacute;a tay.<br />\r\n<br />\r\nNam Cung V&ocirc; Phong gật đầu, l&ecirc;n tiếng:&rdquo; Ta nhớ được Nam Thi&ecirc;n C&aacute;c, vừa mở một chi nh&aacute;nh ở b&ecirc;n Đ&ocirc;ng Huyền Ch&acirc;u. Ta dự định để Tịnh Di qua b&ecirc;n đ&oacute; phụ tr&aacute;ch một thời gian&rdquo;, hắn n&oacute;i ch&iacute;nh nữ nhi của m&igrave;nh.<br />\r\n<br />\r\nNam Cung Tịnh Di năm nay mười hai tuổi, nắm giữ thi&ecirc;n ph&uacute; Ẩn linh thể, Phong linh thể, c&ugrave;ng cấp bậc với Bắc Tử Y của Bắc tộc chi địa. Chỉ l&agrave; do ni&ecirc;n kỷ c&ograve;n nhỏ, n&ecirc;n chưa được đặt ngang h&agrave;ng. Nhưng hai người đều c&oacute; điểm chung, l&agrave; mỹ nh&acirc;n khuynh th&agrave;nh, được tr&ecirc;n dưới hai tộc c&ocirc;ng nhận.<br />\r\n<br />\r\nMột trong ba Ẩn tộc c&ograve;n lại. Đ&oacute; l&agrave; Thể Long nhất tộc, đ&acirc;y l&agrave; một tộc rất đặc biệt, được truyền thừa từ xa xưa, trong người chảy huyết mạch của Long tộc. Tr&ecirc;n dưới nhất tộc, căn cơ bước đầu đều luyện thể, đệ tử của tộc n&agrave;y thể chất rất mạnh, c&ugrave;ng cảnh giới rất kh&oacute; chơi.<br />\r\n<br />\r\nTrước mắt, tương truyền xa xưa, tộc n&agrave;y c&oacute; ba giai đoạn tr&ugrave;ng k&iacute;ch quan trọng, nếu như kh&ocirc;ng mở được huyết mạch, th&igrave; cũng chỉ hơn được thể chất tu sĩ kh&aacute;c dưới Nguy&ecirc;n Anh.<br />\r\n<br />\r\nGiai đoạn một, l&agrave; bước v&agrave;o Nguy&ecirc;n Anh tr&ugrave;ng k&iacute;ch mở ra huyết mạch, một khi th&agrave;nh c&ocirc;ng th&igrave; thể chất sẽ được nhảy vọt l&ecirc;n một cấp độ cao, c&oacute; thể chịu được một k&iacute;ch của Hư Thần sơ kỳ. M&agrave; đ&acirc;y vẫn chỉ l&agrave; giai đoạn sơ kỳ.<br />\r\n<br />\r\nTiếp theo giai đoạn hai, sẽ k&iacute;ch th&iacute;ch mở ra thi&ecirc;n ph&uacute; khi tr&ugrave;ng k&iacute;ch cảnh giới Ngưng Thể, đa số mọi người sẽ mở ra được Hắc Hỏa. Hắc Hỏa l&agrave; một loại thi&ecirc;n ph&uacute; của Hắc Long tộc, l&agrave; Long tộc nhưng thuộc loại t&agrave; &aacute;c, &acirc;m l&atilde;nh, kh&ocirc;ng phải l&agrave; huyết mạnh nhất của Long tộc. Huyết mạch mạnh nhất trong Long tộc l&agrave; Ho&agrave;ng Kim Long Tộc. Hắc Hỏa ngọn lửa &acirc;m l&atilde;nh, m&agrave;u đen c&oacute; thể s&aacute;nh ngang với thi&ecirc;n cấp dị hỏa, mười vị đầu.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nGiai đoạn n&agrave;y, tương truyền rằng đ&atilde; c&oacute; đệ tử mở ra thần th&ocirc;ng Hắc Sắc Hỏa, c&oacute; thể thi&ecirc;u đốt mọi thứ, cắn n&aacute;t ph&ograve;ng ngự. Đặc biệt n&oacute; c&oacute; thể chuyển h&oacute;a hỏa diễm, bao quanh cả người tu sĩ trong một m&eacute;t, vừa n&acirc;ng cao lực ph&ograve;ng ngự, cận chiến c&agrave;ng thi&ecirc;u đốt uy lực mạnh mẽ hơn. Ngọn lửa n&agrave;y vẫn l&agrave; m&agrave;u đen, nhưng b&ecirc;n trong &aacute;nh l&ecirc;n m&agrave;u v&agrave;ng sắc b&eacute;n. Mạnh hơn nhiều so với Hắc Hỏa th&ocirc;ng thường, thuộc về dị biến Hắc Hỏa.<br />\r\n<br />\r\nGiai đoạn cuối c&ugrave;ng, ch&iacute;nh l&agrave; khi tr&ugrave;ng k&iacute;ch cảnh giới H&oacute;a Ch&acirc;n kỳ, đến bước n&agrave;y cần phải c&oacute; huyết mạch tinh thuần cực kỳ, một khi k&iacute;ch hoạt được giai đoạn n&agrave;y, th&igrave; c&oacute; thể được coi l&agrave; Tượng Long h&igrave;nh người.<br />\r\n<br />\r\nĐể n&oacute;i r&otilde; hơn th&igrave; đ&acirc;y l&agrave; qu&aacute; tr&igrave;nh thăng hoa huyết mạch, l&agrave;m cho thể chất tiến gần với Kim Long nhất tộc, c&oacute; được năng lực đặc th&ugrave;, c&oacute; thể mở ra thần th&ocirc;ng một trong Kim L&acirc;n Gi&aacute;p, ch&iacute;nh l&agrave; tạo th&agrave;nh một lớp &aacute;o gi&aacute;p bằng vẩy Hắc Long nhất tộc, c&oacute; lực ph&ograve;ng ngự gần như tuyệt đối trong c&ugrave;ng cảnh giới.<br />\r\n<br />\r\nThế n&ecirc;n cảnh giới H&oacute;a Ch&acirc;n trong tộc n&agrave;y, ch&iacute;nh l&agrave; thuộc về đỉnh cấp chiến lực của đại lục, đ&atilde; c&oacute; một c&aacute;i kỷ nguy&ecirc;n ch&iacute;nh l&agrave; Thể Long nhất tộc xưng b&aacute; đại lục. Đ&oacute; c&oacute; thể thấy rằng l&agrave; một tộc đ&aacute;ng sợ m&agrave; hiếu chiến.<br />\r\n<br />\r\nNhưng ch&iacute;nh v&igrave; sự cao ngạo đ&oacute;, m&agrave; mấy ng&agrave;n năm trước chọc phải t&ecirc;n Sở Cửu Vũ, trước khi hắn phi thăng đ&atilde; dạo qua một v&ograve;ng tộc n&agrave;y, kh&ocirc;ng biết bao nhi&ecirc;u cường giả ng&atilde; xuống, sau đ&oacute; phải đ&oacute;ng lại vực giới, đến nay chưa chắc đ&atilde; hồi phục nguy&ecirc;n kh&iacute;.<br />\r\n<br />\r\nC&oacute; lẽ l&agrave; khoảng một ng&agrave;n năm trước tộc n&agrave;y, đ&atilde; từng mở ra một lần nhưng vẫn rất cao ngạo, bởi v&igrave; c&oacute; li&ecirc;n quan tới người kia, n&ecirc;n Bắc tộc l&agrave; đối tượng Thể Long g&acirc;y chuyện.<br />\r\n<br />\r\nNhưng m&agrave; trải qua kiếp nạn đ&oacute;, th&igrave; Thể Long tộc đ&acirc;u c&ograve;n l&agrave; đối thủ của một Bắc tộc vẫn ph&aacute;t triển mạnh mẽ. Bị thế hệ trẻ của Bắc tộc nghiền &eacute;p kh&ocirc;ng thở nổi, đ&agrave;nh phải đ&oacute;ng giới vực một lần nữa.<br />\r\n<br />\r\nC&oacute; thể n&oacute;i b&acirc;y giờ thực lực chỉ bằng Cửu đại thế lực bề nổi của đại lục. Chẳng qua tộc n&agrave;y vẫn c&ograve;n trấn ph&aacute;i trận ph&aacute;p nửa bước ti&ecirc;n giới, n&ecirc;n rất kh&oacute; tộc n&agrave;o diệt được.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nN&oacute;i tới trận ph&aacute;p th&igrave; cả ba tộc, đều l&agrave; truyền thừa từ ti&ecirc;n giới c&aacute;c tộc lưu truyền xuống.<br />\r\n<br />\r\nBởi v&igrave; thế n&ecirc;n d&ugrave; h&ocirc;m nay, Bắc tộc c&oacute; dị tượng nhưng m&agrave; Thể Long nhất tộc kh&ocirc;ng d&aacute;m ra ngo&agrave;i d&ograve; x&eacute;t.<br />\r\n<br />\r\nCũng v&igrave; ba tộc đều c&oacute; giới vực ri&ecirc;ng, n&ecirc;n tr&ecirc;n đại lục rất kh&oacute; ph&aacute;t hiện sự hiện diện, cũng chỉ c&oacute; c&aacute;c cao tầng của cửu đại thế lực hoặc c&aacute;c đại năng cường giả biết một ch&uacute;t .<br />\r\n<br />\r\nĐặc biệt l&agrave; một trong cửu tinh thế lực c&oacute; quan hệ gần với Bắc tộc, đ&oacute; l&agrave; thế lực của mẫu th&acirc;n Bắc Tiểu Lục, biết r&otilde; nội t&igrave;nh một hai của Bắc tộc.<br />\r\n<br />\r\nC&ograve;n lại c&aacute;c thế lực kh&aacute;c, mặc d&ugrave; ki&ecirc;ng kị nhưng cũng coi thường, chỉ v&igrave; ba thế lực n&agrave;y rất &iacute;t ra mặt ở Tu ch&acirc;n giới, họ cũng chỉ coi l&agrave; truyền thừa cổ xưa ch&uacute;t th&ocirc;i.<br />\r\n<br />\r\nĐặc biệt l&agrave; sự kiện của Thể Long nhất tộc, bị một cường giả su&yacute;t diệt tộc đ&atilde; dấy l&ecirc;n sự ho&agrave;i nghi về nội t&igrave;nh của c&aacute;c tộc.<br />\r\n<br />\r\nCho d&ugrave; l&agrave; trước kia từng c&oacute; mấy thế lực đỉnh cấp, từng bị Bắc tộc diệt họ cũng chỉ nghĩ do Sở Cửu Vũ l&agrave;m.</p>', '2021-11-30 21:02:39', 10, 1),
(251, 68, 'Một Ngày Bình Thường Và Bất Thường Xảy Ra.', '1', '<p>Trong một th&ocirc;n x&oacute;m nhỏ, nơi y&ecirc;n b&igrave;nh v&agrave; lặng lẽ v&agrave;o mỗi buổi chiều tối. D&ugrave; đ&acirc;y kh&ocirc;ng phải l&agrave; một l&agrave;ng qu&ecirc; lạc hậu, ngh&egrave;o t&uacute;ng nhưng mọi người sống ở đ&acirc;y đều rất giản dị.<br />\r\n<br />\r\nVẫn c&oacute; những c&aacute;nh đồng rộng lớn chiếm đa số diện t&iacute;ch, hoạt động n&ocirc;ng nghiệp th&igrave; phải n&oacute;i l&agrave; tấp nập v&ocirc; c&ugrave;ng.<br />\r\n<br />\r\nV&agrave; tại một ng&ocirc;i nh&agrave; hai tầng cũ kĩ ở giữa l&agrave;ng, c&oacute; một gia đ&igrave;nh nhỏ đang trải qua những ng&agrave;y th&aacute;ng &acirc;m trầm lặng lẽ.<br />\r\n<br />\r\nTrong nh&agrave;, cặp vợ chồng gi&agrave; nu&ocirc;i ba đứa con hai g&aacute;i một trai. Hai người con g&aacute;i đ&atilde; lớn, c&oacute; thể tự lo cho bản th&acirc;n. C&ograve;n người con trai th&igrave; c&ograve;n nhỏ, mới học đến lớp năm, cậu được coi l&agrave; cậu ấm trong nh&agrave;, nhận hết mọi sự nu&ocirc;ng chiều.<br />\r\n<br />\r\nTrong căn ph&ograve;ng nhỏ chỉ chứa đủ một chiếc giường v&agrave; một chiếc b&agrave;n học của cậu con trai, l&uacute;c n&agrave;y cậu đang lặng lẽ ngồi học b&agrave;i như b&igrave;nh thường.<br />\r\n<br />\r\nGiờ đ&atilde; l&agrave; buổi tối, đến s&aacute;ng h&ocirc;m sau cậu lại tiếp tục cắp s&aacute;ch đến trường học.<br />\r\n<br />\r\nC&aacute;nh cửa sổ mở h&eacute; ra, cơn gi&oacute; lạnh cuối thu thổi v&agrave;o khiến trang vở hơi lật l&ecirc;n một ch&uacute;t. Cậu trai đứng dậy, với tay ra định đ&oacute;ng k&iacute;n cửa sổ lại th&igrave; bất chợt, b&ecirc;n ngo&agrave;i vườn l&oacute;e l&ecirc;n &aacute;nh s&aacute;ng nhỏ v&uacute;t qua.<br />\r\n<br />\r\n&ldquo; Hửm, l&agrave; đom đ&oacute;m &agrave;? HIếm khi tr&ocirc;ng thấy thật đấy.&rdquo; Cậu trai n&oacute;i với v&agrave;i phần kinh ngạc, nhưng cũng lập tức đ&oacute;ng c&aacute;nh cửa lại.<br />\r\n<br />\r\nM&agrave; ngay tại l&uacute;c đ&oacute;, b&ecirc;n ngo&agrave;i vườn nh&agrave; cậu, &aacute;nh s&aacute;ng kia lại hiện l&ecirc;n, một đ&ocirc;i mắt xanh l&egrave; hướng về c&aacute;nh cửa sổ nh&igrave;n chằm chằm thật l&acirc;u, rồi lập tức lại biến mất.<br />\r\n<br />\r\nĐến s&aacute;ng sớm h&ocirc;m sau, như thường ng&agrave;y, cậu trai dậy từ s&aacute;u giờ s&aacute;ng rồi chuẩn bị sinh hoạt, ăn s&aacute;ng sau đ&oacute; đến trường.<br />\r\n<br />\r\nTại nh&agrave; cậu, l&uacute;c n&agrave;o cũng c&oacute; một loại kh&ocirc;ng kh&iacute; y&ecirc;n tĩnh. Người trong gia đ&igrave;nh cũng rất &iacute;t n&oacute;i chuyện với nhau, cậu trai tuy nhỏ tuổi nhưng cũng đ&atilde; quen rồi.<br />\r\n<br />\r\nNh&agrave; cậu thuộc dạng kh&ocirc;ng gi&agrave;u m&agrave; cũng chả ngh&egrave;o, n&oacute;i vậy nhưng chi ti&ecirc;u mọi thứ th&igrave; đều rất tiết kiệm. Đặc biệt l&agrave; mẹ của cậu, l&agrave; một người tiết kiệm v&ocirc; c&ugrave;ng, v&agrave; cũng đồng thời l&agrave;m việc rất nhiều.<br />\r\n<br />\r\nBan ng&agrave;y ra đồng l&agrave;m, đến tối về ăn cơm vội sau đ&oacute; lại l&agrave;m th&ecirc;m c&ocirc;ng việc may v&aacute; đến tận khuya.<br />\r\n<br />\r\nCậu trai tuy biết mẹ m&igrave;nh cực nhọc nhưng lại kh&ocirc;ng biết phải l&agrave;m như thế n&agrave;o, cậu chỉ biết đứng quan s&aacute;t từ xa rồi suy nghĩ lan man. Bởi cậu b&igrave;nh thường cũng chẳng hay n&oacute;i chuyện với mẹ cho lắm, đ&uacute;ng hơn l&agrave; cậu cho rằng mẹ v&agrave; m&igrave;nh kh&ocirc;ng hiểu nhau, mỗi lần n&oacute;i chuyện lại l&agrave; một trận c&atilde;i nhau kh&ocirc;ng dẫn đến kết quả g&igrave; hay ho.<br />\r\n<br />\r\nC&ograve;n người bố th&igrave; phải đi l&agrave;m thợ x&acirc;y, xa nh&agrave; thường xuy&ecirc;n n&ecirc;n cũng c&agrave;ng xa c&aacute;ch hơn. Người bố t&iacute;nh hay c&aacute;u gắt, mắng mỏ gia đ&igrave;nh mỗi khi kh&oacute; chịu. Nhưng trong nh&agrave; ai cũng biết rằng &ocirc;ng lại l&agrave; người t&igrave;nh cảm nhất, lu&ocirc;n chăm lo cho gia đ&igrave;nh.<br />\r\n<br />\r\nNhững nỗi vất vả khi đi l&agrave;m thợ x&acirc;y ở v&ugrave;ng đất kh&aacute;ch qu&ecirc; người đ&acirc;u phải ai cũng hiểu được. Cậu trai cũng rất hiểu người bố, l&agrave; người m&agrave; cậu hết mực k&iacute;nh trọng, y&ecirc;u thương.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nNhưng tất cả cảm x&uacute;c đ&oacute; cậu chỉ giữ ở trong l&ograve;ng m&agrave; th&ocirc;i, đ&acirc;y ch&iacute;nh l&agrave; một thứ r&agrave;o cản v&ocirc; h&igrave;nh tồn tại trong l&ograve;ng cậu.<br />\r\n<br />\r\n<br />\r\nDắt xe đạp ra trước s&acirc;n, cậu trai nhảy l&ecirc;n v&agrave; đạp, miệng kh&ocirc;ng qu&ecirc;n c&acirc;u ch&agrave;o &ldquo; Ch&agrave;o bố mẹ con đi học!&rdquo; .<br />\r\n<br />\r\n&ldquo; Đi đường cẩn thận nh&eacute; con! Nhớ học h&agrave;nh nghi&ecirc;m t&uacute;c đấy!&rdquo; Mẹ l&ecirc;n tiếng đ&aacute;p trả giống như h&agrave;ng ng&agrave;y.<br />\r\n<br />\r\nC&oacute; lẽ đ&acirc;y l&agrave; những gi&acirc;y ph&uacute;t hiếm hoi m&agrave; hai mẹ con n&oacute;i chuyện với nhau.<br />\r\n<br />\r\nCậu trai đi tr&ecirc;n đường với t&acirc;m thế hứng khởi cho một ng&agrave;y học mới, gương mặt tươi cười vui vẻ, miệng lẩm bẩm lời b&agrave;i h&aacute;t m&agrave; cậu th&iacute;ch.<br />\r\n<br />\r\n&ldquo; N&agrave;y Minh, ng&agrave;y n&agrave;o cũng đi sớm nhể?&rdquo; Tiếng gọi cậu trai vang l&ecirc;n từ ph&iacute;a sau, c&oacute; hai chiếc xe đạp kh&aacute;c đang ph&oacute;ng tới, l&agrave; hai người bạn của cậu.<br />\r\n<br />\r\nCậu trai t&ecirc;n l&agrave; Minh, học sinh lớp 5B trường Tiểu học Đồng X&aacute;.<br />\r\n<br />\r\n&ldquo; Ng&agrave;y n&agrave;o tao chả đi giờ n&agrave;y.&rdquo; Minh đ&aacute;p lại.<br />\r\n<br />\r\n&ldquo; Minh n&agrave;y, vừa rồi b&agrave;i kiểm tra m&agrave;y được mấy ấy nhỉ? Tao được c&oacute; s&aacute;u điểm thế l&agrave; bố mẹ mắng cho từ qua đến giờ.&rdquo; Cậu bạn b&ecirc;n cạnh t&ecirc;n Việt hỏi.<br />\r\n<br />\r\n&ldquo; Tao được 10 điểm, b&agrave;i đ&oacute; với tao cũng dễ.&rdquo; Minh thong dong trả lời, vốn dĩ cậu l&agrave; học sinh giỏi top đầu trường n&ecirc;n kh&ocirc;ng ai lấy l&agrave;m k&igrave; qu&aacute;i cả.<br />\r\n<br />\r\n&ldquo; Haizz, chả b&ugrave; cho tao, thi m&atilde;i m&agrave; vẫn duy tr&igrave; con 8, chắc do số trời định rồi.&rdquo; Cậu bạn kh&aacute;c t&ecirc;n Dũng than thở.<br />\r\n<br />\r\n&ldquo; Th&ocirc;i đi, ch&uacute;ng m&agrave;y cứ cố gắng hết sức l&agrave; được. Bọn m&igrave;nh vẫn c&ograve;n nhỏ lắm, vui chơi đi, lo g&igrave;.&rdquo; Minh cười n&oacute;i. Thực chất ở trường th&igrave; cậu l&agrave; học sinh kh&aacute; l&agrave; s&ocirc;i nổi v&agrave; quậy ph&aacute;, giống như c&oacute; hai t&iacute;nh c&aacute;ch kh&aacute;c nhau vậy.<br />\r\n<br />\r\nCả ba cười đ&ugrave;a vừa đi đến trường, con đường k&eacute;o d&agrave;i qua c&aacute;nh đồng, khu chợ tập trung của x&atilde;.<br />\r\n<br />\r\nNg&ocirc;i trường v&ugrave;ng qu&ecirc; nh&igrave;n kh&aacute; l&agrave; đơn sơ, kh&ocirc;ng hề c&oacute; những thiết bị hiện đại hỗ trợ việc học v&agrave; an ninh. Những thứ như mạng internet hay m&aacute;y t&iacute;nh th&igrave; vẫn c&oacute; nhưng chủ yếu l&agrave; gi&aacute;o vi&ecirc;n sử dụng.<br />\r\n<br />\r\n&ldquo; T&ugrave;ng t&ugrave;ng t&ugrave;ng!&rdquo; Tiếng trống trường vang l&ecirc;n, học sinh n&ocirc; nức chạy vội v&agrave;o lớp sợ bị cờ đỏ bắt lại.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nMinh cũng chia tay hai người bạn để bước v&agrave;o lớp học của m&igrave;nh, họ kh&ocirc;ng học c&ugrave;ng một lớp.<br />\r\n<br />\r\nLại bắt đầu với những m&ocirc;n học ch&aacute;n nản v&agrave; gi&aacute;o vi&ecirc;n cứng ngắc, Minh ng&aacute;p suốt giờ học, chỉ mong đợi những giờ ra chơi v&agrave; giờ về nh&agrave;. Đ&ocirc;i với Minh th&igrave; việc học l&agrave; c&aacute;i g&igrave; đấy rất mệt nhọc, mặc d&ugrave; cậu học giỏi đấy nhưng n&oacute; cũng kh&ocirc;ng gi&uacute;p cho tinh thần cậu tăng cao ch&uacute;t n&agrave;o.<br />\r\n<br />\r\nBuổi học nhanh ch&oacute;ng tr&ocirc;i qua, Minh lại c&ugrave;ng c&aacute;c bạn đạp xe trở về nh&agrave; nghỉ ngơi, lấy sức chiến đấu tiếp v&agrave;o buổi chiều.<br />\r\n<br />\r\n&ldquo; Con ch&agrave;o mẹ!&rdquo; Về đến nh&agrave;, Minh dựng xe dưới gốc c&acirc;y trước s&acirc;n, h&ocirc; l&ecirc;n ch&agrave;o mẹ rồi bước v&agrave;o nh&agrave;.<br />\r\n<br />\r\nMinh thấy mẹ m&igrave;nh đang loay hoay sắp xếp đồ đạc th&igrave; bất chợt hỏi: &ldquo; Mẹ chuẩn bị đi ch&ugrave;a hả?&rdquo;<br />\r\n<br />\r\n&ldquo; Ừ, lần n&agrave;y đi xa n&ecirc;n mẹ phải đi từ tối nay, con chịu kh&oacute; ở nh&agrave; một m&igrave;nh nh&eacute;!&rdquo; Mẹ bảo,<br />\r\n<br />\r\n&ldquo; V&acirc;ng&hellip;!&rdquo; Minh trả lời, sau đ&oacute; quay đi v&agrave;o b&agrave;n ăn cơm.<br />\r\n<br />\r\nViệc n&agrave;y cũng thường xuy&ecirc;n xảy ra, mẹ cậu l&agrave; người kh&aacute; l&agrave; t&iacute;n n&ecirc;n thường xuy&ecirc;n theo đo&agrave;n đi đến c&aacute;c ng&ocirc;i ch&ugrave;a c&uacute;ng b&aacute;i.<br />\r\n<br />\r\nMinh cũng hay ở nh&agrave; một m&igrave;nh n&ecirc;n quen rồi, ngược lại đ&acirc;y lại l&agrave; dịp hiếm hoi m&agrave; cậu mong chờ. Bởi chỉ khi ở một m&igrave;nh th&igrave; cậu mới c&oacute; cảm gi&aacute;c thoải m&aacute;i tự do nhất, l&agrave;m những điều m&igrave;nh th&iacute;ch m&agrave; kh&ocirc;ng ai n&oacute;i g&igrave; được.<br />\r\n<br />\r\n<br />\r\n&ldquo; Tối nay n&ecirc;n l&agrave;m g&igrave; đ&acirc;y? Hay l&agrave; mua mấy g&oacute;i b&aacute;nh về vừa ăn vừa xem phim nhỉ? Quyết vậy đi!&rdquo; Minh vừa ăn cơm vừa nghĩ cho kế hoạch tối nay.<br />\r\n<br />\r\nMinh để gi&agrave;nh một &iacute;t tiền ri&ecirc;ng l&agrave; chuẩn bị cho những t&igrave;nh huống như vậy, mặc d&ugrave; nh&agrave; cậu th&igrave; kh&ocirc;ng ai cho ph&eacute;p như vậy cả.<br />\r\n<br />\r\nĐến chiều, khi Minh đi học về, trong cặp s&aacute;ch chứa to&agrave;n b&aacute;nh kẹo v&agrave; nước ngọt. Cậu vẫn ch&agrave;o mẹ v&agrave; mang ch&uacute;ng v&agrave;o nh&agrave; như b&igrave;nh thường, mẹ cậu th&igrave; đ&atilde; chuẩn bị đi rồi.<br />\r\n<br />\r\n&ldquo; Mẹ đi nh&eacute;, con ở nh&agrave; kh&oacute;a cửa cẩn thận đấy kẻo trộm n&oacute; v&agrave;o l&agrave; dở!&rdquo; Mẹ Minh nhắc nhở sau đ&oacute; x&aacute;ch t&uacute;i đồ l&ecirc;n, đi ra cổng đợi xe.<br />\r\n<br />\r\nMinh vẻ mặt thản nhi&ecirc;n c&ograve;n trong l&ograve;ng th&igrave; hứng khởi, sắp đến lức để cho cậu x&otilde;a rồi.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nĐến tối, tắm rửa xong xu&ocirc;i đ&acirc;u đ&oacute;, Minh ăn cơm rồi rửa b&aacute;t, qu&eacute;t nh&agrave; như b&igrave;nh thường. Sau đ&oacute; ra kh&oacute;a cổng, rồi vội chạy v&agrave;o nh&agrave; đ&oacute;ng cửa tắt điện, bật tivi l&ecirc;n.<br />\r\n<br />\r\n&ldquo; Mười ph&uacute;t nữa l&agrave; chiếu phim rồi, sắp đồ ăn ra l&agrave; vừa, h&ecirc; h&ecirc;.&rdquo; Minh vui vẻ lấy ra đồ ăn trong chiếc cặp s&aacute;ch, b&agrave;y ra b&agrave;n trước mặt. Chuẩn bị một chiếc gối để nằm k&ecirc; lưng cho đỡ mỏi.<br />\r\n<br />\r\n&ldquo; R&ocirc;m rốp&hellip;&rdquo; Tiếng nhai b&aacute;nh gi&ograve;n tan vang l&ecirc;n, bộ phim đ&atilde; đươc chiếu.<br />\r\n<br />\r\nMải m&ecirc; xem đến tận 11 giờ đ&ecirc;m, Minh đ&atilde; ngủ l&uacute;c n&agrave;o kh&ocirc;ng hay, để tivi vẫn c&ograve;n bật.<br />\r\n<br />\r\nNhưng đ&uacute;ng l&uacute;c n&agrave;y, m&agrave;n h&igrave;nh bỗng nhiễu đi, chập chờn như bị hỏng. Trong g&oacute;c tối cạnh đ&oacute;, đ&ocirc;i mắt xanh l&egrave; lại hiện ra nh&igrave;n chằm chằm v&agrave;o Minh giống như t&ograve; m&ograve; g&igrave; đ&oacute;.<br />\r\n<br />\r\n&ldquo; Hửm, tivi sao thế n&agrave;y? Hỏng &agrave;?&rdquo; Minh bỗng giật m&igrave;nh tỉnh dậy, mắt đảo xung quanh một c&aacute;ch mơ m&agrave;ng v&agrave; v&ocirc; t&igrave;nh, cậu đ&atilde; nh&igrave;n thấy đ&ocirc;i mắt trong b&oacute;ng tối.<br />\r\n<br />\r\n&ldquo; C&aacute;i g&igrave; kia? M&igrave;nh vẫn mơ ngủ &agrave;?&rdquo; Minh ngạc nhi&ecirc;n n&oacute;i.<br />\r\n<br />\r\nĐ&ocirc;i mắt kia cũng cảm nhận được &aacute;nh nh&igrave;n từ Minh, n&oacute; vội quay đầu chạy đi nhưng đ&atilde; muộn. Kh&ocirc;ng gian trong nh&agrave; bỗng s&aacute;ng l&ecirc;n, chiếu v&agrave;o nơi đ&ocirc;i mắt đang đứng.<br />\r\n<br />\r\n&Acirc;m thanh g&agrave;o th&eacute;t nhỏ b&eacute; vang l&ecirc;n chốc l&aacute;t rồi vụt tắt, sinh vật đen thui hiện ra trong khoảnh khắc khi chạm v&agrave;o &aacute;nh đ&egrave;n điện do Minh bật l&ecirc;n th&igrave; đ&atilde; tan biến, chỉ c&ograve;n lại hai con mắt xanh l&egrave; h&oacute;a th&agrave;nh hai vi&ecirc;n bi bằng ng&oacute;n ch&acirc;n c&aacute;i lăn l&oacute;c dưới s&agrave;n nh&agrave;.<br />\r\n<br />\r\n&ldquo; Chuột &agrave;? N&oacute; tha c&aacute;i g&igrave; đi ấy nhỉ? &ldquo; Minh hiếu k&igrave; tiến lại gần, đưa tay nhặt l&ecirc;n hai vi&ecirc;n bi xanh chăm ch&uacute; đ&aacute;nh gi&aacute;.<br />\r\n<br />\r\n&ldquo; Ồ, hai vi&ecirc;n bi đẹp qu&aacute;, b&ecirc;n trong c&ograve;n c&oacute; h&igrave;nh giống như mấy tia điện n&agrave;y.&rdquo;<br />\r\n<br />\r\nĐột nhi&ecirc;n, hai con mắt ph&aacute;t s&aacute;ng khi m&agrave; Minh thử đưa ch&uacute;ng l&ecirc;n mắt m&igrave;nh để nh&igrave;n kĩ hơn. Rồi cậu cảm thấy cho&aacute;ng v&aacute;ng, kh&ocirc;ng gian xung quanh cậu bỗng trở n&ecirc;n k&igrave; lạ, c&oacute; thế thấy đươc sự u &aacute;m của n&oacute; khiến Minh ớn lạnh cả người.<br />\r\n<br />\r\nSợ h&atilde;i qu&aacute; mức, Minh ngất đi ngay lập tức, m&agrave; hai con mắt kia cũng bằng một c&aacute;ch k&igrave; lạ n&agrave;o đ&oacute; m&agrave; tan biến th&agrave;nh những hạt bụi s&aacute;ng rồi chui v&agrave;o mắt cậu.<br />\r\n<br />\r\nV&agrave; từ đ&acirc;y, Minh bắt đầu dần dần tiếp x&uacute;c với những thứ m&agrave; cậu kh&ocirc;ng hề biết đến, mở ra cuộc đời kh&ocirc;ng hề nh&agrave;m ch&aacute;n m&agrave; cậu mong muốn nhưng cũng phải đ&aacute;nh đổi rất nhiều.</p>', '2021-11-30 21:06:40', 9, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(252, 68, 'Một Ngày Kì Lạ, Những Sinh Vật Kì Lạ.', '2', '<p>S&aacute;ng ng&agrave;y h&ocirc;m sau, v&igrave; l&agrave; chủ nhật n&ecirc;n Minh kh&ocirc;ng phải đi học. Cậu bất chợt tỉnh dậy, ng&oacute; nghi&ecirc;ng xung quanh, gương mặt c&oacute; ch&uacute;t căng thẳng.<br />\r\n<br />\r\n&ldquo; H&ocirc;m qua sao thế nhỉ? Hai vi&ecirc;n bi kia đ&acirc;u mất rồi?&rdquo; Minh tự hỏi.<br />\r\n<br />\r\nCậu cảm gi&aacute;c thấy đầu m&igrave;nh t&ecirc; dại, với một đứa trẻ th&igrave; cơn đau n&agrave;y rất mạnh. Đầu Minh như muốn nổ tung ra, khung cảnh trước mắt trở n&ecirc;n như một m&agrave;n h&igrave;nh bị nhiễu, &acirc;m thanh xẹt xẹt vang l&ecirc;n trong n&atilde;o.<br />\r\n<br />\r\n&ldquo; Aaaa.. đau qu&aacute;!!&rdquo; Minh &ocirc;m đầu k&ecirc;u la. Bởi v&igrave; hiện tại kh&ocirc;ng c&oacute; ai ở nh&agrave; n&ecirc;n cậu kh&ocirc;ng nhận được gi&uacute;p đỡ g&igrave; cả.<br />\r\n<br />\r\nMột l&uacute;c sau, Minh đ&atilde; đau đến rơi nước mắt, d&ugrave; sao th&igrave; cũng chỉ l&agrave; một đứa trẻ con mười tuổi. Minh nằm so&agrave;i ra nền nh&agrave;, th&acirc;n h&igrave;nh quằn quại như sắp chết.<br />\r\n<br />\r\nV&agrave; trong l&uacute;c đ&oacute;, t&acirc;m thần của Minh hiện ra tại một kh&ocirc;ng gian lạ lẫm. Một nơi tối tăm, u &aacute;m đến đ&aacute;ng sợ, &acirc;m thanh gi&oacute; r&iacute;t g&agrave;o vang vọng thật l&acirc;u.<br />\r\n<br />\r\n&ldquo; Hả, nơi n&agrave;o đ&acirc;y? Kh&ocirc;ng lẽ m&igrave;nh chết rồi sao? Chết m&agrave; kh&ocirc;ng biết l&iacute; do?&rdquo; Minh bắt đầu run rẩy sợ h&atilde;i nh&igrave;n xung quanh n&oacute;i.<br />\r\n<br />\r\nĐột nhi&ecirc;n, từ dưới ch&acirc;n cạnh nơi cậu đứng bắt đầu nh&ocirc; l&ecirc;n một cột trụ lớn, Minh thấy cột trụ n&agrave;y &iacute;t nhất cũng phải như một t&ograve;a nh&agrave; cao nhất thế giới m&agrave; cậu nh&igrave;n qua tivi vậy.<br />\r\n<br />\r\n&ldquo; N&agrave;y l&agrave; sao đ&acirc;y trời?&rdquo; Minh c&agrave;ng l&uacute;c c&agrave;ng hoảng hồn, ng&atilde; bệt xuống.<br />\r\n<br />\r\nCột trụ trong suốt như pha l&ecirc;, tỏa ra loại &aacute;nh s&aacute;ng ch&oacute;i lọi khiến Minh cảm thấy bớt đi phần n&agrave;o sợ h&atilde;i.<br />\r\n<br />\r\n&ldquo; Hửm, cảm gi&aacute;c như c&aacute;i trụ n&agrave;y l&agrave; một phần của m&igrave;nh th&igrave; phải?&rdquo; Minh bất chợt nhận ra điều k&igrave; lạ.<br />\r\n<br />\r\nTrong s&acirc;u thẳm linh hồn cậu cảm nhận được sự tồn tại của c&ocirc;t trụ pha l&ecirc; n&agrave;y, cảm gi&aacute;c c&oacute; một sợi d&acirc;y li&ecirc;n kết giữa hai b&ecirc;n với nhau.<br />\r\n<br />\r\nBỗng nhi&ecirc;n, cột trụ s&aacute;ng l&ecirc;n, hai vi&ecirc;n bi m&agrave; Minh nhặt được tối h&ocirc;m trước đột nhi&ecirc;n xuất hiện lao về ph&iacute;a cột trụ.<br />\r\n<br />\r\n&ldquo; Ầm&hellip;&rdquo; Rung động mạnh mẽ, cột trụ bị k&iacute;ch th&iacute;ch ph&aacute;t ra &aacute;nh s&aacute;ng đen ng&ograve;m bao phủ xung quanh.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nNgay cả Minh l&uacute;c n&agrave;y cũng đơ người, vừa rồi ch&iacute;nh cậu cũng cảm nhận được hai vi&ecirc;n bi kia dung v&agrave;o l&agrave;m một với cơ thể. Cảm gi&aacute;c lạ lẫm khiến Minh kh&ocirc;ng biết diễn tả ra sao.<br />\r\n<br />\r\n&ldquo; Đ&acirc;y l&agrave; mơ sao? Nhưng m&igrave;nh chưa bao giờ c&oacute; giấc mơ ch&acirc;n thực như thế n&agrave;y. Hay l&agrave; do xem nhiều phim qu&aacute; rồi?&rdquo; Minh tự vấn bản th&acirc;n, cố hướng suy nghĩ theo c&aacute;ch b&igrave;nh thường nhất c&oacute; thể.<br />\r\n<br />\r\nNhưng ngay l&uacute;c đ&oacute;, đầu Minh lại vang l&ecirc;n &acirc;m thanh k&igrave; lạ, kh&ocirc;ng gian xung quanh bắt đầu mờ dần v&agrave; cậu bắt đầu thấy những cảnh tượng quen thuộc nơi nh&agrave; m&igrave;nh.<br />\r\n<br />\r\nTrong gi&acirc;y ph&uacute;t cuối đ&oacute;, Minh c&oacute; lướt qua cột trụ v&agrave; thấy được hai chữ [Khởi Nguy&ecirc;n].<br />\r\n<br />\r\n&ldquo; &Ocirc;i c&aacute;i đầu t&ocirc;i, đau chết mất!&rdquo; Minh giật m&igrave;nh tỉnh lại, xem x&eacute;t thời gian th&igrave; mới qua v&agrave;i ph&uacute;t từ l&uacute;c ch&igrave;m v&agrave;o giấc mơ kia.<br />\r\n<br />\r\n&ldquo; K&igrave; lạ nhỉ, sao mơ g&igrave; m&agrave; lại thực thế? Chắc bớt xem tivi lại một ch&uacute;t th&ocirc;i.&rdquo; Minh lắc lắc đầu ngao ng&aacute;n, con đau vẫn c&ograve;n tồn đọng lại ch&uacute;t &iacute;t.<br />\r\n<br />\r\nCậu bắt đầu đứng l&ecirc;n dọn dẹp nh&agrave; cửa, ng&ocirc;i nh&agrave; l&uacute;c n&agrave;y bừa bộn đủ thứ vụn b&aacute;nh rơi v&atilde;i, bốc m&ugrave;i kinh khủng. Minh chắc chắn kh&ocirc;ng thể để ai thấy được cảnh n&agrave;y, cậu l&agrave; người kh&aacute; quan t&acirc;m đến đ&aacute;nh gi&aacute; của người kh&aacute;c.<br />\r\n<br />\r\n&ldquo; Haizzz, biết thế h&ocirc;m qua rủ bọn kia chơi b&agrave;i cho rồi, c&oacute; khi kh&ocirc;ng bị đau đầu như n&agrave;y.&rdquo; Minh tiếp tục vừa than v&atilde;n vừa qu&eacute;t nh&agrave;.<br />\r\n<br />\r\nMột tiếng sau, trải qua qu&aacute; tr&igrave;nh qu&eacute;t dọn v&agrave; lau th&igrave; ng&ocirc;i nh&agrave; trở lại d&aacute;ng vẻ sạch b&oacute;ng của n&oacute;. Minh thở ph&agrave;o ngồi phịch xuống ghế, uống một cốc nước lớn rồi nhắm mắt lại suy nghĩ.<br />\r\n<br />\r\nTừ n&agrave;y đến giờ, cậu cứ c&oacute; cảm gi&aacute;c kh&ocirc;ng đ&uacute;ng lắm. Giấc mơ kia qu&aacute; ch&acirc;n thực khiến MInh kh&ocirc;ng ngừng nghĩ về n&oacute;, m&agrave; c&agrave;ng nghĩ th&igrave; c&agrave;ng cảm thấy n&oacute; thực hơn.<br />\r\n<br />\r\n&ldquo; Hay m&igrave;nh lại bị t&acirc;m thần rồi? Kh&ocirc;ng phải chứ? M&igrave;nh xem tivi cũng đ&acirc;u c&oacute; nhiều đến mức ấy đ&acirc;u?&rdquo; Li&ecirc;n tục c&oacute; những lo lắng hiện ra trong suy nghĩ của Minh khiến cậu bối rối. Ch&iacute;nh những l&uacute;c thế n&agrave;y, Minh lại rất cần đến một người hỏi han quan t&acirc;m m&igrave;nh.<br />\r\n<br />\r\nĐang nhắm mắt suy nghĩ th&igrave; bỗng nhi&ecirc;n, Minh nghe thấy b&ecirc;n ngo&agrave;i c&oacute; &acirc;m thanh k&igrave; lạ vang l&ecirc;n, nghe như l&agrave; c&oacute; ai đ&oacute; đang n&oacute;i chuyện vậy.<br />\r\n<br />\r\nMinh bật dậy, mở cửa chạy ra ngo&agrave;i xem, &aacute;nh mắt nh&igrave;n khắp s&acirc;n sau đ&oacute; dừng lại ở vườn.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&ldquo; Ực..&rdquo; Triệt để đơ người, Minh lẳng lặng nh&igrave;n lấy cảnh tượng trước mắt m&igrave;nh.<br />\r\n<br />\r\n&ldquo; He he, em c&oacute; th&iacute;ch ăn t&aacute;o kh&ocirc;ng? C&acirc;y t&aacute;o trong vườn nh&agrave; n&agrave;y ngon lắm đ&oacute;!&rdquo; Một sinh vật k&igrave; lạ c&oacute; h&igrave;nh d&aacute;ng giống như con ong nhưng lớn bằng nắm tay cười n&oacute;i.<br />\r\n<br />\r\nĐối diện với n&oacute; l&agrave; sinh vật tương tự nhưng tr&ocirc;ng c&oacute; vẻ nhỏ hơn ch&uacute;t x&iacute;u, m&agrave;u l&ocirc;ng cũng lộng lẫy hơn, n&oacute; đ&aacute;p lại đối phương:<br />\r\n<br />\r\n&ldquo; Anh kh&ocirc;ng c&ograve;n g&igrave; cho em ăn hay sao? Chứ mấy quả t&aacute;o n&agrave;y c&oacute; g&igrave; m&agrave; ngon?&rdquo; &Acirc;m thanh c&oacute; vẻ nũng nịu.<br />\r\n<br />\r\n&ldquo; H&igrave; h&igrave;, em cũng biết m&agrave;, ch&uacute;ng ta hiện chỉ đến đ&acirc;y thăm d&ograve; m&agrave; th&ocirc;i. Nếu em muốn th&igrave; khi trở lại qu&ecirc; nh&agrave; anh sẽ mời em ăn bất cứ m&oacute;n g&igrave; em th&iacute;ch, được kh&ocirc;ng?&rdquo;<br />\r\n<br />\r\n<br />\r\n&ldquo; Anh hứa rồi đấy!&rdquo;<br />\r\n<br />\r\nHai sinh vật cứ thoải m&aacute;i tr&ograve; chuyện m&agrave; kh&ocirc;ng hề để &yacute; đến Minh đang nh&igrave;n chằm chằm với &aacute;nh mắt kh&ocirc;ng thể tin nổi.<br />\r\n<br />\r\n&ldquo; Chuyện g&igrave; xảy ra đ&acirc;y? M&igrave;nh xuy&ecirc;n kh&ocirc;ng &agrave;? Hay c&oacute; chương tr&igrave;nh tivi n&agrave;o đang quay ở đ&acirc;y?...&rdquo; Minh l&uacute;c n&agrave;y loạn hết l&ecirc;n rồi, nh&igrave;n trước mắt hai sinh vật m&agrave; kh&ocirc;ng biết n&oacute;i g&igrave; hơn.<br />\r\n<br />\r\n&ldquo; Hửm, anh xem t&ecirc;n con người kia giống như đang nh&igrave;n ch&uacute;ng ta th&igrave; phải?&rdquo; Sinh vật nhỏ với bộ l&ocirc;ng đỏ hồng bỗng n&oacute;i.<br />\r\n<br />\r\n&ldquo; Kh&ocirc;ng thể n&agrave;o đ&acirc;u, chỉ l&agrave; một thằng nh&oacute;c lo&agrave;i người th&ocirc;i. Kẻ nh&igrave;n thấy ch&uacute;ng ta trong khu vực n&agrave;y đ&atilde; bị chỉ huy khử rồi.&rdquo; Sinh vật lớn hơn với bộ l&ocirc;ng v&agrave;ng thản nhi&ecirc;n n&oacute;i, n&oacute; nh&igrave;n Minh với &aacute;nh mắt khinh bỉ.<br />\r\n<br />\r\nC&ograve;n Minh, l&uacute;c n&agrave;y mặt cậu lạnh như băng, kh&ocirc;ng ch&uacute;t cảm x&uacute;c m&agrave; quay người trở v&agrave;o trong nh&agrave;. Đ&oacute;ng chặt cửa lại, c&agrave;i then đ&agrave;ng ho&agrave;ng sau đ&oacute; chui v&agrave;o g&oacute;c nh&agrave; run bần bật.<br />\r\n<br />\r\n&ldquo; Chết..su&yacute;t ch&uacute;t nữa l&agrave; chết rồi. M&igrave;nh đang ở đ&acirc;u thế n&agrave;y, bố ơi, mẹ ơi!!&rdquo; Minh sợ h&atilde;i kh&oacute;c l&oacute;c, ngồi &ocirc;m gối co r&uacute;m kh&ocirc;ng d&aacute;m động đậy.<br />\r\n<br />\r\nChuyện xảy ra vượt ngo&agrave;i tầm hiểu biết của tất cả mọi người chứ đừng n&oacute;i l&agrave; một đứa trẻ như Minh. Cậu kh&ocirc;ng l&agrave;m được g&igrave; nhiều, kh&ocirc;ng c&oacute; bản lĩnh để m&agrave; đối mặt với những sinh vật kia.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nMinh l&uacute;c n&agrave;y đ&atilde; gần như nghĩ bản th&acirc;n bị đi&ecirc;n rồi, chỉ l&agrave; c&ograve;n phần l&iacute; tr&iacute; n&agrave;o đ&oacute; m&aacute;ch bảo rằng cậu vẫn b&igrave;nh thường, thứ cậu nh&igrave;n thấy ho&agrave;n to&agrave;n l&agrave; thật.<br />\r\n<br />\r\nM&agrave; d&ugrave; theo c&aacute;ch n&agrave;o đi chăng nữa th&igrave; Minh vẫn sợ h&atilde;i m&agrave; th&ocirc;i, v&igrave; đ&oacute; l&agrave; điều duy nhất một đứa trẻ c&oacute; thể l&agrave;m.<br />\r\n<br />\r\nCứ thế, Minh ngồi trong g&oacute;c nh&agrave; cho đến tận chiều tối. Nh&igrave;n thấy trời sắp đen lại, Minh định đứng dậy ra bật đ&egrave;n l&ecirc;n nhưng vừa đứng dậy th&igrave; đ&ocirc;i ch&acirc;n bắt đầu run run, cậu kh&ocirc;ng c&oacute; dũng kh&iacute; n&agrave;y.<br />\r\n<br />\r\nTưởng chừng sẽ phải đối mặt với b&oacute;ng tối sắp đến, đột nhi&ecirc;n &acirc;m thanh quen thuộc vang l&ecirc;n.<br />\r\n<br />\r\n&ldquo; Minh ơi, mở cửa đi con. Bố về n&agrave;y!&rdquo; L&agrave; người bố đi l&agrave;m xa của Minh về, c&oacute; lẽ l&agrave; c&ocirc;ng tr&igrave;nh đ&atilde; xong n&ecirc;n được nghỉ một thời gian.<br />\r\n<br />\r\nMinh bỗng cảm thấy đầy sức sống trở lại, đ&ocirc;i ch&acirc;n cứng c&aacute;p hơn bật dậy chạy ra mở cửa. Nh&igrave;n thấy bố m&igrave;nh trước mặt, cậu vỡ &ograve;a trong vui sướng, &ocirc;m trầm lấy &ocirc;ng, nước mắt chảy ra.<br />\r\n<br />\r\n&ldquo; &Ocirc;i trời, mấy tuổi rồi c&ograve;n kh&oacute;c nh&egrave; thế n&agrave;y. Để tối về kể cho mẹ con nghe, xong sau đấy kể cho mấy b&agrave; h&agrave;ng x&oacute;m nh&eacute;, cho mọi người c&ugrave;ng biết lu&ocirc;n, ha ha.&rdquo; &Ocirc;ng bố thấy con kh&oacute;c l&oacute;c &ocirc;m lấy m&igrave;nh trong l&ograve;ng cũng kh&ocirc;ng k&igrave;m được vui sướng, cũng kh&ocirc;ng khỏi tr&ecirc;u đ&ugrave;a v&agrave;i c&acirc;u.<br />\r\n<br />\r\n&ldquo; Hức...bố ơi, vườn nh&agrave; m&igrave;nh h&igrave;nh như c&oacute; ma đấy!&rdquo; Minh kh&ocirc;ng k&igrave;m được n&oacute;i ra.<br />\r\n<br />\r\n&ldquo; N&agrave;o n&agrave;o, ma quỷ g&igrave; ở đ&acirc;y. Chắc ở nh&agrave; một m&igrave;nh n&ecirc;n sợ đ&uacute;ng kh&ocirc;ng?&rdquo; &Ocirc;ng bố vẻ mặt dịu lại n&oacute;i, nghĩ đến việc con ở nh&agrave; một m&igrave;nh thế n&agrave;y khiến &ocirc;ng dần im lặng l&acirc;m v&agrave;o suy nghĩ.<br />\r\n<br />\r\n&ldquo; Th&ocirc;i được rồi, đợi bố đi tắm c&aacute;i đ&atilde;, xong bố con m&igrave;nh c&ugrave;ng nấu cơm đợi mẹ về ăn.&rdquo;<br />\r\n<br />\r\nMinh cuối c&ugrave;ng cũng thả tay ra, để bố đi tắm, c&ograve;n m&igrave;nh th&igrave; b&igrave;nh tĩnh trở lại, tay cầm điều khiển mở tivi l&ecirc;n theo th&oacute;i quen, nhưng chợt cậu nghĩ g&igrave; đ&oacute; lại tắt tivi đi, ngồi đung đưa tr&ecirc;n ghế kh&ocirc;ng biết suy nghĩ g&igrave;.<br />\r\n<br />\r\nM&agrave; kh&ocirc;ng biết rằng, cuộc tr&ograve; chuyện của hai bố con vừa rồi đ&atilde; bị nghe thấy. Qua &ocirc; k&iacute;nh tr&ecirc;n cửa nh&agrave;, đ&ocirc;i mắt xanh s&aacute;ng quắc nh&igrave;n v&agrave;o trong nh&agrave;, tập trung l&ecirc;n người Minh, bộ l&ocirc;ng đỏ hồng lấp l&oacute;e trong g&oacute;c tối rồi vụt mất.</p>', '2021-11-30 21:07:03', 6, 1),
(253, 68, 'Gia đình Buồn Tẻ. Cánh Cửa đến Với Thế Giới Mới.', '3', '<p>Đến tầm 7 giờ tối, mẹ Minh vừa về đến nh&agrave; sau chuyến đi. Nh&igrave;n b&agrave; c&oacute; vẻ rất mệt mỏi, tay x&aacute;ch chiếc t&uacute;i đựng hoa quả c&ugrave;ng &iacute;t b&aacute;nh lễ b&ecirc;n trong.<br />\r\n<br />\r\n&ldquo; Con ch&agrave;o mẹ!&rdquo; Minh vẫn ngồi tr&ecirc;n ghế đung đưa suy nghĩ m&ocirc;ng lung, quay ra ch&agrave;o nhẹ một c&acirc;u.<br />\r\n<br />\r\nMẹ cậu thấy vậy cũng kh&ocirc;ng c&oacute; vẻ biểu cảm g&igrave; cho lắm, chỉ hơi cười cười rồi đi v&agrave;o ph&ograve;ng, l&aacute;t sau bước ra b&agrave; hỏi: &ldquo; Bố nay về &agrave; con?&rdquo;<br />\r\n<br />\r\n&ldquo; V&acirc;ng, bố về l&uacute;c chiều, đang nấu cơm trong bếp.&rdquo; Minh trả lời.<br />\r\n<br />\r\nCuộc n&oacute;i chuyện của hai mẹ con chỉ đến như vậy, vốn dĩ đ&oacute; l&agrave; điều b&igrave;nh thường trong căn nh&agrave; n&agrave;y từ trước đến nay.<br />\r\n<br />\r\nNgười mẹ bước v&agrave;o bếp, nh&igrave;n thấy chồng m&igrave;nh cũng kh&ocirc;ng c&oacute; vẻ g&igrave; l&agrave; vui vẻ, chỉ lặng lẽ gật đầu ch&agrave;o nhau th&ocirc;i, sau đ&oacute; lấy quần &aacute;o đi tắm.<br />\r\n<br />\r\nSau đ&oacute;, cả nh&agrave; c&ugrave;ng ăn cơm, mỗi người ngồi tr&ecirc;n chiếc ghế nhựa xung quanh chiếc b&agrave;n bầu dục lặng lẽ ăn cơm. Kh&ocirc;ng kh&iacute; bữa cơm &acirc;m trầm đến lạ lẫm, họ cứ như những người lạ ăn cơm c&ugrave;ng nhau vậy, kh&ocirc;ng hề c&oacute; những cuộc tr&ograve; chuyện vui vẻ g&igrave; cả.<br />\r\n<br />\r\nĐến gần cuối bữa cơm, bố Minh bắt đầu đặt b&aacute;t xuống, trầm giọng n&oacute;i: &ldquo; Từ giờ đừng c&oacute; m&agrave; bỏ con ở nh&agrave; một m&igrave;nh như thế nữa, đi ch&ugrave;a th&igrave; cũng c&oacute; ng&agrave;y th&ocirc;i chứ, cứ đều đều như vậy th&igrave; con c&aacute;i vứt đi đ&acirc;u?&rdquo;<br />\r\n<br />\r\nMẹ Minh đang ăn cơm nghe thế cũng im lại, đặt b&aacute;t xuống n&oacute;i nhẹ: &ldquo; T&ocirc;i đi l&agrave; việc của t&ocirc;i, với lại Minh n&oacute; cũng lớn rồi, l&uacute;c trước vẫn vậy sao giờ tự nhi&ecirc;n &ocirc;ng lại hỏi đến? B&igrave;nh thường c&oacute; mấy khi về đến c&aacute;i nh&agrave; n&agrave;y đ&acirc;u?&rdquo;<br />\r\n<br />\r\nMinh ngồi đối diện bố mẹ, cố ăn cho hết b&aacute;t cơm, lặng lẽ ngồi nghe một ch&uacute;t rồi bật dậy.<br />\r\n<br />\r\n&ldquo; Con ăn xong rồi!&rdquo;<br />\r\n<br />\r\nCậu bước v&agrave;o ph&ograve;ng m&igrave;nh, bỏ lại m&acirc;m cơm với tiếng c&atilde;i nhau của bố mẹ. Trong l&ograve;ng cậu th&igrave; lại bối rối v&ocirc; c&ugrave;ng, đ&acirc;y kh&ocirc;ng phải lần đầu ti&ecirc;n xảy ra chuyện n&agrave;y.<br />\r\n<br />\r\nM&agrave; cứ mỗi lần xảy ra la Minh lại bỏ đi kh&ocirc;ng quan t&acirc;m, đ&uacute;ng hơn l&agrave; cậu kh&ocirc;ng d&aacute;m xen v&agrave;o mặc d&ugrave; hiểu được chỉ cần m&igrave;nh mở miệng n&oacute;i l&agrave; mọi chuyện sẽ &ecirc;m đẹp xuống th&ocirc;i.<br />\r\n<br />\r\n&ldquo; Haizz, sao m&igrave;nh kh&ocirc;ng n&oacute;i được g&igrave; chứ. Tại sao kh&ocirc;ng n&oacute;i được nhiều như khi c&ograve;n ở lớp học? Bố mẹ c&atilde;i nhau l&agrave; lỗi tại m&igrave;nh, tại m&igrave;nh m&atilde;i chưa trưởng th&agrave;nh cho bố mẹ bớt lo lắng&rdquo; Minh &acirc;m thầm ngồi trong ph&ograve;ng suy nghĩ.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nTuy nhỏ tuổi, nhưng trải qua cuộc sống như vậy cũng khiến Minh c&oacute; sự s&acirc;u sắc hơn trong suy nghĩ, chỉ vướng một điều l&agrave; cậu kh&ocirc;ng thể giao tiếp tự nhi&ecirc;n với bố mẹ được.<br />\r\n<br />\r\nCảm thấy hơi n&oacute;ng, Minh mở cửa sổ ph&ograve;ng m&igrave;nh ra, cửa sổ hướng ra vườn nhưng Minh l&uacute;c n&agrave;y cũng qu&ecirc;n khuấy mất những chuyện kh&aacute;c, kh&ocirc;ng để &yacute; thấy từ b&ecirc;n ngo&agrave;i c&oacute; một con ong lớn chậm r&atilde;i bay v&agrave;o.<br />\r\n<br />\r\nCon ong bay đến trước mặt Minh, đậu xuống mặt b&agrave;n học, mở hai mắt xanh nh&igrave;n lấy cậu.<br />\r\n<br />\r\n&ldquo; Xin ch&agrave;o, cậu b&eacute;!&rdquo; Con ong cất tiếng l&agrave;m cho Minh r&ugrave;ng m&igrave;nh, cậu nhớ lại tất cả những chuyện ban s&aacute;ng trong gi&acirc;y l&aacute;t, rồi cố giữ cho m&igrave;nh b&igrave;nh tĩnh.<br />\r\n<br />\r\nMinh biết nếu m&igrave;nh đ&aacute;p lại hay tỏ ra kinh h&atilde;i một ch&uacute;t th&ocirc;i th&igrave; chắc chắn sẽ gặp nguy hiểm ngay lập tức, đ&acirc;y l&agrave; một loại bản năng m&aacute;ch bảo.<br />\r\n<br />\r\n&ldquo; Ồ, vậy l&agrave; ta nhầm sao? Cậu kh&ocirc;ng nh&igrave;n thấy ta?&rdquo; Con ong thở d&agrave;i rồi bay l&ecirc;n, nhưng mắt n&oacute; lại to&aacute;t ra vẻ gian xảo.<br />\r\n<br />\r\nBay v&ograve;ng ra ph&iacute;a sau lưng Minh, lượn lờ v&agrave;i v&ograve;ng quanh căn ph&ograve;ng nhỏ rồi hướng ra cửa sổ. Ngay l&uacute;c đ&oacute;, Minh kh&ocirc;ng k&igrave;m được m&agrave; liếc mắt ra nh&igrave;n th&igrave; đột nhi&ecirc;n, con ong v&egrave;o một c&aacute;i xuất hiện trước mắt cậu nở nụ cười nguy hiểm khiến cậu giật bắn người, ng&atilde; nh&agrave;o khỏi ghế.<br />\r\n<br />\r\n&ldquo; Hừ hừ, xem ra m&igrave;nh đo&aacute;n tr&uacute;ng rồi. Cậu b&eacute;, cậu nh&igrave;n thấy ta đ&uacute;ng kh&ocirc;ng?&rdquo; Con ong n&oacute;i. Đồng thời từ người n&oacute; tỏa ra một loại dao động v&ocirc; h&igrave;nh lan khắp căn ph&ograve;ng.<br />\r\n<br />\r\n&ldquo; M&agrave;y..m&agrave;y l&agrave; c&aacute;i g&igrave;?&rdquo; Minh hốt hoảng n&oacute;i, từ l&uacute;c n&agrave;y cậu đ&atilde; lu&ocirc;n cảm nhận được sự nguy hiểm tới gần m&igrave;nh.<br />\r\n<br />\r\n&ldquo; Ấy ấy, trẻ con sao lại v&ocirc; ph&eacute;p thế. Ta lớn tuổi hơn cậu đấy. Phải gọi ta l&agrave; c&ocirc; hay d&igrave; chứ?&rdquo; Con ong cười khanh kh&aacute;ch.<br />\r\n<br />\r\n&ldquo; C&ocirc;? Kh&ocirc;ng, tao kh&ocirc;ng biết m&agrave;y l&agrave; c&aacute;i g&igrave;, mau c&uacute;t đi.&rdquo; Minh sợ đến mức cảm thấy tức ngực, thở hổn hển n&oacute;i từng chữ.<br />\r\n<br />\r\n&ldquo; Trẻ kh&ocirc;ng ngoan ngo&atilde;n th&igrave; phải bị trừng phạt.&rdquo; Con ong n&oacute;i lại.<br />\r\n<br />\r\nChỉ thấy từ miệng của n&oacute;, chiếc v&ograve;i vươn d&agrave;i ra, nhọn hoắt như mũi kim hướng đến tr&aacute;n của Minh. m thanh &ldquo; kh&egrave; kh&egrave;&rdquo; ph&aacute;t ra từ con ong khiến Minh t&aacute;i mặt, định h&ocirc; gọi bố mẹ nhưng nhận ra bản th&acirc;n kh&ocirc;ng n&oacute;i l&ecirc;n lời, nước mắt cậu lại bắt đầu tu&ocirc;n ra trong im lặng.<br />\r\n<br />\r\nCon ong lao đến với chiếc v&ograve;i, vừa khi gần chạm v&agrave;o tr&agrave;n Minh th&igrave; đột nhi&ecirc;n, kh&ocirc;ng gian xung quanh tối sầm lại. Chỉ c&oacute; Minh l&agrave; c&ograve;n giữ được &yacute; thức l&uacute;c n&agrave;y, cậu giật m&igrave;nh nh&igrave;n xung quanh.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&ldquo; Tối qu&aacute;, sao ph&ograve;ng m&igrave;nh tự nhi&ecirc;n tối như vậy? Nhưng m&igrave;nh vẫn c&oacute; thể nh&igrave;n thấy r&otilde; mọi thứ xung quanh. C&ograve;n con &ograve;ng n&agrave;y th&igrave; sao? N&oacute; sao lại dừng giữa chừng thế n&agrave;y?&rdquo; Minh trong đầu đầy nghi vấn.<br />\r\n<br />\r\nĐang l&uacute;c đ&oacute;, đầu Minh lại hiện l&ecirc;n cơn đau, v&agrave; một lần nữa, cậu bị cuốn v&agrave;o c&aacute;i thế giới nơi c&oacute; cột trụ khổng lồ kia.<br />\r\n<br />\r\nChỉ l&agrave; l&uacute;c n&agrave;y Minh kh&ocirc;ng hoảng hốt như trước nữa, cậu chăm ch&uacute; nh&igrave;n v&agrave;o cột trụ trong suốt đang phun ra m&agrave;u sắc đen ng&ograve;m, b&ecirc;n trong cột trụ hiện l&ecirc;n d&ograve;ng chữ [Khởi Nguy&ecirc;n].<br />\r\n<br />\r\n&ldquo; Khởi Nguy&ecirc;n? Nghĩa l&agrave; sao nhỉ? H&igrave;nh như &yacute; l&agrave; chỉ nguồn gốc g&igrave; đ&oacute; th&igrave; phải. Nhưng l&agrave; nguồn gốc của c&aacute;i g&igrave; mới được?&rdquo; Minh tự hỏi.<br />\r\n<br />\r\nSau đ&oacute;, m&agrave;u đen xung quanh cột trụ hội tụ lại, dần dần biến th&agrave;nh một cột trụ kh&aacute;c đứng b&ecirc;n cạnh.<br />\r\n<br />\r\nCột trụ mới n&agrave;y đen nh&aacute;nh, nhỏ hơn cột trụ pha l&ecirc; nhiều, m&agrave; b&ecirc;n trong hiện l&ecirc;n d&ograve;ng chữ kh&aacute;c lần lượt xếp h&igrave;nh v&ograve;ng tr&ograve;n l&agrave;: [ &Acirc;m] - [&Aacute;m] - [Vong] - [Diệt] - [T&agrave;] - [Ma]<br />\r\n<br />\r\nS&aacute;u chữ lớn, mối chữ mang đến cho Minh cảm gi&aacute;c kh&aacute;c nhau, m&agrave; chung quy th&igrave; cậu đều cảm thấy đ&aacute;ng sợ cả th&ocirc;i.<br />\r\n<br />\r\n&ldquo; C&aacute;i n&agrave;y nghe giống với mấy c&aacute;i trong phim ti&ecirc;n hiệp thế nhỉ? H&igrave;nh như c&aacute;i g&igrave; m&agrave; đạo hay t&acirc;m g&igrave; đ&oacute; th&igrave; phải. M&agrave; n&oacute;i chung m&igrave;nh cũng c&oacute; hiểu họ n&oacute;i c&aacute;i g&igrave; đ&acirc;u cơ chứ.&rdquo;<br />\r\n&ldquo; Khoan, h&igrave;nh như c&oacute; cảm gi&aacute;c lạ lạ th&igrave; phải. C&aacute;i cột đen kia h&igrave;nh như m&igrave;nh điều khiển được.&rdquo;<br />\r\n<br />\r\nMinh trong cơn m&ecirc; bất chợt hiểu ra c&aacute;ch sử dụng cột trụ mới, cảm gi&aacute;c lần n&agrave;y đến với cậu giống như một loại phản xạ tự nhi&ecirc;n. Minh coi n&oacute; giống với việc n&oacute;i chuyện hay ăn uống vậy, cứ thế m&agrave; l&agrave;m th&ocirc;i.<br />\r\n<br />\r\nQuay trở lại thực tại, Minh vẫn đang đứng trong ph&ograve;ng m&igrave;nh, kh&ocirc;ng gian tối om vụt s&aacute;ng trở lại. Con ong kia cũng lấy lại &yacute; thức của m&igrave;nh, ngạc nhi&ecirc;n khựng người lại, quay sang b&ecirc;n phải với &aacute;nh mắt sợ h&atilde;i n&oacute;i:<br />\r\n<br />\r\n&ldquo; M&agrave;y...l&agrave;m thế n&agrave;o m&agrave; m&agrave;y lại..&rdquo;<br />\r\n<br />\r\nMinh l&uacute;c n&agrave;y đang thản nhi&ecirc;n ngồi tr&ecirc;n giường m&igrave;nh, biết được bản th&acirc;n vừa nhận được c&aacute;i g&igrave; khiến cậu tự tin v&ocirc; c&ugrave;ng, tho&aacute;ng chốc cảm thấy con ong trước mặt giống như con ruồi vậy, vỗ ph&aacute;t l&agrave; chết.<br />\r\n<br />\r\nTrong đầu Minh l&uacute;c n&agrave;y hiện l&ecirc;n h&igrave;nh ảnh cột trụ đen, cậu bắt đầu tập trung l&ecirc;n s&aacute;u chứ ghi tr&ecirc;n đ&oacute;.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&ldquo; Bắt đầu với [ &Acirc;m] xem sao?&rdquo;<br />\r\n<br />\r\nNhắm v&agrave;o chữ [ &Acirc;m], một loại sức mạnh k&igrave; diệu từ trong cơ thể Minh tho&aacute;t ra ngo&agrave;i, cậu điều khiển n&oacute; hướng đến con ong kia.<br />\r\n<br />\r\nM&agrave; con ong khi thấy động t&aacute;c của Minh th&igrave; sợ h&atilde;i, mắt trợn tr&ograve;n nh&igrave;n cậu.<br />\r\n<br />\r\n&ldquo; Kh&ocirc;ng thể, m&agrave;y chưa c&oacute; Căn Nguy&ecirc;n, tại sao lại d&ugrave;ng được Nguy&ecirc;n năng?&rdquo; Con ong thốt l&ecirc;n.<br />\r\n<br />\r\n&ldquo; Căn Nguy&ecirc;n? Nguy&ecirc;n năng? To&agrave;n từ nghe kh&ocirc;ng hiểu. N&oacute;i cho m&agrave;y biết, tao vừa thức tỉnh si&ecirc;u năng lực đấy, nghĩa l&agrave; tao rất mạnh biết kh&ocirc;ng?&rdquo; Minh trả lời một c&aacute;ch ng&acirc;y thơ, cậu kh&ocirc;ng biết t&iacute; g&igrave; về những điều m&agrave; con ong kia n&oacute;i, chỉ nhớ đến mấy bộ phim si&ecirc;u nh&acirc;n từng xem m&agrave; th&ocirc;i.<br />\r\n<br />\r\nThực ra th&igrave; l&agrave; một đứa trẻ, Minh vẫn lu&ocirc;n ao ước c&oacute; si&ecirc;u năng lực trong người, c&oacute; thể l&agrave;m bất cứ điều g&igrave; m&igrave;nh th&iacute;ch.<br />\r\n<br />\r\n&ldquo; Si&ecirc;u năng lực? Chưa nghe bao giờ, m&agrave;y lừa tao sao?&rdquo; Con ong c&atilde;i lại.<br />\r\n<br />\r\n&ldquo; Th&ocirc;i th&ocirc;i, bay giờ n&oacute;i cho tao biết mau, m&agrave;y l&agrave; thứ g&igrave;, c&ograve;n l&uacute;c s&aacute;ng đi c&ugrave;ng một con ong nữa l&agrave; thế n&agrave;o? Đến từ đ&acirc;u? Chuyện ch&uacute;ng m&agrave;y n&oacute;i l&agrave; c&oacute; &yacute; g&igrave;?&rdquo; Minh bắt đầu bắt chước nh&acirc;n vật trong phim, tra hỏi li&ecirc;n tục khiến con ong sợ chết v&iacute;a.<br />\r\n<br />\r\nĐồng thời, sức mạnh m&agrave; cậu tỏa ra cũng khiến cơ thể con ong đ&ocirc;ng cứng lại giữa kh&ocirc;ng trung, chỉ c&oacute; c&aacute;i đầu c&ograve;n hoạt động được.<br />\r\n<br />\r\n&ldquo; Hừm, trước xem tivi c&oacute; nghe n&oacute;i &acirc;m độ l&agrave; rất lạnh nhỉ? C&oacute; lẽ chữ [ &Acirc;m] n&agrave;y l&agrave; chỉ si&ecirc;u năng lực hệ băng rồi, h&igrave; h&igrave;, cũng dễ điều khiển phết đấy chứ.&rdquo; Minh cảm nhận lại sức mạnh vừa rồi, trong l&ograve;ng dấy l&ecirc;n cảm gi&aacute;c s&ocirc;i sục kh&oacute; hiểu.<br />\r\n<br />\r\nNhững sinh vật lạ? Si&ecirc;u năng lực? Tất cả đều như trong mơ vậy, quả thực l&agrave; điều m&agrave; những cậu b&eacute; mười tuổi hay tưởng tượng ra, v&agrave; cũng l&agrave; ước muốn ng&acirc;y thơ của ch&uacute;ng.<br />\r\n<br />\r\nNgay l&uacute;c n&agrave;y, Minh bắt đầu mở ra c&aacute;nh cửa đến với thế giới đ&oacute;.</p>', '2021-11-30 21:07:27', 5, 1),
(254, 68, 'Nguy Hiểm Dần Tới.', '4', '<p>&ldquo; Mau n&oacute;i đi, hay muốn bị đ&ocirc;ng cứng đến chết hả?&rdquo; Minh đối mặt với con ong đang bị đ&ocirc;ng cứng.<br />\r\n<br />\r\nMinh tỏ ra tinh nghịch hơn l&agrave; nghi&ecirc;m t&uacute;c tra hỏi, đối với cậu th&igrave; chuyện n&agrave;y mang t&iacute;nh giải tr&iacute; nhiều hơn. M&agrave; cũng l&agrave; lẽ thường khi một cậu b&eacute; c&oacute; suy nghĩ như vậy, đ&acirc;u thể n&agrave;o bắt n&oacute; nghĩ đến những thứ lớn lao hơn cho được.<br />\r\n<br />\r\nMinh thấy một l&aacute;t m&agrave; con ong kia vẫn kh&ocirc;ng n&oacute;i g&igrave;, bắt đầu cảm thấy ch&aacute;n nản. Cậu lại ảy &yacute; định thử nghiệm mấy năng lực kh&aacute;c, đ&uacute;ng hơn l&agrave; c&ocirc;ng dụng của những chữ c&ograve;n lại.<br />\r\n<br />\r\nBắt đầu với chữ [&Aacute;m], khi tập trung v&agrave;o n&oacute;, Minh bắt đầu thấy kh&ocirc;ng gian xung quanh tối đi, điều n&agrave;y khiến cậu hơi sợ h&atilde;i, nhưng rất nhanh b&igrave;nh tĩnh lại bởi cậu c&oacute; thể nh&igrave;n r&otilde; thấy mọi thứ trong n&agrave;y, thậm ch&iacute; c&ograve;n r&otilde; n&eacute;t hơn cả l&uacute;c b&igrave;nh thường nhiều lần.<br />\r\n<br />\r\n&ldquo; Ồ hay qu&aacute;, si&ecirc;u năng lực hệ b&oacute;ng tối l&uacute;c n&agrave;o cũng ngầu nhất, nhưng m&agrave; c&aacute;i n&agrave;y d&ugrave;ng l&agrave;m g&igrave; được nhỉ? Hay đi ăn trộm? Th&ocirc;i th&ocirc;i bỏ đi..&rdquo; Minh bắt đầu c&oacute; những suy nghĩ t&aacute;o bạo, nhưng ngay lập tức phủi đi.<br />\r\n<br />\r\n&ldquo; Để xem, [T&agrave;] với [Ma] c&oacute; lẽ l&agrave; c&aacute;i g&igrave; &aacute;c lắm, bỏ qua. C&ograve;n [Diệt] với [Vong] th&igrave; c&oacute; khi l&agrave; hủy diệt với tử vong rồi. He he, c&aacute;i n&agrave;y m&agrave; d&ugrave;ng th&igrave; b&aacute; ch&aacute;y lu&ocirc;n. Nhưng m&agrave; phải cẩn thận kh&ocirc;ng kh&eacute;o d&iacute;nh l&ecirc;n người ta th&igrave; hỏng.&rdquo;<br />\r\n<br />\r\nMinh li&ecirc;n tục ngồi suy nghĩ mi&ecirc;n man đến &ldquo; si&ecirc;u năng lực&rdquo; của m&igrave;nh, bỏ mặc con ong đang t&aacute;i xanh mặt nh&igrave;n cậu.<br />\r\n<br />\r\n&ldquo; Thằng nh&oacute;c n&agrave;y, n&oacute; l&agrave; thứ qu&aacute;i thai g&igrave; vậy? Theo th&ocirc;ng tin từ cấp tr&ecirc;n th&igrave; lo&agrave;i người tuy đ&uacute;ng l&agrave; c&oacute; nhiều kẻ gọi l&agrave; thi&ecirc;n t&agrave;i thật nhưng đ&acirc;u ai c&oacute; tiềm lực cỡ n&agrave;y. Kh&ocirc;ng lẽ&hellip;&rdquo; Con ong kia bắt đầu nghĩ đến g&igrave; đ&oacute; th&igrave; giật m&igrave;nh, n&oacute; thấy Minh đang chỉ tay về ph&iacute;a m&igrave;nh, t&acirc;m tr&iacute; n&oacute; trở n&ecirc;n hoảng hốt.<br />\r\n<br />\r\n&ldquo; X&egrave;o x&egrave;o..&rdquo; Một loại kh&iacute; m&agrave;u đen tu&ocirc;n ra từ ng&oacute;n trỏ của Minh, hướng đến phần bụng của con ong.<br />\r\n<br />\r\nKhi m&agrave; kh&iacute; đen chạm v&agrave;o cơ thể n&oacute;, tiếng th&eacute;t g&agrave;o vang l&ecirc;n, bụng n&oacute; giống như bị axit ăn m&ograve;n, m&ugrave;i kh&eacute;t ph&aacute;t ra. Phần bụng dần dần bị xuy&ecirc;n thủng, m&aacute;u xanh của con ong chảy ra cũng đều bị đốt ch&aacute;y hết.<br />\r\n<br />\r\n&ldquo; Ọe, kh&eacute;t qu&aacute; đi mất. M&agrave;y c&ograve;n kh&ocirc;ng mau trả lời tao th&igrave; sẽ đau hơn đấy.&rdquo; Minh n&oacute;i lại một lần nữa với con ong.<br />\r\n<br />\r\n&ldquo; Kh&ocirc;ng, grittt aaaa...Tha ,,,tha cho t&ocirc;i. Ch&uacute;ng t&ocirc;i l&agrave;&hellip;&rdquo; Con ong đang định n&oacute;i ra th&igrave; &acirc;m thanh đứt qu&atilde;ng, n&oacute; đ&atilde; chết.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nL&agrave; do kh&iacute; đen kia của Minh qu&aacute; đ&aacute;ng sợ, tuy mới chỉ tiếp x&uacute;c một &iacute;t nhưng nỗi đau m&agrave; con ong nhận được th&igrave; hơn thế rất nhiều, n&oacute; đau qu&aacute; m&agrave; chết đi.<br />\r\n<br />\r\n&ldquo; Ơ k&igrave;a, chết rồi &agrave;. Hừm, n&oacute; chắc cũng chỉ l&agrave; loại l&iacute;nh t&eacute;p riu th&ocirc;i nhỉ. Kể m&agrave; bắt được con boss th&igrave; tốt rồi.&rdquo; Minh tr&agrave;n đầy tiếc nuối tự n&oacute;i.<br />\r\n<br />\r\nĐột nhi&ecirc;n, mắt cậu mờ dần đi, tầm nh&igrave;n trở n&ecirc;n đảo loạn rồi &ldquo; Uỳnh&rdquo; một c&aacute;i, cả người cậu ng&atilde; xuống đất bất tỉnh.<br />\r\n<br />\r\n&hellip;&hellip;&hellip;..<br />\r\n<br />\r\n&ldquo; Minh, con c&oacute; sao kh&ocirc;ng? N&agrave;y Minh, nghe mẹ n&oacute;i kh&ocirc;ng con?&rdquo;<br />\r\n<br />\r\nHai mắt Minh từ từ mở ra, trong đ&ocirc;i mắt hiện l&ecirc;n h&igrave;nh ảnh người mẹ của cậu đang sốt sắng hỏi han.<br />\r\n<br />\r\n&ldquo; Mẹ &agrave;, c&oacute; chuyện g&igrave; thế?&rdquo; Minh cất giọng hỏi.<br />\r\n<br />\r\n&ldquo; Con bị ốm sao kh&ocirc;ng n&oacute;i cho bố mẹ biết. Để tối h&ocirc;m qua ngất cả ra s&agrave;n nh&agrave; như thế, may m&agrave; bố con v&agrave;o kip chứ kh&ocirc;ng b&acirc;y giờ c&ograve;n chưa biết ra sao đấy,&rdquo; Mẹ cậu n&oacute;i, sự lo lắng khiến mặt b&agrave; nhợt nhạt hẳn đi.<br />\r\n<br />\r\n&ldquo; Th&ocirc;i, kh&ocirc;ng sao l&agrave; tốt rồi con ạ, từ giờ ch&uacute; &yacute; giữ g&igrave;n sức khỏe đấy. Ăn phải nhiều v&agrave;o th&igrave; mới kh&ocirc;ng ốm được.&rdquo; B&ecirc;n cạnh, bố cậu nhẹ nh&agrave;ng n&oacute;i, tr&ecirc;n mặt cũng dần hiện l&ecirc;n nụ cười.<br />\r\n<br />\r\n&ldquo; &Agrave;, v&acirc;ng, con biết rồi.&rdquo; Minh đ&aacute;p nhẹ, sau đ&oacute; lại nhắm mắt lại tỏ vẻ như muốn ngủ th&ecirc;m.<br />\r\n<br />\r\nBố mẹ cậu cũng kh&ocirc;ng l&agrave;m phiền nữa, cả hai bước ra ngo&agrave;i rồi đ&oacute;ng cửa ph&ograve;ng lại. M&agrave; b&ecirc;n trong, Minh đang nhắm mắt suy nghĩ những chuyện xảy ra tối qua.<br />\r\n<br />\r\nTrong đầu cậu l&uacute;c n&agrave;y, bản th&acirc;n đang ở thế giới k&igrave; lạ kia, trước mắt vẫn l&agrave; hai cậy trụ một lớn một nhỏ.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nCột trụ đen lớn hơn một ch&uacute;t, Minh cảm gi&aacute;c r&otilde; r&agrave;ng m&igrave;nh cũng mạnh hơn so với trước nhưng kh&ocirc;ng nhiều lắm.<br />\r\n<br />\r\n&ldquo; Tất cả đều l&agrave; thật, kh&ocirc;ng phải do m&igrave;nh tưởng tượng, qu&aacute; hay, ha ha.&rdquo; Minh th&iacute;ch th&uacute; nghĩ đến những thứ m&igrave;nh c&oacute; thể l&agrave;m với &ldquo; si&ecirc;u năng lực n&agrave;y.<br />\r\n<br />\r\nCậu mở mắt ra, bỗng nhi&ecirc;n xung quanh trở n&ecirc;n tối sầm lại, nhưng Minh th&igrave; lại thấy r&otilde; m&ocirc;n một mọi thứ xung quanh m&igrave;nh, thậm ch&iacute; cậu chỉ cần cảm nhận chứ kh&ocirc;ng cần mở mắt ra.<br />\r\n<br />\r\nSau đ&oacute; nhiệt độ ph&ograve;ng hạ dần xuống, một con muỗi trong ph&ograve;ng đang bay th&igrave; lập tức bị đ&ocirc;ng cứng rồi vỡ ra th&agrave;nh mảnh vụn.<br />\r\n<br />\r\n&ldquo; Ph&ugrave;...Giờ mới biết d&ugrave;ng đến năng lực thật đ&uacute;ng l&agrave; mệt qu&aacute;, ngang với chạy bộ quanh l&agrave;ng một v&ograve;ng rồi. Kh&ocirc;ng biết c&oacute; c&aacute;ch n&agrave;o gi&uacute;p m&igrave;nh mạnh hơn kh&ocirc;ng nhỉ?&rdquo; Minh tự hỏi.<br />\r\n<br />\r\n&ldquo; A, h&ocirc;m qua sau khi giết con ong kia th&igrave; cột trụ đen n&agrave;y lớn hơn một &iacute;t nhỉ? Nếu m&igrave;nh t&igrave;m được th&ecirc;m những con kh&aacute;c th&igrave; biết đ&acirc;u&hellip;&rdquo; Minh tự hỏi.<br />\r\n<br />\r\nSau đ&oacute;, Minh nh&igrave;n xuống ph&iacute;a dưới nơi c&oacute; x&aacute;c của con ong vừa chết tối qua, n&oacute; kh&ocirc;ng bị bố mẹ cậu ph&aacute;t hiện ra.<br />\r\n<br />\r\nMinh cui xuống nhặt l&ecirc;n c&aacute;i x&aacute;c, quan s&aacute;t một hồi th&igrave; kh&ocirc;ng thấy c&oacute; g&igrave; đặc biệt cả, duy nhất chỉ c&oacute; k&iacute;ch thước th&igrave; to hơn nhiều so với những con ong m&agrave; cậu biết.<br />\r\n<br />\r\n&ldquo; Kh&ocirc;ng c&oacute; vi&ecirc;n bi n&agrave;o cả, kh&aacute;c với con vật h&ocirc;m trước. Rốt cuộc l&agrave; sao nhỉ?&rdquo; Minh nhớ lại tối h&ocirc;m đ&oacute;, khi m&agrave; cậu lần đầu ti&ecirc;n tiếp x&uacute;c với những sinh vật k&igrave; lạ.<br />\r\n<br />\r\nB&acirc;y giờ, Minh c&oacute; thể chắc chắn rằng thực sự tồn tại những sinh vật k&igrave; b&iacute; kh&aacute;c trong thế giới m&igrave;nh đang sống, nhưng cậu vẫn kh&ocirc;ng biết được tầm quan trọng của việc n&agrave;y như thế n&agrave;o.<br />\r\n<br />\r\n&ldquo; Ay d&ocirc;, mỏi người qu&aacute;. Dậy vận động ch&uacute;t xem n&agrave;o. M&agrave; h&ocirc;m nay l&agrave; thứ hai nhỉ, chắc chiều lại phải đi học th&ocirc;i, ch&aacute;n qu&aacute;.&rdquo; Minh than v&atilde;n, sau cũng vẫn đi ra khỏi nh&agrave;, lại gần khu vườn nh&agrave; m&igrave;nh quan s&aacute;t một l&uacute;c.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&ldquo; Kh&ocirc;ng thấy c&oacute; con n&agrave;o kh&aacute;c, h&ocirc;m qua thấy ch&uacute;ng n&oacute; th&acirc;n nhau lắm cơ m&agrave;.&rdquo; Minh n&oacute;i.<br />\r\n&hellip;&hellip;&hellip;.<br />\r\n<br />\r\nQuay lại tối h&ocirc;m trước, thời điểm khi m&agrave; Minh vừa giết chết con ong kia, th&igrave; tại một căn nh&agrave; hoang ngo&agrave;i c&aacute;nh đồng c&aacute;ch rất xa l&agrave;ng của Minh, &acirc;m thanh g&agrave;o th&eacute;t vang l&ecirc;n.<br />\r\n<br />\r\n&ldquo; Kh&ocirc;ng thể n&agrave;o, sao t&iacute;n hiệu sinh mệnh của c&ocirc; ấy tắt mất rồi. L&agrave; ai giết c&ocirc; ấy, kh&ocirc;ng phải n&oacute;i chỉ huy đ&atilde; ti&ecirc;u diệt kẻ địch nguy hiểm rồi sao? Hay l&agrave; c&oacute; viện binh lo&agrave;i người đến?&rdquo;<br />\r\n<br />\r\n&Acirc;m thanh l&agrave; từ một con ong v&agrave;ng, ch&iacute;nh l&agrave; t&igrave;nh nh&acirc;n với con ong hồng m&agrave; Minh nh&igrave;n thấy l&uacute;c trước, c&oacute; điều l&uacute;c n&agrave;y n&oacute; lớn hơn nhiều. K&iacute;ch cỡ phải bằng cơ thể của một người trưởng th&agrave;nh b&igrave;nh thường.<br />\r\n<br />\r\n&ldquo; Đội trưởng, t&ocirc;i nghĩ n&ecirc;n b&aacute;o c&aacute;o với cấp tr&ecirc;n th&igrave; hơn. D&ugrave; sao sức mạnh của Ph&oacute; đội trưởng cũng kh&ocirc;ng phải vừa, lại chết nhanh như thế&hellip;&rdquo; Một con ong kh&aacute;c nhỏ hơn một ch&uacute;t n&oacute;i.<br />\r\n<br />\r\nCon ong v&agrave;ng to lớn kia ch&iacute;nh l&agrave; Đội trưởng ở đ&acirc;y, trong ng&ocirc;i nh&agrave; hoang n&agrave;y c&oacute; đến hai trăm con ong lớn, l&agrave; cả một đội qu&acirc;n.<br />\r\n<br />\r\nOng đội trưởng ngẫm nghĩ một ch&uacute;t, đột nhi&ecirc;n n&oacute; nhớ ra một điều, l&agrave; h&igrave;nh ảnh khi ở khu vườn nh&agrave; Minh, t&igrave;nh nh&acirc;n của n&oacute; c&oacute; n&oacute;i rằng Minh giống như nh&igrave;n thấy bọn họ, sau đ&oacute; liền t&aacute;ch ra m&agrave; kh&ocirc;ng n&oacute;i một lời.<br />\r\n<br />\r\n&ldquo; Kh&ocirc;ng lẽ l&agrave; nơi đ&oacute; sao?&rdquo; Đội trưởng tự hỏi, cuối c&ugrave;ng quyết định: &ldquo; Hai mươi l&iacute;nh đi c&ugrave;ng ta đến một nơi, c&ograve;n cậu, b&aacute;o c&aacute;o với chỉ huy chuyện n&agrave;y đi.&rdquo;<br />\r\n<br />\r\nĐội trưởng chỉ tay v&agrave;o một con ong b&ecirc;n cạnh n&oacute;i, sau đ&oacute; mang theo hai mươi con ong kh&aacute;c bay đi, mục ti&ecirc;u l&agrave; nh&agrave; Minh.<br />\r\n<br />\r\n&ldquo; Nếu thật sự l&agrave; thằng nh&oacute;c đ&oacute;, th&igrave; cần phải xem lại thực lực của lo&agrave;i người rồi. C&ograve;n trẻ như thế m&agrave; đ&atilde;&hellip;.&rdquo; Ong đội trưởng vừa bay đi vừa suy tư, m&agrave; cả đội h&igrave;nh l&uacute;c n&agrave;y thu nhỏ lại chỉ như một đ&agrave;n ong b&igrave;nh thường, lao nhanh tr&ecirc;n trời một c&aacute;ch im lặng.</p>', '2021-11-30 21:07:45', 5, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(255, 68, 'Học Sinh Mới Kì Lạ.', '5', '<p>&ldquo; Ch&agrave;o bố mẹ con đi học đ&acirc;y.&rdquo; Minh bắt đầu buổi chiều học h&agrave;nh vất vả, l&agrave; thứ m&agrave; cậu ch&aacute;n nản nhất trong cuộc sống.<br />\r\n<br />\r\nĐạp chiếc xe ra khỏi cổng, Minh lại mang theo cảm gi&aacute;c hứng khởi, đ&acirc;y l&agrave; cảm x&uacute;c mỗi lần cậu đạp xe tr&ecirc;n đường chứ chả phải l&agrave; do việc đến trường học.<br />\r\n<br />\r\n&ldquo; Haizz, một năm nữa mới được nghỉ h&egrave;, đến l&uacute;c đấy tha hồ m&agrave; trải nghiệm si&ecirc;u năng lực, h&ecirc; h&ecirc;. M&agrave; kể nếu c&oacute; năng lực bay th&igrave; tốt biết mấy, ch&aacute;n thật.&rdquo; Minh lại tiếp t&uacute;c suy tưởng về cảnh tượng m&igrave;nh d&ugrave;ng đến si&ecirc;u năng lực, tưởng tượng ra bản th&acirc;n ngầu cỡ n&agrave;o.<br />\r\n<br />\r\n&ldquo; Nhưng kh&oacute; c&aacute;i l&agrave; phải kh&ocirc;ng để người kh&aacute;c biết được, chứ kh&ocirc;ng giống mấy bộ si&ecirc;u nh&acirc;n kia to&agrave;n bi đem ra l&agrave;m th&iacute; nghi&ecirc;m th&igrave; chết.&rdquo; Minh suy nghĩ.<br />\r\n<br />\r\n&ldquo; &Ecirc;, Minh ơi, s&aacute;ng nay l&agrave;m sao đấy, ốm nặng lắm kh&ocirc;ng?&rdquo; &Acirc;m thanh quen thuộc từ những người bạn của Minh từ đằng sau cất l&ecirc;n, người n&oacute;i l&agrave; Việt.<br />\r\n<br />\r\n&ldquo; &Agrave; ờ, cũng b&igrave;nh thường th&ocirc;i. Tao c&oacute; bao giờ ốm nặng đ&acirc;u, khỏe m&agrave;.&rdquo; Minh trả lời.<br />\r\n<br />\r\n&ldquo; Ồ, s&aacute;ng nay lớp m&agrave;y c&oacute; học sinh mới chuyển về đấy. Nghe n&oacute;i l&agrave; từ th&agrave;nh phố về đ&acirc;y học, xinh cực phẩm lu&ocirc;n m&agrave;y ạ.&rdquo; Dũng mở miệng chen v&agrave;o.<br />\r\n<br />\r\n&ldquo; Thế &aacute;, n&oacute; ngồi đ&acirc;u, gần chỗ tao kh&ocirc;ng?&rdquo; Minh vội hỏi. cậu nh&oacute;c n&agrave;y xem ra cũng rất quan t&acirc;m đến mấy vấn đề n&agrave;y.<br />\r\n<br />\r\n&ldquo; H&agrave; h&agrave;, th&iacute;ch rồi chứ g&igrave;? M&agrave; tao nghĩ nếu l&agrave; m&agrave;y th&igrave; c&oacute; khi bạn mới cũng th&iacute;ch đấy, bạn ấy ngồi c&ugrave;ng b&agrave;n m&agrave;y lu&ocirc;n, tại m&agrave;y ngồi một m&igrave;nh một g&oacute;c c&ograve;n g&igrave;.&rdquo; Dũng hăng h&aacute;i trả lời.<br />\r\n<br />\r\n&ldquo; Th&ocirc;i hai &ocirc;ng ơi, mới c&oacute; dăm ba c&aacute;i tuổi đầu đ&atilde; nghĩ vớ nghĩ vẩn. Ch&uacute;ng m&agrave;y kh&ocirc;ng lo học th&igrave; kẻo suốt ng&agrave;y bị bố mẹ mắng đấy, xong ra đường gặp mấy b&agrave; h&agrave;ng x&oacute;m th&igrave; th&ocirc;i rồi.&rdquo; Việt một b&ecirc;n than thở.<br />\r\n<br />\r\n&ldquo; Ha ha, lo g&igrave;, m&agrave;y c&oacute; cần tao gi&uacute;p đỡ kh&ocirc;ng, c&oacute; b&agrave;i n&agrave;o kh&oacute; cứ chạy sang hỏi tao l&agrave; được. M&agrave; tiện thể kiếm ch&uacute;t thể diện với bạn mới ch&uacute;t, h&igrave; h&igrave;.&rdquo; Minh cười n&oacute;i.<br />\r\n<br />\r\n&ldquo; Kinh qu&aacute;, đ&atilde; t&iacute;nh to&aacute;n thế rồi cơ &agrave;? Đ&atilde; thế chiều nay tao sang lu&ocirc;n, xem m&agrave;y c&oacute; d&aacute;m kh&ocirc;ng, hay lại run lẩy bẩy l&ecirc;n.&rdquo; Việt đ&aacute;p.<br />\r\n<br />\r\nBa đứa c&ugrave;ng nhau đến trường, c&acirc;u chuyện chả l&uacute;c n&agrave;o hết cả, m&atilde;i một l&uacute;c sau mới đến được trường học.<br />\r\n<br />\r\nBước v&agrave;o lớp học, Minh đưa mắt nh&igrave;n xung quanh rồi dừng lại tr&ecirc;n người một bạn nữ. Chắc chắn ch&iacute;nh l&agrave; học sinh mới rồi, ngồi ngay cạnh Minh.<br />\r\n<br />\r\n&ldquo; Trời, đ&uacute;ng l&agrave; g&aacute;i phố c&oacute; kh&aacute;c, chậc chậc.&rdquo; Minh thầm đ&aacute;nh gi&aacute; một phen.<br />\r\n<br />\r\nĐ&oacute; l&agrave; một b&eacute; g&aacute;i rất dễ thương, m&aacute;i t&oacute;c ngắn uốn cong &ocirc;m v&agrave;o s&aacute;t mặt, mắt đeo k&iacute;nh cận tr&ograve;n rất hợp. Nh&igrave;n qua th&igrave; Minh thấy bạn n&agrave;y cũng cao gần bằng m&igrave;nh, khoảng chừng một m&eacute;t bốn mấy, m&agrave; Minh th&igrave; hơn m&eacute;t năm một ch&uacute;t.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nM&agrave; nếu đ&aacute;nh gi&aacute; kĩ lại th&igrave; Minh cũng l&agrave; một cậu trai bảnh bao, người c&oacute; hơi gầy, d&aacute;ng thẳng, mặt thon gọn với nước da trắng kh&ocirc;ng k&eacute;m mấy đứa con g&aacute;i, nhưng so với bạn mới kia th&igrave; vẫn thua xa.<br />\r\n<br />\r\nBước v&agrave;o chỗ ngồi, Minh đang định mở miệng ch&agrave;o hỏi th&igrave; cảm thấy lạ lạ, tự dưng cổ họng như nghẹn lại kh&ocirc;ng n&oacute;i được, tim th&igrave; đập th&igrave;nh thịch.<br />\r\n<br />\r\n&ldquo; Sao thế n&agrave;y? M&igrave;nh ngại &aacute;?&rdquo; Minh thầm nghĩ.<br />\r\n<br />\r\n&ldquo; N&agrave;y, cậu..cậu l&agrave; học sinh mới hả?&rdquo; M&atilde;i mới thốt ra được c&acirc;u, Minh l&agrave;m bộ dạng thở ph&agrave;o.<br />\r\n<br />\r\n&ldquo; &Agrave; đ&uacute;ng rồi. Ch&agrave;o...ch&agrave;o cậu, m&igrave;nh t&ecirc;n l&agrave; Vy, &agrave;...Ho&agrave;ng Kh&aacute;nh Vy...c&ograve;n..c&ograve;n cậu?&rdquo; C&ocirc; g&aacute;i trả lời ấp &uacute;ng kh&ocirc;ng k&eacute;m g&igrave;, xem ra t&iacute;nh c&aacute;ch cũng rất nh&uacute;t nh&aacute;t.<br />\r\n<br />\r\n&ldquo; Ồ, ha ha, t&ecirc;n cậu hay qu&aacute;. &Agrave;...ờ...ừm...c&ograve;n m&igrave;nh t&ecirc;n l&agrave; Minh, L&yacute; Nhật Minh.&rdquo; Minh đ&aacute;p lại, c&oacute; vẻ cũng ấp &uacute;ng kh&ocirc;ng k&eacute;m.<br />\r\n<br />\r\n&ldquo; Cậu họ L&yacute; &aacute;? Lần đầu m&igrave;nh nghe lu&ocirc;n đấy.&rdquo; Vy ngạc nhi&ecirc;n trả lời, s&acirc;u trong mắt dường như vừa &aacute;nh l&ecirc;n tia nh&igrave;n hiếu k&igrave;.<br />\r\n<br />\r\n&ldquo; &Agrave;, th&igrave; theo như bố m&igrave;nh kể th&igrave; họ nh&agrave; m&igrave;nh đ&uacute;ng thật rất hiếm, nhưng h&igrave;nh như n&oacute;i l&agrave; tổ ti&ecirc;n nh&agrave; m&igrave;nh vốn dĩ ng&agrave;y xưa sống tại v&ugrave;ng hẻo l&aacute;nh lắm, kiểu như d&acirc;n tộc thiểu số ấy. Về sau đến đời cụ m&igrave;nh mới chuyển ra đ&acirc;y sống.&rdquo; Minh nhớ lại những lời kể của bố m&igrave;nh m&agrave; n&oacute;i.<br />\r\n<br />\r\nNhưng cậu kh&ocirc;ng c&oacute; n&oacute;i hết, rằng vốn dĩ nguồn gốc d&ograve;ng họ l&agrave; như thế n&agrave;o, v&agrave; một số b&iacute; mật kh&aacute;c. Nhưng n&oacute;i chung th&igrave; Minh cũng chả nhớ đến mấy c&aacute;i đấy l&agrave;m g&igrave;, nghe như truyện lịch sử vậy.<br />\r\n<br />\r\n&ldquo; Vậy sao, nhưng m&agrave; t&ecirc;n cậu cũng hay lắm chứ, hơn nữa c&ograve;n độc nhất cơ m&agrave;, h&igrave; h&igrave;.&rdquo; Vy cười cười đ&aacute;p.<br />\r\n<br />\r\nNghe thấy lời khen đ&oacute;, mặt Minh bỗng n&oacute;ng l&ecirc;n, c&agrave;ng th&ecirc;m bối rối kh&ocirc;ng n&oacute;i được g&igrave;.<br />\r\n<br />\r\n&ldquo; &Agrave;...ừm&hellip;, sao cậu lại chuyển về đ&acirc;y thế? Học tr&ecirc;n th&agrave;nh phố kh&ocirc;ng phải th&iacute;ch hơn &agrave;?&rdquo; Minh hỏi tiếp.<br />\r\n<br />\r\n&ldquo; C&aacute;i n&agrave;y l&agrave; do c&ocirc;ng việc của gia đ&igrave;nh m&igrave;nh, bắt buộc phải vậy th&ocirc;i. M&igrave;nh t&iacute;nh học ở đ&acirc;y đến hết năm rồi lại chuyển đi.&rdquo; Vy trả lời.<br />\r\n<br />\r\n&ldquo; Như vậy vất vả thật đấy. Nhưng m&igrave;nh cũng muốn được đi nhiều nơi như thế lắm, ở m&atilde;i một chỗ thế n&agrave;y cảm gi&aacute;c như bị c&ocirc; lập vậy.&rdquo; Minh n&oacute;i.<br />\r\n<br />\r\n&ldquo; Sau n&agrave;y lớn l&ecirc;n cậu vẫn c&oacute; thể l&agrave;m được m&agrave;, l&uacute;c đ&oacute; th&igrave; đi đ&acirc;u l&agrave;m g&igrave; đều do cậu cả.&rdquo; Vy đ&aacute;p.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&ldquo; Cậu n&oacute;i chuyện nghe người lớn nhỉ, học sinh th&agrave;nh phố đều trưởng th&agrave;nh vậy &agrave;?&rdquo; Minh lại hỏi.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; lần đầu cậu c&oacute; cảm gi&aacute;c mới lạ như vậy, lần đầu c&oacute; một người c&ugrave;ng tuổi m&agrave; m&igrave;nh c&oacute; thể chia sẻ những cảm x&uacute;c như thế. Từ trước đến giờ, Minh n&oacute;i chuyện với bạn b&egrave; chỉ to&agrave;n l&agrave; cười đ&ugrave;a, những chuyện linh tinh tr&ecirc;n trời dưới đất m&agrave; th&ocirc;i.<br />\r\n<br />\r\n&ldquo; Cậu thấy vậy sao? Thực ra kh&ocirc;ng phải tất cả đều vậy, chỉ l&agrave; m&igrave;nh mong muốn nhanh ch&oacute;ng lớn l&ecirc;n m&agrave; th&ocirc;i.&rdquo; Vy n&oacute;i nhẹ, &acirc;m thanh c&agrave;ng l&uacute;c c&agrave;ng nhỏ, giống như c&oacute; điều g&igrave; kh&oacute; n&oacute;i ra.<br />\r\n<br />\r\nMinh thấy vậy cũng hiểu, kh&ocirc;ng c&oacute; hỏi tiếp m&agrave; chuyển sang chủ đề kh&aacute;c, cậu n&oacute;i:<br />\r\n<br />\r\n&ldquo; Th&ocirc;i nh&eacute;, sắp v&agrave;o học rồi. Từ giờ mong cậu gi&uacute;p đỡ nhiều rồi, h&igrave; h&igrave;.&rdquo; Minh tỏ ra khi&ecirc;m tốn n&oacute;i.<br />\r\n<br />\r\n&ldquo; Ừm, c&oacute; g&igrave; cậu cứ hỏi m&igrave;nh l&agrave; được, m&igrave;nh sẽ gi&uacute;p đỡ hết sức.&rdquo; Vy vui vẻ trả lời, hơi c&oacute; ch&uacute;t ngượng ng&ugrave;ng.<br />\r\n<br />\r\nThực chất, học lực của Vy cũng kh&ocirc;ng cao lắm, chỉ gọi l&agrave; mức tr&ecirc;n kh&aacute; một ch&uacute;t th&ocirc;i, nhưng c&ocirc; b&eacute; n&agrave;y lại nghĩ hoc sinh ở qu&ecirc; như Minh th&igrave; c&oacute; thể sẽ k&eacute;m hơn bản th&acirc;n. M&agrave; Vy lại kh&ocirc;ng biết rằng nếu n&oacute;i đến lực học th&igrave; Minh ăn đứt m&igrave;nh, chỉ l&agrave; cậu muốn khi&ecirc;m tốn một ch&uacute;t m&agrave; th&ocirc;i.<br />\r\n<br />\r\nNgược lai Minh th&igrave; kh&ocirc;ng nghĩ theo hướng như vậy, mục đ&iacute;ch cậu n&oacute;i vậy chỉ l&agrave; c&aacute;i cơ th&ocirc;i. Minh thực chất muốn dựa v&agrave;o đ&oacute; bắt chuyện với Vy, căn bản bởi v&igrave; c&oacute; lẽ cậu nhận ra bản th&acirc;n c&oacute; ch&uacute;t cảm nắng bạn g&aacute;i n&agrave;y rồi.<br />\r\n<br />\r\nCũng kh&ocirc;ng thể tr&aacute;ch được, Minh c&ograve;n cảm thấy nhiều &aacute;nh mắt kh&aacute;c cũng đang kh&oacute; chịu nh&igrave;n m&igrave;nh n&oacute;i chuyện với Vy đ&acirc;y.<br />\r\n<br />\r\nGi&aacute;o vi&ecirc;n bước v&agrave;o lớp, l&agrave; giờ học m&ocirc;n To&aacute;n, Minh v&agrave; Vy lấy vở ra ghi b&agrave;i, bắt đầu một buổi học ch&aacute;n ngắt.<br />\r\n<br />\r\nThỉnh thoảng Minh c&oacute; liếc sang nh&igrave;n vở của Vy một ch&uacute;t, th&acirc;y c&ocirc; b&eacute; đang loay hoay th&igrave; cười cười.<br />\r\n<br />\r\n&ldquo; H&oacute;a ra cũng kh&ocirc;ng phải học xuất sắc lắm nhỉ. M&agrave; th&ocirc;i kệ, l&agrave;m chậm lại ch&uacute;t xong thi thoảng hỏi b&agrave;i, h&igrave; h&igrave;.&rdquo; Minh nghĩ trong đầu.<br />\r\n<br />\r\nNhưng đột nhi&ecirc;n, Vy dừng b&uacute;t lại, ngẩng đầu l&ecirc;n bục giảng ngơ ng&aacute;c, l&aacute;t sau lại cười cười th&iacute;ch th&uacute; lắm.<br />\r\n<br />\r\nMinh thấy vậy th&igrave; hiếu k&igrave; nh&igrave;n theo, khi nh&igrave;n l&ecirc;n th&igrave; cậu triệt để đơ người, cảnh tượng kh&ocirc;ng thể tin được diễn ra.<br />\r\n&ldquo; Một con m&egrave;o? Nhưng sao...sao lại&hellip;.chẳng lẽ l&agrave; n&oacute;?&rdquo; Minh tự hỏi, rồi quay sang nh&igrave;n Vy một lần nữa, thấy c&ocirc; b&eacute; vẫn đang cười th&igrave; chợt giống như nghĩ ra c&aacute;i g&igrave;.<br />\r\n<br />\r\nCon m&egrave;o đen n&agrave;y theo c&aacute;ch nh&igrave;n của Minh th&igrave; ch&iacute;nh l&agrave; sinh vật đặc biệt. Hai tai nhọn hoắt, mắt v&agrave;ng rọi to&aacute;t ra &aacute;nh s&aacute;ng lấp l&aacute;nh, chiếc đu&ocirc;i th&igrave; nhỏ v&agrave; d&agrave;i, đặc biệt phần cuối c&ograve;n c&oacute; h&igrave;nh dạng như một mũi t&ecirc;n.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nCon m&egrave;o ở tr&ecirc;n b&agrave;n học, n&oacute; lăn qua lăn lại giống như b&agrave;y tr&ograve;. K&igrave; thực Minh thấy n&oacute; cũng rất dễ thương, c&oacute; điều chuyện n&agrave;y kh&ocirc;ng phải để cậu thưởng thức, m&agrave; l&uacute;c n&agrave;y Minh nh&igrave;n quanh. Ngoại trừ cậu ra th&igrave; kh&ocirc;ng ai kh&aacute;c nh&igrave;n thấy n&oacute;, chỉ c&oacute; Vy b&ecirc;n cạnh li&ecirc;n tục che miệng cười.<br />\r\n<br />\r\n&ldquo; Kh&ocirc;ng lẽ Vy nh&igrave;n thấy &agrave;? Nếu thế thật th&igrave; chứng tỏ cậu ấy cũng c&oacute; si&ecirc;u năng lực giống m&igrave;nh? M&agrave; khoan, cứ thử hỏi xem đ&atilde;.&rdquo; Minh suy nghĩ, rồi quay sang nh&igrave;n Vy n&oacute;i:<br />\r\n<br />\r\n&ldquo; N&agrave;y, cậu kh&ocirc;ng l&agrave;m b&agrave;i &agrave;? Cười g&igrave; thế?&rdquo;<br />\r\n<br />\r\n&ldquo; &Agrave;, xin lỗi nh&eacute;. M&igrave;nh hơi buồn cười x&iacute;u ấy m&agrave;, h&igrave; h&igrave;.&rdquo; Vy trả lời, mắt vẫn liếc liếc chỗ con m&egrave;o.<br />\r\n<br />\r\n&ldquo; Cậu đang nh&igrave;n c&aacute;i g&igrave; đấy?&rdquo; Minh hỏi.<br />\r\n<br />\r\n&ldquo; Kh&ocirc;ng c&oacute; g&igrave;, tự dưng nghĩ đến con m&egrave;o nh&agrave; m&igrave;nh dễ thương th&ocirc;i.&rdquo; Vy trả lời.<br />\r\n<br />\r\n&ldquo; Ồ, nh&agrave; cậu nu&ocirc;i m&egrave;o &agrave;, th&iacute;ch nhỉ. N&oacute; tr&ocirc;ng như thế n&agrave;o?&rdquo; Minh lại hỏi.<br />\r\n<br />\r\n&ldquo; Ừm, n&oacute; c&oacute; bộ l&ocirc;ng m&agrave;u đen, mắt v&agrave;ng, tai th&igrave; nhọn. &Agrave; m&agrave; đu&ocirc;i n&oacute; cũng rất đặc biệt nữa, tr&ocirc;ng rất th&iacute;ch lu&ocirc;n. N&oacute; hay l&agrave;m tr&ograve; lắm, đặc biệt l&agrave; lăn qua lăn lại xong b&agrave;y ra c&aacute;i mặt ng&acirc;y thơ l&agrave;m m&igrave;nh cười suốt.&rdquo; Vy cười đ&aacute;p.<br />\r\n<br />\r\nM&agrave; trong khi nghe đối phương trả lời, Minh đơ người ra. &ldquo; Chẳng phải ch&iacute;nh l&agrave; con m&egrave;o tr&ecirc;n kia &agrave;?&rdquo; cậu nghĩ.<br />\r\n<br />\r\nNhưng kh&ocirc;ng n&oacute;i ra, Minh im lặng nh&igrave;n Vy một hồi, đắn đo kh&ocirc;ng biết c&oacute; n&ecirc;n hỏi thẳng kh&ocirc;ng. Nhưng qua hồi l&acirc;u, Minh quyết định kh&ocirc;ng hỏi, bởi v&igrave; c&ograve;n qu&aacute; nhiều điều m&agrave; cậu kh&ocirc;ng biết về thế giới m&agrave; m&igrave;nh mới kh&aacute;m ph&aacute; ra.<br />\r\n<br />\r\n&ldquo; Cần phải biết th&ecirc;m nữa mới được, nhỡ may đ&acirc;y lại l&agrave; gi&aacute;n điệp th&igrave; sao? Sau đ&oacute; m&igrave;nh lại bị đem ra l&agrave;m th&iacute; nghiệm th&igrave; chết.&rdquo; Minh nghĩ, sau c&ugrave;ng tiếng trống vang l&ecirc;n, đ&atilde; đến giờ ra chơi.<br />\r\n<br />\r\nV&agrave; trong l&uacute;c n&agrave;y ngay tại nh&agrave; Minh, một nh&oacute;m gồm hai mươi con ong v&agrave; một ong đội trưởng đ&atilde; đến nơi. Bọn ch&uacute;ng chia ra lượn lờ xung quanh khu nh&agrave; t&igrave;m kiếm thứ g&igrave; đ&oacute;, sau c&ugrave;ng, một con ong bay đến chỗ đội trưởng của n&oacute; b&aacute;o:<br />\r\n<br />\r\n&ldquo; Đội trưởng, đ&atilde; t&igrave;m thấy x&aacute;c của Ph&oacute; đội trưởng trong bụi cỏ kia!&rdquo;<br />\r\n<br />\r\nOng đội trưởng vội bay lại gần, mắt nh&igrave;n xuống x&aacute;c ong đỏ hồng chỉ c&ograve;n lại ch&uacute;t nhợt nhạt th&igrave; li&ecirc;n im lặng hồi l&acirc;u, sau đ&oacute; n&oacute;i :&rdquo; Mang x&aacute;c c&ocirc; ấy về căn cứ, đợi chỉ huy ra lệnh sau đ&oacute; liền h&agrave;nh động.&rdquo;</p>', '2021-11-30 21:08:21', 4, 1),
(256, 68, 'Đi Chơi Cùng Nhau.', '6', '<p>&ldquo; N&agrave;y Trix, sao m&agrave;y lại tự &yacute; chạy đến đ&acirc;y như vậy? Nhỡ c&oacute; ai ph&aacute;t hiện ra th&igrave; sao?&rdquo; Vy đang trốn trong một g&oacute;c ph&iacute;a sau ng&ocirc;i nh&agrave;, c&ocirc; b&eacute; ngồi xổm xuống, trước mắt l&agrave; con m&egrave;o đen k&igrave; lạ kia.<br />\r\n<br />\r\n&ldquo; Meoo, chủ nh&acirc;n y&ecirc;n t&acirc;m, em rất cẩn thận m&agrave;. Hơn nữa ở v&ugrave;ng n&agrave;y l&agrave;m g&igrave; c&oacute; ai nh&igrave;n thấy được em. Hơn nữa c&oacute; thấy th&igrave; cũng kh&ocirc;ng l&agrave;m g&igrave; được, nh&igrave;n b&ecirc;n ngo&agrave;i em c&oacute; hơi đặc biệt ch&uacute;t nhưng chung quy vẫn giống con m&egrave;o b&igrave;nh thường th&ocirc;i m&agrave;.&rdquo; Con m&egrave;o đen t&ecirc;n Trix cất tiếng n&oacute;i.<br />\r\n<br />\r\nNghe thế, Vy cũng đ&agrave;nh thở d&agrave;i, c&ocirc; b&eacute; đ&agrave;nh bất lực với con vật n&agrave;y, liền n&oacute;i:<br />\r\n<br />\r\n&ldquo; Đừng qu&ecirc;n ch&uacute;ng ta đến đ&acirc;y l&agrave; để l&agrave;m g&igrave;, lần n&agrave;y theo hai vị kia l&agrave; để quan s&aacute;t học hỏi. Gia đ&igrave;nh nh&agrave; ta phải tốn rất nhiều c&ocirc;ng mới mời được bọn họ đấy. M&agrave;y phải biết một nh&acirc;n vật cấp cao như thế l&agrave; c&oacute; &yacute; nghĩ g&igrave;, cẩn th&acirc;n l&agrave; tr&ecirc;n hết.&rdquo;<br />\r\n<br />\r\n&ldquo; Hầy, nghe r&otilde; rồi chủ nh&acirc;n. Em lập tức quay về ngay. Gi&aacute; như m&agrave; chủ nh&acirc;n sớm mở ra được &ldquo; Th&uacute; Nguy&ecirc;n&rdquo; th&igrave; tốt rồi, c&oacute; thể mang em theo b&ecirc;n m&igrave;nh, haizz.&rdquo; Trix n&oacute;i với giọng thất vọng.<br />\r\n<br />\r\n&ldquo; M&egrave;o thối n&agrave;y, d&aacute;m khinh thường chủ nh&acirc;n &agrave;? D&ugrave; sao tao cũng mới c&oacute; mười tuổi, m&agrave;y c&oacute; biết d&ugrave; l&agrave; thi&ecirc;n t&agrave;i cấp quốc gia cũng phải đến mười lăm tuổi mới mở ra Căn Nguy&ecirc;n được kh&ocirc;ng? Th&ocirc;i c&uacute;t về đi!&rdquo; Vy trừng mắt l&ecirc;n n&oacute;i với m&egrave;o Trix, rồi cũng quay người trở lại lớp.<br />\r\n<br />\r\nVy kh&ocirc;ng hề biết l&agrave;, cuộc tr&ograve; chuyện n&agrave;y vừa được nghe hết bởi Minh, cậu d&ugrave;ng năng lực chữ [&Aacute;m] trốn dưới b&oacute;ng c&acirc;y gần đ&oacute; nghe trộm.<br />\r\n<br />\r\nM&agrave; Minh l&uacute;c n&agrave;y dường như đầu &oacute;c nổ tung vậy, xem ra thế giới m&agrave; cậu mới bước v&agrave;o n&agrave;y kh&ocirc;ng đơn giản như thế.<br />\r\n<br />\r\n&ldquo; Vậy l&agrave; vẫn c&ograve;n những người kh&aacute;c như m&igrave;nh nữa &agrave;? Mới l&uacute;c n&atilde;y c&oacute; nghe thấy Căn Nguy&ecirc;n với cả Th&uacute; Nguy&ecirc;n, l&uacute;c trước con ong kia cũng c&oacute; n&oacute;i như vậy th&igrave; phải. Liệu c&oacute; li&ecirc;n quan đến c&aacute;i cột trụ Khởi Nguy&ecirc;n của m&igrave;nh kh&ocirc;ng nhỉ?&rdquo; Minh nằm b&ecirc;n trong chiếc b&oacute;ng c&acirc;y suy nghĩ, đồng thời &yacute; thức tiến v&agrave;o kh&ocirc;ng gian cột trụ của m&igrave;nh.<br />\r\n<br />\r\nCậu thử lại gần quan s&aacute;t hai c&acirc;y trụ, đặc biệt ch&uacute; &yacute; đến cột trụ , c&agrave;ng quan s&aacute;t kĩ th&igrave; c&agrave;ng cảm thấy bản th&acirc;n hiểu biết nhiều hơn về n&oacute;.<br />\r\n<br />\r\n&ldquo; Luồng th&ocirc;ng tin cứ thế tr&agrave;n v&agrave;o đầu m&igrave;nh, k&igrave; lạ thật.&rdquo; Minh thầm nghĩ.<br />\r\n<br />\r\nCứ thế, đến mức Minh qu&ecirc;n lu&ocirc;n cả buổi học, khiến cho c&ocirc; gi&aacute;o phải nhờ người đi t&igrave;m cả buổi, cuối c&ugrave;ng cậu tự động xuất hiện v&agrave; xin lỗi.<br />\r\n&hellip;&hellip;&hellip;&hellip;.<br />\r\n<br />\r\nS&aacute;ng h&ocirc;m sau, một buổi học mới bắt đầu, Minh bước v&agrave;o lớp v&agrave; nh&igrave;n thấy Vy đang ngồi cắm c&uacute;i l&agrave;m b&agrave;i, c&oacute; vẻ đang rất vội.<br />\r\n<br />\r\n&ldquo; N&agrave;y Vy, h&ocirc;m qua sao kh&ocirc;ng l&agrave;m b&agrave;i m&agrave; lại để đến giờ thế n&agrave;y?&rdquo; Minh hỏi trước. Mặc d&ugrave; xảy ra chuyện h&ocirc;m qua nhưng Minh vẫn tỏ ra b&igrave;nh thường với người bạn n&agrave;y, đ&uacute;ng hơn l&agrave; cố gắng sao cho tự nhi&ecirc;n nhất c&oacute; thể.<br />\r\n<br />\r\n&ldquo; Ph&ugrave;...tối qua nh&agrave; m&igrave;nh c&oacute; c&ocirc;ng việc n&ecirc;n kh&ocirc;ng kịp l&agrave;m b&agrave;i tập, l&aacute;t nữa l&agrave; c&ocirc; kiểm tra rồi, haizz.&rdquo; Vy thở d&agrave;i n&oacute;i.<br />\r\n<br />\r\nĐột nhi&ecirc;n, một tập vở xuất hiện trước mặt c&ocirc; b&eacute;, vở ghi t&ecirc;n L&yacute; Nhật Minh. Vy bất ngờ nh&igrave;n đối phương, hỏi: &ldquo; Ồ, c&oacute; sao kh&ocirc;ng đấy? Nhỡ may c&ocirc; bắt được th&igrave; cậu cũng bị phạt..&rdquo;<br />\r\n<br />\r\n&ldquo; Kh&ocirc;ng sao đ&acirc;u, ch&eacute;p nhanh l&ecirc;n!&rdquo; Minh thản nhi&ecirc;n n&oacute;i, sau đ&oacute; ngồi xuống b&agrave;n học.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&ldquo; Ừm, cảm ơn nh&eacute;! H&igrave; h&igrave;.&rdquo; Vy cười vui vẻ n&oacute;i.<br />\r\n<br />\r\n&ldquo; &Agrave; n&agrave;y, nh&agrave; cậu c&oacute; ở c&aacute;ch xa chỗ n&agrave;y kh&ocirc;ng?&rdquo; Vy bỗng nhi&ecirc;n hỏi.<br />\r\n<br />\r\n&ldquo; Nh&agrave; m&igrave;nh cũng kh&aacute; xa, ở ng&ocirc;i l&agrave;ng gi&aacute;p c&aacute;nh đồng ph&iacute;a kia cơ.&rdquo; Minh vừa n&oacute;i vừa chỉ tay qua cửa sổ, tư đ&acirc;y c&oacute; thể thấy được khung cảnh c&aacute;nh đồng rộng lớn.<br />\r\n<br />\r\n&ldquo; Oa, th&iacute;ch thật nhỉ. M&igrave;nh cũng muốn đến c&aacute;nh đồng đ&oacute; thử một lần.&rdquo; Vy h&agrave;o hứng n&oacute;i.<br />\r\n<br />\r\n&ldquo; Thật kh&ocirc;ng? Thực ra cũng chẳng c&oacute; g&igrave; cả đ&acirc;u, nhưng m&agrave; cậu th&iacute;ch th&igrave; l&aacute;t nữa về đi c&ugrave;ng m&igrave;nh?&rdquo; Minh chớp thời cơ hỏi.<br />\r\n<br />\r\n&ldquo; Ờ,...ừm. &Agrave; kh&ocirc;ng, &yacute; m&igrave;nh l&agrave; chỉ muốn ngắm qua một lần th&ocirc;i, kh&ocirc;ng phải muốn để cậu dẫn đi đ&acirc;u, nhớ đấy.&rdquo; Vy bỗng dưng hốt l&ecirc;n n&oacute;i.<br />\r\n<br />\r\n&ldquo; H&igrave; h&igrave;, m&igrave;nh hiểu m&agrave;, kh&ocirc;ng cần lo qu&aacute;.&rdquo; Minh đạt được mục đ&iacute;ch liền h&iacute; hửng cười đ&aacute;p lại.<br />\r\n<br />\r\nD&ugrave; n&oacute;i thế n&agrave;o th&igrave; Vy vẫn l&agrave; một c&ocirc; b&eacute; xinh xắn dễ thương, bất kể c&oacute; &ldquo; si&ecirc;u năng lực&rdquo; hay kh&ocirc;ng th&igrave; vẫn vậy, Minh cố gắng kh&ocirc;ng để chuyện n&agrave;y ảnh hưởng đến cuộc sống của m&igrave;nh qu&aacute; nhiều.<br />\r\n<br />\r\n&ldquo; Nhớ đấy, chỉ đi một l&uacute;c th&ocirc;i v&igrave; m&igrave;nh c&ograve;n phải về sớm.&rdquo; Vy n&oacute;i.<br />\r\n<br />\r\nHai người lại trải qua ng&agrave;y học mệt mỏi, cuối c&ugrave;ng tiếng trống ra về cũng vang l&ecirc;n, l&agrave; gi&acirc;y ph&uacute;t m&agrave; Minh v&agrave; Vy mong chờ nhất.<br />\r\n<br />\r\n&ldquo; Vy ơi, đi th&ocirc;i. Cậu c&oacute; xe kh&ocirc;ng?&rdquo; Minh hỏi. Mặc d&ugrave; cậu biết chắc l&agrave; Vy kh&ocirc;ng c&oacute; xe, v&agrave; cuối c&ugrave;ng hai người sẽ đi chung.<br />\r\n<br />\r\nNhưng để Minh thất vọng l&agrave;, Vy tiến lại gần với chiếc xe đạp m&agrave;u xanh l&aacute;, tr&ecirc;n giỏ xe l&agrave; con m&egrave;o đen đang nằm, Vy mỉm cười n&oacute;i:<br />\r\n<br />\r\n&ldquo; M&igrave;nh vừa mượn được chiếc xe n&agrave;y, c&ugrave;ng đi th&ocirc;i.&rdquo; N&oacute;i xong c&ocirc; b&eacute; ngồi l&ecirc;n xe v&agrave; đạp đi, bỏ lại Minh đang ngơ ng&aacute;c v&igrave; vỡ mộng ph&iacute;a sau.<br />\r\n<br />\r\n&ldquo; C&ocirc; chủ của t&ocirc;i ơi, t&ocirc;i d&aacute;m chắc l&agrave; t&ecirc;n nh&oacute;c kia nghĩ c&ocirc; kh&ocirc;ng c&oacute; xe đấy, v&agrave; cũng chắc chắn rằng cậu ta vừa mới vỡ mộng.&rdquo; M&egrave;o Trix tinh ranh n&oacute;i.<br />\r\n<br />\r\n&ldquo; C&aacute;i n&agrave;y...hừ, đừng lo chuyện linh tinh nữa đi. M&agrave; tao thấy cậu ấy cũng kh&aacute; l&agrave; đẹp trai đ&oacute; chứ, h&igrave; h&igrave;.&rdquo; Vy ng&acirc;y thơ cười n&oacute;i.<br />\r\n<br />\r\n&ldquo; &Ocirc;i trời, con g&aacute;i trẻ thật ng&acirc;y thơ, kh&ocirc;ng biết l&uacute;c n&agrave;o sẽ bị người ta lừa về tay đ&acirc;y.&rdquo; Trix than thở.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&ldquo; D&ugrave; sao th&igrave; cậu ta với tao cũng kh&ocirc;ng thể c&oacute; chuyện g&igrave; được đ&acirc;u, m&agrave;y biết m&agrave;.&rdquo; Vy nhẹ giọng n&oacute;i.<br />\r\n<br />\r\n&ldquo; Ừm, nhưng t&ocirc;i thấy cậu b&eacute; đ&oacute; dường như kh&ocirc;ng b&igrave;nh thường cho lắm.&rdquo; Trix quay lại ph&iacute;a sau nh&igrave;n Minh m&agrave; đ&aacute;nh gi&aacute;.<br />\r\n<br />\r\nTừ l&uacute;c Vy gặp Minh cho đến giờ, Trix lu&ocirc;n cảm thấy bản th&acirc;n đang bị ai đ&oacute; kh&aacute;c ngo&agrave;i Vy đang nh&igrave;n chằm chằm v&agrave;o, tuy nhi&ecirc;n cũng kh&aacute; l&agrave; mơ hồ.<br />\r\n<br />\r\n&ldquo; N&agrave;y Vy, đi chậm th&ocirc;i, đợi với!&rdquo; Minh ở ph&iacute;a sau kh&ocirc;ng nghe thấy cuộc tr&ograve; chuyện của hai người. Cậu tiến đến gần hơn, nhưng sự ch&uacute; &yacute; cố gắng dồn hết v&agrave;o Vy, tr&aacute;nh cho con m&egrave;o đen ph&aacute;t hiện.<br />\r\n<br />\r\nMột l&uacute;c sau, hai người đạp xe đến c&aacute;nh đồng, dọc theo c&ograve;n đường tiến về nh&agrave; Minh.<br />\r\n<br />\r\nNh&igrave;n thấy khung cảnh đồng ruộng giản dị thế n&agrave;y, Vy kh&ocirc;ng k&igrave;m được m&agrave; thốt l&ecirc;n: &ldquo; Đẹp qu&aacute;!!!&rdquo;<br />\r\n<br />\r\nMinh b&ecirc;n cạnh nh&igrave;n thấy Vy cười th&igrave; tự dưng cũng vui theo, cậu quay ra nh&igrave;n c&aacute;nh đồng, cũng thấy n&oacute; thật sự đẹp.<br />\r\n<br />\r\n&ldquo; L&agrave; sao nhỉ? B&igrave;nh thường m&igrave;nh đi qua đ&acirc;y đ&acirc;u c&oacute; cảm gi&aacute;c n&agrave;y?&rdquo; Minh tự hỏi, nhưng lần n&agrave;y lại cố t&iacute;nh n&oacute;i to cho Vy nghe thấy.<br />\r\n<br />\r\nC&ocirc; b&eacute; quay sang nh&igrave;n Minh kh&oacute; hiểu, nhưng chỉ chốc l&aacute;t sau đ&atilde; đỏ hết mặt. Vy cũng kh&ocirc;ng c&oacute; ngốc, nghe một cậu trai n&oacute;i như thế n&agrave;o c&oacute; chuyện kh&ocirc;ng hiểu cơ chứ.<br />\r\n<br />\r\nCả hai lặng im một l&uacute;c l&acirc;u sau c&acirc;u n&oacute;i của Minh, kh&ocirc;ng kh&iacute; ngai ng&ugrave;ng bắt đầu dấy l&ecirc;n trong l&ograve;ng hai đứa trẻ.<br />\r\n<br />\r\n&ldquo; Đi với m&igrave;nh ra chỗ n&agrave;y một ch&uacute;t nh&eacute;!&rdquo; Bỗng Minh l&ecirc;n tiếng, hướng mắt đến hỏi Vy.<br />\r\n<br />\r\n&ldquo; Đi...đi đ&acirc;u cơ? Xa kh&ocirc;ng? M&agrave; l&agrave; chỗ n&agrave;o cơ?&rdquo; Vy bối rối hỏi.<br />\r\n<br />\r\n&ldquo; H&igrave; h&igrave;, chỉ l&agrave; chỗ hồi xưa hay chơi th&ocirc;i, đi một ch&uacute;t nh&eacute;!&rdquo; Minh cười n&oacute;i.<br />\r\n<br />\r\n&ldquo; Ừm, đi.&rdquo;<br />\r\n<br />\r\nThế l&agrave; cả hai xuống xe, song song đi bộ xuống c&aacute;nh đồng, đường đất m&ograve;n dẫn ra một c&aacute;i mương nhỏ, nơi đ&oacute; c&oacute; một c&acirc;y cầu l&agrave;m bằng th&acirc;n c&acirc;y dừa vắt ngang qua, nước ph&iacute;a dưới trong veo, phản chiếu lấy &aacute;nh mặt trời ho&agrave;ng h&ocirc;n đỏ rực.<br />\r\n<br />\r\n&ldquo; Trời, chỗ n&agrave;y m&agrave; ngồi chơi th&igrave; th&iacute;ch phải biết nhỉ? C&ograve;n c&oacute; c&aacute; nữa n&agrave;y&hellip;&rdquo; Vy th&iacute;ch th&uacute; nh&igrave;n xung quanh, quả thực nếu c&oacute; thể so s&aacute;nh th&igrave; n&oacute; giống như một phi&ecirc;n bản thu nhỏ của một con s&ocirc;ng vậy, rất thơ mộng.<br />\r\n<br />\r\n&ldquo; Ừm, đẹp thật.&rdquo; Ngay cả Minh cũng kh&ocirc;ng nghĩ đến nơi n&agrave;y lại đẹp thế, c&oacute; lẽ l&agrave; do cậu chưa từng đến đ&acirc;y v&agrave;o l&uacute;c n&agrave;y. Ng&agrave;y trước chỉ c&oacute; trưa nắng mới chạy ra nghịch nước m&agrave; th&ocirc;i, chỉ nhớ đến sự vui vẻ khi chơi đ&ugrave;a chứ l&agrave;m g&igrave; đ&atilde; biết thưởng thức c&aacute;i đẹp.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nThấy Vy ngồi xuống c&acirc;y cầu, thả hai ch&acirc;n đong đưa trong nước m&aacute;t, miệng th&igrave; nở nụ cười hạnh ph&uacute;c, c&ocirc; b&eacute; nh&igrave;n Minh đang ngại ng&ugrave;ng đứng nh&igrave;n, n&oacute;i:<br />\r\n<br />\r\n&ldquo; Sao vậy? Cậu kh&ocirc;ng lại đ&acirc;y ngồi &agrave;?&rdquo;<br />\r\n<br />\r\nMinh nghe vậy th&igrave; cũng từ từ tiến lại, nhẹ nh&agrave;ng ngồi xuống cạnh Vy, trong l&ograve;ng th&igrave; dường như c&oacute; đắn đo suy nghĩ.<br />\r\n<br />\r\nMinh nh&igrave;n sang thấy m&egrave;o đen vẫn nằm cạnh Vy kh&ocirc;ng rời, cậu chỉ d&aacute;m lướt mắt qua chứ kh&ocirc;ng nh&igrave;n l&acirc;u, sợ bị ph&aacute;t hiện.<br />\r\n<br />\r\n&ldquo; Sao đ&acirc;y? C&oacute; n&ecirc;n n&oacute;i thẳng ra l&agrave; m&igrave;nh cũng c&oacute; thể nh&igrave;n thấy con m&egrave;o n&agrave;y kh&ocirc;ng? Cứ để thế n&agrave;y th&igrave; kh&oacute; xử qu&aacute;.Nhưng n&oacute;i ra rồi th&igrave; liệu c&oacute; c&ograve;n được như vậy nữa kh&ocirc;ng nhỉ?&rdquo; Minh suy nghĩ trong l&ograve;ng.<br />\r\n<br />\r\n&ldquo; N&agrave;y, nghĩ g&igrave; m&agrave; trầm tư thế? N&agrave;y&hellip;&rdquo; Vy thấy Minh im lặng th&igrave; quay sang hỏi.<br />\r\n<br />\r\n&ldquo; &Agrave; kh&ocirc;ng, kh&ocirc;ng c&oacute; g&igrave;. Tại cảm gi&aacute;c hơi lạ th&ocirc;i ấy m&agrave;, h&igrave; h&igrave;.&rdquo; Minh giật m&igrave;nh lắc đầu lia lịa.<br />\r\n<br />\r\n&ldquo; Lạ &aacute;? L&agrave; v&igrave; c&oacute; m&igrave;nh ở đ&acirc;y &agrave;?&rdquo; Vy bỗng hỏi, d&ugrave;ng hết can đảm để hỏi Minh.<br />\r\n<br />\r\n&ldquo; &Agrave;..ờ..chắc vậy. Ờm..m&agrave;, cậu th&iacute;ch chứ? &Yacute; m&igrave;nh l&agrave; th&iacute;ch chỗ n&agrave;y?&rdquo; Minh ấp &uacute;ng hỏi.<br />\r\n<br />\r\nVy c&agrave;ng l&uacute;c c&agrave;ng ngai ng&ugrave;ng, c&ocirc; b&eacute; trong nội t&acirc;m biết rằng giữa hai người c&oacute; khoảng c&aacute;ch kh&ocirc;ng thể x&oacute;a nh&ograve;a, nhưng kh&ocirc;ng hiểu sao vẫn cảm thấy bồn chồn như vậy.<br />\r\n<br />\r\n&ldquo; Tất nhi&ecirc;n l&agrave; th&iacute;ch...rồi.&rdquo; Vy trả lời. C&acirc;u trả lời n&agrave;y khiến Minh đơ người ra, cậu dường như hiểu n&oacute; theo một nghĩa kh&aacute;c, mặt đỏ l&ecirc;n nh&igrave;n Vy.<br />\r\n<br />\r\n&ldquo; Sao thế? Mặt m&igrave;nh c&oacute; g&igrave; &agrave;?&rdquo; Vy hỏi lại, &acirc;m thanh rất b&eacute;.<br />\r\n<br />\r\n&ldquo; &Agrave; kh&ocirc;ng, haha. Cậu th&iacute;ch l&agrave; tốt rồi. Giờ về nh&eacute;, trời cũng sắp tối rồi đấy.&rdquo; Minh cười bảo.<br />\r\n<br />\r\n&ldquo; Ừ đ&uacute;ng rồi, m&igrave;nh qu&ecirc;n mất, su&yacute;t ch&uacute;t nữa l&agrave; muộn rồi, về nh&agrave; lại bị la chết.&rdquo; Vy qu&yacute;nh l&ecirc;n, vội đứng dậy, chạy như bay l&ecirc;n đường tr&ecirc;n.<br />\r\n<br />\r\n&ldquo; N&agrave;y Minh, ng&agrave;y mai lại gặp ở đ&acirc;y nh&eacute;! M&igrave;nh muốn đến đ&acirc;y mỗi ng&agrave;y lu&ocirc;n, tạm biệt.&rdquo; Trước khi đi, Vy quay lại b&aacute;o với Minh một c&acirc;u khiến sự ch&aacute;n nản của cậu tan biến hết, thay v&agrave;o đ&oacute; l&agrave; sự mong chờ đến ng&agrave;y mai hơn.</p>', '2021-11-30 21:09:16', 4, 1),
(257, 68, 'Lần đầu Trải Nghiệm(1)', '7', '<p>Một ng&agrave;y mới bắt đầu, h&ocirc;m nay, Minh vui hơn b&igrave;nh thường, bởi v&igrave; cậu c&oacute; cuộc hẹn đang chờ m&igrave;nh v&agrave;o cuối ng&agrave;y.<br />\r\n<br />\r\nCậu hăng h&aacute;i đạp xe đến trường thật sớm, đi ra khỏi cổng v&agrave; vẫn men theo con đường cũ qua c&aacute;nh đồng.<br />\r\n<br />\r\n&Aacute;nh mắt Minh hướng về nơi h&ocirc;m qua hai đứa ngồi c&ugrave;ng nhau, thầm tưởng tượng ra những điều vui vẻ.<br />\r\n<br />\r\nNhưng bỗng nhi&ecirc;n, Minh dừng xe lại, mắt nh&iacute;u lại nh&igrave;n về một mảnh ruộng gần đ&oacute;, giống như một con vật n&agrave;o đ&oacute; đang ở trong ruộng vậy. N&oacute; b&ograve; từ từ ra s&aacute;t bờ, &acirc;m thanh l&uacute;a x&agrave;o xạc, rồi một giọng n&oacute;i vang l&ecirc;n:<br />\r\n<br />\r\n&ldquo; Chết thật, l&uacute;a b&acirc;y giờ vẫn chưa ch&iacute;n, chưa ăn được. Cứ thế n&agrave;y th&igrave; chết đ&oacute;i mất. Hay l&agrave; v&agrave;o nh&agrave; con người ăn nhỉ, d&ugrave; sao ch&uacute;ng cũng kh&ocirc;ng nh&igrave;n thấy m&igrave;nh. Ừm, c&ograve;n hơn l&agrave; tay kh&ocirc;ng trở về, thủ lĩnh lại h&agrave;nh cho một trận nhớ đời.&rdquo;<br />\r\n<br />\r\nXuất hiện l&agrave; một con chuột x&aacute;m, n&oacute; ng&oacute;c đầu l&ecirc;n hướng nh&igrave;n về ph&iacute;a nh&agrave; d&acirc;n, tỏ ra th&egrave;m thuồng. Nhưng khi vừa nh&igrave;n thấy Minh, n&oacute; cảm nhận được Minh cũng đang nh&igrave;n n&oacute;.<br />\r\n<br />\r\n&ldquo; Hả, thằng nh&oacute;c n&agrave;y nh&igrave;n thấy m&igrave;nh &aacute;? Sao lại thế được?&rdquo; Chuột x&aacute;m giật m&igrave;nh, c&oacute; ch&uacute;t hoảng hốt nhưng rất nhanh lấy lại b&igrave;nh tĩnh.<br />\r\n<br />\r\n&ldquo; Cũng chỉ l&agrave; thằng nh&oacute;c th&ocirc;i, c&oacute; g&igrave; m&agrave; phải sợ chứ?&rdquo;<br />\r\n<br />\r\nNhưng kh&ocirc;ng được bao l&acirc;u, chuột x&aacute;m tắt hẳn nụ cười v&igrave; thấy Minh cũng cười nhưng l&agrave; nụ cười đầy sự nguy hiểm.<br />\r\n<br />\r\n&ldquo; L&aacute;ch c&aacute;ch..&rdquo; m thanh gi&ograve;n tan vang l&ecirc;n. Kh&iacute; lạnh u &aacute;m men theo từ ch&acirc;n Minh lan qua d&ograve;ng nước rồi d&iacute;nh v&agrave;o người chuột x&aacute;m khiến n&oacute; cứng người tại chỗ, kh&ocirc;ng gian xung quanh đều bị đ&oacute;ng băng tuy rằng chỉ l&agrave; phạm vi nhỏ.<br />\r\n<br />\r\n&ldquo; Hở, sao thế n&agrave;y? Thằng nh&oacute;c kia kh&ocirc;ng lẽ&hellip;&rdquo; Chuột x&aacute;m sợ h&atilde;i, nh&igrave;n Minh như nh&igrave;n một t&ecirc;n s&aacute;t nh&acirc;n.<br />\r\n<br />\r\nC&ograve;n Minh th&igrave; kh&ocirc;ng nghĩ nhiều như thế, mỉm cười lại gần con chuột, vừa nghĩ:&rdquo; Tr&uacute;ng m&aacute;nh rồi. Phen n&agrave;y phải tra ra tận ổ của n&oacute; mới được.&rdquo;<br />\r\n<br />\r\n&ldquo; N&agrave;y con chuột x&aacute;m, n&oacute;i cho tao biết m&agrave;y c&ograve;n đồng bọn kh&ocirc;ng, sống ở chỗ n&agrave;o?&rdquo; Minh hỏi.<br />\r\n<br />\r\n&ldquo; Hả? Kh&ocirc;ng được, kh&ocirc;ng n&oacute;i được.&rdquo; Chuột x&aacute;m sợ h&atilde;i trả lời.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nMinh thấy thế th&igrave; kh&oacute; chịu, kh&ocirc;ng hiểu sao lũ n&agrave;y cứ th&iacute;ch chơi tr&ograve; can đảm nữa. Nhưng v&igrave; sắp đến giờ v&agrave;o lớp n&ecirc;n Minh kh&ocirc;ng n&oacute;i nhiều, cũng kh&ocirc;ng giết con chuột m&agrave; đ&oacute;ng băng khắp người n&oacute; rồi nhặt lấy c&aacute;i t&uacute;i đen bọc lại, để v&agrave;o giỏ xe đạp.<br />\r\n<br />\r\n&ldquo; Đến tối về để tao h&agrave;nh m&agrave;y ra b&atilde;, hừ hừ.&rdquo; Minh n&oacute;i thầm rồi lập tức đạp vội xe đi v&igrave; sợ muộn học.<br />\r\n<br />\r\nĐến chiều h&ocirc;m đ&oacute;, Minh c&ugrave;ng Vy lại như h&ocirc;m qua đến chỗ c&acirc;y cầu giữa c&aacute;nh đồng chơi. Cả hai tuy chả n&oacute;i g&igrave; nhiều nhưng cảm gi&aacute;c lại rất th&iacute;ch, kh&ocirc;ng ai cảm thấy ch&aacute;n cả.<br />\r\n<br />\r\nHai đứa trẻ ngồi t&acirc;m sự với nhau những chuyện về gia đ&igrave;nh v&agrave; cả mơ ước nữa. Để cho Minh bất ngờ l&agrave; Vy lại n&oacute;i sau n&agrave;y sẽ l&agrave;m cảnh s&aacute;t, tuy Minh biết &ldquo; cảnh s&aacute;t &ldquo; m&agrave; Vy n&oacute;i đến kh&ocirc;ng giống b&igrave;nh thường nhưng cũng kh&ocirc;ng n&oacute;i g&igrave; nhiều, cậu thầm ch&uacute;c cho Vy sẽ thực hiện được ước mơ,<br />\r\n<br />\r\n&ldquo; Thế Minh th&igrave; sao? Minh sau n&agrave;y muốn l&agrave;m g&igrave;?&rdquo; Vy quay sang hỏi.<br />\r\n<br />\r\n&ldquo; M&igrave;nh &aacute;, m&igrave;nh chưa từng nghĩ đến c&aacute;i đ&oacute;. C&oacute; lẽ sau n&agrave;y c&aacute;i g&igrave; đến với m&igrave;nh th&igrave; l&agrave;m c&aacute;i đ&oacute; th&ocirc;i, miễn sao bản th&acirc;n kh&ocirc;ng gh&eacute;t l&agrave; được.&rdquo; Minh trả lời. Đ&uacute;ng thật l&agrave; cậu chưa từng nghĩ đến tương lai sau n&agrave;y.<br />\r\n<br />\r\nL&uacute;c trước kh&ocirc;ng nghĩ đến, m&agrave; b&acirc;y giờ cuộc sống lại c&oacute; thay đổi lớn như thế c&agrave;ng khiến Minh đắn đo hơn.<br />\r\n<br />\r\n&ldquo; Nếu cho Vy biết m&igrave;nh cũng giống cậu ấy th&igrave; c&oacute; lẽ hai đứa sẽ được ở gần nhau chăng?&rdquo; Minh bỗng nổi l&ecirc;n suy nghĩ.<br />\r\n<br />\r\nNhưng bất chợt, Vy giống như giật m&igrave;nh bật dậy, nh&igrave;n về con m&egrave;o đen Trix như ra hiệu g&igrave; đ&oacute;, rồi quay sang n&oacute;i với Minh:<br />\r\n<br />\r\n&ldquo; Xin lỗi, h&ocirc;m nay phải về sớm rồi. Để ng&agrave;y mai nh&eacute;!&rdquo; N&oacute;i xong Vy liền tức tốc chạy về.<br />\r\n<br />\r\nM&agrave; c&ograve;n Minh th&igrave; tỏ ra nghi vấn, những h&agrave;nh động của Vy với m&egrave;o đen cậu đều nh&igrave;n thấy hết, biết chắc l&agrave; c&oacute; chuyện g&igrave; đ&oacute; xảy ra c&agrave;ng khiến cậu t&ograve; m&ograve; hơn.<br />\r\n<br />\r\nNh&igrave;n trời sắp tối, Minh nảy ra &yacute; tưởng, cậu chạy theo Vy, vận dụng chữ [&Aacute;m] để ẩn m&igrave;nh trong b&oacute;ng tối. Trong đ&oacute;, Minh c&oacute; thể di chuyển nhanh hay chậm t&ugrave;y th&iacute;ch, m&agrave; việc n&agrave;y cũng kh&ocirc;ng tốn mấy sức.<br />\r\n<br />\r\nQua nhiều ng&agrave;y vụng trộm luyện tập v&agrave;o ban đ&ecirc;m th&igrave; hiện tại Minh sử dụng năng lực rất thuần thục, c&oacute; điều vẫn c&oacute; những h&atilde;n chế nhất định m&agrave; Minh nghĩ l&agrave; do bản th&acirc;n thiếu kinh nghiệm.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nẨn n&uacute;p trong b&oacute;ng tối đuổi theo Vy, để Minh bất ngờ l&agrave; lại c&oacute; hai người kh&aacute;c đột nhi&ecirc;n xuất hiện trước mặt c&ocirc; b&eacute;, một nam một nữ nh&igrave;n kh&aacute; l&agrave; trẻ.<br />\r\n<br />\r\n&ldquo; Hai người n&agrave;y l&agrave; ai? Quan hệ g&igrave; với Vy đ&acirc;y?&rdquo; Minh tự hỏi, cũng &acirc;m thầm đề ph&ograve;ng.<br />\r\n<br />\r\n&ldquo; Vy &agrave;, sao mấy h&ocirc;m nay hay về muộn thế? Kh&ocirc;ng muốn học nữa hả?&rdquo; Người đ&agrave;n &ocirc;ng nghi&ecirc;m nghị n&oacute;i.<br />\r\n<br />\r\n&ldquo; Em...em c&oacute; ch&uacute;t việc với bạn ở lớp ấy m&agrave;. Nhưng sao h&ocirc;m nay lại bắt đầu sớm vậy thầy Sơn&rdquo;. Vy nhẹ giọng đ&aacute;p.<br />\r\n<br />\r\n&ldquo; Ph&iacute;a tr&ecirc;n b&aacute;o tin xuống, ph&aacute;t hiện hoạt động của một nh&oacute;m D&atilde; qu&aacute;i gần đ&acirc;y, h&igrave;nh như l&agrave; bọn chuột x&aacute;m th&igrave; phải. H&ocirc;m nay tự dưng ch&uacute;ng hoạt động nhiều hơn b&igrave;nh thường n&ecirc;n ch&uacute;ng ta mới ph&aacute;t hiện ra.&rdquo; Người phụ nữ trả lời, giọng n&oacute;i c&oacute; vẻ dịu d&agrave;ng hơn.<br />\r\n<br />\r\n&ldquo; C&ocirc; Nguyệt, vậy giờ ch&uacute;ng ta bắt ch&uacute;ng sao?&rdquo; Vy lại hỏi, l&uacute;c n&agrave;y c&ocirc; b&eacute; dường như rất hưng phấn.<br />\r\n<br />\r\n&ldquo; Ừm, nhưng đ&aacute;m chuột n&agrave;y c&oacute; thủ lĩnh cấp năm, em chưa thể giải quyết được. Đợi l&uacute;c ch&uacute;ng t&ocirc;i giết n&oacute; xong th&igrave; sẽ bắt v&agrave;i con yếu hơn cho em luyện tập.&rdquo; Thầy Sơn n&oacute;i.<br />\r\n<br />\r\nVy nghe xong th&igrave; c&oacute; ch&uacute;t buồn, nhưng c&ocirc; b&eacute; cũng biết m&igrave;nh kh&ocirc;ng c&oacute; khả năng n&ecirc;n chấp nhận.<br />\r\n<br />\r\n&ldquo; Vy &agrave;, cứ từ từ em nh&eacute;. Con người ch&uacute;ng ta cần phải từng bước luyện tập th&igrave; mới mạnh dần l&ecirc;n được, kh&ocirc;ng như những Dị qu&aacute;i sinh ra đ&atilde; c&oacute; sẵn sức mạnh đ&acirc;u. Hơn nữa em cũng phải đợi đến khi mở được Căn Nguy&ecirc;n th&igrave; mới coi l&agrave; ch&iacute;nh thức nhập m&ocirc;n , l&uacute;c đ&oacute; với sự gi&uacute;p đỡ của gia đ&igrave;nh em th&igrave; chẳng mấy chốc m&agrave; mạnh như thầy c&ocirc; đ&acirc;u.&rdquo; C&ocirc; Nguyệt &ocirc;n tồn giảng giải.<br />\r\n<br />\r\nMinh n&uacute;p trong b&oacute;ng tối nghe thấy mười mươi, v&ocirc; số th&ocirc;ng tin kh&aacute;c lại đến với cậu.<br />\r\n<br />\r\n&ldquo; Dị qu&aacute;i? D&atilde; qu&aacute;i? Lại c&ograve;n g&igrave; m&agrave; luyện tập với cả nhập m&ocirc;n nữa? Trời ạ, thật muốn lao ra hỏi hết cho r&otilde; qu&aacute;!&rdquo; Minh thấp thỏm trong l&ograve;ng, sự hiếu k&igrave; của một cậu b&eacute; l&agrave; rất m&atilde;nh liệt, nhưng Minh k&igrave;m lại được.<br />\r\n<br />\r\nL&aacute;t sau, khi trời đ&atilde; tối om, ba người bắt đầu nhanh ch&oacute;ng chạy về ph&iacute;a c&aacute;nh đồng, thầy Sơn v&agrave; c&ocirc; Nguyệt giống như vận động vi&ecirc;n vậy, chạy nhanh thoăn thoắt băng qua c&aacute;nh đồng, c&ograve;n theo sau l&agrave; Vy lại đang cười con m&egrave;o Trix l&uacute;c n&agrave;y đ&atilde; biến lớn như một con b&aacute;o đen.<br />\r\n<br />\r\nMinh th&igrave; thảnh thơi tr&ocirc;i theo sau, ở trong b&oacute;ng tối th&igrave; cậu như c&aacute; trong nước, thoải m&aacute;i tung tăng m&agrave; kh&ocirc;ng sợ g&igrave;. Ngay cả hai người b&iacute; ẩn được Vy gọi l&agrave; thầy c&ocirc; kia cũng kh&ocirc;ng thể ph&aacute;t hiện ra được.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&ldquo; Đến nơi rồi. Theo như tin tức th&igrave; ch&iacute;nh l&agrave; chỗ n&agrave;y.&rdquo; Thầy sơn bỗng dừng lại, hướng về ph&iacute;a một mảnh ruộng bỏ hoang n&oacute;i.<br />\r\n<br />\r\n&ldquo; Mảnh ruộng n&agrave;y nguy&ecirc;n do l&agrave; ở gần khu nghĩa trang n&ecirc;n kh&ocirc;ng ai d&aacute;m trồng trọt g&igrave;, bọn ch&uacute;ng chọn địa b&agrave;n ở đ&acirc;y cũng hợp l&iacute;.&rdquo; C&ocirc; Nguyệt cũng n&oacute;i.<br />\r\n<br />\r\n&ldquo; Vy, em đứng n&uacute;p từ đ&acirc;y quan s&aacute;t một l&uacute;c. Đợi đến khi t&ocirc;i ra hiệu th&igrave; h&atilde;y tiến đến. Trix nhớ bảo vệ c&ocirc; chủ thật tốt, như vậy th&igrave; m&agrave;y mới c&oacute; cơ hội nhận được Ch&acirc;n Huyết.&rdquo; Thầy Sơn nhắc nhở, sau đ&oacute; c&ugrave;ng với c&ocirc; Nguyệt t&aacute;ch ra hai b&ecirc;n từ từ tiếp cận hang ổ lũ chuột x&aacute;m.<br />\r\n<br />\r\n&ldquo; Chuột x&aacute;m thực chất l&agrave; c&aacute;ch gọi của ch&uacute;ng ta th&ocirc;i, t&ecirc;n đ&uacute;ng của bọn ch&uacute;ng l&agrave; tộc lo&agrave;i Nanh X&aacute;m, l&agrave; lo&agrave;i chuy&ecirc;n đi ph&aacute; hủy mọi thứ m&agrave; ch&uacute;ng gh&eacute;t bằng h&agrave;m răng của m&igrave;nh. Nhưng do hạn chế bởi m&ocirc;i trường thế giới ch&uacute;ng ta m&agrave; tộc lo&agrave;i n&agrave;y kh&ocirc;ng đủ sức để l&agrave;m thế. Tuy nhi&ecirc;n với kẻ yếu như c&ocirc; chủ đ&acirc;y th&igrave; vẫn phải cẩn thận, c&ocirc; kh&ocirc;ng l&agrave; g&igrave; với thủ lĩnh của bọn ch&uacute;ng đ&acirc;u.&rdquo; Trix giải th&iacute;ch cho Vy hiểu r&otilde; vấn đề, để cho c&ocirc; b&eacute; ng&acirc;y thơ n&agrave;y nhận thức đ&uacute;ng ranh giới của bản th&acirc;n.<br />\r\n<br />\r\nM&agrave; một b&ecirc;n Minh cũng nghe thấy hết, cậu chăm ch&uacute; ghi nhớ từng ch&uacute;t một, bởi v&igrave; điều n&agrave;y gi&uacute;p t&iacute;nh t&ograve; m&ograve; của cậu giảm bớt.<br />\r\n<br />\r\n&ldquo;B&ugrave;m..uỳnh&hellip;&rdquo; Tiếng nổ vang l&ecirc;n, k&egrave;m theo l&agrave; một loại dao động trong kh&ocirc;ng kh&iacute; giống như lực trường lan ra xung quanh tạo th&agrave;nh bức m&agrave;n. Bức m&agrave;n n&agrave;y bao phủ phạm vi b&aacute;n k&iacute;nh 50m, khiến &acirc;m thanh v&agrave; &aacute;nh s&aacute;ng kh&ocirc;ng thể lọt ra b&ecirc;n ngo&agrave;i được.<br />\r\n<br />\r\n&ldquo; Đ&acirc;y l&agrave; kĩ năng cơ bản khi mở ra được Căn Nguy&ecirc;n, . Kĩ năng tạo kết giới ngăn chặn mọi xung năng lượng tỏa ra, t&ugrave;y v&agrave;o cấp độ của người d&ugrave;ng m&agrave; c&oacute; độ bền kh&aacute;c nhau.&rdquo; Trix tiếp tục giảng giải.<br />\r\n<br />\r\nB&ecirc;n trong , cảnh tượng m&aacute;u me diễn ra&hellip;<br />\r\n<br />\r\nNgười đ&agrave;n &ocirc;ng lạnh l&ugrave;ng, tay bỗng hiện ra c&acirc;y gậy ngắn, anh ta nhẹ nh&agrave;ng lướt qua đ&aacute;m Nanh X&aacute;m l&uacute;c n&agrave;y đ&atilde; h&oacute;a về trạng th&aacute;i nguy&ecirc;n bản,, cao đến ngang lưng anh ta.<br />\r\n<br />\r\nNhưng c&acirc;y gậy giống như lưỡi kiếm sắc b&eacute;n, từng đường cắt qua th&acirc;n thể ch&uacute;ng một c&aacute;ch nhẹ nh&agrave;ng, v&agrave; cứ mỗi lần như thế l&agrave; lại c&oacute; m&ugrave;i kh&eacute;t bốc l&ecirc;n.<br />\r\n<br />\r\n&ldquo; Trời, ngầu dữ. Năng lực của ch&uacute; n&agrave;y l&agrave; g&igrave; nhỉ? Nh&igrave;n c&oacute; vẻ như c&oacute; thể cắt đứt mọi thứ vậy.&rdquo; Minh nấp trong b&oacute;ng tối h&acirc;m mộ c&ugrave;ng h&agrave;o hứng, lần đầu ti&ecirc;n cậu thấy cảnh h&agrave;nh động ngo&agrave;i đời thực như thế n&agrave;y, đ&uacute;ng l&agrave; giống với những g&igrave; cậu thường hay tưởng tượng đến.</p>', '2021-11-30 21:09:52', 5, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(258, 68, 'Lần đầu Trải Nghiệm(2)', '8', '<p>Cuộc chiến vẫn tiếp tục xảy ra, hang ổ của đ&aacute;m Nanh X&aacute;m tuy chỉ g&oacute;i gọn trong một mảnh ruộng nhưng số lượng của ch&uacute;ng kh&ocirc;ng phải vừa. Nguy&ecirc;n nh&acirc;n l&agrave; ch&uacute;ng b&igrave;nh thường đều d&ugrave;ng k&iacute;ch thước nhỏ để tồn tại n&ecirc;n kh&ocirc;ng chiếm mấy diện t&iacute;ch.<br />\r\n<br />\r\nNhưng ngay l&uacute;c n&agrave;y, h&agrave;ng trăm con Nanh X&aacute;m h&oacute;a trở lại h&igrave;nh dạng vốn c&oacute;, ch&uacute;ng lũ lượt lao đến tấn c&ocirc;ng hai người bằng h&agrave;m răng sắc nhọn, lấp l&oacute;e m&agrave;u b&agrave;ng bạc.<br />\r\n<br />\r\n&ldquo; Vẫn chỉ l&agrave; mấy con t&eacute;p riu, c&ocirc; Nguyệt, mau xử l&iacute; nhanh ch&oacute;ng đi!&rdquo; Thầy Sơn bỗng k&ecirc;u l&ecirc;n, tay cầm chiếc gậy sắt hướng l&ecirc;n trời.<br />\r\n<br />\r\nm thanh tia điện xuất hiện, xung quanh c&acirc;y gậy &aacute;nh l&ecirc;n những tia s&aacute;ng bạc, rồi trong nh&aacute;y mắt, thầy Sơn vung c&acirc;y gậy xuống. Như một tia s&eacute;t từ tr&ecirc;n trời, &acirc;m thanh tiếng nổ mạnh đến mức hất văng đ&aacute;m người đang quan s&aacute;t từ xa ra.<br />\r\n<br />\r\n&ldquo; Trời, sấm s&eacute;t &aacute;? Kinh khủng dữ vậy?&rdquo; Minh cũng bị hất văng đi, khi nh&igrave;n lại th&igrave; đ&atilde; thấy đ&aacute;m Nanh X&aacute;m ch&aacute;y đen nằm la liệt tr&ecirc;n đất.<br />\r\n<br />\r\nNhưng số lượng ch&uacute;ng vẫn c&ograve;n rất nhiều, lần n&agrave;y Minh ch&uacute; &yacute; thấy c&ocirc; Nguyệt bắt đầu ra tay. Kh&ocirc;ng d&ugrave;ng vũ kh&iacute; n&agrave;o cả, người phụ nữ đứng im một chỗ mặc cho đ&aacute;m chuột tấn c&ocirc;ng. Nhưng cứ tiếp cận phạm vi b&aacute;n k&iacute;nh năm m&eacute;t xung quanh th&igrave; ch&uacute;ng đều k&ecirc;u la, rồi cả người bỗng dưng bị ch&eacute;m th&agrave;nh từng mảnh.<br />\r\n<br />\r\n&ldquo; C&ocirc; Nguyệt mở ra Căn Nguy&ecirc;n, h&igrave;nh th&aacute;i l&agrave; những sợi tơ mỏng nhưng lại cứng gần như sắt th&eacute;p, dễ d&agrave;ng qua mắt kẻ địch m&agrave; ch&eacute;m ch&uacute;ng th&agrave;nh từng mảnh. Chỉ cần đan những sợi tơ đ&oacute; th&agrave;nh lưới bao bọc xung quanh th&igrave; coi như l&agrave; tấm m&agrave;n vừa c&ocirc;ng vừa thủ mạnh mẽ.&rdquo; Trix đứng c&ugrave;ng Vy chăm ch&uacute; quan s&aacute;t m&agrave; giải th&iacute;ch. Mặc d&ugrave; nghe kh&ocirc;ng biết bao nhi&ecirc;u lần nhưng Vy vẫn cảm thấy bất ngờ.<br />\r\n<br />\r\n&ldquo; Ồ, vậy th&igrave; nguy hiểm qu&aacute; rồi! Chậc chậc, người phụ nữ n&agrave;y tr&ocirc;ng xinh đẹp vậy m&agrave;. Giống như trong phim th&igrave; thường sẽ l&agrave; những nữ s&aacute;t thủ m&aacute;u lạnh nhỉ?&rdquo; Minh ở một b&ecirc;n nghe trộm th&igrave; thầm cảm th&aacute;n.<br />\r\n<br />\r\n&ldquo; Xoẹt..xoẹt..&rdquo; C&ocirc; Nguyệt vung tay một c&aacute;i, những sợi tơ v&ocirc; h&igrave;nh lan rộng ra xung quanh, uốn &eacute;o loạn xạ khiến tất cả Nanh X&aacute;m đều chưa kịp phản ứng m&agrave; chết, m&agrave; c&aacute;i chết n&agrave;y lại m&aacute;u me v&ocirc; c&ugrave;ng.<br />\r\n<br />\r\n&ldquo; C&oacute; trẻ nhỏ đấy, ra tay bớt bớt lại d&ugrave;m!&rdquo; Thầy Sơn lại gần n&oacute;i với c&ocirc; Nguyệt.<br />\r\n<br />\r\n&ldquo; H&igrave; h&igrave;, d&ugrave; sao th&igrave; sau n&agrave;y n&oacute; cũng phải thấy th&ocirc;i, thậm ch&iacute; l&agrave; tự ra tay t&agrave;n s&aacute;t như vậy nữa m&agrave;.&rdquo; C&ocirc; Nguyệt cười cười đ&aacute;p.<br />\r\n<br />\r\nM&agrave; từ xa, Vy nh&igrave;n thấy đ&aacute;m Nanh X&aacute;m bị phanh ra từng mảnh, m&aacute;u chảy nhuộm đầy đất đ&atilde; trợn tr&ograve;n mắt, bịt miệng lại v&igrave; cảm thấy buồn n&ocirc;n.<br />\r\n<br />\r\nNgay cả Minh cũng kh&ocirc;ng k&igrave;m được m&agrave; run bần bật, lồng ngực đập th&igrave;nh thịch, thầm nhủ kh&ocirc;ng n&ecirc;n tiếp x&uacute;c nhiều với người phụ nữ vẻ ngo&agrave;i hiền hậu kia.<br />\r\n&ldquo; Gaooo...gừ gừ.&rdquo; Tiếng th&eacute;t lớn ph&aacute;t ra từ dưới l&ograve;ng đất khiến mặt đất rung l&ecirc;n, rồi &ldquo;uỳnh&rdquo; một c&aacute;i, từ b&ecirc;n dưới một con Nanh X&aacute;m cực lớn nhảy l&ecirc;n.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nCon n&agrave;y lớn hơn tất cả những con c&ograve;n lại, cao hơn thầy Sơn một c&aacute;i đầu, h&agrave;m răng b&agrave;ng bạc d&iacute;nh đầy nước r&atilde;i, đ&ocirc;i mắt đỏ sọng nh&igrave;n hai con người trước mắt n&oacute;.<br />\r\n<br />\r\n&ldquo; Lo&agrave;i người, mau c&uacute;t đi. Chẳng mấy chốc c&aacute;c ngươi sẽ th&agrave;nh n&ocirc; lệ, đến l&uacute;c đ&oacute; c&oacute; thể ta sẽ cho hai ngươi đặc c&aacute;ch l&agrave;m đồ ăn thượng hạng phục vụ c&aacute;c vị kia.&rdquo; Nanh X&aacute;m khổng lồ n&oacute;i.<br />\r\n<br />\r\n&ldquo; Ồ, D&atilde; qu&aacute;i cấp bốn &agrave;, kh&ocirc;ng phải thủ lĩnh. Mau gọi thủ lĩnh bọn ngươi ra đ&acirc;y nhanh l&ecirc;n, sắp khuya rồi.&rdquo; Thầy Sơn hờ hững n&oacute;i với con Nanh X&aacute;m n&agrave;y.<br />\r\n<br />\r\n&ldquo; Hừ hừ, thủ lĩnh biết c&aacute;c ngươi sẽ tới, ng&agrave;i rời đi l&acirc;u rồi. Nhưng m&agrave; chỉ cần ta ở đ&acirc;y cũng đủ rồi, cho c&aacute;c ngươi biết d&ugrave; chỉ l&agrave; phụ t&aacute; nhưng ta vẫn nghiền n&aacute;t được hết c&aacute;c ngươi.&rdquo; Nanh X&aacute;m gầm gừ n&oacute;i.<br />\r\n<br />\r\n&ldquo; Th&ocirc;i th&ocirc;i, đ&atilde; vậy th&igrave; l&agrave;m nhanh gọn vậy. Nhường phụ nữ l&agrave; điều n&ecirc;n l&agrave;m nhỉ? Thầy Sơn quay sang mỉm cười n&oacute;i với c&ocirc; Nguyệt.<br />\r\n<br />\r\n&ldquo; Hừ, bảo sao m&atilde;i kh&ocirc;ng c&oacute; ai y&ecirc;u l&agrave; phải.&rdquo; C&ocirc; Nguyệt hậm hực bước l&ecirc;n, xung quanh tỏa ra những sợi tơ gần như v&ocirc; h&igrave;nh bao bọc.<br />\r\n<br />\r\n&ldquo; Đ&atilde; l&agrave;m th&igrave; l&agrave;m cho ch&oacute;t, cho c&ocirc; b&eacute; thấy trận chiến m&aacute;u me thực sự đi.&rdquo; Thầy Sơn n&oacute;i nhỏ.<br />\r\n<br />\r\nỞ trung t&acirc;m, c&ocirc; Nguyệt với Nanh X&aacute;m đứng đối diện nhau, loại &aacute;p lực đ&aacute;ng sợ va chạm tạo th&agrave;nh những cơn gi&oacute; xo&aacute;y.<br />\r\n<br />\r\n&ldquo; V&egrave;o..&rdquo; Nanh X&aacute;m x&ocirc;ng l&ecirc;n trước, tốc độ tỉ lệ nghịch với cơ thể, như một c&aacute;i b&oacute;ng xuất hiện ph&iacute;a sau c&ocirc; Nguyệt. Nhưng đột nhi&ecirc;n, &acirc;m thanh kim loại va chạm vang l&ecirc;n, l&agrave; do Nanh X&aacute;m va phải sợi tơ của c&ocirc; Nguyệt.<br />\r\n<br />\r\n&ldquo; Ồ, đ&atilde; h&oacute;a cứng được bộ l&ocirc;ng rồi sao? Nhưng c&oacute; vẻ như vẫn c&oacute; chỗ chưa được nhỉ?&rdquo; C&ocirc; Nguyệt cười n&oacute;i, mắt nh&igrave;n v&agrave;o phần bụng của Nanh X&aacute;m, nơi c&oacute; một vết cắt đang rỉ m&aacute;u.<br />\r\n<br />\r\n&ldquo; Lo&agrave;i Nanh X&aacute;m sinh ra với sức mạnh cường h&oacute;a cơ thể th&agrave;nh sắt th&eacute;p, nhưng đa phần tập trung v&agrave;o bộ răng coi như vũ kh&iacute;. C&oacute; điều những con cấp cao c&ograve;n tập trung cường h&oacute;a những bộ phận kh&aacute;c nữa, m&agrave; cường h&oacute;a bộ l&ocirc;ng ho&agrave;n to&agrave;n ch&iacute;nh l&agrave; dấu hiệu của Thủ Lĩnh. Con n&agrave;y coi như gần đạt đến cấp năm rồi, cũng kh&ocirc;ng vừa đ&acirc;u.&rdquo; Thầy Sơn đứng xa quan s&aacute;t n&oacute;i, cố &yacute; n&oacute;i to để Vy từ xa nghe được.<br />\r\n<br />\r\n&ldquo; Mấy tr&ograve; vặt v&atilde;nh của ngươi cũng chỉ l&agrave;m ta trầy xước ch&uacute;t m&agrave; th&ocirc;i. Tiếp tục đi!&rdquo; Nanh X&aacute;m kh&ocirc;ng chịu thua lại lao l&ecirc;n, để lại t&agrave;n b&oacute;ng mờ trong m&agrave;n đếm tối.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nC&ocirc; Nguyệt cười cười, mắt liếc nh&igrave;n xung quanh. Trong mắt c&ocirc;, những sợi tơ ng&agrave;y c&agrave;ng chia nhỏ ra, d&agrave;n th&agrave;nh những chiếc gai nhọn hướng ra xung quanh.<br />\r\n<br />\r\nNanh X&aacute;m vừa di chuyển li&ecirc;n tục xung quanh vừa d&ugrave;ng răng cắn ph&aacute; về ph&iacute;a trước, một c&aacute;ch l&agrave;m qu&eacute;t sạch chướng ngại vật m&agrave; kh&ocirc;ng cần biết ch&uacute;ng ở đ&acirc;u.<br />\r\n<br />\r\nNhưng đồng thời n&oacute; cũng phải tr&aacute;nh đi những sợi tơ từ ph&iacute;a sau lao đến, do đ&oacute; kh&ocirc;ng c&aacute;ch n&agrave;o lại gần được đối phương.<br />\r\n<br />\r\n&ldquo; C&aacute;ch chiến đấu của người n&agrave;y hay thật, như vậy kh&aacute;c n&agrave;o chơi đ&ugrave;a với đối phương, c&oacute; khi ngồi đ&oacute; vừa uống tr&agrave; đọc s&aacute;ch th&igrave; kẻ địch cũng kh&ocirc;ng l&agrave;m g&igrave; được.&rdquo; Minh quan s&aacute;t m&agrave; thấy phấn kh&iacute;ch, cảm gi&aacute;c s&ocirc;i tr&agrave;o trong người ng&agrave;y c&agrave;ng mạnh.<br />\r\n<br />\r\n&ldquo; C&ocirc; Nguyệt mạnh qu&aacute;! N&agrave;y Trix, m&agrave;y nghĩ tao c&oacute; thể mạnh như thế kh&ocirc;ng?&rdquo; Vy đứng quan s&aacute;t cũng h&acirc;m mộ kh&ocirc;ng th&ocirc;i.<br />\r\n<br />\r\n&ldquo; Ừm, nếu tương lai c&ocirc; chủ c&oacute; thể mở ra Căn Nguy&ecirc;n xịn s&ograve; một ch&uacute;t v&agrave; cộng th&ecirc;m khế ước với t&ocirc;i th&igrave; cũng kh&ocirc;ng phải kh&ocirc;ng thể.&rdquo; Trix trả lời.<br />\r\n<br />\r\n&ldquo; Vậy c&ocirc; Nguyệt l&agrave; c&oacute; Căn Nguy&ecirc;n g&igrave;?&rdquo; Vy t&ograve; m&ograve; hỏi.<br />\r\n<br />\r\n&ldquo; L&agrave; Căn Nguy&ecirc;n của nh&agrave; họ Vũ, c&ocirc; chủ biết chứ? Gọi l&agrave; Huyết Kim Căn Nguy&ecirc;n, đệ tam Căn Nguy&ecirc;n trong giới s&aacute;t thủ. N&oacute; c&oacute; rất nhiều c&aacute;ch vận dụng kh&aacute;c nhau, t&ugrave;y v&agrave;o x&aacute;c suất thức tỉnh. M&agrave; c&ocirc; Nguyệt th&igrave; lại đ&uacute;ng l&agrave; si&ecirc;u may mắn, thức tỉnh được h&igrave;nh th&aacute;i b&aacute; đạo n&agrave;y.&rdquo; Trix trả lời.<br />\r\n<br />\r\n&ldquo; Haizzz, vậy c&oacute; Căn Nguy&ecirc;n tốt l&agrave; một chuyện, m&agrave; h&igrave;nh th&aacute;i Căn Nguy&ecirc;n lại l&agrave; chuyện kh&aacute;c &agrave;?&rdquo; Vy than thở, thầm cầu mong may mắn của m&igrave;nh cao một ch&uacute;t.<br />\r\n<br />\r\n&ldquo; C&ocirc; chủ kh&ocirc;ng cần lo, Căn Nguy&ecirc;n của nh&agrave; họ Ho&agrave;ng l&agrave; Dục Th&uacute; Căn Nguy&ecirc;n, tuy h&igrave;nh th&aacute;i kh&ocirc;ng kh&aacute;c nhau v&agrave; đa phần l&agrave; c&ocirc;ng dụng như nhau th&ocirc;i, cũng đều l&agrave; khống th&uacute; với h&oacute;a th&uacute;. Đ&acirc;y ch&iacute;nh l&agrave; hạn chế của Th&uacute; Nguy&ecirc;n nhưng b&ugrave; lại l&agrave; sức chiến đấu của gia tộc kh&ocirc;ng phải dạng vừa.&rdquo; Trix động vi&ecirc;n.<br />\r\n<br />\r\n&ldquo; Tại sao lại như vậy? C&oacute; g&igrave; đặc biệt &agrave;?&rdquo; Vy t&ograve; m&ograve;, k&igrave; thực c&ocirc; b&eacute; rất lười t&igrave;m hiểu th&ocirc;ng tin.<br />\r\n<br />\r\n&ldquo; Haizz, n&oacute;i c&ocirc; phải chăm chỉ đọc s&aacute;ch c&ugrave;ng t&igrave;m hiểu về gia đ&igrave;nh m&agrave; kh&ocirc;ng nghe. Vậy nghe đ&acirc;y, do Th&uacute; Nguy&ecirc;n c&oacute; t&iacute;nh hạn chế h&igrave;nh th&aacute;i, nhưng ch&iacute;nh v&igrave; thế m&agrave; c&oacute; thể dễ d&agrave;ng nghi&ecirc;n cứu v&agrave; t&igrave;m hướng ph&aacute;t triển tốt nhất. Nh&agrave; họ Ho&agrave;ng đ&atilde; bỏ ra thời gian rất l&acirc;u để t&igrave;m kiếm v&agrave; khế ước với những sủng th&uacute; mạnh mẽ, rồi h&igrave;nh th&agrave;nh n&ecirc;n quan hệ cộng t&aacute;c cho cả thế hệ sau. Cũng giống như c&ocirc; chủ với t&ocirc;i vậy, từ khi sinh ra th&igrave; đ&atilde; quyết định l&agrave; sẽ khế ước với nhau rồi. L&agrave; do tổ ti&ecirc;n ch&uacute;ng ta giao k&egrave;o với nhau.&rdquo; Trix n&oacute;i một tr&agrave;ng giải th&iacute;ch.<br />\r\n<br />\r\n&ldquo; Ồ, vậy m&agrave;y c&oacute; mạnh kh&ocirc;ng?&rdquo; Vy ngơ ng&aacute;c hỏi.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&ldquo; Chậc, kh&ocirc;ng hiểu sao c&oacute; cảm gi&aacute;c bị x&uacute;c phạm nữa. Cứ đợi đến l&uacute;c c&ocirc; mở ra Dục Th&uacute; Căn Nguy&ecirc;n đi, tự nhi&ecirc;n sẽ biết t&ocirc;i mạnh hay kh&ocirc;ng, h&agrave; h&agrave;.&rdquo; Trix kh&ocirc;ng th&egrave;m n&oacute;i nữa, quay sang tập trung theo d&otilde;i trận chiến.<br />\r\n<br />\r\n&ldquo; Vậy l&agrave; c&ograve;n cả một gia tộc si&ecirc;u năng lực nữa &aacute;? M&igrave;nh c&oacute; phải đ&atilde; xuy&ecirc;n kh&ocirc;ng kh&ocirc;ng nhỉ? Lạ qu&aacute; rồi, sao tivi kh&ocirc;ng n&oacute;i về mấy c&aacute;i n&agrave;y?&rdquo; Minh đi&ecirc;n đầu, qu&aacute; nhiều thứ qu&aacute;i lạ đến mức cậu cảm thấy đ&acirc;y kh&ocirc;ng c&ograve;n l&agrave; thế giới m&igrave;nh biết nữa rồi.<br />\r\n<br />\r\nQuay trở lại trận chiến, l&uacute;c n&agrave;y Nanh X&aacute;m ho&agrave;n to&agrave;n rơi v&agrave;o thế yếu, đ&uacute;ng hơn l&agrave; đ&atilde; thất bại rồi nhưng l&agrave; do c&ocirc; Nguyệt kh&ocirc;ng nghi&ecirc;m t&uacute;c n&ecirc;n chưa chết th&ocirc;i.<br />\r\n<br />\r\nTr&ecirc;n người n&oacute; chằng chịt vết cắt s&acirc;u nh&igrave;n thấy cả xương b&ecirc;n trong, m&aacute;u nhuộm khắp người biến n&oacute; từ Nanh X&aacute;m th&agrave;nh &ldquo; Nanh Đỏ&rdquo;, tr&ocirc;ng rất th&ecirc; thảm.<br />\r\n<br />\r\n&ldquo; Sao kh&ocirc;ng giết ta lu&ocirc;n đi? Con người th&acirc;m độc!&rdquo; Nanh X&aacute;m gằn từng chữ.<br />\r\n<br />\r\n&ldquo; Ồ, n&oacute;i nghe c&oacute; vẻ ch&iacute;nh trực nhỉ? Kh&ocirc;ng phải tộc lo&agrave;i bọn ngươi muốn x&acirc;m chiếm ph&aacute; hủy thế giới n&agrave;y sao? Giờ lại b&agrave;y ra th&aacute;i độ hi sinh v&igrave; tổ quốc &agrave;?&rdquo; C&ocirc; Nguyệt chế nhạo n&oacute;i, giọng n&oacute;i lạnh l&ugrave;ng hơn nhiều.<br />\r\n<br />\r\n&ldquo; Khặc..&rdquo; M&aacute;u tươi phun ra từ miệng, Nanh X&aacute;m kia kh&ocirc;ng biết n&oacute;i g&igrave; cho phải.<br />\r\n<br />\r\n&ldquo; Xoẹt...phụt..phụt&hellip;&rdquo; Chớp mắt, từ b&ecirc;n trong cơ thể con Nanh X&aacute;m phun ra v&ocirc; số tia m&aacute;u như một chiếc v&ograve;i hoa sen. Th&acirc;n thể n&oacute; bị thủng lỗ chỗ bởi những sợi tơ đ&acirc;m từ trong ra ngo&agrave;i.<br />\r\n<br />\r\n&lt; Huyết T&acirc;m Li&ecirc;n&gt;, c&ocirc; Nguyệt lẩm bẩm, vừa rồi c&ocirc; đ&atilde; sử dụng đến kĩ năng ri&ecirc;ng của m&igrave;nh.<br />\r\n<br />\r\n&ldquo; Đ&aacute;ng sợ qu&aacute;! Chiến đấu với Dị tộc l&agrave; như n&agrave;y sao?&rdquo; Vy nh&igrave;n v&agrave;o chiến trường đầy m&aacute;u me, trong l&ograve;ng kh&ocirc;ng khỏi sợ h&atilde;i.<br />\r\n<br />\r\n&ldquo; Vừa rồi l&agrave; g&igrave;? Đọc t&ecirc;n chi&ecirc;u thức &aacute;? M&igrave;nh c&oacute; n&ecirc;n như thế kh&ocirc;ng nhỉ? Hay phải đọc thế th&igrave; mới xuất hiện kĩ năng mạnh như vậy? Nghĩ ngại chết th&ocirc;i, ha ha. Hay l&agrave; v&igrave; thế m&agrave; c&ocirc; ấy kh&ocirc;ng d&aacute;m h&eacute;t to l&ecirc;n nhỉ?...&rdquo; Ngược lại Minh th&igrave; kh&ocirc;ng mấy sợ h&atilde;i, cậu cho l&agrave; n&oacute; cũng chỉ giống mấy bộ phim kinh dị từng xem m&agrave; th&ocirc;i. C&aacute;i cậu quan t&acirc;m l&agrave; tr&ograve; đọc t&ecirc;n kĩ năng k&igrave;a.</p>', '2021-11-30 21:10:17', 8, 1),
(259, 69, 'Trốn Thoát', '1', '<p>Diệp Thi&ecirc;n b&ograve; khỏi vực s&acirc;u khổng lồ m&igrave;nh vừa v&ocirc; &yacute; tạo ra, nằm ngửa ngay b&ecirc;n miệng vực thở dốc. Hắn đưa tay l&ecirc;n cao rồi nắm chặt lại, l&agrave;m ra thủ thế chiến thắng rồi vui vẻ n&oacute;i:<br />\r\n<br />\r\n- Rốt cuộc cũng tho&aacute;t ra khỏi nơi đ&oacute;.<br />\r\n<br />\r\nDiệp Thi&ecirc;n kh&ocirc;ng phải t&ugrave; nh&acirc;n hay g&igrave; đ&oacute; tương tự. Ngược lại, hắn l&agrave; th&aacute;i thượng trưởng l&atilde;o trẻ tuổi nhất của Th&aacute;i Hư Thần Cung, thế lực mạnh nhất tại Thần Giới, thống trị v&ocirc; số vị diện trung cấp v&agrave; hạ cấp.<br />\r\n<br />\r\nDiệp Thi&ecirc;n sinh ra đ&atilde; ở vị tr&iacute; m&agrave; những người kh&aacute;c c&oacute; cố gắng cả đời cũng kh&ocirc;ng mơ đến được. Phụ mẫu l&agrave; hai vị th&aacute;i thượng trưởng l&atilde;o của Th&aacute;i Hư Thần Cung, địa vị ch&iacute; t&ocirc;n. Bản th&acirc;n hắn thi&ecirc;n ph&uacute; hơn người, cộng th&ecirc;m v&ocirc; số bảo vật ủng hộ, tu luyện một đường thuận lợi kh&ocirc;ng c&oacute; b&igrave;nh cảnh.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nV&agrave;o năm Diệp Thi&ecirc;n mười s&aacute;u tuổi, hai phu th&ecirc; Diệp Chấn tập trung to&agrave;n bộ cường giả Th&aacute;i Hư Thần Cung tổ chức lễ th&agrave;nh ni&ecirc;n cho hắn, kh&ocirc;ng ngờ kẻ th&ugrave; t&igrave;m đến &aacute;m s&aacute;t. Biết kh&ocirc;ng đ&aacute;nh lại Diệp Chấn v&agrave; Tần Khuynh Mộng, t&ecirc;n kia liền nhắm v&agrave;o Diệp Thi&ecirc;n. Cho d&ugrave; hắn thi&ecirc;n ph&uacute; hơn người th&igrave; vẫn mới mười s&aacute;u tuổi, tu vi c&oacute; hạn. D&ugrave; được ứng cứu kịp thời th&igrave; vẫn bị kh&iacute; trường của người kia chấn thương. May mắn k&iacute;ch hoạt được V&ocirc; Cấu Bảo Thể, tự động tiến v&agrave;o trạng th&aacute;i chết giả để bảo to&agrave;n một tia sinh mệnh lực cuối c&ugrave;ng.<br />\r\n<br />\r\nPhu th&ecirc; Diệp Chấn li&ecirc;n tục d&ugrave;ng Thần Lực trong mười vạn năm để duy tr&igrave; sinh mạng cho hắn, sau c&ugrave;ng hao tận m&agrave; chết. Lại th&ecirc;m mười vạn năm tr&ocirc;i qua, hầu hết mọi người đ&atilde; qu&ecirc;n mất rằng b&ecirc;n trong Th&aacute;i Hư Thần Cung c&oacute; một vị thi&ecirc;n t&agrave;i vẫn đang một mực ngủ say. Ng&agrave;y Diệp Thi&ecirc;n thức tỉnh, trận ph&aacute;p ph&ograve;ng ngự quanh Th&aacute;i Hư Thần Cung bị chấn vỡ, kh&ocirc;ng gian ho&agrave;n to&agrave;n sụp đổ, nhật nguyệt đều kh&ocirc;ng tồn tại trong khoảnh khắc đ&oacute;.<br />\r\n<br />\r\nV&ocirc; Cấu Bảo Thể d&ugrave;ng mười vạn năm để tiếp nhận lực lượng của phu th&ecirc; Diệp Chấn, lại d&ugrave;ng th&ecirc;m mười vạn năm để chuyển h&oacute;a n&oacute; th&agrave;nh sức mạnh cho Diệp Thi&ecirc;n, thậm ch&iacute; c&agrave;ng l&agrave; tiến th&ecirc;m một bước nữa. Sau đ&oacute;&hellip;Diệp Thi&ecirc;n ch&iacute;nh thức trở th&agrave;nh th&aacute;i thượng trưởng l&atilde;o của Th&aacute;i Hư Thần Cung.<br />\r\n<br />\r\nThế nhưng hắn ngo&agrave;i tu vi tối cao ra th&igrave; chẳng c&oacute; g&igrave; giống với một vị th&aacute;i thượng trưởng l&atilde;o. C&ocirc;ng ph&aacute;p tu luyện vẫn l&agrave; loại sơ cấp, thần kỹ chẳng c&oacute; g&igrave; đặc biệt, nhất l&agrave; kinh nghiệm sống thiếu thốn. Khoảng thời gian tiếp theo Diệp Thi&ecirc;n tr&ocirc;i qua rất đau khổ. Hắn bị cung chủ, trưởng l&atilde;o, thậm ch&iacute; l&agrave; mấy vị th&aacute;i thượng trưởng l&atilde;o kh&aacute;c &eacute;p phải l&agrave;m đủ mọi chuyện, từ tu tập ch&iacute; cao bảo điển, thần kỹ trấn ph&aacute;i cho đến luyện đan, luyện kh&iacute;, ph&ugrave; lục, trận ph&aacute;p&hellip;phong ấn tu vi để thực chiến, gia tăng kinh nghiệm chiến đấu, đọc qua đủ loại thư tịch về lịch sử, kỳ văn b&iacute; điển.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nCứ như vậy tiếp diễn một thời gian d&agrave;i, Diệp Thi&ecirc;n cảm thấy m&igrave;nh sắp đi&ecirc;n rồi. Hắn cố gắng nhẫn nhịn cho đến khi học xong thần kỹ th&acirc;n ph&aacute;p mạnh nhất của Th&aacute;i Hư Thần Cung, Th&aacute;i Thanh Du Thi&ecirc;n Bộ. Thần kỹ vừa th&agrave;nh, Diệp Thi&ecirc;n lập tức giải khai phong ấn thực lực, ph&aacute; vỡ kh&ocirc;ng gian rời khỏi thần giới, xuy&ecirc;n qua hư kh&ocirc;ng v&ocirc; tận đến một c&aacute;i vị diện v&ocirc; danh. Sau c&ugrave;ng c&oacute; hơi bất cẩn, kết quả tạo th&agrave;nh c&aacute;i vực s&acirc;u khổng lồ n&agrave;y.<br />\r\n<br />\r\n- Mấy l&atilde;o gi&agrave; kia cho rằng ta chỉ c&oacute; tu vi ngang với phụ th&acirc;n v&agrave; mẫu th&acirc;n n&ecirc;n mới b&agrave;y ra ch&iacute;n mươi ch&iacute;n tầng phong ấn m&agrave; kh&ocirc;ng biết rằng V&ocirc; Cấu Bảo Thể sau khi hấp thu to&agrave;n bộ sức mạnh đ&atilde; đột ph&aacute; ra một cảnh giới ho&agrave;n to&agrave;n mới. Phong ấn kia ta c&oacute; thể ph&aacute; bất kỳ l&uacute;c n&agrave;o, chẳng qua phải nhẫn nhịn đợi đến khi học xong Th&aacute;i Thanh Du Thi&ecirc;n Bộ m&agrave; th&ocirc;i.<br />\r\n<br />\r\nKh&ocirc;ng t&iacute;nh thời gian ngủ say cũng như học tập đi&ecirc;n cuồng tại Th&aacute;i Hư Thần Cung, t&acirc;m t&iacute;nh của Diệp Thi&ecirc;n chỉ như một thanh ni&ecirc;n mười s&aacute;u, mười bảy tuổi. Ở độ tuổi n&agrave;y của hắn lại nắm trong tay sức mạnh v&ocirc; thượng, suy nghĩ đầu ti&ecirc;n kh&ocirc;ng phải tiếp tục tu luyện, truy cầu những cảnh giới cao hơn chưa một ai biết đến m&agrave; l&agrave;m sống một cuộc sống ti&ecirc;u dao, l&agrave;m những việc m&igrave;nh ưa th&iacute;ch. Trốn khỏi Th&aacute;i Hư Thần Cung chỉ l&agrave; bước đầu ti&ecirc;n của cuộc sống n&agrave;y. Với tu vi của Diệp Thi&ecirc;n cộng th&ecirc;m Th&aacute;i Thanh Du Thi&ecirc;n Bộ, tin rằng người của Th&aacute;i Hư Thần Cung sẽ kh&ocirc;ng c&aacute;ch n&agrave;o truy đuổi được hắn. Diệp Thi&ecirc;n cũng c&oacute; thể an t&acirc;m l&agrave;m điều m&igrave;nh th&iacute;ch.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nC&ograve;n đang đắc &yacute;, Diệp Thi&ecirc;n v&ocirc; t&igrave;nh c&oacute; một đ&aacute;m người đang tiến về ph&iacute;a của m&igrave;nh. Dựa v&agrave;o tu vi của hắn đương nhi&ecirc;n c&oacute; thể rời đi một c&aacute;ch an to&agrave;n m&agrave; kh&ocirc;ng ai hay biết, t&ecirc;n n&agrave;y lại chọn một biện ph&aacute;p &iacute;t ai nghĩ đến, lấy đất c&aacute;t b&ocirc;i l&ecirc;n cơ thể rồi giả vờ nằm bất tỉnh.<br />\r\n<br />\r\nP/S: Truyện viết ở một thế giới song song, c&oacute; những điểm giống v&agrave; kh&aacute;c tr&aacute;i đất. N&ecirc;n đừng ai lấy những g&igrave; ở tr&aacute;i đất ra so s&aacute;nh rồi n&oacute;i l&agrave; logic phải thế n&agrave;y nọ. V&igrave; l&agrave; thế giới kh&aacute;c n&ecirc;n m&igrave;nh viết n&oacute; thế n&agrave;o th&igrave; tức l&agrave; thế ấy, nhưng cmt y&ecirc;u cầu phải thế n&agrave;y hay thế kia m&igrave;nh sẽ kh&ocirc;ng trả lời. (d&agrave;nh cho những bạn th&iacute;ch y&ecirc;u cầu logic giống tr&aacute;i đất).</p>', '2021-11-30 21:12:06', 5, 1),
(260, 69, 'Thế Giới Lạ Lẫm', '2', '<p>Vừa rồi thần thức của Diệp Thi&ecirc;n qu&eacute;t qua, ph&aacute;t hiện đ&acirc;y l&agrave; một thế giới ho&agrave;n to&agrave;n xa lạ. N&oacute;i xa lạ kh&ocirc;ng phải v&igrave; hắn chưa từng đến nơi n&agrave;y, m&agrave; l&agrave; cảnh vật, c&aacute;ch ăn mặc cũng như c&aacute;ch thức hoạt động của thế giới n&agrave;y ho&agrave;n to&agrave;n kh&aacute;c xa với những g&igrave; Diệp Thi&ecirc;n từng thấy.<br />\r\n<br />\r\nTh&ocirc;ng qua kinh nghiệm lịch luyện Diệp Thi&ecirc;n c&oacute; được, gặp lại t&igrave;nh huống n&agrave;y, biện ph&aacute;p tốt nhất ch&iacute;nh l&agrave; giả vờ ngu ngốc hoặc mất tr&iacute; nhớ, sau đ&oacute; giả vờ t&igrave;m hiểu. Vừa rồi g&acirc;y ra động tĩnh lớn như vậy, hiện tại c&oacute; người đến điều tra. Hắn chỉ cần nằm tại đ&acirc;y, giả vờ l&agrave; người bị vụ nổ l&agrave;m mất tr&iacute;, như vậy c&oacute; thể thuận lợi x&acirc;m nhập, t&igrave;m hiểu thế giới n&agrave;y.<br />\r\n<br />\r\nĐi đến một đ&aacute;m người, dẫn đầu l&agrave; hai nữ nh&acirc;n ăn mặc kh&aacute; lạ mắt. Một người t&oacute;c d&agrave;i thả ngang lưng, mặc đồ c&ocirc;ng sở m&agrave;u đen, ch&acirc;n mang gi&agrave;y cao g&oacute;t. Người c&ograve;n lại mặc đồng phục cảnh s&aacute;t, t&oacute;c d&agrave;i buộc cao, d&aacute;ng đi hi&ecirc;n ngang. Hai nữ nh&acirc;n đều c&oacute; dung mạo cực kỳ xinh đẹp, đối với kẻ cả người phải tiếp x&uacute;c với mấy l&atilde;o gi&agrave; ở Th&aacute;i Hư Thần Cung như Diệp Thi&ecirc;n quả thực rất hấp dẫn, thần thức kh&ocirc;ng nhịn được lướt qua người hai n&agrave;ng v&agrave;i lần. Sau lưng hai nữ nh&acirc;n l&agrave; một đ&aacute;m người, dựa v&agrave;o quần &aacute;o tr&ecirc;n người c&oacute; thể ph&acirc;n ra th&agrave;nh hai nh&oacute;m.<br />\r\n<br />\r\nTiến lại gần, nữ nh&acirc;n mặc đồng phục cảnh s&aacute;t bất ngờ gia tăng tốc độ vượt l&ecirc;n ph&iacute;a trước, đến ngay b&ecirc;n cạnh Diệp Thi&ecirc;n mới dừng lại, kinh h&atilde;i nh&igrave;n về hố s&acirc;u khổng lồ trước mặt m&igrave;nh:<br />\r\n<br />\r\n- Rốt cuộc l&agrave; thứ g&igrave; c&oacute; sức c&ocirc;ng ph&aacute; lớn đến như vậy? Lẽ n&agrave;o l&agrave; c&aacute;c hắc hội đang thử vũ kh&iacute; n&oacute;ng? C&ograve;n c&oacute;, t&ecirc;n n&agrave;y tại sao vẫn l&agrave;nh lặn kh&ocirc;ng bị ch&uacute;t thương tổn n&agrave;o?<br />\r\n<br />\r\nNữ nh&acirc;n c&ograve;n lại chậm r&atilde;i bước đến, d&ugrave;ng giọng ch&acirc;m chọc đ&aacute;p:<br />\r\n<br />\r\n- Ngươi hỏi nhiều như vậy th&igrave; c&oacute; &iacute;ch g&igrave;? Nhiệm vụ của đội đặc vụ c&aacute;c ngươi kh&ocirc;ng phải l&agrave; điều tra nguy&ecirc;n nh&acirc;n của chuyện n&agrave;y sao?<br />\r\n<br />\r\n- Kh&ocirc;ng cần ngươi dạy.<br />\r\n<br />\r\nKh&ocirc;ng kh&iacute; sặc m&ugrave;i thuốc s&uacute;ng, c&oacute; thể thấy quan hệ giữa hai vị mỹ nữ n&agrave;y kh&aacute; gay gắt. Nữ nh&acirc;n &aacute;o đen ch&acirc;m chọc xong một c&acirc;u liền phất tay cho thuộc hạ ph&iacute;a sau:<br />\r\n<br />\r\n- Mang hắn đi.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n- Ngươi muốn l&agrave;m g&igrave;? Hắn l&agrave; nh&acirc;n chứng duy nhất chứng kiến chuyện n&agrave;y, ta phải mang người về điều tra.<br />\r\n<br />\r\n- Điều tra vốn l&agrave; việc của c&aacute;c ngươi. Nhưng chuyện n&agrave;y xảy ra tr&ecirc;n mảnh đất ta vừa đấu thầu xong, cho n&ecirc;n ta c&oacute; quyền mang người đi.<br />\r\n<br />\r\n- C&ograve;n n&oacute;i l&agrave; tốt nghiệp đại học danh tiếng, ch&uacute;t luật ph&aacute;p cũng kh&ocirc;ng hiểu biết. Chuyện n&agrave;y g&acirc;y hậu quả rất nghi&ecirc;m trọng, tổ chức c&aacute; nh&acirc;n như ngươi chỉ c&oacute; thể hỗ trợ, kh&ocirc;ng được trực tiếp nh&uacute;ng tay v&agrave;o.<br />\r\n<br />\r\n- Ta ưa th&iacute;ch. Ai bảo cha của ta l&agrave; thị trưởng.<br />\r\n<br />\r\n- Cha ta l&agrave; trung tướng&hellip;<br />\r\n<br />\r\n- Gia gia ta l&agrave; nguy&ecirc;n l&atilde;o quốc gia.<br />\r\n<br />\r\n- Đừng qu&ecirc;n gia gia của ch&uacute;ng ta từng l&agrave; chiến hữu.<br />\r\n<br />\r\nMột đ&aacute;m người ph&iacute;a sau đưa tay lau mồ h&ocirc;i tr&ecirc;n tr&aacute;n. Hai vị l&atilde;o tổ t&ocirc;ng n&agrave;y tranh đua nhau th&igrave; cũng th&ocirc;i đi, hiện tại lấy cả l&atilde;o đầu nh&agrave; m&igrave;nh ra h&ugrave; dọa đối phương. Đương nhi&ecirc;n, hai n&agrave;ng đ&atilde; sớm biết th&acirc;n phận của nhau n&ecirc;n kh&ocirc;ng hề sợ h&atilde;i. Chỉ c&oacute; bọn hắn chịu khổ. Lỡ như hai vị n&agrave;y nổi giận muốn động thủ, d&ugrave; kết quả c&oacute; thế n&agrave;o người l&atilde;nh đủ đều l&agrave; bọn hắn.<br />\r\n<br />\r\nDiệp Thi&ecirc;n nằm dưới đất giả vờ bất tỉnh, trong đầu th&igrave; đang suy tư về những danh từ xa lạ hai n&agrave;ng vừa n&oacute;i. &ldquo;Vũ kh&iacute; n&oacute;ng&rdquo;, &ldquo;đại học&rdquo;, &ldquo;thị trưởng&rdquo;, &ldquo;trung tướng&rdquo; đều l&agrave; lần đầu ti&ecirc;n hắn nghe được. Hiện tại Diệp Thi&ecirc;n c&oacute; thể chắc chắn bản th&acirc;n đ&atilde; đến một nơi c&aacute;ch Thần Giới cực xa. Lần đầu ti&ecirc;n hắn dốc hết sức thi triển Th&aacute;i Thanh Du Thi&ecirc;n Bộ, quả thực kh&ocirc;ng khống chế tốt. Diệp Thi&ecirc;n đ&atilde; sớm bước v&agrave;o một cảnh giới từ trước đến nay chưa c&oacute; ai từng đạt đến, ch&iacute;nh hắn cũng kh&ocirc;ng r&otilde; m&igrave;nh mạnh đến mức n&agrave;o. Từ đầu đến cuối vẫn bị đ&aacute;m trưởng l&atilde;o kia quản th&uacute;c, đ&acirc;y cũng l&agrave; lần đầu ti&ecirc;n Diệp Thi&ecirc;n dốc to&agrave;n lực.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nMặc kệ, giả vờ liền giả vờ đến c&ugrave;ng. Diệp Thi&ecirc;n kh&ocirc;ng quan t&acirc;m ai sẽ đưa m&igrave;nh đi, d&ugrave; sao cứ giả vờ mất tr&iacute; nhớ, sau đ&oacute; chậm r&atilde;i t&igrave;m hiểu, l&agrave;m quen với thế giới n&agrave;y l&agrave; được.<br />\r\n<br />\r\nHai người c&atilde;i nhau một hồi bất ph&acirc;n thắng bại. Gia thế kh&ocirc;ng ch&ecirc;nh lệch, c&atilde;i nhiều năm như vậy đ&atilde; sớm hiểu r&otilde; nhau, rất kh&oacute; chiếm được ưu thế. Một t&ecirc;n thuộc hạ cảm thấy kh&ocirc;ng tốt cho lắm liền l&ecirc;n tiếng:<br />\r\n<br />\r\n- Tuy&ecirc;n Mộng tiểu thư, Mộ Vũ tiểu thư, c&oacute; thể nghe ta n&oacute;i v&agrave;i c&acirc;u hay kh&ocirc;ng.<br />\r\n<br />\r\nTần Khinh Tuyết l&agrave; t&ecirc;n của nữ nh&acirc;n mặc y phục c&ocirc;ng sở, c&ograve;n L&yacute; Mộ Vũ đương nhi&ecirc;n l&agrave; nữ cảnh s&aacute;t. Hai n&agrave;ng đồng thời nh&igrave;n sang người vừa l&ecirc;n tiếng kia, nh&acirc;n cơ hội n&agrave;y t&igrave;m bậc thang đi xuống:<br />\r\n<br />\r\n- N&oacute;i đi.<br />\r\n<br />\r\nNgươi kia vội đưa ra &yacute; kiến:<br />\r\n<br />\r\n- Ta cảm thấy người n&agrave;y rất kh&ocirc;ng b&igrave;nh thường. Vụ nổ lớn như vậy hắn ở ngay b&ecirc;n cạnh lại kh&ocirc;ng chịu ch&uacute;t thương t&iacute;ch n&agrave;o.<br />\r\n<br />\r\n- Lời thừa, lẽ n&agrave;o bọn ta kh&ocirc;ng nhận ra.<br />\r\n<br />\r\n- Kh&ocirc;ng, &yacute; của ta l&agrave; hắn rất c&oacute; thể&hellip;c&aacute;c ngươi cũng hiểu. Hơn nữa đẳng cấp cực kỳ cao. Loại người n&agrave;y ch&uacute;ng ta kh&ocirc;ng thể tự tiện mang đi. Nếu hai vị tiểu thư đồng &yacute;, ta c&oacute; thể đưa hắn về đặc khu chữa trị rồi điều tra một phen, sau đ&oacute; muốn xử l&yacute; thế n&agrave;o th&igrave; t&ugrave;y hai người.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nĐ&acirc;y quả thực l&agrave; lựa chọn tốt nhất. Hai n&agrave;ng tuy c&oacute; gia thế hiển h&aacute;ch nhưng chuyện li&ecirc;n quan đến những kẻ &ldquo;đặc biệt&rdquo; n&agrave;y thật kh&ocirc;ng tiện xen v&agrave;o. &Iacute;t nhất phải đợi đến khi ph&iacute;a đặc khu đưa ra kết luận mới mang đi được.<br />\r\n<br />\r\nTần Khinh Tuyết gật đầu:<br />\r\n<br />\r\n- Được, l&agrave;m theo lời của ngươi đi. Sau khi chữa trị v&agrave; điều tra xong ta sẽ lập tức mang hắn đi.<br />\r\n<br />\r\nL&yacute; Mộ Vũ hừ khẽ, cũng lười đ&aacute;p lại. Đến l&uacute;c đ&oacute; ai mang đi c&ograve;n chưa biết được. N&oacute;i cho c&ugrave;ng, th&acirc;n l&agrave; cảnh s&aacute;t đặc vụ, về t&igrave;nh về l&yacute; n&agrave;ng đều c&oacute; ưu thế hơn Tần Khinh Tuyết. Chẳng qua nh&igrave;n gia thế hai b&ecirc;n cũng rất kh&oacute; n&oacute;i. Tuy Tần Khinh Tuyết l&agrave; danh nh&acirc;n nhưng đại ca của n&agrave;ng c&oacute; địa vị rất cao trong qu&acirc;n đội, t&ecirc;n kia lại nổi tiếng sủng &aacute;i muội muội đến trời.<br />\r\n<br />\r\nĐạt được sự đồng &yacute;, người kia lập tức ra hiệu cho những người ph&iacute;a sau tiến l&ecirc;n đem Diệp Thi&ecirc;n đưa v&agrave;o trong xe chở thẳng đến đặc khu. Tần Khinh Tuyết c&ugrave;ng L&yacute; Mộ Vũ cũng theo sau, đương nhi&ecirc;n l&agrave; mỗi người một xe kh&aacute;c nhau.<br />\r\n<br />\r\nQuan hệ giữa hai nh&agrave; Tần L&yacute; từ xưa đến nay vẫn lu&ocirc;n rất tốt. Khi c&ograve;n b&eacute; Tần Khinh Tuyết v&agrave; L&yacute; Mộ Vũ cũng l&agrave; bằng hữu th&acirc;n thiết. Nhưng kh&ocirc;ng hiểu v&igrave; sao đến năm cấp ba hai n&agrave;ng lại ph&aacute;t sinh xung đột, trực tiếp kh&ocirc;ng nh&igrave;n mặt nhau. M&atilde;i cho đến hiện tại ch&iacute;nh l&agrave; một đ&ocirc;i oan gia, vừa gặp liền c&atilde;i, kh&ocirc;ng ai nhường ai.<br />\r\n<br />\r\nĐể diễn tốt vai của m&igrave;nh, Diệp Thi&ecirc;n vẫn một mực nằm y&ecirc;n. Hắn th&ocirc;ng qua thần thức diện nhỏ thấy được rất nhiều cảnh tượng tr&ecirc;n đường đi, cảm thấy người của thế giới n&agrave;y c&oacute; ch&uacute;t yếu. Kh&ocirc;ng đ&uacute;ng, l&agrave; rất yếu, cực kỳ yếu, ch&acirc;n ch&iacute;nh l&agrave; ph&agrave;m nh&acirc;n chưa từng tu luyện. Đ&aacute;nh gi&aacute; kỹ lại Tần Khinh Tuyết v&agrave; L&yacute; Mộ Vũ, hai n&agrave;ng c&oacute; lẽ so với người b&igrave;nh thường mạnh hơn một ch&uacute;t. Tất nhi&ecirc;n một ch&uacute;t n&agrave;y đối với Diệp Thi&ecirc;n chẳng c&oacute; &yacute; nghĩa g&igrave;.<br />\r\n<br />\r\nBất qu&aacute; hắn cũng kh&ocirc;ng khinh thường, đ&atilde; sớm si&ecirc;u việt cả phụ mẫu, gi&aacute; trị vũ lực của Diệp Thi&ecirc;n si&ecirc;u việt bất kỳ ai hắn từng gặp. V&igrave; vậy mạnh yếu đều như nhau, đối xử c&ocirc;ng bằng. &ldquo;Tất cả sinh mạng đều đ&aacute;ng được tr&acirc;n qu&yacute;. Đại năng kh&ocirc;ng phải kẻ dẫm đạp l&ecirc;n sinh mạnh người kh&aacute;c m&agrave; l&agrave; người g&igrave;n giữ sự c&ocirc;ng bằng&rdquo;, đ&acirc;y l&agrave; điều cuối c&ugrave;ng phụ th&acirc;n dạy hắn. Tất nhi&ecirc;n đ&acirc;y l&agrave; quan niệm c&aacute; nh&acirc;n, kh&ocirc;ng đại biểu ch&iacute;nh nghĩa đ&uacute;ng sai. Nhưng Diệp Chấn l&agrave; người Diệp Thi&ecirc;n t&ocirc;n trọng nhất, hắn vẫn lu&ocirc;n tự nhủ bản th&acirc;n tu&acirc;n theo mọi lời phụ th&acirc;n đ&atilde; dạy.</p>', '2021-11-30 21:12:28', 6, 1),
(261, 69, 'Lựa Chọn', '3', '<p>Trong khi Diệp Thi&ecirc;n đang suy nghĩ l&agrave;m c&aacute;ch n&agrave;o để dung nhập ho&agrave;n to&agrave;n với thế giới n&agrave;y, trở th&agrave;nh một phần tử b&igrave;nh thường nhất, kh&ocirc;ng biết từ l&uacute;c n&agrave;o hắn đ&atilde; bị đưa v&agrave;o trong đặc khu.<br />\r\n<br />\r\nNơi n&agrave;y được x&acirc;y dựng s&acirc;u trong một c&aacute;nh rừng, dưới ch&acirc;n một ngọn n&uacute;i nhỏ. Nh&igrave;n từ b&ecirc;n ngo&agrave;i rất b&igrave;nh thường nhưng ph&iacute;a trong đều l&agrave; những sản phẩm khoa học kỹ thuật ti&ecirc;n tiến bậc nhất. Diệp Thi&ecirc;n d&ugrave;ng thần thức qu&eacute;t qua một hồi, hứng th&uacute; bừng bừng trỗi dậy. Hắn c&oacute; x&uacute;c động muốn đem tất cả những thứ kỳ lạ n&agrave;y ra nghi&ecirc;n cứu, nhất định sẽ rất th&uacute; vị. Bất qu&aacute; sau c&ugrave;ng Diệp Thi&ecirc;n cũng nhịn được xuống. Kh&ocirc;ng vội, cứ ho&agrave;n th&agrave;nh m&agrave;n biểu diễn n&agrave;y, về sau sẽ c&oacute; nhiều cơ hội.<br />\r\n<br />\r\nTiến v&agrave;o đặc khu, sau khi ho&agrave;n th&agrave;nh v&agrave;i thủ tục đăng k&yacute;, Diệp Thi&ecirc;n nhanh ch&oacute;ng được đưa đi x&eacute;t nghiệm. Tiếp theo ch&iacute;nh l&agrave; một m&agrave;n l&agrave;m cho người kh&aacute;c c&acirc;m n&iacute;n. Kh&ocirc;ng c&oacute; bất kỳ thứ g&igrave;, bao gồm cả dao laze hay tia gamma c&oacute; thể cắt xuy&ecirc;n qua cơ thể hắn. Muốn th&ocirc;ng qua miệng đưa đường ống v&agrave;o kiểm tra cơ thể cũng kh&ocirc;ng được. Những thứ vừa đặt v&agrave;o trong lập tức vỡ tan, thậm ch&iacute; thiết bị kết nối cũng nổ tung.<br />\r\n<br />\r\nDằn vặt suốt ba ng&agrave;y, to&agrave;n thể nh&acirc;n vi&ecirc;n v&agrave; nh&agrave; khoa học tại đặc khu gần như ph&aacute;t đi&ecirc;n. Đ&acirc;y l&agrave; lần đầu ti&ecirc;n bọn hắn ph&aacute;t hiện một t&ecirc;n nh&acirc;n loại c&oacute; thể biến th&aacute;i đến mức n&agrave;y. Đ&acirc;y c&ograve;n c&oacute; thể gọi l&agrave; người sao? Kh&ocirc;ng đ&uacute;ng, r&otilde; r&agrave;ng l&agrave; qu&aacute;i vật h&igrave;nh người, nếu kh&ocirc;ng sao c&oacute; thể sống s&oacute;t sau vụ nổ kia. Nhưng th&acirc;n phận hắn l&agrave; g&igrave;, từ đ&acirc;u đến lại kh&ocirc;ng ai biết được.<br />\r\n<br />\r\nMột mực giả vờ ngủ say, Diệp Thi&ecirc;n cũng cảm thấy c&oacute; ch&uacute;t tội nghiệp những kẻ cả ng&agrave;y k&ecirc;u g&agrave;o trong tuyệt vọng n&agrave;y. Cảm thấy bấy nhi&ecirc;u đ&atilde; đủ, đến ng&agrave;y thứ tư, khi đ&aacute;m người kia gần như đ&atilde; đem to&agrave;n bộ thiết bị c&oacute; thể nhất đưa đến để liều lần cuối, Diệp Thi&ecirc;n đột nhi&ecirc;n mở mắt rồi chậm r&atilde;i ngồi dậy.<br />\r\n<br />\r\nTrong &aacute;nh mắt kinh h&atilde;i của một đ&aacute;m người mặc đồ bảo hộ, Diệp Thi&ecirc;n mờ mịt hỏi:<br />\r\n<br />\r\n- Đ&acirc;y l&agrave; nơi n&agrave;o? C&aacute;c ngươi l&agrave; ai?<br />\r\n<br />\r\nHai c&acirc;u hỏi chuy&ecirc;n dụng d&ugrave;ng trong mọi trường hợp, cộng th&ecirc;m vẻ mặt ngu ngơ của Diệp Thi&ecirc;n tạo th&agrave;nh hiệu quả tương đối tốt. Một l&atilde;o nh&acirc;n ước chừng bảy mươi tuổi, d&ugrave;ng tay n&acirc;ng mắt k&iacute;nh thận trọng hỏi:<br />\r\n<br />\r\n- Ngươi c&ograve;n nhớ m&igrave;nh l&agrave; ai sao?</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nDiệp Thi&ecirc;n vẫn giữ vẻ mờ mịt, suy nghĩ một l&aacute;t rồi lắc đầu:<br />\r\n<br />\r\n- Kh&ocirc;ng nhớ ra được, đầu của ta c&oacute; ch&uacute;t đau.<br />\r\n<br />\r\nL&atilde;o nh&acirc;n gật đầu, dặn d&ograve; người b&ecirc;n cạnh d&ograve; hỏi th&ecirc;m rồi vội bước ra ngo&agrave;i. Nửa giờ sau, Diệp Thi&ecirc;n trải qua một loạt c&acirc;u hỏi, đa phần đ&aacute;p &aacute;n của hắn đều l&agrave; kh&ocirc;ng biết, kh&ocirc;ng r&otilde;. Đ&aacute;m người kia kh&ocirc;ng hỏi được g&igrave; liền dứt kho&aacute;t ghi ch&uacute; ở cuối bi&ecirc;n bản: Mất tr&iacute; nhớ to&agrave;n phần.<br />\r\n<br />\r\nKhoảng ba giờ sau, Tần Khinh Tuyết v&agrave; L&yacute; Mộ Vũ dẫn theo một đ&aacute;m người tiến v&agrave;o. Hai n&agrave;ng đều l&agrave; kẻ c&oacute; th&acirc;n phận, chức vị đặc th&ugrave;. Cộng th&ecirc;m việc cả hai l&agrave; người đầu ti&ecirc;n ph&aacute;t hiện ra Diệp Thi&ecirc;n n&ecirc;n được ưu ti&ecirc;n th&ocirc;ng b&aacute;o trước. Sau một qu&aacute; tr&igrave;nh đấu tranh l&acirc;u d&agrave;i, vận dụng đến cả th&acirc;n phận v&agrave; quan hệ, hai n&agrave;ng rốt cuộc c&ugrave;ng được chấp thuận mang Diệp Thi&ecirc;n đi. D&ugrave; sao mọi thiết bị đều kh&ocirc;ng d&ugrave;ng được, chỉ c&oacute; thể chậm r&atilde;i tiếp x&uacute;c, chờ đối phương kh&ocirc;i phục tr&iacute; nhớ mới điều tra tiếp được. Để hắn rời khỏi đ&acirc;y l&agrave; sự lựa chọn kh&ocirc;ng tệ.<br />\r\n<br />\r\nPhần tiếp theo tương đối đau đầu. Cả Tần Khinh Tuyết lẫn L&yacute; Mộ Vũ đều muốn mang Diệp Thi&ecirc;n về chỗ của m&igrave;nh. L&yacute; Mộ Vũ l&agrave; đặc c&ocirc;ng, địa vị trong qu&acirc;n đội tương đối đặc th&ugrave; lại c&oacute; chỗ chống lưng. M&agrave; Tần Khinh Tuyết tuy l&agrave; l&agrave;m kinh tế nhưng sau lưng n&agrave;ng lại c&oacute; một vị ca ca cực kỳ lợi hại c&ugrave;ng thế lực của gia gia. Quan trọng nhất, Tần Khinh Tuyết l&agrave; cố vấn cao cấp, c&oacute; một v&agrave;i đặc quyền.<br />\r\n<br />\r\nHai b&ecirc;n kh&ocirc;ng ai nhường ai khiến những người kh&aacute;c c&oacute; ch&uacute;t đau đầu. Hai vị l&atilde;o tổ t&ocirc;ng n&agrave;y kh&ocirc;ng dễ chiều. Thảo luận một hồi, tất cả đều nhất tr&iacute; để Diệp Thi&ecirc;n tự lựa chọn. D&ugrave; sao kẻ n&agrave;y qu&aacute; thần b&iacute;, th&acirc;n thể mạnh mẽ như vậy, gi&aacute; trị vũ lực tuyệt đối si&ecirc;u ph&agrave;m. Lỡ như &eacute;p buộc khiến hắn kh&oacute; chịu liền kh&ocirc;ng xong. Thậm ch&iacute; v&agrave;i nh&agrave; khoa học l&agrave;m x&eacute;t nghiệm cho Diệp Thi&ecirc;n mấy ng&agrave;y qua cũng kh&ocirc;ng biết c&oacute; thứ g&igrave; đủ sức đả thương hắn hay kh&ocirc;ng, d&ugrave; sao t&ecirc;n n&agrave;y cũng qu&aacute; mức biến th&aacute;i rồi.<br />\r\n<br />\r\nHai b&ecirc;n ra sức dụ dỗ, cuối c&ugrave;ng Diệp Thi&ecirc;n liền lựa chọn đi theo Tần Khinh Tuyết trong &aacute;nh mắt như muốn ăn tươi nuốt sống hắn của L&yacute; Mộ Vũ. L&yacute; do rất đơn giản, chỉ v&igrave; một c&acirc;u n&oacute;i của Tần Khinh Tuyết: để hắn tự do. Tại sao Diệp Thi&ecirc;n phải bỏ lại cả Th&aacute;i Hư Thần Cung để chạy trốn đến nơi xa lạ? Chẳng phải v&igrave; tự do sao? Đối với hắn kh&ocirc;ng c&oacute; g&igrave; quan trọng hơn thứ n&agrave;y. Mấy ng&agrave;y qua, tuy vẫn c&oacute; nhiều thứ xa lạ nhưng Diệp Thi&ecirc;n cũng hiểu được v&agrave;i thứ cơ bản. Hắn biết đi c&ugrave;ng L&yacute; Mộ Vũ sẽ tương đối phiền phức. Tần Khinh Tuyết cũng kh&ocirc;ng c&oacute; tự do tuyệt đối, bất qu&aacute; vẫn tốt hơn nhiều.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nC&ugrave;ng ngồi tr&ecirc;n một chiếc xe, Tần Khinh Tuyết đưa Diệp Thi&ecirc;n rời khỏi đặc khu trở về chỗ của n&agrave;ng. Tr&ecirc;n đường đi, n&agrave;ng kh&ocirc;ng qu&ecirc;n d&ograve; hỏi:<br />\r\n<br />\r\n- Ngươi n&oacute;i m&igrave;nh mất tr&iacute; nhớ, kh&ocirc;ng lưu lại bất kỳ k&yacute; ức n&agrave;o?<br />\r\n<br />\r\nDiệp Thi&ecirc;n tiếp tục diễn:<br />\r\n<br />\r\n- Ta chỉ biết m&igrave;nh gọi l&agrave; Diệp Thi&ecirc;n.<br />\r\n<br />\r\n- Được, Diệp Thi&ecirc;n. Th&acirc;n thể ngươi mạnh mẽ như vậy l&agrave; do bẩm sinh hay r&egrave;n luyện. C&oacute; thể ngươi kh&ocirc;ng nhớ nhưng nếu l&agrave; r&egrave;n luyện, trong tiềm thức sẽ nảy sinh một loại bản năng. V&iacute; dụ như thế n&agrave;y&hellip;<br />\r\n<br />\r\nN&agrave;ng chưa n&oacute;i hết c&acirc;u đ&atilde; bất ngờ xuất thủ, một con dao găm đặc chế kh&ocirc;ng biết từ đ&acirc;u nằm trong tay Tần Khinh Tuyết ch&eacute;m thẳng về ph&iacute;a cổ Diệp Thi&ecirc;n. Tốc độ v&agrave; sức mạnh đ&uacute;ng l&agrave; đ&atilde; vượt xa một ph&agrave;m nh&acirc;n b&igrave;nh thường. Bất qu&aacute; Diệp Thi&ecirc;n tr&aacute;nh cũng lười tr&aacute;nh, t&ugrave;y tiện đưa tay chạm nhẹ một c&aacute;i. Con dao kia lập tức bị cắt th&agrave;nh hai nửa, vuột khỏi tay Tần Khinh Tuyết.<br />\r\n<br />\r\nN&agrave;ng chỉ tỏ ra kinh ngạc đ&ocirc;i ch&uacute;t rồi gật đầu:<br />\r\n<br />\r\n- Xem ra l&agrave; bản năng. Vừa rồi ta kh&ocirc;ng cảm nhận được Kh&iacute;, đơn thuần l&agrave; lực lượng th&acirc;n thể, hẳn l&agrave; Luyện Thể V&otilde; Giả.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n- V&otilde; Giả?<br />\r\n<br />\r\nTần Khinh Tuyết chậm r&atilde;i giải đ&aacute;p:<br />\r\n<br />\r\n- Tr&ecirc;n thế giới n&agrave;y c&oacute; một số kẻ mạnh mẽ vượt xa người b&igrave;nh thường. Trong đ&oacute; chia th&agrave;nh v&agrave;i nh&oacute;m. Những kẻ sinh ra với năng lực đặc th&ugrave;, th&ocirc;ng qua r&egrave;n luyện cường h&oacute;a những năng lực đ&oacute; gọi l&agrave; Dị Năng Giả. Nhưng kẻ trời sinh thể chất th&iacute;ch hợp tu luyện đạo ph&aacute;p, thần th&ocirc;ng trừ y&ecirc;u diệt quỷ gọi chung l&agrave; Tu Đạo Giả. Hai nh&oacute;m tr&ecirc;n sinh ra đ&atilde; c&oacute; tư chất vượt trội, l&agrave;m &iacute;t hưởng nhiều, v&igrave; vậy thường rất tự đại, kh&oacute; kiềm chế. Nh&oacute;m thứ ba ch&iacute;nh l&agrave; V&otilde; Giả ch&uacute;ng ta, tư chất b&igrave;nh thường, dựa v&agrave;o nỗ lực v&agrave; cố gắng kh&ocirc;ng ngừng để mạnh l&ecirc;n, đến một mức n&agrave;o đ&oacute; sẽ đủ sức đối đầu với hai nh&oacute;m người kia. V&otilde; Giả lại chia l&agrave;m hai loại, Luyện Kh&iacute; v&agrave; Luyện Thể, ngươi ch&iacute;nh l&agrave; loại thứ hai. Tất nhi&ecirc;n cũng c&oacute; trường hợp Kh&iacute; Thể đồng tu. Nhưng loại người n&agrave;y rất hiếm, kh&ocirc;ng chỉ cần nghị lực hơn người m&agrave; lại kh&oacute; tiến xa do ph&acirc;n t&acirc;m.<br />\r\n<br />\r\n- X&atilde; hội hiện đại c&oacute; ph&aacute;p trị, bất kể l&agrave; Dị Năng Giả, Tu Đạo Giả hay V&otilde; Giả đều phải chịu thua trước vũ kh&iacute; n&oacute;ng. Hiện tại đ&atilde; phần chỉ hoạt động trong b&oacute;ng tối, nhưng sự kiện li&ecirc;n quan đều bị ch&iacute;nh phủ v&agrave; cơ quan đặc th&ugrave; t&igrave;m c&aacute;ch che giấu. Cho n&ecirc;n ngươi về sau cẩn thận một ch&uacute;t, trừ những trường hợp đặc biệt th&igrave; đừng thể hiện sức mạnh của b&igrave;nh. Cũng c&ograve;n tốt ngươi l&agrave; Luyện Thể Giả, ngoại trừ sức mạnh si&ecirc;u cường th&igrave; kh&ocirc;ng c&oacute;&hellip;<br />\r\n<br />\r\nN&agrave;ng c&ograve;n chưa n&oacute;i hết c&acirc;u đ&atilde; trừng mắt nh&igrave;n hai mảnh dao vỡ bay lượn quanh người Diệp Thi&ecirc;n:<br />\r\n<br />\r\n- Ta c&oacute; thể cũng l&agrave; một Luyện Kh&iacute; Giả a.</p>', '2021-11-30 21:12:49', 6, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(262, 69, 'Chuyển Đến', '4', '<p>Kinh ngạc mất một hồi l&acirc;u, Tần Khinh Tuyết cuối c&ugrave;ng cũng b&igrave;nh t&acirc;m trở lại. Bất qu&aacute; giọng n&oacute;i của n&agrave;ng vẫn c&ograve;n run rẩy:<br />\r\n<br />\r\n- Kh&iacute; thể đồng tu, th&acirc;n thể cực hạn, nội kh&iacute; ly thể, ngươi rốt cuộc l&agrave; loại qu&aacute;i vật g&igrave;?<br />\r\n<br />\r\nDiệp Thi&ecirc;n g&atilde;i đầu, nữ nh&acirc;n n&agrave;y dường như kh&ocirc;ng được lễ ph&eacute;p cho lắm. Tại thế giới của hắn, hai từ qu&aacute;i vật n&agrave;y c&oacute; &yacute; nghĩa đặc biệt nghi&ecirc;m trọng. Chẳng qua ở tại đ&acirc;y mấy ng&agrave;y, Dương Thi&ecirc;n cũng biết thế giới n&agrave;y văn h&oacute;a kh&aacute;c biệt, c&oacute; lẽ qu&aacute;i vật ở đ&acirc;y l&agrave; một c&aacute;ch t&aacute;n thưởng kh&aacute;c chăng?<br />\r\n<br />\r\n- Ta cũng kh&ocirc;ng r&otilde;, chỉ l&agrave; thấy việc n&agrave;y rất đơn giản. Cũng kh&ocirc;ng đ&aacute;ng kinh ngạc như biểu cảm của ngươi a.<br />\r\n<br />\r\nTần Khinh Tuyết im lặng, nội t&acirc;m thầm k&ecirc;u may mắn. May mắn khoang xe của n&agrave;ng rộng lại c&aacute;ch &acirc;m, kh&ocirc;ng c&oacute; ghi h&igrave;nh. Những người kh&aacute;c kh&ocirc;ng ai biết những việc Diệp Thi&ecirc;n vừa l&agrave;m. May mắn v&igrave; Diệp Thi&ecirc;n chọn đi theo n&agrave;ng, nếu l&agrave; L&yacute; Mộ Vũ, kết quả rất kh&oacute; tưởng tượng. So với Tần Khinh Tuyết, tư tưởng của L&yacute; Mộ Vũ bị ảnh hưởng nhiều từ gia đ&igrave;nh. C&aacute;i g&igrave; m&agrave; qu&acirc;n nh&acirc;n, g&oacute;p sức cho quốc gia. Diệp Thi&ecirc;n c&oacute; thể chất cực mạnh, điều n&agrave;y kh&ocirc;ng sai. Nhưng nếu hắn ki&ecirc;m tu của Luyện Kh&iacute; đến nội kh&iacute; ly thể th&igrave; l&agrave; một phạm tr&ugrave; ho&agrave;n to&agrave;n kh&aacute;c. Hai thứ n&agrave;y kết hợp đ&acirc;u chỉ l&agrave; một cộng một bằng hai, Diệp Thi&ecirc;n lại đem cả hai luyện đến đỉnh cao. Tr&ecirc;n thế giới n&agrave;y liệu c&ograve;n ai l&agrave; đối thủ của hắn? Dị Năng Giả cấp SSS hay Tu Đạo Giả T&ocirc;ng Sư Chi Cảnh? Về phần V&otilde; Giả Tần Khinh Tuyết kh&ocirc;ng nghĩ đến, bởi v&igrave; n&agrave;ng c&oacute; thể đảm bảo tuyệt đối kh&ocirc;ng c&oacute; một ai đồng tu Kh&iacute; Thể đạt đến cấp độ n&agrave;y.<br />\r\n<br />\r\nTuy gia đ&igrave;nh cũng c&oacute; người l&agrave;m trong qu&acirc;n đội, gia gia l&agrave; nguy&ecirc;n l&atilde;o quốc gia, bản th&acirc;n l&agrave; cố vấn cao cấp, Tần Khinh Tuyết cũng kh&ocirc;ng c&oacute; cố chấp như L&yacute; Mộ Vũ. Diệp Thi&ecirc;n rất mạnh, nhưng đ&acirc;y l&agrave; sức mạnh thuộc về hắn, hắn c&oacute; quyền l&agrave;m điều m&igrave;nh muốn. Đương nhi&ecirc;n, một kẻ đ&aacute;ng sợ thế n&agrave;y vẫn phải khống chế trong phạm vi nhất định, kh&ocirc;ng để hắn l&agrave;m ra loại chuyện thương thi&ecirc;n hại l&yacute;. Hiện tại Diệp Thi&ecirc;n đ&atilde; mất tr&iacute; nhớ, một khi hắn nhớ ra&hellip;lỡ như đối phương c&oacute; th&acirc;n phận đặc biệt hoặc từng l&agrave; tội phạm&hellip;hậu quả thật kh&oacute; tưởng tượng. Suy nghĩ một l&aacute;t, Tần Khinh Tuyết nghĩ ra một biện ph&aacute;p, để Diệp Thi&ecirc;n che giấu năng lực, sống như một người b&igrave;nh thường, hạn chế tiếp x&uacute;c với tầng thứ kia l&agrave; tốt nhất. Theo thời gian qua đi, khi hắn đ&atilde; quen với con người mới n&agrave;y, cho d&ugrave; nhớ lại chuyện cũ th&igrave; vẫn sẽ bị t&iacute;nh c&aacute;ch hiện tại ảnh hưởng.<br />\r\n<br />\r\nGiống như hai loại nước đen trắng trộn lẫn lại, số lượng b&ecirc;n n&agrave;o nhiều hơn th&igrave; ảnh hưởng sẽ lớn hơn. Đ&acirc;y l&agrave; biện ph&aacute;p tốt nhất m&agrave; Tần Khinh Tuyết c&oacute; thể nghĩ ra trong thời gian ngắn. D&ugrave; sao đối với loại qu&aacute;i vật n&agrave;y, biện ph&aacute;p đối ph&oacute; thực sự kh&ocirc;ng nhiều, lại kh&ocirc;ng thể qu&aacute; cứng rắn m&agrave; đắc tội đối phương.<br />\r\n<br />\r\nTần Khinh Tuyết thử d&ograve; hỏi:<br />\r\n<br />\r\n- Tiếp theo đ&acirc;y ngươi dự định thế n&agrave;o? Tuy ta c&oacute; hứa sẽ để ngươi tự do, đồng thời d&igrave;m sự kiện kia xuống nhưng năng lực của ngươi thật qu&aacute; đặc th&ugrave;, đo&aacute;n chừng ph&iacute;a tr&ecirc;n sẽ ph&aacute;i người theo d&otilde;i.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nDiệp Thi&ecirc;n giả vờ suy nghĩ một l&aacute;t rồi lắc đầu:<br />\r\n<br />\r\n- Kh&ocirc;ng c&oacute; k&yacute; ức, ta cảm thấy thế giới n&agrave;y rất lạ lẫm. C&oacute; lẽ trước ti&ecirc;n phải l&agrave;m quen, t&igrave;m hiểu một phen rồi hẳn quyết định.<br />\r\n<br />\r\n- Rất tốt. Vậy ngươi đến nh&agrave; ta ở một thời gian. Khi n&agrave;o nghĩ ra hẳn quyết định cũng chưa muộn.<br />\r\n<br />\r\nDiệp Thi&ecirc;n giật m&igrave;nh buộc miệng n&oacute;i ra:<br />\r\n<br />\r\n- Ở c&ugrave;ng ngươi? Nam nữ thụ thụ bất th&acirc;n, nữ nh&acirc;n của thế giới n&agrave;y đều t&ugrave;y tiện như vậy sao?<br />\r\n<br />\r\nTần Khinh Tuyết trừng mắt:<br />\r\n<br />\r\n- Ngươi n&oacute;i ai t&ugrave;y tiện? Khoan, ngươi vừa n&oacute;i nữ nh&acirc;n của thế giới n&agrave;y, lẽ n&agrave;o ngươi đến từ thế giới kh&aacute;c?<br />\r\n<br />\r\nBiết m&igrave;nh lỡ miệng, đầu &oacute;c Diệp Thi&ecirc;n lập tức xoay chuyển:<br />\r\n<br />\r\n- Hiện tại ta kh&ocirc;ng c&oacute; k&yacute; ức, đối với mọi thứ đều xa lạ, giống như một thế giới ho&agrave;n to&agrave;n mới a.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nTần Khinh Tuyết nghe xong liền gật g&ugrave;, giải th&iacute;ch n&agrave;y t&ugrave;y c&oacute; hơi qua loa nhưng cũng rất hợp l&yacute;. Tất nhi&ecirc;n nguy&ecirc;n nh&acirc;n ch&iacute;nh l&agrave; do n&agrave;ng c&oacute; nằm mơ cũng kh&ocirc;ng nghĩ đến Diệp Thi&ecirc;n vậy m&agrave; thực sự đến từ một thế giới kh&aacute;c.<br />\r\n<br />\r\n- Vậy &yacute; của ngươi thế n&agrave;o, c&oacute; muốn ở c&ugrave;ng ta hay kh&ocirc;ng?<br />\r\n<br />\r\n- Chỉ cần ngươi kh&ocirc;ng ngại, nam nh&acirc;n như ta sao phải từ chối.<br />\r\n<br />\r\n- Rất quyết đo&aacute;n nha.<br />\r\n<br />\r\nTheo Tần Khinh Tuyết về đến nh&agrave;, Diệp Thi&ecirc;n rốt cuộc cũng hiểu v&igrave; sao n&agrave;ng lại thoải m&aacute;i ở c&ugrave;ng như vậy. Tần Khinh Tuyết ở trong một c&aacute;i trang vi&ecirc;n khổng lồ nằm ở khu vực ngoại th&agrave;nh. Ch&iacute;nh giữa l&agrave; một t&ograve;a biệt thự rất lớn, xung quanh trang vi&ecirc;n được thiết kế tinh xảo từ đường đi, vườn hoa, khu vực đ&aacute;nh golf&hellip;<br />\r\n<br />\r\nLối kiến tr&uacute;c của biệt thự đối với Diệp Thi&ecirc;n rất lạ lẫm. N&oacute; chia th&agrave;nh từng ph&ograve;ng ra lớn nhỏ kh&aacute;c nhau, được nối lại bằng cầu thang v&agrave; h&agrave;nh lang. Diện t&iacute;ch nhỏ hơn Th&aacute;i Hư Thần Cung v&ocirc; số lần nhưng thiết kế rất tốt, kh&ocirc;ng c&oacute; cảm gi&aacute;c chật hẹp. Nếu như kh&ocirc;ng c&oacute; g&igrave; ngo&agrave;i &yacute; muốn, hắn v&agrave; Tần Khinh Tuyết mỗi người sẽ ở một ph&ograve;ng. Th&igrave; ra đ&acirc;y mới l&agrave; định nghĩa ở chung nh&agrave; của n&agrave;ng.<br />\r\n<br />\r\nXe từ cửa v&agrave;o đến cổng biệt thự cần mất hơn mười ph&uacute;t, Tần Khinh Tuyết cũng tiện thể giải th&iacute;ch về nơi n&agrave;y. Đ&acirc;y l&agrave; trang ri&ecirc;ng vi&ecirc;n của Tần Khinh Tuyết, những người kh&aacute;c của Tần gia cũng kh&ocirc;ng ở nơi n&agrave;y. B&ecirc;n trong chỉ c&oacute; quản gia, gi&uacute;p việc, l&aacute;i xe c&ugrave;ng v&agrave;i người c&oacute; chức vụ đặc th&ugrave;.<br />\r\n<br />\r\nXe dừng trước cổng biệt thự, chỉ c&oacute; quản gia đi ra ngh&ecirc;nh đ&oacute;n Tần Khinh Tuyết. N&agrave;ng kh&ocirc;ng phải loại người th&iacute;ch hư vinh, cũng kh&ocirc;ng cần tất cả người gi&uacute;p việc đứng xếp h&agrave;ng ch&agrave;o đ&oacute;n m&igrave;nh.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nĐưa Dương Thi&ecirc;n v&agrave;o ph&ograve;ng kh&aacute;ch, Tần Khinh Tuyết để hắn ngồi xuống rồi n&oacute;i tiếp:<br />\r\n<br />\r\n- Vườn hoa, s&acirc;n golf&hellip;c&ugrave;ng phần lớn c&aacute;c khu vực chung trong biệt thự ngươi đều c&oacute; thể đến. Tất nhi&ecirc;n kh&ocirc;ng t&iacute;nh ph&ograve;ng ri&ecirc;ng của một v&agrave;i người. Quan trọng nhất ch&iacute;nh l&agrave; tuyệt đối kh&ocirc;ng được lại gần căn ph&ograve;ng cuối h&agrave;nh lang. Nơi đ&oacute; l&agrave; ph&ograve;ng nghỉ của gia gia mỗi khi đến chơi, b&ecirc;n trong c&oacute; cất rất nhiều bảo vật của hắn. Cho d&ugrave; l&agrave; ta cũng chưa từng bước v&agrave;o.<br />\r\n<br />\r\nDiệp Thi&ecirc;n gật đầu tỏ vẻ đ&atilde; hiểu. Hắn cũng kh&ocirc;ng c&oacute; hứng th&uacute; g&igrave; với căn ph&ograve;ng kia. C&aacute;i gọi l&agrave; bảo vật kia trong mắt Diệp Thi&ecirc;n chẳng c&oacute; ch&uacute;t gi&aacute; trị g&igrave;, c&ograve;n chẳng bằng đi t&igrave;m hiểu thế giới mới lạ n&agrave;y.<br />\r\n<br />\r\nTần Khinh Tuyết giới thiệu Diệp Thi&ecirc;n với quản gia v&agrave; những người kh&aacute;c trong nh&agrave; dưới th&acirc;n phận kh&aacute;ch qu&yacute;. Sau khi để quản gia sắp xếp cho hắn một ph&ograve;ng xong n&agrave;ng lập tức rời đi. Tần Khinh Tuyết phải tiếp tục xử l&yacute; hậu quả Diệp Thi&ecirc;n để lại, đối ph&oacute; một đ&aacute;m người li&ecirc;n tục gọi điện đến hỏi thăm th&ocirc;ng tin về hắn cũng như trấn an dư luận, d&igrave;m chuyện n&agrave;y xuống, thu&ecirc; nh&acirc;n c&ocirc;ng đem c&aacute;i hố khổng lồ kia lấp lại.<br />\r\n<br />\r\nDiệp Thi&ecirc;n được quản gia sắp xếp cho một căn ph&ograve;ng kh&aacute; lớn, b&ecirc;n trong đầy đủ tiện nghi. Ở tại đặc khu v&agrave;i ng&agrave;y, hắn th&ocirc;ng qua thần thức quan s&aacute;t cũng biết được c&aacute;ch sử dụng v&agrave;i thứ. Diệp Thi&ecirc;n kh&ocirc;ng c&oacute; đồ đạc g&igrave;, quản gia đợi hắn l&agrave;m quen ph&ograve;ng một l&uacute;c liền muốn dẫn hắn đến si&ecirc;u thị mua sắm.<br />\r\n<br />\r\nDiệp Thi&ecirc;n đương nhi&ecirc;n kh&ocirc;ng từ chối. Thế giới rộng lớn, phải đi nhiều mới học được nhiều. Để t&igrave;m hiểu chuy&ecirc;n s&acirc;u phải c&oacute; kiến thức cơ bản. Vậy muốn c&oacute; đủ kiến thức cơ bản trong thời gian ngắn nhất phải đi nơi n&agrave;o? Tất nhi&ecirc;n l&agrave; nh&agrave; s&aacute;ch rồi.<br />\r\n<br />\r\nKh&aacute;c với t&agrave;ng kinh c&aacute;c tại Thần Giới, b&ecirc;n trong chất đầy c&ocirc;ng ph&aacute;p, thần kỹ gi&aacute; trị cao, muốn v&agrave;o phải trả một c&aacute;i gi&aacute; rất đắt, nh&agrave; s&aacute;ch tại nơi đ&acirc;y kh&aacute; đơn giản. Kh&ocirc;ng c&oacute; tiền cũng c&oacute; thể v&agrave;o, thậm ch&iacute; đọc l&eacute;n miễn ph&iacute;, chỉ c&oacute; thu&ecirc; hoặc mua mới phải trả tiền. Vậy n&ecirc;n vừa rời khỏi trang vi&ecirc;n, mục ti&ecirc;u đầu ti&ecirc;n của Diệp Thi&ecirc;n ch&iacute;nh l&agrave; nh&agrave; s&aacute;ch lớn nhất th&agrave;nh phố.</p>', '2021-11-30 21:13:10', 4, 1),
(263, 69, 'Mua Sắm Điên Cuồng', '5', '<p>Khi Diệp Thi&ecirc;n bắt đầu qu&aacute; tr&igrave;nh t&igrave;m hiểu thế giới mới, Tần Khinh Tuyết đ&atilde; bị gia gia của n&agrave;ng gọi đến gặp mặt. Tần Khinh Tuyết đứng thẳng người, bộ dạng nghi&ecirc;m t&uacute;c kh&ocirc;ng ch&uacute;t sợ h&atilde;i nh&igrave;n vị l&atilde;o nh&acirc;n đang ngồi sau chiếc b&agrave;n đối diện. L&atilde;o nh&acirc;n n&agrave;y t&oacute;c đ&atilde; bạc trắng thế nhưng đối mắt rất c&oacute; th&acirc;n, d&aacute;ng người to lớn chứ kh&ocirc;ng yếu đuối như những &ocirc;ng l&atilde;o b&igrave;nh thường. Hắn nh&igrave;n chằm chằm Tần Khinh Tuyết một hồi l&acirc;u, thấy n&agrave;ng kh&ocirc;ng c&oacute; &yacute; định nhượng bộ mới thầm thở ra một hơi:<br />\r\n<br />\r\n- Khinh Tuyết, ngươi kh&ocirc;ng định cho ta một lời giải th&iacute;ch về chuyện lần n&agrave;y sao?<br />\r\n<br />\r\nTần Khinh Tuyết b&igrave;nh tĩnh đ&aacute;p:<br />\r\n<br />\r\n- Gia gia, kh&ocirc;ng phải ta đ&atilde; sớm n&oacute;i r&otilde; rồi sao. Đ&acirc;y l&agrave; biện ph&aacute;p giải quyết tốt nhất.<br />\r\n<br />\r\nTần Ch&iacute;nh tức giận mắng:<br />\r\n<br />\r\n- Hồ đồ. Một kẻ mạnh như vậy cho d&ugrave; mất tr&iacute; nhớ cũng sẽ trở th&agrave;nh mối nguy hiểm với x&atilde; hội. Ngươi để hắn tự do, lỡ như xảy ra chuyện th&igrave; phải l&agrave;m thế n&agrave;o?<br />\r\n<br />\r\nTần Khinh Tuyết kh&ocirc;ng trả lời m&agrave; hỏi lại:<br />\r\n<br />\r\n- Vậy theo ngươi ch&uacute;ng ta phải l&agrave;m sao?<br />\r\n<br />\r\n- Đương nhi&ecirc;n l&agrave; chi&ecirc;u h&agrave;ng, phục vụ cho qu&acirc;n đội.<br />\r\n<br />\r\n- Nếu như thất bại?<br />\r\n<br />\r\n- Kẻ n&agrave;y qu&aacute; đ&aacute;ng sợ, kh&ocirc;ng d&ugrave;ng được tất nhi&ecirc;n phải loại trừ.<br />\r\n<br />\r\nTần Khinh Tuyết khẽ lắc đầu:</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n- Gia gia, tư tưởng của ngươi qu&aacute; lạc hậu, c&agrave;ng l&agrave; kh&ocirc;ng thể &aacute;p dụng trong trường hợp n&agrave;y. Ngươi cho rằng một kẻ mạnh như hắn n&oacute;i loại bỏ l&agrave; loại bỏ được sao? Ta cũng kh&ocirc;ng ngại n&oacute;i cho ngươi biết, Diệp Thi&ecirc;n l&agrave; Kh&iacute; Thể đồng tu, cả hai lĩnh vực đều đạt đến đẳng cấp cao nhất. Hắn so với Dị Năng Giả cấp SSS hay Tu Đạo Giả t&ocirc;ng sư đều mạnh hơn nhiều. Thậm ch&iacute; ta nghi ngờ c&aacute;i hố s&acirc;u kia l&agrave; do ch&iacute;nh hắn tạo ra chứ kh&ocirc;ng phải vũ kh&iacute; n&oacute;ng. Muốn giết một kẻ như vậy&hellip;ngươi c&oacute; từng nghĩ c&aacute;i gi&aacute; ch&uacute;ng ta phải trả đắt đến thế n&agrave;o hay kh&ocirc;ng?<br />\r\n<br />\r\nTần Ch&iacute;nh trừng to mắt. Những g&igrave; Tần Khinh Tuyết vừa n&oacute;i vốn rất kh&oacute; tin. Bất qu&aacute; hắn hiểu r&otilde; t&iacute;nh c&aacute;ch của n&agrave;ng, những chuyện quan trọng tuyệt đối sẽ kh&ocirc;ng n&oacute;i qu&aacute;. Từ trước đến nay, V&otilde; Giả vẫn lu&ocirc;n bị Dị Năng Giả v&agrave; Tu Đạo Giả ch&egrave;n &eacute;p. D&ugrave; sao đối phương mới sinh ra đ&atilde; c&oacute; tư chất vượt bậc, V&otilde; Giả b&igrave;nh thường kh&ocirc;ng so s&aacute;nh được. Thậm ch&iacute; V&otilde; Giả c&agrave;ng tu c&agrave;ng kh&oacute;, đối phương chỉ cần kh&ocirc;ng chết sớm muộn cũng sẽ th&agrave;nh cao thủ. Bọn hắn c&oacute; lợi thế duy nhất ch&iacute;nh l&agrave; vũ kh&iacute; n&oacute;ng. Thế nhưng khi đ&aacute;m người kia qu&aacute; mạnh, vũ kh&iacute; n&oacute;ng cũng hạn chế rất nhiều. D&ugrave; sao cũng kh&ocirc;ng thể t&ugrave;y tiện d&ugrave;ng t&ecirc;n lửa đạn đạo chỉ để tấn c&ocirc;ng một người được.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; lần đầu ti&ecirc;n xuất hiện một V&otilde; Giả ph&aacute; vỡ những hạn chế n&agrave;y, Tần Ch&iacute;nh c&oacute; thể kh&ocirc;ng ngạc nhi&ecirc;n sao? Người n&agrave;y nếu d&ugrave;ng được sẽ đủ sức trấn &aacute;p những kẻ v&ocirc; ph&aacute;p v&ocirc; thi&ecirc;n kia.<br />\r\n<br />\r\n- Vậy ngươi phải mời ch&agrave;o hắn bằng mọi c&aacute;ch mới đ&uacute;ng.<br />\r\n<br />\r\nTần Ch&iacute;nh đ&atilde; đổi từ chi&ecirc;u h&agrave;ng sang mời ch&agrave;o, chứng tỏ hắn đ&atilde; d&agrave;nh cho Diệp Thi&ecirc;n sự t&ocirc;n trọng cao nhất. Tần Khinh Tuyết chỉ lắc đầu:<br />\r\n<br />\r\n- Ngươi kh&ocirc;ng hiểu. Tuy chỉ tiếp x&uacute;c một thời gian ngắn, ta đ&atilde; hiểu phần n&agrave;o t&iacute;nh c&aacute;ch của người n&agrave;y. Ban đầu ta v&agrave; L&yacute; Mộ Vũ đ&atilde; thử đưa ra rất nhiều điều kiện nhưng Diệp Thi&ecirc;n ho&agrave;n to&agrave;n kh&ocirc;ng hề c&oacute; hứng th&uacute;. Chỉ khi ta nhắc đến việc để cho hắn tự do, t&ecirc;n n&agrave;y đ&atilde; lập tức đồng &yacute;. N&oacute;i c&aacute;ch kh&aacute;c, ngo&agrave;i tự do ra hắn kh&ocirc;ng quan t&acirc;m đến bất kỳ thứ g&igrave; kh&aacute;c. Ngươi nghĩ một người như vậy sẽ chấp nhận gia nhập, chịu sự quản chế của ch&uacute;ng ta sao?<br />\r\n<br />\r\nTần Ch&iacute;nh vẫn tỏ ra tiếc nuối:<br />\r\n<br />\r\n- Ngươi n&ecirc;n thử th&ecirc;m một ch&uacute;t. Cao thủ như vậy, ch&uacute;ng ta c&oacute; thể cho hắn những ưu đ&atilde;i đặc biệt.<br />\r\n<br />\r\nTần Khinh Tuyết lắc đầu:<br />\r\n<br />\r\n- Ta cảm thấy kh&ocirc;ng cần thiết. Một kẻ như vậy nếu c&agrave;ng cố gắng khống chế c&agrave;ng dễ g&acirc;y ra phản ứng ngược lại. Nếu để hắn c&oacute; &aacute;c cảm, thậm ch&iacute; trở mặt th&agrave;nh th&ugrave;, hậu quả rất kh&oacute; tưởng tượng.<br />\r\n<br />\r\n- Vậy ngươi dự định&hellip;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n- Để hắn tự do, đồng thời tạo quan hệ thật tốt. Ta thấy t&iacute;nh c&aacute;ch người n&agrave;y kh&ocirc;ng tệ. Nếu ch&uacute;ng ta đối xử tốt với hắn, khi c&oacute; chuyện cần hỗ trợ hắn ch&iacute;n phần sẽ kh&ocirc;ng từ chối. D&ugrave; kh&ocirc;ng thể một mực sai khiến nhưng vẫn sẽ gi&uacute;p ch&uacute;ng ta giải quyết kh&aacute; nhiều kh&oacute; khăn. Đ&aacute;m người kia lộng h&agrave;nh nhiều năm như vậy, mượn Diệp Thi&ecirc;n trấn &aacute;p bọn hắn l&agrave; được.<br />\r\n<br />\r\n- Ngươi nắm chắc?<br />\r\n<br />\r\n- &Iacute;t nhất khả năng th&agrave;nh c&ocirc;ng cao hơn biện ph&aacute;p của ngươi.<br />\r\n<br />\r\n- Được rồi, nếu ngươi đ&atilde; quyết định ta cũng kh&ocirc;ng nhiều lời th&ecirc;m nữa. Ph&iacute;a tr&ecirc;n hỏi xuống ta sẽ thay ngươi xử l&yacute;, những việc kh&aacute;c cũng kh&ocirc;ng c&oacute; vấn đề. Ngươi tập trung tạo quan hệ với Diệp Thi&ecirc;n l&agrave; đủ rồi.<br />\r\n<br />\r\n- Gia gia, ngươi y&ecirc;n t&acirc;m. Ta sẽ l&agrave;m tốt chuyện n&agrave;y.<br />\r\n<br />\r\n&hellip;<br />\r\n<br />\r\nDiệp Thi&ecirc;n kh&ocirc;ng biết những chuyện n&agrave;y, cho d&ugrave; biết hắn cũng sẽ kh&ocirc;ng bận t&acirc;m. Hắn đang thử nghiệm việc bộc lộ một phần sức mạnh, nếu tiến triển kh&ocirc;ng như mong muốn liền x&oacute;a sạch k&yacute; ức về bản th&acirc;n của những người kia, sau đ&oacute; t&igrave;m một khởi đầu mới kh&aacute;c l&agrave; được. Bất qu&aacute; hiện tại cũng kh&ocirc;ng tệ lắm, c&oacute; chỗ ăn ở, c&oacute; người gi&uacute;p đỡ l&agrave;m quen thế giới, c&ograve;n hơn một th&acirc;n một m&igrave;nh tự t&igrave;m hiểu. Những kinh nghiệm Diệp Thi&ecirc;n c&oacute; được phần lớn đều l&agrave; từ trong huyễn cảnh, nhưng bởi v&igrave; tu vi của hắn qu&aacute; cao n&ecirc;n cảm thấy những huyễn cảnh kia qu&aacute; giả tạo v&agrave; nh&agrave;m ch&aacute;n. Hiện tại ch&acirc;n ch&iacute;nh tiếp x&uacute;c với người thật, việc thật n&ecirc;n th&uacute; vị hơn nhiều.<br />\r\n<br />\r\nQuản gia của Tần gia gọi l&agrave; M&atilde; Đức H&ugrave;ng, một họ kh&aacute; hiếm gặp. Hắn tự giới thiệu l&agrave; c&ocirc; nhi do gia gia của Tần Khinh Tuyết thu dưỡng từ nhỏ, lớn l&ecirc;n bộc lộ ch&uacute;t t&agrave;i năng n&ecirc;n được gia tộc trọng dụng. Về sau một mực ở b&ecirc;n cạnh Tần Khinh Tuyết, vừa l&agrave;m quản gia, vừa gi&uacute;p n&agrave;ng xử l&yacute; vụ sự.<br />\r\n<br />\r\nLớn l&ecirc;n c&ugrave;ng Tần Khinh Tuyết, M&atilde; Đức H&ugrave;ng từ th&aacute;i độ c&ugrave;ng lời n&oacute;i liền biết Tần Khinh Tuyết rất xem trọng Diệp Thi&ecirc;n n&ecirc;n tỏ ra kh&aacute; cởi mở, th&acirc;n thiện với hắn. Diệp Thi&ecirc;n cũng rất vui vẻ đ&aacute;p lại. Diệp Thi&ecirc;n ch&iacute;nh l&agrave; rất dễ tin người, d&ugrave; sao tr&ecirc;n thế giới n&agrave;y cũng kh&ocirc;ng c&oacute; ai l&agrave;m hại được hắn. C&oacute; đủ sức mạnh liền suy nghĩ đơn giản một ch&uacute;t để cuộc sống vui vẻ hơn.<br />\r\n<br />\r\nM&atilde; Đức H&ugrave;ng c&ugrave;ng Diệp Thi&ecirc;n đi v&agrave;o nh&agrave; s&aacute;ch. Hắn c&oacute; đ&ocirc;i ch&uacute;t ngạc nhi&ecirc;n khi Diệp Thi&ecirc;n chỉ lướt qua từng kệ s&aacute;ch lớn chứ kh&ocirc;ng đọc ch&uacute;t n&agrave;o. Nh&agrave; s&aacute;ch tuy lớn nhưng tốc độ đi của cả hai rất nhanh, chỉ hơn ba mươi ph&uacute;t liền xong. Diệp Thi&ecirc;n xử l&yacute; to&agrave;n bộ th&ocirc;ng tin vừa tiếp nhận được chỉ trong v&agrave;i ph&uacute;t rồi cười n&oacute;i:<br />\r\n<br />\r\n- Thế giới n&agrave;y thật th&uacute; vị a.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nM&atilde; Đức H&ugrave;ng kh&ocirc;ng hiểu lắm về c&acirc;u n&oacute;i của hắn:<br />\r\n<br />\r\n- Ngươi vừa n&oacute;i g&igrave;?<br />\r\n<br />\r\nDiệp Thi&ecirc;n lắc đầu:<br />\r\n<br />\r\n- Kh&ocirc;ng c&oacute; g&igrave;, ch&uacute;ng ta đi mua một ch&uacute;t vật dụng cần thiết đi.<br />\r\n<br />\r\n- Nhưng ngươi c&ograve;n chưa xem&hellip;<br />\r\n<br />\r\n- Kh&ocirc;ng cần thiết nữa.<br />\r\n<br />\r\nDiệp Thi&ecirc;n cũng kh&ocirc;ng thể n&oacute;i khi vừa bước v&agrave;o nh&agrave; s&aacute;ch, thần thức của hắn đ&atilde; xem qua tất cả s&aacute;ch ở đ&acirc;y. Nửa giờ đồng hồ đi lại chẳng qua l&agrave; h&igrave;nh thức, d&ugrave; sao cũng kh&ocirc;ng thể vừa nh&igrave;n thấy liền quay xe đi được.<br />\r\n<br />\r\nC&oacute; đầy đủ th&ocirc;ng tin, Diệp Thi&ecirc;n xem như đ&atilde; c&oacute; hiểu biết cơ bản về thế giới n&agrave;y. Hắn h&agrave;nh động rất tự nhi&ecirc;n, từ mua sắm vật dụng thiết yếu đến y phục, điện thoại v&agrave; m&aacute;y t&iacute;nh b&agrave;n. Tần Khinh Tuyết rất gi&agrave;u, Tần gia lại c&agrave;ng gi&agrave;u, ch&uacute;t tiền n&agrave;y đối với n&agrave;ng chẳng c&oacute; &yacute; nghĩa g&igrave;. M&agrave; Diệp Thi&ecirc;n trước nay ưu điểm ch&iacute;nh l&agrave; da mặt d&agrave;y, kh&ocirc;ng cần n&oacute;i hắn cũng sẽ kh&ocirc;ng kh&aacute;ch s&aacute;o.<br />\r\n<br />\r\nLần đầu ti&ecirc;n tiếp x&uacute;c với những thứ mới lạ n&agrave;y, Diệp Thi&ecirc;n vừa thấy liền mua. Đến mức M&atilde; Đức H&ugrave;ng kh&ocirc;ng chịu nổi phải gọi người đến v&aacute;c đồ hộ. Bọn hắn kh&ocirc;ng thể để người của si&ecirc;u thị chuyển h&agrave;ng đến tận nh&agrave;, d&ugrave; sao trang vi&ecirc;n của Tần Khinh Tuyết cũng cấm người kh&ocirc;ng li&ecirc;n quan tiếp cận.<br />\r\n<br />\r\nĐồ đạc đ&atilde; chất th&agrave;nh đống, đ&aacute;m người theo đu&ocirc;i l&ecirc;n đến hơn hai mươi, sắc trời cũng tối, Diệp Thi&ecirc;n đ&agrave;nh trở về trong tiếc nuối. Hắn ho&agrave;n to&agrave;n kh&ocirc;ng biết Tần Khinh Tuyết đ&atilde; sớm ở nh&agrave; đợi m&igrave;nh&hellip;</p>', '2021-11-30 22:10:15', 13, 1),
(264, 70, 'Giữa Chiến địa.', '1', '<p>Long Hưng, Đại Việt, 1390.</p>\r\n\r\n<p><br />\r\nGiếttttttt......</p>\r\n\r\n<p><br />\r\nHai đo&agrave;n kỵ binh như 2 cơn lũ sắt th&eacute;p lao thẳng v&agrave;o nhau, trường thương g&atilde;y đoạn, người ng&atilde; ngựa đổ, tay ch&acirc;n cụt nủn, m&aacute;u v&agrave; nội tạng vương v&atilde;i khắp nơi. Khắp chiến địa l&agrave; tiếng la h&eacute;t, chửi rủa, ngựa h&iacute;, tiếng binh kh&iacute; va chạm ch&aacute;t ch&uacute;a, kh&ocirc;ng kh&iacute; ngập tr&agrave;n m&ugrave;i m&aacute;u tươi tanh tưởi quện với m&ugrave;i b&ugrave;n đất&hellip;<br />\r\n<br />\r\nPhải mất h&agrave;ng vạn, h&agrave;ng triệu năm con người mới tiến h&oacute;a được như h&ocirc;m nay nhưng để biến thanh d&atilde; th&uacute; chỉ cần v&agrave;i ph&uacute;t...Giữa chiến trường, nơi m&agrave; nh&acirc;n t&iacute;nh l&agrave; thứ g&igrave; đ&oacute; xa vời, những con người với đ&ocirc;i mắt vằn đỏ như lo&agrave;i d&atilde; th&uacute;, lao v&agrave;o nhau, lăn xả v&agrave;o nhau, cắn x&eacute; lẫn nhau...mục đ&iacute;ch duy nhất l&agrave; giết chết đối phương. Những lời hoa mĩ mi&ecirc;u tả về sự dũng cảm, về vinh dự, vinh quang kh&ocirc;ng tồn tại ở nơi đ&acirc;y...ở đ&acirc;y chỉ c&oacute; m&aacute;u v&agrave; sắt, những lời nguyền rủa c&ugrave;ng với những tiếng r&ecirc;n rỉ th&ecirc; lương của người sắp chết...Chiến trường l&agrave; nơi nh&acirc;n gian luyện ngục, nơi m&agrave; d&atilde; t&iacute;nh, c&aacute;i phần con của con người được thể hiện r&otilde; r&agrave;ng nhất...<br />\r\n..........<br />\r\nC&aacute;nh tay đau như muốn g&atilde;y đoạn khiến hắn tỉnh lại...mờ mịt nh&igrave;n những thứ diễn ra xung quanh...khắp nơi đều l&agrave; cảnh ch&eacute;m giết v&agrave; m&aacute;u...&acirc;m thanh hỗn tạp những tiếng va chạm ch&aacute;t ch&uacute;a, r&ecirc;n la....tất cả như một cơn &aacute;c mộng ch&acirc;n thực, kinh khủng nhất....Kh&ocirc;ng để cho hắn thất thần l&acirc;u, một thanh đao sắc lẻm theo tiếng x&eacute; gi&oacute; đang ch&eacute;m đến đầu hắn....theo bản năng, tay tr&aacute;i giơ l&ecirc;n để đỡ..tưởng chừng như chỉ chậm một gi&acirc;y nữa th&ocirc;i, c&aacute;nh tay hắn sẽ cụt lủn, m&aacute;u tươi bắn tứ tung v&agrave; c&oacute; khi l&agrave; k&egrave;m theo cả c&aacute;i đầu của hắn...Nhưng kh&ocirc;ng, chỉ nghe &ldquo;Choang&rdquo; một tiếng thật to, thanh đao bị cản lại bởi tấm khiến đeo tr&ecirc;n tay tr&aacute;i, mặt khi&ecirc;n hiện r&otilde; một v&eacute;t l&otilde;m s&acirc;u như chứng minh cho sức mạnh của nh&aacute;t ch&eacute;m cũng như độ bền của khi&ecirc;n.....<br />\r\n<br />\r\nMột luồng điện chạy dọc theo cơ thể, bản năng chiến đấu trỗi dậy, những kỹ năng ch&eacute;m giết được học tập, lặp đi lặp lại qua v&ocirc; số cuộc chiến lớn nhỏ đ&atilde; khắc s&acirc;u v&agrave;o linh hồn, v&agrave;o cơ thể, tay phải cầm đao của hắn vung mạnh ra, k&egrave;m với đ&oacute; l&agrave; một tiếng &ldquo;Phật&rdquo; c&ugrave;ng một khỏa đầu l&acirc;u bay giữa kh&ocirc;ng chung k&eacute;o theo vệt m&aacute;u th&ecirc; mĩ....tr&ecirc;n khu&ocirc;n mặt vẫn c&ograve;n đọng lại n&eacute;t dữ dằn, vặn vẹo c&ugrave;ng với kh&ocirc;ng cam l&ograve;ng...Th&acirc;n thể mất điều khiển lăn xuống khỏi lưng ngựa.....</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nD&ograve;ng m&aacute;u n&oacute;ng bắn v&agrave;o mặt hắn, c&aacute;i cảm gi&aacute;c n&oacute;ng ấm v&agrave; m&ugrave;i tanh tưởi n&agrave;y l&agrave;m hắn như t&ecirc; dại...một cảm x&uacute;c kh&oacute; tả...tất cả những thứ n&agrave;y, c&aacute;i chiến trường n&agrave;y, những kỵ binh n&agrave;y, đao kiếm cung gi&aacute;p, m&aacute;u tươi c&ugrave;ng nội tạng...tất cả đều rất kh&ocirc;ng ch&acirc;n thực đối với hắn, một người sinh hoạt ở thế kỉ 21, nơi m&agrave; chỉ 2 người đ&aacute;nh nhau đ&atilde; l&agrave; to chuyện....nhưng tất cả c&aacute;i cảm gi&aacute;c n&agrave;y, c&aacute;nh tay tr&aacute;i đau nhức, m&ugrave;i m&aacute;u tanh nồng c&ugrave;ng cảm gi&aacute;c ấm ấm nhớt nhớt ở mặt n&oacute;i cho hắn biết đ&acirc;y kh&ocirc;ng phải mộng, tất cả đều l&agrave; thực..V&agrave; hắn, đang ở giữa chiến trường....giữa một cỗ m&aacute;y say thịt khổng lồ...<br />\r\n<br />\r\nMặc kệ cảm x&uacute;c hắn như thế n&agrave;o đi chăng nữa, ở tr&ecirc;n chiến trường n&agrave;y kh&ocirc;ng ai quan t&acirc;m, kẻ địch th&igrave; muốn giết hết bọn hắn, c&ograve;n đồng bạn cũng đi&ecirc;n cuồng ch&eacute;m giết...tất cả đều trong cơn đi&ecirc;n, đi&ecirc;n cuồng ch&eacute;m giết....Một kỵ sĩ chỉ được ph&eacute;p thơ thẩn mộng mơ khi đi tr&ecirc;n đường l&agrave;ng, trong thời b&igrave;nh chứ kh&ocirc;ng phải ở nơi đ&acirc;y...Giữa chiến trường, một kỵ sĩ đứng ngơ ng&aacute;c sẽ l&agrave; miếng mồi b&eacute;o bở cho kẻ địch...<br />\r\n<br />\r\nKh&ocirc;ng cần chờ đợi l&acirc;u, chỉ nghe &ldquo;uỳnh&rdquo; một tiếng, vật g&igrave; đ&ograve; rất nặng đập hắn bay khỏi lưng ngựa. &ldquo;rầm..&rdquo; Hắn nằm s&otilde;ng so&agrave;i tr&ecirc;n đất, giữa đống b&ugrave;n nhầy nhụa...Tay tr&aacute;i đau nhức lợi hại, ch&iacute;nh n&oacute; c&ugrave;ng tấm khi&ecirc;n t&agrave;n tạ đ&atilde; cứu hắn 2 mạng trong chưa đầy 5 ph&uacute;t, nếu kh&ocirc;ng c&oacute; khi&ecirc;n chắn giữa, ngực hắn c&oacute; thể đ&atilde; bị đập vỡ...v&agrave; hắn cũng kh&ocirc;ng thể nằm đ&acirc;y m&agrave; cảm nhận cơn đau...<br />\r\n<br />\r\nChật vật b&ograve; dậy, bộ gi&aacute;p sắt nặng nề như muốn k&eacute;o hắn ng&atilde; xuống, tấm khi&ecirc;n cũng l&agrave; một g&aacute;nh nặng to lớn nhưng nếu bỏ ra th&igrave; chỉ khiến hắn chết nhanh hơn th&ocirc;i...<br />\r\n<br />\r\n&ldquo;Tr&aacute;nh khỏi c&aacute;i nơi chết tiệt n&agrave;y c&agrave;ng nhanh c&agrave;ng tốt&rdquo;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nNết c&aacute;i th&acirc;n thể t&agrave;n tạ dậy, cố gắng đứng vững x&aacute;c định phương hướng để bỏ chạy nhưng hỡi &ocirc;i, khắp nơi đều l&agrave; cảnh ch&eacute;m giết, qu&acirc;n l&iacute;nh hai b&ecirc;n giết đỏ cả mắt, từ kỵ chiến đ&aacute;nh th&agrave;nh bộ chiến, lăn xả v&agrave;o nhau, h&ograve;ng giết sạch đối phương&hellip;. kh&ocirc;ng c&oacute; đường n&agrave;o để trốn tho&aacute;t.&rdquo;<br />\r\n<br />\r\n&ldquo;H&Iacute;III&hellip;Ầm&rdquo; Một con chiến m&atilde; ng&atilde; sập xuống ngay cạnh hắn, cổ bị ngọn gi&aacute;o ghim v&agrave;o, m&aacute;u ph&igrave; ph&igrave; chảy ra khỏi m&otilde;m, n&oacute; r&ecirc;n rỉ bất lực, đ&ocirc;i mắt to như quả b&oacute;ng tennis tr&agrave;n ngập th&ecirc; lương, gi&atilde;y giụa, Kỵ sĩ tr&ecirc;n lưng n&oacute; bị đ&egrave; bẹp, thực ra y đ&atilde; chết từ l&acirc;u, c&aacute;i x&aacute;c kh&ocirc;ng đầu c&ograve;n mắc tr&ecirc;n lưng ngựa m&agrave; th&ocirc;i. Nh&igrave;n &aacute;o gi&aacute;p giống với &aacute;o gi&aacute;p hắn đang mặc, chắc l&agrave; qu&acirc;n m&igrave;nh đ&acirc;y m&agrave;. Kệ th&ocirc;i, phải trốn đ&atilde;.<br />\r\n<br />\r\n&ldquo;Cứ đ&acirc;m bừa một hướng&rdquo; hắn quyết t&acirc;m nhưng trời kh&ocirc;ng bao giờ chiều l&ograve;ng người, một t&ecirc;n địch cầm gi&aacute;o lao về ph&iacute;a hắn, mặt cười vặn v&egrave;o đầy dữ tợn &ldquo;một thằng l&iacute;nh mới lại được mặc gi&aacute;p sắt &agrave;...sau khi mi chết th&igrave; n&oacute; l&agrave; của ta&rdquo;<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; đường để chạy &ldquo;Mịa, đừng qua đ&acirc;y&rdquo; tay hắn run mạnh, t&ecirc;n kia thấy thế th&igrave; nụ cười c&agrave;ng th&ecirc;m &ldquo;rực rỡ&rdquo; cầm gi&aacute;o đ&acirc;m mạnh về ph&iacute;a hắn..Nhanh người n&aacute;ch qua một b&ecirc;n, đao cũng ch&eacute;m mạnh xuống cổ t&ecirc;n địch, m&aacute;u bắn tứ tung, đao cũng mắc kẹt kh&ocirc;ng r&uacute;t ra được...Khu&ocirc;n mặt t&ecirc;n kia kh&ocirc;ng c&ograve;n nụ cười vặn vẹo nữa m&agrave; đầy thống khổ, y muốn n&oacute;i g&igrave; đ&oacute; nhưng kh&ocirc;ng được, cứ mở mồm l&agrave; m&aacute;u ọc ra, mắt y đầy cừu hận c&ugrave;ng kh&ocirc;ng cam t&acirc;m nhưng dần dần tan r&atilde;, mất đi ti&ecirc;u cự....Hắn cũng chưa ho&agrave;n hồn lại, tay phải c&agrave;ng run hơn, kh&ocirc;ng cầm vững được chu&ocirc;i đao để k&eacute;o ra...N&oacute;i th&igrave; l&acirc;u nhưng mọi thứ diễn ra chỉ trong v&ograve;ng v&agrave;i gi&acirc;y, trong khi hắn c&ograve;n kh&ocirc;ng r&uacute;t đao ra kịp th&igrave; đầu bị thứ g&igrave; đ&oacute; đập mạnh, cảnh vật xung quang dần trở l&ecirc;n mờ ảo rồi tối sầm đi, hắn ng&atilde; xuống nằm ngay cạnh t&ecirc;n địch m&agrave; hắn vừa giết..<br />\r\n<br />\r\nTh&igrave; ra khi hắn đang loanh hoay r&uacute;t đao th&igrave; một t&ecirc;n định kh&aacute;c từ ph&iacute;a sau lấy khi&ecirc;n đập mạnh v&agrave;o đầu hắn, ngay khi muốn tiến l&ecirc;n cho nh&acirc;n vật ch&iacute;nh một đao kết th&uacute;c th&igrave; t&ecirc;n kia đ&atilde; bị một đao bay đầu, x&aacute;c đ&egrave; l&ecirc;n hắn...Những cảnh như vậy diễn ra khắp nơi tr&ecirc;n chiến trường.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nTrận chiến vẫn tiếp tục, kh&ocirc;ng c&oacute; v&igrave; một hai c&aacute; nh&acirc;n chết m&agrave; dừng lại..m&aacute;u tươi l&agrave;m tất cả trở l&ecirc;n đi&ecirc;n cuồng hơn...Trời mưa to tầm t&atilde;, tr&ecirc;n trời lo&agrave;i chim ăn x&aacute;c thối vẫn bay lượn, mặc kệ mưa d&ocirc;ng, như đang chờ đợi để thượng thức bữa tiệc thịnh soạn b&ecirc;n dưới.....Hắn nằm đ&oacute;, tr&ecirc;n đống b&ugrave;n lạnh lẽo đầy m&aacute;u tươi như bao x&aacute;c chết kh&aacute;c tr&ecirc;n chiến trường...<br />\r\n<br />\r\nMột trận tao ngộ chiến nho nhỏ kh&ocirc;ng đ&aacute;ng kể trong cuộc chiến tranh Đại Việt &ndash; Chi&ecirc;m Th&agrave;nh nhưng mức độ thảm khốc th&igrave; kh&ocirc;ng cần phải b&agrave;n c&atilde;i, hiếm khi n&agrave;o m&agrave; hai đội kỵ binh lại đi&ecirc;n m&aacute;u lao đầu trực diện v&agrave;o nhau, kh&ocirc; m&aacute;u đến người cuối c&ugrave;ng như vậy.<br />\r\n<br />\r\nTr&ecirc;n phim ảnh, cảnh kỵ binh xung phong, đ&acirc;m thẳng v&agrave;o đội h&igrave;nh bộ binh với những ngọn gi&aacute;o tua tủa, hay hai đội kỵ binh lao v&agrave;o nhau quần ẩu diễn ra rất thường xuy&ecirc;n, gần như phim n&agrave;o cũng c&oacute; nhưng tr&ecirc;n thực tế, trong chiến tranh thực th&igrave; việc n&agrave;y lại kh&aacute; &iacute;t khi xảy ra&hellip;kể cả đối với những đội kỵ binh mạnh mẽ nhất. Con người ai cũng muốn sống, con vật cũng vậy, thế n&ecirc;n việc lao v&agrave;o nhau với tốc độ cao gần như kh&ocirc;ng thể, kể cả khi c&aacute;c kỵ sĩ sẵn s&agrave;ng tử chiến th&igrave; bọn ngựa cũng nản l&ograve;ng. 2 đội kỵ binh lao v&agrave;o nhau với tốc độ cao để người ng&atilde; ngựa đổ th&igrave; &iacute;t ỏi v&ocirc; v&ugrave;ng&hellip;.v&agrave; đen thay cho nh&acirc;n vật ch&iacute;nh của ch&uacute;ng ta, vừa ch&agrave;o thời đại mới bằng một trận chiến c&oacute; một kh&ocirc;ng hai, một trận kh&ocirc; m&aacute;u m&agrave; &iacute;t đội qu&acirc;n n&agrave;o muốn.</p>', '2021-11-30 22:16:25', 9, 1),
(265, 71, 'Hồng Thủy', '1', '<p>T&ocirc; L&ecirc; l&agrave; bị bảy giờ bốn mươi điện thoại di động đồng hồ b&aacute;o thức thức tỉnh.<br />\r\n<br />\r\nVừa mới tỉnh lại, người c&ograve;n c&oacute; ch&uacute;t mơ mơ m&agrave;ng m&agrave;ng, đầu mơ hồ l&agrave;m đau, đưa tay từ tr&ecirc;n tủ đầu giường cầm điện thoại di động l&ecirc;n, quan đồng hồ b&aacute;o thức, cảm gi&aacute;c được c&oacute; ch&uacute;t qu&aacute; m&oacute;t, từ tr&ecirc;n giường b&ograve; l&ecirc;n.<br />\r\n<br />\r\n&quot;Tối h&ocirc;m qua lại uống say rồi, sau đ&oacute; thật kh&ocirc;ng thể lại như thế uống.&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; cảm gi&aacute;c miệng kh&ocirc; lưỡi kh&ocirc;, trong cổ họng giống chạm hỏa giống như kh&oacute; chịu.<br />\r\n<br />\r\nTối h&ocirc;m qua l&agrave; bồi trọng yếu kh&aacute;ch h&agrave;ng, v&igrave; k&iacute; xuống kia b&uacute;t tờ khai, bữa tối, KTV, ăn khuya liền uống ba trường, hắn tuy rằng tửu lượng kh&ocirc;ng sai, cũng kh&ocirc;ng chống đỡ được.<br />\r\n<br />\r\nUống liền l&eacute;n l&uacute;t đi ph&ograve;ng rửa tay m&oacute;c yết hầu nhổ ra, trở về lại tiếp tục uống, như vậy uống nhổ, nhổ ra uống, cuối c&ugrave;ng say như chết, liền l&agrave;m sao về nh&agrave; cũng đ&atilde; nhớ kh&ocirc;ng r&otilde; rồi.<br />\r\n<br />\r\n&quot;Ai, lại uống nhỏ nhặt rồi.&quot; T&ocirc; L&ecirc; cảm gi&aacute;c đầu hỗn loạn, trong nội t&acirc;m nơi s&acirc;u xa cảm th&aacute;n sinh hoạt kh&ocirc;ng dễ, một th&acirc;n một m&igrave;nh ở ngoại địa dốc sức l&agrave;m tư vị, c&oacute; thể kh&ocirc;ng dễ chịu.<br />\r\n<br />\r\nTừ tr&ecirc;n giường ngồi dậy đến, mới vừa mặc v&agrave;o d&eacute;p lại ph&aacute;t hiện giầy l&agrave; ướt.<br />\r\n<br />\r\n&quot;Hả? L&agrave;m sao mặt đất ẩm ướt như vậy?&quot; T&ocirc; L&ecirc; nhất thời tỉnh t&aacute;o hơn nửa, vội v&agrave;ng đứng l&ecirc;n.<br />\r\n<br />\r\nLẽ n&agrave;o l&agrave; tối h&ocirc;m qua uống say, mở ra cái nào v&ograve;i nước kh&ocirc;ng c&oacute; đ&oacute;ng, kết quả nước tr&agrave;n đi ra, đem mặt đất cho ch&igrave;m rồi?<br />\r\n<br />\r\nNh&igrave;n đến mặt đất tr&ecirc;n đ&acirc;u đ&acirc;u cũng c&oacute; một mảnh ẩm ướt, T&ocirc; L&ecirc; vội v&agrave;ng kiểm tra nh&agrave; bếp c&ugrave;ng ph&ograve;ng vệ sinh, x&aacute;c định kh&ocirc;ng c&oacute; chỗ n&agrave;o ở r&ograve; nước.<br />\r\n<br />\r\n&quot;Kỳ qu&aacute;i, kh&ocirc;ng nh&igrave;n thấy nơi n&agrave;o r&ograve; nước a, l&agrave;m sao mặt đất ẩm ướt như vậy, thật giống bị nước ch&igrave;m qu&aacute; giống như, lẽ n&agrave;o l&agrave; nơi n&agrave;o ống nước vỡ tan rồi? Nhưng ta n&agrave;y đ&atilde; l&agrave; tầng cao nhất, cũng kh&ocirc;ng thể l&agrave; tr&ecirc;n lầu r&ograve; nước xuống đi. Thật phiền phức, l&agrave; ch&iacute;nh m&igrave;nh t&igrave;m sư phụ tới xem một ch&uacute;t vẫn l&agrave; trực tiếp li&ecirc;n hệ chủ nh&agrave; trọ? Qu&ecirc;n đi, chờ tan tầm trở lại hẵng n&oacute;i đi.&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; lắc đầu một c&aacute;i, cảm gi&aacute;c được c&oacute; ch&uacute;t đau đầu, hắn l&agrave; sợ nhất phiền phức người.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; hắn thu&ecirc; nh&agrave;, căn cao ốc n&agrave;y cộng hữu ba mươi tầng, hắn thu&ecirc; gian n&agrave;y vừa vặn ở v&agrave;o cao ốc tầng cao nhất.<br />\r\n<br />\r\nNh&igrave;n xem thời gian, đ&atilde; sắp 7 giờ 50, hắn t&aacute;m giờ rưỡi phải đi l&agrave;m, từ nơi n&agrave;y dựng xe c&ocirc;ng cộng đi hắn đi l&agrave;m c&ocirc;ng ty ước chừng 20 ph&uacute;t đường xe.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nHắn kh&ocirc;ng d&aacute;m lại l&agrave;m lỡ, vội v&agrave;ng đ&aacute;nh răng rửa mặt.<br />\r\n<br />\r\nBởi v&igrave; tối h&ocirc;m qua ngủ đ&ecirc;́n qu&aacute; trễ, hiện tại một đ&ocirc;i mắt c&ograve;n đang mơ hồ nở, ở l&uacute;c rửa mặt liền thuận tay d&ugrave;ng khăn mặt che hai mắt, qu&aacute; rồi chừng mười gi&acirc;y mới lấy xuống, l&uacute;c n&agrave;y cảm gi&aacute;c con mắt thoải m&aacute;i kh&ocirc;ng &iacute;t, tinh thần cũng kh&ocirc;i phục một &iacute;t.<br />\r\n<br />\r\nBắt khăn mặt, nghi&ecirc;ng đầu nh&igrave;n b&ecirc;n người kia d&aacute;n v&agrave;o kh&ocirc;ng minh bạch giấy b&oacute;ng k&iacute;nh ph&ograve;ng vệ sinh cửa sổ khe hở mở được kh&aacute; l&agrave; nhỏ, đưa tay đẩy cửa sổ, đem ho&agrave;n to&agrave;n mở ra.<br />\r\n<br />\r\nTheo cửa sổ ra b&ecirc;n ngo&agrave;i vừa nh&igrave;n, T&ocirc; L&ecirc; m&iacute; mắt đột nhi&ecirc;n nhảy một c&aacute;i.<br />\r\n<br />\r\n&quot;N&agrave;y. . . Đ&acirc;y l&agrave;. . .&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; con ngươi ph&oacute;ng to, vội v&agrave;ng đem đầu vươn ra ngo&agrave;i, nh&igrave;n ph&iacute;a b&ecirc;n ngo&agrave;i cửa sổ, h&iacute;t v&agrave;o một ngụm kh&iacute; lạnh.<br />\r\n<br />\r\nTừ cửa sổ nh&igrave;n ra ngo&agrave;i, b&ecirc;n ngo&agrave;i l&agrave; s&oacute;ng nước lấp lo&aacute;ng mặt nước, trừ bỏ nước, c&aacute;i g&igrave; cao lầu, con đường, xe cộ, tất cả đều kh&ocirc;ng nh&igrave;n thấy rồi.<br />\r\n<br />\r\n&quot;Sao c&oacute; thể c&oacute; chuyện đ&oacute;?&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; kh&ocirc;ng nhịn được thất thanh gọi l&ecirc;n, ngực th&igrave;nh thịch nhảy loạn, ngay lập tức sẽ nhắm hai mắt lại, cả người ở khẽ run.<br />\r\n<br />\r\n&quot;Vừa mới nhất định l&agrave; d&ugrave;ng khăn mặt che mắt thời gian qu&aacute; d&agrave;i, sản sinh ảo gi&aacute;c.&quot; T&ocirc; L&ecirc; trong l&ograve;ng y&ecirc;n lặng nghĩ, lại xoa xoa hai mắt, sau, mở mắt lần nữa.<br />\r\n<br />\r\nPh&iacute;a b&ecirc;n ngo&agrave;i cửa sổ, vẫn l&agrave; mặt nước, thậm ch&iacute; hắn c&ograve;n nh&igrave;n thấy c&aacute;ch đ&oacute; kh&ocirc;ng xa tr&ecirc;n mặt nước, bồng bềnh một c&aacute;i m&agrave;u lam d&eacute;p nhựa.<br />\r\n<br />\r\n&quot;Ầm&quot; một tiếng, lần n&agrave;y T&ocirc; L&ecirc; đột nhi&ecirc;n đem c&aacute;nh cửa sổ n&agrave;y đ&oacute;ng chặt lại, trong l&ograve;ng b&ocirc;́c l&ecirc;n thấy lạnh cả người, lao ra ph&ograve;ng vệ sinh, bởi v&igrave; vọt tới qu&aacute; nhanh, đầu ng&oacute;n ch&acirc;n của hắn đ&aacute; v&agrave;o một b&ecirc;n tr&ecirc;n khung cửa, đau đến hắn c&aacute;i tr&aacute;n đều b&ocirc;́c l&ecirc;n mồ h&ocirc;i lạnh.<br />\r\n<br />\r\nChỉ l&agrave; kh&ocirc;ng lo được đến xem đầu ng&oacute;n ch&acirc;n c&oacute; bị thương kh&ocirc;ng, T&ocirc; L&ecirc; khập khễnh vọt tới tr&ecirc;n ban c&ocirc;ng, k&eacute;o ra s&acirc;n thượng r&egrave;m cửa sổ, sau đ&oacute;, hắn ng&acirc;y người như phỗng đứng ở b&ecirc;n s&acirc;n thượng, cả người cứng ngắc, tr&aacute;i tim nhảy l&ecirc;n kịch liệt, hai tay kh&ocirc;ng ngừng được ở khẽ run.<br />\r\n<br />\r\nXuy&ecirc;n thấu qua s&acirc;n thượng n&agrave;y một h&agrave;ng cửa sổ, T&ocirc; L&ecirc; rốt cục triệt để nhìn rõ ràng rồi.<br />\r\n<br />\r\nHắn vào mắt c&oacute; thể nh&igrave;n thấy th&agrave;nh thị, ho&agrave;n to&agrave;n bị nước nhấn ch&igrave;m rồi, th&agrave;nh phố n&agrave;y đ&atilde; biến th&agrave;nh rộng lớn v&ocirc; bi&ecirc;n mặt nước.<br />\r\n<br />\r\nỞ tr&ecirc;n mặt nước, chỉ c&oacute; thể xa xa nh&igrave;n thấy một &iacute;t linh tinh nh&ocirc; ra kiến tr&uacute;c.<br />\r\n<br />\r\nNhững n&agrave;y lộ ra mặt nước tất cả đều l&agrave; vượt qua ba mươi tầng kiến tr&uacute;c, ba mươi tầng trở xuống, đều bị nước nhấn ch&igrave;m rồi.<br />\r\n<br />\r\nT&ocirc; L&ecirc; trụ căn cao ốc n&agrave;y vừa vặn l&agrave; ba mươi tầng, cũng chỉ c&oacute; hắn chỗ ở tầng cao nhất lộ ở ngo&agrave;i mặt nước, từ tầng hai mươi ch&iacute;n đi xuống, to&agrave;n bộ bị nhấn ch&igrave;m rồi.<br />\r\n<br />\r\n&quot;Đ&acirc;y căn bản kh&ocirc;ng thể. . . Ta n&agrave;y nhất định l&agrave; tại nằm mơ!&quot;<br />\r\n<br />\r\nTuy rằng T&ocirc; L&ecirc; đ&atilde; hai mươi bảy tuổi, cũng coi như trải qua hai năm x&atilde; hội đ&aacute;nh đập, t&acirc;m ch&iacute; vẫn t&iacute;nh th&agrave;nh thục, nhưng giờ khắc này vẫn tr&agrave;n ngập hoảng sợ, ho&agrave;n to&agrave;n kh&ocirc;ng thể tiếp thu con mắt nh&igrave;n thấy tất cả, hắn mạnh mẽ bấm tay tr&aacute;i m&igrave;nh c&aacute;nh tay một hồi.<br />\r\n<br />\r\nHắn bấm đ&ecirc;́n mức r&acirc;́t t&agrave;n nhẫn, tr&ecirc;n c&aacute;nh tay ngay lập tức sẽ xanh một tảng lớn, đau đến hắn h&iacute;t một hơi thật d&agrave;i hơi lạnh.<br />\r\n<br />\r\nN&agrave;y kịch liệt đau đớn c&ugrave;ng trước mắt ch&acirc;n thực l&agrave;m cho hắn &yacute; thức được trước mắt nh&igrave;n thấy tất cả tuyệt đối kh&ocirc;ng phải mộng, cũng căn bản kh&ocirc;ng thể l&agrave; ảo gi&aacute;c.<br />\r\n<br />\r\nHắn hỗn loạn kh&ocirc;ng thể tả đại n&atilde;o chậm r&atilde;i b&igrave;nh tĩnh một &iacute;t, hắn c&uacute;i đầu nh&igrave;n về ph&iacute;a mặt đất ẩm ướt.<br />\r\n<br />\r\nChẳng lẽ n&oacute;i, đ&acirc;y ch&iacute;nh l&agrave; mặt đất ẩm ướt nguy&ecirc;n nh&acirc;n thực sự?<br />\r\n<br />\r\nỞ tối h&ocirc;m qua ch&iacute;nh m&igrave;nh say rượu ngủ sau, th&agrave;nh phố n&agrave;y thật đột nhi&ecirc;n bị hồng thủy nhấn ch&igrave;m?<br />\r\n<br />\r\nChỉ l&agrave;, nếu như đ&uacute;ng l&agrave; đột ph&aacute;t khổng lồ như thế hồng thủy, l&agrave;m sao sẽ một ch&uacute;t động tĩnh đều kh&ocirc;ng c&oacute;? Vẫn l&agrave; n&oacute;i m&igrave;nh bởi v&igrave; say rượu ngủ đ&ecirc;́n qu&aacute; nặng, sở dĩ kh&ocirc;ng nghe bất luận c&aacute;i g&igrave; động tĩnh?<br />\r\n<br />\r\nRất nhanh T&ocirc; L&ecirc; giống nghĩ tới điều g&igrave;, mở cửa, đi đến tr&ecirc;n h&agrave;nh lang, vội v&agrave;ng đ&aacute;nh s&aacute;t v&aacute;ch hộ gia đ&igrave;nh cửa.<br />\r\n<br />\r\nN&agrave;y một đơn nguy&ecirc;n c&aacute;c gia đ&igrave;nh trừ bỏ hắn ở ngo&agrave;i, c&ograve;n c&oacute; mặt kh&aacute;c hai nh&agrave;, T&ocirc; L&ecirc; c&ugrave;ng bọn họ kh&ocirc;ng quen, chỉ biết s&aacute;t v&aacute;ch ở ch&iacute;nh l&agrave; c&aacute;i trang phục thời thượng nữ h&agrave;i tuổi trẻ, chuyển tới c&ograve;n kh&ocirc;ng l&acirc;u, khoảng chừng chỉ c&oacute; hai, ba th&aacute;ng d&aacute;ng vẻ.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nBởi v&igrave; song phương giờ đi l&agrave;m gần như, đụng tới số lần kh&ocirc;ng &iacute;t, chỉ l&agrave; xưa nay chưa hề n&oacute;i chuyện.<br />\r\n<br />\r\nPhần lớn thời gian thấy n&agrave;ng đều l&agrave; một người độc lai độc v&atilde;ng, tình cờ cũng c&oacute; thể nh&igrave;n thấy nàng mang theo nam sinh về nh&agrave;, chỉ l&agrave; hắn đụng tới ba lần, nàng ba lần mang nam sinh đều kh&ocirc;ng giống nhau.<br />\r\n<br />\r\n&quot;N&agrave;y, c&oacute; hay kh&ocirc;ng ở nh&agrave;!&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; kh&ocirc;ng lo được lễ ph&eacute;p, đem cửa đập đ&ecirc;́n vang động trời, lại kh&ocirc;ng người đ&aacute;p lại.<br />\r\n<br />\r\nMắt thấy kh&ocirc;ng phản ứng, T&ocirc; L&ecirc; theo h&agrave;nh lang lại x&ocirc;ng tới kh&aacute;c một chỗ hộ gia đ&igrave;nh trước cửa.<br />\r\n<br />\r\nHắn nhớ tới nơi n&agrave;y trụ ch&iacute;nh l&agrave; một đ&ocirc;i tuổi trẻ tiểu phu th&ecirc;, nu&ocirc;i một c&aacute;i rất ầm ĩ Chihuahua.<br />\r\n<br />\r\nHắn đang muốn g&otilde; cửa, đột nhi&ecirc;n ph&aacute;t hiện n&agrave;y cửa cũng kh&ocirc;ng c&oacute; kh&oacute;a tr&ecirc;n, m&agrave; l&agrave; kh&eacute;p hờ.<br />\r\n<br />\r\n&quot;C&oacute; người c&oacute; ở nh&agrave; kh&ocirc;ng?&quot; T&ocirc; L&ecirc; dừng lại v&agrave;i gi&acirc;y, gặp kh&ocirc;ng c&oacute; người đ&aacute;p lại, liền đem cửa đẩy ra rồi.<br />\r\n<br />\r\nCửa bị hắn đẩy ra, vào mắt nh&igrave;n thấy trong ph&ograve;ng kh&aacute;ch t&ugrave;m la t&ugrave;m lum, cửa giầy tủ một b&ecirc;n c&oacute; hai đ&ocirc;i giầy.<br />\r\n<br />\r\nMột đ&ocirc;i l&agrave; nam sĩ gi&agrave;y da, một đ&ocirc;i l&agrave; nữ sĩ giầy cao g&oacute;t, trong đ&oacute; c&oacute; một c&aacute;i gi&agrave;y da lăn tới, đ&aacute;y gi&agrave;y hướng về ph&iacute;a tr&ecirc;n.<br />\r\n<br />\r\nMột b&ecirc;n th&ugrave;ng r&aacute;c ng&atilde; tr&ecirc;n mặt đất, b&ecirc;n trong trang r&aacute;c rưởi tung đi ra.<br />\r\n<br />\r\nV&agrave;o cửa tr&ecirc;n b&agrave;n ăn đặt hai c&aacute;i thức ăn ngo&agrave;i thường d&ugrave;ng h&igrave;nh tr&ograve;n hộp ny lon, một trong hộp trang hơn nửa b&aacute;t nước canh, c&ograve;n c&oacute; một hộp đổ v&agrave;o tr&ecirc;n b&agrave;n, m&igrave; nước đều giội đi ra, chỉ l&agrave; kia nước canh đ&atilde; ở tr&ecirc;n b&agrave;n đọng lại rồi.</p>', '2021-12-01 01:13:37', 8, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(266, 71, 'Bị Nhốt', '2', '<p>Tựa hồ đ&atilde; từng ở nơi n&agrave;y một đ&ocirc;i tiểu phu th&ecirc; nguy&ecirc;n bản đang ngồi ở tr&ecirc;n b&agrave;n ăn ăn thức ăn ngo&agrave;i đưa tới m&igrave; nước, l&agrave;m ăn được một nửa thời điểm, đột nhi&ecirc;n gặp được c&aacute;i g&igrave;, l&agrave;m bọn họ hoảng sợ cực điểm, liều lĩnh x&ocirc;ng ra ngo&agrave;i đi, khiến cho b&ecirc;n trong một th&ugrave;ng nước canh dội ở tr&ecirc;n b&agrave;n.<br />\r\n<br />\r\nBởi v&igrave; qu&aacute; mức hoảng loạn, bọn họ đ&aacute; ng&atilde; để ở một b&ecirc;n th&ugrave;ng r&aacute;c, kh&ocirc;ng lo được đổi b&igrave;nh thường mặc đi ra ngo&agrave;i gi&agrave;y da c&ugrave;ng giầy cao g&oacute;t, m&agrave; l&agrave; ăn mặc d&eacute;p liền x&ocirc;ng ra ngo&agrave;i.<br />\r\n<br />\r\nT&ocirc; L&ecirc; nh&igrave;n trước mắt ph&ograve;ng kh&aacute;ch, trong đầu mơ hồ giống hiện l&ecirc;n đ&ocirc;i n&agrave;y tiểu phu th&ecirc; hỗn loạn hoảng sợ lao ra cảnh tượng.<br />\r\n<br />\r\nChỉ l&agrave;, bọn họ gặp được c&aacute;i g&igrave;, sẽ l&agrave;m bọn họ như vậy hoảng sợ liền x&ocirc;ng ra ngo&agrave;i, thậm ch&iacute; liền m&igrave; nước đều dội đến tr&ecirc;n b&agrave;n, đ&aacute; ng&atilde; th&ugrave;ng r&aacute;c?<br />\r\n<br />\r\n&quot;Lẽ n&agrave;o. . . Tối h&ocirc;m qua thật đột ph&aacute;t hồng thủy? Cặp vợ chồng n&agrave;y ph&aacute;t hiện, cho n&ecirc;n mới liều lĩnh x&ocirc;ng ra ngo&agrave;i, bởi v&igrave; vọt tới qu&aacute; nhanh, sở dĩ m&igrave; nước đều tung, giầy cũng kh&ocirc;ng kịp đổi, thậm ch&iacute; cửa đều kh&ocirc;ng c&oacute; quan.&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; nắm thật chặt hai tay, l&ograve;ng b&agrave;n tay đang chầm chậm ra b&ecirc;n ngo&agrave;i thấm mồ h&ocirc;i: &quot;M&agrave; ta bởi v&igrave; uống rượu say, ngủ đ&ecirc;́n qu&aacute; chết, sở dĩ c&aacute;i g&igrave; cũng kh&ocirc;ng biết, lẽ n&agrave;o, trong cả cao ốc n&agrave;y liền chỉ c&ograve;n lại ta một người? Những người kh&aacute;c tất cả đều đ&agrave;o tẩu rồi?<br />\r\n<br />\r\nLoại &yacute; nghĩ n&agrave;y lệnh cả người hắn ho&agrave;n to&agrave;n lạnh lẽo, trong l&ograve;ng h&agrave;n &yacute; c&agrave;ng ng&agrave;y c&agrave;ng s&acirc;u.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng đ&uacute;ng, ra sao đột ph&aacute;t hồng thủy, c&oacute; thể ở trong nửa ng&agrave;y đem cả t&ograve;a th&agrave;nh thị đều nhấn ch&igrave;m rồi, n&agrave;y ho&agrave;n to&agrave;n kh&ocirc;ng hợp với lẽ thường.&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; tối h&ocirc;m qua tuy rằng uống đoạn phiến, nhưng vẫn c&ograve;n c&oacute; ch&uacute;t ấn tượng, ch&iacute;nh m&igrave;nh ch&iacute; &iacute;t ăn khuya đến rạng s&aacute;ng 1 giờ tr&aacute;i phải, khi đ&oacute; căn bản liền mưa cũng kh&ocirc;ng xuống, coi như l&agrave; từ rạng s&aacute;ng 1 giờ t&iacute;nh l&ecirc;n, đến hiện tại cũng bất qu&aacute; bảy tiếng, ra sao hồng thủy c&oacute; thể ở kh&ocirc;ng tới trong v&ograve;ng bảy tiếng đem một t&ograve;a hiện đại th&agrave;nh thị nhấn ch&igrave;m.<br />\r\n<br />\r\nNh&igrave;n ph&ograve;ng kh&aacute;ch, T&ocirc; L&ecirc; cũng kh&ocirc;ng c&oacute; lập tức vọt v&agrave;o, tr&aacute;i lại cảm gi&aacute;c được kh&ocirc;ng n&oacute;i ra được quỷ dị, tựa hồ trong ph&ograve;ng kh&aacute;ch n&agrave;y giống ẩn giấu đi c&aacute;i g&igrave; qu&aacute;i vật khủng bố, chỉ đợi ch&iacute;nh m&igrave;nh đi v&agrave;o, qu&aacute;i vật n&agrave;y liền lại đột nhi&ecirc;n x&ocirc;ng tới, đem ch&iacute;nh m&igrave;nh x&eacute; n&aacute;t.<br />\r\n<br />\r\nL&agrave;m con mắt của hắn rơi xuống kia ph&ograve;ng kh&aacute;ch tr&ecirc;n khay tr&agrave; điện thoại thời điểm, mới cơ linh run l&ecirc;n, thầm k&ecirc;u một tiếng đ&aacute;ng chết, ch&iacute;nh m&igrave;nh bởi v&igrave; qu&aacute; mức khủng hoảng, đột nhi&ecirc;n bị n&agrave;y kh&ocirc;ng thể tưởng tượng nổi một m&agrave;n, cũng chỉ nghĩ đến lao ra h&agrave;nh lang đến gọi người, lại đ&atilde; qu&ecirc;n c&ograve;n c&oacute; thể gọi điện thoại.<br />\r\n<br />\r\nT&ocirc; L&ecirc; điện thoại di động kh&ocirc;ng mang tr&ecirc;n người, lập tức về nh&agrave;, tr&ecirc;n tủ đầu giường t&igrave;m tới điện thoại di động của ch&iacute;nh m&igrave;nh, li&ecirc;n hệ bạn g&aacute;i Vương Lam.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nVương Lam nhỏ hơn hắn hai tuổi, người gốc th&agrave;nh phố, c&ugrave;ng hắn ở c&ugrave;ng một c&ocirc;ng ty đi l&agrave;m, tuy rằng hai người nhận thức hơn nửa năm, nhưng x&aacute;c lập luyến &aacute;i quan hệ c&ograve;n chưa vượt qua hai th&aacute;ng, Vương Lam vẫn kh&ocirc;ng c&oacute; n&oacute;i cho cha mẹ, nàng mỗi đ&ecirc;m c&ograve;n cần về nh&agrave; ở, cũng kh&ocirc;ng c&oacute; c&ugrave;ng T&ocirc; L&ecirc; ở c&ugrave;ng một chỗ.<br />\r\n<br />\r\nBấm Vương Lam điện thoại, lại ph&aacute;t hiện điện thoại di động kh&ocirc;ng c&oacute; t&iacute;n hiệu, căn bản kh&ocirc;ng đ&aacute;nh ra được.<br />\r\n<br />\r\n&quot;Xảy ra chuyện g&igrave;? Nơi n&agrave;y t&iacute;n hiệu vẫn rất tốt đẹp.&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; liền với bấm mấy điện thoại, bao qu&aacute;t cha mẹ điện thoại, bao qu&aacute;t bạn b&egrave; L&yacute; Vĩnh Thắng điện thoại, tất cả đều kh&ocirc;ng gọi được.<br />\r\n<br />\r\n&quot;L&agrave;m sao sẽ một điểm t&iacute;n hiệu đều kh&ocirc;ng c&oacute;?&quot; T&ocirc; L&ecirc; theo s&aacute;t bấm b&aacute;o nguy cầu viện điện thoại , tương tự kh&ocirc;ng c&oacute; phản ứng, tr&aacute;n của hắn, chậm r&atilde;i xuất hiện mồ h&ocirc;i lạnh, t&igrave;nh huống dưới mắt, c&agrave;ng ng&agrave;y c&agrave;ng quỷ dị rồi.<br />\r\n<br />\r\nKh&ocirc;ng chỉ điện thoại di động kh&ocirc;ng c&oacute; t&iacute;n hiệu, hắn điện thoại di động gắn l&ecirc;n v&ocirc; tuyến mạng lưới cũng kh&ocirc;ng c&oacute; t&iacute;n hiệu, trong điện thoại di động WeChat, QQ tất cả đều l&agrave; đứt sợi trạng th&aacute;i.<br />\r\n<br />\r\nHắn kh&ocirc;ng cam l&ograve;ng muốn đem m&aacute;y truyền h&igrave;nh mở ra, mới ph&aacute;t hiện bị c&uacute;p điện rồi.<br />\r\n<br />\r\nTheo s&aacute;t T&ocirc; L&ecirc; nghĩ đến vừa mới ở cặp tiểu phu th&ecirc; kia ở lại ph&ograve;ng ốc trong ph&ograve;ng kh&aacute;ch, nh&igrave;n thấy điện thoại b&agrave;n.<br />\r\n<br />\r\nĐiện thoại di động kh&ocirc;ng c&oacute; bất kỳ t&iacute;n hiệu g&igrave;, n&agrave;y kia điện thoại b&agrave;n đ&acirc;y?<br />\r\n<br />\r\nHắn kh&ocirc;ng c&oacute; c&ocirc;ng việc điện thoại b&agrave;n, muốn đ&aacute;nh kia điện thoại b&agrave;n, chỉ c&oacute; thể đi cặp tiểu phu th&ecirc; kia trong ph&ograve;ng.<br />\r\n<br />\r\nT&ocirc; L&ecirc; lần thứ hai đi đến cặp tiểu phu th&ecirc; kia ở lại trước cửa ph&ograve;ng.<br />\r\n<br />\r\nCửa chống trộm vẫn mở ra, T&ocirc; L&ecirc; con mắt nh&igrave;n chằm chằm kia ph&ograve;ng kh&aacute;ch tr&ecirc;n khay tr&agrave; điện thoại b&agrave;n, s&acirc;u trong nội t&acirc;m mơ hồ c&oacute; một loại cảm gi&aacute;c, điện thoại n&agrave;y c&oacute; thể c&ugrave;ng điện thoại di động một dạng kh&ocirc;ng gọi được, nhưng hắn vẫn l&agrave; đi v&agrave;o.<br />\r\n<br />\r\nTuy rằng kh&ocirc;ng c&oacute; b&aacute;o hy vọng qu&aacute; lớn, nhưng hắn cũng kh&ocirc;ng thể bỏ qua bất luận c&aacute;i n&agrave;o c&oacute; thể c&ugrave;ng li&ecirc;n lạc với b&ecirc;n ngo&agrave;i độ khả thi.<br />\r\n<br />\r\nV&agrave;o cửa, T&ocirc; L&ecirc; ngửi được một luồng m&ugrave;i lạ, con mắt liền rơi xuống một b&ecirc;n th&ugrave;ng r&aacute;c tr&ecirc;n.<br />\r\n<br />\r\nHắn ngửi được trong c&aacute;i th&ugrave;ng r&aacute;c đổ n&agrave;y, truyền đến một luồng m&ugrave;i h&ocirc;i m&ugrave;i lạ.<br />\r\n<br />\r\nĐến gần th&ugrave;ng r&aacute;c, hắn nh&igrave;n đến nơi n&agrave;y c&oacute; ch&uacute;t cơm thừa đồ ăn thừa, chỉ l&agrave; đ&atilde; ph&aacute;t mốc, đang tản ph&aacute;t ra m&ugrave;i h&ocirc;i thối, lại nh&igrave;n b&agrave;n ăn, kia giội ở tr&ecirc;n b&agrave;n tr&ecirc;n vắt m&igrave;, cũng xuất hiện rất nhiều chỗ mốc.<br />\r\n<br />\r\nT&ocirc; L&ecirc; vươn tay ra, ở tr&ecirc;n b&agrave;n ăn vừa sờ, m&ograve; đến một lớp bụi.<br />\r\n<br />\r\n&quot;Nếu như bọn họ thực sự l&agrave; tối h&ocirc;m qua nửa đ&ecirc;m cảm gi&aacute;c được c&aacute;i g&igrave; kh&ocirc;ng đ&uacute;ng, chạy ra ngo&agrave;i, chỉ l&agrave; thời gian nửa ng&agrave;y, tr&ecirc;n b&agrave;n tại sao c&oacute; thể c&oacute; d&agrave;y như thế một lớp bụi, những đồ ăn n&agrave;y cũng sẽ kh&ocirc;ng mốc meo mục n&aacute;t, chẳng lẽ n&oacute;i bọn họ đ&atilde; rời nh&agrave; rất nhiều ng&agrave;y rồi?&quot;<br />\r\n<br />\r\n&quot;Nghĩ như thế n&agrave;o đều rất kh&ocirc;ng đ&uacute;ng. . . Ph&ograve;ng ốc n&agrave;y. . .&quot; T&ocirc; L&ecirc; trong l&ograve;ng mơ hồ sinh ra &acirc;m thầm sợ h&atilde;i cảm, đối với ph&ograve;ng ốc tĩnh mịch n&agrave;y sinh ra sợ h&atilde;i, nhưng hắn cũng kh&ocirc;ng hề rời đi, nh&igrave;n kia đặt ở tr&ecirc;n khay tr&agrave; điện thoại b&agrave;n, T&ocirc; L&ecirc; cố n&eacute;n trong l&ograve;ng bất an, cầm lấy đồng dạng che k&iacute;n tro bụi điện thoại, lại ph&aacute;t hiện trong điện thoại kh&ocirc;ng c&oacute; &acirc;m thanh.<br />\r\n<br />\r\nT&ocirc; L&ecirc; trong l&ograve;ng cảm gi&aacute;c nặng nề đi, nhưng vẫn l&agrave; chưa từ bỏ &yacute; định ấn b&aacute;o nguy điện thoại.<br />\r\n<br />\r\nQuả nhi&ecirc;n, kh&ocirc;ng c&oacute; phản ứng ch&uacute;t n&agrave;o.<br />\r\n<br />\r\nĐiện thoại b&agrave;n n&agrave;y, kh&ocirc;ng gọi được.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nCuối c&ugrave;ng một ch&uacute;t hy vọng cũng thất bại, ph&ograve;ng ốc n&agrave;y cho hắn cảm gi&aacute;c qu&aacute;i dị lại c&agrave;ng ng&agrave;y c&agrave;ng m&atilde;nh liệt, T&ocirc; L&ecirc; rốt cục kh&ocirc;ng nhịn được, trực tiếp đem ống điện thoại mất rồi, x&ocirc;ng ra ngo&agrave;i.<br />\r\n<br />\r\nVọt tới tr&ecirc;n h&agrave;nh lang, T&ocirc; L&ecirc; mới đại đại thở ra một hơi, trong l&ograve;ng cảm gi&aacute;c sợ h&atilde;i giảm xuống, nhưng kinh hoảng cảm gi&aacute;c bất lực lại c&agrave;ng ng&agrave;y c&agrave;ng m&atilde;nh liệt.<br />\r\n<br />\r\n&quot;Ta hiện tại đến c&ugrave;ng n&ecirc;n l&agrave;m g&igrave;? Lẽ n&agrave;o thật sự ch&iacute;nh l&agrave; tất cả mọi người đều r&uacute;t đi rồi? Chỉ c&oacute; ta bị vứt bỏ ở nơi n&agrave;y? Nhưng là coi như l&agrave; trong th&agrave;nh thị người đều rời đi, tại sao liền một điểm t&iacute;n hiệu đều kh&ocirc;ng c&oacute;, đ&acirc;y rốt cuộc l&agrave; tại sao, Vương Lam hiện tại thế n&agrave;o rồi, nàng an to&agrave;n rời đi sao?&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; cảm gi&aacute;c trong đầu rất hỗn loạn, một l&aacute;t mới &eacute;p buộc ch&iacute;nh m&igrave;nh tỉnh t&aacute;o lại, ngẩng đầu l&ecirc;n, nh&igrave;n thấy cuối h&agrave;nh lang thang m&aacute;y.<br />\r\n<br />\r\nHồng thủy n&agrave;y đem cao ốc ba mươi tầng trở xuống ho&agrave;n to&agrave;n nhấn ch&igrave;m rồi, coi như thang m&aacute;y c&ograve;n c&oacute; thể vận h&agrave;nh, cũng kh&ocirc;ng c&aacute;ch n&agrave;o thừa thang m&aacute;y xuống, nhưng T&ocirc; L&ecirc; suy nghĩ một ch&uacute;t, vẫn l&agrave; đi tới, thử một ch&uacute;t thang m&aacute;y đ&egrave; vặn, kh&ocirc;ng phản ứng ch&uacute;t n&agrave;o, hiển nhi&ecirc;n thang m&aacute;y đ&atilde; cắt điện đ&igrave;nh chỉ vận h&agrave;nh.<br />\r\n<br />\r\nT&ocirc; L&ecirc; lại đẩy ra h&agrave;nh lang một b&ecirc;n hai c&aacute;nh cửa gỗ, trong cửa l&agrave; c&aacute;i cầu thang h&agrave;nh lang.<br />\r\n<br />\r\nHắn đi v&agrave;o, liền nh&igrave;n thấy đi về tầng hai mươi ch&iacute;n cầu thang đ&atilde; bị nước nhấn ch&igrave;m rồi, mặt nước khoảng c&aacute;ch ba mươi tầng mặt đất chỉ c&oacute; 4, 5 centim&eacute;t.<br />\r\n<br />\r\nNước xem ra so s&aacute;nh vẩn đục, T&ocirc; L&ecirc; nh&igrave;n thấy b&ecirc;n trong bồng bềnh chậu nhựa, khăn mặt, t&uacute;i r&aacute;c chờ tạp vật, thậm ch&iacute; c&ograve;n c&oacute; một c&aacute;i bồng bềnh ở trong đ&oacute; chuột chết.<br />\r\n<br />\r\nT&ocirc; L&ecirc; ngồi xổm xuống, do dự một ch&uacute;t, sau đ&oacute; d&ugrave;ng tay nhẹ nh&agrave;ng đụng v&agrave;o trước mặt nước, sau đ&oacute; lại rụt trở về, nh&igrave;n giọt nước theo ng&oacute;n tay của ch&iacute;nh m&igrave;nh nhỏ xuống, rơi xuống tr&ecirc;n mặt nước, bốc ra nho nhỏ gợn sóng, sau đ&oacute;, hắn h&iacute;t một hơi thật s&acirc;u.</p>', '2021-12-01 01:13:58', 7, 1),
(267, 71, 'Xác Chết Trôi', '3', '<p>Loại n&agrave;y ch&acirc;n thực x&uacute;c gi&aacute;c n&oacute;i cho hắn kh&ocirc;ng phải đang nằm mơ, cũng kh&ocirc;ng phải sinh ra ảo gi&aacute;c, hết thảy trước mắt, đều l&agrave; thật sự.<br />\r\n<br />\r\nNh&igrave;n tr&ecirc;n mặt nước phản chiếu ch&iacute;nh m&igrave;nh khu&ocirc;n mặt mơ hồ kia, T&ocirc; L&ecirc; tinh thần trở n&ecirc;n hơi hoảng hốt l&ecirc;n.<br />\r\n<br />\r\nMột th&acirc;n một m&igrave;nh nằm ở loại n&agrave;y tĩnh mịch địa phương, kh&ocirc;ng nh&igrave;n thấy một tia sinh kh&iacute; c&ugrave;ng hi vọng, T&ocirc; L&ecirc; trong l&ograve;ng sinh ra một loại bị to&agrave;n bộ thế giới đều vứt bỏ tuyệt vọng c&ugrave;ng cảm gi&aacute;c c&ocirc; độc.<br />\r\n<br />\r\nĐột nhi&ecirc;n, trước mặt trong nước b&ocirc;́c l&ecirc;n một c&aacute;i bọt nước, mơ hồ giống truyền đến &acirc;m thanh n&agrave;o đ&oacute;.<br />\r\n<br />\r\nĐang c&oacute; ch&uacute;t hoảng hốt T&ocirc; L&ecirc; bị sợ hết hồn, đột nhi&ecirc;n liền đứng l&ecirc;n.<br />\r\n<br />\r\n&quot;L&agrave; ai?&quot; Hắn đột nhi&ecirc;n cảm gi&aacute;c trước mắt trong nước giống c&oacute; m&oacute;n đồ g&igrave;, bản năng để hắn l&ugrave;i về sau một bước.<br />\r\n<br />\r\nHắn nh&igrave;n chằm chằm mặt nước đầy đủ m&acirc;́y chục gi&acirc;y, ph&aacute;t hiện bồng bềnh ở tr&ecirc;n mặt nước chậu nhựa ở hơi rung nhẹ, ngo&agrave;i ra, cũng kh&ocirc;ng c&oacute; c&aacute;i kh&aacute;c động tĩnh.<br />\r\n<br />\r\nKhoảng chừng qu&aacute; rồi một hai ph&uacute;t, x&aacute;c định kh&ocirc;ng c&oacute; c&aacute;i kh&aacute;c động tĩnh sau, T&ocirc; L&ecirc; mới một lần nữa chậm r&atilde;i đến gần cầu thang mặt nước một b&ecirc;n, lần thứ hai khoảng c&aacute;ch gần hướng về trong nước nh&igrave;n lại.<br />\r\n<br />\r\nLần n&agrave;y, hắn nh&igrave;n thấy n&agrave;y đi về tầng tiếp theo cầu thang nơi kh&uacute;c quanh, ở trong nước kia, ng&acirc;m một đo&agrave;n b&oacute;ng đen, chỉ l&agrave; bởi v&igrave; bị cầu thang chặn lại r&ocirc;̀i tầm mắt, hắn kh&ocirc;ng thấy r&otilde; đo&agrave;n n&agrave;y b&oacute;ng đen l&agrave; c&aacute;i g&igrave;.<br />\r\n<br />\r\nT&ocirc; L&ecirc; nh&igrave;n chằm chằm b&oacute;ng đen kia, ngực bắt đầu ầm ầm nhảy loạn, từ hắn c&oacute; thể nh&igrave;n thấy g&oacute;c độ xem ra, n&agrave;y hiển lộ ra b&oacute;ng đen, c&agrave;ng xem c&agrave;ng giống một c&aacute;i đầu người.<br />\r\n<br />\r\nHắn c&oacute; thể khẳng định, trước cũng kh&ocirc;ng thấy đo&agrave;n n&agrave;y b&oacute;ng đen, cũng kh&ocirc;ng biết l&agrave; trước kh&ocirc;ng c&oacute; ch&uacute; &yacute; tới vẫn l&agrave; b&oacute;ng đen n&agrave;y l&agrave; vừa mới thổi qua đến.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Kia. . . Kia giống đầu người. . . Những kia tung bay mở, như l&agrave; t&oacute;c rất d&agrave;i, lẽ n&agrave;o l&agrave; một người phụ nữ đầu. . .&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; c&agrave;ng nghĩ c&agrave;ng thấy đ&ecirc;́n t&ecirc; cả da đầu, sống lưng nơi b&ocirc;́c l&ecirc;n thấy lạnh cả người, rốt cục kh&ocirc;ng khắc chế được loại n&agrave;y cảm gi&aacute;c sợ h&atilde;i, để hắn lảo đảo lui trở về.<br />\r\n<br />\r\nHắn một đường trốn về ph&ograve;ng của ch&iacute;nh m&igrave;nh, rầm một tiếng đ&oacute;ng cửa lại, trở lại quen thuộc địa phương, trong l&ograve;ng hắn hoảng sợ c&ugrave;ng bất an mới tho&aacute;ng giảm bớt, người cũng b&igrave;nh tĩnh một &iacute;t, bắt đầu một lần nữa suy nghĩ vừa mới nh&igrave;n thấy đồ vật.<br />\r\n<br />\r\n&quot;Đ&uacute;ng, kia c&oacute; thể đ&uacute;ng l&agrave; c&aacute;i nữ nh&acirc;n đầu, bởi v&igrave; kia c&oacute; thể l&agrave; c&aacute;i bị chết đuối người, v&ocirc; c&ugrave;ng c&oacute; khả năng ch&iacute;nh l&agrave; ở tại lầu hai mươi ch&iacute;n c&aacute;c gia đ&igrave;nh, nếu như đ&acirc;y l&agrave; thật, vậy n&oacute;i r&otilde; cũng kh&ocirc;ng phải tất cả mọi người đều r&uacute;t đi, m&agrave; l&agrave; c&oacute; rất nhiều người kh&ocirc;ng kịp đ&agrave;o tẩu liền bị đột ph&aacute;t đại hồng thủy chết đuối, ta lại l&agrave; trong hồng thủy người may mắn c&ograve;n sống s&oacute;t, có th&ecirc;̉ c&ograve;n c&oacute; c&aacute;i kh&aacute;c giống như ta người may mắn c&ograve;n sống s&oacute;t bị nhốt, ta cũng kh&ocirc;ng phải c&ocirc; độc một người.&quot;<br />\r\n<br />\r\nVừa nghĩ như thế, T&ocirc; L&ecirc; t&acirc;m thần y&ecirc;n ổn một &iacute;t, hắn suy nghĩ một ch&uacute;t sau, quyết định lại đi kia giữa cầu thang nh&igrave;n c&aacute;i r&otilde; r&agrave;ng, hắn muốn biết vậy rốt cuộc c&oacute; phải l&agrave; một bộ ng&acirc;m ở b&ecirc;n trong nước thi thể.<br />\r\n<br />\r\nHắn lục tung t&ugrave;ng ph&egrave;o, t&igrave;m tới một c&aacute;i thật l&acirc;u chưa từng d&ugrave;ng đ&egrave;n pin cầm tay, thử một ch&uacute;t c&ograve;n c&oacute; thể sử dụng, đem đ&egrave;n pin cầm tay mang tới, lại cầm lấy một c&aacute;i s&agrave;o phơi đồ, lần thứ hai mở cửa đi ra ngo&agrave;i.<br />\r\n<br />\r\nTr&ecirc;n h&agrave;nh lang vẫn lặng lẽ, T&ocirc; L&ecirc; theo h&agrave;nh lang đi đến cuối con đường, đẩy ra b&ecirc;n h&agrave;nh lang hai c&aacute;nh cửa, đi v&agrave;o.<br />\r\n<br />\r\nĐi tới đi xuống cầu thang một b&ecirc;n, mở ra đ&egrave;n pin cầm tay, hướng về trước mặt trong nước chiếu rọi.<br />\r\n<br />\r\nTuy n&oacute;i hiện tại l&agrave; ban ng&agrave;y, nhưng thang lầu n&agrave;y gian dưới cửa sổ lớn, tia s&aacute;ng cũng kh&ocirc;ng phải đặc biệt s&aacute;ng sủa, vừa mới t&acirc;m hoảng &yacute; loạn, hắn cũng kh&ocirc;ng c&oacute; nh&igrave;n kỹ r&otilde; r&agrave;ng, hiện tại mở ra đ&egrave;n pin cầm tay hướng về trong nước cẩn thận chiếu rọi quan s&aacute;t.<br />\r\n<br />\r\nRốt cục, T&ocirc; L&ecirc; cơ bản c&oacute; thể khẳng định, trong nước kia ở kh&uacute;c quanh thang lầu hiển lộ ra một đo&agrave;n b&oacute;ng đen hẳn l&agrave; ch&iacute;nh l&agrave; một bộ thi thể hiển lộ ra đầu người, bởi v&igrave; ng&acirc;m ở trong nước nguy&ecirc;n nh&acirc;n, tr&ecirc;n đầu người n&agrave;y t&oacute;c d&agrave;i tung bay ra, sở dĩ c&oacute; vẻ rất l&agrave; quỷ dị, mới nh&igrave;n b&ecirc;n dưới, thật l&agrave; dọa người.<br />\r\n<br />\r\nC&oacute; chuẩn bị t&acirc;m l&yacute;, lần n&agrave;y T&ocirc; L&ecirc; cũng kh&ocirc;ng c&oacute; qu&aacute; mức hoảng loạn, h&iacute;t một hơi thật s&acirc;u, ổn ổn t&acirc;m thần, thu hồi đ&egrave;n pin cầm tay, hai tay cầm s&agrave;o phơi đồ, d&ograve; v&agrave;o trong nước, muốn đem bộ n&agrave;y ng&acirc;m ở trong nước thi thể vớt tới.<br />\r\n<br />\r\nMột th&acirc;n một m&igrave;nh đối mặt một bộ thi thể, T&ocirc; L&ecirc; l&ograve;ng b&agrave;n tay đang bốc l&ecirc;n giọt mồ h&ocirc;i nhỏ, cảm gi&aacute;c được yết hầu kh&ocirc; khốc, cắn răng, mới rốt cục lấy dũng kh&iacute;, đem s&agrave;o phơi đồ hướng về trong nước duỗi đi.<br />\r\n<br />\r\nRất nhanh T&ocirc; L&ecirc; liền ph&aacute;t hiện s&agrave;o phơi đồ độ d&agrave;i kh&ocirc;ng đủ, với kh&ocirc;ng tới đầu người n&agrave;y, hắn nhíu nhíu mày, rốt cục theo cầu thang, một cước giẫm tiến trong nước, rơi xuống một bước bậc thang.<br />\r\n<br />\r\nGiầy d&iacute;nh nước, T&ocirc; L&ecirc; cảm gi&aacute;c được một &iacute;t lạnh lẽo.<br />\r\n<br />\r\nNếu hạ quyết t&acirc;m, T&ocirc; L&ecirc; ngược lại kh&ocirc;ng c&oacute; suy nghĩ nhiều, hiện ở trong đầu nghĩ tới ch&iacute;nh l&agrave; l&agrave;m sao đem thi thể n&agrave;y m&ograve; tr&ecirc;n đến nh&igrave;n kỹ hẵng n&oacute;i.<br />\r\n<br />\r\nCầm s&agrave;o phơi đồ, theo bị nước nhấn ch&igrave;m cầu thang, liền với đi xuống mấy bước, rất nhanh nước liền ch&igrave;m qu&aacute; rồi đầu gối của hắn, kia s&agrave;o phơi đồ rốt cục đủ đến kia ng&acirc;m ở trong nước thi thể.<br />\r\n<br />\r\nT&ocirc; L&ecirc; đầu ti&ecirc;n l&agrave; d&ugrave;ng s&agrave;o phơi đồ nhẹ nh&agrave;ng đ&acirc;m một hồi người kia đầu, n&agrave;y bồng bềnh ở b&ecirc;n trong nước đầu người hơi c&oacute; ch&uacute;t lay động, trong nước c&oacute; s&oacute;ng gợn nhộn nhạo l&ecirc;n.<br />\r\n<br />\r\nThi thể n&agrave;y từ thang lầu nơi kh&uacute;c quanh hiển lộ ra một c&aacute;i đầu, bởi v&igrave; mực nước nguy&ecirc;n nh&acirc;n, thi thể bị ph&iacute;a tr&ecirc;n v&aacute;ch tường ngăn trở, thẻ ở nơi đ&oacute;, sở dĩ kh&ocirc;ng c&oacute; bồng bềnh lại đ&acirc;y.<br />\r\n<br />\r\nT&ocirc; L&ecirc; d&ugrave;ng s&agrave;o phơi đồ liền với k&iacute;ch động mấy lần, bởi v&igrave; nước sức nổi t&aacute;c dụng, hắn kh&ocirc;ng cần sử dụng sức lớn bao nhi&ecirc;u, chỉ l&agrave; chọn mấy lần, thi thể n&agrave;y cũng chậm chậm tung bay lại đ&acirc;y.<br />\r\n<br />\r\nNh&igrave;n thấy trong nước thật sự c&oacute; một bộ thi thể bồng bềnh lại đ&acirc;y, T&ocirc; L&ecirc; h&iacute;t một hơi thật s&acirc;u, vội v&agrave;ng l&ugrave;i về sau, theo cầu thang một lần nữa l&ugrave;i tới ph&iacute;a tr&ecirc;n.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nTuy rằng r&otilde; r&agrave;ng đ&acirc;y chỉ l&agrave; một bộ thi thể, trong l&ograve;ng hắn vẫn tu&ocirc;n ra từng trận v&ocirc; ph&aacute;p ức chế h&agrave;n &yacute;, đồng thời trong l&ograve;ng cũng c&oacute; một tia hiếu kỳ, thi thể n&agrave;y đến c&ugrave;ng l&agrave; ai, c&oacute; thể hay kh&ocirc;ng l&agrave; ch&iacute;nh m&igrave;nh người quen biết?<br />\r\n<br />\r\nĐương nhi&ecirc;n, lớn nhất độ khả thi ch&iacute;nh l&agrave; ở tại tầng hai mươi ch&iacute;n c&aacute;c gia đ&igrave;nh, rốt cuộc c&aacute;i kh&aacute;c tầng c&aacute;c gia đ&igrave;nh coi như bị nước chết đuối, thi thể bồng bềnh đến tầng n&agrave;y độ khả thi xa kh&ocirc;ng c&oacute; tầng hai mươi ch&iacute;n lớn như vậy.<br />\r\n<br />\r\nĐem s&agrave;o phơi đồ giao được tay tr&aacute;i, T&ocirc; L&ecirc; lần thứ hai mở ra đ&egrave;n pin cầm tay, nghĩ muốn cẩn thận quan s&aacute;t thi thể n&agrave;y.<br />\r\n<br />\r\nĐ&egrave;n pin cầm tay tia s&aacute;ng chiếu rọi đến bộ n&agrave;y trong nước x&aacute;c chết tr&ocirc;i, T&ocirc; L&ecirc; giật m&igrave;nh r&ugrave;ng m&igrave;nh một c&aacute;i, trong mắt lộ ra một ch&uacute;t sợ h&atilde;i.<br />\r\n<br />\r\nCó th&ecirc;̉ l&agrave; thời gian d&agrave;i ng&acirc;m ở trong nước nguy&ecirc;n nh&acirc;n, bộ n&agrave;y x&aacute;c chết tr&ocirc;i c&oacute; vẻ b&eacute;o phệ mập mạp, T&ocirc; L&ecirc; căn bản kh&ocirc;ng ph&acirc;n biệt ra đến đ&uacute;ng mới l&agrave; ai, chỉ c&oacute; thể th&ocirc;ng qua n&oacute; tung bay t&oacute;c d&agrave;i nh&igrave;n ra đ&acirc;y l&agrave; một nữ nh&acirc;n.<br />\r\n<br />\r\nM&agrave; lệnh T&ocirc; L&ecirc; hoảng sợ nhưng l&agrave; bộ n&agrave;y nữ t&iacute;nh thi thể cũng kh&ocirc;ng hoàn chỉnh, n&oacute; sưng ph&ugrave; thi thể mặt ngo&agrave;i, m&aacute;u thịt be b&eacute;t, bắp đ&ugrave;i c&ugrave;ng ngực bụng bộ đều c&oacute; um t&ugrave;m bạch cốt lộ ra, đ&acirc;y l&agrave; bị gặm nhấm dấu hiệu, xem ra l&agrave;m người sởn cả t&oacute;c g&aacute;y.<br />\r\n<br />\r\nT&ocirc; L&ecirc; sống đến hai mươi bảy tuổi, cũng từ trước tới nay chưa từng gặp qua kinh khủng như thế m&aacute;u tanh t&igrave;nh cảnh, trong l&ograve;ng run l&ecirc;n, đột nhi&ecirc;n hướng sau ng&atilde; xuống hai bước, trước mắt n&agrave;y xem ra c&oacute; ch&uacute;t vẩn đục trong nước, trở n&ecirc;n &acirc;m u quỷ dị l&ecirc;n.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; bị m&oacute;n đồ g&igrave; gặm nhấm, lẽ n&agrave;o l&agrave; bị trong nước c&aacute; hoặc c&aacute;i kh&aacute;c động vật g&igrave;. . .&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; kh&ocirc;ng d&aacute;m lại nghĩ s&acirc;u, t&ecirc; cả da đầu, trong l&ograve;ng đột nhi&ecirc;n tu&ocirc;n ra m&atilde;nh liệt bất an, để hắn kh&ocirc;ng d&aacute;m lại ở lại chỗ n&agrave;y nh&igrave;n kỹ bộ n&agrave;y bị gặm nhấm non nửa thi thể, m&agrave; l&agrave; mang theo s&agrave;o phơi đồ cấp tốc lui về h&agrave;nh lang, sau đ&oacute; đ&oacute;ng chặt lại n&agrave;y đi về giữa cầu thang cửa, chỉ cảm thấy tr&aacute;i tim đập đ&ecirc;́n v&ocirc; c&ugrave;ng kịch liệt.</p>', '2021-12-01 01:14:19', 7, 1),
(268, 71, 'Vết Chân', '4', '<p>Một lần nữa v&ecirc;̀ đ&ecirc;́n nhà, T&ocirc; L&ecirc; thả xuống s&agrave;o phơi đồ, trong đầu tất cả đều l&agrave; b&ocirc;̣ kia bị gặm nhấm đ&ecirc;́n m&aacute;u thịt be b&eacute;t mập mạp nữ thi, trong đầu rất hỗn loạn, một l&aacute;t mới một lần nữa tỉnh t&aacute;o lại.<br />\r\n<br />\r\n&quot;Hiện tại ta đến c&ugrave;ng l&agrave; n&ecirc;n lưu thủ tại chỗ n&agrave;y, chờ cứu viện, vẫn l&agrave; nghĩ biện ph&aacute;p rời đi nơi n&agrave;y, đi t&igrave;m đường ra, hay hoặc l&agrave; trong th&agrave;nh phố n&agrave;y c&ograve;n c&oacute; tương tự ta như vậy người may mắn c&ograve;n sống s&oacute;t?&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; cởi mới vừa tiến v&agrave;o trong nước m&agrave; bị ch&igrave;m ướt giầy,<br />\r\n<br />\r\nĐổi một đ&ocirc;i sạch sẽ giầy, đi tới s&acirc;n thượng b&ecirc;n cửa sổ, đang trầm tư.<br />\r\n<br />\r\nNh&igrave;n ph&iacute;a b&ecirc;n ngo&agrave;i cửa sổ tr&ecirc;n mặt nước, phương xa c&ograve;n c&oacute; linh tinh kiến tr&uacute;c cao tầng b&ocirc;́c l&ecirc;n mặt nước, những n&agrave;y c&oacute; thể b&ocirc;́c l&ecirc;n mặt nước tất cả đều l&agrave; ba mươi tầng c&ugrave;ng ba mươi tầng trở l&ecirc;n kiến tr&uacute;c.<br />\r\n<br />\r\nNh&igrave;n những n&agrave;y b&ocirc;́c l&ecirc;n mặt nước kiến tr&uacute;c, T&ocirc; L&ecirc; liền kh&ocirc;ng nhịn được sẽ nghĩ những kiến tr&uacute;c n&agrave;y b&ecirc;n trong, sẽ có hay kh&ocirc;ng có giống như ch&iacute;nh m&igrave;nh người may mắn c&ograve;n sống s&oacute;t? Nếu như c&oacute;, t&igrave;m tới bọn họ, d&ugrave; sao cũng hơn c&ocirc; đơn một người tốt.<br />\r\n<br />\r\nĐương nhi&ecirc;n, n&agrave;y tr&ecirc;n đường cũng tồn tại kh&ocirc;ng biết nguy hiểm, vừa mới b&ocirc;̣ kia bị gặm nhấm thi thể cho T&ocirc; L&ecirc; lưu lại &aacute;m ảnh trong l&ograve;ng, lu&ocirc;n cảm thấy c&oacute; ch&uacute;t bất an.<br />\r\n<br />\r\n&quot;Hay hoặc l&agrave; tạm thời ở lại chờ cứu viện, có th&ecirc;̉ chẳng mấy chốc sẽ c&oacute; đội cứu viện xuất hiện cũng nói kh&ocirc;ng chắc.&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; duy nhất cảm gi&aacute;c được vui mừng ch&iacute;nh l&agrave; m&igrave;nh bởi v&igrave; một người ở lại, để cho tiện bớt việc, trong nh&agrave; bị kh&ocirc;ng &iacute;t m&igrave; ăn liền, c&ograve;n mua một &iacute;t b&aacute;nh b&iacute;ch quy b&aacute;nh m&igrave; loại h&igrave;nh đảm nhiệm bữa s&aacute;ng, tất cả đều chất đống ở trong tủ lạnh.<br />\r\n<br />\r\nChỉ ch&iacute;nh m&igrave;nh một người, đồ ăn phương diện ki&ecirc;n tr&igrave; c&aacute;i bốn, năm ng&agrave;y vẫn l&agrave; c&oacute; thể.<br />\r\n<br />\r\nT&ocirc; L&ecirc; suy nghĩ một l&aacute;t sau, quyết định l&agrave;m hai tay chuẩn bị.<br />\r\n<br />\r\nMột l&agrave; tạm thời lưu thủ nơi n&agrave;y, hi vọng sẽ c&oacute; khả năng chuyển biến tốt hoặc l&agrave; c&oacute; nh&acirc;n vi&ecirc;n cứu viện xuất hiện. Hai l&agrave; động thủ chế t&aacute;c th&ocirc; sơ b&egrave; gỗ, nếu như mấy ng&agrave;y nay vẫn kh&ocirc;ng c&oacute; nh&acirc;n vi&ecirc;n cứu viện xuất hiện, vậy chỉ c&oacute; thể thừa b&egrave; gỗ mạo hiểm rời đi nơi n&agrave;y, chủ động t&igrave;m kiếm c&aacute;i kh&aacute;c đường sống.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nL&agrave;m quyết định, T&ocirc; L&ecirc; trong l&ograve;ng trở n&ecirc;n ung dung một &iacute;t, trước ti&ecirc;n từ trong tủ lạnh lấy ra một t&uacute;i b&aacute;nh b&iacute;ch quy x&eacute; ra, vừa ăn vừa liền bắt đầu bắt đầu bận t&uacute;i bụi, duy nhất cảm gi&aacute;c được vui mừng ch&iacute;nh l&agrave; nước tạm thời vẫn kh&ocirc;ng c&oacute; ngừng, hắn lấy ra ấm nước chứa đầy nước, sau đ&oacute; mở ra bếp nấu nước.<br />\r\n<br />\r\nHắn sử dụng vẫn l&agrave; kiểu cũ đ&oacute;ng chai thiết bị h&oacute;a lỏng, bất qu&aacute; cực nhỏ khai hỏa l&agrave;m cơm, th&aacute;ng n&agrave;y tổng liền mở ra một lần hỏa, vẫn l&agrave; Vương Lam c&ugrave;ng mấy c&aacute;i bằng hữu đến gia đ&igrave;nh hắn li&ecirc;n hoan, hắn một người thời điểm hầu như chưa từng d&ugrave;ng qua.<br />\r\n<br />\r\nM&agrave; hiện tại hắn tắc muốn cảm tạ c&ograve;n c&oacute; n&agrave;y b&igrave;nh ho&aacute; lỏng kh&iacute;, bằng kh&ocirc;ng hiện tại nghĩ uống miệng nước n&oacute;ng cũng kh&oacute; khăn rồi.<br />\r\n<br />\r\n&quot;Nhiều lắm đốt ch&uacute;t nước bị, tuy rằng hiện tại nước c&ograve;n kh&ocirc;ng ngừng lại, nhưng nếu bị c&uacute;p điện, chỉ sợ n&agrave;y nước cũng bất cứ l&uacute;c n&agrave;o c&oacute; thể sẽ ngừng.&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; tuy rằng kh&ocirc;ng biết nguy&ecirc;n nh&acirc;n cụ thể, có th&ecirc;̉ l&agrave; đường d&acirc;y kh&ocirc;ng giống duy&ecirc;n cớ, tuy rằng ngừng điện, nhưng tạm thời nước vẫn kh&ocirc;ng c&oacute; ngừng.<br />\r\n<br />\r\nTrừ bỏ chuẩn bị nước n&oacute;ng ở ngo&agrave;i, T&ocirc; L&ecirc; c&ograve;n ở nh&agrave; t&igrave;m kiếm khắp nơi c&aacute;c loại c&ocirc;ng cụ, rất nhanh sẽ t&igrave;m tới một &iacute;t đinh sắt, hai c&aacute;i tua v&iacute;t, b&agrave;n &ecirc;-t&ocirc;, c&aacute;i k&eacute;o, c&aacute;i bật lửa, c&ograve;n một c&aacute;i c&acirc;y b&uacute;a, thuận tiện đem đặt ở trong ph&ograve;ng bếp dao phay, dao gọt hoa quả đều thu thập lại, đồng thời chất đống ở ph&ograve;ng kh&aacute;ch tr&ecirc;n khay tr&agrave;, bất cứ l&uacute;c n&agrave;o dự bị.<br />\r\n<br />\r\nĂn xong b&aacute;nh b&iacute;ch quy, uống ch&uacute;t nước, lấp đầy bụng, T&ocirc; L&ecirc; liền bắt đầu l&agrave;m việc, &yacute; tưởng của hắn l&agrave; gi&agrave;nh trước tr&ecirc;n tầng cao nhất, thả ra kh&oacute;i đặc, lấy hấp dẫn ngoại giới nh&acirc;n vi&ecirc;n cứu viện ch&uacute; &yacute;, đương nhi&ecirc;n, nếu như c&oacute; người cứu viện.<br />\r\n<br />\r\nSau ch&iacute;nh m&igrave;nh lại chế t&aacute;c th&ocirc; sơ b&egrave; gỗ chuẩn bị bất cứ t&igrave;nh huống n&agrave;o.<br />\r\n<br />\r\nHắn từ trong nh&agrave; t&igrave;m một &iacute;t quần &aacute;o cũ, bao qu&aacute;t một giường chăn b&ocirc;ng, một đ&ocirc;i d&eacute;p nhựa, c&ograve;n c&oacute; mấy c&aacute;i t&uacute;i ni l&ocirc;ng đều đồng thời g&oacute;i kỹ, sau đ&oacute; mang tới c&aacute;i bật lửa liền chuẩn bị ra cửa.<br />\r\n<br />\r\nHắn phải đem những thứ đồ n&agrave;y &ocirc;m l&ecirc;n lầu đỉnh, sau đ&oacute; ph&oacute;ng hỏa đem đốt, hy vọng c&oacute; thể chế tạo ra đại lượng kh&oacute;i đặc.<br />\r\n<br />\r\nNếu như thật sự c&oacute; đội cứu viện, nh&igrave;n tới đ&acirc;y b&ocirc;́c l&ecirc;n đại lượng người l&agrave;m chế tạo kh&oacute;i đặc, tất nhi&ecirc;n biết nơi n&agrave;y c&oacute; người may mắn c&ograve;n sống s&oacute;t, khi đ&oacute; hắn phải cứu.<br />\r\n<br />\r\nT&ocirc; L&ecirc; mở cửa, đang chuẩn bị &ocirc;m đồ vật ra cửa, đột nhi&ecirc;n lại ngừng lại, con mắt rơi xuống trước mặt tr&ecirc;n h&agrave;nh lang.<br />\r\n<br />\r\nHắn nh&igrave;n thấy ph&iacute;a trước tr&ecirc;n h&agrave;nh lang, xuất hiện ướt dầm dề vết ch&acirc;n, vết ch&acirc;n n&agrave;y từ cuối h&agrave;nh lang xuất hiện, một đường k&eacute;o d&agrave;i tới cặp tiểu phu th&ecirc; kia ở lại ph&ograve;ng ốc b&ecirc;n ngo&agrave;i.<br />\r\n<br />\r\nT&ocirc; L&ecirc; trong l&ograve;ng kinh ho&agrave;ng l&ecirc;n.<br />\r\n<br />\r\nNh&igrave;n vết ch&acirc;n ẩm ướt n&agrave;y, T&ocirc; L&ecirc; r&otilde; r&agrave;ng n&agrave;y cũng kh&ocirc;ng phải l&agrave; m&igrave;nh lưu lại vết ch&acirc;n, trước hắn ch&acirc;n d&iacute;nh nước lưu lại vết ch&acirc;n, cũng sớm đ&atilde; kh&ocirc;, m&agrave; trước mắt tr&ecirc;n h&agrave;nh lang n&agrave;y lưu lại ướt vết ch&acirc;n rất r&otilde; r&agrave;ng l&agrave; vừa mới c&oacute; người đi qua m&agrave; lưu lại, hơn nữa thời gian cũng kh&ocirc;ng l&acirc;u, bằng kh&ocirc;ng vết ch&acirc;n n&agrave;y rất nhanh sẽ kh&ocirc;.<br />\r\n<br />\r\n&quot;Chẳng lẽ n&oacute;i nơi n&agrave;y trừ ta ra c&ograve;n c&oacute; những người kh&aacute;c? Mới vừa từ cầu thang kia đi tới, tiến v&agrave;o đ&ocirc;i phu th&ecirc; kia ở lại gian nh&agrave;? Chỉ l&agrave; b&ecirc;n ngo&agrave;i cầu thang đều bị ch&igrave;m, người n&agrave;y l&agrave;m sao m&agrave; qua nổi đến?&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; nh&igrave;n kia kh&eacute;p hờ cửa, muốn x&ocirc;ng v&agrave;o đi xem r&otilde; ngọn ng&agrave;nh, nhưng s&acirc;u trong nội t&acirc;m lại mơ hồ c&oacute; loại hoảng sợ, lu&ocirc;n cảm thấy việc n&agrave;y c&oacute; ch&uacute;t kh&ocirc;ng n&oacute;i ra được qu&aacute;i lạ.<br />\r\n<br />\r\nHắn cũng kh&ocirc;ng c&oacute; t&ugrave;y tiện k&iacute;ch động, m&agrave; l&agrave; cấp tốc l&ugrave;i trở về nh&agrave;, thả xuống trong tay &ocirc;m chăn b&ocirc;ng c&ugrave;ng y vật, sau đ&oacute; một c&aacute;i cầm lấy đặt ở tr&ecirc;n khay tr&agrave; dao phay c&ugrave;ng c&acirc;y b&uacute;a kia.<br />\r\n<br />\r\nCầm dao phay c&ugrave;ng c&acirc;y b&uacute;a, c&oacute; ph&ograve;ng th&acirc;n vũ kh&iacute;, trong l&ograve;ng của hắn an t&acirc;m một ch&uacute;t, dũng kh&iacute; vi tr&aacute;ng, h&iacute;t một hơi thật s&acirc;u, để cho m&igrave;nh y&ecirc;n tĩnh một ch&uacute;t, lần thứ hai đi ra ngo&agrave;i, muốn đi xem r&otilde; ngọn ng&agrave;nh.<br />\r\n<br />\r\n&quot;Nếu như thật sự c&oacute; c&aacute;i kh&aacute;c người may mắn c&ograve;n sống s&oacute;t l&agrave; tốt rồi.&quot; T&ocirc; L&ecirc; y&ecirc;n lặng nghĩ.<br />\r\n<br />\r\nMột th&acirc;n một m&igrave;nh đối mặt c&aacute;c loại kh&ocirc;ng biết, kh&ocirc;ng chỉ c&ocirc; độc, c&ograve;n c&oacute; loại kia đối kh&ocirc;ng biết cảm gi&aacute;c sợ h&atilde;i, mỗi giờ mỗi khắc bao phủ ở trong l&ograve;ng hắn, để hắn cảm gi&aacute;c được hết sức kiềm chế, hắn rất sợ sệt tiếp tục tiếp tục như vậy, chỉ sợ ch&iacute;nh m&igrave;nh kh&ocirc;ng bao l&acirc;u nữa liền đem tan vỡ, hắn kh&aacute;t vọng nh&igrave;n thấy c&aacute;i kh&aacute;c người sống, đồng bạn, thậm ch&iacute; coi như l&agrave; con ch&oacute; cũng l&agrave; tốt đẹp.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nRất nhanh, T&ocirc; L&ecirc; liền đi tới ngo&agrave;i cửa, nh&igrave;n kh&eacute;p hờ cửa chống trộm, nh&igrave;n trước cửa vết ch&acirc;n ướt đẫm kia.<br />\r\n<br />\r\nRời đ&ecirc;́n gần rồi, T&ocirc; L&ecirc; ph&aacute;t hiện vết ch&acirc;n n&agrave;y hai b&ecirc;n, c&ograve;n c&oacute; đại lượng vệt nước, hiển nhi&ecirc;n, n&agrave;y lưu lại vết ch&acirc;n người, kh&ocirc;ng chỉ l&agrave; một đ&ocirc;i gi&agrave;y ướt, m&agrave; l&agrave; cả người đều ướt đẫm, sở dĩ đi tới thời điểm, kh&ocirc;ng chỉ chừa rơi xuống một h&agrave;ng ướt vết ch&acirc;n, khắp to&agrave;n th&acirc;n đều chảy xu&ocirc;́ng nước, tr&ecirc;n mặt đất lưu lại đại lượng vệt nước.<br />\r\n<br />\r\nBất qu&aacute; theo thời gian chuyển dời, những vệt nước n&agrave;y, ch&iacute;nh đang chầm chậm bốc hơi l&ecirc;n, T&ocirc; L&ecirc; giờ khắc này rời đ&ecirc;́n gần, mới ch&uacute; &yacute; tới rồi.<br />\r\n<br />\r\nT&ocirc; L&ecirc; nắm chặt trong tay c&acirc;y b&uacute;a c&ugrave;ng dao phay, cũng kh&ocirc;ng c&oacute; lập tức đẩy cửa đến xem, yết hầu giật giật, nuốt nước miếng, rốt cục ph&aacute;t ra &acirc;m thanh: &quot;C&oacute; ai kh&ocirc;ng?&quot;<br />\r\n<br />\r\nTrừ bỏ &acirc;m thanh của hắn, h&agrave;nh lang c&ugrave;ng trong ph&ograve;ng ốc n&agrave;y, ho&agrave;n to&agrave;n tĩnh mịch.<br />\r\n<br />\r\nT&ocirc; L&ecirc; dừng lại v&agrave;i gi&acirc;y, lại lần nữa hỏi một lần, đồng thời cẩn thận lắng nghe trong ph&ograve;ng ốc n&agrave;y động tĩnh.<br />\r\n<br />\r\nĐột nhi&ecirc;n, hắn nghe được b&ecirc;n trong truyền đến từng tiếng vang, n&agrave;y như l&agrave; c&aacute;i ghế bị di chuyển một ch&uacute;t ph&aacute;t ra &acirc;m thanh, ở loại n&agrave;y y&ecirc;n tĩnh địa phương, thanh &acirc;m n&agrave;y trở n&ecirc;n đặc biệt vang dội, nghe tới như l&agrave; một người ở xoay người b&ecirc;n trong trong l&uacute;c v&ocirc; t&igrave;nh đụng tới c&aacute;i ghế m&agrave; ph&aacute;t ra &acirc;m thanh.<br />\r\n<br />\r\n&quot;B&ecirc;n trong thật sự c&oacute; người? Chỉ l&agrave; tại sao kh&ocirc;ng trả lời ta?&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; rốt cục kh&ocirc;ng nhịn được, cầm c&acirc;y b&uacute;a tay phải vừa nhấc, liền muốn đem n&agrave;y kh&eacute;p hờ l&ecirc;n cửa đẩy ra.</p>', '2021-12-01 01:14:43', 5, 1),
(269, 71, 'Linh Nguyên', '5', '<p>Mới vừa đẩy ra một điểm, liền cảm gi&aacute;c cửa đụng tới một vật bị chặn lại r&ocirc;̀i, sau đ&oacute; hắn ngẩng đầu nh&igrave;n đến đẩy ra trong khe cửa lộ ra một tấm mập mạp m&agrave; trắng bệch mặt to, hơn nửa khu&ocirc;n mặt đều bị ướt đẫm m&aacute;i t&oacute;c d&agrave;i m&agrave;u đen che lại, chỉ lộ ra một c&aacute;i sưng ph&ugrave; mắt c&aacute; chết.<br />\r\n<br />\r\nCon mắt n&agrave;y, kh&ocirc;ng hề sinh kh&iacute;, hầu như kh&ocirc;ng nh&igrave;n thấy con ngươi, chỉ c&ograve;n lại tr&ograve;ng trắng mắt.<br />\r\n<br />\r\nN&agrave;y trắng bệch sưng ph&ugrave; mặt to quả thực dường như chỉ c&oacute; ở trong điện ảnh mới phải xuất hiện cương thi, c&agrave;ng quỷ dị khủng bố ch&iacute;nh l&agrave; mặt ngo&agrave;i c&ograve;n d&agrave;i ra một tầng mượt m&agrave; l&ocirc;ng trắng.<br />\r\n<br />\r\nChuyện xảy ra qu&aacute; đột nhi&ecirc;n, T&ocirc; L&ecirc; chưa từng nghĩ đến lại đột nhi&ecirc;n nh&igrave;n thấy quỷ dị như thế khủng bố mặt, chỉ sợ đến hồn phi ph&aacute;ch t&aacute;n, kh&ocirc;ng nhịn được ph&aacute;t ra một tiếng h&atilde;i cực r&iacute;t g&agrave;o, bản năng đ&atilde; nghĩ xoay người đ&agrave;o tẩu.<br />\r\n<br />\r\n&quot;Ầm&quot; một tiếng, m&ocirc;n kia bị đột nhi&ecirc;n k&eacute;o ra, vừa mới xoay người T&ocirc; L&ecirc; cảm gi&aacute;c bị ướt đẫm đồ vật nh&agrave;o tr&uacute;ng, n&agrave;y bổ một c&aacute;i kh&iacute; lực rất lớn, hắn kh&ocirc;ng đứng thẳng được, ng&atilde; nh&agrave;o một c&aacute;i tầng tầng ng&atilde; xuống đất, c&aacute;i tr&aacute;n va đến mặt đất, nhất thời mắt nổ đom đ&oacute;m, m&aacute;u tươi chảy r&ograve;ng, theo s&aacute;t liền cảm gi&aacute;c c&oacute; vật nặng đặt ở tr&ecirc;n người ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\nBước ngoặt sinh tử, T&ocirc; L&ecirc; kh&ocirc;ng lo được lại sợ h&atilde;i hoảng sợ, lập tức liều mạng gi&atilde;y dụa, tay ch&acirc;n đầu gối c&ugrave;ng phần eo đồng thời d&ugrave;ng sức, đột nhi&ecirc;n lật một c&aacute;i, liền đem ngăn chặn đồ vật của ch&iacute;nh m&igrave;nh xốc l&ecirc;n.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, hắn rốt cục nhìn rõ ràng rồi.<br />\r\n<br />\r\nN&agrave;y vừa mới đập ra đến ngăn chặn ch&iacute;nh m&igrave;nh dĩ nhi&ecirc;n ch&iacute;nh l&agrave; b&ocirc;̣ kia trước ng&acirc;m ở giữa cầu thang trong nước nữ thi.<br />\r\n<br />\r\nTấm kia bị ng&acirc;m nở trắng bệch mặt to, th&acirc;n thể mập mạp, ngực bụng c&ugrave;ng phần ch&acirc;n đại lượng huyết nhục bị gặm nhấm, lộ ra b&ecirc;n trong bạch cốt, thậm ch&iacute; đều c&oacute; thể mơ hồ nh&igrave;n thấy dạng sệt nội tạng, tr&ecirc;n người c&ugrave;ng t&oacute;c ướt đẫm đang kh&ocirc;ng ngừng lưu nước chảy.<br />\r\n<br />\r\nM&agrave; kinh khủng nhất kh&ocirc;ng thể nghi ngờ ch&iacute;nh l&agrave; nàng lộ ở b&ecirc;n ngo&agrave;i da thịt c&ugrave;ng tr&ecirc;n gương mặt, sinh ra một tầng trắng to&aacute;t nhung l&ocirc;ng, dường như trong truyền thuyết l&ocirc;ng trắng cương thi.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nKh&iacute; lực n&agrave;ng rất lớn, thế nhưng tay ch&acirc;n khớp xương lại c&oacute; vẻ hơi cứng ngắc, kh&ocirc;ng c&oacute; người sống như vậy linh hoạt, vừa mới bị T&ocirc; L&ecirc; lật tung ở một b&ecirc;n, liền đổ v&agrave;o b&ecirc;n cạnh hắn, duỗi ra một đ&ocirc;i mọc ra nhung l&ocirc;ng b&agrave;n tay, muốn b&ograve; l&ecirc;n bắt T&ocirc; L&ecirc;, chỉ l&agrave; động t&aacute;c c&oacute; vẻ so s&aacute;nh chậm chạp.<br />\r\n<br />\r\nT&ocirc; L&ecirc; giẫy giụa b&ograve; l&ecirc;n, trong tay dao phay c&ugrave;ng c&acirc;y b&uacute;a liền phủ đầu đập tới.<br />\r\n<br />\r\nLập tức m&aacute;u thịt tung to&eacute;, ở tại tr&ecirc;n mặt của T&ocirc; L&ecirc;.<br />\r\n<br />\r\nHắn kh&ocirc;ng nghĩ ra bộ n&agrave;y nữ thi v&igrave; sao lại h&agrave;nh động, thậm ch&iacute; c&ograve;n hội c&ocirc;ng k&iacute;ch ch&iacute;nh m&igrave;nh, duy nhất c&oacute; thể nghĩ đến ch&iacute;nh l&agrave; thi biến, bộ n&agrave;y nữ thi đ&atilde; biến th&agrave;nh cương thi một dạng qu&aacute;i vật.<br />\r\n<br />\r\nNữ thi n&agrave;y tuy rằng d&aacute;ng dấp khủng bố, kh&iacute; lực cũng kh&ocirc;ng nhỏ, nhưng động t&aacute;c cứng ngắc, cũng kh&ocirc;ng hiểu được n&eacute; tr&aacute;nh, rất nhanh sẽ bị T&ocirc; L&ecirc; dao phay chặt ở tr&ecirc;n mặt, chu&ocirc;i kia thiết chế c&acirc;y b&uacute;a cũng phủ đầu đ&acirc;̣p trúng n&oacute; n&atilde;o, lập tức vang l&ecirc;n &quot;Lộp bộp&quot; một tiếng xương sọ vỡ vụn vang l&ecirc;n gi&ograve;n gi&atilde;.<br />\r\n<br />\r\nLần n&agrave;y T&ocirc; L&ecirc; hầu như l&agrave; d&ugrave;ng hết kh&iacute; lực, hắn đang đứng ở hết sức hoảng sợ c&ugrave;ng trong hốt hoảng, b&ugrave;ng nổ ra trước nay chưa từng c&oacute; kh&iacute; lực, một b&uacute;a n&agrave;y trực tiếp liền đem nữ thi n&agrave;y đầu đập đ&ecirc;́n l&uacute;n xuống dưới, li&ecirc;̀n b&ecirc;n trong &oacute;c đều bắn đi ra.<br />\r\n<br />\r\nT&ocirc; L&ecirc; giống như đi&ecirc;n cuồng vậy kh&ocirc;ng ngừng vung vẩy dao phay c&ugrave;ng c&acirc;y b&uacute;a, chờ hắn thở hồng hộc ngừng tay thời điểm, nữ thi n&agrave;y một tấm sưng ph&ugrave; mặt to ho&agrave;n to&agrave;n thay đổi h&igrave;nh, quang tr&ecirc;n mặt liền bị chặt bảy, tám đao, mỗi một đao đều s&acirc;u sắc chặt ti&ecirc;́n v&agrave;o bộ mặt xương tủy, m&agrave; c&acirc;y b&uacute;a c&agrave;ng là d&iacute;nh đầy huyết nhục, nửa c&aacute;i đầu đều bị đập kh&ocirc;ng c&ograve;n.<br />\r\n<br />\r\nNh&igrave;n bộ n&agrave;y ho&agrave;n to&agrave;n thay đổi nữ thi chậm r&atilde;i mới ng&atilde; xuống, kh&ocirc;ng động đậy nữa, T&ocirc; L&ecirc; l&uacute;c n&agrave;y mới thở ra một khẩu đại kh&iacute;.<br />\r\n<br />\r\nN&oacute;i cũng kỳ qu&aacute;i, theo nữ thi n&agrave;y mới ng&atilde; xuống, n&oacute; mặt ngo&agrave;i th&acirc;n thể một tầng kia mượt m&agrave; l&ocirc;ng trắng, dĩ nhi&ecirc;n đang nhanh ch&oacute;ng thu lại biến mất, giống một lần nữa co r&uacute;t lại ti&ecirc;́n v&agrave;o trong l&agrave;n da của n&agrave;ng, theo s&aacute;t T&ocirc; L&ecirc; liền nh&igrave;n thấy nàng kia bị gặm nhấm rơi hơn nửa da thịt trong lồng ngực, dĩ nhi&ecirc;n c&oacute; một đo&agrave;n to bằng ng&oacute;n c&aacute;i quả cầu m&agrave;u trắng x&ocirc;ng ra, quả cầu m&agrave;u trắng n&agrave;y mặt ngo&agrave;i l&ocirc;ng x&ugrave;, dường như do v&ocirc; số m&agrave;u trắng nhung l&ocirc;ng tụ hợp lại một nơi h&igrave;nh th&agrave;nh.<br />\r\n<br />\r\nĐo&agrave;n n&agrave;y m&agrave;u trắng nhung l&ocirc;ng từ nữ thi lồng ngực b&ocirc;́c l&ecirc;n, c&agrave;ng giống nắm giữ tự chủ &yacute; thức, đột nhi&ecirc;n bật l&ecirc;n.<br />\r\n<br />\r\nT&ocirc; L&ecirc; chấn động trong l&ograve;ng, chẳng lẽ n&oacute;i nữ thi n&agrave;y lại đột nhi&ecirc;n thi biến, c&oacute; thể h&agrave;nh động, càng dài ra một tầng mượt m&agrave; l&ocirc;ng trắng, nguy&ecirc;n nh&acirc;n thực sự ch&iacute;nh l&agrave; trước mắt đo&agrave;n n&agrave;y m&agrave;u trắng nhung l&ocirc;ng giở tr&ograve; quỷ? Ch&acirc;n ch&iacute;nh thủ phạm l&agrave; n&oacute;?<br />\r\n<br />\r\nNh&igrave;n đo&agrave;n n&agrave;y m&agrave;u trắng nhung l&ocirc;ng bật l&ecirc;n, T&ocirc; L&ecirc; liền cầm dao phay b&igrave;nh vỗ xuống, như muốn đập chết.<br />\r\n<br />\r\nGiờ khắc này hắn đ&atilde; ho&agrave;i nghi đo&agrave;n n&agrave;y m&agrave;u trắng nhung l&ocirc;ng khả năng l&agrave; một loại n&agrave;o đ&oacute; kh&ocirc;ng biết c&ocirc;n tr&ugrave;ng loại sinh vật, vừa mới v&ocirc; c&ugrave;ng c&oacute; khả năng l&agrave; k&yacute; sinh ở trong bộ nữ thi n&agrave;y.<br />\r\n<br />\r\n&quot;Đ&ugrave;ng&quot; một tiếng, dao phay b&igrave;nh đ&acirc;̣p trúng nữ thi lồng ngực, nhưng đoàn kia m&agrave;u trắng nhung l&ocirc;ng bật l&ecirc;n tốc độ vượt xa sự tưởng tượng của hắn, dao phay thất bại, trước mắt bạch quang l&oacute;e l&ecirc;n, đo&agrave;n n&agrave;y m&agrave;u trắng nhung l&ocirc;ng liền nh&agrave;o tr&uacute;ng khu&ocirc;n mặt của hắn.<br />\r\n<br />\r\nT&ocirc; L&ecirc; cảm gi&aacute;c c&aacute;i tr&aacute;n một ngứa, trong l&ograve;ng hoảng hốt, biết kh&ocirc;ng ổn, phản xạ c&oacute; điều kiện liền d&ugrave;ng tay vung l&ecirc;n mặt.<br />\r\n<br />\r\nVung c&aacute;i kh&ocirc;ng, T&ocirc; L&ecirc; vội v&agrave;ng d&ugrave;ng tay đi tr&ecirc;n mặt xoa xoa, lại ph&aacute;t hiện kh&ocirc;ng c&oacute; thứ g&igrave;, lại vội v&agrave;ng đứng dậy t&igrave;m kiếm khắp nơi, run run quần &aacute;o, vừa mới đoàn kia m&agrave;u trắng nhung l&ocirc;ng, dĩ nhi&ecirc;n biến mất kh&ocirc;ng c&ograve;n tăm t&iacute;ch.<br />\r\n<br />\r\n&quot;Xảy ra chuyện g&igrave;? L&agrave;m sao lại đột nhi&ecirc;n biến mất rồi?&quot; T&ocirc; L&ecirc; ngạc nhi&ecirc;n kh&ocirc;ng t&ecirc;n b&ecirc;n trong, đột nhi&ecirc;n cảm gi&aacute;c đầu &oacute;c tu&ocirc;n ra một đạo tin tức, dường như bỗng dưng liền ở trong đầu hắn cắm v&agrave;o một đoạn ho&agrave;n to&agrave;n kh&ocirc;ng thuộc về m&igrave;nh k&yacute; ức.<br />\r\n<br />\r\n&quot;Linh nguy&ecirc;n: 1/5&quot; .<br />\r\n<br />\r\nT&ocirc; L&ecirc; kh&ocirc;ng nhịn được che đầu của ch&iacute;nh m&igrave;nh, đầy mặt kinh h&atilde;i: &quot;Linh nguy&ecirc;n 1/5? Đ&acirc;y l&agrave; &yacute; g&igrave;? Linh nguy&ecirc;n l&agrave; m&oacute;n đồ g&igrave;?&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nTheo s&aacute;t, hắn đột nhi&ecirc;n cảm gi&aacute;c c&aacute;i tr&aacute;n b&ecirc;n trong, tu&ocirc;n ra một luồng cực n&oacute;ng, cỗ n&agrave;y cứu nhiệt dường như từng đạo từng đạo d&ograve;ng nước ấm, đột nhi&ecirc;n hướng về th&acirc;n thể của hắn bốn phương t&aacute;m hướng k&eacute;o d&agrave;i ra đi, thẳng tới ng&oacute;n tay ngọn t&oacute;c, rất nhanh to&agrave;n th&acirc;n đều trở n&ecirc;n n&oacute;ng rực l&ecirc;n.<br />\r\n<br />\r\nBất qu&aacute; n&agrave;y cực n&oacute;ng tới cũng nhanh, biến mất cũng nhanh, trước sau bất qu&aacute; v&agrave;i gi&acirc;y, lại đột nhi&ecirc;n biến mất rồi.<br />\r\n<br />\r\nTheo cỗ n&agrave;y thần b&iacute; m&agrave; đến cực n&oacute;ng sau khi biến mất, T&ocirc; L&ecirc; cảm gi&aacute;c th&acirc;n thể trở n&ecirc;n c&oacute; g&igrave; đ&oacute; kh&ocirc;ng đ&uacute;ng, đầu ti&ecirc;n l&agrave; c&aacute;i tr&aacute;n vừa mới đụng phải đầu ph&aacute; huyết nhục, ch&iacute;nh từng trận đau r&aacute;t đau, hiện tại n&agrave;y đau đớn dĩ nhi&ecirc;n biến mất rồi, chỗ vết thương trở n&ecirc;n m&aacute;t mẻ l&ecirc;n.<br />\r\n<br />\r\nKh&ocirc;ng chỉ như n&agrave;y, hắn vừa mới cầm dao phay c&ugrave;ng c&acirc;y b&uacute;a đi&ecirc;n cuồng ch&eacute;m đập kia nữ thi, d&ugrave;ng sức qu&aacute; mạnh, cảm gi&aacute;c hai tay đau nhức, cả người kh&ocirc;ng c&ograve;n ch&uacute;t sức lực n&agrave;o mềm nhũn, nhưng hiện tại hai tay đau nhức c&ugrave;ng cả người mềm nhũn đều biến mất, một lần nữa trở n&ecirc;n tinh lực d&ocirc;̀i dào, sinh long hoạt hổ.<br />\r\n<br />\r\nTất cả những thứ n&agrave;y biến h&oacute;a, đều đến được qu&aacute; mức đột nhi&ecirc;n, để T&ocirc; L&ecirc; trong khoảng thời gian ngắn che ở tại chỗ, bất qu&aacute; rất nhanh hắn liền h&iacute;t một hơi thật s&acirc;u để cho m&igrave;nh tỉnh t&aacute;o lại, cảm ứng trong đầu bỗng dưng th&ecirc;m ra đến đạo kia tin tức, trong l&ograve;ng mơ hồ c&oacute; ch&uacute;t r&otilde; r&agrave;ng.<br />\r\n<br />\r\nĐoàn kia m&agrave;u trắng nhung l&ocirc;ng h&igrave;nh d&aacute;ng quả cầu, cũng kh&ocirc;ng phải l&agrave; thần b&iacute; biến mất rồi, m&agrave; l&agrave; ở nh&agrave;o tr&uacute;ng tr&aacute;n m&igrave;nh chớp mắt liền dung hợp ti&ecirc;́n v&agrave;o trong th&acirc;n thể của m&igrave;nh.<br />\r\n<br />\r\n&quot;Trong đ&acirc;̀u của ta đột nhi&ecirc;n th&ecirc;m ra đoạn tin tức n&agrave;y, hẳn l&agrave; ch&iacute;nh l&agrave; vật n&agrave;y dung hợp tiến th&acirc;n thể ta m&agrave; mang đến tin tức, linh nguy&ecirc;n: 1/5, chẳng lẽ n&oacute;i, đo&agrave;n n&agrave;y l&ocirc;ng x&ugrave; quả cầu. . . Ch&iacute;nh l&agrave; n&agrave;y c&aacute;i g&igrave; linh nguy&ecirc;n? N&agrave;y linh nguy&ecirc;n tựa hồ c&oacute; thể thay đổi thể chất của ta, để ta c&aacute;i tr&aacute;n vết thương kh&ocirc;ng c&ograve;n đau đớn, c&ograve;n c&oacute; thể ti&ecirc;u trừ mệt nhọc, thậm ch&iacute; thật giống sức mạnh của ta đều lớn rồi một điểm.&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; cầm c&acirc;y b&uacute;a, nhẹ nh&agrave;ng vung vẩy một hồi, cảm gi&aacute;c vung vẩy thời điểm biến ung dung một &iacute;t, n&agrave;y kh&ocirc;ng phải ảo gi&aacute;c, m&agrave; l&agrave; ch&acirc;n thực, sức mạnh của ch&iacute;nh m&igrave;nh, x&aacute;c thực l&agrave; lớn l&ecirc;n một &iacute;t.</p>', '2021-12-01 01:15:05', 5, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(270, 71, 'Ngừng Nước', '6', '<p>Đang x&aacute;c định tất cả những thứ n&agrave;y sau, T&ocirc; L&ecirc; bất ngờ ph&aacute;t hiện ch&iacute;nh m&igrave;nh dĩ nhi&ecirc;n trở n&ecirc;n so với trong tưởng tượng của m&igrave;nh phải tỉnh t&aacute;o nhiều lắm.<br />\r\n<br />\r\nNh&igrave;n trước mặt tr&ecirc;n đất ng&atilde; chổng v&oacute; bộ n&agrave;y sưng ph&ugrave; m&agrave; xấu x&iacute; kh&ocirc;ng thể tả nữ thi, trong l&ograve;ng hắn như c&oacute; ngộ ra.<br />\r\n<br />\r\nThế giới n&agrave;y đ&atilde; trở n&ecirc;n ho&agrave;n to&agrave;n kh&aacute;c nhau, kh&ocirc;ng chỉ l&agrave; c&oacute; ngập trời hồng thủy c&oacute; thể nhấn ch&igrave;m cả t&ograve;a th&agrave;nh thị, c&ograve;n c&oacute; thể sẽ xuất hiện thi biến, thậm ch&iacute; l&agrave; loại n&agrave;y thần b&iacute; &quot;Linh nguy&ecirc;n&quot; .<br />\r\n<br />\r\n&quot;Tất cả mọi thứ đều thay đổi, n&agrave;y vẫn l&agrave; ta trước thế giới đang ở sao? Hiện tại đ&atilde; v&ocirc; ph&aacute;p d&ugrave;ng lẽ thường giải th&iacute;ch ph&aacute;t sinh trước mắt tất cả rồi.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng biết nữ thi n&agrave;y thi biến chỉ l&agrave; ngẫu nhi&ecirc;n một ca, vẫn l&agrave; n&oacute;i hết thảy thi thể đều sẽ xuất hiện loại n&agrave;y thi biến, nếu như l&agrave; những thi thể n&agrave;y đều sẽ thi biến, vậy t&ograve;a th&agrave;nh thị n&agrave;y, trở n&ecirc;n v&ocirc; c&ugrave;ng nguy hiểm, trận n&agrave;y đại hồng thủy cũng kh&ocirc;ng biết chết đuối bao nhi&ecirc;u người, th&agrave;nh phố n&agrave;y. . . Nhưng là ở lại m&acirc;́y triệu nh&acirc;n khẩu. . .&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; nghĩ đến con số n&agrave;y, nhất thời r&ugrave;ng m&igrave;nh một c&aacute;i, mơ hồ cảm gi&aacute;c được sởn cả t&oacute;c g&aacute;y.<br />\r\n<br />\r\nHắn kh&ocirc;ng biết trong th&agrave;nh phố lớn c&oacute; mấy triệu người n&agrave;y, c&oacute; bao nhi&ecirc;u người sống s&oacute;t tho&aacute;t đi, lại c&oacute; bao nhi&ecirc;u người bị nhấn ch&igrave;m đ&atilde; biến th&agrave;nh thi thể.<br />\r\n<br />\r\n&quot;Nơi n&agrave;y xem ra rất kh&ocirc;ng an to&agrave;n, nhất định phải sớm một ch&uacute;t t&igrave;m tới đội cứu viện.&quot; T&ocirc; L&ecirc; nh&acirc;́c theo c&acirc;y b&uacute;a c&ugrave;ng dao phay, vừa liếc nh&igrave;n ng&atilde; tr&ecirc;n mặt đất nữ thi, để hắn cảm gi&aacute;c được vui mừng ch&iacute;nh l&agrave; nữ thi n&agrave;y tuy rằng kh&iacute; lực rất lớn, nhưng động t&aacute;c kh&aacute; cứng ngắc, bằng kh&ocirc;ng hậu quả kh&oacute; m&agrave; lường được.<br />\r\n<br />\r\nT&ocirc; L&ecirc; &yacute; thức được t&igrave;nh huống xa so với ch&iacute;nh m&igrave;nh tưởng tượng c&ograve;n muốn c&agrave;ng đ&aacute;ng sợ, hắn rất nhanh sẽ một lần nữa trở lại trong nh&agrave;, đ&oacute;ng kỹ cửa chống trộm, cầm dao phay c&ugrave;ng c&acirc;y b&uacute;a đi v&agrave;o ph&ograve;ng rửa tay, đối với tấm gương nh&igrave;n một ch&uacute;t c&aacute;i tr&aacute;n vết thương.<br />\r\n<br />\r\nPh&aacute;t hiện vết thương c&oacute; ch&uacute;t sưng đỏ, cũng c&ograve;n tốt chỉ l&agrave; ph&aacute; một điểm da, kh&ocirc;ng nghi&ecirc;m trọng lắm, chỗ vết thương hơi c&oacute; m&aacute;t mẻ cảm gi&aacute;c, chỉ trong thời gian rất ngắn, n&agrave;y r&aacute;ch da vết thương đ&atilde; thu hồi lỗ hổng, thương thế kh&ocirc;i phục tốc độ c&oacute; ch&uacute;t kinh người.<br />\r\n<br />\r\nT&ocirc; L&ecirc; cầm khăn mặt đem vết thương vừa mới chảy xu&ocirc;i đến tr&ecirc;n mặt đ&atilde; sắp muốn đọng lại m&aacute;u tươi cẩn thận lau ch&ugrave;i rơi, lại đem dao phay c&ugrave;ng c&acirc;y b&uacute;a rửa sạch, lại ph&aacute;t hiện v&ograve;i nước nước c&agrave;ng ng&agrave;y c&agrave;ng nhỏ, cuối c&ugrave;ng chỉ c&ograve;n lại giọt nước đi xuống giọt chảy.<br />\r\n<br />\r\n&quot;Thật ngừng nước rồi.&quot; T&ocirc; L&ecirc; thở d&agrave;i, ngược lại cũng kh&ocirc;ng ngo&agrave;i &yacute; muốn, cũng c&ograve;n tốt hắn sớm đốt kh&ocirc;ng &iacute;t nước s&ocirc;i, chứa đầy mấy c&aacute;i b&igrave;nh nước, ngo&agrave;i ra c&ograve;n thả đầy một lớn một nhỏ hai bồn nước, tạm thời đảo kh&ocirc;ng cần lo lắng khuyết nước.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nCho tới thế giới b&ecirc;n ngo&agrave;i tuy rằng tất cả đều l&agrave; nước, nhưng cũng kh&ocirc;ng ai biết c&oacute; sạch sẽ hay kh&ocirc;ng, c&oacute; hay kh&ocirc;ng k&yacute; sinh tr&ugrave;ng c&ugrave;ng bệnh khuẩn, T&ocirc; L&ecirc; kh&ocirc;ng d&aacute;m sử dụng.<br />\r\n<br />\r\nCầm rửa sạch sẽ đao c&ugrave;ng c&acirc;y b&uacute;a, T&ocirc; L&ecirc; &ocirc;m kia một b&oacute; chăn b&ocirc;ng y vật đi ra ngo&agrave;i.<br />\r\n<br />\r\nĐến l&acirc;u đỉnh, T&ocirc; L&ecirc; đem chăn b&ocirc;ng thả ở tr&ecirc;n mặt đất, ph&oacute;ng tầm mắt nh&igrave;n chung quanh, ở m&aacute;i nh&agrave; n&agrave;y kh&ocirc;ng c&oacute; tầm mắt c&aacute;ch trở, T&ocirc; L&ecirc; ph&oacute;ng tầm mắt c&oacute; thể nh&igrave;n thấy ch&iacute;nh l&agrave; rộng lớn v&ocirc; bi&ecirc;n mặt nước, n&agrave;y mặt nước rộng r&atilde;i đ&ecirc;́n dường như hải dương, căn bản kh&ocirc;ng nh&igrave;n thấy biến giới, dường như to&agrave;n bộ thế giới n&agrave;y, đều đ&atilde; biến th&agrave;nh nước thế giới.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y cần l&agrave; khủng bố bao nhi&ecirc;u đại hồng thủy, mới c&oacute; thể tạo th&agrave;nh hậu quả như thế.&quot; T&ocirc; L&ecirc; kh&ocirc;ng nhịn được nắm chặt hai tay.<br />\r\n<br />\r\nLại viễn vọng c&aacute;ch m&igrave;nh nơi n&agrave;y gần nhất kh&aacute;c một tr&agrave;ng b&ocirc;́c l&ecirc;n mặt nước cao ốc, vậy cũng l&agrave; một tr&agrave;ng ba mươi tầng cao cao ốc, giữa lẫn nhau khoảng c&aacute;ch bất qu&aacute; bốn mươi, năm mươi m&eacute;t, T&ocirc; L&ecirc; nh&igrave;n kia lộ ra mặt nước ba mươi tầng, nghĩ nơi đ&oacute; phải chăng c&oacute; giống như ch&iacute;nh m&igrave;nh người may mắn c&ograve;n sống s&oacute;t?<br />\r\n<br />\r\n&quot;Bất luận l&agrave;m sao, tổng phải nghĩ biện ph&aacute;p đi xem xem.&quot; T&ocirc; L&ecirc; y&ecirc;n lặng nghĩ.<br />\r\n<br />\r\nSau hắn đốt chăn b&ocirc;ng, chế tạo ra đại lượng sương m&ugrave; d&agrave;y.<br />\r\n<br />\r\nNh&igrave;n sương m&ugrave; d&agrave;y l&ecirc;n kh&ocirc;ng, T&ocirc; L&ecirc; nghĩ thầm nếu như trong t&ograve;a cao ốc kia thật sự c&oacute; người may mắn c&ograve;n sống s&oacute;t, nhất định c&oacute; thể nh&igrave;n thấy n&agrave;y bốc l&ecirc;n sương m&ugrave; d&agrave;y, c&oacute; lẽ sẽ l&agrave;m ra phản ứng.<br />\r\n<br />\r\nĐ&aacute;ng tiếc hắn đầy đủ đợi nửa giờ, cũng kh&ocirc;ng c&oacute; quan s&aacute;t được trong t&ograve;a cao ốc kia c&oacute; người sống dấu hiệu, chớ n&oacute;i chi l&agrave; c&oacute; phản ứng g&igrave;, cuối c&ugrave;ng chỉ c&oacute; thể từ bỏ chờ đợi, theo cầu thang trở về.<br />\r\n<br />\r\nL&agrave;m theo cầu thang xuống tới ba mươi tầng, &aacute;nh mắt t&ugrave;y &yacute; qu&eacute;t qua, đột nhi&ecirc;n trong l&ograve;ng hơi động, để hắn ngừng lại.<br />\r\n<br />\r\nĐi về tầng hai mươi ch&iacute;n cầu thang đ&atilde; ho&agrave;n to&agrave;n bị nước ch&igrave;m, mực nước sắp muốn ngập đến ba mươi tầng mặt đất rồi.<br />\r\n<br />\r\n&quot;Ta r&otilde; r&agrave;ng nhớ tới lần đầu ti&ecirc;n tới nơi n&agrave;y thời điểm, mực nước n&agrave;y khoảng c&aacute;ch tầng n&agrave;y mặt đất c&ograve;n c&oacute; 4, 5 centim&eacute;t, hiện tại l&agrave;m sao cũng sắp muốn ch&igrave;m đến mặt đất rồi? Chỉ thời gian d&agrave;i như vậy, mực nước n&agrave;y lại tăng l&ecirc;n v&agrave;i centimet, mực nước n&agrave;y. . . C&ograve;n đang chầm chậm tăng l&ecirc;n?&quot;<br />\r\n<br />\r\nSự ph&aacute;t hiện n&agrave;y để T&ocirc; L&ecirc; t&ecirc; cả da đầu, chuyện n&agrave;y &yacute; nghĩa l&agrave; hắn hiện tại đợi n&agrave;y tầng thứ ba mươi cũng kh&ocirc;ng an to&agrave;n, mực nước n&agrave;y l&uacute;c n&agrave;o cũng c&oacute; thể biến h&oacute;a, thậm ch&iacute; đem ba mươi tầng nhấn ch&igrave;m.<br />\r\n<br />\r\nĐiều n&agrave;y l&agrave;m cho trong l&ograve;ng của hắn n&ocirc;n n&oacute;ng l&ecirc;n.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng được, nhất định phải mau ch&oacute;ng l&agrave;m tốt b&egrave; gỗ, cũng kh&ocirc;ng ai biết kế tiếp c&ograve;n c&oacute; biến h&oacute;a g&igrave; đ&oacute;.&quot;<br />\r\n<br />\r\nCảm gi&aacute;c được chuyện qu&aacute; khẩn cấp, T&ocirc; L&ecirc; nơi n&agrave;o c&ograve;n d&aacute;m l&agrave;m lỡ, ba bước cũng hai bước, liền theo h&agrave;nh lang x&ocirc;ng tới trở về, vội v&agrave;ng bắt đầu động thủ chế t&aacute;c b&egrave; gỗ.<br />\r\n<br />\r\nTrừ m&igrave;nh ra cửa ph&ograve;ng ngủ ở ngo&agrave;i, hắn đem trong nh&agrave; kh&aacute;c hai c&aacute;nh cửa gỗ đều ph&aacute; đi, hắn quyết định đem n&agrave;y hai c&aacute;nh cửa gỗ hợp c&ugrave;ng nhau chế th&agrave;nh một c&aacute;i th&ocirc; sơ b&egrave; gỗ.<br />\r\n<br />\r\nHắn t&iacute;nh toán n&agrave;y hai c&aacute;nh cửa gỗ lẽ ra c&oacute; thể đủ chịu đựng được ch&iacute;nh m&igrave;nh trọng lượng, kh&ocirc;ng đến nỗi sẽ ở trong nước ch&igrave;m xuống.<br />\r\n<br />\r\nHắn nhớ tới khi c&ograve;n b&eacute; ở n&ocirc;ng th&ocirc;n, trong th&ocirc;n c&oacute; người đứng ở tr&ecirc;n một c&aacute;nh cửa gỗ, cầm tr&ecirc;n tay một c&aacute;i s&agrave;o tr&uacute;c, đều c&oacute; thể ở tr&ecirc;n nước nổi được, ch&iacute;nh m&igrave;nh đem hai c&aacute;nh cửa gỗ hợp c&ugrave;ng nhau, hẳn l&agrave; khả thi.<br />\r\n<br />\r\nSau hắn lại đem nệm xốc l&ecirc;n, đem ph&iacute;a dưới tấm v&aacute;n gỗ đều từng khối từng khối ph&aacute; đi.<br />\r\n<br />\r\nĐem những n&agrave;y tấm v&aacute;n gỗ nằm ngang đặt ở tr&ecirc;n hai c&aacute;nh cửa gỗ, lại d&ugrave;ng đinh sắt đ&oacute;ng đinh, đ&atilde; như thế, một c&aacute;i th&ocirc; sơ b&egrave; gỗ liền ho&agrave;n c&ocirc;ng rồi.<br />\r\n<br />\r\nTrong nh&agrave; kh&ocirc;ng c&oacute; d&acirc;y thừng, hắn liền t&igrave;m đến ga trải giường c&ugrave;ng vỏ chăn, d&ugrave;ng c&aacute;i k&eacute;o đem ga trải giường vỏ chăn từng c&aacute;i cắt mở, cắt th&agrave;nh từng c&aacute;i từng c&aacute;i thật d&agrave;i vải, lại đem những n&agrave;y vải bi&ecirc;n th&agrave;nh d&acirc;y thừng.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nRất nhanh một ng&agrave;y n&agrave;y liền đi qua, hắn đầy đủ bi&ecirc;n tận mấy c&aacute;i d&acirc;y thừng d&agrave;i, đem b&ecirc;n trong một c&aacute;i d&acirc;y thừng d&agrave;i cố định ở tr&ecirc;n b&egrave; gỗ, c&aacute;i kh&aacute;c giữ lại dự bị.<br />\r\n<br />\r\nChờ hắn hết bận sau, sắc trời &acirc;m tối lại, T&ocirc; L&ecirc; ăn hai khối b&aacute;nh m&igrave; lót dạ, đi tới s&acirc;n thượng b&ecirc;n cửa sổ.<br />\r\n<br />\r\nHắn muốn x&aacute;c định cửa sổ phải chăng đều đ&oacute;ng kỹ, m&agrave;n đ&ecirc;m lập tức đ&aacute;nh đến nơi, ban ng&agrave;y xuất hiện b&ocirc;̣ kia sẽ di động c&ocirc;ng k&iacute;ch nữ thi, để hắn cảm gi&aacute;c nơi n&agrave;y kh&ocirc;ng an to&agrave;n.<br />\r\n<br />\r\nMới vừa mới vừa đi tới b&ecirc;n s&acirc;n thượng, đột nhi&ecirc;n nh&igrave;n thấy s&acirc;n thượng ph&iacute;a b&ecirc;n ngo&agrave;i cửa sổ, kia trong nước bồng bềnh một bộ thi thể.<br />\r\n<br />\r\nThi thể n&agrave;y diện mạo hướng về dưới nước, hắn chỉ c&oacute; thể nh&igrave;n thấy ph&iacute;a sau lưng, bồng bềnh ở b&ecirc;n trong nước, khoảng c&aacute;ch hắn s&acirc;n thượng cửa sổ, bất qu&aacute; một hai m&eacute;t xa.<br />\r\n<br />\r\nT&ocirc; L&ecirc; chấn động trong l&ograve;ng, sắc mặt nhất thời trở n&ecirc;n kh&oacute; xem ra, hắn r&otilde; r&agrave;ng nhớ tới ban ng&agrave;y c&ograve;n chưa ph&aacute;t hiện thi thể n&agrave;y, hiện tại đ&acirc;y l&agrave; từ nơi n&agrave;o bồng bềnh lại đ&acirc;y?<br />\r\n<br />\r\n&quot;Đ&aacute;ng chết, thi thể n&agrave;y sẽ kh&ocirc;ng cũng thi biến chứ?&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; nh&igrave;n khoảng c&aacute;ch n&agrave;y cửa sổ như thế gần x&aacute;c chết tr&ocirc;i, thấy thế n&agrave;o thế n&agrave;o cảm gi&aacute;c kh&oacute; chịu, rốt cục kh&ocirc;ng nhịn được cầm lấy s&agrave;o phơi đồ, mở ra một c&aacute;nh cửa sổ, đem s&agrave;o phơi đồ vươn ra ngo&agrave;i, hướng về n&agrave;y x&aacute;c chết tr&ocirc;i giết đi, muốn đem thi thể n&agrave;y xa xa đẩy ra.<br />\r\n<br />\r\nHắn s&agrave;o phơi đồ vừa mới giết b&ecirc;n trong thi thể n&agrave;y, ch&iacute;nh như muốn đẩy ra, đột nhi&ecirc;n thi thể n&agrave;y đầu đột nhi&ecirc;n nhấc l&ecirc;n.</p>', '2021-12-01 01:15:27', 6, 1),
(271, 71, 'Nguy Cơ', '7', '<p>T&ocirc; L&ecirc; sợ hết hồn, trong tay s&agrave;o phơi đồ k&eacute;m ch&uacute;t doạ rơi mất, chỉ thấy thi thể n&agrave;y giơ l&ecirc;n khu&ocirc;n mặt sưng ph&ugrave;, c&oacute; m&agrave;u xanh t&iacute;m, giờ khắc này mặt ngo&agrave;i sinh ra một tầng tinh tế m&agrave;u trắng l&ocirc;ng mềm, một đ&ocirc;i mở trong đ&ocirc;i mắt kh&ocirc;ng c&oacute; con ngươi, chỉ c&oacute; một mảnh tr&ograve;ng trắng mắt, miệng trương đ&ecirc;́n mức r&acirc;́t lớn, giống ở miệng lớn h&ocirc; hấp.<br />\r\n<br />\r\nThi thể hai tay run run, tựa hồ muốn từ trong nước gi&atilde;y dụa m&agrave; l&ecirc;n, đột nhi&ecirc;n lại đột nhi&ecirc;n ch&igrave;m xuống, trong nước n&agrave;y giống đột nhi&ecirc;n sinh ra một luồng sức mạnh khổng lồ, l&ocirc;i k&eacute;o thi thể n&agrave;y đi xuống, &quot;Ừng ực&quot; một tiếng, thi thể liền ch&igrave;m v&agrave;o trong nước biến mất kh&ocirc;ng c&ograve;n tăm hơi, chỉ ở mặt nước nhấc l&ecirc;n bọt nước.<br />\r\n<br />\r\nN&agrave;y trước sau biến h&oacute;a bất qu&aacute; một hai gi&acirc;y, T&ocirc; L&ecirc; mở to hai mắt, nh&igrave;n thi thể kia đột nhi&ecirc;n liền ch&igrave;m v&agrave;o trong nước, biến th&agrave;nh một đo&agrave;n b&oacute;ng đen, sau đ&oacute; liền b&oacute;ng đen cũng kh&ocirc;ng thấy, m&agrave; hắn lại tay ch&acirc;n lạnh lẽo, liền với l&ugrave;i về sau, sau đ&oacute; đ&oacute;ng chặt cửa sổ lại, sắc mặt ho&agrave;n to&agrave;n trắng bệch.<br />\r\n<br />\r\n&quot;Trong nước n&agrave;y. . . C&oacute; đồ vật. . . Trừ bỏ thi thể n&agrave;y ở ngo&agrave;i, c&ograve;n c&oacute; c&aacute;i kh&aacute;c đồ vật, vừa mới. . . Đ&acirc;y l&agrave; c&oacute; đồ vật k&eacute;o thi thể n&agrave;y ch&igrave;m v&agrave;o trong nước. . .&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; m&ocirc;i hơi run run, lẩm bẩm n&oacute;i nhỏ, l&ugrave;i tới b&ecirc;n b&agrave;n tr&agrave;, nắm chặt dao phay c&ugrave;ng c&acirc;y b&uacute;a, trong l&ograve;ng mới tho&aacute;ng cảm gi&aacute;c được một tia an l&ograve;ng.<br />\r\n<br />\r\nHắn nghĩ tới r&ocirc;̀i trước nh&igrave;n thấy b&ocirc;̣ kia nữ thi, ngực bụng c&ugrave;ng phần ch&acirc;n đều c&oacute; bị gặm nhấm dấu hiệu, một mảnh m&aacute;u thịt be b&eacute;t, l&uacute;c đ&oacute; hắn liền ho&agrave;i nghi trong nước n&agrave;y phải chăng c&oacute; c&aacute;i g&igrave; lo&agrave;i ăn thịt c&aacute; lớn hoặc c&aacute;i kh&aacute;c c&aacute;i g&igrave; trong nước sinh vật gặm nhấm thi thể, kết hợp với vừa mới ph&aacute;t sinh một m&agrave;n, T&ocirc; L&ecirc; trong l&ograve;ng c&oacute; một c&aacute;i đ&aacute;ng sợ suy đo&aacute;n.<br />\r\n<br />\r\nNgo&agrave;i n&agrave;y trong nước ẩn giấu đi một c&aacute;i sinh vật cực kỳ đ&aacute;ng sợ, sẽ gặm nhấm thi thể, hơn nữa từ vừa mới k&eacute;o lấy thi thể kia ch&igrave;m xuống tốc độ đến nh&igrave;n, sinh vật trong nước thần b&iacute; n&agrave;y, cực kỳ đ&aacute;ng sợ, c&oacute; thể đem một bộ x&aacute;c chết tr&ocirc;i cấp tốc như thế k&eacute;o v&agrave;o trong nước biến mất kh&ocirc;ng c&ograve;n tăm hơi, n&oacute; sức mạnh rất lớn, thể h&igrave;nh cũng tuyệt đối kh&ocirc;ng nhỏ.<br />\r\n<br />\r\n&quot;Tổng kh&ocirc;ng đến nỗi sẽ l&agrave; c&aacute; sấu hoặc c&aacute; mập loại h&igrave;nh đi. . .&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; nh&igrave;n b&ecirc;n ngo&agrave;i m&ecirc;nh m&ocirc;ng v&ocirc; bờ mặt nước, trong l&ograve;ng mơ hồ sinh ra tuyệt vọng, trong đầu b&ocirc;́c l&ecirc;n suy đo&aacute;n đ&aacute;ng sợ n&agrave;y.<br />\r\n<br />\r\nLiền một t&ograve;a th&agrave;nh thị đều bị nhấn ch&igrave;m rồi, nếu như n&oacute;i trước mắt n&agrave;y trong nước lại xuất hiện c&aacute; mập hoặc c&aacute; sấu loại h&igrave;nh hung m&atilde;nh động vật, T&ocirc; L&ecirc; cũng kh&ocirc;ng cảm thấy kỳ qu&aacute;i, nhưng đối với hắn m&agrave; n&oacute;i, n&agrave;y nhưng l&agrave; tr&iacute; mạng.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nHắn khổ cực l&agrave;m ra b&egrave; gỗ, đ&atilde; nghĩ s&aacute;ng mai rời đi nơi n&agrave;y, đi t&igrave;m c&aacute;i kh&aacute;c người may mắn c&ograve;n sống s&oacute;t c&ugrave;ng t&igrave;m kiếm cứu viện, nhưng nếu như trong nước thật sinh sống như vậy sinh vật đ&aacute;ng sợ, vậy th&igrave; phiền phức, hắn thừa b&egrave; gỗ chạy đi, c&ugrave;ng chịu chết kh&ocirc;ng kh&aacute;c nhau.<br />\r\n<br />\r\n&quot;Lẽ n&agrave;o chỉ c&oacute; thể ở lại chỗ n&agrave;y, chỉ l&agrave; đồ ăn c&oacute; hạn, nhiều nhất c&ograve;n c&oacute; thể ki&ecirc;n tr&igrave; b&ocirc;́n ng&agrave;y.&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; suy nghĩ hồi l&acirc;u, cũng kh&ocirc;ng c&oacute; biện ph&aacute;p tốt hơn c&oacute; thể tưởng tượng, chỉ c&oacute; thể đem s&acirc;n thượng n&agrave;y một h&agrave;ng cửa sổ đều kh&oacute;a kỹ, sau lại kiểm tra c&aacute;i kh&aacute;c cửa sổ, đều nhất nhất quan trọng kh&oacute;a kỹ.<br />\r\n<br />\r\nChỉ l&agrave; trong l&ograve;ng hắn r&otilde; r&agrave;ng, hắn nơi n&agrave;y l&agrave; ba mươi tầng, b&ecirc;n ngo&agrave;i kh&ocirc;ng c&oacute; lắp đặt cửa sổ chống trộm, nếu như c&ograve;n c&oacute; thi thể thật giống ban ng&agrave;y nữ thi một dạng ph&aacute;t sinh thi biến, c&oacute; thể dễ d&agrave;ng liền đ&aacute;nh n&aacute;t cửa sổ kiếng b&ograve; đi vào, cho tới trong nước kia ẩn giấu đi đến c&ugrave;ng l&agrave; ra sao sinh vật, hắn tuy rằng suy đo&aacute;n nửa ng&agrave;y, cũng kh&ocirc;ng c&oacute; một ch&uacute;t n&agrave;o manh mối, duy nhất hi vọng ch&iacute;nh l&agrave; sinh vật thần b&iacute; n&agrave;y chỉ c&oacute; thể ở b&ecirc;n trong nước sinh hoạt, sẽ kh&ocirc;ng giống kia thi biến thi thể c&oacute; thể l&ecirc;n bờ.<br />\r\n<br />\r\nT&ocirc; L&ecirc; x&aacute;c định đ&atilde; kh&oacute;a kỹ cửa sổ, nh&igrave;n b&oacute;ng đ&ecirc;m c&agrave;ng ng&agrave;y c&agrave;ng mờ, hắn đem đặt ở trong tủ lạnh m&igrave; ăn liền, b&aacute;nh m&igrave; b&aacute;nh b&iacute;ch quy chờ đồ ăn vặt đều lấy ra, sau đ&oacute; t&igrave;m tới một c&aacute;i lữ h&agrave;nh ba l&ocirc;, đem hết thảy đồ ăn đều cất v&agrave;o trong t&uacute;i đeo lưng, để ngừa tao ngộ đột sự t&igrave;nh huống, cần khẩn cấp chạy khỏi nơi n&agrave;y, kh&ocirc;ng kịp mang đi đồ ăn.<br />\r\n<br />\r\nTrong ph&ograve;ng c&agrave;ng ng&agrave;y c&agrave;ng tối, T&ocirc; L&ecirc; trong lồng ngực &ocirc;m dao phay c&ugrave;ng c&acirc;y b&uacute;a, liền nằm ở tr&ecirc;n ghế sal&ocirc;ng, với trong đ&ecirc;m tối, trợn tr&ograve;n mắt, trong l&ograve;ng hắn c&oacute; loại m&atilde;nh liệt kh&ocirc;ng r&otilde; linh cảm, đ&ecirc;m nay chỉ sợ sẽ c&oacute; chuyện.<br />\r\n<br />\r\nLại như muốn ứng nghiệm hắn linh cảm, đột nhi&ecirc;n, ngo&agrave;i cửa tr&ecirc;n h&agrave;nh lang, truyền đến tiếng bước ch&acirc;n.<br />\r\n<br />\r\nTiếng bước ch&acirc;n kh&ocirc;ng lớn, nhưng ở n&agrave;y y&ecirc;n tĩnh ban đ&ecirc;m vang l&ecirc;n, lại đặc biệt r&otilde; r&agrave;ng.<br />\r\n<br />\r\nT&ocirc; L&ecirc; nhất thời cả người căng thẳng, cầm lấy dao phay c&ugrave;ng c&acirc;y b&uacute;a liền từ tr&ecirc;n ghế sa l&ocirc;ng đứng l&ecirc;n.<br />\r\n<br />\r\nN&agrave;y ba mươi tầng hắn căn bản kh&ocirc;ng thấy một người sống, tr&ecirc;n h&agrave;nh lang tại sao c&oacute; thể c&oacute; tiếng bước ch&acirc;n? Lẽ n&agrave;o. . .<br />\r\n<br />\r\nKia bước ch&acirc;n một tiếng tiếp một bước, nghe &acirc;m thanh, l&agrave; hướng về ph&iacute;a b&ecirc;n m&igrave;nh m&agrave; tới.<br />\r\n<br />\r\nT&ocirc; L&ecirc; rốt cục kh&ocirc;ng nhịn được, ở trong b&oacute;ng tối r&oacute;n r&eacute;n, hướng về cửa chống trộm phương hướng dời đi, hắn muốn th&ocirc;ng qua mắt m&egrave;o nh&igrave;n một c&aacute;i, b&ecirc;n ngo&agrave;i n&agrave;y ph&aacute;t ra tiếng bước ch&acirc;n đến c&ugrave;ng l&agrave; c&aacute;i g&igrave;.<br />\r\n<br />\r\nChỉ l&agrave; ngăn ngắn v&agrave;i bước, T&ocirc; L&ecirc; l&ograve;ng b&agrave;n tay tất cả đều l&agrave; mồ h&ocirc;i lạnh, nắm chặt dao phay c&ugrave;ng c&acirc;y b&uacute;a, trong l&ograve;ng đang kh&ocirc;ng ngừng an ủi m&igrave;nh, cho m&igrave;nh đ&aacute;nh bạo.<br />\r\n<br />\r\n&quot;Coi như l&agrave; thi biến cũng kh&ocirc;ng c&oacute; g&igrave; đ&aacute;ng sợ, trừ bỏ ngoại h&igrave;nh xem ra hơi doạ người ở ngo&agrave;i, động t&aacute;c cũng kh&ocirc;ng nhạy b&eacute;n, h&agrave;nh động cũng v&ocirc; c&ugrave;ng cứng ngắc, căn bản kh&ocirc;ng phải l&agrave; đối thủ của ta. . .&quot;<br />\r\n<br />\r\nTrong l&ograve;ng một lần lại một lần đọc thầm, dường như cho m&igrave;nh th&ocirc;i mi&ecirc;n, như vậy kh&ocirc;ng ngừng lặp lại, sau đ&oacute; hắn đi tới cạnh cửa, hướng về trong mắt m&egrave;o nh&igrave;n lại.<br />\r\n<br />\r\nCh&iacute;nh v&agrave;o l&uacute;c n&agrave;y, kia b&ecirc;n ngo&agrave;i tiếng bước ch&acirc;n biến mất rồi, tựa hồ nguy&ecirc;n bản cất bước người ngừng lại.<br />\r\n<br />\r\nT&ocirc; L&ecirc; từ trong mắt m&egrave;o nh&igrave;n lại, b&ecirc;n ngo&agrave;i một đo&agrave;n hắc &aacute;m, c&aacute;i g&igrave; cũng thấy kh&ocirc;ng r&otilde; lắm.<br />\r\n<br />\r\nL&uacute;c n&agrave;y hắn mới nhớ tới b&ecirc;n ngo&agrave;i h&agrave;nh lang c&ugrave;ng trong ph&ograve;ng của hắn một dạng, đều nằm ở trong b&oacute;ng tối, kh&ocirc;ng c&oacute; tia s&aacute;ng, mắt m&egrave;o n&agrave;y thấy kh&ocirc;ng r&otilde; lắm t&igrave;nh huống b&ecirc;n ngo&agrave;i.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nCh&iacute;nh v&agrave;o l&uacute;c n&agrave;y, đột nhi&ecirc;n vừa s&acirc;n thượng nơi truyền đến &quot;Lộp bộp&quot; một tiếng vang gi&ograve;n, đ&acirc;y l&agrave; cửa sổ kiếng tiếng vỡ vụn.<br />\r\n<br />\r\nỞ chỗ n&agrave;y tĩnh mịch b&ecirc;n trong, thanh &acirc;m n&agrave;y đột nhi&ecirc;n vang l&ecirc;n, c&oacute; vẻ đặc biệt ch&oacute;i tai, T&ocirc; L&ecirc; giật m&igrave;nh run l&ecirc;n, cả người đều l&agrave; chấn động, đột nhi&ecirc;n quay đầu, liền nh&igrave;n thấy s&acirc;n thượng nơi một c&aacute;nh cửa sổ bột thủy tinh n&aacute;t, hai c&aacute;nh tay từ b&ecirc;n ngo&agrave;i duỗi v&agrave;o, v&iacute;u ở tr&ecirc;n cửa sổ, một tấm sưng ph&ugrave; xanh t&iacute;m mặt to x&ocirc;ng ra.<br />\r\n<br />\r\nKhu&ocirc;n mặt n&agrave;y bị gặm nhấm rơi mất hơn nửa, một mảnh m&aacute;u thịt be b&eacute;t, trong đ&oacute; một con mắt đột xuất viền mắt, ph&iacute;a sau c&oacute; g&acirc;n liền với, ch&iacute;nh treo ở khu&ocirc;n mặt tr&ecirc;n, kh&ocirc;ng bị gặm nhấm nửa tấm tr&ecirc;n mặt mọc ra một tầng tinh tế m&agrave;u trắng l&ocirc;ng mềm, c&ograve;n lại một con kh&aacute;c kh&ocirc;ng c&oacute; con ngươi con mắt, ch&iacute;nh gắt gao ở trong b&oacute;ng tối nh&igrave;n chằm chằm T&ocirc; L&ecirc;, giống một c&aacute;i rời đi nước c&aacute;, miệng kh&ocirc;ng ngừng v&ocirc; &yacute; thức đ&oacute;ng mở, c&oacute; vẻ v&ocirc; c&ugrave;ng qu&aacute;i dị.<br />\r\n<br />\r\nTuy rằng khu&ocirc;n mặt bị gặm nhấm rơi mất hơn nửa, T&ocirc; L&ecirc; vẫn l&agrave; đầu ti&ecirc;n nh&igrave;n liền nhận ra hắn đ&uacute;ng l&agrave; m&igrave;nh trước ở s&acirc;n thượng b&ecirc;n ngo&agrave;i trong nước nh&igrave;n thấy b&ocirc;̣ kia x&aacute;c chết tr&ocirc;i, l&uacute;c đ&oacute; mặt của hắn vẫn l&agrave; ho&agrave;n chỉnh, bị bắt tiến đ&aacute;y nước biến mất kh&ocirc;ng c&ograve;n tăm hơi, kh&ocirc;ng nghĩ tới giờ khắc này lại x&ocirc;ng ra, đ&aacute;nh n&aacute;t một c&aacute;nh cửa sổ pha l&ecirc;, liền muốn đi đến b&ograve; đến.<br />\r\n<br />\r\nC&oacute; lần thứ nhất đối mặt kia nữ thi kinh nghiệm, giờ khắc này T&ocirc; L&ecirc; tuy rằng vẫn tr&agrave;n ngập hoảng sợ, nhưng vẫn l&agrave; x&ocirc;ng l&ecirc;n tr&ecirc;n, thừa dịp thi thể n&agrave;y vẫn kh&ocirc;ng c&oacute; b&ograve; đi vào, hắn muốn ti&ecirc;n hạ thủ vi cường.<br />\r\n<br />\r\nHắn đ&atilde; sớm ph&aacute;t hiện thi thể n&agrave;y biến th&agrave;nh qu&aacute;i vật tuy rằng khủng bố, nhưng khuyết điểm cũng cực kỳ r&otilde; r&agrave;ng, đ&oacute; ch&iacute;nh l&agrave; khớp xương kh&aacute; cứng ngắc, động t&aacute;c kh&ocirc;ng c&oacute; người b&igrave;nh thường linh hoạt.<br />\r\n<br />\r\nT&ocirc; L&ecirc; x&ocirc;ng l&ecirc;n thời điểm, thi thể n&agrave;y vừa mới đưa đầu v&agrave;o, tay phải hắn nắm chặt c&acirc;y b&uacute;a, phủ đầu một ch&ugrave;y hướng về thi thể n&agrave;y đầu đập xuống.<br />\r\n<br />\r\nThi thể n&agrave;y giơ l&ecirc;n hai tay, hộ tr&ugrave;m đầu mặt, một b&uacute;a n&agrave;y đập ở trong đ&oacute; một c&aacute;nh tay tr&ecirc;n.</p>', '2021-12-01 01:15:47', 5, 1),
(272, 71, 'Tiến Hóa', '8', '<p>T&ocirc; L&ecirc; ph&aacute;t hiện thi thể n&agrave;y sự linh hoạt tựa hồ so với ban ng&agrave;y nữ thi muốn cao một ch&uacute;t, đ&atilde; tiếp cận b&igrave;nh thường người b&igrave;nh thường độ linh hoạt, nếu như l&uacute;c n&agrave;y đối phương l&ugrave;i về sau thu về đi, T&ocirc; L&ecirc; cũng kh&ocirc;ng c&aacute;ch n&agrave;o c&ocirc;ng k&iacute;ch được n&oacute;.<br />\r\n<br />\r\nSau đ&oacute; T&ocirc; L&ecirc; liền ph&aacute;t hiện qu&aacute;i vật thi biến n&agrave;y khuyết điểm thứ hai, đầu &oacute;c kh&ocirc;ng đủ linh quang.<br />\r\n<br />\r\nPhổ th&ocirc;ng người b&igrave;nh thường gặp được t&igrave;nh huống như thế, tay kh&ocirc;ng đối mặt cầm dao phay c&ugrave;ng ch&ugrave;y đầu T&ocirc; L&ecirc;, nhất định sẽ nghĩ trước ti&ecirc;n lui thu về đi, sau lại nghĩ cách đến c&ocirc;ng k&iacute;ch.<br />\r\n<br />\r\nNhưng trước mắt qu&aacute;i vật thi biến n&agrave;y, c&aacute;nh tay đ&atilde; tr&uacute;ng T&ocirc; L&ecirc; một ch&ugrave;y sau, lại vẫn đang liều mạng muốn đi đến b&ograve;, rất nhanh sẽ đem gần nửa người theo cửa sổ b&ograve; v&agrave;o.<br />\r\n<br />\r\nT&ocirc; L&ecirc; thấy thế, tay tr&aacute;i dao phay liền với ch&eacute;m xuống, trước mặt m&aacute;u thịt tung to&eacute;, trong đ&oacute; hai đao ch&eacute;m v&agrave;o đối phương tr&ecirc;n c&aacute;nh tay, c&ograve;n c&oacute; ba đao đều chặt ở tr&ecirc;n khu&ocirc;n mặt, kia ch&ugrave;y đầu cũng liền hạ xuống, chờ thi thể n&agrave;y rốt cục đem nửa người b&ograve; qua cửa sổ sau, liền nằm nho&agrave;i ở chỗ n&agrave;y, kh&ocirc;ng nh&uacute;c nh&iacute;ch, n&oacute; đầu cơ hồ bị T&ocirc; L&ecirc; ch&ugrave;y đầu đ&acirc;̣p nát rồi.<br />\r\n<br />\r\nNh&igrave;n qu&aacute;i vật thi biến n&agrave;y kh&ocirc;ng động đậy nữa, T&ocirc; L&ecirc; trong l&ograve;ng b&ocirc;́c l&ecirc;n một loại đối phương chủ động tống đầu người cảm gi&aacute;c qu&aacute;i dị.<br />\r\n<br />\r\nNh&igrave;n thi thể n&agrave;y nằm nho&agrave;i tr&ecirc;n cửa sổ, da kia mặt ngo&agrave;i xuất hiện một tầng m&agrave;u trắng l&ocirc;ng mềm co r&uacute;t lại biến mất, rất nhanh từ n&oacute; diện mạo kia một đống m&aacute;u thịt mơ hồ b&ecirc;n trong, liền c&oacute; một đo&agrave;n to bằng đầu ng&oacute;n tay m&agrave;u trắng nhung cầu x&ocirc;ng ra, bắn l&ecirc;n, hướng về tr&ecirc;n mặt T&ocirc; L&ecirc; đập tới.<br />\r\n<br />\r\nLần n&agrave;y, T&ocirc; L&ecirc; kh&ocirc;ng c&oacute; lại chống cự, tr&ecirc;n thực tế hắn cũng kh&ocirc;ng căn bản kh&ocirc;ng tr&aacute;nh được, đối phương nhanh bắn tới tốc độ qu&aacute; nhanh.<br />\r\n<br />\r\nLần thứ hai bị nh&agrave;o tr&uacute;ng c&aacute;i tr&aacute;n, hắn đưa tay đến m&ograve;, lại kh&ocirc;ng c&oacute; thứ g&igrave;, sau đ&oacute; cảm gi&aacute;c một luồng cực n&oacute;ng, giống điện lưu vậy đi về to&agrave;n th&acirc;n, trước sau bất qu&aacute; v&agrave;i gi&acirc;y, lại cấp tốc biến mất kh&ocirc;ng c&ograve;n tăm hơi, nhưng trong cơ thể nhưng có một luồng sức mạnh mới ở sinh s&ocirc;i.<br />\r\n<br />\r\nBan ng&agrave;y đ&acirc;̣p nát kia nữ thi đầu, hắn mệt đến thở hồng hộc, nhưng hiện tại dao phay c&ugrave;ng c&acirc;y b&uacute;a liền với hạ xuống, hắn cũng kh&ocirc;ng cảm gi&aacute;c qu&aacute; mức uể oải, m&agrave; giờ khắc n&agrave;y trong cơ thể lại c&oacute; một luồng sức mạnh mới xuất hiện, để hắn cảm gi&aacute;c vung vẩy ch&ugrave;y đầu c&ugrave;ng dao phay thời điểm, trở n&ecirc;n c&agrave;ng thoải m&aacute;i, thậm ch&iacute; hắn cảm gi&aacute;c c&acirc;y b&uacute;a n&agrave;y ph&acirc;n lượng c&oacute; ch&uacute;t lệch nhẹ.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nHắn mơ hồ cảm gi&aacute;c, sức mạnh của ch&iacute;nh m&igrave;nh, &iacute;t n&oacute;i lại tăng cường m&acirc;́y chục c&acirc;n.<br />\r\n<br />\r\nTrong đầu, bỗng dưng nhiều một đạo tin tức, dường như đột nhi&ecirc;n liền c&oacute; một đoạn n&agrave;y k&yacute; ức.<br />\r\n<br />\r\n&quot;Linh nguy&ecirc;n: 2/5&quot; .<br />\r\n<br />\r\nCảm ứng trong đầu th&ecirc;m ra một đạo n&agrave;y tin tức, T&ocirc; L&ecirc; kh&ocirc;ng c&ograve;n kinh ngạc.<br />\r\n<br />\r\n&quot;Quả nhi&ecirc;n, vật n&agrave;y thật gọi linh nguy&ecirc;n, ta giải quyết hai cỗ sống lại thi thể, được hai c&aacute;i linh nguy&ecirc;n, nhưng ph&iacute;a sau 5 l&agrave; c&oacute; &yacute; g&igrave;? &Yacute; tứ l&agrave; muốn chiếm được năm c&aacute;i linh nguy&ecirc;n sao? Chỉ l&agrave;, được năm c&aacute;i linh nguy&ecirc;n sau, sẽ c&oacute; biến h&oacute;a g&igrave; đ&oacute;. . .&quot;<br />\r\n<br />\r\nT&ocirc; L&ecirc; trong đầu nghĩ đến thi thể kia sau khi biến h&oacute;a, mặt ngo&agrave;i th&acirc;n thể mọc ra một tầng m&agrave;u trắng l&ocirc;ng mềm, dường như trong truyền thuyết l&ocirc;ng trắng cương thi, hắn mơ hồ lại c&oacute; ch&uacute;t sợ sệt, sợ sệt trong cơ thể m&igrave;nh linh nguy&ecirc;n kia đ&atilde; biến th&agrave;nh 5/5 sau, cũng sẽ mọc ra l&ocirc;ng trắng, biến th&agrave;nh cương thi.<br />\r\n<br />\r\nT&ocirc; L&ecirc; vừa trầm tư vừa đ&atilde; nghĩ đem bộ n&agrave;y nằm nho&agrave;i tr&ecirc;n cửa sổ thi thể giơ l&ecirc;n tung ra đi, đột nhi&ecirc;n nghe được cửa chống trộm tr&ecirc;n vang l&ecirc;n &quot;Ầm&quot; một thanh &acirc;m vang l&ecirc;n, theo s&aacute;t liền với vang l&ecirc;n &quot;Ầm ầm ầm&quot; ti&ecirc;́ng vang, nhưng l&agrave; c&oacute; người ở b&ecirc;n ngo&agrave;i d&ugrave;ng rất lớn kh&iacute; lực đập cửa.<br />\r\n<br />\r\nT&ocirc; L&ecirc; nắm chặt dao phay c&ugrave;ng c&acirc;y b&uacute;a, xoay người nh&igrave;n chằm chằm cửa chống trộm, n&agrave;y đ&aacute;nh kh&iacute; lực tuy rằng lớn, nhưng cửa chống trộm rất ki&ecirc;n cố, muốn ph&aacute; hoại cửa chống trộm rất kh&oacute; khăn, điều n&agrave;y l&agrave;m cho hắn hơi an l&ograve;ng.<br />\r\n<br />\r\n&quot;Ngươi l&agrave; ai?&quot; T&ocirc; L&ecirc; nh&igrave;n chằm chằm cửa chống trộm, tuy rằng trong l&ograve;ng ho&agrave;i nghi b&ecirc;n ngo&agrave;i n&agrave;y g&otilde; cửa cũng l&agrave; một bộ thi biến qu&aacute;i vật, nhưng vẫn l&agrave; kh&ocirc;ng nhịn được ph&aacute;t ra &acirc;m thanh, tuy rằng chỉ c&oacute; vạn nhất hi vọng, hắn vẫn l&agrave; hi vọng b&ecirc;n ngo&agrave;i g&otilde; cửa kh&ocirc;ng phải thi biến qu&aacute;i vật, m&agrave; l&agrave; c&aacute;i kh&aacute;c người may mắn c&ograve;n sống s&oacute;t.<br />\r\n<br />\r\nHắn vừa mới ph&aacute;t ra &acirc;m thanh, b&ecirc;n ngo&agrave;i g&otilde; cửa &acirc;m thanh ngừng lại, bốn ph&iacute;a một lần nữa trở n&ecirc;n tĩnh mịch, liền ở l&uacute;c, đột nhi&ecirc;n từ hắn ph&ograve;ng ngủ trong ph&ograve;ng truyền đến lanh lảnh tiếng vỡ n&aacute;t vang.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng được!&quot; T&ocirc; L&ecirc; biến sắc mặt, lập tức hướng về ph&ograve;ng ngủ ph&oacute;ng đi.<br />\r\n<br />\r\nKhi hắn vọt v&agrave;o ph&ograve;ng ngủ thời điểm, trong b&oacute;ng tối, liền mơ hồ nh&igrave;n thấy một đo&agrave;n b&oacute;ng đen, ch&iacute;nh theo hắn cửa sổ ph&ograve;ng ngủ b&ograve; v&agrave;o, cửa sổ ph&ograve;ng ngủ pha l&ecirc; đ&atilde; bị đ&aacute;nh n&aacute;t rồi.<br />\r\n<br />\r\nĐo&agrave;n n&agrave;y b&oacute;ng đen mới vừa vừa xuống đất, đột nhi&ecirc;n liền hướng về hắn đ&aacute;nh tới, tốc độ n&agrave;y kh&ocirc;ng chậm.<br />\r\n<br />\r\nMượn trong b&oacute;ng đ&ecirc;m một tia &aacute;nh s&aacute;ng nhỏ, T&ocirc; L&ecirc; miễn cưỡng nhìn rõ ràng c&aacute;i n&agrave;y cũng l&agrave; một bộ thi biến qu&aacute;i vật, khi c&ograve;n sống phải l&agrave; một bốn mươi, năm mươi tuổi người trung ni&ecirc;n, thi thể đồng dạng bị nước ng&acirc;m đ&ecirc;́n b&agrave;nh trướng, bất qu&aacute; trước mắt qu&aacute;i vật thi biến n&agrave;y mặt ngo&agrave;i cũng kh&ocirc;ng c&oacute; bị gặm nhấm dấu hiệu, hắn hơi h&aacute; mồm, khu&ocirc;n mặt bị l&ocirc;ng mềm bao tr&ugrave;m, một đ&ocirc;i mắt trắng b&ecirc;n trong, mơ hồ hiện ra &aacute;nh s&aacute;ng nhỏ.<br />\r\n<br />\r\nT&ocirc; L&ecirc; nh&igrave;n hắn x&ocirc;ng lại tốc độ, trong l&ograve;ng hơi lạnh lẽo.<br />\r\n<br />\r\nTừ ban ng&agrave;y đụng tới b&ocirc;̣ kia nữ thi động t&aacute;c cứng ngắc, lại tới vừa mới giết chết thi biến qu&aacute;i vật động t&aacute;c linh hoạt đ&atilde; c&oacute; thể c&ugrave;ng người b&igrave;nh thường gần như, lại tới trước mắt xuất hiện n&agrave;y bộ thứ ba thi biến qu&aacute;i vật nh&agrave;o tới tốc độ rất nhanh, T&ocirc; L&ecirc; đột nhi&ecirc;n ph&aacute;t hiện qu&aacute;i vật thi biến n&agrave;y thật giống như c&oacute; thể tiến h&oacute;a một dạng, một bộ so với một bộ đ&aacute;ng sợ.<br />\r\n<br />\r\nChỉ l&agrave; hắn kh&ocirc;ng c&oacute; đường lui, chỉ l&agrave; nhắm mắt x&ocirc;ng l&ecirc;n, tốt ở trong tay hắn cầm vũ kh&iacute;, hơn nữa vừa mới được c&aacute;i thứ hai linh nguy&ecirc;n, sức mạnh của hắn lại tăng l&ecirc;n một &iacute;t.<br />\r\n<br />\r\nN&agrave;y nh&agrave;o l&ecirc;n thi biến qu&aacute;i vật, cũng kh&ocirc;ng phải thật sự l&agrave; nh&acirc;n loại, tự nhi&ecirc;n cũng kh&ocirc;ng biết sợ h&atilde;i c&ugrave;ng đau đớn, hắn đột nhi&ecirc;n bổ một c&aacute;i, dĩ nhi&ecirc;n duỗi ra hai tay, t&ugrave;y &yacute; T&ocirc; L&ecirc; dao phay ch&eacute;m tr&uacute;ng c&aacute;nh tay của ch&iacute;nh m&igrave;nh, sau đ&oacute; nh&agrave;o tr&uacute;ng T&ocirc; L&ecirc;, một luồng to lớn lực va đập để hắn đem T&ocirc; L&ecirc; tầng tầng ng&atilde; nh&agrave;o xuống đất tr&ecirc;n.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nT&ocirc; L&ecirc; ng&atilde; xuống đất, ph&iacute;a sau lưng c&aacute;i m&ocirc;ng chạm đất, nhất thời một trận đau đớn, theo s&aacute;t c&aacute;i cổ căng thẳng, liền bị qu&aacute;i vật n&agrave;y hai tay b&oacute;p lấy c&aacute;i cổ, lập tức kh&ocirc;ng thể h&ocirc; hấp.<br />\r\n<br />\r\nT&ocirc; L&ecirc; h&aacute; to mồm, hai mắt phồng đột, sử dụng b&uacute; sữa kh&iacute; lực, hai tay cầm dao phay c&ugrave;ng c&acirc;y b&uacute;a đi&ecirc;n cuồng hướng về n&agrave;y đặt ở tr&ecirc;n người m&igrave;nh thi biến qu&aacute;i vật vung tới.<br />\r\n<br />\r\nTrong nh&aacute;y mắt, hắn ch&iacute; &iacute;t ch&eacute;m qu&aacute;i vật thi biến n&agrave;y năm, sáu đao, đập ph&aacute; qu&aacute;i vật n&agrave;y bốn, năm ch&ugrave;y, thế nhưng qu&aacute;i vật thi biến n&agrave;y cũng kh&ocirc;ng biết đau đớn, kỵ ở tr&ecirc;n người hắn, hai tay chỉ l&agrave; gắt gao ngắt lấy T&ocirc; L&ecirc; c&aacute;i cổ.<br />\r\n<br />\r\nT&ocirc; L&ecirc; khu&ocirc;n mặt căng đ&ecirc;́n xanh t&iacute;m, biết kh&ocirc;ng ổn, hắn hiện tại tư thế đập kh&ocirc;ng tới đầu của qu&aacute;i vật kia, m&agrave; đao ch&ugrave;y ở qu&aacute;i vật n&agrave;y tr&ecirc;n th&acirc;n thể coi như lưu lại nặng đến đ&acirc;u thương thế, cũng kh&ocirc;ng c&aacute;ch n&agrave;o để n&oacute; đ&igrave;nh chỉ động t&aacute;c, m&agrave; hắn bị b&oacute;p lấy c&aacute;i cổ, kh&ocirc;ng thể thở nổi, đại n&atilde;o thiếu oxy, l&uacute;c n&agrave;o cũng c&oacute; thể h&ocirc;n m&ecirc; chết.<br />\r\n<br />\r\nGiữa sống chết, c&aacute;i cổ bị gắt gao b&oacute;p lấy, kh&ocirc;ng thể h&ocirc; hấp, T&ocirc; L&ecirc; chỉ cảm thấy lồng ngực giống muốn nổ tung vậy kh&oacute; chịu, to&agrave;n bộ trong &oacute;c trời đất quay cuồng, vang l&ecirc;n ong ong, cũng kh&ocirc;ng biết kh&iacute; lực từ nơi n&agrave;o tới, đột nhi&ecirc;n hắn h&aacute; to mồm, ph&aacute;t ra &quot;Uống&quot; một tiếng, eo c&ugrave;ng bắp đ&ugrave;i đồng thời d&ugrave;ng sức, đột nhi&ecirc;n đem kỵ ở th&acirc;n thể m&igrave;nh tr&ecirc;n thi biến qu&aacute;i vật văng ra ngo&agrave;i.<br />\r\n<br />\r\n&quot;Ầm&quot; một thanh &acirc;m vang l&ecirc;n, lần n&agrave;y kh&iacute; lực rất lớn, trực tiếp đem qu&aacute;i vật thi biến n&agrave;y xốc l&ecirc;n, tầng tầng va ở một b&ecirc;n tr&ecirc;n v&aacute;ch tường treo lơ lửng m&aacute;y truyền h&igrave;nh.<br />\r\n<br />\r\nChỉ nghe &quot;Lộp bộp&quot; một tiếng vang gi&ograve;n, m&aacute;y truyền h&igrave;nh m&agrave;n h&igrave;nh lập tức chia năm xẻ bảy, ng&atilde; xuống, nện ở qu&aacute;i vật thi biến n&agrave;y tr&ecirc;n người, ph&aacute;t ra &quot;Rầm&quot; ti&ecirc;́ng vang.</p>', '2021-12-01 01:16:05', 13, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(273, 72, 'Xuyên Qua Rồi', '1', '<p>Lecon cảm gi&aacute;c m&igrave;nh xuy&ecirc;n qua rồi, nhưng lại chưa hề ho&agrave;n to&agrave;n xuy&ecirc;n việt.<br />\r\n<br />\r\nTrái Đ&acirc;́t vẫn l&agrave; c&aacute;i n&agrave;y Trái Đ&acirc;́t, t&ecirc;n vẫn l&agrave; danh tự n&agrave;y, nhưng hồn xuy&ecirc;n r&ocirc;̀i, th&acirc;n thể trước chủ k&yacute; ức n&oacute;i cho hắn, hiện nay l&agrave; người ở nước Mỹ, mới ra trường học, đối với tương lai tr&agrave;n ngập m&ecirc; man.<br />\r\n<br />\r\nNhưng ở mấy tiếng trước, hắn vẫn l&agrave; th&agrave;nh tựu Thi&ecirc;n phủ một t&ecirc;n phổ th&ocirc;ng thanh ni&ecirc;n l&ecirc;u lổng, đi l&agrave;m nỗ lực m&ograve; c&aacute;, tan tầm chăm ch&uacute; tr&ograve; chơi, đ&aacute;nh b&agrave;i chưa bao giờ vắng chỗ.<br />\r\n<br />\r\nGần nhất hắn m&ecirc; mẩn một khoản ARPG tr&ograve; chơi, đ&oacute;ng vai một t&ecirc;n &Aacute;c Ma Ph&aacute;p Sư ở địa ngục c&ugrave;ng nh&acirc;n gian nhiều lần nhảy ngang, kh&ocirc;ng ch&iacute;nh kh&ocirc;ng t&agrave; t&aacute;c phong rất được hắn &yacute;, kh&ocirc;ng ít đi đến đầu khắc can.<br />\r\n<br />\r\nNg&agrave;y n&agrave;y liền xoạt qu&aacute;i ch&iacute;nh hăng say, ngo&agrave;i cửa sổ kinh l&ocirc;i li&ecirc;n tục, một loại n&agrave;o đ&oacute; sởn cả t&oacute;c g&aacute;y gi&aacute;c quan thứ s&aacute;u x&ocirc;ng tới trong l&ograve;ng, ngẩng đầu nh&igrave;n l&ecirc;n, trong miệng &quot;Mẹ n&oacute;&quot; mới gọi ra một nửa, miệng ch&eacute;n lớn như vậy một c&aacute;i liền hướng tr&ecirc;n &oacute;t m&igrave;nh va, kết quả chỉ c&ograve;n một tiếng &ocirc; h&ocirc;, phần cứng b&ecirc;n trong m&acirc;́y trăm G học tập tư liệu cũng kh&ocirc;ng kịp x&oacute;a liền đổi cảnh tượng.<br />\r\n<br />\r\nTỉnh lại ph&aacute;t hiện người cũng c&ograve;n tốt, kh&ocirc;ng cụt tay thiếu ch&acirc;n, cũng vẫn trường cao trường so&aacute;i, đại v&agrave;o cảm k&eacute;o chật.<br />\r\n<br />\r\nĐương nhi&ecirc;n th&agrave;nh tựu xuy&ecirc;n việt nh&acirc;n sĩ, trong đầu t&agrave;ng c&aacute;i tr&ograve; chơi Quyển Linh, vậy cũng l&agrave; thường quy thao t&aacute;c.<br />\r\n<br />\r\nNgười kh&aacute;c xuy&ecirc;n việt l&agrave; mang l&atilde;o gia gia, mang hệ thống, hắn nhưng l&agrave; mang c&aacute;i th&agrave;nh tinh &aacute;c ma quyển da d&ecirc; trục &mdash;&mdash; Quyển Linh, ở trong game n&oacute; l&agrave; nh&acirc;n vật ch&iacute;nh ma sủng, b&ecirc;n người b&aacute;ch khoa to&agrave;n thư tồn tại.<br />\r\n<br />\r\nNhư l&agrave; nhiệm vụ nhận lấy, ph&eacute;p thuật học tập hoặc l&agrave; c&ugrave;ng Địa ngục vật vật giao dịch, đều l&agrave; th&ocirc;ng qua n&oacute; để ho&agrave;n th&agrave;nh, n&oacute; c&ograve;n online, liền mang &yacute; nghĩa ch&iacute;nh m&igrave;nh phần mềm hack ổn a!<br />\r\n<br />\r\nNhưng m&agrave; Quyển Linh n&oacute; l&atilde;o nh&acirc;n gia online một l&aacute;t sau, n&oacute;i cho sự t&igrave;nh kh&aacute;c kh&ocirc;ng ổn: &quot;Kh&ocirc;ng biết thế giới ho&agrave;n cảnh, ngươi đẳng cấp b&acirc;y giờ thuộc t&iacute;nh thanh linh, mọi việc đ&ecirc;́n cẩn thận một ch&uacute;t, n&ecirc;n cẩu liền cẩu.&quot;<br />\r\n<br />\r\nQuyển Linh ph&aacute;t sinh cảnh c&aacute;o sau, l&agrave;m một t&ecirc;n kế thừa tr&ograve; chơi nghề nghiệp &Aacute;c Ma Ph&aacute;p Sư &mdash;&mdash; ở đ&acirc;y 100% sẽ bị cho rằng ma quỷ dị đoan thả b&agrave;n th&iacute; nghiệm cắt miếng nh&acirc;n sĩ, kh&ocirc;ng qua loa kh&ocirc;ng được a!<br />\r\n<br />\r\nLiền liền cẩn thận như vậy cẩn thận đi nh&agrave; h&agrave;ng bưng một th&aacute;ng m&acirc;m l&agrave;m người phục vụ, l&agrave;m trong t&uacute;i tiền phiếu phiếu theo kh&ocirc;ng kịp ng&agrave;y c&agrave;ng b&agrave;nh trướng ti&ecirc;u ph&iacute; nhu cầu sau, hắn ph&aacute;t hiện cẩu cũng kh&ocirc;ng được a!<br />\r\n<br />\r\nTh&agrave;nh tựu đường đường Địa Ngục Ma Ph&aacute;p Sư, h&ocirc; mưa gọi gi&oacute;, dời n&uacute;i lấp biển những n&agrave;y chi&ecirc;u số đ&atilde; thanh linh, nhưng n&agrave;y thời gian một th&aacute;ng b&ecirc;n trong, hắn vẫn l&agrave; bắt đầu từ con số kh&ocirc;ng học tập quả cầu lửa băng tiễn chờ ch&uacute;t cơ sở kỹ, hắn thi&ecirc;n ph&uacute; kh&ocirc;ng tệ, nắm giữ được cũng rất nhanh, nhưng cũng chỉ c&oacute; thể trốn ở g&oacute;c ph&ograve;ng chơi trộm?<br />\r\n<br />\r\nKh&ocirc;ng khỏi qu&aacute; oan uổng!</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nLiền hắn đi hỏi kiến thức rộng r&atilde;i Quyển Linh:<br />\r\n<br />\r\n&quot;L&agrave;m sao ở một đ&aacute;m Muggle trước mặt trắng trợn kh&ocirc;ng ki&ecirc;ng d&egrave; sử dụng ph&eacute;p thuật m&agrave; kh&ocirc;ng bị nh&igrave;n thấu?&quot;<br />\r\n<br />\r\n&quot;N&oacute;i cho bọn họ biết đ&acirc;y l&agrave; ảo thuật.&quot;<br />\r\n<br />\r\nLecon: &quot;⊙&omega;⊙ &quot;<br />\r\n<br />\r\nDĩ nhi&ecirc;n rất c&oacute; đạo l&yacute; d&aacute;ng vẻ.<br />\r\n<br />\r\nV&igrave; lẽ đ&oacute; Lecon quyết định đổi nghề, bưng m&acirc;m như truyền tới đồng h&agrave;nh trong tai thực sự l&agrave; mất điểm!<br />\r\n<br />\r\nHắn bắt đầu từ h&ocirc;m nay phải l&agrave;m một t&ecirc;n ảo thuật gia!<br />\r\n<br />\r\nTrải qua hắn một th&aacute;ng n&agrave;y ngủ đ&ocirc;ng c&ugrave;ng quan s&aacute;t, thực thế giới cơ bản tr&ecirc;n trước mặt thế l&agrave; như thế, Michael Jackson như cũ l&agrave; lưu h&agrave;nh thi&ecirc;n vương, Justin Bieber vẫn cứ người hiềm cẩu tăng, liền hắn ở vị tr&iacute; th&agrave;nh thị Los Angeles, c&ocirc;ng nghiệp giải tr&iacute; hưng thịnh, truy mộng thanh ni&ecirc;n v&ocirc; số.<br />\r\n<br />\r\nTư bản chủ nghĩa rực rỡ &oacute;ng &aacute;nh ở đ&acirc;y ng&agrave;y đ&ecirc;m tr&igrave;nh diễn, người c&oacute; tiền mở ra Bentley đi Beverley mua sắm, kh&ocirc;ng tiền người chống lều vải trốn trong v&ograve;m cầu cầu sinh.<br />\r\n<br />\r\nỞ đ&acirc;y l&agrave; con ch&oacute;, chỉ cần ngươi c&oacute; tiền ngươi đều l&agrave; minh tinh l&agrave; VIP, người c&oacute; tiền phạm ph&aacute;p c&oacute; lượng lớn luật sư c&oacute; thể c&ugrave;ng ngươi ch&eacute;m gi&oacute;.<br />\r\n<br />\r\nKh&ocirc;ng tiền người da đen ước mơ lắm mồm lắc lư nổi bật hơn mọi người, nhưng m&agrave; một ng&agrave;y hỗn kh&ocirc;ng bằng một ng&agrave;y chỉ c&oacute; thể mua kh&ocirc;ng đồng kết quả c&ograve;n bị một s&uacute;ng ợ hơi th&iacute;!<br />\r\n<br />\r\nLecon chịu đủ lắm rồi loại n&agrave;y gi&agrave;u ngh&egrave;o ch&ecirc;nh lệch, quyết định b&iacute; qu&aacute; h&oacute;a liều kiếm một c&aacute;i USD!<br />\r\n<br />\r\nNói đi nói lại, Quyển Linh cũng n&oacute;i kh&ocirc;ng sai.<br />\r\n<br />\r\nẢo thuật gia l&agrave; hiện hữu d&agrave;n gi&aacute;o dưới, c&oacute; khả năng nhất giải th&iacute;ch một &iacute;t kh&ocirc;ng c&aacute;ch n&agrave;o giải th&iacute;ch thủ đoạn nghề nghiệp.<br />\r\n<br />\r\nD&ugrave; sao người sống s&oacute;t liền muốn đ&uacute;ng l&uacute;c ăn cơm, liền muốn c&ocirc;ng t&aacute;c.<br />\r\n<br />\r\nNgười ở tha hương nơi đất kh&aacute;ch qu&ecirc; người, th&acirc;n kh&ocirc;ng nhất nghệ tinh, chẳng lẽ muốn bưng m&acirc;m đến gi&agrave;?<br />\r\n<br />\r\nLecon r&uacute;t kinh nghiệm xương m&aacute;u, quyết định thả xuống ng&agrave;y xưa cẩn thận, bắt đầu từ ng&agrave;y mai, l&agrave;m một c&aacute;i lưu luyến với phồn hoa nơi ảo thuật gia, b&agrave;y ra tr&ograve; gian chồng chất ph&aacute;p thuật!<br />\r\n<br />\r\nTh&agrave;nh tựu ảo thuật c&ograve;n cần c&acirc;n nhắc đạo cụ c&acirc;n nhắc thủ ph&aacute;p, th&agrave;nh tựu ph&eacute;p thuật n&oacute; chỉ cần thi ph&aacute;p hiện ra, sau đ&oacute; tuy&ecirc;n xưng đ&acirc;y l&agrave; c&aacute;i ảo thuật, đại gia chỉ sẽ cảm thấy c&aacute;i t&ecirc;n n&agrave;y l&agrave; một thi&ecirc;n t&agrave;i!<br />\r\n<br />\r\nQuyển Linh đối với n&agrave;y kh&ocirc;ng c&oacute; &yacute; kiến, n&oacute; hiện đang bận bịu ph&acirc;n t&iacute;ch thế giới n&agrave;y logic mức độ, nỗ lực lẫn v&agrave;o b&ecirc;n trong, để c&agrave;ng tốt hơn lợi dụng quy tắc, cụ thể n&oacute;i rồi hắn cũng kh&ocirc;ng hiểu.<br />\r\n<br />\r\nN&oacute;i chung l&agrave; một chuyện tốt, nhưng chuyện tốt khi n&agrave;o đến kh&ocirc;ng ai n&oacute;i tới chuẩn.<br />\r\n<br />\r\nLecon kh&ocirc;ng c&oacute; thời gian đợi.<br />\r\n<br />\r\nHắn ch&acirc;n trước rời đi ấm &aacute;p nh&agrave; h&agrave;ng, ch&acirc;n sau ngo&agrave;i ph&ograve;ng gi&oacute; lạnh liền nhắm tr&ecirc;n người qu&aacute;n, h&agrave;n &yacute; để hắn nhiệt huyết d&acirc;ng l&ecirc;n đầu &oacute;c tỉnh t&aacute;o th&ecirc;m một ch&uacute;t, nhưng nếu l&agrave;m quyết định, bước ra bước ch&acirc;n ki&ecirc;n quyết l&agrave; sẽ kh&ocirc;ng quay đầu lại.<br />\r\n<br />\r\nNgười ở một c&aacute;i thư th&iacute;ch khu chờ l&acirc;u, liền sẽ h&igrave;nh th&agrave;nh ỷ lại, mất đi thường dũng kh&iacute; thử, hiện tại hắn đi ra, hắn bắt đầu muốn v&igrave; ch&iacute;nh m&igrave;nh t&acirc;n nh&acirc;n sinh l&agrave;m quy hoạch.<br />\r\n<br />\r\nL&agrave;m một c&aacute;i xem David Copperfield như vậy ảo thuật đại sư, nổi danh thế giới, cả thế gian đều biết &mdash;&mdash; Lecon cũng bắt đầu chuẩn bị cho ch&iacute;nh m&igrave;nh đạo cụ l&ecirc;n.<br />\r\n<br />\r\nHiện nay hắn c&oacute; thể l&agrave;m cũng c&oacute; thể kiếm được tiền, chủ yếu ch&iacute;nh l&agrave; ở như l&agrave; nh&agrave; h&agrave;ng, qu&aacute;n bar hoặc l&agrave; tiệc đứng s&acirc;n vận động b&ecirc;n trong tiến h&agrave;nh biểu diễn, lệ ph&iacute; di chuyển một lượng b&aacute;ch đến m&acirc;́y trăm đ&ocirc; la Mỹ kh&ocirc;ng giống nhau.<br />\r\n<br />\r\nNhưng muốn t&igrave;m được phương diện n&agrave;y diễn bỏ t&agrave;i nguy&ecirc;n, chỉ dựa v&agrave;o hai c&aacute;i ch&acirc;n đi tới m&ocirc;n đi hỏi l&agrave; phi thường chậm hiệu suất, mạng lưới l&agrave; đồ tốt, mặt tr&ecirc;n c&oacute; ảo thuật thương mại biểu diễn hiệp hội, ch&uacute;ng n&oacute; thường thường gặp tuy&ecirc;n bố thương mại tờ khai, nhưng cần hội vi&ecirc;n mới c&oacute; thể chia sẻ.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nHội vi&ecirc;n c&ograve;n phải đăng k&iacute; gia nhập hiệp hội, lại đưa trước hai trăm đ&ocirc; la Mỹ, đăng k&iacute; cũng bắt được một c&aacute;i c&aacute;i gọi l&agrave; hiệp hội th&agrave;nh vi&ecirc;n th&acirc;n phận, sau đ&oacute; l&agrave; c&oacute; thể l&agrave;m một t&ecirc;n ảo thuật gia bắt đầu x&ocirc;ng x&aacute;o giang hồ.<br />\r\n<br />\r\nTheo Lecon liền ở nh&agrave; xoạt m&aacute;y vi t&iacute;nh, nh&igrave;n chằm chằm c&oacute; c&aacute;i g&igrave; th&iacute;ch hợp biểu diễn tr&agrave;ng.<br />\r\n<br />\r\nTrong tay đầu đồng thời ở thiết kế tiết mục, khiến cho xem ra c&agrave;ng xem ảo thuật.<br />\r\n<br />\r\nBởi v&igrave; bản th&acirc;n n&oacute; l&agrave; ph&eacute;p thuật, nhưng kh&ocirc;ng thể c&ocirc;ng khai biểu diễn ph&eacute;p thuật, &iacute;t nhất phải học mấy c&aacute;i ảo thuật thủ ph&aacute;p, ph&oacute;ng th&iacute;ch bom kh&oacute;i khiến người ta cảm thấy th&ocirc;i, n&agrave;y ch&iacute;nh l&agrave; lợi hại điểm ảo thuật th&ocirc;i.<br />\r\n<br />\r\nLecon c&oacute; dự kiến trước, theo ch&iacute;nh m&igrave;nh tiếng tăm truyền b&aacute;, s&acirc;n khấu mở rộng, một ng&agrave;y n&agrave;o đ&oacute; gặp mọi người đều biết, như kh&ocirc;ng hề bảo lưu sử dụng ph&eacute;p thuật, ng&agrave;y n&agrave;o đ&oacute; bị hữu t&acirc;m nh&acirc;n nh&igrave;n thấy kẽ hở, vậy ch&iacute;nh l&agrave; ta ở minh địch ở trong tối!<br />\r\n<br />\r\nV&igrave; lẽ đ&oacute; hắn hiện tại suốt đ&ecirc;m quan s&aacute;t ảo thuật vạch trần video, học tập chế tạo tầm mắt điểm m&ugrave; thủ ph&aacute;p, tỷ như đeo ảo thuật nhẫn, mang găng tay chờ ch&uacute;t ảo thuật gia thường d&ugrave;ng c&ocirc;ng cụ.<br />\r\n<br />\r\nSau đ&oacute; ở ch&acirc;n ch&iacute;nh t&aacute;c dụng địa phương, hắn l&agrave; c&oacute; thể triển khai ph&eacute;p thuật, bỗng dưng đem một c&aacute;i đồ vật biến kh&ocirc;ng, để tr&ecirc;n mặt b&agrave;n quả t&aacute;o biến th&agrave;nh trong tủ lạnh chuối ti&ecirc;u, đem giấy trắng biến th&agrave;nh trong t&uacute;i tiền tiền mặt.<br />\r\n<br />\r\nSơ cấp trong ph&eacute;p thuật c&oacute; rất nhiều tr&ograve; hề n&agrave;y, người hiền l&agrave;nh, c&agrave;ng nhiều đưa đến quấy rầy t&aacute;c dụng hoặc l&agrave; giải tr&iacute; t&aacute;c dụng, nhưng ở ng&agrave;y h&ocirc;m nay, n&oacute; ho&agrave;n to&agrave;n c&oacute; thể th&agrave;nh tựu ra kh&ocirc;ng ngờ ảo thuật, diễm kinh b&ocirc;́n to&agrave;!<br />\r\n<br />\r\nLecon đem một c&aacute;i quả t&aacute;o vật l&yacute; biến kh&ocirc;ng sau, liếc mắt m&agrave;n h&igrave;nh.<br />\r\n<br />\r\n&quot;Blackjack qu&aacute;n bar, cần ấm tr&agrave;ng ảo thuật biểu diễn, y&ecirc;u cầu am hiểu đầu đường ảo thuật, s&acirc;n khấu ảo thuật gia, đ&atilde;i ngộ gặp mặt n&oacute;i chuyện, điện thoại xin mời đ&aacute;nh 323-655-5576. . .&quot;<br />\r\n<br />\r\nLecon một b&ecirc;n đọc vừa d&ugrave;ng điện thoại di động đưa v&agrave;o d&atilde;y số, bấm qu&aacute; khứ: &quot;Hello, ta l&agrave; muốn nhận lời mời c&aacute;c ngươi qu&aacute;n bar biểu diễn ảo thuật gia, hiện trường phỏng vấn thật sao? OK, bốn giờ chiều, tốt ta gặp đ&uacute;ng giờ. . .&quot;<br />\r\n<br />\r\nLễ ph&eacute;p chờ đối phương sau khi c&uacute;p điện thoại, Lecon hưng phấn ch&agrave; x&aacute;t tay, việc đến rồi! C&aacute;c Muggle, chờ c&aacute;i nh&igrave;n gia l&agrave;m sao đ&ugrave;a bỡn kh&ocirc;ng thể n&agrave;o!</p>', '2021-12-01 01:19:14', 7, 1),
(274, 72, 'Cái Thứ Nhất Công Tác', '2', '<p>3h chiều, Lecon mở ra nguy&ecirc;n chủ tám ngàn đồng tiền đ&agrave;o đến Honda t&igrave;m tới qu&aacute;n bar.<br />\r\n<br />\r\nNguy&ecirc;n chủ cũng l&agrave; thảm, ra trận phụ mẫu đều mất, tiền dư hai, ba vị mấy trong l&uacute;c đ&oacute; di động, đại học đọc một nửa bởi v&igrave; chưa đ&oacute;ng nổi học ph&iacute; m&agrave; đuổi học, lăn lộn c&aacute;i bưng m&acirc;m c&ocirc;ng t&aacute;c c&ograve;n muốn bị nh&agrave; tư bản nghiền &eacute;p.<br />\r\n<br />\r\nC&oacute; điều n&agrave;y đều l&agrave; qu&aacute; khứ thức, Lecon ngẩng đầu nh&igrave;n mắt Blackjack bảng hiệu, qu&aacute;n bar n&agrave;y quy m&ocirc; rất lớn, ban ng&agrave;y tuy rằng c&ograve;n kh&ocirc;ng kh&aacute;ch h&agrave;ng, nhưng b&ecirc;n trong trang tr&iacute; trang ho&agrave;ng, cũng c&oacute; thể cảm gi&aacute;c được l&agrave; rơi xuống bản.<br />\r\n<br />\r\nĐi vào b&aacute;o cho &yacute; đồ đến sau, liền bị mang về ph&iacute;a sau đ&agrave;i h&agrave;nh lang. Đến đ&acirc;y phỏng vấn kh&ocirc;ng ngừng hắn một c&aacute;i, sau khi nghe ng&oacute;ng mọi người đều l&agrave; ảo thuật gia, đều muốn c&ocirc;ng việc n&agrave;y, bởi v&igrave; nơi n&agrave;y cho gi&aacute; tiền cao.<br />\r\n<br />\r\nLecon tĩnh tọa chờ đợi, ph&iacute;a trước đi v&agrave;o lại đi ra người c&oacute; ủ rũ c&oacute; mất m&aacute;t, xem ra kh&ocirc;ng qu&aacute; lạc quan, chờ gọi v&agrave;o Lecon t&ecirc;n thời điểm, hắn đứng dậy sửa sang một ch&uacute;t quần &aacute;o, cất bước v&agrave;o nh&agrave;.<br />\r\n<br />\r\nPhỏng vấn ch&iacute;nh l&agrave; một t&ecirc;n người da trắng thiếu phụ, bàn ph&aacute;t k&iacute;nh mắt OL trang rất hiện ra kh&iacute; chất, ngẩng đầu nh&igrave;n hắn, sau đ&oacute; n&oacute;i: &quot;Kh&ocirc;ng c&aacute;i g&igrave; muốn n&oacute;i, xin mời biểu diễn một c&aacute;i ngươi sở trường ảo thuật.&quot;<br />\r\n<br />\r\n&quot;Ta cần một c&aacute;i ly!&quot;<br />\r\n<br />\r\nLecon đi g&oacute;c m&aacute;y pha c&agrave; ph&ecirc; b&ecirc;n lấy c&aacute;i gốm sứ ly, cho n&oacute; đổ đầy c&agrave; ph&ecirc;, n&oacute;ng hổi c&ograve;n bốc l&ecirc;n kh&iacute;, đi đến nữ sĩ trước mặt ngồi xuống.<br />\r\n<br />\r\nJudy lạnh l&ugrave;ng nh&igrave;n, nhưng một gi&acirc;y sau nàng liền bỗng nhi&ecirc;n biến sắc.<br />\r\n<br />\r\nLecon trực tiếp đem miệng ch&eacute;n một phen, che ở một c&aacute;i tay kh&aacute;c l&ograve;ng b&agrave;n tay.<br />\r\n<br />\r\nN&oacute;ng bỏng c&agrave; ph&ecirc; cũng kh&ocirc;ng c&oacute; mang đến tưởng tượng nhe răng trợn mắt h&igrave;nh ảnh, Lecon đem ly lật l&ecirc;n b&atilde;i ch&iacute;nh, b&ecirc;n trong đ&atilde; rỗng tuếch.<br />\r\n<br />\r\nSau đ&oacute; hắn biểu diễn một hồi hai tay kh&ocirc;ng c&oacute; mang theo đạo cụ, tiếp theo đem ch&eacute;n c&agrave; ph&ecirc; ph&oacute;ng tới l&ograve;ng b&agrave;n tay tr&aacute;i, sau đ&oacute; tay phải cao bằng l&ograve;ng b&agrave;n tay tăng l&ecirc;n l&ecirc;n, đột nhi&ecirc;n đập xuống đến,<br />\r\n<br />\r\nĐ&ugrave;ng!<br />\r\n<br />\r\nSong chưởng d&aacute;n v&agrave;o, ch&eacute;n c&agrave; ph&ecirc; biến mất kh&ocirc;ng c&ograve;n tăm hơi.<br />\r\n<br />\r\nJudy lập tức tr&ecirc;n dưới kiểm tra nh&igrave;n hắn tay, thậm ch&iacute; c&uacute;i đầu nh&igrave;n đ&aacute;y b&agrave;n, cũng kh&ocirc;ng thể cất đồ vật, ch&eacute;n c&agrave; ph&ecirc; liền như thế biến mất kh&ocirc;ng c&ograve;n tăm hơi?</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;N&oacute; chạy tới chỗ n&agrave;o?&quot;<br />\r\n<br />\r\nLecon giơ giơ l&ecirc;n cằm: &quot;C&oacute; thể nh&igrave;n ngươi bao.&quot;<br />\r\n<br />\r\nNàng th&acirc;m biểu ho&agrave;i nghi đi mở ra ch&iacute;nh m&igrave;nh thả b&ecirc;n cạnh vẫn kh&ocirc;ng ai c&oacute; thể gặp được t&uacute;i LV, k&eacute;o d&agrave;i kh&oacute;a k&eacute;o, v&ocirc; c&ugrave;ng khiếp sợ từ b&ecirc;n trong lấy ra vừa mới c&aacute;i kia ch&eacute;n c&agrave; ph&ecirc;,<br />\r\n<br />\r\n&quot;Ngươi l&agrave; l&agrave;m thế n&agrave;o đến?&quot;<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; ảo thuật gia cơ mật tối cao.&quot;<br />\r\n<br />\r\nLecon đưa tay tiếp nhận ch&eacute;n c&agrave; ph&ecirc;, đặt l&ecirc;n b&agrave;n, một c&aacute;i tay che ở trước mặt, c&agrave; ph&ecirc; liền r&igrave; r&agrave;o hạ xuống, lấp k&iacute;n ly, sau đ&oacute; hắn bưng l&ecirc;n đến tr&ugrave;ng đối phương hỏi thăm, mỉm cười uống một hớp.<br />\r\n<br />\r\nJudy cảm gi&aacute;c thế giới quan đ&atilde; bị lật đổ, nàng c&ograve;n kh&ocirc;ng hết hi vọng truy hỏi: &quot;Đ&acirc;y l&agrave; thật sự? N&agrave;y, sao lại c&oacute; thể như thế nhỉ!&quot;<br />\r\n<br />\r\n&quot;Th&ocirc;ng thường ta gặp đối với kh&aacute;n giả n&oacute;i đ&acirc;y l&agrave; ph&eacute;p thuật.&quot;<br />\r\n<br />\r\nLecon trong nụ cười tr&agrave;n ngập tự tin.<br />\r\n<br />\r\nNghĩa b&oacute;ng, đ&acirc;y ch&iacute;nh l&agrave; cao minh ảo thuật.<br />\r\n<br />\r\nJudy cũng kh&ocirc;ng t&igrave;m được bất kỳ c&oacute; thể từ chối hắn l&yacute; do, chỉ c&ograve;n một c&aacute;i: &quot;Biểu diễn thời gian khoảng 10 ph&uacute;t, c&oacute; hai kh&uacute;c, ngươi hi vọng nắm bao nhi&ecirc;u chi ph&iacute;?&quot;<br />\r\n<br />\r\nN&agrave;y ngược lại l&agrave; l&agrave;m kh&oacute; Lecon, l&agrave; một người ng&agrave;nh nghề t&acirc;n thủ, hắn c&agrave;ng kh&ocirc;ng kh&ocirc;ng đi hiểu r&otilde; đồng h&agrave;nh nắm bao nhi&ecirc;u th&ugrave; lao, bởi v&igrave; theo bản năng cảm thấy th&ocirc;i, đ&acirc;y l&agrave; đối phương định gi&aacute;, ch&iacute;nh m&igrave;nh tiếp thu sự.<br />\r\n<br />\r\nC&oacute; thể Judy kh&ocirc;ng theo s&aacute;o lộ ra b&agrave;i, nàng l&agrave; cảm thấy đ&ecirc;́n c&aacute;i t&ecirc;n n&agrave;y kỹ thuật nghiền &eacute;p tất cả mọi người, thậm ch&iacute; nàng chưa từng gặp như thế quỷ dị ảo thuật, nàng cũng nắm kh&ocirc;ng cho đối phương bảng gi&aacute;.<br />\r\n<br />\r\nHai người n&agrave;y cũng kh&ocirc;ng biết đối phương suy nghĩ, y&ecirc;n tĩnh mấy gi&acirc;y sau, Lecon thử nghiệm n&oacute;i một c&aacute;i mấy: &quot;500 đ&ocirc;la Mỹ?&quot;<br />\r\n<br />\r\n&quot;OK!&quot;<br />\r\n<br />\r\nLecon &aacute;m đạo gọi thiếu.<br />\r\n<br />\r\nCũng kh&ocirc;ng biết hắn lối ra : mở miệng một khắc đ&oacute;, đều chuẩn bị kỹ c&agrave;ng giá cả qu&aacute; cao m&agrave; bị đối phương ch&eacute;m, thực ch&eacute;m một nửa cho hắn 250 cũng c&oacute; thể tiếp thu, thực sự l&agrave; một ph&acirc;n tiền l&agrave;m kh&oacute; anh h&ugrave;ng h&aacute;n, th&aacute;ng sau tiền thu&ecirc; nh&agrave; c&ograve;n kh&ocirc;ng tin tức a!<br />\r\n<br />\r\nThực gi&aacute; tiền n&agrave;y đối với Judy tới n&oacute;i cũng c&oacute; ch&uacute;t cao, thế nhưng nghĩ tới đ&acirc;y đồng hồ đeo tay diễn thực sự qu&aacute; mức kinh người, muốn loại n&agrave;y cấp bậc ảo thuật gia b&igrave;nh thường đều l&agrave; c&oacute; danh tiếng, &iacute;t nhất muốn l&ecirc;n ngàn đ&ocirc;la Mỹ thậm ch&iacute; nhiều hơn mới được.<br />\r\n<br />\r\nCho n&ecirc;n n&agrave;ng cảm gi&aacute;c cuộc trao đổi n&agrave;y c&oacute; thể thử một lần, trước ti&ecirc;n d&ugrave;ng một buổi tối nh&igrave;n kh&aacute;n giả tiếng vọng, nếu như hiệu quả tốt lời n&oacute;i, bọn họ c&ograve;n c&oacute; thể k&eacute;o d&agrave;i hợp t&aacute;c.<br />\r\n<br />\r\nNàng đem c&ocirc;ng t&aacute;c sự hạng n&oacute;i r&otilde;: Tiền lương nhật kết m&agrave; đến tiếp sau hợp t&aacute;c xem qu&aacute;n bar.<br />\r\n<br />\r\nLecon biểu thị đ&ecirc;m nay l&agrave; c&oacute; thể tới l&agrave;m.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; đối với song phương đều c&oacute; chỗ tốt hợp t&aacute;c, thoải m&aacute;i k&yacute; t&ecirc;n nắm tay.<br />\r\n<br />\r\nJudy để trợ thủ th&ocirc;ng b&aacute;o xuống: &quot;Mặt sau kh&ocirc;ng cần phỏng vấn, ch&uacute;ng ta đ&atilde; t&igrave;m tới th&iacute;ch hợp ảo thuật gia.&quot;<br />\r\n<br />\r\nLecon đi rồi, xu&acirc;n phong đắc &yacute; v&ecirc;nh vang đắc &yacute;.<br />\r\n<br />\r\nLiền biết c&oacute; n&agrave;y một tay c&ocirc;ng phu, ta l&agrave;m sao c&oacute; khả năng đ&oacute;i bụng đ&acirc;y!<br />\r\n<br />\r\nPh&iacute;a trước bị Quyển Linh lời n&oacute;i doạ đến, qu&aacute; cẩu đều su&yacute;t ch&uacute;t nữa ch&iacute;nh m&igrave;nh đem m&igrave;nh hố chết!<br />\r\n<br />\r\nL&aacute;i xe về nh&agrave;, tiện đường đi phụ cận si&ecirc;u thị mua điểm nguy&ecirc;n liệu nấu ăn, ng&agrave;y h&ocirc;m nay m&igrave; nhiều hơn hai c&aacute;i trứng c&ugrave;ng một khối b&ograve; b&iacute;t tết, sữa chua kh&ocirc;ng liếm nắp, t&ugrave;y hứng!<br />\r\n<br />\r\nMua xong đồ vật trở về căn hộ, địa phương kh&ocirc;ng lớn, l&agrave; loại kia rất truyền thống ba, b&ocirc;́n tầng cao li&ecirc;n b&agrave;i nơi ở, ph&acirc;n chia kh&ocirc;ng giống gian ph&ograve;ng cho thu&ecirc;, ngư long hỗn tạp c&aacute;c loại mọi người c&oacute;, Hollywood theo đuổi ước mơ nữ h&agrave;i, thị trấn nhỏ đến văn nghệ thanh ni&ecirc;n, thừa dịp tuổi trẻ muốn nhiều m&ograve; điểm **** v&acirc;n v&acirc;n.<br />\r\n<br />\r\nMuốn n&oacute;i chỗ tốt duy nhất ch&iacute;nh l&agrave; c&aacute;ch Hollywood kh&ocirc;ng xa, khoảng c&aacute;ch những minh tinh kia ch&oacute;i lọi thế giới tựa hồ gần trong gang tấc, đưa tay l&agrave; c&oacute; thể chạm tới, nhưng hiện thực thường thường gặp đem bọn họ đ&aacute;nh cho thương t&iacute;ch khắp người, c&oacute; nhan trị c&oacute; t&agrave;i hoa qu&aacute; nhiều người, c&oacute; thể tuyệt đại đa số người như cũ tầm thường v&ocirc; vi.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nLecon ăn hắn xa hoa m&igrave;, miệng lớn thịt b&ograve; v&agrave;o bụng, sau đ&oacute; xem xem tiết mục ti vi, ca vũ biểu diễn talent show h&agrave;ng năm đều c&oacute;, ước ao một hồi những vận may n&agrave;y v&ocirc; c&ugrave;ng tốt gia hỏa.<br />\r\n<br />\r\nNhưng tạm thời kh&ocirc;ng c&oacute; quan hệ g&igrave; với ch&iacute;nh m&igrave;nh, trước ti&ecirc;n kiếm một b&uacute;t nhanh tiền, sau đ&oacute; sẽ c&acirc;n nhắc đi như thế n&agrave;o n&agrave;y điều ảo thuật thi&ecirc;n vương si&ecirc;u sao con đường. Hắn đời n&agrave;y l&agrave;m c&ocirc;ng l&agrave; kh&ocirc;ng thể, c&oacute; cơ hội nhất định phải nắm chặt.<br />\r\n<br />\r\nĂn nghĩ, nhưng cũng kh&ocirc;ng c&aacute;i cụ thể manh mối.<br />\r\n<br />\r\nNh&igrave;n xuống thời gian c&ograve;n sớm, liền luyện tập mấy c&aacute;i buổi tối muốn biểu diễn &quot;Ảo thuật&quot;, nhiều lần mấy lần thuận lợi mới thả xuống đồ vật, đi tắm rửa sạch sẽ, thay quần &aacute;o, tinh thần chấn hưng chuẩn bị đi l&agrave;m.<br />\r\n<br />\r\nNgười Mỹ sống về đ&ecirc;m kh&ocirc;ng bằng ch&uacute;ng ta Hoa Hạ rực rỡ đa dạng, chủ yếu liền tập trung v&agrave;o c&aacute;c loại qu&aacute;n bar, c&acirc;u lạc bộ đ&ecirc;m b&ecirc;n trong, đ&ecirc;m tối khuya khoắt tr&ecirc;n đường phố loạn lắc l&agrave; kh&ocirc;ng tồn tại, ngươi sơ &yacute; một ch&uacute;t liền c&oacute; thể c&oacute; thể bị một nh&oacute;m l&atilde;o Hắc cướp.<br />\r\n<br />\r\nĐa số cửa h&agrave;ng rất sớm cũng đ&atilde; đ&oacute;ng cửa, người v&ocirc; gia cư bắt đầu qua lại hoạt động, tình cờ c&oacute; ch&uacute;t c&ograve;n giao lộ đ&oacute;n xe xin tiền, khiến người ta kh&ocirc;ng d&aacute;m tin tưởng đ&acirc;y l&agrave; nước Mỹ th&agrave;nh phố lớn thứ hai.<br />\r\n<br />\r\nM&atilde;i đến tận Sunset Boulevard mới tốt l&ecirc;n, b&ecirc;n n&agrave;y l&agrave; trị an t&ocirc;́t hơn khu nh&agrave; gi&agrave;u, cảnh lực d&ocirc;̀i dào, tuần tra kh&ocirc;ng ngừng, tự nhi&ecirc;n những người c&oacute; tiền kia mới c&oacute; thể c&agrave;ng an t&acirc;m qua lại ti&ecirc;u ph&iacute;.<br />\r\n<br />\r\nLecon đem xe ngừng được, từ c&ocirc;ng nh&acirc;n v&agrave;o miệng : lối v&agrave;o đi đến ng&agrave;y kia ph&ograve;ng nghỉ ngơi, tr&ecirc;n đường nh&igrave;n thấy Judy, vị n&agrave;y gi&agrave; giặn nữ sĩ ch&iacute;nh đang v&igrave; l&agrave; tiết mục buổi tối bận rộn, sắp xếp c&aacute;i n&agrave;y ban nhạc, c&aacute;i kia ca sĩ.<br />\r\n<br />\r\nSau đ&oacute; cũng sắp xếp Lecon: &quot;Ngươi ở tại bọn hắn diễn xuất xong xu&ocirc;i sau l&ecirc;n đ&agrave;i, tốt nhất khống chế thời gian ở mười ph&uacute;t, nếu như kh&aacute;n giả tiếng vọng nhiệt liệt, ngươi cũng c&oacute; thể ngẫu hứng ph&aacute;t huy.&quot;<br />\r\n<br />\r\n&quot;Th&ecirc;m ra đến thời gian t&iacute;nh tiền sao?&quot;<br />\r\n<br />\r\nLecon hỏi.<br />\r\n<br />\r\nJudy ngạc nhi&ecirc;n nghi ngờ liếc nh&igrave;n hắn hai mắt, sau đ&oacute; nở nụ cười: &quot;Xem ra ngươi đối với m&igrave;nh tương đương c&oacute; tự tin, t&iacute;nh tiền, nếu như ngươi biểu diễn đầy đủ đặc sắc lời n&oacute;i.&quot;<br />\r\n<br />\r\n&quot;OK, ta sẽ tận lực biểu hiện!&quot;</p>', '2021-12-01 01:19:33', 5, 1),
(275, 72, 'Khống Chế Toàn Trường', '3', '<p>Theo b&oacute;ng đ&ecirc;m dần th&acirc;m, qu&aacute;n bar bắt đầu n&aacute;o nhiệt l&ecirc;n.<br />\r\n<br />\r\nN&oacute; tọa lạc ở Sunset Boulevard b&ecirc;n trong, vị tr&iacute; Hollywood trung t&acirc;m, lại đi hướng t&acirc;y vừa đi ch&iacute;nh l&agrave; đại danh đỉnh đỉnh Beverly Hills, v&ugrave;ng n&agrave;y tất cả đều l&agrave; qu&aacute;n bar c&acirc;u lạc bộ đ&ecirc;m c&ugrave;ng xa hoa nh&agrave; h&agrave;ng, cũng l&agrave; số &iacute;t buổi tối gặp rực rỡ đoạn đường.<br />\r\n<br />\r\nNhững người gi&agrave;u c&oacute; kia khu c&oacute; tiền l&atilde;o bạch, đi ra t&igrave;m th&uacute; vui minh tinh danh lưu, liền bắt đầu chen ch&uacute;c m&agrave; tới, đến hưởng thụ bọn họ sống về đ&ecirc;m, đi l&agrave;m l&agrave; cao cấp c&acirc;u lạc bộ đ&ecirc;m VIP ngang ngược.<br />\r\n<br />\r\nBlackjack qu&aacute;n bar, c&aacute;i kia một loạt cao gầy mỹ lệ người nữ phục vụ cũng đ&atilde; sắp xếp, c&aacute;c n&agrave;ng bưng m&acirc;m qua lại ở trong đ&aacute;m người, chịu đựng những người đ&agrave;n &ocirc;ng kia cực n&oacute;ng ch&uacute; &yacute; lễ, ở tiếng cười cười n&oacute;i n&oacute;i b&ecirc;n trong kh&ocirc;ng ch&uacute;t lưu t&igrave;nh cướp đi tiền boa của bọn họ.<br />\r\n<br />\r\nLecon ở s&acirc;n khấu b&ecirc;n cạnh đường nối, lẳng lặng nh&igrave;n t&igrave;nh cảnh n&agrave;y.<br />\r\n<br />\r\nQu&aacute;n bar biểu diễn tức sắp bắt đầu rồi, ban nhạc đ&atilde; l&ecirc;n đ&agrave;i, y&ecirc;n tảng đại tay hoa nữ h&aacute;t ch&iacute;nh &ocirc;m đ&agrave;n ghita, thuần thục gảy c&aacute;i kh&uacute;c nhạc dạo, l&agrave; ở chỗ đ&oacute; th&iacute;ch thả cuộc đời của bọn họ cảm kh&aacute;i hoặc l&agrave; hận đời.<br />\r\n<br />\r\nKh&ocirc;ng n&oacute;i những c&aacute;i kh&aacute;c, tr&igrave;nh độ vẫn đ&uacute;ng l&agrave; rất cao, c&oacute; điều lường trước nơi n&agrave;y l&agrave; Hollywood, to&agrave;n thế giới giải tr&iacute; trung t&acirc;m, Kim Tự Th&aacute;p đỉnh đều d&ograve;ng người dũng di chuyển, người ưu t&uacute; c&aacute;i kia đ&uacute;ng l&agrave; nhiều như ch&oacute;.<br />\r\n<br />\r\n&quot;Lecon! Chuẩn bị đến ngươi!&quot;<br />\r\n<br />\r\nBan nhạc nửa giờ l&agrave;m n&oacute;ng s&acirc;n đ&atilde; kết th&uacute;c, Judy trợ thủ đến th&ocirc;ng b&aacute;o hắn chuẩn bị l&ecirc;n đ&agrave;i.<br />\r\n<br />\r\nLecon mặc v&agrave;o hắn ảo thuật gia lễ phục, liền đứng dậy theo trợ thủ rời đi ph&ograve;ng nghỉ ngơi.<br />\r\n<br />\r\nLần thứ nhất l&ecirc;n đ&agrave;i, nh&igrave;n dưới đ&agrave;i một đ&aacute;m &aacute;o mũ chỉnh tề kh&aacute;ch h&agrave;ng, căng thẳng kh&oacute; tr&aacute;nh khỏi gặp c&oacute;, c&oacute; điều kiếp trước cũng kh&ocirc;ng ít ở c&ocirc;ng ty trong hội nghị bị l&atilde;nh đạo điểm danh ph&ecirc; b&igrave;nh, nội t&acirc;m đ&atilde; đủ mạnh.<br />\r\n<br />\r\nH&iacute;t s&acirc;u một hơi, Lecon mặt mỉm cười: &quot;Nữ sĩ c&aacute;c ti&ecirc;n sinh, rất vinh hạnh đứng ở chỗ n&agrave;y, ng&agrave;y h&ocirc;m nay ta muốn cho đại gia biểu diễn c&aacute;i b&agrave;i Poker ảo thuật.&quot;<br />\r\n<br />\r\nLecon vừa n&oacute;i vừa sờ t&uacute;i, nhưng hơi nhướng m&agrave;y, c&ograve;n vỗ vỗ tr&ecirc;n người hắn vị tr&iacute;.<br />\r\n<br />\r\nỞ một b&ecirc;n quan s&aacute;t Judy t&acirc;m cũng theo nhắc tới : nhấc l&ecirc;n, c&aacute;i t&ecirc;n n&agrave;y sẽ kh&ocirc;ng l&agrave; qu&ecirc;n đồ vật chứ?<br />\r\n<br />\r\nGiờ khắc này tuy rằng kh&ocirc;ng phải sở hữu kh&aacute;ch h&agrave;ng đều nh&igrave;n s&acirc;n khấu, nhưng &iacute;t nhất c&oacute; một nửa sự ch&uacute; &yacute; ở ph&iacute;a tr&ecirc;n, trước mặt nhiều người như vậy, Lecon cười cợt.<br />\r\n<br />\r\n&quot;Xin lỗi sự t&igrave;nh c&oacute; ch&uacute;t bất ngờ nho nhỏ, ta cần c&ugrave;ng ph&iacute;a trước vị n&agrave;y đẹp đẽ nữ sĩ mượn một tấm khăn giấy, OK, cảm tạ ngươi h&ugrave;ng hồn!&quot;<br />\r\n<br />\r\nLecon trước mặt b&agrave;i kh&aacute;ch h&agrave;ng mượn một tấm khăn giấy, tới tay sẽ theo liền vung một c&aacute;i, khăn giấy nhất thời biến th&agrave;nh trong tay đầu một bộ b&agrave;i Poker, khoảng c&aacute;ch gần như vậy biểu diễn, thực tại để n&agrave;y một b&agrave;n t&igrave;nh nh&acirc;n kinh ngạc đến ng&acirc;y người.<br />\r\n<br />\r\nLecon cho kh&aacute;n giả biểu diễn Poker, một b&ecirc;n Judy nh&acirc;́c theo t&acirc;m cũng theo bu&ocirc;ng ra, tiểu tử n&agrave;y!<br />\r\n<br />\r\nC&oacute; điều biểu diễn thi&ecirc;n ph&uacute; ngược lại kh&ocirc;ng tệ, thậm ch&iacute; ngay cả ch&iacute;nh m&igrave;nh cũng đ&atilde; lừa gạt đi tới.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nC&oacute; mở m&agrave;n chi&ecirc;u n&agrave;y kinh diễm, hiện tại c&oacute; c&agrave;ng nhiều người sự ch&uacute; &yacute; đi đến Lecon tr&ecirc;n người, hắn c&aacute; nh&acirc;n s&acirc;n diễn bắt đầu rồi!<br />\r\n<br />\r\nHắn để ph&iacute;a trước một vị kh&aacute;c kh&aacute;ch h&agrave;ng tuyển một tấm b&agrave;i Poker, ở ph&iacute;a tr&ecirc;n k&iacute; t&ecirc;n.<br />\r\n<br />\r\nSau đ&oacute; Lecon ở trước mặt tất cả mọi người, đem b&agrave;i Poker x&eacute; th&agrave;nh mảnh vỡ, n&eacute;m tới trong miệng, nhai hai lần dĩ nhi&ecirc;n ho khan l&ecirc;n, kẹt yết hầu!<br />\r\n<br />\r\n&quot;Nhanh cho ta một c&aacute;i trống rỗng ly!&quot;<br />\r\n<br />\r\nHắn từ b&ecirc;n cạnh trải qua người nữ phục vụ trong tay tiếp nhận một c&aacute;i trống rỗng ly, tay run l&ecirc;n, đốn trong thời gian đựng nước kho&aacute;ng, ngửa đầu cho m&igrave;nh uống hai ngụm, mới rốt cục vỗ ngực một c&aacute;i nuốt xuống.<br />\r\n<br />\r\nKh&aacute;n giả l&uacute;c n&agrave;y đ&atilde; hết sức chăm ch&uacute;.<br />\r\n<br />\r\nLecon đem c&ograve;n lại b&agrave;i Poker cho ph&iacute;a trước một b&agrave;n kiểm tra, để hắn x&aacute;c định kh&ocirc;ng c&oacute; k&iacute; t&ecirc;n tấm kia 7 &diams;.<br />\r\n<br />\r\n&quot;Bởi v&igrave; ta mới vừa mới đem n&oacute; ăn, su&yacute;t ch&uacute;t nữa kh&ocirc;ng đem ta nghẹn.&quot;<br />\r\n<br />\r\nDưới đ&agrave;i ch&iacute;nh l&agrave; gợi ra li&ecirc;n tiếp tiếng cười.<br />\r\n<br />\r\nLecon &aacute;nh mắt nh&igrave;n quanh hiện trường, hiện tại &aacute;nh mắt của mọi người đều ở tr&ecirc;n người hắn, đến hiện tại, hắn cũng kh&ocirc;ng c&oacute; s&acirc;n khấu cảm gi&aacute;c căng thẳng, m&agrave; l&agrave; một loại đều ở chấp chưởng, hưởng thụ ch&uacute; &yacute; vui sướng.<br />\r\n<br />\r\n&quot;C&aacute;c ngươi d&aacute;m tin tưởng, ta th&ocirc;ng qua ph&eacute;p thuật thủ đoạn, đưa n&oacute; chuyển đến trong điện thoại di động của ta sao?&quot;<br />\r\n<br />\r\nLecon n&oacute;i lấy ra điện thoại di động của ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\nMặt hướng kh&aacute;n giả, cũng mở ra album.<br />\r\n<br />\r\nB&ecirc;n trong bức ảnh đầu ti&ecirc;n ch&iacute;nh l&agrave; vừa n&atilde;y b&agrave;i Poker 7 &diams;, mặt tr&ecirc;n c&ograve;n c&oacute; kh&aacute;ch h&agrave;ng k&iacute; t&ecirc;n.<br />\r\n<br />\r\nT&ecirc;n kia người qua đường nam kh&aacute;ch h&agrave;ng kinh ngạc nh&igrave;n: &quot;Đ&acirc;y ch&iacute;nh l&agrave; ta k&iacute; t&ecirc;n kh&ocirc;ng sai, ừ! Ta xin thề, ta với hắn kh&ocirc;ng phải một nh&oacute;m!&quot;<br />\r\n<br />\r\nHiện trường mọi người lần thứ hai nở nụ cười, c&oacute; người tin tưởng cũng c&oacute; người cảm thấy phải l&agrave; th&aacute;c, thế nhưng cũng kh&ocirc;ng trở ngại n&agrave;y rất thần kỳ một m&agrave;n tiếp tục tr&igrave;nh diễn.<br />\r\n<br />\r\n&quot;N&oacute; ở trong điện thoại di động, ta phải nghĩ biện ph&aacute;p lấy ra.&quot;<br />\r\n<br />\r\nLecon suy nghĩ một ch&uacute;t, đưa điện thoại di động mặt hướng kh&aacute;n giả, một c&aacute;i tay theo : ấn ở tr&ecirc;n m&agrave;n ảnh, đột nhi&ecirc;n hướng về phải hoa, k&iacute; t&ecirc;n 7 &diams; liền xuất hiện ở trong tay hắn!<br />\r\n<br />\r\nHiện trường kinh ngạc thanh kh&ocirc;ng ngừng, Lecon đem 7 &diams; giao cho t&ecirc;n kia ti&ecirc;n sinh: &quot;Xin ngươi d&ugrave;ng cơm đĩa đưa n&oacute; đ&egrave; ở ph&iacute;a dưới, n&oacute; c&oacute; ch&uacute;t nghịch ngợm, đừng cho chạy loạn.&quot;<br />\r\n<br />\r\nKh&aacute;ch h&agrave;ng rất c&oacute; tham dự cảm nghe theo, chu vi cũng l&agrave; một đ&aacute;m tham đầu n&agrave;y sang đ&acirc;y xem hiếu kỳ kh&aacute;n giả.<br />\r\n<br />\r\n&quot;Ừ kh&ocirc;ng!&quot;<br />\r\n<br />\r\nLecon đột nhi&ecirc;n l&ecirc;n tiếng,<br />\r\n<br />\r\n&quot;Ngươi kh&ocirc;ng đắp k&iacute;n, n&oacute; chạy!&quot;<br />\r\n<br />\r\nVị kia kh&aacute;ch h&agrave;ng lập tức mở ra m&oacute;n ăn đĩa, ph&aacute;t hiện b&agrave;i Poker bỗng nhi&ecirc;n biến mất, chỉ c&ograve;n dư lại một tấm khăn giấy!<br />\r\n<br />\r\n&quot;WHAT?&quot;<br />\r\n<br />\r\n&quot;Chuyện n&agrave;y làm sao l&agrave;m được?&quot;<br />\r\n<br />\r\nBang này b&igrave;nh thường kh&ocirc;ng phải c&ocirc;ng ty cao quản ch&iacute;nh l&agrave; về hưu người c&oacute; tiền gia hỏa, b&acirc;y giờ bị Lecon n&agrave;y một tay m&ecirc; đ&ecirc;́n nh&igrave;n chằm chằm kh&ocirc;ng chớp mắt, rượu đều đ&atilde; qu&ecirc;n uống.<br />\r\n<br />\r\nLiền ngay cả một b&ecirc;n Judy đều kh&ocirc;ng kiềm chế nổi hiếu kỳ, thay đổi mọi khi &ocirc;m c&aacute;nh tay khống tr&agrave;ng th&aacute;i độ, cũng đều tập hợp nh&igrave;n l&ecirc;n, m&atilde;i đến tận trợ thủ th&ocirc;ng b&aacute;o nàng: &quot;Thời gian sắp đến rồi nữ sĩ.&quot;<br />\r\n<br />\r\n&quot;Để hắn biểu diễn th&ocirc;i.&quot; Judy kh&ocirc;ng ch&uacute;t do dự n&oacute;i, &quot;Nh&igrave;n t&igrave;nh h&igrave;nh b&acirc;y giờ, nếu như ch&uacute;ng ta nửa đường đổi tiết mục, kh&aacute;ch h&agrave;ng gặp tạo phản!&quot;<br />\r\n<br />\r\nTr&ecirc;n thực tế x&aacute;c thực kh&ocirc;ng thể k&igrave;m được qu&aacute;n bar sắp xếp tiết mục.<br />\r\n<br />\r\nLecon n&agrave;y li&ecirc;n tiếp ảo thuật, th&iacute;ch l&agrave;m g&igrave; th&igrave; l&agrave;m, lại ra kh&ocirc;ng ngờ, c&oacute; thể n&oacute;i chỉ cần hắn nghĩ, hắn liền c&oacute; thể biến đến ng&agrave;y thứ hai hừng đ&ocirc;ng, ngược lại l&agrave; ph&eacute;p thuật xiếc, c&oacute; thể chơi bao l&acirc;u xem ch&iacute;nh l&agrave; MP trị.<br />\r\n<br />\r\nNhưng bởi v&igrave; những n&agrave;y tr&ograve; vặt h&aacute;o ma rất thấp, tr&ecirc;n căn bản ch&iacute;nh m&igrave;nh cũng c&oacute; thể kh&ocirc;i phục như cũ, v&igrave; lẽ đ&oacute; tr&ecirc;n l&yacute; thuyết, chơi c&aacute;i ba ng&agrave;y ba đ&ecirc;m cũng l&agrave; c&oacute; thể.<br />\r\n<br />\r\nHơn nữa nhổ l&ocirc;ng cừu m&agrave;, Judy đ&aacute;p ứng rồi n&oacute;i biểu diễn nhiều thời giờ th&ecirc;m tiền, vậy cũng chớ tr&aacute;ch ta bắt lấy ngươi đem quần l&oacute;t đều hao kh&ocirc;ng rồi!<br />\r\n<br />\r\nLecon ảo thuật vẫn c&ograve;n tiếp tục.<br />\r\n<br />\r\nNghịch ngợm 7 &diams; chạy tới chạy lui, thậm ch&iacute; xuất hiện ở trung gian một vị ti&ecirc;n sinh trong v&iacute; tiền, mở ra sau hắn cũng l&agrave; một mặt cho&aacute;ng v&aacute;ng, sau đ&oacute; cười l&ecirc;n: &quot;Ta nghĩ ta liền kh&ocirc;ng cần tự chứng kh&ocirc;ng phải lấy chứ?&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nNgười chung quanh đều cười ha ha,<br />\r\n<br />\r\n&quot;Mike, ta tin tưởng ngươi kh&ocirc;ng n&agrave;y cần phải!&quot;<br />\r\n<br />\r\n&quot;Hảo xiếc! Ngươi c&oacute; thể đem b&agrave;i Poker biến th&agrave;nh tiền mặt sao?&quot;<br />\r\n<br />\r\nLecon kh&ocirc;ng quen biết đối phương, nhưng xem mọi người phản ứng, c&aacute;i t&ecirc;n n&agrave;y hẳn l&agrave; c&oacute; ch&uacute;t tiếng tăm, kh&ocirc;ng thể l&agrave; ch&iacute;nh m&igrave;nh một c&aacute;i v&ocirc; danh nh&acirc;n vật l&agrave;m th&aacute;c.<br />\r\n<br />\r\nNếu ngươi c&oacute; danh tiếng. . . Lecon cũng kh&ocirc;ng c&oacute; cụ thể quy tr&igrave;nh ảo thuật, liền chuyển đến tr&ecirc;n người đối phương: &quot;Nếu như ti&ecirc;n sinh cho ta một đ&ocirc;la Mỹ tiền mặt, ta liền c&oacute; thể đem n&oacute; biến th&agrave;nh hai tấm.&quot;<br />\r\n<br />\r\nT&ecirc;n l&agrave; Mike người rất thoải m&aacute;i lấy ra một tấm một đ&ocirc;la Mỹ, Lecon tay tr&aacute;i tiếp tiền cũng giao cho tay phải, tay phải lại đưa trả lại cho hắn hai tấm một đ&ocirc;la Mỹ.<br />\r\n<br />\r\nMike cầm v&iacute; tiền động t&aacute;c dừng lại, sau đ&oacute; hắn ph&acirc;n cao thấp từ b&ecirc;n trong lại lấy ra một tờ năm đ&ocirc;la Mỹ: &quot;Ngươi c&ograve;n c&oacute; thể sao?&quot;<br />\r\n<br />\r\nLecon tiếp tục tay tr&aacute;i đổi tay phải, hai tấm năm đ&ocirc;la Mỹ trả lại hắn.<br />\r\n<br />\r\nNước chảy m&acirc;y tr&ocirc;i kh&ocirc;ng mang theo điểm dừng lại.<br />\r\n<br />\r\nMike giơ tay vỗ tay, nhưng thấy ảo thuật gia c&ograve;n kh&ocirc;ng rời đi, hắn đột nhi&ecirc;n c&oacute; tr&ograve; đ&ugrave;a dai trong l&ograve;ng, đem trong v&iacute; tiền tiền mặt tất cả lấy ra: &quot;Ta chỗ n&agrave;y c&oacute; m&ocirc;̣t ngàn đ&ocirc;la Mỹ, nếu như ngươi c&oacute; thể &mdash;&mdash; &quot;<br />\r\n<br />\r\nLecon tay tr&aacute;i tiếp tiền giao cho tay phải, hai tay hợp lại, lại mở ra tay đ&atilde; là rỗng tuếch.<br />\r\n<br />\r\nMike trợn mắt ngo&aacute;c mồm, Lecon nhấc tay mặt hướng kh&aacute;n giả biểu diễn, sau đ&oacute; chuyện cười: &quot;Xin lỗi ti&ecirc;n sinh, ng&agrave;i kh&ocirc;ng n&ecirc;n d&ugrave;ng tiền t&agrave;i đến thử th&aacute;ch l&ograve;ng người!&quot;<br />\r\n<br />\r\n&quot;Ha ha ha ha!&quot;<br />\r\n<br />\r\nTo&agrave;n trường cười vang, tiếng vỗ tay kh&ocirc;ng ngừng, thậm ch&iacute; b&ecirc;n ngo&agrave;i c&aacute;ch kh&aacute; xa một &iacute;t kh&aacute;n giả v&igrave; nhìn rõ ràng, c&ograve;n đứng l&ecirc;n, ph&oacute;ng tầm mắt tới, kh&ocirc;ng muốn bỏ qua nơi n&agrave;y đặc sắc.<br />\r\n<br />\r\nJudy bất tri bất gi&aacute;c cũng chen ở xem cuộc vui trong đ&aacute;m người, th&agrave;nh tựu qu&aacute;n bar hoạt động quản l&yacute;, nàng giờ khắc này thậm ch&iacute; đều qu&ecirc;n c&ograve;n c&oacute; ca sĩ đ&atilde; ở ph&iacute;a sau đ&agrave;i khổ sở chờ đợi 20 ph&uacute;t!<br />\r\n<br />\r\nNhưng l&uacute;c n&agrave;y kh&ocirc;ng người quan t&acirc;m, bọn họ ch&iacute;nh l&agrave; muốn xem ảo thuật, muốn thưởng thức n&agrave;y ma huyễn qu&aacute; tr&igrave;nh c&ugrave;ng vị n&agrave;y c&oacute; thể chế tạo v&ocirc; hạn kh&ocirc;ng thể ảo thuật gia!</p>', '2021-12-01 01:19:52', 5, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(276, 72, 'Thực Lực Tạo Nên Cơ Hội', '4', '<p>Kh&aacute;n giả nhiệt liệt cũng kh&ocirc;ng ra Lecon dự liệu.<br />\r\n<br />\r\nC&aacute;c Muggle ho&agrave;n to&agrave;n say sưa ở n&agrave;y ma huyễn biểu diễn b&ecirc;n trong, bọn họ hay l&agrave; xem qua một &iacute;t giống thật m&agrave; l&agrave; giả biểu diễn, khả năng ở tr&ecirc;n ti vi, hoặc l&agrave; ở hiện thực n&oacute; ảo thuật biểu diễn b&ecirc;n trong xem qua.<br />\r\n<br />\r\nNhưng tuyệt đối kh&ocirc;ng c&oacute; c&aacute;i n&agrave;o một lần c&oacute; thể xem Lecon như vậy, th&iacute;ch l&agrave;m g&igrave; th&igrave; l&agrave;m, thi&ecirc;n m&atilde; h&agrave;nh kh&ocirc;ng ph&aacute;t huy!<br />\r\n<br />\r\nD&ugrave; cho tr&ecirc;n đường c&oacute; ch&uacute;t kh&aacute;n giả bất ngờ ra đề mục, lại như vừa n&atilde;y Mike lấy ra sở hữu tiền mặt cảnh tượng, những kh&aacute;c ảo thuật gia sợ l&agrave; cũng bị l&agrave;m kh&oacute;, đối mặt tẻ ngắt.<br />\r\n<br />\r\nNhưng Lecon liền miễn cưỡng ăn, c&ograve;n thắng được to&agrave;n trường đẹp đẽ hoan h&ocirc;, nhất sau, hắn vẫn l&agrave; thần kỳ đem tiền mặt một lần nữa biến trở về đối phương trong v&iacute; tiền, thu được cả sảnh đường ủng hộ!<br />\r\n<br />\r\nLecon ở tiếng vỗ tay c&ugrave;ng ch&uacute; &yacute; lui trở về tr&ecirc;n s&agrave;n nhảy, hướng mọi người c&uacute;i m&igrave;nh v&aacute;i ch&agrave;o.<br />\r\n<br />\r\n&quot;Rất cao hứng ta biểu diễn c&oacute; thể thắng c&aacute;c ngươi tiếng vỗ tay c&ugrave;ng ủng hộ, đ&acirc;y đối với ta một c&aacute;i từ trấn nhỏ đến truy mộng thanh ni&ecirc;n, l&agrave; lớn lao cổ vũ, cảm tạ c&aacute;c ngươi!&quot;<br />\r\n<br />\r\nỞ sấm dậy tiếng vỗ tay b&ecirc;n trong, Lecon lần thứ hai hướng th&iacute;nh ph&ograve;ng c&uacute;c cung, sau đ&oacute; phất tay một c&aacute;i, trở về hậu trường.<br />\r\n<br />\r\nỞ ph&iacute;a sau đ&agrave;i ph&ograve;ng h&oacute;a trang, Judy rất nhanh sẽ xuất hiện,<br />\r\n<br />\r\n&quot;L&agrave;m được qu&aacute; xinh đẹp tiểu tử! Ngươi tuyệt kh&ocirc;ng biết ch&iacute;nh m&igrave;nh cho mặt sau biểu diễn bao lớn &aacute;p lực!&quot;<br />\r\n<br />\r\n&quot;Nhưng đ&acirc;y l&agrave; ta đ&atilde; thấy đặc sắc nhất ảo thuật!&quot;<br />\r\n<br />\r\nJudy kh&ocirc;ng c&oacute; tiếc rẻ ca ngợi, vị n&agrave;y b&aacute;n l&atilde;o từ nương tr&ecirc;n mặt tr&agrave;n trề vui sướng c&ugrave;ng thưởng thức, trong đầu cũng đang t&iacute;nh to&aacute;n l&agrave;m sao c&agrave;ng tốt hơn triển khai bọn họ hợp t&aacute;c.<br />\r\n<br />\r\nD&ugrave; sao vừa n&atilde;y n&aacute;o nhiệt nàng nh&igrave;n ở trong mắt, nếu như c&oacute; thể thường thường c&oacute; như vậy ảo thuật t&uacute;, qu&aacute;n bar nh&acirc;n kh&iacute; tuyệt đối sẽ c&agrave;ng tr&ecirc;n một nấc thang, ở một đ&aacute;m đồng h&agrave;nh b&ecirc;n trong bộc lộ t&agrave;i năng.<br />\r\n<br />\r\nV&igrave; lẽ đ&oacute; hiện tại ch&iacute;nh l&agrave; đến thấy sang bắt qu&agrave;ng l&agrave;m họ, bắt đầu với hắn hỏi han &acirc;n cần, hiểu r&otilde; hiện trạng của hắn, biết được hắn muốn đi giải tr&iacute; con đường, liền đưa ra kiến nghị: &quot;Ở Hollywood t&ugrave;y tiện một g&oacute;c đều c&oacute; thể t&igrave;m tới truy mộng người, nhưng kh&ocirc;ng phải l&agrave; người n&agrave;o đều c&oacute; thể c&oacute; cơ hội, ngươi phải c&oacute; th&iacute;ch hợp b&igrave;nh đ&agrave;i, c&oacute; c&agrave;ng dễ d&agrave;ng đụng tới danh lưu x&aacute;c suất. . .&quot;<br />\r\n<br />\r\nNghĩa b&oacute;ng Lecon nghe được, bọn họ Blackjack qu&aacute;n bar vị tr&iacute; được, kh&aacute;ch h&agrave;ng chất lượng cao, thường thường c&oacute; minh tinh danh lưu qua lại, ngươi tới đ&acirc;y chuẩn kh&ocirc;ng sai.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nN&oacute;i đều như vậy n&oacute;i rồi, Lecon đương nhi&ecirc;n liền mừng rỡ thảo luận một hồi hợp t&aacute;c c&ocirc;ng việc: &quot;Ta rất cảm tạ Judy cho cơ hội của ta, v&igrave; lẽ đ&oacute; ở th&ugrave; lao th&iacute;ch hợp t&igrave;nh huống, ta rất t&igrave;nh nguyện lưu lại nơi n&agrave;y.&quot;<br />\r\n<br />\r\nJudy mỉm cười: &quot;Ta rất sớm đ&atilde; nh&igrave;n ra tr&ecirc;n người ngươi t&agrave;i hoa, cũng rất cao hứng ngươi nhanh như vậy liền thả ra ngo&agrave;i, v&igrave; lẽ đ&oacute; ta c&oacute; thể cho đến ngươi 1000 đ&ocirc;la Mỹ lệ ph&iacute; di chuyển, đ&acirc;y l&agrave; đ&atilde; th&agrave;nh danh ảo thuật gia tr&igrave;nh độ, ta kh&ocirc;ng chiếm tiện nghi của ngươi, cũng hi vọng ngươi c&oacute; thể nhanh ch&oacute;ng th&agrave;nh danh.&quot;<br />\r\n<br />\r\nC&ograve;n kh&ocirc;ng th&agrave;nh danh liền cho ta c&aacute;i gi&aacute; n&agrave;y, nghe tới rất ph&uacute;c hậu, nhưng Lecon lần trước sau khi trở về từng điều tra th&ugrave; lao tham khảo ti&ecirc;u chuẩn, Judy nữ sĩ quả thật c&oacute; ch&uacute;t mưu t&iacute;nh nhỏ ở b&ecirc;n trong.<br />\r\n<br />\r\nC&oacute; điều l&agrave;m ăn m&agrave;, lại kh&ocirc;ng phải l&agrave;m từ thiện, đối phương từ vừa mới bắt đầu x&aacute;c thực liền rất chăm s&oacute;c hắn, v&igrave; lẽ đ&oacute; Lecon cũng kh&ocirc;ng c&oacute; c&ograve; k&egrave; mặc cả, tr&ugrave;ng nàng đưa tay,<br />\r\n<br />\r\n&quot;Hợp t&aacute;c vui vẻ!&quot;<br />\r\n<br />\r\n&quot;Hi vọng ngươi trở th&agrave;nh từ ch&uacute;ng ta nơi n&agrave;y đi ra ngo&agrave;i đại minh tinh!&quot;<br />\r\n<br />\r\nLecon nhếch miệng nở nụ cười, Judy vỗ vỗ c&aacute;nh tay hắn, mỉm cười: &quot;Nghỉ ngơi thật tốt dưới, đ&oacute;i bụng lời n&oacute;i c&oacute; c&ocirc;ng nh&acirc;n bữa ăn khuya, đừng g&ograve; b&oacute;.&quot;<br />\r\n<br />\r\n&quot;Cảm tạ.&quot;<br />\r\n<br />\r\nLecon thay xong quần &aacute;o, đem mang đến đạo cụ thu thập đến trong t&uacute;i đeo lưng, đang định rời đi, Judy lại vội v&atilde; lại đ&acirc;y: &quot;Ta vừa n&atilde;y c&ugrave;ng Mike Dur h&agrave;n huy&ecirc;n, ch&uacute;ng ta n&oacute;i tới ngươi, ta n&oacute;i với hắn ngươi l&agrave; c&aacute;i rất c&oacute; biểu diễn thi&ecirc;n ph&uacute;, chăm ch&uacute; khắc khổ ảo thuật gia, ta hy vọng c&oacute; thể cho ngươi t&igrave;m một cơ hội, hiện tại hắn muốn ngươi đi gặp hắn.&quot;<br />\r\n<br />\r\n&quot;Mike Dur?&quot;<br />\r\n<br />\r\nJudy n&oacute;i: &quot;Ch&iacute;nh l&agrave; ngươi mới vừa mới đem hắn tiền mặt biến kh&ocirc;ng c&ograve;n ti&ecirc;n sinh, hắn l&agrave; c&aacute;i tiết mục nh&agrave; sản xuất c&ugrave;ng c&ograve; m&ocirc;i giới, bằng hữu của hắn ch&iacute;nh l&agrave; chế t&aacute;c show America&#39;s Got Talent Jeff Ralph, với hắn giữ g&igrave;n mối quan hệ, cơ hội của ngươi liền lớn hơn!&quot;<br />\r\n<br />\r\n&quot;Ồ?&quot;<br />\r\n<br />\r\nLecon đ&atilde; hiểu, cũng thật l&agrave; c&aacute;i c&oacute; lai lịch gia hỏa a.<br />\r\n<br />\r\nVậy dĩ nhi&ecirc;n l&agrave; muốn đi gặp.<br />\r\n<br />\r\nLecon từ ph&iacute;a sau đ&agrave;i đi ra, t&igrave;m tới vừa n&atilde;y Mike c&aacute;i kia một b&agrave;n, tuy rằng thay đổi quần &aacute;o, nhưng người chung quanh đều c&ograve;n nhớ hắn, cũng kh&ocirc;ng c&oacute; thiếu người giơ l&ecirc;n ly rượu tr&ugrave;ng hắn hỏi thăm,<br />\r\n<br />\r\n&quot;Đại ảo thuật gia, cụng ly!&quot;<br />\r\n<br />\r\n&quot;Cảm tạ c&aacute;c ngươi!&quot;<br />\r\n<br />\r\nLecon cũng cười tr&ugrave;ng bọn họ phất tay.<br />\r\n<br />\r\nMike Dur l&agrave; c&aacute;i ngo&agrave;i ba mươi l&atilde;o bạch, trang phục rất thời thượng, tr&ecirc;n người một luồng nồng nặc m&ugrave;i nước hoa, d&aacute;ng dấp kia vừa nh&igrave;n kh&ocirc;ng phải t&uacute; &ocirc;ng ch&iacute;nh l&agrave; l&agrave;m minh tinh c&ograve; m&ocirc;i giới.<br />\r\n<br />\r\nTrong miệng hắn kh&ocirc;ng yểm đối với Lecon thưởng thức: &quot;Ta cảm thấy cho ngươi ảo thuật phi thường kỳ diệu, hơn nữa ngươi c&oacute; rất tốt biểu diễn năng lực, ngươi th&iacute;ch ứng s&acirc;n khấu, kh&ocirc;ng n&ecirc;n v&acirc;y ở c&aacute;i n&agrave;y tiểu trong b&igrave;nh đ&agrave;i.&quot;<br />\r\n<br />\r\nLecon khi&ecirc;m tốn n&oacute;i: &quot;Ta cũng muốn c&oacute; s&acirc;n khấu lớn, nhưng cơ hội c&ograve;n chưa tới tr&ecirc;n đầu ta.&quot;<br />\r\n<br />\r\nMike kh&ocirc;ng c&oacute; quanh co l&ograve;ng v&ograve;ng: &quot;T&acirc;n một kỳ Got Talent liền muốn bắt đầu casting quay chụp, năm nay tổng qu&aacute;n qu&acirc;n tiền thưởng đạt đến chưa từng c&oacute; ng&agrave;n vạn đ&ocirc;la Mỹ, hướng về ph&iacute;a tiết mục tổ d&atilde; t&acirc;m, ngươi kh&ocirc;ng l&yacute; do kh&ocirc;ng l&ecirc;n đi biểu hiện thi&ecirc;n ph&uacute; của ch&iacute;nh m&igrave;nh.&quot;<br />\r\n<br />\r\nLecon biết tiết mục n&agrave;y, l&acirc;u năm đứng đầu talent show, đ&uacute;ng l&agrave; c&aacute;i nổi danh b&igrave;nh đ&agrave;i, hắn phải cảm tạ đối phương: &quot;Cảm tạ ngươi kiến nghị, Dur ti&ecirc;n sinh.&quot;<br />\r\n<br />\r\n&quot;Ngươi c&oacute; thể gọi ta Mike l&atilde;o huynh, v&ograve;ng người b&ecirc;n trong đều như thế gọi ta.&quot;<br />\r\n<br />\r\nMike mỉm cười, sau đ&oacute; mục đ&iacute;ch liền đi ra,<br />\r\n<br />\r\n&quot;Ta c&oacute; thể an b&agrave;i ngươi đi tham gia casting, nếu như ngươi c&oacute; thể thu được thăng cấp, như vậy ta gặp rất t&igrave;nh nguyện c&ugrave;ng ngươi k&yacute; t&ecirc;n c&ograve; m&ocirc;i giới hợp t&aacute;c, d&ugrave;ng nhiều tư nguy&ecirc;n hơn c&ugrave;ng c&agrave;ng nhiều biểu diễn cơ hội phủng ngươi, đưa ngươi đi Las Vegas, tr&ecirc;n New York Broadway s&acirc;n khấu, đem ngươi chế tạo th&agrave;nh một c&aacute;i si&ecirc;u cấp minh tinh.&quot;<br />\r\n<br />\r\nĐối phương đang vẽ b&aacute;nh, c&oacute; điều Lecon c&ograve;n thật sự c&oacute; đem kh&ocirc;ng tưởng nội dung biến hiện thực năng lực.<br />\r\n<br />\r\nBởi v&igrave; tự th&acirc;n thực lực cứng l&agrave; tuyệt đối kh&ocirc;ng cái nào ảo thuật gia c&oacute; thể so với, ph&agrave;m l&agrave; c&oacute; một con đường, hoặc l&agrave; cho ta một c&aacute;i cột mốc đường, ta đều c&oacute; thể s&aacute;ng tạo một c&aacute;i th&ocirc;ng thi&ecirc;n đại n&oacute;i tới.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nLecon đương nhi&ecirc;n t&igrave;nh nguyện: &quot;Ta phi thường cảm tạ, Mike, cơ hội n&agrave;y sẽ kh&ocirc;ng ở trong tay ta l&atilde;ng ph&iacute;!&quot;<br />\r\n<br />\r\nMike giơ l&ecirc;n bia ly: &quot;Mong ước ngươi c&oacute; thể đẹp đẽ xuất sắc tự m&igrave;nh!&quot;<br />\r\n<br />\r\nLecon với hắn chạm cốc: &quot;Cụng ly!&quot;<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nMột phen tr&ograve; chuyện sau Lecon mới c&aacute;o từ đối phương.<br />\r\n<br />\r\nT&uacute; một tay nhỏ thực lực, đ&atilde;i ngộ c&ugrave;ng cơ hội liền theo nhau m&agrave; tới! Nh&acirc;n sinh thật sự kh&ocirc;ng n&ecirc;n như thế cẩu a, l&uacute;c n&ecirc;n xuất thủ vẫn phải l&agrave; ra tay!<br />\r\n<br />\r\nBuổi tối hắn cầm m&ocirc;̣t ngàn đ&ocirc;la Mỹ chi phiếu trở lại, muốn n&oacute;i nước Mỹ điểm ấy liền để hắn rất kh&ocirc;ng th&iacute;ch ứng, quẹt thẻ chuyển khoản thủ tục ph&iacute; đặc biệt cao, dẫn đến chi phiếu loại n&agrave;y thế kỷ trước đồ vật như cũ lưu h&agrave;nh, mua nh&agrave; mua xe, thậm ch&iacute; mua c&aacute;i m&oacute;n đồ lớn đều kh&ocirc;ng thể rời bỏ n&oacute;.<br />\r\n<br />\r\nC&oacute; điều nằm ở tr&ecirc;n giường nh&igrave;n những n&agrave;y linh thời điểm, vẫn rất c&oacute; cảm gi&aacute;c th&agrave;nh c&ocirc;ng.<br />\r\n<br />\r\nMỗi tuần biểu diễn c&aacute;i hai, ba lần, c&aacute;i kia kh&ocirc;ng phải nguyệt v&agrave;o hơn vạn, bước v&agrave;o thỏa thỏa giai cấp trung lưu sao?<br />\r\n<br />\r\nThiết &mdash;&mdash; liền điểm ấy theo đuổi!<br />\r\n<br />\r\nLecon nhổ nước bọt dưới ch&iacute;nh m&igrave;nh, nhưng hồi tưởng một th&aacute;ng trước bưng m&acirc;m cuộc đời, cũng thật l&agrave; c&oacute; ch&uacute;t khổ tận cam lai tư vị, muốn nổi danh, muốn kiếm bộn tiền, khẳng định phải đi từng bước một.<br />\r\n<br />\r\nCh&iacute; &iacute;t hiện tại bắt đầu cũng rất thuận lợi, đợi được ng&agrave;y thứ hai tỉnh lại, hồi l&acirc;u kh&ocirc;ng c&oacute; động tĩnh Quyển Linh, c&ograve;n mang đến một tin tức tốt &mdash;&mdash; n&oacute; đ&atilde; c&oacute; thể hoạt động ho&agrave;n chỉnh c&ocirc;ng năng!</p>', '2021-12-01 01:20:16', 10, 1),
(277, 73, 'Ta Gọi Dịch Tích Phong', '1', '<p>&quot;Đại Vương gọi ta đến tuần núi! ~~~~&quot; một trận tố chất thần kinh điện thoại di động chu&ocirc;ng b&aacute;o thức, c&ugrave;ng trong chăn một trận nỉ non tiếng chửi rủa c&ugrave;ng đập tiếng giường. Một gian kh&ocirc;ng lớn nhưng lại chỉnh tề ph&ograve;ng ngủ lại một lần nữa rơi v&agrave;o y&ecirc;n ả.<br />\r\n<br />\r\n&quot;T&ecirc;n đi&ecirc;n, lại bị l&atilde;o sư gọi v&agrave;o ph&ograve;ng l&agrave;m việc? Ngươi thật giỏi, l&atilde;o sư thật th&iacute;ch ngươi a? Ha ha, trắng n&otilde;n ch&iacute;nh l&agrave; tốt! Giống ch&uacute;ng ta dạng n&agrave;y, nếu l&agrave; l&ecirc;n lớp đến trễ 40 ph&uacute;t đồng hồ, buổi tối khẳng định phải th&ecirc;m l&agrave;m một tuần trực nhật!&quot; Một sắc mặt xanh đen người cao nam h&agrave;i đứng ở ph&ograve;ng học trong h&agrave;nh lang, hai tay cắm ở trước ngực, 1 th&acirc;n m&agrave;u xanh nhạt đồng phục đ&atilde; tẩy thay đổi bạch, ở h&agrave;nh lang dưới đ&egrave;n c&oacute; ch&uacute;t v&agrave;ng ố, hợp với một đ&ocirc;i hơi cũ l&agrave; gi&agrave;y chơi b&oacute;ng.<br />\r\n<br />\r\nGiờ ph&uacute;t n&agrave;y t&ecirc;n người cao nam h&agrave;i ch&iacute;nh đang cười tr&ecirc;n nổi đau của người kh&aacute;c mắt nh&igrave;n đối diện một trắng no&atilde;n nam h&agrave;i. Trắng n&otilde;n nam h&agrave;i c&aacute;i đầu kh&ocirc;ng cao, ước chừng 17 0 centimet, tại người đồng lứa b&ecirc;n trong cũng kh&ocirc;ng dễ thấy, ngũ quan coi như thanh t&uacute;, nếu n&oacute;i hơi nổi bật ch&iacute;nh l&agrave; trắng no&atilde;n l&agrave;n da c&ugrave;ng xấu hổ nụ cười.<br />\r\n<br />\r\nNghe được đen kịt người cao nam h&agrave;i m&agrave; n&oacute;i, trắng n&otilde;n nam h&agrave;i lườm hắn một c&aacute;i, sau đ&oacute; lạnh l&ugrave;ng n&oacute;i ra: &quot;Kh&ocirc;ng phải l&agrave; đến trễ, l&agrave; ta tới sớm 5 ph&uacute;t đồng hồ.&quot;<br />\r\n<br />\r\nĐen kịt nam h&agrave;i ngẩn người, cười to n&oacute;i: &quot;Nh&igrave;n qua da mặt d&agrave;y, chưa thấy qua ngươi dạng n&agrave;y chững chạc đ&agrave;ng ho&agrave;ng n&oacute;i bậy bạ.&quot;<br />\r\n<br />\r\nTrắng n&otilde;n nam h&agrave;i liếc mắt nh&igrave;n hắn, r&ograve; rỉ ra xấu hổ nụ cười, n&oacute;i ra: &quot;Đ&acirc;y cũng kh&ocirc;ng phải l&agrave; n&oacute;i năng bậy bạ, c&aacute;i n&agrave;y để lối tho&aacute;t a. C&oacute; lối tho&aacute;t a ph&iacute;a dưới ngươi ta th&igrave; dễ n&oacute;i chuyện, ngươi nếu kh&ocirc;ng trang phục, 1 hồi ph&iacute;a dưới tự học buổi tối ch&uacute;ng ta đơn đấu mấy thế cục T&aacute;n Thủ như thế n&agrave;o?&quot;<br />\r\n<br />\r\nMắt nh&igrave;n trắng n&otilde;n nam h&agrave;i xấu hổ nụ cười, đen kịt nam h&agrave;i giật nảy m&igrave;nh r&ugrave;ng m&igrave;nh một c&aacute;i. Cười lớn n&oacute;i: &quot;Tốt a t&ecirc;n đi&ecirc;n, ngươi đừng r&egrave;n luyện ta, ngươi n&oacute;i ngươi một c&aacute;i T&aacute;n Thủ cao thủ, cần g&igrave; kh&oacute; xử ta một tiểu b&igrave;nh d&acirc;n đ&acirc;y? Ta c&oacute; thể l&agrave; lương d&acirc;n!&quot;<br />\r\n<br />\r\n&quot;Thiết, ngươi c&aacute;i n&agrave;y k&eacute;m cỏi, ta kh&ocirc;ng phải cao thủ? Chỉ bất qu&aacute; khi c&ograve;n b&eacute; học qua v&agrave;i năm Karate vẫn l&agrave; thiếu ni&ecirc;n lớp, l&ecirc;n cao trung đi ph&ograve;ng tập thể thao luyện 2 năm T&aacute;n Thủ. Nhiều lắm xem như l&agrave; th&acirc;n thể linh hoạt, cũng l&agrave; ngươi c&aacute;i n&agrave;y k&eacute;m cỏi bạch lớn l&ecirc;n cao như vậy như vậy cường tráng.&quot; Trắng n&otilde;n nam h&agrave;i khinh bỉ mắt nh&igrave;n đen kịt nam h&agrave;i.<br />\r\n<br />\r\nĐột nhi&ecirc;n, trắng n&otilde;n nam h&agrave;i giống như l&agrave; nhớ ra c&aacute;i g&igrave; đ&oacute;, quay người chạy đi, vừa chạy vừa h&ocirc;: &quot;Hắc Tử, ngươi bắt ta bao bọc trước về ph&ograve;ng học, b&agrave;i thi ta đều viết xong, ngươi ch&eacute;p xong gi&uacute;p ta nộp l&ecirc;n, ta hiện tại đi thao trường một chuyến!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nGọi Hắc Tử đen kịt nam h&agrave;i ngẩn ra một ch&uacute;t, trắng n&otilde;n nam h&agrave;i đ&atilde; chạy mất dạng, &quot;T&ecirc;n đi&ecirc;n ch&iacute;nh l&agrave; t&ecirc;n đi&ecirc;n, chạy c&ograve;n nhanh hơn thỏ! Giống như l&agrave;m điền kinh đ&aacute;m kia kẻ v&ocirc; lại đều kh&ocirc;ng nhanh như vậy a ~&quot; Hắc Tử lẩm bẩm quay người hướng ph&ograve;ng học đi đến.<br />\r\n<br />\r\nDưới m&agrave;n d&ecirc;m thao trường cũng kh&ocirc;ng c&oacute; người, trắng n&otilde;n nam h&agrave;i nhanh ch&oacute;ng xuy&ecirc;n qua từng t&ograve;a lầu dạy học, đi đến tr&ecirc;n b&atilde;i tập. Giờ ph&uacute;t n&agrave;y thao trường đen kịt một m&agrave;u, chỉ c&oacute; thể nh&igrave;n thấy nơi xa lầu dạy học nơi đ&oacute; truyền tới &aacute;nh đ&egrave;n, chắc hẳn giờ ph&uacute;t n&agrave;y c&aacute;c bạn học đều tại b&ecirc;n tr&ecirc;n lớp tự học. Trắng n&otilde;n nam h&agrave;i ngẩng đầu nh&igrave;n bầu trời đ&ecirc;m, giống như l&agrave; đang t&igrave;m c&aacute;i g&igrave;.<br />\r\n<br />\r\nƯớc chừng qua th&ecirc;m v&agrave;i ph&uacute;t đồng hồ, trắng n&otilde;n nam h&agrave;i &aacute;nh mắt s&aacute;ng l&ecirc;n, h&eacute;t lớn: &quot;T&igrave;m được! T&igrave;m được! Ph&ograve;ng Tinh bắt nguồn từ đ&ocirc;ng, xu&acirc;n bắt đầu cũng. [ Tử B&igrave;nh ] đ&atilde; n&oacute;i tại đ&acirc;y đại địa vạn vật mới tỉnh thời đi&ecirc;̉m, c&oacute; thể hấp thu vạn vật sức sống lấy cường th&acirc;n!&quot; N&oacute;i ra trắng n&otilde;n nam h&agrave;i liền ở tr&ecirc;n b&atilde;i tập đ&aacute;nh l&ecirc;n quyền.<br />\r\n<br />\r\nNgay từ đầu coi như c&oacute; ch&uacute;t bố cục, về sau n&oacute;i l&agrave; đ&aacute;nh quyền kh&ocirc;ng bằng n&oacute;i l&agrave; l&agrave;m thể thao, c&oacute; trụ cột Karate nhập m&ocirc;n đ&aacute; v&agrave;o cẳng ch&acirc;n hoạt động, cũng c&oacute; T&aacute;n Thủ động t&aacute;c n&oacute;ng người, ngẫu nhi&ecirc;n c&ograve;n c&oacute; thể xem được đến set thứ 8 tập thể dục theo đ&agrave;i hoạt động. Thẳng đến về sau, trắng n&otilde;n nam h&agrave;i l&agrave;m mồ h&ocirc;i đầm đ&igrave;a, mới chậm r&atilde;i dừng lại. T&iacute;nh to&aacute;n thời gian c&aacute;i n&agrave;y một trận &quot;Quyền&quot; đ&aacute;nh xuống, đ&aacute;nh suốt hai giờ!<br />\r\n<br />\r\nNh&igrave;n ph&iacute;a xa lầu dạy học phương hướng đen kịt một m&agrave;u, l&uacute;c trước &aacute;nh đ&egrave;n đ&atilde; biến mất kh&ocirc;ng thấy g&igrave; nữa. Trắng n&otilde;n nam h&agrave;i nghĩ thầm: &quot;Xem ra l&agrave; tự học buổi tối tan lớp, đ&aacute;m n&agrave;y ngu ngốc h&agrave;ng 8 th&agrave;nh l&agrave; vội v&atilde; trở về đọc tiểu thuyết đi! Đ&aacute;m n&agrave;y đầu đất bao giờ cũng mắt nh&igrave;n người kh&aacute;c th&agrave;nh ti&ecirc;n tu đạo sảng kho&aacute;i, lại kh&ocirc;ng suy nghĩ một ch&uacute;t m&igrave;nh luyện l&agrave; được, chẳng phải l&agrave; tốt hơn? Mặc d&ugrave; kh&ocirc;ng c&oacute; khả năng th&agrave;nh ti&ecirc;n th&agrave;nh đạo, nhưng l&agrave; l&agrave;m một d&aacute;m l&agrave;m việc nghĩa tốt thanh ni&ecirc;n vẫn l&agrave; c&oacute; thể.&quot;<br />\r\n<br />\r\nTrắng n&otilde;n nam h&agrave;i thở hổn hển hướng về k&yacute; t&uacute;c x&aacute; phương hướng đi đến, n&oacute;i lầm bầm: &quot;H&ocirc; ~ h&ocirc; ~ tr&ecirc;n s&aacute;ch n&oacute;i hấp thu thi&ecirc;n địa sức sống c&oacute; thể cường th&acirc;n kiện thể, thậm ch&iacute; phản l&atilde;o ho&agrave;n đồng, cũng kh&ocirc;ng biết ta đ&acirc;y lội quyền đ&aacute;nh xuống tới, hấp thu hoặc &iacute;t hoặc nhiều? H&ocirc; ~ ch&iacute;nh l&agrave; ~ h&ocirc; ~ cảm thấy c&oacute; chút ~ h&ocirc; ~ kh&ocirc;ng thở nổi!&quot; Trắng n&otilde;n nam h&agrave;i miệng lớn thở hổn hển,<br />\r\n<br />\r\nChậm r&atilde;i dừng bước lại, đồng thời cũng tự tr&aacute;ch m&igrave;nh v&igrave; sao đ&ecirc;m nay sức chịu đựng như vậy k&eacute;m.<br />\r\n<br />\r\nNhưng tựa hồ miệng to thở dốc cũng kh&ocirc;ng c&oacute; l&agrave;m dịu hắn ngạt thở cảm gi&aacute;c, chậm r&atilde;i hắn ng&atilde; tr&ecirc;n mặt đất, ngắm nh&igrave;n bầu trời, lồng ngực chập tr&ugrave;ng kịch liệt lấy, mồ h&ocirc;i cũng như nước mưa một dạng chảy xuống. Hai tay của hắn chậm r&atilde;i nắm chặt mặt đất cỏ xanh, con mắt cũng bởi v&igrave; khuyết dưỡng dần dần tr&agrave;n ngập nước mắt c&ugrave;ng tơ m&aacute;u. Đột nhi&ecirc;n hắn hoảng sợ mở to hai mắt! Phảng phất ph&aacute;t hiện c&aacute;i g&igrave; kh&oacute; tin đồ vật. Bởi v&igrave; hắn ph&aacute;t hiện m&igrave;nh đặt xuống chỗ lại c&oacute; thực cỏ xanh, kh&ocirc;ng phải l&agrave; trường học mặt cỏ nh&acirc;n tạo. M&agrave; bản th&acirc;n ngước nh&igrave;n tinh kh&ocirc;ng cũng kh&ocirc;ng phải trước đ&oacute; phiến kia quen thuộc tinh kh&ocirc;ng . . .<br />\r\n<br />\r\nTrắng n&otilde;n nam h&agrave;i d&ugrave;ng hết ch&uacute;t sức lực cuối c&ugrave;ng lẩm bẩm n&oacute;i: &quot;Đ&acirc;y con mẹ n&oacute; ch&iacute;nh l&agrave; chỗ đ&oacute;? Tại sao c&oacute; thể c&oacute; 2 c&aacute;i &aacute;nh trăng . . .&quot; Sau đ&oacute; liền đ&atilde; bất tỉnh.<br />\r\n<br />\r\nKh&ocirc;ng biết qua bao l&acirc;u, một tia nắng vẩy v&agrave;o trắng n&otilde;n nam h&agrave;i tr&ecirc;n mặt, nhưng chung quanh cũng kh&ocirc;ng c&oacute; vang l&ecirc;n &quot;Đại Vương gọi ta đến tuần n&uacute;i&quot; chu&ocirc;ng b&aacute;o thức, trắng n&otilde;n nam h&agrave;i n&oacute;i lầm bầm: &quot;Đừng vang a! Ta ngủ tiếp một l&aacute;t a! Ngoan, đừng vang!&quot; Nhưng l&agrave; kh&ocirc;ng như mong muốn, một trận c&ugrave;ng loại heo rừng tiếng g&agrave;o th&eacute;t từ đằng xa truyền đến, trắng n&otilde;n nam h&agrave;i nỉ non mắng.<br />\r\n<br />\r\nHeo rừng tiếng g&agrave;o th&eacute;t đồng thời kh&ocirc;ng ngừng nghỉ, m&agrave; l&agrave; li&ecirc;n tiếp vang l&ecirc;n. Trắng n&otilde;n nam h&agrave;i nghe theo kh&ocirc;ng ki&ecirc;n nhẫn, n&acirc;ng l&ecirc;n quyền tr&aacute;i, h&eacute;t lớn: &quot;K&ecirc;u la c&aacute;i g&igrave;, kh&ocirc;ng biết ta để cho c&aacute;i kia ai đi tuần n&uacute;i rồi?&quot; Đồng thời 1 quyền đập xuống đất . . .<br />\r\n<br />\r\nMột tiếng thanh th&uacute;y trẻ sơ sinh tiếng la kh&oacute;c vang l&ecirc;n, chỉ thấy một tiểu Oa Oa từ trong bụi cỏ một lăn l&ocirc;ng lốc đứng l&ecirc;n, bưng b&iacute;t lấy bản th&acirc;n mập mạp nắm tay nhỏ, vẻ mặt m&ugrave; mờ, luống cuống c&ograve;n c&oacute; đau biểu lộ. Bẩn thỉu tr&ecirc;n khu&ocirc;n mặt nhỏ nhắn d&iacute;nh đầy b&ugrave;n, mồ h&ocirc;i c&ograve;n c&oacute; hạt sương, hắn giống một tượng b&ugrave;n một dạng sững sờ ngồi ở kia, chẳng biết đang suy nghĩ g&igrave;.<br />\r\n<br />\r\n&quot;G&agrave;o rống ~~&quot; nơi xa 1 tiếng to r&otilde; lợn rừng tru l&ecirc;n đ&aacute;nh thức tiểu Oa Oa, hắn cuống qu&iacute;t từ trong bụi cỏ đứng l&ecirc;n, mắt nh&igrave;n tr&ecirc;n người mang theo từng c&acirc;y x&eacute; n&aacute;t vải, c&ugrave;ng mơ hồ c&oacute; thể thấy được đồng phục huy hiệu trường k&yacute; hiệu, kh&ocirc;ng biết l&agrave; kh&oacute;c l&agrave; cười.<br />\r\n<br />\r\n&quot;Nhất định phải lập tức rời đi nơi n&agrave;y, sau đ&oacute; hiểu r&otilde; đến c&ugrave;ng xảy ra chuyện g&igrave;!&quot; Tiểu Oa Oa lau lau tr&ecirc;n mặt vết bẩn, k&eacute;o treo ở tr&ecirc;n người vải, cầm lấy phiến kia mang theo huy hiệu trường tấm vải, hướng về bản th&acirc;n trong ấn tượng trường học t&uacute;c x&aacute; phương hướng chạy tới.<br />\r\n<br />\r\nTiểu Oa Oa vừa chạy, một b&ecirc;n nghĩ linh tinh: &quot;Phản l&atilde;o ho&agrave;n đồng? Tới địa ngục đi phản l&atilde;o ho&agrave;n đồng! Lão tử kh&ocirc;ng phải l&agrave; Edogawa Conan! Lão tử kh&ocirc;ng phải l&agrave; Shinichi Kudo!&quot;<br />\r\n<br />\r\nBởi v&igrave; th&acirc;n cao vấn đề, chung quanh hắn đều l&agrave; bụi cỏ, cũng thấy kh&ocirc;ng r&otilde; chung quanh sự vật, chỉ l&agrave; cắm đầu chạy về ph&iacute;a trước, ước chừng chạy nửa giờ, đang l&uacute;c hắn cảm thấy phải chăng thay một c&aacute;i phương hướng chạy thời điểm cảnh vật trước mắt s&aacute;ng tỏ th&ocirc;ng suốt &mdash; &mdash; trước mắt xuất hiện 1 t&ograve;a th&ocirc;n trang.<br />\r\n<br />\r\nX&ocirc;ng ra bụi cỏ nh&igrave;n thấy th&ocirc;n trang trong nh&aacute;y mắt, . Cũng &eacute;p vỡ hắn sau c&ugrave;ng 1 căn thần kinh, một đ&ecirc;m n&agrave;y hắn kinh lịch qu&aacute; nhiều chuyện, hắn giống một tấm căng cứng cung, tại thời khắc n&agrave;y mềm oặt ng&atilde; tr&ecirc;n mặt đất. S&aacute;ng sớm điền trang đột nhi&ecirc;n th&ecirc;m ra một bẩn thỉu tiểu Oa Oa, tự nhi&ecirc;n đưa tới chung quanh th&ocirc;n d&acirc;n ch&uacute; &yacute;.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nKhi hắn tỉnh lại lần nữa, cảm gi&aacute;c được bản th&acirc;n lại một lần nữa nằm ở tr&ecirc;n giường thời điểm trong l&ograve;ng lập tức an t&acirc;m rất nhiều, hắn theo th&oacute;i quen giơ l&ecirc;n quyền tr&aacute;i, nhưng ở kh&ocirc;ng trung dừng dừng kh&ocirc;ng c&oacute; rơi xuống. Bởi v&igrave; hắn cảm gi&aacute;c được tay tr&aacute;i của hắn b&ecirc;n trong nắm một khối vải r&aacute;ch, nơi đ&oacute; c&oacute; dấu một huy hiệu trường.<br />\r\n<br />\r\nHắn h&iacute;t s&acirc;u một hơi, d&ugrave;ng thời gian ba c&aacute;i h&ocirc; hấp y&ecirc;n tĩnh &yacute;, sau đ&oacute; từ từ mở mắt, tự nhủ: &quot;Thật chẳng lẽ th&agrave;nh Conan ?&quot;<br />\r\n<br />\r\nĐập v&agrave;o mi mắt l&agrave; một treo nồi đất ấm, b&ecirc;n trong truyền đến từng đợt thảo dược m&ugrave;i, chắc hẳn nở rộ m&ocirc;̣t chút dược dịch. Trong ph&ograve;ng ngồi một &ocirc;ng l&atilde;o.<br />\r\n<br />\r\nChỉ thấy l&atilde;o giả n&agrave;y, r&acirc;u ngắn, t&oacute;c mai l&ocirc;ng mi trắng hắc, đầu cuộn t&oacute;c b&uacute;i t&oacute;c, b&ecirc;n tr&ecirc;n lấy vải th&ocirc; đen trắng y, thả xuống vải th&ocirc; t&iacute;m b&agrave;o, ch&acirc;n lấy sợi đay dấn bước, b&ecirc;n h&ocirc;ng đừng một hồ l&ocirc;, b&ecirc;n tr&ecirc;n đồng &yacute; ph&ugrave; mấy mấy, kh&ocirc;ng giống người hiện đại lại nhiều cổ phong.<br />\r\n<br />\r\nL&atilde;o giả thấy hắn tỉnh lại, tiến l&ecirc;n đ&egrave; tay của hắn lại, sờ l&ecirc;n hắn mạch tượng, về sau hơi gật gật đầu, hướng hắn n&oacute;i một c&acirc;u n&oacute;i, nhưng tiểu Oa Oa cũng kh&ocirc;ng biết &yacute;, đờ đẫn lắc đầu.<br />\r\n<br />\r\nL&atilde;o giả trong mắt hơi c&oacute; vẻ kinh ngạc, về sau lại th&ecirc;m ra một ch&uacute;t thương hại. Đứng l&ecirc;n sờ l&ecirc;n tiểu Oa Oa đầu, quay người đi ra ngo&agrave;i ph&ograve;ng, l&uacute;c n&agrave;y tiểu Oa Oa đột nhi&ecirc;n n&oacute;i ra: &quot;C&aacute;m ơn ngươi đ&atilde; cứu ta, mặc d&ugrave; ngươi kh&ocirc;ng minh bạch ta n&oacute;i c&aacute;i g&igrave;, ta cũng kh&ocirc;ng hiểu ngươi n&oacute;i c&aacute;i g&igrave;. Nhưng l&agrave; vẫn c&aacute;m ơn ngươi, đo&aacute;n chừng ngươi sẽ hỏi ta mới vừa n&oacute;i Conan l&agrave; ai? Ta v&agrave; hắn kh&ocirc;ng quen, ta cũng sẽ kh&ocirc;ng phải hắn, v&igrave; lẽ đ&oacute; ngươi sẽ rất an to&agrave;n, m&agrave; ta sẽ lớn l&ecirc;n, hắn sẽ kh&ocirc;ng. A, qu&ecirc;n n&oacute;i, ta gọi Dịch T&iacute;ch Phong, xin nhiều chiếu cố!&quot;<br />\r\n<br />\r\nL&atilde;o giả trong mắt kinh ngạc c&agrave;ng đậm, sau đ&oacute; trong mắt th&ecirc;m ra mỉm cười, quay người ra khỏi ph&ograve;ng, để cho người l&agrave; Dịch T&iacute;ch Phong chuẩn bị 1 ch&uacute;t cơm canh tự kh&ocirc;ng n&oacute;i chơi.</p>', '2021-12-01 01:25:43', 6, 1),
(278, 73, 'Tuổi Nhỏ đem Tập Nghệ', '2', '<p>&quot;T&iacute;ch Phong! T&iacute;ch Phong! Ch&uacute;ng ta c&ugrave;ng đi ra chơi đ&ugrave;a cưỡi ngựa đ&aacute;nh trận a! 1 hồi ng&agrave;i th&ocirc;n trưởng đến liền lại phải học thuộc l&ograve;ng s&aacute;ch.&quot; Một người d&aacute;ng dấp đen đ&uacute;a gầy g&ograve; tiểu đồng đứng tư thục ngưỡng cửa hướng về ph&iacute;a b&ecirc;n trong một trắng n&otilde;n tiểu đồng gọi v&agrave;o.<br />\r\n<br />\r\nTrắng n&otilde;n tiểu đồng ch&iacute;nh đang một tấm thư sập nh&igrave;n đằng trước lấy quyển s&aacute;ch tr&ecirc;n tay, nhăn lại đ&aacute;ng y&ecirc;u l&ocirc;ng m&agrave;y l&atilde;o kh&iacute; ho&agrave;nh thu*(như &ocirc;ng cụ non) n&oacute;i: &quot;Hắc Tử, tiểu tử ngươi cả ng&agrave;y nghĩ đến chơi, ng&agrave;y h&ocirc;m nay s&aacute;ng sớm đọc lại đến muộn nửa canh giờ.&quot;<br />\r\n<br />\r\nHắc gầy tiểu đồng bản khởi khu&ocirc;n mặt nhỏ n&oacute;i ra: &quot;Đừng gọi ta Hắc Tử, ta gọi L&acirc;m Phong Hỏa, ngươi c&oacute; thể gọi ta phong hỏa.&quot; Sau đ&oacute; L&acirc;m Phong Hỏa cười n&oacute;i: &quot;Đ&acirc;y kh&ocirc;ng phải l&agrave; đến trễ nửa canh giờ . . .&quot;<br />\r\n<br />\r\n&quot;&Acirc;n đúng, xem như l&agrave; ph&iacute;a dưới tiết kh&oacute;a tới sớm nửa canh giờ?&quot; Dịch T&iacute;ch Phong tr&ecirc;u ghẹo nhận được.<br />\r\n<br />\r\nSau đ&oacute; 2 người cười ha ha một tiếng, c&ugrave;ng nhau chạy ra học đường.<br />\r\n<br />\r\n&quot;T&iacute;ch Phong, v&igrave; sao ngươi bao giờ cũng dậy sớm như vậy? Hơn nữa ngươi đọc s&aacute;ch rất chăm chỉ ai! Ngươi cảm thấy đọc những kinh văn kia sử tập hữu dụng kh&ocirc;ng?&quot; L&acirc;m Phong Hỏa cầm 1 căn c&acirc;y sao, một b&ecirc;n b&oacute;c lấy vỏ c&acirc;y một b&ecirc;n hỏi Dịch T&iacute;ch Phong.<br />\r\n<br />\r\n&quot;S&aacute;ch đến thời gian sử dụng mới hận thiếu, l&yacute; giải thi&ecirc;n hạ n&agrave;y nhiều một ch&uacute;t l&agrave; hơn điểm chỗ tốt.&quot; Dịch T&iacute;ch Phong ngại ng&ugrave;ng cười cười.<br />\r\n<br />\r\n&quot;Ta cảm thấy sử dụng kh&ocirc;ng lớn, tối thiểu nhất với ta m&agrave; n&oacute;i l&agrave; như thế n&agrave;y. Giấc mộng của ta l&agrave; luyện v&otilde;, xem một ch&uacute;t thi&ecirc;n hạ cao thủ, trở th&agrave;nh thi&ecirc;n hạ đứng đầu nhất một nh&oacute;m kia . . .&quot; L&acirc;m Phong Hỏa ki&ecirc;u ngạo lấy huy động trong tay c&acirc;y sao, l&uacute;c n&agrave;y c&acirc;y sao đ&atilde; trở th&agrave;nh 1 c&aacute;i c&ugrave;ng loại đao gỗ d&aacute;ng vẻ.<br />\r\n<br />\r\n&quot;Sau đ&oacute; th&igrave; sao?&quot; Dịch T&iacute;ch Phong nh&aacute;y mắt hỏi.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nL&acirc;m Phong Hỏa vốn dĩ ki&ecirc;u ngạo lấy đối với Dịch T&iacute;ch Phong cười, nghe theo Dịch T&iacute;ch Phong vừa n&oacute;i như vậy, cũng cảm thấy chẳng biết trả lời như thế n&agrave;o, sờ sờ đầu trở lại: &quot;Sau đ&oacute;? Ta cũng kh&ocirc;ng biết, gi&uacute;p cha ta r&egrave;n sắt? Kh&ocirc;ng, ta muốn mời thi&ecirc;n hạ tốt nhất thợ r&egrave;n gi&uacute;p cha ta r&egrave;n sắt!&quot;<br />\r\n<br />\r\nĐang l&uacute;c L&acirc;m Phong Hỏa tại xoắn xu&yacute;t mời tốt nhất thợ r&egrave;n gi&uacute;p hắn cha r&egrave;n sắt c&oacute; thể hay kh&ocirc;ng đoạt cha hắn chuyện l&agrave;m ăn thời điểm Dịch T&iacute;ch Phong n&oacute;i ra: &quot;Ta ngược lại th&acirc;̣t ra kh&ocirc;ng c&oacute; lớn như vậy d&atilde; t&acirc;m, ta chỉ muốn xem khắp c&aacute;i thế giới n&agrave;y phong cảnh bất đồng.&quot;<br />\r\n<br />\r\n&quot;Thế giới?&quot; L&acirc;m Phong Hỏa kh&ocirc;ng hiểu g&igrave; chỉ biết rất lợi hại m&agrave; nh&igrave;n xem Dịch T&iacute;ch Phong.<br />\r\n<br />\r\n&quot;A, thế giới &yacute; tứ ch&iacute;nh l&agrave; thi&ecirc;n hạ, so thi&ecirc;n hạ, c&ograve;n muốn lớn hơn!&quot;<br />\r\n<br />\r\n2 c&aacute;i tiểu đồng cứ như vậy tại đất hoang b&ecirc;n trong tr&ograve; chuyện, n&oacute;i giấc mộng của m&igrave;nh.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; Dịch T&iacute;ch Phong đi tới c&aacute;i thế giới n&agrave;y năm thứ ba, d&ugrave;ng hắn ở c&aacute;i thế giới n&agrave;y tuổi t&aacute;c t&iacute;nh to&aacute;n, hắn đ&atilde; năm tuổi. Hắn d&ugrave;ng 1 năm thời gian học xong c&aacute;i thế giới n&agrave;y ng&ocirc;n ngữ, lại d&ugrave;ng một năm thời gian đi t&igrave;m hiểu c&aacute;i thế giới n&agrave;y.<br />\r\n<br />\r\nBọn họ sinh hoạt tr&ecirc;n phiến đại lục n&agrave;y v&ocirc; c&ugrave;ng rộng lớn, địa thế h&igrave;nh dạng mặt đất cũng cực kỳ phong ph&uacute;. Ở mảnh n&agrave;y đại lục tồn tại cường đại thi&ecirc;n địa quy tắc, c&oacute; thể hiểu ra những thi&ecirc;n địa n&agrave;y quy tắc, liền c&oacute; thể dựa v&agrave;o thi&ecirc;n địa lực lượng cường h&oacute;a bản th&acirc;n. V&igrave; lẽ đ&oacute; ở c&aacute;i thế giới n&agrave;y V&otilde; Đạo đại th&agrave;nh người, khắp nơi c&oacute; thể si&ecirc;u nhi&ecirc;n thế tục b&ecirc;n ngo&agrave;i.<br />\r\n<br />\r\nHai đời kinh lịch để cho Dịch T&iacute;ch Phong minh bạch, tri thức c&oacute; khi so với v&otilde; c&ocirc;ng c&agrave;ng trọng yếu hơn, v&igrave; lẽ đ&oacute; từ hắn học được c&aacute;i thế giới n&agrave;y ng&ocirc;n ngữ về sau, liền đang li&ecirc;n tục thu hoạch c&aacute;i thế giới n&agrave;y đủ loại tri thức. Th&ocirc;ng qua 2 năm kh&ocirc;ng ngừng học tập, Dịch T&iacute;ch Phong ph&aacute;t hiện c&aacute;i thế giới n&agrave;y xa so với hắn tưởng tượng lớn, hắn ở tại th&ocirc;n gọi l&agrave; Ẩn Nh&acirc;n th&ocirc;n, người trong th&ocirc;n cũng rất &iacute;t c&ugrave;ng ngoại giới c&acirc;u th&ocirc;ng. Chỉ l&agrave; những người bu&ocirc;n b&aacute;n kinh doanh cứ s&aacute;u th&aacute;ng một lần sẽ đến thị trấn của quận gần nhất &mdash; &mdash; Lạc Diệp th&agrave;nh l&agrave;m giao dịch. Dịch T&iacute;ch Phong l&agrave;m một t&ecirc;n c&ocirc; nhi sinh hoạt tại nh&agrave; trưởng th&ocirc;n, từ 3 năm trước đ&acirc;y bị th&ocirc;n trưởng mang về hắn liền một mực sống ở nơi n&agrave;y.<br />\r\n<br />\r\nDịch T&iacute;ch Phong cứ như vậy c&ugrave;ng L&acirc;m Phong Hỏa vừa tr&ograve; chuyện b&ecirc;n cạnh hướng th&ocirc;n mặt ph&iacute;a bắc bước đi. Ẩn Nh&acirc;n th&ocirc;n mặt ph&iacute;a bắc l&agrave; 1 mảnh b&aacute;t ng&aacute;t đất trống, d&agrave;i rộng chừng trăm trượng, nơi n&agrave;y l&agrave; Ẩn Nh&acirc;n th&ocirc;n Diễn V&otilde; trường. Diễn V&otilde; trường lại hướng bắc l&agrave; 1 mảnh thấp l&ugrave;n đồi n&uacute;i, đồi n&uacute;i thụ mộc rậm rạp, xa xa có th&ecirc;̉ nh&igrave;n thấy thủ vệ th&ocirc;n tuần n&uacute;i đội c&ugrave;ng tr&ecirc;n g&ograve; đất th&aacute;p quan s&aacute;t.<br />\r\n<br />\r\nẨn Nh&acirc;n th&ocirc;n l&agrave;m một c&aacute;i th&ocirc;n trang độc lập, th&ocirc;n d&acirc;n ước chừng c&oacute; hơn một ng&agrave;n người. Trong đ&oacute; 18 tuổi đến 40 tuổi thanh tr&aacute;ng ni&ecirc;n c&oacute; khoảng t&aacute;m trăm người. Ẩn Nh&acirc;n th&ocirc;n người quản l&yacute; ch&iacute;nh l&agrave; th&ocirc;n trưởng L&acirc;m Hằng Sơn, hắn đồng thời đều l&agrave; L&acirc;m Phong Hỏa gia gia, m&agrave; Ẩn Nh&acirc;n th&ocirc;n quyền lực tối cao người l&agrave; lấy tam l&atilde;o cầm đầu Trưởng L&atilde;o hội, liền xem như th&ocirc;n trưởng cũng phải phục t&ugrave;ng trưởng l&atilde;o hội quyết nghị.<br />\r\n<br />\r\nTừng c&aacute;i sinh hoạt tại Ẩn Nh&acirc;n th&ocirc;n Oa Oa từ năm tuổi l&ecirc;n, liền bắt đầu tiếp nhận do Trưởng L&atilde;o hội hệ thống &quot;Tập nghệ&quot; . C&aacute;i gọi l&agrave; &quot;Tập nghệ&quot; ch&iacute;nh l&agrave; gi&aacute;o hội những h&agrave;i tử n&agrave;y cơ bản nhất kỹ năng sinh tồn.<br />\r\n<br />\r\nTừ giờ M&atilde;o (buổi s&aacute;ng s&aacute;u điểm) l&ecirc;n, s&aacute;ng sớm đọc biết chữ, n&oacute;i văn, giải sử 1 canh giờ, đ&acirc;y l&agrave; &quot;Th&ocirc;ng nghệ&quot; tức v&ocirc; luận l&agrave; ai cũng c&acirc;̀n tham gia. Từ giờ Th&igrave;n (buổi s&aacute;ng t&aacute;m giờ) l&ecirc;n, chia l&agrave;m &quot;V&otilde; nghệ&quot; c&ugrave;ng &quot;Văn nghệ&quot; cả hai chọn một m&agrave; th&ocirc;i, &quot;V&otilde; nghệ&quot; bao gồm l&agrave; chạy, vọt, đứng, đấu, bắn; &quot;Văn nghệ&quot; bao gồm thức, biện, to&aacute;n, b&aacute;c, thư. Đến giờ ngọ (mười hai giờ trưa), ăn cơm nghỉ trưa 1 canh giờ. Chưa bao giờ l&uacute;c (hai giờ chiều) l&ecirc;n, bắt đầu &quot;Luyện nghệ&quot;, &quot;Luyện nghệ&quot; c&oacute; thể chia l&agrave;m ba c&aacute;i giai đoạn: Giai đoạn thứ nhất, năm tuổi đến 7 tuổi đứa b&eacute; tự do hoạt động; giai đoạn thứ hai, 7 tuổi đến 10 tuổi thiếu ni&ecirc;n, &quot;V&otilde; nghệ thiếu ni&ecirc;n&quot; tiến về xung quanh rừng c&acirc;y săn bắt chỉ định con mồi, &quot;Văn nghệ thiếu ni&ecirc;n&quot; tiến về xung quanh rừng c&acirc;y thu thập chỉ định dược thảo; giai đoạn thứ ba, 10 tuổi đến 12 tuổi thiếu ni&ecirc;n, &quot;V&otilde; nghệ thiếu ni&ecirc;n&quot; đi theo tuần n&uacute;i đội tuần n&uacute;i, &quot;Văn nghệ thiếu ni&ecirc;n&quot; đi theo thương hộ tiến về Lạc Diệp th&agrave;nh.<br />\r\n<br />\r\nĐầu m&ugrave;a xu&acirc;n &aacute;nh nắng mặt trời giống như kim tuyến một dạng để lọt tiến v&agrave;o ngọn c&acirc;y, tung ở tr&ecirc;n s&acirc;n tập v&otilde;, mắt nh&igrave;n tr&ecirc;n diễn v&otilde; trường hơn 50 c&aacute;i học nghệ thiếu ni&ecirc;n, Dịch T&iacute;ch Phong c&ugrave;ng L&acirc;m Phong Hỏa đều cũng to&aacute;t ra hướng tới &aacute;nh mắt. C&ograve;n c&oacute; 2 th&aacute;ng L&acirc;m Phong Hỏa liền đ&acirc;̀y năm tuổi, sẽ phải bắt đầu hắn tập nghệ kiếp sống, m&agrave; Dịch T&iacute;ch Phong c&ograve;n phải đợi th&ecirc;m năm th&aacute;ng mới c&oacute; thể đ&acirc;̀y năm tuổi. Kh&ocirc;ng qua với tư c&aacute;ch l&agrave;m người hai đời, hắn biết r&otilde; thời gian như kim. V&igrave; lẽ đ&oacute; từ 4 tuổi l&ecirc;n, Dịch T&iacute;ch Phong liền ki&ecirc;n tr&igrave; tham gia &quot;Tập nghệ&quot;, tham gia s&aacute;ng sớm đọc, nhưng l&agrave; bởi v&igrave; hắn qu&aacute; nhỏ kh&ocirc;ng tham ngộ th&ecirc;m &quot;V&otilde; nghệ&quot;, thế l&agrave; Dịch T&iacute;ch Phong liền theo học tập &quot;Văn nghệ&quot; .&quot;Văn nghệ&quot; chủ yếu chia l&agrave;m: Thức, sơ bộ quen biết thực vật động vật; biện, sơ bộ ph&acirc;n r&otilde; dược liệu đạt tới độc vật; to&aacute;n, chắc chắn, đơn giản k&yacute; sổ; b&aacute;c, đơn giản đ&aacute;nh cuộc, phương ph&aacute;p kinh doanh; thư, văn chương cơ bản phương ph&aacute;p s&aacute;ng t&aacute;c. Dịch T&iacute;ch Phong sinh l&yacute; tuổi t&aacute;c mặc d&ugrave; chỉ c&oacute; 4 tuổi, nhưng l&agrave; trong l&ograve;ng tuổi t&aacute;c vượt xa đ&aacute;m n&agrave;y 5 ~ 6 tuổi tiểu th&iacute; h&agrave;i, chỉ d&ugrave;ng một năm thời gian liền ho&agrave;n th&agrave;nh &quot;Văn nghệ&quot; giai đoạn thứ nhất, hiện tại hắn mỗi ng&agrave;y buổi chiều đều sẽ đi theo 1 đ&aacute;m t&aacute;m, chín tuổi thiếu ni&ecirc;n tiến về xung quanh rừng c&acirc;y thu thập thảo dược.<br />\r\n<br />\r\nV&igrave; lẽ đ&oacute; tại Ẩn Nh&acirc;n th&ocirc;n chung quanh trong rừng c&acirc;y thường xuy&ecirc;n sẽ thấy 1 đ&aacute;m t&aacute;m, chín tuổi thiếu ni&ecirc;n b&ecirc;n trong c&oacute; một phấn đi&ecirc;u ngọc tr&aacute;c tiểu đồng, ở sau lưng một c&ugrave;ng hắn kh&ocirc;ng ch&ecirc;nh lệch nhiều thuốc giỏ. Ngay cả giảng dạy Dịch T&iacute;ch Phong dược liệu kiến thức Vương l&atilde;o lang trung, . Đều cũng kỳ lạ Dịch T&iacute;ch Phong thi&ecirc;n ph&uacute;. Nhưng Dịch T&iacute;ch Phong bản th&acirc;n nắm được, m&igrave;nh ở y dược phương diện cũng kh&ocirc;ng c&oacute; bao nhi&ecirc;u ng&agrave;y ph&uacute;, duy nhất chiếm ưu thế ch&iacute;nh l&agrave; c&oacute; một c&aacute;i th&agrave;nh thục linh hồn, tại 1 đ&aacute;m 5 ~ 6 tuổi h&agrave;i đồng b&ecirc;n trong tự nhi&ecirc;n l&agrave; phi thường đột xuất, nhưng l&agrave; c&ugrave;ng 1 đ&aacute;m t&aacute;m, chín tuổi thiếu ni&ecirc;n đồng thời h&aacute;i thuốc thời điểm loại ưu thế n&agrave;y liền kh&ocirc;ng r&otilde; r&agrave;ng như vậy, ngẫu nhi&ecirc;n c&oacute; ch&uacute;t quả thật c&oacute; dược liệu thi&ecirc;n ph&uacute; thiếu ni&ecirc;n, l&agrave;m kh&ocirc;ng thể so Dịch T&iacute;ch Phong k&eacute;m.<br />\r\n<br />\r\nThẳng đến Dịch T&iacute;ch Phong sửa sang năm tuổi 1 ng&agrave;y n&agrave;y, ở kết th&uacute;c một buổi chiều h&aacute;i thuốc về sau, Dịch T&iacute;ch Phong đem tr&agrave;n đầy một giỏ dược liệu giao cho tiệm thuốc Vương lang trung, tại Vương lang trung &aacute;nh mắt t&aacute;n dương b&ecirc;n trong, Dịch T&iacute;ch Phong xấu hổ cười n&oacute;i: &quot;Vương gia gia, trời s&aacute;ng Tiểu Phong kh&ocirc;ng tới.&quot;<br />\r\n<br />\r\nVương lang trung hơi sững sờ, về sau cười n&oacute;i: &quot;L&agrave;m sao? Lại muốn c&ugrave;ng lấy thương đội đi trong th&agrave;nh nh&igrave;n một ch&uacute;t, Vương gia gia n&oacute;i ngươi c&ograve;n nhỏ, mặc d&ugrave; ngươi đang biện dược, thức độc phương diện xem như l&agrave; xuất sư, nhưng c&ograve;n cần ma luyện.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải l&agrave;, &yacute; của ta l&agrave;, ta sau n&agrave;y kh&ocirc;ng thể học c&aacute;i n&agrave;y văn nghệ kỹ năng.&quot; Dịch T&iacute;ch Phong nghi&ecirc;m t&uacute;c hướng về Vương lang trung n&oacute;i ra.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng học văn nghệ kỹ năng? C&aacute;i kia học c&aacute;i g&igrave;?&quot; Vương lang trung nghi hoặc hỏi.<br />\r\n<br />\r\n&quot;V&otilde; nghệ!&quot; Dịch T&iacute;ch Phong n&oacute;i như đinh ch&eacute;m sắt xuất 2 chữ, trong mắt tr&agrave;n ngập cuồng nhiệt!<br />\r\n<br />\r\nVương lang trung nh&igrave;n ra Dịch T&iacute;ch Phong trong mắt cuồng nhiệt c&ugrave;ng chấp nhất, đang kinh ngạc đồng thời hơi c&oacute; một tia tiếc nuối.<br />\r\n<br />\r\nDịch T&iacute;ch Phong n&oacute;i ra 2 c&aacute;i kia chữ về sau liền nh&igrave;n chằm chằm v&agrave;o Vương lang trung, mặc d&ugrave; luyện v&otilde; nghệ vốn ch&iacute;nh l&agrave; bản th&acirc;n một mực mộng tưởng, nhưng d&ugrave; sao đi theo Vương l&atilde;o lang trung thời gian hai năm học tập thức, biện kỹ năng, đối với c&aacute;i n&agrave;y nghi&ecirc;m nghị l&atilde;o đầu vẫn l&agrave; tr&agrave;n đầy cảm k&iacute;ch. Dịch T&iacute;ch Phong khom người thật s&acirc;u đối với Vương lang trung thi lễ, xoay người, ki&ecirc;n định đi ra tiệm thuốc, thẳng đến đi tới cửa thời điểm Dịch T&iacute;ch Phong d&ugrave;ng non nớt khẩu &acirc;m n&oacute;i ra:<br />\r\n<br />\r\n&quot;Văn v&otilde; kh&ocirc;ng trước sau, nắm đao lại giống chấp b&uacute;t. Ch&iacute;nh kh&iacute; chấn động Tứ Hải, ph&oacute;ng kho&aacute;ng x&acirc;u thi&ecirc;n hạ. Anh h&ugrave;ng kiếm trong tay, ti&ecirc;n sinh b&uacute;t trong tay. Chớ luận văn c&ugrave;ng v&otilde;, tuổi nhỏ đem tập nghệ!&quot;</p>', '2021-12-01 01:26:03', 7, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(279, 73, 'Có Thể Uống Nhất Chén Không', '3', '<p>Ẩn Nh&acirc;n th&ocirc;n vị tr&iacute; trung t&acirc;m, c&oacute; năm đầu v&agrave;o v&agrave; năm đầu ra đại trạch viện, trạch viện chung quanh đều c&oacute; d&aacute;ng người sung sức, v&ocirc; c&ugrave;ng mạnh mẽ th&ocirc;n d&acirc;n bảo vệ. M&agrave; ở 1 ch&uacute;t yếu hại vị tr&iacute; đều sẽ c&oacute; 1 người người mặc m&agrave;u x&aacute;m nhạt li&ecirc;n thể mũ tr&ugrave;m th&acirc;n ảnh, Họ kh&ocirc;ng cứng c&aacute;p, nhưng tinh xảo b&igrave; gi&aacute;p vẫn kh&oacute; n&eacute;n bọn họ mạnh mẽ th&acirc;n h&igrave;nh, bọn họ đều mang mũ tr&ugrave;m thấy kh&ocirc;ng r&otilde; mặt mũi, nhưng từ chung quanh th&ocirc;n d&acirc;n ngẫu nhi&ecirc;n quăng tới k&iacute;nh sợ &aacute;nh mắt, đ&oacute; c&oacute; thể thấy được bọn họ tại Ẩn Nh&acirc;n th&ocirc;n th&ocirc;n d&acirc;n trong l&ograve;ng địa vị, bọn họ liền l&agrave; Ẩn Nh&acirc;n th&ocirc;n cường đại nhất vũ trang sức mạnh &mdash; &mdash; Tuần Sơn đội. Bởi vậy c&oacute; thể thấy được chỗ n&agrave;y trạch viện tầm quan trọng, nơi n&agrave;y ch&iacute;nh l&agrave; Ẩn Nh&acirc;n th&ocirc;n Trưởng L&atilde;o hội.<br />\r\n<br />\r\n~~~ l&uacute;c n&agrave;y tại Trưởng L&atilde;o hội trung t&acirc;m nhất một gian nội sảnh b&ecirc;n trong, ngồi 4 vị l&atilde;o giả, trong đ&oacute; ngồi ở tr&ecirc;n nhất tay bất ngờ ch&iacute;nh l&agrave; Ẩn Nh&acirc;n th&ocirc;n th&ocirc;n trưởng L&acirc;m Hằng Sơn. Ở trước mặt của hắn b&agrave;y biện một tấm to lớn sa b&agrave;n địa đồ, trương n&agrave;y sa b&agrave;n địa đồ chế t&aacute;c cực kỳ ho&agrave;n mỹ, sở hiện ra cũng l&agrave; Ẩn Nh&acirc;n th&ocirc;n chung quanh vị tr&iacute; địa l&yacute; c&ugrave;ng h&igrave;nh dạng mặt đất.<br />\r\n<br />\r\n&quot;Tướng qu&acirc;n, Thiết T&acirc;m th&ocirc;n đ&aacute;m kia loạn thần tặc tử đ&atilde; c&ugrave;ng ch&uacute;ng ta sinh ra mấy lần xung đột, liền ở h&ocirc;m qua chạng vạng tối, bọn họ đ&aacute;nh l&eacute;n ch&uacute;ng ta tại th&ocirc;n mặt đ&ocirc;ng một chỗ quặng mỏ, ch&uacute;ng ta chết hơn mười người th&ocirc;n d&acirc;n c&ugrave;ng 3 c&aacute;i trai tr&aacute;ng.&quot;<br />\r\n<br />\r\n&quot;Tuần Sơn đội thế nhưng bị ph&aacute;t hiện?&quot; L&acirc;m Hằng Sơn &aacute;nh mắt lạnh lẽo n&oacute;i ra.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute;, Tuần Sơn đội dựa theo quy định kh&ocirc;ng c&oacute; nhận được mệnh lệnh, chỉ l&agrave; ẩn nấp, kh&ocirc;ng c&oacute; xuất thủ. C&oacute; mặt 5 t&ecirc;n hộ vệ cũng đi c&ugrave;ng t&ecirc;n kia Tuần Sơn đội vi&ecirc;n c&ugrave;ng nhau r&uacute;t ra.&quot;<br />\r\n<br />\r\n&quot;Tốt! Mười mấy c&aacute;i th&ocirc;n d&acirc;n người trong gia đ&igrave;nh hảo hảo trợ cấp, c&aacute;i kia 3 đứa h&agrave;i tử, đem t&ecirc;n của bọn hắn khắc v&agrave;o hậu viện tr&ecirc;n tấm bia a!&quot; L&acirc;m Hằng Sơn nhẹ nh&agrave;ng dừng lại, cũng kh&ocirc;ng quay đầu lại đối với sau lưng người kia n&oacute;i: &quot;L&atilde;o nhị, đem Thừa Đ&agrave;o c&aacute;i đứa b&eacute; kia gọi tới.&quot;<br />\r\n<br />\r\nSau lưng người kia khom người đồng &yacute;, về sau lui ra khỏi ph&ograve;ng.<br />\r\n<br />\r\nCũng kh&ocirc;ng l&acirc;u lắm, trong sảnh đột nhi&ecirc;n xuất hiện 1 th&acirc;n ảnh, hắn th&acirc;n cao 1m75 tả hữu, th&acirc;n mang m&agrave;u x&aacute;m liền &aacute;o mũ tr&ugrave;m, hắn kh&ocirc;ng phải đến mũ tr&ugrave;m, một tấm oai h&ugrave;ng sạch sẽ mặt để cho hắn mang theo 1 tia d&aacute;ng vẻ thư sinh, thoạt nh&igrave;n chỉ c&oacute; chừng hai mươi tuổi, chỉ l&agrave; đ&ocirc;i m&ocirc;i hơi mỏng c&ugrave;ng hai đầu l&ocirc;ng m&agrave;y 1 tia kia như c&oacute; như kh&ocirc;ng s&aacute;t &yacute;, khiến mọi người biết r&otilde; người n&agrave;y cũng kh&ocirc;ng muốn nh&igrave;n từ bề ngo&agrave;i như vậy yếu đuối.<br />\r\n<br />\r\n&quot;B&aacute;i kiến đại nh&acirc;n!&quot; Nam tử trẻ tuổi tại L&acirc;m Hằng Sơn sau lưng năm bước chỗ dừng lại, khom m&igrave;nh h&agrave;nh lễ.<br />\r\n<br />\r\n&quot;Tuần Sơn đội huấn luyện như thế n&agrave;o?&quot; L&acirc;m Hằng Sơn hướng về sa b&agrave;n hỏi.<br />\r\n<br />\r\n&quot;C&ograve;n c&oacute; thể.&quot; Nam tử trẻ tuổi trầm thấp trả lời.<br />\r\n<br />\r\n&quot;Ngươi tiến Tuần Sơn đội gần mười năm a?&quot; L&acirc;m Hằng Sơn khẽ ngẩng đầu l&ecirc;n nh&igrave;n về ph&iacute;a treo ở tr&ecirc;n sa b&agrave;n mới ngọn đ&egrave;n, xuất thần hỏi.<br />\r\n<br />\r\n&quot;L&agrave;, 9 năm lẻ năm th&aacute;ng.&quot; Nam tử trẻ tuổi trả lời.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Ngươi đến Tuần Sơn đội diệt Thiết T&acirc;m th&ocirc;n 10 ng&agrave;y thế nhưng đầy đủ?&quot; L&acirc;m Hằng Sơn lần thứ nhất quay đầu hướng về trước mặt vị trẻ tuổi n&agrave;y.<br />\r\n<br />\r\n&quot;Bảy ng&agrave;y liền c&oacute; thể.&quot; Nam tử trẻ tuổi lạnh l&ugrave;ng trả lời.<br />\r\n<br />\r\nL&acirc;m Hằng Sơn chậm r&atilde;i đi đến nam tử trẻ tuổi trước mặt, nhẹ giọng lại v&ocirc; c&ugrave;ng ki&ecirc;n định n&oacute;i: &quot;5 năm, 3 ng&agrave;y diệt quyết t&acirc;m!&quot;<br />\r\n<br />\r\nNam tử trẻ tuổi nh&igrave;n thẳng L&acirc;m Hằng Sơn, một l&uacute;c l&acirc;u sau, khom người n&oacute;i ra: &quot;V&acirc;ng!&quot;<br />\r\n<br />\r\n. . .... ... ... .... . .<br />\r\n<br />\r\nDịch T&iacute;ch Phong giờ ph&uacute;t n&agrave;y ch&iacute;nh ở tr&ecirc;n s&acirc;n tập v&otilde; khu&ocirc;n mặt nhỏ đỏ bừng tiến h&agrave;nh &quot;V&otilde; nghệ&quot; b&ecirc;n trong &quot;Đứng&quot; huấn luyện, hắn cũng kh&ocirc;ng biết, trong tương lai 5 năm Ẩn Nh&acirc;n th&ocirc;n tương ngh&ecirc;nh đến to lớn biến cố, hắn hiện tại nghĩ l&agrave; như thế n&agrave;o ho&agrave;n th&agrave;nh những c&aacute;i n&agrave;y hắn thấy cơ bản kh&ocirc;ng c&oacute; khả năng ho&agrave;n th&agrave;nh huấn luyện.&quot;V&otilde; nghệ&quot; chia l&agrave;m: Chạy, chạy cự li d&agrave;i; vọt, nhảy c&oacute;c; đứng, đứng tấn; đấu, bắt đối với đấu vật; bắn, bắn t&ecirc;n trả gi&aacute;.<br />\r\n<br />\r\nDịch T&iacute;ch Phong mỗi ng&agrave;y từ giờ M&atilde;o đến giờ Th&igrave;n, học tập 1 canh giờ (hai giờ) &quot;Hiểu nghệ&quot;, lấy tăng trưởng tri thức. Từ giờ Th&igrave;n đến giờ ngọ 2 canh giờ, luyện tập chạy nửa canh giờ, luyện tập vọt nửa canh giờ, luyện tập đứng 1 canh giờ. Bởi v&igrave; Dịch T&iacute;ch Phong tuổi t&aacute;c c&ograve;n thấp c&ograve;n chưa bắt đầu luyện tập đấu c&ugrave;ng bắn. Nhưng ch&iacute;nh l&agrave; ba hạng n&agrave;y huấn luyện đối với đại đa số giống Dịch T&iacute;ch Phong dạng n&agrave;y vừa mới bắt đầu huấn luyện năm tuổi tiểu đồng, cũng l&agrave; kh&ocirc;ng thể n&agrave;o ho&agrave;n th&agrave;nh. C&ugrave;ng Dịch T&iacute;ch Phong một nh&oacute;m tiến v&agrave;o &quot;Tập nghệ&quot; năm tuổi tiểu đồng, năm nay ước chừng c&oacute; 50 người. C&oacute; thể ho&agrave;n th&agrave;nh nửa canh giờ chạy huấn luyện c&oacute; 27 người, 27 c&aacute;i n&agrave;y người b&ecirc;n trong có th&ecirc;̉ ho&agrave;n th&agrave;nh vọt huấn luyện chỉ c&oacute; s&aacute;u người.<br />\r\n<br />\r\nM&agrave; đứng huấn luyện cũng ch&iacute;nh l&agrave; đứng tấn, cho đến nay kh&ocirc;ng c&oacute; người ho&agrave;n th&agrave;nh.<br />\r\n<br />\r\n&quot;Ph&ugrave; ph&ugrave;!&quot; Dịch T&iacute;ch Phong đặt m&ocirc;ng hạ ngồi tr&ecirc;n mặt đất, đỏ rực khu&ocirc;n mặt nhỏ vẻ mặt phiền muộn, một đ&ocirc;i b&eacute;o m&uacute;p m&iacute;p tay nhỏ đặt ở tr&ecirc;n đầu gối, vẫn ngăn kh&ocirc;ng được hai ch&acirc;n run rẩy. Hắn quay đầu lại mắt nh&igrave;n một dạng đỏ bừng cả khu&ocirc;n mặt, tư thế đ&atilde; nghi&ecirc;m trọng biến dạng L&acirc;m Phong Hỏa, kh&ocirc;ng c&oacute; tim kh&ocirc;ng c&oacute; phổi n&oacute;i ra: &quot;Hắc Tử, tr&ecirc;n mặt đất ngồi thế nhưng thư th&aacute;i! Ngươi kh&ocirc;ng thử một ch&uacute;t?&quot; N&oacute;i ra tay nhỏ vẫn ở b&ecirc;n cạnh thổ địa b&ecirc;n tr&ecirc;n vỗ vỗ.<br />\r\n<br />\r\nL&acirc;m Phong Hỏa hướng Dịch T&iacute;ch Phong chớp mắt v&agrave;i c&aacute;i, lại hướng hắn nhe nhe răng. Cũng kh&ocirc;ng l&acirc;u lắm, &quot;Ph&ugrave; ph&ugrave;!&quot; 1 tiếng, hắn cũng hạ ng&atilde; tr&ecirc;n mặt đất, c&ugrave;ng Dịch T&iacute;ch Phong ngồi dưới đất kh&aacute;c biệt, hắn l&agrave; trực tiếp h&igrave;nh chữ đại nằm ở nơi đ&oacute;, hơi h&iacute;p mắt lại n&oacute;i ra: &quot;T&iacute;ch Phong, ta cảm thấy ngươi n&oacute;i kh&ocirc;ng đ&uacute;ng, ngươi khối kia khẳng định kh&ocirc;ng bằng ta đ&acirc;y khối dễ chịu.&quot;<br />\r\n<br />\r\n2 người một nằm, một ngồi kh&ocirc;ng n&oacute;i g&igrave;, đều tại trải nghiệm c&aacute;i n&agrave;y hiếm thấy thời gian nghỉ ngơi. Ước chừng qua 1 khắc, lại l&agrave; 1 tiếng &quot;Ph&ugrave; ph&ugrave;&quot; &acirc;m thanh, chỉ thấy một cao gầy nam h&agrave;i cũng ngồi sập xuống đất. Hắn nh&igrave;n qua muốn so Dịch T&iacute;ch Phong, L&acirc;m Phong Hỏa lớn hơn một ch&uacute;t, ước chừng 7 tuổi tả hữu d&aacute;ng vẻ, th&acirc;n mang 1 th&acirc;n thanh sam, ch&acirc;n lấy một đ&ocirc;i m&agrave;u trắng gi&agrave;y vải. L&ocirc;ng m&agrave;y hơi c&oacute; vẻ thưa thớt, c&aacute;i mũi cao thẳng như ưng c&acirc;u, bờ m&ocirc;i hơi bạc. Hắn ngồi dưới đất hơi hơi thở hổn hển, thoạt nh&igrave;n cũng rất mệt mỏi, nhưng xa c&ograve;n chưa tới Dịch T&iacute;ch Phong, L&acirc;m Phong Hỏa dạng n&agrave;y mệt mỏi mức độ.<br />\r\n<br />\r\nHắn hơi hơi liếc nh&igrave;n chung quanh một v&ograve;ng, ph&aacute;t hiện chung quanh trong bạn c&ugrave;ng lứa tuổi kh&ocirc;ng ai đứng đấy, trong mắt vẻ tự đắc lại kh&oacute; che lấp. Đột nhi&ecirc;n &aacute;nh mắt của hắn s&aacute;ng l&ecirc;n, kh&oacute;e miệng lộ ra mỉm cười, sau đ&oacute; chậm r&atilde;i đứng l&ecirc;n, hướng L&acirc;m Phong Hỏa đi tới.<br />\r\n<br />\r\n&quot;Phong Hỏa thiếu gia, xem ra th&ocirc;n trưởng đại nh&acirc;n cũng kh&ocirc;ng c&oacute; truyền thụ cho ngươi hắn ch&acirc;n truyền a. Vẫn l&agrave; n&oacute;i ngươi học đồ vật ch&iacute;nh l&agrave; kh&ocirc;ng bằng gia gia của ta dạy ta?&quot; Cao gầy nam h&agrave;i cười hắc hắc n&oacute;i.<br />\r\n<br />\r\n&quot;Mũi to, ngươi c&aacute;ch ta xa một ch&uacute;t. C&aacute;i mũi của ngươi muốn đụng phải ta!&quot; L&acirc;m Phong Hỏa nh&igrave;n cũng chưa từng nh&igrave;n cao gầy nam h&agrave;i, vẫn hiện ra h&igrave;nh chữ đại nằm ở đ&oacute;. Nhưng lời của hắn lại l&agrave;m cho chung quanh 1 đ&aacute;m tiểu đồng nghe r&otilde;, dẫn tới chung quanh một trận cười to, Dịch T&iacute;ch Phong c&aacute;ch L&acirc;m Phong Hỏa kh&ocirc;ng xa, tự nhi&ecirc;n nghe r&otilde;, kh&oacute;e miệng kh&ocirc;ng khỏi hiện l&ecirc;n vẻ mỉm cười.<br />\r\n<br />\r\nCao gầy nam h&agrave;i ph&aacute;t hiện chung quanh tiểu đồng đều tại cười hắn, lập tức đỏ mặt l&ecirc;n, nhưng lại kh&ocirc;ng d&aacute;m đối với L&acirc;m Phong Hỏa ph&aacute;t t&aacute;c, đ&atilde; thấy b&ecirc;n cạnh mặt Dịch T&iacute;ch Phong cũng mặt mỉm cười, lập tức giận dữ, &aacute;c thanh đạo: &quot;Ngươi c&aacute;i n&agrave;y d&acirc;n đen cười c&aacute;i g&igrave;? !&quot;<br />\r\n<br />\r\nDịch T&iacute;ch Phong ngồi dưới đất ngẩng đầu nh&igrave;n một ch&uacute;t cao gầy nam h&agrave;i, m&iacute;m m&ocirc;i lộ ra một xấu hổ mỉm cười, con mắt cũng h&iacute;p lại th&agrave;nh đẹp mắt nguyệt nha h&igrave;nh, hắn vừa mới muốn n&oacute;i g&igrave;, 1 b&ecirc;n L&acirc;m Phong Hỏa lại một lần ngồi dậy, mặt lạnh đối với cao gầy nam h&agrave;i n&oacute;i: &quot;L&acirc;m L&ocirc;i, ngươi tốt nhất c&aacute;ch bằng hữu của ta cũng xa một ch&uacute;t nhi!&quot;<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave;? Phong Hỏa thiếu gia, ngươi, ngươi v&acirc;̣y mà n&oacute;i . . . Hắn, hắn l&agrave;m sao phối hợp . . .&quot; Đang l&uacute;c L&acirc;m L&ocirc;i một b&ecirc;n chấn kinh tại L&acirc;m Phong Hỏa m&agrave; n&oacute;i, lại phẫn nộ tại Dịch T&iacute;ch Phong chế giễu l&uacute;c. 1 tiếng thanh &acirc;m lạnh l&ugrave;ng ở sau lưng hắn vang l&ecirc;n.<br />\r\n<br />\r\n&quot;Phối hợp c&ugrave;ng kh&ocirc;ng xứng chỉ c&oacute; ch&iacute;nh hắn định đoạt. L&acirc;m L&ocirc;i, ngươi nếu c&oacute; thời gian ở trong n&agrave;y chế giễu so ngươi tuổi nhỏ người, kh&ocirc;ng bằng đi ph&iacute;a bắc rừng c&acirc;y săn một con s&oacute;i.&quot;<br />\r\n<br />\r\nL&acirc;m L&ocirc;i quay đầu, nh&igrave;n thấy 1 vị người mặc &aacute;o gi&aacute;p trung ni&ecirc;n nam tử, &aacute;nh nắng từ nam tử đỉnh đầu bỏ ra đến, để cho hắn thấy kh&ocirc;ng r&otilde; nam tử dung mạo, nhưng l&agrave; L&acirc;m L&ocirc;i biết r&otilde; vị n&agrave;y lấy nghi&ecirc;m ngặt xưng danh tập nghệ tổng gi&aacute;o đầu &mdash; &mdash; L&yacute; Thừa Kiền.<br />\r\n<br />\r\nL&yacute; Thừa Kiền l&uacute;c tuổi c&ograve;n trẻ l&agrave; một người Tuần Sơn đội đội vi&ecirc;n, với tư c&aacute;ch th&ocirc;n thần b&iacute; nhất, cũng l&agrave; cường đại nhất vũ trang sức mạnh. L&yacute; Thừa Kiền tự nhi&ecirc;n nhận th&ocirc;n nh&acirc;n k&iacute;nh y&ecirc;u. Chậm r&atilde;i L&yacute; Thừa Kiền tuổi t&aacute;c cao, liền thối lui ra khỏi Tuần Sơn đội, tại diễn v&otilde; trường l&agrave;m l&ecirc;n tổng gi&aacute;o đầu, dạy bảo th&ocirc;n đời sau.<br />\r\n<br />\r\nL&acirc;m L&ocirc;i run run n&oacute;i ra: &quot;Tổng, tổng gi&aacute;o đầu, lang . . . Lang . . .&quot;<br />\r\n<br />\r\nL&yacute; Thừa Kiền cắt đứt L&acirc;m L&ocirc;i m&agrave; n&oacute;i, n&oacute;i ra: &quot;L&acirc;m L&ocirc;i, c&ograve;n c&oacute; 3 th&aacute;ng ngươi liền 8 tuổi, tuy n&oacute;i săn lang l&agrave; 10 tuổi v&otilde; nghệ thiếu ni&ecirc;n mới c&oacute; thể độc lập ho&agrave;n th&agrave;nh huấn luyện. Nhưng l&agrave; ta hiểu ngươi, ngươi thi&ecirc;n ph&uacute; kh&ocirc;ng tồi, nhưng dễ d&agrave;ng tự đắc &yacute; đ&acirc;̀y, cần phải c&oacute; nhất định &aacute;p lực mới c&oacute; thể tốt hơn trưởng th&agrave;nh, ta tin tưởng ngươi, kh&ocirc;ng muốn cho gia gia ngươi mất mặt! L&atilde;o nh&acirc;n gia năm đ&oacute; cũng l&agrave; như vậy huấn luyện ta! Ha ha ha&quot; n&oacute;i xong, L&yacute; Thừa Kiền liền tự m&igrave;nh nở nụ cười. Bất qu&aacute; Dịch T&iacute;ch Phong l&agrave;m sao nghe theo đều cảm thấy c&oacute; một tia cười tr&ecirc;n nỗi đau của người kh&aacute;c, 1 tia b&aacute;o th&ugrave; rửa hận, 1 tia đạt được ước muốn cảm gi&aacute;c.<br />\r\n<br />\r\nL&acirc;m L&ocirc;i ch&aacute;n gh&eacute;t chạy, L&yacute; Thừa Kiền cũng dừng lại cười to. Hắn mắt nh&igrave;n vẻ mặt kh&oacute; chịu L&acirc;m Phong Hỏa n&oacute;i ra: &quot;Tiểu tử th&uacute;i, kh&ocirc;ng tệ lắm, mới tới 3 th&aacute;ng liền đ&atilde; ki&ecirc;n tr&igrave; đứng huấn luyện hơn nửa canh giờ, xem ra ngươi đ&atilde; t&igrave;m được bản th&acirc;n vận k&igrave;nh phương thức, . Quả nhi&ecirc;n thi&ecirc;n tư rất cao!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nĐ&oacute;n lấy, L&yacute; Thừa Kiền quay đầu đối với Dịch T&iacute;ch Phong n&oacute;i: &quot;C&aacute;c ngươi 1 nh&oacute;m n&agrave;y s&aacute;u người b&ecirc;n trong, th&acirc;n thể của ngươi cơ sở xem như l&agrave; k&eacute;m nhất, miễn cưỡng xem như l&agrave; c&oacute; ch&uacute;t tư chất.&quot; L&uacute;c n&agrave;y L&yacute; Thừa Kiền ngữ điệu kh&ocirc;ng mang theo 1 tia t&igrave;nh cảm, chắc hẳn đ&acirc;y l&agrave; một c&aacute;i phi thường đ&uacute;ng trọng t&acirc;m đ&aacute;nh gi&aacute;.<br />\r\n<br />\r\nDịch T&iacute;ch Phong gật đầu một c&aacute;i, sạch sẽ tr&ecirc;n khu&ocirc;n mặt nhỏ nhắn cũng kh&ocirc;ng c&oacute; 1 tia uể oải hoặc l&agrave; mất m&aacute;t, hắn vẫn b&igrave;nh tĩnh mắt nh&igrave;n L&yacute; Thừa Kiền.<br />\r\n<br />\r\nL&yacute; Thừa Kiền c&oacute; vẻ kinh ngạc, chậm r&atilde;i n&oacute;i ra: &quot;Nhưng l&agrave; ngươi vậy m&agrave; tại đi tới nơi n&agrave;y kh&ocirc;ng tới thời gian một th&aacute;ng b&ecirc;n trong, ki&ecirc;n tr&igrave; đứng huấn luyện r&ograve;ng r&atilde; nửa canh giờ. Kh&ocirc;ng thể kh&ocirc;ng n&oacute;i ta đối với kỳ vọng của ngươi nhiều nhất l&agrave; th&ocirc;ng qua vọt huấn luyện.&quot;<br />\r\n<br />\r\n&quot;C&agrave;ng l&agrave;m ta hơn ngạc nhi&ecirc;n l&agrave;, ngươi cũng kh&ocirc;ng hiểu bản th&acirc;n Ngự K&igrave;nh ph&aacute;p.&quot; L&yacute; Thừa Kiền liếc Dịch T&iacute;ch Phong hai ch&acirc;n run rẩy một c&aacute;i, trong ng&ocirc;n ngữ vẻ t&aacute;n thưởng dần dần d&agrave;y.<br />\r\n<br />\r\n&quot;Cổ nh&acirc;n n&oacute;i, kẻ thu th&acirc;̣p, nghị lực l&agrave;m đầu. Một c&aacute;i tuổi gần năm tuổi tiểu đồng, lại c&oacute; như vậy ki&ecirc;n cường &yacute; ch&iacute;! Kh&oacute; c&oacute; được kh&oacute; c&oacute; được!&quot; L&yacute; Thừa Kiền c&agrave;ng n&oacute;i c&agrave;ng vui vẻ, từ b&ecirc;n h&ocirc;ng lấy ra một c&aacute;i hồ l&ocirc; rượu uống. Uống v&agrave;o mấy ngụm, L&yacute; Thừa Kiền đột nhi&ecirc;n đối với Dịch T&iacute;ch Phong n&oacute;i ra: &quot;Tiểu tử, ta dạy cho ngươi Ngự K&igrave;nh như thế n&agrave;o?&quot;<br />\r\n<br />\r\nDịch T&iacute;ch Phong nghĩ thầm: &quot;Loại sự t&igrave;nh n&agrave;y l&agrave; lựa chọn đề thiết lập sao? Kh&ocirc;ng phải l&agrave; sửa c&acirc;u c&oacute; vấn đề sao? Đem c&acirc;u nghi vấn cải th&agrave;nh c&acirc;u trần thuật.&quot;<br />\r\n<br />\r\nDịch T&iacute;ch Phong một ch&uacute;t khom người n&oacute;i: &quot;C&oacute; mong muốn vậy, kh&ocirc;ng d&aacute;m mời ngươi.&quot;<br />\r\n<br />\r\nL&yacute; Thừa Kiền lại n&eacute; tr&aacute;nh Dịch T&iacute;ch Phong 1 b&aacute;i n&agrave;y, cười mắng: &quot;Ngươi tiểu tử th&uacute;i n&agrave;y nhưng thật ra th&ocirc;ng minh, đ&aacute;ng tiếc tư chất ngươi c&oacute; hạn, ta truyền cho ngươi Ngự K&igrave;nh ph&aacute;p cũng kh&ocirc;ng phải thu ngươi l&agrave;m đồ, ngươi lại giống kh&ocirc;ng cần b&aacute;i ta. Hơn nữa ta truyền cho ngươi phương ph&aacute;p, c&oacute; n&oacute; tr&iacute; mạng khuyết điểm ta sẽ nắm được n&oacute;i r&otilde; với ngươi, học c&ugrave;ng kh&ocirc;ng học to&agrave;n ở ngươi lấy hay bỏ.&quot;<br />\r\n<br />\r\n&quot;Mời trưởng giả ban thưởng!&quot; Dịch T&iacute;ch Phong vẫn khom người n&oacute;i.<br />\r\n<br />\r\n&quot;Ha ha ha ha ha&quot; L&yacute; Thừa Kiền thoải m&aacute;i cười một tiếng, đột nhi&ecirc;n n&acirc;ng l&ecirc;n trong tay hồ l&ocirc; rượu hướng Dịch T&iacute;ch Phong lắc lắc, hỏi: &quot;Có th&ecirc;̉ uống một ch&eacute;n kh&ocirc;ng?&quot;</p>', '2021-12-01 01:26:22', 6, 1),
(280, 73, 'Tửu Khí Ngự Kình', '4', '<p>Dịch T&iacute;ch Phong hướng về trước mắt hồ l&ocirc; rượu, nghe được lời n&oacute;i của L&yacute; Thừa Kiền. Thầm nghĩ trong l&ograve;ng: &quot;Ta cũng kh&ocirc;ng c&oacute; t&ugrave;y th&acirc;n đến c&aacute;i ch&eacute;n a!&quot; Nhưng ngay l&uacute;c đ&oacute; hắn ph&aacute;t hiện đ&oacute; cũng kh&ocirc;ng phải vấn đề trọng điểm, kh&ocirc;ng khỏi nhăn nhăn đẹp mắt l&ocirc;ng m&agrave;y, nh&igrave;n về ph&iacute;a L&yacute; Thừa Kiền, tiếng non nớt n&oacute;i: &quot;V&igrave; sao?&quot;<br />\r\n<br />\r\nL&yacute; Thừa Kiền thu liễm nụ cười, b&igrave;nh tĩnh n&oacute;i: &quot;Tiểu tử, ngươi c&oacute; biết c&aacute;i g&igrave; l&agrave; rượu sao?&quot; L&yacute; Thừa Kiền đem hồ l&ocirc; chậm r&atilde;i nghi&ecirc;ng đổ, m&agrave;u hổ ph&aacute;ch rượu chậm r&atilde;i từ miệng hồ l&ocirc; chảy ra, tại giữa trưa dưới &aacute;nh mặt trời giống 1 đầu ho&agrave;ng kim ống kh&oacute;a. L&yacute; Thừa Kiền cũng kh&ocirc;ng c&oacute; cho Dịch T&iacute;ch Phong trả lời thời cơ, hắn tiếp tục n&oacute;i: &quot;Thế gian vạn vật tương sinh tương khắc, bắt đầu tại hỗn độn, tạo th&agrave;nh &acirc;m dương, tạo tại ngũ h&agrave;nh. &Acirc;m dương tương sinh tương khắc, như vậy h&ograve;a hợp &acirc;m dương m&agrave; sống vạn vật, h&ograve;a hợp ngũ h&agrave;nh th&igrave; sống tạo h&oacute;a. Đ&uacute;ng như người ph&acirc;n nam nữ ch&iacute;nh l&agrave; &acirc;m dương, &acirc;m dương giao hợp liền c&oacute; thể tạo ra mới sinh mệnh, c&aacute;i n&agrave;y ngươi sau khi lớn l&ecirc;n tự nhi&ecirc;n sẽ hiểu được. Ta h&ocirc;m nay muốn truyền thụ Ngự K&igrave;nh ph&aacute;p ch&iacute;nh l&agrave; bắt ngu&ocirc;̀n từ đạo n&agrave;y.&quot;<br />\r\n<br />\r\n&quot;Vậy c&ugrave;ng rượu c&oacute; quan hệ g&igrave;? C&aacute;i g&igrave; l&agrave; rượu?&quot; L&acirc;m Phong Hỏa nghe được say sưa ngon l&agrave;nh, nhịn kh&ocirc;ng được cắt ngang hỏi.<br />\r\n<br />\r\n&quot;Hỏi rất hay, trong ngũ h&agrave;nh thủy hỏa bất dung, thi&ecirc;n hạ đều biết, như vậy rượu lại kh&ocirc;ng như thế. Thứ nhất, đem rượu vung v&agrave;o hỏa b&ecirc;n trong cũng kh&ocirc;ng thể tương khắc lẫn nhau ti&ecirc;u tan, ngược lại giống như lửa ch&aacute;y đổ th&ecirc;m dầu, để cho thế lửa mạnh hơn, c&oacute; biết rượu ch&iacute;nh l&agrave; nước b&ecirc;n trong hỏa. Thứ hai, nếu đem rượu tại dương hỏa ph&iacute;a dưới thi&ecirc;u đốt hoặc l&agrave; tại nồi b&ecirc;n trong đun s&ocirc;i, th&igrave; tửu kh&iacute; tr&agrave;n v&agrave;o, về sau như nước sạch, c&oacute; biết rượu cũng l&agrave; hỏa b&ecirc;n trong nước. Từ đ&oacute; c&oacute; thể biết, rượu n&agrave;y ở tại &acirc;m dương b&ecirc;n trong, kh&ocirc;ng ở trong ngũ h&agrave;nh!&quot;<br />\r\n<br />\r\nDịch T&iacute;ch Phong mắt nh&igrave;n chậm r&atilde;i hạ xuống rượu, lẩm bẩm n&oacute;i: &quot;Ở tại &acirc;m dương b&ecirc;n trong, kh&ocirc;ng ở trong ngũ h&agrave;nh . . .&quot;<br />\r\n<br />\r\n&quot;Ta truyền cho ngươi phương ph&aacute;p n&agrave;y ch&iacute;nh l&agrave; nhờ v&agrave;o rượu l&agrave; &acirc;m dương đồ vật, v&agrave;o trong th&acirc;n thể, điều tiết &acirc;m dương, đẩy huyết Ngự K&igrave;nh! V&igrave; vậy ph&aacute;p danh viết tửu kh&iacute; Ngự K&igrave;nh!&quot; L&yacute; Thừa Kiền nh&igrave;n về ph&iacute;a Dịch T&iacute;ch Phong, ph&aacute;t hiện tiểu tử n&agrave;y ch&iacute;nh đang xấu hổ nh&igrave;n m&igrave;nh, kh&ocirc;ng khỏi sinh ra t&ograve; m&ograve;, hỏi: &quot;Ngươi liền kh&ocirc;ng nhớ học?&quot;<br />\r\n<br />\r\n&quot;Tiền bối nếu dạy, tiểu tử may mắn, tiền bối kh&ocirc;ng dạy, tiểu tử chi mệnh. Như vậy tiền bối từng n&oacute;i, học c&ugrave;ng kh&ocirc;ng học để cho tiểu tử bản th&acirc;n định đoạt, tiểu tử cả gan hỏi tiền bối phương ph&aacute;p n&agrave;y c&oacute; thể c&oacute; c&aacute;i g&igrave; tai hại?&quot;<br />\r\n<br />\r\nL&yacute; Thừa Kiền hơi t&aacute;n thưởng xem Dịch T&iacute;ch Phong một c&aacute;i, về sau chậm r&atilde;i nhắm mắt lại, n&oacute;i: &quot;C&oacute;.&quot;<br />\r\n<br />\r\n&quot;Ta tại 25 năm trước ngẫu nhi&ecirc;n phải phương ph&aacute;p n&agrave;y, đ&oacute; l&agrave; ta hơn 20 tuổi, v&otilde; nghệ ch&uacute;t th&agrave;nh tựu. Th&ocirc;ng luyện phương ph&aacute;p n&agrave;y ph&aacute;t hiện trừ bỏ để cho m&igrave;nh to&agrave;n th&acirc;n thoải m&aacute;i dễ chịu b&ecirc;n ngo&agrave;i cũng kh&ocirc;ng c&oacute; n&oacute; theo như lời c&oacute; &iacute;ch. Về sau ta nắm giữ tập nghệ, v&igrave; th&ocirc;n bồi dưỡng hậu bối, đ&atilde; từng dạy bọn hắn phương ph&aacute;p n&agrave;y. Mới đầu hiệu quả r&otilde; r&agrave;ng, nhưng sau khi lớn l&ecirc;n thần tr&iacute; đều cũng sơ lược nhận ảnh hưởng. Khi đ&oacute; ta liền biết r&otilde;, rượu l&agrave; &acirc;m dương, nh&acirc;n thể cũng l&agrave; &acirc;m dương, lấy &acirc;m dương ngự &acirc;m dương, những h&agrave;i tử n&agrave;y c&ograve;n nhỏ tuổi, t&acirc;m tr&iacute; chưa định, rượu kh&oacute; tr&aacute;nh khỏi khiến cho &acirc;m dương hỗn độn, thần hồn bị hao tổn.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nL&yacute; Thừa Kiền n&oacute;i xong mở to mắt mắt nh&igrave;n Dịch T&iacute;ch Phong n&oacute;i: &quot;Ngươi c&oacute; học hay kh&ocirc;ng?&quot;<br />\r\n<br />\r\nDịch T&iacute;ch Phong chưa đ&aacute;p lời, L&acirc;m Phong Hỏa cướp lời n&oacute;i: &quot;Thần hồn bị hao tổn được kh&ocirc;ng b&ugrave; mất, kh&ocirc;ng học cũng được. Nhưng l&agrave; gi&aacute;o đầu n&oacute;i loại n&agrave;y Ngự K&igrave;nh ph&aacute;p đối với gi&aacute;o đầu bản th&acirc;n ngươi kh&ocirc;ng c&oacute; t&aacute;c dụng qu&aacute; lớn, v&igrave; sao vậy?&quot;<br />\r\n<br />\r\n&quot;Ta l&uacute;c ấy đ&atilde; chừng hai mươi, đ&atilde; sớm ngộ ra bản th&acirc;n Ngự K&igrave;nh ph&aacute;p, th&iacute;ch hợp bản th&acirc;n mới l&agrave; tốt nhất. Như vậy phương ph&aacute;p n&agrave;y trọng đang đối th&acirc;n thể bồi dưỡng, tửu kh&iacute; nu&ocirc;i dưỡng huyết, đẩy huyết Ngự K&igrave;nh. Ta l&uacute;c ấy th&acirc;n thể đ&atilde; l&agrave; trưởng th&agrave;nh, cho n&ecirc;n hiệu quả kh&ocirc;ng lớn.&quot; L&yacute; Thừa Kiền lắc đầu cười n&oacute;i.<br />\r\n<br />\r\n&quot;Ta nguyện học, mong tiền bối chỉ điểm!&quot; Dịch T&iacute;ch Phong c&uacute;i người h&agrave;nh lễ, hướng L&yacute; Thừa Kiền c&uacute;i đầu đến.<br />\r\n<br />\r\nL&yacute; Thừa Kiền hơi kinh ngạc xem Dịch T&iacute;ch Phong một c&aacute;i, hắn vốn dĩ v&igrave; thiếu ni&ecirc;n n&agrave;y c&oacute; thể đang dụ dỗ trước mặt vẫn c&oacute; thể kh&ocirc;ng mất tỉnh t&aacute;o ph&aacute;t gi&aacute;c được loại n&agrave;y vận k&igrave;nh phương ph&aacute;p tai hại, kh&ocirc;ng thuộc người đoản ch&iacute;, cần g&igrave; n&oacute;ng l&ograve;ng nhất thời? Nhưng lại lo lắng n&oacute; thiếu ni&ecirc;n t&acirc;m t&iacute;nh, cho n&ecirc;n nhắc nhở: &quot;Tửu Kh&iacute; Ngự K&iacute;nh ph&aacute;p mặc d&ugrave; kh&ocirc;ng b&agrave;n m&agrave; hợp thi&ecirc;n địa đại đạo, nhưng ngươi cũng phải biết thần hồn ch&iacute;nh l&agrave; nh&oacute;m người căn bản, ta xem ngươi &ocirc;ng cụ non, nhưng d&ugrave; sao thần hồn t&acirc;m tr&iacute; kh&ocirc;ng kịp th&agrave;nh người, vẫn cẩn thận l&agrave; hơn, d&ugrave; l&agrave; sau n&agrave;y t&igrave;m phương ph&aacute;p kh&aacute;c học được Ngự K&igrave;nh chi đạo, th&agrave;nh quả chưa chắc sẽ k&eacute;m.&quot;<br />\r\n<br />\r\nN&oacute;i đến chỗ n&agrave;y, L&yacute; Thừa Kiền dừng một ch&uacute;t, lại n&oacute;i &quot;Cần biết, th&iacute;ch hợp bản th&acirc;n mới l&agrave; tốt nhất.&quot;<br />\r\n<br />\r\nDịch T&iacute;ch Phong thầm nghĩ: Phương ph&aacute;p n&agrave;y mặc d&ugrave; tồn tại rất lớn tai hại, đối với những người kh&aacute;c c&oacute; lẽ l&agrave; g&acirc;n g&agrave;, nhưng đối với ta gi&aacute; trị lại l&agrave; kh&aacute;c biệt! Ta cũng kh&ocirc;ng thuộc ở c&aacute;i thế giới n&agrave;y,<br />\r\n<br />\r\nMặc d&ugrave; từ hai tuổi tới liền sinh hoạt ở nơi n&agrave;y, nhưng thể chất kh&ocirc;ng bằng những đứa trẻ sinh trưởng ở nơi đ&acirc;y. C&aacute;i n&agrave;y cũng b&ecirc;n cạnh chứng minh phiến thi&ecirc;n địa n&agrave;y quy tắc cường đại, sinh hoạt ở nơi n&agrave;y người, tiềm năng th&acirc;n thể muốn vượt xa kiếp trước. Tố chất th&acirc;n thể của ta ở kiếp trước tuyệt đối l&agrave; đứng đầu, ở đi qua đ&ecirc;m h&ocirc;m đ&oacute; phản l&atilde;o ho&agrave;n đồng bồi dưỡng tăng l&ecirc;n về sau, ở c&aacute;i thế giới n&agrave;y vẫn như cũ chỉ c&oacute; thể coi l&agrave; người thuộc lớp trung. M&agrave; c&aacute;i n&agrave;y c&ocirc;ng ph&aacute;p thi&ecirc;n về tại th&acirc;n thể bồi dưỡng, ch&iacute;nh hợp ta d&ugrave;ng! Về phần c&oacute; hại thần hồn, lại l&agrave; đối với tuổi nhỏ người, t&iacute;nh to&aacute;n ta đến đ&acirc;y 3 năm, c&ograve;n c&oacute; kiếp trước mười bảy năm, ta thần hồn cũng c&oacute; 20 tuổi. Chắc hẳn l&uacute;c trước s&aacute;ng tạo ra bộ n&agrave;y c&ocirc;ng ph&aacute;p người, cũng sẽ kh&ocirc;ng nghĩ tới thế gian sẽ c&oacute; giống người như ta a!<br />\r\n<br />\r\nL&yacute; Thừa Kiền gặp Dịch T&iacute;ch Phong c&uacute;i đầu kh&ocirc;ng n&oacute;i, cũng kh&ocirc;ng l&ecirc;n tiếng quấy rầy, lời n&ecirc;n n&oacute;i hắn đều n&oacute;i, con đường tương lai lựa chọn như thế n&agrave;o đều l&agrave; ri&ecirc;ng m&igrave;nh tạo h&oacute;a.<br />\r\n<br />\r\nDịch T&iacute;ch Phong &aacute;nh mắt thu v&agrave;o, trịnh trọng hướng L&yacute; Thừa Kiền thi lễ, n&oacute;i ra: &quot;Xin tiền bối ban thưởng Tửu Kh&iacute; Ngự K&iacute;nh ph&aacute;p!&quot;<br />\r\n<br />\r\nL&yacute; Thừa Kiền khẽ gật đầu, n&oacute;i ra: &quot;H&ocirc;m nay giờ Th&acirc;n (buổi chiều 4 giờ), rừng phong luyện c&ocirc;ng!&quot; L&yacute; Thừa Kiền vứt xuống c&acirc;u n&oacute;i n&agrave;y về sau liền rời đi Diễn V&otilde; trường.<br />\r\n<br />\r\nL&acirc;m Phong Hỏa nh&igrave;n một ch&uacute;t vẻ mặt hưng phấn Dịch T&iacute;ch Phong, n&oacute;i ra: &quot;Kỳ thật ta nghe gia gia n&oacute;i qua c&aacute;i n&agrave;y Nam M&ocirc;n Ngự k&igrave;nh phương ph&aacute;p, mặc d&ugrave; tu luyện phương ph&aacute;p n&agrave;y đối với th&acirc;n thể kh&iacute; huyết bổ &iacute;ch qu&aacute; lớn, thậm ch&iacute; rất nhiều &acirc;m dương cấp Ngự K&igrave;nh c&ocirc;ng ph&aacute;p đều cũng so với kh&ocirc;ng l&ecirc;n, nhưng đối Thần Hồn tổn hại lại l&agrave;m cho đại đa số người ch&ugrave;n bước, v&igrave; vậy ph&aacute;p cũng chỉ c&oacute; thể coi l&agrave; ngũ h&agrave;nh cấp Ngự K&igrave;nh c&ocirc;ng ph&aacute;p.&quot;<br />\r\n<br />\r\n&quot;&Acirc;m dương cấp? Ngũ h&agrave;nh cấp? Đ&acirc;y l&agrave; c&aacute;i g&igrave;?&quot; Dịch T&iacute;ch Phong t&ograve; m&ograve; hỏi.<br />\r\n<br />\r\n&quot;Kỳ thật ta cũng kh&ocirc;ng hiểu nhiều, chỉ l&agrave; nghe theo gia gia n&oacute;i qua. Thi&ecirc;n hạ Ngự K&igrave;nh ph&aacute;p, chia l&agrave;m hỗn độn, &acirc;m dương, ngũ h&agrave;nh, Vạn Tượng tứ loại. Hỗn độn cấp c&ocirc;ng ph&aacute;p gia gia của ta cũng chưa từng thấy qua, chỉ l&agrave; nghe n&oacute;i qua, ch&iacute;nh l&agrave; đoạt Thi&ecirc;n Địa tạo h&oacute;a, thế gian rất l&agrave; thưa thớt, mỗi lần xuất hiện đều sẽ g&acirc;y n&ecirc;n một hồi gi&oacute; tanh mưa m&aacute;u. Hỗn độn cấp c&ocirc;ng ph&aacute;p gần trăm năm b&ecirc;n trong nhiều lần c&oacute; truyền văn, nhưng phần lớn l&agrave; giả, chỉ c&oacute; chứng có th&ecirc;̉ kiểm tra c&ograve;n phải xem như l&agrave; 500 năm ph&iacute;a trước, an ổn Thi&ecirc;n T&ocirc;ng t&ocirc;ng chủ B&aacute;ch L&yacute; B&igrave;nh Thi&ecirc;n một m&igrave;nh s&aacute;ng tạo thần c&ocirc;ng &mdash; &mdash; Hỗn Nguy&ecirc;n Nhất Kh&iacute; C&ocirc;ng.&quot; N&oacute;i đến đ&acirc;y, chỉ thấy L&acirc;m Phong Hỏa hưng phấn kh&ocirc;ng biết v&igrave; sao, khu&ocirc;n mặt nhỏ đỏ bừng, trong miệng nghĩ linh tinh đạo, cũng nghe mơ hồ n&oacute;i c&aacute;i g&igrave;, tựa như ch&iacute;nh hắn sẽ phải thi triển thần c&ocirc;ng một dạng.<br />\r\n<br />\r\nDịch T&iacute;ch Phong kh&ocirc;ng khỏi nhịn kh&ocirc;ng được cười l&ecirc;n, đồng thời cũng nhớ kỹ &quot;An ổn Thi&ecirc;n T&ocirc;ng&quot; c&ugrave;ng &quot;B&aacute;ch L&yacute; B&igrave;nh Thi&ecirc;n&quot; danh tự.<br />\r\n<br />\r\nC&ugrave;ng L&acirc;m Phong Hỏa hơi tỉnh t&aacute;o lại, hắn n&oacute;i tiếp đi: &quot;B&aacute;ch L&yacute; B&igrave;nh Thi&ecirc;n tư chất cổ kim v&ocirc; song, dựa v&agrave;o tự s&aacute;ng tạo hỗn độn cấp thần c&ocirc;ng, bại tận thi&ecirc;n hạ cao thủ, chế an ổn Thi&ecirc;n T&ocirc;ng, lập b&igrave;nh Thi&ecirc;n Bảng &mdash; &mdash; b&igrave;nh luận anh h&ugrave;ng thi&ecirc;n hạ. Từng bảy năm xuất nhất bảng, bảng thiết lập trăm người, đứng đầu bảng người, ch&iacute;nh l&agrave; thi&ecirc;n hạ đệ nhất.&quot;<br />\r\n<br />\r\n&quot;Đ&acirc;y chẳng phải l&agrave; n&oacute;i B&aacute;ch L&yacute; B&igrave;nh Thi&ecirc;n bản th&acirc;n lập một bảng, bản th&acirc;n đem thật nhiều lần đứng đầu bảng?&quot; Dịch T&iacute;ch Phong kinh ngạc hỏi, . Lập tức cảm thấy B&aacute;ch L&yacute; B&igrave;nh Thi&ecirc;n cũng chỉ thường th&ocirc;i, qu&aacute; dối tr&aacute; ch&uacute;t.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nL&acirc;m Phong Hỏa kh&ocirc;ng c&oacute; trả lời, chỉ l&agrave; cười qu&aacute;i dị nh&igrave;n xem hắn, trầm mặc lắc đầu.<br />\r\n<br />\r\nDịch T&iacute;ch Phong gặp L&acirc;m Phong Hỏa kh&ocirc;ng n&oacute;i lời n&agrave;o, c&agrave;ng cảm thấy kinh ngạc, bỗng nhi&ecirc;n linh quang l&oacute;e l&ecirc;n minh bạch huyền ảo trong đ&oacute;, sợ h&atilde;i than n&oacute;i: &quot;Hảo m&ocirc;̣t cái B&aacute;ch L&yacute; B&igrave;nh Thi&ecirc;n! Hảo m&ocirc;̣t cái thi&ecirc;n hạ đệ nhất &mdash; &mdash; B&aacute;ch L&yacute; B&igrave;nh Thi&ecirc;n ph&iacute;a dưới người thứ nhất!&quot;<br />\r\n<br />\r\nL&acirc;m Phong Hỏa cười h&igrave; h&igrave; n&oacute;i tiếp: &quot;Đ&acirc;y đều l&agrave; ta nghe gia gia của ta n&oacute;i, tổng thể m&agrave; n&oacute;i, hỗn độn cấp c&ocirc;ng ph&aacute;p muốn so &acirc;m dương cấp mạnh, &acirc;m dương cấp c&ocirc;ng ph&aacute;p muốn so ngũ h&agrave;nh cấp mạnh, ngũ h&agrave;nh cấp c&ocirc;ng ph&aacute;p muốn so Vạn Tượng cấp mạnh, nhưng l&agrave; đồng thời kh&ocirc;ng phải l&agrave; tuyệt đối. Vạn Tượng cấp c&ocirc;ng ph&aacute;p rất nhiều, c&oacute; một m&ocirc;n c&ocirc;ng ph&aacute;p gọi Ngũ Cầm H&iacute;, mới đầu chỉ l&agrave; cường th&acirc;n kiện thể, đ&atilde; từng c&oacute; 1 người đem Ngũ Cầm H&iacute; luyện đến tuyệt đỉnh cảnh giới, dung nhập bản th&acirc;n hiểu được, tự s&aacute;ng tạo l&ecirc;n cấp c&ocirc;ng ph&aacute;p &mdash; &mdash; ngũ thần h&iacute; kịch. Uy lực của n&oacute; ch&iacute;nh l&agrave; đỉnh cấp hỗn độn cấp c&ocirc;ng ph&aacute;p cũng kh&ocirc;ng thua k&eacute;m bao nhi&ecirc;u!&quot;<br />\r\n<br />\r\nNếu như trước đ&oacute; Dịch T&iacute;ch Phong l&agrave; kinh h&atilde;i th&aacute;n phục B&aacute;ch L&yacute; B&igrave;nh Thi&ecirc;n tư chất ng&uacute;t trời, như vậy 1 lần n&agrave;y ch&iacute;nh l&agrave; cảm thấy khiếp sợ s&acirc;u sắc. Kh&ocirc;ng khỏi thốt ra: &quot;C&ocirc;ng ph&aacute;p c&ograve;n c&oacute; thể l&ecirc;n cấp? !&quot;<br />\r\n<br />\r\nL&acirc;m Phong Hỏa được Dịch T&iacute;ch Phong giật nảy m&igrave;nh, ngẩn người n&oacute;i ra: &quot;A, &acirc;n, đương nhi&ecirc;n c&oacute; thể! Gia gia n&oacute;i th&iacute;ch hợp bản th&acirc;n đ&uacute;ng l&agrave; tốt nhất.&quot;<br />\r\n<br />\r\nDịch T&iacute;ch Phong nghe được trả lời khẳng định, kh&ocirc;ng khỏi nắm chặt nắm đấm, mắt nh&igrave;n chung quanh đ&atilde; trống trải Diễn V&otilde; trường, mới cảm thấy trong bụng một trận đ&oacute;i kh&aacute;t. Dịch T&iacute;ch Phong c&ugrave;ng L&acirc;m Phong Hỏa nh&igrave;n một ch&uacute;t thời điểm kh&ocirc;ng c&ograve;n sớm, tất nhi&ecirc;n l&agrave; vắt ch&acirc;n l&ecirc;n cổ chạy về nh&agrave; ăn cơm.<br />\r\n<br />\r\nSau buổi cơm trưa, Dịch T&iacute;ch Phong một người tới đến th&ocirc;n ph&iacute;a nam rừng c&acirc;y phong, hắn biết m&igrave;nh đến sớm 2 canh giờ. Hắn kh&ocirc;ng phải giống c&aacute;i kh&aacute;c năm tuổi tiểu đồng như vậy trong rừng chơi đ&ugrave;a, cũng kh&ocirc;ng giống L&acirc;m Phong Hỏa như thế ăn cơm trưa xong đi ngủ. Mặc d&ugrave; năm tuổi đến 7 tuổi, &quot;Luyện nghệ&quot; nội dung ch&iacute;nh l&agrave; tự do hoạt động, nhưng Dịch T&iacute;ch Phong cũng kh&ocirc;ng định l&atilde;ng ph&iacute; trong khoảng thời gian n&agrave;y, hắn lại tại lưa thưa rừng lá phong b&ecirc;n trong bắt đầu luyện tập chạy, vọt, đứng . . .</p>', '2021-12-01 01:26:43', 9, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(281, 73, '5 Tuổi Hiểu Quanh Thân', '5', '<p>Cuối m&ugrave;a thu rừng lá phong, giống như phấp phới &aacute;nh t&agrave; hải triều, Hồng l&atilde;ng cuồn cuộn kh&ocirc;ng dứt. Một th&acirc;n ảnh nhỏ gầy ở mảnh n&agrave;y Hồng lãng b&ecirc;n trong ẩn hiện. Đầy trời theo gi&oacute; chập chờn l&aacute; đỏ giống như hồng v&acirc;n, c&ugrave;ng tr&ecirc;n mặt đất thật d&agrave;y như thảm một dạng l&aacute; phong c&ugrave;ng nhau phủ k&iacute;n to&agrave;n bộ sơn d&atilde;.<br />\r\n<br />\r\n&quot;1197, 1198, 1199, 1200!&quot; Dịch T&iacute;ch Phong kh&oacute; khăn l&agrave;m xong vọt huấn luyện hạng mục. Th&ocirc;ng qua mấy ng&agrave;y nay v&otilde; nghệ huấn luyện, hắn dần dần cảm nhận được chạy, vọt, đứng, đấu, bắn! Mỗi một hạng đều cũng nh&igrave;n như đơn giản lại đều ph&ugrave; hợp nh&acirc;n thể ph&aacute;t triển chi đạo. Chạy, r&egrave;n g&acirc;n cốt, luyện n&oacute; phế phủ; vọt, r&egrave;n n&oacute; tứ chi, tăng n&oacute; bạo ph&aacute;t; đứng, ổn n&oacute; hạ b&agrave;n, tăng n&oacute; sức chịu đựng. Về phần c&ograve;n chưa từng tiếp x&uacute;c đấu c&ugrave;ng bắn, Dịch T&iacute;ch Phong cảm thấy hẳn l&agrave; cũng c&oacute; chỗ độc đ&aacute;o của n&oacute;.<br />\r\n<br />\r\nĐang l&uacute;c Dịch T&iacute;ch Phong chậm r&atilde;i từ dưới đất b&ograve; dậy đến, chuẩn bị đứng tấn thời đi&ecirc;̉m, đột nhi&ecirc;n cảm thấy thần hậu c&oacute; lực gi&oacute; đ&aacute;nh tới, Dịch T&iacute;ch Phong trong l&ograve;ng biết m&igrave;nh l&agrave; được đ&aacute;nh l&eacute;n, mắt thấy trốn kh&ocirc;ng kịp, đ&agrave;nh phải tại cực kỳ nguy cấp thời khắc, hơi hơi nghi&ecirc;ng người, tr&aacute;nh đi yếu hại!<br />\r\n<br />\r\n&quot;Bành!&quot; Một khối đ&aacute; sỏi ch&iacute;nh giữa Dịch T&iacute;ch Phong vai phải, Dịch T&iacute;ch Phong t&ugrave;y theo xoay người ngả xuống đất, hai thước d&agrave;y l&aacute; phong tầng vừa vặn c&oacute; thể che giấu n&oacute; ấu tiểu th&acirc;n h&igrave;nh, hắn ng&atilde; tr&ecirc;n mặt đất kh&ocirc;ng nh&uacute;c nh&iacute;ch.<br />\r\n<br />\r\nỞ c&aacute;ch hắn 20 trượng c&acirc;y phong b&ecirc;n tr&ecirc;n, nhảy xuống 1 người. Người n&agrave;y mắt nh&igrave;n ngo&agrave;i hai mươi trượng nằm tr&ecirc;n mặt đất bất động Dịch T&iacute;ch Phong, kh&oacute;e miệng lộ ra nụ cười khinh miệt, lại c&uacute;i đầu nh&igrave;n một ch&uacute;t trong tay cầm đ&aacute; sỏi, hắn vốn định vừa ph&aacute;t kh&ocirc;ng tr&uacute;ng bổ khuyết th&ecirc;m một k&iacute;ch, chỉ l&agrave; đối thủ của hắn giống như ngo&agrave;i &yacute; liệu yếu. Hắn tiện tay n&eacute;m xuống đ&aacute; sỏi, thản nhi&ecirc;n hướng Dịch T&iacute;ch Phong đi đến.<br />\r\n<br />\r\nDịch T&iacute;ch Phong ngửa mặt nằm ở mềm mại l&aacute; phong tầng b&ecirc;n trong, tại c&aacute;i th&acirc;n ảnh kia xuất hiện trong nh&aacute;y mắt, hắn liền nhận ra tập k&iacute;ch hắn người, ch&iacute;nh l&agrave; h&ocirc;m nay buổi s&aacute;ng v&otilde; nghệ l&uacute;c huấn luyện, c&ugrave;ng hắn c&ugrave;ng L&acirc;m Phong Hỏa gợi l&ecirc;n xung đột L&acirc;m L&ocirc;i.<br />\r\n<br />\r\nDịch T&iacute;ch Phong nằm tr&ecirc;n mặt đất kh&ocirc;ng hề động, khi hắn nh&igrave;n thấy L&acirc;m L&ocirc;i thời điểm liền đ&atilde; đo&aacute;n được vị n&agrave;y 7 tuổi thiếu ni&ecirc;n &yacute; nghĩ trong l&ograve;ng. V&igrave; lẽ đ&oacute; hắn kh&ocirc;ng phải lập tức xoay người chạy trốn, m&agrave; l&agrave; lẳng lặng quan s&aacute;t L&acirc;m L&ocirc;i h&agrave;nh động kế tiếp. Dịch T&iacute;ch Phong nắm được L&acirc;m L&ocirc;i thực lực muốn hơn m&igrave;nh xa, d&ugrave; l&agrave; cộng th&ecirc;m L&acirc;m Phong Hỏa, ph&iacute;a b&ecirc;n m&igrave;nh hai người cũng kh&ocirc;ng nhất định có th&ecirc;̉ ở trong tay L&acirc;m L&ocirc;i chiếm được lợi. D&ugrave; sao L&acirc;m L&ocirc;i muốn so Dịch T&iacute;ch Phong c&ugrave;ng L&acirc;m Phong Hỏa th&ecirc;m ra r&ograve;ng r&atilde; 2 năm tu luyện kinh lịch, huống chi L&acirc;m L&ocirc;i tư chất cũng kh&ocirc;ng k&eacute;m.<br />\r\n<br />\r\nDịch T&iacute;ch Phong ch&iacute;nh đang trong đầu kh&ocirc;ng ngừng c&acirc;n nhắc song phương so s&aacute;nh thực lực thời đi&ecirc;̉m, hắn ph&aacute;t hiện L&acirc;m L&ocirc;i v&acirc;̣y mà đem trong tay đ&aacute; sỏi vứt bỏ, chậm r&atilde;i hướng bản th&acirc;n đi tới. Hắn hơi hơi h&iacute;p mắt lại, cảm thấy n&ecirc;n cho c&aacute;i n&agrave;y người mắt cao hơn đầu 1 ch&uacute;t gi&aacute;o huấn.<br />\r\n<br />\r\nL&acirc;m L&ocirc;i vừa đi về ph&iacute;a Dịch T&iacute;ch Phong, một b&ecirc;n &acirc;m thầm nghĩ tới: &quot;Kh&ocirc;ng c&oacute; nghĩ đến c&aacute;i n&agrave;y d&acirc;n đen như vậy k&eacute;m cỏi, trước đ&oacute; nh&igrave;n hắn đứng tấn đứng nửa canh giờ, c&ograve;n tưởng rằng hắn c&oacute; ch&uacute;t năng lực đ&acirc;y!&quot; 20 trượng vốn l&agrave; kh&ocirc;ng xa, rất nhanh L&acirc;m L&ocirc;i đi tới Dịch T&iacute;ch Phong b&ecirc;n người, ph&aacute;t hiện đối phương đ&atilde; ngất đi, lập tức trong l&ograve;ng liền tới nhục nh&atilde; đối phương một bữa dự định, nghĩ thầm chờ một l&aacute;t trước ti&ecirc;n đem c&aacute;i n&agrave;y d&acirc;n đen cột l&ecirc;n c&acirc;y rồi h&atilde;y n&oacute;i, nghĩ đến liền xoay người hướng Dịch T&iacute;ch Phong cổ &aacute;o chộp tới.<br />\r\n<br />\r\nLiền ở L&acirc;m L&ocirc;i tay khoảng c&aacute;ch Dịch T&iacute;ch Phong c&ograve;n c&oacute; m&acirc;́y t&acirc;́c thời đi&ecirc;̉m, Dịch T&iacute;ch Phong đột nhi&ecirc;n mở to mắt, nh&igrave;n hằm hằm L&acirc;m L&ocirc;i, hai tay từ dưới đất n&acirc;ng l&ecirc;n vung l&ecirc;n, h&ocirc; 1 tiếng: &quot;Xem ta tuyệt kỹ th&agrave;nh danh!&quot;<br />\r\n<br />\r\nL&acirc;m L&ocirc;i vốn cho rằng Dịch T&iacute;ch Phong ngất đi, sớm đ&atilde; kh&ocirc;ng c&ograve;n ph&ograve;ng bị, một tiếng gầm n&agrave;y quả thực dọa hắn nhảy một c&aacute;i, kh&ocirc;ng khỏi trợn mắt hốc mồm, nhưng c&ograve;n chưa chờ L&acirc;m L&ocirc;i kịp phản ứng, Dịch T&iacute;ch Phong liền đ&atilde; vung đến một chưởng, lập tức L&acirc;m L&ocirc;i chỉ cảm thấy trong mắt đất c&aacute;t đột nhập, đau đớn kh&ocirc;ng chịu nổi, L&acirc;m L&ocirc;i v&ocirc; &yacute; thức n&acirc;ng hai tay l&ecirc;n che hai mắt, h&aacute; to mồm đang muốn k&ecirc;u th&agrave;nh tiếng.<br />\r\n<br />\r\nDịch T&iacute;ch Phong n&oacute;i th&igrave; chậm m&agrave; xảy ra th&igrave; nhanh, ở ph&iacute;a dưới c&ocirc;ng b&ecirc;n tr&ecirc;n, n&acirc;ng l&ecirc;n một cước ch&iacute;nh đạp ở L&acirc;m L&ocirc;i hạ bộ. L&acirc;m L&ocirc;i sắc mặt từ vừa mới hạt c&aacute;t h&iacute;p mắt l&uacute;c đỏ bừng trong nh&aacute;y mắt biến th&agrave;nh đỏ tía, L&acirc;m L&ocirc;i miệng d&aacute;ng dấp c&agrave;ng lớn, lại kh&ocirc;ng ph&aacute;t ra được một ch&uacute;t thanh &acirc;m. Vốn đ&atilde; bưng b&iacute;t lấy cặp mắt hai tay, lập tức lại bưng b&iacute;t hướng hạ bộ, tại l&aacute; phong trong đất đ&aacute;nh l&ecirc;n lăn nhi.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nĐ&uacute;ng l&uacute;c n&agrave;y, một hơi c&oacute; vẻ lười biếng thanh &acirc;m tại sau lưng vang l&ecirc;n, &quot;T&aacute;t Sa Tử, Li&ecirc;u &Acirc;m Thối! Hừ hừ, ngươi đ&acirc;y l&agrave; c&aacute;i g&igrave; tuyệt kỹ th&agrave;nh danh?&quot; Chỉ thấy 1 người mặc &aacute;o gi&aacute;p trung ni&ecirc;n nam tử, cầm hồ l&ocirc; rượu chẳng biết l&uacute;c n&agrave;o liền đứng ở ph&iacute;a sau của hắn, vẻ mặt nghiền ngẫm m&agrave; nh&igrave;n xem hắn.<br />\r\n<br />\r\nDịch T&iacute;ch Phong hơi sững sờ, chẳng biết vị n&agrave;y tổng gi&aacute;o đầu khi n&agrave;o đến đ&acirc;y, ngại ng&ugrave;ng cười một tiếng n&oacute;i ra: &quot;Kh&ocirc;ng c&oacute; g&igrave; t&ecirc;n, ch&iacute;nh l&agrave; ph&ugrave; hợp ta V&otilde; Đạo.&quot;<br />\r\n<br />\r\n&quot;Ngươi V&otilde; Đạo? L&agrave; c&aacute;i g&igrave;?&quot; L&yacute; Thừa Kiền kinh ngạc hỏi.<br />\r\n<br />\r\n&quot;Ta V&otilde; Đạo l&agrave; được, ngươi nếu l&agrave; t&ecirc;n đi&ecirc;n, ta liền T&aacute;t Sa Tử; ngươi nếu l&agrave;m ra vẻ, ta liền vung!&quot; Dịch T&iacute;ch Phong nghi&ecirc;m trang n&oacute;i.<br />\r\n<br />\r\nL&yacute; Thừa Kiền cười khổ g&otilde; Dịch T&iacute;ch Phong một lần, răn dạy n&oacute;i: &quot;Ta mặc d&ugrave; kh&ocirc;ng minh bạch ngươi n&oacute;i c&aacute;i g&igrave;, nhưng ngươi phải biết, kh&ocirc;ng l&agrave;m hiệp giả, kh&ocirc;ng thể xem thường V&otilde; Đạo!&quot; N&oacute;i xong, L&yacute; Thừa Kiền liếc qua đ&atilde; chậm r&atilde;i đứng l&ecirc;n kh&ocirc;ng c&ograve;n lăn lộn nhi L&acirc;m L&ocirc;i, nh&agrave;n nhạt n&oacute;i: &quot;Vừa rồi c&aacute;i kia một thạch, đầu nhập đ&iacute;ch thật v&ocirc; c&ugrave;ng tốt. Nhưng khinh địch lười biếng địch lại l&agrave; v&otilde; giả tối kỵ. Từ từ mai, buổi chiều luyện nghệ đi Diễn V&otilde; trường ph&iacute;a bắc l&aacute; rụng rừng rậm săn lang a! Săn kh&ocirc;ng tới cũng đừng trở về.&quot;<br />\r\n<br />\r\nL&acirc;m L&ocirc;i sắc mặt t&aacute;i nhợt hướng L&yacute; Thừa Kiền khom người đ&aacute;p ứng, lại c&oacute; ch&uacute;t ki&ecirc;ng kỵ xem Dịch T&iacute;ch Phong một c&aacute;i. Quay người hướng rừng phong đi ra ngo&agrave;i.<br />\r\n<br />\r\nMắt nh&igrave;n L&acirc;m L&ocirc;i đi xa, Dịch T&iacute;ch Phong h&aacute; miệng hỏi, &quot;Kỳ thật tiền bối tới sớm a?&quot;<br />\r\n<br />\r\nL&yacute; Thừa Kiền kh&ocirc;ng c&oacute; trả lời, chỉ l&agrave; nh&agrave;n nhạt n&oacute;i: &quot;Ngươi so với ta tưởng tượng ưu t&uacute;.&quot;<br />\r\n<br />\r\nDịch T&iacute;ch Phong ngượng ng&ugrave;ng g&atilde;i đầu một c&aacute;i, kh&ocirc;ng c&oacute; trả lời.<br />\r\n<br />\r\n&quot;T&aacute;t Sa Tử, Li&ecirc;u &Acirc;m Thối mặc d&ugrave; c&oacute; ch&uacute;t l&ecirc;n kh&ocirc;ng lộ ra, nhưng ngươi 2 chi&ecirc;u n&agrave;y nối liền một c&aacute;ch tr&ocirc;i chảy, vả lại L&acirc;m L&ocirc;i thực lực muốn hơn xa ngươi. Có th&ecirc;̉ muốn ra loại chi&ecirc;u thức n&agrave;y ứng đối, cũng thuộc về kh&oacute; c&oacute; được. Nhưng ngươi phải r&otilde; r&agrave;ng L&acirc;m L&ocirc;i mặc d&ugrave; lần n&agrave;y ở ngươi tr&ecirc;n tay bị thất thế, đ&oacute; l&agrave; hắn khuyết thiếu kinh nghiệm thực chiến, nếu một lần nữa, ngươi rất kh&oacute; thủ thắng, kh&ocirc;ng thể tự m&atilde;n.&quot;<br />\r\n<br />\r\n&quot;Tiểu tử ghi nhớ tiền bối dạy bảo!&quot; Dịch T&iacute;ch Phong khom người đ&aacute;p.<br />\r\n<br />\r\nL&yacute; Thừa Kiền đem b&ecirc;n h&ocirc;ng hồ l&ocirc; rượu cởi xuống, đưa cho Dịch T&iacute;ch Phong, n&oacute;i ra: &quot;Ngồi xếp bằng tr&ecirc;n đất, tinh thần trở về hải, v&acirc;y quanh hai tay, quy về đan điền.&quot; Dịch T&iacute;ch Phong theo lời m&agrave; l&agrave;m.<br />\r\n<br />\r\nĐem rượu chứa một ngụm tại trong miệng, 3 hơi nuốt xuống 1 lần, một ngụm ph&acirc;n 3 lần uống v&agrave;o.&quot; Dịch T&iacute;ch Phong đem rượu đổ v&agrave;o trong miệng, v&agrave;o miệng chỉ cảm thấy ngọt m&aacute;t, cũng kh&ocirc;ng giống như kiếp trước rượu như vậy cay độc. Y&ecirc;n tĩnh &yacute; 3 hơi, Dịch T&iacute;ch Phong thử nghiệm uống v&agrave;o một ngụm, lại cảm gi&aacute;c giống như uống v&agrave;o 1 đầu hỏa tuyến, thẳng v&agrave;o trong cổ, cả người đều trở n&ecirc;n n&oacute;ng l&ecirc;n. Lại l&agrave; 3 hơi, Dịch T&iacute;ch Phong lại uống v&agrave;o một ngụm, vị tr&iacute; hiểm yếu cảm gi&aacute;c kh&ocirc;ng giống lần đầu ti&ecirc;n l&uacute;c như vậy n&oacute;ng rực, tr&ecirc;n người lại nhiều hơn một phần kh&ocirc; &yacute;. Lại qua 3 hơi, Dịch T&iacute;ch Phong cuối c&ugrave;ng rồi sẽ trong miệng rượu to&agrave;n bộ nuốt xuống, lập tức cảm thấy trong ngực giống như &ocirc;m một bếp l&ograve;, to&agrave;n th&acirc;n tr&ecirc;n dưới thoải m&aacute;i đến cực điểm.<br />\r\n<br />\r\n&quot;B&atilde;o nguy&ecirc;n quy nhất, tụ tửu kh&iacute; ở đan điền, như &ocirc;m củi đi nhanh hỏa đốt b&ecirc;n trong m&agrave; m&agrave; rắn chắc b&ecirc;n ngo&agrave;i. , thủ thần quy khiếu ,tụ thanh kh&iacute; ở thần hải, như đại ẩn tại thị cường b&ecirc;n ngo&agrave;i m&agrave; tu thần!&quot; L&yacute; Thừa Kiền b&igrave;nh ổn thanh &acirc;m tại sau lưng vang l&ecirc;n.<br />\r\n<br />\r\nDịch T&iacute;ch Phong lẳng lặng cảm thụ th&acirc;n thể biến h&oacute;a, chỉ cảm thấy v&ugrave;ng đan điền đoàn kia nhiệt kh&iacute; quay cuồng li&ecirc;n tục, đ&atilde; chậm r&atilde;i d&acirc;ng l&ecirc;n, kh&ocirc;ng l&acirc;u lắm, liền cảm thấy chếnh cho&aacute;ng d&acirc;ng l&ecirc;n, m&igrave;nh c&oacute; ch&uacute;t đầu nặng ch&acirc;n nhẹ.<br />\r\n<br />\r\nL&yacute; Thừa Kiền gặp Dịch T&iacute;ch Phong chếnh cho&aacute;ng d&acirc;ng l&ecirc;n, khu&ocirc;n mặt nhỏ đỏ bừng, qu&aacute;t lớn: &quot;B&atilde;o nguy&ecirc;n quy nhất! Tụ tập tửu kh&iacute; kh&ocirc;ng thể l&agrave;m cho tr&ecirc;n đ&oacute; tu&ocirc;n ra tr&ugrave;ng k&iacute;ch thần hải. Thủ thần quy khiếu! Tập trung tinh lực giữ vững t&acirc;m linh thanh minh.&quot;<br />\r\n<br />\r\nDịch T&iacute;ch Phong lập tức ổn định t&acirc;m thần, đem v&ugrave;ng đan điền l&ecirc;n cao lăn lộn đoàn kia nhiệt kh&iacute; dưới sự trấn an đến, lại bỏ đi tạp niệm bảo tr&igrave; lại bản th&acirc;n đầu &oacute;c thanh tỉnh.<br />\r\n<br />\r\nL&yacute; Thừa Kiền gặp Dịch T&iacute;ch Phong sắc mặt dần dần kh&ocirc;i phục b&igrave;nh thường, khẽ gật đầu n&oacute;i: &quot;Y theo phương ph&aacute;p n&agrave;y lại uống. Thẳng đến kh&ocirc;ng thể chịu đựng mới th&ocirc;i.&quot;<br />\r\n<br />\r\n~~~ l&uacute;c n&agrave;y Dịch T&iacute;ch Phong t&acirc;m v&ocirc; tạp niệm, nghe theo L&yacute; Thừa Kiền n&oacute;i như thế, kh&ocirc;ng n&oacute;i hai lời cầm trong tay hồ l&ocirc; rượu b&ecirc;n trong rượu đổ v&agrave;o trong mi&ecirc;̣ng.<br />\r\n<br />\r\nMột ngụm, 2 ngụm,. . .<br />\r\n<br />\r\nĐem Dịch T&iacute;ch Phong uống đến đệ thất ngụm thời điểm L&yacute; Thừa Kiền đ&atilde; rất l&agrave; chấn kinh, im lặng ngồi tới Dịch T&iacute;ch Phong sau lưng v&igrave; đ&oacute; hộ ph&aacute;p.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nĐem Dịch T&iacute;ch Phong uống đến thứ mười hai ngụm thời điểm L&yacute; Thừa Kiền cũng c&oacute; ch&uacute;t hối hận bản th&acirc;n lỗ m&atilde;ng, kh&ocirc;ng n&ecirc;n để cho kẻ n&agrave;y d&ugrave;ng phương ph&aacute;p n&agrave;y tu luyện, một phần vạn ra một tốt xấu, chẳng phải l&agrave; l&atilde;ng ph&iacute; một mầm mống tốt?<br />\r\n<br />\r\nĐem Dịch T&iacute;ch Phong uống đến thứ 18 ngụm thời điểm L&yacute; Thừa Kiền tại xoắn xu&yacute;t phải chăng lập tức cắt ngang Dịch T&iacute;ch Phong tiếp tục uống rượu, d&ugrave; l&agrave; l&uacute;c n&agrave;y cắt ngang hắn sẽ đối với L&yacute; Thừa Kiền tạo th&agrave;nh cực nội thương nghi&ecirc;m trọng, c&oacute; thể tốt hơn mất mạng.<br />\r\n<br />\r\nCũng may, trong hồ l&ocirc; rượu rượu đ&atilde; uống xong. Nhưng Dịch T&iacute;ch Phong cũng sẽ kh&ocirc;ng l&agrave; l&uacute;c trước bộ d&aacute;ng, chỉ thấy hắn to&agrave;n th&acirc;n đỏ bừng, sắc mặt lại l&agrave; trắng bệch, giọt giọt mồ h&ocirc;i từ hắn sinh ra kẽ hở nhỏ xuống. Hắn chỉ cảm thấy trong ngực giống như &ocirc;m lấy 1 đo&agrave;n liệt diễm, huyết dịch khắp người đều tại quay chung quanh đan điền xoay tr&ograve;n, thay thế huy&ecirc;́t dịch tại th&acirc;n thể to&agrave;n th&acirc;n lưu chuyển l&agrave; 1 cỗ n&oacute;ng rực tửu kh&iacute;, Dịch T&iacute;ch Phong thử nghiệm khống chế những rượu n&agrave;y kh&iacute; quỹ t&iacute;ch vận h&agrave;nh, tại c&oacute; ph&aacute;t hiện kh&ocirc;ng t&aacute;c dụng về sau đ&agrave;nh phải tạm thời từ bỏ. Tửu kh&iacute; du tẩu to&agrave;n th&acirc;n, vả lại c&agrave;ng ng&agrave;y c&agrave;ng kh&ocirc;ng bị khống chế, chậm r&atilde;i sắc mặt của hắn cũng trở n&ecirc;n đỏ bừng hết sức, tửu kh&iacute; kh&ocirc;ng ngừng tr&ugrave;ng k&iacute;ch tinh thần của hắn, để cho hắn cảm thấy một trận m&ecirc; muội, tr&ecirc;n mặt của hắn cũng chầm chậm lộ ra một vệt sốt ruột thần sắc.<br />\r\n<br />\r\n&quot;Mặc hắn long trời lở đất, ta từ thần kh&ocirc;ng mất!&quot; L&yacute; Thừa Kiền thanh &acirc;m ki&ecirc;n định từ Dịch T&iacute;ch Phong sau lưng truyền đến.<br />\r\n<br />\r\nNghe được L&yacute; Thừa Kiền m&agrave; n&oacute;i, Dịch T&iacute;ch Phong tập trung &yacute; ch&iacute;, kh&ocirc;ng th&egrave;m quan t&acirc;m rượu kia kh&iacute; như thế n&agrave;o t&aacute;n loạn. Kh&ocirc;ng biết qua bao l&acirc;u, dường như nửa canh giờ, cũng giống l&agrave; 2 canh giờ. Dịch T&iacute;ch Phong chậm r&atilde;i thả ra t&acirc;m thần, hắn đầu ti&ecirc;n nhận ra được l&agrave; trong cơ thể m&igrave;nh đ&atilde; kh&ocirc;ng c&oacute; nửa điểm tửu kh&iacute;, thay v&agrave;o đ&oacute; l&agrave; c&agrave;ng th&ecirc;m tinh thuần huyết mạch chi lực. Hắn từ từ mở mắt, hai mắt mở ra trong nh&aacute;y mắt, một vệt tinh quang từ đ&aacute;y mắt hiện l&ecirc;n, l&uacute;c n&agrave;y đ&ecirc;m đ&atilde; khuya, nhưng hắn vẫn c&oacute; thể thấy r&otilde; chung quanh mơ hồ cảnh vật. . Hắn c&uacute;i đầu xuống nh&igrave;n cả người tr&ecirc;n dưới mang theo huyết cấu, cảm thấy kinh ngạc.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; Huyết Thốn, l&agrave; ngươi trong kinh mạch tạp chất.&quot; Nh&igrave;n thấy Dịch T&iacute;ch Phong tỉnh lại, L&yacute; Thừa Kiền chậm r&atilde;i từ ph&iacute;a sau hắn đứng l&ecirc;n. Tai mắt thanh minh Dịch T&iacute;ch Phong r&otilde; r&agrave;ng ph&aacute;t gi&aacute;c được b&ecirc;n người L&yacute; Thừa Kiền biến h&oacute;a, lại nh&igrave;n hắn mặt mũi t&aacute;i nhợt, Dịch T&iacute;ch Phong hơi suy nghĩ một ch&uacute;t liền đo&aacute;n được L&yacute; Thừa Kiền trước đ&oacute; nhất định l&agrave; v&igrave; ch&iacute;nh m&igrave;nh hộ ph&aacute;p, đồng thời hao tổn kh&aacute; lớn. Dịch T&iacute;ch Phong rất l&agrave; l&agrave;m cảm động, khẽ khom người nắm một c&aacute;i đệ tử lễ, hướng L&yacute; Thừa Kiền b&aacute;i đi. L&yacute; Thừa Kiền hơi hơi nghi&ecirc;ng người kh&ocirc;ng c&oacute; đ&oacute;n hắn 1 b&aacute;i n&agrave;y, cười mắng: &quot;Ngươi cho ta L&yacute; Thừa Kiền l&agrave; người phương n&agrave;o? Trước đ&oacute; kh&ocirc;ng thu ngươi l&agrave;m đồ đệ, hiện tại gặp ngươi c&oacute; ch&uacute;t thi&ecirc;n ph&uacute; liền lại thu? Ha ha, l&agrave;m người h&aacute; c&oacute; thể như vậy? N&oacute;i kh&ocirc;ng thu liền kh&ocirc;ng thu!&quot;<br />\r\n<br />\r\nDịch T&iacute;ch Phong l&uacute;ng t&uacute;ng cười cười, minh bạch đ&acirc;y l&agrave; L&yacute; Thừa Kiền kh&ocirc;ng muốn để cho bản th&acirc;n cảm thấy thiếu hắn rất nhiều, thế nhưng c&agrave;ng l&agrave; như vậy Dịch T&iacute;ch Phong c&agrave;ng l&agrave; cảm k&iacute;ch, thấp giọng k&ecirc;u l&ecirc;n: &quot;Tiền bối truyền c&ocirc;ng đại &acirc;n, v&atilde;n bối kh&ocirc;ng thể b&aacute;o đ&aacute;p.&quot;<br />\r\n<br />\r\nL&yacute; Thừa Kiền gật đầu, n&oacute;i tiếp: &quot;Liền thi&ecirc;n sinh tư chất m&agrave; n&oacute;i, th&acirc;n thể của ngươi tư chất chỉ c&oacute; thể coi l&agrave; trung thượng chi tư. Bất qu&aacute; đơn thuần Tửu Kh&iacute; Ngự K&iacute;nh ph&aacute;p thi&ecirc;n ph&uacute;, ngươi đ&uacute;ng l&agrave; thi&ecirc;n ph&uacute; dị bẩm, sau n&agrave;y kh&ocirc;ng c&ograve;n ai kh&ocirc;ng d&aacute;m n&oacute;i, xưa nay chưa từng c&oacute; xem như l&agrave; c&oacute;.&quot; Dịch T&iacute;ch Phong ngại ng&ugrave;ng cười cười.<br />\r\n<br />\r\n&quot;Ta chấp gi&aacute;o trong th&ocirc;n &quot;Tập nghệ&quot; mười bảy năm, Tửu Kh&iacute; Ngự K&iacute;nh ph&aacute;p, lần thứ nhất mở kinh Th&aacute;c Mạch có th&ecirc;̉ uống v&agrave;o ngũ ngụm trở l&ecirc;n người, ngươi l&agrave; người thứ nhất. Sau đ&oacute; từng năm tăng l&ecirc;n, đến 14 tuổi thời đi&ecirc;̉m, liền c&oacute; thể uống v&agrave;o 14 ngụm rượu. Nhờ v&agrave;o c&aacute;i n&agrave;y 14 ngụm tửu kh&iacute; liền c&oacute; thể nối liền 14 kinh, li&ecirc;n tiếp hai mạch nh&acirc;m đốc c&ugrave;ng thập nhị ch&iacute;nh kinh. Cho n&ecirc;n trước đ&oacute; tu tập phương ph&aacute;p n&agrave;y nhanh nhất người, 14 tuổi liền c&oacute; thể đả th&ocirc;ng quanh th&acirc;n 14 kinh. Người tập v&otilde; biết được, c&agrave;ng sớm đả th&ocirc;ng quanh th&acirc;n 14 kinh, luyện v&otilde; cơ sở lại c&agrave;ng ki&ecirc;n cố, k&igrave;nh lực cũng c&agrave;ng th&acirc;m hậu.&quot; N&oacute;i đến đ&acirc;y, L&yacute; Thừa Kiền quay đầu liếc Dịch T&iacute;ch Phong một c&aacute;i, cười n&oacute;i: &quot;Nhưng lại chẳng biết từ đ&acirc;u chạy tới ngươi một c&aacute;i như vậy tiểu Tửu Quỷ! Một hơi uống 18 ngụm! Chậc chậc . . . Năm tuổi hiểu quanh th&acirc;n, từ xưa đến nay chưa từng c&oacute;!&quot;</p>', '2021-12-01 01:27:06', 16, 1),
(282, 74, 'Còn Trị Không được Ngươi', '1', '<p>&quot;Hai ng&agrave;y a!&quot;<br />\r\n<br />\r\nThần sắc b&ecirc;n trong, ở v&agrave;o Trường Lĩnh quận B&aacute;ch An huyện phủ nha hậu viện b&ecirc;n trong thanh tr&uacute;c xanh ngắt ướt &aacute;t, liếc nh&igrave;n lại tất cả đều l&agrave; xanh um tươi tốt, gi&oacute; sớm h&ocirc; h&ocirc; m&agrave; qua. Một thanh ni&ecirc;n th&acirc;n mang &aacute;o trắng, mười ng&oacute;n giấu ở trong tay &aacute;o, lẳng lặng đứng ở nơi đ&oacute; , mặc cho gi&oacute; m&aacute;t thổi nh&iacute;u quần &aacute;o của hắn.<br />\r\n<br />\r\nĐi v&agrave;o c&aacute;i n&agrave;y thế giới đ&atilde; hai ng&agrave;y, Thẩm Ngọc vẫn l&agrave; c&oacute; ch&uacute;t kh&ocirc;ng th&iacute;ch ứng cuộc sống b&acirc;y giờ, dạng n&agrave;y kh&ocirc;ng c&oacute; điện thoại, kh&ocirc;ng c&oacute; m&aacute;y vi t&iacute;nh thời gian, phảng phất liền chỉ c&ograve;n lại c&oacute; buồn tẻ.<br />\r\n<br />\r\nC&aacute;i n&agrave;y áo trắng thanh ni&ecirc;n họ Thẩm t&ecirc;n Ngọc, chữ Hằng Chi, Cảnh Long t&aacute;m năm tiến sĩ, sau đ&oacute; bị chọn ph&aacute;i đi v&agrave;o c&aacute;i n&agrave;y B&aacute;ch An huyện v&igrave; huyện lệnh, kỳ thật ch&acirc;n ch&iacute;nh t&iacute;nh to&aacute;n ra, từ hắn tiền nhiệm đến nay kỳ thật cũng bất qu&aacute; ngắn ngủi mấy ng&agrave;y thời gian m&agrave; th&ocirc;i.<br />\r\n<br />\r\nCh&iacute;nh l&agrave; bởi v&igrave; c&ugrave;ng người chung quanh cũng đều kh&ocirc;ng lớn quen thuộc, cũng thuận tiện hắn lừa gạt tới.<br />\r\n<br />\r\nN&oacute;i đến, c&aacute;i n&agrave;y B&aacute;ch An huyện thế nhưng l&agrave; tương đối nổi danh một c&aacute;i huyện, kh&ocirc;ng phải l&agrave; bởi v&igrave; n&oacute; đầy đủ m&agrave;u mỡ, m&agrave; l&agrave; m&agrave; l&agrave; bởi v&igrave; nơi n&agrave;y hai năm kh&ocirc;ng c&oacute; ba c&aacute;i huyện lệnh.<br />\r\n<br />\r\nC&aacute;i thứ nhất huyện lệnh bởi v&igrave; tham &ocirc; nhận hối lộ chuyện xảy ra, cho n&ecirc;n bị răng rắc. C&aacute;i thứ hai c&ograve;n chưa tới đ&acirc;u, cũng bởi v&igrave; tr&ecirc;n đường gặp sơn phỉ cướp đường m&agrave; bất hạnh bỏ m&igrave;nh. Về phần c&aacute;i thứ ba huyện lệnh cũng l&agrave; đến nhận chức kh&ocirc;ng đến một năm, cũng bởi v&igrave; kh&ocirc;ng quen kh&iacute; hậu th&ecirc;m nữa l&acirc;y nhiễm phong h&agrave;n, cuối c&ugrave;ng liền kh&ocirc;ng c&oacute; chống đỡ.<br />\r\n<br />\r\nMặc d&ugrave; những n&agrave;y đều chỉ có th&ecirc;̉ t&iacute;nh ngo&agrave;i &yacute; muốn, nhưng B&aacute;ch An huyện lại trở th&agrave;nh đại gia có th&ecirc;̉ tr&aacute;nh liền tr&aacute;nh địa phương. Thẳng đến thi hội về sau, kh&ocirc;ng quyền kh&ocirc;ng thế kh&ocirc;ng c&oacute; chỗ dựa Thẩm Ngọc, mới bị ph&aacute;i đi nơi n&agrave;y, trở th&agrave;nh một quang vinh quan phụ mẫu.<br />\r\n<br />\r\nM&agrave; vừa tới nơi n&agrave;y, chưa quen cuộc sống nơi đ&acirc;y Thẩm Ngọc ch&iacute;nh ra phố giải phong thổ, vừa vặn ngay tại tr&ecirc;n đường gặp nh&agrave; gi&agrave;u thiếu gia trắng trợn cướp đoạt d&acirc;n nữ tiết mục.<br />\r\n<br />\r\nCũng kh&ocirc;ng biết l&agrave; ai cho hắn dũng kh&iacute;, một c&aacute;i tay tr&oacute;i g&agrave; kh&ocirc;ng chặt thư sinh yếu đuối, kh&ocirc;ng c&oacute; h&ocirc; người hỗ trợ, v&acirc;̣y mà trực tiếp v&eacute;n tay &aacute;o l&ecirc;n liền l&ecirc;n trước ngăn cản. Kết quả l&agrave; t&uacute; t&agrave;i gặp gỡ binh, trực tiếp để người đụng v&agrave;o tr&ecirc;n mặt đất, tận lực bồi tiếp một trận đ&aacute;nh tơi bời.<br />\r\n<br />\r\nCuối c&ugrave;ng, nếu kh&ocirc;ng phải B&aacute;ch An huyện bổ đầu Chu Nguy&ecirc;n kịp thời chạy tới, hắn khả năng liền trở th&agrave;nh B&aacute;ch An huyện trong v&ograve;ng hai năm c&aacute;i thứ tư kh&ocirc;ng c&oacute; huyện lệnh. Bất qu&aacute; cũng ch&iacute;nh l&agrave; như thế, mới tiện nghi xuy&ecirc;n qua m&agrave; đến hắn.<br />\r\n<br />\r\nC&aacute;i n&agrave;y hai ng&agrave;y, Thẩm Ngọc trừ sắp xếp như &yacute; nguy&ecirc;n chủ k&yacute; ức b&ecirc;n ngo&agrave;i, cũng tại nghi&ecirc;n cứu trong đầu đột nhi&ecirc;n xuất hiện &quot;Hiệp kh&aacute;ch đ&aacute;nh dấu hệ thống&quot; . Cũng kh&ocirc;ng biết c&aacute;i hệ thống n&agrave;y l&agrave; theo m&igrave;nh xuy&ecirc;n qua m&agrave; đến, vẫn l&agrave; xuy&ecirc;n qua tới về sau mới tại dưới cơ duy&ecirc;n xảo hợp thức tỉnh.<br />\r\n<br />\r\nT&oacute;m lại, khi Thẩm Ngọc lần thứ nhất tại c&aacute;i n&agrave;y thế giới mở mắt thời điểm, khi nh&igrave;n đến chung quanh ho&agrave;n to&agrave;n xa lạ ho&agrave;n cảnh đồng thời, cũng nh&igrave;n thấy đang c&ugrave;ng hắn kh&oacute;a lại b&ecirc;n trong hệ thống.<br />\r\n<br />\r\nChờ hệ thống triệt để kh&oacute;a lại về sau, Thẩm Ngọc mới biết c&aacute;i hệ thống n&agrave;y t&ecirc;n l&agrave; &quot;Hiệp kh&aacute;ch đ&aacute;nh dấu hệ thống&quot;, theo c&aacute;i kia &yacute; tứ, hẳn l&agrave; chỉ cần h&agrave;nh hiệp trượng nghĩa, liền c&oacute; thể đ&aacute;nh dấu thu hoạch được c&aacute;c loại ban thưởng.<br />\r\n<br />\r\nC&aacute;i n&agrave;y hai ng&agrave;y Thẩm Ngọc cũng thử một ch&uacute;t, n&oacute;i như thế n&agrave;o đ&acirc;y. Bếp sau Trương mụ sinh bệnh, Thẩm Khang cho hai lượng bạc để n&agrave;ng nh&igrave;n đại phu, đ&aacute;nh dấu ban thưởng hai mươi lượng bạc. Gi&uacute;p ngay tại sinh bệnh Trương mụ chẻ củi, đ&aacute;nh dấu đạt được một thanh ch&ugrave;y, vẫn l&agrave; m&ocirc;̣t thanh chỉ c&oacute; thể d&ugrave;ng để g&otilde; hạch đ&agrave;o ch&ugrave;y nhỏ.<br />\r\n<br />\r\nTa tin ngươi c&aacute;i ch&ugrave;y, đ&acirc;y l&agrave; c&aacute;i g&igrave; ph&aacute; hệ thống. Thẩm Ngọc c&ograve;n tưởng rằng m&igrave;nh có th&ecirc;̉ trực tiếp k&yacute; ra Cửu &Acirc;m Cửu Dương loại h&igrave;nh v&otilde; c&ocirc;ng, trực tiếp trở th&agrave;nh một đời cao thủ đ&acirc;u, d&ugrave; sao c&aacute;i n&agrave;y thế giới thật kh&ocirc;ng đơn giản.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nTại Thẩm Ngọc trong tr&iacute; nhớ, c&aacute;i n&agrave;y thế giới thuộc về cổ đại ho&agrave;ng triều, nhưng cũng kh&ocirc;ng phải Trung Quốc cổ đại bất kỳ một c&aacute;i triều đại n&agrave;o. Đại Thịnh ho&agrave;ng triều lập quốc hơn hai trăm năm, v&otilde; phong cực thịnh.<br />\r\n<br />\r\nM&agrave; lại, khi Thẩm Ngọc c&ograve;n tại kinh th&agrave;nh đi thi thời điểm, c&ograve;n từng ngẫu nhi&ecirc;n gặp được một đội bổ kho&aacute;i đuổi bắt tặc nh&acirc;n. Ba bốn tầng tửu l&acirc;u ch&iacute; &iacute;t cũng c&oacute; c&aacute;i hơn mười mét, những người n&agrave;y lại có th&ecirc;̉ nhẹ nh&agrave;ng từ mặt đất nhảy tới m&aacute;i nh&agrave;.<br />\r\n<br />\r\nTranh đấu ở giữa c&agrave;ng l&agrave; đao kh&iacute; tung ho&agrave;nh, c&aacute;i kia đ&aacute;ng sợ lực ph&aacute; hoại, thế nhưng l&agrave; một mực khắc ấn tại Thẩm Ngọc trong đầu.<br />\r\n<br />\r\nTh&ocirc;ng qua những k&yacute; ức n&agrave;y, Thẩm Ngọc khắc s&acirc;u r&otilde; r&agrave;ng ch&iacute;nh m&igrave;nh vị tr&iacute; kh&ocirc;ng l&agrave; b&igrave;nh thường thế giới, m&agrave; l&agrave; một c&aacute;i c&oacute; v&otilde; c&ocirc;ng c&oacute; giang hồ nguy hiểm thế giới. Nhất l&agrave; l&agrave;m quan, kia thế nhưng l&agrave; cao nguy ng&agrave;nh nghề, kh&ocirc;ng chừng c&aacute;i kia một ng&agrave;y liền c&oacute; người đến đ&acirc;y h&agrave;nh hiệp trượng nghĩa.<br />\r\n<br />\r\n&quot;Đại nh&acirc;n, c&ocirc;ng tử nh&agrave; họ Th&aacute;i trước đến tự thủ!&quot;<br />\r\n<br />\r\n&quot;Biết!&quot; Kho&aacute;t tay &aacute;o, Thẩm Khang h&iacute;t s&acirc;u một hơi. C&ocirc;ng tử nh&agrave; họ Th&aacute;i ch&iacute;nh l&agrave; c&aacute;i kia b&ecirc;n đường trắng trợn cướp đoạt d&acirc;n nữ ph&uacute; gia c&ocirc;ng tử, cũng l&agrave; bắt hắn cho đ&aacute;nh c&aacute;i kia.<br />\r\n<br />\r\nNg&agrave;y đ&oacute; tại Chu bổ đầu đến về sau, c&ocirc;ng tử nh&agrave; họ Th&aacute;i biết được th&acirc;n phận của hắn, lập tức liền chạy v&ocirc; tung v&ocirc; ảnh. Về sau Thẩm Ngọc sau khi tỉnh dậy một mực tại quen thuộc ho&agrave;n cảnh c&ugrave;ng nghi&ecirc;n cứu hệ thống, k&eacute;m ch&uacute;t đem c&aacute;i n&agrave;y sự t&igrave;nh đem qu&ecirc;n đi.<br />\r\n<br />\r\nKh&ocirc;ng nghĩ tới, h&ocirc;m nay đối phương v&acirc;̣y mà lựa chọn sẽ chủ động tự th&uacute;. Kh&ocirc;ng đ&uacute;ng, nếu l&agrave; thật c&oacute; c&aacute;i n&agrave;y gi&aacute;c ngộ, c&ugrave;ng ng&agrave;y buổi chiều liền n&ecirc;n tới, c&ograve;n cần đến chờ hai ng&agrave;y?<br />\r\n<br />\r\n&quot;Thăng đường!&quot;<br />\r\n<br />\r\n&quot;Uy. . . V&otilde;!&quot;<br />\r\n<br />\r\nMặc quan tốt phục, Thẩm Ngọc lẳng lặng ngồi ngay ngắn ở tr&ecirc;n đại sảnh. Hai b&ecirc;n nha dịch cũng được xưng tụng l&agrave; uy vũ h&ugrave;ng tr&aacute;ng, trong l&uacute;c nhất thời, c&ograve;n giống như thật c&oacute; như vậy điểm &yacute; tứ.<br />\r\n<br />\r\nNương theo lấy những n&agrave;y trầm thấp uy vũ &acirc;m thanh, c&ocirc;ng tử nh&agrave; họ Th&aacute;i nhanh ch&acirc;n đi v&agrave;o c&ocirc;ng đường, &aacute;nh mắt kh&ocirc;ng e d&egrave; c&ugrave;ng Thẩm Ngọc đối mặt, tr&ecirc;n mặt phảng phất c&ograve;n mang theo một tia h&agrave;i l&ograve;ng.<br />\r\n<br />\r\nLiền c&aacute;i n&agrave;y th&aacute;i độ, đ&acirc;y l&agrave; đến tự thủ? Kh&ocirc;ng biết, c&ograve;n tưởng rằng l&agrave; tại dạo chơi ngoại th&agrave;nh đ&acirc;u!<br />\r\n<br />\r\n&quot;Lớn mật!&quot; Thấy đối phương lớn lối như thế, Thẩm Ngọc cũng kh&ocirc;ng quen lấy hắn, trực tiếp cao giọng qu&aacute;t &quot;Đường hạ người n&agrave;o, gặp bản quan v&igrave; sao kh&ocirc;ng quỳ!&quot;<br />\r\n<br />\r\n&quot;Ngươi!&quot; Giận dữ nh&igrave;n Thẩm Ngọc một ch&uacute;t, kh&ocirc;ng nghĩ tới đối phương kh&ocirc;ng cho mặt mũi như vậy, kh&ocirc;ng biết hắn l&agrave; ai a?<br />\r\n<br />\r\nBất qu&aacute; cuối c&ugrave;ng mặc d&ugrave; c&ocirc;ng tử nh&agrave; họ Th&aacute;i rất kh&ocirc;ng nguyện &yacute;, nhưng bất đắc dĩ hắn kh&ocirc;ng c&oacute; c&ocirc;ng danh mang theo. Người ta c&ugrave;ng hắn giảng quy củ, cho d&ugrave; lại thế n&agrave;o kh&ocirc;ng t&igrave;nh nguyện, cũng chỉ c&oacute; thể th&agrave;nh th&agrave;nh thật thật quỳ xuống tới.<br />\r\n<br />\r\n&quot;Ba!&quot; Vỗ kinh đường mộc, Thẩm Ngọc cư cao l&acirc;m hạ nh&igrave;n xem c&ocirc;ng tử nh&agrave; họ Th&aacute;i lớn tiếng n&oacute;i ra: &quot;Lớn mật Th&aacute;i H&ograve;a, hai ng&agrave;y trước đ&oacute; ngươi tại ban ng&agrave;y ban mặt ph&iacute;a dưới trắng trợn cướp đoạt d&acirc;n nữ, ngươi c&oacute; biết tội của ngươi kh&ocirc;ng?&quot;<br />\r\n<br />\r\n&quot;Oan uổng a, đại nh&acirc;n!&quot; Lại nghe được Thẩm Ngọc về sau, Th&aacute;i H&ograve;a phảng phất diễn luyện rất nhiều lần đồng dạng, lập tức liền cao giọng h&ocirc; &quot;Đại nh&acirc;n, người kia vốn l&agrave; nh&agrave; ta mua được tỳ nữ, c&oacute; văn tự b&aacute;n m&igrave;nh l&agrave;m chứng, sao l&agrave; trắng trợn cướp đoạt m&agrave; n&oacute;i?&quot;<br />\r\n<br />\r\nĐang khi n&oacute;i chuyện, Th&aacute;i H&ograve;a liền đem văn tự b&aacute;n m&igrave;nh đưa l&ecirc;n. M&agrave; tiếp nhận đối phương trong tay văn tự b&aacute;n m&igrave;nh, Thẩm Ngọc hơi nh&iacute;u cau m&agrave;y, ngược lại lại nh&igrave;n một ch&uacute;t huyện nha mọi người.<br />\r\n<br />\r\nC&aacute;i n&agrave;y văn tự b&aacute;n m&igrave;nh v&acirc;̣y mà l&agrave; thật, c&aacute;i n&agrave;y hai ng&agrave;y người Th&aacute;i gia thật đ&uacute;ng l&agrave; kh&ocirc;ng c&oacute; nh&agrave;n rỗi! Trọng yếu nhất ch&iacute;nh l&agrave;, văn tự b&aacute;n m&igrave;nh được th&ocirc;ng qua nha m&ocirc;n xử l&yacute; thủ tục mới được, n&oacute;i như vậy, nha người trong cửa cũng kh&ocirc;ng đ&aacute;ng tin a!<br />\r\n<br />\r\nM&agrave; l&uacute;c n&agrave;y, ng&agrave;y đ&oacute; bị Th&aacute;i H&ograve;a trắng trợn cướp đoạt nữ tử cũng chầm chậm đi v&agrave;o đại đường, ph&ugrave; ph&ugrave; một ch&uacute;t quỳ xuống tới. Thiếu nữ tướng mạo xinh đẹp, da thịt trong suốt như ngọc, c&agrave;ng th&ecirc;m d&aacute;ng người thon thả, yếu đuối thon d&agrave;i.<br />\r\n<br />\r\nChỉ bất qu&aacute;, l&uacute;c n&agrave;y thiếu nữ kia r&otilde; r&agrave;ng sưng đỏ trong &aacute;nh mắt lộ ra một v&ograve;ng ai o&aacute;n, c&agrave;ng lộ ra c&oacute; ch&uacute;t điềm đạm đ&aacute;ng y&ecirc;u.<br />\r\n<br />\r\nKh&oacute; tr&aacute;ch sẽ chọc cho được c&ocirc;ng tử nh&agrave; họ Th&aacute;i b&ecirc;n đường trắng trợn cướp đoạt, quả nhi&ecirc;n c&oacute; vốn liếng. Kh&ocirc;ng đ&uacute;ng, đ&acirc;y l&agrave; người bị hại, phi! Th&aacute;i H&ograve;a t&ecirc;n cặn b&atilde; n&agrave;y, dạng n&agrave;y yếu đuối thiếu nữ cũng hạ thủ được!<br />\r\n<br />\r\n&quot;Đại nh&acirc;n cho bẩm, n&ocirc; gia đ&iacute;ch thật l&agrave; Th&aacute;i gia tỳ nữ, ng&agrave;y đ&oacute; l&agrave; n&ocirc; gia tư đ&agrave;o, bị c&ocirc;ng tử bắt về cũng l&agrave; chuyện đương nhi&ecirc;n, n&ocirc; gia nhận tội!&quot;<br />\r\n<br />\r\n&quot;C&ocirc; nương, l&agrave; c&oacute; người hay kh&ocirc;ng uy hiếp ngươi rồi? Ngươi y&ecirc;n t&acirc;m, bản quan nhất định sẽ v&igrave; ngươi l&agrave;m chủ!&quot; T&igrave;nh huống n&agrave;y, đồ đần cũng nh&igrave;n ra được thiếu nữ l&agrave; bị người bức hiếp, để Thẩm Ngọc l&agrave;m sao c&oacute; thể từ bỏ &yacute; đồ.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng, n&ocirc; gia đ&iacute;ch thật l&agrave; Th&aacute;i gia n&ocirc; tỳ, mời đại nh&acirc;n minh x&eacute;t!&quot;<br />\r\n<br />\r\n&quot;Đại nh&acirc;n, văn tự b&aacute;n m&igrave;nh đều tại đại nh&acirc;n tr&ecirc;n tay, m&agrave; lại ch&iacute;nh n&agrave;ng đều thừa nhận, nh&acirc;n chứng vật chứng đều tại, chuyện ng&agrave;y đ&oacute; cũng chỉ l&agrave; c&aacute;i hiểu lầm m&agrave; th&ocirc;i!&quot;<br />\r\n<br />\r\nHững hờ x&ocirc;ng Thẩm Ngọc chắp tay, sau đ&oacute; Th&aacute;i H&ograve;a liền chuẩn bị đứng l&ecirc;n rời đi &quot;Huyện lệnh đại nh&acirc;n, đ&atilde; sự t&igrave;nh đều đ&atilde; điều tra r&otilde;, kia thảo d&acirc;n trước hết rời đi, ha ha!&quot;<br />\r\n<br />\r\n&quot;Chậm!&quot; Thấy Th&aacute;i H&ograve;a muốn rời đi, Thẩm Ngọc hừ lạnh một tiếng, trực tiếp vỗ kinh đường mộc &quot;Th&aacute;i H&ograve;a, cho d&ugrave; n&agrave;ng n&agrave;y l&agrave; ngươi Th&aacute;i gia tỳ nữ, c&aacute;i kia cũng chỉ l&agrave; ngươi trắng trợn cướp đoạt d&acirc;n nữ tội danh kh&ocirc;ng th&agrave;nh lập!&quot;<br />\r\n<br />\r\n&quot;Th&aacute;i H&ograve;a, ngươi nhưng biết, ng&agrave;y đ&oacute; ngươi ẩu đả bản quan nhưng cũng l&agrave; sự thật. Ban ng&agrave;y ban mặt ph&iacute;a dưới, b&ecirc;n đường ẩu đả mệnh quan triều đ&igrave;nh. Th&aacute;i H&ograve;a, ngươi n&oacute;i, ngươi phải bị tội g&igrave;!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Người tới, cho ta cầm xuống, nặng đ&aacute;nh ba mươi đại bản, sau đ&oacute; bắt giam v&agrave;o t&ugrave; hậu thẩm!&quot;<br />\r\n<br />\r\n&quot;Đại nh&acirc;n nghĩ lại a!&quot; C&aacute;i n&agrave;y thời điểm, b&ecirc;n cạnh sư gia vội v&agrave;ng x&ocirc;ng tới, nhỏ giọng x&ocirc;ng Thẩm Ngọc n&oacute;i &quot;Đại nh&acirc;n, c&aacute;i n&agrave;y thế nhưng l&agrave; Th&aacute;i gia c&ocirc;ng tử, huyện thừa đại nh&acirc;n ch&aacute;u ruột!&quot;<br />\r\n<br />\r\n&quot;Kia lại như thế n&agrave;o?&quot; Hung hăng trợn mắt nh&igrave;n sư gia một ch&uacute;t, Thẩm Ngọc tiếp lấy x&ocirc;ng nha dịch la lớn: &quot;C&ograve;n đứng ng&acirc;y đ&oacute; l&agrave;m g&igrave;, đ&aacute;nh cho ta, hung hăng đ&aacute;nh!&quot;<br />\r\n<br />\r\nHuyện thừa chất tử? Ta liền n&oacute;i a, t&ecirc;n vương b&aacute;t đản n&agrave;y l&agrave;m sao đem văn tự b&aacute;n m&igrave;nh giải quyết cho! Kh&ocirc;ng biết quan hệ n&agrave;y c&ograve;n tốt, đ&atilde; biết, vậy liền nhất định đ&aacute;nh nặng một ch&uacute;t, để hắn ghi nhớ thật l&acirc;u, thật sự coi ch&iacute;nh m&igrave;nh c&aacute;i n&agrave;y huyện lệnh l&agrave; c&aacute;i b&agrave;i tr&iacute; a.<br />\r\n<br />\r\n&quot;Ngươi d&aacute;m đ&aacute;nh ta?&quot; Giống như c&oacute; ch&uacute;t kh&oacute; tin nh&igrave;n về ph&iacute;a Thẩm Ngọc, d&agrave;i đến như thế lớn, m&igrave;nh tại B&aacute;ch An huyện một mực l&agrave; đi ngang, ai d&aacute;m chọc hắn? Cho d&ugrave; l&agrave; hắn cha ruột, cũng chưa từng đ&aacute;nh qua hắn nửa lần.<br />\r\n<br />\r\n&quot;Lăn đi!&quot; Thấy nha dịch tựa hồ thật d&aacute;m động thủ, Th&aacute;i H&ograve;a giận t&iacute;m mặt &quot;Cẩu n&ocirc; t&agrave;i, ta nh&igrave;n c&aacute;c ngươi ai d&aacute;m động đến tay!&quot;<br />\r\n<br />\r\n&quot;L&agrave;m c&agrave;n!&quot; Gặp qua ph&aacute;ch lối, chưa từng thấy qua ph&aacute;ch lối như vậy, Thẩm Ngọc tiểu t&iacute;nh t&igrave;nh cũng nổi l&ecirc;n, trực tiếp vỗ kinh đường mộc la lớn &quot;Gào thét c&ocirc;ng đường, tội th&ecirc;m một bậc, người tới, cho ta nặng đ&aacute;nh s&aacute;u mươi đại bản, hướng nặng b&ecirc;n trong đ&aacute;nh!&quot;<br />\r\n<br />\r\n&quot;Ngao, &ocirc;i. . . Thật đ&aacute;nh a, c&aacute;c ngươi điểm nhẹ, điểm nhẹ! Đại nh&acirc;n, đại nh&acirc;n ta biết sai, điểm nhẹ. . . .&quot;<br />\r\n<br />\r\n&quot;Hiện tại mới biết sai, chậm, đ&aacute;nh tiếp, một ch&uacute;t đều kh&ocirc;ng thể thiếu!&quot; Nghe ph&iacute;a dưới tiếng k&ecirc;u thảm thiết th&ecirc; lương, Thẩm Ngọc kh&ocirc;ng khỏi nhếch miệng.<br />\r\n<br />\r\n&quot;Ti&ecirc;̉u tử, c&ograve;n trị kh&ocirc;ng được ngươi!&quot;<br />\r\n<br />\r\nNguy&ecirc;n bản hắn c&ograve;n lo lắng nha dịch nhường, kết quả ai bảo Th&aacute;i H&ograve;a con h&agrave;ng n&agrave;y m&igrave;nh miệng tiện, trực tiếp đối bọn nha dịch mắng đ&acirc;u, kh&ocirc;ng đ&aacute;nh nặng một ch&uacute;t đều c&oacute; lỗi với bọn họ m&igrave;nh, đ&aacute;ng đời!<br />\r\n<br />\r\n&quot;Hệ thống, đ&aacute;nh dấu!&quot;<br />\r\n<br />\r\n&quot;Đ&aacute;nh dấu th&agrave;nh c&ocirc;ng, thu hoạch được To&agrave;n Ch&acirc;n t&acirc;m ph&aacute;p!&quot;<br />\r\n<br />\r\n&quot;To&agrave;n Ch&acirc;n t&acirc;m ph&aacute;p? Bạo b&iacute; tịch? C&aacute;c loại!&quot; Giờ khắc n&agrave;y, Thẩm Ngọc tựa hồ giống như m&ograve; tới hệ thống một ch&uacute;t x&iacute;u quy luật, chẳng lẽ hệ thống ch&iacute;nh x&aacute;c mở ra phương thức l&agrave; c&aacute;i n&agrave;y kiểu d&aacute;ng?</p>', '2021-12-01 01:30:17', 5, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(283, 74, 'Thành Công đánh Dấu', '2', '<p>&quot;To&agrave;n Ch&acirc;n t&acirc;m ph&aacute;p!&quot;<br />\r\n<br />\r\nTheo đ&aacute;nh dấu th&agrave;nh c&ocirc;ng thanh &acirc;m nhắc nhở vang l&ecirc;n, v&ocirc; số tin tức chui v&agrave;o Thẩm Ngọc n&atilde;o hải b&ecirc;n trong. Phảng phất trong chốc l&aacute;t, đ&atilde; đem bản n&agrave;y v&otilde; học ho&agrave;n to&agrave;n hiểu r&otilde;, tất cả tin tức to&agrave;n bộ ho&agrave; hợp qu&aacute;n th&ocirc;ng, ch&ecirc;nh lệch cũng chỉ l&agrave; năm th&aacute;ng t&iacute;ch lũy m&agrave; th&ocirc;i.<br />\r\n<br />\r\nC&ugrave;ng l&uacute;c đ&oacute;, Thẩm Ngọc cảm gi&aacute;c trong cơ thể m&igrave;nh kh&iacute; huyết s&ocirc;i tr&agrave;o, to&agrave;n th&acirc;n huy&ecirc;́t dịch tựa như thi&ecirc;u đốt. Đột nhi&ecirc;n, một tia yếu ớt kh&iacute; cảm một ch&uacute;t x&iacute;u ngưng tụ, tại kinh mạch b&ecirc;n trong chậm r&atilde;i vận chuyển, cuối c&ugrave;ng quy về đan điền b&ecirc;n trong.<br />\r\n<br />\r\nMột loại kh&oacute; n&oacute;i l&ecirc;n lời kho&aacute;i cảm từ đan điền hướng to&agrave;n th&acirc;n lan tr&agrave;n, kia một loại phảng phất đang băng l&atilde;nh trong đ&ecirc;m đột nhi&ecirc;n tới gần ấm &aacute;p hỏa l&ocirc; cảm gi&aacute;c. Vui vẻ cảm gi&aacute;c nh&aacute;y mắt chảy xu&ocirc;i to&agrave;n th&acirc;n, lại chậm r&atilde;i biến mất.<br />\r\n<br />\r\nHiển nhi&ecirc;n tại c&aacute;i n&agrave;y ngắn ngủi một l&aacute;t thời gian, To&agrave;n Ch&acirc;n t&acirc;m ph&aacute;p đ&atilde; đến nhập m&ocirc;n tr&igrave;nh độ.<br />\r\n<br />\r\nNhững người kh&aacute;c đ&ocirc;ng luyện ba ch&iacute;n hạ luyện tam phục, &iacute;t nhất ba năm Tr&uacute;c Cơ r&egrave;n luyện th&acirc;n thể, sau đ&oacute; mới c&oacute; thể tiến h&agrave;nh theo chất lượng tu luyện nội c&ocirc;ng. Tư chất k&eacute;m một điểm, thậm ch&iacute; khả năng bị ngưỡng cửa n&agrave;y ngăn ở b&ecirc;n ngo&agrave;i.<br />\r\n<br />\r\nM&agrave; hắn chỉ l&agrave; đ&aacute;nh dấu liền thu được một m&ocirc;n thượng đẳng nội c&ocirc;ng t&acirc;m ph&aacute;p, c&agrave;ng l&agrave; một bước trực tiếp nhập m&ocirc;n, hệ thống n&agrave;y c&ocirc;ng hiệu thật đ&uacute;ng l&agrave; cường đại!<br />\r\n<br />\r\n&quot;Dừng tay, dừng tay cho ta!&quot;<br />\r\n<br />\r\nNgay tại Th&aacute;i H&ograve;a bị đ&aacute;nh kh&ocirc;ng bao d&agrave;i thời gian, đ&aacute;m người b&ecirc;n trong đột nhi&ecirc;n truyền đến một trận tiếng gầm gừ, l&agrave; B&aacute;ch An huyện huyện thừa Th&aacute;i Trọng. Cũng ch&iacute;nh bởi v&igrave; vậy, động thủ nha dịch dọa đến khẽ run rẩy, lập tức đ&igrave;nh chỉ động t&aacute;c.<br />\r\n<br />\r\nHuyện thừa đại nh&acirc;n a, ai d&aacute;m g&acirc;y, kh&ocirc;ng sợ ăn dưa rơi a!<br />\r\n<br />\r\nNh&igrave;n thấy c&aacute;i n&agrave;y một m&agrave;n, Thẩm Ngọc hơi nh&iacute;u cau m&agrave;y, hững hờ n&oacute;i &quot;Th&aacute;i huyện thừa, nơi n&agrave;y l&agrave; c&ocirc;ng đường ph&iacute;a tr&ecirc;n, bản quan đ&atilde; tuy&ecirc;n &aacute;n, ngươi l&agrave; đang chất vấn bản quan a?&quot;<br />\r\n<br />\r\n&quot;Hạ quan kh&ocirc;ng d&aacute;m!&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng d&aacute;m? Ta l&agrave;m sao nh&igrave;n Th&aacute;i huyện thừa quan uy lớn như vậy chứ!&quot; Hừ lạnh một tiếng, Thẩm Ngọc lập tức lạnh l&ugrave;ng n&oacute;i &quot;Đầu ti&ecirc;n l&agrave; b&ecirc;n đường ẩu đả mệnh quan triều đ&igrave;nh, sau đ&oacute; c&ograve;n gào thét c&ocirc;ng đường, huyện thừa đại nh&acirc;n c&oacute; biết ch&aacute;u ngươi đ&atilde; phạm tội g&igrave;?&quot;<br />\r\n<br />\r\n&quot;Huyện lệnh đại nh&acirc;n bớt giận, c&ugrave;ng m&agrave; hắn vẫn chỉ l&agrave; đứa b&eacute;, kh&ocirc;ng hiểu chuyện, c&ograve;n xin huyện lệnh đại nh&acirc;n giơ cao đ&aacute;nh khẽ! !&quot;<br />\r\n<br />\r\n&quot;H&agrave;i tử? Lớn như vậy vẫn c&ograve;n con n&iacute;t? Ta nhổ v&agrave;o, c&aacute;c ngươi c&ograve;n biết xấu hổ hay kh&ocirc;ng!&quot; Mặc d&ugrave; ngo&agrave;i miệng kh&ocirc;ng n&oacute;i chuyện, nhưng Thẩm Ngọc trong nội t&acirc;m đ&atilde; sớm mắng l&ecirc;n. Đối với dạng n&agrave;y người, Thẩm Ngọc thế nhưng l&agrave; xưa nay kh&ocirc;ng nu&ocirc;ng chiều.<br />\r\n<br />\r\nĐ&acirc;y ch&iacute;nh l&agrave; kh&ocirc;ng c&oacute; trải qua x&atilde; hội đ&aacute;nh đập, nhiều kinh lịch mấy lần liền tốt. Ta đ&acirc;y l&agrave; gi&uacute;p hắn trưởng th&agrave;nh, kh&ocirc;ng cần kh&aacute;ch kh&iacute;!</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Đ&aacute;nh tiếp, đừng ngừng!&quot;<br />\r\n<br />\r\n&quot;Chờ một ch&uacute;t!&quot; B&agrave;n tay giấu tại trong tay &aacute;o nắm th&agrave;nh quyền đầu, nhưng tr&ecirc;n mặt vẫn như cũ l&agrave; vẻ mặt tươi cười bộ d&aacute;ng, Th&aacute;i Trọng h&iacute;t s&acirc;u một hơi sau đ&oacute; x&ocirc;ng Thẩm Ngọc chắp tay n&oacute;i &quot;Huyện lệnh đại nh&acirc;n, vậy ng&agrave;i cảm thấy hạ quan hẳn l&agrave; như thế n&agrave;o l&agrave;m, đại nh&acirc;n mới c&oacute; thể giơ cao đ&aacute;nh khẽ?&quot;<br />\r\n<br />\r\n&quot;Huyện thừa đại nh&acirc;n l&agrave; hiểu lầm, bản quan đ&acirc;y l&agrave; theo lẽ c&ocirc;ng bằng ph&aacute; &aacute;n, đ&uacute;ng, huyện thừa đại nh&acirc;n c&oacute; thể cho bản quan giải th&iacute;ch một ch&uacute;t c&aacute;i n&agrave;y văn tự b&aacute;n m&igrave;nh l&agrave; chuyện g&igrave; xảy ra a?&quot;<br />\r\n<br />\r\n&quot;Huyện lệnh đại nh&acirc;n hạ quan kh&ocirc;ng hiểu, c&aacute;i n&agrave;y văn tự b&aacute;n m&igrave;nh hợp l&yacute; hợp ph&aacute;p!&quot; X&ocirc;ng Thẩm Ngọc chắp tay, huyện thừa th&aacute;i độ r&otilde; r&agrave;ng c&oacute; ch&uacute;t biến h&oacute;a &quot;N&agrave;ng n&agrave;y ch&iacute;nh l&agrave; ta Th&aacute;i gia người, cho d&ugrave; l&agrave; giết cũng kh&ocirc;ng c&oacute; người d&aacute;m n&oacute;i c&aacute;i g&igrave;!&quot;<br />\r\n<br />\r\n&quot;Tốt, tốt một c&aacute;i Th&aacute;i gia!&quot; Hừ lạnh một tiếng, Thẩm Ngọc lạnh l&ugrave;ng nh&igrave;n đối phương một ch&uacute;t, m&agrave; đối phương v&acirc;̣y mà kh&ocirc;ng ch&uacute;t kh&aacute;ch kh&iacute; c&ugrave;ng hắn đối mặt. Một l&uacute;c sau, Thẩm Ngọc mới h&iacute;t s&acirc;u một hơi, cưỡng &eacute;p đ&egrave; xuống lửa giận trong l&ograve;ng.<br />\r\n<br />\r\n&quot;Th&aacute;i huyện thừa, Th&aacute;i H&ograve;a sự t&igrave;nh tự nhi&ecirc;n l&agrave; c&oacute; thể thương lượng, chỉ l&agrave; c&aacute;i n&agrave;y văn tự b&aacute;n m&igrave;nh. . . .&quot;<br />\r\n<br />\r\n&quot;Huyện lệnh đại nh&acirc;n kh&aacute;ch kh&iacute;, đ&atilde; như vậy c&aacute;i n&agrave;y tỳ nữ liền đưa cho đại nh&acirc;n ng&agrave;i, liền xem như ta Th&aacute;i gia đưa cho huyện lệnh đại nh&acirc;n tiền nhiệm lễ vật!&quot;<br />\r\n<br />\r\n&quot;Ai, sao c&oacute; thể đưa đ&acirc;u, bản quan cũng kh&ocirc;ng phải loại kia thu hối lộ người. Như vậy đi, nh&igrave;n c&aacute;i n&agrave;y văn tự b&aacute;n m&igrave;nh b&ecirc;n tr&ecirc;n viết một lượng bạc gi&aacute; cả, vậy bản quan liền ra một lượng bạc, gi&aacute; gốc mua c&oacute; thể?&quot;<br />\r\n<br />\r\nThẩm Ngọc rất r&otilde; r&agrave;ng đ&aacute;m người n&agrave;y nước tiểu t&iacute;nh, nếu l&agrave; hiện tại kh&ocirc;ng giao đại r&otilde; r&agrave;ng, ng&agrave;y sau n&oacute;i kh&ocirc;ng chừng cũng l&agrave;m người ta khi tay cầm d&ugrave;ng. Mặc d&ugrave; hắn mới vừa vặn tiền nhiệm kh&ocirc;ng c&oacute; mấy ng&agrave;y, nhưng đối B&aacute;ch An huyện t&igrave;nh huống cũng đại thể sờ soạng c&aacute;i kh&ocirc;ng sai biệt lắm.<br />\r\n<br />\r\nTo&agrave;n bộ B&aacute;ch An huyện, tr&ecirc;n cơ bản bị ba nh&agrave; một bang khống chế. Huyện thừa Th&aacute;i Trọng chỗ Th&aacute;i gia, huyện &uacute;y Ph&ugrave;ng Trung Viễn chỗ Ph&ugrave;ng gia, B&aacute;ch An huyện nh&agrave; gi&agrave;u nhất T&ocirc;n gia. C&ugrave;ng xưng b&aacute; to&agrave;n bộ B&aacute;ch An huyện hắc đạo Phi Hổ đường.<br />\r\n<br />\r\n&quot;Một lượng bạc? Gian thương!&quot; Cho d&ugrave; trong l&ograve;ng lại thế n&agrave;o hận đến nghiến răng nghiến lợi, nhưng tr&ecirc;n mặt Th&aacute;i Trọng vẫn như cũ l&agrave; một bộ mang ơn bộ d&aacute;ng &quot;Đa tạ đại nh&acirc;n!&quot;<br />\r\n<br />\r\n&quot;Đại nh&acirc;n!&quot; M&agrave; liền tại c&aacute;i n&agrave;y thời điểm nguy&ecirc;n bản quỳ tr&ecirc;n mặt đất thiếu nữ đột nhi&ecirc;n mở miệng, c&oacute; ch&uacute;t nơm nớp lo sợ nhỏ giọng n&oacute;i &quot;D&acirc;n nữ, d&acirc;n nữ phụ th&acirc;n c&ograve;n tại Th&aacute;i phủ!&quot;<br />\r\n<br />\r\n&quot;Ồ? Th&igrave; ra l&agrave; thế a!&quot; Lặng lẽ qu&eacute;t về ph&iacute;a Th&aacute;i H&ograve;a, Thẩm Ngọc kh&oacute;e miệng lướt l&ecirc;n một tia tr&agrave;o ph&uacute;ng nụ cười &quot;Th&aacute;i phủ thật sự l&agrave; bản lĩnh thật lớn!&quot;<br />\r\n<br />\r\n&quot;Huyện lệnh đại nh&acirc;n n&oacute;i đ&ugrave;a, c&ograve;n kh&ocirc;ng mau cho ta đi đem người mang tới!&quot;<br />\r\n<br />\r\nM&agrave; liền tại Th&aacute;i Trọng để hạ nh&acirc;n đi đem người mang tới kh&ocirc;ng l&acirc;u, liền c&oacute; hạ nh&acirc;n vội v&agrave;ng m&agrave; đến &quot;L&atilde;o gia, kh&ocirc;ng xong, l&atilde;o gia! Vừa vặn đại l&atilde;o gia b&ecirc;n kia truyền tin tới, l&atilde;o đầu kia kh&ocirc;ng chịu chịu thua, v&acirc;̣y mà, v&acirc;̣y mà tự s&aacute;t!&quot;<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\n&quot;Chết rồi?&quot; Nghe vậy, b&ecirc;n cạnh Thẩm Ngọc mặt mũi tr&agrave;n đầy sương lạnh &quot;Tốt, Th&aacute;i gia gia phong h&ocirc;m nay bản quan l&agrave; thấy được, người tới, cho ta đ&aacute;nh tiếp, đ&aacute;nh cho đến chết!&quot;<br />\r\n<br />\r\n&quot;Huyện lệnh đại nh&acirc;n, ngươi vừa vặn thế nhưng l&agrave; đ&aacute;p ứng. . . .&quot;<br />\r\n<br />\r\n&quot;Bản quan đ&aacute;p ứng ngươi c&aacute;i g&igrave; rồi? Bản quan vừa vặn r&otilde; r&agrave;ng cũng kh&ocirc;ng n&oacute;i g&igrave;! Ch&uacute;ng ta l&agrave; một tay giao tiền, một tay giao văn tự b&aacute;n m&igrave;nh, c&ocirc;ng bằng giao dịch gi&agrave; trẻ kh&ocirc;ng gạt. Giao dịch đ&atilde; đạt th&agrave;nh, huyện thừa đại nh&acirc;n &acirc;n hận cũng đ&atilde; chậm!&quot;<br />\r\n<br />\r\nN&oacute;i xong, Thẩm Ngọc cũng mặc kệ Th&aacute;i Trọng sắc mặt cỡ n&agrave;o kh&oacute; coi, tiếp lấy x&ocirc;ng những c&aacute;i kia bọn nha dịch la lớn &quot;C&ograve;n đứng ng&acirc;y đ&oacute; l&agrave;m g&igrave;, đ&aacute;nh, cho ta hung hăng đ&aacute;nh!&quot;<br />\r\n<br />\r\n&quot;Huyện lệnh đại nh&acirc;n đ&acirc;y l&agrave; kh&ocirc;ng cho hạ quan mặt mũi?&quot;<br />\r\n<br />\r\n&quot;Sai, huyện thừa đại nh&acirc;n, bản quan kh&ocirc;ng phải nhằm v&agrave;o ngươi, m&agrave; l&agrave; quốc ph&aacute;p v&ocirc; t&igrave;nh dung kh&ocirc;ng được nửa điểm d&agrave;n xếp! Bản quan liền muốn l&agrave;m cho tất cả mọi người biết, bản quan đ&atilde; tới B&aacute;ch An huyện, vậy liền lại kh&ocirc;ng c&oacute; người c&oacute; thể t&ugrave;y tiện ức hiếp b&aacute;ch t&iacute;nh. Quốc ph&aacute;p ph&iacute;a dưới, kh&ocirc;ng c&oacute; người c&oacute; thể may mắn tho&aacute;t khỏi!&quot;<br />\r\n<br />\r\n&quot;Tốt, huyện lệnh đại nh&acirc;n thật đ&uacute;ng l&agrave; c&aacute;i v&igrave; d&acirc;n l&agrave;m chủ quan tốt, vậy hạ quan c&aacute;o lui, đại nh&acirc;n tự giải quyết cho tốt!&quot; Lạnh l&ugrave;ng nh&igrave;n Thẩm Ngọc một ch&uacute;t, Th&aacute;i Trọng trực tiếp phẩy tay &aacute;o bỏ đi, tr&ecirc;n th&acirc;n băng l&atilde;nh kh&iacute; tức c&agrave;ng l&agrave;m cho người kh&ocirc;ng khỏi muốn nhượng bộ lui binh.<br />\r\n<br />\r\n&quot;Đại nh&acirc;n, h&ocirc;m nay ng&agrave;i x&uacute;c động!&quot; Tại Th&aacute;i Trọng sau khi đi, sư gia c&oacute; ch&uacute;t lo lắng nh&igrave;n tho&aacute;ng qua Th&aacute;i Trọng đi xa b&oacute;ng lưng.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng, ngươi sai, v&igrave; d&acirc;n chờ lệnh ch&iacute;nh l&agrave; bản quan chỗ chức tr&aacute;ch. Vừa vặn bản quan cũng đ&atilde; n&oacute;i, v&ocirc; luận l&agrave; ai, chỉ cần x&uacute;c phạm quốc ph&aacute;p liền phải nhận tội đền tội! Kh&ocirc;ng chỉ c&oacute; c&aacute;c ngươi muốn biết bản quan t&acirc;m &yacute;, tất cả b&aacute;ch t&iacute;nh đều phải biết!&quot;<br />\r\n<br />\r\n&quot;Đại nh&acirc;n. . . Ai!&quot; Sư gia lắc đầu, kh&ocirc;ng khỏi c&oacute; ch&uacute;t c&oacute; ch&uacute;t thở d&agrave;i. Vẫn l&agrave; tuổi c&ograve;n rất trẻ a, Th&aacute;i gia cắm rễ B&aacute;ch An huyện nhiều năm, sớm đ&atilde; l&agrave; th&acirc;m căn cố đế. C&aacute;c nh&agrave; lẫn nhau ở giữa c&oacute; nhiều th&ocirc;ng gia, đ&atilde; l&agrave; c&oacute; vinh c&ugrave;ng vinh.<br />\r\n<br />\r\nBởi v&igrave; c&aacute;i gọi l&agrave; l&agrave;m bằng sắt doanh trại qu&acirc;n đội, nước chảy huyện lệnh. Tại B&aacute;ch An huyện kh&ocirc;ng nể mặt bọn họ, bọn hắn cũng sẽ kh&ocirc;ng cho mặt mũi ngươi. C&aacute;i n&agrave;y mới tới huyện lệnh đại nh&acirc;n đem đường cho đi hẹp, về sau kh&ocirc;ng thể thiếu nếm m&ugrave;i đau khổ.<br />\r\n<br />\r\nKh&ocirc;ng được, về sau vẫn l&agrave; chớ c&ugrave;ng hắn đi qu&aacute; gần, để tr&aacute;nh ng&agrave;y sau bị nhằm v&agrave;o!<br />\r\n<br />\r\n&quot;C&ocirc; nương, c&aacute;i n&agrave;y văn tự b&aacute;n m&igrave;nh cho ngươi, ngươi về sau nhưng c&oacute; t&iacute;nh to&aacute;n g&igrave;?&quot;<br />\r\n<br />\r\n&quot;D&acirc;n nữ, d&acirc;n nữ vốn l&agrave; theo phụ th&acirc;n đến B&aacute;ch An huyện nương nhờ họ h&agrave;ng, nhưng hiện b&acirc;y giờ, hiện b&acirc;y giờ liền thừa ta một người kh&ocirc;ng chỗ nương tựa, d&acirc;n nữ cũng kh&ocirc;ng biết n&ecirc;n đi nơi n&agrave;o. . . .&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nTrong l&uacute;c nhất thời, thiếu nữ rốt cuộc khống chế kh&ocirc;ng nổi, trực tiếp quỳ xuống đất kh&oacute;c lớn tiếng. Nhu nhược kia d&aacute;ng vẻ, khỏi phải n&oacute;i l&agrave; Thẩm Ngọc, ngay cả chung quanh những c&aacute;i kia bọn nha dịch cũng nhịn kh&ocirc;ng được c&oacute; ch&uacute;t thương hại đối phương bắt đầu.<br />\r\n<br />\r\nDần dần thiếu nữ đ&igrave;nh chỉ th&uacute;t th&iacute;t, xoa xoa nước mắt, ngẩng đầu nh&igrave;n về ph&iacute;a Thẩm Ngọc, sau đ&oacute; lại đột nhi&ecirc;n quỳ rạp tr&ecirc;n đất &quot;Khẩn cầu đại nh&acirc;n thu lưu, giặt quần &aacute;o nấu cơm những n&agrave;y d&acirc;n nữ đều c&oacute; thể, c&ograve;n xin đại nh&acirc;n c&oacute; thể thu lưu d&acirc;n nữ!&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y. . . . . Ai, cũng được, tạm thời ngươi trước hết đi theo ta đi!&quot; Thẩm Ngọc hắn rất r&otilde; r&agrave;ng, dạng n&agrave;y một c&aacute;i yếu đuối nữ lưu, nếu l&agrave; bị Th&aacute;i gia để mắt tới, đo&aacute;n chừng ngay cả B&aacute;ch An huyện đều ra kh&ocirc;ng được!<br />\r\n<br />\r\nV&igrave; kế hoạch h&ocirc;m nay, cũng chỉ c&oacute; hắn mới c&oacute; thể tạm thời che chở đối phương. Nếu như về sau đối phương muốn đi, cũng t&ugrave;y thời đều c&oacute; thể.<br />\r\n<br />\r\n&quot;Đ&uacute;ng rồi, hệ thống đ&aacute;nh dấu!&quot;<br />\r\n<br />\r\n&quot;Đ&aacute;nh dấu th&agrave;nh c&ocirc;ng, thu hoạch được Dưỡng Ng&ocirc; kiếm ph&aacute;p!&quot;<br />\r\n<br />\r\n&quot;Dưỡng Ng&ocirc; kiếm ph&aacute;p?&quot; Theo hệ thống nhắc nhở &acirc;m rơi xuống, v&ocirc; số tin tức tr&agrave;n v&agrave;o trong đầu b&ecirc;n trong. Phảng phất trước mắt hiện ra một đạo tr&ecirc;n dưới bốc l&ecirc;n b&oacute;ng người, kiếm quang lưu chuyển ở giữa mang theo kh&oacute; lường uy lực.<br />\r\n<br />\r\nPhảng phất mấy năm thời gian lặng y&ecirc;n m&agrave; qua, Thẩm Ngọc cảm gi&aacute;c m&igrave;nh tựa hồ đ&atilde; khổ luyện v&agrave;i năm l&acirc;u, đ&atilde; đem Dưỡng Ng&ocirc; kiếm ph&aacute;p triệt để chưởng, thậm ch&iacute; đi ra con đường của m&igrave;nh.<br />\r\n<br />\r\nC&agrave;ng th&ecirc;m tiền th&acirc;n thế nhưng học h&agrave;nh gian khổ mười mấy năm, ch&iacute;nh hai t&aacute;m trải qua người đọc s&aacute;ch, tựa hồ c&ugrave;ng bộ kiếm ph&aacute;p kia tương đương ph&ugrave; hợp. Ngay cả tự th&acirc;n kh&iacute; chất, cũng tựa hồ t&ugrave;y theo ph&aacute;t sinh biến h&oacute;a.<br />\r\n<br />\r\nM&agrave; c&ugrave;ng l&uacute;c đ&oacute;, ở chung quanh người trong mắt, Thẩm Ngọc tr&ecirc;n th&acirc;n phảng phất đang trong khoảnh khắc cũng c&oacute; một ch&uacute;t đặc th&ugrave; biến h&oacute;a, giống như nhiều hơn mấy phần hạo nhi&ecirc;n, nhiều hơn mấy phần kh&iacute; đ&oacute;ng sơn h&agrave; kh&iacute; chất.<br />\r\n<br />\r\nDưỡng Ng&ocirc; kiếm ph&aacute;p, đ&atilde; l&agrave; một loại kiếm ph&aacute;p, lại phảng phất g&aacute;nh chịu một loại nh&acirc;n c&aacute;ch lực lượng, giảng cứu hạo nhi&ecirc;n ch&iacute;nh đại, ch&iacute; ch&iacute;nh ch&iacute; cương. C&aacute;i n&agrave;y kiếm ph&aacute;p so Thẩm Ngọc trong tưởng tượng mạnh hơn nhiều, phảng phất c&oacute; thể theo hắn tu dưỡng kh&ocirc;ng ngừng đề cao, m&agrave; kh&ocirc;ng ngừng thăng hoa.<br />\r\n<br />\r\nBất qu&aacute; hắn cũng r&otilde; r&agrave;ng, hệ thống qu&aacute;n đỉnh chỉ l&agrave; để hắn trở th&agrave;nh l&yacute; luận đại sư, nếu l&agrave; muốn đem những n&agrave;y to&agrave;n bộ biến ho&aacute; để cho bản th&acirc;n sử dụng, c&ograve;n được chăm học khổ luyện một phen mới được.<br />\r\n<br />\r\n&quot;Quả nhi&ecirc;n ta phỏng đo&aacute;n l&agrave; đ&uacute;ng, thật sự cho rằng h&ocirc;m nay ta l&agrave; x&uacute;c động a, kh&ocirc;ng dạng n&agrave;y l&agrave;m sao dẫn tới b&aacute;ch t&iacute;nh đến b&aacute;o &aacute;n tố khổ đ&acirc;u!&quot;<br />\r\n<br />\r\nChỉ cần kh&ocirc;ng ngừng đ&aacute;nh dấu, liền c&oacute; thể kh&ocirc;ng ngừng mạnh l&ecirc;n, cầm kiếm thi&ecirc;n hạ, mỹ nh&acirc;n trong ngực. . . . Nha nha, kh&ocirc;ng thể nghĩ, kh&ocirc;ng thể nghĩ, v&igrave; sao ta cảm gi&aacute;c giống như đ&atilde; đi l&ecirc;n nh&acirc;n sinh đỉnh phong.</p>', '2021-12-01 01:30:44', 4, 1),
(284, 74, 'Sổ Sách', '3', '<p>&quot;H&ocirc;, h&ocirc;!&quot;<br />\r\n<br />\r\nĐ&aacute;nh dấu đạt được ban thưởng sau Thẩm Ngọc t&acirc;m t&igrave;nh thật tốt, t&igrave;m người ra ngo&agrave;i mua một thanh kiếm, sau đ&oacute; đem sở hữu người đuổi ra ngo&agrave;i về sau, m&igrave;nh ngay tại trong viện luyện.<br />\r\n<br />\r\nCho d&ugrave; Thẩm Ngọc t&ugrave;y th&acirc;n bội kiếm, những người kh&aacute;c cũng kh&ocirc;ng thấy phải c&oacute; c&aacute;i g&igrave;. Đầu năm nay, c&oacute; c&ocirc;ng danh tr&ecirc;n người c&oacute; rất nhiều th&iacute;ch cầm quạt phối kiếm, c&agrave;i bộ d&aacute;ng m&agrave; th&ocirc;i, cũng kh&ocirc;ng cần qu&aacute; coi l&agrave; thật.<br />\r\n<br />\r\nC&aacute;i n&agrave;y mới tiền nhiệm huyện lệnh đại nh&acirc;n đến tột c&ugrave;ng l&agrave; c&aacute;i tr&igrave;nh độ, vậy bọn hắn c&ograve;n c&oacute; thể kh&ocirc;ng biết a. Có th&ecirc;̉ bị Th&aacute;i H&ograve;a bực n&agrave;y bất học v&ocirc; thuật c&ocirc;ng tử ca h&agrave;nh hung, thỏa thỏa thư sinh yếu đuối, n&oacute;i kh&ocirc;ng chừng ngay cả c&aacute;i cường tr&aacute;ng điểm phụ nh&acirc;n đều đ&aacute;nh kh&ocirc;ng lại.<br />\r\n<br />\r\nM&agrave; bọn hắn kh&ocirc;ng biết ch&iacute;nh l&agrave;, nội viện b&ecirc;n trong Thẩm Ngọc Dưỡng Ng&ocirc; kiếm ph&aacute;p v&ugrave; v&ugrave; x&eacute; gi&oacute;, một chi&ecirc;u trong l&uacute;c nhất thời hiển thị r&otilde; huy ho&agrave;ng kh&iacute; quyển. Mặc d&ugrave; bởi v&igrave; vừa mới bắt đầu luyện tập c&ograve;n mang theo kh&ocirc;ng lưu lo&aacute;t, nhưng nghĩ đến kh&ocirc;ng được bao l&acirc;u, hắn liền c&oacute; thể ho&agrave;n to&agrave;n đăng đường nhập thất.<br />\r\n<br />\r\nMột cước lập tức bước ra, h&ugrave;ng hậu nội lực qu&aacute;n ch&uacute; to&agrave;n th&acirc;n, nguy&ecirc;n địa cứng rắn b&ecirc;n tr&ecirc;n thậm ch&iacute; đều ấn ra một ch&uacute;t dấu ch&acirc;n. Sau đ&oacute; hướng ph&iacute;a trước bỗng nhi&ecirc;n một kiếm, tại Thẩm Ngọc nội lực gia tr&igrave; ph&iacute;a dưới, một kiếm n&agrave;y giống như nhất sắc b&eacute;n mũi t&ecirc;n, mang theo thế l&ocirc;i đ&igrave;nh vạn qu&acirc;n.<br />\r\n<br />\r\nDưỡng Ng&ocirc; kiếm ph&aacute;p phối hợp To&agrave;n Ch&acirc;n t&acirc;m ph&aacute;p, kia cầm kiếm tung ho&agrave;nh ti&ecirc;u s&aacute;i trạng th&aacute;i, k&eacute;m ch&uacute;t để Thẩm Ngọc m&igrave;nh trầm m&ecirc; trong đ&oacute;. Đ&acirc;y cũng ch&iacute;nh l&agrave; chung quanh kh&ocirc;ng c&oacute; m&ecirc; muội, nếu kh&ocirc;ng sớm l&agrave; một trận lại một trận th&eacute;t l&ecirc;n tiếng hoan h&ocirc;.<br />\r\n<br />\r\n&quot;Ba!&quot; Lại lần nữa hướng về ph&iacute;a trước dậm ch&acirc;n m&agrave; ra, lần n&agrave;y, Thẩm Ngọc dưới ch&acirc;n phiến đ&aacute; lại l&agrave; l&ecirc;n tiếng m&agrave; nứt, cũng l&agrave;m cho hắn sửng sốt một ch&uacute;t.<br />\r\n<br />\r\nMặc d&ugrave; hắn hiện tại đ&atilde; c&oacute; được một ch&uacute;t thực lực, miễn cưỡng t&iacute;nh kh&ocirc;ng l&ecirc;n yếu g&agrave; h&agrave;ng ngũ, nhưng cũng kh&ocirc;ng c&oacute; lợi hại như vậy. Một cước n&agrave;y xuống dưới, c&oacute; thể đem thanh nham thạch trải s&agrave;n nh&agrave; cho giẫm nứt rồi?<br />\r\n<br />\r\nHắn nội c&ocirc;ng chỉ l&agrave; nhập m&ocirc;n cũng kh&ocirc;ng phải đăng đường nhập thất, n&agrave;o c&oacute; uy lực như vậy. Nh&igrave;n nh&igrave;n lại c&aacute;i kh&aacute;c địa phương, cũng liền miễn cưỡng giẫm ra điểm dấu ch&acirc;n, c&aacute;i n&agrave;y coi như kỳ qu&aacute;i.<br />\r\n<br />\r\nĐi l&ecirc;n trước, Thẩm Ngọc ph&aacute;t hiện c&aacute;i n&agrave;y dưới s&agrave;n nh&agrave; tựa hồ c&oacute; ch&uacute;t trống rỗng. Kh&oacute; tr&aacute;ch như thế dễ d&agrave;ng liền r&aacute;ch ra, nguy&ecirc;n lai dưới đ&aacute;y l&agrave; trống rỗng.<br />\r\n<br />\r\nKh&ocirc;ng đ&uacute;ng, nơi n&agrave;y d&ugrave; sao cũng l&agrave; huyện nha phủ viện, l&agrave;m sao c&ograve;n c&oacute; thể c&oacute; b&atilde; đậu c&ocirc;ng tr&igrave;nh, kh&ocirc;ng nghĩ tới r&ocirc;̀i sao?<br />\r\n<br />\r\nPh&aacute;t gi&aacute;c c&oacute; ch&uacute;t dị thường Thẩm Ngọc, đem trong tay trường kiếm trực tiếp cắm v&agrave;o phiến đ&aacute; khe hở ở giữa, nội lực vận chuyển ph&iacute;a dưới bỗng nhi&ecirc;n vừa d&ugrave;ng lực, đem khối n&agrave;y vốn l&agrave; vỡ vụn phiến đ&aacute; đẩy ra, cũng lộ ra giấu ở đồ vật b&ecirc;n trong.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; c&aacute;i g&igrave;? Giấu như thế chặt chẽ?&quot; Phiến đ&aacute; b&ecirc;n trong cất giấu một c&aacute;i bị thật d&agrave;y vải dầu bọc giấy bao lấy đồ vật, đem đồ vật b&ecirc;n trong đem ra, để lộ một tầng lại một tầng bao khỏa chặt chẽ vải dầu giấy, lộ ra b&ecirc;n trong s&aacute;ch.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave;. . . Sổ s&aacute;ch?&quot; Nh&igrave;n xem s&aacute;ch trong tay s&aacute;ch, Thẩm Ngọc ho&agrave;n nguy&ecirc;n tưởng rằng trước kia huyện lệnh giấu loại kia kh&ocirc;ng thể mi&ecirc;u tả thư tịch, nhưng chờ cẩn thận lật về sau, v&acirc;̣y mà ph&aacute;t hiện b&ecirc;n trong l&agrave; một c&aacute;i sổ s&aacute;ch.<br />\r\n<br />\r\nCó th&ecirc;̉ bị bao khỏa như thế chặt chẽ, lại giấu ở c&aacute;i n&agrave;y địa phương, Thẩm Ngọc trong l&ograve;ng cũng hưng khởi một ch&uacute;t hiếu k&igrave;. Đem sổ s&aacute;ch cầm v&agrave;o trong nh&agrave;, tinh tế lật nh&igrave;n bắt đầu, ph&aacute;t hiện c&aacute;i n&agrave;y v&acirc;̣y mà l&agrave; B&aacute;ch An huyện phủ khố sổ s&aacute;ch.<br />\r\n<br />\r\nMặc d&ugrave; hắn xuy&ecirc;n qua trước l&agrave;m việc kh&ocirc;ng phải chuy&ecirc;n m&ocirc;n l&agrave;m t&agrave;i vụ, nhưng trước kia c&aacute;i đồ chơi n&agrave;y cũng l&agrave; học qua. Trải qua cẩn thận lật xem qua về sau, Thẩm Ngọc vẫn kh&ocirc;ng khỏi cảm gi&aacute;c c&oacute; ch&uacute;t mồ h&ocirc;i lạnh l&acirc;m ly.<br />\r\n<br />\r\nSổ s&aacute;ch ph&iacute;a tr&ecirc;n r&otilde; r&agrave;ng bị ph&ecirc; b&igrave;nh ch&uacute; giải rất nhiều, rất nhiều giấu diếm khoản đều bị v&ograve;ng ra. Nh&igrave;n ph&iacute;a tr&ecirc;n n&agrave;y ph&ecirc; b&igrave;nh ch&uacute; giải chữ viết, c&ugrave;ng B&aacute;ch An huyện đời trước vị kia huyện lệnh tựa hồ rất tương tự. Trong huyện nha c&ograve;n c&oacute; lưu tiền nhiệm lưu lại mặc bảo, cho n&ecirc;n điểm n&agrave;y hắn hẳn l&agrave; sẽ kh&ocirc;ng nh&igrave;n lầm.<br />\r\n<br />\r\nKinh nghi qua đi, Thẩm Ngọc lại lần nữa từ đầu tới đu&ocirc;i đem sổ s&aacute;ch tinh tế lật xem, sau đ&oacute; lại tự m&igrave;nh t&iacute;nh một lần, lập tức cảm thấy mở rộng tầm mắt.<br />\r\n<br />\r\nSổ s&aacute;ch người sau lưng, th&ocirc;ng qua c&aacute;c loại thủ đoạn giấu diếm khoản, hư nhấc gi&aacute; cả c&aacute;c loại, m&agrave; một v&agrave;o một ra ở giữa, B&aacute;ch An huyện phủ khố lại bị m&oacute;c rỗng hơn ph&acirc;n nửa, những người n&agrave;y vẫn l&agrave; thực biết chơi!<br />\r\n<br />\r\nCũng may c&aacute;i n&agrave;y sổ s&aacute;ch cũng r&otilde; r&agrave;ng kh&ocirc;ng phải hiện tại, hẳn l&agrave; hai, ba năm trước. Đợi l&aacute;t nữa, hai, ba năm trước, đ&acirc;y chẳng phải l&agrave;. . . . .<br />\r\n<br />\r\nKhoảng thời gian n&agrave;y, Thẩm Ngọc cũng biết qua trước kia chuyện xảy ra. Tại hai, ba năm trước vị kia B&aacute;ch An huyện huyện lệnh, trước đ&oacute; cũng coi l&agrave; c&aacute;i quan tốt, mặc d&ugrave; t&iacute;nh kh&ocirc;ng l&ecirc;n thiện trị sở trường, nhưng thắng ở cần c&ugrave; chăm chỉ, bởi vậy phong b&igrave;nh cũng kh&ocirc;ng tệ.<br />\r\n<br />\r\nNhưng đại kh&aacute;i hai năm trước Trường Lĩnh quận ph&aacute;t sinh t&igrave;nh h&igrave;nh hạn h&aacute;n, triều đ&igrave;nh mệnh c&aacute;c nơi mở kho ph&aacute;t th&oacute;c, cuối c&ugrave;ng lại ph&aacute;t hiện B&aacute;ch An huyện phủ khố trống rỗng. Kh&ocirc;ng chỉ c&oacute; như thế, b&ecirc;n trong lương thực v&acirc;̣y mà hơn ph&acirc;n nửa đều l&agrave; nấm mốc gạo. Như thế t&igrave;nh huống, lập tức dẫn tới s&oacute;ng to gi&oacute; lớn.<br />\r\n<br />\r\nM&agrave; lại bởi v&igrave; cũng kh&ocirc;ng đủ lương thực, cũng dẫn đến k&eacute;m ch&uacute;t k&iacute;ch th&iacute;ch d&acirc;n biến, cho n&ecirc;n triều đ&igrave;nh rất l&agrave; tức giận, ngay l&uacute;c đ&oacute; huyện lệnh liền bị trực tiếp cầm xuống răng rắc.<br />\r\n<br />\r\nTham quan &ocirc; lại nha, bị giết thời điểm b&aacute;ch t&iacute;nh vỗ tay bảo hay. M&agrave; lại bởi v&igrave; kh&ocirc;ng thể tới l&uacute;c chẩn tai, l&uacute;c ấy b&aacute;ch t&iacute;nh nhiều chết đ&oacute;i, cho n&ecirc;n hận kh&ocirc;ng thể ăn sống huyết nhục.<br />\r\n<br />\r\nB&acirc;y giờ nh&igrave;n lại, l&uacute;c trước tựa hồ c&oacute; ẩn t&igrave;nh kh&aacute;c a. M&agrave; lại từ phủ khố trống rỗng lộ ra &aacute;nh s&aacute;ng đến cuối c&ugrave;ng huyện lệnh bị cầm xuống, to&agrave;n bộ qu&aacute; tr&igrave;nh kh&ocirc;ng khỏi c&oacute; ch&uacute;t qu&aacute; nhanh, thật giống như c&oacute; người ở ph&iacute;a sau đẩy đi đồng dạng.<br />\r\n<br />\r\nĐột nhi&ecirc;n, Thẩm Ngọc trong đầu nghĩ đến m&igrave;nh tiền nhiệm, c&aacute;i kia vừa tiền nhiệm kh&ocirc;ng đến một năm liền bởi v&igrave; kh&ocirc;ng quen kh&iacute; hậu, lại l&acirc;y nhiễm phong h&agrave;n, cuối c&ugrave;ng liền kh&ocirc;ng c&oacute; chống đỡ vị kia.<br />\r\n<br />\r\nC&aacute;i n&agrave;y sổ s&aacute;ch ch&iacute;nh l&agrave; hắn ph&ecirc; b&igrave;nh ch&uacute; giải, sau đ&oacute; vị n&agrave;y tiền nhiệm huyện lệnh kh&ocirc;ng l&acirc;u liền l&acirc;y nhiễm phong h&agrave;n, tr&ecirc;n đời n&agrave;y n&agrave;o c&oacute; tr&ugrave;ng hợp như vậy sự t&igrave;nh?<br />\r\n<br />\r\nKh&ocirc;ng biết v&igrave; c&aacute;i g&igrave;, suy nghĩ lung tung ở giữa Thẩm Ngọc đột nhi&ecirc;n cảm thấy to&agrave;n th&acirc;n c&oacute; ch&uacute;t lạnh, một cỗ kh&ocirc;ng hiểu h&agrave;n kh&iacute; phảng phất tốc thẳng v&agrave;o mặt. Vũng nước n&agrave;y, c&oacute; ch&uacute;t s&acirc;u nha!<br />\r\n<br />\r\nBất qu&aacute; sau đ&oacute;, một cỗ nhiệt huyết x&ocirc;ng l&ecirc;n đầu, đem tất cả h&agrave;n kh&iacute; đều xua đuổi. M&igrave;nh c&oacute; hệ thống nơi tay, th&igrave; sợ g&igrave; một ch&uacute;t khi&ecirc;u chiến. Huống chi, m&igrave;nh nhiệt huyết cũng chưa từng mẫn diệt.<br />\r\n<br />\r\nHiện b&acirc;y giờ m&igrave;nh thế nhưng l&agrave; một huyện chi trưởng, bởi v&igrave; c&aacute;i gọi l&agrave; tại vị mưu ch&iacute;nh. M&igrave;nh xuy&ecirc;n qua trước khả năng kh&ocirc;ng c&oacute; cơ hội n&agrave;y, nhưng đ&atilde; hiện tại c&oacute; cơ hội, đương nhi&ecirc;n muốn tạo ph&uacute;c b&aacute;ch t&iacute;nh, v&igrave; d&acirc;n chờ lệnh, mở rộng ch&iacute;nh nghĩa! Như thế, phương kh&ocirc;ng phụ m&igrave;nh đến như thế một lần!<br />\r\n<br />\r\nĐương nhi&ecirc;n, thuận tiện c&ograve;n c&oacute; thể k&yacute; c&aacute;i đến!<br />\r\n<br />\r\nTheo Thẩm Ngọc hiểu r&otilde; t&igrave;nh huống, B&aacute;ch An huyện b&ecirc;n trong ba nh&agrave; một bang, vậy liền kh&ocirc;ng c&oacute; mấy c&aacute;i đồ tốt. Nhất l&agrave; huyện thừa chỗ Th&aacute;i gia, ng&agrave;y b&igrave;nh thường ỷ thế hiếp người mua thấp b&aacute;n cao. M&agrave; lại hai năm trước đại hạn c&agrave;ng l&agrave; trữ h&agrave;ng đầu cơ t&iacute;ch trữ, mảy may kh&ocirc;ng để &yacute; tới b&aacute;ch t&iacute;nh chết sống.<br />\r\n<br />\r\nPhủ khố sự t&igrave;nh, Thẩm Ngọc kh&ocirc;ng tin tưởng bọn họ một ch&uacute;t tin tức đều kh&ocirc;ng biết. Đ&aacute;m n&agrave;y s&acirc;u mọt, vậy th&igrave; phải to&agrave;n bộ bắt tới một t&ecirc;n cũng kh&ocirc;ng để lại!<br />\r\n<br />\r\n&quot;Người tới, để Chu bổ đầu đem bọn bổ kho&aacute;i to&agrave;n mang đến!&quot;<br />\r\n<br />\r\n&quot;Đại nh&acirc;n!&quot; Liền rất nhanh, Chu bổ đầu liền mang theo bọn bổ kho&aacute;i vội v&agrave;ng m&agrave; tới. Đến b&acirc;y giờ hắn c&ograve;n một mặt mộng tướng, kh&ocirc;ng biết Thẩm Ngọc muốn ph&acirc;n ph&oacute; sự t&igrave;nh g&igrave;, lại đem tất cả bổ kho&aacute;i đều triệu tập tới.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nGần nhất B&aacute;ch An huyện kh&ocirc;ng n&oacute;i rất th&aacute;i b&igrave;nh đi, nhưng cũng kh&ocirc;ng c&oacute; g&igrave; đại sự a, nếu l&agrave; c&oacute; c&aacute;i g&igrave; đại sự, m&igrave;nh c&aacute;i n&agrave;y bổ đầu có th&ecirc;̉ kh&ocirc;ng biết a.<br />\r\n<br />\r\n&quot;Tới?&quot; Nh&igrave;n Chu Nguy&ecirc;n một ch&uacute;t, lại nh&igrave;n mắt ph&iacute;a sau hắn bọn bổ kho&aacute;i, Thẩm Ngọc l&uacute;c n&agrave;y mới h&agrave;i l&ograve;ng gật đầu, sau đ&oacute; hướng bọn hắn vẫy vẫy tay: &quot;Đi, đều c&ugrave;ng bản quan đi!&quot;<br />\r\n<br />\r\n&quot;Đại nh&acirc;n, ch&uacute;ng ta đi đ&acirc;u?&quot;<br />\r\n<br />\r\n&quot;Đi phủ khố!&quot;<br />\r\n<br />\r\n&quot;A? Đi phủ khố?&quot; Nghe được Thẩm Ngọc, Chu bổ đầu c&ograve;n c&oacute; ch&uacute;t phản ứng kh&ocirc;ng kịp. Đi phủ khố m&agrave; th&ocirc;i, cần phải mang nhiều người như vậy a, kh&ocirc;ng biết c&ograve;n tưởng rằng l&agrave; muốn đi đoạt cướp đ&acirc;u.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng sai, c&ugrave;ng bản quan đi, nhanh!&quot; Thẩm Ngọc ch&iacute;nh l&agrave; đi đ&aacute;nh cược một lần, cược đ&aacute;m người n&agrave;y ch&oacute; kh&ocirc;ng đổi được đớp cứt! Hơn hai năm trước có th&ecirc;̉ th&acirc;m hụt phủ khố, hai năm về sau c&aacute;i kia c&ograve;n c&oacute; thể để cho phủ khố tr&agrave;n đầy rồi?<br />\r\n<br />\r\nM&egrave;o gi&agrave; gối c&aacute; ướp muối, sợ l&agrave; &aacute;nh s&aacute;ng c&ograve;n lại xương cốt đi!<br />\r\n<br />\r\nTuy n&oacute;i b&acirc;y giờ chọn lựa nước ấm nấu ếch xanh phương thức đối ph&oacute; những người kia c&agrave;ng ổn thỏa một ch&uacute;t, nhưng Thẩm Ngọc r&otilde; r&agrave;ng ch&iacute;nh m&igrave;nh hiện tại đ&atilde; c&ugrave;ng Th&aacute;i gia giao &aacute;c, nước ấm nấu ếch xanh chi&ecirc;u n&agrave;y chỉ sợ kh&ocirc;ng được tốt lắm. Huống chi, Thẩm Ngọc mới đến cũng chia kh&ocirc;ng r&otilde; địch ta.<br />\r\n<br />\r\nCho n&ecirc;n kh&ocirc;ng rung c&acirc;y dọa khỉ một ch&uacute;t, bọn hắn sao c&oacute; thể nhảy ra! M&agrave; lại giải quyết dứt kho&aacute;t ph&iacute;a dưới, ch&iacute; &iacute;t có th&ecirc;̉ đoạn bọn hắn một đầu c&aacute;nh tay. Trọng yếu nhất ch&iacute;nh l&agrave;, xử l&yacute; tham quan &ocirc; lại c&ograve;n c&oacute; thể đ&aacute;nh dấu.<br />\r\n<br />\r\nT&oacute;m lại, c&aacute;i n&agrave;y một đợt tuyệt đối kh&ocirc;ng lỗ. . .</p>', '2021-12-01 01:31:36', 5, 1),
(285, 74, 'Ngươi Biết Võ Công?', '4', '<p>&quot;Kho lại ở đ&acirc;u, đem cửa mở ra, thuận tiện đem sổ s&aacute;ch lấy tới!&quot;<br />\r\n<br />\r\nMang theo một đ&aacute;m người ngựa thẳng đến phủ khố chỗ, Thẩm Ngọc cũng kh&ocirc;ng n&oacute;i nhảm, trực tiếp hướng kho lại y&ecirc;u cầu phủ khố sổ s&aacute;ch, thuận tiện để bọn hắn đem phủ khố lấy mở ra thuận tiện hắn từng c&aacute;i kiểm tra thực hư.<br />\r\n<br />\r\n&quot;Đại nh&acirc;n, phủ khố trọng địa, h&aacute; c&oacute; thể tuỳ tiện mở ra?&quot; Nhiều người như vậy đột nhi&ecirc;n xuất hiện, kho lại tr&ecirc;n mặt r&otilde; r&agrave;ng hiển lộ ra một ch&uacute;t bối rối chi sắc, nhưng rất nhanh liền trấn tĩnh xuống tới.<br />\r\n<br />\r\n&quot;L&agrave;m c&agrave;n! Bản quan quan mới tiền nhiệm, đương nhi&ecirc;n phải kiểm k&ecirc; phủ khố, chẳng lẽ lại b&ecirc;n trong c&oacute; mờ &aacute;m a? Tr&aacute;nh ra!&quot; Lạnh a một tiếng, Thẩm Ngọc trực tiếp mệnh bọn bổ kho&aacute;i đem người đẩy ra, sau đ&oacute; mở ra phủ khố đại m&ocirc;n đi v&agrave;o.<br />\r\n<br />\r\nChỉ bất qu&aacute; trước mắt cảnh tượng c&ugrave;ng tưởng tượng b&ecirc;n trong hơi c&oacute; ch&ecirc;nh lệch, kho lẫm b&ecirc;n trong lương thực đ&acirc;̀y kho, phủ khố tr&agrave;n đầy, tựa hồ một điểm kh&ocirc;ng c&oacute; th&acirc;m hụt d&aacute;ng vẻ.<br />\r\n<br />\r\nTiện tay d&ugrave;ng kiếm tại mỹ t&uacute;i hất l&ecirc;n một c&aacute;i lỗ nhỏ, sung m&atilde;n hạt gạo thuận lỗ hổng &agrave;o &agrave;o rơi xuống, đi theo ph&iacute;a sau tiến đến kho lại tr&ecirc;n mặt kh&ocirc;ng khỏi lộ ra một tia tươi cười đắc &yacute;, hướng về ph&iacute;a Thẩm Ngọc chắp tay.<br />\r\n<br />\r\n&quot;Đại nh&acirc;n, phủ khố trọng địa một mực l&agrave; huyện thừa đại nh&acirc;n tự m&igrave;nh gi&aacute;m thị, ch&uacute;ng ta cũng l&agrave; cẩn thận chặt chẽ kh&ocirc;ng d&aacute;m c&oacute; ch&uacute;t lười biếng, đại nh&acirc;n y&ecirc;n t&acirc;m, phủ khố chi địa hẳn l&agrave; vạn v&ocirc; nhất thất, tuyệt sẽ kh&ocirc;ng c&oacute; nửa điểm sơ hở!&quot;<br />\r\n<br />\r\nNgẩng đầu, kho lại kia đắc &yacute; biểu lộ kh&ocirc;ng che giấu ch&uacute;t n&agrave;o, phảng phất đang chờ đợi Thẩm Ngọc kh&iacute;ch lệ. Lại l&agrave; kh&ocirc;ng biết tại Thẩm Ngọc trong mắt, hắn n&eacute;t mặt b&acirc;y giờ thật để người nhịn kh&ocirc;ng được muốn vung l&ecirc;n nắm đấm đ&aacute;nh hắn.<br />\r\n<br />\r\n&quot;Vạn v&ocirc; nhất thất? Phải kh&ocirc;ng?&quot; Nh&agrave;n nhạt nh&igrave;n đối phương một ch&uacute;t, Thẩm Ngọc lại nh&igrave;n một ch&uacute;t c&aacute;i n&agrave;y đ&acirc;̀y kho lương thực, sau đ&oacute; x&ocirc;ng bọn bổ kho&aacute;i vẫy vẫy tay: &quot;Đến a, đem b&ecirc;n ngo&agrave;i những n&agrave;y đều đẩy ra, bản quan muốn kiểm tra b&ecirc;n trong lương thực!&quot;<br />\r\n<br />\r\n&quot;Đại nh&acirc;n!&quot; Thẩm Ngọc đột nhi&ecirc;n quyết định, để kho lại giật nảy cả m&igrave;nh, sắc mặt r&otilde; r&agrave;ng c&oacute; biến h&oacute;a, vội v&agrave;ng n&oacute;i &quot;Đại nh&acirc;n, ng&agrave;i quan mới tiền nhiệm khả năng kh&ocirc;ng biết, phủ khố trọng địa kh&ocirc;ng được khinh động, vạn nhất xảy ra sự t&igrave;nh g&igrave;, ch&uacute;ng ta đều đảm đương kh&ocirc;ng n&ocirc;̉i!&quot;<br />\r\n<br />\r\n&quot;Ta để c&aacute;c ngươi đảm đương r&ocirc;̀i sao? Xảy ra chuyện g&igrave;, bản quan phụ tr&aacute;ch! Cho ta chuyển!&quot;<br />\r\n<br />\r\nH&eacute;t lớn một tiếng, Thẩm Ngọc sau đ&oacute; mệnh lệnh bọn bổ kho&aacute;i h&agrave;nh động. Rất nhanh, tại Thẩm Ngọc mệnh lệnh dưới, những n&agrave;y bọn bổ kho&aacute;i liền đem ph&iacute;a ngo&agrave;i c&ugrave;ng lương thực đem đến một b&ecirc;n. C&agrave;ng đi b&ecirc;n trong, Thẩm Ngọc r&otilde; r&agrave;ng có th&ecirc;̉ cảm gi&aacute;c được b&ecirc;n cạnh kho lại nh&oacute;m dần dần trở n&ecirc;n bối rối, đến cuối c&ugrave;ng thậm ch&iacute; đều c&oacute; ch&uacute;t run rẩy.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nC&aacute;i n&agrave;y một đợt, xem ra l&agrave; ổn! Hắn liền biết ch&oacute; kh&ocirc;ng đổi được đớp cứt, bất qu&aacute; l&agrave; l&agrave;m được b&iacute; mật hơn ch&uacute;t m&agrave; th&ocirc;i, bất qu&aacute; l&agrave; l&agrave;m l&agrave;m mặt ngo&agrave;i c&ocirc;ng phu che lấp một phen, đi&ecirc;u tr&ugrave;ng tiểu kỹ!<br />\r\n<br />\r\n&quot;Vụt!&quot; R&uacute;t ra t&ugrave;y th&acirc;n bội kiếm, Thẩm Ngọc trực tiếp cắm v&agrave;o b&ecirc;n trong t&uacute;i gạo b&ecirc;n tr&ecirc;n, rất nhanh, rầm rầm b&ugrave;n c&aacute;t đ&aacute; vụn liền thuận lỗ hổng chảy ra.<br />\r\n<br />\r\n&quot;Đ&acirc;y ch&iacute;nh l&agrave; vạn v&ocirc; nhất thất?&quot; Quay đầu mắt nh&igrave;n kho lại, Thẩm Ngọc hừ lạnh một tiếng, sau đ&oacute; từ trong ra ngo&agrave;i lần lượt th&ocirc;ng một lần. To&agrave;n bộ phủ khố b&ecirc;n trong, lập tức tất cả đều l&agrave; b&ugrave;n c&aacute;t đ&aacute; vụn rơi xuống đất &agrave;o &agrave;o thanh &acirc;m.<br />\r\n<br />\r\nThẩm Ngọc vươn tay tiếp thổi phồng, sắc mặt kh&ocirc;ng khỏi trở n&ecirc;n c&oacute; ch&uacute;t &acirc;m trầm. N&atilde;i n&atilde;i, lũ khốn kiếp n&agrave;y kh&ocirc;ng khỏi qu&aacute; ph&aacute;ch lối ch&uacute;t, chỉ c&oacute; ph&iacute;a ngo&agrave;i m&ocirc;̣t đi&ecirc;̉m là thật, c&ograve;n lại tất cả đều l&agrave; giả, c&aacute;i n&agrave;y mẹ n&oacute; cũng qu&aacute; tham ch&uacute;t đi.<br />\r\n<br />\r\nKia ng&agrave;y sau nếu l&agrave; điều tra ra, c&oacute; phải l&agrave; m&igrave;nh c&aacute;i n&agrave;y đương nhiệm huyện lệnh lại phải bối hắc oa? Thật giống như trước tiền nhiệm đồng dạng, bị giết kh&ocirc;ng minh bạch!<br />\r\n<br />\r\nThậm ch&iacute;, nếu l&agrave; m&igrave;nh tại tr&ecirc;n vị tr&iacute; n&agrave;y ngồi thời gian l&acirc;u d&agrave;i, tr&aacute;ch nhiệm đẩy kh&ocirc;ng xong thời điểm, đ&aacute;m người n&agrave;y ho&agrave;n to&agrave;n liền c&oacute; thể d&ugrave;ng n&agrave;y đến &aacute;p chế hắn. Đến thời điểm, phủ khố trống rỗng hắn kh&ocirc;ng thể đổ cho người kh&aacute;c, chỉ c&oacute; thể mặc cho người x&acirc;m lược. Kh&ocirc;ng phải người ta trở tay một c&aacute;i b&aacute;o c&aacute;o, hắn liền phải chịu kh&ocirc;ng nổi.<br />\r\n<br />\r\nCũng may hắn hiện tại vừa vặn tiền nhiệm kh&ocirc;ng c&oacute; mấy ng&agrave;y, c&aacute;i n&agrave;y nồi coi như l&agrave;m sao bỏ cũng kh&ocirc;ng đến hắn tr&ecirc;n th&acirc;n.<br />\r\n<br />\r\n&quot;Nh&igrave;n xem, đ&acirc;y ch&iacute;nh l&agrave; ch&iacute;nh ngươi n&oacute;i phủ khố tr&agrave;n đầy, c&aacute;c ngươi thật to gan!&quot; Đem trong tay b&ugrave;n đất hung hăng n&eacute;m v&agrave;o bọn hắn tr&ecirc;n mặt, Thẩm Ngọc sau đ&oacute; x&ocirc;ng bọn bổ kho&aacute;i la lớn &quot;Đều cho ta cầm xuống!&quot;<br />\r\n<br />\r\n&quot;Ai nha nha, đ&acirc;y l&agrave; thế n&agrave;o. . .&quot; Đột nhi&ecirc;n, phủ khố b&ecirc;n ngo&agrave;i vang l&ecirc;n một đạo thanh &acirc;m quen thuộc, rất nhanh, huyện thừa Th&aacute;i Trọng liền vội v&agrave;ng m&agrave; đến, nh&igrave;n thấy b&ecirc;n trong cảnh tượng, lại thấy được t&uacute;i gạo b&ecirc;n trong &agrave;o &agrave;o rơi xuống b&ugrave;n c&aacute;t, bắp thịt tr&ecirc;n mặt r&otilde; r&agrave;ng run l&ecirc;n.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; c&oacute; chuyện g&igrave;? Gạo đ&acirc;u?&quot; Trong nh&aacute;y mắt, Th&aacute;i Trọng tr&ecirc;n mặt liền chỉ c&ograve;n lại c&oacute; phẫn nộ, một tay lấy bị đ&egrave; xuống đất kho lại k&eacute;o l&ecirc;n, hai mắt b&ecirc;n trong lửa giận giống như phun ra ngo&agrave;i, biểu lộ dữ tợn giống như muốn nhắm người m&agrave; phệ.<br />\r\n<br />\r\n&quot;Giang Ngư, uổng ta như thế t&iacute;n nhiệm ngươi, ngươi v&acirc;̣y mà trung gian kiếm lời t&uacute;i tiền ri&ecirc;ng, th&acirc;m hụt phủ khố, ta, ta giết ngươi!&quot;<br />\r\n<br />\r\nĐang khi n&oacute;i chuyện, Th&aacute;i Trọng đ&uacute;ng l&agrave; một thanh r&uacute;t ra b&ecirc;n cạnh bổ kho&aacute;i b&ecirc;n h&ocirc;ng đao, hung hăng chặt xuống dưới. M&agrave; đ&uacute;ng l&uacute;c n&agrave;y, một thanh kiếm từ kh&iacute;a cạnh đột nhi&ecirc;n xuất hiện, chặn c&aacute;i n&agrave;y tất s&aacute;t một k&iacute;ch.<br />\r\n<br />\r\n&quot;Huyện thừa đại nh&acirc;n, đ&acirc;y l&agrave; chuẩn bị giết người diệt khẩu a?&quot;<br />\r\n<br />\r\n&quot;Giết người diệt khẩu? Huyện lệnh đại nh&acirc;n, lời n&agrave;y từ đ&acirc;u m&agrave; đến? Cơm c&oacute; thể ăn bậy, lời n&oacute;i nhưng kh&ocirc;ng thể n&oacute;i lung tung, bản quan l&agrave;m được đang ngồi thẳng, bọn hắn d&ugrave;ng b&ugrave;n c&aacute;t thay thế lương thực c&ugrave;ng bản quan c&oacute; li&ecirc;n can g&igrave;? Bản quan hiện tại cũng l&agrave; đau l&ograve;ng nhức &oacute;c a!&quot;<br />\r\n<br />\r\n&quot;Th&aacute;i huyện thừa, bản quan tiền nhiệm trước đ&oacute; trong huyện sự vụ đều l&agrave; ngươi tại xử l&yacute;, sao c&oacute; thể n&oacute;i kh&ocirc;ng hề c&oacute; một ch&uacute;t quan hệ đ&acirc;u. Huống chi b&acirc;y giờ phủ khố trống rỗng, những n&agrave;y b&aacute;o c&aacute;o l&aacute;o khoản, theo thứ tự h&agrave;ng nh&aacute;i, đủ loại tội &aacute;c, kh&ocirc;ng chỉ c&oacute; bọn hắn c&oacute; tội, huyện thừa đại nh&acirc;n ngươi cũng kh&oacute; từ tội lỗi!&quot;<br />\r\n<br />\r\nHừ lạnh một tiếng, Thẩm Ngọc bỗng nhi&ecirc;n vung l&ecirc;n ống tay &aacute;o, kh&ocirc;ng ch&uacute;t n&agrave;o cho nửa phần mặt mũi &quot;Chuyện n&agrave;y bản quan sẽ bẩm b&aacute;o quận trưởng, nhưng ở n&agrave;y trước đ&oacute;, c&ograve;n xin huyện thừa đại nh&acirc;n ngươi cởi xuống c&aacute;i n&agrave;y th&acirc;n quan phục!<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave;? Ngươi!&quot; &Aacute;nh mắt khẽ h&iacute;p một c&aacute;i, một v&ograve;ng kh&iacute; tức nguy hiểm từ Th&aacute;i Trọng tr&ecirc;n th&acirc;n ph&aacute;t ra, băng l&atilde;nh thanh &acirc;m sau đ&oacute; vang l&ecirc;n &quot;Huyện lệnh đại nh&acirc;n kh&ocirc;ng cảm thấy tự m&igrave;nh l&agrave;m qua a?&quot;<br />\r\n<br />\r\n&quot;L&agrave;m sao? Huyện thừa đại nh&acirc;n c&oacute; &yacute; kiến? Th&aacute;i huyện thừa, ngươi muốn biết một việc, mặc kệ ch&acirc;n tướng như thế n&agrave;o, ngươi thiếu gi&aacute;m s&aacute;t chi tội đều l&agrave; trốn kh&ocirc;ng tho&aacute;t. Chỉ l&agrave; để ngươi cởi xuống quan phục m&agrave; th&ocirc;i, lại chưa từng truy n&atilde; ngươi v&agrave;o t&ugrave;, Th&aacute;i huyện thừa cũng đừng l&agrave;m cho bản quan kh&oacute; l&agrave;m!&quot;<br />\r\n<br />\r\n&quot;Tốt, tốt rất!&quot; Lạnh l&ugrave;ng nh&igrave;n Thẩm Ngọc một ch&uacute;t, Th&aacute;i Trọng trong l&ograve;ng minh bạch, c&aacute;i n&agrave;y huyện lệnh nhỏ l&agrave; đem tiền nhiệm về sau c&acirc;y đuốc thứ nhất đốt tới m&igrave;nh tr&ecirc;n th&acirc;n, liền sợ ngươi c&aacute;i n&agrave;y tiểu th&acirc;n bản đảm đương kh&ocirc;ng nổi!<br />\r\n<br />\r\nKh&ocirc;ng tiếp tục để &yacute; tới Thẩm Ngọc, Th&aacute;i Trọng ngược lại nh&igrave;n về ph&iacute;a bị bắt nhanh giam giữ kho lại nh&oacute;m, băng l&atilde;nh tr&ecirc;n mặt đ&atilde; mang tới mấy phần s&aacute;t cơ &quot;C&aacute;c ngươi những người n&agrave;y d&aacute;m can đảm ở phủ khố b&ecirc;n trong động tay ch&acirc;n, c&aacute;c ngươi nhưng biết đ&acirc;y l&agrave; tội g&igrave;?&quot;<br />\r\n<br />\r\n&quot;Huyện lệnh đại nh&acirc;n sẽ kh&ocirc;ng bỏ qua c&aacute;c ngươi, bản quan cũng sẽ kh&ocirc;ng bỏ qua c&aacute;c ngươi! C&aacute;c ngươi tốt nhất nghĩ r&otilde; r&agrave;ng, coi như kh&ocirc;ng v&igrave; m&igrave;nh nghĩ, cũng phải v&igrave; người nh&agrave; của m&igrave;nh, v&igrave; m&igrave;nh phụ mẫu vợ con ngẫm lại! N&ecirc;n như thế n&agrave;o l&agrave;m, kh&ocirc;ng cần ta dạy cho ngươi nh&oacute;m đi?&quot;<br />\r\n<br />\r\n&quot;Th&aacute;i huyện thừa, ngươi kh&ocirc;ng cảm thấy lời của ngươi qua a?&quot;<br />\r\n<br />\r\n&quot;Huyện lệnh đại nh&acirc;n lời n&agrave;y c&aacute;i g&igrave; &yacute; tứ, hạ quan kh&ocirc;ng hiểu!&quot; Ngẩng đầu, Th&aacute;i Trọng nhẹ nh&agrave;ng cười một tiếng, lạnh nhạt n&oacute;i &quot;Hạ quan chỉ l&agrave; tại n&oacute;i cho bọn hắn một sự thật m&agrave; th&ocirc;i!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;A!&quot; B&ecirc;n n&agrave;y Th&aacute;i Trọng vừa mới n&oacute;i xong hạ kh&ocirc;ng bao l&acirc;u, đột nhi&ecirc;n Giang Ngư bỗng nhi&ecirc;n tr&aacute;nh tho&aacute;t bổ kho&aacute;i truy n&atilde;, một chưởng hướng Thẩm Ngọc b&ecirc;n n&agrave;y đ&aacute;nh tới. Kh&ocirc;ng kịp phản ứng ph&iacute;a dưới, Thẩm Ngọc theo bản năng liền c&ugrave;ng hắn chạm nhau một chưởng, cả người từ từ lui về sau mấy bước.<br />\r\n<br />\r\nM&agrave; Giang Ngư b&ecirc;n n&agrave;y c&agrave;ng l&agrave; ngo&agrave;i &yacute; muốn, kh&ocirc;ng chỉ c&oacute; liền l&ugrave;i lại ba bước, thậm ch&iacute; c&oacute; một sợi m&aacute;u tươi thuận kh&oacute;e miệng chảy xuống tới.<br />\r\n<br />\r\n&quot;Bảo hộ đại nh&acirc;n!&quot; C&aacute;i n&agrave;y thời điểm, bổ đầu Chu Nguy&ecirc;n cũng kịp phản ứng, bay l&ecirc;n một cước đem Giang Ngư đ&aacute; bay, mấy c&aacute;i bổ kho&aacute;i nhanh ch&oacute;ng r&uacute;t ra bội đao, đem Giang Ngư cho tr&ugrave;ng điệp v&acirc;y quanh.<br />\r\n<br />\r\n&quot;Ngươi biết v&otilde; c&ocirc;ng? Ngươi vậy m&agrave; lại v&otilde; c&ocirc;ng?&quot; Trước mắt c&aacute;i n&agrave;y một m&agrave;n, nhất qua kinh ngạc kh&ocirc;ng ai c&oacute; thể hơn huyện thừa Th&aacute;i Trọng, hắn thực sự kh&ocirc;ng nghĩ tới c&aacute;i n&agrave;y huyện lệnh vậy m&agrave; lại v&otilde; c&ocirc;ng, m&agrave; lại tựa hồ c&ocirc;ng phu c&ograve;n kh&ocirc;ng thấp.<br />\r\n<br />\r\nMuốn biết Giang Ngư th&acirc;n thủ đ&atilde; coi như l&agrave; hảo thủ , b&igrave;nh thường s&aacute;u bảy cường tr&aacute;ng đại h&aacute;n thế nhưng l&agrave; kh&ocirc;ng tới gần được, nhưng vậy m&agrave; tại huyện lệnh nhỏ thủ hạ ăn phải c&aacute;i lỗ vốn. Nếu kh&ocirc;ng phải Giang Ngư h&ocirc;m nay đột nhi&ecirc;n xuất thủ, bức ra v&otilde; c&ocirc;ng của đối phương, vậy bọn hắn c&ograve;n muốn bị c&aacute;i n&agrave;y huyện lệnh nhỏ giấu bao l&acirc;u?<br />\r\n<br />\r\nTrong nh&aacute;y mắt, tại Th&aacute;i Trọng trong l&ograve;ng nhấc l&ecirc;n thao thi&ecirc;n cự l&atilde;ng. Nguy&ecirc;n bản c&oacute; c&ocirc;ng phu trong người huyện lệnh lại muốn giả l&agrave;m ra một bộ tay tr&oacute;i g&agrave; kh&ocirc;ng chặt d&aacute;ng vẻ, c&ograve;n bị m&igrave;nh t&ecirc;n ph&ecirc;́ v&acirc;̣t kia chất tử đ&aacute;nh th&agrave;nh như thế, c&aacute;i n&agrave;y r&otilde; r&agrave;ng l&agrave; tại giấu dốt!<br />\r\n<br />\r\nChẳng lẽ ng&agrave;y đ&oacute; ch&aacute;u m&igrave;nh sở dĩ sẽ đ&aacute;nh hắn, l&agrave; c&aacute;i n&agrave;y tiểu vương b&aacute;t đản đặt ra bẫy, ch&iacute;nh l&agrave; v&igrave; dẫn bọn hắn v&agrave;o cuộc?<br />\r\n<br />\r\nMấu chốt l&agrave;, đối phương vừa tiền nhiệm tựa hồ liền đ&atilde; nhận ra c&aacute;i g&igrave;, b&acirc;y giờ c&agrave;ng l&agrave; trực tiếp giết tới c&aacute;i n&agrave;y phủ khố chi địa, lại ti&ecirc;́p tục như th&ecirc;́, bọn hắn coi như đều nguy hiểm.<br />\r\n<br />\r\nHai tay nắm chặt tại ống tay &aacute;o ở giữa, Th&aacute;i Trọng trong mắt thần sắc kh&ocirc;ng chừng, len l&eacute;n nh&igrave;n Thẩm Ngọc một ch&uacute;t.<br />\r\n<br />\r\nNgười tuổi trẻ b&acirc;y giờ, thật sự c&oacute; đ&aacute;ng sợ như vậy a?</p>', '2021-12-01 01:32:08', 4, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(286, 74, 'Nên Uống Thuốc', '5', '<p>&quot;Đại nh&acirc;n, ng&agrave;i kh&ocirc;ng c&oacute; sao chứ!&quot;<br />\r\n<br />\r\nMột cước đem Giang Ngư đ&aacute; bay về sau, bổ đầu Chu Nguy&ecirc;n vội v&agrave;ng đi v&agrave;o Thẩm Ngọc b&ecirc;n cạnh, sợ Thẩm Ngọc nhận tổn thương g&igrave;. Huyện lệnh nhưng lại tại b&ecirc;n cạnh m&igrave;nh đ&acirc;u, c&aacute;i n&agrave;y nếu l&agrave; vạn nhất xảy ra chuyện g&igrave;, l&agrave;m bổ đầu hắn thế nhưng l&agrave; kh&oacute; từ tội lỗi.<br />\r\n<br />\r\nBất qu&aacute; để hắn c&oacute; ch&uacute;t ngo&agrave;i &yacute; muốn ch&iacute;nh l&agrave;, c&aacute;i n&agrave;y nh&igrave;n yếu kh&ocirc;ng ra gi&oacute; huyện lệnh đại nh&acirc;n đ&uacute;ng l&agrave; kh&ocirc;ng hư hao ch&uacute;t n&agrave;o, đục tr&ecirc;n th&acirc;n hạ khỏi phải n&oacute;i l&agrave; đả thương, ngay cả c&acirc;u chửi thề cũng kh&ocirc;ng c&oacute; thở. C&aacute;i n&agrave;y thực lực, sợ l&agrave; so với m&igrave;nh cũng kh&ocirc;ng thua k&eacute;m bao nhi&ecirc;u đi.<br />\r\n<br />\r\nKia ng&agrave;y đ&oacute; v&igrave; sao muốn tại c&ocirc;ng tử nh&agrave; họ Th&aacute;i trước mặt kh&ocirc;ng chịu được như thế, bị người b&ecirc;n đường đ&aacute;nh tơi bời, để nhiều như vậy b&aacute;ch t&iacute;nh chế giễu?<br />\r\n<br />\r\nC&aacute;i n&agrave;y trong nh&aacute;y mắt, Chu Nguy&ecirc;n cũng l&agrave; nghĩ đến rất nhiều, c&aacute;i tr&aacute;n kh&ocirc;ng khỏi rịn ra v&agrave;i tia mồ h&ocirc;i lạnh, bất qu&aacute; mặt ngo&agrave;i lại kh&ocirc;ng ch&uacute;t n&agrave;o biểu hiện ra ngo&agrave;i. Tốt xấu hắn cũng đ&atilde; gặp s&oacute;ng gi&oacute;, tự nhi&ecirc;n biết c&aacute;i g&igrave; gọi l&agrave; kh&oacute; được hồ đồ!<br />\r\n<br />\r\nM&igrave;nh chỉ l&agrave; c&aacute;i bổ đầu m&agrave; th&ocirc;i, chỉ cần y&ecirc;n lặng l&agrave;m tốt ch&iacute;nh m&igrave;nh sự t&igrave;nh l&agrave; được rồi. Về phần ph&iacute;a tr&ecirc;n c&aacute;c đại nh&acirc;n đ&aacute;nh như thế n&agrave;o, kia đều kh&ocirc;ng c&oacute; quan hệ g&igrave; với m&igrave;nh. Hắn liền bảo vệ tốt m&igrave;nh một mẫu ba phần đất, tốt nhất ai cũng kh&ocirc;ng đắc tội.<br />\r\n<br />\r\n&quot;Y&ecirc;n t&acirc;m, bản quan kh&ocirc;ng ngại!&quot; X&ocirc;ng Chu Nguy&ecirc;n kho&aacute;t tay &aacute;o, Thẩm Ngọc ngay sau đ&oacute; nh&igrave;n về ph&iacute;a Giang Ngư phương hướng &quot;Giang Ngư, th&acirc;m hụt phủ khố can hệ trọng đại, tr&aacute;ch nhiệm n&agrave;y ch&iacute;nh c&aacute;c ngươi nhưng c&otilde;ng kh&ocirc;ng nổi!&quot;<br />\r\n<br />\r\n&quot;Bản quan biết mấy người bọn ngươi chẳng qua l&agrave; đồng l&otilde;a m&agrave; th&ocirc;i, cũng kh&ocirc;ng phải l&agrave; thủ phạm ch&iacute;nh! Chỉ cần c&aacute;c ngươi c&oacute; thể n&oacute;i ra ai l&agrave; chủ sử sau m&agrave;n, bản quan cam đoan tận lực h&ograve;a giải, c&oacute; thể bảo đảm c&aacute;c ngươi một mạng, m&agrave; lại tuyệt sẽ kh&ocirc;ng li&ecirc;n luỵ đến nh&agrave; c&aacute;c ngươi tr&ecirc;n th&acirc;n người!&quot;<br />\r\n<br />\r\n&quot;Đại nh&acirc;n, kh&ocirc;ng cần c&ugrave;ng bọn hắn n&oacute;i nhảm!&quot; C&aacute;i n&agrave;y thời điểm, Chu Nguy&ecirc;n gh&eacute; v&agrave;o Thẩm Khang b&ecirc;n tai nhẹ n&oacute;i &quot;Đại nh&acirc;n y&ecirc;n t&acirc;m, chỉ cần l&agrave; tiến nh&agrave; ngục b&ecirc;n trong, ch&iacute;nh l&agrave; thiết nh&acirc;n ti chức cũng c&oacute; biện ph&aacute;p để hắn mở miệng!&quot;<br />\r\n<br />\r\n&quot;Giang Ngư, ngươi thật to gan, d&aacute;m &aacute;m s&aacute;t mệnh quan triều đ&igrave;nh!&quot; L&uacute;c n&agrave;y Th&aacute;i Trọng, tr&ecirc;n th&acirc;n đ&atilde; sớm bị mồ h&ocirc;i lạnh ướt nhẹp, bất qu&aacute; lại cưỡng &eacute;p để cho m&igrave;nh tỉnh t&aacute;o xuống tới. Sau đ&oacute; nh&igrave;n về ph&iacute;a Giang Ngư đ&aacute;m người phương hướng, trong mắt lộ ra một tia s&aacute;t cơ.<br />\r\n<br />\r\nVừa dứt lời, Th&aacute;i Trọng liền qu&aacute;t l&ecirc;n một tiếng lớn, tại quơ lấy đao x&ocirc;ng tới. Kh&ocirc;ng biết c&ograve;n tưởng rằng vị n&agrave;y Th&aacute;i huyện thừa cỡ n&agrave;o anh dũng nhiệt huyết đ&acirc;u. Kết quả người c&ograve;n kh&ocirc;ng c&oacute; x&ocirc;ng đi l&ecirc;n, liền bị tay mắt lanh lẹ Thẩm Ngọc một thanh cho k&eacute;o lại.<br />\r\n<br />\r\nTi&ecirc;̉u tử, dưới m&iacute; mắt ngươi c&ograve;n d&aacute;m chơi t&acirc;m nh&atilde;n, coi l&agrave; ta l&agrave; ăn cơm kh&ocirc; a, đ&atilde; sớm đề ph&ograve;ng ngươi đ&acirc;y!<br />\r\n<br />\r\nĐ&aacute;ng tiếc m&igrave;nh trong tay kh&ocirc;ng c&oacute; bằng chứng, kh&ocirc;ng phải trực tiếp đem hắn cầm xuống h&aacute; kh&ocirc;ng tới c&agrave;ng tốt hơn!<br />\r\n<br />\r\nBị Thẩm Ngọc k&eacute;o lại, Th&aacute;i Trọng lại kh&ocirc;ng ch&uacute;t n&agrave;o thu lại, ngược lại lớn tiếng h&ocirc; &quot;Giang Ngư, ngươi th&acirc;m hụt phủ khố, sau đ&oacute; lại &aacute;m s&aacute;t mệnh quan triều đ&igrave;nh, cọc cọc vật n&agrave;o cũng l&agrave; tội chết, m&agrave; lại tuyệt đối sẽ li&ecirc;n luỵ người nh&agrave;. Đ&acirc;y l&agrave; ngươi cơ hội cuối c&ugrave;ng, ngươi chớ c&oacute; sai l&acirc;̀m a!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Th&aacute;i huyện thừa, lời của ngươi nhiều, người tới, đem huyện thừa đại nh&acirc;n l&ocirc;i đi!&quot;<br />\r\n<br />\r\n&quot;Đại nh&acirc;n, ti chức bất qu&aacute; l&agrave; tại thuyết phục Giang Ngư quay đầu l&agrave; bờ m&agrave; th&ocirc;i, lại c&oacute; g&igrave; sai chi c&oacute;?&quot; Một b&ecirc;n tr&aacute;nh tho&aacute;t b&ecirc;n cạnh tới l&ocirc;i k&eacute;o m&igrave;nh bổ kho&aacute;i, Th&aacute;i Trọng c&ograve;n một b&ecirc;n tự biện, tiện thể lấy lại x&ocirc;ng Giang Ngư b&ecirc;n kia lớn tiếng h&ocirc; một c&acirc;u.<br />\r\n<br />\r\n&quot;Giang Ngư!&quot;<br />\r\n<br />\r\n&quot;A!&quot; Phảng phất nhận lấy c&aacute;i g&igrave; k&iacute;ch th&iacute;ch b&igrave;nh thường, bị mấy c&aacute;i bổ kho&aacute;i v&acirc;y khốn Giang Ngư giống như nổi đi&ecirc;n mạnh mẽ đ&acirc;m tới, cuối c&ugrave;ng tr&ugrave;ng điệp nh&igrave;n Th&aacute;i Trọng b&ecirc;n n&agrave;y một ch&uacute;t, sau đ&oacute; lại trực tiếp mở ra ngực canh, phảng phất chủ động đưa đến bổ kho&aacute;i đao hạ.<br />\r\n<br />\r\nLưỡi đao trực tiếp kh&ocirc;ng c&oacute; tại Giang Ngư giữa ngực b&ecirc;n trong, chỉ một tho&aacute;ng m&aacute;u tươi bốn ph&iacute;a, mặt đất nh&aacute;y mắt liền bị nhiễm l&ecirc;n đỏ thắm chi sắc.<br />\r\n<br />\r\n&quot;A, a!&quot; Trừng tr&ograve;ng mắt, Giang Ngư tựa hồ muốn n&oacute;i cái g&igrave;, nh&igrave;n ch&ograve;ng chọc v&agrave;o Th&aacute;i Trọng b&ecirc;n n&agrave;y. Thẳng đến Th&aacute;i Trọng hướng hắn lặng lẽ nhẹ gật đầu về sau, Giang Ngư l&uacute;c n&agrave;y mới nhắm mắt lại. V&ugrave;ng vẫy một l&aacute;t, liền lại kh&ocirc;ng c&oacute; sinh tức.<br />\r\n<br />\r\n&quot;Huyện thừa đại nh&acirc;n, tốt thủ đoạn!&quot; Thẩm Ngọc đương nhi&ecirc;n biết, khả năng Giang Ngư ch&iacute;nh l&agrave; Th&aacute;i Trọng một tuyến, tất cả mệnh lệnh cũng đều l&agrave; Th&aacute;i Trọng đơn độc hướng Giang Ngư truyền đạt.<br />\r\n<br />\r\nVề phần c&ograve;n lại những n&agrave;y kho lại nh&oacute;m, d&ugrave; l&agrave; biết Th&aacute;i Trọng c&ugrave;ng phủ khố th&acirc;m hụt sự t&igrave;nh c&oacute; quan hệ, trong tay cũng cũng tuyệt đối kh&ocirc;ng c&oacute; chứng cứ. Cho n&ecirc;n Giang Ngư một kh&ocirc;ng, nếu l&agrave; kh&ocirc;ng c&oacute; c&aacute;i kh&aacute;c chứng cớ x&aacute;c thực, kia Th&aacute;i Trọng nhiều lắm l&agrave; ch&iacute;nh l&agrave; thiếu gi&aacute;m s&aacute;t chi tội!<br />\r\n<br />\r\nT&ecirc;n vương b&aacute;t đản n&agrave;y, thủ đoạn thật đ&uacute;ng l&agrave; hung &aacute;c! Được rồi, c&ograve;n nhiều thời gian, trước lột hắn quan phục, để hắn kh&ocirc;ng c&oacute; ỷ v&agrave;o, c&ograve;n lại ta chậm r&atilde;i chơi!<br />\r\n<br />\r\n&quot;Hệ thống, đ&aacute;nh dấu!&quot;<br />\r\n<br />\r\n&quot;Đ&aacute;nh dấu th&agrave;nh c&ocirc;ng, thu hoạch được mười năm nội lực!&quot;<br />\r\n<br />\r\nHệ thống thanh &acirc;m nhắc nhở vừa dứt hạ, mơ hồ ở giữa, Thẩm Ngọc phảng phất thấy được một v&ograve;ng quang mang tr&agrave;n v&agrave;o b&ecirc;n trong th&acirc;n thể của m&igrave;nh, trong nh&aacute;y mắt, Thẩm Ngọc thể nội phảng phất s&ocirc;i tr&agrave;o.<br />\r\n<br />\r\nGiờ khắc n&agrave;y, Thẩm Ngọc cảm gi&aacute;c m&igrave;nh phảng phất tại đồng ruộng ở giữa tĩnh tu nội c&ocirc;ng, cần c&agrave;y kh&ocirc;ng ngừng. Năm th&aacute;ng giao thế, xu&acirc;n thu thay đổi, mười năm khổ tu chớp mắt m&agrave; qua.<br />\r\n<br />\r\nNguy&ecirc;n bản trong đan điền từng tia từng sợi ch&acirc;n kh&iacute; hoa v&igrave; r&oacute;c r&aacute;ch nước chảy, từ đan điền l&ecirc;n, tại kinh mạch ở giữa tu&ocirc;n hướng to&agrave;n th&acirc;n. Trong l&uacute;c nhất thời, thể nội rất nhiều khiếu huyệt đ&uacute;ng l&agrave; bị từng c&aacute;i x&ocirc;ng mở!<br />\r\n<br />\r\n&quot;H&ocirc;! Mười năm c&ocirc;ng lực, nhị lưu v&otilde; giả!&quot; Nhẹ nh&agrave;ng thở hắt ra, Thẩm Ngọc chậm r&atilde;i mở mắt. Vừa vặn kia trong nh&aacute;y mắt, hắn cảm gi&aacute;c m&igrave;nh tựa như luyện c&ocirc;ng mười năm, nhưng k&igrave; thực lại chỉ mới qua trong chốc l&aacute;t . Bất qu&aacute;, kia mười năm c&ocirc;ng lực lại l&agrave; thực sự.<br />\r\n<br />\r\nBằng v&agrave;o mười năm c&ocirc;ng lực, hắn c&agrave;ng l&agrave; nhất cử trở th&agrave;nh nhị lưu cao thủ, m&agrave; lại l&agrave; nhị lưu cao thủ b&ecirc;n trong đỉnh phong. Đ&aacute;ng tiếc To&agrave;n Ch&acirc;n t&acirc;m ph&aacute;p cũng kh&ocirc;ng phải l&agrave; loại kia tục xưng c&ocirc;ng ph&aacute;p, giảng cứu ch&iacute;nh l&agrave; hậu t&iacute;ch bạc ph&aacute;t, tiến h&agrave;nh theo chất lượng.<br />\r\n<br />\r\nL&agrave; lấy hắn hiện tại nội tức k&eacute;o d&agrave;i k&eacute;o d&agrave;i, hơn xa b&igrave;nh thường nội c&ocirc;ng. Chỉ bất qu&aacute; giai đoạn trước c&acirc;̀n t&iacute;ch lũy, cho n&ecirc;n tiến độ ưu thế cũng kh&ocirc;ng t&iacute;nh lớn. Nhưng c&agrave;ng về sau, hậu t&iacute;ch bạc ph&aacute;t ph&iacute;a dưới, th&agrave;nh tựu đinh sẽ c&agrave;ng ng&agrave;y c&agrave;ng cao.<br />\r\n<br />\r\nDẫn người về tới huyện nha, Thẩm Ngọc lập tức bắt đầu viết văn thư chuẩn bị b&aacute;o c&aacute;o. Ph&aacute;t sinh chuyện lớn như vậy, hắn được chiếm c&aacute;i ti&ecirc;n cơ sớm b&aacute;o c&aacute;o, tỉnh để người cho nhanh ch&acirc;n đến trước.<br />\r\n<br />\r\n&quot;Đại nh&acirc;n, h&ocirc;m nay nghe n&oacute;i đại nh&acirc;n bị tặc nh&acirc;n đ&aacute;nh l&eacute;n, cũng may đại nh&acirc;n tuyệt kh&ocirc;ng thụ thương, nhưng nghĩ đến đại nh&acirc;n cũng kh&oacute; tr&aacute;nh khỏi thụ một ch&uacute;t kinh h&atilde;i. Đ&acirc;y l&agrave; ta v&igrave; đại nh&acirc;n chế biến an thần ch&eacute;n thuốc, đại nh&acirc;n ng&agrave;i uống l&uacute;c c&ograve;n n&oacute;ng đi!&quot;<br />\r\n<br />\r\nNgay tại Thẩm Ngọc vừa vặn viết xong văn thư thời đi&ecirc;̉m, quản gia liền bưng một b&aacute;t ch&eacute;n thuốc đi đến, tr&ecirc;n mặt c&ograve;n mang theo v&agrave;i phần lấy l&ograve;ng nụ cười.<br />\r\n<br />\r\n&quot;Tốt, ta biết, đặt ở nơi đ&oacute; liền tốt, quản gia c&oacute; l&ograve;ng!&quot; Nhẹ gật đầu, Thẩm Ngọc x&ocirc;ng quản gia kho&aacute;t tay &aacute;o, ra hiệu hắn c&oacute; thể lui xuống.<br />\r\n<br />\r\nThẩm Ngọc m&igrave;nh l&agrave; c&aacute;i thư sinh ngh&egrave;o, trong nh&agrave; tự nhi&ecirc;n l&agrave; kh&ocirc;ng có quản gia. Trước mắt đ&acirc;y l&agrave; trong huyện nha l&atilde;o nh&acirc;n, tại nơi n&agrave;y chờ đợi v&agrave;i chục năm, c&aacute;c đời huyện lệnh cũng kh&ocirc;ng từng xua đuổi hắn.<br />\r\n<br />\r\nMột phương mặt hắn đ&uacute;ng l&agrave; huyện nha l&atilde;o nh&acirc;n, đối B&aacute;ch An huyện sự t&igrave;nh cơ bản r&otilde; như l&ograve;ng b&agrave;n tay, c&oacute; thể thuận tiện huyện lệnh c&agrave;ng nhanh chưởng khống B&aacute;ch An huyện. Mặt kh&aacute;c một phương mặt, cũng l&agrave; hắn tự th&acirc;n c&oacute; bản lĩnh.<br />\r\n<br />\r\nNh&igrave;n một c&aacute;i c&aacute;i n&agrave;y nh&atilde;n lực k&igrave;nh, chuyện mới vừa ph&aacute;t sinh, người ta tiếp lấy liền đến lấy l&ograve;ng, c&aacute;i n&agrave;y c&oacute; thể l&agrave; người b&igrave;nh thường a. Nếu l&agrave; kh&ocirc;ng c&oacute; c&aacute;i n&agrave;y c&oacute; ch&uacute;t t&agrave;i năng, có th&ecirc;̉ tại huyện nha nơi n&agrave;y tiếp tục chờ đợi? Sớm bảo người cho &eacute;p buộc đi!<br />\r\n<br />\r\n&quot;Huyện lệnh đại nh&acirc;n, thuốc n&agrave;y kh&ocirc;ng thể lạnh, đại nh&acirc;n vẫn l&agrave; uống l&uacute;c c&ograve;n n&oacute;ng đi!&quot; Nh&igrave;n thấy Thẩm Ngọc kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o muốn uống thuốc &yacute; tứ, quản gia nhịn kh&ocirc;ng được lại l&agrave; nhẹ giọng nhắc nhở một ch&uacute;t.<br />\r\n<br />\r\nChỉ bất qu&aacute;, c&aacute;i n&agrave;y thời điểm Thẩm Ngọc ngay tại suy nghĩ về sau sự t&igrave;nh, n&agrave;o c&oacute; t&acirc;m t&igrave;nh uống g&igrave; thuốc. B&acirc;y giờ hắn mượn cơ hội đem Th&aacute;i Trọng cho lột xuống đến, cũng đem một đ&aacute;m kho lại đ&aacute;nh v&agrave;o đại lao, liền đợi đến t&igrave;m hiểu nguồn gốc, dẫn ra một đ&aacute;m người lớn tới.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nNh&igrave;n như t&igrave;nh thế tốt đẹp, k&igrave; thực cũng l&agrave; đem người bức cho đến nhất định ph&acirc;n thượng. Tục ngữ n&oacute;i tốt, con thỏ gấp c&ograve;n cắn người đ&acirc;u, huống chi những người n&agrave;y từng c&aacute;i tuyệt kh&ocirc;ng phải loại lương thiện!<br />\r\n<br />\r\nCho n&ecirc;n c&agrave;ng l&agrave; c&aacute;i n&agrave;y thời điểm, liền c&agrave;ng c&acirc;̀n cẩn thận, ph&ograve;ng ngừa bọn hắn ch&oacute; c&ugrave;ng rứt giậu!<br />\r\n<br />\r\nHiện tại Thẩm Ngọc có th&ecirc;̉ biết đến l&agrave;, th&ocirc;ng qua phủ khố b&ecirc;n trong Th&aacute;i Trọng biểu hiện c&oacute; thể x&aacute;c định, hắn tuyệt tham dự trong đ&oacute;. Về phần những người kh&aacute;c đến tột c&ugrave;ng c&oacute; bao nhi&ecirc;u người tham gia lại l&agrave; kh&ocirc;ng được biết, bất qu&aacute; chỉ sợ tuyệt đối kh&ocirc;ng thể thiếu.<br />\r\n<br />\r\nTo&agrave;n bộ B&aacute;ch An huyện c&aacute;c nh&agrave; lẫn nhau ở giữa rắc rối kh&oacute; gỡ, ba nh&agrave; một bang cầm giữ to&agrave;n bộ B&aacute;ch An huyện c&oacute; thể n&oacute;i l&agrave; th&acirc;m căn cố đế, m&agrave; dưới tay m&igrave;nh c&oacute; thể sử dụng người l&aacute;c đ&aacute;c kh&ocirc;ng c&oacute; mấy. Đừng nh&igrave;n bổ đầu Chu Nguy&ecirc;n tựa hồ rất nghe lời, nhưng Thẩm Ngọc thật kh&ocirc;ng d&aacute;m 100% t&iacute;n nhiệm, cuối c&ugrave;ng c&ograve;n được dựa v&agrave;o ch&iacute;nh m&igrave;nh mới được.<br />\r\n<br />\r\n&quot;Đại nh&acirc;n, uống thuốc đi!&quot; C&aacute;i n&agrave;y thời điểm, thấy Thẩm Ngọc chậm chạp kh&ocirc;ng c&oacute; động t&aacute;c, b&ecirc;n cạnh l&atilde;o quản gia lại nhịn kh&ocirc;ng được nhắc nhở một c&acirc;u.<br />\r\n<br />\r\n&quot;Ngươi l&agrave;m sao c&ograve;n tại nơi n&agrave;y?&quot; Ngẩng đầu, kinh ngạc nh&igrave;n tho&aacute;ng qua quản gia, sau đ&oacute; Thẩm Ngọc hơi hơi kh&ocirc;ng ki&ecirc;n nhẫn n&oacute;i &quot;Thuốc ta chờ một l&uacute;c uống, ngươi đi xuống trước đi!&quot;<br />\r\n<br />\r\nMặc d&ugrave; ngo&agrave;i miệng n&oacute;i như vậy, nhưng Thẩm Ngọc nhưng như cũ kh&ocirc;ng c&oacute; một ch&uacute;t muốn uống thuốc &yacute; tứ. N&oacute;i đ&ugrave;a, hắn h&ocirc;m nay lại kh&ocirc;ng chịu thiệt, một ch&uacute;t sự t&igrave;nh cũng kh&ocirc;ng c&oacute;, uống g&igrave; thuốc. C&aacute;i n&agrave;y khổ bẹp, kh&ocirc;ng phải m&igrave;nh t&igrave;m tội thụ a.<br />\r\n<br />\r\nM&agrave; c&aacute;i n&agrave;y thời điểm Thẩm Ngọc lại lần nữa ngẩng đầu, ph&aacute;t hiện quản gia vẫn l&agrave; tại nơi đ&oacute;, căn bản kh&ocirc;ng c&oacute; x&ecirc; dịch th&acirc;n thể &yacute; tứ. Chỉ l&agrave; lẳng lặng nh&igrave;n hắn, thậm ch&iacute; tựa hồ lộ ra một tia kh&oacute; m&agrave; h&igrave;nh dung nụ cười.<br />\r\n<br />\r\n&quot;Huyện lệnh đại nh&acirc;n, thuốc được uống l&uacute;c c&ograve;n n&oacute;ng mới được, c&aacute;i n&agrave;y thế nhưng l&agrave; ta tự tay v&igrave; đại nh&acirc;n chế biến!&quot; Sau đ&oacute;, quản gia cầm ch&eacute;n thuốc cầm tại trong tay, quan t&acirc;m hướng Thẩm Ngọc đi tới.<br />\r\n<br />\r\n&quot;Ta n&oacute;i lui ra!&quot; Như thế t&igrave;nh huống, khiến Thẩm Ngọc kh&ocirc;ng khỏi hơi nh&iacute;u cau m&agrave;y, liền &acirc;m thanh cũng nghi&ecirc;m khắc mấy phần. Nhưng quản gia lại l&agrave; mảy may kh&ocirc;ng để &yacute;, vẫn như cũ bưng ch&eacute;n thuốc từng bước một hướng hắn đi tới.<br />\r\n<br />\r\n&quot;Huyện lệnh đại nh&acirc;n, ng&agrave;i n&ecirc;n uống thuốc!&quot;</p>', '2021-12-01 01:32:39', 5, 1),
(287, 74, 'Tị độc Ngọc Châu', '6', '<p>&quot;Ngươi l&agrave; ai?&quot;<br />\r\n<br />\r\nChậm r&atilde;i r&uacute;t ra b&ecirc;n cạnh trường kiếm, Thẩm Ngọc lạnh l&ugrave;ng nh&igrave;n chằm chằm từng bước một đến gần quản gia, tr&ecirc;n mặt biểu lộ viết đầy đề ph&ograve;ng.<br />\r\n<br />\r\n&quot;Đại nh&acirc;n, kh&ocirc;ng cần v&ugrave;ng vẫy!&quot; Chậm r&atilde;i đi tới, quản gia kh&ocirc;ng nh&uacute;c nh&iacute;ch ch&uacute;t n&agrave;o. C&oacute; lẽ tại hắn xem ra, trước mắt Thẩm Ngọc bất qu&aacute; l&agrave; vật trong b&agrave;n tay, l&uacute;c n&agrave;y mới ho&agrave;n to&agrave;n kh&ocirc;ng ngại bại lộ ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\n&quot;Ta biết đại nh&acirc;n ng&agrave;i c&oacute; c&ocirc;ng phu mang theo, c&oacute; thể c&ugrave;ng Giang Ngư đối chưởng thậm ch&iacute; có th&ecirc;̉ k&iacute;ch thương hắn. Đ&aacute;ng tiếc a, ta từng gặp Giang Ngư, bất qu&aacute; l&agrave; vừa vặn tu ra nội tức, mới v&agrave;o lưu v&otilde; giả m&agrave; th&ocirc;i. Tại hạ bất t&agrave;i, tự học ra nội tức đến nay khổ luyện gần ba mươi năm, b&acirc;y giờ đ&atilde; l&agrave; nhị lưu v&otilde; giả!&quot;<br />\r\n<br />\r\nN&oacute;i đến nơi n&agrave;y, quản gia kh&ocirc;ng khỏi lộ ra mấy phần ch&iacute; đắc &yacute; đ&acirc;̀y thần sắc, phảng phất đối với m&igrave;nh th&agrave;nh tựu tương đương h&agrave;i l&ograve;ng. Bất qu&aacute; tại Thẩm Ngọc xem ra, cũng liền b&igrave;nh thường m&agrave; th&ocirc;i. Khổ luyện ba mươi năm mới vừa vặn nhập nhị lưu v&otilde; giả, liền c&aacute;i n&agrave;y cũng đ&aacute;ng gi&aacute; khoe khoang?<br />\r\n<br />\r\nBất qu&aacute; ngẫm lại cũng thế, m&igrave;nh đạt được To&agrave;n Ch&acirc;n t&acirc;m ph&aacute;p, kia thế nhưng l&agrave; Đạo gia ch&iacute;nh t&ocirc;ng nội c&ocirc;ng t&acirc;m ph&aacute;p, mặc d&ugrave; t&iacute;nh kh&ocirc;ng được đứng đầu nhất loại kia, nhưng cũng cũng kh&ocirc;ng phải l&agrave; b&igrave;nh thường nội c&ocirc;ng c&oacute; thể so s&aacute;nh được.<br />\r\n<br />\r\n&quot;Đến, đại nh&acirc;n đem thuốc uống đi. Đại nh&acirc;n y&ecirc;n t&acirc;m, ngươi sẽ kh&ocirc;ng lập tức xảy ra chuyện, sẽ chỉ trước xụi lơ tại giường sau đ&oacute; chậm r&atilde;i ốm chết!&quot;<br />\r\n<br />\r\nĐang khi n&oacute;i chuyện, quản gia đ&atilde; đi tới &quot;Đại nh&acirc;n cũng kh&ocirc;ng phải l&agrave; người địa phương, lại l&agrave; th&acirc;n thể suy yếu. Ta B&aacute;ch An huyện chỗ vắng vẻ, ho&agrave;n cảnh cũng kh&ocirc;ng được tốt lắm, đại nh&acirc;n kh&ocirc;ng quen kh&iacute; hậu bị bệnh cũng l&agrave; b&igrave;nh thường!&quot;<br />\r\n<br />\r\n&quot;Ngươi. . . .&quot; Con mắt khẽ h&iacute;p một c&aacute;i, Thẩm Ngọc c&oacute; ch&uacute;t kh&ocirc;ng x&aacute;c định hỏi: &quot;Kia đời trước huyện lệnh, nhưng cũng l&agrave; b&uacute;t t&iacute;ch của ngươi a?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng tệ!&quot; Tựa hồ l&agrave; ăn chắc Thẩm Ngọc, đ&atilde; ho&agrave;n to&agrave;n bại lộ quản gia của m&igrave;nh ho&agrave;n to&agrave;n kh&ocirc;ng c&ograve;n che giấu, trực tiếp điểm đầu thừa nhận.<br />\r\n<br />\r\n&quot;Đời trước huyện lệnh đại nh&acirc;n vừa vặn tiền nhiệm liền kh&ocirc;ng th&agrave;nh thật, c&oacute; &iacute;t người sợ, kh&ocirc;ng nguyện &yacute; hắn tiếp tục lưu lại, cho n&ecirc;n hắn liền bệnh qua đời! Đại nh&acirc;n, hiện b&acirc;y giờ những người kia cũng kh&ocirc;ng hi vọng đại nh&acirc;n ng&agrave;i lưu lại, cho n&ecirc;n ta mới đến đưa đại nh&acirc;n ng&agrave;i đoạn đường!&quot;<br />\r\n<br />\r\n&quot;Ai, đời n&agrave;y sự t&igrave;nh ph&aacute;t triển ch&iacute;nh l&agrave; kỳ diệu như vậy!&quot; Nh&igrave;n về ph&iacute;a Thẩm Ngọc phương hướng, quản gia kh&ocirc;ng khỏi nhẹ giọng cảm th&aacute;n &quot;Muốn ta năm đ&oacute; tung ho&agrave;nh lục l&acirc;m bị truy n&atilde; thời điểm đều chưa hề từng nghĩ tới muốn giết quan, b&acirc;y giờ lại ngay cả giết hai c&aacute;i, đ&aacute;ng gi&aacute;!&quot;<br />\r\n<br />\r\n&quot;Lục l&acirc;m? Dưới đĩa đ&egrave;n th&igrave; tối!&quot; Hơi hơi nghĩ, Thẩm Ngọc liền c&oacute; thể nghĩ rõ ràng. Một c&aacute;i bị truy n&atilde; sơn phỉ, b&acirc;y giờ lại thay h&igrave;nh đổi dạng giấu v&agrave;o huyện nha b&ecirc;n trong. C&oacute; thể l&agrave;m được điểm n&agrave;y, thế nhưng l&agrave; kh&ocirc;ng nhiều a.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nAi c&oacute; thể nghĩ đến, một c&aacute;i lục rừng cao tay sẽ trở th&agrave;nh huyện lệnh quản gia, ng&agrave;y b&igrave;nh thường vẫn l&agrave; một bộ trung thực bộ d&aacute;ng. Hoặc l&agrave; n&oacute;i lăn lộn giang hồ, vậy th&igrave; nhất định phải phải c&oacute; diễn kỹ b&agrave;ng th&acirc;n, ph&agrave;m l&agrave; c&oacute; chút diễn kỹ đều kh&ocirc;ng về phần nhanh như vậy lĩnh cơm hộp.<br />\r\n<br />\r\n&quot;Kia c&oacute; thể hay kh&ocirc;ng n&oacute;i cho ta, đến tột c&ugrave;ng l&agrave; ai muốn giết ta, cũng để cho ch&iacute;nh ta minh bạch!&quot; Kiếm giữ tại trong tay, Thẩm Ngọc nội t&acirc;m kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o gợn s&oacute;ng, sắc mặt b&igrave;nh tĩnh nh&igrave;n về ph&iacute;a đối phương.<br />\r\n<br />\r\n&quot;Đại nh&acirc;n, l&agrave; ai muốn giết ngươi ch&iacute;nh ng&agrave;i trong l&ograve;ng kh&ocirc;ng r&otilde; r&agrave;ng a, huyện thừa đại nh&acirc;n n&oacute;i, đại nh&acirc;n ng&agrave;i th&agrave;nh th&agrave;nh thật thật hợp l&yacute; ng&agrave;i huyện lệnh th&igrave; cũng th&ocirc;i đi, v&igrave; sao nhất định phải g&acirc;y chuyện thị phi đ&acirc;u! Huyện thừa đại nh&acirc;n đối ta c&oacute; &acirc;n, &acirc;n t&igrave;nh n&agrave;y ta được b&aacute;o! M&agrave; lại, hắn ra gi&aacute; cũng kh&ocirc;ng thấp a!&quot;<br />\r\n<br />\r\n&quot;Huyện thừa Th&aacute;i Trọng, quả nhi&ecirc;n l&agrave; hắn!&quot; Ch&acirc;n trước m&igrave;nh vừa đem hắn quan phục lột, ch&acirc;n sau hắn liền trực tiếp d&ugrave;ng ra đ&ograve;n s&aacute;t thủ. Nhị lưu cao thủ lại tăng th&ecirc;m quản gia n&agrave;y th&acirc;n phận, chậc chậc, thủ b&uacute;t thật lớn.<br />\r\n<br />\r\nHuống chi trước mắt c&aacute;i n&agrave;y quản gia trong phủ thế nhưng l&agrave; chờ đợi v&agrave;i chục năm, c&aacute;i n&agrave;y chẳng phải l&agrave; n&oacute;i v&agrave;i chục năm b&ecirc;n trong c&aacute;c đời huyện lệnh nhất cử nhất động người ta đều biết, cũng kh&oacute; tr&aacute;ch trước mấy đời huyện lệnh đều bị nắm gắt gao.<br />\r\n<br />\r\nĐ&aacute;ng tiếc a, h&ocirc;m nay con cờ n&agrave;y chỉ sợ cũng muốn n&aacute;t!<br />\r\n<br />\r\n&quot;Xem ra huyện lệnh đại nh&acirc;n cũng kh&ocirc;ng t&iacute;nh trung thực phối hợp, đại nh&acirc;n h&agrave; tất phải như vậy đ&acirc;u?&quot; Nh&igrave;n xem Thẩm Ngọc r&uacute;t kiếm động t&aacute;c, quản gia hừ lạnh một tiếng, khắp khu&ocirc;n mặt l&agrave; vẻ khinh thường &quot;Chỉ cần ngươi ngoan ngo&atilde;n phối hợp ta cam đoan qu&aacute; tr&igrave;nh sẽ kh&ocirc;ng qu&aacute; kh&oacute; chịu, v&igrave; sao nhất định phải nếm ch&uacute;t khổ sở mới được đ&acirc;u!&quot;<br />\r\n<br />\r\n&quot;C&aacute;c ngươi những người đọc s&aacute;ch n&agrave;y a, từng c&aacute;i ch&iacute;nh l&agrave; thiếu đ&aacute;nh. Nhiều đ&aacute;nh hai lần, cũng liền biết phối hợp!&quot;<br />\r\n<br />\r\nĐang khi n&oacute;i chuyện, quản gia đ&atilde; đột nhi&ecirc;n x&ocirc;ng về Thẩm Ngọc. Hắn mặc d&ugrave; đối Thẩm Ngọc điểm n&agrave;y c&ocirc;ng lực khinh thường, nhưng lại một mực đề ph&ograve;ng. Liếm m&aacute;u tr&ecirc;n lưỡi đao l&acirc;u như vậy, hắn tự nhi&ecirc;n biết sư tử vồ thỏ cũng vẫn cần to&agrave;n lực đạo l&yacute;, sẽ kh&ocirc;ng bởi v&igrave; đối thủ nhỏ yếu m&agrave; bu&ocirc;ng lỏng cảnh gi&aacute;c.<br />\r\n<br />\r\nM&agrave; c&ugrave;ng l&uacute;c đ&oacute;, một thanh kiếm &aacute;nh s&aacute;ng đột nhi&ecirc;n chợt s&aacute;ng, phảng phất đ&acirc;m r&aacute;ch hắc &aacute;m một kiếm. M&oacute;n n&agrave;y nhanh như thiểm điện, nhưng lại phảng phất huy ho&agrave;ng kh&iacute; quyển, kh&iacute; đ&oacute;ng sơn h&agrave;, c&oacute; một cỗ để người rất cảm thấy &aacute;p lực đặc biệt &yacute; ch&iacute;.<br />\r\n<br />\r\nChỉ một tho&aacute;ng, liền để hắn t&acirc;m thần v&igrave; đ&oacute; hoảng hốt. Sau lưng nh&aacute;y mắt bị mồ h&ocirc;i lạnh thấm ướt, quản gia kia cấp tốc bắn vọt th&acirc;n thể bỗng nhi&ecirc;n dừng lại, th&acirc;n thể trực tiếp tr&aacute;i với định luật vật l&yacute; b&igrave;nh thường đột nhi&ecirc;n về sau giơ l&ecirc;n.<br />\r\n<br />\r\nMột c&aacute;i lừa lười lăn lăn, hiểm m&agrave; lại hiểm tr&aacute;nh đi bất th&igrave;nh l&igrave;nh một kiếm. M&agrave; chờ hắn lại lần nữa ngẩng đầu, tr&ecirc;n mặt đ&atilde; sớm đổi lại kinh h&atilde;i c&ugrave;ng sợ h&atilde;i thần sắc.<br />\r\n<br />\r\n&quot;V&otilde; c&ocirc;ng của ngươi? L&agrave;m sao c&oacute; thể?&quot; Quản gia con ngươi bỗng nhi&ecirc;n co rụt lại, cũng chỉ c&oacute; ch&iacute;nh hắn minh bạch vừa vặn hung hiểm.<br />\r\n<br />\r\nGiao thủ ngắn ngủi trong nh&aacute;y mắt, hắn phảng phất một ch&uacute;t về tới năm đ&oacute; liếm m&aacute;u tr&ecirc;n lưỡi đao sinh hoạt, kinh lịch lấy sinh tử ch&eacute;m giết. Th&acirc;n thể mỗi một chỗ, đều đang nhắc nhở m&igrave;nh nguy hiểm mau trốn!<br />\r\n<br />\r\nNhất l&agrave; đối phương tr&ecirc;n th&acirc;n vừa vặn đột nhi&ecirc;n chợt hiện đ&aacute;ng sợ kh&iacute; tức, kia r&otilde; r&agrave;ng l&agrave; so với m&igrave;nh còn cường thịnh hơn mấy phần kh&iacute; tức.<br />\r\n<br />\r\nTh&aacute;i Trọng kh&ocirc;ng phải n&oacute;i với hắn đối phương chỉ so với Giang Ngư mạnh nhất tuyến a, c&aacute;i n&agrave;y mẹ n&oacute; l&agrave; mạnh nhất tuyến a, treo l&ecirc;n đ&aacute;nh một đ&aacute;m cũng kh&ocirc;ng th&agrave;nh vấn đề đi. C&aacute;i n&agrave;y cẩu quan c&oacute; hay kh&ocirc;ng điểm nh&atilde;n lực, nhưng hại chết hắn!<br />\r\n<br />\r\n&quot;Dưỡng Ng&ocirc; kiếm ph&aacute;p!&quot; Một kiếm qua đi, Thẩm Ngọc kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o dừng lại, Dưỡng Ng&ocirc; kiếm ph&aacute;p thỏa th&iacute;ch huy vẩy m&agrave; ra, một chi&ecirc;u một thức ở giữa hiển thị r&otilde; huy ho&agrave;ng kh&iacute; quyển, to lớn ch&iacute; cương. Huy ho&agrave;ng đại thế, phảng phất muốn nghiền &eacute;p hết thảy.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; Thẩm Ngọc từ được đến Dưỡng Ng&ocirc; kiếm ph&aacute;p đến nay lần thứ nhất c&ugrave;ng người thực chiến, bất qu&aacute; Thẩm Ngọc lại kh&ocirc;ng ch&uacute;t n&agrave;o cảm gi&aacute;c bất kỳ kh&oacute; chịu n&agrave;o, ngược lại tại kh&ocirc;ng đoạn giao tay b&ecirc;n trong nhanh ch&oacute;ng đem m&ocirc;n n&agrave;y kiếm ph&aacute;p chưởng khống.<br />\r\n<br />\r\nCứ như vậy, thế nhưng l&agrave; khổ đối diện quản gia. Năm đ&oacute; hắn l&agrave; hỗn lục l&acirc;m, bản th&acirc;n cũng kh&ocirc;ng c&oacute; g&igrave; truyền thừa, tu luyện nội c&ocirc;ng c&ugrave;ng ngoại c&ocirc;ng đều l&agrave; tam lưu mặt h&agrave;ng. Danh m&ocirc;n ch&iacute;nh ph&aacute;i c&ugrave;ng t&aacute;n tu ở giữa, kỳ thật ch&ecirc;nh lệch vẫn c&ograve;n tương đối lớn.<br />\r\n<br />\r\nĐối mặt Thẩm Ngọc kiếm ph&aacute;p, quản gia sớm đ&atilde; l&agrave; vướng tr&aacute;i vướng phải, trong chốc l&aacute;t tr&ecirc;n th&acirc;n liền c&oacute; th&ecirc;m mấy đạo kiếm thương. M&aacute;u tươi vẩy xuống, th&acirc;n h&igrave;nh cũng dần dần chống đỡ kh&ocirc;ng nổi. Li&ecirc;n thủ b&ecirc;n tr&ecirc;n động t&aacute;c, đều hơi chậm một ch&uacute;t chậm.<br />\r\n<br />\r\n&quot;Cơ hội tốt!&quot; Đột nhi&ecirc;n, Thẩm Ngọc trong mắt tinh quang l&oacute;e l&ecirc;n, một kiếm đẩy ra quản gia ph&ograve;ng ngự song chưởng, bỗng nhi&ecirc;n hướng hắn ra một kiếm. Một kiếm n&agrave;y, phảng phất bầu trời xẹt qua một đạo lưu tinh, lộng lẫy m&agrave; nhiều m&agrave;u lại lộ ra v&ocirc; tận s&aacute;t cơ.<br />\r\n<br />\r\n&quot;&Aacute;ch, &aacute;ch. . .&quot; Một kiếm ch&iacute;nh giữa yết hầu, m&aacute;u tươi từ chỗ cổ phun ra, quản gia kh&ocirc;ng ngừng vung vẩy lấy hai tay v&ugrave;ng vẫy một l&aacute;t muốn bắt lấy c&aacute;i g&igrave;, cuối c&ugrave;ng lại chỉ c&oacute; thể kh&ocirc;ng cam l&ograve;ng đổ xuống, sau đ&oacute; lại kh&ocirc;ng nửa điểm sinh cơ.<br />\r\n<br />\r\nThấy đối phương triệt để kh&ocirc;ng một tiếng động về sau, Thẩm Ngọc mới cao giọng h&ograve; h&eacute;t n&oacute;i: &quot;Người tới, người tới!&quot;<br />\r\n<br />\r\n&quot;Đại nh&acirc;n, đ&acirc;y, đ&acirc;y l&agrave;. . . . .&quot; Trước mắt c&aacute;i n&agrave;y một m&agrave;n, để nghe tiếng chạy tới mọi người c&oacute; ch&uacute;t mộng, nằm dưới đất kh&ocirc;ng phải quản gia a, đến tột c&ugrave;ng xảy ra chuyện g&igrave; rồi?</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Người n&agrave;y l&agrave; giặc cướp, ẩn n&uacute;p huyện nha v&agrave;i chục năm. Để Chu bổ đầu tra một ch&uacute;t, nếu l&agrave; x&aacute;c nhận kh&ocirc;ng lầm liền lấy đến thị ch&uacute;ng. Đ&uacute;ng, tuyệt đối đừng qu&ecirc;n c&ugrave;ng huyện thừa đại nh&acirc;n n&oacute;i một tiếng!&quot;<br />\r\n<br />\r\n&quot;Giặc cướp? C&aacute;i n&agrave;y. . . . Nặc!&quot;<br />\r\n<br />\r\n&quot;Th&aacute;i Trọng!&quot; Nhẹ nh&agrave;ng g&otilde; b&agrave;n một c&aacute;i, Thẩm Ngọc hừ nhẹ một tiếng. Hắn trong l&ograve;ng tiểu s&aacute;ch vở b&ecirc;n tr&ecirc;n, thế nhưng l&agrave; đem c&aacute;i n&agrave;y danh tự nhớ kỹ!<br />\r\n<br />\r\n&quot;Hệ thống, đ&aacute;nh dấu!&quot;<br />\r\n<br />\r\n&quot;Đ&aacute;nh dấu th&agrave;nh c&ocirc;ng, thu hoạch được tị độc ngọc ch&acirc;u!&quot;<br />\r\n<br />\r\n&quot;Tị độc ngọc ch&acirc;u!&quot; Theo hệ thống nhắc nhở &acirc;m biến mất, một vi&ecirc;n &oacute;ng &aacute;nh s&aacute;ng long lanh m&agrave;u trắng ngọc ch&acirc;u rơi v&agrave;o trong l&ograve;ng b&agrave;n tay, m&agrave; lại rất nhanh một ch&uacute;t tin tức cũng theo đ&oacute; tr&agrave;n v&agrave;o trong đầu.<br />\r\n<br />\r\nC&aacute;i n&agrave;y tị độc ngọc ch&acirc;u c&oacute; nhất định giải độc c&ocirc;ng hiệu m&agrave; lại gặp độc biến sắc, như biến th&agrave;nh m&agrave;u lam, tị độc ngọc ch&acirc;u ho&agrave;n to&agrave;n c&oacute; thể giải hết. Tr&ecirc;n thực tế, thế gian phần lớn độc, tị độc ngọc ch&acirc;u đều c&oacute; thể giải.<br />\r\n<br />\r\nNhưng nếu ngọc ch&acirc;u biến th&agrave;nh m&agrave;u đỏ, th&igrave; chứng minh l&agrave; kịch độc b&ecirc;n trong kịch độc, tị độc ngọc ch&acirc;u chỉ sợ chỉ c&oacute; thể ức chế, m&agrave; kh&ocirc;ng thể ho&agrave;n to&agrave;n giải độc. Gặp được ngọc ch&acirc;u biến đỏ, tốt nhất phương ph&aacute;p ch&iacute;nh l&agrave; tranh thủ thời gian trượt.<br />\r\n<br />\r\n&quot;Đồ tốt!&quot; Đem ngọc ch&acirc;u đeo tại th&acirc;n, Thẩm Ngọc h&agrave;i l&ograve;ng tr&ecirc;n dưới nh&igrave;n một ch&uacute;t.<br />\r\n<br />\r\nKh&ocirc;ng thể kh&ocirc;ng n&oacute;i, hắn hiện tại rất kh&ocirc;ng c&oacute; cảm gi&aacute;c an to&agrave;n, cảm gi&aacute;c được chỗ đều l&agrave; địch nh&acirc;n. Kh&ocirc;ng biết c&aacute;i g&igrave; thời điểm khả năng liền c&oacute; người c&oacute; thể mua được ph&ograve;ng bếp cho hắn hạ điểm thứ g&igrave;, c&oacute; c&aacute;i đồ chơi n&agrave;y, về sau liền rốt cuộc kh&ocirc;ng sợ c&oacute; người cho hắn hạ độc.</p>', '2021-12-01 01:33:22', 4, 1),
(288, 74, 'Nhìn Ngươi Làm Sao Tránh', '7', '<p>&quot;Đại nh&acirc;n, huyện thừa đại nh&acirc;n mẫu th&acirc;n mừng thọ, cho n&ecirc;n cố &yacute; đưa tới thiếp mời, mời đại nh&acirc;n tiến đến dự tiệc!&quot;<br />\r\n<br />\r\nLại qua hai ng&agrave;y, khi Thẩm Ngọc bắt đầu kiểm k&ecirc; phủ khố, tra khoản thời điểm, Th&aacute;i Trọng một phần thiếp mời bị đưa l&ecirc;n, ngược lại để hắn cảm thấy c&oacute; ch&uacute;t ngo&agrave;i &yacute; muốn.<br />\r\n<br />\r\nNếu n&oacute;i hiện tại huyện thừa Th&aacute;i Trọng hận nhất người đều c&oacute; ai, kia trong danh s&aacute;ch khẳng định c&oacute; Thẩm Ngọc một c&aacute;i. Th&aacute;i Trọng quan phục đều bị Thẩm Ngọc cho lột, m&agrave; lại hiện tại điều tra phủ khố, Thẩm Ngọc kh&ocirc;ng e d&egrave; nhằm v&agrave;o lấy Th&aacute;i Trọng.<br />\r\n<br />\r\nNếu n&oacute;i Th&aacute;i Trọng hiện tại hận nhất người đều c&oacute; ai, kia tr&ecirc;n danh s&aacute;ch tuyệt đối c&oacute; Thẩm Ngọc danh tự. Bọn hắn hiện tại quan hệ trong đ&oacute;, coi như kh&ocirc;ng phải thế như nước lửa cũng kh&ocirc;ng x&ecirc; x&iacute;ch g&igrave; nhiều. N&agrave;y lại Th&aacute;i Trọng nuốt hắn t&acirc;m đều c&oacute;, có th&ecirc;̉ hảo t&acirc;m mời hắn đi dự tiệc?<br />\r\n<br />\r\nĐương nhi&ecirc;n, dự tiệc cũng kh&ocirc;ng phải kh&ocirc;ng thể, kh&ocirc;ng quan t&acirc;m giữa bọn hắn quan hệ thế n&agrave;o, người ta đ&atilde; mời, Thẩm Ngọc đương nhi&ecirc;n sẽ kh&ocirc;ng trốn tr&aacute;nh, nhưng mấu chốt nhất l&agrave; Thẩm Ngọc ph&aacute;t hiện m&igrave;nh trong t&uacute;i kh&ocirc;ng l&agrave; b&igrave;nh thường ngượng ng&ugrave;ng.<br />\r\n<br />\r\nNguy&ecirc;n bản Thẩm Ngọc ch&iacute;nh l&agrave; c&aacute;i thư sinh ngh&egrave;o, tr&ecirc;n th&acirc;n lớn nhất một khoản tiền, vẫn l&agrave; đ&aacute;nh dấu đạt được hai mươi lượng.<br />\r\n<br />\r\nNgười ta mừng thọ kia kh&ocirc;ng được đưa ch&uacute;t lễ vật &yacute; tứ &yacute; tứ a, nhưng lễ vật n&agrave;y tặng nhẹ kh&ocirc;ng c&oacute; th&agrave;nh &yacute;, tặng nặng đ&acirc;u lại kh&ocirc;ng bỏ ra nổi đến, m&igrave;nh trong t&uacute;i c&aacute;i n&agrave;y hai mươi lượng hắn c&agrave;ng l&agrave; kh&ocirc;ng nỡ hướng Th&aacute;i Trọng trong nh&agrave; n&eacute;m. Lại nói, Th&aacute;i Trọng mời hắn dự tiệc, cũng kh&ocirc;ng phải l&agrave; muốn mượn cơ hội nhục nh&atilde; hắn a?<br />\r\n<br />\r\nĐược rồi, hắn l&uacute;c đầu c&ugrave;ng Th&aacute;i Trọng cũng kh&ocirc;ng c&oacute; g&igrave; giao t&igrave;nh, đưa c&aacute;i g&igrave; lễ vật, trực tiếp viết bức chữ đưa qua, bởi v&igrave; c&aacute;i gọi l&agrave; lễ nhẹ nhưng t&igrave;nh nặng m&agrave;!<br />\r\n<br />\r\nChờ đến thời gian, Thẩm Ngọc tiện tay viết một bức chữ, cất liền tiến Th&aacute;i phủ. L&agrave;m ngh&ecirc;nh t&acirc;n h&ocirc; l&ecirc;n c&acirc;u kia &quot;Huyện lệnh đại nh&acirc;n đưa th&acirc;n b&uacute;t tranh chữ một bức&quot; thời điểm, Thẩm Ngọc r&otilde; r&agrave;ng cảm gi&aacute;c được giống như tất cả mọi người nh&igrave;n lại.<br />\r\n<br />\r\nBất qu&aacute; c&oacute; c&acirc;u n&oacute;i n&oacute;i rất đ&uacute;ng, chỉ cần ch&iacute;nh ta kh&ocirc;ng ch&ecirc; xấu hổ, kia l&uacute;ng t&uacute;ng ch&iacute;nh l&agrave; người kh&aacute;c. Lại n&oacute;i, m&igrave;nh c&aacute;i n&agrave;y li&ecirc;m khiết thanh bạch, kia l&agrave; thật to thanh quan, n&ecirc;n ki&ecirc;u ngạo mới l&agrave;, ai d&aacute;m c&oacute; &yacute; kiến?<br />\r\n<br />\r\n&quot;Huyện lệnh đại nh&acirc;n đ&ecirc;́n nhà, hạ quan nơi n&agrave;y vẻ vang cho kẻ h&egrave;n n&agrave;y, đại nh&acirc;n ng&agrave;i qu&aacute; kh&aacute;ch kh&iacute;, mau mau thượng tọa!&quot;<br />\r\n<br />\r\nKhi thấy Thẩm Ngọc đến về sau, l&uacute;c n&agrave;y Th&aacute;i Trọng ho&agrave;n to&agrave;n l&agrave; một bộ nhiệt t&igrave;nh qu&aacute; mức d&aacute;ng vẻ, nh&igrave;n kh&ocirc;ng ra một điểm phẫn nộ tại n&ocirc;n n&oacute;ng, k&eacute;o Thẩm Ngọc liền đi tới chủ vị.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nTại c&aacute;i b&agrave;n n&agrave;y l&ecirc;n ngồi đều l&agrave; B&aacute;ch An huyện nhất c&oacute; địa vị c&ugrave;ng uy vọng người, huyện &uacute;y Ph&ugrave;ng b&ecirc;n trong, B&aacute;ch An huyện nh&agrave; gi&agrave;u nhất T&ocirc;n Hạc Linh đều tại trong đ&oacute;.<br />\r\n<br />\r\nLại nh&igrave;n quanh bốn ph&iacute;a nh&igrave;n xem c&aacute;i kh&aacute;c t&acirc;n kh&aacute;ch, kia c&oacute; một c&aacute;i t&iacute;nh một c&aacute;i, đều l&agrave; B&aacute;ch An huyện người c&oacute; mặt mũi. Thậm ch&iacute; tại t&acirc;n kh&aacute;ch b&ecirc;n trong, Thẩm Ngọc c&ograve;n chứng kiến bổ đầu Chu Nguy&ecirc;n. Bất qu&aacute; đối phương c&agrave; vị kh&ocirc;ng nhiều đủ, kh&ocirc;ng c&oacute; có th&ecirc;̉ c&ugrave;ng bọn hắn một b&agrave;n.<br />\r\n<br />\r\n&quot;Huyện lệnh đại nh&acirc;n có th&ecirc;̉ trăm bận bịu b&ecirc;n trong d&agrave;nh thời gian m&agrave; đến, hạ quản hết sức vinh hạnh. Đến, để ch&uacute;ng ta trước k&iacute;nh đại nh&acirc;n một ch&eacute;n!&quot; Thẩm Ngọc vừa hạ xuống t&ograve;a, Th&aacute;i Trọng liền giơ ly rượu l&ecirc;n, bầu kh&ocirc;ng kh&iacute; một ch&uacute;t liền nhiệt liệt.<br />\r\n<br />\r\nRất nhanh, người chung quanh cũng nhao nhao bắt đầu hướng hắn mời rượu. N&acirc;ng ly cạn ch&eacute;n ở giữa, v&agrave;i ch&eacute;n rượu hạ bụng. M&agrave; dần dần, Thẩm Ngọc cho người cảm gi&aacute;c phảng phất c&oacute; ch&uacute;t hơi say rượu, th&acirc;n h&igrave;nh tựa hồ c&oacute; ch&uacute;t lung la lung lay. Chỉ bất qu&aacute;, cũng chỉ c&oacute; Thẩm Ngọc minh bạch hắn hiện tại thế nhưng l&agrave; rất thanh tỉnh.<br />\r\n<br />\r\nMượn tửu k&igrave;nh, phảng phất nửa tỉnh nửa say Thẩm Ngọc giơ ch&eacute;n rượu, giống như c&oacute; ch&uacute;t v&ocirc; &yacute; x&ocirc;ng Th&aacute;i Trọng n&oacute;i ra: &quot;Th&aacute;i huyện thừa nhưng biết c&aacute;i n&agrave;y hai ng&agrave;y huyện nha ra đại sự, huyện ta nha trong phủ quản gia v&acirc;̣y mà l&agrave; bị truy n&atilde; cường đạo, việc n&agrave;y đ&atilde; bị Chu bổ đầu chứng thực!&quot;<br />\r\n<br />\r\n&quot;Ồ? Lại c&oacute; việc n&agrave;y?&quot; Nghe được Thẩm Ngọc, Th&aacute;i Trọng tựa hồ tương đương kinh ngạc, tr&ecirc;n mặt nh&igrave;n kh&ocirc;ng ra mảy may sơ hở &quot;Hạ quan c&aacute;i n&agrave;y hai ng&agrave;y đều tại bế m&ocirc;n hối lỗi, tăng th&ecirc;m lại muốn chuẩn bị từ th&acirc;n t&ocirc;i thọ yến, n&agrave;y đối với chuyện xảy ra b&ecirc;n ngo&agrave;i kh&ocirc;ng c&oacute; nghe thấy!&quot;<br />\r\n<br />\r\n&quot;Thật sao?&quot; Giơ ly rượu l&ecirc;n, Thẩm Ngọc trừng mắt c&oacute; ch&uacute;t m&ecirc; ly con mắt nh&igrave;n về ph&iacute;a Th&aacute;i Trọng &quot;Nhưng bản quan l&agrave;m sao nghe được cường đạo tựa như l&agrave; huyện thừa đại nh&acirc;n ngươi an b&agrave;i tiến huyện nha đ&acirc;u!&quot;<br />\r\n<br />\r\n&quot;Đại nh&acirc;n n&oacute;i đ&ugrave;a, như thế cường đạo hạ quan l&agrave;m sao c&oacute; thể c&ugrave;ng hắn quen biết, c&agrave;ng đừng n&oacute;i đem hắn an b&agrave;i tiến huyện nha, c&aacute;i n&agrave;y r&otilde; r&agrave;ng l&agrave; c&oacute; người cố &yacute; tại vu oan h&atilde;m hại. Kia cường đạo ở nơi n&agrave;o, hạ quan nguyện &yacute; c&ugrave;ng hắn giằng co!&quot;<br />\r\n<br />\r\nDứt lời về sau, Th&aacute;i Trọng vẫn l&agrave; một bộ thụ lớn lao ủy khuất bộ d&aacute;ng, diễn kỹ n&agrave;y, t&agrave;i nghệ n&agrave;y, để người kh&ocirc;ng thể kh&ocirc;ng bội phục!<br />\r\n<br />\r\nQuản gia đền tội sự t&igrave;nh đ&atilde; sớm truyền ra, đang ngồi ai kh&ocirc;ng biết. Kh&ocirc;ng thể kh&ocirc;ng n&oacute;i, Th&aacute;i Trọng c&aacute;i n&agrave;y mở mắt n&oacute;i lời bịa đặt bản sự, Thẩm Ngọc l&agrave; học cũng học kh&ocirc;ng được.<br />\r\n<br />\r\nChỉ bất qu&aacute;, l&uacute;c n&agrave;y Thẩm Ngọc phảng phất kh&ocirc;ng muốn bỏ qua hắn, con mắt nh&igrave;n thẳng đối phương. &Aacute;nh mắt kia s&aacute;t cơ, cho d&ugrave; l&agrave; tại men say che lấp lại, vẫn như cũ l&agrave; như thế r&otilde; r&agrave;ng.<br />\r\n<br />\r\n&quot;Tốt nhất l&agrave; như thế, bản quan sẽ tra, cũng hi vọng ch&iacute;nh như Th&aacute;i huyện thừa m&igrave;nh n&oacute;i tới như vậy!&quot;<br />\r\n<br />\r\n&quot;Nghĩa phụ, chư vị đại nh&acirc;n, n&ocirc; gia chuy&ecirc;n tới để k&iacute;nh chư vị đại nh&acirc;n một ch&eacute;n!&quot; Ngay tại c&aacute;i n&agrave;y gần như giương cung bạt kiếm thời đi&ecirc;̉m, đột nhi&ecirc;n một đạo thanh &acirc;m thanh lệ vang l&ecirc;n, nh&aacute;y mắt ph&aacute; vỡ cục diện bế tắc, cũng đem mọi người lực ch&uacute; &yacute; đều hấp dẫn tới.<br />\r\n<br />\r\nTrước mắt nữ tử, th&acirc;n mang m&agrave;u hồng quần &aacute;o, th&acirc;n h&igrave;nh thon thả, dung mạo t&uacute; lệ, thanh tịnh s&aacute;ng tỏ trong hai con ngươi lộ ra từng tia từng tia mị hoặc, nhất cử nhất động một c&aacute;i nhăn m&agrave;y cười một tiếng, phảng phất đều mang mấy phần mị th&aacute;i. Trong l&uacute;c nhất thời, trước mắt phảng phất chỉ c&ograve;n lại c&oacute; đạo n&agrave;y tịnh lệ phong cảnh.<br />\r\n<br />\r\nC&aacute;i n&agrave;y nữ tử dung mạo c&ugrave;ng l&uacute;c trước bị Th&aacute;i H&ograve;a trắng trợn cướp đoạt thiếu nữ kh&oacute; ph&acirc;n tr&ecirc;n dưới, đều l&agrave; cực k&igrave; hấp dẫn người. Chỉ bất qu&aacute; c&ocirc; g&aacute;i kia l&agrave; thanh thuần, m&agrave; trước mắt nữ tử ch&iacute;nh l&agrave; mị hoặc.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; tiểu nữ a Như, l&agrave; ta vừa vặn nhận hạ nghĩa nữ!&quot; Cười hướng sở hữu người giới thiệu một phen, sau đ&oacute; Th&aacute;i Trọng vội v&agrave;ng n&oacute;i &quot;A Như, đến, hướng chư vị đại nh&acirc;n k&iacute;nh một ch&eacute;n rượu. Nhất l&agrave; huyện lệnh đại nh&acirc;n, hắn vừa vặn thế nhưng l&agrave; c&ugrave;ng ta mở một c&aacute;i thật l&agrave; lớn tr&ograve; đ&ugrave;a!&quot;<br />\r\n<br />\r\n&quot;Đại nh&acirc;n!&quot; Giơ ly rượu l&ecirc;n đi v&agrave;o Thẩm Ngọc trước người, nữ tử mị nh&atilde;n như tơ, h&igrave;nh như c&oacute; kh&ocirc;ng n&oacute;i ra được dụ hoặc, liền &acirc;m thanh b&ecirc;n trong phảng phất đều lộ ra một ch&uacute;t xốp gi&ograve;n &yacute;.<br />\r\n<br />\r\n&quot;Đại nh&acirc;n, n&ocirc; gia mời ng&agrave;i một ch&eacute;n!&quot;<br />\r\n<br />\r\n&quot;Tốt!&quot; Nhẹ gật đầu, Thẩm Ngọc nhẹ nh&agrave;ng cười một tiếng, sau đ&oacute; &aacute;nh mắt nh&igrave;n về ph&iacute;a nơi kh&aacute;c, lại kh&ocirc;ng ch&uacute; &yacute; b&ecirc;n n&agrave;y. Trước mắt nữ tử xinh đẹp l&agrave; xinh đẹp, nhưng l&agrave; người Th&aacute;i gia, vẫn l&agrave; Th&aacute;i Trọng nghĩa nữ. Nghĩa nữ nha, tất cả mọi người hiểu!<br />\r\n<br />\r\n&quot;Đợi l&aacute;t nữa, rượu n&agrave;y. . . . .&quot; Coi như Thẩm Ngọc bưng ch&eacute;n rượu l&ecirc;n chuẩn bị uống một hơi cạn sạch thời điểm, tị độc ngọc ch&acirc;u đột nhi&ecirc;n biến th&agrave;nh m&agrave;u lam nhạt, cũng l&agrave;m cho Thẩm Ngọc sắc mặt c&oacute; ch&uacute;t l&ecirc;n một ch&uacute;t biến h&oacute;a.<br />\r\n<br />\r\n&quot;Tốt một c&aacute;i Th&aacute;i Trọng, v&acirc;̣y mà thật d&aacute;m hạ độc!&quot; Tị độc ngọc ch&acirc;u biến sắc, đủ để chứng minh hắn trong tay ch&eacute;n rượu n&agrave;y c&oacute; vấn đề. Ngay trước nhiều người như vậy trước mặt, Th&aacute;i Trọng thật sự l&agrave; thật to gan, đ&acirc;y l&agrave; chuẩn bị vạch mặt a!<br />\r\n<br />\r\n&quot;Ta ngược lại muốn xem xem, ngươi c&ograve;n c&oacute; mấy phần thủ đoạn!&quot; Trong l&ograve;ng khinh thường hừ lạnh một tiếng, Thẩm Ngọc nhẹ nh&agrave;ng lung lay tị độc ngọc ch&acirc;u, sau đ&oacute; đem rượu trong ch&eacute;n uống một hơi cạn sạch.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nTrước mắt nữ tử chỉ ở nơi n&agrave;y chờ đợi một l&aacute;t, liền lặng lẽ thối lui, m&agrave; Thẩm Ngọc th&igrave; l&agrave; tại n&acirc;ng ly cạn ch&eacute;n ở giữa phảng phất men say c&agrave;ng ng&agrave;y c&agrave;ng s&acirc;u, thẳng đến cuối c&ugrave;ng một ch&uacute;t nằm ở tr&ecirc;n b&agrave;n.<br />\r\n<br />\r\n&quot;Đại nh&acirc;n, ng&agrave;i say!&quot; Một ch&uacute;t đỡ Thẩm Ngọc, Th&aacute;i Trọng lập tức hướng chung quanh mang theo &aacute;y n&aacute;y n&oacute;i &quot;Chư vị, trước xin lỗi kh&ocirc;ng tiếp được một ch&uacute;t, ta trước mang huyện lệnh đại nh&acirc;n đi nghỉ ngơi một ch&uacute;t!&quot;<br />\r\n<br />\r\nSau khi n&oacute;i xong, Th&aacute;i Trọng liền vội v&agrave;ng mang theo một c&aacute;i hạ nh&acirc;n đỡ lấy Thẩm Ngọc, đem hắn mang đi nghỉ ngơi địa phương. M&agrave; đi tới đi tới, Thẩm Ngọc dường như hồ bắt đầu miệng s&ugrave;i bọt m&eacute;p, ngay cả sắc mặt đều trở n&ecirc;n tương đương kh&oacute; coi.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; c&oacute; chuyện g&igrave;?&quot; Nh&igrave;n thấy như thế t&igrave;nh huống, Th&aacute;i Trọng cũng l&agrave; giật nảy cả m&igrave;nh &quot;Hạ kh&ocirc;ng phải m&ocirc;ng h&atilde;n dược a, l&agrave;m sao lại miệng s&ugrave;i bọt m&eacute;p, chẳng lẽ l&agrave; liều lượng hạ nhiều?&quot;<br />\r\n<br />\r\n&quot;Chỉ l&agrave; m&ocirc;ng h&atilde;n dược? Ta s&aacute;t, tr&igrave;nh diễn qua!&quot; Nghe n&oacute;i như thế, nguy&ecirc;n bản c&ograve;n tại chững chạc đ&agrave;ng ho&agrave;ng diễn kịch Thẩm Ngọc lập tức kịp phản ứng. Hợp lấy m&igrave;nh uống xong kh&ocirc;ng phải độc dược, chỉ l&agrave; m&ocirc;ng h&atilde;n dược. Nhưng Th&aacute;i Trọng cho m&igrave;nh hạ m&ocirc;ng h&atilde;n dược l&agrave;m c&aacute;i g&igrave;, ch&iacute;nh l&agrave; v&igrave; để hắn ngủ một giấc?<br />\r\n<br />\r\nRất nhanh, Thẩm Ngọc liền được đưa tới nghỉ ngơi địa phương. M&agrave; vừa tới nơi n&agrave;y, Th&aacute;i Trọng liền lập tức để b&ecirc;n cạnh hạ nh&acirc;n đem hắn cởi sạch n&eacute;m tới tr&ecirc;n giường. Trong l&uacute;c đ&oacute; Thẩm Ngọc mấy lần chuẩn bị xuất thủ, nhưng đều nhịn xuống tới. Hắn đến muốn nh&igrave;n một ch&uacute;t, đối phương đến tột c&ugrave;ng muốn l&agrave;m g&igrave;!<br />\r\n<br />\r\nNh&igrave;n xem ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; &yacute; thức Thẩm Ngọc, Th&aacute;i Trọng h&agrave;i l&ograve;ng nhẹ gật đầu, sau đ&oacute; hướng b&ecirc;n cạnh hạ nh&acirc;n ph&acirc;n ph&oacute; n&oacute;i &quot;Đi, đem a Như mang đến!&quot;<br />\r\n<br />\r\n&quot;V&acirc;ng, l&atilde;o gia!&quot;<br />\r\n<br />\r\n&quot;Ha ha ha. . . .&quot; Tại hạ nh&acirc;n sau khi đi, Th&aacute;i Trọng tựa hồ rốt cuộc kh&ocirc;ng kềm được, trực tiếp bắt đầu cất tiếng cười to l&ecirc;n, thanh &acirc;m b&ecirc;n trong lộ ra v&ocirc; tận đắc &yacute;.<br />\r\n<br />\r\n&quot;Ban ng&agrave;y ban mặt ph&iacute;a dưới, ức hiếp phụ nữ đ&agrave;ng ho&agrave;ng. Thẩm Ngọc, Thẩm Hằng Chi, ta nh&igrave;n ngươi lần n&agrave;y l&agrave;m sao tr&aacute;nh!&quot;</p>', '2021-12-01 01:35:15', 10, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(289, 70, 'Sống Lại.', '2', '<p>Th&acirc;y chất đầy đồng, x&aacute;c ngựa ngổn ngang, vũ kh&iacute; hư hỏng, cờ quạt đứt g&atilde;y...cả chiến địa chỉ c&ograve;n tiếng qu&agrave;ng quạc của lo&agrave;i chim ăn x&aacute;c th&ocirc;i, ch&uacute;ng k&ecirc;u như cười nhạo lo&agrave;i người, một lũ ngu xuẩn chỉ biết ch&eacute;m giết lẫn nhau, cung cấp cho ch&uacute;ng thật nhiều đồ ăn...Đ&acirc;y đ&oacute;, c&ograve;n tiếng r&ecirc;n nh&egrave; nhẹ đầy tuyệt vọng của những thương binh đang nằm chờ chết......Mưa đ&atilde; dừng từ l&acirc;u nhưng nước mưa kh&ocirc;ng mang đi m&aacute;u tươi, kh&ocirc;ng rửa sạch đại địa m&agrave; chỉ l&agrave;m n&oacute; c&agrave;ng th&ecirc;m kinh khủng......<br />\r\n<br />\r\nHắn nằm đ&oacute;, lẳng lặng như một x&aacute;c chết, kh&ocirc;ng biết thời gian tr&ocirc;i qua bao l&acirc;u, 1 ph&uacute;t, 1 tiếng, 1 ng&agrave;y hay 1 tuần, hắn cũng kh&ocirc;ng r&otilde;...Đầu đau như b&uacute;a bổ, những cơn mộng mị cứ đến lại đi...Những chuyện đ&atilde; qua từ thật l&acirc;u nay cũng hiện về trong giấc mơ...Rồi trong giấc mơ, hắn như trải qua một cuộc đời kh&aacute;c...cuộc đời của ch&agrave;ng trai t&ecirc;n Vũ Đại Hải....<br />\r\n<br />\r\nSinh ra ở ch&acirc;u Tam Đ&aacute;i, lộ Đ&ocirc;ng Đ&ocirc;, cha l&agrave; một v&otilde; quan nhỏ...gia cảnh kh&ocirc;ng gi&agrave;u c&oacute; g&igrave; nhưng đủ ăn đủ mặc...tuổi thơ &ecirc;m đ&ecirc;m chăn tr&acirc;u cắt cỏ, chọc l&agrave;ng ph&aacute; x&oacute;m c&ugrave;ng ch&uacute;ng bạn...Ng&agrave;y ng&agrave;y tr&ocirc;i qua &ecirc;m đềm, theo cha học v&otilde; nghệ, theo mẹ học thơ ca....Rồi chiến tranh, thi&ecirc;n tai xảy ra li&ecirc;n mi&ecirc;n, nạn d&acirc;n ng&agrave;y c&agrave;ng nhiều, cha được thăng chức...cả nh&agrave; chuyển l&ecirc;n kinh đ&ocirc; phồn hoa nhưng cũng đầy hủ bại...Giặc d&atilde; nổi l&ecirc;n khắp nơi, qu&acirc;n Chi&ecirc;m x&acirc;m phạm, ph&aacute; cả kinh th&agrave;nh...Cha tử trận, mẹ chết bệnh tr&ecirc;n đường chạy nạn, chỉ c&oacute; hắn l&agrave; may mắn c&ograve;n sống. Th&ugrave; nh&agrave; nợ nước, hắn tham gia qu&acirc;n ngũ, xin ra trận giết giặc....v&otilde; nghệ hơn người lại c&oacute; t&agrave;i cưỡi ngựa n&ecirc;n được chọn v&agrave;o đội kỵ binh....hắn đi&ecirc;n cuồng chiến đấu, ph&aacute;t tiết hận th&ugrave; trong l&ograve;ng, kẻ địch chết tr&ecirc;n tay hắn kh&ocirc;ng biết bao m&agrave; kể, chiến c&ocirc;ng mang đến cho hắn chức tước c&ugrave;ng lợi lộc.... nhưng v&agrave;ng bạc, rượu ngon c&ugrave;ng mĩ nữ kh&ocirc;ng l&agrave;m cho hắn hứng th&uacute;, hắn chỉ muốn ch&eacute;m giết, trả th&ugrave;....Cuộc đời của Đại Hải c&oacute; lẽ sẽ m&atilde;i như vậy đến khi hắn ta chết đi, c&oacute; lẽ chết đi l&agrave; một c&aacute;ch giải tho&aacute;t.....<br />\r\n.........................<br />\r\n&ldquo;Phựtttttt..&rdquo;<br />\r\nMột c&aacute;nh tay nh&ocirc; l&ecirc;n từ đống x&aacute;c, những con quạ đang thưởng thức &ldquo;mỹ thực&rdquo; giật m&igrave;nh bay nh&aacute;o nh&aacute;c...Từ từ, một c&aacute;ch nặng nề c&aacute;i x&aacute;c kh&ocirc;ng đầu bị đẩy ra để lộ người nằm b&ecirc;n dưới...một thanh ni&ecirc;n khoảng 20 tuổi, , mặt mũi bầm dập khắp người l&agrave; m&aacute;u tươi c&ugrave;ng b&ugrave;n s&igrave;nh, bộ gi&aacute;p sắt kh&ocirc;ng c&ograve;n b&oacute;ng lo&aacute;ng nữa m&agrave; hoen ố, nhơ nhuốc, &aacute;o b&agrave;o nh&agrave;u nhĩ. Hắn nằm đ&oacute;, nặng nề h&iacute;t thở, kh&ocirc;ng biết do th&acirc;n thể qu&aacute; mệt mỏi hay l&agrave; t&acirc;m t&igrave;nh kh&ocirc;ng ổn định...<br />\r\n<br />\r\nHắn, một sinh vi&ecirc;n rất b&igrave;nh thường như bao sinh vi&ecirc;n kh&aacute;c ở đất nước VN, cả ng&agrave;y mỗi việc ăn với học, thi thoảng đọc truyện giải tr&iacute; , đọc nhiều cũng ảo tưởng xuy&ecirc;n kh&ocirc;ng về thời cổ đại, lấy kiến thức hiện đại, đại s&aacute;t tứ phương, l&agrave; vua l&agrave;m ch&uacute;a, cưới hết mỹ nh&acirc;n thi&ecirc;n hạ...nhưng đấy chỉ l&agrave; tự yy ch&uacute;t th&ocirc;i, chứ cho hắn cũng kh&ocirc;ng muốn, cuộc sống của hắn b&acirc;y giờ rất tốt, rất y&ecirc;n b&igrave;nh rồi, kh&ocirc;ng c&oacute; như cầu thay đổi...Khổ thay, &ocirc;ng trời qu&aacute; chiều l&ograve;ng người m&agrave; cho hắn xuy&ecirc;n....bao nhi&ecirc;u dự định, kế hoạch chưa thực hiện, c&ograve;n gia đ&igrave;nh, bạn b&egrave; nữa....Hận....<br />\r\n<br />\r\nCơ thể đau nhức, kh&ocirc;ng muốn cử động....chỉ đ&agrave;nh nằm than th&acirc;n tr&aacute;ch phận....đ&atilde; xuy&ecirc;n sao kh&ocirc;ng cho xuy&ecirc;n v&agrave;o ni&ecirc;n đại h&ograve;a b&igrave;nh n&agrave;o đấy, đời vua Th&aacute;i Tổ, Th&aacute;i T&ocirc;ng n&agrave;y, Hồng Đức thịnh thế n&agrave;y, &iacute;t nhất kh&ocirc;ng lo bị giết chết bất cứ l&uacute;c n&agrave;o, c&oacute; thể kinh doanh ch&uacute;t ch&uacute;t, l&agrave;m gi&agrave;u, l&agrave;m trọc ph&uacute;,.....sao lại v&agrave;o c&aacute;i thời chiến tranh loạn lạc, mạng người như cỏ r&aacute;c, đ&atilde; thế c&ograve;n l&agrave; l&iacute;nh chiến nữa, kiếm sống tr&ecirc;n đầu thương ngọn gi&aacute;o...Haizzz, Vũ Đại Hải cũng thật đ&aacute;ng thương, m&agrave; d&acirc;n thời loạn mấy ai lại kh&ocirc;ng đ&aacute;ng thương, th&ocirc;i th&igrave; cậu h&atilde;y y&ecirc;n nghỉ đi, để t&ocirc;i thay cậu sống tiếp đoạn đời c&ograve;n lại, sống cuộc đời đ&aacute;ng sống cho cả 2 ta.....</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nMi&ecirc;n man suy nghĩ hồi l&acirc;u cuối c&ugrave;ng hắn cũng định thần lại, sức lực cũng trở về với cơ thể, d&ugrave; muốn hay kh&ocirc;ng, hắn đ&atilde; xuy&ecirc;n kh&ocirc;ng, từ h&ocirc;m nay, hắn l&agrave; Vũ Đại Hải, th&agrave;nh vi&ecirc;n thứ n+1 của tập đo&agrave;n xuy&ecirc;n kh&ocirc;ng, hắn phải sống thật tốt để kh&ocirc;ng c&oacute; lỗi với c&ocirc;ng ơn sinh th&agrave;nh dưỡng dục của cha mẹ kiếp trước cũng như kiếp n&agrave;y, hắn phải đ&aacute;nh ra một v&ugrave;ng trời của m&igrave;nh, lưu danh sử s&aacute;ch, cưới bạch ph&uacute; mỹ, đi l&ecirc;n nh&acirc;n sinh đỉnh cao, kh&ocirc;ng l&agrave;m mất mặt tập đo&agrave;n.....<br />\r\n<br />\r\nQuả thực, sống trong ni&ecirc;n đại h&ograve;a b&igrave;nh sung sướng suốt hơn hai mươi năm, vẫn chưa phải đi l&agrave;m,chưa đối mặt với những mặt tối của x&atilde; hội n&ecirc;n t&acirc;m hồn của hắn c&ograve;n rất mộng mơ, thần kinh vẫn rất th&ocirc;...Trong ho&agrave;n cảnh đầy bi đ&aacute;t c&ograve;n c&oacute; thể &ldquo;h&agrave;o t&igrave;nh vạn trượng&rdquo; đưa ra tuy&ecirc;n ng&ocirc;n với đời. Kh&ocirc;ng biết, những năm th&aacute;ng sau n&agrave;y, khi phải trải qua kh&oacute;i lửa chiến tranh, phải chứng kiến cảnh loạn lạc, tang t&oacute;c, lầm than của đất nước, của d&acirc;n tộc hắn c&ograve;n giữ được bản th&acirc;n m&igrave;nh hay kh&ocirc;ng, c&oacute; thực hiện được tuy&ecirc;n ng&ocirc;n của m&igrave;nh hay kh&ocirc;ng..hay hắn sẽ nằm lại đ&acirc;y tr&ecirc;n mặt đất lạnh lẽo như bao con người trong thời loạn, bị thời đại nhấn ch&igrave;m, như một bọt s&oacute;ng rất nhỏ trong đại dương bao la.....<br />\r\n<br />\r\nNgồi dậy, th&acirc;n thể vẫn c&ograve;n rất bết b&aacute;t, d&otilde;i mắt nh&igrave;n mọi thứ xung quanh, đến b&acirc;y giờ hắn mới c&oacute; thời gian v&agrave; kh&ocirc;ng gian để đ&aacute;nh gi&aacute; hết ho&agrave;n cảnh xung quanh, chỉ c&oacute; &ldquo;nh&acirc;n gian luyện ngục&rdquo; mới lột tả được vị tr&iacute; của hắn l&uacute;c n&agrave;y. Khắp nơi l&agrave; x&aacute;c người, x&aacute;c ngựa, ch&acirc;n tay cụt lủn, binh kh&iacute; đứt đoạn, m&aacute;u tươi nội tạng h&ograve;a c&ugrave;ng b&ugrave;n đất tạo th&agrave;nh thứ hỗn hợp kinh tởm, kh&ocirc;ng kh&iacute; tr&agrave;n đầy m&ugrave;i m&aacute;u tươi, m&ugrave;i mồ h&ocirc;i c&ugrave;ng m&ugrave;i b&ugrave;n đất..Rồi l&agrave; ruồi nhặng, chim ăn x&aacute;c thối....Tất cả những thứ n&agrave;y, c&oacute; thể l&agrave;m cho người gan dạ nhất s&oacute;n ra quần khi lần đầu ti&ecirc;n tr&ocirc;ng thấy...Hắn vốn cũng kh&ocirc;ng phải người gan dạ g&igrave; cho cam, n&ecirc;n...&rdquo;ọeeeeee&rdquo; sau một hồi n&ocirc;n ra cả mật, cuối c&ugrave;ng hắn cũng cảm thấy kh&aacute; hơn đ&ocirc;i ch&uacute;t...Chỉ l&agrave; kh&aacute; hơn đ&ocirc;i ch&uacute;t th&ocirc;i, hắn lại cảm thấy bi ai cho cuộc sống sau n&agrave;y của m&igrave;nh, chắc chắn, c&aacute;i cảnh n&agrave;y sẽ chỉ nhiều hơn chứ kh&ocirc;ng &iacute;t đi, hắn phải mạnh mẽ l&ecirc;n, nếu kh&ocirc;ng hắn sẽ nằm lại đ&acirc;y, kh&ocirc;ng đầu, kh&ocirc;ng tay ch&acirc;n, l&agrave;m mồi cho th&uacute; hoang chứ đừng n&oacute;i g&igrave; đến cưới bạch ph&uacute; mỹ, đi l&ecirc;n nh&acirc;n sinh đỉnh cao..<br />\r\n<br />\r\nH&iacute;t thật s&acirc;u v&agrave;i ngụm để đề cao tinh thần, khốn thay, cả phổi tr&agrave;n ngập kh&ocirc;ng kh&iacute; dơ bẩn của m&aacute;u tươi v&agrave; b&ugrave;n đất, tỉnh t&aacute;o hơn kh&ocirc;ng thấy đ&acirc;u chỉ c&oacute; tởm hơn th&ocirc;i, hắn run rẩy chống ngọn gi&aacute;o g&atilde;y b&ecirc;n cạnh đứng dậy, c&oacute; n&eacute;n sự kinh tởm v&agrave; cơn buồn n&ocirc;n, hắn.....đi loot đồ, muốn sống tiếp th&igrave; phải c&oacute; đồ, phải c&oacute; tư bản.<br />\r\n<br />\r\nKhắp nơi đều l&agrave; x&aacute;c chết v&agrave; m&aacute;u, hắn vung vẩy ngọn gi&aacute;o g&atilde;y đuổi đi chim ch&oacute;c, run rẩy đưa tay đi m&ograve; x&aacute;c. Kh&ocirc;ng biết do tố chất t&acirc;m l&yacute; của hắn qu&aacute; tốt hay kinh nghiệm sa trường của cơ thể, hắn dần quen với cảnh địa ngục n&agrave;y, tay ch&acirc;n cũng nhanh nhẹn mạnh bạo hơn. Nếu chỉ l&agrave; một sinh vi&ecirc;n đơn thuần th&igrave; c&oacute; cho tiền hắn cũng kh&ocirc;ng d&aacute;m duỗi tay sờ v&agrave;o mấy c&aacute;i x&aacute;c đầy m&aacute;u me n&agrave;y.<br />\r\n<br />\r\nHắn rất sợ chết n&ecirc;n muốn vũ trang m&igrave;nh tốt nhất c&oacute; thể, c&aacute;i g&igrave; cũng muốn lấy, nhưng phải ch&uacute; &yacute; l&agrave; n&ecirc;n lấy đồ của triều đ&igrave;nh th&ocirc;i, v&igrave; hắn l&agrave; l&iacute;nh triều đ&igrave;nh, đang tr&ecirc;n l&atilde;nh thổ Đại Việt, vớ vẩn lấy đồ của người Chi&ecirc;m mặc l&ecirc;n người c&oacute; khi bị phục k&iacute;ch chết mất x&aacute;c. Kể đến &ldquo;Đại Hải&rdquo; cũng kh&ocirc;ng phải tay vừa, hắn cũng đ&atilde; l&agrave; một đ&ocirc; trưởng, chỉ huy gần 100 kỵ binh, l&agrave; một v&otilde; quan loại nhỏ, phải nhớ l&agrave; hắn c&ograve;n rất trẻ, mới 20 tuổi, đ&atilde; l&ecirc;n được chức đ&ocirc; trưởng lại c&ograve;n l&agrave; chỉ huy kỵ binh, chứng tỏ hắn đ&atilde; ra sức như thế n&agrave;o trong chiến đấu để c&oacute; đủ c&ocirc;ng hu&acirc;n như vậy.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nTrận chiến n&agrave;y l&agrave; một trận tao ngộ chiến giữa đ&ocirc; kỵ binh của hắn với một nh&aacute;nh kỵ binh tinh nhuệ của qu&acirc;n Chi&ecirc;m, kết quả như n&agrave;o cũng đ&atilde; r&otilde;, gần như to&agrave;n qu&acirc;n bị ti&ecirc;u diệt, kh&ocirc;ng biết c&oacute; ai sống s&oacute;t kh&ocirc;ng.<br />\r\n<br />\r\n<br />\r\nChiến tranh l&agrave; một cơ hội rất tốt để ph&aacute;t t&agrave;i, theo mọi c&aacute;ch, m&agrave; c&aacute;ch nhanh nhất ch&iacute;nh l&agrave; cướp b&oacute;c, chỉ sau một tho&aacute;ng lục x&aacute;c m&agrave; hắn cũng kiếm được một m&oacute;n b&eacute;o bở, hơn 10 lượng v&agrave;ng v&agrave; gần 100 lượng bạc c&ugrave;ng v&ocirc; số thứ thượng v&agrave;ng hạ c&aacute;m kh&aacute;c, đ&acirc;y l&agrave; khối t&agrave;i sản rất lớn, một gia đ&igrave;nh n&ocirc;ng d&acirc;n chỉ cần 2 lượng bạc l&agrave; đ&atilde; đủ sống sung t&uacute;c cả th&aacute;ng trời rồi....phải n&oacute;i đến, l&agrave; do hầu như tất cả đều đ&atilde; chết, chỉ c&ograve;n m&igrave;nh hắn đi thu hoạch chiến lợi phẩm n&ecirc;n mới được như vậy.....Với số tiền n&agrave;y, hắn đủ sức kiếm chỗ n&agrave;o kh&ocirc;ng nhiều người rồi trốn ở đấy, mua &iacute;t đất, l&agrave;m địa chủ trọc ph&uacute; sống đến hết đời, khả năng l&agrave; đến hết đời nếu kh&ocirc;ng bị chiến tranh lan đến...nhưng mảnh lụa tr&ecirc;n tay hắn, với v&agrave;i d&ograve;ng chữ đơn giản &ldquo;Chế Bồng Nga tr&ecirc;n chiến thuyền m&agrave;u lục&rdquo; để hắn do dự suy nghĩ hồi l&acirc;u.....<br />\r\n<br />\r\nQua k&iacute; ức của &ldquo;Đại Hải&rdquo; th&igrave; hắn cũng biết m&igrave;nh đang ở đ&acirc;u v&agrave; thời n&agrave;o, đ&acirc;y c&oacute; thể l&agrave; qu&aacute; khứ, cũng c&oacute; thể l&agrave; một thời kh&ocirc;ng song song n&agrave;o đ&oacute;. Thời gian v&agrave;o cuối thời nh&agrave; Trần, đầu nh&agrave; Hồ, một giai đoạn loạn lạc nhất, chiến tranh li&ecirc;n mi&ecirc;n trong lịch sử Việt Nam. Trong khoảng 30 năm trở lại, li&ecirc;n tục l&agrave; những cuộc chiến tranh với Chi&ecirc;m Th&agrave;nh, thắng th&igrave; &iacute;t m&agrave; thua th&igrave; nhiều. Ai c&oacute; thể ngờ rằng, d&ograve;ng d&otilde;i Đ&ocirc;ng A từng đ&aacute;nh bại đội qu&acirc;n h&ugrave;ng mạnh nhất thế giới lại li&ecirc;n tục thất bại trước cuộc x&acirc;m lấn của vương triều Chăm pa, một tiểu quốc m&agrave; trước đ&oacute; phải xưng thần v&agrave; cống nạp cho Đại Việt, kinh đ&ocirc; thất thủ, d&acirc;n ch&uacute;ng lầm than, bị cướp b&oacute;c, bị giết, hiếp, b&aacute;n l&agrave;m n&ocirc; lệ, vua t&ocirc;i khiếp nhược nghe tin giặc đ&atilde; muốn chạy...Haizzz, đ&aacute;ng buồn cho d&ograve;ng d&otilde;i Đ&ocirc;ng A h&agrave;o h&ugrave;ng một thời.......<br />\r\n<br />\r\nQua tấm lụa, hắn c&agrave;ng th&ecirc;m chắc chắn được khoảng thời gian m&agrave; m&igrave;nh đang ở, c&oacute; lẽ l&agrave; giai đoạn Trần Kh&aacute;t Ch&acirc;n giết được vua Chi&ecirc;m, v&agrave; tấm lụa hắn đang cầm l&agrave; mấu chốt của mọi vấn đề, mấu chốt của việc giết được Chế Bồng Nga hay kh&ocirc;ng, cứu vớt c&aacute;i triều đại mục n&aacute;t n&agrave;y hay l&agrave; Đại Việt sẽ phải oằn m&igrave;nh trước ch&acirc;n voi Chi&ecirc;m Th&agrave;nh.<br />\r\n<br />\r\n<br />\r\nHắn đứng giữa 2 lựa chọn nan giải, trốn tr&aacute;nh hay đối mặt. Trốn tr&aacute;nh th&igrave; hắn sẽ c&oacute; được cuộc sống &ecirc;m đềm nhưng nh&agrave; Trần sẽ diệt vong sớm hơn, nh&acirc;n d&acirc;n Đại Việt sẽ kh&ocirc;ng biết đi đ&acirc;u về đ&acirc;u, khả năng lớn sẽ bị b&aacute;n đi l&agrave;m n&ocirc; lệ, hắn sẽ l&agrave; tội đồ của d&acirc;n tộc, mặc d&ugrave; kh&ocirc;ng ai biết việc hắn l&agrave;m nhưng mỗi đ&ecirc;m đi ngủ, lương t&acirc;m hắn c&oacute; cắn dứt hay kh&ocirc;ng, khi đẩy h&agrave;ng ngh&igrave;n h&agrave;ng vạn đồng b&agrave;o v&agrave;o cảnh cơ cực, nồi da nấu thịt....Đối mặt th&igrave; hắn sẽ bị cuốn v&agrave;o đợt s&oacute;ng lịch sử mạnh mẽ n&agrave;y, h&agrave;ng trăm h&agrave;ng ng&agrave;n cuộc chiến đẫm m&aacute;u, h&agrave;ng loạt vương triều sụp đổ suy vong&hellip;..biết bao người sẽ chết trong cuộc thay đổi triều đại n&agrave;y&hellip;...v&agrave; hắn, c&aacute;i mạng nhỏ của hắn chưa chắc đ&atilde; giữ được.....</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n<br />\r\nMịaaaaaaa, l&agrave;m trai cho đ&aacute;ng l&ecirc;n trai, sức d&agrave;i vai rộng m&agrave; kh&ocirc;ng lo việc nước.....chết th&igrave; chết th&ocirc;i, c&ugrave;ng lắm th&igrave; da ngựa bọc th&acirc;y, hắn tự xỉ nhục bản th&acirc;n v&igrave; đ&atilde; c&oacute; những suy nghĩ trốn chạy như vậy. Đường đường l&agrave; con rồng ch&aacute;u ti&ecirc;n, sao lại phải sợ mấy thằng giặc cỏ, đ&atilde; l&agrave; người xuy&ecirc;n việt, lại cam l&ograve;ng sống tầm thường .....Kh&ocirc;ng hề.......Tiến tới!!!! Được ăn cả ng&atilde; về kh&ocirc;ng, đ&atilde; kh&ocirc;ng c&oacute; vướng bận th&igrave; sao phải lo lắng!<br />\r\n&ldquo;uỳnh uỳnh...&rdquo;<br />\r\nTừ xa , một đội kỵ binh đang lao nhanh đến ph&iacute;a hắn, c&aacute;t bụi mịt m&ugrave;...<br />\r\n&ldquo;Xongggg. C&ograve;n chưa kịp l&agrave;m g&igrave;&rdquo;<br />\r\nVừa lấy quyết t&acirc;m chưa kịp h&agrave;nh động th&igrave; biến cố đ&atilde; đến....<br />\r\nHắn kh&ocirc;ng chạy, kh&ocirc;ng c&oacute; sức để m&agrave; chạy, kể cả chạy cũng kh&ocirc;ng được, hai ch&acirc;n kh&ocirc;ng nhanh bằng bốn ch&acirc;n, bọn kỵ binh kia chắc chắn cũng đ&atilde; ph&aacute;t hiện ra hắn......Chiến th&ocirc;i, may th&igrave; tho&aacute;t, kh&ocirc;ng th&igrave; treo...</p>', '2021-12-01 01:36:02', 8, 1),
(290, 70, 'Huyết Chiến', '3', '<p>&ldquo;HAAAAAA. C&oacute; ch&oacute; hoang đi lạc, nhanh, kh&ocirc;ng cho n&oacute; tho&aacute;t&rdquo;<br />\r\n<br />\r\nMột t&ecirc;n trung ni&ecirc;n đen thui, nở nụ cười khả ố khoe bộ răng v&agrave;ng kh&egrave; của l&atilde;o, qu&aacute;t mấy g&atilde; thuộc hạ lao về ph&iacute;a Đại Hải, sau l&atilde;o l&agrave; chục t&ecirc;n l&iacute;nh Chi&ecirc;m h&uacute; h&eacute;t th&uacute;c ngựa theo sau. Đao thương s&aacute;ng lo&aacute;ng đến lạnh người.<br />\r\n<br />\r\n<br />\r\n&ldquo;Giếttttttt&rdquo;<br />\r\n<br />\r\nD&ograve;ng m&aacute;u chiến binh chảy trong người Hải như s&ocirc;i l&ecirc;n, th&uacute;c dục g&atilde; tiến đến, ch&eacute;m giết, tận hưởng m&aacute;u tươi của qu&acirc;n th&ugrave;. C&oacute; thể Hải sinh vi&ecirc;n kh&ocirc;ng phải dạng hiếu chiến g&igrave; nhưng cũng kh&ocirc;ng phải đớn h&egrave;n đến mức mặc người ch&eacute;m giết, khỏi cần n&oacute;i đến Hải đ&ocirc; trưởng sinh ra thời loạn, lớn l&ecirc;n trong chiến tranh, trải qua h&agrave;ng trăm trận chiến lớn nhỏ, đối với chiến trường kh&ocirc;ng hề c&oacute; sợ h&atilde;i, chỉ c&oacute; kh&aacute;t vọng.<br />\r\n<br />\r\n&ldquo;VỤT&rdquo;<br />\r\n<br />\r\n&ldquo;Phập&rdquo;<br />\r\n<br />\r\nMũi gi&aacute;o ph&aacute; kh&ocirc;ng bay đi, nhanh như chớp, ngọn gi&aacute;o bay đi qu&aacute; nhanh, chỉ thấy được t&agrave;n ảnh, sức ph&aacute; hoại kinh người, ph&aacute; tan lớp gi&aacute;p sắt mỏng manh, xuy&ecirc;n thủng ngực t&ecirc;n trung ni&ecirc;n cưỡi ngựa đi đầu, y chỉ kịp a một tiếng rồi lật ra ph&iacute;a sau, một c&aacute;i l&ocirc;ng của g&atilde; &ldquo;ch&oacute; hoang&rdquo; kia c&ograve;n chưa chạm được đ&atilde; phải nuốt hận chết đi...<br />\r\n<br />\r\nMấy t&ecirc;n kỵ binh h&uacute; h&eacute;t theo sau bỗng khựng lại, ngơ ng&aacute;c nh&igrave;n đội trưởng của m&igrave;nh lăn xuống ngựa chết, một lũ hăng m&aacute;u như bị một g&aacute;o nước lạnh tạt v&agrave;o to&agrave;n th&acirc;n đều cảm thấy lạnh lẽo....&rdquo;Trả th&ugrave; cho thủ lĩnh&rdquo;..kh&ocirc;ng biết t&ecirc;n n&agrave;o h&eacute;t l&ecirc;n đầu, bọn c&ograve;n lại c&ugrave;ng h&eacute;t l&ecirc;n &ldquo;TRẢ TH&Ugrave;&rdquo; rồi ầm ầm lao đến...Sợ c&aacute;i quần qu&egrave; g&igrave;, 10 chả nhẽ kh&ocirc;ng ăn được một, lại l&agrave; kỵ binh....Thịt n&oacute;....<br />\r\n<br />\r\nChỉ kịp n&eacute;m đi một đầu gi&aacute;o nhưng hạ ngay được t&ecirc;n th&ugrave; lĩnh l&agrave;m Hải kh&aacute; h&agrave;i l&ograve;ng, kh&ocirc;ng thẹn với chức v&otilde; quan, tốc chất v&agrave; bản năng chiến đấu của th&acirc;n thể đều l&agrave; nhất lưu, hắn kh&ocirc;ng c&oacute; kinh nghiệm chiến đấu nhưng kỹ năng v&agrave; kinh nghiệm của &ldquo;Đại Hải&rdquo; th&igrave; c&ograve;n đ&oacute;, chỉ chờ khai quật....Linh hồn v&agrave; th&acirc;n thể mới kết hợp ng&agrave;y c&agrave;ng ho&agrave;n mỹ, một sức mạnh v&ocirc; h&igrave;nh n&agrave;o đ&oacute; sinh s&ocirc;i trong cơ thể của hắn, vừa trải qua trận huyết chiến m&agrave; giờ đ&acirc;y cơ thể tr&agrave;n đầy sức sống, cảm gi&aacute;c như cỏ thể bẻ g&atilde;y sừng tr&acirc;u ngay được...<br />\r\n<br />\r\n<br />\r\nChỉ v&agrave;i nhịp thở, đội kỵ binh đ&atilde; lao đến trước mặt g&atilde;, kh&ocirc;ng hổ l&agrave; lực lượng tinh nhuệ nhất tr&ecirc;n chiến trường trung cổ, khả năng cơ động qu&aacute; cao, tốt chất binh l&iacute;nh cũng tốt, chủ tướng chết m&agrave; kh&ocirc;ng loạn trận tuyến. Ở bất kỳ quốc gia n&agrave;o, đặc biệt c&aacute;c quốc gia thiếu ngựa như Đại Việt hay Chi&ecirc;m Th&agrave;nh, để trở th&agrave;nh một kỵ binh, t&ecirc;n n&agrave;o kh&ocirc;ng phải l&agrave; tinh nhuệ trong tinh nhuệ, c&ugrave;ng với đ&oacute; l&agrave; đ&atilde;i ngộ cao hơn hẳn c&aacute;c lực lượng kh&aacute;c, k&eacute;o theo tố chất th&acirc;n thể cũng cao hơn một bậc.<br />\r\n<br />\r\n<br />\r\nMười t&ecirc;n kỵ binh xung phong, kh&iacute; thế mặc d&ugrave; kh&ocirc;ng đến mức rời non lấp bể nhưng cũng rất rầm rộ, hơn hẳn trăm bộ binh xung phong, v&oacute; ngựa đạp tan mặt đất, bụi bay m&ugrave; mịt, chiến đao s&aacute;ng lo&aacute;ng chỉ chờ chặt lấy đầu của kẻ th&ugrave;. Phải kẻ kh&aacute;c kh&eacute;o đ&atilde; đ&aacute;i ra m&aacute;u nhưng Hải th&igrave; kh&ocirc;ng, kẻ t&agrave;i cao th&igrave; gan lớn, tay hắn cũng c&oacute; một ch&uacute;t run rẩy, c&oacute; run sợ nhưng nhiều hơn l&agrave; run l&ecirc;n bởi k&iacute;ch th&iacute;ch, l&agrave; kh&aacute;t vọng muốn thử, muốn l&agrave;m thịt lũ kỵ binh n&agrave;y....<br />\r\n<br />\r\n&ldquo;H&acirc;yyyyy...&rdquo;<br />\r\n<br />\r\n&ldquo;Phập...&rdquo;<br />\r\n<br />\r\nChạm v&agrave;o l&agrave; bỏ ra ngay, Đại Hải lập tức thả ra c&aacute;n gi&aacute;o, nhảy nhanh qua một b&ecirc;n, đồng thời giơ cao l&ecirc;n tấm khi&ecirc;n tr&ograve;n, tay phải r&uacute;t đao ch&eacute;m mạnh. Cả một tổ hợp c&aacute;c động t&aacute;c diễn ra chỉ trong t&iacute;ch tắc, chiến m&atilde; lướt ngang qua người g&atilde;, chỉ chậm 1 gi&acirc;y th&ocirc;i hoặc chỉ lệch 10cm nữa th&ocirc;i l&agrave; hắn đ&atilde; bị ngựa h&uacute;c phải, kh&ocirc;ng chết th&igrave; cũng trọng thương, b&ecirc;n mũi hắn c&ograve;n thoang thoảng m&ugrave;i mồ h&ocirc;i ngựa....</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&ldquo;Choengggg&rdquo;<br />\r\n<br />\r\nLười đao lướt qua tấm khi&ecirc;n mang theo &acirc;m thanh ch&aacute;t ch&uacute;a nhưng kh&ocirc;ng k&egrave;m theo đầu của Đại Hải. Vạn hạnh. Đao của hắn cũng kh&ocirc;ng chậm, ch&eacute;m mạnh ngay v&agrave;o cẳng ch&acirc;n sau của con ngựa khi n&oacute; lướt qua g&atilde;...<br />\r\n<br />\r\n&ldquo;H&iacute; iiiiiiiiii&rdquo;...... &ldquo;Rầmmm&rdquo;<br />\r\n<br />\r\nTrời đất quay cuồng, th&acirc;n thể đau điếng, t&ecirc;n kỵ binh lồm cồm b&ograve; dậy, đao kh&ocirc;ng biết đ&atilde; bay đi đ&acirc;u, th&igrave; đ&atilde; thấy t&ecirc;n &ldquo;ch&oacute; hoang&rdquo; kia v&aacute;c đao x&ocirc;ng đến....<br />\r\n<br />\r\n&ldquo;Đại ca, tha cho em một mạng...ở nh&agrave; c&ograve;n.....&rdquo;<br />\r\n<br />\r\n&ldquo;Phật&rdquo;<br />\r\n<br />\r\nĐầu g&atilde; bay l&ecirc;n cao, tr&ecirc;n mặt c&ograve;n nguy&ecirc;n vẻ ngỡ ng&agrave;ng c&ugrave;ng xin khoan thứ, ch&iacute;nh mắt g&atilde; c&ograve;n nh&igrave;n thấy th&acirc;n thể kh&ocirc;ng đầu của m&igrave;nh m&aacute;u phun xối xả rồi đổ xuống. Mọi thứ trở l&ecirc;n kh&ocirc;ng r&otilde; r&agrave;ng, mồm m&aacute;y động như muốn n&oacute;i g&igrave; nữa nhưng thanh &acirc;m kh&ocirc;ng ph&aacute;t ra....<br />\r\n<br />\r\nG&atilde; &ldquo;ch&oacute; hoang&rdquo; kia, khi&ecirc;m che trước mặt đao lăm lăm trong tay, miệng c&ograve;n lẩm bẩm &ldquo;Xin lỗi, ng&agrave;y c&ograve;n đi học lười biếng, kh&ocirc;ng hiểu ngoại ngữ&rdquo; Mắt chăm chăm nh&igrave;n bọn kỵ binh lao đi xa đang quay đầu ngựa lại...<br />\r\n<br />\r\nChỉ chưa đầy một ph&uacute;t, 2 t&ecirc;n kỵ binh Chi&ecirc;m qu&acirc;n đ&atilde; nằm lại đ&acirc;y, một g&atilde; gi&aacute;o đ&acirc;m thủng bụng, vẫn đang nằm vắt vẻo tr&ecirc;n lưng ngựa, c&ograve;n g&atilde; c&ograve;n lại th&igrave; nằm đ&acirc;y, kh&ocirc;ng đầu, bồi c&ugrave;ng g&atilde; l&agrave; con ngựa chiến của g&atilde;, ch&acirc;n sau gần như đứt đoạn, đang nằm h&iacute; một c&aacute;ch n&atilde;o nề, như biết được rằng chủ n&oacute; đ&atilde; chết v&agrave; n&oacute; cũng kh&ocirc;ng qua nổi h&ocirc;m n&agrave;y...<br />\r\n<br />\r\nGiết 2 người, mặt cũng kh&ocirc;ng th&egrave;m đổi sắc, linh hồn c&ugrave;ng th&acirc;n thể c&agrave;ng ng&agrave;y c&agrave;ng h&ograve;a hợp hơn, c&agrave;ng l&agrave;m chủ được th&acirc;n thể, tiếp thu những kinh nghiệm kỹ năng của &ldquo;Đại Hải&rdquo; th&igrave; hắn c&agrave;ng cảm thấy mọi thứ &ldquo;b&igrave;nh thường&rdquo; giết người như cắt rau th&aacute;i dưa th&ocirc;i, kh&ocirc;ng qu&aacute; gh&ecirc; gớm....Hắn cũng kh&ocirc;ng phản cảm, thời đại n&agrave;y ch&iacute;nh l&agrave; như thế v&agrave; con đường của hắn sau n&agrave;y cũng như thế, kh&ocirc;ng c&oacute; chỗ cho yếu đuối hay th&aacute;nh mẫu...<br />\r\n<br />\r\n8 t&ecirc;n Chi&ecirc;m binh lao qua Đại Hải khoảng 50 m&eacute;t th&igrave; quay ngựa lại, mặt mũi ngưng trọng<br />\r\n<br />\r\n<br />\r\n&ldquo;Đậu xanh, đạp phải đ&aacute; cứng rồi&rdquo;<br />\r\n<br />\r\n<br />\r\n&ldquo;Thằng ch&oacute; hoang n&agrave;y c&oacute; vẻ kh&ocirc;ng dễ xơi đ&acirc;u, l&agrave;m n&oacute; kh&ocirc;ng hay t&eacute; tụi bay?&rdquo;<br />\r\n<br />\r\n&ldquo;L&agrave;m n&oacute;, kh&ocirc;ng t&iacute;nh trả th&ugrave; cho anh em &agrave;&rdquo;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nMấy t&ecirc;n kỵ binh c&ograve;n lại nh&igrave;n y như nh&igrave;n thằng ngu, l&agrave;m gương mặt đen nhẻm của y kh&ocirc;ng khỏi đỏ l&ecirc;n ch&uacute;t ch&uacute;t<br />\r\n<br />\r\n&ldquo;Thế tao n&oacute;i g&igrave; sai &agrave;?&rdquo;<br />\r\n<br />\r\n&ldquo;Trả c&aacute;i đầu m&agrave;y, c&ograve;n chưa biết c&oacute; ăn được n&oacute; kh&ocirc;ng hay lại treo như mấy thằng kia th&igrave; bỏ mẹ&rdquo;<br />\r\n<br />\r\n&ldquo;Tụi bay, nh&igrave;n gi&aacute;p n&oacute; xem, m&aacute;, gi&aacute;p sắt tr&ocirc;ng ngon với nặng thế kia, kh&eacute;o l&agrave; thằng tướng qu&acirc;n&rdquo;<br />\r\n<br />\r\n&ldquo;Tướng qu&acirc;n th&igrave; sao??? Nh&igrave;n thấy bọn chưa kịp xanh cỏ b&ecirc;n kia kh&ocirc;ng&rdquo;<br />\r\n<br />\r\nVừa n&oacute;i g&atilde; vừa chỉ sang b&ecirc;n chiến trường, nơi m&agrave; đ&ocirc; của Đại Hải chiến với đ&aacute;m kỵ binh Chi&ecirc;m, x&aacute;c chết la liệt c&ograve;n tươi mới, m&ugrave;i m&aacute;u c&ograve;n rất nồng chứ chưa bốc m&ugrave;i h&ocirc;i thối do ph&acirc;n hủy...<br />\r\n<br />\r\n&ldquo;Hay lui tụi bay. M&igrave;nh chỉ l&agrave; đ&aacute;m do th&aacute;m th&ocirc;i, biết tin rồi t&eacute; th&ocirc;i, tao tr&ocirc;ng thằng ch&oacute; kia c&oacute; vẻ xương lắm, kh&ocirc;ng gặm được đ&acirc;u&rdquo;<br />\r\n<br />\r\n&ldquo;Chuẩn rồi tụi bay, kh&eacute;o c&ograve;n mấy thằng qu&acirc;n Việt quanh đ&acirc;y, n&oacute; nghe đ&aacute;nh nhau lao đến th&igrave; l&uacute;c đấy th&igrave; ngập mồm h&agrave;nh&rdquo;<br />\r\n<br />\r\n&ldquo;Th&ocirc;i, tụi bay, nh&igrave;n xem, c&ograve;n m&igrave;nh n&oacute;,n&oacute; lại vừa o&aacute;nh một trận to, nh&igrave;n cũng bết b&aacute;t thế kia, chắc kh&ocirc;ng chịu được l&acirc;u, m&igrave;nh m&agrave;i n&oacute; l&uacute;c l&agrave; n&oacute; chết mịa rồi...Đậu xanh, cả c&aacute;i b&atilde;i to thế kia chưa c&oacute; thằng n&agrave;o m&ograve;, nh&igrave;n xem, kiểu g&igrave; hả kiếm được m&oacute;n với dăm ba bộ gi&aacute;p, chẳng lẽ ch&uacute;ng m&agrave;y kh&ocirc;ng muốn.&rdquo;<br />\r\n<br />\r\n<br />\r\nT&ecirc;n to con nhất nh&igrave;n cũng gi&agrave; dặn nhất trong đ&aacute;m kỵ binh l&ecirc;n tiếng. Cả bọn kỵ binh ở đ&acirc;y n&agrave;o c&oacute; thằng n&agrave;o ngu, tất cả đều kh&ocirc;n ranh như s&oacute;i, biết l&agrave; xương cứng th&igrave; ai muốn gặm, nhỡ giết được thằng ch&oacute; hoang kia m&agrave; m&igrave;nh treo th&igrave; c&oacute; bằng h&ograve;a &agrave;, chẳng đ&aacute;ng...Nhưng thằng n&agrave;o cũng tham, c&aacute;i chiến địa kia chưa ai xo&aacute;t, xo&aacute;t l&uacute;c kiểu g&igrave; chả kiếm được m&oacute;n hời, kiếm m&oacute;n tiền lớn về mua đất, mua mấy con n&ocirc; lệ đẹp, khỏi phải đi ch&eacute;m giết...nghĩ th&ocirc;i đ&atilde; sướng. T&ecirc;n kỵ binh n&agrave;y c&ograve;n một c&acirc;u chưa n&oacute;i với bọn kia, m&agrave;i được thằng ch&oacute; n&agrave;y, mang đầu n&oacute; c&ugrave;ng với gi&aacute;p về l&agrave; một m&oacute;n qu&acirc;n c&ocirc;ng lớn, thăng l&ecirc;n l&agrave;m đội trưởng hay ch&aacute;nh ph&oacute; chứ chả chơi, l&uacute;c đấy th&igrave; muốn g&igrave; chả c&oacute;, muốn tiền c&oacute; tiền, muốn g&aacute;i c&oacute; g&aacute;i......<br />\r\n<br />\r\nCả lũ thống nhất xong th&igrave; xoay ngựa lại nhưng lần n&agrave;y ch&uacute;ng kh&ocirc;ng xung phong nữa v&agrave; xoay v&ograve;ng xung quanh Đại Hải. Một lũ mắt hiện l&ecirc;n m&agrave;u xanh lục, như đ&agrave;n s&oacute;i đ&oacute;i tham lam nh&igrave;n chằm chằm v&agrave;o con mồi b&eacute;o bở đang bị ch&uacute;ng v&acirc;y quanh. Nhưng Đại Hải c&oacute; phải con d&ecirc; b&eacute;o của ch&uacute;ng kh&ocirc;ng? Con d&ecirc; n&agrave;y cmn vừa gặm 3 con s&oacute;i, xương cũng kh&ocirc;ng nhả, kh&ocirc;ng dễ m&agrave;i như ch&uacute;ng nghĩ....<br />\r\n<br />\r\n&ldquo;Choeng&rdquo;<br />\r\n<br />\r\nTấm khi&ecirc;n lại cứu Đại Hải th&ecirc;m một mạng, tay tr&aacute;i hắn đau nhức lợi hại. Đậu xanh, mấy thằng Chi&ecirc;m n&agrave;y đ&atilde; vờn hắn được 15 ph&uacute;t rồi, ch&uacute;ng cưỡi ngựa xoay xung quanh hắn, thi thoảng sẽ c&oacute; một t&ecirc;n lao v&agrave;o cắn trộm rồi r&uacute;t ra ngay, kh&ocirc;ng d&acirc;y dưa bẩn thỉu d&ugrave; chỉ 1 ch&uacute;t....C&aacute;ch m&agrave;i n&agrave;y của ch&uacute;ng qu&aacute; lợi hại, Đại Hải c&oacute; khỏe đến đ&acirc;u đi chăng nữa m&agrave; cứ bị m&agrave;i thế n&agrave;y th&igrave; cũng xong, l&uacute;c n&agrave;o cũng phải căng thẳng, chăm ch&uacute; trước những đợt tấn c&ocirc;ng bất ngờ của qu&acirc;n Chi&ecirc;m, chỉ cần sơ sẩy một ch&uacute;t th&ocirc;i l&agrave; mất đầu như chơi. 15 ph&uacute;t hắn chỉ xử th&ecirc;m được một t&ecirc;n, đang nằm kia r&ecirc;n la hấp hối, ruột non ruột gi&agrave; n&ograve;i hết cả ra.<br />\r\n<br />\r\n&ldquo;Đậu xanh rau m&aacute;&rdquo;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nChửi thề một tiếng, muốn chạy cũng kh&ocirc;ng được, bọn n&agrave;y cứ xoay v&ograve;ng v&ograve;ng, Đại Hải vừa lao ra l&agrave; ch&uacute;ng lại qu&acirc;y cho trở lại, 2 ch&acirc;n ngắn ăn sao được 4 ch&acirc;n ngựa d&agrave;i thế kia. Mịa, cứ thế n&agrave;y chỉ c&oacute; nước chết. Chỉ c&oacute; một thằng treo, vẫn c&ograve;n 7 thằng, ch&uacute;ng n&oacute; vẫn chưa sợ, m&agrave; m&igrave;nh lại nh&igrave;n c&agrave;ng ng&agrave;y c&agrave;ng lừ đừ, ch&uacute;ng n&oacute; chắc chắn sẽ kh&ocirc;ng tha. Đậu xanh, thế m&agrave; lại kh&ocirc;ng xung phong tiếp, b&acirc;y giờ c&oacute; xin h&agrave;ng n&oacute; cũng kh&ocirc;ng tha, thịt của ch&uacute;ng n&oacute; 4 thằng rồi kia m&agrave;....M&aacute;u n&oacute;ng b&acirc;y giờ cũng đ&atilde; nguội hẳn, hắn đang đau đầu l&agrave;m sao tho&aacute;t được khỏi c&aacute;i tử cục n&agrave;y, bị m&agrave;i th&ecirc;m dăm ba chục ph&uacute;t nữa l&agrave; hắn treo thật...<br />\r\n<br />\r\n&ldquo;V&ugrave;....v&ugrave;...v&ugrave;......&rdquo;<br />\r\n<br />\r\n&ldquo;Phập....Phập...Phập.....&rdquo;<br />\r\n<br />\r\n&ldquo;Hự.....H&iacute;iiiii.....&rdquo;<br />\r\n<br />\r\nBỗng từ đ&acirc;u t&ecirc;n bắn ra như mưa, bắn v&agrave;o đ&aacute;m qu&acirc;n Chi&ecirc;m đang xoay v&ograve;ng, người ng&atilde; ngựa đổ, c&oacute; mấy t&ecirc;n đ&atilde; bị bắn th&agrave;nh nh&iacute;m, c&oacute; mấy t&ecirc;n th&igrave; bị hất văng xuống ngựa đang lồm cồm b&ograve; dậy...<br />\r\n<br />\r\n&ldquo;Thời cơ đến!!!!!!!!!!&rdquo;<br />\r\n<br />\r\nNh&igrave;n nhanh xung quanh rồi n&eacute;m mạnh thanh đao của m&igrave;nh ra ngo&agrave;i<br />\r\n<br />\r\n&ldquo;V&iacute;u...Phập&rdquo;<br />\r\n<br />\r\nThanh đao cắm thẳng ngực t&ecirc;n Chi&ecirc;m qu&acirc;n cao to nhất, đang vất vả đỡ t&ecirc;n. Bứt tốc nhanh hơn cả Usain Bolt, Đại Hải lao về ph&iacute;a hắn, k&eacute;o xuống ngựa, r&uacute;t lại đao, leo l&ecirc;n. Tất cả đều như nước chảy m&acirc;y tr&ocirc;i.<br />\r\n<br />\r\n&ldquo;Trựaaaaaaaa&rdquo;<br />\r\n<br />\r\nMiệng vừa qu&aacute;t tay cũng kh&ocirc;ng chậm, ch&eacute;m ngay một ph&aacute;t nhẹ v&agrave;o m&ocirc;ng ngựa, con ngựa h&iacute; lồng l&ecirc;n rồi lao đi mất dạng, tất nhi&ecirc;n l&agrave; mang theo cả Đại Hải rồi, hắn chao đảo su&yacute;t ng&atilde; nhưng may, tay chắc giữ được.<br />\r\n<br />\r\nTho&aacute;t, su&yacute;t nữa th&igrave; treo bởi con ngựa chứ kh&ocirc;ng phải kẻ th&ugrave;, lần sau &iacute;t học theo truyện T&agrave;u, qu&aacute; &acirc;m hiểm, to&agrave;n lừa người, t&iacute; th&igrave; toi......<br />\r\n<br />\r\n&ldquo;HASJFDCFT$A@$#ADYVAG....&rdquo;</p>', '2021-12-01 01:36:29', 12, 1),
(291, 75, 'Kinh Khủng Chân Nhân Tú', '1', '<p>&quot;Muốn trở th&agrave;nh mu&ocirc;n người chú ý đại minh tinh sao?&quot;<br />\r\n<br />\r\n&quot;Muốn một đ&ecirc;m chợt gi&agrave;u, trở th&agrave;nh Nh&acirc;n Thượng Nh&acirc;n sao?&quot;<br />\r\n<br />\r\n&quot;Muốn c&oacute; v&ocirc; số fan sao?&quot;<br />\r\n<br />\r\n&quot;Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng chờ đợi ngươi gia nhập. Xin h&atilde;y chuẩn bị ghi danh đi.&quot;<br />\r\n<br />\r\nTrong căn ph&ograve;ng đi thu&ecirc;, ngồi ở tr&ecirc;n ghế sa lon, nh&igrave;n điện thoại di động b&ecirc;n trong app, Di&ecirc;u Thi&ecirc;n Qu&acirc;n vẻ mặt phẫn nộ.<br />\r\n<br />\r\nC&aacute;i n&agrave;y t&ecirc;n l&agrave; Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng app, tr&ecirc;n thực tế l&agrave; cha mẹ của hắn thanh to&aacute;n.<br />\r\n<br />\r\nL&uacute;c trước t&ecirc;n gọi Quần Tinh T&uacute; Tr&agrave;ng, thượng tuyến sau đ&oacute; một đ&ecirc;m bạo nổ hỏa. Hấp dẫn rất nhiều diễn vi&ecirc;n, thậm ch&iacute; c&oacute; rất nhiều L&atilde;o H&iacute; Cốt gia nhập li&ecirc;n minh.<br />\r\n<br />\r\nC&oacute; thể bởi v&igrave; đắc tội nh&acirc;n, c&aacute;i n&agrave;y app bị buộc hạ gi&aacute;. M&agrave; hắn cũng bị l&agrave;ng giải tr&iacute; đuổi ra khỏi.<br />\r\n<br />\r\nSau đ&oacute; ở nơi n&agrave;y Quần Tinh T&uacute; Tr&agrave;ng lần nữa chưng b&agrave;y, lại trở th&agrave;nh Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng.<br />\r\n<br />\r\nRất r&otilde; r&agrave;ng, c&oacute; người cướp đi cha mẹ trọn đời t&acirc;m huyết, ph&aacute; hủy bọn họ to&agrave;n bộ hi vọng.<br />\r\n<br />\r\nNhưng để cho Di&ecirc;u Thi&ecirc;n Qu&acirc;n thống khổ, hay lại l&agrave; phụ Mẫu Thần b&iacute; mất t&iacute;ch.<br />\r\n<br />\r\nỞ trước khi mất t&iacute;ch dạ, mẫu th&acirc;n gọi điện thoại cho hắn, ng&ocirc;n ngữ ch&iacute;nh giữa tr&agrave;n đầy kinh ho&agrave;ng.<br />\r\n<br />\r\n&quot;Con trai, coi như ta y&ecirc;u cầu ngươi, v&ocirc; luận như thế n&agrave;o, cũng kh&aacute;c tới t&igrave;m ch&uacute;ng ta.&quot;<br />\r\n<br />\r\n&quot;C&ograve;n nữa, đừng đi Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng. Ng&agrave;n vạn lần chớ đi.&quot;<br />\r\n<br />\r\nC&ograve;n kh&ocirc;ng chờ Di&ecirc;u Thi&ecirc;n Qu&acirc;n hỏi, mẫu th&acirc;n liền c&uacute;p điện thoại.<br />\r\n<br />\r\nSau đ&oacute; ở nơi n&agrave;y, cha mẹ của hắn giống như bốc hơi khỏi thế gian như thế, Di&ecirc;u Thi&ecirc;n Qu&acirc;n nổi đi&ecirc;n như thế t&igrave;m một c&aacute;i nguyệt, cha mẹ giống như bốc hơi khỏi thế gian một c&aacute;i dạng.<br />\r\n<br />\r\n&quot;Cha mẹ mất t&iacute;ch, khẳng định với Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng c&oacute; quan hệ!&quot;<br />\r\n<br />\r\n&quot;Ta nhất định phải điều tra ra, Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng kết quả c&oacute; c&aacute;i g&igrave; nội mạc!&quot;<br />\r\n<br />\r\nCầm điện thoại di động l&ecirc;n, Di&ecirc;u Thi&ecirc;n Qu&acirc;n quyết định ghi danh, trở th&agrave;nh Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng diễn vi&ecirc;n.<br />\r\n<br />\r\nChỉ c&oacute; như vậy, mới c&oacute; thể t&igrave;m được ch&acirc;n tướng.<br />\r\n<br />\r\nV&ocirc; luận như thế n&agrave;o, hắn cũng phải b&aacute;o th&ugrave;, muốn cho Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng chủ nh&acirc;n trả gi&aacute; thật lớn.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Ch&uacute;c mừng ngươi ghi danh th&agrave;nh c&ocirc;ng, mời ở buổi chiều 3 điểm trước, trước hướng T&acirc;y Nhai cư d&acirc;n lầu số 103 thử sức.&quot;<br />\r\n<br />\r\nHo&agrave;ng Tuyền T&uacute; Tr&agrave;ng, ph&aacute;t ra như vậy nhắc nhở.<br />\r\n<br />\r\n&quot;T&acirc;y Nhai Ngọc Long tiểu khu cư d&acirc;n lầu số 103 sao?&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n nh&igrave;n một c&aacute;i điện thoại di động n&oacute;i.<br />\r\n<br />\r\nT&acirc;y Nhai c&aacute;ch nơi n&agrave;y cũng kh&ocirc;ng xa, v&ocirc; luận như thế n&agrave;o đi qua nh&igrave;n một ch&uacute;t lại n&oacute;i.<br />\r\n<br />\r\nMười ph&uacute;t sau, hắn đi bộ tới đến T&acirc;y Nhai, đi tới Ngọc Long tiểu khu, lại thấy được một c&aacute;i nh&agrave; t&ograve;a cũ n&aacute;t nh&agrave; lầu.<br />\r\n<br />\r\nThử sức địa điểm, lại l&agrave; nơi n&agrave;y, Di&ecirc;u Thi&ecirc;n Qu&acirc;n khẽ cau m&agrave;y, nhưng vẫn l&agrave; đi v&agrave;o.<br />\r\n<br />\r\nNơi n&agrave;y kh&ocirc;ng c&oacute; bất kỳ ai, h&igrave;nh như l&agrave; ph&aacute; bỏ v&agrave; dời đi lầu. Chung quanh thậm ch&iacute; kh&ocirc;ng c&oacute; một người.<br />\r\n<br />\r\nC&aacute;i n&agrave;y l&agrave;m cho Di&ecirc;u Thi&ecirc;n Qu&acirc;n thập phần ho&agrave;i nghi, ch&iacute;nh m&igrave;nh c&oacute; phải hay kh&ocirc;ng l&agrave; bị người tr&ecirc;u.<br />\r\n<br />\r\nChờ hắn đi tới số 103 thời điểm, ch&iacute;nh l&agrave; năm tầng ph&iacute;a t&acirc;y. Cửa đ&oacute;ng chặt đến.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n d&ograve; x&eacute;t t&iacute;nh g&otilde; cửa một c&aacute;i, sau đ&oacute; cửa được mở ra, một người c&oacute; m&aacute;i t&oacute;c hoa r&acirc;m l&atilde;o nh&acirc;n mở cửa.<br />\r\n<br />\r\n&quot;Ngươi cũng l&agrave; đến thử sức đi.&quot;<br />\r\n<br />\r\nL&atilde;o ni&ecirc;n nam tử n&oacute;i.<br />\r\n<br />\r\n&quot; Ừ.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n gật đầu một c&aacute;i.<br />\r\n<br />\r\n&quot;V&agrave;o đi, ta cũng l&agrave; qu&aacute; tới thử k&iacute;nh.&quot; L&atilde;o nh&acirc;n cười n&oacute;i.<br />\r\n<br />\r\nMặc d&ugrave; trong l&ograve;ng nghi hoặc, Di&ecirc;u Thi&ecirc;n Qu&acirc;n hay lại l&agrave; đi v&agrave;o cửa ph&ograve;ng. K&egrave;m theo ph&ograve;ng cửa đ&oacute;ng k&iacute;n, hắn nh&igrave;n về ph&iacute;a trong ph&ograve;ng.<br />\r\n<br />\r\nTrong ph&ograve;ng rất u tối, cũng kh&ocirc;ng c&oacute; &aacute;nh đ&egrave;n.<br />\r\n<br />\r\nTr&ecirc;n ghế sa lon ngồi rồi một người mặc đường v&acirc;n &aacute;o l&ocirc;ng l&atilde;o ni&ecirc;n phụ nữ, trừ lần đ&oacute; ra, c&ograve;n c&oacute; một c&aacute;i th&acirc;n cao gầy, c&uacute;i đầu, t&oacute;c viết ngo&aacute;y sắc mặt t&aacute;i nhợt nữ tử.<br />\r\n<br />\r\n&quot;Mọi người đều đến đ&ocirc;ng đủ, vậy th&igrave; nh&igrave;n một ch&uacute;t điện thoại di động đi.&quot; L&atilde;o nh&acirc;n đột nhi&ecirc;n mở miệng n&oacute;i.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n sửng sốt một ch&uacute;t, &aacute;nh mắt nh&igrave;n về ph&iacute;a điện thoại di động. Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng l&uacute;c, đ&atilde; ban bố một c&aacute;i nhiệm vụ.<br />\r\n<br />\r\n&quot; Ai Mưu S&aacute;t &Aacute;i T&igrave;nh Của Ta Rồi, Ch&acirc;n Nh&acirc;n T&uacute; mở m&agrave;n.&quot;<br />\r\n<br />\r\n&quot;Nội dung cốt truyện giới thiệu t&oacute;m tắt: Nữ chủ Vi&ecirc;n H&acirc;n gần đ&acirc;y sự nghiệp kh&ocirc;ng thuận, &aacute;i t&igrave;nh cũng kh&ocirc;ng thuận. N&agrave;ng lu&ocirc;n cảm thấy trượng phu xuất quỹ. B&agrave; b&agrave; với c&ocirc;ng c&ocirc;ng đối với n&agrave;ng, giống vậy đối với n&agrave;ng thập phần kh&ocirc;ng tốt. N&agrave;ng trong đầu, lu&ocirc;n c&oacute; thập phần t&agrave;n bạo &yacute; tưởng. Hơn nữa loại t&acirc;m t&igrave;nh n&agrave;y c&agrave;ng ng&agrave;y c&agrave;ng nghi&ecirc;m trọng. Tr&ugrave;ng hợp, trượng phu điện thoại di động n&oacute;i chuyện phiếm ghi ch&eacute;p, đưa tới n&agrave;ng sự ch&uacute; &yacute;...&quot;<br />\r\n<br />\r\nNh&igrave;n điện thoại di động kh&ocirc;ng đầu kh&ocirc;ng đu&ocirc;i kịch bản, trong l&ograve;ng Di&ecirc;u Thi&ecirc;n Qu&acirc;n m&ecirc; mang.<br />\r\n<br />\r\nNhưng l&uacute;c n&agrave;y Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng l&agrave;m ra n&oacute;i r&otilde;.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; một trận Ch&acirc;n Nh&acirc;n T&uacute;, c&aacute;c ngươi biểu diễn bị rất nhiều người thưởng thức, mỗi người c&aacute;c ngươi ngẫu nhi&ecirc;n ph&acirc;n phối th&acirc;n phận, dựa theo ph&acirc;n phối th&acirc;n phận ngẫu hứng biểu diễn. Biểu diễn c&agrave;ng ch&acirc;n thực, lại c&agrave;ng sẽ phải chịu người xem hoan ngh&ecirc;nh. Tiền đ&oacute;ng phim th&igrave; cũng c&agrave;ng cao.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; lời kịch, hết thảy đều phải dựa v&agrave;o ch&iacute;nh m&igrave;nh sao?&quot; Nh&igrave;n một c&aacute;i điện thoại di động, Di&ecirc;u Thi&ecirc;n Qu&acirc;n tự lẩm bẩm, nội t&acirc;m lại tr&agrave;n ngập hưng phấn.<br />\r\n<br />\r\nNếu b&agrave;n về diễn kỹ, hắn tuyệt đối sẽ kh&ocirc;ng thua bất kỳ người n&agrave;o.<br />\r\n<br />\r\nNhững người khác cũng đều thấy được điện thoại di động nhắc nhở, t&oacute;c trắng l&atilde;o nh&acirc;n nh&igrave;n bốn ph&iacute;a, trong miệng tự lẩm bẩm: &quot;Kỳ qu&aacute;i, nếu l&agrave; Ch&acirc;n Nh&acirc;n T&uacute;, như vậy m&aacute;y thu h&igrave;nh ở nơi n&agrave;o?&quot;<br />\r\n<br />\r\n&quot;M&aacute;y thu h&igrave;nh khẳng định kh&ocirc;ng thể để cho ngươi thấy, nếu kh&ocirc;ng th&igrave; kh&ocirc;ng ch&acirc;n thật.&quot; L&atilde;o ni&ecirc;n người phụ nữ n&oacute;i.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y cũng kh&ocirc;ng sai.&quot; L&atilde;o nh&acirc;n gật đầu một c&aacute;i.<br />\r\n<br />\r\nĐang l&uacute;c n&agrave;y, điện thoại di động reo đứng l&ecirc;n: &quot;Ngẫu nhi&ecirc;n th&acirc;n phận ph&acirc;n phối trung...&quot;<br />\r\n<br />\r\nĐiện thoại của Di&ecirc;u Thi&ecirc;n Qu&acirc;n b&ecirc;n trong rất sắp xuất hiện rồi một c&aacute;i nhắc nhở: &quot;Th&acirc;n phận của ngươi v&igrave; Nghi&ecirc;m Nguy&ecirc;n Ch&iacute;nh, ngươi l&agrave; Vi&ecirc;n H&acirc;n trượng phu, ngươi sự nghiệp c&oacute; th&agrave;nh, kh&iacute; vũ hi&ecirc;n dương, l&agrave; c&ocirc;ng ty cao quản. Ni&ecirc;n thi&ecirc;́u nhiều tiền. Ngươi t&iacute;nh c&aacute;ch n&oacute;ng nảy, t&iacute;nh kh&iacute; rất k&eacute;m cỏi. Ngươi c&oacute; một người sinh vi&ecirc;n đại học t&igrave;nh nh&acirc;n Ng&ocirc; Lỵ Lỵ. Ngươi vẫn muốn vứt bỏ Vi&ecirc;n H&acirc;n c&aacute;i n&agrave;y đi&ecirc;n b&agrave; nương.&quot;<br />\r\n<br />\r\n&quot;Ngươi diễn dịch mục ti&ecirc;u: Giấu giếm ch&iacute;nh m&igrave;nh Tiểu Tam, giết chết Vi&ecirc;n H&acirc;n, hoặc l&agrave; với Vi&ecirc;n H&acirc;n ly h&ocirc;n.&quot;<br />\r\n<br />\r\nNh&igrave;n tới điện thoại di động nhiệm vụ, nội t&acirc;m của Di&ecirc;u Thi&ecirc;n Qu&acirc;n hơi sửng sờ, xem ra hắn nh&acirc;n vật n&agrave;y, hẳn l&agrave; một c&aacute;i vứt bỏ th&ecirc; tử, l&aacute;nh tầm t&acirc;n hoan cặn b&atilde; nam.<br />\r\n<br />\r\nMặc d&ugrave; đối với như vậy nh&acirc;n vật c&oacute; ch&uacute;t bất m&atilde;n, nhưng Di&ecirc;u Thi&ecirc;n Qu&acirc;n c&ograve;n l&agrave; tin t&acirc;m tr&agrave;n đầy.<br />\r\n<br />\r\n&quot;Mười ph&uacute;t sau, Ch&acirc;n Nh&acirc;n T&uacute; ch&iacute;nh thức bắt đầu. Xin chuẩn bị kỹ lưỡng.&quot;<br />\r\n<br />\r\nK&egrave;m theo Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng nhắc nhở, tất cả mọi người trở n&ecirc;n nghi&ecirc;m t&uacute;c. Bắt đầu suy nghĩ, tiếp theo n&ecirc;n n&oacute;i c&aacute;i g&igrave; lời kịch.<br />\r\n<br />\r\nD&ugrave; sao cũng l&agrave; ngẫu hứng biểu diễn, tất cả mọi người rất nghi&ecirc;m t&uacute;c.<br />\r\n<br />\r\nRất nhanh thập phần chung đi qua, k&egrave;m theo Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng nhắc nhở.<br />\r\n<br />\r\nTr&agrave;ng n&agrave;y Ch&acirc;n Nh&acirc;n T&uacute; bắt đầu!<br />\r\n<br />\r\nTối động đậy trước l&agrave; nữ nh&acirc;n, n&agrave;ng đen v&agrave;nh mắt, to&agrave;n bộ tinh thần kh&ocirc;ng dao động, &aacute;nh mắt t&agrave;n bạo nh&igrave;n Di&ecirc;u Thi&ecirc;n Qu&acirc;n: &quot;L&atilde;o c&ocirc;ng, ngươi giải th&iacute;ch cho ta một ch&uacute;t, số điện thoại n&agrave;y rốt cuộc l&agrave; chuyện g&igrave; xảy ra!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nỞ trong tay n&agrave;ng, l&agrave; một c&aacute;i lật điện thoại của c&aacute;i. L&agrave; đ&atilde; sớm đ&igrave;nh sản bảng hiệu.<br />\r\n<br />\r\nNhập vai diễn c&ograve;n rất nhanh.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n cũng kh&ocirc;ng cam chịu yếu thế, tr&ecirc;n mặt nhiều hơn một tia kh&ocirc;ng ki&ecirc;n nhẫn, cả người lộ ra lạnh l&ugrave;ng lại lạnh gi&aacute;: &quot;Ngươi lại nghi thần nghi quỷ, rốt cuộc c&oacute; phiền hay kh&ocirc;ng? Ta đ&atilde; n&oacute;i rất nhiều lần rồi, đ&acirc;y l&agrave; ta đồng bạn l&agrave;m ăn.&quot;<br />\r\n<br />\r\n&quot;Đồng bạn l&agrave;m ăn?&quot; &Aacute;nh mắt cuả Vi&ecirc;n H&acirc;n tr&agrave;n đầy lạnh gi&aacute;, n&agrave;ng cười lạnh một tiếng, chỉ đến m&agrave;n h&igrave;nh điện thoại di động n&oacute;i: &quot;Điều tra ta qu&aacute; ngươi n&oacute;i chuyện phiếm ghi ch&eacute;p, ngươi mỗi đ&ecirc;m ng&agrave;y c&ugrave;ng với n&agrave;ng gọi điện thoại. Đừng cho l&agrave; ta kh&ocirc;ng biết ngươi buổi tối ngồi ở tr&ecirc;n bồn cầu, c&ugrave;ng với n&agrave;ng lời ngon tiếng ngọt!&quot;<br />\r\n<br />\r\n&quot;Điều tra ngươi ta?&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n thẹn qu&aacute; th&agrave;nh giận, &aacute;nh mắt tr&agrave;n đầy hốt hoảng.<br />\r\n<br />\r\nCực kỳ rất nhanh hắn tỉnh t&aacute;o lại, &aacute;nh mắt tr&agrave;n đầy khinh bỉ: &quot;Ta với ngươi kh&ocirc;ng lời n&oacute;i c&oacute; thể n&oacute;i! Nếu như ngươi lại ho&agrave;i nghi như vậy ta, cuộc sống n&agrave;y kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o sống!&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng được, ngươi phải đem lời n&oacute;i r&otilde; r&agrave;ng ra! Nữ nh&acirc;n kia rốt cuộc l&agrave; ai?&quot; Vi&ecirc;n H&acirc;n k&eacute;o lại Di&ecirc;u Thi&ecirc;n Qu&acirc;n c&aacute;nh tay, kh&ocirc;ng tha thứ.<br />\r\n<br />\r\nN&agrave;ng m&agrave;u da kh&ocirc; h&eacute;o, tinh thần uể oải kh&ocirc;ng dao động. &Aacute;nh mắt lại tr&agrave;n đầy đi&ecirc;n cuồng.<br />\r\n<br />\r\nNh&igrave;n n&agrave;ng d&aacute;ng vẻ, giống như rất l&acirc;u kh&ocirc;ng hảo hảo ngủ, cả người vẻ mặt nhiều hơn một tia đi&ecirc;n cuồng.<br />\r\n<br />\r\nLoại n&agrave;y diễn kỹ, thật l&agrave; tuyệt!<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n cảm thấy uy hiếp lớn lao.<br />\r\n<br />\r\nHắn vốn cho l&agrave;, thế hệ trẻ, c&ograve;n kh&ocirc;ng c&oacute; bao nhi&ecirc;u người diễn kỹ c&oacute; thể vượt qua ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\nB&acirc;y giờ nh&igrave;n lại, người mạnh c&ograve;n c&oacute; người mạnh hơn a!<br />\r\n<br />\r\nKh&ocirc;ng được, phải lấy lại danh dự.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n nhỏ nhắm lại con mắt, chờ hắn trợn mở con mắt, &aacute;nh mắt tr&agrave;n đầy kh&ocirc;ng ki&ecirc;n nhẫn c&ugrave;ng t&agrave;n bạo: &quot;Ngươi xong chưa sao? Tiện nữ nh&acirc;n.&quot;<br />\r\n<br />\r\nTrong l&uacute;c n&oacute;i chuyện, hắn đ&atilde; một bạt tai đ&aacute;nh tới.<br />\r\n<br />\r\nKh&ocirc;ng nghĩ tới Vi&ecirc;n H&acirc;n kh&ocirc;ng n&eacute; kh&ocirc;ng tr&aacute;nh, gắng gượng thừa nhận rồi một bạt tai n&agrave;y.<br />\r\n<br />\r\nMặc d&ugrave; Di&ecirc;u Thi&ecirc;n Qu&acirc;n cũng kh&ocirc;ng c&oacute; d&ugrave;ng sức, nhưng Vi&ecirc;n H&acirc;n bởi v&igrave; kh&ocirc;ng c&oacute; ph&ograve;ng bị, hay lại l&agrave; lảo đảo một c&aacute;i t&eacute; ng&atilde; tr&ecirc;n đất.<br />\r\n<br />\r\nN&agrave;ng bụm mặt, tựa hồ sớm đ&atilde; th&agrave;nh th&oacute;i quen trượng phu gia bạo.</p>', '2021-12-01 14:48:36', 11, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(292, 75, 'Làm Cặn Bã Nam Thật Tốt', '2', '<p>&Aacute;nh mắt cuả n&agrave;ng tr&agrave;n đầy o&aacute;n hận n&oacute;i: &quot;L&atilde;o c&ocirc;ng, ngươi liền n&oacute;i cho ta biết, nữ nh&acirc;n kia đến tột c&ugrave;ng l&agrave; ai!&quot;<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n lạnh r&ecirc;n một tiếng, tự nhi&ecirc;n kh&ocirc;ng t&iacute;nh trả lời.<br />\r\n<br />\r\nMột c&aacute;i l&atilde;o th&aacute;i b&agrave; đi ra, &aacute;nh mắt phiền n&atilde;o nh&igrave;n Vi&ecirc;n H&acirc;n h&ocirc;: &quot;Ngươi xong chưa? Cả ng&agrave;y đi&ecirc;n đi&ecirc;n kh&ugrave;ng kh&ugrave;ng. Con của ta cưới ngươi, thật l&agrave; đến t&aacute;m đời huyết m&ocirc;i.&quot;<br />\r\n<br />\r\nN&oacute;i xong n&agrave;ng k&eacute;o qua Di&ecirc;u Thi&ecirc;n Qu&acirc;n, một b&ecirc;n đi về ph&ograve;ng ngủ, vừa hướng con d&acirc;u h&ugrave;ng h&ugrave;ng hổ hổ.<br />\r\n<br />\r\nĐều l&agrave; vai diễn tinh!<br />\r\n<br />\r\nMấy người n&agrave;y diễn kỹ, cũng thập phần cao si&ecirc;u. Cực kỳ Di&ecirc;u Thi&ecirc;n Qu&acirc;n kh&ocirc;ng sợ h&atilde;i ch&uacute;t n&agrave;o.<br />\r\n<br />\r\nNgồi ở tr&ecirc;n ghế sa lon, &aacute;nh mắt của hắn uy nghi&ecirc;m h&ocirc;: &quot;Vi&ecirc;n H&acirc;n, ngươi tới đ&acirc;y cho ta.&quot;<br />\r\n<br />\r\nHắn cũng kh&ocirc;ng c&oacute; k&ecirc;u l&atilde;o b&agrave;, m&agrave; l&agrave; d&ugrave;ng t&ecirc;n của n&agrave;ng, c&agrave;ng lộ vẻ cứng rắn c&ugrave;ng xa c&aacute;ch.<br />\r\n<br />\r\nVi&ecirc;n H&acirc;n đi tới, &aacute;nh mắt th&ecirc; lương nh&igrave;n hắn: &quot;Nữ nh&acirc;n kia l&agrave; ai ? Ngươi phải n&oacute;i cho ta biết!&quot;<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n c&ograve;n kh&ocirc;ng chờ mở miệng, b&ecirc;n cạnh nam nh&acirc;n l&ecirc;n tiếng.<br />\r\n<br />\r\n&Aacute;nh mắt của hắn uy nghi&ecirc;m nh&igrave;n Di&ecirc;u Thi&ecirc;n Qu&acirc;n, b&agrave;n tay đặt ở tr&ecirc;n đầu gối, ngồi ở tr&ecirc;n ghế sa lon cư cao l&acirc;m hạ n&oacute;i: &quot;Con trai, ta tại sao dạy ngươi? Đối đ&atilde;i nữ nh&acirc;n, liền muốn giống như đối đ&atilde;i s&uacute;c sinh như thế!&quot;<br />\r\n<br />\r\n&quot;C&agrave;ng đ&aacute;nh v&agrave;o chỗ chết, nữ nh&acirc;n th&igrave; sẽ c&agrave;ng nghe ngươi lời n&oacute;i.&quot;<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n trợn mắt hốc mồm, bất qu&aacute; hắn cũng biết, hết thảy c&aacute;c thứ n&agrave;y đều l&agrave; đang diễn tr&ograve;.<br />\r\n<br />\r\nKh&ocirc;ng nghi ngờ ch&uacute;t n&agrave;o, c&aacute;i gia đ&igrave;nh n&agrave;y, bao gồm ch&iacute;nh m&igrave;nh đóng vai trượng phu, hay lại l&agrave; nữ chủ, c&ocirc;ng c&ocirc;ng, b&agrave; b&agrave;. Tất cả đều c&oacute; vấn đề.<br />\r\n<br />\r\nNh&acirc;n vật nam ch&iacute;nh cha, đại nam tử chủ nghĩa, v&ocirc; c&ugrave;ng uy nghi&ecirc;m. Nh&acirc;n vật nam ch&iacute;nh mẫu th&acirc;n một mực thi&ecirc;n vị con m&igrave;nh.<br />\r\n<br />\r\nVề phần nh&acirc;n vật nam ch&iacute;nh th&ecirc; tử, tinh thần c&oacute; ch&uacute;t kh&ocirc;ng qu&aacute; b&igrave;nh thường.<br />\r\n<br />\r\nNh&acirc;n vật nam ch&iacute;nh hết lần n&agrave;y tới lần kh&aacute;c lại vừa l&agrave; một c&aacute;i cặn b&atilde; nam, đ&acirc;y l&agrave; c&aacute;i g&igrave; kỳ qu&aacute;i gia đ&igrave;nh a!<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n nghĩ đến đ&acirc;y, lại cho Vi&ecirc;n H&acirc;n một bạt tai, kh&ocirc;ng ki&ecirc;n nhẫn h&ocirc;: &quot;Ta đ&atilde; sớm được đủ ngươi, ch&uacute;ng ta ly h&ocirc;n đi.&quot;<br />\r\n<br />\r\n&quot;Ly h&ocirc;n.&quot; &Aacute;nh mắt cuả Vi&ecirc;n H&acirc;n trừng lớn con mắt, sắc mặt t&agrave;n bạo nh&igrave;n hắn: &quot;Ngươi muốn theo ta ly h&ocirc;n?&quot;<br />\r\n<br />\r\nKh&ocirc;ng biết tại sao, thấy n&agrave;ng &aacute;nh mắt, Di&ecirc;u Thi&ecirc;n Qu&acirc;n cảm gi&aacute;c t&ecirc; cả da đầu. Phảng phất bị thứ g&igrave; để mắt tới đi như thế.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nCực kỳ tr&igrave;nh diễn đến một bước n&agrave;y, Di&ecirc;u Thi&ecirc;n Qu&acirc;n chỉ c&oacute; thể ki&ecirc;n tr&igrave; đến c&ugrave;ng tiếp tục diễn th&ocirc;i.<br />\r\n<br />\r\nHắn đ&oacute;ng vai l&agrave; một c&aacute;i cặn b&atilde; nam, nhất định phải v&ocirc; sỉ.<br />\r\n<br />\r\n&quot;Dĩ nhi&ecirc;n muốn ly h&ocirc;n, xe nh&agrave; ở đều l&agrave; ta, ngươi lập tức c&uacute;t cho ta về nh&agrave; mẹ đẻ đi.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n chỉa về ph&iacute;a n&agrave;ng tức miệng mắng to đến.<br />\r\n<br />\r\n&quot; Đ&uacute;ng, trong nh&agrave; hết thảy, đều l&agrave; con của ta.&quot; L&atilde;o th&aacute;i b&agrave; c&oacute; l&yacute; chẳng sợ n&oacute;i.<br />\r\n<br />\r\n&quot;Con trai, ngươi li&ecirc;n quan đẹp đẽ. Đến lượt xuất ra nam nh&acirc;n kh&iacute; ph&aacute;ch.&quot; Một b&ecirc;n c&ocirc;ng c&ocirc;ng, v&agrave;o l&uacute;c n&agrave;y cũng l&ecirc;n tiếng. Hắn &aacute;nh mắt &acirc;m trầm nh&igrave;n chằm chằm Vi&ecirc;n H&acirc;n, &aacute;nh mắt tr&agrave;n đầy khinh thường.<br />\r\n<br />\r\nĐối mặt đến một nh&agrave; ba người người sở hữu uy hiếp.<br />\r\n<br />\r\n&Aacute;nh mắt cuả Vi&ecirc;n H&acirc;n hung t&agrave;n nh&igrave;n của bọn hắn, cả người đều run rẩy đến: &quot;Ngươi muốn theo ta ly h&ocirc;n, ngươi muốn theo ta ly h&ocirc;n!&quot;<br />\r\n<br />\r\nN&agrave;ng lặp lại những lời n&agrave;y, đến ph&iacute;a sau, cả người đ&atilde; cuồng loạn đứng l&ecirc;n.<br />\r\n<br />\r\nHai tay n&agrave;ng nắm đầu m&igrave;nh da, trong ng&oacute;n tay l&agrave; từng thanh t&oacute;c!<br />\r\n<br />\r\nNh&igrave;n n&agrave;ng đi&ecirc;n đi&ecirc;n kh&ugrave;ng kh&ugrave;ng d&aacute;ng vẻ, Di&ecirc;u Thi&ecirc;n Qu&acirc;n su&yacute;t ch&uacute;t nữa th&igrave; vỗ &aacute;n khen ngợi.<br />\r\n<br />\r\nTốt diễn kỹ, đưa c&aacute;i n&agrave;y tinh thần kh&ocirc;ng b&igrave;nh thường nữ nh&acirc;n, diễn dịch tinh tế!<br />\r\n<br />\r\nNhưng rất nhanh, hắn liền kh&ocirc;ng cười được.<br />\r\n<br />\r\nBởi v&igrave; Vi&ecirc;n H&acirc;n lảo đảo đi tới ph&ograve;ng bếp, sau đ&oacute; lấy ra một c&aacute;i đao nhọn, cứ như vậy t&agrave;n bạo đi tới.<br />\r\n<br />\r\nVi&ecirc;n H&acirc;n hung t&agrave;n vung trong tay đao nhọn, &aacute;nh mắt nh&igrave;n bọn họ, ng&ocirc;n ngữ th&ecirc; lương h&ocirc;: &quot;C&aacute;c ngươi khi dễ ta, ta ở nơi n&agrave;y gia đ&atilde; kh&ocirc;ng vượt qua nổi rồi. Đ&atilde; như vậy, ch&uacute;ng ta kh&ocirc;ng bằng c&ugrave;ng chết đi.&quot;<br />\r\n<br />\r\nNh&igrave;n n&agrave;ng biểu hiện như thế dữ tợn, Di&ecirc;u Thi&ecirc;n Qu&acirc;n sắc mặt hốt hoảng n&oacute;i: &quot;Ngươi muốn l&agrave;m g&igrave;!&quot;<br />\r\n<br />\r\n&quot;Bu&ocirc;ng đao xuống.&quot; L&atilde;o th&aacute;i b&agrave; vọt tới, muốn đoạt lấy Vi&ecirc;n H&acirc;n trong tay đao.<br />\r\n<br />\r\nNhưng sau một khắc, &aacute;nh mắt của Vi&ecirc;n H&acirc;n tho&aacute;ng qua một tia hung quang.<br />\r\n<br />\r\nĐao nhọn x&acirc;u đi xuy&ecirc;n qua, l&atilde;o th&aacute;i b&agrave; n&eacute; tr&aacute;nh kh&ocirc;ng kịp, đao nhọn xuy&ecirc;n qua l&atilde;o th&aacute;i b&agrave; lồng ngực.<br />\r\n<br />\r\nL&atilde;o th&aacute;i b&agrave; k&ecirc;u thảm một tiếng, th&acirc;n thể về ph&iacute;a sau đến đi xuống.<br />\r\n<br />\r\nHuyết, đỏ thẫm m&aacute;u tươi ph&uacute;n ra ngo&agrave;i. Rơi v&agrave;o Vi&ecirc;n H&acirc;n tr&ecirc;n mặt, tr&ecirc;n người.<br />\r\n<br />\r\nTr&ecirc;n mặt n&agrave;ng d&iacute;nh m&aacute;u tươi, nhưng n&agrave;ng nhưng ở cuồng tiếu, giống như địa ngục &aacute;c quỷ.<br />\r\n<br />\r\nV&agrave;o giờ khắc n&agrave;y, Di&ecirc;u Thi&ecirc;n Qu&acirc;n c&ugrave;ng l&atilde;o nam nh&acirc;n to&agrave;n bộ đều ng&acirc;y dại.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; đang diễn xuất a, mau gọi xe cứu thương!&quot; L&atilde;o nam nh&acirc;n vội v&agrave;ng h&ocirc;.<br />\r\n<br />\r\nNhưng l&uacute;c n&agrave;y, &aacute;nh mắt cuả Vi&ecirc;n H&acirc;n hung t&agrave;n nh&igrave;n về ph&iacute;a l&atilde;o nam nh&acirc;n. Trong b&agrave;n tay lần nữa giơ l&ecirc;n đao nhọn.<br />\r\n<br />\r\nLại vừa l&agrave; một đao, trực tiếp xuy&ecirc;n qua m&agrave; qua. L&atilde;o nam nh&acirc;n k&ecirc;u thảm một tiếng, hắn t&oacute;m lấy Vi&ecirc;n H&acirc;n bả vai, &aacute;nh mắt kinh hoảng h&ocirc;: &quot;Ngươi đi&ecirc;n rồi sao? Đ&acirc;y l&agrave; diễn xuất.&quot;<br />\r\n<br />\r\nNhưng hắn thấy, nhưng l&agrave; Vi&ecirc;n H&acirc;n huyết hồng con mắt.<br />\r\n<br />\r\nHung &aacute;c &aacute;nh mắt từ Vi&ecirc;n H&acirc;n đ&aacute;y mắt chợt l&oacute;e l&ecirc;n, để cho người ta lạnh cả người.<br />\r\n<br />\r\nL&atilde;o nam nh&acirc;n căn bản kh&ocirc;ng kịp phản kh&aacute;ng, Vi&ecirc;n H&acirc;n liền r&uacute;t ra đao nhọn, lại ghim mấy đao.<br />\r\n<br />\r\nM&aacute;u me t&igrave;nh cảnh, đem Di&ecirc;u Thi&ecirc;n Qu&acirc;n sợ cho&aacute;ng v&aacute;ng.<br />\r\n<br />\r\nTại sao c&oacute; thể như vậy? R&otilde; r&agrave;ng chỉ l&agrave; một trận Ch&acirc;n Nh&acirc;n T&uacute;, kết quả cho tới b&acirc;y giờ, đ&atilde; kh&ocirc;ng c&aacute;ch n&agrave;o v&atilde;n hồi!<br />\r\n<br />\r\nNh&igrave;n l&atilde;o nam nh&acirc;n ng&atilde; xuống th&acirc;n thể, Vi&ecirc;n H&acirc;n r&uacute;t ra nhuốm m&aacute;u đao nhọn, &aacute;nh mắt quay lại.<br />\r\n<br />\r\nN&agrave;ng &aacute;nh mắt cuả hung t&agrave;n, để cho người ta h&iacute;t thở kh&ocirc;ng th&ocirc;ng.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n run lập cập về ph&iacute;a sau chạy tới, bước ch&acirc;n hắn ph&ugrave; mềm mại, &aacute;nh mắt v&ocirc; c&ugrave;ng kinh ho&agrave;ng.<br />\r\n<br />\r\nLảo đảo, hắn đi tới cửa, muốn muốn mở ra m&ocirc;n, lại ph&aacute;t hiện cửa bị vững v&agrave;ng kh&oacute;a.<br />\r\n<br />\r\nHắn liều mạng đưa tay ra, k&eacute;o chốt cửa, lại căn bản kh&ocirc;ng mở ra.<br />\r\n<br />\r\nL&uacute;c n&agrave;y tiếng bước ch&acirc;n vang l&ecirc;n, Vi&ecirc;n H&acirc;n b&oacute;ng người chậm r&atilde;i đi tới.<br />\r\n<br />\r\nTr&ecirc;n người n&agrave;ng khắp nơi đều l&agrave; m&aacute;u tươi, kh&ocirc;ng thấy r&otilde; vẻ mặt tr&ecirc;n mặt, cặp kia đồng tử tr&agrave;n đầy hung &aacute;c c&ugrave;ng giận dữ.<br />\r\n<br />\r\n&quot;Đừng... Kh&aacute;c trở lại.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n đưa tay ra c&aacute;nh tay, &aacute;nh mắt cầu khẩn nh&igrave;n n&agrave;ng.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nMặc d&ugrave; kh&ocirc;ng biết n&agrave;ng nổi đi&ecirc;n l&agrave;m g&igrave;, c&oacute; thể dưới mắt, Di&ecirc;u Thi&ecirc;n Qu&acirc;n căn bản kh&ocirc;ng c&oacute; biện ph&aacute;p ch&uacute;t n&agrave;o.<br />\r\n<br />\r\n&quot;Nữ nh&acirc;n kia l&agrave; ai ?&quot; &Aacute;nh mắt cuả Vi&ecirc;n H&acirc;n hung t&agrave;n nh&igrave;n ta, d&ugrave;ng kh&agrave;n kh&agrave;n th&ecirc; lương thanh &acirc;m hỏi.<br />\r\n<br />\r\nNh&igrave;n n&agrave;ng khu&ocirc;n mặt, Di&ecirc;u Thi&ecirc;n Qu&acirc;n đột nhi&ecirc;n ng&acirc;y dại.<br />\r\n<br />\r\nB&acirc;y giờ chẳng lẽ ph&aacute;t sinh hết thảy, cũng l&agrave; v&igrave; vậy nữ nh&acirc;n nhập vai diễn qu&aacute; s&acirc;u? Loại t&igrave;nh huống n&agrave;y, hắn nghe cha n&oacute;i qua.<br />\r\n<br />\r\nC&oacute; ch&uacute;t diễn vi&ecirc;n nhập vai diễn qu&aacute; s&acirc;u, thật l&acirc;u cũng kh&ocirc;ng c&oacute; kh&ocirc;i phục như cũ. Cho n&ecirc;n mới l&agrave;m ra cực đoan h&agrave;nh vi.<br />\r\n<br />\r\nNếu như l&agrave; như vậy, như vậy phối hợp n&agrave;ng diễn xuất l&agrave; tốt.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n h&aacute; miệng, tựu muốn đem Tiểu Tam Ng&ocirc; Lỵ Lỵ n&oacute;i ra.<br />\r\n<br />\r\nNhưng khi hắn muốn l&uacute;c mở miệng sau khi, trong l&ograve;ng đột nhi&ecirc;n hiện l&ecirc;n l&ecirc;n một ch&uacute;t bất an.<br />\r\n<br />\r\nNếu như hắn thật n&oacute;i ra c&aacute;i n&agrave;y Tiểu Tam, như vậy Vi&ecirc;n H&acirc;n c&oacute; thể hay kh&ocirc;ng dưới cơn n&oacute;ng giận giết chết hắn?<br />\r\n<br />\r\nB&acirc;y giờ Vi&ecirc;n H&acirc;n tinh thần, đ&atilde; rất kh&ocirc;ng ổn định rồi.<br />\r\n<br />\r\nKh&ocirc;ng thể lại k&iacute;ch th&iacute;ch n&agrave;ng.<br />\r\n<br />\r\n&quot;N&oacute;i mau!&quot; Vi&ecirc;n H&acirc;n đi&ecirc;n cuồng nh&igrave;n hắn, tay nắm giữ ở đao nhọn, t&ugrave;y thời muốn đ&acirc;m chết hắn.<br />\r\n<br />\r\nỞ thời khắc nguy cấp n&agrave;y, Di&ecirc;u Thi&ecirc;n Qu&acirc;n cắn răng một c&aacute;i, quyết định liều mạng.<br />\r\n<br />\r\nN&ecirc;n ta biểu diễn l&uacute;c!<br />\r\n<br />\r\n&quot;Căn bản kh&ocirc;ng c&oacute; nữ nh&acirc;n kia!&quot; &Aacute;nh mắt của hắn cười thảm, tr&ecirc;n mặt tr&agrave;n đầy c&ocirc; đơn.<br />\r\n<br />\r\n&quot;Ngươi n&oacute;i bậy!&quot; Vi&ecirc;n H&acirc;n nghỉ n&agrave;y b&ecirc;n trong địa h&ocirc;: &quot;Nữ nh&acirc;n kia l&agrave; ngươi Tiểu Tam, ngươi phản bội ta, lựa chọn n&agrave;ng!&quot;<br />\r\n<br />\r\n&quot;Nếu quả thật l&agrave; như vậy, ngươi liền giết ta được rồi. Nếu như ngươi c&oacute; thể vui vẻ lời n&oacute;i.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n si ngốc nh&igrave;n n&agrave;ng, &aacute;nh mắt tr&agrave;n đầy &ocirc;n nhu.<br />\r\n<br />\r\nNh&igrave;n hắn lần đầu ti&ecirc;n &ocirc;n nhu, Vi&ecirc;n H&acirc;n hốt hoảng lắc lắc đầu, hai tay nắm da đầu, &aacute;nh mắt đi&ecirc;n h&ocirc;: &quot;Kh&ocirc;ng c&oacute; chuyện n&agrave;y! Ch&iacute;nh l&agrave; ngươi phản bội ta!&quot;</p>', '2021-12-01 14:48:58', 6, 1),
(293, 75, 'Nên Ta Ra Sân Biểu Diễn', '3', '<p>&quot;C&uacute; điện thoại kia d&atilde;y số, căn bản liền kh&ocirc;ng phải l&agrave; c&aacute;i g&igrave; Tiểu Tam. M&agrave; l&agrave; bệnh t&acirc;m thần viện Ng&ocirc; chủ nhiệm điện thoại.&quot;<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n thống khổ c&uacute;i đầu xuống, nước mắt từ hốc mắt l&agrave;m giữa d&ograve;ng chảy xuống.<br />\r\n<br />\r\nHắn thất hồn lạc ph&aacute;ch nh&igrave;n m&igrave;nh th&ecirc; tử, sắc mặt nhiều vẻ khổ sở nụ cười.<br />\r\n<br />\r\n&quot;Ngươi chẳng lẽ kh&ocirc;ng c&oacute; ph&aacute;t hiện sao? Ngươi sớm đ&atilde; c&oacute; bệnh t&acirc;m thần ph&acirc;n liệt rồi. Ngươi một mực suy nghĩ chủ quan b&ecirc;n cạnh ta c&oacute; một c&aacute;i Tiểu Tam. Thực ra c&aacute;i kia Tiểu Tam, kh&ocirc;ng l&agrave; người kh&aacute;c. Ch&iacute;nh l&agrave; ch&iacute;nh ngươi!&quot;<br />\r\n<br />\r\n&quot;Ngươi lu&ocirc;n cảm thấy ta b&ecirc;n ngo&agrave;i c&oacute; người, người kia l&agrave; ch&iacute;nh ngươi!&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng, kh&ocirc;ng thể n&agrave;o!&quot; Vi&ecirc;n H&acirc;n sắc nhọn g&agrave;o l&ecirc;n. N&agrave;ng lắc lắc đầu, &aacute;nh mắt hung t&agrave;n nh&igrave;n hắn: &quot;Kh&ocirc;ng thể n&agrave;o! Kh&ocirc;ng c&oacute; chuyện n&agrave;y!&quot;<br />\r\n<br />\r\n&quot;Ta buổi tối ở trong nh&agrave; cầu, len l&eacute;n cho Ng&ocirc; chủ nhiệm gọi điện thoại, l&agrave; v&igrave; đem ngươi đưa v&agrave;o bệnh viện t&acirc;m thần. Cho ngươi tiếp nhận chữa trị!&quot;<br />\r\n<br />\r\nN&oacute;i tới chỗ n&agrave;y, &aacute;nh mắt cuả Di&ecirc;u Thi&ecirc;n Qu&acirc;n vẻ mặt nh&igrave;n n&agrave;ng, sắc mặt tr&agrave;n đầy thống khổ.<br />\r\n<br />\r\nHắn chỉ chỉ ph&ograve;ng ngủ phương hướng, sắc mặt tuyệt vọng n&oacute;i: &quot;C&oacute; thể ngươi cũng đ&atilde; l&agrave;m g&igrave;? Ngươi đem ba giết, đem mụ mụ cũng giết!&quot;<br />\r\n<br />\r\nN&oacute;i tới chỗ n&agrave;y, Di&ecirc;u Thi&ecirc;n Qu&acirc;n thống khổ bưng k&iacute;n đầu.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng, ngươi gạt ta, ngươi gạt ta!&quot; Vi&ecirc;n H&acirc;n th&eacute;t l&ecirc;n, &aacute;nh mắt c&agrave;ng ng&agrave;y c&agrave;ng đi&ecirc;n cuồng.<br />\r\n<br />\r\nNhưng l&uacute;c n&agrave;y, Di&ecirc;u Thi&ecirc;n Qu&acirc;n lại chợt nh&agrave;o tới, &ocirc;m lấy th&acirc;n thể n&agrave;ng.<br />\r\n<br />\r\nGiờ khắc n&agrave;y, &aacute;nh mắt cuả Vi&ecirc;n H&acirc;n, đột nhi&ecirc;n trở n&ecirc;n v&ocirc; c&ugrave;ng mờ mịt.<br />\r\n<br />\r\n&quot;Th&acirc;n &aacute;i, giết ta đi. Ngươi l&agrave; bệnh t&acirc;m thần nh&acirc;n, sẽ kh&ocirc;ng bị xử tử h&igrave;nh.&quot;<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n nh&igrave;n chăm ch&uacute; n&agrave;ng đ&ocirc;i mắt, &aacute;nh mắt tr&agrave;n đầy &ocirc;n nhu. Nước mắt chảy xu&ocirc;i xuống.<br />\r\n<br />\r\n&Aacute;nh mắt cuả Vi&ecirc;n H&acirc;n phức tạp nh&igrave;n hắn, v&agrave;o giờ khắc n&agrave;y, n&agrave;ng phảng phất h&ograve;a tan ở Di&ecirc;u Thi&ecirc;n Qu&acirc;n &ocirc;n nhu b&ecirc;n dưới.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng, ta kh&ocirc;ng thể động thủ.&quot; Vi&ecirc;n H&acirc;n lắc lắc đầu.<br />\r\n<br />\r\n&quot;Th&acirc;n &aacute;i, hết thảy c&aacute;c thứ n&agrave;y đều l&agrave; ta sai. Nếu như kh&ocirc;ng phải ta &yacute; ch&iacute; kh&ocirc;ng ki&ecirc;n định, những chuyện n&agrave;y cũng sẽ kh&ocirc;ng ph&aacute;t sinh.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n cười thảm một tiếng, đưa tay ra bắt được b&agrave;n tay của n&agrave;ng đao nhọn.<br />\r\n<br />\r\n&quot;Hết thảy c&aacute;c thứ n&agrave;y tội, liền để ta l&agrave;m g&aacute;nh v&aacute;c đi.&quot;<br />\r\n<br />\r\nN&oacute;i tới chỗ n&agrave;y, hắn chợt đoạt lấy đao nhọn. Sau đ&oacute; cầm điện thoại di động l&ecirc;n, liền muốn gọi điện thoại b&aacute;o cảnh s&aacute;t.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Kh&ocirc;ng, l&atilde;o c&ocirc;ng, ta sai lầm rồi, hết thảy c&aacute;c thứ n&agrave;y đều l&agrave; ta l&agrave;m.&quot; Vi&ecirc;n H&acirc;n vội v&agrave;ng h&ocirc;.<br />\r\n<br />\r\n&quot;Th&acirc;n &aacute;i, nghe lời.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n cầm c&aacute;nh tay n&agrave;ng, &aacute;nh mắt &ocirc;n nhu nh&igrave;n n&agrave;ng, tự lẩm bẩm: &quot;Thật tốt sống tiếp.&quot;<br />\r\n<br />\r\nN&oacute;i xong hắn bấm điện thoại b&aacute;o cảnh s&aacute;t.<br />\r\n<br />\r\n&quot;Ta giết phụ mẫu ta, tới bắt ta đi, địa chỉ l&agrave;...&quot;<br />\r\n<br />\r\nSau đ&oacute; ở nơi n&agrave;y, hắn y&ecirc;n lặng ngồi ở trước cửa, b&agrave;n tay nắm nhuốm m&aacute;u chủy thủ, thảm cười n&oacute;i: &quot;Cuối c&ugrave;ng l&agrave; ta một người chịu đựng rồi to&agrave;n bộ!&quot;<br />\r\n<br />\r\nThấy Di&ecirc;u Thi&ecirc;n Qu&acirc;n d&aacute;ng vẻ, Vi&ecirc;n H&acirc;n đột nhi&ecirc;n mất khống chế đứng l&ecirc;n, n&agrave;ng nắm ch&iacute;nh m&igrave;nh mặt, &aacute;nh mắt đi&ecirc;n cuồng h&ocirc;: &quot;Ta rốt cuộc l&agrave;m những g&igrave;!&quot;<br />\r\n<br />\r\nTrong l&uacute;c n&oacute;i chuyện, n&agrave;ng xấu hổ nh&igrave;n Di&ecirc;u Thi&ecirc;n Qu&acirc;n liếc mắt, đột nhi&ecirc;n mất khống chế x&ocirc;ng về cửa sổ.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n căn bản kh&ocirc;ng kịp ngăn cản, liền nghe được t&eacute; lầu thanh &acirc;m.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng được, n&agrave;ng tự s&aacute;t, lần n&agrave;y ta n&ecirc;n l&agrave;m c&aacute;i g&igrave;?&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n mặt đầy t&aacute;i nhợt, ban đầu bốn người, b&acirc;y giờ chỉ c&oacute; hắn c&ograve;n sống, hắn sao rồi hung thủ giết người sao?<br />\r\n<br />\r\n&quot;Đ&uacute;ng rồi, m&aacute;y thu h&igrave;nh.&quot; Hắn chợt nghĩ tới điều g&igrave;, bắt đầu t&igrave;m kiếm khắp nơi m&aacute;y thu h&igrave;nh.<br />\r\n<br />\r\nChỉ l&agrave; t&igrave;m nửa ngày, hắn cũng kh&ocirc;ng c&oacute; t&igrave;m được m&aacute;y thu h&igrave;nh.<br />\r\n<br />\r\n&quot;Đ&aacute;ng chết, Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng m&aacute;y thu h&igrave;nh thả v&agrave;o địa phương n&agrave;o?&quot; Trong l&ograve;ng Di&ecirc;u Thi&ecirc;n Qu&acirc;n mắng thầm, &aacute;nh mắt nh&igrave;n về ph&iacute;a m&agrave;n h&igrave;nh điện thoại di động. Sắc mặt lại ng&acirc;y dại.<br />\r\n<br />\r\nHo&agrave;ng Tuyền T&uacute; Tr&agrave;ng app, vang l&ecirc;n nhắc nhở.<br />\r\n<br />\r\n&quot;Ch&uacute;c mừng ngươi ho&agrave;n th&agrave;nh cảnh tượng, Ai Mưu S&aacute;t &Aacute;i T&igrave;nh Của Ta Rồi.&quot;<br />\r\n<br />\r\n&quot;Ngươi diễn dịch chấm điểm: Ba sao. (m&atilde;n phần ngũ tinh ) &quot;<br />\r\n<br />\r\n&quot;Ngươi tiền đ&oacute;ng phim: Năm trăm &Aacute;c Linh Tệ.&quot;<br />\r\n<br />\r\n&quot;Bởi v&igrave; ngươi l&agrave; lần đầu ti&ecirc;n ho&agrave;n th&agrave;nh cảnh tượng, ngươi đạt được danh xưng Khủng Bố T&acirc;n T&uacute;.&quot;<br />\r\n<br />\r\nNh&igrave;n đến đ&acirc;y, trong l&ograve;ng Di&ecirc;u Thi&ecirc;n Qu&acirc;n rung động, chẳng lẽ hết thảy c&aacute;c thứ n&agrave;y cũng l&agrave; bị người đạo diễn? ,<br />\r\n<br />\r\nCực kỳ vừa nghĩ tới b&acirc;y giờ m&igrave;nh t&igrave;nh cảnh, hắn mắng to một c&acirc;u, đoạt m&ocirc;n đi.<br />\r\n<br />\r\nN&oacute;i đến cũng kỳ qu&aacute;i, r&otilde; r&agrave;ng kh&oacute;a lại cửa ph&ograve;ng, lại bị hắn t&ugrave;y tiện mở ra.<br />\r\n<br />\r\nChỉ l&agrave; hắn mới vừa vọt tới dưới lầu, liền thấy mấy chiếc xe cảnh s&aacute;t, đ&atilde; bao v&acirc;y nh&agrave; n&agrave;y lầu.<br />\r\n<br />\r\n&quot;Giơ tay l&ecirc;n.&quot; Một người cảnh s&aacute;t giơ tay l&ecirc;n thương h&ocirc;.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n cuống qu&iacute;t vứt bỏ đao nhọn d&iacute;nh m&aacute;u, đưa hai tay ra, mặt đầy v&ocirc; tội h&ocirc;: &quot;Ta đầu h&agrave;ng.&quot;<br />\r\n<br />\r\nMười ph&uacute;t sau, ph&ograve;ng thẩm vấn b&ecirc;n trong, Di&ecirc;u Thi&ecirc;n Qu&acirc;n ngồi ở tr&ecirc;n ghế, b&agrave;n tay đ&atilde; bị đeo c&ograve;ng tay l&ecirc;n, k&iacute;ch động n&oacute;i: &quot;Nh&acirc;n thật kh&ocirc;ng phải ta s&aacute;t, giết người l&agrave; Vi&ecirc;n H&acirc;n.&quot;<br />\r\n<br />\r\nTại hắn đối diện, một c&aacute;i cảnh s&aacute;t th&acirc;m ni&ecirc;n ung dung thong thả uống tr&agrave;, &aacute;nh mắt nh&igrave;n chăm ch&uacute; hắn: &quot;Vi&ecirc;n H&acirc;n đ&atilde; chết.&quot;<br />\r\n<br />\r\n&quot;Ta biết, c&oacute; thể n&agrave;ng l&agrave; hung thủ.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n k&iacute;ch động n&oacute;i.<br />\r\n<br />\r\n&quot;N&agrave;ng l&agrave; năm năm trước chết.&quot; Cảnh s&aacute;t th&acirc;m ni&ecirc;n n&oacute;i.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n muốn mở miệng, nghe được c&acirc;u n&agrave;y, lại bị chợt cắm ở trong giọng.<br />\r\n<br />\r\nCảnh s&aacute;t th&acirc;m ni&ecirc;n &aacute;nh mắt kinh ngạc nh&igrave;n hắn: &quot;Ch&uacute;ng ta tiến v&agrave;o gian ph&ograve;ng kia, kh&ocirc;ng thấy c&aacute;i g&igrave; thi thể. Cực kỳ nơi đ&oacute; x&aacute;c thực xảy ra thảm &aacute;n diệt m&ocirc;n, nhưng đ&oacute; l&agrave; năm năm trước sự t&igrave;nh.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng, kh&ocirc;ng thể n&agrave;o.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n run lập cập n&oacute;i, &aacute;nh mắt tr&agrave;n đầy kh&ocirc;ng thể tưởng tượng nổi.<br />\r\n<br />\r\nHắn r&otilde; r&agrave;ng tận mắt thấy Vi&ecirc;n H&acirc;n nhảy lầu tự s&aacute;t, kết quả cảnh s&aacute;t lại n&oacute;i, Vi&ecirc;n H&acirc;n ở năm năm trước liền chết?<br />\r\n<br />\r\nVậy hắn thấy, đến tột c&ugrave;ng l&agrave; c&aacute;i g&igrave;?<br />\r\n<br />\r\n&quot;Năm năm trước, c&aacute;i kia tiểu khu xảy ra một m&oacute;n khiếp sợ vốn l&agrave; thảm &aacute;n diệt m&ocirc;n. Th&ecirc; tử Vi&ecirc;n H&acirc;n bởi v&igrave; kh&ocirc;ng c&aacute;ch n&agrave;o sinh dục, trường kỳ tao thụ trượng phu, c&ocirc;ng c&ocirc;ng, b&agrave; b&agrave; ngược đ&atilde;i. Kết quả tinh thần thất thường, d&ugrave;ng một c&aacute;i đao nhọn, đem người một nh&agrave; to&agrave;n bộ giết chết. Sau đ&oacute; nhảy lầu tự s&aacute;t.&quot;<br />\r\n<br />\r\nCảnh s&aacute;t th&acirc;m ni&ecirc;n uống một hớp nước tr&agrave;, &aacute;nh mắt nghi&ecirc;m t&uacute;c n&oacute;i: &quot;Vụ &aacute;n kia, ch&iacute;nh l&agrave; ta l&agrave;m.&quot;<br />\r\n<br />\r\n&quot;L&uacute;c ấy, người một nh&agrave; tất cả đều chết hết. Kh&ocirc;ng c&oacute; ai sống tiếp.&quot;<br />\r\n<br />\r\n&quot;C&oacute; thể v&acirc;ng.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n c&ograve;n muốn n&oacute;i điều g&igrave;.<br />\r\n<br />\r\nCảnh s&aacute;t th&acirc;m ni&ecirc;n đưa tới một tấm h&igrave;nh: &quot;Ngươi nh&igrave;n một ch&uacute;t, tấm h&igrave;nh n&agrave;y đ&uacute;ng l&agrave; bọn họ ảnh gia đ&igrave;nh.&quot;<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n nhận lấy h&igrave;nh nh&igrave;n một c&aacute;i, sắc mặt nhất thời đại biến.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nĐ&acirc;y l&agrave; một tấm một nh&agrave; bốn chiếc ảnh gia đ&igrave;nh, một đ&ocirc;i l&atilde;o phu th&ecirc; ngồi ở tr&ecirc;n ghế, m&agrave; ở c&aacute;i ghế ph&iacute;a sau, l&agrave; Vi&ecirc;n H&acirc;n c&ugrave;ng một c&aacute;i nam tử xa lạ.<br />\r\n<br />\r\nVi&ecirc;n H&acirc;n cười rất hạnh ph&uacute;c, nh&igrave;n một c&aacute;i ch&iacute;nh l&agrave; t&acirc;n h&ocirc;n kh&ocirc;ng l&acirc;u. Chồng của n&agrave;ng, &Acirc;u phục, mang theo mắt kiếng gọng v&agrave;ng, trưởng rất anh tuấn.<br />\r\n<br />\r\nMột nh&agrave; n&agrave;y bốn chiếc, ngoại trừ Vi&ecirc;n H&acirc;n, Di&ecirc;u Thi&ecirc;n Qu&acirc;n kh&ocirc;ng quen biết bất cứ ai.<br />\r\n<br />\r\nV&agrave;o giờ khắc n&agrave;y, hắn r&ugrave;ng m&igrave;nh một c&aacute;i, đ&atilde; hiểu.<br />\r\n<br />\r\nKhi hắn đi v&agrave;o thời điểm, trận kia năm năm trước thảm kịch lập lại.<br />\r\n<br />\r\nM&agrave; hắn bất hạnh trở th&agrave;nh Vi&ecirc;n H&acirc;n trượng phu diễn vi&ecirc;n.<br />\r\n<br />\r\nN&oacute;i như vậy, từ vừa mới bắt đầu, Vi&ecirc;n H&acirc;n ch&iacute;nh l&agrave; một c&aacute;i người chết?<br />\r\n<br />\r\nNếu như dựa theo nguy&ecirc;n lai nội dung cốt truyện, hắn hẳn bị Vi&ecirc;n H&acirc;n giết chết mới đ&uacute;ng!<br />\r\n<br />\r\nNghĩ đến đ&acirc;y, &aacute;nh mắt cuả Di&ecirc;u Thi&ecirc;n Qu&acirc;n tr&agrave;n đầy vui mừng.<br />\r\n<br />\r\nCảnh s&aacute;t th&acirc;m ni&ecirc;n liếc hắn một c&aacute;i: &quot;Ngươi cũng kh&ocirc;ng c&oacute; giết người, c&aacute;i kia tiểu khu đ&atilde; sớm s&aacute;ch thi&ecirc;n. B&acirc;y giờ kh&ocirc;ng c&oacute; một b&oacute;ng người. M&agrave; ngươi n&oacute;i địa phương, năm năm trước tựu l&agrave; hung trạch.&quot;<br />\r\n<br />\r\n&quot;Cực kỳ ngươi n&oacute;i xạo cảnh s&aacute;t l&agrave; kh&ocirc;ng n&ecirc;n, đi đem tiền phạt đ&oacute;ng một ch&uacute;t đi.&quot;<br />\r\n<br />\r\n&quot;Được.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n gật đầu một c&aacute;i, tr&ecirc;n mặt lại c&agrave;ng ng&agrave;y c&agrave;ng m&ecirc; hoặc.<br />\r\n<br />\r\nGiao xong rồi tiền phạt, Di&ecirc;u Thi&ecirc;n Qu&acirc;n thất hồn lạc ph&aacute;ch đi ra cục cảnh s&aacute;t.<br />\r\n<br />\r\nNh&igrave;n điện thoại di động b&ecirc;n tr&ecirc;n Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng, trong l&ograve;ng Di&ecirc;u Thi&ecirc;n Qu&acirc;n v&ocirc; c&ugrave;ng rung động.<br />\r\n<br />\r\nRốt cuộc chuyện n&agrave;y như thế n&agrave;o?<br />\r\n<br />\r\nTại sao tr&agrave;ng n&agrave;y Ch&acirc;n Nh&acirc;n T&uacute;, lại sẽ kinh khủng như vậy.<br />\r\n<br />\r\nR&otilde; r&agrave;ng ban đầu kh&ocirc;ng phải như vậy.</p>', '2021-12-01 14:49:17', 6, 1),
(294, 75, 'Điện ảnh Chiếu Phim Rồi', '4', '<p>Trở lại ph&ograve;ng trọ, Di&ecirc;u Thi&ecirc;n Qu&acirc;n tỉnh t&aacute;o lại, đ&aacute;nh mở điều tra m&aacute;y t&iacute;nh n&agrave;y l&ecirc;n thảm &aacute;n diệt m&ocirc;n.<br />\r\n<br />\r\nQuả nhi&ecirc;n với cảnh s&aacute;t th&acirc;m ni&ecirc;n n&oacute;i giống nhau như đ&uacute;c.<br />\r\n<br />\r\nNhư vậy đề t&agrave;i tới, đ&atilde; từng thảm &aacute;n diệt m&ocirc;n hiện trường, tại sao biến th&agrave;nh Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng cảnh tượng?<br />\r\n<br />\r\nCăn cứ Di&ecirc;u Thi&ecirc;n Qu&acirc;n quan s&aacute;t, người b&ecirc;n trong trừ m&igrave;nh ra, c&ocirc;ng c&ocirc;ng, b&agrave; b&agrave; diễn vi&ecirc;n, cũng kh&ocirc;ng phải tự m&igrave;nh. Chỉ c&oacute; Vi&ecirc;n H&acirc;n l&agrave; năm năm trước người chết.<br />\r\n<br />\r\nN&oacute;i như vậy, tr&agrave;ng n&agrave;y diễn xuất, lại chết hai người!<br />\r\n<br />\r\nHơn nữa nữ chủ diễn, l&agrave; đ&atilde; sớm chết đi năm năm nh&acirc;n!<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n cầm điện thoại di động l&ecirc;n, sắc mặt rung động nh&igrave;n Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng, trong miệng tự m&igrave;nh lẩm bẩm: &quot;Ngươi rốt cuộc c&oacute; b&iacute; mật g&igrave;?&quot;<br />\r\n<br />\r\nMở ra Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng app, li&ecirc;n quan tới t&agrave;i liệu m&igrave;nh rất đơn giản.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n: Cửu Cấp diễn vi&ecirc;n.<br />\r\n<br />\r\nDanh xưng: Khủng Bố T&acirc;n T&uacute;,<br />\r\n<br />\r\n&Aacute;c Linh Tệ: Năm trăm.<br />\r\n<br />\r\nDiễn dịch trải qua: &laquo; Ai Mưu S&aacute;t &Aacute;i T&igrave;nh Của Ta Rồi. &raquo;<br />\r\n<br />\r\n&quot;&Aacute;c Linh Tệ c&oacute; &iacute;ch lợi g&igrave;?&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n cầm điện thoại di động l&ecirc;n, muốn t&igrave;m được sử dụng đường tắt. Lại ph&aacute;t hiện m&igrave;nh quyền hạn chưa đủ.<br />\r\n<br />\r\nTo&agrave;n bộ Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng app, nội dung phi thường đơn sơ. Chỉ c&oacute; một nhiệm vụ hệ thống. Sẽ kh&ocirc;ng định kỳ cho m&igrave;nh ph&aacute;i đưa nhiệm vụ.<br />\r\n<br />\r\nC&oacute; thể nhiệm vụ n&oacute;i r&otilde;, lại thập phần kinh khủng.<br />\r\n<br />\r\n&quot;Trở th&agrave;nh Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng diễn vi&ecirc;n, ngươi sẽ đi về ph&iacute;a một c&aacute;i đi th&ocirc;ng hắc &aacute;m minh tinh con đường.&quot;<br />\r\n<br />\r\n&quot;Ở ngươi trở th&agrave;nh Ảnh Đế trước, ngươi kh&ocirc;ng c&oacute; cự tuyệt nhiệm vụ quyền lực.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng định kỳ ph&aacute;t ra nhiệm vụ ngươi phải tiếp nhận, nếu như ngươi cự tuyệt ho&agrave;n th&agrave;nh, ngươi th&igrave; sẽ từ bốc hơi khỏi thế gian.&quot;<br />\r\n<br />\r\nNh&igrave;n đến đ&acirc;y, Di&ecirc;u Thi&ecirc;n Qu&acirc;n đồng tử co rụt lại, tr&ecirc;n mặt nhiều một nụ cười khổ.<br />\r\n<br />\r\nBất qu&aacute; hắn vốn l&agrave; kh&ocirc;ng c&oacute; &yacute; định l&ugrave;i bước.<br />\r\n<br />\r\nCha mẹ mất t&iacute;ch, khẳng định với Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng c&oacute; quan hệ. Coi như ph&iacute;a trước c&oacute; lớn hơn nữa kinh khủng, hắn cũng chỉ c&oacute; thể ki&ecirc;n tr&igrave; đến c&ugrave;ng đi xuống.<br />\r\n<br />\r\nĐến buổi tối.<br />\r\n<br />\r\nNáo thị khu, một nh&agrave; cửa h&agrave;ng lớn b&ecirc;n trong, Di&ecirc;u Thi&ecirc;n Qu&acirc;n với Lỗ Phong Hoa c&ugrave;ng nhau ăn cơm.<br />\r\n<br />\r\nLỗ Phong Hoa l&agrave; hắn đồng đảng, tướng mạo b&igrave;nh thường, nhưng vẫn muốn l&agrave;m minh tinh. Trước mắt l&agrave;m một c&aacute;i quần ch&uacute;ng diễn vi&ecirc;n.<br />\r\n<br />\r\n&quot;Di&ecirc;u Thi&ecirc;n Qu&acirc;n, ngươi c&oacute; muốn hay kh&ocirc;ng cũng theo ta đồng thời l&agrave;m Vai quần ch&uacute;ng.&quot; Lỗ Phong Hoa một b&ecirc;n v&ugrave;i đầu ăn cơm, vừa nh&igrave;n điện thoại di động.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot; Được rồi, ta tạm thời kh&ocirc;ng muốn diễn tr&ograve;.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n cười khổ n&oacute;i.<br />\r\n<br />\r\n&quot;Vậy th&igrave; thật l&agrave; đ&aacute;ng tiếc ngươi tốt như vậy diễn kỹ.&quot; Lỗ Phong Hoa mặt đầy tiếc nuối n&oacute;i, hắn ch&iacute;nh l&agrave; gặp qua Di&ecirc;u Thi&ecirc;n Qu&acirc;n diễn kỹ.<br />\r\n<br />\r\nĐ&acirc;y tuyệt đối l&agrave; để cho người kh&ocirc;ng thể ch&ecirc; bai.<br />\r\n<br />\r\nPhải biết, Di&ecirc;u Thi&ecirc;n Qu&acirc;n cha mẹ, mặc d&ugrave; kh&ocirc;ng phải l&agrave; c&aacute;i g&igrave; đại minh tinh. Lại l&agrave; đ&atilde; ra danh L&atilde;o H&iacute; Cốt.<br />\r\n<br />\r\n&quot;N&agrave;y ngược lại kh&ocirc;ng c&oacute;, ta ở những địa phương kh&aacute;c, t&igrave;m được đất dụng v&otilde;.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n nh&uacute;n nh&uacute;n vai, tr&ecirc;n mặt nhiều hơn một ch&uacute;t bất đắc dĩ.<br />\r\n<br />\r\nLỗ Phong Hoa nghe kh&ocirc;ng hiểu hắn đang n&oacute;i g&igrave;, trong miệng đột nhi&ecirc;n n&oacute;i: &quot;Đ&uacute;ng rồi, ta nghe n&oacute;i Tử Hiểu Linh, khoảng thời gian n&agrave;y tinh đồ nhưng l&agrave;...&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng cho nhấc n&agrave;ng!&quot;<br />\r\n<br />\r\nMột tiếng n&oacute;ng nảy &acirc;m thanh vang l&ecirc;n, Di&ecirc;u Thi&ecirc;n Qu&acirc;n anh tuấn tr&ecirc;n mặt, nhiều hơn một tia kh&ocirc;ng ki&ecirc;n nhẫn. &Aacute;nh mắt ch&iacute;nh giữa, c&agrave;ng l&agrave; mơ hồ l&oacute;e l&ecirc;n một tia thống khổ.<br />\r\n<br />\r\n&quot;Được rồi.&quot; Lỗ Phong Hoa kh&ocirc;ng n&oacute;i th&ecirc;m g&igrave; nữa.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n t&ecirc; liệt tr&ecirc;n ghế ngồi, trong l&ograve;ng thập phần khổ sở.<br />\r\n<br />\r\nL&uacute;c trước ho&agrave;n mỹ t&igrave;nh nh&acirc;n, k&egrave;m theo cha mẹ mất t&iacute;ch, hết thảy đều th&agrave;nh bọt nước.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; cha mẹ trợ gi&uacute;p, diễn xuất kh&aacute; hơn nữa, ở l&agrave;ng giải tr&iacute; giống vậy nửa bước kh&oacute; đi.<br />\r\n<br />\r\nLỗ Phong Hoa y&ecirc;n lặng nh&igrave;n điện thoại di động, đột nhi&ecirc;n nh&igrave;n thấy g&igrave;, sắc mặt ng&acirc;y dại.<br />\r\n<br />\r\n&quot;Hảo tiểu tử!&quot;<br />\r\n<br />\r\nHắn đột nhi&ecirc;n đưa tay ra, chỉ Di&ecirc;u Thi&ecirc;n Qu&acirc;n h&ocirc;: &quot;Nguy&ecirc;n lai ngươi len l&eacute;n đ&oacute;ng phim rồi!&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; a.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n vẻ mặt v&ocirc; cùng nghi hoặc.<br />\r\n<br />\r\n&quot;C&ograve;n n&oacute;i kh&ocirc;ng c&oacute;, n&agrave;y kh&ocirc;ng phải ngươi diễn vi&ecirc;n ch&iacute;nh sao?&quot; Lỗ Phong Hoa cầm điện thoại di động l&ecirc;n, đưa cho hắn.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n nh&igrave;n tới điện thoại di động sau, sắc mặt nhất thời ng&acirc;y dại.<br />\r\n<br />\r\nTr&ecirc;n m&agrave;n ảnh điện thoại di động, ch&iacute;nh l&agrave; một bộ Internet điện ảnh.<br />\r\n<br />\r\nĐiện ảnh t&ecirc;n gọi &laquo; Ai Mưu S&aacute;t &Aacute;i T&igrave;nh Của Ta Rồi &raquo; !<br />\r\n<br />\r\nHắn vội v&agrave;ng cầm điện thoại di động l&ecirc;n, click rồi ph&aacute;t ra.<br />\r\n<br />\r\nRất nhanh, điện ảnh liền mở m&agrave;n.<br />\r\n<br />\r\nBộ phim n&agrave;y, đ&uacute;ng l&agrave; m&igrave;nh đ&iacute;ch th&acirc;n trải qua sự t&igrave;nh.<br />\r\n<br />\r\nChỉ l&agrave; nội dung cốt truyện so với ch&iacute;nh m&igrave;nh diễn, c&agrave;ng đầy đặn.<br />\r\n<br />\r\nỞ bộ phim n&agrave;y b&ecirc;n trong, ch&iacute;nh m&igrave;nh ho&agrave;n to&agrave;n l&agrave; một c&aacute;i nh&acirc;n vật phản diện. Lợi dụng nữ chủ giết chết cha mẹ m&igrave;nh. Giết chết ch&iacute;nh m&igrave;nh cừu địch.<br />\r\n<br />\r\nSau đ&oacute; ch&iacute;nh l&agrave; m&igrave;nh ỷ ở trước cửa, kia đoạn th&acirc;m t&igrave;nh c&aacute;o biệt, đưa đến nữ chủ tự s&aacute;t.<br />\r\n<br />\r\nCuối c&ugrave;ng, ch&iacute;nh m&igrave;nh thừa kế cha mẹ t&agrave;i sản, c&ograve;n thu được số lớn tiền bảo hiểm.<br />\r\n<br />\r\nTo&agrave;n bộ điện ảnh nh&igrave;n xong, Di&ecirc;u Thi&ecirc;n Qu&acirc;n cả người cũng kh&ocirc;ng tốt.<br />\r\n<br />\r\nLỗ Phong Hoa lại ở một b&ecirc;n nh&igrave;n đến nồng nhiệt.<br />\r\n<br />\r\n&quot;Ngươi diễn kỹ rất tốt, đem c&aacute;i t&ecirc;n xấu xa n&agrave;y diễn qu&aacute; dọa người.&quot;<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n kh&ocirc;ng n&oacute;i g&igrave;, nh&igrave;n về ph&iacute;a điện ảnh b&igrave;nh luận.<br />\r\n<br />\r\nXem qua bộ phim n&agrave;y nh&acirc;n, cũng cho ra ch&iacute;nh m&igrave;nh đ&aacute;nh gi&aacute;.<br />\r\n<br />\r\n&quot;Trời ạ, người nam n&agrave;y chủ thật l&agrave; đẹp trai, lại tr&agrave;n đầy t&agrave; kh&iacute;.&quot;<br />\r\n<br />\r\n&quot;Cho ta cảm gi&aacute;c kh&ocirc;ng r&eacute;t m&agrave; run.&quot;<br />\r\n<br />\r\n&quot;Ta chỉ th&iacute;ch như vậy nh&acirc;n vật nam ch&iacute;nh, thật giống như bị hắn chinh phục.&quot;<br />\r\n<br />\r\n&quot;Bộ phim n&agrave;y, nh&acirc;n vật nam ch&iacute;nh từ đầu tới cuối khống chế hết thảy, thật sự l&agrave; qu&aacute; đ&aacute;ng sợ. Nữ chủ bị hắn đ&ugrave;a bỡn trong l&ograve;ng b&agrave;n tay.&quot;<br />\r\n<br />\r\n&quot;Bộ phim n&agrave;y thật rất đ&aacute;ng sợ.&quot;<br />\r\n<br />\r\n&quot;Thật l&agrave; một bộ kh&ocirc;ng tệ huyền nghi điện ảnh.&quot;<br />\r\n<br />\r\nNh&igrave;n bạn tr&ecirc;n mạng khen ngợi, Lỗ Phong Hoa k&iacute;ch động h&ocirc;: &quot;Ngươi đ&acirc;y l&agrave; muốn hỏa tiết tấu.&quot;<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n kh&ocirc;ng n&oacute;i g&igrave;, hỏa kh&ocirc;ng hỏa đối với hắn m&agrave; n&oacute;i, cũng kh&ocirc;ng coi v&agrave;o đ&acirc;u.<br />\r\n<br />\r\nC&oacute; thể trong căn ph&ograve;ng kia, r&otilde; r&agrave;ng kh&ocirc;ng c&oacute; m&aacute;y thu h&igrave;nh.<br />\r\n<br />\r\nTại sao điện ảnh hay lại l&agrave; chụp xuống?<br />\r\n<br />\r\nTrong l&ograve;ng của hắn cảm thấy lẫn lộn, nhưng tất cả những thứ n&agrave;y, nhất định l&agrave; Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng tạo th&agrave;nh.<br />\r\n<br />\r\nChỉ l&agrave; người xem sẽ kh&ocirc;ng biết, bộ phim n&agrave;y nữ chủ, tr&ecirc;n thực tế đ&atilde; sớm chết rồi.<br />\r\n<br />\r\nN&agrave;y căn bản liền kh&ocirc;ng phải l&agrave; c&aacute;i g&igrave; huyền nghi kịch!<br />\r\n<br />\r\nTrả điện thoại di động lại cho rồi Lỗ Phong Hoa, Di&ecirc;u Thi&ecirc;n Qu&acirc;n ngồi xuống ghế, &aacute;nh mắt bất đắc dĩ n&oacute;i: &quot; Được rồi, tiếp tục uống rượu.&quot;<br />\r\n<br />\r\nLỗ Phong Hoa gật đầu một c&aacute;i, c&ugrave;ng hắn uống rượu.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nSay khướt trở lại ph&ograve;ng trọ, Di&ecirc;u Thi&ecirc;n Qu&acirc;n nh&igrave;n về ph&iacute;a tr&ecirc;n b&agrave;n.<br />\r\n<br />\r\nTr&ecirc;n b&agrave;n l&agrave; một Trương To&agrave;n gia ph&uacute;c, hắn ở ở giữa nhất, cha trắng x&aacute;m đến t&oacute;c, mang mắt k&iacute;nh, lộ ra văn chất nho nh&atilde;. Mẫu th&acirc;n nở nụ cười, giống vậy đoan trang hoa qu&yacute;.<br />\r\n<br />\r\nĐưa tay ra, vuốt ve ảnh gia đ&igrave;nh. Di&ecirc;u Thi&ecirc;n Qu&acirc;n thở d&agrave;i một c&aacute;i: &quot;Ta rất nhớ c&aacute;c ngươi.&quot;<br />\r\n<br />\r\nNằm ở tr&ecirc;n giường, hắn mới vừa buồn ngủ, đột nhi&ecirc;n một c&aacute;i điện thoại xa lạ gọi lại.<br />\r\n<br />\r\nKết nối sau đ&oacute;, trong điện thoại di động thanh &acirc;m, để cho Di&ecirc;u Thi&ecirc;n Qu&acirc;n trong l&ograve;ng căng thẳng.<br />\r\n<br />\r\n&quot;Ta nh&igrave;n thấy ngươi chụp kia bộ Internet điện ảnh, thật rất kh&ocirc;ng tồi &quot;<br />\r\n<br />\r\n&quot;Thật sao?&quot;<br />\r\n<br />\r\n&quot;Ta c&oacute; thể gi&uacute;p ngươi.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng nhất thiết phải thế.&quot;<br />\r\n<br />\r\n&quot;Ngươi ch&iacute;nh l&agrave; lạnh l&ugrave;ng như vậy, r&otilde; r&agrave;ng ch&uacute;ng ta ban đầu...&quot;<br />\r\n<br />\r\nĐiện thoại di động bị c&uacute;p, Di&ecirc;u Thi&ecirc;n Qu&acirc;n mặt như H&agrave;n Sương đưa điện thoại di động tắt m&aacute;y, sau đ&oacute; nằm ở tr&ecirc;n giường rất nhanh th&igrave; ngủ thiếp đi.<br />\r\n<br />\r\nNg&agrave;y thứ 2, đ&aacute;nh răng rửa mặt, Di&ecirc;u Thi&ecirc;n Qu&acirc;n nh&igrave;n điện thoại di động điện ảnh.<br />\r\n<br />\r\n&laquo; Ai Mưu S&aacute;t &Aacute;i T&igrave;nh Của Ta Rồi &raquo; chiếu phim sau, số liệu một dạng cũng chẳng c&oacute; bao nhi&ecirc;u người xem.<br />\r\n<br />\r\nC&oacute; thể mỗi một người xem, cũng cho ra kh&ocirc;ng tệ đ&aacute;nh gi&aacute;.<br />\r\n<br />\r\nHo&agrave;ng Tuyền T&uacute; Tr&agrave;ng b&ecirc;n n&agrave;y, giống vậy c&oacute; tin tức: &quot;Ngươi điện ảnh ở dương gian chiếu phim rồi, ngươi sẽ được bộ phim n&agrave;y chia hoa hồng.&quot;<br />\r\n<br />\r\nThấy tin tức n&agrave;y, Di&ecirc;u Thi&ecirc;n Qu&acirc;n nở nụ cười.<br />\r\n<br />\r\n&quot;Dương gian chiếu phim? Đ&acirc;y l&agrave; &yacute; g&igrave;?&quot;<br />\r\n<br />\r\n&quot; Được rồi, đừng để &yacute; n&oacute;.&quot;<br />\r\n<br />\r\nĐơn giản ăn điểm t&acirc;m xong, Di&ecirc;u Thi&ecirc;n Qu&acirc;n ra cửa.<br />\r\n<br />\r\nỞ một qu&aacute;n c&agrave; ph&ecirc; b&ecirc;n trong, hắn gọi một ly c&agrave; ph&ecirc;, cứ như vậy chờ đợi.<br />\r\n<br />\r\nHắn một th&acirc;n đồ hưu nh&agrave;n phục, s&aacute;ng b&oacute;ng trắng n&otilde;n gương mặt, lộ ra g&oacute;c cạnh r&otilde; r&agrave;ng L&atilde;nh Tuấn, đen nh&aacute;nh đ&ocirc;i mắt th&acirc;m th&uacute;y, hiện l&ecirc;n m&ecirc; người m&agrave;u sắc; kia nồng đậm l&ocirc;ng mi, cao thẳng sống mũi.<br />\r\n<br />\r\nN&oacute;i ri&ecirc;ng về nhan gi&aacute; trị, hắn tuyệt đối kh&ocirc;ng thua g&igrave; đang ăn kh&aacute;ch tiểu thịt tươi.</p>', '2021-12-01 14:52:28', 6, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(295, 75, 'Âm Vương Phủ', '5', '<p>V&igrave; vậy Di&ecirc;u Thi&ecirc;n Qu&acirc;n nhất cử nhất động, đều bị người chung quanh ch&uacute; &yacute; tới.<br />\r\n<br />\r\n&quot;Oa, đ&acirc;y cũng qu&aacute; đẹp trai đi.&quot;<br />\r\n<br />\r\n&quot;L&agrave; một c&aacute;i minh tinh sao?&quot;<br />\r\n<br />\r\n&quot;Thật l&agrave; so với chồng của ta c&ograve;n so&aacute;i.&quot;<br />\r\n<br />\r\nChung quanh nữ nh&acirc;n, thỉnh thoảng ph&aacute;t ra th&aacute;n phục.<br />\r\n<br />\r\nC&ocirc; b&aacute;n h&agrave;ng đi tới, &aacute;nh mắt k&iacute;nh mến hỏi &quot;Ti&ecirc;n sinh, c&oacute; cần hay kh&ocirc;ng ta hỗ trợ?&quot;<br />\r\n<br />\r\n&quot;Gi&uacute;p ta t&igrave;m c&aacute;i an tĩnh l&ocirc; ghế ri&ecirc;ng đi.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n cau m&agrave;y n&oacute;i.<br />\r\n<br />\r\nV&igrave; vậy, hắn đi tới một c&aacute;i đơn độc trong bao sương.<br />\r\n<br />\r\nQua mười ph&uacute;t, một c&aacute;i kẹp cặp t&aacute;p, người mặc &acirc;u phục người đ&agrave;n &ocirc;ng trung ni&ecirc;n vội v&atilde; đi tới.<br />\r\n<br />\r\n&quot; Xin lỗi, ta tới trể.&quot; Nam tử cười khan một tiếng n&oacute;i.<br />\r\n<br />\r\n&quot;Ngồi đi, Vương Th&uacute;c.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n đưa tay ra, vẻ mặt l&atilde;nh đạm.<br />\r\n<br />\r\nNam tử ngồi ở đối diện, &aacute;nh mắt cẩn thận từng li từng t&iacute; nh&igrave;n hắn: &quot;Thi&ecirc;n Qu&acirc;n, ngươi lần n&agrave;y tới t&igrave;m ta, kết quả muốn l&agrave;m g&igrave;?&quot;<br />\r\n<br />\r\n&quot;Ta muốn biết, phụ mẫu ta thanh to&aacute;n app, rốt cuộc rơi v&agrave;o trong tay ai?&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n nh&igrave;n về ph&iacute;a Vương Th&uacute;c.<br />\r\n<br />\r\n&quot;Li&ecirc;n quan tới c&aacute;i n&agrave;y, ta cũng kh&ocirc;ng biết.&quot; Vương Th&uacute;c l&uacute;ng t&uacute;ng cười một tiếng, &aacute;nh mắt nh&igrave;n về ph&iacute;a hắn n&oacute;i: &quot;Ta bi&ecirc;́t ngươi bởi v&igrave; cha mẹ mất t&iacute;ch m&agrave; canh c&aacute;nh trong l&ograve;ng, nhưng l&agrave; n&agrave;y l&agrave; sinh &yacute; tr&agrave;ng thượng sự t&igrave;nh, kh&ocirc;ng đến nổi mưu t&agrave;i hại mệnh.&quot;<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n lắc đầu một c&aacute;i, &aacute;nh mắt nh&igrave;n về ph&iacute;a hắn hỏi &quot;Ngươi chắc chắn biết, nếu kh&ocirc;ng ta cũng sẽ kh&ocirc;ng tới t&igrave;m ngươi.&quot;<br />\r\n<br />\r\nVương Th&uacute;c thở d&agrave;i một c&aacute;i, &aacute;nh mắt th&agrave;nh khẩn nh&igrave;n hắn. Mặt mũi khổ sở n&oacute;i: &quot;Ta thật kh&ocirc;ng biết người l&agrave; ai vậy kia, nhưng nghe cha mẹ ngươi n&oacute;i qua. Người kia rất lợi hại, l&agrave;m cho c&aacute;c n&agrave;ng sợ h&atilde;i.&quot;<br />\r\n<br />\r\n&quot;L&uacute;c ấy ta liền kỳ qu&aacute;i. Sau đ&oacute; quả nhi&ecirc;n l&agrave; xảy ra chuyện.&quot;<br />\r\n<br />\r\n&quot;Phụ mẫu ta trước khi mất t&iacute;ch, c&oacute; kh&ocirc;ng c&oacute; n&oacute;i cho ngươi biết thứ g&igrave;?&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n vội v&agrave;ng hỏi.<br />\r\n<br />\r\n&quot;Bọn họ ngược lại l&agrave; đ&atilde; n&oacute;i với ta một &iacute;t kỳ qu&aacute;i lời n&oacute;i, bọn họ để cho ta ngăn cản ngươi, v&ocirc; luận như thế n&agrave;o, đều kh&ocirc;ng thể điều tra đi bọn họ mất t&iacute;ch nguy&ecirc;n nh&acirc;n.&quot;<br />\r\n<br />\r\n&quot;Bất qu&aacute; ta phỏng chừng bọn họ cũng biết t&iacute;nh kh&iacute; của ngươi.&quot;<br />\r\n<br />\r\nVương Th&uacute;c thở d&agrave;i một c&aacute;i, từ trong t&uacute;i c&ocirc;ng văn, lấy ra một c&aacute;i bọc đưa cho hắn: &quot;Cho n&ecirc;n, bọn họ cho ta gởi thư rồi một c&aacute;i bọc. Ta l&uacute;c ấy ch&iacute;nh ở nước ngo&agrave;i, nhận được bọc lại sau, ta liền đuổi về.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;C&aacute;i bao n&agrave;y, ch&iacute;nh l&agrave; cha mẹ ngươi cho ngươi.&quot;<br />\r\n<br />\r\nNh&igrave;n trước mắt bọc lại, Di&ecirc;u Thi&ecirc;n Qu&acirc;n kh&ocirc;ng n&oacute;i g&igrave;, cầm bọc l&ecirc;n xoay người rời đi.<br />\r\n<br />\r\nTrở lại trong căn ph&ograve;ng đi thu&ecirc;, hắn vội v&agrave;ng bắt đầu hủy đi bọc lại.<br />\r\n<br />\r\nBọc lại bị mở ra sau, b&ecirc;n trong cũng kh&ocirc;ng c&oacute; cha mẹ lưu lại bất kỳ phong thơ, ngược lại th&igrave; c&oacute; một c&aacute;i quỷ dị tượng gỗ.<br />\r\n<br />\r\nC&aacute;i con rối n&agrave;y, lớn chừng b&agrave;n tay, tinh vi tỉ mỉ, bộ d&aacute;ng với ch&iacute;nh m&igrave;nh giống nhau y hệt.<br />\r\n<br />\r\nCầm l&ecirc;n c&aacute;i con rối n&agrave;y, Di&ecirc;u Thi&ecirc;n Qu&acirc;n thập phần m&ecirc; muội.<br />\r\n<br />\r\nCha mẹ tại sao phải đem c&aacute;i con rối n&agrave;y cho m&igrave;nh? Vật n&agrave;y rốt cuộc c&oacute; &iacute;ch lợi g&igrave;?<br />\r\n<br />\r\nMặc d&ugrave; trong l&ograve;ng nghi hoặc, c&oacute; thể Di&ecirc;u Thi&ecirc;n Qu&acirc;n biết, cha mẹ th&igrave; sẽ kh&ocirc;ng hại ch&iacute;nh m&igrave;nh. Dứt kho&aacute;t đem c&aacute;i con rối n&agrave;y bỏ v&agrave;o trong t&uacute;i tiền của m&igrave;nh.<br />\r\n<br />\r\nNgồi ở tr&ecirc;n ghế sa lon, Di&ecirc;u Thi&ecirc;n Qu&acirc;n cầm điện thoại di động l&ecirc;n, &aacute;nh mắt kinh ho&agrave;ng nh&igrave;n Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng app.<br />\r\n<br />\r\nKh&ocirc;ng nghi ngờ ch&uacute;t n&agrave;o, c&aacute;i n&agrave;y app, tất nhi&ecirc;n c&oacute; lực lượng quỷ dị.<br />\r\n<br />\r\nĐ&aacute;ng sợ như vậy cảnh tượng, mặc d&ugrave; c&oacute; thể t&aacute;i hiện, chỉ sợ sẽ l&agrave; bởi v&igrave; Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng.<br />\r\n<br />\r\nKết quả n&agrave;y l&agrave; chuyện g&igrave; xảy ra?<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n ho&agrave;n to&agrave;n kh&ocirc;ng biết. Hắn chỉ l&agrave; mơ hồ cảm gi&aacute;c một c&aacute;i kh&ocirc;ng biết thế giới.<br />\r\n<br />\r\nĐ&oacute; l&agrave; một c&aacute;i tr&agrave;n đầy hắc &aacute;m c&ugrave;ng tuyệt vọng thế giới.<br />\r\n<br />\r\nNgay tại hắn trầm tư thời điểm, Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng lại lần nữa vang l&ecirc;n.<br />\r\n<br />\r\n&quot;Mời ở trong v&ograve;ng ba canh giờ đi Ảnh Th&agrave;nh.&quot;<br />\r\n<br />\r\n&quot;&laquo; &Acirc;m Vương Phủ &raquo; sắp action.&quot;<br />\r\n<br />\r\nNh&igrave;n tới điện thoại di động, Di&ecirc;u Thi&ecirc;n Qu&acirc;n minh bạch, m&igrave;nh l&agrave; kh&ocirc;ng tr&aacute;nh tho&aacute;t.<br />\r\n<br />\r\nLần n&agrave;y hắn tự nhi&ecirc;n phải chuẩn bị sẵn s&agrave;ng.<br />\r\n<br />\r\nCầm điện thoại di động l&ecirc;n, điều tra hắn nổi l&ecirc;n ph&aacute;t sinh ở &Acirc;m Vương Phủ vụ &aacute;n.<br />\r\n<br />\r\nChỉ l&agrave; cẩn thận điều tra hắn mới ph&aacute;t hiện, &Acirc;m Vương Phủ cũng kh&ocirc;ng c&oacute; g&igrave; &aacute;n mạng.<br />\r\n<br />\r\n&Acirc;m Vương Phủ cũng kh&ocirc;ng phải thật Vương phủ, l&agrave; năm trước Ảnh Th&agrave;nh x&acirc;y dựng chụp diễn c&ocirc;ng tr&igrave;nh.<br />\r\n<br />\r\nLần n&agrave;y hắn thất vọng, dứt kho&aacute;t mang theo ba l&ocirc;, hắn xoay người rời đi.<br />\r\n<br />\r\nĐi tới Ảnh Th&agrave;nh, nơi n&agrave;y khắp nơi đều l&agrave; đo&agrave;n kịch.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n t&igrave;m được Lỗ Phong Hoa, để cho hắn mang ch&iacute;nh m&igrave;nh đi &Acirc;m Vương Phủ.<br />\r\n<br />\r\nAi biết hắn cười n&oacute;i: &quot;C&aacute;i kia Vương phủ, nhất định ch&iacute;nh l&agrave; Ảnh Th&agrave;nh tr&ograve; cười. Ta mang ngươi tới.&quot;<br />\r\n<br />\r\nV&igrave; vậy, ở Lỗ Phong Hoa dưới sự hướng dẫn, Lỗ Phong Hoa đi tới &Acirc;m Vương Phủ.<br />\r\n<br />\r\n&Acirc;m Vương Phủ.<br />\r\n<br />\r\nBa c&aacute;i kim nước sơn chữ to bảng hiệu thập phần dễ thấy treo ở m&ocirc;n tr&ecirc;n đầu, hai miếng Chu cửa lớn m&agrave;u đỏ trực tiếp biểu minh trạch chủ nh&acirc;n cao qu&yacute; th&acirc;n phận, ph&iacute;a tr&ecirc;n chỉnh tề khảm từng h&agrave;ng kim sắc phao đinh.<br />\r\n<br />\r\nMột h&agrave;ng tám c&aacute;i m&agrave;u đỏ thẫm trụ cột y&ecirc;n lặng n&oacute;i ra nơi n&agrave;y uy nghi&ecirc;m h&ograve;a kh&iacute; ph&aacute;ch, trước cửa hai đầu thạch sư tử c&agrave;ng l&agrave; giương nanh m&uacute;a vuốt thủ ở trước cửa, cả ng&ocirc;i nh&agrave; tử để cho cổ đại đỉnh cấp h&agrave;o m&ocirc;n nh&agrave; gi&agrave;u kh&iacute; ph&aacute;i hiện ra hết.<br />\r\n<br />\r\nChỉ l&agrave; đi v&agrave;o, nơi n&agrave;y trở n&ecirc;n rất vắng lặng, hơn nữa nơi n&agrave;y c&ocirc;ng tr&igrave;nh, thấy thế n&agrave;o cũng thập phần cũ kỹ, l&agrave;m cho người ta một loại &acirc;m trầm cảm gi&aacute;c.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y Vương phủ, căn bản kh&ocirc;ng c&oacute; người n&agrave;o cho mướn.&quot; Lỗ Phong Hoa cười nhạo n&oacute;i: &quot;Chuẩn bị như vậy &acirc;m trầm, cũng kh&ocirc;ng biết c&oacute; phải hay kh&ocirc;ng l&agrave; muốn chụp quỷ phiến.&quot;<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n gật đầu một c&aacute;i, cười n&oacute;i: &quot;Ta chỉ l&agrave; tới nơi n&agrave;y nh&igrave;n một ch&uacute;t, quả nhi&ecirc;n l&agrave; ho&agrave;n to&agrave;n hoang lương.&quot;<br />\r\n<br />\r\nLỗ Phong Hoa kh&ocirc;ng nghi ngờ g&igrave;, mang theo Di&ecirc;u Thi&ecirc;n Qu&acirc;n đi dạo một v&ograve;ng.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n quen thuộc phương sau, liền mượn cớ rời đi.<br />\r\n<br />\r\nNhưng nửa giờ sau, hắn lại l&eacute;n l&eacute;n l&uacute;t l&uacute;t đến nơi n&agrave;y.<br />\r\n<br />\r\nM&agrave; ở cửa vương phủ, đ&atilde; nhiều s&aacute;u người, bốn nam hai nữ.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n đến, cũng kh&ocirc;ng c&oacute; đưa tới bọn họ kinh ngạc.<br />\r\n<br />\r\n&quot;Mọi người đều đến đ&ocirc;ng đủ đi, đ&acirc;y ch&iacute;nh l&agrave; l&agrave;m minh tinh cơ hội tốt.&quot; Một người d&aacute;ng dấp đẹp trai, nh&igrave;n một c&aacute;i ch&iacute;nh l&agrave; sinh vi&ecirc;n nam tử, hưng phấn n&oacute;i.<br />\r\n<br />\r\nNhững người khác gật đầu một c&aacute;i, giống vậy hết sức k&iacute;ch động.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n nh&igrave;n một c&aacute;i, cũng biết bọn họ l&agrave; mới vừa ghi danh Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng người mới.<br />\r\n<br />\r\nNếu như l&agrave; lần thứ hai, tuyệt đối kh&ocirc;ng thể n&agrave;o hưng phấn như thế.<br />\r\n<br />\r\nCực kỳ ở s&aacute;u người n&agrave;y ch&iacute;nh giữa, ngược lại th&igrave; một người mang k&iacute;nh mắt nam tử, hấp dẫn Di&ecirc;u Thi&ecirc;n Qu&acirc;n sự ch&uacute; &yacute;.<br />\r\n<br />\r\nN&agrave;y người nam tử trưởng rất phổ th&ocirc;ng, trốn ở một b&ecirc;n, kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o cảm gi&aacute;c tồn tại. Chỉ l&agrave; từ đầu tới cuối, hắn cũng kh&ocirc;ng n&oacute;i g&igrave;.<br />\r\n<br />\r\n&quot;Nếu mọi người đều đến đ&ocirc;ng đủ, kia liền chuẩn bị Ch&acirc;n Nh&acirc;n T&uacute; đi.&quot; Nam tử cầm điện thoại di động l&ecirc;n n&oacute;i.<br />\r\n<br />\r\nNhững người khác giống vậy nh&igrave;n về ph&iacute;a điện thoại di động.<br />\r\n<br />\r\nHo&agrave;ng Tuyền T&uacute; Tr&agrave;ng tin tức nhất thời xuất hiện.<br />\r\n<br />\r\n&quot;&Acirc;m Vương Phủ Ch&acirc;n Nh&acirc;n T&uacute; mở m&agrave;n.&quot;<br />\r\n<br />\r\n&quot;Nội dung cốt truyện giới thiệu t&oacute;m tắt: &quot;&Acirc;m Vương Phủ l&agrave; xa gần nổi tiếng hung địa, nghe n&oacute;i b&ecirc;n trong c&oacute; đủ loại kinh khủng tin đồn. Một nh&oacute;m sinh vi&ecirc;n kh&ocirc;ng tin, tiến v&agrave;o &Acirc;m Vương Phủ b&ecirc;n trong th&aacute;m hiểm, nhưng bọn hắn cũng kh&ocirc;ng biết, khi bọn hắn tiến v&agrave;o &Acirc;m Vương Phủ thời điểm, ch&acirc;n ch&iacute;nh kinh khủng đ&atilde; bắt đầu rồi...&quot;<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n nh&igrave;n một c&aacute;i điện thoại di động của m&igrave;nh.<br />\r\n<br />\r\n&quot;Vai phụ, Đồng Ngọc Long. Ngươi l&agrave; hội đo&agrave;n ch&iacute;nh giữa tối nhỏ nhặt kh&ocirc;ng đ&aacute;ng kể tồn tại. Một mực thầm mến Đinh Mộ Ngưng.&quot;<br />\r\n<br />\r\n&quot;Diễn dịch mục ti&ecirc;u: Đi theo hội đo&agrave;n tiến v&agrave;o &Acirc;m Vương Phủ, nghĩ biện ph&aacute;p từ &Acirc;m Vương Phủ ch&iacute;nh giữa sống s&oacute;t. Bảo vệ Đinh Mộ Ngưng.&quot;<br />\r\n<br />\r\nNh&igrave;n đến đ&acirc;y, Di&ecirc;u Thi&ecirc;n Qu&acirc;n ngo&agrave;i mặt bất động thần sắc, nội t&acirc;m lại tr&agrave;n đầy hoảng sợ.<br />\r\n<br />\r\nTrải qua lần trước sự kiện sau, nội t&acirc;m của hắn minh bạch, lần n&agrave;y nội dung cốt truyện, khẳng định so với lần trước c&agrave;ng nguy hiểm.<br />\r\n<br />\r\nCh&iacute;nh m&igrave;nh ng&agrave;n vạn phải coi chừng.<br />\r\n<br />\r\nNghĩ đến đ&acirc;y, &aacute;nh mắt của hắn quan s&aacute;t &Acirc;m Vương Phủ, trong l&ograve;ng cẩn thận từng li từng t&iacute;.<br />\r\n<br />\r\n&quot;Ha ha, lần n&agrave;y ta tới diễn nh&acirc;n vật ch&iacute;nh Dịch Thừa.&quot; Nam tử đẹp trai cầm điện thoại di động l&ecirc;n, cười n&oacute;i.<br />\r\n<br />\r\n&quot;Ta l&agrave; vai nữ ch&iacute;nh Đinh Mộ Ngưng.&quot; Một người v&oacute;c d&aacute;ng cao gầy, da thịt bạch tạm nữ nh&acirc;n, nh&igrave;n điện thoại di động, h&agrave;i l&ograve;ng nở nụ cười.<br />\r\n<br />\r\nNhững người khác sắc mặt liền kh&oacute; coi.</p>', '2021-12-01 14:53:13', 7, 1),
(296, 75, 'Thăng Bằng Bị đánh Vỡ', '6', '<p>&quot;Vận kh&iacute; ta kh&ocirc;ng được, chỉ l&agrave; một vai phụ.&quot;<br />\r\n<br />\r\n&quot;Ta cũng l&agrave; vai phụ, ta đ&acirc;y c&aacute;i vai phụ Vương Vũ, thầm mến Đinh Mộ Ngưng, hơn nữa c&ograve;n muốn mượn cơ hội đ&aacute;nh nh&acirc;n vật ch&iacute;nh một hồi.&quot; Một người v&oacute;c d&aacute;ng kh&ocirc;i ng&ocirc;, tướng mạo dũng m&atilde;nh nam tử t&ugrave;y tiện n&oacute;i.<br />\r\n<br />\r\nNghe được hắn n&oacute;i như vậy, Dịch Thừa nở nụ cười: &quot;Ta đ&acirc;y n&ecirc;n ch&uacute; &yacute;.&quot;<br />\r\n<br />\r\n&quot;Được rồi, ch&uacute;ng ta tiến v&agrave;o &Acirc;m Vương Phủ sau, ch&iacute;nh l&agrave; Ch&acirc;n Nh&acirc;n T&uacute; hiện trường.&quot;<br />\r\n<br />\r\n&quot;Mọi người l&acirc;m trận ph&aacute;t huy đi.&quot;<br />\r\n<br />\r\nK&egrave;m theo lời như vậy, mọi người rất nhanh tiến v&agrave;o &Acirc;m Vương Phủ.<br />\r\n<br />\r\nĐẩy ra &Acirc;m Vương Phủ nặng nề m&ocirc;n, kh&ocirc;ng biết tại sao, mọi người chỉ cảm thấy một cổ &acirc;m phong tr&agrave;n tới.<br />\r\n<br />\r\nĐi tuốt ở đ&agrave;ng trước Dịch Thừa, kh&ocirc;ng nhịn được đ&aacute;nh một c&aacute;i giật m&igrave;nh, hắn miễn cười gượng n&oacute;i: &quot;Đ&acirc;y ch&iacute;nh l&agrave; để cho người ta nghe tin đ&atilde; sợ mất mật &Acirc;m Vương Phủ sao? Nh&igrave;n cũng kh&ocirc;ng c&oacute; g&igrave;.&quot;<br />\r\n<br />\r\n&quot;Vẫn cẩn thận một ch&uacute;t đi.&quot; Đinh Mộ Ngưng yểu điệu n&oacute;i. N&agrave;ng diễn kỹ phi thường xuất sắc, đem một c&aacute;i hoa kh&ocirc;i diễn dịch rất xuất sắc.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n ở trong đ&aacute;m người, cũng kh&ocirc;ng l&agrave;m người kh&aacute;c ch&uacute; &yacute;.<br />\r\n<br />\r\n&Aacute;nh mắt của hắn cảnh gi&aacute;c nh&igrave;n xung quanh bốn ph&iacute;a, cẩn thận từng li từng t&iacute; n&oacute;i: &quot;Cẩn thận một ch&uacute;t, &Acirc;m Vương Phủ b&ecirc;n trong đ&atilde; chết kh&ocirc;ng &iacute;t người.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng muốn lo lắng như vậy.&quot; Dịch Thừa đi tới, vỗ vỗ bả vai hắn, cười n&oacute;i: &quot;Đồng Ngọc Long, ngươi h&atilde;y y&ecirc;n t&acirc;m, hết thảy c&oacute; ta đ&acirc;y.&quot;<br />\r\n<br />\r\nNh&igrave;n hắn tr&agrave;n đầy tự tin d&aacute;ng vẻ, nội t&acirc;m của Di&ecirc;u Thi&ecirc;n Qu&acirc;n thập phần khinh thường.<br />\r\n<br />\r\nDịch Thừa ngăn ở trước mặt Di&ecirc;u Thi&ecirc;n Qu&acirc;n, vẻ mặt tươi cười chỉ huy mọi người, h&agrave;nh tẩu ở &Acirc;m Vương Phủ ch&iacute;nh giữa.<br />\r\n<br />\r\nHắn c&oacute; ch&iacute;nh m&igrave;nh tiểu to&aacute;n b&agrave;n, ở trong đ&aacute;m người, Di&ecirc;u Thi&ecirc;n Qu&acirc;n nhan gi&aacute; trị cao nhất, đối với hắn cực kỳ c&oacute; uy hiếp.<br />\r\n<br />\r\nV&igrave; vậy chỗ hắn nơi ch&egrave;n &eacute;p Di&ecirc;u Thi&ecirc;n Qu&acirc;n, triển ph&aacute;t hiện m&igrave;nh diễn kỹ. Liền l&agrave; hi vọng ch&iacute;nh m&igrave;nh bộc lộ t&agrave;i năng.<br />\r\n<br />\r\nTr&agrave;ng n&agrave;y Ch&acirc;n Nh&acirc;n T&uacute;, hắn nhất định phải r&uacute;t ra đầu tr&ugrave;!<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n sớm liền nh&igrave;n ra &yacute; tưởng của hắn, cũng kh&ocirc;ng đi theo hắn cạnh tranh. D&ugrave; sao b&acirc;y giờ hắn diễn l&agrave; vai phụ. Nh&acirc;n vật ch&iacute;nh l&agrave; Dịch Thừa.<br />\r\n<br />\r\n&Acirc;m Vương Phủ b&ecirc;n trong thập phần &acirc;m trầm, v&ocirc; luận l&agrave; kiến tr&uacute;c, hay lại l&agrave; những vật kh&aacute;c, r&otilde; r&agrave;ng l&agrave; năm ngo&aacute;i x&acirc;y cất. Cũng kh&ocirc;ng biết tại sao, lu&ocirc;n c&oacute; một loại cũ n&aacute;t thối rữa cảm gi&aacute;c.<br />\r\n<br />\r\nDịch Thừa mang theo mọi người h&agrave;nh tẩu ở Vương phủ ch&iacute;nh giữa, thỉnh thoảng nhất kinh nhất sạ. Cả người to&aacute;t ra cảm gi&aacute;c sợ h&atilde;i.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Oa, đ&acirc;y l&agrave; vật g&igrave;?&quot;<br />\r\n<br />\r\n&quot;Oa, nơi n&agrave;y qu&aacute; &acirc;m trầm rồi hả?&quot;<br />\r\n<br />\r\nHắn vẻ mặt kinh ho&agrave;ng d&aacute;ng vẻ, &aacute;nh mắt sợ h&atilde;i nh&igrave;n bốn ph&iacute;a.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n liếc hắn một c&aacute;i, nội t&acirc;m thập phần khinh thường. Liền loại n&agrave;y diễn kỹ, căn bản kh&ocirc;ng tư c&aacute;ch xưng l&agrave; diễn vi&ecirc;n.<br />\r\n<br />\r\nLoại n&agrave;y cố l&agrave;m kinh ngạc d&aacute;ng vẻ, ngoại trừ l&agrave;m người ta n&ocirc;n mửa b&ecirc;n ngo&agrave;i, căn bản kh&ocirc;ng c&oacute; ch&uacute;t &yacute; nghĩa n&agrave;o.<br />\r\n<br />\r\nNhưng những n&agrave;y nh&acirc;n, v&igrave; thể ph&aacute;t hiện m&igrave;nh sợ h&atilde;i, từng c&aacute;i thỉnh thoảng ph&aacute;t ra tiếng th&aacute;n phục &acirc;m, c&ograve;n c&oacute; nh&acirc;n thấy một c&aacute;i &acirc;m trầm căn ph&ograve;ng. Sẽ h&ugrave; dọa cả người ph&aacute;t run, chỉ cửa gian ph&ograve;ng. Kh&ocirc;ng n&oacute;i ra lời.<br />\r\n<br />\r\n&quot;Trời ạ, c&aacute;i n&agrave;y &Acirc;m Vương Phủ qu&aacute; đ&aacute;ng sợ.&quot;<br />\r\n<br />\r\n&quot;Đ&uacute;ng vậy, ch&uacute;ng ta đi thăm xong cũng nhanh ch&uacute;t rời đi đi.&quot;<br />\r\n<br />\r\nNhững người n&agrave;y mặc d&ugrave; l&agrave; n&oacute;i như vậy đến, lại ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; sợ h&atilde;i d&aacute;ng vẻ.<br />\r\n<br />\r\nD&ugrave; l&agrave; vụng về diễn kỹ, thậm ch&iacute; để cho th&eacute;t ch&oacute;i tai cũng lộ ra cực kỳ l&agrave;m bộ.<br />\r\n<br />\r\n&quot;A, qu&aacute; đ&aacute;ng sợ.&quot; Đinh Mộ Ngưng đột nhi&ecirc;n h&eacute;t l&ecirc;n một tiếng.<br />\r\n<br />\r\n&quot;Thế n&agrave;o?&quot; Dịch Thừa vội v&agrave;ng đi tới, &ocirc;m lấy n&agrave;ng. Vỗ bả vai n&agrave;ng, nhẹ giọng an ủi: &quot; Cục cưng, đừng sợ, hết thảy c&oacute; ta.&quot;<br />\r\n<br />\r\nĐinh Mộ Ngưng trừng mắt liếc hắn một c&aacute;i, trực tiếp đẩy hắn ra. Ph&ograve;ng ngừa hắn chiếm tiện nghi.<br />\r\n<br />\r\nN&agrave;ng l&ograve;ng vẫn c&ograve;n sợ h&atilde;i chỉ ph&ograve;ng lương, sắc mặt kinh h&atilde;i n&oacute;i: &quot;C&aacute;c ngươi mau nh&igrave;n!&quot;<br />\r\n<br />\r\n&Aacute;nh mắt cuả mọi người nh&igrave;n sang, lại ph&aacute;t hiện tr&ecirc;n x&agrave; nh&agrave;, treo một c&aacute;i nhuốm m&aacute;u lụa trắng.<br />\r\n<br />\r\nLụa trắng vết m&aacute;u đỏ nhạt, giống như c&oacute; người treo chết ở chỗ n&agrave;y như thế.<br />\r\n<br />\r\nĐinh Mộ Ngưng thấy một m&agrave;n như vậy, cả người run rẩy. &Aacute;nh mắt kinh ho&agrave;ng lui về ph&iacute;a sau. Một b&ecirc;n lui n&agrave;ng một b&ecirc;n h&ocirc;: &quot;Nơi n&agrave;y qu&aacute; đ&aacute;ng sợ, ch&uacute;ng ta đi những địa phương kh&aacute;c đi.&quot;<br />\r\n<br />\r\nChỉ l&agrave; Di&ecirc;u Thi&ecirc;n Qu&acirc;n từ n&agrave;ng đ&ocirc;i mắt s&acirc;u b&ecirc;n trong, thấy được một tia h&iacute; ngược.<br />\r\n<br />\r\nKh&ocirc;ng nghi ngờ ch&uacute;t n&agrave;o, từ đầu tới cuối, n&agrave;ng cũng kh&ocirc;ng c&oacute; sợ h&atilde;i. Chỉ l&agrave; cố &yacute; diễn xuất m&agrave; th&ocirc;i.<br />\r\n<br />\r\nTất cả mọi người đều đem nơi n&agrave;y, coi th&agrave;nh Ch&acirc;n Nh&acirc;n T&uacute;. Mỗi người đều muốn biểu diễn ch&iacute;nh m&igrave;nh diễn kỹ.<br />\r\n<br />\r\nNhưng bọn hắn cũng kh&ocirc;ng biết, đ&acirc;y l&agrave; một trận t&agrave;n nhẫn diễn xuất.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n đối Đinh Mộ Ngưng nghe lời răm rắp, lộ ra ch&iacute;nh m&igrave;nh cung k&iacute;nh một mặt.<br />\r\n<br />\r\nBởi v&igrave; hắn đ&oacute;ng vai l&agrave; Đinh Mộ Ngưng người &aacute;i mộ.<br />\r\n<br />\r\nNhưng hắn cử động, lại để cho người chung quanh hiểu lầm. Cho l&agrave; hắn muốn nh&acirc;n cơ hội tr&ecirc;u muội. V&igrave; vậy đều rất gạt bỏ hắn.<br />\r\n<br />\r\n&quot;Ai nha, thật l&agrave; qu&aacute; đ&aacute;ng sợ.&quot;<br />\r\n<br />\r\n&quot;Trời ạ, l&agrave;m sao sẽ đ&aacute;ng sợ như thế.&quot;<br />\r\n<br />\r\nNhững người n&agrave;y như cũ cho thấy ch&iacute;nh m&igrave;nh ph&ocirc; trương diễn kỹ, v&ocirc; luận nam nữ, đối chung quanh &acirc;m trầm ho&agrave;n cảnh, cũng biểu hiện tương đối sợ h&atilde;i.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n giống vậy biểu hiện ra sợ h&atilde;i, chỉ l&agrave; cũng kh&ocirc;ng c&oacute; như vậy ph&ocirc; trương.<br />\r\n<br />\r\nHắn trong đầu, một mực suy t&iacute;nh, tiếp theo n&ecirc;n l&agrave;m g&igrave;.<br />\r\n<br />\r\nB&acirc;y giờ mặc d&ugrave; &Acirc;m Vương Phủ &acirc;m trầm, cũng kh&ocirc;ng c&oacute; cho thấy kinh khủng một mặt.<br />\r\n<br />\r\nC&oacute; thể k&egrave;m theo bọn họ c&agrave;ng l&uacute;c c&agrave;ng th&acirc;m nhập, nơi n&agrave;y trở n&ecirc;n c&agrave;ng ng&agrave;y c&agrave;ng đ&aacute;ng sợ.<br />\r\n<br />\r\nNhất l&agrave;, bọn họ đi thăm một c&aacute;i Tư thiết lập tại Vương phủ ph&ograve;ng giam, lần n&agrave;y ngay cả Di&ecirc;u Thi&ecirc;n Qu&acirc;n cũng kh&ocirc;ng nhịn được k&ecirc;u th&agrave;nh tiếng.<br />\r\n<br />\r\nỞ chỗ n&agrave;y khắp nơi đều l&agrave; kinh khủng h&igrave;nh cụ, lột da, ch&eacute;m eo, ngũ m&atilde; ph&acirc;n th&acirc;y, c&acirc;u ngũ h&igrave;nh, lăng tr&igrave;, ải thủ.<br />\r\n<br />\r\nỞ chỗ n&agrave;y kh&iacute; giới, mỗi một dạng cũng d&iacute;nh cũ kỹ vết m&aacute;u.<br />\r\n<br />\r\nCũng kh&ocirc;ng ai biết ở nơi n&agrave;y trong ph&ograve;ng giam, rốt cuộc c&oacute; bao nhi&ecirc;u thiếu đ&aacute;ng sợ đồ vật.<br />\r\n<br />\r\n&quot;Đi mau.&quot; Dịch Thừa sắc mặt trắng nhợt, &aacute;nh mắt tr&agrave;n đầy kinh h&atilde;i. Vội v&agrave;ng k&ecirc;u mọi người rời đi.<br />\r\n<br />\r\nMọi người thấy những thứ n&agrave;y h&igrave;nh cụ, từng c&aacute;i cũng cảm thấy kh&oacute; chịu.<br />\r\n<br />\r\nCho n&ecirc;n bọn họ vội v&agrave;ng rời đi, Di&ecirc;u Thi&ecirc;n Qu&acirc;n l&agrave; người cuối c&ugrave;ng rời đi, ở l&uacute;c sắp đi, hắn cầm đi một c&aacute;i sắc b&eacute;n chủy thủ.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nMặc d&ugrave; kh&ocirc;ng biết, tiếp đ&oacute; sẽ ph&aacute;t sinh c&aacute;i g&igrave;. Nhưng kh&ocirc;ng nghi ngờ ch&uacute;t n&agrave;o, c&oacute; một c&acirc;y chủy thủ hộ th&acirc;n, nhất định l&agrave; c&oacute; tương đối tốt nơi.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n đi ở trong đ&aacute;m người, đối Đinh Mộ Ngưng chiếu cố c&oacute; thừa.<br />\r\n<br />\r\nT&ugrave;y thời lo lắng n&agrave;ng nhất cử nhất động, đối với hắn h&agrave;nh vi, người chung quanh &aacute;nh mắt bất thiện.<br />\r\n<br />\r\nNgược lại l&agrave; Đinh Mộ Ngưng, &aacute;nh mắt nhu t&igrave;nh nh&igrave;n hắn, đối với c&aacute;i n&agrave;y c&aacute;i nhan gi&aacute; trị cực cao, tướng mạo đẹp trai Di&ecirc;u Thi&ecirc;n Qu&acirc;n. N&agrave;ng cũng c&oacute; ch&uacute;t hảo cảm.<br />\r\n<br />\r\nChỉ l&agrave; n&agrave;ng cũng kh&ocirc;ng biết, mặc d&ugrave; Di&ecirc;u Thi&ecirc;n Qu&acirc;n nh&igrave;n, đối với n&agrave;ng thập phần k&iacute;nh mến.<br />\r\n<br />\r\nC&oacute; thể từ đầu tới cuối hắn chỉ l&agrave; đang diễn tr&ograve; m&agrave; th&ocirc;i.<br />\r\n<br />\r\nQuỷ dị &Acirc;m Vương Phủ, ở chỗ n&agrave;y coi như ban ng&agrave;y, cũng lộ ra thập phần u &aacute;m.<br />\r\n<br />\r\nĐi&ecirc;u lầu ngọc thế Vương phủ, chung quanh tr&ocirc;i giạt băng lụa m&agrave;u, rộng r&atilde;i trong s&acirc;n y&ecirc;n lặng như tờ, chỉ c&oacute; u o&aacute;n phong thanh cử động chấn song c&ugrave;ng m&agrave;u trắng đ&egrave;n lồng giấy, k&eacute;t vang dội.<br />\r\n<br />\r\nH&agrave;nh tẩu ở trong đ&oacute;, chỉ c&oacute; sợ h&atilde;i ở t&acirc;m l&yacute; tr&agrave;n ngập, c&agrave;ng ng&agrave;y c&agrave;ng s&acirc;u, c&agrave;ng ng&agrave;y c&agrave;ng s&acirc;u...<br />\r\n<br />\r\nDịch Thừa đi tuốt ở đ&agrave;ng trước, vẫn biểu hiện đến khoa trương diễn kỹ.<br />\r\n<br />\r\nKh&ocirc;ng ngừng ph&aacute;t ra tiếng th&aacute;n phục &acirc;m, c&oacute; thể k&egrave;m theo c&agrave;ng đi xuống, hắn trở n&ecirc;n c&agrave;ng ng&agrave;y c&agrave;ng kinh ho&agrave;ng.<br />\r\n<br />\r\n&quot;C&aacute;i địa phương quỷ qu&aacute;i n&agrave;y, nh&igrave;n thật l&agrave; khủng bố. Cực kỳ đi một v&ograve;ng, cũng kh&ocirc;ng c&oacute; g&igrave; chứ sao.&quot; Dịch Thừa cười một c&aacute;i, đột nhi&ecirc;n vỗ vỗ tay, hướng mọi người tuy&ecirc;n bố: &quot;Đ&atilde; như vậy, mọi người kh&ocirc;ng bằng t&aacute;ch ra h&agrave;nh động đi. Mọi người khắp nơi đi thăm một ch&uacute;t.&quot;<br />\r\n<br />\r\nHắn giọng rất dễ d&agrave;ng, phảng phất một người hướng dẫn du lịch như thế.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n trong l&ograve;ng căng thẳng, tới!<br />\r\n<br />\r\nỞ &Acirc;m Vương Phủ thời gian d&agrave;i như vậy, mặc d&ugrave; chung quanh nh&igrave;n thật &acirc;m trầm. Nhưng cũng kh&ocirc;ng c&oacute; c&aacute;i g&igrave; chuyện lạ t&igrave;nh ph&aacute;t sinh.<br />\r\n<br />\r\nNếu như một mực tiếp tục như vậy, ngược lại cũng kh&ocirc;ng tệ.<br />\r\n<br />\r\nChỉ tiếc, thăng bằng lại bị Dịch Thừa ph&aacute; vỡ.<br />\r\n<br />\r\nHắn l&agrave; chủ gi&aacute;c, lại n&oacute;i ra, kh&ocirc;ng n&ecirc;n nhất n&oacute;i ra lời.</p>', '2021-12-01 14:53:59', 7, 1),
(297, 75, 'Đùa Mà Thành Thật', '7', '<p>Ở phim kinh dị nhất qu&aacute;n bộ s&aacute;ch v&otilde; thuật b&ecirc;n trong, kinh khủng nhất kh&ocirc;ng phải kinh khủng tới, m&agrave; l&agrave; từng c&aacute;i người kh&ocirc;ng phải l&agrave; muốn ph&acirc;n t&aacute;n m&agrave; đi, cuối c&ugrave;ng mỗi người chết thảm.<br />\r\n<br />\r\nH&agrave;nh động đơn độc, ở phim kinh dị b&ecirc;n trong, ngu xuẩn nhất h&agrave;nh vi.<br />\r\n<br />\r\nĐ&atilde; c&oacute; v&ocirc; số người, v&igrave; vậy bỏ ra gi&aacute;.<br />\r\n<br />\r\nV&igrave; vậy Di&ecirc;u Thi&ecirc;n Qu&acirc;n trực tiếp phản đối: &quot;Kh&ocirc;ng cần phải đơn đi, nơi n&agrave;y qu&aacute; &acirc;m trầm rồi. Vạn nhất ra một &iacute;t chuyện l&agrave;m sao b&acirc;y giờ?&quot;<br />\r\n<br />\r\nMới vừa rồi một mực trầm mặc &iacute;t n&oacute;i, kh&ocirc;ng n&oacute;i một c&acirc;u, mang g&atilde; đeo k&iacute;nh tử, v&agrave;o l&uacute;c n&agrave;y giống vậy phản đối: &quot;Kh&ocirc;ng sai, nơi n&agrave;y ch&iacute;nh l&agrave; &Acirc;m Vương Phủ, thật l&agrave; nhiều người đều chết ở b&ecirc;n trong.&quot;<br />\r\n<br />\r\n&quot;&Acirc;m Vương Phủ chỉ l&agrave; một Truyền Thuyết m&agrave; th&ocirc;i, căn bản kh&ocirc;ng cần sợ h&atilde;i.&quot; Dịch Thừa mặt đầy khinh thường, hắn cố l&agrave;m cao ngạo d&aacute;ng vẻ: &quot;Chẳng lẽ ngươi sợ?&quot;<br />\r\n<br />\r\n&quot; Đ&uacute;ng, ta sợ, ta muốn c&ugrave;ng với c&aacute;c ngươi.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n mặt đầy kinh ho&agrave;ng n&oacute;i, th&acirc;n thể cũng hướng của bọn hắn nh&iacute;ch lại gần.<br />\r\n<br />\r\nHắn đ&atilde; quyết định chủ &yacute;, v&ocirc; luận như thế n&agrave;o, cũng phải đi theo Dịch Thừa c&ugrave;ng Đinh Mộ Ngưng b&ecirc;n người.<br />\r\n<br />\r\nV&ocirc; luận phim kinh dị biết bao nguy hiểm, như vậy đi theo nh&acirc;n vật ch&iacute;nh b&ecirc;n người, cũng c&oacute; thể chuyển nguy th&agrave;nh an.<br />\r\n<br />\r\n&quot;Thật l&agrave; một t&ecirc;n quỷ nh&aacute;t gan.&quot; Dịch Thừa khinh thường nh&igrave;n Di&ecirc;u Thi&ecirc;n Qu&acirc;n liếc mắt, nội t&acirc;m lại c&oacute; điểm nổi n&oacute;ng.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n c&aacute;i n&agrave;y vai phụ, nhất định phải cướp ch&iacute;nh m&igrave;nh nh&acirc;n vật ch&iacute;nh vai diễn.<br />\r\n<br />\r\nChẳng lẽ hắn thật vừa &yacute; Đinh Mộ Ngưng rồi hả?<br />\r\n<br />\r\nHắn trong l&ograve;ng tức giận, c&oacute; thể dưới mắt hắn cũng kh&ocirc;ng tiện ph&aacute;t t&aacute;c. Dứt kho&aacute;t phất tay một c&aacute;i, t&ugrave;y tiện n&oacute;i: &quot;Đ&atilde; như vậy, mọi người c&oacute; nguyện &yacute; h&agrave;nh động đơn độc, liền đi xem một ch&uacute;t đi. Kh&ocirc;ng muốn, c&ugrave;ng với ch&uacute;ng ta.&quot;<br />\r\n<br />\r\nHắn vừa dứt lời, v&agrave;i người liền quyết định h&agrave;nh động đơn độc.<br />\r\n<br />\r\nD&ugrave; sao đi theo nh&acirc;n vật ch&iacute;nh b&ecirc;n người, căn bản l&agrave; kh&ocirc;ng c&oacute; c&aacute;ch triển ph&aacute;t hiện m&igrave;nh diễn kỹ. Kh&ocirc;ng bằng khắp nơi nh&igrave;n một ch&uacute;t.<br />\r\n<br />\r\nỞ trong mắt bọn hắn, &Acirc;m Vương Phủ chỉ l&agrave; một Ch&acirc;n Nh&acirc;n tu tr&agrave;ng.<br />\r\n<br />\r\nỞ chỗ n&agrave;y địa phương ẩn n&uacute;p, đều đ&atilde; an gắn m&aacute;y thu h&igrave;nh.<br />\r\n<br />\r\nV&igrave; vậy bọn họ hết sức muốn triển hiện năng lực m&igrave;nh, hy vọng c&oacute; thể bị Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng nh&igrave;n tr&uacute;ng, liền c&oacute; cơ hội trở th&agrave;nh đại minh tinh.<br />\r\n<br />\r\nNhững người kh&aacute;c rời đi, chỉ c&oacute; Di&ecirc;u Thi&ecirc;n Qu&acirc;n đi theo Dịch Thừa c&ugrave;ng Đinh Mộ Ngưng b&ecirc;n người.<br />\r\n<br />\r\nDịch Thừa đ&atilde; đem Di&ecirc;u Thi&ecirc;n Qu&acirc;n, coi th&agrave;nh ch&iacute;nh m&igrave;nh lớn nhất cạnh tranh đối tượng.<br />\r\n<br />\r\nV&igrave; vậy hắn mang theo Đinh Mộ Ngưng ở &Acirc;m Vương Phủ du đ&atilde;ng thời điểm, đối với n&agrave;ng quan t&acirc;m đầy đủ. Cho thấy một bộ ấm d&aacute;ng vẻ.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n với ở hai người bọn họ sau lưng, cảnh gi&aacute;c quan s&aacute;t, trong l&ograve;ng đang suy tư, tiếp theo n&ecirc;n l&agrave;m g&igrave;.<br />\r\n<br />\r\nĐang l&uacute;c n&agrave;y, một tiếng k&ecirc;u th&ecirc; lương thảm thiết, đưa tới mọi người ch&uacute; &yacute; lực.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nRất nhanh, mọi người liền hướng k&ecirc;u thảm thiết địa phương chạy tới.<br />\r\n<br />\r\nĐợi mọi người chạy tới sau, lại thấy một người nữ sinh, hướng về ph&iacute;a một căn ph&ograve;ng k&ecirc;u la om s&ograve;m.<br />\r\n<br />\r\n&quot;Thế n&agrave;o?&quot; Dịch Thừa đi tới.<br />\r\n<br />\r\n&quot;Nơi n&agrave;y thật giống như c&oacute; một cỗ thi thể.&quot; Nữ nh&acirc;n kinh ho&agrave;ng chỉ nh&agrave;, sắc mặt v&ocirc; c&ugrave;ng trắng bệch.<br />\r\n<br />\r\nNhững người khác vội v&agrave;ng x&iacute;t tới, rất nhanh mọi người liền bật cười.<br />\r\n<br />\r\nTrong ph&ograve;ng x&aacute;c thực c&oacute; một c&aacute;i người giả.<br />\r\n<br />\r\nC&aacute;i n&agrave;y giả tr&ecirc;n người c&oacute; đao ch&eacute;m r&igrave;u chặt vết t&iacute;ch, ph&iacute;a tr&ecirc;n khắp nơi đều l&agrave; vết thương. Chỉ tiếc, n&agrave;y d&ugrave; sao chỉ l&agrave; một người giả.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n đi tới, &aacute;nh mắt nh&igrave;n về ph&iacute;a người giả.<br />\r\n<br />\r\nC&aacute;i n&agrave;y người giả l&agrave; đi&ecirc;u khắc, đ&atilde; bị ch&eacute;m ho&agrave;n to&agrave;n thay đổi.<br />\r\n<br />\r\nNhưng hắn tử quan s&aacute;t kỹ đi qua, nhưng trong l&ograve;ng nhiều hơn một tia khủng hoảng.<br />\r\n<br />\r\nC&aacute;i n&agrave;y người giả nh&igrave;n một c&aacute;i ch&iacute;nh l&agrave; gặp một người bạo lực vung ch&eacute;m, từ người giả vết t&iacute;ch đến xem, đối phương tựa hồ đưa c&aacute;i n&agrave;y người giả coi th&agrave;nh cừu địch, hạ thủ tương đối t&agrave;n bạo.<br />\r\n<br />\r\nHắn tại sao phải l&agrave;m như vậy? Hết thảy c&aacute;c thứ n&agrave;y cũng kh&ocirc;ng biết được.<br />\r\n<br />\r\nMọi người giải t&aacute;n lập tức, ai cũng kh&ocirc;ng coi l&agrave; chuyện to t&aacute;t.<br />\r\n<br />\r\n&quot;Nhất kinh nhất sạ, thật l&agrave; nh&aacute;t gan.&quot;<br />\r\n<br />\r\n&quot;Đ&uacute;ng vậy, c&aacute;i n&agrave;y c&oacute; g&igrave;.&quot;<br />\r\n<br />\r\nNhững người n&agrave;y cũng rất khinh thường, nội t&acirc;m của Di&ecirc;u Thi&ecirc;n Qu&acirc;n im lặng kh&ocirc;ng l&ecirc;n tiếng, trong đầu lại kinh h&atilde;i muốn chết.<br />\r\n<br />\r\nKh&ocirc;ng nghi ngờ ch&uacute;t n&agrave;o, lớn hơn kinh khủng gần sẽ xuất tr&agrave;ng.<br />\r\n<br />\r\nTiếng h&eacute;t thảm n&agrave;y, chỉ l&agrave; l&agrave;m cho n&agrave;y tr&agrave;ng thảm kịch ph&oacute;ng mở một c&aacute;i mở m&agrave;n m&agrave; th&ocirc;i.<br />\r\n<br />\r\nSau đ&oacute; thời gian, Di&ecirc;u Thi&ecirc;n Qu&acirc;n trở n&ecirc;n c&agrave;ng nh&aacute;t gan.<br />\r\n<br />\r\nNh&igrave;n hắn chiến chiến nguy nguy d&aacute;ng vẻ, Dịch Thừa dĩ nhi&ecirc;n l&agrave; cực độ khinh thường, l&ecirc;n tiếng ch&acirc;m chọc mấy c&acirc;u.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n im lặng kh&ocirc;ng l&ecirc;n tiếng, cũng kh&ocirc;ng phản kh&aacute;ng, biểu hiện tương đối ủy khuất.<br />\r\n<br />\r\nNgay tại Đinh Mộ Ngưng mở lời an ủi hắn thời điểm, lại vừa l&agrave; một tiếng đột ngột k&ecirc;u thảm thiết vang l&ecirc;n.<br />\r\n<br />\r\nC&aacute;i thanh &acirc;m n&agrave;y v&ocirc; c&ugrave;ng th&ecirc; thảm, phảng phất l&agrave; một người trước khi chết tiếng k&ecirc;u.<br />\r\n<br />\r\nTrong l&ograve;ng Di&ecirc;u Thi&ecirc;n Qu&acirc;n căng thẳng, chẳng lẽ c&oacute; nh&acirc;n phải chết?<br />\r\n<br />\r\nNhưng Đinh Mộ Ngưng cũng rất kh&ocirc;ng ki&ecirc;n nhẫn, n&agrave;ng cau m&agrave;y, dậm ch&acirc;n n&oacute;i: &quot;Cũng l&uacute;c n&agrave;y, ai c&ograve;n đ&ugrave;a kiểu n&agrave;y.&quot;<br />\r\n<br />\r\n&quot;Đừng để &yacute; tới hắn.&quot; Dịch Thừa dửng dưng n&oacute;i.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n lu&ocirc;n cảm thấy, tiếng h&eacute;t thảm n&agrave;y kh&ocirc;ng phải đ&ugrave;a.<br />\r\n<br />\r\nNhưng hắn lại kh&ocirc;ng d&aacute;m rời đi n&agrave;y hai người b&ecirc;n người.<br />\r\n<br />\r\nV&ocirc; luận như thế n&agrave;o, đi theo nh&acirc;n vật ch&iacute;nh b&ecirc;n người vậy đ&uacute;ng rồi.<br />\r\n<br />\r\nChỉ cần nh&acirc;n vật ch&iacute;nh bất tử, ta cũng kh&ocirc;ng chết được.<br />\r\n<br />\r\nGiờ ph&uacute;t n&agrave;y Di&ecirc;u Thi&ecirc;n Qu&acirc;n ch&iacute;nh l&agrave; như vậy &yacute; tưởng.<br />\r\n<br />\r\nC&oacute; thể tiếp đ&oacute;, lại vừa l&agrave; một tiếng k&ecirc;u th&ecirc; lương thảm thiết, k&egrave;m theo c&ograve;n c&oacute; giết người tiếng qu&aacute;t th&aacute;o.<br />\r\n<br />\r\nLần n&agrave;y Dịch Thừa c&oacute; ch&uacute;t sợ h&atilde;i, &aacute;nh mắt của hắn kinh hoảng nh&igrave;n Di&ecirc;u Thi&ecirc;n Qu&acirc;n, cả người run run n&oacute;i: &quot;Ch&uacute;ng ta qua xem một ch&uacute;t đi, n&oacute;i kh&ocirc;ng chừng thật c&oacute; người chết rồi.&quot;<br />\r\n<br />\r\n&quot;Ch&uacute;ng ta đi qua đi.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n bất đắc dĩ gật đầu một c&aacute;i.<br />\r\n<br />\r\nKhi bọn hắn chạy tới k&ecirc;u thảm thiết ph&aacute;t sinh phương l&uacute;c, lại thấy một người nằm ở vũng m&aacute;u ch&iacute;nh giữa. Th&acirc;n thể của hắn ph&iacute; c&ocirc;ng gi&ugrave;ng giằng, hai tay run rẩy nắm thổ địa.<br />\r\n<br />\r\nDịch Thừa tối trước đi tới, khen ngợi một tiếng: &quot;Diễn thật đ&uacute;ng l&agrave; giống như.&quot;<br />\r\n<br />\r\nBất qu&aacute; hắn lập tức một bộ kinh ho&agrave;ng d&aacute;ng vẻ, hắn vội v&agrave;ng vọt tới, đem người n&agrave;y lật l&ecirc;n, vẻ mặt bi phẫn h&ocirc;: &quot;L&agrave; ai giết ngươi? N&oacute;i cho ta biết hung thủ l&agrave; ai?&quot;<br />\r\n<br />\r\nHắn hết sức lộ ra ch&iacute;nh m&igrave;nh bi phẫn, tr&ecirc;n mặt nhưng vẫn l&agrave; một bộ sắp bật cười d&aacute;ng vẻ.<br />\r\n<br />\r\nĐinh Mộ Ngưng đi tới, thấy một m&agrave;n như vậy, buồn cười lại kh&ocirc;ng d&aacute;m cười.<br />\r\n<br />\r\nNhưng Di&ecirc;u Thi&ecirc;n Qu&acirc;n lại kh&ocirc;ng cười nổi.<br />\r\n<br />\r\nTr&ecirc;n người người n&agrave;y kh&ocirc;ng ngừng chảy m&aacute;u, mặt mũi v&ocirc; c&ugrave;ng trắng bệch.<br />\r\n<br />\r\nHắn bị vết thương tr&iacute; mạng!<br />\r\n<br />\r\nDịch Thừa kịch liệt lắc lắc người n&agrave;y th&acirc;n thể, biểu hiện ph&aacute; lệ k&iacute;ch động: &quot;N&oacute;i cho ta biết! L&agrave; ai giết huynh đệ của ta! Ta muốn b&aacute;o th&ugrave; cho hắn.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nNgười n&agrave;y muốn mở miệng, nghi&ecirc;ng đầu một c&aacute;i, cũng đ&atilde; mất đi kh&iacute; tức.<br />\r\n<br />\r\n&quot;Ngươi đừng tử a huynh đệ, n&oacute;i cho ta biết hung thủ l&agrave; ai!&quot; Con mắt của Dịch Thừa đỏ bừng, &aacute;nh mắt bi phẫn nh&igrave;n hắn. Cả người phảng phất một c&aacute;i C&ocirc; Lang như thế.<br />\r\n<br />\r\nNhưng k&egrave;m theo hắn lay động, tr&ecirc;n người người n&agrave;y m&aacute;u tươi, kh&ocirc;ng ngừng chảy xu&ocirc;i ở tr&ecirc;n người hắn.<br />\r\n<br />\r\nHắn vừa mới bắt đầu c&ograve;n lơ đễnh, chỉ l&agrave; đến ph&iacute;a sau, gay mũi m&ugrave;i m&aacute;u tanh, để cho sắc mặt hắn hơi kh&oacute; coi rồi.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng cần phải chơi đ&ugrave;a như vậy thật chứ ?&quot; Dịch Thừa cười khan một tiếng, r&uacute;t tay về, nh&igrave;n người n&agrave;y trắng x&aacute;m sắc mặt. Run rẩy đem ng&oacute;n tay đặt ở hắn dưới mũi mặt.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; một tí kh&iacute; tức!<br />\r\n<br />\r\nHắn giật m&igrave;nh một ch&uacute;t, r&uacute;t tay trở về chỉ.<br />\r\n<br />\r\nSau đ&oacute; cười khan một tiếng, v&eacute;n l&ecirc;n quần &aacute;o của hắn : &quot;Huynh đệ chớ giả bộ, chơi đ&ugrave;a như vậy thật, ta đều bị giật m&igrave;nh.&quot;<br />\r\n<br />\r\nChậm r&atilde;i v&eacute;n l&ecirc;n quần &aacute;o của hắn , Dịch Thừa mặt mũi, trong ph&uacute;t chốc đ&atilde; kinh biến đến mức v&ocirc; c&ugrave;ng nhợt nhạt.<br />\r\n<br />\r\n&quot;Thế n&agrave;o... Khả năng?&quot;<br />\r\n<br />\r\nHắn trừng lớn con mắt, đồng tử thu nhỏ lại, &aacute;nh mắt tr&agrave;n đầy kinh h&atilde;i.<br />\r\n<br />\r\nTrước mắt l&agrave; một c&aacute;i to lớn xuy&ecirc;n qua vết thương, vẫn c&ograve;n đang chảy m&aacute;u.<br />\r\n<br />\r\nN&agrave;y căn bản liền kh&ocirc;ng phải diễn xuất!<br />\r\n<br />\r\n&quot;A!&quot; Dịch Thừa cổ họng ch&iacute;nh giữa, ph&aacute;t ra một tiếng th&eacute;t ch&oacute;i tai. Vội v&agrave;ng bu&ocirc;ng lỏng người n&agrave;y th&acirc;n thể.<br />\r\n<br />\r\nHắn run lập cập h&ocirc;, sắc mặt mặt như m&agrave;u đất: &quot;Người chết! Chết thật rồi!&quot;<br />\r\n<br />\r\nĐinh Mộ Ngưng cũng kh&ocirc;ng cười được, n&agrave;ng cũng nh&igrave;n thấy người n&agrave;y vết thương.<br />\r\n<br />\r\n&quot;L&agrave;m sao sẽ người chết đ&acirc;y?&quot;<br />\r\n<br />\r\nN&agrave;ng vừa n&oacute;i, một b&ecirc;n cầm l&ecirc;n điện thoại di động, hướng về ph&iacute;a b&aacute;n kh&ocirc;ng k&ecirc;u: &quot;Quay chụp tạm ngừng, c&oacute; người chết thật rồi! Nhanh l&ecirc;n một ch&uacute;t k&ecirc;u cảnh s&aacute;t.&quot;<br />\r\n<br />\r\nRun lập cập đến, n&agrave;ng đẩy gọi điện thoại.<br />\r\n<br />\r\nC&oacute; thể điện thoại v&ocirc; luận như thế n&agrave;o cũng kh&ocirc;ng gọi được, lần n&agrave;y n&agrave;ng nhất thời trở n&ecirc;n kinh hoảng v&ocirc; c&ugrave;ng.</p>', '2021-12-01 14:54:22', 7, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(298, 75, 'Diễn Xuất Vẫn Còn Tiếp Tục', '8', '<p>&quot;Điện thoại di động ta kh&ocirc;ng thể d&ugrave;ng, điện thoại di động của ngươi c&oacute; thể sử dụng sao?&quot; Đinh Mộ Ngưng kinh ho&agrave;ng hỏi.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n lắc đầu một c&aacute;i, cầm điện thoại di động l&ecirc;n đưa cho n&agrave;ng nh&igrave;n: &quot;Điện thoại di động ta cũng kh&ocirc;ng t&iacute;n hiệu.&quot;<br />\r\n<br />\r\n&quot;Vậy phải l&agrave;m sao b&acirc;y giờ?&quot; Đinh Mộ Ngưng hốt hoảng.<br />\r\n<br />\r\nVốn chỉ l&agrave; một trận Ch&acirc;n Nh&acirc;n T&uacute;, lại ngo&agrave;i &yacute; muốn người chết!<br />\r\n<br />\r\nL&uacute;c n&agrave;y, những người khác lục tục đi tới.<br />\r\n<br />\r\n&quot;Thế n&agrave;o?&quot;<br />\r\n<br />\r\n&quot;Ta mới v&ugrave;a nghe được rất đ&aacute;ng sợ k&ecirc;u thảm thiết.&quot;<br />\r\n<br />\r\nChẳng qua l&agrave; khi bọn họ thấy thi thể sau, giống vậy h&eacute;t rầm l&ecirc;n. C&ograve;n c&oacute; nh&acirc;n chạy trối chết.<br />\r\n<br />\r\nCực kỳ mọi người d&ugrave; sao cũng l&agrave; người trưởng th&agrave;nh, rất nhanh tỉnh t&aacute;o lại.<br />\r\n<br />\r\nBọn họ kinh ho&agrave;ng trốn ở xa xa, nh&igrave;n c&aacute;ch đ&oacute; kh&ocirc;ng xa thi thể, ai cũng kh&ocirc;ng d&aacute;m đi qua.<br />\r\n<br />\r\nChỉ c&oacute; Di&ecirc;u Thi&ecirc;n Qu&acirc;n đi tới, đứng ở b&ecirc;n cạnh thi thể, tập trung tinh thần quan s&aacute;t cổ thi thể n&agrave;y.<br />\r\n<br />\r\nCổ thi thể n&agrave;y vừa mới chết kh&ocirc;ng l&acirc;u, l&agrave; bị người đ&acirc;m chết.<br />\r\n<br />\r\nNhọn lưỡi dao sắc b&eacute;n, xuy&ecirc;n qua hắn phổi. Hắn chết rất nhanh, như vậy c&oacute; thể thấy hung thủ ch&iacute;nh l&agrave; v&igrave; giết người.<br />\r\n<br />\r\nNếu như l&agrave; lời như vậy, hung thủ rất c&oacute; thể kh&ocirc;ng hề rời đi. Hoặc l&agrave; ngay tại mọi người ch&iacute;nh giữa.<br />\r\n<br />\r\nVừa nghĩ tới khả năng n&agrave;y, &aacute;nh mắt cuả Di&ecirc;u Thi&ecirc;n Qu&acirc;n c&agrave;n qu&eacute;t qu&aacute; những đ&aacute;m người n&agrave;y.<br />\r\n<br />\r\nTrừ hắn v&agrave; chết đi vị n&agrave;y, hiện trường nh&acirc;n c&ograve;n c&oacute; sáu c&aacute;i.<br />\r\n<br />\r\nS&aacute;u người n&agrave;y, chia ra l&agrave;m Đinh Mộ Ngưng c&ugrave;ng Dịch Thừa, Khương Thi&ecirc;n Kh&ocirc;ng, Dư Điền c&ugrave;ng Lạc Ấu Nam.<br />\r\n<br />\r\nC&ograve;n c&oacute; c&aacute;i kia từ đầu tới cuối, cũng kh&ocirc;ng c&oacute; tiếng tăm g&igrave; g&atilde; đeo k&iacute;nh tử.<br />\r\n<br />\r\nTrong đ&oacute; nam vai nữ ch&iacute;nh hiềm nghi c&oacute; thể loại bỏ, Di&ecirc;u Thi&ecirc;n Qu&acirc;n một mực với ở b&ecirc;n cạnh họ.<br />\r\n<br />\r\nBốn người kh&aacute;c đều c&oacute; trọng đại hiềm nghi.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n bất động thần sắc c&aacute;ch xa bọn họ, sợ bọn họ ch&iacute;nh giữa c&oacute; người nổi l&ecirc;n giết người!<br />\r\n<br />\r\n&quot;Điện thoại di động kh&ocirc;ng gọi được, ch&uacute;ng ta đi ra ngo&agrave;i b&aacute;o cảnh s&aacute;t đi.&quot;<br />\r\n<br />\r\n&quot;Đ&uacute;ng vậy, cũng người chết, Ch&acirc;n Nh&acirc;n T&uacute; đ&atilde; kết th&uacute;c.&quot;<br />\r\n<br />\r\nNhững người n&agrave;y đều tại b&agrave;n luận x&ocirc;n xao, thấy người chết, tất cả mọi người kh&ocirc;ng t&acirc;m t&igrave;nh đ&oacute;ng kịch.<br />\r\n<br />\r\nNhưng v&agrave;o l&uacute;c n&agrave;y, Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng ph&aacute;t ra nhắc nhở: &quot;Ở chưa ho&agrave;n th&agrave;nh diễn dịch trước, bất luận kẻ n&agrave;o đều kh&ocirc;ng thể rời đi. Nếu kh&ocirc;ng coi l&agrave; bu&ocirc;ng tha nhiệm vụ, đem sẽ bị bốc hơi khỏi thế gian.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Thật l&agrave; h&ugrave; dọa ai vậy.&quot; Dịch Thừa cười lạnh một tiếng, mắng một c&acirc;u: &quot;L&atilde;o Tử kh&ocirc;ng chơi!&quot;<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave; ch&oacute; m&aacute; Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng, l&agrave;m c&aacute;i g&igrave;.&quot;<br />\r\n<br />\r\nHắn mắng một c&acirc;u, người chung quanh rối r&iacute;t hưởng ứng.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng sai, c&aacute;i g&igrave; Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng, ch&iacute;nh l&agrave; gạt người đồ vật.&quot;<br />\r\n<br />\r\n&quot;Ch&uacute;ng ta kh&ocirc;ng diễn.&quot;<br />\r\n<br />\r\nNgay tại mọi người quần t&igrave;nh c&ocirc;ng phẫn thời điểm, một c&aacute;i thanh &acirc;m lạnh nhạt vang l&ecirc;n.<br />\r\n<br />\r\n&quot;Tuồng vui n&agrave;y, c&aacute;c ngươi v&ocirc; luận như thế n&agrave;o, đều phải diễn th&ocirc;i. Nếu kh&ocirc;ng chờ c&aacute;c ngươi, chỉ c&oacute; một con đường chết một c&aacute;i.&quot;<br />\r\n<br />\r\nN&oacute;i chuyện, ch&iacute;nh l&agrave; mang mắt k&iacute;nh, tướng mạo b&igrave;nh thường. Từ đầu đ&uacute;ng chỗ, cũng kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o cảm gi&aacute;c tồn tại nam nh&acirc;n.<br />\r\n<br />\r\nV&agrave;o l&uacute;c n&agrave;y, lại đi v&agrave;o trong đ&aacute;m người. &Aacute;nh mắt nh&igrave;n về ph&iacute;a người sở hữu.<br />\r\n<br />\r\n&quot;L&atilde;o Tử sẽ kh&ocirc;ng diễn? Ngươi c&ograve;n c&oacute; thể l&agrave;m g&igrave; ta?&quot; Dịch Thừa vẻ mặt bướng bỉnh h&ocirc;.<br />\r\n<br />\r\nNam nh&acirc;n lắc đầu một c&aacute;i, hắn cầm điện thoại di động l&ecirc;n, chỉ đến m&agrave;n h&igrave;nh điện thoại di động, vẻ mặt nghi&ecirc;m t&uacute;c n&oacute;i: &quot;Như vậy diễn xuất ta đ&atilde; tham gia ba trận rồi, mỗi lần th&ocirc;i diễn biểu t&igrave;nh nh&acirc;n, cũng sẽ bị bốc hơi khỏi thế gian.&quot;<br />\r\n<br />\r\n&quot;L&atilde;o Tử vậy mới kh&ocirc;ng tin.&quot; Dịch Thừa mắng một c&acirc;u, trực tiếp đi về ph&iacute;a đại m&ocirc;n.<br />\r\n<br />\r\nNhững người khác cũng với ở b&ecirc;n cạnh hắn, chuẩn bị rời đi nơi n&agrave;y.<br />\r\n<br />\r\nC&oacute; thể đi đến đại m&ocirc;n, mọi người lại ph&aacute;t hiện cửa đ&oacute;ng chặt.<br />\r\n<br />\r\nDịch Thừa đạp một cước, đại m&ocirc;n v&ocirc; c&ugrave;ng vững chắc, căn bản kh&ocirc;ng mở ra.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng việc g&igrave;, ch&uacute;ng ta tr&egrave;o tường.&quot; Dịch Thừa l&ograve;ng tin tr&agrave;n đầy n&oacute;i.<br />\r\n<br />\r\n&Acirc;m Vương Phủ v&aacute;ch tường rất cao, đạt tới cao ba m&eacute;t, ho&agrave;n to&agrave;n coi l&agrave; th&acirc;m cung đại viện.<br />\r\n<br />\r\nNhưng ở mấy nam nh&acirc;n dưới sự gi&uacute;p đỡ, Dịch Thừa thứ nhất leo l&ecirc;n v&aacute;ch tường.<br />\r\n<br />\r\n&quot;Ta đi ra ngo&agrave;i trước b&aacute;o cảnh s&aacute;t, mọi người chờ ta tin tức tốt đi.&quot; Hắn đứng ở tr&ecirc;n v&aacute;ch tường, cười n&oacute;i.<br />\r\n<br />\r\nVừa n&oacute;i, đầu hắn lệch xoay qua chỗ kh&aacute;c, &aacute;nh mắt nh&igrave;n về ph&iacute;a ngo&agrave;i tường.<br />\r\n<br />\r\nSau một khắc, sắc mặt hắn trở n&ecirc;n lớn thay đổi.<br />\r\n<br />\r\nGiống như thấy được c&otilde;i đời n&agrave;y đ&aacute;ng sợ nhất thứ g&igrave; đ&oacute;.<br />\r\n<br />\r\nHắn mặt mũi v&ocirc; c&ugrave;ng kinh ho&agrave;ng, cả người run rẩy, con mắt trừng to lớn, phảng phất mau đưa hốc mắt mở ra như thế.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n cho tới b&acirc;y giờ kh&ocirc;ng từ tr&ecirc;n mặt người, thấy sợ h&atilde;i như vậy khu&ocirc;n mặt.<br />\r\n<br />\r\nKh&aacute; hơn nữa diễn kỹ, sợ rằng cũng kh&ocirc;ng l&agrave;m được.<br />\r\n<br />\r\nN&agrave;y là th&acirc;n người thể bản năng phản ứng.<br />\r\n<br />\r\nMiệng hắn trưởng to lớn, kh&oacute;e miệng đ&atilde; sắp muốn nứt mở.<br />\r\n<br />\r\nHắn muốn muốn l&ecirc;n tiếng, lại căn bản l&agrave; kh&ocirc;ng c&oacute; c&aacute;ch l&ecirc;n tiếng.<br />\r\n<br />\r\nHắn ở tr&ecirc;n v&aacute;ch tường căn bản đứng kh&ocirc;ng vững, cứ như vậy th&acirc;n thể chợt t&eacute; xuống.<br />\r\n<br />\r\nTh&acirc;n thể của hắn ng&atilde; xuống Vương phủ ch&iacute;nh giữa, l&agrave;m Di&ecirc;u Thi&ecirc;n Qu&acirc;n bọn họ bao hơi đi tới thời điểm, lại ph&aacute;t hiện sắc mặt của Dịch Thừa t&aacute;i nhợt, &aacute;nh mắt đờ đẫn, mi&ecirc;̣ng há đại đại, lại bị sống sờ sờ h&ugrave; chết!<br />\r\n<br />\r\n&quot;Người chết!&quot; Đinh Mộ Ngưng t&ecirc; liệt ngồi dưới đất, &aacute;nh mắt kinh h&atilde;i muốn chết.<br />\r\n<br />\r\nDư Điền run rẩy chỉ v&aacute;ch tường, &aacute;nh mắt kinh ho&agrave;ng tự lẩm bẩm: &quot;Ngo&agrave;i tường kết quả c&oacute; vật g&igrave;?&quot;<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; ai n&oacute;i cho hắn biết đ&aacute;p &aacute;n n&agrave;y, mọi người đ&atilde; bị trước mắt thảm kịch dọa sợ.<br />\r\n<br />\r\nHo&agrave;ng Tuyền T&uacute; Tr&agrave;ng lại ph&aacute;t tới tin tức: &quot;Đ&atilde; c&oacute; nh&acirc;n bởi v&igrave; cự tuyệt tr&agrave;ng n&agrave;y diễn xuất m&agrave; chết, c&aacute;c ngươi c&ograve;n phải với hắn sao?&quot;<br />\r\n<br />\r\nNh&igrave;n đến đ&acirc;y, tất cả mọi người đ&atilde; hiểu.<br />\r\n<br />\r\nHết thảy c&aacute;c thứ n&agrave;y đều l&agrave; Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng giở tr&ograve; quỷ, nhưng b&acirc;y giờ mọi người căn bản kh&ocirc;ng c&oacute; biện ph&aacute;p ch&uacute;t n&agrave;o.<br />\r\n<br />\r\nĐối mặt kh&ocirc;ng thể dự đo&aacute;n kh&ocirc;ng biết sợ h&atilde;i, mọi người chỉ c&oacute; kinh ho&agrave;ng run lẩy bẩy.<br />\r\n<br />\r\n&quot;Tiếp tục bắt đầu tr&agrave;ng n&agrave;y diễn xuất đi, nếu kh&ocirc;ng ch&uacute;ng ta đều phải chết.&quot; Đeo mắt kiếng nam tử đẩy một c&aacute;i mắt k&iacute;nh, &aacute;nh mắt bất đắc dĩ n&oacute;i.<br />\r\n<br />\r\n&quot; Được, ta diễn.&quot; &Aacute;nh mắt cuả Đinh Mộ Ngưng khẩn trương.<br />\r\n<br />\r\nKhi nh&igrave;n đến Dịch Thừa thảm kịch sau, tất cả mọi người căn bản kh&ocirc;ng d&aacute;m phản kh&aacute;ng.<br />\r\n<br />\r\nV&igrave; vậy mọi người dựa theo ch&iacute;nh m&igrave;nh nh&acirc;n vật, tiếp tục diễn.<br />\r\n<br />\r\nChỉ l&agrave; mỗi người biểu diễn, đều l&agrave; kh&oacute; coi.<br />\r\n<br />\r\nSợ h&atilde;i k&iacute;ch th&iacute;ch bọn họ tim, để cho bọn họ qu&ecirc;n mất ch&iacute;nh m&igrave;nh n&ecirc;n l&agrave;m c&aacute;i g&igrave;.<br />\r\n<br />\r\nNgay cả Đinh Mộ Ngưng c&aacute;i n&agrave;y vai nữ ch&iacute;nh, n&oacute;i chuyện đều l&agrave; bừa b&atilde;i d&aacute;ng vẻ.<br />\r\n<br />\r\nC&oacute; thể Di&ecirc;u Thi&ecirc;n Qu&acirc;n lại như cũ k&iacute;nh chức tr&aacute;ch nhiệm diễn xuất, đối Đinh Mộ Ngưng ph&aacute; lệ chiếu cố.<br />\r\n<br />\r\nHắn h&agrave;nh vi để cho những người n&agrave;y &aacute;nh mắt trở n&ecirc;n rất qu&aacute;i dị.<br />\r\n<br />\r\nC&oacute; thể Di&ecirc;u Thi&ecirc;n Qu&acirc;n lại b&igrave;nh thản ung dung.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nBởi v&igrave;, diễn kỹ đối với hắn m&agrave; n&oacute;i, hồn nhi&ecirc;n thi&ecirc;n th&agrave;nh.<br />\r\n<br />\r\nHắn v&ocirc; thời v&ocirc; khắc đều tại diễn xuất.<br />\r\n<br />\r\nĐinh Mộ Ngưng mang theo mọi người ở &Acirc;m Vương Phủ b&ecirc;n trong đi dạo một v&ograve;ng, dọc theo đường đi, ai cũng kh&ocirc;ng c&oacute; n&oacute;i lung tung.<br />\r\n<br />\r\n&quot;Được rồi, mọi người đ&atilde; đi dạo xong, c&oacute; thể đi về. Kh&ocirc;ng c&oacute; g&igrave; c&oacute; thể nh&igrave;n.&quot; Đinh Mộ Ngưng cười khan một tiếng n&oacute;i.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n giống như nh&igrave;n ngu si như thế, nh&igrave;n n&agrave;ng một c&aacute;i.<br />\r\n<br />\r\nNếu như đơn giản như vậy liền kết th&uacute;c, sợ rằng cũng kh&ocirc;ng cần chết.<br />\r\n<br />\r\nCực kỳ, đi lại một đường, tất cả mọi người với nhau đến gần, ai cũng kh&ocirc;ng d&aacute;m đơn đi.<br />\r\n<br />\r\nV&igrave; vậy, cho đến b&acirc;y giờ, đến l&uacute;c đ&oacute; kh&ocirc;ng c&oacute; người chết đi.<br />\r\n<br />\r\nNếu như l&agrave; như vậy, như vậy hung thủ v&ocirc; kế khả thi. Tr&agrave;ng n&agrave;y diễn xuất liền thật n&ecirc;n kết th&uacute;c rồi &agrave;?<br />\r\n<br />\r\nMọi người tiếp tục chẳng c&oacute; mục đ&iacute;ch, ở &Acirc;m Vương Phủ du đ&atilde;ng.<br />\r\n<br />\r\nRất nhanh mọi người đi tới một c&aacute;i trong ph&ograve;ng kh&aacute;ch, ở chỗ n&agrave;y để rất nhiều c&aacute;i ghế.<br />\r\n<br />\r\nNhững thứ n&agrave;y c&aacute;i ghế phi thường tinh xảo, tr&ecirc;n b&agrave;n c&ograve;n c&oacute; tr&agrave; cụ. Nhất l&agrave; ở giữa nhất ghế Th&aacute;i sư, ph&iacute;a tr&ecirc;n c&ograve;n bọc hồng sắc tơ lụa.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n thờ ơ liếc mắt nh&igrave;n, cũng chưa c&oacute; để ở trong l&ograve;ng.<br />\r\n<br />\r\nKhương Thi&ecirc;n Kh&ocirc;ng lại hết sức t&ograve; m&ograve;, hắn ngồi ở ở giữa nhất tr&ecirc;n ghế th&aacute;i sư. Lắc lắc th&acirc;n thể, tr&ecirc;n mặt nhiều một nụ cười ch&acirc;m biếm: &quot;Nơi n&agrave;y l&agrave; Vương gia cố định phương chứ ? B&acirc;y giờ n&oacute; thuộc về ta.&quot;<br />\r\n<br />\r\nNhững người khác gật đầu một c&aacute;i, &aacute;nh mắt nh&igrave;n hắn. T&acirc;m t&igrave;nh cũng b&igrave;nh phục kh&ocirc;ng &iacute;t.<br />\r\n<br />\r\nKhương Thi&ecirc;n Kh&ocirc;ng ngồi ở tr&ecirc;n ghế th&aacute;i sư, đắc &yacute; cười. Đang muốn n&oacute;i ch&uacute;t g&igrave;.<br />\r\n<br />\r\nĐột nhi&ecirc;n ghế Th&aacute;i sư đột nhi&ecirc;n vỡ vụn ra, Khương Thi&ecirc;n Kh&ocirc;ng kh&ocirc;ng ch&uacute; &yacute;, th&acirc;n thể t&eacute; ngửa về ph&iacute;a sau đi xuống.<br />\r\n<br />\r\nK&egrave;m theo h&eacute;t thảm một tiếng, mọi người vội v&agrave;ng x&iacute;t tới, sắc mặt lại vạn phần hoảng sợ.<br />\r\n<br />\r\nKhương Thi&ecirc;n Kh&ocirc;ng th&acirc;n thể về ph&iacute;a sau ng&atilde; tr&ecirc;n đất, sau lưng lại bị tr&ecirc;n đất đạt tới 5 mười ph&acirc;n nhọn đinh th&eacute;p xuy&ecirc;n qua.<br />\r\n<br />\r\nĐ&aacute;ng sợ m&aacute;u tươi tu&ocirc;n ra ngo&agrave;i, hắn ph&iacute; c&ocirc;ng đưa tay ra, cũng đ&atilde; kh&ocirc;ng kịp l&ecirc;n tiếng.<br />\r\n<br />\r\n&quot;Cứu... Ta.&quot;<br />\r\n<br />\r\nHắn c&ograve;n kh&ocirc;ng chờ n&oacute;i xong, th&acirc;n thể co r&uacute;t một ch&uacute;t, sau đ&oacute; kh&ocirc;ng nh&uacute;c nh&iacute;ch.</p>', '2021-12-01 14:54:42', 8, 1),
(299, 75, 'Hung Thủ Xuất Hiện', '9', '<p>&quot;Cơ quan.&quot; Nh&igrave;n một c&aacute;i, Di&ecirc;u Thi&ecirc;n Qu&acirc;n đ&atilde; hiểu c&aacute;i g&igrave;.<br />\r\n<br />\r\nVương phủ b&ecirc;n trong hiện đầy giết người cơ quan, trước mắt chỉ l&agrave; trong đ&oacute; một nơi.<br />\r\n<br />\r\nChỉ l&agrave; kh&ocirc;ng cẩn thận, Khương Thi&ecirc;n Kh&ocirc;ng k&iacute;ch ph&aacute;t m&agrave; th&ocirc;i.<br />\r\n<br />\r\n&quot;Ta kh&ocirc;ng chơi! Ta kh&ocirc;ng chơi!&quot;<br />\r\n<br />\r\nDư Điền thấy một m&agrave;n như vậy, đột nhi&ecirc;n đi&ecirc;n như thế, hướng xa xa chạy ra ngo&agrave;i.<br />\r\n<br />\r\n&quot;Chớ đi, ngươi đi, ngươi nhất định phải chết.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n vội v&agrave;ng muốn k&eacute;o ở hắn.<br />\r\n<br />\r\nC&oacute; thể bị k&iacute;ch th&iacute;ch Dư Điền chạy thật nhanh, trong nh&aacute;y mắt liền kh&ocirc;ng c&aacute;i b&oacute;ng rồi.<br />\r\n<br />\r\nLần n&agrave;y phiền to&aacute;i.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n thấy một m&agrave;n như vậy, thở d&agrave;i một c&aacute;i, &aacute;nh mắt nh&igrave;n về ph&iacute;a người trước mắt.<br />\r\n<br />\r\nCho đến b&acirc;y giờ, chỉ c&ograve;n lại năm người. Dư Điền, Lạc Ấu Nam, Đinh Mộ Ngưng, g&atilde; đeo k&iacute;nh tử. C&ograve;n c&oacute; ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\n&quot;Mọi người chớ tới gần Vương phủ kiến tr&uacute;c, nơi n&agrave;y khắp nơi đều hữu cơ quan. Ch&uacute;ng ta hay lại l&agrave; nghĩ biện ph&aacute;p từ nơi n&agrave;y đi ra ngo&agrave;i đi.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n nghi&ecirc;m t&uacute;c n&oacute;i.<br />\r\n<br />\r\nKinh hoảng thất thố mọi người, v&agrave;o l&uacute;c n&agrave;y gật đầu một c&aacute;i, ai cũng kh&ocirc;ng d&aacute;m đến gần kiến tr&uacute;c.<br />\r\n<br />\r\nBốn người cứ như vậy ở Vương phủ l&agrave;m b&ecirc;n trong h&agrave;nh tẩu đến, ở chỗ n&agrave;y địa h&igrave;nh rắc rối phức tạp, kh&ocirc;ng chỉ c&oacute; như thế, thường thường c&oacute; quỷ dị cơ quan.<br />\r\n<br />\r\nMọi người kh&ocirc;ng d&aacute;m khắp nơi đi loạn, c&oacute; thể lưu tại chỗ cũng chỉ c&oacute; thể chờ chết.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n đ&atilde; mơ hồ biết, tại sao tr&agrave;ng n&agrave;y diễn xuất kh&ocirc;ng c&aacute;ch n&agrave;o kết th&uacute;c nguy&ecirc;n nh&acirc;n.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng t&igrave;m được hung thủ, ch&uacute;ng ta d&ugrave; ai cũng kh&ocirc;ng c&aacute;ch n&agrave;o rời đi &Acirc;m Vương Phủ.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n mị đến con mắt, sắc mặt khổ sở n&oacute;i.<br />\r\n<br />\r\n&quot;Đ&uacute;ng vậy.&quot; G&atilde; đeo k&iacute;nh đẩy một c&aacute;i mắt k&iacute;nh, tr&ecirc;n mặt tr&agrave;n đầy kh&ocirc;ng thể tưởng tượng nổi thần sắc: &quot;Như vậy ngươi cảm thấy hung thủ sẽ l&agrave; ai?&quot;<br />\r\n<br />\r\n&quot;Hung thủ khả năng ở trong ch&uacute;ng ta.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n n&oacute;i như vậy đến.<br />\r\n<br />\r\n&quot;Ta cũng cho l&agrave; như thế.&quot; G&atilde; đeo k&iacute;nh khẽ mỉm cười, &aacute;nh mắt c&agrave;n qu&eacute;t qu&aacute; bốn ph&iacute;a, cười n&oacute;i: &quot;Như vậy trong ch&uacute;ng ta, ai đều c&oacute; thể sẽ l&agrave; hung thủ rồi hả?&quot;<br />\r\n<br />\r\n&quot;Cho n&ecirc;n, ch&uacute;ng ta đề ph&ograve;ng lẫn nhau đi.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n lạnh r&ecirc;n một tiếng n&oacute;i.<br />\r\n<br />\r\nĐang l&uacute;c bọn hắn trong l&uacute;c n&oacute;i chuyện, Lạc Ấu Nam lại tiến l&ecirc;n giảng h&ograve;a.<br />\r\n<br />\r\n&quot;Mọi người chớ ồn &agrave;o, nhanh l&ecirc;n một ch&uacute;t t&igrave;m tới hung thủ đi.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nLần n&agrave;y, bốn người quyết định đi đại m&ocirc;n nh&igrave;n một ch&uacute;t, muốn t&igrave;m đi ra ngo&agrave;i biện ph&aacute;p.<br />\r\n<br />\r\nNhưng ở Vương phủ lầu c&aacute;c b&ecirc;n tr&ecirc;n, một đ&ocirc;i lạnh gi&aacute; đồng tử, ch&iacute;nh d&ograve;m ng&oacute; bốn người tiểu đội. Trong &aacute;nh mắt tr&agrave;n đầy hung &aacute;c quang mang.<br />\r\n<br />\r\n&quot;Ta đ&atilde; n&oacute;i với ngươi, ch&uacute;ng ta muốn...&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n ch&iacute;nh n&oacute;i chuyện với Lạc Ấu Nam.<br />\r\n<br />\r\nMột nh&aacute;nh lạnh gi&aacute; mủi t&ecirc;n, đ&atilde; xuy&ecirc;n qua m&agrave; qua.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n ng&acirc;y dại, trợn mắt hốc mồm nh&igrave;n Lạc Ấu Nam.<br />\r\n<br />\r\nN&agrave;ng lồng ngực bị một mủi t&ecirc;n xuy&ecirc;n qua, n&agrave;ng c&uacute;i đầu xuống nh&igrave;n m&igrave;nh ngực. Chỉ cảm thấy đau nhức đ&aacute;nh tới.<br />\r\n<br />\r\nĐưa tay ra n&agrave;ng muốn hướng Di&ecirc;u Thi&ecirc;n Qu&acirc;n nhờ gi&uacute;p đỡ, chỉ l&agrave; c&aacute;nh tay mới đưa đến một nửa. Liền ph&iacute; c&ocirc;ng rủ xuống đi.<br />\r\n<br />\r\nNh&igrave;n trước mắt t&eacute; xuống đất thi thể, Di&ecirc;u Thi&ecirc;n Qu&acirc;n vội v&agrave;ng trốn ở v&aacute;ch tường b&ecirc;n cạnh sau b&ecirc;n trong. &Aacute;nh mắt kinh ho&agrave;ng h&ocirc;: &quot;Cẩn thận, c&oacute; người ở lầu c&aacute;c b&ecirc;n tr&ecirc;n bắn t&ecirc;n!&quot;<br />\r\n<br />\r\nNghe được lời n&oacute;i của hắn, Đinh Mộ Ngưng, g&atilde; đeo k&iacute;nh tử vội v&agrave;ng trốn.<br />\r\n<br />\r\nMủi t&ecirc;n lần nữa xuy&ecirc;n qua m&agrave; qua, rơi v&agrave;o Di&ecirc;u Thi&ecirc;n Qu&acirc;n b&ecirc;n cạnh.<br />\r\n<br />\r\nHắn trốn ở v&aacute;ch tường ph&iacute;a sau, hướng về ph&iacute;a lầu c&aacute;c h&ocirc;: &quot;Dư Điền! Quả nhi&ecirc;n l&agrave; ngươi. Ta sớm n&ecirc;n đo&aacute;n được!&quot;<br />\r\n<br />\r\nMột c&aacute;i hung &aacute;c thanh &acirc;m &acirc;m l&atilde;nh vang l&ecirc;n: &quot;B&acirc;y giờ ngươi đo&aacute;n được, đ&atilde; qu&aacute; muộn.&quot;<br />\r\n<br />\r\nTrong l&uacute;c n&oacute;i chuyện, lạnh gi&aacute; mủi t&ecirc;n, rơi v&agrave;o Di&ecirc;u Thi&ecirc;n Qu&acirc;n b&ecirc;n cạnh chưa đủ ba m&eacute;t nơi.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n một b&ecirc;n tr&aacute;nh n&eacute;, vừa lật lăn lộn.<br />\r\n<br />\r\nC&oacute; thể sắc b&eacute;n mủi t&ecirc;n, nhiều lần thiếu ch&uacute;t nữa đ&aacute;nh tr&uacute;ng hắn.<br />\r\n<br />\r\nHắn vị tr&iacute; mới l&agrave; Nội Viện, nơi n&agrave;y thập phần rộng r&atilde;i. Dư Điền lại c&agrave;ng ng&agrave;y c&agrave;ng gần.<br />\r\n<br />\r\nNếu như kh&ocirc;ng nghĩ biện ph&aacute;p, ch&iacute;nh m&igrave;nh chắc chắn phải chết.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n nhanh tr&iacute;, &aacute;nh mắt nh&igrave;n về ph&iacute;a kinh hoảng vạn phần Đinh Mộ Ngưng, nhếch miệng l&ecirc;n một tia cười lạnh, hắn cười n&oacute;i: &quot;N&ecirc;n ta ra s&acirc;n biểu diễn!&quot;<br />\r\n<br />\r\nDư Điền th&acirc;n thể đ&atilde; đi tới, trong tay hắn bưng một c&aacute;i cổ đại Thủ Nỗ. Sau lưng c&ograve;n đeo một c&aacute;i bao đựng t&ecirc;n.<br />\r\n<br />\r\nHắn cẩn thận cảnh gi&aacute;c nh&igrave;n bốn ph&iacute;a, &aacute;nh mắt tr&agrave;n đầy hung &aacute;c.<br />\r\n<br />\r\nChờ hắn đi tới Nội Viện thời điểm, l&agrave;m người ta kinh ngạc l&agrave;, Di&ecirc;u Thi&ecirc;n Qu&acirc;n cũng kh&ocirc;ng c&oacute; chạy trốn. B&agrave;n tay hắn nắm một c&acirc;y chủy thủ, đặt ở cổ Đinh Mộ Ngưng b&ecirc;n tr&ecirc;n. Hắn bắt giữ Đinh Mộ Ngưng, th&acirc;n thể ẩn n&uacute;p ở sau lưng n&agrave;ng.<br />\r\n<br />\r\n&quot;Ngươi l&agrave;m g&igrave;?&quot; Dư Điền thấy một m&agrave;n như vậy, sắc mặt trở n&ecirc;n v&ocirc; c&ugrave;ng lạnh gi&aacute;.<br />\r\n<br />\r\n&quot;L&agrave;m g&igrave;? Dĩ nhi&ecirc;n l&agrave; muốn tiếp tục sống.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n nanh cười một tiếng, mặt mũi tr&agrave;n đầy lạnh gi&aacute;: &quot;Ta bi&ecirc;́t ngươi nghĩ đem ch&uacute;ng ta giết hết, đ&aacute;ng tiếc ta sẽ kh&ocirc;ng để cho ngươi được s&iacute;nh!&quot;<br />\r\n<br />\r\n&quot;Ngươi t&ecirc;n hỗn đản n&agrave;y.&quot; Dư Điền giơ bàn tay l&ecirc;n Thủ Nỗ, chỉ cần hắn theo như động một c&aacute;i ng&oacute;n tay.<br />\r\n<br />\r\nL&agrave; c&oacute; thể đem Đinh Mộ Ngưng c&ugrave;ng Di&ecirc;u Thi&ecirc;n Qu&acirc;n to&agrave;n bộ bắn chết.<br />\r\n<br />\r\nNhưng l&agrave; hắn cũng kh&ocirc;ng c&oacute; động thủ.<br />\r\n<br />\r\n&quot;Đồng Ngọc Long, ngươi bắt giữ ta c&oacute; &iacute;ch lợi g&igrave;?&quot; Đinh Mộ Ngưng nổi n&oacute;ng nh&igrave;n Di&ecirc;u Thi&ecirc;n Qu&acirc;n, n&agrave;ng thế n&agrave;o cũng sẽ kh&ocirc;ng nghĩ tới, Di&ecirc;u Thi&ecirc;n Qu&acirc;n như thế n&agrave;y m&agrave; h&egrave;n hạ v&ocirc; sỉ. Đem th&acirc;n thể của m&igrave;nh l&agrave;m th&agrave;nh bia đỡ đạn.<br />\r\n<br />\r\n&quot;Đương nhi&ecirc;n hữu dụng, thậm ch&iacute; n&oacute;i, có tác dụng lớn.&quot;<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n nanh cười một tiếng, khu&ocirc;n mặt anh tuấn đ&atilde; kinh biến đến mức &acirc;m trầm v&ocirc; c&ugrave;ng.<br />\r\n<br />\r\n&quot;Ta từ mới vừa gia nhập &Acirc;m Vương Phủ, liền quan s&aacute;t c&aacute;c ngươi mỗi một người.&quot;<br />\r\n<br />\r\n&quot;Trong đ&oacute; để cho ta ho&agrave;i nghi l&agrave; Dư Điền, c&ograve;n c&oacute; c&aacute;i kia đeo mắt kiếng.&quot;<br />\r\n<br />\r\nHắn vừa n&oacute;i, vừa đem nh&igrave;n sang.<br />\r\n<br />\r\nCon mắt nam đứng ở một b&ecirc;n, hai tay thả l&ecirc;n đỉnh đầu, sắc mặt hết sức kh&oacute; xử.<br />\r\n<br />\r\n&quot;Ta từ tiến v&agrave;o &Acirc;m Vương Phủ, quan s&aacute;t trong n&agrave;y hết thảy. Liền mơ hồ đo&aacute;n được.&quot;<br />\r\n<br />\r\n&quot;Ở chỗ n&agrave;y c&oacute; rất nhiều cơ quan h&igrave;nh cụ, ch&iacute;nh l&agrave; v&igrave; giết sạch ch&uacute;ng ta m&agrave; tồn tại.&quot;<br />\r\n<br />\r\n&quot;Cho n&ecirc;n, ta một mực nghĩ đủ phương c&aacute;ch, kh&ocirc;ng để cho m&igrave;nh bị giết.&quot;<br />\r\n<br />\r\n&quot;Cực kỳ k&egrave;m theo người chết c&agrave;ng ng&agrave;y c&agrave;ng nhiều, ta ngược lại biết.&quot;<br />\r\n<br />\r\n&quot;C&oacute; người đ&atilde; sớm tới gọi hồn Vương phủ, đem nơi n&agrave;y khắp nơi phủ đầy giết người cơ quan.&quot;<br />\r\n<br />\r\n&quot;Khi ch&uacute;ng ta l&uacute;c đi v&agrave;o sau khi, tử vong cũng đ&atilde; l&agrave; quyết định.&quot;<br />\r\n<br />\r\nN&oacute;i tới chỗ n&agrave;y, Di&ecirc;u Thi&ecirc;n Qu&acirc;n tr&ecirc;n mặt, to&aacute;t ra vẻ dữ tợn, tuyệt đối kh&ocirc;ng thể so với Dư Điền thiếu.<br />\r\n<br />\r\nB&agrave;n tay hắn cầm chủy thủ, &aacute;nh mắt hưng phấn nh&igrave;n Dư Điền: &quot;Kh&ocirc;ng khỏi kh&ocirc;ng thừa nhận, ngươi l&agrave; một c&aacute;i đ&aacute;ng sợ s&aacute;t thủ. Nếu như ngươi dụng hết to&agrave;n lực, mọi người ch&uacute;ng ta kh&oacute; tho&aacute;t khỏi c&aacute;i chết.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Nhưng ta rất nhanh ph&aacute;t hiện, sự t&igrave;nh cũng kh&ocirc;ng phải như vậy &quot;<br />\r\n<br />\r\n&quot;Ngươi muốn giết nh&acirc;n, lại kh&ocirc;ng phải muốn giết người sở hữu.&quot;<br />\r\n<br />\r\n&Aacute;nh mắt cuả Dư Điền nh&igrave;n chằm chằm Di&ecirc;u Thi&ecirc;n Qu&acirc;n, tay nắm giữ rồi Thủ Nỗ, t&ugrave;y thời muốn nhấn Khai Quan.<br />\r\n<br />\r\nNhưng hắn cũng kh&ocirc;ng c&oacute;, m&agrave; l&agrave; lạnh l&ugrave;ng hỏi &quot;Ngươi l&agrave;m sao thấy được?&quot;<br />\r\n<br />\r\n&quot;Mặc d&ugrave; ngươi biểu hiện thờ ơ kh&ocirc;ng động l&ograve;ng, c&oacute; thể từ đầu tới cuối, &aacute;nh mắt cuả ngươi một mực ở tr&ecirc;n người Đinh Mộ Ngưng. Cho tới b&acirc;y giờ kh&ocirc;ng c&oacute; dời đi qu&aacute;.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng chỉ c&oacute; như thế, mới vừa rồi ở đại sảnh thời điểm. Đinh Mộ Ngưng thứ nhất muốn ngồi l&ecirc;n c&aacute;i ghế kia.&quot;<br />\r\n<br />\r\n&quot;C&oacute; thể ngươi lại d&ugrave;ng th&acirc;n thể chặn lại n&agrave;ng, để cho n&agrave;ng cũng kh&ocirc;ng c&oacute; đi.&quot;<br />\r\n<br />\r\n&quot;L&uacute;c ấy kh&ocirc;ng c&oacute; người ch&uacute; &yacute; tới ngươi động t&aacute;c, sợ rằng liền Đinh Mộ Ngưng sợ rằng đều kh&ocirc;ng &yacute; thức được. Nhưng tất cả những thứ n&agrave;y đều bị ta thu hết v&agrave;o mắt.&quot;<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n vẻ mặt tươi cười, &aacute;nh mắt lại tr&agrave;n đầy &acirc;m trầm: &quot;Khi đ&oacute; ta cũng biết, ngươi y&ecirc;u Đinh Mộ Ngưng. Ngươi th&iacute;ch n&agrave;ng.&quot;<br />\r\n<br />\r\n&quot;Ngươi thiết tr&iacute; nhiều như vậy cơ quan, đủ giết chết ch&uacute;ng ta hơn trăm lần. Nhưng ch&uacute;ng ta nhưng vẫn b&igrave;nh an v&ocirc; sự.&quot;<br />\r\n<br />\r\n&quot;Đ&oacute; l&agrave; bởi v&igrave;, Đinh Mộ Ngưng ở b&ecirc;n người ch&uacute;ng ta, ngươi bất tiện động thủ m&agrave; th&ocirc;i.&quot;<br />\r\n<br />\r\n&quot;Ta n&oacute;i đ&uacute;ng kh&ocirc;ng?&quot;<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n n&oacute;i tới chỗ n&agrave;y, đột nhi&ecirc;n tiến tới, h&ocirc;n Đinh Mộ Ngưng một c&aacute;i.<br />\r\n<br />\r\n&quot;Ngươi t&ecirc;n hỗn đản n&agrave;y!&quot; Dư Điền muốn nứt kh&oacute;e mắt, &aacute;nh mắt tr&agrave;n đầy đi&ecirc;n cuồng. C&aacute;nh tay đều run rẩy đến.<br />\r\n<br />\r\n&quot;Kh&aacute;c k&iacute;ch th&iacute;ch hắn.&quot; Đinh Mộ Ngưng kinh ho&agrave;ng h&ocirc;.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n cười lạnh một tiếng, &aacute;nh mắt nh&igrave;n Dư Điền h&ocirc;: &quot;Bỏ vũ kh&iacute; trong tay xuống, nếu kh&ocirc;ng ta liền giết n&agrave;ng.&quot;<br />\r\n<br />\r\n&quot;Ngươi cho rằng là, ngươi c&oacute; thể uy hiếp ta sao?&quot; &Aacute;nh mắt cuả Dư Điền tr&agrave;n đầy khinh thường.<br />\r\n<br />\r\nNhưng ở người sở hữu kinh ngạc &aacute;nh mắt ch&iacute;nh giữa, Di&ecirc;u Thi&ecirc;n Qu&acirc;n kh&ocirc;ng ch&uacute;t do dự lau tới.<br />\r\n<br />\r\nTrong b&agrave;n tay chủy thủ, vạch qua cổ Đinh Mộ Ngưng, để cho n&agrave;ng k&ecirc;u thảm một tiếng. Cổ n&agrave;ng nhiều hơn một c&aacute;i lỗ. Mặc d&ugrave; kh&ocirc;ng lớn. Lại để cho n&agrave;ng vạn phần kinh ho&agrave;ng c&ugrave;ng thống khổ.</p>', '2021-12-01 14:55:10', 6, 1),
(300, 75, 'Xuất Sắc Diễn Dịch', '10', '<p>&quot;Kh&ocirc;ng!&quot; Dư Điền vội v&agrave;ng mu&ocirc;́n x&ocirc;ng tới, lại bị Di&ecirc;u Thi&ecirc;n Qu&acirc;n d&ugrave;ng &aacute;nh mắt ngăn cản.<br />\r\n<br />\r\n&quot;Ta d&ugrave; sao cũng sống đủ rồi, ngươi kh&ocirc;ng bu&ocirc;ng tha ta, ta h&atilde;y c&ugrave;ng n&agrave;ng đồng quy vu tận!&quot; &Aacute;nh mắt của Di&ecirc;u Thi&ecirc;n Qu&acirc;n đi&ecirc;n cuồng n&oacute;i.<br />\r\n<br />\r\nGiờ ph&uacute;t n&agrave;y hắn, cho thấy, l&agrave; một c&aacute;i c&ugrave;ng đồ mạt lộ thứ liều mạng.<br />\r\n<br />\r\nTrong &aacute;nh mắt t&agrave;n &aacute;c c&ugrave;ng ki&ecirc;n quyết, để cho Dư Điền đều ng&acirc;y dại.<br />\r\n<br />\r\nR&otilde; r&agrave;ng ta mới l&agrave; biến th&aacute;i s&aacute;t nh&acirc;n cuồng c&oacute; được hay kh&ocirc;ng?<br />\r\n<br />\r\nTại sao ngươi biểu hiện so với ta c&ograve;n đi&ecirc;n cuồng?<br />\r\n<br />\r\nTrong n&agrave;y nh&acirc;n, kh&ocirc;ng đều l&agrave; ta giết chết sao? C&oacute; thể ngươi biểu hiện đi&ecirc;n cuồng như vậy, rốt cuộc l&agrave; muốn l&agrave;m g&igrave;?<br />\r\n<br />\r\nTrong l&uacute;c nhất thời Dư Điền lại c&oacute; loại ho&agrave;i nghi nh&acirc;n sinh cảm gi&aacute;c.<br />\r\n<br />\r\n&quot;Nhanh l&ecirc;n một ch&uacute;t đem Thủ Nỗ c&ugrave;ng bao đựng t&ecirc;n n&eacute;m tới!&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n sắc mặt t&agrave;n &aacute;c h&ocirc;.<br />\r\n<br />\r\n&quot;Được.&quot; Dư Điền cuống qu&iacute;t n&eacute;m qua, nhưng hắn phi thường giảo hoạt, vứt bỏ Thủ Nỗ địa phương, c&aacute;ch hắn dưới ch&acirc;n chỉ c&oacute; một bước ngắn.<br />\r\n<br />\r\n&quot;Thiếu cho ta đ&ugrave;a bỡn bịp bợm, đem Thủ Nỗ c&ugrave;ng bao đựng t&ecirc;n n&eacute;m cho hắn.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n d&ugrave;ng mắt Thần Uy buộc, để cho Dư Điền đem Thủ Nỗ nh&eacute;t v&agrave;o g&atilde; đeo k&iacute;nh b&ecirc;n người.<br />\r\n<br />\r\nG&atilde; đeo k&iacute;nh run lập cập cầm l&ecirc;n Thủ Nỗ, nh&igrave;n b&ecirc;n ch&acirc;n bao đựng t&ecirc;n, &aacute;nh mắt l&uacute;ng t&uacute;ng nh&igrave;n trước mắt cục diện. Cũng kh&ocirc;ng biết như thế n&agrave;o cho phải.<br />\r\n<br />\r\n&quot;B&acirc;y giờ c&oacute; thể đem n&agrave;ng cho ta chứ ?&quot; &Aacute;nh mắt cuả Dư Điền m&aacute;u đỏ n&oacute;i, hắn nh&igrave;n cổ Đinh Mộ Ngưng b&ecirc;n tr&ecirc;n vết thương c&ograve;n đang chảy m&aacute;u, trong l&ograve;ng liền d&acirc;ng l&ecirc;n một tia phẫn nộ.<br />\r\n<br />\r\n&quot;Dĩ nhi&ecirc;n kh&ocirc;ng th&agrave;nh vấn đề.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n nanh cười một tiếng, sau đ&oacute; chợt liền đẩy ra Đinh Mộ Ngưng.<br />\r\n<br />\r\nĐinh Mộ Ngưng h&eacute;t l&ecirc;n một tiếng, th&acirc;n thể về ph&iacute;a trước nh&agrave;o tới.<br />\r\n<br />\r\nDư Điền vội v&agrave;ng nh&agrave;o qua, &ocirc;m lấy Đinh Mộ Ngưng.<br />\r\n<br />\r\nM&agrave; l&uacute;c n&agrave;y Di&ecirc;u Thi&ecirc;n Qu&acirc;n nanh cười một tiếng, vọt tới g&atilde; đeo k&iacute;nh trước mặt. Một cước đạp ra hắn. Gi&agrave;nh lấy trong tay hắn Thủ Nỗ.<br />\r\n<br />\r\nHắn cầm l&ecirc;n Thủ Nỗ, ph&iacute;a tr&ecirc;n vừa vặn c&oacute; một quả sắc b&eacute;n mủi t&ecirc;n.<br />\r\n<br />\r\n&quot;Đi chết đi!&quot;<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n ph&aacute;t ra đi&ecirc;n cuồng th&eacute;t ch&oacute;i tai, giơ l&ecirc;n Thủ Nỗ cũng kh&ocirc;ng c&oacute; nhắm Dư Điền, ngược lại nhắm ngay Đinh Mộ Ngưng.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng!&quot; &Ocirc;m lấy Đinh Mộ Ngưng Dư Điền, thấy một m&agrave;n như vậy, chợt đẩy ra Đinh Mộ Ngưng.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nSau một khắc, sắc b&eacute;n mủi t&ecirc;n, đ&atilde; xuy&ecirc;n qua lồng ngực của hắn!<br />\r\n<br />\r\nNặng nề lực lượng xuy&ecirc;n qua th&acirc;n thể của hắn, để cho th&acirc;n thể của hắn t&eacute; xuống.<br />\r\n<br />\r\nM&agrave; Di&ecirc;u Thi&ecirc;n Qu&acirc;n nanh cười một tiếng, giơ l&ecirc;n Thủ Nỗ, lại l&ecirc;n một mủi t&ecirc;n, lần nữa nhắm ngay Đinh Mộ Ngưng.<br />\r\n<br />\r\nDư Điền chợt gi&atilde;y giụa đứng l&ecirc;n, lại chắn trước người Đinh Mộ Ngưng.<br />\r\n<br />\r\nMột mủi t&ecirc;n n&agrave;y, lại xuy&ecirc;n qua v&agrave;o th&acirc;n thể của hắn.<br />\r\n<br />\r\nCực kỳ Di&ecirc;u Thi&ecirc;n Qu&acirc;n phảng phất đ&aacute;nh mất l&yacute; tr&iacute;, b&agrave;n tay cầm l&ecirc;n Li&ecirc;n Nỗ, b&ecirc;n tr&ecirc;n rồi lần lượt mủi t&ecirc;n.<br />\r\n<br />\r\nSắc b&eacute;n mủi t&ecirc;n, kh&ocirc;ng ngừng rơi v&agrave;o tr&ecirc;n người Dư Điền.<br />\r\n<br />\r\nT&igrave;nh cảnh x&uacute;c động l&ograve;ng người.<br />\r\n<br />\r\nXa xa g&atilde; đeo k&iacute;nh t&ecirc; liệt ngồi dưới đất, trợn tr&ograve;n mắt.<br />\r\n<br />\r\nRốt cuộc ai l&agrave; s&aacute;t nh&acirc;n cuồng? B&acirc;y giờ Di&ecirc;u Thi&ecirc;n Qu&acirc;n d&aacute;ng vẻ, thật sự l&agrave; qu&aacute; đ&aacute;ng sợ.<br />\r\n<br />\r\n&quot;Ta cũng kh&ocirc;ng tin kh&ocirc;ng giết được ngươi.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n t&agrave;n bạo nh&igrave;n chằm chằm Đinh Mộ Ngưng, trong tay tiếp tục giơ l&ecirc;n Thủ Nỗ.<br />\r\n<br />\r\nĐinh Mộ Ngưng h&eacute;t l&ecirc;n một tiếng, vội v&agrave;ng muốn muốn chạy trốn.<br />\r\n<br />\r\nMủi t&ecirc;n đ&atilde; hướng n&agrave;ng xuy&ecirc;n qua m&agrave; qua, nhưng lại bị Dư Điền ngăn trở.<br />\r\n<br />\r\n&quot;Lẽ n&agrave;o lại như vậy!&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n lửa giận vạn trượng, cầm l&ecirc;n Thủ Nỗ, li&ecirc;n tục bắn hơn hai mươi ph&aacute;t tiễn t&ecirc;n.<br />\r\n<br />\r\nDư Điền cuối c&ugrave;ng vẫn ng&atilde; xuống, th&acirc;n thể của hắn t&eacute; xuống đất, ph&iacute;a tr&ecirc;n cắm đầy mũi t&ecirc;n, sắp biến th&agrave;nh rồi nh&iacute;m rồi.<br />\r\n<br />\r\nCả người kh&ocirc;ng cam l&ograve;ng ng&atilde; xuống, đ&atilde; mất đi sinh tức.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n hay lại l&agrave; đuổi kịp Đinh Mộ Ngưng.<br />\r\n<br />\r\nĐinh Mộ Ngưng th&eacute;t ch&oacute;i tai dựa v&agrave;o ở tr&ecirc;n v&aacute;ch tường, nh&igrave;n s&aacute;ng lo&aacute;ng Thủ Nỗ, hai tay của hắn giơ l&ecirc;n thật cao, mặt mũi kinh ho&agrave;ng h&ocirc;: &quot;Đừng giết ta.&quot;<br />\r\n<br />\r\n&quot;Đi chết đi.&quot;<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n cười gằn n&oacute;i xong, đ&atilde; nhấn Thủ Nỗ.<br />\r\n<br />\r\nĐinh Mộ Ngưng nhắm lại con mắt, chờ đợi ch&iacute;nh m&igrave;nh ng&agrave;y giổ.<br />\r\n<br />\r\nNhưng đợi một hồi l&acirc;u, n&agrave;ng vẫn kh&ocirc;ng c&oacute; chờ đến. Kinh ngạc trợn mở con mắt. N&agrave;ng lại thấy Di&ecirc;u Thi&ecirc;n Qu&acirc;n ngồi dưới đất, mặt đầy lười biếng nh&igrave;n n&agrave;ng, nh&uacute;n nh&uacute;n vai n&oacute;i: &quot;C&oacute; sợ hay kh&ocirc;ng? C&oacute; ngo&agrave;i &yacute; muốn hay kh&ocirc;ng?&quot;<br />\r\n<br />\r\nĐinh Mộ Ngưng n&agrave;y mới tỉnh ngộ lại, kh&oacute;c t&ecirc; liệt t&eacute; xuống đất: &quot;Ngươi l&agrave;m g&igrave; vậy nha, ngươi l&agrave;m ta sợ muốn chết!&quot;<br />\r\n<br />\r\n&quot;Ta kh&ocirc;ng l&agrave;m như vậy, tử c&oacute; thể ch&iacute;nh l&agrave; ch&uacute;ng ta rồi.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n cười tủm tỉm n&oacute;i, tr&ecirc;n người cũng c&oacute; một loại như tr&uacute;t được g&aacute;nh nặng cảm gi&aacute;c.<br />\r\n<br />\r\n&quot;Ngươi l&agrave;m g&igrave; vậy phải d&ugrave;ng Thủ Nỗ bắn ta?&quot; Đinh Mộ Ngưng kinh ngạc hỏi.<br />\r\n<br />\r\nAi biết Di&ecirc;u Thi&ecirc;n Qu&acirc;n m&eacute;o một ch&uacute;t đầu, chỉ chỉ xa xa Dư Điền thi thể: &quot;Bị ta bắn hơn hai mươi mũi t&ecirc;n mới tử qu&aacute;i vật, nếu như ta mục ti&ecirc;u ngay từ đầu ch&iacute;nh l&agrave; hắn. Hắn khả năng đ&atilde; sớm giết ta.&quot;<br />\r\n<br />\r\n&quot;Hơn nữa ta lại kh&ocirc;ng phải chuy&ecirc;n nghiệp Nỗ Thủ, hắn c&aacute;ch ta kh&aacute; xa. Ta kh&ocirc;ng nắm chắc th&agrave;nh c&ocirc;ng.&quot;<br />\r\n<br />\r\n&quot;Cho n&ecirc;n ta đem mục ti&ecirc;u đặt ở tr&ecirc;n người của ngươi, hắn nhất định sẽ cứu ngươi.&quot;<br />\r\n<br />\r\nĐinh Mộ Ngưng gật đầu một c&aacute;i, như c&oacute; điều suy nghĩ, nhưng n&agrave;ng lập tức hỏi &quot;Nếu như hắn kh&ocirc;ng cứu ta, ta đ&acirc;y kh&ocirc;ng phải bị ngươi giết?&quot;<br />\r\n<br />\r\n&quot;Hắn nhất định sẽ cứu ngươi, hắn v&igrave; chiếm đoạt ngươi, giết nhiều người như vậy. Chế tạo nhiều như vậy cơ quan. Đối với ngươi tất nhi&ecirc;n c&oacute; cực kỳ biến th&aacute;i muốn chiếm l&agrave;m của ri&ecirc;ng.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n dựa v&agrave;o ở tr&ecirc;n v&aacute;ch tường, vẻ mặt đắc &yacute; n&oacute;i.<br />\r\n<br />\r\n&quot;Đ&uacute;ng vậy.&quot; Đinh Mộ Ngưng gật đầu một c&aacute;i, mặt đầy hưng phấn n&oacute;i: &quot;Ngươi thật thật lợi hại, ta sẽ kh&ocirc;ng suy nghĩ nhiều như vậy.&quot;<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n kh&ocirc;ng n&oacute;i g&igrave;, nội t&acirc;m cũng hiểu được, tự m&igrave;nh n&oacute;i l&aacute;o.<br />\r\n<br />\r\n&Yacute; tưởng của hắn đ&uacute;ng l&agrave; như vậy, nhưng nếu như hắn thất thủ, như vậy Đinh Mộ Ngưng nhất định sẽ chết.<br />\r\n<br />\r\nNhưng l&uacute;c đ&oacute; hắn cũng kh&ocirc;ng c&oacute; nghĩ qu&aacute; nhiều, ngược lại nếu như hắn kh&ocirc;ng động thủ, như vậy ngoại trừ Đinh Mộ Ngưng, những người khác tất nhi&ecirc;n sẽ chết ở Dư Điền trong tay.<br />\r\n<br />\r\nDư Điền qu&aacute; đ&aacute;ng sợ, nếu như kh&ocirc;ng phải l&agrave; v&igrave; bảo vệ Đinh Mộ Ngưng, m&igrave;nh tuyệt đối kh&ocirc;ng thể n&agrave;o l&agrave; đối thủ.<br />\r\n<br />\r\nN&oacute;i cho c&ugrave;ng, hắn cũng kh&ocirc;ng phải chết ở Di&ecirc;u Thi&ecirc;n Qu&acirc;n trong tay, m&agrave; l&agrave; chết ở ch&uacute;t t&igrave;nh cảm n&agrave;y ph&iacute;a tr&ecirc;n.<br />\r\n<br />\r\nĐang l&uacute;c n&agrave;y, điện thoại di động reo đứng l&ecirc;n. Di&ecirc;u Thi&ecirc;n Qu&acirc;n mở điện thoại di động l&ecirc;n.<br />\r\n<br />\r\nCh&iacute;nh l&agrave; Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng ph&aacute;t h&agrave;nh.<br />\r\n<br />\r\n&quot;Ngươi diễn dịch, để cho người ta mở rộng tầm mắt.&quot;<br />\r\n<br />\r\n&quot;Đối ph&oacute; s&aacute;t nh&acirc;n cuồng biện ph&aacute;p tốt nhất, ch&iacute;nh l&agrave; biến th&agrave;nh so với hắn c&agrave;ng hung &aacute;c s&aacute;t nh&acirc;n cuồng.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Ch&uacute;c mừng ngươi ho&agrave;n th&agrave;nh cảnh tượng: &quot;&Acirc;m Vương Phủ &quot;<br />\r\n<br />\r\n&quot;Ngươi diễn dịch chấm điểm: Tứ Tinh. (m&atilde;n phần ngũ tinh ) &quot;<br />\r\n<br />\r\n&quot;Ngươi tiền đ&oacute;ng phim: Một ng&agrave;n &Aacute;c Linh Tệ.&quot;<br />\r\n<br />\r\nNh&igrave;n đến đ&acirc;y, Di&ecirc;u Thi&ecirc;n Qu&acirc;n thở ph&agrave;o nhẹ nh&otilde;m, bất kể như thế n&agrave;o. Cửa ải n&agrave;y cuối c&ugrave;ng đi qua.<br />\r\n<br />\r\nĐinh Mộ Ngưng ngược lại l&agrave; nghi ngờ kh&ocirc;ng hiểu, &aacute;nh mắt kinh ho&agrave;ng nh&igrave;n m&igrave;nh điện thoại di động.<br />\r\n<br />\r\nG&atilde; đeo k&iacute;nh đi tới, đưa tay ra: &quot;Thật l&agrave; đa tạ ngươi, muốn kh&ocirc;ng phải ngươi, ch&uacute;ng ta đều phải chết.&quot;<br />\r\n<br />\r\n&quot;Ngươi thu được mấy sao chấm điểm?&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n đột nhi&ecirc;n hiếu kỳ hỏi.<br />\r\n<br />\r\n&quot;Nhất tinh.&quot; G&atilde; đeo k&iacute;nh nh&uacute;n nh&uacute;n vai n&oacute;i.<br />\r\n<br />\r\n&quot;Được rồi.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n đứng l&ecirc;n, chụp đ&aacute;nh một c&aacute;i tr&ecirc;n người m&igrave;nh bụi đất.<br />\r\n<br />\r\nBất kể như thế n&agrave;o, sự t&igrave;nh cuối c&ugrave;ng kết th&uacute;c.<br />\r\n<br />\r\n&quot;Ch&uacute;ng ta c&oacute; thể đi về.&quot;<br />\r\n<br />\r\nN&oacute;i xong Di&ecirc;u Thi&ecirc;n Qu&acirc;n muốn đi ra.<br />\r\n<br />\r\nL&uacute;c n&agrave;y Đinh Mộ Ngưng gọi hắn lại, &aacute;nh mắt kinh hoảng hỏi &quot;Rốt cuộc chuyện n&agrave;y như thế n&agrave;o?&quot;<br />\r\n<br />\r\n&quot;Ngươi rất nhanh th&igrave; biết.&quot; Di&ecirc;u Thi&ecirc;n Qu&acirc;n căn bản kh&ocirc;ng hứng th&uacute; giải th&iacute;ch, xoay người rời đi.<br />\r\n<br />\r\nChờ hắn &Acirc;m Vương Phủ cửa thời điểm, đưa tay ra đẩy một c&aacute;i.<br />\r\n<br />\r\nVốn l&agrave; mở cửa kh&ocirc;ng ra, b&acirc;y giờ t&ugrave;y tiện đ&atilde; bị mở ra.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n rời đi &Acirc;m Vương Phủ thời điểm, nghi&ecirc;ng đầu. Nh&igrave;n về ph&iacute;a kh&iacute; ph&aacute;i Vương phủ. Chu cửa lớn m&agrave;u đỏ.<br />\r\n<br />\r\n&Acirc;m Vương Phủ Truyền Thuyết vẫn c&ograve;n tiếp tục, hấp dẫn lần lượt quấn qu&yacute;t si m&ecirc; linh hồn.<br />\r\n<br />\r\nHắn nghi&ecirc;ng đầu, đem tử vong n&eacute;m ở sau lưng.</p>', '2021-12-01 14:55:33', 17, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(301, 75, 'Chân Tướng Sau Chuyện Này', '10', '<p>Trở lại ph&ograve;ng trọ sau, Di&ecirc;u Thi&ecirc;n Qu&acirc;n lập tức đ&aacute;nh mở điều tra m&aacute;y t&iacute;nh l&ecirc;n &Acirc;m Vương Phủ.<br />\r\n<br />\r\nKết quả điều tra, để cho hắn thất kinh.<br />\r\n<br />\r\nNguy&ecirc;n lai năm ngo&aacute;i &Acirc;m Vương Phủ xảy ra đồng thời đ&aacute;ng sợ &aacute;n mạng, vụ &aacute;n n&agrave;y cố sự phi thường ly kỳ.<br />\r\n<br />\r\nNghe n&oacute;i &Acirc;m Vương Phủ Kiến Th&agrave;nh thời điểm, lại c&oacute; nh&acirc;n tới nơi n&agrave;y du ngoạn. M&agrave; ở trong n&agrave;y, liền c&oacute; một người sinh vi&ecirc;n đại học đo&agrave;n đội.<br />\r\n<br />\r\nN&agrave;y người sinh vi&ecirc;n đại học đo&agrave;n đội, cộng lại tổng cộng c&oacute; bảy người.<br />\r\n<br />\r\nTrong đ&oacute; cốt l&otilde;i nhất nh&acirc;n, ch&iacute;nh l&agrave; Đinh Mộ Ngưng.<br />\r\n<br />\r\nN&agrave;ng l&agrave; Đại Học Gi&aacute;o hoa, v&oacute;c người cao gầy, trưởng đẹp đẽ. Kh&ocirc;ng biết hấp dẫn bao nhi&ecirc;u người.<br />\r\n<br />\r\nN&agrave;ng r&otilde; r&agrave;ng c&oacute; bạn trai Dịch Thừa rồi, b&ecirc;n người nhưng vẫn v&acirc;y quanh rất nhiều liếm cẩu.<br />\r\n<br />\r\nNhững thứ n&agrave;y liếm cẩu đối với n&agrave;ng v&ocirc; c&ugrave;ng h&egrave;n mọn, nguyện &yacute; v&igrave; n&agrave;ng bỏ ra hết thảy.<br />\r\n<br />\r\nV&igrave; vậy Đinh Mộ Ngưng một b&ecirc;n hưởng thụ bạn trai Dịch Thừa được, một b&ecirc;n cũng kh&ocirc;ng c&oacute; bu&ocirc;ng tha b&ecirc;n người liếm cẩu.<br />\r\n<br />\r\nN&agrave;ng rất c&oacute; thủ đoạn, chợt xa chợt gần, để cho rất nhiều liếm cẩu, đều l&agrave; n&agrave;ng hồn khi&ecirc;n mộng nhiễu. Lại cũng kh&ocirc;ng hề rời đi n&agrave;ng.<br />\r\n<br />\r\nNhững thứ n&agrave;y liếm cẩu, từng c&aacute;i bị n&agrave;ng đ&ugrave;a bỡn với vỗ tay ch&iacute;nh giữa cũng kh&ocirc;ng tự biết.<br />\r\n<br />\r\nBỏ ra kim tiền, thời gian, tinh lực.<br />\r\n<br />\r\nC&oacute; thể quay đầu lại, lại bị đ&ugrave;a bỡn xoay quanh.<br />\r\n<br />\r\nM&agrave; ở những thứ n&agrave;y liếm cẩu ch&iacute;nh giữa, Dư Điền ch&iacute;nh l&agrave; đi&ecirc;n cuồng nhất một c&aacute;i.<br />\r\n<br />\r\nHắn đối Đinh Mộ Ngưng vừa thấy đ&atilde; y&ecirc;u, đại học ba năm, đối với n&agrave;ng một mực đi&ecirc;n cuồng theo đuổi.<br />\r\n<br />\r\nNhưng l&agrave; hắn v&ocirc; luận như thế n&agrave;o theo đuổi, quay đầu lại Đinh Mộ Ngưng cũng đối với hắn phớt lờ kh&ocirc;ng để &yacute; tới.<br />\r\n<br />\r\nV&igrave; lấy được Đinh Mộ Ngưng tr&aacute;i tim, Dư Điền bỏ ra ch&iacute;nh m&igrave;nh hết thảy.<br />\r\n<br />\r\nC&oacute; thể n&oacute;i, Dư Điền đối với n&agrave;ng đ&atilde; đến đi&ecirc;n cuồng tr&igrave;nh độ.<br />\r\n<br />\r\nĐủ loại xa hoa x&aacute;ch tay hiệu nổi tiếng, đồng hồ đeo tay, đồ trang điểm, tất cả đều mua cho Đinh Mộ Ngưng.<br />\r\n<br />\r\nV&igrave; Đinh Mộ Ngưng, Dư Điền c&oacute; thể một ng&agrave;y đi l&agrave;m th&ecirc;m tam phần c&ocirc;ng phu.<br />\r\n<br />\r\nV&igrave; Đinh Mộ Ngưng, Dư Điền c&oacute; thể liều lĩnh.<br />\r\n<br />\r\nHắn cho l&agrave; c&oacute; thể cảm động Đinh Mộ Ngưng, c&oacute; thể sự thật hắn chỉ l&agrave; một vỏ xe ph&ograve;ng hờ m&agrave; th&ocirc;i.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nĐinh Mộ Ngưng vừa mới bắt đầu, cho l&agrave; gặp ph&uacute; nhị đại. Chẳng qua l&agrave; khi n&agrave;ng r&otilde; r&agrave;ng Dư Điền gia đ&igrave;nh sau, liền đối với hắn kh&ocirc;ng th&egrave;m liếc một c&aacute;i.<br />\r\n<br />\r\nDư Điền y&ecirc;u v&ocirc; c&ugrave;ng si cuồng, c&oacute; thể Đinh Mộ Ngưng cho tới b&acirc;y giờ kh&ocirc;ng c&oacute; coi l&agrave; chuyện to t&aacute;t.<br />\r\n<br />\r\nCứ như vậy Dư Điền ước chừng theo đuổi Đinh Mộ Ngưng ba năm.<br />\r\n<br />\r\nN&agrave;y thời gian ba năm, Dư Điền l&agrave;m rất nhiều rồi cảm động sự t&igrave;nh, thậm ch&iacute; để cho người chung quanh đều cảm gi&aacute;c được lệ n&oacute;ng doanh tr&ograve;ng.<br />\r\n<br />\r\nC&oacute; thể Đinh Mộ Ngưng đối với hắn vẫn l&agrave; &ocirc;n ho&agrave;.<br />\r\n<br />\r\nCực kỳ Dư Điền đối với n&agrave;ng được, n&agrave;ng hay lại l&agrave; một m&igrave;nh to&agrave;n thu.<br />\r\n<br />\r\nCực kỳ n&agrave;ng thật đang muốn t&igrave;m nam nh&acirc;n, hay lại l&agrave; bản xứ c&oacute; ph&ograve;ng c&oacute; xe, gia đ&igrave;nh ho&agrave;n cảnh tốt nam nh&acirc;n.<br />\r\n<br />\r\nCứ như vậy Đinh Mộ Ngưng rất nhanh d&otilde;i theo Dịch Thừa, bắt đầu đuổi ngược.<br />\r\n<br />\r\nDịch Thừa rất nhanh trở th&agrave;nh bạn trai n&agrave;ng, một m&agrave;n n&agrave;y cũng kh&ocirc;ng biết để cho bao nhi&ecirc;u liếm cẩu v&igrave; chi t&acirc;m bể.<br />\r\n<br />\r\nDư Điền c&agrave;ng l&agrave; thống khổ vạn phần. Hắn v&igrave; m&igrave;nh nữ thần, đ&atilde; bỏ ra hết thảy. C&oacute; thể quay đầu lại nhưng l&agrave; như vậy kết quả.<br />\r\n<br />\r\nVốn l&agrave; hắn lấy vì chuy&ecirc;̣n này kết th&uacute;c, nhưng ai biết Đinh Mộ Ngưng đối th&aacute;i độ của hắn, lại thay đổi kh&aacute; hơn.<br />\r\n<br />\r\nHắn vốn cho l&agrave; c&oacute; hi vọng, mỗi ng&agrave;y cho Đinh Mộ Ngưng đưa bữa ăn s&aacute;ng. Đối với n&agrave;ng nhẫn nhục chịu kh&oacute;.<br />\r\n<br />\r\nC&oacute; thể từ đầu tới cuối, Đinh Mộ Ngưng cũng kh&ocirc;ng c&oacute; đem hắn để ở trong mắt. Chỉ l&agrave; coi hắn l&agrave; th&agrave;nh vỏ xe ph&ograve;ng hờ.<br />\r\n<br />\r\nDư Điền v&igrave; n&agrave;ng, khắp nơi vay tiền, đủ loại mượn vay online.<br />\r\n<br />\r\nKết quả hắn thiếu năm trăm ng&agrave;n, khắp nơi bị đ&ograve;i nợ.<br />\r\n<br />\r\nỞ dạng n&agrave;y tuyệt vọng ch&iacute;nh giữa, Dư Điền t&acirc;m linh dần dần vặn vẹo.<br />\r\n<br />\r\nHắn biết m&igrave;nh l&agrave; vỏ xe ph&ograve;ng hờ, v&ocirc; luận như thế n&agrave;o cũng kh&ocirc;ng chiếm được Đinh Mộ Ngưng y&ecirc;u.<br />\r\n<br />\r\nNhưng hắn kh&ocirc;ng cam l&ograve;ng, hắn đ&atilde; c&ugrave;ng đường, đ&atilde; kh&ocirc;ng sợ h&atilde;i.<br />\r\n<br />\r\nV&igrave; vậy hắn d&ugrave;ng rồi thời gian một th&aacute;ng, đem &Acirc;m Vương Phủ biến th&agrave;nh giết người ph&aacute;p trường.<br />\r\n<br />\r\nSau đ&oacute; m&ecirc; hoặc Dịch Thừa mang theo Đinh Mộ Ngưng bọn họ đi tới nơi n&agrave;y.<br />\r\n<br />\r\nSau đ&oacute; ở nơi n&agrave;y, tru diệt liền bắt đầu rồi.<br />\r\n<br />\r\nDư Điền thủ đoạn hung t&agrave;n, thập phần tỉnh t&aacute;o.<br />\r\n<br />\r\nTiến v&agrave;o &Acirc;m Vương Phủ nh&acirc;n, bị hắn từng c&aacute;i giết chết.<br />\r\n<br />\r\nNhất l&agrave; Dịch Thừa, bị Dư Điền h&agrave;nh hạ đến chết. Gặp kh&oacute; m&agrave; h&igrave;nh dung thống khổ.<br />\r\n<br />\r\nC&oacute; thể n&oacute;i, Dư Điền t&acirc;m linh đ&atilde; ho&agrave;n to&agrave;n m&eacute;o m&oacute; rồi.<br />\r\n<br />\r\nHắn chỉ muốn chiếm đoạt Đinh Mộ Ngưng, v&igrave; c&aacute;i n&agrave;y mục đ&iacute;ch, hắn c&oacute; thể kh&ocirc;ng tiếc bất cứ gi&aacute; n&agrave;o.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; ai biết hắn l&agrave; thế n&agrave;o nghĩ, nhưng hắn vẫn kh&ocirc;ng cố kỵ ch&uacute;t n&agrave;o l&agrave;m.<br />\r\n<br />\r\nCứ như vậy k&egrave;m theo lần lượt thảm kịch ph&aacute;t sinh, Đinh Mộ Ngưng nam nh&acirc;n b&ecirc;n người to&agrave;n bộ chết đi.<br />\r\n<br />\r\nRất nhanh to&agrave;n bộ &Acirc;m Vương Phủ, chỉ c&ograve;n lại hắn v&agrave; Đinh Mộ Ngưng rồi.<br />\r\n<br />\r\nĐinh Mộ Ngưng sợ h&atilde;i k&ecirc;u to, c&oacute; thể quay đầu lại, lại đ&atilde; kh&ocirc;ng c&oacute; nh&acirc;n sẽ cứu n&agrave;ng rồi.<br />\r\n<br />\r\nDưới t&igrave;nh huống n&agrave;y, Đinh Mộ Ngưng chỉ c&oacute; thể khuất phục ở Dư Điền trong tay.<br />\r\n<br />\r\nCứ như vậy, bọn họ sống ở &Acirc;m Vương Phủ ch&iacute;nh giữa.<br />\r\n<br />\r\nDư Điền cuối c&ugrave;ng vẫn đạt được ước muốn, trở th&agrave;nh Đinh Mộ Ngưng nam nh&acirc;n. Thỏa m&atilde;n nội t&acirc;m của tự m&igrave;nh đi&ecirc;n cuồng &yacute; tưởng.<br />\r\n<br />\r\nSau đ&oacute;, bọn họ mất t&iacute;ch kinh động cảnh s&aacute;t, sau đ&oacute; rất nhanh th&igrave; bọn họ bị ph&aacute;t hiện.<br />\r\n<br />\r\nDư Điền tự nhi&ecirc;n dựa v&agrave;o nơi hiểm yếu chống lại, &aacute;nh mắt tr&agrave;n đầy đi&ecirc;n cuồng. Nhưng hắn vẫn bị đ&aacute;nh chết.<br />\r\n<br />\r\nVề phần Đinh Mộ Ngưng, bị giải cứu ra.<br />\r\n<br />\r\nChuyện n&agrave;y cứ như vậy hổ đầu đu&ocirc;i rắn kết th&uacute;c.<br />\r\n<br />\r\nNhưng từ đ&oacute; về sau, &Acirc;m Vương Phủ liền thường c&oacute; chuyện lạ ph&aacute;t sinh. Nghe n&oacute;i ở &Acirc;m Vương Phủ ch&iacute;nh giữa, một c&aacute;i trong tay cầm Thủ Nỗ nam nh&acirc;n. Ch&iacute;nh v&ocirc; t&igrave;nh săn giết từng c&aacute;i đi v&agrave;o nh&acirc;n.<br />\r\n<br />\r\n&quot;Nhưng hắn m&atilde;i m&atilde;i cũng sẽ kh&ocirc;ng l&agrave;m thương tổn một c&aacute;i t&ecirc;n l&agrave; Đinh Mộ Ngưng nữ nh&acirc;n, cũng kh&ocirc;ng biết l&agrave; nguy&ecirc;n nh&acirc;n g&igrave;.&quot;<br />\r\n<br />\r\nSi t&igrave;nh linh hồn, quay đầu lại bởi v&igrave; căm gh&eacute;t. Đ&atilde; kinh biến đến mức ho&agrave;n to&agrave;n thay đổi.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n thở ph&agrave;o nhẹ nh&otilde;m, nội t&acirc;m thư ho&atilde;n xuống.<br />\r\n<br />\r\nRốt cuộc t&igrave;m được nguy&ecirc;n nh&acirc;n, nguy&ecirc;n lai l&agrave; như vậy.<br />\r\n<br />\r\n&Acirc;m Vương Phủ lại xảy ra chuyện lớn như vậy t&igrave;nh, nhưng lại kh&ocirc;ng biết g&igrave; cả. Thật sự l&agrave; qu&aacute; kỳ qu&aacute;i.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nNhưng kh&ocirc;ng nghi ngờ ch&uacute;t n&agrave;o, Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng đ&iacute;ch thực qu&aacute; đ&aacute;ng sợ.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n trợn mắt hốc mồm, đối với Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng đ&aacute;ng sợ, c&oacute; một c&aacute;i nhận thức mới.<br />\r\n<br />\r\nC&oacute; thể bất kể như thế n&agrave;o, diễn xuất đ&atilde; ho&agrave;n th&agrave;nh, Di&ecirc;u Thi&ecirc;n Qu&acirc;n nh&igrave;n điện thoại di động, t&igrave;m kiếm đầu mối mới.<br />\r\n<br />\r\nChỉ tiếc, muốn t&igrave;m được đầu mối, thật sự l&agrave; v&ocirc; c&ugrave;ng kh&oacute; khăn.<br />\r\n<br />\r\nTrải qua Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng đ&aacute;ng sợ sau, Di&ecirc;u Thi&ecirc;n Qu&acirc;n đ&atilde; rất r&otilde;.<br />\r\n<br />\r\nHắn đ&atilde; l&acirc;m v&agrave;o một c&aacute;i thế giới hắc &aacute;m ch&iacute;nh giữa, chỉ c&oacute; gi&atilde;y giụa mới c&oacute; thể l&agrave;m cho n&agrave;ng sống tiếp.<br />\r\n<br />\r\nĐang l&uacute;c n&agrave;y, Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng lần nữa vang l&ecirc;n: &quot;Ch&uacute;c mừng ngươi, ngươi tham diễn điện ảnh &laquo; &Acirc;m Vương Phủ &raquo; đ&atilde; th&agrave;nh c&ocirc;ng chiếu phim.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n hơi sửng sờ, vội v&agrave;ng cầm điện thoại di động l&ecirc;n tra nh&igrave;n.<br />\r\n<br />\r\nLại thấy ch&iacute;nh m&igrave;nh thẻ ng&acirc;n h&agrave;ng b&ecirc;n trong, cũng kh&ocirc;ng biết l&uacute;c n&agrave;o, nhiều một khoản tiền rất lớn.<br />\r\n<br />\r\n&quot;Số tiền n&agrave;y l&agrave; chuyện g&igrave; xảy ra?&quot; Trong l&ograve;ng Di&ecirc;u Thi&ecirc;n Qu&acirc;n kinh ngạc. Kiểm tra Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng. Hắn biết ch&acirc;n tướng.<br />\r\n<br />\r\nNguy&ecirc;n lai hắn tố đ&oacute;ng phim &laquo; Ai Mưu S&aacute;t &Aacute;i T&igrave;nh Của Ta Rồi &raquo; , bởi v&igrave; mua rất nhiều người, v&igrave; vậy hắn thu được rất lớn nhất b&uacute;t chia hoa hồng.<br />\r\n<br />\r\nDi&ecirc;u Thi&ecirc;n Qu&acirc;n nh&igrave;n thấy b&agrave;n tay thẻ ng&acirc;n h&agrave;ng, sắc mặt tr&agrave;n đầy kh&ocirc;ng tưởng tượng nổi, nguy&ecirc;n lai Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng, lại c&oacute; thể d&ugrave;ng đến như vậy kiếm tiền.<br />\r\n<br />\r\nCực kỳ sự t&igrave;nh đến bước n&agrave;y, Di&ecirc;u Thi&ecirc;n Qu&acirc;n đ&atilde; mơ hồ biết.<br />\r\n<br />\r\nMặc d&ugrave; Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng đ&aacute;ng sợ, c&oacute; thể mỗi m&ocirc;̣t bộ Ch&acirc;n Nh&acirc;n T&uacute; cũng c&oacute; thể thu được số lớn tiền.<br />\r\n<br />\r\nNếu như diễn kỹ đủ được, thậm ch&iacute; c&oacute; nổi danh cơ hội.<br />\r\n<br />\r\nSợ rằng c&oacute; c&agrave;ng ng&agrave;y c&agrave;ng nhiều nh&acirc;n, sẽ được đọa như Th&acirc;m Uy&ecirc;n, kh&ocirc;ng thể tự kềm chế.<br />\r\n<br />\r\nD&ugrave; l&agrave; Ho&agrave;ng Tuyền T&uacute; Tr&agrave;ng nội dung, tất cả đều để cho người ta kh&ocirc;ng r&eacute;t m&agrave; run.<br />\r\n<br />\r\nNghĩ đến đ&acirc;y, hắn bất đắc dĩ thở d&agrave;i một c&aacute;i. Bu&ocirc;ng xuống con chuột, tiếp tục t&igrave;m đầu mối.<br />\r\n<br />\r\nHo&agrave;ng Tuyền T&uacute; Tr&agrave;ng, kết quả l&agrave; vật g&igrave;? L&agrave; ai đem n&oacute; thanh to&aacute;n th&agrave;nh c&aacute;i bộ d&aacute;ng n&agrave;y?<br />\r\n<br />\r\nTại sao, n&oacute; sẽ t&aacute;i diễn một m&agrave;n lại một m&agrave;n thảm kịch?</p>', '2021-12-01 14:55:54', 0, 1),
(302, 76, 'Thái Thị Khẩu Trọng Sinh', '1', '<p>Th&aacute;i Thị Khẩu c&oacute; thể hiểu l&agrave; cổng chợ<br />\r\n<br />\r\nTrường Sinh Thi&ecirc;n Tri&ecirc;u, Tầm Ti&ecirc;n huyện.<br />\r\n<br />\r\nT&iacute; t&aacute;ch t&iacute; t&aacute;ch mưa dầm, bao phủ sắp tối thời gian chợ b&aacute;n thức ăn đường phố.<br />\r\n<br />\r\nNguy&ecirc;n bản đi chợ biển người dần muốn t&aacute;n đi, đột nhi&ecirc;n truyền đến một trận đ&aacute;nh trống reo h&ograve;.<br />\r\n<br />\r\nĐ&atilde; thấy mấy chục binh l&iacute;nh từ một chỗ kh&aacute;c chạy tới, nhung trang cầm đao, s&aacute;t kh&iacute; bốc l&ecirc;n, ở giữa l&agrave; mười c&aacute;i xiềng x&iacute;ch gia th&acirc;n, mặc vết bẩn &aacute;o t&ugrave; nh&acirc;n, to&agrave;n th&acirc;n h&ocirc;i thối, vết sẹo miệng vết thương đếm kh&ocirc;ng hết thanh ni&ecirc;n t&ugrave; phạm.<br />\r\n<br />\r\nPh&iacute;a trước nhất l&agrave; một c&aacute;i m&ocirc;i d&agrave;y r&acirc;u đen, mặt mũi tr&agrave;n đầy dữ tợn, cưỡi ngựa cao to trung ni&ecirc;n quan lại.<br />\r\n<br />\r\nTối hậu phương, l&agrave; mấy chiếc d&ugrave;ng để chở liễm thi thể xe ngựa.<br />\r\n<br />\r\nĐo&agrave;n người n&agrave;y g&otilde; chi&ecirc;ng dẹp đường, phi thường n&aacute;o nhiệt.<br />\r\n<br />\r\nMuốn t&aacute;n d&acirc;n ch&uacute;ng lập tức lại tụ lại, thậm ch&iacute; c&agrave;ng xa x&ocirc;i người cũng đều chạy tới.<br />\r\n<br />\r\nTrận thế n&agrave;y, ai cũng biết được muốn ph&aacute;t sinh c&aacute;i g&igrave;.<br />\r\n<br />\r\nChợ b&aacute;n thức ăn đường phố nhất đại cảnh!<br />\r\n<br />\r\nB&ecirc;n đường chặt đầu.<br />\r\n<br />\r\nKh&ocirc;ng bao l&acirc;u, n&agrave;y khu vực liền ba tầng trong ba tầng ngo&agrave;i v&acirc;y đầy người.<br />\r\n<br />\r\nC&oacute; b&igrave;nh d&acirc;n, c&oacute; h&agrave;i đồng, c&oacute; khất c&aacute;i, c&oacute; nh&agrave;n h&aacute;n, c&oacute; người gi&agrave;u c&oacute;, thậm ch&iacute; c&ograve;n c&oacute; cầm gọi l&agrave; &quot;M&aacute;y ảnh&quot; sự vật, t&oacute;c v&agrave;ng mắt xanh ph&iacute;a t&acirc;y di nh&acirc;n.<br />\r\n<br />\r\nTuy n&oacute;i kh&ocirc;ng &iacute;t d&acirc;n ch&uacute;ng bản th&acirc;n cũng l&agrave; xanh xao v&agrave;ng vọt, giống như nạn d&acirc;n, nhưng c&aacute;i n&agrave;y kh&ocirc;ng ch&uacute;t n&agrave;o ảnh hưởng bọn họ tham gia n&aacute;o nhiệt tr&aacute;i tim.<br />\r\n<br />\r\nCanh giờ vừa đến, b&aacute;o giờ quan dắt tiếng n&oacute;i lớn tiếng b&aacute;o giờ.<br />\r\n<br />\r\nGi&aacute;m trảm quan tay cầm b&uacute;t son, b&ecirc;n miệng ngậm lấy cười lạnh, từng c&aacute;i ph&aacute;c hoạ.<br />\r\n<br />\r\nĐao phủ ai v&agrave;o chỗ nấy, từ đ&ocirc;ng đến t&acirc;y, liền muốn theo thứ tự chặt xuống.<br />\r\n<br />\r\n&quot;Muốn chặt muốn chặt, chợ b&aacute;n thức ăn đường phố quả nhi&ecirc;n danh bất hư truyện, thật c&oacute; thể nh&igrave;n chặt đầu.&quot;<br />\r\n<br />\r\n&quot;Cũng l&agrave; đ&aacute;ng thương a, nh&igrave;n xem thật th&ecirc; thảm.&quot;<br />\r\n<br />\r\n&quot;Tuổi c&ograve;n trẻ liền mất mạng, ai, thật sự l&agrave; đ&aacute;ng thương a.&quot;<br />\r\n<br />\r\n&quot;Đều l&agrave; đỉnh tốt người thanh ni&ecirc;n, chạy tới phản triều đ&igrave;nh, kết quả bị phản đồ b&aacute;n, bị cẩu quan cho tận diệt.&quot;<br />\r\n<br />\r\n&quot;Mẹ h&agrave;i nhi, m&agrave;n thầu mang a?&quot;<br />\r\n<br />\r\n&quot;Nếu l&agrave; ai c&oacute; thừa tiền, c&oacute; thể tại sau đ&oacute; hỗ trợ thu dọn bọn họ thi thể.&quot;<br />\r\n<br />\r\n&quot;Thu dọn c&aacute;i g&igrave; a, tội danh l&agrave; mưu nghịch, những h&agrave;i tử n&agrave;y đầu l&acirc;u đều l&agrave; muốn bị treo đến chi&ecirc;u hồn c&aacute;n b&ecirc;n tr&ecirc;n, trực hệ cũng chỉ c&oacute; thể sau bảy ng&agrave;y đến chuộc, như kh&ocirc;ng c&oacute; th&acirc;n nh&acirc;n xuất tiền, chỉ sợ đều sẽ bị n&eacute;m đến b&atilde;i tha ma đi, bị những c&aacute;i kia linh cẩu s&oacute;i hoang gặm sạch sẽ.&quot;<br />\r\n<br />\r\n...<br />\r\n<br />\r\nNhững n&agrave;y ồn &agrave;o động tĩnh, bừng tỉnh Đ&agrave;o Tiềm.<br />\r\n<br />\r\nHắn vừa mở mắt, trước nh&igrave;n thấy v&acirc;y xem d&acirc;n ch&uacute;ng, tiếp theo l&agrave; tr&ecirc;n người tr&oacute;i buộc cảm gi&aacute;c.<br />\r\n<br />\r\nV&ocirc; &yacute; thức nh&igrave;n quanh quanh m&igrave;nh, một loại m&atilde;nh liệt kinh dị đ&aacute;nh tới.<br />\r\n<br />\r\nT&ugrave;y theo m&agrave; đến, c&ograve;n giống như như cuồng triều ph&acirc;n loạn tr&iacute; nhớ.<br />\r\n<br />\r\nRất nhanh, Đ&agrave;o Tiềm minh ngộ.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nHắn xuy&ecirc;n việt, m&agrave; lại l&agrave; xuy&ecirc;n việt th&agrave;nh một c&aacute;i sắp bị chặt đầu tội phạm.<br />\r\n<br />\r\nB&acirc;y giờ l&agrave; Trường Sinh Thi&ecirc;n Tri&ecirc;u, Thi&ecirc;n Mệnh năm thứ ch&iacute;n.<br />\r\n<br />\r\nC&aacute;i n&agrave;y th&acirc;n thể nguy&ecirc;n chủ ch&iacute;nh l&agrave; Nam Việt một huyện th&agrave;nh nh&acirc;n sĩ, gia cảnh kh&ocirc;ng tầm thường, ai ngờ một c&aacute;i năm trong nh&agrave; kh&ocirc;ng cẩn thận đắc tội nơi đ&oacute; th&acirc;n h&agrave;o, bị cừu gia giày vò cửa n&aacute;t nh&agrave; tan.<br />\r\n<br />\r\nThiếu ni&ecirc;n may mắn tho&aacute;t đi, sau gặp gỡ một ch&uacute;t ch&iacute; sĩ, liền cũng mang một bầu nhiệt huyết chạy tới tham gia phản triều đ&igrave;nh hoạt động.<br />\r\n<br />\r\nMấy năm qua li&ecirc;n chiến gần ph&acirc;n nửa Thi&ecirc;n Tri&ecirc;u, mấy ng&agrave;y trước &yacute; đồ tại Tầm Ti&ecirc;n huyện cổ động tr&uacute; qu&acirc;n khởi xướng vũ trang h&agrave;nh động.<br />\r\n<br />\r\nĐ&aacute;ng tiếc bị phản đồ b&aacute;n, cứ điểm bị trừ bỏ, tất cả mọi người bị một lưới bắt hết.<br />\r\n<br />\r\nHiện tại c&agrave;ng l&agrave; c&ugrave;ng nhau bị k&eacute;o đến c&aacute;i n&agrave;y chợ b&aacute;n thức ăn đường phố, muốn h&agrave;nh h&igrave;nh.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y c&aacute;i g&igrave; bắt đầu a, người kh&aacute;c coi như Địa Ngục bắt đầu, cũng sẽ cho ch&uacute;t người nh&agrave;, cho điểm phản ứng cầu sinh thời gian.&quot;<br />\r\n<br />\r\n&quot;L&agrave;m sao đến nơi n&agrave;y của ta, v&agrave;i gi&acirc;y sau liền muốn chịu nhất đao, rơi c&aacute;i thi thể t&aacute;ch rời hạ tr&agrave;ng?&quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm ch&iacute;nh bi phẫn.<br />\r\n<br />\r\nN&agrave;y mấy c&aacute;i một th&acirc;n vải đay th&ocirc; đỏ thẫm trang phục, khỏa khăn cột đỏ, &ocirc;m &acirc;́p Quỷ Đầu Đao đao phủ nh&oacute;m cũng kh&ocirc;ng để &yacute; c&aacute;i kh&aacute;c, trực tiếp liền muốn đem c&aacute;i n&agrave;y mười c&aacute;i t&ugrave; phạm nhấn quỳ xuống, tiếp theo vung đao ch&eacute;m tới đầu.<br />\r\n<br />\r\nNhưng ai c&oacute; thể tưởng đến, những n&agrave;y bị cực h&igrave;nh tra tấn chỉ c&ograve;n lại nửa c&aacute;i mạng thanh ni&ecirc;n loạn đảng nh&oacute;m, giờ ph&uacute;t n&agrave;y đ&uacute;ng l&agrave; c&ugrave;ng nhau giằng co.<br />\r\n<br />\r\nKh&ocirc;ng một người, nguyện &yacute; quỳ xuống.<br />\r\n<br />\r\nBọn họ đ&ocirc;i mắt b&ecirc;n trong, kh&ocirc;ng cái gì đối tử vong sợ h&atilde;i.<br />\r\n<br />\r\nBọn họ kh&ocirc;ng để &yacute; trong cổ họng d&acirc;ng tr&agrave;o m&aacute;u tươi, trừng mắt hai mắt, miệng h&aacute; lớn, &yacute; đồ hướng d&acirc;n ch&uacute;ng ph&aacute;t ra &acirc;m thanh.<br />\r\n<br />\r\nTrong đ&aacute;m người, h&iacute;t v&agrave;o kh&iacute; lạnh thanh &acirc;m kh&ocirc;ng dứt b&ecirc;n tai.<br />\r\n<br />\r\nTất cả mọi người giờ ph&uacute;t n&agrave;y đều tr&ocirc;ng thấy, những người tuổi trẻ n&agrave;y đầu lưỡi vậy m&agrave; đều bị nh&ocirc;̉ đi.<br />\r\n<br />\r\nĐẫm m&aacute;u khe, nh&igrave;n thấy m&agrave; giật m&igrave;nh.<br />\r\n<br />\r\nMọi người chỉ c&oacute; thể nghe thấy một ch&uacute;t &yacute; nghĩa kh&ocirc;ng r&otilde; thanh &acirc;m, tr&ocirc;ng thấy bọt m&aacute;u phun tung to&eacute;.<br />\r\n<br />\r\nD&ugrave; vậy, c&aacute;i n&agrave;y mười c&aacute;i sắp chết thanh ni&ecirc;n vẫn kh&ocirc;ng từ bỏ ph&aacute;t ra g&agrave;o th&eacute;t, tựa hồ l&agrave; muốn tỉnh lại v&acirc;y xem d&acirc;n ch&uacute;ng.<br />\r\n<br />\r\nĐ&agrave;o Tiềm vốn định tỉnh t&aacute;o suy tư, nh&igrave;n c&oacute; thể hay kh&ocirc;ng tại mấy gi&acirc;y cuối c&ugrave;ng t&igrave;m tới cầu sinh chi ph&aacute;p.<br />\r\n<br />\r\nC&oacute; thể trong th&acirc;n thể c&ograve;n s&oacute;t lại &yacute; ch&iacute;, vẫn chi phối lấy th&acirc;n thể.<br />\r\n<br />\r\nHắn gắt gao thẳng lấy đầu gối, hai mắt trợn l&ecirc;n nh&igrave;n xem kinh hoảng, hiếu k&igrave;, ng&acirc;y thơ d&acirc;n ch&uacute;ng.<br />\r\n<br />\r\nĐầu lưỡi của hắn c&ograve;n s&oacute;t lại kh&aacute; nhiều, l&agrave; lấy hắn ho khan m&aacute;u, nhưng cũng miễn cưỡng ph&aacute;t ra một ch&uacute;t thanh &acirc;m.<br />\r\n<br />\r\n&quot;Ch&uacute;ng ta... L&agrave; người... Tuyệt kh&ocirc;ng quỳ cẩu quan...&quot;<br />\r\n<br />\r\n&quot;Triều đ&igrave;nh hồ đồ... D&acirc;n ch&uacute;ng lầm than... Hữu t&acirc;m giết tặc... V&ocirc; lực hồi thi&ecirc;n...&quot;<br />\r\n<br />\r\n&quot;Gia cừu quốc hận... Mau thức tỉnh ng&ocirc; d&acirc;n...&quot;<br />\r\n<br />\r\n&quot;Oanh &quot;<br />\r\n<br />\r\nMột c&acirc;u cuối c&ugrave;ng g&agrave;o th&eacute;t ph&aacute;t ra, Đ&agrave;o Tiềm trực tiếp bị đẩy v&agrave;o ban đầu th&acirc;n thể ch&iacute; sĩ k&yacute; ức hải.<br />\r\n<br />\r\nPhức tạp m&agrave; ngắn ngủi hơn hai mươi người trẻ tuổi sinh kinh lịch, m&atilde;nh liệt bao phủ tới.<br />\r\n<br />\r\nM&agrave; ngoại giới, n&agrave;y m&ocirc;i d&agrave;y r&acirc;u đen gi&aacute;m trảm quan đ&atilde; l&agrave; l&acirc;m v&agrave;o kinh hoảng, li&ecirc;n tiếp vứt xuống thăm đỏ, giận dữ h&eacute;t:<br />\r\n<br />\r\n&quot;Trảm!&quot;<br />\r\n<br />\r\n&quot;Nhanh trảm cho ta, c&aacute;c ngươi đ&aacute;m phế vật n&agrave;y, lại để cho mưu nghịch loạn đảng mở miệng, bản quan đem c&aacute;c ngươi c&ugrave;ng một chỗ chặt.&quot;<br />\r\n<br />\r\n&quot;Trảm a!&quot;<br />\r\n<br />\r\nChủ quan nổi giận, nguy&ecirc;n bản hơi qua loa đao phủ nh&oacute;m kh&ocirc;ng d&aacute;m tiếp tục tr&igrave; ho&atilde;n.<br />\r\n<br />\r\nNhao nhao ra tay độc &aacute;c, thậm ch&iacute; đem ch&iacute; sĩ nh&oacute;m đầu gối đ&aacute;nh n&aacute;t, chỉ l&agrave; vung đao trước đều l&agrave; thấp giọng n&oacute;i: &quot;Chư vị, ch&uacute;ng ta hầu hạ c&aacute;c ngươi đi, tuyệt kh&ocirc;ng để c&aacute;c ngươi chịu khổ, l&ecirc;n đường đi.&quot;<br />\r\n<br />\r\nThoại &acirc;m rơi xuống l&uacute;c, đ&atilde; thấy s&aacute;ng b&oacute;ng đao quang bắt đầu từng đạo s&aacute;ng l&ecirc;n.<br />\r\n<br />\r\nĐầu l&acirc;u rơi xuống đất, m&aacute;u vẩy đất v&agrave;ng.<br />\r\n<br />\r\nMột lời bầu nhiệt huyết d&acirc;ng tr&agrave;o l&uacute;c, đao phủ nh&oacute;m c&ograve;n muốn thanh miệng, phun tửu, nghỉ k&igrave;nh, đ&ocirc;̉i đao.<br />\r\n<br />\r\nV&agrave;i gi&acirc;y sau, rốt cục đến phi&ecirc;n Đ&agrave;o Tiềm.<br />\r\n<br />\r\nM&agrave; tại thời khắc n&agrave;y, Đ&agrave;o Tiềm đ&atilde; tựa như &quot;Xem&quot; xong ban đầu th&acirc;n thể một đời, chỉ c&ograve;n lại sau c&ugrave;ng tuổi thơ thời kỳ.<br />\r\n<br />\r\nM&agrave; để Đ&agrave;o Tiềm kinh ngạc biến cố, cũng tại l&uacute;c n&agrave;y xuất hiện.<br />\r\n<br />\r\nTừ trong tr&iacute; nhớ Đ&agrave;o Tiềm biết được, thế giới n&agrave;y d&ugrave; c&ugrave;ng kiếp trước triều đại n&agrave;o đ&oacute; những năm cuối c&ugrave;ng loại, nhưng cũng c&oacute; qu&aacute; nhiều kh&aacute;c biệt.<br />\r\n<br />\r\nTỉ như, nơi n&agrave;y lại tồn tại đại lượng y&ecirc;u ma quỷ qu&aacute;i, v&ocirc; giải hiện tượng thần b&iacute;, kh&ocirc;ng thể diễn tả quỷ vật loại h&igrave;nh.<br />\r\n<br />\r\nNh&acirc;n loại, dường như c&ugrave;ng y&ecirc;u ma thần ti&ecirc;n quỷ hỗn cư?<br />\r\n<br />\r\nLại hoặc l&agrave;, l&agrave; c&aacute;i n&agrave;y rất nhiều qu&aacute;i dị l&agrave; từ nh&acirc;n loại sinh s&ocirc;i ra?<br />\r\n<br />\r\nBan đầu th&acirc;n thể khi c&ograve;n nhỏ, liền c&oacute; linh dị kinh lịch:<br />\r\n<br />\r\nHắn ngộ nhập hoang d&atilde; mộ phần khu, tại c&aacute;i n&agrave;o đ&oacute; mồ mả tổ ti&ecirc;n ngủ, cũng mộng thấy m&igrave;nh c&ugrave;ng một con hồ ly chung đọc s&aacute;ch.<br />\r\n<br />\r\nN&agrave;y &quot;Hồ s&aacute;ch&quot; b&ecirc;n trong hơn ph&acirc;n nửa nội dung, đều tan biến tại nguy&ecirc;n th&acirc;n thể trong tr&iacute; nhớ.<br />\r\n<br />\r\nDuy chỉ c&oacute; trong đ&oacute; một đoạn khẩu quyết, lại nhưng v&agrave;o l&uacute;c n&agrave;y giờ ph&uacute;t n&agrave;y, từ ban đầu yếu ớt, đến đằng sau dần dần lớn tiếng, thậm ch&iacute; l&agrave; trực tiếp đọc ra.<br />\r\n<br />\r\nPh&uacute;c ch&iacute; t&acirc;m linh, lại hoặc l&agrave; &ocirc;m lấy ngựa chết l&agrave;m ngựa sống &yacute; nghĩ.<br />\r\n<br />\r\nĐ&agrave;o Tiềm nh&igrave;n xem kh&oacute;e mắt xuất hiện đao quang, đ&aacute;y l&ograve;ng lập tức đi theo mặc niệm l&ecirc;n khẩu quyết kia đến:<br />\r\n<br />\r\n&quot;Bọ hung chuyển ho&agrave;n, ho&agrave;n th&agrave;nh m&agrave; tinh nghĩ chi, m&agrave; c&oacute; nhuyễn bạch giả lưu giữ ho&agrave;n b&ecirc;n trong, Nga đi x&aacute;c m&agrave; ve. Kia khương kh&ocirc;ng nghĩ, kia nhuyễn hề bạch?&quot;<br />\r\n<br />\r\n&quot;B&agrave;o người canh cua, di một ch&acirc;n mấy b&ecirc;n tr&ecirc;n, cua đ&atilde; canh, m&agrave; di đủ c&ograve;n động.&quot;<br />\r\n<br />\r\n&quot;L&agrave; sinh tử người, nhất kh&iacute; tụ t&aacute;n nhĩ. Kh&ocirc;ng sống kh&ocirc;ng chết, m&agrave; người ho&agrave;nh kế n&oacute;i sinh tử... .&quot;<br />\r\n<br />\r\nMột lần, vẻn vẹn chỉ l&agrave; một lần.<br />\r\n<br />\r\nChuyện quỷ dị, ph&aacute;t sinh.<br />\r\n<br />\r\nĐ&agrave;o Tiềm chỉ cảm thấy m&igrave;nh ph&uacute;t chốc tiến v&agrave;o một loại cực cổ qu&aacute;i trạng th&aacute;i, trong đầu của hắn một đo&agrave;n tin tức lưu bỗng nhi&ecirc;n nổ tung.<br />\r\n<br />\r\nKh&ocirc;ng đợi hắn đi cảm gi&aacute;c, một loại kh&aacute;c &acirc;m l&atilde;nh, kh&iacute; tức kinh khủng sinh ra.<br />\r\n<br />\r\nKh&iacute; tức kia, lập tức liền để Đ&agrave;o Tiềm li&ecirc;n tưởng đến &quot;Thi thể&quot;, &quot;Kh&ocirc;i lỗi&quot;, &quot;Hư thối&quot; v&acirc;n v&acirc;n.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nMắt thấy muốn trượt hướng th&acirc;m uy&ecirc;n, bỗng nhi&ecirc;n linh hồn của hắn chỗ s&acirc;u, đ&uacute;ng l&agrave; lại tu&ocirc;n ra một cỗ kh&ocirc;ng c&aacute;ch n&agrave;o n&oacute;i r&otilde;, kh&ocirc;ng c&aacute;ch n&agrave;o tưởng tượng lực lượng.<br />\r\n<br />\r\nTồi kh&ocirc; lạp hủ, đem n&agrave;y khủng bố &acirc;m kh&iacute; va n&aacute;t.<br />\r\n<br />\r\nN&agrave;y một c&aacute;i chớp mắt, Đ&agrave;o Tiềm cả người hung hăng đ&aacute;nh một c&aacute;i lạnh run.<br />\r\n<br />\r\nSau đ&oacute;, Đ&agrave;o Tiềm tr&ocirc;ng thấy.<br />\r\n<br />\r\nTự th&acirc;n đầu người lăn xuống, huyết dịch phun ra, lại một tơ một h&agrave;o cảm gi&aacute;c đau cũng kh&ocirc;ng.<br />\r\n<br />\r\nChỉ l&agrave; linh hồn chậm r&atilde;i r&uacute;t ra t&agrave;n thi, trước nh&igrave;n về ph&iacute;a c&aacute;i kh&aacute;c ch&iacute; sĩ, chỉ thấy được hơn mười đạo ảm đạm bạch quang hiện l&ecirc;n, lượn lờ v&ocirc; tung.<br />\r\n<br />\r\nM&agrave; hắn tự th&acirc;n, lại kh&ocirc;ng khỏi cảm thấy được quanh m&igrave;nh c&aacute;c nơi, truyền đến to to nhỏ nhỏ kh&ocirc;ng đồng nhất lực hấp dẫn.<br />\r\n<br />\r\nRất nhanh, Đ&agrave;o Tiềm liền biết được &quot;Lực hấp dẫn chi nguy&ecirc;n&quot; l&agrave; c&aacute;i g&igrave;.<br />\r\n<br />\r\nN&agrave;y r&otilde; r&agrave;ng l&agrave; từng cỗ vừa mới chết kh&ocirc;ng l&acirc;u thi thể.<br />\r\n<br />\r\nB&ecirc;n đường bị chết đ&oacute;i l&atilde;o khất c&aacute;i, cống r&atilde;nh b&ecirc;n trong chuột chết hoặc con gi&aacute;n, trong tiệm cơm say chết trong ch&eacute;n con muỗi, c&aacute;ch đ&oacute; kh&ocirc;ng xa thuỷ sản quầy b&ecirc;n trong vừa mới chết t&ocirc;m c&aacute; cua... Những n&agrave;y, lại đều đối Đ&agrave;o Tiềm hồn linh sinh ra hấp xả lực lượng.<br />\r\n<br />\r\nĐ&agrave;o Tiềm c&oacute; chỗ minh ngộ, hắn tựa hồ l&agrave; c&oacute; phụ thể cơ hội sống lại.<br />\r\n<br />\r\nNhưng những n&agrave;y con muỗi t&ocirc;m c&aacute;, Đ&agrave;o Tiềm bản năng cự tuyệt.<br />\r\n<br />\r\nLinh hồn của hắn ng&acirc;y ng&ocirc; phi&ecirc;u đ&atilde;ng ra, đ&atilde; nghe kh&ocirc;ng gặp d&acirc;n ch&uacute;ng, đao phủ, gi&aacute;m trảm quan ồn &agrave;o.<br />\r\n<br />\r\nChỉ l&agrave; kiệt lực chống cự lại những c&aacute;i kia con muỗi thi thể động vật tản m&aacute;t ra hấp xả, muốn chọn tr&uacute;ng một bộ ph&ugrave; hợp thể x&aacute;c.<br />\r\n<br />\r\nNhưng rất nhanh, hắn cảm nhận được tai hoạ ngập đầu.<br />\r\n<br />\r\nTuy n&oacute;i ngày mưa d&acirc;̀m, kh&ocirc;ng c&oacute; &aacute;nh mặt trời chiếu xuống.<br />\r\n<br />\r\nCó th&ecirc;̉ t&ugrave;y thời nổi l&ecirc;n gi&oacute; lạnh, lại cũng c&oacute; thể thương tổn được linh hồn của hắn.<br />\r\n<br />\r\nV&agrave;o thời khắc n&agrave;y, một trận ch&acirc;n ch&iacute;nh băng l&atilde;nh thấu xương &acirc;m phong qu&eacute;t tới, k&eacute;m ch&uacute;t liền để hắn triệt để hồn phi ph&aacute;ch t&aacute;n.<br />\r\n<br />\r\nV&igrave; mạng sống!<br />\r\n<br />\r\nĐ&agrave;o Tiềm kh&ocirc;ng l&agrave;m lựa chọn kh&ocirc;ng được, khuất phục tại đại lượng hấp xả lực b&ecirc;n trong lớn nhất một cỗ.<br />\r\n<br />\r\nThế l&agrave; một gi&acirc;y sau, Đ&agrave;o Tiềm hồn linh bỗng nhi&ecirc;n đột tiến, bị một cỗ cự lực nắm k&eacute;o h&oacute;a th&agrave;nh bạch quang.<br />\r\n<br />\r\nPh&uacute;t chốc k&iacute;ch xạ đến chợ b&aacute;n thức ăn cuối phố, một m&ocirc;n hộ đ&oacute;ng chặt cửa h&agrave;ng b&ecirc;n trong.<br />\r\n<br />\r\nC&aacute;i n&agrave;y đ&uacute;ng l&agrave; c&aacute;i tối tăm, chật hẹp tiệm s&aacute;ch.<br />\r\n<br />\r\nĐể m&agrave; chiếu s&aacute;ng ngọn đ&egrave;n sớm đ&atilde; dập tắt, mặt đất rối bời phủ k&iacute;n một đống s&aacute;ch.<br />\r\n<br />\r\nB&ecirc;n quầy, th&igrave;nh l&igrave;nh nằm một bộ cao cao gầy g&ograve;, mặc vải b&ocirc;ng trường b&agrave;o, phủ lấy tiểu M&atilde; &aacute;o kho&aacute;c nam t&iacute;nh thi thể.<br />\r\n<br />\r\n&quot;Cũng l&agrave; ngươi.&quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm căn bản kh&ocirc;ng c&oacute; c&aacute;i kh&aacute;c lựa chọn.<br />\r\n<br />\r\nMặc cho n&agrave;y hấp xả lực quyển linh hồn của hắn, hướng c&aacute;i kia kh&ocirc;ng biết nguy&ecirc;n nh&acirc;n c&aacute;i chết, kh&ocirc;ng biết tuổi t&aacute;c nam thi b&ecirc;n trong bịt lại.</p>', '2021-12-01 14:59:53', 8, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(303, 76, 'Sổ Sách Nhật Ký', '2', '<p>Rả r&iacute;ch mưa dầm cuốn th&agrave;nh m&agrave;n, vẫn như cũ che Tầm Ti&ecirc;n huyện.<br />\r\n<br />\r\nBan ng&agrave;y phi thường n&aacute;o nhiệt, chỉ l&agrave; theo vi&ecirc;n kia c&aacute;i đầu người rơi xuống đất, vạn ch&uacute;ng nh&igrave;n trừng trừng hạ bị treo đến chợ b&aacute;n thức ăn giữa đường chỗ, n&agrave;y gọi l&agrave; &quot;Chi&ecirc;u hồn c&aacute;n&quot; cọc gỗ b&ecirc;n tr&ecirc;n, cuối c&ugrave;ng vẫn l&agrave; kết th&uacute;c.<br />\r\n<br />\r\nTrừ số &iacute;t mấy vị chấp nhất vu dược d&acirc;n ch&uacute;ng, đa số người đối với n&agrave;y hơn mười vị ch&iacute; sĩ bị giết đều l&agrave; &ocirc;m chặt lấy bi phẫn th&aacute;i độ, thở d&agrave;i kh&ocirc;ng th&ocirc;i.<br />\r\n<br />\r\nĐ&aacute;ng tiếc, loạn thế kh&iacute; tượng đ&atilde; hiện, bất kể l&agrave; ai đều l&agrave; ốc c&ograve;n kh&ocirc;ng mang nổi m&igrave;nh ốc, nơi n&agrave;o có th&ecirc;̉ quản được c&aacute;i kh&aacute;c.<br />\r\n<br />\r\nV&agrave;o đ&ecirc;m về sau, chợ b&aacute;n thức ăn đường phố rất l&agrave; y&ecirc;n tĩnh.<br />\r\n<br />\r\nChỉ c&oacute; một vị lấy &aacute;o kho&aacute;c ngo&agrave;i, đ&egrave;n lồng lồng, cầm đồng la, mắt quầng th&acirc;m cực nặng tuổi trẻ canh phu, tại b&ecirc;n đường đ&aacute;nh chi&ecirc;ng, miệng h&ocirc; số canh.<br />\r\n<br />\r\nĐ&aacute;nh xong rơi canh, n&agrave;y canh phu trước mắt phương dựng thẳng gần cao mười m&eacute;t chi&ecirc;u hồn c&aacute;n.<br />\r\n<br />\r\nTheo triều đ&igrave;nh ph&aacute;p, bị trảm thủ sau c&oacute; tư c&aacute;ch treo l&ecirc;n, hoặc l&agrave; mưu nghịch loạn đảng, hoặc l&agrave; giang dương đại đạo.<br />\r\n<br />\r\nGiờ ph&uacute;t n&agrave;y, ph&iacute;a tr&ecirc;n kia treo một dải đầu l&acirc;u, ch&iacute;nh l&agrave; chạng vạng tối l&uacute;c bị chặt mười c&aacute;i người trẻ tuổi.<br />\r\n<br />\r\nMột trận gi&oacute; đ&ecirc;m qu&eacute;t tới, mười mấy khỏa đầu l&acirc;u liền cũng đi theo lắc.<br />\r\n<br />\r\nThịt c&ugrave;ng xương ma s&aacute;t cột thanh &acirc;m, nghe tới ph&aacute; lệ quỷ dị.<br />\r\n<br />\r\nCanh phu khẽ thở d&agrave;i một c&aacute;i, dưới ch&acirc;n tốc độ gia tốc, rất nhanh liền rời c&aacute;i n&agrave;y tối như mực một mảnh chợ b&aacute;n thức ăn đường phố.<br />\r\n<br />\r\nHắn vừa mới đi, cuối phố một gian cửa h&agrave;ng b&ecirc;n trong.<br />\r\n<br />\r\nTrong b&oacute;ng tối, nguy&ecirc;n bản nằm tr&ecirc;n mặt đất, nhắm chặt hai mắt một cỗ thi thể, bỗng nhi&ecirc;n mở hai mắt ra.<br />\r\n<br />\r\nVừa mới &quot;Phục sinh&quot; tới.<br />\r\n<br />\r\nĐ&agrave;o Tiềm dường như l&acirc;m v&agrave;o một loại n&agrave;o đ&oacute; cứng ngắc trạng th&aacute;i, trong đầu, đoàn kia tr&igrave; ho&atilde;n bộc ph&aacute;t tin tức lưu, đang cưỡng &eacute;p qu&aacute;n ch&uacute; tiến đến.<br />\r\n<br />\r\nBan đầu những tin tức kia lộn xộn, tối nghĩa kh&oacute; hiểu.<br />\r\n<br />\r\nDường như cổ ng&ocirc;n, lại như l&agrave; một ch&uacute;t kh&ocirc;ng thể n&agrave;o hiểu được hồ ng&ocirc;n loạn ngữ, thậm ch&iacute; l&agrave; một ch&uacute;t chữ như g&agrave; bới biểu tượng ph&ugrave; văn.<br />\r\n<br />\r\nTuy nhi&ecirc;n dần dần, ch&uacute;ng n&oacute; tự động chải vuốt gom.<br />\r\n<br />\r\nSau c&ugrave;ng, đ&uacute;ng l&agrave; h&igrave;nh th&agrave;nh một loại thuận tiện Đ&agrave;o Tiềm l&yacute; giải đặc th&ugrave; c&aacute;ch thức.<br />\r\n<br />\r\n【 ch&iacute; t&ecirc;n: Đ&agrave;o Tiềm. 】<br />\r\n<br />\r\n【 ch&iacute; loại: Qu&aacute;i dị. 】<br />\r\n<br />\r\n【 ch&iacute; thuật: Bởi v&igrave; bị trảm thủ l&uacute;c tụng niệm kh&ocirc;ng biết t&agrave;n quyết m&agrave; thu được &quot;Kh&ocirc;ng chết&quot; đặc t&iacute;nh, bởi vậy tiến v&agrave;o kh&ocirc;ng sống kh&ocirc;ng chết h&igrave;nh dạng h&igrave;nh d&aacute;ng, một ngụm nguy&ecirc;n kh&iacute; kh&ocirc;ng rời liền c&oacute; thể kh&ocirc;ng chết, cho đến lần sau tử vong l&uacute;c, cần nặng ngưng nguy&ecirc;n kh&iacute;, nếu l&agrave; thất bại th&igrave; đem ho&agrave;n to&agrave;n chết đi. 】<br />\r\n<br />\r\n【 ch&uacute; một: N&ecirc;n t&agrave;n quyết mỗi tụng niệm một lần, đều muốn trả gi&aacute; trở xuống đại giới: Th&acirc;n thể vĩnh cửu hư thối, linh hồn ng&acirc;y ng&ocirc;, phụ thể sau h&oacute;a th&agrave;nh một loại t&ecirc;n l&agrave; &quot;Hồn Thi&quot; quỷ vật. 】<br />\r\n<br />\r\n【 ch&uacute; hai: Hồn Thi người, giới thời khắc sinh tử, ngơ ngơ ng&aacute;c ng&aacute;c, th&iacute;ch ăn người, thị u &aacute;m ẩm ướt, mỗi khi gặp Huyết Nguyệt liền phi nước đại ba ng&agrave;n dặm, kh&ocirc;ng biết suy yếu. 】<br />\r\n<br />\r\n【 ch&uacute; ba: Đại giới đ&atilde; miễn trừ! 】<br />\r\n<br />\r\n...<br />\r\n<br />\r\nTi&ecirc;u h&oacute;a xong tin tức lưu, Đ&agrave;o Tiềm sửng sốt.<br />\r\n<br />\r\nRất nhiều nghi hoặc, giờ ph&uacute;t n&agrave;y giải khai.<br />\r\n<br />\r\nN&agrave;y t&agrave;n quyết, hắn v&igrave; sao bị trảm thủ lại kh&ocirc;ng chết?<br />\r\n<br />\r\nC&ograve;n c&oacute; khi đ&oacute; hắn cảm nhận được c&ugrave;ng &quot;Hư thối&quot;, &quot;Thi thể&quot; c&oacute; li&ecirc;n quan kh&iacute; tức.<br />\r\n<br />\r\nHiển nhi&ecirc;n, đ&oacute; ch&iacute;nh l&agrave; kh&ocirc;ng chết cần đại giới.<br />\r\n<br />\r\nChỉ l&agrave; kh&ocirc;ng khỏi, linh hồn của hắn tựa hồ kh&aacute;c thường, thời khắc mấu chốt va n&aacute;t kh&iacute; tức kia, để hắn c&oacute; thể miễn trừ đại giới.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Cho n&ecirc;n, c&aacute;i n&agrave;y rất c&oacute; thể ch&iacute;nh l&agrave; ta kim thủ chỉ hack, đối si&ecirc;u ph&agrave;m quỷ dị tin tức m&atilde;nh liệt cảm gi&aacute;c, c&ugrave;ng đại giới miễn trừ?&quot;<br />\r\n<br />\r\n&quot;Tuy nhi&ecirc;n vẫn cần nghiệm chứng, d&ugrave; sao chứng cứ duy nhất kh&ocirc;ng lập.&quot;<br />\r\n<br />\r\nSuy nghĩ rơi xuống, Đ&agrave;o Tiềm lấy lại tinh thần.<br />\r\n<br />\r\nChậm r&atilde;i đứng dậy, tại cửa h&agrave;ng b&ecirc;n trong lục lọi.<br />\r\n<br />\r\nNửa ng&agrave;y qua đi, hắn thắp s&aacute;ng một ngọn đ&egrave;n dầu.<br />\r\n<br />\r\nMờ nhạt &aacute;nh đ&egrave;n, dần dần lan tr&agrave;n ra, miễn cưỡng phủ k&iacute;n căn n&agrave;y nho nhỏ cửa h&agrave;ng s&aacute;ch.<br />\r\n<br />\r\nMượn &aacute;nh s&aacute;ng, hắn lại tại nơi hẻo l&aacute;nh b&ecirc;n trong t&igrave;m tới một mặt bị m&agrave;i đến cực tốt gương đồng.<br />\r\n<br />\r\nMột gi&acirc;y sau, Đ&agrave;o Tiềm thấy r&otilde; r&agrave;ng m&igrave;nh b&acirc;y giờ bộ d&aacute;ng.<br />\r\n<br />\r\nThật bất ngờ, c&aacute;i n&agrave;y đ&uacute;ng l&agrave; người trẻ tuổi.<br />\r\n<br />\r\nƯớc chừng hai lăm hai s&aacute;u ni&ecirc;n kỷ, cao gầy lại bạch, dung mạo cũng coi như anh tuấn.<br />\r\n<br />\r\nKh&oacute;e miệng c&oacute; tiếu văn, th&ecirc;m nữa c&oacute; ch&uacute;t h&iacute;p h&iacute;p mắt, tổng thể cho người ta một loại &aacute;nh nắng, ấm &aacute;p kh&iacute; tức.<br />\r\n<br />\r\nDạng n&agrave;y người, l&agrave; thế n&agrave;o chết?<br />\r\n<br />\r\nĐ&agrave;o Tiềm n&atilde;o hải vừa sinh ra vấn đề n&agrave;y, v&ocirc; c&ugrave;ng tự nhi&ecirc;n, một đo&agrave;n tr&iacute; nhớ tu&ocirc;n ra.<br />\r\n<br />\r\nTrừ cỗ th&acirc;n thể n&agrave;y cuộc đời, c&ograve;n phụ tặng một đoạn &quot;Th&ecirc; mỹ&quot; t&igrave;nh y&ecirc;u cố sự.<br />\r\n<br />\r\nNgười n&agrave;y lại cũng họ Đ&agrave;o, chữ Tri Mệnh.<br />\r\n<br />\r\nL&agrave; c&aacute;i hai mươi lăm tuổi thư sinh, nhiều lần thi rớt, th&ecirc;m nữa phụ mẫu qua đời, lại phẫn hận triều đ&igrave;nh hồ đồ, t&acirc;m t&igrave;nh buồn bực hạ dứt kho&aacute;t kh&ocirc;ng c&ograve;n chuẩn bị kiểm tra.<br />\r\n<br />\r\nPh&iacute;a trước đoạn thời gian hao hết sạch t&iacute;ch s&uacute;c, đi v&agrave;o huyện th&agrave;nh b&aacute;n đi chợ b&aacute;n thức ăn đường phố b&ecirc;n n&agrave;y sừng cửa h&agrave;ng, gian tiệm s&aacute;ch.<br />\r\n<br />\r\nLấy t&ecirc;n &quot;Th&agrave;nh Hữu tiệm s&aacute;ch&quot;, b&aacute;n c&aacute;c thức thư tịch.<br />\r\n<br />\r\nS&aacute;ch mới cổ tịch tự thiếp, c&aacute;i g&igrave; cần c&oacute; đều c&oacute;.<br />\r\n<br />\r\nTheo l&yacute; thuyết, b&aacute;n s&aacute;ch loại sự t&igrave;nh n&agrave;y rất kh&oacute; kiếm nhiều tiền, nhưng hỗn c&aacute;i ấm no n&ecirc;n cũng dễ d&agrave;ng.<br />\r\n<br />\r\nC&oacute; thể thư sinh kia l&agrave;m người cổ hủ cố chấp, mảy may t&acirc;m nh&atilde;n cũng kh&ocirc;ng c&oacute;, tự nhi&ecirc;n l&agrave; kh&ocirc;ng kiếm được tiền g&igrave;, ngược lại mấy ng&agrave;y liền thua thiệt c&aacute;i cơ sở rơi.<br />\r\n<br />\r\nCũng may tiệm s&aacute;ch c&oacute; một nữ t&iacute;nh kh&aacute;ch quen, thường xuy&ecirc;n nhẹ lời an ủi.<br />\r\n<br />\r\nMột tới hai đi, hai người xem như nh&igrave;n vừa &yacute;.<br />\r\n<br />\r\nNhưng ai ngờ tới, ng&agrave;y h&ocirc;m trước c&oacute; một nha ho&agrave;n đến đ&acirc;y truyền tin, n&oacute;i tiểu thư nh&agrave; m&igrave;nh đ&atilde; bị mụ mụ an b&agrave;i gả cho trong th&agrave;nh nhất đại gia đ&igrave;nh l&agrave;m vợ b&eacute;.<br />\r\n<br />\r\nHai người tư t&igrave;nh đ&agrave;nh phải kiếp sau lại nối tiếp, thư mời sinh ng&agrave;n vạn qu&ecirc;n ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\nNh&igrave;n đến đ&acirc;y, Đ&agrave;o Tiềm &aacute;nh mắt liếc nh&igrave;n n&agrave;y quầy h&agrave;ng mặt b&agrave;n.<br />\r\n<br />\r\nThượng diện quả nhi&ecirc;n b&agrave;y biện một c&aacute;i ly rượu kh&ocirc;ng, c&ugrave;ng một c&aacute;i c&ograve;n s&oacute;t lại một loại n&agrave;o đ&oacute; khả nghi bột phấn bọc giấy.<br />\r\n<br />\r\nHiển nhi&ecirc;n, thư sinh đối với chuyện n&agrave;y c&ugrave;ng n&agrave;y &quot;Kiếp sau&quot; hai chữ l&yacute; giải, đại kh&aacute;i ch&iacute;nh l&agrave;... Tuẫn t&igrave;nh?<br />\r\n<br />\r\n&quot;Tri Mệnh Tri Mệnh, v&igrave; c&aacute;i g&igrave; kh&ocirc;ng thử một ch&uacute;t phản kh&aacute;ng vận mệnh đ&acirc;u.&quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm thở d&agrave;i một ch&uacute;t, sau đ&oacute; n&oacute;i.<br />\r\n<br />\r\nCảm th&aacute;n một c&acirc;u, Đ&agrave;o Tiềm kh&ocirc;ng c&oacute; nghĩ nhiều nữa.<br />\r\n<br />\r\nThư sinh n&agrave;y tự m&igrave;nh kết t&aacute;nh mạng, vứt xuống một bộ thể x&aacute;c, đối Đ&agrave;o Tiềm đến n&oacute;i xem như qu&agrave; tặng.<br />\r\n<br />\r\nNếu c&oacute; cơ hội, hắn sẽ nếm thử b&aacute;o đ&aacute;p.<br />\r\n<br />\r\nĐ&agrave;o Tiềm đứng tại chỗ mấy ph&uacute;t, ch&iacute;nh c&aacute;c loại hồn linh c&ugrave;ng mới th&acirc;n thể ho&agrave;n to&agrave;n dung hợp, kh&ocirc;ng c&oacute; bất kỳ c&aacute;i g&igrave; quan ải về sau, liền bắt đầu tại s&aacute;ch n&agrave;y cửa h&agrave;ng b&ecirc;n trong đi lại.<br />\r\n<br />\r\nC&aacute;i n&agrave;y cửa h&agrave;ng vốn nhỏ, c&aacute;ch cục cũng đơn giản.<br />\r\n<br />\r\nPh&iacute;a trước l&agrave; ba h&agrave;ng l&agrave;m bằng gỗ gi&aacute; s&aacute;ch, ở giữa c&ograve;n c&oacute; c&aacute;i chồng s&aacute;ch s&agrave;n gỗ, tiếp theo l&agrave; t&iacute;nh tiền quầy h&agrave;ng.<br />\r\n<br />\r\nLại đằng sau l&agrave; c&aacute;i gian ph&ograve;ng nhỏ, d&ugrave;ng để ngủ.<br />\r\n<br />\r\nKh&ocirc;ng bao l&acirc;u, Đ&agrave;o Tiềm liền ho&agrave;n to&agrave;n quen thuộc th&acirc;n phận mới của m&igrave;nh, nh&agrave; mới chỗ.<br />\r\n<br />\r\nTrước đ&acirc;y kh&ocirc;ng l&acirc;u bị chặt đầu, lại vừa mới chuyển sinh phục sinh.<br />\r\n<br />\r\nLoại n&agrave;y sinh sinh tử tử k&iacute;ch th&iacute;ch kinh lịch, đủ để cho người kh&aacute;m ph&aacute; hồng trần.<br />\r\n<br />\r\nĐến Đ&agrave;o Tiềm nơi n&agrave;y, th&igrave; l&agrave; để hắn kh&ocirc;ng c&oacute; một tơ một h&agrave;o bối rối.<br />\r\n<br />\r\nMượn &aacute;nh đ&egrave;n trước rửa mặt một phen, lại sẽ đầy đất loạn s&aacute;ch chỉnh l&yacute; tốt, sau c&ugrave;ng Đ&agrave;o Tiềm lần theo trong đầu tr&iacute; nhớ, từ quầy h&agrave;ng trong ngăn k&eacute;o lấy ra một bản kh&ocirc;ng d&agrave;y kh&ocirc;ng tệ, c&ugrave;ng loại sổ s&aacute;ch, kỳ thật c&ugrave;ng nhật k&yacute; cũng k&eacute;m kh&ocirc;ng nhiều lắm s&aacute;ch.<br />\r\n<br />\r\nTuy nhi&ecirc;n Đ&agrave;o Tiềm c&oacute; thể kh&ocirc;ng chướng ngại xem th&acirc;n thể tr&iacute; nhớ, nhưng d&ugrave; sao kh&ocirc;ng phải tự m&igrave;nh kinh lịch, như l&agrave; gi&aacute; h&agrave;ng loại h&igrave;nh rất nhiều chi tiết đều l&agrave; mơ hồ.<br />\r\n<br />\r\nC&oacute; nhật k&yacute; sổ s&aacute;ch d&ugrave;ng để l&agrave;m s&acirc;u sắc học tập, lại thuận tiện bất qu&aacute;.<br />\r\n<br />\r\nN&oacute;i đến, nếu như th&acirc;n phận mới l&agrave; c&aacute;i ba kh&ocirc;ng nh&acirc;n vi&ecirc;n, Đ&agrave;o Tiềm nhất định ngay lập tức lẩn trốn ẩn n&uacute;p.<br />\r\n<br />\r\nNhưng b&acirc;y giờ may mắn đến c&aacute;i có th&ecirc;̉ lộ ra &aacute;nh s&aacute;ng b&ecirc;n ngo&agrave;i thể x&aacute;c, tự nhi&ecirc;n hẳn l&agrave; trước th&iacute;ch ứng d&agrave;n xếp lại.<br />\r\n<br />\r\n&quot;Thế giới n&agrave;y c&ugrave;ng ta kiếp trước triều đại n&agrave;o đ&oacute; những năm cuối rất giống, nhưng c&agrave;ng nhiều địa phương lại ho&agrave;n to&agrave;n kh&ocirc;ng giống.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng n&oacute;i trước x&atilde; hội c&aacute;ch cục, lịch sử d&acirc;n tục những n&agrave;y, vẻn vẹn những c&aacute;i kia v&ocirc; c&ugrave;ng ch&acirc;n thực y&ecirc;u ma quỷ qu&aacute;i, hiện tượng thần b&iacute;, đã đ&acirc;̀y đủ n&oacute;i r&otilde; thế giới n&agrave;y nước s&acirc;u bao nhi&ecirc;u, đến cỡ n&agrave;o hung hiểm.&quot;<br />\r\n<br />\r\n&quot;Ta mới đến, trước hất l&ecirc;n một lớp da cẩu xuống tới, mới l&agrave; sinh tồn chi đạo.&quot;<br />\r\n<br />\r\nN&oacute;i thầm hai c&acirc;u.<br />\r\n<br />\r\nSau đ&oacute;, Đ&agrave;o Tiềm tiện tay lật ra sổ s&aacute;ch.<br />\r\n<br />\r\n&quot;Thi&ecirc;n Mệnh năm thứ ch&iacute;n, m&ugrave;ng 3 th&aacute;ng 6, tinh, kh&ocirc;ng gi&oacute;.&quot;<br />\r\n<br />\r\n&quot;Ta Đ&agrave;o Tri Mệnh d&ugrave; chưa th&agrave;nh gia, cũng đ&atilde; lập nghiệp, Th&agrave;nh Hữu tiệm s&aacute;ch nay ch&iacute;nh thức khai trương, tứ phương bạn b&egrave; tụ tập, đều khẳng kh&aacute;i gi&uacute;p tiền, b&aacute;n đi tạp thư kh&aacute; nhiều.&quot;<br />\r\n<br />\r\n&quot;C&oacute; một bạn c&aacute;t sĩ lan, chọn tr&uacute;ng trong tiệm ta cố &yacute; mua &laquo; Việt Man Đường B&uacute;t K&yacute; &raquo;, cuốn s&aacute;ch n&agrave;y l&agrave; ta s&ugrave;ng k&iacute;nh nhất chi đại nho viết, s&aacute;ch tư đạt 20 nguy&ecirc;n chi cự, c&aacute;t huynh n&oacute;i hiện xấu hổ v&iacute; tiền rỗng tuếch , c&oacute; thể hay kh&ocirc;ng mượn trước đi, mấy ng&agrave;y qua trả nợ.&quot;<br />\r\n<br />\r\n&quot;Người đọc s&aacute;ch ở giữa nh&atilde; sự, c&oacute; thể n&agrave;o cự tuyệt, vui vẻ đồng &yacute; chi.&quot;<br />\r\n<br />\r\n&quot;Đ&ecirc;m đến c&ugrave;ng người kh&aacute;c bạn tụ tập đức thuận cư, ăn uống linh đ&igrave;nh, được kh&ocirc;ng kho&aacute;i hoạt, c&oacute; hai m&oacute;n ăn t&ecirc;n l&agrave; &#39;Nấm tr&uacute;c bào ngư&#39;, &#39;R&acirc;u rồng v&acirc;y c&aacute;&#39;, hương vị v&ocirc; c&ugrave;ng tốt, hỏi một ch&uacute;t đ&uacute;ng l&agrave; c&aacute;t huynh chỗ điểm, quả nhi&ecirc;n l&agrave; đại hộ nh&acirc;n gia ra c&ocirc;ng tử, sẽ ăn, chỉ l&agrave; đồ ăn gi&aacute; quả thực đắt ch&uacute;t, chỉ c&aacute;i n&agrave;y hai đạo liền cần hai nguy&ecirc;n, thật sự l&agrave; m&oacute;n ch&iacute;nh.&quot;<br />\r\n<br />\r\n&quot;H&ocirc;m nay nhập trướng: Mười nguy&ecirc;n lục gi&aacute;c bốn.&quot;<br />\r\n<br />\r\n&quot;H&ocirc;m nay chi ti&ecirc;u: Mười hai nguy&ecirc;n.&quot;<br />\r\n<br />\r\n...<br />\r\n<br />\r\n&quot;Th&aacute;ng s&aacute;u bốn, gi&oacute; nhẹ, c&oacute; mưa.&quot;<br />\r\n<br />\r\n&quot;H&ocirc;m nay kh&aacute;ch nh&acirc;n &iacute;t, nhưng mưa ph&ugrave;n rả r&iacute;ch, rất tốt thời gian, trộm đến tr&ocirc;i qua nữa ng&agrave;y nh&agrave;n nh&atilde;.&quot;<br />\r\n<br />\r\n&quot;Cũng may cũng b&aacute;n đi một quyển &laquo; Thẩm Vi&ecirc;n B&uacute;t Đ&agrave;m &raquo;, chung nhập trướng: Một nguy&ecirc;n nhị gi&aacute;c.&quot;<br />\r\n<br />\r\n&quot;Chi ti&ecirc;u chủ yếu l&agrave; tiền cơm c&ugrave;ng nghe s&aacute;ch tiền, s&aacute;t nhập chung: Tam gi&aacute;c mười c&aacute;i tiền đồng.&quot;<br />\r\n<br />\r\n...</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Th&aacute;ng s&aacute;u 5, mưa to.&quot;<br />\r\n<br />\r\n&quot;S&aacute;ng sớm vừa mở cửa liền đến tin dữ, c&oacute; một bạn b&egrave; đến cửa c&aacute;o tri, n&agrave;y c&aacute;t sĩ Lan gia đ&atilde; người đi nh&agrave; trống, nguy&ecirc;n lai c&aacute;i thằng n&agrave;y trong nh&agrave; sớm đ&atilde; quyết định dọn đi tỉnh th&agrave;nh định cư, ng&agrave;y h&ocirc;m trước nghe ta mới mở tiệm s&aacute;ch, cố &yacute; đến chỗ của ta mượn đi trong tiệm qu&yacute; nhất lớn s&aacute;ch, hạ quyết t&acirc;m lại ta sổ s&aacute;ch.&quot;<br />\r\n<br />\r\n&quot;Mượn m&agrave; kh&ocirc;ng phải l&agrave; v&igrave; tặc, c&aacute;i thằng n&agrave;y qu&aacute; mức đ&aacute;ng gh&eacute;t, lại bị ta gặp gỡ nhất định phải đ&aacute;nh nổ hắn đầu ch&oacute;.&quot;<br />\r\n<br />\r\n&quot;Qu&aacute; mức phẫn hận, cửa h&agrave;ng chỉ mở nửa ng&agrave;y.&quot;<br />\r\n<br />\r\n&quot;B&aacute;n đi một chữ thiếp, nhập trướng: T&aacute;m c&aacute;i tiền đồng.&quot;<br />\r\n<br />\r\n&quot;H&ocirc;m nay chi ti&ecirc;u người tiền cơm, ăn nhiều ch&uacute;t, chung: Hai gi&aacute;c.&quot;<br />\r\n<br />\r\n...<br />\r\n<br />\r\n&quot;Th&aacute;ng s&aacute;u s&aacute;u, kh&ocirc;ng gi&oacute; kh&ocirc;ng mưa.&quot;<br />\r\n<br />\r\n&quot;G&oacute;c đường mới mở nh&agrave; đậu hũ cửa h&agrave;ng, chủ qu&aacute;n l&agrave; vị nữ sĩ, dung mạo kh&ocirc;ng tầm thường, tư th&aacute;i cũng kh&ocirc;ng phải ph&agrave;m, c&oacute; thể xưng đậu hũ nương b&ecirc;n trong T&acirc;y Thi.&quot;<br />\r\n<br />\r\n&quot;Mua kh&aacute;ch thực tế qu&aacute; nhiều, gian nan chen v&agrave;o, chỉ mua đến mấy khối n&aacute;t đậu hũ, cũng ngọt.&quot;<br />\r\n<br />\r\n&quot;H&ocirc;m nay b&aacute;n đi mấy s&aacute;ch bản ch&eacute;p tay, nhập trướng: Hai gi&aacute;c, hai c&aacute;i tiền đồng.&quot;<br />\r\n<br />\r\n&quot;Chi ti&ecirc;u người tiền cơm, xem kịch tiền, s&aacute;t nhập chung: Bảy gi&aacute;c.&quot;<br />\r\n<br />\r\n...<br />\r\n<br />\r\n&quot;Th&aacute;ng s&aacute;u bảy, lớn tinh, Liệt Dương ch&oacute;i mắt.&quot;<br />\r\n<br />\r\n&quot;H&ocirc;m nay kh&ocirc;ng c&oacute; mấy người đến cửa mua s&aacute;ch, chỉ c&aacute;ch b&iacute;ch b&aacute;n kỳ thạch chủ qu&aacute;n tới đ&acirc;y lấy đi h&ocirc;m qua đặt trước &laquo; Tiễn Đăng T&acirc;n Thoại &raquo;, ta vội v&agrave;ng đọc qua v&agrave;i trang, tất cả đều l&agrave; ch&uacute;t ly kỳ b&iacute; ẩn, nh&acirc;n y&ecirc;u quỷ luyến sự t&igrave;nh, c&ograve;n c&oacute; thể nh&igrave;n qua.&quot;<br />\r\n<br />\r\n&quot;Chủ qu&aacute;n kia đi kh&ocirc;ng bao l&acirc;u, l&acirc;n cận đường phố một tiệm thuốc l&atilde;o bản lại tới cửa, nhờ ta đi s&aacute;ch trận mua &laquo; Ngọc L&acirc;u Xu&acirc;n &raquo;, &laquo; Cửu Vĩ Quy &raquo; c&aacute;c loại s&aacute;ch, cũng sớm cung cấp tiền mười mấy nguy&ecirc;n.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng nghĩ tới như thế dung tục chi thư, gi&aacute; cả kh&ocirc;ng &iacute;t li&ecirc;̀n th&ocirc;i, ham m&ecirc; người cũng nhiều như thế.&quot;<br />\r\n<br />\r\n&quot;Nếu ta nhiều mua một ch&uacute;t, lại b&aacute;n ra ngo&agrave;i, chẳng phải l&agrave;... Kh&ocirc;ng được kh&ocirc;ng được, ta l&agrave; người đọc s&aacute;ch, nhất định kh&ocirc;ng thể b&ocirc;i nhọ ch&iacute;nh m&igrave;nh.&quot;<br />\r\n<br />\r\n&quot;H&ocirc;m nay nhập trướng tuy nhiều, nhưng ng&agrave;y mai vẫn đến xuất ra đi mua s&aacute;ch, chung: Mười lăm nguy&ecirc;n bảy gi&aacute;c s&aacute;u &quot;<br />\r\n<br />\r\n&quot;T&acirc;m t&igrave;nh tốt lại ăn nhiều ch&uacute;t, Th&aacute;i An cư thịt d&ecirc; qu&aacute; đẹp vị, chung chi ti&ecirc;u: Lục gi&aacute;c.&quot;<br />\r\n<br />\r\n...<br />\r\n<br />\r\n&quot;Th&aacute;ng s&aacute;u ch&iacute;n , trời trong xanh.&quot;<br />\r\n<br />\r\n&quot;H&ocirc;m nay kh&aacute;ch nh&acirc;n hơi nhi&ecirc;̀u, b&aacute;n ra nhiều s&aacute;ch đồng s&aacute;ch, tự thiếp, ta l&ograve;ng rất an ủi.&quot;<br />\r\n<br />\r\n&quot;Nghe n&oacute;i đầu phố mới mở một nh&agrave; T&acirc;y Dương nh&agrave; h&agrave;ng, chủ cửa h&agrave;ng nguy&ecirc;n l&agrave; tỉnh th&agrave;nh lớn tiệm ăn học đồ, sư t&ograve;ng một c&aacute;i t&oacute;c v&agrave;ng mắt xanh L&atilde;o di nh&acirc;n. S&aacute;t v&aacute;ch kỳ thạch chủ qu&aacute;n vừa đi nếm qua, khen lớn mỡ b&ograve; b&aacute;nh m&igrave; c&ugrave;ng nổ sườn lợn r&aacute;n mỹ vị, chỉ nghe c&aacute;i n&agrave;y đơn giản th&ocirc; tục chi t&ecirc;n m&oacute;n ăn, ta l&agrave; kh&ocirc;ng tin hắn, ng&agrave;y mai ta cũng đi nếm thử c&aacute;i n&agrave;y T&acirc;y Dương quỷ s&uacute;c thức ăn.&quot;<br />\r\n<br />\r\n&quot;H&ocirc;m nay nhập trướng: Một nguy&ecirc;n nhị gi&aacute;c, s&aacute;u c&aacute;i tiền đồng.&quot;<br />\r\n<br />\r\n&quot;H&ocirc;m nay chi ti&ecirc;u: Tam gi&aacute;c.&quot;<br />\r\n<br />\r\n...<br />\r\n<br />\r\nĐ&agrave;o Tiềm quyển s&aacute;ch tr&ecirc;n tay s&aacute;ch, nh&igrave;n như sổ s&aacute;ch, k&igrave; thực canh thi&ecirc;n hướng về nhật k&yacute;.<br />\r\n<br />\r\nKhả năng bởi v&igrave; nguy&ecirc;n bản l&agrave; &quot;M&igrave;nh&quot; viết.<br />\r\n<br />\r\nNgay từ đầu c&ograve;n lạ lẫm, nhưng xem đến phần sau, Đ&agrave;o Tiềm ho&agrave;n to&agrave;n đắm ch&igrave;m v&agrave;o.<br />\r\n<br />\r\nQu&aacute; tr&igrave;nh b&ecirc;n trong, Đ&agrave;o Tiềm đối với thế giới n&agrave;y cảm gi&aacute;c xa lạ thật nhanh biến mất, ch&acirc;n thực cảm gi&aacute;c c&ugrave;ng dung nhập cảm gi&aacute;c, từng ch&uacute;t từng ch&uacute;t hiển hiện l&ecirc;n.</p>', '2021-12-01 15:00:16', 4, 1),
(304, 76, 'Thần Tiên Hà Lan Nước', '3', '<p>L&uacute;c s&aacute;ng sớm, sương m&ugrave; hỗn tạp kh&oacute;i lửa, tại Tầm Ti&ecirc;n huyện c&aacute;c nơi phi&ecirc;u đ&atilde;ng.<br />\r\n<br />\r\nC&aacute;c loại bữa s&aacute;ng quầy l&agrave; đợt thứ nhất n&aacute;o nhiệt, sau đ&oacute; l&agrave; đi chợ, mỗi c&aacute;i cửa h&agrave;ng cũng nhao nhao khai trương, cả huyện th&agrave;nh bỗng nhi&ecirc;n ti&ecirc;n hoạt.<br />\r\n<br />\r\nChợ b&aacute;n thức ăn đường phố tầm thường nhất, một nh&agrave; gọi l&agrave; &quot;Th&agrave;nh Hữu tiệm s&aacute;ch&quot; cửa h&agrave;ng b&ecirc;n trong.<br />\r\n<br />\r\nĐ&agrave;o Tiềm chậm r&atilde;i kh&eacute;p lại sổ s&aacute;ch, phun ra một ngụm t&iacute;ch đ&ecirc;m trọc kh&iacute;.<br />\r\n<br />\r\nChịu một đ&ecirc;m, xem hết ng&agrave;y h&ocirc;m đ&oacute; nhớ thể sổ s&aacute;ch.<br />\r\n<br />\r\nĐ&agrave;o Tiềm đ&atilde; đối thế giới n&agrave;y gi&aacute; h&agrave;ng c&ugrave;ng rất nhiều chi tiết c&oacute; chỗ hiểu biết, cũng kh&ocirc;ng chướng ngại ch&uacute;t n&agrave;o dung nhập th&acirc;n phận mới.<br />\r\n<br />\r\nTrực tiếp đứng dậy, trước hướng ph&iacute;a sau ngủ cửa h&agrave;ng đi một chuyến.<br />\r\n<br />\r\nTrở lại l&uacute;c trong tay th&ecirc;m ra một c&aacute;i vải xanh t&uacute;i, rộng mở miệng t&uacute;i, liền nghe đinh đinh đang đang một trận.<br />\r\n<br />\r\nNgừng l&uacute;c, quỹ diện liền lẻ loi tổng tổng nhiều mấy c&aacute;i đồng bạc, mấy c&aacute;i Ng&acirc;n Gi&aacute;c tử, c&ugrave;ng mấy chục mai tiền đồng.<br />\r\n<br />\r\nĐ&agrave;o Tiềm trước đếm một lượt, m&agrave;y nhăn lại.<br />\r\n<br />\r\nSau đ&oacute;, lại số lần thứ hai, nhăn c&agrave;ng chặt.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; lại đi số lần thứ ba, chỉ l&agrave; tr&acirc;n trọng đem những tiền bạc n&agrave;y thả lại t&uacute;i tiền, sắc mặt bất đắc dĩ thầm n&oacute;i:<br />\r\n<br />\r\n&quot;Một nh&agrave; t&iacute;ch s&uacute;c, hơn nửa th&aacute;ng liền bại kh&ocirc;ng sai biệt lắm, chỉ c&ograve;n lại t&aacute;m nguy&ecirc;n bảy gi&aacute;c, cộng th&ecirc;m năm mươi ba mai tiền đồng, thật đ&uacute;ng l&agrave; c&aacute;i bại gia tử.&quot;<br />\r\n<br />\r\n&quot;Một vi&ecirc;n tiền đồng có th&ecirc;̉ mua mười hạt tám hạt đường, hoặc l&agrave; một khối b&aacute;nh nướng cộng th&ecirc;m b&aacute;nh quẩy, lại th&ecirc;m một vi&ecirc;n, c&ograve;n c&oacute; thể lại th&ecirc;m một b&aacute;t Hồ Lạt s&uacute;p, sức mua nh&igrave;n như ho&agrave;n th&agrave;nh, c&oacute; thể kh&ocirc;ng chịu nổi trước mắt đ&acirc;y ch&iacute;nh l&agrave; tổng tư sản a.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng n&oacute;i trước b&igrave;nh thường ăn uống, quần &aacute;o, giải tr&iacute; loại h&igrave;nh chi ti&ecirc;u, ri&ecirc;ng n&agrave;y cửa h&agrave;ng một th&aacute;ng tiền thu&ecirc; cũng l&agrave; 20 nguy&ecirc;n, c&ograve;n c&oacute; mua thư tịch c&aacute;i n&agrave;y lớn nhất hạng...&quot;<br />\r\n<br />\r\n&quot;N&oacute;i c&aacute;ch kh&aacute;c, ta hiện tại phi thường ngh&egrave;o.&quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm nắm bắt t&uacute;i tiền, nhả r&atilde;nh lấy ban đầu th&acirc;n thể.<br />\r\n<br />\r\nDựa theo hiện hữu gi&aacute; h&agrave;ng t&iacute;nh to&aacute;n, hắn rất kh&oacute; đem căn n&agrave;y Th&agrave;nh Hữu tiệm s&aacute;ch duy tr&igrave; một th&aacute;ng kh&ocirc;ng đ&oacute;ng cửa.<br />\r\n<br />\r\nDuy nhất để Đ&agrave;o Tiềm cảm thấy c&oacute; chỗ an ủi l&agrave;, n&agrave;y sổ s&aacute;ch b&ecirc;n trong kỳ thật cất giấu một đầu con đường ph&aacute;t t&agrave;i.<br />\r\n<br />\r\nChỉ l&agrave; nguy&ecirc;n chủ cảm thấy c&oacute; nhục nh&atilde; nhặn, l&agrave; lấy kh&ocirc;ng c&oacute; lại tiếp tục.<br />\r\n<br />\r\n&quot;C&aacute;i gọi l&agrave; thực sắc t&iacute;nh d&atilde;, chắc bụng sau khi, lại thỏa m&atilde;n hạ đời sống tinh thần, c&aacute;i n&agrave;y kh&ocirc;ng thể b&igrave;nh thường hơn được, như thế n&agrave;o l&agrave; c&oacute; nhục nh&atilde; nhặn đ&acirc;u.&quot;<br />\r\n<br />\r\n&quot;Chờ một l&uacute;c nếu l&agrave; kh&ocirc;ng c&oacute; sinh &yacute; liền đi s&aacute;ch trận t&igrave;m xem, đem &laquo; C&aacute;ch Li&ecirc;m Hoa Ảnh &raquo;, &laquo; Kh&ocirc;ng Kh&ocirc;ng Huyễn &raquo;, &laquo; Li&ecirc;n Hương Bạn &raquo;, &laquo; Thiền Ch&acirc;n Dật Sử &raquo; c&aacute;i n&agrave;y trọn vẹn đều mua đủ, lặng lẽ tuy&ecirc;n truyền một ch&uacute;t, ta ch&iacute;nh l&agrave; đầu n&agrave;y chợ b&aacute;n thức ăn đường phố lớn nhất tịnh con.&quot;<br />\r\n<br />\r\n&quot;Thư sinh quả l&agrave; muộn tao, cảm thấy những s&aacute;ch n&agrave;y qu&aacute; tục, mặt khiển tr&aacute;ch đến cửa kh&aacute;ch nh&acirc;n, ngược lại l&agrave; lặng lẽ meo meo đem t&ecirc;n s&aacute;ch đều ghi nhật k&yacute; b&ecirc;n tr&ecirc;n.&quot;<br />\r\n<br />\r\n&quot;Quả nhi&ecirc;n, viết nhật k&yacute;, c&oacute; thể l&agrave; người đứng đắn?&quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm đem t&uacute;i tiền cất v&agrave;o trong ngực, d&ugrave;ng nhả r&atilde;nh lải nhải h&ograve;a tan m&igrave;nh xuy&ecirc;n việt sống lại sau khi mờ mịt.<br />\r\n<br />\r\nC&oacute; thể l&agrave; bởi v&igrave; đ&atilde; th&agrave;nh &quot;Kh&ocirc;ng chết qu&aacute;i dị&quot;, hoặc l&agrave; t&acirc;m t&igrave;nh khuấy động qu&aacute; mức.<br />\r\n<br />\r\nCả đ&ecirc;m kh&ocirc;ng ngủ, Đ&agrave;o Tiềm kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o bối rối.<br />\r\n<br />\r\nNghe b&ecirc;n ngo&agrave;i truyền đến c&aacute;c thức tiếng rao h&agrave;ng, Đ&agrave;o Tiềm dứt kho&aacute;t đứng dậy, rửa mặt một phen.<br />\r\n<br />\r\nMấy ph&uacute;t sau, hắn c&oacute; ch&uacute;t trịnh trọng đem cửa hộ mở ra.<br />\r\n<br />\r\nTrước mắt lập tức trời s&aacute;ng choang, n&aacute;o nhiệt tr&agrave;ng diện &ocirc;m theo m&atilde;nh liệt thực cảm gi&aacute;c, xung k&iacute;ch tới.<br />\r\n<br />\r\nCứ việc chỉ buổi s&aacute;ng t&aacute;m ch&iacute;n điểm d&aacute;ng vẻ, nhưng huyện th&agrave;nh n&agrave;y đ&atilde; l&agrave; d&ograve;ng người cuồn cuộn.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nĐ&agrave;o Tiềm trước giật m&igrave;nh mấy gi&acirc;y, chợt chậm chạp m&agrave; chăm ch&uacute;, đem ph&iacute;a trước rất nhiều h&igrave;nh ảnh đều thu nhập trong mắt m&igrave;nh:<br />\r\n<br />\r\nBốc hơi n&oacute;ng, kh&aacute;ch h&agrave;ng kh&ocirc;ng &iacute;t m&igrave; ho&agrave;nh th&aacute;nh quầy.<br />\r\n<br />\r\nĐầu ng&otilde; ng&atilde; sấp tr&ecirc;n đất, gian nan lục t&igrave;m một khối tan n&aacute;t b&aacute;nh ng&ocirc; l&atilde;o khất c&aacute;i.<br />\r\n<br />\r\nMười c&aacute;i từ chỗ hắn chạy nạn đến, s&aacute;ng sớm v&agrave;o th&agrave;nh, quần &aacute;o tả tơi, xanh xao v&agrave;ng vọt nạn d&acirc;n.<br />\r\n<br />\r\nĐầu đường g&otilde; kh&oacute;a tử thợ cắt t&oacute;c, b&aacute;n c&aacute;c thức đồ d&ugrave;ng Tiểu Phiến.<br />\r\n<br />\r\nĐang trong th&agrave;nh mạnh mẽ đ&acirc;m tới, cưỡi thử một loại t&ecirc;n l&agrave; &quot;Xe đạp&quot; dương đồ chơi con em nh&agrave; gi&agrave;u.<br />\r\n<br />\r\nC&aacute;ch đ&oacute; kh&ocirc;ng xa kiểu mới tiệm cơm lầu c&aacute;c vị tr&iacute; cạnh cửa sổ, hai vị mặc &aacute;o d&agrave;i, tay nắm ch&eacute;n c&agrave; ph&ecirc;, h&agrave;i l&ograve;ng tr&ograve; chuyện thon thả nữ sĩ.<br />\r\n<br />\r\nTừ ngo&agrave;i th&agrave;nh m&agrave; đến, mặc qu&acirc;n trang c&otilde;ng s&uacute;ng trường, cưỡi ngựa cao to, đi ngang qua đường lớn m&agrave; đi kiểu mới c&aacute;c binh sĩ.<br />\r\n<br />\r\nNgười mặc đoản đả, th&acirc;n thể cường tráng, mang theo c&aacute;c thức dị trạng vũ kh&iacute; giang hồ nh&acirc;n sĩ.<br />\r\n<br />\r\n...<br />\r\n<br />\r\n&quot;Chỉ tốt ở bề ngo&agrave;i a.&quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm &aacute;nh mắt phức tạp, cảm gi&aacute;c bất lực th&aacute;n một c&acirc;u.<br />\r\n<br />\r\nĐang muốn l&agrave;m mấy thứ g&igrave; đ&oacute;, trong bụng truyền đến một trận ục ục &acirc;m thanh.<br />\r\n<br />\r\nC&aacute;i n&agrave;y thanh &acirc;m nhắc nhở, để Đ&agrave;o Tiềm nhớ tới m&igrave;nh b&acirc;y giờ lại l&agrave;m người.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; tiến h&agrave;nh chống cự, Đ&agrave;o Tiềm cố &yacute; đi xa một ch&uacute;t, đi đường đi một chỗ kh&aacute;c, mua nhất đại t&uacute;i m&agrave;n thầu.<br />\r\n<br />\r\nVen đường đi kh&ocirc;ng nhanh, bắt chước ban đầu th&acirc;n thể loại kia cổ hủ thư sinh đi đường phong c&aacute;ch.<br />\r\n<br />\r\nLại thư trả lời cửa h&agrave;ng l&uacute;c, trong tay m&agrave;n thầu t&aacute;n đến vừa vặn chỉ c&ograve;n một c&aacute;i, liền dư &ocirc;n hai ba miếng ăn.<br />\r\n<br />\r\nS&aacute;ng sớm tự nhi&ecirc;n kh&ocirc;ng c&oacute; khả năng c&oacute; người n&agrave;o đến mua s&aacute;ch, nhưng Đ&agrave;o Tiềm cũng kh&ocirc;ng c&oacute; &yacute; định nh&agrave;n rỗi.<br />\r\n<br />\r\nV&eacute;n tay &aacute;o l&ecirc;n, bắt đầu chỉnh l&yacute; c&aacute;i n&agrave;y bố cục rối bời cửa h&agrave;ng s&aacute;ch.<br />\r\n<br />\r\nTuy nhi&ecirc;n hiện nay rất ngh&egrave;o, d&ugrave; sao cũng l&agrave; c&oacute; sản nghiệp người.<br />\r\n<br />\r\nChỉnh l&yacute; qu&aacute; tr&igrave;nh b&ecirc;n trong, Đ&agrave;o Tiềm thỉnh thoảng cầm l&ecirc;n một bản cảm thấy hứng th&uacute; s&aacute;ch, vội v&agrave;ng qu&eacute;t cướp, phần lớn l&agrave; &quot;Thần Ma ch&iacute; qu&aacute;i&quot;, &quot;Si&ecirc;u ph&agrave;m thoại bản&quot;, &quot;Quỷ dị kỷ thực&quot; loại h&igrave;nh.<br />\r\n<br />\r\nHắn &yacute; nghĩ, l&agrave; nghĩ c&oacute; thể hay kh&ocirc;ng từ đ&oacute; nh&igrave;n thấy thế giới n&agrave;y, hoặc l&agrave; n&oacute;i si&ecirc;u ph&agrave;m tu h&agrave;nh thế giới vụn vặt.<br />\r\n<br />\r\nTrải qua chặt đầu, kh&ocirc;ng chết, trọng sinh, tăng th&ecirc;m hai phần bản địa thổ d&acirc;n tr&iacute; nhớ.<br />\r\n<br />\r\nĐ&agrave;o Tiềm suy đo&aacute;n, thế giới n&agrave;y c&oacute; y&ecirc;u ma quỷ qu&aacute;i thần ti&ecirc;n, đại kh&aacute;i dẫn đầu cũng c&oacute; nh&acirc;n loại người tu h&agrave;nh.<br />\r\n<br />\r\nHắn hiện giai đoạn mục ti&ecirc;u l&agrave; sống y&ecirc;n phận, trước hảo hảo sống s&oacute;t.<br />\r\n<br />\r\nNếu c&oacute; hiểu biết ch&acirc;n tướng, chạm đến si&ecirc;u ph&agrave;m cơ hội, hắn cũng kh&ocirc;ng c&oacute; &yacute; định bỏ qua.<br />\r\n<br />\r\nChẳng qua đ&aacute;ng tiếc, bận rộn nửa giờ về sau, hắn kh&ocirc;ng c&oacute; bất kỳ ph&aacute;t hiện n&agrave;o.<br />\r\n<br />\r\nRất b&igrave;nh thường, lẽ thường m&agrave; n&oacute;i, tu ti&ecirc;n cũng tốt, y&ecirc;u ma cũng tốt, c&oacute; lẽ sẽ c&oacute; bộ phận c&ugrave;ng nh&acirc;n loại hỗn hợp, nhưng ở giữa tất nhi&ecirc;n sẽ tồn tại một tầng ngăn c&aacute;ch.<br />\r\n<br />\r\nNgười binh thường cho d&ugrave; hữu t&acirc;m đi t&igrave;m, cũng chưa chắc có th&ecirc;̉ toại nguyện.<br />\r\n<br />\r\nPhương diện n&agrave;y, coi trọng c&aacute;i gọi l&agrave; cơ duy&ecirc;n.<br />\r\n<br />\r\nĐương nhi&ecirc;n, vất vả nửa giờ cũng l&agrave; c&oacute; ch&uacute;t thu hoạch, ch&iacute; &iacute;t Đ&agrave;o Tiềm ho&agrave;n to&agrave;n thăm d&ograve; nh&agrave; m&igrave;nh cửa h&agrave;ng s&aacute;ch.<br />\r\n<br />\r\nC&aacute;i n&agrave;y nho nhỏ cửa h&agrave;ng, lấy trường dạy vỡ l&ograve;ng thư tịch, thư ph&aacute;p tự thiếp l&agrave;m chủ, lợi nhuận cũng nhiều đến từ n&agrave;y một loại.<br />\r\n<br />\r\nQuyển s&aacute;ch kh&aacute;c, c&aacute;i g&igrave; du k&yacute; tạp đ&agrave;m, tiểu thuyết thoại bản, địa đồ ho&agrave;ng lịch v&acirc;n v&acirc;n, đều bị ban đầu th&acirc;n thể qu&ecirc;n nhập &quot;Tạp thư&quot; thuộc loại.<br />\r\n<br />\r\nĐ&aacute;ng gi&aacute; khen ngợi l&agrave;, ban đầu th&acirc;n thể mua, to&agrave;n bộ đều l&agrave; ch&iacute;nh bản s&aacute;ch.<br />\r\n<br />\r\nĐương nhi&ecirc;n, kia đại kh&aacute;i cũng l&agrave; hắn thua thiệt sạch của cải nguy&ecirc;n nh&acirc;n một trong.<br />\r\n<br />\r\nSau khi thu thập xong, Đ&agrave;o Tiềm bắt đầu kinh doanh.<br />\r\n<br />\r\nKh&ocirc;ng biết c&oacute; phải hay kh&ocirc;ng l&agrave; thay c&aacute;i hồn, cũng bởi vậy chuyển vận.<br />\r\n<br />\r\nCho tới trưa, Th&agrave;nh Hữu tiệm s&aacute;ch sinh &yacute; tuy nhi&ecirc;n kh&ocirc;ng bằng thập tự nhai miệng n&agrave;y mấy nh&agrave;, nhưng lui tới Đ&agrave;o Tiềm cũng tiếp đ&atilde;i kh&ocirc;ng &iacute;t người.<br />\r\n<br />\r\nĐến buổi trưa, Đ&agrave;o Tiềm rốt cục kh&ocirc;ng xuống tới, mang theo đem b&agrave;n t&iacute;nh ở nơi đ&oacute; lốp bốp một hồi lay.<br />\r\n<br />\r\n&quot;Chung b&aacute;n ra mười bản s&aacute;ch, s&aacute;u bản &laquo; đối tướng bốn n&oacute;i &raquo;, hai s&aacute;ch tự thiếp, một bản ho&agrave;ng lịch, c&ograve;n c&oacute; bản &laquo; Chu c&ocirc;ng du k&yacute; &raquo;, chung nhập trướng: Một nguy&ecirc;n nhị gi&aacute;c.&quot;<br />\r\n<br />\r\n&quot;Đ&ocirc;i kia tướng bốn n&oacute;i nghe n&oacute;i l&agrave; c&aacute;i n&agrave;o đ&oacute; trường tư l&atilde;o sư y&ecirc;u cầu dạy học phụ t&agrave;i, quả nhi&ecirc;n mặc kệ tại thời đại n&agrave;o, hay l&agrave; b&aacute;n t&agrave;i liệu giảng dạy tương đối kiếm tiền, đ&aacute;ng tiếc ta xuy&ecirc;n việt l&uacute;c kh&ocirc;ng c&oacute; đem ho&agrave;ng cương vị b&iacute; s&aacute;ch mang đến.&quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm ch&iacute;nh n&oacute;i thầm.<br />\r\n<br />\r\nBỗng nhi&ecirc;n, nơi cửa bỗng nhi&ecirc;n truyền đến &quot;Băng x&ugrave;y&quot; một loại thanh &acirc;m quen thuộc.<br />\r\n<br />\r\nNgẩng đầu một c&aacute;i, Đ&agrave;o Tiềm liền gặp hai th&acirc;n ảnh, c&aacute;c mang theo c&aacute;i g&igrave;, cười nhẹ nh&agrave;ng đứng tại cửa ra v&agrave;o.<br />\r\n<br />\r\nHai người n&agrave;y nh&igrave;n đều l&agrave; chừng ba mươi tuổi, nghi&ecirc;m chỉnh Thanh trung ni&ecirc;n.<br />\r\n<br />\r\nMột người mặc cổ tr&ograve;n trường sam, mang theo đỉnh Thanh nhung ấm mũ, ngũ quan đoan ch&iacute;nh, con mắt nhỏ, một mặt kh&ocirc;n kh&eacute;o, cực gi&ocirc;́ng L&acirc;m Vĩnh Kiện.<br />\r\n<br />\r\nMột c&aacute;i kh&aacute;c l&agrave; trường b&agrave;o &aacute;o kho&aacute;c ngo&agrave;i, s&uacute;c lấy r&acirc;u h&igrave;nh chử b&aacute;t, nh&igrave;n c&oacute; ch&uacute;t gian tr&aacute;.<br />\r\n<br />\r\nĐ&agrave;o Tiềm n&atilde;o hải, tương ứng tr&iacute; nhớ nổi l&ecirc;n.<br />\r\n<br />\r\nXảo, hai người đều l&agrave; h&agrave;ng x&oacute;m của hắn, tối h&ocirc;m qua Đ&agrave;o Tiềm c&ograve;n nh&igrave;n qua c&oacute; quan hệ hai người văn tự ghi ch&eacute;p.<br />\r\n<br />\r\nMua &laquo; Tiễn Đăng Dạ Thoại &raquo; kỳ thạch chủ tiệm L&yacute; Tam Ngư.<br />\r\n<br />\r\nCầu mua &laquo; Ngọc L&acirc;u Xu&acirc;n &raquo; c&aacute;c loại s&aacute;ch tiệm thuốc l&atilde;o bản Giả Cường.<br />\r\n<br />\r\n&quot;Đ&agrave;o thư sinh, cho tới trưa l&agrave;m ăn kh&aacute; khẩm a.&quot;<br />\r\n<br />\r\n&quot;Ta c&oacute; thể nh&igrave;n thấy, tới tới lui lui cũng l&agrave;m th&agrave;nh v&agrave;i chục lần, đ&aacute;ng gi&aacute; ăn mừng một trận.&quot;<br />\r\n<br />\r\n&quot;Ch&uacute;ng ta c&aacute;i n&agrave;y kh&ocirc;ng liền đến a, Đ&agrave;o thư sinh kh&ocirc;ng ăn cơm trưa đi, hai anh em ch&uacute;ng ta trước hết mời ngươi một lần, lần sau ngươi đến bổ sung.&quot;<br />\r\n<br />\r\n&quot;Tuy nhi&ecirc;n c&aacute;i n&agrave;y cắn người miệng mềm, thư sinh c&oacute; rảnh hay l&agrave; đến lại gi&uacute;p ch&uacute;ng ta ch&acirc;n chạy, thập đại cấm thư hai anh em ch&uacute;ng ta đều kh&ocirc;ng thu thập đầy đủ đ&acirc;u.&quot;<br />\r\n<br />\r\n&quot;Hắc hắc, kh&ocirc;ng sai kh&ocirc;ng sai.&quot;<br />\r\n<br />\r\nHai người n&agrave;y giống như l&agrave; giảng tướng thanh đồng dạng, vừa n&oacute;i, một b&ecirc;n ho&agrave;n to&agrave;n kh&ocirc;ng kh&aacute;ch kh&iacute; đi v&agrave;o trong tới.<br />\r\n<br />\r\nC&aacute;c chuyển c&aacute;i b&agrave;n nhỏ, tại thấp tr&ecirc;n s&agrave;n gỗ thu thập ra một mảnh đất.<br />\r\n<br />\r\nĐem ri&ecirc;ng ph&acirc;̀n mình mang tới thịt b&ograve; kho, nổ đậu phộng c&aacute;c loại ăn uống mang l&ecirc;n.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nM&agrave; l&uacute;c n&agrave;y, Đ&agrave;o Tiềm lực ch&uacute; &yacute; lại đều rơi v&agrave;o hai người mang tới ba con tr&ograve;n vo b&igrave;nh thủy tinh b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nTrong đ&oacute; một b&igrave;nh đ&atilde; mở đ&oacute;ng, b&ecirc;n trong c&oacute; m&agrave;u xanh nhạt trong suốt chất lỏng, kh&ocirc;ng qu&aacute; r&otilde; r&agrave;ng bọt kh&iacute; đang ừng ực l&ecirc;n.<br />\r\n<br />\r\nQu&aacute;i dị cảm gi&aacute;c quen thuộc lập tức vọt tới, mập trạch kho&aacute;i lạc... Mấy chữ lập tức liền muốn đến b&ecirc;n miệng.<br />\r\n<br />\r\nThấy Đ&agrave;o Tiềm &quot;Hiếu k&igrave;&quot; d&aacute;ng vẻ, L&yacute; Tam Ngư c&ugrave;ng Giả Cường l&uacute;c n&agrave;y liếc nhau, c&aacute;c nh&iacute;u nh&iacute;u m&agrave;y, vẻ đắc &yacute; hiển hiện ra.<br />\r\n<br />\r\nN&agrave;y cực gi&ocirc;́ng L&acirc;m Vĩnh Kiện L&yacute; Tam Ngư chỉ chỉ b&igrave;nh thủy tinh, đắc &yacute; n&oacute;i:<br />\r\n<br />\r\n&quot;Kiến thức rộng r&atilde;i Đ&agrave;o thư sinh mộng bức đi, n&agrave;y gọi l&agrave; C&aacute; Dương đồ chơi.&quot;<br />\r\n<br />\r\n&quot;Gọi đồ bỏ &#39;H&agrave; Lan nước&#39;, nghe n&oacute;i l&agrave; từ T&acirc;y Dương quỷ s&uacute;c nơi đ&oacute; truyền tới, tỉnh th&agrave;nh Đại Thương năm ngo&aacute;i khai gia nh&agrave; xưởng sản xuất c&aacute;i n&agrave;y, kh&ocirc;ng c&oacute; mấy th&aacute;ng liền vang dội to&agrave;n bộ tỉnh th&agrave;nh, l&ecirc;n tới quan lại quyền qu&yacute;, hạ đến người b&igrave;nh thường, cũng nhịn kh&ocirc;ng được nếm thử tươi.&quot;<br />\r\n<br />\r\n&quot;V&agrave;i ng&agrave;y trước ph&acirc;n xưởng tại ta Tầm Ti&ecirc;n huyện mở, h&ocirc;m nay ch&iacute;nh thức th&agrave;nh lập, tuy nhi&ecirc;n tạm thời chỉ cung ứng những c&aacute;i kia cấp cao kh&aacute;ch sạn c&ugrave;ng qu&yacute; nh&acirc;n.&quot;<br />\r\n<br />\r\n&quot;Hai anh em ch&uacute;ng ta nh&igrave;n ngươi mấy ng&agrave;y nay t&acirc;m t&igrave;nh kh&ocirc;ng tốt lắm, cố &yacute; t&igrave;m Đức Thuận cư l&atilde;o bản hỗ trợ cầm ba b&igrave;nh.&quot;<br />\r\n<br />\r\n&quot;Mau tới đ&acirc;y nếm thử, uống qua đều n&oacute;i c&aacute;i đồ chơi n&agrave;y vừa xuống bụng ứa ra kh&iacute;, kho&aacute;i hoạt giống như thần ti&ecirc;n, gọi l&agrave; một c&aacute;i c&agrave;ng hăng.&quot;<br />\r\n<br />\r\n...<br />\r\n<br />\r\nĐ&agrave;o Tiềm l&uacute;c đầu đối hai người &quot;Như quen thuộc&quot; biểu hiện c&oacute; ch&uacute;t kh&ocirc;ng th&iacute;ch ứng, tuy nhi&ecirc;n theo đối thoại tiếp tục, hắn rất nhanh cũng l&agrave; quen thuộc.<br />\r\n<br />\r\nBan đầu th&acirc;n thể tr&iacute; nhớ, v&agrave;o l&uacute;c n&agrave;y c&oacute; hiệu quả.<br />\r\n<br />\r\nĐ&agrave;o thư sinh cổ hủ cố chấp, kh&ocirc;ng biết biến b&aacute;o, tiệm s&aacute;ch vừa th&agrave;nh lập liền bị hố thảm, bởi vậy nh&igrave;n ra kh&ocirc;ng c&oacute; g&igrave; thật bằng hữu, kh&ocirc;ng n&oacute;i l&agrave; hồ bằng cẩu hữu, nhất định cũng l&agrave; kh&ocirc;ng đi t&acirc;m.<br />\r\n<br />\r\nNgược lại l&agrave; c&ugrave;ng hai vị l&acirc;n cận chủ tiệm, bởi v&igrave; một ch&uacute;t diễm tục cấm thư dần dần quen thuộc.<br />\r\n<br />\r\nHai người hơi lớn tuổi ban đầu th&acirc;n thể, đều xem như kh&ocirc;n kh&eacute;o tiểu thương nh&acirc;n, đều c&oacute; thể nh&igrave;n ra Đ&agrave;o thư sinh l&agrave; c&aacute;i lương thiện đ&aacute;ng ngưỡng mộ người, thường xuy&ecirc;n sẽ chiếu cố một hai.<br />\r\n<br />\r\nĐương nhi&ecirc;n, lại như thế n&agrave;o cũng kh&ocirc;ng phải th&acirc;n nh&acirc;n, kh&ocirc;ng c&aacute;ch n&agrave;o tại ph&ugrave; hợp l&uacute;c ngăn cản ban đầu th&acirc;n thể tự m&igrave;nh kết.<br />\r\n<br />\r\nNghe L&yacute; Tam Ngư, Đ&agrave;o Tiềm theo ban đầu th&acirc;n thể tr&iacute; nhớ, rất tự nhi&ecirc;n biểu hiện ra nghi hoặc.<br />\r\n<br />\r\nTự nhi&ecirc;n hơn, đ&ocirc;i mắt b&ecirc;n trong lộ ra hiếu k&igrave;, kh&ocirc;ng phục.<br />\r\n<br />\r\nCầm trong tay b&agrave;n t&iacute;nh n&eacute;m một c&aacute;i, trực tiếp đi qua, cầm lấy n&agrave;y b&igrave;nh thủy tinh, đầu ti&ecirc;n l&agrave; n&oacute;i ra:<br />\r\n<br />\r\n&quot;Kho&aacute;i hoạt giống như thần ti&ecirc;n? Ta kh&ocirc;ng tin.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải liền l&agrave; một b&igrave;nh nước a?&quot;<br />\r\n<br />\r\nN&oacute;i xong, Đ&agrave;o Tiềm rất l&agrave; thuần thục đem n&agrave;y miệng b&igrave;nh hướng miệng b&ecirc;n trong một r&oacute;t.<br />\r\n<br />\r\nTấn tấn tấn!<br />\r\n<br />\r\nNấc!<br />\r\n<br />\r\nMột hơi, cả b&igrave;nh xử l&yacute;.<br />\r\n<br />\r\nVốn l&agrave; c&ograve;n ch&uacute;t ưu sầu sắc mặt, một ch&uacute;t gi&atilde;n ra.<br />\r\n<br />\r\nĐ&agrave;o Tiềm nhếch miệng l&ecirc;n nụ cười, đối ch&acirc;n ch&iacute;nh mộng bức hai người dựng thẳng giơ ng&oacute;n tay c&aacute;i, sau đ&oacute; dễ chịu n&oacute;i: &quot;Thần ti&ecirc;n kh&ocirc;ng thần ti&ecirc;n ta kh&ocirc;ng biết, tuy nhi&ecirc;n a, x&aacute;c thực c&agrave;ng hăng.&quot;</p>', '2021-12-01 15:00:54', 4, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(305, 76, 'Sách Thuyền Cùng Câu Cá', '4', '<p>Ch&iacute;nh Đ&agrave;o Tiềm cũng kh&ocirc;ng nghĩ tới, tại c&aacute;i n&agrave;y xa lạ dị thế giới, v&acirc;̣y mà cũng có th&ecirc;̉ lần nữa cảm nhận được mập trạch kho&aacute;i lạc.<br />\r\n<br />\r\nTuy nhi&ecirc;n lời n&oacute;i thật đ&aacute;nh gi&aacute;, c&aacute;i n&agrave;y gọi l&agrave; &quot;H&agrave; Lan nước&quot; nước ngọt, hương vị hay l&agrave; kh&ocirc;ng bằng Coca Cola, đại kh&aacute;i tương đương với chanh vị nước ngọt.<br />\r\n<br />\r\nVề phần trăm sự t&igrave;nh?<br />\r\n<br />\r\nM&oacute;n đồ kia, c&oacute; thể uống a?<br />\r\n<br />\r\nĐ&agrave;o Tiềm t&acirc;m t&igrave;nh vui vẻ nhả r&atilde;nh, d&ugrave; sao thế giới n&agrave;y cũng kh&ocirc;ng c&oacute; người c&ugrave;ng hắn tranh.<br />\r\n<br />\r\nHắn cũng kh&ocirc;ng c&oacute; lại xoắn xu&yacute;t thế giới n&agrave;y c&ugrave;ng kiếp trước n&agrave;y triều đại n&agrave;o đ&oacute; những năm cuối v&igrave; sao như vậy tương tự, nhưng lại c&oacute; đại lượng chi tiết ho&agrave;n to&agrave;n kh&aacute;c biệt.<br />\r\n<br />\r\nKh&ocirc;ng n&oacute;i c&aacute;i kh&aacute;c, vẻn vẹn lịch sử một hạng, liền để Đ&agrave;o Tiềm hai mắt sờ m&ugrave;.<br />\r\n<br />\r\nHắn mặc d&ugrave; l&agrave; c&aacute;i lịch sử m&ugrave;, nhưng d&aacute;m khẳng định kiếp trước l&agrave; kh&ocirc;ng tồn tại một c&aacute;i gọi l&agrave; &quot;Trường Sinh Thi&ecirc;n Tri&ecirc;u&quot; ng&agrave;n năm vương triều.<br />\r\n<br />\r\nHiện thế thời cuộc cực kỳ hỗn loạn phức tạp, c&aacute;i n&agrave;y gọi l&agrave; Thi&ecirc;n Tri&ecirc;u triều đ&igrave;nh d&ugrave; c&oacute; được to lớn bản đồ, b&ecirc;n ngo&agrave;i nh&igrave;n rất cường đại, nhưng kỳ thật đ&atilde; sinh tuổi gi&agrave; kh&iacute; tượng.<br />\r\n<br />\r\nNgo&agrave;i c&oacute; v&ocirc; số cường địch v&acirc;y quanh, b&ecirc;n trong lại l&agrave; qu&acirc;n phiệt nổi l&ecirc;n bốn ph&iacute;a, c&aacute;t cứ một phương.<br />\r\n<br />\r\nC&agrave;ng th&ecirc;m c&oacute; ng&agrave;y tai nh&acirc;n họa, c&aacute;c loại &ocirc;n dịch tứ ngược.<br />\r\n<br />\r\nĐ&agrave;o Tiềm rất muốn nhiều hiểu biết ch&uacute;t chi tiết, c&ugrave;ng hai vị bản địa thổ d&acirc;n tr&ograve; chuyện, kh&ocirc;ng thể nghi ngờ l&agrave; c&aacute;i cơ hội tốt.<br />\r\n<br />\r\nBất qu&aacute; với nhiều lời nhiều sai, Đ&agrave;o Tiềm kh&ocirc;ng c&oacute; &yacute; định qu&aacute; tận lực dẫn đạo đề t&agrave;i.<br />\r\n<br />\r\nChỉ đ&oacute;ng vai tốt ban đầu th&acirc;n thể loại kia t&iacute;ch chữ như v&agrave;ng phong c&aacute;ch, ngẫu nhi&ecirc;n chen một c&acirc;u, dẫn hướng m&igrave;nh muốn đạt được tin tức l&agrave; đủ.<br />\r\n<br />\r\nAi ngờ b&ecirc;n n&agrave;y hắn vừa hạ quyết t&acirc;m giật hạ, hai người kia trực tiếp đem đầu m&acirc;u nhắm ngay Đ&agrave;o Tiềm.<br />\r\n<br />\r\nDường như cảm thấy quan hệ đến, cũng hoặc l&agrave; thật lo lắng.<br />\r\n<br />\r\nN&agrave;y phảng phất L&acirc;m Vĩnh Kiện b&agrave;o đệ kỳ thạch chủ tiệm L&yacute; Tam Ngư ăn phiến thịt b&ograve; kho, nuốt xuống bụng về sau, mắt nhỏ s&aacute;ng rực nh&igrave;n về ph&iacute;a Đ&agrave;o Tiềm, rất l&agrave; chăm ch&uacute; hỏi:<br />\r\n<br />\r\n&quot;Đ&agrave;o thư sinh, ta nh&igrave;n ngươi h&ocirc;m nay cho tới trưa đều tại nghi&ecirc;m t&uacute;c b&aacute;n s&aacute;ch, c&ugrave;ng ng&agrave;y xưa bại hoại qua loa ho&agrave;n to&agrave;n kh&aacute;c biệt, kh&ocirc;ng phải l&agrave; dự định hảo hảo sinh hoạt &agrave; nha?&quot;<br />\r\n<br />\r\n&quot;Nếu thật sự l&agrave; như thế, ta ngược lại l&agrave; c&oacute; mấy c&aacute;i l&agrave;m ăn tiểu khiếu m&ocirc;n c&oacute; thể chia sẻ cho ngươi.&quot;<br />\r\n<br />\r\n&quot;Cũng kh&ocirc;ng thu ngươi qu&aacute; nhiều tiền, Th&aacute;i An cư một hồi thịt d&ecirc; nồi lẩu liền c&oacute; thể trấn an ca ca dạ d&agrave;y.&quot;<br />\r\n<br />\r\nB&ecirc;n n&agrave;y L&yacute; Tam Ngư vỗ c&aacute;i bụng vừa n&oacute;i xong, một mặt gian tr&aacute; Giả Cường bỗng nhi&ecirc;n cũng cười.<br />\r\n<br />\r\nThần sắc của hắn, th&igrave; l&agrave; mang nhiều lấy mấy phần tr&ecirc;u chọc, v&ecirc; l&ecirc;n một b&ocirc;ng hoa sinh n&eacute;m v&agrave;o trong miệng, sau đ&oacute; n&oacute;i:<br />\r\n<br />\r\n&quot;Xảo kh&ocirc;ng phải, L&yacute; l&atilde;o bản c&oacute; kiếm tiền tiểu khiếu m&ocirc;n, ta cũng c&oacute; mấy c&aacute;i, dứt kho&aacute;t c&ugrave;ng một chỗ đưa cho Đ&agrave;o thư sinh.&quot;<br />\r\n<br />\r\n&quot;Tuy nhi&ecirc;n ca ca ta kh&ocirc;ng thế n&agrave;o tốt ăn uống chi dục, c&agrave;ng muốn no bụng nh&igrave;n một lần cho thỏa, ta nghe n&oacute;i tỉnh th&agrave;nh b&ecirc;n kia đ&atilde; bắt đầu lưu h&agrave;nh một loại thải hiệt quảng c&aacute;o, có th&ecirc;̉ v&ocirc; c&ugrave;ng thần kỳ đem c&aacute;c loại sắc th&aacute;i in ấn đến tuy&ecirc;n truyền trang đi l&ecirc;n.&quot;<br />\r\n<br />\r\n&quot;Đ&aacute;m kia đại thương hội chơi a, nghe n&oacute;i có th&ecirc;̉ trực tiếp đem những c&aacute;i kia mỹ lệ kiều diễm &#39;Lớn nh&acirc;n vật&#39; vẽ đi l&ecirc;n, c&ugrave;ng ch&acirc;n nh&acirc;n giống nhau như đ&uacute;c, thậm ch&iacute; canh c&oacute; phong vận.&quot;<br />\r\n<br />\r\n&quot;Liền n&oacute;i c&aacute;i n&agrave;y &#39;Quan sinh b&agrave;i H&agrave; Lan nước&#39; đi, n&agrave;y đại l&atilde;o bản v&acirc;̣y mà mời đến Ho&agrave;ng Nguyệt Anh khi đồ bỏ người ph&aacute;t ng&ocirc;n, c&ograve;n cho vẽ mười t&aacute;m kh&aacute;c biệt tư thế tuy&ecirc;n truyền trang, ta nghe tỉnh th&agrave;nh bằng hữu h&igrave;nh dung, nước mỹ gọi l&agrave; một c&aacute;i tư oa nổi l&ecirc;n a.&quot;<br />\r\n<br />\r\n&quot;Ta Tầm Ti&ecirc;n huyện tuy c&oacute; bến cảng, ở b&ecirc;n tr&aacute;i gần mười c&aacute;i trong huyện cũng coi l&agrave; phồn hoa, nhưng đến c&ugrave;ng so kh&ocirc;ng tỉnh th&agrave;nh, loại kia đồ chơi c&ograve;n kh&ocirc;ng c&oacute; truyền đến ch&uacute;ng ta chỗ n&agrave;y, qu&aacute; l&agrave;m cho ta tiếc nuối.&quot;<br />\r\n<br />\r\n&quot;Tuy nhi&ecirc;n a, cũng kh&ocirc;ng phải kh&ocirc;ng c&oacute; cơ hội.&quot;<br />\r\n<br />\r\n&quot;Ta nghe n&oacute;i c&oacute; kh&ocirc;ng &iacute;t tiệm s&aacute;ch đem những c&aacute;i kia tuy&ecirc;n truyền trang, hoạ b&aacute;o tổng thể sổ b&aacute;n, ch&agrave;o gi&aacute; tuy cao ngang, nhưng gi&aacute; trị tuyệt đối.&quot;<br />\r\n<br />\r\n&quot;Chờ một l&uacute;c n&agrave;y s&aacute;ch trận liền muốn mở, ta kh&ocirc;ng c&oacute; &#39;S&aacute;ch bằng&#39; b&ecirc;n tr&ecirc;n chẳng nhiều mấy chiếc s&aacute;ch lớn thuyền, Đ&agrave;o thư sinh d&ugrave; sao cũng muốn đi nhập h&agrave;ng, vừa vặn vất vả hạ, gi&uacute;p ca ca ta vơ v&eacute;t một phen, c&oacute; Ho&agrave;ng Nguyệt Anh tốt nhất, nếu như c&ograve;n c&oacute; c&aacute;i g&igrave; b&aacute;ch phi phượng, l&yacute; ch&acirc;u ch&acirc;u, Ph&ugrave;ng h&agrave;nh, mặt m&agrave;y cho... Ta cũng sẽ kh&ocirc;ng gh&eacute;t bỏ a, c&agrave;ng nhiều c&agrave;ng tốt.&quot;<br />\r\n<br />\r\nNhững lời n&agrave;y n&oacute;i xong, kh&ocirc;ng chỉ l&agrave; Đ&agrave;o Tiềm, b&ecirc;n h&ocirc;ng L&yacute; Tam Ngư cũng hướng c&aacute;i thằng n&agrave;y n&eacute;m đi một đạo khinh bỉ &aacute;nh mắt.<br />\r\n<br />\r\nSau đ&oacute;, quay đầu đối Đ&agrave;o Tiềm b&ugrave; một c&acirc;u n&oacute;i: &quot;Kh&ocirc;ng ch&ecirc; vất vả, gi&uacute;p ca ca cũng vơ v&eacute;t một phần, s&aacute;ch tư kh&ocirc;ng l&agrave; vấn đề, ca ca l&agrave; c&oacute; tiền.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nHai c&aacute;i L&atilde;o sắc... Đ&agrave;o Tiềm nuốt xuống đến b&ecirc;n miệng v&ocirc; t&igrave;nh nhả r&atilde;nh.<br />\r\n<br />\r\nĐ&aacute;y l&ograve;ng nhịn kh&ocirc;ng được cảm th&aacute;n, ni&ecirc;n đại n&agrave;y liền bắt đầu c&oacute; người ph&aacute;t ng&ocirc;n, nữ minh tinh hiệu ứng a.<br />\r\n<br />\r\nKhinh bỉ th&igrave; khinh bỉ, Đ&agrave;o Tiềm kỳ thật cũng muốn... Khụ khụ, d&ugrave; sao c&ugrave;ng trắng b&oacute;ng, s&aacute;ng l&oacute;ng l&aacute;nh đồng bạc kh&ocirc;ng c&oacute; th&ugrave; g&igrave; o&aacute;n niệm.<br />\r\n<br />\r\nC&aacute;i n&agrave;y hai l&atilde;o ca thoạt nh&igrave;n l&agrave; thật c&ugrave;ng ban đầu th&acirc;n thể quan hệ kh&ocirc;ng tệ, thấy hắn tỉnh lại, liền đều nghĩ chăm ch&uacute; chỉ điểm một hai.<br />\r\n<br />\r\nSuy nghĩ chỉ đi dạo, Đ&agrave;o Tiềm bắt chước thư sinh trực tiếp đứng dậy, đối hai người l&agrave;m c&aacute;i v&aacute;i ch&agrave;o, rất l&agrave; ch&acirc;n th&agrave;nh n&oacute;i: &quot;Mời hai vị ca ca kh&ocirc;ng tiếc chỉ điểm, thư sinh v&ocirc; c&ugrave;ng cảm k&iacute;ch.&quot;<br />\r\n<br />\r\nThấy Đ&agrave;o Tiềm lộ ra c&aacute;i n&agrave;y tư th&aacute;i, hai người t&acirc;m t&igrave;nh đều rất tốt.<br />\r\n<br />\r\nHay l&agrave; n&agrave;y L&yacute; Tam Ngư, l&ocirc;i k&eacute;o Đ&agrave;o Tiềm ngồi xuống, điểm điểm b&agrave;n nhỏ sau n&oacute;i:<br />\r\n<br />\r\n&quot;Đ&agrave;o thư sinh, tuy n&oacute;i ta am hiểu nhất ch&iacute;nh l&agrave; b&aacute;n thạch đầu, nhưng c&aacute;i gọi l&agrave; mua b&aacute;n, kỳ thật cũng liền những chuyện kia, ngươi ki&ecirc;n tr&igrave; ch&iacute;nh bản thư tịch, cho n&ecirc;n những c&aacute;i kia ch&acirc;n ch&iacute;nh có th&ecirc;̉ kiếm tiền lại nhẹ nh&otilde;m chi&ecirc;u số, ta liền kh&ocirc;ng n&oacute;i với ngươi.&quot;<br />\r\n<br />\r\n&quot;Ngươi b&acirc;y giờ lớn nhất n&ecirc;n l&agrave;m, l&agrave; mở r&ocirc;̣ng nguồn ti&ecirc;u thụ, ngươi chỉ ở trong tiệm ngồi ti&ecirc;u l&agrave; kh&ocirc;ng được, qu&aacute; ngu.&quot;<br />\r\n<br />\r\n&quot;Ngươi nh&igrave;n đường lớn b&ecirc;n tr&ecirc;n thế dụ đường, lộng lẫy đường hai nh&agrave;, người trừ trong tiệm, c&ograve;n c&ugrave;ng trong th&agrave;nh quan học, c&aacute;c tộc trường tư c&oacute; quan hệ, trực tiếp l&ocirc;i k&eacute;o tr&ecirc;n s&aacute;ch m&ocirc;n b&aacute;n, nghe n&oacute;i c&ograve;n nhớ hơn ph&acirc;n nửa người đọc s&aacute;ch, đại nho, l&atilde;o học cứu trong nh&agrave; địa chỉ, c&oacute; hàng mới b&iacute; s&aacute;ch đều l&agrave; trực tiếp ph&aacute;i hỏa kế đến cửa chào hàng.&quot;<br />\r\n<br />\r\n&quot;Ngươi kh&ocirc;ng qu&aacute; mức quan hệ, lại ngượng nghịu mặt, vậy liền mở ra lối ri&ecirc;ng, thế dụ c&ugrave;ng lộng lẫy bị giới hạn quan phủ quy củ, thư tịch loại mục bị hạn chết một ch&uacute;t.&quot;<br />\r\n<br />\r\n&quot;Ngươi c&aacute;i n&agrave;y s&aacute;ch nhỏ cửa h&agrave;ng vừa vặn kh&ocirc;ng c&oacute; n&agrave;y hạn chế, dứt kho&aacute;t giảm bớt kinh, sử, tử, tập c&aacute;i n&agrave;y, nhiều lấy ch&iacute; qu&aacute;i, v&otilde; hiệp, nh&acirc;n y&ecirc;u, t&igrave;nh y&ecirc;u c&aacute;c loại tiểu thuyết l&agrave;m chủ, lại th&ecirc;m ho&agrave;ng lịch tự thiếp, thường d&ugrave;ng s&aacute;ch thuốc những n&agrave;y, &iacute;t l&atilde;i ti&ecirc;u thụ mạnh. Tuy nhi&ecirc;n rất kh&oacute; đại ph&uacute;, nhưng hỗn c&aacute;i ấm no tuyệt kh&ocirc;ng th&agrave;nh vấn đề.&quot;<br />\r\n<br />\r\nL&yacute; Tam Ngư n&oacute;i xong, nắm bắt r&acirc;u h&igrave;nh chử b&aacute;t Giả Cường gật đầu t&aacute;n đồng.<br />\r\n<br />\r\nSau đ&oacute;, lộ ra một loại canh vẻ đắc &yacute;, cất cao giọng n&oacute;i:<br />\r\n<br />\r\n&quot;L&yacute; l&atilde;o bản đ&acirc;y l&agrave; ổn thỏa biện ph&aacute;p, tuy nhi&ecirc;n ta cảm thấy ngươi c&oacute; thể thử lại lần nữa k&iacute;ch th&iacute;ch ch&uacute;t.&quot;<br />\r\n<br />\r\n&quot;Ngươi cũng biết được, bởi v&igrave; hai lần đ&oacute; thất bại chiến tranh, triều đ&igrave;nh c&oacute; kh&ocirc;ng &iacute;t tuổi trẻ đại quan bắt đầu l&agrave;m c&aacute;i g&igrave; dương c&aacute;i g&igrave; vận động, n&oacute;i l&agrave; muốn đem T&acirc;y Dương quỷ s&uacute;c một ch&uacute;t học thuyết b&iacute; thuật to&agrave;n học qua đến, phong ph&uacute; ta Thi&ecirc;n Tri&ecirc;u Thượng Quốc tri thức căn bản.&quot;<br />\r\n<br />\r\n&quot;Những c&aacute;i kia quan l&atilde;o gia đại kế ta kh&ocirc;ng cần để &yacute;, chỉ cần biết cho d&ugrave; l&agrave; Tầm Ti&ecirc;n trong huyện, cũng c&oacute; rất nhiều người đối những c&aacute;i kia T&acirc;y Dương quỷ s&uacute;c đồ vật cảm thấy hứng th&uacute; ch&iacute;nh l&agrave;.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i gọi l&agrave; hợp &yacute;, ngươi chỉ cần tiến ch&uacute;t đi qua phi&ecirc;n dịch T&acirc;y Dương thư tịch, tuyệt đối l&agrave; trong th&agrave;nh phần độc nhất, kh&ocirc;ng lo nguồn ti&ecirc;u thụ.&quot;<br />\r\n<br />\r\n&quot;Tuy nhi&ecirc;n ngươi động thủ phải nhanh, ta cho ngươi c&aacute;i nội t&igrave;nh tin tức.&quot;<br />\r\n<br />\r\n&quot;Ta Nam Việt mới nhậm chức Tổng đốc qu&yacute; đại nh&acirc;n, trước đ&oacute; v&agrave;i ng&agrave;y trước đ&aacute;nh bại một cỗ 【 th&aacute;i b&igrave;nh loạn qu&acirc;n 】, lại ti&ecirc;u diệt một cỗ 【 Y&ecirc;u Thần qu&acirc;n 】, danh vọng ch&iacute;nh long, quyền h&agrave;nh cũng lớn.&quot;<br />\r\n<br />\r\n&quot;Hắn c&oacute; cảm gi&aacute;c tại T&acirc;y Dương quỷ s&uacute;c mới lạ vũ kh&iacute; c&ugrave;ng học thuyết, thụ &yacute; nh&agrave; m&igrave;nh chất tử, mới xử l&yacute; một nh&agrave; 【 đức h&agrave;nh thư cục 】, phi&ecirc;n dịch ấn chế một nh&oacute;m T&acirc;y Dương s&aacute;ch, ngươi đi s&aacute;ch trận c&oacute; thể hỏi thăm một ch&uacute;t, nếu c&oacute; thể đặt trước một ch&uacute;t, lợi nhuận n&ecirc;n kh&ocirc;ng nhỏ.&quot;<br />\r\n<br />\r\n&quot;Đúng, trừ chi&ecirc;u n&agrave;y, c&ograve;n c&oacute; hoạ b&aacute;o nữ lang sổ, diễm tục cấm thư c&aacute;i n&agrave;y hai đầu ổn tr&aacute;m con đường ph&aacute;t t&agrave;i, ngươi như nguyện bu&ocirc;ng xuống cổ hủ chi t&acirc;m, cũng c&oacute; thể thử một ch&uacute;t.&quot;<br />\r\n<br />\r\n...<br />\r\n<br />\r\n&quot;Hai vị ca ca quả nhi&ecirc;n đều l&agrave; kiến thức rộng r&atilde;i, vừa mới một phen cạn đ&agrave;m đ&atilde; thắng qua mấy năm đọc s&aacute;ch, thư sinh cảm k&iacute;ch kh&ocirc;ng th&ocirc;i.&quot;<br />\r\n<br />\r\n&quot;Ng&agrave;y mai Th&aacute;i An cư, ta mời hai vị ca ca nhấm nh&aacute;p thịt d&ecirc; nồi lẩu.&quot;<br />\r\n<br />\r\nTh&agrave;nh Hữu tiệm s&aacute;ch cửa ra v&agrave;o, Đ&agrave;o Tiềm l&ocirc;i k&eacute;o hai người.<br />\r\n<br />\r\nMột hồi kh&ocirc;ng cần tiền m&ocirc;ng ngựa xuống dưới, để hai vị n&agrave;y hỗ trợ b&agrave;y mưu t&iacute;nh kế gian thương đều l&agrave; vừa l&ograve;ng thỏa &yacute; rời đi.<br />\r\n<br />\r\nĐương nhi&ecirc;n, Đ&agrave;o Tiềm kh&ocirc;ng &iacute;t lời n&oacute;i đều l&agrave; thật l&ograve;ng.<br />\r\n<br />\r\nHắn tuy được hai phần tr&iacute; nhớ, nhưng đối rất nhiều chuyện c&ugrave;ng chi tiết đều l&agrave; kh&ocirc;ng thực cảm gi&aacute;c.<br />\r\n<br />\r\nVừa mới một phen tr&ograve; chuyện, đem những n&agrave;y bổ sung.<br />\r\n<br />\r\nĐối với Trường Sinh Thi&ecirc;n Tri&ecirc;u c&ugrave;ng hiện nay thời cuộc, hắn đ&atilde; c&oacute; coi như kỹ c&agrave;ng hiểu biết.<br />\r\n<br />\r\nĐứng tại cửa ra v&agrave;o, Đ&agrave;o Tiềm suy nghĩ mấy gi&acirc;y.<br />\r\n<br />\r\nBước nhanh đi trở về trong tiệm, lấy tờ giấy trắng, viết xuống:<br />\r\n<br />\r\n&quot;Ra ngo&agrave;i mua s&aacute;ch, ng&agrave;y mai lại mở.&quot;<br />\r\n<br />\r\n&quot;Sẽ c&oacute; hàng mới, tr&ocirc;ng h&acirc;n hạnh chiếu cố.&quot;<br />\r\n<br />\r\nMười s&aacute;u chữ th&ocirc;ng tục dễ hiểu, kiểu chữ cương trực.<br />\r\n<br />\r\nKế thừa ban đầu th&acirc;n thể cơ bắp tr&iacute; nhớ, Đ&agrave;o Tiềm kh&ocirc;ng ph&iacute; sức liền c&oacute; được một tay kh&ocirc;ng k&eacute;m b&uacute;t l&ocirc;ng chữ bản lĩnh.<br />\r\n<br />\r\nH&agrave;i l&ograve;ng vỗ vỗ tay, trở lại liền đem cửa tiệm đ&oacute;ng lại, đem giấy d&aacute;n đi l&ecirc;n.<br />\r\n<br />\r\nCũng kh&ocirc;ng cần lại nhiều thu thập, d&ugrave; sao th&acirc;n gia tổng cộng &quot;Ch&iacute;n nguy&ecirc;n cửu gi&aacute;c năm mươi ba mai tiền đồng&quot;, tất cả đều thăm d&ograve; trong ngực.<br />\r\n<br />\r\nX&aacute;c nhận kh&ocirc;ng lộ ch&uacute;t sơ hở c&aacute;i g&igrave; về sau, Đ&agrave;o Tiềm ph&acirc;n biệt ph&iacute;a dưới hướng, dạo chơi liền hướng Tầm Ti&ecirc;n huyện ch&acirc;n ch&iacute;nh n&aacute;o nhiệt chi địa, n&agrave;y gọi l&agrave; &quot;Tầm Ti&ecirc;n bến đ&ograve;&quot; bến cảng khu đi đến.<br />\r\n<br />\r\nHắn đến đ&oacute;, tự nhi&ecirc;n kh&ocirc;ng phải v&igrave; dời gạch chuyển h&agrave;ng, m&agrave; ch&iacute;nh l&agrave; v&igrave; nhập h&agrave;ng.<br />\r\n<br />\r\nHơi c&oacute; ch&uacute;t kh&ocirc;ng thể tưởng tượng nổi.<br />\r\n<br />\r\nNhưng Tầm Ti&ecirc;n huyện s&aacute;ch trận, ho&agrave;n to&agrave;n ch&iacute;nh x&aacute;c tại bến đ&ograve;.<br />\r\n<br />\r\nThậm ch&iacute; kh&ocirc;ng chỉ l&agrave; thư tịch giao dịch, như l&agrave; đại t&ocirc;ng n&ocirc;ng sinh, muối sắt, than đ&aacute; v&acirc;n v&acirc;n, thậm ch&iacute; cả một ch&uacute;t nhục thể b&ecirc;n tr&ecirc;n trung t&acirc;m giao dịch, cũng đều tại n&agrave;y khu vực.<br />\r\n<br />\r\nTầm Ti&ecirc;n huyện, nhưng thật ra l&agrave; c&aacute;i tương đối phồn hoa bến cảng th&agrave;nh trấn.<br />\r\n<br />\r\nĐương nhi&ecirc;n, n&oacute;i l&agrave; &quot;S&aacute;ch trận&quot; .<br />\r\n<br />\r\nTr&ecirc;n bản chất, nhưng thật ra l&agrave; mấy c&aacute;i từ tỉnh th&agrave;nh m&agrave; đến đại h&igrave;nh s&aacute;ch thuyền sẽ tại một ch&uacute;t thời khắc đặc biệt, dừng s&aacute;t ở Tầm Ti&ecirc;n huyện m&agrave; th&ocirc;i.<br />\r\n<br />\r\nThời gian kh&ocirc;ng d&agrave;i kh&ocirc;ng ngắn, vừa l&uacute;c đủ trong huyện to to nhỏ nhỏ bảy t&aacute;m nh&agrave; tiệm s&aacute;ch cửa h&agrave;ng s&aacute;ch l&ecirc;n thuyền mua sắm, ngẫu nhi&ecirc;n cũng sẽ mười dặm t&aacute;m hương một ch&uacute;t s&aacute;ch nhỏ tứ l&atilde;o bản tiến đến.<br />\r\n<br />\r\nC&aacute;i gọi l&agrave; s&aacute;ch thuyền, dĩ nhi&ecirc;n l&agrave; chỉ chuy&ecirc;n m&ocirc;n b&aacute;n s&aacute;ch thuyền.<br />\r\n<br />\r\nĐ&acirc;y cũng l&agrave; bản tỉnh đặc sắc một trong.<br />\r\n<br />\r\nTỉnh th&agrave;nh xuất bản in ấn nghiệp cực ph&aacute;t đạt, th&ecirc;m nữa đường thủy giao th&ocirc;ng dị thường tiện lợi, viễn si&ecirc;u đường bộ, cũng liền thúc đ&acirc;̉y sinh trưởng ra &quot;S&aacute;ch thuyền&quot; loại n&agrave;y đặc th&ugrave; sản phẩm.<br />\r\n<br />\r\nMỗi c&aacute;ch một đoạn thời gian, từ tỉnh th&agrave;nh xuất ph&aacute;t từng chiếc từng chiếc s&aacute;ch thuyền liền sẽ đường tắt mỗi c&aacute;i bến cảng, mượn từ cửa h&agrave;ng s&aacute;ch l&atilde;o bản hoặc l&agrave; c&aacute;i kh&aacute;c c&oacute; được &quot;S&aacute;ch bằng&quot; người tay, đem c&aacute;c thức thư tịch, b&aacute;o ch&iacute; ph&acirc;n ph&aacute;t lưu th&ocirc;ng đến to&agrave;n tỉnh c&aacute;c nơi.<br />\r\n<br />\r\nThời gian nguy&ecirc;n bản kh&ocirc;ng qu&aacute; định, c&oacute; l&uacute;c l&agrave; mấy ng&agrave;y một chuyến, c&oacute; khi thậm ch&iacute; l&agrave; mười mấy ng&agrave;y một chuyến.<br />\r\n<br />\r\nTuy nhi&ecirc;n từ ban đầu th&acirc;n thể trong tr&iacute; nhớ c&oacute; biết, gần đoạn thời gian đến nay, s&aacute;ch thuyền cơ hồ l&agrave; mỗi ng&agrave;y liền sẽ xuất ph&aacute;t một chuyến.<br />\r\n<br />\r\nM&agrave; lại, cũng kh&ocirc;ng chỉ l&agrave; thư tịch giao dịch.<br />\r\n<br />\r\nPhương diện kh&aacute;c, đồng dạng trở n&ecirc;n hết sức phồn vinh sinh động.<br />\r\n<br />\r\nĐiều n&agrave;y tựa hồ c&oacute; ch&uacute;t m&acirc;u thuẫn, b&acirc;y giờ qu&acirc;n phiệt nổi l&ecirc;n bốn ph&iacute;a, loạn đảng tứ ngược, triều đ&igrave;nh n&agrave;y hiển nhi&ecirc;n đ&atilde; c&oacute; tuổi gi&agrave; kh&iacute; tượng, l&agrave;m sao c&ograve;n c&agrave;ng th&ecirc;m phồn hoa?<br />\r\n<br />\r\nHồi quang phản chiếu?<br />\r\n<br />\r\nĐ&agrave;o Tiềm n&oacute;i thầm đến nơi đ&acirc;y, cũng đ&uacute;ng l&uacute;c đi đến chợ b&aacute;n thức ăn đường phố nơi trung t&acirc;m nhất.<br />\r\n<br />\r\nBỗng nhi&ecirc;n, hắn h&igrave;nh như c&oacute; nhận thấy.<br />\r\n<br />\r\nChậm r&atilde;i ngẩng đầu, vừa vặn tr&ocirc;ng thấy ph&iacute;a trước n&agrave;y gần cao mười m&eacute;t cọc gỗ.<br />\r\n<br />\r\nChi&ecirc;u hồn c&aacute;n!</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nKh&ocirc;ng đợi Đ&agrave;o Tiềm nhả r&atilde;nh c&aacute;i n&agrave;y ph&aacute; cột lấy c&aacute;i quỷ g&igrave; t&ecirc;n, một trận kh&ocirc; n&oacute;ng gi&oacute; thổi phật đứng l&ecirc;n.<br />\r\n<br />\r\nLập tức liền thấy n&agrave;y c&aacute;n b&ecirc;n tr&ecirc;n, mười mấy khỏa đi qua chống ph&acirc;n huỷ xử l&yacute; đầu người, t&ugrave;y phong lung lay.<br />\r\n<br />\r\nC&aacute;i n&agrave;y c&oacute; thể n&oacute;i l&agrave; kinh dị, một m&agrave;n kinh khủng, lại kh&ocirc;ng có th&ecirc;̉ g&acirc;y n&ecirc;n d&ugrave; l&agrave; một tiếng kinh h&ocirc;.<br />\r\n<br />\r\nTr&ecirc;n đường, biển người biển người.<br />\r\n<br />\r\nTrừ Đ&agrave;o Tiềm b&ecirc;n ngo&agrave;i, mỗi một người tựa hồ cũng đ&atilde; tập m&atilde;i th&agrave;nh th&oacute;i quen.<br />\r\n<br />\r\nH&ocirc;m qua n&aacute;o nhiệt l&agrave; h&ocirc;m qua, kết th&uacute;c cũng liền kết th&uacute;c.<br />\r\n<br />\r\nĐ&agrave;o Tiềm trước mắt nh&igrave;n thuộc về &quot;M&igrave;nh&quot; đầu, v&ecirc;́t thương ch&ocirc;̀ng ch&acirc;́t, hai mắt nguy&ecirc;n bản đ&ecirc;́n ch&ecirc;́t đều l&agrave; trừng mắt, b&acirc;y giờ bị v&aacute; lại.<br />\r\n<br />\r\nCũng kh&ocirc;ng chỉ l&agrave; hắn, c&aacute;i kh&aacute;c mấy c&aacute;i bị trảm thủ sau kh&ocirc;ng c&oacute; nhắm mắt ch&iacute; sĩ, đồng dạng l&agrave; đ&atilde;i ngộ như thế.<br />\r\n<br />\r\nĐ&agrave;o Tiềm kh&ocirc;ng c&oacute; tiếp tục đi, m&agrave; ch&iacute;nh l&agrave; y&ecirc;n lặng đếm xem.<br />\r\n<br />\r\n&quot;Thiếu mấy khỏa, hả?&quot;<br />\r\n<br />\r\n&Yacute; niệm n&agrave;y c&ugrave;ng một chỗ, Đ&agrave;o Tiềm lập tức nh&igrave;n về ph&iacute;a c&aacute;ch chi&ecirc;u hồn c&aacute;n kh&ocirc;ng xa một c&aacute;i lều che hạ.<br />\r\n<br />\r\nNơi đ&oacute; đặt v&agrave;o Trương Ban b&aacute;c b&agrave;n gỗ, sau c&aacute;i b&agrave;n l&agrave; c&aacute;i quan &aacute;o xuy&ecirc;n được xi&ecirc;u xi&ecirc;u vẹo vẹo, lộ ra hơn ph&acirc;n nửa lồng ngực, đ&aacute;nh thẳng ngủ gật mặt đen l&atilde;o lại.<br />\r\n<br />\r\nTrong đầu, tương ứng tr&iacute; nhớ hiển hiện.<br />\r\n<br />\r\nBan đầu th&acirc;n thể d&ugrave; sao tr&ecirc;n đường mở một đoạn thời gian cửa h&agrave;ng s&aacute;ch, n&ecirc;n người quen biết đều biết.<br />\r\n<br />\r\nAi c&oacute; thể n&oacute;i chuyện, ai kh&ocirc;ng thể, Đ&agrave;o Tiềm giờ ph&uacute;t n&agrave;y cũng r&otilde; r&agrave;ng.<br />\r\n<br />\r\nĐ&agrave;o Tiềm đi qua, nhẹ nh&agrave;ng g&otilde; g&otilde; c&aacute;i b&agrave;n, n&agrave;y l&atilde;o lại tựa như căn bản l&agrave; kh&ocirc;ng c&oacute; ngủ, một gi&acirc;y sau liền mở ra đục ngầu hai mắt.<br />\r\n<br />\r\nC&ugrave;ng n&oacute; liếc nhau, Đ&agrave;o Tiềm n&oacute;i:<br />\r\n<br />\r\n&quot;Vi&ecirc;n L&atilde;o, ta nghe n&oacute;i những người n&agrave;y đầu muốn chờ sau bảy ng&agrave;y mới c&oacute; thể d&ugrave;ng tiền chuộc về hạ t&aacute;ng, l&agrave;m sao mới qua một ng&agrave;y liền thiếu đi ch&uacute;t, c&oacute; phải l&agrave; cấp tr&ecirc;n c&oacute; c&aacute;i g&igrave; d&agrave;n xếp, nếu như c&oacute; thể m&agrave; n&oacute;i, ta ngược lại l&agrave; muốn l&agrave;m điểm việc thiện, kh&ocirc;ng biết...&quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm kh&ocirc;ng c&oacute; n&oacute;i th&ecirc;m g&igrave; đi nữa, bởi v&igrave; hắn ph&aacute;t hiện trước mặt l&atilde;o lại bỗng nhi&ecirc;n d&ugrave;ng một loại quỷ dị &aacute;nh mắt nh&igrave;n m&igrave;nh chằm chằm.<br />\r\n<br />\r\nTuy nhi&ecirc;n Đ&agrave;o Tiềm cũng duy tr&igrave; trấn tĩnh, hắn kh&ocirc;ng c&oacute; g&igrave; đ&aacute;ng sợ.<br />\r\n<br />\r\nBị trảm thủ kh&ocirc;ng chết, phụ thể trọng sinh... Kh&uacute;c chiết như vậy quỷ dị, c&ograve;n c&oacute; thể bị một l&atilde;o lại nh&igrave;n thấu, n&agrave;y Đ&agrave;o Tiềm cũng nhận.<br />\r\n<br />\r\nĐương nhi&ecirc;n, ch&acirc;n ch&iacute;nh cho Đ&agrave;o Tiềm phấn kh&iacute;ch, l&agrave; ban đầu th&acirc;n thể tr&iacute; nhớ.<br />\r\n<br />\r\nC&aacute;i n&agrave;y l&atilde;o lại, l&agrave; c&aacute;i n&agrave;y tr&ecirc;n đường c&ocirc;ng nhận người tốt.<br />\r\n<br />\r\nQuả nhi&ecirc;n, n&agrave;y l&atilde;o lại kh&ocirc;ng thể xem thấu Đ&agrave;o Tiềm n&agrave;y ho&agrave;n mỹ v&ocirc; khuyết đ&oacute;ng vai, chằm chằm một l&aacute;t liền biết thư sinh n&agrave;y cổ hủ sức lực lại phạm.<br />\r\n<br />\r\nMặt đen l&atilde;o lại đối Đ&agrave;o Tiềm đ&ugrave;a cợt cười một tiếng, trừ trừ r&aacute;y tai, nghe, đạn đi, nhếch miệng lộ ra một ngụm răng v&agrave;ng, l&uacute;c n&agrave;y mới n&oacute;i:<br />\r\n<br />\r\n&quot;D&agrave;n xếp? Ngươi c&aacute;i n&agrave;y ngốc thư sinh l&agrave;m sao c&ograve;n như thế ng&acirc;y thơ.&quot;<br />\r\n<br />\r\n&quot;Xem ở ngươi c&aacute;i n&agrave;y ngu đần b&ecirc;n tr&ecirc;n, l&atilde;o đầu tử ta nhắc nhở ngươi một ch&uacute;t, muốn l&agrave;m việc thiện ngươi đi hoa ng&otilde; hẻm cũng tốt, Tầm Ti&ecirc;n tr&ecirc;n s&ocirc;ng những c&aacute;i kia tr&ecirc;n mặt thuyền hoa cũng tốt, hoặc l&agrave; ngươi đi khất c&aacute;i hẻm đi một lần đều được.&quot;<br />\r\n<br />\r\n&quot;T&oacute;m lại, chuyện n&agrave;y a, ng&agrave;n vạn kh&ocirc;ng thể lẫn v&agrave;o.&quot;<br />\r\n<br />\r\n&quot;Huyện nha đ&aacute;m người kia tập trung tinh thần c&acirc;u c&aacute; lớn, ngươi đầu n&agrave;y ngốc c&aacute; tr&aacute;nh xa một ch&uacute;t, miễn cho chết như thế n&agrave;o cũng kh&ocirc;ng biết.&quot;<br />\r\n<br />\r\n&quot;C&uacute;t đi, đừng quấy rầy l&atilde;o tử ngủ ngon, x&uacute;i quẩy.&quot;</p>', '2021-12-01 15:01:16', 3, 1),
(306, 76, 'Phiên Bản Cổ đại Hộp đen', '5', '<p>Mặt đen l&atilde;o lại mở mi&ecirc;̣ng m&ocirc;̣t ti&ecirc;́ng x&uacute;i quẩy, hun đến Đ&agrave;o Tiềm hiện tại liền kh&ocirc;ng nhịn được mặt mũi.<br />\r\n<br />\r\nHừ lạnh một tiếng, vứt xuống c&acirc;u &quot;Kh&ocirc;ng thể n&oacute;i l&yacute;&quot; liền vung lấy tay &aacute;o nện bước bước nhanh đi, n&agrave;y l&agrave;m d&aacute;ng cực giống n&eacute;m mặt mũi cổ hủ thư sinh.<br />\r\n<br />\r\nChỉ l&agrave; ai cũng kh&ocirc;ng c&oacute; ch&uacute; &yacute; tới, Đ&agrave;o Tiềm trong mắt hiện ra vẻ phức tạp, bất đắc dĩ, may mắn c&ugrave;ng phẫn nộ.<br />\r\n<br />\r\nHắn muốn đem ch&iacute; sĩ nh&oacute;m đầu l&acirc;u chuộc về an t&aacute;ng, tự nhi&ecirc;n kh&ocirc;ng phải nhất thời x&uacute;c động, cũng kh&ocirc;ng phải bởi v&igrave; đến vị kia ch&iacute; sĩ tr&iacute; nhớ c&ugrave;ng t&agrave;n quyết, ch&iacute; &iacute;t đ&acirc;y kh&ocirc;ng phải nguy&ecirc;n nh&acirc;n ch&iacute;nh.<br />\r\n<br />\r\nKhu động Đ&agrave;o Tiềm nghĩ l&agrave;m như vậy, l&agrave; tư tưởng b&ecirc;n tr&ecirc;n t&aacute;n đồng.<br />\r\n<br />\r\nĐ&aacute;ng tiếc, thời cơ kh&ocirc;ng đ&uacute;ng.<br />\r\n<br />\r\nN&agrave;y l&atilde;o lại &quot;Trong l&uacute;c v&ocirc; t&igrave;nh&quot; tiết lộ tin tức, đ&atilde; l&agrave; c&aacute;i c&oacute; thể cứu mệnh nhắc nhở.<br />\r\n<br />\r\n&quot;Thở ra, c&acirc;u c&aacute;?&quot;<br />\r\n<br />\r\n&quot;Hoặc l&agrave; l&uacute;c trước cứ điểm bị trừ bỏ l&uacute;c c&ograve;n c&oacute; người trốn, Tầm Ti&ecirc;n huyện cẩu quan kia nghĩ thiết lập c&aacute;i cạm bẫy, &ocirc;m c&acirc;y đợi thỏ. Hoặc l&agrave; c&agrave;ng lớn d&atilde; t&acirc;m, nh&ocirc;̉ Tầm Ti&ecirc;n cứ điểm, lại nhớ thương địa phương kh&aacute;c ch&iacute; sĩ, nghĩ đến c&aacute;i dẫn x&agrave; xuất động.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i sau khả năng c&agrave;ng lớn, tuy nhi&ecirc;n mặc kệ l&agrave; loại n&agrave;o, ta b&acirc;y giờ th&acirc;n phận n&agrave;y, tạm thời cũng kh&ocirc;ng thể ra mặt.&quot;<br />\r\n<br />\r\n&quot;Lại chờ một ch&uacute;t, chi&ecirc;u kia hồn c&aacute;n b&ecirc;n tr&ecirc;n t&oacute;m lại l&agrave; muốn đổi mới, nhất định c&ograve;n c&oacute; cơ hội, nhiều nhất cũng ch&iacute;nh l&agrave; ph&aacute; rủi ro.&quot;<br />\r\n<br />\r\nT&acirc;m niệm cuồn cuộn, Đ&agrave;o Tiềm tạm thời kh&ocirc;ng ra cam &yacute; nghĩ đ&egrave; xuống.<br />\r\n<br />\r\nNhớ tới &quot;Rủi ro&quot;, Đ&agrave;o Tiềm lại rất nhanh &yacute; thức được m&igrave;nh b&acirc;y giờ l&agrave; c&aacute;i thực sự ngh&egrave;o bức.<br />\r\n<br />\r\nTrong ngực cất to&agrave;n bộ gia sản, cũng bất qu&aacute; l&agrave; chín khối bạc hơn nguy&ecirc;n.<br />\r\n<br />\r\nCăn cứ n&atilde;o hải tr&iacute; nhớ, Tầm Ti&ecirc;n tr&ecirc;n bến t&agrave;u một vị cường tráng ch&uacute;t, chịu kh&oacute; ch&uacute;t c&ocirc;ng nh&acirc;n bốc v&aacute;c, một th&aacute;ng tiền lương cũng c&oacute; s&aacute;u nguy&ecirc;n tả hữu, trong th&agrave;nh tiệm tơ lụa, hiệu cầm đồ, chưởng quỹ tiệm thuốc ti&ecirc;n sinh loại h&igrave;nh, mỗi th&aacute;ng lương bổng cũng c&oacute; t&aacute;m nguy&ecirc;n ch&iacute;n nguy&ecirc;n, như đụng tới ph&uacute;c hậu đ&ocirc;ng gia, qua c&aacute;i mười nguy&ecirc;n cũng l&agrave; c&oacute;.<br />\r\n<br />\r\nVừa nghĩ đến đ&acirc;y, Đ&agrave;o Tiềm n&agrave;y thẳng tắp lưng lại cong điểm, nhịn kh&ocirc;ng được thở d&agrave;i.<br />\r\n<br />\r\nLẫn trong đ&aacute;m người, Đ&agrave;o Tiềm một b&ecirc;n hướng mục đ&iacute;ch m&agrave; đi, một b&ecirc;n th&igrave; l&agrave; suy tư l&ecirc;n kiếm tiền đại kế.<br />\r\n<br />\r\nN&oacute;i thực ra, buổi trưa L&yacute; Tam Ngư, Giả Cường hai vị n&agrave;y gian thương miễn ph&iacute; tặng cho kiếm tiền tiểu khiếu m&ocirc;n, cũng kh&ocirc;ng thể qu&ecirc;n rất hiếm c&oacute;, kh&ocirc;ng phải c&aacute;i g&igrave; rất b&aacute; đạo rất độc nhất v&ocirc; nhị kỹ xảo.<br />\r\n<br />\r\nNhưng Đ&agrave;o Tiềm lấy &quot;Th&agrave;nh Hữu tiệm s&aacute;ch l&atilde;o bản&quot; c&aacute;i th&acirc;n phận n&agrave;y tới suy nghĩ, nhất định phải thừa nhận, phương ph&aacute;p đều l&agrave; hữu hiệu.<br />\r\n<br />\r\nTu&acirc;n theo hai người n&oacute;i, kiếm ch&uacute;t ấm no tiền tuyệt kh&ocirc;ng phải việc kh&oacute; g&igrave;.<br />\r\n<br />\r\nĐ&agrave;o Tiềm cũng kh&ocirc;ng c&oacute; ban đầu th&acirc;n thể như vậy cổ hủ, kh&ocirc;ng c&oacute; &yacute; định cự tuyệt &quot;Đồng bạc&quot; đ&aacute;ng y&ecirc;u như thế sự vật, cho n&ecirc;n ngay cả gi&atilde;y dụa do dự một ch&uacute;t đều kh&ocirc;ng c&oacute;, trực tiếp liền chạy bến đ&ograve; b&ecirc;n cạnh s&aacute;ch trận đi.<br />\r\n<br />\r\nC&aacute;i g&igrave; kế thứ nhất kế thứ hai, hắn dự định đều l&agrave;m theo.<br />\r\n<br />\r\nNh&acirc;n y&ecirc;u quỷ luyến, thần thoại ch&iacute; qu&aacute;i tiểu thuyết tiến một đợt, diễm tục cấm thư trộm đạo đến trọn vẹn, hoạ b&aacute;o nữ lang c&oacute; m&agrave; n&oacute;i cũng vơ v&eacute;t tới, T&acirc;y Dương quỷ s&uacute;c phi&ecirc;n dịch s&aacute;ch cũng hết thảy đều cả b&ecirc;n tr&ecirc;n... H&ocirc;m nay c&aacute;i n&agrave;y to&agrave;n bộ gia sản kh&ocirc;ng tốn ra ngo&agrave;i c&ograve;n chưa xong, nhiều nhất lưu mấy khối tiền đồng mua m&agrave;n thầu ăn.<br />\r\n<br />\r\nVề phần đ&atilde; n&oacute;i xong ng&agrave;y mai mời hai vị l&atilde;o ca Th&aacute;i An cư thịt d&ecirc; nồi lẩu... Ta ngh&egrave;o như vậy chua thư sinh, nợ lần sổ s&aacute;ch cũng l&agrave; rất hợp l&yacute; sự t&igrave;nh đi.<br />\r\n<br />\r\nĐ&agrave;o Tiềm nghĩ như vậy, kh&oacute;e miệng nhịn kh&ocirc;ng được giương l&ecirc;n đứng l&ecirc;n.<br />\r\n<br />\r\nKh&ocirc;ng khỏi nhanh Đ&agrave;o Tiềm lại nghĩ tới m&igrave;nh người xuy&ecirc;n việt th&acirc;n phận, tư duy lập tức canh ph&aacute;t t&aacute;n.<br />\r\n<br />\r\n&quot;Đ&uacute;ng a, ta thế nhưng l&agrave; người xuy&ecirc;n việt a, chỉ nghe qua sướng chết người xuy&ecirc;n việt, chưa từng nghe qua ngh&egrave;o chết.&quot;<br />\r\n<br />\r\n&quot;Để ta ngẫm lại, đến loại n&agrave;y thời đại bối cảnh thế giới, tốt nhất kiếm tiền phương thức c&oacute; những c&aacute;i kia?&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;B&aacute;n tất ch&acirc;n? Kh&ocirc;ng được kh&ocirc;ng được, c&aacute;i n&agrave;y &iacute;t nhất phải ta cũng l&agrave;m b&ecirc;n tr&ecirc;n qu&acirc;n phiệt mới c&oacute; thao t&aacute;c kh&ocirc;ng gian, huống chi ai biết thế giới n&agrave;y người phương t&acirc;y nh&oacute;m tr&egrave;o khoa học kỹ thuật Thụ leo tới nơi đ&oacute;, vạn nhất người ta đ&atilde; tiện tay đem đai đeo viền ren, chỉ đen tơ trắng đều cả b&ecirc;n tr&ecirc;n đ&acirc;u.&quot;<br />\r\n<br />\r\n&quot;Kẻ ch&eacute;p văn? C&oacute; vẻ như c&oacute; thể a, tuy nhi&ecirc;n bởi v&igrave; lịch sử c&aacute;ch cục ho&agrave;n to&agrave;n kh&aacute;c biệt, như l&agrave; &laquo; đại quốc quật khởi &raquo; c&aacute;i n&agrave;y c&oacute; thể thu hoạch danh vọng s&aacute;ch ch&eacute;p kh&ocirc;ng được, nhưng ti&ecirc;n hiệp huyền huyễn loại h&igrave;nh, hay l&agrave; c&oacute; hi vọng.&quot;<br />\r\n<br />\r\n...<br />\r\n<br />\r\nMột phen &yacute; d&acirc;m mặc sức tưởng tượng kết th&uacute;c, Đ&agrave;o Tiềm người đ&atilde; l&agrave; c&aacute;ch chợ b&aacute;n thức ăn đường phố, lại qua non nửa th&agrave;nh khu, rất nhanh tới đạt mục đ&iacute;ch.<br />\r\n<br />\r\nTầm Ti&ecirc;n bến đ&ograve;!<br />\r\n<br />\r\nĐ&agrave;o Tiềm tuy nhi&ecirc;n đ&atilde; tại nguy&ecirc;n th&acirc;n thể trong tr&iacute; nhớ sớm xem qua bến đ&ograve; bộ d&aacute;ng, c&oacute; thể giờ ph&uacute;t n&agrave;y ch&acirc;n ch&iacute;nh nh&igrave;n thấy, vẫn như cũ l&agrave; nhịn kh&ocirc;ng được lộ ra đồ nh&agrave; qu&ecirc; chuy&ecirc;n ch&uacute;c biểu lộ.<br />\r\n<br />\r\nBởi v&igrave; chợ b&aacute;n thức ăn đường phố ph&aacute;p trường, trong huyện b&igrave;nh d&acirc;n sinh hoạt t&igrave;nh trạng c&aacute;c loại, Đ&agrave;o Tiềm ngầm thừa nhận thế giới n&agrave;y c&ugrave;ng m&igrave;nh kiếp trước so s&aacute;nh qu&aacute; lạc hậu.<br />\r\n<br />\r\nNhưng nh&igrave;n thấy trước mắt, lại l&agrave;m cho Đ&agrave;o Tiềm cảm gi&aacute;c m&igrave;nh sai.<br />\r\n<br />\r\nPh&iacute;a trước bến cảng, giang h&agrave; giao hội, trăm t&agrave;u ng&agrave;n buồm, biển người phun tr&agrave;o.<br />\r\n<br />\r\nTrừ to to nhỏ nhỏ mộc thuyền buồm, ở ph&iacute;a xa s&acirc;u hơn nước khu, c&ograve;n xuất hiện từng chiếc từng chiếc bốc l&ecirc;n hắc kh&iacute; hơi nước thuyền th&eacute;p.<br />\r\n<br />\r\nTr&ecirc;n bến t&agrave;u n&aacute;o nhiệt ho&agrave;n to&agrave;n kh&ocirc;ng phải chợ b&aacute;n thức ăn đường phố c&oacute; thể so s&aacute;nh, c&oacute; mặc đoản đả, hoặc l&agrave; ở trần c&ocirc;ng nh&acirc;n bốc v&aacute;c, c&oacute; c&aacute;c thức b&aacute;n h&agrave;ng rong, c&oacute; c&aacute;c thức người gi&agrave;u c&oacute; thương nh&acirc;n, c&oacute; giang hồ nh&acirc;n sĩ, c&oacute; quan vi&ecirc;n binh l&iacute;nh, thậm ch&iacute; c&ograve;n c&oacute; kh&ocirc;ng &iacute;t t&oacute;c v&agrave;ng hồng ph&aacute;t T&acirc;y Dương di nh&acirc;n.<br />\r\n<br />\r\n&quot;Tầm Ti&ecirc;n huyện như thế, n&agrave;y tỉnh th&agrave;nh đ&acirc;u, c&ograve;n c&oacute; ban đầu cả đời qua chưa đi qua Ma Đ&ocirc;, đế đ&ocirc; đ&acirc;u?&quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm bỗng nhi&ecirc;n c&oacute; ch&uacute;t nhiệt huyết s&ocirc;i tr&agrave;o l&ecirc;n.<br />\r\n<br />\r\nThế giới n&agrave;y, c&oacute; lẽ so hắn tưởng tượng b&ecirc;n trong c&agrave;ng th&ecirc;m mỹ lệ, rộng lớn.<br />\r\n<br />\r\nĐ&atilde; đến một lần, kh&ocirc;ng rất thăm d&ograve; thể nghiệm một phen, chẳng phải l&agrave; thiệt th&ograve;i lớn.<br />\r\n<br />\r\n&Yacute; niệm n&agrave;y rơi xuống, Đ&agrave;o Tiềm cười cười, kh&ocirc;ng c&oacute; quan s&aacute;t tiếp nữa trước mặt thịnh cảnh.<br />\r\n<br />\r\nLần theo trong đầu tr&iacute; nhớ, hướng bến đ&ograve; một b&ecirc;n đi đến.<br />\r\n<br />\r\nTầm Ti&ecirc;n huyện l&agrave;m kh&ocirc;ng nhỏ bến cảng th&agrave;nh trấn, bến đ&ograve; người khu thuộc về như l&agrave; muối sắt than đ&aacute; loại n&agrave;y l&agrave;m ăn lớn.<br />\r\n<br />\r\nS&aacute;ch thuyền, hoa thuyền một loại, chỉ c&oacute; thể tại g&oacute;c nhỏ.<br />\r\n<br />\r\nKh&ocirc;ng sai, hai c&aacute;i n&agrave;y l&agrave; c&ugrave;ng nhau.<br />\r\n<br />\r\nKhi Đ&agrave;o Tiềm đi v&agrave;o địa phương, c&ograve;n kh&ocirc;ng c&oacute; tới gần mấy chiếc kia s&aacute;ch lớn thuyền, lại l&agrave; trước một bước nh&igrave;n thấy dừng ở kh&uacute;c s&ocirc;ng b&ecirc;n trong, ước chừng bảy t&aacute;m chiếc ăn mặc trang điểm lộng lẫy, diễm lệ y&ecirc;u d&atilde; &quot;Hoa thuyền&quot; .<br />\r\n<br />\r\nTới gần ch&uacute;t về sau, c&agrave;ng l&agrave; nh&igrave;n đ&atilde; mắt.<br />\r\n<br />\r\nTrong đ&oacute; tr&ecirc;n một con thuyền, c&oacute; vị ăn mặc m&aacute;t lạnh nữ tử đang nha ho&agrave;n hầu hạ hạ tẩy ph&aacute;t.<br />\r\n<br />\r\nNữ tử kia nhiều nhất hai mươi tuổi, hất l&ecirc;n tử sắc sa mỏng &aacute;o ngo&agrave;i, mơ hồ c&oacute; thể thấy được b&ecirc;n trong gấm c&aacute;i yếm, hắn xoay người đưa lưng về ph&iacute;a b&ecirc;n bờ l&uacute;c, eo hạ độ cong cực kỳ kinh người.<br />\r\n<br />\r\nT&ecirc; t&ecirc;... Đ&agrave;o Tiềm b&ecirc;n tai, h&iacute;t một hơi l&atilde;nh kh&iacute; thanh &acirc;m nhao nhao vang l&ecirc;n.<br />\r\n<br />\r\nNgắm nh&igrave;n bốn ph&iacute;a, đ&atilde; thấy b&ecirc;n cạnh th&acirc;n đ&aacute;m nam nh&acirc;n, mặc kệ ngh&egrave;o gi&agrave;u L&atilde;o lớn mạnh, tất cả đều l&agrave; một mặt thống khổ bất đắc dĩ c&uacute;i người.<br />\r\n<br />\r\nD&ugrave; vậy, c&ograve;n từng c&aacute;i v&ocirc; c&ugrave;ng quật cường ngẩng đầu muốn đi nh&igrave;n, tư th&aacute;i giống như đưa đầu r&ugrave;a đen.<br />\r\n<br />\r\nĐ&agrave;o Tiềm nhập bộ phim s&acirc;u, sau c&ugrave;ng vội v&agrave;ng đảo qua thuyền kia liếc một ch&uacute;t, ghi lại t&ecirc;n &quot;Ho&aacute;n Kh&ecirc; h&agrave;o&quot; sau.<br />\r\n<br />\r\nQuay đầu liền d&ugrave;ng khinh bỉ &aacute;nh mắt c&agrave;n qu&eacute;t bọn n&agrave;y L&atilde;o sắc ph&ecirc;, chợt vứt xuống một c&acirc;u:<br />\r\n<br />\r\n&quot;C&oacute; nhục nh&atilde; nhặn!&quot;<br />\r\n<br />\r\nThoại &acirc;m rơi xuống, cũng kh&ocirc;ng quay đầu lại liền hướng b&ecirc;n tr&aacute;i kh&uacute;c s&ocirc;ng mấy chiếc kia nước ăn kh&ocirc;ng cạn đại thuyền đi.<br />\r\n<br />\r\nĂn hắn một c&aacute;i đ&acirc;m lưng nhả r&atilde;nh mọi người thật cũng kh&ocirc;ng buồn bực, lưu luyến kh&ocirc;ng rời lại nh&igrave;n v&agrave;i lần về sau, c&oacute; kh&ocirc;ng &iacute;t người cũng c&ugrave;ng l&ecirc;n đến.<br />\r\n<br />\r\nTrong n&agrave;y, kh&ocirc;ng một l&agrave; người ngh&egrave;o.<br />\r\n<br />\r\nTừ mặc nh&igrave;n lại, mỗi một c&aacute;i đều so Đ&agrave;o Tiềm muốn gi&agrave;u qu&aacute; nhiều, lại c&ugrave;ng L&yacute; Tam Ngư, Giả Cường bực n&agrave;y phổ th&ocirc;ng thương nh&acirc;n kh&aacute;c biệt, lại tới đ&acirc;y người đều c&oacute; một ch&uacute;t thư hương kh&iacute;, c&aacute;ch ăn mặc thanh nh&atilde;, cử chỉ nh&atilde; nhặn.<br />\r\n<br />\r\nS&aacute;ch thuyền quy củ: Muốn l&ecirc;n đi, nhất định phải c&oacute; &quot;S&aacute;ch bằng&quot; thứ n&agrave;y.<br />\r\n<br />\r\nC&aacute;i n&agrave;y vật cũng kh&ocirc;ng phải l&agrave; chuy&ecirc;n thuộc về Đ&agrave;o Tiềm loại s&aacute;ch n&agrave;y chủ tiệm, tr&ecirc;n thực tế rất nhiều người đều c&oacute; tư c&aacute;ch thu hoạch.<br />\r\n<br />\r\nTỉ như quan lại, sinh vi&ecirc;n, cử nh&acirc;n v&acirc;n v&acirc;n.<br />\r\n<br />\r\nC&ograve;n c&oacute; bản địa đại tộc tử đệ, d&acirc;n gian đại nho, thư ph&aacute;p đại gia v&acirc;n v&acirc;n.<br />\r\n<br />\r\nTrừ tiệm s&aacute;ch, s&aacute;ch tứ l&atilde;o bản những n&agrave;y, những người c&ograve;n lại c&oacute; thể gọi chung l&agrave; &quot;T&agrave;ng thư kh&aacute;ch&quot;, bọn họ kỳ thật cũng l&agrave; thụ nhất s&aacute;ch chủ thuyền người hoan ngh&ecirc;nh h&agrave;o kh&aacute;ch.<br />\r\n<br />\r\nTr&aacute;i lại giống Đ&agrave;o Tiềm c&aacute;i n&agrave;y cửa h&agrave;ng s&aacute;ch nhỏ l&atilde;o bản, tuy nhi&ecirc;n chiếm c&aacute;i b&aacute;n bu&ocirc;n t&ecirc;n tuổi, nhưng đều m&oacute;c m&oacute;c lục so&aacute;t, kh&ocirc;ng c&oacute; g&igrave; sức mua, l&agrave; lấy ở v&agrave;o s&aacute;ch thuyền khinh bỉ li&ecirc;n trong c&ugrave;ng nhất.<br />\r\n<br />\r\nLẽ thường đến n&oacute;i, Đ&agrave;o Tiềm n&ecirc;n sợ h&atilde;i rụt r&egrave;, tr&ocirc;́n ở sau c&ugrave;ng mới l&agrave;.<br />\r\n<br />\r\nTuy nhi&ecirc;n ban đầu th&acirc;n thể kh&ocirc;ng phải loại người n&agrave;y a.<br />\r\n<br />\r\nĐ&agrave;o Tiềm cũng đ&agrave;nh phải học thượng, ngh&ecirc;nh ngang đi qua, trực tiếp tuyển lớn nhất một chiếc gọi l&agrave; &quot;Trạng Nguy&ecirc;n h&agrave;o&quot; s&aacute;ch thuyền, đưa ra một trương quan phủ ban ph&aacute;t s&aacute;ch bằng về sau, giẫm l&ecirc;n c&aacute;i thang đạp đạp trừng mấy bước liền l&ecirc;n đi.<br />\r\n<br />\r\nHắn vừa mới đứng vững, liền gặp mười mấy tuổi thư đồng ch&agrave;o đ&oacute;n.<br />\r\n<br />\r\nThư đồng n&agrave;y nh&igrave;n xem tuổi c&ograve;n nhỏ, nh&atilde;n lực ngược lại l&agrave; cực độc &aacute;c, liếc mắt một c&aacute;i liền ph&acirc;n biệt ra được Đ&agrave;o Tiềm th&acirc;n phận c&ugrave;ng t&agrave;i lực.<br />\r\n<br />\r\nTr&ecirc;n mặt vừa nở rộ mở rực rỡ nụ cười, quay đầu liền đưa cho Đ&agrave;o Tiềm sau lưng đi l&ecirc;n, từ một vị nh&igrave;n qua ch&iacute;nh l&agrave; đại nho r&acirc;u d&agrave;i l&atilde;o giả dẫn theo bảy t&aacute;m vị đại tộc tử đệ.<br />\r\n<br />\r\nBoong tàu vẫn c&ograve;n c&oacute; đ&oacute;n kh&aacute;ch thư đồng, mặc d&ugrave; l&agrave; phần phật một ch&uacute;t v&acirc;y quanh, nhưng phản ứng cũng đều l&agrave; như vậy, đều đi ngh&ecirc;nh những c&aacute;i kia cửa h&agrave;ng lớn l&atilde;o bản, h&agrave;o sảng t&agrave;ng thư kh&aacute;ch, đại tộc đại nho loại h&igrave;nh.<br />\r\n<br />\r\nNgh&egrave;o kiết hủ lậu thư sinh?<br />\r\n<br />\r\nXin cứ tự nhi&ecirc;n.<br />\r\n<br />\r\nCũng may, Đ&agrave;o Tiềm cũng kh&ocirc;ng phải c&aacute;i g&igrave; kh&aacute;ch kh&iacute; người.<br />\r\n<br />\r\nTrực tiếp n&iacute;u lại một c&aacute;i thư đồng, từ trong tay hắn r&uacute;t qua một chồng thư mục đơn, m&agrave; ph&iacute;a sau cũng kh&ocirc;ng trở về liền hướng đ&atilde;i kh&aacute;ch người khoang thuyền đi đến.<br />\r\n<br />\r\nVừa đi, một b&ecirc;n c&uacute;i đầu nh&igrave;n về ph&iacute;a mục lục.<br />\r\n<br />\r\nThư mục đơn b&ecirc;n tr&ecirc;n dựa theo kinh, sử, tử, tập, chư đạo t&agrave;ng thư, tiểu thuyết h&iacute; kh&uacute;c, b&uacute;t đ&agrave;m tạp k&yacute; v&acirc;n v&acirc;n ph&acirc;n loại, trừ loại lớn phương ph&aacute;p ph&acirc;n loại, c&ograve;n chia nhỏ c&aacute;c loại kh&aacute;c biệt tiểu loại, đ&aacute;nh dấu kh&aacute;c biệt in ấn phi&ecirc;n bản.<br />\r\n<br />\r\nNếu như l&agrave; ban đầu th&acirc;n thể, kỳ thật đều kh&ocirc;ng cần c&agrave;ng th&acirc;m nhập đi v&agrave;o.<br />\r\n<br />\r\nChỉ cần đứng tại boong tàu, trực tiếp c&ugrave;ng hạ đơn đặt h&agrave;ng giao tiền, kh&ocirc;ng bao l&acirc;u liền c&oacute; thể đeo một c&aacute;i t&uacute;i lớn khỏa xuống thuyền. Nếu như mua đa tạ, l&atilde;o bản sẽ c&ograve;n cố &yacute; mời c&aacute;i khổ lực hỗ trợ đưa.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nNhưng lần n&agrave;y tới, l&agrave; ch&iacute;nh Đ&agrave;o Tiềm.<br />\r\n<br />\r\n&quot;Một chiếc s&aacute;ch thuyền, liền tương đương với một t&ograve;a tiểu h&igrave;nh thư viện.&quot;<br />\r\n<br />\r\n&quot;M&agrave; lại những s&aacute;ch n&agrave;y thuyền trừ sẽ b&aacute;n b&igrave;nh thường thư tịch b&ecirc;n ngo&agrave;i, sẽ c&ograve;n ph&aacute;i người đi xu&ocirc;i theo phụ c&aacute;c nơi thu mua thư tịch, nhất l&agrave; nh&igrave;n chằm chằm những c&aacute;i kia nh&agrave; ngh&egrave;o khổ tổ t&ocirc;ng tồn tại bản độc nhất b&iacute; s&aacute;ch, cho n&ecirc;n những s&aacute;ch n&agrave;y tr&ecirc;n thuyền thường thường sẽ xuất hiện một ch&uacute;t c&oacute; gi&aacute; trị kh&ocirc;ng nhỏ, hiếm c&oacute; đặc th&ugrave; s&aacute;ch hay.&quot;<br />\r\n<br />\r\n&quot;Nh&acirc;n y&ecirc;u quỷ luyến, diễm tục cấm thư, hoạ b&aacute;o nữ lang những n&agrave;y t&ugrave;y thời đều c&oacute; thể mua, đ&atilde; đến, xem trước một ch&uacute;t c&oacute; thể hay kh&ocirc;ng nhặt nhạnh chỗ tốt, thuận tiện cũng nghiệm chứng một v&agrave;i thứ.&quot;<br />\r\n<br />\r\n&quot;Những c&aacute;i kia thần ti&ecirc;n quỷ dị, si&ecirc;u ph&agrave;m tu h&agrave;nh c&aacute;c loại tồn tại, nếu như sẽ tại trang s&aacute;ch lưu lại vết t&iacute;ch... Nơi n&agrave;y, n&ecirc;n kh&ocirc;ng nhỏ cơ hội c&oacute; thể tiếp x&uacute;c đến a?&quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm trong mắt l&oacute;e ra &yacute; vị kh&ocirc;ng r&otilde; chi sắc, &aacute;nh mắt vẫn v&ocirc; &yacute; thức tại s&aacute;ch đơn b&ecirc;n tr&ecirc;n đi tuần tra.<br />\r\n<br />\r\nBỗng nhi&ecirc;n, hắn dừng bước.<br />\r\n<br />\r\n&Aacute;nh mắt rơi v&agrave;o thư mục đơn cuối c&ugrave;ng chỗ, nơi đ&oacute; c&oacute; một h&agrave;ng r&otilde; r&agrave;ng l&agrave; về sau mới th&ecirc;m v&agrave;o đi hồng sắc kiểu chữ.<br />\r\n<br />\r\nTr&ecirc;n viết: Văn Kh&uacute;c ph&uacute;c hộp!<br />\r\n<br />\r\nĐ&agrave;o Tiềm ch&acirc;n m&agrave;y hơi nh&iacute;u lại, tiện tay lại l&agrave; n&iacute;u lại một c&aacute;i ch&iacute;nh thao thao bất tuyệt Hướng mỗ tiệm s&aacute;ch l&atilde;o bản giới thiệu bản mới trường dạy vỡ l&ograve;ng t&ugrave;ng thư thư đồng.<br />\r\n<br />\r\nRất l&agrave; trực tiếp điểm n&agrave;y bốn chữ, hỏi:<br />\r\n<br />\r\n&quot;Văn Kh&uacute;c ta biết, c&aacute;i n&agrave;y ph&uacute;c hộp lại l&agrave; c&aacute;i g&igrave; &yacute; tứ?&quot;<br />\r\n<br />\r\nBị giữ chặt thư đồng tuy nhi&ecirc;n rất l&agrave; bất m&atilde;n, tuy nhi&ecirc;n tựa hồ cũng mang theo nhiệm vụ mang theo.<br />\r\n<br />\r\nThấy rốt cục c&oacute; kh&aacute;ch hỏi mới ti&ecirc;u thụ hạng mục, mặt m&agrave;y cũng lập tức bay bổng l&ecirc;n, dứt kho&aacute;t đối bao qu&aacute;t Đ&agrave;o Tiềm ở b&ecirc;n trong, quanh m&igrave;nh một v&ograve;ng kh&aacute;ch nh&acirc;n, hưng phấn giới thiệu n&oacute;i:<br />\r\n<br />\r\n&quot;Kh&aacute;ch qu&yacute; ng&agrave;i thế nhưng l&agrave; lập tức t&igrave;m tới đồ tốt.&quot;<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; ch&uacute;ng ta đ&ocirc;ng gia &#39;Gi&aacute;c thế bại quan&#39; mới nhất đẩy ra hạng mục, chư vị cũng đều biết được, ch&uacute;ng ta Trạng Nguy&ecirc;n h&agrave;o sẽ đi xu&ocirc;i theo phụ c&aacute;c nơi thu mua bản độc nhất b&iacute; s&aacute;ch.&quot;<br />\r\n<br />\r\n&quot;Luận đến t&agrave;ng thư số lượng, bản tỉnh thậm ch&iacute; cả quanh m&igrave;nh mấy tỉnh, trừ quan gia thư khố b&ecirc;n ngo&agrave;i, nh&agrave; ch&uacute;ng ta n&oacute;i thứ hai, chỉ sợ cũng kh&ocirc;ng nh&agrave; n&agrave;o d&aacute;m n&oacute;i l&agrave; đệ nhất.&quot;<br />\r\n<br />\r\n&quot;Ch&uacute;ng ta đ&ocirc;ng gia c&oacute; cảm gi&aacute;c chư vị đối s&aacute;ch chi &aacute;i, cố &yacute; thiết kế một c&aacute;i mới lạ c&aacute;ch chơi.&quot;<br />\r\n<br />\r\n&quot;Ch&uacute;ng ta chuyến n&agrave;y chuẩn bị tổng cộng m&ocirc;̣t ngàn Văn Kh&uacute;c ph&uacute;c hộp, mỗi một trong hộp đều c&oacute; giấu mười s&aacute;ch kh&ocirc;ng biết thư tịch, c&oacute; thường s&aacute;ch, cũng c&oacute; tr&acirc;n qu&yacute; bản độc nhất b&iacute; s&aacute;ch, hoặc l&agrave; hoạ b&aacute;o tuy&ecirc;n truyền trang.&quot;<br />\r\n<br />\r\n&quot;Ph&uacute;c hộp gi&aacute; cả, chia một khối đồng bạc, hai khối đồng bạc c&ugrave;ng năm khối đồng bạc ba loại.&quot;<br />\r\n<br />\r\n&quot;Chư vị, ch&uacute;ng ta đ&ocirc;ng gia hứa hẹn, cho d&ugrave; chưa thể mở ra gi&aacute; trị trăm nguy&ecirc;n, ng&agrave;n nguy&ecirc;n bản độc nhất b&iacute; s&aacute;ch, trong hộp c&ograve;n lại thư tịch gi&aacute; cả, cũng tuyệt kh&ocirc;ng thấp hơn ph&uacute;c hộp bản th&acirc;n gi&aacute; cả.&quot;<br />\r\n<br />\r\n&quot;Hoa &quot;<br />\r\n<br />\r\nThư đồng n&agrave;y một phen để người nhiệt huyết s&ocirc;i tr&agrave;o chào hàng ngữ n&oacute;i xong, Đ&agrave;o Tiềm lập tức liền thấy quanh m&igrave;nh rất nhiều mắt người đều s&aacute;ng l&ecirc;n, h&ocirc; hấp cũng gấp gấp r&uacute;t ch&uacute;t.<br />\r\n<br />\r\nM&agrave; ch&iacute;nh Đ&agrave;o Tiềm, giờ ph&uacute;t n&agrave;y ho&agrave;n to&agrave;n &aacute;p chế kh&ocirc;ng nổi nhả r&atilde;nh &yacute; nghĩ.<br />\r\n<br />\r\n&quot;Kia c&aacute;i g&igrave; gi&aacute;c thế bại quan, thật l&agrave; một c&aacute;i nh&acirc;n t&agrave;i.&quot;<br />\r\n<br />\r\n&quot;Thần mẹ n&oacute; Văn Kh&uacute;c ph&uacute;c hộp, c&aacute;i n&agrave;y kh&ocirc;ng phải liền l&agrave; phi&ecirc;n bản cổ đại hộp đen a?&quot;</p>', '2021-12-01 15:01:36', 4, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(307, 76, 'Bách Cầm Hí Tàn Sách', '6', '<p>N&agrave;y một mặt gian tướng thư đồng đem Văn Kh&uacute;c ph&uacute;c hộp phương ph&aacute;p n&oacute;i ra l&uacute;c, Đ&agrave;o Tiềm đ&atilde; có th&ecirc;̉ nghĩ đến quanh m&igrave;nh những người n&agrave;y phản ứng.<br />\r\n<br />\r\nC&oacute; n&agrave;y bản độc nhất b&iacute; s&aacute;ch dụ hoặc, tăng th&ecirc;m s&aacute;ch chủ thuyền hứa hẹn bảo đảm gi&aacute;, trừ c&ugrave;ng sinh &yacute; tương quan tiệm s&aacute;ch l&atilde;o bản, hoặc l&agrave; một ch&uacute;t thanh t&acirc;m quả dục đại nho mọi người.<br />\r\n<br />\r\nNhững người c&ograve;n lại, hơn ph&acirc;n nửa l&agrave; chịu kh&ocirc;ng nổi.<br />\r\n<br />\r\nQuả nhi&ecirc;n, Đ&agrave;o Tiềm đảo mắt một v&ograve;ng, lập tức liền qu&eacute;t đến từng đ&ocirc;i tỏa s&aacute;ng con mắt.<br />\r\n<br />\r\n&quot;Đ&aacute;nh bạc hại người a!&quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm nội t&acirc;m khuy&ecirc;n nhủ, tất nhi&ecirc;n l&agrave; kh&ocirc;ng người nghe thấy.<br />\r\n<br />\r\nĐ&atilde;i kh&aacute;ch người trong khoang thuyền, chỉ vang l&ecirc;n một c&acirc;u &quot;Mang bọn ta đi&quot;, chợt một đ&aacute;m người ngay tại mấy c&aacute;i thư đồng dẫn đầu hạ, soạt hướng ph&iacute;a c&aacute;i n&agrave;o đ&oacute; khoang t&agrave;u đi.<br />\r\n<br />\r\nĐ&agrave;o Tiềm nhả r&atilde;nh xong, cười hắc hắc, cũng theo sau.<br />\r\n<br />\r\nTuy n&oacute;i trong t&uacute;i kh&ocirc;ng c&oacute; g&igrave; tiền bạc, nhưng kiến thức một phen lu&ocirc;n lu&ocirc;n tốt.<br />\r\n<br />\r\nKh&ocirc;ng bao l&acirc;u, một đ&aacute;m người xuất hiện tại một c&aacute;i so s&aacute;nh rộng r&atilde;i s&aacute;ng ngời trong khoang thuyền, ph&iacute;a trước xuất hiện giản dị ngăn c&aacute;ch, ba c&aacute;i cực lớn gi&aacute; s&aacute;ch trưng b&agrave;y vững vững v&agrave;ng v&agrave;ng.<br />\r\n<br />\r\nMỗi c&aacute;i tr&ecirc;n gi&aacute; s&aacute;ch đều đặt v&agrave;o đại lượng vẻ ngo&agrave;i cực giản nhưng lộ ra tinh xảo kh&iacute; tức sơn hộp, &aacute;nh đ&egrave;n chiếu rọi xuống, ch&oacute;i mắt v&ocirc; c&ugrave;ng.<br />\r\n<br />\r\nBọn n&agrave;y thư sinh, cử nh&acirc;n, đại tộc tử đệ, thư họa đại gia vừa mới đứng vững, ngăn c&aacute;ch b&ecirc;n trong ngh&ecirc;nh đ&oacute;n một vị trường b&agrave;o chưởng quỹ, kh&iacute; chất nho nh&atilde;, trước đối mọi người vừa chắp tay, sau đ&oacute; chủ động cao giọng giới thiệu n&oacute;i:<br />\r\n<br />\r\n&quot;Chư vị kh&aacute;ch qu&yacute;, hoan ngh&ecirc;nh thể nghiệm ch&uacute;ng ta Trạng Nguy&ecirc;n h&agrave;o mới nhất mua s&aacute;ch hạng mục.&quot;<br />\r\n<br />\r\n&quot;Bởi v&igrave; l&agrave; lần đầu, cho n&ecirc;n đ&ocirc;ng gia cố &yacute; đ&atilde; th&ocirc;ng b&aacute;o, tuyệt kh&ocirc;ng kiếm chư vị một ph&acirc;n tiền, nơi n&agrave;y mỗi một phần ph&uacute;c hộp, trong hộp thư tịch tổng gi&aacute; trị, đều vượt qua ph&uacute;c hộp bản th&acirc;n gi&aacute; cả.&quot;<br />\r\n<br />\r\n&quot;Thi&ecirc;n Địa Nh&acirc;n ba gi&aacute; s&aacute;ch, đối ứng giá cả là 5 đồng bạc, hai đồng bạc c&ugrave;ng một đồng bạc.&quot;<br />\r\n<br />\r\n&quot;Lần n&agrave;y bán h&ecirc;́t, lại c&oacute; n&agrave;y hoạt động, ch&iacute; &iacute;t cũng l&agrave; nửa th&aacute;ng sau.&quot;<br />\r\n<br />\r\n&quot;Chư vị đều l&agrave; biết s&aacute;ch y&ecirc;u s&aacute;ch người, tại hạ kh&ocirc;ng ngại trước tiết lộ một điểm nhỏ b&iacute; mật, nếu c&oacute; c&ugrave;ng đ&ocirc;ng gia quen biết kh&aacute;ch nh&acirc;n, ng&agrave;n vạn kh&ocirc;ng thể n&oacute;i l&agrave; ta n&oacute;i.&quot;<br />\r\n<br />\r\n&quot;Theo ta được biết a, đ&ocirc;ng gia đem &laquo; theo vườn di thư &raquo;, &laquo; Gi&agrave; Lam t&agrave;n kinh &raquo;, &laquo; quỷ h&ugrave;ng kh&uacute;c &raquo; c&aacute;i n&agrave;y ba bản gi&aacute; trị mấy trăm thậm ch&iacute; hơn một ng&agrave;n đồng bạc bản độc nhất b&iacute; s&aacute;ch, ph&acirc;n biệt giấu ở Thi&ecirc;n Địa Nh&acirc;n ba loại ph&uacute;c trong hộp.&quot;<br />\r\n<br />\r\n&quot;Trừ ngo&agrave;i ra, c&ograve;n c&oacute; như l&agrave; &laquo; thiền &yacute; tập &raquo;, &laquo; ngọc đ&agrave;i vịnh &raquo;, &laquo; Đạo Đức Kinh Minh Th&agrave;nh c&ocirc;ng viết tay bản &raquo;, &laquo; vệ c&ocirc;ng hiểu biết đồ thủ khắc bản &raquo; ... v&acirc;n v&acirc;n tr&acirc;n qu&yacute; s&aacute;ch.&quot;<br />\r\n<br />\r\n&quot;C&oacute; thể khai ra loại kia b&iacute; s&aacute;ch, gi&aacute; trị bao nhi&ecirc;u, đều xem chư vị số phận.&quot;<br />\r\n<br />\r\n&quot;T&ecirc; &quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm l&agrave;m tiểu trong suốt lẫn trong đ&aacute;m người, giờ ph&uacute;t n&agrave;y nhịn kh&ocirc;ng được c&oacute; ch&uacute;t chua.<br />\r\n<br />\r\nHiện tại hắn x&aacute;c định, chiếc n&agrave;y s&aacute;ch thuyền đ&ocirc;ng gia, tuyệt bức l&agrave; c&aacute;i l&atilde;o gian thương.<br />\r\n<br />\r\nĐầu ti&ecirc;n l&agrave; b&agrave;y ra mới lạ hộp đen c&aacute;ch chơi, hiện tại lại n&eacute;m ra ngo&agrave;i phần thưởng l&agrave;m mồi nhử, tăng th&ecirc;m nơi n&agrave;y đều l&agrave; v&ocirc; c&ugrave;ng tinh chuẩn mục ti&ecirc;u đ&aacute;m người, ngay cả s&agrave;ng chọn đều kh&ocirc;ng cần.<br />\r\n<br />\r\nC&acirc;u c&aacute;?<br />\r\n<br />\r\nKh&ocirc;ng, c&aacute;i thằng n&agrave;y căn bản ch&iacute;nh l&agrave; trực tiếp m&ograve; c&aacute;.<br />\r\n<br />\r\nĐ&agrave;o Tiềm &yacute; niệm n&agrave;y vừa dứt hạ, đ&aacute;m người lập tức c&oacute; một người la lớn: &quot;Ta, để cho ta tới, quyển s&aacute;ch sinh vận kh&iacute; lu&ocirc;n lu&ocirc;n kh&ocirc;ng tệ, s&aacute;ng nay đi ra ngo&agrave;i c&ograve;n nhặt một g&oacute;c ng&acirc;n h&agrave;o.&quot;<br />\r\n<br />\r\nĐang khi n&oacute;i chuyện, một người mặc bạch b&agrave;o, trong tay cầm quạt, c&aacute;ch ăn mặc tao bao thư sinh đi tới.<br />\r\n<br />\r\nG&otilde; g&otilde; quỹ diện, đương đương mấy lần, tám khối đồng bạc kết th&uacute;c, sau đ&oacute; hắn một mặt kh&ocirc;ng quan t&acirc;m tiền thư sinh cũng l&agrave; rất c&oacute; tiền khoe khoang biểu lộ, k&eacute;o căng d&ugrave;ng sức n&oacute;i: &quot;Ba loại ph&uacute;c hộp, đều cho ta đến c&aacute;i trước.&quot;<br />\r\n<br />\r\nChưởng quỹ kia cười h&igrave; h&igrave; d&ograve; x&eacute;t tay &aacute;o thu, quay đầu ra hiệu hai c&aacute;i thư đồng ph&acirc;n biệt chuyển đến người, ph&uacute;c hộp, hắn th&igrave; tự m&igrave;nh đi n&acirc;ng tới một c&aacute;i chữ thi&ecirc;n ph&uacute;c hộp.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nBa ph&uacute;c hộp, xếp th&agrave;nh một h&agrave;ng.<br />\r\n<br />\r\nChưởng quỹ kia b&agrave;n tay một dẫn, l&agrave;m ra cung thỉnh ng&agrave;i mở tư th&aacute;i.<br />\r\n<br />\r\nThư sinh v&ocirc; &yacute; thức tưởng tượng d&acirc;n cờ bạc mở b&agrave;i l&uacute;c trước dạng xoa xoa tay, &yacute; thức được bất nh&atilde;, liền cố n&eacute;n l&agrave;m ra một bộ kh&ocirc;ng lắm để &yacute; thần sắc, d&ugrave;ng trong tay quạt giấy trước nhẹ nh&agrave;ng đẩy ra chữ nh&acirc;n ph&uacute;c hộp.<br />\r\n<br />\r\nSơn trong hộp, quả nhi&ecirc;n trưng b&agrave;y mười s&aacute;ch thư tịch.<br />\r\n<br />\r\nThư sinh lấy tay lấy ra, nhanh ch&oacute;ng đọc qua, nơi n&agrave;y mỗi một người đều xem như uy&ecirc;n b&aacute;c chi sĩ, &aacute;nh mắt qu&eacute;t qua cũng đều xem cho r&otilde; r&agrave;ng.<br />\r\n<br />\r\n&quot;&laquo; kim văn b&iacute; gi&aacute;m &raquo;, &laquo; Thạch Hổ lục &raquo;, &laquo; Lưu thị gia huấn &raquo;, &laquo; Ma Đ&ocirc; tạp k&yacute; &raquo;... L&agrave; ch&uacute;t thường s&aacute;ch, tổng gi&aacute; trị ho&agrave;n to&agrave;n ch&iacute;nh x&aacute;c kh&ocirc;ng ngừng một khối đồng bạc.&quot;<br />\r\n<br />\r\nTrong đ&aacute;m người, c&oacute; người miễn ph&iacute; gi&aacute;m định nói.<br />\r\n<br />\r\nThư sinh kia thấy kh&ocirc;ng c&oacute; kinh hỉ, bĩu m&ocirc;i, cũng kh&ocirc;ng thu thập, trực tiếp mở ra Địa tự ph&uacute;c hộp.<br />\r\n<br />\r\nL&uacute;c n&agrave;y tốc độ tay c&agrave;ng nhanh, mười bản s&aacute;ch cũng lập tức xuất hiện trong mắt mọi người.<br />\r\n<br />\r\nBan đầu mấy quyển tất cả mọi người l&agrave; sắc mặt như thường, nhưng rất nhanh, thứ mười bản xuất hiện, thư sinh kia k&eacute;m ch&uacute;t ph&aacute;t ra &quot;Ngao&quot; một tiếng.<br />\r\n<br />\r\nNgười chung quanh, đều l&agrave; trợn tr&ograve;n hai mắt, h&ocirc; hấp đi theo gấp r&uacute;t.<br />\r\n<br />\r\nC&oacute; người trực tiếp ao ước hoảng sợ n&oacute;i:<br />\r\n<br />\r\n&quot;L&agrave; &laquo; vệ c&ocirc;ng hiểu biết đồ thủ khắc bản &raquo;, cuốn s&aacute;ch n&agrave;y ch&iacute;nh l&agrave; trăm năm trước đại nho vệ c&ocirc;ng sở lưu, lần đầu chỉ khắc ấn mấy chục bản, hơn ph&acirc;n nửa đ&atilde; di thất, b&acirc;y giờ một bản gi&aacute; trị ch&iacute; &iacute;t hai trăm đồng bạc.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i thằng n&agrave;y, chẳng lẽ s&aacute;ng nay giẫm cứt ch&oacute;.&quot;<br />\r\n<br />\r\nThư sinh kia giờ ph&uacute;t n&agrave;y t&acirc;m t&igrave;nh v&ocirc; c&ugrave;ng tốt, cũng kh&ocirc;ng c&oacute; để &yacute; người b&ecirc;n ngo&agrave;i ao ước hạ chửi bới.<br />\r\n<br />\r\nTrước đem n&agrave;y đắt s&aacute;ch tr&acirc;n trọng bỏ v&agrave;o trong ngực, sau đ&oacute; đầy c&otilde;i l&ograve;ng mong đợi, xoa xoa tay, mở ra sau c&ugrave;ng ph&ograve;ng chữ Thi&ecirc;n ph&uacute;c hộp.<br />\r\n<br />\r\nLần n&agrave;y, tất cả mọi người nghển cổ n&eacute;m đi lo lắng &aacute;nh mắt.<br />\r\n<br />\r\nBan đầu cũng l&agrave; thường thường kh&ocirc;ng c&oacute; g&igrave; lạ, cho đến đếm ngược cuốn thứ ba xuất hiện, h&iacute;t một hơi l&atilde;nh kh&iacute; thanh &acirc;m vang vọng khoang t&agrave;u.<br />\r\n<br />\r\n&quot;L&agrave; &laquo; ngọc đ&agrave;i vịnh &raquo;!&quot;<br />\r\n<br />\r\n&quot;Nh&igrave;n n&agrave;y phẩm tướng, ch&iacute; &iacute;t gi&aacute; trị ba trăm đồng bạc.&quot;<br />\r\n<br />\r\n&quot;Đ&aacute;ng chết, c&aacute;i thằng n&agrave;y s&aacute;ng nay tuyệt đối giẫm kh&ocirc;ng ngừng một đống cứt ch&oacute;.&quot;<br />\r\n<br />\r\n&quot;Chưởng quỹ ta cũng muốn mở, cho ta một loại tới một c&aacute;i, kh&ocirc;ng, mỗi một loại đều đến b&ecirc;n tr&ecirc;n ba hộp.&quot;<br />\r\n<br />\r\n&quot;C&ograve;n c&oacute; ta, cho ta to&agrave;n bộ thượng thi&ecirc;n danh tiếng ph&uacute;c hộp, 5 c&aacute;i l&ecirc;n.&quot;<br />\r\n<br />\r\n&quot;Ph&ograve;ng chữ Nh&acirc;n ph&uacute;c hộp, 20 hộp, mau mau.&quot;<br />\r\n<br />\r\n&quot;Ba loại, c&aacute;c 5 hộp.&quot;<br />\r\n<br />\r\n...<br />\r\n<br />\r\nTrong khoang thuyền, hơi kh&ocirc;ng khống chế được.<br />\r\n<br />\r\nĐ&agrave;o Tiềm c&aacute;i n&agrave;y tiểu trong suốt, k&eacute;m ch&uacute;t bị chen đến ngo&agrave;i cửa đi.<br />\r\n<br />\r\nNh&igrave;n xem bọn n&agrave;y trước l&uacute;c n&agrave;y đều biểu hiện nho nh&atilde; lễ độ, phi thường nho nh&atilde; thư sinh, đại nho c&ograve;n c&oacute; t&agrave;ng thư nh&agrave;, b&acirc;y giờ biểu hiện c&ugrave;ng chợ b&aacute;n thức ăn tranh mua đ&aacute;nh g&atilde;y rau quả đại gia đại mụ cũng kh&ocirc;ng c&oacute; g&igrave; sai biệt.<br />\r\n<br />\r\nĐ&agrave;o Tiềm lắc đầu, trực tiếp nhả r&atilde;nh n&oacute;i: &quot;Đ&aacute;nh bạc, hại người a.&quot;<br />\r\n<br />\r\nC&aacute;i gọi l&agrave; Văn Kh&uacute;c ph&uacute;c hộp, bản chất c&ugrave;ng đ&aacute;nh bạc rút thưởng kh&ocirc;ng c&oacute; g&igrave; sai biệt.<br />\r\n<br />\r\nNhất l&agrave; c&aacute;i n&agrave;y Trạng Nguy&ecirc;n xưng l&agrave; chào hàng, c&ograve;n sử dụng rất nhiều phương ph&aacute;p.<br />\r\n<br />\r\nTỉ như mở đầu n&agrave;y li&ecirc;n tiếp mở ra hai bản đắt s&aacute;ch thư sinh, theo Đ&agrave;o Tiềm, tuyệt trốn kh&ocirc;ng &quot;Kẻ lừa gạt&quot; hiềm nghi.<br />\r\n<br />\r\nXuy&ecirc;n thủng tất cả phương ph&aacute;p Đ&agrave;o Tiềm, giờ ph&uacute;t n&agrave;y vốn n&ecirc;n quay người rời đi.<br />\r\n<br />\r\nMở hộp đen?<br />\r\n<br />\r\nL&atilde;o tử ngại nhiều tiền?<br />\r\n<br />\r\nLoại &yacute; nghĩ n&agrave;y vừa sinh ra c&ograve;n chưa rơi xuống, đột ngột, Đ&agrave;o Tiềm th&acirc;n h&igrave;nh bỗng nhi&ecirc;n định trụ, đầu l&acirc;u quay lại, &aacute;nh mắt đột nhi&ecirc;n kh&oacute;a chặt người kia danh tiếng gi&aacute; s&aacute;ch, tối cao một loạt, chỗ tốt nhất một c&aacute;i ph&uacute;c hộp.<br />\r\n<br />\r\nHắn loại phản ứng n&agrave;y, nguồn gốc từ vừa mới một khắc n&agrave;y, cảm nhận được cái chủng loại kia quen thuộc rung động cảm gi&aacute;c.<br />\r\n<br />\r\nĐ&agrave;o Tiềm tại bị trảm thủ trước từ n&agrave;y ch&iacute; sĩ trong đầu đ&agrave;o m&oacute;c ra &quot;T&agrave;n quyết&quot; l&uacute;c, từng c&oacute; đồng dạng cảm thụ.<br />\r\n<br />\r\n&quot;Si&ecirc;u ph&agrave;m cảm gi&aacute;c?&quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm đ&aacute;y l&ograve;ng, lập tức d&acirc;ng l&ecirc;n một loại n&agrave;o đ&oacute; suy đo&aacute;n.<br />\r\n<br />\r\nKh&ocirc;ng đợi hắn lại nhiều nghĩ lại, tho&aacute;ng nh&igrave;n người kia danh tiếng tr&ecirc;n gi&aacute; s&aacute;ch sơn hộp lấy tốc độ m&agrave; mắt thường cũng c&oacute; thể thấy được tại biến mất.<br />\r\n<br />\r\nChỉ sợ d&ugrave;ng kh&ocirc;ng bao l&acirc;u, để hắn c&oacute; rung động cảm gi&aacute;c n&agrave;y một hộp liền sẽ bị mua đi.<br />\r\n<br />\r\nKhẽ cắn m&ocirc;i, Đ&agrave;o Tiềm c&oacute; động t&aacute;c.<br />\r\n<br />\r\nDo dự?<br />\r\n<br />\r\nNếu như l&agrave; gi&aacute; trị năm khối đồng bạc chữ thi&ecirc;n ph&uacute;c hộp, n&oacute;i kh&ocirc;ng chừng hắn thật hội.<br />\r\n<br />\r\nChữ nh&acirc;n, chỉ một khối đồng bạc, ta giao nổi... Mang theo &yacute; niệm n&agrave;y, Đ&agrave;o Tiềm qu&aacute;i lực bắn ra, ngạnh sinh sinh từ bi&ecirc;n giới chỗ lại chen v&agrave;o.<br />\r\n<br />\r\nNhanh ch&oacute;ng đi v&agrave;o tủ trước, th&ocirc; bạo k&eacute;o tới một c&aacute;i thư đồng, đem một khối đồng bạc nh&eacute;t v&agrave;o trong tay hắn, sau đ&oacute; chỉ v&agrave;o mục ti&ecirc;u sơn hộp n&oacute;i:<br />\r\n<br />\r\n&quot;C&aacute;i kia, ta chỉ cần c&aacute;i kia.&quot;<br />\r\n<br />\r\nTuy nhi&ecirc;n lấy tiền, nhưng k&eacute;m ch&uacute;t bị gh&igrave;m n&ocirc;n thư đồng kh&ocirc;ng mấy vui vẻ.<br />\r\n<br />\r\nXoay người đi cho Đ&agrave;o Tiềm lấy ph&uacute;c hộp l&uacute;c, miệng b&ecirc;n trong im ắng n&oacute;i thầm lấy: &quot;Người ngh&egrave;o ngh&egrave;o coi trọng, có th&ecirc;̉ mở c&aacute;i g&igrave; s&aacute;ch l&agrave; xem vận kh&iacute;, tuyển c&aacute;i g&igrave; cổ qu&aacute;i kỳ lạ vị tr&iacute; cũng l&agrave; v&ocirc; dụng.&quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm kh&ocirc;ng nghe thấy thư đồng nhả r&atilde;nh, lực ch&uacute; &yacute; tất cả n&agrave;y cổ qu&aacute;i ph&uacute;c hộp b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nKh&ocirc;ng bao l&acirc;u, hắn v&ocirc; kinh v&ocirc; hiểm đạt được thuộc về m&igrave;nh sơn hộp.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; lập tức mở ra, m&agrave; ch&iacute;nh l&agrave; &ocirc;m ph&uacute;c hộp lại gạt ra.<br />\r\n<br />\r\nĐ&agrave;o Tiềm nguy&ecirc;n bản &yacute; nghĩ: Cầm trước ph&uacute;c hộp về nh&agrave;, một m&igrave;nh mở ra, thật c&oacute; thu hoạch cũng c&oacute; thể giữ b&iacute; mật.<br />\r\n<br />\r\nNhưng rất nhanh, hắn lại tự m&igrave;nh phủ định.<br />\r\n<br />\r\n&quot;Nghiệm chứng trước, nếu l&agrave; n&agrave;y rung động, loại kia cảm gi&aacute;c, c&ugrave;ng ta suy đo&aacute;n đồng dạng.&quot;<br />\r\n<br />\r\n&quot;Ta tại c&aacute;i n&agrave;y quen thuộc vừa xa lạ, hắc &aacute;m lại phồn vinh quỷ dị thế giới, liền c&oacute; sống y&ecirc;n phận, thậm ch&iacute; nhiều hơn khả năng.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Kim thủ chỉ cũng tốt, hack cũng được, đến m&atilde;nh liệt ch&uacute;t đi.&quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm đ&aacute;y l&ograve;ng lải nhải, đi v&agrave;o nơi hẻo l&aacute;nh chỗ, hai tay xoa xoa, cực giống si m&ecirc; d&acirc;n cờ bạc.<br />\r\n<br />\r\nCũng may giờ ph&uacute;t n&agrave;y trong khoang thuyền, giống như hắn h&agrave;nh động như vậy người cũng kh&ocirc;ng &iacute;t, c&oacute; thể n&oacute;i l&agrave; kh&ocirc;ng ch&uacute;t n&agrave;o thu h&uacute;t.<br />\r\n<br />\r\nXoạch một tiếng, Đ&agrave;o Tiềm mở ra ph&uacute;c hộp.<br />\r\n<br />\r\nKh&ocirc;ng cần đi đọc s&aacute;ch t&ecirc;n, kh&ocirc;ng cần đi đọc qua nội dung.<br />\r\n<br />\r\nĐ&agrave;o Tiềm lần theo n&agrave;y &quot;Kh&ocirc;ng khỏi cảm gi&aacute;c&quot;, trực tiếp kh&oacute;a chặt mười bản s&aacute;ch b&ecirc;n trong cuốn thứ ba.<br />\r\n<br />\r\nChưa lấy ra, chỉ xốc l&ecirc;n trước hai bản, &aacute;nh mắt n&eacute;m đi l&ecirc;n.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; một bản cực mỏng s&aacute;ch, b&igrave;a s&aacute;ch m&agrave;u xanh đen, cũ kỹ, tr&agrave;n đầy nếp uốn, nhưng kh&ocirc;ng c&oacute; g&igrave; vết bẩn.<br />\r\n<br />\r\nT&ecirc;n s&aacute;ch l&agrave; lấy một loại xi&ecirc;u xi&ecirc;u vẹo vẹo, cực giống n&ograve;ng nọc cổ triện viết, nhờ c&oacute; ban đầu th&acirc;n thể c&oacute; chỗ đọc lướt qua, kh&ocirc;ng chướng ngại nhận ra.<br />\r\n<br />\r\n&quot;M&ocirc; phỏng chim mười bộ phim?&quot;<br />\r\n<br />\r\nMột c&aacute;i cực cổ qu&aacute;i t&ecirc;n s&aacute;ch, bị Đ&agrave;o Tiềm nhận ra.<br />\r\n<br />\r\nKinh ngạc, Đ&agrave;o Tiềm trực tiếp lật ra tờ thứ nhất.<br />\r\n<br />\r\nThu v&agrave;o trong mắt, r&otilde; r&agrave;ng l&agrave; một loại n&agrave;o đ&oacute; bức hoạ s&aacute;ch nhỏ, thượng diện vẽ lấy một ch&uacute;t kh&ocirc;ng mặt tiểu nh&acirc;n, cứng rắn cố chấp lấy tứ chi, m&ocirc; phỏng lấy một &iacute;t động vật.<br />\r\n<br />\r\nThứ n&agrave;y, Đ&agrave;o Tiềm kiếp trước cũng biết, c&ugrave;ng loại Ngũ Cầm h&iacute; một loại vận động tiểu thủ s&aacute;ch.<br />\r\n<br />\r\nC&ugrave;ng tu luyện b&iacute; tịch, n&ecirc;n kh&ocirc;ng hề quan hệ.<br />\r\n<br />\r\nĐ&agrave;o Tiềm ch&iacute;nh nghĩ như vậy, c&oacute; thể theo đầu ng&oacute;n tay hắn chạm đến n&agrave;y s&aacute;ch.<br />\r\n<br />\r\nBỗng dưng, cả người một c&aacute;i chớp mắt hoảng hốt, c&aacute;i kia vốn l&agrave; xi&ecirc;u xi&ecirc;u vẹo vẹo t&ecirc;n s&aacute;ch tựa như lưu động đứng l&ecirc;n, phảng phất l&agrave; ảo gi&aacute;c, lại thay c&aacute;i t&ecirc;n mới.<br />\r\n<br />\r\nB&aacute;ch Cầm H&iacute;!<br />\r\n<br />\r\nKh&ocirc;ng đợi Đ&agrave;o Tiềm suy tư, trong đầu một đo&agrave;n tin tức lưu bỗng nhi&ecirc;n nổ tung.<br />\r\n<br />\r\nBan đầu hỗn loạn, nhưng lập tức liền h&igrave;nh th&agrave;nh lần trước hắn từng nh&igrave;n qua cổ qu&aacute;i c&aacute;ch thức:<br />\r\n<br />\r\n【 ch&iacute; t&ecirc;n: B&aacute;ch Cầm H&iacute; (t&agrave;n s&aacute;ch). 】<br />\r\n<br />\r\n【 ch&iacute; loại: C&ocirc;ng ph&aacute;p. 】<br />\r\n<br />\r\n【 ch&iacute; thuật: T&aacute;m mươi năm trước một vị nh&acirc;n loại tu sĩ, tự xưng &quot;B&aacute;ch Cầm l&atilde;o nh&acirc;n&quot;, tự s&aacute;ng tạo phương ph&aacute;p tu h&agrave;nh, th&agrave;nh s&aacute;ch t&ecirc;n l&agrave; &laquo; B&aacute;ch Cầm H&iacute; &raquo;, n&agrave;y ph&aacute;p tu h&agrave;nh, c&oacute; thể dần lột đi h&igrave;nh người, đến B&aacute;ch Cầm chi lực, luyện chi chỗ s&acirc;u c&agrave;ng c&oacute; thể th&acirc;n thể h&oacute;a B&aacute;ch Cầm chi h&igrave;nh, b&ecirc;n tr&ecirc;n c&oacute; thể ngao du thi&ecirc;n khung, hạ có th&ecirc;̉ lặn rơi Ho&agrave;ng Tuyền. Ph&aacute;p n&agrave;y giấu giếm hung lệ, ủ th&agrave;nh B&aacute;ch Cầm chi loạn, gặp tai hoạ người hơn vạn người, khiến đ&agrave; huyện khu Vực thập thất cửu kh&ocirc;ng, cho n&ecirc;n đ&atilde; th&agrave;nh s&aacute;ch chi thư hơn ph&acirc;n nửa bị hủy, chỉ một hai s&aacute;ch bị ngụy th&agrave;nh hắn s&aacute;ch, truyền lưu thế gian. 】<br />\r\n<br />\r\n【 ch&uacute; một: N&agrave;y t&agrave;n s&aacute;ch chiếm to&agrave;n thư chừng mười phần c&oacute; một, nhưng tu h&agrave;nh đại giới tuyệt kh&ocirc;ng cải biến, một khi bắt đầu tập luyện, ban đầu đem bỏ đi h&igrave;nh người, tiếp theo l&agrave; nh&acirc;n t&acirc;m nh&acirc;n t&iacute;nh, cho đến ho&agrave;n to&agrave;n bị cầm th&uacute; chi t&iacute;nh tr&agrave;n ngập hồn linh, từ đ&acirc;y h&oacute;a th&agrave;nh y&ecirc;u dị chi vật, tung ho&agrave;nh thi&ecirc;n địa, si&ecirc;u tho&aacute;t tự tại. 】<br />\r\n<br />\r\n【 ch&uacute; hai: Muốn tu luyện ph&aacute;p n&agrave;y, cần trước thu thập trong s&aacute;ch chứa đựng giống chim chi huyết, lấy m&aacute;u h&agrave;nh ph&aacute;p, mới c&oacute; thể nhập m&ocirc;n. 】<br />\r\n<br />\r\n【 ch&uacute; ba: Đại giới c&oacute; thể miễn trừ! 】<br />\r\n<br />\r\n...<br />\r\n<br />\r\nKhi tất cả tin tức lưu ti&ecirc;u h&oacute;a xong, Đ&agrave;o Tiềm lại một lần nữa sửng sốt.</p>', '2021-12-01 15:04:08', 5, 1),
(308, 76, 'Tu Hành Giới đệ Nhất định Luật', '7', '<p>Trong đầu c&oacute; cổ qu&aacute;i c&aacute;ch thức h&igrave;nh ảnh, bao h&agrave;m cực lớn lượng tin tức, đến mức Đ&agrave;o Tiềm cũng sững sờ một l&aacute;t mới ti&ecirc;u h&oacute;a.<br />\r\n<br />\r\nNg&oacute;n tay đầu ti&ecirc;n l&agrave; bu&ocirc;ng lỏng, để s&aacute;ch quy vị, đi theo đem ph&uacute;c tr&ecirc;n nắp hộp.<br />\r\n<br />\r\nTr&ecirc;n mặt lộ ra một bộ kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o thu hoạch rất l&agrave; thất lạc thần sắc, n&atilde;o hải th&igrave; bắt đầu vuốt vuốt suy nghĩ.<br />\r\n<br />\r\nPhen n&agrave;y, trong đầu hắn c&agrave;ng đa nghi hơn nghi ngờ đạt được giải đ&aacute;p.<br />\r\n<br />\r\nĐầu ti&ecirc;n, hắn thật c&oacute; được c&ugrave;ng loại kim thủ chỉ hack đồ vật.<br />\r\n<br />\r\nCụ thể biểu hiện l&agrave; tại trong phạm vi nhất định, đối si&ecirc;u ph&agrave;m quỷ dị sự vật c&oacute; m&atilde;nh liệt cảm gi&aacute;c, tại đụng v&agrave;o hoặc l&agrave; ho&agrave;n to&agrave;n chưởng khống về sau, thậm ch&iacute; có th&ecirc;̉ trong đầu chải vuốt ra c&oacute; tương quan c&aacute;ch thức, lấy thuận tiện Đ&agrave;o Tiềm l&yacute; giải đặc th&ugrave; tin tức lưu.<br />\r\n<br />\r\nTrừ c&aacute;i đ&oacute; ra, linh hồn của hắn chỗ s&acirc;u c&oacute; dị dạng, tựa hồ có th&ecirc;̉ triệt ti&ecirc;u một &iacute;t c&ocirc;ng ph&aacute;p tu h&agrave;nh mang tới đại giới.<br />\r\n<br />\r\nTuy nhi&ecirc;n bởi vậy sinh ra c&agrave;ng nhiều vấn đề:<br />\r\n<br />\r\nC&aacute;i n&agrave;y thế giới c&oacute; phải hay kh&ocirc;ng tất cả c&ocirc;ng ph&aacute;p tu h&agrave;nh đều cần trả gi&aacute; đắt?<br />\r\n<br />\r\nC&ugrave;ng, hắn c&oacute; thể hay kh&ocirc;ng to&agrave;n bộ miễn trừ?<br />\r\n<br />\r\nSuy nghĩ lấp l&oacute;e đến nơi đ&acirc;y, Đ&agrave;o Tiềm lại nh&igrave;n về ph&iacute;a trong tay ph&uacute;c hộp.<br />\r\n<br />\r\nTuy nhi&ecirc;n bản th&acirc;n l&agrave; c&aacute;i Phi t&ugrave;, nhưng hắn hiện tại ho&agrave;n to&agrave;n ch&iacute;nh x&aacute;c đạt được một bản tu h&agrave;nh b&iacute; tịch, d&ugrave;ng một khối đồng bạc mua.<br />\r\n<br />\r\nChỉ l&agrave; c&aacute;i n&agrave;y gọi l&agrave; &quot;B&aacute;ch Cầm H&iacute; t&agrave;n s&aacute;ch&quot; b&iacute; tịch, từ những tin tức kia đến xem, hiển nhi&ecirc;n kh&ocirc;ng phải đứng đắn g&igrave; tu ti&ecirc;n c&ocirc;ng ph&aacute;p.<br />\r\n<br />\r\nGiấu giếm hung lệ... B&aacute;ch Cầm chi loạn... Khiến hơn vạn người gặp tai hoạ... Thập thất cửu kh&ocirc;ng... Những chữ n&agrave;y, nghe liền rất đ&aacute;ng sợ bộ d&aacute;ng.<br />\r\n<br />\r\nĐ&agrave;o Tiềm &ocirc;m ph&uacute;c hộp, l&ograve;ng c&oacute; lo lắng, nhưng lại c&oacute; ch&uacute;t nỗi buồn.<br />\r\n<br />\r\nKh&ocirc;ng đề cập tới con kia tại trảm thủ l&uacute;c mới c&oacute; t&aacute;c dụng kh&ocirc;ng biết t&agrave;n quyết, trong ngực b&iacute; tịch n&agrave;y, ch&iacute;nh l&agrave; hắn b&acirc;y giờ chỉ c&oacute; phương ph&aacute;p tu h&agrave;nh.<br />\r\n<br />\r\nBỏ lỡ, chẳng lẽ kh&ocirc;ng phải qu&aacute; đ&aacute;ng tiếc.<br />\r\n<br />\r\n&quot;Ta bị giết l&uacute;c tụng niệm kh&ocirc;ng biết t&agrave;n quyết, thu hoạch được 【 kh&ocirc;ng chết đặc t&iacute;nh 】, vốn n&ecirc;n trả gi&aacute; đắt l&agrave; th&acirc;n thể hư thối, linh hồn ng&acirc;y ng&ocirc;, h&oacute;a th&agrave;nh một loại t&ecirc;n l&agrave; &#39;Hồn Thi&#39; quỷ vật.&quot;<br />\r\n<br />\r\n&quot;Nhưng ta miễn trừ, cho n&ecirc;n đến kh&ocirc;ng chết đặc t&iacute;nh, lại kh&ocirc;ng cái gì hậu di chứng.&quot;<br />\r\n<br />\r\n&quot;Như tu luyện 【 B&aacute;ch Cầm H&iacute; t&agrave;n s&aacute;ch 】 cũng l&agrave; hiệu quả như thế, ta chẳng phải l&agrave; có th&ecirc;̉ bạch chơi n&agrave;y mười loại B&aacute;ch Cầm chi lực?&quot;<br />\r\n<br />\r\nSau c&ugrave;ng một đạo suy nghĩ đứng l&ecirc;n, Đ&agrave;o Tiềm tr&aacute;i tim nhịn kh&ocirc;ng được thẳng thắn nhảy.<br />\r\n<br />\r\nVẻ hưng phấn, tại đ&aacute;y mắt hiện l&ecirc;n.<br />\r\n<br />\r\nTuy nhi&ecirc;n Đ&agrave;o Tiềm cũng kh&ocirc;ng phải l&agrave; người lỗ m&atilde;ng, rất nhanh lại b&igrave;nh tĩnh xuống tới.<br />\r\n<br />\r\nTại c&aacute;i n&agrave;y ph&uacute;c hộp trong khoang thuyền tiếp tục đợi một l&aacute;t. Vững tin lại kh&ocirc;ng rung động cảm gi&aacute;c truyền đến về sau, Đ&agrave;o Tiềm quay người rời đi.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nSau đ&oacute;, Đ&agrave;o Tiềm tr&agrave;n đầy phấn khởi, cơ hồ đạp biến &quot;Trạng Nguy&ecirc;n h&agrave;o&quot; mỗi một c&aacute;i khoang t&agrave;u, chỉ cần l&agrave; lưu giữ s&aacute;ch chi địa, hắn đều đi một chuyến.<br />\r\n<br />\r\nHắn l&agrave; kho&aacute;i lạc, cũng l&agrave; khổ từng c&aacute;i c&ugrave;ng đi thư đồng của hắn, bị tr&igrave; ho&atilde;n kh&ocirc;ng &iacute;t thời gian cũng liền th&ocirc;i, c&oacute; thể c&aacute;i thằng n&agrave;y chỉ nh&igrave;n kh&ocirc;ng mua, thực tế l&agrave; m&oacute;c muốn chết.<br />\r\n<br />\r\nĐến đằng sau Trạng Nguy&ecirc;n h&agrave;o chư Tiếp Dẫn thư đồng liền cũng biết, tr&ecirc;n thuyền c&oacute; c&aacute;i ngh&egrave;o kiết hủ lậu thư sinh, &ocirc;m c&aacute;i Văn Kh&uacute;c ph&uacute;c hộp cả thuyền t&aacute;n loạn, chỉ định l&agrave; c&oacute; c&aacute;i g&igrave; mao bệnh.<br />\r\n<br />\r\nƯớc chừng nửa giờ sau, Đ&agrave;o Tiềm một mặt tiếc nuối đi đến boong t&agrave;u, đi theo ph&iacute;a sau c&aacute;i mặt kh&ocirc;ng biểu t&igrave;nh thư đồng.<br />\r\n<br />\r\nC&aacute;i sau l&agrave;m nghề phục vụ nh&acirc;n sĩ, lấy Trạng Nguy&ecirc;n h&agrave;o quản l&yacute; mức độ, tr&ecirc;n mặt hắn n&ecirc;n l&uacute;c n&agrave;o cũng đều treo để người xem như ở nh&agrave;, như mộc xu&acirc;n phong rực rỡ nụ cười.<br />\r\n<br />\r\nHắn ngay từ đầu đ&iacute;ch thật l&agrave; rất chuy&ecirc;n nghiệp, cho đến bồi Đ&agrave;o Tiềm đi dạo trải qua.<br />\r\n<br />\r\nĐ&agrave;o Tiềm đi ở ph&iacute;a trước, &aacute;nh mắt đ&atilde; bắt đầu d&ograve; x&eacute;t tr&ecirc;n bến t&agrave;u c&ograve;n lại s&aacute;ch thuyền, một b&ecirc;n th&igrave; n&oacute;i:<br />\r\n<br />\r\n&quot;Trạng Nguy&ecirc;n h&agrave;o danh bất hư truyện, quả thật l&agrave; t&agrave;ng thư như biển, chỉ l&agrave; h&ocirc;m nay qu&aacute; mức tiếc nuối, chưa thể t&igrave;m ta hợp mắt của ta duy&ecirc;n chi thư.&quot;<br />\r\n<br />\r\nPh&iacute;a sau hắn thư đồng nghe vậy, cuối c&ugrave;ng l&agrave; nhịn kh&ocirc;ng được trợn mắt một c&aacute;i.<br />\r\n<br />\r\nKh&oacute;e miệng co giật, giả cười n&oacute;i:<br />\r\n<br />\r\n&quot;Kh&aacute;ch qu&yacute; &aacute;nh mắt cao đ&aacute;ng lấy l&yacute; giải, mời ng&agrave;i ng&agrave;n vạn cũng đi đức hạnh h&agrave;o, s&aacute;ch L&acirc;m h&agrave;o, Thanh Phong h&agrave;o, tuyết lỏng h&agrave;o đi tới một lần, những s&aacute;ch n&agrave;y tr&ecirc;n thuyền c&oacute; lẽ c&oacute; ng&agrave;i y&ecirc;u th&iacute;ch chi thư.&quot;<br />\r\n<br />\r\nThoại &acirc;m rơi xuống, thư đồng n&agrave;y cũng mặc kệ Đ&agrave;o Tiềm phản ứng, quay người liền quay lại khoang t&agrave;u ch&agrave;o hỏi kh&aacute;ch nh&acirc;n kh&aacute;c đi.<br />\r\n<br />\r\nBị người bất &acirc;m bất dương chống đỡ một hồi, Đ&agrave;o Tiềm đ&atilde; kh&ocirc;ng giận, cũng kh&ocirc;ng c&oacute; nhả r&atilde;nh, chỉ l&agrave; kh&ocirc;ng để &yacute; cười cười.<br />\r\n<br />\r\nHai tay &ocirc;m chặt trong ngực ph&uacute;c hộp, lại phỏng đo&aacute;n nh&agrave; m&igrave;nh t&uacute;i tiền.<br />\r\n<br />\r\nChỉ c&ograve;n lại tám khối bạc hơn nguy&ecirc;n a, đến d&ugrave;ng &iacute;t đi ch&uacute;t, thật muốn xuất thủ, tu h&agrave;nh b&iacute; tịch vị thứ nhất, hoạ b&aacute;o nữ lang thứ hai, diễm tục cấm thư thứ ba, tiểu thuyết v&otilde; hiệp thứ tư... Đ&agrave;o Tiềm một b&ecirc;n hạ Trạng Nguy&ecirc;n h&agrave;o, bạch bạch bạch đạp đi c&aacute;i kh&aacute;c s&aacute;ch thuyền, một b&ecirc;n dưới đ&aacute;y l&ograve;ng t&iacute;nh to&aacute;n như thế n&agrave;o ti&ecirc;u x&agrave;i.<br />\r\n<br />\r\nĐ&aacute;ng tiếc, tiếp xuống lại l&agrave; gần nửa giờ, Đ&agrave;o Tiềm bằng nhanh nhất tốc độ đi dạo h&ecirc;́t c&ograve;n lại s&aacute;ch thuyền.<br />\r\n<br />\r\nThu hoạch?<br />\r\n<br />\r\nKh&ocirc;ng.<br />\r\n<br />\r\nMột bản như 【 B&aacute;ch Cầm H&iacute; t&agrave;n s&aacute;ch 】 như vậy tu h&agrave;nh b&iacute; tịch cũng kh&ocirc;ng.<br />\r\n<br />\r\nĐứng tại tr&ecirc;n bến t&agrave;u, Đ&agrave;o Tiềm một mặt sầu khổ chi sắc.<br />\r\n<br />\r\nNgay cả c&aacute;ch đ&oacute; kh&ocirc;ng xa n&agrave;y gọi l&agrave; &quot;Ho&aacute;n Kh&ecirc; h&agrave;o&quot; tr&ecirc;n mặt thuyền hoa mỹ lệ cảnh tr&iacute;, Đ&agrave;o Tiềm cũng kh&ocirc;ng c&oacute; h&agrave;o hứng đi xem.<br />\r\n<br />\r\nGần một đoạn thời gian đến nay, những s&aacute;ch n&agrave;y thuyền tuy nhi&ecirc;n mỗi ng&agrave;y đều đến, nhưng chỉ ngừng một hai c&aacute;i giờ, hiện tại thời gian nhanh đến.<br />\r\n<br />\r\nĐ&agrave;o Tiềm dự định ỷ v&agrave;o m&igrave;nh c&oacute; si&ecirc;u ph&agrave;m rađa m&agrave; tầm bảo h&agrave;o hứng, cũng hạ xuống xuống tới.<br />\r\n<br />\r\nNgầm thừa nhận m&igrave;nh Phi t&ugrave; vận kh&iacute;, quay người đi hướng một chiếc t&ecirc;n l&agrave; &quot;Tuyết lỏng h&agrave;o&quot; s&aacute;ch thuyền.<br />\r\n<br />\r\nHắn đ&atilde; so s&aacute;nh qua, tr&ecirc;n bến t&agrave;u s&aacute;ch thuyền mua b&aacute;n hơn ph&acirc;n nửa đều l&agrave; ch&iacute;nh bản s&aacute;ch, nhưng gi&aacute; cả m&agrave; n&oacute;i, tuyết lỏng h&agrave;o lớn nhất lợi &iacute;ch thực tế.<br />\r\n<br />\r\nKh&ocirc;ng t&igrave;m được cuốn thứ hai tu h&agrave;nh b&iacute; tịch, Đ&agrave;o Tiềm quyết định mua ch&uacute;t hoạ b&aacute;o nữ lang sổ, mấy bộ diễm tục cấm thư.<br />\r\n<br />\r\n&quot;Những n&agrave;y n&ecirc;n tốt nhất b&aacute;n, trước lời &iacute;t tiền, hồi hồi m&aacute;u lại n&oacute;i.&quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm n&oacute;i thầm, dự định tới kiến thức hạ c&aacute;i n&agrave;y thời đại, thuộc về nam nh&acirc;n tinh thần lương thực.<br />\r\n<br />\r\nThế nhưng cũng l&agrave; tại thời khắc n&agrave;y, bỗng nhi&ecirc;n, hắn đầu ti&ecirc;n l&agrave; ngửi được một trận đặc biệt thư tịch mốc meo sau m&ugrave;i vị.<br />\r\n<br />\r\nSau đ&oacute; lập tức, n&agrave;y quen thuộc &quot;Rung động cảm gi&aacute;c&quot; lần nữa hiển hiện.<br />\r\n<br />\r\nB&aacute; một c&aacute;i, Đ&agrave;o Tiềm bỗng nhi&ecirc;n quay người, nh&igrave;n về ph&iacute;a mặt nước.<br />\r\n<br />\r\nĐ&atilde; thấy c&aacute;ch đ&oacute; kh&ocirc;ng xa tr&ecirc;n mặt s&ocirc;ng, một chiếc đi qua một loại n&agrave;o đ&oacute; cải tiến thuyền đ&aacute;nh c&aacute; ch&iacute;nh l&aacute;i tới, hai t&ecirc;n người ch&egrave;o thuyền cộng đồng dao r&atilde;nh, đầu thuyền đứng một thần sắc mệt mỏi s&aacute;ch kh&aacute;ch, tr&ecirc;n thuyền th&igrave; chất đống đại lượng thư tịch, lấy d&acirc;y gai tr&oacute;i buộc, ph&ograve;ng ngừa rơi xuống trong nước s&ocirc;ng.<br />\r\n<br />\r\nC&ugrave;ng thuyền n&agrave;y tiếp nhận, ch&iacute;nh l&agrave; tuyết lỏng h&agrave;o.<br />\r\n<br />\r\nN&agrave;y s&aacute;ch kh&aacute;ch vội v&agrave;ng c&ugrave;ng một l&atilde;o th&agrave;nh thư đồng giao tiếp về sau, trực tiếp nhập b&ecirc;n trong khoang thuyền nghỉ ngơi đi.<br />\r\n<br />\r\nThư đồng kia chỉ huy người ch&egrave;o thuyền đem thư tịch mang l&ecirc;n boong t&agrave;u, nhưng lại ngăn lại bọn họ đem s&aacute;ch chuyển nhập trong khoang thuyền, m&agrave; ch&iacute;nh l&agrave; đối b&ecirc;n cạnh th&acirc;n một c&aacute;i mười mấy tuổi thực tập thư đồng ph&acirc;n ph&oacute; n&oacute;i:<br />\r\n<br />\r\n&quot;Những s&aacute;ch n&agrave;y vừa bị thu lại, chẳng những m&ugrave;i nấm mốc nồng đậm, tất cũng sinh con mọt loại h&igrave;nh ăn mọt s&aacute;ch trĩ.&quot;<br />\r\n<br />\r\n&quot;Ngay tại c&aacute;i n&agrave;y boong t&agrave;u b&agrave;y chỉnh tề, trước bạo chiếu mấy canh giờ lại v&agrave;o kho.&quot;<br />\r\n<br />\r\n&quot;Nếu c&oacute; mới kh&aacute;ch nh&igrave;n tr&uacute;ng s&aacute;ch n&agrave;y chồng b&ecirc;n trong một ch&uacute;t s&aacute;ch, kh&ocirc;ng ngại để bọn hắn chọn lựa, d&agrave;y s&aacute;ch b&aacute;n 50 tiền đồng, b&ecirc;n trong s&aacute;ch ba mươi, s&aacute;ch mỏng 20, nếu l&agrave; tự thiếp một loại, mười tiền đồng liền c&oacute; thể.&quot;<br />\r\n<br />\r\n&quot;Bất qu&aacute; l&agrave; c&aacute;i ngay cả cử nh&acirc;n đều kh&ocirc;ng phải ngh&egrave;o kiết hủ lậu l&atilde;o thư sinh cất giấu chi thư, kh&ocirc;ng qu&aacute; mức b&iacute; ẩn, kh&ocirc;ng qu&aacute; mức gi&aacute; trị, sau khi chết bị con của hắn lấy một khối đồng bạc b&aacute;n sạch sẽ.&quot;<br />\r\n<br />\r\n&quot;Đọc s&aacute;ch đến bạc đầu, ph&iacute; thời gian cả đời, tuy nhi&ecirc;n một nguy&ecirc;n Nhĩ.&quot;<br />\r\n<br />\r\nL&atilde;o th&agrave;nh thư đồng n&oacute;i xong, quay người cũng hướng trong khoang thuyền đi.<br />\r\n<br />\r\nTrở lại qu&aacute; tr&igrave;nh b&ecirc;n trong, kh&oacute;e mắt của hắn dư quang vừa hay nh&igrave;n thấy l&ecirc;n thuyền Đ&agrave;o Tiềm, nhưng hắn l&agrave;m như kh&ocirc;ng nh&igrave;n thấy, dưới ch&acirc;n gia tốc, nhanh như chớp kh&ocirc;ng c&ograve;n h&igrave;nh b&oacute;ng.<br />\r\n<br />\r\nHiển nhi&ecirc;n l&agrave; hạ quyết t&acirc;m, nếu như Đ&agrave;o Tiềm truy v&agrave;o đến, nhất định đụng tới cũng l&agrave; s&aacute;ch kh&aacute;c đồng.<br />\r\n<br />\r\nĐ&aacute;ng tiếc hắn nghĩ sai, Đ&agrave;o Tiềm vừa l&ecirc;n đến, thẳng đến n&agrave;y m&ugrave;i nấm mốc nồng đậm s&aacute;ch chồng.<br />\r\n<br />\r\nTrước đối ch&iacute;nh l&agrave;m việc hai vị người ch&egrave;o thuyền vừa chắp tay, sau đ&oacute; nh&igrave;n về ph&iacute;a n&agrave;y thực tập thư đồng, kh&aacute;ch kh&iacute; n&oacute;i:<br />\r\n<br />\r\n&quot;Vừa mới vị kia m&agrave; n&oacute;i ta cũng nghe thấy, những n&agrave;y s&aacute;ch mới ta vừa vặn c&oacute; ch&uacute;t hứng th&uacute; , c&oacute; thể hay kh&ocirc;ng chọn lựa?&quot;<br />\r\n<br />\r\n&quot;Kh&aacute;ch qu&yacute; mời!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nThực tập sinh thấy c&oacute; sinh &yacute;, lập tức rực rỡ cười đ&aacute;p ứng.<br />\r\n<br />\r\nĐ&agrave;o Tiềm kỳ thật nh&aacute;y mắt liền kh&oacute;a chặt mục ti&ecirc;u, nhưng ra ngo&agrave;i cẩn thận, hắn hay l&agrave; giả vờ giả vịt tại s&aacute;ch chồng b&ecirc;n trong lật qua nhặt nhặt l&ecirc;n.<br />\r\n<br />\r\nRất nhanh, hắn t&ugrave;y &yacute; cầm lấy một bản in ấn th&ocirc; r&aacute;p, b&igrave;a s&aacute;ch tr&agrave;n đầy vết bẩn, thậm ch&iacute; c&ograve;n c&oacute; lỗ r&aacute;ch xanh đen b&ecirc;n trong s&aacute;ch.<br />\r\n<br />\r\nT&ecirc;n s&aacute;ch?<br />\r\n<br />\r\nRất kỳ qu&aacute;i, cũng kh&ocirc;ng.<br />\r\n<br />\r\nĐ&agrave;o Tiềm quản l&yacute; tốt biểu lộ, trực tiếp lật ra tờ thứ nhất.<br />\r\n<br />\r\nCũng l&agrave; c&aacute;i n&agrave;y một c&aacute;i chớp mắt, Đ&agrave;o Tiềm trong &oacute;c, c&aacute;i kia quỷ dị tin tức lưu lại lần nữa nổ tung.<br />\r\n<br />\r\nĐ&atilde; quen thuộc cổ qu&aacute;i c&aacute;ch thức, chậm r&atilde;i bị chải vuốt m&agrave; ra:<br />\r\n<br />\r\n【 ch&iacute; t&ecirc;n: V&ocirc; danh b&iacute; s&aacute;ch. 】<br />\r\n<br />\r\n【 ch&iacute; loại: C&ocirc;ng ph&aacute;p. 】<br />\r\n<br />\r\n【 ch&iacute; thuật: Thi&ecirc;n Mệnh năm thứ ch&iacute;n, Tầm Ti&ecirc;n cư sĩ Ng&ocirc; Minh vẫn ở trong nh&agrave; lạnh giường, quanh năm ch&iacute;n mươi c&oacute; ba, một th&acirc;n thanh cao bu&ocirc;ng thả, trời sinh t&iacute;nh ch&iacute;nh trực, d&ugrave; hắn năm ngh&egrave;o si&ecirc;ng năng, chăm học kh&ocirc;ng lười, nhưng lu&ocirc;n thi kh&ocirc;ng thứ, tại s&aacute;u mươi tuổi l&uacute;c chuyển tu ti&ecirc;n đạo, tự xưng Tầm Ti&ecirc;n cư sĩ... N&agrave;y s&aacute;ch ch&iacute;nh l&agrave; hắn tuổi gi&agrave; viết, bởi v&igrave; l&uacute;c đ&oacute; một th&acirc;n đ&atilde; thần ch&iacute; kh&ocirc;ng r&otilde;, l&agrave; trong v&ograve;ng b&ecirc;n trong chứa đựng cũng bừa b&atilde;i, n&oacute;i g&igrave; kh&ocirc;ng hiểu, duy một trang cuối c&ugrave;ng, c&oacute; lưu một thi&ecirc;n &laquo; Ti&ecirc;n Ngư quyết &raquo;, c&oacute; thể th&ocirc;ng thi&ecirc;n đạo. 】<br />\r\n<br />\r\n【 ch&uacute; một: N&agrave;y s&aacute;ch bởi v&igrave; bị &quot;Ng&ocirc; Minh&quot; t&acirc;m thần &ocirc; nhiễm, đ&atilde; c&oacute; khiến người đi&ecirc;n cuồng đặc t&iacute;nh, như tu h&agrave;nh tr&ecirc;n đ&oacute; chứa đựng Ti&ecirc;n Ngư quyết, mỗi tu h&agrave;nh một lần, t&acirc;m tr&iacute;, hồn linh đều muốn bị thi&ecirc;n đạo chỗ đồng h&oacute;a, cho đến thể x&aacute;c cứng ngắc m&agrave; chết, t&acirc;m thần hồn linh th&igrave; như l&agrave; Ti&ecirc;n Ngư dung nhập thi&ecirc;n đạo. 】<br />\r\n<br />\r\n【 ch&uacute; hai: Tu h&agrave;nh Ti&ecirc;n Ngư quyết đại giới thứ hai, bất luận tu h&agrave;nh bao nhi&ecirc;u lần, hồn linh h&oacute;a c&aacute; ngao du hỗn độn thời đi&ecirc;̉m, đều muốn c&oacute; tỉ lệ bạo chết. 】<br />\r\n<br />\r\n【 ch&uacute; ba: Tu h&agrave;nh mười lần, c&oacute; thể miễn trừ ch&iacute;n lần, dư một kh&ocirc;ng thể khống. 】<br />\r\n<br />\r\n...<br />\r\n<br />\r\n&quot;S&aacute;ch &quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm kh&ocirc;ng c&oacute; l&ecirc;n tiếng, đ&aacute;y l&ograve;ng th&igrave; l&agrave; chấn kinh h&iacute;t v&agrave;o kh&iacute; lạnh.<br />\r\n<br />\r\nMặc d&ugrave; l&agrave; c&oacute; t&acirc;n thu lấy được, nhưng nh&igrave;n c&aacute;i n&agrave;y hiển hiện ra tin tức, Đ&agrave;o Tiềm kh&ocirc;ng khỏi kh&ocirc;ng vui.<br />\r\n<br />\r\nTừ n&agrave;y để hắn thu hoạch được &quot;Kh&ocirc;ng chết đặc t&iacute;nh&quot; kh&ocirc;ng biết t&agrave;n quyết bắt đầu, tiếp theo l&agrave; B&aacute;ch Cầm H&iacute; b&iacute; s&aacute;ch, sau đ&oacute; l&agrave; c&aacute;i n&agrave;y v&ocirc; danh b&iacute; s&aacute;ch.<br />\r\n<br />\r\nLi&ecirc;n tiếp ba loại r&otilde; r&agrave;ng đến từ si&ecirc;u ph&agrave;m tu h&agrave;nh giới sự vật, đều c&oacute; một c&aacute;i điểm đặc trưng chung, c&aacute;i n&agrave;y rất kh&oacute; lại d&ugrave;ng tr&ugrave;ng hợp để h&igrave;nh dung.<br />\r\n<br />\r\nĐ&agrave;o Tiềm cảm gi&aacute;c m&igrave;nh ẩn ẩn thấy r&otilde; đến tu h&agrave;nh giới đầu thứ nhất định luật, c&oacute; lẽ l&agrave; thiết luật:<br />\r\n<br />\r\nPh&agrave;m tu đi, si&ecirc;u ph&agrave;m sự t&igrave;nh, tất c&oacute; đại giới!</p>', '2021-12-01 15:04:30', 4, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(309, 76, 'Khủng Bố Lão Nhân', '8', '<p>Đ&agrave;o Tiềm vội v&agrave;ng hạ c&aacute;i kết luận, nhưng hắn kỳ thật cũng kh&ocirc;ng d&aacute;m chắc chắn.<br />\r\n<br />\r\nD&ugrave; sao t&iacute;nh đến trước mắt, hắn tuy nhi&ecirc;n trong b&oacute;ng tối chạm đến si&ecirc;u ph&agrave;m tương quan sự vật, nhưng vẫn chưa c&ugrave;ng một c&aacute;i ch&acirc;n ch&iacute;nh người tu h&agrave;nh sĩ trao đổi qua.<br />\r\n<br />\r\nCứ việc Đ&agrave;o Tiềm từ n&agrave;y ch&iacute; sĩ, c&ugrave;ng thư sinh n&atilde;o hải trong tr&iacute; nhớ biết, tr&ecirc;n thế giới n&agrave;y tuyệt đối tồn tại kh&ocirc;ng &iacute;t người trong tu h&agrave;nh.<br />\r\n<br />\r\nNếu như hắn gi&oacute;ng trống khua chi&ecirc;ng đi t&igrave;m, đại kh&aacute;i dẫn đầu rất nhanh sẽ c&oacute; thu hoạch.<br />\r\n<br />\r\nNhưng như thế, chỉ sợ cũng l&agrave; đường đến chỗ chết.<br />\r\n<br />\r\nB&oacute; lớn người xuy&ecirc;n việt tiền bối kinh nghiệm n&oacute;i cho Đ&agrave;o Tiềm, cẩu n&oacute;i, mới l&agrave; vương đạo.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng biết t&agrave;n quyết li&ecirc;n quan đến thi thể, hư thối c&aacute;c loại kh&iacute; tức, cực giống b&agrave;ng m&ocirc;n tả đạo chi vật sự t&igrave;nh, c&oacute; ch&uacute;t đại giới rất b&igrave;nh thường, n&agrave;y B&aacute;ch Cầm H&iacute; cũng l&agrave; c&ugrave;ng loại logic.&quot;<br />\r\n<br />\r\n&quot;C&oacute; thể c&aacute;i n&agrave;y 【 Ti&ecirc;n Ngư quyết 】, nh&igrave;n mi&ecirc;u tả, rất như l&agrave; Đạo gia Ti&ecirc;n gia ph&aacute;p quyết, lại c&oacute; c&agrave;ng đ&aacute;ng sợ tu h&agrave;nh đại giới...&quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm n&atilde;o hải, &yacute; niệm cuồn cuộn.<br />\r\n<br />\r\nTuy nhi&ecirc;n kh&ocirc;ng x&aacute;c định, nhưng Đ&agrave;o Tiềm tự m&igrave;nh cảm gi&aacute;c, hắn suy đo&aacute;n n&agrave;y định luật chỉ sợ l&agrave; ch&acirc;n thực.<br />\r\n<br />\r\nĐ&aacute;y l&ograve;ng c&oacute; ng&agrave;n vạn nghi hoặc nghĩ hiểu biết, đồng thời cũng muốn nghi&ecirc;m t&uacute;c quan s&aacute;t hạ đến tay hai bản b&iacute; s&aacute;ch, nhưng nơi n&agrave;y hiển nhi&ecirc;n kh&ocirc;ng phải ph&ugrave; hợp chi địa.<br />\r\n<br />\r\nĐ&agrave;o Tiềm thu liễm suy nghĩ, thần sắc như thường lại tại s&aacute;ch chồng b&ecirc;n trong lựa mấy quyển tự thiếp, sau đ&oacute; c&ugrave;ng một chỗ hướng n&agrave;y thực tập thư đồng biểu hiện ra hạ.<br />\r\n<br />\r\nChợt, đưa qua mấy chục mai tiền đồng.<br />\r\n<br />\r\nThư đồng kia vui vẻ thu, Đ&agrave;o Tiềm ngẫm lại, một chuyện kh&ocirc;ng phiền hai người, lại từ trong tay &aacute;o r&uacute;t ra tuyết t&ugrave;ng h&agrave;o thư mục đơn, đem sớm đ&atilde; ngưỡng mộ trong l&ograve;ng mấy bộ hoạ b&aacute;o, s&aacute;ch đều mua lại.<br />\r\n<br />\r\nTuy nhi&ecirc;n vẫn như cũ chỉ l&agrave; mấy khối đồng bạc giao dịch, nhưng đối n&agrave;y thực tập thư đồng m&agrave; n&oacute;i, Đ&agrave;o Tiềm cũng coi l&agrave; c&aacute;i kh&aacute;ch h&agrave;ng lớn.<br />\r\n<br />\r\nKh&ocirc;ng bao l&acirc;u, thư đồng n&agrave;y liền gian nan đeo một c&aacute;i t&uacute;i lớn khỏa từ trong khoang thuyền ra.<br />\r\n<br />\r\nMột tay giao s&aacute;ch, một tay tiếp nhận Đ&agrave;o Tiềm nỗi buồn đưa tới t&aacute;m c&aacute;i đồng bạc.<br />\r\n<br />\r\nL&uacute;c đến t&uacute;i tiền coi như trống, c&aacute;ch l&uacute;c đinh đinh đang đang chỉ c&ograve;n lại mấy c&aacute;i ng&acirc;n h&agrave;o.<br />\r\n<br />\r\nĐ&agrave;o Tiềm c&otilde;ng một c&aacute;i đổ đầy s&aacute;ch bao khỏa, tuyết rơi lỏng h&agrave;o, lần theo lai lịch liền muốn quay lại nh&agrave; m&igrave;nh Th&agrave;nh Hữu tiệm s&aacute;ch.<br />\r\n<br />\r\nTrước khi đi, tr&ugrave;ng hợp lại l&agrave; nh&igrave;n thấy n&agrave;y &quot;Ho&aacute;n Kh&ecirc; h&agrave;o&quot; b&ecirc;n tr&ecirc;n, mấy c&aacute;i da thịt khi sương t&aacute;i tuyết c&ocirc; nương, ch&iacute;nh tựa ở ph&iacute;a trước cửa sổ n&oacute;i chuyện phiếm, bầu kh&ocirc;ng kh&iacute; nh&igrave;n qua rất kh&ocirc;ng tệ, để người rất nhớ gia nhập đi v&agrave;o.<br />\r\n<br />\r\nC&oacute; c&aacute;i hơi lớn gan, mặc đỏ c&aacute;i yếm c&ocirc; nương, xa xa cũng nh&igrave;n thấy Đ&agrave;o Tiềm.<br />\r\n<br />\r\nC&oacute; lẽ l&agrave; gặp hắn vừa đưa thư thuyền, hay l&agrave; cảm thấy c&aacute;i thằng n&agrave;y d&aacute;ng dấp vẫn được, nhẹ giơ l&ecirc;n tay trắng, nh&igrave;n tư thế kia hiển nhi&ecirc;n l&agrave; tại ch&agrave;o hỏi Đ&agrave;o Tiềm đi qua.<br />\r\n<br />\r\nĐ&agrave;o Tiềm cho người ta so c&aacute;i c&aacute;i k&eacute;o tay, sau đ&oacute; một mặt đ&aacute;ng tiếc đi l&ecirc;n ph&iacute;a trước, vừa đi vừa tự m&igrave;nh chắc chắn n&oacute;i:<br />\r\n<br />\r\n&quot;Ta kh&ocirc;ng thể l&ecirc;n hoa thuyền, nguy&ecirc;n nh&acirc;n c&oacute; hai.&quot;<br />\r\n<br />\r\n&quot;Đầu ti&ecirc;n l&agrave; ngh&egrave;o, thứ hai l&agrave; việc n&agrave;y c&oacute; nhục nh&atilde; nhặn.&quot;<br />\r\n<br />\r\nTr&ecirc;u chọc ở giữa, Đ&agrave;o Tiềm cước bộ gia tốc, &yacute; đồ bằng nhanh nhất tốc độ quay lại cửa h&agrave;ng s&aacute;ch.<br />\r\n<br />\r\nTuy nhi&ecirc;n cơ hồ ti&ecirc;u hết to&agrave;n bộ th&acirc;n gia, nhưng hắn mục đ&iacute;ch chuyến đi n&agrave;y, c&oacute; thể n&oacute;i l&agrave; vi&ecirc;n m&atilde;n đạt th&agrave;nh.<br />\r\n<br />\r\nMuốn mua s&aacute;ch tới tay kh&ocirc;ng đ&aacute;ng cao hứng như thế.<br />\r\n<br />\r\nCh&acirc;n ch&iacute;nh để Đ&agrave;o Tiềm hưng phấn, tự nhi&ecirc;n l&agrave; 【 B&aacute;ch Cầm H&iacute; t&agrave;n s&aacute;ch 】 c&ugrave;ng 【 v&ocirc; danh b&iacute; s&aacute;ch 】 c&aacute;i n&agrave;y hai bản tu h&agrave;nh b&iacute; tịch.<br />\r\n<br />\r\nTuy nhi&ecirc;n hai loại, đều cần trả một c&aacute;i gi&aacute; thật lớn.<br />\r\n<br />\r\nNhưng Đ&agrave;o Tiềm tự biết hắn l&agrave; kh&ocirc;ng giống, ho&agrave;n to&agrave;n có th&ecirc;̉ miễn trừ hơn ph&acirc;n nửa thậm ch&iacute; l&agrave; to&agrave;n bộ đại giới, c&oacute; thể n&oacute;i l&agrave; bạch chơi.<br />\r\n<br />\r\nThử hỏi, tr&ecirc;n thế giới n&agrave;y ai c&oacute; thể cự tuyệt bạch chơi đ&acirc;u?</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nCũng ch&iacute;nh l&agrave; Đ&agrave;o Tiềm t&iacute;nh t&igrave;nh coi như trầm ổn, nếu kh&ocirc;ng đ&atilde; sớm chạy như bay.<br />\r\n<br />\r\nTuy nhi&ecirc;n khả năng cũng l&agrave; hắn kh&ocirc;ng c&oacute; sớm b&ocirc;n tẩu, cho n&ecirc;n mới kh&ocirc;ng c&oacute; c&ugrave;ng lại một đường quen thuộc rung động cảm gi&aacute;c bỏ lỡ.<br />\r\n<br />\r\nCũng l&agrave; sau đ&oacute; một khắc, Đ&agrave;o Tiềm đầu ti&ecirc;n l&agrave; nghe được bến đ&ograve; ph&iacute;a trước truyền đến một trận qu&aacute;t mắng đ&aacute;nh trống reo h&ograve;, tiếp theo liền c&oacute; quen thuộc cảm gi&aacute;c.<br />\r\n<br />\r\nNguy&ecirc;n bản chen ch&uacute;c đ&aacute;m người, thật giống như bị người n&agrave;o ngạnh sinh sinh vỡ ra.<br />\r\n<br />\r\nĐ&agrave;o Tiềm thấy quanh m&igrave;nh người đi đường tất cả đều l&agrave; một mặt hoảng sợ bộ d&aacute;ng, rất tự gi&aacute;c hướng hai b&ecirc;n đường triệt tho&aacute;i ph&iacute;a sau, một b&ecirc;n r&uacute;t, c&ograve;n vừa thấp giọng cảnh c&aacute;o người kh&aacute;c:<br />\r\n<br />\r\n&quot;Mau tr&aacute;nh ra, l&agrave; T&agrave;o bang người.&quot;<br />\r\n<br />\r\n&quot;Hỏng b&eacute;t, khẳng định l&agrave; c&oacute; chuyện g&igrave; ph&aacute;t sinh, T&agrave;o bang Tầm Ti&ecirc;n đ&agrave; đ&agrave; chủ tự m&igrave;nh đến, hơn nữa nh&igrave;n sắc mặt rất l&agrave; phẫn nộ.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng n&ecirc;n n&oacute;i lung tung, lui lại c&uacute;i đầu.&quot;<br />\r\n<br />\r\nL&agrave;m một ngh&egrave;o kiết hủ lậu cửa h&agrave;ng s&aacute;ch l&atilde;o bản, nghe những &acirc;m thanh n&agrave;y, Đ&agrave;o Tiềm lựa chọn biết nghe lời phải.<br />\r\n<br />\r\nHơi hơi c&uacute;i đầu, lui lại đến mọi người sau lưng.<br />\r\n<br />\r\nV&ocirc; c&ugrave;ng tự nhi&ecirc;n, dung nhập trong đ&aacute;m người.<br />\r\n<br />\r\nKh&ocirc;ng bao l&acirc;u, hắn nh&igrave;n thấy ch&iacute;nh chủ.<br />\r\n<br />\r\nMột đ&aacute;m tr&ecirc;n trăm c&aacute;i người mặc trang phục, hiển lộ ra to&agrave;n th&acirc;n cường tráng bắp thịt đại h&aacute;n, đang hung thần &aacute;c s&aacute;t mở đường.<br />\r\n<br />\r\nC&oacute; bảy t&aacute;m người bị bọn họ chen ch&uacute;c ở giữa vị tr&iacute;, mỗi một vị đều cho Đ&agrave;o Tiềm một loại giống như như d&atilde; th&uacute; cảm gi&aacute;c sợ h&atilde;i.<br />\r\n<br />\r\nTuy nhi&ecirc;n ch&acirc;n ch&iacute;nh để Đ&agrave;o Tiềm kinh h&atilde;i, k&eacute;m ch&uacute;t thất thố.<br />\r\n<br />\r\nL&agrave; người đi đường n&agrave;y b&ecirc;n trong, một người cầm đầu l&atilde;o giả.<br />\r\n<br />\r\nN&oacute;i l&agrave; l&atilde;o giả, nhưng người kia trừ một đầu hơi bạc t&oacute;c, nh&agrave;o bột m&igrave; bộ nếp nhăn b&ecirc;n ngo&agrave;i, địa phương c&ograve;n lại c&ugrave;ng &quot;L&atilde;o&quot; căn bản l&agrave; kh&ocirc;ng ch&uacute;t n&agrave;o d&iacute;nh d&aacute;ng.<br />\r\n<br />\r\nHắn th&acirc;n cao ch&iacute; &iacute;t một m&eacute;t ch&iacute;n, người mặc đan xen viền v&agrave;ng vải tơ trường b&agrave;o, tứ chi cực tr&aacute;ng kiện, một đ&ocirc;i c&oacute; ch&uacute;t đục ngầu nhưng v&ocirc; c&ugrave;ng sắc b&eacute;n đ&ocirc;i mắt , bất kỳ c&aacute;i g&igrave; bị hắn tiếp cận người, ngay lập tức sẽ c&oacute; một loại lợi nhận gia th&acirc;n cảm gi&aacute;c.<br />\r\n<br />\r\nL&atilde;o giả n&agrave;y, ở thời đại n&agrave;y, tại bến t&agrave;u n&agrave;y.<br />\r\n<br />\r\nH&agrave;nh tẩu trong đ&aacute;m người, h&igrave;nh ảnh kia quả thực c&ugrave;ng một đầu hoang man hung th&uacute;, h&agrave;nh tẩu tại bầy cừu b&ecirc;n trong kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c biệt.<br />\r\n<br />\r\nChỉ l&agrave; những n&agrave;y, đều kh&ocirc;ng phải để Đ&agrave;o Tiềm kinh h&atilde;i nguy&ecirc;n nh&acirc;n.<br />\r\n<br />\r\nCh&acirc;n ch&iacute;nh để hắn kh&ocirc;ng thể tin được, l&agrave; cảm gi&aacute;c của hắn:<br />\r\n<br />\r\nN&agrave;y rung động cảm gi&aacute;c chi nguy&ecirc;n, cũng kh&ocirc;ng phải l&agrave; n&agrave;o đ&oacute; bản thư tịch, r&otilde; r&agrave;ng cũng l&agrave; l&atilde;o giả n&agrave;y.<br />\r\n<br />\r\n&quot;V&acirc;ng, ta n&agrave;y si&ecirc;u ph&agrave;m cảm gi&aacute;c, chưa hề n&oacute;i chỉ hạn định tại c&ocirc;ng ph&aacute;p b&iacute; tịch b&ecirc;n tr&ecirc;n.&quot;<br />\r\n<br />\r\n&quot;Ta tự th&acirc;n, chẳng phải bị n&agrave;y cổ qu&aacute;i c&aacute;ch thức, chia nhập 【 qu&aacute;i dị 】 một loại a?&quot;<br />\r\n<br />\r\n&quot;Dựa theo n&agrave;y tin tức mi&ecirc;u tả, c&aacute;i gọi l&agrave; qu&aacute;i dị, l&agrave; chỉ thu hoạch được một loại n&agrave;o đ&oacute; si&ecirc;u ph&agrave;m đặc t&iacute;nh sinh mạng thể.&quot;<br />\r\n<br />\r\n&quot;Vậy c&aacute;i n&agrave;y l&atilde;o giả đ&acirc;u, hắn lại l&agrave; một loại n&agrave;o?&quot;<br />\r\n<br />\r\n&quot;Ta cần chạm đến th&acirc;n thể của hắn, mới c&oacute; thể để cho cảm gi&aacute;c c&agrave;ng c&oacute; hiện h&oacute;a? Thấy r&otilde; nhiều b&iacute; mật hơn?&quot;<br />\r\n<br />\r\nGiờ n&agrave;y khắc n&agrave;y, Đ&agrave;o Tiềm đ&aacute;y l&ograve;ng &yacute; niệm căn bản l&agrave; kh&ocirc;ng c&oacute; c&aacute;ch ngăn chặn.<br />\r\n<br />\r\nC&aacute;c loại vấn đề c&ugrave;ng &yacute; nghĩ, đi&ecirc;n cuồng to&aacute;t ra.<br />\r\n<br />\r\nNếu c&oacute; c&aacute;ch gần đ&oacute; người tỉ mỉ quan s&aacute;t, liền sẽ ph&aacute;t hiện c&aacute;i n&agrave;y ngh&egrave;o kiết hủ lậu cửa h&agrave;ng s&aacute;ch l&atilde;o bản, dường như ở v&agrave;o một loại đ&atilde; hưng phấn vừa khẩn trương trạng th&aacute;i b&ecirc;n trong.<br />\r\n<br />\r\nHưng phấn?<br />\r\n<br />\r\nĐương nhi&ecirc;n.<br />\r\n<br />\r\nCh&iacute;nh như l&agrave; như cự th&uacute;, từng bước một đi tới, th&acirc;n phận l&agrave; &quot;T&agrave;o bang Tầm Ti&ecirc;n ph&acirc;n đ&agrave; đ&agrave; chủ&quot; l&atilde;o giả.<br />\r\n<br />\r\nTh&igrave;nh l&igrave;nh ch&iacute;nh l&agrave; Đ&agrave;o Tiềm trừ tự th&acirc;n b&ecirc;n ngo&agrave;i, cảm ứng được c&aacute;i thứ hai c&ugrave;ng si&ecirc;u ph&agrave;m, tu h&agrave;nh tương quan nh&acirc;n loại.<br />\r\n<br />\r\nNếu như kh&ocirc;ng phải c&acirc;n nhắc đến c&aacute;i mạng nhỏ của m&igrave;nh quan trọng, Đ&agrave;o Tiềm phi thường muốn x&ocirc;ng tới, đối l&atilde;o giả kia một hồi m&oacute;c sờ, nh&igrave;n c&oacute; thể hay kh&ocirc;ng đem kỹ c&agrave;ng tin tức cảm gi&aacute;c ra.<br />\r\n<br />\r\nĐ&aacute;ng tiếc, hắn kh&ocirc;ng thể.<br />\r\n<br />\r\nNh&igrave;n quanh m&igrave;nh người phản ứng liền biết, l&atilde;o giả n&agrave;y tuyệt kh&ocirc;ng phải c&aacute;i g&igrave; lương thiện.<br />\r\n<br />\r\nĐ&agrave;o Tiềm giống như những người kh&aacute;c, đối với thời khắc n&agrave;y t&igrave;nh trạng, đều ở v&agrave;o nghi hoặc b&ecirc;n trong.<br />\r\n<br />\r\nKh&ocirc;ng biết c&aacute;i n&agrave;y bến đ&ograve; đến c&ugrave;ng xảy ra chuyện g&igrave;, bọn n&agrave;y &quot;Hung nh&acirc;n&quot;, lại l&agrave; tới l&agrave;m c&aacute;i g&igrave;.<br />\r\n<br />\r\nTuy nhi&ecirc;n c&aacute;i n&agrave;y nghi hoặc, cũng kh&ocirc;ng c&oacute; tiếp tục qu&aacute; l&acirc;u.<br />\r\n<br />\r\nMột gi&acirc;y sau, biến đổi lớn ph&aacute;t sinh.<br />\r\n<br />\r\nOanh!<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; bất kỳ c&aacute;i g&igrave; b&aacute;o hiệu, một tiếng vang thật lớn, xuất hiện tại bến đ&ograve; người khu lớn nhất trong kho h&agrave;ng.<br />\r\n<br />\r\nNương theo lấy c&aacute;i n&agrave;y đại bạo tạc, kh&oacute;i đặc, hỏa quang c&ugrave;ng c&aacute;c loại h&agrave;ng h&oacute;a to&aacute;i phiến, hướng về quanh m&igrave;nh tứ t&aacute;n.<br />\r\n<br />\r\nChỉ một c&aacute;i chớp mắt, bến đ&ograve; l&acirc;m v&agrave;o đại hỗn loạn.<br />\r\n<br />\r\nĐ&aacute;m người như bị kinh đ&agrave;n chuột tản ra, bốn ph&iacute;a chạy trốn, tiếng k&ecirc;u sợ h&atilde;i, tiếng h&ocirc; ho&aacute;n, tiếng cầu cứu, tiếng rống giận dữ hỗn tạp c&ugrave;ng một chỗ, lỗ tai hơi kh&ocirc;ng tốt, tại chỗ liền c&ugrave;ng điếc kh&ocirc;ng c&oacute; kh&aacute;c nhau.<br />\r\n<br />\r\nNhưng rất nhanh, tất cả tạp &acirc;m đều bị một tiếng như rồng giống như hổ trầm thấp g&agrave;o th&eacute;t trấn &aacute;p xuống.<br />\r\n<br />\r\nKh&ocirc;ng ngạc nhi&ecirc;n ch&uacute;t n&agrave;o, l&ecirc;n tiếng ch&iacute;nh l&agrave; l&atilde;o giả kia.<br />\r\n<br />\r\nHắn giống như ho&agrave;n to&agrave;n kh&ocirc;ng th&egrave;m để &yacute; bến đ&ograve; hỗn loạn, chỉ cười lạnh nh&igrave;n về ph&iacute;a con kia dư phế t&iacute;ch nh&agrave; kho phương vị.<br />\r\n<br />\r\n&quot;Ra đi, non nớt lại kh&ocirc;ng hiểu ẩn t&agrave;ng s&aacute;t kh&iacute;, l&atilde;o phu c&aacute;ch một con đường cũng đ&atilde; ngửi được.&quot;<br />\r\n<br />\r\nL&atilde;o giả tiếng n&oacute;i vừa hạ xuống hạ.<br />\r\n<br />\r\nLại nghe bến đ&ograve; c&aacute;c nơi, đồng thời vang l&ecirc;n g&oacute;c &aacute;o thanh &acirc;m x&eacute; gi&oacute;, ước chừng tr&ecirc;n trăm đạo mặc c&aacute;c thức &aacute;o b&agrave;o, nhưng đều tại c&aacute;i tr&aacute;n cột đai đỏ th&acirc;n ảnh xuất hiện.<br />\r\n<br />\r\nMỗi một người cho Đ&agrave;o Tiềm cảm gi&aacute;c, lại đều so T&agrave;o bang đ&aacute;m người kia mạnh hơn.<br />\r\n<br />\r\nThậm ch&iacute;, c&ograve;n c&oacute; một loại cực kỳ nhỏ &quot;Rung động cảm gi&aacute;c&quot; .<br />\r\n<br />\r\nNhất l&agrave; cầm đầu, cũng ch&iacute;nh l&agrave; bị l&atilde;o giả kh&oacute;a chặt, từ n&agrave;y phế t&iacute;ch trong kh&oacute;i đặc chậm r&atilde;i đi ra một người trẻ tuổi.<br />\r\n<br />\r\nHắn th&acirc;n cao cũng đạt tới thiếu một gạo ch&iacute;n, mặc c&ugrave;ng loại đạo b&agrave;o giống như quần &aacute;o.<br />\r\n<br />\r\nNgũ quan cực anh tuấn, một vết sẹo từ mắt tr&aacute;i ph&iacute;a dưới, k&eacute;o d&agrave;i đến mắt phải hạ, quả thực vượt ngang hắn n&agrave;y cả khu&ocirc;n mặt.<br />\r\n<br />\r\nHắn hiển lộ ra th&acirc;n thể tuy nhi&ecirc;n kh&ocirc;ng c&oacute; l&atilde;o giả khoa trương như vậy, nhưng cũng thon d&agrave;i cường tráng, viễn si&ecirc;u giữa s&acirc;n bất kỳ người n&agrave;o.<br />\r\n<br />\r\nNếu như l&agrave; tại Đ&agrave;o Tiềm kiếp trước, dạng n&agrave;y người tuyệt đối c&oacute; thể tiến ng&agrave;nh giải tr&iacute; được xưng l&agrave; nam thần.<br />\r\n<br />\r\nM&agrave; hắn cho Đ&agrave;o Tiềm rung động cảm gi&aacute;c, chỉ so với l&atilde;o giả kia yếu một &iacute;t.<br />\r\n<br />\r\nL&uacute;c n&agrave;y Đ&agrave;o Tiềm, đ&atilde; từ hưng phấn, c&oacute; một ch&uacute;t chết lặng.<br />\r\n<br />\r\n&quot;Xuy&ecirc;n việt trọng sinh hai ng&agrave;y, tốn sức t&igrave;m kiếm si&ecirc;u ph&agrave;m, quỷ dị, tu h&agrave;nh tồn tại, kh&ocirc;ng nghĩ tới c&aacute;i n&agrave;y kh&ocirc;ng hẹn m&agrave; gặp, trực tiếp đưa ra một tổ.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Tuy nhi&ecirc;n đ&atilde; đang nh&igrave;n qua ch&iacute; sĩ c&ugrave;ng thư sinh tr&iacute; nhớ sau c&oacute; đo&aacute;n trước, nhưng c&aacute;i n&agrave;y đ&iacute;ch x&aacute;c l&agrave; qu&aacute; nhanh.&quot;<br />\r\n<br />\r\n&quot;Bất qu&aacute;, nghi hoặc hay l&agrave; dị thường nhi&ecirc;̀u, tu h&agrave;nh giới đến c&ugrave;ng l&agrave; như thế n&agrave;o t&igrave;nh trạng?&quot;<br />\r\n<br />\r\n&quot;Hai b&ecirc;n bất kể l&agrave; ai, thật muốn sờ l&ecirc;n một c&aacute;i, để ta xem một ch&uacute;t c&ograve;n c&oacute; c&aacute;i g&igrave; ch&iacute; dị ph&acirc;n loại?&quot;<br />\r\n<br />\r\nĐ&agrave;o Tiềm một b&ecirc;n nhanh ch&oacute;ng suy tư, một b&ecirc;n cũng kh&ocirc;ng qu&ecirc;n nhanh ch&oacute;ng lui lại.<br />\r\n<br />\r\nKiếp trước rất nhiều truyền h&igrave;nh điện ảnh kịch kinh nghiệm n&oacute;i cho hắn, một trận thanh thế thật lớn ch&eacute;m giết sắp bắt đầu, tại c&aacute;i thằng n&agrave;y giết b&ecirc;n trong tử vong mạo hiểm cao nhất, cũng kh&ocirc;ng phải l&agrave; song phương, m&agrave; ch&iacute;nh l&agrave; quần ch&uacute;ng v&acirc;y xem.<br />\r\n<br />\r\nTại Đ&agrave;o Tiềm nhanh l&ugrave;i lại l&uacute;c, b&ecirc;n kia song phương giống như đều đang n&oacute;i c&aacute;i g&igrave;.<br />\r\n<br />\r\nTuy nhi&ecirc;n Đ&agrave;o Tiềm lui qu&aacute; xa, nghe kh&ocirc;ng ch&acirc;n thiết.<br />\r\n<br />\r\nĐ&agrave;o Tiềm cũng kh&ocirc;ng cần nghe qu&aacute; r&otilde; r&agrave;ng, kh&ocirc;ng cần đo&aacute;n cũng biết, đơn giản l&agrave; một loại n&agrave;o đ&oacute; trả th&ugrave; tiết mục.<br />\r\n<br />\r\nCũng liền tại Đ&agrave;o Tiềm cảm thấy m&igrave;nh lui đến đủ xa, lặng lẽ trở lại, nh&igrave;n về ph&iacute;a bến đ&ograve; người khu l&uacute;c.<br />\r\n<br />\r\nVượt qua bất luận kẻ n&agrave;o tưởng tượng ch&eacute;m giết, kh&ocirc;ng c&oacute; dấu hiệu n&agrave;o tr&igrave;nh diễn.<br />\r\n<br />\r\nN&agrave;y T&agrave;o bang đ&agrave; chủ, l&atilde;o giả kia.<br />\r\n<br />\r\nHắn v&acirc;̣y mà chủ động phất tay, muốn một đ&aacute;m thủ hạ triệt tho&aacute;i ph&iacute;a sau.<br />\r\n<br />\r\nC&agrave;ng khiến người ta cảm thấy kỳ qu&aacute;i l&agrave;, đ&aacute;m kia đại h&aacute;n tại l&atilde;o giả ph&aacute;t h&agrave;o trước đ&oacute;, đ&atilde; chuẩn bị chuồn đi, vừa được khiến c&agrave;ng l&agrave; tè ra qu&acirc;̀n hướng ph&iacute;a sau phi nước đại.<br />\r\n<br />\r\nMọi người ban đầu đều cảm thấy kỳ qu&aacute;i, thẳng đến một gi&acirc;y sau:<br />\r\n<br />\r\n&quot;Rống!&quot;<br />\r\n<br />\r\nKhi đạo n&agrave;y phảng phất l&agrave; viễn cổ hung th&uacute; g&agrave;o th&eacute;t, vang vọng bến đ&ograve; l&uacute;c.<br />\r\n<br />\r\nTrăm người?<br />\r\n<br />\r\nNg&agrave;n người?<br />\r\n<br />\r\nĐ&uacute;ng vậy, ch&iacute; &iacute;t hơn ngh&igrave;n người tai mắt đều cảm nhận được kịch liệt đau nhức, m&aacute;u tươi bắn ra.<br />\r\n<br />\r\nThảm hại hơn, tự nhi&ecirc;n l&agrave; c&aacute;ch l&atilde;o giả gần nhất, muốn v&acirc;y giết hắn đ&aacute;m người tuổi trẻ kia.<br />\r\n<br />\r\nTrừ cầm đầu mặc đạo b&agrave;o người kia, những người c&ograve;n lại, tựa hồ cũng l&acirc;m v&agrave;o &quot;Cứng ngắc&quot; trạng th&aacute;i.<br />\r\n<br />\r\nM&agrave; l&atilde;o giả kia, rốt cục động.<br />\r\n<br />\r\nTrong cơ thể của hắn tựa hồ c&oacute; một loại n&agrave;o đ&oacute; kinh khủng đồ vật thức tỉnh, hắn ở trong mắt Đ&agrave;o Tiềm, một c&aacute;i chớp mắt mất đi nh&acirc;n loại n&ecirc;n c&oacute; kh&iacute; tức.<br />\r\n<br />\r\nHắn cười gằn, một c&aacute;i bước d&agrave;i ra, như d&atilde; th&uacute; nhảy vọt đến một s&aacute;t thủ trước mặt.<br />\r\n<br />\r\nQuạt hương bồ lớn b&agrave;n tay nắm tay, như ra ho&agrave;nh ch&ugrave;y.<br />\r\n<br />\r\nChỉ nghe &quot;Bành&quot; một tiếng, s&aacute;t thủ kia một vi&ecirc;n đầu l&acirc;u tựa như bị gi&aacute;ng đ&ograve;n nặng nề như dưa hấu sụp đổ.<br />\r\n<br />\r\nM&agrave; xuống một khắc, ch&acirc;n ch&iacute;nh để bao qu&aacute;t Đ&agrave;o Tiềm ở b&ecirc;n trong, tất cả mọi người che c&aacute;i &oacute;t, kh&ocirc;ng c&aacute;ch n&agrave;o khống chế biểu lộ một m&agrave;n xuất hiện.<br />\r\n<br />\r\nL&atilde;o giả kia ch&ugrave;y bạo s&aacute;t trong tay sọ về sau, đ&uacute;ng l&agrave; duỗi b&agrave;n tay, đem t&agrave;n thi bắt tới.<br />\r\n<br />\r\nTo&eacute;t ra miệng, bỗng nhi&ecirc;n nhắm ngay n&agrave;y bắn ra m&aacute;u tươi c&aacute;i cổ đụng l&ecirc;n đi.<br />\r\n<br />\r\nSau đ&oacute; &quot;Ừng ực ~ ừng ực&quot;, bắt đầu miệng lớn uống.</p>', '2021-12-01 15:04:53', 11, 1),
(310, 77, 'Anduin Cùng Siêu Năng Lực', '1', '<p>1980 năm th&aacute;ng 7 ban đầu, Anh quốc London<br />\r\n<br />\r\nĐ&atilde; qua buổi tối 8, Elens Gi&aacute;o hội Ph&uacute;c Lợi Viện, một gian ph&ograve;ng đang l&uacute;c như cũ lờ mờ l&oacute;e ra &aacute;nh đ&egrave;n.<br />\r\n<br />\r\n&quot;Ai, thực chịu kh&ocirc;ng.&quot;<br />\r\n<br />\r\nTrong ph&ograve;ng thiếu ni&ecirc;n nh&igrave;n xem tủ quần &aacute;o thượng phản chiếu lấy ch&iacute;nh m&igrave;nh th&acirc;n ảnh tấm gương, kh&ocirc;ng ngừng vuốt c&agrave;m, sau đ&oacute; lại thỉnh thoảng vươn tay, một đ&ocirc;i hết sức nhỏ nhưng lại đường cong r&otilde; r&agrave;ng b&agrave;n tay nhỏ b&eacute; mở ra lại kh&eacute;p lại, phảng phất tại hướng trong hư kh&ocirc;ng bắt nhiếp lấy c&aacute;i g&igrave;.<br />\r\n<br />\r\nMười một năm!<br />\r\n<br />\r\nĐi tới nơi n&agrave;y c&aacute;i ph&aacute; thế giới đ&atilde; mười một năm, m&agrave; c&ograve;n tại một c&aacute;i những năm t&aacute;m mươi c&ocirc; nhi viện, kh&ocirc;ng c&oacute; Computer, kh&ocirc;ng c&oacute; di động, thật kh&oacute; tưởng tượng hắn đến tột c&ugrave;ng l&agrave; như thế n&agrave;o qua.<br />\r\n<br />\r\n&quot;Anduin, l&atilde;o đại, ngươi muốn c&aacute;i g&igrave;...&quot; Kh&ocirc;ng đợi thiếu ni&ecirc;n cảm kh&aacute;i một hồi, đ&atilde; bị đại lực tiếng đập cửa bừng tỉnh.<br />\r\n<br />\r\n&quot;H&ocirc; to gọi nhỏ l&agrave;m g&igrave;, muộn như vậy kh&ocirc;ng sợ bị ph&aacute;t hiện sao?&quot; Anduin mở ra đại m&ocirc;n, nh&iacute;u m&agrave;y n&oacute;i.<br />\r\n<br />\r\n&quot;&Aacute;ch, xin lỗi a, chủ yếu l&agrave; &ocirc;m n&agrave;y một rương lớn đồ vật nhưng l&agrave;m ta mệt chết, hắc hắc. . .&quot; Một vị nh&igrave;n l&ecirc;n mười s&aacute;u mười bảy tuổi thiếu ni&ecirc;n mở miệng n&oacute;i, hắn ăn mặc kh&ocirc;ng t&iacute;nh cũ kỹ, nhưng nhăn nhăn nh&uacute;m nh&uacute;m &aacute;o sơmi, d&ugrave;ng m&oacute;c treo hợp với quần t&acirc;y đến l&agrave; mười phần vừa người, t&oacute;c kh&ocirc;ng quản l&yacute; lộn xộn.<br />\r\n<br />\r\ncho Anduin tặng đồ người trẻ tuổi k&ecirc;u William, so với Anduin lớn hơn v&agrave;i tuổi, đồng dạng l&agrave; c&ocirc; nhi viện c&ocirc; nhi nhất.<br />\r\n<br />\r\n&quot;Đi, như thế n&agrave;o muộn như vậy mới trở về, kh&ocirc;ng phải l&agrave; Bắc khu đ&aacute;m kia gia hỏa lại t&igrave;m ngươi nh&oacute;m phiền to&aacute;i a.&quot; Anduin hơi hơi nhăn nh&iacute;u m&agrave;y, kh&oacute; hiểu hỏi.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Sao c&oacute; thể nha, từ lần trước ngươi đem Bắc khu đ&aacute;m kia lưu manh gi&aacute;o huấn một lần, đ&atilde; kh&ocirc;ng ai tới t&igrave;m ch&uacute;ng ta phiền to&aacute;i, h&ocirc;m nay l&agrave; huynh đệ mấy c&aacute;i ngược lại đến kh&ocirc;ng &iacute;t thứ tốt, liền đem thời gian k&eacute;o muộn.&quot; William n&oacute;i xong đ&agrave;o đ&agrave;o t&uacute;i, lấy ra tấm v&eacute; tiền giấy đưa qua, &quot;Vừa vặn, đ&acirc;y l&agrave; tuần n&agrave;y ng&agrave;i phần.&quot;<br />\r\n<br />\r\nAnduin cũng kh&ocirc;ng n&oacute;i g&igrave; nh&agrave;n nhạt tiếp nhận tiền &quot;Đi, h&ocirc;m nay vất vả ngươi.&quot; N&oacute;i xong đang chuẩn bị mở ra rương h&ograve;m, ph&aacute;t hiện William vẫn đứng ở b&ecirc;n cạnh gượng cười, đồng thời xoa xoa tay kh&ocirc;ng c&oacute; động tĩnh.<br />\r\n<br />\r\n&quot;Như thế n&agrave;o, ta c&ograve;n muốn luyện c&ocirc;ng nha.&quot;<br />\r\n<br />\r\n&quot;Hắc hắc, l&agrave; như thế n&agrave;y, d&ugrave; sao thời gian c&ograve;n sớm, mấy người ch&uacute;ng ta vừa vặn nghĩ thư gi&atilde;n một t&iacute;...&quot;<br />\r\n<br />\r\n&quot;Đi, nghĩ chơi mạt chược đ&uacute;ng kh&ocirc;ng.&quot; Anduin cắt đứt William, cũng kh&ocirc;ng n&oacute;i th&ecirc;m c&aacute;i g&igrave;, y&ecirc;n lặng đi đến b&ecirc;n giường, từ dưới giường l&ocirc;i ra một cái h&ocirc;̣p g&ocirc;̃ nhỏ, &quot;Vui đ&ugrave;a một ch&uacute;t đi, kh&aacute;c b&agrave;i bạc a, tr&aacute;nh ma ma lại tức giận cho ngươi tịch thu.&quot;<br />\r\n<br />\r\n&quot;Được rồi, y&ecirc;n t&acirc;m đi, hai ng&agrave;y nữa cho ngươi trả lại.&quot; William n&oacute;i xong cũng vội v&agrave;ng x&aacute;ch qua c&aacute;i hộp, ch&acirc;̣p choạng trơn trượt ti&ecirc;u thất trong ph&ograve;ng.<br />\r\n<br />\r\n&quot;Xong, lại đi&ecirc;n một c&aacute;i, ai, cũng kh&ocirc;ng biết dạy bọn họ chơi mạt chược c&oacute; phải hay kh&ocirc;ng gi&aacute;o sai&quot; Anduin đ&oacute;ng cửa lại kh&ocirc;ng lời đạo<br />\r\n<br />\r\nNgay sau đ&oacute; Anduin cao hứng ch&agrave; ch&agrave; tay, mở ra William đưa tới đại th&ugrave;ng giấy.<br />\r\n<br />\r\n&quot;Hắc hắc, x&igrave;-dầu, tương vừng, dấm chua, rượu gia vị, đ&atilde; l&acirc;u kh&ocirc;ng gặp c&aacute;c bằng hữu, kh&ocirc;ng c&oacute; di động, Computer, ta đ&acirc;y chỉ c&oacute; thể ở kh&aacute;c địa phương b&ugrave;, mỗi ng&agrave;y ăn Anh quốc rau đều nhanh cho ta ăn nhả.&quot;<br />\r\n<br />\r\nN&agrave;y rương đồ vật kh&ocirc;ng kh&aacute;c biệt, thật sự l&agrave; Anduin để cho William hỗ trợ từ phố người Hoa mua gia vị.<br />\r\n<br />\r\nAnduin. Wilson đi đến thế giới n&agrave;y, kh&ocirc;ng biết m&igrave;nh rốt cuộc l&agrave; xuy&ecirc;n vi&ecirc;̣t c&ograve;n l&agrave; chuyển thế, tr&iacute; nhớ kiếp trước c&oacute; rất nhiều đ&atilde; trở n&ecirc;n mơ hồ kh&ocirc;ng r&otilde;, bất qu&aacute; Anduin c&ograve;n nhớ r&otilde; một &iacute;t, v&iacute; dụ như ch&iacute;nh m&igrave;nh kiếp trước xuất th&acirc;n c&ugrave;ng v&otilde; thuật thế gia, v&iacute; dụ như ch&iacute;nh m&igrave;nh vẫn đ&atilde; từng đi l&iacute;nh. . ..<br />\r\n<br />\r\nHắn kh&ocirc;ng biết m&igrave;nh hiện tại sở ở thế giới vẫn c&oacute; phải hay kh&ocirc;ng l&uacute;c trước thế giới kia, vừa sinh ra liền ở c&ocirc; nhi viện, c&ograve;n l&agrave; những năm 60-70, bất qu&aacute; nếu như tới phải nhập gia t&ugrave;y tục, Anduin chung quy từng c&oacute; người trưởng th&agrave;nh kinh lịch c&ugrave;ng k&yacute; ức, ngược lại kh&ocirc;ng sĩ diện c&atilde;i l&aacute;o.<br />\r\n<br />\r\nAnduin sở ở c&ocirc; nhi viện thuộc về Gi&aacute;o hội cai quản, William theo ch&acirc;n bọn họ một đ&aacute;m huynh đệ cũng c&ugrave;ng Anduin đồng dạng ở c&ocirc; nhi viện lớn l&ecirc;n, so với Anduin tuổi t&aacute;c muốn lớn hơn v&agrave;i tuổi, bất qu&aacute; William Ca mấy c&aacute;i đều rất nghe Anduin m&agrave; n&oacute;i.<br />\r\n<br />\r\nTại sao vậy chứ?<br />\r\n<br />\r\nBởi v&igrave; bọn họ kh&ocirc;ng thể kh&ocirc;ng nghe.<br />\r\n<br />\r\nAnduin v&ocirc; c&ugrave;ng c&oacute; thể đ&aacute;nh, d&ugrave; sao cũng l&agrave; xuất th&acirc;n từ v&otilde; thuật thế gia, đồng thời c&ograve;n từ qua qu&acirc;n, cho n&ecirc;n tại rất khi c&ograve;n b&eacute; cũng rất ch&uacute; &yacute; đối với th&acirc;n thể r&egrave;n luyện, c&aacute;i g&igrave; Qu&acirc;n Thể Quyền, Th&ocirc;ng T&iacute; quyền, Vịnh Xu&acirc;n, Ph&aacute;ch Quải, Th&aacute;i Cực. . ., đ&atilde; h&igrave;nh th&agrave;nh bản năng đại n&atilde;o k&yacute; ức, rất nhanh liền c&oacute; thể luyện trở về.<br />\r\n<br />\r\nCho n&ecirc;n tại 9 tuổi thời điểm quần ẩu qua mấy người bọn hắn một bữa về sau, William bọn họ tại Anduin trước mặt liền mười phần nhu thuận, đồng thời tại Anduin chỉ đạo, lấy William cầm đầu một đ&aacute;m c&aacute;c huynh đệ thậm ch&iacute; ở c&ocirc; nhi viện ngo&agrave;i l&agrave;m l&ecirc;n sinh &yacute;.<br />\r\n<br />\r\n&quot;Tại như vậy lăn lộn hạ xuống, ta muốn biến th&agrave;nh lưu manh đội đầu, mỗi ng&agrave;y c&ugrave;ng thu ph&iacute; bảo hộ đồng dạng.&quot; Anduin tự giễu một phen, kh&ocirc;ng tại nhiều nghĩ, cầm trong rương đồ gia vị cất kỹ, chuẩn bị h&ocirc;m n&agrave;o r&uacute;t sạch ch&iacute;nh m&igrave;nh xuống bếp kiếm ch&uacute;t m&oacute;n cơm t&agrave;u khao khao ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\nThu thập xong những t&agrave;i liệu n&agrave;y, Anduin quay người đem cửa gian ph&ograve;ng gắt gao kh&oacute;a tr&aacute;i lại.<br />\r\n<br />\r\nĐối với hắn m&agrave; n&oacute;i, đi đến thế giới n&agrave;y, c&ograve;n c&oacute; một c&aacute;i bất luận kẻ n&agrave;o cũng kh&ocirc;ng biết b&iacute; mật.<br />\r\n<br />\r\nHắn, kỳ thật hội si&ecirc;u năng lực.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nAnduin nh&igrave;n chằm chằm g&oacute;c tường bầy đặt, một c&acirc;y mười kil&ocirc;met c&acirc;n tạ tay, lấy tay hơi hơi chỉ, nhất thời liền tr&ocirc;ng thấy kia c&ugrave;ng tạ tay như l&agrave; bị một cỗ v&ocirc; h&igrave;nh lực lượng k&eacute;o lấy đồng dạng, lẳng lặng phi&ecirc;u ph&ugrave; ở kh&ocirc;ng trung.<br />\r\n<br />\r\nHắn thao t&uacute;ng tạ tay tr&ocirc;i nổi tr&ecirc;n m&igrave;nh kh&ocirc;ng, cũng kh&ocirc;ng đi quản l&yacute; hắn, đi đến trước b&agrave;n s&aacute;ch, lẳng lặng mở ra một quyển s&aacute;ch nh&igrave;n l&ecirc;n.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; Anduin ch&iacute;nh m&igrave;nh ph&aacute;t minh một loại r&egrave;n luyện si&ecirc;u năng lực phương thức, hắn xưng l&agrave; k&eacute;o d&agrave;i th&igrave; huấn luyện.<br />\r\n<br />\r\nTrước kia bảy t&aacute;m tuổi thời điểm Anduin liền cảm thấy m&igrave;nh c&ugrave;ng người kh&aacute;c bất đồng, tại một lần r&egrave;n luyện ho&agrave;n tất về sau hắn muốn uống nước, đang suy nghĩ Ph&aacute;p vừa mới d&acirc;ng l&ecirc;n, tr&ecirc;n mặt b&agrave;n ch&eacute;n nước liền lăng kh&ocirc;ng hướng hắn bay tới.<br />\r\n<br />\r\nTại Anduin quan s&aacute;t, hắn ph&aacute;t hiện tối thiểu ở c&ocirc; nhi viện b&ecirc;n trong chỉ c&oacute; hắn l&agrave; c&aacute;i kia đặc biệt, cho n&ecirc;n hắn b&igrave;nh thường phi thường cẩn thận, tại c&aacute;i n&agrave;y Gi&aacute;o hội chủ tr&igrave; Ph&uacute;c Lợi Viện trong bị người kh&aacute;c ph&aacute;t hiện, kh&ocirc;ng chừng đ&atilde; bị người trở th&agrave;nh qu&aacute;i vật hoặc dị đoan cho xử l&yacute;.<br />\r\n<br />\r\nLoại n&agrave;y năng lực đặc th&ugrave; bị Anduin xưng l&agrave; &quot;Si&ecirc;u năng lực&quot;, tại hắn tận lực khảo th&iacute; c&ugrave;ng luyện tập hạ loại năng lực n&agrave;y từ khi c&oacute; khi kh&ocirc;ng, đến hắn chậm r&atilde;i c&oacute; thể rất nhỏ nắm giữ.<br />\r\n<br />\r\nHắn ph&aacute;t hiện đang luyện v&otilde; thu c&ocirc;ng đứng như cọc gỗ, c&oacute; thể tốt hơn nắm chắc cỗ lực lượng n&agrave;y, tạm thời cầm kia xưng l&agrave; lực lượng a, Anduin ch&iacute;nh m&igrave;nh ph&acirc;n t&iacute;ch n&agrave;y c&ugrave;ng m&igrave;nh l&uacute;c ấy trạng th&aacute;i c&oacute; quan hệ, sau khi thu c&ocirc;ng hắn hội ở v&agrave;o một loại đại n&atilde;o thả kh&ocirc;ng trạng th&aacute;i, c&oacute; lẽ loại t&igrave;nh huống đ&oacute; hạ c&oacute; thể tốt hơn nắm chắc loại lực lượng n&agrave;y.<br />\r\n<br />\r\nTừ kia về sau nguy&ecirc;n bản liền tương đối tự hạn chế Anduin đối với ch&iacute;nh m&igrave;nh liền c&oacute; nghi&ecirc;m khắc quy hoạch, chung quy tại hắn nh&igrave;n tới đ&acirc;y ch&iacute;nh l&agrave; &quot;Đặc dị c&ocirc;ng năng&quot; nha, cũng kh&ocirc;ng thể l&atilde;ng ph&iacute;, mỗi ng&agrave;y đ&uacute;ng hạn luyện v&otilde;, ngồi xuống, minh tưởng, đồng thời mỗi ng&agrave;y r&egrave;n luyện &quot;Si&ecirc;u năng lực&quot; .<br />\r\n<br />\r\nHắn bắt đầu r&egrave;n luyện phương ph&aacute;p kỳ thật c&ugrave;ng b&igrave;nh thường th&acirc;n thể của m&igrave;nh r&egrave;n luyện thời điểm kh&ocirc;ng sai biệt lắm, đ&oacute; ch&iacute;nh l&agrave; tiến h&agrave;nh phụ trọng huấn luyện, tại hắn c&oacute; thể dần dần chưởng khống &quot;Si&ecirc;u năng lực&quot;, hắn liền bắt đầu t&iacute;nh mũi nhọn huấn luyện, ngay từ đầu l&agrave; ch&eacute;n tr&agrave;, s&aacute;ch vở c&aacute;c loại, đằng sau biến th&agrave;nh c&aacute;i ghế c&aacute;i b&agrave;n, thế nhưng hắn chậm r&atilde;i ph&aacute;t hiện kh&ocirc;ng đ&uacute;ng.</p>', '2021-12-01 15:12:23', 5, 1),
(311, 77, 'Hogwarts', '2', '<p>Bởi v&igrave; Anduin ph&aacute;t hiện trọng lượng biến h&oacute;a kỳ thật cũng kh&ocirc;ng c&oacute; để cho hắn sản sinh phi thường lớn mệt nhọc cảm gi&aacute;c, thường xuy&ecirc;n tiến h&agrave;nh th&acirc;n thể huấn luyện hắn biết đ&acirc;y kh&ocirc;ng phải chuyện g&igrave; tốt, tựa như luyện cơ bắp luyện khối thời điểm kh&ocirc;ng cảm gi&aacute;c được x&eacute; r&aacute;ch cảm gi&aacute;c c&ugrave;ng mệt nhọc m&agrave; n&oacute;i, liền chứng minh kh&ocirc;ng c&oacute; huấn luyện đ&uacute;ng chỗ.<br />\r\n<br />\r\nBởi vậy Anduin rất nhanh đối với huấn luyện tiến h&agrave;nh điều chỉnh, kh&ocirc;ng tại một mặt truy cầu trọng lượng, hắn bắt đầu tiến h&agrave;nh k&eacute;o d&agrave;i th&igrave; huấn luyện, cũng ch&iacute;nh l&agrave; th&ocirc;ng qua &quot;Niệm lực&quot; để cho vật thể tận khả năng thời gian d&agrave;i lơ lửng.<br />\r\n<br />\r\nQuả nhi&ecirc;n c&aacute;i n&agrave;y sản sinh hiệu quả, ngay từ đầu c&oacute; thể ki&ecirc;n tr&igrave; 5 ph&uacute;t đồng hồ, liền sẽ bắt đầu đầu ch&aacute;ng v&aacute;ng n&atilde;o trướng, hắn cũng kh&ocirc;ng d&aacute;m tại c&ograve;n chưa hiểu loại năng lực n&agrave;y trước vượt qua phụ tải huấn luyện, bởi vậy cũng l&agrave; điểm đến l&agrave; dừng, bất qu&aacute; nghỉ ngơi một ng&agrave;y sau đ&oacute; cảm gi&aacute;c tinh thần kh&ocirc;ng c&oacute; ảnh hưởng g&igrave;, liền y&ecirc;n t&acirc;m tại mỗi l&uacute;c trời tối luyện c&ocirc;ng r&uacute;t thời gian tiến h&agrave;nh huấn luyện, đi qua quanh năm suốt th&aacute;ng t&iacute;ch lũy, Anduin c&oacute; thể ki&ecirc;n tr&igrave; thời gian cũng c&agrave;ng ng&agrave;y c&agrave;ng d&agrave;i.<br />\r\n<br />\r\nTại kinh lịch đ&atilde; hơn một năm r&egrave;n luyện về sau hiện tại Anduin đ&atilde; c&oacute; thể duy tr&igrave; vật thể lơ lửng gần 40 ph&uacute;t đồng hồ.<br />\r\n<br />\r\nChỉ thấy Anduin thao t&uacute;ng di động tr&ecirc;n kh&ocirc;ng trung tạ tay tại quanh th&acirc;n xoay tr&ograve;n v&acirc;y quanh, tựa như v&acirc;y quanh th&aacute;i dương h&agrave;nh tinh, đồng thời bắt đầu kh&ocirc;ng ngừng biến đổi phi h&agrave;nh quỹ t&iacute;ch, một hồi ấn 8 chữ v&acirc;y quanh, một hồi tr&ecirc;n dưới tung bay.<br />\r\n<br />\r\nSau một khoảng thời gian, Anduin tinh thần cảm thấy một ch&uacute;t mệt nhọc, hắn r&otilde; r&agrave;ng r&egrave;n luyện thời gian kh&ocirc;ng sai biệt lắm, liền thao t&uacute;ng tạ tay trở về vị tr&iacute; cũ, tho&aacute;ng gi&atilde;n ra th&acirc;n thể, &quot;Điều khiển l&ecirc;n c&agrave;ng ng&agrave;y c&agrave;ng nhẹ nh&otilde;m, kh&ocirc;ng biết l&uacute;c n&agrave;o t&agrave;i năng như trong tiểu thuyết đồng dạng Ngự kiếm phi h&agrave;nh nha.&quot;<br />\r\n<br />\r\nN&agrave;o ngờ tại Antony đang suy nghĩ lung tung thời điểm, tại England phương bắc chỗ n&agrave;o đ&oacute;, một chỗ t&ecirc;n l&agrave; Hogwarts trường học ma ph&aacute;p đang tại ph&aacute;i ph&aacute;t ra c&oacute; chứa nhập học th&ocirc;ng b&aacute;o con c&uacute; m&egrave;o, trong đ&oacute; một c&aacute;i đang hướng về London Elens Gi&aacute;o hội Ph&uacute;c Lợi Viện m&agrave; đến.<br />\r\n<br />\r\nS&aacute;ng sớm h&ocirc;m sau 6, l&uacute;c n&agrave;y trong c&ocirc; nhi viện cơ bản đều c&ograve;n kh&ocirc;ng người hoạt động, Anduin đ&aacute;nh thu&ecirc; ph&ograve;ng cửa sổ h&iacute;t s&acirc;u một hơi, thay xong y phục chậm r&atilde;i từ từ đi tới c&ocirc; nhi viện trong s&acirc;n, hơi k&eacute;o duỗi một ch&uacute;t, liền trong s&acirc;n đ&aacute;nh một bộ Th&ocirc;ng T&iacute; quyền.<br />\r\n<br />\r\n&quot;H&ocirc;...&quot; Anduin sướng kho&aacute;i thở d&agrave;i ra một hơi, lưu một th&acirc;n mồ h&ocirc;i rịn hắn một b&ecirc;n mở rộng th&acirc;n thể, một b&ecirc;n như thường ng&agrave;y hướng nh&agrave; ăn đi đến.<br />\r\n<br />\r\n&quot;Ch&agrave;o buổi s&aacute;ng a, Tris ma ma, n&agrave;y b&iacute; đỏ ch&aacute;o m&ugrave;i thơm ta thật xa tựu nghe đến.&quot; Anduin cười đối với đang đang chuẩn bị bữa s&aacute;ng Ph&uacute;c Lợi Viện nữ tu sĩ vấn an.<br />\r\n<br />\r\n&quot;Ch&agrave;o buổi s&aacute;ng a, Anduin, ngươi r&egrave;n luyện trở về, mỗi ng&agrave;y đều dậy sớm như thế, nếu như những h&agrave;i tử kh&aacute;c c&oacute; thể c&oacute; ngươi một nửa hiểu chuyện ta đ&atilde; c&oacute; thể bớt lo.&quot; Tuổi gi&agrave; Tris nữ tu sĩ một b&ecirc;n cho Anduin một ch&eacute;n ch&aacute;o, một b&ecirc;n cười n&oacute;i.<br />\r\n<br />\r\nAnduin tiếp nhận điểm t&acirc;m mỉm cười cũng kh&ocirc;ng c&oacute; trả lời, lời n&agrave;y hắn cũng kh&ocirc;ng d&aacute;m hồi, nếu như hắn cổ th&acirc;n thể n&agrave;y trong kh&ocirc;ng phải l&agrave; một người trưởng th&agrave;nh linh hồn m&agrave; n&oacute;i, hắn khả năng nếu so với những người kh&aacute;c c&agrave;ng kh&oacute; quản.<br />\r\n<br />\r\nQua loa ăn s&aacute;ng xong, cho Tris ma ma sau khi n&oacute;i c&aacute;m ơn, Anduin liền đứng dậy phản trở về ph&ograve;ng.<br />\r\n<br />\r\nVừa về tới trong ph&ograve;ng, Anduin liền cảm gi&aacute;c được kh&ocirc;ng đ&uacute;ng, một c&aacute;i m&agrave;u n&acirc;u con c&uacute; m&egrave;o đang tại hắn tr&ecirc;n b&agrave;n s&aacute;ch dỡ h&agrave;ng, đương nhi&ecirc;n nơi n&agrave;y c&aacute;i gọi l&agrave; dỡ h&agrave;ng kh&ocirc;ng phải n&oacute;i n&oacute; tại k&eacute;o Ba Ba, m&agrave; l&agrave; rất quen thuộc luyện tướng tr&ecirc;n đ&ugrave;i một phong thư dỡ xuống cũng đặt l&ecirc;n b&agrave;n, sau đ&oacute; liền bay đến bệ cửa sổ lẳng lặng nh&igrave;n xem Anduin.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nAnduin nh&igrave;n xem cảnh tượng n&agrave;y mơ hồ cảm gi&aacute;c được họa phong c&oacute; ch&uacute;t kh&ocirc;ng đ&uacute;ng, quay người đ&oacute;ng cửa ph&ograve;ng, trực tiếp đi về hướng b&agrave;n học cầm lấy phong thư, kh&ocirc;ng c&oacute; d&aacute;n tem, trong phong thư đang l&uacute;c d&ugrave;ng Phỉ Th&uacute;y lục mực nước viết một h&agrave;ng chữ.<br />\r\n<br />\r\nShaftesbury đường c&aacute;i, Elens Gi&aacute;o hội Ph&uacute;c Lợi Viện, Anduin. Wilson ti&ecirc;n sinh thu<br />\r\n<br />\r\nAnduin vượt qua phong thư, thấy được mặt sau vẽ lấy một c&aacute;i tấm chắn văn chương, văn chương trung t&acirc;m l&agrave; một c&aacute;i viết k&eacute;p &quot;H&quot; chữ c&aacute;i, tấm chắn bốn c&aacute;i g&oacute;c khắc lấy một đầu sư tử, một c&aacute;i ưng, một c&aacute;i chồn c&ugrave;ng một con rắn.<br />\r\n<br />\r\n&quot;Ngoạ t&agrave;o, đ&acirc;y l&agrave; Hogwarts?&quot; Anduin kh&ocirc;ng thể tin được ch&iacute;nh m&igrave;nh &aacute;nh mắt nh&igrave;n xem phong thư n&agrave;y, muốn biết r&otilde; Anduin chưa từng c&oacute; nghĩ tới ch&iacute;nh m&igrave;nh sẽ đến đến một c&aacute;i ma ph&aacute;p thế giới, coi như m&igrave;nh thức tỉnh ma lực cũng hiểu được kia bất qu&aacute; l&agrave; &quot;Đặc dị c&ocirc;ng năng&quot; a.<br />\r\n<br />\r\n&quot;Bất qu&aacute; kh&ocirc;ng đ&uacute;ng nha&quot;, Anduin tuy xuy&ecirc;n vi&ecirc;̣t hoặc l&agrave; n&oacute;i chuyển thế đối với kiếp trước k&yacute; ức c&oacute; ch&uacute;t mơ hồ, hơn nữa chưa từng c&oacute; nghĩ tới phương diện n&agrave;y qua dẫn đến n&agrave;y một loại bi&ecirc;n giới k&yacute; ức c&agrave;ng th&ecirc;m kh&ocirc;ng r&otilde; r&agrave;ng lắm, thật giống như người trưởng th&agrave;nh hồi ức l&uacute;c nhỏ l&uacute;c nhỏ thấy được Anime hội kh&ocirc;ng nhớ nổi nội dung cốt truyện đồng dạng.<br />\r\n<br />\r\nBất qu&aacute; chung quy Harry Potter điện ảnh c&ograve;n l&agrave; xem qua, lờ mờ c&ograve;n nhớ r&otilde; đ&oacute; l&agrave; thập ni&ecirc;n 90 về sau chuyện ph&aacute;t sinh.<br />\r\n<br />\r\nThế nhưng Anduin hiện tại vị tr&iacute; thời gian thế nhưng l&agrave; 1980 năm!<br />\r\n<br />\r\nAnduin tỉ mỉ hồi ức một ch&uacute;t tr&iacute; nhớ kiếp trước, chỉ nhớ r&otilde; Dumbledore, Harry Potter, Voldemort những t&ecirc;n n&agrave;y, cụ thể nội dung cốt truyện mười phần mơ hồ, thế nhưng kia kỳ thật kh&ocirc;ng trọng yếu, bởi v&igrave; ch&iacute;nh m&igrave;nh căn bản c&ugrave;ng điện ảnh nội dung cốt truyện kh&ocirc;ng phải l&agrave; một c&aacute;i thời đại người!<br />\r\n<br />\r\n&quot;Được chứ, vẫn cho l&agrave; m&igrave;nh hội đặc dị c&ocirc;ng năng, kết quả b&ecirc;n người to&agrave;n bộ đều si&ecirc;u nh&acirc;n&quot;, Anduin ph&aacute;t hiện m&igrave;nh vẫn cho l&agrave; l&agrave; si&ecirc;u năng lực &quot;Niệm lực&quot; kết quả kỳ thật l&agrave; &quot;Ma ph&aacute;p&quot; mười phần kh&ocirc;ng lời, nhưng may m&agrave; l&agrave; c&oacute; th&ecirc;m người trưởng th&agrave;nh linh hồn, h&iacute;t s&acirc;u mấy hơi ph&iacute;a sau b&igrave;nh phục.<br />\r\n<br />\r\nAnduin kh&ocirc;ng đang suy nghĩ lung tung, cầm lấy phong thư ngồi ở trước b&agrave;n s&aacute;ch, từ b&ecirc;n trong r&uacute;t ra tấm da d&ecirc; chăm ch&uacute; đọc lấy tới:<br />\r\n<br />\r\nHogwarts trường học ma ph&aacute;p hiệu trưởng: Albus. Dumbledore (quốc tế ma ph&aacute;p li&ecirc;n hiệp hội hội trưởng, Vu sư Hiệp Hội Hội Trưởng, Merlin tước sĩ đo&agrave;n Ma Ph&aacute;p Sư Cấp 1)<br />\r\n<br />\r\nTh&acirc;n &aacute;i Wilson ti&ecirc;n sinh:<br />\r\n<br />\r\nCh&uacute;ng ta vui sướng địa th&ocirc;ng b&aacute;o ng&agrave;i, ng&agrave;i đ&atilde; được ph&eacute;p tại Hogwarts trường học ma ph&aacute;p liền đọc. Theo t&iacute;n phụ thượng cần thiết s&aacute;ch vở v&agrave; trang bị danh s&aacute;ch. Học kỳ quyết định ch&iacute;n th&aacute;ng một ng&agrave;y bắt đầu. Ch&uacute;ng ta v&agrave;o khoảng Thất Nguyệt ba mươi mốt ng&agrave;y trước lặng chờ ng&agrave;i con c&uacute; m&egrave;o mang đến ng&agrave;i hồi &acirc;m.<br />\r\n<br />\r\nPh&oacute; hiệu trưởng (nữ) Minerva McGonagall th&acirc;n &aacute;i.<br />\r\n<br />\r\nHogwarts trường học ma ph&aacute;p<br />\r\n<br />\r\nNăm nhất t&acirc;n sinh c&acirc;̀n:<br />\r\n<br />\r\n1. Ba bộ đồ đồ hộp c&ocirc;ng t&aacute;c b&agrave;o (hắc sắc)<br />\r\n<br />\r\n2. Đỉnh đầu ban đ&ecirc;m mang đồ hộp đỉnh nhọn c&aacute;i mũ (hắc sắc)<br />\r\n<br />\r\n3. Một đ&ocirc;i ph&ograve;ng hộ bao tay (da rồng hoặc đồng loại t&agrave;i liệu chế t&aacute;c)<br />\r\n<br />\r\n4. Một kiện đ&ocirc;ng d&ugrave;ng &aacute;o cho&agrave;ng (hắc sắc, ng&acirc;n khấu trừ)<br />\r\n<br />\r\nXin ch&uacute; &yacute;: Đệ tử to&agrave;n bộ trang phục đồng đều tu xuyết hữu t&iacute;nh danh nh&atilde;n hiệu<br />\r\n<br />\r\n[ s&aacute;ch gi&aacute;o khoa ] to&agrave;n bộ đệ tử đồng đều cần chuẩn bị dưới đ&acirc;y s&aacute;ch b&aacute;o:<br />\r\n<br />\r\n&quot; ti&ecirc;u chuẩn ch&uacute; ngữ, sơ cấp &quot;, Miranda. Goshawk lấy<br />\r\n<br />\r\n&quot; ma ph&aacute;p lịch sử &quot;, Bathilda. Bagshot<br />\r\n<br />\r\n&quot; ma ph&aacute;p l&yacute; luận &quot;, Adalbert. Waffling lấy<br />\r\n<br />\r\n&quot; ban đầu học biến h&igrave;nh hướng dẫn &quot;, Emeric. Switch lấy</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot; ng&agrave;n loại thần kỳ thảo dược v&agrave; khuẩn loại &quot;, Phyllida. Spore lấy<br />\r\n<br />\r\n&quot; ma ph&aacute;p dược tề c&ugrave;ng nước thuốc &quot;. Arsenius Jigger<br />\r\n<br />\r\n&quot; qu&aacute;i th&uacute; cực kỳ nơi sản sinh &quot;, Newt. Scamander lấy<br />\r\n<br />\r\n&quot; hắc &aacute;m lực lượng: Tự vệ hướng dẫn &quot;, Quentin Trimble lấy<br />\r\n<br />\r\n[ c&aacute;i kh&aacute;c trang bị ]<br />\r\n<br />\r\nMột chi ma trượng<br />\r\n<br />\r\nMột c&aacute;i lớn nồi (t&iacute;ch chế, ti&ecirc;u chuẩn đ&aacute;nh gi&aacute; số 2)<br />\r\n<br />\r\nMột bộ thủy tinh hoặc thủy tinh b&igrave;nh thuốc nhỏ<br />\r\n<br />\r\nMột trận k&iacute;nh viễn vọng<br />\r\n<br />\r\nMột c&aacute;i c&acirc;n bằng đồng<br />\r\n<br />\r\nĐệ tử c&oacute; thể mang theo một con c&uacute; m&egrave;o hoặc một con m&egrave;o hoặc một c&aacute;i Thiềm Thừ.<br />\r\n<br />\r\nL&uacute;c n&agrave;y đặc biệt đệ tr&igrave;nh gia trưởng ch&uacute; &yacute;, năm nhất t&acirc;n sinh kh&ocirc;ng cho ph&eacute;p k&egrave;m theo ch&ocirc;̉i bay c&acirc;y chổi ( chỉ nam nữ Vu sư thừa l&uacute;c cưỡi c&aacute;i chổi )</p>', '2021-12-01 15:12:46', 4, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(312, 77, 'McGonagall Giáo Sư', '3', '<p>Anduin kỹ c&agrave;ng xem xong thư sờ sờ c&aacute;i cằm, hất đầu mắt nh&igrave;n tr&ecirc;n bệ cửa sổ con c&uacute; m&egrave;o, &quot;Ngươi tại đều ta hồi &acirc;m phải kh&ocirc;ng? Đ&aacute;ng tiếc ta l&uacute;c trước kh&ocirc;ng c&oacute; nu&ocirc;i dưỡng qua sủng vật, kh&ocirc;ng c&oacute; vật g&igrave; tốt c&oacute; thể chi&ecirc;u đ&atilde;i ngươi.&quot;<br />\r\n<br />\r\nTr&ecirc;n bệ cửa sổ con c&uacute; m&egrave;o như cũ trừng mắt mắt c&aacute; chết lẳng lặng nh&igrave;n chằm chằm Anduin.<br />\r\n<br />\r\n&quot;Hảo, ta hiện tại liền ghi hồi &acirc;m, &quot; Anduin nh&uacute;n nh&uacute;n vai, liền cầm lấy tr&ecirc;n b&agrave;n s&aacute;ch giấy b&uacute;t bắt đầu s&aacute;ng t&aacute;c hồi &acirc;m, d&ugrave;ng mười phần th&agrave;nh khẩn ngữ kh&iacute; biểu thị hết sức vui vẻ tiến nhập Hogwarts liền đọc, nhưng tiếc nuối biểu thị ch&iacute;nh m&igrave;nh từ trước đến nay chưa c&oacute; tiếp x&uacute;c qua ma ph&aacute;p thế giới, đồng thời ch&iacute;nh m&igrave;nh l&agrave; một đứa c&ocirc; nhi, hỏi c&oacute; hay kh&ocirc;ng c&oacute; thể cho l&agrave; m&igrave;nh cung cấp một &iacute;t trợ gi&uacute;p.<br />\r\n<br />\r\nViết xong hồi &acirc;m, Anduin đem phong thư nhập một c&aacute;i mới tinh phong thư, đưa tới con c&uacute; m&egrave;o trước mặt, bệ cửa sổ con c&uacute; m&egrave;o v&ocirc; c&ugrave;ng thuần thục tiếp nhận phong thư cũng tại tr&ecirc;n th&acirc;n thể đeo hảo, cũng kh&ocirc;ng quay đầu lại bay đi.<br />\r\n<br />\r\n&quot;Bỗng nhi&ecirc;n, c&ograve;n rất cao lạnh, cũng kh&ocirc;ng biết thời gian n&agrave;y ma ph&aacute;p thế giới l&agrave; dạng g&igrave;, Voldemort bị Harry đạn chết kh&ocirc;ng c&oacute;&quot; Anduin đối với &quot; Harry Potter &quot; chỉ c&oacute; một ch&uacute;t điểm ấn tượng, dường như l&agrave; tại Harry khi c&ograve;n b&eacute; Voldemort giết Harry kh&ocirc;ng th&agrave;nh bị giết lại, thế nhưng 1980 năm ma ph&aacute;p giới l&agrave; t&igrave;nh huống g&igrave;, Anduin thật sự l&agrave; kh&ocirc;ng hiểu ra sao.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng nghĩ nhiều như vậy, đều Hogwarts hồi &acirc;m a, tại bất kỳ thế giới thực lực đều l&agrave; cơ sở, trở n&ecirc;n mạnh mẽ khẳng định kh&ocirc;ng c&oacute; g&igrave; sai&quot; nghĩ xong liền tiếp theo c&ugrave;ng ng&agrave;y b&agrave;i học đi, Anduin ở c&ocirc; nhi viện nghỉ h&egrave; cho m&igrave;nh an b&agrave;i gấp v&ocirc; c&ugrave;ng tiếp cận r&egrave;n luyện hạng mục, tập thể dục, luyện quyền, đứng như cọc gỗ, c&oacute; dưỡng huấn luyện, minh tưởng, &quot;Niệm lực&quot; kh&ocirc;ng b&acirc;y giờ l&agrave; ma ph&aacute;p huấn luyện. . . Như cũ tại đ&acirc;u v&agrave;o đấy tiến h&agrave;nh.<br />\r\n<br />\r\nTự hạn chế sinh hoạt, thời gian lu&ocirc;n l&agrave; tr&ocirc;i qua rất nhanh, tại thu được Hogwarts nhập học thư th&ocirc;ng b&aacute;o kh&ocirc;ng c&oacute; hai ng&agrave;y, một vị kh&aacute;ch kh&ocirc;ng mời m&agrave; đến t&igrave;m đến ph&iacute;a tr&ecirc;n t&igrave;m đến Anduin.<br />\r\n<br />\r\n&quot;Ng&agrave;i l&agrave; n&oacute;i, ng&agrave;i ch&iacute;nh l&agrave; Hogwarts McGonagall gi&aacute;o sư sao?&quot; Anduin đối với xuất hiện ở gian ph&ograve;ng của m&igrave;nh m&ocirc;n khẩu một người trung ni&ecirc;n nữ t&iacute;nh hỏi, vị n&agrave;y được xưng McGonagall gi&aacute;o sư nữ nh&acirc;n đeo h&igrave;nh vu&ocirc;ng k&iacute;nh mắt, c&oacute; cong v&ograve;ng buộc th&agrave;nh cao b&uacute;i t&oacute;c t&oacute;c đen, ăn mặc m&agrave;u xanh l&aacute; c&acirc;y đậm trường b&agrave;o.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng sai, ta ch&iacute;nh l&agrave; Minerva McGonagall, Hogwarts ph&oacute; hiệu trưởng, ta nh&igrave;n thấy ngươi hồi &acirc;m Wilson ti&ecirc;n sinh, ch&uacute;ng ta sẽ đối với chưa c&oacute; tiếp x&uacute;c qua ma ph&aacute;p thế giới Muggle gia đ&igrave;nh tiến h&agrave;nh d&ograve; hỏi.&quot; McGonagall gi&aacute;o sư khả năng đột nhi&ecirc;n nghĩ đến Anduin th&acirc;n thế, đ&oacute;n lấy bổ sung &quot;Đương nhi&ecirc;n c&ograve;n c&oacute; như ngươi đồng dạng độc lập sinh hoạt tuổi trẻ ti&ecirc;n sinh cũng đồng dạng.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Kh&ocirc;ng mời ta tiến v&agrave;o ngồi một ch&uacute;t sao? Wilson ti&ecirc;n sinh.&quot;<br />\r\n<br />\r\n&quot;A, kh&ocirc;ng phải, kh&ocirc;ng c&oacute; &yacute; tứ McGonagall gi&aacute;o sư, mời đến, ta chỉ l&agrave; vẫn chưa ho&agrave;n to&agrave;n ti&ecirc;u h&oacute;a hết trường học ma ph&aacute;p n&agrave;y việc sự t&igrave;nh.&quot; Anduin mặt lộ vẻ &aacute;y n&aacute;y tr&aacute;nh ra th&acirc;n thể, đem McGonagall gi&aacute;o sư mời tiến đến.<br />\r\n<br />\r\nTiến đến trong ph&ograve;ng McGonagall gi&aacute;o sư liền bắt đầu quan s&aacute;t, tất cả gian ph&ograve;ng kh&ocirc;ng lớn, một giường lớn, một bộ c&aacute;i b&agrave;n, c&aacute;i b&agrave;n hợp với gi&aacute; s&aacute;ch, một c&aacute;i ngăn tủ, một c&acirc;y gi&aacute; &aacute;o, g&oacute;c hẻo l&aacute;nh chỉnh tề để đ&oacute; mấy c&aacute;i tạ tay, giường chiếu ph&ocirc;́ chỉnh tề, tr&ecirc;n bệ cửa sổ vẫn loại một chậu. . . , nếu như n&agrave;ng kh&ocirc;ng nh&igrave;n lầm lời đ&oacute; l&agrave; một chậu h&agrave;nh t&acirc;y?<br />\r\n<br />\r\nSạch sẽ gian ph&ograve;ng c&ugrave;ng sạch sẽ nhẹ nh&agrave;ng khoan kho&aacute;i bầu kh&ocirc;ng kh&iacute; McGonagall gi&aacute;o sư đối với Anduin sản sinh kh&ocirc;ng nhỏ hảo cảm, chung quy h&ocirc;m nay l&agrave; đột nhi&ecirc;n đến t&igrave;m hiểu, n&agrave;y chứng minh vị n&agrave;y t&acirc;n sinh b&igrave;nh thường cũng l&agrave; mười phần tự hạn chế.<br />\r\n<br />\r\n&quot;Xin lỗi, McGonagall gi&aacute;o sư, gian ph&ograve;ng tương đối đơn sơ.&quot; Anduin cho McGonagall gi&aacute;o sư đạo &acirc;m thanh &aacute;y n&aacute;y liền đi chuyển trong ph&ograve;ng duy nhất c&aacute;i ghế.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng cần kh&aacute;ch kh&iacute; Anduin, ngươi kh&ocirc;ng ngại ta xưng h&ocirc; như vậy ngươi đi, ch&iacute;nh ta chuẩn bị c&aacute;i ghế.&quot; Dứt lời McGonagall gi&aacute;o sư từ trong l&ograve;ng m&oacute;c ra ma trượng, đối với b&ecirc;n tường tạ tay chỉ, tạ tay trong chớp mắt liền thay đổi h&oacute;a th&agrave;nh một ghế dựa nhẹ nh&agrave;ng cũng ngồi xuống.<br />\r\n<br />\r\nAnduin cũng bị McGonagall gi&aacute;o sư chi&ecirc;u thức ấy h&ugrave; một ch&uacute;t, chung quy tại tr&ecirc;n TV nh&igrave;n c&ograve;n kh&ocirc;ng c&oacute; ph&aacute;t sinh ở trước mắt ch&acirc;n thật, bất qu&aacute; cũng cũng kh&ocirc;ng c&oacute; vứt bỏ rụt r&egrave;, k&eacute;o ra c&aacute;i ghế mặt hướng McGonagall gi&aacute;o sư c&ugrave;ng nhau ngồi xuống.<br />\r\n<br />\r\n&quot;Ta nh&igrave;n thấy tr&ecirc;n thư y&ecirc;u cầu th&aacute;ng 7 ng&agrave;y 31 trước hồi phục, ta c&ograve;n tưởng rằng sẽ l&agrave; tại th&aacute;ng 8 mới c&oacute; hồi &acirc;m hoặc tới chơi nha.&quot;<br />\r\n<br />\r\n&quot;Vậy chủ yếu l&agrave; nhằm v&agrave;o rất nhiều Vu sư gia đ&igrave;nh h&agrave;i tử, đối với kh&ocirc;ng c&oacute; tiếp x&uacute;c qua ma ph&aacute;p giới h&agrave;i tử ch&uacute;ng ta hội sớm d&ograve; hỏi, hơn nữa hiện tại ma ph&aacute;p giới tương đối mẫn cảm...&quot; McGonagall gi&aacute;o sư c&oacute; ch&uacute;t muốn n&oacute;i lại th&ocirc;i, bất qu&aacute; kh&ocirc;ng c&oacute; qu&aacute; nhiều tr&ograve; chuyện m&agrave; l&agrave; thẳng v&agrave;o chủ đề.<br />\r\n<br />\r\n&quot;C&oacute; được ma ph&aacute;p thi&ecirc;n ph&uacute; h&agrave;i tử đến tương ứng tuổi t&aacute;c liền sẽ bị th&ocirc;ng b&aacute;o nhập học, Hogwarts sẽ rất hảo Gi&aacute;o hội vị th&agrave;nh ni&ecirc;n Vu sư như thế n&agrave;o khống chế, sử dụng ma ph&aacute;p, tin tưởng ngươi tại những năm gần đ&acirc;y thời điểm hẳn l&agrave; cảm nhận được b&ecirc;n người ph&aacute;t sinh qua một &iacute;t đặc biệt sự t&igrave;nh a, vị th&agrave;nh ni&ecirc;n Vu sư hội dần dần thức tỉnh ma lực, c&oacute; đ&ocirc;i khi c&ograve;n c&oacute; thể sản sinh ma lực bạo động.&quot;<br />\r\n<br />\r\nAnduin sau khi nghe xong gật gật đầu, duỗi vung tay l&ecirc;n, một c&aacute;i ch&eacute;n liền tr&ocirc;i nổi ở trước mặt hắn.<br />\r\n<br />\r\nMcGonagall gi&aacute;o sư thấy như vậy một m&agrave;n nội t&acirc;m kh&ocirc;ng khỏi &acirc;m thầm kinh h&atilde;i, đ&acirc;y l&agrave; c&oacute; thể tự do nắm giữ tr&ocirc;i nổi nguyền rủa? Muốn biết r&otilde; đ&acirc;y c&ograve;n l&agrave; kh&ocirc;ng tiếng động kh&ocirc;ng trượng thi ph&aacute;p, &quot;Anduin ti&ecirc;n sinh, ngươi x&aacute;c định ngươi l&uacute;c trước kh&ocirc;ng c&oacute; học tập hoặc bị người chỉ đạo qua ma ph&aacute;p sao?&quot;<br />\r\n<br />\r\n&quot;L&agrave; McGonagall gi&aacute;o sư, ta tại ph&aacute;t hiện ta c&ugrave;ng người kh&aacute;c chỗ bất đồng, liền bắt đầu luyện từ từ tập, th&ocirc;ng qua ngồi xuống minh tưởng c&aacute;i g&igrave;, ta c&ograve;n tưởng rằng l&agrave; đặc dị c&ocirc;ng năng đ&acirc;u, đ&acirc;y l&agrave; ch&uacute;ng ta người b&igrave;nh thường thuyết ph&aacute;p, đương nhi&ecirc;n cũng cứ như vậy, hướng ng&agrave;i như vậy biến ra một c&aacute;i ghế ta c&oacute; thể l&agrave;m kh&ocirc;ng được.&quot; Anduin n&oacute;i.<br />\r\n<br />\r\n&quot;Tại Hogwarts ngươi hội học được.&quot; McGonagall gi&aacute;o sư nghe xong Anduin lời đối diện trước vị n&agrave;y vị th&agrave;nh ni&ecirc;n Vu sư c&agrave;ng thoả m&atilde;n, tự hạn chế, chăm chỉ, nỗ lực, đ&acirc;y l&agrave; thật tốt Vu sư hạt gi&ocirc;́ng nha, đảo mắt nghĩ đến trước mắt ma ph&aacute;p giới hỗn loạn c&ugrave;ng &acirc;m thầm nh&igrave;n xem hắc &aacute;m, kh&ocirc;ng khỏi lại sản sinh một ch&uacute;t lo nghĩ.<br />\r\n<br />\r\n&quot;Anduin, thấy được ngươi như vậy nỗ lực ta rất vui mừng, để cho ch&uacute;ng ta nhanh ch&oacute;ng l&ecirc;n đường đi, ch&uacute;ng ta thời gian rất gấp, ch&uacute;ng ta c&ugrave;ng với Ph&uacute;c Lợi Viện l&agrave;m tốt giao tiếp, c&ograve;n muốn mang theo ngươi sớm chuẩn bị đến trường muốn d&ugrave;ng đồ vật nha.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nMcGonagall gi&aacute;o sư L&ocirc;i Lệ Phong H&agrave;nh, hiệu suất cao th&aacute;i qu&aacute;, đơn độc c&ugrave;ng Ph&uacute;c Lợi Viện cao tầng giao lưu, rất nhanh liền c&ugrave;ng Ph&uacute;c Lợi Viện phương xử l&yacute; tốt Anduin đến trường c&ocirc;ng việc, Anduin ho&agrave;i nghi McGonagall gi&aacute;o sư khẳng định d&ugrave;ng c&aacute;i g&igrave; ma ph&aacute;p, Ph&uacute;c Lợi Viện phương diện cư nhi&ecirc;n đều kh&ocirc;ng c&oacute; sản sinh nghi vấn g&igrave;.<br />\r\n<br />\r\n&quot;Ng&agrave;i tại c&ugrave;ng Tris ma ma bọn họ c&acirc;u th&ocirc;ng thời điểm cũng d&ugrave;ng ma ph&aacute;p m&agrave;, bọn họ cư nhi&ecirc;n như vậy thống kho&aacute;i đồng &yacute;.&quot; Anduin c&ograve;n l&agrave; nhịn kh&ocirc;ng được hỏi l&ecirc;n.<br />\r\n<br />\r\n&quot;Một điểm nho nhỏ x&aacute;o trộn nguyền rủa, n&agrave;y c&oacute; trợ gi&uacute;p đề thăng hiệu suất kh&ocirc;ng phải sao?&quot; McGonagall gi&aacute;o sư hiếm thấy lộ ra h&agrave;m chứa nghịch ngợm nụ cười, &quot;Y&ecirc;n t&acirc;m n&agrave;y sẽ kh&ocirc;ng đối với bọn họ sản sinh c&aacute;i g&igrave; ảnh hưởng bất lợi, bọn họ cũng sẽ kh&ocirc;ng qu&ecirc;n ngươi, chờ ngươi nghỉ thời gian như cũ c&oacute; thể trở lại đ&acirc;y&quot; .<br />\r\n<br />\r\n&quot;Hảo, kế tiếp ta dẫn ngươi đi mua sắm đến trường cần d&ugrave;ng phẩm, x&eacute;t thấy ngươi t&igrave;nh huống, ta hướng trường học chủ tịch sẽ v&igrave; ngươi xin ngh&egrave;o kh&oacute; sinh học bổng, h&agrave;ng năm c&oacute; thể c&oacute; 12 mai Galleon trợ cấp&quot; .<br />\r\n<br />\r\n&quot;A đối với Galleon l&agrave; Vu sư thế giới tiền tệ, một c&aacute;i Galleon c&oacute; thể hối đo&aacute;i mười bảy c&aacute;i ng&acirc;n sickles, một c&aacute;i ng&acirc;n sickles lấy hối đo&aacute;i 29 c&aacute;i Nate.&quot;<br />\r\n<br />\r\nN&ecirc;n,phải hỏi kh&ocirc;ng hổ l&agrave; Anh quốc m&agrave;, liền tiền tệ hối đo&aacute;i đều như vậy nhức trứng, Anduin hơi nhức đầu.</p>', '2021-12-01 15:13:05', 5, 1),
(313, 77, 'Hẻm Xéo', '4', '<p>&quot;Bảng Anh kh&ocirc;ng thể tại ma ph&aacute;p giới sử dụng sao?&quot;<br />\r\n<br />\r\n&quot;Tại Gringotts c&oacute; thể d&ugrave;ng Muggle tiền tệ hối đo&aacute;i Galleon, năm bảng Anh c&oacute; thể hối đo&aacute;i 1 Galleon, bất qu&aacute; kh&ocirc;ng thể nhiều đổi, tối đa chỉ c&oacute; thể hối đo&aacute;i một trăm mai Galleon.&quot; McGonagall gi&aacute;o sư quanh năm phụ tr&aacute;ch chi&ecirc;u sinh c&ocirc;ng t&aacute;c, đối với c&aacute;c phương diện vấn đề đều mười phần r&otilde; r&agrave;ng, bất qu&aacute; n&agrave;ng đến kh&ocirc;ng cho rằng Anduin c&oacute; thể c&oacute; bao nhi&ecirc;u t&iacute;ch g&oacute;p, c&oacute; thể l&agrave; c&oacute; một ch&uacute;t tiền ti&ecirc;u vặt như hối đo&aacute;i a.<br />\r\n<br />\r\n&quot;Ồ, th&igrave; ra l&agrave; thế n&agrave;y, ta đ&acirc;y c&oacute; tiền.&quot; N&oacute;i qua ngay tại McGonagall gi&aacute;o sư giật m&igrave;nh dưới &aacute;nh mắt m&oacute;c ra một xấp bảng Anh, rất quen thuộc luyện d&ugrave;ng ng&oacute;n tay liếm liếm nước miếng điểm, &quot;Đủ, nhiều những c&aacute;i kia vừa vặn trở về tr&ecirc;n đường c&ograve;n c&oacute; thể mua điểm kh&aacute;c đồ vật.&quot;<br />\r\n<br />\r\nMcGonagall gi&aacute;o sư mang theo Anduin đi ra c&ocirc; nhi viện, ngoặt hướng b&ecirc;n cạnh một c&aacute;i kh&ocirc;ng người hẻm nhỏ, &quot;Vốn ta hẳn l&agrave; c&ugrave;ng với ngươi l&ecirc;n t&agrave;u Muggle phương tiện giao th&ocirc;ng hoặc l&agrave; kỵ sĩ xe bu&yacute;t đi Hẻm X&eacute;o, bất qu&aacute; gần nhất ma ph&aacute;p giới kh&ocirc;ng qu&aacute; y&ecirc;n ổn, chỉ c&oacute; thể ủy khuất ngươi đi theo ta một chỗ ảo ảnh di h&igrave;nh đi qua.&quot;<br />\r\n<br />\r\nDứt lời McGonagall gi&aacute;o sư liền r&uacute;t ra ma trượng n&oacute;i &quot;. Tới bắt lấy tay ta Anduin.&quot;<br />\r\n<br />\r\nMa ph&aacute;p giới kh&ocirc;ng qu&aacute; y&ecirc;n ổn? Anduin một b&ecirc;n ti&ecirc;u h&oacute;a lấy McGonagall gi&aacute;o sư, một b&ecirc;n bắt tay đưa tới.<br />\r\n<br />\r\nNgay sau đ&oacute; tại một hồi &quot;Ba ba&quot; trong tiếng McGonagall gi&aacute;o sư c&ugrave;ng Anduin ti&ecirc;u thất ở chỗ cũ.<br />\r\n<br />\r\n&quot;N&ocirc;n ọe!&quot; Anduin vừa c&ugrave;ng McGonagall gi&aacute;o sư từ ảo ảnh hiện h&igrave;nh xuất ra liền kh&ocirc;ng tự chủ được bắt đầu đỡ tường n&ocirc;n ọe, loại cảm gi&aacute;c đ&oacute; rất kh&oacute; khăn chịu, giống như bị n&eacute;m v&agrave;o một c&aacute;i ngăm đen m&aacute;y giặt quần &aacute;o bị khai mở tối cường ngăn cản đồng dạng, to&agrave;n th&acirc;n cao thấp cũng bị kh&ocirc;ng hiểu đ&egrave; &eacute;p, cuối c&ugrave;ng Anduin thậm ch&iacute; sản sinh ảo gi&aacute;c, thật giống như to&agrave;n th&acirc;n bị co lại th&agrave;nh một c&aacute;i tiểu cầu .<br />\r\n<br />\r\n&quot;Xin lỗi, ta lần đầu ti&ecirc;n sử dụng ma ph&aacute;p n&agrave;y thời điểm cũng kh&ocirc;ng chịu nổi.&quot; McGonagall gi&aacute;o sư dứt lời liền duỗi ra ma trượng chỉ hướng Anduin, ma trượng đỉnh to&aacute;t ra nhu h&ograve;a quang lưu, dần dần dung nhập v&agrave;o Anduin trong cơ thể, điều n&agrave;y l&agrave;m cho Anduin dễ chịu kh&ocirc;ng &iacute;t.<br />\r\n<br />\r\nMay m&agrave; Anduin b&igrave;nh thường th&acirc;n thể tố chất vượt qua thử th&aacute;ch, b&igrave;nh h&agrave;nh cảm cũng kh&ocirc;ng tệ, hơi tr&igrave; ho&atilde;n một l&aacute;t sau c&ograve;n l&agrave; b&igrave;nh tĩnh trở lại.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; ma ph&aacute;p sao? Như thế n&agrave;o cảm gi&aacute;c c&ugrave;ng trong tưởng tượng kh&ocirc;ng qu&aacute; đồng dạng, n&agrave;y kh&ocirc;ng ra một c&aacute;i Might and Magic tới truyền tống cũng qu&aacute; low a, Anduin trong nội t&acirc;m một hồi độc miệng, nhưng vẻ mặt vẫn biểu thị b&igrave;nh tĩnh.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Ch&uacute;ng ta kế tiếp đi đ&acirc;u, c&ograve;n phải lại truyền tống một lần sao?&quot;<br />\r\n<br />\r\n&quot;Đ&acirc;y kh&ocirc;ng phải l&agrave; truyền tống, l&agrave; ảo ảnh di h&igrave;nh c&ugrave;ng ảo ảnh hiện h&igrave;nh, đ&acirc;y l&agrave; trưởng th&agrave;nh Vu sư nh&oacute;m tương đối thường d&ugrave;ng xuất h&agrave;nh ma ph&aacute;p, chờ ngươi 7 ni&ecirc;n cấp đều c&oacute; thể học tập.&quot;<br />\r\n<br />\r\n&quot;Hơn nữa kh&ocirc;ng cần lo lắng, ch&uacute;ng ta lập tức đi ra.&quot;<br />\r\n<br />\r\nMcGonagall gi&aacute;o sư mang theo Anduin đi ra bọn họ mới xuất hiện ng&otilde; nhỏ, tại tr&ecirc;n đường phố đi một hồi, đi đến một c&aacute;i đĩa nhạc điếm c&ugrave;ng một hiệu s&aacute;ch giữa, Anduin một mực ở quan s&aacute;t xung quanh, ph&aacute;t hiện tại hai c&aacute;i trong cửa h&agrave;ng đang l&uacute;c r&otilde; r&agrave;ng mở lấy một nh&agrave; phong c&aacute;ch c&ugrave;ng hai b&ecirc;n cửa h&agrave;ng kh&aacute;c lạ qu&aacute;n bar, m&agrave; đến hướng người qua đường đối với c&aacute;i n&agrave;y l&agrave;m như kh&ocirc;ng thấy.<br />\r\n<br />\r\n&quot;Bọn họ nh&igrave;n kh&ocirc;ng thấy nh&agrave; n&agrave;y qu&aacute;n bar sao?&quot;<br />\r\n<br />\r\n&quot;Xuất sắc sức quan s&aacute;t, Anduin, nơi n&agrave;y bị g&acirc;y đại lượng che dấu ph&ugrave; ch&uacute;, Muggle nh&oacute;m l&agrave; nh&igrave;n kh&ocirc;ng thấy ph&aacute; nồi đồng qu&aacute;n bar.&quot; McGonagall gi&aacute;o sư bi&ecirc;n giải th&iacute;ch bi&ecirc;n dẫn Anduin đi v&agrave;o qu&aacute;n bar.<br />\r\n<br />\r\n&quot;Gi&aacute;o sư, ng&agrave;i thường xuy&ecirc;n n&oacute;i Muggle l&agrave; c&oacute; &yacute; g&igrave;, l&agrave; chỉ những c&aacute;i kia kh&ocirc;ng biết ma ph&aacute;p người b&igrave;nh thường &agrave;.&quot; Anduin cảm gi&aacute;c rất vớ vẩn, nhớ mang m&aacute;ng kiếp trước c&oacute; xem qua qua Harry Potter ấn tượng, thế nhưng th&acirc;̣t đúng đang tiếp x&uacute;c, như cũ c&aacute;i g&igrave; thưởng thức đều kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\n&quot;V&acirc;ng, Anduin, ch&uacute;ng ta xưng kh&ocirc;ng tin ma ph&aacute;p người b&igrave;nh thường hoặc l&agrave; n&oacute;i kh&ocirc;ng ma ph&aacute;p nh&acirc;n loại v&igrave; Muggle, bởi v&igrave; &quot; quốc tế Bảo Mật Ph&aacute;p &quot; duy&ecirc;n cớ, Muggle nh&oacute;m cũng kh&ocirc;ng biết ch&uacute;ng ta tồn tại.&quot;<br />\r\n<br />\r\nCũng kh&ocirc;ng biết &quot; quốc tế Bảo Mật Ph&aacute;p &quot; về sau tại Anh quốc ma ph&aacute;p giới c&ograve;n c&oacute; thể hay kh&ocirc;ng tiếp tục tồn tại, McGonagall gi&aacute;o sư nhớ tới trước mắt Anh quốc ma ph&aacute;p giới h&igrave;nh thức kh&ocirc;ng khỏi khẽ nh&iacute;u m&agrave;y.<br />\r\n<br />\r\n&quot;Vậy sao, kia phụ mẫu ta cũng l&agrave; Muggle.&quot; Anduin nhớ tới mới ra sinh kh&ocirc;ng bao l&acirc;u liền mất cha mẹ, trước kia tại Ph&uacute;c Lợi Viện vẫn điều tra, kh&ocirc;ng c&oacute; cảm gi&aacute;c c&oacute; c&aacute;i g&igrave; dị thường, ch&iacute;nh l&agrave; người b&igrave;nh thường, Anduin đem m&igrave;nh ma ph&aacute;p thi&ecirc;n ph&uacute; to&agrave;n bộ quy về chuyển thế tr&ecirc;n người.<br />\r\n<br />\r\n&quot;C&oacute; lẽ ngươi tổ ti&ecirc;n c&oacute; người l&agrave; Vu sư cũng n&oacute;i kh&ocirc;ng chừng, đương nhi&ecirc;n tại Muggle b&ecirc;n trong cũng sẽ sản sinh c&oacute; Vu sư thi&ecirc;n ph&uacute; h&agrave;i tử, Hogwarts tr&aacute;ch nhiệm ch&iacute;nh l&agrave; Gi&aacute;o hội c&aacute;c ngươi chưởng khống bản th&acirc;n ma ph&aacute;p.&quot;<br />\r\n<br />\r\nAnduin c&ugrave;ng McGonagall gi&aacute;o sư bi&ecirc;n tr&ograve; chuyện vừa đi nhập qu&aacute;n bar, trong qu&aacute;n rượu hiển lộ mười phần lờ mờ đơn sơ, lấy &aacute;nh s&aacute;ng cực kỳ kh&ocirc;ng xong, c&oacute; một c&aacute;i quầy bar, g&oacute;c hẻo l&aacute;nh b&oacute;ng mờ trong b&agrave;y c&oacute; mấy b&agrave;n lớn.<br />\r\n<br />\r\nHơn nữa nh&igrave;n l&ecirc;n kh&ocirc;ng c&oacute; g&igrave; sinh &yacute;, qu&aacute;n bar l&atilde;o bản một người tại quầy bar y&ecirc;n lặng lau ch&eacute;n.<br />\r\n<br />\r\n&quot;Tom, gần nhất sinh &yacute; thế n&agrave;o.&quot; McGonagall gi&aacute;o sư nh&igrave;n l&ecirc;n c&ugrave;ng l&atilde;o bản rất quen thuộc.<br />\r\n<br />\r\n&quot;C&ograve;n c&oacute; thể thế n&agrave;o, gần nhất c&agrave;ng ng&agrave;y c&agrave;ng loạn, đ&ecirc;m qua tại Hẻm Knockturn Thần S&aacute;ng c&ugrave;ng bọn họ lại giao hỏa, tiếng nổ mạnh thiếu ch&uacute;t cầm ta n&agrave;y ch&eacute;n chấn vỡ, sinh &yacute; cũng c&agrave;ng ng&agrave;y c&agrave;ng kh&ocirc;ng tốt l&agrave;m...&quot; Tom cảm gi&aacute;c l&agrave; tốt như chưa c&ugrave;ng người n&oacute;i chuyện, h&aacute; miệng liền ph&agrave;n n&agrave;n kh&ocirc;ng ngừng.<br />\r\n<br />\r\n&quot;Đi Tom, nơi n&agrave;y c&ograve;n c&oacute; vị tuổi trẻ ti&ecirc;n sinh nha.&quot;<br />\r\n<br />\r\n&quot;L&agrave; sắp nhập học t&acirc;n sinh &agrave;.&quot; Tom lườm một c&aacute;i n&oacute;i, đo&aacute;n chừng hắn cũng thấy qu&aacute;i kh&ocirc;ng kinh.<br />\r\n<br />\r\n&quot;Ngươi hảo ti&ecirc;n sinh.&quot; Với tư c&aacute;ch l&agrave; lễ nghi chi bang xuất th&acirc;n Anduin, đến l&uacute;c đ&oacute; hiển lộ nho nh&atilde; lễ độ.<br />\r\n<br />\r\n&quot;Mau v&agrave;o đi th&ocirc;i, một hồi bầu trời tối đen liền kh&ocirc;ng an to&agrave;n.&quot; Thấy được lễ ph&eacute;p Anduin, Tom hảo t&acirc;m nhắc nhở đến.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nMcGonagall gi&aacute;o sư hướng qu&aacute;n bar l&atilde;o bản gật gật đầu, mang theo Anduin xuy&ecirc;n qua qu&aacute;n bar, đi đến ph&aacute; nồi đồng qu&aacute;n bar s&acirc;n vườn nhỏ, &quot;B&ecirc;n trong ch&iacute;nh l&agrave; Hẻm X&eacute;o, tương lai học tập dụng cụ mua sắm đều lại ở chỗ n&agrave;y, thấy r&otilde; r&agrave;ng, về sau ngươi được từ m&igrave;nh.&quot;<br />\r\n<br />\r\n&quot;Th&ocirc;ng qua d&ugrave;ng ma trượng g&otilde; th&ugrave;ng r&aacute;c b&ecirc;n cạnh đặc biệt tường gạch, th&ugrave;ng r&aacute;c ph&iacute;a tr&ecirc;n số ba khối, lại ho&agrave;nh lấy số hai khối, đều c&oacute; thể tiến nhập&quot; .<br />\r\n<br />\r\nDứt lời McGonagall gi&aacute;o sư r&uacute;t ra ma trượng, g&otilde; tương ứng vị tr&iacute;, chỉ thấy nguy&ecirc;n bản cũ n&aacute;t tường gạch tại McGonagall gi&aacute;o sư đ&aacute;nh kh&ocirc;ng ngừng lật qua lật lại, dần dần biến h&oacute;a ra một người cao lớn cổng v&ograve;m.<br />\r\n<br />\r\n&quot;Hoan ngh&ecirc;nh đi đến Hẻm X&eacute;o, Anduin.&quot;<br />\r\n<br />\r\nAnduin c&aacute;i miệng nhỏ nhắn khẻ nhếch nh&igrave;n xem một m&agrave;n n&agrave;y, đối với ma ph&aacute;p thế giới c&agrave;ng hướng tới, lướt qua cổng v&ograve;m vừa nh&igrave;n, đập v&agrave;o mi mắt l&agrave; một mảnh đ&aacute; cuội trải th&agrave;nh phố d&agrave;i, phố d&agrave;i hai b&ecirc;n ph&acirc;n bố lấy đủ loại kiểu d&aacute;ng cửa h&agrave;ng.<br />\r\n<br />\r\nBất qu&aacute; Anduin ch&uacute; &yacute; tới, n&agrave;y sẽ Hẻm X&eacute;o cũng kh&ocirc;ng c&oacute; hắn trong tưởng tượng hối hả bộ d&aacute;ng, hai b&ecirc;n đường phố c&oacute; c&aacute; biệt cửa h&agrave;ng kh&ocirc;ng c&oacute; khai trương, tr&ecirc;n đường c&ograve;n c&oacute; một &iacute;t ăn mặc áo khoác người đi đường, xem bọn hắn bộ d&aacute;ng kh&ocirc;ng muốn l&agrave; tại dạo phố phản giống như l&agrave; tại tuần tra .<br />\r\n<br />\r\n&quot;Gi&aacute;o sư, những c&aacute;i kia mặc áo khoác người qua đường nh&igrave;n l&ecirc;n kh&ocirc;ng giống như l&agrave; kh&aacute;ch h&agrave;ng nha, chẳng lẽ n&oacute;i...&quot; Anduin hỏi ra ch&iacute;nh m&igrave;nh nghi vấn, kết hợp vừa bắt đầu McGonagall gi&aacute;o sư c&ugrave;ng Tom đối thoại, trong nội t&acirc;m sản sinh một c&aacute;i kh&ocirc;ng r&otilde; &yacute; niệm trong đầu.<br />\r\n<br />\r\n&quot;Những thứ kia Thần S&aacute;ng, ở b&ecirc;n cạnh duy tr&igrave; trị an, gần nhất ma ph&aacute;p giới kh&ocirc;ng phải l&agrave; rất th&aacute;i b&igrave;nh, bất qu&aacute; nơi n&agrave;y l&agrave; Anh quốc ma ph&aacute;p giới phồn hoa nhất thương nghiệp đường đi, vẫn rất an to&agrave;n, bọn họ chỉ l&agrave; th&ocirc;ng lệ c&ocirc;ng vụ, hơn nữa đừng lo lắng, ngươi tại Hogwarts sẽ rất an to&agrave;n, ta cam đoan!&quot; McGonagall gi&aacute;o sư v&igrave; Anduin giải th&iacute;ch n&oacute;i.</p>', '2021-12-01 15:13:26', 5, 1),
(314, 77, 'Ma Trượng', '5', '<p>&quot;Đi th&ocirc;i, ch&uacute;ng ta đi trước Gringotts đổi &iacute;t tiền, ta nghĩ ngươi cũng chờ kh&ocirc;ng được a.&quot; McGonagall gi&aacute;o sư biết Anduin tồn khoản kh&ocirc;ng &iacute;t.<br />\r\n<br />\r\n&quot;Gringotts l&agrave; đ&oacute; l&agrave; Anh quốc duy nhất một nh&agrave; Vu sư ng&acirc;n h&agrave;ng, từ một bầy y&ecirc;u tinh kinh doanh, nơi đ&oacute; bị xưng l&agrave; tr&ecirc;n thế giới chỉ đứng sau Hogwarts đệ nhị an to&agrave;n địa phương.&quot; McGonagall gi&aacute;o sư vừa đi vừa v&igrave; Anduin giới thiệu n&oacute;i<br />\r\n<br />\r\nTại McGonagall gi&aacute;o sư dưới sự dẫn dắt, Anduin đi đến một c&aacute;i đường đi ph&acirc;n nh&aacute;nh giao lộ, một b&ecirc;n cột mốc đường thượng viết Hẻm Knockturn, một b&ecirc;n l&agrave; Hẻm X&eacute;o chủ đường, Hẻm X&eacute;o c&ugrave;ng Hẻm Knockturn tiếp lời đứng vững một t&ograve;a bạch sắc kiến tr&uacute;c, m&ocirc;n khẩu đứng mặc m&agrave;u đỏ tươi nạm v&agrave;ng chế phục y&ecirc;u tinh thủ vệ, sau khi tiến v&agrave;o đầu ti&ecirc;n l&agrave; xuy&ecirc;n qua đệ nhất s&aacute;ng long lanh Thanh Đồng cửa, hai b&ecirc;n y&ecirc;u tinh thủ vệ tại bọn hắn đi qua th&igrave; c&uacute;i người ch&agrave;o, tại xuy&ecirc;n qua đạo thứ hai ng&acirc;n sắc sau đại m&ocirc;n b&ecirc;n trong l&agrave; rộng r&atilde;i đ&aacute; cẩm thạch ph&ograve;ng, ước chừng gần trăm mười c&aacute;i y&ecirc;u tinh ngồi ở trưởng ph&iacute;a sau quầy hướng đại sổ s&aacute;ch thượng qua loa đăng k&yacute;. C&oacute; &iacute;ch thi&ecirc;n b&igrave;nh sẵn tiền tệ, c&oacute; &iacute;ch k&iacute;nh quang lọc kiểm tra đo lường bảo thạch.<br />\r\n<br />\r\nXem ra ngoại giới rung chuyển đối với mấy c&aacute;i n&agrave;y y&ecirc;u tinh nh&oacute;m sinh hoạt cũng kh&ocirc;ng c&oacute; sản sinh bao nhi&ecirc;u ảnh hưởng.<br />\r\n<br />\r\nTại McGonagall gi&aacute;o sư chỉ dẫn hạ Anduin đi đến một c&aacute;i quầy h&agrave;ng trước mặt, đưa ra Hogwarts nhập học thư th&ocirc;ng b&aacute;o, nhận lấy ngh&egrave;o kh&oacute; sinh học bổng 12 Galleon, tuy Anduin c&oacute; tiền c&oacute; thể hối đo&aacute;i, thế nhưng d&ugrave; sao đ&atilde; xin, kh&ocirc;ng muốn ngu sao m&agrave; kh&ocirc;ng muốn, về sau lại d&ugrave;ng bảng Anh hối đo&aacute;i 100 Galleon, đối với một c&aacute;i 11 tuổi thiếu ni&ecirc;n m&agrave; n&oacute;i, đ&acirc;y đ&atilde; l&agrave; một khoản tiền lớn.<br />\r\n<br />\r\nAnduin &ocirc;m tr&agrave;n đầy Galleon d&ecirc; t&uacute;i da đi ra Gringotts, đưa tay m&oacute;c ra từng mai tỉ mỉ xem nh&igrave;n, &quot;Thoạt nh&igrave;n l&agrave; v&agrave;ng r&ograve;ng, thế nhưng c&oacute; cảm gi&aacute;c c&ugrave;ng phổ th&ocirc;ng ho&agrave;ng kim kh&ocirc;ng qu&aacute; đồng dạng.&quot;<br />\r\n<br />\r\n&quot;Vậy đều l&agrave; từ y&ecirc;u tinh nh&oacute;m d&ugrave;ng ho&agrave;ng kim chế tạo, sử dụng đặc biệt y&ecirc;u tinh c&ocirc;ng nghệ, nghe n&oacute;i vẫn g&acirc;y đặc hữu y&ecirc;u tinh ma ph&aacute;p, đề ph&ograve;ng dừng lại ngoại nh&acirc;n trực tiếp d&ugrave;ng ho&agrave;ng kim giả tạo.&quot;<br />\r\n<br />\r\n&quot;Ta nghĩ ngươi đ&atilde; kh&ocirc;ng thể chờ đợi được nghĩ phải c&oacute; được một c&acirc;y thuộc về ch&iacute;nh ngươi ma trượng, tại ma ph&aacute;p giới kh&ocirc;ng c&oacute; ma trượng c&oacute; thể nửa bước kh&oacute; đi.&quot; McGonagall gi&aacute;o sư nh&igrave;n xem &ocirc;m khoản tiền lớn Anduin, buồn cười n&oacute;i.<br />\r\n<br />\r\nVừa n&oacute;i lời McGonagall gi&aacute;o sư dẫn Anduin, đi đến Ollivander ma trượng điếm, một nh&agrave; lịch sử đ&atilde; l&acirc;u ma trượng điếm, Anduin đi theo McGonagall gi&aacute;o sư đi v&agrave;o cửa h&agrave;ng n&agrave;y b&ecirc;n trong.<br />\r\n<br />\r\nTrong cửa h&agrave;ng thoạt nh&igrave;n l&agrave; một c&aacute;i chen ch&uacute;c, hẹp h&ograve;i, cũ n&aacute;t tiểu điếm, ph&iacute;a trước cửa sổ c&oacute; một c&acirc;y ma trượng đặt ở một c&aacute;i phai m&agrave;u tử sắc tr&ecirc;n nệm triển l&atilde;m, trong tiệm những c&aacute;i kia tốc h&agrave;nh trần nh&agrave; trong tủ ch&eacute;n nhồi v&agrave;o ng&agrave;n vạn c&aacute;i trưởng h&igrave;nh chật vật c&aacute;i hộp, cửa h&agrave;ng đối với đ&agrave;i một vị th&acirc;n cao gầy l&atilde;o đầu đang y&ecirc;n lặng lau sạch lấy một c&acirc;y ma trượng.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Buổi chiều hảo, Ollivander ti&ecirc;n sinh, ch&uacute;ng ta tới mua sắm ma trượng.&quot;<br />\r\n<br />\r\n&quot;A, l&agrave; Minerva, cửu lại một phần hai tấc Anh, lạnh &aacute;o mộc, c&oacute; mấy ng&agrave;y n&agrave;y kh&ocirc;ng gặp ngươi, thời gian thực vui vẻ, lại đ&atilde; cho t&acirc;n sinh nhập học thời điểm!!!&quot; Ollivander c&uacute;i đầu nh&igrave;n về ph&iacute;a McGonagall gi&aacute;o sư, c&oacute; đảo mắt nh&igrave;n b&ecirc;n người Anduin.&quot;Gần nhất mấy ng&agrave;y n&agrave;y cũng kh&ocirc;ng hay qua, nghe n&oacute;i &agrave; ng&agrave;y h&ocirc;m qua...&quot;<br />\r\n<br />\r\n&quot;Ng&agrave;y h&ocirc;m qua rối loạn kh&ocirc;ng c&oacute; k&eacute;o d&agrave;i hạ xuống, ng&agrave;i muốn đối với ma ph&aacute;p bộ c&ugrave;ng Dumbledore c&oacute; l&ograve;ng tin!&quot; McGonagall gi&aacute;o sư liếc mắt nh&igrave;n Anduin, cắt đứt Ollivander, sợ h&atilde;i những c&aacute;i n&agrave;y mặt tr&aacute;i tin tức truyền b&aacute; đả k&iacute;ch đến tiểu Vu sư đối với ma ph&aacute;p giới l&ograve;ng tin.<br />\r\n<br />\r\n&quot;Đ&uacute;ng vậy a, Dumbledore, nếu kh&ocirc;ng l&agrave; c&ograve;n c&oacute; hắn đang chủ tr&igrave; đại cục, ta đo&aacute;n chừng đ&atilde; sớm đ&oacute;ng cửa rời đi, kh&ocirc;ng n&oacute;i những c&aacute;i n&agrave;y, tới cho ta xem nh&igrave;n vị n&agrave;y tuổi trẻ ti&ecirc;n sinh th&iacute;ch hợp c&aacute;i dạng g&igrave; ma trượng.&quot; Ollivander cũng kh&ocirc;ng c&oacute; tại k&eacute;o d&agrave;i l&uacute;c trước chủ đề, quay đầu nh&igrave;n về ph&iacute;a Anduin.<br />\r\n<br />\r\n&quot;Ng&agrave;i khỏe chứ, ti&ecirc;n sinh, ta l&agrave; Anduin. Wilson.&quot;<br />\r\n<br />\r\n&quot;Ngươi hảo Wilson ti&ecirc;n sinh, giống như ngươi vậy c&oacute; lễ ph&eacute;p người trẻ tuổi hiện tại cũng kh&ocirc;ng thấy nhiều.&quot; Ollivander ti&ecirc;n sinh cười từ trong t&uacute;i &aacute;o tr&ecirc;n lấy ra một cuốn thật d&agrave;i ấn c&oacute; ng&acirc;n sắc khắc độ thước cuộn.<br />\r\n<br />\r\n&quot;Ừ. . . Ngươi quen d&ugrave;ng c&aacute;i tay kia?&quot;<br />\r\n<br />\r\n&quot;Ta th&oacute;i quen d&ugrave;ng tay phải.&quot;<br />\r\n<br />\r\nOllivander bắt đầu v&igrave; Anduin lượng nhỏ, trước từ đầu vai đến đầu ng&oacute;n tay, lại từ cổ tay đến khuỷu tay &quot;Tuổi c&ograve;n trẻ th&acirc;n thể cứ như vậy cường tr&aacute;ng, kh&ocirc;ng đơn giản nha!&quot; Ollivander chạm đến Anduin cứng rắn c&aacute;nh tay cơ bắp, kh&ocirc;ng khỏi ph&aacute;t ra t&aacute;n thưởng.<br />\r\n<br />\r\nAnduin kh&ocirc;ng khỏi to&agrave;n th&acirc;n nổi da g&agrave; tất cả đứng l&ecirc;n, l&atilde;o đầu n&agrave;y c&oacute; hay kh&ocirc;ng c&oacute; vấn đề nha, d&ugrave;ng c&acirc;y thước lượng cho d&ugrave;, tại tr&ecirc;n th&acirc;n người sờ tới sờ lui, mua c&aacute;i ma trượng vẫn cũng bị người chấm m&uacute;t?<br />\r\n<br />\r\nOllivander động t&aacute;c rất nhanh, tại Anduin c&ograve;n đang suy nghĩ mi&ecirc;n man thời điểm, đ&atilde; từ tr&ecirc;n kệ r&uacute;t ra một c&acirc;y ma trượng đưa qua.<br />\r\n<br />\r\n&quot;Tới thử xem căn n&agrave;y, s&aacute;p ong mộc, Phượng Ho&agrave;ng l&ocirc;ng vũ, mười một lại một phần hai tấc Anh, ương ngạnh v&agrave; dũng cảm.&quot;<br />\r\n<br />\r\nAnduin tiếp nhận tay, nhẹ nh&agrave;ng huy vũ một ch&uacute;t, ma trượng mũi nhọn đột nhi&ecirc;n tạc xuất một đo&agrave;n hỏa diễm.<br />\r\n<br />\r\n&quot;Hảo, xem ra kh&ocirc;ng th&iacute;ch hợp ngươi.&quot; Ollivander vội v&agrave;ng đem ma trượng r&uacute;t đi, lại lấy ra một c&acirc;y t&acirc;n.<br />\r\n<br />\r\n&quot;Nh&igrave;n xem căn n&agrave;y thế n&agrave;o, du mộc, Hỏa Long tiếng l&ograve;ng chế t&aacute;c, cửu lại ba phần tư tấc Anh, tr&iacute; tuệ ưu nh&atilde; biểu tượng.&quot;<br />\r\n<br />\r\nAnduin vừa mới tiếp nhận, đ&atilde; bị Ollivander r&uacute;t đi, &quot;Xem ra cũng kh&ocirc;ng phải căn n&agrave;y.&quot;<br />\r\n<br />\r\n&quot;Lại tới thử xem căn n&agrave;y, đỏ cao su mộc, giống đực độc gi&aacute;c th&uacute; mao, mười lại một phần hai tấc Anh, nhanh nhẹn hơn người.&quot;<br />\r\n<br />\r\nAnduin tiếp nhận nhẹ nh&agrave;ng vung l&ecirc;n, tr&ecirc;n quầy b&igrave;nh hoa trực tiếp bạo liệt ra.<br />\r\n<br />\r\n&quot;Hảo ba, xem ra ngươi đ&atilde; đầy đủ nhạy b&eacute;n, n&agrave;y c&oacute; ch&uacute;t linh mẫn qu&aacute; mức.&quot; Ollivander thu hồi ma trượng đạo</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Y&ecirc;n t&acirc;m, ngươi ở nơi n&agrave;y nhất định c&oacute; thể t&igrave;m tới th&iacute;ch hợp ngươi ma trượng.&quot; Kế tiếp Anduin lại thử tốt mấy c&acirc;y, nhưng Ollivander đều biểu thị cũng kh&ocirc;ng th&iacute;ch hợp.<br />\r\n<br />\r\n&quot;Tới thử xem căn n&agrave;y, l&ocirc;i k&iacute;ch mộc, sản tự một gốc c&acirc;y cường tr&aacute;ng hắc đ&agrave;n mộc, L&ocirc;i Điểu l&ocirc;ng vũ, mười hai lại một phần tư tấc Anh, ki&ecirc;n định m&agrave; lại ch&uacute;t n&agrave;o kh&ocirc;ng lay được quyết t&acirc;m, mười phần cứng cỏi, đ&acirc;y c&ograve;n l&agrave; d&ugrave;ng đ&atilde; từng Newt đưa ta t&agrave;i liệu l&agrave;m, gi&aacute; trị phi ph&agrave;m, l&uacute;c trước gia c&ocirc;ng căn n&agrave;y ma trượng c&oacute; thể ph&iacute; l&atilde;o đại lực, bất qu&aacute; ngẫu nhi&ecirc;n hội sản sinh hồ quang điện, nghĩ phải lấy được n&oacute; thừa nhận c&oacute; thể kh&oacute; khăn, bắt bẻ kh&aacute;ch nh&acirc;n n&ecirc;n thử một ch&uacute;t bắt bẻ ma trượng.&quot; Ollivander đưa qua một c&acirc;y Mặc Ngọc ma trượng.<br />\r\n<br />\r\nAnduin vừa nh&igrave;n thấy căn n&agrave;y ngọc ma trượng, đ&atilde; bị n&oacute; th&acirc;m th&uacute;y hấp dẫn, vừa tiếp x&uacute;c với qua tay, cũng cảm gi&aacute;c trong tay ma trượng c&ugrave;ng m&igrave;nh huyết mạch tương th&ocirc;ng đồng dạng, biến th&agrave;nh th&acirc;n thể k&eacute;o d&agrave;i.<br />\r\n<br />\r\nAnduin vung khẽ ma trượng, trượng ti&ecirc;m lập l&ograve;e ra rất nhỏ điện quang, hơi hơi nhanh ch&oacute;ng.<br />\r\n<br />\r\n&quot;Ch&iacute;nh l&agrave; căn n&agrave;y, mỗi c&acirc;y ma trượng c&oacute; thể t&igrave;m tới ph&ugrave; hợp chủ nh&acirc;n ta đều tự đ&aacute;y l&ograve;ng cảm thấy cao hứng, nh&igrave;n ra n&oacute; rất th&iacute;ch ngươi.&quot; Ollivander vui sướng cười n&oacute;i, hắn l&agrave; một vị thuần t&uacute;y thợ thủ c&ocirc;ng, v&igrave; ch&iacute;nh m&igrave;nh t&aacute;c phẩm t&igrave;m đến c&otilde;i đi về cũng l&agrave;m hắn mười phần vui sướng.<br />\r\n<br />\r\n&quot;Cảm ơn ng&agrave;i, Ollivander ti&ecirc;n sinh, ta cũng v&ocirc; c&ugrave;ng th&iacute;ch n&oacute;.&quot; Anduin vui v&ocirc; c&ugrave;ng vuốt vuốt vừa tới tay ma trượng.<br />\r\n<br />\r\n&quot;&Ocirc; Mộc cũng ch&iacute;nh l&agrave; hắc đ&agrave;n mộc, v&ocirc; c&ugrave;ng ph&ugrave; hợp chiến đấu ma ph&aacute;p c&ugrave;ng biến h&igrave;nh thuật, tin tưởng n&oacute; hội trong tay ngươi đại ph&oacute;ng dị sắc, mặt kh&aacute;c Vu sư đệ nhất c&acirc;y ma trượng thống nhất gi&aacute; b&aacute;n 7 Galleon, dư thừa hội từ Hogwarts tiến h&agrave;nh phụ cấp, cũng đừng lưu lạc hoặc hư hao!&quot; Ollivander nhắc nhở<br />\r\n<br />\r\n&quot;Y&ecirc;n t&acirc;m đi, Ollivander ti&ecirc;n sinh&quot; Anduin vui sướng phục tiền, cũng lần nữa hướng Ollivander ti&ecirc;n sinh n&oacute;i lời cảm tạ, c&ugrave;ng McGonagall gi&aacute;o sư đi ra Ollivander ma trượng điếm.</p>', '2021-12-01 15:13:47', 6, 1),
(315, 77, 'Potter Vợ Chồng', '6', '<p>&quot;Hảo, Anduin, chọn lựa ma trượng ch&uacute;ng ta hoa kh&ocirc;ng &iacute;t thời gian, kế tiếp muốn nhanh ch&oacute;ng mua sắm c&aacute;i kh&aacute;c dụng cụ.&quot; McGonagall gi&aacute;o sư nh&igrave;n sắc trời, đối với Anduin n&oacute;i.<br />\r\n<br />\r\n&quot;Hảo gi&aacute;o sư.&quot;<br />\r\n<br />\r\nKế tiếp tại McGonagall gi&aacute;o sư c&ugrave;ng đi hạ Anduin lại lần lượt đi đến biến h&oacute;a mực nước tiệm văn ph&ograve;ng phẩm mua sắm đầy đủ tấm da d&ecirc;, mực nước c&ugrave;ng mấy chi l&ocirc;ng vũ b&uacute;t, lại đang Madam Malkin trường b&agrave;o cửa h&agrave;ng, Flourish and Blotts, Potage nồi nấu quặng điếm mua sắm đến trường c&acirc;̀n nồi nấu quặng, Vu sư phục c&ugrave;ng nguy&ecirc;n bộ t&agrave;i liệu giảng dạy.<br />\r\n<br />\r\nAnduin vẫn mua một con c&uacute; m&egrave;o c&ugrave;ng sủng vật khẩu phần lương thực, hắn một chỉ cảm thấy con c&uacute; m&egrave;o loại động vật n&agrave;y ti tiện hề hề nh&igrave;n l&ecirc;n rất buồn cười.<br />\r\n<br />\r\nChỉ chốc l&aacute;t tại Hẻm X&eacute;o đ&atilde; ti&ecirc;u ph&iacute; kh&ocirc;ng thời gian ngắn, ho&agrave;ng h&ocirc;n trời chiều cũng chiếu xuống.<br />\r\n<br />\r\n&quot;Chỉ tới đ&acirc;y th&ocirc;i, Anduin, đến trường cần muốn c&aacute;i g&igrave; cơ vốn đ&atilde; mua sắm ho&agrave;n tất, thời gian cũng kh&ocirc;ng c&ograve;n sớm, ta c&ograve;n l&agrave; đưa ngươi trở về a, mặc d&ugrave; đối với g&oacute;c ng&otilde; hẻm c&ograve;n l&agrave; tương đối rất an to&agrave;n, bất qu&aacute; người trẻ tuổi c&ograve;n l&agrave; tận lực kh&ocirc;ng n&ecirc;n tại buổi tối c&agrave;n rỡ Hoảng tương đối kh&aacute;.&quot;<br />\r\n<br />\r\n&quot;Cảm ơn ng&agrave;i McGonagall gi&aacute;o sư, tại trước khi rời đi ta xin ng&agrave;i uống ch&eacute;n đồ uống lạnh a, chung quy ng&agrave;i l&atilde;ng ph&iacute; đại nửa ng&agrave;y thời gian, đ&acirc;y cũng l&agrave; v&igrave; biểu đạt ta đối với ng&agrave;i cảm tạ.&quot; Anduin nh&igrave;n cự ly bầu trời tối đen c&ograve;n c&oacute; một đoạn thời gian, hướng McGonagall gi&aacute;o sư đề nghị, đồng thời hắn tại Hẻm X&eacute;o đợi l&acirc;u như vậy, cũng c&oacute; ch&uacute;t mệt mỏi, thuận tiện cũng muốn lại hướng McGonagall gi&aacute;o sư nghe ng&oacute;ng một &iacute;t ma ph&aacute;p giới sự t&igrave;nh.<br />\r\n<br />\r\n&quot;Hảo ba, thế nhưng kh&ocirc;ng muốn tr&igrave; ho&atilde;n qu&aacute; nhiều thời gian.&quot; McGonagall gi&aacute;o sư suy nghĩ một ch&uacute;t, cũng hiểu được chậm trễ kh&ocirc;ng bao nhi&ecirc;u thời gian liền đồng &yacute;.<br />\r\n<br />\r\nHai người dứt lời liền lại đến Hẻm X&eacute;o thượng Florin tiệm nước giải kh&aacute;t, điểm hai ch&eacute;n đồ uống lạnh, t&igrave;m một vị tr&iacute; ngồi xuống.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;McGonagall gi&aacute;o sư, hiện tại ta c&oacute; được ma trượng, c&oacute; phải hay kh&ocirc;ng đều c&oacute; thể luyện tập ma ch&uacute; nha?&quot; Anduin uống một ngụm đồ uống liền bắt đầu hướng gi&aacute;o sư hỏi.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng muốn thật cao theo đuổi xa Anduin, ma ch&uacute; so với ngươi nghĩ giống như muốn phức tạp rất nhiều, lung tung ph&oacute;ng th&iacute;ch ma ch&uacute; có khả năng hội sản sinh kh&ocirc;ng xong hậu quả, đặc biệt l&agrave; tại Muggle thế giới trong, b&acirc;y giờ đang l&agrave; mẫn cảm thời k&igrave;, ma ph&aacute;p bộ hiện tại đối với ma ph&aacute;p phản ứng kiểm tra đo lường v&ocirc; c&ugrave;ng nghi&ecirc;m khắc.&quot;<br />\r\n<br />\r\n&quot;Ma ph&aacute;p bộ c&oacute; d&ograve; x&eacute;t mười bảy tuổi ph&iacute;a dưới Vu sư tiến h&agrave;nh ma ph&aacute;p hoạt động ph&ugrave; ch&uacute;, gọi l&agrave; tung tia, ma ph&aacute;p bộ th&ocirc;ng qua n&oacute; tới ph&aacute;t hiện vị th&agrave;nh ni&ecirc;n sử dụng ma ph&aacute;p t&igrave;nh huống, cho n&ecirc;n ngươi ng&agrave;n vạn kh&ocirc;ng muốn ở c&ocirc; nhi viện trong t&ugrave;y &yacute; sử dụng ngươi ma trượng, nếu như tại Vu sư căn cứ phương sử dụng ma ch&uacute; ma ph&aacute;p bộ khả năng v&ocirc; ph&aacute;p chuẩn x&aacute;c định vị đến ngươi, thế nhưng tại Muggle thế giới, kia giống như l&agrave; trong đ&ecirc;m tối đom đ&oacute;m đồng dạng.&quot; McGonagall gi&aacute;o sư ki&ecirc;n nhẫn hướng Anduin giải th&iacute;ch n&oacute;i.<br />\r\n<br />\r\n&quot;Vậy n&oacute;i l&agrave; nếu như ta tại Hẻm X&eacute;o sử dụng ma ch&uacute; liền kh&ocirc;ng quan hệ sao?&quot; Anduin hỏi.<br />\r\n<br />\r\n&quot;Tr&ecirc;n l&yacute; luận l&agrave; như thế n&agrave;y, nhưng ở kh&ocirc;ng người chỉ đạo hạ t&ugrave;y &yacute; thi nguyền rủa l&agrave; v&ocirc; c&ugrave;ng nguy hiểm, ta biết giống như ngươi vậy tuổi trẻ tiểu Vu sư c&oacute; v&ocirc; c&ugrave;ng phong ph&uacute; t&ograve; m&ograve;, nhưng ta nghĩ ngươi tốt nhất đem ngươi phần n&agrave;y t&ograve; m&ograve; c&ugrave;ng l&ograve;ng hiếu kỳ thả trong trường học.&quot; McGonagall gi&aacute;o sư như cũ qu&aacute; chế t&aacute;c l&agrave; l&atilde;o sư bản phận, ki&ecirc;n nhẫn dẫn đạo Anduin.<br />\r\n<br />\r\n&quot;Hảo, ta minh bạch.&quot; Anduin kh&ocirc;ng biết nội t&acirc;m đập v&agrave;o c&aacute;i g&igrave; b&agrave;n t&iacute;nh, ngo&agrave;i miệng t&ugrave;y &yacute; đ&aacute;p lời.<br />\r\n<br />\r\n&quot;Như ng&agrave;i như vậy gi&aacute;o sư Hogwarts vẫn c&oacute; rất nhiều sao? C&aacute;c ngươi b&igrave;nh thường đều tại bận rộn c&aacute;i g&igrave; đ&acirc;u n&agrave;y? C&ograve;n c&oacute; trường học hiệu trưởng, l&agrave; Dumbledore gi&aacute;o sư sao? Hắn l&agrave; c&aacute;i dạng g&igrave; người?&quot; Anduin h&oacute;a th&acirc;n th&agrave;nh mười vạn c&aacute;i v&igrave; c&aacute;i g&igrave;, ho&agrave;n to&agrave;n mặc kệ McGonagall gi&aacute;o sư nhận được chịu kh&ocirc;ng.<br />\r\n<br />\r\n&quot;Ta nghĩ ngươi hẳn l&agrave; tiến Ravenclaw học viện, tuổi trẻ Wilson ti&ecirc;n sinh.&quot; McGonagall gi&aacute;o sư tuy ngo&agrave;i miệng độc miệng, nhưng vẫn l&agrave; rất ki&ecirc;n nhẫn hướng Anduin giải th&iacute;ch hắn vấn đề...<br />\r\n<br />\r\n&quot;Dumbledore l&agrave; trước mắt Vu sư giới cường đại nhất Vu sư, &iacute;t nhất ch&uacute;ng ta cho rằng l&agrave;, ngươi c&oacute; thể nhập học đang từ từ r&otilde; r&agrave;ng hắn, h&ocirc;m nay hiệu trưởng tại Hogsmeade, đ&oacute; l&agrave; trường học b&ecirc;n cạnh một c&aacute;i thuần t&uacute;y Vu sư th&ocirc;n, hắn c&acirc;̀n v&igrave; xem b&oacute;i học phỏng vấn một vị gi&aacute;o sư, tuy liền c&aacute; nh&acirc;n ta m&agrave; n&oacute;i kh&ocirc;ng cho rằng cần phải lại đơn độc khai mở một m&ocirc;n xem b&oacute;i kh&oacute;a.&quot;<br />\r\n<br />\r\n&quot;McGonagall gi&aacute;o sư! Kh&ocirc;ng nghĩ tới lại c&oacute; thể tại đ&acirc;y gặp ngươi.&quot; Đang tại McGonagall gi&aacute;o sư c&ugrave;ng Anduin tr&ograve; chuyện c&oacute; đang hăng say, từ m&ocirc;n khẩu truyền đến một vị nữ sĩ thanh &acirc;m.<br />\r\n<br />\r\nAnduin quay đầu nh&igrave;n lại, chỉ thấy từ m&ocirc;n khẩu đi tới một nam một nữ hai người, nam sĩ đeo một đeo mắt kiếng một đ&ocirc;i m&agrave;u n&acirc;u nhạt &aacute;nh mắt mười phần c&oacute; thần, một đầu t&oacute;c đen sơ rất chỉnh tề, đứng ở hắn hơi nghi&ecirc;ng nữ sĩ l&agrave; một vị phụ nữ c&oacute; thai, chải lấy m&agrave;u đỏ thẫm t&oacute;c d&agrave;i, c&oacute; một đ&ocirc;i hạnh nh&acirc;n h&igrave;nh d&aacute;ng &aacute;nh mắt, hai người hướng McGonagall gi&aacute;o sư gọi qua biến hướng b&ecirc;n n&agrave;y đi tới.<br />\r\n<br />\r\n&quot;Lily, James, c&aacute;c ngươi tại sao sẽ ở n&agrave;y?&quot; McGonagall gi&aacute;o sư thấy được bọn họ ăn cả kinh, đứng người l&ecirc;n ngh&ecirc;nh hướng hai người, Anduin thấy thế cũng đi theo đứng l&ecirc;n.<br />\r\n<br />\r\n&quot;Ch&uacute;ng ta h&agrave;i tử mau ra sinh, vừa vặn qua mua một &iacute;t đồ vật, ng&agrave;i đ&acirc;y l&agrave;?&quot; James hướng McGonagall gi&aacute;o sư giải th&iacute;ch n&oacute;i, vừa vặn nh&igrave;n về ph&iacute;a b&ecirc;n người đứng Anduin &quot;A, ng&agrave;i l&agrave; tới đ&oacute;n t&acirc;n sinh mua sắm nha, Vu sư gia đ&igrave;nh c&oacute; thể kh&ocirc;ng cần ng&agrave;i tự m&igrave;nh đến, xem ra ngươi l&agrave; v&igrave; Muggle Vu sư nha, tuổi trẻ ti&ecirc;n sinh nh&agrave; của ngươi trưởng, như thế n&agrave;o thả một m&igrave;nh ngươi ở chỗ n&agrave;y?&quot; James Potter đại c&ograve;i c&ograve;i hướng Anduin d&ograve; hỏi, cảm gi&aacute;c một ch&uacute;t cũng kh&ocirc;ng đem m&igrave;nh l&agrave;m ngoại nh&acirc;n, b&ecirc;n cạnh Lily thấy thế đưa tay xoa b&oacute;p James c&aacute;nh tay, James kh&oacute; hiểu nh&igrave;n về ph&iacute;a ch&iacute;nh m&igrave;nh người y&ecirc;u.<br />\r\n<br />\r\n&quot;Ng&agrave;i &aacute;nh mắt thực nhạy b&eacute;n, ta đ&uacute;ng l&agrave; Muggle Vu sư, về phần cha mẹ nha, xin lỗi ta l&agrave; c&ocirc; nhi, cha mẹ rất sớm liền kh&ocirc;ng tại.&quot; Tuy cảm gi&aacute;c c&oacute; ch&uacute;t bị mạo phạm đến, Anduin thật kh&ocirc;ng c&oacute; bởi vậy tức giận, chung quy đ&acirc;y đều l&agrave; sự thật, hắn l&agrave; một c&aacute;i về ph&iacute;a trước nh&igrave;n người, kh&ocirc;ng c&oacute; như vậy sĩ diện c&atilde;i l&aacute;o.<br />\r\n<br />\r\n&quot;&Uacute;c, xin lỗi, ta kh&ocirc;ng c&oacute; mạo phạm &yacute; tứ.&quot; James khả năng cũng &yacute; tứ đến tự m&igrave;nh n&oacute;i sai, vội v&agrave;ng hướng Anduin xin lỗi.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nNgược lại l&agrave; Anduin rất lớn kh&iacute; nh&uacute;n nh&uacute;n vai.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; Anduin. Wilson, năm nay chuẩn bị nhập học t&acirc;n sinh, hai vị n&agrave;y l&agrave; James Potter c&ugrave;ng Lily Potter, l&agrave; ngươi đ&atilde; tốt nghiệp hai vị học trưởng, c&ugrave;ng đi ngồi đi, c&aacute;c ngươi cứ như vậy chạy được Hẻm X&eacute;o, Lily vẫn mang mang thai, cũng kh&ocirc;ng sợ nguy hiểm.&quot; McGonagall gi&aacute;o sư đ&aacute;nh cho giảng h&ograve;a, đưa bọn ch&uacute;ng lẫn nhau giới thiệu một lần, c&ugrave;ng nhau ngồi xuống.<br />\r\n<br />\r\n&quot;Ch&uacute;ng ta phải sợ, đ&aacute;ng tiếc Lily cũng sắp muốn sinh nở, ta phải trước chiếu cố n&agrave;ng, hơn nữa ch&uacute;ng ta cũng muốn l&agrave;m ch&uacute;t chuẩn bị.&quot; James vừa nghĩ tới ch&iacute;nh m&igrave;nh h&agrave;i tử sắp sinh ra, liếc mắt nh&igrave;n hắn người y&ecirc;u Lily, lại đang kia cười ng&acirc;y ng&ocirc; l&ecirc;n.<br />\r\n<br />\r\nAnduin thấy như vậy một m&agrave;n kh&ocirc;ng khỏi đối với đối với bọn họ th&acirc;n phận c&oacute; một ch&uacute;t suy đo&aacute;n, nh&igrave;n chằm chằm Lily vấn đạo &quot;H&agrave;i tử mấy th&aacute;ng, sắp sinh ra sao? Danh tự nghĩ kỹ sao?&quot;<br />\r\n<br />\r\nLily nh&igrave;n xem vị n&agrave;y Muggle Vu sư t&acirc;n sinh, cảm gi&aacute;c mười phần th&acirc;n thiết &quot;Dự t&iacute;nh hẳn l&agrave; cuối th&aacute;ng, về phần h&agrave;i tử danh tự nha, ch&uacute;ng ta thương lượng hảo, nếu như l&agrave; nam h&agrave;i gọi Harry, Harry Potter.&quot; Lily n&oacute;i xong vừa nh&igrave;n về ph&iacute;a James, mặt mũi tr&agrave;n đầy hạnh ph&uacute;c.<br />\r\n<br />\r\nAnduin c&aacute;i n&agrave;y triệt để x&aacute;c nhận, đ&acirc;y l&agrave; tương lai ch&uacute;a cứu thế cha mẹ Potter vợ chồng, tuy hắn đ&atilde; kh&ocirc;ng thể n&agrave;o nhớ r&otilde; l&ecirc;n nội dung cốt truyện, nhưng vai ch&iacute;nh danh tự c&ograve;n l&agrave; biết, m&agrave; điều n&agrave;y cũng x&aacute;c nhận Anduin cho tới nay cảm gi&aacute;c kh&ocirc;ng đ&uacute;ng địa phương l&agrave; c&aacute;i g&igrave;, Harry cha mẹ c&ograve;n sống, vậy c&oacute; nghĩa l&agrave; tại thời gian n&agrave;y điểm Voldemort c&ograve;n chưa c&oacute; chết, tr&aacute;ch kh&ocirc;ng được tất cả Hẻm X&eacute;o kh&aacute;ch h&agrave;ng kh&ocirc;ng nhiều lắm, khắp nơi đều c&oacute; Thần S&aacute;ng tại tuần tra.</p>', '2021-12-01 15:14:11', 5, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(316, 77, 'Hộ Thân Phù', '7', '<p>Đối với Harry Potter trẻ con li&ecirc;n quan nội dung cốt truyện Anduin kh&ocirc;ng c&oacute; ấn tượng g&igrave;, chỉ nhớ r&otilde; dường như l&agrave; Voldemort x&ocirc;ng v&agrave;o Potter gia, cũng giết chết Harry cha mẹ, m&agrave; Harry th&igrave; cầm Voldemort đạn chết, c&ograve;n lại nội dung cốt truyện hiện tại một ch&uacute;t cũng nhớ kh&ocirc;ng ra.<br />\r\n<br />\r\nNếu kh&ocirc;ng muốn n&oacute;i cho hắn biết nh&oacute;m, hoặc l&agrave; cảnh c&aacute;o bọn họ đ&acirc;u n&agrave;y? Nh&igrave;n bọn họ hạnh ph&uacute;c bộ d&aacute;ng, Anduin rất xoắn xu&yacute;t.<br />\r\n<br />\r\nLily nh&igrave;n xem Anduin tại kia cau m&agrave;y, kh&ocirc;ng biết suy nghĩ c&aacute;i g&igrave;, mở miệng n&oacute;i &quot;Như thế n&agrave;o, l&agrave; tại vì đến trường khẩn trương sao? Ta cũng l&agrave; Muggle xuất th&acirc;n Vu sư, năm đ&oacute; ở biết được ta muốn đến Hogwarts đến trường thời điểm, ta cả đ&ecirc;m ngủ kh&ocirc;ng y&ecirc;n, thế nhưng đều ng&agrave;i tiến nhập Hogwarts, ngươi nhất định sẽ kh&ocirc;ng hối hận.&quot;<br />\r\n<br />\r\n&quot;Cũng kh&ocirc;ng biết ngươi sẽ bị ph&acirc;n đến đ&acirc;u c&aacute;i học viện, tốt nhất l&agrave; tới Gryffindor, đương nhi&ecirc;n học viện kh&aacute;c cũng kh&aacute; tốt, trừ Slytherin b&ecirc;n ngo&agrave;i.&quot; James cũng qua chen v&agrave;o n&oacute;i, cũng bắt đầu đi&ecirc;n cuồng cho Anduin An Lợi Gryffindor, tuy n&oacute;i một miệng, thế nhưng nh&igrave;n ra được James kh&ocirc;ng qu&aacute; ưa th&iacute;ch Slytherin.<br />\r\n<br />\r\n&quot;Đừng nghe hắn, tin tưởng m&igrave;nh Anduin, từng học viện đều c&oacute; bản th&acirc;n đặc điểm, mặc kệ bị ph&acirc;n đến đ&acirc;u c&aacute;i học viện, phải chăm chỉ ho&agrave;n th&agrave;nh việc học, minh bạch ch&iacute;nh ngươi chuẩn bị th&agrave;nh v&igrave; c&aacute;i g&igrave; dạng người, bất luận từ c&aacute;i n&agrave;o học viện tốt nghiệp ngươi cũng c&oacute; thể lấy rất kh&aacute; th&agrave;nh tựu!&quot; Lily đối với James kia một bộ đề cử học viện c&aacute;ch l&agrave;m hiển nhi&ecirc;n kh&ocirc;ng qu&aacute; cảm mạo.<br />\r\n<br />\r\n&quot;Cảm ơn, ngươi khuy&ecirc;n bảo ta sẽ ghi ở trong l&ograve;ng.&quot; Anduin với tư c&aacute;ch l&agrave; một c&aacute;i cầm giữ c&oacute; người th&agrave;nh ni&ecirc;n linh hồn người đương nhi&ecirc;n sẽ kh&ocirc;ng chống lại học c&oacute; c&aacute;i g&igrave; khẩn trương, bất qu&aacute; nh&igrave;n xem thiện lương Lily tại v&igrave; ch&iacute;nh m&igrave;nh truyền thụ kinh nghiệm, cũng kh&ocirc;ng n&ecirc;n cắt đứt n&agrave;ng.<br />\r\n<br />\r\nChỉ l&agrave; đối với Potter vợ chồng vận mệnh, Anduin vẫn l&agrave; v&ocirc; ph&aacute;p trực tiếp nhắc nhở, chẳng lẽ trực tiếp theo ch&acirc;n bọn họ n&oacute;i tương lai c&oacute; một ng&agrave;y Voldemort hội x&ocirc;ng v&agrave;o nh&agrave; c&aacute;c ngươi đem c&aacute;c ngươi to&agrave;n bộ ti&ecirc;u diệt, sau đ&oacute; bị c&aacute;c ngươi nhi tử giết lại sao? Đừng n&oacute;i bọn họ sẽ kh&ocirc;ng tin, nếu như khiến cho ho&agrave;i nghi khả năng c&ograve;n c&oacute; thể bị ma ph&aacute;p bộ hoặc Dumbledore bắt lại nghi&ecirc;n cứu, hơn nữa bọn họ vốn ch&iacute;nh l&agrave; Voldemort địch nh&acirc;n, khả năng đ&atilde; sớm l&agrave;m tốt trực diện Hắc Ma Vương chuẩn bị đi!<br />\r\n<br />\r\n&quot;Hảo, thời gian kh&ocirc;ng c&ograve;n sớm Anduin, để ta đưa ngươi trở về a, buổi tối c&oacute; thể kh&ocirc;ng an to&agrave;n, James, Lily c&aacute;c ngươi cũng thế.&quot;<br />\r\n<br />\r\n&quot;V&acirc;ng, McGonagall gi&aacute;o sư.&quot; Anduin c&ugrave;ng McGonagall gi&aacute;o sư bắt đầu đứng dậy thu dọn đồ đạc.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nTrước l&uacute;c chia tay Anduin c&ograve;n l&agrave; quay người đối với Lily n&oacute;i: &quot;Potter phu nh&acirc;n, cho d&ugrave; ở nh&agrave; cũng xin ch&uacute; &yacute; an to&agrave;n, c&ograve;n c&oacute;, ta tin tưởng ng&agrave;i h&agrave;i tử nhất định sẽ th&agrave;nh cho c&aacute;c ngươi ki&ecirc;u ngạo!&quot;<br />\r\n<br />\r\nAnduin kh&ocirc;ng biết n&ecirc;n n&oacute;i c&aacute;i g&igrave;, thế nhưng tại đối mặt vị n&agrave;y thiện lương nữ sĩ c&ograve;n l&agrave; nhịn kh&ocirc;ng được nhắc nhở hai c&acirc;u, về phần hắn nh&oacute;m tương lai, lấy hắn hiện tại lực lượng l&agrave; v&ocirc; ph&aacute;p can thiệp.<br />\r\n<br />\r\n&quot;Cảm ơn ngươi ch&uacute;c ph&uacute;c, Anduin.&quot; Lily nh&igrave;n xem vị n&agrave;y tuổi trẻ đệ tử kh&ocirc;ng khỏi cảm thấy mười phần th&acirc;n thiết, khả năng để cho n&agrave;ng nghĩ đến l&uacute;c trước ch&iacute;nh m&igrave;nh, cũng c&oacute; thể l&agrave; để cho n&agrave;ng nghĩ đến đ&atilde; từng c&ugrave;ng n&agrave;ng thanh mai tr&uacute;c m&atilde; người kia.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; ta tiện tay l&agrave;m một c&aacute;i tiểu đồ chơi, xem như c&aacute;i Hộ Th&acirc;n Ph&ugrave; a, vừa vặn với tư c&aacute;ch l&agrave; ch&uacute;c mừng ngươi nhập học lễ vật, hơn nữa b&igrave;nh thường cũng c&oacute; thể viết thơ cho ta.&quot; Lily nh&igrave;n xem Anduin, từ ch&iacute;nh m&igrave;nh t&ugrave;y th&acirc;n mang theo trong t&uacute;i x&aacute;ch lấy ra một vật, đưa qua.<br />\r\n<br />\r\nAnduin tiếp nhận vừa nh&igrave;n, l&agrave; một c&aacute;i tiền lớn nhỏ đồng mảnh, ph&iacute;a tr&ecirc;n c&oacute; khắc một c&aacute;i giống c&aacute;i tẫn lộc, nh&igrave;n l&ecirc;n tr&ocirc;ng rất sống động.<br />\r\n<br />\r\n&quot;Cảm ơn ngươi lễ vật, ta sẽ hảo hảo bảo tồn.&quot; Anduin trịnh trọng đem Lily đưa tiểu lễ vật cất kỹ, cũng lần nữa c&ugrave;ng Potter vợ chồng c&aacute;o biệt, liền theo McGonagall gi&aacute;o sư rời đi.<br />\r\n<br />\r\nLại l&agrave; một lần ảo ảnh di h&igrave;nh, Anduin lần nữa kinh lịch một lần loại n&agrave;y kh&ocirc;ng qu&aacute; thoải m&aacute;i lữ h&agrave;nh, đ&atilde; kh&ocirc;ng c&oacute; lần đầu ti&ecirc;n lớn như vậy phản ứng, lắc lắc c&oacute; phần trắng bệch mặt, hướng McGonagall gi&aacute;o sư c&aacute;o biệt, một m&igrave;nh trở lại Ph&uacute;c Lợi Viện.<br />\r\n<br />\r\nAnduin trở lại c&ocirc; nhi viện trong ph&ograve;ng, tr&ecirc;n giường ngồi v&agrave;o chỗ của m&igrave;nh, hồi tưởng lại xảy ra kim thi&ecirc;n hết thảy, ma ph&aacute;p thế giới hết thảy đối với Anduin c&oacute; m&atilde;nh liệt lực hấp dẫn.<br />\r\n<br />\r\nThế nhưng Anduin lại nghĩ tới, hiện tại ma ph&aacute;p giới, Voldemort vẫn kh&ocirc;ng c&oacute; nấc cái rắm đ&acirc;u, cũng kh&ocirc;ng biết khoảng thời gian n&agrave;y Hogwarts trong l&agrave; t&igrave;nh huống như thế n&agrave;o, thật sự l&agrave; qu&aacute; nguy hiểm, hắn c&oacute; thể lo lắng đem m&igrave;nh mạng nhỏ k&yacute; th&aacute;c v&agrave;o người kh&aacute;c b&agrave;n tay, hơn nữa ch&iacute;nh m&igrave;nh c&ograve;n l&agrave; một c&aacute;i Muggle.<br />\r\n<br />\r\nLiền hiện tại ma ph&aacute;p giới m&agrave; n&oacute;i, nhất định phải c&oacute; được năng lực tự vệ, bằng kh&ocirc;ng kh&ocirc;ng chừng ng&agrave;y n&agrave;o đ&oacute; đ&atilde; bị Voldemort c&ugrave;ng hắn nanh vuốt ti&ecirc;u diệt, Anduin hồi tưởng lại h&ocirc;m nay từ McGonagall gi&aacute;o sư chỗ đ&oacute; thu thập được tin tức, tổng kết ra ph&iacute;a dưới khi n&agrave;o:<br />\r\n<br />\r\nThứ nhất, ma ph&aacute;p bộ hiện tại đang tại giới nghi&ecirc;m, trước kia chỉ c&oacute; tại nhập học mới c&oacute; thể bị tung tia kiểm tra đo lường vị th&agrave;nh ni&ecirc;n Vu sư, b&acirc;y giờ đang ở c&oacute; được ma trượng sử dụng nh&eacute;t v&agrave;o kiểm tra đo lường phạm vi.<br />\r\n<br />\r\nThứ hai, bởi v&igrave; tung tia l&agrave; phạm vi gi&aacute;m s&aacute;t, tại Vu sư căn cứ phương ma ph&aacute;p bộ v&ocirc; ph&aacute;p x&aacute;c định cụ thể l&agrave; người n&agrave;o tiến h&agrave;nh thi nguyền rủa.<br />\r\n<br />\r\nThứ ba, hiện tại Dumbledore vẫn đang chủ tr&igrave; đại cục, ma ph&aacute;p bộ cũng ở t&iacute;ch cực chống cự, Hẻm X&eacute;o c&ograve;n l&agrave; tương đối an to&agrave;n.<br />\r\n<br />\r\nHạ quyết t&acirc;m, Anduin quyết định mau rời khỏi c&ocirc; nhi viện, tại Hẻm X&eacute;o d&agrave;n xếp xuống, nhất định phải tại nhập học trước cầm giữ nắm giữ nhất định năng lực tự vệ, nghĩ xong Anduin m&oacute;c ra ch&iacute;nh m&igrave;nh ma trượng.<br />\r\n<br />\r\nNh&igrave;n xem như ngọc chất liệu, chỉnh thể hiện l&ecirc;n hơi k&eacute;m quang cảm nhận, ngăm đen th&acirc;m th&uacute;y ma trượng, nhẹ khẽ thở d&agrave;i &quot;Sau n&agrave;y sẽ l&agrave; hai ch&uacute;ng ta sống nương tựa lẫn nhau, cho ngươi đặt t&ecirc;n, gọi &aacute;m vẽ a, chuẩn bị cho tốt để cho ma ph&aacute;p n&agrave;y thế giới mở mang kiến thức ch&uacute;ng ta lợi hại sao?&quot;<br />\r\n<br />\r\nTựa như đ&aacute;p lại Anduin đồng dạng, Anduin cảm gi&aacute;c &aacute;m vẽ trong tay hơi hơi rung động một ch&uacute;t, k&iacute;ch ph&aacute;t ra mấy đạo hồ quang điện, nhưng kh&ocirc;ng c&oacute; đối với hắn tạo th&agrave;nh bất cứ thương tổn g&igrave;, chỉ l&agrave; để cho hắn cảm gi&aacute;c m&igrave;nh c&ugrave;ng căn n&agrave;y ma trượng li&ecirc;n hệ dường như c&agrave;ng s&acirc;u.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nKi&ecirc;n định &yacute; nghĩ trong l&ograve;ng, Anduin liền sớm nghỉ ngơi, chuẩn bị ng&agrave;y mai sẽ bắt đầu h&agrave;nh động.<br />\r\n<br />\r\nB&ecirc;n kia, trong buổi tối Hogwarts giống như trong ngủ say Cự Long, v&ocirc; thanh v&ocirc; tức nhưng lại gi&agrave;u c&oacute; lực lượng vẻ đẹp.<br />\r\n<br />\r\nMcGonagall gi&aacute;o sư trục giai trở l&ecirc;n đi đến Dumbledore văn ph&ograve;ng.<br />\r\n<br />\r\n&quot;Dumbledore, buổi tối h&ocirc;m nay phỏng vấn thế n&agrave;o, ta phải đối với biết trước kh&oacute;a b&aacute;o c&oacute; c&aacute;i g&igrave; dạng hi vọng, cho d&ugrave; tại ma ph&aacute;p giới, biết trước người cũng tr&ecirc;n cơ bản đều l&agrave; lừa đảo.&quot; McGonagall gi&aacute;o sư nh&igrave;n xem đang tại đ&ugrave;a Phượng Ho&agrave;ng Fox Dumbledore mở miệng n&oacute;i.<br />\r\n<br />\r\n&quot;Vậy ta chỉ c&oacute; thể thật đ&aacute;ng tiếc th&ocirc;ng b&aacute;o ngươi, ch&uacute;ng ta rất nhanh muốn ngh&ecirc;nh tiếp Hogwarts người nhậm chức đầu ti&ecirc;n biết trước kh&oacute;a l&atilde;o sư, Minerva.&quot; Dumbledore hồi tưởng đến h&ocirc;m nay tại Hogsmeade nghe được kinh thi&ecirc;n lời ti&ecirc;n đo&aacute;n, b&igrave;nh tĩnh hạ t&acirc;m thần, lựa chọn l&ocirc;ng m&agrave;y quay đầu hướng McGonagall gi&aacute;o sư đ&aacute;p, đồng thời lần nữa x&oacute;a mở chủ đề &quot;H&ocirc;m nay nh&igrave;n thấy vị kia Muggle t&acirc;n sinh cảm gi&aacute;c như thế n&agrave;o đ&acirc;y? Ngươi nh&igrave;n l&ecirc;n t&acirc;m t&igrave;nh kh&ocirc;ng tệ. Hắn dường như gọi l&agrave; an. . . An...&quot;<br />\r\n<br />\r\n&quot;Anduin, Anduin. Wilson, một c&aacute;i mười phần tự hạn chế c&ugrave;ng lễ ph&eacute;p h&agrave;i tử, v&ocirc; luận l&agrave; từ ch&iacute;nh ta quan s&aacute;t đến xem, vẫn l&agrave; theo hắn chỗ Ph&uacute;c Lợi Viện r&otilde; r&agrave;ng đến tin tức đến xem đều l&agrave; như thế n&agrave;y, hơn nữa ch&uacute;ng ta đ&uacute;ng l&uacute;c h&ocirc;m nay vẫn gặp Potter vợ chồng, Lily h&agrave;i tử đo&aacute;n chừng cuối th&aacute;ng muốn sinh ra, Anduin cũng đang tạo th&agrave;nh v&igrave; người kh&aacute;c bằng hữu.&quot; McGonagall gi&aacute;o sư thấy được Dumbledore tr&ograve; chuyện l&ecirc;n h&ocirc;m nay vị kia t&acirc;n sinh, cũng l&agrave; ch&uacute;t n&agrave;o kh&ocirc;ng keo kiệt kh&iacute;ch lệ, Anduin cho n&agrave;ng lưu lại rất ấn tượng tốt.</p>', '2021-12-01 15:14:31', 5, 1),
(317, 77, 'Phá Nồi đồng Quán Bar', '8', '<p>&quot;Ngươi n&oacute;i l&agrave;, Lily Potter?&quot; Dumbledore nh&igrave;n l&ecirc;n đối với Potter vợ chồng h&agrave;i tử c&agrave;ng c&oacute; h&agrave;o hứng, &quot;Bọn họ h&agrave;i tử sắp sửa tại cuối th&aacute;ng sinh ra? X&aacute;c định sao?&quot;<br />\r\n<br />\r\n&quot;Theo bọn họ theo như lời l&agrave; như thế n&agrave;y, bọn họ vẫn đưa Anduin một phần lễ vật, ta c&oacute; thể tại kia lễ vật ph&iacute;a tr&ecirc;n cảm nhận được kh&ocirc;ng k&eacute;m ma ph&aacute;p ba động, l&uacute;c trước n&agrave;ng một mực ở n&oacute;i m&igrave;nh chuẩn bị l&agrave;m mấy c&aacute;i luyện kim đạo cụ luyện tay một ch&uacute;t, thực tiễn m&igrave;nh một ch&uacute;t ph&ugrave; văn tr&igrave;nh độ, nh&igrave;n l&ecirc;n th&agrave;nh quả kh&ocirc;ng sai.<br />\r\n<br />\r\nM&agrave; đứa b&eacute; kia so với ta trong tưởng tượng muốn độc lập nhiều lắm, t&iacute;ch g&oacute;p cũng so với ta tin tưởng b&ecirc;n trong phong ph&uacute;, rất kh&oacute; tưởng tượng một c&aacute;i 11 tuổi h&agrave;i tử ở c&ocirc; nhi viện trong l&agrave;m thế n&agrave;o l&agrave;m được, bất qu&aacute; nghe ng&oacute;ng người kh&aacute;c việc ri&ecirc;ng tư cũng kh&ocirc;ng phải l&agrave; c&aacute;i g&igrave; tốt h&agrave;nh vi.&quot; McGonagall gi&aacute;o sư c&ugrave;ng Dumbledore hiệu trưởng r&otilde; r&agrave;ng kh&ocirc;ng tại một c&aacute;i k&ecirc;nh, như cũ tại phối hợp tr&ograve; chuyện Anduin.<br />\r\n<br />\r\n&quot;L&agrave; thế phải kh&ocirc;ng, nghe n&oacute;i Longbottom vợ chồng h&agrave;i tử cũng nhanh sinh ra, hội kh&ocirc;ng phải l&agrave; trong bọn họ một c&aacute;i đ&acirc;u n&agrave;y?&quot; Dumbledore thấp giọng lẩm bẩm tự quyết định, hiển nhi&ecirc;n hắn đối với Lily h&agrave;i tử so với năm nay sắp nhập học t&acirc;n sinh hứng th&uacute; c&agrave;ng lớn.<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave; trong bọn họ một c&aacute;i nha?&quot; McGonagall gi&aacute;o sư đối với Dumbledore lẩm bẩm đầu &oacute;c kh&ocirc;ng th&ocirc;ng.<br />\r\n<br />\r\n&quot;A, kh&ocirc;ng c&oacute; g&igrave; McGonagall, h&ocirc;m nay ngươi cũng mệt mỏi, điểm t&acirc;m s&aacute;ng đi nghỉ ngơi a, ng&agrave;y mai Phượng Ho&agrave;ng x&atilde; m&iacute;t-tinh hội nghị vẫn c&ograve;n ở chỗ cũ, ch&uacute;ng ta vẫn c&oacute; rất nhiều vấn đề muốn một chỗ giải quyết, gần nhất Tử Thần Thực Tử nh&oacute;m c&agrave;ng sinh động.&quot; Dumbledore c&ograve;n kh&ocirc;ng c&oacute; x&aacute;c định &yacute; nghĩ trong l&ograve;ng.<br />\r\n<br />\r\n&quot;Minh bạch, Dumbledore.&quot; McGonagall gi&aacute;o sư nhớ tới vậy c&ograve;n kh&ocirc;ng xua t&aacute;n b&oacute;ng mờ kh&ocirc;ng khỏi nhăn cau m&agrave;y, quay người rời đi.<br />\r\n<br />\r\nM&agrave; Dumbledore như cũ ngồi tại trước b&agrave;n l&agrave;m việc trầm tư, kh&ocirc;ng ai biết hắn tại t&iacute;nh to&aacute;n c&aacute;i g&igrave;.<br />\r\n<br />\r\n...<br />\r\n<br />\r\nNg&agrave;y h&ocirc;m sau, Anduin c&ugrave;ng Tris ma ma c&ugrave;ng William bọn họ c&aacute;o biệt thu thập xong h&agrave;nh l&yacute;, một m&igrave;nh đi đến ph&aacute; nồi đồng qu&aacute;n bar.<br />\r\n<br />\r\n&quot;Ngươi n&oacute;i c&aacute;i g&igrave;, một m&igrave;nh ngươi phải ở n&agrave;y ở đến Hogwarts khai giảng?&quot; Ph&aacute; nồi đồng qu&aacute;n bar l&atilde;o bản Tom kh&ocirc;ng d&aacute;m tin nh&igrave;n l&ecirc;n trước mặt Anduin.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;V&acirc;ng, Tom ti&ecirc;n sinh, y&ecirc;n t&acirc;m ta n&agrave;y c&oacute; tiền.&quot; Anduin từ trong l&ograve;ng m&oacute;c ra mấy mai Galleon đối với Tom n&oacute;i.<br />\r\n<br />\r\n&quot;Đ&acirc;y kh&ocirc;ng phải tiền vấn đề Wilson ti&ecirc;n sinh, ngươi khả năng kh&ocirc;ng c&oacute; c&oacute; &yacute; thức đến vấn đề, hiện ở b&ecirc;n cạnh phải to&aacute;n an to&agrave;n, xin thứ cho ta v&ocirc; ph&aacute;p đ&aacute;p ứng, đ&acirc;y l&agrave; v&igrave; ngươi suy nghĩ.&quot; Tom nghi&ecirc;m t&uacute;c nh&igrave;n xem Anduin đối với hắn n&oacute;i.<br />\r\n<br />\r\n&quot;Ta minh bạch Tom ti&ecirc;n sinh, ng&agrave;y h&ocirc;m qua McGonagall gi&aacute;o sư đ&atilde; theo ta giải th&iacute;ch hỏi đến đề t&iacute;nh nghi&ecirc;m trọng, thế nhưng ta như cũ ki&ecirc;n tr&igrave;, hơn nữa ta nghĩ ta một c&aacute;i vị th&agrave;nh ni&ecirc;n Muggle Vu sư, sinh hoạt tại Muggle thế giới mới l&agrave; nguy hiểm nhất, ta nguyện &yacute; tin tưởng ng&agrave;i, ta tại đ&acirc;y kh&ocirc;ng c&oacute; việc g&igrave; thời điểm vẫn c&oacute; thể gi&uacute;p ngươi kh&ocirc; khốc sống, c&ugrave;ng ng&agrave;i tr&ograve; chuyện kh&ocirc;ng phải sao.&quot; Anduin ngẩng đầu d&ugrave;ng c&aacute;i kia đen thui mắt đen ki&ecirc;n định nh&igrave;n về ph&iacute;a Tom, r&otilde; r&agrave;ng cho thấy kh&ocirc;ng c&oacute; &yacute; định đi.<br />\r\n<br />\r\nTom cau m&agrave;y nh&igrave;n trước mắt thiếu ni&ecirc;n n&agrave;y, suy đi nghĩ lại c&ograve;n l&agrave; đ&aacute;p ứng, ở b&ecirc;n cạnh chung quy cũng kh&ocirc;ng c&oacute; thiếu Thần S&aacute;ng tại tuần tra, để cho hắn một c&aacute;i Muggle Vu sư ở b&ecirc;n ngo&agrave;i c&agrave;n rỡ Hoảng, kh&ocirc;ng chừng vẫn hội xảy ra vấn đề g&igrave;.<br />\r\n<br />\r\n&quot;Đi a, d&ugrave; sao ta n&agrave;y gần nhất cũng kh&ocirc;ng c&oacute; g&igrave; sinh &yacute;, coi như ngươi ưu đ&atilde;i nửa th&aacute;ng tiền thu&ecirc; nh&agrave; liền thu ngươi 5 c&aacute;i Galleon hảo, thế nhưng ngươi phải đ&aacute;p ứng ta kh&ocirc;ng cho ph&eacute;p t&ugrave;y &yacute; chạy loạn, tốt nhất đều đứng ở trong tiệm, buổi tối 8 điểm l&agrave; cấm đi lại ban đ&ecirc;m, phải hồi ngươi gian ph&ograve;ng.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; vấn đề Tom ti&ecirc;n sinh, ta sẽ quy củ, ng&agrave;i y&ecirc;n t&acirc;m hảo.&quot; Anduin nh&igrave;n Tom đ&aacute;p ứng, sảng kho&aacute;i trả tiền, tiếp nhận gian ph&ograve;ng c&aacute;i ch&igrave;a kh&oacute;a liền đem h&agrave;nh l&yacute; lần lượt chuyển tiến gian ph&ograve;ng.<br />\r\n<br />\r\nAnduin đem ch&iacute;nh m&igrave;nh loại h&agrave;nh t&acirc;y đặt ở bệ cửa sổ trước, hắn lần n&agrave;y mang đồ vật cũng kh&ocirc;ng &iacute;t, trừ đến trường muốn d&ugrave;ng học chiếc c&ugrave;ng một &iacute;t h&agrave;nh l&yacute;, liền l&uacute;c trước William mua đồ gia vị đều mang tới, chơi mạt chược cũng mang một bộ.<br />\r\n<br />\r\nKiếp trước Anduin từ qua qu&acirc;n c&ograve;n ra th&acirc;n v&otilde; thuật thế gia, trước kia kh&ocirc;ng c&oacute; g&igrave; ham m&ecirc;, l&agrave; tốt rồi một ngụm bụng chi dục. Hắn ở c&aacute;i thế giới n&agrave;y ăn tầm mười năm Anh quốc rau, nhưng l&agrave;m hắn buồn chết, đối với c&aacute;i n&agrave;y hắn sớm đ&atilde; căm th&ugrave; đến tận xương tuỷ.<br />\r\n<br />\r\nD&agrave;n xếp tốt Anduin d&ograve; x&eacute;t một ch&uacute;t gian ph&ograve;ng, kh&ocirc;ng gian kh&ocirc;ng nhỏ, cơ bản r&egrave;n luyện vị tr&iacute; c&oacute;, thế nhưng kh&ocirc;ng c&oacute; bao nhi&ecirc;u kh&ocirc;ng gian luyện quyền, trước kia hắn chủ yếu luyện được l&agrave; Th&ocirc;ng T&iacute; quyền, Ph&aacute;ch Quải, trường quyền, Hồng quyền. . ., hiện tại ho&agrave;n to&agrave;n thi triển kh&ocirc;ng ra, hiện tại lời trừ c&oacute; thể trong ph&ograve;ng đứng như cọc gỗ, chỉ c&oacute; thể luyện một ch&uacute;t Th&aacute;i Cực.<br />\r\n<br />\r\nNghĩ xong Anduin lấy ra trang giấy đối với mỗi ng&agrave;y học tập c&ugrave;ng trong khi huấn luyện cho tiến h&agrave;nh quy hoạch, hắn l&agrave; một c&aacute;i sinh hoạt cực kỳ quy luật người, đồng thời v&ocirc; c&ugrave;ng tự hạn chế, hiện tại hắn trừ chăn,mền c&oacute; thể sẽ kh&ocirc;ng điệp th&agrave;nh đậu hũ khối b&ecirc;n ngo&agrave;i, cơ bản c&ograve;n l&agrave; án l&acirc;́y ở trong bộ đội th&oacute;i quen sinh hoạt.<br />\r\n<br />\r\nHắn l&agrave; cho m&igrave;nh như vậy an b&agrave;i:<br />\r\n<br />\r\n6 điểm rời giường rửa mặt th&ecirc;m luyện c&ocirc;ng buổi s&aacute;ng, trong khi huấn luyện cho: K&eacute;o duỗi, đứng Hỗn Nguy&ecirc;n cột 30 ph&uacute;t đồng hồ, c&oacute; dưỡng huấn luyện + luyện quyền 30 ph&uacute;t đồng hồ;<br />\r\n<br />\r\n7 giờ rưỡi d&ugrave;ng bữa s&aacute;ng, về sau bắt đầu đọc s&aacute;ch cũng luyện tập ma ch&uacute;;<br />\r\n<br />\r\n12 điểm 13 điểm d&ugrave;ng cơm trưa;<br />\r\n<br />\r\n13 điểm tiếp tục học tập cũng luyện tập ma ch&uacute;;<br />\r\n<br />\r\n18 điểm d&ugrave;ng bữa tối, về sau nh&igrave;n trạng th&aacute;i nghỉ ngơi hoặc tiếp tục luyện tập ma ch&uacute;;<br />\r\n<br />\r\n20 điểm luyện quyền, đứng như cọc gỗ, minh tưởng;<br />\r\n<br />\r\n21 điểm rửa mặt đi ngủ.<br />\r\n<br />\r\nĐi ăn cơm c&ugrave;ng thời gian nghỉ ngơi gi&uacute;p đỡ Tom kh&ocirc; khốc sống.<br />\r\n<br />\r\nTại đ&acirc;y nửa th&aacute;ng trong Anduin quyết định kh&ocirc;ng lo người, chỉ c&oacute; thể l&agrave; hấp thu c&agrave;ng nhiều ma ph&aacute;p tri thức c&ugrave;ng luyện tập.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nL&agrave;m tốt kỹ c&agrave;ng quy hoạch, Anduin nh&igrave;n thời gian, tiếp cận 12, liền quyết định xuống lầu ăn cơm.<br />\r\n<br />\r\nỞ đại sảnh c&ugrave;ng Tom phiếm v&agrave;i c&acirc;u, về sau ph&aacute;t hiện ở b&ecirc;n cạnh cung cấp ăn uống phục vụ cơ bản cũng đều l&agrave; một &iacute;t anh thức đồ ăn, tại c&ugrave;ng Tom x&aacute;c nhận, Anduin lấy ra l&uacute;c trước mua đồ gia vị tự m&igrave;nh xuống bếp cả v&agrave;i đạo ăn s&aacute;ng, vừa vặn thỉnh Tom ăn một bữa, thuận tiện gần hơn kh&aacute;ch h&agrave;ng hai người cảm t&igrave;nh, kh&aacute;ch v&agrave; chủ qu&aacute; vui mừng Anduin chuẩn bị ch&iacute;nh thức bắt đầu ma ph&aacute;p của hắn h&agrave;nh tr&igrave;nh.<br />\r\n<br />\r\nAnduin chủ c&ocirc;ng nội dung chủ yếu l&agrave; &quot; ti&ecirc;u chuẩn ch&uacute; ngữ, sơ cấp &quot;, &quot; ma ph&aacute;p l&yacute; luận &quot;, &quot; hắc &aacute;m lực lượng: Tự vệ hướng dẫn &quot;, hắn chuẩn bị trước nhanh ch&oacute;ng học biết mấy c&aacute;i ma ch&uacute; tương đối kh&aacute;. Biến h&igrave;nh thuật tại kh&ocirc;ng người chỉ đạo t&igrave;nh huống nghĩ rất kh&oacute; khăn, thảo dược học c&ugrave;ng ma dược học c&agrave;ng nhiều l&agrave; c&acirc;̀n thực tiễn nắm giữ.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; vội v&agrave;ng trực tiếp tiến h&agrave;nh thực tiễn luyện tập, Anduin nhanh ch&oacute;ng lật xem s&aacute;ch vở, một b&ecirc;n cầm lấy b&uacute;t đem trong s&aacute;ch trọng điểm nội dung hoặc kh&ocirc;ng r&otilde; địa phương l&agrave;m thượng k&yacute; hiệu, rất nhanh thời gian liền bất tri bất gi&aacute;c đi qua.<br />\r\n<br />\r\nNg&agrave;y h&ocirc;m sau đ&atilde; ăn điểm t&acirc;m, Anduin chuẩn bị bắt đầu luyện tập nh&acirc;n sinh bản th&acirc;n c&aacute;i thứ nhất ma ch&uacute;, tr&ocirc;i nổi nguyền rủa, bởi v&igrave; đi qua nhiều năm luyện tập hắn sớm đều c&oacute; thể tại kh&ocirc;ng c&oacute; ma trượng dưới t&igrave;nh huống khiến vật thể tr&ocirc;i nổi, cho n&ecirc;n hắn quyết định trước từ ch&iacute;nh m&igrave;nh am hiểu nhất bộ phận bắt đầu.<br />\r\n<br />\r\n&quot;Vũ ~ Gadim ~ Leviosa&quot; Anduin so s&aacute;nh lấy tr&ecirc;n s&aacute;ch ch&uacute; ngữ c&ugrave;ng thủ thế, r&uacute;t ra ma trượng đối với tr&ecirc;n b&agrave;n một quyển s&aacute;ch bắt đầu thi nguyền rủa.<br />\r\n<br />\r\nAnduin cảm gi&aacute;c c&oacute; một cỗ v&ocirc; h&igrave;nh năng lượng đem ch&iacute;nh m&igrave;nh, ma trượng c&ugrave;ng s&aacute;ch vở li&ecirc;n hệ c&ugrave;ng một chỗ, gần như kh&ocirc;ng c&oacute; ph&iacute; kh&iacute; lực g&igrave; để cho s&aacute;ch vở tr&ocirc;i nổi, &quot;Ừ, sử dụng ma ch&uacute; về sau r&otilde; r&agrave;ng cảm gi&aacute;c nhẹ nh&otilde;m kh&ocirc;ng &iacute;t, l&agrave; ch&uacute; ngữ để cho ma lực c&agrave;ng th&ecirc;m ổn định sao?&quot;<br />\r\n<br />\r\nĐ&oacute;n lấy Anduin bu&ocirc;ng xuống ma trượng, đồng dạng l&agrave; đối với quyển s&aacute;ch kia, thử hướng lấy trước kia dạng trực tiếp khiến n&oacute; bay l&ecirc;n, thế nhưng thử một hồi l&acirc;u, đều kh&ocirc;ng c&oacute; hiệu quả.</p>', '2021-12-01 15:14:51', 6, 1),
(318, 77, 'Chướng Ngại Nguyền Rủa', '9', '<p>&quot;Kỳ qu&aacute;i, d&ugrave;ng như thế n&agrave;o qua ma trượng, liền t&igrave;m kh&ocirc;ng được lấy trước loại cảm gi&aacute;c đ&oacute; đ&acirc;u n&agrave;y?&quot; Anduin kh&ocirc;ng khỏi suy tư, &quot;Chẳng lẽ n&oacute;i ma trượng kh&ocirc;ng đơn thuần l&agrave; thi nguyền rủa c&ocirc;ng cụ, vẫn đưa đến chải vuốt ma lực t&aacute;c dụng sao? Hoặc l&agrave; n&oacute;i trước kia ma lực đều l&agrave; sinh động, kh&ocirc;ng ổn định, cho n&ecirc;n lại c&agrave;ng dễ tại kh&ocirc;ng sử dụng ma trượng thời điểm sử dụng, m&agrave; ma trượng để cho thi nguyền rủa trở n&ecirc;n đơn giản, l&agrave; v&igrave; th&ocirc;ng qua ma trượng thi nguyền rủa hội gia tăng ma lực lưu động t&iacute;nh ổn định sao?&quot;<br />\r\n<br />\r\nAnduin suy tư về bất đồng t&iacute;nh khả năng, lại cầm lấy ma trượng ph&oacute;ng th&iacute;ch mấy lần tr&ocirc;i nổi nguyền rủa, lặp lại nhận thức một lần d&ugrave;ng ma trượng thi nguyền rủa cảm gi&aacute;c, cũng cảm gi&aacute;c lấy trong cơ thể cổ lực lượng kia vận h&agrave;nh, về sau bu&ocirc;ng xuống ma trượng.<br />\r\n<br />\r\nĐứng dậy đứng lại, hai ch&acirc;n hơi cong, mệnh m&ocirc;n lồi, kh&iacute; thu đan điền, hai mắt kh&eacute;p hờ, cũng thu liễm t&acirc;m thần, đem to&agrave;n th&acirc;n cơ bắp dần dần bu&ocirc;ng lỏng, m&agrave; trong đầu kh&ocirc;ng tại suy nghĩ trước kia v&ocirc; dụng ma trượng điều động ma lực cảm gi&aacute;c, m&agrave; l&agrave; bắt đầu trong đầu bắt d&ugrave;ng ma trượng thi nguyền rủa th&igrave; ma lực quỹ t&iacute;ch.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; một hồi, Anduin chậm r&atilde;i mở mắt, kia d&ugrave;ng để luyện tập ma ch&uacute; s&aacute;ch vở r&otilde; r&agrave;ng đ&atilde; phi&ecirc;u ph&ugrave; ở trước mắt.<br />\r\n<br />\r\n&quot;Sử dụng ma trượng thi nguyền rủa, c&oacute; thể c&agrave;ng hợp l&yacute; dẫn đạo c&ugrave;ng lợi dụng ma lực, khiến cho c&agrave;ng c&oacute; hiệu suất, trước kia phương ph&aacute;p qu&aacute; th&ocirc; r&aacute;p, cho n&ecirc;n tại c&oacute; c&agrave;ng c&oacute; ưu thế r&otilde; r&agrave;ng ngược lại kh&ocirc;ng d&ugrave;ng được.&quot; Anduin tổng kết lấy b&acirc;y giờ c&ugrave;ng dĩ v&atilde;ng điều động ma lực phương thức, &yacute; đồ cho ra một c&aacute;i kết luận.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; ai nghĩ đến l&agrave;, Anduin đang luyện tập n&agrave;y đơn giản nhất tr&ocirc;i nổi nguyền rủa th&igrave; trong l&uacute;c v&ocirc; h&igrave;nh sớm nắm giữ kh&ocirc;ng tiếng động kh&ocirc;ng trượng thi ph&aacute;p b&iacute; quyết.<br />\r\n<br />\r\nNhiều năm kh&ocirc;ng ngừng luyện tập kh&ocirc;ng đơn giản để cho Anduin gia tăng tr&ocirc;i nổi nguyền rủa độ thuần thục, c&agrave;ng l&agrave;m cho hắn bản th&acirc;n đối với ma lực c&agrave;ng th&ecirc;m mẫn cảm.<br />\r\n<br />\r\nĐồng thời hắn ma lực tổng sản lượng c&ugrave;ng cường độ tại quanh năm sau khi r&egrave;n luyện cũng kh&ocirc;ng phải c&aacute;i kh&aacute;c bạn c&ugrave;ng lứa tuổi c&oacute; thể so s&aacute;nh.<br />\r\n<br />\r\nT&igrave;m đến cảm gi&aacute;c quan trọng hơn l&agrave; củng cố hạ xuống, Anduin kế tiếp gần như d&ugrave;ng sở c&oacute; thời gian tới ph&oacute;ng ra tr&ocirc;i nổi nguyền rủa, c&oacute; &iacute;ch ma trượng thi nguyền rủa, cũng c&oacute; kh&ocirc;ng d&ugrave;ng ma trượng thi nguyền rủa, c&oacute; niệm ch&uacute;, cũng c&oacute; kh&ocirc;ng niệm ch&uacute;.<br />\r\n<br />\r\nRất nhanh Anduin cũng cảm gi&aacute;c hắn đối với ma ch&uacute; dần dần c&oacute; dung hợp qu&aacute;n th&ocirc;ng cảm gi&aacute;c.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nC&ograve;n muốn luyện tập một hồi Anduin, nghe được bụng bắt đầu x&igrave; x&agrave;o gọi bậy, một nh&igrave;n thời gian nguy&ecirc;n lai đ&atilde; đến buổi tối, cơm trưa đều bỏ qua, Anduin biết lao động nh&agrave;n hạ kết hợp tầm quan trọng, vội v&agrave;ng b&igrave;nh phục t&acirc;m t&igrave;nh, chuẩn bị xuống lầu d&ugrave;ng cơm.<br />\r\n<br />\r\n&quot;Tiểu tử, người trẻ tuổi kh&ocirc;ng muốn quang sẽ ở gian ph&ograve;ng ngủ nướng nha, liền cơm trưa đều kh&ocirc;ng xuống ăn.&quot; Đang tại quầy bar xem b&aacute;o ch&iacute; Tom tr&ocirc;ng thấy xuống lầu Anduin kh&iacute; kh&ocirc;ng đ&aacute;nh một chỗ, c&ograve;n tưởng rằng Anduin từ buổi s&aacute;ng ngủ đến tối.<br />\r\n<br />\r\n&quot;Ha ha, ng&agrave;i kh&ocirc;ng phải l&agrave; ph&agrave;n n&agrave;n ta giữa trưa kh&ocirc;ng c&oacute; hạ xuống cho ng&agrave;i nấu cơm a.&quot; Từ khi ng&agrave;y h&ocirc;m qua giữa trưa cho Tom thanh t&uacute; một tay tr&ugrave; nghệ, cơm tối c&ugrave;ng h&ocirc;m nay bữa s&aacute;ng đều l&agrave; từ hắn tự m&igrave;nh thao đao, Anduin t&acirc;m t&igrave;nh kh&ocirc;ng tệ kh&ocirc;ng khỏi c&ugrave;ng l&atilde;o bản mở l&ecirc;n vui đ&ugrave;a.<br />\r\n<br />\r\n&quot;Ta chỉ l&agrave; lo lắng ngươi, sợ ngươi chết đ&oacute;i trong ph&ograve;ng, hừ!&quot; Tom nh&igrave;n Anduin vẫn c&oacute; t&acirc;m tư đ&ugrave;a cợt, kh&iacute; kh&ocirc;ng đ&aacute;nh một chỗ.<br />\r\n<br />\r\n&quot;Tr&aacute;ch ta, tr&aacute;ch ta, ng&agrave;i nghỉ ngơi, buổi tối ta cho ng&agrave;i bộc lộ t&agrave;i năng, bao ng&agrave;i thoả m&atilde;n.&quot; Anduin mỉm cười hồi Tom một c&acirc;u, liền đi tới ph&ograve;ng bếp.<br />\r\n<br />\r\nBữa tối Anduin l&agrave;m mười phần phong ph&uacute;, Tom cũng cho Anduin ngược lại một ly mỡ b&ograve; bia, Anduin cảm thấy loại ma ph&aacute;p n&agrave;y thế giới kỳ qu&aacute;i đồ uống mười phần mới lạ, uống l&ecirc;n tới c&oacute; mỡ b&ograve; m&ugrave;i thơm, nhưng l&agrave; vừa kh&ocirc;ng c&oacute; đầy mỡ hương vị.<br />\r\n<br />\r\n&quot;Nh&igrave;n ng&agrave;i vừa mới tại xem b&aacute;o ch&iacute;, c&oacute; c&aacute;i g&igrave; tin tức sao?&quot;<br />\r\n<br />\r\n&quot;Gần nhất c&agrave;ng ng&agrave;y c&agrave;ng loạn, c&aacute;i kia người thần b&iacute; c&ugrave;ng hắn Tử Thần Thực Tử c&ugrave;ng ma ph&aacute;p bộ lại trao ph&aacute;t hỏa, ma ph&aacute;p bộ một vị Thần S&aacute;ng vẫn bởi vậy trọng thương, cũng kh&ocirc;ng biết thời gian n&agrave;y l&uacute;c n&agrave;o mới l&agrave; c&aacute;i đầu nha.&quot; Tom hiển nhi&ecirc;n đối với hiện tại thế cục mười phần lo lắng.<br />\r\n<br />\r\nTử Thần Thực Tử? Ch&iacute;nh l&agrave; đi theo Voldemort người sao? Anduin vừa học đến một c&aacute;i c&oacute; phần quen thuộc t&acirc;n từ, li&ecirc;n quan k&yacute; ức thật sự đi qua qu&aacute; l&acirc;u, hiện tại một &iacute;t đều muốn một lần nữa bắt đầu.<br />\r\n<br />\r\nC&ugrave;ng Tom d&ugrave;ng cơm tối xong, Anduin vung tay l&ecirc;n, tr&ecirc;n mặt b&agrave;n c&aacute;i ch&eacute;n kh&ocirc;ng c&aacute;i đĩa liền theo hắn chỉ huy bay về ph&iacute;a ph&ograve;ng bếp nước r&atilde;nh.<br />\r\n<br />\r\nTom nh&igrave;n xem một m&agrave;n n&agrave;y trừng lớn hai mắt, quen như vậy luyện kh&ocirc;ng trượng thi ph&aacute;p, cho d&ugrave; chỉ l&agrave; đơn giản tr&ocirc;i nổi nguyền rủa, tại trưởng th&agrave;nh Vu sư b&ecirc;n trong cũng kh&ocirc;ng c&oacute; nhiều người c&oacute; thể l&agrave;m được<br />\r\n<br />\r\n&quot;Ngươi, đ&acirc;y l&agrave;!&quot;<br />\r\n<br />\r\n&quot;Hắc hắc, đ&acirc;y l&agrave; kh&ocirc;ng c&oacute; ăn cơm trưa th&agrave;nh quả.&quot; Anduin cười tủm tỉm tại Tom trong l&uacute;c khiếp sợ trở về ph&ograve;ng.<br />\r\n<br />\r\nAnduin tại kế tiếp trong v&ograve;ng v&agrave;i ng&agrave;y kh&ocirc;ng ngừng học được kh&ocirc;ng &iacute;t t&acirc;n ch&uacute; ngữ, như l&agrave; mở kh&oacute;a nguyền rủa, huỳnh quang lấp l&aacute;nh, thanh l&yacute; đổi mới ho&agrave;n to&agrave;n, kh&ocirc;ng thấm nước ph&ograve;ng ẩm ướt, Aguamenti c&aacute;c loại c&ocirc;ng năng h&igrave;nh ma ch&uacute;, hay l&agrave; Impedimenta, mềm ch&acirc;n nguyền rủa, đ&aacute;nh lui nguyền rủa đều Hắc Ma Ph&aacute;p Ph&ograve;ng Ngự loại ch&uacute; ngữ.<br />\r\n<br />\r\nC&oacute; ch&uacute; ngữ dựa theo t&agrave;i liệu giảng dạy tr&uacute;ng ch&uacute; lời n&oacute;i c&ugrave;ng thi nguyền rủa thủ ph&aacute;p liền c&oacute; thể ph&oacute;ng th&iacute;ch, c&oacute; ch&uacute; ngữ chỉ xem s&aacute;ch l&agrave;m cho kh&ocirc;ng r&otilde; &acirc;m đọc đ&atilde; đi xuống lầu thỉnh gi&aacute;o Tom, cơ bản cũng c&oacute; thể nắm giữ.<br />\r\n<br />\r\nThế nhưng c&oacute; thể thuần thục th&ocirc;ng qua kh&ocirc;ng trượng thi ph&aacute;p sử dụng như cũ chỉ c&oacute; tr&ocirc;i nổi nguyền rủa, Anduin cho rằng đ&acirc;y c&ograve;n l&agrave; đối với tương ứng ch&uacute; ngữ nắm giữ c&ograve;n chưa đủ thuần thục, hơn nữa hắn vẫn tổng kết ra một &iacute;t ma ch&uacute; quy luật.<br />\r\n<br />\r\nĐầu ti&ecirc;n đồng dạng ma ch&uacute; kh&ocirc;ng c&ugrave;ng người thi nguyền rủa hiệu quả cường độ đều l&agrave; kh&ocirc;ng đồng nhất, cũng tỷ như Tom ph&oacute;ng th&iacute;ch huỳnh quang lấp l&aacute;nh liền so với Anduin ph&oacute;ng th&iacute;ch muốn s&aacute;ng ngời rất nhiều, đ&oacute; l&agrave; bởi v&igrave; Tom đối với ma ch&uacute; c&agrave;ng th&ecirc;m thuần thục, l&yacute; giải cũng c&agrave;ng s&acirc;u.<br />\r\n<br />\r\nTiếp theo &yacute; ch&iacute; c&ugrave;ng t&acirc;m t&igrave;nh đối với ma ch&uacute; cường độ cũng c&oacute; ảnh hưởng, đ&acirc;y l&agrave; Anduin từ Tom kia r&otilde; r&agrave;ng đến, như l&agrave; c&ocirc;ng k&iacute;ch loại ma ph&aacute;p &yacute; ch&iacute; c&agrave;ng ki&ecirc;n định, quyết t&acirc;m c&agrave;ng mạnh hiệu quả lại c&agrave;ng mạnh mẽ, Anduin đang nổi l&ecirc;n t&acirc;m t&igrave;nh ph&oacute;ng th&iacute;ch huỳnh quang lấp l&aacute;nh quả nhi&ecirc;n so với đơn thuần thi nguyền rủa hiệu quả muốn tốt rất nhiều.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nM&agrave; rất nhiều hắc vu sư đều l&agrave; nghi&ecirc;n cứu Hắc Ma Ph&aacute;p chuy&ecirc;n gia, ma ch&uacute; cũng sẽ phản hướng ảnh hưởng Vu sư t&acirc;m t&igrave;nh.<br />\r\n<br />\r\nBởi vậy Anduin tổng kết ra, &yacute; ch&iacute;, t&acirc;m t&igrave;nh, độ thuần thục đều sẽ ảnh hưởng ma ch&uacute; cường độ, m&agrave; cường độ c&ugrave;ng ma lực tổng sản lượng quyết định một c&aacute;i ma ph&aacute;p hạn mức cao nhất.<br />\r\n<br />\r\nCho n&ecirc;n Anduin rất nhanh đối với kế tiếp học tập nội dung tiến h&agrave;nh điều chỉnh.<br />\r\n<br />\r\nAnduin cho rằng trước mắt trọng yếu nhất l&agrave; tự bảo vệ m&igrave;nh, m&agrave; tự bảo vệ m&igrave;nh phương ph&aacute;p nhanh nhất ch&iacute;nh l&agrave; nhanh ch&oacute;ng nắm giữ một m&ocirc;n sở trường ma ch&uacute;.<br />\r\n<br />\r\nĐầu ti&ecirc;n đơn giản hơn dễ d&agrave;ng học, hiện tại học kh&ocirc;ng thể kh&ocirc;ng đi, tiếp theo muốn th&ocirc;ng dụng t&iacute;nh mạnh mẽ, tốt nhất hạn mức cao nhất c&ograve;n cao.<br />\r\n<br />\r\nHắn b&agrave;n coi một c&aacute;i trước mắt học được tự vệ ma ch&uacute;, quyết định từ Impedimenta, tước vũ kh&iacute; nguyền rủa, đ&aacute;nh lui nguyền rủa b&ecirc;n trong chọn một ma ch&uacute; tiến h&agrave;nh sở trường.<br />\r\n<br />\r\nMuốn biết r&otilde; ở kiếp trước chơi tr&ograve; chơi thời điểm, Anduin liền th&iacute;ch chơi ph&aacute;i cực đoan, cũng ch&iacute;nh l&agrave; tại th&ecirc;m giờ thời điểm chiếu v&agrave;o một c&aacute;i thuộc t&iacute;nh hoặc kỹ năng cuồng đỗi, đỗi đến đỗi bất động th&ocirc;i, d&ugrave; sao một c&acirc;u, đ&aacute;nh kh&ocirc;ng lại ch&iacute;nh l&agrave; lực lượng kh&ocirc;ng đủ.<br />\r\n<br />\r\nSuy đi nghĩ lại, Anduin quyết định trước luyện tập Impedimenta, bởi v&igrave; vậy ma ch&uacute; đầu ti&ecirc;n tại học tập thời điểm nắm giữ rất nhanh, điều n&agrave;y l&agrave;m cho Anduin cảm thấy ch&uacute; ngữ c&ugrave;ng m&igrave;nh tương đối hợp, tiếp theo hắn nhận thức v&igrave; c&aacute;i n&agrave;y ma ch&uacute; c&oacute; thể khai ph&aacute;t tr&igrave;nh độ rất cao, nếu như c&oacute; thể như hắn mong muốn, kia Impedimenta ch&iacute;nh l&agrave; một c&aacute;i c&ocirc;ng thủ gồm nhiều mặt ma ch&uacute;.</p>', '2021-12-01 15:15:18', 15, 1),
(319, 78, 'Hôm Nay Lại Là Không Có đột Phá 1 Ngày', '1', '<p>Thượng Kiếm t&ocirc;ng chiếm cứ Vu Phong loan chập tr&ugrave;ng n&uacute;i xanh ph&iacute;a tr&ecirc;n, trong t&ocirc;ng m&ocirc;n c&oacute; s&aacute;u t&ograve;a đỉnh cao đứng thẳng v&agrave;o trong m&acirc;y.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, một t&ecirc;n thiếu ni&ecirc;n đang ngồi xếp bằng tại s&aacute;u t&ograve;a đỉnh cao b&ecirc;n trong trong đ&oacute; một t&ograve;a ph&iacute;a tr&ecirc;n.<br />\r\n<br />\r\nThiếu ni&ecirc;n t&ecirc;n l&agrave; Tần Phấn, l&agrave; Thượng Kiếm t&ocirc;ng nội m&ocirc;n đệ tử.<br />\r\n<br />\r\nTần Phấn đang chặt chẽ hai mắt, chuy&ecirc;n t&acirc;m hội tụ ch&acirc;n kh&iacute; tại v&ugrave;ng đan điền. Hiện tại đ&uacute;ng l&agrave; hắn đột ph&aacute; khẩn yếu quan đầu, tho&aacute;ng ra nhiều sai lầm đều sẽ dẫn đến hắn ph&iacute; c&ocirc;ng nhọc sức.<br />\r\n<br />\r\nM&agrave; ch&iacute;nh l&agrave; c&aacute;i n&agrave;y một &yacute; nghĩ sai lầm, đưa đến vất vả duy tr&igrave; c&acirc;n bằng bị đ&aacute;nh ph&aacute;.<br />\r\n<br />\r\nTần Phấn c&aacute;i tr&aacute;n một tho&aacute;ng thời gian nổi l&ecirc;n to như hạt đậu mồ h&ocirc;i. Hắn cắn chặt răng, sử xuất to&agrave;n th&acirc;n tr&ecirc;n dưới tất cả lực kh&iacute; đến &aacute;p chế ch&acirc;n kh&iacute; tại v&ugrave;ng đan điền ngưng tụ th&agrave;nh một c&aacute;i tiểu cầu.<br />\r\n<br />\r\nNhưng rất đ&aacute;ng tiếc, quật cường ch&acirc;n kh&iacute; vẫn l&agrave; kh&ocirc;ng nhận khống chế của hắn sinh sinh tại thể nội bạo t&aacute;n ra, m&atilde;nh liệt phản t&aacute;c dụng lực chấn Tần Phấn to&agrave;n th&acirc;n run rẩy.<br />\r\n<br />\r\nCứ việc v&igrave; lần đột ph&aacute; n&agrave;y Tần Phấn l&agrave;m đủ chuẩn bị, nhưng m&agrave; hay l&agrave; thất bại!<br />\r\n<br />\r\nSau một hồi l&acirc;u, Tần Phấn mở hai mắt ra, hướng về ph&iacute;a bầu trời lớn tiếng phun ra mấy chữ: &quot;&Aacute; T&acirc;y Lạp ngươi!&quot;<br />\r\n<br />\r\n&quot;Ngươi biết kh&ocirc;ng biết r&otilde; ta v&igrave; lần n&agrave;y đột ph&aacute; tới Ngưng Kh&iacute; cảnh, bỏ ra bao l&acirc;u thời gian l&agrave;m chuẩn bị a?&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y đều đ&atilde; lần thứ năm, lần thứ năm! C&ugrave;ng ta đồng thời nhập m&ocirc;n nội m&ocirc;n đệ tử c&oacute; ai c&ograve;n kẹt tại Luyện Kh&iacute; giai a! ! !&quot;<br />\r\n<br />\r\nBởi v&igrave; Tần Phấn chỗ đỉnh cao trong ng&agrave;y thường kh&ocirc;ng người tiếp cận, cỏ dại rậm rạp. Cho n&ecirc;n lời của hắn cũng kh&ocirc;ng cần lo lắng bị qua đường đệ tử nghe được.<br />\r\n<br />\r\nTại hướng ph&iacute;a bầu trời hung hăng ph&aacute;t tiết o&aacute;n hận trong l&ograve;ng về sau, Tần Phấn c&oacute; thể cảm gi&aacute;c được t&acirc;m t&igrave;nh tốt thụ một ch&uacute;t, l&uacute;c n&agrave;y hệ thống ph&aacute;n định khung xuất hiện ở trước mắt.<br />\r\n<br />\r\n&quot;Đinh đinh! Người sử dụng: Tần Phấn , nhiệm vụ - đột ph&aacute; tới Ngưng Kh&iacute; cảnh, thất bại!&quot;<br />\r\n<br />\r\n&quot;Nhiệm vụ từ đầu: Ch&acirc;n kh&iacute; tốc độ kh&ocirc;i phục -50%!&quot;<br />\r\n<br />\r\n&quot;Nhiệm vụ từ đầu: C&acirc;́m d&ugrave;ng đan dược!&quot;<br />\r\n<br />\r\n&quot;Nhiệm vụ từ đầu: Đột ph&aacute; thời gian hạn chế -20 ph&uacute;t!&quot;<br />\r\n<br />\r\n&quot;Nhiệm vụ từ đầu: Người sử dụng nhận phản phệ tổn thương +50%!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Nhiệm vụ từ đầu: Người sử dụng đột ph&aacute; độ kh&oacute; +20% &quot;<br />\r\n<br />\r\n&quot;Lần thứ năm xung k&iacute;ch Ngưng Kh&iacute; cảnh thất bại!&quot;<br />\r\n<br />\r\n&quot;Người sử dụng thu hoạch được hệ thống đền b&ugrave; điểm: Lực lượng +10, căn cốt +10, kh&iacute; k&igrave;nh +10, nh&igrave;n r&otilde; +10, th&acirc;n ph&aacute;p +10!&quot;<br />\r\n<br />\r\n&quot;Người sử dụng bảng thuộc t&iacute;nh: Lực lượng +99, căn cốt +99, kh&iacute; k&igrave;nh +99, nh&igrave;n r&otilde; +99, th&acirc;n ph&aacute;p +99!&quot;<br />\r\n<br />\r\nTần Phấn nh&igrave;n xem bảng chỉ cảm thấy một gi&acirc;y sau tự m&igrave;nh muốn phun ra một ngụm ti&ecirc;n huyết tới.<br />\r\n<br />\r\nCăn cứ ng&agrave;y n&agrave;o đ&oacute; đột nhi&ecirc;n thức tỉnh k&yacute; ức đến xem, hắn cũng kh&ocirc;ng phải l&agrave; thế giới n&agrave;y người, m&agrave; l&agrave; một c&aacute;i người xuy&ecirc;n việt!<br />\r\n<br />\r\nXuy&ecirc;n qua trước hắn l&agrave; một c&aacute;i trọng độ tr&ograve; chơi kẻ y&ecirc;u th&iacute;ch ki&ecirc;m độc giả, trong ấn tượng theo một đạo bạch quang hiện l&ecirc;n, &yacute; thức của hắn liền gi&aacute;ng l&acirc;m tại trước mặt ti&ecirc;n hiệp thế giới.<br />\r\n<br />\r\nKh&ocirc;ng đợi hắn lấy lại tinh thần, đ&uacute;ng l&uacute;c gặp một c&aacute;i tự xưng tự m&igrave;nh l&agrave; Thượng Kiếm t&ocirc;ng s&aacute;u Đại trưởng l&atilde;o một trong nữ nh&acirc;n.<br />\r\n<br />\r\nMặc d&ugrave; kh&ocirc;ng biết r&otilde; v&igrave; c&aacute;i g&igrave;, nhưng đối phương giống như l&acirc;̀n đ&acirc;̀u ti&ecirc;n liền coi trọng ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\nC&aacute;i n&agrave;y nữ nh&acirc;n giống như l&agrave; nh&igrave;n thấy c&aacute;i g&igrave; hiếm lạ bảo vật bộ dạng, quan s&aacute;t hắn rất l&acirc;u.<br />\r\n<br />\r\nKh&ocirc;ng biết r&otilde; đối phương c&oacute; nh&igrave;n ra v&acirc;y c&aacute;nh g&igrave; đến, liền n&oacute;i muốn thu Tần Phấn l&agrave;m đồ đệ, truyền thụ cho hắn khai thi&ecirc;n kiếm thuật, tu ti&ecirc;n nh&acirc;n chi ph&uacute;c.<br />\r\n<br />\r\nCứ việc qu&aacute; tr&igrave;nh tiến triển c&oacute; chút nhanh, nhưng Tần Phấn &yacute; thức được tự m&igrave;nh c&acirc;̀m tới c&oacute; thể l&agrave; nam nh&acirc;n vật ch&iacute;nh m&ocirc; bản, từ đ&acirc;y liền muốn bước l&ecirc;n b&igrave;nh bộ thanh v&acirc;n, một bước th&agrave;nh ti&ecirc;n con đường!<br />\r\n<br />\r\nNhưng m&agrave; hắn ph&aacute;t hiện tưởng tượng l&agrave; mỹ hảo, hiện thực l&agrave; t&agrave;n khốc. Tự m&igrave;nh bỏ ra đằng đẵng ba năm thời gian mới miễn cưỡng tu đ&acirc;̀y Luyện Kh&iacute; giai, cho đến ng&agrave;y nay cũng kh&ocirc;ng c&aacute;ch n&agrave;o đột ph&aacute; đến Ngưng Kh&iacute; giai.<br />\r\n<br />\r\nTu luyện thế giới tổng cộng chia l&agrave;m Luyện Kh&iacute;, Ngưng Kh&iacute;, Kết Đan, Th&ocirc;ng Thể, Tụ Linh, H&oacute;a Hồn, Nguy&ecirc;n Thần, Đại Thừa, Si&ecirc;u Ph&agrave;m 9 c&aacute;i cảnh giới.<br />\r\n<br />\r\nM&agrave; mỗi c&aacute;i cảnh giới lại chia nhỏ l&agrave; sơ, trung, thượng, vi&ecirc;n m&atilde;n bốn c&aacute;i tiểu cảnh giới.<br />\r\n<br />\r\nNgưng Kh&iacute; giai chỉ c&oacute; thể n&oacute;i l&agrave; so nhập m&ocirc;n Luyện Kh&iacute; cảnh tốt hơn một điểm ti&ecirc;u chuẩn, m&agrave; Tần Phấn tại sư ph&oacute; đơn độc chỉ điểm ph&iacute;a dưới cũng kh&ocirc;ng th&agrave;nh c&ocirc;ng đột ph&aacute;.<br />\r\n<br />\r\nNghe n&oacute;i s&aacute;t v&aacute;ch ngọn n&uacute;i đệ tử c&oacute; người cũng đ&atilde; tại chuẩn bị đột ph&aacute; Kết Đan cảnh!<br />\r\n<br />\r\nTại lần thứ hai đột ph&aacute; thất bại về sau, sư ph&oacute; đại kh&aacute;i ph&aacute;t hiện l&agrave; tự m&igrave;nh nh&atilde;n quang xuất hiện vấn đề.<br />\r\n<br />\r\nDứt kho&aacute;t coi như l&ecirc;n bỏ mặc chưởng quỹ, lưu lại một phần thư t&iacute;n sau biến mất tại trong t&ocirc;ng m&ocirc;n. Chỉ để lại ch&iacute;nh Tần Phấn một người lẻ loi hiu quạnh canh giữ ở tr&ecirc;n n&uacute;i tu luyện.<br />\r\n<br />\r\nM&agrave; muốn n&oacute;i l&agrave; c&aacute;i g&igrave; Tần Phấn chết sống kh&ocirc;ng đột ph&aacute; nổi Ngưng Kh&iacute; cảnh, vậy th&igrave; chỉ tr&aacute;ch c&aacute;i hệ thống n&agrave;y!<br />\r\n<br />\r\nTần Phấn c&oacute; ch&uacute;t im lặng ch&iacute;nh nh&igrave;n xem bảng khung, cứ việc c&oacute; xinh đẹp thuộc t&iacute;nh gi&aacute; trị, nhưng thật to Luyện Kh&iacute; vi&ecirc;n m&atilde;n bốn chữ vẫn l&agrave; đau nh&oacute;i hắn t&acirc;m.<br />\r\n<br />\r\nBảng đẹp mắt c&oacute; l&agrave;m được c&aacute;i g&igrave;, kh&ocirc;ng chịu nổi cảnh giới của m&igrave;nh thấp a! C&oacute; bản lĩnh vung một b&agrave;n tay đem cảnh giới cao nh&acirc;n trực đ&oacute;n đ&aacute;nh bay, nhưng Tần Phấn cảm gi&aacute;c tự m&igrave;nh kh&ocirc;ng c&oacute; khả năng l&agrave;m được a!<br />\r\n<br />\r\nNgười kh&aacute;c người xuy&ecirc;n việt đạt được hệ thống, cũng kh&ocirc;ng phải d&ugrave;ng hết t&aacute;c dụng cho m&igrave;nh t&uacute;c chủ nhanh ch&oacute;ng tăng l&ecirc;n đẳng cấp, nhường bọn hắn cũng may b&ecirc;n ngo&agrave;i đ&aacute;nh mặt trang b&ocirc;i, m&agrave; hắn hệ thống vừa vặn rất tốt, kh&ocirc;ng gi&uacute;p trở ngại đ&atilde; coi l&agrave; tốt!<br />\r\n<br />\r\nMỗi lần hệ thống đều sẽ buộc hắn l&agrave;m một đống hỗn tạp nhiệm vụ chi nh&aacute;nh đến đề thăng tự m&igrave;nh năm chiều trị số, một khi đến đột ph&aacute; nhiệm vụ, c&ograve;n c&oacute; thể cho m&igrave;nh tăng th&ecirc;m một đống mặt tr&aacute;i từ đầu cưỡng &eacute;p hạn chế tự m&igrave;nh đột ph&aacute; x&aacute;c suất th&agrave;nh c&ocirc;ng!<br />\r\n<br />\r\nLần n&agrave;y đột ph&aacute; tới Ngưng Kh&iacute; cảnh nhiệm vụ, c&agrave;ng l&agrave; kh&ocirc;ng hợp th&oacute;i thường đến xuất hiện năm c&aacute;i mặt tr&aacute;i từ đầu!<br />\r\n<br />\r\nL&agrave;m th&acirc;m ni&ecirc;n người chơi Tần Phấn, đột nhi&ecirc;n &yacute; thức được tự m&igrave;nh xuy&ecirc;n qua trước chơi trong tr&ograve; chơi cũng đ&uacute;ng l&uacute;c lưu h&agrave;nh lấy như thế c&aacute;i c&aacute;ch chơi. Ch&iacute;nh l&agrave; bức b&aacute;ch người chơi khi&ecirc;u chiến tăng th&ecirc;m k&egrave;m theo từ đầu theo đuổi cầu độ kh&oacute; cao hơn th&ocirc;ng quan, cuối c&ugrave;ng liền c&oacute; thể thu hoạch được phi thường cường liệt cảm gi&aacute;c th&agrave;nh tựu.<br />\r\n<br />\r\nC&oacute; thể hệ thống c&oacute; quan t&acirc;m qua Tần Phấn sao? Kh&ocirc;ng c&oacute;!<br />\r\n<br />\r\nTần Phấn căn bản kh&ocirc;ng muốn c&oacute; c&aacute;i g&igrave; cảm gi&aacute;c th&agrave;nh tựu, hắn chỉ muốn l&agrave;m từng bước tu luyện, dầu g&igrave; l&agrave;m ti&ecirc;u chuẩn tr&igrave;nh độ tu sĩ tại Thượng Kiếm t&ocirc;ng ngồi ăn rồi chờ chết cũng tốt a!<br />\r\n<br />\r\nHiện tại hắn kẹt tại Luyện Kh&iacute; giai, nhường Tần Phấn đi ra ngo&agrave;i đều kh&ocirc;ng c&oacute; &yacute; tứ.<br />\r\n<br />\r\nTại Thượng Kiếm t&ocirc;ng trong lịch sử c&ograve;n kh&ocirc;ng c&oacute; c&aacute;i n&agrave;o nội m&ocirc;n đệ tử tu luyện ba năm kh&ocirc;ng c&oacute; đột ph&aacute; đến Ngưng Kh&iacute; giai.<br />\r\n<br />\r\nA kh&ocirc;ng, Tần Phấn khai s&aacute;ng lịch sử, hắn l&agrave; c&aacute;i thứ nhất, cũng kh&ocirc;ng biết r&otilde; tương lai sẽ có hay kh&ocirc;ng có người thứ hai.<br />\r\n<br />\r\nTần Phấn gi&atilde;y dụa đứng l&ecirc;n, đi v&agrave;o b&ecirc;n bờ ao b&ecirc;n cạnh.<br />\r\n<br />\r\nMột c&aacute;i to&agrave;n th&acirc;n quần &aacute;o r&aacute;ch rưới, mặt mũi tr&agrave;n đầy bụi đ&acirc;́t thấy kh&ocirc;ng r&otilde; ngũ quan treo bạo tạc đầu c&aacute;i b&oacute;ng xuất hiện ở tr&ecirc;n mặt nước.<br />\r\n<br />\r\nTần Phấn nh&igrave;n xem như thế ngh&egrave;o t&uacute;ng tự m&igrave;nh, kh&ocirc;ng khỏi c&oacute; ch&uacute;t l&ograve;ng chua x&oacute;t, hai tay n&acirc;ng l&ecirc;n một c&aacute;i ao nước rửa sạch tự m&igrave;nh khu&ocirc;n mặt.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nĐ&oacute;n lấy, một tấm phong thần tuấn l&atilde;ng khu&ocirc;n mặt lo&aacute;ng tho&aacute;ng xuất hiện ở gợn s&oacute;ng ở giữa.<br />\r\n<br />\r\nNam tử trước mặt song mi nồng đậm l&ocirc;ng m&agrave;y h&igrave;nh hướng l&ecirc;n như l&agrave; hai thanh lợi kiếm đồng dạng đứng ở tr&ecirc;n &aacute;nh mắt phương. Nh&igrave;n xuống, một đ&ocirc;i lười biếng cặp mắt đ&agrave;o hoa phối hợp da thịt trắng n&otilde;n, cao thẳng c&aacute;i mũi, như c&oacute; như kh&ocirc;ng nhếch l&ecirc;n kh&oacute;e miệng đều thuyết minh người n&agrave;y bề ngo&agrave;i thật tốt.<br />\r\n<br />\r\nTần Phấn rất đẹp trai! C&aacute;i n&agrave;y cũng c&oacute; thể l&agrave; hắn duy nhất ưu điểm!<br />\r\n<br />\r\nCho đến ng&agrave;y nay Tần Phấn thậm ch&iacute; c&oacute; ch&uacute;t ho&agrave;i nghi, l&uacute;c ấy sư ph&oacute; c&oacute; phải hay kh&ocirc;ng coi trọng ch&iacute;nh l&agrave; m&igrave;nh mặt, căn bản liền kh&ocirc;ng c&oacute; ch&uacute; &yacute; tới m&igrave;nh c&oacute; hay kh&ocirc;ng tu luyện thi&ecirc;n ph&uacute;.<br />\r\n<br />\r\nTần Phấn h&agrave;i l&ograve;ng sờ lấy cằm của m&igrave;nh: &quot;H&ocirc;m nay ta c&ograve;n l&agrave; đẹp trai như vậy! Bất qu&aacute; tại xuy&ecirc;n qua trước chỉ l&agrave; c&aacute;i b&igrave;nh qu&acirc;n tr&igrave;nh độ.&quot;<br />\r\n<br />\r\nChỉ l&agrave; nh&igrave;n thấy trương n&agrave;y anh tuấn khu&ocirc;n mặt, ngay cả m&igrave;nh trước đ&oacute; đột ph&aacute; thất bại phiền muộn cảm gi&aacute;c cũng thu được một ch&uacute;t chậm lại.<br />\r\n<br />\r\nĐang l&uacute;c hắn d&ugrave;ng nước lột b&igrave;nh kiểu t&oacute;c, chuẩn bị thay đổi một th&acirc;n sạch sẽ y phục l&uacute;c, một đạo xa xăm tiếng chu&ocirc;ng đột nhi&ecirc;n vang l&ecirc;n!<br />\r\n<br />\r\nTiếp lấy li&ecirc;n tục mấy đạo tiếng chu&ocirc;ng đồng thời vang l&ecirc;n, quanh quẩn tại trống trải s&aacute;u t&ograve;a ngọn n&uacute;i ở giữa.<br />\r\n<br />\r\n&quot;Hỏng b&eacute;t! Ta qu&ecirc;n h&ocirc;m nay l&agrave; t&ocirc;ng m&ocirc;n mỗi năm một lần chi&ecirc;u sinh đại lễ!&quot;<br />\r\n<br />\r\nTần Phấn đột nhi&ecirc;n &yacute; thức được c&aacute;i g&igrave;, h&ocirc;m nay hắn nguy&ecirc;n kế hoạch ch&iacute;nh l&agrave; tại chi&ecirc;u sinh đại lễ trước đ&oacute; đột ph&aacute; đến Ngưng Kh&iacute; giai, dạng n&agrave;y tại trước c&ocirc;ng ch&uacute;ng cũng sẽ kh&ocirc;ng qu&aacute; l&agrave;m mất mặt sư ph&oacute;!<br />\r\n<br />\r\nM&agrave; lại sư ph&oacute; c&ograve;n đặc biệt phi kiếm truyền tin y&ecirc;u cầu hắn thay thầy thu c&aacute;i đồ đệ.<br />\r\n<br />\r\nCó th&ecirc;̉ đột ph&aacute; thất bại đối với hắn đả k&iacute;ch qu&aacute; lớn, lấy về phần đem việc n&agrave;y qu&ecirc;n mất!<br />\r\n<br />\r\nTần Phấn vội v&agrave;ng theo trong nhẫn chứa đồ m&oacute;c ra một c&aacute;i &aacute;o kho&aacute;c đắp l&ecirc;n tr&ecirc;n người, lại cầm l&ecirc;n một thanh trường kiếm đeo ở h&ocirc;ng.<br />\r\n<br />\r\nThời gian c&oacute; hạn, chỉ c&oacute; thể dạng n&agrave;y c&aacute;ch ăn mặc tự m&igrave;nh.<br />\r\n<br />\r\nChờ hắn d&ugrave;ng phương thức chạy vội v&agrave;ng đi v&agrave;o Thanh Phong đường trước, ph&aacute;t hiện trước đường đ&atilde; k&iacute;n người hết chỗ.</p>', '2021-12-01 15:17:14', 11, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(320, 78, 'Gây Nên đám Người Oanh động Chiêu Sinh đại Lễ', '2', '<p>Thanh Phong đường trước đầu người to&aacute;n loạn, v&ocirc; c&ugrave;ng n&aacute;o nhiệt.<br />\r\n<br />\r\nVừa mới chạy đến Tần Phấn tiện tay bắt một người đi đường mở miệng liền hỏi: &quot;Huynh đệ, hiện tại tiến h&agrave;nh đến một bước n&agrave;o rồi?&quot;<br />\r\n<br />\r\nNgười qua đường Gi&aacute;p l&uacute;c đầu v&igrave; ch&iacute;nh m&igrave;nh bị tiện tay nắm l&ecirc;n c&oacute; ch&uacute;t phẫn nộ, nhưng nh&igrave;n thấy Tần Phấn khu&ocirc;n mặt sau c&oacute; loại n&agrave;y kh&ocirc;ng n&oacute;i ra được sợ h&atilde;i x&ocirc;ng l&ecirc;n đầu, chỉ c&oacute; thể run rẩy trả lời đến: &quot;Hiện tại tất cả những học sinh mới đ&atilde; đều đến đ&ocirc;ng đủ, đang đợi s&aacute;u Đại trưởng l&atilde;o nh&oacute;m nhập tọa bắt đầu chi&ecirc;u sinh đại lễ.&quot;<br />\r\n<br />\r\nTần Phấn c&oacute; ch&uacute;t chẳng biết tại sao, chẳng lẽ m&igrave;nh nh&igrave;n qua lớn l&ecirc;n giống l&agrave; rất hung bộ dạng sao? R&otilde; r&agrave;ng vừa mới tự m&igrave;nh d&ograve; x&eacute;t m&igrave;nh thời điểm vẫn rất so&aacute;i.<br />\r\n<br />\r\nĐang l&uacute;c hai người tr&ograve; chuyện l&uacute;c, năm Đại trưởng l&atilde;o đ&atilde; ngồi xuống xong xu&ocirc;i, chỉ l&agrave; tại ngo&agrave;i c&ugrave;ng b&ecirc;n phải nhất c&ograve;n trống kh&ocirc;ng m&ocirc;̣t cái ch&ocirc;̃ ng&ocirc;̀i.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng được!&quot; Tần Phấn vỗ vỗ đầu, tự m&igrave;nh lại theo bản năng đứng ở dưới đ&agrave;i, hiện tại hẳn l&agrave; l&ecirc;n đ&agrave;i mới l&agrave;.<br />\r\n<br />\r\nNhững học sinh mới gặp chỗ ngồi l&uacute;c rảnh rỗi m&agrave; chi&ecirc;u sinh đại lễ chưa bắt đầu, liền x&igrave; x&agrave;o b&agrave;n t&aacute;n.<br />\r\n<br />\r\n&quot;C&oacute; nh&igrave;n thấy kh&ocirc;ng, c&aacute;i kia vị tr&iacute; l&agrave; Khai Thi&ecirc;n kiếm phong trưởng l&atilde;o vị tr&iacute;!&quot;<br />\r\n<br />\r\n&quot;Khai Thi&ecirc;n kiếm phong? Ngươi n&oacute;i l&agrave; c&aacute;i kia mười năm c&aacute;i chi&ecirc;u thu một t&ecirc;n đệ tử, trong ng&agrave;y thường Thần Long kh&ocirc;ng thấy đu&ocirc;i lục trưởng l&atilde;o?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng sai! Nghe n&oacute;i n&agrave;ng trước đ&acirc;y tuyển nhận đệ tử đ&atilde; đến Kết Đan cảnh, chỉ bằng v&agrave;o kiếm ph&aacute;p ngay tại người c&ugrave;ng thế hệ b&ecirc;n trong kh&ocirc;ng c&oacute; địch thủ!&quot;<br />\r\n<br />\r\n&quot;Kia gia nhập Khai Thi&ecirc;n kiếm phong chẳng phải l&agrave; b&igrave;nh bộ thanh v&acirc;n?&quot;<br />\r\n<br />\r\n&quot;Khai Thi&ecirc;n kiếm phong cơ hồ kh&ocirc;ng l&agrave;m cho thu đệ tử, muốn chi&ecirc;u khẳng định cũng l&agrave; sư huynh như thế tuyệt thế thi&ecirc;n t&agrave;i, ngươi ta vẫn phải chết c&aacute;i &yacute; niệm n&agrave;y đi!&quot;<br />\r\n<br />\r\n&quot;Ta bỏ mặc, ta c&aacute;i biết r&otilde; Khai Thi&ecirc;n kiếm phong sư huynh nhất định rất đẹp trai!&quot; N&oacute;i chuyện nam đệ tử hai mắt mạo Tinh Tinh, hiển nhi&ecirc;n l&agrave; một bộ m&ecirc; đệ h&igrave;nh dạng &quot;Rất muốn nhường sư huynh d&ugrave;ng roi, a kh&ocirc;ng, d&ugrave;ng kiếm quật một c&aacute;i ta &agrave;!&quot;<br />\r\n<br />\r\n&quot;Ngươi coi như xong đi! C&aacute;i kia cơ hội khẳng định l&agrave; cho ta! Muốn quật cũng l&agrave; quật ta! !&quot;<br />\r\n<br />\r\nHưng phấn nh&oacute;m đệ tử bắt đầu bởi v&igrave; kh&ocirc;ng hiểu nguy&ecirc;n nh&acirc;n đẩy tới ngăn đi.<br />\r\n<br />\r\nM&agrave; xem như nghe đồn người trong cuộc Tần Phấn một b&ecirc;n ra sức trong đ&aacute;m người chen tới chen lui, một b&ecirc;n đứt qu&atilde;ng nghe nh&oacute;m đệ tử lời đ&agrave;m luận ngữ.<br />\r\n<br />\r\n&quot;Ừm? Nh&oacute;m ch&uacute;ng ta t&ocirc;ng m&ocirc;n c&aacute;i g&igrave; thời điểm xuất hiện như thế c&aacute;i ngưu nh&acirc;n rồi? Một đời mới đ&atilde; xuất hiện Kết Đan cảnh?&quot;<br />\r\n<br />\r\nTần Phấn kh&ocirc;ng khỏi c&oacute; ch&uacute;t bi phẫn, đ&acirc;y ch&iacute;nh l&agrave; người so với người, tức chết người sao? Ch&iacute;nh r&otilde; r&agrave;ng c&ograve;n tại Luyện Kh&iacute; cảnh gi&atilde;y dụa, v&acirc;̣y mà đ&atilde; c&oacute; đệ tử l&ecirc;n tới Kết Đan cảnh!<br />\r\n<br />\r\nM&agrave; tr&ecirc;n đ&agrave;i năm vị trưởng l&atilde;o khu&ocirc;n mặt nghi&ecirc;m t&uacute;c nh&igrave;n xem dưới đ&agrave;i nghị luận ầm ĩ đ&aacute;m người, l&agrave; chủ bắt người đệ tử cũng hơi c&oacute; vẻ xấu hổ.<br />\r\n<br />\r\n&quot;Sư ph&oacute;, c&oacute; muốn hay kh&ocirc;ng ta đi l&aacute;i Thi&ecirc;n Kiếm phong th&uacute;c th&uacute;c giục Tần sư huynh?&quot;<br />\r\n<br />\r\n&quot;Hồ nh&aacute;o! H&ocirc;m nay l&agrave; c&aacute;i g&igrave; thời gian? ! Tiểu Lục đệ tử t&iacute;nh c&aacute;ch t&iacute;nh t&igrave;nh l&agrave;m sao ho&agrave;n to&agrave;n c&ugrave;ng n&agrave;ng, kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o lấy điều!&quot; N&oacute;i chuyện trưởng l&atilde;o t&oacute;c đỏ mặt đỏ, trợn mắt đ&ocirc;i trừng, c&oacute; ch&uacute;t tức giận vuốt mặt b&agrave;n, cảnh tượng n&agrave;y đ&aacute;y chăn hạ đẳng đợi nh&oacute;m đệ tử thấy được.<br />\r\n<br />\r\nĐ&aacute;m người lập tức cũng biến th&agrave;nh an tĩnh bắt đầu.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Tốt tốt, l&atilde;o tam. Đừng tức giận như vậy, ch&uacute;ng ta quen biết mấy chục năm, tiểu Lục c&aacute;i kia t&iacute;nh c&aacute;ch t&iacute;nh t&igrave;nh, mang ra đệ tử c&oacute; thể l&agrave; đứng đắn g&igrave; t&iacute;nh t&igrave;nh? Thời điểm c&ograve;n sớm, nhiều chờ đ&atilde; cũng kh&ocirc;ng c&oacute; việc g&igrave;!&quot; Một vị kh&aacute;c nh&igrave;n qua mặt mũi hiền l&agrave;nh trưởng l&atilde;o l&ecirc;n tiếng n&oacute;i.<br />\r\n<br />\r\nT&oacute;c đỏ mặt đỏ trưởng l&atilde;o vừa nghe đến hiền l&agrave;nh trưởng l&atilde;o mở miệng, lập tức y&ecirc;n tĩnh trở lại.<br />\r\n<br />\r\nChỉ l&agrave; hai tay vẫn c&oacute; ch&uacute;t phẫn hận bất b&igrave;nh xoa xoa c&aacute;i ghế chỗ tay cầm.<br />\r\n<br />\r\nM&agrave; l&uacute;c n&agrave;y Tần Phấn cuối c&ugrave;ng từ trong đ&aacute;m người tr&aacute;nh tho&aacute;t m&agrave; ra, trực tiếp hướng đi trước s&acirc;n kh&acirc;́u.<br />\r\n<br />\r\nChỉ l&agrave; đột nhi&ecirc;n y&ecirc;n tĩnh kh&ocirc;ng kh&iacute; nhường hắn rất kh&ocirc;ng quen, m&agrave; lại Tần Phấn c&oacute; thể cảm nhận được ph&iacute;a sau nh&oacute;m đệ tử &aacute;nh mắt như l&agrave; kim đ&acirc;m đồng dạng nh&igrave;n hắn ph&iacute;a sau lưng.<br />\r\n<br />\r\n&quot;Nhịn xuống, nhịn xuống! D&aacute;ng vẻ tự nhi&ecirc;n! Tư th&aacute;i tự nhi&ecirc;n! Tần Phấn, nhất cử nhất động của ngươi cũng đại biểu cho sư ph&oacute;!&quot;<br />\r\n<br />\r\n&quot;Ngươi thấy kh&ocirc;ng, c&aacute;i kia giống như ch&iacute;nh l&agrave; Khai Thi&ecirc;n kiếm phong hiện nay đệ tử duy nhất, Tần Phấn sư huynh!&quot;<br />\r\n<br />\r\n&quot;Quả nhi&ecirc;n giống như tin đồn đẹp trai. Kh&iacute; chất mờ mịt xuất trần, mỗi một bước đều c&oacute; thể thể hiện cảnh giới tr&ecirc;n kh&aacute;c biệt! Rất muốn c&ugrave;ng sư huynh đấu kiếm tỷ th&iacute; một phen a!&quot;<br />\r\n<br />\r\n&quot;Trưởng l&atilde;o kh&ocirc;ng đến, vậy n&oacute;i r&otilde; năm nay Khai Thi&ecirc;n kiếm phong vẫn như cũ kh&ocirc;ng l&agrave;m cho người?&quot;<br />\r\n<br />\r\n&quot;Ngươi cũng đừng suy nghĩ, coi như tới, nh&oacute;m ch&uacute;ng ta tư chất thường thường trưởng l&atilde;o l&agrave;m sao lại để mắt đ&acirc;u?&quot;<br />\r\n<br />\r\nNgắn ngủi mấy chục m&eacute;t cự ly, Tần Phấn lại cảm gi&aacute;c tự m&igrave;nh đi mấy canh giờ, l&agrave;m hắn tra tấn kh&ocirc;ng g&igrave; s&aacute;nh được.<br />\r\n<br />\r\nTừng đạo &aacute;nh mắt buộc hắn ph&iacute;a sau lưng đau qu&aacute;.<br />\r\n<br />\r\nThật vất vả đi v&agrave;o dưới đ&agrave;i l&uacute;c, Tần Phấn ki&ecirc;n tr&igrave; đối trưởng l&atilde;o nh&oacute;m xoay người thở d&agrave;i n&oacute;i ra: &quot;C&aacute;c vị trưởng l&atilde;o, tha thứ đệ tử tới chậm.&quot;<br />\r\n<br />\r\n&quot;Hừ! Ngươi cũng biết m&igrave;nh tới trễ! R&otilde; r&agrave;ng theo Khai Thi&ecirc;n kiếm phong ngự kiếm m&agrave; đến bất qu&aacute; một l&aacute;t, v&acirc;̣y mà nhường nh&oacute;m ch&uacute;ng ta đợi l&acirc;u l&acirc;u như vậy!&quot; T&oacute;c đỏ trưởng l&atilde;o hiển nhi&ecirc;n l&agrave; kh&ocirc;ng cao hứng, phất tay l&uacute;c ống tay &aacute;o mang theo một trận gi&oacute; &acirc;m thanh.<br />\r\n<br />\r\n&quot;Đệ tử cảm thấy gần Nhật Thi&ecirc;n chỉ c&oacute; điểm chắn kiếm, liền lựa chọn đi bộ m&agrave; tới. C&ograve;n xin trưởng l&atilde;o nh&oacute;m th&ocirc;ng cảm nhiều hơn.&quot;<br />\r\n<br />\r\nTần Phấn c&oacute; ch&uacute;t xấu hổ, ngự kiếm phi h&agrave;nh thấp nhất cũng muốn Ngưng Kh&iacute; cảnh mới c&oacute; thể sử dụng. M&agrave; b&acirc;y giờ hắn chỉ c&oacute; Luyện Kh&iacute; cảnh, l&agrave;m sao bay?<br />\r\n<br />\r\nN&agrave;o biết nghe xong Tần Phấn lời n&oacute;i, c&ograve;n lại mấy vị trưởng l&atilde;o giống như l&agrave; cảm động l&acirc;y đồng dạng gật đầu.<br />\r\n<br />\r\n&quot;X&aacute;c thực, ta cũng cảm thấy gần đ&acirc;y ngự kiếm phi h&agrave;nh mười điểm phiền phức. Qu&aacute; chặn lại.&quot;<br />\r\n<br />\r\n&quot;Đúng v&acirc;̣y a đ&uacute;ng vậy a, c&aacute;c loại t&ocirc;ng chủ xuất quan, nh&oacute;m ch&uacute;ng ta cũng muốn tr&igrave;nh b&aacute;o một c&aacute;i ngự kiếm phi h&agrave;nh quản chế, nếu kh&ocirc;ng qu&aacute; chặn lại!&quot;<br />\r\n<br />\r\n&quot;Đúng đ&acirc;́y, kia Tần Phấn đi tới x&aacute;c thực phải tốn nhiều thời gian!&quot;<br />\r\n<br />\r\nT&oacute;c đỏ trưởng l&atilde;o vẫn c&oacute; ch&uacute;t kh&ocirc;ng phục, một b&ecirc;n hiền l&agrave;nh trưởng l&atilde;o lại lần nữa l&ecirc;n tiếng<br />\r\n<br />\r\n&quot;L&atilde;o tam, ngươi ta đi qua trầm m&ecirc; tu luyện, cũng bỏ qua kh&ocirc;ng &iacute;t trọng yếu đại điển. Đến trễ mấy ph&uacute;t cũng kh&ocirc;ng ảnh hưởng to&agrave;n cục, cứ như vậy đi!&quot;<br />\r\n<br />\r\n&quot;Hừ! L&atilde;o nhị ngươi liền nhiều lần gi&uacute;p tiểu Lục k&eacute;o lệch khung đi! Ta mặc kệ ta!&quot;<br />\r\n<br />\r\nT&oacute;c đỏ trưởng l&atilde;o giận dữ lắc lắc ống tay &aacute;o, quay đầu nh&igrave;n sang một b&ecirc;n kh&ocirc;ng n&oacute;i th&ecirc;m g&igrave; nữa.<br />\r\n<br />\r\nHiền l&agrave;nh trưởng l&atilde;o thấy thế, quay đầu n&oacute;i với Tần Phấn đến.<br />\r\n<br />\r\n&quot;Tần Phấn, thời điểm kh&ocirc;ng c&ograve;n sớm, ngồi xuống đi.&quot;<br />\r\n<br />\r\nTần Phấn vội v&agrave;ng cự tuyệt: &quot;Trưởng l&atilde;o nh&oacute;m ngồi l&agrave; được, đệ tử mặc d&ugrave; l&agrave; thay sư thu đồ, nhưng c&ograve;n kh&ocirc;ng c&oacute; như thế cuồng vọng. Đệ tử đứng đấy l&agrave; đủ.&quot;<br />\r\n<br />\r\nHiền l&agrave;nh trưởng l&atilde;o nghe vậy gật đầu, lập tức đứng dậy hướng về ph&iacute;a dưới đ&agrave;i tất cả chờ đợi nh&oacute;m đệ tử tuy&ecirc;n bố.<br />\r\n<br />\r\n&quot;Chi&ecirc;u sinh đại lễ bắt đầu!&quot;<br />\r\n<br />\r\nVừa dứt lời, đ&aacute;m người bắt đầu đ&atilde; l&acirc;u bạo động.<br />\r\n<br />\r\nNgười người cũng kỳ vọng m&igrave;nh c&oacute; thể đi v&agrave;o một c&aacute;i tốt ngọn n&uacute;i, từ đ&acirc;y đi đến nh&acirc;n sinh tu luyện đỉnh phong!<br />\r\n<br />\r\n&quot;Tần Phấn, ngươi Khai Thi&ecirc;n kiếm phong nhiều năm qua cũng kh&ocirc;ng tham gia qua một lần t&ocirc;ng m&ocirc;n thi đấu. C&aacute;i n&agrave;y tr&igrave;nh tự chi vị. . .&quot;<br />\r\n<br />\r\nL&uacute;c n&agrave;y lại một vị trưởng l&atilde;o l&ecirc;n tiếng, Tần Phấn lập tức liền minh bạch trong lời n&oacute;i của đối phương &yacute; tứ.<br />\r\n<br />\r\nMỗi năm một lần t&ocirc;ng m&ocirc;n thi đấu c&ugrave;ng chi&ecirc;u sinh đại lễ l&agrave; trong t&ocirc;ng m&ocirc;n hai đại trọng yếu sự t&igrave;nh.<br />\r\n<br />\r\nT&ocirc;ng m&ocirc;n thi đấu so đấu ch&iacute;nh l&agrave; từng c&aacute;i ngọn n&uacute;i đ&atilde; qua một năm nh&oacute;m đệ tử tiến bộ, thu hoạch được chiến thắng t&ocirc;ng m&ocirc;n năm tiếp theo c&oacute; thể thu hoạch được ưu tuyển lựa chọn đệ tử quyền lợi.<br />\r\n<br />\r\nM&agrave; Tần Phấn chỗ Khai Thi&ecirc;n kiếm phong bao năm qua tới đệ tử đều chỉ c&oacute; hắn một người, lại th&ecirc;m Tần Phấn bản th&acirc;n từ chối kh&ocirc;ng được tham gia t&ocirc;ng m&ocirc;n thi đấu. Cho n&ecirc;n Khai Thi&ecirc;n kiếm phong tự nhi&ecirc;n m&agrave; vậy ni&ecirc;n kỉ năm thứ s&aacute;u. N&oacute;i c&aacute;ch kh&aacute;c c&aacute;c loại vị tr&iacute; thứ năm trưởng l&atilde;o lựa chọn xong xu&ocirc;i về sau mới c&oacute; lựa chọn cơ hội.<br />\r\n<br />\r\n&quot;Trưởng l&atilde;o nh&oacute;m, Tần Phấn lần n&agrave;y phụng sư đời chi&ecirc;u, c&aacute;i chi&ecirc;u một người. Về phần thuận vị, đều c&oacute; thể chờ trưởng l&atilde;o nh&oacute;m chọn xong về sau lại tuyển, Tần Phấn tự nhi&ecirc;n kh&ocirc;ng tranh kh&ocirc;ng đoạt.&quot;<br />\r\n<br />\r\nTần Phấn sau khi n&oacute;i xong c&aacute;i tr&aacute;n to&aacute;t ra mấy giọt mồ h&ocirc;i lạnh, n&oacute;i thật, hắn cũng kh&ocirc;ng biết m&igrave;nh muốn chi&ecirc;u dạng g&igrave; đệ tử.<br />\r\n<br />\r\nThời gian trở lại Mạc Thi&ecirc;n s&aacute;ng sớm, Tần Phấn vừa mới tỉnh lại liền ph&aacute;t hiện một phần thư t&iacute;n đinh đứng ở cửa ph&ograve;ng của m&igrave;nh ph&iacute;a tr&ecirc;n.<br />\r\n<br />\r\nHắn đều kh&ocirc;ng cần xem, liền biết r&otilde; đ&acirc;y l&agrave; tự m&igrave;nh sư ph&oacute; thủ ph&aacute;p.<br />\r\n<br />\r\nTrong thư viết đến Khai Thi&ecirc;n kiếm phong đệ tử số qu&aacute; &iacute;t, c&aacute;c loại Tần Phấn ng&agrave;y n&agrave;o kh&ocirc;ng tại t&ocirc;ng m&ocirc;n m&agrave; sư ph&oacute; lại c&ograve;n tại b&ecirc;n ngo&agrave;i du lịch, vậy c&aacute;i n&agrave;y Khai Thi&ecirc;n kiếm phong chỉ c&oacute; một người cũng mất, kh&ocirc;ng chừng sẽ bị chưởng m&ocirc;n ph&acirc;n cho ngọn n&uacute;i kh&aacute;c, như thế chờ hắn trở lại ch&iacute;nh liền nh&agrave; cũng mất, cho n&ecirc;n hi vọng Tần Phấn c&oacute; thể thay thầy chi&ecirc;u đồ.<br />\r\n<br />\r\nTần Phấn nh&igrave;n thấy tin chỉ l&agrave; k&ecirc;u khổ kh&ocirc;ng đưa, tự m&igrave;nh c&aacute;i n&agrave;y tu vi lại thế n&agrave;o c&oacute; thể sẽ xuống n&uacute;i lịch l&atilde;m, Luyện Kh&iacute; cảnh cho linh th&uacute; cho bụng cũng kh&ocirc;ng kịp!</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nC&oacute; thể sư mệnh kh&oacute; vi phạm, hắn vẫn l&agrave; đ&ecirc;́n dựa theo sư ph&oacute; &yacute; chỉ tới đ&acirc;y tuyển nhận một t&ecirc;n đệ tử.<br />\r\n<br />\r\n&quot;Tốt, đ&atilde; Tần Phấn n&oacute;i như vậy, kia nh&oacute;m ch&uacute;ng ta cũng kh&ocirc;ng kh&aacute;ch kh&iacute;!&quot;<br />\r\n<br />\r\nTrưởng l&atilde;o nh&oacute;m nghe n&oacute;i Tần Phấn n&oacute;i như vậy tự nhi&ecirc;n vui l&ograve;ng, bọn hắn bất qu&aacute; l&agrave; thuận miệng hỏi một ch&uacute;t, ai sẽ đem hạt giống tốt tặng cho ngọn n&uacute;i kh&aacute;c?<br />\r\n<br />\r\nTiếp lấy trưởng l&atilde;o nh&oacute;m liền trong đ&aacute;m người chọn lựa tự m&igrave;nh ngưỡng mộ trong l&ograve;ng đệ tử.<br />\r\n<br />\r\n&quot;Vương Bảo Kiện, Thi&ecirc;n Lai kiếm phong!&quot;<br />\r\n<br />\r\n&quot;L&yacute; Mặc Sinh, Tung kiếm phong!&quot;<br />\r\n<br />\r\n&quot;Trương Ngạnh, X&iacute;ch Luyện kiếm phong!&quot;<br />\r\n<br />\r\n&quot;L&yacute; Hữu Đảm, Bồ Đề kiếm phong!&quot;<br />\r\n<br />\r\n&quot;H&aacute;ch Sảng, Huyền Thiết kiếm phong!&quot;<br />\r\n<br />\r\n&quot;. . . .&quot;<br />\r\n<br />\r\nNghe tới t&ecirc;n của m&igrave;nh tại người chủ tr&igrave; trong miệng vang l&ecirc;n, coi như tại chen ch&uacute;c trong đ&aacute;m người n&ecirc;n đệ tử cũng kh&ocirc;ng khỏi nhảy cẫng hoan h&ocirc;.<br />\r\n<br />\r\nM&agrave; kh&ocirc;ng bị chọn được đệ tử cũng hiển lộ ra vẻ lo lắng.<br />\r\n<br />\r\nThượng Kiếm t&ocirc;ng điểm nội m&ocirc;n ngoại m&ocirc;n, nếu l&agrave; c&oacute; may mắn được trưởng l&atilde;o nh&igrave;n tr&uacute;ng thẳng v&agrave;o nội m&ocirc;n, kh&ocirc;ng chỉ c&oacute; thể miễn đi ngoại m&ocirc;n mệt nhọc nỗi khổ c&ograve;n c&oacute; thể thu hoạch được ngoại m&ocirc;n đệ tử kh&ocirc;ng thể lấy được tu luyện b&iacute; ph&aacute;p.<br />\r\n<br />\r\nM&agrave; nội m&ocirc;n nh&acirc;n số c&oacute; hạn, kh&ocirc;ng c&oacute; bị tuyển chọn chỉ c&oacute; thể đi ngoại m&ocirc;n từ l&uacute;c lẫn lộn bắt đầu, nếu l&agrave; tốc độ tu luyện c&ugrave;ng kh&ocirc;ng l&ecirc;n c&ograve;n c&oacute; bị r&otilde; r&agrave;ng lui phong hiểm!<br />\r\n<br />\r\nBị tuyển l&agrave;m nội m&ocirc;n đệ tử c&oacute; thể n&oacute;i l&agrave; theo tu luyện điểm xuất ph&aacute;t liền cao hơn người kh&aacute;c một đoạn!<br />\r\n<br />\r\nC&oacute; thể tiếp xuống một c&acirc;u, nhường ở đ&acirc;y c&aacute;c đệ tử to&agrave;n th&acirc;n nổi da g&agrave; l&ecirc;n.<br />\r\n<br />\r\n&quot;Tiếp xuống đến phi&ecirc;n Khai Thi&ecirc;n kiếm phong!&quot;<br />\r\n<br />\r\n&quot;Thỉnh Khai Thi&ecirc;n kiếm phong chọn lựa đệ tử!&quot;<br />\r\n<br />\r\nNgười chủ tr&igrave; vừa dứt lời, l&uacute;c trước c&ograve;n tại xao động đ&aacute;m người trong chớp mắt liền tất cả đều trở về đến y&ecirc;n tĩnh.</p>', '2021-12-01 15:17:33', 4, 1),
(321, 78, 'Thường Thường Không Có Gì Lạ Tiểu Sư Muội Là Thiên Đạo Kiếm Tượng', '3', '<p>&quot;Tiếp xuống đến phi&ecirc;n Khai Thi&ecirc;n kiếm phong!&quot;<br />\r\n<br />\r\n&quot;Thỉnh Khai Thi&ecirc;n kiếm phong chọn lựa đệ tử!&quot;<br />\r\n<br />\r\nY&ecirc;n tĩnh đ&aacute;m người đầu ti&ecirc;n l&agrave; dừng lại một l&aacute;t, sau đ&oacute; to&agrave;n vi&ecirc;n cũng trở n&ecirc;n cuồng hoan bắt đầu!<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; bị tuyển chọn đệ tử trong nh&aacute;y mắt liền trở n&ecirc;n mừng rỡ như đi&ecirc;n, đ&atilde; được tuyển chọn đệ tử mặt mũi tr&agrave;n đầy viết kh&ocirc;ng thể tin. Bọn hắn nhưng cho tới b&acirc;y giờ cũng chưa n&oacute;i qua năm nay Khai Thi&ecirc;n kiếm phong muốn nhận người a!<br />\r\n<br />\r\nLần n&agrave;y liền hối hận cũng kh&ocirc;ng c&oacute; m&ocirc;n lộ!<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y Khai Thi&ecirc;n kiếm phong danh vọng tại đệ tử b&ecirc;n trong thật đ&uacute;ng l&agrave; kh&ocirc;ng phải tầm thường a!&quot; Một vị trưởng l&atilde;o nh&igrave;n thấy tr&ecirc;n quảng trường ch&uacute;ng sinh lẫn nhau cũng mặt lộ vẻ đắng chát.<br />\r\n<br />\r\n&quot;Hừ, c&aacute;i n&agrave;y Khai Thi&ecirc;n kiếm phong c&oacute; g&igrave; đặc biệt hơn người. M&ocirc;n hạ của ta X&iacute;ch Luyện kiếm phong đệ tử c&ograve;n kh&ocirc;ng phải mỗi năm tranh đoạt hai vị tr&iacute; đầu, c&aacute;i n&agrave;o kh&ocirc;ng phải thi&ecirc;n ph&uacute; dị bẩm? So với hắn Khai Thi&ecirc;n kiếm phong ch&ecirc;nh lệch chỗ n&agrave;o!&quot; T&oacute;c đỏ trưởng l&atilde;o thong thả lại sức, lớn tiếng h&eacute;t l&ecirc;n.<br />\r\n<br />\r\n&quot;Ngươi nếu l&agrave; bản th&acirc;n kh&ocirc;ng c&oacute; thua cho tiểu Lục mấy trăm lần, vậy c&aacute;i n&agrave;y coi như c&oacute; ch&uacute;t thuyết ph&aacute;p. Chỉ tiếc c&oacute; &iacute;t người lớn tuổi như vậy c&ograve;n kh&ocirc;ng c&oacute; thắng nổi một lần, đ&aacute;ng tiếc, đ&aacute;ng tiếc a ~&quot; ngồi trong g&oacute;c một vị trưởng l&atilde;o thầm xoa xoa tổn hại một c&acirc;u, t&oacute;c đỏ trưởng l&atilde;o nghe n&oacute;i sau vốn l&agrave; đỏ bừng gương mặt trở n&ecirc;n c&agrave;ng th&ecirc;m đỏ nhuận.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng giống ta, ta tốt xấu cũng tại tiểu Lục trong tay thắng một phần ba chi&ecirc;u.&quot;<br />\r\n<br />\r\n&quot;Ha ha, vậy ta c&oacute; thể mạnh hơn ngươi nhiều. Ta thắng tiểu Lục nửa chi&ecirc;u!&quot;<br />\r\n<br />\r\n&quot;Ngươi! Ngươi! Tốt, lần sau m&ocirc;n hạ đệ tử của ta nh&igrave;n thấy ngươi Huyền Thiết kiếm phong đệ tử v&agrave;o chỗ chết đ&aacute;nh!&quot;<br />\r\n<br />\r\n&quot;Đến, đến, ta h&aacute; sợ ngươi sao?&quot;<br />\r\n<br />\r\nMấy c&aacute;i trưởng l&atilde;o nhao nhao l&agrave;m một đo&agrave;n, nhưng Tần Phấn kh&ocirc;ng rảnh b&acirc;̣n t&acirc;m những thứ n&agrave;y.<br />\r\n<br />\r\nHắn một mặt mơ hồ đi đến tr&ecirc;n đ&agrave;i, đ&aacute;nh gi&aacute; đ&ocirc;ng đảo đệ tử. Tại hắn trong mắt, căn bản nh&igrave;n kh&ocirc;ng ra ai mạnh ai yếu, ai c&oacute; thi&ecirc;n ph&uacute;. Ngươi nhường Tần Phấn một c&aacute;i Luyện Kh&iacute; cảnh tới l&agrave;m những việc n&agrave;y, kh&ocirc;ng phải l&agrave;m kh&oacute; người sao!<br />\r\n<br />\r\nC&oacute; thể sư ph&oacute; tại tr&ecirc;n thư y&ecirc;u cầu chỉ c&oacute; hai chữ: Người sống!<br />\r\n<br />\r\n&quot;Sư huynh r&acirc;́t đẹp trai! Sư huynh tuyển ta tuyển ta!&quot;<br />\r\n<br />\r\n&quot;Sư huynh, ng&agrave;i trường b&agrave;o tốt gợi cảm, c&oacute; thể hay kh&ocirc;ng để cho ta liếm một c&aacute;i!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;C&uacute;t đi, muốn liếm cũng l&agrave; ta đến liếm!&quot;<br />\r\n<br />\r\n&quot;Sư huynh, d&ugrave;ng kiếm quất ta! Van ngươi sư huynh!&quot;<br />\r\n<br />\r\n&quot;Khụ khụ, khụ khụ.&quot;<br />\r\n<br />\r\nNh&igrave;n xem đ&ocirc;ng đảo nh&atilde;n thần cuồng nhiệt đệ tử, Tần Phấn chỉ c&oacute; thể ho khan v&agrave;i tiếng ra hiệu đ&aacute;m người y&ecirc;n tĩnh.<br />\r\n<br />\r\nHắn d&ugrave;ng nh&atilde;n thần đảo qua mặt của mọi người lỗ, bị qu&eacute;t đến người đều ưỡn ngực ngẩng đầu nhận lấy Tần Phấn xem kỹ.<br />\r\n<br />\r\nKh&ocirc;ng ai kh&ocirc;ng chờ mong bị Khai Thi&ecirc;n kiếm phong thu l&agrave;m m&ocirc;n hạ, kia thế nhưng l&agrave; sống sờ sờ truyền thuyết a!<br />\r\n<br />\r\nAi kh&ocirc;ng muốn trở th&agrave;nh mới truyền thuyết?<br />\r\n<br />\r\nĐột nhi&ecirc;n, Tần Phấn thấy được một đ&ocirc;i lười biếng hai mắt!<br />\r\n<br />\r\n&quot;Chi&ecirc;u c&aacute;i qu&aacute; lợi hại, kh&ocirc;ng được. Vậy hắn t&ugrave;y tiện tu luyện hai ng&agrave;y liền vượt qua ta, đến thời điểm ta liền bại lộ tự m&igrave;nh l&agrave; c&acirc;y củi mục.&quot;<br />\r\n<br />\r\n&quot;Tuyển c&aacute;i qu&aacute; đần, cũng kh&ocirc;ng được. Kia đến thời điểm Khai Thi&ecirc;n kiếm phong mất mặt muốn n&eacute;m hai phần, sư ph&oacute; trở về c&ograve;n kh&ocirc;ng phải chặt ta.&quot;<br />\r\n<br />\r\n&quot;Đến c&ugrave;ng n&ecirc;n l&agrave;m c&aacute;i g&igrave; b&acirc;y giờ?&quot; Tần Phấn c&oacute; ch&uacute;t gi&atilde;y dụa, trọng &aacute;p ph&iacute;a dưới hắn căn bản kh&ocirc;ng l&agrave;m được lựa chọn tới.<br />\r\n<br />\r\nĐang xoắn xu&yacute;t suy nghĩ b&ecirc;n trong, Tần Phấn thấy được trong đ&aacute;m người một đ&ocirc;i mắt đang bối rối mười phần, tựa như lập tức liền phải ngủ lấy đồng dạng!<br />\r\n<br />\r\n&quot;Có th&ecirc;̉ tại loại thời khắc mấu chốt n&agrave;y ngủ g&agrave; ngủ gật, khẳng định kh&ocirc;ng phải c&aacute;i g&igrave; người th&ocirc;ng minh! M&agrave; lại lười cũng tốt, đến thời điểm c&ugrave;ng ta đồng dạng mỗi ng&agrave;y cũng n&uacute;p ở tr&ecirc;n n&uacute;i kh&ocirc;ng xuống, chắc chắn sẽ kh&ocirc;ng cho sư ph&oacute; mất mặt!&quot;<br />\r\n<br />\r\nTheo Tần Phấn &aacute;nh mắt thời gian d&agrave;i dừng lại, đ&aacute;m người cũng đều theo &aacute;nh mắt nh&igrave;n về ph&iacute;a đ&aacute;m người b&ecirc;n trong.<br />\r\n<br />\r\nM&agrave; con mắt chủ nh&acirc;n cũng ch&uacute; &yacute; tới Tần Phấn ngay tại nh&igrave;n ch&ograve;ng chọc v&agrave;o n&agrave;ng xem, hiển nhi&ecirc;n l&agrave; quyết định n&agrave;ng!<br />\r\n<br />\r\nSau một khắc, một đạo Thiến Ảnh từ trong đ&aacute;m người nhảy l&ecirc;n một c&aacute;i, xuất hiện ở Tần Phấn trước mặt.<br />\r\n<br />\r\nN&oacute;i ra thật xấu hổ, khi thấy nữ tử h&igrave;nh d&aacute;ng hiện ra ở trước mặt m&igrave;nh, Tần Phấn v&acirc;̣y mà nhất thời hồi l&acirc;u chuyển kh&ocirc;ng ra cặp mắt của m&igrave;nh.<br />\r\n<br />\r\nThiếu nữ gương mặt hơi c&oacute; vẻ non nớt, ước chừng mười bốn mười lăm tuổi bộ dạng.<br />\r\n<br />\r\nMột đầu mực m&aacute;i t&oacute;c đen d&agrave;i d&agrave;i tới phần eo, c&agrave;ng th&ecirc;m nổi bật l&ecirc;n lộ trong kh&ocirc;ng kh&iacute; da thịt kiều nộn trắng như tuyết.<br />\r\n<br />\r\nĐộng l&ograve;ng người gương mặt b&ecirc;n tr&ecirc;n đứng thẳng ti&ecirc;u ch&iacute; ngũ quan, vừa mới c&ograve;n hiển lười biếng đ&ocirc;i mắt đẹp giờ ph&uacute;t n&agrave;y tỉnh cả ngủ.<br />\r\n<br />\r\nTần Phấn c&oacute; thể thấy được n&agrave;ng đ&ocirc;i trong mắt như c&oacute; tinh thần lưu chuyển!<br />\r\n<br />\r\nThiếu nữ kh&ocirc;ng chỉ l&agrave; tướng mạo xuất ch&uacute;ng, m&agrave; lại kh&iacute; chất cũng l&agrave; tuyệt lu&acirc;n!<br />\r\n<br />\r\nTại Tần Phấn bần c&ugrave;ng từ trong biển chỉ c&oacute; thể hiện ra bốn chữ lớn!<br />\r\n<br />\r\nTuyệt đại phong hoa!<br />\r\n<br />\r\n&quot;Sư huynh, ta nghĩ b&aacute;i nhập Khai Thi&ecirc;n kiếm phong.&quot; Thiếu nữ h&aacute; miệng thanh &acirc;m chậm r&atilde;i ra, thật giống như Tiểu Kh&ecirc; tại giữa n&uacute;i non tr&ugrave;ng điệp lưu động l&uacute;c thanh &acirc;m, mang cho người ta một loại tự nhi&ecirc;n m&agrave; vậy h&agrave;i l&ograve;ng cảm gi&aacute;c.<br />\r\n<br />\r\n&quot;Ngạch, ngạch.&quot; Tần Phấn kịp phản ứng, g&atilde;i cằm của m&igrave;nh, &yacute; đồ che giấu tự m&igrave;nh vừa mới đ&igrave;nh trệ vận chuyển đại n&atilde;o.<br />\r\n<br />\r\n&quot;Nh&oacute;m ch&uacute;ng ta Khai Thi&ecirc;n kiếm phong một mạch thu đồ từ trước đến nay nghi&ecirc;m ngặt, cho tới b&acirc;y giờ đều chỉ tuyển nhận c&oacute; tư chất đồ đệ. Nếu l&agrave; ngươi muốn gia nhập Khai Thi&ecirc;n kiếm phong, tự nhi&ecirc;n muốn hiện ra ngươi chỗ hơn người, nếu kh&ocirc;ng th&igrave; rất kh&oacute; để cho ta sư ph&oacute; h&agrave;i l&ograve;ng.&quot;<br />\r\n<br />\r\nTần Phấn vắt hết &oacute;c mới d&ugrave;ng đầu bi&ecirc;n ra một đoạn như vậy lời n&oacute;i đến bảo hộ ch&iacute;nh m&igrave;nh Khai Thi&ecirc;n kiếm phong cao qu&yacute; cảm gi&aacute;c.<br />\r\n<br />\r\nCứ việc thiếu nữ xuất ch&uacute;ng h&igrave;nh dạng đ&atilde; đại kh&aacute;i ph&ugrave; hợp Khai Thi&ecirc;n kiếm phong y&ecirc;u cầu, nghĩ đến sư ph&oacute; nh&igrave;n thấy cũng l&agrave; sẽ rất h&agrave;i l&ograve;ng.<br />\r\n<br />\r\nNhưng m&igrave;nh cũng kh&ocirc;ng thể t&ugrave;y tiện liền chi&ecirc;u v&agrave;o m&ocirc;n hạ, ch&iacute; &iacute;t kh&ocirc;ng thể giống ch&iacute;nh hắn dạng n&agrave;y củi mục.<br />\r\n<br />\r\nM&agrave; lại nghĩ đến những đệ tử n&agrave;y đều l&agrave; vừa mới bước v&agrave;o con đường tu luy&ecirc;̣n người, sẽ kh&ocirc;ng c&oacute; đặc biệt kh&aacute;c người tồn tại. Cho d&ugrave; c&oacute;, vậy cũng bị ngọn n&uacute;i kh&aacute;c chọn lựa kh&ocirc;ng sai biệt lắm.<br />\r\n<br />\r\nCho n&ecirc;n lấy tự m&igrave;nh th&acirc;m ni&ecirc;n Luyện Kh&iacute; cảnh tr&igrave;nh độ, đ&egrave; &eacute;p đ&aacute;m th&aacute;i điểu n&agrave;y kh&ocirc;ng phải dễ như trở b&agrave;n tay?<br />\r\n<br />\r\n&quot;Ta biết r&otilde;, cho n&ecirc;n ta hi vọng c&oacute; thể c&ugrave;ng sư huynh hơi ch&uacute;t luận b&agrave;n, ch&iacute;nh chứng minh thực lực. Mong rằng sư huynh th&agrave;nh to&agrave;n.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nThiếu nữ thanh &acirc;m n&oacute;i chuyện kh&ocirc;ng nhanh kh&ocirc;ng chậm, lại l&agrave;m cho người c&oacute; thể cảm nhận được trong đ&oacute; ki&ecirc;n định.<br />\r\n<br />\r\n&quot;Tốt, kia nh&oacute;m ch&uacute;ng ta liền qua hai chi&ecirc;u?&quot;<br />\r\n<br />\r\nTần Phấn nghĩ đến đối phương chẳng qua l&agrave; vừa mới nhập m&ocirc;n th&aacute;i điểu, trong l&ograve;ng kh&ocirc;ng khỏi lo lắng c&agrave;ng nhiều một ch&uacute;t.<br />\r\n<br />\r\n&quot;A đ&uacute;ng, xin ngươi đừng c&oacute; chỗ giữ lại. Kh&ocirc;ng cần thẳng m&igrave;nh thể chất tu vi, tận ch&iacute;nh m&igrave;nh to&agrave;n lực ra chi&ecirc;u!&quot;<br />\r\n<br />\r\n&quot;M&atilde;ng liền xong việc. Y&ecirc;n t&acirc;m, sư huynh chịu nổi!&quot;<br />\r\n<br />\r\nV&igrave; để cho đối phương có th&ecirc;̉ thua xinh đẹp điểm, thuận tiện n&acirc;ng đỡ một c&aacute;i tự m&igrave;nh quang minh h&igrave;nh tượng, đ&atilde; xoay người sang chỗ kh&aacute;c Tần Phấn đặc biệt quay đầu hướng về ph&iacute;a thiếu nữ nhắc nhở n&oacute;i, đồng thời c&ograve;n trừng mắt nh&igrave;n.<br />\r\n<br />\r\nThiếu nữ nghe vậy, hai mắt run l&ecirc;n.<br />\r\n<br />\r\nN&agrave;ng kh&ocirc;ng nghĩ tới chỉ l&agrave; ngắn ngủi v&agrave;i lần, tự m&igrave;nh ẩn giấu thủ đoạn liền bị đối phương xem r&otilde; r&otilde; r&agrave;ng r&agrave;ng!<br />\r\n<br />\r\nXem ra sư huynh n&agrave;y c&ugrave;ng theo như đồn đại n&oacute;i nhất tr&iacute;! C&aacute;i n&agrave;y Khai Thi&ecirc;n kiếm phong x&aacute;c thực cao minh!<br />\r\n<br />\r\n&quot;Vậy liền thỉnh sư huynh chỉ gi&aacute;o!&quot;<br />\r\n<br />\r\nThiếu nữ cũng kh&ocirc;ng nghĩ nhiều nữa, trực tiếp r&uacute;t kiếm, đồng thời đem thể nội ch&acirc;n kh&iacute; đều giải ph&oacute;ng, tung khắp trong kh&ocirc;ng kh&iacute;.<br />\r\n<br />\r\nTho&aacute;ng chốc ở giữa, thiếu nữ to&agrave;n th&acirc;n tr&ecirc;n dưới t&aacute;ch ra v&ocirc; tận kiếm &yacute;.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave;? !&quot;<br />\r\n<br />\r\nNh&igrave;n thấy cảnh tượng n&agrave;y chư vị trưởng l&atilde;o to&agrave;n bộ đều kinh h&atilde;i, liền liền t&oacute;c đỏ trưởng l&atilde;o huyết hồng sắc mặt cũng biến th&agrave;nh c&oacute; ch&uacute;t t&aacute;i nhợt, hai tay k&iacute;ch động kh&ocirc;ng th&ocirc;i nắm lấy c&aacute;i ghế đem tay.<br />\r\n<br />\r\n&quot;Thi&ecirc;n Đạo Kiếm Tượng, Thi&ecirc;n Đạo Kiếm Tượng!&quot;</p>', '2021-12-01 15:17:53', 4, 1),
(322, 78, 'Trước Theo Làm Sư Muội Tâm Tính Bắt đầu Làm Lên', '4', '<p>&quot;Thi&ecirc;n Đạo Kiếm Tượng, Thi&ecirc;n Đạo Kiếm Tượng!&quot;<br />\r\n<br />\r\n&quot;Nếu như ta kh&ocirc;ng nh&igrave;n lầm, đ&acirc;y l&agrave; Thi&ecirc;n Đạo Kiếm Tượng?&quot;<br />\r\n<br />\r\nNgồi tại vị tr&iacute; trước trưởng l&atilde;o nh&oacute;m tất cả đều kh&ocirc;ng thể tin la l&ecirc;n, ho&agrave;n to&agrave;n kh&ocirc;ng để &yacute; tự m&igrave;nh ng&agrave;y xưa nghi&ecirc;m t&uacute;c h&igrave;nh tượng.<br />\r\n<br />\r\n&quot;Thi&ecirc;n đạo, kiếm, tượng?&quot; Đứng tại trưởng l&atilde;o nh&oacute;m b&ecirc;n cạnh đệ tử c&oacute; ch&uacute;t mơ hồ, trong l&uacute;c nhất thời kh&ocirc;ng qu&aacute; l&yacute; giải mấy chữ n&agrave;y h&agrave;m nghĩa.<br />\r\n<br />\r\nHắn nhập m&ocirc;n tu luyện nhiều năm c&ograve;n l&agrave; lần đầu ti&ecirc;n nghe được c&aacute;i từ n&agrave;y.<br />\r\n<br />\r\n&quot;Thi&ecirc;n Đạo Kiếm Tượng, đ&acirc;y l&agrave; l&atilde;o thi&ecirc;n thưởng cơm chỗ ban cho thể chất. B&igrave;nh thường chỉ tồn tại ở cổ kinh b&ecirc;n trong, kh&ocirc;ng nghĩ tới sinh thời ta v&acirc;̣y mà có th&ecirc;̉ tận mắt nh&igrave;n thấy.&quot;<br />\r\n<br />\r\n&quot;C&aacute;c ngươi b&acirc;y giờ tu vi c&ograve;n thấp, tự nhi&ecirc;n nh&igrave;n kh&ocirc;ng ra m&ocirc;n đạo g&igrave;. Nếu l&agrave; đến c&aacute;c ngươi Tần Phấn sư huynh tr&igrave;nh độ kia, tự nhi&ecirc;n l&agrave; đ&atilde; hiểu.&quot;<br />\r\n<br />\r\nTrong đ&oacute; một vị trưởng l&atilde;o chậm r&atilde;i mở miệng n&oacute;i ra, hắn hiện tại c&oacute; ch&uacute;t kh&ocirc;ng c&oacute; lực lượng, liền liền giải th&iacute;ch t&acirc;m t&igrave;nh cũng kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nVốn cũng kh&ocirc;ng nhiều sợi t&oacute;c m&agrave;u đen, tại thời khắc n&agrave;y cũng c&oacute; ch&uacute;t trắng bệch.<br />\r\n<br />\r\nC&ugrave;ng Thi&ecirc;n Đạo Kiếm Tượng thể chất so ra, tự m&igrave;nh l&uacute;c trước thu thi&ecirc;n t&agrave;i nh&oacute;m đệ tử ch&iacute;nh l&agrave; c&aacute;i rắm! Vẫn l&agrave; loại kia thanh &acirc;m cũng kh&ocirc;ng c&oacute; cái rắm!<br />\r\n<br />\r\nNếu c&oacute; thể nhận lấy đệ tử như vậy, sợ l&agrave; ban đ&ecirc;m đi ngủ đều ngủ kh&ocirc;ng tốt. Trong ng&agrave;y thường khẳng định th&iacute;ch hợp c&aacute;i bảo bối cung cấp nu&ocirc;i dưỡng! Muốn c&aacute;i g&igrave; cho c&aacute;i g&igrave;, trưởng l&atilde;o nh&oacute;m chỉ cầu đ&ecirc;́n có th&ecirc;̉ tr&ecirc;n người Thi&ecirc;n Đạo Kiếm Tượng lưu lại đắc &yacute; của m&igrave;nh kiếm kỹ.<br />\r\n<br />\r\nBởi v&igrave; Thi&ecirc;n Đạo Kiếm Tượng nhất định l&agrave; muốn trở th&agrave;nh tr&ecirc;n thế giới n&agrave;y kiếm kỹ đỉnh phong tồn tại!<br />\r\n<br />\r\nNh&igrave;n xem thiếu nữ chậm r&atilde;i r&uacute;t ra tế kiếm, tự th&acirc;n kh&iacute; tức phảng phất c&ugrave;ng kiếm hợp hai l&agrave; một, hai người giao h&ograve;a.<br />\r\n<br />\r\nT&oacute;c đỏ trưởng l&atilde;o xem hai mắt cũng thẳng, tại hắn trong mắt chỉ thấy một c&aacute;i tuyệt thế thần binh đang chậm r&atilde;i s&aacute;ng l&ecirc;n mũi kiếm.<br />\r\n<br />\r\n&quot;Bảo kiếm ra khỏi vỏ, thần th&aacute;i hiển thị r&otilde;. Tĩnh như nước sạch tr&agrave;n qua hồ nước thong dong thư gi&atilde;n, động th&igrave; c&ugrave;ng sườn đồi v&aacute;ch đ&aacute; sắc b&eacute;n. N&agrave;ng b&acirc;y giờ tu vi c&ograve;n thấp, c&ograve;n kh&ocirc;ng thể đem thể chất của m&igrave;nh ph&aacute;t triển đến cực hạn, cho d&ugrave; l&agrave; dạng n&agrave;y, tại t&acirc;n sinh b&ecirc;n trong cũng l&agrave; độc nhất đương tồn tại!&quot; Mặt kh&aacute;c mấy c&aacute;i trưởng l&atilde;o nh&oacute;m cũng c&oacute; ch&uacute;t thất &yacute;, c&aacute;i n&agrave;y thế nhưng l&agrave; cổ điển b&ecirc;n trong mới tồn tại truyền thuyết thể chất a! M&agrave; lại đang ở trước mắt, tự m&igrave;nh vậy m&agrave; tại bất tri bất gi&aacute;c b&ecirc;n trong bỏ qua tốt như vậy người kế tục, c&ograve;n tặng cho Khai Thi&ecirc;n kiếm phong một mạch, nhường bọn hắn thật sự l&agrave; kh&ocirc;ng phục a!<br />\r\n<br />\r\nC&oacute; dạng n&agrave;y đồ đệ, đừng bảo l&agrave; cực hạn tại Thượng Kiếm t&ocirc;ng b&ecirc;n trong, ở thế giới dương danh cũng dễ như trở b&agrave;n tay.<br />\r\n<br />\r\nChỉ l&agrave; trong l&ograve;ng bọn họ cũng c&oacute; ch&uacute;t nghi hoặc, tại thiếu nữ kh&ocirc;ng c&oacute; thi triển ra ch&acirc;n kh&iacute; trước, Tần Phấn đến c&ugrave;ng l&agrave; thế n&agrave;o nh&igrave;n ra tr&ecirc;n người n&agrave;ng cất giấu thủ đoạn?<br />\r\n<br />\r\nChẳng lẽ n&oacute;i hắn cũng kế thừa đến tiểu Lục độc kia cay nh&atilde;n quang? !<br />\r\n<br />\r\nM&agrave; đương sự nh&acirc;n chi một Tần Phấn chậm ung dung r&uacute;t kiếm ra, trong l&ograve;ng đ&atilde; trong bụng nở hoa.<br />\r\n<br />\r\nTheo hắn trong mắt chỉ c&oacute; thấy được một điểm: Thiếu nữ to&agrave;n th&acirc;n tr&ecirc;n dưới liền một tia ch&acirc;n kh&iacute; cũng ngưng tụ kh&ocirc;ng ra, cho ăn bể bụng cũng liền một c&aacute;i Luyện Kh&iacute; trung giai tr&igrave;nh độ!</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Đồ ăn, thức ăn ngon!&quot;<br />\r\n<br />\r\n&quot;Tiểu sư muội to&agrave;n th&acirc;n tr&ecirc;n dưới nh&igrave;n kh&ocirc;ng ra một tia ch&acirc;n kh&iacute;. Quả nhi&ecirc;n l&agrave; vẻ mặt gi&aacute; trị lớn hơn thực lực, xem sư huynh ta l&agrave;m sao hảo hảo gi&aacute;o dục ngươi một phen!&quot;<br />\r\n<br />\r\n&quot;Ch&iacute;nh l&agrave; kh&ocirc;ng biết r&otilde; v&igrave; c&aacute;i g&igrave; h&ocirc;m nay gi&oacute; c&oacute; chút lớn, qu&aacute;t ta c&oacute; ch&uacute;t cho&aacute;ng.&quot; Tần Phấn c&oacute; ch&uacute;t buồn bực nh&igrave;n xem sư muội, kh&ocirc;ng biết r&otilde; c&oacute; phải l&agrave; ảo gi&aacute;c của hắn hay kh&ocirc;ng, chỉ cảm thấy h&ocirc;m nay Thanh Phong đường gi&oacute; c&oacute; chút lớn, thổi đến con mắt mười điểm ngứa.<br />\r\n<br />\r\n&quot;Sư muội, trước a &quot;<br />\r\n<br />\r\nTần Phấn d&ugrave;ng trống kh&ocirc;ng tay tr&aacute;i ra hiệu sư muội trước, đối phương cũng kh&ocirc;ng kh&aacute;ch kh&iacute;.<br />\r\n<br />\r\nSau một khắc, sư muội th&acirc;n ảnh biến mất tại Tần Phấn trong tầm mắt.<br />\r\n<br />\r\n&quot;Đang!&quot;<br />\r\n<br />\r\nMột đạo tiếng vang lanh lảnh vang l&ecirc;n, ch&iacute;nh l&agrave; hai người mũi kiếm đụng v&agrave;o nhau đưa tới thanh &acirc;m!<br />\r\n<br />\r\n&quot;Đang!&quot;, &quot;Đang!&quot;, &quot;Đang!&quot;, &quot;Đang!&quot;<br />\r\n<br />\r\nTiếng vang một c&aacute;i tiếp lấy một c&aacute;i li&ecirc;n mi&ecirc;n bất tuyệt, giữa hai người động t&aacute;c kh&ocirc;ng dừng lại ch&uacute;t n&agrave;o.<br />\r\n<br />\r\nCả hai giống như l&agrave; trước đ&oacute; thương lượng xong, diễn tập qua, một chi&ecirc;u một thức cũng ho&agrave;n to&agrave;n dựa theo dự định s&aacute;o lộ đi!<br />\r\n<br />\r\nC&ocirc;ng c&ugrave;ng ph&ograve;ng ho&agrave;n to&agrave;n h&ograve;a l&agrave;m một thể!<br />\r\n<br />\r\nTại đệ tử trong mắt xem ra, chỉ l&agrave; hai đạo hư ảnh tại giao thủ.<br />\r\n<br />\r\nLấy bọn hắn tu vi, muốn nh&igrave;n r&otilde; r&agrave;ng một chi&ecirc;u một thức cũng c&oacute; vẻ hơi ph&iacute; sức!<br />\r\n<br />\r\nM&agrave; lại huy động lưỡi kiếm l&uacute;c mang theo tiếng x&eacute; gi&oacute;, hai kiếm chạm nhau l&uacute;c sinh ra tiếng kiếm reo đều để chiến đấu quan s&aacute;t độ kh&oacute; cao hơn một tầng!<br />\r\n<br />\r\nTrưởng l&atilde;o biểu lộ cũng chầm chậm kh&ocirc;i phục th&agrave;nh l&uacute;c trước nghi&ecirc;m t&uacute;c biểu lộ.<br />\r\n<br />\r\nCứ việc mới nhập m&ocirc;n nh&oacute;m đệ tử xem kh&ocirc;ng r&otilde; r&agrave;ng, nhưng l&agrave;m trưởng l&atilde;o bọn hắn lại đem trận chiến đấu n&agrave;y xem r&otilde; r&agrave;ng!<br />\r\n<br />\r\nGiữa hai b&ecirc;n chiến đấu ho&agrave;n to&agrave;n kh&ocirc;ng tại một c&aacute;i đẳng cấp l&ecirc;n!<br />\r\n<br />\r\nCho thấy tr&ecirc;n xem thiếu nữ chiếm hết ti&ecirc;n cơ, li&ecirc;n tục kh&ocirc;ng ngừng ph&aacute;t động c&ocirc;ng k&iacute;ch, Tần Phấn chỉ c&oacute; thể bị &eacute;p l&agrave;m ph&ograve;ng thủ tư th&aacute;i.<br />\r\n<br />\r\nC&oacute; thể Tần Phấn thậm ch&iacute; cũng kh&ocirc;ng c&oacute; đụng tới ch&acirc;n kh&iacute;, chỉ d&ugrave;ng phổ th&ocirc;ng kiếm thuật liền đem n&oacute; h&oacute;a giải, đ&acirc;y chỉ c&oacute; một loại khả năng.<br />\r\n<br />\r\nĐ&oacute; ch&iacute;nh l&agrave; hắn ho&agrave;n to&agrave;n xem thấu đối phương chi&ecirc;u thức s&aacute;o lộ, hắn c&oacute; l&ograve;ng tin lấy nhỏ nhất lực đạo đến h&oacute;a giải đối phương to&agrave;n lực c&ocirc;ng k&iacute;ch!<br />\r\n<br />\r\nDạng n&agrave;y liền sẽ tại giữa hai b&ecirc;n h&igrave;nh th&agrave;nh một c&aacute;i mười r&otilde; r&agrave;ng lộ vẻ thể lực ch&ecirc;nh lệch, cuối c&ugrave;ng sẽ trở th&agrave;nh ảnh hưởng chiến đấu mấu chốt tay!<br />\r\n<br />\r\nM&agrave; lại Tần Phấn trong chiến đấu kịch liệt, kh&oacute;e miệng c&ograve;n c&oacute; thể mang theo như c&oacute; như kh&ocirc;ng nụ cười, dường như đ&ugrave;a cợt dường như h&agrave;i l&ograve;ng. Thiếu nữ sau khi thấy trong l&ograve;ng kh&ocirc;ng khỏi c&agrave;ng th&ecirc;m sốt ruột, chi&ecirc;u thức cũng d&ugrave;ng c&agrave;ng kịch liệt hơn n&oacute;ng nảy.<br />\r\n<br />\r\nC&aacute;i n&agrave;y kh&ocirc;ng chỉ l&agrave; vật l&yacute; tr&ecirc;n &yacute; nghĩa chiến đấu, vẫn l&agrave; t&acirc;m t&iacute;nh tr&ecirc;n đọ sức!<br />\r\n<br />\r\n&quot;Kh&ocirc;ng nghĩ tới a kh&ocirc;ng nghĩ tới, vốn cho rằng Thi&ecirc;n Đạo Kiếm Tượng thể đ&atilde; l&agrave; kỳ t&agrave;i ng&uacute;t trời, c&oacute; thể c&aacute;i n&agrave;y Tần Phấn cũng l&agrave; như thế!&quot;<br />\r\n<br />\r\n&quot;Thi&ecirc;n Đạo Kiếm Tượng ch&iacute;nh l&agrave; lấy ch&acirc;n kh&iacute; đổi lại kiếm thuật, sở trường kiếm thuật thể chất đặc th&ugrave;. Hắn sở dụng kiếm chi&ecirc;u tức l&agrave; thi&ecirc;n đạo biến th&agrave;nh, sở dụng kiếm ph&aacute;p ch&iacute;nh l&agrave; kiếm thuật bản chất! Cứ việc chỉ c&oacute; Luyện Kh&iacute; trung giai ti&ecirc;u chuẩn, nhưng lấy n&agrave;ng c&oacute; khả năng biểu hiện ra chiến lực thậm ch&iacute; kh&ocirc;ng thua g&igrave; Ngưng Kh&iacute; trung giai!&quot;<br />\r\n<br />\r\n&quot;Liền liền dạng n&agrave;y kiếm thuật cũng bị Tần Phấn &aacute;p chế, hắn thậm ch&iacute; v&ocirc; dụng vận dụng một tia ch&acirc;n kh&iacute;!&quot;<br />\r\n<br />\r\nT&oacute;c đỏ trưởng l&atilde;o tr&ecirc;n mặt nghi&ecirc;m t&uacute;c, l&uacute;c trước đối Tần Phấn th&agrave;nh kiến cũng ho&agrave;n to&agrave;n v&ocirc; tung. C&ograve;n lại mấy vị trưởng l&atilde;o cũng đều l&agrave; t&aacute;n thưởng chi &yacute;.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y tiểu tử x&aacute;c thực thi&ecirc;n t&agrave;i , dựa theo biểu hiện đến xem sợ l&agrave; x&aacute;c thực như l&agrave; nghe đồn đồng dạng đột ph&aacute; Kết Đan cảnh. Đ&aacute;ng tiếc tự m&igrave;nh sư ph&oacute; cả ng&agrave;y kh&ocirc;ng tại ngọn n&uacute;i b&ecirc;n tr&ecirc;n, kh&ocirc;ng người chỉ điểm. Nếu l&agrave; b&aacute;i nhập X&iacute;ch Luyện kiếm phong, ta c&oacute; l&ograve;ng tin đem dạy dỗ th&agrave;nh Th&ocirc;ng Thể, kh&ocirc;ng, Tụ Linh cảnh!&quot;<br />\r\n<br />\r\nTrưởng l&atilde;o nh&oacute;m kh&ocirc;ng n&oacute;i th&ecirc;m g&igrave; nữa, chỉ l&agrave; chuy&ecirc;n t&acirc;m ch&uacute; &yacute; trước mắt chiến đấu. Bọn hắn cũng rất t&ograve; m&ograve;, c&aacute;i n&agrave;y đệ tử thi&ecirc;n t&agrave;i đối đ&acirc;̀u thi&ecirc;n t&agrave;i thể chất n&ecirc;n như thế n&agrave;o kết th&uacute;c c&ocirc;ng việc.<br />\r\n<br />\r\n&quot;Ai u, tiểu sư muội n&agrave;y động t&aacute;c c&oacute; chút nhanh a.&quot;<br />\r\n<br />\r\n&quot;Bất qu&aacute; hết thảy đều ở trong l&ograve;ng b&agrave;n tay, thật sự l&agrave; qu&aacute; đơn giản!&quot;<br />\r\n<br />\r\nTần Phấn kh&ocirc;ng biết trong l&ograve;ng mọi người nghĩ c&oacute; nhiều như vậy, tr&ecirc;n tay kiếm chỉ l&agrave; đang kh&ocirc;ng ngừng ph&aacute; chi&ecirc;u, đồng thời kh&oacute;e miệng khống chế kh&ocirc;ng nổi lộ ra mỉm cười.<br />\r\n<br />\r\nTại hắn g&oacute;c nh&igrave;n xem ra, tiểu sư muội động t&aacute;c mười điểm mau lẹ, nhưng ở tự m&igrave;nh nh&igrave;n r&otilde; +99 bảng tăng th&ecirc;m ph&iacute;a dưới vẫn l&agrave; kh&ocirc;ng chỗ che th&acirc;n, một chi&ecirc;u một thức cũng bị nh&igrave;n r&otilde; r&agrave;ng.<br />\r\n<br />\r\nCho n&ecirc;n tự nhi&ecirc;n m&agrave; vậy bị hắn cản lại, chỉ l&agrave; đối phương đi&ecirc;n cuồng tiến c&ocirc;ng quả thật l&agrave;m cho chỉ d&ugrave;ng phổ th&ocirc;ng kiếm thuật hắn kh&ocirc;ng t&igrave;m được phản c&ocirc;ng cơ hội.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng hổ l&agrave; Luyện Kh&iacute; trung giai, c&ugrave;ng ta Luyện Kh&iacute; đỉnh phong ở giữa ch&ecirc;nh lệch thật sự l&agrave; r&otilde; r&agrave;ng kh&ocirc;ng thể lại r&otilde; r&agrave;ng!&quot;<br />\r\n<br />\r\nLại l&agrave; li&ecirc;n tục mười c&aacute;i chi&ecirc;u thức nhanh ch&oacute;ng trao đổi, Tần Phấn có th&ecirc;̉ r&otilde; r&agrave;ng nh&igrave;n thấy tiểu sư muội mồ h&ocirc;i tr&aacute;n, m&agrave; hắn lại cảm gi&aacute;c kh&ocirc;ng chịu được một tia mỏi mệt.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nMột loại cảm gi&aacute;c ưu việt hiện l&ecirc;n ở Tần Phấn trong l&ograve;ng.<br />\r\n<br />\r\nL&agrave;m một t&ecirc;n ba năm, a kh&ocirc;ng, th&acirc;m ni&ecirc;n Luyện Kh&iacute; cảnh tu luyện nh&acirc;n sĩ, Tần Phấn tự nhận tự m&igrave;nh có th&ecirc;̉ đối Luyện Kh&iacute; cảnh tạo th&agrave;nh m&atilde;nh liệt h&agrave;ng duy đả k&iacute;ch, chỉ c&oacute; thể n&oacute;i kh&ocirc;ng ra hắn sở liệu, c&ugrave;ng tiểu sư muội luận b&agrave;n ho&agrave;n to&agrave;n kh&ocirc;ng cần vận dụng ch&acirc;n kh&iacute;, chỉ l&agrave; phổ th&ocirc;ng kiếm thuật đều c&oacute; thể t&ugrave;y tiện cầm xuống!<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; dạng g&igrave; qu&aacute;i vật! V&igrave; c&aacute;i g&igrave; ta mỗi một chi&ecirc;u một thức cũng bị hắn d&ugrave;ng nhỏ nhất động t&aacute;c bi&ecirc;n độ cản lại! R&otilde; r&agrave;ng ta đ&atilde; đem tốc độ ph&aacute;t triển đến cực hạn!&quot;<br />\r\n<br />\r\nM&agrave; ở trong mắt Nhan Thanh H&agrave;n, chung quanh trưởng l&atilde;o, đệ tử rất về phần mặt đất cũng đ&atilde; đều biến mất, lưu lại xuống một mảnh hắc &aacute;m.<br />\r\n<br />\r\nN&agrave;ng chỉ c&oacute; thể nh&igrave;n thấy Tần Phấn th&acirc;n ảnh trở n&ecirc;n c&agrave;ng th&ecirc;m cao lớn, kinh khủng bắt đầu.<br />\r\n<br />\r\nNhan Thanh H&agrave;n đ&atilde; lấy hết to&agrave;n lực của m&igrave;nh thi triển kiếm quyết, nhưng v&ocirc; luận l&agrave; d&ugrave;ng dạng g&igrave; kỹ xảo vẫn l&agrave; theo bất luận c&aacute;i g&igrave; g&oacute;c độ ph&aacute;t khởi tiến c&ocirc;ng, tất cả đều bị Tần Phấn cho tr&ocirc;ng xuống tới! Đừng bảo l&agrave; ph&aacute; Tần Phấn chi&ecirc;u, tự m&igrave;nh tại li&ecirc;n tiếp c&ocirc;ng k&iacute;ch đến đ&atilde; trước kh&ocirc;ng chịu nổi.<br />\r\n<br />\r\nVề t&acirc;m l&yacute; c&agrave;ng th&ecirc;m đả k&iacute;ch Nhan Thanh H&agrave;n ch&iacute;nh l&agrave;, đối phương thậm ch&iacute; ngay cả ch&acirc;n kh&iacute; đều kh&ocirc;ng d&ugrave;ng! Chỉ l&agrave; dựa v&agrave;o th&acirc;n thể liền đem tự m&igrave;nh ph&ograve;ng như thế ngh&egrave;o t&uacute;ng. C&aacute;i n&agrave;y tương đương với che mắt c&ugrave;ng m&igrave;nh đ&aacute;nh, coi như đối phương l&agrave; tuyệt thế thi&ecirc;n t&agrave;i, vậy c&aacute;i n&agrave;y cũng qu&aacute; tổn thương tự m&igrave;nh l&ograve;ng tự trọng!<br />\r\n<br />\r\nM&agrave; lại c&aacute;i n&agrave;y kh&oacute;e miệng nụ cười l&agrave; chuyện g&igrave; xảy ra?<br />\r\n<br />\r\nL&agrave; đang b&agrave;y tỏ tự m&igrave;nh qu&aacute; c&ugrave;i bắp sao?<br />\r\n<br />\r\nVẫn l&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; đem tự m&igrave;nh để v&agrave;o mắt?<br />\r\n<br />\r\nNhan Thanh H&agrave;n c&agrave;ng th&ecirc;m sốt ruột, kiếm trong tay cũng chầm chậm đ&atilde; mất đi ổn định, c&agrave;ng về sau chỉ c&ograve;n lại c&oacute; lung tung bay m&uacute;a.<br />\r\n<br />\r\nChi&ecirc;u thức tự nhi&ecirc;n cũng kh&ocirc;ng c&aacute;ch n&agrave;o ho&agrave;n to&agrave;n thi triển, tại dạng n&agrave;y t&igrave;nh huống dưới, n&agrave;ng cho thấy một c&aacute;i kẽ hở khổng lồ, bị Tần Phấn nh&igrave;n r&otilde; r&agrave;ng.<br />\r\n<br />\r\nTần Phấn đầu ti&ecirc;n l&agrave; nghi&ecirc;ng người chống chọi kho&aacute;i kiếm, sau đ&oacute; tho&aacute;ng d&ugrave;ng sức liền dễ như trở b&agrave;n tay đ&aacute;nh trật tiểu sư muội mũi kiếm.<br />\r\n<br />\r\nTiếp lấy thuận thế trượt ch&iacute; kiếm th&acirc;n tr&uacute;ng bộ chỗ, tay phải đột nhi&ecirc;n lực bộc ph&aacute;t lượng, mũi kiếm từ dưới hướng l&ecirc;n, chọn phong tr&ecirc;n k&iacute;ch.<br />\r\n<br />\r\nVốn l&agrave; hiển thị r&otilde; vẻ mệt mỏi Nhan Thanh H&agrave;n đương nhi&ecirc;n ngăn kh&ocirc;ng được một k&iacute;ch n&agrave;y, cứ việc n&agrave;ng l&uacute;c trước c&oacute; chỗ b&aacute;o động trước nắm chặt chu&ocirc;i kiếm.<br />\r\n<br />\r\nNhưng trường kiếm vẫn l&agrave; bị thẳng tắp bị đ&aacute;nh bay ra ngo&agrave;i!<br />\r\n<br />\r\nTo&agrave;n bộ Thanh Phong đường ho&agrave;n to&agrave;n y&ecirc;n tĩnh im ắng.</p>', '2021-12-01 15:18:18', 4, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(323, 78, 'Ai Ta Lại Làm Ngươi Một Tay Tâm Tính', '5', '<p>D&ugrave; cho kh&ocirc;ng thể n&agrave;o hiểu được qu&aacute; tr&igrave;nh chiến đấu, nhưng khi mỗi c&aacute;i đệ tử nh&igrave;n thấy cắm tr&ecirc;n mặt đất c&ograve;n tại lắc lư kiếm l&uacute;c, tất cả mọi người r&otilde; r&agrave;ng một ch&uacute;t:<br />\r\n<br />\r\nTiểu sư muội bại!<br />\r\n<br />\r\nM&agrave; trưởng l&atilde;o nh&oacute;m tr&ecirc;n mặt cứ việc giữ vững b&igrave;nh tĩnh, nhưng nội t&acirc;m sớm đ&atilde; s&oacute;ng lớn m&atilde;nh liệt.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y, c&aacute;i n&agrave;y xong?&quot;<br />\r\n<br />\r\nMột vị trưởng l&atilde;o kh&ocirc;ng d&aacute;m tin hỏi, tại trong mắt của bọn hắn xem ra trận chiến đấu n&agrave;y ở giữa ch&ecirc;nh lệch thật sự l&agrave; qu&aacute; mức r&otilde; r&agrave;ng!<br />\r\n<br />\r\nCh&iacute;nh l&agrave; đơn phương kịch đ&ugrave;a nghịch th&ocirc;i!<br />\r\n<br />\r\nTần Phấn tựa như l&agrave; đại nh&acirc;n đ&ugrave;a tiểu h&agrave;i, vẻn vẹn chỉ d&ugrave;ng một chi&ecirc;u, liền đ&aacute;nh Thi&ecirc;n Đạo Kiếm Tượng quăng kiếm lạc bại!<br />\r\n<br />\r\nM&agrave; lại l&agrave; như thế thường thường kh&ocirc;ng c&oacute; g&igrave; lạ, ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; gợn s&oacute;ng một chi&ecirc;u!<br />\r\n<br />\r\nKiếm chi thi&ecirc;n t&agrave;i bại ho&agrave;n to&agrave;n tại chư vị trưởng l&atilde;o trong dự liệu, nhưng bọn hắn kh&ocirc;ng nghĩ tới vậy m&agrave; lại l&agrave; như thế n&agrave;y đơn phương nghiền &eacute;p kết cục.<br />\r\n<br />\r\nNhan Thanh H&agrave;n nh&igrave;n về ph&iacute;a bị đ&aacute;nh bay kiếm một mặt thất &yacute;, mồ h&ocirc;i tr&ecirc;n tr&aacute;n l&agrave;m rối loạn m&aacute;i t&oacute;c, lung tung d&aacute;n tr&ecirc;n mặt.<br />\r\n<br />\r\nVừa mới c&ograve;n nắm chặt chu&ocirc;i kiếm tay phải l&uacute;c n&agrave;y bởi v&igrave; kiệt lực c&ograve;n tại c&oacute; ch&uacute;t run rẩy.<br />\r\n<br />\r\nTần Phấn vừa mới bắt đầu trong l&ograve;ng c&ograve;n tại mừng thầm, bởi v&igrave;, tự m&igrave;nh ở trước mặt mọi người biểu hiện c&oacute; thể xưng ho&agrave;n mỹ!<br />\r\n<br />\r\nTheo chiến đấu thưởng thức t&iacute;nh, tr&igrave;nh độ kịch liệt c&ugrave;ng cuối c&ugrave;ng xinh đẹp kết th&uacute;c c&ocirc;ng việc tất cả đều tại trong l&ograve;ng b&agrave;n tay của m&igrave;nh.<br />\r\n<br />\r\nKh&ocirc;ng hề nghi ngờ, Tần Phấn kh&ocirc;ng c&oacute; l&agrave;m mất mặt sư ph&oacute;, duy tr&igrave; được Khai Thi&ecirc;n kiếm phong cao đại thượng h&igrave;nh tượng.<br />\r\n<br />\r\nNhưng khi hắn nh&igrave;n thấy tiểu sư muội bởi v&igrave; thất bại sinh l&ograve;ng m&ecirc; mang bộ dạng l&uacute;c lại cảm thấy đến kh&ocirc;ng đ&agrave;nh l&ograve;ng.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; biện ph&aacute;p, đ&acirc;y l&agrave; ch&ecirc;nh lệch về cảnh giới!<br />\r\n<br />\r\nNhưng b&acirc;y giờ cảm gi&aacute;c hưng phấn r&uacute;t đi Tần Phấn đột nhi&ecirc;n cảm thấy tự m&igrave;nh l&agrave;m c&oacute; ch&uacute;t qu&aacute; mức.<br />\r\n<br />\r\nTự m&igrave;nh ra tay c&oacute; phải hay kh&ocirc;ng qu&aacute; nặng đi điểm? Vạn nhất đả k&iacute;ch đến tiểu sư muội l&ograve;ng tự tin n&ecirc;n l&agrave;m c&aacute;i g&igrave;? Tự m&igrave;nh hẳn l&agrave; hiện ra điểm sư huynh kh&iacute; độ?<br />\r\n<br />\r\nC&oacute; phải hay kh&ocirc;ng n&ecirc;n biểu hiện c&oacute; chút ph&iacute; sức, hoặc l&agrave; n&oacute;i n&ecirc;n d&ugrave;ng ch&acirc;n kh&iacute; đ&ugrave;a nghịch hai chi&ecirc;u lừa gạt lừa gạt?<br />\r\n<br />\r\nTần Phấn vỗ &oacute;t một c&aacute;i, đột nhi&ecirc;n nghĩ đến.<br />\r\n<br />\r\n&quot;Đ&uacute;ng a, sư muội khẳng định c&ograve;n c&oacute; &aacute;t chủ b&agrave;i ở tr&ecirc;n người. Ai kh&ocirc;ng biết ở tr&ecirc;n người giấu nhiều đồ đ&acirc;u?&quot;<br />\r\n<br />\r\nCứ việc tiểu sư muội biểu hiện đồ ăn, liền ch&acirc;n kh&iacute; cũng l&agrave;m kh&ocirc;ng đến, nhưng khẳng định c&oacute; giữ lại!<br />\r\n<br />\r\nLuyện Kh&iacute; trung giai nhất định c&oacute; thể sử xuất một chi&ecirc;u kia nửa thức ch&acirc;n kh&iacute;, mặc d&ugrave; kh&ocirc;ng nhiều, nhưng khẳng định c&oacute; thể c&oacute;!<br />\r\n<br />\r\nNghĩ đến c&aacute;i n&agrave;y, Tần Phấn t&acirc;m l&yacute; nắm chắc.<br />\r\n<br />\r\nChỉ cần m&igrave;nh nhường tiểu sư muội lại biểu hiện một chi&ecirc;u, đến l&uacute;c đ&oacute; chỉ cần giả bộ c&oacute; chút ph&iacute; sức l&agrave; được!<br />\r\n<br />\r\nNhư thế mặt mũi của m&igrave;nh sẽ kh&ocirc;ng n&eacute;m, tiểu sư muội l&ograve;ng tự tin cũng sẽ kh&ocirc;ng đ&aacute;nh đ&aacute;nh tới.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Tần Phấn a Tần Phấn, ngươi kh&ocirc;ng hổ l&agrave; thi&ecirc;n t&agrave;i a! L&agrave;m sao lại như thế thi&ecirc;n t&agrave;i!&quot; Tần Phấn c&agrave;ng ph&aacute;t đối &yacute; nghĩ n&agrave;y cảm thấy h&agrave;i l&ograve;ng, liền chủ động mở miệng.<br />\r\n<br />\r\n&quot;Tiểu sư muội a, ngươi tu luyện thời gian ngắn ngủi, đ&aacute;nh kh&ocirc;ng lại ta l&agrave; chuyện rất b&igrave;nh thường, chớ c&oacute; để ở trong l&ograve;ng.&quot;<br />\r\n<br />\r\nNghe n&oacute;i Tần Phấn mở miệng, Nhan Thanh H&agrave;n m&ecirc; mang giơ l&ecirc;n &aacute;nh mắt, c&ugrave;ng hắn đối vừa vặn.<br />\r\n<br />\r\nNh&igrave;n qua tiểu sư muội hai mắt b&ecirc;n trong v&ocirc; thần &aacute;nh mắt, Tần Phấn cảm gi&aacute;c buồng tim của m&igrave;nh c&oacute; ch&uacute;t bị đ&acirc;m đau nhức.<br />\r\n<br />\r\nVừa mới hai người chỉ lo tự m&igrave;nh sướng rồi, đúng là kh&ocirc;ng phải người! Ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; c&acirc;n nhắc đến trước mặt mọi người nữ h&agrave;i tử t&acirc;m t&igrave;nh!<br />\r\n<br />\r\n&quot;Theo ta quan s&aacute;t, chắc hẳn ngươi nhất định c&ograve;n c&oacute; lưu c&aacute;i một chi&ecirc;u nửa thức &aacute;t chủ b&agrave;i. To&agrave;n bộ d&ugrave;ng đến a!&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng cần che che lấp lấp, đem hết thảy tất cả đều hiện ra cho sư huynh nh&igrave;n xem. Để cho ta nh&igrave;n xem to&agrave;n lực ngươi đủ tư c&aacute;ch hay kh&ocirc;ng!&quot;<br />\r\n<br />\r\nHiền l&agrave;nh trưởng l&atilde;o nghe vậy nh&iacute;u m&agrave;y, một c&aacute;i kh&aacute;c đầu trọc trưởng l&atilde;o tự lẩm bẩm: &quot;Vẫn c&oacute; một chi&ecirc;u, c&ograve;n c&oacute; một chi&ecirc;u. Sư huynh, kh&ocirc;ng phải l&agrave;?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng sai, ch&iacute;nh l&agrave; ngươi ta suy nghĩ. L&agrave; Thượng Kiếm t&ocirc;ng đời thứ nhất t&ocirc;ng chủ một chi&ecirc;u cuối c&ugrave;ng cũng l&agrave; một chi&ecirc;u mạnh nhất.&quot;<br />\r\n<br />\r\n&quot;Trong truyền thuyết kh&ocirc;ng ai c&oacute; thể đ&oacute;n lấy một kiếm n&agrave;y.&quot;<br />\r\n<br />\r\nNhan Thanh H&agrave;n nghe n&oacute;i Tần Phấn lời n&oacute;i c&oacute; ch&uacute;t chấn kinh, tự m&igrave;nh quả thật c&ograve;n c&oacute; lưu một chi&ecirc;u, thật kh&ocirc;ng nghĩ đến bị đối phương xem như thế thấu triệt.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; tự m&igrave;nh trời sinh sở ngộ mạnh nhất một kiếm, l&agrave; theo xuất sinh l&ecirc;n liền khắc trong t&acirc;m khảm chi&ecirc;u thức.<br />\r\n<br />\r\nC&oacute; thể tự m&igrave;nh cho tới b&acirc;y giờ cũng kh&ocirc;ng c&oacute; đối người kh&aacute;c d&ugrave;ng qua, bởi v&igrave; n&agrave;ng cảm gi&aacute;c kh&ocirc;ng ai c&oacute; thể đ&oacute;n lấy một chi&ecirc;u n&agrave;y.<br />\r\n<br />\r\n&quot;Sư huynh, ngươi. . . Ngươi nhất định phải đ&oacute;n lấy một kiếm n&agrave;y?&quot;<br />\r\n<br />\r\nNhan Thanh H&agrave;n v&eacute;n l&ecirc;n che k&iacute;n tầm mắt sợi t&oacute;c, thăm d&ograve; t&iacute;nh hỏi thăm Tần Phấn.<br />\r\n<br />\r\n&quot;C&oacute; c&aacute;i g&igrave; kh&oacute;, ngươi cứ y&ecirc;n t&acirc;m hướng ta d&ugrave;ng liền xong việc. Ngươi kh&ocirc;ng cần lo lắng sẽ l&agrave;m bị thương đến ta, tận ch&iacute;nh m&igrave;nh c&oacute; khả năng liền xong việc, sư huynh chịu nổi!&quot;<br />\r\n<br />\r\n&quot;Huống hồ ngươi ta cảnh giới k&eacute;m nhiều như vậy, chẳng lẽ lại ngươi c&ograve;n c&oacute; thể một kiếm vượt cấp? Kh&aacute;c lo lắng ta, d&ugrave;ng!&quot;<br />\r\n<br />\r\nTần Phấn hướng về ph&iacute;a tiểu sư muội triển lộ n&eacute;t mặt tươi cười, hắn như tr&uacute;t được g&aacute;nh nặng, cũng may tiểu sư muội x&aacute;c x&aacute;c thật thật c&oacute; giấu một l&aacute; b&agrave;i tẩy.<br />\r\n<br />\r\nTự m&igrave;nh chỉ cần dựa theo nguy&ecirc;n kế hoạch biểu hiện hơi c&oacute; ph&iacute; sức, cho tiểu sư muội lưu đủ l&ograve;ng tin l&agrave; đủ rồi.<br />\r\n<br />\r\nNghe tới Tần Phấn lời n&oacute;i, Nhan Thanh H&agrave;n lần thứ hai r&otilde; r&agrave;ng &yacute; thức được giữa hai người ch&ecirc;nh lệch.<br />\r\n<br />\r\nHiện tại giữa hai người c&aacute;ch một đạo khoan dung độ lượng khoảng c&aacute;ch, bằng hiện tại Nhan Thanh H&agrave;n l&agrave; kh&ocirc;ng c&oacute; năng lực vượt tới.<br />\r\n<br />\r\nCho n&ecirc;n coi như m&igrave;nh sử xuất một chi&ecirc;u n&agrave;y đến Tần Phấn cũng tất nhi&ecirc;n sẽ v&ocirc; sự ph&aacute;t sinh, cho n&ecirc;n tự m&igrave;nh cứ y&ecirc;n t&acirc;m to gan d&ugrave;ng đi!<br />\r\n<br />\r\n&quot;Như vậy sư huynh, ngươi liền tiếp hảo!&quot;<br />\r\n<br />\r\nN&oacute;i xong, Nhan Thanh H&agrave;n hai mắt nhắm lại.<br />\r\n<br />\r\nCh&acirc;n kh&iacute; tại thể nội cuồng bạo lao nhanh.<br />\r\n<br />\r\nKh&ocirc;ng kh&iacute; chung quanh lại lần nữa độ cao lưu động, h&oacute;a th&agrave;nh một c&aacute;i lại một c&aacute;i kh&ocirc;ng kh&iacute; kiếm chờ đợi tại b&ecirc;n cạnh của n&agrave;ng.<br />\r\n<br />\r\nCứ việc chỉ l&agrave; từ kh&ocirc;ng kh&iacute; chỗ cấu th&agrave;nh, nhưng mọi người có th&ecirc;̉ r&otilde; r&agrave;ng cảm nhận được tr&ecirc;n lưỡi kiếm truyền đến h&agrave;n &yacute;.<br />\r\n<br />\r\nĐ&acirc;y cũng kh&ocirc;ng phải l&agrave; tr&ograve; đ&ugrave;a, mỗi một chiếc lơ lửng kiếm đều l&agrave; h&agrave;ng thật gi&aacute; thật!<br />\r\n<br />\r\nMột b&ecirc;n Tần Phấn r&ugrave;ng m&igrave;nh một c&aacute;i, c&aacute;i n&agrave;y gi&oacute; l&agrave;m sao c&oacute; chút &acirc;m hiểm?<br />\r\n<br />\r\n&quot;Đ&acirc;y ch&iacute;nh l&agrave;! Đ&acirc;y ch&iacute;nh l&agrave;!&quot;<br />\r\n<br />\r\nĐ&atilde; đếm kh&ocirc;ng hết h&ocirc;m nay l&agrave; lần thứ mấy nhường trưởng l&atilde;o nh&oacute;m chấn kinh, nhưng một b&ecirc;n người chủ tr&igrave; ngược lại dẫn đầu chết lặng.<br />\r\n<br />\r\nB&igrave;nh thường mặt kh&ocirc;ng thay đổi sư ph&oacute; b&acirc;y giờ tại hắn trong mắt ho&agrave;n to&agrave;n ph&aacute; c&ocirc;ng, m&agrave; tự m&igrave;nh cũng c&aacute;i g&igrave; cũng xem kh&ocirc;ng hiểu.<br />\r\n<br />\r\n&quot;Kiếm &yacute; cụ hiện, thi&ecirc;n đạo h&oacute;a th&acirc;n! Đ&acirc;y ch&iacute;nh l&agrave; Thi&ecirc;n Đạo Kiếm Tượng ho&agrave;n to&agrave;n giải ph&oacute;ng một chi&ecirc;u!&quot;<br />\r\n<br />\r\n&quot;T&ecirc;, t&ecirc;. Tốt như vậy người kế tục lại bị bỏ qua, ta tối về tất nhi&ecirc;n mỗi ng&agrave;y l&agrave;m &aacute;c mộng.&quot;<br />\r\n<br />\r\n&quot; &quot;Ta cũng l&agrave; a. . .&quot; &quot;<br />\r\n<br />\r\nTrưởng l&atilde;o nh&oacute;m một c&aacute;i tiếp một c&aacute;i v&ocirc; lực nằm ở tr&ecirc;n ghế ngồi, bọn hắn d&aacute;m cam đoan h&ocirc;m nay v&ocirc; luận xảy ra chuyện g&igrave; cũng sẽ kh&ocirc;ng khiếp sợ đến đ&acirc;u.<br />\r\n<br />\r\nC&aacute;i n&agrave;y thế nhưng l&agrave; Truyền Thuyết cấp bậc thể chất a! Đ&acirc;y ch&iacute;nh l&agrave; Truyền Thuyết cấp bậc thể chất a!<br />\r\n<br />\r\n&quot;L&atilde;o nhị, ngươi xem chi&ecirc;u n&agrave;y Tần Phấn l&agrave;m như thế n&agrave;o cản?&quot;<br />\r\n<br />\r\n&quot;N&oacute;i thật, ta cũng kh&ocirc;ng r&otilde; r&agrave;ng.&quot;<br />\r\n<br />\r\nHiền l&agrave;nh trưởng l&atilde;o c&ugrave;ng t&oacute;c đỏ trưởng l&atilde;o hai người vẻ mặt nghi&ecirc;m t&uacute;c duy tr&igrave; được trưởng l&atilde;o t&ocirc;n nghi&ecirc;m.<br />\r\n<br />\r\nHai người nh&igrave;n xem tr&ecirc;n trận ph&aacute;t sinh hết thảy, hiện nay Thi&ecirc;n Đạo Kiếm Tượng ho&agrave;n to&agrave;n thực lực đ&atilde; hiện ra, đối một chi&ecirc;u n&agrave;y uy lực kh&ocirc;ng c&oacute; người sẽ ho&agrave;i nghi.<br />\r\n<br />\r\nNhưng vấn đề duy nhất ch&iacute;nh l&agrave; Tần Phấn n&ecirc;n ứng đối ra sao.<br />\r\n<br />\r\nNếu l&agrave; xử l&yacute; bất đương, trọng thương hoặc t&iacute;nh mệnh kh&oacute; đảm bảo đều l&agrave; có khả năng!<br />\r\n<br />\r\n&quot;Sư huynh, ta ra chi&ecirc;u!&quot;<br />\r\n<br />\r\nNhan Thanh H&agrave;n cắn răng một c&aacute;i, cưỡng &eacute;p sử dụng động l&ecirc;n treo ở một b&ecirc;n kh&ocirc;ng kh&iacute; kiếm hướng Tần Phấn bay đi.<br />\r\n<br />\r\n&quot;Hưu!&quot; &quot;Hưu!&quot; &quot;Hưu!&quot; &quot;Hưu!&quot; &quot;Hưu!&quot; &quot;Hưu!&quot;<br />\r\n<br />\r\nNhận được mệnh lệnh kh&ocirc;ng kh&iacute; kiếm mang theo phong thanh, tại kh&ocirc;ng c&oacute; vật g&igrave; Thanh Phong đường trước xẹt qua mấy đạo bạch quang.<br />\r\n<br />\r\nChi&ecirc;u n&agrave;y d&ugrave;ng xong, Nhan Thanh H&agrave;n to&agrave;n th&acirc;n tho&aacute;t lực, sắc mặt trắng bệch, ngực kịch liệt phập phồng.<br />\r\n<br />\r\n&quot;Ừm?&quot; Tần Phấn chưa kịp phản ứng kh&ocirc;ng biết r&otilde; xảy ra chuyện g&igrave;, c&aacute;i biết r&otilde; tiểu sư muội b&ecirc;n cạnh cuồng phong g&agrave;o th&eacute;t, dường như điều khiển c&aacute;i g&igrave; hướng tự m&igrave;nh bay tới!<br />\r\n<br />\r\nThế tới hung m&atilde;nh cuồng phong v&agrave; tiện đường mang theo bụi đất l&aacute; c&acirc;y nhường Tần Phấn trong l&uacute;c nhất thời mở mắt kh&ocirc;ng ra, hắn chỉ c&oacute; thể theo bản năng d&ugrave;ng tay ngăn trở con mắt!<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave;! C&aacute;i n&agrave;y tiểu tử v&acirc;̣y mà che mắt ứng đối!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nMới vừa n&oacute;i xong kh&ocirc;ng c&ograve;n khiếp sợ trưởng l&atilde;o nh&oacute;m từ tr&ecirc;n ghế nhảy dựng l&ecirc;n, chi&ecirc;u n&agrave;y uy lực kh&ocirc;ng ai d&aacute;m ho&agrave;i nghi, như thế nắm Đại Tần phấn sợ l&agrave; t&iacute;nh mệnh kh&oacute; đảm bảo a!<br />\r\n<br />\r\nT&oacute;c đỏ trưởng l&atilde;o ngồi kh&ocirc;ng y&ecirc;n, dẫn đầu đứng dậy: &quot;L&atilde;o nhị! Nh&oacute;m ch&uacute;ng ta xuất thủ cứu người!&quot;<br />\r\n<br />\r\n&quot;Ngừng! Trước kh&ocirc;ng muốn xuất thủ! Nh&oacute;m ch&uacute;ng ta lại xem Tần Phấn ứng đối ra sao, nếu l&agrave; kh&ocirc;ng địch lại lại xuất thủ cũng kh&ocirc;ng muộn!&quot;<br />\r\n<br />\r\nHiền l&agrave;nh trưởng l&atilde;o mặc d&ugrave; sắc mặt ngưng trọng, c&oacute; thể hắn nguyện &yacute; tin tưởng Tần Phấn.<br />\r\n<br />\r\nTin tưởng c&aacute;i n&agrave;y t&ocirc;ng m&ocirc;n thi&ecirc;n t&agrave;i!<br />\r\n<br />\r\nCuồng phong g&agrave;o th&eacute;t, Tần Phấn mở mắt kh&ocirc;ng ra, hắn c&oacute; thể cảm nhận được c&aacute;i g&igrave; đồ vật đang theo tự m&igrave;nh bay tới.<br />\r\n<br />\r\nM&agrave; lại b&ecirc;n tai vang l&ecirc;n &quot;&Ocirc;ng&quot; &quot;&Ocirc;ng&quot; thanh &acirc;m.<br />\r\n<br />\r\nRất ồn &agrave;o! Tựa như ong v&ograve; vẽ quần đang di động đồng dạng!<br />\r\n<br />\r\n&quot;Sư muội tuyệt chi&ecirc;u kh&ocirc;ng phải l&agrave; đưa tới quần ong v&ograve; vẽ đi, thanh &acirc;m như thế lớn!&quot;<br />\r\n<br />\r\nTần Phấn chẳng biết tại sao, trong l&ograve;ng c&oacute; ch&uacute;t kh&ocirc;ng chừng. Bọn n&agrave;y ong v&ograve; vẽ nhường hắn cảm nhận được cực mạnh cảm gi&aacute;c nguy hiểm.<br />\r\n<br />\r\nTần Phấn cảm thấy m&igrave;nh hẳn l&agrave; xuất thủ khu trục rơi bọn n&agrave;y ong v&ograve; vẽ.<br />\r\n<br />\r\nM&agrave; lại đ&ecirc;́n một chi&ecirc;u chế địch, kh&ocirc;ng thể c&oacute; giữ lại. Vạn nhất bị hoảng sợ ong v&ograve; vẽ bay loạn l&agrave;m sao b&acirc;y giờ? Ngủ đ&ocirc;ng đến lớn tuổi trưởng l&atilde;o nh&oacute;m cũng kh&ocirc;ng tốt!<br />\r\n<br />\r\nK&iacute;nh gi&agrave; y&ecirc;u trẻ l&agrave; mỹ đức!<br />\r\n<br />\r\nNghĩ đến c&aacute;i n&agrave;y, Tần Phấn d&ugrave;ng sức nắm chặt chu&ocirc;i kiếm, truyền ch&acirc;n kh&iacute; bao tr&ugrave;m th&acirc;n kiếm.<br />\r\n<br />\r\nTh&acirc;n kiếm bởi v&igrave; kh&ocirc;ng c&aacute;ch n&agrave;o tiếp nhận như thế cường đại ch&acirc;n kh&iacute;, bộ phận vị tr&iacute; cũng bắt đầu xuất hiện vết rạn.<br />\r\n<br />\r\nTần Phấn lập tức hướng về ph&iacute;a phong thanh chỗ đại lực vung kiếm, một đạo mạnh mẽ ch&acirc;n kh&iacute; kiếm ba cứ như vậy huy vũ ra ngo&agrave;i.<br />\r\n<br />\r\n&quot;Ầm ầm!&quot;<br />\r\n<br />\r\nChỉ nghe giống như như kinh l&ocirc;i thanh &acirc;m vang l&ecirc;n, Tần Phấn chậm r&atilde;i bu&ocirc;ng xuống che khuất hai mắt tay tr&aacute;i.<br />\r\n<br />\r\nPh&iacute;a trước đ&atilde; kh&ocirc;i phục như thường, chỉ l&agrave; hắn c&ugrave;ng sư muội ở giữa c&oacute; một c&aacute;i kh&ocirc;ng lớn kh&ocirc;ng nhỏ c&aacute;i hố.<br />\r\n<br />\r\nB&ecirc;n trong kh&ocirc;ng c&oacute; tồn tại c&aacute;i g&igrave; ong v&ograve; vẽ.<br />\r\n<br />\r\nNhan Thanh H&agrave;n sắc mặt t&aacute;i nhợt nh&igrave;n xem c&aacute;i n&agrave;y li&ecirc;n tiếp ph&aacute;t sinh sự t&igrave;nh, n&agrave;ng kh&ocirc;ng thể tin được ph&aacute;t sinh trước mắt hết thảy.<br />\r\n<br />\r\nNội t&acirc;m của n&agrave;ng đ&atilde; hỏng mất!<br />\r\n<br />\r\nĐồng thời theo ầm v&agrave;i tiếng vang l&ecirc;n<br />\r\n<br />\r\nĐ&ocirc;ng đảo trưởng l&atilde;o nh&oacute;m miệng to&agrave;n bộ đập v&agrave;o tr&ecirc;n mặt đất!</p>', '2021-12-01 15:18:48', 13, 1),
(324, 80, 'Sinh Như Hạ Hoa', '1', '<p>Ch&iacute;n giờ tối.<br />\r\n<br />\r\nMa Đ&ocirc; Nghệ Thuật Học Viện.<br />\r\n<br />\r\nHai tay của hắn &ocirc;m chắp sau &oacute;t, nằm ở trường học thao trường cạnh s&acirc;n cỏ nh&igrave;n l&ecirc;n &aacute;nh sao &mdash;&mdash;<br />\r\n<br />\r\nĐỉnh đầu mảnh tinh kh&ocirc;ng n&agrave;y tựa hồ c&ugrave;ng địa cầu giống nhau như đ&uacute;c, nhưng d&ugrave; l&agrave; kh&ocirc;ng t&igrave;m được sao bắc cực hắn cũng biết nơi n&agrave;y l&agrave; kh&ocirc;ng phải địa cầu, m&agrave; l&agrave; một c&aacute;i t&ecirc;n l&agrave; Lam Tinh song song thời kh&ocirc;ng.<br />\r\n<br />\r\n&quot;Ma Đ&ocirc; Nghệ Thuật Học Viện năm thứ hai đại học Soạn nhạc hệ L&acirc;m Uy&ecirc;n.&quot;<br />\r\n<br />\r\nĐ&acirc;y ch&iacute;nh l&agrave; hắn sau khi xuy&ecirc;n việt th&acirc;n phận mới.<br />\r\n<br />\r\nHắn thừa kế nguy&ecirc;n chủ hết thảy, lại kh&ocirc;ng nhớ ch&iacute;nh m&igrave;nh kiếp trước t&ecirc;n g&igrave;, cũng kh&ocirc;ng nhớ ch&iacute;nh m&igrave;nh xuy&ecirc;n việt nguy&ecirc;n nh&acirc;n.<br />\r\n<br />\r\nChỉ nhớ r&otilde; ch&iacute;nh m&igrave;nh kiếp trước h&igrave;nh như l&agrave; c&aacute;i họa sĩ?<br />\r\n<br />\r\nCho n&ecirc;n hắn c&oacute; thể chuyện đương nhi&ecirc;n đem m&igrave;nh l&agrave;m:<br />\r\n<br />\r\n&quot;L&acirc;m Uy&ecirc;n&quot; .<br />\r\n<br />\r\nQu&eacute;t hạ nguy&ecirc;n chủ tr&iacute; nhớ.<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n kh&ocirc;ng ngo&agrave;i dự liệu ph&aacute;t hiện, c&aacute;i thế giới n&agrave;y lịch sử quỹ t&iacute;ch c&ugrave;ng kiếp trước ho&agrave;n to&agrave;n bất đồng, lịch sử từ Tần Triều liền đi l&ecirc;n ph&acirc;n x&oacute;a đường, Ph&ugrave; T&ocirc; thừa kế Doanh Ch&iacute;nh sự nghiệp, dẫn Đại Tần Thiết Kỵ c&agrave;n qu&eacute;t B&aacute;t Hoang Lục Hợp, một hồi thao t&aacute;c trực tiếp để cho Đ&ocirc;ng Phương Chúa tể rồi thế giới, cho đến trăm năm trước bị c&agrave;ng th&ecirc;m cường đại Hạ Quốc thay thế.<br />\r\n<br />\r\nTo&agrave;n cầu nhất thống.<br />\r\n<br />\r\nTừ nay chiến tranh biến mất.<br />\r\n<br />\r\nỞ mảnh n&agrave;y kh&ocirc;ng c&oacute; chiến tranh ốc thổ, nghệ thuật th&agrave;nh Lam Tinh chung nhau theo đuổi, nơi n&agrave;y văn h&oacute;a kh&ocirc;ng kh&iacute; cực kỳ d&agrave;y đặc, điện ảnh &acirc;m nhạc hội họa văn học thư ph&aacute;p đợi từng c&aacute;i văn nghệ li&ecirc;n quan lĩnh vực cũng trước đ&oacute; chưa từng c&oacute; phồn vinh hưng thịnh!<br />\r\n<br />\r\n&quot;L&yacute; tưởng quốc.&quot;<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n l&agrave; như vậy h&igrave;nh dung.<br />\r\n<br />\r\nC&agrave;ng đem đối Vu Văn nghệ H&agrave;nh nghề giả m&agrave; n&oacute;i.<br />\r\n<br />\r\nBất qu&aacute; như thế n&agrave;o đi nữa Miền Đất Hứa thức l&yacute; tưởng quốc, cũng vẫn kh&ocirc;ng hề may mắn ph&aacute;t sinh, L&acirc;m Uy&ecirc;n mặc c&agrave;ng nguy&ecirc;n chủ tr&ecirc;n người liền xảy ra như vậy bất hạnh &mdash;&mdash;<br />\r\n<br />\r\nHắn mắc phải tuyệt chứng.<br />\r\n<br />\r\nKh&ocirc;ng sai, ch&iacute;nh l&agrave; trong kịch ti vi xuất hiện tần số cao v&ocirc; c&ugrave;ng, lại mỗi lần xuất hiện cũng theo một chậu m&aacute;u ch&oacute; tưới đi xuống danh từ:<br />\r\n<br />\r\n&quot;Tuyệt chứng&quot; .<br />\r\n<br />\r\nĐ&acirc;y l&agrave; L&acirc;m Uy&ecirc;n sau khi chuyển kiếp ở trong tr&iacute; nhớ m&igrave;nh ph&aacute;t hiện, ch&iacute;nh m&igrave;nh lại thừa kế một cụ kh&ocirc;ng c&ograve;n s&ocirc;́ng l&acirc;u nữa th&acirc;n thể, thầy thuốc rất sớm trước liền đối nguy&ecirc;n chủ t&igrave;nh huống xử tử h&igrave;nh:<br />\r\n<br />\r\n&quot;Đứa nhỏ n&agrave;y kh&ocirc;ng sống qua 25 tuổi.&quot;<br />\r\n<br />\r\nĐ&acirc;y l&agrave; nguy&ecirc;n chủ kh&ocirc;ng thể chịu đựng đau, cho n&ecirc;n hắn lựa chọn uống thuốc ngủ tự s&aacute;t, đ&acirc;y cũng l&agrave; L&acirc;m Uy&ecirc;n c&oacute; thể trở th&agrave;nh cổ th&acirc;n thể n&agrave;y t&acirc;n chủ nh&acirc;n nguy&ecirc;n nh&acirc;n, năm nay mười ch&iacute;n tuổi nguy&ecirc;n chủ bu&ocirc;ng tha hắn c&ograve;n dư lại kh&ocirc;ng nhiều sinh mệnh &mdash;&mdash;<br />\r\n<br />\r\nBởi v&igrave; sợ chết?<br />\r\n<br />\r\nCho n&ecirc;n lựa chọn tử vong?<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n vốn cũng cho l&agrave; đ&acirc;y l&agrave; nguy&ecirc;n chủ lựa chọn tự s&aacute;t căn do, cho đến hắn c&agrave;ng th&acirc;m nhập duyệt đọc nguy&ecirc;n chủ tr&iacute; nhớ, mới biết sự t&igrave;nh so với ch&iacute;nh m&igrave;nh tưởng tượng c&agrave;ng phức tạp một &iacute;t:<br />\r\n<br />\r\nNguy&ecirc;n chủ l&agrave; gia đ&igrave;nh độc th&acirc;n.<br />\r\n<br />\r\nPhụ th&acirc;n hắn rất sớm đ&atilde; bệnh qua đời.<br />\r\n<br />\r\nL&agrave; mụ mụ một th&acirc;n một m&igrave;nh đem hắn nu&ocirc;i lớn.<br />\r\n<br />\r\nNguy&ecirc;n chủ từ nhỏ đ&atilde; thể nhược nhi&ecirc;̀u bệnh, hở một t&iacute; l&ecirc;n cơn sốt đến h&ocirc;n m&ecirc;, v&igrave; chữa bệnh, ba ng&agrave;y hai lần nằm viện, cự Đại Hoa ti&ecirc;u, to&agrave;n dựa v&agrave;o mụ mụ một người g&aacute;nh v&aacute;c.<br />\r\n<br />\r\nC&oacute; tiền l&agrave; mượn.<br />\r\n<br />\r\nC&oacute; tiền l&agrave; mụ mụ kiếm.<br />\r\n<br />\r\nV&igrave; đem hắn nu&ocirc;i lớn, mụ mụ kh&ocirc;ng biết bị bao nhi&ecirc;u khổ, chớ n&oacute;i chi l&agrave;, nguy&ecirc;n chủ c&ograve;n c&oacute; một c&aacute;i tỷ tỷ, một c&ocirc; em g&aacute;i &mdash;&mdash;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nTỷ tỷ v&agrave; muội muội cũng rất hiểu chuyện.<br />\r\n<br />\r\nCứ việc bởi v&igrave; nguy&ecirc;n chủ quan hệ, c&aacute;c n&agrave;ng chưa từng c&oacute; qu&aacute; c&aacute;i g&igrave; tốt thời gian.<br />\r\n<br />\r\nDo sớm kiếm tiền b&ugrave; gia đ&igrave;nh, tỷ tỷ bu&ocirc;ng tha học nghi&ecirc;n cơ hội.<br />\r\n<br />\r\nV&igrave; giảm bớt gia đ&igrave;nh g&aacute;nh nặng, muội muội cũng l&agrave; từ nhỏ liền bắt đầu xuy&ecirc;n tỷ tỷ xuy&ecirc;n qua quần &aacute;o cũ.<br />\r\n<br />\r\nM&agrave; đ&egrave; chết lạc đ&agrave; cuối c&ugrave;ng một cọng cỏ l&agrave; .<br />\r\n<br />\r\nNguy&ecirc;n chủ mất đi truy đuổi mơ mộng tư c&aacute;ch.<br />\r\n<br />\r\nHắn vốn l&agrave; thanh nhạc hệ học sinh, trời sinh liền nắm giữ cường đại giọng h&aacute;t, giọng n&oacute;i điều kiện ở chuy&ecirc;n nghiệp b&ecirc;n trong l&agrave; ho&agrave;n to&agrave;n xứng đ&aacute;ng số một, hắn mơ mộng, đ&oacute; l&agrave; trở th&agrave;nh một danh ca sĩ.<br />\r\n<br />\r\nNhưng năm thứ nhất đại học thời điểm, hắn bệnh lại một lần nữa ph&aacute;t t&aacute;c, lần n&agrave;y ph&aacute;t t&aacute;c đưa đến hậu quả trực tiếp ch&iacute;nh l&agrave;:<br />\r\n<br />\r\nNguy&ecirc;n chủ kh&ocirc;ng c&aacute;ch n&agrave;o nữa ca h&aacute;t.<br />\r\n<br />\r\nHắn cuống họng bị ch&aacute;y hỏng, kh&ocirc;ng c&aacute;ch n&agrave;o nữa g&aacute;nh v&aacute;c li&ecirc;n tục cường độ cao thanh nhạc huấn luyện, chớ n&oacute;i chi l&agrave; hắn vẫn lấy l&agrave;m ki&ecirc;u ngạo cao &acirc;m &mdash;&mdash;<br />\r\n<br />\r\nBất đắc dĩ.<br />\r\n<br />\r\nHắn chuyển đến cũng chẳng phải giỏi Soạn nhạc hệ.<br />\r\n<br />\r\nHơn nữa ở năm thứ hai đại học năm n&agrave;y, lựa chọn tự s&aacute;t.<br />\r\n<br />\r\nKh&ocirc;ng chỉ l&agrave; nh&acirc;n v&igrave; giấc mộng tao ngộ đả k&iacute;ch, c&agrave;ng bởi v&igrave; hắn kh&ocirc;ng nghĩ lại li&ecirc;n lụy gia nh&acirc;n, sinh mệnh một khi mở ra đếm ngược, từng gi&acirc;y từng ph&uacute;t, đều l&agrave; h&agrave;nh hạ.<br />\r\n<br />\r\nTi&ecirc;u h&oacute;a những k&yacute; ức n&agrave;y.<br />\r\n<br />\r\nXuy&ecirc;n việt tới L&acirc;m Uy&ecirc;n ho&agrave;n to&agrave;n c&oacute; thể l&yacute; giải nguy&ecirc;n chủ quyết định, hắn kh&ocirc;ng c&aacute;ch n&agrave;o đứng ở đạo đức điểm cao chỉ tr&iacute;ch nguy&ecirc;n chủ qu&aacute; h&egrave;n yếu.<br />\r\n<br />\r\nChỉ c&oacute; thể n&oacute;i .<br />\r\n<br />\r\nMỗi người đều c&oacute; mỗi người bất hạnh, m&agrave; c&oacute; v&agrave;i người bất hạnh so s&aacute;nh với người b&igrave;nh thường, c&agrave;ng th&ecirc;m kh&oacute; c&oacute; thể chịu đựng th&ocirc;i.<br />\r\n<br />\r\nC&aacute;i n&agrave;y c&ugrave;ng Phật n&oacute;i &quot;Nh&acirc;n sinh tới tất cả khổ&quot; hiệu quả như nhau.<br />\r\n<br />\r\nNhưng L&acirc;m Uy&ecirc;n kh&ocirc;ng sẽ chọn tự s&aacute;t.<br />\r\n<br />\r\nMặc d&ugrave; hắn thừa kế cổ th&acirc;n thể n&agrave;y, vẫn l&agrave; một cụ rất kh&oacute; sống qua 25 tuổi th&acirc;n thể, nhưng &iacute;t ra c&ograve;n c&oacute; vài năm c&oacute; thể giày vò . Chứ ?<br />\r\n<br />\r\nSao điểm b&agrave;i h&aacute;t.<br />\r\n<br />\r\nSao điểm thư.<br />\r\n<br />\r\nChuy&ecirc;n chở văn h&oacute;a.<br />\r\n<br />\r\nCho người nh&agrave; kiếm &iacute;t tiền.<br />\r\n<br />\r\nL&agrave;, L&acirc;m Uy&ecirc;n đ&atilde; rất kh&oacute; sửa đổi ch&iacute;nh m&igrave;nh tuyệt chứng số mạng, nhưng c&ograve;n dư lại kh&ocirc;ng trong nhiều thời gian, hắn c&oacute; lẽ c&oacute; thể thay đổi người nh&agrave; vận mệnh.<br />\r\n<br />\r\n&Yacute; nghĩ như vậy rất khẩn cấp.<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n kh&ocirc;ng ph&acirc;n r&otilde; đ&acirc;y l&agrave; nguy&ecirc;n chủ &yacute; ch&iacute;, c&ograve;n l&agrave; ch&iacute;nh bản th&acirc;n hắn nguyện vọng.<br />\r\n<br />\r\nC&oacute; lẽ hắn thừa kế, tuyệt kh&ocirc;ng chỉ c&oacute; chỉ l&agrave; nguy&ecirc;n chủ tr&iacute; nhớ, c&ograve;n bao gồm to&agrave;n bộ hỉ nộ ai nhạc, c&ugrave;ng phần kia thần b&iacute; huyết mạch li&ecirc;n kết.<br />\r\n<br />\r\nNhư vậy cảm gi&aacute;c L&acirc;m Uy&ecirc;n cũng kh&ocirc;ng kh&aacute;ng cự.<br />\r\n<br />\r\nNhưng khi L&acirc;m Uy&ecirc;n định nhớ lại một &iacute;t kiếp trước văn nghệ t&aacute;c phẩm l&uacute;c lại ngạc nhi&ecirc;n ph&aacute;t hiện, ch&iacute;nh m&igrave;nh lại c&aacute;i g&igrave; cũng kh&ocirc;ng nhớ g&igrave; cả, tựa như c&ugrave;ng ch&iacute;nh m&igrave;nh qu&ecirc;n ch&iacute;nh m&igrave;nh kiếp trước t&ecirc;n gọi l&agrave; g&igrave;.<br />\r\n<br />\r\nKiếp trước như một trận Huyễn Mộng.<br />\r\n<br />\r\nTỉnh mộng, cũng sẽ kh&ocirc;ng c&ograve;n lại c&aacute;i g&igrave;.<br />\r\n<br />\r\nTa đ&acirc;y xuy&ecirc;n việt m&agrave; &yacute; đồ đến nghĩa l&agrave; c&aacute;i g&igrave;?<br />\r\n<br />\r\nNội t&acirc;m của L&acirc;m Uy&ecirc;n hỏi như vậy.<br />\r\n<br />\r\nSau đ&oacute; trong đ&acirc;̀u của hắn, vang l&ecirc;n một đạo kh&ocirc;ng t&iacute;nh l&agrave; trả lời trả lời: 【 huyết dịch kiểm tra trung . Gien kiểm tra trung . Tr&ograve;ng đen kiểm tra trung . Độ ph&ugrave; hợp phần trăm chi 99. 36 . Ph&ugrave; hợp ti&ecirc;u chuẩn . Kho t&agrave;i liệu lựa chọn . Th&aacute;i Dương Hệ địa cầu . Hệ thống đang ở tr&oacute;i chặt trung . 】<br />\r\n<br />\r\nHệ thống?<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n biết.<br />\r\n<br />\r\nMặc d&ugrave; tr&iacute; nhớ chi tiết cũng mơ hồ, nhưng kiếp trước xem qua nhiều ch&uacute;t hệ thống lưu Internet văn đ&agrave;n, hắn vẫn c&oacute; ấn tượng, biết đ&acirc;y l&agrave; kim thủ chỉ, đ&acirc;y cũng l&agrave; hắn xuy&ecirc;n việt &yacute; nghĩa chỗ.<br />\r\n<br />\r\nKh&ocirc;ng hề suy nghĩ lung tung.<br />\r\n<br />\r\nHắn y&ecirc;n lặng chờ đợi hệ thống gắn.<br />\r\n<br />\r\nCũng kh&ocirc;ng l&acirc;u lắm, kia cơ giới như vậy d&ograve;ng điện &acirc;m thanh lại lần nữa với trong đầu hắn vang l&ecirc;n: 【 gia tr&igrave; th&agrave;nh c&ocirc;ng, văn nghệ hệ thống tr&oacute;i chặt ho&agrave;n th&agrave;nh! 】<br />\r\n<br />\r\n&quot;Ngươi tốt.&quot;<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n chủ động ch&agrave;o hỏi.<br />\r\n<br />\r\n【 ng&agrave;i khỏe chứ, k&iacute; chủ, ch&uacute;c mừng ng&agrave;i bảng định văn nghệ hệ thống, bổn hệ thống tướng sẽ đem hết khả năng trợ gi&uacute;p ng&agrave;i trở th&agrave;nh Lam Tinh nghệ thuật gia, ng&agrave;i c&oacute; thể th&ocirc;ng qua đại n&atilde;o suy nghĩ c&ugrave;ng hệ thống trao đổi, ph&iacute;a dưới đem lấy văn tự h&igrave;nh thức v&igrave; ng&agrave;i biểu diễn k&iacute; chủ tin tức. 】<br />\r\n<br />\r\nTiếng m&aacute;y dừng lại.<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n trước mắt xuất hiện trong suốt phụ đề.<br />\r\n<br />\r\n【 tuổi t&aacute;c: 19 】<br />\r\n<br />\r\n【 tuổi thọ: 22 】<br />\r\n<br />\r\n【 hội họa: 45 】<br />\r\n<br />\r\n【 văn học: 105 】<br />\r\n<br />\r\n【 &acirc;m nhạc: 1038 】<br />\r\n<br />\r\n【 tổng hợp: 1188 】<br />\r\n<br />\r\n【 c&ograve;n lại: Chờ đợi mở ra 】<br />\r\n<br />\r\n【 ch&uacute; th&iacute;ch: Trừ tuổi t&aacute;c c&ugrave;ng tuổi thọ ngoại, mỗi người chia loại trị số đại biểu h&agrave;m nghĩa v&igrave; danh vọng, gần k&iacute; chủ bổn nh&acirc;n ở n&ecirc;n lĩnh vực lấy được ngoại giới c&ugrave;ng với nghiệp giới c&ocirc;ng nhận độ, tr&ecirc;n l&yacute; thuyết tới n&oacute;i kh&ocirc;ng c&oacute; hạn mức tối đa gi&aacute; trị, danh vọng gi&aacute; trị c&agrave;ng cao, k&iacute; chủ đạt được lợi &iacute;ch c&agrave;ng nhiều . 】<br />\r\n<br />\r\nTuổi thọ l&agrave; . 22?<br />\r\n<br />\r\nHệ thống tựa hồ biết &yacute; tưởng của L&acirc;m Uy&ecirc;n.<br />\r\n<br />\r\nLại một h&agrave;ng chữ m&agrave;n xuất hiện: 【 25 tuổi l&agrave; k&iacute; chủ tr&ecirc;n l&yacute; thuyết c&oacute; thể sống đến cực hạn tuổi t&aacute;c, c&acirc;n nhắc đến t&igrave;nh huống thực tế, k&iacute; chủ sống tối đa đến hai mươi hai tuổi, hơn nữa sẽ ở hai mươi mốt tuổi thời điểm, ho&agrave;n to&agrave;n t&ecirc; liệt. 】<br />\r\n<br />\r\n&quot;C&oacute; thể trị kh&ocirc;ng?&quot;<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n ở t&acirc;m l&yacute; đặt c&acirc;u hỏi.<br />\r\n<br />\r\nHệ thống: 【 l&agrave;m k&iacute; chủ danh vọng đi đến hệ thống danh vọng ti&ecirc;u chuẩn, sẽ c&oacute; được hệ thống chữa trị, th&ocirc;ng qua mấy tua chữa trị, c&oacute; thể đi đến hồi phục ti&ecirc;u chuẩn, mỗi đi đến nhất định danh vọng gi&aacute; trị, hệ thống cũng sẽ nhắc nhở k&iacute; chủ . 】<br />\r\n<br />\r\n&Yacute; tứ đ&oacute; l&agrave; c&oacute; thể trị.<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n thuần thục hỏi: &quot;T&acirc;n thủ g&oacute;i qu&agrave; lớn đ&acirc;y?&quot;<br />\r\n<br />\r\nC&oacute; lẽ l&agrave; L&acirc;m Uy&ecirc;n qu&aacute; th&ocirc;ng thạo rồi, cho tới hệ thống đều c&oacute; ch&uacute;t theo kh&ocirc;ng kịp L&acirc;m Uy&ecirc;n tiết tấu, trầm mặc mấy gi&acirc;y mới đ&aacute;p lại: 【 t&acirc;n thủ g&oacute;i qu&agrave; lớn đ&atilde; gửi đi đến k&iacute; chủ hậu trường thương khố. 】<br />\r\n<br />\r\n&quot;Tiến v&agrave;o thương khố.&quot;<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n vừa dứt lời, liền thấy trước mắt xuất hiện tương tự với tr&ograve; chơi ba l&ocirc; như thế giả tưởng giao diện, trong đ&oacute; thứ nhất &ocirc; lưới b&ecirc;n trong, r&otilde; r&agrave;ng l&agrave; nhất đoạn nội tồn kh&ocirc;ng lớn &acirc;m tần &mdash;&mdash;<br />\r\n<br />\r\nCa kh&uacute;c: &laquo; Sinh Như Hạ Hoa &raquo; .</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nT&acirc;n thủ g&oacute;i qu&agrave; lớn liền một ca kh&uacute;c?<br />\r\n<br />\r\nPhiền to&aacute;i đem &quot;Đại&quot; tự loại trừ thật sao?<br />\r\n<br />\r\nNội t&acirc;m của L&acirc;m Uy&ecirc;n nhổ nước bọt đồng thời, đem b&agrave;i h&aacute;t nghe qua một lần, chỉ l&agrave; kh&uacute;c nhạc dạo vang l&ecirc;n, hắn cũng đ&atilde; chắc chắn n&agrave;y liền l&agrave; trong tr&iacute; nhớ m&igrave;nh b&agrave;i h&aacute;t kia t&aacute;c phẩm.<br />\r\n<br />\r\nTr&ecirc;n thực tế.<br />\r\n<br />\r\nHắn mở ra b&agrave;i h&aacute;t n&agrave;y trong nh&aacute;y mắt, kiếp trước đối với c&aacute;i n&agrave;y b&agrave;i h&aacute;t tr&iacute; nhớ liền trong nh&aacute;y mắt d&acirc;ng l&ecirc;n trong l&ograve;ng, trước đ&oacute;, hắn lại sống chết kh&ocirc;ng nhớ nổi n&agrave;y thủ ca kh&uacute;c điều c&ugrave;ng với ca từ.<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n ước chừng l&agrave; biết hệ thống lộ số rồi.<br />\r\n<br />\r\nTrước mặt c&aacute;i gọi l&agrave; danh vọng gi&aacute; trị loại, đại kh&aacute;i ch&iacute;nh l&agrave; m&igrave;nh ph&aacute;t h&agrave;nh ca kh&uacute;c, sau đ&oacute; đạt được đủ c&ocirc;ng nhận loại ngoạn &yacute; nhi đi, khi loại n&agrave;y c&ocirc;ng nhận đi đến cấp bậc nhất định, ch&iacute;nh m&igrave;nh l&agrave; c&oacute; thể trị bệnh kh&ocirc;ng cần thật sớm dẫn hộp cơm rồi .<br />\r\n<br />\r\nThật đ&uacute;ng l&agrave; qua loa lấy lệ thiết lập.<br />\r\n<br />\r\nHệ thống tựa hồ đối với L&acirc;m Uy&ecirc;n nhổ nước bọt rất bất m&atilde;n, kịp thời bổ sung một c&aacute;i thiết lập: 【 k&iacute; chủ ở đạt được danh vọng gi&aacute; trị đồng thời, c&ograve;n c&oacute; thể đạt được cơ hội bốc thăm, tr&uacute;ng giải tỷ lệ cực cao. 】<br />\r\n<br />\r\n&quot;Ồ.&quot;<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n phản ứng b&igrave;nh thản.<br />\r\n<br />\r\nHắn đang suy nghĩ ca kh&uacute;c chuyện.<br />\r\n<br />\r\nHắn d&acirc;y thanh đ&atilde; bị hư, mặc d&ugrave; &laquo; Sinh Như Hạ Hoa &raquo; b&agrave;i h&aacute;t n&agrave;y &acirc;m vực y&ecirc;u cầu cũng kh&ocirc;ng cao, nhưng dựa theo lời dặn của b&aacute;c sĩ, L&acirc;m Uy&ecirc;n cũng kh&ocirc;ng cần ca h&aacute;t tốt.<br />\r\n<br />\r\nBất qu&aacute; n&agrave;y kh&ocirc;ng l&agrave;m kh&oacute; được L&acirc;m Uy&ecirc;n.<br />\r\n<br />\r\nCh&iacute;nh m&igrave;nh h&aacute;t kh&ocirc;ng đến c&oacute; thể cho người kh&aacute;c h&aacute;t.<br />\r\n<br />\r\nChỉ cần c&oacute; thể đạt được danh vọng liền c&oacute; thể, mặc d&ugrave; từ đạo l&yacute; đi l&ecirc;n n&oacute;i, ca h&aacute;t nh&acirc;n dễ d&agrave;ng hơn nổi danh, nhưng L&acirc;m Uy&ecirc;n cũng kh&ocirc;ng th&iacute;ch nổi danh, thậm ch&iacute; c&oacute; ch&uacute;t đ&aacute;ng gh&eacute;t nổi danh &mdash;&mdash;<br />\r\n<br />\r\nHắn cũng kh&ocirc;ng biết tại sao.<br />\r\n<br />\r\nC&oacute; thể l&agrave; chịu rồi kiếp trước ảnh hưởng?<br />\r\n<br />\r\nCứ việc L&acirc;m Uy&ecirc;n kh&ocirc;ng nhớ r&otilde; tr&iacute; nhớ kiếp trước rồi, nhưng hắn c&oacute; thể mơ hồ cảm gi&aacute;c, ch&iacute;nh m&igrave;nh kiếp trước chắc cũng l&agrave; rất lợi hại, n&oacute;i kh&ocirc;ng chừng c&ograve;n c&oacute; nhất định th&agrave;nh tựu đ&acirc;y.<br />\r\n<br />\r\nB&agrave;i h&aacute;t n&agrave;y cũng rất c&oacute; &yacute; tứ.<br />\r\n<br />\r\n&Iacute;t nhất rất th&iacute;ch hợp nguy&ecirc;n chủ t&igrave;nh huống.<br />\r\n<br />\r\nNghĩ tới đ&acirc;y L&acirc;m Uy&ecirc;n bỗng nhi&ecirc;n c&oacute; ch&uacute;t hiếu kỳ: &quot;Hệ thống, ta xuy&ecirc;n việt đến c&aacute;i thế giới n&agrave;y, kiếp trước kia ta, trực tiếp tr&ecirc;n địa cầu biến mất sao?&quot;<br />\r\n<br />\r\nHệ thống: 【 nh&acirc;n sinh trao đổi 】<br />\r\n<br />\r\nHệ thống đ&atilde; thăm d&ograve; L&acirc;m Uy&ecirc;n bộ s&aacute;ch v&otilde; thuật, người n&agrave;y năng lực hiểu phi thường cường đại, lại năng lực tiếp nhận cũng rất lợi hại, th&iacute;ch đơn giản trực tiếp, kh&ocirc;ng cần phải giải th&iacute;ch qu&aacute; nhiều, cho n&ecirc;n cũng bắt đầu lời &iacute;t &yacute; nhiều đứng l&ecirc;n.<br />\r\n<br />\r\n&quot;Nh&acirc;n sinh trao đổi sao.&quot;<br />\r\n<br />\r\n&Aacute;nh mắt cuả L&acirc;m Uy&ecirc;n c&oacute; ch&uacute;t chợt l&oacute;e, chợt lộ ra &ecirc;m &aacute;i nụ cười, c&oacute; người thay thế m&igrave;nh sống tiếp cảm gi&aacute;c, thực ra c&ograve;n kh&ocirc;ng ỷ lại.<br />\r\n<br />\r\nPhản ch&iacute;nh tự m&igrave;nh độc th&acirc;n.<br />\r\n<br />\r\nC&oacute; lẽ rất nhiều chuyện kh&ocirc;ng biết, nhưng kiếp trước những k&yacute; ức ấy mơ hồ đường ranh, L&acirc;m Uy&ecirc;n đại kh&aacute;i c&oacute; thể đem ph&aacute;c họa.<br />\r\n<br />\r\nKh&ocirc;ng được tốt lắm, cũng kh&ocirc;ng đo&aacute;n tệ hại.<br />\r\n<br />\r\nThực ra lại tệ hại nh&acirc;n sinh, chung quy tốt sinh nhật mệnh tiến v&agrave;o đếm ngược đi, ch&uacute;c xa lạ ngươi cũng c&oacute; thể c&oacute; một hệ thống, m&agrave; l&agrave; kh&ocirc;ng phải lại một lần nữa Sinh Như Hạ Hoa &mdash;&mdash;<br />\r\n<br />\r\n&Iacute;t nhất, ch&uacute;ng ta vẫn tồn tại.</p>', '2021-12-09 09:00:44', 5, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(325, 80, 'Ca Đàn Tân Nhân Quý', '2', '<p>&quot;Nguy&ecirc;n lai ngươi ở đ&acirc;y mi&ecirc;u đ&acirc;y.&quot;<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n b&ecirc;n tai vang l&ecirc;n một giọng n&oacute;i, ngay sau đ&oacute;, một Trương Anh tuấn m&agrave; trắng n&otilde;n mặt, chặn lại trước mắt s&aacute;ng ch&oacute;i tinh kh&ocirc;ng.<br />\r\n<br />\r\n&quot;Giản Dịch.&quot;<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n theo bản năng h&ocirc; l&ecirc;n t&ecirc;n đối phương.<br />\r\n<br />\r\nC&aacute;i n&agrave;y d&aacute;ng ngo&agrave;i anh tuấn, v&oacute;c người thon d&agrave;i nam sinh, l&agrave; L&acirc;m Uy&ecirc;n tiểu học đồng học ki&ecirc;m THCS đồng học ki&ecirc;m bạn học chung thời đại học &mdash;&mdash;<br />\r\n<br />\r\nCh&acirc;n ch&iacute;nh đồng đảng.<br />\r\n<br />\r\nM&agrave; b&acirc;y giờ, vị n&agrave;y đồng đảng đ&atilde; đưa tay, đem L&acirc;m Uy&ecirc;n từ dưới đất k&eacute;o l&ecirc;n.<br />\r\n<br />\r\nNgay sau đ&oacute; L&acirc;m Uy&ecirc;n cảm gi&aacute;c tr&ecirc;n vai trầm xuống, một m&oacute;n nữ thức &aacute;o kho&aacute;c đ&atilde; kho&aacute;c ở tr&ecirc;n người m&igrave;nh.<br />\r\n<br />\r\nHắn quay đầu, thấy một tấm đẹp đẽ mặt m&agrave;y vui vẻ.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; một c&aacute;i nữ sinh, t&oacute;c d&agrave;i kho&aacute;c tr&ecirc;n vai, lược thi phấn trang điểm, Quốc Sắc Thi&ecirc;n Hương.<br />\r\n<br />\r\n&quot;Hạ Phồn.&quot;<br />\r\n<br />\r\nVới thấy Giản Dịch l&uacute;c kh&ocirc;ng k&igrave;m l&ograve;ng được như thế, L&acirc;m Uy&ecirc;n giống vậy h&ocirc; l&ecirc;n t&ecirc;n đối phương.<br />\r\n<br />\r\nV&igrave; vậy k&ecirc;u Hạ Phồn nữ sinh giống như Giản Dịch, l&agrave; L&acirc;m Uy&ecirc;n tiểu học đồng học ki&ecirc;m THCS đồng học ki&ecirc;m bạn học chung thời đại học &mdash;&mdash;<br />\r\n<br />\r\nLại một c&aacute;i đồng đảng.<br />\r\n<br />\r\n&quot;Buổi tối c&oacute; phong, nhớ phủ th&ecirc;m &aacute;o kho&aacute;c t&aacute;i xuất m&ocirc;n.&quot;<br />\r\n<br />\r\nHạ Phồn dặn d&ograve; L&acirc;m Uy&ecirc;n, cứ việc n&agrave;ng v&agrave; Giản Dịch, c&ugrave;ng với thao trường chạy bộ người sở hữu, cũng chỉ mặc ph&ugrave; hợp m&ugrave;a h&egrave; ăn mặc phong c&aacute;ch tay ngắn loại.<br />\r\n<br />\r\n&quot; Được.&quot;<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n mở miệng.<br />\r\n<br />\r\nKết quả mới một c&acirc;u n&oacute;i, Giản Dịch c&ugrave;ng Hạ Phồn liền kh&ocirc;ng hẹn m&agrave; c&ugrave;ng theo d&otilde;i hắn, &aacute;nh mắt mang theo một tia hồ nghi.<br />\r\n<br />\r\n&quot;Tại sao ta cảm gi&aacute;c ngươi kh&ocirc;ng đ&uacute;ng chỗ n&agrave;o?&quot;<br />\r\n<br />\r\nN&oacute;i những lời n&agrave;y l&agrave; Giản Dịch.<br />\r\n<br />\r\nMặc d&ugrave; Hạ Phồn kh&ocirc;ng n&oacute;i g&igrave;, nhưng Hạ Phồn biểu t&igrave;nh đ&atilde; đ&atilde; n&oacute;i r&otilde;, n&agrave;ng cũng l&agrave; với Giản Dịch như thế c&aacute;i nh&igrave;n.<br />\r\n<br />\r\n&quot;Bởi v&igrave; ta đ&atilde; kh&ocirc;ng ho&agrave;n to&agrave;n đ&uacute;ng L&acirc;m Uy&ecirc;n rồi.&quot;<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n cười n&oacute;i, cảm gi&aacute;c m&igrave;nh c&aacute;ch n&oacute;i vẫn tương đối kh&aacute;ch quan, hắn c&oacute; một nửa thuộc về nguy&ecirc;n chủ &mdash;&mdash; &iacute;t nhất hắn hỉ nộ ai nhạc, th&acirc;n thể của hắn l&ocirc;ng da, vẫn thuộc về nguy&ecirc;n chủ.<br />\r\n<br />\r\n&quot;Ngươi bị quỷ qu&aacute;i b&aacute;m v&agrave;o người?&quot;<br />\r\n<br />\r\nGiản Dịch ph&igrave;nh bụng cười to, ngược lại kh&ocirc;ng nghi ngờ g&igrave;.<br />\r\n<br />\r\nHạ Phồn biểu t&igrave;nh cũng kh&ocirc;ng khỏi b&igrave;nh thường trở lại đi xuống.<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n thở ph&agrave;o nhẹ nh&otilde;m.<br />\r\n<br />\r\nNhư vậy hắn liền c&oacute; thể y&ecirc;n t&acirc;m thoải m&aacute;i kh&ocirc;ng giải th&iacute;ch nữa.<br />\r\n<br />\r\nHạ Phồn c&ugrave;ng Giản Dịch c&ugrave;ng L&acirc;m Uy&ecirc;n từ nhỏ c&ugrave;ng nhau lớn l&ecirc;n, cho n&ecirc;n đối với L&acirc;m Uy&ecirc;n t&igrave;nh huống biết gốc biết rể.<br />\r\n<br />\r\nCũng v&igrave; vậy, hai người từ nhỏ đ&atilde; một mực chiếu cố th&acirc;n thể l&agrave; kh&ocirc;ng phải cực kỳ tốt L&acirc;m Uy&ecirc;n.<br />\r\n<br />\r\nTừ trong t&igrave;nh cảm m&agrave; n&oacute;i, L&acirc;m Uy&ecirc;n kh&ocirc;ng nghĩ lừa dối hai người, nhưng lại kh&ocirc;ng thể kh&ocirc;ng th&iacute;ch hợp tiến h&agrave;nh lừa dối.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;L&acirc;m ti&ecirc;n sinh, ta kh&aacute;ch quan n&oacute;i một c&acirc;u.&quot;<br />\r\n<br />\r\nGiản Dịch nghi&ecirc;m t&uacute;c n&oacute;i: &quot;C&aacute;i gọi l&agrave; 【 Tiểu Thuyết Gia 】 đ&acirc;u r&ocirc;̀i, đ&acirc;y ch&iacute;nh l&agrave; Lam Tinh c&aacute;c Đại Ch&acirc;u người trẻ tuổi tr&ecirc;n Internet thống nhất bầu bằng phiếu ra thập đại tối ngưỡng mộ trong l&ograve;ng một trong những nghề, coi đ&acirc;y l&agrave; mục ti&ecirc;u m&agrave; cố gắng mọi người kh&ocirc;ng đếm xuể, ngươi chỉ dựa v&agrave;o nhất thời y&ecirc;u th&iacute;ch liền muốn ở c&aacute;i nghề n&agrave;y bộc lộ t&agrave;i năng thật sự l&agrave; qu&aacute; kh&oacute; khăn nhiều ch&uacute;t, cho n&ecirc;n kh&ocirc;ng cần phải ở dạ hắc phong cao s&acirc;n cỏ b&ecirc;n tr&ecirc;n thổi L&atilde;nh Phong ho&agrave;i nghi nh&acirc;n sinh.&quot;<br />\r\n<br />\r\n&quot;Tiểu Thuyết Gia .&quot;<br />\r\n<br />\r\n&Aacute;nh mắt cuả L&acirc;m Uy&ecirc;n c&oacute; ch&uacute;t chớp động.<br />\r\n<br />\r\nGiản Dịch nhắc tới tiểu thuyết l&agrave; c&oacute; nguy&ecirc;n nh&acirc;n.<br />\r\n<br />\r\nBởi v&igrave; trước đ&acirc;y kh&ocirc;ng l&acirc;u, nguy&ecirc;n chủ nảy sinh li&ecirc;n quan tới viết tiểu thuyết &yacute; nghĩ, hơn nữa biến th&agrave;nh h&agrave;nh động, nắm thập vạn chữ bản thảo tham gia một c&aacute;i Internet y&ecirc;u cầu viết b&agrave;i hoạt động, định lấy 【 Tiểu Thuyết Gia 】 th&acirc;n phận xuất đạo.<br />\r\n<br />\r\nLoại n&agrave;y y&ecirc;u cầu viết b&agrave;i rất n&aacute;o nhiệt.<br />\r\n<br />\r\nBởi v&igrave; một khi 【 Tiểu Thuyết Gia 】 xuất đạo th&agrave;nh c&ocirc;ng, kh&ocirc;ng chỉ c&oacute; tr&uacute;ng thưởng người t&aacute;c phẩm sẽ c&oacute; được tr&acirc;n qu&yacute; xuất bản cơ hội, nếu như t&aacute;c phẩm lượng ti&ecirc;u thụ đi đến ti&ecirc;u chuẩn nhất định lời n&oacute;i, c&ograve;n c&oacute; thể tiến h&agrave;nh hoạt h&igrave;nh thậm ch&iacute; c&ograve;n điện ảnh, hoặc l&agrave; tr&ograve; chơi loại soạn lại.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; v&ocirc; số lấy Tiểu Thuyết Gia v&igrave; phấn đấu mục ti&ecirc;u mọi người thật sự tha thiết ước mơ sự t&igrave;nh!<br />\r\n<br />\r\nTrọng yếu nhất l&agrave;, phi thường kiếm tiền!<br />\r\n<br />\r\nNhưng rất đ&aacute;ng tiếc .<br />\r\n<br />\r\nCh&iacute;nh l&agrave; bởi v&igrave; loại hoạt động n&agrave;y v&ocirc; c&ugrave;ng hấp dẫn, m&agrave; nguy&ecirc;n chủ tiểu thuyết s&aacute;ng t&aacute;c thi&ecirc;n ph&uacute; lại l&agrave; thật một dạng cho n&ecirc;n y&ecirc;u cầu viết b&agrave;i v&ograve;ng thứ nhất gi&aacute;m khảo, nguy&ecirc;n chủ t&aacute;c phẩm dự thi liền chịu khổ đ&agrave;o thải.<br />\r\n<br />\r\nGiản Dịch v&igrave; vậy m&agrave; nghĩ rằng, m&igrave;nh l&agrave; bởi v&igrave; y&ecirc;u cầu viết b&agrave;i kh&ocirc;ng th&ocirc;ng qua, cho n&ecirc;n mới một người ở thao trường thổi L&atilde;nh Phong.<br />\r\n<br />\r\nNhưng m&agrave; .<br />\r\n<br />\r\nV&ocirc; luận L&acirc;m Uy&ecirc;n hay lại l&agrave; nguy&ecirc;n chủ,<br />\r\n<br />\r\nThực ra cũng kh&ocirc;ng th&egrave;m để &yacute; tr&agrave;ng n&agrave;y y&ecirc;u cầu viết b&agrave;i kết quả.<br />\r\n<br />\r\nNguy&ecirc;n chủ sẽ chọn tự s&aacute;t, cũng với y&ecirc;u cầu viết b&agrave;i thất bại kh&ocirc;ng c&oacute; một mao tiền quan hệ, d&ugrave; sao hắn tham gia y&ecirc;u cầu viết b&agrave;i liền l&agrave; một loại chơi đ&ugrave;a m&agrave; th&ocirc;i.<br />\r\n<br />\r\nVạn nhất kiếm tiền đ&acirc;y?<br />\r\n<br />\r\nHắn l&agrave; &ocirc;m loại t&acirc;m l&yacute; n&agrave;y đi l&agrave;m chuyện n&agrave;y.<br />\r\n<br />\r\nNguy&ecirc;n chủ l&agrave; một c&aacute;i v&ocirc; thời v&ocirc; khắc kh&ocirc;ng nghĩ sớm một ch&uacute;t tốt nghiệp kiếm tiền nh&acirc;n.<br />\r\n<br />\r\nHắn lu&ocirc;n l&agrave; &ocirc;m một loại đối với người nh&agrave; &aacute;y n&aacute;y trong l&ograve;ng m&agrave; cố gắng phấn đấu.<br />\r\n<br />\r\nHắn cảm thấy tỷ tỷ, muội muội, c&ograve;n c&oacute; mụ mụ, cũng hy sinh cho hắn rồi qu&aacute; nhiều, cho n&ecirc;n hắn muốn hết m&igrave;nh c&oacute; thể bồi thường c&aacute;c n&agrave;ng.<br />\r\n<br />\r\nL&agrave;m ca sĩ, kh&ocirc;ng chỉ l&agrave; nh&acirc;n v&igrave; giấc mộng.<br />\r\n<br />\r\nM&agrave; l&agrave; bởi v&igrave;, c&aacute;i nghề n&agrave;y, c&oacute; thể kiếm nhiều tiền!<br />\r\n<br />\r\nNg&agrave;n vạn đừng n&oacute;i c&aacute;i g&igrave; &quot;Mơ mộng kh&ocirc;ng cho kim tiền tới &ocirc; nhục&quot; lời độc &aacute;c.<br />\r\n<br />\r\nĐối nguy&ecirc;n chủ m&agrave; n&oacute;i, nếu như &ocirc; nhục mộng nghĩ c&oacute; thể đạt được kim tiền, hắn rất hi vọng ch&iacute;nh m&igrave;nh mơ mộng mau mau bị ăn m&ograve;n đến thi&ecirc;n sang b&aacute;ch khổng &mdash;&mdash;<br />\r\n<br />\r\nNhư vậy hắn liền c&oacute; thể v&igrave; muội muội mua một bộ đẹp đẽ &aacute;o đầm.<br />\r\n<br />\r\nV&igrave; tỷ tỷ giải ph&oacute;ng cuộc đời của m&igrave;nh.<br />\r\n<br />\r\nĐể cho mụ mụ cũng sẽ kh&ocirc;ng khổ cực như vậy c&ocirc;ng việc.<br />\r\n<br />\r\n&quot;Đừng nghe Giản Dịch.&quot;<br />\r\n<br />\r\nHạ Phồn cũng cho l&agrave; L&acirc;m Uy&ecirc;n l&agrave; đang ở v&igrave; y&ecirc;u cầu viết b&agrave;i thất bại sự t&igrave;nh m&agrave; ảo n&atilde;o.<br />\r\n<br />\r\nBất qu&aacute; n&agrave;ng kh&ocirc;ng c&oacute; khuy&ecirc;n L&acirc;m Uy&ecirc;n bu&ocirc;ng tha, m&agrave; l&agrave; kh&iacute;ch lệ hắn tiếp tục giữ vững: &quot;Thực ra tương tự y&ecirc;u cầu viết b&agrave;i c&oacute; rất nhiều, tỷ như th&aacute;ng sau, Lam Tinh b&agrave;i danh h&agrave;ng đầu xuất bản phương 【 Ng&acirc;n Lam Thư Khố 】 sẽ tổ chức &#39;Si&ecirc;u T&acirc;n Tinh&#39; y&ecirc;u cầu viết b&agrave;i, đến thời điểm ngươi c&oacute; thể tham gia thử một ch&uacute;t.&quot;<br />\r\n<br />\r\n&quot;Nhờ cậy a Hạ Phồn.&quot;<br />\r\n<br />\r\nGiản Dịch n&acirc;ng tr&aacute;n: &quot;L&acirc;m Uy&ecirc;n liền một c&aacute;i g&agrave; rừng y&ecirc;u cầu viết b&agrave;i cũng th&ocirc;ng kh&ocirc;ng qua được, ngươi để cho hắn đi khi&ecirc;u chiến 【 Si&ecirc;u T&acirc;n Tinh 】?&quot;<br />\r\n<br />\r\n&quot;Trọng ở tham dự chứ sao.&quot;<br />\r\n<br />\r\nHạ Phồn chột dạ n&oacute;i, thực ra n&agrave;ng cũng kh&ocirc;ng hi vọng n&agrave;o L&acirc;m Uy&ecirc;n th&agrave;nh c&ocirc;ng, n&agrave;ng liền l&agrave; đơn thuần hi vọng L&acirc;m Uy&ecirc;n c&oacute; thể t&igrave;m cho m&igrave;nh ch&uacute;t chuyện l&agrave;m, từ cuống họng xảy ra vấn đề, chuyển tới Soạn nhạc hệ sau đ&oacute;, L&acirc;m Uy&ecirc;n t&acirc;m t&iacute;nh một mực l&agrave; kh&ocirc;ng phải rất tốt.<br />\r\n<br />\r\nGiản Dịch kh&ocirc;ng c&oacute; Hạ Phồn nhẵn nhụi.<br />\r\n<br />\r\nHắn ch&iacute;nh nước miếng văng tung t&oacute;e với L&acirc;m Uy&ecirc;n phổ cập khoa học đến t&igrave;nh huống căn bản: &quot;Ngay cả ta c&aacute;i n&agrave;y kh&ocirc;ng ch&uacute; &yacute; giới tiểu thuết nh&acirc;n đều biết Si&ecirc;u T&acirc;n Tinh khủng bố đến mức n&agrave;o, người dự thi cơ bản đều l&agrave; nhiều ch&uacute;t v&igrave; xuất đạo mơ mộng m&agrave; phấn đấu nhiều năm l&atilde;o điểu, thậm ch&iacute; thỉnh thoảng sẽ c&oacute; một &iacute;t đ&atilde; xuất đạo nhưng lăn lộn kh&ocirc;ng qu&aacute; như &yacute; tham gia, ngược lại đối với rất nhiều người m&agrave; n&oacute;i, b&uacute;t hiệu đổi một lần b&iacute; danh mở một c&aacute;i, bọn họ ch&iacute;nh l&agrave; c&aacute;i gọi l&agrave; người mới .&quot;<br />\r\n<br />\r\nTổng kết một c&acirc;u n&oacute;i: Si&ecirc;u T&acirc;n Tinh l&agrave; thần ti&ecirc;n đ&aacute;nh nhau.<br />\r\n<br />\r\n&quot;Biết, biết.&quot;<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n cười cắt đứt Giản Dịch lời n&oacute;i, nội t&acirc;m lại &acirc;m thầm nhớ Si&ecirc;u T&acirc;n Tinh chuyện.<br />\r\n<br />\r\nỞ nghệ thuật kh&ocirc;ng kh&iacute; d&agrave;y đặc thế giới, tựa hồ đạt được c&ocirc;ng nhận độ con đường nhiều một c&aacute;ch đặc biệt đ&acirc;y.<br />\r\n<br />\r\nBất qu&aacute; b&acirc;y giờ m&igrave;nh c&ograve;n kh&ocirc;ng c&oacute; tiểu thuyết.<br />\r\n<br />\r\nPhải đợi hệ thống b&ecirc;n n&agrave;y khuynh t&igrave;nh cung cấp mới được.<br />\r\n<br />\r\nNgược lại l&agrave; &laquo; Sinh Như Hạ Hoa &raquo; b&agrave;i h&aacute;t n&agrave;y, L&acirc;m Uy&ecirc;n phải nghĩ biện ph&aacute;p tuy&ecirc;n bố ra ngo&agrave;i, hắn kh&ocirc;ng khỏi nh&igrave;n về ph&iacute;a Hạ Phồn &mdash;&mdash;<br />\r\n<br />\r\nHạ Phồn l&agrave; thanh nhạc hệ học sinh.<br />\r\n<br />\r\nỞ L&acirc;m Uy&ecirc;n kh&ocirc;ng đổi ng&agrave;nh thời điểm, hai người hay l&agrave; bạn học c&ugrave;ng lớp, liền chuy&ecirc;n nghiệp năng lực m&agrave; n&oacute;i, Hạ Phồn nhất định l&agrave; kh&ocirc;ng c&oacute; vấn đề, n&agrave;ng ca h&aacute;t thi&ecirc;n ph&uacute; &iacute;t ỏi thua k&eacute;m mất đi giọng n&oacute;i trước L&acirc;m Uy&ecirc;n.<br />\r\n<br />\r\nNhưng Hạ Phồn kh&ocirc;ng th&iacute;ch hợp &laquo; Sinh Như Hạ Hoa &raquo; .<br />\r\n<br />\r\nHạ Phồn giọng n&oacute;i th&iacute;ch hợp hơn h&aacute;t lưu h&agrave;nh, &laquo; Sinh Như Hạ Hoa &raquo; l&agrave; đại biểu một loại ca dao phong c&aacute;ch, L&acirc;m Uy&ecirc;n kh&ocirc;ng đến nổi đem b&agrave;i h&aacute;t n&agrave;y cố gắng nh&eacute;t cho Hạ Phồn đi h&aacute;t.<br />\r\n<br />\r\nHơn nữa, Hạ Phồn chỉ l&agrave; học sinh.<br />\r\n<br />\r\nHọc sinh ph&aacute;t b&agrave;i h&aacute;t, chẳng lẽ muốn th&ocirc;ng qua Internet con đường sao?<br />\r\n<br />\r\nKia đạt được c&ocirc;ng nhận tốc độ cũng qu&aacute; chậm nhiều ch&uacute;t, kh&ocirc;ng c&oacute; nhất định tuy&ecirc;n truyền, b&agrave;i h&aacute;t n&agrave;y coi như đ&aacute; ch&igrave;m đ&aacute;y biển cũng kh&ocirc;ng thể kh&ocirc;ng khả năng sự t&igrave;nh.<br />\r\n<br />\r\n&quot;Thế n&agrave;o?&quot;<br />\r\n<br />\r\nHạ Phồn thấy L&acirc;m Uy&ecirc;n nh&igrave;n m&igrave;nh cằm chằm, cảm gi&aacute;c c&oacute; ch&uacute;t kỳ qu&aacute;i: &quot;Ngươi l&agrave; muốn ăn c&aacute;i g&igrave; hay lại l&agrave; muốn uống g&igrave;, b&acirc;y giờ ta cho ngươi đi mua.&quot;<br />\r\n<br />\r\n&quot;L&agrave; kh&ocirc;ng phải.&quot;<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n n&oacute;i: &quot;Ta c&oacute; người bằng hữu . &Acirc;n, bằng hữu, hắn viết một ca kh&uacute;c muốn ph&aacute;t h&agrave;nh, nhưng kh&ocirc;ng biết n&ecirc;n l&agrave;m sao b&acirc;y giờ, c&aacute;c ngươi c&oacute; c&aacute;i g&igrave; con đường đề cử sao?&quot;<br />\r\n<br />\r\n&quot;Ngươi c&oacute; phải hay kh&ocirc;ng l&agrave; cho&aacute;ng v&aacute;ng?&quot;<br />\r\n<br />\r\nGiản Dịch bĩu m&ocirc;i n&oacute;i: &quot;Ngươi khi đ&oacute; l&agrave; kh&ocirc;ng phải lấy năm chục ng&agrave;n đồng tiền gi&aacute; cả, b&aacute;n đứng ch&iacute;nh m&igrave;nh ấy ư, c&oacute; b&agrave;i h&aacute;t đương nhi&ecirc;n l&agrave; t&igrave;m c&ocirc;ng ty a.&quot;<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n sững sờ, chợt bừng tỉnh.<br />\r\n<br />\r\nNguy&ecirc;n Chủ Khảo b&ecirc;n tr&ecirc;n Ma Đ&ocirc; &acirc;m nhạc học viện sau đ&oacute;, cho thấy kinh người giọng n&oacute;i thi&ecirc;n ph&uacute;, cho n&ecirc;n năm thứ nhất đại học thời điểm liền k&yacute; một nh&agrave; Entertainment, k&yacute; hợp đồng ph&iacute; ch&iacute;nh l&agrave; năm chục ng&agrave;n đồng tiền &mdash;&mdash;<br />\r\n<br />\r\nNguy&ecirc;n chủ qu&aacute; cần tiền rồi.<br />\r\n<br />\r\nCho n&ecirc;n d&ugrave; l&agrave; nguy&ecirc;n chủ biết r&otilde; năm chục ng&agrave;n đồng tiền k&yacute; hợp đồng ph&iacute; thật rất gi&aacute; rẻ, nhưng vẫn l&agrave; kh&ocirc;ng ch&uacute;t do dự k&yacute; t&ecirc;n kỳ hạn t&aacute;m năm hiệp ước, m&agrave; hắn bắt v&agrave;o tay k&yacute; hợp đồng ph&iacute; ch&iacute;nh l&agrave; trước ti&ecirc;n giao cho mụ mụ!</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nC&oacute; thể chẳng ai nghĩ tới .<br />\r\n<br />\r\nC&aacute;i kia giọng n&oacute;i thi&ecirc;n ph&uacute; kinh người thiếu ni&ecirc;n, lại đang k&yacute; hợp đồng kh&ocirc;ng l&acirc;u sau, cũng kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o ca h&aacute;t.<br />\r\n<br />\r\nGặp phải loại chuyện n&agrave;y, k&yacute; L&acirc;m Uy&ecirc;n Entertainment chỉ c&oacute; thể tự nhận xui xẻo, từ nay đối L&acirc;m Uy&ecirc;n mặc kệ rồi.<br />\r\n<br />\r\nĐại kh&aacute;i cũng l&agrave; biết L&acirc;m Uy&ecirc;n t&igrave;nh huống gia đ&igrave;nh, cho n&ecirc;n c&ocirc;ng ty kh&ocirc;ng t&igrave;m hắn phải bồi thường loại, chỉ l&agrave; một mực cũng kh&ocirc;ng nhấc giải ước chuyện, đo&aacute;n chừng đều qu&ecirc;n c&ocirc;ng ty k&yacute; hợp đồng ca sĩ b&ecirc;n trong c&ograve;n c&oacute; L&acirc;m Uy&ecirc;n như vậy nh&acirc;n vật số m&aacute;.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng đ&uacute;ng.&quot;<br />\r\n<br />\r\nỞ L&acirc;m Uy&ecirc;n suy nghĩ thời điểm, Giản Dịch bỗng nhi&ecirc;n lấy lại tinh thần: &quot;L&acirc;m ti&ecirc;n sinh, ngươi chừng n&agrave;o th&igrave; c&oacute; ta c&ugrave;ng Hạ Phồn kh&ocirc;ng nhận biết bạn?&quot;<br />\r\n<br />\r\n&quot;V&ocirc; t&igrave;nh l&agrave; bạn.&quot;<br />\r\n<br />\r\nHạ Phồn che miệng cười trộm, ngay sau đ&oacute; n&oacute;i: &quot;Nếu như muốn ph&aacute;t b&agrave;i h&aacute;t lời n&oacute;i lập tức liền c&oacute; một cơ hội, bởi v&igrave; mấy ng&agrave;y nữa ch&iacute;nh l&agrave; mỗi năm một lần 【 Ca Đ&agrave;n T&acirc;n Nh&acirc;n Qu&yacute; 】, đến thời điểm c&aacute;c đại giải tr&iacute; c&ocirc;ng ty cũng sẽ ph&aacute;t lực, điều kiện ti&ecirc;n quyết l&agrave; ngươi c&oacute; thể đủ đi đến c&ocirc;ng ty ghi danh ti&ecirc;u chuẩn .&quot;<br />\r\n<br />\r\n&quot;Biết.&quot;<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n cũng lộ ra nụ cười.<br />\r\n<br />\r\nĐ&acirc;y ch&iacute;nh l&agrave; nghệ thuật gia Miền Đất Hứa, vui chơi giải tr&iacute; thịnh h&agrave;nh l&yacute; tưởng chi quốc!<br />\r\n<br />\r\nChỉ cần ngươi c&oacute; năng lực, th&agrave;nh danh con đường thảm đỏ đơn giản l&agrave; trải ở giăng khắp nơi, duy&ecirc;n triển tới ngươi cửa nh&agrave;!<br />\r\n<br />\r\nCa Đ&agrave;n T&acirc;n Nh&acirc;n Qu&yacute;.<br />\r\n<br />\r\nĐ&acirc;y ch&iacute;nh l&agrave; trong đ&oacute; một c&aacute;i th&agrave;nh danh con đường, đ&acirc;y cũng l&agrave; Lam Tinh v&igrave; bồi dưỡng &acirc;m nhạc nh&acirc;n t&agrave;i m&agrave; cố &yacute; thiết lập quy tắc:<br />\r\n<br />\r\nH&agrave;ng năm th&aacute;ng mười một, Lam Tinh c&aacute;c nơi cũng sẽ tổ chức một lần Ca Đ&agrave;n T&acirc;n Nh&acirc;n Qu&yacute; hoạt động!<br />\r\n<br />\r\nĐến l&uacute;c đ&oacute;, ca đ&agrave;n c&aacute;c tiền bối sẽ ăn &yacute; kh&ocirc;ng ph&aacute;t chuy&ecirc;n tập thậm ch&iacute; đơn kh&uacute;c, tạm thời ngừng bu&ocirc;n b&aacute;n, tốt cho bọn hậu bối lưu lại một định kh&ocirc;ng gian.<br />\r\n<br />\r\nThậm ch&iacute; c&oacute; tiền bối sẽ ở th&aacute;ng mười một, chủ động cho coi trọng hậu bối những người mới đ&aacute;nh bảng.<br />\r\n<br />\r\nSau đ&oacute; c&aacute;c đại giải tr&iacute; c&ocirc;ng ty, cũng sẽ bắt cơ hội như vậy, to&agrave;n lực đẩy ra ca đ&agrave;n người mới!<br />\r\n<br />\r\nVốn l&agrave; L&acirc;m Uy&ecirc;n l&agrave; c&oacute; cơ hội như vậy ở người mới cuối m&ugrave;a xuất đạo.<br />\r\n<br />\r\nNhưng rất đ&aacute;ng tiếc, hắn hiện tại chỉ c&oacute; thể tranh thủ lấy một c&aacute;i ca kh&uacute;c chế th&acirc;n phận xuất đạo rồi.<br />\r\n<br />\r\nBất qu&aacute; .<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n d&ugrave; sao kh&ocirc;ng c&ograve;n l&agrave; nguy&ecirc;n chủ rồi.<br />\r\n<br />\r\nCho n&ecirc;n c&oacute; thể lấy ca kh&uacute;c chế th&acirc;n phận xuất đạo ngược lại c&agrave;ng l&agrave;m cho L&acirc;m Uy&ecirc;n cảm thấy vui vẻ, bởi v&igrave; liền Lam Tinh ph&acirc;n trướng kiểu m&agrave; n&oacute;i, th&ocirc;ng lệ l&agrave; 2. 5 so với 2. 5 so với 5.<br />\r\n<br />\r\nN&oacute;i đơn giản:<br />\r\n<br />\r\nMột ca kh&uacute;c bằng v&agrave;o hoặc l&agrave; nghe đ&agrave;i sinh ra lời, c&ocirc;ng ty lấy đi năm phần mười, c&ograve;n lại năm phần mười, ch&iacute;nh l&agrave; do ca sĩ c&ugrave;ng ca kh&uacute;c chia một nửa.<br />\r\n<br />\r\nNếu như ca sĩ c&oacute; hai vị .<br />\r\n<br />\r\nVậy th&igrave; hai vị ca sĩ ph&acirc;n 2. 5 .<br />\r\n<br />\r\nNếu như ca kh&uacute;c c&oacute; hai vị, l&agrave; Soạn nhạc lấy th&ecirc;m một &iacute;t, viết lời bớt lấy một &iacute;t, cụ thể nh&igrave;n hợp đồng n&oacute;i thế n&agrave;o.<br />\r\n<br />\r\nL&acirc;m Uy&ecirc;n kh&ocirc;ng cần quan t&acirc;m c&aacute;i n&agrave;y.<br />\r\n<br />\r\nHắn ca kh&uacute;c đảm nhiệm, lại soạn nhạc hiệu quả cũng đ&atilde; c&oacute; sẵn l&agrave;m xong, b&acirc;y giờ y&ecirc;u cầu, chỉ l&agrave; c&ocirc;ng ty cung cấp một c&aacute;i đ&aacute;nh v&agrave;o người mới cuối m&ugrave;a con đường.</p>', '2021-12-09 09:01:06', 17, 1),
(326, 83, 'Bắt đầu đánh Dấu Ba Trăm Năm Tu Vi', '1', '<p>. . .<br />\r\n<br />\r\nNam Ch&acirc;u, Thần Huyền ho&agrave;ng triều.<br />\r\n<br />\r\nThi&ecirc;n Vũ hầu phủ.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng nghĩ đến, ti&ecirc;n đế dĩ nhi&ecirc;n sẽ đem đế vị truyền cho tam ho&agrave;ng tử, thực sự l&agrave; ho&agrave;n to&agrave;n ra khỏi bản hầu dự liệu!&quot;<br />\r\n<br />\r\nTrong hoa vi&ecirc;n, một t&ecirc;n mặt như uy nghi&ecirc;m trung ni&ecirc;n tr&aacute;ng h&aacute;n, chắp hai tay sau lưng, khẽ lắc đầu.<br />\r\n<br />\r\nCảm thấy một tia kh&ocirc;ng thể n&agrave;o hiểu được.<br />\r\n<br />\r\nThần Huyền ho&agrave;ng triều, thống ngự Nam Ch&acirc;u gần như bảy phần mười trở l&ecirc;n thổ địa.<br />\r\n<br />\r\nDưới trướng thần d&acirc;n ng&agrave;n tỉ khoảng cách.<br />\r\n<br />\r\nTam ho&agrave;ng tử, một kh&ocirc;ng thế lực cường đại được khống chế, hai v&ocirc; tuyệt thế đế mới, l&agrave;m sao c&oacute; thể ngồi vững v&agrave;ng đế vị.<br />\r\n<br />\r\nDưới c&aacute;i nh&igrave;n của hắn, ti&ecirc;n đế đạo n&agrave;y &yacute; chỉ, rất c&oacute; khả năng gặp khiến Thần Huyền ho&agrave;ng triều rơi v&agrave;o v&ocirc; bi&ecirc;n rung động b&ecirc;n trong.<br />\r\n<br />\r\nỞ trước mặt, ngồi thẳng một vị l&atilde;o nh&acirc;n.<br />\r\n<br />\r\nL&atilde;o nh&acirc;n một tay chấp tr&agrave;, nhẹ nh&agrave;ng nhấp một miếng.<br />\r\n<br />\r\n&quot;Ti&ecirc;n đế tu vi th&ocirc;ng thi&ecirc;n, gần như Thần Ma cảnh giới, kh&ocirc;ng thể đột nhi&ecirc;n nổ chết!&quot;<br />\r\n<br />\r\n&quot;Việc n&agrave;y, e sợ b&ecirc;n trong c&oacute; kh&ocirc;ng &iacute;t kỳ lạ.&quot;<br />\r\n<br />\r\nL&atilde;o nh&acirc;n c&oacute; &yacute; ri&ecirc;ng.<br />\r\n<br />\r\nThần Huyền ho&agrave;ng triều, ch&iacute;nh l&agrave; Nam Ch&acirc;u cường đại nhất ho&agrave;ng triều.<br />\r\n<br />\r\nTh&agrave;nh tựu Thần Huyền ho&agrave;ng triều ho&agrave;ng đế, lấy ti&ecirc;n đế tu vi, l&agrave;m sao c&oacute; khả năng đang y&ecirc;n đang l&agrave;nh đột nhi&ecirc;n nổ chết.<br />\r\n<br />\r\n&quot;&Yacute; của ngươi l&agrave;?&quot; Thi&ecirc;n Vũ hầu con ngươi thu nhỏ lại.<br />\r\n<br />\r\n&quot;Xuỵt, đo&aacute;n được kh&ocirc;ng cần n&oacute;i, biết rồi kh&ocirc;ng n&ecirc;n hỏi.&quot;<br />\r\n<br />\r\n&quot;Ngươi l&agrave; ho&agrave;ng triều người, nhiệm vụ của ngươi, chủ yếu l&agrave; vững chắc ho&agrave;ng triều thế cuộc.&quot;<br />\r\n<br />\r\n&quot;Bất luận tam ho&agrave;ng tử l&agrave;m thế n&agrave;o, l&agrave;m c&oacute; được hay kh&ocirc;ng, Thần Huyền ho&agrave;ng triều cũng kh&ocirc;ng thể bởi vậy chịu đến bất kỳ ảnh hưởng.&quot;<br />\r\n<br />\r\n&quot;Chỉ c&oacute; như vậy, trong tương lai xuất hiện một loại n&agrave;o đ&oacute; biến cố thời điểm.&quot;<br />\r\n<br />\r\n&quot;Th&acirc;n l&agrave; ho&agrave;ng triều Thi&ecirc;n Vũ hầu ngươi, vừa mới c&oacute; thể b&igrave;nh y&ecirc;n v&ocirc; sự!&quot;<br />\r\n<br />\r\nL&atilde;o nh&acirc;n lưu c&acirc;u tiếp theo ch&acirc;m ng&ocirc;n, th&acirc;n h&igrave;nh lại ở Thi&ecirc;n Vũ hầu trước mặt chậm r&atilde;i v&ocirc; h&igrave;nh, biến mất ở trong hoa vi&ecirc;n.<br />\r\n<br />\r\n&quot;Th&igrave; ra l&agrave; như vậy.&quot; Thi&ecirc;n Vũ hầu nhưng kh&ocirc;ng c&oacute; t&acirc;m tư khiếp sợ những n&agrave;y, cũng hoặc l&agrave; đ&atilde; l&agrave; kh&ocirc;ng cảm thấy kinh ngạc.<br />\r\n<br />\r\nHắn giờ ph&uacute;t n&agrave;y, &yacute; thức được l&atilde;o nh&acirc;n trong giọng n&oacute;i đựng &yacute; tứ sau đột nhi&ecirc;n mạo chảy mồ h&ocirc;i lạnh ướt sũng cả người.<br />\r\n<br />\r\nTrước đ&oacute;, hắn c&ograve;n muốn trạm vị.<br />\r\n<br />\r\nPhụ t&aacute; ch&iacute;nh m&igrave;nh vừa &yacute; ho&agrave;ng tử, lật đổ hiện tại tam ho&agrave;ng tử.<br />\r\n<br />\r\nNhưng hiện tại đến xem.<br />\r\n<br />\r\nN&agrave;y kh&ocirc;ng thể nghi ngờ l&agrave; muốn chết!<br />\r\n<br />\r\n&quot;Ổn định ho&agrave;ng triều thế cuộc sao?&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Xem ra cần tiến cung gặp mặt tam ho&agrave;ng tử một lần!&quot;<br />\r\n<br />\r\nThi&ecirc;n Vũ hầu &aacute;nh mắt đọng lại.<br />\r\n<br />\r\nSau một khắc, th&acirc;n h&igrave;nh của hắn, cũng chậm r&atilde;i biến mất ở trong hoa vi&ecirc;n.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nC&ugrave;ng l&uacute;c đ&oacute;.<br />\r\n<br />\r\nHo&agrave;ng đ&ocirc;.<br />\r\n<br />\r\nTr&aacute;ng lệ trong ho&agrave;ng cung.<br />\r\n<br />\r\nHuyền Minh đầy mặt khiếp sợ ngồi ở Long ỷ b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nTrong hai mắt tất cả đều l&agrave; kh&ocirc;ng d&aacute;m tin tưởng.<br />\r\n<br />\r\nNg&agrave;y h&ocirc;m qua, hắn vẫn l&agrave; Đại Huyền ho&agrave;ng triều gốc g&aacute;c yếu nhất ho&agrave;ng tử.<br />\r\n<br />\r\nCử triều tr&ecirc;n dưới, hầu như kh&ocirc;ng c&oacute; ai cho rằng Huyền Minh cuối c&ugrave;ng c&oacute; thể đăng l&acirc;m đế vị.<br />\r\n<br />\r\nThế lực khắp nơi, hầu như đều sẽ bảo &eacute;p đến hắn ho&agrave;ng tử tr&ecirc;n người.<br />\r\n<br />\r\nĐem Huyền Minh trực tiếp qu&ecirc;n.<br />\r\n<br />\r\nNhưng ngay ở tối ng&agrave;y h&ocirc;m qua.<br />\r\n<br />\r\nTi&ecirc;n đế đột nhi&ecirc;n nổ chết.<br />\r\n<br />\r\nMột tấm th&aacute;nh chỉ, khiến Huyền Minh trực tiếp long b&agrave;o gia th&acirc;n, trở th&agrave;nh Đại Huyền ho&agrave;ng triều t&acirc;n đế.<br />\r\n<br />\r\nNhư vậy cấp tốc, l&agrave;m cho cả Đại Huyền ho&agrave;ng triều, kh&ocirc;ng c&oacute; bất kỳ người n&agrave;o phản ứng lại.<br />\r\n<br />\r\n&quot;Sự t&igrave;nh hơi lớn.&quot;<br />\r\n<br />\r\nSau khi hết khiếp sợ, Huyền Minh từ từ phục hồi tinh thần lại.<br />\r\n<br />\r\nTr&ecirc;n mặt đột nhi&ecirc;n hiện l&ecirc;n một vệt vẻ lo &acirc;u.<br />\r\n<br />\r\nThần Huyền ho&agrave;ng triều đế vị, đại diện cho Nam Ch&acirc;u quyền lực trung t&acirc;m.<br />\r\n<br />\r\nKhống chế ng&agrave;n tỉ sinh linh sinh tử, c&oacute; thể gọi Nam Ch&acirc;u ch&uacute;a tể.<br />\r\n<br />\r\nNhưng m&agrave;, nhưng cũng kh&ocirc;ng l&agrave; đơn giản tới ngồi l&ecirc;n, liền c&oacute; thể nắm giữ quyền lực n&agrave;y.<br />\r\n<br />\r\nĐế vị bản th&acirc;n tồn tại, l&agrave; một loại lợi &iacute;ch giao h&agrave;ng rắc rối phức tạp cơ ch&ecirc;́.<br />\r\n<br />\r\nTh&acirc;n ở đế vị, sau lưng cần phải c&oacute; v&ocirc; số ngang nhau lợi &iacute;ch người chống đỡ mới c&oacute; thể ngồi vững v&agrave;ng.<br />\r\n<br />\r\nM&agrave; hắn, Thần Huyền ho&agrave;ng triều tam ho&agrave;ng tử.<br />\r\n<br />\r\nSau lưng hầu như kh&ocirc;ng c&oacute; bất kỳ thế lực chống đỡ.<br />\r\n<br />\r\nDưới loại cục diện n&agrave;y, muốn ngồi vững v&agrave;ng vị tr&iacute; n&agrave;y, kh&ocirc;ng thể nghi ngờ c&ograve;n kh&oacute; hơn l&ecirc;n trời.<br />\r\n<br />\r\nHuống chi, ở b&ecirc;n cạnh hắn, c&ograve;n c&oacute; r&acirc;́t nhi&ecirc;̀u đối với đế vị mắt nh&igrave;n chằm chằm ho&agrave;ng tử.<br />\r\n<br />\r\nHiện nay hắn mặc d&ugrave; c&oacute; thể y&ecirc;n tĩnh ngồi ở tr&ecirc;n mặt n&agrave;y.<br />\r\n<br />\r\nHo&agrave;n to&agrave;n l&agrave; nh&acirc;n v&igrave; ti&ecirc;n đế th&aacute;nh chỉ qu&aacute; mức cấp tốc.<br />\r\n<br />\r\nDẫn đến những ho&agrave;ng tử n&agrave;y, căn bản cũng kh&ocirc;ng đủ thời gian, đi triệu tập ch&iacute;nh m&igrave;nh sức mạnh sau lưng.<br />\r\n<br />\r\nNhưng theo thời gian tr&ocirc;i đi.<br />\r\n<br />\r\nHuyền Minh những n&agrave;y ca ca, đệ đệ, tuyệt đối sẽ kh&ocirc;ng giảng ho&agrave;.<br />\r\n<br />\r\nĐến l&uacute;c đ&oacute;, ngồi tr&ecirc;n đế vị ch&acirc;n ch&iacute;nh &aacute;p lực, liền sẽ dường như b&agrave;i sơn đảo hải b&igrave;nh thường, hướng Huyền Minh lực &eacute;p m&agrave; tới.<br />\r\n<br />\r\nĐến v&agrave;o l&uacute;c ấy, lui ra đế vị tranh cướp mất mặt c&ograve;n chỉ l&agrave; việc nhỏ.<br />\r\n<br />\r\nKh&ocirc;ng biết được l&agrave;m sao chết, mới l&agrave; đại sự!<br />\r\n<br />\r\nNghĩ đến b&ecirc;n trong, Huyền Minh tr&ecirc;n tr&aacute;n nhất thời b&ocirc;́c l&ecirc;n từng tia từng tia tỉ mỉ mồ h&ocirc;i lạnh.<br />\r\n<br />\r\nHắn xuy&ecirc;n việt đến giới n&agrave;y, đ&atilde; c&oacute; thời gian mười năm.<br />\r\n<br />\r\nBiết r&otilde; đạo quyền lực tranh cướp t&agrave;n khốc.<br />\r\n<br />\r\nNếu l&agrave; sau lưng của hắn , tương tự c&oacute; lượng lớn thế lực chống đỡ, Huyền Minh đến c&ograve;n c&oacute; thể c&ugrave;ng hắn ho&agrave;ng tử đọ sức, t&igrave;m kiếm thời cơ, triệt để vững chắc tự th&acirc;n thống trị.<br />\r\n<br />\r\nNhưng, hắn kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nN&agrave;y liền khiến cho hắn, kh&ocirc;ng c&oacute; sự lựa chọn kh&aacute;c.<br />\r\n<br />\r\n&quot;Ta n&ecirc;n l&agrave;m như thế n&agrave;o, mới c&oacute; thể thể diện đem đế vị để cho người kh&aacute;c?&quot;<br />\r\n<br />\r\n&quot;Đồng thời, an to&agrave;n rời đi quyền lực tranh cướp v&ograve;ng xo&aacute;y?&quot;<br />\r\n<br />\r\nHuyền Minh kh&ocirc;ng khỏi rơi v&agrave;o trầm tư.<br />\r\n<br />\r\nM&agrave; ngay ở Huyền Minh suy tư thời điểm.<br />\r\n<br />\r\nChợt nghe một thanh &acirc;m.<br />\r\n<br />\r\nKeng! Vạn Giới Triệu Ho&aacute;n hệ thống k&iacute;ch hoạt, hiện tại bắt đầu tr&oacute;i chặt k&iacute; chủ!<br />\r\n<br />\r\nKeng! Hệ thống tr&oacute;i chặt th&agrave;nh c&ocirc;ng!<br />\r\n<br />\r\nK&iacute; chủ: Thần Huyền ho&agrave;ng triều ho&agrave;ng đế Huyền Minh!<br />\r\n<br />\r\nKh&ocirc;ng thể cởi tr&oacute;i, kh&ocirc;ng thể thay đổi!<br />\r\n<br />\r\nHệ thống tận sức với chung th&acirc;n phục vụ một vị k&iacute; chủ!<br />\r\n<br />\r\nHệ thống?<br />\r\n<br />\r\nHuyền Minh nguy&ecirc;n bản lo lắng t&acirc;m t&igrave;nh, v&agrave;o đ&uacute;ng l&uacute;c n&agrave;y đột nhi&ecirc;n bị chuỗi n&agrave;y m&aacute;y m&oacute;c tiếng nhắc nhở t&aacute;ch ra.<br />\r\n<br />\r\n&quot;Hệ thống, đến rồi?&quot;<br />\r\n<br />\r\nHuyền Minh t&acirc;m thần chấn động.<br />\r\n<br />\r\n&quot;Hừm, đến rồi k&iacute; chủ.&quot;<br />\r\n<br />\r\nHệ thống đ&aacute;nh một tiếng bắt chuyện.<br />\r\n<br />\r\nHuyền Minh: &quot;. . .&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;N&oacute;i một ch&uacute;t coi, ngươi c&oacute; t&aacute;c dụng g&igrave;.&quot;<br />\r\n<br />\r\nHuyền Minh h&iacute;t một hơi thật s&acirc;u d&ograve; hỏi.<br />\r\n<br />\r\nL&uacute;c n&agrave;y giờ khắc này, leo l&ecirc;n đế vị kinh hoảng, thu được hệ thống kinh hỉ hỗn tạp c&ugrave;ng nhau, khiến nội t&acirc;m của hắn v&ocirc; c&ugrave;ng phức tạp.<br />\r\n<br />\r\nNhưng, hắn b&acirc;y giờ.<br />\r\n<br />\r\nLại lạ kỳ v&ocirc; c&ugrave;ng b&igrave;nh tĩnh.<br />\r\n<br />\r\nMười năm qua, bất kể l&agrave; bản th&acirc;n hắn thi&ecirc;n ph&uacute;, vẫn l&agrave; nh&acirc;n tố kh&aacute;c.<br />\r\n<br />\r\nĐều kh&ocirc;ng c&oacute; thể l&agrave;m cho hắn ở Thần Huyền ho&agrave;ng triều tranh b&aacute; b&ecirc;n trong, được c&aacute;i g&igrave; lợi &iacute;ch.<br />\r\n<br />\r\nCảnh n&agrave;y khiến hắn nhất định phải đem th&acirc;n phận của ch&iacute;nh m&igrave;nh, di giao ra.<br />\r\n<br />\r\nNhưng hiện tại, hệ thống xuất hiện, để hắn &yacute; thức được, cơ hội của ch&iacute;nh m&igrave;nh, khả năng rốt cục muốn tới đến.<br />\r\n<br />\r\n&quot;Hồi bẩm k&iacute; chủ: Hệ thống chủ yếu c&ocirc;ng năng chia l&agrave;m hai loại.&quot;<br />\r\n<br />\r\n&quot;Loại thứ nhất v&igrave; l&agrave; đ&aacute;nh dấu c&ocirc;ng năng: K&iacute; chủ mỗi ng&agrave;y c&oacute; thể tiến h&agrave;nh đ&aacute;nh dấu một lần, mỗi lần đ&aacute;nh dấu khen thưởng t&ugrave;y cơ, c&oacute; tu vi qu&aacute;n đỉnh, c&oacute; k&ecirc;u gọi binh sĩ, c&oacute; thu được kỳ dị bảo vật. . .&quot;<br />\r\n<br />\r\n&quot;Loại thứ hai v&igrave; l&agrave; triệu ho&aacute;n c&ocirc;ng năng: K&iacute; chủ mỗi c&aacute;ch một th&aacute;ng, c&oacute; thể từ chư thi&ecirc;n vạn giới b&ecirc;n trong triệu ho&aacute;n đến một vị Thần Ma gi&aacute;ng l&acirc;m, Thần Ma ho&agrave;n to&agrave;n trung thành với k&iacute; chủ. . .&quot;<br />\r\n<br />\r\n&quot;Đ&aacute;nh dấu c&ugrave;ng triệu ho&aacute;n.&quot;<br />\r\n<br />\r\nHuyền Minh s&aacute;ng mắt l&ecirc;n.<br />\r\n<br />\r\nTrong l&ograve;ng thở ph&agrave;o nhẹ nh&otilde;m.<br />\r\n<br />\r\nHệ thống quả nhi&ecirc;n kh&ocirc;ng c&oacute; để hắn thất vọng.<br />\r\n<br />\r\nN&agrave;y hai hạng c&ocirc;ng năng, nếu l&agrave; hắn c&oacute; thể nắm chắc.<br />\r\n<br />\r\nThần Huyền ho&agrave;ng triều đế vị, đem c&oacute; thể triệt để vững chắc.<br />\r\n<br />\r\n&quot;Keng! C&ugrave;ng ng&agrave;y đ&aacute;nh dấu số lần đ&atilde; qu&eacute;t mới!&quot;<br />\r\n<br />\r\n&quot;K&iacute; chủ c&oacute; hay kh&ocirc;ng lựa chọn đ&aacute;nh dấu!&quot;<br />\r\n<br />\r\n&Acirc;m thanh gợi &yacute; của hệ thống vang l&ecirc;n.<br />\r\n<br />\r\n&quot;Đ&aacute;nh dấu đi.&quot;<br />\r\n<br />\r\nHuyền Minh hơi tựa ở Long ỷ b&ecirc;n tr&ecirc;n, trong l&ograve;ng ph&acirc;n ph&oacute; n&oacute;i.<br />\r\n<br />\r\n&quot;Cũng kh&ocirc;ng biết lần n&agrave;y đ&aacute;nh dấu, sẽ xuất hiện tưởng thưởng g&igrave;.&quot;<br />\r\n<br />\r\nHuyền Minh trong l&ograve;ng hiện l&ecirc;n vẻ mong đợi.<br />\r\n<br />\r\nHắn h&ocirc;m nay, chỉ c&oacute; đế vị gia th&acirc;n.<br />\r\n<br />\r\nNhưng bất kể l&agrave; tu vi, vẫn l&agrave; quyền lực, đều xa xa kh&ocirc;ng đạt tới ch&acirc;n ch&iacute;nh Thần Huyền ho&agrave;ng đế cấp độ.<br />\r\n<br />\r\n&quot;Keng! Ch&uacute;c mừng k&iacute; chủ, đ&aacute;nh dấu ba trăm năm tu vi!&quot;<br />\r\n<br />\r\n. . .</p>', '2022-01-06 18:56:27', 6, 1),
(327, 83, 'Lên Cấp Bán Thánh, Triệu Hoán Hai đại Thần Ma', '2', '<p>. . .<br />\r\n<br />\r\nCh&uacute; th&iacute;ch: Đ&aacute;nh dấu thu được ba trăm năm tu vi.<br />\r\n<br />\r\nL&agrave; lấy k&iacute; chủ bản th&acirc;n thi&ecirc;n ph&uacute; tu h&agrave;nh ba trăm năm sau cảnh giới.<br />\r\n<br />\r\nĐo lường đến k&iacute; chủ thi&ecirc;n ph&uacute;!<br />\r\n<br />\r\n&quot;Ba trăm năm tu vi chuyển h&oacute;a b&ecirc;n trong. . .&quot;<br />\r\n<br />\r\n&quot;Keng! X&aacute;c nhận cảnh giới: B&aacute;n Th&aacute;nh!&quot;<br />\r\n<br />\r\n&quot;C&oacute; hay kh&ocirc;ng lập tức truyền v&agrave;o?&quot;<br />\r\n<br />\r\nHệ thống một loạt tiếng nhắc nhở vang l&ecirc;n.<br />\r\n<br />\r\nB&aacute;n Th&aacute;nh! ?<br />\r\n<br />\r\nHuyền Minh tinh thần chấn động.<br />\r\n<br />\r\nNam Ch&acirc;u tu h&agrave;nh:<br />\r\n<br />\r\nChia l&agrave;m Hậu thi&ecirc;n cảnh, Ti&ecirc;n thi&ecirc;n cảnh, B&aacute;n Th&aacute;nh cảnh, Th&aacute;nh cảnh, Thần Ma cảnh năm đại cảnh giới.<br />\r\n<br />\r\nB&aacute;n Th&aacute;nh tuy rằng chỉ l&agrave; b&ecirc;n trong cảnh giới thứ ba.<br />\r\n<br />\r\nNhưng địa vị c&ugrave;ng với thực lực, xưng h&ocirc; Nam Ch&acirc;u đại l&atilde;o cũng kh&ocirc;ng qu&aacute; đ&aacute;ng.<br />\r\n<br />\r\nỞ b&ecirc;n tr&ecirc;n Th&aacute;nh cảnh b&aacute; chủ, to&agrave;n bộ Nam Ch&acirc;u, đều chỉ c&oacute; bảy vị.<br />\r\n<br />\r\nN&agrave;y bảy vị Th&aacute;nh giả b&ecirc;n trong c&oacute; năm vị quy về Thần Huyền ho&agrave;ng triều.<br />\r\n<br />\r\nĐem đế vị truyền đến Huyền Minh Thần Huyền ti&ecirc;n đế, ch&iacute;nh l&agrave; b&ecirc;n trong một trong.<br />\r\n<br />\r\nC&ograve;n lại s&aacute;u vị Th&aacute;nh giả, bất luận th&acirc;n phận l&agrave; thuộc về Thần Huyền ho&agrave;ng triều, vẫn l&agrave; Thần Huyền ho&agrave;ng triều ở ngo&agrave;i, đều cao cao tại thượng, thế gian sự vật rất kh&oacute; t&aacute;i dẫn l&ecirc;n bọn họ nh&igrave;n kỹ.<br />\r\n<br />\r\nN&oacute;i c&aacute;ch kh&aacute;c, mặc d&ugrave; l&agrave; Huyền Minh mấy vị huynh đệ, sau lưng chống đỡ thế lực của bọn họ người mạnh nhất, cũng vẻn vẹn l&agrave; B&aacute;n Th&aacute;nh th&ocirc;i.<br />\r\n<br />\r\nChỉ cần hiện khi chiếm được n&agrave;y B&aacute;n Th&aacute;nh tu vi.<br />\r\n<br />\r\nHuyền Minh tự th&acirc;n, l&agrave; c&oacute; thể ứng đối tất cả nguy cơ.<br />\r\n<br />\r\n&quot;Lấy thi&ecirc;n ph&uacute; của ta, cần tu h&agrave;nh ba trăm năm, mới c&oacute; thể đạt đến B&aacute;n Th&aacute;nh cảnh giới sao?&quot; Huyền Minh hơi ngưng mắt, tr&ecirc;n mặt nổi l&ecirc;n một tia hoảng hốt.<br />\r\n<br />\r\nBa trăm năm, đối với trường sinh cửu thị tu h&agrave;nh văn minh m&agrave; n&oacute;i.<br />\r\n<br />\r\nC&oacute; điều l&agrave; trong nh&aacute;y mắt vung l&ecirc;n.<br />\r\n<br />\r\nNhưng đối với c&ugrave;ng c&aacute; nh&acirc;n m&agrave; n&oacute;i, nhưng l&agrave; v&ocirc; c&ugrave;ng d&agrave;i l&acirc;u.<br />\r\n<br />\r\nTheo Huyền Minh biết, mặc d&ugrave; đột ph&aacute; Ti&ecirc;n thi&ecirc;n cảnh giới, cao nhất tuổi thọ cũng c&oacute; điều tiếp cận hai trăm năm, ba trăm năm mới tu th&agrave;nh B&aacute;n Th&aacute;nh, đổi l&agrave;m b&igrave;nh thường tốc độ thời gian tr&ocirc;i qua, mang &yacute; nghĩa Huyền Minh đời n&agrave;y, đều kh&ocirc;ng đạt tới tầng thứ n&agrave;y.<br />\r\n<br />\r\n&quot;Truyền v&agrave;o đi, hệ thống.&quot;<br />\r\n<br />\r\nHuyền Minh hơi ch&igrave;m ch&igrave;m kh&iacute;, n&oacute;i rằng.<br />\r\n<br />\r\nHo&agrave;ng triều đế vị, quyền lực mạnh mẽ cũng mang v&agrave;o v&ocirc; c&ugrave;ng nguy hiểm.<br />\r\n<br />\r\nChờ ho&agrave;ng triều mọi người phản ứng lại sau khi.<br />\r\n<br />\r\nKh&ocirc;ng chắc gặp c&oacute; chuyện g&igrave;, nhằm v&agrave;o Huyền Minh bản th&acirc;n.<br />\r\n<br />\r\nLoại n&agrave;y thế cuộc, chỉ c&oacute; tự th&acirc;n trở n&ecirc;n mạnh mẽ.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n<br />\r\nMới c&oacute; thể lẩn tr&aacute;nh tất cả nguy hiểm.<br />\r\n<br />\r\n&quot;Keng! Truyền v&agrave;o bắt đầu!&quot;<br />\r\n<br />\r\n&Acirc;m thanh gợi &yacute; của hệ thống vang l&ecirc;n.<br />\r\n<br />\r\nNhất thời, từ kh&ocirc;ng biết t&ecirc;n hư kh&ocirc;ng tr&agrave;n v&agrave;o lượng lớn tinh khiết năng lượng, hướng về Huyền Minh kinh mạch kh&ocirc;ng ngừng truyền v&agrave;o.<br />\r\n<br />\r\nCũng trong l&uacute;c đ&oacute;, Huyền Minh kh&iacute; tức, kh&ocirc;ng ngừng k&eacute;o l&ecirc;n.<br />\r\n<br />\r\nHậu thi&ecirc;n sơ kỳ. . .<br />\r\n<br />\r\nHậu thi&ecirc;n trung kỳ. . .<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nHậu Thi&ecirc;n vi&ecirc;n m&atilde;n. . .<br />\r\n<br />\r\nTi&ecirc;n thi&ecirc;n sơ kỳ. . .<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nCuối c&ugrave;ng h&igrave;nh ảnh ngắt qu&atilde;ng ở B&aacute;n Th&aacute;nh cấp độ!<br />\r\n<br />\r\nTrong ph&uacute;t chốc, Huyền Minh chỉ cảm thấy trong cơ thể kh&iacute; huyết tăng vọt, trong kinh mạch đầy rẫy sức mạnh to lớn, nguồn sức mạnh n&agrave;y b&ecirc;n dưới, Huyền Minh mơ hồ nhận ra được, ch&iacute;nh m&igrave;nh trong l&uacute;c phất tay, đều c&oacute; thể ph&oacute;ng th&iacute;ch lớn lao uy thế.<br />\r\n<br />\r\n&quot;Thực lực như vậy?&quot;<br />\r\n<br />\r\nHuyền Minh lồng ngực hơi chập tr&ugrave;ng.<br />\r\n<br />\r\n&quot;Keng, đo lường đến k&iacute; chủ c&oacute; một lần Thần Ma triệu ho&aacute;n cơ hội!&quot;<br />\r\n<br />\r\n&quot;C&oacute; hay kh&ocirc;ng tiến h&agrave;nh triệu ho&aacute;n?&quot;<br />\r\n<br />\r\nỞ Huyền Minh tỉ mỉ lĩnh hội tự th&acirc;n sức mạnh thời điểm, &acirc;m thanh gợi &yacute; của hệ thống lần thứ hai vang l&ecirc;n.<br />\r\n<br />\r\n&quot;Sử dụng.&quot;<br />\r\n<br />\r\nHuyền Minh kh&ocirc;ng ch&uacute;t do dự n&oacute;i rằng.<br />\r\n<br />\r\nThần Huyền ho&agrave;ng triều khống chế Nam Ch&acirc;u gần như bảy phần mười diện t&iacute;ch, đất rộng của nhiều, sinh linh ng&agrave;n tỉ khoảng cách, muốn khống chế như thế một to&agrave; thế lực to lớn, vẻn vẹn chỉ l&agrave; B&aacute;n Th&aacute;nh tu vi, c&ograve;n còn thi&ecirc;́u r&acirc;́t nhi&ecirc;̀u.<br />\r\n<br />\r\nHuyền Minh muốn trong thời gian ngắn nhất, để cho m&igrave;nh gốc g&aacute;c trở n&ecirc;n v&ocirc; c&ugrave;ng th&acirc;m hậu, chỉ c&oacute; như vậy, mới c&oacute; thể vững v&agrave;ng đem Thần Huyền ho&agrave;ng triều nắm giữ.<br />\r\n<br />\r\nHơn nữa, th&acirc;n ở ho&agrave;ng triều quyền lực v&ograve;ng xo&aacute;y trung t&acirc;m, Huyền Minh kh&ocirc;ng thể mọi chuyện tự m&igrave;nh đi l&agrave;m, cần đ&aacute;ng gi&aacute; t&iacute;n nhiệm người, thay l&agrave;m việc.<br />\r\n<br />\r\nV&igrave; lẽ đ&oacute;, hệ thống triệu ho&aacute;n Thần Ma chức năng n&agrave;y, đương nhi&ecirc;n sẽ kh&ocirc;ng b&agrave;y đặt kh&ocirc;ng cần.<br />\r\n<br />\r\n&quot;Keng! Ch&iacute;nh đang triệu ho&aacute;n b&ecirc;n trong!&quot;<br />\r\n<br />\r\n&quot;Xin mời k&iacute; chủ sau đ&oacute;. . .&quot;<br />\r\n<br />\r\n&quot;Keng! Triệu ho&aacute;n th&agrave;nh c&ocirc;ng!&quot;<br />\r\n<br />\r\n&quot;Ch&uacute;c mừng k&iacute; chủ, triệu ho&aacute;n đến Thần Ma: C&ocirc; Độc Kiếm &mdash;&mdash; Kiếm Th&aacute;nh! V&ocirc; Danh! .&quot;<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nKiếm Th&aacute;nh! V&ocirc; Danh!<br />\r\n<br />\r\nHuyền Minh nhất thời tinh thần hơi chấn động.<br />\r\n<br />\r\nChỉ l&agrave; trong nh&aacute;y mắt.<br />\r\n<br />\r\nHắn liền tất biết hai người n&agrave;y t&ecirc;n lai lịch!<br />\r\n<br />\r\nPhong V&acirc;n thế giới b&ecirc;n trong, mạnh mẽ nhất hai nh&acirc;n vật.<br />\r\n<br />\r\nT&ecirc;n gọi nam V&ocirc; Danh! Bắc Kiếm Th&aacute;nh!<br />\r\n<br />\r\nỞ h&ugrave;ng b&aacute; kh&ocirc;ng c&oacute; m&acirc;n m&ecirc; trước, hai c&aacute;i nh&acirc;n vật n&agrave;y, vẫn l&agrave; Phong V&acirc;n thế giới trần nh&agrave; tồn tại.<br />\r\n<br />\r\nM&agrave; mặc d&ugrave; l&agrave; h&ugrave;ng b&aacute; quật khởi sau khi.<br />\r\n<br />\r\nĐối với hai c&aacute;i nh&acirc;n vật n&agrave;y cũng l&agrave; hết sức ki&ecirc;ng kỵ.<br />\r\n<br />\r\nC&agrave;ng l&agrave; nam V&ocirc; Danh Vạn Kiếm Quy T&ocirc;ng, to&agrave;n bộ thế giới đều kh&ocirc;ng ai c&oacute; thể tiếp được, c&oacute; thể gọi Phong V&acirc;n thế giới ch&acirc;n ch&iacute;nh sự tồn tại v&ocirc; địch.<br />\r\n<br />\r\nBắc Kiếm Th&aacute;nh so s&aacute;nh với đ&oacute;, cũng kh&ocirc;ng c&oacute; nhược bao nhi&ecirc;u.<br />\r\n<br />\r\nBan đầu thời gian bị nam V&ocirc; Danh V&ocirc; Danh Kiếm Ph&aacute;p đ&aacute;nh bại.<br />\r\n<br />\r\nHậu kỳ, bắc Kiếm Th&aacute;nh t&igrave;m hiểu ra Th&aacute;nh linh thứ hai mươi ba kiếm, ung dung đ&aacute;nh bại h&ugrave;ng b&aacute;.<br />\r\n<br />\r\nChỉ tiếc, bộ c&ocirc;ng ph&aacute;p kia c&oacute; thiếu hụt, vậy th&igrave; l&agrave; th&acirc;n thể.<br />\r\n<br />\r\nCuối c&ugrave;ng cũng bởi vậy mất đi.<br />\r\n<br />\r\n&quot;Hệ thống, triệu ho&aacute;n Thần Ma số lượng kh&ocirc;ng ngừng một vị sao?&quot;<br />\r\n<br />\r\nHuyền Minh trong l&ograve;ng nghi hoặc hỏi.<br />\r\n<br />\r\n&quot;Ch&uacute; giải: Thần Ma triệu ho&aacute;n số lần mỗi th&aacute;ng duy nhất!&quot;<br />\r\n<br />\r\n&quot;Mỗi lần triệu ho&aacute;n Thần Ma số lượng t&ugrave;y cơ!&quot;<br />\r\n<br />\r\nMột đạo m&agrave;n &aacute;nh s&aacute;ng m&agrave;u xanh lam nhạt hiện l&ecirc;n, gợi &yacute; của hệ thống n&oacute;i.<br />\r\n<br />\r\n&quot;C&aacute;i kia mở ra V&ocirc; Danh c&ugrave;ng với Kiếm Th&aacute;nh hai người t&agrave;i liệu c&aacute; nh&acirc;n!&quot;<br />\r\n<br />\r\nHuyền Minh h&iacute;t một hơi thật s&acirc;u ph&acirc;n ph&oacute; n&oacute;i.<br />\r\n<br />\r\nNhất thời một đạo m&agrave;n &aacute;nh s&aacute;ng m&agrave;u xanh lam nhạt hiện l&ecirc;n!<br />\r\n<br />\r\n. . . . . Kiếm Th&aacute;nh t&agrave;i liệu c&aacute; nh&acirc;n. . . . .<br />\r\n<br />\r\n&quot;Thần Ma: C&ocirc; Độc Kiếm &mdash;&mdash; Kiếm Th&aacute;nh!&quot;<br />\r\n<br />\r\n&quot;Chủng tộc: Lo&agrave;i người!&quot;<br />\r\n<br />\r\n&quot;Tu vi: Th&aacute;nh cảnh vi&ecirc;n m&atilde;n!&quot;<br />\r\n<br />\r\n&quot;C&ocirc;ng ph&aacute;p: Th&aacute;nh Linh kiếm ph&aacute;p!&quot;<br />\r\n<br />\r\n&quot;Thần th&ocirc;ng: Kiếm 23!&quot;<br />\r\n<br />\r\n&quot;Vũ kh&iacute;: Th&aacute;nh Linh kiếm!&quot;<br />\r\n<br />\r\n. . . . . V&ocirc; Danh t&agrave;i liệu c&aacute; nh&acirc;n. . . . .<br />\r\n<br />\r\n&quot;Thần Ma: Thi&ecirc;n kiếm V&ocirc; Danh!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Chủng tộc: Lo&agrave;i người!&quot;<br />\r\n<br />\r\n&quot;Tu vi: Th&aacute;nh cảnh vi&ecirc;n m&atilde;n!&quot;<br />\r\n<br />\r\n&quot;C&ocirc;ng ph&aacute;p: V&ocirc; Danh Kiếm Ph&aacute;p!&quot;<br />\r\n<br />\r\n&quot;Thần th&ocirc;ng: V&ocirc; Danh Kiếm Quyết, V&ocirc; Thượng Kiếm Đạo, Vạn Kiếm Quy T&ocirc;ng, V&ocirc; Thi&ecirc;n Kiếm Cảnh, Kiếm Huyết Ph&ugrave; Sinh, Cửu Thi&ecirc;n Kiếm Kh&iacute;, V&ocirc; Thi&ecirc;n Tuyệt Kiếm!&quot;<br />\r\n<br />\r\n&quot;Vũ kh&iacute;: Anh H&ugrave;ng kiếm!&quot;<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\n&quot;Hai c&aacute;i Th&aacute;nh cảnh vi&ecirc;n m&atilde;n!&quot;<br />\r\n<br />\r\nHuyền Minh c&oacute; ch&uacute;t khiếp sợ.<br />\r\n<br />\r\nTh&aacute;nh cảnh vi&ecirc;n m&atilde;n, ch&iacute;nh l&agrave; Nam Ch&acirc;u mạnh nh&acirc;́t cảnh giới.<br />\r\n<br />\r\nCho tới b&ecirc;n tr&ecirc;n Thần Ma cảnh, đ&atilde; gần như ngàn năm chưa từng xuất hiện.<br />\r\n<br />\r\nTi&ecirc;n đế, ch&iacute;nh l&agrave; lấy Th&aacute;nh cảnh cảnh giới vi&ecirc;n m&atilde;n, ngồi chắc Nam Ch&acirc;u đệ nhất Th&aacute;nh giả vị tr&iacute;, cũng l&agrave; hắn bốn vị Th&aacute;nh giả c&oacute; thể gia nhập Thần Huyền ho&agrave;ng triều nguy&ecirc;n nh&acirc;n lớn nhất một trong.<br />\r\n<br />\r\nHiện tại, Huyền Minh dưới trướng th&ecirc;m ra hai vị Th&aacute;nh cảnh vi&ecirc;n m&atilde;n cường giả.<br />\r\n<br />\r\nD&ugrave; cho l&agrave; ti&ecirc;n đế giờ khắc này phục sinh, cũng kh&ocirc;ng thể từ Huyền Minh trong tay đoạt lại đế vị.<br />\r\n<br />\r\nHuống chi l&agrave; người kh&aacute;c.<br />\r\n<br />\r\n&quot;Keng! V&ocirc; Danh, Kiếm Th&aacute;nh hai vị Phong V&acirc;n thế giới Thần Ma, đem ở trong v&ograve;ng một canh giờ t&igrave;m đến k&iacute; chủ b&ecirc;n người!&quot;<br />\r\n<br />\r\n&quot;Ch&uacute; th&iacute;ch: N&agrave;y Thần Ma kh&ocirc;ng phải Nam Ch&acirc;u đại lục Thần Ma, một to&agrave; thế giới tối cường giả đỉnh cao, như thần như ma tồn tại, tức l&agrave; hệ thống triệu ho&aacute;n Thần Ma h&agrave;ng ngũ!&quot;<br />\r\n<br />\r\n&quot;Một c&aacute;i canh giờ sao?&quot;<br />\r\n<br />\r\nHuyền Minh gật gật đầu.<br />\r\n<br />\r\nChỉ l&agrave; một c&aacute;i canh giờ.<br />\r\n<br />\r\nHắn chờ nổi.<br />\r\n<br />\r\n&quot;Cũng kh&ocirc;ng biết, trẫm mấy vị kia huynh đệ, gặp l&agrave;m sao đối mặt cục diện b&acirc;y giờ?&quot;<br />\r\n<br />\r\nHuyền Minh trong l&ograve;ng hơi nghĩ đến.<br />\r\n<br />\r\nTi&ecirc;n đế tr&ecirc;n đời thời gian.<br />\r\n<br />\r\nHắn mấy vị kia huynh đệ, liền vẫn minh tranh &aacute;m đấu.<br />\r\n<br />\r\nKh&ocirc;ng ch&uacute;t n&agrave;o hơn nữa che giấu, đối với tranh cướp đế vị, kh&ocirc;ng thể nghi ngờ l&agrave; t&igrave;nh thế bắt buộc.<br />\r\n<br />\r\nNhưng hiện tại, vị tr&iacute; n&agrave;y rơi v&agrave;o Huyền Minh trong tay.<br />\r\n<br />\r\nE sợ, sự t&igrave;nh tuyệt đối sẽ kh&ocirc;ng dễ d&agrave;ng như vậy kết th&uacute;c.<br />\r\n<br />\r\n&quot;Khởi bẩm bệ hạ, ngo&agrave;i điện Thi&ecirc;n Vũ hầu đại nh&acirc;n cầu kiến!&quot;<br />\r\n<br />\r\n. . .</p>', '2022-01-06 18:57:20', 4, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(329, 85, 'Bày Tỏ, Bị Cự Tuyệt ?', '1', '<p>L&acirc;m Giang th&agrave;nh phố, Th&aacute;nh Trạch giai uyển tiểu khu.<br />\r\n<br />\r\nỞ một c&aacute;i nh&agrave; mờ tối trong ph&ograve;ng ngủ.<br />\r\n<br />\r\nHồn hồn ngạc ngạc Giang Chu từ tr&ecirc;n giường ngồi xuống, tr&ecirc;n mặt viết đầy khiếp sợ.<br />\r\n<br />\r\n&quot;Dĩ nhi&ecirc;n trọng sinh ?&quot;<br />\r\n<br />\r\nHắn nh&igrave;n bốn ph&iacute;a xem hiện tại chỗ ở ho&agrave;n cảnh.<br />\r\n<br />\r\nL&agrave; qu&ecirc; qu&aacute;n của m&igrave;nh.<br />\r\n<br />\r\nB&agrave;y tr&ecirc;n b&agrave;n quạt điện vẫn c&ograve;n ở vận chuyển.<br />\r\n<br />\r\nVang l&ecirc;n tiếng ong ong, kh&ocirc;ng ngừng b&atilde;i đầu.<br />\r\n<br />\r\nĐ&acirc;y hết thảy tr&agrave;ng cảnh, đối với Giang Chu đều vừa quen thuộc lại vừa xa lạ.<br />\r\n<br />\r\nTrước khi trọng sinh Giang Chu đ&atilde; 35 tuổi.<br />\r\n<br />\r\nL&agrave; một nh&agrave; c&ocirc;ng ty nước ngo&agrave;i cao quản, lương một năm gần ngh&igrave;n vạn.<br />\r\n<br />\r\nĐại ni&ecirc;n m&ugrave;ng s&aacute;u uống say, ở trong đường cống ngầm n&atilde; ph&aacute;o.<br />\r\n<br />\r\nKết quả dẫn hỏa giếng lộ tr&igrave;nh kh&iacute; m&ecirc;-tan, xảy ra bạo tạc.<br />\r\n<br />\r\nHắn c&ograve;n cho l&agrave; m&igrave;nh chết chắc rồi.<br />\r\n<br />\r\nKh&ocirc;ng nghĩ tới l&atilde;o thi&ecirc;n rốt cuộc lại l&agrave;m cho hắn một lần nữa sống rồi một lần.<br />\r\n<br />\r\nNhưng v&agrave;o l&uacute;c n&agrave;y.<br />\r\n<br />\r\nGiang Chu cảm thấy tr&ecirc;n mặt m&igrave;nh dường như c&oacute; Băng Băng l&agrave;nh lạnh bọt nước.<br />\r\n<br />\r\n&quot;L&agrave; nước mắt ?&quot;<br />\r\n<br />\r\nHắn d&ugrave;ng tay sờ một c&aacute;i, trong l&ograve;ng nhất thời run l&ecirc;n.<br />\r\n<br />\r\n&quot;Chẳng lẽ ta trọng sinh ở tại ta mẹ qua đời ng&agrave;y n&agrave;o đ&oacute; ? !&quot;<br />\r\n<br />\r\nGiang Chu s&acirc;u hấp một khẩu kh&iacute;, quay đầu liền thấy tr&ecirc;n b&agrave;n nhạ cơ &aacute; 523 0.<br />\r\n<br />\r\nMở điện thoại di động l&ecirc;n, ng&agrave;y th&aacute;ng l&agrave; 0 9 năm ngày 11 tháng 6.<br />\r\n<br />\r\nNg&agrave;y n&agrave;y Giang Chu rất quen thuộc, l&agrave; hắn mới vừa thi đại học xong ng&agrave;y thứ ba.<br />\r\n<br />\r\nPhụ mẫu b&igrave;nh an v&ocirc; sự, ch&iacute;nh m&igrave;nh thi cũng kh&ocirc;ng tệ.<br />\r\n<br />\r\nC&aacute;i kia m&igrave;nh tại sao mang theo nước mắt liền ngủ mất rồi hả?<br />\r\n<br />\r\nTrở lại mười t&aacute;m tuổi.<br />\r\n<br />\r\nTr&iacute; nhớ của hắn hỗn loạn giống như l&agrave; một đo&agrave;n tương hồ.<br />\r\n<br />\r\nCăn bản nhớ kh&ocirc;ng nổi 0 9 năm ngày 11 tháng 6 tại sao m&igrave;nh muốn rơi lệ.<br />\r\n<br />\r\nBất qu&aacute; kh&ocirc;ng chờ hắn ngẫm nghĩ, hắn liền từ tr&ecirc;n điện thoại di động thấy được đ&aacute;p &aacute;n.<br />\r\n<br />\r\nT&iacute;ch t&iacute;ch t&iacute;ch &mdash;&mdash;<br />\r\n<br />\r\n&quot;L&atilde;o Giang, ngươi cũng một ng&agrave;y kh&ocirc;ng c&oacute; trở về tin tức ta, kh&ocirc;ng c&oacute; sao chứ ?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải l&agrave; bị Sở Ngữ Vi cự tuyệt sao? Kh&ocirc;ng cần thiết muốn sống muốn chết.&quot;<br />\r\n<br />\r\n&quot;Trường học ch&uacute;ng ta, ai kh&ocirc;ng c&oacute; bị n&agrave;ng cự tuyệt qu&aacute; ?&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\n&quot;Muốn đều giống như ngươi vậy, th&agrave;nh nam THPT đều th&agrave;nh b&atilde;i tha ma!&quot;<br />\r\n<br />\r\nGiang Chu nh&igrave;n lấy tr&ecirc;n điện thoại di động kh&ocirc;ng ngừng bắn ra qq tin tức, trong nh&aacute;y mắt nhớ lại c&aacute;i n&agrave;y ảnh ch&acirc;n dung chủ nh&acirc;n.<br />\r\n<br />\r\nHắn gọi Qu&aacute;ch Vĩ, l&agrave; m&igrave;nh THPT thời kỳ bạn b&egrave;.<br />\r\n<br />\r\nHai người l&agrave; c&ugrave;ng lớp đồng học, t&iacute;nh c&aacute;ch t&iacute;nh kh&iacute; cũng tương đối ăn &yacute;.<br />\r\n<br />\r\nBất qu&aacute; bởi v&igrave; đại học kh&ocirc;ng ở c&ugrave;ng l&agrave; một c&aacute;i th&agrave;nh thị, sở dĩ dần dần cũng liền mất đi li&ecirc;n hệ.<br />\r\n<br />\r\nBất qu&aacute; hắn n&oacute;i Sở Ngữ Vi l&agrave; ai ?<br />\r\n<br />\r\nGiang Chu tỉ mỉ suy tư một ch&uacute;t, k&yacute; ức lập tức kh&ocirc;i phục.<br />\r\n<br />\r\nSở Ngữ Vi, th&agrave;nh nam trung học phổ th&ocirc;ng hoa kh&ocirc;i.<br />\r\n<br />\r\nC&oacute; như ma quỷ v&oacute;c người ho&agrave;n mỹ.<br />\r\n<br />\r\nLại vẫn cứ sinh một tấm tinh xảo đến tinh khiết muốn v&ocirc; địch mối t&igrave;nh đầu khu&ocirc;n mặt.<br />\r\n<br />\r\nTHPT ba năm, kh&ocirc;ng biết m&ecirc; đảo bao nhi&ecirc;u học sinh nam.<br />\r\n<br />\r\nTrong n&agrave;y đương nhi&ecirc;n cũng bao qu&aacute;t Giang Chu.<br />\r\n<br />\r\nNghĩ tới đ&acirc;y, Giang Chu trong nh&aacute;y mắt biết m&igrave;nh tr&ecirc;n mặt v&igrave; sao c&oacute; lệ.<br />\r\n<br />\r\nBởi v&igrave; ngày 11 tháng 6, hắn b&agrave;y tỏ thất bại.<br />\r\n<br />\r\nM&agrave; cự tuyệt hắn đối tượng ch&iacute;nh l&agrave; Sở Ngữ Vi.<br />\r\n<br />\r\nChỉ bất qu&aacute; đại học thời điểm, Giang Chu c&oacute; mới &aacute;i t&igrave;nh.<br />\r\n<br />\r\nCho n&ecirc;n đối với Sở Ngữ Vi k&yacute; ức, hắn cũng liền từng bước qu&ecirc;n l&atilde;ng.<br />\r\n<br />\r\n&quot;Kh&aacute; lắm, kh&ocirc;ng nghĩ tới ta c&aacute;i kia thời gian như thế th&acirc;m t&igrave;nh.&quot;<br />\r\n<br />\r\n&quot;Ngủ thấy đều kh&oacute;c, thật tmd hết con b&ecirc;.&quot;<br />\r\n<br />\r\nGiang Chu c&oacute; ch&uacute;t dở kh&oacute;c dở cười.<br />\r\n<br />\r\nTuy l&agrave; hắn b&acirc;y giờ th&acirc;n thể tuổi t&aacute;c mười t&aacute;m tuổi.<br />\r\n<br />\r\nThế nhưng linh hồn cũng đ&atilde; 35 tuổi.<br />\r\n<br />\r\nĐối với c&aacute;i n&agrave;y chủng t&igrave;nh &aacute;i c&aacute;c loại đồ đạc hắn đ&atilde; sớm nh&igrave;n thấu.<br />\r\n<br />\r\nHơn nữa Giang Chu biết r&otilde;, m&igrave;nh v&agrave; cuộc đời n&agrave;y th&iacute;ch nhất nữ h&agrave;i sẽ ở đại học gặp nhau.<br />\r\n<br />\r\nSở dĩ hắn nhất thời nhớ kh&ocirc;ng ra th&igrave; sao Sở Ngữ Vi c&aacute;i n&agrave;y nh&acirc;n loại cũng t&igrave;nh hữu khả nguy&ecirc;n.<br />\r\n<br />\r\nGiang Chu mở ra khung chat: &quot;Y&ecirc;n t&acirc;m đi đại l&acirc;m, ta kh&ocirc;ng sao.&quot;<br />\r\n<br />\r\n&quot;Dựa v&agrave;o, ngươi c&oacute; thể t&iacute;nh nhắn lại, thực sự kh&ocirc;ng c&oacute; việc g&igrave; ?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải một c&aacute;i THPT hoa kh&ocirc;i, ta kh&ocirc;ng để bụng, cuộc sống đại học mới tốt.&quot;<br />\r\n<br />\r\nQu&aacute;ch Vĩ hồi phục sắp tới tới: &quot;Ngươi lại c&ograve;n chưa l&ecirc;n đại học, l&agrave;m sao biết nơi đ&oacute; sinh hoạt tốt ?&quot;<br />\r\n<br />\r\n&quot;Ngươi dấu chấm c&oacute; chuyện, ta n&oacute;i ch&iacute;nh l&agrave; sinh vi&ecirc;n.&quot;<br />\r\n<br />\r\n&quot;????&quot;<br />\r\n<br />\r\nGiang Chu nhẹ giọng cười, lập tức nhảy xuống giường.<br />\r\n<br />\r\nNhưng v&agrave;o l&uacute;c n&agrave;y, hắn bị tr&ecirc;n đầu giường một tấm hấp dẫn.<br />\r\n<br />\r\nTr&ecirc;n giấy viết rậm rạp chằng chịt m&acirc;́y dòng chữ.<br />\r\n<br />\r\nMới đầu ti&ecirc;u đề &mdash;&mdash; Di Thư.<br />\r\n<br />\r\n&laquo; xin lỗi, ba mẹ, ta bị ta th&iacute;ch nữ h&agrave;i cự tuyệt.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; n&agrave;ng, ta thực sự sống kh&ocirc;ng nổi.<br />\r\n<br />\r\nBởi v&igrave; ta thực sự rất y&ecirc;u n&agrave;ng, so với y&ecirc;u ta m&igrave;nh cũng y&ecirc;u.<br />\r\n<br />\r\nKh&ocirc;ng n&ecirc;n tr&aacute;ch n&agrave;ng, hết thảy đều l&agrave; ta quyết định của ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\nBởi v&igrave; ta muốn d&ugrave;ng sinh mệnh chứng minh, phần của ta đ&acirc;y y&ecirc;u c&oacute; bao nhi&ecirc;u nhiệt liệt &raquo;<br />\r\n<br />\r\nGiang Chu nh&igrave;n lấy phần n&agrave;y &quot;Di Thư &quot; nội dung, k&eacute;m ch&uacute;t chửi ầm l&ecirc;n.<br />\r\n<br />\r\nHắn đều đ&atilde; qu&ecirc;n m&igrave;nh ban đầu lại l&agrave; một liếm cẩu.<br />\r\n<br />\r\nLau, v&igrave; một nữ nh&acirc;n liền bu&ocirc;ng tha nh&acirc;n sinh.<br />\r\n<br />\r\nĐ&acirc;y ch&iacute;nh l&agrave; thanh ni&ecirc;n nh&acirc;n ?<br />\r\n<br />\r\nQu&aacute; kỷ ah xấu hổ chứ ?<br />\r\n<br />\r\nL&agrave;m nam nh&acirc;n n&ecirc;n l&agrave;m Hải Vương a!<br />\r\n<br />\r\nK&eacute;m nhất cũng phải nhiều liếm mấy c&aacute;i, l&agrave;m hải cẩu a.<br />\r\n<br />\r\nBất qu&aacute;. . .<br />\r\n<br />\r\nCh&iacute;nh m&igrave;nh Di Thư đều viết, thế n&agrave;o kh&ocirc;ng chết đ&acirc;u ?<br />\r\n<br />\r\nGiang Chu suy nghĩ một ch&uacute;t, lập tức liền nhớ lại ng&agrave;y n&agrave;o đ&oacute;.<br />\r\n<br />\r\nMười t&aacute;m tuổi ch&iacute;nh m&igrave;nh dường như viết xong Di Thư liền hối hận.<br />\r\n<br />\r\nBằng kh&ocirc;ng cũng sẽ kh&ocirc;ng mở ra quạt điện ngủ, c&ograve;n sợ m&igrave;nh bị nhiệt tỉnh.<br />\r\n<br />\r\n&Acirc;n, c&aacute;i n&agrave;y c&ograve;n c&oacute; điểm giống l&agrave; m&igrave;nh.<br />\r\n<br />\r\nNếu l&agrave; thật v&igrave; một nữ nh&acirc;n chết rồi liền kh&ocirc;ng đ&aacute;ng gi&aacute;.<br />\r\n<br />\r\nHuống chi hắn đối với Sở Ngữ Vi th&iacute;ch, thực sự kh&ocirc;ng c&oacute; trong di thư viết s&acirc;u như vậy.<br />\r\n<br />\r\nNhưng v&agrave;o l&uacute;c n&agrave;y, Giang Chu cửa ph&ograve;ng bỗng nhi&ecirc;n bị đẩy ra.<br />\r\n<br />\r\nVi&ecirc;n Hữu Cầm nữ sĩ cầm c&aacute;i chổi đi đến, kinh ngạc nh&igrave;n Giang Chu liếc mắt.<br />\r\n<br />\r\n&quot;X&uacute; tiểu tử, ngươi tại sao c&ograve;n trong nh&agrave; ?&quot;<br />\r\n<br />\r\n&quot;Mẹ. . .&quot;<br />\r\n<br />\r\nGiang Chu c&oacute; ch&uacute;t k&iacute;ch động đứng l&ecirc;n, cho n&agrave;ng một c&aacute;i to lớn gấu &ocirc;m.<br />\r\n<br />\r\nNữ nh&acirc;n trước mặt l&agrave; mẹ của hắn.<br />\r\n<br />\r\nTại hắn mới vừa l&ecirc;n năm thứ hai đại học thời điểm qua đời.<br />\r\n<br />\r\nL&agrave;m cho Giang Chu trọn kh&oacute; qua v&agrave;i chục năm.<br />\r\n<br />\r\nVi&ecirc;n Hữu Cầm sờ đầu hắn một c&aacute;i: &quot;Bỗng nhi&ecirc;n &ocirc;m ta l&agrave;m c&aacute;i g&igrave;, kh&ocirc;ng c&oacute; ph&aacute;t sốt &agrave;?&quot;<br />\r\n<br />\r\n&quot;Mẹ, ta đ&atilde; v&agrave;i chục năm chưa thấy qua ngươi. . .&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small><br />\r\n<br />\r\n&quot;Bệnh t&acirc;m thần, n&oacute;i c&aacute;i g&igrave; m&ecirc; sảng đ&acirc;y.&quot;<br />\r\n<br />\r\nGiang Chu bu&ocirc;ng mẫu th&acirc;n ra: &quot;Đ&uacute;ng rồi, ba của ta đ&acirc;u ?&quot;<br />\r\n<br />\r\n&quot;Đi l&agrave;m a, ngược lại l&agrave; ngươi, tại sao c&ograve;n trong nh&agrave; ?&quot;<br />\r\n<br />\r\n&quot;Ta kh&ocirc;ng ở nh&agrave; ở nơi n&agrave;o ?&quot;<br />\r\n<br />\r\n&quot;Trường học ng&agrave;y h&ocirc;m qua kh&ocirc;ng phải th&ocirc;ng tri, cho c&aacute;c ngươi đi lấy bằng tốt nghiệp, ngươi sẽ kh&ocirc;ng c&ograve;n chưa c&oacute; đi chứ ?&quot;<br />\r\n<br />\r\nGiang Chu nhức đầu: &quot;C&oacute; thể l&agrave; ngủ bối rối, ta đ&acirc;y sẽ đi ngay b&acirc;y giờ ah.&quot;<br />\r\n<br />\r\n&quot;Nhanh đi, như thế n&agrave;y trở về gi&uacute;p ta bao sủi cảo.&quot;<br />\r\n<br />\r\n&quot;Tốt, ta đ&acirc;y kỵ xa đi qua!&quot;<br />\r\n<br />\r\nGiang Chu n&oacute;i xong, lập tức ra khỏi nh&agrave;.<br />\r\n<br />\r\nHắn vừa đi, một b&ecirc;n ở trong đầu kh&ocirc;ng ngừng suy nghĩ.<br />\r\n<br />\r\nV&otilde;ng Văn tiểu thuyết hắn nh&igrave;n rất nhiều.<br />\r\n<br />\r\nĐối với trọng sinh đương nhi&ecirc;n cũng rất c&oacute; nghi&ecirc;n cứu.<br />\r\n<br />\r\nSống lại người, nhất định l&agrave; muốn g&acirc;y dựng sự nghiệp ph&aacute;t t&agrave;i ngh&ecirc;nh cưới bạch ph&uacute; mỹ.<br />\r\n<br />\r\nNhưng l&agrave;. . . Ch&iacute;nh m&igrave;nh muốn thế n&agrave;o ph&aacute;t t&agrave;i ?<br />\r\n<br />\r\n0 9 năm tin tức m&igrave;nh biết cũng kh&ocirc;ng phải rất nhiều &agrave;?<br />\r\n<br />\r\n0 9 năm. . .<br />\r\n<br />\r\nDường như Nam Cực khoa thi đứng &quot;C&ocirc;n L&ocirc;n đứng&quot; x&acirc;y xong ?<br />\r\n<br />\r\nBắt Chim C&aacute;nh Cụt b&aacute;n ?<br />\r\n<br />\r\nKh&ocirc;ng được, kh&ocirc;ng thực tế.<br />\r\n<br />\r\n0 9 năm. . .<br />\r\n<br />\r\nT&agrave;o Th&aacute;o mộ dường như bị ph&aacute;t hiện.<br />\r\n<br />\r\nTrộm điểm văn vật ?<br />\r\n<br />\r\nT&ecirc;, c&oacute; điểm nguy hiểm a.<br />\r\n<br />\r\n0 9 năm. . .<br />\r\n<br />\r\nHải tặc Somalia bắt c&oacute;c rồi Thi&ecirc;n Lang tinh h&agrave;o thuyền chở dầu, muốn 300 vạn tiền chuộc.<br />\r\n<br />\r\nGia nhập v&agrave;o bọn họ ?<br />\r\n<br />\r\nGiang Chu c&agrave;ng muốn khu&ocirc;n mặt c&agrave;ng hắc.<br />\r\n<br />\r\nCh&iacute;nh m&igrave;nh to&agrave;n bộ hết kh&ocirc;ng nhớ r&otilde; bất luận c&aacute;i g&igrave; tin tức hữu dụng.<br />\r\n<br />\r\nSớm biết liền xem nhiều điểm tin tức.<br />\r\n<br />\r\n&laquo; s&aacute;ch mới c&ocirc;ng bố, phiếu đ&aacute;nh gi&aacute; &raquo;</p>\r\n\r\n<p>&nbsp;</p>', '2022-05-02 01:51:05', 16, 1),
(330, 86, 'Chết Sau Lập Tức Liền Có Thể Biến đại Lão', '1', '<p>Trung ch&acirc;u, C&agrave;n quốc, kinh th&agrave;nh.<br />\r\n<br />\r\nMột c&aacute;i kh&aacute;ch sạn b&ecirc;n trong.<br />\r\n<br />\r\nT&acirc;n khoa trạng nguy&ecirc;n Ngụy Qu&acirc;n từ mộng b&ecirc;n trong tỉnh lại.<br />\r\n<br />\r\nSau đ&oacute;, th&acirc;n thể bắt đầu rất nhỏ run rẩy.<br />\r\n<br />\r\nKh&ocirc;ng phải sợ h&atilde;i, m&agrave; l&agrave; k&iacute;ch động.<br />\r\n<br />\r\nNgụy Qu&acirc;n vốn cho rằng h&ocirc;m qua hắn cao trung trạng nguy&ecirc;n cưỡi ngựa dạo phố sẽ l&agrave; hắn người sinh đỉnh phong, nhưng l&agrave; h&ocirc;m nay hắn đ&atilde; thay đổi &yacute; nghĩ.<br />\r\n<br />\r\nTrạng nguy&ecirc;n tính là cái g&igrave; chứ.<br />\r\n<br />\r\nHắn lập tức ch&iacute;nh l&agrave; thi&ecirc;n đế.<br />\r\n<br />\r\nHắn đ&atilde; thức tỉnh t&uacute;c thế k&yacute; ức.<br />\r\n<br />\r\nHắn, Ngụy Qu&acirc;n, ch&iacute;nh l&agrave; chư thi&ecirc;n vạn giới cường đại nhất l&atilde;o một trong thi&ecirc;n đế chuyển thế.<br />\r\n<br />\r\nChuyển thế ph&iacute;a trước thi&ecirc;n đế th&agrave;nh lập thi&ecirc;n đ&igrave;nh ph&uacute;c xạ chư thi&ecirc;n vạn giới, dưới trướng cường giả v&ocirc; số, l&agrave; vạn giới mạnh nhất đại l&atilde;o một trong.<br />\r\n<br />\r\nNhưng l&agrave; tất lại c&ograve;n c&oacute; c&aacute;i một trong.<br />\r\n<br />\r\nĐ&atilde; l&agrave; thi&ecirc;n đế, l&agrave;m độc nhất v&ocirc; nhị.<br />\r\n<br />\r\nThi&ecirc;n đế c&oacute; đại quyết t&acirc;m, đại nghị lực, đại thần th&ocirc;ng, hắn biết th&ocirc;ng thường tu luyện thủ đoạn kh&ocirc;ng c&oacute; khả năng l&agrave;m hắn vượt qua những c&aacute;i đ&oacute; c&ugrave;ng hắn c&ugrave;ng một c&aacute;i cấp độ đại l&atilde;o, lại th&ecirc;m một số phức tạp nguy&ecirc;n nh&acirc;n, cuối c&ugrave;ng hắn lựa chọn lịch kiếp tr&ugrave;ng tu:<br />\r\n<br />\r\nLu&acirc;n hồi chuyển thế mười vạn lần, lịch kiếp vi&ecirc;n m&atilde;n, hắn liền c&oacute; thể đột ph&aacute; trước đ&oacute; cảnh giới, tiến th&ecirc;m một bước, th&agrave;nh l&agrave; ch&acirc;n ch&iacute;nh ch&iacute; cao v&ocirc; thượng thi&ecirc;n đế.<br />\r\n<br />\r\nM&agrave; một thế n&agrave;y, đ&atilde; l&agrave; Ngụy Qu&acirc;n chuyển thế thứ mười vạn lần.<br />\r\n<br />\r\nBởi v&igrave; sắp c&ocirc;ng h&agrave;nh vi&ecirc;n m&atilde;n, cho n&ecirc;n thi&ecirc;n đế trước ti&ecirc;n thiết tr&iacute; hậu thủ khởi động, l&agrave;m Ngụy Qu&acirc;n tại một thế n&agrave;y hiểu r&otilde; tiền căn hậu quả.<br />\r\n<br />\r\nĐối với thứ mười vạn lần chuyển thế, Ngụy Qu&acirc;n chỉ cần l&agrave;m một việc l&agrave; được rồi:<br />\r\n<br />\r\nChết sớm sớm si&ecirc;u sinh!<br />\r\n<br />\r\nĐối với c&aacute;i n&agrave;y, Ngụy Qu&acirc;n hết sức h&agrave;i l&ograve;ng.<br />\r\n<br />\r\nThi&ecirc;n đế đem hết thảy đều t&iacute;nh tới, hắn cũng lo lắng những c&aacute;i đ&oacute; c&ugrave;ng hắn một c&aacute;i cấp độ đại l&atilde;o hội tra được hắn chuyển thế dấu vết để lại, tại hắn an b&agrave;i cuối c&ugrave;ng một thế l&agrave;m ph&aacute; hư, l&agrave;m hắn thất bại trong gang tấc, cho n&ecirc;n thi&ecirc;n đế thứ mười vạn lần chuyển thế bản th&acirc;n li&ecirc;̀n là d&ugrave;ng để m&ecirc; hoặc địch nh&acirc;n.<br />\r\n<br />\r\nN&ecirc;n l&agrave;m sự t&igrave;nh, n&ecirc;n chuẩn bị l&agrave;m việc, ph&iacute;a trước ch&iacute;n vạn ch&iacute;n ng&agrave;n ch&iacute;n trăm ch&iacute;n mươi ch&iacute;n lần chuyển thế đều đ&atilde; l&agrave;m xong. Thứ mười vạn lần chuyển thế cần phải l&agrave;m liền chỉ c&oacute; một việc &mdash;&mdash; chết bởi người kh&aacute;c tay b&ecirc;n trong.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nMượn nhờ địch nh&acirc;n tay, trợ ch&iacute;nh m&igrave;nh th&agrave;nh đạo.<br />\r\n<br />\r\nNgụy Qu&acirc;n cấp thi&ecirc;n đế điểm một c&aacute;i t&aacute;n, quả nhi&ecirc;n Lang Gia Bảng ( lyb ).<br />\r\n<br />\r\nC&ograve;n sống rất kh&oacute;, muốn t&igrave;m c&aacute;i chết c&ograve;n kh&ocirc;ng dễ d&agrave;ng sao?<br />\r\n<br />\r\nMặc d&ugrave; dựa theo giả thiết kh&ocirc;ng thể tự s&aacute;t, nhưng l&agrave; c&aacute;i n&agrave;y thế giới v&ocirc; số cao thủ, chiến tranh thường xuy&ecirc;n, Ngụy Qu&acirc;n muốn chết, thi&ecirc;n vương l&atilde;o tử đều ngăn kh&ocirc;ng được hắn.<br />\r\n<br />\r\nChết liền c&oacute; thể biến th&agrave;nh vạn giới v&ocirc; địch thi&ecirc;n đế, hơn nữa kỳ thật cũng sẽ kh&ocirc;ng biến th&agrave;nh một người kh&aacute;c, bởi v&igrave; hắn ch&acirc;n linh vẫn lu&ocirc;n ch&iacute;nh l&agrave; thi&ecirc;n đế ch&acirc;n linh, Ngụy Qu&acirc;n ch&iacute;nh l&agrave; thi&ecirc;n đế, thi&ecirc;n đế ch&iacute;nh l&agrave; Ngụy Qu&acirc;n, cho n&ecirc;n Ngụy Qu&acirc;n đối tử vong kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o e ngại, ngược lại tr&agrave;n đầy chờ mong.<br />\r\n<br />\r\nNgụy Qu&acirc;n bắt đầu nghi&ecirc;m t&uacute;c suy nghĩ, ch&iacute;nh m&igrave;nh muốn l&agrave;m thế n&agrave;o mới c&oacute; thể mau ch&oacute;ng muốn chết?<br />\r\n<br />\r\nBất qu&aacute; vẻn vẹn suy tư chỉ chốc l&aacute;t, Ngụy Qu&acirc;n liền từ bỏ.<br />\r\n<br />\r\nBởi v&igrave; t&igrave;m đường chết phương ph&aacute;p nhiều lắm.<br />\r\n<br />\r\nC&aacute;i n&agrave;y nếu l&agrave; c&ograve;n cần đặc biệt dụng t&acirc;m kh&aacute;c c&acirc;n nhắc, Ngụy Qu&acirc;n cảm thấy ch&iacute;nh l&agrave; tại vũ nhục ch&iacute;nh m&igrave;nh th&ocirc;ng minh.<br />\r\n<br />\r\nT&ugrave;y cơ ứng biến l&agrave; được rồi, hắn ch&iacute; &iacute;t c&oacute; một trăm loại kh&aacute;c biệt hoa văn t&igrave;m đường chết biện ph&aacute;p.<br />\r\n<br />\r\nKết quả đơn giản ch&iacute;nh l&agrave; tại c&aacute;i n&agrave;y thế giới nhiều sống một ng&agrave;y c&ograve;n l&agrave; hai ng&agrave;y kh&aacute;c nhau.<br />\r\n<br />\r\nMặc d&ugrave; thi&ecirc;n đế đại bộ phận k&yacute; ức c&ograve;n tại phong ấn trạng th&aacute;i, Ngụy Qu&acirc;n chỉ quay lại tr&iacute; nhớ kiếp trước c&ugrave;ng thi&ecirc;n đế lu&acirc;n hồi chuyển thế tiền căn hậu quả, d&ugrave; sao Ngụy Qu&acirc;n một thế n&agrave;y l&agrave; y&ecirc;u cầu chết, kh&ocirc;ng cần phải sử dụng tr&iacute; nhớ l&uacute;c trước tới mạnh l&ecirc;n, nhưng l&agrave; Ngụy Qu&acirc;n t&acirc;m t&iacute;nh vẫn như cũ so trước đ&oacute; thu được cự đại tăng l&ecirc;n.<br />\r\n<br />\r\nMười vạn lần lu&acirc;n hồi đều trải qua, kh&ocirc;ng quan t&acirc;m nhất thời thời gian tr&ocirc;i qua.<br />\r\n<br />\r\nLại mở mang kiến thức th&ecirc;m c&aacute;i n&agrave;y thế giới phong cảnh lại khẳng kh&aacute;i chịu chết cũng được.<br />\r\n<br />\r\nNgụy Qu&acirc;n mới vừa nghĩ như vậy, cửa gian ph&ograve;ng liền bị g&otilde;.<br />\r\n<br />\r\n&quot;Ngụy huynh, Ngụy huynh, Ngụy huynh.&quot;<br />\r\n<br />\r\n&quot;Đến rồi.&quot;<br />\r\n<br />\r\nNgụy Qu&acirc;n đ&aacute;p ứng, đứng dậy sửa lại một ch&uacute;t ch&iacute;nh m&igrave;nh quần &aacute;o, sau đ&oacute; liền mở cửa ph&ograve;ng.<br />\r\n<br />\r\n&Aacute;nh v&agrave;o hắn tầm mắt, l&agrave; hai c&aacute;i rất quen thuộc đồng m&ocirc;n bạn tốt &mdash;&mdash; Từ Đức c&ugrave;ng Th&aacute;i Kỳ L&acirc;m.<br />\r\n<br />\r\nBa người bọn họ đều l&agrave; Quốc Tử gi&aacute;m học sinh, quan hệ rất tốt, m&agrave; Từ Đức c&ugrave;ng Th&aacute;i Kỳ L&acirc;m tại lần n&agrave;y khoa cử cũng đều đậu tiến sĩ, bất qu&aacute; xếp hạng tự nhi&ecirc;n l&agrave; kh&ocirc;ng c&oacute; Ngụy Qu&acirc;n hảo, hai người bọn họ chỉ l&agrave; nhị gi&aacute;p.<br />\r\n<br />\r\nD&ugrave; vậy, cũng l&agrave; đ&aacute;ng l&agrave;m rạng rỡ tổ t&ocirc;ng sự t&igrave;nh. N&agrave;y nếu l&agrave; đặt tại hắn kiếp trước, ba người cơ bản đều l&agrave; Thanh Bắc tốt nghiệp lại thi đậu c&ocirc;ng chức thi&ecirc;n chi ki&ecirc;u tử, cho n&ecirc;n ba người đều thực hưng phấn.<br />\r\n<br />\r\nM&agrave; hai người l&uacute;c n&agrave;y t&igrave;m đến Ngụy Qu&acirc;n, cũng l&agrave; để ăn mừng.<br />\r\n<br />\r\n&quot;Ngụy huynh, đi th&ocirc;i, đi Diệu &Acirc;m phường, ta thế nhưng l&agrave; đ&atilde; mong đợi nhiều năm.&quot; Th&aacute;i Kỳ L&acirc;m hướng Ngụy Qu&acirc;n nh&aacute;y mắt ra hiệu.<br />\r\n<br />\r\nNgụy Qu&acirc;n biết Th&aacute;i Kỳ L&acirc;m v&igrave; sao lại c&oacute; loại phản ứng n&agrave;y.<br />\r\n<br />\r\nT&acirc;n khoa tiến sĩ tại tr&uacute;ng cử sau, ấn lệ cũ l&agrave; c&oacute; hai ng&agrave;y ph&oacute;ng t&uacute;ng h&agrave;nh vi thời gian, ngươi c&oacute; thể thỏa th&iacute;ch đi l&atilde;ng, đ&acirc;y cũng l&agrave; cấp t&acirc;n khoa tiến sĩ ph&uacute;c lợi.<br />\r\n<br />\r\nD&ugrave; sao cấp tiến sĩ thụ quan về sau, liền muốn ổn trọng h&agrave;nh sự, hai ng&agrave;y nay cũng l&agrave; ban thưởng ngươi ph&iacute;a trước vất vả học tập, chỉ cần kh&ocirc;ng nh&aacute;o ra qu&aacute; chuyện đại sự, đều sẽ kh&ocirc;ng ảnh hưởng tiền đồ.<br />\r\n<br />\r\nM&agrave; Diệu &Acirc;m phường ch&iacute;nh l&agrave; Đại C&agrave;n nổi tiếng nhất nghe tiểu kh&uacute;c địa phương, đương nhi&ecirc;n, đ&acirc;y l&agrave; văn nh&atilde; điểm c&aacute;ch n&oacute;i, Diệu &Acirc;m phường ch&acirc;n ch&iacute;nh thuộc t&iacute;nh, cũng kh&ocirc;ng cần qu&aacute; nhiều giải th&iacute;ch.<br />\r\n<br />\r\nBất qu&aacute; nghe đồn Diệu &Acirc;m phường sau lưng c&oacute; đại địa vị, cho n&ecirc;n b&igrave;nh thường t&igrave;nh huống hạ cũng kh&ocirc;ng người n&agrave;o d&aacute;m &eacute;p buộc Diệu &Acirc;m phường c&ocirc; nương, tại dưới đại bộ phận t&igrave;nh huống, Diệu &Acirc;m phường c&aacute;c c&ocirc; nương đều l&agrave; b&aacute;n nghệ kh&ocirc;ng b&aacute;n th&acirc;n.<br />\r\n<br />\r\nHơn nữa đại đa số Diệu &Acirc;m phường c&ocirc; nương đều t&agrave;i nghệ song tuyệt, đương nhi&ecirc;n, những c&ocirc; nương n&agrave;y liền trở th&agrave;nh rất nhiều người suy nghĩ b&ecirc;n trong nữ thần.<br />\r\n<br />\r\nN&agrave;y s&aacute;o lộ Ngụy Qu&acirc;n r&otilde; r&agrave;ng, đối với c&aacute;i n&agrave;y hắn kỳ thật kh&ocirc;ng c&oacute; bao nhi&ecirc;u chờ mong.<br />\r\n<br />\r\nHắn đời trước l&agrave; v&ograve;ng về tới một c&aacute;i đ&ocirc; thị vị diện, n&agrave;y loại s&aacute;o lộ hắn thấy nhiều, cũng trải qua nhiều hơn.<br />\r\n<br />\r\nVừa v&agrave;o hồng trần s&acirc;u như biển, n&agrave;o c&oacute; c&aacute;i g&igrave; ch&acirc;n ch&iacute;nh b&aacute;n nghệ kh&ocirc;ng b&aacute;n th&acirc;n.<br />\r\n<br />\r\nBất qu&aacute; đ&acirc;y l&agrave; hắn c&ugrave;ng Từ Đức c&ugrave;ng Th&aacute;i Kỳ L&acirc;m đ&atilde; sớm hẹn xong, cho n&ecirc;n Ngụy Qu&acirc;n cũng kh&ocirc;ng c&oacute; qu&eacute;t hai người hưng, tả hữu cũng l&agrave; đi chơi m&agrave; th&ocirc;i.<br />\r\n<br />\r\nN&oacute;i kh&ocirc;ng chừng liền gặp được c&aacute;i g&igrave; ho&agrave;n khố &aacute;c thiếu đ&ugrave;a giỡn &quot;Nh&agrave; l&agrave;nh nữ thần&quot;, sau đ&oacute; hắn đứng ra, cuối c&ugrave;ng bị ho&agrave;n khố &aacute;c thiếu t&ugrave;y t&ugrave;ng xử l&yacute; đ&acirc;u.<br />\r\n<br />\r\nNếu l&agrave; như vậy, coi như qu&aacute; may mắn.<br />\r\n<br />\r\nCăn cứ c&aacute;i n&agrave;y &yacute; niệm, Ngụy Qu&acirc;n thực sảng kho&aacute;i c&ugrave;ng Từ Đức c&ograve;n c&oacute; Th&aacute;i Kỳ L&acirc;m c&ugrave;ng đi Diệu &Acirc;m phường.<br />\r\n<br />\r\n&quot;Ngụy huynh, ngươi nếu l&agrave; hiện th&acirc;n Diệu &Acirc;m phường, n&oacute;i kh&ocirc;ng chừng liền c&oacute; thể bị n&agrave;o đ&oacute; vị c&ocirc; nương coi trọng, trở th&agrave;nh kh&aacute;ch qu&yacute; đ&acirc;u.&quot; Từ Đức h&acirc;m mộ n&oacute;i.<br />\r\n<br />\r\nHắn biết hắn c&ugrave;ng Th&aacute;i Kỳ L&acirc;m khẳng định l&agrave; kh&ocirc;ng c&oacute; hi vọng qu&aacute; lớn, d&ugrave; sao Diệu &Acirc;m phường c&ocirc; nương từ trước đến nay mắt cao hơn đầu.<br />\r\n<br />\r\nNhưng l&agrave; trạng nguy&ecirc;n lu&ocirc;n lu&ocirc;n được xưng l&agrave; Văn Kh&uacute;c tinh chuyển thế, Ngụy Qu&acirc;n l&agrave; t&acirc;n khoa trạng nguy&ecirc;n, l&uacute;c trước c&ugrave;ng hai người bọn họ l&agrave; một c&aacute;i cấp bậc học sinh ngh&egrave;o, về sau liền kh&ocirc;ng đồng dạng.<br />\r\n<br />\r\nLiền xem như Diệu &Acirc;m phường c&ocirc; nương, cũng sẽ đối Ngụy Qu&acirc;n mắt kh&aacute;c đối đ&atilde;i.<br />\r\n<br />\r\nBất qu&aacute; Ngụy Qu&acirc;n chỉ l&agrave; cười nhạt cười, sau đ&oacute; n&oacute;i: &quot;Sẽ kh&ocirc;ng.&quot;<br />\r\n<br />\r\n&quot;V&igrave; c&aacute;i g&igrave; sẽ kh&ocirc;ng? Ngụy huynh, ngươi thế nhưng l&agrave; t&acirc;n khoa trạng nguy&ecirc;n. Diệu &Acirc;m phường c&aacute;c c&ocirc; nương lại mắt cao hơn đầu, cũng sẽ kh&ocirc;ng xem thường ngươi.&quot; Từ Đức kh&iacute;ch lệ n&oacute;i.<br />\r\n<br />\r\nNgụy Qu&acirc;n gật đầu: &quot;Đ&acirc;y l&agrave; đương nhi&ecirc;n, bất qu&aacute; ta kh&ocirc;ng phải người t&ugrave;y tiện như vậy.&quot;<br />\r\n<br />\r\nTrẫm lập tức ch&iacute;nh l&agrave; thi&ecirc;n đế.<br />\r\n<br />\r\n&Aacute;nh mắt đ&atilde; kh&ocirc;ng đồng dạng.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><br />\r\nNgụy Qu&acirc;n tiến v&agrave;o trạng th&aacute;i rất nhanh.<br />\r\n<br />\r\nNgụy Qu&acirc;n kh&ocirc;ng c&oacute; ph&aacute;t hiện, tại hắn c&ugrave;ng Từ Đức c&ograve;n c&oacute; Th&aacute;i Kỳ L&acirc;m n&oacute;i chuyện phiếm thời điểm, kỳ thật vẫn lu&ocirc;n c&oacute; người tại quan s&aacute;t bọn họ.<br />\r\n<br />\r\nDiệu &Acirc;m phường.<br />\r\n<br />\r\nC&aacute;ch bọn họ vị tr&iacute; ch&iacute; &iacute;t c&ograve;n c&oacute; một n&eacute;n nhang lộ tr&igrave;nh.<br />\r\n<br />\r\nNhưng l&agrave; tại lầu cao nhất, một c&aacute;i c&ocirc;ng tử &aacute;o gấm l&ocirc;ng m&agrave;y nhướn l&ecirc;n, thản nhi&ecirc;n n&oacute;i: &quot;Thật l&agrave; lớn ngạo kh&iacute;.&quot;<br />\r\n<br />\r\nC&ocirc;ng tử &aacute;o gấm đối diện ch&iacute;nh l&agrave; Diệu &Acirc;m phường nổi tiếng nhất thanh quan nh&acirc;n Mộng c&ocirc; nương.<br />\r\n<br />\r\nMộng c&ocirc; nương mỉm cười cấp c&ocirc;ng tử &aacute;o gấm r&oacute;t rượu, sau đ&oacute; khẽ mở m&ocirc;i đỏ: &quot;Mười năm gian khổ học tập kh&ocirc;ng người hỏi, nhất cử th&agrave;nh danh thi&ecirc;n hạ biết. Ngụy Qu&acirc;n l&agrave; t&acirc;n khoa trạng nguy&ecirc;n, hắn c&oacute; ngạo kh&iacute; tư c&aacute;ch, kh&ocirc;ng phải Thượng Quan thừa tương cũng sẽ kh&ocirc;ng cố &yacute; l&agrave;m Thượng Quan c&ocirc;ng tử ở chỗ n&agrave;y chờ hắn.&quot;<br />\r\n<br />\r\n&quot;Mộng c&ocirc; nương lời ấy c&oacute; l&yacute;, người c&oacute; năng lực x&aacute;c thực c&oacute; ngạo kh&iacute; tư c&aacute;ch.&quot; Thượng Quan c&ocirc;ng tử nhẹ gật đầu: &quot;Bất qu&aacute; hắn đến c&ugrave;ng c&oacute; thể hay kh&ocirc;ng v&agrave;o ta phụ th&acirc;n mắt, c&ograve;n phải xem hắn kế tiếp biểu hiện.&quot;<br />\r\n<br />\r\n&quot;C&ocirc;ng tử chuẩn bị như thế n&agrave;o thử th&aacute;ch Ngụy Qu&acirc;n?&quot; Mộng c&ocirc; nương hiếu kỳ hỏi.<br />\r\n<br />\r\nThượng Quan c&ocirc;ng tử cũng kh&ocirc;ng c&oacute; che giấu, Ngụy Qu&acirc;n th&acirc;n phận hắn đ&atilde; sớm điều tra r&otilde; r&agrave;ng, kh&ocirc;ng c&oacute; khả năng c&ugrave;ng Mộng c&ocirc; nương nhận biết, cho n&ecirc;n kh&ocirc;ng tồn tại Mộng c&ocirc; nương hướng Ngụy Qu&acirc;n mật b&aacute;o.<br />\r\n<br />\r\nHắn sảng kho&aacute;i n&oacute;i ra ch&iacute;nh m&igrave;nh an b&agrave;i: &quot;Liền nh&igrave;n hắn đợi ch&uacute;t nữa c&oacute; d&aacute;m hay kh&ocirc;ng ngăn cản bổn c&ocirc;ng tử, ta phụ th&acirc;n mặc d&ugrave; coi trọng người c&oacute; t&agrave;i hoa, nhưng c&agrave;ng coi trọng một người phẩm t&iacute;nh c&ugrave;ng đảm lượng. Nếu như hắn l&agrave; một c&aacute;i thẳng thắn cương nghị h&aacute;n tử, bản c&ocirc;ng tử liền tiễn hắn một c&aacute;i cẩm t&uacute; tiền đồ. Nếu như hắn chỉ l&agrave; một c&aacute;i a dua quyền qu&yacute; đồ h&egrave;n nh&aacute;t, vậy sau n&agrave;y liền xem hắn ch&iacute;nh m&igrave;nh tạo h&oacute;a.&quot;<br />\r\n<br />\r\nMộng c&ocirc; nương nghe vậy hiểu r&otilde;, sau đ&oacute; th&acirc;m &yacute; s&acirc;u sắc nh&igrave;n Thượng Quan c&ocirc;ng tử một ch&uacute;t, nhắc nhở: &quot;Chỉ l&agrave; như vậy vừa đến, c&ocirc;ng tử thanh danh chỉ sợ muốn c&agrave;ng hỏng rồi hơn.&quot;<br />\r\n<br />\r\nThượng Quan c&ocirc;ng tử cười to: &quot;Phụ th&acirc;n ta l&agrave; đương triều thừa tướng, quyền khuynh triều ch&iacute;nh. Ta muội muội l&agrave; Thi&ecirc;n &Acirc;m t&ocirc;ng ch&acirc;n truyền đệ tử, danh chấn tu ch&acirc;n giới. Ch&uacute;ng ta Thượng Quan gia đ&atilde; như thế cường thịnh, ta muốn như vậy tốt thanh danh l&agrave;m c&aacute;i g&igrave;? Ta chỉ cần trường mệnh thi&ecirc;n tuế l&agrave; đủ rồi.&quot;<br />\r\n<br />\r\n&quot;C&ocirc;ng tử l&agrave; người th&ocirc;ng minh.&quot; Mộng c&ocirc; nương t&aacute;n th&aacute;n n&oacute;i.<br />\r\n<br />\r\nThượng Quan c&ocirc;ng tử n&oacute;i: &quot;Hy vọng Ngụy Qu&acirc;n kh&ocirc;ng n&ecirc;n qu&aacute; th&ocirc;ng minh, bản c&ocirc;ng tử kh&ocirc;ng th&iacute;ch xem x&eacute;t thời thế người th&ocirc;ng minh.&quot;<br />\r\n<br />\r\nMộng c&ocirc; nương lại cấp Thượng Quan c&ocirc;ng tử ch&acirc;m một ch&eacute;n rượu, đối với chuyện sắp xảy ra kế tiếp cũng c&oacute; ch&uacute;t chờ mong.<br />\r\n<br />\r\nHắn đối Thượng Quan c&ocirc;ng tử l&agrave; hiểu rất r&otilde;, n&agrave;y vị thừa tướng c&ocirc;ng tử được xưng l&agrave; kinh th&agrave;nh bốn đại ho&agrave;n khố đứng đầu, bất học v&ocirc; thuật, tiếng xấu r&otilde; r&agrave;ng, nổi danh hỉ nộ v&ocirc; thường.<br />\r\n<br />\r\nNhưng n&agrave;ng lại biết, Thượng Quan c&ocirc;ng tử kỳ thật thưởng thức nhất những c&aacute;i đ&oacute; d&aacute;m ở ngay trước mặt hắn đắc tội hắn ch&iacute;nh trực người, rất nhiều trước mặt mọi người lạc qua hắn mặt mũi người, đều bị hắn đề cử cho hắn phụ th&acirc;n, c&oacute; ch&uacute;t c&ograve;n tự th&acirc;n cho bọn họ an b&agrave;i tốt hơn tiền đồ, chỉ bất qu&aacute; n&agrave;y đ&oacute; sự t&igrave;nh Thượng Quan c&ocirc;ng tử vẫn lu&ocirc;n l&agrave;m được thực b&iacute; ẩn, &iacute;t c&oacute; người biết.<br />\r\n<br />\r\nHơn nữa d&aacute;m ngay mặt đắc tội thừa tướng c&ocirc;ng tử người kh&ocirc;ng nhiều, cho n&ecirc;n vẫn lu&ocirc;n kh&ocirc;ng c&oacute; qu&aacute; nhiều người ph&aacute;t hiện điểm ấy.<br />\r\n<br />\r\nT&acirc;n khoa trạng nguy&ecirc;n Ngụy Qu&acirc;n, có th&ecirc;̉ th&ocirc;ng qua Thượng Quan c&ocirc;ng tử thử th&aacute;ch sao?<br />\r\n<br />\r\n( bản chương xong )</p>', '2022-05-08 02:45:07', 3, 1),
(331, 86, 'Ta Cùng Tội ác Không đội Trời Chung', '2', '<p>Diệu &Acirc;m phường &quot;Phong cảnh&quot;, c&oacute; ch&uacute;t vượt qu&aacute; Ngụy Qu&acirc;n đo&aacute;n trước.<br />\r\n<br />\r\nNgụy Qu&acirc;n vốn cho l&agrave; hắn khẳng định chướng mắt Diệu &Acirc;m phường n&agrave;y loại phong nguyệt nơi, d&ugrave; sao Trẫm lập tức ch&iacute;nh l&agrave; thi&ecirc;n đế, nhưng l&agrave; sự thật chứng minh, hắn đ&aacute;nh gi&aacute; cao ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\nD&ugrave; sao thi&ecirc;n đế đại bộ phận k&yacute; ức hắn cũng kh&ocirc;ng c&oacute; mở ra, chỉ l&agrave; ho&agrave;n chỉnh quay lại kiếp trước k&yacute; ức, nhưng l&agrave; kiếp trước l&agrave; một c&aacute;i đ&ocirc; thị vị diện, mặc d&ugrave; mỹ nữ như m&acirc;y, bất qu&aacute; c&aacute;i n&agrave;y thế giới &mdash;&mdash; truyền thuyết l&agrave; c&oacute; ti&ecirc;n nh&acirc;n.<br />\r\n<br />\r\nNgụy Qu&acirc;n mặc d&ugrave; kh&ocirc;ng c&oacute; tại c&aacute;i n&agrave;y thế giới gặp qua ti&ecirc;n nh&acirc;n ch&acirc;n ch&iacute;nh, nhưng l&agrave; người tu ti&ecirc;n Ngụy Qu&acirc;n l&agrave; gặp qua.<br />\r\n<br />\r\nPh&agrave;m l&agrave; c&oacute; si&ecirc;u ph&agrave;m lực lượng, nh&acirc;n loại thẩm mỹ liền kh&ocirc;ng khả năng qu&aacute; k&eacute;m.<br />\r\n<br />\r\nCho n&ecirc;n c&aacute;i n&agrave;y thế giới mỹ nh&acirc;n chất lượng, c&oacute; chút ngo&agrave;i &yacute; liệu cao.<br />\r\n<br />\r\nNếu l&agrave; hiện tại Ngụy Qu&acirc;n kh&ocirc;i phục thi&ecirc;n đế to&agrave;n bộ k&yacute; ức, khẳng định sẽ chẳng th&egrave;m ng&oacute; tới. Nhưng l&agrave; từ đ&ocirc; thị vị diện nhảy chuyển tới c&aacute;i n&agrave;y cổ phong ti&ecirc;n hiệp vị diện, Ngụy Qu&acirc;n vẫn cảm gi&aacute;c được rất nhiều kinh hỉ.<br />\r\n<br />\r\nTỷ như Diệu &Acirc;m phường c&ocirc; nương nhan gi&aacute; trị.<br />\r\n<br />\r\nLại tỷ như Diệu &Acirc;m phường c&ocirc; nương t&agrave;i nghệ.<br />\r\n<br />\r\nN&agrave;y đ&oacute; ca kh&uacute;c, ngo&agrave;i &yacute; muốn dễ nghe. Ngụy Qu&acirc;n v&ocirc; luận kiếp trước kiếp n&agrave;y đều kh&ocirc;ng phải &acirc;m nhạc say m&ecirc; c&ocirc;ng việc, nhưng l&agrave; hắn chỉ nghe một l&aacute;t &acirc;m nhạc, liền cảm gi&aacute;c c&oacute; ch&uacute;t t&acirc;m thần thanh thản.<br />\r\n<br />\r\nN&agrave;y đ&oacute; vũ đạo cũng ngo&agrave;i &yacute; muốn đẹp mắt, ti&ecirc;u chuẩn so với hắn kiếp trước nh&igrave;n qua nữ đo&agrave;n m&uacute;a c&ograve;n nhỏ, thậm ch&iacute; kh&ocirc;ng c&oacute; c&aacute;i g&igrave; tao thủ lộng tư, nhưng l&agrave; lại c&oacute; điểm lay động tiếng l&ograve;ng của hắn.<br />\r\n<br />\r\nĐương nhi&ecirc;n, thưởng thức th&igrave; thưởng thức, Ngụy Qu&acirc;n cũng sẽ kh&ocirc;ng biểu hiện một bộ Trư ca bộ d&aacute;ng. Người đều y&ecirc;u th&iacute;ch xinh đẹp đồ vật, thưởng thức đẹp l&agrave; người bản năng, nhưng l&agrave; người l&yacute; tr&iacute; c&oacute; thể khống chế lại n&agrave;y đ&oacute; bản năng.<br />\r\n<br />\r\nBất qu&aacute; cũng kh&ocirc;ng phải tất cả mọi người có th&ecirc;̉ khống chế trụ.<br />\r\n<br />\r\nNgụy Qu&acirc;n nh&igrave;n chung quanh một ch&uacute;t, thực hối hận ch&iacute;nh m&igrave;nh l&agrave;m sao c&ugrave;ng Từ Đức Th&aacute;i Kỳ L&acirc;m n&agrave;y hai c&aacute;i h&oacute;a c&ugrave;ng nhau tới, xem bọn họ kia chảy nước miếng bộ d&aacute;ng, qu&aacute; mất mặt.<br />\r\n<br />\r\nLiền xem như hai chim non, cũng kh&ocirc;ng đến mức như vậy kh&ocirc;ng thấy qua việc đời đi.<br />\r\n<br />\r\nNgụy Qu&acirc;n lập tức c&aacute;ch xa hai người bọn họ, lấy h&agrave;nh động thực tế biểu thị ch&iacute;nh m&igrave;nh kh&ocirc;ng muốn quen biết c&aacute;i n&agrave;y hai người.<br />\r\n<br />\r\nNhưng l&agrave; cố gắng l&agrave; ph&iacute; c&ocirc;ng.<br />\r\n<br />\r\n&quot;Ngụy huynh, Ngụy huynh, Diệu &Acirc;m phường quả nhi&ecirc;n danh bất hư truyền a.&quot; Từ Đức to&agrave;n bộ con mắt tại tỏa &aacute;nh s&aacute;ng.<br />\r\n<br />\r\nM&agrave; Th&aacute;i Kỳ L&acirc;m nhắm mắt lại, từ đ&aacute;y l&ograve;ng cảm kh&aacute;i n&oacute;i: &quot;Ta nếu c&oacute; thể cả một đời ở tại Diệu &Acirc;m phường liền tốt.&quot;<br />\r\n<br />\r\nNgụy Qu&acirc;n: &quot;. . . C&aacute;c ngươi c&oacute; thể hay kh&ocirc;ng đừng c&oacute; lại mất mặt xấu hổ?&quot;<br />\r\n<br />\r\nĐ&acirc;y cũng qu&aacute; khoa trương.<br />\r\n<br />\r\nCứ như vậy chưa từng thấy nữ nh&acirc;n sao?</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small><br />\r\n<br />\r\nNgụy Qu&acirc;n cũng kh&ocirc;ng biết, l&uacute;c n&agrave;y Thượng Quan c&ocirc;ng tử c&ugrave;ng Mộng c&ocirc; nương cũng tại nghị luận hắn.<br />\r\n<br />\r\nHai người ngữ kh&iacute; đều rất kỳ qu&aacute;i.<br />\r\n<br />\r\n&quot;Mộng c&ocirc; nương, Ngụy Qu&acirc;n hẳn kh&ocirc;ng c&oacute; tu luyện qua a?&quot;<br />\r\n<br />\r\nMộng c&ocirc; nương trả lời thực chắc chắn: &quot;Tuyệt đối kh&ocirc;ng c&oacute;, hắn ch&iacute;nh l&agrave; một người b&igrave;nh thường, nếu kh&ocirc;ng vừa rồi tiến v&agrave;o Diệu &Acirc;m phường liền sẽ c&oacute; cảnh b&aacute;o.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y kỳ qu&aacute;i a, vừa rồi Diệu &Acirc;m phường c&aacute;c c&ocirc; nương tại &acirc;m luật thượng phụ ph&aacute;p lực, người b&igrave;nh thường hẳn l&agrave; đều sẽ tr&uacute;ng chi&ecirc;u mới l&agrave;, tỷ như hắn hai c&aacute;i đồng m&ocirc;n. Ngụy Qu&acirc;n l&agrave; chuyện g&igrave; xảy ra? Người b&igrave;nh thường lúc nào có th&ecirc;̉ chống cự Diệu &Acirc;m phường &acirc;m c&ocirc;ng rồi?&quot; Thượng Quan c&ocirc;ng tử kh&ocirc;ng hiểu.<br />\r\n<br />\r\nMộng c&ocirc; nương trầm mặc chỉ chốc l&aacute;t, sau đ&oacute; n&oacute;i: &quot;Chỉ c&oacute; một loại giải th&iacute;ch.&quot;<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\n&quot;Ngụy Qu&acirc;n nội t&acirc;m kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o &acirc;m u &yacute; nghĩ, l&agrave; một c&aacute;i tuyệt đối ch&iacute;nh nh&acirc;n qu&acirc;n tử, cho n&ecirc;n hắn c&oacute; thể l&agrave;m được đối sắc đẹp bất vi sở động.&quot; Mộng c&ocirc; nương nói.<br />\r\n<br />\r\nThượng Quan c&ocirc;ng tử nghe được Mộng c&ocirc; nương như vậy n&oacute;i, lập tức hai mắt tỏa s&aacute;ng: &quot;C&ograve;n c&oacute; n&agrave;y loại người tồn tại?&quot;<br />\r\n<br />\r\n&quot;Ta cũng l&agrave; lần đầu ti&ecirc;n nh&igrave;n thấy như vậy người, nhưng chỉ c&oacute; n&agrave;y một lời giải th&iacute;ch có th&ecirc;̉ nói còn nghe được.&quot; Mộng c&ocirc; nương khẳng định n&oacute;i.<br />\r\n<br />\r\nNếu Ngụy Qu&acirc;n có th&ecirc;̉ nghe đến l&uacute;c n&agrave;y hai người n&oacute;i chuyện, nhất định sẽ dở kh&oacute;c dở cười.<br />\r\n<br />\r\nTrẫm đời trước d&ugrave; sao cũng l&agrave; trải qua nhiều vị l&atilde;o sư dạy bảo người, đ&atilde; sớm đạt đến trong l&ograve;ng kh&ocirc;ng che cảnh giới. Nếu l&agrave; nghe mấy thủ tiểu kh&uacute;c liền khống chế kh&ocirc;ng nổi, kia cũng rất xin lỗi những l&atilde;o sư kia dạy bảo.<br />\r\n<br />\r\nTầm mắt hạn chế bọn họ tưởng tượng lực.<br />\r\n<br />\r\nCho n&ecirc;n tại Mộng c&ocirc; nương c&ugrave;ng Thượng Quan c&ocirc;ng tử trong l&ograve;ng, Ngụy Qu&acirc;n h&igrave;nh tượng lập tức trở n&ecirc;n cao to.<br />\r\n<br />\r\n&quot;Mộng c&ocirc; nương, n&agrave;y loại phẩm t&iacute;nh nam nh&acirc;n, tựa hồ l&agrave; c&aacute;c ngươi t&ocirc;ng ph&aacute;i tốt nhất gửi gắm t&igrave;nh cảm đối tượng a.&quot; Thượng Quan c&ocirc;ng tử nói.<br />\r\n<br />\r\nMộng c&ocirc; nương nhẹ gật đầu: &quot;X&aacute;c thực như thế, Thượng Quan c&ocirc;ng tử, sau đ&oacute; ta bồi c&ocirc;ng tử diễn tuồng n&agrave;y đi.&quot;<br />\r\n<br />\r\nThượng Quan c&ocirc;ng tử hơi kinh ngạc.<br />\r\n<br />\r\nHắn thật kh&ocirc;ng nghĩ tới có th&ecirc;̉ mời được Mộng c&ocirc; nương ra tay phối hợp ch&iacute;nh m&igrave;nh, đối với Mộng c&ocirc; nương lai lịch, hắn l&agrave; c&oacute; hiểu biết. Nếu kh&ocirc;ng phải v&igrave; hồng trần lịch luyện, Mộng c&ocirc; nương tr&ecirc;n l&yacute; luận căn bản sẽ kh&ocirc;ng xu&acirc;́t hi&ecirc;̣n ở đ&acirc;y.<br />\r\n<br />\r\nKinh th&agrave;nh ph&agrave;m l&agrave; trong nh&agrave; c&oacute; một ch&uacute;t bối cảnh người đều được dặn d&ograve; qua tuyệt đối đừng đắc tội Diệu &Acirc;m phường Mộng c&ocirc; nương.<br />\r\n<br />\r\nHắn vốn chỉ l&agrave; dự định mời Diệu &Acirc;m phường một c&aacute;i b&igrave;nh thường c&ocirc; nương c&ugrave;ng hắn diễn kịch, kh&ocirc;ng nghĩ tới Mộng c&ocirc; nương lại c&oacute; thể sẵn s&agrave;ng tự m&igrave;nh hạ tr&agrave;ng, c&aacute;i n&agrave;y Ngụy Qu&acirc;n xem ra đối Mộng c&ocirc; nương c&aacute;c n&agrave;ng t&ocirc;ng m&ocirc;n thật v&ocirc; c&ugrave;ng quan trọng.<br />\r\n<br />\r\nBất qu&aacute; d&ugrave; vậy, Thượng Quan c&ocirc;ng tử cũng kh&ocirc;ng c&oacute; thay đổi ch&iacute;nh m&igrave;nh &yacute; nghĩ.<br />\r\n<br />\r\nNh&acirc;n t&agrave;i như vậy, c&agrave;ng muốn khảo nghiệm r&otilde; r&agrave;ng. Nếu c&oacute; thể th&ocirc;ng qua thử th&aacute;ch, hắn liền to&agrave;n lực trợ gi&uacute;p Ngụy Qu&acirc;n đi l&ecirc;n.<br />\r\n<br />\r\n&quot;Vậy l&agrave;m phiền Mộng c&ocirc; nương.&quot; Thượng Quan c&ocirc;ng tử nói.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nNgụy Qu&acirc;n tới Diệu &Acirc;m phường, thật l&agrave; để thưởng thức &acirc;m nhạc.<br />\r\n<br />\r\nĐương nhi&ecirc;n, cộng th&ecirc;m l&atilde;nh hội hồng trần &yacute; cảnh.<br />\r\n<br />\r\nĐến thi&ecirc;n đế cấp bậc kia, kỳ thật cơ bản đ&atilde; kh&ocirc;ng d&ugrave;ng tu luyện, ngược lại ch&uacute; trọng hơn hồng trần lịch luyện, t&acirc;m t&iacute;nh trưởng th&agrave;nh.<br />\r\n<br />\r\nKiến thức c&agrave;ng nhiều phong cảnh, loại suy, mới c&oacute; thể tiến th&ecirc;m một bước.<br />\r\n<br />\r\nCho n&ecirc;n hắn thật trầm ng&acirc;m v&agrave;o Diệu &Acirc;m phường &acirc;m nhạc b&ecirc;n trong đi.<br />\r\n<br />\r\nThẳng đến &acirc;m nhạc bị một hồi tiếng ồn &agrave;o đ&aacute;nh g&atilde;y.<br />\r\n<br />\r\nNgụy Qu&acirc;n chau m&agrave;y, giương mắt nh&igrave;n đi l&ecirc;n, sau đ&oacute; liền thấy vừa ra truyền h&igrave;nh điện ảnh kịch kinh điển kịch bản: Ho&agrave;n khố &aacute;c thiếu coi trọng đang hồng thanh quan nh&acirc;n, nghĩ muốn chiếm l&agrave;m của ri&ecirc;ng.<br />\r\n<br />\r\n&quot;Thượng Quan c&ocirc;ng tử, Mộng c&ocirc; nương h&ocirc;m nay mệt rồi, n&agrave;ng bảo h&ocirc;m nay kh&ocirc;ng tiếp kh&aacute;ch.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng tiếp kh&aacute;ch c&oacute; thể, kh&ocirc;ng thấy bản c&ocirc;ng tử kh&ocirc;ng được, đem Mộng c&ocirc; nương k&ecirc;u đi ra, cấp bản c&ocirc;ng tử h&aacute;t hai c&aacute;i tiểu kh&uacute;c. Nếu kh&ocirc;ng, bản c&ocirc;ng tử tạp ngươi c&aacute;i n&agrave;y Diệu &Acirc;m phường.&quot;<br />\r\n<br />\r\n&quot;Thượng Quan c&ocirc;ng tử. . .&quot;<br />\r\n<br />\r\nBa!<br />\r\n<br />\r\nC&aacute;i t&aacute;t vang dội.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng muốn khi&ecirc;u chiến bản c&ocirc;ng tử ki&ecirc;n nhẫn, bản c&ocirc;ng tử từ trước đến nay kh&ocirc;ng c&oacute; ki&ecirc;n nhẫn.&quot;<br />\r\n<br />\r\n&Aacute;c chủ tự nhi&ecirc;n c&oacute; đi&ecirc;u n&ocirc;.<br />\r\n<br />\r\n&Aacute;c thiếu tại uy hiếp Diệu &Acirc;m phường người, m&agrave; &aacute;c thiếu t&ocirc;i tớ th&igrave; tại uy hiếp kh&aacute;ch nh&acirc;n kh&aacute;c: &quot;Nh&igrave;n c&aacute;i g&igrave; vậy? Lại nh&igrave;n đem c&aacute;c ngươi tr&ograve;ng mắt moi ra.&quot;<br />\r\n<br />\r\nBị đi&ecirc;u n&ocirc; như vậy một uy hiếp, kh&ocirc;ng &iacute;t người đều cổ co rụt lại, một lần nữa bắt đầu lại uống rượu, ho&agrave;n to&agrave;n kh&ocirc;ng d&aacute;m nhiều tất tất.<br />\r\n<br />\r\nĐ&acirc;y ch&iacute;nh l&agrave; kinh th&agrave;nh bốn đại ho&agrave;n khố đứng đầu uy danh.<br />\r\n<br />\r\nNgụy Qu&acirc;n thấy thế, nh&iacute;u m&agrave;y, bất qu&aacute; hắn cũng kh&ocirc;ng c&oacute; đứng ra chủ tr&igrave; ch&iacute;nh nghĩa.<br />\r\n<br />\r\nThấy Từ Đức c&ugrave;ng Th&aacute;i Kỳ L&acirc;m đều mặt lộ vẻ tức giận c&ugrave;ng vẻ bất đắc dĩ, Ngụy Qu&acirc;n an ủi: &quot;Từ huynh, Th&aacute;i huynh, kh&ocirc;ng cần lo lắng, c&aacute;i n&agrave;y Thượng Quan c&ocirc;ng tử chẳng mấy chốc sẽ xui xẻo.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small><br />\r\n<br />\r\nNgụy Qu&acirc;n n&agrave;y c&acirc;u n&oacute;i rơi xuống Từ Đức c&ugrave;ng Th&aacute;i Kỳ L&acirc;m tai b&ecirc;n trong, cũng rơi v&agrave;o Thượng Quan c&ocirc;ng tử c&ugrave;ng Mộng c&ocirc; nương tai b&ecirc;n trong.<br />\r\n<br />\r\nBọn họ đều rất kỳ qu&aacute;i.<br />\r\n<br />\r\nTh&aacute;i Kỳ L&acirc;m hỏi: &quot;Ngụy huynh v&igrave; sao như vậy n&oacute;i?&quot;<br />\r\n<br />\r\nNgụy Qu&acirc;n tự tin cười một tiếng: &quot;Rất đơn giản, Diệu &Acirc;m phường mở tại dưới ch&acirc;n thi&ecirc;n tử ngật đứng kh&ocirc;ng ng&atilde;, c&oacute; thể l&agrave; kh&ocirc;ng c&oacute; bối cảnh sao? Kh&ocirc;ng chỉ c&oacute; bối cảnh, hơn nữa khẳng định th&ocirc;ng thi&ecirc;n. D&aacute;m ở Diệu &Acirc;m phường dương oai, kh&ocirc;ng l&agrave; muốn chết l&agrave; c&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\nĐ&acirc;y l&agrave; hơi ch&uacute;t động n&atilde;o liền c&oacute; thể đạt được ph&aacute;n đo&aacute;n, cho n&ecirc;n Ngụy Qu&acirc;n liền đứng ra muốn chết &yacute; nghĩ đều kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nBởi v&igrave; hắn biết chắc kh&ocirc;ng chết được.<br />\r\n<br />\r\nV&ocirc; luận l&agrave; c&aacute;i n&agrave;o c&aacute;i thế giới, c&oacute; thể tại thủ phủ nơi mở n&agrave;y loại cấp bậc &quot;Hội sở&quot; đều c&oacute; th&ocirc;ng thi&ecirc;n bối cảnh, kh&ocirc;ng c&oacute; ngoại lệ.<br />\r\n<br />\r\nCho n&ecirc;n căn bản kh&ocirc;ng cần đến chủ tr&igrave; ch&iacute;nh nghĩa.<br />\r\n<br />\r\nNhưng l&agrave; Ngụy Qu&acirc;n rất nhanh liền ph&aacute;t hiện hắn nghĩ lầm.<br />\r\n<br />\r\nTh&aacute;i Kỳ L&acirc;m n&oacute;i: &quot;Ngụy huynh, ngươi chỉ biết một m&agrave; kh&ocirc;ng biết hai, Diệu &Acirc;m phường x&aacute;c thực c&oacute; bối cảnh, nhưng l&agrave; Thượng Quan c&ocirc;ng tử bối cảnh c&agrave;ng mạnh.&quot;<br />\r\n<br />\r\nNgụy Qu&acirc;n c&oacute; ch&uacute;t ngo&agrave;i &yacute; muốn: &quot;Bối cảnh c&agrave;ng mạnh c&ograve;n c&oacute; thể nu&ocirc;i dưỡng được như vậy xuẩn ăn chơi thiếu gia?&quot;<br />\r\n<br />\r\nKh&ocirc;ng n&ecirc;n a.<br />\r\n<br />\r\n&quot;Ngụy huynh, ngươi ch&iacute;nh l&agrave; đọc s&aacute;ch qu&aacute; chuy&ecirc;n t&acirc;m, đều chưa nghe n&oacute;i qua Thượng Quan c&ocirc;ng tử đại danh. Hắn l&agrave; kinh th&agrave;nh tứ đại &aacute;c thiếu đứng đầu, c&agrave;ng l&agrave; Thượng Quan thừa tương nhi tử. Trọng yếu nhất ch&iacute;nh l&agrave;, hắn muội muội l&agrave; tu ch&acirc;n giới thập đại ch&iacute;nh đạo m&ocirc;n ph&aacute;i b&ecirc;n trong Thi&ecirc;n &Acirc;m t&ocirc;ng ch&acirc;n truyền đệ tử. Diệu &Acirc;m phường coi như bối cảnh th&ocirc;ng thi&ecirc;n, l&agrave;m sao c&oacute; thể c&ugrave;ng hắn đấu?&quot; Th&aacute;i Kỳ L&acirc;m lắc đầu: &quot;Kỳ thật n&agrave;y cũng kh&ocirc;ng phải lần đầu ti&ecirc;n, trước kia Thượng Quan c&ocirc;ng tử liền đại n&aacute;o qua Diệu &Acirc;m phường, lúc đương thời người nhịn kh&ocirc;ng được đứng ra b&ecirc;nh vực lẽ phải, kết quả kia người từ đ&oacute; về sau liền kh&ocirc;ng c&ograve;n lại xuất hiện qua, ng&agrave;y kế tiếp liền ở ngo&agrave;i th&agrave;nh ph&aacute;t hiện kia người thi thể.&quot;<br />\r\n<br />\r\nNgụy Qu&acirc;n nghe đến đ&oacute;, lập tức hưng phấn.<br />\r\n<br />\r\nN&agrave;y loại &aacute;c thiếu hắn y&ecirc;u th&iacute;ch a.<br />\r\n<br />\r\nTrẫm từ trước đến nay c&ugrave;ng tội &aacute;c kh&ocirc;ng đội trời chung, mắt b&ecirc;n trong nhu kh&ocirc;ng được mảy may hạt c&aacute;t.<br />\r\n<br />\r\nD&ugrave; l&agrave; muốn đ&aacute;nh bạc t&iacute;nh mạng, hắn cũng phải trả thế gian n&agrave;y một c&aacute;i lanh lảnh c&agrave;n kh&ocirc;n.<br />\r\n<br />\r\nCho n&ecirc;n Ngụy Qu&acirc;n đằng một ch&uacute;t đứng dậy, kh&iacute; thế như hồng, anh dũng kh&ocirc;ng sợ lớn tiếng n&oacute;i: &quot;N&agrave;y, cầm th&uacute;, bu&ocirc;ng ra c&aacute;i kia nữ h&agrave;i, để cho ta tới. Phi, hướng ta tới.&quot;<br />\r\n<br />\r\nPS: &laquo; qu&acirc;n l&acirc;m ng&agrave;nh giải tr&iacute; &raquo; đ&atilde; ho&agrave;nh th&agrave;nh, s&aacute;ch mới ch&iacute;nh thức l&ecirc;n đường. Ta quyển s&aacute;ch trước điểm xuất ph&aacute;t đăng nhiều kỳ trong l&uacute;c đến ho&agrave;n th&agrave;nh ng&agrave;y c&agrave;ng b&igrave;nh qu&acirc;n si&ecirc;u vạn chữ, s&aacute;ch cũ hữu đều c&oacute; thể l&agrave;m chứng, đại gia y&ecirc;n t&acirc;m truy đọc.<br />\r\n<br />\r\n( bản chương xong )</p>', '2022-05-08 02:45:52', 3, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(332, 86, 'Thấy Chết Không Sờn', '3', '<p>Ngụy Qu&acirc;n đứng ra, l&agrave;m kh&ocirc;ng &iacute;t người biến sắc.<br />\r\n<br />\r\nDiệu &Acirc;m phường kh&aacute;ch kh&ocirc;ng &iacute;t người, rất nhanh liền c&oacute; người nhận ra Ngụy Qu&acirc;n t&acirc;n khoa trạng nguy&ecirc;n th&acirc;n phận, sau đ&oacute; n&agrave;y đ&oacute; người xem Ngụy Qu&acirc;n &aacute;nh mắt đều v&ocirc; c&ugrave;ng thương hại.<br />\r\n<br />\r\nTừ Đức c&ugrave;ng Th&aacute;i Kỳ L&acirc;m th&igrave; sắc mặt đại biến, hai người nhanh l&ecirc;n giữ chặt Ngụy Qu&acirc;n, thấp giọng nhanh ch&oacute;ng khuy&ecirc;n nhủ: &quot;Ngụy huynh, kh&ocirc;ng n&ecirc;n vọng động. Thượng Quan c&ocirc;ng tử l&agrave; thừa tướng chi tử, hắn kh&ocirc;ng phải ch&uacute;ng ta b&acirc;y giờ c&oacute; thể đối kh&aacute;ng.&quot;<br />\r\n<br />\r\n&quot;C&ugrave;ng hắn đối nghịch, có khả năng sẽ chết.&quot;<br />\r\n<br />\r\nHai người đều bị Ngụy Qu&acirc;n dọa cho ph&aacute;t sợ.<br />\r\n<br />\r\nTrước kia liền biết Ngụy Qu&acirc;n học vẹt, kh&ocirc;ng thế n&agrave;o th&ocirc;ng đạo l&iacute; đối nh&acirc;n xử thế, nhưng l&agrave; cũng kh&ocirc;ng nghĩ tới Ngụy Qu&acirc;n d&aacute;m như vậy hổ.<br />\r\n<br />\r\nM&agrave; lầu b&ecirc;n tr&ecirc;n Thượng Quan c&ocirc;ng tử nh&igrave;n thấy Ngụy Qu&acirc;n n&agrave;y ph&oacute; biểu hiện, nội t&acirc;m kỳ thật hết sức h&agrave;i l&ograve;ng.<br />\r\n<br />\r\nC&oacute; năng lực người c&oacute; t&agrave;i hoa khắp nơi đều l&agrave;, nhưng l&agrave; tại c&oacute; năng lực t&igrave;nh huống hạ c&ograve;n c&oacute; dũng kh&iacute; người liền rất thưa thớt.<br />\r\n<br />\r\nHắn từ trước đến nay k&iacute;nh nể n&agrave;y loại người.<br />\r\n<br />\r\nNhưng Thượng Quan c&ocirc;ng tử chắc chắn sẽ kh&ocirc;ng liền dễ d&agrave;ng như vậy đổ nước.<br />\r\n<br />\r\nHắn đưa &aacute;nh mắt chuyển dời đến Ngụy Qu&acirc;n tr&ecirc;n người, phối hợp với b&ecirc;n cạnh hắn người hầu hung uy, nh&aacute;y mắt b&ecirc;n trong liền mang cho những người kh&aacute;c cực lớn &aacute;p lực.<br />\r\n<br />\r\nThượng Quan c&ocirc;ng tử l&uacute;c n&agrave;y tay b&ecirc;n trong cầm một thanh quạt xếp, hắn d&ugrave;ng c&acirc;y quạt chỉ chỉ Ngụy Qu&acirc;n, vừa chỉ chỉ ch&iacute;nh m&igrave;nh, ngữ kh&iacute; v&ocirc; c&ugrave;ng kinh ngạc: &quot;Ngươi mới vừa rồi l&agrave; đang mắng ta?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải kh&ocirc;ng phải, Thượng Quan c&ocirc;ng tử ng&agrave;i hiểu lầm, Ngụy huynh hắn vừa rồi chỉ l&agrave; uống nhiều qu&aacute;.&quot; Từ Đức đuổi tại Ngụy Qu&acirc;n trước mặt gi&uacute;p Ngụy Qu&acirc;n giải th&iacute;ch n&oacute;i.<br />\r\n<br />\r\nNgụy Qu&acirc;n cũng theo Từ Đức n&oacute;i n&oacute;i: &quot;Ta sai rồi.&quot;<br />\r\n<br />\r\nThượng Quan c&ocirc;ng tử c&oacute; ch&uacute;t thất vọng.<br />\r\n<br />\r\nC&ograve;n tưởng rằng gặp một c&aacute;i ngạnh h&aacute;n.<br />\r\n<br />\r\nSuy nghĩ cả nửa ng&agrave;y chỉ l&agrave; nhất thời x&uacute;c động.<br />\r\n<br />\r\nĐối n&agrave;y loại người, hắn kh&ocirc;ng hứng th&uacute; g&igrave;.<br />\r\n<br />\r\nNgay tại hắn thất vọng thời điểm, Ngụy Qu&acirc;n lại tiếp tục mở miệng: &quot;Đem ngươi so s&aacute;nh cầm th&uacute;, thật sự l&agrave; vũ nhục cầm th&uacute;, ngươi liền cầm th&uacute; cũng kh&ocirc;ng bằng.&quot;<br />\r\n<br />\r\nĂn dưa quần ch&uacute;ng c&ugrave;ng nhau h&iacute;t s&acirc;u một hơi, to&agrave;n bộ Diệu &Acirc;m phường đều thay đổi ấm mấy độ.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><a href=\"https://c.lazada.vn/t/c.06cO5B?url=https%3A%2F%2Fpages.lazada.vn%2Fwow%2Fgcp%2Froute%2Flazada%2Fvn%2Fupr_1000345_lazada%2Fchannel%2Fvn%2Fupr-router%2Fvn%3Fhybrid%3D1%26data_prefetch%3Dtrue%26prefetch_replace%3D1%26at_iframe%3D1%26wh_pid%3D%2Flazada%2Fmegascenario%2Fvn%2Fsale-gap-boi-5-5-2022%2Fngay-doi-sale-boi\" target=\"_blank\"><img src=\"https://static.cdnno.com/storage/lazada/D5_AFF_MAIN_743x400.jpg\" /></a></p>\r\n\r\n<p><br />\r\n<br />\r\nThượng Quan c&ocirc;ng tử tại mọi người mắt b&ecirc;n trong giận qu&aacute; th&agrave;nh cười, tr&ecirc;n thực tế hắn cũng đ&uacute;ng l&agrave; tại cười: &quot;Thật can đảm, l&ecirc;n một c&aacute;i d&aacute;m như vậy mắng ta người, hiện tại đ&atilde; tại &acirc;m t&agrave;o địa phủ, ngươi muốn chết sao?&quot;<br />\r\n<br />\r\nNgụy Qu&acirc;n rất muốn gật đầu.<br />\r\n<br />\r\nTa thật rất muốn chết.<br />\r\n<br />\r\nBất qu&aacute; ta kh&ocirc;ng thể tự s&aacute;t, đến l&agrave;m cho ngươi động thủ.<br />\r\n<br />\r\nCho n&ecirc;n Ngụy Qu&acirc;n khinh thường n&oacute;i: &quot;Ta liền đứng ở nơi n&agrave;y, ngươi d&aacute;m giết ta sao?&quot;<br />\r\n<br />\r\nMau n&oacute;i ngươi d&aacute;m.<br />\r\n<br />\r\nHo&agrave;n khố liền phải x&uacute;c động.<br />\r\n<br />\r\nNgụy Qu&acirc;n tại trong l&ograve;ng cấp Thượng Quan c&ocirc;ng tử động vi&ecirc;n, Thượng Quan c&ocirc;ng tử biểu hiện cũng kh&ocirc;ng c&oacute; l&agrave;m hắn thất vọng: &quot;Ngụy Qu&acirc;n, t&acirc;n khoa trạng nguy&ecirc;n, bản c&ocirc;ng tử trước đ&oacute; gặp qua ngươi cưỡi ngựa dạo phố. Ngươi cho rằng c&oacute; một c&aacute;i trạng nguy&ecirc;n danh tiếng, liền d&aacute;m chống đối bản c&ocirc;ng tử? Kh&ocirc;ng n&ecirc;n qu&ecirc;n, ngươi b&acirc;y giờ c&ograve;n chưa c&oacute; quan th&acirc;n đ&acirc;u.&quot;<br />\r\n<br />\r\nN&oacute;i xong lời cuối c&ugrave;ng, Thượng Quan c&ocirc;ng tử đ&atilde; một mặt ch&acirc;m chọc: &quot;Lấy bản c&ocirc;ng tử th&acirc;n phận, giết một người b&igrave;nh thường, liền đại lao đều kh&ocirc;ng cần ngồi.&quot;<br />\r\n<br />\r\nNgụy Qu&acirc;n cảm gi&aacute;c Thượng Quan c&ocirc;ng tử qu&aacute; khen.<br />\r\n<br />\r\nQuả thực l&agrave; đuổi tới đưa ấm &aacute;p.<br />\r\n<br />\r\n&quot;Dưới ch&acirc;n thi&ecirc;n tử, lanh lảnh c&agrave;n kh&ocirc;n, Thượng Quan c&ocirc;ng tử ngươi nếu muốn l&agrave;m nhiều chuyện bất nghĩa, kia cứ việc x&ocirc;ng ta tới, kh&ocirc;ng muốn li&ecirc;n luỵ v&ocirc; tội nữ tử.&quot;<br />\r\n<br />\r\nTất cả mọi người tại trong l&ograve;ng v&igrave; Ngụy Qu&acirc;n gọi tốt.<br />\r\n<br />\r\nNhưng kh&ocirc;ng người n&agrave;o d&aacute;m n&oacute;i ra.<br />\r\n<br />\r\nThậm ch&iacute;, Th&aacute;i Kỳ L&acirc;m c&ograve;n lặng y&ecirc;n kh&ocirc;ng tiếng động c&aacute;ch xa Ngụy Qu&acirc;n, xu lợi tr&aacute;nh hại, người bản năng.<br />\r\n<br />\r\nThượng Quan c&ocirc;ng tử nhạy cảm ph&aacute;t hiện điểm ấy, cười ha ha: &quot;Ngụy Qu&acirc;n, ngươi xem một ch&uacute;t ngươi đồng m&ocirc;n bạn tốt, ngươi cho rằng ngươi tại h&agrave;nh hiệp trượng nghĩa, nhưng ngươi tại bọn họ trong l&ograve;ng chỉ l&agrave; một c&aacute;i kẻ ngu ngươi biết kh&ocirc;ng?&quot;<br />\r\n<br />\r\nThượng Quan c&ocirc;ng tử rất hiểu phỏng đo&aacute;n l&ograve;ng người, hắn hiện tại đối Ngụy Qu&acirc;n đ&atilde; rất h&agrave;i l&ograve;ng, nhưng l&agrave; hắn muốn khảo nghiệm Ngụy Qu&acirc;n t&acirc;m t&iacute;nh.<br />\r\n<br />\r\nMột c&aacute;i ch&acirc;n ch&iacute;nh dũng sĩ, kh&ocirc;ng chỉ c&oacute; nếu c&oacute; thể c&oacute; can đảm đắc tội quyền qu&yacute;, c&ograve;n muốn c&oacute; can đảm ở b&ecirc;n người người đều kh&ocirc;ng đồng &yacute; t&igrave;nh huống hạ vẫn như cũ có th&ecirc;̉ dũng cảm tiến tới, kh&ocirc;ng bởi v&igrave; hiện thực &aacute;p lực m&agrave; c&uacute;i đầu.<br />\r\n<br />\r\nNgụy Qu&acirc;n l&agrave;m được.<br />\r\n<br />\r\nM&agrave; Ngụy Qu&acirc;n một lời n&oacute;i, thắng được hiện trường mọi người t&ocirc;n trọng:<br />\r\n<br />\r\n&quot;Th&aacute;i huynh học h&agrave;nh gian khổ hai mươi năm, g&aacute;nh chịu người một nh&agrave; kỳ vọng, chưa hề l&agrave;m qua thương thi&ecirc;n hại l&iacute; sự t&igrave;nh. Hắn sợ ngươi, đương nhi&ecirc;n. Ta đứng ra, nếu li&ecirc;n luỵ đến Th&aacute;i huynh, l&agrave; ta kh&ocirc;ng đ&uacute;ng. Cho n&ecirc;n hắn c&ugrave;ng ta ph&acirc;n r&otilde; quan hệ, cũng kh&ocirc;ng cái gì kh&ocirc;ng ổn, ta nội t&acirc;m vẫn như cũ xem hắn l&agrave; bạn, cũng chỉ hi vọng hắn chớ c&oacute; tr&aacute;ch ta đứng ra cũng được.&quot;<br />\r\n<br />\r\nTh&aacute;i Kỳ L&acirc;m: &quot;. . .&quot;<br />\r\n<br />\r\nHắn dừng bước, nh&igrave;n về ph&iacute;a Ngụy Qu&acirc;n &aacute;nh mắt kh&ocirc;ng phải b&igrave;nh thường cảm động.<br />\r\n<br />\r\nGiờ khắc n&agrave;y, hắn thậm ch&iacute; cảm thấy đ&ecirc;́n Diệu &Acirc;m phường c&ocirc; nương cũng kh&ocirc;ng bằng Ngụy Qu&acirc;n tới c&agrave;ng l&agrave;m cho hắn t&acirc;m động.<br />\r\n<br />\r\nCũng ch&iacute;nh l&agrave; hắn kh&ocirc;ng phải c&aacute;i nữ, kh&ocirc;ng phải x&aacute;c định vững chắc lấy th&acirc;n b&aacute;o đ&aacute;p.<br />\r\n<br />\r\nBất qu&aacute; Th&aacute;i Kỳ L&acirc;m rất nhanh lại nghĩ tới, ch&iacute;nh m&igrave;nh c&ograve;n l&agrave; tư duy qu&aacute; hẹp h&ograve;i, ai n&oacute;i nam liền kh&ocirc;ng thể lấy th&acirc;n b&aacute;o đ&aacute;p?<br />\r\n<br />\r\nĐồng t&iacute;nh chi ph&iacute;ch tại kinh th&agrave;nh lại kh&ocirc;ng hiếm thấy.<br />\r\n<br />\r\nKh&ocirc;ng n&oacute;i đến Th&aacute;i Kỳ L&acirc;m &yacute; nghĩ, Ngụy Qu&acirc;n lời n&oacute;i n&agrave;y, khuất phục cơ hồ sở hữu người.<br />\r\n<br />\r\nD&ugrave; l&agrave; đại gia vẫn như cũ sợ h&atilde;i Thượng Quan c&ocirc;ng tử tiếng xấu, nhưng vẫn l&agrave; c&oacute; người nhịn kh&ocirc;ng được g&otilde; nhịp t&aacute;n thưởng: &quot;C&ocirc;ng tử n&oacute;i rất hay.&quot;<br />\r\n<br />\r\n&quot;Trạng nguy&ecirc;n c&ocirc;ng ti&ecirc;u ph&iacute; h&ocirc;m nay ghi tạc ta t&agrave;i khoản.&quot;<br />\r\n<br />\r\n&quot;Một th&acirc;n ch&iacute;nh kh&iacute;, ta kh&ocirc;ng bằng.&quot;<br />\r\n<br />\r\n&quot;Tất cả im miệng cho ta.&quot;<br />\r\n<br />\r\nThượng Quan c&ocirc;ng tử nội t&acirc;m vui sướng, nhưng mặt lạnh như sương.<br />\r\n<br />\r\nB&ecirc;n cạnh hắn một c&aacute;i người hầu c&agrave;ng l&agrave; tiện tay đưa tới một thanh trường đao, sau đ&oacute; tại trước mắt bao người, ngạnh sinh sinh đem trường đao h&oacute;a th&agrave;nh bột mịn.<br />\r\n<br />\r\nDiệu &Acirc;m phường lại lần nữa c&acirc;m như hến.<br />\r\n<br />\r\nĐại gia theo đối Ngụy Qu&acirc;n thưởng thức về tới hiện thực.<br />\r\n<br />\r\nCuối c&ugrave;ng vẫn kh&ocirc;ng c&oacute; người n&agrave;o d&aacute;m đối kh&aacute;ng ch&iacute;nh diện Thượng Quan c&ocirc;ng tử, bởi v&igrave; hắn sau lưng quyền thế, cũng bởi v&igrave; hắn người hầu thực lực.<br />\r\n<br />\r\nThượng Quan c&ocirc;ng tử nh&igrave;n chung quanh một v&ograve;ng, thẳng đến tất cả mọi người kh&ocirc;ng d&aacute;m c&ugrave;ng hắn ch&iacute;nh diện đối xem, hắn nhếch miệng l&ecirc;n một mạt mỉm cười, sau đ&oacute; một lần nữa nh&igrave;n về ph&iacute;a Ngụy Qu&acirc;n.<br />\r\n<br />\r\n&quot;Hiện tại, sợ sao?&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><a href=\"https://c.lazada.vn/t/c.06cO5B?url=https%3A%2F%2Fpages.lazada.vn%2Fwow%2Fgcp%2Froute%2Flazada%2Fvn%2Fupr_1000345_lazada%2Fchannel%2Fvn%2Fupr-router%2Fvn%3Fhybrid%3D1%26data_prefetch%3Dtrue%26prefetch_replace%3D1%26at_iframe%3D1%26wh_pid%3D%2Flazada%2Fmegascenario%2Fvn%2Fsale-gap-boi-5-5-2022%2Fngay-doi-sale-boi\" target=\"_blank\"><img src=\"https://static.cdnno.com/storage/lazada/D5_AFF_MAIN_743x400.jpg\" /></a></p>\r\n\r\n<p><br />\r\n<br />\r\nNgụy Qu&acirc;n tiến l&ecirc;n một bước, sắc mặt ki&ecirc;n nghị, thậm ch&iacute; &aacute;nh mắt nhiều hơn mấy phần k&iacute;ch động: &quot;Sợ? Ta Ngụy Qu&acirc;n đọc th&aacute;nh hiền chi thư, tu hạo nhi&ecirc;n ch&iacute;nh kh&iacute;, quản thế gian chuyện bất b&igrave;nh, sao l&agrave; sợ? Thượng Quan c&ocirc;ng tử, ta vẫn l&agrave; c&acirc;u n&oacute;i kia, ngươi c&oacute; c&aacute;i g&igrave; liền x&ocirc;ng ta tới, kh&ocirc;ng muốn li&ecirc;n luỵ những người kh&aacute;c, c&agrave;ng đừng lại li&ecirc;n luỵ Mộng c&ocirc; nương.&quot;<br />\r\n<br />\r\nXoạt xoạt.<br />\r\n<br />\r\nMộng c&ocirc; nương gian ph&ograve;ng cửa bị mở ra.<br />\r\n<br />\r\nMộng c&ocirc; nương xuất hiện tại trong mắt mọi người.<br />\r\n<br />\r\nC&oacute; như vậy nh&aacute;y mắt b&ecirc;n trong, to&agrave;n bộ Diệu &Acirc;m phường đều tối đi một ch&uacute;t, bởi v&igrave; bị Mộng c&ocirc; nương dung nhan đoạt đi tất cả &aacute;nh s&aacute;ng m&agrave;u.<br />\r\n<br />\r\nMộng c&ocirc; nương đầu ti&ecirc;n l&agrave; con mắt hiện dị sắc nh&igrave;n Ngụy Qu&acirc;n một ch&uacute;t, sau đ&oacute; đối Thượng Quan c&ocirc;ng tử c&uacute;i ch&agrave;o một lễ, mở miệng n&oacute;i: &quot;Thượng Quan c&ocirc;ng tử, ta đ&atilde; ra tới, c&oacute; thể hay kh&ocirc;ng đừng c&oacute; lại t&igrave;m những người kh&aacute;c phiền phức?&quot;<br />\r\n<br />\r\nThượng Quan c&ocirc;ng tử c&ograve;n chưa l&ecirc;n tiếng, Ngụy Qu&acirc;n trước gấp.<br />\r\n<br />\r\nNgươi n&agrave;y tiểu thư tỷ d&agrave;i cũng kh&ocirc;ng tệ lắm, như thế n&agrave;o t&acirc;m địa như vậy &aacute;c độc đ&acirc;u?<br />\r\n<br />\r\nTa hảo t&acirc;m gi&uacute;p ngươi, ngươi thế m&agrave; muốn cứu ta mệnh, thật sự l&agrave; kh&ocirc;ng l&agrave;m người nữ.<br />\r\n<br />\r\nNgụy Qu&acirc;n ki&ecirc;n quyết kh&ocirc;ng cho ph&eacute;p n&agrave;y loại sự t&igrave;nh ph&aacute;t sinh, cho n&ecirc;n hắn nghĩa ch&iacute;nh ng&ocirc;n từ đoạt tại Thượng Quan c&ocirc;ng tử ph&iacute;a trước mở miệng: &quot;Mộng c&ocirc; nương kh&ocirc;ng cần phải lo lắng, cũng kh&ocirc;ng cần tr&aacute;i lương t&acirc;m phụ họa bực n&agrave;y ho&agrave;n khố &aacute;c thiếu. Nếu Mộng c&ocirc; nương v&igrave; gi&uacute;p ta m&agrave; bị bực n&agrave;y &aacute;c thiếu sở nh&uacute;ng ch&agrave;m, ta Ngụy Qu&acirc;n c&ograve;n c&oacute; mặt mũi n&agrave;o sống tại n&agrave;y thi&ecirc;n địa chi gian?&quot;<br />\r\n<br />\r\nMộng c&ocirc; nương th&acirc;n thể run l&ecirc;n, nh&igrave;n thật s&acirc;u Ngụy Qu&acirc;n một ch&uacute;t, ch&acirc;n th&agrave;nh n&oacute;i: &quot;Ngụy c&ocirc;ng tử, ngươi lại tiếp tục ki&ecirc;n tr&igrave;, thật sẽ chết.&quot;<br />\r\n<br />\r\nN&oacute;i n&agrave;y c&acirc;u n&oacute;i thời điểm, Mộng c&ocirc; nương d&ugrave;ng ch&acirc;n ng&ocirc;n thuật, l&agrave;m cho tất cả mọi người đều theo bản năng tin tưởng lời n&agrave;ng n&oacute;i liền l&agrave; ch&acirc;n l&yacute;.<br />\r\n<br />\r\nNgụy Qu&acirc;n cũng tin.<br />\r\n<br />\r\nC&ograve;n c&oacute; chuyện tốt như vậy?<br />\r\n<br />\r\nHắn cười thập phần vui vẻ: &quot;Chết lại c&oacute; sợ g&igrave;? Ta nếu như h&ocirc;m nay r&uacute;t lui, ta mới l&agrave; thật chết rồi.&quot;<br />\r\n<br />\r\nMộng c&ocirc; nương đạo t&acirc;m rung động.<br />\r\n<br />\r\nN&agrave;y loại thấy chết kh&ocirc;ng sờn h&agrave;o h&ugrave;ng, thật sự l&agrave; qu&aacute; l&agrave;m cho người thưởng thức.<br />\r\n<br />\r\n( bản chương xong )</p>', '2022-05-08 02:49:31', 1, 1),
(333, 87, 'Trọng Sinh Trở Về', '1', '<p>&quot;Ta kh&ocirc;ng cam t&acirc;m a, ta kh&ocirc;ng cam t&acirc;m a!&quot;<br />\r\n<br />\r\nMa Đ&ocirc; đại học t&uacute;c x&aacute; 404 trong ph&ograve;ng ngủ, truyền ra một trận tiếng rống giận dữ.<br />\r\n<br />\r\nL&acirc;m Tử Lạc đột nhi&ecirc;n từ tr&ecirc;n giường ngồi dậy.<br />\r\n<br />\r\nChỉ thấy hắn giờ ph&uacute;t n&agrave;y sắc mặt t&aacute;i nhợt, đầu đầy mồ h&ocirc;i, tựa hồ vừa mới l&agrave;m một c&aacute;i &aacute;c mộng.<br />\r\n<br />\r\n&quot;Chuyện g&igrave; xảy ra, ta kh&ocirc;ng l&agrave; chết sao?&quot;<br />\r\n<br />\r\nL&acirc;m Tử Lạc chưa tỉnh hồn, d&ugrave;ng hai tay bưng k&iacute;n mặt m&igrave;nh, tr&ecirc;n dưới xoa bỗng nh&uacute;c nh&iacute;ch, nghĩ để cho m&igrave;nh tỉnh t&aacute;o lại.<br />\r\n<br />\r\nBất qu&aacute;, đ&acirc;y l&agrave; tay của ta c&ugrave;ng mặt?<br />\r\n<br />\r\nCảm thụ được b&agrave;n tay ở tr&ecirc;n mặt trơn nhẵn x&uacute;c cảm.<br />\r\n<br />\r\nHắn nh&igrave;n về ph&iacute;a b&agrave;n tay của m&igrave;nh.<br />\r\n<br />\r\nTrước mắt b&agrave;n tay trắng n&otilde;n, b&oacute;ng lo&aacute;ng, c&ugrave;ng m&igrave;nh trước đ&oacute; th&ocirc; r&aacute;p lại phủ đầy vết chai hai tay ho&agrave;n to&agrave;n kh&aacute;c biệt.<br />\r\n<br />\r\nChuyện n&agrave;y rốt cuộc l&agrave; như thế n&agrave;o?<br />\r\n<br />\r\nL&acirc;m Tử Lạc nh&igrave;n về ph&iacute;a bốn ph&iacute;a.<br />\r\n<br />\r\nNơi n&agrave;y l&agrave; ta thời đại học t&uacute;c x&aacute;?<br />\r\n<br />\r\nPhủ bụi 10 năm tr&iacute; nhớ bị c&aacute;i n&agrave;y ho&agrave;n cảnh quen thuộc x&uacute;c động, đ&atilde; l&acirc;u nhớ lại ở L&acirc;m Tử Lạc trong đầu hiển hiện.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng sai, đ&acirc;y ch&iacute;nh l&agrave; thời đại học t&uacute;c x&aacute;! Tr&agrave;ng cảnh n&agrave;y bố cục, ta c&ograve;n c&oacute; một ch&uacute;t ấn tượng!&quot;<br />\r\n<br />\r\nHắn k&iacute;ch động n&oacute;i ra, sau đ&oacute; cấp tốc xuống giường.<br />\r\n<br />\r\nGi&agrave;y cũng chưa kịp mặc, th&igrave; chạy v&agrave;o ph&ograve;ng vệ sinh.<br />\r\n<br />\r\nNh&igrave;n lấy trong gương tấm kia gương mặt đẹp trai, L&acirc;m Tử Lạc sững sờ ngay tại chỗ.<br />\r\n<br />\r\nGương mặt n&agrave;y ban đầu c&aacute;i kia hiện đầy xấu x&iacute; vết sẹo, hiện nay lại sạch sẽ.<br />\r\n<br />\r\n10 năm!<br />\r\n<br />\r\nPhủ đầy vết sẹo mặt l&agrave;m đến L&acirc;m Tử Lạc mười năm n&agrave;y đến nay, kh&ocirc;ng c&ograve;n c&oacute; chủ động chiếu qua tấm gương.<br />\r\n<br />\r\nHắn trở lại t&uacute;c x&aacute; lấy ra điện thoại di động của m&igrave;nh, tra nh&igrave;n l&ecirc;n lịch ng&agrave;y.<br />\r\n<br />\r\n【 Lam Tinh trải qua 478 năm ngày 23 tháng 5 】<br />\r\n<br />\r\nThời gian, địa điểm, bề ngo&agrave;i, bao qu&aacute;t m&igrave;nh b&acirc;y giờ th&acirc;n thể tố chất. . .<br />\r\n<br />\r\nTa trọng sinh!<br />\r\n<br />\r\nTa trọng sinh đến tận thế tr&ograve; chơi bu&ocirc;ng xuống một ng&agrave;y n&agrave;y!<br />\r\n<br />\r\nL&acirc;m Tử Lạc rốt cuộc hiểu r&otilde; ph&aacute;t sinh trước mắt hết thảy!<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\n&quot;C&aacute;i kia hai c&aacute;i một mực l&agrave; trung lập trận doanh Chiến Thần mời ta, n&oacute;i l&agrave; ch&uacute;c mừng ta trở th&agrave;nh Chiến Thần.&quot;<br />\r\n<br />\r\n&quot;Ở ch&uacute;ng ta thảo luận thời điểm, Di&ecirc;u Tịnh H&agrave;m chỉ huy một c&aacute;i Zombies Chi Ho&agrave;ng vọt v&agrave;o! T&iacute;nh cả c&aacute;i kia hai c&aacute;i phản bội Chiến Thần, li&ecirc;n thủ giết ta!&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y Zombies Chi Ho&agrave;ng cũng kh&ocirc;ng biết c&ugrave;ng L&yacute; gia ở giữa đ&atilde; đạt th&agrave;nh thỏa thuận g&igrave;, chủ yếu hơn ch&iacute;nh l&agrave; tại sao c&oacute; thể c&oacute; ho&agrave;n to&agrave;n tỏa định Truyền Thuyết cấp kết giới.&quot;<br />\r\n<br />\r\nL&acirc;m Tử Lạc ngồi tr&ecirc;n ghế, hồi tưởng lại ch&iacute;nh m&igrave;nh l&uacute;c c&ograve;n sống ph&aacute;t sinh sự t&igrave;nh.<br />\r\n<br />\r\nDi&ecirc;u Tịnh H&agrave;m!</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><a href=\"https://c.lazada.vn/t/c.06cO5B?url=https%3A%2F%2Fpages.lazada.vn%2Fwow%2Fgcp%2Froute%2Flazada%2Fvn%2Fupr_1000345_lazada%2Fchannel%2Fvn%2Fupr-router%2Fvn%3Fhybrid%3D1%26data_prefetch%3Dtrue%26prefetch_replace%3D1%26at_iframe%3D1%26wh_pid%3D%2Flazada%2Fmegascenario%2Fvn%2Fsale-gap-boi-5-5-2022%2Fngay-doi-sale-boi\" target=\"_blank\"><img src=\"https://static.cdnno.com/storage/lazada/D5_AFF_MAIN_743x400.jpg\" /></a></p>\r\n\r\n<p><br />\r\n<br />\r\nChỉ l&agrave; nghĩ đến c&aacute;i n&agrave;y t&ecirc;n, L&acirc;m Tử Lạc trong l&ograve;ng đều đ&atilde; tu&ocirc;n ra v&ocirc; hạn hận &yacute;, đem nắm đấm siết thật chặt.<br />\r\n<br />\r\nC&aacute;i n&agrave;y c&ugrave;ng m&igrave;nh c&ugrave;ng giường chung gối qua bạn g&aacute;i!<br />\r\n<br />\r\nC&aacute;i n&agrave;y được xưng l&agrave; Ma Đ&ocirc; đại học hoa kh&ocirc;i bạn g&aacute;i!<br />\r\n<br />\r\nThật l&agrave; khiến người buồn n&ocirc;n!<br />\r\n<br />\r\nỞ L&yacute; gia thiếu gia dưới chỉ thị tới gần L&acirc;m Tử Lạc, nỗ lực d&ugrave;ng c&aacute;c loại thủ đoạn đến đả k&iacute;ch hắn, từ đ&oacute; ph&aacute; hủy tinh thần &yacute; ch&iacute; của hắn!<br />\r\n<br />\r\nTận thế bu&ocirc;ng xuống về sau, hai người gặp nhau lần nữa thời điểm, c&ograve;n c&ugrave;ng L&yacute; gia thiếu gia c&ugrave;ng nhau t&igrave;m cơ hội mưu hại hắn.<br />\r\n<br />\r\nMay ra L&acirc;m Tử Lạc mạng lớn, tuy nhi&ecirc;n hủy dung, nhưng l&agrave; sống tiếp được.<br />\r\n<br />\r\nD&ugrave; cho như thế, Di&ecirc;u Tịnh H&agrave;m vẫn kh&ocirc;ng c&oacute; bu&ocirc;ng tha L&acirc;m Tử Lạc.<br />\r\n<br />\r\nM&agrave; l&agrave; tại L&yacute; thị gia tộc che chở cho, kh&ocirc;ng ngừng đối L&acirc;m Tử Lạc tiến h&agrave;nh truy s&aacute;t, ch&egrave;n &eacute;p, giống như m&egrave;o vờn chuột.<br />\r\n<br />\r\nL&acirc;m Tử Lạc lực lượng một người l&agrave;m sao c&oacute; thể chống lại Long quốc một trong tam đại gia tộc L&yacute; thị gia tộc.<br />\r\n<br />\r\nPhải biết nơi n&agrave;y ch&iacute;nh l&agrave; Lam Tinh Long quốc.<br />\r\n<br />\r\nTo&agrave;n bộ Long quốc tuy nhi&ecirc;n từ ch&iacute;nh phủ quản l&yacute;.<br />\r\n<br />\r\nNhưng l&agrave; tam đại gia tộc ở Long quốc sức ảnh hưởng cũng kh&ocirc;ng thể khinh thường.<br />\r\n<br />\r\nN&oacute;i trắng ra l&agrave;, cũng l&agrave; trong truyền thuyết Thổ Ho&agrave;ng Đế.<br />\r\n<br />\r\nĐồng thời tam đại gia tộc mỗi gia tộc đều nắm giữ thật kh&ocirc;ng thể tin nội t&igrave;nh.<br />\r\n<br />\r\nD&ugrave;ng c&oacute; thể tẩy n&atilde;o người b&iacute; thuật bồi dưỡng được đại lượng ho&agrave;n to&agrave;n trung với gia tộc tử sĩ, c&ograve;n c&oacute; c&oacute; thể tăng l&ecirc;n người th&acirc;n thể tố chất b&iacute; thuật. . .<br />\r\n<br />\r\nNhững thứ n&agrave;y b&iacute; thuật th&uacute;c đẩy tam đại gia tộc thực lực ở tận thế sau c&agrave;ng th&ecirc;m b&agrave;nh trướng.<br />\r\n<br />\r\nMay ra l&uacute;c mới bắt đầu, L&yacute; gia cũng kh&ocirc;ng c&oacute; đem L&acirc;m Tử Lạc để ở trong mắt.<br />\r\n<br />\r\nĐể L&acirc;m Tử Lạc ở k&eacute;o d&agrave;i hơi t&agrave;n b&ecirc;n trong, đạt được kh&ocirc;ng ngừng tăng l&ecirc;n cơ hội.<br />\r\n<br />\r\nD&ugrave; vậy, ở L&acirc;m Tử Lạc hao hết trăm cay ngh&igrave;n đắng đột ph&aacute; đến cấp 90, trở th&agrave;nh Long quốc người thứ mười Chiến Thần thời điểm.<br />\r\n<br />\r\nĐều c&ograve;n chưa kịp hướng Di&ecirc;u Tịnh H&agrave;m b&aacute;o th&ugrave;.<br />\r\n<br />\r\nLiền bị Di&ecirc;u Tịnh H&agrave;m mang theo một c&aacute;i Zombies Chi Ho&agrave;ng, t&iacute;nh cả hai g&atilde; kh&aacute;c Chiến Thần, sử dụng Truyền Thuyết cấp đạo cụ đem hắn s&aacute;t hại.<br />\r\n<br />\r\nMười năm đến nay, L&acirc;m Tử Lạc đối Di&ecirc;u Tịnh H&agrave;m hận thấu xương, mỗi thời mỗi khắc đều muốn đem n&agrave;ng giết chết!<br />\r\n<br />\r\nMuốn kh&ocirc;ng phải một mực bị L&yacute; gia bảo hộ lấy. . .<br />\r\n<br />\r\nĐ&uacute;ng rồi! Giết chết!<br />\r\n<br />\r\nL&acirc;m Tử Lạc k&iacute;ch động tay cũng bắt đầu run rẩy l&ecirc;n.<br />\r\n<br />\r\nTa trọng sinh!<br />\r\n<br />\r\nTận thế c&ograve;n kh&ocirc;ng c&oacute; bu&ocirc;ng xuống!<br />\r\n<br />\r\nDi&ecirc;u Tịnh H&agrave;m ngo&agrave;i mặt vẫn l&agrave; bạn g&aacute;i của ta!<br />\r\n<br />\r\nTa ho&agrave;n to&agrave;n c&oacute; thể sớm giải quyết n&agrave;ng!<br />\r\n<br />\r\n....., trước nh&igrave;n thời gian!<br />\r\n<br />\r\nL&acirc;m Tử Lạc cấp tốc l&agrave;m ch&iacute;nh m&igrave;nh tỉnh t&aacute;o lại.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; tận thế nhất định phải đ&atilde; th&agrave;nh th&oacute;i quen, chỉ c&oacute; ở cực hạn tỉnh t&aacute;o t&igrave;nh huống dưới, mới c&oacute; thể c&agrave;ng th&ecirc;m dễ d&agrave;ng suy nghĩ.<br />\r\n<br />\r\nHắn lần nữa lật ra ch&iacute;nh m&igrave;nh cũ n&aacute;t điện thoại di động.<br />\r\n<br />\r\nThời gian b&acirc;y giờ l&agrave; buổi chiều 5:24.<br />\r\n<br />\r\nTr&ecirc;n điện thoại di động biểu hiện c&ograve;n c&oacute; Di&ecirc;u Tịnh H&agrave;m ph&aacute;t tới một c&aacute;i tin tức.<br />\r\n<br />\r\nMật m&atilde; l&agrave;. . . Di&ecirc;u Tịnh H&agrave;m sinh nhật.<br />\r\n<br />\r\nC&ugrave;ng Di&ecirc;u Tịnh H&agrave;m c&oacute; huyết hải th&acirc;m cừu L&acirc;m Tử Lạc l&agrave;m sao c&oacute; thể qu&ecirc;n l&atilde;ng c&aacute;i số n&agrave;y.<br />\r\n<br />\r\nMở ra điện thoại di động kh&oacute;a về sau, L&acirc;m Tử Lạc thấy được trong tin tức cho.<br />\r\n<br />\r\n&quot;Tử Lạc, tối nay s&aacute;u giờ, ta cần muốn đi ra ngo&agrave;i cho h&agrave;i tử học b&ugrave;, xin lỗi a, h&ocirc;m nay sinh nhật của ngươi, ta th&igrave; kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o c&ugrave;ng ngươi qua.&quot;<br />\r\n<br />\r\nVẻn vẹn nh&igrave;n trong tin tức cho, ngươi cảm thấy đ&acirc;y l&agrave; một c&aacute;i tự lực c&aacute;nh sinh c&ocirc; g&aacute;i tốt.<br />\r\n<br />\r\nDuy nhất điểm đ&aacute;ng ngờ cũng l&agrave; v&igrave; sao lại ở bạn trai sinh nhật thời điểm, đi cho người kh&aacute;c học b&ugrave;.<br />\r\n<br />\r\nChỉ c&oacute; L&acirc;m Tử Lạc biết, thế n&agrave;y sao lại l&agrave; cho tiểu h&agrave;i tử học b&ugrave;, đ&acirc;y l&agrave; cho L&yacute; gia Đại thiếu gia tr&ecirc;n giường học b&ugrave; đi!<br />\r\n<br />\r\nL&yacute; gia thiếu gia, t&ecirc;n đầy đủ L&yacute; Hạo B&aacute;c.<br />\r\n<br />\r\nKh&ocirc;ng biết v&igrave; c&aacute;i g&igrave;, hắn đối L&acirc;m Tử Lạc rất l&agrave; căm th&ugrave;, một mực duy tr&igrave; m&egrave;o vờn chuột tr&ecirc;u đ&ugrave;a.<br />\r\n<br />\r\nDi&ecirc;u Tịnh H&agrave;m cũng l&agrave; thụ L&yacute; Hạo B&aacute;c chỉ thị, l&agrave;m bộ c&ugrave;ng L&acirc;m Tử Lạc c&ugrave;ng một chỗ.<br />\r\n<br />\r\nĐến mức L&acirc;m Tử Lạc l&agrave; l&agrave;m sao đem sự t&igrave;nh đều nhớ r&otilde; r&agrave;ng như vậy?<br />\r\n<br />\r\nBởi v&igrave; cũng l&agrave; ở bảy giờ tối nay, một m&igrave;nh hắn ở t&uacute;c x&aacute; sinh nhật thời điểm.<br />\r\n<br />\r\nL&yacute; Hạo B&aacute;c đem Di&ecirc;u Tịnh H&agrave;m như thế n&agrave;o phụ đạo hắn video t&iacute;nh cả chi ti&ecirc;u giấy tờ, c&ograve;n c&oacute; Di&ecirc;u Tịnh H&agrave;m &quot;Ch&uacute;c thọ video&quot; đều ph&aacute;t cho L&acirc;m Tử Lạc.<br />\r\n<br />\r\nTr&ugrave;ng hợp l&uacute;c bảy giờ, lại l&agrave; &quot;Tận thế tr&ograve; chơi&quot; bu&ocirc;ng xuống thời điểm.<br />\r\n<br />\r\nNhận loại đả k&iacute;ch n&agrave;y L&acirc;m Tử Lạc bởi v&igrave; bi thương bỏ qua tốt nhất trưởng th&agrave;nh giai đoạn, lạc hậu hơn người kh&aacute;c.<br />\r\n<br />\r\nB&acirc;y giờ trọng sinh L&acirc;m Tử Lạc tuyệt đối sẽ kh&ocirc;ng để lịch sử t&aacute;i diễn.<br />\r\n<br />\r\nHắn cầm điện thoại di động l&ecirc;n, cho Di&ecirc;u Tịnh H&agrave;m ph&aacute;t một c&aacute;i tin tức.<br />\r\n<br />\r\n&quot;Tịnh H&agrave;m, kh&ocirc;ng c&oacute; quan hệ, đ&atilde; ngươi buổi tối c&oacute; chuyện, vậy ch&uacute;ng ta trước hết gặp một lần, đi thao trường ph&iacute;a sau c&aacute;i kia vứt bỏ nh&agrave; kho đi.&quot;<br />\r\n<br />\r\nGửi đi hết tin tức, L&acirc;m Tử Lạc liền bắt đầu lục tung l&ecirc;n.<br />\r\n<br />\r\nRốt cục, nương tựa theo mơ hồ ấn tượng, ở một c&aacute;i bạn c&ugrave;ng ph&ograve;ng dưới giường, t&igrave;m được một nắm sắc b&eacute;n dao găm.<br />\r\n<br />\r\nĐừng hỏi v&igrave; sao lại c&oacute;.<br />\r\n<br />\r\nXin nhờ, l&ecirc;n qua đại học người n&agrave;o kh&ocirc;ng biết, nam sinh trong t&uacute;c x&aacute; kh&ocirc;ng c&oacute; mấy c&aacute;i gia hỏa sự t&igrave;nh, chỗ n&agrave;o phối gọi nam sinh t&uacute;c x&aacute;.<br />\r\n<br />\r\nL&acirc;m Tử Lạc đem dao găm ước lượng trong ngực, lại cầm l&ecirc;n điện thoại di động.<br />\r\n<br />\r\nDi&ecirc;u Tịnh H&agrave;m đ&atilde; bồi thường tin tức.<br />\r\n<br />\r\n&quot;Tốt a, tại sao muốn ở nơi đ&oacute; gặp mặt a? Bất qu&aacute;, ta 6 giờ trước đ&oacute; nhất định phải đi, ngươi nhanh điểm đi.&quot; &mdash; &mdash; Di&ecirc;u Tịnh H&agrave;m.<br />\r\n<br />\r\nBởi v&igrave; ta muốn lấy mạng ch&oacute; của ngươi a.<br />\r\n<br />\r\nL&acirc;m Tử Lạc cũng kh&ocirc;ng c&oacute; ph&aacute;t ra ngo&agrave;i, m&agrave; ch&iacute;nh l&agrave; x&oacute;a bỏ c&acirc;u n&oacute;i n&agrave;y, một lần nữa ph&aacute;t một c&acirc;u đi qua.<br />\r\n<br />\r\n&quot;Bởi v&igrave; ta muốn cho ngươi một c&aacute;i ngạc nhi&ecirc;n.&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><a href=\"https://c.lazada.vn/t/c.06cO5B?url=https%3A%2F%2Fpages.lazada.vn%2Fwow%2Fgcp%2Froute%2Flazada%2Fvn%2Fupr_1000345_lazada%2Fchannel%2Fvn%2Fupr-router%2Fvn%3Fhybrid%3D1%26data_prefetch%3Dtrue%26prefetch_replace%3D1%26at_iframe%3D1%26wh_pid%3D%2Flazada%2Fmegascenario%2Fvn%2Fsale-gap-boi-5-5-2022%2Fngay-doi-sale-boi\" target=\"_blank\"><img src=\"https://static.cdnno.com/storage/lazada/D5_AFF_MAIN_743x400.jpg\" /></a></p>\r\n\r\n<p><br />\r\n<br />\r\nNh&igrave;n đến tr&ecirc;n điện thoại di động biểu hiện gửi đi ho&agrave;n tất.<br />\r\n<br />\r\nL&acirc;m Tử Lạc đưa điện thoại di động trực tiếp bỏ v&agrave;o trong t&uacute;i quần, lại tr&ecirc;n lưng một c&aacute;i chứa đựng quần &aacute;o ba l&ocirc;.<br />\r\n<br />\r\nSau đ&oacute; mở ra cửa t&uacute;c x&aacute;, đi ra ngo&agrave;i.<br />\r\n<br />\r\nNam sinh t&uacute;c x&aacute; tr&ecirc;n h&agrave;nh lang.<br />\r\n<br />\r\nKh&ocirc;ng &iacute;t người đều tập hợp một chỗ, kho&aacute;c l&aacute;c kho&aacute;c l&aacute;c, h&uacute;t thuốc th&igrave; h&uacute;t thuốc.<br />\r\n<br />\r\nC&ograve;n c&oacute; đang thảo luận v&igrave; c&aacute;i g&igrave; bầu trời lại biến th&agrave;nh m&agrave;u đỏ.<br />\r\n<br />\r\nL&acirc;m Tử Lạc trước đ&oacute; một mực mặt lạnh l&ugrave;ng sắc ở đi ra ngo&agrave;i trong nh&aacute;y mắt th&igrave; biến th&agrave;nh &ocirc;n h&ograve;a l&ecirc;n.<br />\r\n<br />\r\nMột điểm cũng nh&igrave;n kh&ocirc;ng ra đến, hắn sắp đi giết người!<br />\r\n<br />\r\nT&ugrave;y thời t&ugrave;y chỗ chuyển h&oacute;a sắc mặt, đ&acirc;y cũng l&agrave; trong mạt thế một m&ocirc;n m&ocirc;n bắt buộc.<br />\r\n<br />\r\nPh&iacute;a trước một c&aacute;i đứng đấy nam sinh tr&ecirc;n điện thoại di động c&ograve;n tại ph&aacute;t h&igrave;nh chuy&ecirc;n gia thanh &acirc;m.<br />\r\n<br />\r\n&quot;Lần n&agrave;y to&agrave;n cầu phạm vi b&ecirc;n trong sắc trời biến đỏ thực tế v&igrave; năm ng&agrave;n năm vừa gặp hiện tượng tự nhi&ecirc;n, mời mọi người kh&ocirc;ng n&ecirc;n kinh hoảng, hưởng thụ cảnh đẹp l&agrave; đủ.&quot;<br />\r\n<br />\r\nL&acirc;m Tử Lạc cũng nh&igrave;n một ch&uacute;t đỏ như m&aacute;u bầu trời.<br />\r\n<br />\r\nChỉ c&oacute; hắn biết, đ&acirc;y l&agrave; biểu thị &quot;Tận thế tr&ograve; chơi&quot; tức sắp gi&aacute;ng l&acirc;m.<br />\r\n<br />\r\nNhững người ở trước mắt hiện tại c&ograve;n như thế nh&agrave;n nh&atilde;.<br />\r\n<br />\r\nChờ đến bảy giờ đồng hồ, &quot;Tận thế tr&ograve; chơi&quot; bu&ocirc;ng xuống về sau.<br />\r\n<br />\r\nBiến zombies, bị zombies cắn, c&oacute; thể c&ograve;n sống s&oacute;t cũng kh&ocirc;ng biết c&ograve;n c&oacute; mấy c&aacute;i.<br />\r\n<br />\r\nL&acirc;m Tử Lạc theo thang lầu hướng về dưới lầu đi đến.<br />\r\n<br />\r\nTrong l&ograve;ng đem chuyện sắp xảy ra th&ocirc;i diễn một lần.<br />\r\n<br />\r\nHắn ph&aacute;t hiện m&igrave;nh đ&atilde; xuy&ecirc;n qua thao trường, đi tới vứt bỏ trước cửa kho h&agrave;ng.<br />\r\n<br />\r\nC&aacute;i n&agrave;y vứt bỏ nh&agrave; kho kh&ocirc;ng đến ban đ&ecirc;m, cơ hồ l&agrave; kh&ocirc;ng c&oacute; người tới.<br />\r\n<br />\r\nL&acirc;m Tử Lạc lại cẩn thận đem bốn ph&iacute;a quan s&aacute;t một phen, bảo đảm kh&ocirc;ng ai ch&uacute; &yacute; về sau, đi v&agrave;o vứt bỏ nh&agrave; kho.<br />\r\n<br />\r\nVừa v&agrave;o nh&agrave; kho, L&acirc;m Tử Lạc liền thấy ngồi tr&ecirc;n ghế Di&ecirc;u Tịnh H&agrave;m.<br />\r\n<br />\r\nCh&iacute;nh m&igrave;nh trước đ&oacute; thật l&agrave; một c&aacute;i đần độn!<br />\r\n<br />\r\nL&acirc;m Tử Lạc nhịn kh&ocirc;ng được mắng.<br />\r\n<br />\r\nL&uacute;c trước bị &aacute;i t&igrave;nh l&agrave;m cho&aacute;ng v&aacute;ng đầu &oacute;c, ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; ch&uacute; &yacute; tới Di&ecirc;u Tịnh H&agrave;m mặc tr&ecirc;n người những c&aacute;i kia h&agrave;ng xa xỉ, h&agrave;ng hiệu.<br />\r\n<br />\r\nNhững vật n&agrave;y kh&ocirc;ng c&oacute; c&aacute;i s&aacute;u chữ số đều kh&oacute; m&agrave; cầm xuống.<br />\r\n<br />\r\nNhư thế n&agrave;o lại l&agrave; một c&aacute;i b&igrave;nh thường gia đ&igrave;nh Di&ecirc;u Tịnh H&agrave;m c&oacute; thể gồng g&aacute;nh nổi.<br />\r\n<br />\r\nDi&ecirc;u Tịnh H&agrave;m cũng ch&uacute; &yacute; tới v&agrave;o cửa L&acirc;m Tử Lạc.<br />\r\n<br />\r\nN&agrave;ng ngồi tr&ecirc;n ghế cũng kh&ocirc;ng c&oacute; đứng dậy.<br />\r\n<br />\r\nM&agrave; l&agrave; v&ocirc; c&ugrave;ng kh&ocirc;ng nhịn được n&oacute;i: &quot;Ngươi l&agrave;m sao mới đến a, c&oacute; c&aacute;i g&igrave; kinh hỉ mau n&oacute;i, ta chờ đi học b&ugrave; đ&acirc;y.&quot;</p>', '2022-05-08 02:52:09', 1, 1),
(334, 87, 'Diêu Tịnh Hàm Chết Thảm', '2', '<p>L&acirc;m Tử Lạc kh&ocirc;ng c&oacute; trả lời, hắn hướng về c&aacute;i n&agrave;y để cho m&igrave;nh ghi hận 10 năm nữ nh&acirc;n đi tới.<br />\r\n<br />\r\nDi&ecirc;u Tịnh H&agrave;m ch&acirc;n m&agrave;y nh&iacute;u c&agrave;ng chặt: &quot;Ngươi kh&ocirc;ng n&oacute;i lời n&agrave;o c&oacute; &yacute; tứ g&igrave; a, kh&ocirc;ng n&oacute;i lời n&agrave;o ta c&oacute; thể liền đi.&quot;<br />\r\n<br />\r\nN&oacute;i xong, Di&ecirc;u Tịnh H&agrave;m đứng l&ecirc;n.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, L&acirc;m Tử Lạc chạy tới Di&ecirc;u Tịnh H&agrave;m trước mặt.<br />\r\n<br />\r\nHắn nhanh ch&oacute;ng duỗi ra một c&aacute;i tay, một nắm b&oacute;p lấy Di&ecirc;u Tịnh H&agrave;m.<br />\r\n<br />\r\nDi&ecirc;u Tịnh H&agrave;m nơi n&agrave;o sẽ nghĩ đến trước đ&oacute; đối với m&igrave;nh trăm theo trăm L&acirc;m Tử Lạc sẽ l&agrave;m ra loại chuyện n&agrave;y.<br />\r\n<br />\r\nN&agrave;ng liều mạng giằng co, lại căn bản v&ocirc; dụng.<br />\r\n<br />\r\nL&acirc;m Tử Lạc kh&iacute; lực xa so với nu&ocirc;ng chiều từ b&eacute; Di&ecirc;u Tịnh H&agrave;m lớn hơn.<br />\r\n<br />\r\n&quot;478 năm ngày 16 tháng 3, Ma Đ&ocirc; Si&ecirc;u Hiện Đại kh&aacute;ch sạn, ph&iacute; tổn 53239 nguy&ecirc;n.&quot;<br />\r\n<br />\r\nL&acirc;m Tử Lạc thanh &acirc;m trầm thấp vang l&ecirc;n, Di&ecirc;u Tịnh H&agrave;m tựa hồ chưa kịp phản ứng L&acirc;m Tử Lạc n&oacute;i c&aacute;i g&igrave; &yacute; tứ.<br />\r\n<br />\r\nMột nắm sắc b&eacute;n dao găm xuất hiện tại L&acirc;m Tử Lạc trong tay.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\n&quot;478 năm ngày 29 tháng 3, Ma Đ&ocirc; Khải Đế kh&aacute;ch sạn, ph&iacute; tổn 10 9984 nguy&ecirc;n.&quot;<br />\r\n<br />\r\nL&acirc;m Tử Lạc tiếp tục tự m&igrave;nh n&oacute;i ra.<br />\r\n<br />\r\nDi&ecirc;u Tịnh H&agrave;m rốt cục kịp phản ứng, c&aacute;i n&agrave;y l&agrave; m&igrave;nh c&ugrave;ng L&yacute; Hạo B&aacute;c mướn ph&ograve;ng ghi ch&eacute;p a!<br />\r\n<br />\r\nN&agrave;ng muốn giải th&iacute;ch, muốn cầu xin tha thứ, lại căn vốn kh&ocirc;ng c&oacute; một cơ hội nhỏ nhoi n&agrave;o.<br />\r\n<br />\r\n&quot;478 năm ngày 13 tháng 4, Confusion quầy rượu, ph&iacute; tổn 409999 nguy&ecirc;n.&quot;<br />\r\n<br />\r\nDi&ecirc;u Tịnh H&agrave;m trong mắt tr&agrave;n đầy tuyệt vọng.<br />\r\n<br />\r\n&quot;478 năm ngày 25 tháng 4, Ma Đ&ocirc; Si&ecirc;u Hiện Đại kh&aacute;ch sạn, ph&iacute; tổn 8 4320 nguy&ecirc;n.&quot;<br />\r\n<br />\r\n&quot;478 năm ngày 29 tháng 4. . . .&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><a href=\"https://c.lazada.vn/t/c.06cO5B?url=https%3A%2F%2Fpages.lazada.vn%2Fwow%2Fgcp%2Froute%2Flazada%2Fvn%2Fupr_1000345_lazada%2Fchannel%2Fvn%2Fupr-router%2Fvn%3Fhybrid%3D1%26data_prefetch%3Dtrue%26prefetch_replace%3D1%26at_iframe%3D1%26wh_pid%3D%2Flazada%2Fmegascenario%2Fvn%2Fsale-gap-boi-5-5-2022%2Fngay-doi-sale-boi\" target=\"_blank\"><img src=\"https://static.cdnno.com/storage/lazada/D5_AFF_MAIN_743x400.jpg\" /></a></p>\r\n\r\n<p><br />\r\n<br />\r\nL&acirc;m Tử Lạc b&igrave;nh thản n&oacute;i đến đ&acirc;y ch&uacute;t ch&iacute;nh m&igrave;nh nhớ mười năm lời n&oacute;i.<br />\r\n<br />\r\nMỗi n&oacute;i ra một c&acirc;u, mặt đất đều sẽ th&ecirc;m ra một vũng m&aacute;u dịch.<br />\r\n<br />\r\nM&agrave; mặt đất từ l&acirc;u bị huyết dịch cho nhiễm đ&ecirc;́n đỏ tươi.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nHắn một c&aacute;i đem n&agrave;ng đẩy đến tr&ecirc;n mặt ghế.<br />\r\n<br />\r\nL&uacute;c n&agrave;y Di&ecirc;u Tịnh H&agrave;m, trong mắt hiện đầy tuyệt vọng, hối hận, kh&ocirc;ng hiểu, o&aacute;n độc. . .<br />\r\n<br />\r\nL&acirc;m Tử Lạc c&uacute;i người, từ dưới đất nhặt l&ecirc;n Di&ecirc;u Tịnh H&agrave;m rơi xuống điện thoại di động.<br />\r\n<br />\r\nTiếp lấy lợi dụng v&acirc;n tay giải tỏa, th&agrave;nh c&ocirc;ng mở ra điện thoại di động.<br />\r\n<br />\r\nĐiện thoại di động giao diện dừng lại ch&iacute;nh l&agrave; khung ch&aacute;t ch&iacute;t.<br />\r\n<br />\r\nDi&ecirc;u: &quot;Hắc hắc, ta kh&ocirc;ng kịp chờ đợi nghĩ muốn đi t&igrave;m ngươi nữa nha.&quot;<br />\r\n<br />\r\nL&yacute;: &quot;C&ugrave;ng L&acirc;m Tử Lạc n&oacute;i sao? Kh&ocirc;ng n&oacute;i nhưng l&agrave; kh&ocirc;ng dễ chơi.&quot;<br />\r\n<br />\r\nDi&ecirc;u: &quot;N&oacute;i, hắn c&ograve;n tưởng rằng ta thật đi cho người ta học th&ecirc;m đ&acirc;y.&quot;<br />\r\n<br />\r\nL&yacute;: &quot;C&aacute;i kia để ngươi chuẩn bị cho hắn qu&agrave; sinh nhật đ&acirc;u? .&quot;<br />\r\n<br />\r\nDi&ecirc;u: &quot;Y&ecirc;n t&acirc;m đi, chuẩn bị xong, ngươi cũng kh&ocirc;ng muốn qu&ecirc;n ta nh&igrave;n tr&uacute;ng c&aacute;i kia h&atilde;ng t&uacute;i s&aacute;ch nha.&quot;<br />\r\n<br />\r\nN&oacute;i chuyện phiếm giao diện đến nơi đ&acirc;y, l&agrave; một đoạn video.<br />\r\n<br />\r\nNh&igrave;n đến c&aacute;i n&agrave;y quen thuộc video, L&acirc;m Tử Lạc nhẹ nh&agrave;ng ấn mở, sau đ&oacute; đem m&agrave;n ảnh đối hướng về ph&iacute;a Di&ecirc;u Tịnh H&agrave;m.<br />\r\n<br />\r\nTrong video, Di&ecirc;u Tịnh H&agrave;m đứng tại ống k&iacute;nh trước n&oacute;i đến n&oacute;i.<br />\r\n<br />\r\n&quot;L&acirc;m Tử Lạc, ch&uacute;c ngươi sinh nhật vui vẻ.&quot;<br />\r\n<br />\r\n&quot;Ngươi cũng kh&ocirc;ng d&ugrave;ng đầu &oacute;c suy nghĩ một ch&uacute;t, ta th&acirc;n l&agrave; Ma Đ&ocirc; đại học hoa kh&ocirc;i, l&agrave;m sao c&oacute; thể để &yacute; ngươi c&aacute;i n&agrave;y người kh&ocirc;ng c&oacute; đồng n&agrave;o, ngoại trừ mặt b&ecirc;n ngo&agrave;i kh&ocirc;ng c&oacute; c&aacute;i g&igrave; c&ocirc; nhi?&quot;<br />\r\n<br />\r\n&quot;Muốn kh&ocirc;ng phải L&yacute; gia Đại thiếu gia đặc biệt cường điệu ngươi, loại người như ngươi ta đều chẳng muốn nh&igrave;n một ch&uacute;t.&quot;<br />\r\n<br />\r\n&quot;Bất qu&aacute; thật vẫn l&agrave; muốn c&aacute;m ơn ngươi, muốn kh&ocirc;ng phải ngươi, ta cũng sẽ kh&ocirc;ng bị L&yacute; thiếu gia nh&igrave;n tr&uacute;ng, kiếm lời nhiều như vậy.&quot;<br />\r\n<br />\r\nTh&igrave; c&aacute;i n&agrave;y một đoạn ngắn video.<br />\r\n<br />\r\nB&agrave;y ra tr&ecirc;n ghế Di&ecirc;u Tịnh H&agrave;m mở to hai mắt nh&igrave;n.<br />\r\n<br />\r\nN&agrave;ng căn bản kh&ocirc;ng hiểu, n&agrave;ng ở mấy mươi ph&uacute;t trước một người vụng trộm ghi lại video, l&agrave;m sao lại bị người một chữ kh&ocirc;ng k&eacute;m đọc ra.<br />\r\n<br />\r\n&quot;Ha ha ha ha!&quot;<br />\r\n<br />\r\nL&acirc;m Tử Lạc nở nụ cười.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nĐầu người rơi xuống đất<br />\r\n<br />\r\nL&acirc;m Tử Lạc ngồi tr&ecirc;n mặt đất.<br />\r\n<br />\r\nRốt cục bị ta l&agrave;m được!<br />\r\n<br />\r\n10 năm a!<br />\r\n<br />\r\nR&ograve;ng r&atilde; 10 năm a!<br />\r\n<br />\r\nMười năm n&agrave;y qu&aacute; kh&oacute; khăn!<br />\r\n<br />\r\nĐ&uacute;ng rồi c&ograve;n k&eacute;m L&yacute; Hạo B&aacute;c! C&ograve;n k&eacute;m L&yacute; gia!<br />\r\n<br />\r\nL&uacute;c n&agrave;y, một c&aacute;i máy móc giọng n&oacute;i tổng hợp ở L&acirc;m Tử Lạc b&ecirc;n tai vang l&ecirc;n.<br />\r\n<br />\r\n&quot;Đinh, ch&uacute;c mừng k&iacute; chủ th&agrave;nh c&ocirc;ng trọng sinh đến mười năm trước, k&iacute;ch hoạt 【 tận thế tr&ograve; chơi ng&oacute;n tay v&agrave;ng hệ thống 】.&quot;<br />\r\n<br />\r\nL&acirc;m Tử Lạc lấy ra điện thoại di động của m&igrave;nh.<br />\r\n<br />\r\nThời gian biểu hiện 6: 00.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><a href=\"https://c.lazada.vn/t/c.06cO5B?url=https%3A%2F%2Fpages.lazada.vn%2Fwow%2Fgcp%2Froute%2Flazada%2Fvn%2Fupr_1000345_lazada%2Fchannel%2Fvn%2Fupr-router%2Fvn%3Fhybrid%3D1%26data_prefetch%3Dtrue%26prefetch_replace%3D1%26at_iframe%3D1%26wh_pid%3D%2Flazada%2Fmegascenario%2Fvn%2Fsale-gap-boi-5-5-2022%2Fngay-doi-sale-boi\" target=\"_blank\"><img src=\"https://static.cdnno.com/storage/lazada/D5_AFF_MAIN_743x400.jpg\" /></a></p>\r\n\r\n<p><br />\r\n<br />\r\nC&aacute;i n&agrave;y sao c&oacute; thể!<br />\r\n<br />\r\nTận thế tr&ograve; chơi hẳn l&agrave; bảy giờ mới c&oacute; thể bu&ocirc;ng xuống to&agrave;n cầu, đem to&agrave;n cầu cải tạo th&agrave;nh một c&aacute;i tận thế cầu sinh tr&ograve; chơi.<br />\r\n<br />\r\nTo&agrave;n bộ thế giới đều muốn số liệu tr&ograve; chơi h&oacute;a, khi đ&oacute; cần phải mới c&oacute; hệ thống tồn tại.<br />\r\n<br />\r\nM&agrave; lại c&aacute;i n&agrave;y ng&oacute;n tay v&agrave;ng. . .<br />\r\n<br />\r\nC&aacute;i n&agrave;y ta l&agrave;m sao chưa từng c&oacute; nghe qua a.<br />\r\n<br />\r\n&quot;Đinh, tận thế tr&ograve; chơi ng&oacute;n tay v&agrave;ng hệ thống, c&oacute; thể ph&aacute; giải trong tận thế tr&ograve; chơi số liệu, cho k&iacute; chủ mang đến tốt hơn tr&ograve; chơi thể nghiệm.&quot;<br />\r\n<br />\r\n&quot;Hệ thống nhận chủ ho&agrave;n th&agrave;nh, mời k&iacute; chủ r&uacute;t ra điều thứ nhất si&ecirc;u cấp ng&oacute;n tay v&agrave;ng.&quot;<br />\r\n<br />\r\nL&acirc;m Tử Lạc cấp tốc bắt đầu ph&acirc;n t&iacute;ch l&ecirc;n ch&iacute;nh m&igrave;nh nghe được những thứ n&agrave;y.<br />\r\n<br />\r\nTrọng sinh sao?<br />\r\n<br />\r\nĐ&acirc;y l&agrave; ta trọng sinh lấy được hệ thống?<br />\r\n<br />\r\nBất qu&aacute; hệ thống n&agrave;y h&igrave;nh dung như thế n&agrave;o lợi hại như vậy!<br />\r\n<br />\r\nLại c&ograve;n c&oacute; thể ph&aacute; giải tận thế tr&ograve; chơi số liệu!<br />\r\n<br />\r\nL&acirc;m Tử Lạc tuy nhi&ecirc;n kh&ocirc;ng tin, nhưng l&agrave; c&ograve;n nếm thử t&iacute;nh mở miệng n&oacute;i: &quot;R&uacute;t ra điều thứ nhất si&ecirc;u cấp ng&oacute;n tay v&agrave;ng.&quot;<br />\r\n<br />\r\n&quot;Đinh, điều thứ nhất si&ecirc;u cấp ng&oacute;n tay v&agrave;ng ngay tại r&uacute;t ra b&ecirc;n trong.&quot;<br />\r\n<br />\r\n&quot;Đinh, ch&uacute;c mừng người chơi thu được si&ecirc;u cấp ng&oacute;n tay v&agrave;ng: Tuyệt đối tỉ lệ rơi đồ.&quot;<br />\r\n<br />\r\n&quot;Đinh, tuyệt đối tỉ lệ rơi đồ: Đem người chơi tr&ograve; chơi qu&aacute; tr&igrave;nh b&ecirc;n trong tất cả ch&eacute;m giết zombies đồ vật tỉ lệ rơi đồ từ 1% đề cao đến 100%.&quot;<br />\r\n<br />\r\n2</p>', '2022-05-08 02:52:35', 1, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(335, 88, 'Lãnh Cung Lão Thái Giám, Phụ Linh Không Rõ Chim', '1', '<p>Đại Tề Vương Triều.<br />\r\n<br />\r\nLớn như vậy l&atilde;nh cung b&ecirc;n trong, cơ hồ kh&ocirc;ng cảm gi&aacute;c được nửa điểm nh&acirc;n kh&iacute;.<br />\r\n<br />\r\nL&atilde;nh l&atilde;nh thanh thanh lẳng lặng, th&ecirc; th&ecirc; thảm thảm ưu tư.<br />\r\n<br />\r\nTại ho&agrave;ng h&ocirc;n ph&iacute;a dưới, b&oacute;ng c&acirc;y lắc lư, gi&oacute; lạnh thổi phật, ph&aacute;t ra thanh &acirc;m &ocirc; &ocirc;, giống như quỷ ảnh ho&agrave;nh h&agrave;nh.<br />\r\n<br />\r\nCộc cộc cộc. . . .<br />\r\n<br />\r\nMột trận tập tễnh đ&ecirc;́n b&ocirc;̣ pháp từ xa đến gần, rất chậm rất chậm.<br />\r\n<br />\r\nKia l&agrave; một c&aacute;i gi&agrave; nua c&ograve;ng xuống đ&ecirc;́n th&acirc;n ảnh, một tiếng rộng r&atilde;i &aacute;o gai, t&ocirc;n l&ecirc;n th&acirc;n thể gầy g&ograve; như xương.<br />\r\n<br />\r\nTr&ecirc;n tay cầm lấy c&aacute;i chổi, hữu kh&iacute; v&ocirc; lực.<br />\r\n<br />\r\nPhảng phất bất cứ l&uacute;c n&agrave;o cũng sẽ tắt thở.<br />\r\n<br />\r\nTh&acirc;n ảnh ngừng lại, nh&igrave;n một ch&uacute;t lại rơi xuống một ch&uacute;t lá c&acirc;y mặt đất, yếu ớt thở d&agrave;i, sau đ&oacute; chậm r&atilde;i qu&eacute;t.<br />\r\n<br />\r\nHắn qu&eacute;t đến rất chậm.<br />\r\n<br />\r\nNhưng cũng rất cẩn thận.<br />\r\n<br />\r\nĐợi đến qu&eacute;t xong về sau, hắn thở hổn hển, ngẩng đầu nh&igrave;n ch&acirc;n trời một tia s&aacute;ng, tại dần dần biến mất.<br />\r\n<br />\r\nT&acirc;m t&igrave;nh của hắn cũng đồng dạng tịch li&ecirc;u ảm đạm.<br />\r\n<br />\r\n&quot;Ai, ta th&acirc;n thể n&agrave;y đo&aacute;n chừng liền kh&ocirc;ng chịu nổi, cũng n&ecirc;n giải tho&aacute;t.&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh thở d&agrave;i.<br />\r\n<br />\r\nHắn kh&ocirc;ng phải người của thế giới n&agrave;y, m&agrave; l&agrave; từ Địa Cầu xuy&ecirc;n qua m&agrave; tới.<br />\r\n<br />\r\nL&uacute;c đầu c&oacute; thể sống lại một đời, hẳn l&agrave; phi thường đ&aacute;ng được ăn mừng.<br />\r\n<br />\r\nNhưng Diệp Th&agrave;nh cảm thấy m&igrave;nh l&agrave; đắc tội Vận Mệnh nữ thần, kiếp trước tuổi c&ograve;n trẻ liền đột tử, hiện tại xuy&ecirc;n qua đến thế giới n&agrave;y, v&acirc;̣y mà th&agrave;nh một c&aacute;i th&acirc;n thể kh&ocirc;ng trọn vẹn th&aacute;i gi&aacute;m.<br />\r\n<br />\r\nTh&aacute;i gi&aacute;m c&ograve;n chưa t&iacute;nh.<br />\r\n<br />\r\nThế m&agrave; c&ograve;n l&agrave; hơn t&aacute;m mươi tuổi l&atilde;o th&aacute;i gi&aacute;m.<br />\r\n<br />\r\nL&atilde;o th&aacute;i gi&aacute;m c&ograve;n chưa t&iacute;nh, thế m&agrave; kh&ocirc;ng c&oacute; luyện qua ch&uacute;t điểm v&otilde; c&ocirc;ng, c&agrave;ng kh&ocirc;ng c&oacute; nửa phần quyền h&agrave;nh.<br />\r\n<br />\r\nC&aacute;i n&agrave;y c&oacute; chút kh&ocirc;ng hợp th&oacute;i thường.<br />\r\n<br />\r\nLiền th&acirc;n thể n&agrave;y, đo&aacute;n chừng c&ograve;n c&oacute; thể chống đỡ c&aacute;i tầm năm ba th&aacute;ng, c&oacute; lẽ c&ograve;n cần kh&ocirc;ng được.<br />\r\n<br />\r\nCho n&ecirc;n, hắn nguy&ecirc;n bản xuy&ecirc;n qua tr&ugrave;ng sinh mang tới vui sướng, lập tức liền bị tuyệt vọng tr&agrave;n đầy, cả người trở n&ecirc;n &yacute; ch&iacute; tinh thần sa s&uacute;t, kh&ocirc;ng c&oacute; nửa phần hi vọng.<br />\r\n<br />\r\nMỗi ng&agrave;y cứ như vậy ngơ ngơ ng&aacute;c ng&aacute;c đ&ecirc;́n c&ograve;n sống.<br />\r\n<br />\r\n&quot;Tốt bao nhi&ecirc;u thế giới a, c&oacute; cường đại v&otilde; c&ocirc;ng, cường đại người c&oacute; thể lăng kh&ocirc;ng bay qua, nhưng quyền n&aacute;t sơn h&agrave;, c&oacute; thể kiếm mở Thi&ecirc;n M&ocirc;n, c&oacute; thể trường sinh cửu thị. . . . .&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh qu&eacute;t xong địa chi về sau, liền chậm từng c&aacute;i từng c&aacute;i đ&ecirc;́n về tới trong ph&ograve;ng của m&igrave;nh, an vị tại tr&ecirc;n ghế nằm, nh&igrave;n xem b&ecirc;n ngo&agrave;i c&agrave;ng ng&agrave;y c&agrave;ng mờ bầu trời, trong hồi ức cỗ th&acirc;n thể n&agrave;y b&ecirc;n trong c&oacute; quan hệ với thế giới n&agrave;y tin tức.<br />\r\n<br />\r\nĐ&aacute;ng tiếc, đ&acirc;y hết thảy c&ugrave;ng hắn kh&ocirc;ng c&oacute; quan hệ.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><a href=\"https://c.lazada.vn/t/c.06cO5B?url=https%3A%2F%2Fpages.lazada.vn%2Fwow%2Fgcp%2Froute%2Flazada%2Fvn%2Fupr_1000345_lazada%2Fchannel%2Fvn%2Fupr-router%2Fvn%3Fhybrid%3D1%26data_prefetch%3Dtrue%26prefetch_replace%3D1%26at_iframe%3D1%26wh_pid%3D%2Flazada%2Fmegascenario%2Fvn%2Fsale-gap-boi-5-5-2022%2Fngay-doi-sale-boi\" target=\"_blank\"><img src=\"https://static.cdnno.com/storage/lazada/D5_AFF_MAIN_743x400.jpg\" /></a></p>\r\n\r\n<p><br />\r\n<br />\r\nHắn xuy&ecirc;n qua tới đ&atilde; mấy ng&agrave;y, nhưng chưa bao giờ c&oacute; từng đi ra c&aacute;i n&agrave;y l&atilde;nh cung.<br />\r\n<br />\r\nL&atilde;nh cung rất lớn.<br />\r\n<br />\r\nCũng chỉ c&oacute; ba người.<br />\r\n<br />\r\nHắn c&aacute;i n&agrave;y nửa th&acirc;n thể tiến nhanh thổ l&atilde;o th&aacute;i gi&aacute;m.<br />\r\n<br />\r\nC&ograve;n c&oacute; một c&aacute;i đ&agrave;y v&agrave;o l&atilde;nh cung đ&atilde; năm năm, tinh thần ph&acirc;n liệt qu&yacute; phi, c&ugrave;ng một c&aacute;i năm tuổi tiểu ho&agrave;ng tử.<br />\r\n<br />\r\nDiệp Th&agrave;nh rất muốn trước khi chết, đi ra xem một ch&uacute;t thế giới n&agrave;y.<br />\r\n<br />\r\nĐ&aacute;ng tiếc, hắn kh&ocirc;ng c&oacute; cơ hội.<br />\r\n<br />\r\nTiến v&agrave;o l&atilde;nh cung người, l&agrave; tuyệt đối kh&ocirc;ng thể sống lấy đi ra.<br />\r\n<br />\r\nBao qu&aacute;t hắn c&aacute;i n&agrave;y l&atilde;nh cung l&atilde;o th&aacute;i gi&aacute;m.<br />\r\n<br />\r\nĐợi đến sau khi hắn chết, sẽ c&oacute; mới l&atilde;nh cung th&aacute;i gi&aacute;m tiến đến, đ&oacute;n hắn ban.<br />\r\n<br />\r\nOa oa. . . .<br />\r\n<br />\r\nBỗng nhi&ecirc;n, ngo&agrave;i cửa sổ vang l&ecirc;n một trận nghe rất r&ugrave;ng m&igrave;nh tiếng chim h&oacute;t.<br />\r\n<br />\r\nDiệp Th&agrave;nh đục ngầu &aacute;nh mắt, th&ocirc;ng qua cửa sổ, thấy được ngo&agrave;i cửa sổ nh&aacute;nh c&acirc;y tr&ecirc;n đầu, đứng đấy một c&aacute;i hắc điểu.<br />\r\n<br />\r\n&quot;Lại xuất hiện, đ&acirc;y l&agrave; muốn cho ta tống chung sao?&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh nh&igrave;n xem c&aacute;i n&agrave;y hắc điểu, trong l&ograve;ng cảm kh&aacute;i vạn phần.<br />\r\n<br />\r\nTừ khi hắn tr&ugrave;ng sinh đến cỗ th&acirc;n thể n&agrave;y b&ecirc;n tr&ecirc;n về sau, c&aacute;i n&agrave;y hắc điểu liền mỗi đ&ecirc;m thời gian, liền sẽ đ&uacute;ng giờ xuất hiện ở b&ecirc;n ngo&agrave;i, ph&aacute;t ra o&aacute;c o&aacute;c tiếng k&ecirc;u.<br />\r\n<br />\r\nC&aacute;i n&agrave;y ph&iacute;a trước th&acirc;n trong tr&iacute; nhớ l&agrave; kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nDiệp Th&agrave;nh chậm r&atilde;i nổi th&acirc;n, đi đến b&ecirc;n cửa sổ b&ecirc;n tr&ecirc;n, muốn nh&igrave;n r&otilde; r&agrave;ng tr&ecirc;n c&acirc;y hắc điểu đến c&ugrave;ng l&agrave; c&aacute;i g&igrave; chim.<br />\r\n<br />\r\nNi&ecirc;n kỷ của hắn lớn, con mắt đục ngầu, thị lực xuống đến lợi hại, chỉ c&oacute; thể nh&igrave;n r&otilde; r&agrave;ng bảy t&aacute;m m&eacute;t b&ecirc;n trong đồ vật.<br />\r\n<br />\r\nTheo Diệp Th&agrave;nh tới gần, c&aacute;i n&agrave;y hắc điểu cũng kh&ocirc;ng c&oacute; bị dọa chạy.<br />\r\n<br />\r\nNgược lại l&agrave;m cho c&agrave;ng mừng hơn.<br />\r\n<br />\r\nDiệp Th&agrave;nh rốt cục thấy r&otilde; r&agrave;ng c&aacute;i n&agrave;y hắc điểu d&aacute;ng vẻ.<br />\r\n<br />\r\nR&otilde; r&agrave;ng l&agrave; một con quạ.<br />\r\n<br />\r\n&quot;Thế giới n&agrave;y c&oacute; quạ đen sao?&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh ch&acirc;n m&agrave;y cau lại.<br />\r\n<br />\r\nTrong tr&iacute; nhớ tựa như l&agrave; kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nBất qu&aacute;, l&atilde;o th&aacute;i gi&aacute;m từ mười mấy tuổi liền tiến cung, trong ho&agrave;ng cung chờ đợi cả một đời, kh&ocirc;ng biết quạ đen cũng l&agrave; rất b&igrave;nh thường.<br />\r\n<br />\r\n&quot;Chim ch&oacute;c a, ngươi cũng đừng k&ecirc;u, ta trong thời gian ngắn c&ograve;n sẽ kh&ocirc;ng chết đ&acirc;u.&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh cười ha hả n&oacute;i.<br />\r\n<br />\r\nMặc d&ugrave; c&aacute;i n&agrave;y quạ đen to&agrave;n th&acirc;n đen nh&aacute;nh, tại một c&aacute;i thế giới kh&aacute;c ca tụng l&agrave; kh&ocirc;ng r&otilde; chi chim.<br />\r\n<br />\r\nNhưng Diệp Th&agrave;nh cũng kh&ocirc;ng th&egrave;m để &yacute;.<br />\r\n<br />\r\nD&ugrave; sao hắn sớm muộn cũng sẽ chết.<br />\r\n<br />\r\nHiện tại c&oacute; thể tại c&aacute;i n&agrave;y c&ocirc; tịch trong l&atilde;nh cung, nh&igrave;n thấy một con t&acirc;n sinh vật, hắn cảm thấy vẫn l&agrave; rất kh&ocirc;ng tệ.<br />\r\n<br />\r\nBỗng nhi&ecirc;n, con quạ đen kia tựa hồ nghe đ&atilde; hiểu Diệp Th&agrave;nh thanh &acirc;m, v&acirc;̣y mà từ tr&ecirc;n c&acirc;y bay xuống tới, rơi xuống Diệp Th&agrave;nh trước mặt tr&ecirc;n cửa sổ.<br />\r\n<br />\r\nDiệp Th&agrave;nh ngạc nhi&ecirc;n kh&ocirc;ng th&ocirc;i.<br />\r\n<br />\r\nC&aacute;i n&agrave;y quạ đen kh&ocirc;ng sợ m&igrave;nh?<br />\r\n<br />\r\nHắn c&ugrave;ng quạ đen liếc nhau một c&aacute;i, lu&ocirc;n cảm gi&aacute;c c&aacute;i n&agrave;y quạ đen rất th&ocirc;ng minh, thế l&agrave; nếm thử đưa tay đi chạm đến quạ đen.<br />\r\n<br />\r\nHắn rất cẩn thận.<br />\r\n<br />\r\nCũng rất cẩn thận.<br />\r\n<br />\r\nLo lắng sẽ dọa chạy quạ đen.<br />\r\n<br />\r\nKhi hắn ng&oacute;n tay chạm đến quạ đen đen nh&aacute;nh l&ocirc;ng vũ l&uacute;c, một loại kh&ocirc;ng n&oacute;i ra được băng l&atilde;nh cảm gi&aacute;c từ trong tay truyền lại to&agrave;n th&acirc;n, phảng phất muốn đ&ocirc;ng kết th&acirc;n thể thậm ch&iacute; linh hồn.<br />\r\n<br />\r\nSau một khắc, Diệp Th&agrave;nh to&agrave;n th&acirc;n giống như giống như bị chạm điện, đột nhi&ecirc;n r&uacute;t ng&oacute;n tay trở về.<br />\r\n<br />\r\nNhưng hắn lại thần th&aacute;i kh&ocirc;ng hiểu phải xem lấy c&aacute;i n&agrave;y quạ đen.<br />\r\n<br />\r\nBởi v&igrave; hắn c&ugrave;ng quạ đen ở giữa, xuất hiện một loại n&oacute;i kh&ocirc;ng n&ecirc;n lời kỳ dị li&ecirc;n hệ.<br />\r\n<br />\r\nBỗng nhi&ecirc;n, quạ đen oa oa khiếu.<br />\r\n<br />\r\nDiệp Th&agrave;nh tr&ograve;ng mắt đều nhanh trợn lồi ra.<br />\r\n<br />\r\nMặc d&ugrave; l&agrave; tiếng chim h&oacute;t, nhưng Diệp Th&agrave;nh thế m&agrave; nghe hiểu quạ đen tiếng k&ecirc;u &yacute; tứ.<br />\r\n<br />\r\n&quot;Chủ nh&acirc;n, mấy ng&agrave;y nay, bản quạ đi mấy c&aacute;i thế giới, rốt cục cho ng&agrave;i t&igrave;m được đồng dạng đồ tốt.&quot;<br />\r\n<br />\r\nẢo gi&aacute;c.<br />\r\n<br />\r\nKhẳng định l&agrave; ảo gi&aacute;c.<br />\r\n<br />\r\nM&igrave;nh lớn tuổi, tinh thần kh&ocirc;ng tốt, lại th&ecirc;m suy nghĩ lung tung, mới c&oacute; thể xuất hiện loại ảo gi&aacute;c n&agrave;y.<br />\r\n<br />\r\n&quot;Chủ nh&acirc;n, kh&ocirc;ng phải ảo gi&aacute;c, bản quạ thế nhưng l&agrave; ngươi phụ linh a.&quot;<br />\r\n<br />\r\nQuạ đen lại oa oa khiếu.<br />\r\n<br />\r\nDiệp Th&agrave;nh &eacute;p buộc m&igrave;nh tỉnh t&aacute;o lại, nội t&acirc;m ẩn ẩn c&oacute; ch&uacute;t vui mừng.<br />\r\n<br />\r\nChẳng lẽ c&aacute;i n&agrave;y quạ đen ch&iacute;nh l&agrave; m&igrave;nh xuy&ecirc;n qua đến thế giới n&agrave;y kim thủ chỉ sao?</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><a href=\"https://c.lazada.vn/t/c.06cO5B?url=https%3A%2F%2Fpages.lazada.vn%2Fwow%2Fgcp%2Froute%2Flazada%2Fvn%2Fupr_1000345_lazada%2Fchannel%2Fvn%2Fupr-router%2Fvn%3Fhybrid%3D1%26data_prefetch%3Dtrue%26prefetch_replace%3D1%26at_iframe%3D1%26wh_pid%3D%2Flazada%2Fmegascenario%2Fvn%2Fsale-gap-boi-5-5-2022%2Fngay-doi-sale-boi\" target=\"_blank\"><img src=\"https://static.cdnno.com/storage/lazada/D5_AFF_MAIN_743x400.jpg\" /></a></p>\r\n\r\n<p><br />\r\n&quot;Đ&uacute;ng vậy, chủ nh&acirc;n, bản quạ l&agrave;m ng&agrave;i phụ linh, n&oacute;i l&agrave; kim thủ chỉ cũng thực kh&ocirc;ng đủ.&quot;<br />\r\n<br />\r\nQuạ đen lại c&oacute; thể cảm ứng được Diệp Th&agrave;nh &yacute; nghĩ.<br />\r\n<br />\r\n&quot;Phụ linh l&agrave; c&aacute;i quỷ g&igrave;?&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh thầm nghĩ.<br />\r\n<br />\r\nQuạ đen oa oa khiếu: &quot;Chủ nh&acirc;n, bản quạ kh&ocirc;ng biết, chỉ biết l&agrave; bản quạ l&agrave; nương theo lấy ng&agrave;i m&agrave; ra đời, nếu như ng&agrave;i chết rồi, bản quạ cũng sẽ biến mất.&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh rất nghi hoặc, ph&iacute;a trước th&acirc;n trong tr&iacute; nhớ, thế giới n&agrave;y c&oacute; được cường đại v&otilde; giả, tu luyện c&aacute;c loại v&otilde; c&ocirc;ng tuyệt học, từ đ&oacute; c&oacute; phi ph&agrave;m lực lượng.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; một c&aacute;i thi&ecirc;n địa vĩ lực tập trung ở c&aacute; nh&acirc;n tr&ecirc;n người thế giới.<br />\r\n<br />\r\nCường giả vi t&ocirc;n.<br />\r\n<br />\r\nNhưng cũng kh&ocirc;ng c&oacute; li&ecirc;n quan tới phụ linh tin tức a?<br />\r\n<br />\r\nC&oacute; thể l&agrave; tiền th&acirc;n tiếp x&uacute;c kh&ocirc;ng đến li&ecirc;n quan tới phụ linh c&aacute;i n&agrave;y tin tức đi.<br />\r\n<br />\r\n&quot;Đ&atilde; ngươi l&agrave; ta phụ linh, vậy ta gọi ngươi tiểu Hắc đi, ngươi n&oacute;i l&agrave; ta phụ linh, vậy ngươi tổng hẳn l&agrave; c&oacute; c&aacute;i g&igrave; năng lực a?&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh trong l&ograve;ng hỏi.<br />\r\n<br />\r\n&quot;Chủ nh&acirc;n, bản quạ năng lực nhưng mạnh, c&oacute; thể xuy&ecirc;n qua Chư Thi&ecirc;n Vạn Giới, t&igrave;m kiếm c&aacute;c loại kỳ tr&acirc;n dị bảo, đồng thời mang theo trở về, cho chủ nh&acirc;n ng&agrave;i sử dụng.&quot;<br />\r\n<br />\r\nQuạ đen oa oa khiếu nói.<br />\r\n<br />\r\n&quot;A, trước ngươi n&oacute;i cho ta t&igrave;m đồng dạng đồ tốt, l&agrave; c&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh nghe xong tiểu Hắc n&oacute;i, lập tức vui mừng.<br />\r\n<br />\r\nC&aacute;i n&agrave;y quạ đen năng lực tựa hồ thật rất cường h&atilde;n a.<br />\r\n<br />\r\n&quot;Chủ nh&acirc;n, bản quạ hiện tại liền muốn phun ra, ng&agrave;i tiếp hảo.&quot;<br />\r\n<br />\r\nĐen nh&aacute;nh oa oa khiếu, sau đ&oacute; mở miệng ra, phun ra một đạo kỳ dị kim quang.<br />\r\n<br />\r\nĐạo kim quang n&agrave;y phun ra về sau, dần dần ph&oacute;ng đại, tạo th&agrave;nh một đạo đại kh&aacute;i giống như nho lớn nhỏ hạt ch&acirc;u m&agrave;u v&agrave;ng &oacute;ng.<br />\r\n<br />\r\nDiệp Th&agrave;nh đưa tay tiếp được, nh&igrave;n thấy b&agrave;n tay t&acirc;m hạt ch&acirc;u m&agrave;u v&agrave;ng &oacute;ng, rất nghi hoặc.<br />\r\n<br />\r\nC&aacute;i n&agrave;y thứ g&igrave;?<br />\r\n<br />\r\n&quot;Chủ nh&acirc;n, đ&acirc;y ch&iacute;nh l&agrave; đồ tốt, bản quạ tại xuy&ecirc;n qua đến một c&aacute;i thế giới thời điểm, vừa vặn c&oacute; một c&aacute;i sắp vẫn lạc v&otilde; giả, đem tự th&acirc;n tu vi tinh hoa &aacute;p s&uacute;c th&agrave;nh đan, chuẩn bị truyền độ cho hắn d&ograve;ng d&otilde;i, chủ yếu chủ nh&acirc;n hấp thu vi&ecirc;n n&agrave;y mệnh đan, rất nhanh liền c&oacute; thể c&oacute; được một th&acirc;n cường ho&agrave;nh tu vi, m&agrave; lại, mệnh đan ẩn chứa sinh mệnh chi lực, cũng c&oacute; thể v&igrave; ng&agrave;i bổ sung sinh cơ, k&eacute;o d&agrave;i tuổi thọ.&quot;<br />\r\n<br />\r\nQuạ đen oa oa khiếu.<br />\r\n<br />\r\nDiệp Th&agrave;nh kinh hỉ vạn phần, như thế một vi&ecirc;n mệnh đan, hiệu quả lại lốt như vậy sao?<br />\r\n<br />\r\n. . . .</p>', '2022-05-08 02:56:15', 1, 1),
(336, 88, 'Sinh Tử Lựa Chọn Lúc, Mạo Hiểm Nuốt Mệnh đan', '2', '<p>&quot;Tiểu Hắc, vậy ta l&agrave;m như thế n&agrave;o sử dụng vi&ecirc;n n&agrave;y mệnh đan?&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh cố n&eacute;n nội t&acirc;m k&iacute;ch động, tỉnh t&aacute;o phải hỏi nói.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y. . . . Chủ nh&acirc;n, bản quạ cũng kh&ocirc;ng biết a.&quot;<br />\r\n<br />\r\nQuạ đen oa oa khiếu nói.<br />\r\n<br />\r\n&quot;Ngươi kh&ocirc;ng biết? Vậy l&agrave;m sao ngươi biết mệnh đan n&agrave;y c&ocirc;ng hiệu?&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh b&oacute; tay rồi.<br />\r\n<br />\r\n&quot;Chủ nh&acirc;n, bản quạ l&agrave; nghe l&eacute;n đến người v&otilde; giả kia lời n&oacute;i, mới biết, nhưng hắn lại kh&ocirc;ng c&oacute; n&oacute;i l&agrave;m sao sử dụng.&quot;<br />\r\n<br />\r\nQuạ đen oa oa khiếu, mang theo một tia ủy khuất hương vị.<br />\r\n<br />\r\n&quot;Được được được, c&aacute;i n&agrave;y kh&ocirc;ng tr&aacute;ch được ngươi, ngươi cũng l&agrave; tốt bụng.&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh trong l&ograve;ng thở d&agrave;i.<br />\r\n<br />\r\nMặc d&ugrave; kh&ocirc;ng biết l&agrave;m sao sử dụng vi&ecirc;n n&agrave;y mệnh đan, t&oacute;m lại c&oacute; thể chậm r&atilde;i nếm thử, phương ph&aacute;p cũng liền như vậy mấy loại.<br />\r\n<br />\r\nTrực tiếp nuốt.<br />\r\n<br />\r\nNhỏ m&aacute;u.<br />\r\n<br />\r\n. . . .<br />\r\n<br />\r\nDiệp Th&agrave;nh nh&igrave;n xem kim sắc mệnh đan, suy nghĩ một hồi, trực tiếp nuốt khẳng định l&agrave; kh&ocirc;ng được, nếu l&agrave; xuất hiện c&ugrave;ng tiền thế xem qua huyền huyễn tiểu thuyết, dược lực qu&aacute; mạnh, th&acirc;n thể của m&igrave;nh bị no bạo l&agrave;m sao b&acirc;y giờ?<br />\r\n<br />\r\n&quot;Tiểu Hắc, nếu kh&ocirc;ng ngươi đi địa phương kh&aacute;c, t&igrave;m cho ta một ch&uacute;t c&oacute; quan hệ với mệnh đan phương diện thư tịch tới.&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh trong l&ograve;ng hơi động, l&uacute;c n&agrave;y thầm nghĩ.<br />\r\n<br />\r\n&quot;Chủ nh&acirc;n, bản quạ chỉ c&oacute; tại hừng đ&ocirc;ng về sau, mới c&oacute; thể xuy&ecirc;n thẳng qua Chư Thi&ecirc;n Vạn Giới, sau khi trời tối, cũng chỉ c&oacute; thể trở lại ng&agrave;i b&ecirc;n người, m&agrave; lại, ở c&aacute;i thế giới n&agrave;y, bản quạ l&agrave; kh&ocirc;ng thể rời đi chủ nh&acirc;n ngươi b&ecirc;n ngo&agrave;i trăm trượng.&quot;<br />\r\n<br />\r\nQuạ đen oa oa khiếu nói.<br />\r\n<br />\r\n&quot;Ngươi ngay cả thế giới kh&aacute;c đều c&oacute; thể đi, ch&uacute;ng ta c&aacute;ch xa nhau kh&ocirc;ng biết bao xa, ngươi ở c&aacute;i thế giới n&agrave;y thế m&agrave; kh&ocirc;ng thể rời đi ta trăm trượng?&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh kinh ngạc cực k&igrave;.<br />\r\n<br />\r\n&quot;Chủ nh&acirc;n, bản quạ cũng kh&ocirc;ng biết, c&oacute; lẽ đ&acirc;y ch&iacute;nh l&agrave; l&agrave;m ng&agrave;i phụ linh một ch&uacute;t hạn chế.&quot;<br />\r\n<br />\r\nQuạ đen oa oa khiếu nói.<br />\r\n<br />\r\nDiệp Th&agrave;nh chậm r&atilde;i gật đầu, &quot;Ừm, vậy ngươi hừng đ&ocirc;ng về sau, lại cho ta đi thế giới kh&aacute;c t&igrave;m li&ecirc;n quan tới mệnh đan thư tịch, tốt nhất l&agrave; trước ngươi đi qua thế giới kia.&quot;<br />\r\n<br />\r\n&quot;Chủ nh&acirc;n, c&aacute;i n&agrave;y cũng kh&ocirc;ng được, bản quạ có th&ecirc;̉ đi thế giới l&agrave; ngẫu nhi&ecirc;n, thậm ch&iacute; khả năng xuất hiện l&agrave; tĩnh mịch thế giới, trước đ&oacute; bản quạ mỗi lần hừng đ&ocirc;ng đều sẽ xuy&ecirc;n thẳng qua thế giới, nhưng chỉ c&oacute; h&ocirc;m nay tiến v&agrave;o một c&aacute;i c&oacute; sinh mệnh thế giới, c&aacute;i kh&aacute;c mấy lần đều l&agrave; hoang vu vắng lặng. . . .&quot;<br />\r\n<br />\r\nQuạ đen nh&acirc;n t&iacute;nh h&oacute;a đến nỗi ngay cả lắc đầu, ph&aacute;t ra o&aacute;c o&aacute;c thanh &acirc;m.<br />\r\n<br />\r\n&quot;Vậy qu&ecirc;n đi, ngươi có th&ecirc;̉ mang về c&aacute;i g&igrave; liền mang c&aacute;i g&igrave; đi.&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh cũng liền kh&ocirc;ng bắt buộc.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><a href=\"https://c.lazada.vn/t/c.06cO5B?url=https%3A%2F%2Fpages.lazada.vn%2Fwow%2Fgcp%2Froute%2Flazada%2Fvn%2Fupr_1000345_lazada%2Fchannel%2Fvn%2Fupr-router%2Fvn%3Fhybrid%3D1%26data_prefetch%3Dtrue%26prefetch_replace%3D1%26at_iframe%3D1%26wh_pid%3D%2Flazada%2Fmegascenario%2Fvn%2Fsale-gap-boi-5-5-2022%2Fngay-doi-sale-boi\" target=\"_blank\"><img src=\"https://static.cdnno.com/storage/lazada/D5_AFF_MAIN_743x400.jpg\" /></a></p>\r\n\r\n<p><br />\r\nC&aacute;i n&agrave;y quạ đen có th&ecirc;̉ xuy&ecirc;n thẳng qua Chư Thi&ecirc;n Vạn Giới, cho m&igrave;nh mang về dị giới chi vật, đ&atilde; coi như l&agrave; cực k&igrave; cường h&atilde;n thủ đoạn.<br />\r\n<br />\r\nM&igrave;nh l&agrave;m sao c&ograve;n c&oacute; thể chọn ba lấy bốn đ&acirc;u.<br />\r\n<br />\r\nDiệp Th&agrave;nh bắt đầu nghi&ecirc;n cứu tr&ecirc;n tay mệnh đan.<br />\r\n<br />\r\nB&oacute;ng đ&ecirc;m gi&aacute;ng l&acirc;m.<br />\r\n<br />\r\nTrong ph&ograve;ng c&agrave;ng ph&aacute;t ra mờ tối.<br />\r\n<br />\r\nDiệp Th&agrave;nh trong l&ograve;ng b&agrave;n tay mệnh đan, phảng phất c&oacute; thể tự động bị long đong, cũng biến th&agrave;nh mờ đi rất nhiều, phảng phất muốn c&ugrave;ng lờ mờ h&ograve;a l&agrave;m một thể.<br />\r\n<br />\r\n&quot;Nếu kh&ocirc;ng thử trước một ch&uacute;t nhỏ m&aacute;u luyện h&oacute;a chi ph&aacute;p?&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh trong l&ograve;ng t&iacute;nh to&aacute;n một phen về sau, l&uacute;c n&agrave;y c&oacute; quyết định.<br />\r\n<br />\r\nThế l&agrave; hắn c&oacute; ch&uacute;t rung động rung động phải đi đến trong ph&ograve;ng bếp.<br />\r\n<br />\r\nN&oacute;i l&agrave; ph&ograve;ng bếp, kỳ thật b&ecirc;n trong kh&ocirc;ng c&oacute; nồi b&aacute;t c&aacute;i g&igrave;, ngay cả nh&oacute;m lửa địa phương đều kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nL&atilde;nh cung l&atilde;o th&aacute;i gi&aacute;m b&igrave;nh thường sinh hoạt điều kiện dị thường gian khổ.<br />\r\n<br />\r\nB&igrave;nh thường căn bản ăn kh&ocirc;ng được n&oacute;ng h&ocirc;i hổi đồ ăn.<br />\r\n<br />\r\nMặc d&ugrave; cung đ&igrave;nh sẽ cung ứng đồ ăn, nhưng đưa tới thời điểm đều l&agrave; gia c&ocirc;ng qua c&aacute;c loại m&agrave;n thầu b&aacute;nh bao, m&ugrave;a đ&ocirc;ng thả l&acirc;u, liền c&oacute;ng đến d&aacute;t băng cứng rắn.<br />\r\n<br />\r\nĐến m&ugrave;a h&egrave;, sẽ dễ d&agrave;ng biến chất.<br />\r\n<br />\r\nM&agrave; lại, b&ecirc;n ngo&agrave;i đưa v&agrave;o l&atilde;nh cung đồ ăn, căn bản kh&ocirc;ng thể n&agrave;o l&agrave;m được một ng&agrave;y đưa tới, &iacute;t th&igrave; ba ng&agrave;y đưa một lần, nhiều th&igrave; muốn năm ng&agrave;y mới c&oacute; thể đưa một lần.<br />\r\n<br />\r\nKh&ocirc;ng thể kh&ocirc;ng n&oacute;i, l&atilde;o th&aacute;i gi&aacute;m có th&ecirc;̉ nhịn đến như thế c&aacute;i ni&ecirc;n kỷ, quả thực l&agrave; kỳ t&iacute;ch a.<br />\r\n<br />\r\nDiệp Th&agrave;nh t&igrave;m được một c&acirc;y tiểu đao, kh&ocirc;ng biết thả bao l&acirc;u.<br />\r\n<br />\r\nNh&igrave;n thấy ph&iacute;a tr&ecirc;n vết rỉ, Diệp Th&agrave;nh vẫn l&agrave; từ bỏ.<br />\r\n<br />\r\nC&aacute;i n&agrave;y đều rỉ s&eacute;t, nếu như cắt vỡ ng&oacute;n tay, nhiễm l&ecirc;n uốn v&aacute;n l&agrave;m sao b&acirc;y giờ?<br />\r\n<br />\r\nCuối c&ugrave;ng, Diệp Th&agrave;nh t&igrave;m được một c&acirc;y t&uacute; hoa ch&acirc;m.<br />\r\n<br />\r\nB&igrave;nh thường quần &aacute;o hỏng, liền d&ugrave;ng c&aacute;i n&agrave;y t&uacute; hoa ch&acirc;m may may v&aacute; v&aacute;.<br />\r\n<br />\r\nY phục tr&ecirc;n người hắn, đều l&agrave; miếng v&aacute;.<br />\r\n<br />\r\nĐo&aacute;n chừng tiền th&acirc;n mặc v&agrave;o hơn mấy chục năm.<br />\r\n<br />\r\nDiệp Th&agrave;nh h&iacute;t s&acirc;u một hơi, d&ugrave;ng t&uacute; hoa ch&acirc;m đ&acirc;m r&aacute;ch ng&oacute;n tay.<br />\r\n<br />\r\nT&ecirc;!<br />\r\n<br />\r\nDiệp Th&agrave;nh nhịn kh&ocirc;ng được h&iacute;t v&agrave;o một hơi, vẫn l&agrave; rất đau nhức.<br />\r\n<br />\r\nTiếp lấy hắn gạt ra một giọt m&aacute;u tươi, rơi v&agrave;o ảm đạm mệnh đan b&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; bất kỳ c&aacute;i g&igrave; phản ứng.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng được sao?&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh tự lẩm bẩm.<br />\r\n<br />\r\nNhư thế xem ra, hắn chỉ c&oacute; thể nuốt.<br />\r\n<br />\r\nNhưng hắn vẫn c&oacute; ch&uacute;t do dự.<br />\r\n<br />\r\nD&ugrave; sao hắn l&agrave; c&aacute;i rất tiếc mệnh người.<br />\r\n<br />\r\nHiện tại c&oacute; quạ đen c&aacute;i n&agrave;y phụ linh kim thủ chỉ, liền đại biểu cho hắn ở c&aacute;i thế giới n&agrave;y sống s&oacute;t, đồng thời sống tốt tỉ lệ liền gia tăng thật lớn.<br />\r\n<br />\r\n&quot;Nếu kh&ocirc;ng chờ một ch&uacute;t , chờ tiểu Hắc cho ta từ dị giới mang về những vật kh&aacute;c?&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh thầm nghĩ trong l&ograve;ng.<br />\r\n<br />\r\nCoi như hắn đứng dậy, bỗng nhi&ecirc;n đầu v&aacute;ng mắt hoa, tay kh&ocirc;ng c&oacute; đỡ lấy, một c&aacute;i đứng kh&ocirc;ng vững, v&acirc;̣y mà t&eacute; lăn tr&ecirc;n đất.<br />\r\n<br />\r\nC&aacute;i n&agrave;y một n&eacute;m, hắn cảm gi&aacute;c nửa c&aacute;i mạng cũng bị mất.<br />\r\n<br />\r\nBởi v&igrave; hắn cảm gi&aacute;c m&igrave;nh c&oacute; chút kh&ocirc;ng động được.<br />\r\n<br />\r\n&quot;Xong đời, ta, ta sẽ kh&ocirc;ng cứ như vậy chết mất a?&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh nội t&acirc;m hoảng đ&ecirc;́n một b&uacute;t.<br />\r\n<br />\r\nĐừng n&oacute;i như thế c&aacute;i &aacute;c liệt thế giới, liền xem như kiếp trước, một c&aacute;i mẹ go&aacute; con c&ocirc;i l&atilde;o nh&acirc;n, một th&acirc;n một m&igrave;nh sinh hoạt, cũng có khả năng bởi v&igrave; t&eacute; một c&aacute;i, cứ như vậy tươi sống chết đ&oacute;i.<br />\r\n<br />\r\nHiện tại loại t&igrave;nh huống n&agrave;y, hắn một c&aacute;i l&atilde;nh cung l&atilde;o th&aacute;i gi&aacute;m, căn bản kh&ocirc;ng c&oacute; khả năng c&oacute; người tới cứu hắn.<br />\r\n<br />\r\nTuy n&oacute;i l&atilde;nh cung b&ecirc;n trong c&ograve;n c&oacute; hai người, một c&aacute;i l&agrave; tr&ecirc;n cơ bản đi&ecirc;n mất phế phi, một c&aacute;i mới năm tuổi tiểu ho&agrave;ng tử, căn bản cứu kh&ocirc;ng được hắn.<br />\r\n<br />\r\n&quot;Chờ một ch&uacute;t, c&ograve;n c&oacute; cơ hội, ta chỉ cần phục dụng vi&ecirc;n kia mệnh đan, c&ograve;n c&oacute; cơ hội.&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh nh&igrave;n về ph&iacute;a để l&ecirc;n b&agrave;n mệnh đan.<br />\r\n<br />\r\nNhưng hắn căn bản dậy kh&ocirc;ng nổi th&acirc;n.<br />\r\n<br />\r\nChớ n&oacute;i chi l&agrave; đi lấy mệnh đan.<br />\r\n<br />\r\n&quot;Tiểu Hắc, đem mệnh đan cho ta.&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh suy yếu đ&ecirc;́n h&ocirc;.<br />\r\n<br />\r\nRất nhanh, quạ đen bay tới, vội v&agrave;ng đem tr&ecirc;n b&agrave;n mệnh đan đi&ecirc;u, đặt ở Diệp Th&agrave;nh trong l&ograve;ng b&agrave;n tay.<br />\r\n<br />\r\nDiệp Th&agrave;nh nh&igrave;n thấy b&agrave;n tay t&acirc;m mệnh đan, thở ph&agrave;o nhẹ nh&otilde;m, c&ograve;n tốt c&oacute; tiểu Hắc tồn tại, bằng kh&ocirc;ng, hắn liền thật chỉ c&oacute; thể sống sống chờ chết.<br />\r\n<br />\r\nHắn cảm gi&aacute;c được th&acirc;n thể c&agrave;ng ph&aacute;t ra kh&oacute; chịu, r&aacute;ng chống đỡ lấy một hơi, đem trong tay mệnh đan để v&agrave;o trong miệng.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, hắn kh&ocirc;ng lo được nuốt mệnh đan sẽ sinh ra hậu quả g&igrave;.<br />\r\n<br />\r\nKh&ocirc;ng nuốt, trăm phần trăm sẽ như vậy bị tươi sống chết đ&oacute;i.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><a href=\"https://c.lazada.vn/t/c.06cO5B?url=https%3A%2F%2Fpages.lazada.vn%2Fwow%2Fgcp%2Froute%2Flazada%2Fvn%2Fupr_1000345_lazada%2Fchannel%2Fvn%2Fupr-router%2Fvn%3Fhybrid%3D1%26data_prefetch%3Dtrue%26prefetch_replace%3D1%26at_iframe%3D1%26wh_pid%3D%2Flazada%2Fmegascenario%2Fvn%2Fsale-gap-boi-5-5-2022%2Fngay-doi-sale-boi\" target=\"_blank\"><img src=\"https://static.cdnno.com/storage/lazada/D5_AFF_MAIN_743x400.jpg\" /></a></p>\r\n\r\n<p><br />\r\nNuốt, c&oacute; tỉ lệ sống s&oacute;t.<br />\r\n<br />\r\nDiệp Th&agrave;nh biết m&igrave;nh l&agrave;m như thế n&agrave;o lựa chọn.<br />\r\n<br />\r\nTheo mệnh đan cửa v&agrave;o, cũng kh&ocirc;ng như trong tưởng tượng lối v&agrave;o tức h&oacute;a, ngược lại dọc theo yết hầu tuột xuống, tiến v&agrave;o trong dạ d&agrave;y, ngẫu nhi&ecirc;n một cỗ cuồn cuộn nhiệt lưu đột nhi&ecirc;n bộc ph&aacute;t ra.<br />\r\n<br />\r\nChỉ l&agrave; trong nh&aacute;y mắt, Diệp Th&agrave;nh liền ngất đi.<br />\r\n<br />\r\nKh&ocirc;ng biết đi qua bao l&acirc;u, Diệp Th&agrave;nh ung dung đ&ecirc;́n vừa tỉnh lại.<br />\r\n<br />\r\nHắn vẫn như cũ nằm tr&ecirc;n mặt đất.<br />\r\n<br />\r\nNhưng hắn rất nhanh liền ph&aacute;t hiện th&acirc;n thể trở n&ecirc;n kh&ocirc;ng đồng dạng.<br />\r\n<br />\r\nNguy&ecirc;n bản suy yếu phảng phất tại giờ khắc n&agrave;y ho&agrave;n to&agrave;n biến mất kh&ocirc;ng thấy.<br />\r\n<br />\r\nThay v&agrave;o đ&oacute; l&agrave; một loại kh&oacute; m&agrave; h&igrave;nh dung lực lượng, sung doanh th&acirc;n thể.<br />\r\n<br />\r\nHắn vội v&agrave;ng đứng l&ecirc;n.<br />\r\n<br />\r\nNguy&ecirc;n bản c&ograve;ng xuống lưng c&ograve;ng kh&ocirc;ng thấy.<br />\r\n<br />\r\nNgược lại đứng được rất kiệt xuất nh&ocirc;̉ thẳng tắp.<br />\r\n<br />\r\nHắn giơ tay l&ecirc;n, đặt ở trước mặt cẩn thận đầu mối.<br />\r\n<br />\r\nMặc d&ugrave; vẫn như cũ l&agrave;n da tang thương, nhưng kh&ocirc;ng c&oacute; trước đ&oacute; loại kia gầy trơ cả xương.<br />\r\n<br />\r\nC&aacute;c loại, hiện tại đ&atilde; sớm trời tối, ta sao c&oacute; thể thấy rất r&otilde; r&agrave;ng?<br />\r\n<br />\r\nDiệp Th&agrave;nh trong l&ograve;ng hơi động.<br />\r\n<br />\r\nTrong ph&ograve;ng một mảnh đen kịt, đ&atilde; sớm trời tối.<br />\r\n<br />\r\nL&uacute;c trước hắn kh&ocirc;ng biết h&ocirc;n m&ecirc; bao l&acirc;u.<br />\r\n<br />\r\nNhưng hắn phảng phất c&oacute; nh&igrave;n ban đ&ecirc;m mắt, tất cả mọi thứ đều nh&igrave;n thấy r&otilde; r&agrave;ng, liền c&ugrave;ng ban ng&agrave;y.<br />\r\n<br />\r\n&quot;Chẳng lẽ đ&acirc;y ch&iacute;nh l&agrave; ta phục dụng mệnh đan sau kết quả?&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh tự lẩm bẩm.<br />\r\n<br />\r\nM&agrave; lại, hắn cảm nhận được phần bụng vị tr&iacute;, ẩn chứa một cỗ kh&oacute; n&oacute;i l&ecirc;n lời lực lượng, phảng phất tại lưu chuyển, đang phun ra nuốt v&agrave;o, đang nổi l&ecirc;n b&ecirc;n trong.<br />\r\n<br />\r\n&quot;Ta giống như nhiều một ch&uacute;t k&yacute; ức tin tức?&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh nhắm mắt lại, cẩn thận xem x&eacute;t th&ecirc;m ra những k&yacute; ức kia.<br />\r\n<br />\r\nV&ocirc; c&ugrave;ng r&otilde; r&agrave;ng.<br />\r\n<br />\r\n. . .</p>', '2022-05-08 02:56:37', 0, 1),
(337, 88, 'Đáng Thương Tiểu Hoàng Tử, điên Phế Quý Phi', '3', '<p>Hồi l&acirc;u sau, Diệp Th&agrave;nh một lần nữa mở mắt.<br />\r\n<br />\r\nGiờ khắc n&agrave;y, hắn kh&ocirc;ng c&ograve;n c&oacute; bất luận c&aacute;i g&igrave; nghi hoặc.<br />\r\n<br />\r\nBởi v&igrave; hắn biết m&igrave;nh thật dung hợp mệnh đan chi lực.<br />\r\n<br />\r\nHắn đ&atilde; kh&ocirc;ng phải l&agrave; trước đ&oacute; c&aacute;i kia gần đất xa trời l&atilde;o th&aacute;i gi&aacute;m.<br />\r\n<br />\r\nHắn đạt được một c&aacute;i v&otilde; đạo cao thủ một th&acirc;n tu vi.<br />\r\n<br />\r\nKh&ocirc;ng chỉ như vậy, ngay cả c&aacute;i n&agrave;y v&otilde; đạo cao thủ v&otilde; đạo cảm ngộ, tinh thần cảnh giới, c&ocirc;ng ph&aacute;p b&iacute; thuật chờ cũng c&ugrave;ng nhau kế thừa.<br />\r\n<br />\r\nĐương nhi&ecirc;n, hắn chỉ l&agrave; kế thừa, muốn triệt để nắm giữ, dung hợp, ti&ecirc;u h&oacute;a, h&oacute;a th&agrave;nh thuộc về m&igrave;nh lực lượng, đo&aacute;n chừng c&ograve;n muốn một đoạn thời gian rất d&agrave;i mới được.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng biết thế giới n&agrave;y v&otilde; đạo hệ thống, c&ugrave;ng thế giới kia c&oacute; kh&aacute;c biệt kh&ocirc;ng?&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh trong l&ograve;ng thầm nghĩ.<br />\r\n<br />\r\nHắn nuốt mệnh đan về sau, thu được mệnh đan b&ecirc;n trong một phần nhỏ lực lượng, nhưng c&agrave;ng nhiều c&ograve;n chứa đựng tại thể nội , chờ đợi hắn triệt để dung hợp hấp thu.<br />\r\n<br />\r\nTại hắn đạt được mệnh đan trong tin tức, l&agrave; c&oacute; li&ecirc;n quan tại v&otilde; đạo thể hệ ph&acirc;n chia.<br />\r\n<br />\r\nChẳng qua l&agrave; dị giới, m&agrave; kh&ocirc;ng phải thế giới n&agrave;y.<br />\r\n<br />\r\nThế giới kh&aacute;c nhau, hẳn l&agrave; sẽ tồn tại kh&aacute;c biệt.<br />\r\n<br />\r\nLuyện Thể, Hậu Thi&ecirc;n, Ti&ecirc;n Thi&ecirc;n, T&ocirc;ng Sư, Đại T&ocirc;ng Sư, V&otilde; Th&aacute;nh, Thi&ecirc;n Nh&acirc;n. . . Đ&acirc;y ch&iacute;nh l&agrave; dị giới v&otilde; đạo đẳng cấp.<br />\r\n<br />\r\nM&agrave; Diệp Th&agrave;nh hấp thu mệnh đan ngưng tụ người, ch&iacute;nh l&agrave; một t&ocirc;n đến gần v&ocirc; hạn Thi&ecirc;n Nh&acirc;n V&otilde; Th&aacute;nh.<br />\r\n<br />\r\nV&otilde; Th&aacute;nh thọ nguy&ecirc;n c&oacute; hạn, sống tối đa ba trăm năm.<br />\r\n<br />\r\nNhưng đến Thi&ecirc;n Nh&acirc;n, liền c&oacute; thể sống ng&agrave;n năm trở l&ecirc;n.<br />\r\n<br />\r\nT&ocirc;n n&agrave;y V&otilde; Th&aacute;nh vốn l&agrave; c&oacute; tiềm lực đột ph&aacute; đến Thi&ecirc;n Nh&acirc;n, lại gặp bị thương nặng, cũng kh&ocirc;ng c&oacute; cơ hội nữa đột ph&aacute; Thi&ecirc;n Nh&acirc;n chi cảnh, cho n&ecirc;n lựa chọn đem một th&acirc;n tu vi cảnh giới ngưng tụ mệnh lệnh đ&atilde; ban ra đan, truyền thừa cho hậu duệ.<br />\r\n<br />\r\nĐ&aacute;ng tiếc bị xuy&ecirc;n c&agrave;ng dị giới quạ đen đoạt đi.<br />\r\n<br />\r\nCũng liền tiện nghi Diệp Th&agrave;nh.<br />\r\n<br />\r\nDiệp Th&agrave;nh ngồi xếp bằng xuống, chuẩn bị tu luyện mệnh đan b&ecirc;n trong mang theo v&otilde; đạo c&ocirc;ng ph&aacute;p, chậm r&atilde;i hấp thu thể nội ẩn n&uacute;p mệnh đan chi lực.<br />\r\n<br />\r\nHắn tu vi hiện tại. . . Hẳn l&agrave; ở v&agrave;o hậu thi&ecirc;n cực hạn.<br />\r\n<br />\r\nN&oacute;i c&aacute;ch kh&aacute;c, hắn chỉ l&agrave; hấp thu nắm trong tay một phần rất nhỏ mệnh đan chi lực.<br />\r\n<br />\r\nC&aacute;i kia dị giới v&otilde; đạo cường giả l&agrave; đỉnh phong V&otilde; Th&aacute;nh.<br />\r\n<br />\r\nLiền xem như thụ trọng thương, xa xa kh&ocirc;ng c&oacute; đỉnh phong thời kỳ lực lượng, có th&ecirc;̉ ngưng tụ ra mệnh đan, tối thiểu cũng l&agrave; một c&aacute;i b&igrave;nh thường V&otilde; Th&aacute;nh cường giả đi.<br />\r\n<br />\r\nN&oacute;i c&aacute;ch kh&aacute;c, chỉ cần Diệp Th&agrave;nh c&oacute; thể triệt để hấp thu mệnh đan chi lực, trở th&agrave;nh V&otilde; Th&aacute;nh hẳn l&agrave; kh&ocirc;ng c&oacute; vấn đề g&igrave; a?<br />\r\n<br />\r\nNghĩ đến tu vi như vậy cấp độ, ở c&aacute;i thế giới n&agrave;y hẳn l&agrave; cũng sẽ kh&ocirc;ng qu&aacute; k&eacute;m.<br />\r\n<br />\r\nKh&ocirc;ng biết đi qua bao l&acirc;u.<br />\r\n<br />\r\nBỗng nhi&ecirc;n, Diệp Th&agrave;nh to&agrave;n th&acirc;n chấn động, n&atilde;o hải oanh minh, theo s&aacute;t lấy th&acirc;n thể sinh ra biến h&oacute;a kỳ dị, phảng phất phảng phất to&agrave;n th&acirc;n tất cả lỗ ch&acirc;n l&ocirc;ng tất cả đều mở ra, c&ugrave;ng ngoại giới thi&ecirc;n địa sinh ra một loại kỳ dị li&ecirc;n hệ.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><a href=\"https://c.lazada.vn/t/c.06cO5B?url=https%3A%2F%2Fpages.lazada.vn%2Fwow%2Fgcp%2Froute%2Flazada%2Fvn%2Fupr_1000345_lazada%2Fchannel%2Fvn%2Fupr-router%2Fvn%3Fhybrid%3D1%26data_prefetch%3Dtrue%26prefetch_replace%3D1%26at_iframe%3D1%26wh_pid%3D%2Flazada%2Fmegascenario%2Fvn%2Fsale-gap-boi-5-5-2022%2Fngay-doi-sale-boi\" target=\"_blank\"><img src=\"https://static.cdnno.com/storage/lazada/D5_AFF_MAIN_743x400.jpg\" /></a></p>\r\n\r\n<p><br />\r\n&quot;Đ&acirc;y ch&iacute;nh l&agrave; Ti&ecirc;n Thi&ecirc;n?&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh tự lẩm bẩm.<br />\r\n<br />\r\nHậu Thi&ecirc;n cảnh tu luyện, chỉ c&oacute; thể th&ocirc;ng qua đồ ăn, đan dược đến hấp thu năng lượng.<br />\r\n<br />\r\nNhưng đến Ti&ecirc;n Thi&ecirc;n cảnh, liền c&oacute; thể hấp thu giữa thi&ecirc;n địa rời rạc nguy&ecirc;n kh&iacute; tu luyện.<br />\r\n<br />\r\nDạng n&agrave;y mang tới ch&ecirc;nh lệch l&agrave; phi thường lớn.<br />\r\n<br />\r\nBởi v&igrave; đồ ăn đan dược năng lượng c&oacute; hạn.<br />\r\n<br />\r\nNhưng năng lượng thi&ecirc;n địa l&agrave; v&ocirc; c&ugrave;ng v&ocirc; tận.<br />\r\n<br />\r\nDuy nhất hạn chế Ti&ecirc;n Thi&ecirc;n v&otilde; giả tu luyện ch&iacute;nh l&agrave; hấp thu năng lượng thi&ecirc;n địa tốc độ c&ugrave;ng hiệu suất.<br />\r\n<br />\r\nC&aacute;i n&agrave;y d&iacute;nh đến tự th&acirc;n tư chất c&ugrave;ng b&iacute; tịch v&otilde; c&ocirc;ng.<br />\r\n<br />\r\nDiệp Th&agrave;nh tu luyện b&iacute; tịch v&otilde; c&ocirc;ng ch&iacute;nh l&agrave; truyền thừa từ dị giới V&otilde; Th&aacute;nh, t&ecirc;n l&agrave; Tịch T&agrave; Kiếm Điển.<br />\r\n<br />\r\nM&ocirc;n v&otilde; c&ocirc;ng n&agrave;y đặc điểm ch&iacute;nh l&agrave; truy cầu một c&aacute;i nhanh.<br />\r\n<br />\r\nCực hạn nhanh.<br />\r\n<br />\r\nTh&acirc;n ph&aacute;p nhanh, kiếm ph&aacute;p nhanh, c&ocirc;ng k&iacute;ch nhanh.<br />\r\n<br />\r\nCho n&ecirc;n, c&aacute;i kia dị giới V&otilde; Th&aacute;nh, được xưng l&agrave; T&agrave; Ảnh Kiếm Th&aacute;nh.<br />\r\n<br />\r\nChưa từng c&oacute; người n&agrave;o nh&igrave;n qua hắn ch&acirc;n th&acirc;n, nh&igrave;n thấy chỉ l&agrave; một đạo từ v&ocirc; số kiếm kh&iacute; tạo th&agrave;nh đ&aacute;ng sợ T&agrave; Ảnh.<br />\r\n<br />\r\nDiệp Th&agrave;nh vận chuyển Tịch T&agrave; Kiếm Điển Ti&ecirc;n Thi&ecirc;n chi ph&aacute;p, hấp thu năng lượng thi&ecirc;n địa.<br />\r\n<br />\r\nĐ&aacute;ng tiếc, loại n&agrave;y hấp thu tốc độ, so ra m&agrave; n&oacute;i qu&aacute; chậm.<br />\r\n<br />\r\nCho n&ecirc;n, Diệp Th&agrave;nh ngược lại tiếp tục hấp thu thể nội t&iacute;ch chứa mệnh đan chi lực.<br />\r\n<br />\r\nĐ&acirc;y mới l&agrave; hắn lớn nhất bảo khố.<br />\r\n<br />\r\nKh&ocirc;ng n&oacute;i th&agrave;nh tựu V&otilde; Th&aacute;nh, ch&iacute; &iacute;t đến Đại T&ocirc;ng Sư l&agrave; kh&ocirc;ng c&oacute; bất cứ vấn đề g&igrave;.<br />\r\n<br />\r\n&quot;L&atilde;o c&ocirc;ng c&ocirc;ng. . .&quot;<br />\r\n<br />\r\nBỗng nhi&ecirc;n, Diệp Th&agrave;nh nghe được một c&aacute;i non nớt thanh &acirc;m.<br />\r\n<br />\r\nHắn đột nhi&ecirc;n mở mắt ra, ngo&agrave;i cửa sổ sớm đ&atilde; l&agrave; lớn trời đ&atilde; s&aacute;ng.<br />\r\n<br />\r\nTinh thần qu&eacute;t qua, đứng ở ph&iacute;a ngo&agrave;i một đứa b&eacute;.<br />\r\n<br />\r\nQuần &aacute;o r&aacute;ch mướp, ph&iacute;a tr&ecirc;n c&oacute; rất nhiều miếng v&aacute;.<br />\r\n<br />\r\nNhững n&agrave;y miếng v&aacute;, vẫn l&agrave; cỗ th&acirc;n thể n&agrave;y cho bổ.<br />\r\n<br />\r\nKh&ocirc;ng cần phải n&oacute;i, tiểu h&agrave;i tử n&agrave;y ch&iacute;nh l&agrave; c&aacute;i kia tiểu ho&agrave;ng tử L&yacute; Cảnh.<br />\r\n<br />\r\nC&aacute;i n&agrave;y tiểu ho&agrave;ng tử xuất th&acirc;n Ho&agrave;ng tộc, vốn n&ecirc;n l&agrave; th&acirc;n phận t&ocirc;n qu&yacute;.<br />\r\n<br />\r\nĐ&aacute;ng tiếc, lại vận mệnh long đong, vừa ra đời về sau, mẫu th&acirc;n liền bị đ&aacute;nh nhập l&atilde;nh cung, hắn cũng c&ugrave;ng theo v&agrave;o.<br />\r\n<br />\r\nTừ l&agrave; trong t&atilde; l&oacute;t h&agrave;i nhi biến th&agrave;nh hiện tại năm tuổi h&agrave;i đồng.<br />\r\n<br />\r\nC&oacute; thể sống s&oacute;t, phải n&oacute;i đ&atilde; trong bất hạnh vạn hạnh.<br />\r\n<br />\r\nL&atilde;nh cung loại địa phương n&agrave;y, sinh tồn ho&agrave;n cảnh v&ocirc; c&ugrave;ng &aacute;c liệt.<br />\r\n<br />\r\nBệnh, cơ hồ kh&ocirc;ng c&oacute; cơ hội đạt được chữa trị.<br />\r\n<br />\r\nDiệp Th&agrave;nh đứng l&ecirc;n, sau đ&oacute; đi ra ngo&agrave;i.<br />\r\n<br />\r\nVừa đi đến cửa miệng thời điểm, hắn &yacute; thức được tr&ecirc;n người m&igrave;nh biến h&oacute;a, kh&ocirc;ng giống như l&agrave; một c&aacute;i gi&agrave; bảy t&aacute;m mươi tuổi l&atilde;o th&aacute;i gi&aacute;m.<br />\r\n<br />\r\nThế l&agrave; th&acirc;n thể của hắn c&oacute; ch&uacute;t uốn lượn, giả bộ như th&agrave;nh b&igrave;nh thường c&ograve;ng xuống d&aacute;ng vẻ, đi lại tập tễnh đ&ecirc;́n chậm r&atilde;i đi ra ngo&agrave;i.<br />\r\n<br />\r\nTuy n&oacute;i c&aacute;i n&agrave;y trong l&atilde;nh cung kh&ocirc;ng c&oacute; những người kh&aacute;c.<br />\r\n<br />\r\nHắn vẫn l&agrave; kh&ocirc;ng muốn để cho người kh&aacute;c nh&igrave;n thấy tr&ecirc;n người m&igrave;nh biến h&oacute;a.<br />\r\n<br />\r\nHắn đi tới cửa, th&ocirc;ng qua trong chum nước nước, nh&igrave;n một ch&uacute;t bộ d&aacute;ng của m&igrave;nh.<br />\r\n<br />\r\nNgoại trừ &aacute;nh mắt trở n&ecirc;n sắc b&eacute;n lại, tướng mạo cũng kh&ocirc;ng c&oacute; g&igrave; thay đổi, t&oacute;c hoa r&acirc;m, tr&ecirc;n da tr&agrave;n đầy da đốm mồi, nh&igrave;n qua dần dần gi&agrave; đi.<br />\r\n<br />\r\nDiệp Th&agrave;nh thu liễm trong mắt thần quang, sau đ&oacute; mới đi ra khỏi ph&ograve;ng.<br />\r\n<br />\r\n&quot;L&atilde;o c&ocirc;ng c&ocirc;ng, mẹ ta lại kh&ocirc;ng n&oacute;i, ta thật l&agrave; sợ. . .&quot;<br />\r\n<br />\r\nTiểu ho&agrave;ng tử L&yacute; Cảnh nh&igrave;n thấy Diệp Th&agrave;nh ra, phảng phất thấy được th&acirc;n nh&acirc;n, vội v&agrave;ng chạy tới, c&ograve;n rất hiểu chuyện đ&ecirc;́n vịn Diệp Th&agrave;nh.<br />\r\n<br />\r\nTại c&aacute;i n&agrave;y trong l&atilde;nh cung, chỉ c&oacute; hai người đối tốt với hắn, đ&oacute; ch&iacute;nh l&agrave; mẫu th&acirc;n c&ugrave;ng l&atilde;o c&ocirc;ng c&ocirc;ng.<br />\r\n<br />\r\nVề phần ngo&agrave;i l&atilde;nh cung người, đều phi thường hung.<br />\r\n<br />\r\nNh&igrave;n thấy tiểu gia hỏa kia đơn thuần &aacute;nh mắt, l&agrave;m cho người thương tiếc, Diệp Th&agrave;nh trong l&ograve;ng thở d&agrave;i.<br />\r\n<br />\r\nPh&iacute;a trước th&acirc;n trong tr&iacute; nhớ, tiểu gia hỏa n&agrave;y kh&ocirc;ng thể nghi ngờ l&agrave; chiếm cứ rất trọng yếu vị tr&iacute;.<br />\r\n<br />\r\nB&igrave;nh thường c&oacute; c&aacute;i g&igrave; tốt ăn, đều sẽ cho vị n&agrave;y tiểu ho&agrave;ng tử giữ lại.<br />\r\n<br />\r\nM&igrave;nh ăn kh&ocirc;ng đủ no, cũng muốn tiết kiệm tới.<br />\r\n<br />\r\nHoặc l&agrave; n&oacute;i, c&aacute;i n&agrave;y tiểu ho&agrave;ng tử c&oacute; thể tại trong l&atilde;nh cung chịu đựng đến, sống đến b&acirc;y giờ năm tuổi, vị n&agrave;y l&atilde;nh cung l&atilde;o th&aacute;i gi&aacute;m l&agrave; kh&ocirc;ng thể bỏ qua c&ocirc;ng lao.<br />\r\n<br />\r\nKh&ocirc;ng phải dựa v&agrave;o c&aacute;i kia khi th&igrave; b&igrave;nh thường khi th&igrave; đi&ecirc;n phế phi, tiểu gia hỏa sợ l&agrave; đ&atilde; sớm chết.<br />\r\n<br />\r\nQuan hệ của hai người thật c&ugrave;ng ph&iacute;a ngo&agrave;i hai &ocirc;ng ch&aacute;u kh&ocirc;ng c&oacute; kh&aacute;c nhau.<br />\r\n<br />\r\n&quot;Tiểu điện hạ, ch&uacute;ng ta l&agrave; xem một ch&uacute;t đi.&quot;<br />\r\n<br />\r\nDiệp Th&agrave;nh nắm tiểu ho&agrave;ng tử tay nhỏ, chậm r&atilde;i đ&ecirc;́n hướng ph&iacute;a phế phi chỗ ở vị tr&iacute; đi đến.<br />\r\n<br />\r\nL&atilde;nh cung rất lớn.<br />\r\n<br />\r\nBất qu&aacute;, l&atilde;o th&aacute;i gi&aacute;m c&ugrave;ng phế phi chỗ ở địa phương, kh&ocirc;ng phải đặc biệt xa, phải n&oacute;i l&agrave; liền nhau.<br />\r\n<br />\r\nTo&agrave;n bộ l&atilde;nh cung kiến tr&uacute;c, l&acirc;u năm thiếu tu sửa, r&acirc;́t nhi&ecirc;̀u nơi đều r&aacute;ch tung to&eacute;, vừa đến m&ugrave;a mưa khả năng liền đến chỗ rỉ nước, cỏ dại rậm rạp.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><a href=\"https://c.lazada.vn/t/c.06cO5B?url=https%3A%2F%2Fpages.lazada.vn%2Fwow%2Fgcp%2Froute%2Flazada%2Fvn%2Fupr_1000345_lazada%2Fchannel%2Fvn%2Fupr-router%2Fvn%3Fhybrid%3D1%26data_prefetch%3Dtrue%26prefetch_replace%3D1%26at_iframe%3D1%26wh_pid%3D%2Flazada%2Fmegascenario%2Fvn%2Fsale-gap-boi-5-5-2022%2Fngay-doi-sale-boi\" target=\"_blank\"><img src=\"https://static.cdnno.com/storage/lazada/D5_AFF_MAIN_743x400.jpg\" /></a></p>\r\n\r\n<p><br />\r\n<br />\r\nL&atilde;o th&aacute;i gi&aacute;m tại th&acirc;n thể tốt thời điểm, c&ograve;n c&oacute; thể sẽ nhổ cỏ, tu bổ tu bổ lậu nước địa phương.<br />\r\n<br />\r\nNhưng lớn tuổi, th&acirc;n thể c&agrave;ng ph&aacute;t ra k&eacute;m, những chuyện n&agrave;y kh&ocirc;ng l&agrave;m được, cũng chỉ c&oacute; thể qu&eacute;t qu&eacute;t r&aacute;c loại h&igrave;nh.<br />\r\n<br />\r\nĐến phế phi chỗ ở.<br />\r\n<br />\r\nTrống rỗng.<br />\r\n<br />\r\nKhắp nơi hở.<br />\r\n<br />\r\nL&atilde;o th&aacute;i gi&aacute;m trước kia tu bổ qua, nhưng kh&ocirc;ng c&oacute; t&agrave;i liệu g&igrave;, cũng liền vu sự v&ocirc; bổ.<br />\r\n<br />\r\nTại cửa đ&igrave;nh viện, đứng đấy một nữ tử.<br />\r\n<br />\r\nNh&igrave;n qua c&aacute;ch đ&oacute; kh&ocirc;ng xa một c&aacute;i c&acirc;y.<br />\r\n<br />\r\nCũng kh&ocirc;ng nh&uacute;c nh&iacute;ch.<br />\r\n<br />\r\nGiống như tượng b&ugrave;n.<br />\r\n<br />\r\nNữ tử n&agrave;y một th&acirc;n cung đ&igrave;nh trang phục, chỉ bất qu&aacute; thường xuy&ecirc;n thay giặt, cho n&ecirc;n nh&igrave;n hơi trắng bệch.<br />\r\n<br />\r\nNhưng kh&ocirc;ng c&oacute; tổn hại t&igrave;nh huống.<br />\r\n<br />\r\nC&oacute; thể thấy được y phục n&agrave;y chủ nh&acirc;n, b&igrave;nh thường vẫn l&agrave; rất y&ecirc;u qu&yacute;.<br />\r\n<br />\r\nDiệp Th&agrave;nh nắm tiểu ho&agrave;ng tử, chậm r&atilde;i đi tới nữ tử trước người.<br />\r\n<br />\r\nMặc d&ugrave; quần &aacute;o cổ xưa, nhưng như cũ kh&oacute; m&agrave; che lấp nữ tử n&agrave;y nổi bật d&aacute;ng người.<br />\r\n<br />\r\nTướng mạo th&igrave; c&agrave;ng l&agrave; ngũ quan tinh xảo, kh&iacute; chất b&ecirc;n trong mang theo một loại vũ mị hương vị.<br />\r\n<br />\r\nTr&aacute;ch kh&ocirc;ng được năm đ&oacute; l&agrave; đủ ho&agrave;ng sủng &aacute;i nhất phi tử, sắc phong l&agrave;m qu&yacute; phi, bao tr&ugrave;m chư phi ph&iacute;a tr&ecirc;n, chỉ ở ho&agrave;ng hậu ph&iacute;a dưới.<br />\r\n<br />\r\nChỉ tiếc, đắc tội ho&agrave;ng hậu, cuối c&ugrave;ng bị đ&aacute;nh nhập l&atilde;nh cung, ngay cả vừa mới ra đời tiểu ho&agrave;ng tử đều bị d&iacute;nh l&iacute;u.<br />\r\n<br />\r\nThời khắc n&agrave;y qu&yacute; phi, &aacute;nh mắt mộc ngốc, trống rỗng, v&ocirc; thần.<br />\r\n<br />\r\nBờ m&ocirc;i c&oacute; ch&uacute;t rung động, r&otilde; r&agrave;ng đang lầm bầm lầu bầu.<br />\r\n<br />\r\nThường nh&acirc;n khẳng định l&agrave; nghe kh&ocirc;ng được n&agrave;ng đang n&oacute;i c&aacute;i g&igrave;.<br />\r\n<br />\r\nNhưng Diệp Th&agrave;nh đ&atilde; l&agrave; Ti&ecirc;n Thi&ecirc;n v&otilde; giả, nhĩ lực kinh người, cho n&ecirc;n c&oacute; thể đủ nhạy cảm đ&ecirc;́n nghe được qu&yacute; phi n&oacute;i tới.<br />\r\n<br />\r\nDiệp Th&agrave;nh nhưng trong l&ograve;ng chấn động.<br />\r\n<br />\r\nC&aacute;i n&agrave;y Thục phi tự nhi&ecirc;n tự n&oacute;i nội dung phi thường loạn thất b&aacute;t tao. . . . Nhưng hắn vẫn l&agrave; ph&aacute;t hiện c&oacute; ch&uacute;t kh&ocirc;ng đ&uacute;ng.<br />\r\n<br />\r\n. . . . .</p>', '2022-05-08 02:57:01', 0, 1);
INSERT INTO `tbl_chapter` (`id`, `truyen_id`, `tenchuong`, `slug`, `noidung`, `ngaydang`, `luotdoc`, `trangthai`) VALUES
(338, 89, 'Xuyên Qua Thành Nội ứng Tân Thủ Gói Quà Lớn', '1', '<p>Đại Chu ho&agrave;ng th&agrave;nh.<br />\r\n<br />\r\nĐ&ocirc;ng Xưởng, tịnh th&acirc;n ph&ograve;ng.<br />\r\n<br />\r\nMột người mặc &aacute;o lam Phi Ưng phục tiểu th&aacute;i gi&aacute;m đứng tại trước ph&ograve;ng, lỗ mũi đối diện đỉnh điện, thần sắc ki&ecirc;u căng.<br />\r\n<br />\r\nNghe được trong ph&ograve;ng truyền đến một tiếng tiếng k&ecirc;u thảm thiết đau đớn về sau, tiểu th&aacute;i gi&aacute;m l&uacute;c n&agrave;y mới quay đầu nh&igrave;n về ph&iacute;a trước mặt mười mấy t&ecirc;n quần &aacute;o mộc mạc xếp th&agrave;nh một loạt thiếu ni&ecirc;n, giọng the th&eacute; n&oacute;i: &quot;Đến ngươi!&quot;<br />\r\n<br />\r\nXếp tại rất ph&iacute;a trước trắng n&otilde;n tuấn t&uacute; thiếu ni&ecirc;n, thần sắc c&ograve;n c&oacute; mấy phần mờ mịt.<br />\r\n<br />\r\n&quot;Ta tại sao lại ở chỗ n&agrave;y? Xuy&ecirc;n qua rồi?&quot;<br />\r\n<br />\r\nL&acirc;m Dật bừng tỉnh, d&ograve; x&eacute;t chu vi, trong l&ograve;ng tr&agrave;n đầy nghi hoặc.<br />\r\n<br />\r\nTự m&igrave;nh tr&ecirc;n một gi&acirc;y c&ograve;n tại xem, một gi&acirc;y sau mở mắt, liền đi tới nơi n&agrave;y?<br />\r\n<br />\r\nKh&ocirc;ng đợi L&acirc;m Dật tiếp tục suy nghĩ, một đoạn lớn mảnh vỡ k&iacute; ức giống như thủy triều tr&agrave;n v&agrave;o trong đầu. . .<br />\r\n<br />\r\n&quot;Từ nhỏ phụ mẫu đều mất, lưu lạc đầu đường, sau v&igrave; mạng sống, b&aacute;i nhập Ma m&ocirc;n, trở th&agrave;nh trong đ&oacute; tiểu l&acirc;u la. . .&quot;<br />\r\n<br />\r\n&quot;Đoạn trước thời gian, bị cưỡng chế an b&agrave;i đến Ho&agrave;ng cung l&agrave;m nằm v&ugrave;ng. . .&quot;<br />\r\n<br />\r\n&quot;V&agrave;o cung sau biết được muốn tịnh th&acirc;n, trực tiếp bị h&ugrave; chết? !&quot;<br />\r\n<br />\r\n&quot;Nơi n&agrave;y l&agrave;, Đ&ocirc;ng Xưởng tịnh th&acirc;n chỗ? Đ&acirc;y chẳng phải l&agrave; n&oacute;i, tiếp xuống, ta liền c&aacute;i nam nh&acirc;n cũng l&agrave;m kh&ocirc;ng được?&quot;<br />\r\n<br />\r\nTra x&eacute;t xong k&yacute; ức, L&acirc;m Dật cả người đều kh&ocirc;ng tốt.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng được, nhất định phải nghĩ biện ph&aacute;p rời đi nơi n&agrave;y!&quot;<br />\r\n<br />\r\nL&acirc;m Dật cũng kh&ocirc;ng muốn nhận mệnh.<br />\r\n<br />\r\nC&oacute; thể tự m&igrave;nh cỗ th&acirc;n thể n&agrave;y, chỉ l&agrave; một c&aacute;i tu luyện năm năm mới đi v&agrave;o Thối Thể tam trọng nhỏ lạt k&ecirc;, muốn từ bảo vệ nghi&ecirc;m ngặt v&ocirc; số cao thủ Đ&ocirc;ng Xưởng tho&aacute;t đi, đơn giản so c&ograve;n kh&oacute; hơn l&ecirc;n trời, m&agrave; bị nắm ở hạ tr&agrave;ng, căn bản kh&ocirc;ng cần nghĩ. . .<br />\r\n<br />\r\nHuống chi tự m&igrave;nh trước khi tới c&ograve;n nuốt Ma m&ocirc;n Phệ T&acirc;m đan, trong một th&aacute;ng kh&ocirc;ng c&oacute; giải dược, liền sẽ bỏ m&igrave;nh. . .<br />\r\n<br />\r\nMuốn mạng vẫn l&agrave; phải mệnh căn tử?<br />\r\n<br />\r\nKh&oacute; giải.<br />\r\n<br />\r\n&quot;Đinh?&quot;<br />\r\n<br />\r\nL&acirc;m Dật l&ograve;ng mang mong đợi đinh một tiếng.<br />\r\n<br />\r\n【 hệ thống k&iacute;ch hoạt b&ecirc;n trong. . . 】<br />\r\n<br />\r\n&quot;Ngọa t&agrave;o, quả nhi&ecirc;n c&oacute;!&quot;<br />\r\n<br />\r\nL&acirc;m Dật kinh ngạc, c&oacute; hệ thống, vậy n&oacute;i r&otilde; hết thảy cũng c&ograve;n c&oacute; chuyển cơ.<br />\r\n<br />\r\n【 thần cấp nội ứng hệ thống đ&atilde; tr&oacute;i chặt! 】<br />\r\n<br />\r\n&quot;&Aacute;ch? Thần cấp nội ứng hệ thống?&quot;<br />\r\n<br />\r\nL&acirc;m Dật sững sờ, đ&acirc;y l&agrave; c&aacute;i g&igrave; đồ chơi?</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><a href=\"https://c.lazada.vn/t/c.06cO5B?url=https%3A%2F%2Fpages.lazada.vn%2Fwow%2Fgcp%2Froute%2Flazada%2Fvn%2Fupr_1000345_lazada%2Fchannel%2Fvn%2Fupr-router%2Fvn%3Fhybrid%3D1%26data_prefetch%3Dtrue%26prefetch_replace%3D1%26at_iframe%3D1%26wh_pid%3D%2Flazada%2Fmegascenario%2Fvn%2Fsale-gap-boi-5-5-2022%2Fngay-doi-sale-boi\" target=\"_blank\"><img src=\"https://static.cdnno.com/storage/lazada/D5_AFF_MAIN_743x400.jpg\" /></a></p>\r\n\r\n<p><br />\r\n<br />\r\n【 thần cấp nội ứng hệ thống: Chỉ cần t&uacute;c chủ bảo tr&igrave; nội ứng th&acirc;n phận, lại kh&ocirc;ng bị ph&aacute;t hiện, mỗi ng&agrave;y đều c&oacute; thể thu hoạch được phong ph&uacute; ban thưởng! 】<br />\r\n<br />\r\n【 đinh, t&acirc;n thủ g&oacute;i qu&agrave; lớn đ&atilde; cấp cho, phải chăng mở ra? 】<br />\r\n<br />\r\n&quot;Mở ra.&quot;<br />\r\n<br />\r\nL&acirc;m Dật kh&ocirc;ng ch&uacute;t do dự n&oacute;i.<br />\r\n<br />\r\n【 đinh, ch&uacute;c mừng t&uacute;c chủ, thu hoạch được: Tẩy Tủy đan! 】<br />\r\n<br />\r\n【 đinh, ch&uacute;c mừng t&uacute;c chủ, thu hoạch được: Mười năm tu vi! 】<br />\r\n<br />\r\n【 đinh, ch&uacute;c mừng t&uacute;c chủ, thu hoạch được: Man thi&ecirc;n qu&aacute; hải ! 】<br />\r\n<br />\r\n【 đinh, ch&uacute;c mừng t&uacute;c chủ, thu hoạch được: Thế th&acirc;n con rối ( độ ch&acirc;n thực 100%)! 】<br />\r\n<br />\r\nL&acirc;m Dật &aacute;nh mắt đảo qua giao diện ảo, cuối c&ugrave;ng rơi v&agrave;o Thế th&acirc;n con rối ph&iacute;a tr&ecirc;n.<br />\r\n<br />\r\n【 thế th&acirc;n con rối: Triệu hồi ra một c&aacute;i độ ch&acirc;n thực trăm phần trăm thế th&acirc;n, thế th&acirc;n tử vong tức biến mất! 】<br />\r\n<br />\r\n&quot;T&ecirc;. . . C&oacute; c&aacute;i n&agrave;y đồ vật, đ&acirc;y chẳng phải l&agrave; c&oacute; thể thay x&agrave; đổi cột? Kh&ocirc;ng cần bị cắt?&quot;<br />\r\n<br />\r\nL&acirc;m Dật trong l&ograve;ng cơ hồ lập tức nghĩ đến thế th&acirc;n con rối phương ph&aacute;p sử dụng, hệ thống n&agrave;y lợi hại a!<br />\r\n<br />\r\nChỉ cần kh&ocirc;ng bị cắt, kia c&ograve;n sống c&ograve;n c&oacute; hi vọng.<br />\r\n<br />\r\nNghĩ tới đ&acirc;y, L&acirc;m Dật nguy&ecirc;n bản nỗi l&ograve;ng lo lắng cũng l&agrave; bu&ocirc;ng xuống, tỉnh t&aacute;o rất nhiều.<br />\r\n<br />\r\nCăn cứ hệ thống giới thiệu, muốn thu hoạch được c&agrave;ng nhiều ban thưởng, c&ograve;n phải c&acirc;̀n nội ứng c&aacute;i th&acirc;n phận n&agrave;y.<br />\r\n<br />\r\nHuống chi, tự m&igrave;nh nuốt v&agrave;o Phệ T&acirc;m đan, kh&ocirc;ng c&oacute; giải dược, căn bản kh&ocirc;ng c&oacute; biện ph&aacute;p ly khai. . .<br />\r\n<br />\r\nTại Ho&agrave;ng cung nội ứng, l&agrave; hắn đường ra duy nhất.<br />\r\n<br />\r\nY&ecirc;n lặng ph&aacute;t dục xuống dưới, cẩu đến v&ocirc; địch lại ra khỏi n&uacute;i, h&aacute; kh&ocirc;ng đẹp cắm?<br />\r\n<br />\r\n&quot;Bị sợ cho&aacute;ng v&aacute;ng?&quot;<br />\r\n<br />\r\nNgay tại L&acirc;m Dật suy nghĩ thời khắc, kia tiểu th&aacute;i gi&aacute;m the th&eacute; giọng thanh &acirc;m lại lần nữa vang l&ecirc;n.<br />\r\n<br />\r\n&quot;Tiểu c&ocirc;ng c&ocirc;ng, ta muốn đi thuận tiện một c&aacute;i, kh&ocirc;ng biết tiểu c&ocirc;ng c&ocirc;ng c&oacute; thể hay kh&ocirc;ng d&agrave;n xếp. . .&quot;<br />\r\n<br />\r\nL&acirc;m Dật hơi c&oacute; vẻ khẩn trương n&oacute;i.<br />\r\n<br />\r\n&quot;Hừ, kh&ocirc;ng cho ph&eacute;p!&quot;<br />\r\n<br />\r\nKia tiểu th&aacute;i gi&aacute;m v&ecirc;nh v&aacute;o tự đắc, trực tiếp cự tuyệt.<br />\r\n<br />\r\nL&acirc;m Dật trong l&ograve;ng thầm mắng, nụ cười tr&ecirc;n mặt kh&ocirc;ng thay đổi, theo trong tay &aacute;o lặng lẽ m&oacute;c ra một thỏi bạc, kh&ocirc;ng để lại dấu vết đặt ở tiểu th&aacute;i gi&aacute;m b&ecirc;n cạnh tr&ecirc;n b&agrave;n.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; trước đ&oacute; hắn cố gắng nhiều năm t&iacute;ch s&uacute;c.<br />\r\n<br />\r\nLập tức to&agrave;n bộ xuất ra đi, c&ograve;n c&oacute; ch&uacute;t đau l&ograve;ng.<br />\r\n<br />\r\nNhưng kh&ocirc;ng c&oacute; biện ph&aacute;p, ở c&aacute;i thế giới n&agrave;y, kh&ocirc;ng tiền kh&ocirc;ng thế kh&ocirc;ng c&oacute; thực lực, c&oacute; thể n&oacute;i nửa bước kh&oacute; đi, chỉ c&oacute; thể nh&igrave;n người kh&aacute;c sắc mặt.<br />\r\n<br />\r\nBất qu&aacute; chỉ cần c&oacute; thể ph&ograve;ng ngừa m&igrave;nh bị cắt, một thỏi bạc đ&aacute;ng l&agrave; g&igrave;?<br />\r\n<br />\r\nC&oacute; dũng kh&iacute; thu tự m&igrave;nh đồ vật? Sớm muộn đều sẽ cầm về!<br />\r\n<br />\r\nKia tiểu th&aacute;i gi&aacute;m con mắt nhắm lại, do dự một c&aacute;i, đưa tay đem bạc b&oacute;p tại trong tay, cảm nhận được kia trĩu nặng cảm nhận, l&uacute;c n&agrave;y mới lộ ra một v&ograve;ng &yacute; cười, &quot;T&iacute;nh ngươi hiểu chuyện, chuẩn! Nh&agrave; x&iacute; ở b&ecirc;n kia!&quot;<br />\r\n<br />\r\nL&acirc;m Dật n&oacute;i lời cảm tạ một tiếng, lặng lẽ rời khỏi gian ph&ograve;ng, vội v&agrave;ng tiến đến nh&agrave; x&iacute;.<br />\r\n<br />\r\nĐi v&agrave;o nh&agrave; x&iacute;, L&acirc;m Dật bắt đầu từ hệ thống kh&ocirc;ng gian b&ecirc;n trong triệu hồi ra Thế th&acirc;n con rối .<br />\r\n<br />\r\nC&aacute;i n&agrave;y con rối c&ugrave;ng L&acirc;m Dật tướng mạo kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o ph&acirc;n biệt, cơ hồ như đ&uacute;c, kh&ocirc;ng thể ph&acirc;n biệt.<br />\r\n<br />\r\nCh&iacute;nh l&agrave; đỉnh ti&ecirc;m cao thủ tới đ&acirc;y, cũng kh&ocirc;ng c&aacute;ch n&agrave;o ph&aacute;t hiện dị dạng.<br />\r\n<br />\r\nX&aacute;c nhận sẽ kh&ocirc;ng ra sai lầm về sau, L&acirc;m Dật t&acirc;m niệm thao t&uacute;ng con rối, trở lại tịnh th&acirc;n ph&ograve;ng b&ecirc;n trong.<br />\r\n<br />\r\n&quot;Nhanh như vậy?&quot;<br />\r\n<br />\r\nKia tiểu th&aacute;i gi&aacute;m nh&igrave;n thấy L&acirc;m Dật thế m&agrave; nhanh như vậy trở về, c&oacute; mấy phần ngo&agrave;i &yacute; muốn.<br />\r\n<br />\r\nHắn vốn cho rằng L&acirc;m Dật kiếm cớ, l&agrave; muốn mượn n&agrave;y cơ hội mở trốn đ&acirc;y.<br />\r\n<br />\r\nBất qu&aacute; hắn cũng kh&ocirc;ng lo lắng L&acirc;m Dật sẽ trốn, Đ&ocirc;ng Xưởng c&aacute;i g&igrave; địa phương? Muốn chạy trốn người, b&acirc;y giờ mộ phần cỏ sợ đều đ&atilde; cao ba m&eacute;t.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng d&aacute;m trễ n&atilde;i tiểu c&ocirc;ng c&ocirc;ng ch&iacute;nh sự t&igrave;nh!&quot;<br />\r\n<br />\r\nL&acirc;m Dật c&uacute;i đầu n&oacute;i.<br />\r\n<br />\r\n&quot;Đi v&agrave;o đi!&quot; Tiểu th&aacute;i gi&aacute;m hừ lạnh một c&acirc;u.<br />\r\n<br />\r\nL&acirc;m Dật gật đầu, đi v&agrave;o tịnh th&acirc;n ph&ograve;ng.<br />\r\n<br />\r\nTịnh th&acirc;n ph&ograve;ng b&ecirc;n trong, c&oacute; một c&aacute;i l&atilde;o th&aacute;i Gi&aacute;m Ch&iacute;nh đang s&aacute;t lau lấy một c&aacute;i nh&igrave;n kh&ocirc;ng qu&aacute; sắc b&eacute;n tr&agrave;n đầy vết m&aacute;u đao.<br />\r\n<br />\r\nH&igrave;nh tr&ecirc;n kệ, cũng rất bẩn loạn, tr&ecirc;n mặt đất c&oacute; vết m&aacute;u.<br />\r\n<br />\r\n&quot;Nằm xuống.&quot; L&atilde;o th&aacute;i gi&aacute;m lời &iacute;t m&agrave; &yacute; nhiều.<br />\r\n<br />\r\nL&acirc;m Dật giữ im lặng, đi v&agrave;o h&igrave;nh tr&ecirc;n kệ nằm xong.<br />\r\n<br />\r\nKia l&atilde;o th&aacute;i gi&aacute;m nh&igrave;n xem L&acirc;m Dật thần sắc b&igrave;nh tĩnh, trong mắt ngược lại l&agrave; nhiều hơn mấy phần bội phục.<br />\r\n<br />\r\nHắn ở chỗ n&agrave;y l&agrave;m mấy thập ni&ecirc;n, c&ograve;n l&agrave; lần đầu ti&ecirc;n nh&igrave;n thấy c&oacute; người tới nơi n&agrave;y kh&ocirc;ng kh&oacute;c kh&ocirc;ng nh&aacute;o, như thế b&igrave;nh tĩnh.<br />\r\n<br />\r\nPhần n&agrave;y t&acirc;m t&iacute;nh, mười điểm hiếm thấy.<br />\r\n<br />\r\nTương lai rất c&oacute; thể sẽ th&agrave;nh đại kh&iacute; a!<br />\r\n<br />\r\nBất qu&aacute; đ&aacute;ng tiếc. . .<br />\r\n<br />\r\n&quot;Ki&ecirc;n nhẫn một ch&uacute;t!&quot;</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><a href=\"https://c.lazada.vn/t/c.06cO5B?url=https%3A%2F%2Fpages.lazada.vn%2Fwow%2Fgcp%2Froute%2Flazada%2Fvn%2Fupr_1000345_lazada%2Fchannel%2Fvn%2Fupr-router%2Fvn%3Fhybrid%3D1%26data_prefetch%3Dtrue%26prefetch_replace%3D1%26at_iframe%3D1%26wh_pid%3D%2Flazada%2Fmegascenario%2Fvn%2Fsale-gap-boi-5-5-2022%2Fngay-doi-sale-boi\" target=\"_blank\"><img src=\"https://static.cdnno.com/storage/lazada/D5_AFF_MAIN_743x400.jpg\" /></a></p>\r\n\r\n<p><br />\r\n<br />\r\nL&atilde;o th&aacute;i gi&aacute;m hảo t&acirc;m nhắc nhở một c&acirc;u.<br />\r\n<br />\r\nN&oacute;i xong cũng kh&ocirc;ng do dự, trực tiếp &aacute;p đặt xuống dưới. . .<br />\r\n<br />\r\nCảm nhận được thế th&acirc;n bị cắt, L&acirc;m Dật trong l&ograve;ng đều l&agrave; kh&ocirc;ng khỏi nhảy một c&aacute;i, mồ h&ocirc;i lạnh ứa ra.<br />\r\n<br />\r\nMay mắn tự m&igrave;nh c&oacute; thế th&acirc;n, nếu kh&ocirc;ng m&igrave;nh đời n&agrave;y coi như chơi xong m&agrave;.<br />\r\n<br />\r\nC&acirc;y đao kia cắt xuống đi, sợ kh&ocirc;ng phải đ&ecirc;́n uốn v&aacute;n?<br />\r\n<br />\r\nTịnh th&acirc;n xong, L&acirc;m Dật lại l&agrave; đi một chuyến nh&agrave; x&iacute;, c&ugrave;ng thế th&acirc;n thay thế, đem thế th&acirc;n con rối đặt hệ thống kh&ocirc;ng gian, sau đ&oacute; liền bị người mang l&ecirc;n một chỗ kh&ocirc;ng người viện lạc nghỉ ngơi.<br />\r\n<br />\r\nTiểu th&aacute;i gi&aacute;m l&uacute;c gần đi, để lại một c&acirc;u n&oacute;i.<br />\r\n<br />\r\n&quot;Cho c&aacute;c ngươi một ng&agrave;y thời gian kh&ocirc;i phục, ng&agrave;y mai nh&agrave; ta lại đến mang c&aacute;c ngươi đi trong cung!&quot;<br />\r\n<br />\r\nL&acirc;m Dật ngồi tại gian ph&ograve;ng, nh&igrave;n tho&aacute;ng qua giường chung tr&ecirc;n nằm từng c&aacute;i sắc mặt trắng bệch c&ograve;n tại k&ecirc;u r&ecirc;n kh&ocirc;ng chỉ mấy người, khẽ lắc đầu.<br />\r\n<br />\r\nNếu như kh&ocirc;ng phải c&oacute; Thế th&acirc;n, tự m&igrave;nh sợ l&agrave; cũng c&ugrave;ng bọn hắn b&igrave;nh thường.<br />\r\n<br />\r\n&quot;Bất qu&aacute; tự m&igrave;nh kh&ocirc;ng c&oacute; tịnh th&acirc;n, tại trong ho&agrave;ng cung thế nhưng l&agrave; tối kỵ, bị người biết được, sẽ bị mất đầu, c&ograve;n c&oacute; thể li&ecirc;n lụy cửu tộc, về sau đ&ecirc;́n h&agrave;nh sự cẩn thận.&quot;<br />\r\n<br />\r\nL&acirc;m Dật rất r&otilde; r&agrave;ng điểm n&agrave;y, trong l&ograve;ng cảm gi&aacute;c nguy cơ lại l&agrave; kh&ocirc;ng c&oacute; giảm bớt ch&uacute;t n&agrave;o, chợt theo hệ thống kh&ocirc;ng gian trung tướng trước đ&oacute; đạt được Man thi&ecirc;n qu&aacute; hải đem ra.<br />\r\n<br />\r\n【 man thi&ecirc;n qu&aacute; hải: Như lời ngươi n&oacute;i n&oacute;i l&aacute;o, sẽ giấu diếm được tất cả mọi người, đối ngươi lời n&oacute;i, kh&ocirc;ng thể n&agrave;o ho&agrave;i nghi. 】<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y kh&ocirc;ng tệ!&quot;<br />\r\n<br />\r\nL&acirc;m Dật hai mắt tỏa s&aacute;ng.<br />\r\n<br />\r\nTheo hắn biết, c&oacute; ch&uacute;t cường giả c&oacute; thể nh&igrave;n ra một người c&oacute; hay kh&ocirc;ng n&oacute;i dối.<br />\r\n<br />\r\nTrong ho&agrave;ng cung, tất nhi&ecirc;n kh&ocirc;ng thiếu cường giả.<br />\r\n<br />\r\nC&oacute; năng lực n&agrave;y, c&oacute; thể nhường hắn sinh tồn tỉ lệ tăng l&ecirc;n tr&ecirc;n diện rộng.<br />\r\n<br />\r\nNgay lập tức cũng kh&ocirc;ng do dự, trực tiếp đối với m&igrave;nh sử dụng.<br />\r\n<br />\r\n&quot;Bất qu&aacute;, chỉ dựa v&agrave;o những n&agrave;y c&ograve;n chưa đủ, vẫn l&agrave; đ&ecirc;́n c&oacute; thực lực.&quot;<br />\r\n<br />\r\nL&acirc;m Dật nghĩ đến t&acirc;n thủ g&oacute;i qu&agrave; mười năm tu vi, trong l&ograve;ng rất l&agrave; hỏa nhiệt.<br />\r\n<br />\r\nBất qu&aacute;, hắn cũng kh&ocirc;ng c&oacute; vội v&atilde; sử dụng những phần thưởng kh&aacute;c.<br />\r\n<br />\r\nNơi n&agrave;y kh&ocirc;ng an to&agrave;n, vạn nhất bị người ph&aacute;t hiện liền phiền to&aacute;i.<br />\r\n<br />\r\nC&ograve;n nhiều thời gian, kh&ocirc;ng nhất thời vội v&atilde;, mọi thứ đều phải vững v&agrave;ng.<br />\r\n<br />\r\nB&acirc;y giờ vẫn l&agrave; trước hảo hảo nội ứng, h&egrave;n mọn ph&aacute;t dục. . .</p>', '2022-05-08 03:00:49', 19, 1),
(339, 89, 'Nữ Đế Long Thanh Lăng, Xem Thấu Vận Mệnh!', '2', '<p>&quot;Căn cứ Sở trưởng l&atilde;o an b&agrave;i, ng&agrave;y mai sẽ c&oacute; nội ứng mang ta tiến đến Thanh Lăng C&ocirc;ng ch&uacute;a bộ hạ, đến thời điểm muốn biện ph&aacute;p lấy được C&ocirc;ng ch&uacute;a t&iacute;n nhiệm. . .&quot;<br />\r\n<br />\r\nNằm ở tr&ecirc;n giường, L&acirc;m Dật nhớ tới tự m&igrave;nh chuyến n&agrave;y nội ứng nhiệm vụ, trong đầu kh&ocirc;ng khỏi hiện l&ecirc;n c&oacute; quan hệ Thanh Lăng C&ocirc;ng ch&uacute;a một ch&uacute;t nghe đồn.<br />\r\n<br />\r\n&quot;Thanh Lăng C&ocirc;ng ch&uacute;a, thi&ecirc;n ph&uacute; si&ecirc;u quần, tuổi gần mười s&aacute;u, liền đ&atilde; đi v&agrave;o Đạo Cơ cảnh giới, th&acirc;n phụ Ho&agrave;ng Đạo Th&aacute;nh Thể, c&oacute; hi vọng kế nhiệm Đại Thống. . .&quot;<br />\r\n<br />\r\n&quot;Xem ra Cửu U ma m&ocirc;n l&agrave; dự định đối Đại Chu động thủ, thi&ecirc;n hạ cũng đem loạn đi l&ecirc;n. . .&quot;<br />\r\n<br />\r\n&quot;Nghĩ tại trong loạn thế sống s&oacute;t, cũng kh&ocirc;ng dễ d&agrave;ng a!&quot;<br />\r\n<br />\r\nL&acirc;m Dật thầm than một tiếng, mạnh l&ecirc;n t&acirc;m tư c&agrave;ng th&ecirc;m nồng đậm.<br />\r\n<br />\r\n. . .<br />\r\n<br />\r\nTuyết Nguyệt cung.<br />\r\n<br />\r\nMột chỗ nh&atilde; c&aacute;c ph&iacute;a tr&ecirc;n.<br />\r\n<br />\r\nTam c&ocirc;ng ch&uacute;a Long Thanh Lăng người mặc lộng lẫy cung trang, đỉnh đầu mũ phượng, to&agrave;n th&acirc;n cũng tản ra cao qu&yacute; uy nghi&ecirc;m kh&iacute; chất.<br />\r\n<br />\r\nTrong s&aacute;ng &aacute;nh trăng vẩy xuống , l&agrave;m cho tr&ecirc;n người n&agrave;ng th&ecirc;m ra một phần m&ocirc;ng lung mỹ cảm, như Nguyệt Thần, thuần khiết m&agrave; cao qu&yacute;.<br />\r\n<br />\r\n&quot;Tuyết Nguyệt cung? Ta v&acirc;̣y mà kh&ocirc;ng chết?&quot;<br />\r\n<br />\r\nN&agrave;ng đứng tại c&aacute;c tr&ecirc;n đ&agrave;i, thanh l&atilde;nh đ&ocirc;i mắt đẹp nh&igrave;n ra xa như vậy đại ho&agrave;ng th&agrave;nh, ho&agrave;n mỹ ho&agrave;n mỹ tr&ecirc;n gương mặt c&oacute; kinh ngạc hiển hiện.<br />\r\n<br />\r\nN&agrave;ng nhớ r&otilde; r&agrave;ng, tự m&igrave;nh tại kia một trận đại chiến b&ecirc;n trong bị cường giả v&acirc;y c&ocirc;ng, trọng thương bỏ m&igrave;nh a?<br />\r\n<br />\r\nL&agrave;m sao lại lần nữa mở mắt, lại l&agrave; về tới trăm năm trước, tự m&igrave;nh chưa đăng cơ làm đế thời điểm?<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; c&oacute; chuyện g&igrave;?&quot;<br />\r\n<br />\r\nLong Thanh Lăng ho&agrave;n to&agrave;n kh&ocirc;ng thể n&agrave;o hiểu được ph&aacute;t sinh trước mắt đ&acirc;y hết thảy.<br />\r\n<br />\r\nThật l&acirc;u, n&agrave;ng mới khẽ lắc đầu.<br />\r\n<br />\r\n&quot;Bất luận như thế n&agrave;o, đ&acirc;y đều l&agrave; ta một lần cơ hội!&quot;<br />\r\n<br />\r\nLong Thanh Lăng trong mắt quang mang lấp l&oacute;e, c&oacute; s&aacute;t &yacute; hiển hiện.<br />\r\n<br />\r\nTrước đ&oacute;, Đại Chu bệ hạ vẫn lạc, v&igrave; vững chắc Đại Chu căn cơ, n&agrave;ng lấy chồng chủ th&acirc;n phần, tại Ho&agrave;ng Đ&igrave;nh biến cố b&ecirc;n trong, lực &aacute;p rất nhiều Ho&agrave;ng tử, b&agrave;i trừ ch&uacute;ng nghị, th&agrave;nh c&ocirc;ng kế vị, trở th&agrave;nh Đại Chu từ trước tới nay vị thứ nhất Nữ Đế.<br />\r\n<br />\r\nV&igrave; Đại Chu tương lai, n&agrave;ng chăm lo quản l&yacute;, chưa từng lười biếng.<br />\r\n<br />\r\nNhưng, s&oacute;i thần tặc tử, d&atilde; t&acirc;m bừng bừng, mưu đồ Đại Chu giang sơn.<br />\r\n<br />\r\nLại li&ecirc;n thủ đối vừa mới trải qua nội bộ ph&acirc;n loạn Đại Chu xuất thủ.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><a href=\"https://c.lazada.vn/t/c.06cO5B?url=https%3A%2F%2Fpages.lazada.vn%2Fwow%2Fgcp%2Froute%2Flazada%2Fvn%2Fupr_1000345_lazada%2Fchannel%2Fvn%2Fupr-router%2Fvn%3Fhybrid%3D1%26data_prefetch%3Dtrue%26prefetch_replace%3D1%26at_iframe%3D1%26wh_pid%3D%2Flazada%2Fmegascenario%2Fvn%2Fsale-gap-boi-5-5-2022%2Fngay-doi-sale-boi\" target=\"_blank\"><img src=\"https://static.cdnno.com/storage/lazada/D5_AFF_MAIN_743x400.jpg\" /></a></p>\r\n\r\n<p><br />\r\nCh&iacute;nh l&agrave; n&agrave;ng kinh t&agrave;i tuyệt diễm, thi&ecirc;n ph&uacute; v&ocirc; song, cũng kh&ocirc;ng c&aacute;ch n&agrave;o ngăn cản rất nhiều thế lực v&acirc;y c&ocirc;ng.<br />\r\n<br />\r\nĐại Chu vốn l&agrave; bấp b&ecirc;nh, ở v&agrave;o mới cũ giao thế thời kỳ mấu chốt, trong nh&aacute;y mắt binh bại như n&uacute;i đổ, v&ocirc; số th&agrave;nh tr&igrave; lu&acirc;n h&atilde;m, Đại Chu con d&acirc;n bị đồ bị cướp, ch&iacute;nh l&agrave; n&agrave;ng tự m&igrave;nh suất qu&acirc;n xuất chiến, cũng kh&ocirc;ng c&aacute;ch n&agrave;o thay đổi chiến cuộc.<br />\r\n<br />\r\nM&agrave; n&agrave;ng, cũng trở th&agrave;nh hủy diệt Đại Chu kẻ cầm đầu, tội nh&acirc;n thi&ecirc;n cổ, thụ v&ocirc; số người th&oacute;a mạ.<br />\r\n<br />\r\nNản l&ograve;ng tho&aacute;i ch&iacute; n&agrave;ng, mang theo tử ch&iacute;, c&ugrave;ng những c&aacute;i kia cường giả đỉnh cao quyết nhất tử chiến, cuối c&ugrave;ng bất hạnh vẫn lạc. . .<br />\r\n<br />\r\n&quot;Lần n&agrave;y, ta tuyệt đối sẽ kh&ocirc;ng nhường bi kịch t&aacute;i diễn!&quot;<br />\r\n<br />\r\nLong Thanh Lăng &aacute;nh mắt th&acirc;m th&uacute;y, tuyệt mỹ tr&ecirc;n gương mặt tr&agrave;n đầy ki&ecirc;n định.<br />\r\n<br />\r\n&quot;Phụ ho&agrave;ng sắp vẫn lạc, Th&aacute;i Tử chưa lập, v&igrave; tranh đoạt ho&agrave;ng vị, tất nhi&ecirc;n nội loạn, kh&ocirc;ng &iacute;t cường giả tại một trận chiến n&agrave;y vẫn lạc, đ&acirc;y mới l&agrave; Đại Chu Hủy Diệt căn nguy&ecirc;n.&quot;<br />\r\n<br />\r\n&quot;Ta nhất định phải sớm kế vị, khả năng ngăn cản đ&acirc;y hết thảy ph&aacute;t sinh!&quot;<br />\r\n<br />\r\n&quot;Ta hiện tại kh&ocirc;ng c&oacute; g&igrave; cả, muốn tại đoạt đ&iacute;ch chiến b&ecirc;n trong thủ thắng lấy &aacute;p đảo ưu thế thủ thắng , khiến c&aacute;i kh&aacute;c Ho&agrave;ng tử ngoan ngo&atilde;n nghe lời, nhất định phải c&oacute; thuộc về m&igrave;nh nh&acirc;n m&atilde;!&quot;<br />\r\n<br />\r\n&quot;Chỉ cần kh&ocirc;ng nội đấu, nghĩ đến thế lực kh&aacute;c, muốn đối Đại Chu động thủ, cũng phải c&acirc;n nhắc một ch&uacute;t!&quot;<br />\r\n<br />\r\n&quot;Nhưng, b&acirc;y giờ đại bộ phận năng thần, đều đ&atilde; đứng đội, muốn mời ch&agrave;o nh&acirc;n t&agrave;i, độ kh&oacute; kh&ocirc;ng nhỏ!&quot;<br />\r\n<br />\r\n&quot;M&agrave; lại tư x&acirc;y binh quyền, thế nhưng l&agrave; tối kỵ. . .&quot;<br />\r\n<br />\r\nNghĩ tới đ&acirc;y, Long Thanh Lăng tr&ecirc;n mặt nhiều hơn mấy phần thần sắc lo lắng, nhưng rất nhanh kh&ocirc;i phục lại b&igrave;nh tĩnh.<br />\r\n<br />\r\nD&ugrave; l&agrave; lại kh&oacute;, n&agrave;ng cũng muốn l&agrave;m, đền b&ugrave; những c&aacute;i kia tiếc nuối.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, vẫn l&agrave; trước ti&ecirc;n cần phải đem b&ecirc;n người những người kia nằm v&ugrave;ng qu&acirc;n cờ cho nhổ.<br />\r\n<br />\r\n&quot;Người tới!&quot;<br />\r\n<br />\r\nN&agrave;ng nhẹ giọng mở miệng, tiếng như tiếng trời.<br />\r\n<br />\r\n&quot;C&ocirc;ng ch&uacute;a!&quot;<br />\r\n<br />\r\nSau lưng cung nữ vội v&agrave;ng tiến l&ecirc;n.<br />\r\n<br />\r\nLong Thanh Lăng quay người, đang muốn mở miệng, lại đột nhi&ecirc;n bị trước mắt xuất hiện một m&agrave;n cho kinh trụ.<br />\r\n<br />\r\nC&aacute;i gặp tại cung nữ đỉnh đầu, c&oacute; một đạo m&agrave;n s&aacute;ng hiển hiện.<br />\r\n<br />\r\n【 t&iacute;nh danh: Tiểu Hoa 】<br />\r\n<br />\r\n【 mệnh c&aacute;ch: Bạch &middot; Cam Vi Nh&acirc;n N&ocirc;, trắng thường thường kh&ocirc;ng c&oacute; g&igrave; lạ 】<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave;?&quot;<br />\r\n<br />\r\nLong Thanh Lăng mắt phượng nhắm lại, c&oacute; nghi hoặc.<br />\r\n<br />\r\n&quot;Chẳng lẽ l&agrave; bởi v&igrave; thời gian hồi tố sinh ra biến h&oacute;a?&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y mệnh c&aacute;ch l&agrave; c&oacute; &yacute; g&igrave;? Mỗi người cố định vận mệnh?&quot;<br />\r\n<br />\r\nLong Thanh Lăng trong l&ograve;ng tự n&oacute;i.<br />\r\n<br />\r\nV&igrave; chứng thực &yacute; nghĩ trong l&ograve;ng, n&agrave;ng lại mở miệng, &quot;Đem những người kh&aacute;c c&ugrave;ng nhau gọi.&quot;<br />\r\n<br />\r\nCung nữ Tiểu Hoa mặc d&ugrave; kh&ocirc;ng biết sao, nhưng lập tức rời đi.<br />\r\n<br />\r\nRất nhanh, một đ&aacute;m cung nữ th&aacute;i gi&aacute;m trọn vẹn hơn trăm người, đều hội tụ nh&atilde; c&aacute;c ph&iacute;a trước.<br />\r\n<br />\r\nLong Thanh Lăng &aacute;nh mắt tại những n&agrave;y đầu người tr&ecirc;n đỉnh từng c&aacute;i đảo qua.<br />\r\n<br />\r\n【 t&iacute;nh danh: Tiểu L&yacute; Tử 】<br />\r\n<br />\r\n【 mệnh c&aacute;ch: Bạch &middot; kh&ocirc;ng l&agrave;m nam nh&acirc;n, Bạch &middot; Thi&ecirc;n Sinh Phản Cốt 】<br />\r\n<br />\r\n【 t&iacute;nh danh: Tiểu Ngọc 】<br />\r\n<br />\r\n【 mệnh c&aacute;ch: Xanh c&oacute; ch&uacute;t số phận, Bạch &middot; nhan trị xuất ch&uacute;ng 】<br />\r\n<br />\r\n【. . . 】<br />\r\n<br />\r\nLong Thanh Lăng đ&ocirc;i mắt đẹp ngưng lại, th&ocirc;ng tuệ n&agrave;ng đ&atilde; minh bạch đại kh&aacute;i.<br />\r\n<br />\r\nTự m&igrave;nh quả nhi&ecirc;n c&oacute; thể nh&igrave;n thấy người kh&aacute;c cố định vận mệnh!<br />\r\n<br />\r\nC&aacute;i n&agrave;y Tiểu Ngọc l&uacute;c trước ch&iacute;nh l&agrave; rất được n&agrave;ng t&iacute;n nhiệm, thường xuy&ecirc;n ban thưởng, cho n&ecirc;n mệnh c&aacute;ch b&ecirc;n trong c&oacute; c&oacute; ch&uacute;t số phận.<br />\r\n<br />\r\nM&agrave; tiểu L&yacute; Tử, trời sinh phản cốt.<br />\r\n<br />\r\nTrước đ&oacute; n&agrave;ng gặp được h&agrave;nh th&iacute;ch, sau đ&oacute; điều tra mới biết được bị c&aacute;i n&agrave;y tiểu L&yacute; Tử b&aacute;n!<br />\r\n<br />\r\nNếu như kh&ocirc;ng phải m&igrave;nh lại bắt đầu lại từ đầu, sợ cũng l&agrave; kh&ocirc;ng c&aacute;ch n&agrave;o nh&igrave;n ra dị dạng.<br />\r\n<br />\r\nM&agrave; b&acirc;y giờ, c&aacute;i n&agrave;y đột nhi&ecirc;n xuất hiện m&agrave;n s&aacute;ng, lại l&agrave; mười điểm r&otilde; r&agrave;ng!<br />\r\n<br />\r\nLong Thanh Lăng trong l&ograve;ng hiếm thấy c&oacute; một tia mừng rỡ.<br />\r\n<br />\r\nN&agrave;ng hiện tại nhu cầu cấp b&aacute;ch mở rộng thế lực của m&igrave;nh, tự m&igrave;nh chỉ l&agrave; C&ocirc;ng ch&uacute;a, muốn người kh&aacute;c v&igrave; ch&iacute;nh m&igrave;nh hiệu lực, rất kh&ocirc;ng thực tế.<br />\r\n<br />\r\nC&oacute; c&aacute;i n&agrave;y đột nhi&ecirc;n xuất hiện năng lực, tự m&igrave;nh liền c&oacute; thể ph&aacute;t hiện những c&aacute;i kia bị mai một nh&acirc;n t&agrave;i, v&igrave; n&agrave;ng tiết kiệm rất nhiều thời gian t&acirc;m lực.</p>\r\n\r\n<p><small><small>&mdash; QUẢNG C&Aacute;O &mdash;</small></small></p>\r\n\r\n<p><a href=\"https://c.lazada.vn/t/c.06cO5B?url=https%3A%2F%2Fpages.lazada.vn%2Fwow%2Fgcp%2Froute%2Flazada%2Fvn%2Fupr_1000345_lazada%2Fchannel%2Fvn%2Fupr-router%2Fvn%3Fhybrid%3D1%26data_prefetch%3Dtrue%26prefetch_replace%3D1%26at_iframe%3D1%26wh_pid%3D%2Flazada%2Fmegascenario%2Fvn%2Fsale-gap-boi-5-5-2022%2Fngay-doi-sale-boi\" target=\"_blank\"><img src=\"https://static.cdnno.com/storage/lazada/D5_AFF_MAIN_743x400.jpg\" /></a></p>\r\n\r\n<p><br />\r\nXem ra ch&iacute;nh m&igrave;nh ng&agrave;y mai được ra ngo&agrave;i xem một ch&uacute;t, n&oacute;i kh&ocirc;ng chừng c&oacute; thể ph&aacute;t hiện mấy c&aacute;i hiếm thấy nh&acirc;n t&agrave;i.<br />\r\n<br />\r\n&quot;Người tới, đem tiểu L&yacute; Tử tiểu An Tử tiểu Xu&acirc;n Tử cũng cho bản C&ocirc;ng ch&uacute;a k&eacute;o xuống ch&eacute;m!&quot;<br />\r\n<br />\r\nLong Thanh Lăng thu liễm suy nghĩ, c&oacute; ch&uacute;t phất tay, &acirc;m thanh lạnh l&ugrave;ng n&oacute;i.<br />\r\n<br />\r\nLời vừa n&oacute;i ra, tất cả th&aacute;i gi&aacute;m cung nữ cũng h&ocirc;n m&ecirc; rồi.<br />\r\n<br />\r\n&quot;C&ocirc;ng ch&uacute;a tha mạng!&quot;<br />\r\n<br />\r\nTiểu L&yacute; Tử bọn người trong l&ograve;ng nhảy một c&aacute;i, vội v&agrave;ng cầu xin tha thứ.<br />\r\n<br />\r\n&quot;C&ocirc;ng ch&uacute;a. . . Tiểu An Tử bọn hắn, từ trước đến nay trung th&agrave;nh. . .&quot;<br />\r\n<br />\r\nTiểu Ngọc nhịn kh&ocirc;ng được mở miệng cầu t&igrave;nh.<br />\r\n<br />\r\nLong Thanh Lăng cười lạnh, &quot;Trung th&agrave;nh? Ha ha, bọn hắn đối với người n&agrave;o trung th&agrave;nh?&quot;<br />\r\n<br />\r\n&quot;L&agrave; Nhị ho&agrave;ng tử, vẫn l&agrave; Lục ho&agrave;ng tử Cửu ho&agrave;ng tử?&quot;<br />\r\n<br />\r\nNghe được Long Thanh Lăng, tiểu L&yacute; Tử đ&aacute;m người sắc mặt bỗng nhi&ecirc;n ho&agrave;n to&agrave;n biến đổi, n&agrave;ng l&agrave;m sao biết r&otilde;?<br />\r\n<br />\r\nCăn bản kh&ocirc;ng c&oacute; giải th&iacute;ch, theo ba người mặt x&aacute;m như tro biểu lộ, cũng đ&atilde; nh&igrave;n ra m&aacute;nh kh&oacute;e, lập tức liền c&oacute; người đem mấy người k&eacute;o xuống vấn trảm.<br />\r\n<br />\r\n&quot;Đ&acirc;y hết thảy, đều l&agrave; c&aacute;c ngươi tự t&igrave;m, nhưng kh&ocirc;ng tr&aacute;ch được ta!&quot;<br />\r\n<br />\r\nLong Thanh Lăng đ&ocirc;i mắt đẹp nhắm lại, c&oacute; v&ocirc; h&igrave;nh uy nghi&ecirc;m ph&aacute;t ra.<br />\r\n<br />\r\nNguy&ecirc;n bản n&agrave;ng cũng l&agrave; v&ocirc; &yacute; ho&agrave;ng vị, c&agrave;ng kh&ocirc;ng muốn c&ugrave;ng tự m&igrave;nh ho&agrave;ng huynh tranh vị, nhưng m&igrave;nh thi&ecirc;n ph&uacute; v&agrave; Phụ ho&agrave;ng sủng &aacute;i, lại l&agrave; đưa tới những n&agrave;y Ho&agrave;ng tử ki&ecirc;ng kị, đem tự m&igrave;nh coi l&agrave; c&aacute;i đinh trong mắt.<br />\r\n<br />\r\nChẳng những muốn ph&aacute;i người &aacute;m s&aacute;t tự m&igrave;nh, c&ograve;n đưa ra để cho m&igrave;nh c&ugrave;ng người th&ocirc;ng gia, đưa c&aacute;ch Đại Chu, c&aacute;i n&agrave;y x&uacute;c động n&agrave;ng ranh giới cuối c&ugrave;ng.<br />\r\n<br />\r\nN&agrave;ng đ&atilde; kh&ocirc;ng phải l&agrave; trước đ&acirc;y c&aacute;i kia đơn thuần tiểu nữ h&agrave;i!<br />\r\n<br />\r\nTrước đ&oacute; gi&aacute;o huấn, đ&atilde; n&oacute;i cho n&agrave;ng biết, chỉ c&oacute; v&ocirc; t&igrave;nh, mới c&oacute; thể sống đ&ecirc;́n c&agrave;ng l&acirc;u.<br />\r\n<br />\r\nTiểu Ngọc sửng sốt rất l&acirc;u, l&uacute;c n&agrave;y mới kịp phản ứng.<br />\r\n<br />\r\nKh&ocirc;ng nghĩ tới, mấy người n&agrave;y đ&uacute;ng l&agrave; c&aacute;i kh&aacute;c Ho&agrave;ng tử ph&aacute;i tới nội ứng.<br />\r\n<br />\r\nSợ vỗ vỗ bộ ngực, hướng về ph&iacute;a một vị c&ocirc;ng c&ocirc;ng n&oacute;i:<br />\r\n<br />\r\n&quot;Hải c&ocirc;ng c&ocirc;ng, l&agrave;m phiền ngươi đi Đ&ocirc;ng Xưởng t&igrave;m mấy người trừ bị th&aacute;i gi&aacute;m, thay thế tiểu L&yacute; Tử bọn hắn!&quot;</p>', '2022-05-08 03:01:12', 2, 1);
